"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[88259],{20590:(e,n,t)=>{t.r(n),t.d(n,{default:()=>a});var s=t(74848);const a=[{name:"\u7b2c \u4e00 \u7ae0",content:(0,s.jsx)(s.Fragment,{children:'\u7b2c \u4e00 \u7ae0 \u3000\u3000\u6885\u5360\u6625\u5148\uff0c\u51cc\u5bd2\u65e9\u653e\uff0c\u4e0e\u677e\u7af9\u4e3a\u4e09\u53cb\uff0c\u50b2\u51b0\u96ea\u800c\u72ec\u8273\u3002 \u3000\u3000\u65f6\u5f53\u65e9\u6625\uff0c\u6606\u660e\u57ce\u5916\uff0c\u4e94\u534e\u5c71\u91cc\uff0c\u96ea\u6df1\u6885\u5f00\uff0c\u6d51\u82d4\u7f00\u7389\uff0c\u970f\u96ea\u8054\u82f1\uff0c\u867d\u4ecd\u4e25\u98da\u5982\u6545\uff0c \u4f46\u6885\u9999\u6c81\u5fc3\uff0c\u4ee4\u4eba\u5fc3\u813e\u795e\u9aa8\u7686\u6e05\u3002 \u3000\u3000\u540e\u5c71\u6df1\u5904\uff0c\u76f4\u58c1\u8fde\u4e91\uff0c\u7691\u7691\u767d\u96ea\u4e4b\u4e0a\uff0c\u7f00\u4ee5\u8001\u6885\u591a\u672c\uff0c\u7686\u4f3c\u767e\u5e74\u4e4b\u7269\uff0c\u866c\u679d\u5982\u94c1\uff0c \u6697\u9999\u6d6e\u5f71\uff0c\u771f\u4e0d\u77e5\u5929\u5730\u95f4\uff0c\u4f55\u6765\u6b64\u4ed9\u5883\u3002 \u3000\u3000\u66ae\u8272\u56db\u5408\u6726\u80e7\u9647\u4e2d\u666f\u7269\u66f4\u89c1\u80dc\u7edd\uff0c\u5ffd\u5730\u6885\u9634\u6df1\u5904\uff0c\u957f\u957f\u4f20\u6765\u4e00\u58f0\u53f9\u606f\uff0c\u7f13\u7f13\u5761\u51fa\u4e00 \u4f4d\u5112\u670d\u65b9\u5dfe\u7684\u6587\u58eb\uff0c\u4ea6\u4e0d\u77e5\u4ece\u4f55\u5904\u6765\u3002 \u3000\u3000\u4ed6\u4ece\u5bb9\u5730\u5728\u8fd9\u5e7d\u8c37\u56db\u5468\uff0c\u6f2b\u6b65\u4e86\u4e00\u904d\uff0c\u6df1\u539a\u7684\u767d\u96ea\u4e0a\uff0c\u5374\u672a\u89c1\u7559\u4e0b\u4efb\u4f55\u811a\u8ff9\uff0c\u7136\u540e \u8d1f\u624b\u4f2b\u7acb\u5728\u4e00\u682a\u76db\u5f00\u7684\u8001\u6885\u524d\u9762\uff0c\u51dd\u795e\u5730\u671b\u7740\u6885\u82b1\uff0c\u8eab\u4e0a\u7684\u8863\u8882\uff0c\u968f\u98ce\u5fae\u52a8\uff0c\u6b64\u65f6\u6b64\u5730\uff0c \u671b\u4e4b\u76f4\u5982\u795e\u4ed9\u4e2d\u4eba\u3002 \u3000\u3000\u4e07\u7c0c\u4ff1\u5bc2\uff0c\u5c31\u8fde\u6781\u8f7b\u5fae\u7684\u866b\u9e1f\u4e4b\u58f0\uff0c\u5728\u8fd9\u4e25\u5bd2\u7edd\u8c37\u91cc\uff0c\u90fd\u65e0\u6cd5\u542c\u5230\u3002\u4ed6\u968f\u624b\u62fe\u8d77\u4e00 \u6bb5\u67af\u679d\uff0c\u5728\u96ea\u5730\u4e0a\u6d45\u6d45\u52fe\u8d77\u4e00\u5e45\u6885\u82b1\uff0c\u867d\u53ea\u662f\u5be5\u5be5\u6570\u7b14\uff0c\u5374\u628a\u6885\u82b1\u7684\u51cc\u98ce\u50b2\u9aa8\uff0c\u8868\u9732\u65e0 \u9057\u3002 \u3000\u3000\u6b64\u65f6\u8fdc\u5904\u7adf\u9690\u9690\u4f20\u6765\u4e9b\u4eba\u8bed\uff0c\u4f46\u4e5f\u662f\u6781\u4e3a\u8f7b\u5fae\u800c\u9065\u8fdc\u7684\uff0c\u4ed6\u9762\u8272\u5fae\u53d8\uff0c\u5634\u89d2\u6cdb\u8d77\u4e00 \u4e1d\u51b7\u5ced\u7684\u5fae\u7b11\uff0c\u624b\u5fae\u4e00\u6325\uff0c\u90a3\u6bb5\u67af\u679d\u7adf\u6df1\u6df1\u5730\u5d4c\u8fdb\u77f3\u58c1\u91cc\u3002 \u3000\u3000\u7247\u523b\uff0c\u8fdc\u8fdc\u770b\u5230\u51e0\u6761\u6781\u6de1\u7684\u8eab\u5f71\uff0c\u6643\u773c\u95f4\u4fbf\u6765\u5230\u8fd1\u524d\uff0c\u90a3\u79cd\u60ca\u4eba\u7684\u901f\u5ea6\uff0c\u662f\u5e38\u4eba\u6240 \u65e0\u6cd5\u601d\u8bae\u7684\uff0c\u4f46\u4ed6\u89c1\u4e86\uff0c\u5374\u9119\u5937\u5730\u4e00\u7b11\uff0c\u8138\u4e0a\u7684\u795e\u8272\u66f4\u51b7\u5cfb\u4e86\u3002\u90a3\u51e0\u6761\u4eba\u5f71\u5728\u8c37\u53e3\u7565\u4e00 \u76d8\u65cb\uff0c\u4fbf\u76f4\u5954\u4ed6\u6240\u4f2b\u5728\u4e4b\u5904\u800c\u6765\uff0c\u4ed6\u5583\u5583\u5730\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u53ea\u6709\u56db\u4e2a\uff0c\u96be\u9053\u6b64\u6b21\u53c8\u4e0d \u80fd\u4e86\u6211\u5fc3\u613f\u2026\u2026\u201d \u3000\u3000\u90a3\u56db\u4e2a\u4eba\u5230\u4e86\u4ed6\u9762\u524d\u4e08\u4f59\u4e4b\u5904\uff0c\u624d\u987f\u8eab\u5f71\uff0c\u7f13\u6b65\u8d70\u6765\uff0c\u5176\u4e2d\u4e00\u4e2a\u9762\u8272\u8d64\u7ea2\uff0c\u8eab\u6750\u9ad8 \u5927\u7684\u9053\u4eba\uff0c\u9ad8\u58f0\u7b11\u9053\uff1a\u201c\u795e\u541b\u771f\u662f\u4fe1\u4eba\uff0c\u53ea\u662f\u6211\u7b49\u5374\u6765\u8fdf\u4e86\u3002\u201d \u3000\u3000\u7b11\u58f0\u5728\u56db\u8c37\u98d8\u8361\u7740\uff0c\u56de\u97f3\u4f20\u6765\uff0c\u55e1\u55e1\u4f5c\u54cd\u3002\u6587\u58eb\u51b7\u51b7\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u5728\u90a3\u56db\u4eba\u8eab \u4e0a\u7565\u4e00\u6253\u91cf\uff0c\u7136\u540e\u505c\u7559\u5728\u4e00\u4e2a\u67af\u7626\u7684\u8001\u8005\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u8001\u8005\u7a7f\u7740\u6781\u4e3a\u7cbe\u81f4\u7684\u4e1d\u68c9\u888d\u5b50\uff0c\u80cc\u540e\u659c\u80cc\u7740\u67c4\u957f\u5251\uff0c\u90a3\u5251\u8eab\u5f88\u957f\uff0c\u80cc\u5728\u4ed6\u90a3\u67af\u7626 \u7684\u8eab\u8eaf\u4e0a\uff0c\u51e0\u4e4e\u6302\u5230\u5730\u4e0a\u4e86\uff0c\u663e\u5f97\u751a\u662f\u6ed1\u7a3d\uff0c\u7136\u800c\u4ed6\u5e7f\u989d\u6df1\u816e\uff0c\u76ee\u5149\u5982\u9e70\uff0c\u671b\u4e4b\u5374\u53c8\u4ee4 \u4eba\u751f\u754f\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u662f\u9762\u5e26\u7b11\u5bb9\uff0c\u4f46\u8fd9\u52c9\u5f3a\u7684\u7b11\u5bb9\uff0c\u5374\u4e0d\u80fd\u63a9\u9970\u4f4f\u4ed6\u4eec\u5185\u5fc3\u7684\u60ca\u4ff1\u548c\u60f6\u6050\uff0c\u90a3\u662f \u4e00\u79cd\u4eba\u4eec\u5728\u9762\u4e34\u7740\u751f\u4e0e\u6b7b\u7684\u6289\u62e9\u5173\u5934\u65f6\u5019\uff0c\u6240\u65e0\u6cd5\u907f\u514d\u7684\u60ca\u60e7\u548c\u60f6\u6050\uff0c\u5176\u4e2d\u5c24\u5176\u662f\u4e00\u4e2a \u5e74\u8f7b\u800c\u82f1\u4fca\u7684\u5c11\u5e74\uff0c\u4ed6\u751a\u81f3\u5728\u98a4\u6296\u7740\uff0c\u82f1\u4fca\u7684\u9762\u5e9e\u4e0a\uff0c\u4e5f\u8499\u7740\u4e00\u5c42\u6b7b\u7070\u4e4b\u8272\u3002 \u3000\u3000\u8fd9\u4e9b\u795e\u6001\u90fd\u7792\u4e0d\u4e86\u90a3\u51b7\u5cfb\u7684\u6587\u58eb\uff0c\u4ed6\u76ee\u5149\u6781\u5feb\u7684\u4e00\u95ea\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6b66\u6797 \u4e94\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u4eca\u5929\u7adf\u7136\u5230\u4e86\u4e09\u4f4d\uff0c\u771f\u53eb\u6211\u6885\u5c71\u6c11\u9ad8\u5174\u5f97\u5f88\uff0c\u4e0d\u8fc7\u2026\u2026\u201d\u4ed6\u9762\u8272\u4e00 \u53d8\uff0c\u76ee\u5149\u4e2d\u6d41\u9732\u51fa\u4e00\u4e1d\u53ef\u754f\u7684\u6740\u673a\uff0c\u51b7\u51b7\u5730\u8bf4\uff1a\u201c\u6606\u4ed1\u6d3e\u7684\u51cc\u7a7a\u6b65\u865a\u5353\u817e\u548c\u70b9\u82cd\u7684\u638c\u95e8 \u4eba\u8ffd\u98ce\u5251\u8c22\u661f\uff0c\u600e\u5730\u5374\u672b\u89c1\u524d\u6765\uff0c\u96be\u9053\u4ed6\u4eec\u770b\u4e0d\u8d77\u6211\u6885\u67d0\u4eba\u5417\u3002\u201d \u3000\u3000\u90a3\u8d64\u7ea2\u9762\u819b\u7684\u9053\u4eba\uff0c\u5374\u662f\u4e94\u5927\u5b97\u6d3e\u4e4b\u9996\uff0c\u6b66\u5f53\u6d3e\u7684\u638c\u95e8\u4eba\u8d64\u9633\u9053\u957f\uff0c\u6b64\u523b\u95fb\u8a00\uff0c\u7b11 \u9053\uff1a\u201c\u60a8\u7684\u53ec\u5524\uff0c\u4ed6\u4eec\u600e\u6562\u4e0d\u6765\uff0c\u53ea\u662f\u2026\u2026\u201d \u3000\u3000\u90a3\u67af\u7626\u7684\u8001\u8005\u51b7\u51b7\u63a5\u8fc7\u53e3\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u6709\u4e2a\u6bd4\u4f60\u4e03\u5999\u795e\u541b\u66f4\u80dc\u8fc7\u5341\u500d\u7684\u4eba\u5c06\u4ed6 \u4eec\u53ec\u4e86\u53bb\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u53cc\u76ee\u4e00\u5f20\uff0c\u95ea\u7535\u822c\u76ef\u5728\u90a3\u8001\u8005\u8138\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4eba\u662f\u8c01\uff0c\u6211\u6885\u67d0\u4eba\u5012\u8981\u89c1\u8bc6 \u89c1\u8bc6\u3002\u201d \u3000\u3000\u67af\u7626\u8001\u8005\u8138\u4e0a\u6cdb\u8d77\u4e00\u4e1d\u7b11\u610f\uff0c\u4e0d\u7b11\u4fbf\u7f62\u4e86\uff0c\u4e00\u7b11\u5374\u4ee4\u4eba\u4e0d\u7531\u751f\u51fa\u4e00\u4e1d\u5bd2\u610f\uff0c\u4ed6\u8bf4\u9053\uff1a \u201c\u82e5\u4f60\u80fd\u89c1\u5230\u6b64\u4eba\uff0c\u90a3\u6211\u5389\u9e57\u7b2c\u4e00\u4e2a\u5c31\u9ad8\u5174\u5f97\u5f88\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u53d8\u8272\u95ee\u9053\uff1a\u201c\u6b64\u8bdd\u600e\u8bb2\u3002\u201d \u3000\u3000\u8d64\u9633\u9053\u957f\u5fd9\u63a5\u8fc7\u53e3\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u795e\u541b\u5148\u83ab\u52a8\u6012\uff0c\u90a3\u8ffd\u98ce\u5251\u8c22\u5927\u4fa0\uff0c\u548c\u51cc\u7a7a\u6b65\u865a\u5353\u5927 \u4fa0\uff0c\u6570\u6708\u524d\u90fd\u76f8\u7ee7\u4ed9\u53bb\u4e86\uff0c\u662f\u4ee5\u4ed6\u4eec\u90fd\u65e0\u6cd5\u8df5\u795e\u541b\u4e09\u5e74\u524d\u8d4c\u547d\u4e4b\u7ea6\uff0c\u7136\u800c\u2026\u2026\u201d\u4ed6\u7528\u624b \u5fae\u6307\u8eab\u65c1\u7684\u82f1\u4fca\u5c11\u5e74\uff0c\u63a5\u7740\u8bf4\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u70b9\u82cd\u6d3e\u7684\u7b2c\u4e03\u4ee3\u638c\u95e8\u4eba\uff0c\u8ffd\u98ce\u5251\u8c22\u5927\u4fa0\u7684\u8d24 \u55e3\uff0c\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u4eca\u65e5\u7279\u6765\u4ee3\u7236\u8df5\u7ea6\u7684\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u5662\u4e86\u4e00\u58f0\uff0c\u5c16\u9510\u5730\u77aa\u4e86\u90a3\u4ecd\u5728\u51b7\u7b11\u7740\u7684\u5389\u9e57\u4e00\u773c\uff0c\u76ee\u5149\u56de\u5230\u8c22\u957f\u537f\u90a3\u91cc\uff0c\u8bf4 \u9053\uff1a\u201c\u8c22\u4e16\u5144\u82f1\u4fca\u4e0d\u51e1\uff0c\u6545\u4eba\u6709\u540e\uff0c\u771f\u53eb\u6211\u6885\u67d0\u4eba\u9ad8\u5174\u5f97\u5f88\uff0c\u4f46\u662f\u524d\u4e00\u4ee3\u7684\u4e8b\uff0c\u8ba9\u6211\u4eec \u81ea\u5df1\u4e86\u65ad\u597d\u4e86\uff0c\u8c22\u4e16\u5144\u82e5\u65e0\u5fc5\u8981\uff0c\u4e5f\u4e0d\u5fc5\u63d2\u8db3\u6b64\u4e8b\u4e86\u3002\u201d \u3000\u3000\u5728\u8fd9\u5239\u90a3\u95f4\uff0c\u8c22\u957f\u537f\u7684\u5185\u5fc3\uff0c\u5b9b\u5982\u6ce2\u6d9b\u51b2\u6fc0\uff0c\u663e\u7136\u6885\u5c71\u6c11\u7684\u8bdd\u6b63\u89e6\u4e2d\u4e86\u4ed6\u7684\u5fc3\u5e95\u6df1 \u5904\uff0c\u7136\u800c\u4ed6\u751f\u5728\u6b66\u6797\u4e16\u5bb6\uff0c\u73b0\u5728\u53c8\u662f\u4e00\u5927\u5b97\u6d3e\u7684\u638c\u95e8\uff0c\u6709\u8bb8\u591a\u4e8b\uff0c\u4ed6\u5fc5\u987b\u52c9\u5f3a\u7740\u81ea\u5df1\u53bb \u505a\uff0c\u4e3a\u4e86\u70b9\u82cd\u6d3e\u7684\u540d\u8a89\uff0c\u4e3a\u4e86\u4ed6\u81ea\u5df1\u5728\u6c5f\u6e56\u4e2d\u7684\u5730\u4f4d\uff0c\u4ed6\u6781\u529b\u5730\u63a7\u5236\u81ea\u5df1\u7684\u60c5\u611f\uff0c\u4e0d\u8ba9 \u4ed6\u5728\u9762\u5bb9\u4e0a\u8868\u9732\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u53cc\u76ee\u832b\u7136\u51dd\u7740\u8fdc\u65b9\u8bf4\u9053\uff1a\u201c\u795e\u541b\u7684\u8bdd\uff0c\u81ea\u7136\u4e5f\u662f\u9053\u7406\uff0c\u4f46\u662f\u5927\u4e08\u592b\u4e00\u8a00\u65e2\u51fa\u5982\u767d \u67d3\u7682\uff0c\u5148\u7236\u4e0e\u795e\u541b\u65e2\u7136\u6709\u7ea6\u5728\u5148\uff0c\u6211\u81ea\u5f53\u9075\u7740\u5148\u7236\u9057\u547d\uff0c\u4e0e\u795e\u541b\u8df5\u6b64\u4e00\u7ea6\uff0c\u81f3\u4e8e\u6210\u8d25\u751f \u6b7b\uff0c\u53c8\u5c82\u662f\u6211\u7b49\u8ba1\u8f83\u7684\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u5fae\u7b11\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u5fc3\u91cc\u5728\u6697\u81ea\u8d5e\u8d4f\u7740\u8fd9\u5c11\u5e74\u7684\u52c7\u6562\uff0c\u8bf4\u9053\uff1a\u201c\u4eba\u5404\u6709\u5fd7\uff0c\u8c01 \u4e5f\u4e0d\u80fd\u76f8\u5f3a\uff0c\u8c22\u4e16\u5144\u65e2\u5982\u6b64\uff0c\u6211\u6885\u67d0\u4eba\u656c\u4f69\u5f97\u5f88\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u4e00\u987f\uff0c\u53d8\u5f97\u51b7\u9177\u800c\u4e25\u5cfb\uff0c\u8f6c\u8138\u5411\u8d64\u9633\u9053\u957f\u8bf4\u9053\uff0c\u201c\u4e09\u5e74\u4ee5\u524d\uff0c\u4f60\u4eec\u4e94\u5927\u5b97\u6d3e \u5728\u6cf0\u5c71\u7edd\u9876\u4e1c\u9080\u6c5f\u6e56\u540c\u9053\uff0c\u540c\u8d74\u6cf0\u5c71\uff0c\u4e89\u90a3\u5929\u4e0b\u5251\u672f\u7b2c\u4e00\u7684\u79f0\u53f7\u3002\u201d\u8bf4\u81f3\u6b64\u5904\u4ed6\u4ef0\u5929\u957f \u7b11\u4e00\u9635\uff0c\u5197\u957f\u7684\u7b11\u58f0\uff0c\u9707\u5f97\u6885\u679d\u4e0a\u7684\u82b1\u74e3\uff0c\u6f31\u6f31\u98d8\u843d\u3002\u4ed6\u5389\u58f0\u53c8\u8bf4\uff1a\u201c\u60f3\u6211\u4e03\u5999\u795e\u541b\uff0c \u600e\u4f1a\u4e0e\u4f60\u4eec\u8fd9\u822c\u6cbe\u540d\u9493\u8a89\u7684\u72c2\u5f92\uff0c\u53bb\u4e89\u90a3\u52b3\u4ec0\u5b50\u7684\u540d\u53f7\uff0c\u4f60\u4eec\u65e2\u7136\u559c\u6b22\uff0c\u5c31\u8ba9\u4f60\u4eec\u81ea\u79f0 \u5251\u672f\u5929\u4e0b\u7b2c\u4e00\uff0c\u53c8\u6709\u4f55\u59a8\uff0c\u4f46\u662f\u6211\u5374\u4e07\u4e07\u6599\u60f3\u4e0d\u5230\uff0c\u81ea\u79f0\u6b66\u6797\u6b63\u5b97\u7684\u4e00\u6d3e\u638c\u95e8\u4eba\uff0c\u5374\u8054 \u624b\u505a\u4e0b\u90a3\u5351\u9119\u7684\u884c\u4e3a\uff0c\u4e94\u5251\u5408\u58c1\uff0c\u5728\u4f1a\u671f\u524d\u4e00\u5929\uff0c\u5c31\u5c06\u6211\u81f3\u53cb\u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\u4f24\u5728\u5929\u7ec5 \u7011\u4e0b\u2026\u2026\u201d \u3000\u3000\u5389\u9e57\u80a9\u5fae\u95ea\u5904\uff0c\u72ec\u81ea\u63a0\u5230\u6885\u5c71\u6c11\u7684\u9762\u524d\uff0c\u622a\u4f4f\u4e86\u4ed6\u7684\u8bdd\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u8bdd\u4e5f\u4e0d \u7528\u591a\u8bf4\u4e86\uff0c\u90a3\u5434\u8bcf\u4e91\u662f\u548e\u7531\u81ea\u53d6\uff0c\u53c8\u6028\u5f97\u4e86\u8c01\uff01\u4eca\u65e5\u6211\u7b49\u7531\u8fdc\u5904\u800c\u6765\uff0c\u5c31\u4e3a\u7684\u662f\u89c1\u8bc6\u4f60 \u4e03\u5999\u795e\u541b\u5999\u7edd\u5929\u4e0b\u7684\u51e0\u6837\u73a9\u610f\u513f\uff0c\u4f60\u5212\u51fa\u9053\u513f\u6765\uff0c\u6211\u4eec\u603b\u4e00\u4e00\u5949\u966a\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u8bf4\u9053\uff1a\u201c\u53ea\u6015\u4f60\u4eec\u8fd8\u4e0d\u591f\u8d44\u683c\u6765\u89c1\u8bc6\u6211\u7684\u2018\u4e03\u827a\u2019\u3002\u201d\u8d64\u9633\u9053\u957f\u542c\u6885\u5c71\u6c11\u8fde \u9a82\u5e26\u635f\uff0c\u5374\u4ecd\u795e\u8272\u81ea\u82e5\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u81ea\u7136\uff0c\u4e03\u5999\u795e\u541b\uff0c\u4ee5\u5251\u672f\u3001\u8f7b\u529f\u3001\u638c\u529b\uff0c\u4ee5\u53ca\u8bd7\u3001 \u4e66\u3001\u753b\u3001\u8272\uff0c\u5999\u7edd\u5929\u4e0b\uff0c\u60f3\u6211\u7b49\u53ea\u662f\u4e00\u4ecb\u6b66\u592b\uff0c\u90a3\u91cc\u53ca\u5f97\u4e0a\u795e\u541b\u7684\u6587\u6b66\u53cc\u5168\u3002\u201d \u3000\u3000\u5389\u9e57\u53c8\u5728\u4e00\u65c1\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u5c24\u5176\u662f\u90a3\u6700\u540e\u4e00\u6837\uff0c\u6211\u4eec\u66f4\u662f\u671b\u5c18\u83ab\u53ca\u3002\u201d \u3000\u3000\u8d64\u9633\u9053\u957f\u7b11\u7b11\u9053\uff1a\u201c\u5389\u5927\u4fa0\u6b64\u8bdd\u8bf4\u5f97\u6781\u662f\uff0c\u795e\u541b\u98ce\u6d41\u501c\u50a5\uff0c\u90a3\u662f\u6211\u4eec\u51e0\u4e2a\u69fd\u8001\u5934\u5b50 \u6240\u4e07\u4e07\u4e0d\u53ca\u7684\uff0c\u4eca\u65e5\u5728\u4e0b\u4e0e\u5d06\u5cd2\u7684\u5251\u795e\u5389\u5927\u4fa0\uff0c\u5ce8\u5d4b\u7684\u82e6\u5eb5\u4e0a\u4eba\uff0c\u4ee5\u53ca\u70b9\u82cd\u7684\u843d\u82f1\u5251\u8c22 \u8d24\u5f1f\uff0c\u4e13\u7a0b\u6765\u6b64\u8df5\u7ea6\uff0c\u53ea\u60f3\u9886\u6559\u795e\u541b\u7684\u5251\u672f\u548c\u638c\u529b\uff0c\u82e5\u662f\u6211\u4eec\u80fd\u4fa5\u5e78\u548c\u795e\u541b\u5404\u80dc\u4e8c\u573a\uff0c \u90a3\u5c31\u518d\u9886\u6559\u795e\u541b\u7684\u8f7b\u529f\uff0c\u81f3\u65bc\u8bd7\u3001\u4e66\u3001\u753b\u3001\u8272\uff0c\u6211\u4eec\u5374\u662f\u65e0\u6cd5\u5949\u966a\u7684\u4e86\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u6837\u6700\u597d\uff0c\u9996\u5148\u6211\u5c31\u8981\u9886\u6559\u8fd9\u4f4d\u81ea\u79f0\u5929\u4e0b\u7b2c\u4e00\u5251\u7684\u5389\u5927\u4fa0\uff0c\u7a76\u7adf \u6709\u4ec0\u4e48\u7cbe\u5999\u62db\u672f\uff0c\u6562\u8fd9\u6837\u5356\u72c2\u3002\u201d \u3000\u3000\u4ed6\u5634\u8272\u6cdb\u8d77\u4e00\u4e1d\u9634\u68ee\u7684\u6740\u673a\uff0c\u8bf4\u9053\uff1a\u201c\u7136\u540e\u5462\uff0c\u5404\u4f4d\u6709\u4ec0\u4e48\u51fa\u7c7b\u62d4\u8403\u7684\u529f\u592b\u5c3d\u7ba1\u4fbf \u51fa\u6765\uff0c\u6211\u6885\u67d0\u4eba\u603b\u4e0d\u6559\u5404\u4f4d\u5931\u671b\u5c31\u662f\u4e86\uff0c\u53cd\u6b63\u4eca\u65e5\u8eab\u5165\u6b64\u8c37\u7684\u4eba\uff0c\u82e5\u4e0d\u80fd\u80dc\u5f97\u4e86\u6211\u6885\u67d0 \u4eba\uff0c\u8981\u60f3\u6d3b\u7740\u56de\u53bb\uff0c\u53ea\u6015\u529e\u4e0d\u5230\u7684\u4e86\uff0c\u6211\u6885\u67d0\u4eba\u82e5\u662f\u8d25\u5728\u5404\u4f4d\u624b\u91cc\uff0c\u4e5f\u4e0d\u60f3\u6d3b\u7740\u56de\u53bb\uff0c \u6211\u8bdd\u5df1\u8bb2\u660e\uff0c\u5404\u4f4d\u4e5f\u4e0d\u5fc5\u8bb2\u4ec0\u4e48\u6c5f\u6e56\u9053\u4e49\uff0c\u53ea\u7ba1\u62ff\u5bf9\u4ed8\u5434\u8bcf\u4e91\u7684\u624b\u6bb5\u6765\u5bf9\u4ed8\u6211\u597d\u4e86\u3002\u201d \u3000\u3000\u6b64\u523b\u591c\u8272\u5df2\u6d53\uff0c\u5929\u4e0a\u65e0\u661f\u65e0\u6708\uff0c\u4f46\u886c\u7740\u6ee1\u5730\u767d\u96ea\uff0c\u5929\u8272\u4ecd\u4e0d\u663e\u5f97\u592a\u6697\uff0c\u518d\u52a0\u4e0a\u4ed6\u4eec \u4ff1\u662f\u5185\u529b\u9ad8\u6df1\u7684\u4eba\u7269\uff0c\u5728\u9ed1\u6697\u4e2d\u89c6\u7269\uff0c\u867d\u672a\u89c1\u5b9b\u5982\u767d\u663c\uff0c\u4f46\u4e5f\u6e05\u695a\u5f97\u5f88\uff0c\u6885\u5c71\u6c11\u76ee\u5149\u5982 \u7535\uff0c\u6781\u5feb\u5730\u81ea\u4ed6\u4eec\u56db\u4eba\u8138\u4e0a\u63a0\u8fc7\uff0c\u89c1\u4ed6\u4eec\u9762\u4e0a\u867d\u4e0d\u5b9a\uff0c\u4f46\u5374\u4e2a\u4e2a\u6210\u7af9\u5728\u80f8\uff0c\u65e9\u5df2\u6709\u4e86\u5b89 \u6392\u4f3c\u7684\u3002 \u3000\u3000\u4ed6\u5fc3\u7533\u4e0d\u7981\u4e00\u52a8\uff0c\u4f46\u8f6c\u5ff5\u53c8\u60f3\u9053\uff1a\u201c\u5373\u4f7f\u4ed6\u4eec\u6709\u4e86\u4ec0\u4e48\u8be1\u8ba1\uff0c\u96be\u9053\u6211\u4e0d\u80fd\u8bc6\u7834\uff0c\u4f55 \u51b5\u4ed6\u4eec\u7eb5\u7136\u56db\u4eba\u8054\u624b\uff0c\u4e5f\u672a\u5fc5\u4f24\u5f97\u4e86\u6211\u3002\u201d \u3000\u3000\u5251\u795e\u5389\u9e57\u51b7\u54fc\u4e00\u58f0\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u5012\u771f\u662f\u5feb\u4eba\u5feb\u8bed\uff0c\u8bf4\u8bdd\u5e72\u51c0\u5229\u843d\uff0c\u6b63\u5408\u6211\u5389\u67d0\u813e\u80c3\uff0c \u73b0\u5728\u6700\u597d\u95f2\u8bdd\u5c11\u8bf4\uff0c\u65e9\u4f5c\u4e2a\u4e86\u65ad\u3002\u201d \u3000\u3000\u4ed6\u4f38\u624b\u4e00\u62c9\u80f8\u524d\u7684\u6d3b\u6263\uff0c\u5c06\u957f\u5251\u64a4\u5230\u624b\u4e2d\uff0c\u968f\u624b\u4e00\u6296\uff0c\u53ea\u89c1\u5251\u661f\u70b9\u70b9\u5b9b\u5982\u6ee1\u5929\u82b1\u96e8\uff0c \u7f24\u7eb7\u98de\u843d\uff0c\u7adf\u662f\u4e00\u53e3\u540d\u5251\u3002 \u3000\u3000\u4ed6\u5c06\u5251\u9798\u5e73\u7740\u63a8\u51fa\uff0c\u90a3\u5251\u9798\u50cf\u662f\u6709\u4eba\u6258\u7740\uff0c\u5e73\u5e73\u5730\u843d\u5728\u4e00\u5757\u7a81\u51fa\u7684\u5c71\u5ca9\u4e0a\u3002 \u3000\u3000\u6885\u5c71\u6c11\u89c1\u5389\u9e57\u9732\u8fd9\u4e00\u624b\uff0c\u5fc3\u60f3\u76db\u540d\u4e4b\u4e0b\uff0c\u786e\u65e0\u865a\u58eb\uff0c\u4eca\u65e5\u4e00\u4f1a\uff0c\u5012\u771f\u662f\u81ea\u5df1\u80dc\u8d25\u5b58 \u4ea1\u7684\u5173\u952e\uff0c\u6b64\u56db\u4eba\u9664\u4e86\u843d\u82f1\u5251\u8c22\u957f\u537f\u5916\uff0c\u65e0\u4e00\u4e0d\u662f\u5728\u6b66\u6797\u4e2d\u4e45\u4eab\u76db\u540d\u4e4b\u58eb\uff0c\u81ea\u5df1\u867d\u4ee5\u6b66 \u672f\u540d\u6ee1\u5929\u4e0b\uff0c\u4f46\u4e0e\u4e94\u5927\u5b97\u6d3e\u7684\u638c\u95e8\uff0c\u5c1a\u662f\u7b2c\u4e00\u6b21\u52a8\u624b\u3002 \u3000\u3000\u5389\u94a8\u65b9\u81ea\u8bf4\u8bdd\uff0c\u90a3\u4e00\u76f4\u6c89\u9ed8\u7740\u7684\u82e6\u5eb5\u4e0a\u4eba\u888d\u8896\u4e00\u62c2\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u795e\u541b\u6240\u8bf4\u6781\u662f\uff0c \u4eca\u65e5\u5728\u6b64\u805a\u4f1a\u4e4b\u4eba\uff0c\u8c05\u5df1\u65e9\u5c06\u751f\u6b7b\u7f6e\u65bc\u5ea6\u5916\uff0c\u4f46\u8d2b\u50e7\u4e0d\u662f\u8bf4\u53e5\u72c2\u8bdd\uff0c\u6211\u7b49\u6570\u4eba\u5728\u6b66\u6797\u4e2d \u867d\u4e0d\u6562\u8bf4\u662f\u6cf0\u5c71\u5317\u6597\uff0c\u4f46\u4ff1\u975e\u788c\u788c\u4e4b\u58eb\uff0c\u82e5\u50cf\u90a3\u4e9b\u6c5f\u6e56\u83bd\u6c49\u4e00\u6837\u5730\u80e1\u780d\u4e71\u6740\uff0c\u52a8\u624b\u8fc7\u62db\uff0c \u5c82\u975e\u6709\u5931\u8eab\u4efd\uff0c\u4f9d\u8d2b\u50e7\u6240\u89c1\uff0c\u5012\u6709\u4e00\u4e2a\u66f4\u597d\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u53cc\u7709\u4e00\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u4e0a\u4eba\u6709\u4f55\u9ad8\u89c1\uff0c\u53ea\u7ba1\u8bf4\u51fa\u6765\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\u9635\u81ea\u662f\u8f83\u91cf\u5251\u672f\uff0c\u4f46\u4e5f\u4e0d\u5fc5\u8fc7\u62db\uff0c\u201d\u4ed6\u5fae\u5fae\u6307\u4e86\u6307\u8c37\u91cc\u5bbd\u9614 \u7684\u96ea\u5730\uff0c\u8bf4\uff1a\u201c\u6211\u4eec\u5c31\u5728\u8fd9\u96ea\u5730\u4e0a\uff0c\u5212\u4e2a\u5708\u5b50\uff0c\u6211\u4e0e\u8d64\u9633\u9053\u957f\uff0c\u5389\u3001\u8c22\u4e8c\u4f4d\u5404\u5360\u4e00\u65b9\uff0c \u795e\u541b\u53ea\u8981\u80fd\u5728\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\u95ef\u51fa\u6211\u7b49\u6240\u5e03\u4e4b\u5251\u9635\uff0c\u4fbf\u7b97\u6211\u7b49\u8f93\u4e86\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u5c06\u8fd9\u4e3b\u610f\u5728\u5fc3\u4e2d\u7565\u4e00\u63e3\u5ea6\uff0c\u4fbf\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\u3002\u201d \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u9053\uff1a\u201c\u90a3\u6211\u5c31\u8bf7\u795e\u541b\u5148\u5212\u4e2a\u5708\u5b50\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u56de\u8eab\u6298\u4e86\u4e00\u6bb5\u6885\u679d\uff0c\u90a3\u679d\u4e0a\u82b1\u5f00\u5f97\u751a\u662f\u7e41\u884d\uff0c\u7ea6\u6709\u4e8c\u4e09\u5341\u6735\uff0c\u4ed6\u63e1\u7740\u90a3\u6bb5\u6885 \u679d\uff0c\u5185\u529b\u6e17\u5165\u679d\u91cc\uff0c\u679d\u4e0a\u7684\u6885\u82b1\u5ffd\u7136\u4e00\u8d77\u843d\u4e0b\u6765\uff0c\u843d\u4eba\u4ed6\u5bbd\u5927\u8863\u8896\u91cc\uff0c\u4ed6\u7b11\u9053\uff1a\u201c\u60f3\u4e0d \u5230\u4eca\u65e5\u6211\u4e5f\u505a\u4e86\u4e2a\u6467\u82b1\u4e4b\u5ba2\u3002\u201d \u3000\u3000\u968f\u7740\u8bf4\u8bdd\uff0c\u4ed6\u8863\u8896\u4e00\u626c\uff0c\u90a3\u6570\u5341\u6735\u6885\u82b1\u5ffd\u5730\u4e00\u9f50\u81ea\u4ed6\u8896\u4e2d\u98de\u51fa\uff0c\u7eb7\u7eb7\u843d\u5728\u96ea\u5730\u4e0a\uff0c \u7adf\u6446\u6210\u4e00\u4e2a\u6781\u6574\u9f50\u7684\u5708\u5b50\uff0c\u9c9c\u7ea2\u7684\u6885\u82b1\uff0c\u886c\u5728\u6d01\u767d\u7684\u96ea\u5730\u4e0a\uff0c\u5f62\u6210\u4e00\u526f\u6781\u7f8e\u7684\u56fe\u753b\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u89c1\u4e86\uff0c\u8d5e\u8bb8\u7684\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u4ed6\u6240\u8d5e\u8bb8\u7684\uff0c\u5012\u4e0d\u662f\u4e03\u5999\u795e\u541b\u6240\u65bd\u7684\u90a3\u79cd\u8d85\u8d8a \u7684\u624b\u6cd5\uff0c\u800c\u662f\u4ed6\u89c1\u4e03\u5999\u795e\u541b\u6240\u5e03\u7684\u5708\u5b50\u6781\u5c0f\uff0c\u987b\u77e5\u5708\u5b50\u5e03\u7684\u8d8a\u5c0f\uff0c\u90a3\u5728\u5708\u5b50\u91cc\u7684\u4eba\u8d8a\u96be \u95ef\u51fa\uff0c\u4ed6\u4eec\u5bf9\u4eca\u65e5\u4e4b\u4f1a\uff0c\u5fc3\u4e2d\u65e9\u6709\u8ba1\u8f83\uff0c\u5bf9\u8fd9\u7b2c\u4e00\u9635\u7684\u8f93\u8d62\uff0c\u867d\u672b\u5728\u610f\uff0c\u4f46\u89c1\u90a3\u4e03\u5999\u795e \u541b\u5bf9\u8fd9\u79cd\u6709\u5173\u751f\u6b7b\u7684\u4e8b\u60c5\uff0c\u4e5f\u7edd\u4e0d\u53d6\u5de7\uff0c\u4e00\u65b9\u9762\u56fa\u662f\u8d5e\u8bb8\uff0c\u53e6\u4e00\u65b9\u9762\u5374\u60ca\u60e7\u7740\u4e03\u5999\u795e\u541b \u7684\u6001\u5ea6\uff0c\u6015\u4ed6\u4e5f\u65e9\u6709\u6210\u7af9\u5728\u80f8\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u8eab\u8eaf\u6beb\u672b\u4f5c\u52bf\uff0c\u4f17\u4eba\u773c\u795e\u4e00\u4e71\uff0c\u4ed6\u5df2\u7ad9\u5728\u90a3\u5708\u5b50\u91cc\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c31\u8bf7\u5404 \u4f4d\u8d76\u7d27\u8fc7\u6765\uff0c\u8ba9\u6211\u89c1\u8bc6\u89c1\u8bc6\u6b66\u6797\u4e2d\u65e9\u5df2\u76db\u4f20\u7684\u540d\u5bb6\u5251\u6cd5\u3002\u201d \u3000\u3000\u5251\u795e\u5389\u9e57\u7b2c\u4e00\u4e2a\u98de\u7eb5\u51fa\u53bb\uff0c\u7ad9\u5728\u5708\u5b50\u5357\u65b9\uff0c\u8d64\u9633\u9053\u957f\uff0c\u82e6\u5eb5\u4e0a\u4eba\u548c\u843d\u82f1\u5251\u8c22\u957f\u537f\u4e5f \u5404\u7ad9\u4e00\u65b9\uff0c\u5404\u81ea\u64a4\u51fa\u8eab\u540e\u7684\u5251\u3002\u8d64\u9633\u9053\u957f\u5251\u5c16\u5f80\u4e0a\u6311\uff0c\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\u9635\u65e2\u662f\u8f83\u5251\uff0c\u795e\u541b \u5c31\u8bf7 \u3000\u3000\u5feb\u4e9b\u4eae\u5251\u3002\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u624b\u91cc\u4ecd\u62ff\u7740\u90a3\u6bb5\u4e0a\u9762\u5df2\u7136\u6ca1\u6709\u82b1\u74e3\u7684\u6885\u679d\uff0c\u5f00\u53e3\u8bf4\u906d\uff1a\u201c\u8fd1\u5341\u5e74\u6765\u6211\u6885\u67d0 \u4eba\u8fd8\u6ca1\u6709\u52a8\u8fc7\u5175\u5203\uff0c\u4eca\u5929\u4e48\uff0c\u5404\u4f4d\u90fd\u662f\u6b66\u6797\u4e2d\u9876\u5c16\u513f\u9ad8\u624b\uff0c\u6211\u6885\u67d0\u4eba\u4e0d\u5f97\u4e0d\u7834\u6b21\u4f8b\uff0c\u5c31 \u7528\u8fd9\u6bb5\u6811\u679d\uff0c\u6765\u8ba8\u6559\u8ba8\u6559\u5404\u4f4d\u7684\u9ad8\u62db\uff0c\u5404\u4f4d\u5c31\u8bf7\u52a8\u624b\u5427\u3002\u201d \u3000\u3000\u56db\u4eba\u542c\u4ed6\u7adf\u5982\u6b64\u8bf4\uff0c\u8138\u4e0a\u4ff1\u662f\u4e00\u53d8\u3002\u4e03\u5999\u795e\u541b\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u5404\u4f4d\u5207\u83ab\u5c0f\u770b\u6211\u8fd9\u6bb5\u6811 \u679d\uff0c\u5b83\u5728\u6211\u6885\u67d0\u4eba\u624b\u4e0a\uff0c\u4f55\u5f02\u5229\u5251\u3002\u201d \u3000\u3000\u8d64\u9633\u9053\u957f\u518d\u662f\u6db5\u517b\u529f\u6df1\uff0c\u6b64\u523b\u4e5f\u662f\u4f5c\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u795e\u541b\u65e2\u5982\u6b64\u8bf4\uff0c\u6211\u7b49\u5c31\u653e\u8086\u4e86\u3002\u201d \u3000\u3000\u201c\u8bed\u97f3\u65b9\u843d\uff0c\u90a3\u56db\u67c4\u672c\u9759\u6b62\u7740\u7684\u957f\u5251\uff0c\u5ffd\u5982\u7075\u86c7\uff0c\u4ea4\u526a\u800c\u51fa\uff0c\uff0c\u602a\u5c31\u602a\u5728\u90a3\u56db\u67c4\u5251 \u5374\u672a\u5411\u6885\u5c71\u6c11\u8eab\u4e0a\u62db\u547c\uff0c\u53ea\u5728\u4ed6\u56db\u5468\uff0c\u7ed3\u8d77\u4e00\u7247\u5149\u5e55\u3002 \u3000\u3000\u6885\u5c71\u6c11\u53ea\u89c9\u4ed6\u5b9b\u5982\u7f6e\u8eab\u5728\u4e00\u4e2a\u6781\u5927\u7684\u73bb\u7483\u7f69\u5b50\u91cc\uff0c\u56db\u8fb9\u5149\u8292\u8000\u773c\u3002 \u3000\u3000\u90a3\u5251\u5f0f\u751a\u65f6\u8be1\u5f02\uff0c\u5374\u4e5f\u4e0d\u662f\u6b66\u5f53\u3001\u5ce8\u5d4b\u3001\u70b9\u82cd\u3001\u8154\u5cd2\uff0c\u4efb\u4f55\u4e00\u6d3e\u7684\u5251\u672f\uff0c\u53ea\u7ba1\u5251\u5f0f \u8fde\u7ef5\uff0c\u5982\u957f\u6c5f\u5927\u6cb3\u4e4b\u6c34\uff0c\u6ed4\u6ed4\u800c\u6765\uff0c\u53ef\u662f\u53ea\u8981\u4ed6\u9759\u7acb\u4e0d\u52a8\uff0c\u4e5f\u4e0d\u80fd\u4f24\u5f97\u4e86\u4ed6\u3002 \u3000\u3000\u987b\u77e5\u81ea\u53e4\u4ee5\u6765\uff0c\u6b66\u6797\u4e2d\u7684\u5251\u6cd5\uff0c\u4e0d\u662f\u9632\u8eab\uff0c\u4fbf\u662f\u4f24\u4eba\uff0c\u50cf\u8fd9\u79cd\u65e2\u4e0d\u9632\u8eab\uff0c\u53c8\u4e0d\u4f24\u4eba \u7684\u5251\u6cd5\uff0c\u7684\u786e\u662f\u95fb\u6240\u672a\u95fb\uff0c\u4f60\u82e5\u4e0d\u52a8\uff0c\u5c31\u65e0\u6cd5\u8d70\u51fa\u8fd9\u4e2a\u5708\u5b50\uff0c\u4f60\u82e5\u60f3\u52a8\uff0c\u90a3\u56db\u9053\u914d\u5408\u5f97 \u5929\u8863\u65e0\u7f1d\u7684\u5251\u5149\uff0c\u6839\u672c\u65e0\u6cd5\u7834\u53bb\uff0c\u4f11\u8bf4\u662f\u4eba\uff0c\u5c31\u662f\u8fde\u5c18\u57c3\uff0c\u90fd\u65e0\u6cd5\u98de\u5165\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u5728\u5251\u5149\u5185\u9759\u7acb\u83ab\u7ea6\u534a\u76cf\u8336\u65f6\u5149\uff0c\u5374\u82e6\u601d\u4e0d\u5f97\u7834\u9635\u4e4b\u6cd5\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u201c\u602a\u4e0d \u5f97\u4ed6\u4eec\u63d0\u5021\u7528\u6b64\u6cd5\uff0c\u539f\u6765\u7ec3\u5f97\u8fd9\u6837\u602a\u5f02\u597d\u5251\u5f0f\uff0c\u8fd9\u5012\u662f\u6211\u5148\u524d\u6240\u6ca1\u6709\u6599\u5230\u7684\uff0c\u6211\u53ea\u60f3\u4ed6 \u4eec\u56db\u5251\u5408\u58c1\uff0c\u8981\u80dc\u5b83\u867d\u975e\u7247\u523b\u5c31\u80fd\u505a\u5230\uff0c\u8981\u60f3\u95ef\u51fa\uff0c\u8fd8\u4e0d\u662f\u6613\u5982\u53cd\u638c\uff0c\u5374\u672b\u60f3\u5230\u2026\u2026\u201d \u3000\u3000\u4ed6\u6781\u7559\u5fc3\u5730\u770b\u770b\u90a3\u56db\u4eba\u7684\u5251\u5f0f\uff0c\u53ea\u662f\u5251\u5251\u4ff1\u662f\u4ea4\u9519\u800c\u51fa\uff0c\u5251\u5e26\u5fae\u8292\uff0c\u6781\u5feb\u7684\u632f\u52a8\u7740 \u5251\u5e45\uff0c\u5de7\u5999\u5730\u586b\u8865\u4e86\u5251\u4e0e\u5251\u4e4b\u95f4\u7684\u7a7a\u9699\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u5fc3\u4e2d\u4e0d\u7981\u6709\u4e9b\u540e\u6094\uff0c\u4ed6\u81ea\u601d\u9053\uff1a\u201c\u6211\u82e5\u5c06\u90a3\u67c4\u201c\u6885\u9999\u5251\u201d\u5e26\u6765\uff0c\u6b64\u523b\u4e5f\u53ef \u7528\u6570\u5341\u5e74\u6765\u82e6\u7814\u800c\u6210\u7684\u201c\u8f67\u679d\u5251\u5f0f\u201d\u7834\u53bb\u6b64\u9635\uff0c\u4f46\u73b0\u5728\u6211\u624b\u4e2d\u6240\u6301\u5374\u53ea\u662f\u4e00\u6bb5\u6811\u679d\uff0c\u8981 \u60f3\u5728\u8fd9\u56db\u4e2a\u540d\u5bb6\u624b\u4e2d\u7684\u5251\u91cc\uff0c\u8986\u7a7f\u800c\u51fa\uff0c\u90a3\u91cc\u80fd\u591f\u505a\u5230\u3002\u201d \u3000\u3000\u4ed6\u6b63\u601d\u5230\u6b64\u5904\uff0c\u5ffd\u89c1\u6709\u4e24\u6761\u4ea4\u9519\u7740\u7684\u5251\u5149\uff0c\u5fae\u548c\u76f8\u51fb\uff0c\u9535\u5730\u53d1\u51fa\u4e00\u4e1d\u8f7b\u9e23\u3002\u90a3\u672c\u662f \u6beb\u65e0\u7834\u7efd\u7684\u5251\u5f0f\uff0c\u56e0\u8fd9\u76f8\u51fb\uff0c\u4fbf\u505c\u987f\u4e86\u4e00\u4f1a\u3002 \u3000\u3000\u4f46\u90a3\u4ea6\u662f\u90a3\u4e48\u6e3a\u832b\u7684\u4e00\u5239\u90a3\uff0c\u77ed\u6682\u5f97\u50cf\u662f\u9ed1\u6697\u4e2d\u7684\u4e00\u95ea\u5149\u4eae\uff0c\u4e03\u5999\u795e\u541b\u624b\u4e2d\u7684\u6811\u679d\uff0c \u968f\u7740\u90a3\u5fc3\u91cc\u7684\u4e00\u4e2a\u6781\u5feb\u7684\u5ff5\u5934\uff0c\u5411\u90a3\u7a7a\u9699\u4e00\u5251\u523a\u53bb\uff0c\u5de6\u638c\u4e00\u7acb\uff0c\u638c\u98ce\u5982\u5200\uff0c\u6a2a\u5207\u5728\u90a3\u4e24 \u9053\u5251\u5149\u4e0a\u3002 \u3000\u3000\u539f\u6765\u6b64\u5251\u9635\u672c\u662f\u82e6\u5eb5\u4e0a\u4eba\u3001\u8d64\u9633\u9053\u957f\u3001\u5251\u795e\u5389\u9e57\u3001\u548c\u8ffd\u98ce\u5251\u5408\u7ec3\u800c\u6210\uff0c\u4e3a\u7684\u5374\u4e0d\u662f \u7528\u6765\u5bf9\u4ed8\u4e03\u5999\u795e\u541b\uff0c\u800c\u662f\u8981\u5230\u5c71\u4e0a\u53bb\u730e\u53d6\u4e00\u79cd\u6781\u5c11\u6709\u7684\u5cf0\u9e1f\uff0c\u6545\u6b64\u53ea\u5b88\u65e0\u653b\uff0c\u53ea\u662f\u8981\u5c06 \u90a3\u79cd\u5cf0\u9e1f\u56f0\u4f4f\u800c\u5df2\u3002 \u3000\u3000\u5230\u540e\u6765\u8ffd\u98ce\u5251\u8c22\u661f\u4e00\u6b7b\uff0c\u4ed6\u4eec\u5c06\u91c7\u96c6\u5cf0\u9e1f\u7684\u4e8b\u4e5f\u544a\u4e00\u6bb5\u843d\uff0c\u9042\u4e5f\u5c06\u6b64\u9635\u6401\u4e0b\u4e86\u3002 \u3000\u3000\u4f46\u540e\u6765\u4ed6\u4eec\u4e0e\u4e03\u5999\u795e\u541b\u6240\u8ba2\u4e09\u5e74\u4e4b\u7ea6\uff0c\u65e5\u76ca\u8feb\u8fd1\uff0c\u4e03\u5999\u795e\u541b\u5728\u6b66\u6797\u4e2d\u662f\u6709\u540d\u7684\u5fc3\u72e0 \u624b\u8fa3\uff0c\u5f80\u5f80\u5728\u8c08\u7b11\u4e2d\uff0c\u5236\u4eba\u6b7b\u547d\uff0c\u800c\u4e14\u6b66\u529f\u6df1\u7edd\uff0c\u884c\u8d70\u6c5f\u6e56\u591a\u5e74\uff0c\u4ece\u672a\u6709\u4eba\u5728\u4ed6\u624b\u4e2d\u8d70 \u8fc7\u4e8c\u5341\u62db\u7684\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u624d\u4f1a\u540c\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u91cd\u7ec3\u6b64\u9635\uff0c\u4f46\u5728\u8fd9\u5e76\u4e0d\u592a\u957f\u7684\u4e00\u6bb5\u65e5\u5b50\uff0c\u529f\u529b\u539f\u672c\u5c31 \u7a0d\u900a\u7684\u8c22\u957f\u537f\uff0c\u81ea\u7136\u65e0\u6cd5\u5c06\u5251\u5f0f\u548c\u8fd9\u4e09\u4eba\u914d\u5408\u50cf\u8ffd\u98ce\u5251\u4e00\u6837\u4e25\u5bc6\uff0c\u6545\u6b64\u624d\u6709\u4e00\u62db\u4e4b\u6f0f\u3002 \u3000\u3000\u4f46\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5fc3\u601d\u53cd\u5e94\u4e4b\u901f\uff0c\u53c8\u5c82\u662f\u5e38\u4eba\u6240\u80fd\u4f01\u53ca\u7684\u3002 \u3000\u3000\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u53ea\u89c9\u5f97\u624b\u8155\u4e00\u632f\uff0c\u6709\u4e00\u79cd\u602a\u5f02\u7684\u529b\u91cf\uff0c\u4f7f\u4ed6\u6df7\u8eab\u4e00\u98a4\uff0c\u624b\u91cc\u7684\u5251\u81ea \u7136\u4e5f\u8fdf\u949d\u4e0b\u6765\uff0c\u65e0\u6cd5\u518d\u914d\u5408\u5176\u4f59\u4e09\u4eba\u7684\u5251\u5f0f\u4e86\uff0c\u90a3\u672c\u662f\u4e25\u5bc6\u800c\u9738\u9053\u7684\u5251\u9635\uff0c\u4e5f\u56e0\u4ed6\u8fd9\u5fae \u4e00\u8fdf\u949d\uff0c\u800c\u677e\u61c8\u4e0b\u6765\uff0c\u5251\u4e0e\u5251\u4e4b\u95f4\uff0c\u5f00\u59cb\u6709\u4e86\u7a7a\u9699\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u4e58\u52bf\u5de6\u80a9\u6b3a\u4e0a\uff0c\u53f3\u624b\u7684\u6885\u679d\u5316\u505a\u5343\u767e\u6761\u98de\u5f71\uff0c\u70b9\u70b9\u5411\u90a3\u7a7a\u9699\u4e4b\u95f4\u523a\u8fdb\uff0c\u90a3 \u4e00\u79cd\u6781\u5feb\u7684\u6296\u52a8\uff0c\u4f7f\u5f97\u672c\u5df2\u6e10\u5f62\u677e\u61c8\u7684\u5251\u9635\uff0c\u66f4\u5f62\u6563\u4e71\u4e86\u3002 \u3000\u3000\u5251\u795e\u5389\u9e57\u4e00\u770b\u501f\u52bf\u6709\u53d8\uff0c\u84e6\u5730\u957f\u5251\u4e00\u5f15\uff0c\u9000\u51fa\u90a3\u672c\u5251\u5f0f\u8fde\u7ef5\u914d\u5408\u7684\u5251\u9635\uff0c\u957f\u5251\u81ea\u4e0a \u800c\u4e0b\uff0c\u201c\u957f\u8679\u7ecf\u5929\u201d\u5e26\u8d77\u4e00\u9053\u6de1\u9752\u7684\u5149\u8292\uff0c\u5411\u4e03\u5999\u795e\u541b\u8fde\u80a9\u5e26\u80cc\uff0c\u5237\u5730\u4e00\u5251\u523a\u4e0b\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5fae\u4e00\u9519\u6b65\uff0c\u8f7b\u677e\u5730\u907f\u5f00\u6b64\u5251\uff0c\u6885\u679d\u6a2a\u626b\u65f6\uff0c\u624b\u8155\u4e00\u6c89\uff0c\u679d\u5934\u5de7\u5999\u5730\u642d\u5728\u843d\u82f1 \u5251\u8c22\u957f\u537f\u7684\u5251\u8eab\uff0c\u5fae\u4e00\u7528\u529b\uff0c\u8c22\u957f\u537f\u76f4\u89c9\u6709\u4e00\u80a1\u5927\u529b\u81ea\u5251\u8eab\u6e17\u4eba\uff0c\u5fd9\u4e5f\u4f7f\u51fa\u529f\u529b\uff0c\u6765\u548c \u8fd9\u80a1\u529b\u91cf\u76f8\u6297\u3002 \u3000\u3000\u8bf4\u6765\u8bdd\u957f\uff0c\u7136\u800c\u8fd9\u5374\u662f\u970e\u65f6\u95f4\u4e8b\uff0c\u5389\u9e57\u4e00\u5251\u843d\u7a7a\uff0c\u957f\u5251\u731b\u987f\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u201c\u6885\u82b1\u4e09 \u5f04\u201d\u5251\u5f0f\u505a\u4e09\u4e2a\u5708\u5b50\u76f4\u53d6\u4e03\u5999\u795e\u541b\u201c\u80a9\u4e95\u201d\u201c\u4e73\u6cc9\u201d\u4e09\u4e2a\u8981\u7a74\uff0c\u5251\u98ce\u51cc\u5389\uff0c\u7684\u786e\u662f\u5185\u5bb6 \u9ad8\u624b\u3002 \u3000\u3000\u90a3\u8fb9\u82e6\u5eb5\u4e0a\u4eba\u4e0e\u8d64\u9633\u9053\u957f\u89c1\u5251\u9635\u5df1\u4e71\uff0c\u9042\u4e5f\u6beb\u4e0d\u8003\u8651\u5730\u5404\u523a\u51fa\u4e00\u5251\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u6240\u5212\u7684\u5708\u5b50\uff0c\u672c\u5c31\u6781\u5c0f\uff0c\u82e6\u5eb5\u4e0a\u4eba\u3001\u8d64\u9633\u9053\u957f\u548c\u5251\u795e\u5389\u9e57\u7814\u53d1\u51fa\u7684\u5251\u5f0f\uff0c \u5728\u8fd9\u6781\u5c0f\u7684\u5708\u5b50\u540c\u5411\u4e03\u5999\u795e\u541b\u523a\u53bb\uff0c\u4ed6\u4eec\u672c\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u5239\u90a3\u95f4\u53ea\u89c9\u9752\u8292\u7d2b\u7535\uff0c\u4ea4\u51fb\u800c \u6765\u3002 \u3000\u3000\u8fd9\u5374\u4e5f\u6b63\u662f\u4e03\u5999\u795e\u541b\u6240\u5e0c\u671b\u7684\uff0c\u4ed6\u624b\u4e2d\u7684\u6885\u679d\u7a81\u5730\u4e00\u677e\uff0c\u843d\u82f1\u5251\u65e9\u5df2\u6ee1\u84c4\u529f\u529b\u7684\u5251\uff0c \u6b64\u523b\u56e0\u5bf9\u65b9\u52b2\u529b\u987f\u6cc4\uff0c\u76f4\u5982\u79bb\u5f26\u4e4b\u5251\uff0c\u4e0d\u5f97\u4e0d\u53d1\uff0c\u7adf\u5411\u8d64\u9633\u9053\u957f\u548c\u82e6\u5eb5\u4e0a\u4eba\u7684\u5251\u5149\u523a\u51fa\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u5251\uff0c\u662f\u6bd5\u751f\u529f\u529b\u6240\u805a\uff0c\u5251\u8eab\u672a\u5230\uff0c\u5df2\u6709\u4e00\u80a1\u52b2\u529b\uff0c\u5411\u5251\u5149\u4e2d\u51fb\u5230\uff0c\u65bc\u662f\u82e6\u5eb5\u3001 \u8d64\u9633\u4e24\u4eba\u7684\u5251\u98ce\u81ea\u662f\u4e00\u504f\uff0c\u4e03\u5999\u795e\u541b\u811a\u6b65\u8ff7\u8e2a\uff0c\u5411\u5de6\u5fae\u4e00\u4fa7\u8eab\uff0c\u4e00\u58f0\u66b4\u559d\uff0c\u53cc\u638c\u9f50\u626c\uff0c \u96c4\u539a\u7684\u638c\u529b\uff0c\u786c\u751f\u751f\u5730\u51fb\u504f\u4e86\u5251\u795e\u5389\u9e57\u7684\u62db\u5f0f\uff0c\u811a\u4e0b\u7ec6\u788e\u5730\u8e29\u7740\u811a\u6b65\uff0c\u5728\u8fd9\u56db\u5251\u4e2d\u5df1\u5fae \u504f\u7684\u7a7a\u9699\u4e2d\u4ece\u5251\u5149\u91cc\u6781\u5feb\u5730\u95ea\u4e86\u51fa\u53bb\uff0c\u4e00\u58f0\u957f\u7b11\uff0c\u4ed6\u5df2\u8fdc\u8fdc\u5730\u7ad9\u5728\u5251\u5708\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u8fb9\u56db\u4eba\u4e5f\u8fde\u5fd9\u6536\u56de\u5251\u5f0f\uff0c\u82e6\u5eb5\u4e0a\u4eba\u5927\u8e0f\u6b65\u8d70\u4e0a\u524d\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u795e\u541b\u771f\u597d\u8eab\u6cd5\uff0c\u8fd9 \u7b2c\u4e00\u9635\u5f53\u7136\u662f\u7b97\u6211\u7b49\u8f93\u4e86\u3002\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u7b2c\u4e8c\u9635\u53c8\u662f\u600e\u4e48\u4e2a\u6bd4\u6cd5\uff0c\u4e5f\u8bf7\u4e0a\u4eba\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u8bf4\u9053\uff1a\u201c\u8fd9\u7b2c\u4e8c\u9635\u5c31\u7531\u8001\u8872\u548c\u795e\u541b\u6765\u4e00\u8bd5\u638c\u529b\u3002\u201d \u3000\u3000\u8bf4\u7740\u4ed6\u8d70\u5230\u65b9\u624d\u4e03\u5999\u795e\u541b\u6240\u5e03\u4e0b\u7684\u6885\u82b1\u5708\u5b50\u65c1\uff0c\u4fef\u8eab\u62fe\u8d77\u4e00\u6735\u6885\u82b1\uff0c\u4ed6\u8fd9\u4e00\u62fe\u6885\u82b1\uff0c \u624d\u5bf9\u4e03\u5999\u795e\u541b\u7684\u624b\u6cd5\u8d77\u4e86\u66f4\u591a\u7684\u60ca\u8d5e\u3002 \u3000\u3000\u539f\u6765\u90a3\u6885\u82b1\u770b\u4f3c\u98d8\u843d\u5728\u96ea\u5730\u4e0a\uff0c\u4e0d\u751a\u7740\u529b\uff0c\u90a3\u77e5\u82b1\u8482\u5374\u6574\u6574\u5d4c\u5728\u96ea\u5730\u91cc\uff0c\u6735\u6735\u4ff1\u662f \u82b1\u6735\u671d\u4e0a\uff0c\u8fd9\u79cd\u624b\u6cd5\u786e\u662f\u4ed6\u751f\u5e73\u6240\u4ec5\u89c1\uff0c\u4ed6\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u4e03\u5999\u795e\u541b\u7684\u786e\u662f\u53ef\u7b97\u6b66\u6797\u4e2d\u4e00 \u4ee3\u602a\u6770\uff0c\u770b\u4ed6\u5e74\u8f7b\u5e76\u4e0d\u751a\u5927\uff0c\u90a3\u77e5\u5374\u6709\u5982\u6b64\u529f\u529b\uff0c\u82e5\u975e\u6211\u7b49\u65e9\u6709\u5b89\u6392\uff0c\u4eca\u65e5\u6211\u4e94\u5927\u5b97\u6d3e \u7684\u638c\u95e8\uff0c\u5c82\u975e\u90fd\u8981\u846c\u8eab\u5728\u8fd9\u4e94\u534e\u5c71\u91cc\u3002\u201d \u3000\u3000\u4f46\u4ed6\u4ecd\u663e\u5f97\u90a3\u4e48\u5b89\u7965\u548c\u4e0d\u5728\u610f\uff0c\u62ff\u7740\u90a3\u6735\u6885\u82b1\uff0c\u5bf9\u4e03\u5999\u795e\u541b\u8c03\u9053\uff1a\u201c\u795e\u541b\u7684\u529f\u529b\uff0c \u786e\u662f\u8001\u8872\u751f\u5e73\u4ec5\u89c1\uff0c\u8001\u8896\u8fd9\u8bd5\u638c\u4e4b\u6cd5\uff0c\u867d\u662f\u4e0e\u4f17\u7a0d\u6709\u4e0d\u540c\uff0c\u4f46\u5728\u795e\u541b\u9762\u524d\uff0c\u8fd8\u4e0d\u662f\u96d5\u866b \u4e4b\u6280\u5417\u3002\u201d \u3000\u3000\u4ed6\u7528\u98df\u4e2d\u4e8c\u6307\uff0c\u6387\u8d77\u90a3\u6735\u6885\u82b1\uff0c\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u8001\u8872\u6709\u5e78\uff0c\u5f97\u4ee5\u80fd\u9047\u6d77\u5185\u7b2c\u4e00\u5947 \u4eba\uff0c\u53c8\u80fd\u5728\u8fd9\u80dc\u7edd\u4eba\u95f4\u7684\u6885\u8c37\u548c\u795e\u541b\u4e00\u8bd5\u529f\u529b\uff0c\u7d22\u6027\u8001\u8872\u4e5f\u4f5c\u4e2a\u96c5\u4eba\uff0c\u5c31\u62ff\u8fd9\u6885\u82b1\u548c\u795e \u541b\u8bd5\u638c\u3002\u201d \u3000\u3000\u4ed6\u5c06\u6885\u82b1\u653e\u5728\u638c\u5fc3\uff0c\u5168\u795e\u51dd\u4f4f\uff0c\u7f13\u7f13\u5c06\u53f3\u638c\u5e73\u4f38\u51fa\u53bb\uff0c\u90a3\u6885\u82b1\u7adf\u4f3c\u9ecf\u5728\u638c\u5fc3\uff0c\u5e76\u672a \u5760\u4e0b\uff0c\u7136\u540e\u7f13\u7f13\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u795e\u541b\u4e5f\u5c06\u6885\u82b1\u9ecf\u5728\u638c\u5fc3\uff0c\u6211\u4eec\u4e24\u638c\u76f8\u4ea4\uff0c\u5374\u8ba9\u4e24\u6735\u6885\u82b1\u5728 \u4e24\u638c\u4e4b\u95f4\uff0c\u8981\u6885\u82b1\u4e0d\u788e\uff0c\u800c\u5c06\u5bf9\u65b9\u51fb\u8d25\uff0c\u8fd9\u9635\u82e5\u662f\u8001\u8872\u518d\u8d25\uff0c\u6211\u7b49\u56db\u4eba\u4fbf\u4fef\u9996\u542c\u51ed\u795e\u541b \u5904\u7f6e\uff0c\u4e0d\u77e5\u795e\u541b\u5bf9\u6b64\u6cd5\u53ef\u8868\u8d5e\u540c\u3002\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u6717\u58f0\u9053\uff1a\u201c\u4e0a\u4eba\u679c\u771f\u662f\u4e2a\u96c5\u4eba\uff0c\u66f4\u662f\u9ad8\u4eba\uff0c\u60f3\u51fa\u6765\u7684\u65b9\u6cd5\uff0c\u786e\u662f\u5999\u7edd\u4eba\u5bf0\uff0c \u533a\u533a\u5728\u4e0b\uff0c\u90a3\u6709\u53cd\u5bf9\u4e4b\u7406\u3002\u201d \u3000\u3000\u65bc\u662f\u4ed6\u5c31\u968f\u624b\u62fe\u8d77\u4e00\u6735\u6885\u82b1\uff0c\u53f3\u638c\u4e00\u7acb\uff0c\u90a3\u6885\u82b1\u4fbf\u4e5f\u9ecf\u5728\u638c\u5fc3\uff0c\u662f\u90a3\u4e48\u7684\u8f7b\u677e\u81ea\u7136\uff0c \u5168\u7136\u4e0d\u4f3c\u82e6\u5eb5\u4e0a\u4eba\u7684\u51dd\u91cd\u3002 \u3000\u3000\u4ed6\u968f\u53e3\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u4fbf\u8bf7\u843d\u82f1\u5251\u8c22\u4e16\u5144\u6765\u4f5c\u4e2a\u89c1\u8bc1\uff0c\u4e00\u4e2a\u65f6\u8fb0\u5185\u82e5\u65e0\u80dc\u8d1f\uff0c\u4fbf\u7b97\u5728 \u4e0b\u8f93\u4e86\u3002\u201d \u3000\u3000\u843d\u82f1\u5251\u95fb\u8a00\uff0c\u9762\u4e0a\u9732\u51fa\u559c\u8272\uff0c\u7acb\u523b\u8d70\u5230\u4e00\u65c1\uff0c\u90a3\u8d64\u9633\u9053\u957f\u548c\u5251\u795e\u5389\u9e57\u5374\u4ecd\u7d27\u7d27\u7ad9\u5728 \u82e6\u5eb5\u4e0a\u4eba\u8eab\u540e\uff0c\u4e03\u5999\u795e\u541b\u4e5f\u672b\u5728\u610f\uff0c\u8d70\u4e0a\u4e24\u6b65\uff0c\u53f3\u638c\u5fae\u66f2\uff0c\u82e6\u5eb5\u4e0a\u4eba\u4e5f\u8e0f\u4e0a\u4e00\u6b65\uff0c\u4e24\u4eba \u638c\u4e0a\u7684\u6885\u82b1\u4fbf\u642d\u5728\u4e00\u8d77\uff0c\u4f46\u5374\u5fae\u5fae\u89e6\u7740\uff0c\u5e76\u975e\u7d27\u63a5\u5728\u4e00\u8d77\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u4e00\u642d\u4e0a\u624b\uff0c\u5fc3\u4e2d\u4fbf\u662f\u4e00\u5bbd\uff0c\u77e5\u9053\u4eca\u65e5\u80dc\u7b97\u5df1\u7a33\u5728\u63e1\uff0c\u90a3\u82e6\u5e9c\u4e0a\u4eba\u7531\u6885\u74e3\u6240 \u6e17\u51fa\u7684\u638c\u529b\u867d\u662f\u9634\u67d4\u5f02\u5e38\uff0c\u5374\u4e0d\u591f\u96c4\u539a\uff0c\u4ed6\u5fd6\u9053\uff1a\u201c\u8fd9\u82e6\u5eb5\u4e0a\u4eba\u771f\u662f\u4f5c\u6cd5\u81ea\u6bd9\uff0c\u4e0d\u51fa\u534a \u4e2a\u65f6\u8fb0\uff0c\u6211\u4fbf\u8981\u4ed6\u4f24\u5728\u6211\u2018\u6697\u5f71\u6d6e\u9999\u2019\u638c\u529b\u4e4b\u4e0b\uff0c\u60f3\u4e0d\u5230\u8fd9\u7d20\u4ee5\u638c\u529b\u89c1\u79f0\u7684\u4eba\u7269\uff0c\u5374\u4e5f \u4e0d\u8fc7\u5982\u6b64\uff0c\u5509\uff0c\u4eca\u65e5\u6b66\u6797\uff0c\u80fd\u771f\u548c\u6211\u4e00\u8f83\u529f\u529b\u7684\uff0c\u600e\u7684\u5982\u6b64\u4e4b\u5c11\u3002 \u3000\u3000\u4ed6\u8fd9\u5ff5\u5934\u65b9\u81ea\u95ea\u8fc7\uff0c\u5ffd\u89c9\u638c\u4e2d\u538b\u529b\u4e00\u7d27\uff0c\u90a3\u81ea\u6885\u74e3\u6e17\u6765\u7684\u529b\u9053\uff0c\u4f55\u6b62\u589e\u4e86\u4e00\u500d\uff0c\u800c \u4e14\u96c4\u539a\u5f02\u5e38\uff0c\u4ed6\u65b9\u624d\u592a\u4ee5\u8f7b\u654c\uff0c\u6b64\u523b\u638c\u4e0a\u4e00\u9ebb\uff0c\u7adf\u9669\u4e9b\u7acb\u523b\u843d\u8d25\uff0c\u8fde\u5fd9\u4e00\u6574\u5fc3\u795e\uff0c\u5168\u795e \u51dd\u4f4f\uff0c\u5c06\u6bd5\u751f\u529f\u529b\uff0c\u5168\u805a\u638c\u4e0a\u3002 \u3000\u3000\u4ed6\u867d\u5728\u60ca\u5f02\u7740\u82e6\u5eb5\u4e0a\u4eba\u7684\u638c\u529b\uff0c\u7247\u523b\u4e4b\u95f4\u4fbf\u6709\u8fd9\u4e48\u5927\u7684\u53d8\u5316\uff0c\u4f46\u4ed6\u90a3\u91cc\u77e5\u9053\uff0c\u8fd9\u5176 \u4e2d\u5374\u662f\u5bf9\u65b9\u7684\u9634\u8be1\u4e4b\u8ba1\u5462\uff0c\u539f\u6765\u4e2d\u539f\u4e94\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u529f\u529b\u6700\u6df1\u7684\u4fbf\u662f\u5251\u795e\u5389\u9e57\uff0c\u975e \u4f46\u5251\u672f\u9ad8\u5999\uff0c\u638c\u529b\u96c4\u539a\uff0c\u800c\u4e14\u4e60\u5f97\u6b66\u6797\u4e2d\u5931\u4f20\u5df2\u4e45\u7684\u501f\u529b\u6253\u529b\u4e4b\u672f\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u4fa7\u8eab\u7ad9\u5728\u8d64\u9633\u9053\u957f\u548c\u82e6\u5eb5\u4e0a\u4eba\u4e4b\u95f4\uff0c\u5de6\u638c\u63a5\u4f4f\u8d64\u9633\u9053\u957f\u7684\u53f3\u638c\uff0c\u53f3\u638c\u62b5\u4f4f\u82e6 \u5eb5\u4e0a\u4eba\u7684\u80cc\u5fc3\uff0c\u4ee5\u5185\u529b\u5c06\u8d64\u9633\u9053\u957f\u548c\u81ea\u5df1\u7684\u529f\u529b\uff0c\u5f15\u5bfc\u81f3\u82e6\u5eb5\u4e0a\u4eba\u4f53\u5185\uff0c\u518d\u7531\u82e6\u9b54\u638c\u4e0a \u53d1\u51fa\u3002 \u3000\u3000\u8fd9\u6837\u4e03\u5999\u795e\u541b\u4f55\u5f02\u4e0e\u4e09\u5927\u9ad8\u624b\u8054\u96c6\u4e4b\u529b\u76f8\u6297\uff0c\u662f\u4ee5\u4ed6\u867d\u7136\u529f\u529b\u5df2\u81f3\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c \u4f46\u4ecd\u611f\u5230\u90a3\u4e48\u5403\u529b\uff0c\u987b\u77e5\u5185\u5bb6\u9ad8\u624b\u8fd9\u6837\u76f8\u8f83\u5185\u529b\uff0c\u4e00\u4e1d\u4e5f\u677e\u6cc4\u4e0d\u5f97\uff0c\u4e00\u4e2a\u4e0d\u597d\uff0c\u5185\u8151\u4fbf \u53d7\u91cd\u4f24\u3002 \u3000\u3000\u7ea6\u83ab\u76cf\u8336\u65f6\u5149\uff0c\u5728\u5168\u529b\u65bd\u7740\u638c\u529b\u7684\u56db\u4e2a\u4eba\uff0c\u989d\u4e0a\u90fd\u5df2\u5fae\u5fae\u89c1\u6c57\uff0c\u800c\u4e14\u5168\u795e\u4e13\u6ce8\uff0c\u529b \u5b8c\u5168\u805a\u5728\u638c\u4e0a\uff0c\u8eab\u4e0a\u5176\u4f59\u7684\u90e8\u4efd\uff0c\u50cf\u5df2\u4e0d\u5c5e\u4e8e\u81ea\u5df1\u4e86\uff0c\u6b64\u523b\u5c31\u7b97\u662f\u4e00\u4e2a\u7a0d\u6709\u529b\u6c14\u7684\u666e\u901a \u6751\u592b\uff0c\u4e5f\u80fd\u5c06\u4e4b\u51fb\u5012\u3002 \u3000\u3000\u4ed6\u4eec\u811a\u4e0b\u7684\u79ef\u96ea\uff0c\u867d\u56e0\u65e5\u4e45\u5df2\u51dd\u7ed3\u6210\u51b0\uff0c\u4f46\u6b64\u523b\u5374\u88ab\u8fd9\u56db\u4e2a\u5185\u5bb6\u9ad8\u624b\u4f53\u5185\u6240\u6563\u51fa\u7684 \u70ed\u529b\uff0c\u800c\u6eb6\u5316\u4e86\uff0c\u6d74\u5316\u4e86\u7684\u96ea\u6c34\uff0c\u6e10\u6e10\u6e17\u4eba\u90a3\u7ad9\u5728\u4e00\u65c1\u7684\u843d\u82f1\u5251\u8c22\u957f\u537f\u5e03\u5236\u7684\u4fbf\u978b\u91cc\u3002 \u3000\u3000\u4f46\u8c22\u957f\u537f\u5374\u4e1d\u6beb\u6ca1\u6709\u611f\u89c9\u5230\uff0c\u4ed6\u773c\u4e2d\u5728\u770b\u7740\u8fd9\u5e55\u60ca\u5fc3\u7684\u5bf9\u638c\uff0c\u5fc3\u91cc\u53cd\u590d\u601d\u91cf\u7740\uff1a \u201c\u6211\u8be5\u8fd9\u6837\u505a\u5417\uff0c\u6211\u8be5\u8fd9\u6837\u505a\u5417\uff1f\u201d \u3000\u3000\u4ed6\u773c\u671b\u573a\u4e0a\u7684\u60c5\u51b5\uff0c\u5df2\u5c06\u8fd1\u5230\u4e86\u51b3\u5b9a\u6027\u7684\u9636\u6bb5\uff0c\u4e03\u5999\u795e\u541b\u867d\u662f\u4ee5\u4e00\u654c\u4e09\uff0c\u4f46\u4ecd\u7136\u5c79 \u7acb\u5982\u5c71\uff0c\u800c\u82e6\u5eb5\u4e0a\u4eba\u5fae\u66f2\u7740\u624b\u8098\u5df2\u5728\u5fae\u5fae\u98a4\u52a8\u4e86\uff0c\u867d\u7136\u90a3\u662f\u6781\u4e3a\u8f7b\u5fae\u7684\u98a4\u52a8\u3002 \u3000\u3000\u987b\u77e5\u82e6\u5eb5\u4e0a\u4eba\u5df4\u8fbe\u53e4\u7a00\u4e4b\u9f84\uff0c\u867d\u7136\u5185\u529b\u6df1\u6e5b\uff0c\u4f46\u5c81\u6708\u4fb5\u4eba\uff0c\u4ed6\u4f53\u5185\u7684\u62b5\u6297\u4e4b\u529b\uff0c\u5df2 \u4e0d\u590d\u518d\u6709\u5f53\u5e74\u7684\u5f3a\u5065\uff0c\u8d64\u9633\u9053\u957f\u548c\u5251\u795e\u5389\u9e57\uff0c\u4ee5\u5343\u94a7\u5185\u529b\uff0c\u901a\u8fc7\u4ed6\u4f53\u5185\uff0c\u6e10\u6e10\u5730\uff0c\u4ed6\u89c9 \u5f97\u4f53\u5185\u5df2\u7136\u6709\u4e86\u4e00\u79cd\u96be\u8a00\u7684\u4e0d\u9002\uff0c\u8fd9\u662f\u81ea\u7136\u7684\u5a01\u529b\uff0c\u4e0d\u662f\u4eba\u529b\u53ef\u4ee5\u62b5\u6297\u7684\u3002 \u3000\u3000\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u81ea\u7136\u4e5f\u770b\u5230\u6b64\u70b9\uff0c\u4ed6\u5929\u4eba\u4ea4\u6218\u4e86\u4e00\u4f1a\uff0c\u65ad\u7136\u601d\u9053\uff1a\u201c\u8bf4\u4e0d\u5f97\u6211\u53ea\u597d \u505a\u4e00\u6b21\u6627\u5fc3\u4e4b\u4e8b\u4e86\uff0c\u6211\u8fd8\u5e74\u8f7b\uff0c\u6211\u4e0d\u80fd\u5c31\u8fd9\u6837\u65e0\u58f0\u65e0\u606f\u5730\u6b7b\u53bb\uff0c\u800c\u4e14\u8fd9\u8c37\u4e2d\u518d\u65e0\u4ed6\u4eba\uff0c \u5373\u4f7f\u6211\u4f5c\u4e86\u6627\u5fc3\u4e4b\u4e8b\uff0c\u53c8\u6709\u8c01\u4f1a\u4f20\u5c06\u51fa\u53bb\uff0c\u5509\uff01\u6211\u60f3\u4eba\u4eba\u90fd\u8be5\u4e3a\u81ea\u5df1\u6253\u7b97\u5427\u3002\u201d \u3000\u3000\u4ed6\u7f13\u7f13\u5730\u79fb\u52a8\u811a\u6b65\uff0c\u9eef\u6de1\u7684\u5149\u7ebf\uff0c\u4f7f\u5f97\u4ed6\u672c\u6765\u82f1\u4fca\u7684\u9762\u5e9e\uff0c\u770b\u8d77\u6765\u90a3\u6837\u72f0\u72de\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u4e03\u5999\u795e\u541b\u7684\u8eab\u65c1\uff0c\u671b\u7740\u4e03\u5999\u795e\u541b\u5bbd\u9614\u7684\u524d\u989d\uff0c\u7626\u524a\u7684\u9762\u5e9e\uff0c\u548c\u90a3\u53ea\u500f\u7136\u53d1\u51fa \u5149\u8292\u7684\u773c\u6674\uff0c\u8fd9\u4e9b\u4f7f\u8fd9\u9762\u5e9e\u770b\u8d77\u6765\u662f\u90a3\u4e48\u5730\u8131\u4fd7\uff0c\u90a3\u4e48\u5730\u5448\u73b0\u51fa\u4e00\u79cd\u8d85\u4eba\u7684\u667a\u6167\uff0c\u4ed6\u8fdf \u8fdf\u4e86\u534a\u54cd\uff0c\u731b\u4e00\u54ac\u7259\uff0c\u53cc\u624b\u4ff1\u51fa\uff0c\u6781\u5feb\u5730\u70b9\u4e86\u4e03\u5999\u795e\u541b\u7684\u53f3\u80a9\u3001\u80c1\u4e0b\u7684\u201c\u80a9\u4e95\u201d\u201c\u6ca7\u6d77\u201d \u4e24\u4e2a\u8981\u7a74\uff0c\u90a3\u662f\u70b9\u82cd\u7684\u7edd\u5b66\u201c\u4e03\u7edd\u624b\u6cd5\u201d\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u6b63\u81ea\u5168\u795e\u51dd\u4f4f\u7740\uff0c\u4ed6\u4e5f\u611f\u89c9\u5bf9\u65b9\u7684\u624b\u638c\uff0c\u5df1\u6e10\u6e10\u5931\u53bb\u4e86\u575a\u5b9a\uff0c\u5ffd\u7136\u89c9\u5f97\u5168 \u8eab\u4e00\u9635\u9ebb\u75f9\uff0c\u624b\u4e0a\u4e00\u8f6f\uff0c\u63a5\u7740\u4e00\u80a1\u65e0\u6bd4\u7684\u52b2\u9053\uff0c\u7531\u638c\u800c\u81c2\uff0c\u76f4\u4f20\u4eba\u4ed6\u7684\u5fc3\u8151\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u987f\u89c9\u5929\u5730\u53c8\u56de\u590d\u4e86\u6df7\u6c8c\uff0c\u5728\u8fd9\u6e3a\u832b\u7684\u4e00\u523b\u91cc\uff0c\u4ed6\u8111\u6d77\u91cc\u95ea\u51fa\u8bb8\u591a\u4e2a\u719f\u6089\u7684\u5f71 \u5b50\uff0c\u90a3\u90fd\u662f\u7f8e\u4e3d\u800c\u5e74\u8f7b\u7684\u5f71\u5b50\uff0c\u63a5\u7740\uff0c\u4ed6\u4e0d\u80fd\u518d\u611f\u5230\u4efb\u4f55\u4e8b\u4e86\u3002 \u3000\u3000\u5927\u5730\u4f9d\u7136\uff0c\u5929\u4e0a\u5df1\u5c06\u73b0\u66d9\u8272\uff0c\u5bd2\u610f\u4e5f\u66f4\u4fb5\u4eba\u4e86\u3002 \u3000\u3000\u8c37\u91cc\uff0c\u53c8\u56de\u590d\u4e86\u4e00\u8d2f\u9759\u5bc2\uff0c\u50cf\u662f\u6839\u672c\u6ca1\u4efb\u4f55\u4e8b\u60c5\u53d1\u751f\u4f3c\u7684\u3002 \u3000\u3000\u8d64\u9633\u9053\u957f\uff0c\u82e6\u5eb5\u4e0a\u4eba\uff0c\u5251\u795e\u5389\u9e57\uff0c\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u5e26\u7740\u4e00\u79cd\u867d\u662f\u80dc\u5229\uff0c\u4f46\u5e76\u4e0d\u6109\u5feb \u7684\u5fc3\u60c5\u8d70\u4e86\u3002 \u3000\u3000\u5c71\u5ca9\u7684\u7a7a\u9699\u91cc\uff0c\u5ffd\u5730\u95ea\u51fa\u4e00\u4e2a\u9e51\u8863\u767e\u7ed3\u7684\u5c11\u5e74\u4e10\u8005\uff0c\u6781\u5feb\u5730\u63a0\u81f3\u4e03\u5999\u795e\u541b\u5367\u5012\u5728\u767d \u96ea\u4e0a\u7684\u8eab\u8eaf\u65c1\uff0c\u4fef\u8eab\u63a2\u4e86\u63a2\u4ed6\u7684\u9f3b\u606f\uff0c\u53c8\u6478\u4e86\u6478\u4ed6\u7684\u80f8\u53e3\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u6b63 \u60f3\u62b1\u8d77\u4e03\u5999\u795e\u541b\u7684\u949d\u5c38\u4f53\uff0c\u5ffd\u53c8\u6447\u5934\u81ea\u8bed\u9053\uff1a\u201c\u5c31\u8ba9\u4ed6\u8eba\u5728\u8fd9\u91cc\u4e5f\u597d\u4e86\uff0c\u8ba9\u96ea\u628a\u4ed6\u6df9\u6ca1\uff0c \u4ed6\u80fd\u957f\u7720\u5728\u8fd9\u5e7d\u9759\u5730\u7684\u6885\u8c37\u91cc\uff0c\u957f\u4f34\u6885\u82b1\uff0c\u4e5f\u7b97\u5929\u5730\u4e0d\u8d1f\u4ed6\u4e86\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4e10\u8005\u6162\u6162\u5730\u62ac\u8d77\u76ee\u5149\uff0c\u770b\u5230\u5251\u795e\u5389\u9e57\u7684\u5251\u9798\uff0c\u4ecd\u7136\u653e\u5728\u90a3\u5757\u5c71\u77f3\u4e0a\uff0c\u5fae\u4e00\u8f6c \u5ff5\uff0c\u98de\u7eb5\u800c\u8d77\uff0c\u62ff\u8d77\u90a3\u4e2a\u5251\u9798\uff0c\u8eab\u5f62\u731b\u4e00\u987f\u632b\uff0c\u76f4\u5411\u8c37\u5916\u98de\u8eab\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u5bb6\u6751\uff0c\u662f\u6ec7\u6c60\u80cc\u5cb8\u6606\u660e\u57ce\u90ca\u7684\u4e94\u534e\u5c71\u7554\uff0c\u4e00\u4e2a\u5f88\u5c0f\u7684\u6751\u843d\uff0c\u6751\u91cc\u6240\u4f4f\u7684\u4eba\u5bb6\uff0c\u5341 \u4e2d\u6709\u4e5d\uff0c\u90fd\u662f\u59d3\u8f9b\uff0c\u6545\u6b64\u6751\u540d\u4e4b\u8f9b\u5bb6\u6751\u3002 \u3000\u3000\u8f9b\u5bb6\u6751\u867d\u7136\u5f88\u5c0f\uff0c\u7136\u800c\u5728\u4e91\u8d35\u9ad8\u539f\u4e00\u5e26\uff0c\u5374\u662f\u5927\u5927\u7684\u6709\u540d\u3002 \u3000\u3000\u8fd9\u539f\u56e0\u662f\u8f9b\u5bb6\u6751\u5728\u8fd1\u5e74\u6765\uff0c\u51fa\u4e86\u4e24\u4e2a\u4e0e\u4f17\u4e0d\u540c\u7684\u4eba\u7269\uff0c\u8fd9\u4e24\u4eba\u4e00\u7537\u4e00\u5973\uff0c\u662f\u4e00\u5bf9\u592b \u5987\uff0c\u81ea\u5e7c\u672c\u5728\u8f9b\u5bb6\u6751\u751f\u957f\u7684\uff0c\u800c\u4e14\u662f\u5802\u5144\u59b9\u3002 \u3000\u3000\u7537\u7684\u59d3\u8f9b\uff0c\u5b57\u9e4f\u4e5d\uff0c\u5973\u7684\u53eb\u8f9b\u4eea\uff0c\u4e24\u4eba\u81ea\u5e7c\u9752\u6885\u7af9\u9a6c\uff0c\u60c5\u611f\u968f\u7740\u65f6\u65e5\u6e10\u589e\uff0c\u957f\u5927\u540e\uff0c \u4fbf\u6697\u6697\u5b9a\u4e86\u5a5a\u7ea6\uff0c\u90a3\u65f6\u793c\u6559\u751a\u4e25\uff0c\u5802\u5144\u59b9\u901a\u5a5a\uff0c\u662f\u7edd\u4e0d\u53ef\u80fd\u7684\uff0c\u975e\u4f46\u7236\u6bcd\u53cd\u5bf9\uff0c\u8fde\u8f9b\u5bb6 \u6751\u7684\u5c45\u6c11\uff0c\u4e5f\u662f\u7fa4\u8d77\u800c\u653b\uff0c\u8ba4\u4e3a\u662f\u5927\u9006\u4e0d\u9053\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u8fd9\u4e24\u4eba\u60c5\u611f\u751a\u575a\uff0c\u7edd\u672c\u56e0\u5916\u754c\u7684\u4efb\u4f55\u538b\u529b\uff0c\u800c\u6709\u6240\u6539\u53d8\uff0c\u4e8e\u662f\u5728\u90a3\u4e00\u5e74\u7684\u6625\u5929\uff0c \u4ed6\u4e24\u4eba\u4fbf\u53cc\u53cc\u5931\u8e2a\uff0c\u4e5f\u4e0d\u77e5\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u5341\u4f59\u5e74\uff0c\u5f53\u4eba\u4eec\u90fd\u5df2\u5fd8\u5374\u4e86\u8fd9\u4ef6\u4e8b\u7684\u65f6\u5019\uff0c\u8f9b\u9e4f\u4e5d\u3001\u8f9b\u4eea\u7a81\u7136\u53c8\u56de\u5230\u8fd9\u4e2a\u5c0f\u5c0f \u7684\u6751\u843d\uff0c\u800c\u4e14\u8fd8\u751f\u4e86\u4e00\u4e2a\u7537\u5b69\uff0c\u624d\u4e03\u3001\u516b\u5c81\u53d6\u540d\u53eb\u505a\u8f9b\u6377\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u4ed6\u4eec\u7684\u7236\u6bcd\u90fd\u76f8\u7ee7\u53bb\u4e16\u4e86\uff0c\u800c\u4e14\u8f9b\u9e4f\u4e5d\u56de\u6765\u4e4b\u540e\uff0c\u624b\u9762\u751a\u662f\u9614\u7ef0\uff0c\u65e0\u8bba\u8bc6\u4e0e \u4e0d\u8bc6\uff0c\u4ed6\u90fd\u5907\u4e86\u4e00\u4efd\u91cd\u793c\uff0c\u4e00\u56de\u6765\u540e\uff0c\u4fbf\u6328\u6237\u9001\u53bb\u3002 \u3000\u3000\u5c0f\u6751\u7684\u4eba\uff0c\u6700\u662f\u541d\u9119\uff0c\u54ea\u66fe\u89c1\u8fc7\u5982\u6b64\u624b\u9762\uff0c\u4e0d\u4f46\u4e0d\u518d\u53cd\u5bf9\u4ed6\u4e24\u4eba\uff0c\u53cd\u66f4\u606d\u656c\u3002 \u3000\u3000\u6606\u660e\u57ce\u5185\u5916\uff0c\u5c45\u6c11\u591a\u5584\u96d5\u523b\u548c\u5236\u94dc\u5668\uff0c\u8f9b\u5bb6\u6751\u4e5f\u4e0d\u4f8b\u5916\uff0c\u8f9b\u9e4f\u4e5d\u548c\u8f9b\u4eea\uff0c\u672c\u4e5f\u64c5\u957f \u96d5\u523b\uff0c\u6b64\u756a\u56de\u6765\u4e4b\u540e\uff0c\u6240\u96d5\u4e4b\u7269\uff0c\u66f4\u662f\u51fa\u795e\u5165\u5316\u3002 \u3000\u3000\u987b\u77e5\u96d5\u523b\u4e00\u6280\uff0c\u9664\u4e86\u5fc3\u7075\u624b\u5de7\u4e4b\u5916\uff0c\u8fd8\u5f97\u5200\u6c89\u529b\u7a33\uff0c\u96d5\u51fa\u6765\u7684\u7ebf\u6761\uff0c\u624d\u80fd\u6829\u6829\u5982\u751f\uff0c \u8f9b\u9e4f\u4e5d\u592b\u5987\u56de\u6765\u540e\uff0c\u95f2\u65f6\u4fbf\u4e5f\u96d5\u4e9b\u5c0f\u50cf\u6d88\u9063\uff0c\u6709\u65f6\u4e5f\u62ff\u6765\u9001\u4eba\u3002\u6751\u4eba\u4e00\u89c1\u4ed6\u4fe9\u6240\u96d5\u4e4b\u7269\uff0c \u7b80\u76f4\u662f\u5999\u5230\u4e0d\u53ef\u601d\u8bae\uff0c\u6709\u4e9b\u597d\u5229\u7684\u4eba\uff0c\u4fbf\u5c31\u5077\u5077\u62ff\u5230\u57ce\u91cc\u53bb\u5356\uff0c\u60f3\u4e0d\u5230\u552e\u5f97\u5f88\u9ad8\u7684\u4ef7\u7ebf\uff0c \u662f\u4ed6\u4eec\u6240\u4ece\u672a\u5f97\u5230\u7684\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4eec\u56de\u6751\u540e\uff0c\u4fbf\u53c8\u592e\u7740\u8f9b\u9e4f\u4e5d\u592b\u5987\u518d\u9001\u4e9b\u7ed9\u4ed6\u4eec\uff0c\u8f9b\u9e4f\u4e5d\u592b\u5987\uff0c\u6765\u8005\u4e0d\u62d2\uff0c\u4e5f \u5f88\u5c11\u4f7f\u4ed6\u4eec\u5931\u671b\uff0c\u603b\u662f\u5ba2\u6c14\u5730\u5e94\u916c\u7740\u3002 \u3000\u3000\u8fd9\u6837\u4e0d\u6d88\u5e74\u4f59\uff0c\u6606\u660e\u5de6\u8fd1\u7684\u4eba\uff0c\u90fd\u77e5\u9053\u8f9b\u5bb6\u6751\u6709\u4e2a\u201c\u795e\u96d5\u201d\uff0c\u6709\u4e0d\u5c11\u5546\u4eba\uff0c\u89c1\u6709\u5229 \u53ef\u56fe\uff0c\u4fbf\u4e13\u7a0b\u5230\u8f9b\u5bb6\u6751\u53bb\u62dc\u8bbf\u4ed6\u4eec\u592b\u5987\u3002 \u3000\u3000\u8d77\u5148\u4ed6\u592b\u5987\u8fd8\u4e0d\u592a\u600e\u4e48\uff0c\u540e\u6765\u542c\u4eba\u8bf4\u4ed6\u4eec\u7adf\u88ab\u79f0\u4e3a\u201c\u795e\u96d5\u201d\uff0c\u4fbf\u7acb\u5373\u9762\u8272\u5927\u53d8\uff0c\u8bf4 \u597d\u8bf4\u6b79\uff0c\u4e5f\u4e0d\u8ba9\u522b\u4eba\u518d\u5728\u5916\u9762\u53eb\u4ed6\u8fd9\u4e2a\u540d\u5b57\u3002 \u3000\u3000\u4f46\u4eba\u95f4\u7684\u4e8b\uff0c\u6bcf\u6bcf\u90fd\u662f\u90a3\u4e48\u5947\u602a\uff0c\u4f60\u8d8a\u4e0d\u60f3\u51fa\u540d\uff0c\u53cd\u800c\u66f4\u52a0\u51fa\u540d\uff0c\u4f60\u8d8a\u60f3\u51fa\u540d\uff0c\u5374 \u6c38\u8fdc\u4e0d\u4f1a\u51fa\u540d\uff0c\u4eba\u4eec\u867d\u7136\u7b54\u5e94\u4e86\u8f9b\u9e4f\u4e5d\u592b\u5987\uff0c\u4e0d\u518d\u53eb\u4ed6\u4eec\u201c\u795e\u96d5\u201d\u8fd9\u4e2a\u540d\u5b57\uff0c\u79c1\u4e0b\u5374\u4ecd \u7136\u79f0\u547c\u7740\u3002 \u3000\u3000\u4e00\u6643\uff0c\u8f9b\u9e4f\u4e5d\u56de\u5230\u8f9b\u5bb6\u6751\u5df1\u7ecf\u56db\u5e74\u591a\u4e86\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u8f9b\u5bb6\u6751\u9664\u4e86\u6bd4\u4ee5\u524d\u51fa\u540d\u5f97\u591a\u4e4b \u5916\uff0c\u5012\u4e5f\u76f8\u5b89\u65e0\u4e8b\u3002\u8f9b\u9e4f\u4e5d\u7684\u513f\u5b50\u8f9b\u6377\uff0c\u8fd9\u65f6\u4e5f\u6709\u5341\u4e8c\u5c81\u4e86\uff0c\u751f\u5f97\u806a\u660e\u4f36\u4fd0\uff0c\u8eab\u4f53\u4e5f\u6bd4 \u522b\u7684\u5c0f\u5b69\u5f3a\u58ee\u5f97\u591a\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u592b\u5987\uff0c\u672c\u6765\u7ecf\u5e38\u7d27\u7ef7\u7740\u7684\u53cc\u7709\uff0c\u73b0\u5728\u4e5f\u9010\u6e10\u5f00\u6717\u4e86\uff0c\u8fc7\u4e86\u6b63\u6708\uff0c\u6625\u5929\u5df2\u7ecf\u6765 \u5230\u4e86\uff0c\u867d\u7136\u4ecd\u4e0d\u751a\u6696\uff0c\u4f46\u4eba\u4eec\u591a\u5c11\u5df1\u55c5\u5230\u4e86\u6625\u5929\u7684\u6c14\u606f\u3002 \u3000\u3000\u82b1\u671d\u8282\u90a3\u5929\uff0c\u8f9b\u9e4f\u4e5d\u592b\u5987\u5728\u4ed6\u4eec\u7684\u5c0f\u9662\u91cc\uff0c\u6446\u4e86\u4e09\u684c\u9152\uff0c\u8bf7\u4e86\u4e9b\u6751\u4e2d\u7684\u7236\u8001\uff0c\u996e\u9152 \u8d4f\u6885\uff0c\u8f9b\u4eea\u539f\u6765\u4e0d\u4f1a\u70e7\u83dc\uff0c\u8fd9\u56db\u5e74\u6765\uff0c\u5374\u53d8\u6210\u4e2a\u70f9\u996a\u8001\u624b\u4e86\uff0c\u65bc\u662f\u80b4\u7cbe\u9152\u7f8e\uff0c\u4eba\u4eba\u5c3d\u6b22 \u800c\u6563\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u592b\u5987\u8fd9\u5929\u5fc3\u60c5\u50cf\u7279\u522b\u597d\uff0c\u5ba2\u4eba\u8d70\u4e86\u540e\uff0c\u4ecd\u6446\u4e86\u5f20\u5c0f\u684c\u5b50\uff0c\u5750\u5728\u5eca\u68fa\u4e0b\uff0c\u628a\u8f9b \u6377\u4e5f\u53eb\u5230\u65c1\u8fb9\u5750\u4e0b\uff0c\u628a\u9152\u8c08\u5fc3\u3002 \u3000\u3000\u8fdc\u5904\u6709\u66f4\u9f13\u4f20\u6765\uff0c\u6b64\u65f6\u5df2\u8d77\u66f4\u4e86\uff0c\u8f9b\u9e4f\u4e5d\u4e3e\u8d77\u9152\u676f\uff0c\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5bf9\u8f9b\u4eea\u8bf4\uff1a\u201c\u8fd9 \u51e0\u5e74\u6765\uff0c\u771f\u662f\u82e6\u4e86\u4f60\uff0c\u603b\u7b97\u73b0\u5728\u5df2\u7ecf\u6328\u8fc7\u4e94\u5e74\u4e86\uff0c\u53ea\u8981\u6328\u8fc7\u4eca\u591c\uff0c\u65e5\u540e\u6211\u4eec\u7684\u5fc3\u4e8b\u4e5f\u5c31 \u4e86\u5374\u4e86\u3002\u201d \u3000\u3000\u8f9b\u4eea\u5a49\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5c31\u7b97\u65e5\u540e\u6ca1\u4e8b\uff0c\u6211\u4e5f\u4e0d\u613f\u518d\u5165\u6c5f\u6e56\u4e86\uff0c\u5c31\u597d\u597d\u5728\u8fd9\u91cc\u505a\u4e2a\u5b89\u4efd \u826f\u6c11\u5427\uff0c\u90a3\u79cd\u62ff\u5200\u52a8\u5251\u7684\u65e5\u5b50\uff0c\u6211\u771f\u8fc7\u5f97\u817b\u4e86\u3002\u201d \u3000\u3000\u8f9b\u9e4f\u4e5d\u7b11\u9053\uff1a\u201c\u8bf4\u5b9e\u8bdd\uff0c\u8fd9\u51e0\u5e74\u6765\uff0c\u6211\u5012\u771f\u4e2a\u6709\u4e9b\u9759\u6781\u601d\u52a8\u4e86\uff0c\u8981\u4e0d\u662f\u90a3\u4e2a\u9b54\u5934\u592a \u8fc7\u5389\u5bb3\uff0c\u6211\u65e9\u5df2\u71ac\u4e0d\u4f4f\u4e86\uff0c\u5e78\u4e8f\u2026\u2026\u201d \u3000\u3000\u8f9b\u4eea\u5ffd\u5730\u9762\u73b0\u6101\u5bb9\uff0c\u62a2\u7740\u8bf4\uff1a\u201c\u8981\u662f\u8fc7\u4e86\u4eca\u591c\uff0c\u4ed6\u4eec\u4ecd\u4e0d\u653e\u677e\u5462\uff1f\u201d \u3000\u3000\u8f9b\u9e4f\u4e5d\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u90a3\u5012\u4e0d\u4f1a\uff0c\u6d77\u5929\u53cc\u715e\u867d\u662f\u5fc3\u6bd2\u624b\u8fa3\uff0c\u4f46\u4e8c\u5341\u5e74\u6765\uff0c\u5374\u662f\u8a00\u51fa\u5fc5 \u884c\uff0c\u53ea\u8981\u8fc7\u4e86\u4ed6\u7acb\u4e0b\u4e94\u5e74\u4e4b\u671f\uff0c\u4e94\u5e74\u4e4b\u540e\uff0c\u5c31\u662f\u6211\u4eec\u548c\u4ed6\u4eec\u5bf9\u9762\u9047\u4e0a\uff0c\u4ed6\u4eec\u90fd\u4e0d\u4f1a\u4f24\u6211 \u4eec\u4e00\u6839\u6beb\u6bdb\u7684\u3002\u201d \u3000\u3000\u8bdd\u521a\u8bf4\u5b9b\uff0c\u5ffd\u5730\u4f20\u6765\u4e00\u58f0\u9634\u607b\u607b\u51b7\u7b11\uff0c\u4e00\u4e2a\u5c16\u7ec6\u7684\u53e3\u58f0\u8bf4\uff1a\u201c\u8f9b\u8001\u516d\u5012\u771f\u662f\u6211\u7684\u77e5 \u5df1\uff0c\u5c31\u51b2\u4f60\u8fd9\u53e5\u8bdd\uff0c\u6211\u7126\u8001\u5927\u8ba9\u4f60\u6b7b\u4e2a\u75db\u5feb\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e00\u51b7\u7b11\uff0c\u8f9b\u9e4f\u4e5d\u592b\u5987\u542c\u4e86\uff0c\u4f55\u5f02\u9b3c\u5352\u6572\u95e8\uff0c\u592b\u5987\u4ff1\u90fd\u500f\u5730\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u591c\u5bd2\u5982\u6c34\uff0c\u56db\u5468\u4ecd\u7136\u6ca1\u6709\u4eba\u5f71\uff0c\u8f9b\u9e4f\u4e5d\u6ee1\u8179\u4ff1\u662f\u60ca\u4ff1\u4e4b\u8272\uff0c\u5f3a\u81ea\u9547\u5b9a\u7740\uff0c\u6717\u58f0\u8bf4\uff1a \u201c\u5927\u54e5\uff0c\u4e8c\u54e5\u65e2\u7136\u6765\u4e86\uff0c\u4f55\u4e0d\u8bf7\u4e0b\u6765\u3002\u201d \u3000\u3000\u9ed1\u6697\u4e2d\u53c8\u662f\u4e00\u58f0\u9634\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u7684\u8fd8\u8981\u6211\u8d39\u4e8b\u52a8\u624b\u5417\uff0c\u76cf\u8336\u4e4b\u5185\uff0c\u4f60\u592b\u5987\u7236\u5b50 \u4e09\u4eba\uff0c\u82e5\u4e0d\u7acb\u523b\u81ea\u51b3\uff0c\u6050\u6015\u6b7b\u5f97\u66f4\u60e8\u4e86\u3002\u201d \u3000\u3000\u8f9b\u9e4f\u4e5d\u6b64\u523b\u5df2\u9762\u65e0\u4eba\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u592b\u5987\u4e24\u4eba\u81ea\u77e5\u5bf9\u4e0d\u8d77\u5927\u54e5\u4e8c\u54e5\uff0c\u5ff5\u5728\u4ee5\u524d\u7684\u60c5 \u4efd\uff0c\u9976\u8fd9\u5c0f\u5b69\u5b50\u4e00\u547d\u3002\u201d \u3000\u3000\u9ed1\u6697\u4e2d\u51b7\u7b11\u7b54\u9053\uff1a\u201c\u521a\u8bf4\u4f60\u662f\u6211\u7684\u77e5\u5df1\uff0c\u73b0\u5728\u600e\u53c8\u8bf4\u51fa\u8fd9\u6837\u7684\u8bdd\u6765\uff0c\u96be\u9053\u4f60\u4e0d\u77e5\u9053 \u6211\u5f1f\u5144\u7684\u813e\u6c14\uff0c\u8fd8\u4f1a\u8ba9\u4f60\u4eec\u7559\u540e\u5417\u3002\u201d \u3000\u3000\u8f9b\u4eea\u542c\u4e86\uff0c\u82b1\u5bb9\u60e8\u53d8\uff0c\u60b2\u58f0\u6012\u559d\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u8001\u6b8b\u5e9f\uff0c\u4e0d\u8981\u8d76\u4eba\u4eba\u7edd\u8def\uff0c\u96be\u9053\u6211 \u4eec\u8fde\u4e0d\u505a\u5f3a\u76d7\u7684\u81ea\u7531\u90fd\u6ca1\u6709\uff0c\u8981\u77e5\u9053\uff0c\u6211\u4eec\u6ec7\u6842\u53cc\u96d5\u4e5f\u4e0d\u662f\u597d\u6b3a\u8d1f\u7684\uff0c\u6211\u8f9b\u5927\u5a18\u5012\u8981\u770b \u770b\u4f60\u4eec\u6709\u4ec0\u4e48\u901a\u5929\u5f7b\u5730\u7684\u672c\u4e8b\u3002\u201d \u3000\u3000\u8bdd\u58f0\u4e00\u843d\uff0c\u5fae\u98ce\u98d8\u5904\uff0c\u9662\u4e2d\u5df2\u591a\u4e86\u4e24\u4e2a\u7070\u60e8\u60e8\u7684\u4eba\u5f71\uff0c\u4e00\u4e2a\u867d\u7136\u56db\u811a\u4ff1\u5168\uff0c\u4f46\u8138\u4e0a \u5374\u50cf\u662f\u5e73\u6574\u6574\u7684\u4e00\u5757\uff0c\u65e0\u9f3b\u65e0\u8033\uff0c\u8fde\u7709\u6bdb\u90fd\u6ca1\u6709\uff0c\u53ea\u6709\u773c\u775b\u50cf\u662f\u4e24\u5757\u5bd2\u7389\uff0c\u53d1\u51fa\u4e00\u79cd\u5f7b \u9aa8\u7684\u5149\u8292\u3002 \u3000\u3000\u53e6\u4e00\u4eba\u6a21\u6837\u66f4\u5947\u602a\uff0c\u5934\u9885\u3001\u8eab\u8eaf\uff0c\u90fd\u662f\u7279\u522b\u5730\u5927\uff0c\u4e24\u624b\u4e24\u817f\uff0c\u5374\u53c8\u7ec6\u53c8\u77ed\uff0c\u50cf\u4e2a\u516d\u3001 \u4e03\u5c81\u7684\u5c0f\u513f\uff0c\u4e24\u4eba\u4ff1\u662f\u5168\u8eab\u7070\u8863\uff0c\u5728\u8fd9\u9eef\u9ed1\u7684\u5149\u7ebf\u4e0b\uff0c\u7b80\u76f4\u5f62\u540c\u9b3c\u9b45\uff0c\u90a3\u91cc\u50cf\u4e2a\u6d3b\u4eba\u3002 \u3000\u3000\u6b64\u4e24\u4eba\u6b63\u662f\u5f53\u4eca\u6b66\u6797\u4e2d\uff0c\u4e00\u7b49\u4e00\u7684\u9b54\u5934\uff0c\u6d77\u5929\u53cc\u715e\uff0c\u5929\u6b8b\u7126\u5316\uff0c\u5929\u5e9f\u7126\u52b3\u5144\u5f1f\u3002 \u3000\u3000\u9ec4\u6cb3\u5173\u4e2d\u4e5d\u8c6a\uff0c\u9886\u8896\u7eff\u6797\uff0c\u6d77\u5929\u53cc\u715e\u5c31\u662f\u5173\u4e2d\u4e5d\u8c6a\u7684\u8001\u5927\u3001\u8001\u4e8c\uff0c\u90a3\u8f9b\u9e4f\u4e5d\u4e0e\u8f9b\u4eea \u4e8c\u4eba\uff0c\u81ea\u79bb\u8f9b\u5bb6\u6751\u540e\uff0c\u4e1c\u98d8\u897f\u6cca\uff0c\u5374\u65e0\u610f\u4e2d\u5f97\u5230\u4e00\u4f4d\u4e45\u5df2\u6d17\u624b\u7684\u5947\u4eba\u5782\u9752\uff0c\u4f20\u5f97\u4e00\u8eab\u7edd \u6280\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u592b\u5987\uff0c\u56e0\u53d7\u51b7\u773c\u592a\u591a\uff0c\u4e0d\u514d\u5bf9\u4eba\u4e16\u5b58\u4e86\u504f\u6fc0\u4e4b\u89c1\uff0c\u827a\u6210\u540e\uff0c\u631f\u6280\u884c\u8d70\u6c5f\u6e56\uff0c \u5c31\u505a\u4e9b\u6253\u5bb6\u52ab\u820d\u7684\u52fe\u5f53\uff0c\u4e0d\u6570\u5e74\uff0c\u201c\u6ec7\u6842\u53cc\u96d5\u201d\u4e4b\u540d\uff0c\u5373\u4f20\u904d\u6c5f\u6e56\uff0c\u6b66\u6797\u4e2d\u4ff1\u77e5\u6709\u7537\u5973 \u4e24\u4e2a\u72ec\u884c\u5267\u76d7\uff0c\u4e0d\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u800c\u4e14\u624b\u6bb5\u6bd2\u8fa3\uff0c\u624b\u4e0b\u5c11\u6709\u6d3b\u53e3\u3002 \u3000\u3000\u540e\u6765\u90a3\u6d77\u5929\u53cc\u715e\u6240\u7ec4\u7684\u5173\u4e2d\u4e5d\u8c6a\uff0c\u7a81\u7136\u6b7b\u53bb\u4e24\u4eba\uff0c\u6d77\u5929\u53cc\u715e\u4e00\u542c\u201c\u6ec7\u6842\u53cc\u96d5\u201d\u6240\u505a \u6240\u4e3a\uff0c\u751a\u5408\u81ea\u5df1\u7684\u813e\u80c3\uff0c\u4fbf\u62c9\u4ed6\u4fe9\u4eba\u5165\u4f19\uff0c\u987b\u77e5\u201c\u5173\u4e2d\u4e5d\u8c6a\u201d\u4e43\u662f\u9ed1\u9053\u4e2d\u7684\u6cf0\u5c71\u5317\u6597\uff0c \u521a\u521a\u5014\u8d77\u7684\u201c\u6ec7\u6842\u53cc\u96d5\u201d\u54ea\u6709\u4e0d\u613f\u4e4b\u7406\uff0c\u65bc\u662f\u4fbf\u4e5f\u5165\u4e86\u201c\u5173\u4e2d\u4e5d\u8c6a\u201d\u7684\u56e2\u4f53\u3002 \u3000\u3000\u6570\u5e74\u6765\u8f9b\u9e4f\u4e5d\u592b\u5987\uff0c\u6240\u4f5c\u7684\u6076\u8ff9\uff0c\u81ea\u4e5f\u4e0d\u5728\u5c11\u6570\uff0c\u4f46\u540e\u6765\u8f9b\u4eea\u559c\u83b7\u9e9f\u513f\uff0c\u6709\u4e86\u540e\u4ee3 \u7684\u4eba\uff0c\u51e1\u4e8b\u5c31\u5904\u5904\u4e3a\u4e0b\u4e00\u4ee3\u7740\u60f3\uff0c\u8f9b\u9e4f\u4e5d\u81ea\u6709\u4e86\u8f9b\u6377\u4e4b\u540e\uff0c\u5fc3\u60c5\u4e5f\u4e0d\u4f8b\u5916\u5730\u53d8\u4e86\uff0c\u89c9\u5f97 \u81ea\u5df1\u6240\u505a\u6240\u4e3a\uff0c\u5b9e\u5728\u662f\u6709\u8fdd\u5929\u9053\uff0c\u53cc\u53cc\u4e00\u5546\u91cf\uff0c\u4fbf\u60f3\u6d17\u624b\u4e86\u3002 \u3000\u3000\u4f46\u201c\u5173\u4e2d\u4e5d\u8c6a\u201d\u7684\u7ec4\u7ec7\u751a\u662f\u4e25\u5bc6\uff0c\u9664\u4e86\u201c\u6b7b\u201d\u4e4b\u5916\uff0c\u8c01\u4e5f\u4e0d\u80fd\u9000\u51fa\uff0c\u800c\u4e14\u201c\u6d77\u5929\u53cc \u715e\u201d\u6b66\u529f\u9ad8\u51fa\u8f9b\u9e4f\u4e5d\u592b\u5987\u751a\u591a\uff0c\u4ed6\u4e24\u4eba\u4e5f\u4e0d\u6562\u5984\u52a8\uff0c\u8fd9\u6837\u4e00\u803d\u8bef\uff0c\u53c8\u662f\u597d\u591a\u5e74\uff0c\u4f46\u4ed6\u4e24 \u4eba\u5df2\u5728\u5904\u5904\u7559\u5fc3\u7740\u9003\u8d70\u7684\u673a\u4f1a\u3002 \u3000\u3000\u76f4\u5230\u8f9b\u6377\u4e03\u5c81\u90a3\u5e74\uff0c\u6d77\u5929\u53cc\u715e\u8fdc\u8d74\u585e\u5916\uff0c\u5173\u4e2d\u4e5d\u8c6a\u7559\u5728\u5173\u4e2d\u7684\uff0c\u53ea\u5269\u4e0b\u8001\u4e03\u5b50\u6bcd\u79bb \u9b42\u53df\u9648\u8bb0\u8d85\u548c\u8f9b\u9e4f\u4e5d\u592b\u5987\uff0c\u65bc\u662f\u8f9b\u9e4f\u4e5d\u592b\u59c6\u4fbf\u5012\u53cd\u603b\u575b\uff0c\u6740\u6b7b\u4e86\u5b50\u6bcd\u79bb\u9b42\u53df\u9648\u8bb0\u8d85\uff0c\u53cc \u53cc\u8fdc\u884c\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u56de\u5230\u5173\u4e2d\uff0c\u95fb\u60c5\u81ea\u662f\u5927\u6012\uff0c\u4fbf\u4f20\u8a00\u5929\u4e0b\u6b66\u6797\u7eff\u6797\uff0c\u8bf4\u662f\u4e94\u5e74\u4e2d\u201c\u6ec7\u6842\u53cc\u96d5\u201d \u82e5\u4e0d\u81ea\u884c\u6295\u5230\uff0c\u542c\u51ed\u5904\u7f6e\uff0c\u4e94\u5e74\u7684\u6700\u540e\u4e00\u4e2a\u6708\u5185\uff0c\u4fbf\u8981\u53d6\u4ed6\u5168\u5bb6\u6027\u547d\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u592b\u5987\uff0c\u987f\u89c9\u5929\u4e0b\u4e4b\u5927\uff0c\u7adf\u65e0\u4ed6\u4e09\u4eba\u5bb9\u8eab\u4e4b\u5904\uff0c\u8003\u8651\u518d\u4e09\uff0c\u89c9\u5f97\u53ea\u6709\u81ea\u5df1\u7684\u8001 \u5bb6\uff0c\u6606\u660e\u57ce\u90ca\u7684\u4e94\u534e\u5c71\u7554\u7684\u8f9b\u5bb6\u6751\uff0c\u662f\u4ed6\u4eec\u6700\u597d\u7684\u53bb\u5904\u3002 \u3000\u3000\u65bc\u662f\u4ed6\u592b\u5987\u53ca\u8f9b\u6377\u4e09\u4eba\uff0c\u624d\u9690\u5165\u8f9b\u5bb6\u6751\uff0c\u5b89\u7a33\u7684\u8fc7\u4e86\u51e0\u5e74\uff0c\u5374\u52fe\u4e0d\u6599\u5728\u4e94\u5e74\u4e4b\u671f\u7684 \u6700\u540e\u4e00\u5929\uff0c\u6d77\u5929\u53cc\u715e\u7adf\u8d76\u6765\u4e86\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u4e00\u5230\uff0c\u8f9b\u9e4f\u4e5d\u77e5\u9053\u51ed\u81ea\u5df1\u592b\u5987\u7684\u6b66\u529f\uff0c\u4e07\u4e07\u4e0d\u662f\u4ed6\u5f1f\u5144\u4e8c\u4eba\u7684\u5bf9\u624b\uff0c\u800c\u4e14 \u81ea\u5df1\u4e00\u60f3\uff0c\u4ee5\u524d\u6240\u505a\u7684\u6076\u8ff9\uff0c\u867d\u6b7b\u4ea6\u662f\u7f6a\u6709\u5e94\u5f97\uff0c\u53ea\u60f3\u8f6f\u8bed\u592e\u6c42\uff0c\u4e3a\u8f9b\u6377\u4fdd\u5168\u4e00\u6761\u6027\u547d\u3002 \u3000\u3000\u8f9b\u4eea\u5374\u5fcd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\uff0c\u9ad8\u58f0\u9a82\u4e86\u8d77\u6765\uff0c\u90a3\u6d77\u5929\u53cc\u715e\u672c\u662f\u5b6a\u751f\u5144\u5f1f\uff0c\u51fa\u4e16\u540e\u4e00\u4e2a\u662f\u56db \u80a2\u4e0d\u5168\uff0c\u4e00\u4e2a\u5374\u662f\u751f\u6765\u53c8\u804b\u53c8\u54d1\uff0c\u867d\u7136\u81ea\u5df1\u53d6\u540d\u5929\u6b8b\u3001\u5929\u5e9f\uff0c\u5374\u6700\u6068\u522b\u4eba\u79f0\u4ed6\u4eec\u6b8b\u5e9f\uff0c \u542c\u4e86\u8f9b\u4eea\u7684\u6012\u9a82\uff0c\u4f7f\u5f97\u4ed6\u4eec\u672c\u5df1\u6ee1\u8179\u7684\u6740\u673a\uff0c\u66f4\u6d53\u539a\u4e86\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u5431\u54af\u4e00\u58f0\u51b7\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8f9b\u4e5d\u5a18\u7684\u9aa8\u5934\u5012\u6bd4\u8f9b\u8001\u516d\u8fd8\u786c\u3002\u597d\uff0c\u597d\uff0c \u6211\u5f1f\u5144\u4eca\u5929\u82e5\u4e0d\u8ba9\u4f60\u6b7b\u5f97\u8212\u8212\u670d\u670d\u7684\uff0c\u4ece\u6b64\u6b66\u6797\u4e2d\u5c31\u7b97\u6ca1\u6709\u6211\u4eec\u2018\'\u6d77\u5929\u53cc\u2019\'\u8fd9\u5757\u5b57\u53f7\u201d \u3000\u3000\u8f9b\u4eea\u60b2\u58f0\u558a\u9053\uff1a\u201c\u9e4f\u4e5d\u8fd8\u4e0d\u8ddf\u4ed6\u4eec\u62fc\u4e86\u3002\u201d\u8bf4\u9053\u4eba\u5df2\u79bb\u5730\u800c\u8d77\uff0c\u7389\u624b\u7b95\u5f20\uff0c\u4e00\u62db \u201c\u9965\u9e70\u640f\u5154\u201d\u5e26\u7740\u864e\u864e\u98ce\u58f0\uff0c\u76f4\u5411\u5929\u6b8b\u7126\u5316\u51fb\u51fa\uff0c\u58f0\u52bf\u5012\u4e5f\u60ca\u4eba\u3002 \u3000\u3000\u90a3\u77e5\u5979\u76db\u6012\u4e4b\u4e0b\uff0c\u4e00\u51fa\u624b\u4fbf\u72af\u4e86\u5927\u5fcc\uff0c\u8fd9\u201c"\u9965\u9e70\u640f\u5154\u201d\u4e00\u5f0f\uff0c\u53ea\u80fd\u7528\u5bf9\u4ed8\u6bd4\u81ea\u5df1 \u6b66\u529f\u5f31\u7684\u5bf9\u624b\uff0c\u82e5\u662f\u9047\u5230\u5f3a\u624b\uff0c\u53ea\u6709\u66f4\u52a0\u5403\u4e8f\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u4e00\u89c1\u7231\u59bb\u4f7f\u51fa\u8fd9\u62db\uff0c\u4fbf\u77e5\u51f6\u591a\u5409\u5c11\uff0c\u4e00\u58f0\u60ca\u547c\uff0c\u5374\u4e5f\u6765\u4e0d\u53ca\u4e86\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u4e00\u89c1\u8f9b\u4eea\u51cc\u7a7a\u800c\u6765\uff0c\u8eab\u5f62\u731b\u7f29\uff0c\u672c\u5df2\u7578\u5c0f\u7684\u8eab\u4f53\uff0c\u5019\u53c8\u77ee\u4e86\u4e8c\u3001\u4e09\u5c3a\uff0c\u51e0 \u4e4e\u8d34\u7740\u5730\u9762\u4e86\uff0c\u8f9b\u4eea\u6ee1\u84c4\u52b2\u529b\uff0c\u89c1\u5bf9\u624b\u4e0d\u95ea\u4e0d\u907f\uff0c\u6b63\u60f3\u4e00\u51fb\u800c\u4e2d\uff0c\u81f3\u4e0d\u6d4e\u4e5f\u548c\u4ed6\u540c\u5f52\u65bc \u5c3d\uff0c\u5374\u4e0d\u6599\u7126\u5316\u7684\u7f29\u9aa8\u4e4b\u672f\uff0c\u5df1\u81f3\u7089\u706b\u7eaf\u9752\u4e4b\u5883\uff0c\u7b49\u5230\u8f9b\u4eea\u7684\u52b2\u529b\uff0c\u5df1\u81f3\u5f3a\u5b65\u4e4b\u672b\uff0c\u53cc \u624b\u95ea\u7535\u822c\u7684\u4f38\u51fa\uff0c\u6293\u4f4f\u4e86\u8f9b\u4eea\u7684\u4e00\u53cc\u7389\u624b\uff0c\u5fae\u5fae\u4e00\u6296\uff0c\u8f9b\u4eea\u4f46\u89c9\u4e00\u9635\u5267\u75db\uff0c\u53cc\u81c2\u4fbf\u8131\u8282 \u4e86\u3002 \u3000\u3000\u90a3\u8fb9\u8f9b\u4eea\u4e00\u58f0\u60e8\u547c\uff0c\u6454\u5012\u5730\u4e0a\uff0c\u8fd9\u8fb9\u8f9b\u9e4f\u4e5d\u4e5f\u662f\u5fc3\u80c6\u4ff1\u788e\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u8eab\u5f62\u4e00\u52a8\uff0c\u8d34\u5730\u98de\u6765\uff0c\u6781\u5feb\u7684\u56f4\u7740\u8f9b\u9e4f\u4e5d\u4e00\u8f6c\uff0c\u90a3\u79cd\u901f\u5ea6\u51e0\u4e4e\u662f\u8089\u773c\u6240\u770b \u4e0d\u89c1\u7684\uff0c\u7136\u540e\u7ad9\u5728\u8f9b\u9e4f\u4e5d\u7684\u8eab\u524d\uff0c\u51b7\u51b7\u5730\u8bf4\uff1a\u201c\u8f9b\u8001\u516d\uff0c\u4f60\u82e5\u80fd\u4e0d\u51fa\u8fd9\u5708\u5b50\u4e00\u6b65\uff0c\u53ea\u662f \u770b\u7740\u6211\u5f1f\u5144\u4e8c\u4eba\u5904\u7f6e\u4f60\u7684\u8001\u5a46\uff0c\u6211\u5f1f\u5144\u4fbf\u7834\u4e00\u6b21\u4f8b\uff0c\u9976\u4e86\u8fd9\u5c0f\u5b69\u7684\u6027\u547d\uff0c\u5426\u5219\u4f60\u82e5\u8981\u548c \u6211\u5f1f\u5144\u52a8\u624b\uff0c\u4e5f\u662f\u6089\u542c\u5c0a\u610f\uff0c\u4f60\u770b\u7740\u529e\u5427\uff01\u201d \u3000\u3000\u8f9b\u9e4f\u4e5d\u4f4e\u5934\u4e00\u770b\uff0c\u90a3\u575a\u786c\u7684\u6a90\u5eca\u7684\u5730\u4e0a\uff0c\u4e0d\u77e5\u88ab\u5929\u6b8b\u7126\u5316\u7528\u4ec0\u4e48\u624b\u6cd5\uff0c\u5212\u4e86\u4e00\u4e2a\u5708 \u5b50\uff0c\u4ed6\u53c8\u4e00\u671b\u8f9b\u6377\uff0c\u89c1\u4ed6\u7adf\u4ecd\u5750\u5728\u6905\u4e0a\uff0c\u6ee1\u8138\u4ff1\u662f\u575a\u6bc5\u4e4b\u8272\uff0c\u65e2\u4e0d\u60e7\u6015\uff0c\u4e5f\u4e0d\u60ca\u614c\uff0c\u7adf \u6bd4\u81ea\u5df1\u8fd8\u8981\u9547\u5b9a\u5f97\u591a\uff0c\u53ea\u662f\u773c\u4e2d\u5374\u662f\u6cea\u5149\u83b9\u83b9\uff0c\u50cf\u662f\u770b\u89c1\u6bcd\u4eb2\u53d7\u4f24\u6240\u81f4\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u5fc3\u4e2d\u4e0d\u7981\u5927\u5947\uff0c\u4ed6\u60f3\u4e0d\u51fa\u8fd9\u624d\u5341\u4e8c\u5c81\u7684\u5b69\u5b50\uff0c\u7ade\u6709\u8fd9\u6837\u7684\u6027\u683c\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c \u4ed6\u867d\u5bf9\u81ea\u5df1\u8fd9\u552f\u4e00\u7684\u513f\u5b50\u7231\u5230\u6781\u5904\uff0c\u4f46\u76f4\u5230\u4eca\u5929\u4e3a\u6b62\uff0c\u4ed6\u624d\u770b\u51fa\u81ea\u5df1\u8fd9\u4e2a\u513f\u5b50\u4e0e\u4f17\u4e0d\u540c \u7684\u5730\u65b9\uff0c\u4ed6\u77e5\u9053\uff0c\u82e5\u80fd\u8ba9\u8fd9\u5b69\u5b50\u957f\u5927\u6210\u4eba\uff0c\u5c06\u6765\u4e00\u5b9a\u4e0d\u662f\u51e1\u54c1\uff0c\u4ed6\u7edd\u4e0d\u80fd\u8ba9\u8fd9\u5b69\u5b50\u5c31\u6b64 \u6b7b\u53bb\uff0c\u90a3\u6015\u727a\u7272\u4e00\u5207\uff0c\u4ed6\u4e5f\u5728\u6240\u4e0d\u60dc\u3002 \u3000\u3000\u8fd9\u5ff5\u5934\u5728\u4ed6\u8111\u4e2d\u4e00\u95ea\u800c\u8fc7\uff0c\u4ed6\u77e5\u9053\u201c"\u6d77\u5929\u53cc\u715e\u201d\u5c06\u65bd\u4e8e\u4ed6\u59bb\u5b50\u8eab\u4e0a\u7684\u624b\u6bb5\uff0c\u5fc5\u5b9a \u662f\u60e8\u4e0d\u5fcd\u7779\u7684\uff0c\u4f46\u4ed6\u51b3\u5b9a\u5fcd\u53d7\u4e0b\u6765\uff0c\u4ed6\u60f3\u53cd\u8bc1\u603b\u662f\u4e00\u6b7b\uff0c\u7528\u4ec0\u4e48\u65b9\u6cd5\u5904\u6b7b\uff0c\u53c8\u6709\u4ec0\u4e48\u5206 \u522b\u5462\uff01 \u3000\u3000\u5929\u6b8b\u7126\u5316\u4ece\u4ed6\u7684\u795e\u8272\u4e2d\uff0c\u5df2\u77e5\u9053\u8f9b\u9e4f\u4e5d\u613f\u610f\u505a\u81ea\u5df1\u8fd9\u5e55\u620f\u7684\u89c2\u4f17\uff0c\u9ad8\u5174\u5730\u7b11\u4e86\u7b11\uff0c \u4e00\u79cd\u4e0e\u751f\u800c\u6765\u7684\u6b8b\u9177\u4e4b\u6027\uff0c\u4f7f\u5f97\u4ed6\u6709\u4e00\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u75af\u72c2\u60f3\u6cd5\uff0c\u90a3\u5c31\u662f\u5f53\u522b\u4eba\u8d8a\u75db\u82e6\u7684 \u65f6\u5019\uff0c\u4ed6\u5c31\u8d8a\u5feb\u4e50\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u56de\u8f6c\u5934\u53bb\uff0c\u6781\u5feb\u5730\u5411\u90a3\u59cb\u7ec8\u9759\u7acb\u672b\u52a8\u7684\u5929\u5e9f\u7126\u52b3\u505a\u4e86\u51e0\u4e2a\u522b\u4eba\u65e0\u6cd5\u4e86\u89e3\u7684\u624b \u5f0f\uff0c\u7126\u52b3\u4e5f\u5f00\u5fc3\u7684\u7b11\u4e86\u3002\u4ed6\u4e24\u4eba\u8138\u4e0a\u7684\u8fd9\u4e00\u79cd\u7b11\u5bb9\uff0c\u5f80\u5f80\u4ee4\u4eba\u89c1\u4e86\u6709\u6bd4\u201c\u6012\u201d\u66f4\u53ef\u6015\u7684 \u611f\u89c9\uff0c\u8fd9\u662f\u5f53\u4e00\u4e2a\u9965\u997f\u7684\u91ce\u517d\u770b\u89c1\u4e00\u4e2a\u4ed6\u5373\u53ef\u5f97\u5230\u7684\u730e\u7269\u7684\u7b11\u5bb9\u3002\u65b9\u624d\u75db\u660f\u8fc7\u53bb\u7684\u8f9b\u4eea\uff0c \u6b64\u523b\u88ab\u5730\u4e0a\u7684\u5bd2\u51b7\u4e00\u6fc0\uff0c\u6b63\u81ea\u82cf\u9192\u4e86\uff0c\u53d1\u51fa\u4e00\u9635\u9635\u7684\u547b\u541f\uff0c\u7126\u5316\u6ee1\u610f\u5730\u542c\u7740\u8fd9\u58f0\u97f3\uff0c\u7a81 \u5730\u95ea\u8eab\u8fc7\u53bb\uff0c\u5728\u5979\u8eab\u4e0a\u70b9\u4e86\u4e00\u4e0b\uff0c\u8fd9\u662f\u201c\u6d77\u5929\u53cc\u715e\u201d\u72ec\u95e8\u7684\u70b9\u7a74\u624b\u6cd5\u3002\u5b83\u4f7f\u4eba\u6d51\u8eab\u4e0d\u80fd \u52a8\uff0c\u4f46\u5374\u5e76\u672a\u5931\u53bb\u77e5\u89c9\u3002 \u3000\u3000\u7136\u540e\u4ed6\u5411\u7126\u52b3\u5fae\u4e00\u70b9\u5934\uff0c\u7126\u52b3\u5fae\u4e00\u6643\u8eab\uff0c\u4fef\u4e0b\u8eab\u53bb\uff0c\u4f38\u624b\u6293\u5728\u8f9b\u4eea\u7684\u8863\u670d\u4e0a\uff0c\u968f\u624b \u4e00\u63ed\uff0c\u6574\u6574\u7684\u6495\u53bb\u4e86\u4e00\u5927\u7247\u3002 \u3000\u3000\u4e8e\u662f\u8f9b\u4eea\u90a3\u6210\u719f\u800c\u4e30\u6ee1\u7684\u80f8\u819b\uff0c\u4fbf\u66b4\u9732\u5728\u897f\u98ce\u91cc\uff0c\u66b4\u9732\u5728\u6bd4\u897f\u98ce\u66f4\u5bd2\u51b7\u7684\u6d77\u5929\u53cc\u715e \u7684\u76ee\u5149\u91cc\u3002\u8f9b\u9e4f\u4e5d\u53ea\u89c9\u5fc3\u4e2d\u4e00\u9635\u5267\u75db\uff0c\u6068\u4e0d\u5f97\u7acb\u523b\u8fc7\u53bb\u4e00\u62fc\uff0c\u4f46\u4ed6\u624b\u6309\u7740\u7684\u662f\u4ed6\u513f\u5b50\u7684 \u8eab\u8eaf\uff0c\u4ed6\u7684\u7259\u7d27\u7d27\u54ac\u4f4f\uff0c\u7259\u8ddf\u91cc\u7684\u8840\uff0c\u4ece\u4ed6\u7684\u5634\u89d2\u6e17\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8f9b\u4eea\u6b64\u65f6\u6240\u53d7\u7684\u82e6\u96be\uff0c\u66f4\u662f\u975e\u4efb\u4f55\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9\u51fa\u6765\u7684\uff0c\u5979\u611f\u5230\u8111\u524d\u4e00\u51c9\uff0c\u63a5\u7740\u53c8 \u662f\u51e0\u4e0b\u731b\u626f\uff0c\u5979\u6d51\u8eab\u4fbf\u5b8c\u5168\u66b4\u9732\u5728\u5bd2\u98ce\u91cc\uff0c\u53cc\u81c2\u7684\u75db\u695a\uff0c\u867d\u5df2\u6f88\u9aa8\uff0c\u5bd2\u98ce\u4e5f\u4f7f\u5979\u6218\u6817\uff0c \u5374\u90fd\u6bd4\u4e0d\u4e0a\u5979\u5fc3\u7533\u4e4b\u7f9e\u8fb1\u4e0e\u7edd\u671b\uff0c\u5979\u611f\u5230\u8eab\u4e0a\u6bcf\u4e00\u90e8\u5206\u90fd\u53d7\u5230\u88ad\u51fb\uff0c\u5979\u610f\u8bc6\u5230\uff0c\u5c06\u6709\u66f4 \u53ef\u6015\u7684\u4e8b\u60c5\u53d1\u751f\u3002 \u3000\u3000\u4f46\u5979\u9664\u4e86\u547b\u541f\u800c\u5916\uff0c\u4e0d\u80fd\u505a\u4efb\u4f55\u53cd\u6297\u7684\u4e8b\uff0c\u6b64\u523b\u5979\u611f\u5230\u53c8\u75db\u3001\u53c8\u51b7\u3001\u53c8\u7f9e\u3001\u53c8\u82e6\uff0c \u518d\u52a0\u4e0a\u5fc3\u7406\u7684\u7edd\u671b\uff0c\u8eab\u4e0a\u88ab\u88ad\u51fb\u6240\u4ea7\u751f \u3000\u3000\u7684\u9ebb\u8fa3\uff0c\u5979\u75db\u6068\u7740\u201c"\u6d77\u5929\u53cc\u715e\u201d\uff0c\u5979\u4e5f\u75db\u6839\u7740\u81ea\u5df1\u7684\u4e08\u592b\uff0c\u5979\u751a\u81f3\u618e\u6068\u4e16\u4e0a\u6bcf\u4e00 \u4e2a\u4eba\uff0c\u4e8e\u662f\u5979\u95ed\u4e0a\u773c\u6674\uff0c\u5207\u9f7f\u601d\u9053\uff1a\u201c\u5373\u4f7f\u6211\u6b7b\u4e86\uff0c\u6211\u4e5f\u8981\u53d8\u4e3a\u9b54\u9b3c\uff0c\u5411\u6bcf\u4e00\u4e2a\u4eba\u62a5 \u4ec7\u3002\u201d \u3000\u3000\u5341\u4e8c\u5c81\u7684\u8f9b\u6377\uff0c\u5904\u8eab\u5728\u8fd9\u79cd\u6b8b\u5fcd\u800c\u51e0\u4e4e\u706d\u7edd\u4eba\u6027\u7684\u573a\u5408\u91cc\uff0c\u59d4\u5b9e\u662f\u592a\u5e74\u8f7b\u4e5f\u592a\u65e0\u8f9c \u4e86\uff0c\u867d\u7136\u4eba\u4e16\u95f4\u5927\u591a\u6570\u4e8b\uff0c\u4ed6\u5c1a\u4e0d\u80fd\u4e86\u89e3\uff0c\u4f46\u4e0a\u5929\u5374\u8d4b\u7ed9\u4ed6\u4e00\u79cd\u5947\u602a\u7684\u672c\u80fd\uff0c\u90a3\u5c31\u662f\u65e0 \u8bba\u5728\u4efb\u4f55\u73af\u5883\u4e4b\u4e0b\uff0c\u7edd\u4e0d\u505a\u81ea\u8eab\u80fd\u529b\u6240\u4e0d\u80fd\u53ca\u7684\u4e8b\u60c5\uff0c\u4e5f\u8bb8\u8fd9\u662f\u4e0a\u5929\u5bf9\u4ed6\u7684\u4e0d\u5e78\u906d\u9047\u6240 \u4f5c\u7684\u4e00\u4e2a\u8865\u507f\u5427\uff0c\u7136\u800c\u8fd9\u8865\u507f\u53c8\u662f\u4f55\u7b49\u7684\u5947\u602a\u5440\uff01 \u3000\u3000\u4ed6\u773c\u770b\u7740\u81ea\u5df1\u7684\u4eb2\u751f\u6bcd\u4eb2\uff0c\u5728\u53d7\u7740\u4e24\u4e2a\u91ce\u517d\u822c\u7684\u4eba\u7684\u51cc\u8fb1\uff0c\u81ea\u5df1\u7684\u7236\u4eb2\u4e3a\u7740\u81ea\u5df1\uff0c \u5728\u5fcd\u53d7\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u5fcd\u53d7\u7684\u6b3a\u4fae\uff0c\u4ed6\u867d\u7136\u96be\u53d7\uff0c\u4f46\u5374\u4e00\u70b9\u4e5f\u6ca1\u6709\u54ed\u95f9\uff0c\u4e5f\u6ca1\u6709\u5927\u591a\u6570\u5728 \u4ed6\u8fd9\u6837\u7684\u5e74\u7eaa\uff0c\u5904\u8eab\u5728\u8fd9\u79cd\u573a\u5408\u91cc\u7684\u5b69\u5b50\u6240\u4e0d\u8be5\u6709\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u82e5\u4ed6\u662f\u61e6\u5f31\u7684\uff0c\u4ed6\u8be5\u6218\u6817\uff0c\u54ed\u6ce3\u4e86\uff0c\u82e5\u4ed6\u662f\u52c7\u6562\u7684\uff0c\u4ed6\u4e5f\u8be5\u629b\u53bb\u4e00\u5207\uff0c\u53bb\u4fdd\u62a4\u81ea\u5df1 \u7684\u6bcd\u4eb2\uff0c\u4f46\u4ed6\u4efb\u4f55\u4e8b\u90fd\u6ca1\u6709\u505a\uff0c\u4ed6\u53ea\u662f\u5e26\u7740\u4e00\u79cd\u5947\u602a\u7684\u8868\u60c5\uff0c\u5446\u5446\u5730\u5750\u5728\u90a3\u91cc\uff0c\u201c\u6d77\u5929 \u53cc\u715e\u201d\u82e5\u77e5\u9053\u8fd9\u79cd\u8868\u60c5\u91cc\u6240\u5305\u542b\u7684\u575a\u5fcd\u7684\u51b3\u5fc3\uff0c\u6050\u6015\u4f1a\u4e0d\u987e\u4e00\u5207\u8bfa\u8a00\uff0c\u800c\u5c06\u4ed6\u6740\u5374\u7684\u3002 \u3000\u3000\u4f46\u662f\u201c\u6d77\u5929\u53cc\u715e\u201d\u600e\u4f1a\u53bb\u6ce8\u610f\u8fd9\u4e2a\u5b69\u5b50\uff0c\u4ed6\u4eec\u6b63\u88ab\u4e00\u79cd\u75af\u72c2\u7684\u91ce\u517d\u822c\u7684\u6ee1\u8db3\u7684\u60c5\u7eea \u6240\u6df9\u6ca1\uff0c\u4ed6\u4eec\u7528\u624b\u3001\u7528\u811a\u3001\u7528\u4e00\u5207\u5351\u52a3\u7684\u884c\u4e3a\uff0c\u53bb\u6b3a\u51cc\u4e00\u4e2a\u6beb\u65e0\u62b5\u6297\u7684\u5973\u5b50\uff0c\u800c\u4ee5\u6b64\u4e3a \u4e50\u3002 \u3000\u3000\u7136\u540e\u4ed6\u4eec\u6ee1\u8db3\u4e86\uff0c\u4ed6\u4eec\u56de\u8fc7\u5934\u6765\uff0c\u5929\u6b8b\u7126\u5316\u7528\u4ed6\u90a3\u7578\u5f62\u7684\u624b\uff0c\u6307\u7740\u8f9b\u9e4f\u4e5d\u602a\u7b11\u9053\uff1a \u201c\u597d\uff0c\u8f9b\u8001\u516d\uff0c\u6709\u4f60\u7684\uff0c\u975e\u4f46\u4f60\u8fd9\u5b69\u5b50\u7684\u4e00\u6761\u547d\uff0c\u603b\u7b97\u88ab\u4f60\u6361\u56de\u6765\u4e86\uff0c\u800c\u4e14\u6211\u7126\u8001\u5927\u4e00 \u9ad8\u5174\uff0c\u8fde\u4f60\u4e5f\u9976\u4e86\uff0c\u4f60\u82e5\u4ecd\u7136\u8ddf\u7740\u6211\uff0c\u6211\u4e5f\u4ecd\u7136\u50cf\u4ee5\u524d\u4e00\u6837\u7684\u5f85\u4f60\u3002\u201d \u3000\u3000\u8f9b\u9e4f\u4e5d\u56de\u5934\u671b\u4e86\u8f9b\u6377\u4e00\u773c\uff0c\u90a3\u662f\u4ed6\u727a\u7272\u4e86\u81ea\u5df1\u7684\u4e00\u5207\uff0c\u751a\u81f3\u727a\u7272\u81ea\u5df1\u7684\u751f\u547d\u800c\u6362\u53d6 \u7684\u4ed6\u7684\u5ef6\u7eed\u7684\u751f\u547d\uff0c\u7a81\u7136\uff0c\u4ed6\u5fc3\u4e2d\u6d8c\u8d77\u4e07\u5343\u60c5\u7eea\uff0c\u7136\u540e\u56de\u8fc7\u5934\u53bb\uff0c\u5bf9\u7126\u5316\u8bf4\u9053\uff1a\u201c\u4f60\u7b54 \u5e94\u5728\u5341\u5e74\u4e4b\u5185\uff0c\u51b3\u4e0d\u4f24\u8fd9\u5b69\u5b50\u3002\u201d \u3000\u3000\u5929\u6b8b\u7126\u5316\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7126\u8001\u5927\u8a00\u51fa\u5fc5\u884c\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u8f9b\u9e4f\u4e5d\u8bf4\uff1a\u201c\u597d\uff0c\u90a3\u6211\u5c31\u653e\u5fc3\u4e86\u3002\u201d\u968f\u7740\u8bf4\u8bdd\uff0c\u4ed6\u7f13\u7f13\u8d70\u8fd1\u7126\u5316\u7684\u8eab\u540e\uff0c\u5929\u6b8b\u7126\u5316 \u7684\u80cc\u540e\uff0c\u6b63\u51c4\u60e8\u800c\u65e0\u52a9\u7684\u8eba\u7740\u8f9b\u4eea\u7684\u7f8e\u4e3d\u7684\u88f8\u9732\u8eab\u8eaf\uff0c\u4ed6\u773c\u4e2d\u55b7\u51fa\u6012\u706b\uff0c\u731b\u5730\u51fa\u624b\uff0c\u4e00 \u62db\u201c\u6bd4\u7ffc\u53cc\u98de\u201d\u5de6\u53f3\u4e24\u624b\uff0c\u53cc\u53cc\u9f50\u51fa\uff0c\u4e00\u53d6\u5929\u6b8b\u7126\u5316\u8033\u65c1\u7684\u201c\u7384\u73e0\u201d\u91cd\u7a74\uff0c\u4e00\u53d6\u4ed6\u5589\u4e0b \u7684\u547d\u8109\u6240\u5728\u3002 \u3000\u3000\u8fd9\u201c\u6bd4\u7ffc\u53cc\u98de\u201d\u4e43\u662f\u8f9b\u9e4f\u4e5d\u4ed7\u4ee5\u6210\u540d\u7684\u201c\u795e\u9e4f\u638c\u6cd5\u201d\u91cc\u7684\u4e00\u8bb0\u715e\u624b\uff0c\u8f9b\u9e4f\u4e5d\u8fd9\u4e00\u51fb\uff0c \u66f4\u662f\u4e0d\u77e5\u5305\u542b\u7740\u591a\u5c11\u8f9b\u9178\u548c\u60b2\u6124\uff0c\u5a01\u529b\u81ea\u662f\u4e0d\u540c\u5bfb\u5e38\uff0c\u4f55\u51b5\u5929\u6b8b\u7126\u5316\u6b63\u5728\u5fd7\u5f97\u610f\u6ee1\uff0c\u518d \u4e5f\u60f3\u4e0d\u5230\u8f9b\u9e4f\u4e5d\u4f1a\u51fa\u6b64\u4e00\u51fb\uff0c\u7b49\u5230\u731b\u4e00\u60ca\u89c9\uff0c\u638c\u98ce\u5df2\u81ea\u4e34\u5934\u4e86\u3002 \u3000\u3000\u4f46\u5929\u6b8b\u7126\u5316\u80fd\u79f0\u96c4\u73af\u5b87\uff0c\u786e\u975e\u5e78\u81f4\uff0c\u8f9b\u9e4f\u4e5d\u638c\u51fa\u5982\u98ce\uff0c\u7126\u5316\u7684\u8116\u5b50\u50cf\u662f\u7a81\u7136\u62c9\u957f\u4e86 \u51e0\u5bf8\uff0c\u521a\u597d\u591f\u4e0d\u4e0a\u90e8\u4f4d\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u6b64\u51fb\uff0c\u672c\u662f\u5fd7\u5728\u5fc5\u5f97\uff0c\u62db\u4e00\u843d\u7a7a\uff0c\u4ed6\u5c31\u77e5\u9053\u81ea\u5df1\u5180\u6c42\u4e00\u547d\u7684\u5e0c\u671b\uff0c\u5df2\u662f\u7834\u706d\uff0c \u4f46\u4ed6\u672c\u662f\u62b1\u7740\u5fc5\u6b7b\u4e4b\u5fc3\uff0c\u8eab\u8eaf\u5fae\u77ee\uff0c\u201c\u5e73\u6c99\u843d\u7ffc\u201d\u53cc\u638c\u4ea4\u9519\u800c\u4e0b\uff0c\u638c\u5fc3\u5916\u9732\uff0c\u731b\u51fb\u80f8\u819b\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u9634\u607b\u607b\u5730\u4e00\u58f0\u72de\u7b11\uff0c\u811a\u4e0b\u5fae\u4e00\u9519\u6b65\uff0c\u4fa7\u8eab\u8eb2\u8fc7\u6b64\u62db\uff0c\u53f3\u638c\u4e00\u6325\uff0c\u76f4\u70b9\u8f9b\u9e4f \u4e5d\u9f3b\u8fb9\u201c\u6c89\u9999\u201d\u7a74\uff0c\u8eb2\u62db\u53d1\u62db\uff0c\u6d51\u5982\u4e00\u4f53\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u4e00\u54ac\u94a2\u7259\uff0c\u786c\u751f\u751f\u5c06\u8eab\u8eaf\u64a4\u4e86\u56de\u6765\uff0c\u53cc\u638c\u8fde\u73af\u62cd\u51fa\uff0c\u65bd\u5c55\u8d77\u4ed6\u6d78\u6deb\u591a\u5e74\u7684 \u201c\u795e\u96d5\u638c\u6cd5\u201d\uff0c\u975e\u4f46\u62db\u62db\u90fd\u662f\u5f80\u5929\u6b8b\u7126\u5316\u81f4\u547d\u4e4b\u5904\u4e0b\u624b\uff0c\u800c\u4e14\u4e1d\u6beb\u4e0d\u987e\u81ea\u8eab\u7684\u5b89\u5371\u3002\u62db \u62db\u90fd\u662f\u540c\u5f52\u4e8e\u5c3d\u7684\u8fdb\u624b\u62db\u6570\uff0c\u5b8c\u5168\u8c41\u51fa\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u79cd\u52a8\u624b\u7684\u65b9\u6cd5\uff0c\u9664\u975e\u548c\u5bf9\u624b\u6709\u4e0d\u53ef\u89e3\u7684\u6df1\u4ec7\u5927\u6028\uff0c\u800c\u4e14\u62a5\u5b9a\u5fc5\u6b7b\u51b3\u5fc3\uff0c\u5728\u6b66\u6797\u4e2d \u662f\u65e0\u4eba\u4f7f\u7528\u7684\uff0c\u5929\u6b8b\u7126\u5316\u867d\u7136\u6b66\u529f\u901a\u7384\uff0c\u4f46\u5bf9\u8fd9\u79cd\u62db\u5f0f\uff0c\u5e94\u4ed8\u8d77\u6765\uff0c\u4e5f\u9887\u89c9\u5403\u529b\uff0c\u6700\u4e3b \u8981\u7684\u5f53\u7136\u662f\u8f9b\u9e4f\u4e5d\u529f\u529b\u4ea6\u662f\u4e0d\u51e1\uff0c\u4f46\u8f9b\u9e4f\u4e5d\u82e5\u60f3\u4f24\u5f97\u7126\u5316\uff0c\u5374\u4e5f\u662f\u7edd\u4e0d\u53ef\u80fd\u7684\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8f9b\u9e4f\u4e5d\u4fbf\u89c9\u5f97\u540e\u529b\u5df2\u662f\u4e0d\u7ee7\uff0c\u987b\u77e5\u8fd9\u7b49\u6253\u6cd5\u6700\u662f\u8017\u8d39\u771f\u529b\uff0c\u4ed6\u773c\u770b\u7126\u5316 \u4ecd\u7136\u4ece\u5bb9\u5730\u5316\u89e3\u7740\u81ea\u5df1\u7684\u62db\u5f0f\uff0c\u6ca1\u6709\u4e00\u4e1d\u53ef\u4e58\u7684\u673a\u4f1a\uff0c\u800c\u4e14\u5929\u5e9f\u7126\u52b3\u4e5f\u59cb\u7ec8\u51b7\u773c\u7ad9\u5728\u4e00 \u65c1\uff0c\u82e5\u662f\u4ed6\u4e00\u51fa\u624b\uff0c\u81ea\u5df1\u53ea\u6015\u7acb\u523b\u4fbf\u8981\u96be\u9003\u4e00\u6b7b\uff0c\u800c\u4e14\u6b7b\u5f97\u66f4\u60e8\u3002 \u3000\u3000\u8f9b\u5bb6\u7684\u9662\u5b50\u5e76\u4e0d\u751a\u5927\uff0c\u4ed6\u4eec\u5728\u9662\u4e2d\u6781\u5feb\u7684\u817e\u8d8a\u7740\u8eab\u8eaf\uff0c\u51e0\u6b21\u90fd\u4ece\u5929\u5e9f\u7126\u52b3\u7684\u8eab\u65c1\uff0c \u64e6\u8eab\u800c\u8fc7\uff0c\u4f46\u7126\u52b3\u4f9d\u7136\u51b7\u9759\u5730\u7ad9\u7740\uff0c\u5e76\u672a\u4e1d\u6beb\u79fb\u52a8\u8fc7\u3002 \u3000\u3000\u6b64\u65f6\u8f9b\u9e4f\u4e5d\u7684\u4e00\u767e\u4e8c\u4e03\u5f0f\u201c\u795e\u96d5\u638c\u6cd5\u201d\u582a\u582a\u5df1\u5c06\u4f7f\u5c3d\uff0c\u8f9b\u9e4f\u4e5d\u6b63\u81ea\u4f7f\u5230\u6700\u540e\u7684\u8fde\u73af \u5341\u4e8c\u5f0f\u4e2d\u7684\u201c\u675f\u7ffc\u7a7f\u4e91\u201d\uff0c\u4e0b\u9762\u4fbf\u662f\u201c"\u795e\u96d5\u5c55\u7ffc\u201d\u3002\u8fd9\u8fde\u73af\u5341\u4e8c\u5f0f\uff0c\u62db\u4e2d\u5957\u62db\uff0c\u8fde\u7ef5 \u4e0d\u65ad\uff0c\u4e43\u662f\u201c"\u795e\u96d5\u638c\u6cd5\u201d\u4e2d\u7684\u7cbe\u534e\u6240\u5728\uff0c\u5929\u6b8b\u7126\u5316\u867d\u81ea\u6301\u7edd\u6280\uff0c\u4f46\u4e5f\u4e0d\u6562\u592a\u8fc7\u5927\u610f\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u5728\u4f7f\u5230\u8fd9\u62db\u65f6\uff0c\u8eab\u8eaf\u53c8\u9010\u6e10\u79fb\u81f3\u5929\u5e9f\u7126\u52b3\u7684\u8eab\u524d\uff0c\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u5ffd\u5730\u4e00\u4e2a \u5ff5\u5934\u5728\u5fc3\u4e2d\u7535\u5149\u706b\u77f3\u822c\u95ea\u8fc7\uff0c\u4ed6\u53cc\u81c2\u5fae\u5206\uff0c\u770b\u4f3c\u95e8\u6237\u5927\u5f00\uff0c\u5176\u5b9e\u4e2d\u85cf\u5371\u673a\uff0c\u4e0b\u9762\u4fbf\u662f\u8be5 \u6c89\u8098\u66f2\u7729\uff0c\u4e00\u62db\u201c\u7834\u98ce\u800c\u8d77\u201d\uff0c\u5929\u6b8b\u7126\u5316\u4e5f\u671f\u9053\u4ed6\u8fd9\u4e0b\u4e00\u5f0f\u5fc5\u662f\u9634\u62db\u3002 \u3000\u3000\u4f46\u4ed6\u5ffd\u5730\u539f\u5f0f\u672b\u53d8\uff0c\u4fa7\u8eab\u6251\u8eab\u4fa7\u7684\u5929\u5e9f\u7126\u52b3\uff0c\u5f20\u81c2\u7d27\u7d27\u5c06\u7126\u52b3\u62b1\u4f4f\uff0c\u5f20\u81c2\u62b1\u4eba\uff0c\u539f \u662f\u5e02\u4e95\u6cfc\u76ae\u65e0\u8d56\u6253\u67b6\u7684\u884c\u5f84\uff0c\u201c\u6d77\u5916\u53cc\u715e\u201d\u518d\u4e5f\u672a\u60f3\u5230\u4ed6\u4f1a\u4f7f\u51fa\u6b64\u62db\uff0c\u5929\u6b8b\u7126\u5316\u89c1\u4ed6\u5ffd \u7136\u820d\u4e86\u81ea\u5df1\u800c\u53bb\u62b1\u4f4f\u7126\u52b3\uff0c\u66f4\u662f\u4e00\u535a\uff0c\u7136\u800c\u66f4\u8fd8\u6709\u4ee4\u4ed6\u65e0\u6cd5\u60f3\u5230\u4e4b\u4e8b\u3002 \u3000\u3000\u8f9b\u9e4f\u4e5d\u5c06\u4e00\u8eab\u529f\u529b\uff0c\u5168\u968f\u5728\u8fd9\u53cc\u81c2\u4e0a\uff0c\u4f3c\u94c1\u531d\u7740\u5929\u5e9f\u7126\u52b3\u7684\u8eab\u8eaf\uff0c\u7126\u52b3\u6b63\u662f\u4e00\u60ca\uff0c \u5374\u89c1\u8f9b\u9e4f\u4e5d\u7adf\u5f20\u53e3\u5411\u4ed6\u5589\u5934\u54ac\u6765\uff0c\u7126\u52b3\u5e73\u65e5\u4ee5\u638c\u529b\u3001\u5185\u529b\u89c1\u957f\uff0c\u4e0e\u5929\u6b8b\u7126\u5316\u4e4b\u8f6f\u529f\uff0c\u8f7b \u529f\uff0c\u5927\u76f8\u8fd0\u5ead\uff0c\u7f29\u8eab\u6613\u5f62\u4e4b\u672f\uff0c\u4e5f\u8fdc\u8fdc\u4e0d\u53ca\u4e43\u5144\uff0c\u4ed6\u6f5c\u7528\u5185\u529b\uff0c\u771f\u6c14\u8d2f\u8fbe\u56db\u80a2\uff0c\u60f3\u5c06\u8f9b \u9e4f\u4e5d\u9707\u843d\uff0c\u4f46\u5728\u987b\u592e\u4e4b\u95f4\uff0c\u5374\u4e5f\u65e0\u6cd5\u505a\u5230\u3002 \u3000\u3000\u8fd9\u4e8b\u60c5\u7684\u53d8\u5316\uff0c\u662f\u90a3\u4e48\u5feb\uff0c\u7b14\u4e0b\u6240\u5199\u7684\u90a3\u4e48\u591a\u4e8b\uff0c\u5728\u5f53\u65f6\u771f\u662f\u970e\u65f6\u4e4b\u95f4\uff0c\u5929\u5e9f\u7126\u52b3 \u82e5\u8ba9\u8f9b\u9e4f\u4e5d\u54ac\u4e2d\u5589\u5934\uff0c\u5373\u4f7f\u4ed6\u6709\u5929\u5927\u7684\u6b66\u529f\uff0c\u4e5f\u5f97\u7acb\u523b\u6c14\u7edd\uff0c\u4ed6\u5982\u4f55\u4e0d\u60ca\uff0c\u4f46\u4ed6\u6bd5\u7adf\u662f \u4e45\u7ecf\u5927\u654c\uff0c\u5728\u5371\u96be\u4e2d\uff0c\u81ea\u7136\u4f1a\u751f\u51fa\u4e00\u79cd\u8d85\u4e8e\u5e38\u4eba\u7684\u5e94\u53d8\u672c\u80fd\u3002 \u3000\u3000\u4ed6\u53cc\u80a9\u4e00\u8038\uff0c\u5934\u5f80\u4e0b\u4fef\uff0c\u5c06\u90a3\u8106\u5f31\u7684\u5589\u5934\uff0c\u631f\u5728\u4e0b\u989a\u4e0e\u80f8\u80c1\u4e4b\u95f4\uff0c\u8f9b\u9e4f\u4e5d\u4e00\u53e3\u54ac\u6765\uff0c \u5374\u54ac\u5728\u4ed6\u5507\u4e0e\u4e0b\u9102\u4e4b\u95f4\uff0c\u5929\u5e9f\u7126\u52b3\u75db\u6012\u4e4b\u4e0b\uff0c\u53cc\u58c1\u4e00\u6296\uff0c\u4e00\u58f0\u66b4\u559d\uff0c\u80f8\u8179\u6697\u7528\u5185\u5bb6\u5176\u529b\uff0c \u6536\u7f29\u4e4b\u95f4\uff0c\u624b\u638c\u4ece\u7f1d\u9699\u4e2d\u7a7f\u51fa\uff0c\u4e00\u70b9\u5728\u8f9b\u9e4f\u4e5d\u80c1\u4e0b\u7684\u6b7b\u7a74\u3002 \u3000\u3000\u90a3\u80c1\u4e0b\u4e43\u5fc5\u6b7b\u4e4b\u7a74\uff0c\u4f55\u51b5\u5929\u5e9f\u7126\u52b3\u6307\u4e0a\u6f5c\u529b\u60ca\u4eba\uff0c\u8f9b\u9e4f\u4e5d\u8fde\u54fc\u90fd\u6ca1\u54fc\u51fa\u6765\uff0c\u4fbf\u5373\u6b7b \u53bb\u3002 \u3000\u3000\u5929\u5e9f\u7126\u52b3\u6478\u7740\u90a3\u5df2\u88ab\u8f9b\u9e4f\u4e5d\u54ac\u5f97\u51fa\u8840\u7684\u4e0b\u989a\uff0c\u51b7\u7136\u671b\u7740\u90a3\u5730\u4e0b\u7684\u5c38\u8eab\uff0c\u8138\u4e0a\u4f9d\u7136\u4e00 \u65e0\u8868\u60c5\uff0c\u50cf\u662f\u4e16\u95f4\u7684\u4efb\u4f55\u4e8b\uff0c\u90fd\u4e0d\u80fd\u7275\u52a8\u4ed6\u9762\u4e0a\u4e00\u4e1d\u808c\u8089\u4f3c\u7684\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u771f\u4fbf\u5b9c\u4e86\u4ed6\uff0c\u8ba9\u4ed6\u6b7b\u5f97\u8fd9\u4e48\u75db\u5feb\u3002\u201d\u4ed6\u7a81\u7136\u60f3\u8d77\u8fd9\u9662\u4e2d\u9664\u4ed6 \u5144\u5f1f\u4e24\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u5c1a\u672a\u6b7b\u7684\u4eba\uff0c\u4e8e\u662f\u4ed6\u8f6c\u8fc7\u5934\u53bb\u627e\uff0c\u53ea\u89c1\u8f9b\u6377\u4ecd\u7136\u5750\u5728\u684c\u65c1\uff0c\u8138 \u4e0a\u6ee1\u662f\u6cea\u75d5\uff0c\u53cc\u62f3\u7d27\u5f31\u5730\u63e1\u7740\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u5fc3\u4e2d\u6751\u9053\uff1a\u201c\u8fd9\u5c0f\u5b69\u5b50\u600e\u5730\u51ed\u822c\u5947\u602a\uff0c\u83ab\u8bf4\u662f\u8fd9\u6837\u4e2a\u5c0f\u513f\uff0c\u5c31\u7b97\u662f\u4e2a\u666e\u901a \u58ee\u6c49\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4e5f\u9c9c\u6709\u80fd\u4e0d\u52a8\u58f0\u8272\u7684\uff0c\u6b64\u5b50\u82e5\u4e0d\u662f\u75f4\u5446\uff0c\u5c31\u5b9a\u5fc5\u662f\u7279\u522b\u806a\u9896\u2026\u2026 \u82e5\u662f\u75f4\u5446\u7f62\u4e86\uff0c\u82e5\u662f\u7279\u522b\u806a\u9896\uff0c\u5c06\u6765\u5c82\u4e0d\u662f\u4e2a\u7978\u5bb3\u3002\u201d \u3000\u3000\u60f3\u7740\u60f3\u7740\uff0c\u4ed6\u8d70\u5230\u8f9b\u6377\u4e4b\u524d\uff0c\u7f13\u7f13\u4e3e\u8d77\u624b\u6765\uff0c\u60f3\u4e00\u638c\u62cd\u4e0b\uff0c\u514d\u5f97\u5c06\u6765\u53cd\u6210\u517b\u606f\u4e4b\u60a3\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u638c\u4e0b\u53bb\uff0c\u83ab\u8bf4\u662f\u8f9b\u6377\u8840\u8089\u4e4b\u8eaf\uff0c\u5373\u4f7f\u662f\u767e\u7ec3\u91d1\u521a\uff0c\u4e5f\u67cf\u7acb\u523b\u4fbf\u6210\u7c89\u788e\uff0c\u4ed6\u76ee \u6ce8\u7740\u8f9b\u6377\uff0c\u8f9b\u6377\u4e5f\u6b63\u4ee5\u6ee1\u542b\u6012\u6bd2\u7684\u773c\u5149\u770b\u7740\u4ed6\u3002 \u3000\u3000\u4f46\u5929\u6b8b\u5929\u5e9f\u4e24\u4eba\u7684\u5fc3\u60c5\uff0c\u6bcf\u6bcf\u4e0d\u80fd\u5e38\u7406\u63a8\u6d4b\uff0c\u4ed6\u4eec\u706d\u7edd\u4eba\u6027\u53ca\u81f3\u9876\u70b9\uff0c\u5bf9\u4e00\u8a00\u4e4b\u8bfa \u5374\u770b\u5f97\u751a\u91cd\uff0c\u4ed6\u8f6c\u5ff5\u60f3\u53ca\uff1a\u201c\u4f46\u6211\u5df1\u627f\u8bfa\u4e86\u8f9b\u9e4f\u4e5d\uff0c\u51b3\u4e0d\u6740\u6b7b\u8fd9\u4e2a\u5b69\u5b50\uff0c\u82e5\u662f\u7559\u4e0b\u4e86\u4ed6\uff0c \u5c06\u6765\u4e5f\u8bb8\u5012\u6210\u4e86\uff0c\u6211\u5fc3\u8179\u4e4b\u7978\u2026\u2026\u201d\u4ed6\u4e3e\u8d77\u7684\u53f3\u638c\uff0c\u8fdf\u8fdf\u672a\u66fe\u843d\u4e0b\u3002 \u3000\u3000\u662f\u51fb\u4e0b\u6291\u6216\u662f\u4e0d\u51fb\u5462\uff0c\u8fd9\u5ff5\u5934\u5728\u4ed6\u5fc3\u4e2d\u8fdf\u7591\u8005\uff0c\u8f9b\u6377\u7684\u6027\u547d\uff0c\u4e5f\u60ac\u5728\u4ed6\u4e00\u5ff5\u4e4b\u4e2d\uff0c \u5728\u8f9b\u6377\u672c\u8eab\u6765\u8bf4\uff0c\u4ed6\u6ca1\u6709\u4e1d\u6beb\u80fd\u529b\u6765\u6539\u53d8\u8fd9\u4e9b\u3002 \u3000\u3000\u591c\u51c9\u5982\u6c34\uff0c\u800c\u4e14\u7a81\u7136\u522e\u8d77\u98ce\u6765\uff0c\u7531\u8fd9\u5c0f\u5c0f\u7684\u9662\u5b50\u901a\u5230\u540e\u9662\u7684\u4e00\u6761\u5c0f\u5f84\u4e0a\uff0c\u5ffd\u7136\u4f20\u6765 \u6c89\u91cd\u7684\u811a\u6b65\u58f0\uff0c\u800c\u4e14\u8fd8\u50cf\u4e0d\u6b62\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u79cd\u6c89\u91cd\u7684\u6b65\u5b50\uff0c\u5728\u8fd9\u9759\u5bc2\u7684\u5bd2\u591c\u91cc\uff0c\u542c\u6765\u662f\u90a3\u4e48\u523a\u8033\uff0c\u5929\u6b8b\u7126\u5316\u5fae\u5fae\u4e00\u60ca\u3002\u4e00\u6325 \u624b\uff0c\u4ed6\u5f1f\u5144\u4e24\u4eba\u5fc3\u610f\u76f8\u901a\uff0c\u53cc\u53cc\u4e00\u7eb5\uff0c\u9690\u5728\u9662\u7684\u9634\u9ed1\u4e4b\u5904\u3002 \u3000\u3000\u90a3\u77e5\u90a3\u7531\u540e\u9662\u4e2d\u8d70\u51fa\u7684\uff0c\u4e0d\u8fc7\u662f\u4e00\u6761\u725b\uff0c \u3000\u3000\u4e0d\u4f55\u600e\u7684\uff0c\u5728\u6df1\u591c\u91cc\u7adf\u4f1a\u79bb\u5f00\u53a9\u623f\uff0c\u201c\u6d77\u5929\u53cc\u715e\u201d\u89c1\u4e86\uff0c\u76f8\u5bf9\u4f5c\u4e00\u82e6\u7b11\u3002 \u3000\u3000\u90a3\u6761\u725b\u60f3\u662f\u5e73\u65e5\u8c03\u5f97\u597d\uff0c\u751f\u5f97\u53c8\u80a5\u53c8\u58ee\uff0c\u4eae\u8e44\u626c\u89d2\uff0c\u5012\u4e5f\u5a01\u731b\u5f97\u5f88\uff0c\u5929\u6b8b\u7126\u5316\u89c1\u4e86\uff0c \u5fc3\u4e2d\u4fca\u7136\u4e00\u52a8\uff0c\u601d\u9053\uff1a\u201c\u6211\u6240\u7b54\u5e94\u7684\uff0c\u53ea\u662f\u6211\u5144\u5f1f\u4e8c\u4eba\u51b3\u4e0d\u4f24\u6740\u6b64\u5b50\uff0c\u5374\u672a\u7b54\u5e94\u725b\u4e5f\u4e0d \u80fd\u4f24\u5bb3\u6b64\u5b50\u5440\u3002\u201d \u3000\u3000\u4ed6\u60f3\u5230\u8fd9\u91cc\uff0c\u8138\u4e0a\u9732\u51fa\u7b11\u5bb9\uff0c\u50cf\u662f\u4e00\u4ef6\u751a\u96be\u89e3\u51b3\u4e4b\u4e8b\uff0c\u5ffd\u7136\u5f97\u5230\u4e86\u7ed3\u679c\uff0c\u8fd9\u79cd\u5fc3\u7406\uff0c \u548c\u4ed6\u7684\u8fd9\u79cd\u89e3\u91ca\uff0c\u4e5f\u662f\u6781\u96be\u7406\u89e3\u7684\u3002 \u3000\u3000\u90a3\u725b\u8d70\u5230\u9662\u4e2d\uff0c\u9635\u98ce\u5439\u6765\uff0c\u60f3\u662f\u4e5f\u89c9\u5f97\u6709\u4e9b\u5bd2\u51b7\uff0c\u6602\u5934\u4f4e\u9e23\u4e86\u4e00\u58f0\uff0c\u53c8\u5411\u6765\u8def\u8d70\u53bb\uff0c \u5929\u6b8b\u7126\u5316\u5fae\u4e00\u98d8\u8eab\u6321\u5728\u90a3\u725b\u7684\u524d\u9762\u3002 \u3000\u3000\u90a3\u725b\u731b\u4e00\u53d7\u60ca\uff0c\u53cc\u89d2\u4e00\u62b5\uff0c\u4fbf\u8981\u5f80\u524d\u51b2\u53bb\uff0c\u5929\u6b8b\u7126\u5316\u51fa\u624b\u5982\u98ce\uff0c\u63e1\u4f4f\u90a3\u725b\u7684\u53cc\u89d2\uff0c \u8fd9\u7b49\u5185\u5bb6\u7684\u6f5c\u529b\uff0c\u4f55\u7b49\u60ca\u4eba\uff0c\u90a3\u725b\u7a7a\u81ea\u4f7f\u51fa\u86ee\u529b\uff0c\u518d\u4e5f\u4f11\u60f3\u5f80\u524d\u79fb\u52a8\u534a\u6b65\uff0c\u7a7a\u81ea\u628a\u5730\u4e0a \u7684\u6ce5\u6c99\u8e22\u5f97\u6f2b\u5929\u7eb7\u98de\u3002 \u3000\u3000\u7126\u5316\u5de6\u624b\u4e0d\u52a8\uff0c\u817e\u51fa\u53f3\u624b\u6765\uff0c\u671d\u5929\u5e9f\u7126\u52b3\u6253\u4e86\u51e0\u4e2a\u624b\u52bf\uff0c\u90a3\u662f\u6781\u7b80\u5355\u7684\u51e0\u4e2a\u624b\u5f0f\uff0c \u4f46\u5176\u4e2d\u5374\u5305\u6db5\u4e86\u8bb8\u591a\u610f\u601d\uff0c\u8fd9\u662f\u4ed6\u4eec\u591a\u5e74\u6765\u6240\u4e60\u60ef\u7684\u6c9f\u901a\u5fc3\u610f\u4e4b\u6cd5\uff0c\u9664\u4e86\u8fd9\u79cd\u624b\u5f0f\u4e4b\u5916\uff0c \u5929\u5e9f\u7126\u52b3\u518d\u4e5f\u4e0d\u4e86\u89e3\u4e16\u4eba\u4efb\u4f55\u4e00\u79cd\u522b\u4eba\u5411\u4ed6\u8868\u9732\u7684\u5fc3\u610f\u3002 \u3000\u3000\u56e0\u4e4b\u81ea\u5e7c\u4ee5\u6765\uff0c\u5929\u6b8b\u7126\u5316\u7684\u610f\u5fd7\uff0c\u6c38\u8fdc\u4ee3\u8868\u7740\u5929\u5e9f\u7126\u52b3\u7684\u610f\u5fd7\uff0c\u4ed6\u4eec\u4e24\u4eba\u50cf\u662f\u4e00\u4ef6 \u4e0d\u53ef\u5206\u79bb\u7684\u7ed3\u5408\u4f53\uff0c\u5b9e\u662f\u4e8c\u800c\u4e3a\u4e00\u7684\u3002 \u3000\u3000\u5929\u5e9f\u7126\u52b3\uff0c\u6781\u5feb\u5730\u6253\u5f00\u4e86\u9662\u524d\u7684\u5927\u95e8\uff0c\u518d\u95ea\u8eab\u56de\u6765\uff0c\u6a2a\u624b\u4e00\u63a0\uff0c\u5c06\u8f9b\u6377\u631f\u5230\u80c1\u4e0b\u3002 \u3000\u3000\u8f9b\u6377\u65e2\u4e0d\u60ca\u614c\uff0c\u4e5f\u4e0d\u6323\u624e\uff0c\u56e0\u4e3a\u4ed6\u77e5\u9053\uff0c\u8fd9\u4e00\u5207\u90fd\u662f\u591a\u4f59\u7684\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u7684\u547d\u8fd0\uff0c \u662f\u88ab\u64cd\u5728\u8fd9\u4e24\u4e2a\u4f3c\u4eba\u975e\u4eba\u7684\u602a\u7269\u624b\u4e2d\uff0c\u4f46\u662f\u4ed6\u5fc3\u91cc\u5374\u6709\u4e00\u79cd\u5947\u602a\u7684\u81ea\u4fe1\uff0c\u4ed6\u76f8\u4fe1\u603b\u6709\u4e00 \u5929\u4ed6\u8981\u4ee5\u8840\u6765\u507f\u9000\u4eca\u65e5\u7684\u4e00\u5207\u7684\u3002 \u3000\u3000\u4ed6\u52a8\u4e5f\u4e0d\u52a8\u5730\u88ab\u631f\u5230\u90a3\u6761\u5df1\u6e10\u53d1\u72c2\u6027\u7684\u725b\u8eab\u4e0a\uff0c\u90a3\u6761\u725b\u6b63\u5728\u6781\u5ea6\u7684\u98a0\u6c9b\u4e2d\uff0c\u4ed6\u4e00\u5750 \u4e0a\u53bb\uff0c\u5c31\u4e0d\u5f97\u4e0d\u7d27\u7d27\u62b1\u7740\u725b\u7684\u8116\u5b50\uff0c\u8fd9\u6837\u624d\u4e0d\u81f4\u4ece\u725b\u8eab\u4e0a\u629b\u4e0b\u6765\uff0c\u4ed6\u867d\u7136\u5e76\u4e0d\u77e5\u9053\u88ab\u631f \u4e0a\u8fd9\u725b\u80cc\u7a76\u7adf\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4f46\u5374\u660e\u4e86\u8fd9\u4e00\u5b9a\u662f\u5173\u7cfb\u7740\u4ed6\u7684\u751f\u547d\u7684\u3002 \u3000\u3000\u5929\u5e9f\u7126\u52b3\u5c06\u8f9b\u6377\u631f\u4e0a\u725b\u80cc\u540e\u4f38\u624b\u6349\u4f4f\u90a3\u725b\u7684\u53e6\u4e00\u89d2\uff0c\u5f80\u5916\u4e00\u626f\uff0c\u90a3\u725b\u5e9e\u5927\u7684\u8eab\u8eaf\uff0c \u88ab\u4ed6\u8fd9\u4e00\u626f\uff0c\u786c\u751f\u751f\u7ed9\u65cb\u4e86\u8fc7\u6765\uff0c\u725b\u89d2\u7684\u6839\u90e8\uff0c\u4e5f\u6e17\u51fa\u8840\u6765\u3002 \u3000\u3000\u90a3\u725b\u5267\u75db\u4e4b\u4e0b\uff0c\u72c2\u6027\u66f4\u662f\u5927\u53d1\uff0c\u5b83\u88ab\u5236\u5728\u90a3\u79cd\u60ca\u4eba\u529b\u9053\u4e4b\u4e0b\uff0c\u524d\u8fdb\u540e\u9000\u90fd\u4e0d\u80fd\u591f\uff0c \u53ea\u6709\u53d1\u72c2\u5730\u8038\u52a8\u7740\u8eab\u8eaf\uff0c\u5c06\u7f6e\u8eab\u725b\u80cc\u4e4b\u4e0a\u7684\u8f9b\u6377\uff0c\u98a0\u6c9b\u5f97\u80f8\u80c3\u4e4b\u95f4\uff0c\u751f\u51fa\u4e00\u79cd\u8bf4\u4e0d\u51fa\u5730 \u96be\u53d7\uff0c\u5c31\u50cf\u662f\u7acb\u523b\u4fbf\u8981\u5455\u5410\u4e86\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\uff0c\u5c06\u90a3\u63e1\u7740\u725b\u89d2\u7684\u5de6\u624b\u4e00\u677e\uff0c\u624b\u638c\u987a\u52bf\u5212\u4e0b\uff0c\u90a3\u4e48\u575a\u97e7\u7684\u725b\u76ae\uff0c\u88ab\u4ed6\u8fd9\u4e00 \u638c\uff0c\u7adf\u6df1\u6df1\u5730\u5212\u4e86\u4e00\u9053\u53e3\u5b50\uff0c\u9c9c\u8840\u6cca\u6cca\u6d41\u51fa\u3002 \u3000\u3000\u90a3\u725b\u81ea\u662f\u6012\u6781\uff0c\u5929\u6b8b\u7126\u52b3\u521a\u677e\u5f00\u624b\u638c\uff0c\u90a3\u725b\u4fbf\u7bad\u4e5f\u4f3c\u7684\u81ea\u95e8\u53e3\u7a9c\u51fa\uff0c\u4eae\u8e44\u72c2\u5954\u3002 \u3000\u3000\u8f9b\u6377\u7684\u7236\u6bcd\uff0c\u867d\u662f\u8eab\u6000\u6b66\u6280\uff0c\u4f46\u81ea\u8f9b\u6377\u51fa\u5de6\u540e\uff0c\u5373\u5bf9\u6b66\u6797\u751f\u51fa\u538c\u5026\uff0c\u662f\u4ee5\u6839\u672c\u6ca1\u6709 \u4f20\u6388\u6b66\u6280\u4e4b\u4e8b\uff0c\u8f9b\u6377\u9664\u4e86\u8eab\u4f53\u56e0\u7236\u6bcd\u5584\u65bc\u8c03\u517b\uff0c\u800c\u6bd4\u5e38\u7ae5\u7a0d\u58ee\u4e4b\u5916\uff0c\u8fde\u6700\u6d45\u8584\u7684\u6b66\u6280\u90fd \u4e00\u7a83\u4e0d\u901a\u3002 \u3000\u3000\u90a3\u725b\u53d1\u72c2\u5730\u5728\u6df1\u591c\u5bc2\u9759\u7684\u539f\u91ce\u4e0a\u5954\u8dd1\u7740\uff0c\u8f9b\u6377\u4f46\u89c9\u8eab\u65c1\u4e4b\u7269\uff0c\u50cf\u95ea\u7535\u822c\u5730\u5012\u9000\u7740\uff0c \u800c\u4e14\u725b\u53d1\u72c2\u6027\uff0c\u90a3\u79cd\u98a0\u6c9b\u4e0e\u52a8\u8361\uff0c\u66f4\u4e0d\u662f\u4e00\u4e2a\u5341\u4e8c\u5c81\u7684\u5e7c\u7ae5\u6240\u80fd\u5fcd\u53d7\u7684\uff0c\u4ed6\u51e0\u4e4e\u60f3\u677e\u5f00 \u4ed6\u90a3\u7d27\u62b1\u7740\u725b\u8116\u5b50\u7684\u53cc\u624b\uff0c\u8ba9\u81ea\u5df1\u8dcc\u843d\u4e0b\u6765\uff0c\u4f46\u662f\u8fd9\u79cd\u751f\u4e0e\u6b7b\u4e4b\u95f4\u7684\u6289\u62e9\uff0c\u4ed6\u5374\u6ca1\u6709\u52c7 \u6c14\u6765\u9009\u62e9\uff0c\u5373\u4f7f\u987b\u53d7\u5982\u6b64\u7684\u75db\u695a\u3002 \u3000\u3000\u56e0\u4e3a\u4ed6\u5bf9\u81ea\u5df1\u7684\u6027\u547d\uff0c\u62b1\u7740\u6781\u5927\u7684\u671f\u671b\uff0c\u6709\u8bb8\u591a\u4e8b\u662f\u90a3\u51c4\u60e8\u800c\u75db\u82e6\u7684\u4e8b\uff0c\u6b64\u523b\u4ecd\u7136 \u5728\u4ed6\u8111\u6d77\u4e2d\u76d8\u65cb\u7740\uff0c\u4ed6\u5bf9\u81ea\u5df2\u7acb\u4e0b\u8a93\u7ea6\uff0c\u8fd9\u4e9b\u90fd\u662f\u4ed6\u8981\u4eb2\u81ea\u53bb\u507f\u4ed8\u7684\uff0c\u56e0\u6b64\u4ed6\u5fc5\u987b\u73cd\u60dc \u81ea\u5df1\u7684\u751f\u547d\u3002 \u3000\u3000\u8fd9\u4e9b\u601d\u60f3\u5bf9\u4e00\u4e2a\u50cf\u4ed6\u8fd9\u6837\u7684\u5e7c\u7ae5\u6765\u8bf4\uff0c\u867d\u7136\u662f\u6709\u4e9b\u6a21\u7cca\u800c\u9065\u8fdc\uff0c\u4f46\u662f\u60b2\u60e8\u4e8b\u5b9e\u7684\u56de \u5fc6\uff0c\u5bf9\u4ed6\u5374\u662f\u65e0\u6bd4\u7684\u9c9c\u660e\uff0c\u4ed6\u867d\u6ca1\u6709\u80fd\u529b\u53bb\u514b\u670d\u8fd9\u6076\u52a3\u7684\u547d\u8fd0\uff0c\u4f46\u4ed6\u4e0d\u613f\u610f\u81ea\u5df1\u53bb\u52a9\u957f \u8fd9\u79cd\u6076\u52a3\u7684\u547d\u8fd0\uff0c\u56e0\u6b64\u4ed6\u51b3\u4e0d\u677e\u624b\u5730\u7d27\u62b1\u7740\u725b\u7684\u8eab\u5b50\uff0c\u5373\u4f7f\u751f\u547d\u5df2\u7136\u65e0\u671b\uff0c\u4ed6\u4e5f\u8981\u6323\u624e \u5230\u6700\u540e\u4e00\u523b\u3002 \u3000\u3000\u7136\u800c\u4e00\u4e2a\u6beb\u65e0\u6b66\u6280\u7684\u5e7c\u7ae5\uff0c\u7f6e\u8eab\u5728\u4e00\u6761\u72c2\u725b\u7684\u80cc\u4e0a\uff0c\u90a3\u751f\u5b58\u7684\u5e0c\u671b\uff0c\u53c8\u662f\u591a\u4e48\u6e3a\u832b \u5462\u3002 \u3000\u3000\u90a3\u725b\u4e5f\u4e0d\u77e5\u5954\u4e86\u591a\u5c11\u65f6\u95f4\uff0c\u591a\u5c11\u8def\u7a0b\uff0c\u6e10\u6e10\u8f9b\u6377\u7684\u53cc\u81c2\u5df2\u7531\u9178\u75db\uff0c\u800c\u53d8\u4e3a\u9ebb\u6728\u4e86\uff0c \u4ed6\u7684\u795e\u667a\uff0c\u4e5f\u6e10\u6e10\u8ff7\u4e71\uff0c\u53ea\u89c9\u5f97\u90a3\u725b\u50cf\u662f\u5728\u5f80\u9ad8\u5904\u800c\u5954\uff0c\u4eff\u4f5b\u662f\u4e0a\u4e86\u5c71\u5761\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u80fd \u770b\u5f97\u6e05\u695a\u3002 \u3000\u3000\u5929\u8272\u4e5f\u6e10\u6e10\u4eae\u4e86\uff0c\u8f9b\u6377\u7684\u5fc3\u91cc\uff0c\u53ea\u5e0c\u671b\u9047\u5230\u8def\u4eba\uff0c\u5c06\u8fd9\u5954\u725b\u5236\u4f4f\uff0c\u4f46\u5373\u4f7f\u9047\u5230\u8def\u4eba\uff0c \u53c8\u600e\u80fd\u5236\u5f97\u4f4f\u8fd9\u72c2\u725b\u5462\u3002 \u3000\u3000\u4ed6\u53c8\u5e0c\u671b\u8fd9\u725b\u529b\u7aed\u800c\u5012\uff0c\u4f46\u4ed6\u4e5f\u77e5\u9053\uff0c\u6bd4\u8fd9\u725b\u66f4\u5148\u652f\u6301\u4e0d\u4f4f\u7684\u662f\u4ed6\uff0c\u4ed6\u6240\u5269\u4f59\u7684\u4f53 \u529b\uff0c\u5df2\u65e0\u6cd5\u652f\u6301\u4ed6\u591a\u4e45\u4e86\uff0c\u4ed6\u5728\u6b64\u79cd\u60c5\u51b5\u4e4b\u4e0b\u8dcc\u5012\uff0c\u90a3\u91cc\u8fd8\u6709\u547d\u5728\u3002 \u3000\u3000\u4f46\u6b64\u65f6\u4ed6\u7684\u8111\u6d77\u4e2d\uff0c\u5df2\u8ff7\u4e71\u5f97\u751a\u81f3\u8fde\u8fd9\u4e9b\u95ee\u9898\u90fd\u65e0\u6cd5\u518d\u53bb\u8003\u8651\u4e86\uff0c\u6d51\u8eab\u7684\u4e00\u5207\uff0c\u90fd \u50cf\u662f\u4e0d\u518d\u5c5e\u65bc\u4ed6\uff0c\u6240\u6709\u7684\u4e8b\uff0c\u4e5f\u79bb\u4ed6\u66f4\u9065\u8fdc\u4e86\u3002 \u3000\u3000\u5728\u4ed6\u7684\u611f\u89c9\u4e2d\uff0c\u8fd9\u4e00\u6bb5\u65f6\u95f4\u662f\u6f2b\u957f\u7684\uff0c\u5176\u5b9e\u4e5f\u4e0d\u8fc7\u534a\u4e2a\u591a\u65f6\u8fb0\u800c\u5df2\uff0c\u90a3\u725b\u81ea\u8f9b\u5bb6\u6751 \u843d\u8352\u72c2\u5954\uff0c\u4e5f\u4e0d\u8fa8\u8def\u9014\uff0c\u7adf\u95ef\u4e0a\u4e86\u4e94\u534e\u5c71\u3002 \u3000\u3000\u4e94\u534e\u5c71\u5c71\u52bf\u672c\u4e0d\u751a\u9669\u3002\u4f46\u662f\u65e0\u8bba\u4eba\u755c\uff0c\u5728\u98a0\u72c2\u4e4b\u4e2d\uff0c\u5f80\u5f80\u5374\u80fd\u505a\u51fa\u5e73\u65e5\u65e0\u6cd5\u505a\u5230\u4e4b \u4e8b\uff0c\u90a3\u725b\u529e\u662f\u5982\u662f\uff0c\u975e\u4f46\u4e0a\u4e86\u5c71\uff0c\u800c\u4e14\u5165\u4e86\u5c71\u7684\u6df1\u5904\u3002 \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u89c9\u5f97\u90a3\u725b\u672c\u662f\u4e00\u76f4\u7a9c\u7740\u7684\uff0c\u6b64\u523b\u7adf\u7ed5\u8d77\u5708\u5b50\u6765\u4e86\uff0c\u4ed6\u4e94\u89c9\u5f97\u5934\u66f4\u662f\u6655\uff0c\u5ffd \u7136\u5730\u90a3\u725b\u72c2\u5954\u4e4b\u52bf\uff0c\u731b\u7136\u4e00\u987f\uff0c\u4ed6\u5c31\u4ece\u725b\u5934\u4e0a\u76f4\u98de\u4e86\u51fa\u53bb\uff0c\u7830\u5730\u843d\u5728\u96ea\u5730\u4e0a\uff0c\u4fbf\u5931\u53bb\u4e86 \u77e5\u89c9\u3002 \u3000\u3000\u5728\u4ed6\u5c1a\u672b\u5931\u53bb\u77e5\u89c9\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u4ed6\u4eff\u4f5b\u89c9\u5f97\u90a3\u725b\u7adf\u50cf\u88ab\u4eba\u4e00\u629b\uff0c\u4e5f\u8fdc\u8fdc\u843d\u5728\u96ea\u5730\u4e0a\u3002 \u3000\u3000\u6df1\u5c71\u91cc\u7684\u6c14\u5019\uff0c\u6bd4\u8f9b\u5bb6\u6751\u8981\u51b7\u5f97\u591a\u4e86\uff0c\u800c\u4e14\u96ea\u82b1\u4e0d\u65ad\u98d8\u843d\uff0c\u5931\u53bb\u77e5\u89c9\u7684\u8f9b\u6377\uff0c\u8eba\u5728 \u96ea\u5730\u91cc\uff0c\u5e76\u672a\u591a\u4e45\uff0c\u5c31\u9192\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5f53\u4ed6\u7741\u5f00\u773c\u6674\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4ed6\u770b\u89c1\u4e00\u4e2a\u7855\u957f\u7684\u5f71\u5b50\u4f2b\u7acb\u5728\u4ed6\u800c\u524d\uff0c\u4e8e\u662f\u4ed6\u52aa\u529b\u6e05\u4e86 \u6e05\u81ea\u5df1\u7684\u773c\u5e18\uff0c\u4ed6\u770b\u89c1\u4e00\u4e2a\u7626\u524a\u800c\u6a35\u60b4\u7684\u4eba\u6b63\u4e5f\u4f4e\u5934\u671b\u7740\u4ed6\u3002\u4ed6\u4eba\u662f\u90a3\u4e48\u7684\u6a35\u60b4\u800c\u8870\u5f31\uff0c \u9762\u5b54\u51e0\u4e4e\u6ca1\u6709\u4e00\u4e1d\u8840\u8272\uff0c\u50cf\u662f\u521a\u4ece\u9634\u6697\u7684\u575f\u5893\u91cc\u8d70\u51fa\u6765\u4f3c\u7684\uff0c\u4f2b\u7acb\u5728\u6e05\u6668\u6296\u5ced\u7684\u98ce\u548c\u96ea \u91cc\uff0c\u663e\u5f97\u90a3\u6837\u5730\u4e0d\u7a33\u5b9a\uff0c\u867d\u7136\u4ed6\u60f3\u633a\u76f4\u5730\u7ad9\u7740\uff0c\u7136\u800c\u5374\u50cf\u968f\u65f6\u90fd\u4f1a\u8dcc\u5012\u3002 \u3000\u3000\u98ce\u96ea\u4ea4\u52a0\uff0c\u90a3\u4eba\u4ec5\u7a7f\u7740\u4ef6\u5355\u8584\u7684\u6587\u58eb\u8863\u886b\uff0c\u5728\u5bd2\u98ce\u91cc\u4e0d\u4f4f\u5730\u54c6\u55e6\u7740\uff0c\u770b\u89c1\u8f9b\u6377\u9192\u6765\uff0c \u8138\u4e0a\u6cdb\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u90a3\u7b11\u662f\u4eb2\u5207\u800c\u6e29\u6696\u7684\u3002 \u3000\u3000\u8f9b\u6377\u770b\u89c1\u8fd9\u7b11\u5bb9\uff0c\u987f\u65f6\u5fd8\u5374\u4e86\u4ed6\u90a3\u79cd\u964c\u751f\u6050\u60e7\uff0c\u60f3\u6323\u624e\u7740\u5750\u8d85\u6765\uff0c\u4ed6\u8ba4\u4e3a\u7ad9\u5728\u4ed6\u9762 \u524d\u7684\u4eba\uff0c\u662f\u4e2a\u6025\u5207\u9700\u8981\u7740\u5e2e\u52a9\u7684\u4eba\uff0c\u867d\u7136\u4ed6\u81ea\u5df1\u4e5f\u662f\u90a3\u4e48\u5730\u4e0d\u5e78\uff0c\u8fd9\u6b63\u662f\u8f9b\u6377\u7684\u5584\u826f\u4e4b \u5904\u3002 \u3000\u3000\u90a3\u4eba\u50cf\u662f\u5df2\u6d1e\u6089\u4e86\u8f9b\u6377\u7684\u5fc3\u4e8b\uff0c\u5fae\u5f31\u5730\u5f20\u53e3\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u52a8\uff0c\u518d\u8eba\u4e00\u4f1a\u3002\u201d\u7136\u800c\u8f9b \u6377\u4f9d\u65e7\u5728\u6323\u624e\u722c\u8d77\u6765\uff0c\u90a3\u4eba\u76ee\u5149\u9661\u7136\u4e00\u53d8\uff0c\u90a3\u4e48\u6a35\u60b4\u7684\u9762\u5b54\uff0c\u4ecd\u7136\u663e\u51fa\u4e00\u79cd\u96be\u8a00\u7684\u5a01\u529b\u3002 \u3000\u3000\u4ed6\u4f38\u624b\u4e00\u52a8\uff0c\u60f3\u963b\u4f4f\u8f9b\u6377\uff0c\u7136\u800c\u5374\u4e00\u4e2a\u8e09\u8dc4\uff0c\u865a\u8f6f\u5730\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8bd5\u7740\u722c\u8d77\u6765\u7684\u8f9b\u6377\uff0c\u5374\u4e0d\u77e5\u9053\u82e5\u975e\u81ea\u5df1\u673a\u7f18\u592a\u5de7\uff0c\u6b64\u523b\u7109\u6709\u547d\u5728\uff0c\u7136\u800c\u5728\u7ecf\u8fc7\u90a3\u4e48 \u957f\u5730\u98a0\u6c9b\uff0c\u90a3\u4e48\u82e6\u7684\u6298\u78e8\u4e4b\u540e\uff0c\u4ed6\u7eb5\u7136\u4f53\u683c\u518d\u5065\uff0c\u4e5f\u4e0d\u80fd\u518d\u4f2b\u7acb\u8d77\u6765\u4e86\uff0c\u6251\u5730\uff0c\u53c8\u8eba\u5728 \u96ea\u5730\u91cc\u3002 \u3000\u3000\u8f9b\u6377\u548c\u964c\u751f\u7684\u4eba\uff0c\u5e76\u6392\u5367\u5012\u5728\u96ea\u5730\u91cc\uff0c\u6b64\u5730\u867d\u7136\u5e7d\u7edd\uff0c\u4f46\u8f9b\u633a\u5374\u4e0d\u611f\u5230\u5bc2\u83ab\uff0c\u56e0\u4e3a \u4ed6\u7684\u8eab\u65c1\uff0c\u5c31\u6709\u4eba\u5728\u966a\u4f34\u7740\uff0c\u800c\u4e14\u4ed6\u5e7c\u5c0f\u7684\u5fc3\u7075\uff0c\u5bf9\u90a3\u964c\u751f\u4eba\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u7adf\u751f\u51fa\u4e00\u79cd \u5947\u602a\u7684\u60c5\u611f\u3002 \u3000\u3000\u4ed6\u867d\u5468\u8eab\u5931\u529b\uff0c\u4f46\u795e\u667a\u5374\u751a\u6e05\u695a\uff0c\u4ed6\u56db\u5468\u6253\u91cf\u7740\u4ed6\u6240\u5b58\u8eab\u7684\u5730\u65b9\uff0c\u7adf\u662f\u4e00\u4e2a\u666f\u8272\u7edd \u7f8e\u7684\u5e7d\u8c37\uff0c\u866c\u679d\u6697\u9999\uff0c\u56db\u5468\u90fd\u662f\u6885\u82b1\u3002 \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u542c\u5230\u90a3\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u5c0f\u5b69\uff0c\u600e\u4f1a\u9a91\u7740\u72c2\u725b\uff0c\u8dd1\u5230\u8fd9\u91cc\u6765\uff0c\u4f60\u662f\u8c01\uff0c\u4f60\u7684 \u5bb6\u4f4f\u5728\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u4ed6\u8fd9\u51e0\u53e5\u8bdd\u95f4\u7684\u58f0\u97f3\u751a\u662f\u51b7\u5cfb\uff0c\u8f9b\u6377\u6115\u4e86\u4e00\u4e0b\uff0c\u90a3\u60b2\u60e8\u7684\u56de\u4ebf\uff0c\u91cd \u53c8\u5728\u4ed6\u8111\u4e2d\u6cdb\u8d77\uff0c\u4f7f\u5f97\u4ed6\u4e0d\u7531\u81ea\u4e3b\u5730\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u4eba\u89c1\u4ed6\u54ed\u4e86\uff0c\u548c\u7f13\u5730\u95ee\u9053\uff1a\u201c\u4f60\u522b\u54ed\uff0c\u6709\u4ec0\u4e48\u96be\u8fc7\u7684\u4e8b\uff0c\u53ea\u7ba1\u5bf9\u6211\u8bb2\u3002\u201d \u3000\u3000\u8f9b\u6377\u867d\u7136\u8ba4\u4e3a\u5373\u4f7f\u5c06\u4ed6\u8fd9\u79cd\u60b2\u51c4\u800c\u6b8b\u9177\u7684\u906d\u9047\uff0c\u544a\u8bc9\u8fd9\u770b\u6765\u6bd4\u4ed6\u66f4\u5b71\u5f31\u7684\u4eba\uff0c\u4e0d\u4f1a \u6709\u4ec0\u4e48\u7528\u5904\uff0c\u4f46\u5728\u6b64\u523b\uff0c\u4ed6\u5df2\u5c06\u8fd9\u4e0e\u4ed6\u76f8\u5904\u5728\u8fd9\u6e3a\u65e0\u4eba\u8e2a\u7684\u5e7d\u8c37\u91cc\u7684\u4eba\uff0c\u770b\u6210\u4ed6\u552f\u4e00\u53ef \u4ee5\u4eb2\u8fd1\u7684\u4eba\uff0c\u4eba\u4eec\u90fd\u6709\u5c06\u81ea\u5df1\u7684\u5fc3\u4e8b\uff0c\u5410\u9732\u7ed9\u81ea\u5df1\u4eb2\u4eba\u7684\u4e60\u60ef\u3002 \u3000\u3000\u4e8e\u662f\u8f9b\u6377\u555c\u6ce3\u7740\uff0c\u8bf4\u51fa\u81ea\u5df1\u7684\u906d\u9047\uff0c\u5728\u4ed6\u8bf4\u6765\uff0c\u4e0d\u8fc7\u662f\u4e00\u79cd\u60c5\u611f\u7684\u53d1\u6cc4\u800c\u5df2\uff0c\u7136\u800c \u4ed6\u4e07\u4e07\u4e0d\u4f1a\u6599\u5230\uff0c\u8fd9\u5374\u4f7f\u4ed6\u5f97\u5230\u4e86\u4ed6\u610f\u60f3\u4e0d\u5230\u7684\u5947\u7f18\u3002 \u3000\u3000\u539f\u6765\u4ed6\u6240\u53d9\u8bf4\u7684\u5bf9\u8c61\uff0c\u7adf\u662f\u4eca\u65e5\u6b66\u6797\u4e2d\u7b2c\u4e00\u5947\u4eba\uff0c\u4ee5\u201c\u795e\u529f\u4e03\u827a\u201d\u540d\u4f20\u56db\u6d77\u7684\u4e03\u5999 \u795e\u541b\u6885\u5c71\u6c11\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u88ab\u70b9\u82cd\u7b2c\u4e5d\u4ee3\u638c\u95e8\u4eba\uff0c\u70b9\u82cd\u53cc\u5251\u4e2d\u7684\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u4ee5\u70b9\u82cd\u7edd\u5b66\u201c"\u4e03\u7edd \u91cd\u624b\u201d"\u70b9\u201c\u80a9\u4e95\u201d\u201c\u6ca7\u6d77\u201d\u4e24\u5904\u5927\u7a74\uff0c\u5185\u8151\u4e5f\u88ab\u82e6\u5eb5\u4e0a\u4eba\uff0c\u8d64\u9633\u9053\u957f\uff0c\u4ee5\u53ca\u5251\u795e\u5389\u9e57 \u7684\u5185\u529b\u6240\u4f24\uff0c\u5728\u522b\u4eba\u8bf4\u6765\uff0c\u8fd9\u4e24\u6837\u53ea\u8981\u8eab\u53d7\u5176\u4e00\uff0c\u4e5f\u662f\u975e\u6b7b\u4e0d\u53ef\u7684\u3002 \u3000\u3000\u4f46\u662f\u4e03\u5999\u795e\u541b\uff0c\u5148\u5929\u5c31\u6709\u4e00\u79cd\u5f02\u4e8e\u5e38\u4eba\u7684\u624d\u667a\uff0c\u540e\u5929\u53c8\u5f97\u5230\u4e86\u975e\u51e1\u7684\u85b0\u9676\uff0c\u4ed6\u7684\u4e00 \u5207\uff0c\u90fd\u4e0d\u662f\u4efb\u4f55\u4e00\u4e2a\u6b66\u6797\u4e2d\u4eba\uff0c\u6240\u80fd\u671b\u5176\u9879\u80cc\u7684\u3002 \u3000\u3000\u4ed6\u4ee5\u591a\u5e74\u6765\u8d85\u4eba\u7684\u4fee\u4e3a\uff0c\u52aa\u529b\u5730\u8fd0\u8f6c\u7740\u4f53\u5185\u7684\u5148\u5929\u4e4b\u6c14\uff0c\u4f46\u662f\u80f8\u8179\u4e4b\u95f4\u5374\u59cb\u7ec8\u4e0d\u80fd \u8fd0\u884c\uff0c\u4ed6\u77e5\u9053\u4ed6\u6240\u53d7\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u5fc5\u662f\u5f97\u6709\u79d8\u4f20\uff0c\u82e5\u662f\u4ed6\u5185\u8151\u672b\u66fe\u53d7\u4f24\uff0c\u4ed6\u6216\u8bb8\u80fd\u4ee5\u81ea \u8eab\u529f\u529b\uff0c\u89e3\u5f00\u6b64\u7a74\uff0c\u4f46\u6b64\u523b\uff0c\u5374\u662f\u7edd\u4e0d\u53ef\u80fd\u505a\u5230\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u89c9\u56db\u80a2\u662f\u90a3\u4e48\u8f6f\u7ef5\u800c\u65e0\u529b\uff0c\u751a\u81f3\u60f3\u79fb\u52a8\u4e00\u4e0b\u624b\u6307\uff0c\u90fd\u505a\u4e0d\u5230\uff0c\u800c\u4e14\u8151\u80ba\u4e4b\u95f4\u7684 \u6de4\u8840\uff0c\u6162\u6162\u5730\u5c55\u5f00\uff0c\u5df2\u662f\u4ed6\u6240\u5269\u4e0b\u7684\u529f\u529b\uff0c\u6240\u4e0d\u80fd\u63a7\u5236\u7684\u4e86\uff0c\u4ed6\u53ea\u80fd\u56f0\u82e6\u7684\u6323\u624e\u7740\uff0c\u6162 \u6162\u5730\u7b49\u5019\u6b7b\u4ea1\uff0c\u6216\u8005\u662f\u5947\u8ff9\u7684\u6765\u4e34\u3002 \u3000\u3000\u4ed6\u662f\u5e73\u5367\u5728\u96ea\u5730\u4e0a\uff0c\u5730\u5e95\u7684\u9634\u5bd2\uff0c\u4e5f\u5728\u4fb5\u8680\u7740\u4ed6\u4f53\u5185\u7684\u529f\u529b\uff0c\u5f53\u4ed6\u6b63\u5df2\u7edd\u671b\u7684\u65f6\u5019\uff0c \u5ffd\u7136\u5df1\u542c\u89c1\u8c37\u53e3\u6709\u4e00\u79cd\u6781\u4e3a\u91cd\u6d4a\u800c\u6025\u901f\u7684\u8e44\u58f0\u4f20\u6765\uff0c\u8fd9\u65f6\u4ed6\u591a\u4e48\u5e0c\u671b\u90a3\u6765\u7684\u662f\u4e00\u4e2a\u80fd\u591f \u5e2e\u52a9\u4ed6\u7684\u4eba\u5440\u3002 \u3000\u3000\u90a3\u8e44\u58f0\u50cf\u4e00\u9635\u98ce\uff0c\u95ef\u8fdb\u8c37\u91cc\uff0c\u63a5\u7740\u4ed6\u770b\u89c1\u4e00\u6761\u72c2\u5954\u7740\u7684\u725b\uff0c\u4ece\u4ed6\u8eab\u8fb9\u5954\u4e86\u8fc7\u53bb\uff0c\u5728 \u8c37\u91cc\u6025\u5267\u5730\u5954\u8dd1\u7740\uff0c\u4ed6\u610f\u8bc6\u5230\u90a3\u4ec5\u4ec5\u662f\u4e00\u5339\u53d1\u72c2\u6027\u7684\u725b\u800c\u5df2\uff0c\u4e00\u5339\u53d1\u4e86\u72c2\u7684\u725b\uff0c\u5bf9\u4ed6\u53c8 \u80fd\u6709\u4ec0\u4e48\u5e2e\u52a9\u5462\u3002 \u3000\u3000\u90a3\u725b\u5728\u8c37\u91cc\u5954\u4e86\u4e00\u8f6c\uff0c\u7adf\u53c8\u76f4\u76f4\u5730\u671d\u4ed6\u5367\u8eab\u4e4b\u5904\u5954\u5230\uff0c\u4ed6\u65e0\u6cd5\u8eb2\u907f\uff0c\u53ea\u6709\u95ed\u76ee\u7b49\u7740 \u725b\u8e44\u81ea\u4ed6\u8eab\u4e0a\u8e29\u8fc7\uff0c\u5728\u4ed6\u95ed\u4e0a\u773c\u6674\u90a3\u4e00\u5239\u95f4\uff0c\u4ed6\u731b\u7136\u89c9\u5f97\u81ea\u5df1\u4e73\u4e0b\u7684\u201c\u4e73\u6cc9\u201d\uff0c\u8110\u8180\u7684 \u201c\u7384\u77f6\u201d\u4e24\u5904\u5168\u7a74\uff0c\u88ab\u4e00\u79cd\u5343\u94a7\u4e4b\u529b\uff0c\u6781\u5feb\u5730\u6253\u4e86\u4e24\u4e0b\uff0c\u4ed6\u77e5\u9053\u90a3\u662f\u725b\u8e44\uff0c\u4f46\u602a\u5c31\u602a\u5728\uff0c \u4ed6\u5168\u8eab\u987f\u89c9\u4e00\u7545\uff0c\u4f53\u5185\u7684\u771f\u6c14\uff0c\u867d\u7136\u5fae\u5f31\uff0c\u4f46\u5374\u80fd\u81ea\u7531\u8fd0\u8f6c\u4e86\uff0c\u4e00\u79cd\u201c\u751f\u201d\u7684\u5e0c\u671b\uff0c\u9661 \u7136\u53c8\u5728\u4ed6\u5fc3\u4e2d\u590d\u6d3b\u4e86\uff0c\u4ed6\u60f3\u53ea\u8981\u81ea\u5df1\u80fd\u81ea\u7531\u8fd0\u6c14\uff0c\u56db\u80a2\u5fc5\u4e5f\u53ef\u6d3b\u52a8\uff0c\u90a3\u4e48\u5373\u4fbf\u662f\u518d\u91cd\u7684 \u4f24\uff0c\u53c8\u4f55\u6101\u4e0d\u80fd\u6cbb\u7597\u5462\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5f00\u59cb\u79fb\u52a8\u81ea\u5df1\u7684\u624b\u81c2\uff0c\u679c\u7136\uff0c\u4ed6\u89c9\u5f97\u808c\u8089\u95f4\u5df1\u6709\u4e86\u529b\u91cf\uff0c\u867d\u7136\u8fd9\u529b\u91cf\u548c\u4ed6\u4ee5 \u524d\u7684\u6f5c\u529b\u76f8\u5dee\u5f97\u5f88\u8fdc\uff0c\u4f46\u5df1\u8db3\u4ee5\u4f7f\u4ed6\u72c2\u559c\u4e86\u3002 \u3000\u3000\u7136\u800c\uff0c\u6b64\u523b\u90a3\u72c2\u725b\u53c8\u72c2\u5954\u7740\u5230\u4ed6\u6240\u5367\u4e4b\u5904\u800c\u6765\uff0c\u8fd9\u6b21\uff0c\u4ed6\u4e0d\u518d\u60ca\u614c\u4e86\uff0c\u4ed6\u60f3\uff0c\u867d\u7136 \u81ea\u5df1\u7684\u529f\u529b\u635f\u5931\u4e86\u8fd9\u4e48\u591a\uff0c\u4f46\u5e94\u4ed8\u8fd9\u4e00\u6761\u8ff8\u725b\u603b\u8be5\u4e0d\u6210\u95ee\u9898\u5427\uff0c\u4f46\u662f\u4ed6\u4e00\u5ff5\uff0c\u7adf\u94f8\u4e0b\u4e86 \u5927\u9519\u3002 \u3000\u3000\u5f53\u90a3\u72c2\u725b\u518d\u4ece\u4ed6\u8eab\u4e0a\u8e0f\u8fc7\u7684\u65f6\u5019\uff0c\u4e03\u5999\u795e\u541b\u5c06\u5168\u8eab\u771f\u529b\u90fd\u805a\u96c6\u5728\u53cc\u81c2\u4e4b\u4e0a\uff0c\u5411\u4e0a\u4e00 \u63a8\uff0c\u90a3\u5e9e\u5927\u7684\u725b\u8eab\u7adf\u88ab\u8fd9\u4e00\u51fb\uff0c\u51fb\u5f97\u76f4\u98de\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u662f\u4e03\u5999\u795e\u541b\u5728\u8fd9\u4e00\u51fb\u4e4b\u540e\uff0c\u7a81\u7136\u6709\u4e86\u4e00\u79cd\u4ed6\u6570\u5341\u5e74\u6765\u4ece\u672a\u6709\u8fc7\u7684\u611f\u89c9\uff0c\u90a3\u5c31\u662f\u75b2 \u52b3\u3002 \u3000\u3000\u987b\u77e5\u4e03\u5999\u795e\u541b\u7684\u5185\u529f\uff0c\u5df1\u5230\u4e86\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\u7684\u5883\u754c\uff0c\u8fd9\u75b2\u52b3\u4e8c\u5b57\uff0c\u4ed6\u662f\u7edd\u4e0d\u4f1a\u611f\u89c9 \u5230\u7684\uff0c\u7136\u800c\u6b64\u523b\uff0c\u4ed6\u53ea\u89c9\u5f97\u6d51\u8eab\u9aa8\u8282\u9178\u75db\uff0c\u53e3\u4e2d\u4e5f\u5fae\u5fae\u5598\u7740\u6c14\uff0c\u50cf\u662f\u4e00\u4e2a\u6beb\u65e0\u6b66\u529f\u7684\u4eba\uff0c \u5728\u7ecf\u8fc7\u4e86\u957f\u671f\u7684\u52b3\u7d2f\u4e4b\u540e\u6240\u6709\u7684\u611f\u89c9\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4e03\u5999\u795e\u541b\u4e5f\u80fd\u610f\u4f1a\u5230\u8fd9\u662f\u4ef6\u4ec0\u4e48\u4e8b\u53d1\u751f\u4e86\uff0c\u90a3\u5c31\u662f\u4ed6\u7684\u529f\u529b\u5df1\u6563\uff0c\u5728\u7ecf\u8fc7\u5916 \u6765\u7684\u4fb5\u5bb3\uff0c\u672c\u8eab\u7684\u4f24\u75db\u4e4b\u540e\uff0c\u4ed6\u82e5\u80fd\u5c06\u5269\u4f59\u7684\u771f\u6c14\u5584\u52a0\u4fdd\u517b\uff0c\u4ed6\u867d\u4e0d\u80fd\u5f88\u5feb\u7684\u6062\u590d\u539f\u529f \u529b\uff0c\u4f46\u4e5f\u975e\u65e0\u671b\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u5c06\u4ec5\u4f59\u7684\u771f\u6c14\u4f5c\u4e86\u5168\u529b\u7684\u4e00\u51fb\uff0c\u70b9\u82cd\u7684\u4e03\u7edd\u624b\u6cd5\u672c\u5c31\u662f\u4f7f\u4eba\u6709\u6563\u5c3d\u529f\u529b\u540e \u6162\u6162\u6b7b\u53bb\u7684\uff0c\u4e03\u5999\u795e\u541b\u6b66\u529f\u867d\u66fe\u51a0\u76d6\u5929\u4e0b\uff0c\u4f46\u6b64\u523b\u53c8\u56de\u590d\u6210\u4e00\u4e2a\u51e1\u592b\u4e86\u3002 \u3000\u3000\u7531\u4e00\u4e2a\u8d85\u4eba\u800c\u56de\u590d\u5230\u51e1\u4eba\u7684\u90a3\u79cd\u611f\u89c9\uff0c\u662f\u4ee4\u4eba\u6700\u96be\u5fcd\u53d7\u7684\uff0c\u518d\u52a0\u4e00\u4e2a\u6b66\u529f\u9ad8\u6df1\u7684\u4eba \u6563\u529f\u65f6\u6240\u5fc5\u6709\u7684\u75db\u695a\uff0c\u4f7f\u5f97\u6885\u5c71\u6c11\u6709\u4e86\u4e00\u79cd\u9003\u907f\u7684\u5ff5\u5934\uff0c\u800c\u6700\u597d\u7684\u4e00\u79cd\u9003\u907f\u7684\u65b9\u6cd5\uff0c\u5c31 \u662f\u6b7b\u3002 \u3000\u3000\u7136\u800c\u4ed6\u201c\u6b7b\u201d\u7684\u5ff5\u5934\uff0c\u5374\u88ab\u53e6\u4e00\u4ef6\u4e8b\u6253\u65ad\u4e86\uff0c\u90a3\u5c31\u662f\u5728\u8fd9\u4e2a\u5e7d\u8c37\u91cc\uff0c\u4ed6\u5ffd\u7136\u542c\u5230\u53e6 \u4e00\u4e2a\u4eba\u7684\u5598\u606f\u4e4b\u58f0\uff0c\u6885\u5c71\u6c11\u5f00\u59cb\u751f\u51fa\u4e00\u79cd\u597d\u5947\u7684\u60ca\u5f02\u7684\u611f\u89c9\uff0c\u4e8e\u662f\u4ed6\u52aa\u529b\u5730\u9f13\u7740\u6700\u540e\u7684 \u7cbe\u529b\uff0c\u7ad9\u7acb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u53d1\u73b0\u4e86\u8f9b\u6377\uff0c\u5f53\u4ed6\u8d70\u5230\u8f9b\u6377\u9762\u524d\u65f6\uff0c\u6655\u8ff7\u7740\u7684\u8f9b\u6377\u4e5f\u6b63\u5728\u6b64\u65f6\u7741\u773c\u770b\u5230\u4e86 \u4ed6\u3002 \u3000\u3000\u7edd\u671b\u4e86\u7684\u4e03\u5999\u795e\u541b\u5728\u542c\u4e86\u8f9b\u6377\u6240\u53d9\u8ff0\u7684\u90a3\u4e00\u6bb5\u60e8\u7edd\u4eba\u5bf0\u7684\u906d\u9047\u4e4b\u540e\uff0c\u5fc3\u91cc\u88ab\u6124\u6012\u548c \u4e0d\u5e73\u6240\u66ff\u4ee3\u3002\u5c31\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u8f9b\u6377\u51b3\u5b9a\u4e86\u4ed6\u7ec8\u751f\u7684\u547d\u8fd0\uff0c\u4ed6\u5c06\u8981\u6210\u4e3a\u6b66\u6797\u4e2d\u7684\u715e\u661f\uff0c\u4ed6 \u7684\u58f0\u540d\u548c\u6b66\u6280\uff0c\u5c06\u8981\u88ab\u6240\u6709\u7684\u6b66\u6797\u4e2d\u4eba\u6240\u60e7\u6015\u3002 \u3000\u3000\u8fd9\u65f6\u96ea\u4e5f\u505c\u4e86\uff0c\u5e7d\u8c37\u91cc\u66f4\u663e\u5f97\u9759\u5bc2\uff0c\u6885\u5c71\u6c11\u7a81\u5730\u60f3\u53ca\uff1a\u201c\u5929\u4e0b\u600e\u4f1a\u6709\u8fd9\u4e48\u5947\u602a\u7684\u4e8b\uff0c \u8fd9\u72c2\u725b\u7adf\u4f1a\u5954\u5230\u8fd9\u7ec8\u5e74\u6e3a\u65e0\u4eba\u8e2a\u7684\u5730\u65b9\uff0c\u83ab\u975e\u662f\u6709\u4eba\u60f3\u85c9\u6b64\u82e6\u8089\u4e4b\u8ba1\uff0c\u9a97\u5f97\u6211\u6b66\u529f\u53bb\uff0c \u6211\u867d\u5185\u529b\u5df2\u6563\uff0c\u4f46\u80f8\u4e2d\u7684\u7cbe\u5965\u6b66\u5b66\uff0c\u53c8\u5c82\u662f\u90a3\u4e9b\u6b66\u6797\u4e2d\u4eba\u53ef\u4ee5\u6bd4\u62df\u7684\u3002\u201d \u3000\u3000\u4ed6\u6781\u4e3a\u56f0\u96be\u7684\u6323\u624e\u7740\u5750\u4e86\u8d77\u6765\uff0c\u671b\u7740\u8f9b\u6377\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u8c01\u5417\u3002\u201d \u3000\u3000\u8f9b\u6377\u832b\u7136\u5730\u6447\u4e86\u6447\u5934\uff0c\u4ed6\u5728\u5947\u602a\u7740\u6885\u5c71\u6c11\u7684\u95ee\u9898\uff0c\u81ea\u7136\uff0c\u4ed6\u600e\u4f1a\u8ba4\u5f97\u6885\u5c71\u6c11\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u7684\u90a3\u79cd\u832b\u7136\u7684\u8868\u60c5\uff0c\u5f88\u5feb\u5730\u4fbf\u88ab\u6885\u5c71\u6c11\u4e86\u89e3\u4e86\u5176\u4e2d\u7684\u7528\u610f\uff0c\u4e03\u5999\u795e\u541b\u806a\u9896\u7edd \u4eba\uff0c\u4ed6\u4ece\u8f9b\u6377\u7684\u8138\u8272\u4e0a\uff0c\u76f8\u4fe1\u4e86\u8f9b\u6377\u7684\u8bda\u5b9e\uff0c\u4e00\u79cd\u201c\u540e\u7ee7\u6709\u4eba\u201d\u559c\u60a6\uff0c\u4f7f\u5f97\u4ed6\u7b11\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u7740\u5411\u8f9b\u6377\u8bf4\uff1a\u201c\u73b0\u5728\u4f60\u4e5f\u662f\u65e0\u4eb2\u65e0\u9760\u4e86\uff0c\u4f60\u53ef\u613f\u8ddf\u968f\u7740\u6211\u3002\u201d \u3000\u3000\u8f9b\u6377\u770b\u7740\u8fd9\u5c3e\u5f31\u800c\u75b2\u4e4f\u7684\u4eba\uff0c\u80af\u5b9a\u5730\u8bf4\uff1a\u201c\u597d\uff0c\u6211\u4e00\u5b9a\u8ddf\u968f\u7740\u4f60\uff0c\u7167\u987e\u7740\u4f60\uff0c\u4f60\u522b \u770b\u6211\u73b0\u5728\u6d51\u8eab\u6ca1\u6709\u529b\u6c14\uff0c\u53ea\u8981\u6211\u6b47\u4e00\u4f1a\u513f\uff0c\u6211\u529b\u6c14\u5012\u5927\u5f97\u5f88\uff0c\u4ec0\u4e48\u4e8b\u90fd\u80fd\u505a\u7684\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u88ab\u4ed6\u8fd9\u79cd\u5929\u771f\u7684\u8bdd\u6240\u6df1\u6df1\u7684\u611f\u52a8\u4e86\uff0c\u4ed6\u53d1\u73b0\u8fd9\u5b69\u5b50\u7684\u5fc3\u5730\u7684\u7eaf\u826f\uff0c\u4e8e\u662f\u4ed6\u7b11 \u7740\u8fde\u8fde\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u6b63\u9700\u8981\u4f60\u7684\u7167\u987e\u5462\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u95ed\u4e0a\u773c\u6674\uff0c\u9759\u9759\u7684\u5750\u7740\uff0c\u4f46\u662f\uff0c\u9965\u997f\u3001\u5bd2\u51b0\u3001\u75b2\u5026\u3001\u75db\u695a\uff0c\u8fd9\u8bb8\u591a\u79cd\u4ed6\u672a \u7ecf\u5386\u8fc7\u7684\u611f\u89c9\uff0c\u6b64\u65f6\u90fd\u88ad\u51fb\u800c\u6765\uff0c\u4e8e\u662f\u4ed6\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5411\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u4f60\u80fd\u4e0d\u80fd\u7ad9\u8d77\u6765\uff0c \u6276\u7740\u6211\u8d70\u51fa\u8fd9\u5c71\u8c37\u53bb\u3002\u201d \u3000\u3000\u8f9b\u6377\u7a0d\u4e00\u8f6c\u52a8\uff0c\u56db\u80a2\u5c31\u751f\u51fa\u9ebb\u5eb3\u7684\u75db\u82e6\uff0c\u4f46\u662f\u4e00\u79cd\u597d\u80dc\u7684\u8d23\u4efb\u611f\uff0c\u4f7f\u4ed6\u89c9\u5f97\u5728\u8fd9\u79cd \u60c5\u51b5\u4e0b\uff0c\u4ed6\u5fc5\u987b\u6210\u4e3a\u8f83\u575a\u5f3a\u7684\u4e00\u4e2a\uff0c\u4e8e\u662f\u4ed6\u54ac\u7740\u7259\u7ad9\u4e86\u8d77\u6765\uff0c\u548c\u6885\u5c71\u6c11\u56f0\u82e6\u8e09\u8dc4\u8d70\u51fa\u8c37 \u53bb\u3002 \u3000\u3000\u4e94\u534e\u5c71\u672c\u662f\u6606\u660e\u57ce\u5916\u6709\u540d\u7684\u6e38\u8d4f\u53bb\u5904\uff0c\u867d\u7136\u90a3\u7edd\u8c37\u4e2d\u6e3a\u65e0\u4eba\u8ff9\uff0c\u4f46\u5c71\u4e0a\u6e38\u4eba\u672c\u591a\uff0c \u6885\u5c71\u6c11\u548c\u8f9b\u6377\u5e76\u6ca1\u6709\u6323\u624e\u8bb8\u4e45\uff0c\u4fbf\u9047\u7740\u5c71\u4e0a\u7684\u6e38\u4eba\uff0c\u770b\u89c1\u4ed6\u4e24\u4eba\u7684\u72fc\u72c8\u4e4b\u72b6\uff0c\u6781\u60ca\u5f02\u5730 \u8dd1\u8fc7\u6765\u95ee\u6709\u4ec0\u4e48\u4e8b\u53d1\u751f\uff0c\u6885\u5c71\u6c11\u6de1\u6de1\u5730\u6577\u884d\u4e86\u51e0\u53e5\uff0c\u627e\u7740\u4e86\u4e24\u9876\u9001\u6e38\u4eba\u4e0a\u5c71\u7684\u5c71\u8f7f\uff0c\u548c \u8f9b\u6377\u5750\u7740\u4e0b\u4e86\u5c71\uff0c\u5230\u4e86\u6606\u660e\u57ce\u3002 \u3000\u3000\u6606\u660e\u53f7\u79f0\u56db\u5b63\u5e38\u6625\u4e4b\u5904\uff0c\u6e29\u5ea6\u81ea\u548c\u6df1\u5c71\u4e0d\u540c\uff0c\u66f4\u662f\u56db\u5b63\u96be\u89c1\u96ea\u5316\uff0c\u8f9b\u6377\u89c9\u5f97\u5947\u602a\u7684 \u662f\u6885\u5c71\u6c11\u624b\u9762\u7684\u9614\u7ef0\uff0c\u4ed6\u4eec\u5750\u5728\u6700\u597d\u7684\u5ba2\u5bd3\u4e2d\uff0c\u5403\u7740\u6700\u597d\u7684\u996e\u98df\uff0c\u6885\u5c71\u6c11\u8fd8\u66ff\u8f9b\u6377\u4e70\u4e86 \u8bb8\u591a\u8863\u670d\uff0c\u800c\u4e14\u81ea\u5c0f\u5230\u5927\uff0c\u5e74\u5e74\u90fd\u6709\uff0c\u5c06\u8f9b\u6377\u81ea\u73b0\u5728\u5230\u6210\u4eba\uff0c\u6240\u9700\u7528\u7684\u8863\u7269\u90fd\u4e70\u5168\u4e86\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u6885\u5c71\u6c11\u96c7\u4e86\u8f86\u5927\u8f66\uff0c\u81ea\u6606\u660e\u51fa\u53d1\uff0c\u4e00\u8def\u4e0a\u8d70\u5f97\u5f88\u6162\uff0c\u6885\u5c71\u6c11\u4e5f\u4e0d\u7740\u6025\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u4e0d\u77e5\u7ecf\u8fc7\u4e9b\u4ec0\u4e48\u5730\u65b9\uff0c\u53ea\u89c9\u5f97\u8f66\u5b50\u8d70\u4e86\u5f88\u4e45\uff0c\u6e10\u6e10\uff0c\u4ed6\u7684\u8eab\u4f53\u5df2\u590d\u539f\u4e86\uff0c\u4f46 \u4ed6\u770b\u7740\u6885\u5c71\u6c11\uff0c\u5374\u4ecd\u8c61\u662f\u975e\u5e38\u5b71\u5f31\u3002 \u3000\u3000\u8d70\u4e86\u6708\u4f59\uff0c\u5df2\u7ecf\u662f\u4ef2\u6625\u4e86\uff0c\u8f9b\u6377\u53ea\u89c9\u8def\u4e0a\u6811\u6728\u6e10\u7eff\uff0c\u4e5f\u4e0d\u77e5\u7a76\u7adf\u5230\u4e86\u4f55\u5904\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5728\u8def\u9014\u4e0a\uff0c\u5df2\u6362\u8fc7\u4e86\u51e0\u6b21\u8f66\uff0c\u8fd9\u65e5\u6765\u5230\u4e00\u4e2a\u6751\u843d\uff0c\u90a3\u6751\u843d\u4e0d\u8fc7\u6bd4\u8f9b\u5bb6\u6751\u7a0d\u8bb8 \u5927\u4e86\u4e9b\uff0c\u6885\u5c71\u6c11\u53c8\u53eb\u8f66\u5b50\u505c\u4e86\uff0c\u548c\u8f9b\u6377\u6f2b\u6b65\u6751\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u5f97\u6885\u5c71\u6c11\u5fc3\u60c5\u4eff\u4f5b\u751a\u597d\uff0c\u968f\u610f\u8bf4\u7b11\u7740\uff0c\u4e5f\u4e0d\u518d\u5524\u8f66\u3002 \u3000\u3000\u7a7f\u8fc7\u6751\u843d\uff0c\u53c8\u8d70\u4e86\u83ab\u7ea6\u534a\u91cc\u8def\uff0c\u6885\u5c71\u6c11\u5df2\u663e\u51fa\u5f88\u75b2\u4e4f\u7684\u6837\u5b50\uff0c\u4f46\u795e\u60c5\u5374\u6781\u5174\u594b\u3002 \u3000\u3000\u8d70\u8fc7\u4e00\u4e2a\u5e76\u4e0d\u5341\u5206\u6d53\u5bc6\u7684\u6811\u6797\uff0c\u8f9b\u6377\u770b\u5230\u51e0\u95f4\u5f88\u7cbe\u81f4\u7684\u74e6\u5c4b\uff0c\u6885\u5c71\u6c11\u6307\u7740\u5bf9\u8f9b\u6377\u8bf4 \u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9\u5c31\u662f\u6211\u7684\u5bb6\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u6697\u81ea\u5947\u602a\u7740\uff0c\u6885\u53d4\u53d4\u7684\u5bb6\u600e\u4f1a\u7adf\u8fdc\u5728\u6b64\u5904\uff0c\u800c\u4ed6\u5374\u5947\u5f02\u5730\u5728\u4e94\u534e\u5c71\u7684\u5e7d\u8c37\u91cc\uff0c \u4f46\u662f\u8fd9\u4e9b\u95ee\u9898\u4ed6\u90fd\u6ca1\u6709\u4ed4\u7ec6\u5730\u53bb\u63a2\u8ba8\u3002 \u3000\u3000\u6885\u5c71\u6c11\u8d70\u5230\u95e8\u524d\uff0c\u8f7b\u8f7b\u5730\u62cd\u4e86\u51e0\u4e0b\u95e8\uff0c\u90a3\u6697\u7d2b\u8272\u7684\u5927\u95e8\u4fbf\u7acb\u523b\u5e94\u58f0\u800c\u5f00\uff0c\u5f00\u95e8\u7684\u662f \u7626\u524a\u7684\u4e2d\u5e74\u6c49\u5b50\uff0c\u89c1\u662f\u6885\u5c71\u6c11\uff0c\u4fbf\u606d\u656c\u5730\u5f2f\u4e0b\u8170\u53bb\uff0c\u6c89\u58f0\u8bf4\u9053\uff1a\u201c\u60a8\u56de\u6765\u4e86\u3002\u201d\u8138\u4e0a\u4e1d \u6beb\u6ca1\u6709\u4efb\u4f55\u8868\u60c5\u3002 \u3000\u3000\u6885\u5c71\u6c11\u7b11\u7740\u70b9\u4e86\u5934\uff0c\u62c9\u7740\u8f9b\u6377\u8d70\u8fdb\u5927\u95e8\uff0c\u8f9b\u6377\u53ea\u89c9\u5f97\u6b64\u623f\u7cbe\u81f4\u5df2\u6781\uff0c\u5c4b\u4e2d\u5e03\u7f6e\u5f97\u66f4 \u662f\u4e95\u7136\u6709\u6761\uff0c\u4f46\u662f\u501f\u5927\u7684\u51e0\u95f4\u5c4b\u5b50\uff0c\u90fd\u7a7a\u65f7\u5730\u6ca1\u6709\u4eba\u58f0\u3002 \u3000\u3000\u90a3\u7626\u524a\u7684\u4e2d\u5e74\u6c49\u5b50\u5c16\u9510\u5730\u770b\u4e86\u8f9b\u6377\u4e00\u773c\uff0c\u6885\u5c71\u6c11\u8f7b\u8f7b\u62cd\u7740\u8f9b\u633a\u7684\u5934\u8bf4\uff1a\u201c\u8fd9\u662f\u6211\u6536 \u7684\u5f92\u5f1f\uff0c\u4f60\u770b\u597d\u4e0d\u597d\u3002\u201d \u3000\u3000\u63a5\u7740\u4ed6\u53c8\u4e00\u7b11\u8bf4\u9053\uff1a\u201c\u5979\u4eec\u90fd\u597d\u5427\u3002\u201d \u3000\u3000\u90a3\u7626\u524a\u7684\u4e2d\u5e74\u6c49\u5b50\u5fae\u4e00\u8e0c\u8e87\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5df1\u5c06\u5979\u4eec\u90fd\u6253\u53d1\u4e86\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u7acb\u523b\u9762\u8272\u5927\u53d8\uff0c\u6025\u7740\u8ffd\u95ee\u9053\uff1a\u201c\u90fd\u6253\u53d1\u4e86\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u4f4e\u4e0b\u5934\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u8fd1\u65e5\u6c5f\u6e56\u4f20\u8a00\u60a8\u5df2\u5728\u4e91\u5357\u4e94\u534e\u5c71\u91cc\uff0c\u906d\u4e86\u5251\u795e\u5389\u9e57\u7684\u6bd2 \u624b\uff0c\u800c\u4e14\u6c5f\u5357\u4e10\u5e2e\u4e2d\uff0c\u66f4\u76db\u4f20\u6709\u4eba\u76ee\u7779\u60a8\u7684\u5c38\u8eab\uff0c\u6211\u8003\u8651\u518d\u4e09\uff0c\u6050\u6015\u7559\u7740\u5979\u4eec\u5c06\u6765\u53cd\u4f1a \u751f\u4e8b\uff0c\u4fbf\u4e00\u4e00\u5c06\u5979\u4eec\u6253\u53d1\u4e86\uff0c\u6b63\u51c6\u5907\u5230\u5d06\u5cd2\u5c71\u53bb\u2026\u2026\u201d \u3000\u3000\u6885\u5c71\u6c11\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u622a\u4f4f\u4ed6\u7684\u8bdd\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u8fd9\u6b21\u6211\u771f\u662f\u6b7b\u91cc\u9003\u751f\uff0c\u5c06\u4e07\u4e8b \u90fd\u770b\u5f97\u6de1\u4e86\uff0c\u53ea\u662f\u5979\u4eec\u5012\u5e95\u548c\u6211\u76f8\u805a\u4e00\u573a\uff0c\u4f60\u53ef\u66fe\u8ba9\u5979\u4eec\u5403\u4e86\u5927\u82e6\u5934\uff1b\u8fd8\u6709\u90a3\u7f2a\u4e5d\u5a18 \u5462\uff1f\u201d \u3000\u3000\u90a3\u7626\u524a\u7684\u4e2d\u5e74\u6c49\u5b50\u4f9d\u7136\u795e\u8272\u4e0d\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u60a8\u653e\u5fc3\uff0c\u6211\u7edd\u6ca1\u6709\u8ba9\u5979\u4eec\u5403\u534a\u70b9\u82e6\u5934\uff0c \u53ea\u662f\u90a3\u7f15\u4e5d\u5a18\uff0c\u4e00\u542c\u60a8\u8eab\u906d\u4e0d\u6d4b\uff0c\u4e58\u7740\u6df1\u591c\u5c31\u8d70\u4e86\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4e0b\u843d\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u70b9\u4e86\u70b9\u5934\uff0c\u9eef\u6de1\u5730\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u8fd9\u6837\u4e5f\u597d\u3002\u201d \u3000\u3000\u8f9b\u6377\u542c\u7740\u4ed6\u4eec\u8bb2\u8bdd\uff0c\u5374\u4e1d\u6beb\u4e0d\u77e5\u9053\u5176\u4e2d\u610f\u601d\uff0c\u5446\u5446\u5730\u770b\u7740\u6885\u5c71\u6c11\uff0c\u6885\u5c71\u6c11\u4f4e\u5934\u53d1\u89c9 \u4e86\uff0c\u4fbf\u62c9\u8d77\u4ed6\u7684\u624b\uff0c\u6307\u7740\u90a3\u7626\u524a\u6c49\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u7684\u597d\u5f1f\u5144\uff0c\u4f60\u4ee5\u540e\u8981\u53eb\u4ed6\u4faf\u4e8c\u53d4\uff0c \u53ea\u8981\u4ed6\u6b22\u559c\uff0c\u4f60\u4ee5\u540e\u4fdd\u9669\u6709\u597d\u5904\u3002\u201d \u3000\u3000\u8f9b\u6377\u62ac\u5934\u671b\u4e86\u4e00\u773c\uff0c\u4f4e\u4f4e\u5524\u4e86\u58f0\uff1a\u201c\u4faf\u4e8c\u53d4\u201d\u3002\u90a3\u4faf\u4e8c\u53d4\u4ec5\u51b7\u51b7\u770b\u4e86\u4ed6\u4e00\u773c\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u5f97\u8fd9\u4faf\u4e8c\u53d4\u8fdc\u4e0d\u53ca\u6885\u53d4\u53d4\u53ef\u4eb2\uff0c\u8d76\u7d27\u53c8\u4f4e\u4e0b\u5934\u53bb\uff0c\u6885\u5c71\u6c11\u5fae\u7b11\u7740\u629a\u7740\u4ed6\u7684 \u80a9\uff0c\u671d\u90a3\u4e2d\u5e74\u7684\u7626\u524a\u6c49\u5b50\u8bf4\u9053\uff1a\u201c\u4f60\u4ecd\u7136\u5728\u4e0a\u9762\u597d\u4e86\uff0c\u53eb\u8001\u4fde\u6309\u65f6\u9001\u996d\u4e0b\u53bb\uff0c\u4f60\u82e5\u6ca1\u6709 \u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\uff0c\u4e5f\u4e0d\u8981\u51fa\u53bb\uff0c\u8fd1\u51e0\u5e74\u6211\u6050\u6015\u4e0d\u4f1a\u518d\u4e0a\u6765\u4e86\u3002\u201d \u3000\u3000\u90a3\u7626\u524a\u6c49\u5b50\u70b9\u5934\u8bf4\u662f\uff0c\u5ffd\u5730\u53cc\u76ee\u4e00\u5f20\uff0c\u7d27\u7d27\u76ef\u7740\u6885\u5c71\u6c11\u770b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u770b\u60a8 \u8fd9\u6b21\u56de\u6765\uff0c\u597d\u50cf\u6709\u4e9b\u4e0d\u5bf9\uff0c\u83ab\u975e\u2026\u2026\u201d \u3000\u3000\u6885\u5c71\u6c11\u53c8\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6162\u6162\u518d\u8bf4\uff0c\u6162\u6162\u518d\u8bf4\uff0c\u65e5\u540e\u4f60\u603b\u4f1a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u4ed6\u8f6c\u5934\u62c9\u7740\u8f9b\u6377\uff0c\u8d70\u51fa\u5ba2\u5385\uff0c\u8f6c\u5230\u4e00\u95f4\u975e\u5e38\u96c5\u6d01\u7684\u4e66\u623f\uff0c\u7528\u624b\u6309\u4e86\u6309\u90a3\u9760\u7740 \u5899\u800c\u7acb\u7684\u4e66\u67b6\u65c1\u7684\u4e00\u5757\u82b1\u7eb9\u7816\uff0c\u4e66\u67b6\u4fbf\u7a81\u5730\u4e00\u5206\uff0c\u9732\u51fa\u4e00\u5904\u5730\u9053\uff0c\u77f3\u9636\u76f4\u901a\u7740\u5730\u5e95\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u770b\u5f97\u5446\u4e86\uff0c\u6885\u5c71\u6c11\u53c8\u62c9\u7740\u8f9b\u6377\u5f80\u77f3\u9636\u4e0b\u8d70\u53bb\uff0c\u56de\u624b\u53c8\u662f\u4e00\u6309\uff0c\u90a3\u4e66\u67b6\u53c8\u500f \u7136\u800c\u5408\uff0c\u4f46\u5730\u9053\u4e2d\u5e76\u672a\u56e0\u4e66\u67b6\u4e4b\u5408\u800c\u663e\u5f97\u9ed1\u6697\u3002 \u3000\u3000\u8f9b\u6377\u88ab\u8fd9\u4e00\u5207\u6240\u6df1\u6df1\u5730\u60ca\u5f02\u4e86\uff0c\u4f46\u662f\u4ed6\u7d20\u6765\u80c6\u5927\uff0c\u800c\u4e14\u4ed6\u77e5\u9053\u6885\u53d4\u53d4\u5bf9\u4ed6\u7edd\u65e0\u6076\u610f\uff0c \u662f\u4ee5\u4ed6\u6beb\u4e0d\u8fdf\u7591\u5730\u8ddf\u7740\u6885\u5c71\u6c11\u8d70\u4e0b\u77f3\u9636\u3002 \u3000\u3000\u90a3\u77e5\u8fd9\u77f3\u9636\u4e4b\u4e0b\uff0c\u7adf\u522b\u6709\u5929\u5730\uff0c\u771f\u5982\u5e7b\u5883\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u53ea\u89c9\u5f97\u5bcc\u4e3d\u7e41\u534e\uff0c\u4e0d\u53ef\u8a00\u55bb\uff0c \u6bd4\u4e0a\u9762\u7684\u90a3\u51e0\u95f4\u623f\u5b50\uff0c\u53c8\u4e0d\u77e5\u5f3a\u80dc\u591a\u5c11\u500d\u4e86\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5e26\u7740\u8f9b\u6377\u5728\u5730\u5e95\u8f6c\u4e86\u4e00\u5708\uff0c\u5730\u5e95\u7adf\u5206\u6709\u4e03\u95f4\u5c4b\u5b50\uff0c\u95f4\u95f4\u90fd\u662f\u7cbe\u7f8e\u7edd\u4f26\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u773c\u5149\u64a9\u4e71\uff0c\u4ed6\u5fc3\u4e2d\u6b63\u6697\u559c\u7740\u8fd9\u4f4f\u5904\u4e4b\u7f8e\uff0c\u90a3\u77e5\u6885\u5c71\u6c11\u53c8\u5e26\u4ed6\u8d70\u8fdb\u4e00\u95f4\u623f\u5b50\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u8d70\u8fdb\u8fd9\u5c4b\u5b50\uff0c\u5c31\u50cf\u6709\u4e00\u80a1\u5bd2\u51b7\u4e4b\u6c14\uff0c\u6251\u9762\u800c\u6765\uff0c\u6b64\u5c4b\u4e2d\u5e8a\u3001\u51e0\u5168\u662f\u77f3\u5236\uff0c\u56db \u58c1\u4e5f\u662f\u7528\u9752\u77f3\u6240\u94fa\uff0c\u767e\u58c1\u4e0a\u6302\u7740\u4e00\u67c4\u957f\u5251\uff0c\u5251\u65c1\u60ac\u7740\u4e00\u4e2a\u9526\u56ca\uff0c\u77f3\u51e0\u4e0a\u653e\u7740\u4e00\u4e9b\u4e66\u7c4d\uff0c \u9664\u6b64\u4e4b\u5916\uff0c\u5c4b\u4e2d\u5c31\u522b\u65e0\u4ed6\u7269\u3002 \u3000\u3000\u6885\u5c71\u6c11\u7b11\u7740\u5bf9\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u5929\u8d77\uff0c\u4f60\u5c31\u8981\u4f4f\u5728\u8fd9\u623f\u95f4\u91cc\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u542c\u4e86\uff0c\u5fc3\u4e2d\u4e00\u51b7\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5730\u5e95\u6709\u8fd9\u4e48\u591a\u623f\u95f4\uff0c\u4ed6\u90fd\u4e0d\u8981\u6211\u4f4f\uff0c\u5374\u504f\u504f\u8981 \u6211\u4f4f\u5728\u8fd9\u9b3c\u623f\u95f4\u91cc\u2026\u2026\u201d\u5fc3\u4e2d\u867d\u5728\u57cb\u6028\uff0c\u9762\u4e0a\u5374\u53c8\u4e0d\u597d\u610f\u601d\u8868\u9732\u51fa\u6765\uff0c\u52c9\u5f3a\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u6885\u5c71\u6c11\u4f3c\u4e4e\u6d1e\u6089\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5728\u602a\u6211\u8981\u4f60\u4f4f\u5728\u6b64\u5904\uff0c\u53ef\u662f\u4f60\u4e5f\u8981 \u77e5\u9053\uff0c\u82e5\u6709\u4eba\u60f3\u4f4f\u5728\u6211\u8fd9\u91cc\u7684\u4e03\u95f4\u5176\u4ed6\u623f\u95f4\uff0c\u5012\u662f\u8fd8\u5bb9\u6613\uff0c\u53ef\u662f\u8981\u60f3\u4f4f\u5728\u6b64\u5904\uff0c\u5374\u662f\u96be \u5982\u767b\u5929\u5462\u3002\u201d \u3000\u3000\u8f9b\u6377\u770b\u7740\u5899\u4e0a\u7684\u5251\uff0c\u53c8\u60f3\u8d77\u90a3\u4faf\u4e8c\u53d4\u9510\u5229\u7684\u76ee\u5149\uff0c\u548c\u4ed6\u4eec\u4fe9\u4eba\u7684\u5bf9\u8bdd\uff0c\u7a81\u5730\u798f\u81f3\u5fc3 \u7075\uff0c\u7acb\u523b\u8bf4\u9053\uff1a\u201c\u6211\u559c\u6b22\u4f4f\u5728\u8fd9\u91cc\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u7b11\u5bb9\u4e00\u655b\uff0c\u76ee\u5149\u7559\u604b\u5730\u5728\u8fd9\u77f3\u5ba4\u56db\u5468\u4e00\u671b\uff0c\u611f\u559f\u7740\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u6211\u5df2 \u548c\u8fd9\u77f3\u5ba4\u7edd\u7f18\u4e86\uff0c\u4f60\u867d\u5929\u8d44\u751a\u9ad8\uff0c\u4f46\u80fd\u5426\u5c3d\u4f20\u6211\u7684\u2018\u4e03\u827a\u2019\uff0c\u8fd8\u8981\u770b\u4f60\u662f\u5426\u80fd\u523b\u82e6\u7528 \u529f\u3002\u201d \u3000\u3000\u8f9b\u6377\u6000\u7591\u5730\u95ee\u9053\uff1a\u201c\u4e03\u827a\uff1f\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u7565\u5c55\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4e03\u827a\uff0c\u4f60\u82e5\u80fd\u5c3d\u5f97\u6211\u7684\u2018\u4e03\u827a\u2019\uff0c\u4f55\u6101\u5927\u4ec7\u4e0d \u80fd\u62a5\u5462\u3002\u201d\u4ed6\u53cc\u76ee\u4ef0\u671b\u7740\u77f3\u5c4b\u4e4b\u9876\uff0c\u53f9\u9053\uff1a\u201c\u4e0d\u4f46\u4f60\u7684\u5927\u4ec7\u5f85\u62a5\uff0c\u6211\u7684\u4ec7\u6068\u4e5f\u8981\u4f60\u53bb\u62a5 \u5462\u3002\u201d \u3000\u3000\u8f9b\u6377\u671b\u7740\u4ed6\uff0c\u6781\u529b\u5730\u601d\u7d22\u7740\u4ed6\u7684\u8bdd\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u8f9b\u6377\u8fd8\u4e0d\u77e5\u9053\uff0c\u7ad9\u5728\u4ed6\u9762\u524d\u7684\u770b \u6765\u90a3\u4e48\u5c3e\u5f31\u7684\u6885\u53d4\u53d4\uff0c\u5c31\u662f\u6b66\u6797\u4e2d\u7684\u7b2c\u4e00\u5947\u4eba\uff1a\u4e03\u5999\u795e\u541b\u3002 \u3000\u3000\u4f46\u662f\u81ea\u4ece\u4ed6\u968f\u7740\u6885\u53d4\u53d4\u56de\u5230\u5bb6\u4ee5\u540e\uff0c\u8fd9\u8bb8\u591a\u5947\u602a\u7684\u4e8b\uff0c\u5df1\u4f7f\u4ed6\u77e5\u9053\u6885\u53d4\u53d4\u4e00\u5b9a\u4e0d\u662f \u4e2a\u5e73\u5e38\u7684\u4eba\u3002\u4ece\u6b64\uff0c\u4ed6\u5c31\u5728\u8fd9\u77f3\u5ba4\u4e2d\u4f4f\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u8fd9\u77f3\u5ba4\u662f\u5728\u5730\u5e95\uff0c\u518d\u52a0\u4e0a\u7528\u5177\u4ff1\u662f\u77f3\u5236\uff0c\u56e0\u6b64\u7ec8\u65e5\u9634\u5bd2\uff0c\u5c24\u5176\u665a\u4e0a\u7761\u7720\u4e4b\u591c\uff0c\u8f9b\u6377 \u89c9\u5f97\u8fd9\u79cd\u9634\u5bd2\u4e4b\u6c14\u7b80\u76f4\u5f88\u96be\u5fcd\u53d7\u3002 \u3000\u3000\u65e5\u590d\u4e00\u65e5\uff0c\u8f9b\u6377\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6e10\u6e10\uff0c\u4ed6\u5df1\u80fd\u9002\u5e94\u8fd9\u9634\u5bd2\u4e4b\u6c14\uff0c\u9664\u4e86\u6bcf\u65e5\u6709\u4eba\u9001 \u6765\u5403\u98df\u4e4b\u5916\uff0c\u4ed6\u8fde\u6885\u53d4\u53d4\u90fd\u89c1\u4e0d\u5230\u3002 \u3000\u3000\u65e0\u804a\u7684\u65f6\u5019\uff0c\u4ed6\u5f00\u59cb\u7ffb\u9605\u77f3\u51e0\u4e0a\u7684\u4e66\u7c4d\uff0c\u8fd9\u4e9b\u4e66\u90fd\u6d53\u539a\u5730\u5438\u5f15\u7740\u4ed6\u7684\u5174\u8da3\uff0c\u867d\u7136\u5176 \u4e2d\u6709\u8bb8\u591a\u5730\u65b9\u662f\u4ed6\u4e0d\u80fd\u4e86\u89e3\u7684\uff0c\u4f46\u662f\u4ed6\u4ecd\u4ed4\u7ec6\u5730\u770b\u4e0b\u53bb\u3002 \u3000\u3000\u4e66\u5f88\u5feb\u5730\u88ab\u770b\u5b8c\u4e86\uff0c\u53e6\u4e00\u6279\u65b0\u7684\u4e66\u88ab\u9001\u6765\uff0c\u6709\u65f6\u6885\u53d4\u53d4\u4e5f\u6765\u6559\u4ed6\u4e00\u4e9b\u4ed6\u4e0d\u61c2\u7684\u5730\u65b9\uff0c \u65e5\u5b50\u8fc7\u5f97\u4e0d\u77e5\u4e0d\u89c9\uff0c\u8f9b\u6377\u4e5f\u4e0d\u77e5\u770b\u4e86\u591a\u5c11\u4e66\u3002 \u3000\u3000\u4ed6\u662f\u5929\u8d44\u7edd\u9876\u4e4b\u4eba\uff0c\u518d\u88ab\u8fd9\u8bb8\u591a\u4e66\u6240\u9676\u51b6\uff0c\u4ed6\u5df2\u5b8c\u5168\u5730\u6210\u4e3a\u4e00\u4e2a\u667a\u8005\u3002 \u3000\u3000\u4f46\u662f\u6709\u4e00\u5929\uff0c\u5f53\u4ed6\u5c06\u4e00\u6279\u4e66\u770b\u5b8c\u7684\u65f6\u5019\uff0c\u5c31\u4e0d\u518d\u6709\u4e66\u9001\u6765\uff0c\u9664\u4e86\u4e00\u672c\u5f88\u8584\u5f88\u8584\u7684\u6284 \u672c\uff0c\u8f9b\u6377\u770b\u90a3\u4e66\u9761\u4e0a\u5199\u7740\u201c\u6697\u5f71\u6d6e\u9999\u201d\u51e0\u4e2a\u7bc6\u5b57\uff0c\u91cc\u9762\u5374\u662f\u4e00\u4e9b\u4fee\u4e3a\uff0c\u7ec3\u6c14\u7684\u57fa\u7840\u529f\u592b\uff0c \u65bc\u662f\u4ed6\u5f00\u59cb\u5b66\u5230\u4e86\u4e03\u5999\u795e\u541b\u591a\u5e74\u82e6\u7814\u800c\u6210\u7684\u65e0\u4e0a\u5185\u529f\u5fc3\u6cd5\u201c\u6697\u5f71\u6d6e\u9999\u201d\u3002 \u3000\u3000\u4ed6\u81ea\u5df1\u5e76\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u4fee\u4e3a\u8fdb\u5883\uff0c\u4f46\u662f\u6885\u5c71\u6c11\u5374\u77e5\u9053\uff0c\u5929\u8d44\u7edd\u9876\u7684\u8f9b\u6377\uff0c\u5728\u8fd9\u4e13\u4e3a \u7ec3\u529f\u800c\u9020\u7684\u77f3\u5ba4\u4e2d\uff0c\u4e13\u5fc3\u5730\u7ec3\u7740\uff0c\u5e76\u6ca1\u6709\u591a\u4e45\uff0c\u4ed6\u53ea\u89c9\u5f97\u4f53\u5185\u7684\u771f\u6c14\uff0c\u4eff\u4f5b\u5df1\u53d8\u6210\u6709\u5f62 \u4e4b\u7269\uff0c\u53ef\u4ee5\u968f\u610f\u6307\u6325\uff0c\u800c\u4e14\u8eab\u4f53\u66f4\u4e0d\u94dc\u6bd4\u4ee5\u524d\u7075\u4fbf\u4e86\u591a\u5c11\uff0c\u4ed6\u5e38\u5e38\u89c9\u5f97\u53ea\u8981\u81ea\u5df1\u4e00\u63d0\u6c14\uff0c \u4fbf\u6709\u4e00\u79cd\u817e\u7a7a\u800c\u4e0a\u7684\u611f\u89c9\u3002 \u3000\u3000\u7b49\u5230\u201c\u6697\u5f71\u6d6e\u9999\u201d\u90a3\u672c\u4e66\u6362\u4e3a\u201c\u624e\u679d\u5251\u7b3c\u201d\uff0c\u800c\u767e\u5ba4\u4e2d\u7684\u5149\u7ebf\u4e5f\u4e00\u5929\u6bd4\u4e00\u5929\u6697\u7684\u65f6 \u5019\uff0c\u5df2\u662f\u8f9b\u6377\u5230\u77f3\u5ba4\u4e2d\u7684\u7b2c\u4e94\u5e74\u4e86\u3002 \u3000\u3000\u4e94\u5e74\u4e2d\uff0c\u8f9b\u6377\u5df1\u957f\u6210\u4e3a\u5341\u4e03\u5c81\u7684\u5c11\u5e74\u4e86\uff0c\u4ed6\u7684\u5fc3\u60c5\uff0c\u5df2\u7531\u70e6\u8e81\u4e0d\u5b89\uff0c\u800c\u53d8\u4e3a\u65e0\u6bd4\u7684 \u5b81\u9759\uff0c\u4ed6\u5df2\u7531\u4e00\u4e2a\u5e38\u4eba\uff0c\u800c\u53d8\u4e3a\u975e\u5e38\u4eba\u4e86\u3002 \u3000\u3000\u800c\u6885\u5c71\u6c11\u8fd9\u51e0\u5e74\u6765\uff0c\u5374\u53d8\u5f97\u90a3\u4e48\u82cd\u8001\uff0c\u751a\u81f3\u8fde\u9b13\u53d1\u90fd\u6591\u767d\uff0c\u4f46\u4ed6\u7684\u5fc3\u60c5\uff0c\u4ecd\u662f\u6109\u5feb \u7684\uff0c\u4ed6\u773c\u770b\u7740\u8f9b\u6377\u7684\u957f\u6210\uff0c\u4eff\u4f5b\u662f\u81ea\u5df1\u65b0\u7684\u751f\u547d\uff0c\u4ed6\u5c31\u89c9\u5f97\u4e00\u5207\u90fd\u5df2\u5f97\u5230\u4e86\u8865\u507f\u3002 \u3000\u3000\u7b2c\u516d\u5e74\uff0c\u7b2c\u4e03\u5e74\u2026\u2026\u65e5\u5b50\u98de\u5feb\u5730\u8fc7\u53bb\uff0c\u957f\u5904\u5728\u767e\u5ba4\u4e2d\u7684\u8f9b\u6377\uff0c\u51e0\u4e4e\u5fd8\u8bb0\u4e86\u5916\u9762\u7684\u4e16 \u754c\uff0c\u73b0\u5728\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u77e5\u9053\u4ed6\u81ea\u5df1\u7684\u6b66\u529f\u4e86\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u5728\u5404\u79cd\u59ff\u5f0f\u4e0b\uff0c\u8eab\u8eaf\u968f\u610f\u5347\u817e\uff0c\u5728\u5e73\u6ed1\u7684\u767e\u58c1\u4e0a\uff0c\u4ed6\u53ef\u4ee5\u968f\u610f\u9a7b\u8db3\u5728\u4efb\u4f55\u4e00 \u5904\uff0c\u5728\u5df2\u53d8\u5f97\u5b8c\u5168\u6f06\u9ed1\u7684\u623f\u95f4\u91cc\uff0c\u4ed6\u53ef\u4ee5\u63cf\u7ed8\u51fa\u5382\u5e45\u6781\u7ec6\u817b\u7684\u56fe\u753b\uff0c\u4ed6\u552f\u4e00\u4e0d\u77e5\u9053\u7684\u662f\uff0c \u4ed6\u7684\u201c\u5251\u201d\u201c\u638c\u201d\u7a76\u7adf\u5df1\u6709\u4e86\u4f55\u79cd\u5a01\u529b\uff0c\u56e0\u4e3a\u5728\u8fd9\u77f3\u5ba4\u4e2d\uff0c\u4ed6\u65e0\u6cd5\u8003\u8bc1\u81ea\u5df1\u201c\u5251\u201d\u201c\u638c\u201d \u7684\u529f\u529b\u3002 \u3000\u3000\u5341\u5e74\u4e86\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u65e0\u6cd5\u60f3\u50cf\u4ed6\u4f55\u4ee5\u80fd\u5728\u8fd9\u77f3\u5ba4\u4e2d\u6e21\u8fc7\u8fd9\u4e48\u60a0\u957f\u7684\u5c81\u6708\uff0c\u4ed6\u60f3\uff0c\u8fd9 \u4e5f\u8bb8\u662f\u4e00\u79cd\u63a2\u5bfb\u77e5\u8bc6\u7684\u6b32\u671b\u548c\u5174\u8da3\uff0c\u4f7f\u5f97\u4ed6\u80fd\u8fd9\u4e48\u505a\u5427\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u4ed6\u6e34\u671b\u81ea\u5df1\u80fd\u6210 \u4e3a\u4e00\u4e2a\u975e\u51e1\u7684\u4eba\u3002 \u3000\u3000\u56e0\u4e3a\uff0c\u6709\u8bb8\u8bb8\u591a\u591a\u4ed6\u5e94\u505a\u7684\u4e8b\uff0c\u4e0d\u662f\u51e1\u4eba\u80fd\u505a\u5f97\u5230\u7684\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u6885\u5c71\u6c11\u8ba4\u4e3a\u8f9b\u6377\u5b66\u4f1a\u4e86\u4e00\u5207\u4ed6\u80fd\u6559\u7684\uff0c\u751a\u81f3\u6709\u4e9b\u5730\u65b9\uff0c\u8fde\u5f53\u5e74\u4ed6\u81ea\u5df1\u90fd\u6ca1\u6709 \u8fbe\u5230\u7684\uff0c\u800c\u8f9b\u6377\u5c45\u7136\u8fbe\u5230\u4e86\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u5e26\u7740\u8f9b\u6377\uff0c\u8d70\u51fa\u4e86\u90a3\u95f4\u8f9b\u6377\u66fe\u8eba\u5728\u90a3\u91cc\u5341\u5e74\u7684\u77f3\u5ba4\u3002 \u3000\u3000\u5f53\u8f9b\u6377\u8d70\u51fa\u5730\u5e95\uff0c\u7b2c\u4e00\u773c\u770b\u89c1\u5929\u5149\u65f6\uff0c\u4ed6\u7684\u5fc3\u60c5\u662f\u65e0\u6cd5\u63cf\u8ff0\u7684\uff0c\u90a3\u662f\u4e00\u79cd\u6e17\u5408\u4e86\u559c \u60a6\u3001\u964c\u751f\uff0c\u4ee5\u53ca\u4e00\u4e9b\u60ca\u5947\u7684\u60c5\u611f\u3002 \u3000\u3000\u6885\u5c71\u6c11\u6307\u7740\u4e00\u5f20\u653e\u5728\u4e66\u623f\u91cc\u7684\u56f4\u6905\u8ba9\u4ed6\u5750\u4e0b\uff0c\u7136\u540e\u7b11\u7740\u9053\uff1a\u201c\u8fd9\u4e9b\u5e74\u6765\uff0c\u4f60\u89c9\u5f97\u4f60 \u5728\u77f3\u5ba4\u4e2d\u6240\u53d7\u7684\u82e6\u6ca1\u6709\u767d\u53d7\u5427\u3002\u201d \u3000\u3000\u8f9b\u6377\u611f\u6fc0\u5730\u5782\u4e0b\u5934\u53bb\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u5168\u662f\u6885\u53d4\u53d4\u7684\u683d\u57f9\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u7b11\u7740\u70b9\u5934\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4f60\u77e5\u9053\u5c31\u597d\u3002\u201d\u4ed6\u4fa7\u8eab\u7167\u4e86\u7167\u653e\u5728\u684c\u4e0a\u7684\u94dc\u955c\uff0c\u8bf4 \u9053\uff1a\u201c\u4f60\u770b\u6211\u6bd4\u5728\u5c71\u8c37\u4e2d\u9047\u89c1\u4f60\u65f6\u8001\u5f97\u591a\u4e86\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u671b\u7740\u4ed6\u5df2\u6591\u767d\u4e86\u7684\u5934\u53d1\uff0c\u8d77\u4e86\u76b1\u7eb9\u7684\u9762\u5b54\uff0c\u90a3\u786e\u662f\u5df1\u548c\u5f53\u5e74\u5c71\u8c37\u4e2d\u7684\u4e66\u751f\uff0c\u5927 \u4e0d\u76f8\u540c\u4e86\uff0c\u65bc\u662f\u4ed6\u5c0f\u5fc3\u5730\u8bf4\uff1a\u201c\u6885\u53d4\u53d4\u662f\u8001\u5f97\u591a\u4e86\uff0c\u4f46\u662f\u6211\u770b\u6885\u53d4\u53d4\u7684\u8eab\u4f53\u5374\u6bd4\u90a3\u65f6\u597d \u591a\u4e86\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u629a\u6478\u7740\u8eab\u4e0a\u5df2\u662f\u677e\u6563\u4e86\u7684\u808c\u8089\uff0c\u6115\u4e86\u4e00\u4f1a\uff0c\u7a81\u7136\u95ee\u9053\uff1a\u201c\u4f60\u77e5\u9053\u6211\u662f\u8c01\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u521a\u60f3\u5f20\u53e3\u56de\u7b54\uff0c\u4e00\u65f6\u5374\u5b9a\u4f4f\u4e86\uff0c\u8fd9\u95ee\u9898\u8f9b\u6377\u5728\u8c37\u4e2d\u521d\u9047\u5230\u4ed6\u65f6\uff0c\u4ed6\u5c31\u95ee\u8fc7\u8f9b\u6377\uff0c \u8f9b\u6377\u90a3\u65f6\u786e\u662f\u4e0d\u77e5\uff0c\u4f46\u6b64\u65f6\u8f9b\u6377\u548c\u4ed6\u5df2\u76f8\u5904\u5341\u5e74\uff0c\u8f9b\u6377\u9664\u4e86\u77e5\u9053\u4ed6\u662f\u6885\u53d4\u53d4\u4e4b\u5916\uff0c\u5c31\u4e00 \u65e0\u6240\u77e5\u4e86\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5e76\u672a\u6ce8\u610f\u5230\u4ed6\u7684\u7a98\u6001\uff0c\u611f\u559f\u7740\u9053\uff1a\u201c\u542c\u4f60\u6240\u8bf4\uff0c\u4f60\u7684\u6bcd\u4eb2\u4e5f\u662f\u5173\u4e2d\u4e5d\u8c6a\u4e2d\u7684 \u4eba\u7269\uff0c\u4f60\u53ef\u66fe\u542c\u8bf4\u8fc7\uff1a\u201c\u2018\u5173\u4e2d\u9738\u4e5d\u8c6a\uff0c\u6cb3\u6d1b\u552f\u4e00\u5251\uff0c\u6d77\u5185\u5c0a\u4e03\u5999\uff0c\u4e16\u5916\u6709\u4e09\u4ed9\uff0c\u2019\u8fd9 \u53e5\u8bdd\u3002\u201d\u8f9b\u6377\u6c89\u601d\u4e86\u4e00\u4f1a\uff0c\u7136\u540e\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u6885\u5c71\u6c11\u9053\uff1a\u201c\u8fd9\u4e5f\u96be\u602a\u4f60\uff0c\u4f60\u90a3\u65f6\u8fd8\u5c0f\uff0c\u5c31\u662f\u542c\u5230\u8fc7\uff0c\u4e5f\u65e9\u5df2\u5fd8\u8bb0\u4e86\uff0c\u4e0d\u8fc7\u6211\u73b0\u5728 \u53ef\u4ee5\u544a\u8bc9\u4f60\uff0c\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\u5c31\u662f\u8bf4\u5173\u4e2d\u5730\u65b9\u662f\u5173\u4e2d\u4e5d\u8c6a\u79f0\u9738\u7684\uff0c\u6cb3\u6d1b\u4e00\u5e26\uff0c\u5374\u552f\u6709\u4e00\u4e2a \u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\u53ef\u8bf4\u5f97\u4e0a\u662f\u7b2c\u4e00\u4eba\u7269\uff0c\u4f46\u662f\u6d77\u5185\u6b66\u6797\u4e2d\u4eba\uff0c\u90fd\u8981\u5c0a\u91cd\u7684\uff0c\u5374\u662f\u4e03\u5999\u795e\u541b\uff0c \u8fd9\u4e9b\u90fd\u662f\u5728\u6b66\u6797\u4e2d\u4eab\u6709\u76db\u540d\u7684\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u66f4\u6709\u4e09\u4e2a\u636e\u8bf4\u5df2\u6210\u4e0d\u574f\u4e4b\u8eab\u7684\u4eba\u7269\uff0c\u6b66\u6797\u4e2d \u4eba\u53ea\u6709\u542c\u8bf4\u800c\u5df2\uff0c\u8c01\u4e5f\u6ca1\u6709\u89c1\u8fc7\uff0c\u5927\u5bb6\u90fd\u4ee5\u2018\u4e16\u5916\u4e09\u4ed9\u2019\u6765\u79f0\u547c\u4ed6\u4eec\u4e09\u4eba\u3002\u201d \u3000\u3000\u4ed6\u76ee\u5149\u4e2d\u6d41\u52a8\u7740\u8f9b\u6377\u5c11\u89c1\u7684\u5149\u8292\uff0c\u50cf\u662f\u5728\u56de\u5fc6\u7740\u4ec0\u4e48\uff0c\u8f9b\u6377\u4e0d\u6562\u53bb\u6253\u6270\u4ed6\uff0c\u53ea\u662f\u9759 \u9759\u5730\u542c\u4ed6\u7ee7\u7eed\u8bf4\u7740\uff1a\u201c\u73b0\u5728\u5173\u4e2d\u4e5d\u8c6a\u65e9\u5df2\u6563\u4f19\uff0c\u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\uff0c\u4e5f\u4f24\u5728\u90a3\u4e9b\u4ee5\u6b66\u6797\u6b63 \u5b97\u81ea\u547d\u7684\u5c0f\u4eba\u624b\u4e2d\uff0c\u65e9\u5df2\u53bb\u4e16\u4e86\uff0c\u800c\u6614\u65e5\u79f0\u5c0a\u6d77\u5185\u7684\u4e03\u5999\u795e\u541b\u5462\uff01\u558f\uff0c\u5c31\u662f\u73b0\u5728\u5750\u5728\u4f60 \u8eab\u524d\u7684\u4eba\uff0c\u5c31\u662f\u6211\u3002\u201d \u3000\u3000\u8f9b\u6377\u60ca\u5f02\u5730\u7741\u5927\u4e86\u773c\u6674\uff0c\u4ed6\u4ece\u672a\u60f3\u5230\u8fc7\u5230\u7684\u6587\u5f31\u7684\u6885\u53d4\u53d4\u7adf\u662f\u5982\u6b64\u4eba\u7269\u3002 \u3000\u3000\u6885\u5c71\u6c11\u7528\u624b\u8f7b\u8f7b\u62ed\u7740\u9886\u4e0b\u7684\u5fae\u987b\uff0c\u53f9\u9053\uff1a\u201c\u770b\u6765\u82b8\u82b8\u6b66\u6797\u4e2d\uff0c\u80fd\u5c79\u7acb\u4e0d\u5012\u7684\uff0c\u53ea\u6709 \u2018\u4e16\u5916\u4e09\u4ed9\u2019\u4e86\uff0c\u4f46\u6211\u5374\u8ba4\u4e3a\uff0c\u7eb5\u7136\u5982\u6b64\uff0c\u4f46\u7a7a\u5c06\u4e00\u8eab\u7edd\u6280\uff0c\u57cb\u6ca1\u5728\u5c71\u6c34\u4e4b\u95f4\uff0c\u5c82\u4e0d\u662f \u53ef\u501f\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u4ed4\u7ec6\u5730\u542c\u7740\uff0c\u5fc3\u4e2d\u6d8c\u8d77\u8bb8\u591a\u601d\u6f6e\uff0c\u5341\u5e74\u6765\u7684\u90c1\u79ef\uff0c\u6b64\u523b\u7a81\u7136\u4e00\u6d8c\u800c\u51fa\uff0c\u800c\u4e14\u96c4 \u5fd7\u987f\u8d77\uff0c\u9887\u60f3\u4ee5\u4e00\u8eab\u6240\u5b66\uff0c\u7acb\u523b\u4fbf\u5728\u6b66\u6797\u4e2d\u4e00\u4e89\u957f\u77ed\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7684\u8fd9\u4e9b\u601d\u6f6e\uff0c\u867d\u7136\u5f88\u96be\u900f\u8fc7\u4ed6\u90a3\u591a\u5e74\u6765\u5728\u5730\u5e95\u77f3\u5ba4\u4e2d\u5df2\u51dd\u7ed3\u6210\u51b0\u7684\u82cd\u767d\u9762\u5b54\uff0c \u4f46\u6885\u5c71\u6c11\u4ece\u4ed6\u95ea\u70c1\u7684\u773c\u795e\u4e2d\uff0c\u4ecd\u53ef\u770b\u51fa\u4ed6\u7684\u5fc3\u4e8b\u3002 \u3000\u3000\u4e8e\u662f\u6885\u5c71\u6c11\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\uff0c\u6211\u5e26\u4f60\u6765\u5230\u6b64\u5904\uff0c\u9664\u4e86\u662f\u540c\u60c5\u4f60\u7684\u906d\u9047\uff0c\u52a9\u4f60\u590d\u4ec7 \u4e4b\u5916\uff0c\u6700\u4e3b\u8981\u7684\u8fd8\u662f\u6211\u770b\u51fa\u4f60\u7684\u6839\u9aa8\u592a\u597d\uff0c\u7a0d\u4e00\u7422\u78e8\uff0c\u4fbf\u6210\u5927\u5668\uff0c\u679c\u7136\u4f60\u5e76\u6ca1\u6709\u4ee4\u6211\u5931 \u671b\uff0c\u4ee5\u4f60\u73b0\u5728\u6240\u5177\u7684\u6b66\u529f\uff0c\u8db3\u53ef\u4ee5\u79f0\u9738\u6c5f\u6e56\u4e86\uff0c\u4ece\u4eca\u5929\u8d77\uff0c\u4f60\u5c31\u662f\u7b2c\u4e8c\u4e2a\u4e03\u5999\u795e\u541b\uff0c\u6211 \u4ee5\u524d\u6240\u672a\u5b8c\u6210\u7684\u4e8b\uff0c\u4f60\u90fd\u8981\u4e00\u4e00\u53bb\u66ff\u6211\u505a\u597d\u3002\u201d \u3000\u3000\u4ed6\u8138\u4e0a\u95ea\u8fc7\u559c\u60a6\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u4e03\u5999\u795e\u541b\uff0c\u53c8\u8981\u91cd\u73b0\u6c5f\u6e56\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u7a81\u7136\u63a5\u53d7\u5230\u8fd9\u79cd\u5947\u5f02\u800c\u5174\u594b\u7684\u4efb\u52a1\uff0c\u773c\u5149\u56e0\u5174\u594b\u800c\u66f4\u95ea\u70c1\u4e86\uff0c\u4ed6\u867d\u6ca1\u6709\u592a\u5927\u7684 \u81ea\u4fe1\uff0c\u4f46\u662f\u4ed6\u613f\u610f\u53bb\u95ef\u4e00\u95ef\u3002 \u3000\u3000\u7a81\u7136\u9662\u4e2d\u6709\u4e00\u4e2a\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\uff0c\u90a3\u662f\u8eab\u5177\u8f7b\u529f\u7684\u4eba\u7531\u9ad8\u5904\u843d\u4e0b\u6240\u53d1\u751f\u7684\u58f0\u97f3\uff0c\u800c\u4e14 \u662f\u6781\u4e3a\u8f7b\u5fae\u7684\uff0c\u4f46\u662f\u90a3\u7792\u4e0d\u4e86\u5728\u77f3\u5ba4\u4e2d\u5341\u5e74\u82e6\u7ec3\u7684\u8f9b\u6377\uff0c\u4ed6\u4e00\u542c\u58f0\u97f3\u6709\u5f02\uff0c\u731b\u4e00\u63d0\u6c14\uff0c \u8eab\u8eaf\u50cf\u4e00\u6761\u98de\u7740\u7684\u9c7c\u4e00\u6837\uff0c\u4ece\u5fae\u5f00\u7740\u7684\u7a97\u6237\u4e2d\u6ed1\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4f46\u9662\u4e2d\u4e00\u7247\u7a7a\u8361\uff0c\u6ca1\u6709\u4efb\u4f55\u4eba\u5f71\u3002 \u3000\u3000\u4ed6\u6781\u5feb\u5730\u5728\u56db\u5468\u7565\u4e00\u76d8\u65cb\uff0c\u627e\u4e0d\u5230\u4efb\u4f55\u53ef\u7591\u7684\u73b0\u8c61\uff0c\u5931\u671b\u5730\u53c8\u7a9c\u56de\u623f\u4e2d\u3002 \u3000\u3000\u4ed6\u4e00\u8fb9\u623f\uff0c\u5c31\u770b\u89c1\u4ed6\u539f\u5148\u6240\u5750\u7684\u6905\u5b50\u4e0a\uff0c\u5750\u4e86\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u4ed6\u4ece\u7a97\u53e3\u7a9c\u8fdb\uff0c\u90a3\u4eba\u8fde \u671b\u90fd\u6ca1\u6709\u671b\u4e00\u4e0b\uff0c\u4ecd\u7136\u7aef\u5750\u7740\u3002 \u3000\u3000\u4ed6\u5947\u602a\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u53ef\u662f\u4ed6\u968f\u5373\u770b\u51fa\u90a3\u4eba\u5c31\u662f\u4ed6\u521d\u5230\u6b64\u5904\u6240\u89c1\u7684\u4faf\u4e8c\u53d4\uff0c\u4ed6\u6697\u81ea\u60ed \u6127\u7740\u81ea\u5df1\u7684\u614c\u5f20\uff0c\u8eac\u8eab\u53eb\u4e86\u58f0\uff1a\u201c\u4faf\u4e8c\u53d4\u201d\u3002 \u3000\u3000\u4faf\u4e8c\u53d4\u51b7\u5cfb\u7684\u9762\u5bb9\uff0c\u7adf\u4f3c\u6709\u4e86\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u4e00\u522b\u5341\u5e74\uff0c\u8d24\u4f84\u679c\u7136\u8eab\u624b\u4e0d\u540c\u51e1\u54cd\u4e86\uff0c \u771f\u662f\u6240\u8c13\u4e00\u4ee3\u65b0\u4eba\u6362\u65e7\u4eba\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u60f3\u5230\u81ea\u5df1\u867d\u7136\u6781\u5feb\u5730\u7a9c\u4e86\u51fa\u53bb\uff0c\u4f46\u4eba\u5bb6\u5374\u5df2\u5b89\u5750\u623f\u4e2d\uff0c\u4e0d\u7981\u60ed\u6127\u7684\u4f4e\u4e0b\u5934\u53bb\u3002 \u3000\u3000\u6885\u5c71\u6c11\u8bf4\u9053\uff1a\u201c\u59dc\u662f\u8001\u7684\u8fa3\uff0c\u6377\u513f\u5012\u5e95\u7ecf\u5386\u592a\u5c11\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5411\u4faf\u4e8c\u53d4\u95ee\u9053\uff1a\u201c\u4e8b\u60c5\u5982\u4f55\u4e86\u3002\u201d \u3000\u3000\u4faf\u4e8c\u53d4\u8bf4\u9053\uff1a\u201c\u5927\u81f4\u5df2\u529e\u59a5\u4e86\uff0c\u6211\u5728\u6b66\u6c49\u4e00\u5e26\uff0c\u548c\u957f\u6c5f\u6cbf\u5cb8\u7684\u5927\u57ce\uff0c\u90fd\u8bbe\u4e0b\u4e86\u5c71\u6885 \u73e0\u5b9d\u53f7\uff0c\u5df2\u6709\u5341\u4e09\u5904\uff0c\u53ea\u8981\u4e00\u5429\u5490\uff0c\u6377\u513f\u4fbf\u53ef\u53bb\u4e3b\u6301\u4e86\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u70b9\u4e86\u70b9\u5934\uff0c\u5411\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u6b64\u756a\u6211\u867d\u547d\u4f60\u53bb\u95ef\u6c5f\u6e56\uff0c\u5374\u4e0d\u613f\u4f60\u53bb\u548c\u90a3\u4e9b\u6b66\u6797 \u4e2d\u83bd\u6c49\u4e89\u540d\u593a\u5229\uff0c\u5df1\u7ecf\u66ff\u4f60\u6253\u597d\u4e86\u57fa\u7840\uff0c\u4faf\u4e8c\u53d4\u5728\u6c5f\u5357\u4e00\u5e26\uff0c\u5df2\u66ff\u4f60\u8bbe\u4e86\u5341\u51e0\u5904\u73e0\u5b9d\u53f7\uff0c \u4f60\u4ece\u6b64\u4fbf\u662f\u8fd9\u4e9b\u73e0\u5b9d\u53f7\u7684\u4e1c\u4e3b\uff0c\u6211\u8fd9\u6837\u505a\uff0c\u4e00\u6765\u662f\u4e0d\u8981\u4f60\u53bb\u53d7\u82e6\uff0c\u518d\u6765\u4e5f\u662f\u56e0\u4e3a\u6c5f\u6e56\u4e0a \u975e\u94b1\u83ab\u529e\u7684\u4e8b\u60c5\u592a\u591a\uff0c\u6709\u4e86\u94b1\uff0c\u6211\u53eb\u4f60\u53bb\u66ff\u6211\u505a\u7684\u4e8b\uff0c\u5c31\u597d\u5f97\u591a\u3002\u201d \u3000\u3000\u4ed6\u53c8\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u6b21\u51fa\u53bb\uff0c\u4ec0\u4e48\u4e8b\u90fd\u53ef\u4ee5\u968f\u5fc3\u53bb\u505a\uff0c\u53ea\u8981\u4e0d\u4f24\u5bb3\u5584\u826f\u7684\u4eba\u5c31\u884c \u4e86\uff0c\u9664\u4e86\u2018\u6d77\u5929\u53cc\u715e\u2019\u662f\u4f60\u8981\u5bf9\u4ed8\u4e4b\u5916\uff0c\u4e2d\u539f\u6b66\u6797\u7684\u4e94\u5927\u5b97\u6d3e\uff0c\u4f60\u66f4\u8981\u597d\u597d\u5730\u53bb\u5bf9\u4ed8\u4ed6 \u4eec\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u81f3\u6b64\u5904\uff0c\u7528\u624b\u4e00\u62cd\u684c\u5b50\uff0c\u6012\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u7269\u5047\u5192\u4f2a\u5584\uff0c\u80cc\u7740\u2018\u6b66\u6797\u4e94\u5b97\u2019\u7684\u724c \u5b50\uff0c\u5374\u505a\u4e9b\u5351\u9119\u65e0\u803b\u7684\u4e8b\uff0c\u4f60\u5343\u4e07\u8981\u6ce8\u610f\u3002\u201d \u3000\u3000\u8f9b\u6377\u6781\u5174\u594b\u5730\u79f0\u662f\uff0c\u4ed6\u867d\u4e0d\u4e86\u89e3\u6b66\u6797\u4e2d\u7684\u60c5\u5f62\uff0c\u4f46\u662f\u53ea\u8981\u6885\u53d4\u53d4\u6240\u8bf4\u7684\uff0c\u4ed6\u90fd\u8ba4\u4e3a \u662f\u5bf9\u7684\uff0c\u56e0\u6b64\u65e5\u540e\u6b66\u6797\u4e2d\uff0c\u5e73\u751f\u51fa\u5929\u5927\u7684\u98ce\u6ce2\u3002 \u3000\u3000\u4faf\u4e8c\u53d4\u671b\u7740\u81ea\u5df1\u7684\u624b\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5251\u795e\u5389\u9e57\uff0c\u73b0\u5728\u5df2\u662f\u4e2d\u539f\u6b66\u6797\u4e2d\u7684\u9886\u8896\u4eba\u7269\uff0c \u6b66\u6797\u4e2d\u53ea\u8981\u2018\u5929\u4e0b\u7b2c\u4e00\u5251\u2019\u7684\u4f20\u67ec\u4e00\u5230\uff0c\u5929\u5927\u7684\u4e8b\u4e5f\u7acb\u523b\u5316\u89e3\uff0c\u5509\uff0c\u6211\u82e5\u4e0d\u662f\u6614\u5e74\u53d7\u4e86 \u91cd\u4f24\uff0c\u53cc\u624b\u603b\u662f\u7528\u4e0d\u5f97\u529b\uff0c\u6211\u771f\u8981\u6211\u8fd9\u4e9b\u4eba\u4e00\u8f83\u957f\u77ed\uff0c\u73b0\u5728\u8fd9\u4e9b\u4e8b\uff0c\u90fd\u53ea\u597d\u7b49\u6377\u513f\u53bb\u505a \u4e86\u3002\u201d \u3000\u3000\u8bf4\u9053\uff0c\u4ed6\u8138\u4e0a\u53c8\u95ea\u8fc7\u4e00\u4e1d\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4ece\u660e\u5929\u8d77\uff0c\u6211\u5c31\u4e0d\u80fd\u518d\u53eb\u4f60\u6377\u513f\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e00\u6115\u3002 \u3000\u3000\u6885\u5c71\u6c11\u7b11\u9053\uff1a\u201c\u4f60\u4eca\u540e\u884c\u8d70\u6c5f\u6e56\uff0c\u6709\u8bb8\u591a\u9605\u5386\u90fd\u8fd8\u5dee\u5f97\u592a\u8fdc\uff0c\u800c\u4e14\u4f60\u548c\u90a3\u4e9b\u73e0\u5b9d\u5e97 \u90fd\u6ca1\u6709\u8054\u7edc\uff0c\u4e3a\u4e86\u65b9\u4fbf\u8d77\u89c1\uff0c\u6211\u53eb\u4f60\u4faf\u4e8c\u53d4\u966a\u7740\u4f60\uff0c\u5c31\u7b97\u505a\u4f60\u7684\u8001\u5bb6\u4eba\uff0c\u4ed6\u8981\u53eb\u4f60\u5c11\u7237\uff0c \u81ea\u662f\u4e0d\u80fd\u518d\u53eb\u4f60\u6377\u513f\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u8e0c\u8e87\u7740\u9053\uff1a\u201c\u8fd9\u600e\u4e48\u2026\u2026\u201d\u5019\u4e8c\u53d4\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u662f\u6211\u81ea\u544a\u594b\u52c7\u7684\uff0c\u4f60\u4e0d\u8981\u591a\u7ba1\uff0c \u4ece\u4eca\u4f60\u5c31\u53eb\u6211\u4faf\u4e8c\u597d\u4e86\u3002\u201d \u3000\u3000\u6b66\u660c\u3001\u6c49\u53e3\u3001\u6c49\u9633\uff0c\u4e09\u5730\u5bf9\u5cd9\uff0c\u4e2d\u9694\u957f\u6c5f\uff0c\u81ea\u53e4\u5373\u4e3a\u9102\u4e4b\u91cd\u9547\u3002 \u3000\u3000\u8fd9\u65e5\u6c49\u53e3\u6c5f\u5cb8\u7684\u7801\u5934\u4e0a\uff0c\u4e00\u65e9\u4fbf\u6765\u4e86\u4e00\u7fa4\u7a7f\u7740\u6781\u5e72\u51c0\u7684\u5b9d\u84dd\u953b\u9762\u957f\u888d\u7684\u751f\u610f\u4eba\uff0c\u671b \u53bb\u90fd\u50cf\u4f3c\u5546\u53f7\u7684\u5e97\u4e1c\uff0c\u4e00\u4e2a\u4e2a\u8863\u5c65\u534e\u8d35\uff0c\u6c14\u6d3e\u975e\u51e1\u3002 \u3000\u3000\u6709\u4e9b\u597d\u4e8b\u7684\u5c31\u4e0d\u514d\u63a2\u542c\u8fd9\u4e9b\u4eba\u662f\u8c01\uff0c\u4e3a\u4ec0\u4e48\u8863\u670d\u90fd\u76f8\u540c\uff0c\u4e00\u65e9\u5c31\u805a\u96c6\u5728\u7801\u5934\u4e0a\u3002 \u3000\u3000\u6253\u542c\u4e4b\u4e0b\uff0c\u624d\u77e5\u9053\u8fd9\u4e9b\u4eba\u90fd\u662f\u65b0\u5f00\u5f20\u7684\u5927\u73e0\u5b9d\u53f7\u5c71\u6885\u53f7\u7684\u638c\u67dc\uff0c\u5e97\u4f19\uff0c\u4ed6\u4eec\u805a\u96c6\u5728 \u7801\u5934\u4e0a\u662f\u4e3a\u4e86\u8fce\u63a5\u4ed6\u4eec\u7684\u8001\u677f\u3002 \u3000\u3000\u4eba\u4eec\u90fd\u662f\u975e\u5e38\u52bf\u5229\u7684\uff0c\u770b\u89c1\u8fd9\u4e9b\u8863\u51a0\u695a\u695a\u7684\u4eba\u7269\uff0c\u4e0d\u8fc7\u4ec5\u662f\u5e97\u4f19\u800c\u5df2\uff0c\u800c\u4e14\u53c8\u542c\u8bf4 \u6c49\u53e3\u7684\u5c71\u6885\u73e0\u5b9d\u53f7\u4e0d\u8fc7\u662f\u5341\u51e0\u5bb6\u5206\u53f7\u4e4b\u4e00\u800c\u5df2\uff0c\u957f\u6c5f\u6cbf\u5cb8\uff0c\u53e6\u5916\u8fd8\u8bbe\u6709\u591a\u5904\uff0c\u4e8e\u662f\u66f4\u90fd \u60f3\u4e00\u7779\u8fd9\u767e\u4e07\u5927\u8d3e\u7684\u771f\u9762\u76ee\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4e2a\u65f6\u8fb0\u5de6\u53f3\uff0c\u6c5f\u9762\u9a76\u6765\u4e00\u8258\u53cc\u6845\u5927\u8239\uff0c\u4e0d\u4f46\u6cb9\u6f06\u5168\u65b0\uff0c\u800c\u4e14\u88c5\u7f6e\u5f97\u5bcc\u4e3d\u5802\u7687\uff0c \u8239\u5934\u7684\u706f\u7b3c\u4e0a\u5199\u7740\u6597\u5927\u7684\u5c71\u6885\u4e24\u5b57\u3002 \u3000\u3000\u5927\u5bb6\u5c31\u77e5\u9053\u8fd9\u662f\u5c71\u6885\u73e0\u5b9d\u53f7\u7684\u5e97\u4e1c\u5230\u4e86\uff0c\u90a3\u4e9b\u5e97\u4f19\u4eec\u66f4\u662f\u6781\u606d\u656c\u5730\u7ad9\u5728\u7801\u5934\u4e0a\u7b49\u7740\u3002 \u3000\u3000\u8239\u4e0a\u7684\u8239\u592b\uff0c\u90fd\u50cf\u662f\u6781\u8001\u7684\u6c34\u9762\u597d\u624b\uff0c\u5e73\u7a33\u800c\u8fc5\u901f\u5730\u5c06\u8239\u9760\u4e86\u5cb8\uff0c\u642d\u4e0a\u8df3\u677f\uff0c\u8239\u8231 \u7684\u95e8\u5e18\u4e00\u6380\uff0c\u8d70\u51fa\u4e24\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u662f\u4e2a\u5e74\u7ea6\u4e94\u5341\u7684\u7626\u524a\u6c49\u5b50\uff0c\u5e97\u4f19\u4eec\u90fd\u8ba4\u5f97\u662f\u5f53\u521d\u65a5\u8d44\u5f00\u53f7\u7684\u4eba\uff0c\u53e6\u4e00\u4e2a\u5374 \u662f\u4e2a\u4e8c\u5341\u4e0a\u4e0b\u7684\u82f1\u4fca\u5e74\u9752\u4eba\uff0c\u7a7f\u7740\u751a\u662f\u534e\u4e3d\uff0c\u9762\u5bb9\u82cd\u767d\uff0c\u6c14\u52bf\u4e0d\u51e1\uff0c\u795e\u60c5\u4e5f\u5014\u50b2\u5f97\u5f88\u3002 \u3000\u3000\u5927\u5bb6\u90fd\u77e5\u9053\u6b64\u4eba\u5c31\u662f\u5c71\u6885\u53f7\u7684\u5e97\u4e1c\u4e86\uff0c\u4ed6\u4eec\u539f\u5148\u60f3\u6b64\u4eba\u5fc5\u662f\u4e2a\u4e2d\u5e74\u7684\u5927\u8179\u8d3e\uff0c\u6b64\u523b \u4e00\u89c1\uff0c\u5374\u662f\u4e2a\u5e74\u9752\u4eba\uff0c\u90fd\u5728\u5cb8\u8fb9\u8bae\u8bba\u8d77\u6765\u3002 \u3000\u3000\u6b64\u4e24\u4eba\u4e0d\u8bf4\u800c\u77e5\uff0c\u4fbf\u662f\u521d\u5165\u6c5f\u6e56\u7684\u8f9b\u6377\u548c\u4e54\u88c5\u8001\u4ec6\u7684\u4faf\u4e8c\u4e24\u4eba\u4e86\u3002 \u3000\u3000\u4ed6\u4e8c\u4eba\u4e0a\u4e86\u5cb8\uff0c\u8f9b\u6377\u6781\u6709\u5206\u5bf8\u5730\u5e94\u4ed8\u4e86\u4e0b\u8fce\u63a5\u4ed6\u7684\u4eba\u4eec\uff0c\u4fbf\u5750\u4e0a\u4e86\u4e00\u8f86\u65e9\u5df2\u51c6\u5907\u597d \u4e86\u7684\u9a6c\u8f66\uff0c\u5411\u57ce\u91cc\u9a76\u53bb\u3002 \u3000\u3000\u5f53\u5929\u4e0b\u5348\uff0c\u521a\u5230\u6c49\u53e3\u7684\u5c71\u6885\u73e0\u5b9d\u53f7\u5e97\u4e1c\u8f9b\u6377\uff0c\u4fbf\u5177\u540d\u67ec\u9080\u6b66\u6c49\u4e09\u9547\u7684\u9556\u5c40\u9556\u5934\uff0c\u548c \u5f53\u5730\u6b66\u6797\u4e2d\u7565\u6709\u540d\u6c14\u7684\u4eba\u7269\uff0c\u7b2c\u4e8c\u5929\u665a\u4e0a\u5728\u6b66\u6c49\u4e09\u9547\u6700\u5927\u7684\u996d\u9986\u201c\u5cb3\u9633\u697c\u201d\u665a\u81b3\uff0c\u800c\u4e14 \u8bf7\u5927\u5bb6\u52a1\u5fc5\u8981\u5230\u3002 \u3000\u3000\u4e00\u4e2a\u8eab\u5bb6\u949c\u4e07\u7684\u73e0\u5b9d\u53f7\u5e97\u4e1c\uff0c\u53ef\u8bf4\u662f\u548c\u6b66\u6797\u4e2d\u7edd\u5bf9\u5730\u98ce\u9a6c\u725b\u4e0d\u76f8\u53ca\uff0c\u7136\u800c\u4ed6\u5728\u5230\u57e0 \u7684\u7b2c\u4e00\u5929\uff0c\u4e0d\u8bf7\u4e0e\u4ed6\u751f\u610f\u6709\u5173\u7684\u5546\u53f7\u8001\u677f\uff0c\u5374\u8bf7\u4e9b\u6b66\u6797\u4e2d\u4eba\uff0c\u8fd9\u4ef6\u4e8b\u4f7f\u5f97\u5927\u5bb6\u90fd\u5947\u602a\u5f97 \u5f88\u3002 \u3000\u3000\u63a5\u5230\u8bf7\u67ec\u7684\u4eba\u58eb\uff0c\u5168\u90fd\u4e0d\u8ba4\u8bc6\u5177\u540d\u7684\u4eba\u7269\uff0c\u63a2\u8be2\u4e4b\u4e0b\uff0c\u77e5\u9053\u662f\u4e2a\u5982\u6b64\u5982\u6b64\u7684\u751f\u610f\u4eba\uff0c \u4e0d\u514d\u89c9\u5f97\u975e\u5e38\u5947\u602a\uff0c\u5230\u522b\u7684\u6b66\u6797\u4eba\u7269\u5904\u53bb\u4e00\u95f4\uff0c\u7adf\u7136\u4e5f\u662f\u4e00\u6837\uff0c\u800c\u4e14\u51e0\u4e4e\u6b66\u6797\u3001\u9556\u5c40\u6709 \u5934\u6709\u9762\u7684\u4eba\u7269\uff0c\u5168\u8bf7\u5230\u4e86\u3002 \u3000\u3000\u9556\u5c40\u4e2d\u4eba\u5e73\u65f6\u548c\u73e0\u5b9d\u53f7\u5e97\u672c\u6709\u8054\u7edc\uff0c\u4f46\u4e0d\u8fc7\u90fd\u662f\u8ba8\u8bba\u4fdd\u9556\u7684\u4e8b\uff0c\u50cf\u8fd9\u79cd\u4e8b\u867d\u5c5e\u521d\u89c1\uff0c \u5728\u60c5\u7406\u4e0a\u8fd8\u53ef\u4ee5\u60f3\u5f97\u51fa\u6765\u3002 \u3000\u3000\u7136\u800c\u90a3\u4e9b\u5e73\u65e5\u4e0e\u4fdd\u9556\u65e0\u5173\uff0c\u751a\u81f3\u6709\u7684\u5df2\u7ecf\u534a\u9000\u4f11\u4e86\u7684\u6b66\u6797\u4e2d\u4eba\uff0c\u6839\u672c\u65e0\u6cd5\u731c\u51fa\u8fd9\u8bf7 \u67ec\u4ec0\u4e48\u610f\u601d\uff0c\u5f7c\u6b64\u76f8\u719f\u7684\uff0c\u4e0d\u514d\u5927\u5bb6\u731c\u6d4b\uff0c\u4f46\u4e5f\u731c\u4e0d\u51fa\u4ec0\u4e48\u7ed3\u679c\u6765\uff0c\u8ba8\u8bba\u4e4b\u4e0b\uff0c\u90fd\u8ba4\u4e3a \u8be5\u53bb\u4e00\u770b\u7a76\u7adf\u3002 \u3000\u3000\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u5cb3\u9633\u697c\u4e0a\u65e9\u5df2\u6446\u597d\u51e0\u5f20\u684c\u9762\uff0c\u53ef\u662f\u5927\u5bb6\u90fd\u5230\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u4ecd\u672a\u770b\u5230\u4e3b \u4eba\u7684\u5f71\u5b50\uff0c\u53ea\u6709\u51e0\u4e2a\u5c71\u6885\u53f7\u7684\u4f19\u8ba1\u5728\u62db\u547c\u7740\u3002 \u3000\u3000\u4e8e\u662f\u8fd9\u4e9b\u6b66\u6797\u8c6a\u58eb\uff0c\u4e0d\u514d\u4e00\u4e2a\u4e2a\u706b\u5192\u4e09\u4e08\uff0c\u6b63\u5f85\u53d1\u4f5c\u4e4b\u9645\uff0c\u90a3\u4e9b\u5e97\u4f19\u4eec\u5df2\u7ecf\u5728\u9ad8\u58f0 \u547c\u9053\uff1a\u201c\u8f9b\u8001\u677f\u6765\u4e86\uff0c\u8f9b\u8001\u677f\u6765\u4e86\u3002\u201d \u3000\u3000\u767b\u3001\u767b\u3001\u767b\u3001\u697c\u68af\u54cd\u5904\uff0c\u4f17\u4eba\u53ea\u89c9\u5f97\u773c\u524d\u4e00\u4eae\uff0c\u7fa4\u8c6a\u4e5f\u4ff1\u672b\u60f3\u5230\u8fd9\u2018\u8f9b\u8001\u677f\u2019\u7adf\u662f \u4e2a\u8fd9\u6837\u7684\u4fca\u54c1\u4eba\u7269\uff0c\u60ca\u5947\u4e4b\u4e0b\uff0c\u706b\u6c14\u90fd\u51cf\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u4e0a\u697c\u6765\uff0c\u5c31\u6ee1\u9762\u6625\u98ce\u7684\u62b1\u62f3\u8bf4\u9053\uff1a\u201c\u5404\u4f4d\u4e45\u5019\u4e86\uff0c\u5b9e\u662f\u62b1\u6b49\u4e4b\u81f3\uff0c\u5c0f\u5f1f\u4fd7\u52a1 \u592a\u591a\uff0c\u8fd8\u8bf7\u5404\u4f4d\u6055\u7f6a\u3002\u201d \u3000\u3000\u63a5\u7740\u4ed6\u5c31\u6328\u4e2a\u5730\u5411\u90a3\u4e9b\u6b66\u6797\u4eba\u7269\u8bf7\u6559\u59d3\u540d\uff0c\u63e1\u624b\u5bd2\u55a7\u3002 \u3000\u3000\u7b75\u5e2d\u968f\u5373\u5f00\u4e0a\uff0c\u8f9b\u6377\u62f1\u624b\u8bf7\u5ba2\u4eba\u5750\uff0c\u9152\u8fc7\u4e09\u5de1\uff0c\u8f9b\u6377\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u867d\u662f\u4e2a\u6d51\u8eab \u94dc\u81ed\u7684\u5c0f\u5546\u4eba\uff0c\u5374\u81ea\u5e7c\u5373\u559c\u7ed3\u4ea4\u6b66\u6797\u8c6a\u58eb\uff0c\u8fd9\u6b21\u5c0f\u5f1f\u5f00\u8bbe\u8fd9\u4e9b\u884c\u53f7\uff0c\u4e5f\u662f\u60f3\u5728\u5404\u5904\u591a\u4ea4 \u4e9b\u670b\u53cb\u7684\u610f\u601d\uff0c\u6b64\u6b21\u4e0d\u8f9e\u5192\u6627\uff0c\u5c06\u5404\u4f4d\u5927\u9a7e\u8bf7\u6765\uff0c\u5b9e\u56e0\u5c0f\u5f1f\u4e45\u95fb\u9102\u4e2d\u8c6a\u58eb\u5982\u4e91\uff0c\u6b66\u5f53\u95e8 \u4e0b\u7684\u5f1f\u5b50\uff0c\u66f4\u662f\u4e2a\u4e2a\u8eab\u6000\u7edd\u6280\uff0c\u4e45\u60f3\u4e00\u7779\u98ce\u91c7\u4e4b\u6545\u3002\u201d \u3000\u3000\u4ed6\u65e5\u5149\u6a2a\u626b\uff0c\u6781\u7559\u5fc3\u5730\u89c2\u770b\u5ea7\u4e0a\u4eba\u7269\u7684\u8868\u60c5\uff0c\u5f53\u4ed6\u770b\u5230\u5176\u4e2d\u6709\u4e9b\u4e0d\u662f\u6b66\u5f53\u95e8\u4e0b\u7684\u8c6a \u58eb\uff0c\u8138\u4e0a\u5df1\u6709\u4e0d\u60a6\u4e4b\u8272\uff0c\u5fc3\u4e2d\u6697\u518d\uff0c\u7b11\u7740\u63a5\u9053\u201c\u5c0f\u5f1f\u867d\u662f\u4e0d\u4f1a\u6b66\u6280\uff0c\u4f46\u5374\u61c2\u5f97\u4e00\u70b9\uff0c\u65e5 \u540e\u5982\u679c\u6709\u7f18\uff0c\u4f46\u671b\u80fd\u89c1\u8bc6\u5404\u4f4d\u7684\u7edd\u6280\uff0c\u5c24\u5176\u6b66\u5f53\u7684\u5251\u6cd5\uff0c\u66f4\u662f\u4e45\u4ef0\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e24\u6b21\u63d0\u5230\u6b66\u5f53\uff0c\u5374\u6545\u610f\u5730\u672a\u63d0\u4e2d\u539f\u5176\u4ed6\u56db\u5927\u5b97\u6d3e\uff0c\u5ea7\u4e0a\u8bf8\u8c6a\uff0c\u5df2\u5728\u4e0d\u6ee1\u4e86\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u4e00\u4e3e\u9152\u676f\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u6211\u8fd9\u7b2c\u4e00\u676f\u9152\uff0c\u5374\u8981\u656c\u656c\u6b66\u5f53\u95e8\u4e0b\u7684\u4e5d\u5bab\u5251\u674e\u5927 \u4fa0\uff0c\u6765\u6765\u6765\uff0c\u674e\u5927\u4fa0\uff0c\u6211\u4eec\u5e72\u8fd9\u4e00\u676f\u3002\u201d \u3000\u3000\u90a3\u4e5d\u5bab\u5251\u674e\u6cbb\u534e\uff0c\u867d\u662f\u6b66\u5f53\u95e8\u4e0b\u5f1f\u5b50\uff0c\u4f46\u5728\u6b66\u6c49\u4e09\u9547\uff0c\u5e76\u7b97\u4e0d\u4e0a\u4e00\u6d41\u4eba\u7269\uff0c\u6b64\u523b\u4ed6 \u89c1\u8f9b\u6377\u9996\u5148\u4fbf\u5411\u4ed6\u656c\u9152\uff0c\u4e0d\u514d\u9ad8\u5174\u5f97\u5f88\u3002 \u3000\u3000\u4ed6\u4e3e\u8d77\u9152\u676f\uff0c\u7ad9\u4e86\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u627f\u8f9b\u8001\u677f\u770b\u5f97\u8d77\u6211\u4eec\u6b66\u5f53\u6d3e\uff0c\u6211\u674e\u6cbb\u534e\u5b9e\u5728\u611f\u6fc0\uff0c \u6211\u674e\u6cbb\u534e\u867d\u7136\u4e0d\u8db3\u9053\u54c9\uff0c\u4f46\u6211\u4eec\u6b66\u5f53\u6d3e\uff0c\u5012\u7684\u786e\u662f\u6b66\u6797\u4e4b\u9996\uff0c\u5c0f\u5f1f\u4e5f\u5c31\u539a\u989c\u5e72\u4e86\u8f9b\u8001\u677f \u7684\u9152\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u521a\u8bf4\u5b8c\uff0c\u90a3\u77e5\u201c\u9500\u201d\u4e00\u58f0\uff0c\u624b\u4e2d\u9152\u676f\u7adf\u88ab\u51fb\u5f97\u7c89\u788e\u3002\u90a3\u674e\u6cbb\u534e\u6b63\u81ea\u5fd7\u5f97\u610f\u6ee1\u4e4b \u9645\uff0c\u624b\u4e0a\u9152\u676f\uff0c\u5ffd\u6321\u5730\u4e00\u58f0\uff0c\u88ab\u51fb\u5f97\u7c89\u788e\uff0c\u676f\u4e2d\u4e4b\u9152\uff0c\u6d12\u5f97\u4ed6\u9752\u84dd\u7684\u6b66\u58eb\u8863\u6ee1\u5904\u7686\u662f\u3002 \u3000\u3000\u5ea7\u4e0a\u4ff1\u4e3a\u6b66\u6797\u4e2d\u4eba\uff0c\u773c\u529b\u591a\u5feb\uff0c\u65e9\u770b\u51fa\u90a3\u662f\u5750\u5728\u9e23\u8fdc\u9556\u5c40\u7684\u603b\u9547\u5934\u94f6\u67aa\u5b5f\u4f2f\u8d77\u8eab\u4fa7 \u7684\u9762\u8272\u6de1\u9ec4\u7684\u4e00\u4eba\uff0c\u5728\u674e\u6cbb\u534e\u5174\u9ad8\u91c7\u70c8\u5730\u5938\u8000\u7740\u6b66\u5f53\u6d3e\u65f6\uff0c\u624b\u5fae\u4e00\u626c\uff0c\u624b\u4e2d\u7684\u7259\u7b77\uff0c\u4fbf \u5c06\u90a3\u676f\u51fb\u788e\u3002 \u3000\u3000\u90a3\u7259\u7b77\u53bb\u52bf\u9887\u6025\uff0c\u529b\u9053\u53c8\u731b\uff0c\u51fb\u4e2d\u9152\u676f\u540e\uff0c\u4ecd\u76f4\u98de\u51fa\u53bb\uff0c\u201c\u593a\u201d\u5730\u4e00\u58f0\uff0c\u7adf\u6df1\u6df1\u5d4c \u4eba\u5899\u91cc\u3002 \u3000\u3000\u674e\u6cbb\u534e\u9152\u676f\u88ab\u51fb\uff0c\u9762\u8272\u7acb\u53d8\uff0c\u56db\u9762\u4e00\u987e\uff0c\u89c1\u8bf8\u4eba\u90fd\u5728\u60ca\u6115\u5730\u671b\u7740\u90a3\u9762\u8272\u6de1\u9ec4\u7684\u6c49\u5b50\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5947\u602a\uff0c\u77e5\u9053\u9152\u676f\u5fc5\u662f\u88ab\u6b64\u4eba\u51fb\u788e\uff0c\u4f46\u81ea\u5df1\u5374\u548c\u6b64\u4eba\u7d20\u4e0d\u76f8\u8bc6\uff0c\u800c\u4e14\u81ea\u5df1\u5728\u6b66 \u6c49\u591a\u5e74\uff0c\u770b\u6765\u6b64\u4eba\u7edd\u975e\u6b66\u6c49\u5730\u9762\u7684\u8c6a\u5ba2\uff0c\u600e\u5730\u5374\u51fa\u624b\u51fb\u788e\u81ea\u5df1\u7684\u9152\u676f\uff0c\u987b\u77e5\u6b64\u4e8b\u751a\u5931\u9762 \u5b50\uff0c\u6b66\u6797\u4e2d\u82e5\u6709\u6b64\u4e8b\u53d1\u751f\uff0c\u9664\u4e86\u52a8\u624b\u89e3\u51b3\u4e4b\u5916\uff0c\u522b\u65e0\u4ed6\u6cd5\u3002 \u3000\u3000\u674e\u6cbb\u534e\u9762\u5982\u51dd\u971c\uff0c\u6012\u9053\uff1a\u201c\u76f8\u597d\u7684\uff0c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff0c\u8981\u5bf9\u4ed8\u6211\u59d3\u674e\u7684\uff0c\u53ea\u7ba1\u5212\u51fa\u9053 \u513f\u5c31\u662f\uff0c\u8bf4\u4ec0\u4e48\u6211\u59d3\u674e\u7684\u5168\u63a5\u4f4f\u4f60\u7684\u3002\u201d \u3000\u3000\u8f9b\u6377\u89c1\u6709\u4eba\u51fa\u624b\u51fb\u788e\u674e\u6cbb\u534e\u7684\u9152\u676f\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u5fd6\u9053\uff1a\u201c\u679c\u7136\u4e0d\u51fa\u6211\u6240\u6599\uff0c\u800c\u4e14\u6765 \u5f97\u8fd9\u4e48\u5feb\uff0c\u8fde\u6211\u90fd\u6709\u4e9b\u610f\u5916\u5462\u3002\u201d \u3000\u3000\u4f46\u662f\u4ed6\u9762\u4e0a\u5374\u4f5c\u51fa\u4e00\u5e45\u60f6\u6050\u7684\u6837\u5b50\uff0c\u53cc\u624b\u8fde\u6446\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u8bdd\u597d\u8bf4\uff0c\u6709\u4ec0\u4e48\u8bdd\u597d\u8bf4\uff0c \u5343\u4e07\u522b\u52a8\u6012\uff0c\u8fd9\u6837\u5c0f\u5f1f\u592a\u96be\u4e3a\u60c5\u4e86\u3002\u201d \u3000\u3000\u90a3\u9762\u8272\u6de1\u9ec4\u7684\u6c49\u5b50\uff0c\u53cc\u624b\u671d\u8f9b\u6377\u4e00\u62f1\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u8fde\u773c\u89d2\u90fd\u6ca1\u6709\u5411\u674e\u6cbb\u534e\u6f02\u4e00\u4e0b\uff0c \u4f3c\u4e4e\u5bf9\u674e\u6cbb\u534e\u5b8c\u5168\u4e0d\u5c51\u4e00\u987e\u3002 \u3000\u3000\u674e\u6cbb\u534e\u7684\u6012\u706b\u4e0d\u7531\u66f4\u76db\uff0c\u4f30\u91cf\u975e\u6b66\u6797\u91cc\u7684\u4e00\u7b49\u89d2\u8272\uff0c\u4f46\u6709\u4eba\u5f53\u7740\u5982\u8bb8\u8c6a\u58eb\uff0c\u516c\u7136\u7684 \u4fae\u8fb1\u4e86\u4ed6\uff0c\u800c\u4e14\u662f\u8fd9\u6837\u5730\u8f7b\u8511\u5730\u4fae\u8fb1\u3002 \u3000\u3000\u4ed6\u6076\u6bd2\u5730\u671b\u7740\u90a3\u4eba\uff0c\u90a3\u4eba\u4ecd\u5374\u4f3c\u5168\u7136\u6ca1\u6709\u5c06\u4ed6\u653e\u5728\u773c\u91cc\uff0c\u4ece\u5bb9\u5730\u5411\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u5728 \u4e0b\u4e8e\u4e00\u98de\uff0c\u5076\u6e38\u6b66\u6c49\uff0c\u95fb\u4eba\u8a00\u53ca\u8f9b\u8001\u677f\u7684\u76db\u4e3e\uff0c\u5fc3\u91cc\u5411\u5f80\u5f97\u5f88\uff0c\u9042\u505a\u4e86\u4e2a\u4e0d\u901f\u4e4b\u5ba2\uff0c\u8fd8 \u671b\u8f9b\u8001\u677f\u6055\u7f6a\u3002\u201d \u3000\u3000\u8f9b\u6377\u542c\u4ed6\u4e00\u62a5\u540d\u5b57\uff0c\u5fc3\u4e2d\u66f4\u559c\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e8e\u4e00\u98de\u5927\u7ea6\u5c31\u662f\u4faf\u4e8c\u53d4\u6240\u8bf4\u7684\u5d06\u5cd2\u4e09\u7edd \u5251\u4e2d\u7684\u5730\u7edd\u5251\u4e86\uff0c\u6b64\u4e8b\u82e5\u7531\u4ed6\u5f00\u573a\uff0c\u90a3\u5c31\u66f4\u597d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u5728\u8f6c\u7740\u5ff5\u5934\uff0c\u5634\u91cc\u5374\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4eca\u65e5\u4e4b\u4e3e\uff0c\u4e3a\u7684\u5c31\u662f\u7ed3\u4ea4\u5929\u4e0b\u597d\u6c49\uff0c\u4e8e\u5927 \u4fa0\u80af\u8d4f\u5149\uff0c\u5c0f\u5f1f\u5b9e\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u201d\u4ed6\u773c\u8272\u6a2a\u626b\u4e86\u674e\u6cbb\u534e\u4e00\u773c\uff0c\u89c1\u674e\u6cbb\u534e\u795e\u8272\u66f4\u662f\u96be\u770b\uff0c \u800c\u4e14\u8fd8\u6709\u4e9b\u5fae\u9732\u51fa\u4e9b\u4e0d\u5b89\uff0c\u77e5\u9053\u8fd9\u4e8e\u4e00\u98de\u7684\u540d\u5934\uff0c\u5df2\u7136\u60ca\u9707\u4e86\u4ed6\uff0c\u82e5\u7136\u4ed6\u7f29\u5934\u4e00\u6015\u4e8b\uff0c \u8fd9\u4e8b\u53c8\u95f9\u4e0d\u8d77\u6765\u4e86\uff0c\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u4fbf\u53c8\u6709\u4e86\u8ba1\u8f83\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u63a5\u7740\u8bf4\uff1a\u201c\u53ea\u662f\u8fd9\u4f4d\u674e\u5927\u4fa0\uff0c\u662f\u6b66\u5f53\u9ad8\u5f92\u3002\u4e8e\u5927\u4fa0\u83ab\u975e\u548c\u674e\u5927\u4fa0\u7ed3\u6709\u4ec0\u4e48\u6881 \u5b50\uff0c\u4f9d\u5c0f\u5f1f\u4e4b\u89c1\uff0c\u8fd8\u662f\u7b97\u4e86\u5427\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u4e2d\u53c8\u5fae\u5fae\u5e26\u51fa\u6b66\u5f53\u6d3e\uff0c\u5730\u7edd\u5251\u4ef0\u9996\u54c8\u54c8\u4e00\u9635\u5927\u7b11\uff0c\u72c2\u50b2\u5730\u8bf4\uff1a\u201c\u4e8e\u67d0\u4eba\u867d\u7136\u4e0d \u624d\uff0c\u4f46\u82e5\u8bf4\u8fd9\u59d3\u674e\u7684\u548c\u4e8e\u67d0\u4eba\u7ed3\u4e0b\u6881\u5b50\uff0c\u54fc\uff0c\u4ed6\u8fd8\u4e0d\u914d\uff0c\u6211\u4e8e\u67d0\u4eba\u4e0d\u8fc7\u770b\u4ed6\u53e3\u53d1\u72c2\u8a00\uff0c \u624d\u51fa\u624b\u6559\u8bad\u6559\u8bad\u4ed6\u3002\u201d \u3000\u3000\u5ea7\u4e0a\u8bf8\u4eba\uff0c\u4e00\u770b\u4fbf\u77e5\u6b64\u4e8b\u4eca\u65e5\u53c8\u662f\u4e2a\u4e0d\u4e86\u4e4b\u5c40\uff0c\u90a3\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4e43\u6b66\u6797\u7b2c\u4e00\u5251\u5251\u795e \u5389\u9e57\u7684\u7b2c\u4e8c\u4e2a\u5f1f\u5b50\uff0c\u4e0e\u5929\u7edd\u5251\u8bf8\u845b\u660e\uff0c\u4eba\u7edd\u5251\u82cf\u6620\u96ea\uff0c\u5e76\u79f0\u4e3a\u201c\u5d06\u5cd2\u4e09\u7edd\u5251\u201d\u3002\u8fd1\u5e74\u65e9 \u5df2\u540d\u52a8\u6b66\u6797\u3002 \u3000\u3000\u90a3\u674e\u6cbb\u534e\u5728\u6b66\u6797\u4e2d\u867d\u662f\u5e73\u5e73\u4e4b\u8f88\uff0c\u4f46\u4ea6\u662f\u6b66\u5f53\u5f1f\u5b50\uff0c\u6b66\u5f53\u6d3e\u5411\u4ee5\u5929\u4e0b\u7b2c\u4e00\u5b97\u6d3e\u81ea\u79f0\uff0c \u95e8\u4e0b\u5f1f\u5b50\u4e5f\u90fd\u662f\u4e9b\u5014\u67b6\u7684\u89d2\u8272\uff0c\u600e\u4f1a\u5728\u4eba\u524d\u7518\u53d7\u6b64\u8fb1\u3002 \u3000\u3000\u4f46\u4e8b\u4e0d\u5e72\u5df1\uff0c\u5927\u5bb6\u90fd\u51b7\u773c\u770b\u7740\u6b64\u4e8b\u7684\u8fdb\u5c55\uff0c\u65e0\u4eba\u53d1\u8a00\u529d\u89e3\u3002 \u3000\u3000\u674e\u6cbb\u534e\u7ad9\u5728\u90a3\u91cc\uff0c\u8138\u4e0a\u9752\u4e00\u9635\uff0c\u767d\u4e00\u9635\uff0c\u4ed6\u81ea\u5fd6\u6b66\u529f\uff0c\u5b9e\u975e\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u7684\u5bf9\u624b\uff0c \u4f46\u4ed6\u7a76\u7adf\u5728\u6b66\u6c49\u5730\u9762\u4e0a\u4e5f\u7b97\u5f97\u4e0a\u4e00\u53f7\u4eba\u7269\uff0c\u65e0\u8bba\u5982\u4f55\uff0c\u4e5f\u5f97\u8981\u60f3\u51fa\u6cd5\u5b50\u6765\u633d\u56de\u81ea\u5df1\u7684\u9762 \u5b50\u3002 \u3000\u3000\u4ed6\u60f3\u6765\u60f3\u53bb\u3002\u5fc3\u4e2d\u6709\u4e86\u4e2a\u4e3b\u610f\uff0c\u4e8e\u662f\u4ed6\u505a\u51fa\u6781\u7aef\u6124\u6012\u7684\u6837\u5b50\uff0c\u731b\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u5316\u9053\uff1a \u201c\u59d3\u4e8e\u7684\uff0c\u4f60\u5c11\u5356\u72c2\uff0c\u522b\u4eba\u754f\u60e7\u4f60\u2018\u5d06\u5cd2\u4e09\u7edd\u5251\u2019\uff0c\u6211\u674e\u6cbb\u534e\u5012\u8981\u89c1\u8bc6\u89c1\u8bc6\u4f60\u5230\u5e95\u6709\u4ec0 \u4e48\u51fa\u4eba\u5934\u5730\u7684\u529f\u592b\u3002\u201d \u3000\u3000\u4ed6\u56db\u987e\u7fa4\u8c6a\uff0c\u770b\u89c1\u8bf8\u4eba\u9762\u4e0a\uff0c\u90fd\u9732\u51fa\u4e9b\u60ca\u8be7\u4e4b\u5bb9\uff0c\u7686\u56e0\u8fd9\u674e\u6cbb\u534e\u5e73\u65e5\u53ea\u662f\u5634\u4e0a\u7684\u628a \u5f0f\uff0c\u771f\u9047\u4e0a\u4e8b\u603b\u662f\u7f29\u5934\u4e00\u8eb2\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u9047\u5230\u4e86\u5411\u79f0\u624e\u624b\u7684\u4e8e\u4e00\u98de\uff0c\u5374\u4e00\u70b9\u513f\u4e5f\u4e0d\u542b\u7cca\u3002 \u3000\u3000\u90a3\u77e5\u674e\u6cbb\u534e\u5fc3\u4e2d\u5374\u53e6\u6709\u8ba1\u8f83\uff0c\u4ed6\u4e5f\u6015\u4e8e\u4e00\u98de\u7684\u6b66\u529f\uff0c\u4ee5\u4ed6\u7684\u4e2a\u6027\uff0c\u600e\u4f1a\u5403\u6b64\u773c\u524d\u4e8f\uff0c \u4f46\u662f\u4ed6\u5374\u60f3\u5c06\u81ea\u5df1\u548c\u4e8e\u4e00\u98de\u4e4b\u4e89\uff0c\u53d8\u4e3a\u201c\u6b66\u5f53\u201d\u548c\u201c\u5d06\u5cd2\u201d\u4e4b\u4e89\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u65e0\u8bba\u4f55\u4e8b\uff0c \u90fd\u6709\u6b66\u5f53\u6d3e\u6765\u66ff\u4ed6\u51fa\u5934\uff0c\u800c\u4ed6\u672c\u8eab\uff0c\u5374\u4e00\u70b9\u4e5f\u4e0d\u4f1a\u53d7\u635f\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u6253\u7740\u5982\u610f\u7b97\u76d8\uff0c\u6b63\u662f\u8f9b\u6377\u6240\u5180\u6c42\u7684\uff0c\u4f46\u8f9b\u6377\u5374\u505a\u51fa\u4e00\u526f\u606f\u4e8b\u5b81\u4eba\u7684\u6837\u5b50\uff0c\u8d70 \u51fa\u5ea7\u6765\uff0c\u529d\u89e3\u7740\u8bf4\uff1a\u201c\u8fd9\u662f\u4f55\u82e6\u5462\uff0c\u674e\u5927\u4fa0\u2026\u2026\u201d \u3000\u3000\u674e\u6cbb\u534e\u4e00\u6446\u624b\uff0c\u62e6\u4f4f\u8f9b\u6377\u7684\u8bdd\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8f9b\u8001\u677f\u4e0d\u8981\u591a\u8bf4\u4e86\uff0c\u6211\u674e\u6cbb\u534e\u5c82\u662f\u4e0d\u61c2 \u4e8b\u7684\u5c0f\u5b69\u5b50\uff0c\u4f1a\u5728\u6b64\u6b22\u805a\u4e4b\u65f6\u751f\u4e8b\uff0c\u59d3\u4e8e\u7684\u4f60\u662f\u6709\u79cd\u7684\uff0c\u4e09\u65e5\u4e4b\u540e\uff0c\u5b50\u6b63\u65f6\u523b\uff0c\u4f60\u6211\u5728 \u9ec4\u9e64\u697c\u4e0b\u4e00\u51b3\u751f\u6b7b\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u4e00\u77aa\uff0c\u76ee\u5149\u5b9b\u5982\u5229\u526a\uff0c\u77aa\u5728\u674e\u6cbb\u534e\u7684\u8138\u4e0a\u3002 \u3000\u3000\u674e\u6cbb\u534e\u5fc3\u4e2d\u4e00\u51db\uff0c\u4ed6\u77e5\u9053\u4e8e\u4e00\u98de\u82e5\u7136\u6b64\u65f6\u5c31\u52a8\u624b\uff0c\u81ea\u5df1\u5fc5\u7136\u8ba8\u4e0d\u4e86\u597d\u53bb\uff0c\u4e8e\u662f\u4ed6\u811a \u4e0b\u63e9\u6cb9\uff0c\u505a\u51fa\u6c14\u6124\u4e4b\u72b6\uff0c\u8e6d\u8e6d\u4e0b\u697c\u53bb\u4e86\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u8138\u5e26\u4e0d\u5c51\u4e4b\u5bb9\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u5802\u5802\u6b66\u5f53\u95e8\u6d3e\uff0c\u5374\u662f\u4e9b\u65e0\u803b\u7684\u5c0f\u4eba\u3002\u201d \u3000\u3000\u8f9b\u6377\u89c1\u674e\u6cbb\u534e\u4e00\u8d70\uff0c\u5fc3\u91cc\u6697\u6697\u597d\u7b11\uff0c\u4f46\u5374\u505a\u51fa\u6447\u5934\u60cb\u60dc\u7684\u6837\u5b50\uff0c\u9644\u5408\u7740\u4e8e\u4e00\u98de\u8bf4\u9053\uff1a \u201c\u5509\uff01\u6211\u4e5f\u60f3\u4e0d\u5230\uff0c\u6211\u539f\u4ee5\u4e3a\u2026\u201d \u3000\u3000\u4ed6\u6545\u610f\u4e00\u987f\uff0c\u7136\u540e\u6539\u53d8\u8bdd\u5934\u8bf4\u9053\uff0c\u4e8e\u5927\u4fa0\u82f1\u59ff\u6f47\u6d12\uff0c\u4e0d\u6562\u8bf7\u95ee\u662f\u54ea\u4e00\u5927\u5b97\u6d3e\u7684\u95e8 \u4e0b\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u4eba\u6700\u5403\u6367\uff0c\u542c\u5230\u8f9b\u6377\u6367\u4ed6\uff0c\u9ad8\u5174\u5730\u8bf4\u9053\uff1a\u201c\u8f9b\u8001\u677f\u592a\u5ba2\u6c14\u4e86\uff0c\u5c0f\u5f1f\u4e0d\u624d\uff0c\u6069 \u5e08\u5374\u662f\u5f53\u4eca\u5929\u4e0b\u65e0\u4eba\u4e0d\u656c\u4ef0\u7684\u4eba\u7269\uff0c\u8f9b\u8001\u677f\u65e2\u597d\u6b66\uff0c\u53ef\u66fe\u542c\u8bf4\u8d77\u8fc7\u2018\u5929\u4e0b\u7b2c\u4e00\u5251\u2019\u7684\u540d \u5934\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u62cd\u524d\u989d\uff0c\u505a\u51fa\u604d\u7136\u5927\u609f\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u771f\u662f\u7cca\u6d82\uff0c\u542c\u4e86\u4e8e\u5927\u4fa0\u7684\u540d\u5b57\uff0c \u65e9\u8be5\u60f3\u5230\u662f\u5f53\u4eca\u5929\u4e0b\u6b66\u6797\u7b2c\u4e00\u9ad8\u4eba\u5251\u795e\u5389\u5927\u4fa0\u7684\u95e8\u4e0b\uff0c\u540d\u52a8\u6b66\u6797\u7684\u2018\u5d06\u5cd2\u4e09\u7edd\u5251\u2019\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e3e\u8d77\u9152\u676f\uff0c\u4ef0\u9996\u5e72\u4e86\uff0c\u7b11\u9053\uff1a\u201c\u4e0d\u77e5\u4e4b\u7f6a\uff0c\u5c0f\u5f1f\u8be5\u7f5a\u4e00\u676f\u3002\u201d \u3000\u3000\u4ed6\u4e3e\u8d77\u58f6\u6765\uff0c\u53c8\u659f\u4e86\u4e00\u676f\u9152\uff0c\u73af\u987e\u56db\u5ea7\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u5207\u83ab\u56e0\u4e9b\u987b\u5c0f\u4e8b\uff0c\u8d25\u4e86\u6e05\u5174\uff0c \u4eca\u65e5\u4e0d\u9189\u65e0\u5f52\uff0c\u5404\u4f4d\u4e00\u5b9a\u8981\u5c3d\u6b22\u800c\u6563\u624d\u662f\u3002\u201d \u3000\u3000\u8bf4\u9053\u4ed6\u62cd\u4e86\u4e24\u4e0b\u5df4\u638c\uff0c\u4e00\u4e2a\u9152\u5e97\u4e2d\u7684\u4f19\u8ba1\u5e94\u58f0\u800c\u6765\uff0c\u5df4\u7ed3\u7684\u95ee\u9053\uff1a\u201c\u8001\u7237\u6709\u4ec0\u4e48\u4e8b \u5429\u5490\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u5ea7\u4e2d\u4ff1\u662f\u82f1\u96c4\uff0c\u6709\u82f1\u96c4\u4e0d\u53ef\u65e0\u7f8e\u4eba\u76f8\u4f34\uff0c\u4f60\u53bb\u628a\u57ce\u91cc\u6709\u540d\u7684\u7c89\u5934 \u5168\u7ed9\u6211\u53eb\u6765\uff0c\u4e0d\u8bba\u662f\u8c01\uff0c\u53ea\u8981\u6765\u7684\uff0c\u4e00\u5f8b\u7ed9\u4e00\u767e\u4e24\u94f6\u5b50\u3002\u201d \u3000\u3000\u5e97\u4f19\u4e00\u542c\uff0c\u5fc3\u91cc\u53c8\u60ca\u53c8\u518d\uff0c\u60ca\u7684\u662f\u8fd9\u4f4d\u51fa\u624b\u771f\u5927\uff0c\u4e00\u51fa\u624b\u5c31\u662f\u4e00\u767e\u4e24\u94f6\u5b50\uff0c\u987b\u77e5\u6309 \u5f53\u65f6\u7684\u7269\u4ef7\uff0c\u4e00\u5ea7\u9876\u9876\u4e0a\u597d\u7684\u71d5\u7fc5\u5e2d\uff0c\u624d\u53ea\u4e00\u4e24\u4e8c\u5206\u94f6\u5b50\uff0c\u4e00\u767e\u4e24\u94f6\u5b50\u8db3\u591f\u4e2d\u7b49\u4eba\u5bb6\u597d \u51e0\u4e2a\u6708\u7684\u56bc\u8c37\u4e86\u3002 \u3000\u3000\u559c\u7684\u662f\uff0c\u8fd9\u4e00\u8d9f\u53c8\u5927\u6709\u6cb9\u6c34\u53ef\u8d5a\uff0c\u5fd9\u66f4\u5df4\u7ed3\u5730\u5e94\u58f0\u53bb\u4e86\u3002 \u3000\u3000\u5ea7\u4e0a\u8bf8\u8c6a\uff0c\u4e0d\u4f46\u60ca\u5f02\u7740\u4ed6\u7684\u8c6a\u9614\uff0c\u800c\u4e14\u8f9b\u6377\u6b64\u4e3e\uff0c\u66f4\u662f\u6295\u4e86\u5927\u5bb6\u7684\u813e\u80c3\uff0c\u5927\u5bb6\u8f70\u7136 \u4e00\u9635\u6b22\u547c\uff0c\u90fd\u5bf9\u8f9b\u6377\u6709\u4e86\u597d\u611f\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u4e5f\u81ea\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u677f\u771f\u662f\u4e00\u4f4d\u6325\u91d1\u5982\u571f\u7684\u516c\u5b50\uff0c\u548c\u90a3\u4e9b\u6ee1\u8eab\u94dc\u81ed\u7684\u5546\u4eba\u4e0d\u5927 \u76f8\u540c\uff0c\u5c0f\u5f1f\u4e0d\u5acc\u5192\u6627\uff0c\u5012\u60f3\u548c\u9601\u4e0b\u4ea4\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u8f9b\u6377\u628a\u7740\u4e8e\u4e00\u98de\u7684\u81c2\u7b11\u9053\uff1a\u201c\u8fd9\u771f\u662f\u5c0f\u5f1f\u7684\u751f\u4e4e\u6700\u5927\u5feb\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u56db\u987e\u7fa4\u8c6a\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u788c\u788c\u4e00\u4e2a\u51e1\u592b\uff0c\u80fd\u4ea4\u5230\u8fd9\u8bb8\u591a\u82f1\u96c4\u8c6a\u6770\uff0c\u5c31\u662f\u8d34\u4e0a\u8eab \u5bb6\u6027\u547d\uff0c\u4e5f\u662f\u9ad8\u5174\u7684\uff0c\u6765\uff0c\u5927\u5bb6\u5e72\u4e00\u676f\u3002\u201d \u3000\u3000\u4ed6\u53c8\u4e3e\u8d77\u9152\u676f\uff0c\u4ef0\u9996\u4e00\u996e\u800c\u5c3d\uff0c\u7fa4\u8c6a\u4e5f\u4ff1\u90fd\u5e72\u4e86\u4e00\u676f\u3002 \u3000\u3000\u8f9b\u6377\u98ce\u6d41\u501c\u50a5\uff0c\u590d\u53c8\u6177\u6982\u591a\u91d1\uff0c\u8fd9\u7fa4\u6b66\u6797\u8c6a\u5ba2\uff0c\u4ff1\u90fd\u5b58\u4e86\u4ea4\u7ed3\u4e4b\u5fc3\u3002 \u3000\u3000\u5927\u5bb6\u4f60\u4e00\u8a00\uff0c\u6211\u4e00\u8bed\uff0c\u90fd\u5728\u8d5e\u6155\u7740\u8f9b\u6377\uff0c\u4e5f\u5728\u8c08\u8bba\u7740\u65b9\u624d\u7684\u4e8b\u6545\u3002 \u3000\u3000\u7a81\u5730\u697c\u4e0b\u7684\u5802\u500c\uff0c\u626f\u76f4\u5589\u5499\u53eb\u9053\uff1a\u201c\u7fe0\u559c\u73ed\u7684\u500c\u4eba\u7389\u51e4\u3001\u7389\u5170\u548c\u5c0f\u7fe0\u3001\u7389\u559c\u56db\u4f4d\u5230 \u4e86\u3002\u201d\u63a5\u7740\u697c\u68af\u4e0a\u4f20\u6765\u4e00\u9635\u7ec6\u788e\u7684\u811a\u6b65\u58f0\u3002 \u3000\u3000\u7fa4\u8c6a\u7cbe\u795e\u4e00\u632f\uff0c\u773c\u5149\u90fd\u671d\u5411\u697c\u68af\u53e3\uff0c\u679c\u7136\u8885\u8885\u5a77\u5a77\u8d70\u4e0a\u56db\u4f4d\u4e3d\u4eba\uff0c\u4ff1\u90fd\u6ee1\u5934\u73e0\u7389\uff0c \u6253\u626e\u5f97\u82b1\u679d\u62db\u5c55\u7684\uff0c\u4e00\u4e0a\u697c\u5c31\u5bf9\u7fa4\u8c6a\u5b09\u7136\u4e00\u7b11\u3002 \u3000\u3000\u8fd9\u4e9b\u6b66\u6797\u8c6a\u5ba2\uff0c\u5927\u534a\u662f\u98ce\u6708\u573a\u4e2d\u7684\u719f\u5ba2\uff0c\u89c1\u4e86\u6b64\u56db\u5973\u4e0a\u6765\uff0c\u7eb7\u7eb7\u4e00\u9635\u563b\u7b11\uff0c\u6709\u76f8\u719f \u7684\u4fbf\u8d70\u4e0a\u53bb\u63a5\u7740\uff0c\u8ba9\u5ea7\uff0c\u8f9b\u6377\u4e5f\u62db\u547c\u7740\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5802\u500c\u53c8\u558a\u9053\uff1a\u201c\u51e4\u6797\u73ed\u7684\u500c\u4eba\u7a1a\u98ce\u3001\u7f8e\u6797\u3001\u767d\u8389\u4e09\u4f4d\u5230\u4e86\u3002\u201d \u3000\u3000\u63a5\u7740\u5802\u4fe1\u53c8\u558a\u4e86\u51e0\u904d\uff0c\u603b\u4e4b\u57ce\u4e2d\u7a0d\u6709\u540d\u6c14\u7684\u5993\u9662\u91cc\u5993\u5973\uff0c\u5927\u534a\u90fd\u6765\u4e86\u3002 \u3000\u3000\u8fd9\u4e5f\u662f\u94b1\u80fd\u901a\u795e\uff0c\u5979\u4eec\u672c\u4ee5\u6b64\u4e3a\u751f\uff0c\u542c\u5230\u6709\u5982\u6b64\u8c6a\u5ba2\uff0c\u8c01\u4e0d\u60f3\u5df4\u7ed3\u3002 \u3000\u3000\u8fd9\u4e9b\u5973\u5b50\u4e00\u4e0a\u697c\u6765\uff0c\u697c\u4e0a\u81ea\u7136\u53c8\u662f\u756a\u666f\u8c61\uff0c\u6709\u7684\u8fd8\u4e0d\u8fc7\u4ec5\u4ec5\u659f\u9152\u731c\u62f3\uff0c\u6253\u60c5\u9a82\u4fcf\uff0c \u6709\u7684\u672c\u662f\u76f8\u597d\uff0c\u7adf\u5c31\u62c9\u6765\u5750\u5230\u819d\u4e0a\uff0c\u516c\u7136\u8c03\u7b11\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u867d\u7136\u505a\u51fa\u4e00\u5e45\u8001\u7ec3\u7684\u6837\u5b50\uff0c\u4f46\u4ed6\u867d\u7136\u751f\u6027\u4e0d\u7f81\uff0c\u5230\u5e95\u662f\u7b2c\u4e00\u6b21\u9047\u89c1\u8fd9\u79cd\u573a\u5408\uff0c \u5fc3\u91cc\u4e5f\u5fae\u5fae\u6709\u4e9b\u4f5c\u614c\uff0c\u5f3a\u81ea\u9547\u5b9a\u7740\u3002 \u3000\u3000\u7fa4\u8c6a\u4e00\u770b\u8f9b\u6377\u4ecd\u7136\u5728\u72ec\u81ea\u5750\u7740\uff0c\u91d1\u5f13\u795e\u5f39\u4fbf\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u4eec\u53ea\u987e\u81ea\u5df1\u73a9\u4e50\uff0c\u5374\u628a\u4e3b \u4eba\u51b7\u843d\u4e86\uff0c\u771f\u662f\u8be5\u7f5a\uff0c\u771f\u662f\u8be5\u7f5a\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u8bf8\u4f4d\u81ea\u7ba1\u5c3d\u6b22\uff0c\u5c0f\u5f1f\u521d\u5230\u57ce\u6b64\uff0c\u8fd8\u751f\u758f\u5f97\u5f88\u5462\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u7c89\u5934\u4e00\u542c\u4e4b\u4e0b\uff0c\u624d\u77e5\u9053\u6b64\u4eba\u5c31\u662f\u6325\u91d1\u5982\u571f\u7684\u9614\u5c11\uff0c\u518d\u52a0\u4e0a\u8f9b\u6377\u82f1\u59ff\u633a\u79c0\uff0c\u59d0\u513f \u7231\u949e\uff0c\u4e5f\u7231\u4fcf\uff0c\u5a9a\u76ee\u90fd\u98de\u5230\u8f9b\u6377\u8eab\u4e0a\u3002 \u3000\u3000\u51e4\u6797\u73ed\u7684\u7a1a\u51e4\uff0c\u662f\u6b66\u6c49\u9547\u6570\u4e00\u6570\u4e8c\u7684\u7ea2\u500c\u4eba\uff0c\u5979\u7ad9\u4e86\u8d77\u6765\uff0c\u4fcf\u751f\u751f\u5730\u8d70\u5230\u8f9b\u6377\u8eab\u65c1\uff0c \u6328\u5728\u8f9b\u6377\u8eab\u4e0a\uff0c\u5a07\u7b11\u9053\uff1a\u201c\u6696\uff0c\u4f60\u5bb6\u8d35\u59d3\u5440\uff0c\u600e\u4e48\u4ece\u6765\u6ca1\u6709\u5230\u6211\u4eec\u90a3\u513f\u53bb\u5750\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u5979\u7684\u4e00\u53ea\u7ea4\u7ea4\u7389\u624b\uff0c\u5c31\u642d\u5230\u8f9b\u6377\u80a9\u4e0a\uff0c\u8f9b\u6377\u53ea\u89c9\u5f97\u4e00\u9635\u751c\u817b\u7684\u9999\u6c14\uff0c\u76f4\u51b2\u4eba \u9f3b\u5b54\uff0c\u5fc3\u91cc\u4e5f\u7830\u7136\u52a0\u901f\u4e86\u8df3\u52a8\u3002 \u3000\u3000\u7a1a\u51e4\u7684\u6625\u8471\u822c\u7684\u624b\u6307\uff0c\u6709\u610f\u65e0\u610f\u5730\u64a9\u7740\u8f9b\u6377\u7684\u8033\u6735\uff0c\u89c1\u8f9b\u6377\u4e0d\u8bf4\u8bdd\uff0c\u7c89\u8138\u5c31\u50f5\u5230\u4ed6 \u8033\u65c1\uff0c\u4fcf\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u8bdd\u5440\u3002\u201d \u3000\u3000\u8f9b\u6377\u5bf9\u8fd9\u4e9b\u5eb8\u4fd7\u8102\u7c89\uff0c\u5fc3\u4e2d\u867d\u89c9\u5f97\u6709\u4e9b\u538c\u70e6\uff0c\u4f46\u4ed6\u5929\u6027\u672c\u5c31\u501c\u50a5\u4e0d\u7f81\uff0c\u518d\u52a0\u4e0a\u4ed6\u5341 \u5e74\u6765\u90fd\u53d7\u7740\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u7684\u85b0\u9676\uff0c\u89c9\u5f97\u9664\u4e86\u662f\u771f\u6b63\u6709\u5173\u9053\u5fb7\u3001\u4ec1\u4e49\u7684\u4e8b\u4ee5\u5916\uff0c\u5176\u4f59\u5374 \u53ef\u968f\u610f\u884c\u4e4b\u3002 \u3000\u3000\u4f55\u51b5\u4ed6\u77e5\u9053\uff0c\u4ed6\u65e2\u4ee5\u7ae0\u53f0\u8d70\u9a6c\u7684\u738b\u5b59\u516c\u5b50\u8eab\u4efd\u51fa\u73b0\uff0c\u65e5\u540e\u8fd9\u79cd\u573a\u5408\u8fd8\u591a\u7684\u662f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u7b11\u7740\u63e1\u8d77\u7a1a\u98ce\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4ee5\u540e\u6211\u53ef\u8981\u53bb\u8d70\u8d70\u4e86\uff0c\u201d \u3000\u3000\u7a1a\u51e4\u54af\u54af\u4e00\u9635\u5a07\u7b11\uff0c\u7d22\u6027\u4e5f\u5750\u5230\u8f9b\u6377\u8eab\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u662f\u9a97\u6211\u7684\u3002\u201d \u3000\u3000\u94f6\u67aa\u5b5f\u4f2f\u8d77\u8eab\u7ad9\u4e86\u8d77\u6765\uff0c\u7b11\u6307\u7740\u4e8c\u4eba\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u770b\uff0c\u7a1a\u51e4\u8fd9\u5c0f\u59ae\u5b50\uff0c\u6709\u4e86\u77e5\u60c5\u8bc6 \u8da3\u7684\u8f9b\u516c\u5b50\uff0c\u5c31\u628a\u6211\u4eec\u8fd9\u4e9b\u8001\u7c97\u4e22\u5f00\u4e86\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u53c8\u662f\u4e00\u9635\u5927\u7b11\uff0c\u91d1\u5f13\u795e\u5f39\u8bf4\u9053\uff1a\u201c\u8fd9\u4e5f\u8be5\u7f5a\uff0c\u7f5a\u8fd9\u5c0f\u59ae\u5b50\u5531\u4e00\u6bb5\u7ed9\u6211\u4eec\u542c\u542c\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u53c8\u54c4\u7136\u5e94\u597d\u3002 \u3000\u3000\u7a1a\u51e4\u64a4\u5a07\u7740\u4e0d\u4f9d\u9053\uff1a\u201c\u8303\u7237\u6700\u574f\u4e86\uff0c\u4eba\u5bb6\u4e0d\u4f1a\u5531\uff0c\u5531\u4ec0\u4e48\u5440\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e5f\u7b11\u7740\u7eb5\u607f\uff0c\u7a1a\u98ce\u4ef0\u5934\u5411\u8f9b\u6377\u4fcf\u8bf4\u9053\uff1a\u201c\u6211\u53ea\u5531\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u8bf4\u7740\u5979\u7ad9\u4e86\u8d77\u6765\uff0c\u4ecd\u7136\u4f9d\u5728\u8f9b\u6377\u8eab\u65c1\uff0c\u7ea4\u624b\u4e00\u62e2\u53d1\u89d2\uff0c\u6b4c\u9053\uff1a\u201c\u5e76\u5200\u5982\u6c34\uff0c\u5434\u76d1\u80dc \u96ea\uff0c\u7ea4\u624b\u7834\u65b0\u6a59\uff0c\u9526\u63e1\u521d\u6e29\uff0c\u517d\u9999\u4e0d\u65ad\uff0c\u76f8\u5bf9\u5750\u8c03\u7b19\u3002\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u7528\u624b\u6307\u9a9a\u7740\u8f9b\u6377\u7684\u80cc\uff0c\u8f9b\u6377\u4e00\u62ac\u5934\uff0c\u6b63\u89c1\u5979\u4f4e\u5934\u5ae3\u7136\u671b\u7740\u81ea\u5df1\uff0c\u6b4c\u9053\uff1a \u3000\u3000\u201c\u4f4e\u58f0\u95ee\uff1a\u2018\u5411\u8c01\u884c\u5bbf\uff1f\u57ce\u4e0a\u5df1\u4e09\u66f4\uff0c\u9a6c\u6ed1\u971c\u6d53\uff0c\u4e0d\u5982\u4f11\u53bb\u76f4\u662f\u5c11\u4eba\u884c\u2019\u3002 \u3000\u3000\u5979\u5c06\u8fd9\u9996\u5b8b\u671d\u8bcd\u5bb6\u5468\u90a6\u5f66\u7684\u201c\u5c11\u5e74\u6e38\u201d\u5531\u5f97\u5a13\u5a13\u52a8\u542c\uff0c\u800c\u4e14\u5a07\u58f0\u5a49\u8f6c\uff0c\u773c\u6ce2\u6697\u8bed\uff0c \u4f1a\u610f\u4eba\u5f53\u77e5\u5176\u4e2d\u53c8\u522b\u6709\u6240\u5bc4\u3002 \u3000\u3000\u7fa4\u8c6a\u53c8\u54c4\u7136\u53eb\u7740\u597d\uff0c\u94f6\u67aa\u5b5f\u4f2f\u8d77\u5374\u662f\u4e2a\u6587\u6b66\u53cc\u5168\u7684\u4eba\u7269\uff0c\u82b1\u4e1a\u4e2d\u4e5f\u53ef\u79f0\u5f97\u4e0a\u662f\u540d\u624b\uff0c \u6b64\u523b\u7b11\u7740\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u770b\uff0c\u8f9b\u516c\u5b50\u624d\u6765\u4e00\u5929\uff0c\u5df2\u7ecf\u6709\u4f73\u4eba\u7559\u5bbf\u4e86\uff0c\u770b\u6837\u5b50\u4eca\u591c\u8f9b\u516c\u5b50\u662f \u6ce8\u5b9a\u8981\u7559\u5728\u6e29\u67d4\u4e61\u4e86\u3002\u201d \u3000\u3000\u7a1a\u51e4\u53c8\u662f\u4e00\u9635\u5a07\u7b11\uff0c\u4e0d\u80dc\u5a07\u7f9e\u5730\u4e00\u5934\u94bb\u8fdb\u8f9b\u6377\u6000\u91cc\uff0c\u8f9b\u6377\u5fc3\u4e2d\u53c8\u731b\u5730\u4e00\u8df3\u3002 \u3000\u3000\u6625\u4e0a\u9152\u697c\uff0c\u65f6\u95f4\u5728\u6b22\u4e50\u4e2d\u98de\u5feb\u7684\u6d41\u8fc7\u53bb\uff0c\u9152\u5728\u6dfb\u7740\uff0c\u83dc\u4e5f\u5728\u6dfb\u7740\u3002 \u3000\u3000\u4f46\u662f\u7ec8\u4e8e\u5230\u4e86\u8be5\u6563\u914c\u65f6\u5019\u4e86\u3002 \u3000\u3000\u90a3\u4e9b\u8eab\u4efd\u8f83\u4f4e\uff0c\u540d\u5934\u8f83\u5f31\u7684\uff0c\u4fbf\u5148\u8d70\u4e86\uff0c\u8d8a\u8d70\u8d8a\u591a\uff0c\u90a3\u4e9b\u73ed\u5b50\u91cc\u7684\u7c89\u5934\uff0c\u4e5f\u5927\u591a\u5728 \u8d26\u623f\u5904\u9886\u4e86\u94f6\u5b50\u8d70\u4e86\u3002 \u3000\u3000\u5230\u540e\u6765\u9152\u697c\u4e0a\u53ea\u5269\u4e0b\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\u3001\u94f6\u67aa\u5b5f\u4f2f\u8d77\u3001\u548c\u5730\u7edd\u5251\u624b\u4e8e\u4e00\u98de\u3001\u8f9b\u6377\uff0c\u4ee5 \u53ca\u51e4\u6797\u73ed\u7684\u7a1a\u51e4\u3001\u7f8e\u6797\u3001\u7fe0\u559c\u73ed\u7684\u7389\u51e4\u3001\u5c0f\u7fe0\u51e0\u4e2a\u4eba\u3002 \u3000\u3000\u7a1a\u51e4\u4e00\u76f4\u817b\u5728\u8f9b\u6377\u8eab\u4e0a\uff0c\u91d1\u5f13\u795e\u5f39\u7b11\u8bf4\uff1a\u201c\u6211\u4eec\u4e5f\u8be5\u8d70\u4e86\uff0c\u8ba9\u8f9b\u5144\u9759\u9759\u5730\u5230\u7a1a\u51e4\u90a3 \u91cc\u53bb\u804a\u804a\uff0c\u514d\u5f97\u7a1a\u51e4\u8fd9\u5c0f\u59ae\u5b50\u602a\u6211\u4eec\u4e0d\u77e5\u8da3\u3002\u201d \u3000\u3000\u8bf4\u7740\u5c31\u7ad9\u4e86\u8d77\u6765\uff0c\u62c9\u7740\u94f6\u67aa\u5b5f\u4f2f\u8d77\u8981\u8d70\uff0c\u7fe0\u559c\u3001\u7389\u51e4\u4e5f\u5728\u6253\u8da3\u7740\u3002 \u3000\u3000\u8f9b\u6377\u8fd9\u624d\u771f\u7684\u614c\u4e86\uff0c\u5fd9\u9053\uff1a\u201c\u4e8e\u5927\u4fa0\u5343\u4e07\u4e0d\u80fd\u8d70\uff0c\u4eca\u591c\u4e00\u9f50\u5230\u5c0f\u5f1f\u4f4f\u5904\u53bb\uff0c\u4f60\u6211\u4e00 \u89c1\u5982\u6545\uff0c\u5c0f\u5f1f\u8981\u548c\u5144\u53f0\u4f5c\u4e2a\u957f\u591c\u4e4b\u996e\u3002\u201d \u3000\u3000\u7a1a\u51e4\u54ac\u7740\u5634\u62e7\u4e86\u8f9b\u6377\u4e00\u628a\uff0c\u4e8e\u4e00\u98de\u89c1\u4e86\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5012\u60f3\u53bb\uff0c\u53ea\u6015\u4eba\u5bb6\u7a1a \u51e4\u59d1\u5a18\u4e0d\u7b54\u5e94\uff0c\u54c8\u54c8\u3002\u201d \u3000\u3000\u8f9b\u6377\u81ea\u6000\u4e2d\u638f\u51fa\u51e0\u9897\u6676\u83b9\u7684\u73cd\u73e0\uff0c\u90a3\u90fd\u662f\u4e9b\u4ef7\u503c\u4e0d\u83f2\u7684\u73cd\u7269\uff0c\u4ed6\u9012\u4e86\u7f8e\u6797\u3001\u7fe0\u559c\u3001 \u7389\u51e4\u6bcf\u4eba\u4e00\u7c92\uff0c\u5979\u4eec\u90fd\u9ad8\u5174\u5730\u8c22\u4e86\u63a5\u8fc7\u3002 \u3000\u3000\u4ed6\u53c8\u5c06\u5269\u4e0b\u7684\u51e0\u7c92\uff0c\u4e00\u80a1\u513f\u585e\u5728\u7a1a\u51e4\u624b\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u4f60\u5148\u8d70\u5427\uff0c\u8fc7\u4e24\u5929\u6211\u518d\u5230 \u4f60\u90a3\u91cc\u53bb\uff0c\u4f60\u653e\u5fc3\uff0c\u6211\u4e00\u5b9a\u4f1a\u53bb\u7684\u3002\u201d \u3000\u3000\u7a1a\u51e4\u90a3\u66fe\u89c1\u8fc7\u8fd9\u6837\u7684\u8c6a\u5ba2\uff0c\u6e29\u67d4\u5730\u51d1\u5230\u8f9b\u6377\u8eab\u65c1\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u7b49\u4f60\u3002\u201d\u4e8e\u662f\u5979 \u5a40\u5a1c\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u62db\u547c\u7740\u7f8e\u6797\u3001\u7389\u51e4\u4e00\u9f50\u8d70\u4e86\uff0c\u8d70\u5230\u68af\u53e3\uff0c\u5979\u8fd8\u56de\u5934\u5411\u8f9b\u6377\u5ae3\u7136\u4e00\u987e\uff0c \u8f9b\u6377\u6697\u7b11\u9053\uff1a\u201c\u6885\u53d4\u53d4\u672c\u8bf4\u4ed6\u7684\u2018\u4e03\u827a\u2019\u6211\u53ea\u5b66\u5f97\u4e86\u5176\u516d\uff0c\u53ef\u662f\u4ed6\u60f3\u4e0d\u5230\u6211\u5374\u5b66\u5168\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53c8\u671b\u4e86\u91d1\u5f13\u795e\u5f39\u3001\u94f6\u67aa\u5b5f\u4f2f\u8d77\u548c\u4e8e\u4e00\u98de\u4e00\u773c\uff0c\u5fd6\u9053\uff1a\u201c\u4eca\u665a\u6211\u7684\u6536\u83b7\uff0c\u5012\u7684\u786e\u4e0d \u5c11\uff0c\u6885\u53d4\u53d4\u82e5\u662f\u77e5\u9053\u4e86\uff0c\u4e5f\u5fc5\u7136\u9ad8\u5174\u5f97\u5f88\u3002\u201d \u3000\u3000\u94f6\u67aa\u5b5f\u4f2f\u8d77\u9053\uff1a\u201c\u4eca\u5929\u80fd\u53d8\u5f97\u8f9b\u5144\u8fd9\u6837\u7684\u670b\u53cb\uff0c\u6211\u5b9e\u5728\u9ad8\u5174\u5f97\u5f88\uff0c\u65e5\u540e\u8f9b\u5144\u5982\u957f\u4f4f \u6b64\u5730\uff0c\u5c0f\u5f1f\u5fc5\u5b9a\u8981\u5e38\u53bb\u62dc\u8bbf\u7684\u3002\u201d \u3000\u3000\u91d1\u5f13\u795e\u5f39\u4e5f\u5fd9\u7740\u9053\uff1a\u201c\u90a3\u662f\u5f53\u7136\uff0c\u5c31\u662f\u8f9b\u5144\u4e0d\u8bf7\uff0c\u5c0f\u5f1f\u4e5f\u8981\u539a\u7740\u8138\u76ae\u53bb\u7684\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u4eca\u65e5\u672a\u7adf\u4e4b\u6b22\uff0c\u8fc7\u4e24\u5929\u5c0f\u5f1f\u4e00\u5b9a\u8981\u518d\u8bf7\u4e24\u4f4d\u5c3d\u4e4b\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u5ba2\u6c14\u5730\u5c06\u4ed6\u4eec\u4e24\u4eba\u9001\u5230\u697c\u4e0b\uff0c\u56de\u987e\u4e8e\u4e00\u98de\u9053\uff1a\u201c\u4e8e\u5144\u5982\u65b9\u4fbf\uff0c\u5c31\u8bf7\u5230\u5c0f\u5f1f\u5904 \u53bb\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u9053\uff1a\u201c\u5c0f\u5f1f\u672c\u662f\u7ecf\u8fc7\u6b64\u95f4\uff0c\u5230\u6b66\u5f53\u5c71\u53bb\u4e3a\u5bb6\u5e08\u7d22\u56de\u4e00\u7269\uff0c\u4eca\u665a\u4fbf\u8981\u8d70\u7684\uff0c\u54ea \u77e5\u5374\u7ed3\u4ea4\u5230\u8f9b\u5144\u8fd9\u6837\u7684\u670b\u53cb\u3002\u201d \u3000\u3000\u4ed6\u53cc\u7709\u4e00\u76b1\uff0c\u8138\u4e0a\u9732\u51fa\u8083\u6740\u4e4b\u6c14\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u4f55\u51b5\u5c0f\u5f1f\u4e09\u65e5\u540e\u8fd8\u6709\u4e9b\u672a\u4e86\u4e4b\u4e8b\u3002\u8bf4 \u4e0d\u5f97\u53ea\u597d\u6253\u6270\u8f9b\u5144\u4e09\u3001\u4e94\u5929\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fd9\u9053\uff1a\u201c\u4e8e\u5144\u5982\u80af\u7559\u4e0b\uff0c\u5c0f\u5f1f\u5b9e\u5728\u9ad8\u5174\u5f97\u5f88\uff0c\u8fd9\u4e09\u5929\u6211\u5b9a\u8981\u597d\u597d\u5730\u966a\u4e8e\u5144\u5c3d\u5c3d \u6b22\u3002\u201d\u4ed6\u53c8\u53f9\u53e3\u6c14\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u4e09\u65e5\u4e4b\u540e\uff0c\u4e8e\u5144\u53ef\u8981\u5343\u4e07\u5c0f\u5fc3\uff0c\u90a3\u59d3\u674e\u7684\u5fc5\u662f\u9080\u96c6 \u5e2e\u624b\u53bb\u4e86\u3002\u5509\uff0c\u5c0f\u5f1f\u5b9e\u662f\u65e0\u80fd\uff0c\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u4e0d\u80fd\u52a9\u4e8e\u5144\u4e00\u81c2\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u72c2\u7b11\u4e00\u58f0\uff0c\u62cd\u7740\u8f9b\u6377\u7684\u80a9\u9053\uff1a\u201c\u8f9b\u5144\u53ea\u7ba1\u653e\u5fc3\uff0c\u5c0f\u5f1f\u5b9e\u8fd8\u672a\u5c06\u90a3\u4e9b\u4eba\u653e\u5728\u5fc3 \u4e0a\u3002\u201d\u8bed\u6c14\u4e4b\u95f4\uff0c\u6709\u7740\u592a\u591a\u7684\u81ea\u4fe1\u3002 \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u6211\u4eff\u4f5b\u542c\u8bf4\u2018\u6b66\u5f53\u2019\u2018\u5d06\u5cd2\u2019\u672c\u4e3a\u8fde\u624b\uff0c\u4e8e\u5144\u6b64\u4e3e\uff0c\u662f\u5426\u2026\u2026\u201d \u3000\u3000\u4e8e\u4e00\u98de\u9f3b\u5b54\u91cc\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u82e5\u975e\u4e3a\u4e86\u2018\u6b66\u5f53\u6d3e\u2019\u5341\u4f59\u5e74\u524d\u548c\u5bb6\u5e08\u7684\u4e00\u70b9 \u4ea4\u60c5\uff0c\u4eca\u591c\u600e\u4f1a\u8ba9\u90a3\u59d3\u674e\u7684\u4ece\u5bb9\u8d70\u53bb\u3002\u201d \u3000\u3000\u4ed6\u53c8\u9053\uff1a\u201c\u8f9b\u5144\u6709\u6240\u4e0d\u77e5\uff0c\u90a3\u2018\u6b66\u5f53\u2019\u6297\u7740\u2018\u6b66\u6797\u7b2c\u4e00\u5927\u5b97\u6d3e\u2019\u7684\u62db\u724c\uff0c\u72c2\u5984\u81ea\u5927 \u7684\u4e0d\u5f97\u4e86\uff0c\u5176\u5b9e\u6b66\u5f53\u95e8\u5f92\uff0c\u5374\u90fd\u662f\u4e9b\u9152\u56ca\u996d\u888b\uff0c\u5bb6\u5e08\u672c\u544a\u8beb\u6211\u7b49\uff0c\u5728\u4eca\u5e74\u79cb\u5929\u6cf0\u5c71\u7edd\u9876 \u7684\u5251\u4f1a\u4ee5\u524d\uff0c\u4e0d\u8981\u548c\u6b66\u5f53\u95e8\u4eba\u7ed3\u6028\uff0c\u4f46\u4eca\u65e5\u8fd9\u6837\u4e00\u6765\uff0c\u5c0f\u5f1f\u5374\u8981\u5148\u6740\u6740\u4ed6\u4eec\u7684\u9a84\u6c14\uff0c\u5373 \u4f7f\u5bb6\u5e08\u602a\u7f6a\uff0c\u4e5f\u8bf4\u4e0d\u5f97\u4e86\uff0c \u3000\u3000\u8f9b\u6377\u95ee\u9053\uff1a\u201c\u90a3\u6cf0\u5c71\u7edd\u9876\u7684\u5251\u4f1a\uff0c\u53ef\u5c31\u662f\u4ee5\u4e94\u5927\u5b97\u6d3e\u4e3a\u9996\uff0c\u67ec\u9080\u6b66\u6797\u4e2d\u4eba\u5230\u6cf0\u5c71\u7edd \u9876\u4e00\u8f83\u6b66\u529f\uff0c\u4e89\u90a3\u5929\u4e0b\u7b2c\u4e00\u5251\u7684\u540d\u5934\uff0c\u82e5\u662f\u8fd9\u6837\uff0c\u5012\u4e0d\u4e89\u4e5f\u7f62\uff0c\u8bd5\u60f3\u5f53\u4eca\u5929\u4e0b\uff0c\u8fd8\u6709\u80fd \u80dc\u8fc7\u4ee4\u5e08\u7684\u4eba\u5417\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u5f97\u610f\u5730\u7b11\u9053\uff1a\u201c\u90a3\u4e2a\u81ea\u7136\uff0c\u6cf0\u5c71\u4e4b\u4f1a\uff0c\u5341\u5e74\u4e00\u671f\uff0c\u5341\u5e74\u524d\u5bb6\u5e08\u4ee5\u638c\u4e2d\u4e4b\u5251\uff0c \u6280\u538b\u7fa4\u96c4\uff0c\u53d6\u5f97\u2018\u5929\u4e0b\u7b2c\u4e00\u5251\u2019\u7684\u540d\u53f7\uff0c\u8fde\u5ce8\u5d4b\u7684\u82e6\u5eb5\u4e0a\u4eba\u548c\u4ee5\u5185\u5bb6\u5251\u6cd5\u81ea\u9e23\u7684\u6b66\u5f53\u638c \u6559\u8d64\u9633\u9053\u957f\u7b49\u4eba\uff0c\u90fd\u7518\u62dc\u4e0b\u98ce\uff0c\u53ea\u662f\u8fd9\u6cf0\u5c71\u4e4b\u4f1a\u5374\u7acb\u4e0b\u4e00\u6761\u89c4\u7ea6\uff0c\u90a3\u5c31\u662f\u4e0a\u4e00\u6b21\u4e0e\u4f1a\u6bd4 \u8bd5\u4e4b\u4eba\uff0c\u4e0b\u4e00\u6b21\u5c31\u4e0d\u5f97\u53c2\u52a0\u3002\u201d \u3000\u3000\u4ed6\u53cc\u7709\u4e00\u8f69\uff0c\u610f\u6c14\u98de\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u4ee5\u8fd9\u6b21\u6cf0\u5c71\u4e4b\u4f1a\uff0c\u5c31\u662f\u6211\u7b49\u4e00\u8f88\u7684\u5929\u4e0b\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u6697\u54fc\u4e00\u58f0\uff0c\u53e3\u4e2d\u5374\u5949\u627f\u7740\u8bf4\uff1a\u201c\u5d06\u5cd2\u4e09\u7edd\u5251\uff0c\u540d\u6ee1\u6b66\u6797\uff0c\u770b\u6765\u2018\u5929\u4e0b\u7b2c\u4e00\u5251\u2019 \u7684\u540d\u53f7\uff0c\u53c8\u975e\u4f60\u4eec\u5d06\u5cd2\u83ab\u5c5e\u4e86\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u54c8\u54c8\u4e00\u7b11\uff0c\u50cf\u662f\u5bf9\u8f9b\u6377\u7684\u8bdd\u9ed8\u8ba4\u4e86\uff0c\u8f9b\u6377\u80f8\u4e2d\u53c8\u6697\u54fc\u4e86\u4e00\u4e0b\uff0c\u76ee\u4e2d\u6d41\u51fa\u5f02\u6837 \u7684\u5149\u5f69\u3002 \u3000\u3000\u4f46\u662f\u4e8e\u4e00\u98de\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u8fd9\u4e9b\uff0c\u4ed6\u968f\u7740\u8f9b\u6377\u4e0a\u4e86\u8f66\u5b50\uff0c\u5174\u9ad8\u91c7\u70c8\u5730\u8d70\u4e86\uff0c\u50cf\u662f\u4ed6\u5df2 \u624b\u6301\u7740\u5251\uff0c\u7ad9\u5728\u6cf0\u5c71\u9876\u4e0a\uff0c\u88ab\u6b66\u6797\u79f0\u4e3a\u2018\u5929\u4e0b\u7b2c\u4e00\u5251\u2019\u7684\u6837\u5b50\u3002 \u3000\u3000\u8f66\u4e2d\u4e24\u4eba\uff0c\u5fc3\u4e2d\u5404\u6709\u5fc3\u4e8b\uff0c\u662f\u4ee5\u53ea\u6709\u8f66\u58f0\u78f7\u78f7\uff0c\u4e24\u4eba\u90fd\u672a\u8bf4\u8bdd\u3002 \u3000\u3000\u5ffd\u7136\u8f66\u9876\u4e0a\uff0c\u6251\u5730\u4e00\u58f0\u5927\u9707\uff0c\u4f3c\u4e4e\u6709\u4e2a\u5f88\u91cd\u7684\u4e1c\u897f\uff0c\u843d\u5728\u8f66\u9876\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u3001\u4e8e\u4e00\u98de\u4e24\u4eba\u7686\u81ea\u4e00\u60ca\u3002 \u3000\u3000\u53c8\u542c\u5f97\u90a3\u8f66\u9876\u4e0a\u6709\u4e00\u4e2a\u5a07\u5ae9\u7684\u5c11\u5973\u53e3\u97f3\uff0c\u5598\u7740\u6c14\u8bf4\u9053\uff1a\u201c\u5feb\u8d70\uff0c\u5feb\u8d70\uff0c\u4e0d\u8bb8\u505c\u4e0b \u6765\u3002\u201d \u3000\u3000\u63a5\u7740\u9a6c\u8f66\u4fbf\u52a0\u5feb\u4e86\u901f\u5ea6\u5411\u524d\u5954\u53bb\uff0c\u4f3c\u4e4e\u662f\u56e0\u4e3a\u9a6c\u8f66\u592b\u53d7\u4e86\u8fd9\u4e2a\u5c11\u5973\u7684\u5a01\u80c1\uff0c\u800c\u4e0d\u5f97 \u4e0d\u7b56\u9a6c\u72c2\u5954\uff0c\u663e\u7136\u90a3\u5c11\u5973\u624b\u4e2d\u5fc5\u6709\u5229\u5203\u3002 \u3000\u3000\u8f66\u4e2d\u4e24\u4eba\uff0c\u4ff1\u662f\u6b66\u6797\u4e2d\u4e00\u7b49\u4e00\u7684\u89d2\u8272\uff0c\u8f9b\u6377\u4f2a\u88c5\u4e0d\u61c2\u6b66\u6280\uff0c\u6b64\u523b\u53ea\u4e0d\u8fc7\u76b1\u4e86\u76b1\u7709\uff0c \u5fc3\u4e2d\u6697\u81ea\u5947\u602a\u7740\u8fd9\u4e8b\uff0c\u4ed6\u60f3\uff1a\u201c\u8fd9\u96be\u9053\u662f\u62e6\u8def\u6253\u52a8\u7684\u5417\uff0c\u4f46\u4ece\u8fd9\u5973\u5b50\u843d\u5230\u8f66\u9876\u4e0a\u914c\u8eab\u6cd5 \u542c\u6765\uff0c\u8f7b\u529f\u4e0d\u8fc7\u5e73\u5e73\uff0c\u800c\u4e14\u5598\u6c14\u4e4b\u58f0\u9887\u6025\uff0c\u53c8\u50cf\u662f\u5728\u88ab\u4eba\u8ffd\u8d76\u7740\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u5374\u4e00\u62c9\u8f9b\u6377\u7684\u8863\u89d2\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u8fd9\u5973\u5b50\u597d\u751f\u4e0d\u5f00\u773c\uff0c\u5c45\u7136\u5728\u6211\u7b49\u6240 \u4e58\u7684\u8f66\u4e0a\uff0c\u5f04\u8d77\u624b\u811a\u6765\u4e86\uff0c\u4eca\u591c\u53cd\u6b63\u65e0\u4e8b\uff0c\u5c0f\u5f1f\u5c31\u62ff\u6b64\u5973\u5f00\u4e2a\u73a9\u7b11\uff0c\u4ee5\u535a\u8f9b\u5144\u4e00\u4e50\uff0c\u4e5f \u85c9\u6b64\u60e9\u6212\u60e9\u6212\u5979\u3002\u201d \u3000\u3000\u4ed6\u8bdd\u8bf4\u5b8c\uff0c\u4e00\u652f\u8f66\u53a2\u540e\u7684\u7a97\u5b50\uff0c\u5fae\u4e00\u7528\u529b\uff0c\u8eab\u8eaf\u4fbf\u50cf\u4e00\u6761\u6e38\u9c7c\uff0c\u81ea\u5ea7\u4e2d\u6ed1\u51fa\u7a97\u5916\uff0c \u8eab\u624b\u7684\u654f\u6377\uff0c\u7684\u786e\u65e0\u6127\u5728\u6b66\u6797\u4e2d\u4eab\u6709\u76db\u540d\u3002 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c \u4e8c \u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c \u4e8c \u7ae0 \u3000\u3000\u8f9b\u6377\u968f\u542c\u90a3\u8f66\u4e0a\u5c11\u5973\u4e00\u58f0\u60ca\u53eb\uff0c\u53f1\u9053\uff1a\u201c\u4f60\u8fd9\u6076\u2026\u2026\u201d\u4f46\u5979\u5c1a\u672a\u8bf4\u5b8c\uff0c\u4fbf\u7a81\u7136\u987f\u4f4f\uff0c \u8f9b\u6377\u77e5\u9053\u5979\u5df2\u88ab\u4e8e\u4e00\u98de\u5236\u4f4f\u3002 \u3000\u3000\u679c\u7136\uff0c\u8f66\u7a97\u5916\u4e8e\u4e00\u98de\u558a\u9053\uff1a\u201c\u8f9b\u5144\u63a5\u7740\u3002\u201d\u8f9b\u6377\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u4e8e\u4e00\u98de\u5df2\u5c06\u4e00\u4eba\u81ea\u7a97 \u5916\u629b\u5165\uff0c\u8f9b\u6377\u4e0b\u610f\u8bc6\u5730\u4e00\u4f38\u624b\uff0c\u8f7b\u6613\u5730\u5c06\u5979\u63a5\u7740\uff0c\u4f46\u53c8\u5ffd\u7136\u60f3\u8d77\u81ea\u5df1\u4f2a\u88c5\u7684\u8eab\u4efd\uff0c\u5468\u8eab \u529b\u9053\u731b\u61c8\uff0c\u968f\u7740\u90a3\u629b\u6765\u4e4b\u52bf\uff0c\u4e24\u4eba\u4e00\u9f50\u8dcc\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u7acb\u65f6\u611f\u89c9\u5230\u538b\u5728\u4ed6\u8eab\u4e0a\u7684\u662f\u4e00\u4e2a\u6781\u67d4\u8f6f\u800c\u6e29\u6696\u7684\u8eab\u8eaf\uff0c\u800c\u4e14\u521a\u597d\u4e0e\u4ed6\u9762\u5bf9\u7740\u9762\uff0c \u5a07\u5598\u4f9d\u4f9d\uff0c\u90fd\u5410\u5728\u4ed6\u8138\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u8138\u4e0a\u4e00\u70ed\uff0c\u4ed6\u77e5\u9053\u8fd9\u5c11\u5973\u5fc5\u5b9a\u5df1\u88ab\u4e8e\u4e00\u98de\u70b9\u4f4f\u7a74\u9053\uff0c\u4f46\u90a3\u5c11\u5973\u795e\u667a\u4ecd\u6e05\uff0c\u4e00\u770b \u81ea\u5df1\u7684\u8138\u6b63\u8d34\u5728\u4e00\u4e2a\u7537\u5b50\u7684\u8138\u4e0a\uff0c\u800c\u4e14\u58f0\u606f\u4e92\u95fb\uff0c\u4f46\u5979\u53c8\u82e6\u4e8e\u4e1d\u6beb\u4e0d\u80fd\u52a8\u8f6c\uff0c\u7f9e\u5f97\u53ea\u597d \u5c06\u773c\u95ed\u4e0a\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u81ea\u540e\u7a97\u8f7b\u5de7\u5730\u7ffb\u4e86\u8fdb\u6765\uff0c\u770b\u89c1\u4e24\u4eba\u6b63\u5377\u4f0f\u5728\u8f66\u53a2\u5185\u4e00\u5757\u5e76\u4e0d\u751a\u5927\u7684\u5730\u65b9\u4e0a\uff0c \u54c8\u54c8\u4e00\u7b11\uff0c\u8f7b\u4f38\u733f\u81c2\uff0c\u5c06\u90a3\u5c11\u5973\u6284\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8f9b\u6377\u8fd9\u65f6\u624d\u6323\u624e\u7740\u722c\u8d77\u6765\uff0c\u5598\u7740\u6c14\uff0c\u57cb\u6028\u5730\u8bf4\u9053\uff1a\u201c\u4e8e\u5144\u53c8\u975e\u4e0d\u77e5\uff0c\u5c0f\u5f1f\u600e\u63a5\u5f97 \u4f4f\u3002\u201d \u3000\u3000\u4ed6\u4e00\u773c\u671b\u89c1\u90a3\u5c11\u5973\u5df1\u88ab\u4e8e\u4e00\u98de\u653e\u5728\u5ea7\u4e0a\uff0c\u4e8e\u4e00\u98de\u7b11\u9053\uff1a\u201c\u8f9b\u5144\u5e94\u5f53\u611f\u8c22\u5c0f\u5f1f\u624d\u662f\uff0c \u5c06\u8fd9\u6837\u4e00\u4e2a\u7f8e\u4eba\uff0c\u9001\u5230\u9601\u4e0b\u6000\u91cc\uff0c\u600e\u5730\u5374\u57cb\u6028\u8d77\u5c0f\u5f1f\u6765\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u89c1\u90a3\u5c11\u5973\u867d\u7136\u53d1\u9b03\u96f6\u4e71\uff0c\u8863\u7740\u4e0d\u6574\uff0c\u4f46\u7684\u786e\u662f\u4e2a\u7f8e\u4eba\u80da\u5b50\uff0c\u5979\u6b64\u523b\u4ecd\u95ed\u7740\u773c\u6674\uff0c \u957f\u957f\u7684\u776b\u6bdb\u8986\u76d6\u5728\u773c\u5e18\u4e0a\uff0c\u4e30\u6ee1\u7684\u80f8\u819b\u6025\u5267\u5730\u8d77\u4f0f\u7740\uff0c\u8f9b\u6377\u60f3\u8d77\u65b9\u624d\u7684\u60c5\u666f\uff0c\u8138\u4e0a\u53c8\u662f \u4e00\u70ed\u3002 \u3000\u3000\u4ed6\u5fd9\u81ea\u6e05\u4e86\u6e05\u5589\u5499\uff0c\u63a9\u9970\u7740\u81ea\u5df1\u7a98\u6001\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u600e\u5730\u6df1\u591c\u8df3\u5230\u6211\u7b49\u7684\u8f66\u9876 \u4e0a\u6765\uff0c\u8bf7\u59d1\u5a18\u8bf4\u4e2a\u6e05\u695a\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u542c\u4e86\uff0c\u7a81\u5730\u7741\u5f00\u773c\u6674\uff0c\u4e24\u9053\u9ed1\u767d\u5206\u660e\uff0c\u79cb\u6c34\u4e3a\u795e\u7684\u773c\u5149\uff0c\u5728\u8f9b\u6377\u548c\u4e8e\u4e00\u98de\u8138 \u4e0a\u4e00\u626b\uff0c\u4f3c\u4e4e\u53d1\u89c9\u5e76\u4e0d\u662f\u81ea\u5df1\u6240\u60f3\u50cf\u7684\u4eba\uff0c\u5fc3\u60c5\u4e00\u677e\uff0c\u8138\u4e0a\u6cdb\u8d77\u4e00\u4e1d\u5bbd\u6170\u7684\u7b11\u610f\uff0c\u5f20\u53e3 \u60f3\u8bf4\u8bdd\uff0c\u4f46\u5979\u77ac\u5373\u53d1\u89c9\u81ea\u5df1\u9664\u4e86\u773c\u76ae\u53ef\u4ee5\u5f00\u5408\u4e4b\u5916\uff0c\u5468\u8eab\u8fde\u8bf4\u8bdd\u7684\u529b\u6c14\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u770b\u4e8e\u4e00\u98de\u6240\u7528\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u867d\u5c06\u4eba\u5236\u4f4f\uff0c\u4f46\u5374\u5e76\u4e0d\u4f24\u4eba\uff0c\u4e0d\u7981\u6697\u81ea\u5bf9\u4e8e\u4e00\u98de \u7565\u6709\u597d\u611f\uff0c\u89c9\u5f97\u4ed6\u505a\u4e8b\u5c1a\u6709\u5206\u5bf8\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u4e00\u7b11\uff0c\u4f38\u624b\u6781\u5feb\u5730\u5728\u90a3\u5c11\u5973\u80c1\u4e0b\uff0c\u80cc\u810a\u4e0a\u4e00\u62cd\uff0c\u90a3\u5c11\u5973\u6c89\u91cd\u7684\u900f\u4e86\u4e00\u53e3\u6c14\uff0c \u62ac\u4e86\u62ac\u624b\uff0c\u8eab\u8eaf\u7adf\u80fd\u52a8\u8f6c\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u8f66\u884c\u5df2\u7f13\uff0c\u5916\u9762\u8857\u9053\u6781\u4e3a\u9759\u5bc2\uff0c\u5e97\u94fa\uff0c\u4eba\u5bb6\u90fd\u4e5f\u7184\u4e86\u706f\u7761\u89c9\u4e86\u3002 \u3000\u3000\u7a81\u7136\u4e00\u4e2a\u7c97\u54d1\u5589\u5499\u7684\u58f0\u97f3\u558a\u9053\uff1a\u201c\u5e76\u80a9\u5b50\uff0c\u4e0a\u5440\uff0c\u96cf\u513f\u5165\u4e86\u6d3b\u7a91\u4e86\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u5251\u7709\u53c8\u662f\u4e00\u8f69\uff0c\u90a3\u5c11\u5973\u5374\u6251\u5730\u8dea\u5728\u5730\u4e0a\uff0c\u54c0\u6c42\u7740\u8bf4\u9053\uff1a\u201c\u4e24\u4f4d\u5343\u4e07\u8981\u6551\u6551\u6211\uff0c \u8fd9\u4e9b\u90fd\u4e0d\u662f\u597d\u4eba\uff0c\u4ed6\u4eec\u8981\u2026\u2026\u201d \u3000\u3000\u5979\u8138\u4e0a\u4e00\u7ea2\uff0c\u8bdd\u53c8\u8bf4\u4e0d\u4e0b\u53bb\u4e86\uff0c\u4f46\u8f9b\u6377\u548c\u4e8e\u4e00\u98de\u90fd\u5df2\u4e86\u89e3\u4e86\u5979\u8bdd\u4e2d\u7684\u610f\u601d\uff0c\u4e8e\u4e00\u98de \u5012\u5e95\u662f\u6b66\u6797\u6b63\u5b97\uff0c\u4e00\u542c\u4e0d\u7531\u5927\u6012\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u822c\u5bb6\u4f19\u4e5f\u592a\u53ef\u6076\u4e86\uff0c\u5c45\u7136\u5728\u8fd9\u57ce\u91cc\u5c31\u6492\u91ce \u901e\u51f6\u3002\u201d\u4ed6\u8f6c\u5934\u5411\u90a3\u5c11\u5973\u95ee\u9053\uff1a\u201c\u4ed6\u4eec\u662f\u8c01\uff0c\u4f60\u53ef\u8ba4\u8bc6\u4ed6\u4eec\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u521a\u6447\u4e86\u6447\u5934\uff0c\u8f66\u5916\u8857\u9053\u4e0a\u53c8\u201c\u6251\u6251\u201d\u51e0\u58f0\uff0c\u50cf\u662f\u6709\u51e0\u4e2a\u4eba\u4ece\u623f\u4e0a\u8df3\u4e86\u4e0b\u6765\uff0c \u9a6c\u8f66\u592b\u4e5f\u662f\u4e00\u58f0\u60ca\u547c\uff0c\u63a5\u7740\u5148\u524d\u90a3\u7c97\u54d1\u5589\u5499\u7684\u58f0\u97f3\u5728\u559d\u53f1\u9053\uff1a\u201c\u5582\uff0c\u8fd9\u8f86\u8f66\u5b50\u5feb\u7ed9\u6211\u505c \u4e0b\u3002\u201d \u3000\u3000\u8f9b\u6377\u81ea\u5df1\u867d\u4e0d\u80fd\u52a8\u624b\uff0c\u4f46\u4ed6\u5374\u77e5\u9053\u51ed\u4e8e\u4e00\u98de\u7684\u8eab\u624b\uff0c\u8981\u5bf9\u4ed8\u8fd9\u7c7b\u4f3c\u65e0\u8d56\u7684\u5f3a\u76d7\uff0c\u7b80 \u76f4\u592a\u5bb9\u6613\u4e86\uff0c\u56e0\u6b64\u4ed6\u9759\u9759\u5730\u5750\u7740\uff0c\u8981\u770b\u4e8e\u4e00\u98de\u600e\u4e48\u5e94\u4ed8\u6b64\u4e8b\uff0c\u4e5f\u60f3\u770b\u770b\u4e8e\u4e00\u98de\u5728\u5251\u6cd5\u4e0a \u5012\u5e95\u6709\u4f55\u9020\u8be3\u3002 \u3000\u3000\u8f66\u5b50\u505c\u4e86\uff0c\u90a3\u5c11\u5973\u60ca\u60f6\u5730\u7f29\u5728\u8f66\u53a2\u7684\u89d2\u843d\u91cc\uff0c\u4e24\u773c\u6050\u60e7\u5730\u671b\u7740\u5916\u9762\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u63a2\u9996\u5916\u671b\uff0c\u770b\u89c1\u8f66\u524d\u7ad9\u7740\u6709\u4e03\u3001\u516b\u4e2a\u624b\u91cc\u62ff\u7740\u660e\u6643\u6643\u5c16\u5200\u7684\u6c49\u5b50\u3002 \u3000\u3000\u5176\u4e2d\u4e00\u4e2a\u821e\u52a8\u7740\u624b\u91cc\u7684\u5200\u8bf4\u9053\uff1a\u201c\u5582\uff01\u8f66\u91cc\u7684\u4eba\u542c\u7740\uff0c\u6211\u4eec\u662f\u957f\u6c5f\u4e0b\u6e38\u6c34\u8def\u603b\u74e2\u628a \u5b50\u5c0f\u795e\u9f99\u8d3a\u4fe1\u96c4\u7684\u5f1f\u5144\uff0c\u4eca\u65e5\u8def\u8fc7\u6b64\u5730\uff0c\u5e76\u4e0d\u60f3\u6253\u6270\u826f\u6c11\uff0c\u53ea\u662f\u521a\u624d\u6709\u4e00\u4e2a\u81ea\u6211\u4eec\u8239\u4e0a \u9003\u4e0b\u7684\u5973\u5b50\uff0c\u8df3\u8fdb\u4f60\u4eec\u8f66\u91cc\uff0c\u4f60\u4eec\u5feb\u5c06\u5979\u653e\u4e0b\u6765\uff0c\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u54fc\u4e00\u58f0\uff0c\u63a8\u5f00\u8f66\u95e8\uff0c\u50b2\u7136\u8d70\u4e86\u4e0b\u53bb\uff0c\u53f1\u9053\uff1a\u201c\u4ec0\u4e48\u5973\u5b50\u4e0d\u5973\u5b50\u7684\uff0c\u8fd9\u8f66\u4e0a\u6ca1 \u6709\uff0c\u5c31\u662f\u6709\uff0c\u4e5f\u4e0d\u80fd\u4ea4\u7ed9\u4f60\u4eec\u3002\u201d \u3000\u3000\u90a3\u4e9b\u6c49\u5b50\u770b\u89c1\u4e8e\u4e00\u98de\u8eab\u540e\u80cc\u7740\u5251\uff0c\u8bf4\u8bdd\u53c8\u6ee1\u4e0d\u5728\u5e73\uff0c\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u6765\u8def\u3002 \u3000\u3000\u90a3\u5148\u524d\u53d1\u8bdd\u7684\u6c49\u5b50\uff0c\u597d\u50cf\u662f\u5176\u4e2d\u7684\u5934\u5b50\uff0c\u6b64\u523b\u8d70\u4e86\u4e0a\u6765\uff0c\u4e00\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u76f8\u597d\u7684 \u770b\u6837\u5b50\u4e5f\u662f\u7ebf\u4e0a\u7684\u670b\u53cb\uff0c\u8bf7\u62a5\u4e2a\u4e07\u513f\u6765\uff0c\u5356\u54b1\u4eec\u4e00\u4e2a\u4ea4\u60c5\uff0c\u65e5\u540e\u6211\u4eec\u8d3a\u5f53\u5bb6\u4e00\u5b9a\u6709\u8865\u62a5 \u4e4b\u5904\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u4e00\u62ac\u773c\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u4ea4\u60c5\u4e0d\u4ea4\u60c5\uff0c\u5927\u7237\u5168\u4e0d\u61c2\u8fd9\u4e00\u5957\uff0c\u4f60\u4eec\u82e5\u662f\u8bc6\u8da3 \u7684\u5feb\u5939\u7740\u5c3e\u5df4\u6eda\u86cb\uff0c\u4e0d\u7136\u4f60\u4eec\u60f3\u8d70\u5374\u4e5f\u8d70\u4e0d\u4e86\u5566\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u6ee1\u4ee5\u4e3a\u81ea\u5df1\u8bb2\u7684\u8bdd\u6709\u677f\u6709\u773c\uff0c\u90a3\u77e5\u4eba\u5bb6\u5168\u4e0d\u5356\u6005\uff0c\u800c\u4e14\u770b\u6837\u5b50\u7b80\u76f4\u6ca1\u628a\u81ea\u5df1 \u8fd9\u73ed\u4eba\u770b\u5728\u773c\u91cc\uff0c\u6c14\u5f97\u54c7\u54c7\u53eb\u9053\uff1a\u201c\u76f8\u597d\u7684\uff0c\u4f60\u6562\u60c5\u60f3\u627e\u6b7b\u5440\u3002\u201d\u8bf4\u7740\u8bdd\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c \u4e86\u4e0a\u6765\uff0c\u5200\u5149\u4e00\u95ea\uff0c\u201c\u529b\u5288\u534e\u5c71\u201d\u5288\u5411\u4e8e\u4e00\u98de\u5934\u4e0a\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u4e0d\u907f\u4e0d\u95ea\uff0c\u770b\u89c1\u5200\u5149\u5df2\u5728\u5934\u4e0a\uff0c\u53f3\u624b\u4e00\u4f38\uff0c\u7528\u98df\u3001\u4e2d\u4e8c\u6307\u7adf\u631f\u4f4f\u76f4\u5f80\u4e0b\u5288\u7684 \u5927\u5200\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u53f1\u9053\uff1a\u201c\u8eba\u4e0b\u3002\u201d \u3000\u3000\u90a3\u6c49\u5b50\u679c\u7136\u542c\u8bdd\uff0c\u968f\u7740\u4e8e\u4e00\u98de\u6325\u624b\u4e4b\u52bf\uff0c\u8fdc\u8fdc\u8dcc\u5012\u5730\u4e0a\u3002\u8f66\u91cc\u7684\u8f9b\u6377\uff0c\u89c1\u90a3\u6c49\u5b50\u5982 \u6b64\u8113\u5305\uff0c\u4e0d\u89c9\u6709\u4e9b\u5931\u671b\uff0c\u4ed6\u539f\u60f3\u85c9\u6b64\u770b\u770b\u4e8e\u4e00\u98de\u7684\u6b66\u529f\uff0c\u90a3\u77e5\u4e8e\u4e00\u98de\u4e00\u4e3e\u624b\uff0c\u5df1\u89e3\u51b3\u4e86 \u4e00\u4e2a\u3002\u5176\u4f59\u7684\u90a3\u4e9b\u6c49\u5b50\uff0c\u7acb\u65f6\u4e00\u9635\u7eb7\u4e71\uff0c\u4f46\u4ed6\u4eec\u4e0d\u8fc7\u53ea\u503c\u5f97\u4e09\u62db\u4e24\u5f0f\uff0c\u82e5\u8bba\u6b66\u529f\uff0c\u7b80\u76f4 \u8c08\u4e5f\u8c08\u4e0d\u4e0a\uff0c\u4e0d\u8fc7\u53ea\u662f\u4ed7\u7740\u4eba\u591a\uff0c\u6253\u7740\u70c2\u4ed7\u800c\u5df2\uff0c\u78b0\u5230\u4e8e\u4e00\u98de\u8fd9\u79cd\u8eab\u6000\u7edd\u6280\u7684\u5185\u5bb6\u9ad8\u624b\uff0c \u6b63\u662f\u4ed6\u4eec\u5408\u8be5\u5012\u9709\uff0c\u4e03\u3001\u516b\u4e2a\u4eba\u4e3e\u7740\u5200\u4e0a\u6765\uff0c\u8fd8\u6ca1\u6709\u5f04\u6e05\u695a\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5df2\u88ab\u8dcc\u5f97\u4e03\u6655 \u516b\u7d20\uff0c\u8fde\u4e8e\u4e00\u98de\u7684\u8863\u8882\u90fd\u6ca1\u6709\u78b0\u5230\u3002 \u3000\u3000\u90a3\u6700\u5148\u8dcc\u5728\u5730\u4e0a\u7684\u6c49\u5b50\uff0c\u5df2\u722c\u4e86\u8d77\u6765\uff0c\u5ffd\u7136\u9ad8\u5174\u5730\u53eb\u9053\uff1a\u201c\u597d\u4e86\uff0c\u597d\u4e86\uff0c\u4e8c\u5f53\u5bb6\u7684 \u6765\u4e86\uff0c\u5e76\u80a9\u5b50\u4f4f\u624b\u5427\uff0c\u770b\u8fd9\u5c0f\u5b50\u8fd8\u53d1\u4e0d\u53d1\u6a2a\u3002\u201d \u3000\u3000\u90a3\u4e9b\u6c49\u5b50\u679c\u7136\u9f50\u90fd\u4f4f\u4e86\u624b\uff0c\u4e00\u4e2a\u8eab\u6750\u9880\u957f\uff0c\u6ee1\u8eab\u767d\u8863\u7684\u6c49\u5b50\u5982\u98de\u5954\u4e86\u6765\uff0c\u4e00\u770b\u81ea\u5df1 \u7684\u5f1f\u5144\u6709\u7684\u8dcc\u5012\u5728\u5730\u4e0a\uff0c\u6709\u7684\u5782\u5934\u4e27\u6c14\u7684\u62ff\u7740\u5200\u7ad9\u5728\u8eab\u65c1\uff0c\u518d\u770b\u5230\u8f66\u65c1\u7a33\u5982\u5c71\u5cb3\u7ad9\u7740\u7684 \u4e8e\u4e00\u98de\uff0c\u5fc3\u4e2d\u5df2\u660e\u767d\u4e86\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u53cc\u7709\u4e00\u76b1\uff0c\u8d70\u4e86\u4e0a\u6765\uff0c\u671d\u4e8e\u4e00\u98de\u8bf4\u9053\uff1a\u201c\u8fd9\u4f4d\u670b \u53cb\u8bf7\u4e86\uff0c\u5728\u4e0b\u7b49\u4e0e\u670b\u53cb\u4e95\u6c34\u4e0d\u72af\u6cb3\u6c34\uff0c\u83ab\u975e\u670b\u53cb\u548c\u90a3\u5c0f\u59d0\u513f\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u786c\u8981\u6765\u67b6\u8fd9\u6a2a \u6881\uff0c\u8fd9\u4e5f\u597d\u8bf4\uff0c\u670b\u53cb\u53ea\u8981\u62a5\u4e0a\u4e2a\u4e07\u513f\uff0c\u82e5\u771f\u662f\u6210\u540d\u9732\u8138\u4eba\u7269\uff0c\u6211\u6c5f\u91cc\u767d\u9f99\u9a6c\u4e0a\u62cd\u624b\u5e7f\u8d70\uff0c \u8fd9\u5c0f\u59d0\u5c31\u7b97\u662f\u670b\u53cb\u4f60\u7684\u3002\u201d \u3000\u3000\u4e8e\u5341\u98de\u4e00\u542c\u6c5f\u91cc\u767d\u9f99\u7684\u540d\u5934\uff0c\u4fbf\u77e5\u9053\u6b64\u4eba\u4e5f\u662f\u4e2a\u89d2\u8272\uff0c\u53ea\u56e0\u957f\u6c5f\u4e00\u5e26\uff0c\u6c34\u8def\u7eff\u6797\u867d \u662f\u5949\u5c0f\u9f99\u795e\u8d3a\u96c4\u4e3a\u603b\u74e2\u628a\u5b50\uff0c\u4f46\u5e2e\u91cc\u5927\u5927\u5c0f\u5c0f\u7684\u4e8b\uff0c\u5374\u662f\u5168\u7531\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\u4f5c\u4e3b\u3002 \u3000\u3000\u8fd9\u6c5f\u91cc\u767d\u9f99\u4e0d\u4f46\u6c34\u4e0a\u3001\u9646\u4e0a\u7684\u529f\u592b\u90fd\u6709\u4e24\u4e0b\uff0c\u800c\u4e14\u4e3a\u4eba\u5bb9\u667a\u767e\u51fa\uff0c\u5728\u957f\u6c5f\u4e00\u5e26\uff0c\u58f0 \u540d\u9887\u54cd\uff0c\u5730\u7edd\u5251\u8d70\u52a8\u6c5f\u6e56\uff0c\u4e5f\u66fe\u542c\u5230\u8fc7\u4ed6\u7684\u540d\u5934\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u89c1\u6c5f\u91cc\u767d\u9f99\u8eab\u6750\u9880\u957f\uff0c\u53cc\u76ee\u70af\u7136\uff0c\u5012\u4e5f\u50cf\u662f\u4e2a\u4eba\u7269\uff0c\u4fbf\u8bf4\u9053\uff1a\u201c\u5176\u5b9e\u8fd9\u5c0f\u59d0 \u513f\u548c\u4e8e\u67d0\u4eba\u4e5f\u6ca1\u6709\u5e72\u7cfb\uff0c\u53ea\u662f\u6211\u4e8e\u67d0\u4eba\u5374\u770b\u4e0d\u60ef\u522b\u4eba\u6b3a\u51cc\u5f31\u5973\uff0c\u60f3\u5b59\u5f53\u5bb6\u7684\u4e5f\u662f\u6210\u540d\u9732 \u8138\u7684\u597d\u6c49\uff0c\u4f55\u82e6\u7d27\u7d27\u8feb\u7740\u4e00\u4e2a\u5973\u5b50\uff0c\u5c31\u770b\u5728\u6211\u4e8e\u4e00\u98de\u7684\u9762\u4e0a\uff0c\u9976\u4e86\u5979\u5427\u3002\u201d \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u5e76\u4e0d\u662f\u4ec0\u4e48\u771f\u6b63\u4ed7\u4e49\u9504\u5f3a\u7684\u4eba\u7269\uff0c\u521a\u624d\u6fc0\u4e8e\u4e00\u65f6\u4e49\u6c14\uff0c\u5305\u63fd\u4e0b\u6b64\u4e8b\uff0c \u540e\u6765\uff0c\u53c8\u540e\u6094\u81ea\u5df1\u591a\u7ba1\u95f2\u4e8b\uff0c\u4f55\u82e6\u5e73\u7a7a\u7ed3\u4e0b\u8fd9\u7b49\u5f3a\u4ec7\uff0c\u6b64\u523b\u4ed6\u8bf4\u51fa\u6b64\u8bdd\uff0c\u4fbf\u60f3\u6c5f\u91cc\u767d\u9f99 \u80fd\u5356\u81ea\u5df1\u4e00\u4e2a\u9762\u5b50\uff0c\u5c06\u6b64\u4e8b\u626f\u8fc7\u53bb\u5c31\u7b97\u4e86\uff0c\u514d\u5f97\u518d\u591a\u60f9\u4e8b\u751f\u975e\u3002 \u3000\u3000\u90a3\u6c5f\u91cc\u767d\u9f99\u60ca\u54e6\u4e86\u4e00\u58f0\uff0c\u4e0a\u4e0b\u6253\u91cf\u7740\u4e8e\u4e00\u98de\u51e0\u773c\uff0c\u8bf4\u9053\uff1a\u201c"\u539f\u6765\u9601\u4e0b\u5c31\u662f\u2018\u5d06\u5cd2 \u4e09\u7edd\u5251\u2019\u91cc\u7684\u5730\u7edd\u5251\u4e8e\u4e8c\u7237\uff0c\u5176\u5b9e\u51ed\u7740\u4f60\u4e8c\u7237\u4e00\u53e5\u8bdd\uff0c\u653e\u8d70\u8fd9\u5c0f\u59d0\u513f\u6709\u4ec0\u4e48\u53ef\u8bf4\u7684\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u4e00\u4e50\uff0c\u5fc3\u60f3\u8fd9\u6c5f\u91cc\u767d\u9f99\u679c\u7136\u8bc6\u5f97\u51fa\u597d\u6b79\uff0c\u90a3\u77e5\u5b59\u8d85\u8fdc\u53c8\u63a5\u7740\u8bf4\uff1a\u201c\u53ea\u662f\u8fd9\u5c0f \u59d0\u513f\u5374\u4e5f\u4e0d\u662f\u5f0a\u5e2e\u91cc\u7684\u8d27\u89d2\uff0c\u800c\u662f\u53e6\u5916\u4e00\u4eba\u6258\u655d\u5e2e\u4fdd\u7ba1\u7684\uff0c\u655d\u5e2e\u59d4\u5b9e\u62db\u60f9\u6b64\u4eba\u4e0d\u8d77\uff0c\u8bf4 \u8d77\u6765\uff0c\u4e8e\u4e8c\u7237\u4e5f\u8bb8\u5bf9\u6b64\u4eba\u4e5f\u662f\u6709\u4e2a\u8ba4\u8bc6\uff0c\u4e5f\u4f1a\u5356\u4ed6\u4e00\u4e2a\u4ea4\u60c5\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u5fd9\u95ee\u9053\uff1a\u201c\u6b64\u4eba\u662f\u8c01\uff1f\u201d \u3000\u3000\u5b59\u8fdc\u8d85\u795e\u79d8\u5730\u4e00\u7b11\uff0c\u5de6\u638c\u5411\u7a7a\u4e2d\u865a\u6309\u4e86\u4e00\u4e0b\uff0c\u53f3\u624b\u62c7\u6307\u4e00\u4ef6\uff0c\u505a\u4e86\u4e2a\u624b\u52bf\uff0c\u8bf4\u9053\uff1a \u201c\u5c31\u662f\u4ed6\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u89c1\u4e86\u8fd9\u4e2a\u624b\u5f0f\uff0c\u9762\u8272\u4e00\u53d8\uff0c\u6c89\u541f\u4e86\u534a\u54cd\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5c0f\u59d0\u513f\u65e2\u662f\u6b64\u4eba\u6240\u4ea4\u6258 \u7684\uff0c\u5f53\u7136\u65e0\u8bdd\u53ef\u8bf4\u3002\u201d\u4ed6\u4e00\u6307\u8f66\u5185\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\uff01\u8fd9\u5c0f\u59d0\u513f\u5c31\u5728\u8f66\u5185\uff0c\u5b59\u5f53\u5bb6\u7684\u81ea\u5df1\u52a8 \u624b\u597d\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u5728\u8f66\u5185\u4e00\u542c\uff0c\u66f4\u662f\u4e00\u60ca\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u540d\u5934\u9887\u5927\uff0c\u6b66\u529f\u4e0d\u5f31\uff0c\u800c \u4e14\u53c8\u6709\u9760\u5c71\uff0c\u4ed7\u7740\u5251\u795e\u5389\u9e57\uff0c\u72c2\u50b2\u5f97\u4e0d\u5f97\u4e86\uff0c\u4f55\u4ee5\u770b\u4e86\u8fd9\u4e2a\u624b\u5f0f\uff0c\u5c31\u4e56\u4e56\u5730\u4e0d\u518d\u8bf4\u8bdd\uff0c \u90a3\u624b\u5f0f\u6240\u4ee3\u8868\u7684\u4eba\u7269\uff0c\u5c82\u975e\u4e0d\u53ef\u601d\u8bae\u4e86\uff0c\u4f46\u5374\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u89c1\u4e8e\u4e00\u98de\u4ece\u5bb9\u5730\u5c31\u5c06\u90a3\u4e9b\u6c49\u5b50\u51fb\u8d25\uff0c\u6b63\u9ad8\u5174\u7740\u81ea\u5df1\u5df2\u5f97\u6551\u4e86\uff0c\u90a3\u77e5\u4e8b\u60c5\u5374\u53d8 \u5f97\u5982\u6b64\uff0c\u5979\u54c0\u6028\u5730\u770b\u4e86\u8f9b\u6377\u4e00\u773c\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u5f97\u5979\u7684\u773c\u5149\u50cf\u662f\u76f4\u523a\u4eba\u81ea\u5df1\u5fc3\u91cc\uff0c\u51e0\u4e4e\u9a6c\u4e0a\u5c31\u8981\u4e0d\u987e\u4e00\u5207\u633a\u8eab\u800c\u51fa\u6765\u76f8\u52a9\uff0c \u4f46\u4ed6\u8f6c\u5ff5\u53c8\u60f3\u8d77\u81ea\u5df1\u6240\u8d1f\u7684\u4f7f\u547d\uff0c\u548c\u81ea\u5df1\u5bf9\u5c06\u6765\u7684\u62b1\u8d1f\uff0c\u4e00\u79cd\u66f4\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u4f7f\u4ed6\u538b\u5236 \u4e86\u6b64\u523b\u7684\u6fc0\u52a8\u3002 \u3000\u3000\u8f6c\u773c\uff0c\u90a3\u6c5f\u91cc\u767d\u9f99\u5df1\u8d70\u5230\u8f66\u65c1\uff0c\u4f38\u8fdb\u5934\u6765\u7b11\u563b\u563b\u5bf9\u90a3\u5c11\u5973\u8bf4\u9053\uff1a\u201c\u65b9\u59d1\u5a18\uff0c\u6211\u770b\u4f60 \u8fd8\u662f\u4e56\u4e56\u5730\u8ddf\u7740\u6211\u8d70\u5427\uff01\u9003\u6709\u4ec0\u4e48\u7528\u5462\uff1f\u51ed\u4f60\u8eab\u4e0a\u8fd9\u70b9\u513f\u672c\u4e8b\uff0c\u8fd8\u60f3\u9003\u5230\u54ea\u91cc\u53bb\u5417\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u5c06\u8eab\u4f53\u66f4\u7f29\u5728\u89d2\u843d\u91cc\uff0c\u5168\u8eab\u8737\u505a\u4e00\u56e2\uff0c\u8f9b\u6377\u770b\u4e86\uff0c\u5fc3\u91cc\u96be\u53d7\u5f97\u5f88\uff0c\u60f3\u4e86\u60f3\uff0c \u7a81\u7136\u8bf4\u9053\uff1a\u201c\u4f60\u5feb\u70b9\u8ddf\u4eba\u5bb6\u53bb\u5427\uff01\u4e0d\u7136\u90a3\u5c11\u5973\u89c1\u8f9b\u6377\u4e00\u53d1\u8bdd\uff0c\u72e0\u72e0\u5730\u76ef\u4e86\u4ed6\u4e00\u773c\uff0c\u8fd9\u4e00 \u773c\u5305\u542b\u7740\u90a3\u4e48\u6028\u6068\uff0c\u4f7f\u5f97\u8f9b\u6377\u5fc3\u4e2d\u53c8\u662f\u4e00\u52a8\uff0c\u4e0d\u5f97\u4e0d\u6781\u529b\u5730\u538b\u5236\u7740\u81ea\u5df1\u60c5\u611f\u3002 \u3000\u3000\u6c5f\u91cc\u767d\u9f99\u4e00\u4f38\u624b\uff0c\u62c9\u7740\u5979\u7684\u81c2\u8180\u5c31\u5f80\u5916\u62d6\uff0c\u90a3\u5c11\u5973\u4e00\u7529\u624b\uff0c\u5f3a\u5fcd\u7740\uff0c\u6068\u58f0\u8bf4\uff1a\u201c\u8d70 \u5c31\u8d70\uff0c\u4f60\u518d\u62d6\u59d1\u5a18\u53ef\u8981\u9a82\u4f60\u4e86\u3002\u201d \u3000\u3000\u5979\u7a81\u7136\u4e00\u633a\u8170\uff0c\u7ad9\u5230\u5730\u4e0a\uff0c\u8d70\u51fa\u4e86\u8f66\u53a2\uff0c\u518d\u4e5f\u4e0d\u671b\u8f9b\u6377\u4e00\u773c\u3002 \u3000\u3000\u6c5f\u91cc\u767d\u9f99\u5fae\u4e00\u793a\u610f\uff0c\u5c31\u6709\u4e24\u4e2a\u7c97\u957f\u5927\u6c49\u4e00\u8fb9\u67b6\u4f4f\u5c11\u5973\u7684\u53cc\u624b\uff0c\u90a3\u5c11\u5973\u867d\u60f3\u6323\u624e\uff0c\u4f46 \u5979\u90a3\u91cc\u6709\u90a3\u4e24\u4e2a\u5927\u6c49\u7684\u86ee\u529b\u3002 \u3000\u3000\u5b59\u8d85\u8fdc\u968f\u5411\u4e8e\u4e00\u98de\u62b1\u62f3\uff0c\u8bf4\u9053\uff1a\u201c\u4e8e\u5927\u4fa0\u4eca\u5929\u9ad8\u62ac\u8d35\u624b\uff0c\u4e0d\u4f46\u6211\u5b59\u67d0\u4eba\u611f\u6fc0\u4e0d\u5c3d\uff0c \u5c31\u662f\u6211\u4eec\u8d3a\u5f53\u5bb6\u7684\u548c\u90a3\u4f4d\u4e3b\u513f\uff0c\u82e5\u662f\u77e5\u9053\uff0c\u4e5f\u5fc5\u6709\u8865\u62a5\u4e8e\u5927\u4fa0\u4e4b\u5904\uff0c\u4eca\u65e5\u5c31\u6b64\u522b\u8fc7\u3002\u201d \u8bf4\u7740\u4fbf\u626c\u957f\u53bb\u4e86\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u8baa\u8baa\u5730\u8d70\u4e0a\u8f66\u6765\uff0c\u671d\u8f9b\u6377\u52c9\u5f3a\u7b11\u9053\uff1a\u201c\u4eca\u5929\u6211\u4eec\u771f\u662f\u81ea\u8ba8\u6ca1\u8da3\uff0c\u5509\uff0c\u82e5\u4e0d\u662f \u8fd9\u4e2a\u4e3b\u513f\uff0c\u4e5f\u8fd8\u7f62\u4e86\uff0c\u5374\u53c8\u504f\u504f\u662f\u4ed6\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fd9\u95ee\u9053\uff1a\u201c\u4ed6\u5230\u5e95\u662f\u8c01\u5440\uff1f\u5c0f\u5f1f\u5374\u5982\u95f7\u5728\u9f13\u91cc\u3002\u201d\u4e8e\u4e00\u98de\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a \u201c\u6b66\u6797\u4e2d\u6709\u4e9b\u4e8b\u8f9b\u5144\u662f\u65e0\u6cd5\u660e\u4e86\u7684\uff0c\u6539\u65e5\u6709\u673a\u4f1a\u518d\u8be6\u8c08\u5427\u3002\u201d \u3000\u3000\u8f9b\u6377\u77e5\u4ed6\u4e0d\u613f\u8bf4\u51fa\uff0c\u53cd\u6b63\u81ea\u5df1\u6b64\u65f6\u5df1\u6709\u4e86\u6253\u7b97\uff0c\u9042\u4e5f\u4e0d\u518d\u95ee\u3002 \u3000\u3000\u8f66\u5b50\u5f88\u5feb\u5230\u4e86\u8f9b\u6377\u6240\u8bbe\u7684\u5c71\u6885\u73e0\u5b9d\u5e97\uff0c\u90a3\u662f\u4e00\u95f4\u89c4\u6a21\u6c14\u6d3e\u90fd\u76f8\u5f53\u5927\u7684\u5e97\u94fa\uff0c\u8f66\u592b\u8def \u4e0a\u9047\u5230\u8fd9\u4e9b\u4e8b\uff0c\u6068\u4e0d\u5f97\u9a6c\u4e0a\u7f29\u8fdb\u88ab\u7a9d\u7761\u89c9\uff0c\u6b64\u523b\u4e00\u89c1\u5df2\u56de\u5230\u4e86\u5bb6\uff0c\u8fde\u5fd9\u8df3\u4e0b\u8f66\u53bb\u6572\u8d77\u95e8 \u6765\u3002 \u3000\u3000\u5e97\u91cc\u4e00\u4e2a\u7761\u610f\u6726\u80e7\u7684\u58f0\u8a00\u6ca1\u597d\u6c14\u7684\u95ee\u9053\uff1a\u201c\u662f\u8c01\u5728\u6572\u95e8\u5440\uff1f\u201d\u8f66\u592b\u7b54\u9053\uff1a\u201c\u662f\u8001\u677f \u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u7acb\u523b\u53d8\u5f97\u70ed\u60c5\u800c\u5df4\u7ed3\uff0c\u558a\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765\u4e86\uff0c\u9a6c\u4e0a\u6765\u4e86\u201d \u3000\u3000\u4e8e\u4e00\u98de\u7ecf\u8fc7\u6b64\u4e8b\u540e\uff0c\u4f3c\u4e4e\u4e5f\u89c9\u5f97\u8138\u4e0a\u6302\u4e0d\u4f4f\uff0c\u65e0\u7cbe\u6253\u91c7\u5730\uff0c\u8fdb\u4e86\u5e97\u540e\uff0c\u8f9b\u6377\u4fbf\u62db\u547c \u4ed6\u7761\u4e86\u3002 \u3000\u3000\u591c\u66f4\u6df1\uff0c\u5c71\u6885\u73e0\u5b9d\u5e97\u91cc\uff0c\u7a81\u7136\u6781\u6289\u5730\u95ea\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u5411\u6c5f\u5cb8\u98de\u8eab\u800c\u53bb\u3002 \u3000\u3000\u90a3\u79cd\u8d85\u7edd\u7684\u8f7b\u529f\u529f\u592b\uff0c\u7684\u786e\u662f\u6b66\u6797\u7f55\u89c1\uff0c\u53ea\u662f\u7a0d\u7a0d\u5730\u4e00\u6cbe\u5c4b\u9762\uff0c\u4fbf\u6a2a\u8d8a\u51fa\u5f88\u8fdc\uff0c\u4ee5 \u81f3\u770b\u8d77\u6765\u53ea\u50cf\u4e00\u9053\u70df\u5149\uff0c\u5e76\u4e0d\u80fd\u770b\u51fa\u4ed6\u8eab\u5f62\u7684\u8f6e\u5ed3\u3002 \u3000\u3000\u6643\u773c\uff0c\u90a3\u4eba\u5f71\u4fbf\u5230\u4e86\u6c5f\u8fb9\uff0c\u4f46\u662f\u4ed6\u5374\u4eff\u4f5b\u5e76\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u76ee\u7684\u4e4b\u5904\uff0c\u53ea\u5728\u6c5f\u5cb8\u5904\u6781 \u4f9b\u5730\u98d8\u52a8\u7740\uff0c\u627e\u5bfb\u7740\u4ed6\u7684\u76ee\u6807\u3002 \u3000\u3000\u6b64\u523b\u5cb8\u8fb9\u505c\u6cca\u7684\u8239\u53ea\u4e0a\uff0c\u6ca1\u6709\u4e86\u706f\u5149\uff0c\u53ea\u6709\u6c5f\u5fc3\u51e0\u8258\u6355\u9c7c\u7684\u5c0f\u8247\uff0c\u70b9\u7740\u4e00\u76cf\u87ca\u8424\u706f \u5149\uff0c\u4e00\u95ea\u4e00\u95ea\u5730\u53d1\u51fa\u9eef\u6de1\u7684\u660f\u9ec4\u4e4b\u8272\u3002 \u3000\u3000\u90a3\u4eba\u5f71\u50cf\u662f\u6709\u4e9b\u5931\u671b\uff0c\u505c\u987f\u4e86\u4e00\u4f1a\uff0c\u5ffd\u5730\u63a0\u8d77\u5982\u9e70\uff0c\u98de\u843d\u5728\u4e00\u53ea\u8f83\u5927\u7684\u5546\u8239\u4e0a\uff0c\u6781 \u8f7b\u5de7\u5730\u56db\u5468\u5bdf\u770b\u4e86\u4e00\u904d\u3002 \u3000\u3000\u7136\u540e\uff0c\u4ed6\u53c8\u63a0\u81f3\u7b2c\u4e8c\u8258\uff0c\u7b2c\u4e09\u8258\uff0c\u4f46\u4f3c\u4e4e\u5176\u4e2d\u90fd\u6ca1\u6709\u4ed6\u6240\u8981\u5bfb\u627e\u4e4b\u7269\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u53d1\u73b0\u5728\u79bb\u5cb8\u751a\u8fdc\u7684\u5730\u65b9\uff0c\u5e76\u6392\u6cca\u7740\u4e24\u8258\u5927\u8239\uff0c\u800c\u4e14\u5176\u4e2d\u4e00\u8258\u8239\u4e0a\uff0c\u4ecd\u7136\u70b9 \u7740\u706f\u706b\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u7a97\u91cc\u4e5f\u50cf\u8fd8\u6709\u52a8\u7740\u7684\u4eba\u5f71\u3002 \u3000\u3000\u90a3\u4e24\u8258\u8239\u79bb\u5cb8\u8fd8\u6709\u4e8c\u5341\u4f59\u4e08\u8fdc\u8fd1\uff0c\u5373\u4f7f\u7ad9\u5728\u79bb\u5b83\u6700\u8fd1\u7684\u8239\u4e0a\uff0c\u4e5f\u8fd8\u76f8\u9694\u7740\u5341\u4f59\u4e08\u7684 \u8ddd\u79bb\uff0c\u4ed6\u72b9\u8c6b\u4e86\u4e00\u4f1a\uff0c\u663e\u7136\u8fd9\u8ddd\u79bb\u7684\u786e\u662f\u592a\u8fdc\u4e86\u3002 \u3000\u3000\u6c5f\u4e0a\u7684\u98ce\u5f88\u5927\uff0c\u5439\u5f97\u8239\u4e0a\u6302\u5f97\u706f\u7b3c\uff0c\u5728\u98ce\u4e2d\u6447\u66f3\u7740\uff0c\u90a3\u4eba\u5f71\u4e00\u4f38\u624b\uff0c\u5c06\u90a3\u6302\u7740\u7684\u706f \u7b3c\u62ff\u5728\u624b\u4e2d\uff0c\u7aef\u7965\u4e86\u534a\u523b\u3002 \u3000\u3000\u4ed6\u50cf\u662f\u7a81\u7136\u6709\u4e86\u4e0a\u4e3b\u610f\uff0c\u8f7b\u8f7b\u5730\u98de\u8eab\uff0c\u5c31\u7740\u706f\u7b3c\u4e0a\u7ef3\u5b50\uff0c\u5c06\u90a3\u706f\u7b3c\u5957\u5728\u811a\u4e0a\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u731b\u4e00\u63d0\u6c14\uff0c\u8eab\u5f62\u98d5\u5730\u5f80\u6c5f\u4e2d\u7a9c\u53bb\uff0c\u8fd9\u4e00\u7a9c\u81f3\u5c11\u6709\u4e94\u3001\u516d\u4e08\u8fdc\u8fd1\u3002 \u3000\u3000\u5728\u843d\u6c34\u4e4b\u9645\uff0c\u4ed6\u811a\u4e0a\u6346\u7740\u7684\u706f\u7b3c\uff0c\u5e73\u7740\u6c34\u9762\u4e00\u62cd\uff0c\u4eba\u53c8\u85c9\u52bf\u7a9c\u4e86\u4e09\u3001\u56db\u4e08\uff0c\u53c8\u5728\u7a7a \u4e2d\u4e00\u6362\u6c14\uff0c\u4e00\u4e2a\u66fc\u5999\u8f6c\u4fa7\uff0c\u5c06\u811a\u4e0a\u7684\u706f\u7b3c\u89e3\u5728\u624b\u91cc\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u79bb\u90a3\u4e24\u8258\u8239\u8fd8\u6709\u4e94\u3001\u516d\u4e08\u4e4b\u9065\uff0c\u4f46\u770b\u89c1\u4ed6\u50cf\u662f\u5df2\u5feb\u529b\u7aed\u800c\u843d\u6c34\uff0c\u5ffd\u7136\u5728\u5c06\u843d \u6c34\u672a\u843d\u4e4b\u9645\uff0c\u5728\u6c34\u9762\u4e0a\u5e73\u7740\u8eab\u5b50\u4e00\u63a0\uff0c\u624b\u91cc\u62ff\u7740\u7684\u706f\u7b3c\uff0c\u53c8\u671d\u6c34\u9762\u4e0a\u4e00\u62cd\uff0c\u8eab\u8eaf\u50cf\u4e00\u53ea \u6284\u6c34\u7684\u873b\u8712\uff0c\u6beb\u65e0\u58f0\u606f\u5730\u843d\u5728\u90a3\u4e24\u8258\u8239\u4e0a\uff0c\u50cf\u662f\u6ca1\u6709\u4e00\u4e1d\u91cd\u91cf\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u90fd\u662f\u7f8e\u5999\u800c\u60ca\u4eba\u7684\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u5728\u6697\u5730\u9ad8\u5174\u7740\uff0c\u661f\u5149\u6620\u5f97\u4ed6\u8499\u5728\u4e00\u5757\u4e0a\u9762\u7ee3 \u7740\u6885\u82b1\u7684\u5e15\u5b50\u540e\u7684\u773c\u775b\uff0c\u6d41\u52a8\u7740\u5f97\u610f\u7684\u5149\u8f89\u3002 \u3000\u3000\u4ed6\u6574\u4e86\u6574\u659c\u80cc\u5728\u80cc\u540e\u7684\u4e00\u67c4\u5f62\u5f0f\u9887\u53e4\u7684\u957f\u5251\uff0c\u6563\u63a0\u800c\u81f3\u90a3\u6247\u4ecd\u7136\u4eae\u7740\u706f\u5149\u7684\u7a97\u524d\uff0c \u5c31\u7740\u7a97\u5b50\u7684\u9699\u7f1d\u5411\u91cc\u4e00\u671b\uff0c\u770b\u89c1\u8239\u91cc\u653e\u7740\u4e00\u5f20\u516b\u4ed9\u684c\u5b50\uff0c\u684c\u5b50\u8fb9\u6b63\u6709\u4e24\u4e2a\u6c49\u5b50\u5728\u996e\u7740\u9152\uff0c \u684c\u5b50\u4e0a\u653e\u7740\u51e0\u6837\u83dc\u80b4\uff0c\u4ed6\u8ba4\u5f97\u5176\u4e2d\u4e00\u4eba\u6b63\u662f\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\u3002\u300c \u3000\u3000\u4ed6\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u53e6\u5916\u4e00\u4eba\u60f3\u5fc5\u5c31\u662f\u5c0f\u9f99\u795e\u8d3a\u4fe1\u96c4\u4e86\u3002\u201d \u3000\u3000\u7136\u540e\u4ed6\u6781\u5feb\u5730\u63a0\u81f3\u53e6\u4e00\u7a97\u5b50\uff0c\u7a97\u5185\u867d\u672a\u70b9\u706f\uff0c\u4f46\u85c9\u7740\u90bb\u7a97\u7684\u706f\u706b\uff0c\u4ecd\u7136\u6709\u4e9b\u4eae\u5149\uff0c \u4ed6\u53c8\u4fa7\u76ee\u4e00\u671b\uff0c\u89c1\u91cc\u9762\u679c\u7136\u6709\u4e2a\u4eba\u4fa7\u5367\u5728\u5e8a\u4e0a\uff0c\u6b63\u77aa\u7740\u4e24\u53ea\u5927\u773c\u775b\uff0c\u671b\u7740\u7a97\u677f\uff0c\u4e0d\u77e5\u5728 \u60f3\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u4ed6\u5e73\u7740\u624b\u638c\u653e\u5728\u7a97\u7eb8\u4e0a\uff0c\u4e00\u4f1a\uff0c\u90a3\u7a97\u7eb8\u4f3c\u4e4e\u88ab\u70ed\u529b\u6240\u7194\uff0c\u65e0\u58f0\u65e0\u606f\u7684\u7834\u4e86\u4e00\u5927\u5757\uff0c \u90a3\u5973\u5b50\u4ecd\u672a\u53d1\u89c9\uff0c\u50cf\u662f\u5979\u6240\u60f3\u7684\u662f\u4e2a\u5979\u6781\u5173\u5fc3\u7684\u95ee\u9898\uff0c\u662f\u4ee5\u522b\u7684\u4e8b\u5c31\u5168\u7136\u6ca1\u6709\u6ce8\u610f\u4e86\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u4e0d\u518d\u987e\u8651\u4ed6\u4f1a\u5f04\u51fa\u58f0\u97f3\uff0c\u4f38\u624b\u4e00\u62cd\u7a97\u5b50\uff0c\u90a3\u7a97\u5b50\u4fbf\u88ab\u62cd\u6210\u7c89\u788e\u3002 \u3000\u3000\u63a5\u7740\u4ed6\u95ea\u7535\u822c\u7a9c\u5230\u5e8a\u4e0a\uff0c\u4f38\u624b\u5728\u90a3\u60ca\u60f6\u7684\u5973\u5b50\u8db3\u5fc3\u65c1\u7684\u201c\u6d8c\u6cc9\u7a74\u201d\u4e00\u70b9\uff0c\u5236\u6b62\u4e86\u90a3 \u5973\u5b50\u4e0d\u5fc5\u8981\u7684\u60ca\u547c\u548c\u52a8\u5f39\u3002 \u3000\u3000\u6b64\u65f6\u5916\u9762\u6240\u5750\u7684\u4e24\u4eba\u5df1\u540c\u65f6\u7a9c\u4e86\u8fdb\u53bb\uff0c\u5389\u58f0\u559d\u95ee\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u4ed6\u5374\u6a2a\u624b\u62b1\u7740\u90a3\u5973\u5b50\uff0c\u8eab\u5f62\u5fae\u52a8\uff0c\u7adf\u4ece\u90a3\u4e24\u4eba\u8eab\u4fa7\u7a7f\u4e86\u8fc7\u53bb\uff0c\u5927\u523a\u523a\u5730\u5f80\u684c\u65c1\u7684\u6905 \u5b50\u4e0a\u4e00\u5750\uff0c\u5c06\u90a3\u5973\u5b50\u659c\u659c\u5730\u9760\u5750\u5728\u684c\u65c1\u3002 \u3000\u3000\u90a3\u4e24\u4eba\u679c\u771f\u662f\u957f\u6c5f\u6c34\u8def\u7684\u603b\u6807\u628a\u5b50\u5c0f\u9f99\u795e\u8d3a\u4fe1\u96c4\u548c\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\uff0c\u8bba\u6b66\u529f\u4ea6\u662f\u4e0d \u5f31\uff0c\u4f46\u6b64\u523b\u88ab\u4eba\u81ea\u8eab\u4fa7\u64e6\u4e86\u8fc7\u53bb\uff0c\u4e0d\u7531\u5927\u60ca\u3002 \u3000\u3000\u4e24\u4eba\u731b\u4e00\u56de\u8eab\uff0c\u5374\u89c1\u90a3\u4eba\u5df1\u7aef\u5750\u5728\u524d\u8231\u91cc\uff0c\u4e1d\u6beb\u6ca1\u6709\u9003\u9038\u7684\u6837\u5b50\uff0c\u5fc3\u4e2d\u66f4\u662f\u5947\u602a\uff0c \u5c0f\u9f99\u795e\u8d3a\u4fe1\u96c4\u559d\u9053\uff1a\u201c\u670b\u53cb\u662f\u8c01\uff1f\u6765\u6b64\u4f55\u5e72\uff1f\u201d \u3000\u3000\u90a3\u4eba\u6e05\u8d8a\u5730\u4ef0\u5929\u4e00\u7b11\uff0c\u6307\u7740\u8499\u5728\u8138\u4e0a\u7684\u7ee3\u5e15\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8ba4\u8bc6\u8fd9\u4e2a\u5417\uff1f\u201d \u3000\u3000\u90a3\u7ee3\u5e15\u4e43\u4e00\u6da8\u7c89\u7ee2\uff0c\u4e0a\u9762\u7ee3\u7740\u4e03\u6735\u9c9c\u7ea2\u7684\u6885\u82b1\uff0c\u5c0f\u9f99\u795e\u53ca\u6c5f\u91cc\u767d\u9f99\u884c\u8d70\u6c5f\u6e56\u4ea6\u6709\u5341 \u4f59\u5e74\uff0c\u7a81\u5730\u540c\u65f6\u60f3\u8d77\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u4f46\u6b64\u4eba\u7edd\u8ff9\u6c5f\u6e56\u5df1\u6709\u5341\u5e74\uff0c\u800c\u4e14\u4f20\u95fb\u5df1\u4e27\u5728\u56db\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u624b\u91cc\uff0c\u6b64\u523b\u600e\u4f1a\u53c8\u5728 \u6b64\u51fa\u73b0\uff0c\u5c0f\u9f99\u795e\u4e0d\u7981\u6000\u7591\u9053\uff1a\u201c\u96be\u9053\u4f60\u662f\u2014\uff1f\u201d\u90a3\u4eba\u53c8\u662f\u4e00\u9635\u957f\u7b11\uff0c\u6253\u65ad\u4e86\u5c0f\u9f99\u795e\u7684\u8bdd\uff0c \u63a5\u7740\u6717\u541f\u9053\uff1a\u201c\u6d77\u5185\u5c0a\u4e03\u5999\u3002\u201d \u3000\u3000\u58f0\u72b9\u672a\u4e86\uff0c\u7a81\u81ea\u8eab\u540e\u62bd\u51fa\u957f\u5251\uff0c\u659c\u659c\u4e00\u6296\uff0c\u987f\u65f6\u53ea\u89c9\u5251\u5f71\u91cd\u91cd\uff0c\u5251\u82b1\u70b9\u70b9\uff0c\u6296\u8d77\u4e03 \u4e2a\u6885\u82b1\u822c\u7684\u5708\u5b50\uff0c\u53c8\u7a81\u5730\u6536\u5251\u56de\u8eab\u3002 \u3000\u3000\u4ed6\u62d4\u5251\uff0c\u659c\u524a\uff0c\u51e0\u4e4e\u662f\u5728\u540c\u4e00\u5239\u90a3\u91cc\u5b8c\u6210\uff0c\u662f\u4ee5\u5c0f\u9f99\u795e\u53ca\u6c5f\u91cc\u767d\u9f99\u770b\u8d77\u6765\uff0c\u53ea\u89c9\u5f97 \u4e03\u6735\u95ea\u70c1\u7684\u6885\u82b1\uff0c\u5728\u4ed6\u4eec\u9762\u524d\u4e00\u63a0\uff0c\u7acb\u65f6\u53c8\u65e0\u8e2a\u5f71\uff0c\u6b64\u65f6\u4ed6\u4eec\u5fc3\u4e2d\u54ea\u91cc\u8fd8\u6709\u6000\u7591\u4e4b\u610f\uff0c \u8131\u53e3\u53eb\u9053\uff1a\u201c\u4e03\u5999\u795e\u541b\u201d\uff0c\u987f\u65f6\u5413\u5f97\u534a\u8fb9\u8eab\u5b50\u9669\u4e9b\u8f6f\u4e86\u3002 \u3000\u3000\u6309\u8bf4\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\u4ee5\u53ca\u5c0f\u9f99\u795e\u8d3a\u4fe1\u96c4\uff0c\u4e43\u662f\u957f\u6c5f\u6c34\u8def\u7eff\u6797\u7684\u603b\u74e2\u628a\u5b50\uff0c\u5728\u6b66\u6797\u4e2d \u4ea6\u53ef\u7b97\u5f97\u4e0a\u662f\u58f0\u540d\u8d6b\u8d6b\u7684\u4eba\u7269\uff0c\u600e\u4f1a\u4e00\u542c\u5230\u4e86\u201c\u4e03\u5999\u795e\u541b\u201d\u540d\u5934\uff0c\u5c31\u7acb\u523b\u5413\u6210\u8fd9\u4e2a\u6837\u5b50\u3002 \u3000\u3000\u4f46\u987b\u77e5\u5f53\u5e74\u201c\u4e03\u5999\u795e\u541b\u201d\u5728\u6b66\u6797\u4e2d\u7684\u58f0\u671b\u3001\u5730\u4f4d\u53ca\u6b66\u529f\uff0c\u90fd\u53ef\u8bf4\u662f\u65e0\u4e0e\u4f26\u6bd4\u7684\uff0c\u800c \u4e14\u51fa\u540d\u7684\u624b\u8fa3\uff0c\u5f80\u5f80\u8c08\u7b11\u4e2d\u4fbf\u5236\u4eba\u4e8e\u6b7b\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u4e00\u522b\u6c5f\u6e56\u5341\u5e74\uff0c\u6b64\u523b\u5374\u7a81\u7136\u5728\u4ed6\u4e24\u4eba\u8239\u4e0a\u51fa\u73b0\uff0c\u5374\u770b\u4e0d\u5230\u4ed6\u8138\u4e0a\u7684\u8868\u60c5\uff0c \u4e0d\u7981\u751f\u8d77\u4e00\u9635\u5bd2\u6c14\uff0c\u81ea\u810a\u6881\u76f4\u4e0a\u5934\u9876\u3002 \u3000\u3000\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\uff0c\u672c\u7d20\u4ee5\u673a\u8b66\u89c1\u79f0\uff0c\u4ed6\u7565\u4e00\u9547\u9759\uff0c\u770b\u5230\u90a3\u65b9\u59d3\u5c11\u5973\u6b63\u88ab\u4e03\u5999\u795e\u541b\u6276 \u5728\u4e00\u65c1\uff0c\u5fc3\u77e5\u4ed6\u5fc5\u5b9a\u4e3a\u6b64\u800c\u6765\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4e45\u95fb\u4e03\u5999\u795e\u541b\u2018\u4e03\u827a\u2019\u4e2d\u6700\u540e\u4e00\u827a\uff0c\u4fbf\u662f \u8272\u5b57\uff0c\u4eca\u65e5\u60f3\u5fc5\u4e5f\u662f\u4e3a\u6b64\u5973\u800c\u6765\uff0c\u53cd\u6b63\u6b64\u5973\u53e6\u6709\u4e3b\u4eba\uff0c\u6211\u4e50\u5f97\u4e0d\u7ba1\u6b64\u4e8b\uff0c\u7b49\u5230\u90a3\u4eba\u6765\u65f6 \u518d\u8bf4\uff0c\u4ed6\u4e24\u4eba\uff0c\u4e00\u4e2a\u662f\u6c5f\u6e56\u4e0a\u4e45\u5df2\u4eab\u540d\u7684\u96be\u60f9\u4eba\u7269\uff0c\u4e00\u4e2a\u662f\u521d\u51fa\u6c5f\u6e56\u4fbf\u60ca\u9707\u6b66\u6797\u7684\u9b54\u5934\uff0c \u6b63\u597d\u4e00\u62fc\u3002\u201d \u3000\u3000\u4ed6\u4e00\u5ff5\u81f3\u6b64\uff0c\u5fc3\u91cc\u9042\u5c31\u5927\u5b9a\uff0c\u8bf4\u9053\uff1a\u201c\u795e\u541b\u4e45\u522b\u6c5f\u6e56\uff0c\u60f3\u4e0d\u5230\u4eca\u65e5\u665a\u8f88\u4eec\u5374\u6709\u5e78\u5f97 \u89c1\u795e\u541b\u4e0a\u9762\uff0c\u665a\u8f88\u6597\u80c6\u731c\u4e0a\u4e00\u731c\uff0c\u795e\u541b\u6df1\u591c\u6765\u5230\u655d\u8239\uff0c\u53ef\u662f\u4e3a\u4e86\u8fd9\u4e2a\u5973\u5b50\u3002\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u53c8\u51b7\u7b11\u4e86\u4e00\u9635\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u5012\u662f\u806a\u660e\u5f97\u5f88\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u5e72\u7b11\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u662f\u795e\u541b\u7684\u610f\u601d\uff0c\u665a\u8f88\u90a3\u6562\u8fdd\u80cc\uff0c\u53ea\u662f\u6b64\u5973\u5b50\u4e43\u522b\u4eba \u4ea4\u6258\u7ed9\u665a\u8f88\u7684\u2026\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u54fc\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u4eba\u4ea4\u6258\u53c8\u600e\u6837\uff0c\u96be\u9053\u6211\u4e03\u5999\u795e\u541b\u90fd\u4e0d\u80fd\u5c06\u4eba\u5e26\u8d70 \u5417\uff1f\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u5fd9\u8bf4\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u53ea\u662f\u665a\u8f88\u5374\u4e0d\u77e5\u80fd\u5426\u8bf7\u524d\u8f88\u7559\u4e0b\u4e2a\u4fe1\u7269\uff0c\u8ba9 \u665a\u8f88\u4e5f\u597d\u5bf9\u522b\u4eba\u6709\u4e2a\u4ea4\u5f85\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u8bf4\u6b64\u8bdd\u65f6\uff0c\u771f\u662f\u634f\u7740\u4e00\u628a\u51b7\u6c57\uff0c\u4ed6\u77e5\u9053\u4e03\u5999\u795e\u541b\uff0c\u751f\u6027\u602a\u50fb\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u53e5\u8bdd \u5c31\u60f9\u4e86\u4ed6\u7684\u813e\u6c14\uff0c\u90a3\u4e48\u81ea\u5df1\u53ea\u6015\u5f53\u65f6\u4fbf\u8981\u96be\u770b\uff0c\u4f46\u5982\u4e0d\u8bf4\u7684\u8bdd\uff0c\u53e6\u5916\u4e00\u4e2a\u4e5f\u662f\u81ea\u5df1\u7edd\u5bf9 \u60f9\u4e0d\u8d77\u7684\u4eba\u7269\u3002 \u3000\u3000\u54ea\u77e5\u4e03\u5999\u795e\u541b\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u5c06\u624b\u4eba\u6000\uff0c\u53d6\u51fa\u4e00\u5757\u91d1\u724c\uff0c\u629b\u5728\u684c\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6b64\u724c\u5c31 \u662f\u6211\u7684\u4fe1\u7269\uff0c\u82e5\u662f\u6709\u4eba\u5bf9\u6211\u4e03\u5999\u795e\u541b\u4e0d\u670d\u6c14\u7684\u8bdd\uff0c\u53ea\u8981\u8bf4\u51fa\u6765\uff0c\u4e0d\u8981\u4ed6\u627e\u6211\uff0c\u6211\u81ea\u4f1a\u53bb \u627e\u4ed6\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\uff0c\u8d3a\u4fe1\u96c4\u662f\u5e0c\u671b\u4e03\u5999\u795e\u541b\u5982\u6b64\uff0c\u4f46\u5374\u6599\u4e0d\u5230\u4ed6\u4f1a\u8fd9\u4e48\u8f7b\u6613\u5730\u7b54\u5e94\u4e86\uff0c\u4ed6\u4eec\u5fc3 \u4e2d\u4e0d\u7981\u751f\u51fa\u540c\u6837\u4e8c\u79cd\u60f3\u6cd5\uff0c\u90a3\u5c31\u662f\u8fd9\u6c5f\u6e56\u4e0a\u4eba\u4eba\u95fb\u800c\u751f\u754f\u7684\u4e03\u5999\u795e\u541b\uff0c\u4f3c\u4e4e\u6ca1\u6709\u4f20\u8bf4\u4e2d \u90a3\u79cd\u4e56\u50fb\u548c\u53ef\u6015\u3002 \u3000\u3000\u7136\u800c\u4ed6\u4eec\u600e\u77e5\u8fd9\u5176\u4e2d\u53c8\u53e6\u6709\u9690\u60c5\uff0c\u6b64\u4e03\u5999\u795e\u541b\uff0c\u5df2\u975e\u5341\u5e74\u524d\u7684\u4e03\u5999\u795e\u541b\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u559c\u60a6\u5730\u671b\u7740\u684c\u4e0a\u7684\u91d1\u724c\uff0c\u53ea\u89c1\u90a3\u4e0a\u9762\u94f8\u7740\u4e03\u6735\u6885\u82b1\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u968f\u7740\u8bf4\u8bdd\uff0c\u53c8\u5c06\u90a3\u5c11\u5973\u6a2a\u62b1\u5728\u6000\u91cc\uff0c\u4e3e\u6b65\u8d70\u51fa\u8231\u5916\u3002 \u3000\u3000\u6b64\u4e03\u5999\u795e\u541b\u671b\u7740\u4e00\u7247\u6c5f\u6c34\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff0c\u4ed6\u6b64\u523b\u624b\u4e2d\u53c8\u591a\u4e86\u4e00\u4eba\uff0c\u600e\u80fd\u518d\u50cf\u65b9\u624d \u90a3\u6837\u4ee5\u7edd\u9876\u8f7b\u529f\u98de\u6e21\u8fd9\u4e8c\u5341\u4f59\u4e08\u7684\u6c5f\u9762\u3002 \u3000\u3000\u4f46\u4ed6\u52bf\u53c8\u4e0d\u80fd\u53eb\u4eba\u5bb6\u5907\u8239\u9001\u81ea\u5df1\u8fc7\u53bb\uff0c\u90a3\u6837\u4e00\u6765\uff0c\u5c82\u975e\u5931\u4e86\u81ea\u5df1\u7684\u8eab\u4efd\u3002 \u3000\u3000\u4ed6\u76ee\u6ce8\u6c5f\u5fc3\uff0c\u5374\u53d1\u73b0\u81ea\u5df1\u65b9\u624d\u7528\u4ee5\u98de\u6e21\u6c5f\u9762\u7684\u90a3\u53ea\u706f\u7b3c\u6b63\u98d8\u6d6e\u5728\u79bb\u8239\u516d\u4e08\u8fdc\u8fd1\u7684\u6c5f \u9762\u4e0a\uff0c\u5fc3\u4e2d\u53c8\u5fd6\u9053\uff1a\u201c\u82e5\u662f\u6211\u7528\u2018\u6697\u9999\u6d6e\u5f71\u2019\u91cc\u7684\u2018\u9999\u95fb\u5341\u91cc\u2019\u8eab\u6cd5\uff0c\u6216\u53ef\u6e21\u6b64\u4e00\u6bb5\u6c5f \u9762\uff0c\u4f46\u8fd9\u2018\u9999\u95fb\u5341\u91cc\u2019\u7684\u8eab\u6cd5\uff0c\u6211\u4ec5\u5728\u77f3\u5ba4\u4e2d\u9759\u5750\u7ec3\u6c14\uff0c\u5374\u672a\u66fe\u4f7f\u7528\u8fc7\uff0c\u4f55\u51b5\u624b\u4e0a\u8fd8\u6709 \u4e00\u4eba\uff0c\u82e5\u4e00\u4e2a\u4e0d\u597d\uff0c\u5c82\u975e\u66f4\u662f\u96be\u582a\u3002\u201d \u3000\u3000\u987b\u77e5\u4e03\u5999\u795e\u541b\u4e4b\u201c\u6697\u9999\u6d6e\u5f71\u201d\u3002\u867d\u662f\u5185\u529f\u7ec3\u4e60\u7684\u8981\u8bc0\uff0c\u4f46\u5374\u5c06\u8f7b\u529f\u4e2d\u7edd\u5999\u7684\u8eab\u6cd5\uff0c \u5bd3\u4e4b\u4e8e\u5185\uff0c\u8fd9\u79cd\u5185\u529f\u4e0e\u8f7b\u529f\u8fde\u7ec3\u7684\u65b9\u6cd5\uff0c\u4e5f\u5c31\u662f\u4e03\u5999\u795e\u541b\u7684\u8f7b\u529f\u80fd\u72ec\u6b65\u6b66\u6797\u7684\u7f18\u6545\u3002 \u3000\u3000\u8fd9\u5ff5\u5934\u5728\u4ed6\u5fc3\u4e2d\u6781\u5feb\u7684\u601d\u7d22\u4e86\u4e00\u904d\uff0c\u6b64\u65f6\u90a3\u5b59\u8d85\u8fdc\u4e0e\u8d3a\u4fe1\u96c4\u4e5f\u6765\u8239\u5934\u3002 \u3000\u3000\u5c0f\u9f99\u795e\u8eac\u8eab\u62b1\u62f3\u9053\uff1a\u201c\u795e\u541b\u6765\u53bb\u5306\u5306\uff0c\u665a\u8f88\u4e5f\u672a\u80fd\u4e00\u5c3d\u4ef0\u6155\u4e4b\u5ff1\uff0c\u4f46\u671b\u65e5\u540e\u6709\u7f18\uff0c \u80fd\u518d\u7779\u795e\u541b\u98ce\u91c7\uff0c\u7565\u9886\u6559\u8bf2\u3002\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u5fae\u4e00\u6446\u624b\uff0c\u5fc3\u4e2d\u53c8\u5fd6\u9053\uff1a\u201c\u770b\u4ed6\u4eec\u5bf9\u6211\u7684\u606d\u656c\u4e4b\u8272\u3002\u5c31\u53ef\u4ee5\u77e5\u9053\u2018\u4e03\u5999\u795e \u541b\u2019\u8fd9\u56db\u5b57\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u4ece\u4eca\u800c\u540e\uff0c\u8fd9\u2018\u4e03\u5999\u795e\u541b\u2019\u56db\u5b57\u5c31\u8981\u6211\u6765\u53d1\u626c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u601d\u7d22\u81f3\u6b64\uff0c\u518d\u4e0d\u8003\u8651\uff0c\u5e73\u624b\u4e00\u63a8\uff0c\u7adf\u5c06\u90a3\u5c11\u5973\u7684\u8eab\u8eaf\u76f4\u9001\u53bb\u3002 \u3000\u3000\u4ed6\u5185\u529b\u672c\u662f\u60ca\u4eba\uff0c\u53ea\u8d1d\u90a3\u5c11\u5973\u7684\u8eab\u8eaf\uff0c\u5b9b\u5982\u79bb\u5f26\u4e4b\u7bad\uff0c\u5e73\u7740\u76f4\u98de\u51fa\u53bb\u3002 \u3000\u3000\u6c5f\u91cc\u767d\u9f99\u4ee5\u53ca\u5c0f\u9f99\u795e\u8d3a\u4fe1\u96c4\u9f50\u90fd\u4e00\u6115\u3002\u4e0d\u77e5\u4ed6\u6b64\u4e3e\u4f55\u4e3a\u3002 \u3000\u3000\u90a3\u77e5\u4ed6\u4eba\u65b9\u79bb\u624b\uff0c\u81ea\u5df1\u4e5f\u76f4\u98de\u51fa\u53bb\uff0c\u51fa\u52bf\u7adf\u6bd4\u90a3\u88ab\u629b\u5c11\u5973\u8fd8\u6025\uff0c\u811a\u5c16\u627e\u7740\u90a3\u98d8\u6d6e\u5728 \u6c34\u9762\u4e0a\u7684\u706f\u7b3c\uff0c\u6b64\u65f6\u90a3\u5c11\u5973\u7684\u8eab\u8eaf\u4e5f\u6070\u6b63\u98de\u6765\u3002 \u3000\u3000\u4ed6\u53cc\u624b\u9f50\u51fa\uff0c\u8f7b\u8f7b\u6258\u7740\u90a3\u5c11\u5973\u7684\u8eab\u8eaf\uff0c\u4eba\u968f\u7740\u53bb\u52bf\u800c\u98d8\uff0c\u811a\u5c16\u4ecd\u8e0f\u5728\u706f\u7b3c\u4e0a\u3002 \u3000\u3000\u5b59\u3001\u8d3a\u4e8c\u4eba\uff0c\u8fdc\u8fdc\u671b\u53bb\uff0c\u53ea\u89c9\u4ed6\u51cc\u7a7a\u865a\u6e21\uff0c\u5b9b\u5982\u795e\u4ed9\uff0c\u5fc3\u91cc\u66f4\u662f\u60ca\u4f69\u5f97\u65e0\u4ee5\u590d\u52a0\u3002 \u3000\u3000\u5c31\u8fd9\u6837\uff0c\u4ed6\u4ee5\u7edd\u9876\u7684\u8eab\u6cd5\uff0c\u5728\u6c5f\u9762\u4e0a\u6ed1\u8fc7\u53bb\u5341\u4e08\u8fdc\u8fd1\uff0c\u79bb\u5cb8\u53ea\u6709\u516d\u3001\u4e03\u4e08\u8fdc\u4e86\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5fae\u5fae\u4e00\u559c\uff0c\u90a3\u77e5\u8fd0\u7528\u8fd9\u79cd\u5185\u5bb6\u7684\u7edd\u9876\u529f\u592b\uff0c\u5fc3\u795e\u4e00\u4e1d\u4e5f\u677e\u6563\u4e0d\u5f97\uff0c\u4ed6\u5fc3\u4e2d\u4e00 \u559c\uff0c\u811a\u4e0b\u4fbf\u4e00\u6c89\uff0c\u4ed6\u77e5\u9053\u771f\u6c14\u5c06\u6563\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u60ca\u3002 \u3000\u3000\u5ffd\u7136\u4ed6\u89c9\u5f97\u5df2\u6e10\u4e0b\u6c89\u7684\u706f\u7b3c\u5374\u731b\u53c8\u5f80\u4e0a\u4e00\u5347\uff0c\u539f\u6765\u6b64\u65f6\u6b63\u597d\u4e00\u4e2a\u6d6a\u82b1\u6d8c\u6765\uff0c\u5c06\u4e0b\u6c89 \u706f\u7b3c\u5f80\u4e0b\u4e00\u6258\uff0c\u8f7b\u529f\u7ec3\u81f3\u5fae\u5999\u4e4b\u5904\uff0c\u5c31\u662f\u98de\u8747\u4e4b\u529b\uff0c\u4e5f\u80fd\u5c06\u8eab\u8eaf\u6258\u8d77\uff0c\u4f55\u51b5\u8fd9\u529b\u9053\u5f3a\u80dc \u4e0d\u77e5\u5343\u4e07\u500d\u7684\u6d6a\u82b1\uff1b \u3000\u3000\u4ed6\u5fc3\u795e\u7565\u52a8\uff0c\u8eab\u8eaf\u968f\u7740\u8fd9\u706f\u7b3c\u4e0a\u5347\u4e4b\u52bf\u4e00\u6d6e\uff0c\u5728\u90a3\u6d6a\u5934\u6700\u9ad8\u4e4b\u65f6\uff0c\u811a\u5c16\u7528\u529b\u4e00\u8e0f\uff0c \u8eab\u5f62\u4e00\u5f13\uff0c\u55d6\u5730\u98de\u8d8a\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u867d\u7136\u4ed6\u624b\u4e0a\u6258\u7740\u4e00\u4eba\uff0c\u4f46\u5f53\u4ed6\u98de\u8d77\u5728\u7a7a\u4e2d\u65f6\uff0c\u8eab\u5f62\u4ecd\u7136\u662f\u90a3\u4e48\u5b89\u7965\u800c\u7f8e\u5999\uff0c\u5bbd\u5927\u7684 \u8863\u8882\u968f\u7740\u6c5f\u98ce\u98d8\u821e\u7740\uff0c\u90a3\u60c5\u51b5\u662f\u96be\u4ee5\u63cf\u6479\u7684\u3002 \u3000\u3000\u7b49\u5230\u8fd9\u6b21\u4ed6\u8eab\u5f62\u843d\u4e0b\u65f6\uff0c\u5df2\u662f\u5cb8\u8fb9\u4e86\uff0c\u4ed6\u5df2\u52bf\u7aed\uff0c\u9759\u7acb\u4e86\u534a\u664c\uff0c\u8c03\u5300\u4e86\u4f53\u5185\u7684\u771f\u6c14\uff0c \u5c06\u6258\u7740\u90a3\u5c11\u5973\u7684\u53cc\u624b\uff0c\u5e73\u653e\u4e86\u4e0b\u6765\uff0c\u6781\u5feb\u7684\u51e0\u4e2a\u7eb5\u8eab\uff0c\u5411\u57ce\u5185\u98de\u8eab\u800c\u53bb\u3002\u6643\u773c\u4fbf\u9690\u6ca1\u5728 \u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9192\u6765\u65f6\uff0c\u53d1\u89c9\u81ea\u5df1\u5904\u8eab\u4e8e\u4e00\u95f4\u6781\u4e3a\u534e\u4e3d\u7684\u623f\u95f4\u91cc\uff0c\u90a3\u662f\u5979\u4ece\u672a\u4eab\u53d7\u8fc7\u7684\u534e\u4e3d\uff0c \u751a\u81f3\u8fde\u6240\u7761\u7684\u5e8a\uff0c\u90fd\u90a3\u4e48\u67d4\u8f6f\u800c\u6e29\u99a8\u3002 \u3000\u3000\u5e8a\u4e0a\u6302\u7740\u7684\u6d41\u82cf\u7684\u5e10\u5b50\uff0c\u94fa\u7740\u9526\u7f0e\u88c5\u6210\u7684\u88ab\u8925\uff0c\u623f\u95f4\u6240\u6446\u8bbe\u7684\uff0c\u4e5f\u7edd\u4e0d\u662f\u4e00\u4e2a\u5e73\u6c11 \u6240\u80fd\u68a6\u60f3\u7684\uff0c\u5979\u8212\u5c55\u4e86\u4e00\u4e0b\u56db\u80a2\uff0c\u5728\u5979\u9192\u6765\u7684\u4e00\u5239\u90a3\u91cc\uff0c\u8fd9\u4e00\u5207\u786e\u4e4e\u90fd\u4ee4\u5979\u8ff7\u60d1\u4e86\u3002 \u3000\u3000\u7136\u540e\uff0c\u5979\u7a81\u7136\u8bb0\u8d77\u5979\u672c\u662f\u88ab\u56f0\u5728\u8239\u91cc\uff0c\u4e00\u6761\u7a81\u6765\u7684\u4eba\u5f71\uff0c\u4f7f\u8fa9\u6bd4\u5979"\u5f97\u5979\u660f\u8ff7\u4e86\uff0c \u6b64\u540e\u5979\u4fbf\u832b\u7136\u4e00\u65e0\u6240\u77e5\u3002 \u3000\u3000\u4f46\u73b0\u5728\u5374\u600e\u5730\u53c8\u4f1a\u8eba\u5728\u8fd9\u91cc\u5462\uff1f \u3000\u3000\u5979\u66f4\u8ff7\u60d1\u4e86\uff0c\u5979\u60f3\u8d77\u8fd9\u4e24\u4e2a\u591a\u6708\u6240\u906d\u9047\u7684\u4e00\u5207\uff0c\u5374\u8fdc\u6bd4\u5979\u4e00\u751f\u4e2d\u5176\u4f59\u90a3\u4e48\u957f\u7684\u65f6\u65e5 \u603b\u79ef\u8fd8\u591a\uff0c\u8fd9\u4e0d\u662f\u5947\u5f02\u7684\u4e8b\u5417\u3002 \u3000\u3000\u5979\u60f3\u8d77\u5979\u7684\u201c\u5bb6\u201d\uff0c\u90a3\u672c\u662f\u4e00\u4e2a\u5b89\u7965\u800c\u8212\u9002\u7684\u5bb6\uff0c\u7236\u4eb2\u65b9\u4e91\u5947\u5728\u5f53\u5730\u5f00\u4e86\u4e2a\u5c0f\u5c0f\u7684 \u6559\u6b66\u573a\u5b50\uff0c\u6536\u4e86\u4e09\u3001\u56db\u5341\u4e2a\u5b66\u751f\uff0c\u867d\u7136\u5e76\u4e0d\u5341\u5206\u5bcc\u88d5\uff0c\u4f46\u5374\u662f\u5c0f\u5eb7\u4e86\uff0c\u5c0f\u57ce\u7684\u5c45\u6c11\uff0c\u4e5f \u5bf9\u4ed6\u4eec\u90fd\u5f88\u5c0a\u656c\u3002\u4f46\u662f\u6709\u4e00\u5929\u3002\u5979\u60f3\u90a3\u662f\u574f\u8fd0\u5f00\u59cb\u7684\u4e00\u5929\uff0c\u4e00\u4e2a\u8863\u7740\u534e\u4e3d\u7684\u9752\u5e74\uff0c\u95ef\u8fdb \u5979\u7684\u751f\u6d3b\uff0c\u4f7f\u5f97\u5979\u5931\u53bb\u4e86\u5b89\u7965\u548c\u8212\u9002\u3002 \u3000\u3000\u4f46\u662f\u7236\u6bcd\u5374\u90a3\u4e48\u9ad8\u5174\u7740\u90a3\u5c11\u5e74\u7684\u56de\u6765\uff0c\u53eb\u6211\u53eb\u4ed6\u54e5\u54e5\uff0c\u540e\u6765\u53c8\u53eb\u6211\u79f0\u4ed6\u6b39\u54e5\uff0c\u5e76\u4e14 \u544a\u8bc9\u6211\u4ed6\u53eb\u91d1\u6b39\uff0c\u662f\u7236\u4eb2\u5931\u8e2a\u4e86\u5341\u591a\u5e74\u4eb2\u751f\u513f\u5b50\u3002 \u3000\u3000\u6211\u5f00\u59cb\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u7236\u4eb2\u7684\u4eb2\u751f\u513f\u5b50\u59d3\u91d1\uff0c\u800c\u4e14\u5931\u8e2a\u4e86\u8fd9\u4e48\u4e45\u3002 \u3000\u3000\u7236\u4eb2\u544a\u8bc9\u6211\uff0c\u4ed6\u7684\u6b39\u513f\u8fd9\u5341\u591a\u5e74\u6765\uff0c\u5728\u5916\u9762\u9047\u7740\u4e86\u8bb8\u591a\u5947\u602a\u7684\u4e8b\uff0c\u800c\u4e14\u6709\u4e00\u4e2a\u672c\u4e8b \u975e\u5e38\u5927\u7684\u4eba\uff0c\u6559\u7ed9\u4ed6\u4e00\u8eab\u6b66\u529f\u3002 \u3000\u3000\u8fd9\u4e9b\u4e8b\u6211\u867d\u542c\u5f97\u6709\u8da3\uff0c\u4f46\u5374\u4e0d\u77e5\u600e\u5730\uff0c\u5bf9\u6211\u7684\u2018\u6b39\u54e5\u2019\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u6765\u7684\u8ba8\u538c\uff0c\u4ed6 \u603b\u662f\u90a3\u4e48\u9634\u9633\u602a\u6c14\u7684\uff0c\u4e24\u53ea\u773c\u6674\u66f4\u662f\u53c8\u51f6\uff0c\u53c8\u72e0\uff0c\u53c8\u51b7\uff0c\u770b\u8d77\u4eba\u6765\uff0c\u50cf\u662f\u8981\u628a\u522b\u4eba\u5403\u4e0b \u53bb\u4f3c\u7684\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4e9b\u8fd8\u4e0d\u7b97\u6700\u574f\u7684\uff0c\u6700\u574f\u7684\u662f\u7236\u4eb2\u6709\u4e00\u5929\u7a81\u7136\u8981\u6211\u5ac1\u7ed9\u6211\u7684\u6b39\u6b4c\uff0c\u6211\u5413\u6b7b\u4e86\uff0c \u59b9\u59b9\u600e\u80fd\u5ac1\u7ed9\u54e5\u54e5\u5462\uff0c\u7236\u4eb2\u8fd9\u624d\u544a\u8bc9\u6211\uff0c\u6211\u4e0d\u662f\u4ed6\u7684\u4eb2\u751f\u5973\u513f\uff0c\u53c8\u8bf4\u6b39\u54e5\u672c\u4e8b\u600e\u4e48\u5927\uff0c \u5728\u5916\u9762\u6709\u600e\u4e48\u5927\u7684\u5730\u4f4d\u3002 \u3000\u3000\u6211\u4e0d\u80af\uff0c\u6211\u600e\u4e48\u90fd\u4e0d\u80af\uff0c\u7236\u4eb2\u6c14\u4e86\uff0c\u8bf4\uff1a\u2018\u4e0d\u5ac1\u4e5f\u8981\u5ac1\uff0c\u2019\u4ed6\u50cf\u53d8\u4e86\u4e00\u4e2a\u4eba\u4f3c\u7684\uff0c \u5bf9\u6211\u53c8\u51f6\u53c8\u72e0\uff0c\u6211\u6025\u5f97\u54ed\u4e86\u3002 \u3000\u3000\u6211\u4e5f\u4e0d\u77e5\u9053\u54ed\u4e86\u591a\u4e45\u3002\u90a3\u6b39\u54e5\u7a81\u7136\u7ad9\u5728\u6211\u7684\u8eab\u4fa7\uff0c\u6211\u4e5f\u4e0d\u77e5\u4ed6\u600e\u4e48\u8fdb\u6765\u7684\uff0c\u4ed6\u95ee\u6211 \u4e3a\u4ec0\u4e48\u4e0d\u80af\u5ac1\u7ed9\u4ed6\uff0c\u53c8\u8bf4\u2018\u4ed6\u5341\u5206\u559c\u6b22\u6211\u3002\u2019 \u3000\u3000\u8fd9\u65f6\u5019\u6211\u6068\u900f\u4e86\uff0c\u6068\u7236\u4eb2\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u6211\u8981\u5ac1\u7ed9\u4ed6\uff0c\u4ed6\u8fd8\u4e0d\u8bf4\uff0c\u6211\u5c31\u6c14\u7740\u8bf4\uff0c\u2018\u53ea\u8981 \u5c06\u4ed6\u7684\u7236\u4eb2\u3001\u6bcd\u4eb2\u5168\u6740\u6b7b\uff0c\u6211\u5c31\u5ac1\u7ed9\u4ed6\uff0c\u2019\u4ed6\u7ad9\u4e86\u4e00\u4f1a\uff0c\u5c31\u51fa\u53bb\u4e86\uff0c\u6211\u672c\u6765\u662f\u8bf4\u4e00\u65f6\u6c14 \u8bdd\uff1a\u201c\u90a3\u77e5\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ed6\u4e00\u624b\u62b1\u7740\u7236\u4eb2\uff0c\u4e00\u624b\u62b1\u7740\u6bcd\u4eb2\uff0c\u8d70\u5230\u623f\u91cc\u6765\uff0c\u5f80\u5730\u4e0a\u4e00\u4e22\uff0c\u6211 \u8fde\u5fd9\u722c\u8d77\u6765\u4e00\u770b\uff0c\u5440\uff0c\u7236\u4eb2\u6bcd\u4eb2\u771f\u7684\u90fd\u88ab\u4ed6\u6740\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u6211\u7b80\u76f4\u5413\u5f97\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u6211\u518d\u6ca1\u6709\u60f3\u5230\u4ed6\u5c45\u7136\u8fd9\u4e48\u6ca1\u6709\u4eba\u6027\uff0c\u6211\u53c8\u54ed\u3001\u53c8\u95f9\u3001 \u53c8\u9a82\uff0c\u4ed6\u53ea\u662f\u62ce\u51b7\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u8bdd\u4e5f\u4e0d\u8bb2\u4e00\u53e5\u3002 \u3000\u3000\u6211\u66f4\u6015\u4e86\uff0c\u6211\u77e5\u9053\u9664\u4e86\u4e00\u6b7b\u4e4b\u5916\uff0c\u518d\u4e5f\u6ca1\u6709\u522b\u7684\u529e\u6cd5\u6765\u9003\u907f\u4ed6\uff0c\u4e8e\u662f\u6211\u62ff\u8d77\u5200\u5c31\u8981 \u81ea\u520e\uff0c\u90a3\u77e5\u4ed6\u624b\u4e00\u52a8\uff0c\u6211\u7684\u5200\u5c31\u8dd1\u5230\u4ed6\u624b\u4e0a\u53bb\u4e86\u3002\u201d \u3000\u3000\u5c31\u8fd9\u6837\uff0c\u6211\u6b7b\u4e5f\u6b7b\u4e0d\u6210\uff0c\u4f46\u6211\u66f4\u7acb\u5b9a\u51b3\u5fc3\u4e0d\u5ac1\u7ed9\u4ed6\uff0c\u6709\u5929\u4ed6\u8bf4\uff1a\u201c\u4f60\u4e0d\u8981\u4ee5\u4e3a\u6211\u771f \u62ff\u4f60\u6ca1\u529e\u6cd5\u3002\u5176\u5b9e\u6211\u624b\u4e00\u70b9\uff0c\u8981\u4f60\u600e\u6837\u4fbf\u600e\u6837\uff0c\u53ea\u662f\u6211\u5b9e\u5728\u592a\u559c\u6b22\u4f60\uff0c\u4e0d\u613f\u610f\u5f3a\u8feb\u4f60\u3002\u201d \u3000\u3000\u4ed6\u65e5\u76ee\u591c\u591c\u5730\u770b\u7740\u6211\uff0c\u4e00\u5929\u591c\u6211\u542c\u5230\u4e00\u79cd\u5947\u602a\u7684\u58f0\u97f3\uff0c\u50cf\u662f\u9e1f\u53eb\uff0c\u53c8\u50cf\u662f\u733f\u557c\uff0c\u4ed6 \u4e5f\u542c\u5230\u4e86\uff0c\u800c\u4e14\u9762\u8272\u9a6c\u4e0a\u53d8\u6210\u90a3\u4e48\u96be\u770b\u3002\u201d \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u4ed6\u4e00\u76f4\u6ca1\u7761\uff0c\u5728\u601d\u7d22\u7740\uff0c\u7b2c\u4e8c\u5929\u7edd\u65e9\u4fbf\u5e26\u7740\u6211\u8981\u8d70\uff0c\u8fd9\u65f6\u6211\u5df2\u7ecf\u77e5\u9053\u4ed6\u786e \u5b9e\u6709\u7740\u4e0d\u53ef\u601d\u8bae\u7684\u529f\u592b\uff0c\u6015\u4ed6\u4e00\u7528\u5f3a\uff0c\u6211\u66f4\u6ca1\u6709\u529e\u6cd5\uff0c\u5c31\u53ea\u597d\u8ddf\u7740\u4ed6\u8d70\u4e86\u3002\u8d70\u4e86\u534a\u5929\uff0c \u5230\u4e86\u957f\u6c5f\u7684\u5cb8\u8fb9\uff0c\u4ed6\u627e\u6765\u627e\u53bb\uff0c\u627e\u7740\u4e00\u6761\u5c0f\u8239\uff0c\u8bf4\u4e86\u51e0\u53e5\u6211\u4e0d\u503c\u7684\u8bdd\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5cb8 \u8fb9\u5c31\u9a76\u6765\u4e86\u4e24\u6761\u5927\u8239\uff0c\u4ed6\u4e0d\u7b49\u8239\u9760\u5cb8\uff0c\u5c31\u631f\u7740\u6211\u8df3\u4e86\u4e0a\u53bb\uff0c\u8239\u4e0a\u7684\u4eba\u770b\u662f\u4ed6\u6765\u4e86\uff0c\u90fd\u50cf \u662f\u53c8\u60ca\u53c8\u6015\uff0c\u90fd\u90a3\u4e48\u606d\u656c\u7684\u95ee\u4ed6\u6709\u4ec0\u4e48\u4e8b\uff0c\u4e8e\u662f\u4ed6\u5c31\u5c06\u6211\u7559\u5728\u8239\u4e0a\uff0c\u53eb\u90a3\u4e9b\u4eba\u770b\u5b88\u7740\u6211\uff0c \u800c\u4e14\u8981\u597d\u597d\u5f85\u6211\uff0c\u81ea\u5df1\u5c31\u8d70\u4e86\u3002 \u3000\u3000\u6211\u5728\u8239\u4e0a\u8eba\u4e86\u4e24\u5929\uff0c\u624d\u77e5\u9053\u662f\u5f3a\u76d7\u8239\uff0c\u6709\u4e00\u4e2a\u5934\u5b50\u53eb\u5c0f\u795e\u9f99\uff0c\u8fd8\u6709\u4e00\u4e2a\u59d3\u5b59\u7684\uff0c\u5bf9 \u6211\u548c\u6c14\u5f97\u5f88\uff0c\u53ea\u662f\u5374\u53eb\u4e00\u4e2a\u6e05\u8138\u80e1\u5b50\u7684\u5f3a\u76d7\u65e5\u591c\u770b\u7740\u6211\uff0c\u4e0d\u51c6\u6211\u8fd9\u6837\uff0c\u4e0d\u51c6\u6211\u90a3\u6837\u3002 \u3000\u3000\u6709\u5929\u665a\u4e0a\uff0c\u90a3\u80e1\u5b50\u559d\u4e86\u5f88\u591a\u9152\uff0c\u7a81\u7136\u6251\u5230\u6211\u8eab\u4e0a\uff0c\u6478\u6211\u3001\u4eb2\u6211\u3001\u66f4\u6c61\u8fb1\u6211\uff0c\u6211\u7684\u5634 \u53c8\u88ab\u4ed6\u4eec\u585e\u4f4f\u4e86\uff0c\u60f3\u53eb\u53c8\u53eb\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u4e2a\u65f6\u5019\uff0c\u90a3\u59d3\u5b59\u7684\u6765\u4e86\uff0c\u4e00\u628a\u5c06\u90a3\u80e1\u5b50\u626f\u4e86\u8d77\u6765\uff0c\u8fd8\u8bf4\u8981\u6740\u6b7b\u4ed6\uff0c\u90a3\u80e1\u5b50\u6025 \u4e86\uff0c\u5c31\u548c\u4ed6\u6253\u4e86\u8d77\u6765\uff0c\u6211\u4e00\u770b\uff0c\u5c31\u4e58\u6b64\u673a\u4f1a\u9003\u51fa\u8239\u4e86\u3002 \u3000\u3000\u90a3\u77e5\u540e\u6765\u8fd8\u662f\u88ab\u4ed6\u4eec\u6293\u56de\u53bb\u4e86\uff0c\u6211\u5728\u8def\u4e0a\u78b0\u7740\u7684\u4e24\u4e2a\u4eba\uff0c\u770b\u6837\u5b50\u5012\u50cf\u662f\u4e2a\u82f1\u96c4\uff0c\u60f3 \u4e0d\u5230\u5374\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\uff0c\u5c24\u5176\u662f\u90a3\u4e00\u4e2a\u3002 \u3000\u3000\u6211\u518d\u88ab\u6293\u5230\u8239\u4e0a\u4e4b\u540e\uff0c\u4ed6\u4eec\u7adf\u5c06\u8239\u9a76\u5230\u6c5f\u5fc3\u4e86\uff0c\u6211\u77e5\u9053\u66f4\u6ca1\u6709\u529e\u6cd5\u9003\u8d70\uff0c\u4f55\u51b5\u8fd9\u662f \u90a3\u59d3\u5b59\u7684\u4eb2\u81ea\u770b\u7740\u6211\uff0c\u53ef\u662f\u600e\u4e48\u73b0\u5728\u5374\u4f1a\u6765\u5230\u8fd9\u4e2a\u5730\u65b9\u5462\uff1f\u96be\u9053\u8fd9\u91cc\u662f\u4ed6\u4eec\u7684\u5f3a\u76d7\u7a9d \u5417\uff1f\u201d \u3000\u3000\u5979\u4f0f\u5728\u5e8a\u4e0a\uff0c\u5f80\u4e8b\u5982\u68a6\uff0c\u4e00\u5e55\u5e55\u5730\u81ea\u5979\u5fc3\u5934\u95ea\u8fc7\uff0c\u8fd9\u4e2a\u98d8\u6cca\u65e0\u4f9d\u7684\u5c11\u5973\uff0c\u6b64\u65f6\u67d4\u80a0 \u767e\u7ed3\uff0c\u4f0f\u5728\u5e8a\u4e0a\uff0c\u9e23\u54bd\u4e86\u8d77\u6765\u3002 \u3000\u3000\u7a81\u7136\u5979\u542c\u5230\u8eab\u540e\u6709\u4eba\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u5979\u60ca\u5f97\u8df3\u4e86\u8d77\u6765\uff0c\u5750\u5728\u5e8a\u4e0a\u4e00\u770b\uff0c\u5374\u662f\u5979\u5728\u8f66\u91cc \u9047\u5230\u7684\uff0c\u5979\u8ba4\u4e3a\u6700\u6ca1\u6709\u7528\u7684\u90a3\u4e2a\u5c11\u5e74\u3002 \u3000\u3000\u8f9b\u6377\u6b63\u7b11\u541f\u541f\u5730\u671b\u7740\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u9192\u6765\u4e86\u5417\uff1f\u201d\u5979\u66f4\u662f\u5947\u602a\u5f97\u65e0\u4ece\u590d\u52a0\uff0c\u600e \u5730\u8fd9\u5c11\u5e74\u4f1a\u7a81\u7136\u800c\u6765\uff0c\u96be\u9053\u8fd9\u662f\u4ed6\u7684\u5bb6\uff0c\u7adf\u662f\u4ed6\u5c06\u81ea\u5df1\u6551\u51fa\u6765\u7684\u5417\uff1f\u4e00\u65f6\u5979\u6014\u4f4f\u4e86\uff0c\u8bf4 \u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u8f9b\u6377\u53c8\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u4e0d\u5fc5\u7591\u5fc3\uff0c\u5728\u4e0b\u867d\u662f\u65e0\u80fd\uff0c\u5374\u6709\u4e00\u4e2a\u80fd\u529b\u5f88\u5927\u7684\u670b\u53cb\uff0c\u4ece \u8239\u4e0a\u5c06\u59d1\u5a18\u6551\u4e86\u4e0b\u6765\uff0c\u59d1\u5a18\u6700\u597d\u8fd8\u662f\u5c31\u5728\u8fd9\u91cc\u9759\u5fc3\u5f85\u4e00\u6bb5\u65e5\u5b50\uff0c\u8fd9\u91cc\u662f\u5728\u4e0b\u7684\u9759\u5ba4\uff0c\u7edd \u5bf9\u4e0d\u4f1a\u6709\u4eba\u6765\u9a9a\u6270\u59d1\u5a18\u3002\u201d \u3000\u3000\u8f9b\u6377\u8bf4\u5b8c\u8bdd\uff0c\u4e5f\u4e0d\u7b49\u5979\u540c\u610f\uff0c\u8f6c\u8eab\u8d70\u4e86\u51fa\u6765\uff0c\u7a7f\u8fc7\u51e0\u4e2a\u623f\u95f4\uff0c\u8d70\u5230\u5927\u5385\uff0c\u5374\u89c1\u4e8e\u4e00 \u98de\u6b63\u5750\u5728\u90a3\u91cc\u555c\u7740\u8336\uff0c\u89c1\u4ed6\u6765\u4e86\uff0c\u5c31\u7ad9\u4e86\u8d77\u6765\uff0c\u7b11\u9053\uff1a\u201c\u8f9b\u5144\u600e\u5730\u8d77\u5f97\u5982\u6b64\u665a\uff0c\u5c0f\u5f1f\u5df1 \u5230\u524d\u9762\u53bb\u6e9c\u4e86\u4e00\u8f6c\uff0c\u800c\u4e14\u8fd8\u542c\u5230\u5e97\u4f19\u8bf4\u8d77\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u600e\u6bd4\u5f97\u4e0a\u4e8e\u5144\uff0c\u4eca\u65e5\u8d77\u5f97\u8fd8\u7b97\u65e9\u7684\u4e86\u3002\u201d \u3000\u3000\u53c8\u95ee\u9053\uff1a\u201c\u4e8e\u5144\u6240\u542c\u5230\u7684\u5947\u4e8b\uff0c\u53c8\u662f\u4f55\u4e8b\uff1f\u201d \u3000\u3000\u4e8e\u4e00\u98de\u8bf4\u9053\uff1a\u201c\u6628\u591c\u6c5f\u5cb8\u7684\u51e0\u4e2a\u6e14\u592b\uff0c\u90fd\u8bf4\u89c1\u5230\u6c5f\u5fc3\u9f99\u738b\u663e\u5723\uff0c\u5728\u6c34\u9762\u4e0a\u6765\u6765\u53bb\u53bb \u7684\u8d70\uff0c\u4eca\u5929\u4e00\u65e9\uff0c\u5c31\u4f20\u904d\u4e86\u6b66\u6c49\u5462\u3002\u201d \u3000\u3000\u8f9b\u6377\u54e6\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u6697\u7b11\uff0c\u77e5\u9053\u662f\u81ea\u5df1\u6628\u591c\u5728\u6c5f\u9762\u65bd\u5c55\u8f7b\u529f\uff0c\u5374\u88ab\u90a3\u4e9b\u6e14\u592b\u8ba4\u6210\u9f99 \u738b\u663e\u5723\u4e86\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u53c8\u9053\uff1a\u201c\u4f9d\u5c0f\u5f1f\u770b\u6765\uff0c\u90a3\u4e0d\u8fc7\u53ea\u662f\u4e2a\u8f7b\u529f\u7edd\u5999\u7684\u4eba\uff0c\u5728\u6c5f\u9762\u65bd\u5c55\u8f7b\u529f\u7f62\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u7709\u5fc3\u4e00\u76b1\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u82e5\u80fd\u5728\u6c5f\u9762\u968f\u610f\u884c\u8d70\uff0c\u8fd9\u4eba\u7684\u8f7b\u529f\u5c82\u975e\u771f\u5230\u4e86\u9a6d\u6c14\u98de\u884c \u5730\u6b65\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e8e\u4e00\u98de\u7b11\u9053\uff1a\u201c\u8f9b\u5144\u8fd8\u771f\u4e2a\u4ee5\u4e3a\u90a3\u4eba\u662f\u2018\u968f\u610f\u884c\u8d70\u2019\u5417\uff1f\u5c0f\u5f1f\u5374\u770b\u5927\u534a\u662f\u6e14\u592b\u4eec\u7684 \u6545\u7384\u5176\u8bdd\u7f62\u4e86\uff0c\u4e0d\u8fc7\u603b\u800c\u8a00\u4e4b\uff0c\u6b64\u4eba\u4e00\u5b9a\u662f\u4e2a\u597d\u624b\uff0c\u4f46\u7a81\u5728\u6b66\u6c49\u51fa\u73b0\uff0c\u96be\u9053\u662f\u51b2\u7740\u6211\u4e8e \u4e00\u98de\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5fcd\u4f4f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e8e\u5144\u592a\u8fc7\u8651\u4e86\uff0c\u90a3\u674e\u6cbb\u534e\u5c31\u662f\u8bf7\u5e2e\u624b\uff0c\u4e5f\u4e0d\u4f1a\u6709\u8fd9\u4e48\u5feb\u5440\uff01\u201d \u3000\u3000\u4e8e\u4e00\u98de\u8138\u4e00\u7ea2\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u5012\u4e0d\u662f\u6015\u4ed6\u8bf7\u5e2e\u624b\uff0c\u53ea\u662f\u6709\u70b9\u5947\u602a\u7f62\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u6015\u4ed6\u53d1\u7a98\uff0c\u5fd9\u8f6c\u8bdd\u9898\u652f\u4e86\u5f00\u53bb\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u521d\u5230\u6b66\u6c49\uff0c\u4e8e\u5144\u4e45\u8d70\u6c5f\u6e56\uff0c\u60f3\u5fc5 \u6765\u5f97\u591a\u4e86\uff0c\u4e0d\u77e5\u53ef\u5426\u966a\u5c0f\u5f1f\u5230\u5904\u8d70\u8d70\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d \u3000\u3000\u4e24\u4eba\u8d70\u51fa\u5e97\u6765\uff0c\u4e5f\u672a\u4e58\u8f66\uff0c\u968f\u610f\u5728\u8857\u4e0a\u8d70\u7740\uff0c\u6b66\u6c49\u4e43\u9102\u4e2d\u91cd\u9547\uff0c\u53c8\u662f\u957f\u6c5f\u7684\u8d27\u7269\u8fd0 \u9001\u96c6\u6563\u4e4b\u5730\uff0c\u8857\u9053\u5e02\u9762\u7684\u7e41\u534e\u70ed\u95f9\uff0c\u81ea\u662f\u4e0d\u51e1\uff0c\u8f9b\u6377\u5750\u5c45\u77f3\u5ba4\u5341\u5e74\uff0c\u6b64\u756a\u89c1\u5230\u8fd9\u82b1\u82b1\u4e16 \u754c\uff0c\u518d\u662f\u4fee\u4e3a\u9ad8\u6df1\uff0c\u4e5f\u9ad8\u5174\u5f97\u5f88\u3002 \u3000\u3000\u4e24\u4eba\u968f\u610f\u5728\u9152\u697c\u4e2d\u7528\u4e86\u4e9b\u9152\u83dc\uff0c\u4fbf\u56de\u8f6c\u5e97\u91cc\uff0c\u5e97\u4f19\u89c1\u5230\u5e97\u4e1c\u56de\u6765\u4e86\uff0c\u5df4\u7ed3\u5730\u8fce\u4e86\u4e0a \u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8001\u7237\u56de\u6765\u4e86\u3002\u201d\u8f9b\u6377\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u90a3\u5e97\u4f19\u8bf4\u9053\uff1a\u201c\u521a\u624d\u6709\u4e24\u4f4d\u5ba2\u4eba\u6765\u8bbf\u8001\u7237\uff0c\u4e00\u4f4d\u59d3\u5b5f\uff0c\u4e00\u4f4d\u59d3\u8303\uff0c\u5c0f\u7684\u8ba4\u5f97\u662f\u57ce\u91cc \u6709\u540d\u7684\u5927\u9547\u5934\uff0c\u4fbf\u62db\u5f85\u4e24\u4f4d\u8fdb\u53bb\u4e86\uff0c\u6b64\u523b\u8fd8\u5728\u91cc\u9762\u5462\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u4e86\u7b11\uff0c\u626d\u5934\u5411\u4e8e\u4e00\u98de\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u8303\u9547\u5934\u548c\u5b5f\u9547\u5934\u4eca\u65e5\u5c31\u6765\u56de\u62dc\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7740\u4e0e\u4e8e\u4e00\u98de\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\u4e00\u89c1\u4ed6\u4e24\u4eba\u8d70\u4e86\u8fdb\u6765\uff0c\u54c8\u54c8\u7b11\u7740\u8bf4\uff1a\u201c\u4e24\u4f4d\u5012\u771f\u662f\u597d\u96c5\u5174\uff0c\u8fd9\u4e48\u4e00 \u5927\u65e9\u5c31\u8dd1\u51fa\u53bb\u901b\u8857\uff0c\u53ef\u662f\u5230\u51e4\u6797\u73ed\u53bb\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u8303\u5144\u4f11\u5f97\u53d6\u7b11\uff0c\u5012\u662f\u4ee4\u4e24\u4f4d\u4e45\u7b49\u4e86\uff0c\u5c0f\u5f1f\u5b9e\u662f\u4e0d\u5b89\u5f97\u5f88\u3002\u201d \u3000\u3000\u56db\u4eba\u53c8\u53d6\u7b11\u4e86\u4e00\u9635\uff0c\u94f6\u67aa\u5b5f\u4f2f\u8d77\u7a81\u5bf9\u4e8e\u4e00\u98de\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u6211\u7b49\u524d\u6765\uff0c\u9664\u4e86\u56de\u62dc\u8f9b\u5144 \u4e4b\u5916\uff0c\u8fd8\u6709\u4e86\u4ef6\u5927\u4e8b\u8981\u8bf4\u4e0e\u4e8e\u5144\u77e5\u9053\u2026\u2026\u201d \u3000\u3000\u5b5f\u4f2f\u8d77\u9053\uff1a\u201c\u90a3\u5341\u5e74\u524d\u6c5f\u6e56\u4e0a\u7684\u5947\u4eba\u2018\u4e03\u5999\u795e\u541b\u2019\u6628\u665a\u53c8\u7a81\u5728\u6b66\u5f53\u73b0\u8eab\u4e86\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u542c\u4e86\uff0c\u8138\u8272\u4e00\u53d8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6050\u6015\u4e0d\u53ef\u80fd\u5427\uff01\u636e\u5bb6\u5e08\u66fe\u5411\u5c0f\u5f1f\u8a00\u53ca\uff0c\u5341\u5e74\u524d \u5728\u4e94\u534e\u5c71\u91cc\uff0c\u4e03\u5999\u795e\u541b\u4e2d\u4e86\u5bb6\u5e08\u4e00\u638c\uff0c\u53c8\u88ab\u70b9\u82cd\u7684\u638c\u95e8\u4eba\u4ee5\u4e03\u7edd\u91cd\u624b\u70b9\u4e86\u4e24\u5904\u7a74\u9053\uff0c\u7109 \u80fd\u6d3b\u5230\u4eca\u65e5\uff1f\u201d \u3000\u3000\u5b5f\u4f2f\u8d77\u9053\uff1a\u201c\u6b64\u8bdd\u662f\u5343\u771f\u4e07\u786e\uff0c\u5c0f\u5f1f\u6709\u4e2a\u81f3\u53cb\uff0c\u53eb\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\uff0c\u4e8e\u5144\u60f3\u5fc5\u4e5f\u77e5 \u6b64\u4eba\uff0c\u6628\u591c\u5c31\u66fe\u4eb2\u773c\u770b\u5230\u4e03\u5999\u795e\u541b\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u8138\u8272\u53d8\u5f97\u66f4\u96be\u770b\uff0c\u8f9b\u6377\u5374\u5750\u5728\u4e0b\u65c1\uff0c\u4f5c\u51fa\u7559\u610f\u503e\u542c\u7684\u6837\u5b50\u3002 \u3000\u3000\u5b5f\u4f2f\u8d77\u53c8\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5b59\u5144\u8d85\u8fdc\u4eca\u65e5\u6e05\u6668\u4fbf\u6765\u5230\u5c0f\u5f1f\u5904\uff0c\u544a\u8bc9\u5c0f\u5f1f\u6b64\u4e8b\uff0c\u5e76\u53eb\u5c0f\u5f1f \u8fd9\u51e0\u65e5\u8981\u7279\u5730\u7559\u795e\uff0c\u8bf4\u662f\u773c\u770b\u6c5f\u6e56\u4e2d\u5c31\u8981\u751f\u51fa\u98ce\u6ce2\u5462\u3002\u201d \u3000\u3000\u91d1\u5f13\u795e\u5f39\u5728\u65c1\u63a5\u53e3\u9053\uff1a\u201c\u5176\u5b9e\u5b5f\u5144\u4e5f\u662f\u592a\u591a\u8651\u4e86\uff0c\u518d\u5927\u7684\u98ce\u6ce2\uff0c\u4e5f\u60f9\u4e0d\u5230\u4f60\u3001\u6211\u7684 \u5934\u4e0a\uff0c\u5c31\u8ba9\u4ed6\u4e24\u62fc\u4e2a\u6027\u8d1f\uff0c\u53c8\u5173\u4f60\u3001\u6211\u751a\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u4f5c\u51fa\u832b\u7136\u4e4b\u6001\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u66fe\u542c\u8bf4\u8fc7\u6b66\u6797\u4e2d\u6709\u4e2a\u5947\u4eba\u2018\u4e03\u5999\u795e\u541b\u2019\uff0c \u6b66\u529f\u51a0\u7edd\u5929\u4e0b\uff0c\u5374\u53c8\u6709\u4f55\u4eba\u80fd\u4e0e\u4ed6\u4e00\u62fc\u80dc\u8d1f\u5462\uff1f\u201d \u3000\u3000\u8303\u6cbb\u6210\u9053\uff1a\u201c\u8bf4\u8d77\u6b64\u4eba\u6765\uff0c\u8fd1\u65e5\u6c5f\u6e56\u4e0a\u771f\u662f\u8c08\u864e\u8272\u53d8\uff0c\u5927\u5bb6\u53ea\u6653\u4ed6\u59d3\u91d1\u540d\u6b39\uff0c\u6709 \u2018\u5929\u9b54\u2019\u4e4b\u79f0\uff0c\u5374\u65e0\u4eba\u77e5\u4ed6\u5e08\u627f\u6765\u5386\uff0c\u4ed6\u51fa\u9053\u6c5f\u6e56\u624d\u53ea\u6570\u5e74\uff0c\u4fbf\u5df2\u505a\u51fa\u51e0\u4ef6\u60ca\u4eba\u4e4b\u4e8b\uff0c \u636e\u8bf4\u975e\u4f46\u6b66\u529f\u4e4b\u9ad8\uff0c\u4e0d\u53ef\u601d\u8bae\uff0c\u800c\u4e14\u624b\u6bb5\u4e4b\u6bd2\u8fa3\uff0c\u66f4\u662f\u532a\u5937\u6240\u601d\uff0c\u4e24\u6cb3\u4e2d\u6b66\u6797\u7684\u76df\u4e3b \u2018\u516b\u5c01\u6e38\u8eab\u638c\u80e1\u5927\u4e4b\u2019\u4e0d\u77e5\u600e\u5730\u5f97\u7f6a\u4e86\u4ed6\uff0c\u7adf\u88ab\u4ed6\u5355\u4eba\u5339\u9a6c\uff0c\u4e00\u591c\u4e4b\u95f4\u5c06\u6ee1\u95e8\u6740\u5f97\u5e72\u5e72 \u51c0\u51c0\uff0c\u5f53\u65f6\u8fd8\u6709\u5317\u65b9\u77e5\u540d\u7684\u5251\u5ba2\u2018\u516b\u6b65\u8d76\u8749\u53e4\u5c14\u5251\u2019\u2018\u4e94\u864e\u65ad\u95e8\u5200\u5f6d\u5929\u65d7\u2019\u5728\u573a\uff0c\u4f46\u8fd9 \u4e09\u4f4d\u8d6b\u8d6b\u6709\u540d\u7684\u6b66\u5e08\uff0c\u7adf\u672a\u80fd\u654c\u8fc7\u4ed6\u4e00\u4eba\uff0c\u5168\u906d\u4e86\u6bd2\u624b\uff0c\u8fd9\u6b21\u4e03\u5999\u795e\u541b\u593a\u4e86\u4ed6\u7684\u5973\u5b50\uff0c \u4ed6\u5c82\u80af\u7f62\u4f11\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u54e6\u4e86\u4e00\u58f0\uff0c\u5411\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u6628\u591c\u90a3\u5973\u5b50\uff0c\u7adf\u60f9\u5f97\u4e03\u5999\u795e\u541b\u4e5f\u52a8\u4e86\u624b\u3002\u201d \u3000\u3000\u4ed6\u6c89\u541f\u4e86\u534a\u54cd\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u6b64\u6b21\u4e03\u5999\u795e\u541b\u91cd\u4eba\u6c5f\u6e56\uff0c\u5012\u7684\u786e\u662f\u4ef6\u5927\u4e8b\uff0c\u5c0f\u5f1f\u5f85\u6b64\u95f4 \u4e8b\u4e86\uff0c\u4fbf\u7acb\u523b\u8981\u8fd4\u56de\u5d06\u5cd2\uff0c\u7980\u62a5\u5bb6\u5e08\uff0c\u5929\u9b54\u91d1\u6b39\u548c\u4e03\u5999\u795e\u541b\u7684\u70ed\u95f9\u518d\u597d\u770b\uff0c\u5c0f\u5f1f\u4e5f\u65e0\u5fc3 \u770b\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u6697\u9a82\u4e86\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u8981\u770b\u6211\u7684\u70ed\u95f9\uff0c\u5c82\u4e0d\u77e5\u4f60\u81ea\u5df1\u7684\u70ed\u95f9\u66f4\u597d\u770b\u5462\uff1f\u201d \u3000\u3000\u94f6\u67aa\u5b5f\u4f2f\u8d77\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u6b66\u6797\u4e2d\u5e73\u9759\u4e86\u5c06\u8fd1\u5341\u5e74\uff0c\u6211\u5c31\u77e5\u9053\u5fc5\u662f\u5e7f\u573a\u5927\u98ce \u66b4\u7684\u524d\u594f\uff0c\u679c\u4e0d\u5176\u7136\uff0c\u4e4d\u770b\u6c5f\u6e56\u4e2d\u53c8\u5c06\u662f\u4e00\u756a\u8165\u98ce\u8840\u96e8\uff0c\u4e2d\u539f\u4e94\u5927\u6b66\u6797\u5b97\u6d3e\uff0c\u81ea\u8eab\u5c31\u6709 \u4e86\u7ea0\u7eb7\uff0c\u73b0\u5728\u4e03\u5999\u795e\u541b\u53c8\u91cd\u4eba\u6c5f\u6e56\uff0c\u518d\u52a0\u4e0a\u5929\u9b54\u91d1\u6b39\uff0c\u5509\uff01\u201d \u3000\u3000\u91d1\u5f13\u795e\u5f39\u4e5f\u6101\u5bb9\u6ee1\u9762\u5730\u8bf4\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u7684\u6df7\u4e71\u5c1a\u4e0d\u6b62\u6b64\u5462\uff0c\u6614\u5e74\u5173\u4e2d\u4e5d\u8c6a\u4e4b\u9996\uff0c \u2018\u6d77\u5929\u53cc\u715e\u2019\u5929\u6b8b\u3001\u5929\u5e9f\u5144\u5f1f\uff0c\u636e\u8bf4\u4e5f\u9759\u6781\u601d\u52a8\uff0c\u60f3\u91cd\u632f\u58f0\u5a01\uff0c\u6211\u4eec\u9556\u5c40\u8fd9\u884c\u996d\u672c\u5df2\u662f \u5728\u5200\u53e3\u8210\u8840\u5403\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u8fd9\u884c\u996d\u773c\u770b\u662f\u5403\u4e0d\u4e0b\u53bb\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u542c\u5230\u201c\u6d77\u5929\u53cc\u715e\u201d\u56db\u5b57\uff0c\u6d51\u8eab\u4e00\u9707\uff0c\u5e78\u597d\u4ed6\u4e09\u4eba\u6b63\u5728\u5404\u81ea\u60f3\u7740\u5fc3\u4e8b\uff0c\u5e76\u6ca1\u6709\u6ce8 \u610f\u5230\u4ed6\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u4ed6\u8bf4\u9053\uff1a\u201c\u90a3\u6d77\u5929\u53cc\u715e\u771f\u7684\u4e5f\u8981\u91cd\u4eba\u6c5f\u6e56\u5417\uff1f\u201d \u3000\u3000\u91d1\u5f13\u795e\u5f39\u5947\u602a\u5730\u671b\u4e86\u4ed6\u4e00\u773c\u8bf4\u9053\uff1a\u201c\u8f9b\u5144\u5bf9\u6b66\u6797\u4eba\u7269\uff0c\u600e\u5730\u77e5\u9053\u5982\u6b64\u6e05\u695a\uff0c\u4e0d\u8fc7\u5e78 \u597d\u8f9b\u5144\u5c1a\u975e\u6b66\u6797\u4e2d\u4eba\uff0c\u6c5f\u6e56\u4e0a\u7684\u98ce\u6ce2\u518d\u5927\uff0c\u4e5f\u4e0d\u4f1a\u7f20\u5230\u8f9b\u5144\u5934\u4e0a\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u4e86\u7b11\uff0c\u5f53\u7136\u4ed6\u4eec\u4e0d\u4f1a\u53d1\u89c9\u4ed6\u7b11\u58f0\u7684\u5f02\u6837\u3002 \u3000\u3000\u4e09\u65e5\u540e\uff0c\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4e00\u4eba\u9ed1\uff0c\u5c31\u9759\u5750\u623f\u91cc\uff0c\u8c03\u606f\u8fd0\u529f\u3002\u8f9b\u6377\u89c1\u4e86\uff0c\u4e0d\u7981\u6697\u81ea\u70b9\u5934\uff0c \u5fd6\u9053\uff1a\u201c\u96be\u602a\u8fd9\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u540d\u6ee1\u6c5f\u6e56\uff0c\u4ed6\u4eba\u867d\u9a84\u72c2\uff0c\u4f46\u9047\u7740\u771f\u6b63\u5f3a\u654c\uff0c\u5374\u4e00\u70b9\u4e5f\u4e0d\u9a6c \u864e\u3002\u201d \u3000\u3000\u79bb\u5b50\u591c\u8fd8\u6709\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e8e\u4e00\u98de\u6536\u62fe\u59a5\u5f53\uff0c\u5c06\u957f\u5251\u7d27\u5bc6\u800c\u59a5\u5f53\u5730\u659c\u80cc\u5728\u8eab\u540e\uff0c\u8bd5\u4e86\u8bd5 \u5bf9\u52a8\u624b\u6beb\u65e0\u59a8\u788d\uff0c\u624d\u8d70\u51fa\u623f\u95f4\u3002 \u3000\u3000\u8f9b\u6377\u6b63\u5f98\u5f8a\u5728\u9662\u5b50\u91cc\u7b49\u4ed6\uff0c\u6708\u5149\u751a\u660e\uff0c\u6b64\u65f6\u6708\u6b63\u4e2d\u5929\uff0c\u4e8e\u4e00\u98de\u8d70\u51fa\u9662\u5b50\u540e\uff0c\u89c1\u8f9b\u6377 \u4ecd\u5728\u5f98\u5f8a\uff0c\u95ee\u9053\uff1a\u201c\u8f9b\u5144\u4f55\u4e0d\u65e9\u4e9b\u5b89\u6b47\uff1f\u5c0f\u5f1f\u6b64\u53bb\uff0c\u8c05\u4e0d\u81f4\u6709\u4f55\u5dee\u9519\uff0c\u8f9b\u5144\u653e\u5fc3\u597d\u3002\u201d \u3000\u3000\u8f9b\u6377\u6697\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u5012\u662f\u4e2a\u76f4\u80a0\u6c49\u5b50\uff0c\u8fd8\u5728\u4ee5\u4e3a\u6211\u5173\u5fc3\u4ed6\u3002\u201d \u3000\u3000\u6b64\u5ff5\u4e00\u751f\uff0c\u65e5\u540e\u4e8e\u4e00\u98de\u771f\u7684\u5f97\u4e86\u4e0d\u5c11\u597d\u5904\uff0c\u5374\u975e\u4e8e\u4e00\u98de\u6240\u80fd\u6599\u60f3\u5230\u7684\u3002 \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u4e8e\u5144\u96be\u9053\u4e0d\u77e5\u5c0f\u5f1f\u6700\u662f\u597d\u6b66\uff0c\u6709\u8fd9\u7b49\u70ed\u95f9\u573a\u9762\uff0c\u5c0f\u5f1f\u7109\u6709\u4e0d\u53bb\u4e4b\u7406\uff1f\u201d \u3000\u3000\u4e8e\u4e00\u98de\u6447\u624b\u9053\uff1a\u201c\u8f9b\u5144\u53ef\u53bb\u4e0d\u5f97\uff0c\u8bd5\u60f3\u8f9b\u5144\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\uff0c\u5230\u4e86\u90a3\u7b49\u51f6\u6740\u4e4b\u6240\uff0c\u4e07 \u4e00\u5c0f\u5f1f\u4e00\u4e2a\u7167\u6599\u4e0d\u53ca\uff0c\u6559\u522b\u4eba\u4f24\u4e86\u8f9b\u5144\u5343\u91d1\u4e4b\u8eaf\uff0c\u8fd9\u5929\u5927\u7684\u62c5\u5b50\uff0c\u5c0f\u5f1f\u4e07\u4e07\u8d1f\u4e0d\u8d77\u3002\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u5c31\u662f\u4e8e\u5144\u4e0d\u5e26\u5c0f\u5f1f\u53bb\uff0c\u5c0f\u5f1f\u4e5f\u8981\u968f\u540e\u8d76\u53bb\u7684\uff0c\u90a3\u4e9b\u4eba\u4e0e\u5c0f\u5f1f\u65e0\u6028\u65e0\u4ec7\uff0c \u53c8\u600e\u4f1a\u5bf9\u5c0f\u5f1f\u5982\u4f55\u5462\uff1f\u201d \u3000\u3000\u4e8e\u4e00\u98de\u53f9\u9053\uff1a\u201c\u8f9b\u5144\u65e2\u662f\u6267\u610f\u5982\u6b64\uff0c\u5c0f\u5f1f\u4e5f\u65e0\u6cd5\u529d\u6b62\uff0c\u53ea\u662f\u5230\u65f6\u8f9b\u5144\u5207\u8bb0\u4e0d\u8981\u4e71\u52a8\uff0c \u7ad9\u5728\u4e00\u65c1\u770b\u770b\uff0c\u4e5f\u5e76\u975e\u4e0d\u53ef\u3002\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u5f1f\u7406\u4f1a\u5f97\u3002\u201d \u3000\u3000\u4e24\u4eba\u98de\u8f66\u8d76\u5230\u5cb8\u8fb9\uff0c\u8f9b\u6377\u65e9\u5df2\u5907\u597d\u6e21\u8239\uff0c\u6e21\u81f3\u5bf9\u5cb8\u65f6\uff0c\u521a\u597d\u662f\u5b50\u6b63\u4e4b\u65f6\u3002 \u3000\u3000\u9ec4\u9e64\u697c\u672c\u5728\u6e21\u5934\u4e4b\u65c1\uff0c\u697c\u4e0b\u4e00\u7247\u7a7a\u5730\uff0c\u672c\u662f\u65e5\u95f4\u644a\u8d29\u7fa4\u96c6\u4e4b\u5904\uff0c\u4f46\u6b64\u65f6\u5df2\u662f\u5b50\u591c\uff0c \u7a7a\u8361\u8361\u5730\u65e9\u65e0\u4eba\u8ff9\uff0c\u4e8e\u4e00\u98de\u5947\u602a\u9053\uff1a\u201c\u600e\u5730\u6b66\u5f53\u95e8\u4e0b\uff0c\u8fd8\u65e0\u4e00\u4eba\u524d\u6765\uff0c\u4ed6\u4eec\u7684\u67b6\u5b50\uff0c\u4e5f \u672a\u514d\u592a\u5927\u4e86\u4e9b\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u4e43\u5c45\u4e2d\u539f\u6b66\u6797\u5404\u6d3e\u76df\u5ea7\uff0c\u6c14\u6d3e\u81ea\u7136\u4e0d\u540c\u4e86\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u54fc\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5bf9\u6b66\u5f53\u6d3e\uff0c\u53c8\u52a0\u6df1\u4e86\u4e00\u5206\u82a5\u8482\u3002 \u3000\u3000\u4e24\u4eba\u6b63\u5728\u7b49\u5f97\u5fc3\u7126\uff0c\u8f9b\u6377\u7a81\u7136\u671b\u89c1\u8fdc\u5904\u6162\u65bd\u65bd\u8d70\u6765\u4e09\u4eba\uff0c\u8131\u53e3\u8bf4\u9053\uff1a\u201c\u6765\u4e86\uff0c\u6765 \u4e86\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u968f\u58f0\u671b\u53bb\uff0c\u4e5f\u5df2\u53d1\u73b0\uff0c\u4ed6\u53ef\u5e76\u672a\u7ec6\u8651\u4e3a\u4f55\u8f9b\u6377\u7684\u76ee\u5149\u6bd4\u4ed6\u5feb\u3002 \u3000\u3000\u90a3\u4e09\u4eba\u60f3\u662f\u4e5f\u671b\u89c1\u4e24\u4eba\uff0c\u8eab\u5f62\u8d77\u5904\uff0c\u5982\u98de\u800c\u6765\uff0c\u4ed6\u4eec\u76f8\u8ddd\u539f\u4e0d\u751a\u8fdc\uff0c\u6643\u773c\u4fbf\u6765\u5230\u8fd1 \u524d\uff0c\u4e8e\u4e00\u98de\u4e00\u770b\u5f53\u5148\u4e00\u4eba\u7adf\u662f\u6b66\u5f53\u6d3e\u540e\u8d77\u7fa4\u5251\u4e2d\u6700\u6770\u51fa\u7684\u4e00\u4eba\uff0c\u795e\u9e64\u8a79\u5e73\uff0c\u7b2c\u4e8c\u4eba\u5374\u662f \u6b66\u5f53\u7684\u638c\u95e8\u9996\u5f92\u51cc\u98ce\u5251\u5ba2\u3002 \u3000\u3000\u90a3\u6700\u540e\u4e00\u4eba\uff0c\u81ea\u662f\u60f9\u7978\u7684\u6839\u82d7\u4e5d\u5bab\u5251\u674e\u6cbb\u534e\u4e86\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u4eca\u65e5\u5374\u60f3\u4e0d\u5230\u662f\u795e\u9e64\u8a79\u5e73\u548c\u51cc\u98ce\u5251\u5ba2\u9f50\u6765\u3002\u4ed6\u4e8c\u4eba\u636e\u8bf4 \u662f\u6b66\u5f53\u7b2c\u4e8c\u4ee3\u7684\u6700\u6770\u51fa\u7684\u9ad8\u624b\uff0c\u82e5\u662f\u52a8\u8d77\u624b\u6765\uff0c\u6211\u62b5\u6321\u4e00\u4eba\uff0c\u6599\u8fd8\u4e0d\u81f3\u6709\u5dee\uff0c\u82e5\u662f\u4ed6\u4e24 \u4eba\u9f50\u4e0a\uff0c\u90a3\u5c31\u96be\u8bf4\u4e86\u3002\u201d \u3000\u3000\u4ed6\u90a3\u91cc\u77e5\u9053\uff0c\u8fd9\u51cc\u98ce\u5251\u4e0e\u795e\u9e64\u8a79\u5e73\u6b64\u6765\uff0c\u5374\u662f\u7acb\u4e0b\u51b3\u5fc3\u8981\u5c06\u5730\u7edd\u5251\u6298\u8fb1\u4e00\u756a\u7684\u3002 \u3000\u3000\u8fd1\u5e74\u6b66\u5f53\u6d3e\u867d\u4ecd\u6267\u4e2d\u539f\u6b66\u6797\u4e2d\u5404\u6d3e\u7684\u725b\u8033\uff0c\u4f46\u5b9e\u9645\u4e0a\uff0c\u5d06\u5cd2\u6d3e\u81ea\u638c\u95e8\u4eba\u5251\u795e\u5389\u9e57\u5728 \u6cf0\u6b64\u7edd\u9876\u8fde\u8d25\u5341\u4e00\u4e2a\u5bf9\u5bb6\u540d\u5251\u624b\u800c\u53d6\u5f97\u2018\u5929\u4e0b\u7b2c\u5341\u5251\u2019\u7684\u540d\u53f7\u540e\uff0c\u58f0\u52bf\u5728\u8bb8\u591a\u5730\u65b9\u5df2\u51cc \u9a7e\u6b66\u5f53\u4e4b\u4e0a\u3002 \u3000\u3000\u662f\u4ee5\u6b66\u5f53\u5d06\u5cd2\u4e24\u6d3e\uff0c\u65e0\u5f62\u4e2d\u9020\u6210\u4e00\u79cd\u4e92\u76f8\u5fcc\u6068\u7684\u5c40\u52bf\uff0c\u5d06\u5cd2\u81ea\u662f\u4e0d\u6ee1\u6b66\u5f53\u7684\u4ecd\u5904\u5904 \u4ee5\u201c\u5185\u5bb6\u6b63\u5b97\u3001\u6b66\u6797\u5404\u6d3e\u4e4b\u9996\u201d\u6765\u6807\u699c\uff0c\u800c\u6b66\u5f53\u5374\u4e5f\u5bf9\u5d06\u5cd2\u8fd1\u5e74\u6765\u5728\u6c5f\u6e56\u6b62\u65e5\u76ca\u8dcb\u6248\u751a \u4e3a\u5fcc\u6068\u3002 \u3000\u3000\u4e24\u6d3e\u7684\u5acc\u9699\u7531\u6765\u5df1\u4e45\uff0c\u4f46\u5374\u59cb\u7ec8\u788d\u7740\u9762\u5b50\uff0c\u53c8\u65e0\u5bfc\u706b\u4e4b\u7ebf\uff0c\u603b\u7b97\u672a\u66fe\u7834\u8138\u3002 \u3000\u3000\u6b66\u5f53\u6d3e\u91cc\uff0c\u5c24\u5176\u4ee5\u795e\u9e64\u8a79\u5e73\u6700\u662f\u50b2\u67b6\u4e0d\u7fa4\uff0c\u4ed6\u5929\u8d4b\u9887\u4f73\uff0c\u4eba\u53c8\u7528\u529f\uff0c\u5e74\u7eaa\u867d\u5c0f\uff0c\u5df2 \u5c3d\u5f97\u6b66\u5f53\u771f\u4f20\uff0c\u65f6\u65f6\u523b\u523b\u90fd\u60f3\u505a\u4e00\u4e9b\u60ca\u5929\u52a8\u5730\u7684\u4e8b\uff0c\u4e00\u6765\u66ff\u81ea\u5df1\u626c\u540d\u7acb\u4e07\uff0c\u4e8c\u6765\u4e5f\u662f\u60f3 \u632f\u6b66\u5f53\u6d3e\u7684\u5a01\u98ce\u3002 \u3000\u3000\u800c\u70b9\u82cd\u3001\u5ce8\u5d4b\u3001\u5d06\u5cd2\u4e09\u6d3e\uff0c\u5404\u62e5\u79d8\u6280\uff0c\u4f55\u5c1d\u4e0d\u60f3\u505a\u4e00\u4e2a\u9886\u8896\uff0c\u6b66\u6797\u7684\u5b97\u6d3e\uff0c\u4e5f\u65f6\u65f6 \u90fd\u5728\u4f3a\u673a\u800c\u52a8\uff0c\u53ea\u82e6\u4e8e\u65f6\u673a\u672a\u5230\u800c\u5df2\u3002 \u3000\u3000\u6885\u5c71\u6c11\u867d\u5341\u5e74\u6765\u8db3\u672a\u51fa\u6237\uff0c\u4f46\u6b66\u6797\u4e2d\u8fd9\u79cd\u5fae\u5999\u7684\u5c40\u52bf\uff0c\u600e\u80fd\u7792\u5f97\u4e86\u4ed6\u3002 \u3000\u3000\u4ed6\u5bf9\u8fd9\u4e94\u5927\u5b97\u6d3e\uff0c\u6028\u6bd2\u81ea\u6df1\uff0c\u8f9b\u6377\u6280\u6210\u540e\uff0c\u4ed6\u5f53\u7136\u60f3\u8f9b\u6377\u66ff\u81ea\u5df1\u62a5\u90a3\u4e94\u534e\u5c71\u91cc\u6697\u7b97 \u4e4b\u4ec7\uff0c\u4f46\u4ed6\u5374\u77e5\u9053\u5355\u51ed\u8f9b\u6377\u4e00\u4eba\u4e4b\u529b\uff0c\u8981\uff0c\u60f3\u5bf9\u4ed8\u5728\u6b66\u6797\u4e2d\u6839\u6df1\u8482\u56fa\u7684\u201c\u4e94\u5927\u5b97\u6d3e\u201d\u5b9e \u4e0d\u53ef\u80fd\u3002\u4ed6\u8fd9\u624d\u6388\u8ba1\u8f9b\u6377\u3002\u8ba9\u4e94\u5927\u5b97\u6d3e\u81ea\u76f8\u6b8b\u6740\uff0c\u7136\u540e\u518d\u9010\u4e00\u51fb\u7834\u3002 \u3000\u3000\u6885\u5c71\u6c11\u751f\u6027\u672c\u5c31\u5947\u50fb\uff0c\u6563\u529f\u540e\u66f4\u53d8\u5f97\u5bf9\u6b64\u4e8b\u62b1\u7740\u504f\u6fc0\u7684\u770b\u6cd5\uff0c\u662f\u4ee5\u4ed6\u7edd\u4e0d\u53bb\u60f3\u8fd9\u6837 \u4e00\u6765\uff0c\u6b66\u6797\u4e2d\u66f4\u751f\u51fa\u4f55\u7b49\u98ce\u6ce2\uff0c\u6709\u591a\u5c11\u4eba\u5c06\u8981\u56e0\u6b64\u800c\u4e27\u547d\uff0c\u4f55\u51b5\u8f9b\u6377\u5e7c\u906d\u5b64\u72ec\uff0c\u5bf9\u4eba\u4e16 \u4e5f\u62b1\u7740\u5947\u50fb\u7684\u770b\u6cd5\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u89c1\u51cc\u98ce\u5251\u5ba2\uff0c\u795e\u9e64\u8a79\u5e73\u53ca\u4e5d\u5bab\u5251\u6765\u5230\u8fd1\u524d\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5509\u54ce\uff0c\u60f3\u4e0d \u5230\uff0c\u60f3\u4e0d\u5230\uff0c\u4e8e\u4e00\u98de\u533a\u533a\u4e00\u4e2a\u6b66\u6797\u5c0f\u5352\uff0c\u5374\u52b3\u52a8\u4e86\u51cc\u98ce\u5251\u5ba2\u4e0e\u795e\u9e64\u8a79\u5927\u4fa0\u4e24\u4f4d\u7684\u5927\u9a7e\u3002\u201d \u3000\u3000\u795e\u9e64\u8a79\u5e73\u4e0d\u5f85\u638c\u95e8\u5e08\u5144\u53d1\u8bdd\uff0c\u53cd\u5507\u9053\uff1a\u201c\u5d06\u5cd2\u4e09\u7edd\u5251\u540d\u6ee1\u6c5f\u6e56\uff0c\u90a3\u91cc\u4f1a\u5c06\u6211\u7b49\u6b66\u5f53 \u6d3e\u653e\u5728\u773c\u4e0b\uff0c\u5728\u4e0b\u542c\u5e08\u5f1f\u56de\u6765\u4e00\u8bf4\uff0c\u867d\u7136\u660e\u77e5\u51ed\u6211\u4eec\u8fd9\u4e24\u624b\u4e09\u811a\u732b\u7684\u5251\u6cd5\uff0c\u4e07\u4e07\u4e0d\u662f\u5d06 \u5cd2\u5251\u5ba2\u7684\u654c\u624b\uff0c\u4f46\u6211\u8a79\u67d0\u4eba\u81ea\u4e0d\u91cf\u529b\uff0c\u5374\u8981\u6765\u8ba8\u6559\u4e8e\u5927\u4fa0\u7684\u9ad8\u62db\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u671b\u4e86\u5728\u65c1\u9634\u7b11\u7740\u7684\u4e5d\u5bab\u5251\u674e\u6cbb\u534e\u4e00\u773c\uff0c\u77e5\u9053\u4ed6\u4e0d\u5b9a\u53c8\u5728\u4ed6\u4eec\u9762\u524d\u8bf4\u4e86\u4ec0\u4e48\u66f4 \u96be\u542c\u7684\u8bdd\uff0c\u4f46\u4ed6\u5fc3\u9ad8\u5fd7\u50b2\uff0c\u6b63\u60f3\u627e\u6b66\u5f53\u6d3e\u7684\u5c94\u5b50\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u6b63\u4e2d\u4e0b\u6000\uff0c\u662f\u4ee5\u51b7\u51b7\u8bf4\u9053\uff1a \u201c\u8a79\u5927\u4fa0\u771f\u662f\u592a\u5ba2\u6c14\u4e86\uff0c\u5728\u4e0b\u62d9\u4e8e\u8a00\u8f9e\uff0c\u771f\u4e0d\u4f55\u8bf4\u4ec0\u4e48\u597d\uff0c\u53ea\u597d\u5728\u624b\u5e95\u4e0b\u8ba8\u6559\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u756a\u8bdd\u65e0\u5f02\u8bf4\u6211\u8bdd\u8bb2\u4e0d\u8fc7\u4f60\uff0c\u4f46\u624b\u5e95\u4e0b\u53ef\u4e0d\u542b\u7cca\uff0c\u51cc\u98ce\u5251\u5ba2\u3001\u795e\u9e64\u8a79\u5e73\uff0c\u90fd\u662f\u4e45 \u8d70\u6c5f\u6e56\u7cbe\u660e\u5f3a\u5e72\u7684\u89d2\u8272\uff0c\u5c82\u6709\u542c\u4e0d\u51fa\u7684\u9053\u7406\u3002 \u3000\u3000\u51cc\u98ce\u5251\u5ba2\u51b7\u7b11\u9053\uff1a\u201c\u4e8e\u5927\u4fa0\u771f\u662f\u5feb\u8bed\uff0c\u8fd9\u6837\u518d\u597d\u4e0d\u8fc7\u4e86\u3002\u201d\u4ed6\u4fa7\u8eab\u4e00\u671b\u8f9b\u6377\uff0c\u8bf4\u9053\uff1a \u201c\u8fd9\u4f4d\u662f\u2026\u2026\u201d \u3000\u3000\u4e8e\u4e00\u98de\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u655d\u53cb\u8f9b\u6377\uff0c\u4e45\u4ef0\u6b66\u5f53\u5251\u6cd5\uff0c\u7279\u6765\u77bb\u4ef0\u77bb\u4ef0\u7684\u3002\u201d \u3000\u3000\u4e5d\u5bab\u5251\u674e\u6cbb\u534e\u62a2\u7740\u9053\uff1a\u201c\u8fd9\u4f4d\u5c31\u662f\u6211\u66fe\u5411\u5e08\u5144\u63d0\u53ca\u7684\u8f9b\u8001\u677f\u3002\u201d \u3000\u3000\u51cc\u98ce\u5251\u5ba2\u54e6\u4e86\u4e00\u58f0\uff0c\u4e0a\u4e0b\u6253\u91cf\u4e86\u8f9b\u6377\u51e0\u773c\uff0c\u542b\u7b11\u671d\u8f9b\u6377\u5fae\u4e00\u62b1\u62f3\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u5fd9\u7b11\u7740\u7b54\u793c\u3002 \u3000\u3000\u795e\u9e64\u78e8\u5e73\u5e7f\u63a0\u81f3\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u5728\u4e0b\u5c31\u5148\u9886\u6559\u4e8e\u5927\u4fa0\u51e0\u62db\u3002\u201d \u3000\u3000\u4e24\u4eba\u8868\u9762\u4e0a\u867d\u662f\u5ba2\u5ba2\u6c14\u6c14\uff0c\u4f46\u5fc3\u4e2d\u5404\u542b\u6740\u673a\uff0c\u90fd\u5b58\u5fc3\u5c06\u5bf9\u65b9\u6bc1\u5728\u5251\u4e0b\uff0c\u7edd\u4e0d\u662f\u6b66\u6797 \u4e2d\u8ba8\u6559\u8fc7\u62db\uff0c\u70b9\u5230\u4e3a\u6b62\u7684\u5fc3\u7406\u3002 \u3000\u3000\u662f\u4ee5\u4e24\u4eba\u66f4\u4e0d\u7b54\u8bdd\uff0c\u795e\u6c14\u5185\u655b\uff0c\u76ee\u6ce8\u5bf9\u65b9\uff0c\u90fd\u6015\u88ab\u5bf9\u65b9\u62a2\u4e86\u5148\u7740\u3002 \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u65e9\u5df2\u8fdc\u8fdc\u7ad9\u5f00\uff0c\u597d\u50cf\u751f\u6015\u5251\u5149\u4f1a\u843d\u5230\u81ea\u5df1\u5934\u4e0a\u4f3c\u7684\u3002 \u3000\u3000\u6b63\u503c\u6b64\u9645\uff0c\u5cb8\u8fb9\u7a81\u53c8\u98de\u8dd1\u6765\u51e0\u4eba\uff0c\u811a\u6b65\u4e0b\u4e5f\u53ef\u770b\u51fa\u529f\u592b\u4e0d\u5f31\u3002 \u3000\u3000\u795e\u9e64\u8a79\u5e73\u53d8\u8272\u95ee\u9053\uff1a\u201c\u4e8e\u5927\u4fa0\u5012\u8bf7\u4e86\u4e0d\u5c11\u5e2e\u624b\uff0c\u201d\u8bf4\u5b8c\u51b7\u7b11\u4e00\u58f0\u3002 \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4e5f\u81ea\u695e\u7136\uff0c\u51e0\u4eba\u8d70\u5230\u8fd1\u524d\uff0c\u4fbf\u505c\u4e0b\u4e86\uff0c\u7ad9\u5728\u4e00\u8fb9\uff0c\u4e5f\u4e0d\u8fc7\u6765\uff0c\u4e8e\u4e00\u98de \u4e00\u770b\uff0c\u5374\u662f\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\uff0c\u94f6\u67aa\u5b5f\u4f2f\u8d77\uff0c\u53ca\u51e0\u4e2a\u6b66\u6c49\u7684\u6210\u540d\u4eba\u7269\u3002 \u3000\u3000\u8fd9\u51e0\u4eba\u4e8e\u53cc\u65b9\u90fd\u662f\u7d20\u8bc6\uff0c\u5374\u53ea\u8fdc\u8fdc\u4e00\u62b1\u62f3\uff0c\u663e\u7136\u662f\u770b\u70ed\u95f9\u6765\u4e86\u3002 \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u5f97\u7406\u4e0d\u8ba9\u4eba\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4e8e\u67d0\u4eba\u867d\u4e0d\u6210\u624d\uff0c\u5374\u4e0d\u4f1a\u627e\u4e2a\u5e2e\u624b\u3002\u201d \u3000\u3000\u4ed6\u7684\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u6211\u4e8e\u4e00\u98de\u662f\u5355\u67aa\u5339\u9a6c\u800c\u6765\uff0c\u4f60\u4eec\u6765\u7684\u5374\u4e0d\u6b62\u4e00\u4eba\u3002 \u3000\u3000\u795e\u9e64\u8a79\u5e73\u51b7\u54fc\u4e00\u58f0\uff0c\u9762\u8272\u94c1\u9752\uff0c\u811a\u6b65\u4e00\u9519\uff0c\u53cd\u624b\u4e00\u63e1\uff0c\u5251\u5df2\u51fa\u5323\uff0c\u53f1\u9053\uff1a\u201c\u6709\u50ed \u4e86\uff0c\u201d\u655b\u968f\u8eab\u8d70\uff0c\u7a81\u5730\u8f7b\u7075\uff0c\u659c\u659c\u4e00\u5251\uff0c\u5e26\u8d77\u4e00\u6e9c\u9752\u5149\uff0c\u6781\u5feb\u5730\u76f4\u53d6\u4e8e\u4e00\u98de\u7684\u80a9\u80db\u4e4b\u5904\u3002 \u3000\u3000\u6b66\u5f53\u672c\u662f\u5185\u5bb6\u5251\u6cd5\uff0c\u5e76\u4e0d\u4ee5\u8f7b\u7075\u89c1\u957f\uff0c\u4f46\u795e\u9e64\u8a79\u5e73\u8fd9\u4e00\u5251\uff0c\u4e0d\u8fc7\u662f\u865a\u62db\u800c\u5df2\uff0c\u5e76\u6ca1 \u6709\u65bd\u5c55\u51fa\u6b66\u5f53\u5251\u6cd5\u4e2d\u7684\u7cbe\u5965\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u76ee\u6ce8\u5251\u5934\uff0c\u7b49\u5230\u5251\u5c16\u5df2\u582a\u582a\u5230\u4e86\u9762\u524d\uff0c\u624d\u731b\u7136\u4e00\u64a4\u6b65\uff0c\u811a\u8ddf\u534a\u65cb\uff0c\u5251\u5149\u4e00\u95ea\uff0c \u4e0d\u77e5\u4f55\u65f6\u5df2\u5c06\u957f\u5251\u64a4\u5728\u624b\u91cc\uff0c\u987a\u52bf\u4e00\u5251\uff0c\u4e00\u51fa\u624b\u4fbf\u662f\u5d06\u5cd2\u7684\u9547\u5c71\u5251\u6cd5\uff0c\u2018\u5c11\u9633\u4e5d\u4e00\u5f0f\u2019 \u91cc\u7684\u4e00\u62db\u2018\u98de\u9f99\u521d\u73b0\u2019\u5251\u5e26\u98ce\u96f7\uff0c\u663e\u89c1\u8fd9\u4e8e\u4e00\u98de\u5185\u529f\u9887\u6709\u706b\u5019\u3002 \u3000\u3000\u8fd9\u201c\u5c11\u9633\u4e5d\u4e00\u5f0f\u201d\u4e43\u662f\u5251\u795e\u5389\u9e57\u672c\u7740\u5d06\u5cd2\u539f\u6709\u7684\u5251\u6cd5\uff0c\u9510\u5316\u800c\u6210\uff0c\u5251\u795e\u5389\u9e57\u5341\u5e74\u524d \u5c31\u4ee5\u6b64\u5251\u6cd5\u53d6\u5f97\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u201d\u7684\u8854\u5934\uff0c\u626c\u540d\u5929\u4e0b\uff0c\u7531\u6b64\u53ef\u60f3\u6b64\u5251\u6cd5\u7684\u5a01\u529b\uff0c\u81ea\u662f\u4e0d\u51e1\u3002 \u3000\u3000\u201c\u884c\u5bb6\u4e00\u51fa\u624b\uff0c\u4fbf\u77e5\u6709\u6ca1\u6709\u201d\uff0c\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u5251\u5149\u4e00\u5806\uff0c\u795e\u9e64\u8a79\u5e73\u5c31\u77e5\u4eca\u65e5\u786e\u5b9e\u9047 \u5230\u4e86\u52b2\u654c\uff0c\u7a81\u5730\u6c89\u8098\u632b\u8155\uff0c\u53cd\u5251\u4e0a\u5f15\uff0c\u53bb\u524a\u4e8e\u4e00\u98de\u7684\u624b\u8155\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u8fde\u524a\u5e26\u6253\uff0c\u5374\u53c8\u4e0d\u9732\u950b\u8292\uff0c\u6b63\u662f\u6b66\u5f53\u7684\u201c\u4e5d\u5bab\u8fde\u73af\u5251\u201d\u91cc\u7684\u5999\u62db\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u6c89\u58f0\u9053\uff1a\u201c\u597d\u5251\u6cd5\uff01\u201d\u5251\u5149\u4e00\u64a4\uff0c\u731b\u53c8\u518d\u8d77\uff0c\u5339\u7ec3\u822c\u7684\u5251\u5f71\u4fbf\u7acb\u523b\u5728\u81ea\u5df1\u56db \u5468\u5e03\u4e0b\u4e00\u9053\u5251\u56f4\uff0c\u5149\u8292\u7f2d\u4e71\u4e4b\u4e2d\uff0c\u5251\u8eab\u7a81\u81ea\u4e0a\u800c\u4e0b\u4e00\u5251\u524a\u6765\uff0c\u6b63\u662f\u201c\u5c11\u9633\u4e5d\u4e00\u5f0f\u201d\u91cc\u7684 \u201c\u795e\u9f99\u73b0\u5c3e\u201d\u3002 \u3000\u3000\u795e\u9e64\u8a79\u5e73\u4e86\u58f0\u6e05\u5578\uff0c\u51cc\u98ce\u5251\u5ba2\u5728\u65c1\u5df1\u4f55\u4ed6\u8fd9\u5e08\u5f1f\u52a8\u4e86\u771f\u6012\uff0c\u7686\u56e0\u8a79\u5e73\u201c\u795e\u9e64\u201d\u4e4b\u53f7 \u7531\u6765\uff0c\u5373\u5728\u4ed6\u6bcf\u5728\u6740\u4eba\u4e4b\u5148\uff0c\u5fc5\u7136\u8f7b\u5578\u4e00\u58f0\u3002 \u3000\u3000\u679c\u7136\u795e\u9e64\u8a79\u5e73\u5251\u5149\u5982\u8679\uff0c\u6309\u7740\u811a\u4e0b\u8e29\u65b9\u4f4d\uff0c\u6bcf\u5251\u53d1\u51fa\uff0c\u5fc5\u662f\u4e8e\u4e00\u98de\u7684\u8981\u5bb3\u3002 \u3000\u3000\u8f9b\u6377\u770b\u5728\u773c\u770b\uff0c\u5374\u6b63\u5408\u4e86\u4ed6\u7684\u5fc3\u610f\uff0c\u4ed6\u77e5\u9053\u6b64\u4e24\u4eba\u53ea\u8981\u6709\u4e00\u4eba\u53d7\u4f24\uff0c\u5c31\u662f\u4e0d\u4e86\u4e4b\u5c40\u3002 \u3000\u3000\u4e24\u4eba\u5251\u6cd5\uff0c\u4ff1\u662f\u5f97\u81ea\u540d\u5bb6\uff0c\u201c\u5c11\u9633\u4e5d\u4e00\u5f0f\u201d\u62db\u5f0f\u7cbe\u5947\uff0c\u4e8e\u4e00\u98de\u5185\u529b\u53c8\u539a\uff0c\u5251\u5251\u90fd\u5e26 \u7740\u98ce\u96f7\u4e4b\u58f0\uff0c\u770b\u6765\u715e\u662f\u60ca\u4eba\u3002 \u3000\u3000\u4f46\u6b66\u5f53\u4e4b\u201c\u4e5d\u5bab\u8fde\u73af\u5251\u201d\u79f0\u5c0a\u4e2d\u539f\u6b66\u6797\u65e5\u6570\u5341\u5e74\uff0c\u62db\u62db\u7a33\u5065\uff0c\u5374\u53c8\u5251\u6263\u8fde\u73af\uff0c\u62db\u4e2d \u5957\u62db\uff0c\u76f4\u5982\u957f\u6c5f\u5927\u6cb3\u4e4b\u6c34\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u52a8\u624b\uff0c\u4fbf\u662f\u6570\u5341\u7167\u9762\uff0c\u4f17\u4eba\u4f46\u89c9\u5251\u5149\u7f2d\u7ed5\uff0c\u5251\u6c14\u6f2b\u5929\u3002 \u3000\u3000\u4fbf\u662f\u8f9b\u6377\uff0c\u4e5f\u81ea\u6697\u70b9\u5934\u6697\u8d5e\u7740\u201c\u6b66\u5f53\u201d\u3001\u201c\u5d06\u5cd2\u201d\u80fd\u4ee5\u626c\u540d\u6c5f\u6e56\uff0c\u786e\u975e\u5e78\u81f4\u3002 \u3000\u3000\u4ed6\u6697\u4e2d\u7559\u5fc3\u770b\u6bcf\u4e00\u62db\u7684\u53d1\u51fa\uff0c\u89c9\u5f97\u4e24\u4eba\u7684\u5251\u6cd5\u867d\u7136\u4e25\u5bc6\uff0c\u4f46\u5374\u4ecd\u6709\u7a7a\u9699\u9732\u51fa\uff0c\u867d\u7136 \u90a3\u7a7a\u9699\u662f\u5728\u5e38\u4eba\u7edd\u96be\u53d1\u62db\u7684\u90e8\u4f4d\u3002 \u3000\u3000\u4ed6\u6697\u91cc\u5fae\u7b11\uff0c\u604d\u7136\u4e86\u89e3\u4e86\u201c\u866c\u679d\u5251\u6cd5\u201d\u91cc\u6709\u4e9b\u770b\u4f3c\u65e0\u7528\u7684\u62db\u5f0f\uff0c\u6b63\u662f\u4e13\u5bf9\u7740\u8fd9\u4e9b\u7a7a \u9699\u800c\u8bbe\uff0c\u590d\u77e5\u6885\u5c71\u6c11\u5b66\u7a76\u5929\u4eba\uff0c\u5f53\u521d\u521b\u7acb\u8fd9\u201c\u8679\u679d\u5251\u6cd5\u201d\u7684\u65f6\u5019\uff0c\u65e9\u5df2\u5c06\u4e2d\u539f\u5404\u95e8\u5404\u6d3e \u7684\u5f31\u70b9\u4e86\u7136\u4e8e\u5fc3\u3002 \u3000\u3000\u53c8\u662f\u6570\u5341\u62db\u8fc7\u53bb\uff0c\u4e24\u4eba\u4ecd\u672a\u5206\u51fa\u80dc\u8d1f\uff0c\u7a81\u5730\u5929\u7a7a\u4e00\u7247\u4e4c\u4e91\u906e\u6765\uff0c\u63a9\u4f4f\u6708\u5149\uff0c\u5927\u5730\u66f4 \u5f62\u9ed1\u6697\uff0c\u4e24\u4eba\u7684\u5251\u5149\u4e5f\u66f4\u8000\u76ee\u4e86\u3002 \u3000\u3000\u7247\u523b\uff0c\u7adf\u54d7\u5730\u843d\u4e0b\u96e8\u6765\uff0c\u591c\u95f4\u9aa4\u96e8\uff0c\u96e8\u70b9\u9887\u5927\uff0c\u65c1\u89c2\u7684\u4eba\u90fd\u8fde\u5fd9\u8eb2\u5728\u9ec4\u9e64\u697c\u7684\u5eca\u6a90 \u4e0b\uff0c\u4f46\u52a8\u624b\u4e2d\u7684\u4e24\u4eba\uff0c\u5374\u4ecd\u5728\u96e8\u4e2d\u6fc0\u6218\u7740\u3002 \u3000\u3000\u8fd9\u4e24\u4eba\u90fd\u53ef\u8bf4\u662f\u4ee3\u8868\u4e86\u201c\u5d06\u5cd2\u201d\u201c\u6b66\u5f53\u201d\u7b2c\u4e8c\u4ee3\u7684\u7cbe\u534e\uff0c\u867d\u7136\u4ed6\u4eec\u90fd\u4e0d\u662f\u638c\u95e8\u5f1f\u5b50\uff0c \u4f46\u90fd\u58f0\u671b\u5f88\u9ad8\uff0c\u4e24\u4eba\u4e5f\u77e5\u9053\u4eca\u65e5\u4e4b\u6218\u7684\u4e25\u91cd\u6027\uff0c\u662f\u4ee5\u4ff1\u90fd\u5fc3\u795e\u8d2f\u6ce8\uff0c\u8fde\u4e0b\u96e8\u4e5f\u987e\u4e0d\u5f97\u4e86\u3002 \u3000\u3000\u7a81\u7136\uff0c\u96e8\u58f0\u4e2d\u6709\u6b4c\u5531\u4e4b\u58f0\u4f20\u6765\uff0c\u6709\u4eba\u5728\u5531\u7740\uff1a\u201c\u4ece\u524d\u6709\u4e2a\u59dc\u592a\u516c\uff0c\u5230\u4e86\u4e03\u5341\u8fd8\u6ca1\u7528\uff0c \u62c5\u7740\u9762\u7c89\u4e0a\u8857\u5356\uff0c\u5374\u53c8\u649e\u4e0b\u96e8\u548c\u98ce\u3002\u201d \u3000\u3000\u8bf8\u4eba\u4ff1\u90fd\u5927\u5947\uff0c\u5728\u6b64\u6df1\u591c\u4e4b\u4e2d\uff0c\u600e\u5730\u4f1a\u6709\u4eba\u5531\u8d77\u83b2\u82b1\u843d\u6765\u3002 \u3000\u3000\u6b4c\u58f0\u6108\u6765\u6108\u8fd1\uff0c\u53ea\u89c1\u96e8\u4e2d\u6709\u4eba\u5243\u91cc\u62d6\u843d\u7684\u8d70\u6765\uff0c\u4e00\u8fb9\u5531\uff0c\u4e00\u8fb9\u8fd8\u7528\u624b\u4e2d\u4e24\u5757\u957f\u5f62\u7684 \u68cd\u68d2\u4e92\u76f8\u6572\u7740\uff0c\u4f17\u4eba\u66f4\u662f\u53c8\u60ca\u53c8\u5947\u3002 \u3000\u3000\u90a3\u4eba\u4e00\u89c1\u6709\u4eba\u6bd4\u5251\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u53c8\u8fb9\u6253\u8fb9\u5531\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u771f\u70ed\u95f9\uff0c\u522e\u522e\u53eb\uff0c\u4e24\u4eba\u6253 \u5f97\u771f\u70ed\u95f9\uff0c\u522e\u522e\u53eb\uff0c\u626c\u5dde\u6709\u4e2a\u96ea\u91cc\u5e99\uff0c\u9547\u6c5f\u6709\u4e2a\u8fde\u73af\u5957\u2026\u2026\u201d\u8fb9\u5531\u8fb9\u8d70\uff0c\u4e5f\u8d70\u5230\u5eca\u6905\u4e0b\uff0c \u5f80\u8f9b\u6377\u8eab\u8fb9\u4e00\u5750\uff0c\u53c8\u5531\u9053\uff1a\u201c\u4e1b\u524d\u6709\u4e2a\u597d\u5730\u65b9\uff0c\u540d\u5b57\u53eb\u505a\u4ec0\u4e48\u51e4\u9633\uff0c\u98ce\u9633\u51fa\u4e86\u4e2a\u6731\u6d2a\u6b66\u3001 \u5341\u5e74\u5012\u6709\u4e5d\u5e74\u8352\uff0c\u549a\u549a\u67aa\uff0c\u549a\u549a\u67aa\u2026\u2026\u201d \u3000\u3000\u4ed6\u53c8\u5531\u53c8\u6572\uff0c\u95f9\u5f97\u4e0d\u53ef\u5f00\u4ea4\uff0c\u50cf\u662f\u65c1\u82e5\u65e0\u4eba\uff0c\u91d1\u5f13\u795e\u5f39\u89c1\u4ed6\u8863\u7740\u6253\u626e\uff0c\u5374\u50cf\u4e2a\u82b1\u5b50\uff0c \u4f46\u662f\u5934\u8138\u7686\u51c0\uff0c\u53cc\u624b\u6d01\u767d\u5982\u7389\uff0c\u7559\u7740\u5bf8\u4f59\u957f\u6307\u7532\uff0c\u7a81\u5730\u60f3\u8d77\u4e00\u4eba\uff0c\u4f4e\u58f0\u5bf9\u94f6\u67aa\u5b5f\u4f2f\u8d77\u5600 \u5495\u4e86\u51e0\u53e5\uff0c\u5b5f\u9762\u8272\u5927\u53d8\uff0c\u8f6c\u8138\u60ca\u5f02\u5730\u671b\u7740\u6b64\u4eba\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u4e86\u4ed6\u4e24\u4eba\u7684\u4e3e\u52a8\uff0c\u5fc3\u91cc\u4e00\u52a8\uff0c\u4fbf\u4e5f\u76d8\u819d\u5750\u4e86\u4e0b\u6765\u3002\u90a3\u4eba\u4e00\u8f6c\u5934\uff0c\u89c1\u8f9b\u6377\u5750\u5728 \u4ed6\u8eab\u8fb9\uff0c\u9762\u8272\u4e00\u53d8\uff0c\u4ed4\u7ec6\u5730\u770b\u4e86\u8f9b\u6377\u4e24\u773c\uff0c\u5374\u53c8\u671d\u8f9b\u6377\u7b11\u4e86\u7b11\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u671d\u90a3\u4eba\u7b11\u4e86\u7b11\uff0c\u91d1\u5f13\u795e\u5f39\u4e0e\u94f6\u62a2\u5b5f\u4f2f\u8d77\u89c1\u4e86\uff0c\u5bf9\u671b\u4e86\u4e00\u773c\uff0c\u4eff\u4f5b\u89c9\u5f97\u751a\u662f\u8be7 \u5f02\u3002 \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u548c\u795e\u9e64\u8a79\u5e73\uff0c\u53cc\u53cc\u88ab\u4ed6\u5531\u5f97\u53eb\u82e6\u8fde\u5929\uff0c\u987b\u77e5\u9ad8\u624b\u52a8\u62db\uff0c\u5fc3\u795e\u4e00\u4e1d\u4e5f\u6270 \u4e71\u4e0d\u5f97\uff0c\u6b64\u65f6\u96e8\u52bf\u672c\u5927\uff0c\u518d\u52a0\u4e0a\u6b64\u4eba\u53c8\u5531\u53c8\u6572\uff0c\u4e24\u4eba\u82e6\u6218\u4e0d\u4e0b\uff0c\u5fc3\u91cc\u90fd\u5f00\u59cb\u6025\u8e81\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4e24\u4eba\u6c14\u529b\u90fd\u89c9\u5f97\u6709\u4e9b\u4e0d\u6d4e\uff0c\u5251\u62db\u4e5f\u663e\u5f97\u4e0d\u5982\u4ee5\u524d\u7684\u77eb\u5065\uff0c\u4f46\u4e24\u4eba\u5374\u90fd\u77e5\u9053\u5728\u8fd9\u79cd\u65f6 \u5019\uff0c\u5c31\u662f\u5206\u51fa\u80dc\u8d1f\u7684\u5173\u5934\u4e86\u3002 \u3000\u3000\u51cc\u98ce\u5251\u5ba2\u6700\u662f\u5173\u5fc3\u3001\u7adf\u4e00\u6b65\u6b65\u5730\u5f80\u524d\u8fdb\uff0c\u7ad9\u5728\u96e8\u4e0b\u4e5f\u4e0d\u81ea\u89c9\u3002 \u3000\u3000\u6b64\u65f6\u795e\u9e3d\u8a79\u5e73\u7a81\u8d70\u9669\u62db\uff0c\u4fa7\u8eab\u6b3a\u8fdb\uff0c\u5de6\u624b\u5212\u4e2a\u5251\u8bc0\u53bb\u70b9\u4e8e\u4e00\u98de\u7684\u6301\u5251\u624b\u8155\uff0c\u53f3\u624b\u5e73 \u98de\u4e00\u6307\uff0c\u53bb\u524a\u4e8e\u4e00\u98de\u7684\u516d\u9633\u3002 \u3000\u3000\u6b64\u62db\u5b9e\u662f\u9669\u6781\uff0c\u9ad8\u624b\u8fc7\u62db\uff0c\u7a0d\u6cbe\u5373\u8d70\uff0c\u90a3\u91cc\u6709\u4ed6\u8fd9\u6837\u5168\u8eab\u6b3a\u4eba\u7684\uff0c\u51cc\u98ce\u5251\u5ba2\u5728\u65c1\u770b \u4e86\uff0c\u4e0d\u7981\u5931\u58f0\u53eb\u4e86\u51fa\u6765\uff0c\u5c31\u77e5\u8981\u7cdf\uff0c\u811a\u5c16\u4e00\u70b9\uff0c\u4fbf\u5f80\u4e24\u4eba\u6bd4\u6b66\u4e4b\u5904\u98de\u53bb\uff0c\u90a3\u77e5\u5374\u5df2\u8fdf\u4e86 \u4e00\u6b65\u3002 \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u53cc\u8db3\u7262\u7262\u9489\u5728\u5730\u9762\u4e0a\uff0c\u8eab\u5f62\u7a81\u5730\u540e\u4ef0\uff0c\u53f3\u624b\u4e00\u653e\uff0c\u7adf\u5c06\u957f\u5251\u677e\u4e86\uff0c\u5728 \u5251\u843d\u4e0b\u4e4b\u9645\u7a81\u53c8\u53cd\u624b\u6284\u7740\uff0c\u5251\u628a\u5728\u5916\uff0c\u75be\u5730\u4e00\u70b9\uff0c\u70b9\u5411\u795e\u9e64\u8a79\u5e73\u7684\u201c\u5c06\u53f0\u201d\u91cd\u7a74\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u624b\u7684\u786e\u662f\u5947\u8be1\u5f97\u5f88\uff0c\u624b\u4e2d\u4e4b\u5251\uff0c\u4e00\u677e\u4e00\u653e\uff0c\u8eb2\u5f00\u4e86\u795e\u9e64\u8a79\u5e73\u70b9\u6765\u7684\u624b\u6307\uff0c\u5374 \u53c8\u5251\u628a\u5728\u5916\uff0c\u5411\u8a79\u5e73\u70b9\u53bb\uff0c\u8fd9\u79cd\u62db\u5f0f\uff0c\u4efb\u4f55\u4e00\u5bb6\u5251\u8c31\u90fd\u6ca1\u6709\uff0c\u4e0d\u8fc7\u53ea\u662f\u4e8e\u4e00\u98de\u60c5\u6025\u5e94\u53d8 \u4e4b\u4e0b\uff0c\u6240\u60f3\u51fa\u6765\u7684\u800c\u5df2\uff0c\u795e\u9e64\u8a79\u5e73\u5927\u51fa\u610f\u5916\uff0c\u8eb2\u65e0\u53ef\u8eb2\uff0c\u6251\u5730\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u51cc\u98ce\u5251\u5ba2\u8eab\u5f62\u5982\u98ce\uff0c\u4f46\u8d76\u6765\u65f6\u795e\u9e64\u8a79\u5e73\u5df2\u5012\u5728\u5730\u4e0a\uff0c\u624b\u4e2d\u4ecd\u7d27\u63e1\u7740\u5251\uff0c\u9762\u4e0a\u5df2\u6cdb\u51fa \u9752\u9ec4\u4e4b\u8272\uff0c\u53cc\u76ee\u4e5f\u95ed\u8d77\u6765\u4e86\u3002 \u3000\u3000\u51cc\u98ce\u5251\u5ba2\u5927\u60ca\u4e4b\u4e0b\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u522b\u7684\uff0c\u5fd9\u4fef\u8eab\u5c06\u795e\u9e64\u8a79\u5e73\u62b1\u5728\u6000\u91cc\uff0c\u67e5\u770b\u4ed6\u7684\u4f24\u52bf\u3002 \u3000\u3000\u65c1\u89c2\u8bf8\u4eba\u4e5f\u81ea\u4e00\u58f0\u60ca\u547c\uff0c\u6dcb\u7740\u843d\u4e0b\u6765\u7684\u96e8\u70b9\uff0c\u90fd\u8dd1\u5411\u4ed6\u4e24\u4eba\u7684\u8eab\u65c1\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u90a3\u602a\u4eba\uff0c\u5374\u50cf\u6839\u672c\u6ca1\u6709\u5c06\u8fd9\u4e9b\u4e8b\u770b\u5728\u773c\u91cc\u4f3c\u7684\uff0c\u4ecd\u81ea\u7ba1\u5531\u7740\uff0c\u4e8e\u662f\u4ed6\u4e5f\u5750\u7740 \u4e0d\u8d70\u3002 \u3000\u3000\u51cc\u98ce\u5251\u5ba2\u89c1\u795e\u9e64\u8a79\u5e73\u7adf\u88ab\u70b9\u4e86\u201c\u5c06\u53f0\u201d\u91cd\u7a74\uff0c\u53c8\u6025\u53c8\u6148\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u5d06\u5cd2\u5251 \u5ba2\u679c\u7136\u597d\u529f\u592b\uff0c\u597d\u624b\u6cd5\uff0c\u6b66\u5f53\u6d3e\u4eca\u5929\u7b97\u662f\u683d\u5728\u4f60\u7684\u624b\u91cc\u3002\u201d \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u6b64\u523b\u8863\u886b\u5c3d\u6e7f\uff0c\u8eab\u5fc3\u4ff1\u75b2\uff0c\u77e5\u9053\u51cc\u98ce\u5251\u5ba2\u82e5\u7136\u6b64\u523b\u5411\u81ea\u5df1\u52a8\u624b\uff0c\u81ea\u5df1 \u5374\u975e\u654c\u624b\u4e86\uff0c\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u662f\u5426\u4e5f\u60f3\u4e00\u8bd5\u8eab\u624b\u3002\u201d \u3000\u3000\u51cc\u98ce\u5251\u5ba2\u6012\u6781\u9053\uff1a\u201c\u8d2b\u9053\u5374\u4e0d\u4f1a\u627e\u5360\u4fbf\u5b9c\u7684\u67b6\u6253\uff0c\u4f60\u59d3\u4e8e\u7684\u8eab\u624b\uff0c\u8d2b\u9053\u8fdf\u65e9\u603b\u8981\u9886 \u6559\u7684\u3002\u201d \u3000\u3000\u4ed6\u5f53\u7740\u6b66\u6c49\u7684\u8fd9\u4e9b\u6210\u540d\u82f1\u96c4\uff0c\u8bdd\u8bf4\u5f97\u6781\u4e3a\u6f02\u4eae\uff0c\u90a3\u77e5\u4ed6\u5374\u5e76\u975e\u4e0d\u613f\u4e58\u4eba\u4e4b\u5371\uff0c\u800c\u662f \u795e\u9e64\u8a79\u5e73\u6b64\u65f6\u547d\u5728\u987b\u81fe\uff0c\u975e\u8d76\u7d27\u6551\u6cbb\u4e0d\u53ef\u3002 \u3000\u3000\u4ed6\u6a2a\u62b1\u8d77\u795e\u9e64\u8a79\u5e73\u7684\u8eab\u8eaf\uff0c\u671d\u5728\u65c1\u53d1\u7740\u6014\u7684\u4e5d\u5bab\u674e\u6cbb\u534e\u6012\u9053\uff1a\u201c\u8fd8\u4e0d\u8d70\u3002\u201d \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u53c8\u9053\uff1a\u201c\u9601\u4e0b\u8bf7\u8f6c\u544a\u4ee4\u5e08\uff0c\u5c31\u8bf4\u897f\u5d06\u5cd2\u7684\u6545\u4eba\uff0c\u95ee\u4ed6\u5341\u5e74\u524d\u7684\u65e7\u7269\u53ef \u66fe\u9057\u843d\uff0c\u8bf7\u4ee4\u5e08\u5982\u7ea6\u9001\u8fd8\u5d06\u5cd2\u5c71\u4e0a\u3002\u201d \u3000\u3000\u51cc\u98ce\u5251\u5ba2\u6012\u9053\uff1a\u201c\u4e00\u6708\u4e4b\u5185\uff0c\u5bb6\u5e08\u5fc5\u5b9a\u4eb2\u81f3\u5d06\u5cd2\uff0c\u8bf7\u9601\u4e0b\u653e\u5fc3\u597d\u4e86\u3002\u201d \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4ef0\u5929\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4eca\u79cb\u7684\u6cf0\u5c71\u4e4b\u4f1a\uff0c\u8fd8\u5e0c\u671b\u9601\u4e0b\u4e5f\u6765\u4e00\u663e\u8eab\u624b\u3002\u201d \u3000\u3000\u51cc\u98ce\u5ba2\u53f1\u9053\uff1a\u201c\u5f53\u7136\u3002\u201d \u3000\u3000\u8eab\u5f62\u4e00\u6643\uff0c\u62b1\u7740\u795e\u9e64\u8a79\u5e73\u98de\u5954\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u542c\u4e86\u4e24\u4eba\u6240\u8bf4\u7684\u8bdd\uff0c\u77e5\u9053\u201c\u6b66\u5f53\u201d\u201c\u5d06\u5cd2\u201d\u4e24\u6d3e\uff0c\u4ece\u6b64\u4fbf\u6210\u6c34\u706b\uff0c\u4ed6\u8f6c\u8138\u671b\u90a3 \u602a\u4eba\uff0c\u89c1\u4ed6\u58f0\u97f3\u6108\u5531\u6108\u5c0f\uff0c\u6b64\u65f6\u7adf\u4f3c\u7761\u7740\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u4e00\u7b11\uff0c\u7ad9\u4e86\u8d77\u6765\u3002\u8d70\u5411\u4e8e\u4e00\u98de\u7b11\u9053\uff1a\u201c\u4e8e\u5144\u679c\u7136\u5251\u6cd5\u7edd\u4f26\uff0c\u4eca\u65e5\u5c0f\u5f1f\u771f\u5f00 \u4e86\u773c\u754c\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5411\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\u7b49\u4eba\u8bf4\u9053\uff1a\u201c\u4eca\u65e5\u5c0f\u5f1f\u4f5c\u4e1c\uff0c\u5728\u90a3\u51e4\u6797\u73ed\u91cc\u8bf7\u5404\u4f4d\u559d\u9152\u4e3a\u4e8e \u5144\u5e86\u529f\uff0c\u5404\u4f4d\u53ef\u8d5e\u6210\uff1f\u201d \u3000\u3000\u4e8e\u4e00\u98de\u5fd9\u9053\uff1a\u201c\u8f9b\u5144\u7684\u597d\u610f\uff0c\u5c0f\u5f1f\u5fc3\u9886\u4e86\uff0c\u5c0f\u5f1f\u5fc5\u987b\u8fde\u591c\u56de\u5d06\u5cd2\uff0c\u5411\u5bb6\u5e08\u7980\u62a5\u6b64 \u4e8b\u3002\u201d\u4ed6\u987f\u4e86\u987f\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u90a3\u2018\u4e03\u5999\u795e\u541b\u2019\u91cd\u73b0\u6c5f\u6e56\uff0c\u5c0f\u5f1f\u4e5f\u8981\u7acb\u523b\u7980\u660e\u5bb6\u5e08\u4f5c\u4e2a\u51c6 \u5907\u3002\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u4e8e\u5144\u5982\u6709\u6b63\u4e8b\uff0c\u5c0f\u5f1f\u81ea\u662f\u4e0d\u80fd\u76f8\u5f3a\uff0c\u4f46\u4eca\u65e5\u4e00\u522b\uff0c\u540e\u4f1a\u65e0\u671f\uff0c\u5c0f\u5f1f\u5374\u96be \u8fc7\u5f97\u5f88\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u5b50\u7136\u4e00\u8eab\uff0c\u6765\u53bb\u81ea\u5982\uff0c\u53ea\u5f85\u4e8b\u4e86\uff0c\u5c0f\u5f1f\u5fc5\u518d\u6765\u6b64\u95f4\uff0c\u4e0e\u5404\u4f4d\u5c3d \u5341\u65e5\u4e4b\u6b22\uff0c\u4eca\u65e5\u5c31\u6b64\u522b\u8fc7\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7f62\u4e00\u62f1\u624b\uff0c\u4e5f\u81ea\u8eab\u5f62\u52a8\u5904\uff0c\u5982\u98de\u8d70\u4e86\uff0c\u970e\u65f6\u4fbf\u65e0\u8e2a\u8ff9\uff0c\u6d88\u5931\u5728\u96e8\u4e1d\u91cc\u3002 \u3000\u3000\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\u7a81\u8d70\u4e86\u8fc7\u6765\uff0c\u6084\u58f0\u9053\uff1a\u201c\u8f9b\u5144\u53ef\u8ba4\u8bc6\u90a3\u4eba\u5417\uff1f\u201d\u4ed6\u7528\u624b\u5fae\u5fae\u6307\u4e86\u6307 \u90a3\u4ecd\u5750\u5728\u5eca\u6a90\u4e0b\u7684\u602a\u4eba\u3002 \u3000\u3000\u8f9b\u6377\u6447\u5934\u9053\uff1a\u201c\u4e0d\u8ba4\u5f97\u3002\u201d \u3000\u3000\u91d1\u5f13\u795e\u5f39\u6b63\u8981\u8bf4\u8bdd\uff0c\u7a81\u89c1\u90a3\u4eba\u4ef0\u5929\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u5fd9\u5c06\u8981\u8bf4\u7684\u8bdd\u54bd\u56de\u809a\u91cc\u3002 \u3000\u3000\u94f6\u67aa\u5b5f\u4f2f\u8d77\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u96e8\u4e2d\u4e0d\u662f\u8c08\u8bdd\u4e4b\u5904\uff0c\u8f9b\u5144\u4e0d\u5982\u4e0e\u5c0f\u5f1f\u4eec\u4e00\u9f50\u5750\u8239 \u6e21\u6c5f\u5427\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u6700\u662f\u597d\u5947\uff0c\u8fd8\u60f3\u7559\u5728\u6b64\u5730\uff0c\u8303\u5144\uff0c\u5b5f\u5144\u5148\u8bf7\u56de\u5427\uff01\u201d \u3000\u3000\u91d1\u5f13\u795e\u5f39\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u8bf4\u4e0d\u5b9a\u8f9b\u5144\u8fd8\u6709\u5947\u9047\uff0c\u53ea\u662f\u5c0f\u5f1f\u4eec\u5374\u8981 \u5148\u8d70\u4e00\u6b65\u3002\u201d \u3000\u3000\u5b5f\u4f2f\u8d77\u4e5f\u597d\u50cf\u4e0d\u613f\u5728\u8fd9\u91cc\u518d\u591a\u9017\u7559\u4e00\u523b\u4f3c\u7684\uff0c\u4e00\u62f1\u624b\uff0c\u62c9\u7740\u8303\u6cbb\u6210\u7b49\u4eba\u5306\u5306\u8d70\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u4f38\u624b\u62ed\u4e86\u62ed\u9762\u4e0a\u7684\u96e8\u6c34\uff0c\u53c8\u8e31\u56de\u68fa\u4e0b\uff0c\u89c1\u90a3\u602a\u4eba\u53c8\u4f3c\u5728\u6c89\u6c89\u7761\u7740\uff0c\u7ad9\u5728\u90a3\u91cc\u60f3 \u4e86\u4e00\u56de\uff0c\u4ed6\u53c8\u5750\u5728\u90a3\u4eba\u8eab\u4fa7\u3002 \u3000\u3000\u5750\u4e86\u4e00\u4f1a\uff0c\u96e8\u52bf\u6e10\u4f4f\uff0c\u5929\u8272\u4e5f\u5c06\u4eae\u4e86\uff0c\u90a3\u602a\u4eba\u4ecd\u65e0\u52a8\u9759\uff0c\u8f9b\u6377\u6e10\u6e10\u4e0d\u8010\uff0c\u5fd6\u9053\uff1a \u201c\u4e07\u4e00\u6b64\u65f6\u6709\u4eba\u8d70\u6765\u770b\u89c1\uff0c\u5c82\u975e\u53c8\u662f\u7b11\u8bdd\u3002\u201d \u3000\u3000\u6668\u66e6\u5fae\u660e\u4e2d\uff0c\u8f9b\u6377\u770b\u89c1\u6c5f\u8fb9\u679c\u7136\u6709\u4eba\u6765\u4e86\uff0c\u4f3c\u8fd8\u4e0d\u6b62\u4e00\u4eba\u3002 \u3000\u3000\u4ed6\u76ee\u529b\u7279\u5f3a\uff0c\u8fdc\u8fdc\u671b\u53bb\u7adf\u7136\u5168\u662f\u5973\u5b50\uff0c\u5176\u4e2d\u56db\u4eba\u62ac\u7740\u4e00\u7269\uff0c\u50cf\u662f\u8f7b\u8f7f\u4e4b\u7c7b\u7684\u4e1c\u897f\uff0c \u53e6\u4e00\u4e2a\u5973\u5b50\u8d70\u5728\u524d\u9762\uff0c\u5374\u7a7a\u7740\u624b\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u53c8\u6697\u5730\u53eb\u82e6\uff0c\u8bd5\u60f3\u4e00\u4e2a\u8863\u7740\u534e\u4e3d\u7684\u5c11\u5e74\uff0c\u4e0e\u4e00\u4e2a\u8863\u886b\u697c\u697c\u7684\u82b1\u5b50\uff0c\u5728\u5982\u6b64 \u6e05\u6668\uff0c\u5e76\u80a9\u5750\u5728\u5730\u4e0a\uff0c\u88ab\u4eba\u89c1\u4e86\uff0c\u6210\u4f55\u4f53\u7edf\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u6253\u7740\u9f13\uff0c\u5374\u89c1\u90a3\u4e3a\u9996\u5c11\u5973\u7528\u624b\u5411\u81ea\u5df1\u6240\u5750\u4e4b\u5904\u4e00\u70b9\uff0c\u9762\u4e0a\u4f3c\u6709\u559c\u5bb9\u3002 \u3000\u3000\u4ed6\u66f4\u662f\u5947\u602a\uff0c\u81ea\u5df1\u548c\u8fd9\u5c11\u5973\u7d20\u6627\u751f\u5e73\uff0c\u8fd9\u5c11\u5973\u600e\u4f1a\u6307\u70b9\u7740\u81ea\u5df1\uff0c\u96be\u9053\u662f\u5728\u7b11\u6211\u8fd9\u79cd \u60c5\u51b5\u7684\u6ed1\u7a3d\uff0c\u4f46\u4e00\u4e2a\u5c11\u5973\u4f3c\u4e5f\u4e0d\u5e94\u5982\u6b64\u5440\u3002 \u3000\u3000\u90a3\u5c11\u5973\u7a7f\u7740\u7fe0\u7eff\u8272\u7684\u8863\u88d9\uff0c\u4e91\u53d1\u9ad8\u633d\uff0c\u5728\u6b64\u5fae\u660e\u7684\u6668\u66e6\uff0c\u671b\u4e4b\u76f4\u5982\u56fe\u753b\u4e2d\u4eba\uff0c\u8f9b\u6377 \u4e0d\u89c9\u5f97\u75f4\u4e86\u3002\u90a3\u5c11\u5973\u8d8a\u8d70\u8fc7\u8fd1\uff0c\u800c\u4e14\u6839\u672c\u5c31\u662f\u51b2\u7740\u8f9b\u6377\u6240\u5750\u4e4b\u5904\u800c\u6765\uff0c\u540e\u9762\u53e6\u56db\u4e2a\u5c11\u5973 \u4f3c\u662f\u5974\u5a62\uff0c\u4e00\u4eba\u4e00\u89d2\u62ac\u7740\u4e00\u53ea\u8f6f\u69bb\u3002 \u3000\u3000\u8f9b\u6377\u5b9e\u662f\u5982\u5760\u4e94\u91cc\u4e91\u4e2d\uff0c\u8d8a\u770b\u8d8a\u89c9\u5947\u602a\uff0c\u90a3\u77e5\u66f4\u5947\u7684\u662f\u90a3\u5c11\u5973\u7ade\u8d70\u5230\u4ed6\u7684\u9762\u524d\uff0c\u53e3 \u89d2\u4e00\u626c\uff0c\u6d45\u6d45\u4e00\u7b11\uff0c\u76c8\u76c8\u5411\u4ed6\u62dc\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u88ab\u8fd9\u4e00\u7b11\uff0c\u4e00\u62dc\uff0c\u5f04\u5f97\u4e0d\u77e5\u6240\u63aa\uff0c\u614c\u5f20\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u6014\u5728\u90a3\u91cc\u4e86\u3002 \u3000\u3000\u540e\u9762\u90a3\u56db\u4e2a\u5974\u5a62\u72b6\u7684\u5c11\u5973\uff0c\u4e5f\u51b2\u7740\u4ed6\u4e00\u62dc\uff0c\u4f46\u5374\u8dea\u5728\u90a3\u72b6\u4f3c\u4e10\u8005\u7684\u602a\u4eba\u9762\u524d\uff0c\u5c06\u90a3 \u602a\u4eba\u5e73\u5e73\u62ac\u4e86\u8d77\u6765\uff0c\u653e\u5728\u90a3\u8f6f\u584c\u4e0a\uff0c\u90a3\u602a\u4eba\u5fae\u4e00\u5f00\u773c\uff0c\u56db\u987e\u4e86\u4e00\u4e0b\uff0c\u53c8\u6c89\u6c89\u7761\u53bb\u4e86\u3002\u8fd9 \u4e00\u6765\uff0c\u786e\u662f\u4f7f\u8f9b\u6377\u66f4\u4e3a\u8ff7\u60d8\uff0c\u4ed6\u832b\u7136\u671b\u7740\u90a3\u5c11\u5973\uff0c\u90a3\u5c11\u5973\u53c8\u662f\u76c8\u76c8\u4e00\u7b11\uff0c\u8f9b\u6377\u8fde\u5fd9\u4e00\u63d6 \u5230\u5730\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u2026\u2026\u201d\u4f46\u4ed6\u53ea\u8bf4\u4e86\u8fd9\u4e24\u4e2a\u5b57\uff0c\u5374\u5f20\u53e3\u7ed3\u820c\u5730\u518d\u4e5f\u8bf4\u4e0d\u4e0b\u53bb\uff0c\u7686\u56e0\u4ed6 \u6839\u672c\u4e0d\u77e5\u9053\u8fd9\u5c11\u5973\u662f\u8c01\uff0c\u4e5f\u4e0d\u77e5\u9053\u8fd9\u5c11\u5973\u548c\u602a\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u4e3a\u4f55\u9886\u7740\u56db\u4e2a\u5a62\u73af\u6765\u62ac\u8fd9 \u602a\u4eba\uff0c\u66f4\u4e0d\u77e5\u9053\u8fd9\u5c11\u5973\u4e3a\u4f55\u5bf9\u81ea\u5df1\u4e00\u7b11\u3002\u5982\u77e5\u90a3\u5c11\u5973\u89c1\u8f9b\u6377\u7684\u6837\u5b50\uff0c\u7b2c\u4e09\u6b21\u53c8\u76c8\u76c8\u4e00\u7b11\uff0c \u8fd9\u65f6\u9633\u5149\u521d\u5347\uff0c\u8f9b\u6377\u539f\u662f\u82cd\u767d\u7684\u9762\u5e9e\uff0c\u6b64\u523b\u7adf\u9690\u9690\u6cdb\u4e00\u4e1d\u7ea2\u8272\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u5a62\u73af\u5c06\u90a3\u602a\u4eba\u653e\u5728\u8f6f\u69bb\u4e0a\u540e\uff0c\u53c8\u4e00\u4eba\u62ac\u7740\u4e00\u89d2\u3001\u62ac\u7740\u8f6f\u69bb\u5411\u6765\u8def\u8d70\u53bb\u3002 \u3000\u3000\u5c11\u5973\u7f8e\u76ee\u4e00\u8f6c\uff0c\u7a81\u5730\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u5bb6\u7236\u591a\u627f\u516c\u5b50\u7167\u5e94\uff0c\u8d31\u5984\u611f\u6fc0\u5f97\u5f88\uff0c\u4eca\u665a\u8d31\u5984\u7565 \u5907\u6c34\u9152\uff0c\u5728\u655d\u821f\u606d\u5019\u516c\u5b50\u5927\u9a7e\uff0c\u804a\u62a5\u6b64\u60c5\u3002\u201d \u3000\u3000\u8bf4\u7f62\u53c8\u6df1\u6df1\u4e00\u62dc\uff0c\u8f6c\u5934\u8d70\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u66f4\u8ff7\u60d8\u4e86\uff0c\u4ed6\u518d\u4e5f\u60f3\u4e0d\u900f\uff0c\u8fd9\u4e2a\u98ce\u534e\u7edd\u4ee3\u7684\u5c11\u5973\uff0c\u7adf\u662f\u90a3\u4e10\u8005\u7684\u5973\u513f\uff0c\u4ed6\u66f4\u60f3 \u4e0d\u900f\u4e3a\u4f55\u8fd9\u5c11\u5973\u8bf7\u81ea\u5df1\u5230\u821f\u4e0a\u996e\u9152\uff0c\u53c8\u8bf4\u81ea\u5df1\u7167\u987e\u4e86\u5979\u7684\u7236\u4eb2\uff0c\u96be\u9053\u8fd9\u4e10\u8005\u771f\u662f\u5979\u7236\u4eb2 \u5417\uff1f\u5373\u4f7f\u8fd9\u4e10\u8005\u662f\u5979\u7236\u4eb2\uff0c\u81ea\u5df1\u4e5f\u672a\u7167\u987e\u8fc7\u8fd9\u4e10\u8005\u5440\u3002 \u3000\u3000\u4f55\u51b5\u5979\u7684\u8239\u662f\u54ea\u4e00\u6761\u5462\uff1f\u6c5f\u8fb9\u4e0a\u6709\u8bb8\u591a\u8239\uff0c\u53c8\u600e\u77e5\u54ea\u4e00\u8258\u662f\u5462\uff1f\u81ea\u5df1\u5373\u4f7f\u6709\u5fc3\u8d74\u7ea6\uff0c \u4f46\u4e5f\u603b\u4e0d\u80fd\u6761\u6761\u8239\u90fd\u53bb\u95ee\u4e00\u95ee\u5440\u3002 \u3000\u3000\u8fd9\u8bb8\u591a\u95ee\u9898\u5728\u8f9b\u633a\u5fc3\u5934\u6253\u7740\u8f6c\uff0c\u4ed6\u81ea\u8bed\u9053\uff1a\u201c\u5947\u9047\uff0c\u5947\u9047\uff0c\u7684\u786e\u662f\u5947\u9047\uff0c\u8fd9\u5c11\u5973\u7f8e \u5f97\u79bb\u5947\uff0c\u4e5f\u602a\u5f97\u79bb\u5947\uff0c\u8fd9\u756a\u5012\u662f\u7ed9\u8303\u6cbb\u6210\u8bf4\u4e2d\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u731b\u5730\u4e00\u62cd\u524d\u989d\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u771f\u662f\u7cca\u6d82\uff0c\u90a3\u8303\u6cbb\u6210\u770b\u6765\u77e5\u9053\u8fd9\u602a\u4e10\u7684\u5e95 \u7ec6\uff0c\u4eca\u65e5\u56de\u53bb\uff0c\u6211\u4e00\u603b\u95ee\u4ed6\uff0c\u4e0d\u662f\u4ec0\u4e48\u4e8b\u90fd\u77e5\u9053\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u6682\u4e14\u5c06\u8fd9\u4e9b\u95ee\u9898\u629b\u5f00\uff0c\u6574\u4e86\u6574\u8863\u886b\uff0c\u5411\u4ecd\u5728\u6c5f\u8fb9\u7b49\u7740\u81ea\u5df1\u7684\u6e21\u8239\u8d70\u53bb\u3002 \u3000\u3000\u4f46\u8239\u81f3\u6c5f\u5fc3\uff0c\u8f9b\u6377\u671b\u7740\u6d69\u6e2f\u6c5f\u6c34\uff0c\u5fc3\u601d\u4ecd\u7136\u7d0a\u4e71\u5f97\u5f88\u5728\u77f3\u5ba4\u4e2d\u7684\u5341\u5e74\uff0c\u4ed6\u4e60\u60ef\u5355\u8c03 \u800c\u67af\u71e5\u7684\u751f\u6d3b\uff0c\u4e60\u60ef\u4e86\u9664\u5374\u6b66\u529f\u4e4b\u5916\uff0c\u4ed6\u4e0d\u53bb\u60f3\u4efb\u4f55\u4e8b\uff0c\u4f46\u662f\u6b64\u523b\u4ed6\u79bb\u5f00\u77f3\u5ba4\u8e0f\u5165\u6c5f\u6e56 \u53ea\u5be5\u5be5\u56db\u3001\u4e94\u5929\uff0c\u5df2\u6709\u90a3\u4e48\u591a\u4e8b\u9700\u8981\u4ed6\u53bb\u8003\u8651\u548c\u601d\u7d22\u4e86\u3002 \u3000\u3000\u6885\u5c71\u6c11\u4ea4\u7ed9\u4ed6\u7684\uff0c\u662f\u4e00\u4ef6\u90a3\u4e48\u56f0\u96be\u548c\u590d\u6742\u7684\u4efb\u52a1\u3002 \u3000\u3000\u5341\u5e74\u524d\u7684\u60e8\u75db\u7684\u56de\u5fc6\u3002\u4ed6\u4e5f\u5e76\u672a\u56e0\u65f6\u95f4\u7684\u957f\u4e45\uff0c\u800c\u6709\u6240\u6de1\u5fd8\u3002 \u3000\u3000\u518d\u52a0\u4e0a\u4ed6\u81ea\u5df1\u6700\u8fd1\u624d\u611f\u89c9\u5230\u7684\u90a3\u4e00\u79cd\u201c\u751c\u5bc6\u7684\u70e6\u607c\u201d\u4ed6\u66fe\u7528\u4e86\u8bb8\u591a\u529b\u6c14\u6551\u56de\u6765\u7684\u65b9 \u59d3\u5c11\u5973\u90a3\u54c0\u6028\u800c\u7f8e\u4e3d\u7684\u773c\u6674\uff0c\u9ec4\u9e64\u697c\u4e0b\u7684\u7fe0\u7eff\u5c11\u5973\u7684\u751c\u751c\u7684\u7b11\uff0c\u90fd\u4f7f\u4ed6\u5fc3\u6e56\u4e2d\u8d77\u8457\u6d9f\u6f2a\u3002 \u3000\u3000\u5c31\u7b97\u662f\u51e4\u6797\u73ed\u7684\u90a3\u4e2a\u5993\u5973\u7a1a\u51e4\u5427\uff0c\u867d\u7136\u4ed6\u5351\u89c6\u5979\u7684\u804c\u4e1a\uff0c\u4f46\u90a3\u79cd\u6210\u719f\u5973\u5b50\u7684\u67d4\u60c5\u98ce \u97f5\uff0c\u4e5f\u662f\u4ed6\u4ece\u672a\u7ecf\u5386\u8fc7\u7684\uff0c\u4e5f\u4f7f\u5f97\u4ed6\u6df1\u6df1\u5730\u523a\u6fc0\u7740\uff0c\u867d\u7136\u4ed6\u5206\u4e0d\u6e05\u90a3\u662f\u5c5e\u4e8e\u5fc3\u7075\u7684\uff0c\u8fd8 \u662f\u5c5e\u4e8e\u8089\u4f53\u7684\u3002 \u3000\u3000\u8239\u9760\u4e86\u5cb8\u3002 \u3000\u3000\u90a3\u8f66\u592b\u6b63\u5750\u5728\u8f66\u4e0a\uff0c\u7f29\u5728\u8863\u9886\u91cc\u75b2\u5026\u800c\u5931\u795e\u5730\u7b49\u7740\u4ed6\uff0c\u4ed6\u4e0d\u7981\u5f00\u59cb\u5bf9\u4e16\u754c\u4e0a\u4e00\u4e9b\u8d2b \u82e6\u800c\u5351\u5fae\u7684\u4eba\u4eec\uff0c\u8d77\u4e86\u4e00\u79cd\u601c\u60af\u7684\u540c\u60c5\u3002 \u3000\u3000\u8f66\u592b\u89c1\u4ed6\u6765\u4e86\uff0c\u6b23\u559c\u5730\u8df3\u4e0b\u8f66\u6765\uff0c\u6253\u5f00\u8f66\u95e8\uff0c\u606d\u656c\u5730\u95ee\u9053\uff1a\u201c\u8001\u7237\u56de\u5bb6\u53bb\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u70b9\u4e86\u70b9\u5934\uff0c\u4ed6\u5f00\u59cb\u60f3\uff1a\u201c\u4eba\u4eec\u7684\u6b32\u671b\u6709\u7740\u591a\u5927\u7684\u4e0d\u540c\u5440\uff01\u8fd9\u8f66\u592b\u770b\u5230\u6211\u6765\u4e86\uff0c \u5c31\u89c9\u5f97\u5f88\u6ee1\u8db3\u548c\u6b23\u559c\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u53ef\u4ee5\u56de\u5230\u4ed6\u90a3\u5e76\u4e0d\u8212\u9002\u7684\u5e8a\u4e0a\uff0c\u4e0d\u518d\u800c\u8981\u5728\u6e05\u6668\u7684\u98ce\u91cc \u7b49\u6211\uff0c\u800c\u6211\u7684\u6b32\u671b\u5462\uff1f\u5230\u73b0\u5728\u6211\u8fd8\u4e0d\u77e5\u9053\u6211\u7684\u6b32\u671b\u7a76\u7adf\u662f\u4ec0\u4e48\uff0c\u53ea\u77e5\u9053\u90a3\u662f\u4e00\u79cd\u5f3a\u70c8\u7684 \u6b32\u671b\uff0c\u5e0c\u671b\u6211\u6240\u5f97\u5230\u7684\u90fd\u662f\u65e0\u4e0a\u7684\u5b8c\u7f8e\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u6211\u80fd\u5f97\u5230\u5417\uff1f\u201d\u4ed6\u957f\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u8d70\u5230\u8f66\u5b50\u4e0a\u3002 \u3000\u3000\u8f66\u53a2\u91cc\u5bc2\u5bde\u800c\u5c0f\uff0c\u4ed6\u671b\u7740\u89d2\u843d\uff0c\u6b64\u523b\u4ed6\u591a\u4e48\u5e0c\u671b\u90a3\u66fe\u5728\u89d2\u843d\u91cc\u60ca\u60f6\u5730\u8737\u4f0f\u7740\u7684\u5973\u5b69\uff0c \u73b0\u5728\u6b63\u4f34\u7740\u4ed6\u5750\u5728\u8f66\u5b50\u91cc\u5462\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u50ac\u4fc3\u7740\u8f66\u592b\uff0c\u5feb\u4e9b\u8d76\u8f66\uff0c\u5176\u5b9e\u4ed6\u672c\u77e5\u9053\uff0c\u4ece\u6c5f\u8fb9\u56de\u5bb6\uff0c\u53ea\u662f\u4e00\u6bb5\u5f88\u77ed\u7684\u8def\u800c \u5df2\u3002 \u3000\u3000\u5c71\u6885\u73e0\u5b9d\u53f7\u521a\u542f\u4e0b\u95e8\uff0c\u5e97\u4f19\u4eec\u60fa\u677e\u7740\u7761\u773c\u5728\u505a\u7740\u6742\u4e8b\u3002 \u3000\u3000\u8f9b\u6377\u6f20\u7136\u5bf9\u5411\u4ed6\u6bb7\u52e4\u5730\u62db\u547c\u7740\u7684\u5e97\u4f19\u4eec\u70b9\u4e86\u70b9\u5934\u3002\u6bd5\u76f4\u5730\u8d70\u5411\u90a3\u5c11\u5973\u7684\u623f\u91cc\u3002 \u3000\u3000\u4ed6\u5e76\u672a\u6572\u95e8\uff0c\u591a\u5e74\u6765\u77f3\u5ba4\u7684\u72ec\u5c45\uff0c\u4f7f\u4ed6\u6839\u672c\u5bf9\u4e16\u4fd7\u7684\u4e00\u4e9b\u793c\u4eea\u65e0\u6cd5\u9075\u5b88\u3002\u867d\u7136\u4ed6\u8bfb \u8fc7\u8bb8\u591a\u4e66\uff0c\u4f46\u6bcf\u5f53\u505a\u8d77\u6765\uff0c\u4ed6\u603b\u662f\u5e38\u5e38\u9057\u5fd8\u4e86\uff0c\u800c\u53ea\u662f\u51ed\u7740\u81ea\u5df1\u5fc3\u4e2d\u597d\u6076\uff0c\u968f\u610f\u5730\u53bb\u505a \u7740\u3002 \u3000\u3000\u90a3\u5c11\u5973\u6b63\u65e0\u804a\u5730\u659c\u501a\u5728\u5e8a\u4e0a\uff0c\u89c1\u5f97\u4ed6\u8fdb\u6765\u4e86\uff0c\u5f20\u53e3\u60f3\u53eb\u4ed6\uff0c\u4f46\u77ac\u5373\u53c8\u53d1\u89c9\u81ea\u5df1\u7684\u5931 \u4eea\uff0c\u7ea2\u7740\u8138\u9760\u4e86\u56de\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u5f97\u5fc3\u91cc\u751c\u751c\u7684\uff0c\u542b\u7740\u7b11\uff0c\u6e29\u67d4\u5730\u8bf4\uff1a\u201c\u59d1\u5a18\u5728\u8fd9\u91cc\u53ef\u5b89\u9002\u5417\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u776b\u6bdb\u4e00\u62ac\uff0c\u660e\u4eae\u773c\u6674\u91cc\u7684\u54c0\u6028\u90c1\u5fe7\u4e4b\u8272\uff0c\u90fd\u51cf\u5c11\u4e86\u5927\u534a\uff0c\u800c\u6362\u4e0a\u4e00\u79cd\u9519\u7efc\u590d \u6742\u7684\u5149\u8292\u3002 \u3000\u3000\u5979\u542b\u7740\u7f9e\u8bf4\u9053\uff1a\u201c\u6211\u59d3\u65b9\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u5fd9\u5e94\u58f0\u9053\uff1a\u201c\u65b9\u59d1\u5a18\uff0c\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u89c9\u5f97\u7a81\u7136\u6709\u4e86\u4e00\u79cd\u5b81\u9759\u7684\u611f\u89c9\uff0c\u89c1\u4e86\u8fd9\u5c11\u5973\uff0c\u4ed6\u4eff\u4f5b\u5728\u611f\u60c5\u4e0a\u6709\u4e86\u4e00\u79cd\u53ef\u4ee5 \u4f9d\u9760\u7684\u5730\u65b9\uff0c\u518d\u4e0d\u8981\u53bb\u62c5\u5fc3\u81ea\u5df1\u7684\u5b64\u96f6\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5df2\u7f9e\u5f97\u53c8\u4f4e\u4e0b\u4e86\u5934\uff0c\u987b\u77e5\u4e00\u4e2a\u672a\u5ac1\u5973\u5b50\uff0c\u5411\u4e00\u4e2a\u964c\u751f\u7537\u5b50\u8bf4\u51fa\u81ea\u5df1\u7684\u59d3\u6c0f\uff0c \u90a3\u5176\u4e2d\u7684\u542b\u4e49\u662f\u975e\u5e38\u6df1\u8fdc\u7684\uff0c\u90a3\u8868\u793a\u5728\u8fd9\u5973\u5b50\u5fc3\u76ee\u4e2d\uff0c\u81f3\u5c11\u5df1\u5bf9\u8fd9\u7537\u5b50\u6709\u4e86\u4e00\u4efd\u5f88\u6df1\u7684 \u60c5\u610f\u3002 \u3000\u3000\u5979\u81ea\u5c0f\u6240\u89c1\u7684\u7537\u5b50\uff0c\u4e0d\u662f\u6751\u592b\uff0c\u4fbf\u662f\u7a77\u76d7\uff0c\u548c\u90a3\u9634\u9633\u602a\u6c14\u7684\u91d1\u6b39\uff0c\u8f9b\u633a\u723d\u671f\u7684\u82f1\u59ff\uff0c \u548c\u853c\u7684\u7b11\u5bb9\uff0c\u4f7f\u5f97\u5979\u5c11\u5973\u795e\u5723\u800c\u4e25\u5bc6\u7684\u5fc3\u6249\uff0c\u7f13\u7f13\u5f00\u4e86\u3002 \u3000\u3000\u867d\u7136\u5979\u5e76\u4e0d\u4e86\u89e3\u8f9b\u6377\uff0c\u751a\u81f3\u6839\u672c\u4e0d\u8ba4\u5f97\u4ed6\uff0c\u4f46\u4eba\u7c7b\u7684\u60c5\u611f\u5374\u662f\u6700\u5947\u602a\u7684\uff0c\u5f80\u5f80\u4f60\u5bf9 \u4e00\u4e2a\u521d\u89c1\u9762\u7684\u4eba\u6240\u6709\u7684\u60c5\u611f\uff0c\u8fdc\u6bd4\u4e00\u4e2a\u4f60\u671d\u5915\u76f8\u5904\u5f88\u4e45\u7684\u4e3a\u6df1\uff0c\u5c24\u5176\u662f\u7537\u5973\u4e4b\u95f4\u7684\u60c5\u611f\uff0c \u66f4\u6bcf\u591a\u5982\u6b64\u3002 \u3000\u3000\u8f9b\u6377\u5f53\u7136\u5e76\u4e0d\u77e5\u9053\u5979\u5fc3\u4e2d\u6240\u60f3\u7684\uff0c\u4ed6\u5bf9\u4eba\u7c7b\u7684\u5fc3\u7406\uff0c\u4e86\u89e3\u5f97\u8fdc\u4e0d\u5982\u4ed6\u81ea\u5df1\u60f3\u50cf\u5f97\u591a\u3002 \u3000\u3000\u623f\u95f4\u91cc\u4e24\u4eba\u90fd\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u4f46\u7a7a\u6c14\u4e2d\u5374\u5145\u6ee1\u4e86\u4e00\u79cd\u5f02\u5e38\u7684\u548c\u853c\uff0c\u53ea\u8981\u4e24\u60c5\u6b22\u60a6\uff0c\u53c8 \u5c82\u662f\u4efb\u4f55\u8a00\u8bed\u6240\u80fd\u4ee3\u8868\u7684\u3002 \u3000\u3000\u8f9b\u6377\u832b\u7136\u627e\u7740\u8bed\u9898\uff0c\u53c8\u95ee\u4e86\u53e5\uff1a\u201c\u59d1\u5a18\u5728\u8fd9\u91cc\u53ef\u5b89\u9002\u5417\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u7adf\u6447\u4e86\u6447\u5934\u3002\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5bc2\u83ab\u5f97\u5f88\uff0c\u6ca1\u6709\u4e8b\u505a\uff0c\u53c8\u4e0d\u6562\u51fa\u53bb\u3002\u201d \u3000\u3000\u5979\u4e0e\u8f9b\u6377\u4e4b\u95f4\uff0c\u6b64\u65f6\u7adf\u50cf\u6709\u4e86\u4e00\u4efd\u6df1\u6df1\u7684\u4e86\u89e3\uff0c\u662f\u4ee5\u5979\u6beb\u4e0d\u9690\u7792\u5730\u8bf4\u51fa\u81ea\u5df1\u5fc3\u4e2d\u6240 \u60f3\u7684\u8bdd\u3002 \u3000\u3000\u8f9b\u6377\u70b9\u4e86\u70b9\u5934\uff0c\u4e5f\u6beb\u672a\u89c9\u5f97\u5979\u8bf4\u7684\u8bdd\uff0c\u5bf9\u4e00\u4e2a\u76f8\u8bc6\u6570\u9762\u7684\u4eba\u6765\u8bf4\uff0c\u662f\u592a\u7387\u76f4\u4e86\u4e9b\uff0c \u4ed6\u60f3\u4e86\u4e00\u4f1a\uff0c\u6073\u5207\u5730\u8bf4\uff1a\u201c\u59d1\u5a18\u4e00\u5b9a\u6709\u8bb8\u591a\u5fc3\u4e8b\uff0c\u6211\u4e0d\u77e5\u9053\u59d1\u5a18\u53ef\u4e0d\u53ef\u4ee5\u544a\u8bc9\u6211\u4e00\u4e9b\uff1f\u201d \u3000\u3000\u4ed6\u5fae\u5401\u4e00\u58f0\uff0c\u611f\u52a8\u5730\u53c8\u8bf4\u9053\uff1a\u201c\u800c\u4e14\u6211\u77e5\u9053\u59d1\u5a18\u4e00\u5b9a\u6709\u7740\u8bb8\u591a\u4f24\u5fc3\u7684\u4e8b\uff0c\u5176\u5b9e\u6211\u548c \u59d1\u5a18\u4e00\u6837\uff0c\u5f80\u4e8b\u6bcf\u6bcf\u90fd\u4ee4\u6211\u96be\u53d7\u5f97\u5f88\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u4f4e\u58f0\u555c\u6ce3\u4e86\u8d77\u6765\uff0c\u8fd9\u8bb8\u591a\u65e5\u5b50\u91cc\u5979\u6240\u53d7\u7684\u59d4\u5c48\uff0c\u6240\u4e0d\u80fd\u5411\u4eba\u8bc9\u8bf4\u7684\u59d4\u5c48\uff0c\u6b64 \u65f6\u90fd\u50cf\u6709\u4e86\u8bc9\u8bf4\u7684\u5bf9\u8c61\uff0c\u5979\u54bd\u7740\uff0c\u8bf4\u51fa\u81ea\u5df1\u7684\u906d\u9047\uff0c\u8bf4\u5230\u5979\u7684\u201c\u7236\u4eb2\u201d\u65b9\u8001\u6b66\u5e08\uff0c\u8bf4\u5230 \u5979\u7684\u201c\u6b39\u54e5\u201d\uff0c\u8bf4\u5230\u81ea\u5df1\u7684\u4f36\u4ec3\u5b64\u82e6\uff0c\u4ee5\u53ca\u81ea\u5df1\u6240\u53d7\u7684\u6b3a\u51cc\u3002 \u3000\u3000\u8f9b\u6377\u663e\u7136\u662f\u88ab\u6df1\u6df1\u5730\u611f\u52a8\u4e86\uff0c\u4ed6\u6781\u4e3a\u7559\u5fc3\u542c\u7740\uff0c\u5f53\u4ed6\u542c\u5230\u201c\u91d1\u6b39\u201d\u8fd9\u4e2a\u540d\u5b57\u65f6\uff0c\u4ed6 \u7acb\u523b\u89c9\u5f97\u5fc3\u4e2d\u5347\u8d77\u4e00\u79cd\u201c\u4e0d\u80fd\u4e24\u7acb\u201d\u7684\u6124\u6012\u3002 \u3000\u3000\u4ed6\u6e29\u67d4\u7684\u529d\u7740\u5979\uff0c\u63e1\u7740\u5979\u7684\u624b\uff0c\u5979\u4e5f\u987a\u4ece\u5730\u8ba9\u4ed6\u63e1\u7740\uff0c\u5f7c\u6b64\u5fc3\u4e2d\uff0c\u90fd\u89c9\u5f97\u8fd9\u662f\u90a3\u4e48 \u81ea\u7136\u7684\u4e8b\uff0c\u4e00\u4e1d\u4e5f\u6ca1\u6709\u52c9\u5f3a\uff0c\u6ca1\u6709\u751f\u6da9\u3002 \u3000\u3000\u8f9b\u6377\u79bb\u5f00\u5979\u623f\u95f4\u7684\u65f6\u5019\uff0c\u5fc3\u91cc\u5df2\u89c9\u5f97\u4e0d\u518d\u7a7a\u865a\uff0c\u4ed6\u7684\u5fc3\u91cc\uff0c\u5df2\u6709\u4e00\u4e2a\u5c11\u5973\u7684\u7eaf\u771f\u7684 \u60c5\u611f\u5728\u5145\u5b9e\u7740\uff0c\u4e24\u4e2a\u5bc2\u83ab\u7684\u4eba\uff0c\u5f7c\u6b64\u89e3\u9664\u4e86\u5bf9\u65b9\u7684\u5bc2\u83ab\uff0c\u8fd9\u662f\u591a\u4e48\u7f8e\u597d\u800c\u5947\u5999\u7684\u4e8b\u5440\uff01 \u3000\u3000\u4ed6\u4f4e\u58f0\u5ff5\u9053\uff1a\u201c\u65b9\u5c11\u74a7\uff0c\u65b9\u5c11\u74a7\uff01\u201d\u4ed6\u7b11\u4e86\u3002\u8fd9\u4e09\u4e2a\u5b57\uff0c\u5bf9\u4ed6\u800c\u8a00\uff0c\u4e0d\u4ec5\u4ec5\u662f\u4e09\u4e2a \u5b57\u800c\u5df2\uff0c\u5176\u4e2d\u6240\u5305\u6db5\u7684\u610f\u601d\uff0c\u662f\u96be\u4ee5\u8a00\u55bb\u7684\u3002 \u3000\u3000\u8fd9\u79cd\u6e29\u99a8\u7684\u611f\u89c9\uff0c\u5728\u4ed6\u5fc3\u91cc\u76d8\u636e\u7740\uff0c\u4f46\u662f\u522b\u7684\u95ee\u9898\u7ec8\u4e8e\u6765\u4e86\u3002 \u3000\u3000\u6709\u8bb8\u591a\u4e8b\uff0c\u90fd\u8981\u4ed6\u53bb\u89e3\u51b3\uff0c\u6700\u8feb\u5207\u7684\u4e00\u6869\uff0c\u5c31\u662f\u9ec4\u9e64\u697c\u4e0b\u7684\u602a\u4e10\u548c\u7eff\u8863\u5973\u6240\u8ba2\u7684\u7ea6 \u4f1a\u3002 \u3000\u3000\u4ed6\u7684\u786e\u88ab\u8fd9\u4ef6\u4e8b\u6240\u5438\u5f15\u4e86\uff0c\u597d\u5947\u4e4b\u5916\uff0c\u8fd8\u6709\u79cd\u60f3\u5f97\u5230\u4e9b\u4ec0\u4e48\u7684\u6b32\u671b\uff0c\u662f\u4ee5\u4ed6\u51b3\u5b9a\u5fc5 \u987b\u53bb\u8d74\u7ea6\uff0c\u4ed6\u60f3\u8d77\u65b9\u5c11\u74a7\uff0c\u4e8e\u662f\u4ed6\u81ea\u5df1\u5b89\u6170\u7740\u81ea\u5df1\uff1a\u201c\u6211\u8d74\u7ea6\u7684\u539f\u56e0\u53ea\u662f\u4e3a\u4e86\u597d\u5947\u7f62\u4e86\uff0c \u90a3\u5c11\u5973\u7684\u7f8e\u8c8c\u548c\u7b11\uff0c\u5bf9\u6211\u5df2\u4e0d\u91cd\u8981\u4e86\uff0c\u56e0\u4e3a\u6211\u7684\u60c5\u611f\uff0c\u5df2\u5145\u5b9e\u5f97\u4e0d\u518d\u9700\u8981\u522b\u4eba\u4e86\u3002\u201d \u3000\u3000\u8fd9\u662f\u6bcf\u4e00\u4e2a\u521d\u5760\u60c5\u7f51\u7684\u4eba\u5168\u6709\u7684\u611f\u89c9\uff0c\u95ee\u9898\u662f\u5728\u4ed6\u8fd9\u79cd\u611f\u89c9\u80fd\u6301\u7eed\u591a\u4e45\u5c31\u662f\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u53eb\u4eba\u51c6\u5907\u597d\u8f66\u5b50\uff0c\u4ed6\u8981\u53bb\u627e\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\uff0c\u53bb\u95ee\u95ee\u90a3\u602a\u4e10\u548c\u5c11\u5973\u7684\u6765\u5386\uff0c \u5f53\u7136\uff0c\u4ed6\u4e5f\u662f\u53bb\u95ee\u4ed6\u4eec\u6240\u5750\u7684\u8239\uff0c\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u7279\u522b\u7684\u6807\u8bb0\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u811a\u8fc8\u51fa\u5927\u95e8\uff0c\u5374\u89c1\u4e00\u5339\u5065\u9a6c\u500f\u5730\u5728\u95e8\u524d\u505c\u4e0b\uff0c\u9a6c\u80cc\u4e0a\u8df3\u4e0b\u6765\u7684\u6b63\u662f\u4ed6\u8981\u53bb\u63a2 \u8bbf\u7684\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\u3002 \u3000\u3000\u8303\u6cbb\u6210\u89c1\u8f9b\u6377\u6b65\u5c65\u4ece\u5bb9\u50cf\u662f\u6839\u672c\u6ca1\u6709\u4efb\u4f55\u4e8b\u53d1\u751f\uff0c\u559c\u9053\uff1a\u201c\u8f9b\u5144\u5df2\u56de\u6765\u4e86\uff1f\u597d\u6781 \u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u4e00\u6115\u9053\uff1a\u201c\u6211\u5f53\u7136\u56de\u6765\u4e86\uff0c\u4f60\u8fd9\u8bdd\u95ee\u5f97\u5c82\u975e\u5947\u602a\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u4e00\u628a\u62c9\u7740\u8f9b\u6377\uff0c\u8d70\u8fdb\u5e97\u9762\uff0c\u8fb9\u8d70\u8fb9\u95ee\u9053\uff1a\u201c\u90a3\u91d1\u4e00\u9e4f\u53ef\u66fe\u5bf9\u8f9b\u5144\u8bf4\u8fc7\u4ec0\u4e48 \u8bdd\u3002\u201d \u3000\u3000\u8f9b\u6377\u53c8\u662f\u4e00\u6837\uff0c\u5fd6\u9053\uff1a\u201c\u91d1\u4e00\u9e4f\u53c8\u662f\u4ec0\u4e48\u4eba\uff1f\u201d\u4f46\u4ed6\u968f\u5373\u4f1a\u610f\uff1a\u201c\u60f3\u6765\u5fc5\u5b9a\u5c31\u662f\u90a3 \u5947\u602a\u7684\u4e10\u8005\u4e86\u3002\u201d\u4e8e\u662f\u8bf4\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u90a3\u8fde\u8f9b\u6377\u90fd\u4e0d\u77e5\u9053\u6765\u5386\u7684\u4faf\u4e8c\uff0c\u6b64\u65f6\u6b63\u5750\u5728\u67dc\u53f0\u91cc\uff0c\u542c\u5f97\u91d1\u5f13\u795e\u5f39\u8bf4\u4e86\u91d1\u4e00\u9e4f\u4e09\u5b57\uff0c \u9762\u8272\u4e00\u53d8\uff0c\u4f3c\u4e4e\u8fd9\u201c\u91d1\u4e00\u9e4f\u201d\u4e09\u5b57\uff0c\u4f7f\u4ed6\u611f\u5230\u83ab\u5927\u7684\u9519\u6115\u548c\u60ca\u5f02\uff0c\u751a\u81f3\u8fd8\u5e26\u7740\u4e9b\u8bb8\u6050\u60e7 \u7684\u610f\u5473\u3002 \u3000\u3000\u4ed6\u7ad9\u4e86\u8d77\u6765\uff0c\u60f3\u8d70\u51fa\u67dc\u53f0\uff0c\u60f3\u4e86\u60f3\uff0c\u770b\u4e86\u8303\u6cbb\u6210\u4e00\u773c\uff0c\u53c8\u5750\u4e86\u56de\u53bb\u3002 \u3000\u3000\u8303\u6cbb\u6210\u5f53\u7136\u4e0d\u4f1a\u6ce8\u610f\u5230\u8fd9\u4e9b\uff0c\u4ed6\u542c\u5230\u8f9b\u6377\u8bf4\uff1a\u201c\u6ca1\u4ec0\u4e48\u3002\u201d\u8138\u4e0a\u4e00\u4efd\uff0c\u50cf\u662f\u9ad8\u5174\uff0c \u53c8\u50cf\u662f\u5931\u671b\uff0c\u4f46\u8f9b\u6377\u968f\u5373\u8bf4\uff1a\u201c\u4e0d\u8fc7\u2026\u2026\u201d\u4ed6\u7acb\u523b\u622a\u4f4f\u8bdd\u5934\uff0c\u95ee\u9053\uff1a\u201c\u4e0d\u8fc7\u600e\u5730\uff1f\u201d \u3000\u3000\u8f9b\u6377\u7b11\u4e86\u4e00\u7b11\uff0c\u63a5\u7740\u9053\uff1a\u201c\u4e0d\u8fc7\u4ed6\u6709\u4e2a\u5973\u513f\uff0c\u5374\u9080\u6211\u4eca\u665a\u53bb\u4ed6\u821f\u4e2d\u4e00\u6664\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u987f\u73b0\u5f02\u5bb9\uff0c\u95ee\u9053\uff1a\u201c\u771f\u7684\uff01\u201d \u3000\u3000\u8f9b\u6377\u62c2\u7136\u9053\uff1a\u201c\u5c0f\u5f1f\u600e\u6562\u6b3a\u9a97\u5144\u53f0\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u5fd9\u9053\uff1a\u201c\u5c0f\u5f1f\u4e0d\u662f\u6b64\u610f\uff0c\u53ea\u662f\u6b64\u4e8b\u6765\u5f97\u592a\u8fc7\u8be1\u5f02\uff0c\u8f9b\u5144\u4e0d\u77e5\u6b64\u4eba\u4e4b\u6765\u5386\uff0c\u5fc3 \u4e2d\u662f\u5766\u7136\uff0c\u53ea\u662f\u5c0f\u5f1f\u5374\u6709\u4e9b\u66ff\u8f9b\u5144\u7740\u6025\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u8fb9\u8d70\u8fb9\u8bf4\uff0c\u8303\u6cbb\u6210\u4e0d\u7b49\u8f9b\u6377\u8bf4\u8bdd\uff0c\u53c8\u62a2\u9053\uff1a\u201c\u8fd9\u4e09\u5929\u6765\u6b66\u6c49\u4e09\u9547\u5947\u4e8b\u9891\u51fa\uff0c\u771f \u628a\u5c0f\u5f1f\u7ed9\u5f04\u7cca\u6d82\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u672c\u5c31\u63e3\u6d4b\u90a3\u91d1\u4e00\u9e4f\u7236\u5973\u5fc5\u975e\u5e38\u4eba\uff0c\u4ed6\u627e\u91d1\u5f13\u795e\u5f39\uff0c\u4e5f\u5c31\u662f\u60f3\u6253\u542c\u6b64\u4e8c\u4eba\u7684\u6765\u5386\uff0c \u6b64\u523b\u542c\u8303\u6cbb\u6210\u5982\u6b64\u8bf4\uff0c\u66f4\u8bc1\u5b9e\u4e86\u5fc3\u4e2d\u7684\u63e3\u6d4b\u3002 \u3000\u3000\u4ed6\u5165\u4e16\u867d\u6d45\uff0c\u5fc3\u667a\u5374\u662f\u673a\u53d8\u767e\u51fa\uff0c\u770b\u5230\u8303\u6cbb\u6210\u5982\u6b64\uff0c\u5fc3\u77e5\u4fbf\u662f\u81ea\u5df1\u4e0d\u95ee\uff0c\u8303\u6cbb\u6210\u4e5f \u4f1a\u5c06\u6b64\u4eba\u7684\u6765\u5386\u8bf4\u51fa\uff0c\u4e8e\u662f\u53cd\u800c\u4f5c\u51fa\u6de1\u7136\u4e4b\u6001\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e00\u8d70\u8fdb\u540e\u5385\uff0c\u8303\u6cbb\u6210\u5c31\u5fcd\u4e0d\u4f4f\u8bf4\u9053\uff1a\u201c\u8f9b\u5144\uff0c\u4f60\u53ef\u77e5\u9053\u4f60\u9047\u89c1\u7684\u662f\u4f55\u7b49\u4eba\u7269 \u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e00\u7b11\uff0c\u6447\u5934\u9053\uff1a\u201c\u5c0f\u5f1f\u81ea\u662f\u4e0d\u77e5\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u53f9\u9053\uff1a\u201c\u8f9b\u5144\u82e5\u662f\u77e5\u9053\uff0c\u6b64\u523b\u60f3\u4e5f\u4e0d\u4f1a\u5982\u6b64\u5fc3\u5b89\u7406\u5f97\u4e86\u3002\u201d \u3000\u3000\u4ed6\u671d\u5385\u4e0a\u7684\u6a80\u6728\u9760\u6905\u91cc\u4e00\u5750\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u5148\u524d\u6211\u8fd8\u4e0d\u76f8\u4fe1\u6b64\u4eba\u771f\u662f\u91d1\u4e00\u9e4f\uff0c\u540e\u6765\u4e00 \u60f3\uff0c\u9664\u4e86\u4ed6\u5916\uff0c\u8fd8\u6709\u8c01\u5462\uff0c\u8f9b\u5144\u4e0d\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u5e74\u8f7b\u53c8\u8f83\u8f7b\uff0c\u81ea\u662f\u4e0d\u4f1a\u8bc6\u5f97\u6b64\u4eba\uff0c\u4f46\u5c0f \u5f1f\u5728\u6c5f\u6e56\u4e2d\u6df7\u4e86\u4e8c\u3001\u4e09\u5341\u5e74\uff0c\u542c\u5230\u6709\u5173\u6b64\u4eba\u4e4b\u4f20\u8bf4\uff0c\u4e0d\u77e5\u591a\u5c11\u56de\u4e86\uff0c\u662f\u4ee5\u5c0f\u5f1f\u4e00\u89c1\u6b64\u4eba\uff0c \u4fbf\u80fd\u8ba4\u51fa\u6b64\u4eba\u7684\u6765\u5386\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u4ecd\u672a\u8f6c\u5165\u6b63\u9898\uff0c\u8bf4\u5230\u6b64\u4eba\u6765\u5386\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u6b64\u4eba\u7a76\u7adf\u662f\u8c01\u5440\uff1f\u201d \u3000\u3000\u8303\u6cbb\u6210\u53c8\u53f9\u9053\uff1a\u201c\u4e8c\u5341\u591a\u5e74\u524d\uff0c\u6c5f\u6e56\u4e0a\u6709\u53e5\u4fd7\u8bed\uff0c\u9053\uff1a\u2018\u9047\u89c1\u4e24\u541b\uff0c\u9e21\u72ac\u4e0d\u5b81\u3002\u2019 \u9e21\u72ac\u5c1a\u4e14\u4e0d\u5b81\uff0c\u4f55\u51b5\u4eba\u5462\uff1f\u6c5f\u6e56\u4e2d\u4eba\u751a\u81f3\u4ee5\u6b64\u8d4c\u5492\uff0c\u8c01\u90fd\u4e0d\u613f\u9047\u5230\u8fd9\u2018\u4e24\u541b\u2019\uff0c\u8fd9\u4e24\u4e2a \u4eba\u4e00\u4e2a\u662f\u8001\u5999\u795e\u541b\u6885\u5c71\u6c11\uff0c\u4e00\u4e2a\u5c31\u662f\u8fd9\u6bd2\u541b\u91d1\u4e00\u9e4f\u4e86\uff0c\u4ed6\u4eec\u4e00\u4ee5\u2018\u4e03\u827a\u2019\u540d\u4f20\u6d77\u5185\uff0c\u4e00 \u4e2a\u5374\u4ee5\u2018\u6bd2\u2019\u9707\u60ca\u5929\u4e0b\uff0c\u8fd9\u91d1\u4e00\u9e4f\u6d51\u8eab\u4e0a\u4e0b\uff0c\u65e0\u4e00\u4e0d\u662f\u6bd2\u7269\uff0c\u6cbe\u7740\u4e9b\uff0c\u5341\u4e8c\u4e2a\u65f6\u8fb0\u5185\u5fc5 \u6b7b\uff0c\u800c\u4e14\u666e\u5929\u4e4b\u4e0b\uff0c\u65e0\u836f\u53ef\u89e3\uff0c\u6c5f\u6e56\u4e0a\u63d0\u8d77\u6bd2\u541b\uff0c\u771f\u662f\u95fb\u800c\u53d8\u8272\u3002\u201d \u3000\u3000\u8f9b\u6377\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u4ed6\u641c\u7d22\u7740\u8bb0\u5fc6\uff0c\u4f46\u6885\u5c71\u6c11\u5374\u7edd\u672b\u5411\u4ed6\u63d0\u8d77\u8fc7\u6b64\u4eba\uff0c\u4e0d\u7981\u4e5f\u9732\u51fa \u8be7\u5f02\u4e4b\u8272\u6765\u3002 \u3000\u3000\u8303\u6cbb\u6210\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u6b64\u4eba\u548c\u4e03\u5999\u795e\u541b\uff0c\u4e00\u5357\u4e00\u5317\uff0c\u672c\u662f\u4e92\u4e0d\u4fb5\u72af\uff0c\u90a3\u77e5 \u4e03\u5999\u795e\u541b\u4e0d\u77e5\u600e\u5730\uff0c\u5374\u5df4\u5df4\u5730\u8dd1\u5230\u5927\u6cb3\u4ee5\u5317\uff0c\u627e\u7740\u6b64\u4eba\uff0c\u8981\u548c\u4ed6\u4e00\u5206\u5f3a\u5f31\uff0c\u8be6\u7ec6\u7684\u60c5\u5f62\uff0c \u6c5f\u6e56\u4e0a\u4eba\u8a00\u4eba\u6b8a\uff0c\u8c01\u4e5f\u4e0d\u77e5\u771f\u8c61\u7a76\u7adf\uff0c\u4f46\u4ece\u90a3\u65f6\u4e4b\u540e\uff0c\u6bd2\u541b\u5374\u4ece\u6b64\u7edd\u8ff9\u6c5f\u6e56\uff0c\u6ca1\u6709\u518d\u73b0 \u8fc7\u8e2a\u5f71\u3002\u201d \u3000\u3000\u201c\u8fd9\u4ef6\u4e8b\u5728\u6c5f\u6e56\u4e0a\u77ac\u5373\u4f20\u904d\uff0c\u4eba\u4eba\u629a\u638c\u79f0\u5feb\uff0c\u751a\u81f3\u6709\u4e9b\u4eba\u8fd8\u4f20\u8bf5\uff1a\u2018 \u3000\u3000\u4e03\u5999\u9664\u6bd2\u541b\uff0c\u6c5f\u6e56\u5f97\u592a\u5e73\u2019\u3002\u201d\u4ed6\u82e6\u7b11\u4e86\u7b11\u5bf9\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u90a3\u4e03\u5999\u795e\u541b\u4e1c\u662f\u6c5f\u6e56\u4e0a \u4eba\u4eba\u89c1\u4e86\u90fd\u5934\u75db\u7684\u89d2\u8272\uff0c\u53ef\u662f\u5927\u5bb6\u5374\u60c5\u613f\u4e03\u5999\u795e\u541b\u9664\u4e86\u8fd9\u6bd2\u541b\uff0c\u8f9b\u5144\u7531\u6b64\u53ef\u4ee5\u60f3\u89c1\u8fd9\u6bd2 \u541b\u7684\u2018\u6bd2\u2019\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u5927\u611f\u5174\u8da3\uff0c\u95ee\u9053\uff1a\u201c\u540e\u6765\u5462\uff1f\u201d \u3000\u3000\u8303\u6cbb\u6210\u9053\uff1a\u201c\u540e\u6765\u2018\u4e03\u5999\u795e\u541b\u2019\u5728\u4e94\u534e\u5c71\u4e00\u4f1a\u4e2d\uff0c\u4f20\u95fb\u8eab\u6b7b\uff0c\u5173\u4e2d\u4e5d\u8c6a\u4e5f\u6d88\u58f0\u706d\u8ff9\uff0c \u6c5f\u6e56\u4e2d\u66f4\u662f\u4e2a\u4e2a\u79f0\u5e86\uff0c\u53ea\u9053\u4ece\u6b64\u771f\u4e2a\u662f\u2018\u592a\u5e73\u2019\u4e86\uff0c\u5176\u5b9e\u6c5f\u6e56\u4e0a\u4e5f\u786e\u5b9e\u592a\u5e73\u4e86\u51e0\u5e74\uff0c\u90a3 \u77e5\u9053\u73b0\u5728\u8fd9\u4e9b\u4e45\u5df1\u7edd\u8ff9\u6c5f\u6e56\uff0c\u751a\u81f3\u4e5f\u4f20\u4e91\u4e0d\u5728\u4eba\u4e16\u7684\u9b54\u5934\uff0c\u5c45\u7136\u4e00\u4e2a\u4e2a\u90fd\u5728\u6b66\u6c49\u73b0\u4e86\u8ff9 \u5f71\u3002\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ed6\u53cc\u7709\u7d27\u7d27\u76b1\u5728\u4e00\u8d77\uff0c\u53c8\u9053\uff1a\u201c\u5c0f\u5f1f\u552f\u4e00\u4e0d\u89e3\u7684\u662f\u8fd9\u9b54\u5934\u4e3a\u4f55\u770b\u6765\u7adf\u5bf9\u8f9b\u5144 \u751a\u4e3a\u9752\u7750\uff0c\u800c\u4e14\u8fd9\u9b54\u5934\u867d\u662f\u5947\u884c\u602a\u50fb\uff0c\u4e5f\u4ece\u672a\u542c\u8bf4\u8fc7\u4ee5\u4e5e\u4e10\u7684\u9762\u76ee\u51fa\u73b0\u7684\uff0c\u6211\u82e5\u4e0d\u662f\u770b \u5230\u4ed6\u7684\u4e00\u53ea\u624b\uff0c\u548c\u4ed6\u90a3\u5f02\u4e8e\u5e38\u4eba\u7684\u76ae\u80a4\uff0c\u4e5f\u4e07\u4e07\u4e0d\u4f1a\u60f3\u5230\u662f\u4ed6\u3002\u4eca\u665a\u8f9b\u5144\u82e5\u7136\u8981\u53bb\u8d74\u7ea6\uff0c \u5012\u8981\u4e09\u601d\u800c\u884c\u5462\uff1f\u201d\u8f9b\u6377\u6c89\u541f\u4e86\u534a\u664c\uff0c\u7a81\u7136\u95ee\u9053\uff1a\u201c\u90a3\u6bd2\u541b\u7684\u5973\u513f\u770b\u6765\u751a\u4e3a\u5e74\u8f7b\uff0c\u4e0d\u77e5 \u9053\u662f\u5426\u771f\u662f\u4ed6\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u4e00\u542c\u8f9b\u6377\u95ee\u53ca\u90a3\u5973\u5b50\uff0c\u6697\u9053\uff1a\u201c\u6b64\u4eba\u771f\u662f\u4e2a\u4e0d\u77e5\u5929\u591a\u9ad8\u5730\u591a\u539a\u7684\u7ea8\u88e4\u516c\u5b50\uff0c \u4e00\u9047\u5230\u8fd9\u79cd\u4e8b\uff0c\u8fd8\u5728\u6253\u4eba\u5bb6\u5973\u513f\u7684\u5ff5\u5934\u3002\u201d \u3000\u3000\u9042\u53c8\u8f6c\u5ff5\u5fd6\u9053\uff1a\u201c\u4f3c\u524d\u6211\u4e5f\u4ece\u672a\u542c\u8bf4\u8fc7\u9b54\u5934\u6709\u4e2a\u5973\u513f\uff0c\u5440\u2026\u2026\u54e6\uff0c\u60f3\u6765\u90a3\u65f6\u90a3\u5973\u513f \u5e74\u8f7b\u5c1a\u5e7c\uff0c\u6c5f\u6e56\u4e0a\u81ea\u7136\u4e0d\u4f1a\u6709\u4eba\u77e5\u9053\u4ed6\u6709\u4e2a\u5973\u513f\u4e86\u201d \u3000\u3000\u4ed6\u62ac\u5934\u671b\u89c1\u8f9b\u6377\u4ecd\u9759\u9759\u5730\u7b49\u7740\u4ed6\u7684\u7b54\u8986\uff0c\u9042\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u5f1f\u5012\u4e0d\u751a\u6e05\u695a\u3002\u4e0d\u8fc7\uff0c \u4f9d\u5c0f\u5f1f\u4e4b\u89c1\uff0c\u8f9b\u5144\u4eca\u665a\u8fd8\u662f\u4e0d\u8981\u8d74\u7ea6\u7684\u597d\u3002\u201d\u8303\u6cbb\u6210\u529d\u8bf4\u7740\u3002 \u3000\u3000\u8f9b\u6377\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6bd2\u541b\u65e2\u662f\u5982\u6b64\u4eba\u7269\uff0c\u6240\u4e58\u4e4b\u8239\uff0c\u5fc5\u5b9a\u6709\u4e9b\u7279\u6b8a\u6807\u8bb0\uff0c\u8303\u5144 \u53ef\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u8303\u6cbb\u6210\u5f53\u7136\u77e5\u9053\u4ed6\u8fd9\u4e00\u95ee\uff0c\u65e0\u5f02\u662f\u8bf4\u4e00\u5b9a\u8981\u53bb\u4e86\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u4e0e\u6b64\u4eba\u53cd\u8bc1\u65e0\u751a\u6df1\u4ea4\uff0c \u4ed6\u4e00\u5b9a\u8981\u53bb\u5bfb\u627e\u9ebb\u70e6\uff0c\u6211\u53c8\u4f55\u82e6\u4f5c\u6897\uff0c\u8fd9\u79cd\u516c\u5b50\u54e5\u513f\uff0c\u4e0d\u662f\u771f\u5403\u4e86\u82e6\u5934\uff0c\u4efb\u4f55\u4eba\u8bf4\u90fd\u662f \u65e0\u7528\u7684\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u9605\u5386\u867d\u4e30\uff0c\u53ef\u662f\u518d\u4e5f\u6ca1\u6709\u60f3\u5230\u8fd9\u4f4d\u5bb6\u8d44\u5de8\u4e07\u7684\u98ce\u6d41\u9614\u5c11\uff0c\u7adf\u662f\u8eab\u6000\u7edd\u6280\u7684\u76d6 \u4e16\u5947\u4eba\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u4e0d\u518d\u987e\u5fcc\u5730\u8bf4\u9053\uff1a\u201c\u4ed6\u8239\u4e0a\u6709\u4ec0\u4e48\u7279\u6b8a\u6807\u8bb0\u6211\u5012\u4e0d\u77e5\u9053\uff0c\u4e0d\u8fc7\u636e\u6c5f\u6e56\u4f20\u8a00\uff0c \u51e1\u662f\u6bd2\u541b\u6240\u5728\u4e4b\u5904\uff0c\u6240\u7529\u7269\u54c1\u5168\u662f\u7eff\u8272\u7684\uff0c\u60f3\u6765\u4ed6\u6240\u4e58\u4e4b\u8239\uff0c\u5fc5\u5b9a\u4e5f\u662f\u7eff\u8272\u7684\uff0c\u8f9b\u5144\u4e0d \u96be\u627e\u5230\u3002\u201d \u3000\u3000\u8f9b\u6377\u89c1\u81ea\u5df1\u6240\u95ee\u7684\u8bdd\uff0c\u90fd\u5f97\u5230\u4e86\u7b54\u6848\uff0c\u4fbf\u4e71\u4ee5\u4ed6\u8bed\uff0c\u4e0d\u518d\u63d0\u5230\u6709\u5173\u8fd9\u6bd2\u541b\u91d1\u4e00\u9e4f\u7684 \u8bdd\u3002\u4e24\u4eba\u5fc3\u4e2d\u5404\u6709\u5fc3\u4e8b\uff0c\u8bdd\u9042\u6e10\u4e0d\u6295\u673a\uff0c\u91d1\u5f13\u795e\u5f39\u5750\u4e86\u4e00\u56de\uff0c\u81ea\u89c9\u65e0\u8da3\uff0c\u4fbf\u8d77\u8eab\u544a\u8f9e\u8981 \u8d70\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u987e\u5fcc\u7740\u81ea\u5df1\u76ee\u524d\u7684\u5730\u4f4d\uff0c\u4e5f\u4e0d\u613f\u5f97\u7f6a\u4ed6\uff0c\u633d\u7559\u4e86\u4e24\u53e5\uff0c\u4eb2\u81ea\u9001\u5230\u95e8\u53e3\u3002 \u3000\u3000\u4ed6\u843d\u5bde\u5730\u671b\u7740\u8857\u4e0a\u7199\u6765\u6518\u5f80\u7684\u4eba\u4eec\uff0c\u5fc3\u60f3\u6b64\u65f6\u53c8\u6709\u51e0\u4eba\u4e0d\u4f1a\u4e3a\u540d\u5229\u5954\u6ce2\uff0c\u4e0d\u7981\u957f\u53f9 \u4e86\u4e00\u53e3\u6c14\uff0c\u8f6c\u8eab\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5750\u5728\u67dc\u53f0\u91cc\u7684\u5019\u4e8c\uff0c\u8fce\u4e86\u51fa\u6765\uff0c\u8eac\u8eab\u5411\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u5c11\u7237\uff0c\u6211\u6709\u51e0\u53e5\u8bdd\u8981\u8ddf\u5c11\u7237 \u8bf4\u3002\u201d \u3000\u3000\u8f9b\u6377\u56de\u987e\u90a3\u4e9b\u606d\u8c28\u5730\u4f8d\u7acb\u5728\u65c1\u7684\u5e97\u4f19\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u8bdd\uff0c\u8ddf\u6211\u8fdb\u53bb\u8bf4\u5427\uff01\u201d \u3000\u3000\u5019\u4e8c\u5fd9\u9053\uff1a\u201c\u662f\u3002\u201d\u8ddf\u7740\u8f9b\u6377\u8d70\u8fdb\u540e\u9662\u7684\u5c4b\u91cc\uff0c\u968f\u624b\u628a\u95e8\u5173\u4e0a\uff0c\u663e\u5f97\u6709\u4e9b\u614c\u5f20\u7684\u6837 \u5b50\u3002 \u3000\u3000\u8f9b\u6377\u77e5\u9053\u8fd9\u4f4d\u4faf\u4e8c\u53d4\u5fc5\u662f\u975e\u5e38\u4eba\uff0c\u9605\u5386\u4e4b\u4e30\u4e0e\u4e34\u4e8b\u7684\u9547\u9759\uff0c\u90fd\u4e0d\u662f\u81ea\u5df1\u53ef\u4ee5\u671b\u5176\u9879 \u80cc\u7684\uff0c\u6b64\u523b\u5982\u6b64\uff0c\u5fc5\u5b9a\u662f\u6709\u4e8b\u53d1\u751f\uff0c\u9042\u95ee\u9053\uff1a\u201c\u4faf\u4e8c\u53d4\u6562\u60c5\u662f\u6709\u4ec0\u4e48\u91cd\u8981\u7684\u4e8b\u8981\u8ddf\u5c0f\u4f84 \u8bf4\u5417\uff1f\u201d \u3000\u3000\u4faf\u4e8c\u53cc\u76ee\u4e00\u5f20\uff0c\u7d27\u7d27\u76ef\u5728\u8f9b\u6377\u8138\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u89c1\u5230\u91d1\u4e00\u9e4f\u4e86\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u70b9\u5934\uff0c\u4faf\u4e8c\u53c8\u95ee\u9053\uff1a\u201c\u90a3\u91d1\u4e00\u9e4f\u7684\u5973\u513f\u4f60\u53ef\u66fe\u89c1\u5230\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5927\u5947\uff0c\u600e\u5730\u8fd9\u201c\u4faf\u4e8c\u53d4\u201d\u8db3\u672b\u51fa\u6237\uff0c\u5374\u5bf9\u6b64\u4e8b\u6d1e\u82e5\u89c2\u706b\uff0c\u8fde\u7ec8\u65e5\u5728\u6c5f\u6e56\u4e2d\u6253\u6eda \u7684\u91d1\u5f13\u795e\u5f39\u90fd\u4e0d\u77e5\u9053\u91d1\u4e00\u9e4f\u6709\u4e2a\u5973\u513f\uff0c\u4ed6\u5374\u77e5\u9053\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u76ee\u5149\u4e00\u62ac\uff0c\u671b\u89c1\u4faf\u4e8c\u90a3\u4e00\u5411\u51b7\u51b0\u51b0\u7684\u9762\u5b54\uff0c\u6b64\u523b\u5374\u50cf\u56e0\u5fc3\u4e2d\u60c5\u611f\u7684\u6fc0\u52a8\uff0c\u800c\u663e \u5f97\u90a3\u4e48\u70ed\u70c8\u800c\u5947\u602a\uff0c\u5fc3\u4e2d\u4e0d\u7981\u66f4\u662f\u8be7\u5f02\uff0c\u4ed6\u81ea\u4e0e\u5019\u4e8c\u76f8\u5904\u4ee5\u6765\uff0c\u4ece\u672a\u89c1\u4ed6\u6709\u8fc7\u8fd9\u6837\u7684\u795e \u8272\u3002 \u3000\u3000\u4ed6\u5f00\u59cb\u89c9\u5f97\u8fd9\u4faf\u4e8c\u7684\u4e00\u5207\uff0c\u90fd\u6210\u4e86\u4e2a\u6781\u5927\u7684\u8c1c\uff0c\u4ed6\u672c\u5c31\u77e5\u9053\u5019\u4e8c\u5fc5\u5b9a\u5927\u6709\u6765\u5386\uff0c\u6b64 \u523b\u6df1\u6df1\u4e00\u63a8\u7a76\uff0c\u66f4\u786e\u5b9a\u4ed6\u5fc5\u6709\u6781\u5927\u7684\u9690\u60c5\uff0c\u53d7\u8fc7\u7edd\u6df1\u7684\u523a\u6fc0\uff0c\u4ee5\u81f3\u5982\u4eca\u53d8\u5f97\u8fd9\u6837\u5b50\uff0c\u8fde \u59d3\u540d\u90fd\u4e0d\u613f\u793a\u4eba\uff0c\u8fd9\u201c\u4faf\u4e8c\u201d\u4e24\u5b57\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e2a\u5047\u540d\u7f62\u4e86\uff0c\u4f46\u662f\u4ed6\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u800c\u4e14\u4ece \u4ed6\u6b64\u523b\u7684\u8868\u60c5\u770b\u6765\uff0c\u83ab\u975e\u4ed6\u4e0e\u6bd2\u541b\u91d1\u4e00\u9e4f\u4e4b\u95f4\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u8054\u5417\uff1f \u3000\u3000\u8fd9\u4e00\u5207\uff0c\u4f7f\u5f97\u8f9b\u6377\u8ff7\u60d1\u4e86\uff0c\u4ed6\u7adf\u6ca1\u6709\u56de\u7b54\u4faf\u4e8c\u7684\u95ee\u8bdd\u3002 \u3000\u3000\u4faf\u4e8c\u76ee\u5149\u4e00\u53d8\uff0c\u53c8\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u4f60\u53ef\u66fe\u89c1\u5230\u4ed6\u7684\u5973\u513f\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e00\u60ca\uff0c\u5fd9\u7b54\u9053\uff1a\u201c\u5c0f\u4f84\u89c1\u8fc7\u4e86\uff0c\u90a3\u5c11\u5973\u8fd8\u9080\u5c0f\u4f84\u4eca\u665a\u53bb\u5979\u821f\u4e0a\u4f1a\u6664\uff0c\u5c0f\u4f84\u60f3\u6765 \u60f3\u53bb\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u4f55\u7406\u3002\u201d \u3000\u3000\u4faf\u4e8c\u8138\u4e0a\u7684\u808c\u8089\uff0c\u987f\u65f6\u8d77\u4e00\u9635\u5947\u602a\u7684\u75c9\u631b\uff0c\u4e0d\u77e5\u662f\u9ad8\u5174\u8fd8\u662f\u6124\u6068\u3002 \u3000\u3000\u4ed6\u53cc\u62f3\u7d27\u63e1\uff0c\u4f3c\u7b11\u975e\u7b11\u5730\u8bf4\u9053\uff1a\u201c\u5929\u53ef\u601c\u6211\uff0c\u7ec8\u4e8e\u8ba9\u6211\u5728\u6b64\u5904\u5f97\u5230\u4e86\u4ed6\u4eec\u7684\u4e0b\u843d\u3002\u201d \u3000\u3000\u8f9b\u6377\u770b\u5230\u4ed6\u7684\u8868\u60c5\uff0c\u542c\u5230\u4ed6\u7684\u8bdd\uff0c\u5fc3\u4e2d\u66f4\u662f\u4e0d\u89e3\uff0c\u5fcd\u4e0d\u4f4f\u60f3\u95ee\uff1a\u201c\u4faf\u4e8c\u53d4\u2026\u201d \u3000\u3000\u54ea\u77e5\u5019\u4e8c\u957f\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u624b\u4e00\u6446\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u522b\u8bf4\uff0c\u5148\u5750\u4e0b\u6765\uff0c\u6211\u8bb2\u4e2a\u6545\u4e8b\u7ed9\u4f60 \u542c\u3002\u201d \u3000\u3000\u8f9b\u6377\u77e5\u9053\u8fd9\u6545\u4e8b\u5fc5\u5b9a\u5927\u6709\u6587\u7ae0\uff0c\u9042\u4e0d\u518d\u591a\u8bf4\uff0c\u5750\u5728\u9760\u5899\u7684\u6905\u4e0a\u3002 \u3000\u3000\u4faf\u4e8c\u76ee\u5149\u8fdc\u8fdc\u6295\u5411\u7a97\u5916\u7684\u767d\u4e91\u82cd\u7a79\uff0c\u60a0\u7136\u8bf4\u9053\uff1a\u201c\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u6cb3\u5317\u6709\u4e2a\u975e\u5e38\u5feb \u4e50\u7684\u4eba\uff0c\u4ed6\u51fa\u751f\u4e16\u5bb6\uff0c\u5bb6\u8d22\u5de8\u4e07\uff0c\u4ea4\u6e38\u904d\u5929\u4e0b\uff0c\u81ea\u5e7c\u7ec3\u5f97\u4e00\u8eab\u7edd\u4f73\u6b66\u529f\uff0c\u6c5f\u6e56\u4e0a\u65e0\u8bba\u9ed1 \u767d\u4e24\u9053\uff0c\u542c\u5f97\u4ed6\u7684\u540d\u5934\uff0c\u90fd\u4f1a\u4f38\u8d77\u5927\u59c6\u6307\u8bf4\u4e00\u58f0\u2018\u597d\u2019\uff0c\u800c\u4e14\u4ed6\u5bb6\u6709\u5a07\u59bb\uff0c\u5a07\u7f8e\u5982\u82b1\uff0c \u81ea\u5df1\u4eba\u53c8\u5e74\u8f7b\u3002\u201d \u3000\u3000\u4ed6\u6536\u56de\u76ee\u5149\uff0c\u671b\u7740\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u7684\u4eba\uff0c\u5c82\u975e\u662f\u6700\u5feb\u4e50\u7684\u4eba\u5417\uff1f\u201d \u3000\u3000\u201c\u540e\u6765\uff0c\u4ed6\u6709\u4e86\u4e00\u4e2a\u5c0f\u5973\u513f\uff0c\u4ed6\u4fbf\u89c9\u5f97\u4e07\u4e8b\u4ff1\u8db3\u3002\u53ea\u662f\u4ed6\u4e45\u5c45\u6cb3\u5317\uff0c\u4ece\u672a\u51fa\u53bb\u8fc7\uff0c \u60f3\u8d77\u53e4\u4eba\u2018\u884c\u4e07\u91cc\u8def\uff0c\u8bfb\u4e07\u5377\u4e66\u2019\u7684\u8bdd\uff0c\u542c\u5230\u522b\u4eba\u8bf4\u8d77\u6d77\u5185\u7684\u540d\u5c71\u5927\u5ddd\uff0c\u603b\u662f\u60a0\u7136\u795e \u5f80\u3002\u201d \u3000\u3000\u4ed6\u7f13\u6162\u800c\u6e05\u6670\u5730\u53d9\u8bf4\u7740\uff0c\u50cf\u662f\u8fd9\u4e9b\u4e8b\uff0c\u5728\u4ed6\u5fc3\u5934\u5df2\u4e0d\u77e5\u7ffb\u8f6c\u8fc7\u5343\u767e\u904d\u3002 \u3000\u3000\u201c\u7ec8\u4e8e\uff0c\u4ed6\u6452\u6321\u4e00\u5207\uff0c\u51fa\u6765\u6e38\u5386\uff0c\u4e00\u5e74\u591a\u4ee5\u6765\uff0c\u4ed6\u7684\u786e\u589e\u5e7f\u4e86\u4e0d\u5c11\u89c1\u8bc6\uff0c\u5f00\u4e86\u4e0d\u5c11 \u773c\u754c\uff0c\u4ed6\u6b63\u89c9\u6b64\u751f\u5df2\u4e0d\u590d\u6709\u61be\uff0c\u90a3\u77e5\u9053\uff0c\u4ed6\u56de\u5230\u5bb6\u4e2d\u65f6\uff0c\u5bb6\u4e2d\u5374\u5b8c\u5168\u6539\u53d8\u4e86\u5462\uff1f\u201d\u8bf4\u5230 \u8fd9\u91cc\uff0c\u4ed6\u76ee\u5149\u53c8\u662f\u4e00\u51db\uff0c\u90a3\u76ee\u4e2d\u8574\u7740\u7684\u6028\u6bd2\uff0c\u4f7f\u5f97\u8f9b\u6377\u4e0d\u7981\u6253\u4e86\u4e2a\u51b7\u6218\u3002 \u3000\u3000\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u770b\u5230\u5bb6\u91cc\u6240\u6709\u7684\u4e1c\u897f\uff0c\u90fd\u6362\u4e0a\u4e86\u7eff\u8272\uff0c\u5c31\u8fde\u4ed6\u7684\u59bb\u5b50\u548c\u4ed6\u7684\u624d\u4e00\u5c81\u591a \u7684\u5973\u513f\uff0c\u90fd\u7a7f\u7684\u662f\u7eff\u8272\u7684\u8863\u670d\uff0c\u4e0b\u4eba\u4eec\u4e5f\u90fd\u662f\u751f\u9762\u5b54\uff0c\u90fd\u4ee5\u4e00\u79cd\u5947\u5f02\u7684\u76ee\u5149\u671b\u7740\u4ed6\uff0c\u4ed6 \u5947\u602a\uff0c\u5c31\u53bb\u95ee\u4ed6\u7684\u59bb\u5b50\uff0c\u90a3\u77e5\u9053\u4ed6\u7684\u59bb\u5b50\u4e5f\u5bf9\u4ed6\u51b7\u6de1\u6de1\u7684\uff0c\u50cf\u662f\u5f88\u751f\u758f\u3002\u4ed6\u53c8\u60ca\u3001\u53c8\u5947\u3001 \u53c8\u6012\uff0c\u53ef\u662f\u4ed6\u5374\u4e0d\u77e5\u9053\u8fd9\u4e00\u5207\u5230\u5e95\u662f\u4ec0\u4e48\u539f\u6545\u3002\u201d\u4ed6\u7565\u4e00\u505c\u987f\uff0c\u773c\u4e2d\u7684\u6028\u6bd2\u4e4b\u8272\u66f4\u91cd\u4e86\u3002 \u3000\u3000\u7b49\u4ed6\u770b\u5230\u4e00\u4e2a\u7a7f\u7740\u706b\u4e00\u6837\u7ea2\u7684\u8863\u670d\u7684\u4eba\u4ece\u540e\u9762\u51fa\u6765\u65f6\uff0c\u4ed6\u624d\u77e5\u9053\u4ed6\u79bb\u5bb6\u4e00\u5e74\uff0c\u4ed6\u7684 \u5bb6\u548c\u4ed6\u7684\u59bb\u5b50\u5df2\u7ecf\u88ab\u522b\u4eba\u9738\u5360\u4e86\uff0c\u800c\u4e14\u9738\u5360\u7684\u4eba\uff0c\u7adf\u662f\u90a3\u65f6\u5019\u6c5f\u6e56\u4e0a\u6700\u5389\u5bb3\u7684\u4eba\u7269\u4e4b\u4e00 \u201c\u6bd2\u541b\u91d1\u4e00\u9e4f\u201d\u3002 \u3000\u3000\u8f9b\u6377\u5f00\u59cb\u611f\u89c9\u5230\uff0c\u8fd9\u6545\u4e8b\u4e2d\u7684\u4e3b\u4eba\uff0c\u5c31\u662f\u201c\u4faf\u4e8c\u201d\uff0c\u4e5f\u5f00\u59cb\u4e86\u89e3\uff0c\u5f53\u4ed6\u63d0\u5230\u201c\u6bd2\u541b \u91d1\u4e00\u9e4f\u201d\u65f6\uff0c\u4ed6\u773c\u7533\u7684\u6028\u6bd2\u4e4b\u8272\u7684\u7531\u6765\u3002 \u3000\u3000\u8f9b\u6377\u89c9\u5f97\u8fd9\u4e00\u5207\u7b80\u76f4\u662f\u4e0d\u53ef\u601d\u8bae\u7684\u6b79\u6bd2\uff0c\u4e0d\u7981\u540c\u60c5\u800c\u4e86\u89e3\u5730\u671b\u4e86\u201c\u4faf\u4e8c\u201d\u4e00\u773c\uff0c\u8bd5 \u60f3\u4e00\u4e2a\u79bb\u5bb6\u6e38\u5386\u7684\u4eba\uff0c\u56de\u5bb6\u65f6\u53d1\u73b0\u672c\u5c5e\u4e8e\u4ed6\u7684\u4e00\u5207\uff0c\u7a81\u7136\u90fd\u4e0d\u518d\u5c5e\u4e8e\u4ed6\uff0c\u4ed6\u8be5\u6709\u4ec0\u4e48\u611f \u89c9\u5462\uff1f \u3000\u3000\u4faf\u4e8c\u82e6\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u867d\u7136\u77e5\u9053\u90a3\u6bd2\u541b\u7684\u540d\u5934\uff0c\u53ef\u662f\u4ed6\u81ea\u5df1\u4e5f\u662f\u8eab\u6000\u7edd\u6280\uff0c\u6c14 \u6124\u4e4b\u4e0b\uff0c\u5c31\u8981\u53bb\u548c\u91d1\u4e00\u9e4f\u62fc\u547d\uff0c\u90a3\u77e5\u91d1\u4e00\u9e4f\u5374\u7b11\u563b\u563b\u5730\u51b2\u7740\u4ed6\u8bf4\uff1a\u2018\u4f60\u4e0d\u8981\u548c\u6211\u62fc\u547d\uff0c \u662f\u4f60\u7684\u8001\u5a46\u81ea\u5df1\u559c\u6b22\u6211\uff0c\u8981\u6211\u4f4f\u5728\u8fd9\u91cc\uff0c\u4f60\u81ea\u5df1\u7ba1\u4e0d\u4e86\u4f60\u7684\u8001\u5a46\uff0c\u6765\u627e\u6211\u62fc\u547d\u5e72\u4ec0\u4e48\uff1f\u2019 \u4ed6\u4e00\u542c\u8fd9\u8bdd\uff0c\u987f\u65f6\u89c9\u5f97\u597d\u8c61\u5728\u4e07\u4e08\u6c5f\u5fc3\u4e2d\u5931\u8db3\uff0c\u5fc3\u4e2d\u832b\u7136\u4e00\u7247\uff0c\u6d51\u8eab\u7684\u529b\u91cf\u90fd\u5931\u53bb\u4e86\uff0c \u4ed6\u518d\u4e5f\u60f3\u4e0d\u5230\u4ed6\u6240\u7231\u7684\u59bb\u5b50\uff0c\u7adf\u662f\u8fd9\u6837\u7684\u4e00\u4e2a\u4eba\u3002\u201d \u3000\u3000\u201c\u4ed6\u53bb\u770b\u4ed6\u7684\u59bb\u5b50\uff0c\u53ea\u89c1\u4ed6\u7684\u59bb\u5b50\u6b63\u51b2\u7740\u4ed6\u51b7\u7b11\uff0c\u4ed6\u672c\u662f\u9876\u5929\u7acb\u5730\u7684\u7537\u5b50\u6c49\uff0c\u7a81\u7136 \u906d\u5230\u8fd9\u79cd\u4e8b\uff0c\u53ea\u89c9\u5f80\u6614\u7684\u82f1\u96c4\u58ee\u5fd7\uff0c\u90fd\u5316\u505a\u98de\u7070\uff0c\u90a3\u91cc\u8fd8\u518d\u6709\u627e\u522b\u4eba\u62fc\u547d\u7684\u52c7\u6c14\u3002\u201d \u3000\u3000\u4faf\u4e8c\u8bf4\u5230\u8fd9\u91cc\u4e5f\u9893\u7136\u5012\u5728\u6905\u4e0a\uff0c\u8f9b\u6377\u4e00\u62cd\u684c\u5b50\uff0c\u5fc3\u4e2d\u4e5f\u5728\u6697\u9a82\u4ed6\u7684\u59bb\u5b50\u7684\u65e0\u803b\uff0c\u5df1 \u7ecf\u5230\u4e86\u6beb\u65e0\u4eba\u6027\u7684\u5730\u6b65\u4e86\u3002 \u3000\u3000\u5019\u4e8c\u53c8\u9053\uff1a\u201c\u8fd9\u65f6\u4ed6\u7a81\u7136\u770b\u5230\uff0c\u4ed6\u7684\u5c0f\u5973\u513f\u6b63\u51b2\u7740\u4ed6\u7b11\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u9178\uff0c\u5fcd\u4f4f\u6cea\uff0c\u4f38 \u624b\u62b1\u4ed6\u7684\u5c0f\u5973\u513f\uff0c\u90a3\u77e5\u4ed6\u624b\u4e00\u89e6\u7740\u4ed6\u5973\u513f\u7684\u8863\u670d\uff0c\u5168\u8eab\u597d\u50cf\u88ab\u7535\u6b9b\u4e00\u6837\uff0c\u53d8\u5f97\u865a\u8131\u7684\uff0c \u4e24\u6761\u624b\u81c2\u66f4\u597d\u50cf\u5728\u88ab\u5343\u4e07\u4e2a\u8682\u8681\u6240\u54ac\u7740\uff0c\u75db\u6781\u3001\u75d2\u6781\uff0c\u539f\u6765\u90a3\u2018\u6bd2\u541b\u2019\u4e4b\u6bd2\uff0c\u7684\u786e\u662f\u532a \u5937\u6240\u601d\uff0c\u7adf\u5728\u4ed6\u5973\u513f\u7684\u8863\u670d\u4e0a\uff0c\u65bd\u4e0a\u4e86\u7edd\u6bd2\u4e4b\u7269\uff0c\u53ea\u8981\u4ed6\u624b\u4e00\u89e6\u7740\uff0c\u4fbf\u662f\u65e0\u836f\u53ef\u6551\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u4e00\u80a1\u51b7\u6c14\uff0c\u81ea\u80cc\u810a\u900f\u8d77\uff0c\u8fd9\u79cd\u6bd2\u7269\uff0c\u7684\u786e\u662f\u4ee4\u4eba\u89c9\u5f97\u592a\u6050\u6016\u4e86\u3002 \u3000\u3000\u201c\u4ed6\u5f53\u65f6\u762b\u8f6f\u5728\u6905\u4e0a\uff0c\u90a3\u6bd2\u541b\u5374\u563b\u563b\u5730\u5728\u4ed6\u9762\u524d\u6402\u7740\u4ed6\u7684\u59bb\u5b50\u4eb2\u5634\uff0c\u53ea\u628a\u4ed6\u770b\u5f97\u773c \u91cc\u5192\u51fa\u706b\u6765\uff0c\u4f46\u56db\u80a2\u65e0\u529b\uff0c\u4e00\u70b9\u529e\u6cd5\u4e5f\u6ca1\u6709\u3002\u201d\u4faf\u4e8c\u5c06\u5634\u91cc\u7684\u7259\u54ac\u5f97\u5431\u5431\u4f5c\u54cd\uff0c\u50cf\u662f\u90a3 \u65f6\u7684\u60c5\u5f62\uff0c\u6b64\u523b\u4ecd\u4f7f\u4ed6\u65e0\u6bd4\u7684\u6124\u6012\u3002 \u3000\u3000\u8f9b\u6377\u60f3\u5230\u4ed6\u81ea\u5df1\u7684\u906d\u9047\uff0c\u5f53\u4ed6\u7684\u6bcd\u4eb2\u88ab\u201c\u5929\u6b8b\u5929\u5e9f\u201d\u4e24\u4e2a\u602a\uff0c\u7269\u8fb1\u5f04\u65f6\uff0c\u4ed6\u7684\u7236\u4eb2 \u4e0d\u662f\u4e5f\u5728\u65c1\u770b\u7740\u5417\uff1f\u4f46\u90a3\u65f6\u4ed6\u7236\u4eb2\u5e76\u975e\u56db\u80a2\u65e0\u529b\uff0c\u800c\u662f\u4e3a\u4e86\u4ed6\u624d\u5fcd\u7740\u8fd9\u4fae\u8fb1\uff0c\u8f9b\u6377\u7684\u773c \u6674\uff0c\u4e0d\u89c9\u4e5f\u6e7f\u4e86\u3002 \u3000\u3000\u4faf\u4e8c\u54ac\u7259\u53c8\u8bf4\u9053\uff1a\u201c\u4ed6\u6b63\u5728\u6068\u4e0d\u5f97\u7acb\u65f6\u6b7b\u53bb\u7684\u65f6\u5019\uff0c\u5c4b\u4e2d\u4e0d\u77e5\u600e\u7684\uff0c\u7a81\u7136\u591a\u4e86\u4e00\u4eba\uff0c \u7a7f\u7740\u6587\u58eb\u7684\u8863\u886b\uff0c \u3000\u3000\u6307\u7740\u91d1\u4e00\u9e4f\u7b11\u9a82\u9053\uff1a\u2018\u4f60\u8fd9\u4e2a\u6bd2\u7269\uff0c\u771f\u662f\u6bd2\u5f97\u53ef\u4ee5\uff0c\u5360\u4e86\u522b\u4eba\u7684\u8001\u5a46\uff0c\u8fd8\u8981\u5f04\u6b7b\u522b \u4eba\uff0c\u6211\u6885\u5c71\u6c11\u53ef\u6709\u70b9\u770b\u4e0d\u8fc7\u53bb\u4e86\u3002\u2019\u4ed6\u4e00\u542c\u6587\u58eb\u7adf\u662f\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\uff0c\u4e0d\u89c9\u7741\u5927\u4e86\u773c\u6674 \u53bb\u770b\u8fd9\u4e8b\u7684\u53d1\u5c55\u3002\u201d \u3000\u3000\u8f9b\u6377\u604d\u7136\u77e5\u9053\u4e86\u4e03\u5999\u795e\u541b\u9664\u53bb\u6bd2\u541b\u7684\u7f18\u56e0\uff0c\u4e0d\u7981\u5bf9\u201c\u6885\u53d4\u53d4\u201d\u66f4\u662f\u94a6\u4f69\u8d77\u6765\uff0c\u5bf9 \u201c\u6885\u53d4\u53d4\u201d\u8981\u4ed6\u53bb\u505a\u7684\u4e8b\uff0c\u4e5f\u66f4\u6709\u4e86\u4fe1\u5fc3\u3002 \u3000\u3000\u4faf\u4e8c\u53c8\u9053\uff1a\u201c\u679c\u7136\uff0c\u4e03\u5999\u795e\u541b\u548c\u90a3\u91d1\u4e00\u9e4f\u52a8\u8d77\u624b\u6765\uff0c\u4ed6\u4e00\u770b\u8fd9\u4e24\u4eba\u52a8\u624b\uff0c\u624d\u77e5\u9053\u81ea \u5df1\u7684\u6b66\u529f\u5dee\u5f97\u592a\u8fdc\uff0c\u90a3\u6bd2\u541b\u7684\u529f\u592b\u5df2\u662f\u4e0d\u53ef\u601d\u8bae\uff0c\u4f46\u4e03\u5999\u795e\u541b\u5374\u66f4\u5389\u5bb3\uff0c\u4ed6\u53ea\u89c9\u5f97\u6ee1\u5c4b \u90fd\u662f\u4ed6\u4e24\u4eba\u7684\u638c \u3000\u3000\u5f71\uff0c\u98ce\u58f0\u864e\u864e\uff0c\u5c06\u5c4b\u91cc\u7684\u684c\u6905\u3001\u6446\u8bbe\uff0c\u5168\u51fb\u5f97\u7247\u7247\u98de\u821e\uff0c\u4ed6\u90a3\u4e2a\u5c0f\u5973\u513f\uff0c\u66f4\u5413\u5f97\u653e \u58f0\u5927\u54ed\u8d77\u6765\uff0c\u8fde\u4ed6\u81ea\u5df1\uff0c\u90fd\u88ab\u638c\u98ce\u51fb\u5f97\u5012\u5728\u5730\u4e0a\uff0c\u4f46\u4ed6\u5374\u7741\u773c\u770b\u4ed6\u4eec\u4e24\u4eba\u6bd4\u6b66\u3002\u201d \u3000\u3000\u201c\u6253\u4e86\u4e00\u4f1a\uff0c\u4ed6\u770b\u5230\u91d1\u4e00\u9e4f\u638c\u5f0f\u4e00\u7f13\uff0c\u53f3\u80a9\u9732\u51fa\u4e00\u5757\u7a7a\u95e8\uff0c\u6885\u5c71\u6c11\u659c\u659c\u4e00\u638c\uff0c\u62cd\u4e86 \u4e0a\u53bb\uff0c\u4ed6\u7a81\u7136\u60f3\u8d77\u4ed6\u4e2d\u7684\u6bd2\uff0c\u90a3\u6bd2\u541b\u80fd\u5c06\u6bd2\u9644\u5728\u4ed6\u5973\u513f\u8eab\u4e0a\uff0c\u65e7\u662f\u4e5f\u80fd\u9644\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c \u6885\u5c71\u6c11\u638c\u51fa\u5982\u98ce\uff0c\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u95f4\uff0c\u4ed6\u5c3d\u529b\u5927\u543c\u9053\uff1a\u2018\u6709\u6bd2\uff01\u2019\u6885\u5c71\u6c11\u638c\u4e00\u7f13\uff0c\u7a81 \u5730\u5316\u638c\u4e3a\u6307\uff0c\u51cc\u7a7a\u4e00\u62db\uff0c\u70b9\u5728\u91d1\u4e00\u9e4f\u7684\u2018\u80a9\u8fdb\u2019\u7a74\u4e0a\uff0c\u539f\u6765\u6885\u5c71\u6c11\u7684\u5185\u529f\uff0c\u5df2\u5230\u4e86\u9694\u7a7a \u6253\u7a74\u7684\u5730\u6b65\u3002\u201d \u3000\u3000\u201c\u4ed6\u89c1\u91d1\u4e00\u9e4f\u88ab\u70b9\u4e2d\u7a74\u9053\uff0c\u4e5f\u5012\u5728\u5730\u4e0a\uff0c\u6885\u5c71\u6c11\u56de\u5934\u5411\u4ed6\u4e00\u7b11\uff0c\u611f\u6fc0\u5730\u70b9\u4e86\u70b9\u5934\uff0c \u8bf4\u9053\uff1a\u2018\u4f60\u4e0d\u8981\u52a8\uff0c\u6211\u53bb\u66ff\u4f60\u6211\u89e3\u836f\u3002\u2019\u8bf4\u7740\uff0c\u6885\u5c71\u6c11\u5c31\u8dd1\u5230\u540e\u9762\u53bb\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u5bbd\uff0c \u671b\u7740\u91d1\u4e00\u9e4f\uff0c\u5fd6\u9053\uff1a\u2018\u53ea\u8981\u6211\u89e3\u4e86\u6bd2\uff0c\u4e00\u5b9a\u8981\u4eb2\u624b\u6740\u6b7b\u4f60\u3002\u2019\u201d \u3000\u3000\u201c\u90a3\u77e5\u9053\u6bd2\u541b\u7684\u5185\u529f\u7edd\u4f73\uff0c\u867d\u7136\u88ab\u70b9\u7a74\u9053\uff0c\u4f46\u5374\u80fd\u81ea\u89e3\uff0c\u770b\u89c1\u6885\u5c71\u6c11\u4e00\u8dd1\u5230\u540e\u9762\u53bb\uff0c \u98de\u5feb\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u4e00\u624b\u62b1\u7740\u4ed6\u7684\u59bb\u5b50\uff0c\u4e00\u624b\u62b1\u7740\u4ed6\u7684\u5973\u513f\uff0c\u4ece\u7a97\u6237\u98de\u8eab\u800c\u51fa\uff0c\u4ed6\u773c\u7741\u7741 \u5730\u770b\u7740\uff0c\u4e5f\u65e0\u529e\u6cd5\u3002\u201d \u3000\u3000\u201c\u7b49\u5230\u6885\u5c71\u6c11\u627e\u7740\u89e3\u836f\u56de\u6765\uff0c\u91d1\u4e00\u9e4f\u5df2\u7ecf\u8d70\u4e86\uff0c\u6885\u5c71\u6c11\u66ff\u4ed6\u89e3\u4e86\u6bd2\uff0c\u4f46\u662f\u4ed6\u4e24\u81c2\u4e2d \u6bd2\u8fc7\u4e45\uff0c\u6885\u5c71\u6c11\u53c8\u4e0d\u77e5\u9053\u6bd2\u6027\uff0c\u867d\u7136\u4ed6\u751f\u547d\u5df2\u662f\u65e0\u788d\uff0c\u4f46\u662f\u4e24\u6761\u624b\u81c2\u5374\u4ece\u6b64\u4e0d\u80fd\u7528\u529b \u4e86\u3002\u201d \u3000\u3000\u4faf\u4e8c\u832b\u7136\u671b\u7740\u81ea\u5df1\u7684\u624b\u81c2\uff0c\u8f9b\u6377\u6b64\u65f6\u5df2\u7ecf\u5b8c\u5168\u4e86\u89e3\u4e86\u4e00\u5207\uff0c\u5bf9\u91d1\u4e00\u9e4f\u7684\u6bd2\uff0c\u548c\u90a3\u5987 \u4eba\u7684\u65e0\u803b\uff0c\u81ea\u4e5f\u662f\u503a\u6068\u4e0d\u5df2\uff0c\u540c\u65f6\uff0c\u4ed6\u4e86\u89e3\u4e86\u6240\u8c13\u91d1\u4e00\u9e4f\u7684\u5973\u513f\uff0c\u5b9e\u5728\u5374\u662f\u4faf\u4e8c\u751f\u7684\uff0c \u96be\u602a\u65b9\u624d\u4faf\u4e8c\u5230\u5979\u65f6\uff0c\u6709\u90a3\u4e48\u5947\u602a\u7684\u8868\u60c5\u4e86\u3002 \u3000\u3000\u4faf\u4e8c\u559f\u7136\u9053\uff1a\u201c\u4ece\u6b64\uff0c\u4ed6\u4e0d\u518d\u63d0\u8d77\u81ea\u5df1\u7684\u59d3\u540d\uff0c\u90a3\u6bd2\u541b\u91d1\u4e00"\u9e4f\uff0c\u4e5f\u5982\u77f3\u6c89\u5927\u6d77\uff0c \u5168\u7136\u6ca1\u6709\u4e00\u4e9b\u6d88\u606f\uff0c\u4e00\u6643\u5341\u4f59\u5e74\u5feb\u4e8c\u5341\u4e86\uff0c\u4ed6\u5374\u6c38\u8fdc\u65e0\u6cd5\u5fd8\u8bb0\u8fd9\u4ec7\u6068\u3002\u201d \u3000\u3000\u4faf\u4e8c\u4f38\u624b\u62ed\u53bb\u773c\u5e18\u4e0a\u7684\u6cea\u73e0\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u6545\u4e8b\u8bb2\u5b8c\u4e86\u3002\u201d \u3000\u3000\u5e55\u8272\u5df1\u964d\uff0c\u7a97\u5916\u7684\u5149\u7ebf\u4e5f\u6697\u6de1\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u671b\u7740\u4ed6\u9762\u4e0a\u6df1\u907d\u7684\u76b1\u7eb9\uff0c\u4e00\u79cd\u601c\u60af\u7684\u540c\u60c5\uff0c\u4f7f\u5f97\u8fd9\u4e24\u4e2a\u8eab\u6000\u7edd\u6280\u7684\u4fa0\u571f\uff0c\u505c\u7559 \u5728\u6c89\u9ed8\u91cc\u3002 \u3000\u3000\u591c\u5e55\u65e2\u5782\uff0c\u6c49\u53e3\u5e02\u8857\u4ecd\u50cf\u5f80\u5e38\u4e00\u6837\u7e41\u534e\u800c\u70ed\u95f9\uff0c\u5c71\u6885\u73e0\u5b9d\u53f7\u91cc\uff0c\u6b63\u6709\u51e0\u4e2a\u8863\u7740\u534e\u4e3d \u7684\u516c\u5b50\u8d35\u5987\uff0c\u5728\u9009\u8d2d\u6625\u73e0\u5b9d\u3002 \u3000\u3000\u4ece\u91cc\u9762\u5306\u5306\u8d70\u51fa\u7684\u8f9b\u6377\uff0c\u53cc\u7709\u7d27\u76b1\uff0c\u9762\u8272\u51dd\u91cd\uff0c\u671b\u90fd\u6ca1\u6709\u671d\u8fd9\u4e9b\u4eba\u671b\u4e0a\u4e00\u773c\u3002 \u3000\u3000\u9a6c\u97ad\u626c\u8d77\uff0c\u5237\u5730\u843d\u4e0b\uff0c\u9a6c\u8f66\u98de\u5feb\u7684\u5954\u5411\u6c5f\u8fb9\uff0c\u8d76\u8f66\u7684\u89c9\u5f97\u4eca\u65e5\u4e3b\u4eba\u6709\u4e9b\u5947\u602a\uff0c\u663e\u5f97 \u90a3\u4e48\u5fc3\u795e\u4e0d\u5b81\u7684\u6837\u5b50\uff0c\u4e0d\u4f3c\u5f80\u5e38\u7684\u5b89\u7965\u3002 \u3000\u3000\u5750\u5728\u8f66\u91cc\u7684\u8f9b\u6377\uff0c\u6b64\u523b\u6b63\u4ee5\u81ea\u5df1\u7684\u667a\u6167\uff0c\u8003\u8651\u7740\u4e00\u5207\u3002 \u3000\u3000\u4f7f\u5f97\u4ed6\u8ff7\u60d8\u7684\u4e8b\u5f88\u591a\uff0c\u5c24\u5176\u662f\u5728\u91d1\u5f13\u795e\u5f39\u548c\u4faf\u4e8c\u53d4\u5634\u91cc\u3001\u90a3\u6bd2\u541b\u91d1\u4e00\u9e4f\u672c\u8be5\u662f\u4e2a\u9634 \u6bd2\u7684\u4eba\u7269\uff0c\u4f46\u53c8\u4f55\u4ee5\u4f1a\u8dcc\u8db3\u72c2\u6b4c\u4e8e\u6df1\u591c\u7684\u9ec4\u9e64\u697c\u4e0b\uff0c\u770b\u8d77\u6765\u5374\u50cf\u662f\u4e2a\u6e38\u620f\u98ce\u5c18\u7684\u72c2\u58eb\u5462\u3002 \u3000\u3000\u201c\u4e5f\u8bb8\u90a3\u4eba\u4e0d\u662f\u91d1\u4e00\u9e4f\u5427\uff1f\u201d\u4ed6\u6697\u6697\u5fd6\u9053\uff1a\u201c\u4ed6\u770b\u8d77\u6765\u5e76\u4e0d\u50cf\u662f\u90a3\u4e48\u6bd2\u8fa3\u800c\u65e0\u4eba\u6027 \u7684\u4eba\u7269\u5440\uff01\u201d \u3000\u3000\u8f66\u5b50\u5230\u4e86\u6c5f\u8fb9\uff0c\u5206\u5429\u5490\u8d76\u8f66\u7684\u6cbf\u7740\u6c5f\u8fb9\u6e9c\u7740\uff0c\u4ece\u8f66\u7a97\u91cc\u671b\u51fa\u53bb\uff0c\u6c5f\u8fb9\u505c\u6cca\u7740\u7684\u8239\u53ea \u90a3\u4e48\u591a\uff0c\u4ed6\u53c8\u600e\u80fd\u5206\u8fa9\u5462\uff1f\u7eb5\u7136\u4ed6\u77e5\u9053\u91d1\u4e00\u9e4f\u7684\u8239\u5fc5\u5b9a\u662f\u7eff\u8272\u7684\u5427\uff01 \u3000\u3000\u201c\u7eff\u8272\u2026\u2026\u201d\u4ed6\u5583\u5583\u4f4e\u8bed\u7740\uff0c\u7a81\u7136\u60f3\u8d77\u90a3\u5c11\u5973\u7fe0\u7eff\u8272\u7684\u886b\u88d9\uff0c\u9042\u5373\u8bc1\u5b9e\u4e86\u81ea\u5df1\u7684\u7591 \u95ee\uff0c\u82e6\u7b11\u5fd6\u9053\uff1a\u201c\u73b0\u5728\u5979\u8863\u670d\u4e0a\u8fd8\u6709\u6ca1\u6709\u9644\u7740\u6bd2\u5462\uff1f\u201d \u3000\u3000\u8f66\u5b50\u6cbf\u7740\u6c5f\u8fb9\u6765\u56de\u8d70\u4e86\u4e24\u6b21\uff0c\u8f9b\u6377\u7a81\u7136\u770b\u5230\u6c5f\u5fc3\u7f13\u7f13\u9a76\u6765\u4e00\u8258\u5927\u8239\uff0c\u6cca\u5728\u5cb8\u8fb9\uff0c\u8239 \u4e0a\u642d\u8d77\u8df3\u677f\uff0c\u4e0d\u4e00\u4f1a\uff0c\u51fa\u6765\u56db\u4e2a\u6311\u7740\u7eff\u7eb1\u706f\u7b3c\u7684\u5c11\u5973\u3002 \u3000\u3000\u8f9b\u6377\u76ee\u529b\u672c\u5f02\u4e8e\u5e38\u4eba\uff0c\u6b64\u523b\u85c9\u7740\u4e9b\u8bb8\u5fae\u5149\uff0c\u66f4\u662f\u5c06\u90a3\u56db\u4e2a\u5c11\u5973\u770b\u5f97\u6e05\u6e05\u695a\u695a\u3002 \u3000\u3000\u4ed6\u89c1\u90a3\u56db\u4e2a\u5c11\u5973\u4ff1\u662f\u4e00\u8eab\u7eff\u8863\uff0c\u8885\u8885\u5a1c\u5a1c\u81ea\u8df3\u677f\u4e0a\u8d70\u4e0b\u6765\uff0c\u4e0d\u662f\u9ec4\u9e64\u697c\u4e0b\u62ac\u8d70\u91d1\u4e00 \u9e4f\u7684\u90a3\u56db\u4e2a\u4e2b\u73af\u662f\u8c01\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u8d76\u7d27\u559d\u4f4f\u4e86\u8f66\u5b50\uff0c\u7f13\u6b65\u8d70\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u5c11\u5973\u4e00\u770b\uff0c\u60f3\u4e5f\u662f\u8ba4\u5f97\u4ed6\uff0c\u7b11\u563b\u563b\u5730\u8fce\u4e86\u4e0a\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5bb6\u7684\u8001\u7237\u548c\u5c0f\u59d0\uff0c \u6b64\u523b\u6b63\u5728\u8239\u91cc\u606d\u5019\u516c\u5b50\u7684\u5927\u9a7e\uff0c\u8bf7\u516c\u5b50\u5feb\u4e9b\u4e0a\u8239\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u6b64\u6765\uff0c\u672c\u5c31\u662f\u62b1\u7740\u51b3\u5fc3\u4e00\u63a2\u7a76\u7adf\uff0c\u95fb\u8a00\u4fbf\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u8bf7\u59d1\u5a18\u4eec\u5e26\u8def\u5427\uff01\u201d\u90a3 \u4e9b\u5c11\u5973\u63a9\u53e3\u4fcf\u7b11\u7740\uff0c\u6253\u7740\u706f\u7b3c\uff0c\u5f15\u7740\u8f9b\u6377\u8d70\u5230\u8239\u524d\u3002 \u3000\u3000\u8f9b\u633a\u62ac\u5934\u4e00\u770b\uff0c\u90a3\u8239\u679c\u7136\u662f\u6f06\u6210\u7fe0\u7eff\u8272\uff0c\u91cc\u9762\u7684\u706f\u5149\u4e5f\u90fd\u662f\u7eff\u8272\u7684\uff0c\u5728\u8fd9\u6df1\u591c\u7684\u6c5f \u8fb9\uff0c\u770b\u4e0a\u53bb\u662f\u90a3\u4e48\u522b\u81f4\u800c\u4fcf\u4e3d\u3002 \u3000\u3000\u53ef\u662f\u53c8\u6709\u8c01\u77e5\u9053\uff0c\u5728\u8fd9\u522b\u81f4\u800c\u4fcf\u4e3d\u7684\u8239\u4e0a\uff0c\u7adf\u4f4f\u7740\u4e2a\u9707\u60ca\u6c5f\u6e56\u7684\u9b54\u5934\u5462\uff1f \u3000\u3000\u8f9b\u6377\u9644\u8d70\u4e0a\u8239\uff0c\u90a3\u4e91\u53d1\u7fe0\u670d\u7684\u5c11\u5973\u5df2\u8fce\u4e86\u51fa\u6765\uff0c\u5728\u8fd9\u7fe0\u7eff\u8272\u5982\u70df\u5982\u96fe\u7684\u706f\u5149\u91cc\uff0c\u66f4 \u663e\u5f97\u7f8e\u79c0\u7edd\u4f26\uff0c\u76f4\u5982\u5e7f\u5bd2\u4ed9\u5b50\u3002 \u3000\u3000\u90a3\u5c11\u5973\u8fce\u7740\u8f9b\u6377\u5b09\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8f9b\u76f8\u516c\u771f\u662f\u4fe1\u4eba\uff0c\u6211\u8fd8\u4ee5\u4e3a\u76f8\u516c\u4e0d\u6765\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e00\u60ca\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u5440\uff0c\u5979\u5c45\u7136\u5df2\u7ecf\u77e5\u9053\u4e86\u6211\u7684\u59d3\u540d\uff0c\u96be\u9053\u5979\u4e5f\u77e5\u9053\u4e86\u6211\u7684\u5e95\u7ec6\uff0c \u624d\u9080\u6211\u6765\u6b64\u5417\uff1f\u82e5\u662f\u5982\u6b64\uff0c\u90a3\u6211\u5012\u8981\u771f\u4e2a\u5c0f\u5fc3\u4e9b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u867d\u5728\u5982\u6b64\u5600\u5495\u7740\uff0c\u4f46\u795e\u8272\u4e0a\u5374\u4ecd\u6781\u4e3a\u6ee1\u6d12\u800c\u4ece\u5bb9\uff0c\u8fd9\u5c31\u662f\u4ed6\u5f02\u4e8e\u5e38\u4eba\u7684\u5730\u65b9\u3002 \u3000\u3000\u4ed6\u6717\u58f0\u7b11\u9053\uff1a\u201c\u65e2\u8499\u5ba0\u53ec\uff0c\u7109\u6709\u4e0d\u6765\u4e4b\u7406\uff0c\u53ea\u662f\u5374\u53e8\u6270\u4e86\u3002\u201d\u90a3\u5c11\u5973\u62bf\u5634\u4e00\u7b11\uff0c\u8f9b \u6377\u53ea\u89c9\u5f97\u5979\u7b11\u5f97\u542b\u610f\u751a\u6df1\uff0c\u5374\u53c8\u4e0d\u77e5\u5979\u7a76\u7adf\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u5fc3\u4e2d\u66f4\u662f\u7830\u7830\u6253\u9f13\u3002 \u3000\u3000\u987b\u77e5\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\u53ca\u201c\u4faf\u4e8c\u201d\u7684\u4e00\u756a\u8bdd\uff0c\u5df2\u5728\u8f9b\u6377\u5fc3\u4e2d\u7559\u4e0b\u4e86\u5148\u4eba\u4e4b\u89c1\uff0c\u4f7f\u5f97\u4ed6 \u5bf9\u8fd9\u201c\u6bd2\u541b\u201d\u7684\u201c\u6bd2\u201d\uff0c\u6709\u4e86\u4e9b\u8bb8\u6050\u4ff1\uff0c\u662f\u4ee5\u4ed6\u51e1\u4e8b\u90fd\u5411\u6700\u574f\u4e4b\u5904\u53bb\u60f3\uff0c\u6050\u6015\u201c\u6bd2\u541b\u201d \u5df2\u77e5\u4ed6\u7684\u5e95\u7ec6\u3002 \u3000\u3000\u5f53\u7136\uff0c\u4ed6\u8fd9\u5fc3\u4e2d\u7684\u4e0d\u5b81\uff0c\u4ea6\u975e\u4ff1\u6015\uff0c\u800c\u662f\u7565\u4e3a\u6709\u4e9b\u7d27\u5f20\u7f62\u4e86\uff0c\u8fd9\u662f\u4eba\u4eec\u5728\u9762\u5bf9\u7740 \u201c\u672a\u77e5\u201d\u65f6\uff0c\u6240\u5fc5\u6709\u7684\u73b0\u8c61\u3002 \u3000\u3000\u5ffd\u5730\u8239\u8eab\u540e\u8237\uff0c\u98d5\u5730\u98d8\u8d77\u4e00\u6761\u4eba\u5f71\uff0c\u8eab\u6cd5\u77eb\u82e5\u6e38\u9f99\uff0c\u8fc5\u6377\u5df2\u6781\uff0c\u6643\u773c\u4fbf\u9690\u4eba\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u53c8\u662f\u4e00\u60ca\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u4eba\u597d\u5feb\u7684\u8eab\u6cd5\uff0c\u6b64\u523b\u79bb\u8239\u800c\u53bb\uff0c\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u89c1\u8f9b\u6377\u4e45\u672a\u8bf4\u8bdd\uff0c\u53c8\u662f\u5fae\u6a84\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u76f8\u516c\u8fd8\u4e0d\u8bf7\u5230\u8231\u91cc\u53bb\u5750\uff0c\u5bb6\u7236\u8fd8 \u5728\u606d\u5019\u5927\u9a7e\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u8fd9\u5c11\u5973\u672a\u8bed\u5148\u7b11\uff0c\u7b11\u5f97\u5982\u767e\u5408\u521d\u653e\uff0c\u5728\u5979\u8138\u4e0a\u7efd\u5f00\u4e00\u6735\u6e05\u4e3d\u7684\u9c9c\u82b1\uff0c\u4ee4\u4eba \u89c1\u4e86\u5982\u6c90\u6625\u98ce\u4e4b\u4e2d\uff0c\u8bf4\u4e0d\u51fa\u7684\u4e00\u79cd\u6ecb\u5473\u3002 \u3000\u3000\u90a3\u5c11\u5973\u89c1\u8f9b\u6377\u75f4\u75f4\u5730\u671b\u7740\u81ea\u5df1\uff0c\u68a8\u6da1\u53c8\u73b0\uff0c\u8f6c\u8eab\u8d70\u4e86\u8fdb\u53bb\u3002\u8f9b\u6377\u8138\u4e00\u70ed\uff0c\u5fd9\u4e5f\u8ddf\u4e86 \u8fdb\u53bb\uff0c\u8fd9\u65f6\u7eb5\u7136\u524d\u9762\u662f\u5251\u6797\u5200\u5c71\uff0c\u4ed6\u4e5f\u5168\u4e0d\u987e\u5fcc\u4e86\u3002 \u3000\u3000\u91cc\u9762\u662f\u4e00\u5c42\u7fe0\u7eff\u8272\u7684\u539a\u7ed2\u95e8\u5e18\uff0c\u8f9b\u6377\u4e00\u6380\u5e18\u5b50\uff0c\u4f46\u89c9\u773c\u524d\u4e00\u51c9\uff0c\u5b9b\u5982\u8fdb\u4e86\u6842\u6bbf\u7684\u7fe1 \u7fe0\u5bab\u91cc\u3002 \u3000\u3000\u8231\u5185\u867d\u4e0d\u751a\u5927\uff0c\u4f46\u56db\u9762\u5d4c\u7740\u65e0\u6570\u7fe0\u7389\u77f3\u677f\uff0c\u6d6e\u5149\u63a0\u76ee\uff0c\u5c06\u8fd9\u5c0f\u5c0f\u4e00\u95f4\u8239\u8231\uff0c\u6620\u5f71\u5f97 \u5b9b\u5982\u5341\u767e\u95f4\u3002 \u3000\u3000\u8231\u5185\u65e0\u4eba\uff0c\u201c\u90a3\u5c11\u5973\u60f3\u662f\u53c8\u8f6c\u5927\u91cc\u9762\u53bb\u4e86\uff0c\u8f9b\u6377\u89c1\u8231\u5185\u5668\u76bf\uff0c\u90fd\u662f\u7fe0\u7389\u6240\u5236\uff0c\u4e00\u676f \u4e00\u74f6\uff0c\u5c11\u8bf4\u90fd\u662f\u4ef7\u503c\u5de8\u4e07\u7684\u73cd\u7269\uff0c\u6700\u602a\u7684\u662f\u5c31\u8fde\u684c\u3001\u51e0\u3001\u6905\u3001\u51f3\uff0c\u4e5f\u5168\u662f\u7fe0\u7389\u6240\u5236\uff0c\u8f9b \u6377\u89c9\u5f97\u4eff\u4f5b\u81ea\u5df1\u4e5f\u5168\u53d8\u6210\u7eff\u8272\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u968f\u610f\u5728\u4e00\u5f20\u6905\u4e0a\u5750\u4e0b\uff0c\u53ea\u89c9\u89e6\u80a1\u4e4b\u5904\uff0c\u5bd2\u6c14\u5165\u9aa8\uff0c\u7ade\u4f3c\u81ea\u5df1\u5341\u5e74\u6765\u6240\u5c45\u7684\u5730\u5e95\u77f3 \u5ba4\uff0c\u6697\u6697\u5fd6\u9053\uff1a\u201c\u770b\u6765\u8fd9\u91d1\u4e00\u9e4f\u7684\u786e\u662f\u9047\u5f02\u5e38\u4eba\uff0c\u5c31\u62ff\u8fd9\u95f4\u8239\u8231\u6765\u8bf4\uff0c\u5c31\u4e0d\u77e5\u4ed6\u600e\u4e48\u5efa \u9020\u7684\u3002\u201d \u3000\u3000\u5ffd\u5730\u91cc\u9762\u4f20\u6765\u7b11\u58f0\uff0c\u4f3c\u4e4e\u542c\u5f97\u90a3\u5c11\u5973\u5a07\u55d4\u9053\uff1a\u201c\u55ef\uff0c\u6211\u4e0d\u6765\u4e86\u3002\u201d\u63a5\u7740\u4e00\u9635\u5927\u7b11\u4e4b \u58f0\uff0c\u4e00\u4e2a\u5168\u8eab\u706b\u7ea2\u7684\u8001\u8005\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u5c31\u50cf\u5728\u9752\u8471\u6797\u6728\u4e4b\u4e2d\uff0c\u5377\u6765\u4e00\u56e2\u70c8\u7130\uff0c\u90a3\u8231\u91cc\u5d4c\u7740\u7684\u7fe0\u7389\u77f3\u677f\u4e0a\uff0c\u4e5f\u6597\u7136\u51fa\u73b0\u4e86 \u5341\u6570\u4e2a\u706b\u7ea2\u7684\u5f71\u5b50\uff0c\u8fd9\u666f\u8c61\u662f\u90a3\u4e48\u8be1\u5f02\uff0c\u6b64\u4e2d\u7684\u4eba\u7269\uff0c\u53c8\u662f\u90a3\u4e48\u7684\u6151\u4eba\u8033\u76ee\uff0c\u8f9b\u6377\u4e0d\u89c9 \u66f4\u63d0\u9ad8\u4e86\u8b66\u60d5\u3002 \u3000\u3000\u4ed6\u4e00\u773c\u671d\u90a3\u8001\u8005\u671b\u53bb\uff0c\u53ea\u89c1\u4ed6\u80a4\u5982\u9752\u7389\uff0c\u773c\u89d2\u4e0a\u5e26\u7740\u4e00\u4e1d\u5bd2\u610f\uff0c\u5634\u89d2\u4e0a\u5374\u53c8\u6302\u7740\u4e00 \u4e1d\u7b11\u610f\uff0c\u867d\u7136\u88c5\u675f\u4e0e\u6c14\u5ea6\u4e0d\u540c\u4e86\uff0c\u4f46\u4e0d\u662f\u9ec4\u9e64\u697c\u4e0b\uff0c\u8e0f\u96e8\u9ad8\u6b4c\u7684\u72c2\u4e10\u662f\u8c01\uff1f\u6b64\u60c5\u6b64\u666f\uff0c \u8fd9\u72c2\u4e10\u4e0d\u662f\u2018\u6bd2\u541b\u2019\u662f\u8c01\uff1f \u3000\u3000\u201c\u4f46\u662f\u8fd9\u91d1\u4e00\u9e4f\u7684\u6c14\u5ea6\u548c\u5f62\u6001\uff0c\u600e\u5730\u5728\u8fd9\u4e00\u65e5\u4e4b\u95f4\uff0c\u4f1a\u53d8\u5f97\u8fe5\u7136\u800c\u5f02\u5462\uff1f\u201d\u8fd9\u95ee\u9898 \u5728\u8f9b\u6377\u7684\u8111\u6d77\u4e2d\uff0c\u4e45\u4e45\u76d8\u636e\u7740\u3002 \u3000\u3000\u4ed6\u7ad9\u4e86\u8d77\u6765\uff0c\u671d\u91d1\u4e00\u9e4f\u6df1\u6df1\u4e00\u63d6\uff0c\u8bf4\u5230\uff1a\u201c\u627f\u8499\u8001\u4e08\u5ba0\u53ec\uff0c\u5c0f\u5b50\u5982\u4f55\u4e4b\u5e78\uff1f\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u76ee\u5149\u5982\u9e70\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5c06\u8f9b\u6377\u6253\u91cf\u4e86\u4e00\u904d\uff0c\u56de\u5934\u5411\u4fcf\u7acb\u5728\u95e8\u53e3\u7684\u7fe0\u886b\u5c11\u5973\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u7684\u773c\u5149\u5012\u771f\u5389\u5bb3\uff0c\u8fd9\u4f4d\u8f9b\u516c\u5b50\u4e0d\u4f46\u6ee1\u8179\u73e0\u7391\uff0c\u624d\u9ad8\u516b\u6597\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a \u5185\u5bb6\u7684\u7edd\u9876\u9ad8\u624b\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6377\u542c\u4e86\uff0c\u8fd9\u4e00\u60ca\u66f4\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4ed6\u6781\u529b\u88c5\u4f5c\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\u8fd9\u2018\u6bd2\u541b\u2019\u4e00\u773c\u5c31\u770b\u51fa \u81ea\u5df1\u7684\u884c\u85cf\uff0c\u4f46\u5947\u602a\u7684\u662f\u53c8\u4f3c\u7edd\u65e0\u6076\u610f\u3002 \u3000\u3000\u4ed6\u63e3\u6d4b\u4e0d\u8fc7\u8fd9\u4f4d\u4ee5\u2018\u6bd2\u2019\u9707\u60ca\u5929\u4e0b\u7684\u91d1\u4e00\u9e4f\uff0c\u5bf9\u81ea\u5df1\u7a76\u7adf\u662f\u4f55\u5fc3\u610f\uff0c\u66f4\u63e3\u6d4b\u4e0d\u51fa\u8fd9 \u4f4d\u6bd2\u541b\u4e00\u65e5\u6765\u8eab\u4efd\u548c\u6c14\u5ea6\u7684\u53d8\u5316\uff0c\u7a76\u7adf\u662f\u4f55\u7f18\u56e0\uff0c\u4f46\u662f\u4e0e\u751f\u4ff1\u6765\u7684\u4e00\u79cd\u8d85\u4e8e\u5e38\u4eba\u7684\u9547\u9759 \u6027\u683c\uff0c\u4f7f\u5f97\u4ed6\u9762\u4e0a\u4e1d\u6beb\u6ca1\u6709\u9732\u51fa\u7591\u60e7\u4e4b\u8272\u3002 \u3000\u3000\u4ed6\u8bc8\u88c5\u4e0d\u89e3\uff0c\u8be7\u5f02\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\u5eb8\u5eb8\u788c\u788c\uff0c\u8001\u4e08\u5982\u6b64\u8bf4\uff0c\u771f\u6559\u5c0f\u5b50\u6c57\u989c\u65e0\u5730\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u76ee\u5149\u4e00\u8f6c\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u53eb\u771f\u4eba\u4e0d\u9732\u50cf\uff0c\u9732\u50cf\u4e0d\u771f\u4eba\uff0c\u8f9b\u516c\u5b50\u865a\u6000\u82e5\u8c37\uff0c \u7684\u786e\u4e0d\u662f\u5e38\u4eba\u6240\u80fd\u770b\u7834\u7684\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u4e00\u505c\uff0c\u8138\u4e0a\u987f\u65f6\u53c8\u73b0\u51fa\u4e00\u79cd\u51b7\u51db\u4e4b\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u9601\u4e0b\u4e24\u773c\u795e\u5149\u5185\u8574\uff0c\u6c14 \u5b9a\u795e\u8db3\uff0c\u4e0d\u8bf4\u522b\u7684\uff0c\u5c31\u8bf4\u6211\u8fd9\u5bd2\u7389\u6905\u5427\uff0c\u53c8\u5c82\u662f\u5bfb\u5e38\u4eba\u80fd\u591f\u5750\u5f97\u7684\uff0c\u9601\u4e0b\u82e5\u975e\u5185\u529f\u6df1\u6e5b\uff0c \u6b64\u523b\u6015\u5df2\u65e9\u5c31\u51bb\u82e5\u5bd2\u8749\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u77e5\u9053\u5df2\u7792\u4e0d\u8fc7\u53bb\u4e86\uff0c\u53cd\u5766\u7136\u8bf4\u9053\uff1a\u201c\u8001\u4e08\u7684\u786e\u662f\u9ad8\u624b\uff0c\u5c0f\u5b50\u867d\u7136\u81ea\u5e7c\u7ec3\u5f97\u4e00\u4e9b \u529f\u592b\uff0c\u4f46\u82e5\u8bf4\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u90a3\u7684\u786e\u4e0d\u662f\u5c0f\u5b50\u68a6\u60f3\u5f97\u5230\u7684\u3002\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u8fd9\u624d\u9732\u51fa\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u5012\u4e0d\u662f\u6211\u76ee\u5149\u72ec\u5230\uff0c\u800c\u662f\u5c0f\u5973\u6885\u9f84\uff0c\u4e00\u773c\u4fbf\u770b\u51fa\u9601 \u4e0b\u5fc5\u975e\u5e38\u4eba\uff0c\u9601\u4e0b\u4e5f\u4e0d\u5fc5\u9690\u7792\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u62ac\u773c\uff0c\u89c1\u90a3\u7fe0\u886b\u5c11\u5973\u6b63\u671b\u7740\u81ea\u5df1\u62bf\u5634\u800c\u7b11\uff0c\u56db\u76ee\u76f8\u5bf9\uff0c\u8f9b\u6377\u6025\u5fd9\u5c06\u76ee\u5149\u8f6c\u5f00\uff0c \u5fd6\u9053\uff1a\u201c\u8fd9\u6bd2\u541b\u5bf9\u6211\u4f3c\u65e0\u6076\u610f\uff0c\u800c\u4e14\u751a\u6709\u597d\u611f\uff0c\u4f46\u662f\u4ed6\u5374\u60f3\u4e0d\u5230\uff0c\u6211\u5374\u8981\u53d6\u4ed6\u7684\u6027\u547d \u5462\u3002\u201d \u3000\u3000\u4ed6\u773c\u8272\u53c8\u98d8\u5411\u90a3\u5c11\u5973\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5973\u7684\u540d\u5b57\uff0c\u60f3\u6765\u5c31\u662f\u6885\u9f84\u4e86\uff0c\u53ea\u662f\u5979\u5374\u8be5\u53eb \u2018\u4faf\u6885\u9f84\u2019\u624d\u662f\uff0c\u7b49\u4e00\u4e0b\u6211\u66ff\u5979\u62a5\u4e86\u4ec7\uff0c\u518d\u544a\u8bc9\u5979\u4e8b\u60c5\u7684\u59cb\u672b\uff0c\u5979\u4e0d\u77e5\u8981\u600e\u6837\u611f\u8c22\u6211 \u5462\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u8f9b\u6377\u8138\u5e26\u5fae\u7b11\uff0c\u867d\u7136\u4ed6\u4e5f\u77e5\u9053\u8fd9\u201c\u6bd2\u541b\u201d\u91d1\u4e00\u9e4f\u5e76\u975e\u6613\u4e0e\u4e4b\u8f88\uff0c\u4f46\u662f\u4ed6 \u6210\u7af9\u5728\u80f8\uff0c\u5bf9\u4e00\u5207\u5c31\u6709\u4e86\u901a\u76d8\u7684\u6253\u7b97\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u667a\u7075\u654f\uff0c\u6b64\u523b\u5df1\u7ecf\u77e5\u9053\uff0c\u8fd9\u91d1\u4e00\u9e4f\u6240\u77e5\u9053\u7684\u4ec5\u662f\u81ea\u5df1\u53eb\u8f9b\u6377\uff0c\u662f\u4e2a\u5177\u6709\u5185\u529f \u7684\u5bcc\u5bb6\u516c\u5b50\u800c\u5df2\uff0c\u4ee5\u81ea\u5df1\u8fd9\u51e0\u65e5\u5728\u6b66\u6c49\u4e09\u9547\u7684\u58f0\u540d\uff0c\u91d1\u4e00\u9e4f\u81ea\u662f\u4e0d\u96be\u6253\u542c\u5f97\u5230\uff0c\u4ed6\u6697\u4e2d \u51b7\u7b11\u9053\uff1a\u201c\u53ef\u662f\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5c31\u662f\u4f60\u7684\u5927\u5bf9\u5934\u2018\u4e03\u5999\u795e\u541b\u2019\u5462\uff1f\u201d\u6b64\u523b\u4ed6\u5fc3\u5ff5\u4e4b\u95f4\uff0c\u81ea \u5df1\u4e0d\u4f46\u7ee7\u627f\u4e86\u201c\u4e03\u5999\u795e\u541b\u201d\u7684\u8863\u94b5\uff0c\u800c\u4e14\u5df1\u662f\u201c\u4e03\u5999\u795e\u541b\u201d\u7684\u5316\u8eab\u4e86\uff0c\u8fd9\u6b63\u662f\u6885\u5c71\u6c11\u6240 \u5e0c\u671b\uff0c\u4e5f\u662f\u6885\u5c71\u6c11\u6240\u9020\u5c31\u7684\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u4e4b\u5ff5\uff0c\u91d1\u4e00\u9e4f\u90a3\u4f1a\u77e5\u9053\uff0c\u6b64\u523b\u4ed6\u89c1\u8f9b\u6377\u5728\u8fd9\u56db\u5468\u7684\u7fe0\u7eff\u5149\u534e\u63a9\u6620\u4e2d\uff0c\u66f4\u663e\u5f97 \u4eba\u5982\u7389\u6811\uff0c\u5353\u79c0\u4e0d\u51e1\uff0c\u6697\u9053\uff1a\u201c\u6885\u513f\u7684\u773c\u5149\u679c\u7136\u4e0d\u9519\uff0c\u5979\u5e74\u8f7b\u8fd9\u4e48\u5927\u4e86\uff0c\u4e5f\u8be5\u6709\u4e2a\u5f52\u5bbf\uff0c \u8fd9\u59d3\u8f9b\u7684\u867d\u6709\u6b66\u529f\uff0c\u4f46\u5374\u53c8\u4e0d\u662f\u6b66\u6797\u4e2d\u4eba\uff0c\u6b63\u662f\u6700\u597d\u7684\u5bf9\u8c61\u3002\u201d \u3000\u3000\u4ed6\u56de\u5934\u4e00\u770b\u91d1\u6885\u9f84\uff0c\u89c1\u5979\u6b63\u542b\u7738\u51dd\u7747\u7740\u8f9b\u6377\uff0c\u9042\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u592b\u813e\u6c14\u867d\u602a\uff0c\u5374\u6700 \u559c\u6b22\u5e74\u9752\u6709\u529b\u7684\u540e\u751f\uff0c\u8f9b\u8001\u5f1f\uff0c\u4e0d\u662f\u8001\u592b\u6258\u5927\uff0c\u603b\u6bd4\u4f60\u75f4\u957f\u51e0\u5c81\uff0c\u4f60\u6211\u4e00\u89c1\u6295\u7f18\uff0c\u4ee5\u540e \u5b9a\u8981\u591a\u805a\u805a\u3002\u201d \u3000\u3000\u4ed6\u53c8\u5fae\u4e00\u62cd\u638c\uff0c\u8bf4\u9053\uff1a\u201c\u5feb\u9001\u4e9b\u9152\u83b1\u4e0a\u6765\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u66f4\u5947\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u91d1\u4e00\u9e2d\u5728\u6c5f\u6e56\u4e0a\u6709\u540d\u7684\u2018\u6bd2\u2019\uff0c\u4eca\u65e5\u4e00\u89c1\uff0c\u5374\u5bf9\u6211\u5982\u6b64\uff0c \u53c8\u662f\u4f55\u6545\u5462\uff1f\u201d \u3000\u3000\u4ed6\u82e5\u77e5\u9053\u6b64\u523b\u91d1\u4e00\u9e4f\u5df2\u5c06\u4ed6\u89c6\u5982\u4e1c\u5e8a\u5feb\u5a7f\uff0c\u5fc3\u4e2d\u53c8\u4e0d\u77e5\u8981\u600e\u751f\u60f3\u4e86\u3002\u8fd9\u8239\u8231\u7684\u4e09\u4e2a \u4eba\uff0c\u5404\u4eba\u90fd\u6709\u4e00\u756a\u5fc3\u610f\uff0c\u800c\u4e14\u4e09\u4eba\u76f8\u4e92\u4e4b\u95f4\uff0c\u6069\u6028\u76d8\u7ed3\uff0c\u9519\u7efc\u590d\u6742\uff0c\u7edd\u4e0d\u662f\u7247\u8a00\u6240\u80fd\u89e3 \u91ca\u5f97\u6e05\u7684\uff0c\u5c24\u5176\u662f\u8f9b\u6377\uff0c\u6b64\u523b\u7591\u5ff5\u767e\u751f\uff0c\u7eb5\u7136\u4ed6\u5fc3\u667a\u8d85\u4eba\uff0c\u4e5f\u65e0\u6cd5\u4e00\u4e00\u89e3\u91ca\u3002 \u3000\u3000\u9152\u83dc\u77ac\u5373\u9001\u6765\uff0c\u676f\u76d8\u4e5f\u4ff1\u662f\u7fe0\u7389\u6240\u5236\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u8bf7\u5ba2\u4eba\u5750\uff0c\u91d1\u6885\u9f84\u5c31\u5750\u5728\u4fa7\u9996\u76f8\u966a\uff0c\u91d1\u4e00\u9e4f\u4e3e\u676f\u7b11\u9053\uff1a\u201c\u529d\u541b\u540c\u996e\u4e00\u676f\u9152\uff0c \u4e0e\u541b\u540c\u6d88\u4e07\u53e4\u6101\uff0c\u6765\uff0c\u6765\uff0c\u6765\uff0c\u5e72\u4e00\u676f\u3002\u201d \u3000\u3000\u4ef0\u7740\u4e00\u996e\u800c\u5c3d\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u5f1f\uff0c\u4f60\u662f\u73e0\u5b9d\u4e16\u5bb6\uff0c\u770b\u770b\u6211\u8fd9\u5957\u676f\u76bf\uff0c\u8fd8\u80fd\u4eba\u5f97\u4e86 \u773c\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u6697\u7b11\uff0c\u8fd9\u91d1\u4e00\u9e4f\u679c\u771f\u5c06\u81ea\u5df1\u5f53\u505a\u73e0\u5b9d\u4e16\u5bb6\uff0c\u5176\u5b9e\u4ed6\u5bf9\u73e0\u5b9d\u5374\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c \u4f46\u4e0d\u5f97\u4e0d\u5047\u610f\u89c2\u6479\u4e86\u4e00\u4f1a\uff0c\u6781\u529b\u8d5e\u597d\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u53c8\u662f\u6597\u58f0\u5927\u7b11\uff0c\u5f97\u610f\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u8001\u592b\u5356\u72c2\uff0c\u5c31\u662f\u8fd9\u5957\u5668\u76bf\uff0c\u6050\u6015\u8fde\u7687\u5bab \u5927\u5185\u90fd\u6ca1\u6709\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6377\u968f\u53e3\u5e94\u4ed8\u7740\uff0c\u91d1\u4e00\u9e4f\u5374\u4f3c\u5174\u81f4\u9876\u597d\uff0c\u62c9\u7740\u4ed6\u8c08\u5929\u8bf4\u5730\uff0c\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u8f9b\u6377\u968f\u610f\u542c \u6765\uff0c\u89c9\u5f97\u8fd9\u2018\u6bd2\u541b\u2019\u80f8\u4e2d\u7684\u786e\u662f\u5305\u7f57\u751a\u591a\uff0c\u4e0d\u5728\u2018\u6885\u53d4\u53d4\u2019\u4e4b\u4e0b\u3002 \u3000\u3000\u90a3\u91d1\u6885\u9f84\u4ea6\u662f\u7b11\u8bed\u98ce\u751f\uff0c\u8f9b\u6377\u89c9\u5f97\u5979\u548c\u65b9\u5c11\u74a7\u7684\u5a07\u7f9e\u76f8\u6bd4\uff0c\u53e6\u6709\u4e00\u822c\u9189\u4eba\u4e4b\u5904\u3002 \u3000\u3000\u867d\u4ed6\u8868\u9762\u4e0a\u4ea6\u662f\u8a00\u7b11\u664f\u664f\uff0c\u4f46\u5fc3\u4e2d\u5374\u5728\u65f6\u65f6\u4f8d\u673a\u800c\u52a8\uff0c\u51c6\u5907\u4e00\u51fa\u624b\u4fbf\u5236\u4f4f\u91d1\u4e00\u9e4f\uff0c \u7136\u540e\u518d\u5f53\u7740\u91d1\u6885\u9f84\u4e4b\u9762\uff0c\u5c06\u5341\u6570\u5e74\u524d\u90a3\u4e00\u6bb5\u65e7\u4e8b\u63ed\u53d1\u51fa\u6765\u3002 \u3000\u3000\u4f46\u662f\u91d1\u4e00\u9e4f\u76ee\u5149\u70af\u7136\uff0c\u4ed6\u53c8\u4e0d\u6562\u968f\u4fbf\u51fa\u624b\uff0c\u987b\u77e5\u4ed6\u5e74\u8f7b\u867d\u8f7b\uff0c\u4f46\u505a\u4e8b\u5374\u6781\u8c28\u614e\uff0c\u6050 \u6015\u4e00\u51fb\u4e0d\u4e2d\uff0c\u81ea\u5df1\u4e07\u4e00\u4e0d\u662f\u540d\u626c\u6b66\u6797\u7684\u6bd2\u541b\u4e4b\u5bf9\u624b\uff0c\u53cd\u800c\u8bef\u4e86\u5927\u4e8b\uff0c\u662f\u4ee5\u4ed6\u8fdf\u8fdf\u8fd8\u672a\u52a8 \u624b\u3002 \u3000\u3000\u6b64\u523b\u90a3\u6bd2\u541b\u91d1\u4e00\u9e4f\uff0c\u5df2\u91ba\u7136\u6709\u4e86\u51e0\u5206\u9189\u610f\uff0c\u7a81\u5730\u4e00\u62cd\u684c\u5b50\uff0c\u53cc\u76ee\u7d27\u7d27\u6ce8\u89c6\u7740\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u60ca\uff0c\u91d1\u4e00\u9e4f\u7a81\u5730\u957f\u53f9\u4e00\u58f0\uff0c\u76ee\u5149\u5782\u843d\u5230\u684c\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u76f8\u8bc6\u904d\u5929\u4e0b\uff0c\u77e5\u5fc3\u5f97 \u51e0\u4eba\uff0c\u6211\u91d1\u4e00\u9e4f\u540d\u626c\u5929\u4e0b\uff0c\u53c8\u6709\u8c01\u77e5\u9053\u6211\u5fc3\u4e2d\u7684\u82e6\u95f7\uff1f\u201d\u8bf4\u7740\u4e3e\u8d77\u9152\u676f\uff0c\u4ef0\u7740\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u90a3\u91d1\u6885\u9f84\u5fd9\u53bb\u62ff\u8d77\u58f6\u6765\uff0c\u4e3a\u4ed6\u659f\u6ee1\u4e00\u676f\u4e0b\u76ee\u5149\u4e2d\u4f3c\u4e4e\u5bf9\u5979\u7684\u201c\u7239\u7239\u201d\u751a\u4e3a\u656c\u7231\u3002 \u3000\u3000\u8f9b\u6377\u6697\u6697\u5947\u602a\uff1a\u201c\u8fd9\u9b54\u5934\u5fc3\u4e2d\u53c8\u6709\u4ec0\u4e48\u82e6\u95f7\uff1f\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u53c8\u957f\u957f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u773c\u4e2d\u7adf\u4f3c\u610f\u5174\u8427\u7d22\uff0c\u629a\u6848\u9053\uff1a\u201c\u534e\u53d1\u5df2\u6591\uff0c\u4e00\u4e8b\u672c\u6210\uff0c \u53ea\u843d\u5f97\u4e2a\u5343\u79cb\u9a82\u540d\uff0c\u5509\uff0c\u8f9b\u8001\u5f1f\u201d \u3000\u3000\u7a81\u6c60\u8239\u8237\u4fa7\u5fae\u5fae\u5bf8\u54cd\uff0c\u867d\u7136\u90a3\u662f\u6781\u4e3a\u8f7b\u5fae\u7684\uff0c\u4f46\u8f9b\u6377\u5df2\u611f\u89c9\u5230\u90a3\u662f\u591c\u884c\u4eba\u7684\u8db3\u97f3\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u53cc\u7709\u4e00\u7acb\uff0c\u5389\u58f0\u559d\u9053\uff1a\u201c\u662f\u8c01\uff1f\u201d\u7a97\u5916\u7b54\u9053\uff1a\u201c\u5e08\u5085\uff0c\u662f\u6211\u3002\u201d \u3000\u3000\u968f\u7740\u95e8\u5e18\u4e00\u6380\uff0c\u8d70\u8fdb\u4e00\u4e2a\u9762\u8272\u715e\u767d\u7684\u5c11\u5e74\uff0c\u7a7f\u7740\u751a\u662f\u8003\u7a76\uff0c\u4e00\u8ff8\u95e8\u6765\uff0c\u76ee\u5149\u5982\u5200\uff0c \u5c31\u63a0\u5728\u8f9b\u6377\u8138\u4e0a\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u89c1\u4e86\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8138\u4e0a\u7adf\u663e\u51fa\u5341\u5206\u548c\u972d\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u56de\u6765\u4e86\uff0c\u4f60 \u8981\u627e\u7684\u4eba\u627e\u5230\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5927\u523a\u523a\u5730\uff0c\u4e5f\u671d\u6905\u4e0a\u5750\u4e0b\uff0c\u91d1\u6885\u9f84\u9012\u8fc7\u53bb\u4e00\u676f\u9152\u4ed6\u4ef0\u9996\u559d\u4e0b\uff0c\u8f9b\u6377\u89c1\u91d1\u6885\u9f84 \u4e0e\u8fd9\u5c11\u5e74\u4eff\u4f5b\u751a\u4e3a\u70ed\u7edc\uff0c\u5fc3\u4e2d\u7adf\u89c9\u5f97\u6ee1\u4e0d\u662f\u6ecb\u5473\uff0c\u8f9b\u6377\u89c1\u4ed6\u9762\u9614\u816e\u524a\uff0c\u6ee1\u8138\u4ff1\u662f\u51f6\u72e1\u4e4b \u8272\uff0c\u66f4\u5bf9\u6b64\u4eba\u8d77\u4e86\u6076\u611f\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u559d\u5b8c\u4e86\u9152\uff0c\u671d\u91d1\u4e00\u9e4f\u8bf4\u9053\uff1a\u201c\u672c\u6765\u6211\u4ee5\u4e3a\u4eba\u6d77\u832b\u832b\uff0c\u4f55\u5904\u627e\u5979\u53bb\uff0c\u90a3\u77e5\u9053\uff0c \u795e\u4f7f\u9b3c\u5dee\uff0c\u5979\u5c45\u7136\u5750\u5728\u4e00\u5bb6\u5e97\u94fa\u91cc\uff0c\u88ab\u6211\u78b0\u4e0a\u4e86\uff0c\u6211\u4e5f\u4e0d\u52a8\u58f0\u8272\uff0c\u7b49\u5230\u5929\u65b9\u4e24\u9f13\uff0c\u6211\u5c31 \u8fdb\u53bb\u628a\u5979\u8bf7\u51fa\u6765\u4e86\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u9762\u5e26\u5fae\u7b11\uff0c\u50cf\u662f\u5bf9\u8fd9\u5c11\u5e74\u751a\u662f\u75bc\u7231\uff0c\u95fb\u8a00\u8bf4\u9053\uff1a\u201c\u90a3\u597d\u6781\u4e86\uff0c\u5e26\u5979\u8fdb\u6765\u8ba9\u6211 \u770b\u770b\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4fa7\u76ee\u53c8\u76ef\u4e86\u8f9b\u6377\u4e00\u773c\uff0c\u91d1\u4e00\u9e4f\u7b11\u9053\uff1a\u201c\u54e6\uff0c\u4f60\u4eec\u8fd8\u4e0d\u76f8\u8bc6\uff0c\u8fd9\u4f4d\u5c31\u662f\u5c71\u6885\u73e0 \u5b9d\u53f7\u7684\u8f9b\u516c\u5b50\uff0c\u8fd9\u4e2a\u662f\u6211\u7684\u5927\u5f92\u5f1f\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u54e6\u4e86\u4e00\u58f0\uff0c\u8138\u4e0a\u6beb\u65e0\u8868\u60c5\uff0c\u4e0d\u77e5\u662f\u559c\u3001\u662f\u6012\uff0c\u8f9b\u6377\u9f3b\u5b54\u91cc\u6697\u54fc\u4e00\u58f0\uff0c\u53ea\u6de1\u6de1 \u5730\u5fae\u4e00\u62f1\u624b\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u8f6c\u8eab\u8d70\u51fa\u8231\u53bb\uff0c\u63a5\u7740\u8239\u8eab\u4e00\u8361\uff0c\u7adf\u4f3c\u7f13\u7f13\u8d70\u5f00\u4e86\u3002\u8f9b\u6377\u5fc3\u4e2d\u53c8\u662f\u4e00\u60ca\uff0c\u5fc3\u60f3 \u597d\u751f\u751f\u5730\u5c06\u8239\u5f00\u8d70\u4f5c\u751a\uff0c\u54ea\u77e5\u95e8\u5916\u7a81\u7136\u4e00\u58f0\u5a07\u557c\uff0c\u7830\u7136\u4e00\u58f0\uff0c\u63a5\u7740\u4e00\u4e2a\u5c11\u5973\u8dcc\u8dcc\u649e\u649e\u7684 \u8d70\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u770b\u8fd9\u5c11\u5973\uff0c\u9976\u4ed6\u518d\u662f\u9547\u9759\uff0c\u4e5f\u4e0d\u7531\u60ca\u5f97\u7ad9\u4e86\u8d77\u6765\u3002\u90a3\u5c11\u5973\u773c\u6ce2\u56db\u8f6c\uff0c\u4e00\u773c\u770b \u5230\u8f9b\u6377\uff0c\u4e5f\u662f\u4e00\u58f0\u60ca\u547c\uff0c\u8d70\u4e86\u4e24\u6b65\uff0c\u60f3\u8dd1\u5230\u8f9b\u6377\u9762\u524d\uff0c\u7a81\u53c8\u7ad9\u4f4f\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5df2\u51b7\u51b7\u8ddf\u4e86\u8fdb\u6765\uff0c\u9634\u607b\u607b\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8ba4\u8bc6\u5427\uff1f\u201d \u3000\u3000\u8fd9\u7a81\u751f\u4e4b\u53d8\uff0c\u975e\u4f46\u4f7f\u5f97\u8f9b\u6377\u624b\u8db3\u5931\u63aa\uff0c\u91d1\u4e00\u9e4f\u4e0e\u91d1\u6885\u9f84\u4e5f\u5927\u4e3a\u60ca\u5947\u3002 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c \u4e09 \u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c \u4e09 \u7ae0 \u3000\u3000\u91d1\u4e00\u9e4f\u5389\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u9634\u607b\u607b\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u5c31\u662f\u6211\u8ddf\u5e08\u5085\u8bf4\u8d77\u7684\u65b9\u5c11\u74a7\uff0c\u6211\u56e0\u542c\u5e08\u5085\u7a81\u7136 \u5357\u6765\uff0c\u6240\u4ee5\u5c31\u4ea4\u5979\u5bc4\u653e\u5728\u957f\u6c5f\u6c34\u5be8\u91cc\uff0c\u90a3\u5929\u6211\u89c1\u4e86\u5e08\u5085\uff0c\u7980\u660e\u6b64\u4e8b\uff0c\u518d\u95ee\u957f\u6c5f\u6c34\u5be8\u7684\u6c5f \u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\u8981\u4eba\u65f6\uff0c\u4ed6\u5374\u8bf4\u4eba\u5df2\u88ab\u2018\u4e03\u5999\u795e\u541b\u2019\u52ab\u8d70\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u54fc\u4e86\u4e00\u58f0\uff0c\u9762\u5982\u51dd\u971c\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u201d\u90a3\u5c11\u5e74\u671d\u8f9b\u6377\u51db\u7136\u4e00 \u89c6\u3002\u8f9b\u6377\u672b\u52a8\u58f0\u8272\uff0c\u4f46\u5df1\u6697\u6697\u8c03\u8fd0\u771f\u6c14\uff0c\u4ed6\u5fd6\u9053\uff1a\u201c\u60f3\u8fd9\u4e2a\u5c11\u5e74\u5c31\u662f\u4ed6\u4eec\u53e3\u4e2d\u7684\u5929\u9b54\u91d1 \u6b39\u4e86\uff0c\u5374\u60f3\u4e0d\u5230\u4ed6\u7adf\u662f\u6bd2\u541b\u91d1\u4e00\u9e4f\u7684\u5f1f\u5b50\uff0c\u770b\u6765\u4eca\u65e5\u8bf4\u4e0d\u5f97\u8981\u6709\u4e00\u756a\u6076\u6597\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u679c\u7136\u5c31\u662f\u8fd1\u65e5\u6c5f\u6e56\u4e2d\u95fb\u800c\u8272\u53d8\u7684\u5929\u9b54\u91d1\u6b39\uff0c\u4ed6\u51b7\u51b7\u53c8\u9053\uff1a\u201c\u6211\u4e00\u542c\u662f\u4e03\u5999\u795e \u541b\u52a8\u7684\u624b\uff0c\u5c31\u8d76\u7d27\u56de\u6765\u7980\u660e\u5e08\u5085\uff0c\u518d\u53c8\u51fa\u53bb\u627e\u4eba\uff0c\u90a3\u77e5\u6211\u8d70\u5230\u8857\u4e0a\uff0c\u5374\u770b\u5230\u8fd9\u8d31\u4eba\u5750\u5728 \u5c71\u6885\u73e0\u5b9d\u53f7\u91cc\u3002\u201d \u3000\u3000\u8f9b\u6377\u6697\u6697\u53eb\u82e6\uff0c\u671b\u4e86\u65b9\u5c11\u74a7\u4e00\u773c\uff0c\u89c1\u5979\u6b63\u5782\u7740\u5934\uff0c\u6ee1\u8138\u4ff1\u662f\u60ca\u6115\u4e4b\u8272\uff0c\u6697\u9053\uff1a\u201c\u6211 \u53eb\u4f60\u5b88\u5728\u623f\u91cc\u4e0d\u8981\u51fa\u6765\uff0c\u4f60\u53c8\u8dd1\u51fa\u6765\u505a\u4ec0\u4e48\u3002\u201d \u3000\u3000\u6bd2\u541b\u91d1\u4e00\u9e4f\u76ee\u5149\u4e00\u51db\uff0c\u671b\u7740\u8f9b\u6377\u9053\uff1a\u201c\u6885\u5c71\u6c11\u662f\u4f60\u7684\u4ec0\u4e48\u4eba\uff1f\u4ed6\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u201d \u3000\u3000\u8f9b\u6377\u672a\u7b54\u8bdd\uff0c\u5728\u8003\u8651\u7740\u8be5\u600e\u6837\u5e94\u4ed8\u8fd9\u5f53\u524d\u7684\u5c40\u9762\uff0c\u4ed6\u77e5\u9053\u6b64\u523b\u9762\u5bf9\u7740\u7684\u90fd\u662f\u6b66\u6797\u4e2d \u7684\u7edd\u9876\u9ad8\u624b\uff0c\u800c\u4e14\u91d1\u4e00\u9e4f\u4ee5\u6bd2\u95fb\u540d\uff0c\u53ea\u8981\u7a0d\u4e00\u4e0d\u614e\uff0c\u4fbf\u662f\u8eab\u4e2d\u5de8\u6bd2\uff0c\u8fde\u6551\u90fd\u4e0d\u4f1a\u6709\u4eba\u6765 \u6551\u3002 \u3000\u3000\u91d1\u6885\u9f84\u773c\u6ce2\u4e00\u8f6c\uff0c\u8f7b\u8f7b\u8e22\u8f9b\u6377\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5012\u662f\u5feb\u8bf4\u5440\uff01\u201d \u3000\u3000\u6b64\u523b\u8239\u8eab\u6ce2\u52a8\u5f88\u5927\uff0c\u50cf\u662f\u8239\u5df2\u9a76\u5230\u6c5f\u5fc3\uff0c\u8f9b\u6377\u6697\u7b97\uff1a\u201c\u8fd9\u5929\u9b54\u91d1\u6b39\u6bd4\u4ed6\u5e08\u5085\u8fd8\u6bd2\uff0c \u751f\u6015\u6211\u9003\u8d70\uff0c\u7adf\u5c06\u8239\u9a76\u5230\u6c5f\u5fc3\u6765\u4e86\u3002\u201d \u3000\u3000\u987b\u77e5\u5373\u4fbf\u6b66\u529f\u518d\u9ad8\uff0c\u5728\u4e00\u65e0\u51ed\u85c9\u4e4b\u4e0b\uff0c\u4e5f\u7edd\u96be\u98de\u5ea6\u8fd9\u6570\u5341\u4e08\u6c5f\u9762\u3002 \u3000\u3000\u8fd9\u4e0e\u4ed6\u81ea\u6c5f\u91cc\u767d\u9f99\u795e\u8239\u4e2d\u6551\u8d70\u65b9\u5c11\u74a7\uff0c\u60c5\u51b5\u5927\u662f\u4e0d\u540c\uff0c\u4e00\u6765\u90a3\u65f6\u8239\u8ddd\u6c5f\u5cb8\u6ca1\u6709\u5982\u6b64 \u8fdc\uff0c\u4e8c\u6765\u90a3\u65f6\u8eab\u4fa7\u6ca1\u6709\u9ad8\u624b\u73af\u4f3a\uff0c\u4ed6\u53ef\u4ece\u4ece\u5bb9\u5bb9\u5730\u98de\u8eab\u800c\u6e21\u3002 \u3000\u3000\u4f46\u662f\u8f9b\u6377\u751f\u6027\u72ec\u7279\uff0c\u867d\u7136\u4e8b\u5df2\u81f3\u6b64\uff0c\u4f46\u5374\u4e1d\u6beb\u4e5f\u4e0d\u614c\u4e71\uff0c\u4ed6\u5e74\u8f7b\u90a3\u4e48\u5c0f\u7684\u65f6\u5019\uff0c\u9762 \u5bf9\u7740\u201c\u5929\u5e9f\u201d\u3001\u201c\u5929\u6b8b\u201d\u4e24\u4e2a\u9b54\u5934\uff0c\u5c1a\u4e14\u4e0d\u60e7\uff0c\u4f55\u51b5\u8fd9\u5341\u5e74\u6765\uff0c\u4ed6\u66f4\u5b66\u5f97\u4e00\u8eab\u60ca\u4eba\u7684\u827a \u4e1a\u5462\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u91cc\u4e5f\u6709\u4e86\u6253\u7b97\uff0c\u5fc3\u60f3\uff1a\u201c\u65e0\u8bba\u7ed3\u679c\u5982\u4f55\uff0c\u597d\u6b79\u6211\u4e5f\u8981\u5148\u5c06\u91d1\u6885\u9f84\u7684 \u6765\u5386\uff0c\u6296\u9732\u51fa\u6765\uff0c\u8ba9\u4f60\u4eec\u4e5f\u4e0d\u5f97\u5b89\u7a33\u3002\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u89c1\u4ed6\u6b64\u523b\u4ecd\u5728\u5fae\u7b11\uff0c\u800c\u4e14\u4f9d\u65e7\u6ee1\u6ee1\u6d12\u6d12\uff0c\u4e00\u70b9\u513f\u4e5f\u4e0d\u9732\u614c\u5f20\u4e4b\u8272\uff0c\u5fc3\u4e2d\u4e0d\u7981 \u4e5f\u6697\u8d5e\u4ed6\u7684\u52c7\u6c14\u3002 \u3000\u3000\u8f9b\u6377\u73af\u76ee\u56db\u987e\uff0c\u6717\u58f0\u8bf4\u9053\uff1a\u201c\u8001\u4e08\u95ee\u8d77\u6885\u5c71\u6c11\uff0c\u96be\u9053\u8001\u4e08\u4e0e\u90a3\u6885\u5c71\u6c11\u6709\u4ec0\u4e48\u8fc7\u8282\u4e0d \u6210\u3002\u201d \u3000\u3000\u90a3\u5929\u9b54\u91d1\u6b39\u5374\u6012\u559d\u9053\uff1a\u201c\u4f60\u7ba1\u5f97\u7740\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4ef0\u5929\u6253\u4e86\u4e2a\u54c8\u54c8\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u8001\u4e08\u4e0d\u8bf4\uff0c\u5728\u4e0b\u4e5f\u7565\u77e5\u4e00\u4e8c\u3002\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u9762\u8272\u4e00\u53d8\uff0c\u671b\u4e86\u4fa7\u7acb\u5728\u65c1\u7684\u91d1\u6885\u9f84\u4e00\u773c\uff0c\u8f9b\u6377\u66f4\u662f\u5f97\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u8bf8\u4f4d\u5148\u83ab \u52a8\u624b\uff0c\u5f85\u5c0f\u751f\u8bf4\u4e2a\u6545\u4e8b\u4e0e\u8bf8\u4f4d\u542c\u542c\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u6307\u624b\u5212\u811a\uff0c\u5c06\u201c\u5019\u4e8c\u201d\u8bf4\u7ed9\u4ed6\u542c\u7684\u6545\u4e8b\uff0c\u53c8\u8bf4\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8bf4\u4e86\u4e00\u534a\uff0c\u90a3\u5929\u9b54\u91d1\u6b39\u4e00\u58f0\u6012\u559d\uff0c\u98de\u63a0\u8fc7\u6765\uff0c\u9a88\u6307\u5982\u5251\uff0c\u53f3\u624b\u75be\u70b9\u4ed6\u5589\u4e0b\u201c\u9501\u5589 \u7a74\u201d\uff0c\u5de6\u638c\u6a2a\u5207\uff0c\u5e26\u8d77\u4e00\u9635\u52b2\u98ce\uff0c\u76f4\u53d6\u5c0f\u8179\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e24\u5f0f\uff0c\u51fa\u624b\u5982\u7535\uff0c\u52b2\u529b\u5185\u8574\uff0c\u65e0\u4e00\u4e0d\u662f\u715e\u624b\uff0c\u679c\u771f\u4e0d\u540c\u51e1\u54cd\u3002 \u3000\u3000\u8f9b\u6377\u54c8\u54c8\u4e00\u7b11\uff0c\u8eab\u5f62\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u582a\u582a\u907f\u5f00\uff0c\u5374\u5e76\u4e0d\u8fd8\u624b\uff0c\u4ecd\u7136\u6ed4\u6ed4\u5730\u8bf4\u7740\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u53c8\u662f\u4e00\u58f0\u6012\u559d\uff0c\u626c\u638c\u4e09\u5f0f\uff0c\u201c\u52fe\u9b42\u7d22\u547d\u201d\uff0c\u201c\u9b3c\u7b14\u70b9\u775b\u201d\uff0c\u201c\u6e38\u9b42\u56db\u98d8\u201d\uff0c \u6f2b\u5929\u638c\u5f71\uff0c\u7b3c\u7f69\u5728\u8f9b\u6377\u56db\u4fa7\u3002 \u3000\u3000\u8f9b\u6377\u811a\u8e29\u8ff7\u8e2a\uff0c\u8eab\u5f62\u4e71\u8f6c\uff0c\u4e00\u9762\u8eb2\uff0c\u5634\u91cc\u4ecd\u4e0d\u95f2\u7740\uff0c\u8fd8\u662f\u5728\u8bb2\u3002 \u3000\u3000\u91d1\u6885\u9f84\u773c\u542b\u75db\u6cea\uff0c\u51dd\u795e\u5728\u542c\uff0c\u90a3\u65b9\u5c11\u74a7\u9aa4\u89c1\u8f9b\u6377\u5982\u6b64\u8eab\u624b\uff0c\u4e0d\u77e5\u662f\u60ca\u662f\u559c\uff0c\u773c\u775b\u77ac \u4e5f\u4e0d\u77ac\u5730\u968f\u7740\u4ed6\u7684\u8eab\u5f62\u6253\u8f6c\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u7684\u795e\u8272\u66f4\u662f\u96be\u770b\u5df2\u6781\uff0c\u5374\u4ecd\u7aef\u5750\u5e76\u672a\u51fa\u624b\uff0c\u7a81\u5730\u559d\u9053\uff1a\u201c\u6b39\u513f\u4f4f\u624b\uff0c\u8ba9\u4ed6\u8bf4 \u4e0b\u53bb\u3002\u201d \u3000\u3000\u8f9b\u6377\u6697\u6697\u79f0\u5947\uff1a\u201c\u600e\u5730\u8fd9\u91d1\u4e00\u9e4f\u5374\u8ba9\u81ea\u5df1\u8bf4\u4e0b\u53bb\uff1f\u201d \u3000\u3000\u90a3\u5929\u9b54\u91d1\u6b39\u95fb\u58f0\u800c\u6b62\uff0c\u6c14\u6124\u5730\u7ad9\u5230\u65c1\u8fb9\uff0c\u8f9b\u6377\u66f4\u662f\u8001\u5b9e\u4e0d\u5ba2\u6c14\uff0c\u5750\u5230\u6905\u4e0a\u5c06\u8fd9\u6545\u4e8b \u6e90\u6e90\u672c\u672c\u5730\u8bb2\u5b8c\uff0c\u671b\u7740\u91d1\u6885\u9f84\uff1a\u201c\u4f60\u8bf4\u8fd9\u6545\u4e8b\u597d\u542c\u4e0d\u597d\u542c\uff1f\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u9762\u4e0a\u5ffd\u9634\u5ffd\u6674\uff0c\u7a81\u5730\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u8bb2\u4e2a\u6545\u4e8b\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u8f9b\u6377\u66f4\u662f\u5947\u602a\uff1a\u201c\u8fd9\u6bd2\u541b\u4e0d\u4f46\u6bd2\uff0c\u800c\u4e14\u2018\u602a\u2019\u5f97\u53ef\u4ee5\uff0c\u600e\u5730\u5374\u8981\u8bb2\u8d77\u6545\u4e8b\u6765\uff0c\u83ab\u975e \u4ed6\u8fd9\u6545\u4e8b\u91cc\uff0c\u53c8\u6709\u4ec0\u4e48\u6587\u7ae0\u5417\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u601d\u7d22\uff0c\u5634\u4e2d\u5374\u9053\uff1a\u201c\u5c0f\u751f\u6d17\u8033\u606d\u542c\uff0c\u8001\u4e08\u8bf7\u8bf4\u5427\uff01\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u795e\u8272\u751a\u5f02\uff0c\u8bf4\u9053\uff1a\u201c\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u6cb3\u5317\u6709\u4e2a\u975e\u5e38\u5feb\u4e50\u7684\u5c11\u5973\u2026\u2026\u201d \u3000\u3000\u65b9\u624d\u542c\u5230\u8fd9\u91cc\uff0c\u8f9b\u6377\u5fc3\u4e2d\u5c31\u662f\u4e00\u52a8\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u4ed6\u6240\u8bf4\u7684\u4e5f\u5728\u6cb3\u5317\uff0c\u4e5f\u662f\u4e2a\u5feb\u4e50\u7684 \u4eba\uff0c\u5374\u662f\u4e2a\u5c11\u5973\uff0c\u8fd9\u5176\u4e2d\u5fc5\u5b9a\u5927\u6709\u6587\u7ae0\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u51dd\u795e\u542c\u90a3\u91d1\u4e00\u9e4f\u8bb2\u9053\uff1a\u201c\u90a3\u5c11\u5973\u975e\u4f46\u8273\u4e0d\u53ef\u65b9\u7269\uff0c\u800c\u4e14\u7236\u6bcd\u4ff1\u5728\uff0c\u5bb6\u9053\u5c0f\u5eb7\uff0c \u5bf9\u5979\u53c8\u662f\u4ff1\u6781\u7231\u62a4\uff0c\u4f60\u8bf4\u8fd9\u6837\u7684\u5c11\u5973\u5feb\u4e50\u4e0d\u5feb\u4e50\u3002\u201d \u3000\u3000\u8f9b\u6377\u832b\u7136\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u53c8\u9053\uff1a\u201c\u90a3\u77e5\u5979\u6240\u4f4f\u7684\u5730\u65b9\uff0c\u6709\u4e2a\u6709\u8d22\u6709\u52bf\u7684\u5e74\u9752\u4eba\uff0c\u53c8\u81ea\u547d\u4e3a\u53e4\u4e4b\u5b5f\u5c1d\uff0c \u7ed3\u4ea4\u4e86\u4e0d\u5c11\u9e21\u9e23\u72d7\u76d7\u4e4b\u5f92\uff0c\u6574\u65e5\u5f20\u7259\u821e\u722a\uff0c\u4e0d\u53ef\u4e00\u4e16\uff0c\u90a3\u5c11\u5973\u7684\u7236\u4eb2\u662f\u4e2a\u5c0f\u5546\u4eba\uff0c\u7ec8\u65e5 \u4e3a\u7740\u4e9b\u8bb8\u8747\u5934\u4e4b\u5229\u800c\u5fd9\u788c\uff0c\u6709\u4e00\u5929\u90a3\u4e2a\u6709\u8d22\u6709\u52bf\u7684\u5e74\u9752\u4eba\uff0c\u6d3e\u4e86\u4e2a\u4eba\u53bb\u4ed6\u5e97\u4e2d\u4e70\u4e1c\u897f\uff0c \u90a3\u5c11\u5973\u7684\u7236\u4eb2\u4e3a\u4e86\u8d5a\u94b1\uff0c\u5927\u7ea6\u662f\u5c06\u4ef7\u94b1\u62ac\u9ad8\u4e86\u4e9b\uff0c\u8fd9\u672c\u662f\u4eba\u4e4b\u5e38\u60c5\uff0c\u7f6a\u603b\u4e0d\u81f4\u6b7b\u5427\uff1f\u201d \u3000\u3000\u4ed6\u773c\u4e2d\u5e26\u7740\u4e00\u79cd\u903c\u4eba\u7684\u5149\u8292\uff0c\u671b\u7740\u8f9b\u6377\uff0c\u8f9b\u6377\u53c8\u832b\u7136\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u51b7\u7b11\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u77e5\u662f\u90a3\u4e2a\u5e74\u8f7b\u4eba\uff0c\u81ea\u547d\u4fa0\u4e49\uff0c\u786c\u8bf4\u5979\u7684\u7236\u4eb2\u662f\u5978\u5546\uff0c \u53c8\u8bf4\u81ea\u53e4\u4ee5\u6765\uff0c\u8d2a\u5b98\u5978\u5546\uff0c\u4e3a\u6076\u6700\u70c8\uff0c\u4e0d\u95ee\u9752\u7ea2\u7682\u767d\uff0c\u6d3e\u4e86\u51e0\u4e2a\u4eba\u5230\u90a3\u5e97\u4e2d\uff0c\u6253\u5f97\u843d\u82b1 \u6d41\u6c34\uff0c\u5979\u7684\u7236\u4eb2\u8fde\u4f24\u3001\u5e26\u6025\u3001\u5e26\u6c14\u3001\u7adf\u7136\u4e00\u547d\u545c\u547c\u4e86\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e8b\u5728\u90a3\u5e74\u8f7b\u4eba\u6765\u8bf4\uff0c\u81ea\u8bf4\u662f\u4e00\u6869\u4e49\u4e3e\uff0c\u8fc7\u4e86\u4e0d\u4e45\uff0c\u5c31\u5fd8\u6000\u4e86\uff0c\u90a3\u5c11\u5973\u4e00\u5bb6\uff0c\u5374 \u56e0\u6b64\u800c\u8dcc\u5165\u707e\u96be\uff0c\u7236\u4eb2\u4e00\u6b7b\uff0c\u6bcd\u4eb2\u8ddf\u7740\u4e5f\u6b7b\u4e86\uff0c\u53ea\u5269\u4e0b\u90a3\u5c11\u5973\u5b64\u82e6\u4f36\u4ec3\u4e00\u4eba\uff0c\u60f3\u62a5\u4ec7\uff0c \u5374\u600e\u654c\u5f97\u8fc7\u90a3\u6709\u94b1\u6709\u52bf\u7684\u4eba\u5462\uff1f\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u51b7\u7b11\u4e00\u58f0\uff0c\u63a5\u7740\u53c8\u9053\uff1a\u201c\u4f46\u662f\u90a3\u5c11\u5973\u5fc3\u4e2d\u6028\u6bd2\u5df2\u6df1\uff0c\u52bf\u6b32\u590d\u4ec7\u800c\u7518\u5fc3\uff0c\u6258\u4e86 \u5a92\u4eba\uff0c\u53bb\u8ddf\u90a3\u5e74\u9752\u4eba\u8bf4\u4eb2\uff0c\u90a3\u5e74\u9752\u4eba\u5c45\u7136\u5c31\u7b54\u5e94\u4e86\uff0c\u90a3\u5c11\u5973\u540d\u867d\u662f\u5ac1\u7ed9\u4ed6\uff0c\u4f46\u5374\u6068\u4e0d\u5f97 \u98df\u4ed6\u4e4b\u8089\uff0c\u5bdd\u4ed6\u4e4b\u76ae\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u8f9b\u6377\u5df1\u9690\u9690\u7ea6\u7ea6\u63e3\u6d4b\u5230\u4e86\u51e0\u5206\uff0c\u4ed6\u773c\u8272\u98d8\u5411\u91d1\u6885\u9f84\uff0c\u89c1\u5979\u53cc\u773c\u7ea2\u80bf\uff0c\u6cea \u73e0\u4e00\u4e32\u4e32\u843d\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u7528\u624b\u629a\u7740\u5979\u7684\u624b\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u4f46\u662f\u90a3\u9752\u5e74\u4e0d\u4f46\u6709\u94b1\u6709\u52bf\uff0c\u8fd8\u6709\u4e00\u8eab\u6b66\u529f\uff0c\u90a3 \u5c11\u5973\u65f6\u65f6\u4f3a\u673a\u800c\u52a8\uff0c\u603b\u6ca1\u6709\u673a\u4f1a\uff0c\u5979\u4e00\u4e2a\u5f31\u4e0d\u7981\u98ce\u7684\u5c11\u5973\uff0c\u8981\u6697\u7b97\u4e00\u4e2a\u6b66\u529f\u6df1\u6e5b\u7684\u4eba\u8c08 \u4f55\u5bb9\u6613\uff0c\u6709\u65f6\u5979\u7b49\u4ed6\u7761\u719f\u4e86\uff0c\u60f3\u523a\u6b7b\u4ed6\uff0c\u90a3\u77e5\u53ea\u8981\u5979\u4e00\u52a8\uff0c\u90a3\u5e74\u9752\u4eba\u4fbf\u81ea\u60ca\u89c9\uff0c\u4f55\u51b5\u5979 \u6839\u672c\u4e00\u4e1d\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u4e24\u53ea\u7ea4\u7ea4\u7389\u624b\uff0c\u60f3\u7ee3\u82b1\u8fd8\u53ef\u4ee5\uff0c\u60f3\u62ff\u7740\u5200\u6740\u4eba\uff0c\u5374\u6839\u672c\u529e\u4e0d\u5230\u3002\u201d \u3000\u3000\u201c\u5979\u60f3\u4e0b\u6bd2\uff0c\u53c8\u6ca1\u6709\u4e00\u4e2a\u4eb2\u8fd1\u7684\u4eba\u4e3a\u4ed6\u4e70\u6bd2\u836f\uff0c\u4f55\u51b5\u5373\u4f7f\u4e0b\u624b\u4e86\uff0c\u4e5f\u96be\u514d\u4e0d\u88ab\u90a3\u5e74 \u9752\u4eba\u53d1\u89c9\uff0c\u8fd9\u6837\u8fc7\u4e86\u51e0\u5e74\uff0c\u5979\u7adf\u66ff\u5979\u7684\u4ec7\u4eba\u751f\u4e86\u4e2a\u5973\u513f\uff0c\u5fc3\u4e2d\u7684\u6101\u3001\u6068\u3001\u60b2\uff0c\u771f\u662f\u522b\u4eba \u60f3\u90fd\u4e0d\u6562\u60f3\u7684\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u5a13\u5a13\u9053\u6765\uff0c\u91d1\u6885\u9f84\u5df2\u662f\u54ed\u5f97\u5982\u5e26\u96e8\u68a8\u82b1\uff0c\u5c31\u8fde\u65b9\u5c11\u74a7\u542c\u4e86\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u6ed1\u7136\u6cea \u4e0b\u3002 \u3000\u3000\u201c\u540e\u6765\uff0c\u90a3\u5e74\u9752\u4eba\u6e38\u5174\u5927\u53d1\uff0c\u5c45\u7136\u8dd1\u51fa\u53bb\u6e38\u5c71\u73a9\u6c34\u53bb\u4e86\uff0c\u90a3\u5c11\u5973\u5fc3\u4e2d\u4ec7\u6068\u672b\u6d88\uff0c\u60b2 \u6028\u65e0\u6cd5\u81ea\u9053\uff0c\u8dd1\u5230\u5e99\u91cc\u53bb\u81ea\u60b2\u8eab\u4e16\uff0c\u90a3\u77e5\u5374\u88ab\u4e00\u4e2a\u4eba\u542c\u4e86\u3002\u8fd9\u4e2a\u4eba\u81ea\u5e7c\u4e5f\u662f\u88ab\u4e16\u4e0a\u4e00\u822c \u6b3a\u4e16\u76d7\u540d\u4e4b\u5f92\u6240\u5bb3\uff0c\u957f\u6210\u540e\u5b66\u4e86\u4e00\u8eab\u7edd\u6280\uff0c\u5c31\u4e13\u548c\u4e16\u95f4\u7684\u90a3\u4e9b\u5c0f\u4eba\u4f5c\u5bf9\uff0c\u65e0\u610f\u542c\u4e86\u8fd9\u5c11 \u5973\u7684\u8eab\u4e16\uff0c\u751f\u6c14\u5f97\u5f88\uff0c\u5c31\u81ea\u544a\u594b\u52c7\u5730\u51fa\u6765\uff0c\u4e3a\u8fd9\u5c11\u5973\u590d\u4ec7\uff0c\u4f60\u80fd\u8bf4\u8fd9\u662f\u9519\u5417\uff1f\u201d\u91d1\u4e00\u9e4f \u51b7\u7136\u95ee\u9053\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u6115\uff0c\u6b64\u523b\u4ed6\u5df2\u77e5\u9053\u8fd9\u4e8b\u7684\u7a76\u7adf\uff0c\u4f46\u662f\u8fd9\u4e8b\u60c5\u662f\u975e\u66f2\u76f4\uff0c\u53c8\u6709\u8c01\u80fd\u4e0b\u4e00\u516c\u8bba\u5462\uff1f \u3000\u3000\u91d1\u4e00\u9e4f\u51c4\u7136\u4e00\u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u77e5\u9053\u547d\u4e0d\u7531\u4eba\u3002\u90a3\u5973\u5b50\u542b\u7f9e\u5fcd\u8fb1\uff0c\u8fd8\u662f\u62a5\u4e0d\u4e86\u4ec7\uff0c \u534a\u8def\u4e0a\u53c8\u6740\u51fa\u4e00\u4e2a\u2018\u4e03\u5999\u795e\u541b\u2019\u6765\uff0c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\uff0c\u4e5f\u4e0d\u95ee\u4e2a\u6e05\u695a\uff0c\u5c31\u5c06\u8fd9\u4e8b\u5f04\u5f97\u4e71\u4e03 \u516b\u7cdf\uff0c\u90a3\u63d2\u624b\u6253\u62b1\u4e0d\u5e73\u7684\u4eba\uff0c\u90a3\u65f6\u81ea\u95ee\u4e0d\u662f\u6885\u5c71\u6c11\u7684\u5bf9\u624b\uff0c\u5c31\u5e26\u7740\u90a3\u5c11\u5973\u548c\u5979\u7684\u5973\u513f\u8d70 \u4e86\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u54ed\u58f0\u66f4\u662f\u60b2\u5207\uff0c\u8f9b\u6377\u5fc3\u4e2d\u4e5f\u4e0d\u7981\u9eef\u7136\uff0c\u5fd6\u9053\uff1a\u201c\u5509\uff01\u5979\u8eab\u4e16\u4e4b\u60e8\uff0c\u66f4\u662f\u4e0d\u53ef \u601d\u8bae\uff0c\u5979\u7684\u2018\u4ec7\u4eba\u2019\u7adf\u662f\u5979\u7684\u7236\u4eb2\uff0c\u4f46\u5979\u7684\u7236\u4eb2\uff0c\u771f\u662f\u5979\u7684\u2018\u4ec7\u4eba\u2019\u5417\uff1f\u82e5\u5979\u7684\u7236\u4eb2\u4e0d \u662f\u4ed6\u7684\u2018\u4ec7\u4eba\u2019\uff0c\u90a3\u8fd9\u4ed7\u4e49\u63f4\u52a9\u5979\u6bcd\u4eb2\u7684\u2018\u6bd2\u541b\u2019\u91d1\u4e00\u9e4f\uff0c\u53c8\u600e\u80fd\u8bf4\u662f\u5979\u7684\u2018\u4ec7\u4eba\u2019 \u5462\uff1f\u201d \u3000\u3000\u5929\u9b54\u91d1\u6b39\u5374\u4ecd\u7136\u5168\u65e0\u8868\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u5e08\u5085\uff0c\u548c\u8fd9\u79cd\u4eba\u7f57\u55e6\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u77e5\u8d70\u5230\u8def\u4e0a\uff0c\u90a3\u5c11\u5973\u7adf\u629b\u4e0b\u5979\u4eb2\u751f\u7684\u5973\u513f\uff0c\u6295\u6cb3\u81ea \u5c3d\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u542c\u4e86\uff0c\u66f4\u662f\u89c9\u5f97\u5bf9\u8fd9\u4f4d\u201c\u6bd2\u541b\u201d\u6709\u4e9b\u6b49\u610f\uff0c\u4ed6\u672c\u4ee5\u4e3a\u8fd9\u201c\u6bd2\u541b\u201d\u7684\u6bd2\uff0c\u548c\u90a3 \u201c\u6deb\u5987\u201d\u7684\u6deb\uff0c\u90fd\u662f\u4e07\u6076\u4e0d\u8d66\u7684\uff0c\u90a3\u77e5\u9053\u8fd9\u201c\u6bd2\u541b\u201d\u5e76\u4e0d\u6bd2\uff0c\u90a3\u201c\u6deb\u5987\u201d\u66f4\u662f\u4e0d\u6deb\uff0c\u800c \u4e14\u8fd8\u6b7b\u5f97\u8fd9\u4e48\u51c4\u60e8\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u535a\u7136\u7b11\u9053\uff1a\u201c\u4ece\u6b64\uff0c\u90a3\u4f38\u624b\u7ba1\u95f2\u4e8b\u7684\u4eba\uff0c\u5c31\u5e26\u7740\u90a3\u5e7c\u5973\u8fdc\u8d70\u5929\u6daf\uff0c\u4ed6\u77e5\u9053\u82b8 \u82b8\u4f17\u751f\uff0c\u53c8\u6709\u51e0\u4e2a\u4eba\u4e0d\u662f\u5728\u9a82\u4ed6\u7684\uff0c\u4f46\u662f\u4ed6\u867d\u7136\u624b\u6bb5\u6bd2\u8fa3\uff0c\u5374\u81ea\u95ee\u6ca1\u6709\u505a\u8fc7\u4e8f\u5fc3\u4e4b\u4e8b\uff0c \u95ee\u5fc3\u65e0\u6127\u4e86\u3002\u201d \u3000\u3000\u8bf4\u5b8c\uff0c\u4ed6\u8138\u4e0a\u53c8\u6362\u6210\u8083\u6740\u4e4b\u6c14\uff0c\u77aa\u7740\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u4e0d\u7ba1\u4f60\u662f\u6885\u5c71\u6c11\u7684\u4ec0\u4e48\u4eba\uff0c\u4f60\u53ef \u4ee5\u56de\u53bb\u544a\u8bc9\u4ed6\u8fd9\u4ef6\u4e8b\u7684\u59cb\u672b\uff0c\u54c8\u54c8\uff0c\u6211\u4e00\u60f3\u5230\u4ed6\u542c\u4e86\u8fd9\u4ef6\u4e8b\u771f\u76f8\u4e4b\u540e\u7684\u96be\u53d7\uff0c\u6211\u5c31\u5feb\u4e50 \u4e86\u3002\u201d \u3000\u3000\u4ed6\u7b11\u58f0\u8d8a\u6765\u8d8a\u5389\u5bb3\uff0c\u7a81\u7136\u53cc\u624b\u4e00\u6293\u3001\u4e00\u6495\uff0c\u5c06\u8eab\u4e0a\u7a7f\u7684\u7ea2\u888d\u53c8\u6495\u6210\u788e\u7247\uff0c\u53cc\u811a\u4e00\u987f\uff0c \u7535\u4e5f\u4f3c\u7684\u7a9c\u5230\u95e8\u5916\uff0c\u53ea\u542c\u5f97\u7830\u7136\u4e00\u58f0\u6c34\u54cd\uff0c\u4fbf\u6ca1\u4e86\u58f0\u606f\u3002 \u3000\u3000\u4ed6\u8fd9\u4e3e\u52a8\u5feb\u5982\u95ea\u7535\uff0c\u8f9b\u6377\u76f4\u60ca\u5f97\u7ad9\u4e86\u8d77\u6765\uff0c\u4e0d\u77e5\u51fa\u4e86\u4f55\u4e8b\u3002\u9762\u4e0a\u59cb\u7ec8\u6ca1\u6709\u8868\u60c5\u7684\u91d1 \u6b39\uff0c\u53f9\u9053\uff1a\u201c\u5e08\u5085\u7684\u75c5\uff0c\u600e\u5730\u8d8a\u6765\u8d8a\u5389\u5bb3\u4e86\u3002\u201d\u53cc\u7709\u4e5f\u7d27\u7d27\u76b1\u5230\u4e00\u5904\u3002 \u3000\u3000\u8f9b\u6377\u5947\u602a\uff1a\u201c\u600e\u5730\u8fd9\u8eab\u6000\u7edd\u6280\u7684\u4eba\uff0c\u53c8\u6709\u4ec0\u4e48\u75c5\uff1f\u201d\u4ed6\u987f\u7136\u60f3\u8d77\u9ec4\u9e64\u697c\u4e0b\u7684\u4ed6\u7684\u72c2 \u6001\uff0c\u7a81\u7136\u609f\u9053\uff1a\u201c\u96be\u9053\u4ed6\u5c61\u53d7\u523a\u6fc0\uff0c\u7adf\u7136\u75c5\u4e86\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u54ed\u58f0\u672a\u4f4f\uff0c\u5f80\u4e8b\u65b0\u6101\uff0c\u4f7f\u5f97\u8fd9\u5c11\u5973\u6cea\u73e0\u66f4\u7c0c\u7c0c\u800c\u843d\uff0c\u8231\u4e2d\u4f17\u4eba\u7cbe\u795e\u53d7\u4e86\u8fd9\u4e9b \u6fc0\u8361\uff0c\u5c45\u7136\u5728\u8fd9\u7247\u523b\u95f4\u90fd\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u6c89\u9759\uff0c\u5374\u4ee4\u4eba\u66f4\u89c9\u5f97\u6709\u4e00\u79cd\u96be\u8a00\u7684\u7a92\u606f\uff0c\u75f4\u7acb\u7740\u7684\u65b9\u5c11\u74a7\uff0c\u601d\u6f6e\u7d0a\u4e71\uff0c\u4e5f\u5fcd \u4e0d\u4f4f\u653e\u58f0\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8f9b\u6377\u8d70\u4e0a\u4e24\u6b65\uff0c\u8f7b\u8f7b\u629a\u7740\u5979\u7684\u79c0\u53d1\uff0c\u4e00\u65f6\u4e5f\u627e\u4e0d\u51fa\u9002\u5f53\u7684\u8bdd\u6765\u8bf4\uff0c\u65b9\u5c11\u74a7\u53ea\u89c9\u629a\u5728 \u5979\u5934\u4e0a\u7684\u624b\uff0c\u662f\u90a3\u4e48\u591a\u60c5\u800c\u6e29\u67d4\uff0c\u6b62\u4f4f\u4e86\u54ed\uff0c\u62ac\u5934\u671b\u7740\u4ed6\uff0c\u4e24\u4eba\u90fd\u89c9\u5f97\u6e29\u99a8\u65e0\u6bd4\uff0c\u7adf\u5fd8 \u4e86\u6b64\u65f6\u8eab\u5728\u4f55\u5730\u3002 \u3000\u3000\u91d1\u6885\u9f84\u89c1\u4e86\uff0c\u773c\u4e2d\u53c8\u73b0\u5e7d\u6028\u4e4b\u8272\uff0c\u4f4e\u4f4e\u53c8\u62bd\u6ce3\u4e86\u8d77\u6765\u3002\u5929\u9b54\u91d1\u6b39\u5992\u706b\u4e2d\u70e7\uff0c\u84e6\u5730\u4e00 \u58f0\u5927\u559d\uff1a\u201c\u90fd\u662f\u4f60\u3002\u201d\u5288\u9762\u4e00\u638c\uff0c\u5411\u8f9b\u6377\u6253\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u60ca\uff0c\u672c\u80fd\u5730\u4e00\u9519\u6b65\uff0c\u91d1\u6b39\u4fa7\u8eab\u6b3a\u4e0a\uff0c\u53f3\u624b\u6a2a\u6253\uff0c\u5de6\u638c\u659c\u524a\uff0c\u53f3\u8db3\u4e00\u8e22\uff0c\u6b63\u662f \u201c\u6bd2\u541b\u201d\u201c\u9634\u638c\u4e03\u5341\u4e8c\u5f0f\u201d\u91cc\u7684\u715e\u624b\u201c\u7acb\u5730\u52fe\u9b44\u201d\u3002 \u3000\u3000\u4ed6\u975e\u4f46\u62db\u5f0f\u72e0\u8fa3\uff0c\u638c\u529b\u66f4\u662f\u9634\u6bd2\uff0c\u53ea\u8981\u6cbe\u4e0a\u4e00\u70b9\uff0c\u4fbf\u4e2d\u5de8\u6bd2\uff0c\u8f9b\u6377\u53ea\u89c9\u4ed6\u638c\u98ce\u4e4b\u4e2d\uff0c \u7adf\u6709\u4e9b\u70ed\u529b\uff0c\u5fc3\u5934\u4e00\u51db\uff0c\u4e00\u62db\u201c\u51cc\u5bd2\u521d\u653e\u201d\uff0c\u8eab\u5411\u5de6\u8f6c\uff0c\u53f3\u624b\u6a2a\u5207\u4ed6\u7684\u5de6\u638c\uff0c\u582a\u582a\u60f3\u907f \u8fc7\u4ed6\u7684\u53f3\u8098\u548c\u5de6\u817f\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u5b88\u4e2d\u5e26\u653b\uff0c\u800c\u4e14\u542b\u52b2\u672a\u653e\uff0c\u679c\u7136\u4e0d\u540c\u51e1\u54cd\uff0c\u91d1\u6b39\u55ef\u4e86\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u9519\uff0c\u65bd\u5c55 \u5f00\u201c\u9634\u638c\u4e03\u5341\u4e8c\u5f0f\u201d\uff0c\u638c\u638c\u62cd\u81f3\u8f9b\u6377\u81f4\u547d\u4e4b\u5904\u3002 \u3000\u3000\u8f9b\u6377\u521d\u9047\u5f3a\u654c\uff0c\u6253\u70b9\u8d77\u7cbe\u795e\u5e94\u4ed8\u7740\uff0c\u8fd9\u5c0f\u5c0f\u4e00\u95f4\u8239\u8231\uff0c\u600e\u7981\u5f97\u8d77\u8fd9\u4e24\u4eba\u7684\u5267\u6597\uff0c\u987f \u65f6\u684c\u7ffb\u6905\u5012\uff0c\u4ef7\u503c\u4e0d\u83f2\u7684\u7fe0\u7389\u5668\u5177\uff0c\u788e\u5f97\u4e00\u5730\u90fd\u662f\u3002 \u3000\u3000\u91d1\u6885\u9f84\u89c1\u4e86\u4e24\u4eba\u820d\u751f\u5fd8\u6b7b\u7684\u6597\u7740\uff0c\u5e7d\u5e7d\u5fd6\u9053\uff1a\u201c\u8fd9\u4e24\u4eba\u8fd9\u6837\u7684\u6253\u6cd5\uff0c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4e00 \u4e2a\u5973\u5b50\uff0c\u53ea\u6709\u6211\u5b64\u82e6\u4f36\u4ec3\uff0c\u53c8\u6709\u8c01\u6765\u75bc\u6211\uff1f\u201d \u3000\u3000\u65b9\u5c11\u74a7\u5413\u5f97\u8eb2\u5728\u8231\u89d2\uff0c\u7741\u5927\u4e86\u773c\u6674\uff0c\u6068\u4e0d\u5f97\u8f9b\u6377\u4e00\u638c\u5c31\u5c06\u91d1\u6b39\u5288\u6b7b\u3002\u5979\u6b66\u529f\u592a\u5f31\uff0c \u6839\u672c\u65e0\u6cd5\u770b\u6e05\u8fd9\u4e24\u4e2a\u7edd\u9876\u9ad8\u624b\u7684\u62db\u5f0f\u3002 \u3000\u3000\u4e24\u4eba\u77ac\u5373\u62c6\u4e86\u4e94\u3001\u4e03\u5341\u62db\uff0c\u4e03\u5999\u795e\u541b\u8f7b\u529f\u72ec\u6b65\u6d77\u5185\uff0c\u4f46\u5728\u8fd9\u5c0f\u5c0f\u4e00\u95f4\u8239\u8231\u4e4b\u4e2d\uff0c\u8f9b \u6377\u5374\u65e0\u4ece\u53d1\u6325\u771f\u5a01\u529b\uff0c\u800c\u4e14\u4ed6\u521d\u5ea6\u51fa\u624b\uff0c\u4fbf\u78b0\u7740\u4e86\u8fd9\u6837\u5f3a\u654c\uff0c\u6253\u4e86\u8bb8\u4e45\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u6697 \u7740\u6025\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u7740\u6025\uff0c\u5374\u4e0d\u77e5\u5929\u9b54\u91d1\u6b39\u4e0d\u4ec5\u6bd4\u4ed6\u66f4\u7740\u6025\uff0c\u800c\u4e14\u8fd8\u5927\u4e3a\u5947\u602a\uff0c\u4ed6\u53d7\u201c\u6bd2\u541b\u91d1\u4e00 \u9e4f\u201d\u591a\u5e74\u8549\u9676\uff0c\u4e0d\u8bf4\u6697\u5668\u4e0e\u5175\u5203\u4e4b\u6bd2\uff0c\u5c31\u62ff\u8fd9\u5957\u638c\u6cd5\uff0c\u5df2\u4e0d\u77e5\u6709\u591a\u5c11\u6c5f\u6e56\u4e0a\u8d6b\u8d6b\u6709\u540d\u7684 \u6b66\u5e08\uff0c\u4e27\u5728\u4ed6\u7684\u638c\u4e0b\u3002 \u3000\u3000\u6b64\u756a\u4ed6\u89c1\u8f9b\u6377\u53ea\u662f\u4e2a\u5e74\u9752\u4e66\u751f\uff0c\u800c\u4e14\u540d\u4e0d\u89c1\u8f7b\u4f20\uff0c\u5728\u6b66\u6797\u4e2d\u8fde\u4e2a\u201c\u4e07\u513f\u201d\u90fd\u6ca1\u6709\uff0c \u4f46\u81ea\u5df1\u5374\u4ec5\u4ec5\u52c9\u5f3a\u6253\u4e2a\u5e73\u624b\uff0c\u5c82\u975e\u5f02\u4e8b\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u5fc3\u795e\u6025\u8e81\uff0c\u638c\u62db\u66f4\u89c1\u72e0\u8fa3\u3002 \u3000\u3000\u987b\u77e5\u8f9b\u6377\u6b66\u529f\u867d\u5df1\u5c3d\u5f97\u6885\u5c71\u6c11\u7684\u771f\u4f20\uff0c\u4f46\u9664\u4e86\u529f\u529b\u5c1a\u5dee\u4e4b\u5916\uff0c\u6700\u4e3b\u8981\u7684\u8fd8\u662f\u4e34\u654c\u7ecf \u5386\u592a\u5c11\uff0c\u5f80\u5f80\u6709\u8bb8\u591a\u7a0d\u7eb5\u5373\u901d\u7684\u5236\u654c\u673a\u5148\u7684\u673a\u4f1a\uff0c\u4ed6\u5374\u672a\u80fd\u628a\u63e1\u4f4f\uff0c\u662f\u4ee5\u4ec5\u80fd\u548c\u91d1\u6b39\u6218 \u4e2a\u5e73\u624b\u3002 \u3000\u3000\u4f46\u867d\u662f\u5982\u6b64\uff0c\u4ed6\u8fd9\u8eab\u6b66\u529f\uff0c\u4e0d\u4f46\u666e\u901a\u6b66\u6797\u4e2d\u4eba\u89c1\u4e86\u5b9a\u4f1a\u76ee\u5b9a\u53e3\u5446\uff0c\u5c31\u8fde\u91d1\u6885\u9f84\u89c1\u4e86 \u4e5f\u662f\u79f0\u5947\u4e0d\u5df2\uff0c\u5979\u4e5f\u6ca1\u6709\u60f3\u5230\u8fd9\u4e00\u4e2a\u770b\u4f3c\u6587\u5f31\u3001\u6700\u591a\u5185\u529f\u7a0d\u6709\u706b\u4faf\u7684\u5c11\u5e74\u4e66\u751f\uff0c\u7adf\u6709\u5982 \u6b64\u529f\u529b\u3002 \u3000\u3000\u638c\u98ce\u6fc0\u52b2\uff0c\u7830\u5730\u5c06\u7a97\u6237\u4e5f\u9707\u5f00\u4e86\uff0c\u91d1\u6885\u9f84\u4fa7\u8eab\u7a97\u5916\uff0c\u6697\u6697\u5438\u4e86\u53e3\u51c9\u6c14\uff0c\u539f\u6765\u8239\u987a\u6fc0 \u6d41\uff0c\u5df2\u4e0d\u77e5\u653e\u5230\u4ec0\u4e48\u6240\u5728\u4e86\u3002 \u3000\u3000\u5ffd\u5730\uff0c\u5979\u611f\u89c9\u5230\u4e24\u5cb8\u7684\u5730\u5e73\u7ebf\u9010\u6e10\u4e0a\u5347\uff0c\u518d\u4e00\u53d1\u73b0\uff0c\u7adf\u662f\u8239\u8eab\u9010\u6e10\u4e0b\u964d\uff0c\u6162\u6162\u5411\u6c34 \u91cc\u6c89\u4e0b\u3002 \u3000\u3000\u518d\u4e00\u63a2\u8eab\u5916\u671b\uff0c\u6c34\u9762\u7adf\u5df2\u5230\u4e86\u8239\u8237\uff0c\u800c\u4e14\u64cd\u8239\u7684\u8239\u592b\uff0c\u4e5f\u4e0d\u89c1\u4e00\u4e2a\u4e86\u3002 \u3000\u3000\u5979\u987e\u4e0d\u5f97\u8231\u4e2d\u4e24\u4eba\u7684\u62fc\u6597\uff0c\u7eb5\u8eab\u63a0\u51fa\u7a97\u5916\uff0c\u53ea\u89c1\u8239\u4e0a\u5012\u7740\u51e0\u5177\u6b7b\u5c38\uff0c\u8fde\u5fd9\u7eb5\u8eab\u8fc7\u53bb\uff0c \u7adf\u662f\u64cd\u8239\u7684\u8239\u592b\uff0c\u65e0\u58f0\u65e0\u606f\u5730\u88ab\u4eba\u5168\u523a\u6b7b\u4e86\u3002 \u3000\u3000\u8bd5\u60f3\u8239\u653e\u4e2d\u6d41\uff0c\u8239\u4e2d\u7684\u4eba\u4ff1\u662f\u7edd\u9876\u9ad8\u624b\uff0c\u7eb5\u7136\u662f\u5404\u4eba\u90fd\u6709\u5fc3\u4e8b\uff0c\u4f46\u88ab\u4eba\u5728\u8231\u5916\u5c06\u8239 \u592b\u5168\u90e8\u5236\u6b7b\uff0c\u5c82\u975e\u4e0d\u53ef\u601d\u8bae\u4e4b\u4e8b\u3002 \u3000\u3000\u91d1\u6885\u9f84\u60ca\u7591\u4e07\u72b6\uff0c\u4fef\u4e0b\u8eab\u53bb\uff0c\u53ea\u89c1\u6bcf\u4e2a\u8239\u592b\u9888\u4e0a\u90fd\u6a2a\u8d2f\u4e86\u4e00\u679d\u5c0f\u7bad\uff0c\u88ab\u7bad\u5c04\u4e2d\u7684\u808c \u8089\u56db\u5468\uff0c\u6cdb\u51fa\u4e4c\u9ed1\u4e4b\u8272\uff0c\u800c\u4e14\u8fd8\u6709\u9ed1\u8272\u6d53\u6c41\u6d41\u51fa\u3002 \u3000\u3000\u5979\u968f\u7740\u201c\u6bd2\u541b\u201d\u591a\u5e74\uff0c\u5929\u4e0b\u5404\u6bd2\uff0c\u518d\u4e5f\u6ca1\u6709\u6bd2\u8fc7\u201c\u6bd2\u541b\u201d\u7684\uff0c\u5979\u4e00\u770b\u4fbf\u77e5\u9053\u8fd9\u4e9b\u8239 \u592b\u5168\u662f\u4e2d\u7edd\u6bd2\u6697\u5668\uff01\u4f38\u624b\u4eba\u6000\uff0c\u53d6\u51fa\u4e00\u53ea\u9e7f\u76ae\u624b\u5957\uff0c\u6234\u5728\u624b\u4e0a\uff0c\u62d4\u51fa\u90a3\u4e2a\u7bad\u4e00\u770b\uff0c\u8138\u4e0a \u4e0d\u7981\u500f\u7136\u8272\u53d8\u3002 \u3000\u3000\u90a3\u4e2a\u7bad\u4e4b\u4e0a\uff0c\u523b\u7740\u4e00\u4e2a\u7bc6\u4e66\u201c\u5510\u201d\u5b57\u3002 \u3000\u3000\u91d1\u6885\u9f84\u4e00\u58f0\u4f4e\u5524\uff0c\u5fd6\u9053\uff1a\u201c\u56db\u5ddd\u7684\u5510\u5bb6\u600e\u5730\u4f1a\u5230\u6b64\u5730\uff0c\u5728\u8239\u4e0a\u505a\u4e86\u624b\u811a\uff0c\u5374\u53c8\u4e0d\u89c1 \u4eba\u5f71\u5462\uff1f\u201d \u3000\u3000\u5979\u4e00\u62ac\u5934\uff0c\u89c1\u90a3\u8239\u9996\u7684\u6a2a\u6728\u4e0a\uff0c\u8fce\u98ce\u98d8\u821e\u7740\u4e00\u5f20\u5b57\u6761\uff0c\u5979\u8eab\u5982\u98de\u71d5\uff0c\u5c06\u90a3\u5b57\u6761\u62ff\u5230 \u624b\u4e0a\u3002 \u3000\u3000\u6b64\u523b\u5929\u5df2\u5fae\u5fae\u89c1\u767d\uff0c\u5979\u85c9\u7740\u4e9b\u8bb8\u6668\u66e6\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u5b57\u6761\u4e0a\u7aef\u7aef\u6b63\u6b63\u5199\u7740\uff1a \u3000\u3000\u201c\u51a4\u9b44\u7d22\u547d\uff0c\u5eff\u5e74\u4e0d\u6563\uff0c\u4eca\u65e5\u4e00\u8239\uff0c\u9001\u541b\u4eba\u6c5f\uff0c\u89c1\u4e86\u960e\u738b\uff0c\u4f11\u6028\u8001\u5510\u3002\u201d \u3000\u3000\u5979\u518d\u4fa7\u76ee\u4e00\u671b\uff0c\u8239\u8d8a\u6c89\u8d8a\u6df1\uff0c\u773c\u770b\u5c31\u8981\u5b8c\u5168\u5165\u6c34\u4e86\uff0c\u56db\u987e\u6c5f\u9762\uff0c\u70df\u6ce2\u6d69\u7ff0\uff0c\u6b63\u662f\u6c5f \u5fc3\u4e4b\u5904\u3002 \u3000\u3000\u5979\u60ca\u60e7\u4ea4\u96c6\uff0c\u8eab\u5f62\u5982\u98de\uff0c\u63a0\u8ff8\u8231\u5185\uff0c\u53ea\u89c1\u8231\u5185\u638c\u98ce\u5df2\u606f\uff0c\u5929\u9b54\u91d1\u6b39\u6b63\u7ad9\u5728\u90a3\u513f\u51b7\u7b11\u3002 \u3000\u3000\u518d\u4e00\u770b\uff0c\u8f9b\u6377\u8138\u8272\u82cd\u767d\uff0c\u53f3\u624b\u6367\u7740\u5de6\u624b\uff0c\u80cc\u5899\u800c\u7acb\uff0c\u65b9\u5c11\u74a7\u7126\u6025\u5730\u6321\u5728\u8f9b\u6377\u8eab\u524d\uff0c \u4e24\u53ea\u773c\u775b\u72e0\u72e0\u5730\u76ef\u7740\u5929\u9b54\u91d1\u6b39\u3002 \u3000\u3000\u5979\u4e00\u770b\u8f9b\u6377\u7684\u9762\u8272\uff0c\u4fbf\u77e5\u8f9b\u6377\u5df2\u4e2d\u4e86\u5de8\u6bd2\uff0c\u65e0\u836f\u53ef\u89e3\uff0c\u9664\u91d1\u4e00\u9e4f\u672c\u8eab\u4e4b\u5916\uff0c\u8c01\u4e5f\u6ca1 \u6709\u89e3\u836f\uff0c\u5c31\u7b97\u4eb2\u5982\u4ed6\u81ea\u5df1\u7684\u5f1f\u5b50\u91d1\u6b39\u548c\u91d1\u6885\u9f84\uff0c\u4ed6\u4e5f\u53ea\u4f20\u6bd2\u65b9\uff0c\u4e0d\u4f20\u89e3\u65b9\uff0c\u8fd9\u81ea\u662f\u91d1\u4e00 \u9e4f\u751f\u6027\u5947\u7279\u4e4b\u5904\uff0c\u4ed6\u81ea\u4ece\u77e5\u9053\u6885\u5c71\u6c11\u627e\u5230\u89e3\u836f\uff0c\u6551\u4e86\u201c\u4faf\u4e8c\u201d\u7684\u6027\u547d\u4e4b\u540e\uff0c\u8bc8\u4e5f\u4e0d\u77e5\u9053 \u4ed6\u5c06\u89e3\u836f\u653e\u5728\u90a3\u91cc\uff0c\u6b64\u523b\u8f9b\u6377\u4e2d\u7684\u6bd2\u867d\u8fd8\u4e0d\u592a\u591a\uff0c\u4f46\u4e5f\u4ec5\u4ec5\u53ea\u80fd\u6d3b\u4e2a\u4e09\u4e24\u5929\u800c\u5df2\u3002 \u3000\u3000\u5979\u5bf9\u8f9b\u6377\u82b3\u5fc3\u5df2\u6697\u6697\u5fc3\u8bb8\uff0c\u89c1\u4e86\u4ed6\u8eab\u53d7\u5de8\u6bd2\uff0c\u81ea\u662f\u5927\u9a87\uff0c\u4f46\u968f\u5373\u60f3\u5230\u81ea\u5df1\u8eab\u5728\u6c5f\u5fc3 \u6c89\u8239\u4e4b\u4e0a\uff0c\u53c8\u4f55\u5c1d\u80fd\u4fdd\u5f97\u4e86\u6027\u547d\u3002 \u3000\u3000\u5979\u4e00\u5ff5\u81f3\u6b64\uff0c\u53cd\u89c9\u5766\u7136\uff0c\u671d\u5929\u9b54\u91d1\u6b39\u7b11\u9053\uff1a\u201c\u5e08\u54e5\uff0c\u4f60\u770b\u770b\u7a97\u5916\u3002\u201d \u3000\u3000\u539f\u6765\u8f9b\u6377\u4e0e\u91d1\u6b39\u62c6\u4e86\u767e\u4f59\u62db\u540e\uff0c\u5df2\u6e10\u6e10\u609f\u51fa\u4e86\u5236\u654c\u7684\u9053\u7406\uff0c\u62a2\u624b\u6570\u638c\uff0c\u5c06\u91d1\u6b39\u903c\u5728 \u4e0b\u98ce\u3002 \u3000\u3000\u91d1\u6b39\u5fc3\u91cc\u53c8\u614c\u53c8\u6025\uff0c\u7a81\u7136\u770b\u5230\u7a97\u6839\u4e0a\u6446\u7740\u4e03\u53ea\u82b1\u74f6\uff0c\u5df2\u9707\u5728\u5730\u4e0a\uff0c\u53ea\u6709\u4e00\u53ea\uff0c\u8fd8\u659c \u5728\u89d2\u843d\u91cc\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u77e5\u9053\u8fd9\u4e03\u53ea\u82b1\u74f6\u90fd\u9644\u6709\u5947\u6bd2\uff0c\u662f\u6bd2\u541b\u91d1\u4e00\u9e4f\u5e73\u53e3\u7ec3\u638c\u6240\u7528\uff0c\u91d1\u6b39\u81ea\u5df1 \u4e5f\u5728\u8fd9\u4e03\u53ea\u74f6\u4e0a\uff0c\u4e0b\u8fc7\u4e0d\u5c11\u529f\u592b\uff0c\u4f46\u82e5\u975e\u5148\u670d\u4e0b\u89e3\u836f\uff0c\u4f53\u80a4\u4e00\u6cbe\u6b64\u74f6\uff0c\u4fbf\u4e2d\u5de8\u6bd2\uff0c\u5929\u9b54 \u91d1\u6b39\u4e45\u7ec3\u6bd2\u638c\uff0c\u81ea\u662f\u4e0d\u6015\uff0c\u82e5\u8f9b\u6377\u7684\u624b\u638c\u6cbe\u4e86\u6b64\u74f6\u4e00\u70b9\uff0c\u5374\u662f\u5927\u7978\u3002 \u3000\u3000\u4ed6\u5fc3\u5ff5\u4e00\u8f6c\uff0c\u811a\u6b65\u5411\u82b1\u74f6\u6240\u5728\u4e4b\u5730\u79fb\u53bb\uff0c\u6781\u5feb\u5730\u4f38\u624b\u53d6\u5f97\u8fd9\u74f6\u5b50\uff0c\u53f3\u638c\u5c3d\u529b\u4e00\u5288\uff0c \u8eab\u5f62\u540e\u7eb5\u3002 \u3000\u3000\u8f9b\u6377\u5fae\u4e00\u4fa7\u8eab\uff0c\u907f\u8fc7\u6b64\u638c\uff0c\u8eab\u5f62\u524d\u6251\uff0c\u4e00\u62db\u201c\u6885\u5360\u6625\u5148\u201d\uff0c\u6b63\u8981\u5411\u91d1\u6b39\u62cd\u53bb\uff0c\u5374\u89c1 \u4e00\u53ea\u82b1\u74f6\uff0c\u8fce\u9762\u6253\u6765\uff0c\u4ed6\u60f3\u4e5f\u4e0d\u60f3\uff0c\u4e00\u638c\u5411\u90a3\u836f\u74f6\u62cd\u53bb\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u624b\u638c\u4e00\u6cbe\u90a3\u74f6\u5b50\uff0c\u5c31\u89c9\u5f97\u6709\u4e00\u79cd\u5f02\u6837\u7684\u611f\u89c9\uff0c\u4ed6\u731b\u7136\u60f3\u8d77\u201c\u4faf\u4e8c\u201d\u7684\u8bdd\uff0c\u5728 \u8fd9\u4e00\u5239\u90a3\u91cc\uff0c\u201c\u6b7b\u201d\u7684\u611f\u89c9\u50cf\u5e7d\u7075\u4e4b\u7ffc\uff0c\u6084\u7136\u5411\u4ed6\u88ad\u6765\uff0c\u4ed6\u811a\u8ddf\u731b\u65cb\uff0c\u5c06\u5411\u524d\u7eb5\u7684\u529b\u91cf \u987f\u4f4f\uff0c\u7eb5\u8eab\u9000\u5230\u58c1\u524d\u3002 \u3000\u3000\u91d1\u6b39\u9634\u607b\u607b\u7684\u7b11\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u59d3\u8f9b\u7684\uff0c\u660e\u5e74\u4eca\u5929\uff0c\u5c31\u662f\u9601\u4e0b\u7684\u5fcc\u8fb0\u3002\u201d \u3000\u3000\u65b9\u5c11\u74a7\u95fb\u8a00\u5927\u60ca\uff0c\u5954\u5230\u8f9b\u6377\u8ddf\u524d\uff0c\u91d1\u6b39\u4e5f\u4e0d\u963b\u62e6\uff0c\u53ea\u662f\u9634\u9634\u7684\u7b11\u7740\u3002\u4ed6\u9664\u53bb\u5f3a\u4ec7\uff0c \u53c8\u9664\u53bb\u60c5\u654c\uff0c\u5fc3\u4e2d\u81ea\u662f\u5f97\u610f\u5df2\u6781\u3002 \u3000\u3000\u6b64\u523b\u7a81\u7136\u53d1\u73b0\u81ea\u7a97\u5916\u7eb5\u8eab\u800c\u4eba\u7684\u91d1\u6885\u9f84\uff0c\u9762\u5e26\u5f02\u8272\uff0c\u53c8\u53eb\u4ed6\u770b\u770b\u7a97\u5916\uff0c\u4ed6\u4e00\u63a0\u800c\u81f3 \u7a97\u5916\uff0c\u5f97\u610f\u4e4b\u60c5\uff0c\u7acb\u523b\u8d70\u5f97\u5e72\u5e72\u51c0\u51c0\u3002 \u3000\u3000\u539f\u6765\u6c34\u52bf\u4e0a\u6d8c\uff0c\u7adf\u5df1\u5feb\u5230\u7a97\u5b50\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u81ea\u53d1\u73b0\uff0c\u4f46\u4ed6\u8eab\u53d7\u5947\u6bd2\uff0c\u81ea\u77e5\u5df2\u65e0\u6d3b\u547d\uff0c\u53cd\u800c\u6cf0\u7136\uff0c\u4e00\u628a\u6402\u8fc7\u65b9\u5c11\u74a7\uff0c\u54c8\u54c8 \u7b11\u9053\uff1a\u201c\u6211\u6b7b\u4e5f\u548c\u5fc3\u7231\u7684\u4eba\u6b7b\u5728\u4e00\u5757\uff0c\u603b\u6bd4\u4f60\u5f3a\u5f97\u591a\uff0c\u4eba\u7b97\u4e0d\u5982\u5929\u7b97\uff0c\u60f3\u4e0d\u5230\u660e\u5e74\u4eca\u65e5\uff0c \u4e5f\u662f\u9601\u4e0b\u7684\u5fcc\u8fb0\u5462\uff1f\u201d \u3000\u3000\u65b9\u5c11\u671b\u88ab\u4ed6\u6402\u5728\u6000\u91cc\uff0c\u5fc3\u91cc\u89c9\u5f97\u751c\u751c\u5730\uff0c\u751f\u6b7b\u4e5f\u770b\u5f97\u6de1\u4e86\uff0c\u95ed\u4e0a\u773c\u6674\uff0c\u4eab\u53d7\u7740\u8fd9\u7247 \u523b\u6e29\u99a8\u3002 \u3000\u3000\u91d1\u6885\u9f84\u5fc3\u4e2d\u4e00\u9178\uff0c\u6389\u8fc7\u5934\u53bb\uff0c\u4e0d\u518d\u770b\u4ed6\u4eec\u4e24\u4eba\u4eb2\u70ed\u7684\u6837\u5b50\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u89c1\u4e86\uff0c\u5ac9\u5992\u7684\u706b\u7130\uff0c\u4f7f\u5f97\u4ed6\u4e5f\u5fd8\u4e86\u751f\u6b7b\uff0c\u7eb5\u8eab\u6251\u53bb\u3002 \u3000\u3000\u54d7\u5730\u4e00\u58f0\uff0c\u7a97\u5b50\u91cc\u5df2\u6d8c\u8fdb\u6c34\u6765\uff0c\u6643\u773c\u4fbf\u6df9\u6ca1\u8db3\u8e1d\u3002 \u3000\u3000\u8f9b\u6377\u4f46\u89c9\u5168\u8eab\u5df2\u6709\u4e9b\u53d1\u8f6f\uff0c\u52c9\u5f3a\u62c6\u4e86\u4e00\u638c\uff0c\u4f46\u6000\u4e2d\u7684\u65b9\u5c11\u74a7\u5df2\u88ab\u91d1\u6b39\u62a2\u53bb\uff0c\u6402\u5728\u6000 \u91cc\uff0c\u683c\u683c\u602a\u7b11\u9053\uff1a\u201c\u5979\u6b7b\u4e5f\u8981\u548c\u6211\u6b7b\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u8f9b\u6377\u53cc\u638c\u5e76\u51fa\uff0c\u5168\u529b\u51fb\u5411\u91d1\u6b39\uff0c\u4f46\u4ed6\u8eab\u53d7\u5929\u4e0b\u4e4b\u5de8\u6bd2\uff0c\u529f\u529b\u5df2\u5927\u5927\u6253\u4e86\u4e2a\u6298\u6263\uff0c\u91d1 \u6b39\u53f3\u638c\u4e00\u6325\uff0c\u53c8\u5c06\u4ed6\u903c\u4e86\u56de\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u84c4\u52bf\u6b63\u60f3\u518d\u6251\uff0c\u90a3\u77e5\u65b9\u5c11\u74a7\u4e00\u53e3\u54ac\u5728\u91d1\u6b4c\u7684\u53f3\u81c2\u4e0a\uff0c\u91d1\u6b4c\u75db\u6781\uff0c\u4e00\u677e\u624b\uff0c\u65b9\u5c11 \u74a7\u53c8\u6251\u8fdb\u8f9b\u6377\u7684\u6000\u91cc\u3002 \u3000\u3000\u6b64\u65f6\u6c34\u52bf\u5df2\u5feb\u6d78\u5230\u8170\u90e8\u4e86\u3002 \u3000\u3000\u4f46\u91d1\u6b39\u4ecd\u4e0d\u6b7b\u5fc3\uff0c\u53c8\u6251\u4e86\u4e0a\u53bb\uff0c\u8f9b\u6377\u5148\u53d1\u5236\u4eba\uff0c\u4e00\u638c\u62cd\u5411\u4ed6\u7684\u5de6\u80a9\uff0c\u90a3\u77e5\u4ed6\u4e0d\u907f\u4e0d \u95ea\uff0c\u786c\u751f\u751f\u63a5\u4e86\u8f9b\u6377\u4e00\u638c\uff0c\u53cc\u624b\u6293\u7740\u65b9\u5c11\u74a7\uff0c\u53c8\u5c06\u5979\u62a2\u5728\u6000\u91cc\uff0c\u6c34\u52bf\u6c79\u6d8c\uff0c\u5df2\u6f2b\u8fc7\u8170\u90e8 \u4e86\u3002 \u3000\u3000\u91d1\u6885\u9f84\u773c\u542b\u75db\u6cea\uff0c\u4eba\u5728\u4e34\u6b7b\u4e4b\u9645\uff0c\u6700\u9700\u8981\u60c5\u7684\u5b89\u6170\uff0c\u4f46\u662f\u5979\u81f3\u6b7b\u90fd\u662f\u4f36\u4ec3\u4e00\u4eba\uff0c\u8eab \u4fa7\u7684\u4e24\u4eba\uff0c\u4e3a\u7740\u53e6\u4e00\u4e2a\u5973\u4eba\uff0c\u4e89\u5f97\u6fd2\u6b7b\u8fd8\u8981\u4e89\uff0c\u5979\u5fc3\u4e2d\u65e2\u843d\u5bde\uff0c\u53c8\u96be\u53d7\uff0c\u4e00\u79cd\u7a7a\u865a\u800c\u5bc2 \u83ab\u7684\u611f\u89c9\uff0c\u751a\u81f3\u6bd4\u6b7b\u8fd8\u5f3a\u70c8\uff0c\u7d27\u7d27\u8feb\u5411\u5979\u8fd9\u4e2a\u5c11\u5973\uff0c\u5979\u5a07\u557c\u4e00\u58f0\uff0c\u518d\u4e5f\u987e\u4e0d\u5f97\u7f9e\u803b\uff0c\u7eb5 \u8eab\u6251\u5411\u8f9b\u6377\uff0c\u7d27\u7d27\u6402\u7740\u4ed6\u7684\u8116\u5b50\u3002 \u3000\u3000\u201c\u60c5\u201d\u4e4b\u4e00\u5b57\uff0c\u529b\u91cf\u5c31\u662f\u8fd9\u4e48\u4f1f\u5927\uff0c\u53e4\u5f80\u4eca\u6765\uff0c\u552f\u4e00\u80fd\u4f7f\u4eba\u542b\u7b11\u6b7b\u53bb\u7684\uff0c\u4e5f\u53ea\u6709 \u201c\u60c5\u201d\u4e4b\u4e00\u5b57\u800c\u5df2\u3002 \u8f70 \u73cd\u5b9d\uff0c\u51e0\u4e2a\u8239\u592b\u7684\u6b7b\u5c38\uff0c\u548c\u56f0\u6b7b\u540e\u8231\u7684\u56db\u4e2a\u5c11\u5973\uff0c\u4ee5\u53ca\u524d\u8231\u7684\u4e24\u5bf9\u4e3a\u201c\u60c5\u201d\u98a0\u5012\uff0c\u8eab\u6000 \u7edd\u6280\u7684\u7537\u5973\uff0c\u9f50\u90fd\u6c89\u4eba\u6c34\u4e2d\u4e86\u3002 \u3000\u3000\u6c5f\u9762\u4e0a\u8d77\u4e86\u4e00\u4e2a\u6f29\u6da1\uff0c\u4f46\u65cb\u5373\u56de\u590d\u5e73\u9759\u3002 \u3000\u3000\u6c5f\u6c34\u4e1c\u6d41\uff0c\u8fd9\u53ea\u8239\u7684\u6c89\u6ca1\u4e0e\u5426\uff0c\u4e1d\u6beb\u4e0d\u80fd\u5f71\u54cd\u5230\u5b83\u3002 \u3000\u3000\u91d1\u6885\u9f84\u53cc\u624b\u7d27\u7d27\u6402\u7740\u8f9b\u6377\uff0c\u8f9b\u6377\u5fc3\u4e2d\u4e0d\u77e5\u662f\u60ca\u7591\uff1f\u662f\u6e29\u99a8\uff1f\u8fd8\u662f\u8ff7\u60d8\uff1f \u3000\u3000\u5c31\u5728\u8fd9\u96be\u4ee5\u89e3\u91ca\u7684\u60c5\u611f\u4e2d\uff0c\u4ed6\u4e5f\u4f38\u624b\u73af\u62b1\u7740\u91d1\u6885\u9f84\u7684\u8170\u3002 \u3000\u3000\u6c34\u52bf\u6df9\u8fc7\u4e24\u4eba\u7684\u5934\u9876\uff0c\u91d1\u6885\u9f84\u5374\u89c9\u5f97\u5979\u4e00\u751f\u4e4b\u4e2d\uff0c\u518d\u4e5f\u6ca1\u6709\u6bd4\u6b64\u523b\u66f4\u5e78\u798f\u7684\u65f6\u5019\u4e86\u3002 \u3000\u3000\u4e00\u4e2a\u6d6a\u5934\u6253\u8fc7\u6765\uff0c\u4e00\u5757\u751a\u4e3a\u539a\u91cd\u7684\u6728\u677f\uff0c\u78b0\u5230\u5979\u8eab\u4e0a\uff0c\u4f46\u5728\u6c34\u91cc\uff0c\u5979\u5e76\u4e0d\u89c9\u5f97\u6c89\u91cd\u3002 \u3000\u3000\u6c42\u751f\u7684\u672c\u80fd\uff0c\u4f7f\u5f97\u5979\u5300\u51fa\u4e00\u53ea\u624b\u6765\uff0c\u6293\u4f4f\u90a3\u6728\u677f\uff0c\u5979\u5185\u529b\u9887\u6df1\uff0c\u518d\u52a0\u4e0a\u662f\u5728\u8fd9\u79cd\u751f \u6b7b\u4e4b\u95f4\u7684\u5173\u5934\uff0c\u4e94\u6307\u7adf\u90fd\u6df1\u6df1\u5d4c\u5165\u6728\u91cc\u3002 \u3000\u3000\u6c34\u6ce2\u7ffb\u8f6c\uff0c\u6d6a\u82b1\u5982\u96ea\uff0c\u521d\u751f\u65ed\u65e5\uff0c\u5c06\u957f\u6c5f\u6d41\u6c34\uff0c\u6620\u5f71\u6210\u4e00\u6761\u91d1\u9ec4\u7684\u5e26\u5b50\u3002 \u3000\u3000\u91d1\u6885\u9f84\u4e00\u53ea\u624b\u7d27\u7d27\u6402\u7740\u8f9b\u6377\uff0c\u4e00\u53ea\u624b\u7d27\u7d27\u6293\u7740\u6728\u677f\uff0c\u6e10\u6e10\uff0c\u5979\u795e\u667a\u5df2\u5931\uff0c\u5076\u7136\u6ca1\u6709 \u4e86\u77e5\u89c9\u3002 \u3000\u3000\u65e0\u60c5\u6700\u662f\u957f\u6c5f\u6c34\uff0c\u4f46\u8fd9\u6d6a\u82b1\u5374\u662f\u6709\u60c5\uff0c\u7adf\u5c06\u8fd9\u4e24\u4e2a\u7d27\u7d27\u6402\u62b1\u7740\u7684\u4eba\u513f\uff0c\u9001\u5230\u4e86\u5cb8\u4e0a\u3002 \u3000\u3000\u65ed\u65e5\u4e1c\u5347\uff0c\u9633\u5149\u9010\u6e10\u5f3a\u70c8\u3002 \u3000\u3000\u91d1\u6885\u9f84\u7741\u5f00\u773c\u775b\u65f6\uff0c\u5f3a\u70c8\u7684\u9633\u5149\u6b63\u7167\u5728\u5979\u773c\u524d\uff0c\u4f46\u662f\u8fd9\u611f\u89c9\u5bf9\u5979\u6765\u8bf4\uff0c\u662f\u591a\u4e48\u6b23\u7136 \u548c\u72c2\u559c\u5440\u3002 \u3000\u3000\u5979\u60f3\u4f38\u624b\u63c9\u4e00\u63c9\u773c\u775b\uff0c\u6765\u8bc1\u5b9e\u81ea\u5df1\u7684\u611f\u89c9\uff0c\u54ea\u77e5\u4e00\u5757\u957f\u800c\u5927\u7684\u6728\u677f\u5374\u9644\u5728\u5979\u624b\u4e0a\u3002 \u3000\u3000\u671b\u7740\u90a3\u6728\u677f\uff0c\u5979\u611f\u8c22\u5730\u7b11\u4e86\uff0c\u82e5\u4e0d\u662f\u8fd9\u5757\u6728\u677f\uff0c\u5979\u53ea\u6015\u6c38\u8fdc\u4e5f\u89c1\u4e0d\u5230\u9633\u5149\u4e86\u3002 \u3000\u3000\u5979\u5c06\u624b\u6307\u62d4\u4e86\u51fa\u6765\uff0c\u6625\u8471\u822c\u7684\u624b\u6307\uff0c\u5df2\u53d8\u5f97\u6709\u4e9b\u7ea2\u80bf\u4e86\uff0c\u5979\u629a\u6478\u7740\u90a3\u5757\u6728\u677f\uff0c\u53d1\u89c9 \u7adf\u662f\u6bd2\u541b\u91d1\u4e00\u9e4f\u6240\u7761\u7684\u6728\u677f\uff0c\u5979\u60f3\u8d77\u81ea\u5df1\u5c61\u6b21\u529d\u201c\u7239\u7239\u201d\u4e0d\u8981\u7761\u5728\u8fd9\u786c\u6886\u6886\u7684\u6728\u677f\u4e0a\uff0c \u201c\u7239\u7239\u201d\u603b\u662f\u4e0d\u542c\uff0c\u60f3\u4e0d\u5230\u4eca\u5929\u5374\u9760\u8fd9\u5757\u6728\u677f\u9003\u5f97\u6027\u547d\u3002 \u3000\u3000\u5979\u53f3\u81c2\u9ebb\u6728\u5f97\u5f88\uff0c\u539f\u6765\u8f9b\u6377\u6b63\u6795\u5728\u5979\u7684\u624b\u81c2\u4e0a\uff0c\u4ecd\u7136\u660f\u8ff7\u7740\uff0c\u5979\u7b11\u4e86\uff0c\u90a3\u4e48\u5e78\u798f\u5730 \u7b11\u4e86\u3002 \u3000\u3000\u4ece\u6b7b\u4e4b\u4e2d\u9003\u4e86\u51fa\u6765\u7684\u4eba\uff0c\u8eab\u4fa7\u53c8\u6709\u81ea\u5df1\u6240\u949f\u60c5\u7684\u4eba\u513f\u966a\u7740\uff0c\u4e16\u4e0a\u5176\u4ed6\u4efb\u4f55\u4e00\u4ef6\u4e8b\uff0c \u90fd\u4e0d\u8db3\u4e3a\u8651\u4e86\u3002 \u3000\u3000\u5979\u4f38\u51fa\u5de6\u624b\u629a\u6478\u7740\u8f9b\u6377\u7684\u8138\uff0c\u90a3\u77e5\u89e6\u624b\u5374\u50cf\u706b\u4e00\u6837\u7684\u70eb\uff0c\u5979\u8981\u5730\u60f3\u8d77\u8f9b\u6377\u8eab\u4e0a\u7684\u6bd2\uff0c \u4e0d\u7981\u53c8\u9eef\u7136\u4e86\u3002 \u3000\u3000\u91d1\u6885\u9f84\u8eba\u5728\u5730\u4e0a\uff0c\u5ffd\u6101\u5ffd\u559c\uff0c\u67d4\u80a0\u767e\u8f6c\uff0c\u4e0d\u77e5\u600e\u751f\u662f\u597d\u3002 \u3000\u3000\u5979\u6e10\u89c9\u624b\u81c2\u4e0a\u7684\u8f9b\u6377\u5728\u5fae\u5fae\u8f6c\u52a8\u7740\u8eab\u8eaf\uff0c\u5979\u77e5\u9053\u4ed6\u4e5f\u6b63\u5728\u82cf\u9192\u7740\u3002 \u3000\u3000\u9633\u5149\u521d\u9732\uff0c\u7167\u5728\u4ed6\u7684\u8138\u4e0a\uff0c\u91d1\u6885\u9f84\u53ea\u89c9\u5f97\u4ed6\u90a3\u4e48\u82cd\u767d\uff0c\u90a3\u4e48\u6587\u5f31\uff0c\u82e5\u4e0d\u662f\u65b9\u624d\u770b\u5230 \u4ed6\u90a3\u4e00\u756a\u820d\u751f\u5fd8\u6b7b\u7684\u72e0\u6597\uff0c\u771f\u4ee5\u4e3a\u4ed6\u662f\u4e2a\u6587\u5f31\u7684\u4e66\u751f\u3002 \u3000\u3000\u5979\u5fae\u53f9\u4e86\u53e3\u6c14\uff0c\u7ea4\u7ea4\u7389\u6307\u987a\u7740\u4ed6\u5fae\u8038\u7684\u98a7\u9aa8\u6ed1\u4e86\u4e0b\u53bb\uff0c\u505c\u7559\u5728\u4ed6\u7684\u4e0b\u989a\u4e0a\u3002 \u3000\u3000\u201c\u82e5\u7136\u4ed6\u521a\u624d\u7684\u90a3\u4e00\u756a\u820d\u751f\u5fd8\u6b7b\u7684\u62fc\u6597\uff0c\u6709\u4e00\u5206\u662f\u4e3a\u4e86\u6211\uff0c\u6211\u6b7b\u4e5f\u7518\u5fc3\u3002\u201d\u5979\u5e7d\u6028 \u5730\u60f3\u7740\uff0c\u968f\u53c8\u5c55\u989c\u4e00\u7b11\uff0c\u201c\u6211\u60f3\u5230\u6b7b\u5e72\u4ec0\u4e48\uff0c\u73b0\u5728\u6211\u4eec\u4e0d\u662f\u597d\u597d\u5730\u6d3b\u5728\u4e00\u8d77\u5417\uff1f\u957f\u6c5f\u7684 \u5de8\u6d6a\uff0c\u4e5f\u6ca1\u6709\u80fd\u591f\u5206\u5f00\u6211\u4eec\uff0c\u62c6\u6563\u6211\u4eec\uff0c\u5176\u4ed6\u7684\u6211\u66f4\u4e0d\u6015\u4e86\u3002\u201d \u3000\u3000\u60f3\u7740\uff0c\u60f3\u7740\uff0c\u5979\u8138\u4e0a\u9732\u51fa\u6625\u82b1\u822c\u7684\u7b11\u5bb9\uff0c\u671b\u7740\u8f9b\u6377\uff0c\u871c\u610f\u67d4\u60c5\uff0c\u96be\u63cf\u96be\u8ff0\uff0c\u6068\u4e0d\u5f97 \u5929\u5730\u957f\u4e45\uff0c\u6c38\u8fdc\u8fd9\u6837\u53ae\u5b88\u624d\u5bf9\u5fc3\u610f\uff1a\u201c\u5929\u957f\u5730\u4e45\u2026\u2026\u201d\u5979\u5e78\u798f\u5730\u547b\u541f\u7740\uff0c\u5fae\u4e00\u4fa7\u8eab\uff0c\u8ba9 \u56db\u80a2\u66f4\u8212\u670d\u5730\u5367\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u773c\u5e18\u4e00\u62ac\uff0c\u53c8\u5408\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u5979\u7684\u624b\uff0c\u5728\u4ed6\u7684\u4e0b\u989a\u4e0a\u8f6c\u52a8\u7740\uff0c\u5979\u672c\u662f\u4e2a\u73b2\u6301\u7684\u5c11\u5973\uff0c\u53ef\u662f\u521a\u4ece\u6b7b\u4ea1\u7684\u8fb9\u7f18\u56de\u5230\u4eba \u4e16\uff0c\u8fd9\u5bf9\u60a3\u96be\u4e2d\u76f8\u4f9d\u7684\u4eba\u513f\uff0c\u4e0d\u514d\u6709\u4e86\u6f8e\u6e43\u7684\u60c5\u610f\uff0c\u4f55\u51b5\u6b64\u523b\u56db\u91ce\u65e0\u4eba\uff0c\u6668\u98ce\u8f7b\u9001\uff0c\u5929 \u5730\u4e2d\u4eff\u4f5b\u53ea\u5269\u4e0b\u4ed6\u4eec\u4e24\u4eba\u4e86\u3002 \u3000\u3000\u201c\u90fd\u6e7f\u900f\u4e86\u3002\u201d\u5979\u6084\u58f0\u57cb\u6028\u7740\uff0c\u6574\u7406\u7740\u96f6\u4e71\u7684\u8863\u895f\uff0c\u773c\u5149\u52a8\u5904\uff0c\u84e6\u5730\u4e00\u58f0\u60ca\u5524\uff0c\u6307 \u5c16\u4e5f\u7acb\u65f6\u51b0\u51c9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u539f\u6765\u8f9b\u6377\u7684\u53f3\u624b\uff0c\u6b64\u523b\u5df2\u7ecf\u80bf\u5f97\u6d77\u7897\u822c\u7c97\u7ec6\uff0c\u800c\u4e14\u638c\u6307\u4e4b\u95f4\uff0c\u4e5f\u6cdb\u7740\u4e00\u79cd\u6697\u9ed1\u4e4b\u8272\uff0c \u5979\u7a81\u7136\u8bb0\u8d77\u8f9b\u6377\u6240\u4e2d\u4e4b\u6bd2\uff0c\u201c\u90a3\u662f\u65e0\u836f\u53ef\u6551\u7684\u6bd2\u5440\uff01\u9664\u4e86\u7239\u7239\u7684\u89e3\u836f\u4e4b\u5916\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e1c \u897f\u6cbb\u597d\u4ed6\u5462\uff01\u201d \u3000\u3000\u5979\u65e0\u8a00\u5730\u60b2\u54c0\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u4fa7\u4e86\u4e00\u4e0b\uff0c\u5fae\u5f31\u5730\u7741\u5f00\u773c\u6765\uff0c\u8fd9\u7531\u6df7\u6c8c\u56de\u590d\u5230\u6e05\u660e\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u89c9\u5f97\u6709\u4e00\u79cd \u8bf4\u4e0d\u51fa\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u7b2c\u4e8c\u6b21\u6709\u8fd9\u79cd\u611f\u89c9\u4e86\uff0c\u5728\u4e94\u534e\u5c71\u7684\u6885\u8c37\u91cc\uff0c\u4ed6\u66fe\u7ecf\u6709\u8fc7\u8fd9\u79cd\u559c\u60a6\u800c\u8ff7\u60d8\u7684\u611f \u89c9\u3002 \u3000\u3000\u6e10\u6e10\u5730\uff0c\u4ed6\u52a8\u8361\u7684\u795e\u7ecf\u5e73\u9759\u4e86\uff0c\u4ed6\u5f00\u59cb\u5fc6\u8d77\u6bcf\u4e00\u4ef6\u4e8b\uff0c\u56de\u5fc6\uff0c\u6c38\u8fdc\u662f\u5947\u602a\u7684\uff0c\u6709\u65f6 \u4eba\u4eec\u5728\u5341\u5e74\u4e2d\uff0c\u6240\u80fd\u56de\u5fc6\u7684\u4ec5\u662f\u4e00\u4ef6\u4e8b\uff0c\u800c\u53e6\u5916\u7684\u4e00\u4e9b\u65f6\u5019\uff0c\u5374\u4f1a\u5728\u4e00\u5239\u90a3\u95f4\u56de\u5fc6\u8d77\u4e00 \u751f\u7684\u906d\u9047\u3002 \u3000\u3000\u4ed6\u4ef0\u89c6\u7740\u82cd\u7a79\u767d\u4e91\uff0c\u601d\u6f6e\u5982\u6d8c\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u542c\u5230\u8eab\u4fa7\u6709\u555c\u6ce3\u4e4b\u58f0\uff0c\u4e00\u8f6c\u8138\uff0c\u773c\u524d\u7684\u8d6b\u7136\u7adf\u662f\u4e00\u5f20\u7f8e\u4e3d\u800c\u60b2\u6028\u7684\u9762\u5b54\uff0c \u660e\u5a9a\u53cc\u7738\u4e2d\uff0c\u6b63\u5728\u6d41\u7740\u6cea\u73e0\u3002 \u3000\u3000\u201c\u91d1\u6885\u9f84\uff01\u201d\u4ed6\u8f7b\u8f7b\u5730\u4f4e\u547c\u4e86\u4e00\u58f0\uff0c\u77ac\u5373\u4e86\u89e3\u4e86\u4e00\u5207\uff0c\u6b64\u65f6\u6b64\u5730\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u5bf9\u8fd9 \u7f8e\u4e3d\u800c\u53c8\u591a\u60c5\u7684\u5973\u5b50\uff0c\u4ed6\u4e5f\u6709\u4e00\u79cd\u96be\u8a00\u7684\u60c5\u611f\uff0c\u4f46\u662f\uff0c\u4ed6\u6240\u4e0d\u80fd\u4e86\u89e3\u7684\u662f\uff1a\u201c\u4e3a\u4ec0\u4e48\u5979 \u54ed\u4e86\u8d77\u6765\uff0c\u96be\u9053\u5979\u4ee5\u4e3a\u6211\u6b7b\u4e86\u5417\uff1f\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u6e29\u67d4\u5730\u8bf4\uff1a\u201c\u91d1\u59d1\u5a18\uff0c\u4f60\u522b\u54ed\u4e86\uff0c\u6211\u4eec\u90fd\u597d\u597d\u7684\u6d3b\u7740\u5462\u3002\u201d\u4ed6\u60f3\u62ac\u8d77\u624b\u6765\u66ff \u5979\u62ed\u53bb\u989d\u4e0a\u7684\u6cea\u73e0\uff0c\u4f46\u662f\u4ed6\u89c9\u5f97\u624b\u81c2\u7adf\u5168\u7136\u5931\u53bb\u77e5\u89c9\uff0c\u50cf\u662f\u5df2\u4e0d\u5c5e\u4e8e\u81ea\u5df1\u8eab\u4f53\u7684\u4e00\u90e8\u4efd \u4e86\u3002 \u3000\u3000\u91d1\u6885\u9f84\u62bd\u564e\u7740\u8bf4\uff1a\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u6709\u600e\u6837\uff0c\u4e0d\u662f\u2026\u2026\u201d \u3000\u3000\u84e6\u5730\uff0c\u4ed6\u4e5f\u60f3\u8d77\u65b9\u624d\u8231\u4e2d\u90a3\u4e00\u756a\u5267\u6597\uff0c\u60f3\u8d77\u638c\u4e0a\u6240\u4e2d\u7684\u6bd2\uff0c\u6323\u624e\u7740\u652f\u8d77\u8eab\u5b50\uff0c\u671d\u81ea \u5df1\u53f3\u638c\u4e00\u770b\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u770b\uff0c\u4e0d\u7981\u8eab\u4e0a\u51b7\u6c57\u6d94\u6d94\u800c\u843d\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u6211\u53ea\u624b\u638c\u63a5\u89e6\u4e86\u4e00\u4e0b\uff0c\u5374\u5df2\u4e2d\u6bd2\u5982 \u6b64\u4e4b\u6df1\uff0c\u82e5\u7136\u76ae\u7834\u8840\u6d41\uff0c\u6b64\u523b\u90a3\u91cc\u8fd8\u6709\u547d\u5728\uff0c\u8fd9\u2018\u6bd2\u541b\u2019\u4e4b\u2018\u6bd2\u2019\uff0c\u771f\u7684\u662f\u540d\u4e0d\u865a\u4f20\u3002\u201d \u3000\u3000\u4e00\u60ca\u4e4b\u4e0b\uff0c\u4ed6\u518d\u4e5f\u987e\u4e0d\u5f97\u8eab\u65c1\u555c\u6ce3\u5f97\u8d8a\u53d1\u5389\u5bb3\u7684\u91d1\u6885\u9f84\uff0c\u8bd5\u7740\u4e00\u8fd0\u6c14\uff0c\u89c9\u5f97\u771f\u6c14\u4ecd \u80fd\u8fd0\u884c\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5de6\u638c\u652f\u5730\uff0c\u76d8\u819d\u5750\u4e86\u8d77\u6765\uff0c\u4ed6\u60f3\u4ee5\u81ea\u5df1\u672c\u8eab\u7684\u529f\u529b\uff0c\u5c06\u6bd2\u6c14\u6392\u51fa\u4f53 \u5916\u3002 \u3000\u3000\u91d1\u6885\u9f84\u89c1\u4ed6\u5982\u6b64\uff0c\u5fc3\u4e2d\u66f4\u96be\u53d7\uff0c\u5979\u77e5\u9053\u4ed6\u8fd9\u4e0d\u8fc7\u662f\u591a\u6b64\u4e00\u4e3e\u800c\u5df2\uff0c\u83ab\u8bf4\u4ed6\u4e2d\u6bd2\u5982\u6b64 \u4e4b\u4e45\uff0c\u4e2d\u6bd2\u4e4b\u540e\u53c8\u66fe\u8df3\u52a8\u8fc7\uff0c\u5c31\u662f\u521a\u521a\u4e2d\u6bd2\u4e4b\u65f6\u5c31\u8fd0\u6c14\u884c\u529f\uff0c\u4e5f\u65e0\u6cd5\u5c06\u8fd9\u5929\u4e0b\u7684\u81f3\u6bd2\u6392 \u51fa\u4f53\u5916\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u4e0d\u613f\u7834\u706d\u8f9b\u6377\u8fd9\u6700\u540e\u7684\u4e00\u7ebf\u5e0c\u671b\uff0c\u5979\u60f3\uff1a\u201c\u53cd\u6b63\u4f60\u5c31\u8981\u6b7b\u4e86\uff0c\u8ba9\u4f60\u591a\u9ad8\u5174\u4e00 \u4f1a\u5427\uff0c\u5509\uff01\u4f60\u6b7b\u4e86\uff0c\u6211\u53c8\u2026\u2026\u201d\u5979\u4e0d\u6562\u518d\u5f80\u4e0b\u60f3\uff0c\u867d\u7136\u5979\u60c5\u613f\u8ddf\u7740\u8f9b\u6377\u4e00\u9f50\u6b7b\u6389\uff0c\u4f46\u5728 \u5979\u5fc3\u5e95\u6df1\u5904\u5374\u4f3c\u53e6\u6709\u4e00\u79cd\u529b\u91cf\u5728\u963b\u6b62\u7740\u5979\uff0c\u5979\u5fc3\u4e2d\u7d0a\u4e71\uff0c\u8fde\u5979\u81ea\u5df1\u4e5f\u65e0\u6cd5\u77e5\u9053\u5979\u6b64\u523b\u7684 \u60c5\u611f\uff0c\u867d\u7136\uff0c\u5979\u6df1\u7231\u7740\u8f9b\u6377\uff0c\u4f46\u5979\u77e5\u9053\u5979\u7684\u7231\u53ea\u662f\u5355\u65b9\u9762\u7684\uff0c\u56e0\u6b64\uff0c\u5979\u4f3c\u4e4e\u89c9\u5f97\u4e3a\u4ed6\u800c \u6b7b\uff0c\u5bf9\u81ea\u5df1\u662f\u4e00\u79cd\u59d4\u5c48\u3002 \u3000\u3000\u5979\u671b\u7740\u6b63\u5728\u8fd0\u6c14\u7684\u8f9b\u6377\u53cc\u7709\u6b63\u7d27\u7d27\u76b1\u7740\uff0c\u5634\u5507\u95ed\u6210\u4e00\u6761\u4e24\u7aef\u4e0b\u5782\u7684\u5f27\u7ebf\uff0c\u8138\u4e0a\u7684\u8868 \u60c5\u75db\u82e6\u5f97\u5f88\uff0c\u7edd\u4e0d\u662f\u4e00\u4e2a\u5185\u5bb6\u9ad8\u624b\u5728\u8fd0\u6c14\u884c\u529f\u65f6\u6240\u5e94\u6709\u7684\u8868\u60c5\uff0c\u5979\u77e5\u9053\u6bd2\u5df2\u5728\u4ed6\u4f53\u5185\u53d1 \u6563\u4e86\u3002 \u3000\u3000\u201c\u6700\u591a\u518d\u8fc7\u516d\u3001\u4e03\u4e2a\u65f6\u8fb0\u2026\u2026\u201d\u5979\u5583\u5583\u4f4e\u8bed\u7740\uff0c\u6cea\u73e0\u5982\u540c\u65ad\u4e86\u7ebf\u7684\u73cd\u73e0\uff0c\u4e00\u7c92\u4e00\u7c92 \u5730\u843d\u5728\u5979\u672c\u5df2\u6e7f\u900f\u7684\u8863\u88f3\u4e0a\uff0c\u773c\u770b\u7740\u81ea\u5df1\u6240\u7231\u7684\u4eba\u5c06\u8981\u6b7b\u53bb\uff0c\u8fd9\u662f\u4e00\u79cd\u591a\u4e48\u6df1\u5207\u7684\u75db\u82e6 \u5440\uff0c\u7eb5\u7136\u8fd9\u4eba\u4e0d\u7231\u81ea\u5df1\uff0c\u4f46\u8fd9\u53ea\u662f\u66f4\u52a0\u6df1\u81ea\u5df1\u7684\u75db\u82e6\u800c\u5df2\u3002 \u3000\u3000\u8f9b\u6377\u4ef0\u5929\u4e00\u9635\u957f\u53f9\uff0c\u653e\u5f03\u4e86\u8fd9\u5bf9\u81ea\u5df1\u7684\u751f\u547d\u6240\u4f5c\u7684\u6700\u540e\u7684\u52aa\u529b\uff0c\u671b\u7740\u5bf9\u9762\u7684\u6b63\u5728\u4e3a \u81ea\u5df1\u7684\u60b2\u4f24\u7684\u4eba\u513f\uff0c\u4ed6\u60c5\u611f\u7684\u590d\u6742\uff0c\u66f4\u8fdc\u80dc\u91d1\u6885\u9f84\u591a\u500d\u3002 \u3000\u3000\u6b64\u5730\u8ddd\u79bb\u6c5f\u9762\u4e0d\u8fdc\uff0c\u957f\u6c5f\u6d41\u6c34\u545c\u54bd\u4e4b\u58f0\uff0c\u9690\u9690\u53ef\u95fb\uff0c\u518d\u52a0\u4e0a\u91d1\u6885\u9f84\u7684\u555c\u6ce3\u4e4b\u58f0\uff0c\u8f9b \u6377\u5fc3\u4e71\u5982\u9ebb\u3002 \u3000\u3000\u81ea\u8d23\u3001\u81ea\u601c\u3001\u81ea\u6028\u3001\u81ea\u6068\uff0c\u8fd9\u79cd\u79cd\u60c5\u611f\uff0c\u5728\u4ed6\u5fc3\u4e2d\u4ea4\u51fb\u7740\uff0c\u5728\u4ed6\u6781\u5c0f\u7684\u65f6\u5019\uff0c\u5c31\u906d \u53d7\u5230\u90a3\u4e48\u5927\u7684\u4e0d\u5e78\uff0c\u4e94\u534e\u5c71\u6885\u8c37\u91cc\u7684\u5947\u9047\uff0c\u4f7f\u5f97\u4ed6\u53d8\u6210\u4e00\u4e2a\u4e0d\u5e73\u51e1\u7684\u4eba\uff0c\u4ed6\u6b63\u8981\u53bb\u505a\u4e00 \u4e9b\u4ed6\u4e45\u5df2\u671f\u671b\u7740\u53bb\u505a\u7684\u4e8b\u3002 \u3000\u3000\u4f46\u662f\uff0c\u73b0\u5728\u8fd9\u4e00\u5207\u90fd\u5bf9\u4ed6\u4e0d\u518d\u91cd\u8981\uff0c\u4ed6\u751a\u81f3\u5fd8\u8bb0\u4e86\u65b9\u5c11\u74a7\uff0c\u5fd8\u8bb0\u4e86\u65b9\u5c11\u74a7\u5bc2\u5bde\u7684\u60c5 \u610f\uff0c\u56e0\u4e3a\u4ed6\u81ea\u5df1\u975e\u5e38\u6e05\u695a\uff0c\u4ed6\u5df2\u6d3b\u4e0d\u4e45\u4e86\u3002 \u3000\u3000\u968f\u5373\uff0c\u4ed6\u629b\u5f00\u4e86\u8111\u6d77\u4e2d\u4e00\u5207\u7d0a\u4e71\u7684\u601d\u6f6e\u3002 \u3000\u3000\u4ed6\u6602\u9996\u5411\u5929\uff0c\u6e05\u5578\u4e86\u4e00\u58f0\uff0c\u6717\u58f0\u7b11\u9053\uff1a\u201c\u81ea\u53e4\u82f1\u96c4\uff0c\u96be\u9003\u4e00\u6b7b\uff0c\u8f9b\u6377\u5440\uff01\u8f9b\u6377\uff01\u4f60 \u53c8\u4f55\u5fc5\u592a\u96be\u53d7\u5462\uff1f\u201d \u3000\u3000\u4ed6\u4e3e\u8d77\u5de6\u624b\uff0c\u6307\u7740\u60ca\u6115\u800c\u60b2\u54c0\u7684\u91d1\u6885\u9f84\uff0c\u7b11\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u4f60\u6bd4\u6211\u66f4\u75f4\uff0c\u6b7b\uff0c\u53c8\u6709\u4ec0 \u4e48\u53ef\u6015\u7684\uff0c\u4e0d\u8fc7\u662f\u4e00\u6b21\u8f83\u957f\u7684\u7761\u7720\u7f62\u4e86\uff01\u6765\uff0c\u6765\uff0c\u7b11\u4e00\u7b11\uff0c\u80fd\u5f97\u7f8e\u4eba\u4e00\u7b11\uff0c\u6b7b\u590d\u4f55\u61be\uff1f\u201d \u3000\u3000\u8f9b\u6377\u7684\u58f0\u97f3\uff0c\u6709\u4e00\u79cd\u4ee4\u91d1\u6885\u9f84\u6218\u6817\u7684\u8bed\u8c03\uff0c\u5979\u832b\u7136\u6b62\u4f4f\u4e86\u6cea\uff0c\u671b\u7740\u5979\u9762\u524d\u7684\u4eba\uff0c\u8fd9 \u4eba\u649e\u5f00\u4e86\u5979\u5c11\u5973\u7684\u5fc3\u9761\uff0c\u7136\u800c\uff0c\u5979\u5bf9\u8fd9\u4eba\u5374\u53c8\u4e86\u89e3\u5f97\u8fd9\u4e48\u5c11\uff0c\u76f4\u5230\u73b0\u5728\uff0c\u5979\u624d\u53d1\u73b0\u4ed6\u6709 \u4e00\u79cd\u5f02\u4e8e\u5e38\u4eba\u7684\u6027\u683c\u3002 \u3000\u3000\u8f9b\u6377\u5de6\u638c\u671d\u4e0a\u4e00\u6309\uff0c\u8eab\u8eaf\u5e73\u5e73\u98de\u4e86\u8d77\u6765\uff0c\u8d34\u7740\u5730\u9762\uff0c\u6253\u4e86\u4e2a\u8f6c\uff0c\u5750\u5230\u91d1\u6885\u9f84\u7684\u8eab\u4fa7\uff0c \u4ed6\u867d\u7136\u8eab\u53d7\u5de8\u6bd2\uff0c\u4f46\u591a\u5e74\u4e0d\u65ad\u7684\u4fee\u4e3a\uff0c\u4f7f\u5f97\u4ed6\u5728\u65bd\u5c55\u8fd9\u79cd\u4e0a\u4e58\u7684\u8f7b\u529f\u65f6\uff0c\u4ecd\u4e0d\u89c9\u56f0\u96be\u3002 \u3000\u3000\u4ed6\u5ffd\u53c8\u53f9\u4e86\u53e3\u6c14\uff0c\u9053\uff1a\u201c\u4f60\u4e5f\u77e5\u9053\u6211\u53ea\u6709\u51e0\u4e2a\u65f6\u8fb0\u7684\u6d3b\u547d\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u4e0d\u8ba9\u6211\u9ad8\u5174\u9ad8 \u5174\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u671b\u7740\u4ed6\uff0c\u52c9\u5f3a\u5c06\u8138\u4e0a\u7684\u808c\u8089\u6324\u6210\u4e00\u4e2a\u7b11\u7684\u5f62\u72b6\uff0c\u4f46\u662f\u5728\u8fd9\u79cd\u60c5\u5f62\u4e4b\u4e0b\uff0c\u5979\u600e \u4e48\u7b11\u5f97\u51fa\u6765\u3002 \u3000\u3000\u59fb\u867d\u5fcd\u7740\u6cea\u73e0\uff0c\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u201d\u5979\u6697\u91cc\u4e0b\u4e86\u51b3\u5fc3\uff0c\u201c\u5728\u8fd9\u51e0\u4e2a\u65f6\u8fb0\u91cc\uff0c\u6211\u8981\u5c3d\u6211\u7684 \u6240\u80fd\uff0c\u8ba9\u4ed6\u5feb\u4e50\u3002\u201d \u3000\u3000\u201c\u7136\u540e\u5462\u2026\u2026\u201d\u5979\u505c\u987f\u4e86\u5979\u7684\u601d\u60f3\uff0c\u6e29\u67d4\u5730\u4f38\u51fa\u624b\u53bb\uff0c\u63e1\u7740\u8f9b\u6377\u7684\u624b\uff0c\u5c06\u5934\u501a\u5728\u4ed6 \u7684\u80a9\u4e0a\uff0c\u8f7b\u8f7b\u5730\u8bf4\uff1a\u201c\u968f\u4fbf\u4f60\u600e\u4e48\u8bf4\uff0c\u6211\u90fd\u542c\u4f60\u7684\u3002\u6377\u54e5\u54e5\uff0c\u6211\u6c38\u8fdc\u2026\u2026\u6c38\u8fdc\u662f\u4f60\u7684 \u4eba\u3002\u201d \u3000\u3000\u8f9b\u6377\u5e78\u798f\u5730\u7b11\u4e86\uff0c\u8fd9\u5c11\u5973\u7eaf\u771f\u7684\u60c5\u611f\uff0c\u4f7f\u4ed6\u6709\u66f4\u591a\u7684\u52c7\u6c14\u9762\u5bf9\u7740\u6b7b\u4ea1\u3002 \u3000\u3000\u540c\u65f6\uff0c\u4ed6\u4e5f\u6df1\u6df1\u5730\u4e3a\u81ea\u5df1\u80fd\u5360\u636e\u8fd9\u5c11\u5973\u7684\u5fc3\u800c\u9a84\u50b2\u7740\uff0c\u4ed6\u751a\u81f3\u89c9\u5f97\u81ea\u5df1\u7684\u751f\u547d\u867d\u7136 \u77ed\u4fc3\uff0c\u4f46\u5374\u662f\u5145\u5b9e\u7684\u3002 \u3000\u3000\u5f53\u4ed6\u77e5\u9053\u4ed6\u751f\u547d\u7684\u671f\u9650\uff0c\u51e0\u4e4e\u5df1\u6ca1\u6709\u4efb\u4f55\u5e0c\u671b\u6765\u5ef6\u957f\u7684\u65f6\u5019\uff0c\u5c31\u51b3\u5b9a\u8981\u597d\u597d\u4eab\u53d7\u8fd9 \u51e0\u4e2a\u65f6\u8fb0\uff0c\u8fd9\u5c31\u662f\u4ed6\u7684\u6027\u683c\uff0c\u6c38\u8fdc\u4e0d\u4f5c\u65e0\u76ca\u7684\u60b2\u4f24\uff0c\u6c38\u8fdc\u4e0d\u4f5c\u65e0\u6cd5\u505a\u5230\u7684\u4e8b\uff0c\u8fd9\u6027\u683c\u662f \u4e0e\u751f\u4ff1\u6765\u7684\u3002 \u3000\u3000\u867d\u7136\uff0c\u4ed6\u5bf9\u91d1\u6885\u9f84\u5e76\u6ca1\u6709\u6df1\u631a\u7684\u60c5\u611f\uff0c\u4f46\u662f\u4ed6\u5374\u5e0c\u671b\u5979\u5bf9\u81ea\u5df1\u6709\u5f3a\u70c8\u7684\u7231\uff0c\u90a3\u4e48\uff0c \u5728\u4ed6\u6b7b\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4e0d\u4f1a\u611f\u5230\u5bc2\u5bde\u4e86\u3002 \u3000\u3000\u4ed6\u7c97\u72b7\u5730\u5c06\u91d1\u6885\u9f84\u6402\u5728\u81ea\u5df1\u6000\u91cc\uff0c\u5583\u5583\u5730\u8bc9\u8bf4\u7740\uff0c\u6e29\u67d4\u7684\u8a00\u8bed\u50cf\u751c\u871c\u7684\u6708\u5149\uff0c\u4f7f\u91d1 \u6885\u9f84\u6d78\u6d74\u5728\u5feb\u4e50\u91cc\uff0c\u5979\u4ee5\u4e3a\u81ea\u5df1\u771f\u7684\u5e78\u798f\u4e86\uff0c\u56e0\u4e3a\u81f3\u5c11\uff0c\u5979\u5df2\u5f97\u5230\u4e86\u4e00\u4efd\u5979\u6240\u5180\u6c42\u7684\u7231\u3002 \u3000\u3000\u4ecd\u7136\u662f\u6e05\u6668\uff0c\u9633\u5149\u4ece\u4e1c\u65b9\u7167\u8fc7\u6765\u5e76\u4e0d\u5f3a\u70c8\uff0c\u8f9b\u6377\u611f\u5230\u8d34\u5728\u4ed6\u6000\u91cc\u7684\u662f\u4e00\u4e2a\u706b\u70ed\u7684\u80f4 \u4f53\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u8863\u88f3\u90fd\u6781\u8584\uff0c\u6e7f\u900f\u4e86\uff0c\u66f4\u662f\u7d27\u7d27\u5730\u8d34\u5728\u8eab\u4e0a\uff0c\u7b2c\u4e00\u6b21\u770b\u5230\u5c11\u5973\u8eab\u4f53\u4e0a\u7684\u7f8e\u5999 \u7684\u7ebf\u6761\u7684\u8f9b\u6377\uff0c\u5fc3\u623f\u5267\u70c8\u7684\u8df3\u52a8\u7740\uff0c\u4ece\u80a9\u5934\u671b\u4e0b\u53bb\uff0c\u5979\u7684\u80f8\u819b\u662f\u4e00\u4e2a\u5947\u5999\u7684\u9ad8\u5f27\uff0c\u7136\u540e \u6536\u675f\uff0c\u518d\u6269\u6563\uff0c\u518d\u6536\u675f\u4e8e\u4e24\u6761\u6d51\u5706\u7684\u817f\uff0c\u6536\u675f\u4e8e\u90a3\u53cc\u5947\u5999\u6d51\u5706\u7684\u811a\u8e1d\u3002 \u3000\u3000\u4e00\u5207\u90fd\u662f\u67d4\u548c\u7684\uff0c\u4f46\u67d4\u548c\u4e2d\u5374\u8574\u542b\u7740\u4e00\u79cd\u4ee4\u4eba\u5fc3\u8df3\u7684\u72c2\u70ed\uff0c\u8f9b\u6377\u6e34\u671b\u7740\u80fd\u63a5\u89e6\u5230\u8fd9 \u67d4\u548c\u7684\u66f2\u7ebf\u3002 \u3000\u3000\u8fd9\u6e34\u671b\u662f\u90a3\u4e48\u5730\u5f3a\u70c8\uff0c\u4e8e\u662f\u4ed6\u62bd\u51fa\u6402\u7740\u8170\u4e0a\u7684\u624b\uff0c\u5f53\u4ed6\u7099\u70ed\u7684\u624b\u638c\u63a5\u5230\u89e6\u5230\u5979\u65f6\uff0c \u4ed6\u4eec\u4e24\u4eba\u7684\u5fc3\u8df3\u90fd\u51e0\u4e4e\u505c\u6b62\u4e86\u3002 \u3000\u3000\u5979\u95ed\u7740\u773c\u968f\u7740\u4ed6\u7684\u629a\u6478\uff0c\u8fd9\u611f\u89c9\u5bf9\u5979\u8bf4\u6765\uff0c\u4e5f\u662f\u5947\u5f02\u800c\u964c\u751f\u7684\uff0c\u5979\u542c\u5230\u4e86\u4ed6\u7684\u547c\u5438 \u6108\u6765\u8d8a\u7c97\u91cd\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u5979\u53d1\u89c9\u4ed6\u66f4\u8fdb\u4e86\u4e00\u6b65\uff0c\u867d\u7136\u5979\u6ca1\u6709\u8fd9\u79cd\u9700\u8981\uff0c\u4f46\u662f\u5979\u613f\u610f\u987a\u4ece\u7740\u4ed6\uff0c\u613f\u610f\u505a \u4e00\u5207\u4e8b\u3002 \u3000\u3000\u826f\u4e45\uff0c\u56db\u91ce\u53c8\u6062\u590d\u4e86\u5b81\u9759\u3002 \u3000\u3000\u4e4c\u4e91\u63a9\u6765\uff0c\u7adf\u6d59\u6ca5\u7740\u98d8\u8d77\u5c0f\u96e8\u6765\uff0c\u5979\u6df1\u6df1\u5730\u4f9d\u504e\u5728\u4ed6\u7684\u80f8\u819b\u91cc\uff0c\u5979\u5df2\u5c06\u81ea\u5df1\u7684\u4e00\u5207\uff0c \u5b8c\u5168\u4ea4\u7ed9\u4ed6\u4e86\u3002 \u3000\u3000\u4ed6\u4eec\u751a\u81f3\u8fde\u907f\u96e8\u7684\u5730\u65b9\u90fd\u6ca1\u6709\uff0c\u4f46\u662f\u4ed6\u4eec\u4e5f\u6839\u672c\u6ca1\u6709\u907f\u96e8\u7684\u5ff5\u5934\u3002 \u3000\u3000\u65f6\u95f4\u4e00\u523b\u523b\u5730\u6e9c\u8d70\uff0c\u8f9b\u6377\u611f\u89c9\u5230\u4ed6\u79bb\u6b7b\u4ea1\u66f4\u8fd1\u4e86\uff0c\u65b9\u624d\uff0c\u4ed6\u867d\u7136\u5fd8\u8bb0\u4e86\u53f3\u81c2\u7684\u9ebb\u6728 \u4e0e\u75db\u82e6\uff0c\u4f46\u662f\u73b0\u5728\u4ed6\u53c8\u611f\u89c9\u5230\u4e86\uff0c\u518d\u52a0\u4e0a\u90a3\u79cd\u6ee1\u8db3\u540e\u7684\u75b2\u52b3\uff0c\u4ed6\u4f3c\u4e4e\u5df2\u55c5\u5230\u4e86\u201c\u6b7b\u201d\u7684 \u6c14\u606f\u3002 \u3000\u3000\u671b\u7740\u5026\u4f0f\u5728\u6000\u91cc\u7684\u4eba\uff0c\u4ed6\u6df1\u6df1\u5730\u6b49\u759a\u7740\uff0c\u4ed6\u6697\u9a82\u81ea\u5df1\u4e3a\u4ec0\u4e48\u8981\u5728\u4e34\u6b7b\u65f6\u5019\u3001\u5360\u636e\u4e00 \u4e2a\u5c11\u5973\u7684\u8eab\u5fc3\u3002 \u3000\u3000\u7136\u800c\uff0c\u540c\u65f6\u4ed6\u5374\u53c8\u662f\u9a84\u50b2\u3001\u6ee1\u8db3\u548c\u6109\u5feb\u7684\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u751f\u547d\u7684\u77db\u76fe\uff0c\u975e\u4f46\u4ed6\u65e0\u6cd5\u89e3\u91ca\uff0c\u53c8\u6709\u8c01\u80fd\u89e3\u91ca\u5462\uff1f \u3000\u3000\u96e8\u505c\u4e86\uff0c\u4ed6\u7a81\u7136\u611f\u89c9\u5f02\u5e38\u7684\u5bd2\u51b7\uff0c\u4ed6\u8eab\u4e0a\u7684\u98a4\u6296\uff0c\u4f7f\u5f97\u91d1\u6885\u9f84\u4e5f\u611f\u89c9\u5230\u4e86\uff0c\u62ac\u8d77\u5934 \u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u51b7\u5417\uff1f\u201d\u58f0\u97f3\u91cc\u6709\u66f4\u591a\u7684\u6e29\u67d4\uff0c\u8f9b\u6377\u70b9\u4e86\u70b9\u5934\uff0c\u4e8e\u662f\u5979\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\uff1a \u201c\u6211\u66ff\u4f60\u751f\u4e2a\u706b\u597d \u3000\u3000\u8f9b\u6377\u6f2b\u7136\u6447\u4e86\u6447\u5934\uff0c\u8bf4\uff1a\u201c\u4e0d\u7528\u4e86\uff0c\u53cd\u6b63\u6211\u2026\u2026\u201d\u4ed6\u4e0d\u5fcd\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u56e0\u4e3a\u8fd9\u5bf9\u81ea \u5df1\u548c\u5979\uff0c\u90fd\u662f\u592a\u6b8b\u9177\u4e86\uff0c\u4f46\u662f\u91d1\u6885\u9f84\u5f53\u7136\u80fd\u4e86\u89e3\u4ed6\u8bdd\u4e2d\u7684\u542b\u610f\u3002\u60b2\u54c0\uff0c\u53c8\u6df1\u6df1\u5730\u5360\u636e\u4e86 \u5979\u7684\u5fc3\u3002 \u3000\u3000\u8fd9\u7f8e\u4e3d\u7684\u5c11\u5973\u6084\u7136\u56de\u8fc7\u5934\u53bb\uff0c\u7528\u624b\u80cc\u62ed\u53bb\u8138\u4e0a\u7684\u6cea\u73e0\uff0c\u5979\u771f\u6068\u4e0d\u5f97\u653e\u58f0\u5927\u54ed\uff0c\u4f46\u662f \u5979\u5f3a\u81ea\u6b62\u7740\u81ea\u5df1\uff0c\u4e0d\u613f\u8ba9\u81ea\u5df1\u7684\u54ed\u58f0\u66f4\u4f7f\u4e34\u6b7b\u7684\u8f9b\u6377\u96be\u53d7\uff0c\u5979\u800d\u4ed6\u6b7b\u5728\u5b89\u7965\u5feb\u4e50\u91cc\uff0c\u56e0 \u4e3a\u4ed6\u4eec\u4e24\u4eba\u5df2\u6d74\u4e3a\u4e00\u4f53\u4e86\u3002 \u3000\u3000\u5728\u8fd9\u6c5f\u5cb8\u8fb9\u51e0\u4e4e\u6ca1\u6709\u53ef\u4ee5\u751f\u706b\u7684\u4e1c\u897f\uff0c\u5979\u8bb0\u8d77\u5979\u8170\u5e26\u4e0a\u7cfb\u7740\u7684\u5c0f\u8377\u5305\u91cc\u6709\u4e24\u5757\u706b\u77f3\uff0c \u90a3\u662f\u4e3a\u5979\u201c\u7239\u7239\u201d\u62bd\u70df\u888b\u65f6\u7528\u7684\uff0c\u5979\u4f38\u624b\u4e00\u6478\uff0c\u5c45\u7136\u8fd8\u5728\uff0c\u62ff\u51fa\u6765\u4e00\u770b\uff0c\u867d\u7136\u6e7f\u4e86\u5374\u8fd8 \u52c9\u5f3a\u53ef\u4ee5\u7528\u3002 \u3000\u3000\u4f46\u662f\u67f4\u5462\uff1f\u5979\u76ee\u5149\u641c\u7d22\u7740\uff0c\u6c5f\u5cb8\u8fb9\u90fd\u662f\u6ce5\u6c99\u548c\u77f3\u5757\u3002 \u3000\u3000\u7a81\u7136\uff0c\u5979\u53d1\u73b0\u521a\u521a\u6551\u8fc7\u4ed6\u4eec\u4e00\u6b21\u7684\u5e8a\u677f\uff0c\u8fd8\u653e\u5728\u6c5f\u5cb8\u4e0a\uff0c\u5979\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u4e00\u5b9a\u53ef\u751f \u706b\u7684\u201d \u3000\u3000\u4e8e\u662f\u5979\u8d70\u8fc7\u53bb\uff0c\u642c\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8f9b\u6377\u611f\u52a8\u5730\u671b\u7740\u5979\u6b65\u5c65\u8270\u96be\u5730\u4e3a\u4ed6\u505a\u8fd9\u4e9b\u4e8b\uff0c\u4f46\u662f\u6b7b\u4ea1\u7684\u9634\u5f71\uff0c\u6108\u6765\u6108\u91cd\uff0c\u4ed6\u8bf4\uff1a \u201c\u9f84\u59b9\u59b9\uff0c\u4e0d\u8981\u751f\u706b\u4e86\uff0c\u6211\u53ea\u8981\u4f60\u9760\u7740\u6211\uff0c\u6211\u2026\u2026\u6211\u5df2\u7ecf\u6ca1\u6709\u591a\u957f\u7684\u65f6\u5019\u80fd\u548c\u4f60\u5728\u4e00\u8d77 \u4e86\uff0c\u5e0c\u671b\u4f60\u4ee5\u540e\u597d\u597d\u5730\u81ea\u5df1\u4fdd\u91cd\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u5624\u549b\u4e00\u58f0\uff0c\u6251\u5230\u4ed6\u7684\u6000\u91cc\uff0c\u53cc\u80a9\u6025\u907f\u5730\u8038\u52a8\u7740\uff0c\u54ed\u5f97\u5982\u5e26\u96e8\u68a8\u82b1\uff0c\u8f9b\u6377\u4e5f\u4e0d \u89c9\u771f\u60c5\u6d41\u9732\uff0c\u773c\u4e2d\u6389\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u4e0d\u77e5\u591a\u4e45\uff0c\u8f9b\u6377\u53ea\u89c9\u6d51\u8eab\u8d8a\u6765\u8d8a\u51b7\uff0c\u624b\u81c2\u4e5f\u6108\u6765\u6108\u80bf\uff0c\u91d1\u6885\u9f84\u786c\u54bd\u7740\u722c\u4e86\u8d77\u6765\uff0c\u89e3 \u5f00\u8f9b\u6377\u7684\u4e0a\u8863\u4e00\u770b\uff0c\u90a3\u6697\u9ed1\u4e4b\u8272\u5df1\u7ecf\u6269\u5c55\u5230\u80a9\u5934\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u60e8\u7b11\u9053\uff1a\u201c\u8fd8\u6709\u591a\u4e45\uff1f\u201d\u91d1\u6885\u9f84\u4e00\u54ac\u7259\uff0c\u7a81\u5730\u5f20\u53e3\u54ac\u4f4f\u8f9b\u6377\u7684\u80a9\u5934\uff0c\u66ff\u4ed6\u542e\u7740 \u8840\uff0c\u4e00\u53e3\u4e00\u53e3\u5730\uff0c\u4f46\u662f\u6697\u9ed1\u4e4b\u8272\u4e00\u70b9\u4e5f\u6ca1\u6709\u9000\u3002 \u3000\u3000\u8f9b\u6377\u66f4\u611f\u52a8\u3002\u4e0a\u8863\u4e00\u9664\uff0c\u4ed6\u51b7\u5f97\u66f4\u5389\u5bb3\uff0c\u7259\u9f7f\u4e5f\u6253\u8d77\u98a4\u6765\uff0c\u4ed6\u77f3\u5ba4\u5341\u5e74\uff0c\u672c\u5df2\u4e0d\u907f \u5bd2\u6691\uff0c\u6b64\u523b\u6bd2\u6027\u53d1\u4f5c\uff0c\u624d\u4f1a\u8fd9\u6837\u89c9\u5f97\u5947\u5bd2\u5f7b\u9aa8\u3002 \u3000\u3000\u4ed6\u6253\u7740\u6296\u8bf4\u9053\uff1a\u201c\u9f84\u59b9\u59b9\uff0c\u4f60\u751f\u4e2a\u706b\u5427\uff01\u6211\u53d7\u4e0d\u4e86\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u70b9\u4e86\u70b9\u5934\uff0c\u65b9\u624d\u4ed6\u542e\u6bd2\u8840\uff0c\u4e00\u70b9\u6548\u679c\u4e5f\u6ca1\u6709\uff0c\u77e5\u9053\u8f9b\u6377\u7684\u547d\u6700\u591a\u53ea\u80fd\u518d\u6d3b\u4e00 \u4e24\u4e2a\u65f6\u8fb0\u4e86\u3002 \u3000\u3000\u4f46\u662f\u5979\u6b64\u523b\u5df2\u4e0b\u4e86\u51b3\u5fc3\uff0c\u53ea\u8981\u8f9b\u6377\u4e00\u6b7b\uff0c\u5979\u4e5f\u7edd\u4e0d\u518d\u6d3b\u4e0b\u53bb\uff0c\u521a\u624d\u5979\u611f\u89c9\u5230\u7684\u90a3\u79cd \u963b\u6b62\u5979\u8fd9\u6837\u505a\u7684\u529b\u91cf\uff0c\u6b64\u523b\u5df2\u6ca1\u6709\u4e86\uff0c\u56e0\u6b64\u5979\u53cd\u89c9\u6cf0\u7136\u3002 \u3000\u3000\u5979\u8d70\u8fc7\u53bb\u62ff\u8d77\u90a3\u5757\u5e8a\u677f\uff0c\u867d\u7136\u6ca1\u6709\u5200\u65a7\uff0c\u4f46\u5979\u5fc3\u601d\u4e00\u52a8\uff0c\u7acb\u638c\u4e00\u5288\uff0c\u90a3\u5e8a\u677f\u5c31\u5288\u6210 \u4e24\u534a\uff0c\u5979\u5df2\u5c06\u5176\u4e2d\u4e00\u534a\u5288\u6210\u8bb8\u591a\u5c0f\u5757\uff0c\u7528\u706b\u767e\u70b9\u8d77\u706b\u6765\uff0c\u5c06\u8f9b\u6377\u6401\u5728\u706b\u65c1\uff0c\u4e24\u4eba\u4f9d\u504e\u5730 \u5750\u7740\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u4eec\u65f6\u523b\u7684\u5b9d\u8d35\uff0c\u8fdc\u975e\u5176\u4ed6\u4efb\u4f55\u4e8b\u7269\u6240\u80fd\u6bd4\u62df\u7684\uff0c\u4f46\u662f\u4ed6\u4eec\u53cd\u800c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c \u867d\u7136\u8ddd\u6b7b\u5df2\u8fd1\uff0c\u4f46\u53ea\u89c9\u5f97\u67d4\u60c5\u871c\u610f\uff0c\u5145\u6ee1\u5fc3\u80f8\u3002 \u3000\u3000\u90a3\u5e8a\u677f\u4e43\u6a80\u6728\u6240\u5236\uff0c\u70e7\u5f97\u5f88\u5feb\uff0c\u7247\u523b\uff0c\u4fbf\u5feb\u70e7\u5149\u4e86\uff0c\u91d1\u6885\u9f84\u7ad9\u4e86\u8d77\u6765\uff0c\u53bb\u5288\u53e6\u4e00\u534a \u5e8a\u677f\u3002 \u3000\u3000\u8f9b\u6377\u9ed8\u9ed8\u5730\u8ba1\u7b97\u7740\u65f6\u95f4\uff0c\u6b64\u523b\uff0c\u90a3\u79cd\u9ebb\u75f9\u7684\u611f\u89c9\uff0c\u51e0\u5df1\u904d\u53ca\u5168\u8eab\uff0c\u201c\u5feb\u4e86\uff0c\u5feb\u4e86\uff0c\u201d \u4ed6\u4f4e\u8bed\u7740\u3002 \u3000\u3000\u53e6\u4e00\u534a\u5e8a\u677f\u53c8\u4e00\u5288\u4e3a\u4e8c\uff0c\u91d1\u6885\u9f84\u6ee1\u5fc3\u5fe7\u95f7\uff0c\u53f3\u638c\u6ee1\u84c4\u529f\u529b\uff0c\u201c\u62cd\u201d\u5730\u4e00\u638c\uff0c\u5c06\u5e8a\u677f \u62cd\u5f97\u7c89\u788e\u3002 \u3000\u3000\u7a81\u5730\uff0c\u5e8a\u677f\u7684\u8fb9\u7f18\u4e0a\uff0c\u6eda\u51fa\u51e0\u4e2a\u5c0f\u74f6\u5b50\u6765\uff0c\u91d1\u6885\u9f84\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8dd1\u8fc7\u53bb\u62ff\u8d77\u6765\u4e00\u770b\uff0c \u559c\u6781\u9ad8\u547c\uff1a\u201c\u89e3\u836f\u3002\u201d \u3000\u3000\u8f9b\u6377\u5df2\u6e10\u660f\u8ff7\uff0c\u542c\u5230\u8fd9\u4e24\u5b57\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u770b\u5230\u91d1\u6885\u9f84\u9ad8\u5174\u5f97\u53c8\u53eb\u53c8\u8df3\uff0c\u5634\u89d2\u4e5f\u6cdb\u8d77 \u4e00\u9635\u7b11\u610f\uff0c\u8ff7\u8ff7\u7cca\u7cca\u5730\u6655\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7b49\u5230\u4ed6\u518d\u9192\u6765\u7684\u65f6\u5019\uff0c\u5929\u5df2\u7ecf\u9ed1\u4e86\u3002 \u3000\u3000\u91d1\u6885\u9f84\u7126\u6025\u5730\u5b88\u5019\u5728\u4ed6\u65c1\u8fb9\uff0c\u770b\u5230\u4ed6\u7741\u5f00\u773c\u6765\uff0c\u559c\u9053\uff1a\u201c\u6377\u54e5\u54e5\uff0c\u4e0d\u8981\u52a8\uff0c\u4f60\u5df1\u7ecf \u6ca1\u4e8b\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u5e8a\u677f\u6b63\u662f\u201c\u6bd2\u541b\u91d1\u4e00\u9e4f\u201d\u653e\u7f6e\u89e3\u836f\u7684\u6240\u5728\uff0c\u91d1\u6885\u9f84\u4ea6\u77e5\u9053\u89e3\u836f\u7684\u7528\u6cd5\uff0c\u8f9b\u6377 \u53c8\u4e00\u6b21\u9760\u7740\u8fd9\u5757\u5e8a\u677f\uff0c\u6b7b\u91cc\u9003\u751f\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u6bd2\u836f\u867d\u6781\u9738\u9053\uff0c\u4f46\u89e3\u836f\u4e5f\u6781\u5947\u5999\uff0c\u8f9b\u6377\u6b64\u523b\u867d\u89c9\u8eab\u529b\u4ff1\u5026\uff0c\u4f46\u5df2\u6ca1\u6709\u90a3\u79cd\u9ebb \u75f9\u7684\u611f\u89c9\u3002 \u3000\u3000\u91d1\u6885\u9f84\u4e00\u770b\u4ed6\u9192\u6765\uff0c\u9ad8\u5174\u5f97\u53c8\u54ed\u53c8\u7b11\uff0c\u5979\u5185\u529f\u5df2\u6709\u6839\u57fa\uff0c\u5fd9\u4ee5\u672c\u8eab\u7684\u529f\u529b\uff0c\u66ff\u8f9b\u6377 \u63a8\u62ff\u4e86\u4e00\u4f1a\uff0c\u4f46\u5979\u81ea\u5df1\u4ea6\u662f\u53c8\u7d2f\u53c8\u997f\uff0c\u4ece\u6e05\u6668\u5230\u6b64\u523b\uff0c\u5979\u4e00\u76f4\u5b88\u5019\u5728\u8f9b\u6377\u8eab\u65c1\uff0c\u672a\u996e\u672a \u98df\uff0c\u6b64\u523b\u7cbe\u795e\u4e00\u677e\u61c8\uff0c\u9760\u5728\u8f9b\u6377\u65c1\u8fb9\uff0c\u4e0d\u89c9\u6c89\u6c89\u7761\u53bb\u4e86\u3002\u8f9b\u6377\u4e5f\u77e5\u9053\u81ea\u5df1\u751f\u547d\u65e0\u788d\uff0c\u4ed6 \u5bf9\u91d1\u6885\u9f84\u7684\u611f\u6fc0\u548c\u7231\uff0c\u4ea6\u662f\u523b\u9aa8\u94ed\u5fc3\uff0c\u5446\u5446\u5730\u671b\u7740\u5979\uff0c\u957f\u957f\u7684\u776b\u6bdb\u8986\u76d6\u5728\u773c\u5e18\u4e0a\uff0c\u81ea\u5df1 \u4e5f\u4e0d\u89c9\u53c8\u7761\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u89c9\uff0c\u76f4\u53c8\u7761\u4e86\u4e00\u591c\uff0c\u91d1\u6885\u9f84\u7741\u5f00\u773c\u775b\uff0c\u770b\u5230\u8f9b\u6377\u5df2\u9192\u4e86\uff0c\u6b63\u75f4\u75f4\u5730\u671b\u7740\u81ea\u5df1\uff0c \u5a07\u7b11\u9053\uff1a\u201c\u4f60\u770b\uff0c\u6211\u7761\u5f97\u597d\u6c89\u5440\u3002\u201d \u3000\u3000\u8f9b\u6377\u51d1\u8fc7\u5934\u6765\uff0c\u5728\u5979\u7684\u989d\u4e0a\u4eb2\u4e86\u4eb2\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u7761\u5f97\u8fd9\u4e48\u6c89\uff0c\u6709\u4eba\u628a\u4f60\u62d0\u8d70\uff0c\u4f60\u90fd \u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u7b11\u9053\uff1a\u201c\u4f60\u574f\u6b7b\u4e86\u3002\u201d\u60f3\u5230\u6628\u65e5\u7684\u90a3\u4e00\u756a\u60c5\u666f\uff0c\u7ea2\u751f\u53cc\u988a\uff0c\u7f9e\u5f97\u6ee1\u9762\u50cf\u662f\u6735 \u6843\u82b1\u4f3c\u7684\uff0c\u8f9b\u6377\u60c5\u4e0d\u81ea\u7981\uff0c\u53c8\u5728\u5979\u9f3b\u5b50\u4e0a\u4eb2\u4e86\u4eb2\uff0c\u5979\u5a07\u7b11\u7740\u722c\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5582\uff01\u4f60\u4e5f \u8be5\u8d77\u6765\u5566\u3002\u201d \u3000\u3000\u5ffd\u5730\uff0c\u5979\u53c8\u5f2f\u4e0b\u8eab\u53bb\uff0c\u770b\u5230\u8f9b\u6377\u81c2\u4e0a\u7684\u9ed1\u8272\u5df2\u5168\u9000\u5c3d\u4e86\uff0c\u5a07\u7b11\u9053\uff1a\u201d \u3000\u3000\u201c\u6377\u54e5\u54e5\uff0c\u4f60\u8bd5\u8bd5\u770b\u7ad9\u4e0d\u7ad9\u5f97\u8d77\u6765\uff0c\u6211\u4eec\u603b\u4e0d\u80fd\u518d\u7559\u5728\u8fd9\u9b3c\u5730\u65b9\u5440\uff0c\u800c\u4e14\uff0c\u6211\u809a\u5b50 \u5df2\u997f\u5f97\u5471\u5471\u53eb\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u7740\u70b9\u4e86\u70b9\u5934\uff0c\u5fae\u4e00\u7528\u529b\uff0c\u4fbf\u7ad9\u4e86\u8d77\u6765\uff0c\u7adf\u5df2\u75ca\u6108\u4e86\u3002 \u3000\u3000\u4ed6\u7b11\u9053\uff1a\u201c\u4f60\u7239\u7239\u7684\u89e3\u836f\u771f\u597d\u3002\u201d \u3000\u3000\u201c\u6bd2\u836f\u4e5f\u4e0d\u9519\u3002\u201d\u4ed6\u7b11\u7740\u53c8\u8865\u4e86\u53e5\u3002 \u3000\u3000\u91d1\u6885\u9f84\u8138\u4e00\u7ea2\uff0c\u561f\u8d77\u4e86\u5634\uff0c\u80cc\u8fc7\u8eab\u5b50\u53bb\uff0c\u5ffd\u7136\u770b\u5230\u8fdc\u8fdc\u50cf\u662f\u6709\u4e00\u672c\u4e66\uff0c\u5fae\u4e00\u7eb5\u8eab\uff0c \u63a0\u8fc7\u53bb\u6361\u4e86\u56de\u6765\uff0c\u8f9b\u6377\u51d1\u4e0a\u53bb\u4e00\u770b\uff0c\u90a3\u662f\u672c\u9ec4\u7ede\u8ba2\u6210\u7684\u518c\u5b50\uff0c\u5c01\u9762\u4e0a\u662f\u4e24\u4e2a\u7bc6\u4e66\u201c\u6bd2\u7b08\u201d \u4e24\u5b57\u3002 \u3000\u3000\u4e24\u4eba\u8fb9\u8d70\u8fb9\u770b\uff0c\u7b80\u76f4\u5fd8\u8bb0\u4e86\u808c\u997f\uff0c\u53ea\u56e0\u90a3\u4e0a\u9762\u8bb0\u8f7d\u7740\u7684\u90fd\u662f\u5929\u4e0b\u6bd2\u7269\u7684\u6027\u80fd\uff0c\u548c\u5404 \u79cd\u6bd2\u836f\u7684\u914d\u5236\u65b9\u6cd5\uff0c\u8f9b\u6377\u89c1\u6240\u672a\u89c1\uff0c\u95fb\u6240\u672a\u95fb\uff0c\u53ea\u89c1\u4e0a\u9762\u6709\u4e9b\u6bd2\u836f\uff0c\u7b80\u76f4\u6bd2\u5230\u4e0d\u53ef\u601d\u8bae\uff0c \u4e0d\u7981\u94a6\u4f69\u5730\u671d\u91d1\u6885\u9f84\u8bf4\u9053\uff1a\u201c\u9f84\u59b9\u59b9\uff0c\u8bf4\u826f\u5fc3\u8bdd\uff0c\u4f60\u7239\u7239\u771f\u662f\u4f4d\u5947\u4eba\uff0c\u5929\u4e0b\u6240\u6709\u7684\u6bd2\u7269\uff0c \u4ed6\u90fd\u5f04\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4e0d\u8bf4\u522b\u7684\uff0c\u5355\u662f\u7edd\u5bf9\u65e0\u8272\u65e0\u5473\u7684\u6bd2\u836f\uff0c\u5c31\u6709\u597d\u51e0\u79cd\uff0c\u771f\u4e0d\u6653\u5f97\u4ed6\u662f \u600e\u4e48\u5236\u6210\u7684\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u5e7d\u5e7d\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u4e00\u8f88\u5b50\u90fd\u5728\u6bd2\u836f\u91cc\u6253\u6eda\uff0c\u73b0\u5728\u8fde\u4ed6\u8001\u4eba \u5bb6\u81ea\u5df1\u90fd\u88ab\u6bd2\u836f\u5bb3\u4e86\uff0c\u6709\u65f6\u4eba\u4f1a\u53d8\u5f97\u75af\u75af\u98a0\u98a0\u7684\uff0c\u6709\u65f6\u5374\u53c8\u597d\u597d\u7684\uff0c\u73b0\u5728\u4ed6\u8001\u4eba\u5bb6\u4e0d\u77e5 \u53c8\u8dd1\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fd9\u529d\u6170\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u6b66\u529f\u8d85\u51e1\u4eba\u5723\uff0c\u8fd8\u4f1a\u6709\u4ec0\u4e48\u610f\u5916\u5417\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u4e00\u53ea\u624b\u6302\u7740\u8f9b\u6377\u7684\u80f3\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5f97\u8d76\u5feb\u627e\u4e2a\u6709\u4eba\u5bb6\u7684\u5730\u65b9\uff0c\u73b0\u5728\u6211\u4eec \u5012\u5e95\u662f\u5728\u54ea\u91cc\u90fd\u4e0d\u77e5\u9053\uff0c\u4f60\u770b\uff0c\u6211\u8eab\u4e0a\u53c8\u810f\u53c8\u81ed\uff0c\u90a3\u957f\u6c5f\u91cc\u9762\u7684\u6c34\u5440\uff0c\u6211\u770b\u4ec0\u4e48\u4e1c\u897f\u90fd \u6709\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u4e86\u7b11\uff0c\u8eab\u5f62\u52a8\u5904\uff0c\u65bd\u5c55\u5f00\u8eab\u6cd5\uff0c\u901f\u5ea6\u7acb\u523b\u589e\u52a0\u4e86\u597d\u591a\u500d\uff0c\u867d\u7136\u4ed6\u4e2d\u6bd2\u521d\u6108\uff0c \u4f53\u529b\u7a0d\u5f31\uff0c\u4f46\u6302\u4ed6\u81c2\u4e0a\u7684\u91d1\u6885\u9f84\uff0c\u5df2\u5728\u6697\u8d5e\u4ed6\u8f7b\u8eab\u529f\u592b\u7684\u4f73\u5999\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u7684\u529f\u592b\u5012\u5e95 \u54ea\u91cc\u5b66\u7684\u5440\uff1f\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u6211\u6162\u6162\u518d\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u4fe9\u542c\u5230\u4e00\u4e2a\u5973\u5b50\u7684\u60ca\u547c\u4e4b\u58f0\uff0c\u4e24\u4eba\u811a\u6b65\u4e00\u987f\uff0c\u4e0d\u7ea6\u800c\u540c\u5730\u671d\u90a3\u4e2a\u65b9\u5411\u6251\u53bb\uff0c \u8fd9\u4e00\u4e0b\uff0c\u8f9b\u6377\u811a\u4e0b\u901f\u5ea6\u66f4\u5feb\uff0c\u8f6c\u773c\u4fbf\u770b\u5230\u6709\u4e24\u4e2a\u4eba\u5f62\u5728\u6eda\u52a8\u7740\uff0c\u5973\u5b50\u7684\u60ca\u547c\u58f0\u60f3\u5fc5\u662f\u5176 \u4e2d\u4e00\u4eba\u53d1\u51fa\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5148\u53bb\u770b\u770b\u3002\u201d\u6446\u5f00\u91d1\u6885\u9f84\u7684\u624b\uff0c\u4e00\u957f\u8eab\uff0c\u8eab\u5982\u98de\u71d5\uff0c\u4e09\u4e24 \u4e2a\u7eb5\u8eab\uff0c\u5df2\u7a9c\u4e86\u4e0a\u53bb\uff0c\u76ee\u5149\u95ea\u5904\uff0c\u6012\u559d\u9053\uff1a\u201c\u662f\u4f60\uff01\u201d \u3000\u3000\u6eda\u52a8\u7740\u7684\u4e24\u4eba\uff0c\u4e00\u542c\u4eba\u58f0\uff0c\u505c\u4e86\u4e0b\u6765\uff0c\u5374\u6b63\u662f\u5929\u9b54\u91d1\u6b39\u4e0e\u65b9\u5c11\u74a7\u4e24\u4eba\u3002 \u3000\u3000\u539f\u6765\u5929\u9b54\u91d1\u6b4c\u7565\u77e5\u6c34\u6027\uff0c\u8239\u6c89\u65f6\u7d27\u7d27\u62b1\u7740\u65b9\u5c11\u74a7\uff0c\u987a\u7740\u6c5f\u6c34\u98d8\u6d41\u4e86\u4e00\u9635\uff0c\u4e5f\u6293\u5230\u4e00 \u5757\u6728\u677f\uff0c\u98d8\u5230\u5cb8\u4e0a\u3002 \u3000\u3000\u90a3\u65f6\u4ed6\u4eec\u4e8c\u4eba\uff0c\u4e5f\u81ea\u5931\u53bb\u77e5\u89c9\uff0c\u7b49\u65b9\u5c11\u74a7\u82cf\u9192\u7684\u65f6\u5019\uff0c\u53d1\u89c9\u6709\u4e00\u5f20\u5634\u5728\u81ea\u5df1\u8138\u4e0a\u4e71 \u543b\uff0c\u5413\u5f97\u5927\u53eb\u4e86\u4e00\u58f0\uff0c\u7741\u773c\u4e00\u770b\uff0c\u91d1\u6b39\u4e18\u722c\u5728\u8eab\u4e0a\u4eb2\u81ea\u5df1\u7684\u9762\u5b54\uff0c\u53c8\u6025\u53c8\u6c14\uff0c\u731b\u5730\u5c06\u4ed6 \u4e00\u63a8\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u5168\u8eab\u6b66\u529f\uff0c\u6bd4\u5979\u6b66\u529f\u518d\u5f3a\u5341\u500d\u7684\u4eba\uff0c\u4e5f\u63a8\u4ed6\u4e0d\u5f00\u3002 \u3000\u3000\u4f46\u4ed6\u6b64\u65f6\u6b63\u6655\u6655\u7cca\u7cca\uff0c\u5168\u8eab\u6ca1\u6709\u529b\u6c14\uff0c\u88ab\u65b9\u5c11\u74a7\u4e00\u63a8\uff0c\u7adf\u5012\u5728\u5730\u4e0a\u3002\u65b9\u5c11\u74a7\u4e24\u624b\u6491 \u5730\uff0c\u5750\u4e86\u8d77\u6765\uff0c\u6478\u5230\u5730\u4e0a\u4e00\u5757\u5c16\u77f3\u5757\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u662f\u518d\u8fc7\u6765\uff0c\u6211\u5c31\u62ff\u8fd9\u4e1c\u897f\u5212\u7834\u6211\u7684 \u8138\u3002\u201d \u3000\u3000\u5929\u9b54\u91d1\u6b39\u7231\u6781\u4e86\u5979\uff0c\u95fb\u8a00\u679c\u7136\u4e0d\u6562\u8fc7\u53bb\uff0c\u4f46\u65b9\u5c11\u74a7\u770b\u7740\u56db\u5468\u7a7a\u8361\u8361\u7684\uff0c\u6beb\u65e0\u4eba\u8ff9\uff0c \u5413\u5f97\u52a8\u4e5f\u4e0d\u6562\u52a8\u3002 \u3000\u3000\u4e24\u4eba\u5c31\u8fd9\u6837\uff0c\u5c45\u7136\u8017\u4e86\u4e00\u665a\uff0c\u5230\u540e\u6765\u65b9\u5c11\u74a7\u53c8\u75b2\u53c8\u997f\uff0c\u5b9e\u5728\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u7a0d\u4e3a\u6253\u4e86 \u4e2a\u76f9\u3002 \u3000\u3000\u90a3\u77e5\u5929\u9b54\u91d1\u6b39\u5374\u4e58\u673a\u6251\u4e86\u4e0a\u53bb\uff0c\u5148\u4e00\u628a\u62b1\u4f4f\u5979\uff0c\u62a2\u53bb\u5979\u624b\u4e0a\u7684\u77f3\u5757\uff0c\u4e00\u5f20\u5634\u51d1\u4e86\u4e0a \u53bb\uff0c\u53e6\u4e00\u53ea\u624b\u4e5f\u5728\u4e71\u52a8\u3002 \u3000\u3000\u65b9\u5c11\u74a7\u5413\u5f97\u5927\u53eb\uff0c\u4e00\u9762\u62fc\u547d\u7684\u6323\u624e\u3002 \u3000\u3000\u4e24\u4eba\u7ffb\u7ffb\u6eda\u6eda\uff0c\u5929\u9b54\u91d1\u6b39\u60f3\u4e58\u5371\u7d22\u7231\uff0c\u9020\u6210\u4e8b\u5b9e\uff0c\u5374\u4e0d\u77e5\u521a\u597d\u88ab\u8f9b\u6377\u542c\u5230\u53eb\u58f0\uff0c\u8d70 \u6765\u649e\u4e0a\u3002 \u3000\u3000\u65b9\u5c11\u74a7\u773c\u770b\u5230\u8f9b\u6377\uff0c\u559c\u6781\u547c\u9053\uff1a\u201c\u6377\u54e5\u54e5\u3002\u201d \u3000\u3000\u8fde\u722c\u5e26\u8d70\uff0c\u98de\u5954\u8fc7\u6765\uff0c\u4e00\u8fb9\u9ad8\u547c\u9053\uff1a\u201c\u6377\u54e5\u54e5\uff0c\u5feb\u6765\u6551\u6211\uff0c\u4ed6\u8981\u2026\u2026\u4ed6\u8981\u6b3a\u8d1f\u6211\u3002\u201d \u3000\u3000\u5929\u9b54\u91d1\u6b39\uff0c\u4e00\u89c1\u8f9b\u6377\uff0c\u773c\u91cc\u50cf\u662f\u8981\u55b7\u51fa\u706b\u6765\uff0c\u5ffd\u7136\u53c8\u770b\u5230\u91d1\u6885\u9f84\u8ddf\u5728\u4ed6\u8eab\u540e\uff0c\u559d\u9053\uff1a \u201c\u5e08\u59b9\uff0c\u5feb\u8fc7\u6765\uff0c\u548c\u6211\u628a\u8fd9\u5c0f\u5b50\u5bb0\u4e86\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u770b\u5230\u91d1\u6b39\u548c\u65b9\u5c11\u5784\uff0c\u4e5f\u662f\u60ca\u5947\u4e07\u5206\uff0c\u542c\u5230\u91d1\u6b39\u8981\u81ea\u5df1\u5e2e\u7740\u5bb0\u8f9b\u6377\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c \u8d70\u5230\u8f9b\u6377\u8eab\u65c1\uff0c\u7d27\u7d27\u5730\u9760\u7740\u4ed6\u3002 \u3000\u3000\u6b64\u65f6\u65b9\u5c11\u74a7\u4e5f\u5954\u8dd1\u4e86\u6765\uff0c\u770b\u5230\u8fd9\u60c5\u5f62\u5fae\u5fae\u4e00\u695e\uff0c\u4f46\u662f\u4ecd\u7136\u6251\u5230\u8f9b\u6377\u8eab\u4e0a\u6765\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u4e00\u58f0\u6012\u543c\uff0c\u8dd1\u4e86\u4e0a\u6765\uff0c\u4e00\u628a\u6293\u4f4f\u65b9\u5c11\u74a7\u7684\u540e\u5fc3\uff0c\u8f9b\u6377\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u653e \u5f00\uff01\u201d\u811a\u6b65\u4e00\u9519\uff0c\u659c\u5288\u4e00\u638c\uff0c\u638c\u98ce\u55d6\u7136\u3002 \u3000\u3000\u5929\u9b54\u770b\u89c1\u8f9b\u6377\u638c\u98ce\u5f3a\u52b2\uff0c\u800c\u4e14\u624b\u638c\u7684\u989c\u8272\u65e0\u5f02\uff0c\u5fc3\u4e2d\u5947\u602a\uff0c\u5ffd\u5730\u53c8\u770b\u89c1\u91d1\u6885\u9f84\u624b\u4e0a \u62ff\u7740\u7684\u9ec4\u7eeb\u518c\u5b50\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u7adf\u7136\u628a\u6211\u7684\u5e08\u59b9\u52fe\u5f15\u53bb\u4e86\uff0c\u201d\u76ee\u5149\u53c8\u76ef \u4f4f\u91d1\u6885\u9f84\u9053\uff1a\u201c\u4f60\u600e\u4e48\u628a\u5e08\u7236\u7684\u79d8\u7b08\u7ed9\u5077\u51fa\u6765\u4e86\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u9053\uff1a\u201c\u4f60\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u4fa7\u76ee\u770b\u89c1\u65b9\u5c11\u5851\u4ecd\u6302\u5728\u8f9b\u6377\u7684\u8116\u5b50\u4e0a\uff0c\u7eb5\u8eab\u8fc7\u53bb\uff0c\u62cd\u4e86\u62cd\u5979\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5012 \u4e0b\u6765\u5440\u3002\u201d \u3000\u3000\u54ea\u77e5\u9053\u65b9\u5c11\u74a7\u62b1\u5f97\u66f4\u7d27\uff0c\u4e5f\u8bf4\u9053\uff1a\u201c\u4f60\u7ba1\u4e0d\u7740\u3002\u201d \u3000\u3000\u8f9b\u6377\u6697\u6697\u53eb\u82e6\uff0c\u4ed6\u52bf\u4e0d\u80fd\u5c06\u65b9\u5c11\u74a7\u4e22\u4e0b\uff0c\u4f46\u671b\u7740\u6ee1\u9762\u5a07\u55d4\u7684\u91d1\u6885\u9f84\uff0c\u53c8\u4e0d\u80fd\u4efb\u51ed\u65b9 \u5c11\u74a7\u62b1\u7740\u81ea\u5df1\uff0c\u4ed6\u5de6\u53f3\u4e3a\u96be\uff0c\u518d\u52a0\u4e0a\u8fd8\u8981\u5e94\u4ed8\u5f3a\u654c\u5929\u9b54\u91d1\u6b39\uff0c\u4e00\u65f6\u6115\u5728\u90a3\u91cc\uff0c\u4e0d\u77e5\u5982\u4f55 \u662f\u597d\u3002 \u3000\u3000\u91d1\u6885\u9f84\u4e5f\u662f\u53c8\u6c14\u53c8\u5992\uff0c\u5979\u5012\u5e95\u9762\u5ae9\uff0c\u4e0d\u597d\u610f\u601d\u53bb\u62c9\u65b9\u5c11\u74a7\uff0c\u73b0\u5728\u53cd\u5012\u5e0c\u671b\u91d1\u6b39\u80fd\u5c06 \u65b9\u5c11\u671b\u62a2\u53bb\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u548c\u8f9b\u6377\u5728\u8231\u4e2d\u65e9\u4ea4\u8fc7\u4e86\u624b\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u6b66\u529f\u6bd4\u8d77\u8f9b\u6377\u8fd8\u7565\u5dee\u4e00\u7b79\uff0c\u4ed6\u4e3a\u4eba \u9634\u9669\uff0c\u8179\u4e2d\u6697\u6697\u76d8\u7b97\uff0c\u8be5\u600e\u4e48\u6837\u6765\u5e94\u4ed8\u3002 \u3000\u3000\u56db\u4eba\u5173\u7cfb\u590d\u6742\uff0c\u5404\u6709\u5fc3\u4e8b\uff0c\u7adf\u90fd\u6115\u4f4f\u4e86\u3002 \u3000\u3000\u5ffd\u5730\u91d1\u6885\u9f84\u8179\u4e2d\u201c\u5495\u201d\u5730\u4e00\u58f0\u54cd\uff0c\u539f\u6765\u5979\u5df2\u997f\u6781\u4e86\uff0c\u65b9\u5c11\u74a7\u5657\u54e7\u7b11\u4e86\u51fa\u6765\uff0c\u91d1\u6885\u9f84 \u559d\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff0c\u597d\u4e0d\u8981\u8138\uff0c\u6211\u4ece\u6765\u4e5f\u6ca1\u6709\u770b\u8fc7\u6bd4\u4f60\u518d\u4e0d\u8981\u8138\u7684\u4eba\uff0c\u7d27\u7d27\u62b1\u7740\u4eba\u5bb6\u505a \u4ec0\u4e48\uff1f\u201d \u3000\u3000\u65b9\u5c11\u74a7\u53cd\u5507\u9053\uff1a\u201c\u4f60\u624d\u4e0d\u8981\u8138\u5462\uff0c\u6211\u559c\u6b22\u62b1\u6377\u54e5\u54e5\uff0c\u6377\u54e5\u54e5\u559c\u6b22\u6211\u62b1\uff0c\u4f60\u51ed\u4ec0\u4e48\u8d44 \u683c\u7ba1\uff1f\u5662\uff01\u6377\u54e5\u54e5\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6377\u66f4\u662f\u53eb\u82e6\uff0c\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u5929\u9b54\u91d1\u6b39\u8fde\u58f0\u51b7\u7b11\uff0c\u91d1\u6885\u9f84\u4e5f\u6c14\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u7a81\u7136\u8bf4 \u9053\uff1a\u201c\u6211\u662f\u6377\u54e5\u54e5\u7684\u59bb\u5b50\uff0c\u5f53\u7136\u53ef\u4ee5\u7ba1\u3002\u201d \u3000\u3000\u65b9\u5c11\u74a7\u53cc\u624b\u4e00\u677e\uff0c\u62cd\u624b\u7b11\u9053\uff1a\u201c\u5440\uff0c\u8fd9\u4e2a\u4eba\u597d\u4e0d\u8981\u8138\uff0c\u786c\u8bf4\u662f\u4eba\u5bb6\u7684\u8001\u5a46\uff0c\u7f9e\u4e0d\u7f9e\uff0c \u7f9e\u4e0d\u7f9e\u3002\u201d \u3000\u3000\u5929\u9b54\u91d1\u6b39\u5927\u4e3a\u5947\u602a\uff0c\u4ed6\u7d20\u77e5\u9053\u8fd9\u4f4d\u5e08\u59b9\u867d\u7136\u8273\u5982\u6843\u674e\uff0c\u4f46\u5374\u51b7\u82e5\u51b0\u971c\uff0c\u5e73\u5e38\u7537\u5b50\u591a \u770b\u5979\u4e00\u770b\u90fd\u8981\u5012\u9709\uff0c\u4eca\u65e5\u600e\u5730\u6539\u4e86\u5e38\u6001\uff0c\u5f53\u7740\u4eba\u9762\uff0c\u8bf4\u662f\u4eba\u5bb6\u7684\u8001\u5a46\uff0c\u4e0d\u7981\u559d\u9053\uff1a\u201c\u5e08 \u59b9\uff0c\u4f60\u600e\u4e48\u56de\u4e8b\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u53c8\u7f9e\u53c8\u6025\u53c8\u6c14\uff0c\u773c\u6cea\u53c8\u4e00\u7c92\u7c92\u5f80\u4e0b\u6389\uff0c\u8f9b\u6377\u89c1\u4e86\uff0c\u60f3\u8d77\u5979\u5bf9\u81ea\u5df1\u7684\u4e00\u5207\uff0c\u518d \u60f3\u8d77\u5979\u987a\u4ece\u5730\u5fcd\u53d7\u7740\u81ea\u5df1\u75af\u72c2\u65f6\u7684\u59a9\u5a9a\uff0c\u4e0d\u7981\u5fc3\u4e2d\u5927\u4e3a\u4e0d\u5fcd\uff0c\u201c\u55d6\u201d\u5730\u8eab\u5f62\u4e00\u63a0\uff0c\u4e00\u628a \u5c06\u91d1\u6885\u9f84\u62c9\u5728\u8eab\u65c1\uff0c\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u5979\u662f\u6211\u7684\u592a\u592a\u3002\u201d \u3000\u3000\u5929\u9b54\u91d1\u6b39\u66f4\u5947\uff0c\u90a3\u8fb9\u65b9\u5c11\u74a7\u5374\u54c7\u5730\u4e00\u58f0\uff0c\u5750\u5012\u5728\u5730\u4e0a\u54ed\u4e86\u8d77\u6765\uff0c\u5929\u9b54\u91d1\u6b39\u6697\u5fd6\uff1a \u201c\u8fd9\u662f\u6211\u7684\u673a\u4f1a\u6765\u3002\u201d\u8d70\u4e86\u8fc7\u53bb\uff0c\u62cd\u7740\u65b9\u5c11\u74a7\u7684\u80a9\u5934\u9053\uff1a\u201c\u4e0d\u8981\u54ed\uff0c\u4e0d\u8981\u54ed\u3002\u201d\u65b9\u5c11\u671b \u770b\u89c1\u8f9b\u6377\u5c45\u7136\u627f\u8ba4\u53e6\u4e00\u5973\u4eba\u662f\u4ed6\u592a\u592a\uff0c\u60f3\u8d77\u81ea\u5df1\u5bf9\u4ed6\u7684\u60c5\u610f\uff0c\u8d8a\u60f3\u8d8a\u89c9\u5f97\u59d4\u5c48\uff0c\u54ed\u5f97\u51c4 \u60e8\u5df2\u6781\u3002\u542c\u5f97\u6709\u4eba\u529d\u5979\uff0c\u5979\u4e5f\u4e0d\u7ba1\u90a3\u4eba\u662f\u8c01\uff0c\u4fbf\u5012\u5230\u90a3\u4eba\u7684\u6000\u65f6 \u3000\u3000\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b4c\u6697\u6697\u5730\u5f97\u610f\uff0c\u95e8\u4e2d\u5374\u9a82\u9053\uff1a\u201c\u8fd9\u79cd\u865a\u60c5\u5047\u610f\u7684\u4eba\uff0c\u4f60\u7406\u4ed6\u5e72\u4ec0\u4e48\uff0c\u8d70\uff0c\u6211 \u4eec\u5230\u522b\u5904\u53bb\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u4e5f\u5f88\u96be\u8fc7\uff0c\u4ed6\u5e76\u975e\u4e0d\u7231\u65b9\u5c11\u5851\uff0c\u4f46\u53c8\u4e0d\u80fd\u4e0d\u5982\u6b64\u505a\u3002 \u3000\u3000\u90a3\u77e5\u65b9\u5c11\u74a7\u7a81\u5730\u8df3\u4e86\u8d77\u6765\uff0c\u5f80\u6c5f\u8fb9\u8dd1\u53bb\uff0c\u539f\u6765\u6b64\u5730\u4ea6\u79bb\u6c34\u9762\u5f88\u8fd1\uff0c\u8f9b\u6377\u5927\u60ca\uff0c\u5fd6\u9053\uff1a \u201c\u83ab\u975e\u5979\u8981\u81ea\u6740\u3002\u201d\u6765\u4e0d\u53ca\u518d\u60f3\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u8d76\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u6b66\u529f\u9ad8\u51fa\u65b9\u5c11\u74a7\u4e0d\u77e5\u591a\u5c11\u500d\uff0c\u773c\u770b\u8ffd\u4e0a\uff0c\u8eab\u540e\u7a81\u7136\u6709\u4e00\u9053\u52b2\u98ce\u88ad\u6765\uff0c\u4ed6\u53cd\u624b\u60f3\u6284\uff0c \u7a81\u5730\u60f3\u8d77\u6240\u4e2d\u4e4b\u6bd2\uff0c\u8eab\u8eaf\u4e00\u626d\uff0c\u4e00\u5757\u77f3\u5b50\u81ea\u8eab\u4fa7\u98de\u8fc7\uff0c\u63a5\u7740\u5929\u9b54\u91d1\u6b39\u5df2\u6012\u559d\u7740\u8d76\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8f9b\u6377\u53cc\u638c\u4e00\u9519\u3001\u5341\u6307\u5168\u5f20\u3001\u5206\u70b9\u91d1\u6b39\u201c\u6c89\u9999\u201d\u201c\u7384\u73e0\u201d\u201c\u5b9a\u7389\u201d\u201c\u7384\u5173\u201d\u201c\u5c06\u53f0\u201d \u201c\u80a9\u7a74\u201d\u516d\u5904\u8981\u7a74\uff0c\u51fa\u624b\u72e0\u8fa3\uff0c\u518d\u4e0d\u5bb9\u60c5\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u6012\u559d\u8fde\u8fde\uff0c\u65bd\u5c55\u5f00\u201c\u9634\u638c\u4e03\u5341\u4e8c\u5f0f\u201d\uff0c\u638c\u5f71\u7ffb\u98de\u3001\u5288\u3001\u9547\u3001\u64a9\u3001\u6253\u3001\u70b9\uff0c \u5168\u662f\u8fdb\u653b\u3002 \u3000\u3000\u4e24\u4eba\u8eab\u5f62\u4ff1\u5feb\uff0c\u6643\u773c\u4fbf\u653b\u4e86\u5341\u6570\u62db\uff0c\u5ffd\u542c\u5413\u5730\u4e00\u58f0\uff0c\u65b9\u5c11\u74a7\u5df2\u8df3\u8fdb\u957f\u6c5f\u4e86\u3002 \u3000\u3000\u4e24\u4eba\u987e\u4e0d\u5f97\u518d\u6495\u62fc\uff0c\u9f50\u90fd\u4f4f\u4e86\u624b\uff0c\u5411\u6c5f\u8fb9\u5954\u53bb\uff0c\u4f46\u662f\u53ea\u89c1\u6c5f\u6c34\u60a0\u60a0\uff0c\u54ea\u91cc\u8fd8\u6709\u65b9\u5c11 \u74a7\u7684\u4eba\u5f71\u3002 \u3000\u3000\u4e24\u4eba\u4ff1\u90fd\u4e0d\u4f1a\u6c34\uff0c\u91d1\u6b39\u867d\u7565\u8bc6\u6c34\u6027\uff0c\u4f46\u82e5\u8981\u4ed6\u4e0b\u6c34\u6551\u4eba\uff0c\u4e5f\u4e07\u4e07\u4f5c\u4e0d\u5230\uff0c\u4e24\u4eba\u6115\u5728 \u6c5f\u8fb9\uff0c\u8c01\u90fd\u4e0d\u6562\u5f80\u4e0b\u8df3\uff0c\u91d1\u6885\u9f84\u4e5f\u8dd1\u4e86\u8fc7\u6765\uff0c\u770b\u89c1\u8f9b\u6377\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\uff0c\u5fc3\u91cc\u751f\u6c14\uff0c\u4f46 \u60f3\u5230\u65b9\u5c11\u74a7\u4e3a\u60c5\u4e27\u547d\uff0c\u53c8\u89c9\u60cb\u60dc\u3002 \u3000\u3000\u8f9b\u6377\u60f3\u5230\u65b9\u5c11\u74a7\u5bf9\u81ea\u5df1\u4ea6\u662f\u4e00\u5f80\u60c5\u6df1\uff0c\u5982\u4eca\u5374\u53c8\u4e0d\u660e\u4e0d\u767d\u7684\u6b7b\u53bb\uff0c\u6ee1\u8154\u6012\u706b\uff0c\u90fd\u53d1 \u5728\u5929\u9b54\u91d1\u6b39\u8eab\u4e0a\u3002 \u3000\u3000\u90a3\u77e5\u5929\u9b54\u91d1\u6b39\u5bf9\u8f9b\u6377\u4ea6\u662f\u6068\u5165\u9aa8\u9ad3\uff0c\u4e00\u58f0\uff1a\u201c\u90fd\u662f\u4f60\uff01\u201d\u53cc\u638c\u9f50\u51fa\uff0c\u201c\u6731\u7b14\u70b9\u518c\u201d\uff0c \u201c\u51a4\u9b42\u7f20\u817f\u201d\uff0c\u4e0a\u4e0b\u4e24\u62db\uff0c\u8fc5\u5982\u5954\u96f7\u3002 \u3000\u3000\u8f9b\u6377\u5de6\u638c\u62cd\u51fa\uff0c\u5ffd\u5730\u5316\u505a\u4e09\u4e2a\u638c\u5708\uff0c\u6b63\u662f\u201c\u8679\u679d\u5251\u6cd5\u201d\u91cc\u7684\u201c\u6885\u82b1\u4e09\u5f04\u201d\uff0c\u8f9b\u6377\u4ee5 \u638c\u4f5c\u5251\uff0c\u8fde\u6d88\u5e26\u6253\uff0c\u53f3\u638c\u4e0b\u5207\uff0c\u6a2a\u622a\u91d1\u6b39\u5de6\u8155\u3002 \u3000\u3000\u91d1\u6b39\u5fc3\u5934\u4e00\u51db\uff0c\u64a4\u62db\u53d8\u5f0f\uff0c\u4e24\u4eba\u53c8\u6253\u505a\u4e00\u5904\u3002\u6b64\u756a\u4e24\u4eba\u4ff1\u90fd\u80f8\u6000\u6028\u6bd2\uff0c\u4e0b\u624b\u66f4\u4e0d\u5bb9 \u60c5\uff0c\u638c\u98ce\u864e\u864e\uff0c\u5c06\u91d1\u6885\u9f84\u7684\u7684\u8863\u8882\u90fd\u9707\u5f97\u98de\u821e\u4e86\u8d77\u6765\u3002 \u3000\u3000\u91d1\u6885\u9f84\u89c1\u4ed6\u4e8c\u4eba\u53c8\u52a8\u4e0a\u4e86\u624b\uff0c\u82b3\u5fc3\u7d0a\u4e71\uff0c\u4e0d\u77e5\u8be5\u5982\u4f55\u662f\u597d\uff0c\u8fd9\u4e24\u4eba\u4e00\u4e2a\u662f\u5979\u7684\u5e08\u5144\uff0c \u4e00\u4e2a\u5374\u662f\u5979\u7684\u201c\u4e08\u592b\u201d\uff0c\u5979\u52bf\u4e0d\u80fd\u63d2\u624b\u76f8\u52a9\u4efb\u4f55\u4e00\u65b9\uff0c\u4ee5\u5979\u529f\u529b\uff0c\u53c8\u4e0d\u80fd\u5316\u89e3\uff0c\u53ea\u6709\u773c \u7741\u7741\u5730\u770b\u7740\uff0c\u8fde\u809a\u997f\u90fd\u5fd8\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u4e09\u6b21\u548c\u5929\u9b54\u91d1\u6b39\u52a8\u624b\uff0c\u90fd\u4e0d\u80fd\u53d6\u80dc\uff0c\u5fc3\u91cc\u6697\u6697\u7740\u6025\uff0c\u600e\u5730\u51fa\u5e08\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21\u548c \u4eba\u4ea4\u624b\uff0c\u5c31\u82e6\u6218\u4e0d\u4e0b\uff0c\u8fd8\u8c08\u4ec0\u4e48\u5176\u4ed6\u7684\u5927\u4e8b\u3002 \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\u8fd9\u201c\u5929\u9b54\u91d1\u6b4c\u201d\u5e74\u7eaa\u867d\u8f7b\uff0c\u5374\u5df2\u540d\u9707\u6c5f\u6e56\uff0c\u8fde\u201c\u5d06\u5cd2\u201d\u4e09\u7edd\u5251\u90a3\u7b49\u5028\u50b2 \u7684\u89d2\u8272\uff0c\u90fd\u8981\u60e7\u4ed6\u4e2a\u4e09\u5206\uff0c\u82e5\u7136\u6b64\u523b\u6709\u4e2a\u6c5f\u6e56\u8c6a\u58eb\u89c1\u5230\u6709\u4eba\u80fd\u548c\u201c\u5929\u9b54\u91d1\u6b39\u201d\u4e2a\u5e73\u624b\uff0c \u6015\u4e0d\u8981\u5413\u5f97\u8df3\u8d77\u6765\u3002 \u3000\u3000\u4f55\u51b5\u5929\u9b54\u91d1\u6b39\u5bf9\u654c\u7ecf\u9a8c\u8fdc\u80dc\u8f9b\u6377\uff0c\u662f\u4ee5\u8f9b\u6377\u529f\u529b\u91cf\u7565\u80dc\u4e00\u7b79\uff0c\u4f46\u5374\u4e5f\u53ea\u80fd\u6253\u4e2a\u5e73\u624b\u3002 \u3000\u3000\u4f46\u662f\u4e24\u4eba\u52a8\u624b\u65f6\u5019\u4e00\u957f\uff0c\u90a3\u5929\u9b54\u91d1\u6b39\u5374\u6e10\u611f\u4e0d\u652f\uff0c\u8fd9\u4e00\u5929\u591a\u6765\uff0c\u4ed6\u4e0d\u4f46\u672a\u996e\u672a\u98df\uff0c \u800c\u4e14\u4f11\u606f\u90fd\u6ca1\u6709\u4f11\u606f\u8fc7\u4e00\u4e0b\u3002 \u3000\u3000\u91d1\u6b39\u5fc3\u4e2d\u6709\u6570\uff0c\u77e5\u9053\u518d\u6253\u4e0b\u53bb\uff0c\u81ea\u5df1\u5fc5\u5b9a\u843d\u8d25\uff0c\u770b\u81ea\u5df1\u5e08\u59b9\u7684\u6837\u5b50\uff0c\u975e\u4f46\u4e0d\u4f1a\u5e2e\u81ea \u5df1\u7684\u5fd9\uff0c\u4e0d\u53cd\u8fc7\u6765\u6253\u81ea\u5df1\u5c31\u7b97\u597d\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u52a8\u624b\u4e4b\u5904\uff0c\u4e09\u9762\u90fd\u662f\u65f7\u91ce\uff0c\u53e6\u4e00\u65b9\u9762\u5374\u662f\u957f\u6c5f\uff0c\u8fde\u9003\u90fd\u65e0\u6cd5\u9003\uff0c\u6697\u53eb\u4e00\u58f0 \u201c\u82e6\u4e5f\u201d\uff0c\u62db\u5f0f\u66f4\u89c1\u51cc\u5389\uff0c\u7b80\u76f4\u662f\u62fc\u547d\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u66f4\u662f\u534a\u62db\u4e5f\u4e0d\u6562\u677e\u61c8\uff0c\u987b\u77e5\u4ed6\u4e00\u6b21\u4e2d\u6bd2\u4e4b\u540e\uff0c\u5bf9\u201c\u6bd2\u541b\u201d\u7684\u6bd2\uff0c\u5fc3\u4e2d\u6df1\u6000\u754f\u60e7\uff0c \u8fd9\u5929\u9b54\u91d1\u6b39\u65e2\u662f\u91d1\u4e00\u9e4f\u7684\u5927\u5f1f\u5b50\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u4ec0\u4e48\u6bd2\u7269\uff0c\u662f\u4ee5\u4ed6\u534a\u70b9\u4e5f\u4e0d\u6562\u653e\u677e\uff0c\u6015\u91d1 \u6b39\u4e58\u9699\u65bd\u6bd2\u3002 \u3000\u3000\u4ed6\u5374\u4e0d\u77e5\uff0c\u8fd9\u5929\u9b54\u91d1\u6b39\u56ca\u4e2d\u7684\u6bd2\u836f\u6697\u5668\u5982\u679c\u90fd\u5e26\u5728\u8eab\u65c1\uff0c\u6015\u4e0d\u65e9\u5c31\u65bd\u5c55\u4e86\uff0c\u8fd8\u4f1a\u7b49 \u5230\u5df2\u52a8\u4e0a\u624b\u7684\u65f6\u5019\u3002 \u3000\u3000\u539f\u6765\u91d1\u6b39\u51fa\u6c5f\u6e56\uff0c\u6839\u672c\u6ca1\u6709\u78b0\u5230\u8fc7\u654c\u624b\uff0c\u4e0d\u514d\u5fc3\u9ad8\u6c14\u50b2\uff0c\u5c06\u6697\u5668\u90fd\u7f6e\u4e8e\u4ed6\u5904\u4e0d\u7528\uff0c \u6b64\u523b\u4ed6\u5fc3\u91cc\u4e5f\u540e\u6094\u4e0d\u5df2\uff0c\u57cb\u6028\u81ea\u5df1\u6ca1\u6709\u5c06\u6bd2\u836f\u6697\u5668\u653e\u5728\u8eab\u4e0a\u3002 \u3000\u3000\u5ffd\u5730\u6c5f\u4e2d\u98de\u5feb\u5730\u9a76\u6765\u4e00\u8258\u5c0f\u8239\uff0c\u4e58\u98ce\u7834\u6d6a\uff0c\u5728\u8fd9\u6c5f\u9762\u4e0a\u98de\u5feb\u7684\u884c\u8d70\uff0c\u901f\u5ea6\u5feb\u5f97\u60ca\u4eba\u3002 \u91d1\u6885\u9f84\u773c\u89c2\u56db\u8def\uff0c\u770b\u5230\u8fd9\u5c0f\u8239\u7adf\u662f\u5411\u81ea\u5df1\u5b58\u8eab\u4e4b\u5904\u9a76\u6765\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u5979\u968f\u91d1\u4e00\u9e4f\u9080\u6e38\u591a \u5904\uff0c\u4e00\u773c\u4fbf\u770b\u51fa\u8fd9\u8239\u6765\u52bf\u60ca\u4eba\uff0c\u4ee5\u8fd9\u6837\u7684\u5c0f\u8239\uff0c\u6709\u8fd9\u6837\u7684\u901f\u5ea6\uff0c\u60f3\u89c1\u8239\u4e0a\u7684\u4eba\u4e5f\u4e0d\u662f\u5e38 \u4eba\u3002 \u3000\u3000\u5c0f\u8239\u5728\u5cb8\u8fb9\u6253\u4e86\u8f6c\uff0c\u4fbf\u505c\u6cca\u5728\u5cb8\u8fb9\uff0c\u8239\u4e0a\u8df3\u4e0b\u4e09\u4e2a\u4eba\u6765\uff0c\u8f9b\u6377\u548c\u91d1\u6b39\u52a8\u624b\u4e4b\u5904\u6b63\u5728 \u5cb8\u8fb9\uff0c\u8fd9\u4e24\u4eba\u4ff1\u662f\u9ad8\u624b\uff0c\u867d\u5728\u5168\u795e\u5bf9\u654c\uff0c\u5374\u4e5f\u53d1\u73b0\u5cb8\u4e0a\u6765\u4e86\u51e0\u4e2a\u4eba\uff0c\u4f46\u4e24\u4eba\u5374\u8c01\u4e5f\u4e0d\u6562 \u5148\u4f4f\u624b\uff0c\u4e88\u5bf9\u65b9\u53ef\u4e58\u4e4b\u673a\u3002 \u3000\u3000\u90a3\u8239\u4e0a\u4e0b\u6765\u7684\u662f\u4e24\u7537\u4e00\u5973\uff0c\u4e00\u4e2a\u662f\u67af\u7626\u8001\u8005\uff0c\u53e6\u5916\u4e00\u7537\u4e00\u5973\u5374\u662f\u5e74\u8f7b\u4eba\uff0c\u8863\u7740\u4ff1\u7686 \u534e\u4e3d\uff0c\u50cf\u662f\u8c6a\u95e8\u4e16\u5bb6\u7684\u516c\u5b50\u5c0f\u59d0\u3002 \u3000\u3000\u90a3\u4e09\u4eba\u4e0b\u4e86\u8239\uff0c\u5c31\u7ad9\u5728\u5cb8\u8fb9\uff0c\u4e5f\u4e0d\u51fa\u58f0\uff0c\u4f46\u4e09\u4eba\u9762\u4e0a\u90fd\u5e26\u7740\u60ca\u5f02\u4e4b\u8272\u671b\u7740\u8f9b\u6377\u4e0e\u91d1 \u6b39\u4e8c\u4eba\u7684\u6bd4\u6597\uff0c\u90a3\u5c11\u5973\u4f4e\u58f0\u5411\u8001\u8005\u5600\u5495\u4e86\u51e0\u53e5\uff0c\u8001\u8005\u5fae\u6447\u4e86\u6447\u5934\uff0c\u4f46\u4ed6\u4eec\u8bed\u97f3\u6781\u4f4e\uff0c\u542c \u4e0d\u51fa\u7a76\u7adf\u8bf4\u4e86\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e24\u773c\u5374\u76f4\u52fe\u52fe\u5730\u76ef\u7740\u91d1\u6885\u9f84\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u5730\u5728\u5979\u8eab\u4e0a\u6253\u8f6c\u3002 \u3000\u3000\u91d1\u6885\u9f84\u89c1\u90a3\u5c11\u5e74\u5bb9\u8c8c\u867d\u4ea6\u751a\u4fca\u7f8e\uff0c\u4f46\u773c\u89d2\u4e0b\u5782\uff0c\u773c\u5149\u4e0d\u6b63\uff0c\u4e0d\u50cf\u4e2a\u597d\u4eba\uff0c\u5fc3\u4e2d\u4e0d\u7981 \u6709\u6c14\uff0c\u6697\u5fd6\uff1a\u201c\u59d1\u5a18\u7b49\u4f1a\u975e\u6559\u8bad\u6559\u8bad\u4f60\u4e0d\u53ef\u201d \u3000\u3000\u5ffd\u5730\u5979\u770b\u5230\u8fd9\u4e09\u4eba\u8eab\u4e0a\u90fd\u659c\u80cc\u7740\u4e2a\u9e7f\u76ae\u9556\u56ca\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5fd6\u9053\uff1a\u201c\u83ab\u975e\u5c31\u662f\u4ed6\u4eec\uff1f\u201d \u5fc3\u4e2d\u7591\u5ff5\u987f\u8d77\uff0c\u76ee\u5149\u4e5f\u4e0d\u7981\u76f4\u671d\u90a3\u8fb9\u671b\uff0c\u90a3\u5c11\u5e74\u5fae\u5fae\u4e00\u7b11\u7f13\u7f13\u8d70\u4e86\u8fc7\u6765\uff0c\u7ec6\u58f0\u7ec6\u6c14\u8bf4\u9053\uff1a \u201c\u91d1\u59d1\u5a18\uff0c\u4f60\u597d\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4e00\u53e3\u9053\u51fa\u5979\u7684\u59d3\uff0c\u91d1\u6885\u9f84\u5413\u4e86\u4e00\u8df3\u3002 \u3000\u3000\u5979\u672c\u60f3\u95ee\uff1a\u201c\u4f60\u600e\u6837\u77e5\u9053\u6211\u7684\u59d3\u201d\uff0c\u4f46\u770b\u8fd9\u5c11\u5e74\u8d3c\u7709\u8d3c\u773c\u7684\uff0c\u5fc3\u91cc\u6c14\u66f4\u5927\uff0c\u5934\u4e00\u8f6c\uff0c \u4e0d\u53bb\u7406\u4ed6\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u563b\u563b\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u91d1\u59d1\u5a18\u597d\u5927\u7684\u67b6\u5b50\u3002\u201d\u91d1\u6885\u9f84\u6c14\u5f80\u4e0a\u649e\uff0c\u5ffd\u53c8\u5ff5\u5934\u4e00 \u8f6c\uff0c\u5fcd\u4e0b\u4e86\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u9601\u4e0b\u8d35\u59d3\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u773c\u775b\u772f\u8d77\u4e00\u6761\u7ebf\uff0c\u521a\u60f3\u8bf4\u8bdd\uff0c\u5ffd\u5730\u4e00\u58f0\u6012\u5316\uff0c\u63a5\u7740\u7830\u7136\u4e00\u58f0\u5927\u9707\uff0c\u8f6c\u5934\u4e00\u770b\uff0c \u6bd4\u6597\u7740\u7684\u4e24\u4eba\u6b64\u523b\u5df2\u5206\u80dc\u8d1f\u3002 \u3000\u3000\u539f\u6765\u5929\u9b54\u91d1\u6b39\u62db\u5f0f\u8d8a\u53d1\u7280\u5229\uff0c\u4ed6\u81ea\u5df1\u5374\u77e5\u9053\u5df2\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u4e0d\u51fa\u9669\u62db\uff0c\u4eca\u65e5\u52bf\u5fc5\u96be \u9003\u6d3b\u547d\u3002 \u3000\u3000\u8f9b\u6377\u4ea6\u60f3\u65e9\u4e9b\u4e86\u5374\uff0c\u638c\u6cd5\u4e2d\u53c8\u6742\u4ee5\u5251\u6cd5\uff0c\u8eab\u5f62\u98d8\u5ffd\uff0c\u56f4\u7740\u91d1\u6b4c\u6253\u8f6c\uff0c\u4ed6\u806a\u660e\u7edd\u9876\uff0c \u89c1\u5230\u91d1\u6b39\u7684\u72e0\u6253\uff0c\u5fc3\u4e2d\u4ea6\u5df2\u6709\u6570\uff0c\u77e5\u9053\u4ed6\u771f\u6c14\u5df2\u7ecf\u4e0d\u7ee7\u3002 \u3000\u3000\u8fd9\u65f6\u91d1\u6b39\u4e00\u638c\u5f15\u6ee1\uff0c\u5411\u4ed6\u80a9\u5934\u6253\u6765\uff0c\u4ed6\u7d22\u6027\u4e0d\u62db\u67b6\uff0c\u5c06\u5168\u8eab\u771f\u6c14\u90fd\u704c\u6ce8\u5728\u80a9\u4e0a\uff0c\u62fc \u7740\u6328\u4ed6\u4e00\u638c\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b4c\u4e00\u638c\u6012\u5316\uff0c\u4e00\u638c\u65b9\u81ea\u51fb\u4e2d\uff0c\u90a3\u77e5\u80f8\u4e2d\u7830\u7136\u4e5f\u7740\u4e86\u4e00\u62f3\uff0c\u8eab\u8eaf\u76f4\u98de\u4e86\u51fa\u53bb\uff0c \u54c7\u5730\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\u6765\uff0c\u6c14\u5598\u4e0d\u5df2\u3002 \u3000\u3000\u8f9b\u6377\u867d\u7136\u5f97\u9664\u5f3a\u654c\uff0c\u4f46\u81ea\u5df1\u80a9\u5934\u4e2d\u4e86\u4e00\u638c\uff0c\u867d\u662f\u91d1\u6b4c\u771f\u6c14\u5df2\u5f31\u65f6\u51fb\u51fa\uff0c\u800c\u4e14\u4ed6\u4ea6\u65e9 \u6709\u51c6\u5907\uff0c\u4f46\u4ed6\u534a\u8eab\u4e5f\u662f\u53d1\u9ebb\uff0c\u4ed6\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u96c4\u5fc3\u58ee\u5fd7\uff0c\u987f\u65f6\u51b7\u5374\u4e86\u4e00\u534a\uff0c\u5fd6\u9053\uff1a \u201c\u6211\u8fde\u4ed6\u90fd\u80dc\u5f97\u5982\u6b64\u8270\u82e6\uff0c\u8981\u80dc\u90a3\u5929\u4e0b\u7b2c\u4e00\u5251\uff0c\u5c82\u975e\u66f4\u96be\u4e86\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u89c1\u8f9b\u6377\u4eff\u4f5b\u6447\u6447\u6b32\u5012\uff0c\u60ca\u547c\u7740\u7a9c\u4e86\u8fc7\u53bb\uff0c\u4f38\u624b\u6276\u7740\u4ed6\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u4f24\u5f97 \u91cd\u4e0d\u91cd\uff1f\u201d \u3000\u3000\u90a3\u534e\u670d\u5c11\u5e74\u89c1\u91d1\u6885\u9f84\u4e0e\u4ed6\u5982\u6b64\u4eb2\u70ed\uff0c\u5634\u89d2\u6cdb\u8d77\u4e00\u4e1d\u72de\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u7d22\u6027\u8fde\u8fd9\u5c0f\u5b50\u4e5f \u4e00\u9f50\u9001\u7ec8\u3002\u201d\u4f38\u624b\u4eba\u56ca\uff0c\u53d6\u51fa\u4e00\u53ea\u4e4c\u6cb9\u6cb9\u7684\u624b\u5957\uff0c\u5957\u5728\u5de6\u624b\u4e0a\uff0c\u8d70\u8fc7\u53bb\u671d\u91d1\u6885\u9f84\u9634\u7b11\u9053\uff1a \u201c\u73b0\u5728\u59d1\u5a18\u77e5\u9053\u6211\u662f\u8c01\u4e86\u5427\uff1f\u201d\u624b\u4e00\u626c\uff0c\u5c06\u90a3\u53ea\u5957\u7740\u624b\u5957\u7684\u624b\u653e\u5728\u91d1\u6885\u9f84\u773c\u524d\u3002 \u3000\u3000\u8f9b\u6377\u5267\u6597\u5df1\u4f11\uff0c\u653e\u773c\u4e00\u770b\u8fd9\u51e0\u4eba\uff0c\u5fc3\u4e2d\u6b63\u5728\u5947\u602a\u7740\u8fd9\u51e0\u4eba\u7684\u6765\u5386\uff0c\u6b64\u523b\u89c1\u90a3\u5c11\u5e74\u51b7 \u7b11\u7740\u8d70\u8fc7\u6765\uff0c\u5fd6\u9053\uff1a\u201c\u96be\u9053\u8fd9\u4e9b\u4eba\u548c\u5979\u7236\u5973\u6709\u4ec0\u4e48\u74dc\u845b\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u4e00\u89c1\u8fd9\u624b\u5957\uff0c\u65e9\u5df2\u9762\u8272\u5927\u53d8\uff0c\u90a3\u5c11\u5973\u4e0e\u67af\u7626\u8001\u4eba\u4e5f\u7f13\u7f13\u8d70\u4e86\u8fc7\u6765\uff0c\u5374\u4e0d\u7406\u91d1 \u6885\u9f84\uff0c\u56db\u53ea\u773c\u775b\u4e00\u9f50\u6253\u91cf\u7740\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u8fd9\u4e09\u4eba\u884c\u8ff9\u8be1\u5f02\uff0c\u800c\u4e14\u53cc\u773c\u795e\u5145\u6c14\u8db3\uff0c\u90fd\u662f\u5185\u5bb6\u597d\u624b\uff0c\u5c24\u5176\u662f\u90a3\u67af\u7626\u8001\u8005\uff0c \u4e24\u592a\u9633\u7a74\u7adf\u9f13\u8d77\u5bf8\u8bb8\uff0c\u53ef\u60f3\u5185\u529f\u66f4\u662f\u60ca\u4eba\uff0c\u4ed6\u81ea\u5fd6\u4e86\u4e00\u4e0b\u81ea\u5df1\u7684\u5730\u4f4d\u4e0e\u5c06\u6765\u7684\u6253\u7b97\uff0c\u4e0d \u613f\u5f97\u7f6a\u6c5f\u6e56\u4e2d\u4eba\uff0c\u5c24\u5176\u662f\u8fd9\u4e9b\u597d\u624b\uff0c\u4f55\u51b5\u81ea\u5df1\u73b0\u5728\u6c14\u529b\u5df1\u8870\uff0c\u80a9\u5934\u4e5f\u9690\u9690\u53d1\u75db\uff0c\u5b9e\u4e0d\u80fd \u518d\u6811\u5f3a\u654c\uff0c\u9042\u5411\u90a3\u8001\u8005\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u8001\u4e08\u6709\u4f55\u89c1\u6559\uff1f\u201d\u8bdd\u521a\u8bf4\u5b8c\uff0c\u5c31\u53d1\u89c9\u91d1\u6885\u9f84\u5728\u5077 \u5077\u62c9\u81ea\u5df1\u7684\u8863\u8896\u3002 \u3000\u3000\u90a3\u8001\u8005\u76ee\u5149\u5de6\u987e\u53f3\u76fc\uff0c\u770b\u4e86\u8f9b\u6377\u4e00\u773c\uff0c\u53c8\u770b\u91d1\u6885\u9f84\u4e00\u773c\uff0c\u5fc3\u4e2d\u4e5f\u5728\u5947\u602a\u7740\uff1a\u201c\u8fd9\u5c11 \u5e74\u6b66\u529f\u60ca\u4eba\uff0c\u4e0d\u77e5\u662f\u4f55\u6765\u8def\uff0c\u8fd1\u6765\u6b66\u6797\u4e2d\u4f3c\u8fd8\u672a\u542c\u8bf4\u8fc7\u51fa\u4e86\u4e2a\u5982\u6b64\u4eba\u7269\uff0c\u6700\u602a\u7684\u662f\u4ed6\u548c \u91d1\u4e00\u9e4f\u7684\u5973\u513f\u4e3e\u6b62\u4f3c\u751a\u4eb2\u5bc6\uff0c\u5374\u53c8\u548c\u91d1\u4e00\u9e4f\u7684\u5f92\u513f\u820d\u751f\u5fd8\u6b7b\u7684\u72e0\u6597\uff0c\u4e0d\u77e5\u5230\u5e95\u662f\u53cb\u662f \u654c\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u63e3\u6d4b\uff0c\u9887\u6709\u62c9\u62e2\u8f9b\u6377\u4e4b\u610f\uff0c\u4e5f\u5c06\u624b\u4e00\u62f1\uff0c\u7b11\u9053\uff1a\u201c\u5728\u4e0b\u56db\u5ddd\u5510\u658c\uff0c\u6b64\u6765\u53ea\u56e0 \u548c\u91d1\u4e00\u9e4f\u6709\u4e9b\u5c0f\u8fc7\u8282\uff0c\u670b\u53cb\u7aef\u7684\u597d\u8eab\u624b\uff0c\u4e0d\u77e5\u9ad8\u59d3\u5927\u540d\uff0c\u5c0a\u5e08\u662f\u90a3\u4e00\u4f4d\uff0c\u770b\u6765\u50cf\u662f\u548c\u6bd2 \u541b\u4e5f\u6709\u4e9b\u6881\u5b50\uff0c\u4f60\u6211\u4e0d\u59a8\u4ea4\u4e2a\u670b\u53cb\u3002\u201d \u3000\u3000\u5510\u658c\u8001\u5978\u5de8\u6ed1\uff0c\u5148\u62ff\u8bdd\u5957\u4f4f\uff0c\u5c06\u8f9b\u6377\u62c9\u5230\u81ea\u5df1\u8fd9\u8fb9\uff0c\u8f9b\u6377\u4e00\u7b11\uff0c\u809a\u91cc\u96ea\u4eae\uff0c\u6697\u9053\uff1a \u201c\u8fd9\u6837\u6700\u597d\uff0c\u6211\u4e5f\u4e0d\u60f3\u548c\u4f60\u4eec\u7ed3\u4ec7\u3002\u201d\u539f\u6765\u8f9b\u6377\u4e5f\u66fe\u542c\u8d77\u56db\u5ddd\u5510\u95e8\u4e4b\u540d\uff0c\u5c24\u5176\u5510\u5bb6\u7684\u6bd2 \u836f\u6697\u5668\uff0c\u6c5f\u6e56\u4e0a\u591a\u8c08\u4e4b\u8272\u53d8\uff0c\u800c\u4e14\u5510\u95e8\u4e2d\u4eba\u6c14\u91cf\u6700\u7a84\uff0c\u775a\u6bd7\u5fc5\u62a5\uff0c\u53ea\u8981\u60f9\u4e86\u4ed6\u4eec\uff0c\u4e00\u751f \u4e00\u4e16\u4e5f\u6ca1\u6709\u4e2a\u4e86\u5c40\u3002 \u3000\u3000\u8f9b\u6377\u54e6\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u539f\u6765\u8001\u4e08\u7adf\u662f\u540d\u95fb\u5929\u4e0b\u7684\u5510\u8001\u82f1\u96c4\uff0c\u5931\u656c\u4e86\uff0c\u5931\u656c\u4e86\u3002\u201d\u4ed6 \u907f\u5f00\u5510\u658c\u7684\u4e24\u53e5\u95ee\u8bdd\uff0c\u4e0d\u63d0\u81ea\u5df1\u7684\u59d3\u6c0f\uff0c\u5de7\u5999\u5730\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u548c\u91d1\u4e00\u9e4f\u65e0\u4ec7\u6028\uff0c\u4ea6 \u65e0\u74dc\u845b\uff0c\u5510\u82f1\u96c4\u8981\u590d\u4ec7\uff0c\u53ea\u7ba1\u8bf7\u4fbf\uff0c\u53ea\u662f\u90a3\u91d1\u4e00\u9e4f\u6b64\u523b\u5374\u4e0d\u5728\u8fd9\u513f\u5462\u3002\u201d \u3000\u3000\u5929\u9b54\u91d1\u6b39\u4e00\u542c\u662f\u5e08\u7236\u7684\u4ec7\u4eba\u5230\u4e86\uff0c\u81ea\u5df1\u6b64\u523b\u504f\u53c8\u53d7\u4f24\uff0c\u65e0\u6cd5\u5e94\u654c\uff0c\u8fd9\u56db\u5ddd\u5510\u95e8\u4e2d\u4eba\uff0c \u4e2a\u4e2a\u5fc3\u72e0\u624b\u8fa3\uff0c\u5510\u658c\u66f4\u662f\u6709\u540d\u7684\u50ac\u547d\u7b26\uff0c\u81ea\u5df1\u4eca\u65e5\u5f3a\u4ec7\u73af\u4f3a\uff0c\u770b\u6765\u662f\u51f6\u591a\u5409\u5c11\u4e86\uff0c\u4f55\u4e0d \u75db\u75db\u5feb\u5feb\u5730\u5145\u4e2a\u597d\u6c49\uff0c\u4e00\u5ff5\u81f3\u6b64\uff0c\u4ed6\u672c\u6781\u9aa1\u608d\uff0c\u5fcd\u7740\u80f8\u524d\u4e4b\u75bc\uff0c\u4e00\u4e2a\u7bad\u6b65\u7a9c\u4e86\u8fc7\u6765\uff0c\u559d \u9053\uff1a\u201c\u8981\u627e\u6211\u5e08\u7236\u7684\uff0c\u53ea\u7ba1\u51b2\u7740\u6211\u91d1\u6b39\u6765\u597d\u4e86\uff0c\u5927\u7237\u867d\u53d7\u4e86\u4f24\uff0c\u53ef\u4e5f\u4e0d\u542b\u7cca\u4f60\u4eec\u8fd9\u6279\u5c0f \u8f88\u3002\u201d \u3000\u3000\u5510\u658c\u9634\u607b\u607b\u4e00\u58f0\u957f\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6b63\u662f\u3001\u4e94\u662f\uff0c\u90a3\u91d1\u4e00\u9e4f\u867d\u7136\u4e0d\u5728\uff0c\u62ff\u4ed6\u7684\u5f92\u5f1f\u5973\u513f \u6765\u62b5\u4e5f\u662f\u4e00\u6837\uff0c\u7075\u513f\u3001\u66fc\u513f\uff0c\u4f60\u4eec\u5e73\u65e5\u603b\u8bf4\u6697\u5668\u9776\u5b50\u4e0d\u597d\uff0c\u8fd9\u4e24\u4eba\u5c82\u975e\u662f\u4f60\u4eec\u6700\u597d\u7684\u6d3b \u9776\u5b50\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd8\u662f\u4e8c\u53d4\u75bc\u6211\u4eec\uff0c\u5582\uff01\u7075\u54e5\uff0c\u4f60\u6253\u7537\u7684\uff0c\u6211\u6253\u5973\u7684\uff0c\u770b\u8c01\u6253\u4e2d \u7684\u591a\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6b63\u662f\u5510\u95e8\u638c\u95e8\u8ffd\u9b42\u5510\u96f7\u7684\u7231\u5b50\u6bd2\u90ce\u541b\u5510\u7075\uff0c\u95fb\u8a00\u7b11\u563b\u563b\u5730\u8bf4\uff1a\u201c\u6211\u4e0d\u548c\u4f60\u6bd4\uff0c \u4f60\u62db\u547c\u8fd9\u4f4d\u59d1\u5a18\u65f6\uff0c\u53ef\u5343\u4e07\u522b\u6253\u574f\u4e86\u5979\u8fd9\u5f20\u5a07\u6ef4\u6ef4\u7684\u8138\u86cb\uff0c\u4e0d\u7136\uff0c\u6211\u53ef\u8981\u5bf9\u4f60\u4e0d\u5ba2\u6c14 \u5462\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e00\u5439\u4e00\u5531\uff0c\u5c06\u91d1\u6b39\u7b49\u770b\u6210\u56ca\u4e2d\u4e4b\u7269\uff0c\u91d1\u6b39\u7d20\u6027\u9634\u9e37\uff0c\u4eba\u5bb6\u8d8a\u9a82\u4ed6\uff0c\u4ed6\u8d8a\u4e0d\u751f\u6c14\uff0c \u53ea\u662f\u6697\u6697\u8c03\u606f\uff0c\u51c6\u5907\u51fa\u624b\u4e00\u51fb\uff0c\u5148\u5e9f\u6389\u4e00\u4e2a\u3002 \u3000\u3000\u91d1\u6885\u9f84\u5374\u6c14\u5f97\u7c89\u8138\u901a\u7ea2\uff0c\u521a\u8981\u7eb5\u51fa\u53bb\uff0c\u5374\u88ab\u8f9b\u6377\u4e00\u628a\u62c9\u4f4f\u3002 \u3000\u3000\u8f9b\u6377\u957f\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u5510\u8001\u82f1\u96c4\u662f\u6b66\u6797\u4e2d\u7684\u524d\u8f88\uff0c\u5728\u4e0b\u4e00\u5411\u94a6\u4f69\u5f97\u5f88\uff0c\u90a3\u77e5\u4eca\u65e5\u4e00\u89c1\uff0c \u5374\u4e0d\u7981\u4ee4\u5728\u4e0b\u5931\u671b\u3002\u201d \u3000\u3000\u5510\u658c\u8138\u8272\u4e00\u53d8\uff0c\u4ed6\u5b9e\u5728\u770b\u4e0d\u51fa\u8f9b\u6377\u7684\u6765\u8def\uff0c\u53ea\u89c9\u8fd9\u5c11\u5e74\u975e\u4f46\u6b66\u529f\u9ad8\u5f3a\uff0c\u800c\u4e14\u8a00\u8bed\u950b \u5229\u5e94\u53d8\u5bf9\u7b54\uff0c\u50cf\u662f\u591a\u5e74\u7684\u8001\u6c5f\u6e56\u4e86\uff0c\u5fc3\u91cc\u66f4\u5947\u602a\u3002 \u3000\u3000\u201c\u60f3\u4e0e\u5510\u8001\u82f1\u96c4\u7ed3\u4e0b\u6881\u5b50\u7684\u53ea\u662f\u91d1\u4e00\u9e4f\u672c\u4eba\u800c\u5df2\uff0c\u4e0e\u4ed6\u7684\u540e\u8f88\u4f55\u5173\uff0c\u4f55\u51b5\u6b64\u4e24\u4eba\uff0c \u4e00\u4e2a\u662f\u5973\u6d41\u4e4b\u8f88\uff0c\u4e00\u4e2a\u53c8\u53d7\u4e86\u4f24\uff0c\u5510\u8001\u82f1\u96c4\u82e5\u7136\u6b64\u65f6\u52a8\u624b\uff0c\u65e5\u540e\u4f20\u5c06\u51fa\u53bb\uff0c\u5c82\u975e\u843d\u4e2a\u4ee5 \u5927\u6b3a\u5c0f\uff0c\u4e58\u4eba\u4e4b\u5371\u4e4b\u540d\uff0c\u5728\u4e0b\u60f3\u5510\u8001\u82f1\u96c4\u4e0d\u81f3\u4e8e\u5982\u6b64\u5427\uff01\u201d\u8f9b\u6377\u8bdd\u4e2d\u5e26\u523a\uff0c\u5374\u53c8\u8bf4\u5f97\u4e0d \u9732\u75d5\u8ff9\u3002 \u3000\u3000\u5510\u658c\u9762\u8272\u4e00\u53d8\uff0c\u539f\u6765\u4ed6\u65b9\u624d\u4e0a\u5cb8\u65f6\uff0c\u89c1\u5230\u91d1\u6b39\u4e0e\u4eba\u6495\u62fc\uff0c\u5c31\u5b58\u7740\u9e64\u868c\u76f8\u4e89\uff0c\u6e14\u4eba\u5f97 \u5229\u7684\u5fc3\u7406\uff0c\u662f\u4ee5\u53ea\u662f\u5728\u4e00\u65c1\u89c2\u770b\uff0c\u60f3\u7b49\u5230\u91d1\u6b39\u6218\u8d25\uff0c\u81f3\u4e0d\u6d4e\u4e5f\u7b49\u5230\u91d1\u6b39\u6218\u5f97\u7d2f\u4e86\u65f6\uff0c\u624d \u51fa\u624b\uff0c\u90a3\u65f6\u53ea\u5269\u4e86\u91d1\u6885\u9f84\u4e00\u4eba\uff0c\u51ed\u7740\u81ea\u5df1\u4e09\u4eba\u4e4b\u529b\uff0c\u5c82\u975e\u592a\u7b80\u5355\u4e86\u3002 \u3000\u3000\u4ed6\u53ea\u5f53\u8f9b\u6377\u4e5f\u662f\u91d1\u4e00\u9e4f\u4e4b\u4ec7\u654c\uff0c\u90a3\u77e5\u4ed6\u4eec\u5176\u4e2d\u5173\u7cfb\u5f88\u590d\u6742\u5462\uff1f\u6b64\u523b\u8f9b\u6377\u8bdd\u4e2d\u5e26\u523a\uff0c \u5374\u6b63\u523a\u4e2d\u4ed6\u7684\u5fc3\u75c5\uff0c\u4f46\u4ed6\u53ef\u4e0d\u613f\u5728\u6ca1\u6709\u6e05\u695a\u8f9b\u6377\u6765\u5386\u4e4b\u524d\uff0c\u7ed3\u4e0b\u8fd9\u4e2a\u6881\u5b50\uff0c\u4ed6\u4e0d\u4f46\u5fc3\u72e0 \u624b\u8fa3\uff0c\u800c\u4e14\u5978\u72e1\u9634\u6c89\uff0c\u95fb\u8a00\u4e0d\u52a8\u58f0\u8272\u3002\u6bd2\u90ce\u541b\u5510\u7075\u5374\u9634\u7b11\u9053\uff1a\u201c\u670b\u53cb\u53ef\u4e0d\u662f\u91d1\u4e00\u9e4f\u7684\u5973 \u5a7f\uff0c\u6211\u529d\u9601\u4e0b\u8fd8\u662f\u5c11\u7ba1\u95f2\u4e8b\u7684\u597d\u3002\u201d \u3000\u3000\u8f9b\u6377\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u82e5\u7136\u662f\u91d1\u4e00\u9e4f\u7684\u5973\u5a7f\u5462\uff1f\u201d \u3000\u3000\u6bd2\u90ce\u541b\u8138\u4e00\u6c89\uff0c\u63a2\u624b\u4eba\u56ca\uff0c\u63a5\u7740\u624b\u4e00\u626c\uff0c\u53d1\u51fa\u51e0\u7c92\u6781\u5c0f\u7684\u6697\u5668\uff0c\u5206\u53d6\u8f9b\u6377\u7684\u5589\u5934\u3001 \u4e24\u80a9\u3001\u524d\u80f8\u3001\u5c0f\u8179\u3002 \u3000\u3000\u4ed6\u63a2\u624b\u4eba\u56ca\uff0c\u53d6\u51fa\u6697\u5668\uff0c\u63a5\u7740\u53d1\u51fa\uff0c\u51e0\u4e4e\u662f\u5728\u540c\u4e00\u5239\u90a3\u91cc\u5b8c\u6210\uff0c\u771f\u662f\u5feb\u5230\u6781\u5904\uff0c\u52a0 \u4e0a\u8fd9\u6697\u5668\u53d1\u51fa\u65f6\u65e0\u58f0\u65e0\u8272\uff0c\u7aef\u7684\u9738\u9053\u5df2\u6781\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u751f\u5e73\u4e0d\u7528\u6697\u5668\uff0c\u5374\u628a\u5929\u4e0b\u5404\u95e8\u6697\u5668\u7684\u6765\u5386\u7834\u6cd5\uff0c\u90fd\u5f04\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4ed6\u81ea\u4e5f \u503e\u56ca\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u77f3\u5ba4\u5341\u5e74\uff0c\u6697\u4e2d\u80fd\u8fa8\u79cb\u6beb\uff0c\u4f55\u51b5\u5728\u8fd9\u5149\u5929\u5316\u65e5\u4e4b\u4e0b\uff0c\u4ed6\u77e5\u9053\u8fd9\u51c6\u5c31\u662f\u201c\u5510\u95e8\u6697 \u5668\u2018\u6bd2\u7802\u3001\u6bd2\u84ba\u9ece\u2019\u4e86\uff0c\u4e00\u58f0\u51b7\u7b11\uff0c\u5bbd\u5927\u7684\u8863\u8896\u4e00\u5c55\uff0c\u4ed6\u5de6\u80a9\u867d\u5df2\u5fae\u611f\u4e0d\u4fbf\uff0c\u4f46\u53f3\u624b\u4ecd \u7136\u65e0\u788d\uff0c\u888d\u8896\u5e26\u8d77\u4e00\u9635\u52b2\u98ce\uff0c\u5c06\u8fd9\u516d\u7c92\u6697\u5668\u90fd\u6325\u843d\u5728\u8fdc\u8fdc\u7684\u5730\u4e0a\uff0c\u8eab\u5f71\u7adf\u672b\u79fb\u52a8\u534a\u6b65\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u51fa\u624b\uff0c\u975e\u4f46\u5510\u95e8\u8001\u5c11\u4e09\u4eba\u5927\u5403\u4e00\u60ca\u3002\u5929\u9b54\u91d1\u6b39\u4e5f\u608d\u7136\u8272\u53d8\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u65af\u8fd9 \u4e00\u624b\u529f\u529b\u4e4b\u9ad8\uff0c\u771f\u662f\u6211\u751f\u5e73\u4ec5\u89c1\uff0c\u4f46\u662f\u4ed6\u65b9\u624d\u548c\u6211\u52a8\u624b\u7684\u65f6\u5019\uff0c\u5374\u50cf\u5e76\u6ca1\u6709\u8fd9\u4e48\u6df1\u7684\u529f \u529b\u5440\u3002\u201d \u3000\u3000\u4ed6\u600e\u4f1a\u77e5\u9053\u8f9b\u6377\u4e34\u654c\u7684\u7ecf\u9a8c\uff0c\u7b80\u76f4\u53ef\u4ee5\u8bf4\u6ca1\u6709\uff0c\u666e\u901a\u521d\u51fa\u6c5f\u6e56\u7684\u540e\u8d77\u4e4b\u79c0\uff0c\u867d\u7136\u4e34 \u654c\u7ecf\u9a8c\u4e0d\u5c11\uff0c\u4f46\u5728\u5e08\u95e8\u65f6\uff0c\u591a\u5c11\u90fd\u6709\u56e0\u5e08\u7236\u6216\u540c\u95e8\u5e08\u5144\u5f1f\u8fc7\u624b\u5582\u62db\uff0c\u800c\u8f9b\u6377\u5374\u6839\u672c\u8fde\u4e00 \u70b9\u7ecf\u9a8c\u90e8\u6ca1\u6709\uff0c\u662f\u4ee5\u4ed6\u548c\u91d1\u6b39\u52a8\u624b\u65f6\uff0c\u5341\u6210\u529f\u592b\u6700\u591a\u53ea\u4f7f\u51fa\u516d\u6210\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u6b64\u523b\u63a5\u6697\u5668\u65f6\uff0c\u5374\u662f\u6c14\u5b9a\u795e\u8db3\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u5c06\u90a3\u4e9b\u522b\u4eba\u8089\u773c\u5f88\u96be\u770b\u5f97\u6e05\u695a\u7684 \u6697\u5668\u770b\u5f97\u6e05\u695a\u5df2\u6781\u3002 \u3000\u3000\u987b\u77e5\u5510\u95e8\u6697\u5668\u4e4b\u6240\u4ee5\u80fd\u626c\u540d\u5929\u4e0b\uff0c\u65e0\u58f0\u65e0\u8272\u4e5f\u662f\u5176\u4e2d\u7edd\u5927\u7684\u539f\u56e0\uff0c\u56e0\u4e3a\u6697\u5668\u80fd\u65e0\u58f0 \u65e0\u8272\uff0c\u6559\u4eba\u600e\u4e48\u53bb\u8eb2\u3002 \u3000\u3000\u8f9b\u6377\u80fd\u5c06\u522b\u4eba\u770b\u5f97\u6700\u56f0\u96be\u4e4b\u5904\u770b\u5f97\u6e05\u63cf\u6de1\u5199\uff0c\u662f\u4ee5\u4ed6\u89c9\u5f97\u5510\u95e8\u7684\u6697\u5668\u6839\u672c\u6beb\u65e0\u53ef\u6015\uff0c \u751a\u81f3\u8fd8\u6709\u70b9\u602a\u201c\u6885\u53d4\u53d4\u201d\u5c06\u5b83\u4eec\u8bb2\u5f97\u8fc7\u751a\u5176\u8bcd\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u522b\u4eba\u6b64\u523b\u5bf9\u4ed6\u7684\u611f\u89c9\u3002 \u3000\u3000\u5510\u658c\u4e00\u63a0\u800c\u5230\u8f9b\u6377\u7684\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u670b\u53cb\u7684\u786e\u8981\u5f97\uff0c\u60f3\u6765\u5fc5\u662f\u540d\u5e08\u4e4b\u5f92\uff0c\u53ea\u662f\u73b0\u5728 \u6c5f\u6e56\u591f\u8d44\u683c\u5f53\u9601\u4e0b\u7684\u5e08\u5085\u7684\u4eba\u8fd8\u4e0d\u591a\uff0c\u5982\u679c\u6211\u5510\u658c\u8001\u773c\u4e0d\u82b1\uff0c\u5c0a\u5e08\u5927\u6982\u5c31\u662f\u5f53\u4eca\u5929\u4e0b\u7b2c \u4e00\u4eba\u7269\uff0c\u5251\u795e\u5389\u5927\u4fa0\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff1a\u201c\u4f60\u7684\u8001\u773c\u82b1\u900f\u4e86\u3002\u201d \u3000\u3000\u5510\u658c\u89c1\u4ed6\u4e0d\u8bf4\u8bdd\uff0c\u4ee5\u4e3a\u4ed6\u5df2\u9ed8\u8ba4\uff0c\u53c8\u9053\uff1a\u201c\u8001\u673d\u548c\u5389\u5927\u4fa0\u672c\u662f\u7d20\u8bc6\uff0c\u548c\u9601\u4e0b\u51e0\u4f4d\u5e08 \u5144\u5f1f\u4e5f\u6709\u6570\u9762\u4e4b\u7f18\uff0c\u5c31\u662f\u4e0d\u66fe\u89c1\u8fc7\u9601\u4e0b\uff0c\u4f46\u8bb2\u8d77\u6765\u4e5f\u53ef\u7b97\u4e00\u5bb6\u4eba\uff0c\u9601\u4e0b\u4f55\u5fc5\u6765\u6dcc\u8fd9\u8d9f\u6d51 \u6c34\u3002\u201d \u3000\u3000\u4ed6\u6ee1\u4ee5\u4e3a\u8fd9\u756a\u8bdd\u8bb2\u5f97\u5df2\u53ef\u7b97\u9762\u9762\u4ff1\u5230\uff0c\u7686\u56e0\u4ed6\u975e\u4f46\u4e0d\u613f\u60f9\u5d06\u5cd2\u6d3e\uff0c\u4e5f\u4e0d\u613f\u6b64\u4e8b\u591a\u4e00 \u9ad8\u624b\u63d2\u5165\uff0c\u6240\u4ee5\u5e0c\u671b\u8f9b\u6377\u6700\u597d\u6492\u624b\u4e0d\u7ba1\u3002 \u3000\u3000\u90a3\u77e5\u8f9b\u6377\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u5510\u8001\u82f1\u96c4\u8bf4\u7684\u8bdd\uff0c\u5728\u4e0b\u4e00\u70b9\u4e5f\u4e0d\u61c2\uff0c\u4ec0\u4e48\u5251\u795e\u5389\u5927\u4fa0\uff0c \u5728\u4e0b\u66f4\u662f\u8fde\u8ba4\u90fd\u4e0d\u8ba4\u5f97\uff0c\u5510\u8001\u82f1\u96c4\u7684\u4e8b\uff0c\u5728\u4e0b\u66f4\u4e0d\u6562\u7ba1\uff0c\u60f3\u5728\u4e0b\u4e00\u4e2a\u65e0\u540d\u5c0f\u5352\uff0c\u54ea\u6709\u4ec0 \u4e48\u540d\u5e08\uff0c\u4e0d\u8fc7\u4ed6\u7b11\u5bb9\u4e00\u655b\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u662f\u4e00\u5b97\u4e8b\uff0c\u5728\u4e0b\u5374\u8981\u5411\u5510\u8001\u82f1\u96c4\u6c42\u4e2a\u65b9\u4fbf\u3002\u201d \u3000\u3000\u5510\u658c\u5fd9\u9053\uff1a\u201c\u53ea\u7ba1\u8bf4\u3002\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u5510\u8001\u82f1\u96c4\u4eca\u5929\u5356\u533a\u533a\u5728\u4e0b\u4e00\u4e2a\u9762\u5b50\uff0c\u653e\u8fc7\u6b64\u4e8b\uff0c\u5929\u957f\u5730\u4e45\uff0c\u5728\u4e0b\u65e5\u540e\u5fc5 \u6709\u8865\u62a5\u4e4b\u5904\uff0c\u6c5f\u6e56\u4e0a\u4eba\u95fb\u5f97\u6b64\u4e8b\uff0c\u5fc5\u5b9a\u4f1a\u8bf4\u5510\u8001\u82f1\u96c4\u5bbd\u5b8f\u5927\u91cf\uff0c\u4e0d\u548c\u8fd9\u540e\u751f\u5c0f\u8f88\u4e00\u822c\u89c1 \u8bc6\u3002\u201d \u3000\u3000\u5510\u658c\u4e00\u8fde\u58f0\u51b7\u7b11\uff0c\u53cc\u76ee\u4e00\u5f20\uff0c\u8bf4\u9053\uff1a\u201c\u82e5\u662f\u6211\u4e0d\u5356\u9601\u4e0b\u7684\u9762\u5b50\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u90a3\u4e48\u5728\u4e0b\u53ea\u6709\u6492\u624b\u4e0d\u7ba1\uff0c\u8ba9\u5510\u8001\u82f1\u96c4\u5bf9\u91d1\u4e00\u9e4f\u7684\u540e\u4eba\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6b64\u8bdd\u4e00\u51fa\uff0c\u5728\u573a\u4e94\u4eba\u90fd\u611f\u5230\u5927\u51fa\u610f\u5916\uff0c\u518d\u4e5f\u60f3\u4e0d\u5230\u4ed6\u4f1a\u8bf4\u51fa\u8fd9\u8bdd\u6765\u3002 \u3000\u3000\u5c24\u5176\u662f\u91d1\u6885\u9f84\uff0c\u5fc3\u4e00\u51b7\uff0c\u51e0\u4e4e\u6c14\u5f97\u6655\u4e86\u8fc7\u53bb\uff0c\u6697\u5fd6\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u5bf9\u4ed6\u60c5\u6df1\u4f3c\u6d77\uff0c\u5374 \u6362\u5f97\u4ed6\u8fd9\u6837\u4e00\u53e5\u8bdd\uff0c\u7f62\uff0c\u7f62\uff0c\u6211\u4e5f\u5c31\u6b7b\u5728\u4ed6\u9762\u524d\u3002\u201d\u53cd\u800c\u4e0d\u4f1a\u8bf4\u8bdd\u4e86\u3002 \u3000\u3000\u5510\u658c\u4e5f\u662f\u4e00\u6115\uff0c\u968f\u5373\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u5012\u662f\u4e2a\u806a\u660e\u4eba\u7269\uff0c\u89c1\u673a\u6536\u84ec\uff0c\u771f\u662f\u4e0d\u5403\u773c\u524d\u4e8f\u7684 \u5149\u68cd\u3002\u201d \u3000\u3000\u968f\u7740\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u65e2\u662f\u5982\u6b64\uff0c\u90a3\u597d\u6781\u4e86\uff0c\u6211\u5510\u67d0\u4eba\u611f\u6fc0\u4e0d\u5c3d\u3002\u201d \u3000\u3000\u201c\u4e0d\u8fc7\uff0c\u201d\u8f9b\u6377\u4ecd\u7b11\u563b\u563b\u5730\u8bf4\u9053\uff0c\u201c\u5510\u8001\u82f1\u96c4\u53ea\u662f\u8981\u5bf9\u4ed8\u91d1\u4e00\u9e4f\u7684\u4eb2\u4eba\uff0c\u82e5\u975e\u91d1\u4e00 \u9e4f\u7684\u4eb2\u4eba\uff0c\u5510\u8001\u82f1\u96c4\u60f3\u5fc5\u4e5f\u4e0d\u4f1a\u52a8\u624b\u7684\u3002\u201d \u3000\u3000\u201c\u90a3\u81ea\u7136\u3002\u201d\u5510\u658c\u5728\u5947\u602a\u7740\u8f9b\u6377\u7684\u8bdd\u3002 \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u90a3\u4e48\u73b0\u5728\u6b64\u5904\uff0c\u9664\u4e86\u8fd9\u4f4d\u5929\u9b54\u91d1\u6b39\u4e4b\u5916\uff0c\u5c31\u518d\u4e5f\u6ca1\u6709\u91d1\u4e00\u9e4f\u7684 \u540e\u4eba\u4e86\u3002\u201d \u3000\u3000\u5510\u7075\u51b7\u7b11\u63d2\u53e3\u9053\uff1a\u201c\u53ea\u6015\u8fd8\u6709\u4e00\u4e2a\u5427\uff01\u201d\u8f9b\u6377\u9053\uff1a\u201c\u53ea\u6015\u6ca1\u6709\u4e86\u5427\uff01\u201d \u3000\u3000\u5510\u7075\u9053\uff1a\u201c\u6211\u63a2\u8bbf\u8fd9\u91d1\u4e00\u9e4f\u4e0d\u6b62\u4e00\u65e5\uff0c\u96be\u9053\u8fde\u8fd9\u5c0f\u59d0\u662f\u91d1\u4e00\u9e4f\u7684\u5973\u513f\u6211\u90fd\u4e0d\u77e5\u9053\uff0c \u670b\u53cb\u82e5\u8981\u62ff\u6211\u5510\u7075\u770b\u505a\u5446\u5b50\uff0c\u90a3\u670b\u53cb\u4f60\u5c31\u6253\u9519\u7b97\u76d8\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u6b63\u662f\u533a\u533a\u5728\u4e0b\u7684\u59bb\u5b50\uff0c\u6211\u96be\u9053\u8fde\u5979\u662f\u4e0d\u662f\u91d1\u4e00\u9e4f\u7684 \u540e\u4eba\u90fd\u4e0d\u77e5\u9053\uff0c\u987b\u77e5\u9601\u4e0b\u4e0d\u662f\u5446\u5b50\uff0c\u533a\u533a\u5728\u4e0b\u4e5f\u4e0d\u662f\u5446\u5b50\u5462\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u8fd9\u624d\u604d\u7136\u4e86\u89e3\u4e86\u8f9b\u6377\u7684\u7528\u610f\u3002 \u3000\u3000\u987b\u77e5\u8f9b\u6377\u751f\u6027\u5947\u7279\uff0c\u4ece\u4e0d\u613f\u505a\u65e0\u6cd5\u505a\u5230\u4e4b\u4e8b\uff0c\u4ed6\u7565\u4e00\u601d\u5fd6\uff0c\u5bf9\u65b9\u4e09\u4e2a\u770b\u6765\u4ff1\u662f\u80fd\u624b\uff0c \u800c\u81ea\u5df1\u5374\u5df2\u534a\u8eab\u8fd0\u8f6c\u4e0d\u7075\uff0c\u91d1\u6094\u9f84\u4ea6\u662f\u997f\u75b2\u4ea4\u96c6\uff0c\u4f55\u51b5\u7ecf\u8fc7\u6628\u5929\u90a3\u4e00\u756a\u4e8b\u540e\uff0c\u5979\u8eab\u4f53\u53ea \u6015\u66f4\u5f31\u3002 \u3000\u3000\u81f3\u4e8e\u5929\u9b54\u91d1\u6b39\u5462\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u81ea\u7136\u4e5f\u4f1a\u548c\u81ea\u5df1\u8054\u624b\u4e3a\u654c\uff0c\u4f46\u8f9b\u6377\u5f53\u7136\u77e5\u9053\u81ea\u5df1 \u7684\u638c\u529b\uff0c\u4ed6\u4e2d\u4e86\u81ea\u5df1\u4e00\u638c\u6015\u5df2\u91cd\u4f24\uff0c\u62ff\u8fd9\u4e09\u4eba\u548c\u5bf9\u65b9\u4e00\u6bd4\uff0c\u80dc\u7b97\u7edd\u5c11\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u8f9b\u6377 \u51b3\u5b9a\u4e86\u8ba1\u5212\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u624d\u6709\u5982\u6b64\u4e4b\u8bf4\u3002 \u3000\u3000\u5510\u658c\u542c\u4e86\u8f9b\u6377\u6b64\u8bdd\uff0c\u53c8\u90fd\u4e00\u6115\uff0c\u5510\u7075\u6012\u559d\u9053\uff1a\u201c\u4f60\u9a97\u8c01\uff1f\u4e8c\u53d4\uff0c\u6211\u4eec\u4e0d\u8981\u542c\u4ed6\u7684\u9b3c \u8bdd\u3002\u201d \u3000\u3000\u5510\u658c\u6b63\u4f4e\u5934\u6c89\u601d\u7740\uff0c\u5ffd\u5730\u62ac\u5934\u95ee\u9053\uff1a\u201c\u4f60\u6b64\u8bdd\u5f53\u771f\u3002\u201d \u3000\u3000\u201c\u8c01\u4e2a\u9a97\u4f60\u4e0d\u6210\uff1f\u201d\u8f9b\u6377\u6602\u7136\u9053\uff0c\u201c\u5728\u4e0b\u867d\u662f\u6c5f\u6e56\u4e0a\u7684\u65e0\u540d\u5c0f\u5352\uff0c\u53ef\u5374\u4e0d\u662f\u4e71\u8bf4\u901b \u8bdd\u7684\u9a97\u5b50\u3002\u201d \u3000\u3000\u5510\u658c\u7709\u5934\u4e00\u76b1\uff0c\u53cc\u76ee\u5982\u5200\uff0c\u7d27\u7d27\u76ef\u5728\u8f9b\u6377\u9762\u4e0a\uff0c\u5ffd\u5730\u8bf4\u9053\uff1a\u201c\u7075\u513f\u3001\u66fc\u513f\uff0c\u5c06\u90a3\u4e2a \u59d3\u91d1\u7684\u62fe\u593a\u4e0b\u6765\u3002\u201d \u3000\u3000\u5510\u7075\u3001\u5510\u66fc\u5e94\u4e86\u4e00\u58f0\uff0c\u5404\u5404\u4e00\u63a2\u624b\uff0c\u62bd\u51fa\u4e00\u6761\u8f6f\u91d1\u97ad\u6765\uff0c\u6b63\u4e5f\u662f\u5510\u95e8\u7684\u72ec\u95e8\u5175\u5203\uff0c \u901a\u4f53\u7eaf\u91d1\u6240\u5236\uff0c\u53ef\u67d4\u53ef\u521a\uff0c\u62db\u5f0f\u4ea6\u53e6\u6210\u4e00\u5bb6\u3002 \u3000\u3000\u987b\u77e5\u56db\u5ddd\u5510\u95e8\u5a01\u9707\u6b66\u6797\uff0c\u9664\u4e86\u201c\u4e09\u6697\u5668\u201d\u4e4b\u5916\uff0c\u638c\u4e2d\u8f6f\u91d1\u97ad\u201c\u4e03\u715e\u593a\u547d\u97ad\u6cd5\u201d\u4e5f\u5b9e \u6709\u5947\u5999\u7684\u62db\u5f0f\uff0c\u800c\u4e14\u6700\u5389\u5bb3\u7684\u662f\u5510\u95e8\u4e2d\u4eba\uff0c\u6697\u5668\u7686\u4e3a\u5de6\u624b\u53d1\u51fa\uff0c\u662f\u4ee5\u97ad\u6cd5\u65bd\u5c55\u4e2d\uff0c\u53c8\u53ef \u5939\u4ee5\u6697\u5668\uff0c\u4ee4\u4eba\u8eb2\u5f97\u4e86\u97ad\uff0c\u8eb2\u4e0d\u4e86\u6697\u5668\uff0c\u5510\u95e8\u767e\u4f59\u5e74\u6765\uff0c\u7ad9\u7acb\u6b66\u6797\uff0c\u5c31\u7b97\u4e94\u5927\u5b97\u6d3e\uff0c\u4e5f \u8981\u8ba9\u4ed6\u4e09\u5206\uff0c\u5c31\u662f\u8fd9\u4e2a\u539f\u56e0\u3002 \u3000\u3000\u4e24\u4eba\u957f\u97ad\u4e00\u51fa\u624b\uff0c\u8eab\u5f62\u52a8\u5904\uff0c\u4e24\u9053\u74a8\u91d1\u8272\u7684\u5149\u8292\uff0c\u5206\u70b9\u91d1\u6b39\u201c\u671f\u95e8\u201d\u201c\u7acb\u5173\u201d\u4e24\u5904 \u5927\u7a74\uff0c\u5c45\u7136\u5c06\u8f6f\u5175\u5203\u5f53\u505a\u70b9\u7a74\u5668\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u867d\u5df1\u91cd\u4f24\uff0c\u4f46\u4f59\u5a01\u5c24\u5728\uff0c\u8eab\u5f62\u4e00\u9519\uff0c\u4ece\u97ad\u5149\u7684\u7a7a\u9699\u4e2d\u7a7f\u4e86\u51fa \u53bb\uff0c\u5237\uff0c\u5237\uff0c\u4e24\u638c\u5206\u88ad\u5510\u7075\u3001\u5510\u66fc\u4e24\u4eba\uff0c\u53e3\u4e2d\u5927\u559d\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u96be\u9053\u4f60\u771f\u4e0d\u8ba4\u5e08\u5085\u4e86\uff1f\u201d \u5237\uff0c\u5237\uff0c\u53c8\u662f\u4e24\u638c\u3002 \u3000\u3000\u5510\u658c\u671d\u8f9b\u6377\u51b7\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u4e0d\u662f\u91d1\u4e00\u9e4f\u7684\u540e\u4eba\uff0c\u662f\u8c01\u7684\u540e\u4eba\uff1f\u201d\u8f9b\u6377\u65b9\u8981\u7b54\u8bdd\uff0c \u90a3\u77e5\u91d1\u6885\u9f84\u7a81\u7136\u6325\u8131\u4e86\u4ed6\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u4e00\u9e4f\u662f\u6211\u7684\u7239\u7239\uff0c\u4f60\u4eec\u53ea\u7ba1\u4e0a\u6765\u5c31\u662f\u4e86\uff0c\u59d1 \u5a18\u4e5f\u4e0d\u542b\u7cca\u3002\u201d \u3000\u3000\u5510\u658c\u54c8\u54c8\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u8fd9\u624d\u6709\u5fd7\u6c14\u3002\u201d\u8bdd\u672b\u8bf4\u5b8c\uff0c\u5288\u9762\u4e00\u638c\uff0c\u4ed6\u81ea\u6043\u8eab\u4efd\uff0c \u6ca1\u6709\u4eae\u51fa\u5175\u5203\u6765\uff0c\u5bf9\u4ed8\u8fd9\u7a7a\u624b\u7684\u540e\u8f88\u3002 \u3000\u3000\u5c40\u9762\u6025\u8f6c\uff0c\u8f9b\u6377\u77e5\u9053\u81ea\u5df1\u4eca\u65e5\u8981\u60f3\u7f6e\u8eab\u4e8b\u5916\u5df2\u4e0d\u53ef\u80fd\uff0c\u987b\u77e5\u4ed6\u867d\u80fd\u773c\u770b\u7740\u201c\u6d77\u5929\u53cc \u715e\u201d\u6b3a\u51cc\u4ed6\u7684\u6bcd\u4eb2\uff0c\u6740\u6b7b\u4ed6\u7684\u7236\u4eb2\uff0c\u4f46\u90a3\u65f6\u4ed6\u53ea\u662f\u4e2a\u5e7c\u7ae5\uff0c\u60c5\u51b5\u548c\u73b0\u65f6\u5927\u5927\u4e0d\u540c\uff0c\u6b64\u523b \u4ed6\u8eab\u6000\u7edd\u6280\uff0c\u600e\u80fd\u51b7\u773c\u65c1\u89c2\u91d1\u6885\u9f84\u548c\u65c1\u4eba\u7684\u751f\u6b7b\u640f\u6597\uff0c\u4f55\u51b5\u82e5\u7136\u81ea\u5df1\u4e00\u62fc\uff0c\u4e5f\u5e76\u975e\u7edd\u65e0 \u81f4\u80dc\u53ef\u80fd\u3002 \u3000\u3000\u4ed6\u65b9\u81ea\u51c6\u5907\u52a8\u624b\uff0c\u773c\u770b\u5c31\u662f\u4e00\u756a\u6df7\u6218\uff0c\u53cc\u65b9\u7684\u751f\u6b7b\uff0c\u90fd\u5728\u672a\u53ef\u77e5\u4e4b\u6570\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\u5374\u51fa\u4e86\u4e00\u5b97\u60ca\u4eba\u4e4b\u4e8b\uff0c\u4f7f\u5f97\u8fd9\u4e9b\u4eba\u5168\u90fd\u4f4f\u4e86\u624b\u3002\u539f\u6765\u6b64\u523b\u5cb8\u4e0a\u7a81\u7136\u51fa\u73b0\u4e00 \u4e2a\u8eab\u62ab\u8f7b\u7eb1\u7684\u5c11\u5973\uff0c\u88f3\u88f3\u884c\u6765\uff0c\u4e00\u9762\u5a07\u58f0\u9053\uff1a\u201c\u54df\uff0c\u4f60\u4eec\u4e0d\u8981\u6253\u67b6\u561b\uff0c\u6253\u5f97\u4eba\u5bb6\u70e6\u6b7b \u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b49\u516d\u4eba\u4ff1\u90fd\u5403\u4e86\u4e00\u60ca\uff0c\u7686\u56e0\u4ed6\u4eec\u6240\u5728\u4e4b\u5730\u6781\u4e3a\u7a7a\u65f7\uff0c\u8fd9\u5c11\u5973\u5c45\u7136\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9 \u7a81\u7136\u73b0\u8eab\uff0c\u987b\u77e5\u4ed6\u7b49\u516d\u4eba\u4ff1\u4e3a\u6b66\u6797\u4e2d\u4e00\u7b49\u4e00\u7684\u89d2\u8272\uff0c\u5341\u4e08\u65b9\u5706\u5185\uff0c\u98de\u82b1\u843d\u53f6\uff0c\u90fd\u80fd\u60ca\u89c9\uff0c \u800c\u8fd9\u5c11\u5973\u4e00\u76f4\u6765\u5230\u4ed6\u4eec\u8fd1\u524d\uff0c\u4ed6\u4eec\u65b9\u81ea\u53d1\u89c9\uff0c\u5982\u4f55\u4e0d\u60ca\uff1f \u3000\u3000\u8f9b\u6377\u89c1\u8fd9\u5c11\u5973\u6700\u591a\u53ea\u6709\u5341\u516d\u3001\u4e03\u5c81\uff0c\u8eab\u4e0a\u53ea\u62ab\u7740\u4e00\u5927\u7247\u7eaf\u767d\u7684\u8f7b\u7eb1\uff0c\u5c06\u8eab\u4f53\u88f9\u5728\u8fd9 \u8f7b\u7eb1\u91cc\uff0c\u660e\u9635\u5982\u661f\uff0c\u80a4\u8272\u5982\u7389\uff0c\u886c\u7740\u8fd9\u8f7b\u7eb1\uff0c\u8fd9\u4f53\u6001\uff0c\u7f8e\u5f97\u4e0d\u50cf\u4eba\u7c7b\uff0c\u800c\u50cf\u662f\u4e5d\u5929\u4ed9\u5b50\u3002 \u9664\u4e86\u7f8e\u4e4b\u5916\uff0c\u5979\u4ee4\u4eba\u89c1\u4e86\uff0c\u6709\u4e00\u79cd\u51fa\u5c18\u7684\u611f\u89c9\uff0c\u8f9b\u6377\u6697\u5fd6\uff1a\u201c\u8fd9\u5c11\u5973\u771f\u7f8e\uff0c\u9f84\u59b9\u59b9\u3001\u74a7 \u59b9\u59b9\u6211\u672c\u6765\u5df2\u7ecf\u4ee5\u4e3a\u5f88\u5c11\u6709\u4eba\u518d\u7f8e\u5f97\u8fc7\u5979\u4eec\u4e86\uff0c\u53ef\u662f\u548c\u8fd9\u5c11\u5973\u4e00\u6bd4\uff0c\u90a3\u7b80\u76f4\u6bd4\u90fd\u65e0\u6cd5\u6bd4 \u5462\u3002\u201d \u3000\u3000\u9664\u4e86\u5510\u658c\u4e4b\u5916\uff0c\u4ed6\u4eec\u90fd\u88ab\u8fd9\u5c11\u5973\u7684\u7f8e\u6240\u8ff7\u60d1\u4e86\uff0c\u91d1\u6885\u9f84\u4e0d\u81ea\u89c9\u5730\u7406\u4e86\u7406\u51cc\u4e71\u7684\u9b13\u53d1\uff0c \u6697\u5fd6\u9053\uff1a\u201c\u4e0d\u77e5\u9053\u6211\u6bd4\u8d77\u8fd9\u5c11\u5973\u6765\u600e\u6837\u2026\u2026\u201d\u4fa7\u8138\u4e00\u770b\u8f9b\u6377\u7684\u795e\u8272\uff0c\u6697\u53f9\u9053\uff1a\u201c\u770b\u6765\u6211 \u662f\u6bd4\u4e0d\u4e0a\u5979\u7684\u4e86\u201d \u3000\u3000\u5510\u8d4b\u5374\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5973\u4ece\u90a3\u91cc\u6765\u7684\uff0c\u8fd9\u51e0\u4e4e\u662f\u4e0d\u53ef\u80fd\u7684\u5440\uff0c\u5979\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u516d\u4eba\u5fc3\u601d\u867d\u4e0d\u540c\uff0c\u4f46\u5374\u90fd\u88ab\u8fd9\u7a81\u6765\u7684\u5c11\u5973\u6240\u9707\u4f4f\u4e86\uff0c\u5341\u4e8c\u53ea\u773c\u775b\uff0c\u77ac\u4e5f\u4e0d\u77ac\u5730\u76ef\u5728 \u8fd9\u5c11\u5973\u8138\u4e0a\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5ae3\u7136\u4e00\u7b11\uff0c\u9732\u51fa\u4e00\u6392\u6676\u83b9\u7684\u7259\u9f7f\uff0c\u5de7\u7b11\u9053\uff1a\u201c\u6253\u67b6\u53c8\u6709\u4ec0\u4e48\u597d\u73a9\uff1f\u4f60\u4eec\u8981\u662f \u6ca1\u6709\u4e8b\u505a\uff0c\u6349\u6349\u8ff7\u85cf\u4e5f\u597d\uff0c\u4f55\u5fc5\u6253\u67b6\u5462\uff1f \u3000\u3000\u5988\u5988\u8bf4\u559c\u6b22\u6253\u67b6\u7684\u90fd\u4e0d\u662f\u597d\u4eba\uff0c\u54ce\uff01\u4f60\u4eec\u662f\u4e0d\u662f\u597d\u4eba\u5440\uff1f\u201d \u3000\u3000\u5510\u658c\u7b49\u542c\u4e86\u4e00\u4e2a\u4e2a\u54ed\u7b11\u4e0d\u5f97\u3002 \u3000\u3000\u5510\u658c\u7eb5\u6a2a\u6c5f\u6e56\u591a\u5e74\uff0c\u7d20\u6709\u50ac\u547d\u7b26\u4e4b\u79f0\uff0c\u6b66\u6797\u4e2d\u89c1\u4e4b\uff0c\u754f\u5982\u86c7\u874e\uff0c\u73b0\u5728\u5374\u88ab\u4e00\u4e2a\u5c0f\u5973 \u5b69\u5f53\u505a\u5b69\u7ae5\u770b\uff0c\u4ed6\u6697\u6697\u53d1\u6012\uff0c\u4f46\u8fd9\u5c11\u5973\u4e0d \u3000\u3000\u4f46\u8273\u82e5\u5929\u4eba\uff0c\u800c\u4e14\u884c\u8ff9\u8be1\u5f02\uff0c\u5510\u658c\u9605\u4eba\u591a\u77e3\uff0c\u5374\u8fd8\u6ca1\u6709\u89c1\u8fc7\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u4ed6\u5ff5\u5934\u8f6c \u4e86\u4e24\u8f6c\uff0c\u5fc3\u4e2d\u60f3\u6b64\u5c11\u5973\u5fc5\u5b9a\u5927\u6709\u6765\u5386\u3002 \u3000\u3000\u4ed6\u6b63\u5f85\u8bf4\u8bdd\uff0c\u54ea\u77e5\u8f9b\u6377\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u597d\u2026\u597d\u2026\u6211\u4eec\u6765\u6349\u8ff7\u85cf\u597d\u4e86\uff0c\u8fd9\u4f4d\u59d1\u5a18\u53c2\u4e0d\u53c2 \u52a0\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u62cd\u624b\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u54e5\u54e5\u4eba\u771f\u597d\uff0c\u6211\u6700\u559c\u6b22\u6349\u8ff7\u85cf\u4e86\uff0c\u53ef\u60dc\u90a3\u4e9b\u4eba\u8dd1\u5f97\u592a\u6162\uff0c \u6211\u4e00\u6349\u5c31\u6349\u4f4f\u4e86\uff0c\u4e00\u70b9\u513f\u610f\u601d\u90fd\u6ca1\u6709\uff0c\u4f60\u4eec\u4e00\u5b9a\u8dd1\u5f97\u6bd4\u4ed6\u4eec\u5feb\uff0c\u6211\u5148\u6765\u505a\u9b3c\uff0c\u4f60\u4eec\u8c01\u88ab \u6211\u6349\u4f4f\u4e86\uff0c\u8c01\u5c31\u66ff\u6211\u505a\u9b3c\uff0c\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5510\u658c\u7b49\u542c\u4e86\u505a\u58f0\u4e0d\u5f97\uff0c\u5929\u9b54\u91d1\u6b39\u813e\u6c14\u6700\u574f\uff0c\u800c\u4e14\u5929\u6027\u51c9\u8584\uff0c\u8fde\u4eb2\u751f\u4e4b\u7236\u90fd\u5fcd\u5fc3\u6740\u6b7b\uff0c \u73b0\u5728\u53eb\u4ed6\u6765\u6349\u8ff7\u85cf\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5c31\u5f85\u53d1\u4f5c\uff0c\u90a3\u5c11\u5973\u5374\u8d70\u5230\u4ed6\u9762\u524d\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u6765\u4e0d\u6765 \u5440\uff1f\u201d\u91d1\u6b4c\u88ab\u5979\u76ee\u5149\u4e00\u7167\uff0c\u89c9\u5f97\u5fc3\u9b44\u7686\u4e3a\u6240\u593a\uff0c\u5450\u5450\u5730\u8bf4\u9053\uff1a\u201c\u6211\u6765\uff0c\u6211\u6765\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u53c8\u8d70\u5230\u5510\u7075\u9762\u524d\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u5510\u7075\u672c\u4e3a\u8272\u4e2d\u997f\u9b3c\uff0c\u65e9\u5c31\u88ab\u8fd9\u5c11\u5973\u7684\u7f8e\u8ff7\u5f97\u6655\u6655\u5ffd\u5ffd\uff0c\u95fb\u8a00\u4e00\u53e0\u58f0\u8bf4\u9053\uff1a\u201c\u6765\u2026\u6765\u2026 \u6765\u201d \u3000\u3000\u5510\u658c\u9762\u4e0a\u9634\u6697\u4e0d\u5b9a\uff0c\u4ed6\u62ff\u4e0d\u5b9a\u4e3b\u610f\u8be5\u600e\u4e48\u505a\uff0c\u516d\u4eba\u4e2d\u4ee5\u4ed6\u9605\u5386\u6700\u4e30\uff0c\u4ed6\u5148\u524d\u770b\u5230\u8f9b \u6377\uff0c\u5df2\u5728\u60ca\u5f02\u7740\u6c5f\u6e56\u4e2d\u4ece\u54ea\u91cc\u94bb\u51fa\u6765\u8fd9\u6837\u4e00\u4e2a\u5c11\u5e74\uff0c\u4f46\u8fd8\u5e76\u975e\u4e0d\u53ef\u601d\u8bae\uff0c\u5982\u4eca\u89c1\u4e86\u8fd9\u5c11 \u5973\uff0c\u5374\u771f\u7684\u5947\u602a\u4e86\uff0c\u77e5\u9053\u8fd9\u5c11\u5973\u6ca1\u6709\u8d85\u51e1\u5165\u5723\u7684\u8f7b\u8eab\u529f\u592b\uff0c\u5979\u600e\u80fd\u5728\u8fd9\u516d\u5927\u9ad8\u624b\u9762\u524d\u7a81 \u7136\u73b0\u8eab\uff0c\u800c\u4e14\u662f\u5728\u4e00\u7247\u7a7a\u65f7\u4e4b\u5730\u4e0a\u3002 \u3000\u3000\u4ed6\u6b63\u6697\u91cc\u60ca\u5947\uff0c\u90a3\u5c11\u5973\u5df1\u8d70\u5230\u4ed6\u9762\u524d\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u54e5\u54e5\u4f60\u6765\u4e0d\u6765\u5440\uff1f\u201d \u3000\u3000\u5510\u658c\u8138\u4e00\u7ea2\uff0c\u4ed6\u51fa\u751f\u5230\u4eca\uff0c\u8fd8\u6ca1\u6709\u88ab\u4eba\u53eb\u8fc7\u8001\u54e5\u54e5\uff0c\u88ab\u8fd9\u7f8e\u5982\u5929\u4ed9\u7684\u5c11\u5973\u4e00\u53eb\uff0c\u5fc3 \u91cc\u89c9\u5f97\u6709\u4e9b\u96be\u4e3a\u60c5\uff0c\u5374\u53c8\u53d7\u7528\u5f97\u5f88\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u5c11\u5973\u771f\u662f\u53ef\u7231\u3002\u201d\u4fbf\u4e5f\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u6211 \u4e5f\u53c2\u52a0\u3002\u201d \u3000\u3000\u5510\u66fc\u89c1\u5230\u8fd9\u4f4d\u6740\u4eba\u4e0d\u7728\u773c\u7684\u4e8c\u53d4\uff0c\u5c45\u7136\u4e5f\u6349\u8d77\u8ff7\u85cf\u6765\uff0c\u800c\u4e14\u8138\u4e5f\u7ea2\u4e86\uff0c\u4e0d\u7981\u201c\u5657\u54e7\u201d \u4e00\u58f0\uff0c\u7b11\u51fa\u58f0\u6765\u3002 \u3000\u3000\u5510\u658c\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u5979\u6697\u91cc\u4e00\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u6765\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u8138\u4e0a\u5806\u6ee1\u7b11\u5bb9\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u6765\uff0c\u597d\u6781\u4e86\u3002\u201d\u5979\u8d70\u5230\u8f9b\u6377\u9762\u524d\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d \u54e5\u54e5\uff0c\u4f60\u627e\u5757\u624b\u5e15\u51fa\u6765\uff0c\u628a\u6211\u773c\u775b\u8499\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u8fd9\u5c11\u5973\u7b11\u5f97\u5982\u540c\u767e\u5408\u521d\u653e\uff0c\u4e0d\u7981\u770b\u5f97\u75f4\u4e86\uff0c\u90a3\u5c11\u5973\u53c8\u4e00\u7b11\uff0c\u8138\u4e0a\u7adf\u4f3c\u6cdb\u51fa\u7ea2 \u6f6e\u3002 \u3000\u3000\u91d1\u6885\u9f84\u53c8\u5992\u53c8\u6c14\uff0c\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u6765\u3002\u201d \u3000\u3000\u8f9b\u6377\u671d\u5979\u4fbf\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u5979\u53ea\u5f53\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u90a3\u5c11\u5973\u4e00\u6014\uff0c\u968f\u53c8\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u59d0\u59d0\u4e0d\u6765\u4e5f\u597d\uff0c\u66ff\u6211\u4eec\u505a\u516c\u8bc1\uff0c\u8c01\u4e5f\u4e0d\u8bb8\u8d56\u76ae\u3002\u201d \u3000\u3000\u5510\u658c\u8eab\u5f62\u4e00\u52a8\uff0c\u63a0\u5230\u91d1\u6885\u9f84\u524d\u9762\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u6765\u4e5f\u53ef\u4ee5\uff0c\u53ef\u662f\u5374\u4e0d\u51c6\u9003 \u8d70\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u53c8\u62cd\u624b\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u8001\u54e5\u54e5\u8dd1\u5f97\u771f\u5feb\uff0c\u6bd4\u963f\u82b1\u3001\u963f\u72d7\u4ed6\u4eec\u5feb\u591a\u4e86\u3002\u201d \u3000\u3000\u5510\u658c\u542c\u4e86\u5c11\u5973\u5938\u5956\u4ed6\uff0c\u5fc3\u91cc\u6b63\u9ad8\u5174\uff0c\u5374\u53c8\u542c\u5f97\u5979\u62ff\u81ea\u5df1\u548c\u201c\u963f\u82b1\u201d\u201c\u963f\u72d7\u201d\u6765\u6bd4\uff0c \u6c14\u5f97\u8138\u5b54\u94c1\u9752\uff0c\u8bdd\u4e5f\u8bf4\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8f9b\u6377\u201c\u5657\u54e7\u201d\u4e00\u7b11\uff0c\u5510\u66fc\u56de\u8f6c\u4e86\u8138\uff0c\u5634\u5df4\u9f13\u5f97\u5706\u5706\u7684\uff0c\u539f\u6765\u5979\u60f3\u7b11\uff0c\u53c8\u4e0d\u6562\u7b11\u51fa\u58f0 \u6765\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5999\u76ee\u6a2a\u6ce2\uff0c\u779f\u4e86\u8f9b\u6377\u4e00\u773c\uff0c\u5403\u5403\u7b11\u9053\uff1a\u201c\u5582\uff0c\u4f60\u5feb\u66ff\u6211\u7ed1\u4e00\u5757\u624b\u5e15\u5728\u773c\u775b\u4e0a \u5440\u3002\u201d \u3000\u3000\u8f9b\u6377\u4ece\u6000\u4e2d\u4e00\u638f\uff0c\u62ff\u51fa\u4e00\u5757\u624b\u5e15\uff0c\u4fa7\u773c\u4e00\u770b\u91d1\u6885\u9f84\uff0c\u89c1\u5979\u4e24\u773c\u6b63\u52fe\u52fe\u5730\u770b\u7740\u81ea\u5df1\uff0c \u8138\u4e0a\u53d8\u4e86\u989c\u8272\uff0c\u6697\u7b11\u9053\uff1a\u201c\u5979\u7684\u918b\u52b2\u771f\u5927\u3002\u201d\u4f38\u624b\u5c06\u624b\u5e15\u9012\u7ed9\u90a3\u5c11\u5973\uff0c\u9053\uff1a\u201c\u4f60\u81ea\u5df1\u7ed1 \u5427\uff01\u201d \u3000\u3000\u90a3\u5c11\u5973\u5634\u4e00\u561f\uff0c\u62ff\u8fc7\u624b\u5e15\u9053\uff1a\u201c\u6211\u81ea\u5df1\u7ed1\u5c31\u6211\u81ea\u5df1\u7ed1\uff0c\u8c01\u7a00\u7f55\u4f60\u3002\u201d \u3000\u3000\u5510\u7075\u8dd1\u4e86\u8fc7\u6765\uff0c\u7b11\u9053\uff1a\u201c\u6211\u66ff\u59d1\u5a18\u7ed1\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u9053\uff1a\u201c\u8c01\u8981\u4f60\u7ed1\u3002\u201d \u3000\u3000\u5510\u658c\u4eff\u4f5b\u56de\u5230\u51e0\u5341\u5e74\u524d\uff0c\u81ea\u5df1\u5728\u575f\u5730\u91cc\u548c\u4eba\u6349\u8ff7\u85cf\u7684\u65f6\u5019\uff0c\u89c1\u5510\u7075\u78b0\u4e86\u4e2a\u9489\u5b50\uff0c\u5374 \u7b11\u9053\uff1a\u201c\u9a6c\u5c41\u62cd\u5230\u9a6c\u811a\u4e0a\u53bb\u4e86\u3002\u201d \u3000\u3000\u8fd9\u8bdd\u82e5\u662f\u65c1\u4eba\u8bf4\u51fa\uff0c\u5510\u7075\u4e00\u5b9a\u5927\u6012\uff0c\u4f46\u662f\u5510\u658c\u8bf4\u7684\uff0c\u5510\u7075\u53ea\u6709\u5e72\u77aa\u7740\u773c\uff0c\u8138\u4e0a\u9752\u4e00 \u9635\u767d\u4e00\u9635\u7684\uff0c\u4e00\u70b9\u529e\u6cd5\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u90a3\u5c11\u5973\u81ea\u5df1\u7ed1\u597d\u624b\u5e15\uff0c\u9053\uff1a\u201c\u6211\u8bf4\u4e09\u58f0\u2018\u597d\u4e86\u6ca1\u6709\u2019\uff0c\u5c31\u5f00\u59cb\u6349\u4e86\uff0c\u4f60\u4eec\u8981\u5c0f\u5fc3 \u5440\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u5fc3\u91cc\u751f\u6c14\uff0c\u7ad9\u5230\u8fdc\u8fdc\u7684\uff0c\u6697\u6068\u8f9b\u6377\u63d0\u51fa\u8fd9\u9b3c\u82b1\u6837\u6765\u8ba8\u597d\u90a3\u5c11\u5973\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9ad8\u58f0\u8bf4\u9053\uff1a\u201c\u597d\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u5927\u5bb6\u9f50\u90fd\u65bd\u5c55\u5f00\u8eab\u6cd5\u3002 \u3000\u3000\u5510\u658c\u5927\u559d\u9053\uff1a\u201c\u4e0d\u51c6\u8d70\u5f97\u592a\u8fdc\u3002\u201d\u7d27\u8ddf\u7740\u5929\u9b54\u91d1\u6b39\uff0c\u4ed6\u6015\u91d1\u6b39\u4e58\u673a\u6e9c\u8d70\u3002 \u3000\u3000\u91d1\u6b39\u4e00\u77aa\u773c\uff0c\u9053\uff1a\u201c\u4f60\u56b7\u4e9b\u4ec0\u4e48\uff1f\u5927\u7237\u60f3\u8d70\uff0c\u65e9\u5c31\u8d70\u4e86\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u53c8\u53eb\u9053\uff1a\u201c\u597d\u4e86\u6ca1\u6709\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6697\u5fd6\u9053\uff1a\u201c\u770b\u4f60\u600e\u4e48\u6293\u5f97\u7740\u8fd9\u4e9b\u4eba\uff0c\u9664\u975e\u4f60\u6709\u901a\u5929\u7684\u672c\u4e8b\u3002\u201d\u4ed6\u81ea\u5fd6\u8f7b\u529f\uff0c\u82e5 \u7b49\u8fd9\u4e9b\u4eba\u5df2\u8d70\u5230\u90a3\u4e48\u8fdc\u65f6\uff0c\u81ea\u5df1\u53c8\u662f\u7ed1\u7740\u773c\u775b\uff0c\u53ea\u6015\u4e00\u4e2a\u4eba\u4e5f\u6349\u4e0d\u5230\uff0c\u6697\u6697\u5728\u66ff\u90a3\u5c11\u5973 \u62c5\u5fc3\u3002 \u3000\u3000\u90a3\u5c11\u5973\u518d\u53eb\u9053\uff1a\u201c\u597d\u4e86\u6ca1\u6709\u3002\u201d \u3000\u3000\u8bed\u58f0\u65b9\u843d\uff0c\u8eab\u5f62\u5c31\u98d8\u4e86\u51fa\u53bb\uff0c\u7ad9\u5728\u90a3\u91cc\u7684\u91d1\u6885\u9f84\u5413\u4e86\u4e00\u8df3\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5973\u771f\u4e2a \u90aa\u95e8\uff0c\u5979\u8fd9\u7b80\u76f4\u662f\u98de\uff0c\u90a3\u8fd8\u662f\u8f7b\u529f\u3002\u201d \u3000\u3000\u7eaf\u767d\u7684\u8f7b\u7eb1\u50cf\u662f\u4e00\u9635\u8f7b\u70df\uff0c\u88f3\u88f3\u98de\u821e\u7740\uff0c\u90a3\u5c11\u5973\u811a\u5c16\u6839\u672c\u4e0d\u66fe\u70b9\u5730\uff0c\u4eba\u5c31\u8d34\u7740\u5730\u9762 \u98de\u52a8\u7740\uff0c\u50cf\u662f\u5fa1\u98ce\u800c\u884c\u3002 \u3000\u3000\u5979\u8fd9\u4e00\u65bd\u5c61\u8f7b\u529f\uff0c\u5510\u658c\u4e00\u89c1\uff0c\u6697\u6697\u4ee5\u624b\u52a0\u989d\uff0c\u5e86\u5e78\u81ea\u5df1\u5e78\u4e8f\u65b9\u624d\u672a\u66fe\u9c81\u83bd\uff0c\u4ed6\u6697\u5fd6 \u9053\uff1a\u201c\u4eca\u5929\u83ab\u975e\u662f\u649e\u89c1\u4e86\u9b3c\u4e86\uff0c\u6c5f\u6e56\u4e0a\u54ea\u91cc\u6765\u7684\u8fd9\u4e9b\u5e74\u9752\u7537\u5973\uff0c\u4e00\u4e2a\u80dc\u4f3c\u4e00\u4e2a\uff0c\u8fd9\u5c11\u5973 \u7684\u8f7b\u529f\uff0c\u771f\u5df2\u5230\u4e86\u4f20\u8bf4\u4e2d\u2018\u51cc\u7a7a\u6b65\u865a\u2019\u7684\u5730\u6b65\u4e86\uff0c\u4eca\u5929\u6211\u771f\u5f00\u4e86\u773c\u4e86\u3002\u201d \u3000\u3000\u201c\u4f46\u662f\u5979\u7a76\u7adf\u662f\u8c01\u5462\uff1f\u82b8\u82b8\u6b66\u6797\u4e4b\u4e2d\uff0c\u6211\u8fd8\u6ca1\u6709\u542c\u8bf4\u8fc7\u8c01\u7684\u8f7b\u529f\u5df2\u7ec3\u6210\u8fd9\u79cd\u5730\u6b65 \u5462\u3002\u201d\u5510\u658c\u53c8\u5fd6\u9053\u3002 \u3000\u3000\u4ed6\u6697\u5730\u731c\u6d4b\uff0c\u7a81\u7136\u80cc\u4e0a\u5df2\u88ab\u4eba\u62cd\u4e86\u4e00\u4e0b\uff0c\u4ed6\u4e00\u60ca\u8f6c\u8eab\uff0c\u5374\u89c1\u90a3\u5c11\u5973\u5df2\u7ad9\u5728\u4ed6\u80cc\u540e\uff0c \u4e00\u9762\u89e3\u624b\u5e15\u4e00\u9762\u7b11\u9053\uff1a\u201c\u6211\u6349\u4f4f\u4e00\u4e2a\u4e86\u3002\u201d \u3000\u3000\u89e3\u5f00\u624b\u5e15\uff0c\u53c8\u7b11\u9053\uff1a\u201c\u539f\u6765\u662f\u8001\u54e5\u54e5\uff0c\u8fd9\u56de\u8f6e\u5230\u4f60\u505a\u9b3c\u4e86\u3002\u201d \u3000\u3000\u53c8\u53eb\u9053\uff1a\u201c\u4f60\u4eec\u5feb\u56de\u6765\u5440\uff01\u6211\u5df2\u6293\u5230\u4e00\u4e2a\u4e86\u3002\u201d\u5a9a\u76ee\u56db\u8f6c\uff0c\u8fdc\u8fdc\u5730\u53ea\u770b\u5230\u4e09\u4e2a\u4eba\uff0c \u5374\u5c11\u4e86\u4e24\u4e2a\uff0c\u5947\u9053\uff1a\u201c\u80f0\uff01\u8fd8\u6709\u4eba\u5462\uff1f\u201d \u3000\u3000\u5510\u658c\u5fd9\u4e5f\u56db\u4e0b\u641c\u7d22\uff0c\u89c1\u5510\u7075\u3001\u5510\u66fc\u6b63\u56de\u8eab\u8dd1\u6765\uff0c\u5929\u9b54\u91d1\u6b39\u5374\u76f4\u5411\u8fdc\u5904\u5954\u53bb\uff0c\u518d\u4e00\u6253 \u91cf\uff0c\u8f9b\u6377\u548c\u91d1\u6885\u9f84\u5374\u5df2\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u4ed6\u4e00\u6025\uff0c\u9ad8\u543c\u9053\uff1a\u201c\u7075\u513f\u3001\u66fc\u513f\uff0c\u5feb\u8ffd\uff01\u201d\u987e\u4e0d\u5f97\u9762\u524d\u7684\u5c11\u5973\uff0c\u7eb5\u8eab\u51e0\u4e2a\u8d77\u843d\uff0c\u5411 \u91d1\u6b39\u8ffd\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5947\u602a\uff1a\u201c\u8fd9\u4e9b\u4eba\u600e\u4e48\u641e\u7684\uff0c\u90fd\u8fd9\u6837\u75af\u75af\u98a0\u98a0\u7684\uff0c\u6349\u5f97\u597d\u597d\u7684\u8ff7\u85cf\uff0c\u600e\u4e48\u7a81\u7136 \u4e0d\u73a9\u4e86\u3002\u201d \u3000\u3000\u5979\u867d\u5df2\u5341\u516d\u5c81\uff0c\u4f46\u4e00\u5411\u968f\u7740\u7239\u5988\u72ec\u5c45\u5728\u6d77\u5916\u8352\u5c9b\u4e0a\uff0c\u4e16\u4e8b\u4e00\u70b9\u4e5f\u4e0d\u61c2\uff0c\u8fd9\u6b21\u5979\u968f\u7740 \u7239\u5988\u5750\u8239\u5230\u4e2d\u539f\u6765\uff0c\u4e00\u8def\u4e0a\u5979\u5988\u5988\u53c8\u4e0d\u51c6\u5979\u4e0b\u8239\uff0c\u597d\u4e0d\u5bb9\u6613\u627e\u4e86\u4e2a\u673a\u4f1a\u6e9c\u4e86\u4e0b\u6765\uff0c\u78b0\u5230 \u6709\u4eba\u966a\u5979\u73a9\uff0c\u5fc3\u91cc\u6b63\u9ad8\u5174\uff0c\u5c24\u5176\u662f\u90a3\u4e2a\u5e74\u9752\u4eba\uff0c\u773c\u6674\u5927\u5927\u7684\uff0c\u770b\u7740\u5979\uff0c\u4ee4\u5979\u6709\u4e00\u822c\u8bf4\u4e0d \u51fa\u6765\u7684\u6ecb\u5473\uff0c\u90a3\u77e5\u9053\u7a81\u7136\u4e4b\u95f4\uff0c\u8fd9\u4e9b\u4eba\u90fd\u8d70\u4e86\u3002 \u3000\u3000\u5979\u610f\u5174\u8427\u7d22\uff0c\u672c\u60f3\u5c06\u90a3\u4e9b\u4eba\u5168\u8ffd\u56de\u6765\uff0c\u53c8\u4e0d\u613f\u610f\u5f3a\u8feb\u4eba\u5bb6\uff0c\u6b63\u5feb\u5feb\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u7a81 \u7136\u7a7a\u4e2d\u6709\u4e2a\u58f0\u97f3\uff0c\u50cf\u662f\u4ece\u6781\u8fdc\u4e4b\u5904\u4f20\u6765\uff0c\u9053\uff1a\u201c\u83c1\u513f\uff0c\u5feb\u56de\u5230\u8239\u4e0a\u6765\uff0c\u518d\u4e0d\u56de\u6765\u7238\u7238\u5c31 \u8981\u6253\u624b\u5fc3\u4e86\u3002\u201d \u3000\u3000\u90a3\u58f0\u97f3\u53c8\u5a07\u53c8\u5ae9\uff0c\u542c\u8d77\u6765\u8212\u670d\u5f97\u5f88\uff0c\u4f46\u4ece\u90a3\u4e48\u8fdc\u7684\u5730\u65b9\u4f20\u6765\uff0c\u58f0\u97f3\u6e05\u695a\u5f97\u5f88\uff0c\u5c31\u50cf \u662f\u5728\u4f60\u8033\u65c1\u8bf4\u8bdd\u4f3c\u7684\uff0c\u5979\u4e00\u542c\u5c31\u77e5\u9053\u662f\u5988\u5988\u7684\u58f0\u97f3\uff0c\u9f3b\u5b50\u4e00\u76b1\uff0c\u820c\u5934\u4e00\u4ef6\uff0c\u8f6c\u8eab\u5411\u6c5f\u9762 \u63a0\u53bb\u3002 \u3000\u3000\u5230\u4e86\u6c5f\u8fb9\uff0c\u5979\u5fae\u5fae\u505c\u4e86\u4e00\u4e0b\uff0c\u4f3c\u4e4e\u662f\u6362\u4e86\u4e00\u53e3\u6c14\uff0c\u5c31\u63a0\u5230\u6c5f\u9762\u4e0a\uff0c\u8d34\u7740\u6c5f\u6c34\u9762\u524d\u8fdb \u7740\uff0c\u811a\u4e0b\u751a\u81f3\u6ca1\u6709\u4e00\u679d\u4e00\u53f6\uff0c\u5df2\u80fd\u8d8a\u6c5f\u800c\u8fc7\uff0c\u8fd9\u8f7b\u529f\u7b80\u76f4\u4ee4\u4eba\u96be\u4ee5\u76f8\u4fe1\u7684\uff0c\u4f55\u51b5\u7247\u523b\uff0c \u5979\u5c31\u98de\u5230\u6c5f\u5fc3\u7684\u4e00\u8258\u8239\u4e0a\u3002 \u3000\u3000\u90a3\u8239\u6bd4\u901a\u5e38\u5728\u6c5f\u9762\u4e0a\u884c\u9a76\u7684\uff0c\u5927\u4e86\u4e00\u500d\uff0c\u4ece\u5916\u9762\u770b\u4e0a\u53bb\uff0c\u5c31\u89c9\u5f97\u8fd9\u8239\u4e0a\u7684\u6bcf\u4e00\u5757\u6728 \u5934\uff0c\u90fd\u662f\u90a3\u4e48\u7cbe\u5de7\uff0c\u6728\u5757\u4e0e\u6728\u5757\u4e4b\u95f4\uff0c\u53c8\u914d\u5408\u5f97\u90a3\u4e48\u4f73\u5999\uff0c\u5c31\u50cf\u662f\u4e00\u4ef6\u975e\u5e38\u5b8c\u7f8e\u7684\u7ed3\u5408 \u4f53\uff0c\u4ee4\u4eba\u6709\u201c\u968f\u4fbf\u518d\u5927\u7684\u98ce\u6d6a\uff0c\u8fd9\u8239\u90fd\u80fd\u5b89\u7a33\u884c\u9a76\u201d\u7684\u611f\u89c9\u3002 \u3000\u3000\u8239\u8231\u7684\u95e8\uff0c\u662f\u4e24\u5757\u4e0a\u9762\u96d5\u6ee1\u4e86\u82b1\u7eb9\u7684\u6728\u677f\uff0c\u95e8\u91cc\u6709\u4e00\u5c42\u7eaf\u767d\u7684\u5e18\u5b50\u3002 \u3000\u3000\u6b64\u523b\u8231\u95e8\u534a\u5f00\u7740\uff0c\u95e8\u65c1\u542b\u7b11\u7ad9\u7740\u4e00\u4f4d\u4e2d\u5e74\u7f8e\u5987\uff0c\u8eab\u4e0a\u7a7f\u7740\u7684\u4e5f\u662f\u7eaf\u767d\u8272\u7684\u8f7b\u7f57\u957f\u886b\uff0c \u795e\u60c5\u4e4b\u95f4\uff0c\u5e26\u7740\u4e00\u4efd\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\u7684\u9ad8\u8d35\u3002 \u3000\u3000\u90a3\u53eb\u505a\u201c\u83c1\u513f\u201d\u7684\u5c11\u5973\uff0c\u4e00\u63a0\u5230\u8239\u4e0a\uff0c\u5c31\u6251\u5230\u4e2d\u5e74\u7f8e\u5987\u7684\u6000\u91cc\uff0c\u5a07\u61a8\u5730\u53eb\u9053\uff1a\u201c\u5988 \u5988\u3002\u201d \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u773c\u91cc\u4e00\u7247\u6148\u7231\u7684\u5149\u8f89\uff0c\u62cd\u7740\u201c\u83c1\u513f\u201d\u7684\u5934\u7b11\u9053\uff1a\u201c\u4f60\u7238\u7238\u5df1\u7ecf\u5728\u9a82\u4f60\u4e86\uff0c \u8bf4\u8981\u662f\u4f60\u518d\u4e0d\u56de\u6765\uff0c\u6211\u4eec\u5c31\u8981\u56de\u5bb6\u4e86\u3002\u201d \u3000\u3000\u83c1\u513f\u6492\u5a07\u9053\uff1a\u201c\u4eba\u5bb6\u53ea\u5230\u5cb8\u4e0a\u53bb\u4e86\u4e00\u4f1a\u513f\u561b\uff0c\u7239\u7239\u53d1\u4ec0\u4e48\u813e\u6c14\u3002\u201d\u8eab\u8eaf\u626d\u52a8\u7740\uff0c\u4f9d \u504e\u5728\u4e2d\u5e74\u7f8e\u5987\u6000\u91cc\u3002 \u3000\u3000\u4e2d\u5e74\u7f8e\u5987\u62c9\u7740\u5979\u7684\u624b\uff0c\u5fae\u7b11\u7740\u8d70\u8fdb\u8231\u91cc\u3002 \u3000\u3000\u8231\u91cc\u4e00\u7247\u7eaf\u767d\uff0c\u4e00\u5c18\u4e0d\u6881\uff0c\u4efb\u4f55\u4eba\u8d70\u5230\u8fd9\u8231\u91cc\u6765\uff0c\u90fd\u4f1a\u91cd\u91cd\u900f\u4e00\u53e3\u6c14\uff0c\u4fd7\u8651\u4ff1\u6d88\uff0c \u5fc3\u813e\u7686\u6e05\u3002 \u3000\u3000\u8239\u8231\u4e24\u65c1\u7684\u7a97\u6237\u9ad8\u9ad8\u652f\u8d77\uff0c\u7a97\u65c1\u4e00\u4e2a\u767d\u8272\u8863\u886b\u7684\u4e2d\u5e74\u4e66\u751f\uff0c\u6b63\u4fef\u7740\u8eab\u5b50\u63a2\u9996\u5916\u9762\uff0c \u542c\u5230\u6709\u4eba\u8fdb\u6765\uff0c\u56de\u8f6c\u8eab\u5b50\uff0c\u90a3\u5c11\u5973\u4f4e\u4f4e\u53eb\u4e86\u58f0\u201c\u7239\u7239\u201d\u3002 \u3000\u3000\u4e2d\u5e74\u4e66\u751f\u7b11\u9053\uff1a\u201c\u8ff7\u85cf\u6349\u5f97\u597d\u73a9\u5427\uff01\u53ef\u60dc\u4eba\u5bb6\u5168\u8d70\u4e86\uff0c\u6ca1\u6709\u4eba\u8ddf\u4f60\u73a9\u4e86\u3002\u201d\u4ed6\u53cc\u7709 \u5165\u9b13\uff0c\u773c\u89d2\u5e26\u715e\uff0c\u5634\u89d2\u4e0a\u5e26\u7740\u4e00\u4e1d\u51b7\u524a\u4e4b\u6c14\uff0c\u4f46\u662f\u5728\u7b11\u7684\u65f6\u5019\uff0c\u5374\u53c8\u4ee4\u4eba\u89c9\u5f97\u65e0\u6bd4\u7684\u548c \u853c\u53ef\u4eb2\u3002 \u3000\u3000\u83c1\u513f\u4f3c\u4e4e\u5f88\u6015\u5979\u7239\u7239\uff0c\u987d\u76ae\u7684\u795e\u8272\u4e5f\u6536\u4e86\u8d77\u6765\uff0c\u4f4e\u7740\u4e70\u55ef\u4e86\u4e00\u58f0\uff0c\u73a9\u5f04\u7740\u624b\u4e0a\u7684\u624b \u5e15\u3002 \u3000\u3000\u4e2d\u5e74\u4e66\u751f\u773c\u89d2\u4e00\u626c\uff0c\u9053\uff1a\u201c\u4f60\u8fd9\u624b\u5e15\u54ea\u91cc\u6765\u7684\uff1f\u62ff\u6765\u6211\u770b\u770b\u3002\u201d \u3000\u3000\u5c11\u5973\u4e0d\u6562\u4e0d\u62ff\u8fc7\u53bb\u3002 \u3000\u3000\u4e2d\u5e74\u4e66\u751f\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u521a\u521a\u4f60\u8499\u5728\u773c\u775b\u4e0a\u7684\u90a3\u4e00\u5757\u5427\uff01\u201d\u4e00\u9762\u5c06\u624b\u5e15\u5c55\u5f00\u5728\u624b\u4e0a\u770b \u7740\uff0c\u7a81\u7136\u9762\u8272\u4e00\u53d8\uff0c\u9053\uff1a\u201c\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u83c1\u513f\u89c1\u5979\u7239\u7239\u53d8\u8272\uff0c\u773c\u5708\u5413\u5f97\u7ea2\u7ea2\u7684\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u7f8e\u5987\u7b11\u9053\uff1a\u201c\u4f60\u53d1\u4ec0\u4e48\u813e\u6c14\uff1f\u201d \u3000\u3000\u4e2d\u5e74\u8eac\u751f\u5c06\u90a3\u5757\u624b\u5e15\u4e00\u6325\uff0c\u90a3\u624b\u5e15\u5e73\u5e73\u98d8\u5230\u7f8e\u5987\u624b\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u770b\u3002\u201d\u4e2d\u5e74\u7f8e \u5987\u5c06\u624b\u5e15\u5c55\u5f00\u4e00\u770b\uff0c\u4e5f\u53d8\u8272\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4f1a\u662f\u4ed6\uff1f\u201d \u3000\u3000\u83c1\u513f\u59d4\u59d4\u5c48\u5c48\u5730\u8d70\u5230\u5979\u7239\u7239\u65c1\u8fb9\uff0c\u4e2d\u5e74\u4e66\u751f\u6307\u7740\u7a97\u5916\u671d\u5979\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u770b\u90a3\u662f\u4e0d\u662f \u9001\u4f60\u8fd9\u5757\u624b\u5e15\u7684\u4eba\u3002\u201d \u3000\u3000\u83c1\u513f\u63a2\u9996\u7a97\u5916\uff0c\u770b\u89c1\u4e00\u8258\u5c0f\u8239\uff0c\u5728\u6c5f\u9762\u79fb\u52a8\u7740\uff0c\u8239\u4e0a\u5750\u7740\u4e24\u4eba\uff0c\u5979\u76ee\u529b\u4ea6\u5f02\u4e8e\u5e38\u4eba\uff0c \u4ed4\u7ec6\u4e00\u770b\uff0c\u89c1\u90a3\u4e24\u4eba\u5374\u6b63\u662f\u65b9\u624d\u7ed9\u5979\u90a3\u5757\u624b\u5e15\u773c\u775b\u5927\u5927\u7684\u5e74\u9752\u4eba\uff0c\u65c1\u8fb9\u5750\u7684\u5374\u662f\u90a3\u4e0d\u80af \u6349\u8ff7\u85cf\u7684\u5c11\u5973\u3002 \u3000\u3000\u4e8e\u662f\u5979\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u539f\u6765\u8f9b\u6377\u673a\u7075\u5df2\u6781\uff0c\u4ed6\u89c1\u90a3\u5c11\u5973\u4e00\u6765\uff0c\u4fbf\u77e5\u5fc5\u975e\u5e38\u4eba\uff0c\u540e\u6765\u90a3\u5c11\u5973\u8bf4\u5230\u201c\u6349\u8ff7\u85cf\u201d\uff0c \u4ed6\u5fc3\u4e2d\u4fbf\u5df2\u6709\u4e86\u8ba1\u8f83\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u6211\u8131\u8eab\u7684\u673a\u4f1a\u6765\u4e86\u3002\u201d\u4fbf\u62a2\u7740\u63d0\u8bae\u6349\u8ff7\u85cf\uff0c\u4ed6\u77e5\u9053\u5510 \u95e8\u4e09\u4eba\u4e0d\u4f1a\u4e5f\u4e0d\u6562\u53cd\u5bf9\u3002 \u3000\u3000\u679c\u7136\u4e0d\u51fa\u6240\u6599\uff0c\u7b49\u5230\u5510\u658c\u3001\u5510\u7075\u3001\u5510\u66fc\u56db\u4e0b\u4e00\u8d70\uff0c\u800c\u4e14\u5510\u658c\u7684\u6ce8\u610f\u529b\u53c8\u5168\u90fd\u653e\u5728\u91d1 \u6b39\u8eab\u4e0a\uff0c\u8f9b\u6377\u66f4\u662f\u5927\u559c\uff0c\u4ed6\u5374\u7ad9\u5728\u91d1\u6885\u9f84\u8eab\u65c1\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u90a3\u5c11\u5973\u773c\u775b\u88ab\u8499\uff0c\u542c\u98ce\u8fa8\u4f4d\uff0c \u5411\u5510\u658c\u7b49\u4eba\u8ffd\u53bb\uff0c\u81ea\u7136\u4e0d\u4f1a\u6765\u6349\u6839\u672c\u6ca1\u6709\u53d1\u51fa\u884c\u52a8\u7684\u58f0\u97f3\u7684\u8f9b\u6377\u3002 \u3000\u3000\u5c11\u5973\u4e00\u52a8\uff0c\u8f9b\u6377\u4e00\u628a\u62c9\u4f4f\u91d1\u6885\u9f84\uff0c\u98de\u5feb\u5411\u6c5f\u8fb9\u63a0\u53bb\uff0c\u4e0a\u4e86\u5c0f\u8239\uff0c\u671d\u5cb8\u8fb9\u7684\u6ce5\u571f\u4e0a\u53d1 \u4e86\u4e00\u638c\uff0c\u90a3\u5c0f\u8239\u4fbf\u98de\u5feb\u5730\u5411\u6c5f\u5fc3\u9a76\u53bb\u3002 \u3000\u3000\u4ed6\u4ee5\u201c\u6697\u9999\u6d6e\u5f71\u201d\u7684\u8f7b\u529f\u64cd\u7740\u8239\uff0c\u4e00\u4f1a\u513f\u4fbf\u79bb\u5cb8\u751a\u8fdc\uff0c\u4f30\u8ba1\u5510\u658c\u7edd\u65e0\u6cd5\u8ffd\u6765\uff0c\u4fbf\u505c \u624b\u5411\u91d1\u6885\u9f84\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u5403\u4e0d\u5403\u918b\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u8138\u4e00\u7ea2\uff0c\u7528\u624b\u7f9e\u4ed6\u8bf4\uff1a\u201c\u4f60\u597d\u5e0c\u7f55\u4e48\uff0c\u4eba\u5bb6\u90fd\u8981\u5403\u4f60\u7684\u918b\uff1f\u201d\u6697\u4e2d\u5374\u9ad8\u5174\uff0c \u5fd6\u9053\uff1a\u201c\u6211\u521a\u521a\u9519\u602a\u4e86\u4ed6\u3002\u201d \u3000\u3000\u8239\u4e0a\u867d\u6709\u6868\uff0c\u4f46\u4e24\u4eba\u90fd\u4e0d\u4f1a\u5212\u8239\uff0c\u8f9b\u6377\u7528\u6868\u62d4\u4e86\u4e24\u4e0b\uff0c\u8239\u53cd\u800c\u5728\u6c34\u4e2d\u6253\u8f6c\uff0c\u53ea\u5f97\u7f62 \u4e86\uff0c\u4efb\u8239\u968f\u6ce2\u800c\u6d41\u3002 \u3000\u3000\u4ed6\u6697\u5730\u5f97\u610f\uff0c\u81ea\u5df1\u7565\u65bd\u5c0f\u8ba1\uff0c\u4fbf\u8131\u8eab\u4e8b\u5916\uff0c\u4ed6\u5374\u4e0d\u77e5\u9053\u4ed6\u90a3\u5757\u89d2\u4e0a\u7ee3\u4e86\u4e03\u6735\u6885\u82b1\u7684 \u624b\u5e15\uff0c\u66ff\u4ed6\u627e\u6765\u66f4\u5927\u9ebb\u70e6\u3002 \u3000\u3000\u539f\u6765\u8fd9\u8239\u4e0a\u7684\u4e2d\u5e74\u4e66\u751f\uff0c\u5374\u6b63\u662f\u6b66\u6797\u4e2d\u89c6\u4e3a\u4ed9\u4f5b\u7684\u201c\u4e16\u5916\u4e09\u4ed9\u201d\u91cc\u7684\u4e1c\u6d77\u65e0\u6781\u5c9b\u5c9b \u4e3b\u65e0\u6068\u751f\u3002 \u3000\u3000\u4e1c\u6d77\u65e0\u6781\u5c9b\uff0c\u4f4d\u4e8e\u676d\u5dde\u6e7e\u5916\uff0c\u7389\u76d8\u6d0b\u91cc\uff0c\u662f\u5927\u6222\u5c71\u3001\u5c0f\u6222\u5c71\u4e4b\u95f4\u7684\u4e00\u4e2a\u5c0f\u5c9b\uff0c\u65e0\u6781 \u5c9b\u4e3b\u5f20\u6208\uff0c\u672c\u4e3a\u4e00\u4e0d\u7b2c\u79c0\u624d\uff0c\u6124\u800c\u5992\u4e16\uff0c\u8dd1\u5230\u8fd9\u8352\u5c9b\u4e0a\uff0c\u54ea\u77e5\u5374\u65e0\u610f\u4e2d\u670d\u4e86\u529f\u80fd\u593a\u5929\u9020 \u5730\u7684\u4e00\u679a\u5f02\u679c\uff0c\u53c8\u5f97\u5230\u5357\u664b\u7684\u4e00\u4f4d\u5f02\u4fa0\u8c22\u771f\u4eba\u9057\u7559\u4e0b\u7684\u79d8\u7b3c\u3002 \u3000\u3000\u5f20\u6208\u5728\u65e0\u6781\u5c9b\u4e00\u803d\u5341\u4f59\u5e74\uff0c\u7ec3\u5c31\u795e\u9b3c\u83ab\u6d4b\u7684\u672c\u9886\uff0c\u53c8\u56de\u5230\u4e2d\u571f\uff0c\u505a\u4e86\u51e0\u4ef6\u60ca\u5929\u52a8\u5730 \u7684\u4e8b\u3002 \u3000\u3000\u4f46\u4ed6\u5982\u795e\u9f99\uff0c\u6f6e\u7136\u6765\u53bb\uff0c\u4e16\u4eba\u53ea\u77e5\u9053\u6709\u4e2a\u81ea\u53f7\u201c\u65e0\u6068\u751f\u201d\u7684\u5f02\u4fa0\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u4eba\u80fd \u4e00\u7779\u4ed6\u7684\u771f\u9762\u76ee\u3002 \u3000\u3000\u4e8e\u662f\u6b66\u6797\u4e2d\u4eba\u9042\u5c06\u4ed6\u548c\u5927\u6222\u5c9b\u7684\u5e73\u51e1\u4e0a\u4eba\uff0c\u5c0f\u6222\u4e4c\u7684\u6167\u5927\u5e08\uff0c\u5e76\u79f0\u4e3a\u201c\u4e16\u5916\u4e09\u4ed9\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u81ea\u670d\u5f02\u679c\uff0c\u53c8\u5177\u4e0a\u4e58\u5185\u529f\u5999\u8c24\uff0c\u6570\u5341\u5e74\uff0c\u5bb9\u989c\u672a\u6539\uff0c\u5f80\u4e00\u4e2a\u5076\u7136\u7684\u673a\u4f1a\u91cc\uff0c \u4ed6\u53c8\u5076\u6e38\u4e2d\u5dde\uff0c\u9047\u5230\u4e00\u4e2a\u8eab\u624b\u4e0d\u51e1\u7684\u5973\u5b50\uff0c\u4e24\u4eba\u4e00\u89c1\u949f\u60c5\uff0c\u4fbf\u7ed3\u6210\u592b\u5987\uff0c\u90a3\u4fbf\u662f\u73b0\u5728\u4ed6 \u7684\u592b\u4eba\u4e5d\u5929\u7384\u5973\u7f2a\u4e03\u5a18\u4e86\u3002 \u3000\u3000\u592b\u5987\u4e24\u4eba\u60a0\u6e38\u6d77\u4e0a\uff0c\u4e5d\u5929\u7384\u5973\u4e3a\u4ed6\u751f\u4e86\u4e2a\u806a\u6167\u7684\u5973\u513f\uff0c\u53d6\u540d\u5f20\u83c1\uff0c\u4e00\u6643\u591a\u5e74\uff0c\u65e0\u6068 \u751f\u5c06\u65e0\u6781\u5c9b\u7ecf\u8425\u6210\u4e2a\u6d77\u5916\u7684\u4ed9\u571f\uff0c\u53c8\u5728\u6cbf\u6d77\u8bf8\u5730\uff0c\u627e\u4e86\u4e9b\u8d2b\u6c11\u6765\u5145\u505a\u5974\u4ec6\uff0c\u65e5\u5b50\u8fc7\u5f97\u5b89 \u9002\u6109\u5feb\uff0c\u65e0\u6068\u751f\u4e5f\u6ca1\u6709\u4e89\u96c4\u6b66\u6797\u7684\u5ff5\u5934\uff0c\u53ea\u662f\u4ed6\u6124\u4e16\u75be\u4fd7\u4e4b\u6027\u672b\u6539\uff0c\u518d\u4e5f\u4e0d\u613f\u56de\u5230\u4e2d\u571f \u53bb\u3002 \u3000\u3000\u6709\u4e00\u5e74\uff0c\u5f20\u83c1\u624d\u516b\u5c81\uff0c\u6bd4\u8d77\u201c\u75b9\u5b50\u201d\u6765\uff0c\u65e0\u6068\u751f\u5b66\u7a76\u5929\u4eba\uff0c\u5374\u504f\u504f\u4e0d\u4f1a\u533b\u75c5\uff0c\u201c\u75b9 \u5b50\u201d\u4e00\u75c7\uff0c\u672c\u662f\u5c0f\u513f\u5e38\u51fa\u4e4b\u75c5\uff0c\u4f46\u5374\u65e0\u6cd5\u4ee5\u5185\u529f\u533b\u5f97\uff0c\u4e5d\u5929\u7384\u5973\u7231\u5973\u5fc3\u5207\uff0c\u4fbf\u548c\u65e0\u6068\u751f \u4e24\u4eba\uff0c\u8fdc\u8d74\u6d59\u6c5f\uff0c\u627e\u4e86\u4e2a\u6781\u6709\u540d\u7684\u5927\u592b\u5230\u5c9b\u4e0a\u6765\uff0c\u66ff\u5f20\u83c1\u533b\u75c5\u3002 \u3000\u3000\u4ed6\u4eec\u5728\u8def\u9014\u4e0a\uff0c\u9047\u89c1\u4e2a\u7626\u9aa8\u5d99\u5ccb\uff0c\u53c8\u662f\u795e\u7ecf\u5931\u5e38\u7684\u5973\u5b50\uff0c\u6b66\u529f\u5374\u751a\u9ad8\uff0c\u4e5d\u5929\u7384\u5973\u597d \u5947\u5fc3\u8d77\uff0c\u4e0a\u53bb\u4e00\u770b\uff0c\u5374\u662f\u5979\u6700\u5c0f\u7684\u59b9\u59b9\u7389\u9762\u4ed9\u72d0\u7f2a\u4e5d\u5a18\uff0c\u5979\u5927\u60ca\u4e4b\u4e0b\uff0c\u5c06\u5979\u5e26\u56de\u65e0\u6781\u5c9b\u3002 \u3000\u3000\u7f28\u4e5d\u5a18\u6574\u65e5\u54ed\u7b11\u65e0\u5e38\uff0c\u62ff\u7740\u4e00\u5757\u4e0a\u9762\u7ee3\u7740\u4e03\u6735\u6885\u82b1\u7684\u624b\u5e15\uff0c\u53e3\u4e2d\u9891\u9891\u53eb\u7740\uff1a\u201c\u6885\u5c71 \u6c11\uff0c\u5c71\u6c11\u2026\u2026\u201d \u3000\u3000\u4e5d\u5929\u7384\u5973\u4e00\u542c\uff0c\u77e5\u9053\u8fd9\u6885\u5c71\u6c11\uff0c\u4fbf\u662f\u6c5f\u6e56\u4e0a\u9f0e\u9f0e\u5927\u540d\u7684\u201c\u4e03\u5999\u795e\u541b\u201d\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927 \u6012\u3002 \u3000\u3000\u603b\u4e4b\u201c\u4e03\u5999\u795e\u541b\u201d\u7684\u201c\u4e03\u827a\u201d\u91cc\uff0c\u6709\u4e00\u6837\u4fbf\u662f\u201c\u8272\u201d\u5b57\uff0c\u6c5f\u6e56\u4e0a\u6240\u5171\u77e5\uff0c\u4e03\u5999\u795e\u541b \u7684\u98ce\u6d41\u97f5\u4e8b\u6700\u591a\u3002 \u3000\u3000\u4e5d\u5929\u7384\u5973\u7531\u6b64\u4ee5\u4e3a\u81ea\u5df1\u7684\u59b9\u59b9\u53d7\u4e86\u201c\u4e03\u5999\u795e\u541b\u201d\u7684\u73a9\u5f04\uff0c\u795e\u7ecf\u5931\u5e38\uff0c\u7b49\u5230\u7f2a\u4e5d\u5a18\u4e00 \u6b7b\uff0c\u4e5d\u5929\u7384\u5973\u66f4\u5bf9\u6885\u5c71\u6c11\u6068\u5982\u5207\u9aa8\uff0c\u5979\u5374\u4e0d\u77e5\u9053\u4ed6\u59b9\u59b9\u7684\u75af\uff0c\u662f\u4e3a\u4e86\u6885\u5c71\u6c11\u7684\u201c\u6b7b\u201d\uff0c \u5374\u4e0d\u662f\u5979\u6240\u6599\u60f3\u7684\u539f\u56e0\u5462\u3002 \u3000\u3000\u539f\u6765\u7389\u9762\u4ed9\u72d0\u548c\u201c\u4e03\u5999\u795e\u541b\u201d\u60c5\u611f\u6700\u6df1\uff0c\u5f53\u6c5f\u6e56\u4f20\u4e91\u201c\u4e03\u5999\u795e\u541b\u201d\u5df2\u4e27\u8eab\u4e94\u534e\u5c71\u91cc \u7684\u65f6\u5019\uff0c\u7f2a\u4e5d\u5a18\u4fbf\u5b64\u8eab\u4e0a\u5d06\u5cd2\u5c71\u53bb\u4e3a\u4ed6\u590d\u4ec7\uff0c\u90a3\u77e5\u5979\u5374\u4e0d\u662f\u5251\u795e\u5389\u9e57\u7684\u5bf9\u624b\uff0c\u88ab\u5389\u9e57\u8fde \u9a82\u5e26\u8bbd\u8d76\u4e0b\u4e86\u5d06\u5cd2\u5c71\u3002 \u3000\u3000\u5979\u5fc3\u9ad8\u6c14\u50b2\uff0c\u53d7\u6b64\u5947\u803b\u5927\u8fb1\uff0c\u518d\u52a0\u4e0a\u60c5\u4eba\u5df2\u6b7b\uff0c\u4fbf\u5931\u53bb\u7406\u667a\uff0c\u6574\u65e5\u75af\u75af\u98a0\u98a0\u8d77\u6765\uff0c \u6ca1\u6709\u591a\u4e45\uff0c\u6b64\u7edd\u4ee3\u7f8e\u4eba\u4fbf\u9999\u6d88\u7389\u635f\u4e86\u3002 \u3000\u3000\u4e5d\u5929\u7384\u5973\u53c8\u81f3\u4e2d\u5dde\uff0c\u60f3\u627e\u6885\u5c71\u6c11\u7b97\u5e10\uff0c\u54ea\u77e5\u5374\u542c\u5230\u201c\u4e03\u5999\u795e\u541b\u201d\u5df2\u6b7b\u4e4b\u8bf4\uff0c\u600f\u600f\u5730 \u56de\u5230\u65e0\u6781\u5c9b\u4e0a\uff0c\u4e00\u6643\u53c8\u662f\u4e03\u3001\u516b\u5e74\uff0c\u4ed6\u592b\u5987\u4fe9\u518d\u4e5f\u6ca1\u6709\u79bb\u5f00\u65e0\u6781\u5c9b\u4e00\u6b65\uff0c\u53ea\u662f\u7ec8\u65e5\u8c03\u6559 \u4ed6\u4eec\u7684\u5973\u513f\u3002 \u3000\u3000\u5f20\u83c1\u81ea\u5e7c\u5728\u5979\u7236\u6bcd\u201c\u65e0\u6068\u751f\u201d\u592b\u5987\u624b\u91cc\u8c03\u6559\u51fa\u7684\u4e00\u8eab\u672c\u9886\uff0c\u81ea\u4e5f\u662f\u8d85\u51e1\u7edd\u4fd7\u4e86\u3002 \u3000\u3000\u5979\u78e8\u7740\u7239\u5a18\u51fa\u6765\u4e00\u5e7f\u773c\u754c\uff0c\u65e0\u6068\u751f\u5b9e\u5728\u7231\u6781\u4ed6\u5973\u513f\uff0c\u4fbf\u4e58\u7740\u8239\uff0c\u6eaf\u6c5f\u800c\u4e0a\uff0c\u51c6\u5907\u4e00 \u6e38\u4e2d\u5dde\u98ce\u7269\u3002 \u3000\u3000\u54ea\u77e5\u9053\u5f20\u83c1\u5076\u4e00\u5077\u4e0a\u5cb8\u53bb\uff0c\u5e26\u56de\u6765\u7684\u8fd9\u5757\u624b\u5e15\uff0c\u5374\u548c\u6614\u5e74\u7f15\u4e5d\u5a18\u7ec8\u65e5\u6cea\u773c\u76f8\u5bf9\u7684\u90a3 \u5757\u4e00\u6837\u5462\u3002 \u3000\u3000\u65e0\u6068\u751f\u4e00\u89c1\u90a3\u5757\u7ee3\u5e15\uff0c\u81ea\u662f\u5927\u6012\uff0c\u4ed6\u76ee\u529b\u901a\u7384\uff0c\u5728\u8239\u7a97\u4e2d\u65e9\u5c06\u5cb8\u4e0a\u7684\u4e8b\u770b\u5f97\u6e05\u6e05\u695a \u695a\u3002 \u3000\u3000\u8f9b\u6377\u9003\u5230\u8239\u4e0a\u65f6\uff0c\u4ed6\u8fd8\u5728\u6697\u8d5e\u6b64\u4eba\u7684\u673a\u667a\uff0c\u6b64\u523b\u770b\u5230\u5f20\u83c1\u4e00\u70b9\u5934\uff0c\u8f6c\u8eab\u5411\u5979\u59bb\u5b50\u8bf4 \u9053\uff1a\u201c\u539f\u6765\u6885\u5c71\u6c11\u5e76\u672a\u6b7b\uff0c\u6b64\u523b\u5c31\u5728\u5916\u9762\u7684\u5c0f\u8239\u4e0a\u3002\u201d \u3000\u3000\u4e5d\u5929\u7384\u5973\u4e5f\u51d1\u5230\u7a97\u53e3\u4e00\u770b\uff0c\u6012\u9053\uff1a\u201c\u8fd9\u65af\u53c8\u9a97\u4e86\u4e2a\u5c11\u5973\uff0c\u8fd9\u79cd\u4eba\u51b3\u4e0d\u80fd\u518d\u8ba9\u4ed6\u7559\u5728 \u4e16\u4e0a\uff0c\u6211\u4eec\u597d\u6b79\u8981\u4e3a\u4e16\u4eba\u9664\u6b64\u4e00\u53ae\u5bb3\u201d \u3000\u3000\u5f20\u83c1\u60c5\u7aa6\u521d\u5f00\uff0c\u65b9\u624d\u4e00\u9762\u4e4b\u95f4\uff0c\u5df2\u5bf9\u8fd9\u201c\u773c\u6674\u5927\u5927\u7684\u5e74\u9752\u4eba\u201d\u6709\u4e86\u597d\u611f\uff0c\u6b64\u523b\u542c\u4e86 \u8fd9\u8bdd\uff0c\u7741\u7740\u4e00\u5bf9\u660e\u7738\u671b\u7740\u5979\u5988\u5988\uff0c\u4e0d\u77e5\u9053\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6697\u5730\u5947\u602a\u7239\u7239\u5988\u5988\u4e3a\u4f55\u5bf9\u8fd9 \u5e74\u9752\u4eba\u8fd9\u822c\u75db\u6068\u3002 \u3000\u3000\u65e0\u6068\u751f\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\u3002\u201d\u8eab\u8eaf\u4e00\u65cb\uff0c\u4ece\u7a97\u4e2d\u98d8\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u5f03\u4e86\u6868\uff0c\u4efb\u5c0f\u821f\u968f\u7740\u6c5f\u6c34\u98d8\u6d41\uff0c\u4ed6\u659c\u9760\u5728\u8239\u8237\uff0c\u5fc3\u91cc\u4ecd\u4e0d\u80fd\u5fd8\u5374\u65b9\u624d\u90a3\u8f7b\u7eb1\u5c11 \u5973\u7684\u5f71\u5b50\u3002 \u3000\u3000\u91d1\u6885\u9f84\u5634\u4e00\u6487\uff0c\u6307\u7740\u4ed6\u8bf4\uff1a\u201c\u4f60\u5440\uff01\u201d \u3000\u3000\u8f9b\u6377\u4e58\u52bf\u62c9\u4f4f\u5979\u7684\u624b\uff0c\u7b11\u95ee\u9053\uff1a\u201c\u6211\u600e\u7684\uff1f\u201d \u3000\u3000\u201c\u6211\u77e5\u9053\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u91d1\u6885\u9f84\u4efb\u4ed6\u63e1\u7740\u81ea\u5df1\u7684\u624b\uff0c\u7b11\u8bf4\u9053\uff0c\u201c\u4f60\u8fd8\u5728\u60f3\u521a\u521a\u90a3\u4e2a \u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u6211\u662f\u5728\u60f3\u4e00\u4e2a\u5973\u5b69\u5b50\u3002\u201d\u4ed6\u5c06\u91d1\u6885\u9f84\u7684\u624b\u653e\u5728\u5634\u4e0a\u4eb2\u4e86\u4eb2\uff0c\u9053\uff1a\u201c\u4e0d \u8fc7\u6211\u4e0d\u662f\u5728\u60f3\u521a\u521a\u90a3\u4e2a\uff0c\u6211\u662f\u5728\u60f3\u73b0\u5728\u8fd9\u4e2a\u3002\u201d \u3000\u3000\u91d1\u6885\u5a07\u7b11\u9053\uff1a\u201c\u4f60\u6700\u574f\u4e86\u3002\u201d\u5fc3\u91cc\u5374\u751c\u751c\u7684\u3002 \u3000\u3000\u4e24\u4eba\u4f4e\u8bed\u6d45\u7b11\uff0c\u5c06\u4ec0\u4e48\u4e8b\u90fd\u653e\u5f97\u8fdc\u8fdc\u7684\uff0c\u60f3\u4e5f\u4e0d\u60f3\uff0c\u5f7c\u6b64\u53ea\u77e5\u9053\u4e16\u4e0a\u53ea\u6709\u4e2a\u201c\u4f60\u201d\uff0c \u9664\u4e86\u201c\u4f60\u201d\u4e4b\u5916\uff0c\u4efb\u4f55\u4e8b\u90fd\u4e0d\u8db3\u9053\u4e86\u3002 \u3000\u3000\u81f3\u5c11\u5728\u8fd9\u4e00\u5239\u90a3\u91cc\uff0c\u8f9b\u6377\u611f\u5230\u81ea\u5df1\u6709\u8fd9\u6837\u7684\u611f\u89c9\uff0c\u8fd9\u5c11\u5973\u7ed9\u4e86\u4ed6\u4e00\u5207\uff0c\u4ed6\u4e0d\u8be5\u8fd9\u6837 \u5bf9\u5979\u5417\uff1f\u201d \u3000\u3000\u4f46\u662f\u8f9b\u6377\u81ea\u5df1\u7684\u786e\u660e\u4e86\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u4ed6\u81ea\u5df1\u7684\u60c5\u611f\u8fd8\u6ca1\u6709\u4e00\u4e2a\u56fa\u5b9a\u7684\u65b9\u5411\uff0c\u5bf9\u91d1 \u6885\u9f84\u7684\u60c5\u611f\uff0c\u4e5f\u4eff\u4f5b\u662f\u611f\u6fc0\u6bd4\u7231\u8fd8\u591a\u4e00\u4e9b\u3002 \u3000\u3000\u5bf9\u65b9\u5c11\u74a7\u5462\uff1f\u4ed6\u66fe\u7ecf\u4ee5\u4e3a\u4ed6\u662f\u7231\u5979\u7684\uff0c\u53ef\u662f\u73b0\u5728\u5979\u6b7b\u4e86\uff0c\u8fd8\u662f\u4e3a\u4ed6\u800c\u6b7b\u7684\uff0c\u4fbf\u662f\u4ed6 \u5374\u5e76\u6ca1\u6709\u4e3a\u8fd9\u4e2a\u547d\u8fd0\u60b2\u60e8\u7684\u5c11\u5973\u800c\u60b2\u3002\u4ed6\u611f\u53f9\u4e86\uff0c\u4e0e\u5176\u8bf4\u4ed6\u662f\u591a\u60c5\u7684\uff0c\u8fd8\u4e0d\u5982\u8bf4\u4ed6\u662f\u8584 \u60c5\u66f4\u6070\u5f53\u6b64\u3002 \u3000\u3000\u201c\u7136\u800c\u8fd9\u662f\u6211\u7684\u9519\u5417\uff1f\u201d\u4ed6\u6697\u5fd6\u9053\uff0c\u201c\u5f53\u4e00\u4e2a\u5c11\u5973\u660e\u786e\u5730\u8868\u793a\u5979\u662f\u7231\u7740\u6211\u65f6\uff0c\u6211\u80fd \u600e\u4e48\u505a\u5462\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u5ffd\u5730\u6323\u8131\u4e86\u4ed6\u7684\u624b\uff0c\u4ece\u6000\u662f\u638f\u51fa\u4e00\u672c\u4e66\u6765\uff0c\u4ea4\u7ed9\u8f9b\u6377\u9053\uff1a\u201c\u8fd9\u4e2a\u653e\u5728\u4f60\u90a3\u91cc \u597d\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u89c1\u90a3\u672c\u4e66\u6b63\u662f\u6bd2\u541b\u91d1\u4e00\u9e4f\u6240\u5199\u201c\u6bd2\u7b08\u201d\uff0c\u6de1\u7136\u9053\uff1a\u201c\u8fd9\u662f\u4f60\u7239\u7239\u7684\u4e1c\u897f\uff0c\u8fd8\u662f \u653e\u5728\u4f60\u90a3\u91cc\u597d\u4e86\u3002\u201d \u3000\u3000\u81ea\u4ece\u542c\u4e86\u91d1\u4e00\u9e4f\u6240\u8bf4\u7684\u4e00\u4e2a\u6545\u4e8b\u4e4b\u540e\uff0c\u4ed6\u4e0d\u81ea\u89c9\u5730\u5fd8\u4e86\u91d1\u6885\u9f84\u7684\u201c\u7239\u7239\u201d\u8be5\u662f\u4faf\u4e8c\u3002 \u3000\u3000\u53ef\u662f\u81ea\u4ed6\u8bf4\u51fa\u4e86\u4e4b\u540e\uff0c\u53c8\u4e0d\u7981\u6697\u81ea\u8d23\u5907\u81ea\u5df1\uff0c\u89c9\u5f97\u81ea\u5df1\u6709\u4e00\u4e9b\u5bf9\u4e0d\u8d77\u201c\u4faf\u4e8c\u53d4\u201d\uff0c \u4f46\u662f\u8fd9\u611f\u89c9\u5374\u662f\u90a3\u4e48\u5fae\u5f31\uff0c\u5fae\u5f31\u5f97\u4ed6\u81ea\u5df1\u90fd\u4e0d\u5927\u80fd\u5206\u8fa8\u51fa\u6765\u90a3\u662f\u60ed\u6127\uff1f\u6291\u6216\u4ec5\u4ec5\u662f\u6709\u4e9b \u4e0d\u5b89\u3002 \u3000\u3000\u91d1\u6885\u9f84\u5c06\u6bd2\u7b08\u585e\u5230\u4ed6\u7684\u6000\u91cc\uff0c\u9053\uff1a\u201c\u8fd8\u662f\u653e\u5728\u4f60\u90a3\u91cc\u597d\u4e86\uff0c\u653e\u5728\u6211\u8eab\u4e0a\u9f13\u9f13\u5730\uff0c\u96be \u53d7\u6b7b\u4e86\u3002\u201d \u3000\u3000\u5979\u7406\u4e86\u7406\u9b13\u8fb9\u7684\u4e71\u53d1\uff0c\u8138\u7ea2\u7740\uff0c\u5a07\u7b11\u7740\u9053\uff1a\u201c\u4f60\u8fd9\u4eba\u4e5f\u771f\u662f\uff0c\u6211\u7684\u8fd8\u4e0d\u5c31\u662f\u7b49\u4e8e\u4f60 \u7684\u4e00\u6837\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b11\u4e86\uff0c\u5c06\u6bd2\u7b08\u4ed4\u7ec6\u5730\u6536\u5230\u6000\u91cc\u3002 \u3000\u3000\u81ea\u4ece\u4ed6\u7b2c\u4e00\u773c\u770b\u5230\u8fd9\u672c\u4e1c\u897f\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u6df1\u6df1\u88ab\u91cc\u9762\u6240\u8bb0\u8f7d\u7684\u4e1c\u897f\u8ff7\u5012\u4e86\uff0c\u4ed6\u6c42\u77e5 \u6b32\u6781\u76db\uff0c\u5bf9\u4e8e\u4efb\u4f55\u65b0\u5947\u7684\u4e1c\u897f\uff0c\u90fd\u8981\u5b66\u4e00\u5b66\uff0c\u8981\u77e5\u9053\u4e00\u4e9b\u6b32\u671b\u3002\u8fd9\u201c\u6bd2\u7b08\u201d\u91cc\u6240\u8f7d\u7684\uff0c \u4ff1\u662f\u4e9b\u4e0d\u53ef\u601d\u8bae\u7684\u6bd2\u7269\uff0c\u5c31\u4ed7\u7740\u8fd9\u4e9b\uff0c\u91d1\u4e00\u9e4f\u7eb5\u6a2a\u6c5f\u6e56\u591a\u5e74\uff0c\u4f7f\u6b66\u6797\u4e2d\u4eba\u95fb\u800c\u751f\u754f\uff0c\u7531 \u6b64\u5f53\u53ef\u60f3\u89c1\u8fd9\u201c\u6bd2\u7b3c\u201d\u7684\u4e0d\u540c\u51e1\u54cd\uff0c\u800c\u4eba\u4eec\u5bf9\u4e8e\u201c\u4e0d\u540c\u51e1\u54cd\u201d\u7684\u4e1c\u897f\uff0c\u603b\u662f\u6700\u6709\u5174\u8da3\u7684\u3002 \u3000\u3000\u4f55\u51b5\u8f9b\u6377\u8fd9\u6837\u6709\u7740\u6781\u5f3a\u7684\u6c42\u77e5\u6b32\uff0c\u5bf9\u4efb\u4f55\u4e8b\u53c8\u90fd\u62b1\u7740\u6781\u5927\u7684\u91ce\u5fc3\u7684\u4eba\u5462\u3002 \u3000\u3000\u5f53\u4ed6\u6536\u8d77\u90a3\u672c\u6bd2\u7b08\u65f6\uff0c\u4ed6\u7684\u5fc3\u623f\u56e0\u7740\u72c2\u559c\u800c\u6026\u7136\u8df3\u52a8\u7740\u3002\u6b64\u523b\u5915\u9633\u5c06\u843d\uff0c\u665a\u971e\u6f2b\u5929\uff0c \u5c06\u672c\u5df2\u662f\u9ec4\u8272\u7684\u6c5f\u6c34\uff0c\u6620\u6210\u4e00\u7247\u7cdc\u70c2\u7684\u91d1\u8272\uff0c\u6c34\u6ce2\u6d41\u6eda\uff0c\u53c8\u50cf\u662f\u65e0\u6570\u7684\u91d1\u8272\u5c0f\u86c7\u5728\u90a3\u91cc \u8815\u52a8\u7740\u3002 \u3000\u3000\u5915\u9633\u7167\u5728\u91d1\u6885\u9f84\u8138\u4e0a\uff0c\u5979\u66f4\u663e\u5f97\u7f8e\u4e86\u3002 \u3000\u3000\u5979\u4fa7\u8fc7\u8138\uff0c\u95ed\u7740\u773c\u6674\u907f\u5f00\u4e86\u90a3\u7531\u6c34\u4e2d\u53cd\u5c04\u51fa\u7684\u5f3a\u5149\uff0c\u8f7b\u8f7b\u5730\u8bf4\uff1a\u201c\u6211\u997f\u5f97\u8981\u6b7b\uff0c\u6377 \u54e5\u54e5\uff0c\u627e\u70b9\u4e1c\u897f\u7ed9\u6211\u5403\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u5176\u5b9e\u8f9b\u6377\u4f55\u5c1d\u4e0d\u997f\uff0c\u82e6\u7b11\u9053\uff1a\u201c\u7b49\u4e00\u4f1a\u5230\u4e86\u5cb8\uff0c\u6211\u4eec\u53bb\u5927\u5403\u4e00\u987f\u2026\u201d \u3000\u3000\u91d1\u6885\u9f84\u62a2\u7740\u9053\uff1a\u201c\u6211\u8981\u5403\u706b\u817f\u9e21\u6c64\u3001\u51b0\u7cd6\u8098\u5b50\u3002\u201d \u3000\u3000\u8f9b\u6377\u54bd\u4e86\u53e3\u53e3\u6c34\uff0c\u7b11\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u51b0\u7cd6\u8098\u5b50\uff0c\u8fd8\u6709\u2026\u2026\u201d\u7a81\u5730\uff0c\u4ed6\u53c8\u6b62\u4f4f\u4e86\u8bdd\u3002 \u3000\u3000\u91d1\u6885\u9f84\u987a\u7740\u4ed6\u773c\u5149\u4e00\u770b\uff0c\u89c1\u4e00\u6761\u6de1\u6de1\u7684\u767d\u8272\u4eba\u5f71\u81ea\u90a3\u5927\u8239\u7684\u7a97\u53e3\u98d8\u51fa\uff0c\u770b\u4e0a\u5c31\u50cf\u662f \u4e00\u7f15\u70df\u3002 \u3000\u3000\u5947\u602a\u7684\uff0c\u8fd9\u70df\u7adf\u5411\u81ea\u5df1\u8fd9\u6761\u5c0f\u8239\u98d8\u4e86\u8fc7\u6765\uff0c\u5979\u9762\u8272\u4e00\u53d8\uff0c\u5fd6\u9053\uff1a\u201c\u770b\u8fd9\u79cd\u8d85\u51e1\u4eba\u5723 \u7684\u8eab\u6cd5\uff0c\u53ef\u80fd\u53c8\u662f\u90a3\u4e2a\u5973\u5b69\u5b50\uff0c\u5979\u53c8\u8dd1\u4e86\u6765\u5e72\u4ec0\u4e48\uff0c\u96be\u9053\u5979\u771f\u5bf9\u2026\u2026\u201d \u3000\u3000\u5979\u5ff5\u5934\u5c1a\u672a\u53ca\u8f6c\u5b8c\uff0c\u90a3\u9053\u8f7b\u70df\u5df2\u505c\u5728\u4ed6\u4eec\u8239\u4e0a\uff0c\u91d1\u6885\u9f84\u4e00\u62ac\u773c\uff0c\u5374\u89c1\u662f\u4e00\u4e2a\u4e2d\u5e74\u7684 \u4e66\u751f\u3002 \u3000\u3000\u5c0f\u8239\u7edd\u672a\u56e0\u8fd9\u4eba\u7684\u6765\u5230\u800c\u6709\u4e1d\u6beb\u6ce2\u52a8\u3002 \u3000\u3000\u8f9b\u6377\u5168\u7136\u88ab\u8fd9\u7a81\u53d8\u9707\u60ca\u4e86\uff0c\u4ed6\u4f9d\u7a00\u611f\u89c9\u5230\u8fd9\u4eba\u7684\u6765\uff0c\u7edd\u4e0d\u662f\u5584\u610f\u7684\uff0c\u8fd9\u4ece\u4ed6\u5634\u89d2\u7684 \u51b7\u524a\u5c31\u53ef\u4ee5\u770b\u51fa\u6765\uff0c\u8f9b\u6377\u81ea\u5fd6\u80fd\u529b\uff0c\u6781\u654f\u9510\u5730\u611f\u89c9\u5230\u4e00\u4ef6\u4e8b\uff0c\u90a3\u5c31\u662f\u4ed6\u7edd\u4e0d\u662f\u6b64\u4eba\u7684\u654c \u624b\u3002 \u3000\u3000\u8fd9\u4ece\u4ed6\u8fd9\u79cd\u60ca\u4eba\u7684\u8eab\u6cd5\u4e0a\u5c31\u53ef\u4ee5\u770b\u51fa\u6765\uff0c\u8f9b\u6377\u6697\u4e2d\u7740\u6025\uff1a\u201c\u82e5\u7136\u4ed6\u771f\u8981\u5bf9\u6211\u4eec\u4e0d\u5229\uff0c \u6211\u53ef\u771f\u6ca1\u6709\u529b\u91cf\u6765\u5bf9\u4ed8\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u5c31\u662f\u8f9b\u6377\u5f02\u4e8e\u5e38\u4eba\u7684\u5730\u65b9\uff0c\u4ed6\u80fd\u591f\u6781\u5feb\u5730\u5c06\u81ea\u5df1\u548c\u522b\u4eba\u4f5c\u4e00\u4e2a\u516c\u5e73\u6bd4\u8f83\uff0c\u800c\u4ed6\u7684 \u5224\u65ad\u4e5f\u5f80\u5f80\u662f\u6700\u6b63\u786e\u7684\u3002\u8fd9\u79cd\u6b63\u786e\u7684\u5224\u65ad\uff0c\u4f7f\u4ed6\u80fd\u6709\u4e00\u4e2a\u51b7\u9759\u7684\u5934\u8111\u6765\u601d\u8003\u8be5\u600e\u6837\u53bb\u5e94 \u4ed8\u3002 \u3000\u3000\u65e0\u6068\u751f\u50b2\u7136\u4ec1\u7acb\u5728\u5c0f\u8239\u7684\u8239\u5934\u4e0a\uff0c\u5e73\u7a33\u5f97\u50cf\u662f\u4e00\u5c0a\u77f3\u50cf\uff0c\u53ea\u6709\u8863\u8882\u968f\u7740\u6c5f\u4e0a\u7684\u98ce\u5fae \u5fae\u98d8\u52a8\u7740\u3002 \u3000\u3000\u8fd9\u65f6\u4e5d\u5929\u7384\u5973\u6b63\u5411\u5979\u60ca\u7591\u7740\u7684\u5973\u513f\u89e3\u91ca\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u4ef6\u4e8b\u53d1\u751f\u3002 \u3000\u3000\u65e0\u6068\u751f\u5ffd\u7136\u671b\u7740\u8f9b\u6377\u3002 \u3000\u3000\u4ed6\u4e24\u9053\u51b7\u800c\u9510\u5229\u7684\u76ee\u5149\uff0c\u4f7f\u8f9b\u6377\u5fae\u5fae\u611f\u5230\u6709\u4e9b\u4e0d\u5b89\uff0c\u4e8e\u662f\u8f9b\u6377\u8ba5\u7b11\u81ea\u5df1\uff1a\u201c\u6211\u600e\u4e48 \u7a81\u7136\u53d8\u5f97\u8fd9\u4e48\u65e0\u7528\uff0c\u751a\u81f3\u4f1a\u6015\u522b\u4eba\u7684\u76ee\u5149\u3002\u201d \u3000\u3000\u4e3a\u4e86\u8bc1\u660e\u81ea\u5df1\u7684\u52c7\u6c14\uff0c\u8f9b\u6377\u7ad9\u4e86\u8d77\u6765\uff0c\u671d\u8fd9\u767d\u8863\u602a\u5ba2\u5fae\u4e00\u62f1\u624b\uff0c\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u6709\u4f55 \u8d35\u5e72\uff1f\u201d \u3000\u3000\u65e0\u6068\u751f\u4f9d\u7136\u51b7\u9759\u5730\u671b\u7740\u4ed6\uff0c\u5fc3\u4e2d\u5728\u8003\u8651\u7740\u201c\u6d77\u5185\u7b2c\u4e00\u4eba\u201d\u7684\u201c\u4e03\u5999\u795e\u541b\u201d\u80fd\u4e0d\u80fd\u62b5 \u5f97\u8fc7\u81ea\u5df1\u4e09\u62db\uff0c\u56e0\u4e3a\u8f9b\u6377\u770b\u6765\u59d4\u5b9e\u662f\u592a\u5e74\u9752\u4e86\uff0c\u96be\u602a\u65e0\u6068\u751f\u4f1a\u6709\u8fd9\u6837\u7684\u611f\u89c9\uff0c\u4e8e\u662f\u4ed6\u50b2 \u7136\u9053\uff1a\u201c\u52a8\u624b\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u4e00\u60ca\uff0c\u4ed6\u5f88\u96be\u4e86\u89e3\u8fd9\u767d\u8863\u602a\u5ba2\u7a81\u7136\u53eb\u4ed6\u52a8\u624b\u7684\u7528\u610f\uff0c\u201c\u6211\u548c\u4ed6\u7d20\u65e0\u4ec7\u6028\u5440\u3002\u201d \u8f9b\u6377\u6697\u5fd6\u9053\u3002 \u3000\u3000\u65e0\u6068\u751f\u7709\u5934\u4e00\u76b1\uff0c\u5fd6\u9053\uff1a\u201c\u53cd\u6b63\u4ed6\u4e5f\u662f\u6210\u540d\u4eba\u7269\uff0c\u4ed6\u4e0d\u5148\u52a8\u624b\uff0c\u6211\u5c31\u5148\u52a8\u624b\u3002\u201d\u4e8e \u662f\u65e0\u6839\u751f\u5de6\u638c\u8f7b\u98d8\u98d8\u5730\u6325\u5411\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u81ea\u662f\u8bc6\u8d27\uff0c\u4ed6\u89c1\u8fd9\u4e00\u638c\u770b\u6765\u867d\u662f\u5e73\u6de1\u65e0\u5947\uff0c\u4f46\u5176\u4e2d\u6240\u8574\u80b2\u7740\u7684\u53d8\u5316\uff0c\u5374\u592a\u591a\u4e86\uff0c \u591a\u5f97\u4f7f\u4ed6\u4e0d\u6562\u968f\u610f\u53bb\u62db\u67b6\uff0c\u56e0\u4e3a\u4ed6\u660e\u786e\u5730\u77e5\u9053\uff0c\u4e5f\u552f\u6709\u201c\u4e0d\u62db\u67b6\u201d\u624d\u662f\u6700\u597d\u7684\u201c\u62db\u67b6\u201d\u3002 \u3000\u3000\u65e0\u6068\u751f\u51b7\u7b11\u4e00\u58f0\uff0c\u5fc3\u5fd6\uff1a\u201c\u8fd9\u53ae\u5012\u8bc6\u8d27\u3002\u201d\u53f3\u638c\u5212\u4e86\u4e2a\u534a\u5708\uff0c\u55d6\u5730\u63a8\u51fa\uff0c\u5de6\u624b\u53d8\u62db \u5f0f\uff0c\u6539\u6325\u4e3a\u63a8\uff0c\u53cc\u638c\u90fd\u6ce8\u6ee1\u4e86\u771f\u529b\uff0c\u4ed6\u4e0d\u60f3\u591a\u6495\u7f20\uff0c\u56e0\u4e3a\u65b9\u624d\u90a3\u4e00\u62db\uff0c\u4ed6\u5df2\u8bd5\u51fa\u8fd9\u201c\u4e03 \u5999\u795e\u541b\u201d\u786e\u975e\u7b49\u95f2\uff0c\u4fbf\u60f3\u4ee5\u6570\u5341\u5e74\u6765\u7684\u4fee\u4e3a\u5185\u529b\uff0c\u4e00\u4e3e\u53d6\u80dc\u3002 \u3000\u3000\u56e0\u4e3a\u5728\u8fd9\u5c0f\u8239\u4e0a\uff0c\u5bf9\u65b9\u6839\u672c\u6ca1\u6709\u8eb2\u907f\u7684\u4f59\u5730\uff0c\u4e5f\u53ea\u6709\u5c3d\u529b\u4e00\u62fc\uff0c\u548c\u4ed6\u5bf9\u8fd9\u4e00\u638c\u3002 \u3000\u3000\u4f46\u662f\u65e0\u6068\u751f\u5de7\u670d\u5f02\u679c\uff0c\u53c8\u5f97\u79d8\u7b95\uff0c\u518d\u52a0\u6570\u5341\u5e74\u7684\u4fee\u4e3a\uff0c\u638c\u529b\u4e4b\u5f3a\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u80fd\u80dc \u5f97\u8fc7\u4ed6\u7684\u6015\u4e5f\u662f\u7edd\u65e0\u4ec5\u6709\uff0c\u8f9b\u6377\u867d\u4e5f\u662f\u5929\u7eb5\u5947\u624d\uff0c\u4f46\u5230\u5e95\u5e74\u8f7b\uff0c\u6bd4\u8d77\u65e0\u6068\u751f\u6765\uff0c\u53ef\u5b9e\u5728 \u5dee\u5f97\u8fdc\u5462\u3002 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c \u56db \u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c \u56db \u7ae0 \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u638c\u5fc3\u5916\u9732\uff0c\u8272\u5982\u83b9\u7389\uff0c\u5fc3\u4e2d\u84e6\u5730\u4e00\u60ca\uff0c\u518d\u65e0\u601d\u8003\u7684\u4f59\u5730\uff0c\u771f\u6c14\u731b\u63d0\uff0c\u5237\u5730 \u62d4\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u4e34\u654c\u7ecf\u9a8c\u867d\u5f31\uff0c\u4f46\u4ed6\u5374\u6709\u4e00\u79cd\u654f\u9510\u7684\u5224\u65ad\u529b\uff0c\u4ed6\u82e5\u786c\u4ee5\u529f\u529b\u6765\u548c\u65e0\u6068\u751f\u8fd9\u4e00\u638c \u76f8\u6297\uff0c\u52bf\u5fc5\u8981\u9707\u4f24\u5185\u8151\uff0c\u8239\u8eab\u672c\u5c0f\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u4ed6\u53ea\u6709\u5192\u9669\u5c06\u8eab\u5f62\u62d4\u8d77\uff0c\u6682\u65f6\u907f\u8fc7\u8fd9\u62db \u518d\u8bf4\u3002 \u3000\u3000\u8f9b\u6377\u53cc\u81c2\u7ffc\u5f20\uff0c\u62d4\u8d77\u5728\u7a7a\u4e2d\uff0c\u5fc3\u91cc\u6781\u5feb\u5730\u8003\u8651\u7740\u8be5\u5982\u4f55\u5e94\u4ed8\u8fd9\u7a81\u6765\u7684\u5f3a\u654c\uff0c\u4ed6\u4e5f\u77e5 \u9053\u5f53\u4ed6\u8eab\u8eaf\u8fd9\u6b21\u843d\u4e0b\u7684\u65f6\u5019\uff0c\u4fbf\u662f\u81ea\u5df1\u7684\u751f\u6b7b\u5173\u5934\u4e86\u3002 \u3000\u3000\u60ca\u5f02\u7740\u5750\u5728\u8239\u8237\u4e0a\u7684\u91d1\u6885\u9f84\uff0c\u4e5f\u6b63\u5728\u5947\u602a\u8fd9\u8f7b\u529f\u9ad8\u7edd\u7684\u602a\u5ba2\u3002\u65e0\u6068\u751f\u638c\u52b2\u53d1\u51fa\uff0c\u638c \u98ce\u5fae\u5fae\u5e26\u8fc7\u5979\u3002\u5979\u53ea\u89c9\u6709\u4e00\u79cd\u96be\u4ee5\u5f62\u5bb9\u7684\u5f3a\u529b\u5411\u5979\u88ad\u6765\uff0c\u518d\u4e5f\u65e0\u6cd5\u7a33\u4f4f\u8eab\u8eaf\uff0c\u6574\u4e2a\u4eba\u88ab \u8fd9\u638c\u98ce\u5e26\u4e86\u8d77\u6765\uff0c\u6251\u5730\u843d\u4eba\u6c34\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u8eab\u8eaf\u4e00\u5f13\uff0c\u5728\u7a7a\u4e2d\u66fc\u5999\u7684\u8f6c\u6298\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u5237\u5730\u843d\u4e86\u4e0b\u6765\uff0c\u5728\u6c34\u4e2d\u5c06\u91d1\u6885\u9f84\u7684 \u540e\u9886\u4e00\u6284\uff0c\u4eba\u4e5f\u85c9\u7740\u8fd9\u4e00\u63d0\u4e4b\u529b\uff0c\u53c8\u62d4\u8d77\u4e08\u8bb8\uff0c\u4e24\u811a\u5411\u540e\u865a\u7a7a\u4e00\u8e74\uff0c\u98d8\u98d8\u843d\u5728\u5c0f\u8239\u7684\u53e6 \u4e00\u4fa7\u3002 \u3000\u3000\u4ed6\u51ed\u7740\u4e00\u53e3\u771f\u6c14\uff0c\u4ee5\u65e0\u6bd4\u7384\u7f8e\u7684\u59ff\u52bf\uff0c\u5c06\u843d\u5728\u6c34\u4e2d\u7684\u91d1\u6885\u9f84\u6551\u4e0a\u8239\u6765\uff0c\u8eab\u5f62\u786e\u5df1\u5230 \u4e86\u60ca\u4e16\u9a87\u4fd7\u7684\u5730\u6b65\u3002 \u3000\u3000\u65e0\u6068\u751f\u6697\u81ea\u70b9\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u7684\u529f\u592b\uff0c\u5728\u6b66\u6797\u4e2d\u7684\u786e\u662f\u7f55\u89c1\u7684\uff0c\u53ea\u53ef\u60dc\u8fd9\u6837\u7684\u4e00 \u4e2a\u4eba\uff0c\u5374\u662f\u4e2a\u6ca1\u6709\u4eba\u6027\u7684\u6deb\u5f92\uff0c\u6211\u4eca\u65e5\u4e0d\u4e3a\u4e16\u4eba\u9664\u5bb3\uff0c\u65e5\u540e\u53c8\u4e0d\u77e5\u6709\u591a\u5c11\u4e2a\u9ec4\u82b1\u95fa\u5973\u8981 \u574f\u5728\u4ed6\u624b\u4e0a\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u53c8\u662f\u5168\u8eab\u6e7f\u900f\uff0c\u53c8\u60ca\u53c8\u6012\uff0c\u8f9b\u6377\u5374\u5168\u795e\u6212\u5907\u7740\uff0c\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u8fd9\u53ae\u7a76\u7adf\u662f\u4ec0 \u4e48\u6765\u8def\uff0c\u638c\u529b\u5c45\u7136\u5df2\u7ec3\u5230\u5f52\u771f\u5b8c\u749e\u7684\u5730\u6b65\uff0c\u770b\u4ed6\u638c\u5fc3\u5982\u767d\u7389\uff0c\u96be\u9053\u4ed6\u5df2\u7ec3\u6210\u4e86\u6b66\u6797\u4e2d\u6570 \u767e\u5e74\u6765\u65e0\u4eba\u7ec3\u6210\u7684\u2018\u7384\u5973\u901a\u771f\u2019\u4e86\u3002\u201d \u3000\u3000\u4ed6\u77e5\u9053\u81ea\u5df1\u7684\u751f\u6b7b\u5c31\u60ac\u4e8e\u8fd9\u4e00\u5239\u90a3\u4e4b\u95f4\uff0c\u4ed6\u4e0d\u7981\u5fc6\u8d77\u5341\u5e74\u524d\u5929\u6b8b\u7126\u5316\u7684\u624b\u638c\u505c\u7559\u5728 \u4ed6\u5934\u9876\u7684\u90a3\u4e00\u523b\uff0c\u4f46\u662f\u6b64\u65f6\u5df2\u6ca1\u6709\u591a\u4f59\u7684\u65f6\u95f4\u5bb9\u4ed6\u601d\u8003\uff0c\u4ed6\u770b\u5230\u90a3\u4eba\u9762\u5982\u51dd\u971c\uff0c\u53c8\u626c\u638c \u5f85\u53d1\u3002 \u3000\u3000\u4ed6\u5fc3\u5934\u4e00\u51db\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u9601\u4e0b\u4e3a\u4f55\u5982\u6b64\u76f8\u903c\uff0c\u6211\u548c\u9601\u4e0b\u7d20\u65e0\u4ec7\u6028\u2026\u2026\u201d \u3000\u3000\u65e0\u6068\u751f\u76ee\u5149\u5982\u6c34\uff0c\u9690\u542b\u6740\u673a\u3002\u53f1\u9053\uff1a\u201c\u5c11\u7f57\u55e6\u3002\u201d\u8fdb\u8eab\u9519\u6b65\uff0c\u5c31\u5f85\u518d\u65bd\u715e\u624b\uff0c\u4ed6\u6210 \u5fc3\u4e0d\u8ba9\u5e74\u9752\u4eba\u9003\u51fa\u638c\u4e0b\u3002 \u3000\u3000\u7a81\u5730\uff0c\u53c8\u662f\u4e00\u6761\u767d\u5f71\uff0c\u6a2a\u6ce2\u63a0\u6765\u3002\u6084\u751f\u751f\u7ad9\u5728\u5c0f\u8239\u4e2d\u592e\uff0c\u65e0\u6068\u751f\u54a4\u9053\uff1a\u201c\u83c1\u513f\uff0c\u8d70 \u5f00\u3002\u201d \u3000\u3000\u5f20\u83c1\u5a07\u5524\u9053\uff1a\u201c\u7239\u7239\uff0c\u4f60\u8001\u2026\u2026\u201d \u3000\u3000\u65e0\u6068\u751f\u773c\u4e00\u77aa\uff0c\u9053\uff1a\u201c\u600e\u5730\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e0e\u91d1\u6885\u9f84\u4ff1\u90fd\u4e00\u60ca\uff0c\u6697\u5fd6\uff1a\u201c\u539f\u6765\u6b64\u4eba\u662f\u8fd9\u5c11\u5973\u7684\u7236\u4eb2\u3002\u201d\u4f46\u662f\u6b64\u4eba\u4e3a\u4f55\u8981\u4f24 \u81ea\u5df1\u5462\uff1f\u8f9b\u6377\u4ecd\u5982\u5760\u4e94\u91cc\u96fe\u4e2d\u3002 \u3000\u3000\u5f20\u83c1\u751c\u751c\u4e00\u7b11\uff0c\u671d\u5979\u7239\u7239\u8bf4\uff1a\u201c\u7239\u7239\uff0c\u770b\u4ed6\u5e74\u7eaa\u8fd9\u4e48\u8f7b\uff0c\u600e\u4e48\u4f1a\u662f\u4e5d\u963f\u59e8\u6240\u8bf4\u7684\u90a3 \u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u6562\u60c5\u5979\u5df2\u7531\u5979\u6bcd\u4eb2\u53e3\u4e2d\u77e5\u9053\u8fd9\u4e8b\u59cb\u672b\uff0c\u63a2\u9996\u7a97\u5916\uff0c\u770b\u5230\u81ea\u5df1\u7684\u7239\u7239\u8fde\u4e0b\u715e\u624b\uff0c\u4ed6\u5f53 \u7136\u975e\u5e38\u6e05\u695a\u5979\u7239\u7239\u7684\u529f\u529b\uff0c\u5fc3\u60f3\u90a3\u201c\u773c\u775b\u5927\u5927\u7684\u5e74\u9752\u4eba\u201d\u600e\u654c\u5f97\u4f4f\uff0c\u4e00\u6025\uff0c\u4e0d\u518d\u601d\u8651\uff0c \u4e5f\u7a9c\u4e0a\u5c0f\u8239\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u957f\u7709\u4e00\u8f69\uff0c\u6012\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff0c\u90a3\u4e48\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u7a81\u7136\u60f3\u8d77\u81ea\u5df1\u867d\u7136\u6570\u5341\u5e74\u6765\u5bb9\u989c\u672a\u6539\uff0c\u4f46\u5f53\u4e16\u4e4b\u4eba\u8fd8\u6709\u8c01\u80fd\u76f8\u6bd4\uff0c\u201c\u8fde\u5c0f\u6218\u5c9b\u7684 \u6167\u5927\u5e08\u90fd\u4e0d\u884c\uff0c\u5979\u56e0\u6b64\u6c14\u5f97\u53d1\u8a93\u4ece\u6b64\u4e0d\u518d\u51fa\u5c0f\u6218\u5c9b\u4e00\u6b65\u3002\u201d\u4e00\u5ff5\u81f3\u6b64\uff0c\u65e0\u6781\u5c9b\u4e3b\u4e0d\u7981\u6709 \u4e9b\u5f97\u610f\u7684\u611f\u89c9\u3002 \u3000\u3000\u5f20\u83c1\u773c\u775b\u4e00\u8f6c\uff0c\u77e5\u9053\u7239\u7239\u5fc3\u91cc\u5df1\u81ea\u6d3b\u52a8\uff0c\u53c8\u4fcf\u7b11\u9053\uff1a\u201c\u81f3\u5c11\u60a8\u8001\u4eba\u5bb6\u5f97\u95ee\u95ee\u4eba\u5bb6 \u5440\u3002\u201d \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u54fc\u4e86\u4e00\u58f0\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u59ae\u5b50\u600e\u5730\u4eca\u5929\u5c3d\u5e2e\u90a3\u4eba\u8bf4\u8bdd\uff0c\u83ab\u975e\u4e5f\u5bf9\u4ed6\u6709\u610f\u4e86\u3002 \u8fd9\u5c0f\u5b50\u8981\u662f\u6562\u52a8\u6211\u5973\u513f\u4e00\u6839\u6c57\u6bdb\uff0c\u6211\u4e0d\u628a\u4ed6\u8fde\u76ae\u90fd\u63ed\u4e0b\u6765\u624d\u602a\u3002\u201d\u4ed6\u6697\u81ea\u601d\u5fd6\u7740\uff0c\u201c\u53ea \u662f\u83c1\u513f\u7684\u8bdd\u4e5f\u6709\u9053\u7406\uff0c\u8fd9\u5c0f\u5b50\u770b\u6765\u6700\u591a\u53ea\u6709\u4e8c\u5341\u591a\u5c81\uff0c\u4e5f\u8bb8\u4e0d\u662f\u6885\u5c71\u6c11\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u201d \u3000\u3000\u5f20\u83c1\u4e0e\u5979\u7239\u4e00\u95ee\u4e00\u7b54\uff0c\u5fc3\u91cc\u66f4\u7cca\u6d82\uff0c\u5947\u602a\u7740\uff1a\u201c\u8fd9\u7236\u5973\u4e24\u4eba\u7a76\u7adf\u4e0e\u6211\u6709\u4ec0\u4e48\u7275\u8fde\u5440\uff0c \u2018\u4e5d\u963f\u59e8\u2019\uff0c\u2018\u4e5d\u963f\u59e8\u2019\u53c8\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u5374\u9f13\u7740\u816e\u5728\u4e00\u65c1\u751f\u6c14\uff0c\u8fd9\u5c11\u5973\u867d\u662f\u5e2e\u7740\u8f9b\u6377\uff0c\u91d1\u6885\u9f84\u5fc3\u4e2d\u5374\u4e00\u767e\u5eff\u4e94\u4e07\u4e2a\u4e0d \u613f\u610f\u3002 \u3000\u3000\u201c\u77a7\u5979\u7a7f\u7740\u602a\u6a21\u602a\u6837\u7684\uff0c\u51c6\u4e0d\u662f\u4e2a\u597d\u4eba\u3002\u201d\u5979\u5992\u706b\u5982\u711a\uff0c\u5f20\u83c1\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u5979\u90fd\u770b \u7740\u4e0d\u987a\u773c\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u8eab\u5f62\u5fae\u52a8\uff0c\u500f\u7136\u53c8\u7ad9\u5728\u8f9b\u6377\u8eab\u524d\uff0c\u5f20\u500f\u83c1\u5524\u4e86\u4e00\u58f0\uff0c\u54ea\u77e5\u5979\u7239\u7239\u5e76\u672a\u51fa\u624b\uff0c \u53ea\u662f\u5389\u58f0\u95ee\u9053\uff1a\u201c\u90a3\u624b\u5e15\u662f\u8c01\u7684\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e00\u6115\uff0c\u5f20\u810a\u63a5\u53e3\u9053\uff1a\u201c\u5c31\u662f\u4f60\u7ed9\u6211\u8499\u773c\u775b\u7684\u90a3\u5757\u561b\u3002\u201d\u8f9b\u6377\u4f1a\u610f\uff0c\u968f\u53e3\u9053\uff1a \u201c\u662f\u6211\u7684\u3002\u201d \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u8138\u4e00\u6c89\uff0c\u54a4\u9053\uff1a\u201c\u662f\u4f60\u7684\u5c31\u597d\uff01\u201d\u53cc\u81c2\u5fae\u4e00\u541e\u5410\uff0c\u52bf\u631f\u96f7\u9709\uff0c\u547c\u5730\u53c8\u662f\u4e00 \u62db\u3002 \u3000\u3000\u8f9b\u6377\u672c\u5728\u5168\u795e\u6212\u5907\uff0c\u89c1\u4ed6\u80a9\u4e00\u52a8\uff0c\u771f\u6c14\u731b\u5730\u5f80\u4e0b\u4e00\u6c89\uff0c\u90a3\u5c0f\u5c0f\u4e00\u53ea\u8239\uff0c\u600e\u7981\u5f97\u4f4f\u4ed6 \u8fd9\u79cd\u5185\u5bb6\u771f\u529b\uff1f\u547c\u5730\uff0c\u53cd\u4e86\u4e00\u4e2a\u8eab\uff0c\u8239\u5e95\u671d\u4e0a\u3002 \u3000\u3000\u5f20\u6c0f\u7236\u5973\u731d\u4e0d\u53ca\u9632\uff0c\u8eab\u5f62\u968f\u7740\u8239\u8eab\u4e00\u98d8\uff0c\u6c5f\u4e2d\u522b\u65e0\u843d\u8db3\u4e4b\u5904\uff0c\u53ea\u5f97\u53c8\u843d\u5728\u8239\u5e95\u4e0a\u3002 \u3000\u3000\u987b\u77e5\u65e0\u6781\u5c9b\u4e3b\u8f7b\u529f\u518d\u662f\u4f73\u5999\uff0c\u5374\u4e5f\u4e0d\u80fd\u5c06\u8eab\u8eaf\u505c\u5728\u6c5f\u9762\u4e0a\uff0c\u4ed6\u51cc\u6ce2\u800c\u884c\uff0c\u53ea\u4e0d\u8fc7\u85c9 \u7740\u7a7a\u6c14\u7684\u51b2\u6fc0\uff0c\u5c06\u4f53\u4e2d\u7684\u5148\u5929\u4e4b\u6c14\u4e0e\u4e4b\u5408\u800c\u4e3a\u4e00\u800c\u5df2\uff0c\u4f46\u82e5\u505c\u5728\u6c34\u9762\u4e0a\u4e0d\u52a8\uff0c\u5374\u662f\u4e07\u4e07 \u4e0d\u80fd\u3002 \u3000\u3000\u65e0\u6068\u751f\u9762\u76ee\u53d8\u8272\uff0c\u8f9b\u6377\u4e24\u5ea6\u4ece\u4ed6\u638c\u4e0b\u9003\u51fa\uff0c\u5df2\u4f7f\u4ed6\u6012\u6c14\u51b2\u5929\uff0c\u4ed6\u4fee\u4e3a\u767e\u5e74\uff0c\u6742\u5ff5\u4ff1 \u6d88\uff0c\u5c31\u53ea\u8fd9\u201c\u55d4\u201d\u4e4b\u4e00\u5b57\uff0c\u4ecd\u672a\u66fe\u7834\u5f97\u3002 \u3000\u3000\u5f20\u83c1\u6014\u7740\u773c\u671b\u7740\u4ed6\uff0c\u610f\u601d\u5728\u8bf4\uff1a\u201c\u600e\u4e48\u529e\u5462\uff1f\u201d \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u4ea6\u662f\u65e0\u6cd5\uff0c\u4ed6\u603b\u4e0d\u80fd\u4e0d\u4e0b\u6c34\u6349\u4eba\u5440\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u53cc\u638c\u8fde\u626c\uff0c\u6c5f\u9762\u4e0a\u7684\u6c34\uff0c \u88ab\u4ed6\u7684\u771f\u529b\u4e00\u51fb\u98de\u8d77\u6f2b\u5929\u6d6a\u82b1\uff0c\u58f0\u52bf\u7aef\u7684\u60ca\u4eba\u5df2\u6781\uff0c\u5f20\u83c1\u62cd\u624b\u7b11\u9053\uff1a\u201c\u5440\uff0c\u771f\u597d\u770b\uff0c\u771f \u597d\u770b\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u53cc\u811a\u7387\u6027\u9489\u5728\u8239\u5e95\u4e0a\uff0c\u7ffb\u4e86\u8eab\u7684\u5c0f\u8239\u52a8\u4e5f\u4e0d\u52a8\u5730\u505c\u5728\u6c5f\u9762\u4e0a\uff0c\u5c0f\u8239\u56db\u5468\u7684\u6c5f \u6c34\uff0c\u5374\u88ab\u65e0\u6781\u5c9b\u4e3b\u60ca\u4eba\u7684\u638c\u529b\u51b2\u6fc0\u6210\u4e00\u4e2a\u4e2a\u6c34\u7a74\uff0c\u6d6a\u82b1\u98de\u821e\uff0c\u4e00\u6761\u6761\u6d4a\u9ec4\u7684\u6c34\u67f1\uff0c\u5347\u5929 \u800c\u8d77\u3002 \u3000\u3000\u201c\u770b\u4f60\u5f80\u54ea\u91cc\u9003\u3002\u201d\u4ed6\u4e00\u770b\u8239\u7684\u56db\u5468\u6c5f\u5e95\u5e76\u65e0\u4eba\u8ff9\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u5c0f\u5b50\u4e00\u5b9a\u662f\u671d\u5cb8\u8fb9 \u6e38\u53bb\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u8f9b\u6377\u6839\u672c\u4e0d\u4f1a\u6e38\u6c34\uff01 \u3000\u3000\u7136\u800c\u8f9b\u6377\u6b64\u65f6\u53c8\u600e\u6837\u4e86\u5462\uff1f \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u53cc\u817f\u5fae\u66f2\uff0c\u4ee5\u65e0\u6bd4\u7684\u5185\u5bb6\u771f\u6c14\uff0c\u6467\u52a8\u7740\u8fd9\u5c0f\u8239\u671d\u5cb8\u8fb9\u79fb\u52a8\uff0c\u53cc\u638c\u4e0d\u505c\u5730\u671d \u6c5f\u9762\u4e0a\u6325\u52a8\uff0c\u6d6a\u82b1\u6c34\u67f1\uff0c\u6b64\u8d77\u5f7c\u843d\u3002 \u3000\u3000\u8fdc\u8fdc\u6709\u51e0\u6761\u6e14\u8239\u671b\u89c1\u6c5f\u9762\u4e0a\u7a81\u7136\u5347\u8d77\u4e00\u9053\u4e08\u8bb8\u9ad8\u7684\u6c34\u5899\uff0c\u5413\u5f97\u671b\u7a7a\u62dc\u5012\uff0c\u4ee5\u4e3a\u662f\u6c34 \u795e\u663e\u5723\u3002\u8fd9\u4e9b\u6c34\u4e0a\u8ba8\u751f\u6daf\u7684\u4eba\uff0c\u795e\u6743\u601d\u60f3\u6700\u91cd\uff0c\u6709\u7684\u751a\u81f3\u7acb\u523b\u4e70\u6765\u9999\u70db\uff0c\u5c31\u5728\u5cb8\u8fb9\u8bbe\u6848 \u795d\u7977\u4e86\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u5c06\u5c0f\u8239\u50ac\u79fb\u81f3\u8fd1\u5cb8\uff0c\u4ecd\u7136\u672b\u89c1\u8f9b\u6377\u7684\u8e2a\u8ff9\uff0c\u5f20\u83c1\u62bf\u7740\u5634\u7b11\u9053\uff1a\u201c\u7239\u7239\uff0c\u4eba \u5bb6\u4e0d\u4f1a\u671d\u90a3\u8fb9\u7684\u5cb8\u6e38\u8fc7\u53bb\u5417\uff1f\u201d \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u4e5f\u4e0d\u7981\u6697\u6697\u5931\u7b11\uff0c\u8138\u4e0a\u5374\u8e66\u5f97\u7d27\u7d27\u7684\uff0c\u4e24\u817f\u5fae\u66f2\uff0c\u5c0f\u8239\u500f\u5730\u53d8\u4e86\u4e2a\u65b9\u5411\uff0c \u5feb\u5f97\u5982\u79bb\u5f26\u4e4b\u7bad\uff0c\u671d\u5bf9\u5cb8\u5c04\u53bb\u3002 \u3000\u3000\u8fd9\u91cc\u6c5f\u9762\u6d6a\u82b1\uff0c\u8bb8\u4e45\u624d\u56de\u590d\u5e73\u9759\uff0c\u7a81\u5730\u6d6a\u82b1\u53c8\u662f\u4e00\u5192\uff0c\u6c5f\u6c34\u4e2d\u94bb\u51fa\u4e24\u4e2a\u5934\u6765\uff0c\u5374\u6b63 \u662f\u8f9b\u6377\u4e0e\u91d1\u6885\u9f84\u4e24\u4eba\u3002 \u3000\u3000\u539f\u6765\u5c0f\u8239\u4e00\u7ffb\uff0c\u8f9b\u6377\u5fc3\u4e2d\u65e9\u6709\u8ba1\u8f83\uff0c\u4e00\u624b\u62c9\u7740\u91d1\u6885\u9f84\uff0c\u5c4f\u4f4f\u547c\u5438\uff0c\u843d\u5165\u6c34\u4e2d\uff0c\u7b49\u5c0f \u8239\u7ffb\u8eab\u4e4b\u540e\u8239\u8179\u4e0e\u6c34\u9762\u4e4b\u95f4\uff0c\u81ea\u7136\u4f1a\u6709\u4e00\u5757\u7a7a\u9699\uff0c\u8f9b\u6377\u53e6\u4e00\u624b\u6293\u4f4f\u8239\u5f26\uff0c\u5934\u90e8\u4fbf\u4f38\u4eba\u8fd9 \u5757\u7a7a\u9699\u91cc\uff0c\u662f\u4ee5\u4e24\u4eba\u867d\u7136\u8eab\u5728\u6c34\u4e2d\uff0c\u5374\u65e2\u4e0d\u4f1a\u6c89\u5927\u6c34\u91cc\uff0c\u53c8\u4e0d\u81f4\u4e0d\u80fd\u547c\u5438\uff0c\u5c31\u7b97\u8eb2\u4e0a\u4e00 \u5929\uff0c\u4e5f\u7edd\u65e0\u95ee\u9898\u3002 \u3000\u3000\u91d1\u6885\u9f84\u89c1\u8f9b\u6377\u5982\u6b64\u673a\u7075\uff0c\u671d\u4ed6\u751c\u751c\u4e00\u7b11\uff0c\u9887\u4e3a\u8d5e\u8bb8\u3002 \u3000\u3000\u8239\u8179\u9ed1\u6d1e\u6d1e\u5730\uff0c\u8f9b\u6377\u77e5\u9053\u5f3a\u654c\u672b\u53bb\uff0c\u8fde\u5927\u58f0\u547c\u5438\u90fd\u4e0d\u6562\uff0c\u4ed6\u542c\u5230\u56db\u5468\u6c34\u58f0\u8f70\u7136\uff0c\u66f4 \u662f\u5fc3\u60ca\u3002 \u3000\u3000\u540e\u6765\u4ed6\u611f\u89c9\u5230\u5c0f\u8239\u5728\u5fae\u5fae\u79fb\u52a8\uff0c\u534a\u54cd\uff0c\u4ed6\u811a\u5e95\u4f3c\u4e4e\u78b0\u5230\u5b9e\u5730\uff0c\u77e5\u9053\u8239\u5fc5\u5df1\u79bb\u5cb8\u751a\u8fd1 \u4e86\u3002 \u3000\u3000\u7b49\u5230\u5f20\u83c1\u5728\u4e0a\u9762\u51fa\u58f0\u8bf4\u8bdd\uff0c\u4ed6\u77e5\u9053\u8fd9\u5c11\u5973\u5728\u6697\u4e2d\u5e2e\u7740\u81ea\u5df1\uff0c\u5fc3\u91cc\u53d7\u7528\u5f97\u5f88\uff0c\u968f\u5373\u60f3 \u5230\u5979\u7239\u5fc5\u4f1a\u50ac\u52a8\u7740\u8fd9\u5c0f\u821f\u81f3\u53e6\u4e00\u5cb8\uff0c\u62c9\u7740\u91d1\u6885\u9f84\u53c8\u6c89\u5165\u6c34\u4e2d\uff0c\u4ed6\u53cc\u811a\u5df2\u80fd\u8e0f\u7740\u5730\u5e95\uff0c\u5fc3 \u4e2d\u81ea\u662f\u5927\u5b9a\u3002 \u3000\u3000\u4e24\u4eba\u5c4f\u7740\u547c\u5438\u5728\u6c34\u5e95\u826f\u4e45\uff0c\u987b\u77e5\u4ed6\u4e24\u4eba\u4ff1\u4e3a\u5185\u5bb6\u9ad8\u624b\uff0c\u5c4f\u7740\u547c\u5438\u81ea\u4e0d\u56f0\u96be\uff0c\u7b49\u8f9b\u6377 \u786e\u5b9a\u5f3a\u654c\u5df2\u79bb\u8fdc\u53bb\uff0c\u624d\u6084\u6084\u4f38\u51fa\u5934\u6765\u3002 \u3000\u3000\u4ed6\u56db\u4e0b\u671b\u4e00\u4e0b\uff0c\u89c1\u6c5f\u9762\u5df2\u65e0\u654c\u8e2a\uff0c\u5598\u4e86\u4e00\u53e3\u6c14\uff0c\u4e0e\u91d1\u6885\u9f84\u6084\u6084\u8df3\u5230\u5cb8\u4e0a\uff0c\u6697\u9053\uff1a \u201c\u4fa5\u5e78\u201d\u3002 \u3000\u3000\u4ed6\u4fe9\u6e7f\u900f\u4e86\u7684\u8863\u670d\uff0c\u88ab\u884c\u52a8\u65f6\u7684\u98ce\u58f0\u5e26\u52a8\u5f97\u201c\u7c0c\u7c0c\u201d\u5730\u54cd\u3002 \u3000\u3000\u201c\u8ba8\u538c\u3002\u201d\u91d1\u6885\u9f84\u6084\u9a82\u7740\uff0c\u4e00\u9762\u5c06\u8d34\u5728\u8eab\u4e0a\u7684\u8863\u88f3\u62c9\u4e86\u62c9\uff0c\u8f9b\u6377\u5219\u7b11\u8138\u671b\u7740\u5979\uff0c\u4ed6 \u811a\u5c16\u5fae\u4e00\u70b9\u5730\uff0c\u4eba\u4fbf\u63a0\u51fa\u6570\u4e08\u5f00\u5916\u3002 \u3000\u3000\u5f53\u4ed6\u4fe9\u90fd\u5df2\u611f\u5230\u8fd9\u4e24\u65e5\u6765\u7684\u60ca\u9669\u5df2\u6210\u8fc7\u53bb\u2026\u2026 \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u4fe9\u4eba\u8eab\u540e\u591a\u4e86\u4e00\u6761\u767d\u8272\u7684\u4eba\u5f71\uff0c\u624b\u671d\u6beb\u65e0\u6240\u89c9\u7684\u8f9b\u6377\u7684\u80cc\u4e0a\u201c\u7384\u5173\u7a74\u201d\u70b9 \u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u91d1\u6885\u9f84\u84e6\u7136\u89c9\u5f97\u8eab\u65c1\u7684\u8f9b\u6377\u505c\u987f\u4e86\uff0c\u5979\u505c\u4e0d\u4f4f\u811a\uff0c\u8eab\u5f62\u4ecd\u5f80\u524d\u63a0\u4e86\u4e08\u8bb8\uff0c\u624b\u8155\u4e00\u7a7a\uff0c \u5979\u60ca\u5fd6\uff1a\u201c\u600e\u5730\u4e86\u201d\u56de\u5934\u4e00\u671b\uff0c\u4e00\u6761\u6de1\u767d\u7684\u5f71\u5b50\u4e00\u6643\uff0c\u8f9b\u6377\u4e5f\u4e0d\u77e5\u6240\u8e2a\uff0c\u63a5\u7740\uff0c\u5979\u542c\u5230 \u4e00\u4e2a\u6781\u751c\u7f8e\u7684\u58f0\u97f3\u81ea\u7a7a\u4e2d\u4f20\u6765\uff1a\u201c\u59d1\u5a18\uff0c\u4f60\u7684\u4eba\u6211\u5e26\u8d70\u4e86\uff0c\u4e0d\u8fc7\uff0c\u8bb0\u7740\uff0c\u6211\u662f\u4e3a\u4f60\u597d\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u4f46\u89c9\u4e00\u9635\u6655\u7729\uff0c\u56db\u91ce\u5bc2\u7136\uff0c\u6839\u672c\u6ca1\u6709\u4eba\u8ff9\uff0c\u4f46\u8fd9\u58f0\u97f3\u4ece\u54ea\u91cc\u6765\u7684\u5462\uff1f \u3000\u3000\u201c\u96be\u9053\u662f\u2018\u4f20\u97f3\u5165\u5bc6\u2019\u3002\u201d\u5979\u53c8\u662f\u4e00\u9635\u6655\u7729\u3002 \u3000\u3000\u5fae\u98ce\u5439\u5904\uff0c\u5927\u5730\u4e0a\u4f3c\u4e4e\u53ea\u5269\u4e0b\u5979\u4e00\u4e2a\u4eba\uff0c\u5b64\u72ec\uff0c\u5bc2\u5bde\u548c\u60ca\u60e7\uff0c\u201c\u6377\u54e5\u54e5\uff0c\u4f60\u5230\u5e95\u600e \u4e48\u6837\u4e86\u5440\uff1f\u201d\u5979\u53d1\u72c2\u5730\u671d\u90a3\u767d\u5f71\u6d88\u5931\u7684\u65b9\u5411\u5954\u53bb\u3002 \u3000\u3000\u6643\u773c\u5230\u4e86\u5cb8\u8fb9\uff0c\u6c5f\u6c34\u4e1c\u6d41\uff0c\u6c5f\u5fc3\u6b63\u6709\u4e00\u8258\u5927\u8239\u626c\u5e06\u4e1c\u53bb\uff0c\u98ce\u5439\u7740\uff0c\u4e00\u5757\u70e7\u7126\u7684\u6728\u7247 \u6eda\u5230\u5979\u811a\u4e0b\u3002 \u3000\u3000\u5979\u4fef\u8eab\u62fe\u4e86\u8d77\u6765\uff0c\u67d4\u80a0\u767e\u7ed3\u3002 \u3000\u3000\u201c\u8fd9\u5c31\u662f\u6628\u5929\u6211\u66ff\u6377\u54e5\u54e5\u751f\u706b\u65f6\u7684\u6728\u5934\u5427\uff0c\u6377\u54e5\u54e5\uff0c\u4f60\u5230\u54ea\u91cc\u53bb\u4e86\u5440\uff1f\u201d\u6676\u83b9\u7684\u6cea \u73e0\uff0c\u6d41\u8fc7\u5979\u5ae3\u7ea2\u7684\u9762\u988a\u3002 \u3000\u3000\u8fd9\u4e24\u65e5\u6765\u7684\u751f\u6b7b\u640f\u6597\uff0c\u4f3c\u6c34\u67d4\u60c5\uff0c\u90fd\u50cf\u68a6\u5883\u822c\u5730\u6c38\u7559\u5728\u5979\u5fc3\u5934\uff0c\u4f46\u68a6\u4e2d\u7684\u4eba\u5374\u5df2\u4e0d \u77e5\u53bb\u5411\u4e86\u3002 \u3000\u3000\u5979\u4e24\u65e5\u6765\u672a\u8fdb\u6c34\u7c73\uff0c\u518d\u52a0\u8fd9\u7cbe\u795e\u4e0a\u5982\u6b64\u91cd\u7684\u523a\u6fc0\uff0c\u5979\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u865a\u8f6f\u5730\u5012\u5728\u5730 \u4e0a\u3002 \u3000\u3000\u5979\u6655\u8ff7\u4e86\u3002 \u3000\u3000\u6655\u8ff7\u4e2d\uff0c\u5979\u4eff\u4f5b\u542c\u5230\u6709\u4eba\u8bf4\u8bdd\u7684\u58f0\u97f3\uff0c\u5979\u89c9\u5f97\u5634\u4e2d\u82e6\u82e6\u7684\uff0c\u50cf\u662f\u88ab\u4eba\u704c\u4e86\u4e9b\u836f\u3002 \u3000\u3000\u53c8\u534a\u54cd\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u5979\u53ef\u4ee5\u542c\u5f97\u6e05\u695a\u4e9b\u4e86\uff0c\u521a\u60f3\u7741\u5f00\u773c\u6765\uff0c\u7a81\u7136\u611f\u89c9\u5230\u6709\u53ea\u624b\u5728\u5979 \u8eab\u4e0a\u4e00\u78b0\uff0c\u63a5\u7740\u201c\u5427\u201d\u7684\u4e00\u4e0b\uff0c\u662f\u4e24\u638c\u76f8\u62cd\u7684\u58f0\u97f3\uff0c\u4e00\u4e2a\u7c97\u54d1\u7684\u53e3\u97f3\u8bf4\u9053\uff1a\u201c\u8001\u738b\uff0c\u4f60 \u53ef\u4e0d\u80fd\u4e0d\u8bb2\u4ea4\u60c5\uff0c\u8fd9\u5c0f\u59d0\u513f\u662f\u6211\u53d1\u73b0\u7684\uff0c\u81f3\u5c11\u5f97\u8ba9\u6211\u5360\u4e2a\u5934\u7b79\uff0c\u4f60\u4e71\u52a8\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u7c97\u58f0\u7c97\u6c14\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u9053\uff1a\u201c\u4f60\u600e\u4e48\u60e9\u5730\u5c0f\u6c14\uff0c\u6478\u4e00\u628a\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u201d \u3000\u3000\u201c\u4e0d\u51c6\u4f60\u6478\u3002\u201d\u5148\u524d\u4e00\u4eba\u9053\u3002 \u3000\u3000\u201c\u597d\u597d\uff0c\u4e0d\u6478\u5c31\u4e0d\u6478\u3002\u201d\u53e6\u4e00\u4eba\u53c8\u7b11\u9053\uff0c\u201c\u5582\uff0c\u4f60\u4e5f\u5f97\u5feb\u4e00\u70b9\u5440\uff0c\u7b49\u5148\u5b8c\u4e8b\u4e86\uff0c\u6211 \u8fd8\u60f3\u8f84\u8fdb\u4e00\u817f\u5462\uff0c\u4e0d\u7136\u7b49\u4f1a\u5b59\u8001\u4e8c\u6765\uff0c\u5927\u5bb6\u90fd\u6ca1\u4efd\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u5c06\u8fd9\u4e9b\u8bdd\u542c\u5230\u6e05\u6e05\u695a\u695a\uff0c\u6697\u9a82\u9053\uff1a\u201c\u597d\u4e2a\u4e0d\u957f\u773c\u775b\u7684\u6740\u80da\uff0c\u4f60\u662f\u627e\u6b7b\u3002\u201d\u8d8a \u53d1\u5c06\u773c\u6674\u95ed\u5f97\u7d27\u7d27\u5730\u3002 \u3000\u3000\u5148\u524d\u90a3\u4eba\u54c8\u54c8\u7b11\u4e86\u8d77\u6765\u9053\uff1a\u201c\u4e5f\u6ca1\u770b\u89c1\u4f60\u8fd9\u6837\u6027\u6025\u7684\u4eba\uff0c\u8fd9\u5c0f\u59d0\u8fd8\u6ca1\u6709\u9192\uff0c\u5f04\u8d77\u6765 \u6ca1\u6709\u5473\u9053\u3002\u201d \u3000\u3000\u505c\u4e86\u4e00\u4f1a\uff0c\u597d\u50cf\u4ed6\u81ea\u5df1\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u9053\uff1a\u201c\u597d\u597d\uff0c\u4f9d\u4f60\uff0c\u6211\u5c31\u9a6c\u9a6c\u864e\u864e\u5148\u5f04\u4e00\u4e0b\u5427\uff01 \u53ef\u662f\u54b1\u4eec\u5f97\u5148\u8bb2\u597d\u4e86\uff0c\u8fd9\u5c0f\u59d0\u662f\u6211\u7684\uff0c\u4f60\u8981\u8f84\u4e00\u811a\u4e5f\u53ef\u4ee5\uff0c\u53ef\u5f97\u5148\u62ff\u70b9\u94f6\u5b50\u6765\u5b5d\u656c\u5b5d\u656c \u6211\u3002\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u602a\u7b11\u9053\uff1a\u201c\u8d75\u8001\u5927\u7684\u8bdd\uff0c\u8fd8\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u8fd9\u5c0f\u59d0\u6bd4\u9996\u5584\u91cc\u7684\u7a91\u59d0\u513f\u597d\u591a\u4e86\uff0c \u4e00\u4e24\u94f6\u5b50\u4e00\u6b21\u90fd\u503c\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u6697\u6697\u54ac\u7259\uff0c\u5979\u6050\u6015\u81ea\u5df1\u7684\u6c14\u529b\u672b\u590d\uff0c\u662f\u4ee5\u8fdf\u8fdf\u6ca1\u6709\u53d1\u96be\uff0c\u5c06\u773c\u6674\u772f\u5f00\u4e00\u7ebf\uff0c \u770b\u5230\u81ea\u5df1\u4ecd\u662f\u8eba\u5728\u9732\u5929\u91cc\uff0c\u53ea\u662f\u73b0\u5728\u5929\u5df2\u9ed1\u4e86\uff0c\u8ff7\u8ff7\u8499\u8499\u5730\u770b\u5230\u6709\u4e24\u6761\u7c97\u957f\u6c49\u5b50\u6b63\u7ad9\u5728 \u81ea\u5df1\u8eab\u524d\u3002 \u3000\u3000\u201c\u8d75\u8001\u5927\u201d\u6deb\u7b11\u7740\u8131\u6389\u4e0a\u8863\uff0c\u4fef\u4e0b\u8eab\u6765\u60f3\u53bb\u89e3\u91d1\u6885\u9f84\u7684\u8863\u670d\uff0c\u4e00\u9762\u8bf4\uff1a\u201c\u8001\u738b\uff0c\u4f60 \u7ad9\u8fdc\u70b9\u3002\u201d \u3000\u3000\u201c\u8001\u738b\u201d\u53c8\u602a\u7b11\u7740\uff0c\u773c\u6674\u6ef4\u6e9c\u6e9c\u5730\u5728\u8eba\u7740\u7684\u91d1\u6885\u9f84\u8eab\u4e0a\u6253\u8f6c\uff0c\u8bf4\uff1a\u201c\u597d\uff0c\u6211\u7ad9\u8fdc\u70b9 \u5c31\u7ad9\u8fdc\u70b9\u3002\u201d\u811a\u4e0b\u5374\u672a\u79fb\u52a8\u534a\u5206\u3002 \u3000\u3000\u4ed6\u7b11\u58f0\u672a\u4e86\uff0c\u5df2\u662f\u4e00\u58f0\u60ca\u547c\uff0c\u539f\u6765\u8d75\u8001\u5927\u5e9e\u5927\u7684\u8eab\u8eaf\u76f4\u98de\u4e86\u51fa\u53bb\uff0c\u201c\u53ed\u201d\u5730\u843d\u5728\u5730 \u4e0a\uff0c\u58f0\u97f3\u4ff1\u65e0\uff0c\u50cf\u662f\u5df2\u7ecf\u6b7b\u4e86\u3002 \u3000\u3000\u201c\u8001\u738b\uff0c\u8e6c\u8e6c\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u56db\u4e0b\u6253\u91cf\uff0c\u89c1\u90a3\u88ab\u81ea\u5df1\u5728\u5cb8\u8fb9\u53d1\u73b0\u7684\u5973\u5b50\uff0c\u8fd8\u662f\u597d\u597d\u5730 \u8eba\u5728\u5730\u4e0a\uff0c\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4ed6\u53c8\u60ca\u53c8\u6015\uff0c\u4ee5\u4e3a\u649e\u89c1\u9b3c\u4e86\uff0c\u6251\u5730\u8dea\u5230\u5730\u4e0a\uff0c\u53e9\u5934\u5982\u6363\u849c\uff0c\u5634\u91cc \u561f\u561f\u5495\u5495\u5730\uff0c\u50cf\u5728\u6c42\u544a\u3002\u91d1\u6885\u9f84\u6697\u5730\u597d\u7b11\uff0c\u65b9\u624d\u90a3\u201c\u8d75\u8001\u5927\u201d\u521a\u4f0f\u4e0b\u6765\u4e86\uff0c\u5979\u5c31\u75be\u4f38\u53f3 \u624b\uff0c\u4e00\u638c\u62cd\u5728\u201c\u8d75\u8001\u5927\u201d\u80f8\u524d\u3002 \u3000\u3000\u5979\u867d\u7136\u6c14\u529b\u5c1a\u672a\u56de\u590d\uff0c\u4f46\u50cf\u201c\u8d75\u8001\u5927\u201d\u8fd9\u6837\u7684\u89d2\u8272\uff0c\u600e\u7981\u5f97\u4e86\u5979\u4e00\u4e0b\uff0c\u5f53\u573a\u5fc3\u8109\u9707 \u65ad\u800c\u6b7b\u3002 \u3000\u3000\u201c\u8001\u738b\u201d\u600e\u77e5\u9053\u8fd9\u5973\u5b50\u8eab\u6000\u7edd\u6280\uff0c\u6b63\u81ea\u7591\u795e\u7591\u9b3c\uff0c\u95ed\u7740\u773c\u6674\u53e9\u5934\uff0c\u5ffd\u5730\u5f53\u80f8\u7740\u4e86\u4e00 \u811a\uff0c\u6eda\u51fa\u597d\u51e0\u6b65\u53bb\u3002 \u3000\u3000\u4ed6\u53c8\u4e00\u58f0\u60ca\u53eb\uff0c\u722c\u8d77\u6765\u5c31\u8dd1\uff0c\u5374\u542c\u5230\u4e00\u4e2a\u5389\u543c\u9053\uff1a\u201c\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u201c\u8001\u738b\u201d\u4e24\u6761\u817f\u4e00\u8f6f\uff0c\u53c8\u8dea\u4e86\u4e0b\u53bb\uff0c\u56de\u8fc7\u5934\u53bb\u4e00\u770b\uff0c\u81ea\u5df1\u7684\u4e8c\u5934\u9886\uff0c\u4e5f\u662f\u81ea\u5df1\u5e73\u65e5 \u60e7\u6015\u7684\u201c\u6d6a\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\u201d\u6b63\u7ad9\u5728\u8eab\u540e\u3002 \u3000\u3000\u539f\u6765\u8fd9\u201c\u8001\u738b\u201d\u548c\u201c\u8d75\u8001\u5927\u201d\u90fd\u662f\u957f\u6c5f\u4e0a\u7684\u6c34\u5bc7\uff0c\u8fd9\u665a\u4ed6\u4eec\u4e24\u8258\u8239\u6b63\u505c\u6cca\u5728\u90bb\u8fd1\u9ec4 \u5c97\u7684\u4e00\u4e2a\u6c5f\u6e7e\u65c1\uff0c\u201c\u8001\u738b\u201d\u548c\u201c\u8d75\u8001\u5927\u201d\u5230\u5cb8\u8fb9\u5de1\u903b\uff0c\u770b\u5230\u6709\u4e2a\u7edd\u7f8e\u5973\u5b50\u5012\u5367\u5728\u5cb8\u8fb9\uff0c \u4ed6\u4eec\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\uff0c\u574f\u4e3b\u610f\u4e00\u6253\uff0c\u5c31\u7ed9\u5979\u704c\u4e86\u4e9b\u6210\u836f\u4e0b\u53bb\u3002 \u3000\u3000\u7b49\u5230\u201c\u8d75\u8001\u5927\u201d\u8eab\u6b7b\uff0c\u201c\u8001\u738b\u201d\u72c2\u53eb\uff0c\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\u6b63\u5728\u9644\u8fd1\u5de1\u67e5\uff0c\u542c\u89c1\u58f0\u97f3\u4fbf \u8dd1\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u770b\u5230\u5730\u4e0a\u8eba\u7740\u4e00\u4e2a\u5973\u4eba\uff0c\u9694\u4e86\u51e0\u6b65\u5374\u662f\u4e00\u5177\u6b7b\u5c38\uff0c\u201c\u8001\u738b\u201d\u8dea\u5728\u5730\u4e0a\u4e0d\u77e5\u6363\u4ec0\u4e48 \u9b3c\uff0c\u5fc3\u91cc\u4e00\u6c14\uff0c\u8d70\u8fc7\u53bb\u4e00\u811a\u5c06\u4ed6\u8e22\u4e86\u4e2a\u6eda\u6e9c\u3002 \u3000\u3000\u8001\u738b\u4e00\u770b\u4ed6\u6765\u4e86\uff0c\u5413\u5f97\u6bd4\u89c1\u4e86\u9b3c\u8fd8\u5389\u5bb3\u3002 \u3000\u3000\u91d1\u6885\u9f84\u4e00\u770b\u89c1\u6b64\u4eba\uff0c\u5fc3\u91cc\u5374\u6697\u81ea\u9ad8\u5174\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u662f\u4f60\u4eec\u8fd9\u6279\u4e1c\u897f\u5440\u3002\u201d\u7686\u56e0\u8fd9 \u5b59\u8d85\u8fdc\u4e0e\u5929\u9b54\u91d1\u6b39\u76f8\u5904\u751a\u597d\uff0c\u8fdc\u5728\u6570\u5e74\u524d\u91d1\u6b39\u521d\u51fa\u6c5f\u6e56\uff0c\u4fbf\u5df1\u8bc6\u5f97\u6b64\u4eba\uff0c\u5e76\u4e14\u5e26\u4ed6\u89c1\u8fc7 \u91d1\u4e00\u9e4f\u3002 \u3000\u3000\u6240\u4ee5\u91d1\u6885\u9f84\u4e5f\u8bc6\u5f97\u4ed6\uff0c\u5fc3\u4e2d\u5927\u5b9a\u3002 \u3000\u3000\u5b59\u8d85\u8fdc\u51b7\u54fc\u4e00\u58f0\uff0c\u8d70\u8fc7\u53bb\u4fef\u8eab\u4e00\u770b\uff0c\u201c\u8d75\u8001\u5927\u201d\u7adf\u662f\u88ab\u4eba\u7528\u91cd\u624b\u6cd5\u6253\u6b7b\u7684\uff0c\u6697\u81ea\u5947 \u602a\u4f55\u6765\u6b64\u5185\u5bb6\u9ad8\u624b\u3002 \u3000\u3000\u201c\u60f3\u5fc5\u662f\u8fd9\u4e24\u4e2a\u8822\u624d\u5728\u6b64\u6b3a\u51cc\u5f31\u5973\u5b50\uff0c\u88ab\u4e00\u8def\u7ecf\u6b64\u5904\u7684\u9ad8\u624b\u6240\u89c1\u2026\u2026\u201d\u4ed6\u8f6c\u8eab\u53bb\u770b \u90a3\u201c\u5f31\u5973\u5b50\u201d\uff0c\u201c\u54a6\u201d\u4e86\u4e00\u58f0\u9a6c\u4e0a\u5c06\u8fd9\u63a8\u60f3\u6253\u7ffb\u4e86\u3002 \u3000\u3000\u7e41\u661f\u6ee1\u5929\uff0c\u534a\u5f26\u6708\u660e\uff0c\u4ed6\u4f9d\u7a00\u4ecd\u53ef\u770b\u5230\u8fd9\u5973\u5b50\u201c\u7fe0\u7eff\u8272\u201d\u7684\u8863\u88d9\uff0c\u9edb\u7709\u5782\u9f3b\uff0c\u98ce\u773c \u6a31\u5507\u3002 \u3000\u3000\u201c\u539f\u6765\u662f\u5979\u3002\u201d\u5b59\u8d85\u8fdc\u5728\u60ca\u5f02\u4e2d\u8fd8\u5939\u6709\u6050\u60e7\uff0c\u6697\u5fd6\uff0c\u201c\u5979\u600e\u5730\u4f1a\u8dd1\u5230\u6b64\u5730\u6765\uff0c\u5374\u53c8 \u8863\u88d9\u96f6\u4e71\uff0c\u9b13\u53d1\u84ec\u677e\uff0c\u6a21\u6837\u6041\u5730\u72e0\u72c8\u3002\u201d\u8f6c\u5ff5\u53c8\u5fd6\uff0c\u201c\u8fd9\u4e24\u4e2a\u8be5\u6b7b\u7684\u6df7\u86cb\u4e0d\u77e5\u4f5c\u4e86\u4f55\u4e8b \u88ab\u5979\u4e00\u638c\u51fb\u6bd9\u3002\u201d \u3000\u3000\u4ed6\u60ca\u7591\u4ea4\u96c6\uff0c\u8d70\u4e0a\u524d\u53bb\u671d\u91d1\u6885\u9f84\u8eac\u8eab\u9053\uff1a\u201c\u91d1\u59d1\u5a18\u597d\u2026\u2026\u201d \u3000\u3000\u91d1\u6885\u9f84\u51b7\u7b11\u4e00\u4e0b\uff0c\u5374\u4e0d\u7406\u4ed6\u3002 \u3000\u3000\u201c\u8001\u738b\u201d\u89c1\u81ea\u5df1\u7684\u5934\u9886\u5bf9\u8fd9\u5973\u5b50\u8fd9\u822c\u606d\u656c\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u51b7\u6c57\u6d94\u6d94\u843d\u4e0b\uff0c\u5168\u8eab\u98a4 \u4e2a\u4e0d\u4f4f\u3002 \u3000\u3000\u5b59\u8d85\u8fdc\u4ea6\u662f\u5fc3\u5934\u6253\u9f13\uff0c\u4e0d\u77e5\u9053\u8fd9\u4f4d\u201c\u6bd2\u541b\u201d\u7684\u5343\u91d1\u5728\u4f5c\u4f55\u6253\u7b97\uff0c\u4ed6\u5b9e\u5728\u60f9\u4e0d\u8d77\u201c\u5929 \u9b54\u91d1\u6b39\u201d\uff0c\u66f4\u60f9\u4e0d\u8d77\u201c\u6bd2\u541b\u201d\uff0c\u552f\u6050\u91d1\u6885\u9f84\u8fc1\u6012\u4e0e\u4ed6\uff0c\u8c26\u5351\u5730\u8bf4\u9053\uff1a\u201c\u5728\u4e0b\u4e0d\u77e5\u9053\u91d1\u59d1 \u5a18\u5927\u9a7e\u5149\u4e34\uff0c\u6709\u5931\u8fdc\u8fce\uff0c\u52a1\u8bf7\u79fb\u6b65\u655d\u821f\uff0c\u5bb9\u5728\u4e0b\u7565\u8868\u5bf8\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u8eab\u4e3a\u957f\u6c5f\u6c34\u8def\u7684\u526f\u603b\u74e2\u628a\u5b50\uff0c\u624b\u4e0b\u7684\u5f1f\u5144\u4f55\u6b62\u5343\u4eba\uff0c\u6b64\u65f6\u5730\u5bf9\u91d1\u6885\u9f84\u5982\u6b64\u606d\u656c\uff0c \u53ef\u89c1\u201c\u6bd2\u541b\u201d\u548c\u201c\u5929\u9b54\u91d1\u6b39\u201d\u5728\u6c5f\u6e56\u4e2d\u7684\u5730\u4f4d\u3002 \u3000\u3000\u91d1\u6885\u9f84\u51b7\u7b11\u7740\u98d8\u8eab\u7ad9\u4e86\u8d77\u6765\uff0c\u811a\u4e0b\u4ecd\u662f\u865a\u98d8\u98d8\u7684\uff0c\u5979\u5012\u6ca1\u6709\u53d7\u4f24\uff0c\u53ea\u662f\u4e24\u5929\u6765\u6ca1\u6709 \u7528\u8fc7\u98df\u7269\uff0c\u8179\u4e2d\u7a7a\u7a7a\u800c\u5df2\u3002 \u3000\u3000\u5979\u6307\u7740\u201c\u8001\u738b\u201d\u9053\uff1a\u201c\u8fd9\u53ae\u662f\u4f60\u7684\u624b\u4e0b\u5417\uff1f\u6211\u770b\u65e9\u8be5\u5c06\u4ed6\u2026\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u6ca1\u7b49\u5979\u8bf4\u5b8c\uff0c\u5df2\u8fde\u58f0\u7b54\u9053\uff1a\u201c\u662f\uff0c\u662f\u3002\u201d\u4e00\u8f6c\u8eab\uff0c\u7a9c\u5230\u201c\u8001\u738b\u201d\u3002\u8eab\u524d\uff0c\u5355 \u638c\u4e0b\u5288\uff0c\u7adf\u662f\u201c\u94c1\u7802\u638c\u201d\uff0c\u5c06\u201c\u8001\u738b\u201d\u7684\u5929\u7075\u76d6\u5288\u5f97\u7c89\u7cb9\u3002 \u3000\u3000\u91d1\u6885\u9f84\u53cd\u4e00\u60ca\uff0c\u5979\u672c\u53ea\u662f\u60f3\u53eb\u5b59\u8d85\u8fdc\u7565\u4e3a\u60e9\u6212\u4ed6\u800c\u5df2\uff0c\u54ea\u77e5\u5b59\u8d85\u8fdc\u5374\u7a81\u4e0b\u8fa3\u624b\uff0c\u5979 \u4e0d\u7981\u89c9\u5f97\u6b64\u4eba\u6709\u4e9b\u53ef\u601c\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u4ed6\u4e0d\u8fc7\u53ea\u8bb2\u4e86\u4e24\u53e5\u7c97\u8bdd\u800c\u5df2\u2026\u201d\u968f\u8f6c\u5ff5\u9053\uff1a\u201c\u6211\u53ef \u601c\u4ed6\uff0c\u6709\u8c01\u53ef\u601c\u6211\u5462\uff1f\u201d \u3000\u3000\u5979\u5fc3\u4e00\u65e0\u6240\u89c9\uff0c\u832b\u832b\u7136\u5730\u8ddf\u7740\u5b59\u8d85\u8fdc\u79fb\u52a8\u7740\u6b65\u5b50\uff0c\u5b59\u8d85\u8fdc\u8c26\u5351\u606d\u987a\u7684\u8bed\u8c03\uff0c\u4ea6\u4e0d\u80fd \u4ee4\u5979\u89c9\u5f97\u4e00\u4e1d\u559c\u60a6\u6216\u5f97\u610f\u3002 \u3000\u3000\u5c0f\u795e\u9f99\u8bb6\u7136\u770b\u5230\u5b59\u8d85\u8fdc\u5e26\u7740\u4e00\u4e2a\u6a35\u60b4\u800c\u6f66\u5012\u7684\u5973\u5b50\u8d70\u4e0a\u8239\u6765\uff0c\u4ed6\u7d20\u77e5\u5b59\u8d85\u8fdc\u505a\u4e8b\u8c28 \u614e\uff0c\u6b64\u523b\u5374\u4e0d\u514d\u8be7\u5f02\u3002 \u3000\u3000\u5b59\u8d85\u8fdc\u5f53\u7136\u770b\u5f97\u51fa\u4ed6\u7684\u795e\u8272\uff0c\u7b11\u9053\uff1a\u201c\u597d\u6559\u5927\u54e5\u5f97\u77e5\uff0c\u4eca\u65e5\u5c0f\u5f1f\u5374\u8bf7\u6765\u4e00\u4f4d\u8d35\u5bbe \u5462\u3002\u201d \u3000\u3000\u5c0f\u795e\u9f99\u8d3a\u4fe1\u96c4\u5e94\u7740\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u6253\u91cf\u7740\u91d1\u6885\u9f84\uff0c\u5374\u89c1\u5979\u76ee\u5149\u4e00\u7247\u832b\u7136\uff0c\u50cf\u662f\u4ec0\u4e48\u90fd\u672a \u89c1\u5230\u3002 \u3000\u3000\u201c\u600e\u5730\u6b64\u4eba\u50cf\u4e2a\u75f4\u5b50\u3002\u201d\u5c0f\u795e\u9f99\u6697\u5fd6\u3002 \u3000\u3000\u5b59\u8d85\u8fdc\u9053\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5c31\u662f\u91d1\u6b39\u91d1\u5927\u4fa0\u7684\u5e08\u59b9\uff0c\u2018\u5317\u541b\u2019\u7684\u638c\u73e0\uff0c\u91d1\u59d1\u5a18\u3002\u201d\u4ed6\u907f \u8bb3\u7740\u201c\u6bd2\u201d\u5b57\uff0c\u662f\u4ee5\u8bf4\u662f\u5317\u541b\u3002 \u3000\u3000\u5c0f\u795e\u9f99\u8d3a\u4fe1\u96c4\u60ca\u5f02\u5730\u53c8\u201c\u54e6\u201d\u4e86\u4e00\u58f0\uff0c\u8d76\u7d27\u6536\u56de\u90a3\u505c\u7559\u5728\u6885\u9f84\u7f8e\u5999\u7684\u80f4\u4f53\u4e0a\u7684\u773c\u5149\uff0c \u7b11\u9053\uff1a\u201c\u4eca\u5929\u662f\u54ea\u9635\u98ce\u628a\u59d1\u5a18\u5439\u6765\u7684\u5feb\u5750\uff0c\u5feb\u5750\u3002\u201d\u4ed6\u80f8\u65e0\u70b9\u58a8\uff0c\u751f\u6027\u7c97\u8c6a\uff0c\u81ea\u8ba4\u4e3a\u8fd9 \u4e24\u53e5\u8bdd\u5df2\u8bf4\u975e\u5e38\u5ba2\u6c14\u4e86\uff0c\u5b59\u8d85\u8fdc\u4e0d\u7981\u76b1\u4e86\u76b1\u7709\uff0c\u552f\u6050\u8fd9\u4f4d\u59d1\u5a18\u56e0\u6b64\u751f\u6c14\uff0c\u5feb\u3002 \u3000\u3000\u91d1\u6885\u9f84\u5374\u65e0\u52a8\u4e8e\u8877\uff0c\u5979\u8111\u6d77\u4e2d\u60f3\u7740\u7684\u4ff1\u662f\u8f9b\u6377\u7684\u5f71\u5b50\u3002 \u3000\u3000\u77ac\u606f\uff0c\u6446\u4e0a\u4e30\u5bcc\u7684\u9152\u996d\uff0c\u91d1\u6885\u9f84\u9965\u80a0\u788c\u788c\uff0c\u751f\u7406\u7684\u9700\u8981\uff0c\u4f7f \u3000\u3000\u5979\u6682\u65f6\u629b\u5f00\u4e86\u4e00\u5207\u7684\u5fc3\u4e8b\uff0c\u52a8\u8457\u5927\u5403\u8d77\u6765\u3002 \u3000\u3000\u5b59\u8d85\u8fdc\u6697\u7b11\uff1a\u201c\u8fd9\u4f4d\u59d1\u5a18\u5403\u76f8\u5012\u60ca\u4eba\u5f97\u5f88\uff0c\u50cf\u662f\u4e09\u5929\u6ca1\u6709\u5403\u996d\u4e86\u5462\u3002\u201d \u3000\u3000\u5c0f\u795e\u9f99\u89c1\u4e86\uff0c\u5374\u5927\u5408\u813e\u80c3\uff0c\u4e00\u9762\u54c8\u54c8\u7b11\u7740\uff0c\u4e00\u9762\u4e5f\u5927\u5757\u8089\u5927\u7897\u9152\u5730\u5403\u559d\u7740\uff0c\u201c\u8fd9\u4f4d \u59d1\u5a18\u5012\u8c6a\u723d\u5f97\u7d27\u3002\u201d\u4ed6\u4e0d\u7981\u9ad8\u5174\u3002 \u3000\u3000\u90a3\u77e5\u91d1\u6885\u9f84\u65b9\u53ea\u5403\u4e86\u4e9b\u8bb8\u4e1c\u897f\uff0c\u4fbf\u7f13\u7f13\u653e\u4e0b\u7b77\u5b50\uff0c\u773c\u6674\u6014\u6014\u5730\u770b\u7740\u7a97\u5916\u7684\u4e00\u7247\u6f06\u9ed1\uff0c \u5fc3\u5934\u4e5f\u4e0d\u77e5\u5728\u60f3\u7740\u4ec0\u4e48\uff0c\u53ea\u89c1\u5979\u9edb\u7709\u6df1\u98a6\uff0c\u6625\u5c71\u6101\u9501\uff0c\u5c0f\u795e\u9f99\u8d3a\u4fe1\u96c4\u662f\u4e2a\u6ca1\u5962\u906e\u7684\u6c49\u5b50\uff0c \u89c1\u72b6\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5140\u90a3\u8fd9\u5a46\u5a18\uff0c\u600e\u5730\u7a81\u7136\u53d8\u5f97\u6041\u5730\u6101\u7709\u82e6\u8138\uff0c\u50cf\u662f\u6b7b\u4e86\u6c49\u5b50\u4f3c\u7684\u3002\u201d\u4f46\u4ed6\u7ec8\u7a76\u754f\u60e7 \u7740\u201c\u6bd2\u541b\u91d1\u4e00\u9e4f\u201d\u548c\u201c\u5929\u9b54\u91d1\u6b39\u201d\u7684\u540d\u5934\uff0c\u8fd9\u4e9b\u8bdd\u53ea\u662f\u5728\u5fc3\u4e2d\u60f3\u60f3\u800c\u5df2\uff0c\u5374\u4e0d\u6562\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\u65b9\u624d\u91d1\u6885\u9f84\u786e\u662f\u997f\u5f97\u96be\u6328\uff0c\u89c1\u4e86\u98df\u7269\uff0c\u4fbf\u672c\u80fd\u5730\u60f3\u53bb\u5403\u4e00\u4e9b\uff0c\u4f46\u4e9b\u8bb8\u4e1c \u897f\u4e0b\u809a\uff0c\u7565\u4e3a\u7f13\u8fc7\u6c14\uff0c\u6ee1\u8154\u5fc3\u4e8b\uff0c\u5fcd\u4e0d\u4f4f\u53c8\u5728\u5fc3\u5934\u7ffb\u6eda\u7740\uff0c\u684c\u4e0a\u6446\u7684\u5c31\u7b97\u662f\u9f99\u809d\u98ce\u9ad3\uff0c \u5979\u518d\u4e5f\u5403\u4e0d\u4e0b\u534a\u53e3\u3002 \u3000\u3000\u5b59\u8d85\u8fdc\u5fc3\u91cc\u5374\u6697\u81ea\u7eb3\u95f7\uff1a\u201c\u8fd9\u4f4d\u91d1\u59d1\u5a18\u50cf\u662f\u6ee1\u8154\u5fc3\u4e8b\u7684\u6837\u5b50\uff0c\u800c\u4e14\u8863\u886b\u4e0d\u6574\uff0c\u5f62\u72b6 \u9887\u4e3a\u72e0\u72c8\uff0c\u96be\u9053\u8fd9\u4f4d\u8eab\u6000\u7edd\u6280\uff0c\u53c8\u662f\u5f53\u4ee3\u7b2c\u4e00\u9b54\u5934\u91d1\u6b39\u5e08\u59b9\u7684\u5927\u59d1\u5a18\uff0c\u8fd8\u4f1a\u5403\u4e86\u522b\u4eba\u7684 \u4e8f\u4e0d\u6210\u3002\u201d \u3000\u3000\u6c5f\u91cc\u767d\u9f99\u7cbe\u660e\u5e72\u7ec3\uff0c\u5fc3\u60f3\u8fd8\u662f\u65e9\u5c06\u8fd9\u4f4d\u59d1\u5a18\u9001\u8d70\u7684\u597d\uff0c\u6697\u5fd6\uff1a\u201c\u80fd\u591f\u8ba9\u8fd9\u4f4d\u59d1\u5a18\u5403 \u4e8f\u7684\u4eba\uff0c\u6211\u53ef\u66f4\u60f9\u4e0d\u8d77\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u7b11\u9053\uff1a\u201c\u91d1\u59d1\u5a18\u8981\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\uff0c\u53ef\u8981\u6211\u5f1f\u5144\u9001\u4e00\u7a0b\uff0c\u201d\u4ed6\u867d\u7136\u6ee1\u8179\u72d0\u7591\uff0c \u4f46\u53e3\u5934\u4e0a\u5374\u4e0d\u63d0\u4e00\u5b57\u3002 \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\u8fd9\u4e00\u95ee\uff0c\u5374\u5c06\u91d1\u6885\u9f84\u95f4\u5f97\u6014\u4f4f\u4e86\uff0c\u5e7d\u5e7d\u5730\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u67d4\u80a0\u5bf8\u65ad\uff0c\u8fd9\u4e24 \u5929\u6765\u6240\u53d1\u751f\u7684\u4e8b\uff0c\u4e00\u4ef6\u4ef6\u5b9b\u5982\u5229\u5203\uff0c\u5c06\u5979\u7684\u5fc3\u4e00\u5bf8\u5bf8\u5730\u5bb0\u5272\u7740\uff0c\u4e0d\u81ea\u89c9\u5730\uff0c\u5728\u8fd9\u4e24\u4e2a\u964c \u751f\u4eba\u9762\u524d\uff0c\u5979\u6d41\u51fa\u6cea\u6765\u3002 \u3000\u3000\u201c\u5929\u5730\u867d\u5927\uff0c\u4f46\u4f55\u5904\u662f\u6211\u7684\u5bb9\u8eab\u4e4b\u6240\u5462\uff1f\u201d\u91d1\u6885\u9f84\u661f\u7738\u9eef\u7136\uff0c\u5e7d\u6028\u5730\u60f3\u7740\uff0c\u201c\u5509\uff01 \u5176\u5b9e\u6709\u6ca1\u6709\u5bb9\u8eab\u4e4b\u6240\uff0c\u5bf9\u6211\u5df2\u6ca1\u6709\u4ec0\u4e48\u91cd\u8981\u4e86\uff0c\u6211\u5df2\u5c06\u6211\u6574\u4e2a\u7684\u4eba\uff0c\u4ea4\u7ed9\u4ed6\u2026\u2026\u4ed6\u73b0\u5728 \u5012\u5e95\u600e\u4e48\u6837\u5462\uff1f\u201d \u3000\u3000\u8fd9\u4e2a\u88ab\u7231\u60c5\u6df9\u6ca1\u4e86\u7684\u5c11\u5973\uff0c\u6b64\u523b\u4f46\u89c9\u5929\u5730\u4e4b\u95f4\uff0c\u6ca1\u6709\u4efb\u4f55\u4e8b\u5bf9\u5979\u662f\u91cd\u8981\u7684\u4e86\uff0c\u518d\u5927 \u7684\u5149\u660e\uff0c\u6b64\u65f6\u5979\u4e5f\u4f1a\u89c9\u5f97\u662f\u9ed1\u6697\u7684\uff0c\u518d\u5927\u7684\u5feb\u4e50\uff0c\u6b64\u65f6\u5979\u4e5f\u4f1a\u89c9\u5f97\u662f\u75db\u82e6\u7684\uff0c\u6ca1\u6709\u4efb\u4f55 \u865a\u8363\uff0c\u518d\u53ef\u4ee5\u7729\u60d1\u5979\uff0c\u6ca1\u6709\u4efb\u4f55\u8a00\u8bcd\uff0c\u518d\u53ef\u4ee5\u611f\u52a8\u5979\uff0c\u8fd9\u539f\u56e0\u53ea\u6709\u4e00\u4e2a\uff0c\u5979\u5df2\u5931\u53bb\u5979\u6240 \u7231\u7684\u4eba\uff0c\u8fd9\u611f\u89c9\u5bf9\u4e8e\u5df2\u5c06\u60c5\u611f\u548c\u8eab\u4f53\u5b8c\u5168\u4ea4\u7ed9\u8f9b\u6377\u7684\u91d1\u6885\u9f84\u6765\u8bf4\uff0c\u751a\u81f3\u6bd4\u5979\u5931\u53bb\u4e86\u81ea\u5df1 \u8fd8\u96be\u4ee5\u5fcd\u53d7\u3002 \u3000\u3000\u5c0f\u795e\u9f99\u8d3a\u4fe1\u96c4\u548c\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\u4e24\u4eba\uff0c\u600e\u4f1a\u77e5\u9053\u8fd9\u4f4d\u8eab\u6000\u7edd\u6280\u7684\u4fa0\u5973\uff0c\u6b64\u523b\u5fc3\u60c5\u6bd4 \u4e00\u4e2a\u5f31\u4e0d\u7981\u98ce\u7684\u95fa\u5973\u8fd8\u8981\u8106\u5f31\u3002 \u3000\u3000\u4ed6\u4eec\u671b\u7740\u5979\uff0c\u90fd\u6014\u4f4f\u4e86\uff0c\u5b59\u8d85\u8fdc\u662f\u4e0d\u6562\u95ee\uff0c\u4e5f\u4e0d\u613f\u95ee\uff0c\u4ed6\u660e\u54f2\u4fdd\u8eab\uff0c\u5fc3\u60f3\u8fd9\u79cd\u4e8b\u8fd8 \u662f\u4e0d\u77e5\u4e3a\u5999\u3002 \u3000\u3000\u5c0f\u795e\u9f99\u8d3a\u4fe1\u96c4\u5374\u5728\u5fc3\u91cc\u6697\u6697\u5492\u9a82\uff1a\u201c\u5140\u90a3\u8fd9\u5a46\u5a18\uff0c\u53c8\u54ed\u8d77\u6765\u4e86\uff0c\u8001\u5b50\u4e00\u809a\u5b50\u9ad8\u5174\uff0c \u88ab\u5979\u8fd9\u4e00\u54ed\uff0c\u8fd8\u6709\u4e2a\u4ec0\u4e48\u52b2\u3002\u201d\u91cd\u91cd\u5730\u5c06\u624b\u91cc\u7684\u9152\u676f\u4e00\u653e\uff0c\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u8138\u4e0a\u9732\u51fa\u4e0d\u6109 \u4e4b\u8272\u3002 \u3000\u3000\u5b59\u8d85\u8fdc\u671d\u4ed6\u505a\u4e86\u4e2a\u773c\u8272\uff0c\u4ed6\u4e5f\u6ca1\u6709\u770b\u89c1\uff0c\u7c97\u58f0\u7c97\u6c14\u5730\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\u5fc3\u91cc\u6709\u4ec0\u4e48\u4e8b\uff0c \u53ea\u7ba1\u544a\u8bc9\u5144\u5f1f\u597d\u4e86\uff0c\u5144\u5f1f\u867d\u7136\u65e0\u7528\uff0c\u5927\u5c0f\u4e5f\u8fd8\u80fd\u5e2e\u59d1\u5a18\u4e2a\u5fd9\u3002\u201d\u5b59\u8d85\u8fdc\u4e00\u542c\uff0c\u6697\u6697\u53eb\u82e6\uff1a \u201c\u6211\u7684\u5927\u54e5\u5440\uff0c\u4f60\u5e73\u767d\u53c8\u62db\u63fd\u8fd9\u4e9b\u4e8b\u5e72\u4ec0\u4e48\uff0c\u4eba\u5bb6\u529e\u4e0d\u4e86\u7684\u4e8b\uff0c\u51ed\u4f60\u3001\u6211\u8fd8\u80fd\u5e2e\u5f97\u4e86\u4ec0 \u4e48\u5fd9\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u95fb\u8a00\uff0c\u5c06\u4e8c\u9897\u8fdc\u8fdc\u629b\u5f00\u7684\u5fc3\uff0c\u53c8\u6536\u4e86\u56de\u6765\uff0c\u6084\u6084\u5730\u62ed\u4e86\u773c\u89d2\u7684\u6cea\u73e0\uff0c\u6697\u81ea\u602a \u7740\u81ea\u5df1\uff0c\u600e\u5730\u4f1a\u5728\u8fd9\u79cd\u573a\u5408\u91cc\u5c31\u6d41\u4e0b\u6cea\u6765\uff0c\u542c\u4e86\u8d3a\u4fe1\u96c4\u7684\u8bdd\uff0c\u5fc3\u91cc\u4e00\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6b63 \u6709\u4e8b\u8981\u627e\u8d3a\u5927\u54e5\u5e2e\u5fd9\u3002\u201d \u3000\u3000\u5979\u8fd9\u4e00\u58f0\u8d3a\u5927\u54e5\uff0c\u628a\u5c0f\u795e\u9f99\u53eb\u5f97\u5168\u8eab\u8f7b\u98d8\u98d8\u5730\uff0c\u5f20\u5f00\u4e00\u5f20\u5927\u5634\uff0c\u7b11\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4e8b \u53ea\u7ba1\u8bf4\uff0c\u6211\u5c0f\u7fa4\u9f99\u8d3a\u4fe1\u96c4\uff0c\u4e0d\u662f\u5728\u59d1\u5a18\u9762\u524d\u5938\u53e3\uff0c\u5357\u4e03\u7701\u5730\u9762\u4e0a\u5927\u5927\u5c0f\u5c0f\u7684\u4e8b\uff0c\u90fd\u8fd8\u80fd \u63d0\u5f97\u8d77\u6765\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u5012\u5e76\u975e\u865a\u8a00\uff0c\u60f3\u4ed6\u672c\u662f\u957f\u6c5f\u6c34\u8def\u4e0a\u7684\u74e2\u628a\u5b50\uff0c\u5357\u4e03\u7701\u65e0\u8bba\u9ed1\u767d\u4e24\u9053\uff0c\u81ea\u7136\u5f97 \u5356\u4ed6\u4e2a\u4ea4\u60c5\uff0c\u6c5f\u91cc\u767d\u9f99\u5374\u6025\u5f97\u6697\u91cc\u987f\u8db3\uff0c\u201c\u53ef\u662f\u6211\u7684\u5927\u54e5\u5440\uff0c\u50cf\u8fd9\u4f4d\u59d1\u5a18\u7684\u4e8b\uff0c\u4f60\u518d\u52a0 \u4e24\u4e2a\u4e5f\u7ba1\u4e0d\u5f97\u5440\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u5fae\u5fae\u4e00\u7b11\uff0c\u4f46\u5c31\u8fde\u7b11\uff0c\u4e5f\u662f\u90a3\u4e48\u5730\u5fe7\u607c\u3002\u5979\u8bf4\u9053\uff1a\u201c\u90a3\u4e48\u5c31\u8bf7\u8d3a\u5927\u54e5\u9001\u6211\u5230 \u6b66\u6c49\u53bb\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u4e00\u6115\uff0c\u63a5\u53e3\u95ee\u9053\uff1a\u201c\u7136\u540e\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u88ab\u91d1\u6885\u9f84\u8fd9\u4e48\u7b80\u5355\u7684\u8981\u6c42\u6115\u4f4f\u4e86\uff0c\u8d3a\u4fe1\u96c4\u5374\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u592a\u5bb9\u6613\u4e86\u3002\u201d \u4ed6\u4fe9\u4eba\u4ff1\u90fd\u6ca1\u6709\u60f3\u5230\u8fd9\u58f0\u540d\u8d6b\u8d6b\u7684\u4fa0\u5973\uff0c\u6240\u90d1\u91cd\u63d0\u51fa\u7684\u8981\u6c42\uff0c\u7adf\u662f\u5982\u6b64\u7b80\u5355\u800c\u8f7b\u6613\u7684\u4e8b\u3002 \u3000\u3000\u91d1\u6885\u9f84\u4f4e\u4e0b\u4e86\u5934\uff0c\u5374\u63a5\u7740\u5b59\u8d85\u8fdc\u65b9\u624d\u7684\u8bdd\u8bf4\u9053\uff1a\u201c\u7136\u540e\u8fd8\u8bf7\u4e8c\u4f4d\u66ff\u6211\u51c6\u5907\u4e00\u53ea\u8239\uff0c \u4ee5\u53ca\u51e0\u4e2a\u6c34\u624b\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u4e0d\u7981\u7591\u4e91\u5927\u8d77\uff1a\u201c\u5979\u7236\u4eb2\u7684\u90a3\u8258\u8239\uff0c\u6211\u751f\u957f\u6c34\u9762\uff0c\u4e5f\u4ece\u672a\u770b\u89c1\u6bd4\u90a3\u822c\u8239\u66f4\u597d \u7684\uff0c\u6b64\u523b\u5979\u600e\u5730\u5374\u8981\u6211\u7b49\u4e3a\u5979\u51c6\u5907\u4e00\u8258\u8239\uff0c\u96be\u9053\u8fd9\u4f4d\u59d1\u5a18\u662f\u548c\u5979\u7236\u4eb2\u95f9\u7ffb\uff0c\u8d1f\u6c14\u51fa\u8d70 \u7684\u3002\u201d\u6c5f\u91cc\u767d\u9f99\u9976\u662f\u673a\u667a\uff0c\u5374\u4e5f\u60f3\u4e0d\u5230\u91d1\u4e00\u9e4f\u90a3\u8258\u51a0\u7edd\u5929\u4e0b\u7684\u8239\uff0c\u662f\u6c89\u6ca1\u4e86\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8be7\u5f02\u5730\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u8981\u5907\u8239\uff0c\u6562\u60c5\u662f\u8981\u5230\u4ec0\u4e48\u5730\u65b9\u53bb\u6e38\u5386\u5417\uff1f\u201d\u5c0f\u9f99\u795e\u8d3a\u4fe1 \u96c4\u76f4\u80a0\u76f4\u809a\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u6211\u542c\u5b59\u4e8c\u5f1f\u8bf4\uff0c\u59d1\u5a18\u7684\u8001\u592a\u7237\u6709\u4e00\u53ea\u5929\u4e0b\u5c11\u89c1\u7684\u597d\u8239\uff0c\u600e\u5730 \u59d1\u5a18\u5374\u4e0d\u7528\u5462\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u5fae\u4e00\u98a6\u7709\uff0c\u907f\u5f00\u4e86\u8d3a\u4fe1\u96c4\u7684\u95ee\u8bdd\uff0c\u9053\uff1a\u201c\u6211\u60f3\u51fa\u6d77\uff0c\u6240\u4ee5\u4e8c\u4f4d\u5fc5\u987b\u8981\u66ff\u6211\u627e \u51e0\u4e2a\u719f\u6089\u6c34\u6027\u7684\u8239\u592b\u3002\u201d \u3000\u3000\u5979\u81ea\u5e7c\u9890\u6307\u6c14\u4f7f\uff0c\u6b64\u523b\u662f\u5728\u8981\u6c42\u7740\u522b\u4eba\u7684\u65f6\u5019\uff0c\u5374\u4ecd\u5728\u8bed\u6c14\u4e2d\u9732\u51fa\u547d\u4ee4\u7684\u53e3\u543b\uff0c\u5c0f \u795e\u9f99\u9053\uff1a\u201c\u8fd9\u4e2a\u4e5f\u5bb9\u6613\uff0c\u6211\u624b\u4e0b\u6709\u8bb8\u591a\u4eba\uff0c\u539f\u672c\u5c31\u662f\u5728\u6cbf\u6d77\u8ba8\u751f\u6d3b\u7684\u3002\u201d\u4ed6\u6beb\u65e0\u5fc3\u673a\uff0c \u5c06\u91d1\u6885\u9f84\u7684\u6ca1\u6709\u56de\u7b54\u4ed6\u7684\u95ee\u8bdd\uff0c\u5e76\u672b\u653e\u5728\u5fc3\u4e0a\uff0c\u5b59\u8d85\u8fdc\u4f4e\u5934\u6c89\u601d\uff1a\u201c\u8fd9\u5176\u4e2d\u5fc5\u53e6\u6709\u9690\u60c5\u3002 \u4f46\u662f\u8fd9\u5185\u60c5\u6211\u4e0d\u77e5\u9053\u4e5f\u7f62\uff0c\u5979\u65e2\u4e0d\u613f\u56de\u7b54\u5927\u54e5\u7684\u8bdd\uff0c\u53ef\u89c1\u5f97\u5979\u4e00\u5b9a\u4e0d\u613f\u610f\u6211\u4eec\u77e5\u9053\u8fd9\u4ef6 \u4e8b\uff0c\u90a3\u4e48\u6211\u4eec\u53c8\u4f55\u82e6\u518d\u95ee\u5462\uff1f\u53ea\u662f\u8fd9\u4f4d\u59d1\u5a18\u5df4\u5df4\u5730\u8981\u5230\u6d77\u5916\u53bb\uff0c\u53c8\u662f\u4e3a\u7740\u4ec0\u4e48\uff0c\u5374\u4ee4\u6211 \u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002\u201d\u5b59\u8d85\u8fdc\u5fc3\u4e2d\u6697\u5fd6\u7740\uff0c\u53e3\u4e2d\u5374\u6781\u4e3a\u5f00\u6717\u5730\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u59d1\u5a18\u8981\u5230\u6b66\u6c49\u53bb\uff0c \u5fc5\u5b9a\u6709\u7740\u6025\u4e8b\uff0c\u90a3\u4e48\u6211\u4eec\u4e5f\u4e0d\u5fc5\u518d\u5728\u6b64\u505c\u6cca\u4e86\uff0c\u4eca\u591c\u8fde\u591c\u5c31\u5f00\u59cb\u5427\u3002\u201d\u4ed6\u5b9e\u5728\u4e0d\u613f\u610f\u91d1 \u6885\u9f84\u591a\u505c\u7559\u5728\u8239\u4e0a\u3002 \u3000\u3000\u91d1\u6885\u9f84\u559c\u9053\uff1a\u201c\u8fd9\u6837\u518d\u597d\u6ca1\u6709\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u5b59\u8d85\u8fdc\u4e0b\u4ee4\u542f\u8239\uff0c\u6eaf\u6c5f\u800c\u4e0a\uff0c\u7b2c\u4e8c\u5929\u8fd8\u4e0d\u5230\u5348\u65f6\u5c31\u5230\u4e86\u6b66\u6c49\u3002 \u3000\u3000\u91d1\u6885\u9f84\u5fc3\u4e2d\u7684\u6253\u7b97\u662f\uff1a\u5148\u5230\u6b66\u6c49\u6765\u770b\u4e00\u770b\u8f9b\u6377\u7684\u5bb6\uff0c\u5979\u77e5\u9053\u8f9b\u6377\u662f\u5c71\u6885\u73e0\u5b9d\u53f7\u7684\u4e1c \u4e3b\uff0c\u662f\u4ee5\u5979\u60f3\u6253\u542c\u4e00\u4e0b\u8f9b\u6377\u7684\u5e95\u7ec6\uff0c\u5979\u867d\u548c\u8f9b\u6377\u5173\u7cfb\u5df2\u5230\u4e86\u6700\u5bc6\u5207\u7684\u5730\u6b65\uff0c\u53ef\u662f\u5979\u5bf9\u8f9b \u6377\u4ecd\u662f\u4e00\u65e0\u6240\u77e5\u3002 \u3000\u3000\u5979\u60f3\u95ee\u6e05\u8f9b\u6377\u5e95\u7ec6\u7684\u7f18\u7531\uff0c\u662f\u60f3\u67e5\u51fa\u4ed6\u4e3a\u4f55\u4f1a\u548c\u90a3\u201c\u7a7f\u7740\u767d\u886b\u6b66\u529f\u9ad8\u5230\u4e0d\u53ef\u601d\u8bae\u7684 \u4eba\u201d\u7ed3\u4ec7\u3002 \u3000\u3000\u7136\u540e\u5979\u4fbf\u8981\u4e58\u5e06\u4e1c\u53bb\uff0c\u91c7\u67e5\u8f9b\u6377\u7684\u4e0b\u843d\uff0c\u56e0\u4e3a\u5979\u6697\u5730\u601d\u91cf\uff0c\u90a3\u5929\u5979\u5728\u5cb8\u4e0a\u6240\u770b\u5230\u6c5f \u5fc3\u626c\u5e06\u4e1c\u53bb\u7684\u8239\uff0c\u5fc5\u5b9a\u5c31\u662f\u90a3\u795e\u79d8\u7684\u767d\u8863\u4e66\u751f\u548c\u540e\u6765\u90a3\u767d\u8863\u7f8e\u5987\u6240\u4e58\u7684\u8239\uff0c\u90a3\u4e48\u8f9b\u6377\u5fc5 \u5b9a\u4e5f\u662f\u88ab\u63b3\u5230\u90a3\u8239\u4e0a\u3002 \u3000\u3000\u8239\u5230\u4e86\u6b66\u6c49\uff0c\u5b59\u8d85\u8fdc\u4fbf\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4e8b\uff0c\u5c31\u8bf7\u5230\u5cb8\u4e0a\u53bb\u529e\uff0c\u81f3\u8fdf\u4eca\u591c\u660e\u6668\uff0c\u6211\u7b49\u5c31 \u53ef\u4ee5\u5c06\u59d1\u5a18\u8981\u7684\u8239\u548c\u6c34\u624b\u51c6\u5907\u597d\u3002\u201d\u987b\u77e5\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\u5728\u957f\u6c5f\u4e00\u5e26\u52bf\u529b\u6781\u5927\uff0c\u8981\u51c6\u5907 \u4e00\u8258\u8239\uff0c\u81ea\u7136\u662f\u7acb\u523b\u5c31\u80fd\u529e\u5230\u7684\u3002 \u3000\u3000\u91d1\u6885\u9f84\u70b9\u5934\u8c22\u4e86\u3002 \u3000\u3000\u5979\u5306\u5306\u8d70\u4e0a\u5cb8\u53bb\uff0c\u4eba\u4eec\u770b\u5230\u8fd9\u5e26\u7740\u4e00\u8138\u60f6\u6025\u7684\u7edd\u8273\u5c11\u5973\uff0c\u90fd\u4e0d\u7981\u7528\u8be7\u5f02\u7684\u76ee\u5149\u671b\u7740 \u5979\u3002 \u3000\u3000\u5979\u88ab\u8fd9\u79cd\u76ee\u5149\u770b\u5f97\u6709\u4e9b\u751f\u6c14\uff0c\u4f46\u4e5f\u65e0\u6cd5\uff0c\u5979\u60f3\u96c7\u8f86\u8f66\uff0c\u53c8\u82e6\u4e8e\u8eab\u8fb9\u6ca1\u6709\u94f6\u5b50\uff0c\u82e5\u662f \u4e0d\u96c7\u8f66\uff0c\u5979\u53c8\u4e0d\u77e5\u9053\u5c71\u6885\u73e0\u5b9d\u53f7\u7684\u9014\u5f84\uff0c\u53c8\u4e0d\u613f\u5411\u90a3\u4e9b\u4ee5\u8ba8\u538c\u7684\u76ee\u5149\u671b\u7740\u5979\u7684\u4eba\u4eec\u53bb\u95ee \u8def\u3002 \u3000\u3000\u5979\u81ea\u5e7c\u5a07\u751f\u60ef\u517b\uff0c\u5bf9\u4e16\u4e8b\u6839\u672c\u4e00\u7a8d\u4e0d\u901a\uff0c\u8fd9\u4e00\u4ef6\u5c0f\u5c0f\u7684\u4e8b\uff0c\u7adf\u628a\u5979\u96be\u4f4f\u4e86\uff0c\u53c8\u6c14\u3001 \u53c8\u6025\u3001\u5979\u5931\u9b42\u843d\u9b44\u5730\u5728\u8857\u4e0a\u4e71\u95ef\uff0c\u5e0c\u671b\u80fd\u5728\u65e0\u610f\u4e2d\u8d70\u5230\u5c71\u6885\u73e0\u5b9d\u53f7\u7684\u95e8\u53e3\uff0c\u5979\u811a\u6b65\u4e0d\u505c\uff0c \u60f3\u5230\u4e00\u4e8b\uff0c\u5374\u53c8\u4e0d\u7981\u4e00\u60ca\u3002 \u3000\u3000\u5979\u6697\u5fd6\uff1a\u201c\u6211\u8fd9\u526f\u6837\u5b50\uff0c\u8dd1\u5230\u5c71\u6885\u73e0\u5b9d\u53f7\u53bb\u6253\u542c\u4ed6\u7684\u8001\u677f\uff0c\u90a3\u4e9b\u5e97\u4f19\u4e0d\u628a\u6211\u5f53\u75af\u5b50 \u624d\u602a\uff0c\u600e\u4f1a\u628a\u5b9e\u60c5\u544a\u8bc9\u6211\uff1f\u201d \u3000\u3000\u671b\u7740\u8857\u4e0a\u7199\u6765\u6518\u5f80\u7684\u4eba\u7fa4\uff0c\u5979\u72ec\u81ea\u5f77\u60f6\u7740\u3002 \u3000\u3000\u8d70\u7740\u8d70\u7740\uff0c\u5979\u671b\u7740\u524d\u9762\u6709\u4e00\u680b\u6781\u5927\u7684\u623f\u5b50\uff0c\u9ed1\u6f06\u6f06\u7684\u5927\u95e8\u655e\u5f00\u7740\uff0c\u95e8\u53e3\u7684\u9a6c\u77f3\u4e0a\uff0c \u7cfb\u7740\u51e0\u5339\u9a6c\uff0c\u6709\u4e24\u4e2a\u7cbe\u58ee\u7684\u6c49\u5b50\u8e72\u5728\u95e8\u8fb9\uff0c\u5979\u6697\u5fd6\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\u6240\u5728\uff1f\u201d\u8d70\u8fd1\u53bb\u4e00\u770b\uff0c \u53ea\u89c1\u90a3\u95e8\u6963\u4e0a\u6a2a\u5199\u7740\u6b66\u5a01\u9556\u5c40\u56db\u4e2a\u91d1\u8272\u5927\u5b57\u3002 \u3000\u3000\u5979\u7b2c\u4e00\u6b21\u770b\u5230\u9556\u5c40\uff0c\u597d\u5947\u5730\u671b\u4e86\u51e0\u773c\uff0c\u7a81\u7136\u770b\u5230\u91cc\u9762\u6709\u4e24\u4e2a\u4eba\u50cf\u662f\u5728\u4e89\u8bba\u7740\u4ec0\u4e48\uff0c \u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5176\u4e2d\u6709\u4e00\u4eba\u5374\u6b63\u662f\u6c5f\u91cc\u767d\u9f99\u5b59\u8d85\u8fdc\uff0c\u91d1\u6885\u9f84\u89c1\u4e86\u4e00\u559c\uff1a\u201c\u6211\u53eb\u4ed6\u5e26\u6211\u5230\u5c71\u6885\u73e0\u5b9d\u53f7 \u53bb\u4e0d\u5c31\u884c\u4e86\u5417\uff1f\u201d \u3000\u3000\u54ea\u77e5\u5b59\u8d85\u8fdc\u4e5f\u53d1\u73b0\u4e86\u5979\uff0c\u5306\u5306\u8dd1\u4e86\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u59d1\u5a18\uff0c\u5feb\u8d70\u3002\u201d\u91d1\u6885\u9f84\u773c\u4e00\u77aa\uff0c \u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1a\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u53d1\u6025\u9053\uff1a\u201c\u7b49\u4f1a\u518d\u8bf4\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u89c1\u4ed6\u795e\u8272\u4e0d\u5b89\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u53c8\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u96be\u9053\u53c8\u51fa\u4e86\u4ec0\u4e48\u6709\u5173\u6211\u7684\u4e8b\uff1f\u201d \u9042\u4e5f\u4e00\u58f0\u4e0d\u54cd\uff0c\u8ddf\u7740\u4ed6\u8d70\u4e86\u3002 \u3000\u3000\u90a3\u8ddf\u5b59\u8d85\u8fdc\u4e00\u9f50\u8d70\u51fa\u6765\u7684\u4eba\uff0c\u5728\u540e\u9762\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5b59\u4e8c\u54e5\uff0c\u8fd9\u4e8b\u5c31\u62dc\u6258\u4f60\u4e86\uff0c\u5343\u4e07 \u4e0d\u8981\u5fd8\u8bb0\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u4e5f\u56de\u5934\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u5305\u5728\u6211\u8eab\u4e0a\uff0c\u4e0d\u8fc7\u8303\u5927\u54e5\u5374\u518d\u4e5f\u522b\u628a\u8fd9\u4ef6\u4e8b\u7b97\u5728\u6211\u5e10\u4e0a \u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u90a3\u4eba\u6b63\u662f\u6b66\u5a01\u9556\u5c40\u7684\u603b\u9556\u5934\uff0c\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\uff0c\u5b59\u8d85\u8fdc\u4e0e\u4ed6\u672c\u662f\u7d20\u8bc6\u77e5\u4ea4\uff0c\u4e00 \u5230\u4e86\u6b66\u6c49\uff0c\u4fbf\u53bb\u5bfb\u8bbf\u4ed6\u3002 \u3000\u3000\u90a3\u77e5\u5b59\u8d85\u8fdc\u4e00\u5230\u4e86\u6b66\u5a01\u9556\u5c40\uff0c\u8303\u6cbb\u6210\u4fbf\u5e26\u7740\u4e00\u4e9b\u60ca\u614c\u7684\u6837\u5b50\u8bf4\u9053\uff1a\u201c\u5b59\u4e8c\u54e5\uff0c\u4f60\u6765 \u5f97\u6b63\u597d\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u95ee\u9053\uff1a\u201c\u600e\u5730\uff1f\u201d \u3000\u3000\u8303\u6cbb\u6210\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u6c49\u53e3\u53c8\u51fa\u4e86\u8bb8\u591a\u4e8b\uff0c\u7b2c\u4e00\u4ef6\u4fbf\u662f\u6b64\u95f4\u65b0\u8d77\u7684\u5de8\u5546\uff0c\u5c71\u6885\u73e0\u5b9d\u53f7 \u7684\u4e1c\u4e3b\u8f9b\u6377\uff0c\u5c45\u7136\u5931\u8e2a\uff0c\u4eba\u8a00\u7eb7\u7eb7\uff0c\u90fd\u8bf4\u4ed6\u4e00\u5b9a\u662f\u7ed9\u7ed1\u7968\u4e86\u2026\u2026\u201d\u5b59\u8d85\u8fdc\u63a5\u7740\u7b11\u9053\uff1a \u201c\u8fd9\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\u5927\u4e8b\uff1f\u201d \u3000\u3000\u8303\u6cbb\u6210\u9053\uff1a\u201c\u5b59\u4e8c\u54e5\u4f60\u4e0d\u77e5\u9053\uff0c\u8fd9\u4e2a\u8f9b\u6377\uff0c\u5374\u4e0d\u662f\u4e2a\u666e\u901a\u5546\u4eba\u5462\uff1f\u4ed6\u4e0d\u4f46\u548c\u5c0f\u5f1f\u6709 \u4e9b\u4ea4\u60c5\uff0c\u4fbf\u662f\u548c\u2018\u5d06\u5cd2\u4e09\u7edd\u5251\u2019\u91cc\u7684\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4e5f\u662f\u597d\u53cb\uff0c\u6709\u4eba\u7ed1\u4e86\u6b64\u4eba\u7684\u7968\uff0c\u53ea\u6015 \u6709\u4e9b\u4e0d\u59a5\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8303\u5927\u54e5\u83ab\u975e\u7591\u5fc3\u662f\u6211\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u76b1\u7709\u9053\uff1a\u201c\u6211\u5012\u65e0\u6240\u8c13\uff0c\u90a3\u4e8e\u4e00\u98de\u6628\u5929\u7a81\u7136\u53c8\u6298\u56de\u6c49\u53e3\u2026\u2026\u201d\u5b59\u8d85\u8fdc\u63d2\u53e3\u9053\uff1a \u201c\u90a3\u4e8e\u4e00\u98de\u4e0d\u662f\u65e5\u524d\u5c31\u56de\u8f6c\u5d06\u5cd2\u5c71\u4e86\u5417\uff1f\u201d\u539f\u6765\u4ed6\u6d88\u606f\u7075\u901a\uff0c\u5728\u9ec4\u9e64\u697c\u4e0b\u53d1\u751f\u7684\u4e8b\uff0c\u4ed6 \u90fd\u77e5\u9053\u4e86\u3002 \u3000\u3000\u201c\u672c\u6765\uff0c\u6211\u4e5f\u542c\u5230\u4ed6\u8bf4\u8981\u7acb\u523b\u56de\u5d06\u5cd2\uff0c\u5c06\u4ed6\u5728\u6b64\u95f4\u548c\u6b66\u5f53\u6d3e\u6240\u53d1\u751f\u7684\u7ea0\u845b\uff0c\u4ee5\u53ca\u4e03 \u5999\u795e\u541b\u7684\u7a81\u7136\u51fa\u73b0\uff0c\u56de\u5c71\u53bb\u544a\u8bc9\u5251\u795e\u5389\u5927\u4fa0\u3002\u201d\u8303\u6cbb\u6210\u9053\uff1a\u201c\u54ea\u77e5\u9053\u6628\u5929\u4ed6\u968f\u7740\u2018\u5d06\u5cd2 \u4e09\u7edd\u5251\u2019\u91cc\u7684\u5929\u7edd\u8bf8\u845b\u5927\u7237\u548c\u4eba\u7edd\u5251\u82cf\u59d1\u5a18\u4e00\u9f50\u56de\u5230\u6c49\u53e3\uff0c\u5927\u6982\u4ed6\u4eec\u662f\u5728\u8def\u4e0a\u78b0\u5230\u7684\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u4f4d\u5730\u7edd\u5251\u4e00\u5230\u6b64\u95f4\uff0c\u4fbf\u542c\u5230\u5c71\u6885\u73e0\u5b9d\u53f7\u5e97\u4e1c\u8f9b\u6377\u5931\u8e2a\u7684\u6d88\u606f\uff0c \u751f\u6c14\u5f97\u4e0d\u5f97\u4e86\uff0c\u627e\u7740\u5c0f\u5f1f\u8bf4\uff0c\u8fd9\u4e8b\u4e00\u5b9a\u53c8\u662f\u957f\u6c5f\u6c34\u8def\u7684\u4eba\u5e72\u51fa\u6765\u7684\u4e8b\u60c5\uff0c\u60f3\u4e58\u673a\u7d22\u91d1 \u94f6\u2026\u2026\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u4f5c\u8272\u9053\uff1a\u201c\u8303\u5927\u54e5\u600e\u5730\u8bf4\u60e9\u822c\u8bdd\uff0c\u987b\u77e5\u5c0f\u5f1f\u867d\u662f\u5f3a\u76d7\uff0c\u4f46\u76d7\u4ea6\u6709\u9053\uff0c\u6211\u4eec\u4e5f \u6709\u6211\u4eec\u7684\u89c4\u77e9\uff0c\u5403\u6211\u4eec\u6c34\u8def\u4e0a\u996d\u7684\u4eba\uff0c\u5c31\u662f\u9646\u5730\u4e0a\u653e\u7740\u6210\u5806\u7684\u91d1\u94f6\u8d22\u5b9d\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u671b \u4e00\u773c\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8fd9\u4e48\u8bf4\uff0c\u800c\u4e14\u5b59\u4e8c\u54e5\uff0c\u4f60\u4e0d\u77e5\u9053\uff0c\u636e\u6211\u770b\u8fd9\u4f4d\u8f9b\u8001\u677f\u7684\u5931\u8e2a\uff0c \u5176\u4e2d\u8fd8\u5173\u7cfb\u7740\u53e6\u5916\u4e00\u4e2a\u4eba\u5462\uff1f\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u5fd9\u95ee\uff1a\u201c\u662f\u8c01\uff1f\u201d \u3000\u3000\u8303\u6cbb\u6210\u505a\u4e86\u4e2a\u624b\u52bf\uff0c\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u4f4d\u4e3b\u513f\u7684\u5e08\u7236\u3002\u201d \u3000\u3000\u6c5f\u91cc\u767d\u9f99\u4e00\u62cd\u684c\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5012\u771f\u7684\u5947\u602a\u4e86\uff0c\u60f3\u90a3\u59d3\u8f9b\u7684\u4e00\u4e2a\u5546\u4eba\uff0c\u600e\u4f1a\u4e0e\u4ed6\u8001 \u4eba\u5bb6\u751f\u51fa\u5173\u7cfb\u6765\uff1f\u201d \u3000\u3000\u91d1\u5f13\u795e\u5f39\u4fbf\u4e00\u4e94\u4e00\u5341\uff0c\u5c06\u8f9b\u6377\u5982\u4f55\u5728\u9ec4\u9e64\u697c\u4e0b\u9047\u89c1\u5947\u4eba\uff0c\u5982\u4f55\u53d7\u5230\u9080\u8bf7\uff0c\u5982\u4f55\u4e0d\u542c \u81ea\u5df1\u7684\u529d\u544a\u53bb\u8d74\u7ea6\uff0c\u544a\u8bc9\u4e86\u5b59\u8d85\u8fdc\uff0c\u53c8\u9053\uff1a\u201c\u662f\u4ee5\u636e\u6211\u770b\uff0c\u8fd9\u4f4d\u8f9b\u8001\u677f\u7684\u5931\u8e2a\u4e00\u5b9a\u548c\u6bd2 \u541b\u6709\u70b9\u5e72\u7cfb\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5c06\u60f3\u8bf4\u51fa\u201c\u91d1\u6885\u9f84\u4e5f\u6709\u6b64\u95ee\u201d\u7684\u8bdd\uff0c\u5fcd\u5728\u5634\u8fb9\uff0c\u4ed6\u8a00\u8bed\u8c28\u614e\uff0c\u4ece \u6765\u4e0d\u591a\u8bf4\u8bdd\u3002 \u3000\u3000\u8303\u6cbb\u6210\u53c8\u9053\uff1a\u201c\u53ef\u662f\u4e8e\u4e00\u98de\u5374\u4e00\u5b9a\u8981\u8bf4\u662f\u5c0f\u795e\u9f99\u8d3a\u5927\u54e5\u548c\u4f60\u5b59\u4e8c\u54e5\u624b\u4e0b\u7684\u4eba\u5e72\u51fa\u6765 \u7684\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u5fae\u4e00\u51b7\u7b11\u3002 \u3000\u3000\u8303\u6cbb\u6210\u53c8\u9053\uff1a\u201c\u4eca\u5929\u6e05\u6668\uff0c\u4e8e\u4e00\u98de\u4fbf\u548c\u4ed6\u7684\u5e08\u5144\u3001\u5e08\u59b9\u3001\u5317\u4e0a\u6b66\u5f53\u5c71\u4e86\uff0c\u4e34\u884c\u65f6\uff0c \u4ed6\u8fd8\u518d\u6765\u5631\u5490\u5c0f\u5f1f\uff0c\u4e00\u5b9a\u8981\u627e\u51fa\u90a3\u4f4d\u59d3\u8f9b\u7684\u4e0b\u843d\uff0c\u4e0d\u8fc7\u8001\u5b9e\u8bf4\uff0c\u59d3\u8f9b\u7684\u5931\u8e2a\uff0c\u4e5f\u771f\u6709\u70b9 \u5947\u602a\u3002\u201d \u3000\u3000\u4ed6\u5fae\u4e00\u505c\u987f\uff0c\u50cf\u662f\u5728\u601d\u7d22\u7740\u4ec0\u4e48\uff0c\u53c8\u9053\uff1a\u201c\u800c\u4e14\u4ed6\u8fd9\u4eba\u6839\u672c\u5c31\u662f\u602a\u4eba\uff0c\u53ea\u662f\u6211\u5374\u60f3 \u4e0d\u900f\uff0c\u6bd2\u541b\u91d1\u4e00\u9e4f\u82e5\u662f\u60f3\u5bf9\u4ed8\u4ed6\uff0c\u53c8\u4f55\u5fc5\u8981\u9080\u4ed6\u5230\u8239\u4e0a\u53bb\uff0c\u4f55\u51b5\u6bd2\u541b\u6839\u672c\u5c31\u6ca1\u6709\u8981\u5bf9\u4ed8 \u4ed6\u7684\u7406\u7531\u5440\uff01\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u4e5f\u5728\u6697\u81ea\u601d\u7d22\uff1a\u201c\u96be\u9053\u8fd9\u4e2a\u59d3\u8f9b\u7684\u548c\u91d1\u6885\u9f84\u7684\u51fa\u8d70\u6709\u7740\u4ec0\u4e48\u5173\u8054\uff0c\u91d1\u6885\u9f84\u5df4 \u5df4\u5730\u8dd1\u5230\u8fd9\u91cc\u6765\uff0c\u4e5f\u548c\u4ed6\u6709\u5173\u7cfb\u4e0d\u6210\u3002\u201d \u3000\u3000\u4ed6\u5750\u4e86\u4e00\u4f1a\uff0c\u4fbf\u544a\u8f9e\u51fa\u6765\uff0c\u91d1\u5f13\u795e\u5f39\u518d\u4e09\u6258\u4ed6\u6253\u542c\u8f9b\u6377\u7684\u4e0b\u843d\uff0c\u8a00\u4e0b\u7adf\u8fd8\u6709\u4e9b\u7591\u5fc3 \u4ed6\u7684\u610f\u601d\u3002 \u3000\u3000\u6c5f\u91cc\u767d\u9f99\u62c2\u7136\u4e0d\u60a6\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u7a81\u7136\u770b\u5230\u91d1\u6885\u9f84\uff0c\u4ed6\u6015\u8303\u6cbb\u6210\u8ba4\u5f97\u5979\u662f\u91d1\u4e00\u9e4f\u7684 \u201c\u5973\u513f\u201d\uff0c\u4fbf\u5306\u5306\u8d76\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8fd9\u624d\u8981\u5c06\u91d1\u6885\u9f84\u62c9\u5f00\u3002 \u3000\u3000\u8f6c\u8fc7\u5899\u89d2\uff0c\u91d1\u6885\u9f84\u95ee\u9053\uff1a\u201c\u5012\u5e95\u662f\u4ec0\u4e48\u4e8b\u5440\uff1f\u201d \u3000\u3000\u6b64\u65f6\u5b59\u8d85\u8fdc\u53c8\u4e0d\u60f3\u5c06\u6b64\u4e8b\u8bf4\u51fa\uff0c\u4fbf\u968f\u53e3\u652f\u5514\u7740\uff0c\u91d1\u6885\u9f84\u5fc3\u4e2d\u6240\u60f3\u7684\u4ff1\u662f\u8f9b\u6377\uff0c\u4e5f\u5e76 \u4e0d\u5173\u5fc3\u6b64\u4e8b\u3002 \u3000\u3000\u8d70\u4e86\u4e24\u6b65\uff0c\u91d1\u6885\u9f84\u95ee\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u8fd9\u91cc\u6709\u4e2a\u5c71\u6885\u73e0\u5b9d\u53f7\u3002\u201d\u5b59\u8d85\u8fdc\u4e00\u60ca\uff0c\u6697\u5fd6\uff1a \u201c\u679c\u7136\u662f\u4e86\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u53c8\u9053\uff1a\u201c\u6211\u60f3\u5230\u5c71\u6885\u73e0\u5b9d\u53f7\u53bb\u6709\u4e9b\u4e8b\uff0c\u53c8\u4e0d\u8ba4\u8bc6\u8be5\u600e\u6837\u8d70\u6cd5\uff0c\u4f60\u80fd\u4e0d\u80fd\u591f\u5e26 \u6211\u53bb\u4e00\u4e0b\u3002\u201d \u3000\u3000\u5b59\u8d85\u8fdc\u4f6f\u88c5\u4e0d\u77e5\uff0c\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u8981\u5230\u73e0\u5b9d\u53f7\u53bb\uff0c\u6562\u60c5\u662f\u8981\u4e70\u4e9b\u73e0\u5b9d\u5417\uff1f\u8fd9\u5c71\u6885\u73e0\u5b9d \u53f7\u6211\u5012\u542c\u8bf4\u8fc7\uff0c\u53ef\u662f\u5e76\u4e0d\u77e5\u9053\u600e\u4e48\u8d70\u6cd5\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u6025\u9053\uff1a\u201c\u90a3\u600e\u4e48\u529e\u5462\uff1f\u4f60\u4e5f\u4e0d\u8ba4\u5f97\u8def\u3002\u201d \u3000\u3000\u201c\u4e0d\u8981\u7d27\u3002\u201d\u5b59\u8d85\u8fdc\u9053\uff1a\u201c\u6211\u66ff\u59d1\u5a18\u96c7\u8f86\u8f66\u5b50\u597d\u4e86\u3002\u201d\u4ed6\u5fc3\u4e2d\u6697\u5fd6\uff1a\u201c\u770b\u8fd9\u4f4d\u59d1\u5a18 \u7740\u6025\u7684\u6837\u5b50\uff0c\u5979\u5fc5\u5b9a\u548c\u5c71\u6885\u73e0\u5b9d\u53f7\u91cc\u90a3\u59d3\u8f9b\u7684\u5c0f\u5b50\u6709\u7740\u5f88\u6df1\u7684\u5173\u7cfb\uff0c\u8fd9\u95f2\u4e8b\uff0c\u6211\u8fd8\u662f\u5c11 \u7ba1\u4e3a\u5999\u3002\u201d \u3000\u3000\u4ed6\u5904\u5904\u66ff\u81ea\u5df1\u7740\u60f3\uff0c\u5904\u5904\u60f3\u907f\u5f00\u9ebb\u70e6\uff0c\u7acb\u5373\u559d\u4e86\u4e00\u4e2a\u8def\u65c1\u7684\u95f2\u6c49\uff0c\u7ed9\u4e86\u4ed6\u4e9b\u94b1\uff0c\u8981 \u4ed6\u96c7\u8f86\u8f66\u6765\u3002 \u3000\u3000\u91d1\u6885\u9f84\u7ea2\u7740\u8138\uff0c\u5fc3\u91cc\u7740\u6025\uff0c\u5979\u52bf\u4e0d\u80fd\u544a\u8bc9\u5b59\u8d85\u8fdc\u81ea\u5df1\u6ca1\u94b1\uff0c\u4e5f\u66f4\u4e0d\u80fd\u5230\u4e86\u5c71\u6885\u73e0\u5b9d \u53f7\u53bb\u53eb\u522b\u4eba\u5f00\u53d1\u8f66\u94b1\u3002 \u3000\u3000\u5fc3\u91cc\u6b63\u5728\u6253\u9f13\uff0c\u8f66\u5df2\u6765\u4e86\uff0c\u5b59\u8d85\u8fdc\u638f\u51fa\u4e00\u5c0f\u952d\u94f6\u5b50\uff0c\u4ea4\u7ed9\u8d76\u8f66\u7684\u8f66\u592b\uff0c\u9053\uff1a\u201c\u8fd9\u4f4d \u59d1\u5a18\u8981\u5230\u5c71\u6885\u73e0\u5b9d\u53f7\u53bb\uff0c\u4f60\u53ef\u8bc6\u5f97\u8def\u5417\uff1f\u201d \u3000\u3000\u8f66\u592b\u89c1\u4e86\u94f6\u5b50\uff0c\u70b9\u5934\u4e0d\u8fed\u5730\u8bf4\u9053\uff1a\u201c\u8ba4\u5f97\uff0c\u8ba4\u5f97\uff0c\u4f60\u5bb6\u53ea\u7ba1\u653e\u5fc3\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u89c1\u4ed6\u7ed9\u4e86\u8f66\u94b1\uff0c\u5fc3\u91cc\u4e00\u5b9a\uff0c\u8df3\u4e0a\u8f66\u53bb\u53eb\u9053\uff1a\u201c\u5feb\u70b9\u8d70\uff0c\u5feb\u70b9\u8d70\u3002\u201d\u53c8\u4fa7\u5934\u5411 \u5b59\u8d85\u8fdc\u6253\u4e86\u4e2a\u62db\u547c\u3002 \u3000\u3000\u5230\u4e86\u5c71\u6885\u73e0\u5b9d\u53f7\u95e8\u53e3\uff0c\u505c\u4e0b\u4e86\u8f66\uff0c\u8f66\u592b\u642d\u8baa\u9053\uff1a\u201c\u8fd9\u4e24\u5929\u5c71\u6885\u73e0\u5b9d\u53f7\u7684\u8f9b\u8001\u677f\u6559\u571f \u532a\u7ed9\u7ed1\u4e86\u7968\uff0c\u8fde\u5e97\u95e8\u90fd\u5173\u8d77\u6765\u5566\uff01\u201d \u3000\u3000\u91d1\u6885\u9f84\u4e0b\u8f66\u4e00\u770b\uff0c\u94fa\u5b50\u7684\u95e8\u679c\u7136\u5173\u5f97\u7d27\u7d27\u5730\uff0c\u5979\u4e5f\u4e0d\u7ba1\uff0c\u8d70\u8fc7\u53bb\u201c\u562d\uff01\u562d\uff01\u201d\u62cd\u8d77 \u95e8\u6765\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ece\u95e8\u7f1d\u91cc\u4f38\u51fa\u4e00\u4e2a\u5934\u6765\uff0c\u5927\u7ea6\u770b\u89c1\u5916\u9762\u53ea\u662f\u4e00\u4e2a\u5973\u5b50\uff0c\u5c06\u95e8\u5f00\u5f97\u66f4\u5927\u4e86 \u4e9b\u3002 \u3000\u3000\u5f00\u95e8\u7684\u90a3\u5e97\u4f19\u95ee\u9053\uff1a\u201c\u59d1\u5a18\u627e\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u53e5\u6700\u666e\u901a\u7684\u8bdd\uff0c\u53c8\u5c06\u91d1\u6885\u9f84\u95ee\u5f97\u7b54\u4e0d\u4e0a\u8bdd\u6765\uff0c\u5979\u5b9e\u5728\u4e0d\u77e5\u9053\u8be5\u627e\u4ec0\u4e48\u4eba\uff0c\u55eb\u5685 \u4e86\u534a\u54cd\u9053\uff1a\u201c\u6211\u627e\u4f60\u4eec\u8fd9\u91cc\u7684\u7ba1\u4e8b\u7684\u3002\u201d\u5e97\u4f19\u7684\u5934\u53c8\u671d\u5916\u4f38\u51fa\u4e86\u4e00\u4e9b\uff0c\u4ed4\u7ec6\u5730\u671d\u5979\u6253\u91cf \u4e86\u51e0\u773c\u3002\u624d\u8bf4\u9053\uff1a\u201c\u8bf7\u4f60\u5bb6\u7b49\u4e00\u4f1a\u3002\u201d\u7830\u5730\u5173\u4e0a\u4e86\u95e8\uff0c\u91d1\u6885\u9f84\u65e0\u804a\u5730\u7ad9\u5728\u8def\u65c1\uff0c\u53c8\u8fc7\u4e86 \u534a\u54cd\uff0c\u95e8\u5f00\u4e86\u4e00\u6247\uff0c\u90a3\u5e97\u4f19\u7684\u5934\u53c8\u4f38\u51fa\u6765\uff0c\u9053\uff1a\u201c\u8bf7\u4f60\u5bb6\u8fdb\u53bb\u5750\u3002\u201d\u91d1\u6885\u9f84\u62e2\u4e86\u62e2\u5934\u53d1\uff0c \u90a3\u5e97\u4f19\u51e0\u65f6\u770b\u5230\u8fc7\u8fd9\u4e48\u7f8e\u7684\u5c11\u5973\uff0c\u5934\u90fd\u7f29\u4e0d\u8fdb\u53bb\u4e86\u3002 \u3000\u3000\u91cc\u9762\u672c\u662f\u67dc\u53f0\uff0c\u67dc\u53f0\u524d\u4e5f\u6446\u7740\u51e0\u5f20\u7d2b\u6a80\u6728\u7684\u5927\u6905\u5b50\u3002\u91d1\u6885\u9f84\u8d70\u4e86\u8fdb\u53bb\uff0c\u90a3\u5e97\u4f19\u6bb7\u52e4 \u5730\u62db\u547c\u5979\u5750\u4e0b\uff0c\u91d1\u6885\u9f84\u7b2c\u4e00\u6b21\u5230\u8fd9\u79cd\u5730\u65b9\u6765\uff0c\u7b2c\u4e00\u6b21\u5979\u8981\u5355\u72ec\u5e94\u4ed8\u5979\u6240\u4e0d\u8ba4\u8bc6\u7684\u4eba\uff0c\u5fc3 \u91cc\u6709\u4e9b\u53d1\u614c\uff0c\u90a3\u5e97\u4f19\u5728\u65c1\u8fb9\u7ad9\u7740\uff0c\u76f4\u7740\u773c\u671b\u5979\uff0c\u5979\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u3002 \u3000\u3000\u5979\u4f4e\u4e0b\u5934\u53bb\u60f3\u5fc3\u4e8b\uff0c\u5ffd\u7136\u9762\u524d\u6709\u4eba\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u5979\u62ac\u8d77\u5934\u6765\uff0c\u770b\u5230\u4e00\u4e2a\u7626\u524a\u7684\u8001\u4eba \u6b63\u4ee5\u4e00\u79cd\u5947\u5f02\u7684\u76ee\u5149\u770b\u7740\u5979\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u5979\u5fc3\u5934\u7acb\u523b\u4e5f\u5347\u8d77\u4e00\u79cd\u5947\u5f02\u7684\u611f\u89c9\uff0c\u4eff\u4f5b\u89c9\u5f97 \u8fd9\u7626\u524a\u8001\u4eba\u7684\u76ee\u5149\u91cc\uff0c\u5e26\u6709\u4e00\u79cd\u5979\u4e0d\u80fd\u6297\u62d2\u7684\u529b\u91cf\uff0c\u8fd9\u529b\u91cf\u53c8\u548c\u8f9b\u6377\u7684\u76ee\u5149\u6240\u5e26\u7ed9\u5979\u7684 \u8fe5\u7136\u4e0d\u540c\u3002 \u3000\u3000\u8fd9\u7626\u524a\u8001\u4eba\u53c8\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u4f4e\u4f4e\u8bf4\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u548c\u4f60\u4eec\u7684\u8f9b\u2026\u2026\u8f9b\u8001\u677f\u662f\u670b\u53cb\u2026\u2026\u201d\u5979\u7ed3\u7ed3\u5df4\u5df4\u5730\u8bf4 \u5230\u8fd9\u91cc\u3002 \u3000\u3000\u5374\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u6837\u8bf4\u4e0b\u53bb\uff0c\u624d\u80fd\u5c06\u5979\u6240\u8981\u8bf4\u7684\u8bdd\u8bf4\u51fa\u6765\u3002\u7626\u524a\u8001\u4eba\u9762\u8272\u5fae\u5fae\u4e00\u53d8\uff0c \u9053\uff1a\u201c\u8f9b\u8001\u677f\u4e0d\u5728\uff0c\u59d1\u5a18\u627e\u4ed6\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d\u91d1\u6885\u9f84\u9053\uff1a\u201c\u6211\u77e5\u9053\u3002\u201d \u3000\u3000\u7626\u524a\u8001\u4eba\u76ee\u5149\u4e00\u51db\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u77e5\u9053\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u4e00\u62ac\u5934\u9053\uff1a\u201c\u6211\u77e5\u9053\u4ed6\u4e0d\u5728\uff0c\u6211\u662f\u60f3\u6765\u95ee\u95ee\u2026\u2026\u201d\u7626\u524a\u8001\u4eba\u7a81\u7136\u95ee\u9053\uff1a\u201c\u59d1 \u5a18\u8d35\u59d3\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u9053\uff1a\u201c\u6211\u59d3\u91d1\u3002\u201d \u3000\u3000\u7626\u524a\u8001\u4eba\u795e\u8272\u66f4\u662f\u5927\u53d8\uff0c\u95ee\u9053\uff1a\u201c\u91d1\u4e00\u9e4f\u662f\u59d1\u5a18\u4ec0\u4e48\u4eba\uff1f\u201d\u91d1\u6885\u9f84\u5fc3\u91cc\u5947\u602a\uff1a\u201c\u8fd9 \u4e2a\u4eba\u600e\u4e48\u77e5\u9053\u6211\u2018\u7239\u7239\u2019\u5462\uff1f\u770b\u6837\u5b50\u4ed6\u5e94\u8be5\u53ea\u662f\u5c71\u6885\u73e0\u5b9d\u5e97\u7684\u4e00\u4f19\u8ba1\uff0c\u53ef\u662f\u8bf4\u8d77\u8bdd\u6765\uff0c \u53c8\u4e00\u70b9\u4e5f\u4e0d\u50cf\u3002\u201d\u5979\u867d\u7136\u5fc3\u91cc\u5947\u602a\uff0c\u4f46\u8fd9\u7626\u524a\u8001\u4eba\u8bed\u6c14\u4eff\u4f5b\u6709\u4e00\u80a1\u975e\u5e38\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u4f7f \u5f97\u5979\u65e0\u6cd5\u4e0d\u56de\u7b54\u4ed6\u7684\u8bdd\uff0c\u4e8e\u662f\u5979\u53ea\u7a0d\u4e3a\u8e0c\u8e87\u4e86\u4e00\u4e0b\uff0c\u4fbf\u9053\uff1a\u201c\u662f\u6211\u7684\u7239\u7239\u3002\u201d \u3000\u3000\u7626\u524a\u8001\u4eba\u7684\u8138\u8272\u66f4\u662f\u602a\u5f02\u5df2\u6781\uff0c\u8138\u4e0a\u7684\u808c\u8089\uff0c\u4e5f\u5728\u626d\u52a8\u7740\uff0c\u7ad9\u5728\u90a3\u91cc\uff0c\u8bb8\u4e45\u6ca1\u6709\u8bf4 \u8bdd\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u8d70\u524d\u4e00\u6b65\uff0c\u6307\u7740\u91d1\u6885\u9f84\u9053\uff1a\u201c\u4f60\u809a\u8110\u5de6\u8fb9\uff0c\u662f\u4e0d\u662f\u6709\u4e00\u7c92\u9ed1\u75e3\uff0c\u53ea\u6709\u7c73\u7c92 \u822c\u5927\u5c0f\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u5413\u5f97\u4ece\u6905\u4e0a\u8df3\u4e86\u8d77\u6765\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u8001\u5934\u5b50\u600e\u5730\u8fde\u6211\u8eab\u4e0a\u751f\u7684\u75e3\u90fd\u5f04\u5f97\u4e00\u6e05\u4e8c \u695a\u7684\u3002\u201d \u3000\u3000\u201c\u8fd9\u7c92\u75e3\u8fde\u6377\u54e5\u54e5\u90fd\u4e0d\u4e00\u5b9a\u77e5\u9053\u7684\u5440\u3002\u201d\u5979\u6697\u81ea\u5c06\u8fd9\u5947\u602a\u7684\u95ee\u9898\uff0c\u653e\u5728\u5fc3\u5934\uff0c\u4e0d\u77e5 \u8be5\u600e\u4e48\u56de\u7b54\u3002 \u3000\u3000\u7626\u524a\u8001\u4eba\u7684\u80f8\u819b\u6025\u5267\u5730\u8d77\u4f0f\u7740\uff0c\u773c\u775b\u77ac\u4e5f\u4e0d\u77ac\u5730\u671b\u7740\u5979\uff0c\u671f\u5f85\u7740\u5979\u7684\u56de\u7b54\uff0c\u4f46\u91d1\u6885 \u9f84\u53ea\u662f\u602f\u751f\u751f\u5730\u671b\u7740\u8fd9\u5947\u602a\u800c\u4e25\u8083\u7684\u8001\u4eba\u3002 \u3000\u3000\u8001\u4eba\u7a81\u7136\u957f\u53f9\u4e86\u53e3\u6c14\uff0c\u5c16\u9510\u7684\u76ee\u5149\u53d8\u5f97\u65e0\u6bd4\u7684\u6e29\u67d4\uff0c\u5168\u8eab\u4e5f\u50cf\u662f\u7a81\u7136\u677e\u5f1b\u800c\u762b\u8f6f\u4e86\uff0c \u865a\u5f31\u5730\u5012\u5728\u4e00\u5f20\u6905\u5b50\u4e0a\u3002 \u3000\u3000\u201c\u4f60\u7684\u5988\u5988\u5462\uff1f\u5979\u2026\u2026\u5979\u53ef\u597d\u3002\u201d\u8001\u4eba\u5728\u95ee\u8fd9\u8bdd\u65f6\uff0c\u795e\u8272\u4e2d\u53c8\u9732\u51fa\u4e00\u79cd\u96be\u4ee5\u63cf\u8ff0\u4e4b \u6001\u3002 \u3000\u3000\u91d1\u6885\u9f84\u72b9\u8c6b\u7740\uff0c\u8e0c\u8e87\u7740\uff0c\u5728\u5979\u5185\u5fc3\uff0c\u4e5f\u6709\u7740\u4e00\u4e1d\u9884\u611f\uff0c\u5374\u6df1\u6df1\u5730\u4f7f\u5979\u60ca\u5413\u800c\u8ff7\u5076\u4e86\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u5979\u4f4e\u4f4e\u5730\u8bf4\uff1a\u201c\u5988\u5988\u6b7b\u4e86\u3002\u201d \u3000\u3000\u8001\u4eba\u7684\u773c\u776b\u4e24\u8fb9\u6025\u5267\u5730\u8df3\u52a8\u7740\uff0c\u8c01\u4e5f\u770b\u4e0d\u51fa\u4ed6\u773c\u4e2d\u95ea\u70c1\u7740\u7684\u662f\u5174\u594b\u6291\u6216\u662f\u60b2\u54c0\u7684\u6cea \u5149\u3002 \u3000\u3000\u4ed6\u5f20\u53e3\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u662f\u53c8\u6781\u529b\u5fcd\u4f4f\u4e86\uff0c\u98a4\u5dcd\u5dcd\u5730\u7ad9\u4e86\u8d77\u6765\uff0c\u50cf\u662f\u7a81\u7136\u8001\u4e86\u8bb8\u591a\uff0c\u8870 \u5f31\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u7136\u540e\u4ed6\u8d70\u4e86\u8fdb\u53bb\uff0c\u5c06\u53d1\u7740\u6115\u7684\u91d1\u6885\u9f84\u5b64\u96f6\u5730\u7559\u5728\u5927\u5385\u91cc\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u77e5\u9053\uff0c\u8fd9\u8001\u4eba\u7684 \u5fc3\u91cc\u542b\u8574\u7740\u591a\u4e48\u5927\u7684\u60b2\u54c0\u3002 \u3000\u3000\u9762\u5bf9\u7740\u4ed6\u4eb2\u751f\u7684\u5973\u513f\uff0c\u4ed6\u7adf\u90fd\u4e0d\u613f\u5c06\u4ed6\u5fc3\u91cc\u7684\u9690\u8877\u8bf4\u51fa\u6765\uff0c\u4e3a\u7740\u8bb8\u591a\u79cd\u7406\u7531\uff0c\u5176\u4e2d \u6700\u5927\u7684\u4e00\u79cd\uff0c\u5c31\u662f\u4ed6\u4e0d\u613f\u8ba9\u4ed6\u5973\u513f\u53d7\u5230\u6253\u51fb\uff0c\u4e5f\u4e0d\u613f\u8ba9\u4ed6\u7684\u5973\u513f\u5bf9\u201c\u5988\u5988\u201d\u611f\u5230\u5c48\u8fb1\uff0c \u6240\u4ee5\uff0c\u4ed6\u6084\u6084\u5730\u8d70\u4e86\u3002 \u3000\u3000\u4ed6\u5f53\u7136\u4e0d\u77e5\u9053\uff0c\u5f53\u5e74\u4ed6\u7684\u59bb\u5b50\u4e5f\u6709\u7740\u6781\u5927\u7684\u9690\u8877\uff0c\u4ed6\u66f4\u4e0d\u77e5\u9053\uff0c\u4ed6\u5728\u5e74\u8f7b\u65f6\u65e0\u610f\u4e2d \u505a\u51fa\u7684\u4e00\u4ef6\u4e8b\uff0c\u4f7f\u4ed6\u7ec8\u8eab\u90fd\u53d7\u7740\u75db\u82e6\u3002 \u3000\u3000\u91d1\u6885\u9f84\u6115\u4e86\u8bb8\u4e45\uff0c\u7b49\u5979\u4ece\u5e97\u4f19\u4eec\u60ca\u5f02\u7684\u76ee\u5149\u4e2d\u8d70\u51fa\u53bb\u65f6\uff0c\u5979\u624d\u60f3\u8d77\u5979\u8fd9\u6b21\u6765\u6b64\u7684\u76ee \u7684\u3002 \u3000\u3000\u5979\u54ac\u4e86\u54ac\u7259\uff0c\u6697\u81ea\u4e0b\u4e86\u4e2a\u51b3\u5fc3\uff1a\u201c\u4f60\u4eec\u4e0d\u544a\u8bc9\u6211\uff0c\u6211\u4e5f\u4f1a\u81ea\u5df1\u67e5\u51fa\u6765\u3002\u201d\u5979\u6253\u5b9a\u4e3b \u610f\uff0c\u7b49\u5230\u665a\u4e0a\uff0c\u5979\u8981\u51ed\u7740\u81ea\u5df1\u7684\u8eab\u624b\uff0c\u591c\u4eba\u5c71\u6885\u73e0\u5b9d\u5e97\uff0c\u67e5\u660e\u8f9b\u6377\u7684\u8eab\u4e16\uff0c\u8fd9\u624d\u662f\u5979\u6240 \u6700\u5173\u5fc3\u7684\u3002 \u3000\u3000\u60b2\u54c0\u800c\u5b71\u5f31\u7684\u201c\u4faf\u4e8c\u201d\u88ab\u4e00\u79cd\u7236\u5973\u4e4b\u95f4\u6df1\u539a\u800c\u6d53\u70c8\u7684\u60c5\u611f\u6240\u8ff7\u5931\u4e86\uff0c\u5f53\u4ed6\u7b2c\u4e00\u773c\u770b \u5230\u8fd9\u7a7f\u7740\u7eff\u8272\u8863\u670d\u7684\u5c11\u5973\u65f6\uff0c\u4ed6\u5fc3\u91cc\u5c31\u50cf\u662f\u751f\u51fa\u5f88\u5927\u7684\u6fc0\u52a8\uff0c\u53ef\u662f\u7b49\u4ed6\u8bc1\u5b9e\u4e86\u8fd9\u5750\u5728\u4ed6 \u9762\u524d\u7684\u5c11\u5973\uff0c\u771f\u7684\u662f\u4ed6\u4eb2\u751f\u7684\u5973\u513f\u65f6\uff0c\u4ed6\u53cd\u800c\u5c06\u8fd9\u79cd\u6fc0\u52a8\u538b\u5236\u4e86\u4e0b\u6765\uff0c\u5929\u4e0b\u7236\u6bcd\u7231\u5b50\u5973 \u7684\u5fc3\u60c5\u591a\u534a\u5982\u6b64\uff0c\u4ed6\u4eec\u5f80\u5f80\u613f\u610f\u81ea\u5df1\u53d7\u7740\u6781\u5927\u7684\u75db\u82e6\uff0c\u800c\u4e0d\u613f\u81ea\u5df1\u7684\u5b50\u5973\u53d7\u5230\u534a\u5206\u59d4\u66f2\u3002 \u3000\u3000\u4f46\u662f\u91d1\u6885\u9f84\u4f55\u5c1d\u77e5\u9053\u8fd9\u4e9b\uff0c\u867d\u7136\uff0c\u4ed6\u5bf9\u8fd9\u7626\u524a\u800c\u5947\u602a\u7684\u8001\u4eba\uff0c\u4e5f\u751f\u51fa\u4e00\u4efd\u96be\u8a00\u7684\u60c5 \u611f\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4efd\u60c5\u611f\u662f\u6697\u6666\u800c\u865a\u5e7b\u7684\uff0c\u8fdc\u4e0d\u53ca\u5979\u5bf9\u8f9b\u6377\u7684\u5173\u6ce8\u786e\u5207\u800c\u5f3a\u70c8\uff0c\u5979\u900f\u5de1\u7740\uff0c\u53c8 \u56de\u5230\u6c5f\u5cb8\u3002 \u3000\u3000\u8d77\u66f4\uff0c\u521d\u66f4\uff0c\u4e8c\u66f4\u2026\u2026 \u3000\u3000\u5979\u8ba1\u7b97\u7740\u66f4\u9f13\uff0c\u7136\u540e\uff0c\u5979\u7d27\u4e86\u7d27\u8eab\u4e0a\u7684\u8863\u88f3\uff0c\u5c06\u88d9\u89d2\u4e5f\u4ed4\u7ec6\u5730\u624e\u5728\u811a\u4e0a\uff0c\u8bd5\u4e86\u8bd5\u8eab \u624b\u5df2\u6781\u4e3a\u7075\u6d3b\uff0c\u7edd\u4e0d\u4f1a\u53d1\u751f\u4e1d\u6beb\u58f0\u54cd\u6765\u3002 \u3000\u3000\u4e8e\u662f\u5979\u50cf\u4e00\u53ea\u591c\u884c\u7684\u72f8\u732b\uff0c\u7a9c\u5230\u6df1\u591c\u9759\u5bc2\u7684\u5c4b\u9762\u4e0a\u3002 \u3000\u3000\u5979\u8fa8\u7740\u767d\u5929\u8bb0\u4e0b\u7684\u65b9\u5411\uff0c\u4e0d\u4e00\u523b\uff0c\u5df2\u7ecf\u5230\u4e86\u201c\u5c71\u6885\u73e0\u5b9d\u5e97\u201d\uff0c\u867d\u7136\u5979\u731c\u60f3\u5e97\u4e2d\u7684\u5168 \u662f\u666e\u901a\u7684\u5e97\u4f19\uff0c\u4f46\u662f\u767d\u5929\u90a3\u7626\u524a\u8001\u4eba\u7684\u76ee\u5149\uff0c\u4f7f\u5f97\u5979\u6781\u4e3a\u5c0f\u5fc3\u5730\u79fb\u52a8\u7740\u8eab\u8eaf\uff0c\u6781\u529b\u4e0d\u53d1 \u51fa\u4efb\u4f55\u58f0\u97f3\u6765\u3002 \u3000\u3000\u8fdc\u5904\u5c4b\u9876\u4e0a\uff0c\u4f20\u6765\u51e0\u58f0\u732b\u7684\u5636\u9e23\uff0c\u51c4\u5389\u800c\u5e26\u7740\u4e9b\u8361\u4eba\u7684\u53eb\u58f0\uff0c\u4f7f\u5f97\u5979\u8bb0\u8d77\u4e86\u8fd9\u662f\u6625 \u5929\u3002 \u3000\u3000\u201c\u6625\u5929\u2026\u2026\u201d\u5979\u6452\u5f00\u4e86\u8fd9\u8bf1\u4eba\u7684\u540d\u8bcd\uff0c\u76ee\u5149\u50cf\u9e70\u4e00\u6837\u5730\u5728\u4e0b\u9762\u641c\u7d22\u7740\uff0c\u4e0b\u9762\u7684\u706f\u5149 \u5168\u90fd\u65e9\u7184\u4e86\u3002 \u3000\u3000\u5979\u542c\u5230\u81ea\u5df1\u5fc3\u623f\u6025\u907d\u8df3\u52a8\u7684\u58f0\u97f3\uff0c\u867d\u7136\u5979\u81ea\u6043\u6b66\u529f\uff0c\u4f46\u7a76\u7adf\u662f\u7b2c\u4e00\u6b21\u505a\u8fd9\u79cd\u52fe\u5f53\uff0c \u5fc3\u60c5\u4e0d\u514d\u7d27\u5f20\u5f97\u5f88\u3002 \u3000\u3000\u7ad9\u5728\u7a81\u51fa\u7684\u5c4b\u810a\u8fb9\uff0c\u5979\u51e0\u6b21\u60f3\u5f80\u4e0b\u7eb5\uff0c\u4f46\u662f\u53c8\u90fd\u81ea\u5df1\u6b62\u4f4f\u4e86\uff0c\u5979\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u53bb\u5b8c \u6210\u5979\u7684\u76ee\u7684\u3002 \u3000\u3000\u8fd9\u79cd\u6c5f\u6e56\u4e0a\u7684\u7ecf\u9a8c\uff0c\u7edd\u975e\u4e00\u671d\u4e00\u5915\u80fd\u5b66\u4e60\u5f97\u5230\u7684\uff0c\u4f55\u51b5\u5979\u521d\u5165\u4e16\uff0c\u5bf9\u8fd9\u4e9b\u4e8b\u53ef\u8bf4\u662f \u4e00\u7a8d\u4e0d\u901a\uff0c\u53eb\u5979\u5728\u4e00\u4e2a\u9ed1\u6c89\u6c89\u7684\u9662\u843d\u91cc\u6765\u63a2\u67e5\u4e00\u4e9b\u4e8b\uff0c\u6839\u672c\u65e0\u6cd5\u505a\u5230\uff0c\u8d77\u5148\u5979\u6253\u7740\u5982\u610f \u7b97\u76d8\uff0c\u6b64\u523b\u624d\u77e5\u9053\u8981\u505a\u8d77\u6765\u8fdc\u975e\u5979\u6240\u60f3\u50cf\u7684\u90a3\u4e48\u7b80\u5355\u3002 \u3000\u3000\u4e8e\u662f\u5979\u5f77\u60f6\u5728\u591c\u7684\u661f\u7a7a\u4e0b\uff0c\u62ac\u9996\u671b\u5929\uff0c\u5d4c\u5728\u7fe0\u7389\u822c\u82cd\u7a79\u91cc\u7684\u660e\u6708\uff0c\u90fd\u50cf\u662f\u5728\u7728\u773c\u5632 \u7b11\u7740\u5979\u3002 \u3000\u3000\u7a81\u7136\uff0c\u5979\u7684\u80cc\u540e\u6709\u4eba\u8f7b\u8f7b\u5730\u54b3\u55fd\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5979\u60ca\u60f6\u5730\u4e00\u9519\u6b65\uff0c\u8f6c\u56de\u8eab\u6765\uff0c\u4e00\u5f20\u7626\u524a\u800c\u51b7\u5cfb\u7684\u8001\u8005\u7684\u8138\uff0c\u6b63\u5bf9\u7740\u5979\uff0c\u51b7\u51b7\u5730\u8bf4\u9053\uff1a \u201c\u4f60\u53c8\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8fd9\u6b63\u662f\u767d\u5929\u5979\u6240\u89c1\u5230\u7684\u90a3\u4e2a\u8001\u8005\uff0c\u91d1\u6885\u9f84\u60ca\u5fd6\uff1a\u201c\u6b64\u4eba\u679c\u7136\u597d\u6df1\u7684\u6b66\u529f\uff0c\u4ed6\u6765\u5230\u6211 \u8eab\u540e\uff0c\u6211\u4e00\u70b9\u4e5f\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u8fd9\u7626\u524a\u7684\u8001\u4eba\u201c\u4faf\u4e8c\u201d\u6697\u5730\u601d\u91cf\u7740\uff1a\u201c\u5979\u5728\u8fd9\u4e48\u665a\u8dd1\u5230\u8fd9\u91cc\u6765\u5e72\u4ec0\u4e48\uff0c\u96be\u9053\u5979\u5df2\u7ecf \u77e5\u9053\u6211\u662f\u8c01\u4e86\u5417\uff1f\u201d \u3000\u3000\u91d1\u6885\u9f84\u5168\u795e\u6212\u5907\u7740\uff0c\u6ca1\u6709\u56de\u7b54\u4ed6\u7684\u8bdd\uff0c\u201c\u4faf\u4e8c\u201d\u76ee\u5149\u4ecd\u7136\u7d27\u76ef\u5728\u5979\u7684\u8138\u4e0a\uff0c\u95ee\u9053\uff1a \u201c\u4f60\u5012\u5e95\u6765\u5e72\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4faf\u4e8c\u6b64\u523b\u7684\u5fc3\u60c5\u66f4\u662f\u77db\u76fe\u7684\uff0c\u4e00\u65b9\u9762\uff0c\u4ed6\u662f\u90a3\u4e48\u5730\u5e0c\u671b\u8fd9\u7ad9\u5728\u4ed6\u9762\u524d\u7684\u5c11\u5973\u5df2\u7ecf\u77e5 \u9053\u4ed6\u662f\u5979\u7684\u7236\u4eb2\u4e86\u3002 \u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u4ed6\u5374\u53c8\u5e0c\u671b\u8fd9\u4e8b\u6c38\u8fdc\u4e0d\u8981\u8ba9\u5979\u77e5\u9053\u3002 \u3000\u3000\u91d1\u6885\u9f84\u6c89\u601d\u7740\uff0c\u4e00\u62ac\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5e0c\u671b\u4f60\u80fd\u544a\u8bc9\u6211\u8f9b\u6377\u5012\u5e95\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u6211 \u662f\u2026\u2026\u201d\u5979\u7ec8\u4e8e\u4e0d\u597d\u610f\u601d\u5c06\u5979\u548c\u8f9b\u6377\u7684\u5173\u7cfb\u8bf4\u51fa\uff0c\u6781\u5feb\u5730\u63a5\u4e0b\u53bb\u8bf4\uff1a\u201c\u6211\u662f\u8981\u6765\u67e5\u660e\u767d \u4ed6\u5012\u5e95\u662f\u4ec0\u4e48\u4eba\u7684\u3002\u201d \u3000\u3000\u5979\u6781\u56f0\u96be\u5730\u8bf4\u51fa\u8fd9\u53e5\u8bdd\uff0c\u81ea\u5df1\u5df2\u8ba4\u4e3a\u662f\u8981\u8a00\u4e0d\u70e6\uff0c\u95ee\u5f97\u6070\u5230\u597d\u5904\u4e86\uff0c\u5979\u5374\u6ca1\u6709\u60f3\u5230 \u5979\u6df1\u591c\u95ef\u4eba\uff0c\u53c8\u65e0\u5934\u65e0\u8111\u5730\u95ee\u4eba\u5bb6\u8fd9\u4e9b\u8bdd\uff0c\u600e\u4e48\u80fd\u591f\u5f97\u5230\u4eba\u5bb6\u5706\u6ee1\u7684\u7b54\u8986\u5462\uff1f\u201c\u4faf\u4e8c\u201d \u5bf9\u5979\u867d\u7136\u6ee1\u6000\u7740\u7236\u5973\u7684\u4eb2\u60c5\uff0c\u4f46\u662f\u4e5f\u4e0d\u80fd\u5c06\u8f9b\u6377\u7684\u5e95\u7ec6\u8bf4\u51fa\uff0c\u56e0\u4e3a\u8fd9\u4e8b\u5173\u7cfb\u7740\u6885\u5c71\u6c11\u5341 \u5e74\u6765\u671d\u5915\u4e0d\u5fd8\u7684\u8ba1\u5212\uff0c\u90a3\u4e48\u4ed6\u600e\u80fd\u5c06\u4ed6\u7684\u201c\u6551\u547d\u6069\u4eba\u201d\u7684\u8ba1\u5212\u8bf4\u51fa\u6765\u5462\uff1f\u5373\u4f7f\u5bf9\u65b9\u662f\u4ed6 \u7684\u5973\u513f\u3002 \u3000\u3000\u4f55\u51b5\u91d1\u6885\u9f84\u8bf4\u7684\u8bdd\u53c8\u662f\u95ea\u95ea\u7f29\u7f29\u7684\uff0c\u201c\u4faf\u4e8c\u201d\u4e0d\u7981\u7591\u5fc3\u7740\uff1a\u201c\u96be\u9053\u5979\u662f\u5949\u4e86\u2018\u6bd2\u541b\u2019 \u7684\u547d\u4ee4\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u7236\u5973\u4e24\u4eba\uff0c\u5fc3\u4e2d\u6240\u60f3\u7684\uff0c\u622a\u7136\u4e0d\u76f8\u540c\uff0c\u4e8e\u662f\u201c\u4faf\u4e8c\u201d\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u5973\u5b69\u5b50\u5bb6\uff0c \u6df1\u66f4\u534a\u591c\u8dd1\u6765\u8dd1\u53bb\uff0c\u6253\u542c\u4e00\u4e2a\u7537\u4eba\u7684\u5e95\u7ec6\uff0c\u6210\u4e2a\u4ec0\u4e48\u6837\u5b50\uff0c\u8d76\u5feb\u597d\u597d\u7684\u56de\u53bb\u5427\uff01\u201d\u4ed6\u4e0d \u81ea\u89c9\u5730\uff0c\u5728\u8bdd\u4e2d\u6d41\u9739\u51fa\u5bf9\u5973\u513f\u7684\u5173\u6000\u7684\u8bed\u6c14\u3002 \u3000\u3000\u4f46\u662f\u91d1\u6885\u9f84\u5f53\u7136\u4e0d\u4f1a\u542c\u51fa\u6765\uff0c\u5979\u518d\u4e5f\u6ca1\u6709\u60f3\u5230\uff0c\u8fd9\u7ad9\u5728\u5979\u9762\u524d\u7684\u8001\u8005\u4f1a\u662f\u5979\u7684\u4eb2\u751f \u7236\u4eb2\u3002 \u3000\u3000\u9020\u5316\u5f04\u4eba\uff0c\u6bcf\u6bcf\u5982\u662f\uff0c\u91d1\u6885\u9f84\u4e00\u5fc3\u6240\u60f3\u7684\uff0c\u9664\u4e86\u8f9b\u6377\uff0c\u518d\u65e0\u522b\u4eba\uff0c\u5e73\u65e5\u7684\u673a\u667a\u548c\u806a \u9896\uff0c\u6b64\u523b\u4e5f\u88ab\u592a\u591a\u7684\u60c5\u611f\u6240\u6df9\u6ca1\u4e86\u3002 \u3000\u3000\u5979\u7adf\u6000\u6068\u8fd9\u8001\u4eba\uff0c\u4e0d\u80af\u5c06\u8f9b\u6377\u7684\u4e8b\u544a\u8bc9\u5979\uff0c\u4e8e\u662f\u5979\u6124\u6068\u5730\u8bf4\uff1a\u201c\u6211\u4e00\u5b9a\u8981\u77e5\u9053\u8f9b\u6377 \u7684\u5e95\u7ec6\uff0c\u4f60\u8981\u662f\u62e6\u963b\u6211\uff0c\u6211\u2026\u2026\u6211\u5c31\u8981\u5bf9\u4f60\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d\u201c\u4faf\u4e8c\u201d\u9053\uff1a\u201c\u4f60\u6562\u4e0d\u542c\u6211\u7684 \u8bdd\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u54fc\u4e86\u4e00\u58f0\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u6211\u51ed\u4ec0\u4e48\u8981\u542c\u4f60\u7684\u8bdd\u3002\u201d \u3000\u3000\u6b64\u523b\u5979\u8111\u4e2d\u6df7\u6c8c\u5df2\u6781\uff0c\u60c5\u611f\u4e5f\u5728\u51b2\u52a8\u6f8e\u6e43\u7740\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u4e0d\u8ba9\u6211\u77e5\u9053\u4ed6\u7684\u4e8b\uff0c\u6211\u5c31 \u5148\u6253\u5012\u4f60\u518d\u8bf4\u3002\u201d \u3000\u3000\u5979\u7684\u601d\u60f3\uff0c\u5df2\u56e0\u7740\u8fc7\u591a\u7684\u60c5\u611f\uff0c\u800c\u53d8\u5f97\u504f\u6fc0\u4e86\uff0c\u5a07\u53f1\u9053\uff1a\u201c\u4f60\u51ed\u4ec0\u4e48\u8981\u6765\u7ba1\u6211\u7684 \u4e8b\uff1f\u201d \u3000\u3000\u53cc\u638c\u4e00\u9519\uff0c\u53f3\u8098\u5fae\u66f2\uff0c\u53f3\u638c\u524d\u5f15\uff0c\u5237\uff0c\u5237\uff0c\u4e24\u638c\uff0c\u7528\u5c3d\u4e86\u5168\u8eab\u7684\u529f\u529b\uff0c\u5411\u201c\u4faf\u4e8c\u201d \u62cd\u53bb\u3002 \u3000\u3000\u5979\u4e0d\u77e5\u9053\u5979\u7684\u5bf9\u8c61\u662f\u5979\u7684\u7236\u4eb2\uff0c\u201c\u4faf\u4e8c\u201d\u4e5f\u6ca1\u6709\u60f3\u5230\u5979\u4f1a\u7a81\u7136\u51fa\u51fb\uff0c\u60ca\u89c9\u65f6\uff0c\u638c\u98ce \u5df2\u6251\u9762\u800c\u6765\u3002 \u3000\u3000\u201c\u4faf\u4e8c\u201d\u672c\u80fd\u7684\u4e3e\u638c\u76f8\u683c\uff0c\u4f46\u662f\u5728\u8fd9\u4e00\u5239\u90a3\uff0c\u4ed6\u5fd8\u4e86\u4ed6\u53cc\u80a9\u529f\u529b\u5df2\u5931\uff0c\u600e\u654c\u5f97\u8fd9 \u201c\u6bd2\u541b\u91d1\u4e00\u9e4f\u201d\u5341\u5e74\u683d\u57f9\u7684\u91d1\u6885\u9f84\u4e00\u638c\uff0c\u4f55\u51b5\u91d1\u6885\u9f84\u4ee5\u4e3a\u4ed6\u7684\u529f\u529b\u9ad8\u51fa\u81ea\u5df1\u751a\u591a\uff0c\u8fd9\u4e24 \u638c\u66f4\u662f\u5168\u529b\u800c\u65bd\u3002 \u3000\u3000\u91d1\u6885\u9f84\u89c1\u4ed6\u4e3e\u638c\u76f8\u8fce\uff0c\u5fc3\u4e2d\u65b9\u81ea\u4e00\u60ca\uff0c\u6050\u6015\u81ea\u5df2\u63a5\u4e0d\u4f4f\u4ed6\u7684\u638c\u529b\uff0c\u5de6\u638c\u8fce\u5374\uff0c\u53f3\u638c \u5374\u4ece\u5de6\u8098\u4e0b\u7a7f\u51fa\uff0c\u90a3\u77e5\u9053\u5979\u5de6\u638c\u63a5\u89e6\u5230\u7684\u7adf\u662f\u4e00\u53cc\u4e1d\u6beb\u6ca1\u6709\u52b2\u529b\u7684\u624b\u638c\uff0c\u60ca\u7591\u4e4b\u95f4\uff0c\u7a81 \u7136\u4e24\u638c\uff0c\u5df2\u5168\u4e2d\u4e86 \u3000\u3000\u5bf9\u65b9\u7684\u524d\u80f8\u3002 \u3000\u3000\u201c\u4faf\u4e8c\u201d\u9976\u662f\u529f\u529b\u6df1\u539a\uff0c\u4e5f\u7981\u4e0d\u5f97\u5979\u8fd9\u4e24\u638c\uff0c\u201c\u54c7\u201d\u5730\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u5168\u90fd\u6e85\u5728\u91d1 \u6885\u9f84\u7fe0\u7eff\u8272\u7684\u8863\u88f3\u4e0a\uff0c\u91d1\u6885\u9f84\u5fc3\u91cc\u5ffd\u7136\u6709\u4e00\u79cd\u6b49\u759a\u7684\u611f\u89c9\uff0c\u5979\u5bf9\u81ea\u5df1\u80fd\u4e00\u638c\u51fb\u5012\u8fd9\u7626\u524a \u8001\u4eba\uff0c\u767e\u601d\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u5979\u6697\u5fd6\uff1a\u201c\u4ed6\u7684\u529f\u529b\u7edd\u5bf9\u4e0d\u4f1a\u88ab\u6211\u4e00\u638c\u51fb\u5012\u5440\uff01\u5c31\u4ee5\u4ed6\u7684\u8f7b\u529f\u6765\u8bf4\uff0c\u4e5f\u597d\u50cf\u8fdc\u5728\u6211 \u4e4b\u4e0a\u2014\u2014\u201d \u3000\u3000\u201c\u4faf\u4e8c\u201d\u865a\u5f31\u5730\u53f9\u51fa\u4e00\u53e3\u6c14\uff0c\u62ac\u671b\u82cd\u5929\uff0c\u773c\u4e2d\u4e00\u7247\u6a21\u7cca\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u5185\u8151\u5df2\u53d7\u91cd\u4f24\uff0c \u4e0d\u7981\u6697\u6697\u53f9\u606f\u7740\u547d\u8fd0\u5b89\u6392\uff1a\u201c\u4e3a\u4ec0\u4e48\u8ba9\u6211\u6b7b\u5728\u6211\u5973\u513f\u7684\u624b\u4e0a\uff1f\u201d\u4e8e\u662f\u4ed6\u52c9\u5f3a\u62db\u8d77\u624b\u6765\uff0c \u8bf4\uff1a\u201c\u4f60\u8fc7\u6765\u3002\u201d \u3000\u3000\u91d1\u6885\u9f84\u89c9\u5f97\u4f3c\u4e4e\u6709\u4e00\u79cd\u4e0d\u53ef\u6297\u62d2\u7684\u529b\u91cf\uff0c\u4f7f\u5f97\u5979\u4f9d\u7136\u8d70\u5230\u8fd9\u5782\u6b7b\u7684\u8001\u4eba\u9762\u524d\uff0c\u201c\u4faf \u4e8c\u201d\u671b\u7740\u661f\u7a7a\u4e0b\u5979\u5973\u513f\u9762\u5e9e\uff0c\u4e0d\u77e5\u9053\u662f\u559c\uff0c\u662f\u60b2\uff0c\u662f\u6012\u3002 \u3000\u3000\u201c\u5509\uff0c\u4f60\u96be\u9053\u73b0\u5728\u8fd8\u4e0d\u77e5\u9053\u6211\u662f\u8c01\u5417\uff1f\u6211\u662f\u2014\u2014-\u201d\u4ed6\u7a81\u7136\u60f3\u8d77\u6b64\u523b\u600e\u80fd\u8bf4\u51fa\u81ea\u5df1 \u548c\u5979\u7684\u5173\u7cfb\uff0c\u90a3\u5c82\u4e0d\u4f1a\u4fbf\u5979\u62b1\u6068\u7ec8\u751f\uff0c\u4ed6\u5fd6\u9053\uff1a\u201c\u6211\u8be5\u539f\u8c05\u5979\uff0c\u56e0\u4e3a\u5979\u4e0d\u77e5\u9053\u5440\uff0c\u82e5\u6211 \u4f7f\u5979\u7ec8\u751f\u6094\u6068\uff0c\u90a3\u6211\u771f\u662f\u6b7b\u4e0d\u7791\u76ee\u4e86\uff0c\u6211\u4e1d\u6beb\u6ca1\u6709\u5c3d\u5230\u505a\u7236\u4eb2\u7684\u8d23\u4efb\uff0c\u6b64\u523b\u5374\u8be5\u4e3a\u5979\u5c3d \u6700\u540e\u4e00\u4efd\u5fc3\u610f\u4e86\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u5f3a\u5fcd\u7740\u4eba\u7c7b\u6700\u96be\u53d7\u7684\u75db\u82e6\uff0c\u5728\u4e34\u6b7b\u7684\u65f6\u5019\uff0c\u8fd8\u5728\u9690\u85cf\u7740\u4ed6\u5fc3\u91cc\u6700\u4e0d\u613f\u610f\u9690\u85cf \u7684\u4e8b\u3002 \u3000\u3000\u4f46\u662f\u5728\u8fd9\u4e00\u523b\uff0c\u91d1\u6885\u9f84\u7684\u80f8\u6d77\u7a81\u7136\u53d8\u5f97\u5f02\u5e38\u7a7a\u7075\uff0c\u8fd9\u7626\u524a\u8001\u4eba\u7684\u6bcf\u4e00\u53e5\u542b\u7740\u6df1\u610f\uff0c \u800c\u5979\u5f53\u65f6\u5e76\u4e0d\u660e\u4e86\u7684\u8bdd\uff0c\u5728\u6b64\u77ac\u606f\u4e4b\u95f4\u63a0\u8fc7\u5979\u8111\u6d77\u65f6\uff0c\u5979\u7a81\u7136\u5168\u90e8\u4e86\u89e3\u4e86\uff0c\u867d\u7136\u8fd9\u4e86\u89e3 \u662f\u75db\u82e6\u7684\u3002 \u3000\u3000\u201c\u4ed6\u2014\u2014\u4ed6\u96be\u9053\u771f\u662f\u6211\u7684\u7236\u4eb2\u3002\u201d\u867d\u7136\u5979\u5e73\u65e5\u5bf9\u5979\u7684\u7236\u4eb2\u5e76\u6ca1\u6709\u60c5\u611f\uff0c\u751a\u81f3\u8fd8\u6709\u4e9b \u6028\u4ec7\uff0c\u4f46\u6b64\u523b\uff0c\u9aa8\u8089\u7684\u5929\u6027\u50cf\u5c71\u95f4\u7684\u6d2a\u6c34\uff0c\u7a81\u7136\u7206\u53d1\u4e86\u51fa\u6765\uff0c\u201c\u6211\u2014\u2014\u6211\u6740\u6b7b\u4e86\u6211\u7684\u7236 \u4eb2\u3002\u201d \u3000\u3000\u4e8e\u662f\u5979\u75db\u54ed\u4e86\uff0c\u50cf\u66ae\u6625\u557c\u8840\u7684\u675c\u9e43\u3002 \u3000\u3000\u5979\u6251\u5230\u8fd9\u5782\u6b7b\u7684\u8001\u4eba\u8eab\u4e0a\uff0c\u8fd9\u65f6\u5019\uff0c\u5979\u5fd8\u5374\u4e86\u8f9b\u6377\uff0c\u5fd8\u5374\u4e86\u4e00\u5207\uff0c\u4e00\u79cd\u66f4\u5f3a\u5927\u7684\u529b \u91cf\uff0c\u5c06\u5979\u9a71\u5165\u66f4\u75db\u82e6\u7684\u6df1\u6e0a\u91cc\u3002 \u3000\u3000\u201c\u4faf\u4e8c\u201d\u6700\u540e\u7684\u4e00\u4e1d\u5fae\u7b11\uff0c\u6e17\u5408\u7740\u8840\u6c34\u81ea\u5634\u89d2\u6d41\u9732\u51fa\u6765\uff0c\u7136\u540e\u4ed6\u6c38\u8fdc\u79bb\u5f00\u4e86\u5eb8\u788c\u7684 \u4eba\u4e16\u3002 \u3000\u3000\u4ed6\u662f\u542b\u7b11\u800c\u6b7b\u7684\uff0c\u4f46\u4ed6\u7684\u8fd9\u7b11\u5bb9\u662f\u8868\u793a\u7740\u5feb\u4e50\u6291\u6216\u662f\u75db\u82e6\uff0c\u4e16\u4e0a\u6c38\u8fdc\u6ca1\u6709\u4efb\u4f55\u4eba\u80fd \u77e5\u9053\u3002 \u3000\u3000\u6c49\u9633\u4f4d\u4e8e\u6c49\u6c34\u4e4b\u5357\uff0c\u957f\u6c5f\u897f\u5cb8\uff0c\u5317\u6709\u5927\u522b\u5c71\uff0c\u4fd7\u79f0\u9f9f\u5c71\uff0c\u4e0e\u6b66\u660c\u9547\u4e4b\u86c7\u5c71\u9694\u6c5f\u9065\u9065 \u76f8\u5bf9\u3002 \u3000\u3000\u66ae\u6625\u4e09\u6708\uff0c\u83ba\u98de\u8349\u957f\uff0c\u6c49\u9633\u5317\u5cb8\uff0c\u897f\u6708\u6e56\u7554\u7684\u4e00\u5ea7\u5c0f\u5c0f\u7684\u5bfa\u5e99\u6c34\u6708\u5eb5\u91cc\uff0c\u591a\u4e86\u4e2a\u5999 \u9f84\u7684\u5c3c\u59d1\u3002 \u3000\u3000\u6668\u949f\u66ae\u9f13\uff0c\u5c81\u6708\u60a0\u60a0\uff0c\u8fd9\u5999\u9f84\u5c3c\u59d1\u773c\u4e2d\u7684\u6cea\u6c34\uff0c\u6c38\u8fdc\u6ca1\u6709\u4e00\u5929\u662f\u5e72\u7684\uff0c\u5979\u6bd4\u522b\u7684\u5c3c \u59d1\u4fee\u884c\u66f4\u82e6\uff0c\u64cd\u52b3\u66f4\u52e4\uff0c\u50cf\u662f\u60f3\u85c9\u8fd9\u4e9b\u8089\u4f53\u4e0a\u7684\u6298\u78e8\u6765\u6d88\u9664\u7cbe\u795e\u4e0a\u7684\u82e6\u75db\u4f3c\u7684\uff0c\u4f46\u662f\u6bcf \u5f53\u591c\u9759\u66f4\u6df1\uff0c\u4eba\u4eec\u5982\u679c\u7ecf\u8fc7\u8fd9\u5c0f\u5c0f\u7684\u6c34\u6708\u5eb5\u7684\u540e\u9662\uff0c\u5c31\u4f1a\u53d1\u73b0\u8fd9\u82e6\u4fee\u7684\u5999\u9f84\u5c3c\u59d1\u603b\u4f1a\u5728 \u9662\u4e2d\u7ec3\u4e60\u7740\u5185\u5bb6\u7cbe\u6df1\u7684\u6b66\u529f\uff0c\u6216\u8005\u662f\u5728\u5eb5\u5899\u5916\u8349\u5c3e\u6811\u68a2\u4e0a\uff0c\u7ec3\u4e60\u7740\u6b66\u6797\u4e2d\u7edd\u9876\u7684\u8f7b\u8eab\u529f \u592b\u3002 \u3000\u3000\u6bcf\u5f53\u6708\u5706\u82b1\u597d\u4e4b\u65f6\uff0c\u826f\u8fb0\u7f8e\u666f\u4e4b\u4e0b\uff0c\u5979\u53c8\u4f1a\u72ec\u81ea\u8e48\u8e48\u5728\u6708\u5149\u4e4b\u4e0b\uff0c\u5e7d\u5e7d\u53f9\u606f\uff0c\u50cf\u662f \u5979\u5bf9\u4eba\u4e16\u95f4\uff0c\u5c1a\u6709\u8bb8\u591a\u672a\u80fd\u629b\u4e0b\u4e4b\u4e8b\u3002 \u3000\u3000\u5979\u5c31\u662f\u6df1\u6df1\u4edf\u6094\u7740\u7684\u91d1\u6885\u9f84\u3002 \u3000\u3000\u5979\u627e\u4e0d\u51fa\u4e00\u79cd\u53ef\u4ee5\u5bbd\u6055\u5979\u6740\u7236\u884c\u4e3a\u7684\u7406\u7531\uff0c\u7eb5\u7136\u8fd9\u884c\u4e3a\u662f\u5728\u65e0\u610f\u4e2d\u9020\u6210\u7684\uff0c\u4f46\u662f\u5979 \u7684\u826f\u5fc3\u5374\u4e0d\u5141\u8bb8\u5979\u5bbd\u6055\u81ea\u5df1\uff0c\u4e8e\u662f\u5979\u629b\u5f00\u4e86\u2014\u2014\u5207\uff0c\u751a\u81f3\u629b\u5f00\u4e86\u5bf9\u8f9b\u6377\u7684\u6000\u5ff5\uff0c\u72ec\u81ea\u8dd1 \u5230\u8fd9\u5c0f\u5c0f\u7684\u5eb5\u4e2d\u6765\u6f5c\u4fee\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u5bc2\u5bde\u4e2d\u7684\u65f6\u65e5\u662f\u6f2b\u957f\u7684\uff0c\u5979\u80fd\u5fcd\u53d7\u5f97\u4f4f\u5417\uff1f \u3000\u3000\u5c0f\u795e\u9f99\u8d3a\u4fe1\u96c4\u548c\u6c5f\u91cc\u767d\u9f99\u4e3a\u5979\u51c6\u5907\u597d\u4e86\u8239\u548c\u8239\u592b\uff0c\u5374\u7b49\u4e0d\u5230\u5979\u7684\u4eba\uff0c\u4e8e\u662f\u4ed6\u4eec\u4fbf\u626c \u5e06\u4e1c\u53bb\u4e86\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u5b59\u8d85\u8fdc\u6240\u76fc\u671b\u7684\uff0c\u4ed6\u4e0d\u613f\u610f\u8fd9\u4e00\u4efd\u8f9b\u82e6\u521b\u7acb\u7684\u6c34\u4e0a\u57fa\u4e1a\uff0c\u56e0\u4e3a\u7275\u6d89\u5230\u6b66\u6797\u4e2d \u8fd9\u513f\u4e2a\u51fa\u540d\u96be\u60f9\u7684\u4eba\u7269\u800c\u53d7\u5230\u5f71\u54cd\uff0c\u6709\u65f6\uff0c\u4ed6\u4f1a\u6697\u81ea\u601d\u7d22\uff1a\u201c\u8fd9\u5c71\u6885\u73e0\u5b9d\u53f7\u7684\u4e00\u4e2a\u73e0\u5b9d \u5546\u4eba\u4e3a\u4ec0\u4e48\u4f1a\u548c\u8fd9\u8bb8\u591a\u6b66\u6797\u4e2d\u7684\u6709\u540d\u4eba\u7269\u6709\u7740\u5173\u8054\u5462\uff1f\u800c\u4e14\u770b\u8d77\u6765\uff0c\u91d1\u6885\u9f84\u66f4\u50cf\u548c\u4ed6\u6709 \u7740\u4e0d\u5bfb\u5e38\u7684\u5173\u7cfb\u3002\u201d \u3000\u3000\u4e09\u4e2a\u6708\u4e4b\u540e\uff0c\u957f\u6c5f\u6cbf\u5cb8\u7684\u5341\u4e09\u5904\u5c71\u6885\u73e0\u5b9d\u53f7\u5168\u90fd\u795e\u79d8\u7684\u5173\u4e86\u95e8\uff0c\u201c\u8f9b\u6377\u201d\u8fd9\u4e2a\u540d\u5b57\uff0c \u9664\u4e86\u5728\u6b66\u6c49\u4e09\u9547\u4e4b\u5916\uff0c\u672c\u672a\u6fc0\u8d77\u4efb\u4f55\u98ce\u6d6a\uff0c\u73b0\u5728\u5373\u4f7f\u5728\u6b66\u6c49\u4e09\u9547\uff0c\u4e5f\u5f88\u5c11\u6709\u4eba\u518d\u4f1a\u8bb0\u5f97 \u8fd9\u4e2a\u540d\u5b57\u4e86\u3002 \u3000\u3000\u5c31\u7b97\u662f\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\u548c\u94f6\u67aa\u5b5f\u4f2f\u8d77\u8fd9\u4e9b\u4eba\uff0c\u73b0\u5728\u4e5f\u6b63\u88ab\u53e6\u5916\u8bb8\u591a\u771f\u6b63\u9707\u52a8\u6b66\u6797\u7684 \u4e8b\u6240\u5438\u5f15\uff0c\u4e5f\u4e0d\u518d\u53bb\u60f3\u8fd9\u4e2a\u5bb6\u8d22\u5de8\u4e07\u7684\u516c\u5b50\u54e5\u513f\u3002 \u3000\u3000\u7136\u800c\u201c\u8f9b\u6377\u201d\u8fd9\u540d\u5b57\u771f\u662f\u6c38\u8fdc\u6d88\u58f0\u706d\u8ff9\u4e86\u5417\uff1f \u3000\u3000\u8fd9\u4e2a\u95ee\u9898\u8c01\u4e5f\u4e0d\u80fd\u7ed9\u4e00\u4e2a\u80af\u5b9a\u7684\u7b54\u590d\u3002 \u3000\u3000\u5d06\u5cd2\u4e09\u7edd\u5251\u8fde\u8882\u5317\u4e0a\u6b66\u5f53\uff0c\u5728\u89e3\u5251\u6c60\u524d\uff0c\u88ab\u51cc\u98ce\u5251\u5ba2\u4e3a\u9996\u7684\u4e5d\u4e2a\u8d64\u9633\u9053\u957f\u4eb2\u4f20\u5f1f\u5b50\uff0c \u4e5d\u5251\u8fde\u73af\u6240\u5e03\u4e0b\u201c\u4e5d\u5bab\u5251\u9635\u201d\u56f0\u4e86\u516d\u4e2a\u65f6\u8fb0\uff0c\u4eba\u7edd\u5251\u82cf\u6620\u96ea\u529f\u529b\u8f83\u5dee\uff0c\u540e\u80cc\u4e2d\u4e86\u4e00\u638c\u5f53 \u573a\u5410\u8840\u3002 \u3000\u3000\u51cc\u98ce\u5251\u5ba2\u5c06\u201c\u8154\u606b\u4e09\u7edd\u5251\u201d\u51b7\u6f6e\u70ed\u8bbd\u4e86\u4e00\u9635\uff0c\u624d\u9a71\u9010\u4e0b\u5c71\uff0c\u8d64\u9633\u9053\u4eba\u6545\u505a\u4e0d\u77e5\uff0c\u4ed6 \u5b9e\u5728\u4e5f\u60f3\u4e58\u673a\u5c06\u5d06\u5cd2\u6d3e\u6253\u57ae\uff0c\u4e00\u6765\u662f\u786e\u5b9a\u81ea\u5df1\u5728\u6b66\u6797\u4e2d\u7684\u5730\u4f4d\uff0c\u4e8c\u6765\u5374\u662f\u60f3\u5c06\u5f53\u5e74\u4ed6\u548c \u5251\u795e\u5389\u9e57\u4e24\u4eba\u65e0\u610f\u4e2d\u5f97\u6765\u7684\u4e00\u4ef6\u5947\u5b9d\uff0c\u72ec\u81ea\u541e\u6ca1\u3002 \u3000\u3000\u5d06\u5cd2\u4e09\u7edd\u5251\u9996\u6b21\u88ab\u632b\uff0c\u72fc\u72c8\u5730\u4e0b\u4e86\u5c71\uff0c\u4eba\u7edd\u5251\u82cf\u6620\u96ea\u6c14\u606f\u5944\u5944\uff0c\u867d\u670d\u4e0b\u8bb8\u591a\u5d06\u5cd2\u79d8 \u5236\u7684\u8dcc\u6253\u79d8\u836f\uff0c\u4f46\u4ecd\u7136\u6beb\u65e0\u8d77\u8272\u3002 \u3000\u3000\u5929\u7edd\u5251\u8bf8\u845b\u660e\u548c\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4e24\u4eba\uff0c\u90fd\u5728\u6697\u604b\u7740\u8fd9\u4f4d\u5e08\u59b9\uff0c\u89c1\u4e86\u5979\u60e9\u5730\u6a21\u6837\uff0c\u6025\u5f97 \u4e94\u5185\u65e0\u4e3b\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u4e24\u4eba\u4e0d\u7981\u5927\u9a82\u6b66\u5f53\u6d3e\u4ee5\u591a\u4e3a\u80dc\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u5d06\u5cd2\u6d3e\u624d\u7b97\u6b63\u5f0f\u548c\u6b66\u5f53\u6d3e\u7ed3\u4e0b\u6028\u4ec7\uff0c\u7ea0 \u7f20\u591a\u5e74\uff0c\u90fd\u4e0d\u80fd\u4e86\u7ed3\u3002 \u3000\u3000\u4ed6\u4eec\u77e5\u9053\u8981\u7b49\u56de\u5230\u5d06\u5cd2\uff0c\u5e08\u59b9\u7684\u4f24\u6050\u6015\u5c31\u5f88\u96be\u6cbb\u5f97\u597d\u4e86\uff0c\u5929\u7edd\u5251\u8bf8\u845b\u660e\u4e3a\u4eba\u5916\u539a\u5185 \u8584\uff0c\u5728\u6c5f\u6e56\u4e0a\u4eba\u7f18\u6781\u597d\uff0c\u5404\u5730\u90fd\u6709\u719f\u4eba\uff0c\u5ffd\u7136\u60f3\u8d77\u4e00\u4eba\uff0c\u4fbf\u5411\u4e8e\u4e00\u98de\u9053\uff1a\u201c\u6211\u4eec\u4f55\u4e0d\u53bb \u627e\u5362\u9535\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u4e0d\u7981\u629a\u638c\u9053\uff1a\u201c\u5e08\u5144\u8981\u662f\u4e0d\u63d0\uff0c\u5c0f\u5f1f\u5012\u771f\u5fd8\u4e86\uff0c\u73b0\u6210\u5730\u653e\u7740\u4e00\u4f4d\u5999\u624b\u795e\u533b\u5728 \u6b64\uff0c\u5e08\u59b9\u8fd9\u4e00\u5904\u638c\u4f24\uff0c\u53ea\u8981\u4ed6\u80af\u52a8\u624b\u6cbb\uff0c \u3000\u3000\u8fd8\u6015\u4e0d\u624b\u5230\u75c5\u9664\u5417\uff1f\u4e0d\u8fc7\u53ea\u6015\u8fd9\u8001\u5934\u5b50\u53c8\u72af\u4e0a\u602a\u6bdb\u75c5\u5c31\u662f\u4e86\u3002\u201d\u5929\u7edd\u5251\u5374\u7b11\u9053\uff1a \u201c\u6b64\u4eba\u813e\u6c14\u867d\u7136\u53e4\u602a\uff0c\u4e0d\u5408\u610f\u7684\u75c5\u4eba\uff0c\u4f60\u6253\u6b7b\u4ed6\u4ed6\u4e5f\u4e0d\u533b\uff0c\u53ef\u662f\u6b64\u4eba\u5bf9\u6211\u5012\u9887\u4e3a\u9752\u7750\uff0c \u6211\u60f3\u6211\u53bb\u6c42\u4ed6\uff0c\u4ed6\u7edd\u4e0d\u4f1a\u4e0d\u7b54\u5e94\u7684\uff0c\u4eac\u5c71\u79bb\u6b64\u8fd8\u6709\u4e24\u5929\u8def\u7a0b\uff0c\u5c24\u5176\u6211\u4eec\u5e26\u7740\u4e2a\u75c5\u4eba\uff0c\u66f4 \u5f97\u5feb\u8d70\u624d\u884c\u3002\u201d \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u9a91\u7740\u9a6c\uff0c\u5374\u4e3a\u82cf\u6620\u96ea\u96c7\u4e86\u8f86\u5927\u8f66\uff0c\u663c\u591c\u517c\u7a0b\uff0c\u8d76\u5f80\u4eac\u5c71\uff0c\u53bb\u5bfb\u8bbf\u5f53\u65f6\u4ee5\u533b \u9053\u540d\u9707\u5929\u4e0b\u7684\u5999\u624b\u795e\u533b\u5362\u9535\uff0c\u66ff\u4eba\u7edd\u5251\u82cf\u6620\u96ea\u533b\u6cbb\u80cc\u4e0a\u7684\u638c\u4f24\uff0c\u539f\u6765\u5979\u4e2d\u7684\u8fd9\u4e00\u638c\u5df2\u4f24 \u53ca\u5185\u8151\uff0c\u4e0d\u662f\u666e\u901a\u533b\u836f\u53ef\u4ee5\u6cbb\u5f97\u597d\u7684\u4e86\u3002 \u3000\u3000\u4eac\u5c71\u4f4d\u4e8e\u9102\u7701\u4e4b\u4e2d\uff0c\u4f46\u5374\u4e0d\u751a\u7e41\u8363\uff0c\u53ea\u662f\u4e2a\u666e\u901a\u7684\u5c0f\u57ce\uff0c\u5999\u624b\u795e\u533b\u5c31\u5728\u4eac\u5c71\u57ce\u5916\u7ed3 \u5e90\u800c\u5c45\u3002 \u3000\u3000\u4ed6\u813e\u6c14\u6781\u602a\uff0c\u4e0d\u5bf9\u8def\u7684\u4eba\uff0c\u5c31\u7b97\u6b7b\u5728\u4ed6\u9762\u524d\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u533b\u6cbb\uff0c\u800c\u4e14\u4ed6\u6b66\u529f\u867d\u7136\u666e\u901a\uff0c \u533b\u9053\u5374\u6781\u9ad8\u660e\uff0c\u6c5f\u6e56\u4eba\u7684\u6210\u540d\u4fa0\u58eb\uff0c\u53d7\u8fc7\u4ed6\u6069\u60e0\u7684\u4eba\u4e0d\u5c11\uff0c\u6240\u4ee5\u6709\u4e9b\u4eba\u867d\u7136\u5bf9\u4ed6\u7684\u4f5c\u98ce \u4e0d\u6ee1\uff0c\u4e5f\u5948\u4e0d\u4e86\u4ed6\u4f55\u3002 \u3000\u3000\u5929\u7edd\u5251\u8bf8\u845b\u660e\u9a91\u7740\u9a6c\uff0c\u8d70\u5230\u5927\u8f66\u7684\u53f3\u8f95\u3002 \u3000\u3000\u6b64\u523b\u843d\u65e5\u5f52\u5c71\uff0c\u665a\u971e\u6ee1\u5929\uff0c\u66ae\u6625\u5929\u6c14\u867d\u4e0d\u751a\u70ed\uff0c\u4ed6\u4e00\u8def\u6025\u884c\uff0c\u4e5f\u8d76\u5f97\u6ee1\u8138\u5927\u6c57\uff0c\u638f \u51fa\u5757\u6c57\u5dfe\u64e6\u4e86\u64e6\uff0c\u773c\u770b\u7740\u5230\u524d\u9762\u7684\u4e00\u7247\u7af9\u6797\uff0c\u548c\u7af9\u6797\u4e2d\u9690\u9690\u9732\u51fa\u4e00\u5757\u5899\u9662\uff0c\u4e0d\u7531\u7cbe\u795e\u5927 \u632f\u3002 \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4e5f\u9ad8\u5174\u5730\u8bf4\u9053\uff1a\u201c\u524d\u9762\u5c31\u662f\u4e86\u5427\u3002\u201d \u3000\u3000\u8bf8\u845b\u660e\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d \u3000\u3000\u4e24\u4eba\u9f50\u9f50\u4e00\u7d27\u7f30\u7ef3\uff0c\u671d\u8d76\u8f66\u7684\u8bf4\u9053\uff1a\u201c\u5feb\u8d70\u3002\u201d\u4e00\u8f66\u4e24\u9a6c\uff0c\u4fbf\u4ee5\u52a0\u500d\u7684\u901f\u5ea6\uff0c\u671d\u7af9 \u6797\u8d76\u53bb\u3002 \u3000\u3000\u5230\u4e86\u7af9\u6797\u5916\u9762\uff0c\u8f66\u9a6c\u505c\u4f4f\u4e86\uff0c\u8bf8\u845b\u660e\u9053\uff1a\u201c\u6211\u4eec\u6b65\u884c\u8fdb\u53bb\u597d\u4e86\uff0c\u514d\u5f97\u90a3\u8001\u5934\u5b50\u53c8\u53d1 \u602a\u813e\u6c14\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u4fbf\u4e5f\u4e0b\u4e86\u9a6c\uff0c\u81ea\u5927\u8f66\u91cc\u6276\u51fa\u82cf\u6620\u96ea\uff0c\u6b64\u65f6\u5979\u6e05\u6e05\u79c0\u79c0\u7684\u2014\u2014\u5f20\u74dc\u5b50\u8138\uff0c\u4e5f\u53d8 \u5f97\u5f02\u5e38\u82cd\u767d\uff0c\u5f80\u65e5\u4e24\u988a\u4e0a\u7684\u7ea2\u6655\uff0c\u6b64\u523b\u4e5f\u5168\u6ca1\u6709\u4e86\uff0c\u4e8e\u4e00\u98de\u5fc3\u91cc\u4e00\u9635\u601c\u60dc\uff0c\u6b63\u60f3\u5c06\u5979\u6a2a \u62b1\u8d77\u6765\u3002 \u3000\u3000\u90a3\u8fb9\u845b\u8bf8\u660e\u5374\u4e5f\u8d76\u4e86\u51fa\u6765\uff0c\u4f38\u51fa\u5de6\u624b\u6276\u4f4f\u82cf\u6620\u96ea\u7684\u5de6\u81c2\uff0c\u4e8e\u4e00\u98de\u52c9\u5f3a\u5730\u7b11\u4e86\u7b11\u4e86\uff0c \u4e24\u4eba\u4fbf\u4e00\u9f50\u6400\u6276\u7740\u82cf\u6620\u96ea\u5f80\u91cc\u8d70\u3002 \u3000\u3000\u7af9\u6797\u91cc\u662f\u4e00\u6761\u77f3\u5b50\u94fa\u6210\u7684\u8def\uff0c\u76f4\u901a\u5230\u5999\u624b\u795e\u533b\u6240\u4f4f\u7684\u51e0\u95f4\u8349\u5e90\uff0c\u6797\u4e2d\u9759\u5bc2\uff0c\u9e1f\u8bed\u866b \u9e23\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u811a\u6b65\u8e0f\u5728\u788e\u77f3\u5b50\u8def\u4e0a\uff0c\u4e5f\u5237\u5237\u5730\u53d1\u51fa\u58f0\u54cd\u3002 \u3000\u3000\u5899\u662f\u7af9\u679d\u7f16\u6210\u7684\uff0c\u4e0a\u9762\u8584\u8584\u5730\u6577\u7740\u4e00\u5c42\u7070\u6ce5\uff0c\u7070\u6ce5\u4e0a\u722c\u6ee1\u4e86\u5bc4\u751f\u866b\uff0c\u770b\u4e0a\u53bb\u522b\u81f4\u5f97 \u5f88\u3002 \u3000\u3000\u4ed6\u4eec\u8f7b\u8f7b\u5730\u62cd\u7740\u95e8\uff0c\u90a3\u77e5\u62cd\u4e09\u3001\u4e94\u5341\u4e0b\uff0c\u5c4b\u5185\u4e1d\u6beb\u6ca1\u6709\u58f0\u97f3\uff0c\u4e8e\u4e00\u98de\u9053\uff1a\u201c\u96be\u9053\u5e90 \u8001\u5148\u751f\u51fa\u53bb\u4e86\u5417\uff1f\u201d \u3000\u3000\u845b\u8bf8\u660e\u6447\u5934\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\uff0c\u8fd1\u5341\u5e74\u6765\uff0c\u5c31\u6ca1\u6709\u542c\u8bf4\u8fc7\u4ed6\u51fa\u53bb\u8fc7\u3002\u201d\u4ed6\u671d\u56db\u5468\u770b\u4e86\u770b\uff0c \u53c8\u9053\uff1a\u201c\u4f60\u770b\uff0c\u8fd9\u5927\u95e8\u6839\u672c\u6ca1\u6709\u9501\uff0c\u5c31\u7b97\u4ed6\u51fa\u53bb\u4e86\uff0c\u5c4b\u91cc\u4e5f\u8be5\u6709\u4eba\u7167\u987e\u5440\u3002\u201d\u4e8e\u662f\u4ed6\u53c8 \u62cd\u95e8\u3002 \u3000\u3000\u53c8\u62cd\u4e86\u51e0\u4e0b\uff0c\u5927\u95e8\u7adf\u201c\u5440\u201d\u5730\u4e00\u58f0\uff0c\u5f00\u4e86\uff0c\u60f3\u662f\u91cc\u9762\u7684\u95e8\u5e76\u6ca1\u6709\u5173\u597d\uff0c\u845b\u8bf8\u660e\u4fbf\u9053\uff1a \u201c\u8001\u4e8c\uff0c\u6211\u4eec\u8fdb\u53bb\u770b\u770b\u597d\u4e0d\u597d\u3002\u201d \u3000\u3000\u8d70\u5230\u9662\u91cc\uff0c\u4ecd\u662f\u6084\u65e0\u4eba\u58f0\uff0c\u845b\u8bf8\u660e\u9ad8\u58f0\u558a\u9053\uff1a\u201c\u5e90\u5148\u751f\u5728\u5417\uff1f\u201d\u4f46\u9664\u4e86\u9e1f\u8bed\u5916\uff0c\u522b \u65e0\u56de\u7b54\u3002 \u3000\u3000\u4ed6\u4e0d\u7981\u7591\u4e91\u5927\u8d77\uff0c\u4fa7\u9996\u5411\u4e8e\u4e00\u98de\u9053\uff1a\u201c\u4f60\u6276\u7740\u5e08\u59b9\u7ad9\u5728\u8fd9\u91cc\uff0c\u6211\u53bb\u770b\u770b\uff0c\u4e0d\u8981\u662f\u51fa \u4e86\u4ec0\u4e48\u4e8b\u624d\u597d\u3002\u201d \u3000\u3000\u8bed\u672a\u8bf4\u5b8c\uff0c\u7a81\u7136\u5c4b\u91cc\u4e00\u4e2a\u9634\u607b\u607b\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u5feb\u6eda\u51fa\u53bb\u3002\u201d\u867d\u53ea\u56db\u5b57\uff0c\u4f46\u5374\u5e26\u7740 \u4e00\u4e1d\u5bd2\u610f\u3002 \u3000\u3000\u845b\u8bf8\u660e\u4e00\u542c\u6b64\u4eba\u7684\u53e3\u97f3\uff0c\u548c\u5999\u624b\u795e\u533b\u7684\u6e56\u5317\u571f\u97f3\u5927\u5927\u4e0d\u76f8\u540c\uff0c\u4fbf\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8c01\uff0c \u5728\u4e0b\u2018\u5d06\u5cd2\u4e09\u7edd\u5251\u2019\uff0c\u7279\u6765\u62dc\u8bbf\u5e90\u8001\u5148\u751f\u3002\u201d \u3000\u3000\u4ed6\u6ee1\u4ee5\u4e3a\u51ed\u7740\u201c\u5d06\u5cd2\u4e09\u7edd\u5251\u201d\u7684\u540d\u5934\uff0c\u603b\u53ef\u9707\u4f4f\u5bf9\u65b9\u3002 \u3000\u3000\u54ea\u77e5\u90a3\u4eba\u4ecd\u7136\u9634\u607b\u607b\u5730\u8bf4\u9053\uff1a\u201c\u6211\u8bf4\u6eda\u51fa\u53bb\uff0c\u4f60\u4eec\u542c\u5230\u6ca1\u6709\u3002\u201d\u63a5\u7740\u9760\u9662\u5b50\u8fb9\u8fd9\u8fb9 \u7684\u7a97\u6237\uff0c\u201c\u7830\u201d\u5730\u4e00\u58f0\u6253\u5f00\u4e86\uff0c\u7a97\u53e3\u9732\u51fa\u4e00\u5f20\u82cd\u767d\u7684\u9762\u5b54\u6765\uff0c\u6ca1\u6709\u8840\u8272\u7684\u7a0b\u5ea6\u66f4\u8fdc\u5728\u82cf \u6620\u96ea\u4e4b\u4e0a\u3002 \u3000\u3000\u770b\u5230\u8fd9\u5f20\u9762\u5b54\uff0c\u4e8e\u4e00\u98de\u3001\u845b\u8bf8\u660e\u90fd\u4e0d\u7531\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u9f50\u58f0\u559d\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u90a3\u4eba \u9634\u51c4\u51c4\u4e00\u58f0\u957f\u7b11\uff0c\u51b7\u9510\u7684\u76ee\u5149\u6781\u5feb\u5730\u5728\u4ed6\u4eec\u8eab\u4e0a\u6253\u4e86\u4e2a\u8f6c\uff0c\u7136\u540e\u76ef\u5728\u4eba\u7edd\u5251\u82cf\u6620\u96ea\u8138\u4e0a\uff0c \u5567\u5567\u8d5e\u9053\uff1a\u201c\u597d\u6f02\u4eae\u3002\u201d \u3000\u3000\u5929\u7edd\u5251\u3001\u5730\u7edd\u5251\u4e0d\u7531\u5927\u6012\uff0c\u90a3\u77e5\u90a3\u4eba\u6839\u672c\u4e0d\u5c06\u4ed6\u4eec\u653e\u5728\u773c\u91cc\uff0c\u770b\u4e86\u82cf\u6620\u96ea\u4e00\u4f1a\u513f\uff0c \u8138\u5b54\u4e00\u677f\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u8fd8\u803d\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\uff0c\u5e90\u8001\u5934\u5b50\u73b0\u5728\u6ca1\u6709\u529f\u592b\u66ff\u4f60\u4eec\u533b\u75c5\uff0c\u4f60\u4eec\u5feb \u6eda\u3002\u201d \u3000\u3000\u4ed6\u4e00\u8fde\u4e09\u58f0\u201c\u5feb\u6eda\u201d\uff0c\u4e8e\u4e00\u98de\u5927\u6012\u559d\u9053\uff1a\u201c\u670b\u53cb\u662f\u54ea\u6761\u7ebf\u4e0a\u7684\uff0c\u8bf7\u4eae\u4e2a\u2018\u4e07\u513f\u2019\u51fa \u6765\u3002\u201d \u3000\u3000\u90a3\u4eba\u5374\u50cf\u6ee1\u4e0d\u61c2\u8fd9\u4e00\u5957\uff0c\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u6211\u6570\u5230\u5341\uff0c\u4f60\u4eec\u8fd8\u4e0d\u6eda\uff0c\u6211\u5c31\u8981\u5bf9\u4f60\u4eec\u4e0d\u5ba2 \u6c14\u4e86\u3002\u201d \u3000\u3000\u63a5\u7740\uff0c\u4ed6\u5c31\u65c1\u82e5\u65e0\u4eba\u5730\uff0c\u6162\u6162\u6570\u8d77\u6765\uff1a\u201c\u4e00\u3001\u4e8c\u3001\u4e09\u2014\u2014\u201d \u3000\u3000\u4e8e\u4e00\u98de\u9762\u542b\u6740\u673a\uff0c\u4f46\u671b\u4e86\u9893\u5012\u5728\u81ea\u5df1\u624b\u81c2\u4e0a\u6655\u8ff7\u7740\u7684\u82cf\u6620\u96ea\u4e00\u773c\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u5e08\u5144 \u6211\u4eec\u5148\u9000\u51fa\u53bb\u3002\u201d \u3000\u3000\u845b\u8bf8\u660e\u4e5f\u987e\u8651\u7740\u82cf\u6620\u96ea\u7684\u5b89\u5168\uff0c\u5fae\u4e00\u9894\u9996\uff0c\u4e09\u4eba\u4e00\u8d77\u9000\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u4eec\u65b9\u624d\u8d70\u51fa\u9662\u95e8\uff0c\u90a3\u4eba\u4e5f\u521a\u597d\u6570\u5230\u5341\u3002 \u3000\u3000\u6570\u5b8c\u4e86\u4fbf\u54c8\u54c8\u5927\u7b11\u7740\uff0c\u5929\u7edd\u5251\u845b\u8bf8\u660e\u548c\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4f55\u66fe\u53d7\u8fc7\u8fd9\u6837\u7684\u6c14\uff0c\u4e8e\u4e00\u98de\u9053\uff1a \u201c\u5c0f\u5f1f\u5148\u8fdb\u53bb\u770b\u4e2a\u7a76\u7adf\u3002\u201d \u3000\u3000\u4ed6\u77e5\u9053\u7a97\u4e2d\u4e4b\u4eba\u5fc5\u5b9a\u662f\u4e2a\u5f3a\u654c\uff0c\u53cd\u624b\u5c06\u5251\u64a4\u4e86\u51fa\u6765\uff0c\u4ed6\u5728\u8fd9\u67c4\u5251\u4e0a\u5df2\u6709\u4e86\u5341\u6570\u5e74\u7684 \u6d78\u7ec3\uff0c\u5d06\u5cd2\u7684\u201c\u5c11\u9633\u4e5d\u4e00\u5f0f\u201d\u53c8\u662f\u51a0\u7edd\u6c5f\u6e56\uff0c\u4e00\u5251\u5728\u624b\uff0c\u4ed6\u7acb\u523b\u80c6\u6c14\u5927\u589e\uff0c\u5fae\u4e00\u5206\u8eab\uff0c \u53c8\u7a9c\u56de\u9662\u4e2d\u53bb\u3002 \u3000\u3000\u4ed6\u8f7b\u529f\u4e0d\u5f31\uff0c\u843d\u5730\u65f6\u53ef\u8bf4\u7edd\u6ca1\u6709\u53d1\u751f\u58f0\u97f3\u6765\uff0c\u90a3\u77e5\u773c\u524d\u4e00\u6643\uff0c\u90a3\u4eba\u5df2\u7531\u7a97\u4e2d\u63a0\u4e86\u51fa \u6765\uff0c\u8f7b\u529f\u66f4\u8fdc\u5728\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4e4b\u4e0a\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u4e0d\u7531\u5927\u60ca\uff0c\u90a3\u4eba\u5df2\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u4f60\u53ef\u66fe\u542c\u5230\u8bf4\u5929\u9b54\u91d1\u6b39\u624b\u4e0b\u7559\u8fc7\u4e00\u4e2a\u6d3b\u53e3 \u7684\u3002\u201d \u3000\u3000\u201c\u5929\u9b54\u91d1\u6b39\u201d\u8fd9\u56db\u4e2a\u5b57\u53ef\u771f\u5c06\u4e8e\u4e00\u98de\u9707\u4f4f\u4e86\uff0c\u4ed6\u6697\u5fd6\uff1a\u201c\u539f\u6765\u6b64\u4eba\u5c31\u662f\u5929\u9b54\u91d1\u6b39\u3002\u201d \u8138\u4e0a\u7684\u795e\u8272\u4e0d\u89c9\u60ca\u614c\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u53c8\u9053\uff1a\u201c\u770b\u5728\u5389\u9e57\u7684\u9762\u5b50\uff0c\u4eca\u5929\u4f60\u5c31\u662f\u6211\u624b\u4e0b\u9003\u51fa\u7684\u7b2c\u4e00\u4e2a\u6d3b\u53e3\uff0c\u5feb\u6eda \u5427\uff01\u201d \u3000\u3000\u5730\u7edd\u5251\u867d\u7136\u5fc3\u9ad8\u6c14\u50b2\uff0c\u6b64\u65f6\u6b64\u5730\uff0c\u649e\u5230\u8fd9\u7b49\u4eba\u7269\uff0c\u4e5f\u4e0d\u89c9\u7565\u6709\u4e9b\u6c14\u6cae\uff0c\u8003\u8651\u4e86\u534a\u54cd\uff0c \u4e5f\u672a\u8bf4\u8bdd\uff0c\u4fbf\u53c8\u7a9c\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u6084\u6084\u4f38\u624b\u4e00\u62ed\u6c57\uff0c\u8138\u4e0a\u73b0\u51fa\u75db\u82e6\u7684\u795e\u8272\u6765\uff0c\u63a0\u56de\u7a97\u91cc\u65f6\uff0c\u8eab\u624b\u4e5f\u663e\u5f97\u8fdf\u949d \u5f97\u5f88\u3002 \u3000\u3000\u5c4b\u91cc\u653e\u7740\u4e00\u5f20\u957f\u584c\uff0c\u584c\u4e0a\u5782\u76ee\u76d8\u819d\u5750\u7740\u4e00\u4e2a\u9b13\u89d2\u5df2\u7ecf\u82b1\u767d\u7684\u6e05\u970d\u8001\u8005\uff0c\u5bf9\u5916\u9762\u53d1\u751f \u7684\u4e00\u5207\uff0c\u50cf\u662f\u5168\u7136\u65e0\u52a8\u4e8e\u8877\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u8d70\u4e86\u8fc7\u53bb\uff0c\u671d\u90a3\u8001\u8005\u9053\uff1a\u201c\u59d3\u5e90\u7684\uff0c\u4f60\u53ef\u8981\u653e\u806a\u660e\u4e9b\uff0c\u4f60\u603b\u8be5\u77e5\u9053\u2018\u767e\u4f1a \u7a74\u2019\u662f\u600e\u6837\u7684\u4e00\u4e2a\u7a74\u9053\uff0c\u800c\u4e14\u6211\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u5929\u4e0b\u518d\u4e5f\u6ca1\u6709\u522b\u4eba\u89e3\u5f97\u5f00\uff0c\u4f60\u8981\u662f\u518d\u4e0d\u7b54 \u5e94\uff0c\u6211\u59d3\u91d1\u7684\u53ef\u8fd8\u6b7b\u4e0d\u4e86\uff0c\u4f60\u59d3\u5e90\u7684\u53ef\u6d3b\u4e0d\u4e86\u591a\u5c11\u4e2a\u65f6\u8fb0\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u5929\u9b54\u91d1\u6b39\u5728\u7389\u5973\u5f20\u6e05\u79e6\u83c1\u6349\u8ff7\u85cf\u65f6\uff0c\u4e58\u9699\u9003\u8dd1\uff0c\u50ac\u547d\u7b26\u5510\u658c\u5e26\u7740\u5510\u7075\u3001\u5510\u66fc\u5728 \u540e\u9762\u6025\u8ffd\u3002 \u3000\u3000\u53ef\u662f\u5510\u658c\u7b49\u53d1\u6b65\u8f83\u665a\uff0c\u8f7b\u529f\u4e5f\u4e0d\u5982\u91d1\u6b39\uff0c\u600e\u8ffd\u5f97\u4e0a\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u9003\u4e86\u4e00\u4f1a\uff0c\u80f8\u8179\u4e4b\u95f4\uff0c\u75bc\u75db\u65e0\u6bd4\uff0c\u800c\u4e14\u771f\u6c14\u4e5f\u6709\u4e9b\u63d0\u4e0d\u4e0a\u6765\u4e86\uff0c\u539f\u6765\u4ed6\u65b9 \u624d\u4e2d\u4e86\u8f9b\u6377\u7684\u90a3\u4e00\u638c\uff0c\u6b64\u523b\u65b9\u81ea\u53d1\u4f5c\uff0c\u5c24\u5176\u5728\u4ed6\u53d7\u4f24\u4e4b\u540e\uff0c\u53c8\u63d0\u6c14\u72c2\u5954\u4e86\u8fd9\u4e48\u4e45\uff0c\u4f24\u52bf \u66f4\u5f62\u4e25\u91cd\u3002 \u3000\u3000\u4ed6\u56de\u5934\u4e00\u671b\uff0c\u5510\u95e8\u4e2d\u4eba\u5df2\u4e0d\u518d\u8ffd\u6765\uff0c\u4fbf\u5bfb\u5f97\u4e00\u5757\u8f83\u4e3a\u9690\u50fb\u7684\u5730\u65b9\uff0c\u5c06\u606f\u4e86\u534a\u664c\uff0c\u8fd0 \u4e00\u8fd0\u6c14\uff0c\u56db\u80a2\u767e\u9ab8\u597d\u50cf\u8981\u6563\u4e86\u4e00\u6837\uff0c\u4e0d\u7531\u60ca\u5fd6\u9053\uff1a\u201c\u8fd9\u59d3\u8f9b\u7684\u5c0f\u5b50\uff0c\u638c\u529b\u5c45\u7136\u6041\u5730\u5389 \u5bb3\u3002\u201d \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u79cd\u5185\u5bb6\u9ad8\u624b\u7684\u638c\u529b\uff0c\u82e5\u4e0d\u8d76\u5feb\u533b\u6cbb\uff0c\u53ea\u6015\u6c38\u8fdc\u4e5f\u6ca1\u6709\u529e\u6cd5\u6cbb\u4e86\uff0c\u60f6\u6025\u4e4b\u4e0b\uff0c \u4e5f\u7ed9\u4ed6\u60f3\u5230\u5999\u624b\u795e\u533b\u5362\u9535\u6b64\u4eba\uff0c\u4fbf\u4e5f\u517c\u7a0b\u8d76\u5230\u4eac\u5c71\u6c42\u533b\uff0c\u90a3\u77e5\u5999\u624b\u795e\u533b\u542c\u4e86\u91d1\u6b39\u7684\u540d\u5b57 \u8bf4\u4ec0\u4e48\u4e5f\u4e0d\u80af\u66ff\u4ed6\u533b\u6cbb\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u81ea\u662f\u5927\u6012\uff0c\u4fbf\u548c\u5999\u624b\u795e\u533b\u52a8\u8d77\u624b\u6765\uff0c\u4ed6\u867d\u7136\u8eab\u53d7\u5185\u4f24\uff0c\u4f46\u662f\u795e\u533b\u5e90\u94fa\u4ecd\u4e0d \u662f\u4ed6\u7684\u5bf9\u624b\uff0c\u4e09\u4e94\u62db\u4e4b\u4e0b\uff0c\u5c31\u88ab\u4ed6\u70b9\u4e2d\u8111\u95e8\u6b63\u4e2d\u7684\u8981\u7a74\u201c\u767e\u4f1a\u201d\uff0c\u88ab\u62b1\u7740\u5750\u5230\u5e8a\u4e0a\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u5a01\u80c1\u5229\u8bf1\uff0c\u5362\u94fa\u5374\u4ecd\u65e0\u52a8\u4e8e\u8877\uff0c\u5782\u76ee\u9759\u5750\uff0c\u4e00\u53e5\u8bdd\u4e5f\u4e0d\u54cd\uff0c\u91d1\u6b39\u66b4\u8df3\u5982\u96f7\uff0c \u4ed6\u5374\u89c6\u4e3a\u4e0d\u89c1\u3002 \u3000\u3000\u90a3\u77e5\u201c\u5d06\u5cd2\u4e09\u7edd\u5251\u201d\u5374\u53c8\u95ef\u4e86\u8ff8\u6765\uff0c\u5929\u9b54\u91d1\u6b39\u6697\u6697\u53eb\u82e6\uff0c\u4ed6\u77e5\u9053\u6b64\u523b\u81ea\u5df1\u7edd\u975e\u5d06\u5cd2 \u4e09\u7edd\u5251\u7684\u654c\u624b\u3002 \u3000\u3000\u82e5\u662f\u4e07\u4e00\u52a8\u4e86\u624b\uff0c\u81ea\u5df1\u5185\u4f24\u52bf\u5fc5\u53c8\u8981\u52a0\u5267\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u65b9\u624d\u4e09\u8a00\u4e24\u8bed\u4fbf\u5c06\u4e8e\u4e00\u98de\u5413\u8d70\uff0c\u5fc3\u91cc\u6697\u5730\u5f97\u610f\u3002 \u3000\u3000\u4f46\u662f\u770b\u5230\u5999\u624b\u795e\u533b\u8bf4\u4ec0\u4e48\u4e5f\u4e0d\u66ff\u4ed6\u533b\u6cbb\uff0c\u53c8\u89c9\u5f97\u60f6\u6025\u3002\u82e5\u662f\u666e\u901a\u5185\u4f24\uff0c\u4ed6\u81ea\u5df1\u4e5f\u53ef \u533b\u5f97\uff0c\u4f46\u6b64\u65f6\u4ed6\u6240\u8eab\u4e2d\u7684\u4e00\u638c\uff0c\u5a01\u529b\u53c8\u4f55\u6b62\u6bd4\u666e\u901a\u7684\u638c\u529b\u6df1\u4e86\u4e00\u500d\uff0c\u662f\u4ee5\u7edd\u975e\u666e\u901a\u533b\u836f \u53ef\u4ee5\u6cbb\u5f97\u7684\u3002 \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u63a0\u5230\u5899\u5916\uff0c\u5bf9\u8bf8\u845b\u660e\u9053\uff1a\u201c\u90a3\u53ae\u7adf\u662f\u5929\u9b54\u91d1\u6b39\uff0c\u5e08\u5144\uff0c\u4f60\u8bf4\u8be5\u600e\u4e48 \u529e\uff1f\u201d \u3000\u3000\u5929\u7edd\u5251\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u9053\uff1a\u201c\u8fd9\u5929\u9b54\u91d1\u6b39\u8dd1\u5230\u8fd9\u91cc\u6765\u627e\u5999\u624b\u795e\u533b\uff0c\u60f3\u5fc5\u662f\u81ea\u5df1\u53d7\u4e86 \u4f24\u3002\u201d \u3000\u3000\u4ed6\u987f\u4e86\u987f\uff0c\u53c8\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u6211\u4eec\u5c31\u5c06\u5e08\u59b9\u7559\u5728\u7af9\u6797\u91cc\uff0c\u4f60\u6211\u5144\u5f1f\u518d\u8fdb\u53bb\u770b\u770b\uff0c\u6211\u4e0d \u76f8\u4fe1\u4ed6\u4e5f\u662f\u4e2a\u4eba\uff0c\u51ed\u6211\u4eec\u5e08\u5144\u5f1f\u4e8c\u4eba\u8fd8\u5e94\u4ed8\u4e0d\u6765\u5417\uff01\u201d\u4e8e\u4e00\u98de\u81ea\u662f\u8d5e\u540c\uff0c\u4fbf\u5c06\u82cf\u6620\u96ea\u4fa7 \u501a\u5728\u4e00\u6839\u5de8\u7af9\u4e0a\u3002 \u3000\u3000\u5929\u7edd\u5251\u53f3\u624b\u5fae\u626c\uff0c\u505a\u4e86\u4e2a\u624b\u5f0f\uff0c\u4e24\u4eba\u4fbf\u63a0\u56de\u9662\u4e2d\uff0c\u4ece\u652f\u7740\u7684\u7a97\u53e3\u91cc\u4e00\u770b\uff0c\u53ea\u89c1\u5929\u9b54 \u91d1\u6b39\u6b63\u5728\u501a\u6848\u6c89\u601d\u7740\u3002 \u3000\u3000\u5929\u7edd\u5251\u4e00\u626c\u624b\uff0c\u55d6\u5730\u6253\u51fa\u4e00\u5757\u98de\u8757\u77f3\u3002 \u3000\u3000\u5d06\u5cd2\u5c71\u4e3a\u4e94\u5927\u5251\u6d3e\u4e4b\u4e00\uff0c\u5251\u795e\u5389\u9e57\u4e5f\u4e0d\u559c\u7528\u6697\u5668\uff0c\u662f\u4ee5\u5d06\u5cd2\u95e8\u4eba\uff0c\u4f1a\u6253\u6697\u5668\u7684\uff0c\u53ef \u8bf4\u662f\u5c11\u4e4b\u53c8\u5c11\uff0c\u6240\u7528\u7684\u6697\u5668\uff0c\u4e5f\u5927\u591a\u53ea\u662f\u98de\u8757\u77f3\u4e00\u79cd\uff0c\u8fd9\u5c31\u662f\u540d\u95e8\u6b63\u5b97\u7684\u81ea\u6043\u8eab\u4efd\u4e4b\u5904\u3002 \u3000\u3000\u98de\u8757\u77f3\u53ea\u4e0d\u8fc7\u6b66\u6797\u4e2d\u6700\u666e\u901a\u7684\u6697\u5668\u800c\u5df2\uff0c\u7109\u80fd\u6253\u5f97\u4e2d\u8fd9\u5927\u884c\u5bb6\u5929\u9b54\u91d1\u6b39\uff0c\u4ed6\u5fae\u4e00\u6325 \u624b\uff0c\u5c31\u5c06\u8fd9\u98de\u8757\u77f3\u6325\u51fa\u5f88\u8fdc\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u5374\u5e76\u672a\u79fb\u52a8\u8eab\u4f53\uff0c\u539f\u6765\u4ed6\u6b64\u523b\u80f8\u8179\u4e4b\u95f4\u89c9\u5f97\u975e\u5e38\u96be\u53d7\uff0c\u800c\u4e14\u8fd8\u5e26\u7740\u4e9b\u8bb8\u7a92\u606f \u7684\u611f\u89c9\u3002 \u3000\u3000\u5929\u7edd\u5251\u8bf8\u845b\u660e\u53d1\u51fa\u8fd9\u5757\u98de\u8757\u77f3\uff0c\u672c\u672a\u5e0c\u671b\u5b83\u80fd\u6253\u4e2d\u91d1\u6b39\u662f\u4ee5\u5e76\u4e0d\u5947\u602a\uff0c\u4f46\u662f\u4ed6\u53d1\u51fa \u6b64\u77f3\u7684\u7528\u610f\uff0c\u662f\u60f3\u60ca\u52a8\u91d1\u6b39\uff0c\u8ba9\u91d1\u6b39\u63a0\u51fa\u7a97\u6765\uff0c\u6b64\u523b\u89c1\u4ed6\u6beb\u65e0\u884c\u52a8\uff0c\u5374\u4e0d\u7981\u89c9\u5f97\u6709\u4e9b\u5947 \u602a\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u5fc3\u4e2d\u5ffd\u7136\u4e00\u52a8\uff0c\u6084\u58f0\u5411\u8bf8\u845b\u660e\u8bf4\u9053\uff1a\u201c\u8fd9\u9b54\u5934\u65e2\u6765\u5bfb\u8bbf\u5999\u624b\u795e\u533b\uff0c\u60f3\u5fc5\u662f\u4ed6 \u4e5f\u53d7\u4e86\u91cd\u4f24\uff0c\u6b64\u523b\u8fde\u52a8\u90fd\u4e0d\u80fd\u52a8\u4e86\uff0c\u6211\u4eec\u82e5\u60f3\u51fb\u8d25\u8fd9\u9b54\u5934\uff0c\u6b64\u65f6\u6b63\u662f\u5927\u597d\u7684\u673a\u4f1a\uff0c\u5e08\u5144 \u4f60\u7684\u610f\u601d\u5982\u4f55\uff1f\u201d \u3000\u3000\u8bf8\u845b\u660e\u6c89\u541f\u4e86\u534a\u54cd\uff0c\u9053\uff1a\u201c\u770b\u6765\u6211\u4eec\u4eca\u5929\u975e\u52a8\u624b\u4e0d\u53ef\u4e86\uff0c\u65e0\u8bba\u4ed6\u53d7\u4f24\u6ca1\u6709\u90fd\u662f\u4e00\u6837\uff0c \u4f46\u662f\u2026\u2026\u201d \u3000\u3000\u201c\u8fd8\u6709\u4ec0\u4e48\uff1f\u201d\u4e8e\u4e00\u98de\u95ee\u9053\u3002 \u3000\u3000\u201c\u4f46\u662f\u6211\u4eec\u82e5\u8fdb\u623f\u5b50\u52a8\u624b\uff0c\u6015\u4f1a\u5f15\u8d77\u5999\u624b\u795e\u533b\u7684\u4e0d\u5feb\uff0c\u53cd\u800c\u4e0d\u80af\u66ff\u5e08\u59b9\u6cbb\u4f24\uff0c\u90a3\u5c82 \u4e0d\u662f\u66f4\u69fd\u3002\u201d \u3000\u3000\u8bf8\u845b\u660e\u8fd9\u6837\u4e00\u8bf4\uff0c\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4e5f\u89c9\u5f97\u6709\u7406\uff0c\u4ed6\u867d\u7136\u4e0d\u8ba4\u5f97\u8fd9\u5999\u624b\u795e\u533b\uff0c\u4f46\u662f\u6709\u5173 \u4ed6\u53e4\u602a\u813e\u6c14\u7684\u4f20\u8bf4\uff0c\u4e8e\u4e00\u98de\u4e5f\u66fe\u542c\u8fc7\u4e0d\u5c11\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u6c89\u541f\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u8be5\u600e\u4e48\u529e\u5462\uff1f\u201d\u5ffd\u7136\u4ed6\u7740\u6025\u5730\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5c06\u5e08\u59b9\u4e00\u4eba \u7559\u5728\u7af9\u6797\u91cc\u9762\uff0c\u662f\u4e0d\u662f\u592a\u5371\u9669\u4e86\u5440\uff01\u201d \u3000\u3000\u4ed6\u4e00\u5fc3\u5173\u6ce8\u7740\u82cf\u6620\u96ea\u7684\u5b89\u5371\uff0c\u8bf8\u845b\u660e\u542c\u4e86\u5fc3\u91cc\u4e0d\u514d\u6cdb\u8d77\u4e00\u9635\u9178\u610f\uff0c\u6545\u610f\u505a\u51fa\u4e0d\u5728\u4e4e \u7684\u6837\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u5427\uff01\u201d\u53c8\u6362\u4e86\u4e00\u79cd\u5c16\u523b\u7684\u8bed\u8c03\u9053\uff1a\u201c\u4f60\u8981\u662f\u4e0d\u653e\u5fc3\uff0c \u51fa\u53bb\u770b\u770b\u4e5f\u597d\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u6697\u54fc\u4e86\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u548c\u6211\u88c5\u4ec0\u4e48\u849c\u3002\u201d\u53e3\u4e2d\u5374\u8bf4\uff1a\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u5e08\u5144\u5c31 \u8bf7\u5728\u8fd9\u91cc\u5f85\u673a\u800c\u52a8\u597d\u4e86\uff0c\u6211\u51fa\u53bb\u770b\u770b\u5e08\u59b9\u3002\u201d \u3000\u3000\u968f\u7740\uff0c\u4ed6\u5c31\u63a0\u51fa\u5899\u53bb\u3002 \u3000\u3000\u5929\u7edd\u5251\u8bf8\u845b\u660e\u53c8\u7acb\u523b\u5f00\u59cb\u540e\u6094\uff0c\u4e0d\u8be5\u8ba9\u4e8e\u4e00\u98de\u548c\u82cf\u6620\u96ea\u5355\u72ec\u76f8\u5904\uff0c\u4ed6\u548c\u4e8e\u4e00\u98de\u52fe\u5fc3 \u6597\u89d2\u5730\u60f3\u535a\u53d6\u82cf\u6620\u96ea\u7684\u6b22\u5fc3\uff0c\u90a3\u77e5\u82cf\u6620\u96ea\u5374\u6839\u672c\u6ca1\u6709\u5c06\u4ed6\u4eec\u653e\u5728\u5fc3\u4e0a\uff0c\u751a\u81f3\u8fd8\u6709\u4e9b\u8ba8\u538c \u4ed6\u4eec\u3002 \u3000\u3000\u8fd9\u5c31\u662f\u5973\u5b69\u5b50\u4eec\u7684\u5fae\u5999\u5fc3\u7406\uff0c\u4f60\u6108\u662f\u9732\u9aa8\u5730\u5411\u5979\u4eec\u8868\u793a\u7231\u610f\uff0c\u5979\u4eec\u53cd\u4f1a\u89c9\u5f97\u4f60\u65e0\u8db3 \u8f7b\u91cd\uff0c\u7eb5\u4f7f\u5979\u4e5f\u662f\u559c\u6b22\u7740\u4f60\u7684\u3002 \u3000\u3000\u5929\u9b54\u91d1\u6b39\u6b64\u523b\u6e10\u89c9\u4e0d\u5999\uff0c\u771f\u6c14\u5927\u6709\u53cd\u9006\u800c\u4e0a\u4e4b\u52bf\uff0c\u4ed6\u770b\u4e86\u5750\u5728\u69bb\u4e0a\u7684\u5999\u624b\u795e\u533b\u4e00\u773c\uff0c \u77e5\u9053\u8981\u60f3\u4ed6\u4e3a\u81ea\u5df1\u6cbb\u4f24\uff0c\u53ea\u6015\u5df2\u662f\u65e0\u671b\uff0c\u518d\u52a0\u4e0a\u201c\u5d06\u5cd2\u4e09\u7edd\u5251\u201d\u5bf9\u81ea\u5df1\u4e5f\u5728\u864e\u89c6\u803d\u7708\u3002 \u3000\u3000\u4ed6\u5fc3\u6bd2\u624b\u8fa3\uff0c\u505a\u4e8b\u53ea\u6c42\u8fbe\u5230\u76ee\u7684\uff0c\u4ece\u6765\u4e0d\u8ba1\u624b\u6bb5\uff0c\u8bd5\u60f3\u4ed6\u8fde\u81ea\u5df1\u7684\u4eb2\u751f\u7236\u6bcd\u90fd\u80fd\u6740 \u6b7b\uff0c\u5bf9\u522b\u4eba\u7684\u6027\u547d\u770b\u5f97\u66f4\u662f\u4e0d\u8db3\u9053\u4e86\u3002 \u3000\u3000\u6b64\u523b\u4ed6\u6740\u673a\u53c8\u8d77\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u53ae\u65e2\u4e0d\u80af\u66ff\u6211\u6cbb\u4f24\uff0c\u6211\u4e5f\u53eb\u4ed6\u6c38\u8fdc\u4e0d\u80fd\u66ff\u522b\u4eba\u6cbb\u4f24\u3002\u201d \u3000\u3000\u4ed6\u5634\u89d2\u6cdb\u8d77\u51f6\u9669\u7684\u51b7\u7b11\uff0c\u60f3\u5230\u5d06\u5cd2\u4e09\u7edd\u5251\u6b64\u6765\u7684\u76ee\u7684\u4e5f\u4e0d\u80fd\u8fbe\u5230\uff0c\u53c8\u60f3\u5230\u6b64\u540e\u6b66\u6797 \u4e2d\u53d7\u4e86\u91cd\u4f24\u7684\u4eba\u90fd\u65e0\u4eba\u533b\u6cbb\uff0c\u5fc3\u4e2d\u5f97\u610f\u5df2\u6781\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u505a\u7684\u4e8b\uff0c\u90fd\u662f\u80fd\u5f71\u54cd\u5230\u8fd9\u4e48\u591a \u4eba\u7684\u2026\u2026\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u5fcd\u7740\u75bc\u75db\uff0c\u7eb5\u4e86\u8d77\u6765\uff0c\u6781\u5feb\u5730\u63a0\u5230\u584c\u524d\uff0c\u201c\u62cd\u201d\u7684\u4e00\u638c\uff0c\u51fb\u5411\u5999\u624b\u795e\u533b\u8111\u95e8\u3002 \u3000\u3000\u7136\u540e\u4ed6\u6beb\u4e0d\u505c\u7559\uff0c\u4ece\u53e6\u4e00\u8fb9\u7a97\u6237\u63a0\u4e86\u51fa\u6765\uff0c\u6d88\u5931\u5728\u8fdc\u65b9\u3002 \u3000\u3000\u5929\u7edd\u5251\u5728\u7a97\u53e3\u53ea\u80fd\u770b\u5230\u91d1\u6b39\u4e00\u4eba\uff0c\u5374\u770b\u4e0d\u5230\u5750\u5728\u5e8a\u4e0a\u5999\u624b\u795e\u533b\uff0c\u6b64\u523b\u4ed6\u89c1\u91d1\u6b39\u7a81\u7136 \u8d70\u4e86\uff0c\u5fc3\u4e2d\u5927\u611f\u5947\u602a\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u518d\u4e5f\u4e0d\u8003\u8651\uff0c\u4fbf\u63a0\u8fdb\u7a97\u53bb\uff0c\u4e00\u773c\u770b\u5230\u5012\u5728\u5e8a\u4e0a\u7684\u5999\u624b\u795e\u533b\uff0c\u7eb5\u4e86\u8fc7\u53bb\uff0c\u60ca\u614c \u5730\u95ee\u9053\uff1a\u201c\u5362\u8001\u5148\u751f\uff0c\u4f60\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u5999\u624b\u795e\u533b\u8870\u5f31\u5730\u5f20\u5f00\u773c\u775b\uff0c\u773c\u4e2d\u7684\u795e\u5149\u4e5f\u6563\u4e86\uff0c\u6323\u624e\u7740\u8bf4\u9053\uff1a\u201c\u4f60\u5c06\u6709\u8fb9\u67b6\u4e0a\u7684\u7b2c \u4e09\u4e2a\u7eff\u8272\u74f6\u5b50\u62ff\u6765\uff0c\u5feb\u5feb\u3002\u201d \u3000\u3000\u539f\u672c\u91d1\u6b39\u65b9\u624d\u62cd\u5411\u4ed6\u8111\u95e8\u7684\u4e00\u638c\uff0c\u867d\u7136\u4f7f\u4ed6\u53d7\u4e86\u81f4\u547d\u4e4b\u4f24\uff0c\u5374\u6070\u597d\u66ff\u4ed6\u89e3\u5f00\u4e86\u7a74\u9053\uff0c \u662f\u4ee5\u4ed6\u73b0\u5728\u80fd\u51fa\u58f0\u8bf4\u8bdd\uff0c\u56db\u80a2\u4e5f\u80fd\u8f6c\u52a8\u3002 \u3000\u3000\u5929\u7edd\u5251\u8bf8\u845b\u660e\u8fde\u5fd9\u8d70\u5230\u53f3\u8fb9\u7684\u4e00\u4e2a\u6a80\u6728\u67b6\u4e0a\uff0c\u4f9d\u8a00\u53d6\u8fc7\u4e86\u90a3\u53ea\u5236\u4f5c\u5f62\u5f0f\u751a\u53e4\u7684\u7eff\u7389 \u74f6\u5b50\u3002 \u3000\u3000\u5999\u624b\u795e\u533b\u53c8\u6025\u9053\uff1a\u201c\u5012\u51fa\u4e09\u7c92\u6765\uff0c\u653e\u5728\u6211\u5634\u91cc\u3002\u201d \u3000\u3000\u8bf8\u845b\u660e\u62d4\u5f00\u74f6\u76d6\uff0c\u5012\u51fa\u4e09\u7c92\u6e05\u9999\u7684\u836f\u4e38\uff0c\u4ed6\u6697\u5fd6\u9053\uff1a\u201c\u60f3\u6765\u8fd9\u4e2a\u5fc5\u5b9a\u5c31\u662f\u4e13\u6cbb\u5185\u4f24 \u7684\u7075\u836f\u2018\u8ffd\u9b42\u4e38\u2019\u4e86\u3002\u201d \u3000\u3000\u539f\u6765\u5999\u624b\u795e\u533b\u5362\u9535\u7684\u201c\u8ffd\u9b42\u4e38\u201d\uff0c\u4e3a\u4e13\u6cbb\u5185\u5bb6\u638c\u4f24\u7684\u5723\u836f\uff0c\u6b66\u6797\u4e2d\u4eba\u591a\u534a\u77e5\u9053\uff0c\u4f46 \u662f\u5999\u624b\u795e\u533b\u56fa\u6b65\u81ea\u5c01\uff0c\u8f7b\u6613\u4e0d\u4ee5\u4e4b\u793a\u4eba\u3002 \u3000\u3000\u4e8e\u662f\u8bf8\u845b\u660e\u5c06\u5012\u51fa\u7684\u4e09\u7c92\u201c\u8ffd\u9b42\u4e38\u201d\u653e\u4eba\u5999\u624b\u795e\u533b\u7684\u53e3\u4e2d\u540e\uff0c\u4fbf\u6084\u6084\u5730\u5c06\u90a3\u74f6\u5b50\u6536 \u8fdb\u6000\u91cc\u3002 \u3000\u3000\u5999\u624b\u795e\u533b\u5c06\u90a3\u4e09\u7c92\u836f\u4e38\u54bd\u4e0b\u540e\uff0c\u795e\u8272\u4f3c\u4e4e\u7a0d\u89c1\u597d\u8f6c\uff0c\u6323\u624e\u7740\u5750\u4e86\u8d77\u6765\uff0c\u95ed\u76ee\u517b\u4e86\u4e00 \u4f1a\u795e\u957f\u53f9\u4e00\u58f0\uff0c\u7741\u5f00\u773c\u6765\u3002 \u3000\u3000\u8bf8\u845b\u660e\u8d76\u7d27\u95ee\u9053\uff1a\u201c\u5362\u8001\u5148\u751f\u597d\u4e9b\u4e86\u5417\uff1f\u201d \u3000\u3000\u5999\u624b\u795e\u533b\u6447\u5934\u53f9\u9053\uff1a\u201c\u5929\u9b54\u91d1\u6b39\u679c\u771f\u540d\u4e0d\u865a\u4f20\uff0c\u53d7\u4e86\u91cd\u4f24\u540e\uff0c\u4ecd\u6709\u5982\u6b64\u638c\u529b\u3002\u201d\u4ed6 \u5598\u4e86\u4e00\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u6211\u8111\u6d77\u547d\u95e8\u4e2d\u4e86\u4ed6\u4e00\u638c\uff0c\u6b64\u523b\u5c31\u7b97\u662f\u5927\u7f57\u795e\u4ed9\uff0c\u4e5f\u6551\u4e0d\u4e86\u6211\u7684\u547d \u4e86\u3002\u201d \u3000\u3000\u8bf8\u845b\u660e\u5b89\u6170\u5730\u8bf4\u9053\uff1a\u201c\u4e0d\u4f1a\u5427\u2026\u2026\u201d \u3000\u3000\u5999\u624b\u795e\u533b\u7a81\u7136\u6012\u9053\uff1a\u201c\u4ec0\u4e48\u4e0d\u4f1a\uff0c\u6211\u96be\u9053\u6ca1\u6709\u4f60\u77e5\u9053\u3002\u201d\u4ed6\u8fd9\u4e00\u53d1\u6012\uff0c\u7acb\u523b\u66f4\u884c\u4e0d \u652f\uff0c\u731b\u70c8\u5730\u54b3\u55fd\u4e86\u8bb8\u4e45\uff0c\u65ad\u7eed\u5730\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u2026\u2026\u4e0d\u884c\u4e86\uff0c\u5509\uff01\u53ea\u53ef\u60dc\u6211\u7684\u533b\u672f\uff0c \u6ca1\u6709\u2026\u2026\u521a\u8bf4\u5230\u201c\u6709\u201d\u5b57\uff0c\u4ed6\u4fe9\u773c\u4e00\u7ffb\uff0c\u7acb\u65f6\u6c14\u7edd\u3002 \u3000\u3000\u987b\u77e5\u8111\u6d77\u5929\u7075\u4e0a\u5982\u679c\u7a0d\u52a0\u51fb\u6253\uff0c\u4fbf\u4f1a\u6655\u7729\uff0c\u4f55\u51b5\u662f\u5929\u9b54\u91d1\u6b39\u8fd9\u79cd\u6df1\u539a\u7684\u5185\u5bb6\u638c\u529b\uff0c \u5999\u624b\u795e\u533b\u80fd\u652f\u6301\u8fd9\u7247\u523b\uff0c\u4e0d\u8eab\u8fc7\u662f\u9760\u4e86\u4ed6\u5e73\u65e5\u5bf9\u8eab\u4f53\u8c03\u7406\u5f97\u5f53\uff0c\u5185\u529f\u53c8\u9887\u5177\u706b\u5019\uff0c\u548c\u4e09 \u7c92\u201c\u8ffd\u9b42\u4e38\u201d\u7684\u529f\u6548\u7f62\u4e86\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u6b7b\uff0c\u5929\u7edd\u5251\u4e0d\u7981\u614c\u4e86\u624b\u811a\uff0c\u6697\u5fd6\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u8dd1\u6765\u5374\u4e3a\u4ed6\u9001\u7ec8\u4e86\uff0c\u771f\u662f\u5012 \u9709\u3002\u201d \u3000\u3000\u5929\u7edd\u5251\u8bf8\u845b\u660e\u5929\u6027\u6781\u8584\uff0c\u89c1\u4e86\u5999\u624b\u795e\u533b\u7684\u6b7b\u72b6\uff0c\u4e00\u4e1d\u6ca1\u6709\u540c\u60c5\u6216\u60b2\u54c0\u7684\u610f\u601d\uff0c\u53cd\u89c9 \u5f97\u81ea\u5df1\u5012\u9709\u3002\u8fd9\u65f6\u5c4b\u5916\u6709\u51e0\u58f0\u8f7b\u5fae\u7684\u6307\u7532\u76f8\u51fb\u4e4b\u58f0\uff0c\u8fd9\u662f\u6b66\u6797\u4e2d\u540c\u9053\u4f20\u9012\u6d88\u606f\u7684\u65b9\u6cd5\uff0c \u8bf8\u845b\u660e\u4e00\u542c\uff0c\u4fbf\u77e5\u662f\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u53eb\u4ed6\u7acb\u523b\u8d76\u53bb\u7684\u4fe1\u53f7\u3002 \u3000\u3000\u4ed6\u62ac\u773c\u4e00\u626b\uff0c\u53f3\u4fa7\u67b6\u4e0a\u8fd8\u6401\u7740\u51e0\u4e2a\u7eff\u7389\u74f6\u5b50\uff0c\u4fbf\u7a9c\u4e86\u51fa\u53bb\u60f3\u62ff\u8d70\uff0c\u5ffd\u53c8\u60f3\u5230\uff1a\u201c\u5373 \u4f7f\u62ff\u53bb\u8fd9\u4e9b\u74f6\u5b50\uff0c\u4f46\u662f\u6211\u4e0d\u77e5\u9053\u7528\u6cd5\u5c82\u4e0d\u6789\u7136\u3002\u201d\u4e8e\u662f\u4ed6\u53c8\u7f29\u4f4f\u4e86\u624b\uff0c\u811a\u8ddf\u5fae\u987f\uff0c\u63a0\u51fa \u5c4b\u53bb\u3002 \u3000\u3000\u4ed6\u521a\u63a0\u8fc7\u90a3\u9752\u7af9\u7f16\u6210\u7684\u77ed\u5899\uff0c\u5fc3\u4e2d\u4fbf\u662f\u4e00\u60ca\uff0c\u539f\u6765\u5899\u5916\u7af9\u6797\u4fa7\u7684\u4e00\u5c0f\u5757\u7a7a\u5730\u4e0a\uff0c\u9664 \u4e86\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u548c\u53d7\u4e86\u4f24\u7684\u4eba\u7edd\u5251\u82cf\u6620\u96ea\u5916\uff0c\u8fd8\u7ad9\u7740\u4e09\u4e2a\uff0c\u4e24\u4e2a\u4eba\u7a7f\u7740\u84dd\u5e03\u9053\u888d\uff0c\u53e6\u4e00 \u4e2a\u9760\u5728\u4ed6\u4eec\u8eab\u4e0a\u7684\uff0c\u5374\u662f\u4fd7\u5bb6\u88c5\u675f\uff0c\u50cf\u662f\u4e5f\u53d7\u4e86\u4f24\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u6781\u5feb\u7684\u98de\u8dc3\u5230\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u7684\u8eab\u4fa7\uff0c\u62ac\u76ee\u4e00\u770b\uff0c\u5bf9\u65b9\u5374\u539f\u6765\u662f\u6b66\u5f53\u6d3e\u7684\u51cc\u98ce \u9053\u4eba\u548c\u53e6\u4e00\u4e2a\u4e5d\u5927\u5f1f\u5b50\u4e2d\u7684\u9053\u4eba\u3002 \u3000\u3000\u90a3\u53d7\u4e86\u4f24\u7684\uff0c\u5c31\u662f\u795e\u9e64\u8a79\u5e73\u3002 \u3000\u3000\u539f\u6765\u795e\u9e64\u8a79\u5e73\u6240\u4e2d\u4e8e\u4e00\u98de\u7684\u90a3\u4e00\u638c\uff0c\u4f24\u52bf\u4ea6\u6781\u91cd\uff0c\u867d\u7136\u5728\u6b66\u5f53\u5c71\u4e0a\u8c03\u606f\u4e86\u8bb8\u4e45\uff0c\u5403 \u4e86\u8bb8\u591a\u4e39\u836f\uff0c\u4f46\u662f\u4f24\u52bf\u4ea6\u672b\u89c1\u8d77\u8272\uff0c\u4e8e\u662f\u4ed6\u4eec\u4fbf\u4e5f\u60f3\u5230\u8fd9\u4ee5\u533b\u9053\u95fb\u540d\u5929\u4e0b\u7684\u5999\u624b\u795e\u533b\u5362 \u9535\uff0c\u4e5f\u8d76\u6765\u6c42\u6cbb\u3002 \u3000\u3000\u6b64\u523b\u53cc\u53cc\u65b9\u78b0\u9762\uff0c\u5fc3\u4e2d\u5404\u6000\u6028\u6bd2\uff0c\u5927\u5bb6\u5fc3\u7167\u4e0d\u5ba3\uff0c\u90fd\u77e5\u9053\u5bf9\u65b9\u662f\u8d76\u6765\u6c42\u5999\u624b\u795e\u533b\u6cbb \u4f24\u7684\u3002 \u3000\u3000\u53cc\u65b9\u4e92\u76f8\u51dd\u89c6\u4e86\u8bb8\u4e45\uff0c\u51cc\u98ce\u9053\u4eba\u4e00\u8a00\u4e0d\u53d1\uff0c\u63ba\u7740\u795e\u9e64\u8a79\u5e73\u5411\u5999\u624b\u795e\u533b\u6240\u5c45\u7684\u8349\u5e90\u91cc \u8d70\u53bb\u3002 \u3000\u3000\u5929\u7edd\u5251\u8bf8\u845b\u660e\u5fd9\u8f7b\u58f0\u9053\uff1a\u201c\u6211\u4eec\u5feb\u8d70\u3002\u201d\u4e8e\u4e00\u98de\u89c1\u4ed6\u9762\u8272\u51dd\u91cd\uff0c\u77e5\u9053\u5b9a\u6709\u4e8b\u6545\u53d1\u751f\uff0c \u4fbf\u4e5f\u5306\u5306\u5730\u6276\u7740\u4eba\u7edd\u5251\u82cf\u6620\u96ea\uff0c\u7a7f\u8fc7\u7af9\u6797\u3002 \u3000\u3000\u4ed6\u611f\u5230\u82cf\u6620\u96ea\u547c\u5438\u91cd\u6d4a\u4e86\uff0c\u4e0a\u6c14\u4e5f\u6e10\u6e10\u63a5\u4e0d\u7740\u4e0b\u6c14\uff0c\u4e0d\u7981\u6025\u5730\u95ee\u9053\uff1a\u201c\u5e08\u59b9\u7684\u4f24\u600e \u4e48\u529e\uff1f\u201d \u3000\u3000\u8bf8\u845b\u660e\u9053\uff1a\u201c\u4e0d\u8981\u7d27\u3002\u201d\u4ed6\u5f97\u610f\u5730\u8bf4\u9053\uff0c\u201c\u6211\u5df2\u5c06\u5999\u624b\u795e\u533b\u7684\u2018\u8ffd\u9b42\u4e38\u2019\u62ff\u4e86\u4e00\u74f6 \u51fa\u6765\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u6ee1\u8179\u72d0\u7591\u6697\u5fd6\uff1a\u201c\u8fd9\u5999\u624b\u795e\u533b\u600e\u5730\u7a81\u7136\u5927\u65b9\u8d77\u6765\u4e86\uff0c\u5c06\u2018\u8ffd\u9b42\u4e38\u2019\u7ed9\u4e86\u4e00\u74f6 \u7ed9\u4ed6\u3002\u201d \u3000\u3000\u7a81\u5730\uff0c\u4ed6\u60ca\u54df\u4e00\u58f0\uff1a\u201c\u5e08\u59b9\uff01\u201d\u4f38\u624b\u4e00\u63a2\u82cf\u6620\u96ea\u7684\u9f3b\u606f\uff0c\u60ca\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u5e08\u59b9\u7684\u547c \u5438\u597d\u50cf\u505c\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4eec\u5df2\u7a7f\u8fc7\u7af9\u6797\uff0c\u8d70\u5230\u9a6c\u8f66\u65c1\u8fb9\uff0c\u5929\u7edd\u5251\u671b\u4e86\u671b\u8eab\u540e\uff0c\u4ece\u6000\u4e2d\u638f\u51fa\u90a3\u53ea\u7eff\u7389\u74f6\u5b50\uff0c \u9053\uff1a\u201c\u5c06\u8ffd\u9b42\u4e38\u7ed9\u5979\u5403\u4e09\u7c92\u5c31\u4e0d\u59a8\u4e8b\u4e86\u3002\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u7af9\u6797\u4e2d\u7bad\u4e5f\u4f3c\u7684\u7a9c\u51fa\u4e00\u6761\u8eab\u5f71\uff0c\u505c\u5728\u4ed6\u4eec\u8eab\u524d\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\u6bd2\u7684\u2018\u5d06 \u5cd2\u4e09\u7edd\u5251\u2019\uff0c\u5c45\u7136\u5c06\u5999\u624b\u795e\u533b\u90fd\u6740\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u773c\u89d2\u4e00\u7779\u8bf8\u845b\u660e\u624b\u4e0a\u7684\u74f6\u5b50\uff0c\u63a5\u7740\u9053\uff1a\u201c\u8fd8\u5c06\u4eba\u5bb6\u7684\u2018\u8ffd\u9b42\u4e38\u2019\u5077\u4e86\u6765\uff0c\u54fc\uff01\u5929 \u4e0b\u7b2c\u4e00\u5251\u679c\u771f\u8c03\u6559\u5f97\u597d\u5f92\u5f1f\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u542c\u5230\u5999\u624b\u795e\u533b\u5df2\u6b7b\uff0c\u4e5f\u5403\u4e86\u4e00\u60ca\u3002 \u3000\u3000\u5929\u7edd\u5251\u8bf8\u845b\u660e\u4e5f\u51b7\u7b11\u9053\uff1a\u201c\u6b66\u5f53\u6d3e\u7684\u9053\u58eb\u679c\u7136\u5389\u5bb3\uff0c\u4e0d\u5206\u6e05\u7ea2\u7682\u767d\uff0c\u5c31\u80e1\u4e71\u8840\u53e3\u55b7 \u4eba\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u6211\u8840\u53e3\u55b7\u4eba\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u53c8\u5927\u6b65\u4eba\u6797\u4e2d\uff0c\u8bf8\u845b\u660e\u5ffd\u7136\u671b\u4e86\u6ee1\u9762\u6000\u7591\u7684\u4e8e\u4e00\u98de\u4e00\u773c\uff0c\u9053\uff1a\u201c\u5feb\u4e0a\u4e86\u8f66\u518d \u8bf4\u3002\u201d \u3000\u3000\u8f9b\u6377\u77e5\u89c9\u867d\u672a\u5931\uff0c\u4f46\u53e3\u4e0d\u80fd\u8a00\uff0c\u56db\u80a2\u4e0d\u80fd\u52a8\u5f39\uff0c\u88ab\u7f2a\u4e03\u5a18\u631f\u6301\u98de\u884c\uff0c\u53ea\u89c9\u5f97\u98ce\u58f0\u98d2 \u7136\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u6b64\u65f6\u7684\u901f\u5ea6\uff0c\u66f4\u8fdc\u5728\u4ed6\u81ea\u5df1\u65bd\u201c\u6697\u5f71\u6d6e\u9999\u201d\u5230\u4e86\u6781\u5904\u65f6\u90a3\u79cd\u901f\u5ea6\u4e4b\u4e0a\uff0c\u4e8e\u662f \u4ed6\u4e0d\u7981\u6697\u53f9\u6b66\u529f\u7684\u6c38\u65e0\u6b62\u5883\u3002 \u3000\u3000\u4ed6\u968f\u5373\u60f3\u5230\u81ea\u5df1\u7684\u5b89\u5371\uff0c\u6697\u5fd6\uff1a\u201c\u6211\u53c8\u5728\u4ec0\u4e48\u5730\u65b9\u5f97\u7f6a\u4e86\u8fd9\u51e0\u4e2a\u5947\u4eba\uff0c\u4e3a\u4f55\u4ed6\u8981\u82e6 \u82e6\u903c\u7740\u6211\uff1f\u201d \u3000\u3000\u4ed6\u60f3\u53f9\u6c14\uff0c\u4f46\u7adf\u8fde\u6c14\u90fd\u65e0\u6cd5\u53f9\u51fa\u6765\uff0c\u56db\u80a2\u4e5f\u6e10\u9ebb\u75f9\uff0c\u611f\u89c9\u5230\u975e\u4efb\u4f55\u8a00\u8bed\u6240\u80fd\u5f62\u5bb9\u7684 \u96be\u53d7\u3002 \u3000\u3000\u8f9b\u6377\u7b2c\u4e00\u6b21\u5c1d\u5230\u88ab\u4eba\u70b9\u7a74\u7684\u6ecb\u5473\uff0c\u60f6\u6025\u4e4b\u4e2d\uff0c\u8fd8\u5e26\u6709\u6c14\u6124\uff0c\u4ed6\u6124\u6068\u9053\uff1a\u201c\u8fd9\u6b21\u6211\u82e5 \u80fd\u9003\u51fa\u6027\u547d\uff0c\u65e5\u540e\u6211\u4e00\u5b9a\u82e6\u7ec3\u6b66\u529f\uff0c\u8981\u6b64\u4eba\u597d\u770b\u3002\u201d\u4ed6\u88ab\u4eba\u70b9\u4e2d\u7a74\u9053\uff0c\u7adf\u8fde\u4eba\u5bb6\u662f\u7537\u662f \u5973\u90fd\u4e0d\u77e5\u9053\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u9f3b\u7aef\u95fb\u5230\u4e00\u79cd\u6781\u751c\u7f8e\u7684\u9999\u5473\uff0c\u6b63\u662f\u7f2a\u4e03\u5a18\u8eab\u4e0a\u6563\u51fa\u7684\uff0c\u4ed6\u6df1\u6df1\u5438\u4e00\u53e3\uff0c\u6697\u5fd6\uff1a \u201c\u8fd9\u9999\u5473\u7adf\u548c\u9f84\u59b9\u59b9\u8eab\u4e0a\u7684\u5dee\u4e0d\u591a\u201d\u3002 \u3000\u3000\u53c8\u5438\u8fdb\u4e00\u53e3\uff0c\u7a81\u7136\u60f3\u5230\u91d1\u6885\u9f84\uff1a\u201c\u5979\u73b0\u5728\u4e00\u5b9a\u96be\u53d7\u6b7b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u601d\u6742\u4e71\uff0c\u5ffd\u7136\u8033\u7554\u7684\u98ce\u58f0\u987f\u4f4f\uff0c\u5fd9\u6536\u6387\u5fc3\u795e\uff0c\u671d\u56db\u5468\u4e00\u6253\u91cf\uff0c\u89c1\u5904\u8eab\u4e4b\u5730\u53c8\u662f \u4e00\u95f4\u8239\u8231\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e0d\u7981\u6697\u6697\u53eb\u82e6\uff1a\u201c\u600e\u5730\u53c8\u56de\u5230\u6c34\u4e0a\u6765\u4e86\u3002\u201d \u3000\u3000\u7f2a\u4e03\u5a18\u5c06\u8f9b\u6377\u5f80\u5730\u4e0a\u4e00\u629b\uff0c\u8f9b\u6377\u52a8\u4e5f\u4e0d\u80fd\u52a8\uff0c\u53ea\u5f97\u4efb\u5979\u201c\u535f\u201d\u5730\u4e22\u5728\u5730\u4e0a\uff0c\u8dcc\u5f97\u8eab \u4e0a\u9690\u9690\u53d1\u75db\u3002 \u3000\u3000\u539f\u6765\u4ed6\u8fde\u8fd0\u6c14\u90fd\u4e0d\u80fd\uff0c\u6b64\u523b\u9664\u4e86\u5c1a\u672a\u5931\u53bb\u77e5\u89c9\u4e4b\u5916\uff0c\u7b80\u76f4\u5c31\u8ddf\u4e2a\u5e9f\u4eba\u4e00\u6837\uff0c\u6700\u96be\u53d7 \u7684\u662f\u4ed6\u6b64\u523b\u56db\u80a2\u50f5\u786c\uff0c\u65b9\u624d\u4ed6\u662f\u5728\u5954\u8dd1\u65f6\u88ab\u70b9\u4e2d\u7a74\u9053\uff0c\u6b64\u523b\u56db\u80a2\u4ecd\u7136\u662f\u5f2f\u66f2\u7740\u7684\uff0c\u8eba\u5728 \u5730\u4e0a\uff0c\u5f62\u72b6\u6781\u4e3a\u96be\u770b\u3002 \u3000\u3000\u65e0\u6068\u751f\u7a7a\u81ea\u82b1\u4e86\u8bb8\u591a\u529b\u6c14\uff0c\u5728\u957f\u6c5f\u6c5f\u9762\u4e0a\u8dd1\u4e86\u4e24\u8f6c\uff0c\u5c06\u6c5f\u6c34\u51fb\u5f97\u6f2b\u5929\u98de\u821e\uff0c\u4f46\u662f\u8fde \u4eba\u5f71\u90fd\u6ca1\u6709\u627e\u7740\u4e00\u4e2a\uff0c\u53c8\u6c14\u53c8\u6012\uff0c\u5e26\u7740\u5f20\u83c1\u56de\u5230\u81ea\u5df1\u7684\u8239\u4e0a\uff0c\u5374\u89c1\u81ea\u5df1\u8981\u6293\u7684\u4eba\u5df2\u7ecf\u8eba \u5728\u5730\u4e0a\u4e86\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u671d\u4ed6\u7b11\u9053\uff1a\u201c\u5e73\u5e38\u4f60\u603b\u8bf4\u6211\u7b28\uff0c\u8fd9\u6b21\u603b\u8be5\u8f6e\u5230\u6211\u8bf4\u4f60\u4e86\u5427\uff01\u201d \u3000\u3000\u65e0\u6068\u751f\u82e6\u7b11\u9053\uff1a\u201c\u8fd9\u53ae\u5012\u72e1\u733e\u5f97\u5f88\u3002\u201d \u3000\u3000\u5f20\u83c1\u770b\u5230\u201c\u8fd9\u773c\u775b\u5927\u5927\u7684\u5e74\u9752\u4eba\u201d\u53c8\u88ab\u6bcd\u4eb2\u6349\u4e86\u56de\u6765\uff0c\u5fc3\u91cc\u53c8\u60ca\u53c8\u559c\uff0c\u60ca\u7684\u662f\u4e0d\u77e5 \u81ea\u5df1\u7684\u7236\u6bcd\u8981\u600e\u4e48\u5bf9\u4ed8\u4ed6\uff0c\u559c\u7684\u662f\u53c8\u89c1\u7740\u4ed6\u4e86\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u9053\uff1a\u201c\u4f60\u521a\u624d\u95ee\u6e05\u695a\u4e86\u6ca1\u6709\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u9053\uff1a\u201c\u90a3\u624b\u5e15\u679c\u7136\u662f\u4ed6\u7684\uff0c\u4ed6\u81ea\u5df1\u4e5f\u627f\u8ba4\u4e86\u3002\u201d \u3000\u3000\u7f2a\u4e03\u5a18\u6068\u58f0\u9053\uff1a\u201c\u6211\u60f3\u5c06\u4ed6\u5e26\u56de\u5c9b\u4e0a\uff0c\u5230\u4e5d\u59b9\u5893\u524d\uff0c\u518d\u6740\u4e86\u4ed6\u796d\u4e5d\u59b9\uff0c\u8ba9\u4ed6\u77e5\u9053\u8d1f \u5fc3\u7684\u7ed3\u679c\u3002\u201d \u3000\u3000\u5f20\u83c1\u6025\u9053\uff1a\u201c\u600e\u4e48\u6211\u4eec\u53c8\u8981\u56de\u5c9b\u4e0a\u53bb\u5440\u3002\u201d\u5979\u6492\u7740\u5a07\u9053\uff1a\u201c\u6211\u4e0d\u6765\u4e86\uff0c\u7239\u7239\u4e0d\u662f\u7b54 \u5e94\u6211\u5230\u8fd9\u91cc\u6765\u73a9\u4e2a\u75db\u5feb\u5417\uff1f\u73b0\u5728\u4eba\u5bb6\u4ec0\u4e48\u90fd\u6ca1\u6709\u73a9\u5230\uff0c\u600e\u4e48\u5c31\u8981\u56de\u53bb\u4e86\u5462\uff1f\u5c9b\u4e0a\u90a3\u4e48\u5c0f\uff0c \u70e6\u6b7b\u4eba\u4e86\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u7b11\u9053\uff1a\u201c\u4f60\u8bf4\u6211\u4eec\u65e0\u6781\u5c9b\u4e0d\u597d\u73a9\uff0c\u5929\u4e0b\u6b66\u6797\u4e2d\u4eba\u60f3\u5230\u65e0\u6781\u5c9b\u4e0a\u6765\u7684\u4eba\uff0c\u4e0d\u77e5 \u9053\u6709\u51e0\u5343\u51e0\u4e07\u4e2a\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6377\u7a81\u7136\u4e00\u60ca\uff0c\u6697\u5fd6\uff1a\u201c\u539f\u6765\u6b64\u4eba\u5c31\u662f\u65e0\u6781\u5c9b\u4e3b\uff0c\u53ef\u662f\u5929\u6653\u5f97\uff0c\u6211\u53c8\u54ea\u70b9\u5f97\u7f6a\u4e86\u4e1c \u6d77\u4e09\u4ed9\u5440\u3002\u201d \u3000\u3000\u5f20\u83c1\u561f\u8d77\u5634\uff0c\u5a07\u58f0\u8bf4\u9053\uff1a\u201c\u4ed6\u4eec\u8981\u6765\u662f\u4ed6\u4eec\u7684\u4e8b\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u65e0\u6068\u751f\u7709\u5934\u4e00\u76b1\u9053\uff1a\u201c\u4e0d\u8981\u591a\u8bb2\u4e86\uff0c\u4f60\u8981\u5230\u4e2d\u539f\u6765\u73a9\uff0c\u4ee5\u540e\u591a\u7684\u662f\u673a\u4f1a\uff0c\u8fd9\u6b21\u6211\u4eec \u5148\u56de\u53bb\u3002\u201d \u3000\u3000\u5f20\u83c1\u773c\u5708\u4e00\u7ea2\uff0c\u773c\u6cea\u6253\u7740\u8f6c\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u4e00\u628a\u5c06\u5979\u6402\u5728\u6000\u91cc\uff0c\u6e29\u8bed\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u4f60\u6025\u4ec0\u4e48\uff0c\u7239\u7239\u5988\u5988\u603b\u4e0d\u80fd\u4e00\u8f88\u5b50 \u5c06\u4f60\u7559\u5728\u5c9b\u4e0a\u5440\u3002\u201d\u7b11\u4e86\u7b11\uff0c\u53c8\u9053\uff0c\u201c\u4f60\u4ee5\u540e\u603b\u8981\u5ac1\u4eba\u7684\uff0c\u5ac1\u4e86\u4eba\uff0c\u4f60\u5c31\u53ef\u4ee5\u5230\u5904\u53bb\u73a9 \u4e86\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u5f20\u83c1\u7f9e\u5f97\u7ea2\u4e86\u8138\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u5979\u603b\u8bb0\u7740\u8fd9\u8eba\u5728\u5730\u4e0a\u201c\u773c\u775b\u5927\u5927\u7684\u5e74\u9752\u4eba\u201d\u3002\u5979\u60f3\uff1a \u201c\u8981\u662f\u4ee5\u540e\u4ed6\u80fd\u966a\u7740\u6211\u73a9\uff0c\u90a3\u6709\u591a\u597d\u3002\u201d\u518d\u4e00\u60f3\u5230\u201c\u56de\u5230\u5c9b\u4e0a\uff0c\u4ed6\u5c31\u8981\u88ab\u7239\u7239\u5988\u5988\u6740\u6b7b \u4e86\u201d\uff0c\u53c8\u4e0d\u7981\u96be\u53d7\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u8f7b\u8f7b\u629a\u7740\u5979\u7684\u79c0\u53d1\uff0c\u6307\u7740\u8f9b\u6377\u9053\uff1a\u201c\u53ef\u662f\u5440\uff01\u4f60\u4ee5\u540e\u53ef\u5343\u4e07\u4e0d\u80fd\u5ac1\u7ed9\u8fd9\u79cd\u4eba\uff0c \u4ed6\u59d3\u6885\uff0c\u53eb\u6885\u5c71\u6c11\uff0c\u4f60\u7684\u963f\u59e8\u5c31\u662f\u7ed9\u4ed6\u6c14\u6b7b\u7684\uff0c\u5988\u5988\u4e5f\u8981\u6740\u6b7b\u4ed6\uff0c\u7ed9\u4f60\u4e5d\u963f\u59e8\u62a5\u4ec7\u3002\u201d \u3000\u3000\u8f9b\u6377\u59cb\u7ec8\u83ab\u540d\u5176\u5999\uff0c\u8fd9\u4e00\u4e0b\u624d\u604d\u7136\u5927\u609f\uff1a\u201c\u539f\u6765\u662f\u6885\u53d4\u53d4\u7684\u4e8b\uff0c\u73b0\u5728\u90fd\u7b97\u5230\u6211\u5e10\u4e0a \u6765\u4e86\uff0c\u5509\uff01\u6211\u771f\u5012\u9709\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u53c8\u5fd6\u9053\uff1a\u201c\u53ef\u662f\u6211\u6ca1\u6709\u6885\u53d4\u53d4\uff0c\u53c8\u54ea\u91cc\u6709\u4eca\u5929\u5440\uff0c\u53ef\u80fd\u65e9\u6b7b\u5728\u4e94\u534e\u5c71\u91cc\u4e86\uff0c\u73b0 \u5728\u6211\u5c31\u662f\u66ff\u4ed6\u6b7b\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u8fd9\u6837\u6b7b\u5f97\u592a\u4e0d\u503c\u5f97\u5440\uff0c\u6885\u53d4\u53d4\u5012\u5e95\u5bf9\u4ed6\u4eec\u90a3\u4e2a\u2018\u4e5d\u963f\u59e8\u2019\u600e\u4e48\u6837\u5440\uff0c\u4ec0\u4e48 \u2018\u8d1f\u5fc3\u2019\uff0c\u96be\u9053\u6885\u53d4\u53d4\u5c06\u5979\u9057\u5f03\u4e86\u5417\uff1f\u201d \u3000\u3000\u4ed6\u7a81\u7136\u60f3\u5230\u90a3\u5929\u6885\u5c71\u6c11\u5e26\u4ed6\u81ea\u4e94\u534e\u5c71\u56de\u5230\u5bb6\u91cc\u7684\u7b2c\u4e00\u5929\uff0c\u5728\u524d\u5385\u91cc\u201c\u4faf\u4e8c\u53d4\u201d\u5bf9\u6885 \u5c71\u6c11\u6240\u8bf4\u7684\u8bdd\uff0c\u90a3\u65f6\u4ed6\u5b8c\u5168\u4e0d\u61c2\uff0c\u6b64\u523b\u5374\u5168\u660e\u767d\u4e86\uff0c\u6697\u5fd6\uff1a\u201c\u8fd9\u4e2a\u2018\u4e5d\u963f\u59e8\u2019\u60f3\u5fc5\u4e5f\u662f \u5728\u542c\u4e86\u6885\u53d4\u53d4\u5df2\u7ecf\u6b7b\u6389\u7684\u6d88\u606f\u65f6\u8d70\u7684\uff0c\u540e\u6765\u5979\u5927\u6982\u4e0d\u77e5\u600e\u7684\u6b7b\u4e86\uff0c\u800c\u8fd9\u4f4d\u65e0\u6781\u5c9b\u4e3b\u6b66\u529f \u867d\u9ad8\uff0c\u4eba\u5927\u6982\u5f88\u7cca\u6d82\u6ca1\u95ee\u4e2a\u6e05\u695a\uff0c\u5c31\u4ee5\u4e3a\u662f\u6885\u53d4\u53d4\u5bb3\u4e86\u5979\u7684\uff0c\u5509\uff01\u8fd9\u5c82\u4e0d\u5929\u5927\u7684\u51a4\u6789 \u5417\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u91cc\u5728\u60f3\uff0c\u5634\u91cc\u5374\u8bf4\u4e0d\u51fa\u6765\uff0c\u6025\u5f97\u989d\u4e0a\u7684\u6c57\u73e0\u76f4\u5192\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u51b2\u7740\u4ed6\u51b7\u7b11\u9053\uff1a\u201c\u4f60\u4e5f\u6015\u6b7b\u4e86\u5440\u3002\u201d\u51fb\u4e86\u4e24\u4e0b\u638c\uff0c\u8231\u5916\u4fbf\u8d70\u8fdb\u4e24\u4e2a\u8eab\u4f53\u7cbe\u58ee \u7684\u6c34\u624b\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u5429\u5490\u9053\uff1a\u201c\u8f6c\u8235\u5411\u4e1c\uff0c\u6211\u4eec\u8981\u56de\u53bb\u3002\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6c34\u624b\u606d\u656c\u5730\u79f0\u662f\uff0c\u7f2a\u4e03\u5a18\u53c8\u9053\uff1a\u201c\u5c06\u8fd9\u4e2a\u62ac\u5230\u540e\u9762\u5806\u4e1c\u897f\u7684\u8231\u91cc\u53bb\uff0c\u6bcf\u5929\u7ed9 \u4ed6\u704c\u4e00\u70b9\u7a00\u996d\uff0c\u4e0d\u8981\u8ba9\u4ed6\u5230\u8def\u4e0a\u997f\u6b7b\u3002\u201d \u3000\u3000\u8f9b\u6377\u6c14\u5f97\u4e03\u7a83\u751f\u70df\uff0c\u6069\u6028\u5206\u660e\uff0c\u65e0\u8bba\u6069\u3001\u4ec7\uff0c\u90fd\u770b\u5f97\u6781\u91cd\uff0c\u5bf9\u4ed6\u597d\u7684\u4eba\uff0c\u4ed6\u4e00\u5b9a\u60f3 \u7740\u65b9\u6cd5\u62a5\u7b54\uff0c\u5bf9\u4ed6\u574f\u7684\u4eba\uff0c\u4ed6\u4e5f\u8981\u5343\u4e07\u767e\u8ba1\u7684\u6765\u62a5\u590d\uff0c\u6b64\u523b\u4ed6\u5bf9\u7f2a\u4e03\u5a18\u6000\u4e86\u6781\u5927\u7684\u4ec7\u6068\u3002 \u6697\u5fd6\uff1a\u201c\u53ea\u8981\u6211\u4e0d\u6b7b\uff0c\u6211\u4e00\u5b9a\u8981\u597d\u597d\u6574\u6574\u4f60\u8fd9\u4e2a\u5a46\u5a18\u3002\u201d\u4ed6\u4e0b\u4e86\u51b3\u5fc3\uff0c\u8981\u62a5\u590d\u8fd9\u4e2a\u4ec7\u6068\u3002 \u3000\u3000\u968f\u5373\uff0c\u4ed6\u89c9\u5f97\u81ea\u5df1\u50cf\u662f\u4e00\u5757\u6728\u677f\uff0c\u88ab\u4eba\u76f4\u633a\u633a\u5730\u62ac\u51fa\u8231\u53bb\uff0c\u4e34\u51fa\u8231\u524d\uff0c\u4ed6\u770b\u5230\u90a3\u7edd \u7f8e\u7684\u767d\u8863\u5c11\u5973\u7684\u4e00\u53cc\u660e\u7738\uff0c\u4e5f\u5728\u671b\u7740\u81ea\u5df1\uff0c\u8138\u4e0a\u6ee1\u662f\u5173\u6000\uff0c\u601c\u60af\u7684\u795e\u8272\uff0c\u5fc3\u4e2d\u53c8\u4e0d\u7981\u89c9 \u5f97\u611f\u52a8\u4e4b\u6781\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u4e00\u773c\u662f\u77ed\u6682\u7684\uff0c\u4ed6\u5f88\u5feb\u88ab\u62ac\u51fa\u8231\uff0c\u90a3\u4e24\u4e2a\u6c34\u624b\u7c97\u624b\u7b28\u811a\uff0c\u6839\u672c\u50cf\u662f\u6ca1\u6709\u628a\u4ed6 \u5f53\u505a\u4eba\u770b\uff1b\u53ea\u5f53\u505a\u662f\u4e00\u4ef6\u8d27\u7269\u3002 \u3000\u3000\u4ed6\u770b\u5230\u5929\u5149\u4e00\u95ea\uff0c\u63a5\u7740\u53c8\u88ab\u629b\u8fdb\u4e00\u95f4\u6f06\u6697\u7684\u8239\u8231\uff0c\u4ed6\u4fbf\u50cf\u4e00\u5177\u5df2\u7ecf\u53d1\u786c\u4e86\u7684\u6b7b\u5c38\uff0c \u5367\u5728\u8239\u677f\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u629b\u4ed6\u88ab\u629b\u66f4\u8fdc\u3001\u66f4\u91cd\uff0c\u8eab\u4e0a\u7684\u9aa8\u8282\u90fd\u75db\u8d77\u6765\u4e86\uff0c\u8239\u8231\u91cc\u8fd8\u6709\u4e00\u80a1\u8150\u8680\u7684\u81ed\u6c14\uff0c \u718f\u5f97\u4ed6\u5934\u8111\u53d1\u6da8\u3002\u8f9b\u6377\u518d\u4e5f\u60f3\u4e0d\u5230\u81ea\u5df1\u4f1a\u843d\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u6c14\u5f97\u8981\u5410\u8840\uff0c\u8bd5\u7740\u60f3\u81ea\u5df1\u89e3\u5f00 \u7a74\u9053\uff0c\u4f46\u65e0\u6781\u5c9b\u7684\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u4f7f\u88ab\u70b9\u7684\u4eba\u8fde\u8fd0\u6c14\u90fd\u4e0d\u80fd\u591f\uff0c\u8fd9\u79cd\u624b\u6cd5\uff0c\u7adf\u8fd8\u8fdc\u5728\u70b9 \u82cd\u6d3e\u7684\u201c\u4e03\u7edd\u91cd\u624b\u201d\u4e4b\u4e0a\u3002 \u3000\u3000\u4ed6\u5df2\u77e5\u9053\u81ea\u5df1\u7684\u4f01\u56fe\u5931\u8d25\u4e86\uff0c\u5230\u4e86\u8fd9\u65f6\u5019\uff0c\u4ed6\u53cd\u800c\u5e73\u5fc3\u9759\u6c14\uff0c\u7edd\u4e0d\u591a\u4f5c\u65e0\u76ca\u7684\u4e3e\u52a8\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u8bb8\u4e45\uff0c\u6709\u4e2a\u7c97\u6c49\u8dd1\u4e86\u8fdb\u6765\uff0c\u7528\u5927\u7897\u76db\u4e86\u4e00\u5927\u7897\u7a00\u996d\uff0c\u62c9\u5f00\u4ed6\u7684\u5634\u5c31\u5f80\u5589 \u5499\u91cc\u5012\u3002 \u3000\u3000\u7a00\u996d\u53c8\u70eb\uff0c\u70eb\u5f97\u4ed6\u5589\u5499\u90fd\u8d77\u4e86\u6ce1\uff0c\u4ed6\u4e5f\u9006\u6765\u987a\u53d7\uff0c\u56e0\u4e3a\u5373\u4fbf\u4ed6\u4e0d\u613f\u987a\u53d7\uff0c\u4e5f\u6839\u672c\u522b \u65e0\u4ed6\u6cd5\u3002 \u3000\u3000\u90a3\u704c\u7a00\u996d\u7684\u4eba\u4f3c\u4e4e\u5bf9\u8fd9\u5dee\u4e8b\u6781\u611f\u5174\u8da3\uff0c\u8fc7\u4e86\u6ca1\u6709\u591a\u4e45\uff0c\u4ed6\u53c8\u6765\u704c\uff0c\u8fd9\u6837\u6bcf\u9694\u4e00\u6bb5\u5f88 \u77ed\u7684\u65f6\u95f4\uff0c\u4ed6\u5c31\u6765\u66ff\u8f9b\u6377\u704c\u4e0a\u4e00\u5927\u7897\u7a00\u996d\u3002 \u3000\u3000\u5230\u540e\u6765\u8f9b\u6377\u53ea\u89c9\u5f97\u809a\u76ae\u53d1\u6da8\uff0c\u4f46\u4ed6\u4e5f\u6ca1\u6709\u529e\u6cd5\u963b\u6b62\u3002 \u3000\u3000\u704c\u4e86\u516d\u3001\u4e03\u6b21\u7a00\u996d\u4e4b\u540e\uff0c\u4ed6\u5df2\u5b9e\u5728\u5fcd\u53d7\u4e0d\u4f4f\uff0c\u8fd9\u6bd4\u4efb\u4f55\u9177\u5211\u90fd\u5389\u5bb3\uff0c\u5c24\u5176\u662f\u5f53\u6eda\u70ed \u7684\u7a00\u996d\u704c\u8ff8\u90a3\u5df2\u70eb\u5f97\u8d77\u6ce1\u7684\u5589\u5499\u65f6\uff0c\u90a3\u79cd\u75db\u82e6\u7b80\u76f4\u662f\u96be\u4ee5\u5fcd\u53d7\u7684\uff0c\u8fd9\u4e9b\uff0c\u90fd\u66f4\u52a0\u6df1\u4e86\u8f9b \u6377\u5bf9\u7f2a\u4e03\u5a18\u7684\u6028\u6bd2\u3002 \u3000\u3000\u5ffd\u5730\uff0c\u53c8\u6709\u811a\u6b65\u58f0\u4f20\u6765\uff0c\u8f9b\u6377\u53eb\u82e6\u4e0d\u8fed\uff0c\u4ee5\u4e3a\u704c\u7a00\u996d\u7684\u53c8\u6765\u4e86\uff0c\u53ea\u5f97\u7d27\u7d27\u95ed\u8d77\u773c\u775b\u3002 \u3000\u3000\u54ea\u77e5\u8fd9\u6b21\u629a\u6478\u5230\u4ed6\u7684\u8138\u4e0a\u65f6\uff0c\u7adf\u4e0d\u662f\u6bdb\u8338\u8338\u7684\u7c97\u624b\uff0c\u800c\u662f\u4e00\u53cc\u5149\u6ed1\u5f97\u80dc\u8fc7\u767d\u7389\u7684\u624b\uff0c \u8fd8\u5e26\u7740\u4e00\u79cd\u751c\u7f8e\u7684\u9999\u6c14\u3002 \u3000\u3000\u8f9b\u6377\u7741\u5f00\u773c\u6765\uff0c\u5728\u77f3\u5ba4\u4e2d\u7684\u5341\u5e74\u82e6\u7ec3\uff0c\u4ed6\u5728\u9ed1\u6697\u4e2d\u89c6\u7269\u4f9d\u7136\u5b9b\u5982\u767d\u663c\uff0c\u8fd9\u65f6\u5728\u4ed6\u773c \u524d\u7684\uff0c\u662f\u4e00\u5f20\u65e0\u6bd4\u5a07\u7f8e\u7684\u9762\u5e9e\u3002 \u3000\u3000\u90a3\u9762\u5e9e\u4e00\u7b11\uff0c\u4ece\u4e24\u988a\u6d6e\u8d77\u4e24\u6735\u767e\u5408\uff0c\u7b11\u5bb9\u50cf\u662f\u767e\u5408\u7684\u82b1\u74e3\uff0c\u4e00\u74e3\u74e3\u94fa\u6ee1\u4e86\u5979\u7684\u5a07\u7f8e \u7684\u8138\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u4e00\u751c\uff0c\u4e0e\u751f\u4ff1\u6765\u7684\uff0c\u4ed6\u5bf9\u4e8e\u201c\u7f8e\u201d\uff0c\u603b\u6709\u7740\u6781\u6df1\u7684\u60c5\u611f\u548c\u5d07\u62dc\uff0c\u6885\u5c71\u6c11\u7684 \u718f\u9676\uff0c\u66f4\u52a0\u6df1\u4e86\u4ed6\u7684\u8fd9\u79cd\u503e\u5411\u3002 \u3000\u3000\u8fd9\u79cd\u4e0d\u662f\u6bcf\u4e2a\u4eba\u90fd\u80fd\u4e86\u89e3\u7684\u60c5\u611f\uff0c\u4f7f\u5f97\u4ed6\u4ee5\u540e\u5728\u60c5\u611f\u4e0a\u53d7\u4e86\u4e0d\u5c11\u6298\u78e8\uff0c\u4f46\u53ea\u8981\u80fd\u4e86 \u89e3\u5230\uff0c\u5c1d\u8bd5\u8fc7\u7f8e\u914c\u771f\u8c1b\uff0c\u8fd9\u4ee3\u4ef7\u662f\u503c\u5f97\u7684\uff0c\u4ed6\u6b64\u523b\u89c1\u4e86\u8fd9\u7edd\u7f8e\u7684\u9762\u5e9e\uff0c\u5fc3\u4e2d\u7edd\u65e0\u90aa\u5ff5\uff0c \u4f46\u5374\u6709\u4eb2\u8fd1\u7684\u5ff5\u5934\u3002 \u3000\u3000\u98ce\u6d41\u548c\u90aa\u6076\uff0c\u539f\u662f\u6709\u7740\u6781\u5927\u7684\u533a\u522b\u7684\u3002 \u3000\u3000\u95ee\u9898\u662f\u4e16\u4eba\u5bf9\u8fd9\u533a\u522b\uff0c\u4e86\u89e3\u5f97\u592a\u5c11\u4e86\u3002 \u3000\u3000\u5f20\u83c1\u89c1\u8f9b\u6377\u51fa\u795e\u5730\u671b\u7740\u81ea\u5df1\uff0c\u751c\u751c\u7684\u4e00\u7b11\uff0c\u66f4\u575a\u5b9a\u4e86\u81ea\u5df1\u7684\u60f3\u6cd5\uff0c\u90a3\u5c31\u662f\u201c\u653e\u4ed6\u9003 \u53bb\u201d\u3002 \u3000\u3000\u867d\u7136\u5979\u7684\u5fc3\u60c5\u662f\u77db\u76fe\u7684\uff0c\u5979\u77e5\u9053\u53ea\u8981\u5979\u653e\u4e86\u8fd9\u201c\u773c\u775b\u5927\u5927\u7684\u5e74\u9752\u4eba\u201d\u9003\u8d70\uff0c\u90a3\u4e48\u5979 \u6b64\u540e\u6050\u6015\u5c06\u6c38\u8fdc\u89c1\u4e0d\u7740\u4ed6\u4e86\u3002 \u3000\u3000\u53ef\u662f\u5979\u4e5f\u4e0d\u5fcd\u8ba9\u4ed6\u88ab\u81ea\u5df1\u7684\u7239\u7239\u3001\u5988\u5988\u6740\u6b7b\uff0c\u7eb5\u7136\u4ed6\u4e5f\u8bb8\u72af\u8fc7\u8bb8\u591a\u8fc7\u5931\uff0c\u5979\u89c9\u5f97\u90a3 \u4e5f\u662f\u503c\u5f97\u539f\u8c05\u7684\u3002 \u3000\u3000\u7eaf\u6d01\u7684\u5c11\u5973\uff0c\u5bf9\u201c\u7231\u201d\u4e0e\u201c\u618e\u201d\u7684\u5206\u522b\uff0c\u8fdc\u6bd4\u5bf9\u201c\u5bf9\u201d\u4e0e\u201c\u9519\u201d\u7684\u533a\u522b\u6765\u5f97\u5f3a\u70c8\uff0c \u5f20\u83c1\u4e5f\u6b63\u662f\u8fd9\u6837\u7684\u3002 \u3000\u3000\u5979\u6084\u6084\u8bf4\u9053\uff1a\u201c\u6211\u653e\u4f60\u9003\u8d70\uff0c\u8fd9\u91cc\u79bb\u5cb8\u5f88\u8fd1\uff0c\u4f60\u4e00\u5b9a\u53ef\u4ee5\u8df3\u8fc7\u53bb\u7684\uff0c\u53ef\u662f\u4f60\u8981\u8d76 \u5feb\u3002\u201d \u3000\u3000\u5979\u53f3\u624b\u7684\u59c6\u6307\u6309\u7740\u8f9b\u6377\u9f3b\u4e0b\u7684\u201c\u95fb\u9999\u7a74\u201d\uff0c\u5de6\u624b\u6781\u5feb\u5730\u5728\u8f9b\u6377\u524d\u80f8\u548c\u80c3\u4e0b\u62cd\u4e86\u4e24\u638c\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u675f\u7f1a\u81ea\u5df1\u8eab\u4f53\u7684\u56fa\u5236\uff0c\u7a81\u7136\u677e\u5f00\u4e86\uff0c\u88ab\u7981\u9006\u7740\u7684\u771f\u6c14\uff0c\u4e5f\u731b\u7136\u5728\u56db\u80a2\u91cc\u6d41 \u7545\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u5fae\u4e00\u4f5c\u52bf\uff0c\u7ad9\u4e86\u8d77\u6765\uff0c\u9762\u5bf9\u9762\u5730\u7ad9\u5728\u5f20\u83c1\u524d\u9762\uff0c\u9f3b\u7aef\u91cc\u751a\u81f3\u53ef\u4ee5\u95fb\u5230\u8eab\u4e0a\u5e7d \u5170\u7684\u9999\u6c14\u3002 \u3000\u3000\u6b64\u523b\u5929\u5730\u95f4\u4eff\u4f5b\u90fd\u88ab\u9999\u6c14\u5145\u6ee1\u4e86\uff0c\u4e07\u7269\u4e5f\u4eff\u4f5b\u53ea\u5269\u4e0b\u4ed6\u9762\u524d\u8fd9\u5f20\u7edd\u7f8e\u7684\u9762\u5e9e\u3002 \u3000\u3000\u4ed6\u4eec\u5f7c\u6b64\u90fd\u53ef\u4ee5\u542c\u5230\u5bf9\u65b9\u5fc3\u8df3\u7684\u58f0\u97f3\uff0c\u8f9b\u6377\u6728\u7136\u7ad9\u7740\uff0c\u8111\u6d77\u91cc\u4e00\u7247\u7a7a\u6d1e\uff0c\u53e3\u4e2d\u4e5f\u4e0d \u77e5\u8be5\u8bf4\u4ec0\u4e48\u3002 \u3000\u3000\u826f\u4e45\uff0c\u83c1\u810a\u50ac\u4fc3\u9053\uff1a\u201c\u4f60\u5feb\u8d70\u5440\uff01\u88ab\u7239\u7239\u77e5\u9053\u4e86\uff0c\u53ef\u4e0d\u5f97\u4e86\u3002\u201d\u5176\u5b9e\u5979\u53c8\u4f55\u5c1d\u613f\u610f \u4ed6\u8d70\u5462\uff1f \u3000\u3000\u8f9b\u6377\u4e00\u54ac\u7259\uff0c\u8f7b\u8f7b\u5728\u8fd9\u5f20\u7edd\u7f8e\u7684\u9762\u5e9e\u4e0a\u4eb2\u4e86\u4e00\u4e0b\uff0c\u771f\u6c14\u6025\u8feb\u5730\u6ce8\u6ee1\u56db\u80a2\uff0c\u8eab\u5f62\u52a8\u5904\uff0c \u63a0\u51fa\u8231\u5916\u3002 \u3000\u3000\u5916\u9762\u662f\u9ed1\u591c\uff0c\u8239\u662f\u505c\u6cca\u7740\u7684\uff0c\u6b63\u5982\u5f20\u83c1\u6240\u8bf4\uff0c\u79bb\u5cb8\u5e76\u4e0d\u751a\u8fdc\uff0c\u4f46\u4e5f\u83ab\u7ea6\u6709\u4e03\u3001\u516b\u4e08 \u8fdc\u8fd1\u3002 \u3000\u3000\u8f9b\u6377\u7a9c\u51fa\u8231\u5916\uff0c\u8eab\u5f62\u7edd\u672b\u505c\u7559\uff0c\u8fd9\u4e03\u3001\u516b\u4e08\u7684\u8ddd\u79bb\uff0c\u5bf9\u4ed6\u6765\u8bf4\uff0c\u8d8a\u8fc7\u53bb\u5e76\u975e\u5341\u5206\u56f0 \u96be\u3002\u8fd9\u4e00\u7eb5\u5c82\u6709\u4e08\u8fdc\u8fd1\uff0c\u4ed6\u53cc\u817f\u53c8\u731b\uff0c\u5e73\u7740\u8eab\u5b50\u5411\u4e0b\u63a0\u53bb\uff0c\u8fd9\u66fc\u5999\u7684\u8f6c\u6298\uff0c\u5728\u4e2d\u539f\u6b66\u6797 \u4e2d\uff0c\u7684\u786e\u662f\u5df2\u5230\u7edd\u9876\u4e86\u3002 \u3000\u3000\u56db\u91ce\u6e05\u5bd2\uff0c\u6c34\u58f0\u7ec6\u788e\uff0c\u5bc2\u9759\u4e2d\u7a81\u7136\u6709\u4eba\u51b7\u51b7\u5730\u8bf4\u4e86\u4e2a\u201c\u597d\u201d\u5b57\uff0c\u4f59\u97f3\u8885\u8885\uff0c\u56db\u6563\u98d8 \u8361\u3002 \u3000\u3000\u5728\u8f9b\u6377\u8eab\u8eaf\u63a5\u89e6\u5230\u5730\u9762\u7684\u90a3\u4e00\u523b\uff0c\u4ed6\u773c\u5149\u52a8\u5904\uff0c\u9762\u524d\u53c8\u6084\u7136\u7ad9\u7740\u4e00\u6761\u767d\u751f\u751f\u7684\u4eba\u5f71\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u5239\u90a3\u65f6\uff0c\u4ed6\u5fc3\u4e2d\u4e00\u8361\uff1a\u201c\u83ab\u975e\u5979\u820d\u4e0d\u5f97\u6211\u8d70\uff0c\u53c8\u8ffd\u6765\u4e86\u3002\u201d\u811a\u5c16\u70b9\u5230\u5730\u9762\uff0c \u5b9a\u775b\u4e00\u770b\uff0c\u4e0d\u7981\u9b42\u5916\u5929\u5916\u3002 \u3000\u3000\u539f\u6765\u6b64\u523b\u7ad9\u5728\u4ed6\u9762\u524d\u51b7\u7b11\u7740\u7684\uff0c\u5374\u662f\u90a3\u767d\u8863\u4e66\u751f\u65e0\u6781\u5c9b\u4e3b\uff0c\u54ea\u91cc\u662f\u4ed6\u5fc3\u4e2d\u6240\u60f3\u7684\u4eba\u3002 \u3000\u3000\u65e0\u6068\u751f\u51b7\u7136\u9053\uff1a\u201c\u4f60\u60f3\u8d70\u3002\u201d \u3000\u3000\u8f9b\u6377\u4f30\u91cf\u81ea\u5df1\uff0c\u77e5\u9053\u7edd\u5bf9\u9003\u4e0d\u8fc7\u53bb\uff0c\u4e5f\u96be\u52a8\u5f97\u4e86\u4eba\u5bb6\uff0c\u4fbf\u9053\uff1a\u201c\u9601\u4e0b\u6709\u8bb8\u591a\u4e8b\u8bef\u4f1a \u4e86\uff0c\u6211\u2026\u2026\u201d \u3000\u3000\u65e0\u6068\u751f\u5c16\u9510\u7684\u51b7\u7b11\uff0c\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\u3002\u4ed6\u7a81\u8d77\u4fa5\u5e78\u4e4b\u5fc3\uff0c\u53cc\u638c\u6325\u51fa\uff0c\u5341\u6307\u7b95\u5f20\uff0c\u53f3\u624b \u7684\u98df\u6307\u3001\u4e2d\u6307\u3001\u59c6\u6307\uff0c\u70b9\u5411\u65e0\u6068\u751f\u201c\u5929\u5b97\u201d\u3001\u201c\u80a9\u8d1e\u201d\u3001\u201c\u7389\u6795\u201d\u4e09\u7a74\uff0c\u5c0f\u6307\u5fae\u56de\uff0c\u6a2a \u753b\u201c\u795e\u5c01\u201d\u3002 \u3000\u3000\u5de6\u624b\u7684\u4e94\u6307\uff0c\u5374\u70b9\u5411\u65e0\u6068\u751f\u8138\u4e0a\u7684\u201c\u56db\u767d\u201d\u3001\u201c\u4e0b\u5173\u201d\u3001\u201c\u5730\u4ed3\u201d\u3001\u201c\u6c89\u9999\u201d\u3001 \u201c\u4e95\u7a74\u201d\u4e94\u7a74\u3002\u819d\u76d6\u5fae\u56de\uff0c\u649e\u5411\u4e0b\u9634\u3002 \u3000\u3000\u4ed6\u6bd5\u5c3d\u529f\u529b\uff0c\u8fd9\u4e00\u51fb\u6b63\u662f\u5341\u5e74\u6765\u82e6\u7ec3\u7684\u7cbe\u534e\u3002 \u3000\u3000\u65e0\u6068\u751f\u51b7\u7b11\u672b\u505c\uff0c\u8eab\u5f62\u5411\u540e\u66b4\u7f29\uff0c\u8f9b\u6377\u5982\u5f62\u9644\u5f71\uff0c\u8ddf\u4e86\u4e0a\u53bb\uff0c\u4ed6\u6b64\u62db\u62a2\u5c3d\u5148\u673a\uff0c\u4f46 \u662f\u65e0\u6068\u751f\u7684\u8f7b\u529f\uff0c\u5df1\u5230\u4e86\u9a6d\u6c14\u800c\u884c\u7684\u5730\u6b65\uff0c\u4ed6\u7684\u8eab\u8eaf\uff0c\u603b\u548c\u8f9b\u6377\u4fdd\u6301\u7740\u4e00\u6bb5\u8ddd\u79bb\uff0c\u8f9b\u6377 \u6c38\u8fdc\u65e0\u6cd5\u5c06\u62db\u4f7f\u6ee1\u3002 \u3000\u3000\u77ac\u606f\u4e4b\u95f4\uff0c\u4e24\u4eba\u5df2\u5411\u540e\u79fb\u52a8\u4e86\u5341\u6570\u4e08\uff0c\u8f9b\u6377\u771f\u6c14\u5df2\u4e0d\u7ee7\uff0c\u65e0\u6781\u5c9b\u4e3b\u8eab\u5f62\u5fae\u5fae\u4e00\u8f6c\uff0c \u888d\u8896\u62c2\u5904\uff0c\u62c2\u4e2d\u8f9b\u6377\u638c\u7f18\u6b63\u4e2d\u7684\u201c\u540e\u6eaa\u201d\u7a74\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u6307\u5feb\u5982\u95ea\u7535\uff0c\u7528\u7684\u662f\u6b66\u6797\u4e2d\u4e45\u5df2\u5931\u4f20\u7684\u201c\u62c2\u7a74\u201d\u6cd5\uff0c\u8f6c\u8eab\u4e2d\u888d\u8896\u6325\u51fa\uff0c\u6839\u672c \u4e0d\u7528\u51fa\u62db\uff0c\u662f\u4ee5\u4fbf\u4e5f\u7701\u53bb\u4e86\u51fa\u62db\u7684\u65f6\u95f4\uff0c\u8f9b\u6377\u5168\u5f0f\u672a\u52a8\uff0c\u88ab\u5b9a\u5728\u5730\u4e0a\uff0c\u5b9b\u5982\u4e00\u5ea7\u77f3\u5851\u7684 \u795e\u50cf\u3002 \u3000\u3000\u65e0\u6068\u751f\u6b66\u529f\u867d\u7136\u8d85\u51e1\u5165\u5723\uff0c\u4f46\u4e5f\u4e0d\u80fd\u5728\u4e00\u62db\u4e2d\u70b9\u4e2d\u8f9b\u6377\u7684\u7a74\u9053\uff0c\u6b64\u523b\u5374\u662f\u56e0\u4e3a\u8f9b\u6377 \u5fc3\u5148\u5df2\u9981\uff0c\u529b\u53c8\u4e2d\u65ad\uff0c\u6240\u7528\u4e4b\u624b\u6cd5\uff0c\u4e5f\u662f\u8f9b\u6377\u4ece\u6765\u6ca1\u6709\u542c\u5230\u8fc7\u7684\uff0c\u6839\u672c\u6599\u4e0d\u5230\u4f1a\u6709\u6b64\u4e00 \u7740\u3002 \u3000\u3000\u79cd\u79cd\u539f\u56e0\uff0c\u4f7f\u5f97\u8f9b\u6377\u4e00\u62db\u4e4b\u4e0b\uff0c\u5c31\u88ab\u5236\u4f4f\uff0c\u4ed6\u5fc3\u4e2d\u7684\u60f6\u6025\uff0c\u81ea\u8d23\uff0c\u4e0d\u53ef\u8a00\u55bb\uff0c\u96be\u4ee5 \u63cf\u8ff0\u3002 \u3000\u3000\u4ed6\u6697\u5fd6\uff1a\u201c\u60f3\u4e0d\u5230\u6211\u81ea\u4ee5\u4e3a\u5df2\u7ecf\u53ef\u4ee5\u8d70\u904d\u5929\u4e0b\u7684\u6b66\u529f\uff0c\u8fde\u4eba\u5bb6\u8f7b\u63cf\u6de1\u5199\u7684\u4e00\u62db\u90fd\u6321 \u4e0d\u4f4f\u3002\u201d \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u7b11\u58f0\u987f\u4f4f\uff0c\u53f3\u81c2\u4e00\u6284\uff0c\u5c06\u8f9b\u6377\u631f\u5728\u80c1\u4e0b\u3002 \u3000\u3000\u5f20\u83c1\u5e26\u7740\u60b2\u54c0\u7684\u53f9\u58f0\uff0c\u8e31\u5230\u8239\u8237\u65c1\uff0c\u6c5f\u6c34\u6f2b\u6f2b\uff0c\u661f\u6708\u6ee1\u5929\uff0c\u8fdc\u5904\u662f\u4e00\u7247\u9759\u5bc2\u7684\u9ed1\u6697\u3002 \u3000\u3000\u201c\u4f0a\u4eba\u5df2\u53bb\u3002\u60c5\u601d\u6005\u6005\u3002\u201d\u5f20\u83c1\u671b\u7740\u8fd9\u4e00\u7247\u6726\u80e7\u70df\u6c34\uff0c\u6709\u751f\u4ee5\u6765\uff0c\u7b2c\u4e00\u6b21\u89c9\u51fa\u4eba\u751f \u7684\u5bc2\u5bde\u3002 \u3000\u3000\u7a81\u5730\uff0c\u5979\u671b\u89c1\u5cb8\u8fb9\u767d\u5f71\u5fae\u95ea\uff0c\u6bd4\u7535\u5149\u8fd8\u5feb\uff0c\u4e00\u6761\u7eaf\u767d\u8272\u7684\u4eba\u5f71\u63a0\u4e86\u8fc7\u6765\uff0c\u671b\u89c1\u8fd9\u79cd \u60ca\u4eba\u7684\u8eab\u6cd5\uff0c\u5979\u4e0d\u7528\u601d\u8003\uff0c\u5df2\u7ecf\u77e5\u9053\u4e00\u5b9a\u662f\u5979\u7684\u7239\u7239\uff0c\u201c\u7239\u7239\u4e0a\u5cb8\u53bb\u5e72\u4ec0\u4e48\uff0c\u96be\u9053\u4ed6\u53d1 \u73b0\u4e86\u4ed6\u5417\uff1f\u201d \u3000\u3000\u8fd9\u5ff5\u95f4\u65b9\u81ea\u95ea\u8fc7\uff0c\u5df2\u7ecf\u6709\u4e8b\u5b9e\u6765\u56de\u7b54\u5979\u4e86\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u631f\u7740\u8f9b\u6377\uff0c\u56de\u5230\u8239\u4e0a\uff0c\u671d\u7ad9\u5728\u8239\u4fa7\u53d1\u7740\u6014\u7684\u5f20\u83c1\u671b\u4e86\u4e00\u773c\uff0c\u53f3\u81c2\u8d77\u5904\uff0c\u53c8 \u5c06\u8f9b\u6377\u629b\u5728\u8231\u91cc\u3002 \u3000\u3000\u5f20\u83c1\u7684\u4e00\u9897\u5fc3\uff0c\u51e0\u4e4e\u8df3\u5230\u55d3\u773c\u4e86\uff0c\u5979\u60ca\u60e7\u4ea4\u96c6\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u7f13\u7f13\u8d70\u5230\u5979\u9762\u524d\uff0c\u9053\uff1a\u201c\u4f60\u505a\u7684\u597d\u4e8b\uff0c\u5feb\u8ddf\u6211\u56de\u8231\u53bb\u3002\u201d\u9762\u5bd2\u5982\u51b0\uff0c\u663e\u89c1 \u5f97\u662f\u5df2\u52a8\u4e86\u771f\u6012\u3002 \u3000\u3000\u8f9b\u6377\u50cf\u7b2c\u4e00\u6b21\u4e00\u6837\uff0c\u88ab\u63b7\u5165\u6697\u8231\u91cc\uff0c\u66f4\u60e8\u7684\u662f\u4ed6\u8fd9\u6b21\u88ab\u70b9\u4e2d\u7a74\u9053\u65f6\uff0c\u662f\u4e24\u81c2\u524d\u4f38\u535c\uff0c \u4e94\u6307\u7b95\u5f20\uff0c\u53f3\u817f\u5f13\u66f2\u7684\u59ff\u52bf\uff0c\u662f\u4ee5\u4ed6\u6b64\u523b\u4e5f\u53ea\u80fd\u4fdd\u6301\u7740\u8fd9\u4e2a\u59ff\u52bf\uff0c\u4e11\u6076\u800c\u6ed1\u7a3d\u5730\u4ef0\u5367\u5728 \u4e0a\u3002 \u3000\u3000\u9001\u7a00\u996d\u7684\u7c97\u6c49\u4f9d\u7136\u6ca1\u6709\u9650\u5236\u5730\u704c\u4ed6\u7a00\u996d\uff0c\u6bcf\u5929\u4ed6\u552f\u4e00\u80fd\u89c1\u5230\u9633\u5149\u7684\u673a\u4f1a\uff0c\u5c31\u662f\u90a3\u7c97 \u6c49\u631f\u4ed6\u5230\u8231\u5916\u6392\u6cc4\u7684\u65f6\u5019\u3002 \u3000\u3000\u4ed6\u4e5f\u53ea\u80fd\u85c9\u7740\u8fd9\u552f\u4e00\u7684\u9014\u5f84\uff0c\u6765\u8ba1\u7b97\u65f6\u65e5\u3002 \u3000\u3000\u8fd9\u6837\u8fc7\u4e86\u4e94\u3001\u516d\u5929\uff0c\u8f9b\u6377\u5df2\u88ab\u6298\u78e8\u5f97\u4e0d\u6210\u6837\u5b50\uff0c\u4ed6\u8eab\u4f53\u56db\u80a2\u867d\u4e0d\u80fd\u52a8\uff0c\u4f46\u8111\u7b4b\u610f\u5ff5 \u4e5f\u66f4\u5f3a\uff0c\u4f46\u8111\u7b4b\u601d\u60f3\u5374\u66f4\u6d3b\u8dc3\u4e86\u3002 \u3000\u3000\u56e0\u6b64\uff0c\u4ed6\u5bf9\u4ed6\u6240\u6028\u6068\u7684\u4eba\u6028\u6bd2\u66f4\u6df1\uff0c\u5bf9\u4ed6\u6240\u7231\u7684\u4eba\uff0c\u5173\u6000\u610f\u5ff5\u4e5f\u66f4\u5f3a\uff0c\u7136\u800c\u5c31\u5728\u8fd9 \u4e2a\u65f6\u5019\uff0c\u4ed6\u624d\u77e5\u9053\u201c\u7231\u201d\u7684\u529b\u91cf\uff0c\u66f4\u8fdc\u6bd4\u201c\u6068\u201d\u5f3a\u70c8\u3002 \u3000\u3000\u56e0\u4e3a\u5728\u4ed6\u8111\u6d77\u4e2d\u76d8\u65cb\u7740\u7684\uff0c\u4ed6\u6240\u7231\u7684\u4eba\u8fdc\u6bd4\u4ed6\u6240\u6068\u7684\u4eba\u4e3a\u591a\uff0c\u800c\u4ed6\u5bf9\u4e8e\u4e16\u4e8b\u7684\u770b\u6cd5\uff0c \u4e5f\u5728\u6b64\u65f6\u6709\u4e86\u5f88\u5927\u7684\u8f6c\u53d8\u3002 \u3000\u3000\u91d1\u6885\u9f84\uff0c\u5f53\u7136\u662f\u4ed6\u6df1\u5ff5\u7684\u4eba\uff0c\u4ed6\u65f6\u65f6\u523b\u523b\uff0c\u8111\u6d77\u4e2d\u90fd\u4f1a\u6cdb\u8d77\u5979\u90a3\u67d4\u5a9a\u7684\u5f71\u5b50\u3002\u90fd\u4f1a \u610f\u5ff5\u7740\u4ed6\u548c\u5979\u5728\u5bc2\u5bde\u7684\u65f7\u91ce\u91cc\uff0c\u6240\u6e21\u8fc7\u7684\u90a3\u4e00\u4e2a\u767d\u5929\u548c\u4e00\u4e2a\u665a\u4e0a\uff0c\u5bf9\u4e8e\u91d1\u6885\u9f84\u4e3a\u4ed6\u6240\u5949 \u732e\u7684\u4e00\u5207\uff0c\u4ed6\u4e5f\u66f4\u611f\u5230\u73cd\u60dc\u3002 \u3000\u3000\u65b9\u5c11\u74a7\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u5fd8\u6000\u3002 \u3000\u3000\u7136\u800c\u6b64\u523b\u5728\u4ed6\u8111\u6d77\u4e2d\u5370\u8c61\u6700\u9c9c\u660e\u7684\uff0c\u5374\u662f\u5f20\u83c1\u7684\u7edd\u7f8e\u7684\u9762\u5e9e\u3002 \u3000\u3000\u201c\u5979\u6b64\u65f6\u4e0d\u77e5\u600e\u4e48\u6837\u5566\uff0c\u8fd9\u4e48\u591a\u5929\uff0c\u6211\u6ca1\u6709\u770b\u5230\u5979\u7684\u5f71\u5b50\uff0c\u6211\u60f3\uff0c\u5927\u6982\u5979\u5df2\u88ab\u5979\u90a3 \u53ef\u6068\u7684\u7236\u6bcd\u6df1\u6df1\u7684\u8d23\u9a82\u4e86\u5427\u3002\u201d \u3000\u3000\u8f9b\u6377\u6697\u5730\u4e3a\u4ed6\u6240\u7231\u7684\u4eba\u4eec\u795d\u798f\u3002 \u3000\u3000\u4ed6\u751a\u81f3\u5fd8\u5374\u4e86\u81ea\u5df1\u7684\u5b89\u5371\uff0c\u66f4\u5fd8\u5374\u4e86\u4ec7\u6068\u7684\u5b58\u5728\u3002 \u3000\u3000\u5f20\u83c1\u7684\u786e\u662f\u88ab\u65e0\u6781\u5c9b\u4e3b\u592b\u5987\u75db\u8d23\u8fc7\u4e86\uff0c\u5979\u88ab\u5979\u7684\u7236\u6bcd\uff0c\u8f6f\u7981\u5728\u8231\u91cc\uff0c\u53ef\u662f\uff0c\u5979\u4e5f\u4e0d \u80fd\u5fd8\u8bb0\u8fd9\u201c\u773c\u775b\u5927\u5927\u7684\u5e74\u9752\u4eba\u3002\u201d \u3000\u3000\u8239\u7531\u5d07\u660e\u5c9b\u5357\u4fa7\u5cb8\u884c\uff0c\u62df\u7531\u957f\u6c5f\u5357\u53e3\u51fa\u6d77\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u51ed\u7a97\u8fdc\u773a\uff0c\u524d\u9762\u5c31\u662f\u6c34\u5929\u65e0\u9645\uff0c\u6d69\u7ff0\u58ee\u89c2\u7684\u4e1c\u6d77\uff0c\u4e0d\u7981\u5fc3\u80f8\u7545\u7136\uff0c\u7b11\u8bed\u7f2a \u4e03\u5a18\u9053\uff1a\u201c\u6211\u4eec\u53c8\u5feb\u5230\u5bb6\u4e86\u3002\u201d \u3000\u3000\u7f2a\u4e03\u5a18\u7b11\u4e86\u7b11\uff0c\u65e0\u6068\u751f\u7a81\u76b1\u7709\u9053\uff1a\u201c\u8fd9\u6b21\u56de\u5230\u5c9b\u4e0a\uff0c\u771f\u8be5\u597d\u597d\u7ba1\u6559\u83c1\u513f\u4e86\u3002\u201d\u7f28\u4e03 \u5a18\u53c8\u4e00\u7b11\uff0c\u65e0\u6781\u5c9b\u4e3b\u8be7\u7136\u95ee\u9053\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u201c\u6211\u7b11\u6709\u4e9b\u6d3b\u5f97\u4e0d\u592a\u8010\u70e6\u7684\u6d77\u76d7\uff0c\u8981\u6765\u62a2\u6211\u4eec\u7684\u8239\u4e86\u3002\u201d\u7f28\u4e03\u5a18\u6307\u7740\u7a97\u5916\u9053\uff1a\u201c\u8fd9 \u4e24\u5929\u6211\u4eec\u4e5f\u771f\u67af\u71e5\u5f97\u5f88\uff0c\u4eca\u5929\u5012\u53ef\u4ee5\u62ff\u4ed6\u4eec\u6765\u89e3\u89e3\u95f7\u3002\u201d \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u987a\u7740\u5979\u7684\u624b\u6307\u671d\u5916\u770b\u53bb\uff0c\u679c\u7136\u8fdc\u5904\u6709\u4e09\u4e2a\u9ed1\u70b9\uff0c\u65b9\u624d\u4ed6\u5fc3\u4e2d\u6709\u6240\u611f\u6000\uff0c\u662f \u4ee5\u6ca1\u6709\u6ce8\u610f\u3002 \u3000\u3000\u4e8e\u662f\u4ed6\u8be7\u5f02\u5730\u8bf4\u9053\uff1a\u201c\u8fd9\u5012\u5947\u602a\u4e86\uff0c\u4e1c\u6d77\u4e0a\u5c45\u7136\u8fd8\u6709\u4e0d\u8ba4\u8bc6\u6211\u4eec\u8fd9\u8258\u8239\u7684\u6d77\u76d7\u5e2e\u3002\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u4e5f\u8bb8\u4e0d\u662f\u5462\uff01\u201d\u7f28\u4e03\u5a18\u7b11\u7740\u8bf4\u3002 \u3000\u3000\u6d77\u98ce\u5f3a\u52b2\uff0c\u90a3\u4e09\u8258\u8239\u770b\u7740\u50cf\u662f\u6ca1\u6709\u79fb\u52a8\uff0c\u5176\u5b9e\u6765\u52bf\u6781\u5feb\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u5df2\u53ef\u770b\u5230 \u8239\u7684\u5f62\u72b6\u4e86\u3002 \u3000\u3000\u90a3\u4e09\u8258\u8239\u6210\u201c\u54c1\u201d\u5b57\u5f62\u671d\u4ed6\u4eec\u9a76\u4e86\u8fc7\u6765\uff0c\u65e0\u6781\u5c9b\u4e3b\u7b11\u9053\uff1a\u201c\u770b\u6837\u5b50\u679c\u771f\u662f\u6709\u70b9\u610f\u601d \u4e86\u3002\u201d \u3000\u3000\u4ed6\u6b66\u529f\u901a\u7384\uff0c\u81ea\u7136\u6ca1\u6709\u5c06\u8fd9\u4e9b\u6d77\u76d7\u653e\u5728\u5fc3\u4e0a\u3002\u662f\u4ee5\u4ed6\u4ecd\u7136\u5b89\u7965\u5730\u51ed\u7a97\u800c\u5750\uff0c\u4efb\u90a3\u4e09 \u8258\u6d77\u76d7\u8239\u5c06\u4ed6\u6240\u4e58\u7684\u8239\u5305\u56f4\u7740\uff0c\u6ca1\u6709\u52a8\u4e00\u4e1d\u58f0\u8272\u3002 \u3000\u3000\u63a5\u7740\uff0c\u90a3\u4e09\u8258\u8239\u6bcf\u4e00\u8258\u8239\u7684\u8239\u5934\uff0c\u8d70\u51fa\u4e00\u4e2a\u5168\u8eab\u7a7f\u7740\u7d27\u8eab\u6c34\u9760\u7684\u5927\u6c49\uff0c\u6bcf\u4eba\u53d6\u51fa\u4e00 \u53ea\u725b\u89d2\u5236\u6210\u7684\u53f7\u89d2\uff0c\u653e\u5728\u53e3\u4e2d\u5439\u4e86\u8d77\u6765\uff0c\u53d1\u51fa\u4e00\u79cd\u201c\u545c\uff0c\u545c\u201d\u523a\u7684\u58f0\u97f3\uff0c\u5728\u6d77\u9762\u5e7f\u9614\u5730 \u5439\u6563\u7740\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u7b11\u9053\uff1a\u201c\u8fd9\u5e2e\u6d77\u76d7\u6392\u573a\u5012\u4e0d\u5c0f\uff0c\u4e0d\u77e5\u9053\u662f\u54ea\u4e00\u5e2e\u7684\uff1f\u201d\u8bed\u6c14\u4e2d\u6ee1\u5e26\u4e0d\u5c51\u548c\u8f7b \u8511\u3002 \u3000\u3000\u5439\u4e86\u4e00\u9635\u53f7\u89d2\uff0c\u90a3\u4e09\u4e2a\u5927\u6c49\u4fbf\u9000\u5728\u4e00\u65c1\uff0c\u63a5\u7740\u8231\u5185\u9646\u7eed\u8d70\u51fa\u8bb8\u591a\u4e5f\u7a7f\u7740\u7d27\u8eab\u6c34\u9760\u7684 \u6c49\u5b50\u3002 \u3000\u3000\u4e00\u8d70\u51fa\u8231\uff0c\u4ed6\u4eec\u4fbf\u5206\u6210\u4e24\u6392\uff0c\u96c1\u7fc5\u4f3c\u5730\u6cbf\u7740\u8239\u8237\u7ad9\u7740\uff0c\u8fd9\u4e48\u8bb8\u591a\u4eba\uff0c\u5c45\u7136\u8fde\u4e00\u70b9\u58f0 \u97f3\u90fd\u6ca1\u6709\u3002 \u3000\u3000\u6b64\u65f6\u65e0\u6781\u5c9b\u4e3b\u592b\u5987\u4e5f\u4e0d\u514d\u89c9\u5f97\u5947\u602a\uff0c\u7f2a\u4e03\u5a18\u9053\uff1a\u201c\u6211\u8fd8\u6ca1\u6709\u770b\u5230\u6709\u6d77\u76d7\u8fd9\u6837\u62a2\u4eba\u5bb6 \u4e1c\u897f\u7684\u3002\u201d \u3000\u3000\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\u3002\u6bcf\u8258\u8239\u7684\u8231\u4e2d\u53c8\u8d70\u51fa\u5341\u4f59\u4e2a\u7a7f\u7740\u9ec4\u8272\u957f\u886b\u7684\u6c49\u5b50\uff0c\u7f2a\u4e03\u5a18\u9053\uff1a\u201c\u4f60 \u770b\uff0c\u4ed6\u4eec\u600e\u4e48\u7a7f\u7740\u8fd9\u79cd\u8863\u670d\u3002\u201d \u3000\u3000\u6d77\u76d7\u800c\u7a7f\u957f\u886b\u7684\uff0c\u7684\u786e\u662f\u7edd\u65e0\u4ec5\u6709\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u629a\u989d\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u83ab\u975e\u662f\u9ec4\u6d77\u2018\u6cbf\u6d77\u5341\u6c99\u2019\u91cc\u7684\u6d77\u76d7\uff0c\u53ef\u662f\u2026\u201d\u4ed6\u5fae\u4e00\u601d \u7d22\uff0c\u63a5\u7740\u9053\uff1a\u201c\u7edd\u5bf9\u662f\u4e86\uff0c\u82e5\u662f\u4e1c\u6d77\u91cc\u7684\u6d77\u76d7\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4eba\u6765\u6253\u6211\u4eec\u8fd9\u8258\u8239\u7684\u4e3b\u610f\u7684\u3002\u201d \u3000\u3000\u7f2a\u4e03\u5a18\u9053\uff1a\u201c\u4f60\u8bf4\u4ed6\u4eec\u662f\u2018\u91d1\u5b57\u6c99\u2019\u3001\u2018\u9ec4\u5b50\u6c99\u2019\u3001\u2018\u51b7\u5bb6\u6c99\u2019\u8fd8\u6709\u90a3\u4e9b\u4ec0\u4e48\u2018\u5927 \u6c99\u2019\u3001\u2018\u5317\u6c99\u2019\u7684\u4e00\u5927\u7fa4\u6d77\u76d7\u5417\uff1f\u542c\u8bf4\u90a3\u4e9b\u6d77\u76d7\u88ab\u2018\u7389\u9aa8\u9b54\u2019\u5168\u6536\u670d\u4e86\uff0c\u4e0d\u51fa\u9ec4\u6d77\u505a\u6848 \u7684\u5440\uff0c\u600e\u4e48\u4f1a\u5df4\u5df4\u5730\u8dd1\u5230\u4e1c\u6d77\u6765\u5462\uff1f\u201d \u3000\u3000\u4ed6\u8bed\u6c14\u867d\u7136\u8fd8\u662f\u6ee1\u4e0d\u5728\u4e4e\uff0c\u4f46\u5176\u4e2d\u5df2\u786e\u4e4e\u6ca1\u6709\u4e86\u8f7b\u8511\u7684\u6210\u4efd\u3002 \u3000\u3000\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u90a3\u4e09\u8258\u8239\u91cc\u53c8\u4f20\u6765\u4e1d\u7af9\u5439\u5f04\u7684\u58f0\u97f3\uff0c\u4e00\u9762\u9ed1\u5e95\u4e0a\u7ee3\u7740\u4e24\u6bb5\u767d\u8272\u67af\u9aa8 \u7684\u65d7\u5b50\uff0c\u5189\u5189\u5347\u4e0a\u8239\u6845\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u671d\u7f28\u4e03\u5a18\u7b11\u9053\uff1a\u201c\u8fd9\u5e2e\u5bb6\u4f19\u7684\u6392\u573a\u5012\u771f\u4e0d\u5c0f\u3002\u201d \u3000\u3000\u7f28\u4e03\u5a18\u9053\uff1a\u201c\u8fd9\u4e9b\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5f3a\u76d7\uff0c\u73b0\u5728\u5374\u5168\u90fd\u4e00\u4e2a\u4e2a\u89c4\u89c4\u77e9\u77e9\uff0c\u60f3\u6765\u4e00\u5b9a\u662f\u88ab \u90a3\u2018\u7389\u9aa8\u9b54\u2019\u5236\u5f97\u670d\u670d\u8d34\u8d34\u7684\u3002\u201d \u3000\u3000\u5979\u4e00\u56de\u5934\uff0c\u671b\u7740\u65e0\u6781\u5c9b\u4e3b\u9053\uff1a\u201c\u5582\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u8fd9\u4e2a\u2018\u7389\u9aa8\u9b54\u2019\u5012\u5e95\u662f\u600e\u6837\u4e00\u4e2a\u4eba \u5440\uff1f\u201d \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u7b11\u9053\uff1a\u201c\u4f60\u8fd8\u6307\u671b\u6211\u77e5\u9053\u8fd9\u4e9b\u5996\u9b54\u5c0f\u4e11\u7684\u6765\u5386\u5440\u3002\u201d \u3000\u3000\u4ed6\u53c8\u671d\u5f53\u4e2d\u90a3\u8258\u8239\u770b\u4e86\u4e00\u773c\u9053\uff1a\u201c\u4e0d\u8fc7\u8fd9\u4e2a\u2018\u7389\u9aa8\u9b54\u2019\u5012\u662f\u50cf\u771f\u6709\u4e24\u4e0b\u5b50\u7684\u3002\u201d\u80fd \u591f\u8ba9\u65e0\u6781\u5c9b\u4e3b\u8bf4\u201c\u771f\u6709\u4e24\u4e0b\u7684\u201d\uff0c\u6b64\u4eba\u4e5f\u5dee\u53ef\u6170\u4e86\u3002 \u3000\u3000\u201c\u5582\uff0c\u4f60\u8fd9\u4e9b\u5e74\u53c8\u6ca1\u6709\u5728\u5916\u8d70\u52a8\u8fc7\uff0c\u600e\u4e48\u4f1a\u77e5\u9053\u4ed6\u771f\u6709\u4e24\u4e0b\u5462\uff1f\u201d\u7f2a\u4e03\u5a18\u6000\u7591\u5730\u95ee \u9053\uff0c\u201c\u6211\u8d77\u5148\u4e5f\u4e0d\u77e5\u9053\uff0c\u524d\u4e9b\u5e74\u6211\u4eec\u5c9b\u4e0a\u7ba1\u82b1\u6728\u7684\u8001\u5218\uff0c\u5230\u5982\u81ec\u57ce\u53bb\u4e70\u6843\u82b1\u7684\u82b1\u7c7d\uff0c\u56de \u6765\u65f6\u544a\u8bc9\u6211\u8bf4\uff0c\u9ec4\u6d77\u5341\u6c99\u7684\u6d77\u76d7\uff0c\u5168\u90fd\u88ab\u4e00\u4e2a\u53eb\u2018\u7389\u9aa8\u9b54\u2019\u7684\u6536\u670d\u4e86\uff0c\u8fde\u5f53\u5e74\u7eb5\u6a2a\u5357\u6c99 \u7684\u6d89\u6d77\u91d1\u9cd6\u5e9e\u58eb\u6e5b\uff0c\u5168\u90fd\u88ab\u4ed6\u5236\u5f97\u670d\u670d\u8d34\u8d34\u3002\u6211\u5f53\u65f6\u542c\u4e86\uff0c\u867d\u7136\u89c9\u5f97\u5947\u602a\uff0c\u4f46\u5b9e\u5728\u4e5f\u6ca1 \u6709\u5728\u610f\uff0c\u60f3\u4e0d\u5230\u4eca\u5929\u4eba\u5bb6\u5374\u627e\u5230\u6211\u5934\u4e0a\u6765\u4e86\u3002\u201d \u3000\u3000\u7f28\u4e03\u5a18\u7b11\u9053\uff1a\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u8fd9\u5bb6\u4f19\u597d\u771f\u771f\u7684\u4e0d\u77e5\u9053\u6211\u4eec\u7684\u5e95\u7ec6\u3002\u201d\u5979\u773c\u89d2\u4e71\u626b\uff0c\u53c8 \u9053\uff0c\u201c\u4ed6\u4ece\u9ec4\u6d77\u8f9b\u82e6\u7684\u8dd1\u5230\u4e1c\u6d77\u6765\uff0c\u96be\u9053\u662f\u4e13\u6765\u5bf9\u4ed8\u6211\u4eec\u8fd9\u6761\u8239\u7684\u5417\uff1f\u90a3\u6211\u5012\u8981\u770b\u770b\u4ed6 \u5230\u5e95\u600e\u6837\u5389\u5bb3\u3002\u201d \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u7b11\u9053\uff1a\u201c\u4ed6\u6bd4\u4f60\u4e00\u5b9a\u5dee\u8fdc\u4e86\uff0c\u4f60\u8981\u662f\u60f3\u505a\u5f3a\u76d7\uff0c\u6015\u4e0d\u8fde\u5357\u6d77\u7684\u4eba\u90fd\u6536\u7f57\u4e86 \u6765\u624d\u602a\u3002\u201d \u3000\u3000\u4ed6\u4eec\u592b\u5987\u4e24\u4eba\uff0c\u4ecd\u5728\u8bf4\u7b11\u7740\uff0c\u6839\u672c\u5c06\u6d77\u76d7\u6765\u88ad\u7684\u4e8b\uff0c\u770b\u5f97\u592a\u5e73\u6de1\u4e86\u3002 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c \u4e94 \u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c \u4e94 \u7ae0 \u3000\u3000\u8fd9\u65f6\u90a3\u4e09\u8258\u8239\u90fd\u5df2\u8fd1\uff0c\u8239\u4e0a\u52a8\u9759\u66f4\u53ef\u6e05\u695a\u770b\u89c1\u3002\u6e10\u6e10\u5730\uff0c\u4e09\u8239\u8ddd\u65e0\u6781\u5c9b\u4e3b\u4e4b\u8239\u6108\u6765 \u6108\u8fd1\uff0c\u76f8\u8ddd\u5927\u7ea6\u8fd8\u6709\u4e8c\u4e09\u5341\u4e08\u65f6\uff0c\u8239\u9996\u5927\u6c49\u4e00\u58f0\u53f7\u89d2\uff0c\u7acb\u523b\u5378\u4e0b\u4e86\u5e06\uff0c\u987f\u65f6\u901f\u5ea6\u6162\u4e86\u4e0b \u6765\u3002 \u3000\u3000\u65e0\u6068\u751f\u89c1\u8fd9\u6d77\u76d7\u8239\u679c\u771f\u662f\u51b2\u7740\u81ea\u5df1\u6765\u7684\uff0c\u4e0d\u7531\u51b7\u7b11\u4e00\u58f0\u3002\u90a3\u54c1\u5b57\u5f62\u4e09\u8239\u4e3a\u9996\u7684\u4e00\u53ea \u8239\u5934\uff0c\u53c8\u662f\u4e00\u58f0\u53f7\u89d2\u9e23\u54cd\uff0c\u8239\u8237\u4e24\u65c1\u7684\u6c34\u624b\u970d\u5730\u606d\u8eab\u633a\u7acb\uff0c\u4ece\u8231\u4e2d\u7f13\u7f13\u8d70\u51fa\u4e00\u4eba\u6765\uff0c\u53ea \u89c1\u6b64\u4eba\u5e74\u7ea6\u56db\u5341\uff0c\u9762\u5982\u9ec4\u8721\uff0c\u4e00\u88ad\u9ec4\u886b\u53ca\u5730\uff0c\u66f4\u663e\u5f97\u602a\u5f02\uff0c\u65e0\u6068\u751f\u89c1\u4f17\u6c34\u624b\u5bf9\u4ed6\u6267\u793c\u6781 \u606d\uff0c\u5fc3\u60f3\u8fd9\u4eba\u5fc5\u662f\u4e09\u8239\u4e2d\u9996\u9886\u4eba\u7269\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u5374\u51b7\u7b11\u9053\uff1a\u201c\u4e00\u4e2a\u6d77\u76d7\u4e5f\u6709\u8fd9\u4e48\u591a\u81ed\u6392\u573a\u3002\u201d \u3000\u3000\u90a3\u9ec4\u9762\u6c49\u5b50\u8d70\u5728\u8239\u9996\uff0c\u5411\u65e0\u6781\u5c9b\u4e3b\u8fd9\u8fb9\u62b1\u62f3\u4e00\u63d6\uff0c\u5f00\u53e3\u9053\uff1a\u201c\u9ec4\u5b50\u6c99\u603b\u8235\u4e3b\u6210\u4e00\u9752 \u5949\u547d\u95ee\u5019\u65e0\u6781\u5c9b\u4e3b\u4fea\u5b89\u3002\u201d \u3000\u3000\u8fd9\u65f6\u8239\u5df2\u51fa\u6c5f\uff0c\u6d77\u4e0a\u98ce\u6d9b\u6e10\u5927\uff0c\u76f8\u8ddd\u4e8c\u4e09\u5341\u4e08\u8fdc\uff0c\u90a3\u6210\u4e00\u9752\u6240\u53d1\u4e4b\u58f0\u97f3\u4ecd\u6781\u6e05\u6670\u5730 \u4f20\u5230\u65e0\u6781\u5c9b\u4e3b\u8239\u4e0a\uff0c\u8db3\u89c1\u4ed6\u529f\u529b\u6df1\u539a\u3002 \u3000\u3000\u65e0\u6068\u751f\u51b7\u54fc\u4e00\u58f0\uff0c\u626c\u58f0\u9053\uff1a\u201c\u5c31\u8bf7\u6210\u8235\u4e3b\u56de\u4e0a\u8d35\u5e2e\u4e3b\uff0c\u6211\u4e1c\u6d77\u65e0\u6781\u5c9b\u4e3b\u4e45\u4ef0\u5927\u540d\uff0c \u53ea\u662f\u65e0\u6687\u62dc\u4f1a\u3002\u201d \u3000\u3000\u7f2a\u4e03\u5a18\u5374\u89c1\u4ee5\u6210\u4e00\u9752\u8fd9\u529f\u529b\u5c45\u7136\u81e3\u670d\u90a3\u201c\u7389\u9aa8\u9b54\u201d\u624b\u4e0b\uff0c\u60f3\u6765\u90a3\u201c\u7389\u9aa8\u9b54\u201d\u5fc5\u7136\u751a \u662f\u4e0d\u51e1\uff0c\u5fc3\u4e2d\u8f7b\u89c6\u4e4b\u610f\u987f\u706d\u3002 \u3000\u3000\u90a3\u6d77\u76d7\u8239\u4e0a\u6c34\u624b\u89c1\u65e0\u6068\u751f\u4ecd\u5750\u539f\u5904\u52a8\u4e5f\u4e0d\u52a8\uff0c\u672a\u66fe\u52a8\u5bb9\uff0c\u663e\u7136\u751a\u6012\uff0c\u90a3\u6210\u4e00\u9752\u56de\u9996 \u7565\u4e00\u6325\u624b\uff0c\u4f17\u76d7\u7acb\u523b\u5b89\u9759\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u6210\u4e00\u9752\u53c8\u9053\uff1a\u201c\u655d\u5e2e\u4e3b\u66fe\u547d\u5728\u4e0b\u7565\u5907\u7c97\u9152\u4e3a\u5c9b\u4e3b\u63a5\u98ce\uff0c\u656c\u8bf7\u5c9b\u4e3b\u8fc7\u6765\u4e00\u53d9\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u5fc3\u4e2d\u6697\u5947\uff0c\u4f46\u4ecd\u56de\u9053\uff1a\u201c\u8d35\u5e2e\u4e3b\u7f8e\u610f\uff0c\u655d\u592b\u5987\u5fc3\u9886\u4e86\uff0c\u53ea\u662f\u5c1a\u6709\u8981\u4e8b\u5fc5\u987b\u56de \u5c9b\uff0c\u5c31\u8bf7\u9601\u4e0b\u4ee3\u5411\u8d35\u5e2e\u4e3b\u81f4\u610f\u3002\u201d \u3000\u3000\u4ee5\u65e0\u6781\u5c9b\u4e3b\u4e4b\u8eab\u4efd\uff0c\u7adf\u5ba2\u6c14\u5730\u548c\u8fd9\u6d77\u76d7\u6253\u4ea4\u9053\uff0c\u90a3\u7389\u9aa8\u9b54\u5728\u6d77\u4e0a\u7684\u5a01\u52bf\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u6210\u4e00\u9752\u5374\u9053\uff1a\u201c\u5373\u662f\u5982\u6b64\uff0c\u8fd8\u5f85\u6210\u67d0\u656c\u5c9b\u4e3b\u592b\u5987\u4e00\u676f\uff0c\u7565\u8868\u656c\u610f\u3002\u201d \u3000\u3000\u8bf4\u7f62\u81ea\u8eab\u540e\u62ff\u8d77\u4e09\u53ea\u6c34\u6676\u9152\u676f\uff0c\u53c8\u62ff\u8d77\u4e00\u53ea\u7fe1\u7fe0\u58f6\uff0c\u5012\u6ee1\u4e09\u676f\uff0c\u5148\u4e00\u624b\u6301\u7740\u4e00\u676f\uff0c \u53cc\u624b\u4e00\u626c\uff0c\u4e24\u53ea\u9152\u676f\u7adf\u5e73\u5e73\u7a33\u7a33\u98de\u51fa\u3002 \u3000\u3000\u90a3\u9152\u676f\u73b2\u73d1\u900f\u4eae\uff0c\u9152\u66f4\u662f\u78a7\u7eff\u5982\u7389\uff0c\u4e24\u9053\u7eff\u5149\u7a33\u7a33\u98de\u5230\u65e0\u6781\u5c9b\u4e3b\u8239\u4e0a\uff0c\u7adf\u7136\u4e00\u6ef4\u672a \u503e\u3002 \u3000\u3000\u8fd9\u65f6\u4e24\u65b9\u8239\u53ea\u867d\u53c8\u8fd1\u4e86\u4e00\u4e9b\uff0c\u4f46\u5c11\u8bf4\u4ecd\u6709\u4e8c\u5341\u4e08\u8bb8\uff0c\u6210\u4e00\u9752\u4e00\u626c\u95f4\uff0c\u7adf\u5c06\u4e24\u676f\u9152\u7a33 \u7a33\u9001\u4e86\u8fc7\u6765\uff0c\u65e0\u8bba\u52b2\u9053\uff0c\u5185\u529b\u90fd\u81fb\u4e0a\u4e58\u3002 \u3000\u3000\u90a3\u65e0\u6068\u751f\u5374\u662f\u51b7\u7b11\u4e00\u58f0\uff0c\u957f\u8896\u4e00\u62c2\u4e4b\u95f4\uff0c\u4e00\u80a1\u67d4\u548c\u4e4b\u529b\u626b\u51fa\uff0c\u90a3\u4e24\u53ea\u9152\u676f\u7adf\u4f3c\u5728\u7a7a \u4e2d\u505c\u4e86\u7247\u523b\uff0c\u624d\u7f13\u7f13\u843d\u5728\u684c\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u4e0a\u4e58\u6c14\u529f\u7acb\u65f6\u5c06\u7fa4\u76d7\u770b\u5f97\u76ee\u77aa\u53e3\u5446\u3002\u90a3\u6210\u4e00\u9752\u5374\u9762\u4e0d\u6539\u8272\u5730\u7aef\u8d77\u9152\u676f\uff0c\u9053\u58f0\uff1a \u201c\u8bf7\u3002\u201d\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u65e0\u6068\u751f\u9762\u867d\u9732\u51fa\u4e0d\u5c51\u4e4b\u8272\uff0c\u5fc3\u4e2d\u8457\u5b9e\u4e3a\u96be\uff0c\u4ed6\u77e5\u90a3\u201c\u7389\u9aa8\u9b54\u201d\u4e0d\u4ec5\u6b66\u827a\u9ad8\u5f3a\uff0c\u5c24\u5176 \u7cbe\u4e8e\u767e\u6bd2\uff0c\u83ab\u8981\u5728\u6b64\u9152\u4e2d\u4e0b\u4e86\u4ec0\u4e48\u5947\u6bd2\u3002 \u3000\u3000\u518d\u770b\u90a3\u676f\u4e2d\u9152\u8272\u78a7\u7eff\uff0c\u5206\u660e\u662f\u6781\u4f73\u9187\u9152\uff0c\u6b63\u6c89\u541f\u95f4\uff0c\u89c1\u6210\u4e00\u9752\uff0c\u5df2\u4e00\u53e3\u996e\u4e0b\uff0c\u65e0\u6781 \u5c9b\u4e3b\u4f55\u7b49\u8eab\u4efd\uff0c\u5c82\u80fd\u793a\u5f31\uff0c\u6697\u5fd6\u7f2a\u4e03\u5a18\u6216\u4f1a\u529f\u529b\u4e0d\u8db3\uff0c\u81ea\u5df1\u5185\u529f\u4fee\u7ec3\u5df2\u8fc7\u91d1\u521a\u4e0d\u574f\u4e4b\u5730 \u6b65\uff0c\u4efb\u4ed6\u4ec0\u4e48\u6bd2\u7269\u5fc5\u80fd\u903c\u51fa\uff0c\u5f53\u4e0b\u626c\u58f0\u9053\uff1a\u201c\u62d9\u8346\u4e0d\u5584\u996e\u9152\uff0c\u8001\u592b\u4e00\u5e76\u996e\u4e86\u3002\u201d\u4ef0\u9996\u5c06 \u4e24\u676f\u996e\u4e0b\uff0c\u53cc\u624b\u5fae\u6325\uff0c\u4e24\u53ea\u7a7a\u9152\u676f\u5982\u7bad\u98de\u56de\uff0c\u6210\u4e00\u9752\u7b49\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u4e24\u53ea\u6c34\u6676\u676f\u5b50 \u201c\u535f\u201d\u201c\u535f\u201d\u4e24\u58f0\uff0c\u7adf\u81ea\u6df1\u6df1\u9677\u5165\u8239\u677f\uff0c\u76f4\u6ca1\u4e8e\u5e95\uff0c\u5374\u662f\u5b8c\u6574\u65e0\u7f3a\u3002 \u3000\u3000\u65e0\u6068\u751f\u559d\u58f0\uff1a\u201c\u8bf7\u8ba9\u8def\u3002\u201d\u8239\u4e0a\u5e06\u6868\u9f50\u4e3e\uff0c\u52a0\u901f\u5411\u524d\u5f00\u52a8\uff0c\u6210\u4e00\u9752\u4e00\u6325\u624b\uff0c\u4e09\u53ea\u6d77 \u76d7\u8239\u7acb\u65f6\u5411\u65c1\u4e00\u8f6c\uff0c\u8ba9\u5f00\u6c34\u8def\u3002 \u3000\u3000\u90a3\u77e5\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u7136\u9707\u5929\u4e00\u58f0\u66b4\u54cd\uff0c\u65e0\u6068\u751f\u7684\u5927\u8239\u7a81\u7136\u7531\u4e2d\u65ad\u88c2\uff0c\u5927\u80a1\u6c34\u9f99\u55b7\u5165\u8239 \u5185\uff0c\u6845\u6746\u4e5f\u8f70\u7136\u65ad\u5012\uff0c\u788e\u6728\u98de\u4e2d\uff0c\u4e00\u80a1\u6781\u6d53\u914c\u786b\u78fa\u70df\u5473\u5f25\u6f2b\u6ee1\u5929\uff0c\u663e\u7136\u8239\u8eab\u662f\u88ab\u70b8\u836f\u6240 \u6bc1\u3002 \u3000\u3000\u8239\u4e0a\u6c34\u624b\u8840\u8089\u6a2a\u98de\uff0c\u60e8\u547c\u58f0\u9707\u5929\uff0c\u65e0\u6068\u751f\u7f2a\u4e03\u5a18\u5750\u5728\u8239\u9996\uff0c\u4e5f\u662f\u9669\u4e9b\u8dcc\u5012\uff0c\u547c\u547c\u4e24 \u638c\u6392\u5f00\u6d53\u70df\uff0c\u77a5\u89c1\u90a3\u4e09\u53ea\u6d77\u76d7\u8239\u5df2\u5168\u8fdc\u53bb\u3002\u4e0d\u7531\u5927\u559d\u4e00\u58f0\uff1a\u201c\u9f20\u8f88\u6562\u5c14\uff01\u201d\u4e00\u628a\u7275\u7740\u7f28 \u4e03\u5a18\uff0c\u594b\u8eab\u8dc3\u8d77\uff0c\u7adf\u5728\u6d77\u9762\u4e0a\u5c55\u7edd\u9876\u8f7b\u529f\u8d76\u4e86\u4e0a\u53bb\uff01 \u3000\u3000\u6d77\u98ce\u4e0d\u5c0f\uff0c\u4e09\u53ea\u76d7\u8239\u5e06\u6868\u5e76\u4e3e\uff0c\u53bb\u52bf\u6781\u901f\uff0c\u65e0\u6068\u751f\u592b\u5987\u7adf\u5728\u9cb8\u6ce2\u4e0a\u8e0f\u6ce2\u98de\u884c\uff0c\u978b\u9762 \u4e0a\u90fd\u672a\u6cbe\u6e7f\uff01 \u3000\u3000\u4e09\u53ea\u6d77\u76d7\u8239\u53bb\u52bf\u867d\u901f\uff0c\u65e0\u6781\u5c9b\u4e3b\u592b\u5987\u5374\u51ed\u4e00\u53e3\u771f\u6c14\u5728\u6ce2\u6d9b\u5c16\u513f\u4e0a\u75be\u7eb5\uff0c\u7adf\u7136\u6e10\u6e10\u8d76 \u4e0a\u3002 \u3000\u3000\u65e0\u6068\u751f\u7684\u8f7b\u529f\u771f\u578b\u4e86\u7089\u706b\u7eaf\u9752\u5730\u6b65\uff0c\u7f2a\u4e03\u5a18\u529f\u529b\u867d\u7136\u7565\u900a\uff0c\u4f46\u5728\u4e08\u592b\u6276\u6301\u4e0b\uff0c\u4e5f\u662f \u901f\u5ea6\u60ca\u4eba\uff0c\u773c\u770b\u4e0e\u90a3\u4e09\u53ea\u5728\u8239\u95f4\u7684\u8ddd\u79bb\u8d8a\u6765\u8d8a\u8fd1\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u5ffd\u7136\u60f3\u8d77\u83c1\u513f\u8fd8\u5728\u8239\u4e0a\uff0c\u6025\u5fd9\u4e2d\u56de\u9996\u4e00\u770b\uff0c\u53ea\u89c1\u6b64\u523b\u5927\u8239\u5df2\u7ecf\u9010\u6e10\u6c89\u6ca1\uff0c\u4e00 \u4e2a\u5c11\u5973\u5374\u4f3c\u8e0f\u5728\u4e00\u7247\u6728\u677f\u4e0a\u968f\u6ce2\u8d77\u4f0f\uff0c\u6b63\u662f\u81ea\u5df1\u7231\u5973\uff0c\u5fc3\u60f3\u83c1\u513f\u8f7b\u529f\u6781\u4f73\uff0c\u5fc5\u7136\u65e0\u4e8b\uff0c \u5f53\u4e0b\u653e\u5fc3\u6025\u8d76\u3002 \u3000\u3000\u6210\u4e00\u9752\u89c1\u65e0\u6781\u5c9b\u4e3b\u592b\u5987\u8e0f\u6ce2\u800c\u884c\u5c45\u7136\u901f\u5ea6\u60ca\u4eba\uff0c\u4e0d\u7531\u5927\u9a87\uff0c\u4e00\u9762\u547d\u624b\u4e0b\u52aa\u529b\u52a0\u901f\uff0c \u4e00\u9762\u547d\u90a3\u4e00\u6279\u9ec4\u886b\u6c49\u5b50\u5404\u7ad9\u6709\u5229\u4f4d\u7f6e\uff0c\u6253\u7b97\u4e58\u65e0\u6068\u751f\u592b\u5987\u4e0a\u6765\u5c31\u5730\u6253\u4e00\u4e2a\u63aa\u624b\u4e0d\u53ca\u3002 \u3000\u3000\u90a3\u6279\u9ec4\u886b\u6c49\u5b50\u4e2a\u4e2a\u90fd\u662f\u7279\u9009\u6b66\u58eb\uff0c\u53c8\u4e45\u7ecf\u8bad\u7ec3\uff0c\u867d\u89c1\u65e0\u6068\u751f\u6765\u52bf\u9a87\u4eba\uff0c\u4f46\u5404\u5c31\u5404\u4f4d\uff0c \u4e1d\u6beb\u4e0d\u4e71\u3002 \u3000\u3000\u65e0\u6068\u751f\u89c1\u5927\u8239\u70b8\u6bc1\uff0c\u5fc3\u4e2d\u6025\u6012\uff0c\u731b\u63d0\u4e00\u53e3\u6c14\uff0c\u4e00\u62c9\u7f2a\u4e03\u5a18\uff0c\u85c9\u7740\u4e00\u4e2a\u6ce2\u6d6a\u6253\u4e0a\uff0c\u594b \u8eab\u8dc3\u8d77\uff0c\u5b9b\u5982\u4e24\u53ea\u5927\u9e1f\u98de\u6251\u4e0b\u6765\u6210\u4e00\u9752\u521a\u5e03\u7f6e\u597d\uff0c\u56de\u9996\u4e00\u770b\uff0c\u65e0\u6068\u751f\u592b\u5987\u5df2\u6251\u4e0b\uff0c\u5fc3\u4e2d \u5927\u60ca\uff0c\u89c1\u4e24\u4eba\u6251\u5411\u8239\u5c3e\u5de6\u65b9\uff0c\u90a3\u91cc\u4e09\u4e2a\u9ec4\u886b\u6c49\u5b50\u51e0\u4e4e\u540c\u65f6\u7531\u4e09\u4e2a\u4e0d\u540c\u65b9\u4f4d\u9012\u51fa\u5175\u5203\uff0c\u663e \u7136\u8bad\u7ec3\u6709\u7d20\u3002 \u3000\u3000\u90a3\u77e5\u65e0\u6068\u751f\u53cc\u8896\u4e00\u5377\uff0c\u53ea\u89c1\u5f97\u4e00\u7247\u6a21\u7cca\u7684\u5f71\u5b50\uff0c\u547c\u547c\u51e0\u58f0\uff0c\u4e09\u822c\u5175\u5203\u9f50\u9f50\u98de\u8d77\uff0c\u535f \u535f\u4e4b\u58f0\u4e2d\uff0c\u4e09\u4e2a\u9ec4\u886b\u6c49\u5b50\u98de\u843d\u6d77\u4e2d\uff0c\u8eab\u4f53\u72b9\u672a\u6cbe\u7740\u6d77\u9762\uff0c\u5df2\u81ea\u6b7b\u53bb\uff01 \u3000\u3000\u6210\u4e00\u9752\u54ea\u6599\u5230\u65e0\u6781\u5c9b\u4e3b\u5982\u6b64\u5a01\u52bf\uff0c\u4e0d\u7531\u80c6\u602f\uff0c\u5374\u89c1\u8239\u5c3e\u53f3\u65b9\u4e94\u4e2a\u9ec4\u886b\u6c49\u5b50\u6309\u7740\u4e94\u884c \u4f4d\u7f6e\uff0c\u4e92\u76f8\u63a9\u62a4\u4e0b\u56f4\u51fb\u8fc7\u53bb\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u5411\u5176\u4ed6\u4e8c\u8239\u4e0b\u547d\u9053\uff1a\u201c\u7ee7\u7eed\u52a0\u901f\u56de\u8235\uff01\u201d\u4e00\u9762 \u6296\u8d77\u624b\u4e2d\u957f\u5251\u8dc3\u5411\u8239\u5c3e\u3002 \u3000\u3000\u201c\u9ec4\u5b50\u6c99\u201d\u6d77\u76d7\u5e2e\u5728\u672a\u5f52\u670d\u201c\u7389\u9aa8\u9b54\u201d\u524d\uff0c\u5c31\u7d20\u4ee5\u6d77\u5e95\u529f\u592b\u79f0\u9738\u4e1c\u6d77\uff0c\u53ca\u5f52\u5165\u201c\u7389 \u9aa8\u9b54\u201d\u9ebe\u4e0b\uff0c\u6f5c\u6c34\u8bad\u7ec3\u66f4\u662f\u7279\u522b\u6ce8\u91cd\uff0c\u90a3\u70b8\u6bc1\u65e0\u6781\u5c9b\u4e3b\u5750\u8239\u5fc5\u662f\u6210\u4e00\u9752\u7684\u624b\u4e0b\u6f5c\u6c34\u592b\u7684 \u6770\u4f5c\uff0c\u53ea\u662f\u8fde\u65e0\u6068\u751f\u8fd9\u7b49\u4eba\u7269\u90fd\u672a\u53d1\u89c9\u8239\u5e95\u88ab\u505a\u4e86\u624b\u811a\uff0c\u8fd9\u4e9b\u6f5c\u6c34\u592b\u7684\u529f\u592b\u53ef\u60f3\u800c\u77e5\u4e86\uff01 \u3000\u3000\u4e14\u8bf4\u6210\u4e00\u9752\u89c1\u90a3\u4e94\u4e2a\u9ec4\u886b\u6c49\u4e86\u4e43\u662f\u8235\u4e0b\u4e00\u6d41\u597d\u624b\uff0c\u6240\u7ed3\u4e94\u884c\u7684\u65b9\u4f4d\u5965\u5999\u65e0\u6bd4\uff0c\u5fc3\u60f3 \u5fc5\u80fd\u4e00\u963b\u65e0\u6068\u751f\u6c14\u7130\uff0c\u54ea\u77e5\u65e0\u6068\u751f\u54fc\u7136\u51b7\u7b11\uff0c\u53cc\u8896\u62c2\u5904\uff0c\u4e24\u80a1\u75be\u52b2\u65e0\u6bd4\u7684\u5185\u529b\u5c06\u4e94\u5251\u4e00 \u9f50\u9707\u5f00\uff0c\u7f2a\u4e03\u5a18\u8eab\u5f62\u4e00\u5708\uff0c\u4e00\u58f0\u60e8\u53f7\uff0c\u4e00\u4e2a\u9ec4\u886b\u6c49\u5b50\u5df2\u5012\u6bd9\u5730\u4e0a\uff0c\u4e94\u884c\u9635\u4e00\u7834\uff0c\u4e24\u4e09\u4e2a \u7167\u9762\u95f4\uff0c\u8fd1\u5728\u5c3a\u5904\u7684\u6210\u4e00\u9752\u8fde\u63d2\u624b\u90fd\u6ca1\u6709\u673a\u4f1a\uff0c\u5176\u4f59\u56db\u4eba\u90fd\u5206\u522b\u88ab\u65e0\u6781\u5c9b\u4e3b\u592b\u5987\u626b\u4eba\u6d77 \u4e2d\u3002 \u3000\u3000\u65e0\u6068\u751f\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u5ffd\u611f\u80f8\u4e2d\u4e00\u585e\u2014\u2014\u867d\u7136\u662f\u90a3\u4e48\u8f7b\u5fae\uff0c\u4f46\u65e0\u6068\u751f\u8fd9\u79cd\u4e0d\u574f\u4e4b\u8eab \u5c45\u7136\u6709\u6b64\u73b0\u8c61\uff0c\u4ed6\u7acb\u523b\u77e5\u9053\u5fc5\u662f\u90a3\u9152\u4e2d\u4e4b\u6bd2\u5f00\u59cb\u53d1\u4f5c\uff0c\u540c\u65f6\u53c8\u60f3\u5230\u7389\u9aa8\u9b54\u5373\u7528\u6765\u6bd2\u81ea\u5df1\uff0c \u4e00\u5b9a\u7528\u7684\u662f\u6700\u5389\u5bb3\u7684\u6bd2\u836f\uff0c\u81ea\u5df1\u5750\u8239\u5df2\u6bc1\uff0c\u8981\u60f3\u8131\u6b64\u832b\u832b\u5927\u6d77\u5fc5\u5b9a\u8981\u5728\u6bd2\u53d1\u4ee5\u524d\u5c06\u5bf9\u65b9 \u5c3d\u6570\u6d88\u706d\uff0c\u593a\u4e0b\u6b64\u8239\u624d\u597d\uff0c\u5f53\u4e0b\u4e00\u62c9\u7f2a\u4e03\u5a18\u7389\u624b\uff0c\u53cc\u53cc\u6251\u5411\u8231\u5185\u3002 \u3000\u3000\u5f53\u524d\u4e00\u4eba\u6b63\u662f\u6210\u4e00\u9752\uff0c\u65e0\u6068\u751f\u53cc\u638c\u547c\u5730\u63a8\u51fa\uff0c\u76f4\u88ad\u5bf9\u65b9\u80f8\u524d\uff0c\u7f2a\u4e03\u5a18\u5374\u51cc\u7a7a\u8dc3\u8d77\uff0c \u8d8a\u8fc7\u6210\u4e00\u9752\u5934\u4e0a\uff0c\u843d\u5165\u8231\u5185\u3002 \u3000\u3000\u6210\u4e00\u9752\u89c1\u5bf9\u65b9\u638c\u52bf\u592a\u901f\uff0c\u53ea\u597d\u62fc\u529b\u51fb\u51fa\u4e00\u638c\uff0c\u201c\u78b0\u201d\u5730\u4e00\u58f0\uff0c\u6210\u4e00\u9752\u5f53\u5802\u9000\u540e\u6570\u6b65\uff0c \u80f8\u4e2d\u4e00\u9635\u8840\u6c14\u7ffb\u817e\u3002 \u3000\u3000\u6210\u4e00\u9752\u5728\u672a\u5f52\u4f0f\u7389\u9aa8\u9b54\u624b\u4e0b\u5c31\u662f\u662f\u2018\u9ec4\u5b50\u6c99\u2019\u7684\u9996\u9886\uff0c\u4e00\u8eab\u6b66\u827a\u9a70\u8a89\u4e1c\u6d77\uff0c\u540e\u6765\u867d \u4e3a\u7389\u9aa8\u9b54\u6536\u670d\uff0c\u4ecd\u7136\u662f\u7389\u9aa8\u9b54\u624b\u4e0b\u6700\u5f97\u529b\u7684\u52a9\u624b\u4e4b\u4e00\uff0c\u6b64\u65f6\u4e00\u7167\u9762\u5c31\u88ab\u65e0\u6068\u751f\u6253\u5f97\u8840\u6c14 \u7ffb\u817e\uff0c\u5fc5\u4e2d\u81ea\u7136\u60ca\u9a87\u4e4b\u6781\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u65e0\u6068\u751f\u4e0d\u8fc7\u7528\u4e86\u516d\u6210\u529f\u529b\u800c\u5df2\u3002\u6b64\u65f6\u4ed6\u53c8\u662f\u51b7\u7b11\u4e00\u58f0\uff0c\u5355\u638c\u5fae\u626c\uff0c\u4e00\u80a1\u66f4 \u5f3a\u52b2\u98ce\u5411\u6210\u4e00\u9752\u51fb\u53bb\uff0c\u773c\u89d2\u5374\u98d8\u5411\u5de6\u9762\u5c06\u56f4\u4e0a\u6765\u7684\u53e6\u5916\u4e09\u4e2a\u9ec4\u886b\u6c49\u5b50\uff0c\u6210\u4e00\u9752\u6b64\u65f6\u52bf\u6210 \u9a91\u864e\uff0c\u53ea\u597d\u786c\u8d77\u5934\u76ae\u6253\u7b97\u518d\u786c\u63a5\u4e00\u62db\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5934\u53d1\u6839\u6839\u76f4\u7ad6\uff0c\u9ec4\u886b\u50cf\u662f\u7531\u5185\u88ab\u98ce\u704c\u6ee1\u4e00\u822c\uff0c\u5f20\u5f97\u6709\u5982\u5927\u5e06\uff0c\u58f0\u5a01\u7aef\u7684\u795e\u731b\u3002 \u5176\u5b9e\u4ed6\u5185\u5fc3\u5374\u6b63\u6697\u60e7\u4e0d\u77e5\u81ea\u5df1\u62fc\u529b\u4e00\u51fb\u80fd\u5426\u6321\u5f97\u4f4f\u4eba\u5bb6\u8f7b\u63cf\u6de1\u5199\u7684\u4e00\u4e0b\u5462\uff1f \u3000\u3000\u90a3\u77e5\u4ed6\u7684\u638c\u529b\u624d\u9012\u51fa\uff0c\u90a3\u65e0\u6068\u751f\u5355\u638c\u7adf\u5fae\u5fae\u4e00\u7f29\uff0c\u6210\u4e00\u9752\u7acb\u611f\u81ea\u5df1\u5343\u94a7\u638c\u529b\u88ab\u4eba\u5438 \u4f4f\u5374\u6536\u4e0d\u80fd\uff01 \u3000\u3000\u65e0\u6068\u751f\u5355\u638c\u5411\u5de6\u4e00\u6325\uff0c\u628a\u6210\u4e00\u9752\u62fc\u547d\u53d1\u51fa\u7684\u638c\u529b\u786c\u786c\u7c98\u5411\u5de6\u8fb9\uff0c\u8fce\u5411\u51b2\u4e0a\u6765\u7684\u4e09\u4e2a \u9ec4\u886b\u4eba\u3002 \u3000\u3000\u6210\u4e00\u9752\u773c\u7741\u7741\u770b\u89c1\u524d\u9762\u662f\u4e09\u4e2a\u81ea\u5df1\u4eba\uff0c\u5374\u65e0\u6cd5\u6536\u56de\u81ea\u5df1\u638c\u529b\uff0c\u6025\u5f97\u4ed6\u6c57\u5982\u6d46\u51fa\uff0c\u4ecd \u7136\u65e0\u6d4e\u4e8e\u4e8b\uff0c\u53ea\u542c\u5f97\u8f70\u7136\u4e00\u58f0\uff0c\u6b63\u51b2\u4e0a\u7684\u4e09\u4eba\u7acb\u523b\u88ab\u6210\u4e00\u9752\u62fc\u529b\u53d1\u51fa\u7684\u4e00\u638c\u51fb\u5012\u5730\u4e0a\uff01 \u3000\u3000\u65e0\u6068\u751f\u8fd9\u62db\u4e0a\u4e58\u7684\u201c\u79fb\u82b1\u63a5\u6728\u201d\u5185\u529f\uff0c\u771f\u5999\u5230\u6781\u5904\uff0c\u53f3\u9762\u5176\u4ed6\u6d77\u76d7\u672c\u6765\u51c6\u5907\u56f4\u5c06\u4e0a \u7684\uff0c\u4e00\u65f6\u76ee\u77aa\u53e3\u5446\uff0c\u5446\u7acb\u4e0d\u77e5\u6240\u63aa\u3002 \u3000\u3000\u8239\u8231\u5185\u5f62\u52bf\u53c8\u81ea\u4e0d\u540c\uff0c\u7f2a\u4e03\u5a18\u65bd\u5f00\u7edd\u9876\u8f7b\u529f\uff0c\u914d\u5408\u7740\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u5728\u7fa4\u76d7\u4e2d\u5982\u7a7f \u82b1\u8774\u8776\u822c\uff0c\u5de6\u4e00\u638c\uff0c\u53f3\u4e00\u638c\uff0c\u6253\u5f97\u7fa4\u76d7\u4e0d\u4ea6\u5c14\u4e4e\uff0c\u5f80\u5f80\u4e00\u62db\u53d1\u51fa\uff0c\u8fde\u653b\u56db\u4e94\u4eba\uff0c\u4efb\u90a3\u7fa4 \u9ec4\u886b\u6d77\u76d7\u4e5f\u90fd\u662f\u7ecf\u6311\u9009\u51fa\u7684\u597d\u624b\uff0c\u90a3\u89c1\u8fc7\u7f2a\u4e03\u5a18\u8fd9\u7b49\u7edd\u9876\u8eab\u624b\uff0c\u4e00\u65f6\u4e00\u8fde\u51e0\u4e2a\u6c49\u5b50\u76f8\u7ee7 \u88ab\u70b9\u5012\u7532\u677f\u4e0a\u3002 \u3000\u3000\u4e14\u8bf4\u8f9b\u6377\u5728\u8239\u8eab\u70b8\u65ad\u7684\u65f6\u5019\uff0c\u88ab\u9707\u5f97\u6454\u51fa\u5c0f\u623f\uff0c\u4e00\u4e2a\u5927\u6d6a\u5c31\u5c06\u4ed6\u5377\u4eba\u5927\u6d77\u4e2d\uff0c\u4ed6\u7a74 \u9053\u88ab\u5236\u59cb\u7ec8\u662f\u4e00\u4e2a\u5377\u7740\u8eab\u9a71\u7684\u5c34\u5c2c\u59ff\u6001\uff0c\u4e0d\u80fd\u52a8\u5f39\u4e1d\u6beb\u3002\u8fd9\u65f6\u773c\u89c1\u6ce2\u6d9b\u4e00\u4e2a\u63a5\u7740\u4e00\u4e2a\uff0c \u5168\u8eab\u5374\u4e1d\u6beb\u4f7f\u4e0d\u51fa\u529b\uff0c\u773c\u770b\u5c31\u5f97\u846c\u8eab\u9cb8\u6ce2\u3002 \u3000\u3000\u4ed6\u611f\u89c9\u5230\u81ea\u5df1\u5728\u9010\u6e10\u4e0b\u6c89\uff0c\u867d\u7136\u5076\u800c\u4e00\u4e2a\u6380\u6d6a\u53c8\u5c06\u4ed6\u4e3e\u51fa\u6d77\u9762\uff0c\u4f46\u5c24\u5176\u96be\u53d7\u7684\u662f\u8165 \u54b8\u7684\u6d77\u6c34\u4ece\u9f3b\u4e2d\u3001\u8033\u4e2d\u3001\u53e3\u4e2d\u4e0d\u7531\u63a7\u5236\u5730\u704c\u4eba\uff0c\u4ed6\u4f3c\u4e4e\u611f\u89c9\u5230\u6d51\u8eab\u90fd\u5728\u80bf\u80c0\u2014\u2014\u6e10\u6e10\uff0c \u4ed6\u6108\u6765\u6108\u611f\u7a92\u606f\uff0c\u773c\u524d\u5b9b\u5982\u6b7b\u795e\u51fa\u5de8\u7075\u638c\u7d27\u634f\u7740\u4ed6\u7684\u54bd\u5589\uff0c\u800c\u4e14\u6e10\u6536\u7f29\u2014\u2014\u4e00\u970e\u65f6\u95f4\uff0c \u8111\u6d77\u4e2d\u6bd4\u95ea\u7535\u8fd8\u5feb\u5730\u6d6e\u8fc7\u4e00\u4e9b\u5f71\u5b50\uff0c\u7236\u6bcd\u53d7\u4eba\u51cc\u8fb1\u800c\u6b7b\u7684\u60c5\u5f62\uff0c\u6885\u53d4\u6148\u7231\u7684\u8138\u5b54\uff0c\u751a\u81f3 \u90a3\u4faf\u4e8c\u53d4\u60b2\u6006\u7684\u8868\u60c5\u90fd\u4e00\u4e00\u98d8\u8fc7\u3002\u6700\u540e\u91d1\u6885\u9f84\u7684\u5029\u5f71\u5360\u636e\u4e86\u773c\u524d\u7684\u4e00\u5207\u201c\u5979\u73b0\u5728\u5728\u54ea \u91cc\uff1f\u201d\u4ed6\u8fd9\u523b\u7adf\u5ffd\u7136\u95ea\u8fc7\u8fd9\u6837\u4e00\u4e2a\u5ff5\u5934\u3002 \u3000\u3000\u4f46\u5ffd\u7136\uff0c\u8fd9\u4e00\u5207\u90fd\u6d88\u5931\u4e86\uff0c\u4ed6\u773c\u524d\u662f\u4e00\u7247\u58a8\u9ed1\uff0c\u6b7b\u795e\u5df2\u5728\u964d\u4e34\u4e86\u5ffd\u7136\uff0c\u53c8\u662f\u4e00\u4e2a\u5de8 \u6d6a\u4ece\u5e95\u4e0b\u6253\u6765\uff0c\u628a\u4e0b\u6c89\u4e2d\u7684\u8f9b\u6377\u7684\u5934\u90e8\u4e3e\u51fa\u4e86\u6d77\u9762\uff0c\u4f46\u4ed6\u8fde\u6323\u624e\u7684\u4f01\u56fe\u90fd\u6ca1\u6709\uff0c\u56e0\u4e3a\u90a3 \u88ab\u5236\u4f4f\u7684\u7a74\u9053\u4ee4\u4ed6\u5bf8\u6b65\u96be\u884c\u3002 \u3000\u3000\u8fd9\u65f6\u4e00\u58f0\u60ca\u559c\u7684\u547c\u58f0\u7a7f\u8fc7\u5de8\u6d9b\u6c79\u6d8c\u7684\u58f0\u54cd\u4f20\u5165\u8f9b\u6377\u7684\u8033\u4e2d\uff0c\u63a5\u7740\u4ed6\u611f\u5230\u80c1\u4e0b\u88ab\u4e00\u91cd \u7269\u731b\u6572\uff0c\u75db\u5f7b\u5fc3\u80ba\uff0c\u4f46\u7acb\u523b\u4ed6\u610f\u8bc6\u5230\u7a74\u9053\u5df2\u7ecf\u89e3\u4e86\uff0c\u4ed6\u53cc\u81c2\u4e00\u632f\uff0c\u6c34\u6dcb\u6dcb\u5730\u8dc3\u8eab\u51fa\u6d77\uff0c \u89c1\u524d\u9762\u4e00\u4eba\u8e0f\u677f\u51cc\u6ce2\u800c\u884c\uff0c\u6b63\u662f\u83c1\u513f\u3002 \u3000\u3000\u4ed6\u518d\u4f4e\u5934\u4e00\u770b\uff0c\u90a3\u88ab\u83c1\u513f\u63b7\u8fc7\u6765\u89e3\u5f00\u81ea\u5df1\u7a74\u9053\u7684\u201c\u91cd\u7269\u201d\uff0c\u4e0d\u8fc7\u662f\u4e00\u5c0f\u7247\u6728\u677f\uff01 \u3000\u3000\u83c1\u513f\u6d51\u8eab\u51e0\u4e4e\u6e7f\u900f\uff0c\u7ea2\u900f\u7684\u8138\u4e0a\u73b0\u51fa\u65e0\u9650\u6b23\u559c\u4e4b\u8272\uff0c\u5446\u5446\u671b\u7740\u8dc3\u5728\u7a7a\u4e2d\u7684\u8f9b\u6377\uff0c\u90a3 \u4e00\u5934\u79c0\u53d1\u968f\u98ce\u4e71\u98d8\u62c2\u7740\uff0c\u5374\u76ca\u53d1\u589e\u52a0\u4e86\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u7f8e\u3002 \u3000\u3000\u8fd9\u65f6\u8f9b\u6377\u4e0a\u8dc3\u4e4b\u52bf\u5df2\u5c3d\uff0c\u5f00\u59cb\u7f13\u7f13\u4e0b\u843d\uff0c\u83c1\u513f\u4fef\u8eab\u635e\u8d77\u4e24\u5757\u6728\u677f\u5411\u524d\u4e00\u6254\uff0c\u8f9b\u6377\u6b63 \u597d\u843d\u5728\u4e0a\u9762\uff0c\u4ed6\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u4e5f\u4ee5\u4e0a\u4e58\u8f7b\u529f\u7acb\u5728\u6728\u677f\u4e0a\u968f\u6ce2\u800c\u6d6e\u3002 \u3000\u3000\u4e24\u4eba\u90fd\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8f9b\u6377\u539f\u5c31\u662f\u4e00\u4e2a\u6781\u7aef\u7684\u4eba\uff0c\u8fd9\u65f6\u4ed6\u80f8\u4e2d\u5bf9\u83c1\u513f\u7684\u601c\u7231\u771f\u8d85\u51fa\u65b9\u624d \u751f\u6b7b\u6323\u624e\u6240\u7559\u5728\u5fc3\u7075\u4e0a\u7684\u8377\u8d1f\u4f55\u6b62\u5341\u500d\u3002 \u3000\u3000\u4e24\u4eba\u968f\u7740\u6d6a\u6d9b\u6240\u51b2\uff0c\u8ddd\u79bb\u6108\u6765\u6108\u8fd1\uff0c\u5468\u56f4\u7684\u4e00\u5207\u5bf9\u4e24\u4eba\u6765\u8bf4\uff0c\u771f\u662f\u4e0d\u7779\u4e0d\u95fb\u3002 \u3000\u3000\u90a3\u8fb9\u6d77\u76d7\u8239\u4e0a\uff0c\u65e0\u6068\u751f\u5bf9\u4e00\u6279\u6279\u6d8c\u4e0a\u7684\u7fa4\u76d7\u75db\u65bd\u6740\u624b\uff0c\u638c\u98ce\u547c\u547c\u4e2d\uff0c\u53c8\u662f\u6570\u540d\u6d77\u8d3c \u88ab\u51fb\u843d\u6d77\u4e2d\uff0c\u6210\u4e00\u9752\u4e5f\u88ab\u4ed6\u4e00\u638c\u9707\u4f24\u5185\u810f\u3002 \u3000\u3000\u4f46\u5c31\u5728\u4ed6\u594b\u529b\u6325\u638c\u7684\u5f53\u513f\uff0c\u4ed6\u80f8\u4e2d\u5f00\u59cb\u4e00\u9635\u5bd2\u95f7\uff0c\u4ed6\u4e0d\u7531\u6697\u60ca\u8fd9\u6bd2\u836f\u597d\u5389\u5bb3\uff0c\u5c45\u7136 \u4e0d\u53d7\u81ea\u5df1\u5185\u529f\u63a7\u5236\uff0c\u62ac\u5934\u770b\u65f6\uff0c\u5176\u4ed6\u4e8c\u8239\u7684\u7fa4\u76d7\u4e5f\u4e0d\u65ad\u8dc3\u5411\u81ea\u5df1\u6240\u7acb\u4e4b\u8239\uff0c\u663e\u7136\u662f\u52a0\u5165 \u589e\u63f4\uff0c\u800c\u7f2a\u4e03\u5a18\u90a3\u8fb9\u867d\u7136\u5360\u5c3d\u4e0a\u98ce\uff0c\u4f46\u8981\u60f3\u5c06\u7fa4\u76d7\u5c3d\u6b7c\u706d\uff0c\u4ea6\u975e\u4e00\u65f6\u53ef\u80fd\uff0c\u800c\u81ea\u5df1\u4f3c\u4e4e \u4e2d\u6bd2\u5df2\u53d1\uff0c\u5f53\u4e0b\u53c8\u6025\u6012\uff0c\u529b\u8d2f\u53cc\u638c\uff0c\u62db\u62db\u51fb\u51fa\uff0c\u5f53\u524d\u4e00\u4eba\u88ab\u7acb\u6bd9\u638c\u4e0b\uff0c\u5c38\u8eab\u88ab\u5e26\u51fa\u51e0\u4e08 \u4ee5\u5916\uff01 \u3000\u3000\u8fd9\u4e00\u638c\u65e0\u6068\u751f\u65bd\u51fa\u4e86\u771f\u529f\u592b\uff0c\u767b\u65f6\u628a\u5176\u4ed6\u4e24\u4e2a\u6d77\u76d7\u5413\u5f97\u6014\u4e86\u2014\u2014\u6014\uff0c\u65e0\u6068\u751f\u547c\u547c\u53c8 \u662f\u4e00\u638c\u63a8\u51fa\uff0c\u4e24\u4eba\u8fde\u5fd9\u5408\u529b\u62fc\u547d\u4e00\u6321\uff0c\u5361\u64e6\u4e00\u58f0\uff0c\u4e24\u4eba\u624b\u9aa8\u767b\u65f6\u6298\u65ad\uff0c\u75db\u5f97\u660f\u6b7b\u8fc7\u53bb\u8fd9 \u65f6\u4e00\u79cd\u5b9b\u5982\u4e07\u9a6c\u72c2\u5954\u7684\u58f0\u54cd\u4ece\u4e1c\u65b9\u4f20\u4e86\u8fc7\u6765\uff0c\u4e00\u5927\u7247\u9ed1\u4e91\u52bf\u82e5\u5954\u9a6c\u822c\u98de\u538b\u800c\u81f3\uff0c\u5bb4\u65f6\u5929 \u8272\u660f\u6697\uff0c\u5de8\u6d9b\u6d8c\u8d77\uff0c\u5ffd\u7136\u51e0\u6ef4\u8c46\u5927\u7684\u96e8\u6ef4\u659c\u843d\u4e0b\u6765\u8fd9\u6d77\u4e0a\u66b4\u98ce\u6765\u5f97\u771f\u5feb\uff0c\u90a3\u9ed1\u4e91\u8fd8\u6ca1\u6709 \u98de\u5230\u5934\u9876\u4e0a\uff0c\u72c2\u98ce\u5df2\u7ecf\u5f00\u59cb\u6012\u53f7\uff0c\u6d77\u6d6a\u88ab\u6b23\u8d77\u6570\u4e08\u9ad8\uff0c\u76f4\u5377\u4e0a\u8239\u4e0a\u7532\u677f\uff0c\u6845\u6746\u4e0a\u7684\u4e2d\u5e06 \u66f4\u662f\u5403\u5f97\u6ee1\u6ee1\u7684\u2014\u2014\u8fd9\u4e0d\u4e0b\u4e07\u65a4\u7684\u529b\u91cf\u4f7f\u5f97\u8239\u901f\u9aa4\u589e\u800c\u6845\u6746\u4e5f\u659c\u503e\u6b32\u6298\u3002 \u3000\u3000\u6210\u4e00\u9752\u4e45\u5904\u6d77\u4e0a\uff0c\u5c82\u6709\u4e0d\u77e5\u8fd9\u4e1c\u6d77\u98d3\u98ce\u7684\u5a01\u529b\u4e4b\u7406\uff0c\u4ed6\u77e5\u9053\u53ea\u8981\u62c6\u4e0b\u5e06\u6765\uff0c\u5c31\u80fd\u51cf \u5c11\u4e00\u534a\u4ee5\u4e0a\u7684\u5371\u9669\uff0c\u5f53\u4e0b\u5f3a\u5fcd\u4f4f\u5185\u4f24\uff0c\u5927\u547c\u6c34\u624b\u8bbe\u6cd5\u4e0b\u5e06\u3002 \u3000\u3000\u4f46\u8fd9\u88ab\u98d3\u98ce\u6da8\u6ee1\u7684\u5de8\u5e06\uff0c\u6297\u529b\u4f55\u6b62\u4e07\u65a4\uff0c\u5c82\u662f\u5341\u51e0\u6c34\u624b\u6240\u80fd\u62c6\u4e0b\uff0c\u773c\u770b\u5927\u8239\u5c31\u5f97\u5371 \u9669\uff0c\u90a3\u8fb9\u65e0\u6068\u751f\u66f4\u662f\u62fc\u529b\u65bd\u5a01\uff0c\u4e00\u8fde\u51e0\u62db\uff0c\u6321\u8005\u4e0d\u6b7b\u5373\u4f24\uff0c\u4e00\u65f6\u60e8\u53f7\u58f0\u8fde\u8d77\uff0c\u5939\u7740\u96f7\u971e \u4e07\u94a7\u7684\u72c2\u98ce\u58f0\uff0c\u628a\u8fd9\u6d77\u4e0a\u8001\u624b\u7684\u6210\u4e00\u9752\u4e5f\u6025\u5f97\u624b\u8db3\u65e0\u63aa\u3002 \u3000\u3000\u8fd9\u65f6\u54d7\u5566\u7684\u5927\u96e8\u4e5f\u5f00\u59cb\u503e\u76c6\u800c\u6cfb\uff0c\u8f70\u7136\u4e00\u58f0\u5de8\u54cd\uff0c\u8239\u9996\u89e6\u4e86\u6697\u7901\uff0c\u8fd9\u6b63\u6025\u901f\u800c\u884c\u7684 \u5927\u8239\u649e\u51fb\u4e4b\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u7acb\u523b\u5c06\u8239\u5934\u6574\u4e2a\u649e\u788e\uff0c\u63a5\u7740\u5361\u64e6\u4e00\u58f0\uff0c\u4e3b\u6845\u6298\u65ad\uff0c\u5927\u8239\u7acb\u523b\u503e \u5012\uff0c\u4e00\u4e2a\u6ed4\u5929\u5de8\u6d6a\u626b\u8fc7\uff0c\u628a\u8239\u4e0a\u6240\u6709\u7684\u4eba\u548c\u7269\u90fd\u5377\u5165\u65e0\u60c5\u5927\u6d77\uff01 \u3000\u3000\u4f46\u5176\u4e2d\u6709\u4e00\u4e2a\u2014\u2014\u5c31\u662f\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u2014\u2014\u6ca1\u6709\u88ab\u5377\u5165\u5927\u6d77\uff0c\u4ed6\u53cc\u624b\u5341\u6307\u6df1\u6df1\u63d2\u5165 \u7532\u677f\u5185\uff0c\u4ecd\u7559\u5728\u503e\u659c\u5f97\u4e0d\u6210\u6837\u5b50\u7684\u7532\u677f\u4e0a\u3002 \u3000\u3000\u4ed6\u4e58\u7740\u4e00\u6d6a\u521a\u8fc7\uff0c\u4e8c\u6d6a\u672a\u81f3\u7684\u65f6\u5019\uff0c\u56db\u76ee\u4e00\u671b\uff0c\u767d\u832b\u832b\u7684\u4e00\u7247\uff0c\u8fde\u4ed6\u7684\u76ee\u529b\u4e5f\u4e0d\u53ca \u5341\u4e08\u4ee5\u5916\uff0c\u7f2a\u4e03\u5a18\u7684\u5f71\u5b50\u4e0d\u89c1\uff0c\u751a\u81f3\u5176\u4ed6\u76f8\u90bb\u7684\u4e24\u8239\u90fd\u4e0d\u89c1\u4e86\uff01 \u3000\u3000\u4efb\u4ed6\u65e0\u6781\u5c9b\u4e3b\u795e\u529f\u76d6\u4e16\uff0c\u4fee\u7ec3\u81f4\u4e0d\u574f\u4e4b\u8eab\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u80fd\u4e0e\u81ea\u7136\u4e4b\u529b\u76f8\u6297\u8861\uff0c\u4ed6\u53ea\u6709 \u51ed\u7740\u5341\u6307\u7684\u529f\u592b\uff0c\u4e0d\u88ab\u5377\u4eba\u5de8\u6d9b\u800c\u5df2\uff01 \u3000\u3000\u4f46\u90a3\u98ce\u66b4\u5374\u6108\u6765\u6108\u5927\uff0c\u6d6a\u6d9b\u4e5f\u6108\u6253\u6108\u9ad8\uff0c\u672c\u5df2\u659c\u5012\u7684\u8239\u8eaf\u7ec8\u4e8e\u7ecf\u4e0d\u8d77\u5de8\u6d6a\u7684\u731b\u529b\u51b2 \u6253\uff0c\u53c8\u662f\u8f70\u7136\u4e00\u58f0\uff0c\u88ab\u6574\u4e2a\u7ffb\u4e86\u8fc7\u53bb\uff0c\u5de8\u5927\u7684\u8239\u8eab\u518d\u6b21\u649e\u5728\u6697\u7901\u4e0a\uff0c\u7acb\u523b\u652f\u79bb\u7834\u788e\uff0c\u51e0 \u7ecf\u51b2\u51fb\uff0c\u6728\u677f\u7c89\u6563\uff0c\u90a3\u6d88\u7247\u523b\u5c31\u88ab\u541e\u5165\u6d6a\u4e2d\u3002 \u3000\u3000\u4e14\u8bf4\u8f9b\u6377\u4e0e\u83c1\u513f\u5bf9\u9762\u98d8\u5728\u6ce2\u5c16\u4e0a\uff0c\u85c9\u7740\u6ce2\u6d9b\u6108\u6765\u6108\u8fd1\uff0c\u4e24\u4eba\u5fc3\u4e2d\u90fd\u5145\u6ee1\u7740\u67d4\u60c5\u5bc6\u610f\uff0c \u4f46\u662f\u5ffd\u7136\u95f4\uff0c\u5929\u8272\u4e00\u6697\uff0c\u5de8\u6d9b\u5e73\u5730\u9ad8\u5347\u6570\u4e08\uff0c\u63a5\u7740\u72c2\u98ce\u5927\u4e3e\uff0c\u767d\u6d6a\u6380\u5929\uff0c\u8f9b\u6377\u65bd\u51fa\u6700\u4e0a \u4e58\u7684\u201c\u6697\u9999\u6d6e\u5f71\u201d\u8f7b\u529f\uff0c\u4ecd\u7136\u4e0d\u80fd\u7acb\u7a33\uff0c\u5ffd\u89c1\u83c1\u513f\u4e00\u58f0\u5c16\u53eb\uff0c\u4e00\u4e2a\u5de8\u6d6a\u6765\uff0c\u5c06\u5979\u51b2\u5012\u5411 \u540e\u2014\u2014\u8f9b\u6377\u987f\u89c9\u70ed\u8840\u6cb8\u817e\uff0c\u5fd8\u8bb0\u4e86\u81ea\u5df1\u7684\u5371\u9669\uff0c\u4e5f\u5fd8\u8bb0\u4e86\u662f\u5728\u6ce2\u6d9b\u5343\u4e08\u7684\u6012\u6d77\u4e0a\uff0c\u53cc\u8db3 \u731b\u70b9\uff0c\u867d\u7136\u5168\u8eab\u5c3d\u6e7f\uff0c\u4ecd\u7136\u8ba9\u4ed6\u6380\u8d77\u6570\u5c3a\uff0c\u5411\u83c1\u513f\u6251\u53bb\u2014\u2014\u84e6\u7684\u53c8\u4e00\u4e2a\u6ed4\u5929\u5de8\u6d6a\u51fb\u6765\uff0c \u8f9b\u6377\u5728\u6c79\u6d8c\u7684\u6d6a\u6d9b\u4e0a\u501f\u529b\u98de\u8d77\uff0c\u529b\u91cf\u672c\u5c31\u8106\u5f31\uff0c\u90a3\u7ecf\u5f97\u8d77\u8fd9\u5de8\u6d6a\u4e00\u51fb\uff0c\u6d6a\u82b1\u4e2d\u53ea\u89c1\u83c1\u513f \u4e5f\u88ab\u5de8\u6d6a\u5708\u53bb\uff0c\u4e0d\u7531\u5927\u6025\uff0c\u4f46\u6b64\u523b\u90a3\u7531\u5f97\u4ed6\u601d\u7d22\uff0c\u4ed6\u53ea\u89c9\u8033\u4e2d\u3001\u53e3\u4e2d\u3001\u9f3b\u4e2d\u5168\u662f\u54b8\u54b8\u7684 \u6d77\u6c34\uff0c\u5168\u8eab\u4e0d\u7531\u81ea\u4e3b\u7684\u968f\u7740\u6ce2\u6d6a\u8d77\u4f0f\uff0c\u4f46\u4ed6\u4fe9\u53ef\u89c9\u8fa8\u51fa\u81ea\u5df1\u662f\u5728\u6e10\u6e10\u4e0b\u6c89\uff0c\u56e0\u4e3a\u4ed6\u5df2\u6e10 \u6e10\u542c\u4e0d\u89c1\u90a3\u6012\u53f7\u72c2\u98ce\uff1b\u4ed6\u6e10\u6e10\u6df1\u6c89\u5165\u6d77\u5e95\u72c2\u98ce\u66b4\u96e8\u4f9d\u7136\u8086\u8650\uff0c\u6ed4\u5929\u5de8\u6d6a\u6c79\u6d8c\u7740\uff0c\u5927\u81ea\u7136 \u7684\u6012\u543c\u58f0\u9707\u5f7b\u5e95\u5782\u7684\u5929\u7a79\u2026\u2026 \u3000\u3000\u8fd9\u79cd\u98d3\u98ce\u6765\u5f97\u5feb\uff0c\u53bb\u5f97\u4e5f\u901f\uff0c\u66fe\u51e0\u4f55\u65f6\uff0c\u9ed1\u4e91\u8fdc\u53bb\uff0c\u65e5\u5149\u666e\u7167\uff0c\u6d77\u6d6a\u4e5f\u5e73\u9759\u4e0b\u6765\uff0c \u649e\u6bc1\u7684\u8239\u8eab\u4e5f\u9732\u51fa\u6d77\u9762\uff0c\u8fdc\u5904\u4e00\u9053\u4e03\u5f69\u8679\u5149\u5f2f\u5728\u6c34\u5e73\u7ebf\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u7f13\u7f13\u7741\u5f00\u4e86\u773c\u6674\uff0c\u4ed6\u7acb\u523b\u53d1\u73b0\u81ea\u5df1\u8eba\u5728\u4e00\u5e26\u9ec4\u6c99\u6ee9\u4e0a\uff0c\u6d6a\u82b1\u8f7b\u8f7b\u62cd\u4ed6\u7684\u811a\u8e81\uff0c \u4ed6\u8111\u6d77\u4e2d\u4e00\u65f6\u7a7a\u7a7a\uff0c\u4ec0\u4e48\u4e5f\u8bb0\u4e0d\u5f97\uff0c\u4ed6\u7528\u5de6\u624b\u634f\u4f4f\u53f3\u8155\uff0c\u4f9d\u7a00\u80fd\u611f\u89c9\u5230\u5fae\u5fae\u7684\u8109\u8df3\u2014\u2014 \u3000\u3000\u201c\u5bf9\u4e86\uff0c\u8fd9\u5c31\u662f\u751f\u547d\u7684\u640f\u52a8\u2014\u2014\u4eba\u751f\u7684\u949f\u6446\u4e0d\u4e5f\u6b63\u662f\u8fd9\u6837\u6084\u6084\u5730\u52a8\u8361\u7740\u5417\uff1f\u4e0d\u8fc7\u6ca1 \u6709\u4eba\u5bdf\u89c9\u7f62\u4e86\uff0c\u800c\u4eba\u7684\u751f\u547d\u5c31\u5b8c\u5168\u6df9\u6ca1\u5728\u6b64\u8fdf\u7f13\u7684\u640f\u52a8\u4e2d\uff0c\u5176\u4f59\u7684\u2014\u2014\u201d\u4ed6\u5ffd\u7136\u5728\u8111\u6d77 \u4e2d\u601d\u7d22\u7740\u8fd9\u4e2a\u95ee\u9898\u3002 \u3000\u3000\u201c\u5176\u4f59\u7684\u53ea\u662f\u5e7b\u68a6\u7f62\u4e86\uff1b\u4e00\u4e9b\u4e0d\u6210\u5f62\u7684\u5e7b\u68a6\uff0c\u8822\u52a8\u7684\uff0c\u7247\u65ad\u7684\u68a6\uff0c\u4ee4\u4eba\u53ef\u6068\u7684\u53ef\u7b11 \u7684\u5f71\u5b50\u2026\u2026\u5982\u968f\u98ce\u98d8\u8361\u7684\u68c9\u7d6e\u4e00\u822c\u7684\u55a7\u95f9\u58f0\u97f3\uff0c\u5947\u5f62\u602a\u72b6\u7684\u75db\u82e6\uff0c\u6b22\u7b11\u3001\u68a6\u3001\u68a6\u2026\u2026\u4e00 \u5207\u5168\u662f\u68a6\u666f\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4e24\u53ea\u767d\u9e25\u4f4e\u4f4e\u98de\u8fc7\uff0c\u5bf9\u5730\u4e0a\u8eba\u7740\u7684\u4ed6\u5947\u602a\u5730\u770b\u4e86\u4e00\u773c\uff0c\u7136\u540e\u4e92\u76f8\u60ca\u5947\u4f3c\u5730\u5bf9\u9e23 \u4e00\u58f0\uff0c\u51cc\u7a7a\u800c\u53bb\u3002 \u3000\u3000\u201c\u4f46\u662f\u2014\u2014\u4f46\u662f\u5728\u8fd9\u6d51\u660f\u7684\u68a6\u91cc\u5374\u6709\u4e9b\u503c\u5f97\u6355\u6349\u7684\u5f71\u5b50\uff0c\u6709\u65e0\u7a77\u7684\u771f\uff0c\u65e0\u7a77\u7684\u7f8e\u2014 \u2014\u201d \u3000\u3000\u5947\u602a\u7684\u662f\uff0c\u6b64\u523b\u4ed6\u53ea\u80fd\u60f3\u5230\u771f\u4e0e\u7f8e\uff0c\u5374\u60f3\u4e0d\u5230\u201c\u5584\u201d\uff01 \u3000\u3000\u6e10\u6e10\u4ed6\u7a7a\u6d1e\u7684\u8111\u6d77\u5145\u5b9e\u8d77\u6765\uff0c\u9ebb\u6728\u7684\u601d\u60f3\u4e5f\u654f\u6377\u8d77\u6765\u4e86\uff0c\u4ed6\u80fd\u8bb0\u5f97\u4e00\u5207\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u53ef\u7231\u7684\u83c1\u513f\u846c\u8eab\u6d77\u5e95\uff0c\u8fd8\u6709\u81ea\u5df1\u6240\u53d7\u7684\u51cc\u8fb1\uff0c\u201c\u8fd9\u4e00\u5207\u90fd\u662f\u90a3\u53ef\u6068\u7684\u65e0\u6068\u751f \u592b\u5987\u6240\u5f15\u8d77\u7684\uff01\u201d\u4ed6\u4e0d\u7531\u54ac\u7259\u5207\u9f7f\u3002 \u3000\u3000\u4f46\u7acb\u523b\u4ed6\u60f3\u5230\u65e0\u6068\u751f\u8d85\u51e1\u5165\u5723\u7684\u6b66\u827a\uff0c\u81ea\u5df1\u82e6\u7ec3\u5341\u591a\u5e74\u8fde\u4eba\u5bb6\u4e00\u62db\u4e5f\u63a5\u4e0d\u4e86\uff0c\u4ed6\u5ffd \u7136\u89c9\u5f97\u4e03\u5999\u795e\u541b\u6240\u4f20\u7684\u6b66\u827a\u771f\u662f\u592a\u4e0d\u4e2d\u7528\u4e86\u3002 \u3000\u3000\u4f46\u4e8b\u5b9e\u4e0a\u4e0d\u5bb9\u4ed6\u6c38\u8fdc\u8fd9\u6837\u8eba\u7740\u80e1\u601d\u4e71\u60f3\uff0c\u7ec8\u4e8e\u4ed6\u7ad9\u4e86\u8d77\u6765\u3002\u4ed6\u56db\u76ee\u4e00\u671b\uff0c\u663e\u7136\u5730\u8fd9 \u662f\u4e00\u4e2a\u5c0f\u5b64\u5c9b\uff0c\u4ed6\u76f8\u4fe1\u8fd9\u5c9b\u5c0f\u5f97\u5706\u5468\u4e0d\u51fa\u5341\u91cc\u3002\u4f46\u5c9b\u4e2d\u95f4\u5374\u662f\u4e00\u6839\u6839\u77f3\u7b0b\u822c\u7684\u5c71\u5cf0\uff0c\u5149 \u79c3\u79c3\u5730\u5bf8\u8349\u4e0d\u751f\u3002 \u3000\u3000\u4ed6\u8fd8\u8bb0\u5f97\u82e5\u4e0d\u662f\u81ea\u5df1\u5728\u8fd4\u524d\u786c\u63d0\u6c14\u903c\u4f4f\u5185\u7a74\uff0c\u6b64\u523b\u65e9\u5df2\u88ab\u6c34\u6ce1\u6b7b\uff0c\u4f46\u7eb5\u7136\u5982\u6b64\u4ed6\u4e5f \u75b2\u7d2f\u4e0d\u582a\u3002 \u3000\u3000\u4ed6\u6323\u624e\u7740\u5f80\u5c9b\u4e2d\u95f4\u8d70\u53bb\uff0c\u5f53\u4ed6\u52c9\u5f3a\u7ffb\u8fc7\u4e00\u6839\u77f3\u7b0b\u5cf0\u65f6\uff0c\u5ffd\u611f\u4e00\u7247\u5929\u660f\u5730\u6697\uff0c\u56db\u9762\u666f \u8272\uff0c\u4f3c\u865a\u8fd8\u771f\uff0c\u5b9b\u5982\u7f6e\u8eab\u6d77\u5e95\u3002 \u3000\u3000\u800c\u4e14\u4ed6\u5b9e\u5728\u4e5f\u8d70\u4e0d\u52a8\u4e86\uff0c\u4ed6\u53ea\u597d\u5750\u4e0b\u7528\u90a3\u88ab\u8ba4\u4e3a\u201c\u6beb\u4e0d\u4e2d\u7528\u201d\u7684\u5185\u529f\u6765\u4f01\u56fe\u6062\u590d\u4e00 \u4e9b\u771f\u529b\u3002 \u3000\u3000\u7b49\u5230\u771f\u6c14\u8fd0\u5f97\u4e00\u5468\u4e4b\u540e\uff0c\u4ed6\u89c9\u5f97\u771f\u529b\u6062\u590d\u4e0d\u5c11\uff0c\u4f46\u4ed6\u5374\u66f4\u60ca\u5f02\u5730\u5446\u5750\u5728\u5730\u4e0a\uff0c\u539f\u6765 \u4ed6\u53d1\u73b0\u8fd9\u7fa4\u77f3\u7b0b\u4e2d\u4ecd\u7136\u662f\u4e00\u7247\u5929\u660f\u5730\u6697\u2014\u2014\u4ed6\u5148\u8fd8\u4ee5\u4e3a\u662f\u81ea\u5df1\u75b2\u7d2f\u773c\u82b1\u7684\u9519\u89c9\u6240\u81f4\u3002 \u3000\u3000\u56de\u9996\u4e00\u770b\uff0c\u81ea\u5df1\u65b9\u624d\u8fdb\u5165\u7684\u8def\u4e5f\u627e\u4e0d\u5230\u4e86\uff0c\u56db\u5468\u53ea\u662f\u660f\u6697\u7684\u4e00\u7247\uff0c\u4e00\u5207\u5c71\u77f3\u6811\u6728\u90fd \u4f3c\u771f\u8fd8\u865a\uff0c\u8f9b\u6377\u5c3d\u5f97\u4e03\u5999\u795e\u541b\u4e03\u827a\u771f\u4f20\uff0c\u7aef\u7684\u662f\u4e5d\u6d41\u4e09\u6559\u7684\u529f\u592b\u65e0\u6240\u4e0d\u7cbe\uff0c\u6b64\u65f6\u7acb\u523b\u53d1 \u73b0\u662f\u9677\u8eab\u4e8e\u4e00\u4e2a\u9635\u56fe\u4e2d\uff0c\u7531\u6b64\u63a8\u60f3\uff0c\u8fd9\u5c0f\u5c9b\u4e0a\u5fc5\u4f4f\u7740\u4e16\u5916\u9ad8\u4eba\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u7684\u68cb\u827a\u5728\u4e03\u827a\u4e2d\u5c24\u5176\u662f\u4ed6\u6700\u5f97\u610f\u7684\u529f\u592b\uff0c\u4ed6\u7684\u68cb\u827a\u4e0e\u4e00\u822c\u68cb\u58eb\u5927\u4e3a\u4e0d\u540c\uff0c \u4e43\u662f\u5148\u884c\u7814\u7a76\u5404\u79cd\u9635\u6cd5\uff0c\u7a77\u901a\u76f8\u514b\u4e4b\u7406\u4ee5\u540e\uff0c\u624d\u7528\u5230\u68cb\u76d8\u4e0a\u6765\uff0c\u662f\u4ee5\u867d\u65e5\u7cbe\u4e8e\u5f08\u68cb\uff0c\u5176 \u5b9e\u66f4\u7cbe\u4e8e\u5929\u4e0b\u767e\u9635\u3002 \u3000\u3000\u8f9b\u6377\u5c3d\u5f97\u6885\u5c71\u6c11\u771f\u4f20\uff0c\u7565\u4e00\u8fc7\u76ee\uff0c\u4fbf\u77e5\u6b64\u9635\u4e43\u5929\u751f\u77f3\u7b0b\u6240\u5e03\u6210\uff0c\u4f3c\u4e4e\u7c7b\u4f3c\u4e2d\u539f\u6240\u8c13 \u7684\u201c\u5947\u95e8\u4e94\u884c\u9635\u201d\uff0c\u5f53\u4e0b\u7565\u4e00\u76d8\u7b97\uff0c\u8d77\u8eab\u4ece\u5de6\u9762\u201c\u91d1\u95e8\u201d\u8d70\u5165\u3002 \u3000\u3000\u8f9b\u6377\u6309\u5947\u95e8\u4e94\u884c\u9635\u7684\u53d8\u5316\u5de6\u53f3\u76d8\u65cb\u4e86\u4e00\u4f1a\uff0c\u6697\u5fd6\u518d\u4e00\u8f6c\u5f2f\uff0c\u4fbf\u53ef\u7531\u571f\u95e8\u51fa\u9635\uff0c\u90a3\u77e5 \u4e00\u8f6c\u5f2f\uff0c\u7adf\u56de\u5230\u539f\u6765\u7684\u5730\u65b9\u3002\u8fd9\u4e00\u6765\u4ee4\u8f9b\u6377\u60ca\u5f02\u4e0d\u5df2\uff0c\u5fc3\u4e2d\u6697\u601d\u4e0d\u77e5\u6b64\u9635\u7a76\u7adf\u662f\u4f55\u9635\uff1f \u3000\u3000\u6b63\u6f5c\u5fc3\u6c89\u601d\u65f6\uff0c\u5ffd\u7136\u4e00\u9635\u94ee\u58f0\u4f20\u4e86\u8fc7\u6765\uff0c\u90a3\u94ee\u58f0\u97f3\u8c03\u6fc0\u6602\u4e4b\u6781\uff0c\u4f3c\u4e4e\u4e0d\u662f\u5bfb\u5e38\u5f26\u7c27 \u6240\u80fd\u53d1\uff0c\u8f9b\u6377\u4e0d\u7981\u4fa7\u8033\u503e\u542c\uff0c\u90a3\u94ee\u58f0\u94ff\u9535\u9ad8\u6602\uff0c\u6697\u6697\u6709\u91d1\u6208\u94c1\u9a6c\u4e4b\u58f0\u3002\u518d\u542c\u4e00\u4f1a\uff0c\u94ee\u58f0 \u76ca\u53d1\u632f\u4eba\u5fc3\u5f26\uff0c\u4f3c\u4e4e\u5f39\u94ee\u4eba\u6108\u6765\u6108\u6124\uff0c\u94ee\u58f0\u4e5f\u6108\u6765\u6108\u6025\uff0c\u4eff\u4f5b\u90a3\u5f39\u94ee\u4eba\u6068\u4e0d\u5f97\u4e00\u4e3e\u6bc1\u6389 \u6574\u4e2a\u5730\u7403\u4e00\u822c\u3002 \u3000\u3000\u8f9b\u6377\u4ece\u90a3\u53e4\u602a\u7684\u70df\u96fe\u4e2d\u4f9d\u7a00\u53ef\u8fa8\u51fa\u94ee\u58f0\u4e43\u662f\u53d1\u81ea\u77f3\u7b0b\u9635\u7684\u4e2d\u5fc3\uff0c\u4e8e\u662f\u4ed6\u51ed\u542c\u89c9\u5f80\u4e2d \u5fc3\u8d70\u53bb\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u767d\u7ed5\u4e86\u591a\u5c11\u8def\uff0c\u4f46\u7ec8\u4e8e\u90a3\u94ee\u58f0\u6108\u6765\u6108\u8fd1\u4e86\uff0c\u6700\u540e\u8f9b\u6377\u722c\u8fc7\u4e00\u4e2a\u77f3\u5cf0\uff0c\u53d1\u73b0\u94ee \u58f0\u5c31\u53d1\u81ea\u77f3\u5cf0\u6839\u4e0b\u3002 \u3000\u3000\u8fd9\u5168\u9635\u7684\u4e2d\u5fc3\u70df\u96fe\u53cd\u5012\u751a\u662f\u7a00\u8584\uff0c\u8f9b\u6377\u53ef\u6e05\u6670\u770b\u89c1\u4e00\u4e2a\u7ea2\u5149\u6ee1\u9762\u7684\u8001\u548c\u5c1a\u5750\u5728\u77f3\u4e0a \u5f39\u94ee\uff0c\u90a3\u94ee\u91d1\u5149\u95ea\u95ea\uff0c\u7adf\u662f\u7eaf\u94a2\u6240\u94f8\uff0c\u96be\u602a\u58f0\u97f3\u5982\u6b64\u6fc0\u6602\u3002 \u3000\u3000\u90a3\u8001\u8005\u770b\u6765\u94ee\u827a\u4e0d\u751a\u7cbe\u6e5b\u5df2\u5fc5\u987b\u5168\u795e\u8d2f\u6ce8\u624d\u4e0d\u81f4\u5f39\u9519\uff0c\u4f46\u8d77\u6307\u62c2\u8896\u4e4b\u95f4\uff0c\u7adf\u5e26\u730e\u730e \u98ce\u58f0\uff0c\u6c14\u5ea6\u5a01\u731b\u4e4b\u6781\u3002 \u3000\u3000\u8f9b\u6377\u770b\u90a3\u8001\u8005\u7531\u81ea\u53d8\u9ec4\u7684\u80e1\u5b50\uff0c\u770b\u6765\u603b\u8be5\u6709\u767e\u5c81\u4ee5\u4e0a\u7684\u5e74\u9f84\uff0c\u4f46\u4ed6\u7684\u5a01\u731b\u6c14\u5ea6\u5374\u4f3c \u4e94\u516d\u5341\u5c81\u4eba\uff0c\u800c\u4e14\u7ea2\u5149\u6ee1\u9762\uff0c\u5065\u58ee\u4e0e\u5e38\uff0c\u4e0d\u7531\u5927\u5947\u3002 \u3000\u3000\u8fd9\u65f6\u94ee\u4e50\u5df2\u594f\u5230\u9ad8\u6f6e\uff0c\u6025\u6025\u94ee\u97f3\u4e2d\u900f\u51fa\u9635\u9635\u6d77\u5578\u5c71\u5d29\u4e4b\u58f0\uff0c\u4ee4\u4eba\u80c6\u98a4\u5fc3\u60ca\u3002\u84e6\u7684\uff0c \u94ff\u7136\u4e00\u58f0\uff0c\u4f3c\u4e4e\u66f2\u7ec8\u97f3\u6b62\uff0c\u4f46\u90a3\u8001\u8005\u5374\u4f3c\u6108\u6124\u96be\u6b62\uff0c\u62cd\u5730\u4e00\u638c\u51fb\u4e0b\uff0c\u7adf\u5c06\u4e00\u5177\u7eaf\u94a2\u7684\u5927 \u94ee\u6253\u6210\u4e00\u5757\u6241\u6241\u7684\u94c1\u997c\uff0c\u63a5\u7740\u53cd\u624b\u4e00\u62cd\uff0c\u7acb\u523b\u5c06\u8eab\u65c1\u5de8\u77f3\u7b0b\u51fb\u6210\u77f3\u7c89\uff01\u8f9b\u6377\u770b\u4e86\uff0c\u5fc3\u4e2d \u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u82e5\u8005\u529f\u529b\u4e4b\u6df1\uff0c\u7aef\u7684\u5e73\u751f\u672a\u89c1\uff0c\u53ea\u6015\u90a3\u65e0\u6068\u751f\u4e5f\u4e0d\u80fd\u8f7b\u8f7b\u4e00\u638c\u5c06 \u77f3\u7b0b\u62cd\u6210\u7ec6\u7c89\uff0c\u60f3\u4e0d\u5230\u8fd9\u5c0f\u5c9b\u4e0a\u7adf\u6709\u5982\u6b64\u4eba\u7269\uff0c\u96be\u9053\u2014\u2014\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u8001\u8005\u5ffd\u7136\u62ac\u5934\u5411\u81ea\u5df1\u85cf\u5cf0\u5904\u4e00\u62db\u624b\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u542c\u591f\u4e86\u4e48\uff1f\u8fd8\u4e0d\u4e0e\u6211\u4e0b \u6765\uff1f\u201d \u3000\u3000\u8f9b\u6377\u8eb2\u5728\u4e0a\u9762\u81ea\u4ee5\u4e3a\u751a\u662f\u7a33\u59a5\uff0c\u90a3\u6653\u5f97\u4eba\u5bb6\u5934\u90fd\u4e0d\u62ac\uff0c\u5c31\u77e5\u9053\u81ea\u5df1\u6240\u5728\u3002\u5f53\u4e0b\u53ea\u597d \u786c\u7740\u5934\u76ae\uff0c\u4e00\u8dc3\u800c\u4e0b\u3002 \u3000\u3000\u90a3\u8001\u8005\u7741\u773c\u5bf9\u8f9b\u6377\u671b\u4e86\u4e00\u773c\uff0c\u7b11\u7b11\u9053\uff1a\u201c\u5403\u70b9\u4e1c\u897f\u5427\u3002\u201d\u968f\u7740\u5728\u5730\u4e0a\u62fe\u8d77\u4e24\u53ea\u9752\u8272 \u679c\u5b50\u9001\u8fc7\u53bb\u3002\u8f9b\u6377\u89c1\u8001\u8005\u773c\u5149\u51db\u7136\u6709\u795e\uff0c\u4f46\u7a81\u7136\u5bf9\u81ea\u5df1\u4e00\u7b11\uff0c\u8bf7\u81ea\u5df1\u5403\u4e1c\u897f\uff0c\u4e0d\u7981\u53c8\u60ca \u53c8\u559c\u3002 \u3000\u3000\u539f\u6765\u8f9b\u6377\u81ea\u6d77\u4e0a\u9047\u96be\u5230\u73b0\u5728\u4ecd\u662f\u7a7a\u7740\u809a\u5b50\uff0c\u65b9\u624d\u8fd8\u4e0d\u89c9\u600e\u6837\uff0c\u8fd9\u65f6\u88ab\u8001\u8005\u4e00\u63d0\uff0c\u7acb \u89c9\u997f\u5f97\u4e0d\u5f97\u4e86\uff0c\u770b\u90a3\u9752\u8272\u679c\u5b50\u6676\u4eae\u53ef\u7231\uff0c\u4e0d\u7531\u5782\u6d8e\uff0c\u5fd9\u4f38\u624b\u63a5\u8fc7\u3002 \u3000\u3000\u54ac\u4e86\u4e00\u53e3\uff0c\u679c\u7136\u5473\u9053\u9999\u751c\uff0c\u6781\u4e3a\u53ef\u53e3\uff0c\u4f46\u5ffd\u60f3\u5230\uff1a\u201c\u4ed6\u600e\u4e48\u77e5\u9053\u6211\u997f\u7684\u7d27\uff1f\u201d\u4e0d\u514d \u62ac\u5934\u770b\u90a3\u8001\u8005\u4e00\u773c\uff0c\u90a3\u8001\u8005\u5bf9\u4ed6\u4e00\u7b11\uff0c\u8f9b\u6377\u53ea\u89c9\u5f97\u8fd9\u8001\u8005\u6148\u7965\u4e4b\u6781\uff0c\u4f46\u65b9\u624d\u94ee\u58f0\u4e2d\u5374\u662f \u4e00\u7247\u6124\u6012\u4e4b\u97f3\uff0c\u4e0d\u77e5\u4ec0\u4e48\u4e8b\u60f9\u6012\u4e86\u8001\u4eba\uff1f \u3000\u3000\u5403\u5b8c\u4e86\u4e24\u53ea\u679c\u5b50\uff0c\u5ffd\u542c\u90a3\u8001\u8005\u9053\uff1a\u201c\u6211\u8fd9\u4ed9\u679c\u975e\u540c\u51e1\u54c1\uff0c\u770b\u4f60\u6b65\u5c65\u51dd\u7a33\uff0c\u5012\u662f\u6709\u51e0 \u5341\u5e74\u5185\u529f\u5728\u8eab\u4e00\u6837\uff0c\u4f60\u7528\u529f\u8fd0\u6c14\u4e00\u756a\u5c31\u77e5\u8fd9\u679c\u5b50\u7684\u597d\u5904\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u77e5\u600e\u7684\uff0c\u89c9\u5f97\u8fd9\u8001\u4eba\u8bf4\u8bdd\u4e2d\u6709\u4e00\u80a1\u4ee4\u4eba\u4e0d\u80fd\u6297\u62d2\u7684\u529b\u91cf\uff0c\u867d\u89c9\u8fd9\u4e24\u53ea\u5b50\u679c\u5b50 \u96be\u4ee5\u679c\u8179\uff0c\u4f46\u5f53\u4e0b\u4f9d\u8a00\u5750\u4e0b\uff0c\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u7528\u529f\u6253\u5750\u8d77\u6765\u3002 \u3000\u3000\u771f\u6c14\u900f\u8fc7\u5168\u8eab\u7ecf\u8109\u4ee5\u540e\uff0c\u8f9b\u6377\u53ea\u89c9\u6d51\u8eab\u8212\u6cf0\u65e0\u6bd4\uff0c\u9965\u997f\u5168\u6d88\uff0c\u771f\u6709\u8bf4\u4e0d\u51fa\u7684\u53d7\u7528\u3002 \u3000\u3000\u90a3\u8001\u8005\u6b64\u65f6\u5374\u60ca\u54a6\u4e00\u58f0\uff0c\u539f\u6765\u8f9b\u6377\u6b64\u65f6\u76d8\u819d\u7aef\u5750\uff0c\u5b9d\u76f8\u5e84\u4e25\uff0c\u5934\u9876\u9635\u9635\u767d\u6c14\u5192\u51fa\uff0c \u8fd9\u5206\u660e\u662f\u6700\u4e0a\u4e58\u7684\u5185\u5bb6\u529f\u592b\uff0c\u800c\u4e14\u975e\u6709\u56db\u4e94\u5341\u5e74\u529f\u529b\u4e0d\u80fd\u8fbe\u6b64\u5883\u754c\uff0c\u773c\u524d\u8fd9\u5c11\u5e74\u770b\u6765\u6700 \u591a\u4e8c\u5341\u5c81\uff0c\u5374\u5177\u4e00\u8eab\u4e0a\u4e58\u5185\u529f\uff0c\u4e0d\u7531\u5927\u5947\u3002 \u3000\u3000\u8f9b\u6377\u884c\u529f\u5b8c\u6bd5\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u5bf9\u8001\u4eba\u4e00\u63d6\u5230\u5730\uff0c\u9053\uff1a\u201c\u8c22\u8c22\u8001\u524d\u8f88\u539a\u8d50\uff0c\u665a\u8f88\u53d7\u76ca\u975e \u6d45\u3002\u201d \u3000\u3000\u8001\u8005\u6b23\u7136\u4e00\u7b11\u9053\uff1a\u201c\u5a03\u513f\u73b0\u5728\u624d\u77e5\u9053\u597d\u5904\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u88ab\u4ed6\u8bf4\u5f97\u6709\u70b9\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u90a3\u8001\u8005\u53c8\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u7684\u5185\u529f\u53ef\u771f\u4e0d\u9519\u5475\uff0c\u770b\u4f60\u8fd0\u529f\u60c5\u5f62\u4e0d\u4f1a\u662f\u65e0\u6781\u5c9b\u4e3b\u7684\u95e8\u4eba\uff0c \u66f4\u4e0d\u662f\u5c0f\u6222\u5c9b\u7684\u8def\u5b50\uff0c\u96be\u9053\u9664\u4e86\u6211\u4eec\u4e09\u4e2a\u8001\u4e0d\u6b7b\u7684\uff0c\u5929\u4e0b\u8fd8\u6709\u5176\u4ed6\u5982\u6b64\u7cbe\u5965\u7684\u529f\u592b\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4f55\u7b49\u806a\u660e\uff0c\u7acb\u77e5\u5bf9\u9762\u8fd9\u8001\u4eba\u5c31\u662f\u4e16\u5916\u4e09\u4ed9\u4e4b\u9996\u7684\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u3002\u5fd9\u606d\u8eab\u9053\uff1a \u201c\u665a\u8f88\u8f9b\u6377\u62dc\u89c1\u5e73\u51e1\u4e0a\u4eba\u3002\u201d \u3000\u3000\u8f9b\u6377\u53d7\u6885\u53d4\u53d4\u53ee\u5631\uff0c\u4e0d\u53ef\u4ee5\u5e08\u627f\u544a\u4eba\uff0c\u53ea\u597d\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u70b9\u672b\u5b66\u54ea\u80fd\u4e0e\u4e16\u5916\u4e09\u4ed9\u76f8 \u63d0\u5e76\u8bba\u3002\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5012\u662f\u7531\u8877\u4e4b\u8a00\uff0c\u56e0\u4e3a\u4ed6\u6b64\u523b\u5bf9\u81ea\u5df1\u672c\u95e8\u529f\u592b\u5b9e\u5728\u4fe1\u5fc3\u5c3d\u5931\u3002 \u3000\u3000\u90a3\u8001\u8005\u8138\u8272\u4e00\u6c89\u9053\uff1a\u201c\u5c0f\u5c0f\u5e74\u7eaa\u5c31\u8a00\u4e0d\u7531\u8877\uff0c\u6211\u77e5\u4f60\u5fc3\u4e2d\u5b9a\u81ea\u4ee5\u4e3a\u4f60\u5e08\u4f20\u529f\u592b\u80fd\u80dc \u8fc7\u4e16\u5916\u4e09\u4ed9\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5fd9\u8fa9\u9053\uff1a\u201c\u665a\u8f88\u786e\u662f\u7531\u8877\u4e4b\u8a00\uff0c\u65b9\u624d\u665a\u8f88\u7a77\u4e00\u751f\u6240\u5b66\u8fde\u65e0\u6068\u751f\u7684\u4e00\u62db\u90fd\u63a5\u4e0d\u4e0b\u2026 \u5509\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u60f3\u5230\u8fd9\u91cc\u5c31\u61ca\u4e27\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4f46\u806a\u660e\u7684\u4ed6\u5374\u4e0d\u660e\u767d\u8fd9\u5e73\u51e1\u4e0a\u4eba\u4f55\u4ee5\u5982\u6b64\u770b\u91cd \u81ea\u5df1\u8fd9\u70b9\u201c\u5fae\u672b\u201d\u672c\u4e8b\uff1f \u3000\u3000\u4ed6\u539f\u662f\u9ad8\u50b2\u65e0\u6bd4\u7684\u4eba\uff0c\u88ab\u65e0\u6068\u751f\u4e09\u756a\u4e24\u6b21\u64d2\u4f4f\u540e\uff0c\u7070\u5fc3\u5f97\u8fd1\u4e4e\u6709\u70b9\u81ea\u5351\uff0c\u662f\u4ee5\u89c1\u4e86 \u5e73\u51e1\u4e0a\u4eba\u4e0d\u7981\u5bf9\u4ed6\u4efd\u5916\u606d\u656c\uff0c\u751a\u81f3\u6709\u70b9\u5bb3\u6015\u3002 \u3000\u3000\u90a3\u5e73\u51e1\u4e0a\u4eba\u542c\u4ed6\u5982\u6b64\u8bf4\uff0c\u56b7\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u548c\u65e0\u6068\u751f\u4ea4\u8fc7\u624b\uff1f\u201d \u3000\u3000\u8f9b\u6377\u832b\u7136\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4ef0\u9996\u60f3\u4e86\u4e00\u4e0b\uff0c\u5ffd\u7136\u5de6\u624b\u4e00\u4f38\u76f4\u70b9\u8f9b\u6377\u201c\u4e73\u4e0b\u7a74\u201d\uff0c\u8f9b\u6377\u60ca\u53eb\u4e00\u58f0\uff1a\u201c\u524d \u8f88\u4f60\u2014\u2014\u201d\u4f46\u672c\u80fd\u7684\u53cd\u5e94\u4f7f\u4ed6\u7528\u51fa\u201c\u6697\u9999\u6d6e\u5f71\u201d\u7684\u529f\u592b\uff0c\u53ea\u89c1\u4ed6\u53cc\u80a9\u5fae\u8038\uff0c\u8eab\u5f62\u6ef4\u6e9c\u6e9c \u4e00\u8f6c\u5df2\u95ea\u8fc7\u6765\u52bf\uff0c\u90a3\u77e5\u5e73\u51e1\u4e0a\u4eba\u5de6\u624b\u5ffd\u7136\u8f6c\u5f2f\uff0c\u4ece\u65c1\u8fb9\u7ed5\u4e86\u8fc7\u6765\uff0c\u4ecd\u662f\u76f4\u70b9\u8f9b\u6377\u4e73\u4e0b\u7a74\uff0c \u8f9b\u6377\u8db3\u4e0b\u7528\u529b\uff0c\u9000\u540e\u6570\u6c11\u624d\u907f\u5f00\u6b64\u62db\u2014\u2014\u6240\u8c13\u907f\u5f00\uff0c\u4e0d\u8fc7\u662f\u5e73\u51e1\u4e0a\u4eba\u5750\u7740\u4e0d\u518d\u8ffd\u51fb\u800c\u5df2\u3002 \u3000\u3000\u8f9b\u6377\u5446\u77aa\u7740\u773c\uff0c\u56de\u5fc6\u65b9\u624d\u5e73\u51e1\u4e0a\u4eba\u90a3\u62db\u4e0d\u53ef\u601d\u8bae\u7684\u70b9\u7a74\u529f\u592b\uff0c\u56e0\u4e3a\u4ed6\u6325\u624b\u53d8\u62db\u65f6\uff0c \u4ed6\u770b\u5f97\u5206\u660e\uff0c\u7adf\u50cf\u662f\u7531\u81c2\u4e0a\u4e0d\u662f\u5173\u8282\u7684\u5730\u65b9\u53d8\u8fc7\u6765\u7684\uff0c\u8fd9\u79cd\u70b9\u7a74\u624b\u6cd5\u82e5\u662f\u771f\u6b63\u65bd\u5c55\u5f00\u6765\uff0c \u5c82\u4e0d\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\uff1f \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5374\u4e5f\u4ef0\u9996\u9ed8\u601d\uff0c\u4f3c\u4e4e\u6709\u4ec0\u4e48\u4e0d\u89e3\u7684\u4e8b\u56f0\u60d1\u7740\u4ed6\u3002\u4e00\u4f1a\u513f\u4ed6\u7684\u89c6\u7ebf\u53c8\u79fb\u5230\u8f9b \u6377\u8138\u4e0a\uff0c\u5ffd\u5730\u9762\u9732\u7b11\u5bb9\uff0c\u8138\u4e0a\u7591\u4e91\u5c3d\u9664\u3002 \u3000\u3000\u8f9b\u6377\u88ab\u641e\u5f97\u83ab\u540d\u5176\u5999\uff0c\u90a3\u5e73\u51e1\u4e0a\u4eba\u5374\u7b11\u9053\uff1a\u201c\u4e14\u4e0d\u95ee\u4f60\u5e08\u627f\uff0c\u6211\u5012\u8981\u95ee\u4f60\uff0c\u90a3\u65e0\u6068 \u751f\u70b9\u4f60\u65f6\u662f\u5426\u4f7f\u7684\u662f\u2018\u62c2\u7a74\u2019\u624b\u6cd5\uff1f\u201d\u63a5\u7740\u53ea\u89c1\u4ed6\u53f3\u624b\u5411\u524d\u5fae\u6296\uff0c\u4e00\u7247\u8896\u5f71\u4e2d\uff0c\u5c0f\u6307\u5df1 \u7136\u5728\u8f9b\u6377\u201c\u66f2\u6c60\u201d\u7a74\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u60f3\u90a3\u65e0\u6068\u751f\u4e00\u62db\u70b9\u4f4f\u81ea\u5df1\u7684\u6b63\u662f\u8fd9\u4e48\u4e00\u8bb0\u602a\u62db\uff0c\u4f46\u5374\u60f3\u4e0d\u5230\u8fd9\u5c31\u662f\u6b66\u6797\u5931\u4f20 \u5df2\u4e45\u7684\u201c\u62c2\u7a74\u201d\u529f\u592b\uff0c\u5f53\u4e0b\u70b9\u4e86\u70b9\u5934\u3002\u5e73\u51e1\u4e0a\u4eba\u8138\u4e0a\u66f4\u662f\u9732\u51fa\u559c\u8272\u9053\uff1a\u201c\u4ee5\u4f60\u7684\u529f\u529b\u65e0 \u8bba\u5982\u4f55\u4e0d\u81f4\u4e00\u62db\u5c31\u9003\u4e0d\u51fa\u53bb\uff0c\u60f3\u6765\u4f60\u5fc5\u662f\u592a\u8fc7\u7d27\u5f20\uff0c\u624d\u88ab\u65e0\u6068\u751f\u4e00\u62db\u5f97\u624b\u7684\u3002\u6211\u5148\u8fd8\u4ee5 \u4e3a\u65e0\u6068\u751f\u8fd9\u5bb6\u4f19\u5341\u5e74\u4e0d\u89c1\u529f\u529b\u7adf\u7cbe\u8fdb\u5982\u65af\uff0c\u539f\u6765\u4ed6\u8fd8\u662f\u2018\u62c2\u7a74\u2019\u8fd9\u624b\u8001\u529f\u592b\u3002\u54c8\u54c8\uff0c\u4ed6 \u8fd9\u2018\u62c2\u7a74\u2019\u867d\u662f\u4e0d\u51e1\uff0c\u5374\u4e5f\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u771f\u6b63\u7edd\u5999\u7684\u529f\u592b\u3002\u201d\u8bf4\u65f6\u8138\u4e0a\u795e\u91c7\u98de\u626c\uff0c\u5a01\u731b\u4e4b \u6781\u3002 \u3000\u3000\u8f9b\u6377\u5bf9\u65e0\u6068\u751f\u867d\u8bf4\u6068\u4e4b\u4eba\u9aa8\uff0c\u4f46\u5bf9\u4ed6\u7684\u6b66\u529f\u7740\u5b9e\u94a6\u4f69\u4e0d\u5df2\uff0c\u8fd9\u65f6\u89c1\u5e73\u51e1\u4e0a\u4eba\u8f7b\u89c6\u65e0 \u6068\u751f\u7684\u62c2\u7a74\u7edd\u6280\uff0c\u867d\u6709\u4e00\u80a1\u8bf4\u4e0d\u51fa\u7684\u9ad8\u5174\uff0c\u4f46\u5fc3\u4e2d\u4e5f\u7740\u5b9e\u6709\u70b9\u4e0d\u80fd\u7f6e\u4fe1\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u53cc\u624b\u5ffd\u7136\u4e00\u9519\uff0c\u5de6\u624b\u7a81\u5730\u4e0b\u6c89\uff0c\u53ea\u89c1\u4e94\u6307\u66f2\u5f20\uff0c \u75be\u5982\u9e70\u722a\u3002 \u3000\u3000\u8f9b\u6377\u4f55\u7b49\u806a\u654f\uff0c\u89c1\u4ed6\u8fd9\u4e00\u62db\u6bd4\u5212\u51fa\uff0c\u7acb\u523b\u609f\u51fa\u8fd9\u4e43\u662f\u89e3\u7834\u62c2\u7a74\u529f\u592b\u7684\u4e00\u8bb0\u7edd\u5999\u62db\u5f0f\uff0c \u4e00\u65f6\u624b\u4e0a\u4e00\u9762\u4f9d\u6837\u6bd4\u7740\uff0c\u5fc3\u4e2d\u4e00\u9635\u5927\u559c\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5fae\u5fae\u70b9\u9996\uff0c\u4f3c\u4e4e\u6697\u8d5e\u5b7a\u5b50\u53ef\u6559\u3002 \u3000\u3000\u505c\u4e86\u534a\u664c\uff0c\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u5a03\u513f\u4f60\u53ef\u77e5\u9053\u8001\u8872\u7684\u5e74\u5c81\uff1f\u8f9b\u6377\u4ece\u4ed6\u90a3\u5a01\u731b\u6c14\u5ea6\u53ca\u9ec4 \u767d\u957f\u9aef\u4e0a\u5b9e\u5728\u65e0\u6cd5\u65ad\u5b9a\u4ed6\u7684\u5e74\u9f84\uff0c\u53c8\u4e0d\u77e5\u4ed6\u4f55\u4ee5\u6709\u6b64\u4e00\u95ee\uff0c\u5f53\u4e0b\u832b\u7136\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u4fbf\u662f\u8001\u8872\u81ea\u5df1\u4e5f\u8bb0\u4e0d\u6e05\u695a\u4e86\uff0c\u603b\u4e4b\u5927\u7ea6\u5eff\u591a\u5e74\u524d\u65e0\u6068\u751f\u4ed6\u4eec\u66fe\u4ee5 \u6b64\u94ee\u8d60\u6211\uff0c\u8bf4\u662f\u795d\u8001\u8872\u4e09\u7532\u5b50\u5927\u5bff\u2014\u2014\u5475\uff0c\u8fd9\u94ee\u7adf\u7ed9\u6211\u6253\u6bc1\u4e86\u2014\u2014-\u5012\u4e5f\u7b97\u5f97\u4e0a\u4e00\u4ef6\u4e0a \u53e4\u73cd\u54c1\u5462\uff01\u201d \u3000\u3000\u8f9b\u6377\u542c\u4ed6\u5982\u6b64\u4e00\u8bf4\uff0c\u4e0d\u7531\u5927\u60ca\uff0c\u542c\u4ed6\u8bf4\u7adf\u6709\u4e8c\u767e\u5c81\u4e4b\u9ad8\u9f84\uff0c\u96be\u602a\u529f\u529b\u7cbe\u6e5b\u5982\u65af\uff0c\u60f3 \u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u604d\u7136\u5927\u609f\u2014\u2014\u539f\u6765\u5185\u529f\u8981\u60f3\u7ec3\u5230\u9a7b\u989c\u4e0d\u8001\u7684\u5730\u6b65\uff0c\u81f3\u5c11\u8981\u6709\u767e\u5e74\u7684\u529f\u529b\uff0c \u5426\u5219\u65e0\u8bba\u5185\u529b\u5982\u4f55\u82e6\u7ec3\uff0c\u4e5f\u81f3\u591a\u505a\u5230\u4e0d\u6613\u8870\u8001\u800c\u5df2\u2014\u2014\u5f53\u7136\u4e5f\u6709\u4f8b\u5916\uff0c\u8b6c\u5982\u8bf4\u65e0\u6781\u5c9b\u4e3b \u65e0\u6068\u751f\uff0c\u4ed7\u7740\u66fe\u670d\u4ed9\u679c\uff0c\u59cb\u7ec8\u4fdd\u6301\u4e09\u56db\u5341\u5c81\u4e2d\u5e74\u7684\u5f62\u6001\uff0c\u800c\u5e73\u51e1\u4e0a\u4eba\u867d\u6301\u4e09\u7532\u5b50\u7684\u529f\u529b\uff0c \u5df1\u81fb\u4e0d\u574f\u5730\u6b65\uff0c\u7136\u5176\u80fd\u505a\u5230\u9a7b\u989c\u4e0d\u8001\u4e43\u662f\u767e\u9f84\u4e4b\u540e\uff0c\u662f\u4ee5\u770b\u6765\u5c3d\u7ba1\u795e\u91c7\u98de\u626c\uff0c\u4ecd\u6bd4\u65e0\u6068 \u751f\u663e\u5f97\u82cd\u8001\u5f97\u591a\uff0c\u8fd9\u4e5f\u662f\u65e0\u6781\u5c9b\u4e3b\u552f\u4e00\u80fd\u80dc\u8fc7\u5927\u6222\u5c9b\u4e3b\u7684\u5730\u65b9\u3002 \u3000\u3000\u8f9b\u6377\u6b63\u5728\u60f3\u8fd9\u4e9b\u65f6\uff0c\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u65e0\u6068\u751f\u4e0d\u8fc7\u4ed7\u7740\u4e00\u9897\u4ed9\u679c\u800c\u5df2\uff0c\u5426\u5219\u51ed\u4ed6\u90a3 \u70b9\u529f\u529b\uff0c\u5c82\u80fd\u540d\u5217\u4e16\u5916\u4e09\u4ed9\uff1f\u201d \u3000\u3000\u8981\u77e5\u5e73\u51e1\u4e0a\u4eba\u529f\u529b\u8d85\u51fa\u65e0\u6068\u751f\u4e0d\u4e0b\u767e\u5e74\uff0c\u662f\u4ee5\u6b64\u8a00\u4e1d\u6beb\u4e0d\u4e3a\u8fc7\u3002\u4f46\u4e8b\u5b9e\u4e0a\u65e0\u6068\u751f\u66fe \u98df\u4ed9\u679c\uff0c\u4eba\u53c8\u7edd\u9876\u806a\u660e\uff0c\u662f\u4ee5\u5e74\u9f84\u867d\u8fdc\u8f83\u5176\u4ed6\u4e8c\u4ed9\u5e74\u8f7b\uff0c\u5374\u80fd\u4e0e\u5176\u4ed6\u4e8c\u4f4d\u65f7\u4e16\u4ed9\u4eba\u5e76\u9a7e \u9f50\u9a71\uff0c\u8f8e\u73e0\u5e76\u91cd\uff01 \u3000\u3000\u8f9b\u6377\u6bcf\u542c\u5e73\u51e1\u4e0a\u4eba\u4f4e\u8d2c\u65e0\u6068\u751f\uff0c\u80f8\u4e2d\u5c31\u6709\u8bf4\u4e0d\u51fa\u7684\u5feb\u611f\uff0c\u4f46\u5bfb\u5373\u60f3\u5230\u4eba\u5bb6\u90a3\u8eab\u6b66\u529f\uff0c \u7acb\u523b\u5fc3\u53c8\u6c89\u4e86\u4e0b\u53bb\uff0c\u4f46\u4ed6\u4e0d\u660e\u767d\u4f55\u4ee5\u5e73\u51e1\u4e0a\u4eba\u9887\u4e3a\u6ce8\u610f\u4ed6\u7684\u672c\u95e8\u529f\u592b\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u50cf\u662f\u957f\u4e45\u4e0d\u66fe\u4e0e\u4eba\u8c08\u8bdd\uff0c\u53c8\u4f3c\u5bf9\u8f9b\u6377\u7279\u522b\u6295\u7f18\uff0c\u5174\u81f4\u52c3\u52c3\u5730\u53c8\u63a5\u7740\u9053\uff1a \u201c\u56db\u5341\u591a\u5e74\u524d\uff0c\u54b1\u4eec\u4e16\u5916\u4e09\u4ed9\u5728\u65e0\u6781\u5c9b\u4e0a\u4e92\u76f8\u5370\u8bc1\u529f\u592b\uff0c\u65e0\u6068\u751f\u4ed7\u4ed9\u679c\u4e4b\u529f\uff0c\u9a7b\u989c\u4e0d\u8001 \u65b9\u9762\u81ea\u7136\u80dc\u8fc7\u8001\u8896\uff0c\u4f46\u8bba\u5230\u771f\u5b9e\u529f\u529b\uff0c\u90a3\u65e0\u6068\u751f\u4e5f\u81ea\u8ba4\u94a6\u670d\u8001\u8872\u7684\uff0c\u5374\u53ea\u6709\u8fd9\u5c0f\u6222\u5c9b\u4e3b \u6167\u5927\u5e08\uff0c\u4e0d\u80af\u8ba4\u8f93\u53e3\uff0c\u60f3\u6211\u8001\u8872\u8fd9\u5927\u5e74\u7eaa\u4e86\u8fd8\u4f1a\u548c\u5979\u771f\u6b63\u52a8\u624b\uff0c\u90a3\u77e5\u8001\u5c3c\u5a46\u7740\u5b9e\u53ef\u6076\uff0c \u7adf\u6446\u4e0b\u8fd9\u53e4\u602a\u9635\u6cd5\uff0c\u5c06\u8001\u8872\u8db3\u8db3\u56f0\u4e86\u5341\u5e74\uff0c\u8bf4\u6765\u8fd9\u9635\u4e5f\u7740\u5b9e\u53e4\u602a\uff0c\u5341\u5e74\u6765\u8001\u8872\u4ecd\u672a\u609f\u5f97 \u7834\u6cd5\uff0c\u660e\u5929\u5b50\u65f6\u5c31\u662f\u54b1\u4eec\u8d4c\u8d5b\u671f\u6ee1\uff0c\u8bf4\u4e0d\u5b9a\u8001\u8872\u53ea\u597d\u62fc\u4e86\u4e00\u7532\u5b50\u529f\u529b\u5c06\u8fd9\u5c0f\u5c9b\u7ed9\u6bc1\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u604d\u7136\u5927\u609f\uff0c\u539f\u6765\u8fd9\u5e73\u51e1\u4e0a\u4eba\u662f\u548c\u6167\u5927\u5e08\u5728\u6597\u6c14\uff0c\u602a\u4e0d\u5f97\u90a3\u94ee\u58f0\u4e2d\u6ee1\u662f\u6124\u6012\uff0c\u5fc3 \u60f3\u4ed6\u867d\u8bf4\u8fd9\u5927\u5e74\u7eaa\u4e0d\u4e0e\u4eba\u62fc\u6597\uff0c\u5176\u5b9e\u5374\u597d\u80dc\u5f97\u5f88\uff0c\u4ee5\u4ed6\u4e8c\u767e\u5e74\u4fee\u4e3a\u5c1a\u5982\u6b64\uff0c\u53ef\u89c1\u201c\u55d4\u201d \u5ff5\u662f\u5982\u4f55\u96be\u4ee5\u582a\u7834\u4e86\u3002\u60f3\u5230\u4ed6\u6700\u540e\u8bf4\u62fc\u7740\u4e00\u7532\u5b50\u529f\u529b\u4e5f\u8981\u5c06\u6b64\u5c9b\u6bc1\u6389\uff0c\u5fc3\u60f3\u8fd9\u5c9b\u867d\u5c0f\uff0c \u5374\u662f\u81ea\u6d77\u5e95\u4f38\u51fa\uff0c\u5c82\u80fd\u4ee5\u4eba\u529b\u6bc1\u53bb\uff0c\u4e0d\u7981\u751a\u662f\u4e0d\u4fe1\uff0c\u5ffd\u7136\u53c8\u60f3\u5230\u4ed6\u8bf4\u201c\u8fd9\u5c0f\u6222\u5c9b\u201d\uff0c\u96be \u9053\u8fd9\u662f\u5c0f\u6222\u5c9b\u800c\u975e\u5927\u6222\u5c9b\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u524d\u9762\u90a3\u767e\u9635\u4e2d\u5fc3\u6700\u9ad8\u7684\u77f3\u7b0b\u4e0a\u8d6b\u7136\u201c\u5c0f\u6222\u5c9b\u201d\u4e09 \u4e2a\u5927\u5b57\uff0c\u5374\u4e0d\u77e5\u6167\u5927\u5e08\u4f55\u4ee5\u4e0d\u89c1\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53ef\u4e0d\u7ba1\u8f9b\u6377\u5728\u60f3\u4ec0\u4e48\uff0c\u53ea\u50cf\u662f\u618b\u4e86\u5341\u5e74\u7684\u8bdd\u597d\u4e0d\u5bb9\u6613\u9047\u5230\u53ef\u503e\u8ff0\u7684\u4eba\uff0c\u4e0d \u65ad\u5730\u8c08\u81ea\u5df1\u7684\u82f1\u96c4\u5f80\u4e8b\uff0c\u8fd9\u65f6\u89c1\u8f9b\u6377\u59cb\u7ec8\u9759\u9759\u5730\u5728\u542c\u81ea\u5df1\u5439\uff0c\u4e0d\u89c9\u6709\u70b9\u4e0d\u597d\u610f\u601d\uff0c\u5ffd\u7136 \u5938\u9053\uff1a\u201c\u4f60\u8001\u5f1f\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u529f\u529b\u5374\u5982\u6b64\u4e4b\u7eaf\uff0c\u5b9e\u5728\u96be\u5f97\uff0c\u60f3\u4e0d\u5230\u4e2d\u539f\u8fd8\u6709\u5982\u6b64\u4eba\u7269\u80fd\u8c03 \u6559\u51fa\u4f60\u8fd9\u6837\u7684\u4eba\u624d\u3002\u201d \u3000\u3000\u82e5\u662f\u5e38\u4eba\u542c\u4e86\u4e16\u5916\u4e09\u4ed9\u4e4b\u9996\u5982\u6b64\u8d5e\u8d4f\uff0c\u4e00\u5b9a\u632f\u5174\u4e07\u5206\uff0c\u65e0\u5948\u8f9b\u6377\u5df2\u5bf9\u81ea\u5df1\u529f\u529b\u4fe1\u5fc3 \u5c3d\u5931\uff0c\u8138\u4e0a\u4ecd\u662f\u6728\u7136\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5bf9\u8f9b\u6377\u4f3c\u4e4e\u5341\u5206\u6295\u7f18\uff0c\u6b64\u523b\u7adf\u7d22\u6027\u79f0\u4ed6\u201c\u8001\u5f1f\u201d\uff0c\u82e5\u4ee5\u8f88\u4efd\u7b97\u6765\uff0c\u5e73\u51e1\u4e0a \u4eba\u505a\u4ed6\u9ad8\u7956\u4e5f\u6709\u4f59\uff0c\u6b64\u523b\u7adf\u4ee5\u201c\u8001\u5f1f\u201d\u76f8\u79f0\uff0c\u5c82\u4e0d\u6ed1\u7a3d\uff1f \u3000\u3000\u8fd9\u65f6\u5e73\u51e1\u4e0a\u4eba\u89c1\u8f9b\u6377\u5931\u9b42\u843d\u9b44\u7684\u6837\u5b50\uff0c\u7acb\u523b\u9053\uff1a\u201c\u4f60\u6216\u4ee5\u4e3a\u8f93\u7ed9\u65e0\u6068\u751f\u5c31\u81ea\u8ba4\u529f\u592b \u592a\u5dee\u5417\uff1f\u5176\u5b9e\u4f60\u5fd8\u4e86\u4e00\u4ef6\u6700\u91cd\u8981\u7684\u4e8b\u3002\u201d \u3000\u3000\u8f9b\u6377\u62ac\u5934\u95ee\u9053\uff1a\u201c\u665a\u8f88\u5fd8\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u4f60\u53ef\u5fd8\u4e86\u2018\u529f\u529b\u2019\u4e24\u5b57\uff0c\u65e0\u6068\u751f\u66fe\u670d\u4ed9\u679c\uff0c\u518d\u52a0\u4e0a\u8fd1\u767e\u5e74\u4fee\u7ec3\uff0c\u5c82 \u662f\u4f60\u5eff\u51e0\u5c81\u5a03\u5a03\u6240\u80fd\u654c\uff1f\u201d \u3000\u3000\u8f9b\u6377\u672c\u662f\u51b0\u96ea\u806a\u660e\uff0c\u53ea\u56e0\u8f93\u7ed9\u65e0\u6068\u751f\u8f93\u5f97\u592a\u60e8\uff0c\u624d\u5bf9\u672c\u8eab\u6b66\u529f\u4fe1\u5ff5\u5c3d\u5931\uff0c\u8fd9\u65f6\u88ab\u5e73 \u51e1\u4e0a\u4eba\u4e00\u8bed\u9053\u7834\uff0c\u7acb\u523b\u660e\u767d\u81ea\u5df1\u786e\u662f\u5ffd\u7565\u4e86\u201c\u529f\u529b\u201d\u4e24\u5b57\u3002 \u3000\u3000\u4f46\u4ed6\u60f3\u5230\u6bd4\u4eba\u5bb6\u5dee\u4e0a\u767e\u5e74\u4ee5\u4e0a\u7684\u529f\u529b\uff0c\u53ea\u6015\u4eca\u751f\u96be\u4ee5\u53ca\u5f97\u4e0a\u4e86\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53c8\u662f\u4e00\u9635 \u5931\u671b\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u77f3\u5934\u600e\u6837\uff1f\u201d\u8bf4\u7740\u6307\u7740\u524d\u9762\u5757\u5de8\u77f3\u3002 \u3000\u3000\u8f9b\u6377\u770b\u90a3\u77f3\u95e8\u4e43\u662f\u6781\u786c\u7684\u82b1\u5c97\u5ca9\uff0c\u6b63\u5947\u602a\u4f55\u4ee5\u5e73\u51e1\u4e0a\u4eba\u95ee\u8fd9\u77f3\u5934\uff0c\u90a3\u5e73\u51e1\u4e0a\u4eba\u5ffd\u5730 \u5355\u638c\u5fae\u626c\uff0c\u547c\u7684\u4e00\u58f0\u62cd\u51fa\uff0c\u90a3\u5de8\u77f3\u7acb\u523b\u9707\u6210\u7c89\u788e\u3002 \u3000\u3000\u8f9b\u6377\u770b\u4ed6\u7528\u7684\u4e43\u662f\u6781\u666e\u901a\u7684\u201c\u4e94\u884c\u638c\u6cd5\u201d\uff0c\u4f46\u5e73\u51e1\u4e0a\u4eba\u6253\u51fa\uff0c\u5a01\u529b\u81f3\u65af\uff0c\u8fd9\u5c31\u7ed9\u4e86 \u8f9b\u6377\u5bf9\u201c\u529f\u529b\u201d\u4e24\u5b57\u6700\u597d\u7684\u7b54\u6848\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5f97\u610f\u5730\u8bf4\uff1a\u201c\u8fd9\u4f60\u53ef\u4fe1\u5f97\u8fc7\u8001\u8872\u7684\u8bdd\u4e86\u5427\uff01\u8001\u5b9e\u8bf4\uff0c\u4f60\u522b\u628a\u65e0\u6068\u751f\u770b\u5f97\u90a3 \u4e48\u9ad8\uff0c\u6211\u8001\u548c\u5c1a\u4e0d\u7528\u4f20\u4f60\u4e00\u62db\u534a\u5f0f\uff0c\u53ea\u8981\u7565\u4e3a\u6210\u5168\u4f60\uff0c\u4ee5\u4f60\u7684\u672c\u95e8\u62db\u5f0f\uff0c\u4e0e\u4ed6\u63a5\u4e2a\u767e\u6765 \u62db\uff0c\u4fdd\u7ba1\u6ca1\u6709\u95ee\u9898\u3002\u201d \u3000\u3000\u8f9b\u6377\u867d\u7136\u662f\u4e2a\u57ce\u5e9c\u5f88\u6df1\u7684\u4eba\uff0c\u4f46\u6b64\u65f6\u7531\u8877\u5730\u6447\u4e86\u6447\u5934\uff0c\u8868\u793a\u4e0d\u4fe1\uff0c\u4ed6\u5fc3\u4e2d\u6697\u601d\uff1a \u201c\u867d\u8bf4\u65e0\u6068\u751f\u662f\u85c9\u4ed9\u679c\u4e4b\u529b\uff0c\u4f46\u4ed6\u638c\u4e0a\u529f\u592b\u5df2\u81fb\u2018\u7384\u7389\u901a\u771f\u2019\u7684\u81f3\u9ad8\u5883\u754c\uff0c\u5e73\u51e1\u4e0a\u4eba\u529f \u529b\u91cf\u9ad8\uff0c\u8981\u60f3\u7247\u523b\u4e4b\u5185\u4ee4\u6211\u80fd\u4e0e\u4ed6\u5bf9\u62c6\u767e\u62db\uff0c\u800c\u4e14\u4e0d\u6388\u6211\u4e00\u62db\u4e00\u5f0f\uff0c\u8fd9\u53ea\u6015\u4e07\u4e07\u4e0d\u80fd\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u8fd9\u5e74\u8f7b\u4eba\u5c45\u7136\u6447\u5934\u4e0d\u4fe1\u81ea\u5df1\u7684\u8bdd\uff0c\u4e0d\u7981\u6012\u9053\uff1a\u201c\u4f60\u80c6\u6562\u4e0d\u4fe1\u8001\u8872\u6240\u8a00\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u8001\u524d\u8f88\u867d\u7136\u529f\u529b\u76d6\u4e16\uff0c\u65e0\u5948\u665a\u8f88\u529f\u529b\u4e0e\u4eba\u5bb6\u76f8\u5dee\u592a\u8fdc\uff0c\u81ea\u77e5\u7edd\u4e0d\u53ef\u80fd\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4f3c\u4e4e\u6781\u6613\u53d1\u6012\uff0c\u5f53\u4e0b\u6ee1\u8138\u6012\u5bb9\u5730\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u3000\u3000\u8f9b\u6377\u89c1\u8fd9\u6012\u6c14\u52c3\u52c3\u7684\u8001\u8005\u8138\u4e0a\uff0c\u6d41\u9732\u7740\u4e00\u80a1\u86ee\u6a2a\u7684\u795e\u8272\uff0c\u53e3\u4e0a\u7b54\u9053\uff1a\u201c\u665a\u8f88\u786e\u4fe1\u5982 \u6b64\u3002\u201d\u5fc3\u4e2d\u5374\u6697\u7b11\u8fd9\u5e73\u51e1\u4e0a\u4eba\u4e09\u7532\u5b50\u7684\u4fee\u4e3a\uff0c\u6027\u60c5\u4ecd\u7136\u5982\u6b64\uff0c\u4ed6\u5e74\u8f7b\u65f6\u7684\u9a84\u72c2\u53ef\u60f3\u800c\u77e5 \u4e86\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u597d\uff0c\u54b1\u4eec\u8d4c\u4e0a\u4e00\u8d4c\uff0c\u4f60\u4e14\u8fc7\u6765\u3002\u201d\u8f9b\u6377\u89c1\u4ed6\u4e00\u8138\u6b63\u7ecf\uff0c\u4f9d\u8a00\u8fc7\u53bb\uff0c \u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136\u53cc\u638c\u4e00\u7ffb\uff0c\u6263\u4f4f\u8f9b\u6377\u53cc\u624b\u8109\u95e8\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u62db\u75be\u4f3c\u95ea\u7535\uff0c\u8f9b\u6377\u5168\u529b\u65bd\u4e3a\u4ea6\u4e0d\u6613\u8eb2\u8fc7\uff0c\u4f55\u51b5\u6beb\u65e0\u9632\u5907\u7684\u60c5\u5f62\u4e0b\uff0c\u7acb\u523b\u88ab\u4ed6 \u7262\u7262\u6293\u4f4f\uff0c\u5168\u8eab\u767b\u65f6\u8f6f\u7ef5\u7ef5\u7684\uff0c\u4e1d\u6beb\u7528\u4e0d\u51fa\u529b\u6765\u3002 \u3000\u3000\u4f46\u4ed6\u7acb\u523b\u611f\u5230\u4e00\u80a1\u70ed\u6d41\u4ece\u53cc\u624b\u8109\u95e8\u7f13\u7f13\u6d41\u4eba\u4f53\u5185\u90a3\u70ed\u6d41\u4e13\u4ece\u7a74\u9053\u4e2d\u6d41\u8fc7\uff0c\u5168\u8eab\u867d\u7136 \u65bd\u4e0d\u51fa\u529b\u9053\uff0c\u4f46\u56db\u80a2\u767e\u9ab8\u8212\u723d\u65e0\u6bd4\uff0c\u6709\u8bf4\u4e0d\u51fa\u7684\u53d7\u7528\u3002 \u3000\u3000\u6e10\u6e10\u90a3\u70ed\u6d41\u6108\u901f\uff0c\u8feb\u5f97\u4ed6\u8fd0\u8d77\u672c\u95e8\u5185\u529f\u6765\u5f15\u9053\u90a3\u70ed\u6d41\u8fdb\u5165\u6b63\u9053\uff0c\u4ed6\u4e00\u8fd0\u8d77\u5185\u529f\uff0c\u7acb \u5373\u70ed\u6d41\u4e0e\u672c\u8eab\u5185\u529f\u878d\u4e3a\u4e00\u4f53\uff0c\u6781\u5176\u8212\u723d\u5730\u5468\u8f6c\u5168\u8eab\u3002 \u3000\u3000\u5077\u773c\u4e00\u770b\u90a3\u5e73\u51e1\u4e0a\u4eba\uff0c\u6b64\u65f6\u9762\u4e0a\u4e00\u7247\u8083\u7a46\uff0c\u5634\u89d2\u5fae\u5e26\u4e00\u4e1d\u5f97\u610f\u7684\u7b11\u5bb9\uff0c\u521a\u624d\u90a3\u80a1\u6012 \u5bb9\u4e00\u626b\u800c\u7a7a\uff0c\u800c\u7ea2\u5149\u7115\u53d1\u7684\u79c3\u9876\u4e0a\u9635\u9635\u767d\u6c14\u5192\u51fa\uff0c\u8f9b\u6377\u4f55\u7b49\u6167\u8bdd\uff0c\u7acb\u523b\u77e5\u9053\u5e73\u51e1\u4e0a\u4eba\u54ea \u662f\u548c\u81ea\u5df1\u751f\u6c14\uff0c\u4e0d\u8fc7\u85c9\u6545\u6210\u5168\u81ea\u5df1\u7f62\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u5e73\u51e1\u5927\u5e08\u53cc\u638c\u4e00\u677e\uff0c\u7b11\u9053\uff1a\u201c\u73b0\u4f60\u53ef\u518d\u8fd0\u529f\u4e00\u5468\u540e\uff0c\u5bf9\u8fd9\u767e\u7b0b\u53d1\u4e00\u638c\u8bd5 \u8bd5\u3002\u201d \u3000\u3000\u8f9b\u6377\u4f9d\u8a00\u8fd0\u529f\u4e00\u5468\uff0c\u731b\u4e00\u63d0\u6c14\uff0c\u5355\u638c\u4e00\u8bb0\u201c\u4e8c\u90ce\u5f00\u5f13\u201d\u62cd\u51fa\uff0c\u53ea\u542c\u5f97\u8f70\u7136\u4e00\u58f0\uff0c\u4e00 \u65b9\u575a\u786c\u65e0\u6bd4\u7684\u82b1\u5c97\u5ca9\u5de8\u77f3\u7adf\u9694\u7a7a\u51fb\u6210\u7c89\u788e\u3002\u8f9b\u6377\u5bf9\u81ea\u5df1\u529f\u529b\u7cbe\u8fdb\u5982\u65af\uff0c\u60ca\u5f97\u5446\u4e86\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e43\u662f\u4ee5\u201c\u7cca\u63d0\u704c\u9876\u201d\u7684\u7edd\u9876\u5185\u529f\u5c06\u81ea\u5df1\u4e8c\u5341\u5e74\u529f\u529b\u6253\u4eba\u8f9b\u6377\u5168\u8eab\u7a74\u9053\uff0c\u4ee5 \u5e73\u51e1\u4e0a\u4eba\u7684\u4e8c\u5341\u5e74\u529f\u529b\uff0c\u82e5\u8ba9\u8f9b\u6377\u81ea\u884c\u4fee\u7ec3\uff0c\u81f3\u5c11\u4e5f\u8981\u4e00\u7532\u5b50\u7684\u5149\u9634\uff0c\u96be\u602a\u8f9b\u6377\u81ea\u5df1\u4e5f \u8981\u60ca\u5f97\u77aa\u53e3\u5446\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u8fde\u5fd9\u7ffb\u8eab\u62dc\u5012\uff0c\u5e73\u51e1\u4e0a\u4eba\u53cc\u8896\u4e00\u62c2\uff0c\u5c06\u8f9b\u6377\u62ac\u8d77\uff0c\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u5a03\u5a03\u4f60\u83ab\u8c22 \u6211\uff0c\u5c31\u662f\u8001\u8872\u4e5f\u4ece\u4f60\u8fd0\u529f\u65f6\u5f97\u5230\u4e0d\u5c11\u5185\u529f\u5999\u7edd\uff0c\u54c8\u54c8\uff0c\u4f60\u90a3\u5e08\u7236\u679c\u662f\u4e00\u4ee3\u5947\u4eba\uff0c\u8981\u77e5\u867d \u662f\u4ee5\u6211\u7684\u529f\u529b\u6253\u5916\u4f60\u7a74\u9053\u5185\uff0c\u4f46\u5982\u4f60\u672c\u8eab\u6ca1\u6709\u4e00\u79cd\u7cbe\u5999\u4e0e\u8001\u8872\u5185\u529f\u76f8\u5f53\u7684\u5185\u529b\u5f15\u5bfc\uff0c\u4e5f \u662f\u5f92\u7136\uff0c\u73b0\u5728\u4f60\u603b\u8be5\u76f8\u4fe1\u4f60\u672c\u95e8\u5185\u529f\u7cbe\u5999\u4e0d\u5728\u65e0\u6068\u751f\u4e4b\u4e0b\u4e86\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u62ac\u5934\u770b\u7740\u90a3\u7ea2\u5149\u6ee1\u9762\u7684\u6148\u7965\u518d\u76ee\uff0c\u80f8\u4e2d\u70ed\u8840\u4e0a\u6d8c\uff0c\u6b64\u7684\u53eb\u4ed6\u7acb\u523b\u4e3a\u5e73\u51e1\u4e0a\u4eba\u6b7b \u53bb\uff0c\u4ed6\u4e5f\u60c5\u613f\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u7531\u4f60\u7684\u5185\u529f\u4e0a\u731c\u60f3\uff0c\u4f60\u5e08\u95e8\u7684\u62f3\u5251\u529f\u592b\u5fc5\u4ea6\u7cbe\u5947\uff0c\u4f60\u4e14\u65bd\u4e00\u4e24\u62db \u7ed9\u6211\u8001\u513f\u770b\u770b\u3002\u201d \u3000\u3000\u8f9b\u6377\u6697\u9053\uff1a\u201c\u539f\u6765\u4f60\u4e5f\u55dc\u6b66\u5f97\u5f88\u3002\u201d\u5fc3\u4e2d\u4e0d\u7981\u4e00\u4e50\u3002\u53c8\u601d\u81ea\u5df2\u65bd\u51fa\u5e08\u95e8\u7edd\u6280\uff0c\u82e5\u6709 \u7f3a\u70b9\uff0c\u5e73\u51e1\u4e0a\u4eba\u5fc5\u4f1a\u6307\u6b63\uff0c\u8fd9\u6b63\u662f\u5343\u8f7d\u4e00\u9022\u7684\u826f\u673a\uff0c\u4ed6\u5982\u4f55\u80fd\u653e\u8fc7\uff0c\u5f53\u4e0b\u968f\u624b\u5728\u5730\u4e0a\u4e00 \u6478\uff0c\u62fe\u8d77\u4e00\u679d\u67af\u7af9\uff0c\u731b\u7136\u63d0\u6c14\uff0c\u659c\u659c\u4e00\u5251\u5288\u51fa\uff0c\u8f7b\u8106\u67af\u7af9\u5c16\u4e0a\u7adf\u5e26\u4e1d\u4e1d\u98ce\u54cd\uff0c\u6b63\u662f\u4e03\u5999 \u795e\u541b\u6240\u4f20\u5251\u672f\u201c\u6885\u82b1\u4e09\u5f04\u201d\u3002 \u3000\u3000\u8f9b\u6377\u8fd9\u62db\u201c\u6885\u82b1\u4e09\u5f04\u201d\u4e43\u662f\u4e03\u5999\u795e\u541b\u5e73\u751f\u7edd\u5b66\u201c\u8679\u679d\u5251\u5f0f\u201d\u4e2d\u7684\u7b2c\u4e09\u5f0f\uff0c\u8fd9\u65f6\u4ed6\u53c8 \u662f\u5168\u529b\u65bd\u4e3a\uff0c\u5251\u5c16\u6240\u751f\u5c16\u9510\u4e4b\u58f0\u9aa4\u8d77\uff0c\u610f\u7136\u9694\u7a7a\u5c06\u5730\u4e0a\u5212\u5f00\u534a\u5bf8\u6df1\u7684\u77f3\u75d5\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u8f9b\u6377\u53c8\u662f\u5927\u51fa\u610f\u6599\uff0c\u5f53\u65f6\u6885\u5c71\u6c11\u66fe\u5bf9\u4ed6\u8bf4\uff0c\u201c\u866c\u679d\u5251\u5f0f\u201d\u867d\u7136\u7cbe\u5999\uff0c\u4f46\u82e5\u80fd \u7ec3\u5230\u5c06\u771f\u529b\u4efb\u610f\u903c\u51fa\u5251\u5c16\uff0c\u624d\u80fd\u53d1\u6325\u6700\u5927\u5a01\u529b\uff0c\u4f46\u8981\u60f3\u7ec3\u5230\u5982\u6b64\u5730\u6b65\uff0c\u975e\u6709\u4e00\u7532\u5b50\u4ee5\u4e0a \u529f\u529b\u4e0d\u6210\uff0c\u4efb\u4f60\u5929\u8d44\u7edd\u9876\uff0c\u5c0f\u5c0f\u5e74\u7eaa\u7edd\u4e0d\u53ef\u80fd\u8fbe\u6b64\u5883\u754c\uff0c\u8fd9\u65f6\u8f9b\u6377\u89c1\u81ea\u5df1\u5c45\u7136\u80fd\u591f\u8fbe\u6b64\uff0c \u5f53\u7136\u60ca\u559c\u4e0d\u5df2\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u4e00\u62db\u201c\u6885\u82b1\u4e09\u5f04\u201d\u8fd8\u672a\u65bd\u8db3\uff0c\u624b\u8155\u4e00\u7ffb\uff0c\u67af\u679d\u547c\u5730\u4e00\u58f0\u5316\u6210\u4e00\u7247\u679d\u5f71\uff0c\u8fdc\u770b \u8fc7\u53bb\uff0c\u5374\u53ef\u5206\u8fa8\u51fa\u679d\u5c16\u5708\u6210\u4e00\u6735\u6735\u6885\u82b1\uff0c\u4f46\u7a81\u5730\u4e00\u58f0\u8f7b\u5636\uff0c\u4e00\u7247\u679d\u5f71\u4e2d\u7af9\u5c16\u7adf\u5df1\u523a\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u5251\u8d70\u504f\u950b\uff0c\u7aef\u7684\u8be1\u5999\u5df2\u6781\uff0c\u5bf9\u65b9\u82e5\u6709\u6545\u4eba\uff0c\u5fc5\u7136\u6b63\u5fd9\u4e8e\u5e94\u4ed8\u90a3\u4e00\u7247\u5251\u5f71\u65f6\uff0c \u7a81\u89c9\u5251\u5c16\u5df2\u5230\u4e86\u5589\u524d\uff0c\u8eb2\u65e0\u53ef\u8eb2\u3002\u8fd9\u6b63\u662f\u4e03\u5999\u795e\u541b\u7684\u5f97\u610f\u6770\u4f5c\u201c\u51b7\u6885\u62c2\u9762\u201d\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u9177\u7231\u6885\u82b1\uff0c\u6709\u4e00\u5929\u53d1\u73b0\u4e00\u679d\u9690\u85cf\u5728\u8def\u65c1\u5c71\u8def\u65c1\u5c71\u77f3\u540e\u9762\u7684\u4e00\u68f5\u6885\u82b1\uff0c\u90a3\u68f5 \u6885\u82b1\u4f3c\u4e4e\u751f\u6015\u81ea\u5df1\u751f\u5904\u9690\u853d\uff0c\u4e0d\u6613\u4e3a\u4eba\u53d1\u89c9\uff0c\u6240\u4ee5\u7279\u957f\u51fa\u4e00\u679d\u659c\u4f38\u51fa\u8def\u9762\uff0c\u8def\u4eba\u4e00\u4e0d\u6ce8 \u610f\u5c31\u88ab\u6811\u679d\u62c2\u9762\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5f53\u65f6\u7075\u673a\u4e00\u52a8\uff0c\u7acb\u523b\u521b\u51fa\u8fd9\u6837\u4e00\u62db\u4e13\u8d70\u504f\u950b\u7684\u7edd\u5999\u62db\u5f0f\uff0c\u4e5f\u53ea\u6709\u6885\u5c71\u6c11\u8fd9\u79cd \u504f\u6fc0\u800c\u806a\u660e\u7edd\u9876\u7684\u4eba\u624d\u80fd\u521b\u51fa\u8fd9\u4e00\u62db\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5bf9\u8fd9\u9752\u5e74\u751a\u662f\u6b23\u8d4f\uff0c\u8fd9\u65f6\u770b\u4ed6\u9762\u5e26\u60a6\u5bb9\uff0c\u624b\u4e0a\u7af9\u679d\u62db\u62db\u7cbe\u5947\uff0c\u77e5\u4ed6\u5df2\u6062\u590d \u4fe1\u5fc3\uff0c\u4e0d\u7981\u62c8\u987b\u5fae\u7b11\u3002 \u3000\u3000\u53ca\u89c1\u8f9b\u6377\u65bd\u51fa\u8fd9\u4e00\u62db\u201c\u51b7\u6885\u62c2\u9762\u201d\u6765\u8fde\u4ed6\u4e5f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u8981\u77e5\u5e73\u51e1\u4e0a\u4eba\u6b66\u5b66\u5df1\u5165 \u5316\u5883\uff0c\u4efb\u4f55\u5251\u62db\u53ea\u8981\u4e00\u51fa\u624b\uff0c\u7acb\u523b\u80fd\u9884\u77e5\u5b83\u7684\u62db\u5f0f\u53ca\u5229\u5f0a\uff0c\u4f46\u8fd9\u62db\u201c\u51b7\u6885\u62c2\u9762\u201d\u5374\u5927\u51fa \u4ed6\u610f\u6599\u4e4b\u5916\uff0c\u5c82\u80fd\u4e0d\u60ca\u3002\u4f46\u4ed6\u4e43\u662f\u4e00\u4ee3\u5b97\u5e08\uff0c\u4f55\u7b49\u773c\u5149\uff0c\u7acb\u523b\u770b\u51fa\u8fd9\u62db\u7684\u5999\u5904\uff0c\u5f53\u4e0b\u5927 \u559d\u4e00\u58f0\uff1a\u201c\u82e5\u6211\u65bd\u4e00\u62db\u2018\u5434\u521a\u4f10\u6842\u2019\uff0c\u4f60\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6b63\u5c06\u8fd9\u62db\u201c\u51b7\u6885\u62c2\u9762\u201d\u4f7f\u6ee1\uff0c\u5ffd\u95fb\u5e73\u51e1\u4e0a\u4eba\u8fd9\u4e00\u53e5\u8bdd\uff0c\u767b\u65f6\u67af\u7af9\u5782\u5730\uff0c\u5446\u5446\u6014 \u4f4f\u4e86\u3002\u201c\u5434\u521a\u4f10\u6842\u201d\u8fd9\u62db\u6781\u5e73\u51e1\u7684\u62db\u5f0f\uff0c\u4ece\u8111\u6d77\u4e2d\u5982\u95ea\u7535\u822c\u6d41\u8fc7\uff0c\u8fd9\u6781\u666e\u901a\u7684\u62db\u5f0f\u5374\u521a \u597d\u80fd\u5c06\u81ea\u5df1\u8fd9\u62db\u5c01\u4f4f\uff0c\u53ea\u662f\u8fd9\u6781\u666e\u901a\u7684\u62db\u5f0f\u5728\u6b64\u65f6\u7528\u6765\uff0c\u7aef\u7684\u795e\u5999\u65e0\u6bd4\uff0c\u4e03\u5999\u795e\u541b\u5f53\u521d \u521b\u8fd9\u62db\u5f0f\u65f6\uff0c\u66fe\u628a\u6b66\u6797\u4e2d\u4e00\u5207\u4e0a\u4e58\u5251\u6cd5\u90fd\u8003\u8651\u8fc7\uff0c\u4e13\u95e8\u5bf9\u4ed8\u90a3\u4e9b\u540d\u95e8\u5251\u62db\uff0c\u90a3\u77e5\u7adf\u88ab\u5e73 \u51e1\u4e0a\u4eba\u4ee5\u8fd9\u4e00\u8bb0\u666e\u901a\u62db\u5f0f\u6b63\u597d\u5c01\u4f4f\uff0c\u5c31\u662f\u6885\u5c71\u6c11\u672c\u4eba\u4e5f\u5fc5\u6599\u4e0d\u5230\u7684\u3002 \u3000\u3000\u5ffd\u7136\u8f9b\u6377\u5355\u7af9\u518d\u633d\uff0c\u5de6\u8db3\u5fae\u8de8\uff0c\u53f3\u624b\u4e0a\u7af9\u679d\u5374\u7531\u4e0b\u800c\u4e0a\u659c\u659c\u64a9\u4e0a\uff0c\u6b63\u662f\u201c\u866c\u679d\u5251\u5f0f\u201d \u4e2d\u7684\u7b2c\u516d\u5f0f\u201c\u8e0f\u96ea\u5bfb\u6885\u201d\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u6211\u7528\u4e00\u62db\u2018\u6a2a\u98de\u6e21\u6c5f\u2019\u3002\u201d \u3000\u3000\u8f9b\u6377\u53c8\u662f\u4e00\u6014\uff0c\u6697\u601d\u90a3\u201c\u6a2a\u98de\u6e21\u6c5f\u201d\u6b63\u597d\u53c8\u80fd\u5316\u53bb\u81ea\u5df1\u8fd9\u4e00\u62db\uff0c\u4e0d\u7981\u597d\u80dc\u4e4b\u5fc3\u767b\u8d77\u3002 \u3000\u3000\u201c\u6a2a\u98de\u6e21\u6c5f\u201d\u867d\u4e5f\u5341\u5206\u7cbe\u5999\uff0c\u4f46\u4ecd\u7b97\u4e0d\u4e0a\u6700\u4e0a\u4e58\u7684\u5251\u62db\uff0c\u6885\u5c71\u6c11\u7684\u5251\u6cd5\u591a\u662f\u4e13\u4e3a\u5bf9 \u4ed8\u5404\u5927\u95e8\u6d3e\u800c\u521b\uff0c\u62db\u5f0f\u867d\u7136\u795e\u5999\u65e0\u65b9\uff0c\u4f46\u5374\u53cd\u800c\u6ca1\u6709\u987e\u53ca\u4e00\u822c\u666e\u901a\u7684\u62db\u5f0f\uff0c\u5e73\u51e1\u4e0a\u4eba\u6b66 \u5b66\u5df2\u4e0a\u901a\u4e0b\u8fbe\uff0c\u51ed\u6df1\u539a\u529f\u529b\uff0c\u4e00\u773c\u5c31\u770b\u51fa\u8f9b\u6377\u62db\u5f0f\u4e2d\u7684\u7279\u70b9\uff0c\u662f\u4ee5\u5c3d\u7528\u4e00\u4e9b\u666e\u901a\u62db\u5f0f\u6765 \u5316\u89e3\u3002 \u3000\u3000\u8f9b\u6377\u597d\u80dc\u4e4b\u5fc3\u4e00\u8d77\uff0c\u5237\u5237\u5237\u4e00\u8fde\u6570\u5251\uff0c\u5177\u662f\u201c\u866c\u679d\u5251\u5f0f\u201d\u4e2d\u7cbe\u5965\u4e4b\u62db\uff0c\u5e73\u51e1\u4e0a\u4eba\u867d \u7136\u7b11\u53e3\u541f\u541f\u5730\u4e00\u4e00\u5316\u89e3\uff0c\u4f46\u5fc3\u4e2d\u5df1\u6697\u60ca\u8f9b\u6377\u5251\u6cd5\u7684\u7cbe\u5947\u4e86\u3002 \u3000\u3000\u8fd9\u6837\u4e24\u4eba\uff0c\u4e00\u4e2a\u7528\u7af9\u679d\uff0c\u4e00\u4e2a\u7528\u53e3\u820c\uff0c\u4e00\u62db\u4e00\u5f0f\u4e92\u62c6\u8d77\u6765\uff0c\u5230\u4e86\u5eff\u62db\u540e\uff0c\u8f9b\u6377\u65bd\u51fa \u7684\u201c\u866c\u679d\u5251\u5f0f\u201d\u4e0d\u80fd\u4ee5\u666e\u901a\u62db\u5f0f\u76f8\u62c6\uff0c\u5e73\u51e1\u4e0a\u4eba\u53cc\u624b\u4e5f\u5f00\u59cb\u6bd4\u5212\uff0c\u7528\u4ed6\u6bd5\u751f\u5f97\u610f\u7edd\u5b66 \u201c\u5927\u884d\u795e\u5251\u201d\u548c\u201c\u866c\u679d\u5251\u5f0f\u201d\u5bf9\u62c6\u8d77\u6765\u3002 \u3000\u3000\u201c\u5927\u884d\u795e\u5251\u201d\u4e00\u5171\u5341\u5f0f\uff0c\u4f46\u5176\u4e2d\u6bcf\u5f0f\u53c8\u6697\u85cf\u4e94\u4e2a\u53d8\u5316\uff0c\u5171\u662f\u4e94\u5341\u5f0f\uff0c\u6697\u5408\u5927\u884d\u4e4b\u6570\uff0c \u662f\u4e16\u5916\u4e09\u4ed9\u4e4b\u9996\u6bd5\u751f\u5f97\u610f\u4e4b\u4f5c\uff0c\u81ea\u7136\u795e\u5999\u65e0\u65b9\uff0c\u4efb\u201c\u8679\u679d\u5251\u5f0f\u201d\u5947\u62db\u602a\u5f0f\u5c42\u51fa\u4e0d\u7a77\uff0c\u4f46 \u78b0\u4e0a\u5e73\u51e1\u4e0a\u4eba\u53cc\u624b\u5fae\u5fae\u4e00\u6bd4\u5212\uff0c\u7acb\u523b\u5a01\u529b\u987f\u5931\uff0c\u8f9b\u6377\u4e00\u9762\u5c3d\u529b\u65bd\u4e3a\uff0c\u4e00\u9762\u6697\u4e2d\u4f53\u5473\u201c\u5927 \u884d\u795e\u5251\u201d\u4e2d\u7684\u5999\u5904\uff0c\u4ed6\u672c\u5c31\u806a\u660e\u65e0\u6bd4\uff0c\u66f4\u52a0\u5251\u672f\u57fa\u7840\u6781\u4f73\uff0c\u800c\u90a3\u5927\u884d\u795e\u5251\u867d\u7136\u53d8\u5316\u7cbe\u5965 \u65e0\u6bd4\uff0c\u62db\u5f0f\u5374\u662f\u6781\u4e3a\u7b80\u5355\u6613\u8bb0\uff0c\u4e00\u65f6\u867d\u4ecd\u6709\u8bb8\u591a\u5999\u5904\u4e0d\u80fd\u7406\u89e3\uff0c\u4f46\u62db\u5f0f\u5374\u4e00\u4e00\u786c\u8bb0\u4f4f\u3002 \u3000\u3000\u8fd9\u65f6\u201c\u5927\u884d\u5341\u5f0f\u201d\u5df2\u4f7f\u5b8c\u4e00\u904d\uff0c\u5e73\u51e1\u4e0a\u4eba\u4f3c\u4e4e\u6709\u610f\u4f9d\u6b21\u4e00\u62db\u62db\u65bd\u51fa\uff0c\u8ba9\u8f9b\u6377\u4fbf\u4e8e\u8bb0 \u5fc6\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6108\u62c6\u6108\u611f\u8f9b\u6377\u4e4b\u5e08\u7236\u7684\u624d\u534e\u76d6\u4e16\uff0c\u5fc3\u4e2d\u5df2\u77e5\u5176\u5e08\u7236\u5fc5\u4e3a\u4e2d\u539f\u6b66\u6797\u76db\u4f20\u7684\u4e00 \u4ee3\u9b3c\u624d\u201c\u4e03\u5999\u795e\u541b\u201d\u3002 \u3000\u3000\u201c\u866c\u679d\u5251\u5f0f\u201d\u4e5f\u5df1\u5230\u4e86\u6700\u540e\u5341\u5f0f\uff0c\u8fd9\u5341\u5f0f\u4e43\u662f\u6885\u5c71\u6c11\u771f\u6b63\u6bd5\u751f\u5fc3\u8840\u6240\u5728\uff0c\u7b2c\u4e00\u62db \u201c\u5bd2\u6885\u5410\u854a\u201d\u5c31\u5982\u5343\u5251\u4e07\u5f71\u6d12\u4e0b\uff0c\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u82e5\u8981\u5316\u89e3\u4ee5\u6c42\u81ea\u5b88\u5f53\u7136\u6613\u5982\u53cd\u638c\uff0c\u4f46\u8981\u60f3\u5b88\u4e2d\u5e26\u653b\u5730\u56de\u4ed6\u4e00\u62db\u540c\u6837\u4f73\u5999\u7684 \u7edd\u62db\uff0c\u5374\u4e00\u65f6\u4e0d\u80fd\uff0c\u8fd9\u4e00\u4ee3\u5b97\u5e08\u7adf\u88ab\u4e00\u65f6\u6014\u4f4f\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u505c\u7af9\u4e0d\u52a8\uff0c\u51dd\u89c6\u5e73\u51e1\u4e0a\u4eba\u51fa\u4f55\u5999\u62db\u3002\u5927\u7ea6\u4e24\u4e09\u5206\u949f\u540e\uff0c\u5e73\u51e1\u4e0a\u4eba\u5de6\u624b\u4e00\u6325\uff0c \u53f3\u624b\u4e00\u5708\u4e4b\u95f4\u7f13\u7f13\u9012\u51fa\u3002 \u3000\u3000\u8fd9\u62db\u4e0d\u77e5\u540d\u7684\u62db\u5f0f\uff0c\u5374\u6b63\u597d\u5316\u53bb\u8f9b\u6377\u7edd\u5999\u7684\u201c\u5bd2\u6885\u5410\u854a\u201d\uff0c\u800c\u4e14\u53cd\u51fb\u8f9b\u6377\u80a9\u4e0a\u7a74\uff0c \u65e0\u8bba\u65f6\u95f4\u7a7a\u95f4\u90fd\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u786e\u662f\u5999\u7edd\u4eba\u5bf0\u7684\u4e00\u5f0f\u3002 \u3000\u3000\u8f9b\u6377\u6b63\u4e00\u9762\u611f\u53f9\uff0c\u4e00\u9762\u7b79\u601d\u5316\u89e3\u4e4b\u7b56\uff0c\u5ffd\u7136\u4e00\u58f0\u6781\u4e3a\u602a\u5f02\u7684\u7b11\u58f0\u53d1\u81ea\u9ad8\u5904\uff1a\u201c\u540d\u548c \u5c1a\u53d8\u76f8\u6388\u5f92\uff0c\u5927\u6982\u662f\u6015\u4e00\u8eab\u529f\u592b\u846c\u9001\u6b64\u9635\uff0c\u60f3\u627e\u8863\u94b5\u4f20\u4eba\u662f\u4e0d\u662f\uff1f\u201d \u3000\u3000\u8f9b\u6377\u62ac\u5934\u4e00\u770b\uff0c\u4f9d\u7a00\u53ef\u89c1\u4e00\u4e2a\u8001\u5c3c\u7aef\u7acb\u5728\u77f3\u7b0b\u9876\u5904\uff0c\u5bf9\u5e73\u51e1\u4e0a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u8fd8\u5269\u4e00 \u4e2a\u65f6\u8fb0\u4e86\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6b63\u81ea\u5f97\u610f\u81ea\u5df1\u8fd9\u4e00\u62db\uff0c\u4e00\u542c\u8001\u5c3c\u4e4b\u8a00\uff0c\u8138\u4e0a\u7b11\u5bb9\u987f\u655b\uff0c\u7acb\u523b\u5316\u4e3a\u4e00\u8138\u6012\u5bb9\uff0c \u4ef0\u9996\u9053\uff1a\u201c\u8001\u5c3c\u5a46\u4f11\u5f97\u7316\u72c2\uff0c\u8fd8\u6709\u4e00\u4e2a\u65f6\u8fb0\u5462\uff01\u201d \u3000\u3000\u90a3\u8001\u5c3c\u957f\u7b11\u4e00\u58f0\uff0c\u5b9b\u82e5\u8001\u9f99\u957f\u541f\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8d2b\u5c3c\u7565\u5e03\u5c0f\u9635\u5c31\u4ee4\u4f60\u5341\u5e74\u65e0\u6cd5\u7834\u89e3\uff0c \u8fd8\u6709\u4f60\u8bf4\u53e3\u7684\u4efd\u4e48\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4f3c\u4e4e\u88ab\u4ed6\u6fc0\u5f97\u6012\u706b\u4e07\u4e08\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u7adf\u7528\u7684\u662f\u4e0a\u4e58\u5185\u5bb6\u4f5b\u95e8\u72ee\u5b50\u543c\uff0c\u9707\u5f97 \u8f9b\u6377\u5fc3\u795e\u4ff1\u52a8\uff0c\u7aef\u7684\u52a8\u4eba\u5fc3\u9b44\u3002\u53ea\u542c\u4ed6\u72e0\u58f0\u9053\uff1a\u201c\u8001\u5c3c\u5a46\u4e14\u4e0d\u8981\u5f97\u610f\uff0c\u60f9\u5f97\u8001\u795e\u6027\u8d77\uff0c \u5c31\u62fc\u4e86\u4e00\u7532\u5b50\u529f\u529b\u4e5f\u8ba9\u4f60\u8fd9\u5c0f\u5c9b\u9646\u6c89\u3002\u201d \u3000\u3000\u90a3\u8001\u5c3c\u95fb\u8a00\u4f3c\u4e4e\u4e00\u6014\uff0c\u4f46\u968f\u5373\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u544a\u8bc9\u4f60\u4e5f\u4e0d\u59a8\uff0c\u8fd9\u9635\u4e43\u662f\u5524\u7740\u2018\u5f52\u5143 \u56db\u8c61\u9635\u2019\uff0c\u4f60\u82e5\u628a\u5b83\u5f53\u2018\u5947\u95e8\u4e94\u884c\u9635\u2019\uff0c\u90a3\u5c31\u5927\u5927\u9519\u4e86\uff01\u201d\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u8eab\u5f62\u4e00\u6643\uff0c \u7acb\u5931\u8e2a\u5f71\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5fc3\u4e2d\u6697\u9053\u4e00\u58f0\u60ed\u6127\uff0c\u539f\u6765\u4ed6\u5341\u5e74\u6765\u59cb\u7ec8\u628a\u6b64\u767e\u7b0b\u9635\u5f53\u4f5c\u201c\u5947\u95e8\u4e94\u884c\u9635\u201d\u6765 \u7814\u7a76\uff0c\u81ea\u7136\u65e0\u6cd5\u7834\u89e3\uff0c\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u8f7b\u53f9\u4e00\u58f0\u3002 \u3000\u3000\u8f9b\u6377\u4f55\u7b49\u806a\u6167\uff0c\u5f53\u7136\u77e5\u9053\u90a3\u8001\u5c3c\u6b63\u662f\u8fd9\u5c0f\u6222\u5c9b\u4e3b\u6167\u5927\u5e08\u3002\u4ed6\u542c\u6167\u5927\u5e08\u7b2c\u4e00\u53e5\u8bdd\uff0c\u5c31 \u77e5\u662f\u6167\u5927\u5e08\u4e0e\u5e73\u51e1\u4e0a\u4eba\u8d4c\u6597\u6b64\u9635\uff0c\u4ee5\u5341\u5e74\u4e3a\u671f\uff0c\u73b0\u5728\u53ea\u6709\u4e00\u65f6\u8fb0\u5373\u5c06\u671f\u6ee1\uff0c\u800c\u5e73\u51e1\u4e0a\u4eba \u65e0\u6cd5\u7834\u9635\uff0c\u5fc3\u4e2d\u7740\u5b9e\u66ff\u5e73\u51e1\u4e0a\u4eba\u7740\u6025\u3002 \u3000\u3000\u4ed6\u521d\u4e0a\u6b64\u5c9b\uff0c\u4e4d\u4eba\u6b64\u9635\u65f6\uff0c\u4e5f\u4ee5\u4e3a\u662f\u201c\u5947\u95e8\u4e94\u884c\u9635\u201d\u800c\u7740\u4e86\u9053\u513f\uff0c\u53ca\u542c\u6167\u5927\u5e08\u8bf4\u51fa \u6b64\u540d\u4e3a\u201c\u5f52\u5143\u56db\u8c61\u9635\u201d\uff0c\u5fc3\u4e2d\u731b\u7136\u4e00\u52a8\u3002 \u3000\u3000\u5f53\u5e74\u4e03\u5999\u795e\u541b\u5bf9\u4ed6\u89e3\u91ca\u68cb\u7406\u65f6\uff0c\u66fe\u5c06\u5929\u4e0b\u5404\u9635\u8981\u8bc0\u4e00\u4e00\u544a\u77e5\u4ed6\uff0c\u4f46\u72ec\u6709\u8fd9\u201c\u5f52\u5143\u56db \u8c61\u9635\u201d\uff0c\u6885\u5c71\u6c11\u8bf4\u4e43\u662f\u524d\u79e6\u4f20\u4e0b\u7684\u53e4\u9635\uff0c\u73b0\u5df2\u5931\u4f20\u591a\u5e74\uff0c\u6885\u5c71\u6c11\u4f46\u51ed\u4e00\u4e9b\u96f6\u788e\u8d44\u6599\uff0c\u51ed \u81ea\u5df1\u76d6\u4e16\u5947\u624d\uff0c\u7adf\u5c06\u6b64\u9635\u53c2\u609f\u4e86\u4e03\u516b\u5206\uff0c\u81ea\u601d\u4e0e\u53e4\u6cd5\u76f8\u53bb\u4e0d\u4f1a\u592a\u8fdc\uff0c\u662f\u4ee5\u4ed6\u66fe\u50b2\u7136\u9053\uff1a \u201c\u5929\u4e0b\u9664\u6211\u4e4b\u5916\uff0c\u53ea\u6015\u518d\u65e0\u522b\u4eba\u8bc6\u5f97\u6b64\u9635\u2014\u2014\u5c3d\u7ba1\u5b83\u662f\u4e0d\u5168\u7684\u3002\u201d \u3000\u3000\u5f53\u65f6\u8f9b\u6377\u53ea\u5927\u6982\u7814\u7a76\u4e86\u4e00\u4e0b\uff0c\u56e0\u4e03\u5999\u795e\u541b\u672c\u4eba\u4e5f\u53ea\u7701\u5f97\u4e03\u516b\u5206\uff0c\u662f\u4ee5\u6b64\u65f6\u8f9b\u6377\u5bf9\u8fd9 \u9635\u6cd5\u8981\u51b3\u751a\u662f\u6a21\u7cca\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u601d\u7d22\u7740\u8fd9\u4e2a\u4ece\u672a\u542c\u8fc7\u7684\u9635\u540d\uff0c\u832b\u7136\u4e0d\u77e5\u6240\u4e91\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u65c1\u8fb9\u7684\u8f9b\u6377\u2014 \u2014\u6b64\u65f6\u4e5f\u6b63\u4ef0\u9996\u6c89\u601d\uff0c\u805a\u7cbe\u4f1a\u795e\u3002 \u3000\u3000\u4e00\u65f6\u5012\u9759\u4e86\u4e0b\u6765\uff0c\u53ea\u6d77\u98ce\u4e0d\u65f6\u5c06\u4e0d\u8fdc\u5904\u7684\u6d6a\u6d9b\u58f0\u6709\u8282\u62cd\u5730\u4f20\u9001\u8fc7\u6765\u3002 \u3000\u3000\u65f6\u95f4\u662f\u4e0d\u505c\u7559\u5730\u8fc7\u53bb\uff0c\u5e73\u51e1\u4e0a\u4eba\u4ece\u6c89\u601d\u4e2d\u89c9\u9192\u65f6\uff0c\u4ef0\u9996\u89c2\u5929\uff0c\u9661\u7136\u53d1\u89c9\u53ea\u5269\u534a\u4e2a\u65f6 \u8fb0\u4e86\u3002 \u3000\u3000\u201c\u540d\u201d\u4e4b\u4e00\u5b57\uff0c\u4e43\u662f\u4eba\u7c7b\u751f\u800c\u5177\u6709\u7684\u6b32\u671b\uff0c\u6d69\u7ff0\u4eba\u6d77\u4e2d\uff0c\u6709\u51e0\u4eba\u771f\u80fd\u4e0d\u4e3a\u201c\u540d\u201d\u6240 \u52a8\u2014\u2014\u5373\u4f7f\u5305\u62ec\u90a3\u4e9b\u4fee\u7ec3\u591a\u5e74\u7684\u51fa\u5bb6\u4eba\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u867d\u6709\u4e09\u7532\u5b50\u7684\u4fee\u4e3a\uff0c\u4f46\u4ed6\u53ea\u77e5\u5728\u6b66\u5b66\u4e0a\u7814\u7a76\uff0c\u5bf9\u4e8e\u4f5b\u95e8\u4e00\u4e9b\u9ad8\u6df1\u9053\u7406\uff0c\u5374 \u4ece\u6765\u4e0d\u66fe\u601d\u7d22\u8fc7\uff0c\u4ed6\u60f3\u5230\u534a\u4e2a\u65f6\u8fb0\u540e\uff0c\u5728\u6167\u5927\u5e08\u9762\u524d\u8ba4\u8f93\u7684\u60c5\u5f62\uff0c\u4e0d\u7981\u9661\u7136\u8dc3\u8d77\uff0c\u8fd9\u65f6\uff0c \u4ed6\u624d\u60f3\u8d77\u90a3\u4e2a\u201c\u9752\u5e74\u4eba\u201d\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4ecd\u7136\u5446\u5446\u6c89\u601d\uff0c\u624b\u4e0a\u5374\u6301\u7740\u4e00\u679d\u5c0f\u67af\u679d\uff0c\u5728\u5730\u4e0a\u4e0d\u505c\u5730\u5212\u7740\uff0c\u4e00\u4f1a\u513f\u53c8\u7528\u811a\u628a \u5b83\u64e6\u53bb\uff0c\u4e00\u4f1a\u513f\u53c8\u4ef0\u9996\u4e0d\u8bed\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136\u5bf9\u4ed6\u9053\uff1a\u201c\u5582\uff0c\u5a03\u513f\uff0c\u4f60\u8d76\u5feb\u8bbe\u6cd5\u79bb\u5f00\u8fd9\u5c9b\uff0c\u534a\u4e2a\u65f6\u8fb0\u5185\uff0c\u6108\u8fdc\u6108\u597d\uff0c \u54a6\uff1f\u201d \u3000\u3000\u6562\u60c5\u4ed6\u53d1\u73b0\u8f9b\u6377\u5bf9\u4ed6\u6240\u8a00\u5b9b\u5982\u4e0d\u95fb\u7684\u60c5\u5f62\uff0c\u4e0d\u7981\u5927\u5947\u3002\u7b49\u5230\u4ed6\u60f3\u8d77\u8f9b\u6377\u53c8\u5982\u4f55\u80fd\u8d70 \u5165\u8fd9\u9635\u7684\u65f6\u5019\uff0c\u4e0d\u7981\u6697\u7b11\u81ea\u5df1\u6e56\u6d82\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u8fd8\u662f\u7f13\u7f13\u8d70\u5230\u8f9b\u6377\u8eab\u65c1\uff0c\u770b\u770b\u4ed6\u5230\u5e95\u5728\u641e\u4ec0\u4e48\u73a9\u610f\u3002\u53ea\u89c1\u4ed6\u6b63\u7528\u6811\u679d\u5728\u5730\u4e0a\u5212 \u7740\u4e00\u4e9b\u4e0d\u89c4\u5219\u7684\u7ebf\u6761\uff0c\u90a3\u4e9b\u7ebf\u6761\u5c11\u8bf4\u6709\u6570\u5341\u6761\uff0c\u662f\u4ee5\u6742\u4e71\u4e0d\u582a\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u832b\u7136\u4e0d\u77e5\u6240\u4e91\uff0c\u4f46\u4e0d\u7981\u597d\u5947\u5730\u5f2f\u8170\u4e0b\u53bb\u770b\u4e2a\u4ed4\u7ec6\uff0c\u957f\u957f\u7684\u767d\u81c2\uff0c\u62c2\u5728\u8f9b\u6377 \u7684\u9888\u4e0a\uff0c\u4ed6\u5c45\u7136\u6beb\u65e0\u611f\u89c9\u3002 \u3000\u3000\u5ffd\u7136\u8f9b\u6377\u5475\u4e86\u4e00\u58f0\uff0c\u7528\u811a\u628a\u90a3\u4e9b\u7ebf\u6761\u5168\u90e8\u64e6\u53bb\uff0c\u4fa7\u5934\u4f3c\u4e4e\u5728\u52aa\u529b\u8bb0\u5fc6\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e5f\u9677\u4e8e\u6781\u7aef\u7684\u77db\u76fe\u4e2d\u2014\u2014 \u3000\u3000\u672c\u6765\u4ed6\u65e9\u5df2\u51b3\u5b9a\u4e86\u7684\uff0c\u8fd9\u65f6\u5374\u56e0\u8fd9\u81ea\u5df1\u5bf9\u4ed6\u6781\u6709\u597d\u611f\u7684\u9752\u5e74\u800c\u4e0d\u65ad\u5730\u8003\u8651\uff0c\u4ed6\u77e5\u9053 \u53ea\u8981\u62fc\u4e0a\u4e00\u7532\u5b50\u7684\u529f\u529b\u5c06\u77f3\u7b0b\u9635\u4e2d\u592e\u90a3\u6839\u6700\u9ad8\u7684\u77f3\u7b0b\u9f50\u6839\u6bc1\u53bb\uff0c\u8fd9\u5c0f\u5c9b\u5c31\u5f97\u7acb\u523b\u4e3a\u4e4b\u6bc1 \u6c89\u2014\u2014\u8fd9\u662f\u4ed6\u8ba4\u4e3a\u5bf9\u6167\u5927\u5e08\u4e0d\u793a\u5f31\u7684\u6700\u597d\u529e\u6cd5\uff0c\u81f3\u4e8e\u540e\u679c\uff0c\u4ed6\u662f\u4e0d\u8ba1\u7684\u3002 \u3000\u3000\u4f46\u5982\u679c\u73b0\u5728\u5f00\u59cb\u884c\u52a8\uff0c\u8f9b\u6377\u52bf\u5fc5\u8981\u966a\u4e0a\u4e00\u6761\u547d\uff0c\u5e73\u51e1\u4e0a\u4eba\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u867d\u7136\u6211\u662f\u6b66 \u6797\u81f3\u5c0a\u7684\u4e16\u5916\u4e09\u4ed9\u4e4b\u9996\uff0c\u4f46\u6211\u6ca1\u6709\u6743\u5229\u8981\u4ed6\u81ea\u9001\u4e00\u6761\u547d\u554a\uff01\u4f46\u662f\uff0c\u4f46\u662f\u6211\u5c82\u80fd\u793a\u5f31\u4e8e\u8001 \u5c3c\u5a46\uff1f\u201d \u3000\u3000\u5982\u679c\u522b\u4eba\uff0c\u4e00\u5b9a\u5728\u8003\u8651\u80fd\u5426\u5c06\u8fd9\u9ad8\u8038\u4eba\u4e91\u7684\u77f3\u7b0b\u9f50\u6839\u6bc1\u53bb\uff0c\u800c\u4ed6\u5374\u8003\u8651\u7740\u5e94\u4e0d\u5e94\u8be5 \u52a8\u624b\u3002 \u3000\u3000\u5982\u679c\u5e73\u51e1\u4e0a\u4eba\u6bcf\u505a\u4e00\u4ef6\u4e8b\u4ee5\u524d\u80fd\u60f3\u4e24\u904d\uff0c\u90a3\u4e48\u4e0d\u4f46\u4ed6\u4f1a\u89c9\u5f97\u6c89\u5c9b\u4e4b\u4e3e\u662f\u65e0\u804a\uff0c\u800c\u4e14 \u4e5f\u8bb8\u4ed6\u6839\u672c\u4e0d\u4f1a\u548c\u6167\u5927\u5e08\u4f5c\u8fd9\u5341\u5e74\u8d4c\u6597\u4e86\uff0c\u8bf4\u5f97\u66f4\u8fdc\u4e9b\uff0c\u4e5f\u8bb8\u4ed6\u5728\u4f5b\u95e8\u9053\u884c\u65b9\u9762\u4e5f\u4f1a\u548c \u4ed6\u7684\u6b66\u5b66\u540c\u6837\u7684\u9ad8\u6df1\u2014\u2014\u4ee5\u4ed6\u6709\u4e09\u7532\u5b50\u529f\u529b\u800c\u8a00\u3002 \u3000\u3000\u4f46\u8fd9\u65f6\u4ed6\u53ea\u80fd\u60f3\u5230\u5230\u5e95\u5e72\u4e0e\u4e0d\u5e72\u3002 \u3000\u3000\u4ed6\u7684\u5fc3\u91cc\u4f3c\u4e4e\u505c\u987f\u5728\u90a3\u91cc\u4e0d\u80fd\u51b3\u5b9a\uff0c\u8f9b\u6377\u4ef0\u9996\u8ffd\u5fc6\uff0c\u4f3c\u4e4e\u4e5f\u505c\u987f\u4e0d\u524d\uff0c\u4f46\u65f6\u5149\u5374\u8fc5 \u901f\u5730\u98de\u9a70\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u518d\u770b\u4e86\u770b\u5929\uff0c\u4ed6\u731b\u7136\u53d1\u89c9\u5269\u4e0b\u65f6\u95f4\uff0c\u6b63\u53ea\u591f\u4ed6\u6bc1\u53bb\u77f3\u7b0b\u7684\u4e86\uff0c\u4f46\u90a3\u77db\u76fe\u4ecd \u7136\u65e0\u6cd5\u51b3\u65ad\uff0c\u8fd9\u65f6\uff0c\u5ffd\u7136\u6709\u5982\u7535\u5149\u4e00\u95ea\uff0c\u4ed6\u5fc3\u4e2d\u7684\u6b7b\u7ed3\u987f\u65f6\u88ab\u6253\u5f00\u4e86\u2014\u2014 \u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u6211\u4e00\u5b9a\u8981\u62fc\u4e0a\u4e00\u7532\u5b50\u529f\u529b\u53bb\u51fb\u6c89\u5168\u5c9b\uff1f\u6211\u5982\u62fc\u7740\u540c\u6837\u7684\u529f\u529b\u8db3\u591f\u5c06\u6240\u6709\u77f3 \u7b0b\u5168\u90e8\u6bc1\u53bb\uff0c\u9664\u4e86\u4e2d\u95f4\u8fd9\u7279\u9ad8\u7684\u4e00\u6839\uff0c\u8fd9\u6837\u8001\u5c3c\u5a46\u7684\u9635\u6cd5\u5c82\u4e0d\u6bc1\u53bb\u800c\u5c9b\u5e76\u4e0d\u81f4\u51fb\u51b3\uff1f\u7136 \u540e\u2014\u2014\u7136\u540e\u6211\u8001\u548c\u5c1a\u53ef\u987e\u4e0d\u4e86\u4ec0\u4e48\u4e0d\u597d\u610f\u601d\uff0c\u975e\u627e\u5979\u6253\u4e00\u67b6\u4e0d\u53ef\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u4e00\u76f4\u5c31\u6ca1\u6709\u987e\u53ca\u5230\u4ec0\u4e48\u597d\u4e0d\u597d\u610f\u601d\u3002 \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u5f15\u542d\u957f\u5578\u4e00\u58f0\uff0c\u7ea2\u5149\u6ee1\u9762\u7684\u8138\u663e\u51fa\u9f99\u817e\u864e\u8dc3\u7684\u795e\u91c7\uff0c\u9ec4\u767d\u957f\u81c2\u65e0\u98ce\u81ea\u52a8\uff0c \u663e\u7136\u4ed6\u5df2\u5c06\u90a3\u8d85\u51e1\u4eba\u5723\u7684\u529f\u529b\u904d\u5e03\u5168\u8eab\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5bf9\u51c6\u5de6\u9762\u4e00\u6839\u77f3\u7b0b\u7f13\u7f13\u4e00\u638c\u62cd\u51fa\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u9707\u58f0\u54cd\u6f88\u4e91\u9704\uff0c\u767e\u5c42\u7eb7\u98de\u4e2d\uff0c \u5e9e\u7136\u4e00\u6839\u5929\u751f\u77f3\u7b0b\u7adf\u88ab\u5e73\u51e1\u4e0a\u4eba\u4e00\u638c\u4e4b\u529b\u7f13\u7f13\u63a8\u5012\uff0c\u843d\u5728\u5730\u9762\u65f6\uff0c\u53c8\u662f\u4e00\u58f0\u5de8\u54cd\u3002 \u3000\u3000\u4ed6\u6709\u70b9\u5f97\u610f\u5730\u56de\u5934\u770b\u4e86\u770b\u8f9b\u6377\uff0c\u4f46\u8f9b\u6377\u5bf9\u8fd9\u4e24\u58f0\u5de8\u54cd\u4ecd\u82e5\u672b\u95fb\uff0c\u624b\u4e0a\u67af\u679d\u53c8\u81ea\u5f00\u59cb \u51fb\u52a8\u3002 \u3000\u3000\u4ed6\u5fcd\u4e0d\u4f4f\u53c8\u8d70\u8fd1\u4e00\u770b\uff0c\u53ea\u89c1\u5730\u4e0a\u5df1\u6709\u4e0d\u4e0b\u767e\u5341\u4f59\u7ebf\u6761\uff0c\u663e\u5f97\u66f4\u662f\u6742\u4e71\uff0c\u5ffd\u7136\u8f9b\u6377\u81ea \u5df1\u4f3c\u4e4e\u4e5f\u770b\u4e0d\u6e05\u695a\u4e86\uff0c\u7528\u90a3\u67af\u679d\u5728\u6b63\u786e\u7684\u7ebf\u6761\u4e0a\u91cd\u5212\u4e00\u904d\uff0c\u77f3\u5730\u7adf\u88ab\u67af\u679d\u5212\u4e0b\u534a\u5206\u6df1\u7684 \u7ebf\u6761\u3002 \u3000\u3000\u7136\u540e\u4ed6\u6325\u8896\u4e00\u64e6\uff0c\u4e00\u4e9b\u4e0d\u6b63\u786e\u7684\u7ebf\u6761\u7acb\u523b\u64e6\u53bb\uff0c\u53ea\u5269\u4e0b\u4e00\u4e9b\u6df1\u5165\u5730\u9762\u7684\u7ebf\u6761\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4ecd\u770b\u4e0d\u51fa\u6240\u4ee5\u7136\uff0c\u8f6c\u8eab\u5bf9\u540e\u9762\u4e00\u6839\u8f83\u5927\u77f3\u67f1\u53c8\u662f\u4e00\u638c\u63a8\u51fa\u2014\u2014 \u3000\u3000\u201c\u8001\u524d\u8f88\u4e14\u6162\u2014\u2014\u201d\u8f9b\u6377\u6597\u7136\u4e00\u9635\u800c\u8d77\uff0c\u4ed6\u89c1\u5e73\u51e1\u4e0a\u4eba\u4e00\u638c\u6b63\u8981\u62cd\u51fa\uff0c\u5fd9\u9ad8\u53eb\u6b62\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8f6c\u8eab\u4e00\u770b\uff0c\u53ea\u89c1\u8f9b\u6377\u9762\u5e26\u559c\u8272\u5730\u53eb\u4f4f\u81ea\u5df1\uff0c\u5f53\u4e0b\u505c\u4f4f\uff0c\u9759\u5f85\u4e0b\u6587\u3002 \u3000\u3000\u8f9b\u6377\u8fd9\u624d\u7f13\u7f13\u9053\uff1a\u201c\u665a\u8f88\u603b\u7b97\u5c06\u8fd9\u2018\u5f52\u5143\u56db\u8c61\u9635\u2019\u7684\u8981\u8bc0\u8bb0\u4e86\u8d77\u6765\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u66f4\u662f\u60ca\u8bb6\u5730\u77aa\u7740\u8f9b\u6377\uff0c\u600e\u4e48\u6837\u4ed6\u4e5f\u4e0d\u4fe1\u8fd9\u4e8c\u5341\u5c81\u7684\u9752\u5e74\u80fd\u5728\u77ed\u77ed\u7684\u534a\u4e2a\u65f6 \u8fb0\u4e4b\u5185\u6e17\u900f\u81ea\u5df1\u5341\u5e74\u4ecd\u6478\u4e0d\u4e0a\u95e8\u5f84\u7684\u9635\u6cd5\u3002 \u3000\u3000\u8fd9\u65f6\u6708\u4eae\u5df2\u6b63\u5f53\u957f\u7a7a\uff0c\u663e\u7136\u5e73\u51e1\u4e0a\u4eba\u4e0e\u6167\u5927\u5e08\u7ea6\u5b9a\u7684\u65f6\u9650\u7acb\u523b\u5c31\u81f3\uff0c\u8f9b\u6377\u7528\u6811\u679d\u5728 \u5730\u4e0a\u7684\u7ebf\u6761\u4e0a\u6307\u7740\u6700\u5916\u7684\u51e0\u6839\u9053\uff1a\u201c\u4ece\u5e72\u4f4d\u8fdb\u5165\uff0c\u6309\u5de6\u4e09\u53f3\u56db\u4e4b\u5219\uff0c\u5c31\u80fd\u8fdb\u4eba\u9635\u5fc3\uff0c\u4f46 \u51fa\u53bb\u65f6\uff0c\u5374\u4e0d\u5927\u76f8\u540c\u2014\u2014\u201d\u8bf4\u7740\u6307\u7740\u5de6\u9762\u4e00\u4e9b\u96f6\u4e71\u7684\u7ebf\u6761\u9053\uff1a\u201c\u4ece\u9635\u5fc3\u5411\u5de6\u8f6c\u8fdb\uff0c\u4e24\u6b21 \u56de\u7ed5\u540e\uff0c\u5e94\u8be5\u6709\u4e00\u4eba\u4e3a\u7684\u5047\u7b0b\u2014\u2014\u201d \u3000\u3000\u987b\u77e5\u77f3\u7b0b\u9635\u867d\u7136\u5927\u591a\u662f\u501f\u5929\u751f\u77f3\u5cf0\u6240\u6210\uff0c\u4f46\u4ecd\u6709\u8bb8\u591a\u662f\u4eba\u4e3a\u6dfb\u52a0\u4e0a\u53bb\u7684\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u542c\u5230\u8fd9\u91cc\u5ffd\u7136\u8dc3\u8d77\u5927\u547c\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff01\u4e0a\u6b21\u6211\u4ece\u8fd9\u6761\u8def\u7ed5\u53bb\uff0c\u6b63\u662f\u6709\u4e00 \u4eba\u4e3a\u7684\u5047\u77f3\u7b0b\u2014\u2014\u770b\u6765\u4f60\u8fd8\u771f\u6709\u4e00\u5957\uff0c\u54b1\u4eec\u8fd9\u5c31\u8d70\u51fa\u53bb\u5427\uff01\u201d \u3000\u3000\u6562\u60c5\u5341\u5e74\u6765\uff0c\u5dee\u4e0d\u591a\u6bcf\u6761\u8def\u5e73\u51e1\u4e0a\u4eba\u90fd\u8bd5\u7740\u8d70\u8fc7\uff0c\u867d\u4e0d\u80fd\u8d70\u51fa\u6b64\u9635\uff0c\u4f46\u9635\u4e2d\u5927\u6982\u60c5 \u5f62\u5374\u751a\u662f\u6e05\u695a\uff0c\u8fd9\u65f6\u542c\u8f9b\u6377\u6240\u8bf4\u679c\u7136\u4e0d\u9519\uff0c\u81ea\u7136\u751a\u662f\u76f8\u4fe1\u5176\u8a00\u3002 \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u53ea\u662f\u665a\u8f88\u5bf9\u6b64\u53e4\u9635\u6700\u591a\u61c2\u5f97\u5341\u4e4b\u516d\u4e03\uff0c\u82e5\u662f\u6b64\u9635\u5e03\u5f97\u5b8c\u6574\uff0c\u53ea\u6015\u4ecd\u662f \u8d70\u4e0d\u51fa\u53bb\u5462\uff01\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u4e0d\u7ba1\u5b83\uff0c\u54b1\u4eec\u4e14\u8bd5\u5b83\u4e00\u8bd5\u3002\u201d \u3000\u3000\u8f9b\u6377\u7ad9\u8d77\u8eab\u6765\uff0c\u8fa8\u4e86\u8fa8\u65b9\u5411\uff0c\u4ece\u4e1c\u9762\u7b2c\u4e09\u6839\u77f3\u7b0b\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7d27\u8ddf\u5728\u540e\u9762\uff0c\u4e00\u9762\u968f\u7740\u8f9b\u6377\u8d70\uff0c\u4e00\u9762\u5fc3\u4e2d\u6697\u601d\u4f55\u4ee5\u8fd9\u5e74\u7eaa\u8f7b\u8f7b\u7684\u5c0f\u4f19\u5b50\u7adf \u8bc6\u5f97\u8fd9\u8fdc\u53e4\u9057\u9635\uff0c\u800c\u4e14\u6070\u5de7\u5728\u5341\u5e74\u5c06\u6ee1\u524d\u5e26\u81ea\u5df1\u51fa\u9635\uff0c\u5c82\u975e\u5929\u610f\u5b89\u6392\uff1f \u3000\u3000\u8f9b\u6377\u6bcf\u8d70\u5728\u6b67\u9053\u7684\u5730\u65b9\uff0c\u4e0d\u4f4f\u5631\u58f0\u70b9\u5934\uff0c\u4f3c\u4e4e\u679c\u7136\u4e0d\u51fa\u81ea\u5df1\u6240\u6599\u7684\u6837\u5b50\uff0c\u4e8e\u662f\u6beb\u4e0d \u72b9\u7591\u7684\u4ece\u6b63\u786e\u7684\u9053\u8def\u8d70\u5165\uff0c\u5e73\u51e1\u4e0a\u4eba\u89c1\u4ed6\u9762\u6709\u559c\u8272\uff0c\u77e5\u9053\u5fc5\u7136\u6709\u5e0c\u671b\u3002 \u3000\u3000\u8fd9\u65f6\u4e24\u4eba\u5df1\u8d70\u51fa\u5c06\u8fd1\u4e94\u91cc\uff0c\u8bf4\u8fd9\u5c9b\u4e5f\u4e0d\u8fc7\u65b9\u5706\u5341\u91cc\uff0c\u4f46\u5728\u9635\u4e2d\u5374\u4f3c\u6709\u8d70\u4e0d\u5b8c\u7684\u8def\uff0c \u76d8\u56de\u91cd\u91cd\uff0c\u5e73\u51e1\u4e0a\u4eba\u4ee5\u524d\u5c61\u6b21\u8bd5\u7740\u6478\u7d22\uff0c\u65e0\u4e0d\u662f\u8d70\u51fa\u4e0d\u53ca\u4e00\u91cc\uff0c\u5c31\u53c8\u56de\u5230\u4e2d\u5fc3\u539f\u5904\uff0c\u8fd9 \u65f6\u5c45\u7136\u8d70\u51fa\u4e86\u8fd9\u8bb8\u591a\u8def\u800c\u672a\u56de\u81f3\u539f\u5904\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u5bf9\u8f9b\u6377\u66f4\u5177\u4fe1\u5fc3\u3002 \u3000\u3000\u8f9b\u6377\u4ece\u4e24\u4e2a\u77f3\u7b0b\u4e2d\u95f4\u7a7f\u51fa\uff0c\u5bf9\u524d\u9762\u4e00\u5ea7\u7a0d\u5c0f\u7684\u77f3\u7b0b\u770b\u4e86\u4e00\u56de\uff0c\u5411\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u8bf7 \u524d\u8f88\u5c06\u6b64\u7b0b\u6bc1\u53bb\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u8fd9\u8f83\u5c0f\u77f3\u7b0b\u5206\u660e\u4e0d\u662f\u5929\u751f\u8005\uff0c\u60f3\u6765\u5fc5\u662f\u6167\u5927\u5e08\u5e03\u9635\u65f6\u6dfb\u8bbe\u7684\uff0c\u5fc3\u4e2d\u867d\u4e0d \u660e\u4f55\u4ee5\u8f9b\u6377\u8981\u4ed6\u6bc1\u6389\u5b83\uff0c\u4f46\u4ecd\u63d0\u4e0a\u4e00\u53e3\u771f\u6c14\uff0c\u53cc\u638c\u7f13\u7f13\u62cd\u51fa\u3002 \u3000\u3000\u4e00\u80a1\u7eaf\u548c\u65e0\u6bd4\u7684\u638c\u98ce\u51c6\u51fa\uff0c\u529b\u91cf\u5374\u5927\u5f97\u60ca\u4eba\uff0c\u4e00\u6839\u5de8\u77f3\u7adf\u5e94\u58f0\u800c\u6bc1\uff0c\u767e\u5c51\u98de\u51fa\u6570\u4e08\uff0c \u6709\u7684\u94b3\u5165\u5176\u4ed6\u77f3\u7b0b\u4e2d\uff0c\u58f0\u52bf\u60ca\u4eba\uff01 \u3000\u3000\u8f9b\u6377\u6697\u4e2d\u8d5e\u9053\uff1a\u53ea\u6015\u5f53\u4eca\u4e16\u4e0a\u7edd\u65e0\u7b2c\u4e8c\u4eba\u6709\u6b64\u529f\u529b\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u89c1\u77f3\u7b0b\u5df2\u6bc1\uff0c\u7ec6\u7ec6\u5728\u77f3\u7b0b\u6839\u90e8\u89c2\u5bdf\u4e00\u756a\uff0c\u679c\u7136\u53d1\u73b0\u4e00\u6761\u6781\u9690\u853d\u7684\u5c0f\u5f84\uff0c\u82e5\u4e0d \u662f\u5c06\u77f3\u7b0b\u6bc1\u53bb\uff0c\u5b9e\u5728\u65e0\u6cd5\u53d1\u89c9\u3002 \u3000\u3000\u4e8c\u4eba\u4ece\u5c0f\u5f84\u7ee7\u7eed\u8d70\u5165\uff0c\u6bcf\u9022\u4eba\u4e3a\u7684\u77f3\u7b0b\uff0c\u5c31\u7531\u5e73\u51e1\u4e0a\u4eba\u53d1\u638c\u51fb\u6bc1\uff0c\u8f9b\u6377\u53c8\u7ee7\u7eed\u5e26\u8def\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u6108\u8d70\u6108\u5bf9\u52b2\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u559c\uff0c\u4f46\u4e00\u770b\u8f9b\u6377\uff0c\u53ea\u89c1\u4ed6\u9762\u8272\u5982\u540c\u7f69\u4e0a\u4e86\u5c42\u51dd \u971c\uff0c\u4e25\u91cd\u4e4b\u6781\uff0c\u4e0d\u7531\u5927\u5947\u3002 \u3000\u3000\u518d\u7ed5\u8fc7\u4e24\u5ea7\u77f3\u7b0b\uff0c\u773c\u524d\u5ffd\u7136\u5f00\u6717\uff0c\u8d70\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u78b0\u5230\u77f3\u7b0b\uff0c\u5e73\u51e1\u4e0a\u4eba\u5fc3\u60f3\u5fc5\u662f\u63a5 \u8fd1\u9635\u8fb9\u7f18\u4e86\uff0c\u4f46\u518d\u4e00\u770b\u8f9b\u6377\uff0c\u8138\u8272\u66f4\u662f\u7d27\u5f20\u3002 \u3000\u3000\u7ed5\u8fc7\u524d\u9762\u7684\u767e\u7b0b\uff0c\u5929\u8272\u4f3c\u4e4e\u4e00\u4eae\uff0c\u90a3\u6708\u4eae\u7684\u5149\u5374\u50cf\u662f\u6bd4\u5e73\u5e38\u660e\u4eae\u767e\u500d\uff0c\u56db\u9762\u8fdc\u5904\u767d \u6d6a\u6ed4\u6ed4\uff0c\u663e\u7136\u5df2\u51fa\u4e86\u767e\u7b0b\u9635\u3002 \u3000\u3000\u4f46\u8f9b\u6377\u5374\u54a6\u4e86\u4e00\u58f0\uff0c\u5411\u540e\u4ed4\u7ec6\u770b\u4e86\u534a\u5929\uff0c\u8138\u4e0a\u7d27\u5f20\u4e4b\u8272\u987f\u6d88\uff0c\u5401\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u770b \u6765\u8fd9\u6167\u5927\u5e08\u5bf9\u6b64\u9635\u529f\u592b\u4e5f\u6ca1\u6709\u5b66\u5168\uff0c\u5426\u5219\u665a\u8f88\u4e5f\u65e0\u6cd5\u8d70\u51fa\u4e86\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u88ab\u56f0\u9635\u4e2d\u5341\u5e74\uff0c\u6ee1\u8154\u6028\u6124\u4e4b\u6c14\uff0c\u6b64\u65f6\u4e00\u65e6\u8d70\u51fa\u77f3\u9635\uff0c\u4e0d\u7981\u4ef0\u9996\u957f\u5618\u3002 \u3000\u3000\u5929\u4e0a\u7691\u6708\u5f53\u7a7a\uff0c\u660e\u661f\u8424\u8424\uff0c\u8fdc\u5904\u6d6a\u58f0\u7785\u7785\uff0c\u5e26\u7740\u6d53\u539a\u76d0\u5473\u7684\u6d77\u98ce\u9635\u9635\u5439\u6765\uff0c\u4ee4\u4eba\u7cbe \u795e\u4e00\u723d\u3002\u5e73\u51e1\u4e0a\u4eba\u5728\u4e00\u970e\u65f6\u95f4\uff0c\u88ab\u56f0\u5341\u5e74\u7684\u6028\u6124\u4e4b\u6c14\u7adf\u7136\u968f\u7740\u90a3\u4e00\u7f15\u6d77\u98ce\uff0c\u5316\u4e3a\u4e4c\u6709\uff0c \u987f\u89c9\u5fc3\u65f7\u795e\u6021\uff0c\u8363\u8fb1\u7686\u5fd8\uff01 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u867d\u7136\u4ece\u4e0d\u4fee\u7ec3\u81ea\u5df1\u9053\u884c\u65b9\u9762\uff0c\u4f46\u4e09\u7532\u5b50\u7684\u4fee\u4e3a\uff0c\u81ea\u7136\u800c\u7136\u517b\u6210\u4e00\u79cd\u6de1\u6cca\u7684 \u6027\u683c\u3002\u8fd9\u65f6\u628a\u4e00\u5207\u770b\u5f00\u4e86\uff0c\u7b11\u5bf9\u8f9b\u6377\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u4f60\u65e2\u662f\u4e03\u5999\u795e\u541b\u7684\u5f1f\u5b50\uff0c\u81ea\u7136\u61c2\u5f97\u90a3 \u4ec0\u4e48\u5947\u95e8\u4e94\u884c\u7684\u9b3c\u95e8\u9053\u4e86\u3002\u201d \u3000\u3000\u53ef\u7b11\u4ed6\u88ab\u56f0\u5341\u5e74\uff0c\u675f\u624b\u65e0\u7b56\u4e8e\u9635\u4e2d\uff0c\u6b64\u65f6\u4ecd\u79f0\u5947\u95e8\u672f\u6570\u4e3a\u9b3c\u95e8\u9053\u3002 \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u70b9\u672b\u884c\uff0c\u5b9e\u5728\u96be\u5165\u884c\u5bb6\u6cd5\u773c\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u957f\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u5a03\u513f\u4f11\u8981\u5047\u8c26\u865a\u4f2a\uff0c\u5012\u662f\u6211\u8001\u513f\u65b9\u624d\u65bd\u7ed9\u4f60\u770b\u7684\u90a3\u2018\u5927\u884d \u5341\u5f0f\u2019\uff0c\u4f60\u53ef\u66fe\u4ed4\u7ec6\u8bb0\u4f4f\uff1f\u201d \u3000\u3000\u8f9b\u6377\u70b9\u5934\u9053\uff1a\u201c\u665a\u8f88\u6b63\u8981\u611f\u8c22\u524d\u8f88\u4ee5\u4e0d\u4e16\u7edd\u5b66\u76f8\u6388\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u8fd9\u6837\u8bf4\u5012\u662f\u7531\u8877\u8bda\u6073\u4e4b\u8a00\uff0c\u8fd9\u65f6\u4ed6\u53c8\u63a5\u7740\u9053\uff1a\u201c\u53ea\u662f\u665a\u8f88\u4e00\u65f6\u6709\u4e9b\u5730\u65b9\u8fd8\u4e0d\u80fd \u5b8c\u5168\u9886\u4f1a\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u8f9b\u6377\u8bf4\u5f97\u6781\u4e3a\u8bda\u6073\uff0c\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u8001\u8872\u5bf9\u8fd9\u51e0\u62db\u5251\u6cd5\u81ea\u8ba4\u8fd8\u6709\u51e0\u5206\u6ee1\u610f\uff0c \u90a3\u6700\u540e\u4e09\u62db\u4f60\u987b\u597d\u597d\u7814\u7a76\uff0c\u82e5\u662f\u53d1\u6325\u5f97\u5b9c\uff0c\u666e\u5929\u4e4b\u4e0b\u80fd\u63a5\u5f97\u4e0b\u7684\uff0c\u53ea\u6015\u5be5\u5be5\u65e0\u51e0\u5462\uff01\u201d \u8bf4\u5230\u6700\u540e\uff0c\u8138\u4e0a\u626c\u6ea2\u7740\u4e00\u7247\u5f97\u610f\u4e4b\u8272\u3002 \u3000\u3000\u8f9b\u6377\u6b63\u81ea\u6697\u5fd6\u4ed6\u8fd9\u53e5\u8bdd\u5012\u5e95\u662f\u4e0d\u662f\u53e3\u51fa\u72c2\u8a00\uff0c\u90a3\u201c\u5927\u884d\u795e\u5251\u201d\u5b9e\u5728\u795e\u5999\u65e0\u6bd4\uff0c\u81ea\u5df1 \u5f97\u6b64\u5947\u5b66\uff0c\u6b63\u53ef\u548c\u672c\u95e8\u5251\u6cd5\u62e9\u7cbe\u878d\u5408\uff0c\u76f8\u5f97\u76ca\u5f70\u3002\u5ffd\u7136\u4e00\u58f0\u957f\u7b11\u5212\u7834\u957f\u7a7a\uff0c\u90a3\u7b11\u58f0\u597d\u4e0d \u60ca\u4eba\uff0c\u521d\u95fb\u58f0\u65f6\uff0c\u5c1a\u5728\u5c9b\u4e4b\u4e2d\u5fc3\uff0c\u7b11\u58f0\u752b\u843d\uff0c\u4e00\u6761\u4eba\u5f71\u5df2\u5237\u5730\u843d\u5728\u773c\u524d\u4e0d\u53ca\u4e09\u4e08\u5904\uff0c\u8fd9 \u7b49\u8f7b\u529f\u82e5\u662f\u4f20\u5230\u6b66\u6797\u4e2d\uff0c\u53ea\u6015\u65e0\u4eba\u80fd\u4fe1\uff0c\u5c31\u662f\u4ee5\u8f9b\u6377\u5982\u6b64\u529f\u529b\uff0c\u4ea6\u89c9\u5fc3\u6298\u4e0d\u5df2\uff0c\u4e00\u79cd\u76f4 \u89c9\u544a\u8bc9\u4ed6\uff0c\u5fc5\u662f\u4e16\u5916\u4e09\u4ed9\u4e2d\u7684\u53e6\u4e00\u4eba\u6167\u5927\u5e08\u5230\u4e86\u3002 \u3000\u3000\u85c9\u7740\u6708\u5149\u770b\u53bb\uff0c\u6765\u4eba\u662f\u4e2a\u8001\u5c3c\uff0c\u4e00\u88ad\u618e\u8863\u7834\u65e7\u4e0d\u582a\uff0c\u4f46\u5374\u4e00\u5c18\u4e0d\u67d3\uff0c\u5b89\u7965\u5730\u5bf9\u7740\u5e73 \u51e1\u4e0a\u4eba\u4e00\u7b11\uff0c\u6b63\u662f\u5c0f\u6222\u5c9b\u4e3b\u6167\u5927\u5e08\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u56f0\u4f4f\u81ea\u5df1\u5341\u5e74\u7684\u4eba\u7ad9\u5728\u9762\u524d\uff0c\u5374\u4e5f\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5c3c\u5a46\u5343\u65b9\u767e\u8ba1\u8981\u5360 \u6211\u8001\u513f\u4e0a\u98ce\uff0c\u53ef\u662f\u8001\u5929\u6709\u773c\uff0c\u504f\u504f\u603b\u4e0d\u5982\u4f60\u610f\uff0c\u54c8\u54c8\uff01\u201d\u8138\u4e0a\u795e\u8272\u5f97\u610f\u4e4b\u6781\u3002 \u3000\u3000\u6167\u5927\u5e08\u5bff\u7709\u4e00\u626c\u9053\uff1a\u201c\u8001\u5c3c\u6d3b\u5230\u73b0\u5728\u624d\u7b2c\u4e00\u6b21\u542c\u8bf4\u6253\u8d4c\u8981\u9760\u5c0f\u8f88\u52a9\u62f3\u7684\u3002\u201d \u3000\u3000\u6167\u5927\u5e08\u4ee5\u4e3a\u8fd9\u53e5\u8bdd\u5fc5\u80fd\u4f7f\u597d\u80dc\u7684\u5e73\u51e1\u4e0a\u4eba\u6fc0\u6012\u3002\u54ea\u77e5\u5e73\u51e1\u4e0a\u4eba\u53c8\u662f\u54c8\u54c8\u4e00\u7b11\u9053\uff1a \u201c\u54b1\u4eec\u5f53\u5e74\u6253\u8d4c\u65f6\u53ef\u6ca1\u6709\u89c4\u5b9a\u4e0d\u51c6\u522b\u4eba\u81ea\u52a8\u8fdb\u6765\u5e26\u6211\u8001\u513f\u51fa\u53bb\u5427\uff1f\u201d \u3000\u3000\u6167\u5927\u5e08\u54fc\u7136\u51b7\u7b11\u4e00\u58f0\uff0c\u8f6c\u5411\u8f9b\u6377\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u4f60\u8fd9\u5c0f\u5a03\u513f\u5c45\u7136\u8bc6\u5f97\u6211\u8fd9\u53e4\u9635\uff0c\u987b\u77e5 \u4f60\u672a\u7ecf\u8bb8\u53ef\uff0c\u64c5\u5165\u672c\u5c9b\uff0c\u5df2\u662f\u72af\u4e86\u91cd\u89c4\uff0c\u590d\u53c8\u64c5\u5927\u77f3\u7b0b\u9635\uff0c\u66f4\u662f\u7f6a\u4e0d\u53ef\u6055\uff0c\u6211\u5012\u8981\u770b\u770b \u4ec0\u4e48\u4eba\u80c6\u6562\u4e0d\u628a\u8001\u5c3c\u653e\u5728\u773c\u5185\u3002\u201d \u3000\u3000\u8f9b\u6377\u672c\u5c31\u5014\u5f3a\u4e4b\u6781\uff0c\u66f4\u517c\u6167\u5927\u5e08\u72c2\u6001\u903c\u4eba\uff0c\u5f53\u4e0b\u5c06\u90a3\u539f\u6709\u4e00\u70b9\u656c\u754f\u4e4b\u5fc3\u653e\u5f00\uff0c\u6297\u58f0 \u9053\uff1a\u201c\u665a\u8f88\u64c5\u5165\u8d35\u5c9b\uff0c\u672c\u4e3a\u65e0\u5fc3\u4e4b\u8fc7\uff0c\u82e5\u662f\u524d\u8f88\u5b9a\u8981\u4ee5\u6b64\u4e3a\u7531\u6559\u8bad\u665a\u8f88\uff0c\u665a\u8f88\u4e0d\u624d\uff0c\u5374 \u77e5\u5934\u53ef\u6298\u5fd7\u4e0d\u53ef\u5c48\uff01\u201d \u3000\u3000\u8f9b\u6377\u4e00\u9635\u51b2\u52a8\u4e4b\u60c5\u5c06\u8fd9\u5bf9\u4e16\u5916\u4e09\u4ed9\u7684\u656c\u754f\u4e4b\u5fc3\u538b\u8fc7\uff0c\u8fd9\u65f6\u4f83\u4f83\u800c\u8a00\uff0c\u4e0d\u5351\u4e0d\u4ea2\uff0c\u4e24 \u8db3\u633a\u7acb\uff0c\u6c14\u5ea6\u7adf\u7136\u5a01\u731b\u4e4b\u6781\u3002 \u3000\u3000\u6167\u5927\u5e08\u4f3c\u4e4e\u6014\u4e86\u4e00\u6014\uff0c\u53c8\u6253\u91cf\u8f9b\u6377\u4e00\u773c\uff0c\u5ffd\u7136\u632f\u58f0\u957f\u7b11\uff0c\u90a3\u7b11\u58f0\u521d\u65f6\u5176\u4f4e\uff0c\u6e10\u6e10\u6108 \u6765\u6108\u54cd\uff0c\u4f3c\u4e4e\u65e0\u6570\u58f0\u97f3\u76f8\u5408\uff0c\u9707\u5f97\u5730\u52a8\u5c71\u6447\u3002 \u3000\u3000\u4ee5\u8f9b\u6377\u5982\u6b64\u529f\u529b\uff0c\u7adf\u89c9\u8033\u4e2d\u6709\u5982\u9488\u622e\uff0c\u53c8\u89c9\u6709\u5982\u9524\u51fb\uff0c\u6e10\u6e10\u7adf\u6709\u652f\u6301\u4e0d\u4f4f\u4e4b\u611f\u3002 \u3000\u3000\u5ffd\u5730\u5e73\u51e1\u4e0a\u4eba\u731b\u559d\u4e00\u58f0\uff0c\u767b\u65f6\u5c06\u6167\u5927\u5e08\u7b11\u58f0\u6253\u65ad\uff0c\u53ea\u89c1\u4ed6\u6717\u58f0\u7b11\u9053\uff1a\u201c\u8001\u5c3c\u5a46\u8fd9\u5c0f \u5c9b\u4e5f\u6709\u8bb8\u591a\u81ed\u89c4\u77e9\uff0c\u4eca\u65e5\u82e5\u4e0d\u662f\u8fd9\u5a03\u513f\u5373\u65f6\u8d76\u5230\uff0c\u4f60\u8fd9\u5c0f\u5c9b\u6b64\u523b\u6015\u5df2\u5728\u4e07\u4e08\u6d77\u5e95\u4e86\u3002\u201d \u3000\u3000\u6167\u5927\u5e08\u767d\u4e86\u5e73\u51e1\u4e0a\u4eba\u4e00\u773c\uff0c\u53c8\u5bf9\u8f9b\u6377\u9053\uff1a\u201c\u4f60\u65e2\u80fd\u7ecf\u5f97\u4f4f\u6211\u2018\u8be7\u9633\u7384\u97f3\u2019\uff0c\u60f3\u6765\u5fc5 \u6709\u51e0\u5206\u529f\u529b\uff0c\u4f60\u6709\u80c6\u63a5\u8001\u5c3c\u4e09\u62db\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u867d\u89c9\u8fd9\u6167\u5927\u5e08\u529f\u529b\u59d4\u5b9e\u9ad8\u4e0d\u53ef\u6d4b\uff0c\u4f46\u8fd9\u65f6\u5c31\u662f\u5200\u67b6\u5728\u4ed6\u9888\u5b50\u4ed6\u4e5f\u4e0d\u80fd\u9000\u7f29\uff0c\u4e00 \u65f6\u4e00\u8154\u70ed\u8840\u4e0a\u6d8c\uff0c\u5f53\u4e0b\u6297\u58f0\u9053\uff1a\u201c\u665a\u8f88\u4e0d\u81ea\u91cf\u529b\uff0c\u5c31\u63a5\u524d\u8f88\u9ad8\u62db\u3002\u201d \u3000\u3000\u6167\u5927\u5e08\u66f4\u4e0d\u7b54\u8bdd\uff0c\u4e5f\u4e0d\u89c1\u5979\u53cc\u8db3\u7528\u529b\uff0c\u8eab\u5f62\u7adf\u7136\u5e73\u5e73\u98de\u8d77\uff0c\u5355\u8896\u4e00\u62c2\u4e4b\u95f4\uff0c\u4e00\u53cc\u8896 \u5316\u4e3a\u4e00\u7247\u7070\u5f71\u7f69\u4e0b\uff0c\u8f9b\u6377\u867d\u5219\u65e9\u5c31\u771f\u6c14\u904d\u5e03\u5168\u8eab\uff0c\u4f46\u5bf9\u6167\u5927\u5e08\u8fd9\u6781\u4e3a\u5176\u98d8\u5ffd\u7684\u4e00\u62db\u7adf\u611f \u675f\u624b\uff0c\u8fd9\u611f\u89c9\u6b63\u5982\u540c\u4e0a\u6b21\u548c\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u5bf9\u62db\u65f6\u4e00\u6837\uff0c\u4f46\u8f9b\u6377\u6b64\u65f6\u529f\u529b\u5927\u975e\u6614\u6bd4\uff0c\u6025 \u4e2d\u751f\u667a\uff0c\u5bf9\u654c\u52bf\u529b\u4e0d\u95fb\u4e0d\u95ee\uff0c\u5de6\u638c\u4e00\u7acb\uff0c\u53f3\u62f3\u8fd0\u5f0f\u5982\u98ce\uff0c\u547c\u5730\u4e00\u58f0\uff0c\u53cd\u51fb\u6167\u5927\u5e08\u5de6\u80a9\u3002 \u3000\u3000\u82e5\u662f\u4e00\u65e5\u4ee5\u524d\uff0c\u8f9b\u6377\u8fd9\u4e00\u62f3\u638f\u51fa\uff0c\u6167\u5927\u5e08\u5927\u53ef\u65cb\u8eab\u76f4\u8ff8\uff0c\u5982\u65e0\u6068\u751f\u90a3\u6837\u8f7b\u800c\u6613\u4e3e\u64d2 \u4f4f\u8f9b\u6377\u8109\u95e8\uff0c\u4f46\u6b64\u65f6\u8f9b\u6377\u62f3\u51fa\u98ce\u81f3\uff0c\u9690\u9690\u6697\u542b\u98ce\u96f7\u4e4b\u58f0\uff0c\u6167\u5927\u5e08\u54a6\u4e86\u4e00\u58f0\uff0c\u4e0d\u5f85\u62db\u5f0f\u9012 \u6ee1\uff0c\u7070\u8896\u518d\u62c2\uff0c\u4e00\u53cc\u7834\u5e03\u957f\u8896\u7adf\u5982\u4e00\u53cc\u94c1\u68cd\u822c\u6a2a\u626b\u8fc7\u6765\u3002 \u3000\u3000\u7834\u5e03\u67d4\u4e0d\u7740\u529b\uff0c\u6167\u5927\u5e08\u4e0d\u7528\u6362\u5f0f\uff0c\u4ec5\u501f\u52bf\u4e00\u62c2\uff0c\u5c31\u628a\u67d4\u8f7b\u7684\u4e00\u7247\u8896\u5f71\u6536\u6210\u94c1\u68cd\u822c\u6a2a \u626b\u51fa\uff0c\u6bd4\u4e4b\u201c\u6e7f\u675f\u6210\u68cd\u201d\u7684\u529f\u529b\uff0c\u4e0d\u77e5\u53c8\u9ad8\u51fa\u591a\u5c11\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u6167\u5927\u5e08\u8fd9\u4e00\u62c2\u4e4b\u52bf\u867d\u5f3a\u4e0d\u53ef\u5f53\uff0c\u4f46\u62db\u5f0f\u5374\u4f3c\u6b66\u5f53\u6d3e\u7684\u201c\u6a2a\u626b\u5343\u519b\u201d\uff0c\u5bf9\u8fd9\u4e2d \u539f\u5404\u5927\u6d3e\u7684\u62db\u5f0f\u8f9b\u6377\u4e0d\u77e5\u7814\u4e60\u4e86\u51e0\u5343\u904d\uff0c\u8fd9\u65f6\u6beb\u4e0d\u72b9\u8c6b\u5730\u4f7f\u51fa\u201c\u6697\u9999\u6d6e\u5f71\u201d\u8f7b\u529f\u4e2d\u7684\u7edd \u62db\u201c\u9999\u95fb\u5343\u91cc\u201d\uff0c\u8eab\u5f62\u5fae\u5fae\u4e00\u6643\uff0c\u5df2\u81ea\u51fa\u4e86\u6167\u5927\u5e08\u8896\u52bf\u4ee5\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e43\u662f\u4e03\u5999\u795e\u541b\u4e13\u95e8\u5bf9\u4ed8\u6b66\u5f53\u62f3\u62db\u7684\u5999\u7740\uff0c\u6167\u5927\u5e08\u8fd9\u7b49\u62f3\u52b2\uff0c\u4e5f\u88ab\u8f7b\u6613\u8eb2\u8fc7\uff0c \u800c\u4e14\u662f\u5f88\u6f02\u4eae\u5730\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5728\u65c1\u5475\u5475\u5927\u7b11\uff0c\u8fde\u58f0\u79f0\u5999\uff0c\u6167\u5927\u5e08\u4e0d\u7531\u60ca\u4e0a\u52a0\u6012\uff0c\u547c\u7684\u58f0\u4e00\u628a\u6293\u51fa\uff0c\u4e94\u6307 \u7b95\u5f20\u5e26\u7740\u4e94\u7f15\u75be\u98ce\uff0c\u95ea\u7535\u822c\u6293\u4e0b\uff0c\u8f9b\u6377\u6709\u4e86\u7b2c\u4e00\u62db\u7ecf\u9a8c\uff0c\u80c6\u6c14\u4e00\u58ee\uff0c\u53f3\u624b\u4ee5\u6307\u4e3a\u5251\uff0c\u65bd \u51fa\u672c\u95e8\u7edd\u5b66\u201c\u866c\u679d\u5251\u6cd5\u201d\u7684\u7edd\u62db\u201c\u6885\u82b1\u4e09\u5f04\u201d\uff0c\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u6167\u5927\u5e08\u8fd9\u6293\u4e43\u662f\u5e73\u751f\u7edd\u6280\uff0c\u5176\u4e2d\u6697\u85cf\u4e09\u8bb0\u6740\u624b\uff0c\u8fd9\u65f6\u89c1\u8f9b\u6377\u53f3\u638c\u4f3c\u6307\u4f3c\u5251\u5730\u659c\u659c\u5212 \u51fa\uff0c\u6697\u9053\u4f60\u8fd9\u662f\u627e\u6b7b\uff0c\u4e94\u6307\u4e00\u7ffb\uff0c\u5feb\u5f97\u65e0\u4ee5\u590d\u52a0\u5730\u6a2a\u6293\u53bb\uff0c\u90a3\u77e5\u547c\u7684\u4e00\u58f0\uff0c\u8f9b\u6377\u53f3\u638c\u4e5f \u662f\u4e00\u7ffb\uff0c\u4e5f\u662f\u5feb\u5f97\u65e0\u4ee5\u590d\u52a0\u5730\u6307\u5411\u6167\u5927\u5e08\u8109\u95e8\uff0c\u6167\u5927\u5e08\u4f55\u7b49\u529f\u529b\uff0c\u638c\u5f0f\u4e00\u6c89\uff0c\u6697\u85cf\u7684\u7b2c \u4e09\u4e2a\u7edd\u62db\u53c8\u5df2\u65bd\u51fa\uff0c\u53ea\u89c1\u4e94\u6307\u5982\u9e70\uff0c\u79bb\u8f9b\u6377\u80a9\u5934\u5df2\u81ea\u4e0d\u53ca\u534a\u5bf8\u2014\u2014 \u3000\u3000\u4f46\u662f\u51e0\u4e4e\u662f\u540c\u65f6\uff0c\u8f9b\u6377\u201c\u6885\u82b1\u4e09\u5f04\u201d\u4e2d\u201c\u7b2c\u4e00\u5f04\u201d\u4e5f\u5df1\u65bd\u51fa\uff0c\u4e2d\u98df\u4e8c\u6307\u5e76\u7acb\u5982\u621f\uff0c \u95ee\u4e0a\u75be\u70b9\uff0c\u6b63\u4e2d\u6167\u5927\u5e08\u201c\u66f2\u6c60\u201d\u2014\u2014\u53ea\u542c\u5f97\u7830\u7684\u4e00\u58f0\uff0c\u6167\u5927\u5e08\u4e00\u7ffb\u4e4b\u95f4\uff0c\u4e24\u6761\u80f3\u818a\u78b0\u5728 \u4e00\u8d77\uff0c\u6167\u5927\u5e08\u7a33\u7acb\u4e0d\u52a8\uff0c\u8f9b\u6377\u5374\u8dc4\u8e09\u9000\u540e\u4e09\u6b65\u3002 \u3000\u3000\u8f9b\u6377\u60ca\u4e8e\u6167\u5927\u5e08\u7684\u529f\u529b\u6df1\u539a\uff0c\u6167\u5927\u5e08\u5374\u60ca\u4e8e\u81ea\u5df1\u8fde\u73af\u4e09\u62db\u6b63\u597d\u88ab\u5bf9\u65b9\u8fde\u73af\u4e09\u62db\u6240\u7834\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5374\u4e0d\u4f4f\u5927\u53eb\u5999\u6781\u3002 \u3000\u3000\u6167\u5927\u5e08\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u4e24\u8896\u4e00\u6251\uff0c\u8eab\u5f62\u4f3c\u4e4e\u85c9\u7740\u4e00\u6251\u4e4b\u52bf\uff0c\u9661\u7136\u98de\u8d77\u4e24\u4e08\uff0c\u5347\u5230\u9876\u70b9\uff0c \u4e24\u8896\u4e00\u5f20\uff0c\u8eab\u5f62\u7adf\u81ea\u4e00\u505c\uff0c\u7565\u4e00\u76d8\u65cb\uff0c\u624d\u5ffd\u5730\u75be\u6bd4\u52b2\u77e2\u5730\u6251\u52bf\u800c\u4e0b\uff0c\u8eab\u5f62\u7f8e\u5999\u4e4b\u6781\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53ef\u6253\u51fa\u4e86\u6167\u5927\u5e08\u7684\u771f\u706b\uff0c\u8fd9\u4e00\u6251\u4e0b\u65bd\u51fa\u4e86\u5979\u5e73\u751f\u7edd\u6280\u201c\u82cd\u9e6b\u4e03\u5f0f\u201d\uff0c\u53cc\u8896\u4e5f \u7528\u4e0a\u4e86\u516b\u6210\u5185\u52b2\u2014\u2014 \u3000\u3000\u8fde\u5e73\u51e1\u4e0a\u4eba\u90fd\u95ed\u4e0a\u7b11\u53e3\uff0c\u7d27\u5f20\u5730\u770b\u8fd9\u201c\u5a03\u513f\u201d\u600e\u751f\u5e94\u4ed8\u8fd9\u6700\u540e\u4e00\u62db\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u90a3\u638c\u529b\u50cf\u662f\u4ece\u56db\u65b9\u516b\u9762\u88ad\u6765\uff0c\u751a\u81f3\u8eab\u540e\u90fd\u6709\u4e00\u80a1\u75be\u98ce\u88ad\u5230\u2014\u2014\u8fd9\u6b63\u662f\u201c\u82cd \u9e6b\u4e03\u5f0f\u201d\u795e\u79d8\u4e4b\u5904\uff0c\u4ed6\u4e00\u5239\u90a3\u95f4\u5b9e\u4e0d\u77e5\u600e\u6837\u62db\u67b6\u3002 \u3000\u3000\u4e00\u5bb4\u65f6\u95f4\uff0c\u6240\u6709\u5b66\u8fc7\u7684\u62db\u5f0f\u6d77\u6d6a\u822c\u6d8c\u8fc7\u8f9b\u6377\u5934\u8111\uff0c\u7adf\u4f3c\u65e0\u4e00\u80fd\u9002\u5e94\u6b64\u62db\uff0c\u6025\u5207\u95f4\uff0c \u5ffd\u7136\u4e00\u4e2a\u5ff5\u5934\u5982\u95ea\u7535\u822c\u5728\u4ed6\u8111\u6d77\u4e2d\u4e00\u6643\u53ea\u89c1\u4ed6\u4e24\u81c2\u5e73\u4f38\u4e24\u4fa7\uff0c\u540c\u65f6\u5411\u4e2d\u4e00\u5408\uff0c\u5408\u81f3\u6b63\u4e2d \u65f6\uff0c\u5ffd\u5730\u4e00\u7ffb\u800c\u51fa\uff0c\u840e\u65f6\u6ee1\u5929\u638c\u5f71\uff0c\u8fce\u51fb\u800c\u4e0a\uff0c\u6b63\u662f\u5e73\u51e1\u4e0a\u4eba\u65b9\u624d\u4f20\u6388\u7684\u201c\u5927\u884d\u5341\u5f0f\u201d \u4e2d\u7684\u201c\u65b9\u751f\u4e0d\u606f\u201d \u3000\u3000\u6167\u5927\u5e08\u5ffd\u89c9\u5bf9\u65b9\u53cc\u638c\u4e00\u5408\u4e00\u7ffb\u4e4b\u95f4\uff0c\u5e03\u51fa\u4e00\u7247\u638c\u5f71\uff0c\u5bc6\u5bc6\u5c42\u5c42\uff0c\u5b9b\u5982\u65e5\u5149\u666e\u7167\uff0c\u65e0 \u4e00\u4e0d\u53ca\uff0c\u6beb\u65e0\u7834\u7efd\uff0c\u81ea\u5df1\u62db\u5f0f\u7adf\u9012\u4e0d\u8fdb\u53bb\u2014\u2014\u53ea\u89c1\u5979\u53cc\u81c2\u5ffd\u7136\u4e00\u632f\uff0c\u7adf\u4e0d\u518d\u51fb\u4e0b\uff0c\u590d\u53c8 \u62d4\u8d77\u5bfb\u4e08\uff0c\u8f7b\u98d8\u98d8\u843d\u5728\u4e08\u5916\uff0c\u5bf9\u5e73\u51e1\u4e0a\u4eba\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u8001\u548c\u5c1a\uff0c\u597d\u4e00\u62db\u2018\u65b9\u751f\u4e0d \u606f\u2019\uff01\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u8f9b\u6377\u5c06\u81ea\u5df1\u7edd\u5b66\u8fd0\u7528\u5f97\u5de7\u5999\u4e0d\u5df2\uff0c\u4e0d\u7981\u5f97\u610f\u975e\u51e1\uff0c\u95fb\u6167\u5927\u5e08\u4e4b\u8a00\uff0c\u88c2\u53e3 \u7b11\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u6167\u5927\u5e08\u8f6c\u5bf9\u8f9b\u6377\u9053\uff1a\u201c\u54b1\u4eec\u6709\u8a00\u5728\u5148\uff0c\u53ea\u5bf9\u4e09\u62db\uff0c\u4f60\u73b0\u5728\u53ef\u4ee5\u8d70\u4e86\u3002\u201d\u63a5\u7740\u53c8\u5bf9\u5e73 \u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u8001\u5c3c\u4e0d\u8bc6\u76f8\uff0c\u8fd8\u8981\u9886\u6559\u4f60\u8001\u548c\u5c1a\u7684\u2018\u5927\u884d\u5341\u5f0f\u2019\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u5c31\u662f\u8001\u513f\u6211\u4e5f\u89c9\u624b\u75d2\u7684\u7d27\uff0c\u54b1\u4eec\u8d70\u51e0\u62db\u6740\u6740\u95f7\u6b63\u597d\u3002\u201d \u3000\u3000\u6167\u5927\u5e08\u66f4\u4e0d\u6253\u8bdd\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u5de6\u53f3\u624b\u9f50\u51fa\uff0c\u53cc\u8db3\u4e00\u970e\u65f6\u95f4\u901f\u6362\u4e03\u79cd\u67b6\u5f0f\uff0c\u5374\u59cb\u7ec8\u4e0d \u79bb\u65b9\u5bf8\u4e4b\u95f4\uff0c\u540c\u65f6\u624b\u4e0a\u4e5f\u4e00\u53e3\u6c14\u8fde\u653b\u4e86\u5eff \u3000\u3000\u8fd9\u4e03\u62db\u6bcf\u62db\u90fd\u7cbe\u7edd\u65e0\u6bd4\uff0c\u8f9b\u6377\u89c1\u4e86\u65e0\u6068\u751f\u53ca\u5e73\u51e1\u4e0a\u4eba\u7684\u6b66\u827a\uff0c\u4ee5\u4e3a\u5929\u4e0b\u5947\u5b66\u5c3d\u4e8e\u6b64 \u77e3\uff0c\u90a3\u77e5\u6167\u5927\u5e08\u7684\u795e\u5999\u6b65\u6cd5\uff0c\u7adf\u53c8\u662f\u5927\u51fa\u4ed6\u610f\u6599\u4e4b\u5916\u7684\u5965\u5999\uff0c\u5f53\u4e0b\u6d51\u5fd8\u8eab\u4e4b\u6240\u5728\uff0c\u51dd\u795e \u89c2\u770b\u7740\u8fd9\u4e24\u4e2a\u76d6\u4e16\u5947\u4eba\u7684\u62fc\u6597\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u66f4\u662f\u53cc\u8db3\u7262\u5b9a\u4e0d\u79fb\uff0c\u4e0a\u8eab\u524d\u540e\u5de6\u53f3\u7684\u6643\u52a8\u4e4b\u4e2d\uff0c\u5c06\u6167\u5927\u5e08\u4e03\u62db\u653b\u52bf\u4e00\u4e00\u5316 \u53bb\uff0c\u540c\u65f6\u5de6\u624b\u62bd\u7a7a\u8fd8\u51fa\u4e94\u62db\u3002 \u3000\u3000\u8f9b\u6377\u4ed4\u7ec6\u89c2\u5bdf\u6167\u5927\u5e08\u7684\u8eab\u6cd5\uff0c\u53ea\u89c9\u5979\u62f3\u638c\u529f\u592b\u867d\u5999\uff0c\u5374\u4f3c\u4e0d\u53ca\u6b65\u5c65\u95f4\u7684\u795e\u5947\u3002\u90a3\u4e00 \u8de8\u4e00\u8dc3\u4e4b\u95f4\uff0c\u5b9e\u5728\u7cbe\u5965\u65e0\u6bd4\uff0c\u8fde\u8f9b\u6377\u4ee5\u76ee\u524d\u7684\u529f\u529b\u76ee\u654f\uff0c\u4e5f\u53ea\u80fd\u89c9\u51fa\u5341\u5206\u795e\u5999\u800c\u5df2\uff0c\u4ecd \u4e0d\u77e5\u5176\u6240\u4ee5\u7136\u3002 \u3000\u3000\u6bcf\u5f53\u6167\u5927\u5e08\u51fa\u62db\u65f6\uff0c\u4ed6\u5fc5\u626a\u5fc3\u81ea\u95ee\uff0c\u5982\u662f\u548c\u81ea\u5df1\u5bf9\u654c\uff0c\u81ea\u5df1\u5f53\u5982\u4f55\u62db\u67b6\uff0c\u60f3\u51fa\u4ee5\u540e\uff0c \u518d\u770b\u5e73\u51e1\u4e0a\u4eba\u7684\u56de\u62db\uff0c\u679c\u7136\u6bd4\u81ea\u5df1\u6240\u60f3\u7684\u7cbe\u5999\u5341\u500d\uff0c\u4e0d\u7981\u5fc3\u795e\u4ff1\u9189\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u4e0a\u5929\u5b89\u6392\u7684\u597d\u673a\u7f18\uff0c\u5426\u5219\u5e73\u51e1\u4e0a\u4eba\u7684\u201c\u5927\u884d\u5341\u5f0f\u201d\u867d\u5219\u4f20\u7ed9\u8f9b\u6377\uff0c\u4f46\u8fd9\u201c\u5927 \u884d\u5251\u6cd5\u201d\u4e43\u662f\u5e73\u51e1\u4e0a\u4eba\u5728\u5251\u672f\u4e0a\u7a77\u6bd5\u751f\u7cbe\u529b\u6240\u7387\uff0c\u5176\u4e2d\u53d8\u5316\u7cbe\u5fae\uff0c\u4efb\u8f9b\u6377\u624d\u667a\u76d6\u4e16\uff0c\u5982 \u679c\u81ea\u884c\u53c2\u609f\uff0c\u7a77\u4e09\u5341\u5e74\u4e5f\u4e0d\u89c1\u5f97\u80fd\u5b8c\u5168\u9886\u609f\uff0c\u8fd9\u65f6\u76ee\u7779\u4e24\u4e2a\u5947\u4eba\u7684\u62fc\u6597\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c \u5df2\u5c06\u8bb8\u591a\u610f\u6599\u4e0d\u5230\u7684\u7cbe\u5fae\u5904\u609f\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e00\u7728\u773c\u95f4\uff0c\u4e24\u4eba\u5df2\u5bf9\u6362\u4e86\u6570\u767e\u62db\uff0c\u8eab\u5f62\u4e4b\u5feb\uff0c\u53d1\u62db\u4e4b\u901f\uff0c\u5c31\u662f\u4f20\u5230\u6b66\u6797\u4e2d\u53bb\uff0c\u4e5f\u4e0d \u4f1a\u6709\u4eba\u7f6e\u4fe1\u3002 \u3000\u3000\u4f46\u4ece\u5f00\u59cb\u5230\u6570\u767e\u62db\u95f4\uff0c\u5e73\u51e1\u4e0a\u4eba\u59cb\u7ec8\u662f\u5b88\u591a\u653b\u5c11\uff0c\u8fd9\u65f6\u60f3\u662f\u6253\u5f97\u5174\u8d77\uff0c\u957f\u5578\u4e00\u58f0\u638c \u4e0a\u53d8\u638c\u4e3a\u6307\uff0c\u4ee5\u6307\u4e3a\u5251\uff0c\u4e00\u6643\u4e4b\u95f4\uff0c\u4ece\u4e09\u4e2a\u51fa\u4eba\u610f\u8868\u7684\u7edd\u5999\u65b9\u4f4d\u653b\u5411\u6167\u5927\u5e08\uff0c\u4e00\u65f6\u6307\u4e0a \u75be\u98ce\u5927\u4f5c\uff0c\u5999\u7edd\u5929\u4e0b\u7684\u201c\u5927\u884d\u5251\u5f0f\u201d\u5df2\u7136\u65bd\u51fa\u3002 \u3000\u3000\u8fd9\u201c\u5927\u884d\u5341\u5f0f\u201d\u7aef\u7684\u582a\u79f0\u5929\u4e0b\u65e0\u53cc\uff0c\u65bd\u51fa\u7684\u4eba\u53c8\u662f\u5e73\u51e1\u4e0a\u4eba\uff0c\u90a3\u5a01\u529b\u53ef\u60f3\u800c\u77e5\uff0c\u4e00 \u5239\u90a3\u95f4\uff0c\u5f62\u52bf\u5927\u53d8\uff0c\u6167\u5927\u5e08\u638c\u4e0a\u5947\u62db\u5999\u5f0f\u90fd\u4f3c\u4e4e\u5927\u4e3a\u51cf\u8272\uff0c\u5b88\u653b\u4e4b\u52bf\u5927\u53d8\u3002 \u3000\u3000\u4f46\u4e00\u7728\u773c\u53c8\u662f\u6570\u5341\u62db\u8fc7\u53bb\uff0c\u201c\u5927\u884d\u5341\u5f0f\u201d\u867d\u62a2\u5c3d\u653b\u52bf\uff0c\u5374\u4e5f\u4f24\u4e0d\u5f97\u6167\u5927\u5e08\u4e00\u6839\u6beb\u6bdb\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u5e73\u51e1\u4e0a\u4eba\u5c06\u201c\u5927\u884d\u5341\u5f0f\u201d\u65bd\u5c55\u5f00\u6765\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u795e\u5a01\u4e4b\u6781\uff0c\u4e0d\u7531\u611f\u540c\u8eab\u53d7\uff0c \u5728\u4e00\u65c1\u624b\u821e\u8db3\u8e48\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u53c8\u9886\u609f\u5230\u4e0d\u5c11\u7cbe\u5fae\u53d8\u5316\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u53d1\u89c9\u6167\u5927\u5e08\u80fd\u5168\u5b88\u4e0d\u653b\u5730\u5728\u8fd9\u201c\u5927\u884d\u5251\u5f0f\u201d\u4e2d\u5b89\u7136\u65e0\u5fd8\uff0c\u5b8c\u5168\u662f\u90a3\u795e\u5999\u6b65\u6cd5 \u6240\u81f4\uff0c\u4f46\u4ed4\u7ec6\u7814\u7a76\u90a3\u795e\u5999\u6b65\u6cd5\uff0c\u5374\u53c8\u4f3c\u6beb\u65e0\u6cd5\u5ea6\u3002 \u3000\u3000\u4ed6\u54ea\u91cc\u77e5\u9053\u8fd9\u4e43\u662f\u6167\u5927\u5e08\u5e73\u751f\u5f97\u610f\u4e4b\u4f5c\u201c\u8bd8\u6469\u795e\u6b65\u201d\uff0c\u5176\u4e2d\u5965\u5999\u8270\u6df1\u4e4b\u5904\uff0c\u6167\u5927\u5e08 \u672c\u4eba\u4e5f\u662f\u4ece\u4e00\u672c\u53e4\u9057\u4e66\u4e0a\u8d39\u4e86\u65e0\u9650\u5fc3\u8840\u624d\u9886\u609f\u51fa\u6765\u7684\uff0c\u8f9b\u6377\u5c82\u80fd\u9886\u609f\uff1f \u3000\u3000\u8fd9\u65f6\u53cc\u65b9\u5df1\u4e92\u62c6\u5343\u62db\uff0c\u5404\u79cd\u795e\u5947\u62db\u5f0f\u7aef\u7684\u5c42\u51fa\u4e0d\u7a77\uff0c\u628a\u65c1\u8fb9\u7684\u8f9b\u6377\u770b\u7684\u6d51\u5fd8\u4e00\u5207\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5ffd\u7136\u4e00\u58f0\u6e05\u4eae\u7684\u5578\u58f0\u4ece\u8fdc\u5904\u4f20\u6765\uff0c\u90a3\u5578\u58f0\u5c16\u800c\u7ec6\uff0c\u4f46\u5374\u8fdc\u8d85\u8fc7\u6d77\u6d9b\u5de8\u58f0\uff0c\u6e05 \u6670\u5730\u4f20\u5165\u5c9b\u4e0a\u6bcf\u4e00\u4e2a\u4eba\u7684\u8033\u4e2d\uff0c\u5c24\u5176\u662f\u90a3\u5578\u58f0\u4e00\u5165\u8033\u4e2d\uff0c\u7acb\u523b\u4ee4\u4eba\u611f\u5230\u8bf4\u4e0d\u51fa\u7684\u548c\u5e73\u606c \u9759\uff0c\u4e00\u79cd\u8212\u9002\u7684\u611f\u89c9\uff0c\u4f7f\u4eba\u8fde\u52a8\u90fd\u4e0d\u60f3\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u8fde\u5e73\u51e1\u4e0a\u4eba\u6167\u5927\u5e08\u90a3\u7b49\u529f\u529b\uff0c\u5c45\u7136\u90fd\u54a6\u4e86\u4e00\u58f0\uff0c\u5404\u81ea\u4f4f\u624b\uff0c\u4fa7\u8033\u503e\u542c\uff0c\u8f9b\u6377\u66f4\u662f\u53c8 \u60ca\u53c8\u7591\u3002 \u3000\u3000\u6167\u5927\u5e08\u9762\u4e0a\u795e\u8272\u900f\u51fa\u60ca\u5947\u4e4b\u8272\uff0c\u5e73\u51e1\u4e0a\u4eba\u8138\u4e0a\u5374\u662f\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u53e4\u602a\u8868\u60c5\uff0c\u4ef0\u9996\u671b \u5929\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u4ef0\u9996\u671d\u5e73\u51e1\u4e0a\u4eba\u770b\u5904\u671b\u53bb\uff0c\u53ea\u89c1\u9ed1\u6c89\u7684\u5929\u9645\uff0c\u51e0\u9897\u758f\u661f\u6563\u5e03\u5176\u4e0a\uff0c\u54ea\u6709\u4e00\u4e1d \u5f02\u5904\u3002\u4f46\u90a3\u5578\u58f0\u5374\u662f\u4f4e\u7ec6\u800c\u6e05\u6670\u5730\u4e0d\u65ad\u4f20\u6765\uff0c\u4f46\u95fb\u5176\u58f0\u4e0d\u89c1\u5176\u5f71\uff0c\u76ca\u53d1\u663e\u5f97\u602a\u5f02\u3002\u8f9b\u6377 \u5947\u602a\u5730\u56de\u5934\u770b\u4e86\u5e73\u51e1\u4e0a\u4eba\uff0c\u53ea\u89c1\u5e73\u51e1\u4e0a\u4eba\u8138\u8272\u66f4\u662f\u5947\u602a\uff0c\u5ffd\u5730\u64ae\u53e3\u957f\u5578\u548c\u90a3\u5578\u58f0\u9065\u9065\u76f8 \u548c\u3002 \u3000\u3000\u521d\u65f6\u4e24\u79cd\u5578\u58f0\u9887\u4e0d\u4e00\u81f4\uff0c\u4f3c\u4e4e\u5e73\u51e1\u4e0a\u4eba\u5728\u5411\u90a3\u53d1\u5578\u4eba\u7533\u8bc9\u4e0d\u540c\u4e4b\u610f\u89c1\uff0c\u4f46\u6e10\u6e10\u90a3\u5578 \u58f0\u6108\u6765\u6108\u8fd1\uff0c\u5e73\u51e1\u4e0a\u4eba\u7684\u5578\u58f0\u4e5f\u9010\u6e10\u548c\u90a3\u4eba\u4e00\u81f4\uff0c\u4f3c\u4e4e\u5df2\u88ab\u8bf4\u670d\u3002 \u3000\u3000\u8f9b\u6377\u518d\u770b\u5e73\u51e1\u4e0a\u4eba\uff0c\u8138\u4e0a\u4e00\u6d3e\u548c\u5e73\u4e4b\u8272\uff0c\u4e24\u79cd\u5578\u58f0\u90fd\u662f\u4e00\u7247\u5b89\u606c\u4e4b\u6c14\uff0c\u6167\u5927\u5e08\u4e5f\u8083 \u7136\u7acb\u4e8e\u4e00\u65c1\u3002\u5ffd\u7136\u4e00\u58f0\u9e64\u5533\uff0c\u8f9b\u6377\u5fd9\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u8fdc\u5904\u4e00\u53ea\u7edd\u5927\u767d\u9e64\u98de\u6765\uff0c\u98de\u8fd1\u65f6\uff0c\u53ea \u89c1\u9e64\u80cc\u4e0a\u5750\u7740\u4e00\u4e2a\u7626\u957f\u8001\u50e7\uff0c\u5578\u58f0\u6b63\u662f\u4ed6\u6240\u53d1\u3002 \u3000\u3000\u90a3\u8001\u50e7\u8eab\u6750\u6781\u9ad8\uff0c\u5750\u5728\u9e64\u4e0a\u4ecd\u6bd4\u5e38\u4eba\u9ad8\u51fa\u534a\u4e2a\u5934\uff0c\u800c\u4e14\u7626\u5f97\u6709\u5982\u4e00\u6839\u7af9\u6746\uff0c\u4f46\u989d\u4e0b \u94f6\u987b\u5374\u662f\u6839\u6839\u53ef\u89c1\u3002 \u3000\u3000\u6167\u5927\u5e08\u89c1\u4e86\u4ed6\u8138\u4e0a\u60ca\u7591\u4e4b\u8272\u4e0d\u51cf\uff0c\u663e\u7136\u4e0d\u8bc6\u5f97\u6b64\u50e7\uff0c\u5e73\u51e1\u4e0a\u4eba\u5374\u8138\u8272\u5e73\u548c\u8083\u7a46\uff0c\u7f13 \u7f13\u8d70\u8fd1\u90a3\u5de8\u9e64\u3002 \u3000\u3000\u5de8\u9e64\u7565\u4e00\u76d8\u65cb\uff0c\u7f13\u7f13\u843d\u4e86\u4e0b\u6765\uff0c\u4e24\u7fc5\u5f20\u5f00\uff0c\u6015\u4e0d\u6709\u4e24\u4e08\u9614\uff0c\u6251\u51fa\u7684\u98ce\u5439\u5f97\u9ec4\u6c99\u5377\u5377\u3002 \u3000\u3000\u90a3\u8001\u50e7\u624b\u6267\u6728\u9c7c\u201c\u7b03\u201d\u5730\u4e00\u54cd\uff0c\u4e5f\u4e0d\u77e5\u90a3\u6728\u9c7c\u662f\u4ec0\u4e48\u8d28\u6599\u6240\u505a\uff0c\u58f0\u97f3\u4f20\u51fa\u6570\u91cc\u4e4b\u5916\uff0c \u6e05\u4eae\u4e4b\u6781\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5bf9\u4f30\u7626\u8001\u50e7\u4e00\u63d6\uff0c\u53c8\u8f6c\u8eab\u5bf9\u6167\u5927\u5e08\u4e00\u8bed\u4e0d\u53d1\uff0c\u722c\u4e0a\u9e64\u80cc\uff0c\u5bf9\u8f9b\u6377\u7565\u4e00\u70b9\u9996\uff0c \u90a3\u9e64\u53cc\u7fc5\u4e00\u5c55\uff0c\u817e\u7a7a\u800c\u8d77\uff0c\u90a3\u67af\u7626\u8001\u4f2f\u5bf9\u8f9b\u6377\u770b\u4e86\u4e00\u773c\uff0c\u8138\u4e0a\u900f\u51fa\u60ca\u8272\uff0c\u5bf9\u8f9b\u6377\u518d\u4e09\u6253 \u91cf\u540e\uff0c\u5ffd\u7136\u4f4e\u58f0\u541f\u9053\uff1a\u201c\u864e\u8dc3\u53cb\u817e\u98de\u9ec4\u65e5\uff0c\u9e64\u5533\u4e00\u58f0\u6f47\u6e58\u53bb\u3002\u201d \u3000\u3000\u767d\u9e64\u5de8\u7fc5\u6251\u51fa\uff0c\u7728\u773c\u5df2\u5728\u4e09\u5341\u4e08\u5916\uff0c\u4f46\u90a3\u4e24\u53e5\u5374\u6e05\u6670\u4f20\u6765\u3002 \u3000\u3000\u6167\u5927\u5e08\u7adf\u5446\u5446\u671b\u7740\u8fd9\u9a91\u9e64\u201c\u64c5\u4eba\u201d\u5c0f\u6222\u5c9b\u7684\u5947\u50e7\u65bd\u65bd\u7136\u800c\u53bb\uff0c\u4ef0\u9996\u5446\u671b\uff0c\u4f3c\u4e4e\u767e\u601d \u4e0d\u5f97\u5176\u89e3\uff0c\u4f46\u5f53\u5979\u773c\u5149\u7f13\u7f13\u843d\u5728\u8f9b\u6377\u8138\u4e0a\uff0c\u8138\u4e0a\u5374\u9732\u51fa\u4e00\u4e1d\u7b11\u5bb9\u3002 \u3000\u3000\u53ea\u89c1\u5979\u5ffd\u7136\u5730\u53cc\u8896\u4e00\u821e\uff0c\u5728\u6c99\u6ee9\u4e0a\u8d70\u51fa\u90a3\u5957\u5999\u7edd\u4eba\u5bf0\u7684\u201c\u8bd8\u6469\u795e\u6b65\u201d\uff0c\u56db\u5341\u4e5d\u4e2d\u6b65 \u6cd5\u65bd\u5b8c\uff0c\u8eab\u5f62\u4e00\u62d4\uff0c\u7adf\u62d4\u8d77\u5341\u4e08\uff0c\u98d8\u7136\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u8d8b\u524d\u4e00\u770b\uff0c\u53ea\u89c1\u6c99\u5730\u4e0a\u7559\u7740\u4e00\u7247\u811a\u5370\uff0c\u6df1\u8fbe\u6570\u5bf8\uff0c\u4e0d\u7981\u5fc3\u5934\u5927\u559c\uff0c\u77e5\u9053\u6167\u5927\u5e08 \u6709\u610f\u5c06\u8fd9\u5957\u795e\u5947\u65e0\u6bd4\u6b65\u6cd5\u4f20\u6388\u81ea\u5df1\uff0c\u4e00\u65f6\u5174\u594b\u5f97\u6709\u4e9b\u75c5\u4e86\u3002 \u3000\u3000\u8fdc\u5904\u5374\u4f20\u51fa\u4e00\u58f0\uff1a\u201c\u8bd8\u6469\u795e\u6b65\u4f20\u4e0e\u6709\u7f18\uff0c\u534a\u4e2a\u65f6\u8fb0\u5185\u80fd\u4e0d\u80fd\u9886\u609f\uff0c\u5c31\u8981\u770b\u4f60\u7684\u5929\u8d44 \u4e86\u3002\u201d\u6167\u5927\u5e08\u5185\u529b\u4f55\u7b49\u6df1\u539a\uff0c\u4e00\u5b57\u4e00\u5b57\u5728\u6d77\u6d9b\u58f0\u4e2d\u4f20\u51fa\u8001\u8fdc\u3002 \u3000\u3000\u8f9b\u6377\u867d\u4e0d\u660e\u767d\u5979\u8bf4\u4ec0\u4e48\u201c\u534a\u4e2a\u65f6\u8fb0\u201d\uff0c\u4f46\u7acb\u523b\u5411\u5c9b\u5fc3\u8dea\u4e0b\uff0c\u5583\u5583\u795d\u8c22\u3002 \u3000\u3000\u4f46\u662f\uff0c\u7acb\u523b\u4ed6\u5c31\u5fc3\u795e\u6c89\u9189\u5728\u6c99\u96be\u4e0a\u90a3\u7247\u795e\u5965\u65e0\u6bd4\u7684\u811a\u6b65\u5370\u4e2d\u4e86\u3002 \u3000\u3000\u4ee5\u8f9b\u6377\u7684\u529f\u529b\u667a\u6167\uff0c\u7adf\u7136\u770b\u5f97\u5341\u5206\u5403\u529b\uff0c\u5982\u4e0d\u662f\u4ed6\u66fe\u76ee\u7779\u6167\u5927\u5e08\u4eb2\u8eab\u65bd\u5c55\u8fc7\u51e0\u6b21\uff0c \u6839\u672c\u5c31\u65e0\u6cd5\u9886\u609f\u3002\u8fd9\u201c\u8bd8\u6469\u795e\u6b65\u201d\u7aef\u7684\u582a\u79f0\u72ec\u6b65\u5929\u4e0b\uff0c\u8f9b\u6377\u6108\u770b\u6108\u89c9\u8270\u6df1\uff0c\u4e5f\u6108\u89c9\u5f97\u9ad8 \u5174\u3002 \u3000\u3000\u534a\u4e2a\u65f6\u8fb0\u8f6c\u773c\u5373\u81f3\uff0c\u8f9b\u6377\u4ecd\u7136\u6c89\u9189\u5176\u4e2d\uff0c\u4e0d\u77e5\u5916\u754c\u4e8b\u7269\uff0c\u800c\u4e0d\u8fdc\u5904\u7684\u6d77\u6f6e\u5df2\u8d77\uff0c\u53ea \u89c1\u8fdc\u5904\u4f3c\u4e4e\u4ece\u6d77\u5e73\u7ebf\u4e0a\u4e00\u9053\u767d\u7ebf\u5347\u8d77\uff0c\u52bf\u5982\u5954\u96f7\u822c\u76f4\u6eda\u8fc7\u6765\uff0c\u6108\u6eda\u6108\u5feb\uff0c\u4e5f\u6108\u51b2\u6108\u9ad8\uff0c \u90a3\u6d88\u7247\u523b\u5df2\u6210\u4e86\u6570\u4e08\u7684\u6d6a\u5899\uff0c\u6d69\u6d69\u8361\u8361\u5730\u6d8c\u5c06\u4e0a\u6765\u3002 \u3000\u3000\u8f9b\u6377\u6b63\u8eac\u8eab\u82e6\u601d\u201c\u8bd8\u6469\u795e\u6b65\u201d\u6700\u540e\u4e94\u4e2a\u6b65\u6cd5\uff0c\u8fd9\u4e94\u4e2a\u6b65\u6cd5\u4e43\u662f\u5168\u90e8\u795e\u6b65\u4e2d\u6700\u7cbe\u534e\u6240 \u5728\uff0c\u5c24\u5176\u96be\u5965\u65e0\u6bd4\uff0c\u4ed6\u6b63\u5168\u795e\u8d2f\u6ce8\uff0c\u90a3\u6ed4\u5929\u6d77\u6f6e\u5df2\u5230\u8eab\u540e\u6d77\u8fb9\uff0c\u72b9\u81ea\u4e0d\u89c9\u3002 \u3000\u3000\u8f9b\u6377\u7d22\u6027\u53cc\u8db3\u8e0f\u5728\u6167\u5927\u5e08\u811a\u5370\u4e0a\u5c06\u90a3\u6700\u540e\u4e94\u5f0f\u8bd5\u884c\u4e00\u756a\uff0c\u8fd9\u4e00\u8eac\u8eab\u5b9e\u8df5\uff0c\u7acb\u523b\u5c06\u65b9 \u624d\u82e6\u601d\u4e0d\u5f97\u7684\u7591\u95ee\u89e3\u6d88\uff0c\u5fc3\u5934\u4e0d\u7981\u4e00\u9635\u72c2\u559c\uff0c\u6b63\u8981\u8dc3\u8d77\uff0c\u5ffd\u89c9\u811a\u4e0a\u4e00\u51c9\uff0c\u4e00\u56de\u5934\u66f4\u662f\u5927 \u5403\u4e00\u60ca\uff0c\u53ea\u89c1\u767d\u832b\u832b\u7684\u4e00\u7247\u6d6a\u6d9b\u6d8c\u5230\u9762\u524d\uff0c\u4e00\u6025\u4e4b\u4e0b\uff0c\u65bd\u51fa\u201c\u6697\u9999\u6d6e\u5f71\u201d\u7684\u8f7b\u529f\u7edd\u6280\uff0c \u8eab\u5f62\u4e00\u8e2a\u4e4b\u95f4\uff0c\u98d8\u51fa\u516d\u4e03\u4e08\u8fdc\uff0c\u4f46\u5f53\u4ed6\u8eab\u5f62\u624d\u843d\uff0c\u811a\u4e0b\u5df2\u662f\u767d\u832b\u4e00\u7247\u3002 \u3000\u3000\u6f6e\u6c34\u6d8c\u4e0a\u4f55\u7b49\u8fc5\u901f\uff0c\u8f9b\u6377\u4e00\u8dc3\u4e4b\u52bf\uff0c\u7adf\u4e0d\u53ca\u6c34\u6da8\u5f97\u5feb\uff0c\u8f9b\u6377\u8eab\u5728\u7a7a\u4e2d\uff0c\u731b\u7136\u518d\u63d0\u4e00 \u53e3\u6c14\uff0c\u811a\u5c16\u5728\u6d6a\u9762\u4e0a\u4e00\u70b9\uff0c\u8eab\u5f62\u53c8\u62d4\u8d77\u4e08\u4f59\uff0c\u4f46\u90a3\u6d77\u6f6e\u4e00\u5377\u800c\u4e0a\uff0c\u4ed6\u8eab\u5f62\u65b9\u624d\u4e00\u62d4\u8d77\uff0c \u4e0b\u8eab\u81ea\u819d\u4ee5\u4e0b\u5df2\u662f\u5c3d\u6e7f\u3002\u90a3\u77e5\u8eab\u5f62\u4e0b\u843d\u65f6\uff0c\u8f9b\u6377\u4f4e\u5934\u4e00\u770b\uff0c\u811a\u4e0b\u53c8\u662f\u4e00\u7247\u6d6a\u6f6e\uff0c\u8f9b\u6377\u4e0d \u7531\u4e00\u54ac\u7259\uff0c\u8eab\u5f62\u5fae\u4e00\u70b9\u6c34\uff0c\u53c8\u590d\u8dc3\u8d77\uff0c\u65bd\u5f00\u4e0a\u4e58\u8f7b\u529f\uff0c\u62fc\u7740\u4e0b\u8eab\u6e7f\u900f\u548c\u6d77\u6f6e\u62a2\u5feb\u3002 \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u4f55\u7b49\u529f\u529b\uff0c\u201c\u6697\u9999\u6d6e\u5f71\u201d\u53c8\u662f\u6781\u4e0a\u4e58\u7684\u529f\u592b\uff0c\u51e0\u4e2a\u8d77\u843d\u4e4b\u4e0b\uff0c\u7adf\u5c06\u52bf\u82e5\u75be \u98ce\u7684\u6d77\u6f6e\u8fdc\u8fdc\u629b\u5728\u8eab\u540e\u3002 \u3000\u3000\u4e00\u76f4\u5954\u51fa\u4e8c\u5341\u4e08\u8fdc\uff0c\u8f9b\u6377\u624d\u505c\u8eab\u56de\u671b\uff0c\u53ea\u89c1\u8fdc\u5904\u767d\u6f6e\u6380\u5929\uff0c\u65b9\u624d\u7acb\u8db3\u4e4b\u5730\u65e9\u5df2\u6df9\u6ca1 \u6f6e\u4e2d\uff0c\u90a3\u6167\u5927\u5e08\u7559\u4e0b\u7684\u201c\u8bd8\u6469\u795e\u6b65\u201d\u811a\u5370\uff0c\u4e0d\u6d88\u8bf4\u4e00\u5b9a\u88ab\u51b2\u6d17\u65e0\u8e2a\uff0c\u96be\u602a\u6167\u5927\u5e08\u8981\u8bf4 \u201c\u534a\u4e2a\u65f6\u8fb0\u4e4b\u5185\u201d\u7684\u8bdd\u3002 \u3000\u3000\u8f9b\u6377\u76ee\u7779\u6d77\u6f6e\u5947\u666f\uff0c\u53ea\u89c9\u5f97\u5fc3\u80f8\u4e3a\u4e4b\u4e00\u9614\uff0c\u4e00\u65f6\u80f8\u4e2d\u8c6a\u6c14\u52c3\u52c3\uff0c\u96c4\u5fc3\u5343\u4e08\uff0c\u4e0d\u7531\u81ea \u4e3b\u5730\u632f\u8896\u9ad8\u6b4c\u9053\uff1a\u201c\u4e71\u77f3\u5d29\u4e91\uff0c\u60ca\u6d9b\u88c2\u5cb8\uff0c\u5377\u8d77\u5343\u5806\u96ea\uff0c\u6c5f\u5c71\u5982\u753b\uff0c\u4e00\u65f6\u591a\u5c11\u8c6a\u6770\u3002\u201d \u3000\u3000\u5531\u5230\u6b64\u5904\uff0c\u8f9b\u6377\u4e0d\u7981\u60f3\u5230\uff0c\u4e00\u5929\u4ee5\u524d\u81ea\u5df1\u8fd8\u56f0\u675f\u4e8e\u513f\u5973\u4e4b\u60c5\u53ca\u7070\u5fc3\u9893\u5e9f\u4e4b\u4e2d\uff0c\u6b64\u523b \u5374\u8c6a\u6c14\u5e72\u4e91\uff0c\u96c4\u5fc3\u5343\u4e08\uff0c\u4ed6\u6697\u4e2d\u4e0b\u51b3\u5fc3\uff0c\u4e00\u5b9a\u8981\u521b\u4e0b\u4e00\u756a\u8f70\u8f70\u70c8\u70c8\u7684\u5927\u4e8b\uff0c\u624d\u80fd\u8c08\u5230\u5176 \u4ed6\u3002 \u3000\u3000\u6012\u6f6e\u6f8e\u6e43\uff0c\u591c\u8272\u6e10\u892a\uff0c\u5929\u8fb9\u9732\u51fa\u4e00\u4e1d\u66d9\u5149\uff0c\u970e\u65f6\u91d1\u5149\u56db\u5c04\uff0c\u7ea2\u6ce2\u7ffb\u817e\uff0c\u4e00\u8f6e\u7ea2\u65e5\u5347 \u4e86\u4e0a\u6765\u3002 \u3000\u3000\u8f9b\u6377\u6e10\u6e10\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5df2\u4ece\u5c9b\u4e1c\u7ed5\u5230\u5c9b\u897f\uff0c\u4ed6\u5fc3\u4e2d\u6b63\u5728\u6697\u8ba1\u5982\u4f55\u79bb\u5f00\u8fd9\u5b64\u7acb\u5927\u6d77\u7684\u5c0f \u5c9b\uff0c\u4f46\u5f53\u4ed6\u4e00\u62ac\u5934\uff0c\u53ea\u89c1\u6d77\u9762\u5e73\u9759\u5f97\u5f88\uff0c\u5929\u7a7a\u4e00\u671b\u65e0\u4e91\uff0c\u5343\u91cc\u6674\u7a7a\uff0c\u4f46\u6700\u4ee4\u4ed6\u6ce8\u610f\u7684\u5374 \u662f\u6d77\u8fb9\u6c99\u6ee9\u4e0a\u6401\u7740\u4e00\u53ea\u5c0f\u578b\u5e06\u8239\u3002 \u3000\u3000\u8f9b\u6377\u8fde\u5fd9\u5feb\u6b65\u4e0a\u524d\uff0c\u53ea\u89c1\u8239\u524d\u6c99\u4e0a\u5199\u7740\u4e00\u7247\u5927\u5b57\uff1a\u201c\u7531\u5c0f\u6222\u5c9b\u897f\u5357\u884c\uff0c\u6b64\u65f6\u6d77\u9762\u897f \u98ce\u751a\u5f3a\uff0c\u626c\u5e06\u4e00\u65e5\u53ef\u8fbe\u5927\u9646\u3002\u201d\u663e\u7136\u662f\u6167\u5927\u5e08\u4e4b\u7b14\uff0c\u90a3\u8239\u81ea\u7136\u4e5f\u662f\u5979\u9884\u5907\u7684\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u770b\u7f62\u5403\u4e86\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u53ea\u9700\u4e00\u65e5\u822a\u7a0b\u5373\u53ef\u5230\u8fbe\u5927\u9646\uff0c\u8fd9\u5c0f\u6222\u5c9b\u79bb\u5927\u9646\u5982\u6b64\u4e4b \u8fd1\uff1f\u201d\u4e0d\u7981\u6781\u76ee\u8fdc\u773a\uff0c\u679c\u7136\u77a7\u89c1\u8fdc\u65b9\u6c34\u5929\u76f8\u63a5\u5904\u4f9d\u7a00\u53ef\u89c1\u4e00\u5e26\u6781\u6de1\u7684\u5c71\u5f71\uff0c\u90a3\u5929\u8fb9\u662f\u4e73 \u767d\u8272\uff0c\u5c71\u5374\u662f\u6de1\u84dd\u8272\uff0c\u662f\u4ee5\u52c9\u5f3a\u53ef\u4ee5\u8fa8\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u518d\u6b21\u8f6c\u8eab\u5411\u5c9b\u5fc3\u795d\u798f\uff0c\u542f\u5e06\u4eba\u6d77\u3002 \u3000\u3000\u897f\u98ce\u751a\u75be\uff0c\u5374\u751a\u662f\u5e73\u7a33\uff0c\u5c0f\u8239\u53c8\u5f88\u8f7b\u5feb\uff0c\u90a3\u5e06\u5403\u5f97\u9971\u9971\u7684\uff0c\u90a3\u6d88\u7247\u523b\uff0c\u5df2\u8fdc\u79bb\u5c0f\u5c9b\u3002 \u8f9b\u6377\u56de\u9996\u671b\u65f6\uff0c\u5c0f\u6222\u5c9b\u5df2\u6210\u4e86\u4e00\u5c0f\u70b9\u9ed1\u5f71\uff0c\u53ea\u6709\u90a3\u5c9b\u4e0a\u6700\u9ad8\u7684\u4e00\u6839\u77f3\u7b0b\u4ecd\u53ef\u8fa8\u51fa\uff0c\u9ad8\u77d7 \u6674\u7a7a\u3002 \u3000\u3000\u957f\u6c5f\u81f3\u6b66\u6c49\u4e00\u5e26\uff0c\u5411\u4e1c\u5317\u65b9\u5206\u51fa\u4e00\u6761\u652f\u6d41\uff0c\u79f0\u4f5c\u6c49\u6c34\uff0c\u548c\u957f\u6c5f\u6210\u4e4b\u5b57\u5f62\u9694\u5f00\u6b66\u660c\u3001 \u6c49\u53e3\u3001\u6c49\u9633\u4e09\u5730\uff0c\u81ea\u53e4\u4e3a\u6c5f\u9102\u4e00\u5e26\u91cd\u9547\uff0c\u884c\u4eba\u7199\u6518\u70ed\u95f9\u4e4b\u81f3\u3002 \u3000\u3000\u81ea\u4ece\u4e03\u5999\u795e\u541b\u518d\u73b0\u6c5f\u6e56\uff0c\u5728\u6b66\u6c49\u4e00\u5e26\u529e\u8fc7\u51e0\u4ef6\u60ca\u52a8\u6b66\u6797\u7684\u4e8b\u540e\uff0c\u6b66\u6c49\u66f4\u662f\u7fa4\u82f1\u6bd5\u96c6\uff0c \u5404\u6d3e\u9ad8\u624b\u76f8\u7ee7\u8d76\u5230\uff0c\u90fd\u60f3\u5bdf\u77e5\u4e03\u5999\u795e\u541b\u91cd\u73b0\u6c5f\u6e56\u4e4b\u4f20\u95fb\u662f\u5426\u5c5e\u5176\u3002 \u3000\u3000\u5c24\u5176\u662f\u5f53\u5e74\u53c2\u52a0\u56f4\u51fb\u4e03\u5999\u795e\u541b\u7684\u4e94\u5927\u6d3e\u66f4\u662f\u6025\u6b32\u4fa6\u77e5\u4e8b\u5b9e\uff0c\u6545\u6b64\u6b66\u6c49\u4e00\u5e26\u7a7a\u6c14\u767b\u65f6 \u7d27\u5f20\u8d77\u6765\u3002 \u3000\u3000\u65f6\u6b63\u590f\u672b\uff0c\u6b66\u6c49\u4e00\u5e26\u5929\u6c14\u867d\u4ecd\u4e0d\u80fd\u7b97\u5f97\u4e0a\u51c9\u723d\uff0c\u4f46\u5374\u6709\u91d1\u98ce\u9001\u723d\u7684\u6c14\u6c1b\u4e86\u3002 \u3000\u3000\u8fd9\u5929\uff0c\u6c5f\u4e0a\u9a76\u6765\u4e00\u53ea\u5c0f\u821f\uff0c\u8fd9\u5c0f\u821f\u4f3c\u662f\u8981\u5411\u5cb8\u5934\u884c\u62e2\u8fc7\u6765\uff0c\u662f\u4ee5\u884c\u901f\u751a\u6162\uff0c\u52a0\u4e4b\u6c5f \u6c34\u9006\u6d41\uff0c\u770b\u8d77\u6765\u597d\u50cf\u5c0f\u821f\u6839\u672c\u884c\u4e0d\u52a8\u7684\u6a21\u6837\u3002 \u3000\u3000\u8fd9\u65f6\u6c5f\u4e0a\u5e06\u8239\u4f55\u6b62\u6570\u5341\u6761\uff0c\u8fd9\u5c0f\u821f\u5728\u7a7f\u68ad\u822c\u7684\u8239\u6797\u4e2d\u7f13\u7f13\u9760\u5230\u5cb8\u8fb9\uff0c\u8239\u4e0a\u5374\u8d70\u4e0b\u6765 \u4e00\u4e2a\u5e74\u7ea6\u5eff\u5de6\u53f3\u7684\u9752\u5e74\u6587\u58eb\uff0c\u8eab\u7740\u7070\u9752\u8272\u5e03\u8863\uff0c\u7f13\u7f13\u8d70\u4e0a\u5cb8\u8fb9\uff0c\u884c\u52a8\u5341\u5206\u7aef\u5e84\u3002 \u3000\u3000\u8fd9\u9752\u5e74\u4f3c\u4e0d\u613f\u88ab\u90a3\u5f80\u6765\u4e0d\u7edd\u7684\u884c\u4eba\u6240\u963b\uff0c\u4e0a\u5f97\u5cb8\u6765\uff0c\u6025\u6b65\u7a7f\u8fc7\u9a6c\u9053\uff0c\u6cbf\u7740\u9053\u513f\u5411\u6c49 \u53e3\u57ce\u95e8\u8d70\u53bb\u3002 \u3000\u3000\u5982\u4ed4\u7ec6\u89c2\u5bdf\u4e00\u4e0b\uff0c\u4fbf\u53ef\u770b\u51fa\u8fd9\u9752\u5e74\u795e\u8272\u95f4\uff0c\u4f3c\u4e4e\u5145\u6ee1\u7740\u4e00\u79cd\u83ab\u540d\u7684\u795e\u91c7\uff0c\u4f46\u6c14\u8272\u5374 \u53c8\u7115\u53d1\u7684\u51fa\u5947\uff0c\u4e00\u5f20\u79c0\u4fca\u7684\u8138\u513f\u914d\u4e0a\u9ad8\u5ea6\u9002\u4e2d\u7684\u8eab\u6750\uff0c\u518d\u52a0\u4e0a\u884c\u52a8\u6f47\u6d12\uff0c\u786e\u662f\u4e00\u8868\u4eba\u624d\u3002 \u3000\u3000\u552f\u4e00\u7684\u5c31\u662f\u4ed6\u8138\u513f\u4e0a\u5fae\u5fae\u6709\u70b9\u663e\u5f97\u82cd\u767d\u3002 \u3000\u3000\u8fd9\u9752\u5e74\u6b65\u884c\u786e\u662f\u751a\u5feb\uff0c\u4e0d\u6d88\u7247\u523b\u4fbf\u6765\u57ce\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\u6b63\u662f\u5348\u540e\u65f6\u5206\uff0c\u5929\u6c14\u5fae\u5fae\u663e\u5f97\u95f7\u4eba\uff0c\u5c24\u5176\u662f\u98ce\u513f\u98d8\u5f97\u751a\u5927\uff0c\u57ce\u4e2d\u8fd8\u597d\uff0c\u57ce\u5916\u9a6c \u9053\u4e0a\u5374\u662f\u5c18\u6c99\u6f2b\u5929\u3002\u90a3\u9752\u5e74\u8d70\u8fdb\u57ce\u6765\uff0c\u5374\u89c1\u4ed6\u4e00\u8eab\u8863\u670d\u6e05\u51c0\u5982\u5e38\uff0c\u4f3c\u662f\u4e00\u5c18\u672a\u67d3\uff0c\u5b9e\u5728 \u6709\u4e9b\u513f\u51fa\u5947\u3002 \u3000\u3000\u8fce\u9762\u4fbf\u662f\u4e1c\u8857\uff0c\u90a3\u9752\u5e74\u4e0d\u5047\u601d\u7d22\u6253\u6a2a\u91cc\u513f\u8d70\u5411\u4e1c\u8857\uff0c\u671d\u65b0\u8fd1\u624d\u5f00\u94fa\u4e0d\u4e45\u7684\u5c71\u6885\u73e0\u5b9d \u5e97\u8d70\u53bb\u3002 \u3000\u3000\u8d70\u5230\u8fd1\u5904\uff0c\u90a3\u9752\u5e74\u4f3c\u4e4e\u9762\u5fae\u5e26\u60ca\u5947\u4e4b\u8272\uff0c\u811a\u6b65\u5fae\u5fae\u52a0\u5feb\uff0c\u53e3\u4e2d\u5524\u9053\uff1a\u201c\u5f20\u5927\u54e5\u2014\u2014\u201d \u3000\u3000\u73e0\u5b9d\u5e97\u4e2d\u4eba\u5f71\u4e00\u6643\uff0c\u8fce\u95e8\u8d70\u51fa\u4e00\u4e2a\u5e74\u7ea6\u56db\u5341\u5de6\u53f3\u7cbe\u5e72\u7684\u6c49\u5b50\u6b22\u7136\u5bf9\u90a3\u9752\u5e74\u9053\uff1a\u201c\u8f9b \u8001\u677f\uff0c\u4f60\u56de\u6765\u5566\uff0c\u5c0f\u7684\u671b\u4f60\u56de\u6765\u90fd\u7b49\u5230\u773c\u7a7f\u5566\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\uff0c\u795e\u8272\u95f4\u4f3c\u4e4e\u751a\u662f\u60b2\u5fff\u3002 \u3000\u3000\u90a3\u59d3\u8f9b\u7684\u9752\u5e74\u8be7\u7136\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u5f20\u5927\u54e5\u2014\u2014\u201d \u3000\u3000\u90a3\u59d3\u5f20\u7684\u6c49\u5b50\u5df2\u9ed4\u7136\u9053\uff1a\u201c\u4faf\u8001\u4ed6\u2026\u2026\u4ed6\u6b7b\u53bb\u4e86\u2014\u2014\u201d \u3000\u3000\u90a3\u8f9b\u59d3\u9752\u5e74\u4f3c\u4e4e\u5403\u4e86\u4e00\u60ca\uff0c\u8eab\u5f62\u4e00\u52a8\uff0c\u5df2\u6765\u81f3\u90a3\u59d3\u5f20\u6c49\u5b50\u7684\u8eab\u524d\uff0c\u8fd9\u4e00\u624b\u6781\u4e0a\u4e58\u7684 \u79fb\u4f4d\u8f7b\u529f\uff0c\u5982\u679c\u6709\u8bc6\u8d27\u7684\u4eba\u770b\u5230\uff0c\u4e0d\u77e5\u4f1a\u5403\u60ca\u5230\u4ec0\u4e48\u5730\u6b65\u4e86\u3002 \u3000\u3000\u90a3\u9752\u5e74\u6765\u5230\u5f20\u67d0\u8eab\u524d\uff0c\u4e00\u624b\u6293\u4f4f\u5f20\u67d0\u7684\u8863\u9886\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff01\u4f60\u662f\u8bf4\u2014\u2014\u4f60\u662f \u8bf4\u4faf\u4e8c\u53d4\u5df2\u7ecf\u53bb\u4e16\u2026\u2026\u201d \u3000\u3000\u90a3\u59d3\u5f20\u7684\u6c49\u5b50\u51b7\u4e0d\u9632\u88ab\u90a3\u59d3\u8f9b\u7684\u6293\u4f4f\uff0c\u4e00\u65f6\u6323\u4e0d\u8131\uff0c\u542c\u4ed6\u5982\u6b64\u95ee\uff0c\u5fd9\u7b54\u9053\uff1a\u201c\u6b64\u8bdd \u8bf4\u6765\u751a\u957f\uff0c\u5bb9\u5c0f\u7684\u8fdb\u5e97\u518d\u544a\u2014\u2014\u201d \u3000\u3000\u90a3\u8f9b\u59d3\u5c11\u5e74\u4f3c\u4e4e\u751a\u6025\uff0c\u5389\u58f0\u6253\u65ad\u63d2\u5634\u9053\uff1a\u201c\u4faf\u4e8c\u53d4\u5230\u5e95\u600e\u4e48\u6837\u5566\uff01\u201d \u3000\u3000\u90a3\u5f20\u67d0\u5403\u4e86\u4e00\u60ca\uff0c\u98a4\u58f0\u7b54\u9053\uff1a\u201c\u4ed6\u6b7b\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u90a3\u8f9b\u59d3\u9752\u5e74\u653e\u624b\u4fbf\u95ee\u540e\u5012\u4e0b\uff0c\u767b\u65f6\u660f\u8ff7\u8fc7\u53bb\u3002 \u3000\u3000\u59d3\u5f20\u7684\u6c49\u5b50\u5927\u5403\u4e00\u60ca\uff0c\u6025\u5fd9\u6276\u8d77\u90a3\u9752\u5e74\uff0c\u534a\u62d6\u534a\u6276\u8d70\u8fdb\u5e97\u4e2d\uff0c\u6025\u5fd9\u5524\u4e8c\u4e2a\u4f19\u8ba1\u62ac\u4eba \u90a3\u9752\u5e74\uff0c\u81ea\u5df1\u6025\u5fd9\u53bb\u70e7\u4e00\u7897\u59dc\u6c64\uff0c\u51c6\u5907\u5582\u8f9b\u59d3\u5c11\u5e74\u5403\u4e0b\u53bb\u3002 \u3000\u3000\u4e00\u9635\u5fd9\u4e71\uff0c\u59dc\u6c64\u5c1a\u672b\u714e\u597d\uff0c\u90a3\u9752\u5e74\u53cd\u5012\u60a0\u60a0\u9192\u6765\uff0c\u722c\u8d77\u8eab\u6765\uff0c\u5389\u58f0\u95ee\u65c1\u8fb9\u7684\u4f19\u8ba1\u9053\uff1a \u201c\u4faf\u4e8c\u53d4\u662f\u600e\u6837\u6b7b\u7684\uff1f\u201d \u3000\u3000\u4e66\u4e2d\u4ea4\u5f85\uff0c\u8fd9\u9752\u5e74\u5f53\u7136\u4fbf\u662f\u5c71\u6885\u73e0\u5b9d\u5e97\u4e1c\u8f9b\u6377\uff0c\u4ed6\u81ea\u79bb\u5c0f\u6222\u5c9b\u540e\uff0c\u6025\u5fd9\u8d76\u56de\u6b66\u6c49\uff0c \u4e0d\u6599\u95fb\u5230\u81ea\u5c0f\u5f85\u4ed6\u751a\u597d\u7684\u4faf\u4e8c\u53d4\u7adf\u5df2\u6b7b\u53bb\uff0c\u4e00\u65f6\u6025\u54c0\u653b\u5fc3\uff0c\u660f\u8ff7\u8fc7\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u8f9b\u6377\u95ee\u90a3\u4f19\u8ba1\uff0c\u90a3\u4f19\u8ba1\u9053\uff1a\u201c\u5341\u4f59\u5929\u524d\uff0c\u5f20\u5927\u54e5\u5728\u51cc\u6668\u65f6\u5728\u53a2\u623f\u5929\u4e95\u4e2d\u53d1\u73b0\u4faf \u8001\u8eba\u5728\u5730\u4e0a\uff0c\u5df2\u7136\u6b7b\u53bb\uff0c\u5148\u8fd8\u4ee5\u4e3a\u662f\u4e00\u65f6\u4e2d\u98ce\u81f4\u6b7b\uff0c\u4f46\u540e\u6765\u89c1\u4ed6\u80cc\u4e0a\u4f3c\u4e4e\u6709\u5185\u4f24\u4f24\u75d5\uff0c \u8fd9\u624d\u77e5\u662f\u88ab\u4eba\u51fb\u6bd9\uff0c\u5f20\u5927\u54e5\u6025\u5f97\u8981\u6b7b\uff0c\u4ee5\u4e3a\u8f9b\u8001\u677f\u548c\u6b66\u6797\u4eba\u7269\u4ea4\u5f80\u800c\u62db\u81f4\u5927\u7978\uff0c\u53c8\u6015\u532a \u5f92\u518d\u5ea6\u6765\u4e34\uff0c\u5f53\u65f6\u4eba\u5fc3\u60f6\u60f6\uff0c\u66fe\u4e00\u5ea6\u51c6\u5907\u89e3\u6563\u5e97\u52a1\uff0c\u6628\u65e5\u624d\u9001\u4e86\u4faf\u8001\u7684\u4e27\uff0c\u597d\u5728\u4eca\u65e5\u8001 \u677f\u56de\u6765\u4e86\uff01\u201d \u3000\u3000\u8f9b\u6377\u542c\u540e\uff0c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u6014\uff0c\u60b2\u6124\u7684\u4e00\u8dfa\u811a\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u95ee\u5f20\u59d3\u7684\u6c49\u5b50\u9053\uff1a\u201c\u4faf\u4e8c\u53d4 \u846c\u5728\u4ec0\u4e48\u5730\u65b9\u5462\uff1f\u201d \u3000\u3000\u5f20\u67d0\u5fae\u53f9\u4e00\u58f0\uff1a\u201c\u5c0f\u7684\u5e73\u65e5\u7d20\u77e5\u8f9b\u8001\u677f\u751a\u656c\u91cd\u4faf\u8001\uff0c\u6240\u4ee5\u64c5\u81ea\u4e3b\u5f20\u52a8\u7528\u539a\u91d1\u846c\u4e86\u4faf \u8001\uff0c\u5893\u5730\u5c31\u5728\u57ce\u5916\u4e0d\u8fdc\u7684\u897f\u65b9\u4e00\u4e2a\u5c71\u5c97\u4e0a\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u70b9\u5934\uff0c\u8d70\u51fa\u53a2\u623f\uff0c\u5f20\u638c\u67dc\u6025\u8d70\u5411\u524d\u60f3\u963b\u62e6\uff0c\u6015\u4ed6\u5c1a\u672a\u590d\u5143\u4e0d\u80fd\u884c\u52a8\uff0c\u8f9b\u6377 \u5bf9\u4ed6\u6295\u4ee5\u611f\u6fc0\u7684\u4e00\u77a5\uff0c\u7f13\u7f13\u8d70\u53bb\u3002 \u3000\u3000\u4e0d\u6d88\u7247\u523b\uff0c\u6765\u5230\u57ce\u5916\uff0c\u4f9d\u5f20\u638c\u67dc\u7684\u6307\u793a\uff0c\u627e\u5230\u5c71\u5c97\uff0c\u679c\u89c1\u4e00\u4e2a\u5927\u5893\u5c31\u5728\u4e0d\u8fdc\u5904\uff0c\u5fd9 \u4e00\u8f6c\u8eab\u5b50\uff0c\u6251\u5728\u5893\u524d\u3002 \u3000\u3000\u987b\u77e5\u8f9b\u6377\u5e7c\u5e74\u4e27\u7236\u4ea1\u6bcd\uff0c\u552f\u4e00\u7684\u4eb2\u4eba\u4fbf\u662f\u6885\u5c71\u6c11\u6885\u53d4\u53d4\u548c\u4faf\u4e8c\u53d4\uff0c\u53ca\u957f\uff0c\u7a0d\u901a\u4eba\u4e8b\uff0c \u5bf9\u6885\u3001\u4faf\u4e8c\u4eba\u89c6\u82e5\u7236\u53d4\u8f88\uff0c\u5c0a\u656c\u4e4b\u6781\uff0c\u8fd9\u65f6\u7a81\u95fb\u5669\u8017\uff0c\u54ea\u80fd\u4e0d\u4f24\u5fc3\u6b32\u7edd\uff0c\u521a\u624d\u8fd8\u52aa\u529b\u514b \u5236\u4f4f\u4e0d\u6d41\u6cea\u6c34\uff0c\u8fd9\u65f6\u89c1\u5893\u7891\u5728\u524d\uff0c\u89e6\u666f\u751f\u60c5\uff0c\u54ea\u80fd\u4e0d\u75db\u54ed\u6d41\u6d95\uff0c\u60b2\u4f24\u6b32\u7edd\u3002 \u3000\u3000\u4f46\u4ed6\u5230\u5e95\u662f\u8eab\u6000\u7edd\u6280\u7684\u4eba\uff0c\u867d\u7136\u6781\u91cd\u611f\u60c5\uff0c\u5012\u4e5f\u80fd\u53ca\u65f6\u6536\u6cea\uff0c\u5446\u7acb\u5893\u524d\u3002 \u3000\u3000\u8fd9\u65f6\u8f9b\u6377\u7684\u5fc3\u60c5\u53ef\u8bf4\u662f\u4e00\u751f\u4e2d\u6700\u60b2\u54c0\u7684\u65f6\u5019\u4e86\uff0c\u5728\u5e7c\u65f6\u8f9b\u6377\u591c\u906d\u60e8\u53d8\uff0c\u4f46\u5e74\u7eaa\u7a76\u7adf \u5c1a\u5e7c\uff0c\u53ea\u88ab\u5fc3\u60ca\u5413\u81f3\u5446\uff0c\u54ea\u6709\u6b64\u65f6\u7684\u5982\u6b64\u4f24\u5fc3\u65ad\u80a0\uff01 \u3000\u3000\u8f9b\u6377\u5446\u7acb\u5893\u524d\uff0c\u4ef0\u9996\u671b\u5929\uff0c\u76ee\u5149\u75f4\u5446\uff0c\u8138\u4e0a\u6cea\u75d5\u4f9d\u7a00\u6591\u6591\uff0c\u6b64\u65f6\u4ed6\u4e00\u5207\u8b66\u89c9\u5df2\u6709\u5982 \u5168\u5931\uff0c\u5982\u679c\u6709\u4eba\u9661\u65bd\u6697\u7b97\uff0c\u4ed6\u5fc5\u4e0d\u80fd\u9003\u8fc7\u3002 \u3000\u3000\u4ed6\u5583\u5583\u81ea\u8bed\uff0c\u5fc3\u4e2d\u5ff5\u5934\u4e0d\u65ad\u95ea\u8fc7\uff0c\u5374\u59cb\u7ec8\u60f3\u4e0d\u901a\u662f\u4f55\u4eba\u4e0b\u7684\u6bd2\u624b\uff0c\u66f4\u4e0d\u89e3\u4f55\u4ee5\u4faf\u4e8c \u53d4\u5982\u6b64\u529f\u592b\u7adf\u4e5f\u4f1a\u88ab\u51fb\u6bd9\uff01\u6709\u51e0\u6b21\u4ed6\u90fd\u60f3\u6398\u51fa\u4faf\u4e8c\u53d4\u7684\u5c38\u8eab\u67e5\u770b\u7a76\u7adf\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\uff0c\u4f46 \u5374\u8fdf\u8fdf\u4e0d\u52a8\u3002\u60a0\u5730\uff0c\u4ed6\u51b7\u54fc\u4e00\u58f0\uff0c\u4f38\u624b\u62cd\u5728\u77f3\u7891\u4e0a\uff0c\u4ef0\u9996\u5583\u5583\u8bf4\u9053\uff1a\u201c\u6211\u82e5\u4e0d\u628a\u6740\u4faf\u53d4 \u53d4\u7684\u51f6\u624b\u788e\u5c38\u4e07\u6bb5\uff0c\u8a93\u4e0d\u4e3a\u4eba\uff01\u201d \u3000\u3000\u8a93\u7f62\uff0c\u53cd\u8eab\u4fbf\u5411\u5c71\u4e0b\u8d70\u53bb\u3002 \u3000\u3000\u7a81\u7136\u4ed6\u773c\u89d2\u77a5\u89c1\u7ea6\u5728\u5de6\u65b9\u5341\u4f59\u4e08\u4e00\u4e2a\u6797\u4e2d\u597d\u50cf\u4eba\u5f71\u5728\u52a8\uff0c\u8fd9\u65f6\u4ed6\u6ee1\u6000\u60b2\u6124\uff0c\u5bf9\u6bcf\u4e00 \u4e2a\u4eba\u90fd\u62b1\u6709\u6000\u7591\u4e4b\u5ff5\uff0c\u4e8e\u662f\u51b7\u54fc\u4e00\u58f0\uff0c\u95ea\u8eab\u98d8\u8fc7\u6797\u4e2d\u3002 \u3000\u3000\u4eba\u5f97\u6797\u6765\uff0c\u53ea\u89c1\u524d\u65b9\u7ea6\u4e94\u516d\u4e08\u5f00\u5916\u6709\u4e8c\u4e2a\u6c49\u5b50\u6b63\u5728\u62fc\u6597\uff0c\u8f9b\u6377\u8f7b\u529f\u4f55\u7b49\u9ad8\u660e\uff0c\u8fd9\u4e00 \u8fdb\u6765\uff0c\u4e8c\u4eba\u4e00\u65b9\u9762\u4e5f\u6253\u5f97\u51fa\u795e\uff0c\u7adf\u6ca1\u6709\u88ab\u53d1\u73b0\u3002 \u3000\u3000\u4ed6\u4e8e\u662f\u9690\u8eab\u4e00\u682a\u8001\u6811\u540e\uff0c\u95ea\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u8fce\u9762\u4e00\u4eba\u751f\u5f97\u597d\u4e0d\u9b41\u68a7\uff0c\u6ee1\u9762\u624e\u81c2\uff0c\u6b63\u624b \u6301\u4e00\u67c4\u957f\u5251\u653b\u5411\u5bf9\u65b9\uff0c\u5bf9\u65b9\u90a3\u4eba\u80cc\u5bf9\u7740\u8f9b\u6377\uff0c\u770b\u4e0d\u771f\u5207\u9762\u5bb9\uff0c\u4f46\u89c1\u4ed6\u5de6\u624b\u4ec5\u6301\u7740\u4e00\u6587\u957f \u7ea6\u4e00\u5c3a\u534a\u7684\u6811\u679d\u7247\u548c\u90a3\u5927\u6c49\u640f\u6597\u3002 \u3000\u3000\u90a3\u624b\u6301\u6811\u679d\u7684\u4eba\u4f3c\u4e4e\u5468\u8eab\u8f6c\u52a8\u6709\u4e9b\u4e0d\u4fbf\uff0c\u5c24\u5176\u662f\u53f3\u624b\uff0c\u6709\u82e5\u865a\u8bbe\uff0c\u811a\u6b65\u4e5f\u6709\u4e9b\u513f\u5446 \u6ede\u3002 \u3000\u3000\u53cd\u89c2\u4ed6\u7684\u5251\u6cd5\u5374\u7cbe\u5999\u7edd\u4f26\uff0c\u4e8c\u4eba\u8fc5\u95f4\u4fbf\u5bf9\u62c6\u4e86\u7ea6\u6709\u5eff\u4f59\u62db\uff0c\u4f46\u5374\u672a\u95fb\u5175\u5203\u76f8\u89e6\u8fc7\u4e00 \u6b21\u3002 \u3000\u3000\u4e8c\u4eba\u7f04\u53e3\u82e6\u6597\uff0c\u90a3\u624b\u6301\u77ed\u679d\u7684\u6c49\u5b50\u56e0\u8eab\u624b\u4e0d\u7075\u4fbf\u5403\u4e86\u6781\u5927\u7684\u4e8f\uff0c\u6b64\u65f6\u5df2\u88ab\u903c\u5230\u6797\u8fb9\u3002 \u3000\u3000\u90a3\u866c\u9aef\u5927\u6c49\u84e6\u7684\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5454\uff0c\u770b\u4f60\u518d\u60f3\u9003\u2014\u2014\u201d\u8bf4\u7740\u4e00\u5251\u70b9\u5411\u90a3\u624b\u6301\u77ed\u679d\u4eba\u7684 \u7709\u9645\u3002 \u3000\u3000\u8f9b\u6377\u89c2\u6218\u81f3\u6b64\uff0c\u5c1a\u672a\u95fb\u4e8c\u4eba\u5f00\u8fc7\u53e3\uff0c\u8fd9\u65f6\u542c\u90a3\u5927\u6c49\u72c2\u543c\uff0c\u4e2d\u6c14\u5145\u6c9b\u4e4b\u6781\uff0c\u4e0d\u7531\u6697\u5403 \u4e00\u60ca\uff0c\u518d\u770b\u90a3\u80cc\u5bf9\u7740\u81ea\u5df1\u7684\u4eba\u65f6\uff0c\u53ea\u89c1\u4ed6\u8eab\u5b50\u4e00\u77ee\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u7740\u529b\uff0c\u8eab\u5b50\u7a81\u7136\u4e00\u6ed1\uff0c\u7adf \u81ea\u6446\u8131\u51fa\u90a3\u5927\u6c49\u81f4\u547d\u4e00\u51fb\u3002 \u3000\u3000\u4ed6\u6389\u8fc7\u5934\u6765\uff0c\u51c6\u5907\u518d\u63a5\u90a3\u5927\u6c49\u7684\u653b\u51fb\u3002 \u3000\u3000\u8f9b\u6377\u8fd9\u65f6\u624d\u53ef\u89c1\u6e05\u4ed6\u7684\u9762\u5bb9\uff0c\u53ea\u89c1\u4ed6\u5e74\u7ea6\u5eff\u4e00\u3001\u4e8c\uff0c\u76f8\u8c8c\u82f1\u633a\u4e4b\u6781\uff0c\u4e0d\u89c9\u5bf9\u4ed6\u5fc3\u5b58 \u597d\u611f\uff0c\u5c24\u5176\u5bf9\u4ed6\u8fd9\u79cd\u5e26\u4f24\u594b\u6597\u7684\u575a\u6bc5\u7cbe\u795e\u66f4\u611f\u5fc3\u6298\u3002 \u3000\u3000\u90a3\u9752\u5e74\u9976\u662f\u95ea\u8fc7\u6b64\u62db\uff0c\u4f46\u8138\u4e0a\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u4f5c\u51fa\u4e00\u79cd\u75db\u82e6\u7684\u8868\u60c5\u3002\u8f9b\u6377\u4f55\u7b49\u4eba\u7269\uff0c\u5df2 \u77e5\u4ed6\u662f\u88ab\u70b9\u4e86\u7a74\u9053\uff0c\u534a\u8eab\u5468\u8f6c\u4e0d\u7075\uff0c\u662f\u4ee5\u7528\u5de6\u624b\u6301\u5251\u3002\u5fc3\u4e2d\u66f4\u60ca\u4ed6\u7adf\u80fd\u7528\u5185\u529f\u52c9\u5f3a\u5c01\u4f4f \u7a74\u9053\u4e3a\u65f6\u81f3\u4e45\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u968f\u624b\u6298\u4e0b\u4e00\u6bb5\u67af\u679d\u3002 \u3000\u3000\u5374\u89c1\u90a3\u866c\u9aef\u5927\u6c49\u4ed7\u5251\u56de\u9996\u53c8\u662f\u4e00\u5251\u523a\u6765\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u7a81\u7136\u5de6\u624b\u4e00\u6325\uff0c\u4f46\u89c1\u6f2b\u5929\u679d\u5f71\u4e00\u531d\uff0c\u7adf\u81ea\u5728\u8eab\u524d\u5e03\u51fa\u4e00\u9053\u6811\u7f51\uff0c\u5c24\u5176\u7528\u7684\u662f \u5de6\u624b\u5251\uff0c\u66f4\u663e\u5f97\u53e4\u602a\u4e4b\u6781\u3002 \u3000\u3000\u4ed6\u4f7f\u51fa\u8fd9\u62db\uff0c\u90a3\u5927\u6c49\u4e00\u51fb\u6570\u5251\u90fd\u88ab\u5c01\u56de\uff0c\u5c31\u662f\u8fde\u8f9b\u6377\u4e5f\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\uff0c\u8f9b\u6377\u5f20\u624b\u4e00\u5f39\uff0c\u4e00\u622a\u67af\u679d\u5df2\u95ea\u7535\u822c\u5f39\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u7528\u7684\u624b\u6cd5\uff0c\u52b2\u9053\u5de7\u5999\u4e4b\u6781\uff0c\u53ea\u542c\u5f97\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u51fb\u4e2d\u90a3\u5c11\u5e74\u7684\u53f3\u80c1\u4e0b\u7b2c\u5341\u4e00 \u6839\u7b4b\u9aa8\u2014\u2014\u201c\u7ae0\u95e8\u7a74\u201d\u4e0a\u3002\u90a3\u5c11\u5e74\u9661\u7136\u89c9\u5f97\u8eab\u4e0a\u4e00\u9635\u8f7b\u677e\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u7edd\u6280\u5df2\u7136\u4f7f\u51fa\uff0c \u4f46\u89c1\u6f2b\u5929\u5251\u5f71\u4e2d\uff0c\u4e00\u70b9\u9ed1\u7a81\u7a81\u7684\u6811\u5f71\u98d8\u5ffd\u4e0d\u5b9a\u7684\u51fb\u5411\u90a3\u866c\u9aef\u5927\u6c49\uff0c\u90a3\u5927\u6c49\u6025\u5207\u95f4\u6325\u5251\u5212 \u51fa\u4e00\u9053\u5706\u5f27\uff0c\u54ea\u77e5\u9752\u5e74\u8fd9\u4e00\u5251\u4e43\u662f\u5e73\u751f\u7edd\u6280\uff0c\u53ea\u89c1\u6811\u5c16\u5fae\u5fae\u4e00\u6c89\uff0c\u5fae\u5e26\u4e00\u4e1d\u52b2\u98ce\u7adf\u5728\u68ee \u68ee\u5251\u6c14\u4e2d\u5bfb\u9699\u800c\u5165\uff01 \u3000\u3000\u773c\u770b\u90a3\u5927\u6c49\u4e0d\u514d\u8981\u6321\u4e0d\u4f4f\u6811\u679d\u2014\u2014\u522b\u770b\u8fd9\u4e00\u679d\u6811\u679d\uff0c\u5982\u70b9\u5230\u4e86\u8eab\u4e0a\uff0c\u7167\u6837\u662f\u6d1e\u7a7f\u2014 \u2014\u8f9b\u6377\u5728\u4e00\u65c1\u672c\u4e0d\u6b32\u51fa\u624b\uff0c\u7a81\u7136\u4e00\u4e2a\u5ff5\u5934\u95ea\u8fc7\u8111\u9645\uff0c\u4ed6\u5982\u98de\u822c\u95ea\u51fa\u6797\u4e2d\uff0c\u6d2a\u58f0\u9053\uff1a\u201c\u5144 \u53f0\u8bf7\u4f4f\u624b\u3002\u201d\u8bf4\u7740\u6296\u624b\u5288\u51fa\u4e00\u638c\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5f85\u89c1\u6709\u4eba\u7a7f\u51fa\uff0c\u4e14\u653b\u51fa\u4e00\u638c\uff0c\u4e0d\u6c42\u4f24\u4eba\uff0c\u4f46\u6c42\u81ea\u4fdd\uff0c\u8eab\u5f62\u4e00\u9519\uff0c\u9000\u540e\u5bfb\u4e08\uff01 \u3000\u3000\u8f9b\u63d0\u62f1\u624b\u5bf9\u90a3\u866c\u9aef\u5927\u6c49\u9053\uff1a\u201c\u5144\u53f0\u53ef\u662f\u53f7\u79f0\u4e2d\u5dde\u4e00\u5251\u5b5f\u975e\u7684\uff1f\u201d \u3000\u3000\u90a3\u866c\u9aef\u5927\u6c49\u6b7b\u91cc\u9003\u751f\uff0c\u6014\u6014\u7684\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u4e45\u95fb\u5927\u540d\uff0c\u5982\u96f7\u704c\u8033\u2014\u2014\u201d \u3000\u3000\u90a3\u4e2d\u5dde\u4e00\u5251\u957f\u53f9\u4e00\u58f0\uff0c\u6253\u65ad\u4ed6\u7684\u8bdd\u5934\uff0c\u7b54\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u81ea\u6b64\u2014\u2014\u5509\uff01\u201d \u3000\u3000\u8bf4\u7740\u6296\u624b\u63b7\u51fa\u957f\u5251\uff0c\u5411\u90a3\u9752\u5e74\u63b7\u53bb\uff0c\u8f6c\u8eab\u5982\u98de\u800c\u53bb\u3002\u8fd9\u65f6\u90a3\u957f\u5251\u6b63\u63b7\u5411\u90a3\u5c11\u5e74\uff0c\u90a3 \u5c11\u5e74\u5f85\u5251\u5b50\u8fd1\u4e86\uff0c\u7a81\u7136\u8eab\u5b50\u4e00\u62d4\uff0c\u5934\u4e0b\u811a\u4e0a\uff0c\u4fef\u8eab\u4e00\u63a0\uff0c\u4fbf\u5c06\u957f\u5251\u63a5\u7740\u3002 \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u4e00\u7b11\uff0c\u5f00\u53e3\u8d5e\u9053\uff1a\u201c\u5144\u53f0\u597d\u4fca\u7684\u8f7b\u529f\u2014\u2014\u201d\u63a5\u53e3\u53c8\u9053\uff1a\u201c\u5475\uff0c\u5bf9\u4e86\uff0c\u5144\u53f0 \u53ef\u662f\u59d3\u5434\uff1f\u53e3\u5929\u5434\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5fae\u5fae\u4e00\u60ca\uff0c\u968f\u5373\u7b54\u9053\uff1a\u201c\u5728\u4e0b\u6b63\u662f\u59d3\u5434\uff0c\u5144\u53f0\u600e\u4e48\u5f97\u77e5\uff1f\u201d \u3000\u3000\u8f9b\u6377\u7b54\u9053\uff1a\u201c\u4e0d\u77e5\u5144\u53f0\u53ef\u662f\u5a01\u9707\u4e2d\u539f\u7684\u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\u7684\u540e\u8f88\uff1f\u201d \u3000\u3000\u90a3\u5434\u59d3\u5c11\u5e74\u5927\u5403\u4e00\u60ca\uff0c\u7b54\u9053\uff1a\u201c\u6b63\u662f\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u679c\u7136\u662f\u5434\u5144\uff0c\u5728\u4e0b\u59d3\u8f9b\u540d\u6377\uff0c\u5bb6\u5e08\u6885\u5c71\u6c11\u548c\u5434\u8001\u524d\u8f88\u4ee5\u524d\u8981\u597d\u5f97\u5f88\u54e9\uff01\u201d \u3000\u3000\u90a3\u59d3\u5434\u7684\u5c11\u5e74\u8138\u4e0a\u7a81\u7136\u4e00\u559c\uff0c\u6b23\u7136\u9053\uff1a\u201c\u539f\u6765\u8f9b\u5144\u7adf\u662f\u6885\u53d4\u53d4\u7684\u9ad8\u5f1f\u2014\u2014\u201d\u6562\u60c5\u4ed6 \u4e5f\u53eb\u6885\u5c71\u6c11\u4f5c\u53d4\u53d4\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5e74\u6b63\u662f\u65e9\u5e74\u6b7b\u5728\u4e94\u5927\u5251\u6d3e\u56f4\u653b\u4e4b\u4e0b\u7684\u5434\u8bcf\u4e91\u7684\u513f\u5b50\u5434\u51cc\u98ce\u3002\u4ed6\u81ea\u5bb6\u9022\u60e8\u53d8\uff0c \u88ab\u4e00\u5f02\u4eba\u6536\u7559\uff0c\u6559\u4ed6\u6b66\u827a\uff0c\u4f46\u6240\u6559\u7684\u5374\u5168\u662f\u5434\u6c0f\u7559\u4e0b\u6765\u7684\u201c\u6b66\u529f\u79d8\u7b3c\u201d\uff0c\u662f\u4ee5\u5434\u51cc\u98ce\u7684 \u529f\u592b\u548c\u4e43\u7236\u4ecd\u51fa\u4e00\u8f99\u3002 \u3000\u3000\u6700\u8fd1\u5434\u51cc\u98ce\u51fa\u9053\u884c\u4fa0\uff0c\u98ce\u95fb\u6b66\u6c49\u4e00\u5e26\u4e03\u5999\u795e\u541b\u518d\u5ea6\u51fa\u73b0\uff0c\u6885\u5c71\u6c11\u4e43\u662f\u4ed6\u7236\u81f3\u53cb\uff0c\u4ed6 \u767b\u65f6\u8d76\u6765\u5bdf\u770b\uff0c\u4f46\u5de7\u9022\u4faf\u4e8c\u53d4\u51fa\u4e27\uff0c\u4ed6\u81ea\u5c0f\u4fbf\u548c\u4faf\u4e8c\u53d4\u4ea4\u5f80\u751a\u597d\uff0c\u5f53\u4e0b\u6765\u5893\u524d\u796d\u62dc\uff0c\u6b63 \u4f24\u5fc3\u95f4\uff0c\u4e0d\u9632\u8eab\u540e\u4e00\u4e2a\u866c\u9aef\u6c49\u5b50\uff0c\u4e5f\u5c31\u662f\u4e2d\u5dde\u4e00\u5251\u5b5f\u975e\uff0c\u7a81\u65bd\u6697\u7b97\uff0c\u70b9\u4e86\u4ed6\u53f3\u80a9\u7684\u201c\u80a9 \u80db\u7a74\u201d\u4e14\u62d4\u53bb\u4ed6\u7684\u4f69\u5251\uff0c\u5434\u51cc\u98ce\u906d\u9022\u60e8\u53d8\uff0c\u6b63\u60b2\u54c0\u6b32\u7edd\uff0c\u54ea\u9632\u6709\u4eba\u6697\u7b97\uff1f\u4ed6\u53ea\u6709\u8fd0\u6c14\u95ed \u4f4f\u7a74\u9053\uff0c\u52c9\u5f3a\u6298\u4e00\u6839\u6811\u679d\u548c\u90a3\u5b5f\u975e\u640f\u6597\uff0c\u60f3\u662f\u5b5f\u975e\u81ea\u5df1\u4e5f\u89c9\u5f97\u81ea\u5df1\u884c\u52a8\u592a\u8fc7\u5351\u52a3\uff0c\u4fbf\u5c06 \u4ed6\u903c\u81f3\u6797\u4e2d\u52a8\u624b\uff0c\u4ed6\u5148\u8fd8\u6709\u529b\u62db\u67b6\uff0c\u540e\u6765\u5230\u8f9b\u6377\u4e0a\u5188\uff0c\u90a3\u5b5f\u975e\u60f3\u662f\u4e0d\u613f\u5916\u4eba\u5f97\u77e5\uff0c\u4e8e\u662f \u7f04\u53e3\u9ed8\u6597\uff0c\u800c\u5434\u51cc\u98ce\u4e5f\u662f\u4e00\u53e3\u771f\u6c14\u95ed\u4f4f\u4f24\u7a74\uff0c\u66f4\u4e0d\u80fd\u5f00\u53e3\u51fa\u58f0\uff0c\u4e8e\u662f\u4e8c\u4eba\u9ed8\u9ed8\u82e6\u6597\uff0c\u82e5 \u4e0d\u662f\u8f9b\u6377\u773c\u5feb\uff0c\u5fc5\u4e0d\u4f1a\u53d1\u73b0\u4e8c\u4eba\u3002 \u3000\u3000\u5434\u51cc\u98ce\u771f\u6c14\u8d8a\u6765\u8d8a\u5fae\u5f31\uff0c\u88ab\u90a3\u5b5f\u975e\u903c\u5f97\u53ea\u6709\u62db\u67b6\u4e4b\u529b\uff0c\u7a81\u88ab\u8f9b\u6377\u7528\u6697\u5668\u649e\u5f00\u7a74\u9053\uff0c \u662f\u4ee5\u594b\u529b\u4f7f\u51fa\u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\u7684\u7edd\u62db\u201c\u9b3c\u738b\u628a\u706b\u201d\u3002\u5434\u51cc\u98ce\u529f\u592b\u672c\u8fdc\u5728\u5b5f\u975e\u4e4b\u4e0a\uff0c\u6b64\u65f6 \u542b\u5fff\u51fa\u624b\uff0c\u5b5f\u975e\u4e00\u65f6\u62db\u67b6\u4e0d\u4f4f\uff0c\u5012\u662f\u8f9b\u6377\u51fa\u624b\u89e3\u4e86\u5371\u3002\u5b5f\u975e\u672c\u4e8e\u5fc3\u6709\u865a\uff0c\u6b64\u65f6\u89c1\u53e6\u6709\u4eba \u53c2\u4e0e\u6b64\u4e8b\uff0c\u4e0d\u597d\u518d\u505c\u7559\u7247\u523b\uff0c\u662f\u4ee5\u6389\u5934\u63d0\u524d\u8d70\u3002 \u3000\u3000\u5434\u51cc\u98ce\u8349\u8349\u8bf4\u5b8c\u81ea\u5df1\u7684\u906d\u9047\uff0c\u8f9b\u6377\u542c\u4e86\u5fae\u5fae\u70b9\u5934\uff0c\u5f00\u53e3\u8bf4\u9053\uff1a\u201c\u8fd9\u5b5f\u975e\u4e43\u662f\u5929\u4e0b\u4e94 \u5927\u5b97\u6d3e\u4e2d\u5ce8\u5a9a\u6d3e\u5c71\u4e0b\uff0c\u60f3\u5f53\u5e74\u4e94\u5927\u5b97\u6d3e\u8c0b\u5bb3\u4ee4\u5c0a\u4e4b\u4e8b\uff0c\u5fc5\u4e5f\u544a\u77e5\u4ed6\u4eec\u7684\u540e\u8f88\u4e86\u3002\u8fd9\u5b5f\u975e \u5927\u6982\u662f\u8def\u89c1\u4f60\u8eab\u540e\u7684\u4f69\u5251\u800c\u7a81\u4e0b\u6bd2\u624b\u2014\u2014\u201d \u3000\u3000\u5434\u51cc\u98ce\u542c\u5230\u8fd9\u91cc\uff0c\u65e9\u5df2\u6cea\u5982\u96e8\u4e0b\uff0c\u6068\u58f0\u9053\uff1a\u201c\u521a\u624d\u5b9e\u5728\u4e0d\u5e94\u653e\u90a3\u5c0f\u5b50\u79bb\u53bb\uff0c\u53ea\u602a\u5c0f \u5f1f\u4e0d\u77e5\u4ed6\u662f\u5ce8\u5d4b\u6d3e\u7684\uff0c\u5426\u5219\u5fc5\u8ba9\u4ed6\u788e\u5c38\u4e07\u6bb5\u3002\u201d \u3000\u3000\u8f9b\u6377\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5f1f\u4e0d\u8fc7\u662f\u8ba9\u4ed6\u9003\u53bb\uff0c\u501f\u4ed6\u53e3\u544a\u77e5\u5929\u4e0b\u6b66\u6797\uff0c\u5355\u5251\u65ad\u9b42\u548c \u4e03\u5999\u795e\u541b\u7684\u540e\u8f88\u8981\u4ed6\u4eec\u507f\u8fd8\u5341\u5e74\u524d\u7684\u8840\u503a\uff01\u201d \u3000\u3000\u4e24\u4eba\u8bb2\u4e86\u51e0\u53e5\uff0c\u5f7c\u6b64\u90fd\u5fc3\u6298\u5bf9\u65b9\u7684\u98ce\u5ea6\u3001\u6b66\u827a\uff0c\u7acb\u6210\u83ab\u9006\uff0c\u5341\u5206\u6295\u673a\u3002 \u3000\u3000\u5434\u51cc\u98ce\u7b11\u9053\uff1a\u201c\u5475\uff01\u5bf9\u4e86\uff0c\u521a\u624d\u7528\u67af\u679d\u649e\u5f00\u5c0f\u5f1f\u7a74\u9053\u7684\u5fc5\u662f\u8f9b\u5144\u5427\u201d\u8f9b\u6377\u5fae\u5fae\u70b9\u5934\uff0c \u963b\u4f4f\u5434\u51cc\u98ce\u62dc\u8c22\u4e4b\u793c\uff0c\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5c0f\u5f1f\u4eca\u5e74\u4e8c\u5341\u5c81\uff0c\u4e0d\u77e5\u5434\u5144\u2014\u2014\u201d \u3000\u3000\u5434\u51cc\u98ce\u7b54\u9053\uff1a\u201c\u5c0f\u5f1f\u5eff\u6709\u4e00\uff0c\u5982\u4e0d\u5acc\u5f03\uff0c\u79f0\u4f60\u4e00\u58f0\u8d24\u5f1f\u597d\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u672c\u6709\u6b64\u610f\uff0c\u6b22\u58f0\u7b54\u5e94\uff0c\u767b\u65f6\u4e8c\u4eba\u611f\u60c5\u53c8\u52a0\u6df1\u4e00\u6b65\u3002\u5434\u51cc\u98ce\u7a81\u7136\u60f3\u8d77\u4ec0\u4e48\uff0c\u5f00\u53e3 \u9053\uff1a\u201c\u8d24\u5f1f\uff0c\u6c5f\u6e56\u4e0a\u76db\u4f20\u6885\u53d4\u53d4\u51fa\u73b0\u6b66\u6c49\u4e00\u5e26\uff0c\u6b64\u4e8b\u662f\u771f\u662f\u5047\uff0c\u6885\u53d4\u53d4\u597d\u5417\uff1f\u5feb\u5e26\u6211\u53bb \u62dc\u89c1\uff01\u201d \u3000\u3000\u8f9b\u6377\u9eef\u7136\u7b54\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd9\u5c31\u544a\u8bc9\u5927\u54e5\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u5c06\u4e03\u5999\u795e\u541b\u5728\u4e94\u534e\u5c71\u4e0a\u53d7\u4f24\u7684\u7ecf\u8fc7\u4e00\u4e00\u8bf4\u51fa\uff0c\u4e14\u8fde\u81ea\u5df2\u7684\u4efb\u52a1\u4e5f\u8bf4\u4e86\u4e00\u904d\uff0c\u5434 \u51cc\u98ce\u542c\u6885\u53d4\u53d4\u7adf\u4e3a\u81ea\u5df1\u7236\u4eb2\u800c\u53d7\u521b\u6b8b\u5e9f\uff0c\u5fc3\u4e2d\u66f4\u662f\u4e00\u9635\u96be\u8fc7\uff0c\u4e8c\u4eba\u76f8\u5bf9\u6068\u58f0\u53d1\u8a93\u5b9a\u8981\u4e3a \u6885\u3001\u5434\u4e8c\u4eba\u590d\u4ec7\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u540e\u6765\u679c\u7136\u4f7f\u5f97\u6c5f\u6e56\u4e0a\u906d\u4e34\u4e00\u6b21\u6d69\u52ab\uff0c\u6b64\u662f\u540e\u8bdd\u4e0d\u63d0\u3002 \u3000\u3000\u4e8c\u4eba\u518d\u8c08\u4e86\u4e00\u4f1a\uff0c\u4e00\u540c\u8d70\u4e0b\u5c71\u53bb\uff0c\u4e34\u884c\u65f6\u4e00\u8d77\u53c8\u5bf9\u4faf\u4e8c\u53d4\u7684\u5893\u7891\u54ed\u62dc\u4e00\u756a\u3002 \u3000\u3000\u4e8c\u4eba\u5546\u91cf\u4e4b\u4e0b\uff0c\u89c9\u5f97\u76ee\u524d\u9996\u5e94\u5bdf\u51fa\u6740\u4faf\u4e8c\u53d4\u7684\u51f6\u624b\u662f\u8c01\uff1f\u5434\u51cc\u98ce\u731c\u6d4b\u5fc5\u662f\u4e94\u5927\u5b97\u6d3e \u6240\u5e72\uff0c\u4ee5\u4fbf\u5f15\u51fa\u6885\u5c71\u6c11\u540e\u4ee3\u54ed\u796d\uff0c\u662f\u4ee5\u6d3e\u5b5f\u975e\u5728\u5893\u65c1\u7b49\u4faf\u65bd\u4ee5\u6697\u7b97\u3002\u8f9b\u6377\u5219\u77e5\u81ea\u5df1\u884c\u85cf \u5e76\u6ca1\u6709\u88ab\u6b66\u6797\u4eba\u7269\u63a2\u77e5\uff0c\u77e5\u4faf\u4e8c\u53d4\u5fc5\u4e0d\u4f1a\u662f\u4e94\u5927\u5b97\u6d3e\u95e8\u4eba\u6240\u6740\uff0c\u51b5\u4e14\u4ee5\u4faf\u4e8c\u53d4\u7684\u52a8\u529b\uff0c \u5c31\u662f\u4e94\u5927\u5b97\u6d3e\u4efb\u4e00\u638c\u95e8\u4eba\u4eb2\u81ea\u6765\u4e34\uff0c\u4e5f\u672a\u5fc5\u80fd\u591f\u5c06\u4e4b\u51fb\u6bd9\u3002 \u3000\u3000\u4e8c\u4eba\u8fb9\u8d70\u8fb9\u8c08\uff0c\u4e00\u65f6\u4fbf\u6765\u5230\u4e86\u5c71\u6885\u73e0\u5b9d\u5e97\u524d\u3002 \u3000\u3000\u5f20\u638c\u67dc\u65e9\u5df2\u8fce\u81f3\u5e97\u5916\uff0c\u89c1\u8f9b\u6377\u4f34\u7740\u53e6\u4e00\u4e2a\u82f1\u4fca\u7684\u5c11\u5e74\uff0c\u4e14\u80cc\u4e0a\u4e00\u67c4\u957f\u5251\uff0c\u4ee5\u4e3a\u53c8\u662f \u4e9b\u6b66\u6797\u4eba\u7269\uff0c\u5fd9\u9053\uff1a\u201c\u8f9b\u8001\u677f\u56de\u6765\u4e86\u3002\u201d\u4ed6\u7edd\u53e3\u4e0d\u63d0\u4faf\u4e8c\u53d4\u7684\u4e8b\uff0c\u4e43\u662f\u6015\u8f9b\u6377\u518d\u5ea6\u4f24\u5fc3\u3002 \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u6446\u4e86\u6446\u624b\uff0c\u4fbf\u62db\u547c\u4f19\u8ba1\u5b89\u987f\u5434\u51cc\u98ce\u4f4f\u5904\u3002\u4e00\u8fb9\u95ee\u5f20\u638c\u67dc\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u6765\uff0c \u6c5f\u6c49\u4e00\u5e26\u6709\u65e0\u4ec0\u4e48\u91cd\u5927\u7684\u6d88\u606f\uff1f\u201d \u3000\u3000\u5f20\u638c\u67dc\u6025\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u6709\uff0c\u591a\u5f97\u5f88\u54e9\uff0c\u5c0f\u7684\u521a\u624d\u4e00\u65f6\u5fc3\u6025\u8fd8\u4e0d\u66fe\u8bf4\u3002\u201d\u987f\u4e86\u4e00\u4e0b \u53c8\u9053\uff0c\u201c\u636e\u8bf4\u662f\u4ec0\u4e48\u4e03\u5999\u795e\u541b\u518d\u73b0\u6c5f\u6e56\u5f15\u8d77\u8bb8\u591a\u4eba\u7269\u6ce8\u610f\uff0c\u6700\u8f70\u52a8\u7684\u8fd8\u662f\u4e09\u5929\u4ee5\u524d\uff0c\u94f6 \u67aa\u5b5f\u4f2f\u8d77\u8001\u7237\u5b50\u7684\u9556\u5e97\u88ab\u4eba\u6380\u5566\uff0c\u5b5f\u8001\u7237\u5b50\u5f53\u573a\u8eab\u6b7b\uff0c\u800c\u51f6\u624b\u5728\u4e34\u8d70\u4ee5\u524d\u5374\u7559\u8a00\u8bb2\u662f \u2018\u6d77\u5929\u53cc\u715e\u2019\u6240\u5e72\uff0c\u5f53\u4e0b\u5168\u57ce\u9707\u60ca\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u542c\u5230\u8fd9\u91cc\u5df2\u662f\u795e\u8272\u5927\u53d8\uff0c\u5f00\u53e3\u9053\uff1a\u201c\u597d\uff01\u96be\u9053\u8fd9\u4e24\u4e2a\u9b54\u5934\u7adf\u5343\u91cc\u8fe2\u8fe2\u5165\u5173\u4e86\uff0c \u60f3\u4e1c\u5c71\u518d\u8d77\u5417\uff1f\u201d \u3000\u3000\u5f20\u638c\u67dc\u63a5\u53e3\u9053\uff1a\u201c\u8fd9\u4e2a\u5c0f\u7684\u4e0d\u61c2\uff0c\u5012\u662f\u6c5f\u6c49\u4e00\u5e26\u7684\u6b66\u5e08\u90fd\u8c08\u864e\u8272\u53d8\uff0c\u4e00\u4e9b\u4e94\u5927\u5b97\u6d3e \u7684\u4eba\u7269\u4e5f\u6709\u7684\u5664\u4e0d\u6562\u8a00\uff0c\u4e5f\u6709\u7684\u51fa\u8a00\u8981\u6559\u8bad\u8fd9\u4e8c\u4e2a\u8d25\u7c7b\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u5fc3\u4e2d\u5927\u4e71\uff0c\u5fae\u5fae\u6446\u624b\u9053\uff1a\u201c\u77e5\u9053\u4e86\uff0c\u8fd9\u6837\u6c5f\u6e56\u4e0a\u6709\u5f97\u5927\u4e71\u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\u4fbf\u5631\u4eba\u53eb\u5434\u51cc\u98ce\u51fa\u6765\u4e00\u540c\u7528\u665a\u9910\uff0c\u5e76\u544a\u8bc9\u4ed6\u6b64\u4e00\u6d88\u606f\u3002 \u3000\u3000\u5434\u51cc\u98ce\u60f3\u662f\u4e45\u5c45\u6df1\u5c71\uff0c\u5e76\u4e0d\u77e5\u201c\u6d77\u5929\u53cc\u715e\u201d\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u4e5f\u4e0d\u5341\u5206\u6ce8\u610f\uff0c\u8f9b\u6377\u4e0d\u518d \u591a\u8a00\uff0c\u5fc3\u4e2d\u5374\u60f3\u5b9a\u4e86\u53e6\u4e00\u4e2a\u8ba1\u8c0b\u3002 \u3000\u3000\u6b21\u65e5\u6e05\u6668\uff0c\u8f9b\u5434\u4e8c\u4eba\u8d77\u8eab\u540e\uff0c\u8f9b\u6377\u5efa\u8bae\u9053\uff1a\u201c\u5927\u54e5\u6700\u597d\u662f\u626e\u4f5c\u4e00\u4e2a\u6587\u4eba\uff0c\u8fd9\u6837\u4e5f\u597d \u884c\u52a8\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u9887\u89c9\u6709\u7406\uff0c\u4e8e\u662f\u6539\u6362\u88c5\u675f\uff0c\u85cf\u8d77\u60f9\u76ee\u7684\u201c\u65ad\u9b42\u5251\u201d\uff0c\u548c\u8f9b\u6377\u4e00\u540c\u51fa\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u8fde\u6708\u4f59\u79bb\u5f00\u6c5f\u6c49\uff0c\u4e00\u4e9b\u76f8\u719f\u670b\u53cb\u90fd\u4e0d\u514d\u8d77\u7591\uff0c\u662f\u4ee5\u51b3\u5b9a\u53bb\u62dc\u8bbf\u4e00\u4e0b\uff0c\u968f\u4fbf\u7f16 \u4e00\u4e2a\u7406\u7531\u53bb\u5706\u8c0e\u3002 \u3000\u3000\u8d70\u5230\u57ce\u4e1c\uff0c\u4f46\u89c1\u6210\u540d\u6700\u4e45\u7684\u201c\u4fe1\u9633\u9556\u5c40\u201d\u5df2\u662f\u4e00\u7247\u51c4\u51c9\uff0c\u5927\u6982\u662f\u51fa\u4e27\u4e0d\u4e45\uff0c\u95e8\u524d\u4ecd \u6302\u7740\u4e00\u4e9b\u513f\u767d\u5e03\u767d\u706f\uff0c\u66f4\u89c9\u51c4\u82cd\u3002 \u3000\u3000\u8f6c\u8fc7\u9053\u513f\uff0c\u6253\u6a2a\u91cc\u9884\u5907\u5230\u201c\u6b66\u5a01\u9556\u5c40\u201d\u53bb\u62dc\u8bbf\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\u3002\u6765\u5230\u95e8\u524d\uff0c\u4f46\u89c1\u9576 \u5c40\u5185\u5fd9\u5fd9\u788c\u788c\uff0c\u8d70\u4eba\u5c40\u4e2d\uff0c\u95ee\u4e00\u4e2a\u4f19\u8ba1\u9053\uff1a\u201c\u8303\u9556\u5934\u53ef\u5728\u4e48\uff1f\u201d \u3000\u3000\u90a3\u4f19\u8ba1\u70b9\u4e86\u70b9\u5934\uff0c\u968f\u624b\u4e00\u6307\uff0c\u8f9b\u6377\u3001\u5434\u51cc\u98ce\u4e8c\u4eba\u968f\u7740\u4ed6\u6240\u6307\u7684\u5730\u65b9\u4e00\u770b\uff0c\u679c\u7136\u8303\u6cbb \u6210\u6b63\u548c\u4e8c\u4e2a\u5e74\u7ea6\u56db\u5341\u5de6\u53f3\u7684\u4eba\u7269\u7ad9\u5728\u4e00\u8d77\uff0c\u8fd9\u65f6\u8303\u6cbb\u6210\u4e5f\u5df2\u770b\u89c1\u8f9b\u3001\u5434\u4e8c\u4eba\uff0c\u5fae\u5fae\u70b9\u4e86 \u70b9\u5934\uff0c\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u6ee1\u8138\u75b2\u5026\uff0c\u5634\u89d2\u4e0a\u867d\u5e26\u7740\u7b11\u5bb9\uff0c\u4f46\u795e\u8272\u5374\u663e\u7136\u5145\u6ee1\u7740\u5fe7\u8651\uff0c\u8f9b\u6377\u5fc3\u4e2d\u4e86\u7136\uff0c \u5374\u6545\u4f5c\u4e0d\u89e3\u95ee\u9053\uff1a\u201c\u8303\u5144\u597d\u4e45\u4e0d\u89c1\uff0c\u5c0f\u5f1f\u6628\u665a\u624d\u4ece\u56db\u5ddd\u56de\u6765\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u6545\u610f\u987f\u4e86\u4e00\u987f\uff0c\u770b\u90a3\u8303\u6cbb\u6210\u4f3c\u795e\u4e0d\u5b88\u820d\uff0c\u5fc3\u4e2d\u6697\u7b11\uff0c\u6539 \u3000\u3000\u53e3\u9053\uff1a\u201c\u771f\u662f\u5929\u5927\u4e0d\u5e78\uff0c\u5b5f\u5144\u7adf\u8ffd\u5978\u4eba\u6740\u5bb3\u800c\u53bb\u4e16\uff0c\u5c0f\u53ef\u4e0d\u66fe\u53c2\u52a0\u846c\u793c\uff0c\u5fc3\u4e2d\u597d\u751f \u8fc7\u610f\u4e0d\u8fc7\u3002\u201d \u3000\u3000\u8303\u6cbb\u6210\u5fae\u5fae\u4e00\u53f9\u9053\uff1a\u201c\u90a3\u6d77\u5929\u53cc\u715e\u4e5f\u6041\u5730\u592a\u72e0\uff0c\u4ed6\u4eec\u60f3\u518d\u626c\u540d\uff0c\u7adf\u627e\u4e0a\u54b1\u4eec\u8fd9\u4e24\u5bb6 \u9556\u5c40\uff0c\u60f3\u80fd\u6740\u4e00\u4ee5\u5106\u767e\uff0c\u5509\uff0c\u8bf4\u4e0d\u5f97\uff0c\u4eca\u660e\u4e8c\u665a\u611a\u5144\u6027\u547d\u4e0d\u4fdd\u5566\uff01\u201d \u3000\u3000\u8f9b\u6377\u6545\u610f\u8be7\u58f0\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u6d77\u5929\u53cc\u715e\u7adf\u8fd8\u8981\u65bd\u6697\u7b97\u4e8e\u8303\u5144\uff1f\u201d \u3000\u3000\u8303\u6cbb\u6210\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u4f38\u624b\u4eba\u6000\uff0c\u6478\u7d22\u4e00\u9635\uff0c\u6478\u51fa\u4e00\u5f20\u767d\u8272\u7684\u5e16\u5b50\u5bf9\u8f9b\u6377\u8bf4\u9053\uff1a \u201c\u5929\u6b8b\u5730\u7f3a\u7684\u8ffd\u9b42\u4ee4\u5df2\u9001\u5230\uff0c\u8fd9\u4e8c\u4e2a\u9b54\u661f\u4e0d\u51fa\u5341\u4e8c\u4e2a\u65f6\u8fb0\u5fc5\u7136\u8d76\u5230\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u5c06\u8d34\u513f\u9012\u7ed9\u8f9b\u6377\u3002\u8f9b\u6377\u4e00\u770b\uff0c\u53ea\u89c1\u8d34\u4e0a\u753b\u7740\u4e00\u53ea\u4ee4\u7bad\uff0c\u4e0b\u7aef\u7f72\u540d\u5904\u5374\u753b\u7740\u4e00\u5bf9 \u8001\u53df\uff0c\u4e8c\u4e2a\u90fd\u662f\u6b8b\u5e9f\u4e0d\u5168\u7684\uff0c\u4e0d\u7528\u8bf4\u5b9a\u662f\u201c\u6d77\u5929\u53cc\u715e\u201d\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u770b\u4e86\u5fc3\u4e2d\u4e00\u9635\u6fc0\u52a8\uff0c\u795e\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u53e3\u4e2d\u5374\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6240\u8c13\u8ffd\u9b42\u4ee4\uff1f\u201d \u3000\u3000\u8303\u6cbb\u6210\u70b9\u4e86\u70b9\u5934\uff0c\u7b54\u9053\uff1a\u201c\u8fd9\u8ffd\u9b42\u4ee4\u65e2\u5230\uff0c\u611a\u5144\u7279\u5730\u8bf7\u4e86\u4e8c\u4f4d\u9ad8\u624b\u6765\uff0c\u60f3\u8bf7\u4ed6\u4eec\u52a9 \u62f3\uff0c\u4ed6\u4eec\u5012\u662f\u723d\u5feb\u7684\u5f88\uff0c\u7acb\u523b\u7b54\u5e94\u4e0b\u6765\u4e86\u3002\u8f9b\u8001\u677f\uff0c\u6765\uff0c\u6211\u66ff\u4f60\u4eec\u5f15\u89c1\u4e00\u4e0b\u3002\u201d \u3000\u3000\u8bf4\u7740\u6307\u7740\u90a3\u8eab\u6750\u7565\u9ad8\u7684\u4e2d\u5e74\u6c49\u5b50\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u70b9\u82cd\u9ad8\u624b\u5353\u4e4b\u4ef2\u82f1\u96c4\uff0c\u8fd9\u4f4d\u662f\u65b0\u8fd1\u6210 \u540d\u7684\u751f\u6b7b\u5224\u9646\u884c\u7a7a\u3002\u201d\u8bf4\u7740\uff0c\u53c8\u5c06\u8f9b\u6377\u4ecb\u7ecd\u4e00\u4e0b\uff0c\u5012\u662f\u8f9b\u6377\u5148\u5c06\u5434\u51cc\u98ce\u4ecb\u7ecd\u5927\u5bb6\u3002 \u3000\u3000\u5bd2\u55a7\u4e00\u9635\uff0c\u8f9b\u6377\u518d\u80e1\u8bcc\u4e00\u756a\uff0c\u4fbf\u548c\u5434\u51cc\u98ce\u79bb\u53bb\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u8f9b\u6377\u5bf9\u5434\u51cc\u98ce\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u73b0\u5728\u624d\u77e5\u9053\u2018\u6d77\u5929\u53cc\u715e\u2019\u4e0d\u662f\u597d\u60f9\u7684\u4eba\u7269\u5427\uff0c \u5c0f\u5f1f\u5012\u6709\u4e00\u4e2a\u8ba1\u8f83\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u4fbf\u5c06\u8ba1\u8c0b\u8bf4\u4e86\u51fa\u6765\uff0c\u5434\u51cc\u98ce\u8fde\u58f0\u8d5e\u9053\uff1a\u201c\u5999\u8ba1\uff01\u201d\u4e8e\u662f\u4e8c\u4eba\u6cbf\u8857\u968f\u610f\u901b\u4e86\u4e00\u56de\uff0c \u4fbf\u56de\u5230\u201c\u5c71\u6885\u201d\u3002 \u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u4e8c\u4eba\u6311\u706f\u95f2\u8c08\u4e00\u56de\uff0c\u9f50\u4eba\u623f\u51c6\u5907\u3002 \u3000\u3000\u65f6\u4eba\u6df1\u591c\uff0c\u5c71\u6885\u73e0\u5b9d\u5e97\u4e2d\u7a81\u7136\u54cd\u4e86\u4e00\u58f0\u62cd\u638c\u58f0\uff0c\u60a0\u5730\u4e8c\u6761\u4eba\u5f71\u5982\u72f8\u732b\u822c\u7a7f\u4e0a\u623f\u5c4b\uff0c \u4e24\u4eba\u7565\u4e00\u5f20\u671b\uff0c\u4fbf\u4f1a\u5408\u5728\u4e00\u8d77\u3001\u8fd9\u65f6\u5929\u4e0a\u6708\u4eae\u867d\u6e3a\uff0c\u82cd\u7a79\u5374\u660e\uff0c\u501f\u7740\u661f\u5149\u4e00\u770b\uff0c\u53ea\u89c1\u4e8c \u4eba\u8138\u4e0a\u5747\u5305\u4ee5\u5e03\u5dfe\uff0c\u53ea\u9732\u51fa\u4e8c\u53ea\u773c\u6674\u3002 \u3000\u3000\u60a0\u5730\u4e8c\u4eba\u8eab\u5f62\u4e00\u52a8\uff0c\u4e00\u9f50\u98d8\u843d\u5728\u9ed1\u6697\u4e4b\u4e2d\u3002 \u3000\u3000\u65f6\u5df2\u6df1\u591c\uff0c\u6c49\u53e3\u5168\u57ce\u706f\u5149\u5168\u9ed1\uff0c\u53ea\u6709\u4e1c\u8857\u4e0a\u201c\u6b66\u5a01\u9556\u5c40\u201d\u4e2d\u706f\u5149\u8f89\u714c\uff0c\u5728\u9ed1\u591c\u4e2d\u76ca \u53d1\u663e\u5f97\u5149\u660e\u3002 \u3000\u3000\u84e6\u5730\uff0c\u201c\u6b66\u5a01\u9556\u5c40\u201d\u623f\u4e0a\u4e00\u9635\u602a\u5578\uff0c\u4e00\u4e2a\u5947\u5f02\u6781\u7684\u58f0\u97f3\u559d\u9053\uff1a\u201c\u8303\u6cbb\u6210\u2014\u2014\u201d \u3000\u3000\u8bed\u97f3\u65b9\u843d\uff0c\u60a0\u89c1\u897f\u8fb9\u623f\u4e0a\u4e00\u9635\u54cd\uff0c\u4e00\u6761\u4eba\u5f71\u51b2\u5929\u800c\u8d77\uff0c\u76f4\u4e0a\u5347\u81f3\u4e09\u3001\u56db\u4e08\u52b2\u9053\u624d\u5931\uff0c \u5728\u7a7a\u4e2d\u5fae\u5fae\u4e00\u505c\uff0c\u6ef4\u6e9c\u6e9c\u4e00\u8f6c\uff0c\u659c\u63a0\u800c\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u9732\u5f97\u9ad8\u660e\u4e4b\u81f3\uff0c\u65e0\u8bba\u662f\u8eab\u6cd5\u3001\u59ff\u6001\uff0c\u5747\u66fc\u5999\u5df2\u6781\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u90a3\u4eba\u5f71 \u513f\u5df2\u843d\u5728\u5c4b\u9762\u4e0a\u3002 \u3000\u3000\u90a3\u4eba\u624d\u5230\u5c4b\u4e0a\uff0c\u4fbf\u5411\u5de6\u65b9\u559d\u9053\uff1a\u201c\u7126\u5bb6\u5144\u5f1f\uff0c\u5927\u540d\u9f0e\u9f0e\uff0c\u7adf\u662f\u89c1\u4e0d\u5f97\u4eba\u7684\u4e1c\u897f\u4e48\uff1f\u201d \u3000\u3000\u8bdd\u97f3\u65b9\u843d\uff0c\u5de6\u9762\u4e00\u9635\u602a\u7b11\uff0c\u201c\u5237\u201d\u7684\u7eb5\u51fa\u4e8c\u4eba\u3002 \u3000\u3000\u5f53\u5148\u4e00\u4eba\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u5c31\u662f\u8303\u6cbb\u6210\u8bf7\u6765\u7684\u9ad8\u624b\u4e48\uff1f\u201d\u58f0\u97f3\u602a\u5f02\u4e4b\u6781\uff0c\u4e14\u5939\u5e26 \u7740\u91d1\u5c5e\u94ff\u9535\u4e4b\u58f0\uff0c\u523a\u8033\u5df2\u6781\uff0c\u4e14\u4e8c\u4eba\u4f3c\u662f\u6709\u610f\u5356\u5f04\uff0c\u4e2d\u6c14\u5145\u6c9b\uff0c\u5b9b\u5982\u5e73\u5730\u7126\u96f7\u3002 \u3000\u3000\u54ea\u77e5\u5bf9\u9762\u90a3\u4eba\u4e0d\u7406\u4e0d\u776c\uff0c\u4ec5\u51b7\u51b7\u7b54\u9053\uff1a\u201c\u51ed\u91d1\u5f13\u795e\u5f39\u5c31\u80fd\u8bf7\u5f97\u52a8\u6211\uff1f\u201d \u3000\u3000\u90a3\u4eba\u518d\u5ea6\u602a\u58f0\u8bf4\u9053\uff1a\u201c\u5c0f\u5b50\u65e2\u975e\u8303\u8001\u513f\u5e2e\u624b\uff0c\u8fd8\u4e0d\u901f\u9000\uff0c\u5f85\u6211\u4eec\u5144\u5f1f\u5904\u7f6e\u4ed6\u4ee5\u540e\u2014 \u2014\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u90a3\u5bf9\u9762\u7684\u4eba\u5374\u6c89\u58f0\u559d\u9053\uff1a\u201c\u5e9f\u8bdd\u5c11\u8bf4\uff01\u201d \u3000\u3000\u90a3\u4e8c\u4eba\u4f3c\u4e4e\u6014\u4e86\u4e00\u4e0b\uff0c\u84e6\u7684\u4e3a\u9996\u4e00\u4eba\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u770b\u4e0d\u51fa\u6765\uff01\u54c8\u2014\u2014-\u201d \u3000\u3000\u7b11\u58f0\u6709\u5982\u9b3c\u53eb\uff0c\u66f4\u662f\u523a\u8033\u5df2\u6781\uff01\u6562\u60c5\u4ed6\u52a8\u4e86\u6012\uff0c\u60f3\u7528\u201c\u6444\u9b42\u9b3c\u97f3\u201d\u6765\u4f24\u5012\u5bf9\u624b\u3002 \u3000\u3000\u7b11\u97f3\u8d8a\u6765\u8d8a\u9ad8\uff0c\u5bf9\u9762\u90a3\u4eba\u8eab\u5b50\u5fae\u5fae\u4e00\u52a8\uff0c\u663e\u7136\u662f\u5fcd\u53d7\u4e0d\u4f4f\uff01\u84e6\u5730\u9ed1\u6697\u4e2d\u53c8\u6709\u4eba\u65ad\u559d \u4e00\u58f0\u9053\uff1a\u201c\u4f4f\u53e3\uff01\u201d \u3000\u3000\u867d\u53ea\u4ec5\u6709\u4e8c\u5b57\uff0c\u51fa\u53e3\u4e4b\u540e\uff0c\u5374\u6e05\u6670\u5df2\u6781\uff0c\u6709\u82e5\u8001\u9f99\u6e05\u541f\uff0c\u5e73\u548c\u4e4b\u6781\uff0c\u90a3\u53d1\u7b11\u7684\u602a\u4eba \u5fae\u5fae\u4e00\u6014\uff0c\u505c\u4e0b\u53e3\u6765\u3002 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c \u516d \u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c \u516d \u7ae0 \u3000\u3000\u90a3\u4ee5\u201c\u6444\u9b42\u9b3c\u97f3\u201d\u72c2\u7b11\u7684\u602a\u4eba\u4f4f\u4e86\u7b11\u58f0\uff0c\u5f80\u53d1\u58f0\u5904\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\u5237\u5730\u51b2\u51fa\u6765\uff0c \u90a3\u4efd\u8f7b\u7075\u6d12\u8131\uff0c\u4ee4\u4eba\u751f\u51fa\u51fa\u5c18\u7684\u611f\u89c9\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u53ea\u89c1\u7eb5\u51fa\u4e4b\u4eba\u4ea6\u662f\u4ee5\u9ed1\u5e03\u8499\u9762\uff0c\u624b\u4e2d\u6301\u7740\u4e00\u53ea\u957f\u5251\uff0c\u8eab\u6750\u4e2d\u7b49\uff0c\u5fae\u5fae\u663e\u5f97 \u7626\u524a\u3002 \u3000\u3000\u90a3\u602a\u4eba\u5c16\u9510\u7684\u58f0\u97f3\u53c8\u626c\u8d77\uff1a\u201c\u8303\u8001\u513f\u7adf\u6536\u7f57\u4e86\u8fd9\u8bb8\u591a\u9ad8\u4eba\uff0c\u54c8\u54c8\uff0c\u4eca\u591c\u8981\u4f60\u4eec\u77e5\u9053 \u2018\u6d77\u5929\u53cc\u715e\u2019\u7684\u624b\u6bb5\u3002\u201d \u3000\u3000\u987b\u77e5\u201c\u6d77\u5929\u53cc\u715e\u201d\u4e2d\u201c\u5929\u5e9f\u201d"\u7126\u52b3\u4e43\u662f\u54d1\u5b50\uff0c\u662f\u4ee5\u4e00\u76f4\u662f\u201c\u5929\u6b8b\u201d\u7126\u5316\u53d1\u8a00\u3002 \u3000\u3000\u7126\u5316\u58f0\u97f3\u624d\u5b8c\uff0c\u90a3\u539f\u5148\u4f2b\u7acb\u623f\u4e0a\u7684\u8499\u9762\u4eba\u4e5f\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u4eca\u591c\u54b1\u4eec\u6b63\u8981\u9886\u6559\u4e00\u4e0b\u4f60 \u4eec\u8fd9\u5bf9\u6b8b\u5e9f\u7a76\u7adf\u6709\u4ec0\u4e48\u51fa\u7c7b\u62d4\u8403\u7684\u529f\u592b\uff01\u201d \u3000\u3000\u201c\u5929\u5e9f\u201d\u7126\u52b3\u53e3\u4e2d\u4e0d\u77e5\u53d1\u51fa\u4e00\u58f0\u4ec0\u4e48\u97f3\uff0c\u8eab\u5b50\u4e00\u7eb5\uff0c\u4ece\u8ddd\u79bb\u4e0d\u4e0b\u4e94\u4e08\u7684\u5730\u65b9\u4e00\u4e0b\u5b50 \u7eb5\u5230\u8499\u9762\u4eba\u773c\u524d\uff0c\u5355\u638c\u631f\u7740\u4e00\u80a1\u6392\u5c71\u5012\u6d77\u822c\u7684\u52b2\u98ce\u6253\u5230\u3002 \u3000\u3000\u90a3\u8499\u9762\u4eba\u89c1\u4e86\u5929\u5e9f\u90a3\u4ed8\u5e73\u677f\u5f97\u65e0\u9f3b\u65e0\u5634\u7684\u602a\u8138\uff0c\u5fc3\u4e2d\u4e0d\u7981\u53d1\u6bdb\uff0c\u53ea\u542c\u4ed6\u8f7b\u54fc\u4e86\u4e00\u58f0\uff0c \u8eab\u5f62\u5982\u884c\u4e91\u6d41\u6c34\u822c\u9000\u4e86\u4e94\u6b65\uff0c\u907f\u5f00\u4e86\u6b64\u62db\u3002 \u3000\u3000\u7126\u52b3\u8fd8\u5f85\u8ffd\u51fb\uff0c\u201c\u5929\u6b8b\u201d\u7126\u5316\u8fde\u5fd9\u6253\u624b\u4e00\u9635\u4e71\u6bd4\uff0c\u5144\u5f1f\u4e24\u4eba\u5fc3\u610f\u65e9\u901a\uff0c\u7126\u52b3\u4e00\u8dc3\u51c6 \u5907\u4e0b\u623f\u3002 \u3000\u3000\u6562\u60c5\u7126\u5316\u662f\u6015\u623f\u4e0b\u8fd8\u85cf\u6709\u522b\u4eba\uff0c\u662f\u4ee5\u53eb\u7126\u52b3\u4e0b\u53bb\u67e5\u63a2\u4e00\u4e0b\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u6e05\u5578\u963b\u6b62\u201c\u6444\u7470\u9b3c\u97f3\u201d\u7684\u8499\u9762\u4eba\u5ffd\u5730\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u58f0\u97f3\u5b9b\u5982\u98de\u9f99\u884c\u7a7a\uff0c\u7545 \u5176\u4e0d\u77e5\u6240\u6b62\u3002 \u3000\u3000\u201c\u5929\u5e9f\u201d\u7126\u52b3\u867d\u7136\u8033\u804b\u542c\u4e0d\u89c1\uff0c\u4f46\u811a\u4e0b\u74e6\u7247\u5374\u88ab\u5578\u5f97\u7c41\u7c41\u800c\u52a8\uff0c\u4e0d\u7531\u505c\u8eab\u56de\u671b\u3002 \u3000\u3000\u90a3\u8499\u9762\u4eba\u53f3\u624b\u6301\u5251\uff0c\u5de6\u624b\u6267\u5251\u5c16\uff0c\u5c06\u957f\u5251\u5f2f\u6210\u4e00\u4e2a\u4f18\u5f27\uff0c\u4e00\u653e\u5de6\u624b\uff0c\u957f\u5251\u9535\u7136\u5f39\u76f4\uff0c \u96ea\u4eae\u7684\u5251\u5c16\u4e00\u9635\u52a8\u8df3\uff0c\u5728\u9ed1\u6f06\u7684\u7a7a\u4e2d\u5212\u51fa\u4e03\u6735\u5de5\u6574\u7684\u6885\u82b1\u3002 \u3000\u3000\u201c\u5929\u6b8b\u201d\u201c\u5929\u5e9f\u201d\u540c\u65f6\u4e00\u60ca\uff0c\u53ea\u56e0\u8fd9\u4e03\u6735\u6885\u82b1\u6b63\u662f\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u7684\u6807\u5fd7\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u4e0e\u4e03\u5999\u795e\u541b\u9f50\u540d\u6b66\u6797\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u5bf9\u8fc7\u9762\uff0c\u8fd1\u65e5\u4e03\u5999\u795e\u541b\u91cd\u73b0\u6c5f\u6e56\u7684\u4e8b\u4e24 \u4eba\u4ed6\u6709\u8033\u95fb\uff0c\u8fd9\u65f6\u89c1\u773c\u524d\u8499\u9762\u4eba\u7adf\u5f39\u51fa\u4e03\u6735\u6885\u82b1\uff0c\u4e0d\u7adf\u5927\u5947\u3002 \u3000\u3000\u5929\u6b8b\u5fc3\u9053\uff1a\u201c\u8fd9\u65af\u624b\u4e2d\u957f\u5251\u5206\u660e\u4e0d\u662f\u67d4\u8f6f\u4e4b\u7269\uff0c\u4ed6\u5374\u5c06\u5b83\u5f2f\u6210\u4f18\u5f27\u800c\u4e0d\u65ad\uff0c\u8fd9\u4efd\u529f \u529b\u5b9e\u5728\u4e0d\u51e1\uff0c\u96be\u9053\u4e03\u5999\u795e\u541b\u771f\u7684\u91cd\u5165\u6c5f\u6e56\uff1f\u201d \u3000\u3000\u90a3\u8499\u9762\u4eba\u5374\u53c8\u9053\uff1a\u201c\u5173\u4e2d\u9738\u4e5d\u8c6a\uff0c\u6cb3\u6d1b\u60df\u4e00\u5251\uff0c\u6d77\u5185\u5c0a\u4e03\u5999\uff0c\u4e16\u5916\u6709\u4e09\u4ed9\uff01\u5173\u4e2d\u4ea6 \u6d77\u5185\u4e5f\uff0c\u4e5d\u8c6a\u867d\u9738\u5173\u4e2d\uff0c\u5374\u4e5f\u5e94\u5c0a\u6211\u4e03\u5999\u54e9\u3002\u201d \u3000\u3000\u8bf4\u5b8c\u957f\u7b11\u4e00\u58f0\uff0c\u8eab\u5f62\u4fdd\u6301\u539f\u72b6\uff0c\u8db3\u5c16\u7528\u529b\u4e00\u70b9\uff0c\u590d\u62d4\u8d77\u6570\u4e08\uff0c\u8eab\u5f62\u5982\u5f39\u4e38\u822c\u98de\u4e86\u51fa \u53bb\uff0c\u7b11\u58f0\u4e2d\u4f20\u6765\uff1a\u201c\u6d77\u5929\u53cc\u715e\u6709\u79cd\u8ddf\u6211\u6765\u3002\u201d \u3000\u3000\u7126\u5316\u54c8\u54c8\u66b4\u7b11\u9053\uff1a\u201c\u5c31\u6682\u9976\u8303\u8001\u513f\u4e00\u591c\u3002\u201d\u5411\u7126\u52b3\u5fae\u6253\u624b\u52bf\uff0c\u4e24\u4e2a\u6b8b\u7f3a\u4e0d\u5168\u7684\u80a2\u4f53 \u5374\u75be\u5982\u6d41\u661f\u5730\u8ffd\u4e86\u4e0a\u53bb\uff0c\u4e0d\u6d88\u7247\u523b\u5931\u5374\u8e2a\u5f71\u3002 \u3000\u3000\u623f\u4e0a\u7559\u4e0b\u7684\u4e00\u4e2a\u8499\u9762\u4eba\u89c1\u4e09\u4eba\u5982\u98ce\u800c\u53bb\u540e\uff0c\u626f\u4e0b\u8138\u4e0a\u8499\u5dfe\uff0c\u9732\u51fa\u4e00\u5f20\u4fca\u7f8e\u65e0\u6bd4\u7684\u8138 \u5b54\uff0c\u6b63\u662f\u90a3\u5434\u51cc\u98ce\u3002 \u3000\u3000\u5434\u51cc\u98ce\u4fa7\u8033\u542c\u4e86\u542c\uff0c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u600e\u4e48\u6211\u4eec\u5728\u4e0a\u9762\u95f9\u4e86\u534a\u5929\uff0c\u4e0b\u9762\u4e00\u70b9\u52a8\u9759\u4e5f\u6ca1 \u6709\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u4e00\u9635\u591c\u98ce\u5374\u9001\u6765\u4e00\u58f0\u5175\u5203\u76f8\u63a5\u7684\u58f0\u54cd\uff0c\u5434\u51cc\u98ce\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u8dc3\u4e0b\u5c4b\u9876\uff0c \u7ffb\u5165\u8303\u6cbb\u6210\u7684\u9662\u5b50\u3002 \u3000\u3000\u8e0f\u5165\u5185\u9662\uff0c\u53ea\u89c9\u5c4b\u5185\u706f\u706b\u5168\u6ca1\uff0c\u9ed1\u5f97\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u6b63\u8981\u6478\u7d22\u524d\u8fdb\uff0c\u5ffd\u5730\u811a\u4e0b\u4e00\u7eca\uff0c \u9669\u4e9b\u8dcc\u5012\uff0c\u867d\u4ed7\u7740\u9a6c\u6b65\u6c89\u7a33\u6ca1\u6709\u8dcc\u4e0b\uff0c\u4f46\u5df1\u5f04\u51fa\u4e00\u58f0\u5de8\u54cd\u3002 \u3000\u3000\u201c\u64e6\u201d\u5730\u4e00\u58f0\uff0c\u706b\u647a\u5b50\u8fce\u98ce\u800c\u4eae\uff0c\u5434\u51cc\u98ce\u501f\u706b\u5149\u5f80\u4e0b\u4e00\u671b\uff0c\u60ca\u5f97\u51e0\u4e4e\u53eb\u51fa\u4e86\u58f0\uff0c\u539f \u6765\u7eca\u4ed6\u4e00\u4e0b\u7684\u4e43\u662f\u4e00\u4e2a\u4eba\u7684\u8eab\u4f53\u3002 \u3000\u3000\u51d1\u8fd1\u4e00\u770b\uff0c\u7adf\u662f\u8303\u6cbb\u6210\u8bf7\u6765\u7684\u52a9\u62f3\u4e4b\u4e00\u201c\u751f\u6b7b\u5224\u201d\u9646\u884c\u7a7a\u3002 \u3000\u3000\u5434\u51cc\u98ce\u66fe\u89c1\u8fc7\u4ed6\u4e00\u9762\uff0c\u662f\u4ee5\u8ba4\u5f97\u51fa\u3002 \u3000\u3000\u751f\u6b7b\u5224\u5c38\u8eab\u4e0a\u6ca1\u6709\u5175\u5203\u4f24\u75d5\uff0c\u53ea\u662f\u9886\u4e0a\u6709\u4e00\u5c0f\u6ef4\u8840\u8ff9\uff0c\u4f3c\u4e4e\u662f\u4e2d\u4e86\u4ec0\u4e48\u6b79\u6bd2\u6697\u5668\u6240 \u81f4\u3002 \u3000\u3000\u5434\u51cc\u98ce\u4e00\u65f6\u60f3\u4e0d\u901a\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u8fde\u5fd9\u6301\u706b\u7ee7\u7eed\u8d70\u8fdb\u53bb\u3002 \u3000\u3000\u4ed6\u539f\u6765\u548c\u8f9b\u6377\u8ba1\u5212\u7684\u662f\u7531\u8f9b\u6377\u5c06\u6d77\u5929\u53cc\u715e\u5f15\u5f00\uff0c\u4ed6\u4e0b\u53bb\u6253\u6551\u8303\u6cbb\u6210\u2014\u2014\u800c\u5434\u51cc\u98ce\u81ea \u5df1\u8fd8\u60f3\u501f\u673a\u4ece\u90a3\u4e3a\u8303\u6cbb\u6210\u52a9\u62f3\u7684\u70b9\u82cd\u9ad8\u624b\u5353\u4e4b\u4ef2\u5904\u6253\u63a2\u4e00\u4e0b\u70b9\u82cd\u6d3e\u7684\u865a\u5b9e\u3002\u8fd9\u65f6\u751f\u6b7b\u5224 \u9646\u884c\u7a7a\u6a2a\u5c38\u95e8\u524d\uff0c\u771f\u4ee4\u4ed6\u4e0d\u5f97\u5176\u89e3\u3002 \u3000\u3000\u4ed6\u5341\u5206\u8c28\u614e\u5730\u8d70\u8fdb\u5185\u5c4b\uff0c\u706b\u5149\u7167\u5904\uff0c\u5f53\u4e2d\u684c\u4e0a\u8d6b\u7136\u4f0f\u7740\u4e00\u4eba\uff0c\u7ffb\u5f00\u8138\u5b54\u4e00\u770b\uff0c\u7adf\u662f \u8303\u6cbb\u6210\uff01 \u3000\u3000\u8303\u6cbb\u6210\u8138\u8272\u53d1\u9ed1\uff0c\u5168\u8eab\u4e5f\u6ca1\u6709\u4f24\u75d5\uff0c\u4f46\u5434\u51cc\u98ce\u5374\u8bc6\u5f97\u5fc5\u662f\u88ab\u70b9\u82cd\u7684\u4e03\u7edd\u624b\u6cd5\u70b9\u7a74\u81f4 \u6b7b\u3002 \u3000\u3000\u5434\u51cc\u98ce\u662f\u4e2a\u7edd\u9876\u806a\u660e\u7684\u4eba\uff0c\u8fd9\u4e00\u6d3e\u666f\u8c61\uff0c\u7acb\u523b\u4ee4\u4ed6\u6000\u7591\u5230\u90a3\u4e2a\u70b9\u82cd\u9ad8\u624b\u5353\u4e4b\u4ef2\u3002 \u3000\u3000\u4ed6\u653e\u4e0b\u706b\u647a\uff0c\u53cc\u638c\u62a4\u4f4f\u80f8\u9762\uff0c\u4e00\u811a\u8e39\u5f00\u5185\u95e8\uff0c\u4e00\u7247\u7a7a\u8361\u8361\u7684\uff0c\u54ea\u6709\u4ec0\u4e48\u52a8\u9759\u3002 \u3000\u3000\u4f46\u662f\u5f53\u4ed6\u8de8\u5165\u624d\u4e24\u6b65\uff0c\u5ffd\u7136\u8fce\u9762\u5636\u5636\u98ce\u58f0\uff0c\u4ed6\u9661\u7136\u4e00\u4e2a\u94c1\u677f\u6865\uff0c\u5411\u540e\u5012\u4e86\u4e0b\u53bb\uff0c\u53ee \u53ee\u4e24\u58f0\uff0c\u6697\u5668\u5df2\u4ece\u4e0a\u9762\u6253\u7a7a\uff0c\u5927\u6982\u662f\u9489\u5728\u5899\u4e0a\u3002 \u3000\u3000\u5434\u51cc\u51e4\u7565\u4e00\u4f0f\u8eab\uff0c\u8ba9\u773c\u6674\u4e60\u60ef\u4e86\u9ed1\u6697\uff0c\u5b9a\u795e\u4e00\u770b\uff0c\u53ea\u89c1\u5c4b\u5185\u7a7a\u8361\u8361\u7684\uff0c\u53ea\u6709\u53f3\u9762\u5899 \u6839\u5904\u4f3c\u4e4e\u4f0f\u7740\u4e00\u4e2a\u4eba\u4f53\u3002 \u3000\u3000\u5434\u51cc\u98ce\u62ff\u4e86\u706b\u647a\u5b50\u518d\u8d70\u8fd1\u4e00\u770b\uff0c\u5730\u4e0a\u679c\u7136\u662f\u5177\u5c38\u4f53\uff0c\u53ea\u662f\u5fc3\u5934\u5fae\u6e29\uff0c\u597d\u50cf\u624d\u6b7b\u4e0d\u4e45\uff0c \u7ec6\u89c1\u9762\u5b54\u65f6\uff0c\u7adf\u662f\u90a3\u70b9\u82cd\u9ad8\u624b\u5353\u4e4b\u4ef2\uff0c\u80f8\u524d\u4e00\u5904\u4f24\u53e3\uff0c\u4f3c\u662f\u521b\u4f24\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u628a\u4ed6\u539f\u6765\u7684\u6000\u7591\u5168\u90e8\u63a8\u7ffb\u3002\u5434\u51cc\u98ce\u53c8\u6014\u4e86\u4e00\u4f1a\uff0c\u53d1\u73b0\u5353\u4e4b\u4ef2\u624b\u4e2d\u4f3c\u4e4e\u634f\u6709\u4e00 \u7269\uff0c\u7ec6\u770b\u539f\u6765\u662f\u4e00\u53ea\u4e27\u95e8\u9489\uff0c\u770b\u60c5\u5f62\u4f3c\u4e4e\u662f\u65e0\u529b\u6253\u51fa\u5c31\u5df2\u6b7b\u53bb\uff0c\u4ed6\u56de\u5934\u770b\u4e86\u770b\u9489\u5728\u5899\u4e0a \u7684\u4e24\u53ea\u6697\u5668\uff0c\u6b63\u662f\u4e00\u6478\u4e00\u6837\u7684\u4e27\u95e8\u9489\uff0c\u663e\u7136\u65b9\u624d\u6697\u5668\u662f\u5353\u4e4b\u4ef2\u6240\u53d1\u3002 \u3000\u3000\u8fd9\u4e00\u8fde\u4e32\u7684\u6025\u53d8\u4f7f\u5434\u51cc\u98ce\u9677\u5165\u82e6\u601d\u4e2d\uff0c\u5bf9\u7740\u5353\u4e4b\u4ef2\u7684\u5c38\u4f53\u53d1\u5446\u3002 \u3000\u3000\u201c\u8303\u6cbb\u6210\u662f\u6b7b\u5728\u4e03\u7edd\u624b\u6cd5\uff0c\u770b\u6765\u591a\u534a\u662f\u5353\u4e4b\u4ef2\u7684\u6bd2\u624b\u4e86\uff0c\u90a3\u9646\u884c\u7a7a\u4f3c\u4e4e\u6bd4\u8303\u6cbb\u6210\u6b7b \u5f97\u65e9\uff0c\u53ef\u80fd\u4e5f\u662f\u5353\u4e4b\u4ef2\u6240\u6740\uff0c\u53ef\u662f\u5353\u4e4b\u4ef2\u4e3a\u4ec0\u4e48\u8981\u6740\u4ed6\u4eec\u5462\uff1f\u4ed6\u4e0d\u662f\u53d7\u6258\u6765\u4e3a\u8303\u6cbb\u6210\u52a9 \u62f3\u7684\u4e48\uff1f\u5982\u679c\u662f\u5353\u4e4b\u4ef2\u6740\u7684\uff0c\u90a3\u4e48\u5353\u4e4b\u4ef2\u662f\u8c01\u6740\u7684\u5462\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u7684\u773c\u775b\u5ffd\u7136\u53d1\u89c9\u4e86\u4e00\u6869\u4e8b\u3002 \u3000\u3000\u8303\u6cbb\u6210\u5c38\u8eab\u65c1\u7684\u684c\u5b50\u62bd\u5c49\u7b49\u90fd\u88ab\u7ffb\u5f97\u4e03\u96f6\u516b\u843d\uff0c\u9760\u5899\u7684\u67dc\u5b50\u4e5f\u88ab\u6253\u5f00\u3002 \u3000\u3000\u201c\u55ef\uff0c\u5fc5\u662f\u8303\u6cbb\u6210\u6709\u4ec0\u4e48\u5b9d\u7269\u4e4b\u7c7b\u5f15\u8d77\u7684\u51f6\u6740\u2014\u2014\u201d\u4ed6\u8fd9\u6837\u63a8\u65ad\u7740\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u60f3\u8d77\u81ea\u5df1\u66fe\u542c\u89c1\u5175\u5668\u76f8\u78b0\u7684\u58f0\u97f3\uff0c\u90a3\u8303\u6cbb\u6210\u53ca\u9646\u884c\u7a7a\u90fd\u65e9\u5df2\u6b7b\u53bb\uff0c\u53ea\u6709\u8fd9 \u5353\u4e4b\u4ef2\u65b9\u624d\u624d\u6b7b\u53bb\u2014\u2014 \u3000\u3000\u201c\u5bf9\u4e86\uff0c\u5175\u5203\u76f8\u78b0\u7684\u4e24\u4e2a\u4eba\uff0c\u4e00\u4eba\u5fc5\u662f\u5353\u4e4b\u4ef2\uff0c\u53e6\u4e00\u4eba\u5c31\u662f\u6740\u5353\u4e4b\u4ef2\u7684\u4e86\u2014\u2014\u53ea\u6015 \u6b64\u4eba\u8fd8\u672a\u51fa\u5c4b\uff0c\u6211\u4e14\u641c\u4e00\u641c\u2014\u2014\u201d \u3000\u3000\u624d\u8de8\u51fa\u95e8\uff0c\u5916\u9762\u8d70\u8fdb\u4e00\u4e2a\u4eba\u6765\u3002 \u3000\u3000\u5434\u51cc\u98ce\u6597\u7136\u7acb\u4f4f\uff0c\u89c1\u90a3\u4eba\u624b\u6a2a\u957f\u5251\uff0c\u51b7\u51b7\u5bf9\u81ea\u5df1\u9053\uff1a\u201c\u597d\u72e0\u7684\u624b\u6bb5\uff0c\u5341\u53e3\u6c14\u6740\u4e86\u4e09 \u4eba\uff01\u201d \u3000\u3000\u4ed6\u82e5\u4e0d\u8bf4\u8fd8\u597d\uff0c\u8fd9\u4e00\u8bf4\uff0c\u5434\u51cc\u98ce\u7acb\u523b\u6599\u5b9a\u5fc5\u662f\u4ed6\u6740\u4e86\u5353\u4e4b\u4ef2\uff0c\u518d\u4e00\u770b\uff0c\u4ed6\u624b\u4e2d\u5251\u5c16 \u8fd8\u6709\u4e00\u4e1d\u8840\u75d5\uff0c\u76ca\u53d1\u77e5\u9053\u6240\u6599\u4e0d\u5dee\uff0c\u5f53\u4e0b\u559d\u95ee\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8c01\uff1f\u201d \u3000\u3000\u90a3\u4eba\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u5d06\u5cd2\u4e09\u7edd\u5251\u7684\u5927\u540d\u4f60\u7adf\u4e0d\u77e5\uff1f\u201d \u3000\u3000\u5434\u51cc\u98ce\u4e00\u542c\u201c\u5d06\u5cd2\u201d\u4e24\u5b57\uff0c\u8840\u5f80\u4e0a\u51b2\uff0c\u4f46\u4ed6\u4ecd\u51b7\u51b7\u9053\uff1a\u201c\u4e0d\u66fe\u542c\u8fc7\u54e9\uff0c\u8bf7\u6559\u5927\u540d\uff1f\u201d \u3000\u3000\u90a3\u4eba\u6717\u58f0\u9053\uff1a\u201c\u4eba\u79f0\u5929\u7edd\u5251\u8bf8\u845b\u660e\u5c31\u662f\u533a\u533a\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u5ffd\u7136\u5927\u58f0\u559d\u9053\uff1a\u201c\u8bf8\u845b\u660e\uff0c\u8303\u8001\u513f\u7684\u5b9d\u7269\u5feb\u4ea4\u6765\uff01\u201d\u8bf4\u7f62\u53cc\u76ee\u5b9a\u89c6\u8bf8\u845b\u660e\u3002 \u3000\u3000\u8bf8\u845b\u660e\u679c\u7136\u8138\u8272\u5927\u53d8\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u5ffd\u7136\u8f6c\u8eab\u5c31\u8dd1\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5434\u51cc\u98ce\u7acb\u523b\u65ad\u5b9a\u81ea\u5df1\u6240\u6599\u6b63\u786e\uff0c\u4e0d\u52a0\u601d\u7d22\u5730\u8ffd\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8bf8\u845b\u660e\u4e00\u8def\u5f80\u5317\u8dd1\u53bb\uff0c\u51cc\u98ce\u5fc3\u60f3\uff1a\u201c\u90a3\u6d77\u5929\u53cc\u715e\u867d\u7136\u9ad8\u5f3a\uff0c\u4f46\u51ed\u6377\u5f1f\u90a3\u8eab\u8f7b\u529f\u5927\u7ea6 \u5403\u4e0d\u4e86\u4e8f\u3002\u201d\u4e8e\u662f\u4e5f\u4e00\u8def\u8ffd\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u8ffd\u4e0b\u53bb\uff0c\u4ed6\u53d1\u73b0\u4e86\u5d06\u5cd2\u638c\u95e8\u4eba\u5251\u795e\u5389\u9e57\u7684\u8e2a\u8ff9\uff0c\u4ed6\u867d\u81ea\u77e5\u529f\u529b\u4e0e\u5389\u9e57\u76f8\u5dee\u751a\u591a\uff0c \u963b\u4ecd\u62b1\u7740\u201c\u4e0d\u5165\u864e\u7a74\u7109\u5f97\u864e\u5b50\u201d\u7684\u5fc3\u7406\u8ddf\u8e2a\u4e0b\u53bb\uff0c\u53ea\u4e00\u8def\u4e0a\u4f5c\u4e86\u8bb0\u53f7\uff0c\u53eb\u8f9b\u6377\u770b\u4e86\u597d\u8ddf \u4e0a\u6765\u3002 \u3000\u3000\u7559\u5728\u6b66\u5a01\u9556\u5c40\u4e2d\u7684\u4e09\u5177\u5c38\u4f53\uff0c\u5230\u6b21\u65e5\u88ab\u4eba\u53d1\u73b0\u65f6\uff0c\u52bf\u5fc5\u7b97\u5728\u6d77\u5929\u53cc\u715e\u7684\u5e10\u4e0a\u4e86\uff0c\u4f46 \u6d77\u5929\u53cc\u715e\u4e00\u751f\u6740\u4eba\u65e0\u6570\uff0c\u52a0\u4e0a\u8fd9\u4e09\u6761\u547d\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f \u3000\u3000\u518d\u8bf4\uff0c\u90a3\u5f15\u5f00\u201c\u6d77\u5929\u53cc\u715e\u201d\u7684\u201c\u8499\u9762\u4eba\u201d\uff0c\u51fa\u5f97\u5e02\u90ca\uff0c\u54ea\u8fd8\u987e\u5f97\u8bb8\u591a\uff0c\u811a\u4e0b\u52a0\u529b\uff0c \u6709\u5982\u4e00\u6761\u9ed1\u7ebf\uff0c\u201c\u6d77\u5929\u53cc\u715e\u201d\u89c1\u8fd9\u81ea\u79f0\u201c\u4e03\u5999\u795e\u541b\u201d\u7684\u5bb6\u4f19\u811a\u7a0b\u679c\u7136\u60ca\u4eba\uff0c\u5fc3\u4e2d\u6697\u5fd6\u9053\uff1a \u201c\u96be\u9053\u679c\u7136\u662f\u6885\u6c11\u5c71\u5417\uff1f\u201d\u5fc3\u5ff5\u4e00\u52a8\uff0c\u96c4\u5fc3\u7a81\u594b\u3002 \u3000\u3000\u987b\u77e5\u5173\u4e2d\u4e5d\u8c6a\u72ec\u6b65\u7eff\u6797\uff0c\u548c\u4e03\u5999\u795e\u541b\u9f50\u540d\uff0c\u8fd9\u65f6\u89c1\u4e03\u5999\u795e\u541b\u8f7b\u529f\u5982\u6b64\u4f73\u5999\uff0c\u5fc3\u5934\u6bd4 \u8bd5\u4e4b\u5fc3\u5927\u8d77\uff0c\u4e00\u6446\u624b\u5f0f\uff0c\u5144\u5f1f\u4e8c\u4eba\u5168\u529b\u8ffd\u8d76\u4e0b\u53bb\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u7684\u811a\u7a0b\u90fd\u662f\u6c5f\u6e56\u4e0a\u7f55\u89c1\u7684\uff0c\u5168\u529b\u5954\u8dd1\u8d77\u6765\uff0c\u547c\u547c\u98ce\u751f\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4fbf\u6765\u5230\u57ce \u897f\u7684\u9f9f\u5c71\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u4f3c\u4e4e\u6709\u610f\u4e0a\u5c71\uff0c\u56de\u9996\u51b7\u7136\u54fc\u9053\uff1a\u201c\u9f9f\u5c71\u5947\u9669\uff0c\u4e8c\u4eba\u6709\u5174\u8da3\u4e58\u591c\u4e00\u6e38 \u4e48\uff1f\u201d \u3000\u3000\u6d77\u5929\u53cc\u715e\u4f55\u7b49\u8001\u5230\uff0c\u5fc3\u4e2d\u867d\u662f\u6000\u7591\u5bf9\u65b9\u4f7f\u7684\u62d6\u5175\u4e4b\u8ba1\uff0c\u4f46\u4e03\u5999\u795e\u541b\u7684\u540d\u5934\u7740\u5b9e\u592a\u5065\uff0c \u54ea\u6562\u4e1d\u6beb\u5927\u610f\uff0c\u53ea\u5f97\u653e\u5f03\u5206\u5934\u800c\u884c\u7684\u5de5\u4f5c\uff0c\u800c\u5408\u4e00\u8ffd\u8d76\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u7684\u8bdd\u97f3\u65b9\u843d\u3002\u4e0d\u505c\u7a0d\u8bb8\uff0c\u8db3\u5c16\u70b9\u5730\uff0c\u5df2\u5728\u5fae\u4eae\u7684\u82cd\u7a79\u4e0b\u767b\u5c71\u800c\u53bb\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u5fae\u5fae\u4e00\u6014\u3002\u7126\u5316\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u770b\u4f60\u4e03\u5999\u795e\u541b\u80fd\u591f\u5948\u4f55\u5f97\u4e86\u54b1\u4eec\uff01\u201d \u3000\u3000\u602a\u53eb\u58f0\u4e2d\uff0c\u5df1\u548c\u7126\u52b3\u62a2\u767b\u800c\u4e0a\u3002 \u3000\u3000\u9f9f\u5c71\u5947\u9669\u7532\u5929\u4e0b\uff0c\u4efb\u4e09\u4eba\u4e00\u7b49\u4e00\u7684\u8f7b\u8eab\u529f\u5927\uff0c\u5230\u5f97\u5c71\u8170\uff0c\u5df2\u662f\u5929\u8272\u5927\u660e\u3002 \u3000\u3000\u524d\u5934\u7684\u201c\u4e03\u5999\u795e\u541b\u201d\u4f3c\u4e4e\u6015\u201c\u6d77\u5929\u53cc\u715e\u201d\u4e0d\u8010\u800c\u81f4\u8ba1\u7b56\u4e0d\u6210\uff0c\u4e0d\u65f6\u56de\u9996\u6311\u9017\u51e0\u53e5\u3002 \u5176\u5b9e\u4ed6\u4e0d\u5fc5\u5982\u6b64\u62c5\u5fc3\uff0c\u6d77\u5929\u53cc\u715e\u88ab\u4ed6\u6298\u817e\u4e86\u4e00\u591c\uff0c\u65e9\u5b58\u5b9a\u4e86\u4e0d\u5230\u624b\u4e0d\u505c\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u4e00\u8def\u5954\u8dd1\uff0c\u4e00\u8def\u6697\u5fd6\u9053\uff1a\u201c\u6211\u8981\u4e0d\u8981\u8131\u4e0b\u8499\u9762\uff0c\u8ba9\u4ed6\u4eec\u77e5\u9053\u6211\u662f\u8f9b\u4e5d \u9e4f\u7684\u540e\u4ee3\uff1f\u201d \u3000\u3000\u53c8\u8f6c\u5ff5\u5fd6\u9053\uff1a\u201c\u6211\u73b0\u5728\u5192\u7684\u662f\u4e03\u5999\u795e\u541b\u4e4b\u540d\uff0c\u8fd8\u662f\u4e0d\u8981\u66b4\u9732\u8eab\u4efd\uff0c\u4e00\u76f4\u7b49\u5230\u628a\u4ed6\u4eec \u4e8c\u4e2a\u8d3c\u79cd\u70b9\u5012\u540e\u9732\u51fa\u8eab\u4efd\uff0c\u4f7f\u4ed6\u4eec\u77e5\u9053\u8f9b\u4e5d\u9e4f\u540e\u4eba\u4e3a\u7236\u6bcd\u62a5\u4ec7\uff01\u201d \u3000\u3000\u60f3\u5230\u7236\u6bcd\uff0c\u5ffd\u89c9\u5fc3\u4e2d\u6012\u706b\u51b2\u817e\uff0c\u8eab\u5f62\u4e0d\u89c9\u4e00\u7a92\uff0c\u53cc\u715e\u4f55\u7b49\u811a\u7a0b\uff0c\u5df2\u8d76\u8fd1\u6570\u4e08\u4e4b\u591a\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u54fc\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u60f3\u6765\u8fd9\u4e48\u4e45\u65f6\u95f4\uff0c\u8303\u6cbb\u6210\u5fc5\u5e94\u5df2\u8131\u9669\u4e86\uff0c\u201d\u5ff5\u5934\u65e2\u5b9a\uff0c\u500f \u5730\u505c\u4e0b\u811a\u6b65\uff0c\u5237\u5730\u56de\u8fc7\u8eab\u6765\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u4e0d\u865e\u4ed6\u5ffd\u7136\u505c\u6b65\uff0c\u4e5f\u81ea\u4e00\u5de6\u4e00\u53f3\uff0c\u505c\u4e0b\u8eab\u6765\uff0c\u4e09\u4eba\u4e01\u5b57\u5f62\u5bf9\u7acb\uff0c\u8ddd\u79bb\u4e0d\u8fc7 \u5bfb\u4e08\uff01 \u3000\u3000\u8f9b\u6377\u50b2\u7136\u4e00\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u5174\u81f4\u4e0d\u5c0f\uff0c\u5012\u5e95\u662f\u966a\u6211\u2018\u6885\u67d0\u4eba\u2019\u4e0a\u9f9f\u5c71\u4e86\uff01\u201d \u3000\u3000\u5929\u6b8b\u5929\u5e9f\u4e8c\u4eba\u6298\u817e\u4e00\u665a\uff0c\u5fc3\u4e2d\u72c2\u6012\uff0c\u4e8c\u5f20\u9b3c\u8138\u66f4\u5f62\u53ef\u6016\uff0c\u7126\u5316\u5389\u58f0\u9053\uff1a\u201c\u4e03\u5999\u795e\u541b \u628a\u54b1\u4eec\u5f15\u5230\u8fd9\u513f\u6765\u2014\u2014\u201d \u3000\u3000\u6b64\u65f6\u5929\u8272\u5927\u660e\uff0c\u8f9b\u6377\u4ece\u9762\u5dfe\u4e2d\u770b\u90a3\u6d77\u5929\u53cc\u715e\uff0c\u5bb9\u8c8c\u4ecd\u662f\u90a3\u4ed8\u6837\u5b50\uff0c\u548c\u5341\u5e74\u524d\u4e00\u4e1d\u672a \u53d8\uff0c\u5fc3\u4e2d\u5ff5\u5230\u7236\u6bcd\u60e8\u6b7b\u7684\u60c5\u666f\uff0c\u4e0d\u89c9\u5168\u8eab\u98a4\u6296\uff0c\u7126\u5316\u7684\u8bdd\u4e00\u53e5\u4e5f\u6ca1\u6709\u542c\u8fdb\u3002 \u3000\u3000\u505c\u5f97\u7247\u523b\uff0c\u5929\u6b8b\u89c1\u4ed6\u6beb\u65e0\u53cd\u5e94\uff0c\u8bef\u4ee5\u4e3a\u4ed6\u662f\u77a7\u4e0d\u8d77\u81ea\u5df1\uff0c\u6709\u82e5\u706b\u4e0a\u52a0\u6cb9\uff0c\u72c2\u53f1\u9053\uff1a \u201c\u4f60\u4e5f\u4e0d\u5fc5\u5982\u6b64\u72c2\u5984\uff0c\u54b1\u4eec\u6d77\u5929\u53cc\u715e\u4eca\u65e5\u53eb\u4f60\u7acb\u523b\u8840\u6e85\u6b64\u5c71\uff01\u201d \u3000\u3000\u8f9b\u6377\u542c\u4e86\uff0c\u683c\u5916\u89c9\u5f97\u523a\u8033\uff0c\u51c4\u5389\u4e00\u58f0\u957f\u7b11\uff0c\u547c\u7684\u4e00\u624b\u5288\u5411\u5929\u6b8b\u7126\u5316\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u5df2\u5728\u4ed6\u90a3\u7b11\u58f0\u4e2d\u5206\u8fa8\u51fa\u6709\u51e0\u5206\u51c4\u5389\u7684\u5473\u9053\uff0c\u5fc3\u4e2d\u5fae\u5fae\u4e00\u6014\uff0c\u9519\u6b65\u907f\u5f00\u3002 \u3000\u3000\u5012\u662f\u4e00\u65c1\u7684\u7126\u52b3\u63a5\u4e86\u4e00\u62db\uff0c\u8f9b\u6377\u4e1d\u6beb\u4e0d\u9000\uff0c\u5de6\u638c\u53f3\u62f3\u9f50\u51fb\uff0c\u4e00\u5f0f\u201c\u96f7\u52a8\u4e07\u7269\u201d\uff0c\u6253 \u4e86\u51fa\u53bb\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u5fae\u5fae\u4e00\u95ea\uff0c\u5411\u5de6\u8de8\u534a\u6b65\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u5411\u8f9b\u6377\u819d\u76d6\u3002 \u3000\u3000\u8f9b\u6377\u53cc\u8db3\u5b9a\u7acb\uff0c\u53cc\u62f3\u6325\u52a8\uff0c\u8fde\u6253\u516b\u62f3\uff0c\u62f3\u98ce\u51b2\u6fc0\u8d77\u6781\u5927\u6c14\u6d41\uff0c\u5929\u6b8b\u7126\u5316\u8fde\u8fde\u9000\u540e\uff0c \u7528\u4e86\u5341\u591a\u79cd\u8eab\u6cd5\u624d\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u5b8c\u5168\u76f8\u4fe1\u8fd9\u8499\u9762\u5ba2\u662f\u201c\u4e03\u5999\u795e\u541b\u201d\u4e86\uff0c\u81ea\u5df1\u4e00\u4eba\u4e4b\u529b\uff0c\u4e0d\u4f1a\u662f\u5bf9\u624b\u7684\uff01\u4ed6\u6253 \u4e86\u4e2a\u624b\u5f0f\uff0c\u7126\u52b3\u84e6\u7684\u51fa\u624b\u3002 \u3000\u3000\u7126\u52b3\u51fa\u624b\u653b\u51fb\u7684\u65b9\u4f4d\u662f\u8f9b\u6377\u7684\u201c\u7ae0\u95e8\u7a74\u201d\uff0c\u8f9b\u6377\u4e00\u7b11\uff0c\u5de6\u624b\u5411\u5916\u6563\u52fe\uff0c\u60f3\u7834\u6389\u8fd9\u62db\uff0c \u90a3\u77e5\u7126\u52b3\u53cc\u638c\u4e00\u5206\uff0c\u5de6\u624b\u201c\u7384\u4e4c\u5212\u6c99\u201d\uff0c\u51fb\u5411\u8f9b\u6377\u5de6\u808b\uff0c\u53f3\u638c\u5374\u6781\u5176\u5de7\u5999\u7684\u4e00\u7ffb\uff0c\u5e76\u4f38 \u53cc\u6307\u6025\u70b9\u8fc7\u53bb\uff0c\u5df2\u81ea\u5316\u201c\u767d\u9e7f\u6302\u888b\u201d\u4e4b\u5f0f\uff0c\u4e00\u62db\u4e09\u5f0f\uff0c\u8fde\u88ad\u4e8c\u7a74\uff0c\u8f9b\u6377\u4e0d\u7981\u5fae\u5fae\u5403\u60ca\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u80cc\u540e\u98ce\u58f0\u5927\u4f5c\uff0c\u6562\u60c5\u662f\u7126\u5316\u5728\u540e\u51fa\u638c\u3002 \u3000\u3000\u8f9b\u6377\u9661\u7136\u5927\u5403\u4e00\u60ca\uff0c\u539f\u6765\u7126\u5316\u6240\u653b\u7684\u5730\u65b9\u5374\u6b63\u662f\u81ea\u5df1\u5fc5\u9000\u4e4b\u8def\u3002 \u3000\u3000\u8f9b\u6377\u51ed\u6781\u5feb\u7684\u53cd\u5e94\u6743\u8861\u4e00\u4e0b\uff0c\u84e6\u7684\u5de6\u624b\u4e00\u67b6\uff0c\u6070\u597d\u5c01\u4f4f\u7126\u52b3\u7684\u4e00\u62db\u4e8c\u5f0f\uff0c\u53f3\u624b\u95ea\u7535 \u822c\u4e00\u7529\uff0c\u4e00\u8bb0\u201c\u5012\u6253\u91d1\u949f\u201d\uff0c\u53cd\u51fb\u56de\u53bb\uff0c\u4e5f\u6b63\u597d\u7834\u53bb\u7126\u5316\u7684\u4e00\u62db\uff0c\u4f46\u7a76\u7adf\u51fa\u624b\u592a\u8fdf\uff0c\u771f \u529b\u4e0d\u6d4e\uff0c\u5f53\u573a\u8dcc\u9000\u5341\u4f59\u6b65\u3002 \u3000\u3000\u8fd9\u4e2a\u7167\u9762\u4e0b\uff0c\u4e09\u4e2a\u4eba\u8fde\u51fa\u602a\u62db\uff0c\u4e14\u90fd\u662f\u5de7\u7edd\u4eba\u5bf0\u7684\u62db\u5f0f\uff0c\u5047\u5982\u6709\u55dc\u6b66\u8005\u5728\u4e00\u65c1\u89c2\u770b\uff0c \u4e0d\u77e5\u53c8\u4f1a\u53d7\u76ca\u591a\u5c11\uff01 \u3000\u3000\u6d77\u5929\u53cc\u715e\u60c5\u77e5\u5bf9\u624b\u529f\u592b\u592a\u9ad8\uff0c\u4e0d\u6562\u4e1d\u6beb\u4fdd\u7559\uff0c\u4e8c\u4eba\u62f3\u5f71\u98d8\u5ffd\uff0c\u5939\u653b\u4e0a\u53bb\u3002 \u3000\u3000\u8981\u77e5\u8f9b\u6377\u7684\u529f\u592b\uff0c\u6b64\u65f6\u5df2\u5728\u6d77\u5929\u53cc\u715e\u4efb\u4e00\u4eba\u4e4b\u4e0a\uff0c\u4f46\u4e8c\u4eba\u5408\u51fb\u4e4b\u4e0b\uff0c\u5c31\u4e0d\u514d\u6709\u4e9b\u5fd9 \u624b\u5fd9\u811a\u4e4b\u611f\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u5fc3\u610f\u76f8\u901a\uff0c\u4e8c\u4eba\u5408\u51fb\u4e4b\u4e0b\uff0c\u5a01\u529b\u66f4\u662f\u5927\u5f97\u51fa\u5947\uff0c\u4efb\u8f9b\u6377\u5168\u529b\u4f7f\u4e3a\uff0c\u4e5f\u4e0d\u7981 \u4e00\u6b65\u4e00\u6b65\u88ab\u903c\u5f97\u540e\u9000\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u8273\u9633\u5f53\u7a7a\uff0c\u4e09\u4eba\u6325\u6c57\u6495\u6740\uff0c\u5df2\u6709\u4e2a\u628a\u65f6\u8fb0\u3002 \u3000\u3000\u8f9b\u6377\u88ab\u4e8c\u4e2a\u4e00\u7b49\u4e00\u7684\u9ad8\u624b\u903c\u5f97\u6b65\u6b65\u540e\u9000\uff0c\u4ed6\u7684\u80cc\u540e\u662f\u5c71\u9053\uff0c\u662f\u4ee5\u8d8a\u903c\u8d8a\u9ad8\uff0c\u5df2\u5230\u4e86 \u5c71\u9876\u3002 \u3000\u3000\u9f9f\u5c71\u9876\u4e0a\uff0c\u4e07\u91cc\u65e0\u4e91\uff0c\u6674\u7a7a\u4e00\u78a7\uff0c\u4e24\u65c1\u4e00\u4e24\u682a\u6811\u513f\u7c41\u7c41\u7684\u6447\u52a8\u7740\u53f6\u5b50\uff0c\u9732\u51fa\u4e00\u70b9\u513f \u7a7a\u9699\uff0c\u521a\u597d\u53ef\u4ee5\u671b\u89c1\u6570\u5341\u4e08\u5916\u7684\u4e09\u6761\u4eba\u5f71\u3002 \u3000\u3000\u5154\u8d77\u9e1f\u843d\uff0c\u4e09\u6761\u5f71\u5b50\u957f\u957f\u7684\u62d6\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4e00\u9635\u8f7b\u98ce\u62c2\u8fc7\uff0c\u60a0\u5730\u5929\u8272\u5fae\u53d8\uff0c\u4e00\u6735\u4e91\u513f\u7f13\u7f13\u98d8\u6765\uff0c\u6b63\u597d\u628a\u4e09\u4e2a\u4eba\u5f71\u906e\u4f4f\uff0c\u7acb\u523b\uff0c \u4e09\u4e2a\u957f\u957f\u7684\u5f71\u5b50\u5f2f\u6210\u4e86\u4e09\u4e2a\u5c0f\u9ed1\u70b9\u513f\u3002 \u3000\u3000\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u5728\u8fd9\u6837\u7684\u8ddd\u79bb\u4e5f\u53ef\u770b\u89c1\u4e00\u9053\u8000\u76ee\u7684\u5149\u8292\uff0c\u6562\u60c5\u662f\u6709\u4eba\u64a4\u4e0b\u4e86\u5175\u5668\u3002 \u3000\u3000\u4e09\u4e2a\u4eba\u5f71\u60a0\u7684\u53c8\u6539\u53d8\u4e86\u4e00\u4e2a\u65b9\u5411\uff0c\u5411\u8fd9\u4e8c\u682a\u6811\u7eb5\u4e86\u8fc7\u6765\uff0c\u7ec6\u770b\u4e4b\u4e0b\uff0c\u539f\u6765\u662f\u90a3\u8499\u9762 \u4eba\u628a\u957f\u5251\u62d4\u5728\u624b\u4e0a\u3002 \u3000\u3000\u53c8\u662f\u4e00\u9635\u5fae\u98ce\uff0c\u6811\u513f\u518d\u5996\u5996\u7684\u6447\u52a8\uff0c\u9732\u51fa\u66f4\u5927\u7684\u7a7a\u9699\uff0c\u628a\u8fd9\u4e2a\u955c\u5934\u5168\u90e8\u6536\u5165\u773c\u5e18\u3002 \u3000\u3000\u4e91\u513f\u968f\u98ce\u98d8\u8361\uff0c\u518d\u4e5f\u906e\u4e0d\u4f4f\u592a\u9633\u4e86\uff0c\u9163\u6218\u7740\u7684\u4e09\u4eba\u53c8\u88ab\u9633\u5149\u7167\u7740\uff0c\u53ea\u4e0d\u8fc7\u5f71\u5b50\u53c8\u7f29 \u77ed\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u9633\u5149\u9732\u51fa\u4e91\u91cc\uff0c\u4e00\u56e2\u8679\u5149\u9661\u957f\uff0c\u8499\u9762\u4eba\u957f\u5251\u7cbe\u5149\u66b4\u5c04\uff0c\u5c31\u662f\u4e0d\u61c2\u6b66\u827a\u7684\u4eba\u4e5f\u4f1a\u89c9\u5f97 \u8fd9\u8499\u9762\u4eba\u7684\u5251\u672f\u795e\u5999\u3002 \u3000\u3000\u65f6\u95f4\u4e00\u5206\u4e00\u79d2\u7684\u8fc7\u53bb\uff0c\u4e09\u4eba\u4ea4\u6218\u4e2a\u4e0d\u505c\uff0c\u6d77\u5929\u53cc\u715e\u5fc3\u4e2d\u6697\u60ca\uff0c\u4ee5\u81ea\u5df1\u4e8c\u4eba\u7684\u529f\u529b\u9f50 \u6218\u4e03\u5999\u795e\u541b\u53ea\u4e0d\u8fc7\u5e73\u624b\uff0c\u4e0d\u7981\u4e00\u8d77\u72e0\u72e0\u653b\u51fa\u51e0\u62db\u3002 \u3000\u3000\u592a\u9633\u5df2\u7531\u4e2d\u5929\u504f\u897f\u4e86\uff0c\u4e09\u4eba\u7684\u5f71\u5b50\u968f\u7740\u9633\u5149\u6539\u53d8\u4e86\u65b9\u5411\uff0c\u518d\u7531\u77ed\u800c\u957f\uff0c\u659c\u659c\u5370\u5728\u5c71 \u77f3\u4e0a\u3002 \u3000\u3000\u65e5\u5f71\u504f\u897f\u7684\u65f6\u5019\uff0c\u4e09\u4e2a\u5df2\u63a5\u6210\u4e86\u4e09\u5343\u56de\u5408\u3002 \u3000\u3000\u201c\u563f\u201d\uff0c\u8f9b\u6377\u624b\u4e0a\u7cbe\u5149\u9661\u957f\uff0c\u76d8\u7a7a\u4e00\u531d\uff0c\u653b\u51fa\u4e00\u62db\u201c\u6885\u82b1\u4e09\u5f04\u201d\u3002 \u3000\u3000\u4f46\u89c1\u68ee\u68ee\u5251\u5f71\u4e2d\uff0c\u7cbe\u5149\u4e00\u8fde\u4e09\u6298\uff0c\u5728\u6700\u4f73\u7684\u65f6\u95f4\u548c\u5730\u4f4d\u4e2d\u5c06\u6d77\u5929\u53cc\u715e\u903c\u9000\u6570\u6b65\u3002 \u3000\u3000\u8f9b\u6377\u957f\u957f\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u52c9\u5f3a\u628a\u771f\u6c14\u5747\u5300\u3002 \u3000\u3000\u4e00\u591c\u7684\u5954\u8dd1\uff0c\u5927\u534a\u5929\u53ae\u6740\uff0c\u8f9b\u6377\u518d\u597d\u7684\u5185\u529b\u4e5f\u5fae\u5fae\u4e0d\u654c\u3002\u53cd\u89c2\u90a3\u53cc\u715e\u5374\u4e1d\u6beb\u6ca1\u6709\u7d2f \u76f8\uff0c\u5fc3\u4e2d\u4e0d\u7531\u4f69\u670d\u8fd9\u4e8c\u4eba\u7684\u529f\u529b\u7cbe\u7edd\u3002 \u3000\u3000\u7126\u52b3\u51c4\u51c4\u4e00\u58f0\u957f\u7b11\uff0c\u53cc\u811a\u817e\u7a7a\uff0c\u518d\u53d1\u51fa\u81f4\u547d\u7684\u653b\u52bf\u3002\u8f9b\u6377\u53c8\u518d\u6b21\u4e00\u6b65\u4e00\u6b65\u88ab\u903c\u9000\u3002 \u5929\u6b8b\u5929\u5e9f\u4f3c\u4e4e\u5fc3\u6000\u9b3c\u8ba1\uff0c\u4e00\u62db\u4e00\u5f0f\u5168\u529b\u653b\u5411\u8f9b\u6377\u53f3\u65b9\uff0c\u53c8\u4f7f\u8f9b\u6377\u4e00\u6b65\u4e00\u6b65\u9000\u5411\u5de6\u65b9\u7684\u65ad \u5d16\uff01\u4e0d\u6d88\u6570\u5341\u62db\uff0c\u8f9b\u6377\u5df2\u662f\u9000\u81f3\u5d16\u8fb9\uff0c\u7acb\u8db3\u4e4b\u5730\uff0c\u8ddd\u79bb\u5d16\u4e0d\u53ca\u4e94\u4e08\u3002\u8f9b\u6377\u65e9\u5df2\u6d4b\u77e5\u4e8c\u4eba \u7684\u9b3c\u8be1\uff0c\u6570\u6b21\u60f3\u4ece\u4e8c\u4eba\u5934\u9876\u4e0a\u95ea\u8d8a\u800c\u8131\u79bb\u8fd9\u5371\u52bf\uff0c\u4f46\u6d77\u5929\u53cc\u715e\u4f55\u7b49\u7ecf\u9a8c\uff0c\u4e0d\u662f\u7528\u5288\u7a7a\u638c \u529b\uff0c\u4fbf\u662f\u7528\u5947\u62db\u602a\u5f0f\u6321\u4f4f\u3002 \u3000\u3000\u7126\u5316\u7126\u52b3\u518d\u53d1\u52a8\u653b\u52bf\uff0c\u8f9b\u6377\u53cc\u8db3\u9489\u7acb\uff0c\u786c\u63a5\u4e09\u62db\uff0c\u4e0d\u7981\u53c8\u540e\u9000\u5bfb\u4e08\u3002 \u3000\u3000\u53cc\u715e\u56db\u638c\u4e00\u63b7\uff0c\u62f3\u5f71\u970d\u970d\uff0c\u53c8\u81ea\u653b\u5c06\u4e0a\u6765\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u54fc\u4e00\u58f0\uff0c\u957f\u5251\u968f\u624b\u4e00\u6325\uff0c\u4e00\u5f0f\u201c\u56fa\u5c01\u9f99\u5ead\u201d\u7a81\u7136\u5316\u4f5c\u65b0\u8fd1\u5b66\u81ea\u4e1c\u6d77\u4e09\u4ed9\u4e2d\u5e73 \u51e1\u4e0a\u4eba\u7edd\u5b66\u201c\u5927\u884d\u795e\u5251\u201d\u7684\u8d77\u624b\u5f0f\u2014\u2014\u201c\u65b9\u751f\u4e0d\u606f\u201d\u3002 \u3000\u3000\u201c\u5636\u5636\u201d\u98ce\u58f0\u4e2d\uff0c\u957f\u5251\u5df2\u81ea\u6233\u51fa\u5341\u4f59\u5251\u3002\u84e6\u5730\u5251\u5f0f\u4e00\u6536\uff0c\u62db\u5f0f\u53c8\u53d8\uff0c\u6b63\u662f\u7b2c\u4e8c\u5f0f\u3002 \u201c\u98de\u9601\u6d41\u821f\u3002\u201d \u3000\u3000\u201c\u5927\u884d\u795e\u5251\u201d\u6df1\u5965\u65e0\u6bd4\uff0c\u53d8\u5316\u4e4b\u591a\u4e0d\u53ef\u904d\u6570\uff0c\u53cc\u715e\u6597\u7136\u4e00\u60ca\u53cc\u53cc\u5c55\u5f00\u94c1\u677f\u6865\u529f\u592b\u624d \u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u6597\u7136\u4f7f\u51fa\u7edd\u62db\uff0c\u5a01\u529b\u5927\u5f97\u51fa\u5947\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u5403\u4e86\u4e00\u60ca\uff0c\u4e0d\u7531\u81ea\u4e3b\u5012\u9000\u4e24\u6b65\u3002 \u3000\u3000\u4ed6\u53ef\u5fd8\u8bb0\u81ea\u5df1\u8eab\u5728\u5371\u5d16\uff0c\u540e\u9000\u4e8c\u6b65\uff0c\u5df1\u8ddd\u60ac\u5d16\u4e0d\u53ca\u4e09\u4e94\u5c3a\u4e4b\u8ddd\u3002 \u3000\u3000\u4e00\u9635\u5c71\u98ce\u4ece\u80cc\u540e\u88ad\u6765\uff0c\u4ed6\u5fae\u5fae\u4e00\u9635\u60ca\u89c9\uff0c\u811a\u5c16\u7528\u529b\uff0c\u4fbf\u60f3\u8dc3\u8fdb\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6d77\u5929\u53cc\u715e\u6b63\u4ef0\u8d77\u8eab\u5b50\uff0c\u5c82\u5bb9\u8f9b\u6377\u9003\u5f00\u5371\u5d16\uff0c\u9f50\u9f50\u5168\u529b\u5288\u7a7a\u51fb\u53bb\uff01 \u3000\u3000\u8fd9\u65f6\u5929\u8272\u5df2\u662f\u7533\u9149\u65f6\u5206\uff0c\u65e5\u4e89\u504f\u897f\uff0c\u5929\u8272\u5411\u665a\uff0c\u4e00\u8f6e\u7ea2\u65e5\u7167\u5c04\u7740\u8f9b\u6377\u624b\u4e2d\u957f\u5251\uff0c\u6620 \u8d77\u8000\u773c\u5149\u8292\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u53cc\u715e\u653b\u6765\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u786c\u786c\u6536\u56de\u4e0a\u7eb5\u4e4b\u52bf\uff0c\u957f\u5251\u4e00\u9635\u9707\u8361\uff0c\u6fc0\u8d77\u65e0\u6570\u5251\u5f71\uff0c \u5c01\u5b88\u95e8\u6237\uff0c\u6b63\u662f\u201c\u5927\u884d\u795e\u5251\u201d\u4e2d\u7684\u201c\u7269\u6362\u661f\u79fb\u201d\u3002 \u3000\u3000\u5251\u5f0f\u624d\u53d1\u7acb\u6536\uff0c\u95ea\u7535\u822c\u53c8\u5f2f\u4e3a\u201c\u95f2\u4e91\u6f6d\u5f71\u201d\u53cd\u624b\u5288\u5411\u654c\u624b\u53cc\u81c2\u3001\u53cc\u8098\u3002 \u3000\u3000\u201c\u5927\u884d\u5341\u5f0f\u201d\u662f\u4e00\u53e3\u6c14\u65bd\u51fa\uff0c\u975e\u5f97\u5feb\u6377\u4e0d\u53ef\uff0c\u662f\u4ee5\u62db\u5f0f\u672a\u6ee1\uff0c\u7acb\u5373\u6536\u62db\uff0c\u5a01\u529b\u53cd\u800c \u5927\u5f97\u51fa\u5947\u3002 \u3000\u3000\u7126\u5316\u4e0d\u60f3\u8f9b\u6377\u5251\u5f0f\u5982\u6b64\u7cbe\u5999\uff0c\u773c\u770b\u5c31\u8981\u8eb2\u5f00\uff0c\u5219\u8f9b\u6377\u5fc5\u53ef\u8131\u79bb\u9669\u5883\uff0c\u5fc3\u4e2d\u6697\u6697\u5fd6\u9053\uff1a \u201c\u8fd9\u2018\u4e03\u5999\u795e\u541b\u2019\u529f\u529b\u76d6\u4e16\uff0c\u4eca\u65e5\u5982\u6b64\u826f\u673a\uff0c\u4e0d\u5982\u62fc\u547d\u5c06\u4ed6\u5e9f\u4e86\uff0c\u4e5f\u5c11\u53bb\u4e00\u4e2a\u52b2\u654c\u3002\u201d \u3000\u3000\u5929\u6b8b\u5929\u6027\u5f3a\u608d\uff0c\u5fc3\u5ff5\u65e2\u5b9a\uff0c\u602a\u543c\u4e00\u58f0\uff0c\u4f38\u51fa\u5de8\u7075\u4e4b\u638c\uff0c\u786c\u8fce\u8f9b\u6377\u653b\u6765\u5251\u5f0f\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u62db\u4e8c\u5f0f\u76ee\u7684\u662f\u8981\u903c\u5f00\u53cc\u715e\u597d\u8131\u5f00\u9669\u5883\uff0c\u662f\u4ee5\u6536\u53d1\u5feb\u6377\uff0c\u5185\u529b\u5e76\u6ca1\u6709\u4fbf\u4f1a\u3002\u8fd9 \u65f6\u89c1\u7126\u5316\u63a2\u624b\u786c\u593a\u957f\u5251\uff0c\u5fc3\u4e2d\u5927\u60ca\uff0c\u5f39\u6307\u4e4b\u95f4\uff0c\u7126\u5316\u5df2\u63e1\u4f4f\u5251\u8eab\u3002 \u3000\u3000\u8f9b\u6377\u95f7\u54fc\u4e00\u58f0\uff0c\u5185\u529b\u6597\u53d1\uff0c\u5251\u8eab\u4e00\u9635\u52a8\u8361\uff0c\u4f46\u95fb\u53ee\u5f53\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u7126\u5316\u7684\u624b\u7adf\u50cf \u662f\u91d1\u5c5e\u6240\u94f8\uff01 \u3000\u3000\u7126\u52b3\u63e1\u5f97\u826f\u673a\uff0c\u4e00\u638c\u5411\u8f9b\u6377\u9762\u90e8\u62b9\u53bb\u3002 \u3000\u3000\u201c\u5580\u64e6\u201d\u4e00\u58f0\uff0c\u957f\u5251\u5df2\u88ab\u7126\u5316\u786c\u786c\u6273\u65ad\uff0c\u8f9b\u6377\u4e00\u4ef0\u8eab\uff0c\u907f\u8fc7\u7126\u52b3\u4e00\u62db\u3002 \u3000\u3000\u638c\u98ce\u5439\u5904\uff0c\u8499\u9762\u624b\u5dfe\u98d8\u7a7a\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u4f38\u624b\u4e00\u6293\uff0c\u63c9\u7740\u4e00\u56e2\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u5de6\u624b\u62c2\u8896\u906e\u4f4f\u9762\u5b54\uff0c\u4f3c\u4e4e\u4e0d\u613f\u8ba9\u4eba\u770b\u89c1\u4ed6\u7684 \u9762\u76ee\u2014\u2014\u5176\u5b9e\u8f9b\u6377\u5c31\u662f\u4e0d\u906e\u4f4f\u4e5f\u4e0d\u4f1a\u600e\u4e48\u6837\uff0c\u8fd9\u53ea\u4e0d\u8fc7\u662f\u4e0b\u610f\u8bc6\u7684\u52a8\u4f5c\u800c\u5df2\uff0c\u5f53\u4e00\u4e2a\u4eba \u5728\u8499\u9762\u4e2d\u88ab\u4eba\u63ed\u5f00\u9762\u5177\uff0c\u4e00\u5b9a\u4f1a\u7528\u624b\u906e\u4f4f\u9762\u5b54\uff0c\u867d\u7136\u6ca1\u6709\u4ec0\u4e48\u7528\u5904\uff0c\u53ea\u4e0d\u8fc7\u662f\u5fc5\u7136\u7684\u52a8 \u4f5c\u800c\u5df2 \u3000\u3000\u9ad8\u624b\u8fc7\u62db\uff0c\u6beb\u5398\u4e4b\u5dee\u5373\u5931\u826f\u673a\uff0c\u7126\u5316\u51f6\u6027\u5927\u53d1\uff0c\u72c2\u543c\u4e00\u58f0\uff0c\u4e00\u5934\u649e\u5411\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u7a81\u89c9\u52b2\u98ce\u88ad\u4f53\uff0c\u773c\u89d2\u4e00\u98d8\uff0c\u773c\u89c1\u7126\u5316\u4e00\u5934\u649e\u6765\uff0c\u5982\u679c\u4e0d\u907f\uff0c\u5219\u975e\u91cd\u4f24\u4e0d\u53ef\uff0c\u4f46 \u8eb2\u907f\u9664\u4e86\u540e\u9000\u5916\uff0c\u522b\u65e0\u4ed6\u6cd5\uff01\u7535\u5149\u706b\u77f3\u95f4\uff0c\u8f9b\u6377\u4e07\u5206\u65e0\u5948\u7684\u5411\u540e\u5012\u7eb5\uff0c\u8eab\u4f53\u51cc\u7a7a\u65f6\uff0c\u7528 \u5185\u529b\u6296\u624b\u6253\u51fa\u90a3\u4e00\u56e2\u624b\u5dfe\uff0c\u5e76\u4e14\u95ea\u7535\u822c\u4f38\u51fa\u5de6\u624b\u60f3\u52fe\u4f4f\u5bf9\u624b\u786c\u7ffb\u4e0a\u6765\u3002 \u3000\u3000\u90a3\u77e5\u7126\u5316\u65e9\u6599\u5982\u662f\uff0c\u53f3\u624b\u4e00\u7ffb\uff0c\u7adf\u7528\u201c\u5c0f\u64d2\u62ff\u624b\u201d\u53cd\u6263\u8f9b\u6377\u8109\u95e8\u3002\u7126\u52b3\u4f38\u624b\u63a5\u4f4f\u90a3 \u56e2\u5e03\u5dfe\uff0c\u624b\u5fc3\u7adf\u89c9\u5b9b\u5982\u9524\u51fb\uff01\u8f9b\u6377\u6b64\u8ba1\u4e0d\u6210\uff0c\u53ea\u597d\u677e\u624b\uff0c\u843d\u4e0b\u5c71\u5d16\uff0c\u773c\u89d2\u5374\u98d8\u89c1\u7126\u5316\u7684 \u624b\u638c\u4e0a\u8840\u6d41\u6bb7\u7ea2\uff0c\u76ae\u8089\u7ffb\u5377\uff0c\u60f3\u662f\u786c\u593a\u957f\u5251\u7684\u7ed3\u679c\u3002 \u3000\u3000\u8f9b\u6377\u4ef0\u9762\u4e00\u770b\uff0c\u8eab\u4f53\u5df2\u843d\u4e0b\u6570\u4e08\uff0c\u4f46\u4ecd\u53ef\u89c1\u53cc\u715e\u4e24\u5f20\u4e11\u6076\u7684\u8138\u4f38\u51fa\u5d16\u8fb9\u5411\u4e0b\u4fef\u89c6\uff0c \u5fc3\u4e2d\u6012\u6781\uff0c\u53cd\u800c\u957f\u53f9\u4e00\u58f0\uff0c\u60f3\u5230\u81ea\u5df1\u62a5\u4ec7\u672a\u6210\u8eab\u5148\u6b7b\uff0c\u4e0d\u89c9\u6709\u70b9\u60b2\u4ece\u4e2d\u6765\u2014\u2014 \u3000\u3000\u5d16\u4e0a\u4f20\u6765\u4e00\u9635\u5f97\u610f\u7684\u602a\u7b11\uff0c\u4f46\u90a3\u7b11\u58f0\u8d8a\u6765\u8d8a\u8fdc\uff0c\u4e5f\u4e0d\u77e5\u662f\u53cc\u715e\u79bb\u53bb\u4e86\uff0c\u8fd8\u662f\u81ea\u5df1\u8dcc \u79bb\u5d16\u8fb9\u8d8a\u6765\u8d8a\u8fdc\u2026\u2026 \u3000\u3000\u6c49\u6c34\u7684\u5357\u9762\uff0c\u957f\u6c5f\u7684\u4e24\u5cb8\uff0c\u5c31\u662f\u6b66\u6c49\u4e09\u9547\u7684\u53e6\u4e00\u8981\u9547\u2014\u2014\u6c49\u9633\u3002 \u3000\u3000\u6c49\u9633\u7684\u5317\u9762\u77d7\u7acb\u7740\u9f9f\u5c71\uff0c\u4e0e\u6b66\u660c\u7684\u86c7\u5c71\u9065\u9065\u76f8\u5bf9\uff0c\u6c49\u9633\u5317\u5cb8\u7684\u897f\u6708\u6e56\u4e43\u662f\u7fa4\u5ce6\u53e0\u7fe0 \u4e2d\u7684\u4e00\u4e2a\u5927\u6e56\uff0c\u6e56\u5149\u5c71\u8272\uff0c\u98ce\u666f\u5b9c\u4eba\uff0c\u6e56\u4e0a\u6709\u4e00\u5904\u4e0d\u5927\u4e0d\u5c0f\u7684\u5eb5\u5b50\uff0c\u5efa\u7b51\u5728\u4e00\u5927\u7247\u53e4\u7bc1 \u4e4b\u4e2d\uff0c\u53c8\u662f\u5728\u4e00\u7247\u5371\u5d16\u7684\u4e0a\u9762\uff0c\u6240\u4ee5\u4e0d\u4f46\u4eba\u8ff9\u7f55\u81f3\uff0c\u751a\u81f3\u6839\u672c\u6653\u5f97\u6709\u6b64\u5e99\u7684\u4eba\u90fd\u4e0d\u591a\u3002 \u3000\u3000\u662f\u79cb\u5929\u4e86\uff0c\u867d\u7136\u8273\u9633\u5f53\u7a7a\uff0c\u4f46\u90a3\u5c71\u5f84\u4e0a\u7684\u67af\u9ec4\u843d\u53f6\u65e0\u7591\u544a\u8bc9\u4e86\u4eba\u4eec\u590f\u5929\u5df1\u7ecf\u8fc7\u53bb\u4e86\u3002 \u3000\u3000\u9ec4\u660f\uff0c\u5915\u9633\u62d6\u7740\u4e07\u4e08\u7ea2\u5149\u6447\u6447\u6b32\u5760\uff0c\u5c42\u5c42\u7fe0\u7af9\u67d3\u4e0a\u4e86\u91d1\u9ec4\u7684\u53cd\u5149\uff0c\u90a3\u4e2a\u5eb5\u4e0a\u51cb\u65e7\u8131 \u843d\u7684\u6f06\u8282\u96d5\u7269\u4e5f\u88ab\u9633\u5149\u67d3\u4e0a\u4e00\u5c42\u5149\u91c7\uff0c\u597d\u50cf\u662f\u91cd\u65b0\u7c89\u5237\u8fc7\u4e00\u6837\uff0c\u5eb5\u95e8\u4e0a\u7684\u6a2a\u533e\u4e0a\u5199\u7740\u4e09 \u4e2a\u5b57\uff1a\u201c\u6c34\u6708\u5eb5\u201d\u3002 \u3000\u3000\u6a2a\u533e\u4e0b\u9762\uff0c\u6709\u4e00\u767d\u8863\u5c3c\u59d1\u501a\u95e8\u800c\u5750\uff0c\u4ece\u4fee\u957f\u7684\u5f71\u5b50\u4e0a\u4e5f\u53ef\u4ee5\u5206\u8fa8\u51fa\u5979\u90a3\u5a40\u5a1c\u8f7b\u76c8\u7684 \u4f53\u6001\u3002 \u3000\u3000\u5979\u53cc\u773c\u50cf\u4eba\u5b9a\u822c\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u53c8\u50cf\u662f\u5728\u51dd\u89c6\u7740\u6781\u9065\u8fdc\u7684\u5730\u65b9\uff0c\u90a3\u6e05\u6f88\u7684\u773c\u5149\u5374\u4f3c\u8499 \u8499\u5730\u5e26\u7740\u6cea\u73e0\uff0c\u5f2f\u800c\u957f\u7684\u776b\u6bdb\u4e0b\u662f\u4e00\u4e2a\u633a\u76f4\u800c\u5c0f\u5de7\u7684\u9f3b\u5b50\uff0c\u914d\u4e0a\u6a31\u6843\u822c\u7684\u5c0f\u5634\uff0c\u90a3\u5145\u6ee1 \u9752\u6625\u7684\u7f8e\u4e3d\u4e0e\u4e0a\u9762\u5149\u79c3\u7684\u5934\u9876\uff0c\u6210\u4e86\u5f3a\u70c8\u7684\u5bf9\u7167\u3002 \u3000\u3000\u5979\u7684\u76ae\u80a4\u662f\u90a3\u6837\u52a8\u4eba\uff0c\u886c\u7740\u4e00\u88ad\u767d\u8272\u7684\u4f5b\u8863\uff0c\u628a\u90a3\u5bbd\u5927\u7b80\u964b\u7684\u50e7\u8863\u90fd\u886c\u5f97\u597d\u770b\u4e86\u3002 \u8f89\u714c\u7684\u5915\u9633\u7167\u5728\u5979\u8eab\u4e0a\uff0c\u4f46\u5979\u7684\u5fc3\u5374\u5982\u540c\u8499\u5728\u4e07\u4ede\u539a\u7684\u973e\u96ea\u91cc\u3002 \u3000\u3000\u5979\u4ece\u90a3\u6676\u4eae\u7684\u6cea\u5149\u4e2d\uff0c\u4eff\u4f5b\u53c8\u770b\u89c1\u4e86\u90a3\u4e2a\u4fca\u7f8e\u7684\u8eab\u5f62\uff0c\u90a3\u6f47\u6d12\u7684\u8138\u988a\u4e0a\uff0c\u6df1\u60c5\u7684\u5927 \u773c\u775b\u2026\u2026 \u3000\u3000\u5979\u5fcd\u4e0d\u4f4f\u5583\u5583\u4f4e\u547c\uff1a\u201c\u6377\u54e5\u54e5\uff0c\u6377\u54e5\u54e5\u2026\u2026\u201d \u3000\u3000\u5979\u5c31\u662f\u91d1\u6885\u9f84\uff0c\u2014\u2014\u4e0d\uff0c\u5e94\u6838\u8bf4\u662f\u51c0\u83b2\u5973\u5c3c\u3002 \u3000\u3000\u5979\u7684\u773c\u5149\u843d\u5728\u897f\u5929\u90a3\u4e00\u5757\u6d6e\u4e91\uff0c\u4ece\u4e00\u4e2a\u83f1\u5f62\u987b\u81fe\u53d8\u6210\u4e86\u7403\u5f62\uff0c\u6700\u540e\u6210\u4e86\u4e0d\u6210\u5f62\u7684\u4eba \u5806\u3002\u5979\u5fc3\u4e2d\u6697\u6697\u60f3\u5230\uff1a\u201c\u53e4\u4eba\u8bf4\uff1a\u2018\u767d\u4e91\u82cd\u72d7\u2019\uff0c\u800c\u4e8b\u5b9e\u4e0a\u53c8\u4f55\u6b62\u767d\u4e91\u662f\u5982\u6b64\u5462\uff1f\u4e16\u4e0a \u7684\u4e8b\u90fd\u662f\u5728\u8fd9\u6837\u4ee4\u4eba\u4e0d\u5bdf\u89c9\u4e2d\u6e10\u6e10\u5730\u6539\u53d8\uff0c\u7b49\u5230\u4eba\u4eec\u53d1\u89c9\u51fa\u5b83\u7684\u6539\u53d8\u65f6\uff0c\u6614\u65f6\u7684\u4e00\u5207\u65e9 \u5c31\u70df\u6d88\u4e91\u6563\uff0c\u4e0d\u7559\u4e00\u4e1d\u75d5\u8ff9\u4e86\u3002\u201d \u3000\u3000\u5eb5\u5185\u4f20\u6765\u8001\u5e08\u7236\u7b03\u7b03\u7684\u6728\u9c7c\u58f0\uff0c\u66ff\u8fd9\u606c\u9759\u7684\u9ec4\u660f\u589e\u52a0\u4e86\u51e0\u5206\u5b89\u7965\u3002 \u3000\u3000\u5ffd\u5730\uff0c\u5979\u7684\u773c\u5149\u4e2d\u53d1\u73b0\u4e86\u4e00\u70b9\u9ed1\u5f71\uff0c\u5979\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u5c06\u776b\u6bdb\u4e0a\u7684\u6cea\u73e0\u63e9\u53bb\uff0c\u7741\u5927\u4e86 \u773c\u4e00\u770b\u2014\u2014\u5bf9\u9762\u5371\u5d16\u4e0a\u4e00\u4e2a\u9ed1\u5f71\u7ffb\u8df3\u4e86\u4e0b\u6765\uff0c\u5979\u5b9a\u795e\u4e00\u770b\uff0c\u554a\uff0c\u90a3\u662f\u4e00\u4e2a\u4eba\u5f71\uff0c\u5934\u4e0b\u811a \u4e0a\u5730\u7ffb\u8df3\u4e0b\u6765\u3002 \u3000\u3000\u5979\u77e5\u9053\u5bf9\u9762\u90a3\u5371\u5d16\u4e0b\u9762\u4e43\u662f\u5343\u4e08\u6df1\u6e0a\uff0c\u83ab\u8bf4\u8dcc\u843d\u4e0b\u53bb\uff0c\u5c31\u662f\u7ad9\u5728\u5d16\u8fb9\u5411\u4e0b\u4fef\u89c6\uff0c\u90a3 \u8f70\u9686\u6c34\u58f0\u4e5f\u4f1a\u4ee4\u4eba\u5fc3\u795e\u4ff1\u9707\uff0c\u76ee\u7729\u795e\u8ff7\uff0c\u8fd9\u4eba\u8dcc\u843d\u4e0b\u53bb\u54ea\u91cc\u8fd8\u4f1a\u6709\u547d\uff1f \u3000\u3000\u8fd9\u4e00\u60ca\uff0c\u51e0\u4e4e\u9ad8\u53eb\u51fa\u58f0\uff0c\u54ea\u77e5\u66f4\u602a\u7684\u4e8b\u53d1\u751f\u4e86\uff0c\u90a3\u4eba\u5728\u7a7a\u4e2d\u4e00\u7ffb\uff0c\u7acb\u523b\u5934\u4e0a\u811a\u4e0b\uff0c \u800c\u53cc\u811a\u9a6c\u4e0a\u4e00\u9635\u4e71\u52a8\uff0c\u521d\u770b\u5c1a\u4ee5\u4e3a\u662f\u8fd9\u4eba\u5782\u6b7b\u6323\u624e\uff0c\u4f46\u7ec6\u770b\u90a3\u4eba\u4e0b\u843d\u4e4b\u52bf\u7adf\u4f3c\u7f13\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u51c0\u83b2\u5bb6\u5b66\u6e0a\u535a\uff0c\u4e00\u770b\u5c31\u53d1\u73b0\u90a3\u4eba\u53cc\u811a\u4e43\u662f\u6309\u7740\u4e00\u79cd\u5965\u5999\u7684\u6b65\u5b50\u8e22\u51fa\uff0c\u662f\u4ee5\u5c06\u4e0b\u964d\u4e4b \u52bf\u7f13\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u4eba\u4e0d\u4ec5\u4e0b\u843d\u53d8\u7f13\uff0c\u800c\u4e14\u8eab\u4f53\u659c\u659c\u5411\u81ea\u5df1\u8fd9\u8fb9\u98d8\u4e86\u8fc7\u6765\uff0c\u8fd9\u5b9e\u662f\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\uff0c\u90a3 \u4eba\u8eab\u5728\u7a7a\u4e2d\u4e1d\u6beb\u4e0d\u80fd\u7740\u529b\u5730\u5c45\u7136\u5c06\u8fc5\u901f\u5782\u76f4\u4e0b\u843d\u4e4b\u52bf\u53d8\u4e3a\u7f13\u7f13\u659c\u659c\u98d8\u843d\uff0c\u90a3\u79cd\u8f7b\u529f\u771f\u5230 \u4e86\u4e0d\u53ef\u601d\u8bae\u7684\u5883\u754c\u4e86\u3002 \u3000\u3000\u811a\u4e0b\u662f\u5343\u4e08\u5cfb\u8c37\uff0c\u843d\u4e0b\u53bb\u4efb\u4f60\u795e\u4ed9\u4e4b\u8eab\u4e5f\u96be\u4e00\u6b7b\uff0c\u90a3\u4eba\u7f13\u7f13\u98d8\u5c06\u8fc7\u6765\uff0c\u60f3\u843d\u5728\u90a3\u7247 \u77f3\u7af9\u6797\u4e0a\u3002 \u3000\u3000\u5f53\u4ed6\u98d8\u843d\u5728\u7af9\u5c16\u513f\u4e0a\u7684\u65f6\u5019\uff0c\u4ed6\u542c\u5230\u7af9\u6797\u4e0b\u4e00\u58f0\u5973\u4eba\u7684\u5c16\u547c\uff0c\u90a3\u58f0\u97f3\u4f3c\u4e4e\u6709\u4e00\u79cd\u795e \u5947\u7684\u529b\u91cf\uff0c\u4ee4\u4ed6\u5fc3\u795e\u4e00\u9707\uff0c\u4f46\u4ed6\u77e5\u9053\u6b64\u65f6\u5168\u51ed\u63d0\u7740\u4e00\u53e3\u771f\u6c14\uff0c\u4e07\u4e07\u4e0d\u53ef\u5206\u795e\uff0c\u53ea\u542c\u4ed6\u957f \u5578\u4e00\u58f0\uff0c\u53cc\u8db3\u5728\u7af9\u5c16\u513f\u4e0a\u4e00\u9635\u7ed5\u5708\u75be\u884c\uff0c\u6b65\u5c65\u8eab\u6cd5\u5999\u4eba\u6beb\u5398\u2014\u2014 \u3000\u3000\u51c0\u83b2\u5973\u5c3c\u5f53\u90a3\u4eba\u98d8\u843d\u7af9\u5c16\u65f6\uff0c\u5df2\u80fd\u6e05\u695a\u5730\u770b\u89c1\u4ed6\u7684\u9762\u8c8c\uff0c\u8fd9\u4e00\u770b\uff0c\u767b\u65f6\u4ee4\u5979\u60ca\u53eb\u51fa \u58f0\uff0c\u5979\u5dee\u4e00\u70b9\u5c31\u8981\u558a\u51fa\uff1a\u201c\u6377\u54e5\u2026\u2026\u201d \u3000\u3000\u4f46\u5f53\u5979\u51e0\u4e4e\u558a\u51fa\u53e3\u7684\u65f6\u5019\uff0c\u5eb5\u91cc\u4f20\u51fa\u4e00\u58f0\u6e05\u4eae\u7684\u949f\u58f0\uff0c\u90a3\u53e4\u6734\u7684\u58f0\u54cd\u5728\u7fe0\u8c37\u8361\u6f3e\u4e0d \u5df2\uff0c\u5979\u50cf\u662f\u9661\u7136\u60ca\u9192\u8fc7\u6765\u3002\u5979\u60f3\u8d77\uff1a\u201c\u6211\u5df2\u51fa\u4e86\u5bb6\u505a\u4e86\u5c3c\u59d1\u554a\uff01\u201d \u3000\u3000\u4f46\u662f\u90a3\u7af9\u5c16\u4e0a\u7684\u4eba\uff0c\u90a3\u82f1\u4fca\u7684\u9762\u988a\uff0c\u84b2\u6d12\u7684\u8eab\u6001\uff0c\u6b63\u662f\u5979\u68a6\u5bd0\u4e0d\u5fd8\u7684\u201c\u6377\u54e5\u54e5\u201d\uff0c \u5979\u600e\u80fd\u4e0d\u5fc3\u4e2d\u5982\u72c2\uff1f \u3000\u3000\u5979\u4e0d\u77e5\u9053\u4e24\u6708\u4e0d\u89c1\u4f55\u4ee5\u6377\u54e5\u54e5\u7adf\u589e\u957f\u4e86\u8fd9\u8bb8\u591a\u529f\u529b\uff0c\u8fd9\u65f6\u4ed6\u53cc\u8db3\u4e0d\u505c\u7ed5\u5708\u800c\u5954\uff0c\u8eab \u4f53\u5374\u4e0d\u65ad\u76d8\u65cb\u800c\u4e0a\uff0c\u6700\u540e\u843d\u5728\u4e00\u6839\u6700\u9ad8\u7684\u7af9\u5c16\u4e0a\uff0c\u4ed6\u5355\u662f\u5fae\u5f2f\uff0c\u9661\u7136\u4e00\u62d4\uff0c\u8eab\u4f53\u501f\u7740\u90a3 \u76d8\u65cb\u800c\u4e0a\u4e4b\u52bf\uff0c\u5982\u5f39\u4e38\u822c\u98de\u5f39\u5411\u7a7a\u4e2d\u3002 \u3000\u3000\u5979\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u8f7b\u529f\u867d\u7136\u597d\uff0c\u4f46\u8981\u60f3\u8dc3\u4e0a\u8fd9\u5371\u5d16\uff0c\u53ef\u8fd8\u5dee\u5f97\u8fdc\u5440\uff01\u201d \u5979\u867d\u7136\u5c3d\u529b\u5fcd\u4f4f\u60ca\u53eb\u51fa\u58f0\uff0c\u4f46\u90a3\u5a07\u4e3d\u7684\u9762\u4e0a\u6ee1\u662f\u62c5\u5fe7\u7126\u6025\u4e4b\u8272\u3002 \u3000\u3000\u53ef\u662f\u4ed6\u5374\u7a33\u843d\u5728\u534a\u5d16\u58c1\u4e0a\uff0c\u6562\u60c5\u5d16\u58c1\u867d\u8bf4\u5e73\u6ed1\uff0c\u603b\u4e0d\u514d\u51f9\u51f8\u91cd\u91cd\uff0c\u662f\u4ee5\u4f30\u91cf\u843d\u5728\u51f8 \u51fa\u7684\u77f3\u8fb9\u4e0a\uff0c\u8fdc\u770b\u7684\u4eba\u5c1a\u4ee5\u4e3a\u4ed6\u8d34\u5728\u58c1\u4e0a\u54e9\uff01 \u3000\u3000\u4ed6\u4ecd\u662f\u51ed\u4e00\u53e3\u771f\u6c14\uff0c\u65bd\u5c55\u51fa\u76d6\u4e16\u8f7b\u529f\uff0c\u4e00\u8dc3\u6570\u4e08\u5730\u7331\u8eab\u800c\u4e0a\uff0c\u90a3\u6f47\u6d12\u7684\u8eab\u5f62\u7ec8\u4e8e\u5c0f \u5f97\u770b\u4e0d\u89c1\u4e86\u3002 \u3000\u3000\u82e5\u662f\u544a\u8bc9\u522b\u4eba\u8fd9\u4e00\u5e55\u60c5\u5f62\uff0c\u4ed6\u7edd\u4e0d\u80af\u76f8\u4fe1\u4e16\u4e0a\u6709\u8fd9\u7b49\u8f7b\u529f\uff0c\u51c0\u83b2\u867d\u7136\u770b\u89c1\u4e86\uff0c\u4f46\u5979 \u6c38\u6ca1\u6709\u673a\u4f1a\u8bf4\u7ed9\u5916\u4eba\u542c\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u5e55\u795e\u5947\u8f7b\u529f\u7ed9\u5979\u7684\u9707\u52a8\u8fdc\u4e0d\u53ca\u5fc3\u7075\u4e0a\u7684\u538b\u8feb\uff0c\u6b64\u523b\u5979\u5446\u5446\u5730\u4e0d\u77e5\u6240\u63aa\uff0c \u5e76\u4e0d\u662f\u60f3\u7740\u90a3\u7edd\u4e16\u8f7b\u529f\uff0c\u800c\u662f\u60f3\u7740\u90a3\u4e2a\u79c0\u4fca\u7684\u5f71\u5b50\u3002 \u3000\u3000\u201c\u6377\u54e5\u54e5\uff0c\u6211\u4eec\u6c38\u522b\u4e86\uff0c\u5c31\u50cf\u90a3\u5d16\u4e0a\u7684\u4e91\u96fe\uff0c\u8f7b\u98ce\u5439\u6765\uff0c\u5c31\u6563\u5f97\u4e00\u4e1d\u4e0d\u5269\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u53ef\u662f\u6211\u6bd5\u7adf\u518d\u89c1\u4e86\u4f60\u4e00\u9762\uff0c\u867d\u7136\u90a3\u4e48\u5306\u5306\uff0c\u4f46\u6211\u5df2\u7ecf\u6ee1\u8db3\u4e86\u2026\u2026\u201d \u3000\u3000\u201c\u4ece\u6b64\u523b\u8d77\uff0c\u6211\u5c06\u662f\u4e00\u4e2a\u771f\u6b63\u7684\u4e16\u5916\u4e4b\u4eba\uff0c\u4e00\u5c18\u4e0d\u67d3\uff0c\u5fc3\u5982\u6b62\u6c34\uff0c\u81f3\u4e8e\u4f60\uff0c\u4f60\u8fd8\u6709 \u8bb8\u591a\u672a\u4e86\u7684\u4e8b\uff0c\u6211\u53ea\u80fd\u5929\u5929\u795d\u798f\u2026\u2026\u795d\u798f\u4f60\u4e00\u5207\u5e78\u798f\u2014\u2014\u4e00\u5207\u2014\u2014\u201d \u3000\u3000\u83b9\u4eae\u7684\u6cea\u73e0\u6cbf\u7740\u90a3\u7f8e\u4e3d\u7684\u8138\u988a\uff0c\u6ef4\u5728\u5730\u4e0a\uff0c\u970e\u65f6\u88ab\u5e72\u71e5\u7684\u6c99\u571f\u5438\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5979\u7ad9\u4e86\u8d77\u6765\uff0c\u4e3e\u6b65\u56f0\u96be\u5730\u7f13\u7f13\u8d70\u5165\uff0c\u90a3\u6d01\u767d\u7684\u5f71\u5b50\u4ecd\u8361\u6f3e\u5728\u6df1\u8c37\u4e2d\uff0c\u6b63\u5982\u4e00\u6735\u51c0\u6d01 \u7684\u83b2\u82b1\u2014\u2014\u50cf\u5979\u7684\u6cd5\u53f7\u4e00\u6837\u3002 \u3000\u3000\u5929\u5149\u4e00\u9ed1\uff0c\u592a\u9633\u843d\u8fc7\u4e86\u5d16\u58c1\uff0c\u8c37\u4e2d\u987f\u65f6\u5e7d\u6697\u4e0b\u6765\uff0c\u53ea\u6709\u897f\u6708\u6e56\u4e2d\u4ecd\u5012\u6620\u7740\u897f\u5929\u90a3\u4e00 \u89d2\u4f59\u8f89\u3002 \u3000\u3000\u90a3\u5371\u5d16\u4e0a\uff0c\u665a\u98ce\u88ad\u4eba\uff0c\u4ee4\u4eba\u751f\u5bd2\uff0c\u4e00\u6761\u4eba\u5f71\u5982\u7bad\u5c04\u4e86\u4e0a\u6765\u5012\u4e0d\u662f\u8bf4\u4ed6\u5feb\u5f97\u50cf\u7bad\uff0c\u800c \u662f\u4ed6\u90a3\u52c9\u5f3a\u767b\u4e0a\u5d16\u8fb9\u7684\u7d27\u5f20\u60c5\u5f62\u597d\u50cf\u662f\u4e00\u652f\u529b\u7aed\u7684\u7bad\u77e2\u3002 \u3000\u3000\u4ed6\u90a3\u4e0a\u5347\u4e4b\u52bf\u672c\u6765\u4e07\u96be\u4e0a\u5f97\u5d16\u8fb9\uff0c\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u4ed6\u53cc\u811a\u7a7a\u8361\u4e00\u4e0b\uff0c\u53cc\u81c2\u4e00\u62d4\uff0c\u8eab\u4f53 \u5df2\u4e0a\u4e86\u5d16\u8fb9\uff0c\u867d\u5219\u6709\u70b9\u4ed3\u4fc3\uff0c\u4f46\u8fd9\u79cd\u52bf\u5c3d\u53cd\u4e0a\u7684\u8eab\u6b65\uff0c\u5b9e\u662f\u6b66\u6797\u7f55\u89c1\u7684\u795e\u529f\u3002 \u3000\u3000\u4ed6\u7acb\u5b9a\u4e86\u8db3\uff0c\u957f\u957f\u5618\u4e86\u53e3\u6c14\uff0c\u6562\u60c5\u4ed6\u4e00\u53e3\u6c14\u63d0\u4f4f\u4e00\u76f4\u4e0d\u6562\u653e\uff0c\u6240\u4ee5\u903c\u5f97\u8138\u90e8\u6709\u70b9\u7ea2 \u4e86\uff0c\u4ed6\u5583\u5583\u81ea\u8bed\uff1a\u201c\u8fd9\u2018\u8bd8\u6469\u795e\u6b65\uff0c\u7aef\u7684\u5999\u7edd\u4eba\u5bf0\uff0c\u82e5\u4e0d\u662f\u9760\u5b83\uff0c\u6211\u6b64\u523b\u5b9a\u7136\u5df2\u7ecf\u4e27\u751f \u7edd\u58d1\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u8f6c\u8fc7\u8eab\u6765\uff0c\u4fef\u8eab\u5411\u4e0b\u671b\u4e86\u671b\uff0c\u90a3\u5d16\u4e0b\u4e91\u96fe\u8885\u8885\uff0c\u6df1\u4e0d\u89c1\u5e95\uff0c\u53ea\u542c\u5f97\u8c37\u5e95\u5c71\u6cc9 \u8f70\u8f70\u51b2\u51fb\u5c71\u77f3\u4e4b\u58f0\uff0c\u65b9\u624d\u81ea\u5df1\u501f\u811a\u4e0a\u7eb5\u4e4b\u5904\uff0c\u5df2\u662f\u4e91\u6df1\u4e0d\u77e5\u5904\u4e86\uff0c\u4ed6\u6697\u9053\uff1a\u201c\u4e0d\u662f\u90a3\u4e00 \u7247\u7af9\u6797\uff0c\u518d\u597d\u7684\u529f\u592b\uff0c\u4e5f\u8981\u4e27\u751f\u5728\u53cc\u715e\u7684\u624b\u4e2d\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6b63\u5728\u56de\u60f3\u65b9\u624d\u90a3\u4e00\u58f0\u5a07\u547c\uff0c\u90a3\u547c\u58f0\u4e2d\u5145\u6ee1\u7740\u7126\u6025\uff0c\u60ca\u8bb6\uff0c\u662f\u90a3\u4e48\u719f\u6089\u5475\uff01\u4f46\u662f\u65b9 \u624d\u4ed6\u6b63\u786c\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u65e0\u6687\u65c1\u987e\uff0c\u5982\u4eca\u770b\u6765\uff0c\u8fd9\u7edd\u58c1\u6df1\u6e0a\u4e0b\u96be\u9053\u6709\u4eba\u5c45\u4f4f\u4e48\uff1f\u4e0d\u53ef\u80fd\u7684\uff01 \u90a3\u547c\u58f0\u662f\u5e7b\u89c9\u5427\uff1f \u3000\u3000\u4ed6\u8ff7\u5076\u5730\u6447\u4e86\u6447\u5934\uff0c\u4f4e\u58f0\u81ea\u8a00\uff1a\u201c\u6885\u9f84\u554a\uff01\u4f60\u5728\u54ea\u91cc\u5462\u2026\u2026\u201d \u3000\u3000\u90a3\u832b\u832b\u96fe\u6c14\u4e2d\u5ffd\u7136\u73b0\u51fa\u4e86\u4e00\u4e2a\u5a07\u8273\u6e29\u67d4\u7684\u59d1\u5a18\uff0c\u6df1\u60c5\u5730\u770b\u7740\u4ed6\uff0c\u4ed6\u5dee\u4e9b\u513f\u6251\u4e86\u4e0b\u53bb \u2014\u2014 \u3000\u3000\u5ffd\u7136\u90a3\u7f8e\u4e3d\u7684\u9762\u5b54\u53d8\u6210\u4e86\u4e24\u4e2a\u4e11\u6076\u65e0\u6bd4\u7684\u4eba\u5934\uff0c\u4ed6\u731b\u7136\u6536\u4f4f\u81ea\u5df1\u5f80\u5d16\u4e0b\u51b2\u53bb\u7684\u52bf\u5b50\uff0c \u7531\u4e8e\u6536\u52bf\u8fc7\u4e8e\u6025\u4fc3\uff0c\u4e00\u5757\u62f3\u5927\u7684\u77f3\u5757\u88ab\u8e22\u4e0b\u4e86\u5d16\uff0c\u7247\u523b\u6d88\u5931\u5728\u4e91\u96fe\u4e2d\uff0c\u8fde\u843d\u5165\u8c37\u5e95\u7684\u58f0 \u97f3\u90fd\u542c\u4e0d\u89c1\u3002 \u3000\u3000\u4ed6\u731b\u5730\u60ca\u8d77\uff0c\u9ed8\u9ed8\u81ea\u8d23\u2014\u2014\u201c\u8f9b\u6377\u554a\uff0c\u8f9b\u6377\u554a\uff0c\u4f60\u600e\u4e48\u5982\u6b64\u7cca\u6d82\u5462\uff1f\u6740\u7236\u6bcd\u7684\u4ec7\u4e0d \u62a5\uff0c\u6ee1\u8111\u5b50\u5c3d\u662f\u8fd9\u4e9b\u7eb7\u4e71\u7684\u60c5\u4e1d\uff0c\u8fd8\u6709\u6885\u53d4\u53d4\u7684\u4f7f\u547d\uff0c\u4faf\u4e8c\u53d4\u7684\u6df1\u4ec7\u2014\u2014\u201d \u3000\u3000\u4ed6\u60f3\u5230\u8fd9\u91cc\uff0c\u771f\u662f\u6c57\u6d41\u5939\u80cc\uff0c\u867d\u7136\u665a\u98ce\u9635\u9635\u9001\u51c9\uff0c\u4f46\u4ed6\u7d27\u634f\u4e86\u634f\u6ee1\u662f\u51b7\u6c57\u7684\u62f3\u5934\uff0c \u8eab\u5f62\u5b9b\u5982\u4e00\u7f15\u6e05\u70df\u822c\u6d88\u5931\u5728\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u7684\u91cd\u73b0\u6c5f\u6e56\uff0c\u6d77\u5929\u53cc\u715e\u7684\u4e24\u5ea6\u65bd\u51f6\uff0c\u6b66\u6c49\u771f\u6210\u4e86\u6ee1\u57ce\u98ce\u96e8\u7684\u60c5\u51b5\u3002\u52a0\u4e0a\u6b66 \u5f53\u3001\u5d06\u5cd2\u4e24\u5927\u6d3e\u95e8\u4eba\u7684\u4e92\u76f8\u706b\u62fc\uff0c\u654f\u611f\u7684\u4eba\u90fd\u9884\u6599\u5230\u53c8\u4e00\u6b21\u8165\u98ce\u8840\u96e8\u5c06\u88ad\u6b66\u6797\u4e86\u3002 \u3000\u3000\u94f6\u67aa\u5b5f\u4f2f\u8d77\u548c\u91d1\u5f13\u795e\u5f39\u8303\u6cbb\u6210\u88ab\u6740\u4e86\u4e4b\u540e\uff0c\u6b66\u6c49\u4e00\u5e26\u6240\u6709\u7684\u9556\u5c40\u5168\u5173\u4e86\u95e8\uff0c\u5927\u5bb6\u90fd \u4ee5\u4e3a\u6d77\u5929\u53cc\u715e\u7684\u4e1c\u5c71\u518d\u8d77\u5fc5\u7136\u6709\u66f4\u5389\u5bb3\u7684\u4e8b\u4ef6\u53d1\u751f\uff0c\u4f46\u4ece\u8303\u6cbb\u6210\u88ab\u6740\u7684\u4e00\u591c\u540e\uff0c\u6d77\u5929\u53cc \u715e\u53c8\u8eab\u5f71\u6d88\u5931\u4e86\u3002 \u3000\u3000\u6c5f\u6e56\u4e0a\u5145\u6ee1\u7740\u4eba\u5fc3\u60f6\u60f6\u7684\u60c5\u51b5\u3002 \u3000\u3000\u53c8\u662f\u5728\u9ec4\u660f\u7684\u65f6\u5019\u3002\u5b89\u5fbd\u5b98\u9053\u4e0a\u51fa\u73b0\u4e86\u4e00\u4e2a\u5b64\u5355\u7684\u4eba\u5f71\uff0c\u4e0d\uff0c\u5e94\u8be5\u8bf4\u662f\u4e00\u4eba\u4e00\u9a91\u3002 \u90a3\u5339\u9a6c\u901a\u4f53\u5168\u767d\uff0c\u65e0\u4e00\u6839\u6742\u6bdb\uff0c\u5f02\u5e38\u795e\u9a8f\uff0c\u9a6c\u4e0a\u7684\u4eba\u5374\u900f\u7740\u53e4\u602a\uff0c\u4e00\u8eab\u6574\u6d01\u7684\u6de1\u9752\u5112\u670d\uff0c \u5728\u6eda\u6eda\u9ec4\u6c99\u4e2d\u7adf\u662f\u4e00\u5c18\u4e0d\u67d3\uff0c\u800c\u4e14\u80cc\u4e0a\u659c\u80cc\u4e00\u53ea\u957f\u5251\u3002 \u3000\u3000\u5982\u679c\u4f60\u4ed4\u7ec6\u770b\u4e00\u4e0b\uff0c\u4f60\u5b9a\u7136\u60ca\u5947\u90a3\u9a6c\u4e0a\u5112\u751f\u662f\u90a3\u4e48\u79c0\u4fca\u6f47\u6d12\uff0c\u800c\u4e14\u8138\u8272\u767d\u4e2d\u900f\u7740\u7ea2 \u6da6\uff0c\u771f\u6240\u8c13\u201c\u9f99\u884c\u864e\u8dc3\u201d\uff0c\u663e\u7136\u662f\u6709\u4e86\u6781\u6df1\u539a\u5185\u529f\u7684\u73b0\u8c61\u3002 \u3000\u3000\u9a6c\u8e44\u7684\u7684\uff0c\u5954\u5f97\u751a\u75be\uff0c\u5ffd\u5730\u4ed6\u8f7b\u54fc\u4e00\u58f0\uff0c\u4e00\u52d2\u8f94\u5934\uff0c\u90a3\u9a6c\u7aef\u7684\u795e\u9a8f\uff0c\u5237\u5730\u4e00\u4e0b\u5c31\u5c06 \u75be\u9a70\u4e4b\u52bf\u5b9a\u4f4f\uff0c\u5112\u751f\u53cc\u773c\u76ef\u5728\u8def\u65c1\u4e00\u68f5\u5927\u6866\u6811\u4e0a\u3002 \u3000\u3000\u90a3\u6811\u5e72\u4e0a\u523b\u7740\u4e00\u652f\u957f\u5251\uff0c\u5251\u5c16\u6307\u5411\u5317\u65b9\u3002\u90a3\u5251\u523b\u7684\u5341\u5206\u8f7b\u6d45\uff0c\u82e5\u4e0d\u7559\u610f\u5b9a\u7136\u4e0d\u6613\u53d1 \u89c9\uff0c\u6b64\u65f6\u5929\u8272\u5df2\u6697\uff0c\u9a6c\u5954\u53c8\u901f\uff0c\u4e0d\u77e5\u90a3\u4e66\u751f\u600e\u5730\u4e00\u77a5\u773c\u5c31\u80fd\u770b\u6e05\u695a\u4e86\u3002 \u3000\u3000\u4ed6\u4ef0\u8d77\u5934\u770b\u4e86\u770b\u5929\uff0c\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u5434\u5927\u54e5\u4e00\u8def\u7559\u8bb0\u8981\u6211\u5317\u4e0a\uff0c\u5b9a\u7136\u662f\u6709\u6240\u53d1\u73b0\uff0c \u53ea\u662f\u73b0\u5728\u5929\u8272\u5df2\u665a\uff0c\u53ea\u597d\u5148\u627e\u4e2a\u5730\u65b9\u5bbf\u4e0a\u4e00\u591c\u3002\u201d \u3000\u3000\u90a3\u77e5\u771f\u4e0d\u51d1\u5de7\uff0c\u8fd9\u4e00\u6bb5\u9053\u8def\u751a\u4e3a\u8352\u51c9\uff0c\u4ed6\u7b56\u9a6c\u8dd1\u4e86\u4e00\u91cc\u591a\u8def\uff0c\u4e0d\u4f46\u6ca1\u6709\u5ba2\u6808\uff0c\u8fde\u4e2a \u519c\u5bb6\u90fd\u6ca1\u6709\uff0c\u53ea\u6709\u8def\u65c1\u4e00\u8fde\u4e32\u7684\u8352\u571f\uff0c\u591c\u8885\u4e0d\u65f6\u5495\u5495\u5c16\u557c\uff0c\u4ee4\u4eba\u6bdb\u53d1\u76f4\u7acb\u3002 \u3000\u3000\u5929\u76ca\u53d1\u9ed1\u4e86\u3002\u56db\u5468\u66f4\u50cf\u662f\u7279\u522b\u9759\uff0c\u90a3\u9a6c\u8e44\u6251\u6251\u6253\u5728\u571f\u8def\u4e0a\u7684\u58f0\u97f3\uff0c\u4e5f\u663e\u5f97\u5639\u4eae\u523a\u8033 \u8d77\u6765\uff0c\u9a6c\u4e0a\u7684\u5112\u751f\u867d\u4e0d\u80fd\u8bf4\u5bb3\u6015\uff0c\u81f3\u5c11\u751a\u662f\u7126\u6025\u3002 \u3000\u3000\u5ffd\u7136\u4e0d\u8fdc\u5904\u7adf\u53d1\u51fa\u4e00\u58f0\u51c4\u5389\u7684\u5636\u58f0\uff0c\u90a3\u58f0\u97f3\u867d\u7136\u4e0d\u5927\uff0c\u4f46\u9001\u4eba\u8033\u5185\u4ee4\u4eba\u6d51\u8eab\u4e0d\u5feb\uff0c \u4e00\u79cd\u7d27\u5f20\u5fc3\u60c5\u6cb9\u7136\u800c\u751f\u3002 \u3000\u3000\u5594\u5730\u4e00\u58f0\uff0c\u90a3\u5636\u58f0\u53c8\u8d77\uff0c\u4f46\u4ece\u58f0\u97f3\u4e0a\u8fa8\u51fa\u6bd4\u65b9\u624d\u90a3\u58f0\u5df2\u8fd1\u4e86\u6570\u4e08\u800c\u51c4\u5389\u4e4b\u58f0\u5212\u7834\u957f \u7a7a\uff0c\u5468\u56f4\u53c8\u662f\u8fde\u5c71\u8352\u575f\uff0c\u6708\u5149\u867d\u6709\uff0c\u5374\u6de1\u5f97\u5f88\uff0c\u5012\u628a\u4e00\u4e9b\u9732\u5728\u5916\u9762\u7684\u7834\u68fa\u6728\u7167\u5f97\u6050\u6016\u5f02 \u5e38\u3002 \u3000\u3000\u90a3\u9a6c\u513f\u4f3c\u4e5f\u60ca\u4e8e\u8fd9\u53ef\u6016\u60c5\u666f\uff0c\u6b65\u5b50\u81ea\u7136\u5730\u653e\u6162\u4e0b\u6765\u3002 \u3000\u3000\u7b2c\u4e09\u58f0\u602a\u54cd\u5904\uff0c\u5112\u751f\u9a6c\u4e0a\u77a7\u89c1\u4e86\u4e24\u4e2a\u4eba\u5f71\u3002\u4e24\u4e2a\u53c8\u7626\u53c8\u957f\u7684\u4eba\u5f62\uff0c\u90fd\u662f\u4e00\u88ad\u767d\u886b\uff0c \u4e0a\u9762\u5168\u662f\u9ebb\u5e03\u8865\u9489\uff0c\u602a\u7684\u662f\u5934\u4e0a\u90fd\u6234\u7740\u4e00\u9876\u5927\u7ea2\u9ad8\u5e3d\uff0c\u52a0\u4e0a\u7626\u957f\u7684\u8eab\u6750\uff0c\u7adf\u6709\u4e08\u591a\u9ad8\u3002 \u4e24\u4e2a\u8138\u5b54\u90fd\u662f\u4e00\u6a21\u4e00\u6837\uff0c\u9ec4\u8721\u822c\u7684\u989c\u8272\uff0c\u53cc\u773c\u9f13\u51fa\uff0c\u90a3\u9634\u68ee\u68ee\u5730\u6837\u5b50\u54ea\u6709\u4e00\u4e1d\u4eba\u76f8\uff1f \u3000\u3000\u4e24\u4eba\u5e76\u80a9\u75be\u9a70\uff0c\u53cc\u819d\u7adf\u7136\u4e0d\u5f2f\uff0c\u5c31\u4f3c\u98d8\u8fc7\u6765\u7684\u4e00\u6837\uff0c\u6240\u81f3\u5904\uff0c\u591c\u67ad\u4e0d\u4f4f\u5c16\u557c\uff0c\u76ca\u589e \u53ef\u6016\u4e4b\u611f\u3002 \u3000\u3000\u9a6c\u4e0a\u5112\u751f\u5f3a\u81ea\u9547\u5b9a\uff0c\u4f46\u5750\u4e0b\u4e4b\u9a6c\u5374\u4f3c\u4e3a\u8fd9\u4e24\u9b3c\u9634\u68ee\u4e4b\u52bf\u6240\u6151\uff0c\u8fde\u8fde\u9000\u540e\u3002 \u3000\u3000\u4e24\u9b3c\u77ac\u65f6\u5373\u81f3\uff0c\u9634\u98ce\u6251\u9762\uff0c\u5112\u751f\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u5664\uff0c\u4ed6\u53cc\u624b\u7d27\u634f\u9a6c\u978d\uff0c\u80cc\u4e0a\u51b7\u6c57\u5982\u96e8\uff0c \u4f46\u4ed6\u5230\u5e95\u5f3a\u81ea\u58ee\u80c6\uff0c\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f55\u65b9\u5996\u4eba\u88c5\u9b3c\u54e7\u552c\u4eba\uff0c\u6211\u8f9b\u6377\u5728\u6b64\uff01\u201d \u3000\u3000\u201c\u8f9b\u6377\u201d\u8fd9\u540d\u5b57\u53c8\u4e0d\u662f\u201c\u949f\u9997\u201d\uff0c\u53eb\u51fa\u6765\u6709\u4f55\u7528\uff1f\u4f46\u4eba\u5230\u4e86\u5bb3\u6015\u7684\u65f6\u5019\uff0c\u5f80\u5f80\u6545\u610f \u5927\u58f0\u53f1\u559d\uff0c\u4ee5\u58ee\u58f0\u8272\u3002 \u3000\u3000\u4f46\u8fd9\u4e00\u559d\u4e43\u662f\u5185\u5bb6\u771f\u6c14\u6240\u805a\uff0c\u56db\u5468\u7a7a\u6c14\u5374\u88ab\u9707\u5f97\u55e1\u55e1\u54cd\u3002\u4e24\u9b3c\u76f8\u5bf9\u4e00\u89c6\uff0c\u5df1\u98d8\u7136\u800c \u8fc7\uff0c\u53ea\u542c\u5f97\u5de6\u9762\u4e00\u9b3c\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u6211\u8bf4\u4f60\u770b\u8d70\u4e86\u773c\u5427\uff0c\u4eba\u5bb6\u5df2\u505a\u5230\u6536\u655b\u773c\u795e\u7684\u5730\u6b65\u4e86\uff0c \u8fd8\u6015\u54b1\u4eec\u88c5\u9b3c\u8bc8\u5c38\u8fd9\u4e00\u624b\u4e48\uff1f\u5c31\u662f\u65b9\u624d\u90a3\u4e00\u58f0\u2018\u72ee\u543c\u2019\uff0c\u6ca1\u6709\u51e0\u5341\u5e74\u529f\u529b\u4e5f\u505a\u4e0d\u5230\u54e9\uff01\u201d \u3000\u3000\u53f3\u9762\u4e00\u9b3c\u55ef\u4e86\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u5feb\u8d70\u5427\uff01\u201d\u58f0\u97f3\u4f20\u65f6\u5df2\u53bb\u5f97\u8fdc\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u56de\u5934\u671b\u4e86\u671b\u8fd9\u4e24\u4e2a\u201c\u9b3c\u201d\uff0c\u5fc3\u4e2d\u867d\u89c9\u6709\u70b9\u5fff\u6012\uff0c\u4f46\u4e5f\u6709\u4e00\u70b9\u8f7b\u677e\u611f\u89c9\uff0c\u4ed6\u4f4e\u5934 \u4e00\u770b\uff0c\u94c1\u9576\u8fb9\u7684\u9a6c\u978d\u7adf\u88ab\u634f\u6210\u4e00\u5757\u8584\u997c\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u6697\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u4e09\u5206\u50cf\u4eba\u4e03\u5206\u50cf\u9b3c\u7684\u5bb6\u4f19\uff0c\u8f7b\u529f\u7aef\u7684\u4e86\u5f97\uff0c\u4e0d\u77e5\u662f\u54ea\u4e00\u8def\u4eba \u7269\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u9762\u60f3\uff0c\u4e00\u9762\u624b\u4e2d\u4e0d\u77e5\u4e0d\u89c9\u52a0\u52b2\u63d0\u7740\u7f30\u7ef3\uff0c\u767d\u9a6c\u6492\u5f00\u56db\u8e44\u5982\u98de\u75be\u9a70\u3002 \u3000\u3000\u8f9b\u6377\u81ea\u4ece\u83b7\u5f97\u4e16\u5916\u4e09\u4ed9\u4e4b\u9996\u5e73\u51e1\u4e0a\u4eba\u5782\u9752\u540e\uff0c\u529f\u529b\u589e\u4e86\u4f55\u6b62\u4e00\u500d\uff0c\u8fd9\u65f6\u867d\u7136\u6708\u5149\u9eef \u6de1\uff0c\u4f46\u4ed6\u76ee\u5149\u9510\u5229\u5f02\u5e38\uff0c\u65e9\u77a5\u89c1\u5de6\u9762\u6797\u5b50\u91cc\u900f\u51fa\u4e00\u89d2\u5ba4\u5b87\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4ed6\u4e0d\u89c9\u5927\u559c\uff0c\u8fde\u5fd9\u7b56\u9a6c\u524d\u53bb\uff0c\u8f6c\u5f2f\u62b9\u89d2\u5730\u7ed5\u5165\u6797\u5b50\uff0c\u679c\u89c1\u524d\u9762\u6709\u4e00\u6240\u5e99\u3002 \u3000\u3000\u6797\u5b50\u91cc\u66f4\u662f\u9ed1\u5f97\u5f88\uff0c\u8f9b\u6377\u628a\u9a6c\u62f4\u5728\u4e00\u68f5\u6811\u5e72\u4e0a\uff0c\u7f13\u7f13\u8d70\u8fd1\u90a3\u7834\u5e99\u95e8\u65f6\uff0c\u7adf\u81ea\u8fdf\u7591\u4f4f \u4e86\uff0c\u8fdf\u8fdf\u6ca1\u6709\u53bb\u63a8\u2014\u2014 \u3000\u3000\u7ec8\u4e8e\u4ed6\u4e00\u6307\u6572\u4e86\u4e0b\u53bb\uff0c\u90a3\u77e5\u5440\u7684\u4e00\u58f0\uff0c\u90a3\u95e8\u81ea\u6253\u5f00\uff0c\u539f\u6765\u6839\u672c\u5c31\u6ca1\u6709\u4e0a\u9501\u3002 \u3000\u3000\u5e99\u5185\u9ed1\u5f97\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\uff0c\u800c\u4e14\u900f\u51fa\u4e00\u80a1\u9709\u70c2\u7684\u5473\u9053\uff0c\u54ea\u50cf\u662f\u6709\u4eba\u4f4f\u7684\u5730\u65b9\u3002 \u3000\u3000\u8f9b\u6377\u540e\u811a\u624d\u8de8\u5165\u95e8\u69db\uff0c\u4f38\u624b\u6b63\u5f85\u638f\u53d6\u706b\u647a\u5b50\uff0c\u5ffd\u7136\u547c\u5730\u4e00\u58f0\uff0c\u5df1\u6709\u4e00\u7269\u88ad\u5230\u2014\u2014\u8f9b \u6377\u4f38\u8fdb\u676f\u4e2d\u7684\u624b\u90fd\u4e0d\u53ca\u62ff\u51fa\uff0c\u53cc\u811a\u4e0d\u52a8\uff0c\u8eab\u5b50\u731b\u5411\u540e\u4e00\u4ef0\uff0c\u4e0a\u8eab\u4e0e\u4e0b\u8eab\u6210\u4e86\u76f4\u89d2\uff0c\u90a3\u88ad \u6765\u4e4b\u7269\u5982\u662f\u6697\u5668\u7684\u8bdd\uff0c\u4e00\u5b9a\u98de\u8fc7\u53bb\u843d\u4e86\u7a7a\u4f46\u662f\u5e76\u6ca1\u6709\u6697\u5668\u98de\u8fc7\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8f9b\u6377\u8eab\u5f62\u624d\u52a8\uff0c\u8179\u4e0b\u53c8\u611f\u53d7\u88ad\uff0c\u8fd9\u4e00\u4e0b\u8f9b\u6377\u7acb\u523b\u660e\u767d\u90a3\u8fde\u88ad\u81ea\u5df1\u4e4b\u7269\u4e43\u662f\u654c\u4eba\u7684\u624b\uff0c \u800c\u4e14\u53ef\u4ee5\u8fa8\u51fa\u662f\u53cc\u6307\u5e76\u7acb\u5982\u621f\u7684\u70b9\u7a74\u624b\u6cd5\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u6697\u60ca\u8fd9\u4eba\u9ed1\u6697\u4e2d\u8ba4\u7a74\u5c45\u7136\u5982\u6b64\u4e4b\u51c6\uff0c\u4f46\u624b\u4e0a\u5374\u6beb\u4e0d\u8fdf\u7591\u5730\u53cd\u628a\u4e0a\u53bb\uff0c\u8981\u62ff\u5bf9 \u65b9\u7684\u8109\u95e8\uff0c\u8fd9\u79cd\u5e94\u53d8\u7684\u7eaf\u719f\u4fd0\u843d\uff0c\u5b8c\u5168\u8868\u73b0\u51fa\u4ed6\u6df1\u539a\u529f\u529b\u53ca\u673a\u667a\u3002 \u3000\u3000\u5982\u679c\u4e0d\u662f\u5728\u8fd9\u6f06\u9ed1\u7684\u623f\u5b50\u4e2d\uff0c\u4f60\u5b9a\u53ef\u53d1\u89c9\u8f9b\u6377\u8fd9\u4e00\u6293\u4e94\u6307\u5206\u5f20\uff0c\u4e1d\u6beb\u4e0d\u5dee\u5730\u5206\u6263\u654c \u4eba\u8109\u4e0a\u7b4b\uff0c\u5355\u8fd9\u4efd\u529f\u529b\u5c31\u8fdc\u5728\u4e00\u822c\u6240\u8c13\u201c\u95ed\u76ee\u6362\u638c\u201d\u7684\u529f\u592b\u4e4b\u4e0a\u4e86\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\u867d\u770b\u4e0d\u89c1\uff0c\u90a3\u52a8\u624b\u88ad\u8f9b\u6377\u7684\u4eba\u81ea\u5df1\u53ef\u77e5\u9053\uff0c\u5bf9\u65b9\u968f\u624b\u4e00\u6293\uff0c\u81ea\u5df1\u8109\u4e0a\u4e94\u7b4b\u7acb \u523b\u53d7\u4f24\uff0c\u53ea\u542c\u4ed6\u54fc\u4e00\u58f0\uff0c\u63a5\u7740\u7830\u7684\u4e00\u4e0b\u95f7\u58f0\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u60ca\u9a87\u5730\u5012\u9000\u4e24\u6b65\uff0c\u56e0\u4e3a\u4ed6\u7684\u4e00\u628a\u6293\u4e0b\uff0c\u7adf\u6293\u4e86\u4e2a\u7a7a\uff0c\u800c\u4e14\u5bf9\u65b9\u4e0d\u77e5\u7528\u7684\u4e00 \u8bb0\u4ec0\u4e48\u602a\u62db\uff0c\u7adf\u5982\u6e38\u9c7c\u822c\u6ed1\u8fc7\u81ea\u5df1\u4e94\u6307\u9632\u7ebf\uff0c\u62cd\u5730\u6253\u5728\u4ed6\u5c0f\u8179\u4e0a\u2014\u2014 \u3000\u3000\u800c\u66f4\u4ee4\u4eba\u60ca\u5947\u7684\u662f\u8fd9\u4e00\u638c\u6253\u5f97\u6781\u662f\u8f6f\u5f31\u65e0\u529b\uff0c\u662f\u4ee5\u4ed6\u53ea\u611f\u5230\u4e00\u9635\u5fae\u75db\uff0c\u6839\u672c\u4e00\u70b9\u4e5f \u6ca1\u6709\u53d7\u4f24\u3002 \u3000\u3000\u4ed6\u6b63\u5446\u5446\u9000\u7acb\u65f6\uff0c\u5bf9\u65b9\u5df2\u559d\u9053\uff1a\u201c\u65e0\u803b\u8001\u8d3c\uff0c\u8fd8\u8981\u8d76\u5c3d\u6740\u7edd\u4e48\u2014\u2014\u201d\u58f0\u97f3\u5c16\u5ae9\uff0c\u4f3c \u4e4e\u8fd8\u6709\u4e00\u70b9\u7ae5\u58f0\uff0c\u63a5\u7740\u4e00\u9635\u5267\u70c8\u7684\u5598\u606f\u3002 \u3000\u3000\u8f9b\u6377\u6014\u4e86\u6014\uff0c\u4f46\u4ed6\u7684\u773c\u6674\u5df2\u80fd\u770b\u5230\u4e00\u4e2a\u6a21\u7cca\u7684\u8f6e\u5eca\uff0c\u6562\u60c5\u662f\u4ed6\u5df2\u6e10\u6e10\u4e60\u60ef\u4e86\u9ed1\u6697\u7684 \u7f18\u6545\u3002 \u3000\u3000\u867d\u7136\u770b\u4e0d\u771f\u5207\uff0c\u4f46\u4ed6\u5df2\u770b\u51fa\u90a3\u4eba\u534a\u8eba\u5728\u5730\u4e0a\uff0c\u7adf\u50cf\u662f\u8eab\u5bb3\u91cd\u75c5\u7684\u6837\u5b50\u3002 \u3000\u3000\u201c\u64e6\u201d\u5730\u4e00\u58f0\uff0c\u706b\u647a\u5b50\u8fd1\u98ce\u4e00\u6643\uff0c\u5c4b\u5185\u987f\u65f6\u4eae\u4e86\u8d77\u6765\uff0c\u8f9b\u6377\u56e0\u4e3a\u706b\u5728\u81ea\u5df1\u624b\u91cc\uff0c\u800c \u90a3\u4eba\u5728\u6697\u5904\uff0c\u662f\u4ee5\u4e00\u65f6\u770b\u4e0d\u89c1\u90a3\u4eba\uff0c\u800c\u90a3\u4eba\u5374\u60ca\u547c\u4e00\u58f0\u3002 \u3000\u3000\u8f9b\u6377\u5c06\u706b\u647a\u5411\u524d\u7565\u4f38\uff0c\u7acb\u523b\u53d1\u73b0\u8eba\u5728\u5730\u4e0a\u7684\u4e43\u662f\u4e00\u4e2a\u84ec\u9888\u57a2\u9762\u7684\u5c11\u5e74\uff0c\u770b\u6837\u5b50\u6709\u5341 \u4e94\u516d\u5c81\uff0c\u8eab\u4e0a\u7684\u8863\u886b\u66f4\u662f\u810f\u57a2\u6591\u6591\uff0c\u66f4\u517c\u5168\u662f\u8865\u9489\uff0c\u4e00\u526f\u5c0f\u53eb\u5316\u5b50\u7684\u6a21\u6837\uff0c\u8fd9\u65f6\u6b63\u7741\u7740 \u5927\u773c\u77aa\u7740\u8f9b\u6377\uff0c\u4f3c\u4e4e\u65e0\u9650\u60ca\u8bb6\u7684\u6837\u5b50\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u4e00\u76f4\u60ca\u4e8e\u65b9\u624d\u4ed6\u90a3\u4e00\u8bb0\u602a\u62db\uff0c\u8fd9\u65f6\u4e0d\u77e5\u4e0d\u89c9\u95f4\u6301\u706b\u8d70\u8fd1\u4e00\u6b65\uff0c\u7ec6\u7ec6\u4e00\u6253\u91cf \u6b64\u4eba\uff0c\u66f4\u662f\u6697\u4e2d\u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u6b64\u4eba\u867d\u7136\u84ec\u5934\u57a2\u9762\uff0c\u4f46\u7ec6\u770b\u4e4b\u4e0b\uff0c\u53ea\u89c1\u4ed6\u53cc\u7709\u4f3c\u5212\uff0c\u9f3b\u82e5\u60ac\u80c6\uff0c\u6731\u5507\u7693\u9f7f\uff0c\u8138 \u4e0a\u867d\u90fd\u662f\u5c18\u571f\uff0c\u4f46\u9888\u9879\u4e4b\u95f4\u5374\u9732\u51fa\u4e00\u6bb5\u5341\u5206\u7ec6\u5ae9\u7684\u76ae\u6276\uff0c\u4e00\u6d3e\u5bcc\u5bb6\u516c\u5b50\u7684\u6a21\u6837\u3002 \u3000\u3000\u8fd9\u65f6\u90a3\u5c11\u5e74\u5f00\u53e3\u9053\uff1a\u201c\u4f60\u662f\u5389\u8001\u8d3c\u7684\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6014\u4e86\u4e00\u6014\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4ec0\u4e48\u5389\u8001\u8d3c\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5e74\u6447\u4e86\u6447\u5934\u53c8\u9053\uff1a\u201c\u4f60\u771f\u4e0d\u662f\u5389\u8001\u8d3c\u6d3e\u6765\u8ffd\u2014\u2014\u554a\uff0c\u6211\u95ee\u4f60\uff0c\u4f60\u8fdb\u6765\u65f6\u771f\u4e0d\u77e5 \u9053\u6211\u5728\u91cc\u9762\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6697\u7b11\u9053\uff1a\u201c\u5c31\u662f\u6211\u771f\u662f\u4ec0\u4e48\u5389\u8001\u8d3c\u6d3e\u6765\u8ffd\u4f60\u7684\uff0c\u4e5f\u4e0d\u4e00\u5b9a\u5c31\u77e5\u9053\u4f60\u5728\u8fd9\u5e99\u4e2d \u5475\uff01\u201d \u3000\u3000\u4f46\u53e3\u4e0a\u5374\u7b54\u9053\uff1a\u201c\u6211\u54ea\u91cc\u8ba4\u8bc6\u4ec0\u4e48\u5389\u8001\u8d3c\u7684\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u4f3c\u4e4e\u662f\u52c9\u5f3a\u6491\u7740\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u542c\u8f9b\u6377\u5982\u6b64\u8bf4\uff0c\u8f7b\u53f9\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u90a3\u6211\u5c31\u653e\u5fc3 \u4e86\u3002\u201d\u5ffd\u7136\u4e00\u9635\u75c9\u631b\uff0c\u6251\u5730\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u54a6\u4e86\u4e00\u58f0\uff0c\u8d70\u8fd1\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u5c11\u5e74\u53cc\u7709\u7d27\u8e59\uff0c\u4f3c\u4e4e\u6781\u4e3a\u75db\u695a\uff0c\u8f9b\u6377\u4e0d\u7981\u6301\u706b \u5f2f\u4e0b\u8170\u53bb\u770b\u4e2a\u7a76\u7adf\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u60f3\u662f\u75db\u5f97\u5389\u5bb3\uff0c\u4e0d\u7981\u773c\u6cea\u4e5f\u75c5\u4e86\u51fa\u6765\uff0c\u4e24\u9053\u6cea\u6c34\u4ece\u8138\u4e0a\u6d41\u4e0b\uff0c\u5c06\u8138\u4e0a\u7070\u5c18\u51b2 \u6d17\u5e72\u51c0\uff0c\u987f\u65f6\u9732\u51fa\u4e24\u9053\u96ea\u767d\u7684\u80a4\u8272\u3002 \u3000\u3000\u8f9b\u6377\u770b\u8fd9\u5c11\u5e74\u5206\u660e\u8fd8\u662f\u4e00\u4e2a\u5bcc\u5bb6\u5927\u5b69\u5b50\uff0c\u4f46\u4e0d\u77e5\u600e\u5730\u7adf\u50cf\u4e2a\u5c0f\u53eb\u5316\u822c\u8eba\u5728\u5982\u6b64\u8352\u51c9 \u7684\u7834\u5e99\u4e2d\uff0c\u800c\u4e14\u8eab\u53d7\u91cd\u4f24\u3002\u8fd9\u65f6\u4ed6\u89c1\u8fd9\u5c11\u5e74\u79c0\u7709\u7d27\u8e59\uff0c\u51b7\u6c57\u76f4\u5192\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e0d\u5fcd\uff0c\u4f38\u624b \u4e00\u6478\u5c11\u5e74\u9762\u988a\uff0c\u7adf\u662f\u51b7\u5f97\u5f02\u5e38\u3002 \u3000\u3000\u8fd9\u65f6\u5ffd\u7136\u8eab\u540e\u4e00\u58f0\u51b7\u54fc\uff0c\u4e00\u4eba\u9634\u68ee\u5730\u9053\uff1a\u201c\u4e0d\u8981\u8138\u7684\u8d3c\u5b50\u8fd8\u4e0d\u7ed9\u6211\u4f4f\u624b\uff1f\u201d\u63a5\u7740\u4e00 \u80a1\u52b2\u98ce\u76f4\u88ad\u8f9b\u6377\u80cc\u540e\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u624b\u6301\u6709\u706b\u647a\u5b50\uff0c\u53ea\u89c1\u4ed6\u53cc\u8db3\u6a2a\u8de8\uff0c\u8eab\u4f53\u4e0d\u52a8\uff0c\u5934\u90fd\u4e0d\u56de\u5730\u4e00\u6307\u70b9\u5411\u6765\u4eba\u201c\u534e \u76d6\u201d\u8981\u7a74\u3002 \u3000\u3000\u90a3\u4eba\u53c8\u662f\u4e00\u58f0\u51b7\u7b11\uff0c\u90a3\u9634\u68ee\u68ee\u7684\u6c14\u6c1b\u76f4\u4ee4\u4eba\u5fc3\u4e2d\u53d1\u6bdb\uff0c\u4f46\u8f9b\u6377\u5374\u5947\u602a\u4ed6\u4f55\u4ee5\u5bf9\u81ea\u5df1 \u53cd\u800c\u4e00\u70b9\u6beb\u4e0d\u7406\u4f1a\uff1f \u3000\u3000\u54ea\u6653\u5f97\u7535\u5149\u706b\u77f3\u95f4\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u53c8\u662f\u4e00\u80a1\u52b2\u98ce\u6293\u5411\u8f9b\u6377\u5de6\u80a9\uff0c\u8f9b\u6377\u82e5\u662f\u4f38\u6307\u76f4\u8fdb\uff0c \u867d\u80fd\u70b9\u4e2d\u5bf9\u65b9\u534e\u76d6\uff0c\u4f46\u80a9\u4e0a\u4e00\u638c\u90fd\u662f\u81f4\u4ed6\u6b7b\u547d\uff0c\u800c\u8fd9\u4e00\u62db\u53d1\u51fa\u663e\u7136\u4e0d\u662f\u80cc\u540e\u4e4b\u4eba\uff0c\u4e00\u5b9a \u5bf9\u65b9\u53e6\u6709\u5e2e\u624b\uff0c\u800c\u4e14\u4e24\u4eba\u914d\u5408\u5f97\u7aef\u7684\u795e\u5999\u65e0\u6bd4\u3002 \u3000\u3000\u8f9b\u6377\u4ecd\u7136\u53cc\u8db3\u9489\u7acb\uff0c\u80cc\u5bf9\u654c\u4eba\uff0c\u8170\u95f4\u8fde\u6643\u4e24\u4e0b\uff0c\u5355\u624b\u4e0a\u4e0b\u5de6\u53f3\u4e00\u77ac\u95f4\u70b9\u51fa\u56db\u6307\u3002 \u3000\u3000\u53ea\u542c\u547c\u547c\u4e24\u58f0\uff0c\u88ad\u51fb\u7684\u4e24\u4eba\u663e\u7136\u65e0\u6cd5\u5f97\u901e\uff0c\u8dc3\u8eab\u9000\u540e\uff0c\u800c\u8f9b\u6377\u624b\u4e0a\u6301\u7684\u4eba\u647a\u5b50\u8fde\u706b \u5149\u90fd\u6ca1\u6709\u6643\u52a8\u4e00\u4e0b\u3002 \u3000\u3000\u8f9b\u6377\u8fd9\u624d\u7f13\u7f13\u8f6c\u8fc7\u8eab\u6765\uff0c\u8fd9\u4e00\u8f6c\u8eab\uff0c\u4e09\u4eba\u90fd\u554a\u4e86\u4e00\u58f0\u539f\u6765\u90a3\u88ad\u51fb\u8f9b\u6377\u7684\u4e24\u4eba\u7adf\u662f\u8def \u4e0a\u6240\u9047\u626e\u9b3c\u7684\u4e24\u4eba\uff0c\u5374\u4e0d\u77e5\u4e24\u4eba\u4f55\u4ee5\u53bb\u800c\u590d\u8fd4\uff1f \u3000\u3000\u90a3\u4e24\u4eba\u77a7\u6e05\u695a\u8f9b\u6377\uff0c\u56e0\u6b64\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u89c1\u5de6\u9762\u4e00\u4eba\u51b7\u4fa7\u4fa7\u5730\u5e72\u7b11\u4e00\u58f0\uff0c\u9ec4\u8721\u822c\u8138\u4e5f\u4e0a\u51f8\u51fa\u4e00\u53cc\u6ee1\u542b\u6012\u6c14\u7684\u773c\u73e0\uff0c\u706b\u5149\u7167 \u5728\u5927\u7ea2\u7684\u9ad8\u5e3d\u5b50\u4e0a\uff0c\u66f4\u4ee4\u4eba\u6050\u4ff1\u3002 \u3000\u3000\u53f3\u9762\u4e00\u4eba\u957f\u76f8\u4e0e\u5de6\u9762\u5b8c\u5168\u4e00\u6837\uff0c\u53ea\u662f\u9762\u8272\u7a0d\u9ed1\uff0c\u8fd9\u65f6\u51b7\u51b7\u9053\uff1a\u201c\u5389\u8001\u8d3c\u7684\u72d7\u5b50\u8fd8\u8981 \u8d76\u5c3d\u6740\u7edd\u4e48\uff1f\u201d\u8bf4\u7740\u547c\u5730\u5288\u51fa\u4e00\u638c\uff0c\u5c06\u8eab\u65c1\u4e00\u5f20\u6960\u6728\u4f9b\u684c\u6574\u5f20\u9707\u584c\u3002 \u3000\u3000\u8f9b\u6377\u65e9\u89c1\u8fc7\u4e24\u4eba\u8f7b\u529f\uff0c\u5374\u4e0d\u6599\u8fd9\u5bb6\u4f19\u638c\u529b\u4e5f\u60e9\u5730\u5389\u5bb3\uff0c\u53c8\u89c1\u81ea\u5df1\u4e09\u756a\u4e24\u6b21\u88ab\u4eba\u8bef\u4e3a \u4ec0\u4e48\u5389\u8d3c\u7684\u72d7\u5b50\uff0c\u5fc3\u4e2d\u867d\u77e5\u662f\u8bef\u4f1a\uff0c\u4f46\u4ed6\u62ac\u5934\u4e00\u770b\u8fd9\u4e24\u4eba\u51f6\u9738\u7684\u6837\u5b50\uff0c\u7acb\u523b\u53c8\u4e0d\u987e\u89e3\u91ca \u4e86\uff0c\u53ea\u91cd\u91cd\u54fc\u4e86\u4e00\u58f0\uff0c\u8f6c\u5934\u671b\u4e86\u671b\u5730\u4e0a\u7684\u5c11\u5e74\uff0c\u6839\u672c\u77a7\u90fd\u4e0d\u77a7\u90a3\u4e24\u4eba\u4e00\u773c\u3002 \u3000\u3000\u8fd9\u65f6\u5730\u4e0a\u7684\u5c11\u5e74\u4f3c\u4e4e\u82eb\u82e6\u71ac\u8fc7\u4e00\u9635\u6025\u75bc\uff0c\u5df2\u80fd\u5f00\u53e3\u8bf4\u8bdd\uff0c\u671b\u7740\u90a3\u4e24\u4e2a\u4e03\u5206\u4f3c\u9b3c\u7684\u51f6 \u6c49\u7adf\u89c1\u4e86\u4eb2\u4eba\uff0c\u54c7\u5730\u4e00\u58f0\u54ed\u51fa\u4e86\u58f0\uff1a\u201c\u91d1\u53d4\u2014\u2014\u201d\u518d\u4e5f\u53eb\u4e0d\u4e0b\u53bb\uff0c\u773c\u6cea\u5982\u6cc9\u6d8c\u51fa\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u602a\u4eba\u4f3c\u4e4e\u4e00\u540c\u8d77\u8eab\u62a2\u4e86\u8fc7\u6765\uff0c\u628a\u90a3\u5c11\u5e74\u62b1\u5728\u6000\u4e2d\uff0c\u4e0d\u4f4f\u629a\u6478\u4ed6\u7684\u4e00\u5934\u4e71\u53d1\uff0c \u53e3\u4e2d\u5514\u5514\u5440\u5440\uff0c\u4e0d\u77e5\u5728\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u8f9b\u6377\u62ac\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u4e24\u5f20\u6b7b\u4eba\u822c\u7684\u9b3c\u8138\u4e0a\uff0c\u6b64\u65f6\u7adf\u662f\u601c\u7231\u6a2a\u6ea2\uff0c\u65b9\u624d\u4e56\u623e\u4e4b\u6c14\u4e00 \u626b\u800c\u7a7a\uff0c\u4f3c\u4e4e\u5934\u4e0a\u7684\u5927\u7ea2\u9ad8\u5e3d\u4e5f\u4e0d\u592a\u523a\u76ee\u4e86\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u50cf\u662f\u9971\u53d7\u59d4\u5c48\u7684\u5b69\u5b50\u5012\u5728\u6148\u6bcd\u6000\u4e2d\u503e\u8bc9\u4e00\u822c\uff0c\u54ed\u5f97\u53cc\u80a9\u62bd\u52a8\uff0c\u751a\u662f\u60b2\u5207\u3002 \u3000\u3000\u90a3\u8138\u8272\u7a0d\u9ed1\u7684\u4e0d\u4f4f\u4f4e\u58f0\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u771f\u96be\u4e3a\u4e86\u4f60\u8fd9\u4e2a\u5b69\u5b50\uff0c\u771f\u96be\u4e3a\u4f60\u4e86\u2014\u2014\u201d \u3000\u3000\u90a3\u5c11\u5e74\u62ac\u8d77\u5934\u6765\uff0c\u7741\u5927\u6cea\u773c\u5bf9\u4ed6\u671b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u603b\u7b97\u6ca1\u6709\u8ba9\u8001\u8d3c\u62a2\u53bb\u90a3\u5251\u9798 \u2014\u2014\u201d \u3000\u3000\u65c1\u8fb9\u90a3\u9762\u5982\u9ec4\u8721\u7684\u6c49\u5b50\u63a5\u53e3\u5927\u58f0\u9053\uff1a\u201c\u597d\u5b69\u5b50\u4e8f\u4f60\u8eb2\u5f97\u597d\u5730\u65b9\uff0c\u53d4\u53d4\u65b9\u624d\u90fd\u8d70\u8fc7\u4e86 \u5934\u53c8\u56de\u6765\u627e\u5230\u4f60\u54e9\uff0c\u771f\u4e0d\u6127\u4e3a\u54b1\u4eec\u7684\u5e2e\u4e3b\u3002\u201d \u3000\u3000\u58f0\u97f3\u867d\u5c16\u9510\u96be\u542c\uff0c\u5374\u96c4\u58ee\u5f97\u5f88\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u8f6c\u5934\u671b\u7740\u4ed6\uff0c\u8138\u4e0a\u6ce5\u57a2\u88ab\u4ed6\u5728\u6c49\u5b50\u7684\u6000\u4e2d\u4e00\u9635\u63c9\u64e6\uff0c\u65e9\u5df2\u63e9\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u9732 \u51fa\u96ea\u767d\u7684\u76ae\u80a4\uff0c\u9876\u591a\u4e0d\u8fc7\u5341\u4e8c\u4e09\u5c81\u3002\u4f46\u8fd9\u65f6\u5c0f\u8138\u4e0a\u5374\u6d41\u8fc7\u4e00\u4e1d\u575a\u5f3a\u7684\u795e\u8272\u2014\u2014\u4f46\u90a3\u53ea\u662f \u4e00\u5239\u90a3\uff0c\u7acb\u523b\u53c8\u54fd\u54bd\u7740\u8bf4\uff1a \u3000\u3000\u201c\u53ef\u662f\uff0c\u53ef\u662f\u90a3\u4e9b\u8001\u8d3c\u554a\uff0c\u4ed6\u4eec\u4e00\u8def\u4e0a\u8f6e\u6d41\u8ffd\u6211\uff0c\u8ffd\u5f97\u6211\u597d\u82e6\u2026\u2026\u90a3\u4e2a\u5389\u8001\u8d3c\u6253\u4e86 \u6211\u4e00\u638c\uff0c\u4e00\u52a8\u5c31\u75bc\u5f97\u8981\u547d\u2026\u2026\u201d \u3000\u3000\u90a3\u4e24\u4e2a\u6c49\u5b50\u89c1\u5c11\u5e74\u4f24\u6210\u90a3\u4e2a\u6837\u5b50\uff0c\u4e0d\u7531\u6012\u5f62\u4e8e\u8272\uff0c\u4e24\u9053\u4e11\u964b\u4e0d\u582a\u7684\u6d53\u7709\u6324\u5728\u4e00\u8d77\uff0c \u66f4\u663e\u5f97\u4e11\u5f97\u6015\u4eba\u3002 \u3000\u3000\u9762\u5982\u9ec4\u8721\u7684\u6c49\u5b50\u4e00\u638c\u62cd\u5728\u4e00\u4e2a\u571f\u575b\u4e0a\uff0c\u6ce5\u6c99\u7eb7\u98de\u4e2d\u5927\u58f0\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u5389\u8001\u513f\u8fd9\u7b14\u5e10 \u8bb0\u4e0b\u4e86\u2014\u2014\u201d\u53c8\u8f6c\u8eab\u5bf9\u5c11\u5e74\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u770b\u53d4\u53d4\u66ff\u4f60\u51fa\u6c14\uff0c\u5feb\u522b\u54ed\u4e86\uff0c\u4e10\u5e2e\u5e2e\u4e3b\u90fd\u662f\u5927 \u82f1\u96c4\uff0c\u4e0d\u80fd\u8f7b\u5f39\u773c\u6cea\u7684\uff0c\u6765\uff0c\u53d4\u53d4\u5148\u770b\u770b\u4f60\u7684\u4f24\u52bf\u3002\u201d \u3000\u3000\u5947\u7684\u662f\u8f9b\u6377\u4ece\u90a3\u6781\u4e3a\u96be\u542c\u7684\u602a\u97f3\u4e2d\uff0c\u5c45\u7136\u542c\u51fa\u4e00\u4e1d\u6e29\u548c\u7684\u611f\u89c9\u3002 \u3000\u3000\u4e24\u4e2a\u602a\u6c49\u63ed\u5f00\u5c11\u5e74\u7684\u4e0a\u8863\u4e00\u770b\uff0c\u8138\u4e0a\u90fd\u5fae\u5fae\u53d8\u8272\uff0c\u663e\u7136\u5c11\u5e74\u4f24\u52bf\u4e0d\u8f7b\u3002 \u3000\u3000\u9762\u5982\u9ec4\u8721\u7684\u4e00\u4e2a\u5ffd\u7136\u8fd0\u6307\u5982\u98ce\u5730\u5728\u5c11\u5e74\u80f8\u53e3\u8981\u7a74\u90e8\u731b\u70b9\uff0c\u8db3\u8db3\u91cd\u590d\u70b9\u4e86\u5341\u4e8c\u904d\uff0c\u624d \u5401\u4e86\u53e3\u6c14\u7ad9\u8d77\u8eab\u6765\u3002\u90a3\u9762\u8272\u5e26\u9ed1\u7684\u5bf9\u5c11\u5e74\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u53d4\u53d4\u5c06\u4f60\u4f53\u5185\u7684\u6de4\u8840\u90fd\u5316\u5f00\u5566\uff0c \u4f60\u518d\u8fd0\u529f\u4e00\u6b21\u5c31\u53ef\u4ee5\u75ca\u6108\u4e86\u3002\u201d \u3000\u3000\u9762\u5982\u9ec4\u8721\u7684\u6c49\u5b50\u5374\u54fc\u4e86\u4e00\u58f0\uff1a\u201c\u771f\u96be\u4e3a\u90a3\u5389\u8001\u513f\u513f\u7adf\u7aef\u7684\u4e0b\u4e86\u91cd\u624b\uff0c\u54fc\uff0c\u8d70\u7740\u77a7\u5427 \u2014\u2014\u201d \u3000\u3000\u201c\u5662\uff0c\u4f60\u8fd9\u5c0f\u5b50\u8fd8\u6ca1\u6709\u9003\u8d70\u2014\u2014\u201d\u6562\u60c5\u4ed6\u53d1\u73b0\u8f9b\u6377\u8fd8\u7ad9\u5728\u540e\u9762\u2014\u2014\u800c\u4ed6\u662f\u8ba4\u5b9a\u8f9b\u6377 \u4e3a\u201c\u5389\u8001\u513f\u7684\u95e8\u4e0b\u3002\u201d \u3000\u3000\u8f9b\u6377\u6b63\u5f85\u7b7e\u8bdd\uff0c\u90a3\u5c11\u5e74\u5ffd\u6323\u624e\u7740\u558a\u7740\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u4ed6\u4e0d\u662f\u2014\u2014\u201d \u3000\u3000\u80cc\u540e\u5374\u6709\u4e00\u4e2a\u9634\u68ee\u68ee\u58f0\u97f3\u7684\u63a5\u9053\uff1a\u201c\u4ed6\u4e0d\u662f\uff0c\u6211\u662f\uff01\u201d \u3000\u3000\u9762\u8272\u5e26\u9ed1\u7684\u6c49\u5b50\u5411\u540c\u4f34\u4f7f\u4e00\u773c\u8272\uff0c\u4f4e\u58f0\u5bf9\u5c11\u5e74\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u4f60\u4e0d\u8981\u6015\uff0c\u5feb\u8fd0\u529f\u4e00\u5468\uff0c \u53d4\u53d4\u4fdd\u62a4\u4f60\u3002\u201d \u3000\u3000\u8f9b\u6377\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5e99\u95e8\u53e3\u7ad9\u4e86\u4e09\u4e2a\u4eba\uff0c\u4e00\u8bed\u4e0d\u53d1\u3002 \u3000\u3000\u90a3\u9762\u8272\u9ec4\u8721\u7684\u6c49\u5b50\uff0c\u5766\u7136\u8d70\u4e0a\u524d\u53bb\uff0c\u6253\u91cf\u8fd9\u4e09\u4eba\u4e00\u773c\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u76f8\u597d\u7684\uff0c\u54b1\u4eec\u51fa \u53bb\u8c08\u3002\u201d \u3000\u3000\u90a3\u4e09\u4e2a\u770b\u4e86\u770b\u5b88\u62a4\u5c11\u5e74\u7684\u9ed1\u6c49\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u9f50\u9f50\u5012\u51fa\u95e8\u3002 \u3000\u3000\u9ec4\u9762\u6c49\u5b50\u770b\u4e86\u8f9b\u6377\u4e00\u773c\uff0c\u4e5f\u8dc3\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u4e00\u58f0\u66b4\u543c\uff1a\u201c\u91d1\u8001\u5927\uff0c\u54b1\u4eec\u5f97\u7f6a\u5566\u3002\u201d\u63a5\u7740\u547c\u547c\u638c\u58f0\u9aa4\u8d77\uff0c\u4f3c\u4e4e\u5df2\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u5e99\u5916\u91d1\u8001\u5927\u4ee5\u4e00\u654c\u4e09\uff0c\u5168\u65e0\u60e7\u8272\uff0c\u638c\u529b\u51cc\u5389\uff0c\u5bf9\u65b9\u4e09\u4eba\u4e00\u65f6\u8fd1\u4e0d\u5f97\u8eab\u3002 \u3000\u3000\u8f9b\u6377\u6697\u9053\uff1a\u201c\u8fd9\u59d3\u91d1\u7684\u5144\u5f1f\u529f\u529b\u5b9e\u5728\u60ca\u4eba\uff0c\u4e0d\u77e5\u4ed6\u4eec\u4f55\u4ee5\u79f0\u90a3\u5b69\u5b50\u4e3a\u5e2e\u4e3b\uff1f\u8fd8\u6709\u4ed6 \u4eec\u8bf4\u4ec0\u4e48\u5251\u9798\u3001\u5389\u8001\u8d3c\u2014\u2014\u554a\uff0c\u83ab\u975e\u662f\u4ed6 \u3000\u3000\u539f\u6765\u8fd9\u65f6\u4ed6\u770b\u89c1\u4e09\u4e2a\u6765\u4eba\u4e2d\uff0c\u5230\u6709\u4e24\u4e2a\u4f7f\u7684\u662f\u5d06\u5cd2\u638c\u6cd5\uff0c\u53c8\u60f3\u5230\u4ec0\u4e48\u201c\u5389\u8001\u8d3c\u201d\uff0c \u767b\u65f6\u60f3\u8d77\u8fd9\u201c\u5389\u8001\u8d3c\u201d\u5fc5\u662f\u5d06\u5cd2\u638c\u95e8\u4eba\u201c\u5251\u795e\u201d\u5389\u9e57\u3002 \u3000\u3000\u601d\u53ca\u6b64\uff0c\u8f9b\u6377\u53ea\u89c9\u70ed\u8840\u6cb8\u817e\uff0c\u82cd\u767d\u7684\u988a\u4e0a\u987f\u662f\u5982\u559d\u9189\u4e00\u822c\uff0c\u9690\u655b\u7684\u795e\u5149\u4e00\u5c04\u800c\u51fa\uff0c \u4ee4\u4eba\u4e0d\u6562\u4ef0\u89c6\u3002\u5389\u9e57\uff0c\u5389\u9e57\u6b63\u662f\u9677\u5bb3\u6885\u53d4\u53d4\u7684\u4e3b\u51f6\u4e4b\u4e00\uff0c\u8f9b\u6377\u767b\u65f6\u5bf9\u91d1\u8001\u5927\u751f\u51fa\u597d\u611f\u6765 \u4e86\u3002 \u3000\u3000\u201c\u5bf9\u4e86\uff0c\u4e00\u5b9a\u662f\u4ed6\uff0c\u4ee5\u4f17\u51cc\u5be1\uff0c\u4ee5\u5927\u6b3a\u5c0f\uff0c\u6b63\u662f\u4ed6\u7684\u8d2f\u6280\u2014\u2014\u201d\u8f9b\u6377\u4e0d\u7981\u5583\u5583\u81ea\u8bed\uff0c \u53cc\u638c\u63e1\u5f97\u7d27\u7d27\u7684\u3002 \u3000\u3000\u5ffd\u5730\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u5237\u5237\u4ece\u9ed1\u6697\u4e2d\u8df3\u51fa\u4e24\u4e2a\u4eba\u5f71\uff0c\u8f9b\u6377\u5728\u6697\u4e2d\u4e00\u770b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u539f \u6765\u5de6\u9762\u4e00\u4eba\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u76f8\u8c8c\u4e0d\u51e1\uff0c\u6b63\u662f\u81ea\u5df1\u8bc6\u5f97\u7684\u201c\u5d06\u5cd2\u4e09\u7edd\u5251\u201d\u4e2d\u7684\u201c\u5730\u7edd\u5251\u201d\u4e8e\u4e00 \u98de\u3002 \u3000\u3000\u53f3\u9762\u4e00\u4eba\u5e74\u4f3c\u7a0d\u957f\uff0c\u53ea\u662f\u6b65\u5c65\u4e4b\u95f4\u66f4\u89c1\u529f\u529b\u6df1\u539a\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u5bf9\u90a3\u4e09\u4eba\u559d\u9053\uff1a\u201c\u53f2\u5e08\u5f1f\u52a0\u6cb9\u56f0\u4f4f\u4ed6\u3002\u201d\u548c\u65c1\u8fb9\u4e00\u4eba\u4e00\u8d77\u8dc3\u4eba\u5e99\u5185\u3002 \u3000\u3000\u5e99\u5185\u90a3\u5c11\u5e74\u6b63\u76d8\u5750\u8fd0\u529f\uff0c\u90a3\u9762\u5e26\u9ed1\u8272\u7684\u5927\u6c49\u7126\u6025\u5730\u5728\u4e00\u65c1\u65e0\u8ba1\u53ef\u4f7f\uff0c\u5ffd\u5730\u4ed6\u4f38\u51fa\u4e00 \u638c\uff0c\u6309\u5728\u5c11\u5e74\u80cc\u4e0a\uff0c\u4f3c\u4e4e\u60f3\u4ee5\u672c\u8eab\u529f\u529b\u52a9\u5c11\u5e74\u65e9\u4e9b\u6062\u590d\u3002 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5e99\u95e8\u5f00\u5904\uff0c\u5237\u5237\u7eb5\u4eba\u4e24\u4eba\uff0c\u90fd\u662f\u624b\u6301\u957f\u5251\uff0c\u9996\u5148\u4e00\u4eba\u4e00\u628a\u5c31\u5411\u5c11\u5e74\u6293\u6765 \u3000\u3000\u90a3\u9ed1\u6c49\u5b50\u4e00\u638c\u6309\u5728\u5c11\u5e74\u80cc\u4e0a\u770b\u90fd\u4e0d\u770b\u5c31\u4e00\u638c\u5012\u5377\u4e0a\u6765\uff0c\u5de8\u638c\u4e00\u5f20\uff0c\u7adf\u5f80\u6765\u4eba\u8109\u4e0a\u6293 \u53bb\u3002 \u3000\u3000\u6765\u4eba\u8f7b\u54fc\u4e00\u58f0\uff0c\u7ffb\u8eab\u843d\u5730\uff0c\u4e00\u8fde\u4e09\u5251\u523a\u51fa\u8fd9\u4eba\u6b63\u662f\u5d06\u5cd2\u6d3e\u4e09\u5251\u7edd\u4e4b\u9996\uff0c\u5929\u7edd\u5251\u8bf8\u845b \u660e\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u63a5\u5251\u5b88\u4f4f\u95e8\u53e3\uff0c\u9632\u6b62\u654c\u4eba\u9003\u8d70\u3002 \u3000\u3000\u5929\u7edd\u5251\u8bf8\u845b\u660e\u529f\u529b\u4e3a\u5d06\u5cd2\u4e09\u5251\u4e4b\u51a0\uff0c\u8fd9\u4e00\u8fde\u4e09\u5251\u5288\u51fa\uff0c\u5c31\u8fde\u6697\u4e2d\u8f9b\u6377\u4e5f\u4e0d\u4f4f\u70b9\u5934\uff0c \u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u53ae\u5251\u6cd5\u8981\u6bd4\u4e8e\u4e00\u98de\u7cbe\u7eaf\u5f97\u591a\uff0c\u60f3\u6765\u603b\u662f\u4ed6\u5e08\u5144\u4e86\u3002\u201d \u3000\u3000\u90a3\u77e5\u9ed1\u9762\u5927\u6c49\u4ecd\u7136\u5168\u795e\u8d2f\u6ce8\u52a9\u5c11\u5e74\u6062\u590d\u4f24\u52bf\uff0c\u5bf9\u8bf8\u845b\u660e\u4e09\u5f0f\u5b9b\u5982\u4e0d\u89c1\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u5927\u60ca\uff0c\u5fc3\u4e2d\u6697\u60f3\u9053\uff1a\u201c\u4f60\u6b66\u827a\u867d\u5f3a\uff0c\u600e\u80fd\u8fd9\u822c\u6258\u5927\uff1f\u201d \u3000\u3000\u90a3\u77e5\u5c31\u5728\u8bf8\u845b\u660e\u957f\u5251\u582a\u582a\u5288\u5230\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u90a3\u9762\u8272\u5e26\u9ed1\u7684\u2014\u2014\u4e5f\u5c31\u662f\u91d1\u8001\u4e8c\u2014\u2014\u5ffd \u5730\u53cd\u624b\u4e00\u628a\u6293\u51fa\uff0c\u800c\u4e14\u662f\u76f4\u6293\u8bf8\u845b\u660e\u7684\u5251\u8eab\u2014\u2014 \u3000\u3000\u8bf8\u845b\u660e\u89c1\u591a\u8bc6\u5e7f\uff0c\u4e00\u89c1\u91d1\u8001\u4e8c\u4e00\u638c\u6293\u6765\uff0c\u638c\u5fc3\u5168\u5448\u9ed1\u8272\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9 \u53cc\u8db3\u4e00\u6c89\uff0c\u563f\u5730\u4e00\u58f0\uff0c\u786c\u751f\u751f\u5c06\u9012\u51fa\u7684\u5f0f\u5b50\u6536\u56de\u3002 \u3000\u3000\u6697\u4e2d\u8f9b\u6377\u4e5f\u540c\u6837\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u66fe\u542c\u6885\u53d4\u53d4\u8bf4\u8fc7\uff0c\u56db\u5ddd\u843d\u96c1\u6da7\u6709\u4e00\u79cd\u72ec\u95e8\u529f\uff0c\u5524\u4f5c\uff1a \u201c\u9634\u98ce\u9ed1\u6c99\u638c\u201d\uff0c\u7ec3\u5f97\u7cbe\u7eaf\u65f6\u80fd\u591f\u7a7a\u624b\u6293\u6298\u7eaf\u521a\u5175\u5203\uff0c\u662f\u5916\u5bb6\u529f\u592b\u4e2d\u6781\u4e0a\u4e58\u7684\u4e00\u79cd\uff0c\u53ea \u662f\u8fd1\u767e\u5e74\u6765\u6b64\u827a\u4f3c\u4e4e\u5931\u4f20\uff0c\u4e45\u4e45\u4e0d\u89c1\u6709\u4eba\u65bd\u7528\u3002\u60f3\u4e0d\u5230\u8fd9\u91d1\u8001\u4e8c\u65b9\u624d\u4e00\u628a\u6293\u51fa\uff0c\u7adf\u4f3c\u8fd9 \u5931\u4f20\u767e\u5e74\u7684\u7edd\u6280\uff0c\u800c\u4e14\u770b\u6837\u5b50\u529f\u529b\u5df1\u7ec3\u5f97\u751a\u6df1\u3002\u65b9\u624d\u8bf8\u845b\u660e\u5e78\u4e8f\u6536\u62db\u5f97\u5feb\uff0c\u5426\u5219\u4ed6\u90a3\u957f \u5251\u867d\u7136\u4e0d\u662f\u5e73\u51e1\u94a2\u94c1\uff0c\u53ea\u6050\u4e5f\u96be\u7ecf\u5f97\u8d77\u201c\u9634\u98ce\u9ed1\u6c99\u638c\u201d\u4e00\u6293\u5462\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u4f3c\u4e4e\u4e5f\u53d1\u89c9\u91d1\u8001\u4e8c\u638c\u8272\u6709\u5f02\uff0c\u5237\u5730\u8dc3\u8fd1\uff0c\u957f\u5251\u4e00\u659c\uff0c\u6b63\u8fce\u4e0a\u8bf8\u845b\u660e\u7684\u53cd\u624b\u4e00 \u5251\uff0c\u53cc\u53cc\u523a\u5411\u91d1\u8001\u4e8c\u3002 \u3000\u3000\u5929\u5730\u4e24\u5251\u5408\u58c1\uff0c\u5a01\u529b\u5927\u589e\uff0c\u5c24\u5176\u4e24\u4eba\u5251\u5f0f\u4e92\u76f8\u914d\u5408\uff0c\u4e25\u5bc6\u65e0\u6bd4\uff0c\u91d1\u8001\u4e8c\u4ed7\u7740\u96c4\u539a\u638c \u529b\uff0c\u52c9\u5f3a\u652f\u6491\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u7684\u9762\u8272\u8fd9\u65f6\u5374\u7ea2\u5f97\u5f02\u5e38\uff0c\u4f3c\u4e4e\u8fd0\u529f\u5df2\u5230\u4e86\u7d27\u8981\u5173\u5934\uff0c\u91d1\u8001\u4e8c\u66f4\u4e0d\u6562\u677e\u61c8\uff0c\u5355 \u51ed\u4e00\u638c\u6e10\u6e10\u62db\u67b6\u4e0d\u4f4f\u3002 \u3000\u3000\u90a3\u8bf8\u845b\u660e\u5c24\u5176\u72e1\u6ed1\uff0c\u4e0d\u65f6\u62bd\u7a7a\u88ad\u51fb\u6b63\u5728\u8fd0\u529f\u7684\u5c11\u5e74\uff0c\u8feb\u5f97\u91d1\u8001\u4e8c\u66f4\u662f\u624b\u5fd9\u811a\u4e71\u3002 \u3000\u3000\u8fd9\u65f6\u4e8e\u4e00\u98de\u4e00\u62db\u201c\u51e4\u51f0\u5c55\u7fc5\u201d\u76f4\u88ad\u91d1\u8001\u4e8c\u5de6\u80a9\uff0c\u8bf8\u845b\u660e\u5374\u4e00\u5251\u523a\u5411\u7a7a\u7740\u7684\u201c\u5e72\u4f4d\u201d\uff0c \u4f46\u662f\u91d1\u8001\u4e8c\u53ea\u8981\u4e00\u907f\u4e8e\u4e00\u98de\u7684\u5251\u5f0f\uff0c\u7acb\u5373\u5c31\u5f97\u89e6\u4e0a\u8bf8\u845b\u660e\u7684\u5251\u5c16\uff0c\u8fd9\u4e00\u4e0b\u7aef\u7684\u72e0\u6bd2\uff0c\u91d1 \u8001\u4e8c\u867d\u7136\u5206\u795e\u7167\u987e\u5c11\u5e74\uff0c\u4f46\u4ed6\u4f55\u7b49\u8001\u7ecf\u9a8c\uff0c\u8bf8\u845b\u660e\u5251\u5f0f\u6545\u610f\u5411\u7a7a\u5904\u4e00\u9012\uff0c\u4ed6\u7acb\u523b\u77e5\u9053\u5176 \u7528\u610f\uff0c\u53ea\u542c\u4ed6\u66b4\u543c\u4e00\u58f0\uff0c\u5355\u638c\u518d\u6b21\u4f7f\u51fa\u201c\u9634\u98ce\u9ed1\u6c99\u638c\u201d\u786c\u6293\u4e8e\u4e00\u98de\u4e4b\u5251\u950b\u2014\u2014 \u3000\u3000\u4f46\u8bf8\u845b\u660e\u51b7\u7b11\u4e00\u58f0\uff0c\u957f\u5251\u4e00\u7ffb\uff0c\u76f4\u523a\u4ed6\u808b\u4e0b\u201c\u7389\u6795\u201d\uff0c\u773c\u770b\u91d1\u8001\u4e8c\u4e0d\u53ca\u6362\u62db\u2014\u2014 \u3000\u3000\u5ffd\u7136\u53ee\u7684\u4e00\u58f0\uff0c\u8bf8\u845b\u660e\u5012\u9000\u4e09\u6b65\uff0c\u4e8e\u4e00\u98de\u6301\u5251\u7684\u624b\u8155\u5374\u88ab\u4e00\u4e2a\u8499\u9762\u4eba\u634f\u6263\u7740\uff0c\u91d1\u8001 \u4e8c\u5374\u77aa\u7740\u4e00\u53cc\u94dc\u94c3\u822c\u7684\u602a\u773c\u2014\u2014 \u3000\u3000\u539f\u6765\u6b63\u5728\u91d1\u8001\u4e8c\u94c1\u638c\u5373\u5c06\u6293\u4f4f\u4e8e\u4e00\u98de\u957f\u5251\u2014\u2014\u4e5f\u5c31\u662f\u8bf8\u845b\u660e\u5251\u5c16\u4ec5\u79bb\u91d1\u8001\u4e8c\u201c\u7389\u6795 \u7a74\u201d\u4e0d\u53ca\u4e09\u5bf8\u7684\u4e00\u5239\u90a3\uff0c\u4e00\u6761\u9ed1\u5f71\u81ea\u9ed1\u6697\u4e2d\u4e00\u8dc3\u800c\u51fa\uff0c\u53ea\u89c1\u4ed6\u8eab\u5f62\u4e00\u6643\u5df2\u5230\u4e86\u4e09\u4eba\u9762\u524d\uff0c \u4e00\u51fa\u624b\u5c31\u6263\u4f4f\u4e8e\u4e00\u98de\u7684\u8109\u95e8\uff0c\u501f\u52bf\u7528\u4e8e\u4e00\u98de\u7684\u957f\u5251\u5411\u8bf8\u845b\u660e\u5251\u8eab\u4e0a\u4e00\u649e\u2014\u2014 \u3000\u3000\u4e8e\u662f\u91d1\u8001\u4e8c\u4e00\u628a\u6293\u4e86\u4e00\u4e2a\u7a7a\uff0c\u4e8e\u4e00\u98de\u8109\u95e8\u53d7\u5236\uff0c\u6d51\u8eab\u8f6f\u67d4\uff0c\u8bf8\u845b\u660e\u5374\u611f\u4e00\u80a1\u67d4\u548c\u7684 \u52b2\u529b\u4ece\u5251\u4e0a\u4f20\u5165\uff0c\u5413\u5f97\u5012\u9000\u4e09\u6b65\uff01 \u3000\u3000\u4e00\u65f6\u5e99\u4e2d\u5012\u9759\u4e86\u4e0b\u6765\uff0c\u53ea\u6709\u90a3\u5c11\u5e74\u6c89\u91cd\u7684\u547c\u5438\u58f0\u2014\u2014\u6562\u60c5\u4ed6\u8fd0\u529f\u5df2\u5230\u4e86\u6700\u540e\u5173\u5934\u3002 \u3000\u3000\u5e99\u5916\u5ffd\u7136\u4f20\u6765\u4e00\u58f0\u77ed\u4fc3\u800c\u7126\u6025\u7684\u5578\u58f0\uff0c\u8bf8\u845b\u660e\u53ca\u4e8e\u4e00\u98de\u8138\u4e0a\u795e\u8272\u4e00\u53d8\u3002 \u3000\u3000\u8bf8\u845b\u660e\u5ffd\u5730\u4e00\u5251\u523a\u51fa\uff0c\u76f4\u70b9\u8499\u9762\u4eba\u8109\u95e8\uff0c\u8fd9\u8ddd\u79bb\u592a\u8fd1\uff0c\u51fa\u62db\u53c8\u592a\u7a81\u7136\uff0c\u4ed6\u7eb5\u6709\u795e\u4ed9 \u672c\u9886\u4e5f\u53ea\u6709\u4e00\u6761\u8def\u53ef\u8d70\u2014\u2014\u5c31\u662f\u653e\u5f00\u6263\u4f4f\u4e8e\u4e00\u98de\u8109\u95e8\u7684\u624b\u3002 \u3000\u3000\u5c31\u5728\u8499\u9762\u4eba\u9000\u6b65\u653e\u624b\u7684\u4e00\u5239\u90a3\uff0c\u8bf8\u845b\u660e\u4e00\u62c9\u4e8e\u4e00\u98de\u53f3\u624b\uff0c\u559d\u58f0\u201c\u8d70\u201d\uff0c\u53cc\u53cc\u8dc3\u5c06\u51fa \u53bb\u3002 \u3000\u3000\u53ea\u542c\u5f97\u5e99\u5916\u547c\u547c\u98ce\u58f0\uff0c\u63a5\u7740\u662f\u91d1\u8001\u5927\u523a\u8033\u7684\u602a\u53eb\u58f0\uff1a\u201c\u54ea\u91cc\u8d70\uff01\u201d\u63a5\u7740\u4e00\u58f0\u54fc\uff0c\u4e00 \u5207\u5c31\u6062\u590d\u5bc2\u9759\u3002 \u3000\u3000\u91d1\u8001\u5927\u4f4e\u5934\u8fdb\u95e8\uff0c\u4f46\u5934\u4e0a\u7684\u7ea2\u5e3d\u5b50\u4ecd\u662f\u88ab\u95e8\u69db\u78b0\u4e86\u4e00\u4e0b\uff0c\u4ee5\u81f4\u6b6a\u5728\u4e00\u8fb9\u3002 \u3000\u3000\u4ed6\u6276\u6b63\u4e86\u5e3d\u5b50\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u9e4f\u513f\u8fd0\u529f\u5b8c\u4e86\u6ca1\u6709\uff1f\u90a3\u5389\u8001\u8d3c\u53ea\u6015\u5c31\u8981\u5230\u4e86\uff0c\u54b1 \u4eec\u5f97\u5feb\u4e9b\u8d70\uff0c\u554a\u2014\u2014\u201d \u3000\u3000\u6562\u60c5\u4ed6\u53d1\u73b0\u91d1\u8001\u4e8c\u6ca1\u6709\u56de\u7b54\uff0c\u6b63\u5168\u795e\u52a9\u5c11\u5e74\u6e21\u8fc7\u6700\u540e\u96be\u5173\u3002\u201c\u5618\uff01\u201d\u4e00\u58f0\u957f\u5401\uff0c\u91d1 \u8001\u4e8c\u4e00\u8dc3\u800c\u8d77\uff0c\u5c11\u5e74\u4e5f\u7741\u5f00\u4e86\u773c\u3002\u91d1\u8001\u4e8c\u4e00\u8bed\u4e0d\u53d1\u4fbf\u5411\u8499\u9762\u4eba\u2014\u2014\u5f53\u7136\u5c31\u662f\u4e0d\u987e\u88ab\u4e8e\u4e00 \u98de\u8ba4\u51fa\u771f\u9762\u76ee\u7684\u8f9b\u6377\u2014\u2014\u62dc\u5012\u5730\u4e0a\uff0c\u8083\u7136\u9053\uff1a\u201c\u9601\u4e0b\u53d7\u91d1\u8001\u4e8c\u4e00\u62dc\uff0c\u4ece\u6b64\u9601\u4e0b\u5c31\u662f\u4e10\u5e2e \u7684\u5927\u6069\u4eba\uff0c\u8bf7\u6559\u8d35\u59d3\uff1f\u201d\u6562\u60c5\u8f9b\u6377\u5728\u8def\u4e0a\u5927\u559d\u65f6\uff0c\u4ed6\u6ca1\u6709\u542c\u4ed6\u771f\u7684\u59d3\u540d\u3002 \u3000\u3000\u8f9b\u6377\u626f\u53bb\u8499\u9762\u5e03\u5dfe\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u6276\u8d77\u91d1\u8001\u4e8c\u9053\uff1a\u201c\u5c0f\u53ef\u59d3\u8f9b\u540d\u6377\uff0c\u624b\u8db3\u4e4b\u52b3\uff0c\u4f55\u8db3 \u6302\u9f7f\uff1f\u201d\u4e0d\u77e5\u600e\u5730\u8f9b\u6377\u5ffd\u5730\u89c9\u5f97\u81ea\u5df1\u5e94\u8be5\u5c3d\u91cf\u8c6a\u653e\u82f1\u96c4\u4e00\u4e9b\u624d\u5bf9\u3002 \u3000\u3000\u91d1\u8001\u5927\u4e5f\u5411\u8f9b\u6377\u4e00\u63d6\uff0c\u7136\u540e\u8f6c\u8eab\u5bf9\u91d1\u8001\u4e8c\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5c31\u52a8\u8eab\u3002\u201d \u3000\u3000\u91d1\u8001\u4e8c\u7275\u7740\u5c11\u5e74\uff0c\u4e00\u8d77\u8d70\u51fa\u5c0f\u5e99\uff0c\u8f9b\u6377\u4e5f\u8ddf\u7740\u8d70\u51fa\u3002 \u3000\u3000\u91d1\u8001\u5927\u5411\u6797\u65c1\u4e24\u6761\u8def\u671b\u4e86\u671b\uff0c\u7136\u540e\u5728\u5de6\u9762\u4e00\u6761\u4e0a\u6545\u610f\u8df5\u8e0f\u4e86\u4e00\u4e9b\u6811\u679d\uff0c\u7559\u4e0b\u597d\u4e9b\u75d5 \u8ff9\u3002\u8fd9\u65f6\u4ed6\u4ef0\u9996\u89c2\u5929\uff0c\u53ea\u89c1\u6708\u8272\u6aac\u9647\uff0c\u5df2\u5230\u4e86\u6b63\u4e2d\uff0c\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u4e0e\u91d1\u8001\u4e8c\u53cc\u53cc\u8dea\u5728 \u90a3\u5c11\u5e74\u9762\u524d\uff01\u6781\u5176\u5e84\u4e25\u5730\u9053\uff1a\u201c\u4e10\u5e2e\u7b2c\u5341\u56db\u4ee3\u5e2e\u4e3b\u542c\u7980\uff1a\u7b2c\u5341\u4e09\u4ee3\u5185\u5916\u62a4\u6cd5\u91d1\u5143\u4f2f\u91d1\u5143 \u4ef2\u4e8e\u6b64\u523b\u6708\u6b63\u5f53\u4e2d\u8d77\uff0c\u4efb\u671f\u5df2\u6ee1\uff0c\u4e0d\u5f97\u518d\u7559\u5e2e\u5185\uff0c\u8bf7\u5e2e\u4e3b\u4f9d\u5e2e\u89c4\u53e6\u5bfb\u9ad8\u58eb\uff0c\u611a\u5144\u5f1f\u5c31\u6b64 \u76f8\u522b\uff0c\u671b\u6211\u5e2e\u4e3b\u667a\u777f\u624d\u8457\uff0c\u5e2e\u52a1\u65e5\u76ca\u5174\u9686\u3002\u201d \u3000\u3000\u7980\u5b8c\u4e8c\u4eba\u7ad9\u8d77\u3002\u90a3\u5c11\u5e74\u5374\u62b1\u4f4f\u91d1\u8001\u5927\u7684\u624b\u81c2\u9053\uff1a\u201c\u53d4\u53d4\uff0c\u4e0d\u8981\u8d70\uff0c\u4e0d\u8981\u79bb\u5f00\u9e4f\u513f\uff0c \u6211\u60c5\u613f\u4e0d\u8981\u505a\u5e2e\u4e3b\uff0c\u4e5f\u8981\u548c\u53d4\u53d4\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u8bf4\u5230\u540e\u6765\uff0c\u773c\u6cea\u5df2\u662f\u76c8\u7736\u3002 \u3000\u3000\u91d1\u8001\u5927\u65b9\u624d\u8138\u4e0a\u8fd8\u662f\u4e00\u6d3e\u8bda\u4e25\uff0c\u8fd9\u65f6\u53c8\u4ee5\u624b\u629a\u6478\u90a3\u5c11\u5e74\u7684\u5934\u53d1\uff0c\u90a3\u4e11\u964b\u7684\u8138\u4e0a\u7adf\u95ea \u70c1\u7740\u611f\u60c5\u7684\u5149\u8f89\u3002\u534a\u54cd\uff0c\u4ed6\u624d\u5bf9\u5c11\u5e74\u9053\uff1a \u3000\u3000\u201c\u9e4f\u513f\uff0c\u4e10\u5e2e\u7684\u5e2e\u4e3b\u5c82\u80fd\u8f7b\u5f39\u773c\u6cea\uff0c\u8001\u5e2e\u4e3b\u6388\u4f4d\u7ed9\u4f60\u65f6\u600e\u751f\u8bf4\u7684\uff1f\u5feb\u4e0d\u8981\u54ed\u4e86\u3002\u201d \u3000\u3000\u91d1\u8001\u4e8c\u770b\u9e4f\u513f\u52aa\u529b\u5659\u4f4f\u90a3\u5373\u5c06\u6eda\u4e0b\u7684\u6cea\u73e0\uff0c\u4e0d\u7981\u4ef0\u9996\u957f\u53f9\u3002\u4ed6\u63e1\u7740\u9e4f\u513f\u7684\u624b\uff0c\u4f4e\u58f0 \u9053\uff1a\u201c\u9e4f\u513f\uff0c\u4ee5\u540e\u4e10\u5e2e\u662f\u5426\u80fd\u5174\u9686\u8d77\u6765\uff0c\u5c31\u8981\u770b\u4f60\u7684\u5566\uff01\u201d \u3000\u3000\u8f9b\u6377\u6697\u8d5e\u91d1\u8001\u5927\u6599\u7b97\u5982\u795e\uff0c\u679c\u7136\u5c06\u8fd9\u5389\u8001\u513f\u9a97\u8fc7\uff0c\u540c\u65f6\u4ed6\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u5c06\u90a3\u5757\u5e03\u5dfe \u6234\u4e0a\uff0c\u4ed6\u7adf\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u7684\u7d27\u5f20\uff0c\u53cc\u624b\u5728\u5934\u540e\u5bf9\u90a3\u5e03\u5dfe\u6253\u7ed3\u65f6\uff0c\u5f04\u4e86\u534a\u5929\u624d\u7b97\u6253\u597d\uff0c\u800c \u5389\u9e57\u4e00\u884c\u4eba\u5df2\u8dc3\u51fa\u4e08\u591a\u2014\u2014-\u201d \u3000\u3000\u53ea\u542c\u4ed6\u4e00\u58f0\u6e05\u53f1\uff0c\u8eab\u5f62\u9661\u7136\u62d4\u8d77\u4e09\u4e08\uff0c\u5728\u7a7a\u4e2d\u4e00\u4ef0\u4e00\u6298\uff0c\u5df2\u5982\u6d41\u661f\u822c\u8d76\u4e86\u4e0a\u53bb\uff0c\u5237 \u5730\u4e00\u4e0b\uff0c\u53cd\u8fc7\u9762\u6765\u843d\u5728\u5389\u9e57\u9762\u524d\u2014\u2014 \u3000\u3000\u82e5\u8bf4\u8f9b\u6377\u662f\u60ca\u5413\u4e8e\u5389\u9e57\u7684\u529f\u529b\u624d\u7d27\u5f20\u5982\u65af\uff0c\u90a3\u4e5f\u4e0d\u89c1\u5f97\uff0c\u5f53\u65e5\u4e16\u5916\u4e09\u4ed9\u7684\u6167\u5927\u5e08\u4f55 \u7b49\u5a01\u52bf\uff0c\u8f9b\u6377\u4ecd\u6beb\u4e0d\u542b\u7cca\u5730\u786c\u63a5\u5979\u4e09\u62db\uff0c\u8fd9\u5389\u9e57\u867d\u7136\u529f\u529b\u9ad8\u5f97\u51fa\u4e4e\u8f9b\u6377\u610f\u6599\u4e4b\u5916\uff0c\u4f46\u5c82 \u80fd\u5c31\u9547\u5f97\u4f4f\u4ed6\uff1f\u4f55\u51b5\u8fd8\u6709\u6885\u53d4\u53d4\u90a3\u6bb5\u8840\u6d77\u6df1\u4ec7\u5728\u4ed6\u80f8\u4e2d\u6c79\u6d8c\u7740\u5462\u3002 \u3000\u3000\u4f46\u662f\u4e5f\u8bb8\u5c31\u56e0\u4e3a\u4ed6\u65e5\u591c\u65e0\u65f6\u4e0d\u5728\u5ff5\u7740\u5bf9\u8fd9\u4e94\u5927\u5251\u6d3e\u638c\u95e8\u4eba\u6311\u6218\u7684\u60c5\u666f\uff0c\u8fd9\u65f6\u4e8b\u5230\u4e34 \u5934\uff0c\u53cd\u800c\u7d27\u5f20\u8d77\u6765\uff0c\u4e0d\u8fc7\u5f53\u4ed6\u4e00\u8dc3\u800c\u4e0b\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u518d\u6ca1\u6709\u4e1d\u6beb\u7d27\u5f20\u4e86\uff0c\u4ed6\u7684\u8eab\u5f62\u5982 \u884c\u4e91\u6d41\u6c34\u822c\u8d76\u8fc7\u4e86\u5389\u9e57\u7684\u524d\u5934\u2014\u2014 \u3000\u3000\u5389\u9e57\u867d\u5e76\u6ca1\u6709\u4ee5\u5168\u901f\u5954\u8d70\u2014\u2014\u56e0\u4e3a\u8fd8\u6709\u4e24\u4e2a\u529f\u529b\u8f83\u5dee\u8005\u8ddf\u5728\u540e\u9762\u7684\u7f18\u6545\uff0c\u4f46\u90a3\u4e2a\u901f \u5ea6\u5df2\u662f\u5341\u5206\u60ca\u4eba\u4e86\uff0c\u4ed6\u53ea\u542c\u5f97\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u4e2a\u4eba\u5f71\u4ece\u5934\u4e0a\u98de\u8fc7\uff0c\u843d\u5728\u5728\u524d\uff0c\u4ed6\u4e5f\u5237\u5730\u4e00 \u4e0b\uff0c\u505c\u4e0b\u4e86\u8eab\u8eaf\uff0c\u90a3\u4e48\u5feb\u7684\u51b2\u52bf\u4e0d\u77e5\u600e\u7684\u88ab\u5316\u5f97\u4e00\u4e1d\u4e0d\u5269\u3002 \u3000\u3000\u5728\u524d\u7ad9\u7684\u4e00\u4eba\u4ee5\u5dfe\u8499\u9762\uff0c\u53ea\u9732\u51fa\u4e00\u53cc\u7cbe\u5149\u7684\u7738\u5b50\u3002 \u3000\u3000\u201c\u5e08\u7236\uff0c\u5c31\u662f\u4ed6\uff01\u201d \u3000\u3000\u5389\u9e57\u8eab\u540e\u4e00\u4eba\u60ca\u53eb\u8d77\u6765\uff0c\u6b63\u662f\u90a3\u5929\u7edd\u5251\u8bf8\u845b\u660e\uff0c\u53e6\u4e00\u4eba\u5f53\u7136\u5c31\u662f\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u4e86\u3002 \u6562\u60c5\u4ed6\u4eec\u5df2\u7ecf\u628a\u5c0f\u5e99\u4e2d\u8499\u9762\u4eba\u51fa\u624b\u76f8\u62e6\u7684\u4e8b\u544a\u8bc9\u4e86\u5389\u9e57\u3002 \u3000\u3000\u5389\u9e57\u54fc\u4e86\u4e00\u58f0\uff0c\u4e00\u53cc\u5389\u773c\u72e0\u72e0\u6253\u91cf\u4e86\u8f9b\u6377\u4e24\u773c\u3002\u5bf9\u540e\u9762\u7684\u4e8c\u4eba\u9053\uff1a\u201c\u4f60\u4eec\u5148\u8ffd\u4e0b\u53bb\uff0c \u4e0d\u51fa\u4e00\u76cf\u8336\u65f6\u95f4\u6211\u5fc5\u8d76\u4e0a\u3002\u201d\u58f0\u97f3\u4e2d\u5145\u5206\u8868\u73b0\u51fa\u81ea\u4fe1\u7684\u50b2\u6c14\u3002 \u3000\u3000\u8bf8\u845b\u660e\u5e94\u4e86\u4e00\u58f0\uff0c\u62c9\u7740\u4e8e\u4e00\u98de\u7684\u624b\u5411\u524d\u4e00\u8dc3\uff0c\u5728\u4ed6\u4ee5\u4e3a\u8499\u9762\u4eba\u5fc5\u4f1a\u51fa\u624b\u76f8\u62e6\uff0c\u54ea\u77e5 \u8499\u9762\u4eba\u52a8\u4e5f\u672a\u52a8\uff0c\u53ea\u53cc\u76ee\u4e2d\u5c04\u51fa\u4e00\u79cd\u53e4\u602a\u7684\u5149\u8292\u6ce8\u89c6\u7740\u5389\u9e57\u3002 \u3000\u3000\u5389\u9e57\u53cc\u8896\u957f\u5782\uff0c\u4e00\u4ed8\u4e0d\u5728\u4e4e\u7684\u6837\u5b50\uff0c\u5176\u5b9e\u5fc3\u4e2d\u76d8\u7b97\u8fd9\u9762\u524d\u7684\u8499\u9762\u602a\u4eba\u662f\u4ec0\u4e48\u8def\u6570\uff0c \u5c45\u7136\u6562\u5bf9\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u201d\u53eb\u9635\u3002 \u3000\u3000\u8f9b\u6377\u89c6\u6885\u53d4\u53d4\u5982\u4eb2\u7236\uff0c\u6885\u53d4\u53d4\u7684\u4ec7\u4eba\u4ed6\u65e9\u5c31\u5f53\u505a\u81ea\u5df1\u7684\u4ec7\u4eba\uff0c\u867d\u7136\u4ed6\u8fde\u4e94\u5927\u5251\u6d3e\u638c \u95e8\u4eba\u7684\u6837\u5b50\u90fd\u6ca1\u6709\u770b\u8fc7\uff0c\u4f46\u5728\u4ed6\u5fc3\u76ee\u4e2d\u4ed6\u65e9\u5c31\u628a\u8fd9\u51e0\u4e2a\u5bb6\u4f19\u60f3\u50cf\u6210\u6700\u5351\u9119\u7684\u5c0f\u4eba\u2014\u2014\u5c31 \u50cf\u4ed6\u7684\u6740\u7236\u6bcd\u4ec7\u4eba\u201c\u6d77\u5929\u53cc\u715e\u201d\u4e00\u6837\u3002 \u3000\u3000\u5389\u9e4a\u5782\u624b\u662f\u7b49\u5bf9\u65b9\u5148\u52a8\u624b\uff0c\u5341\u5e74\u6765\u4ed6\u5728\u6b66\u6797\u4e2d\u9690\u9690\u4ee5\u201c\u7b2c\u4e00\u4eba\u201d\u81ea\u5c45\uff0c\u517b\u6210\u4e86\u4ece\u4e0d \u5148\u52a8\u624b\u7684\u4e60\u60ef\u3002\u8fd9\u65f6\u4ed6\u4e45\u4e45\u4e0d\u89c1\u5bf9\u65b9\u52a8\u624b\uff0c\u4e0d\u7981\u6709\u4e9b\u5947\u602a\u3002 \u3000\u3000\u54ea\u77e5\u5c31\u5728\u6b64\u65f6\uff0c\u8499\u9762\u4eba\u5de6\u638c\u4e00\u63a2\uff0c\u75be\u5982\u6d41\u661f\u5730\u76f4\u4fb5\u4ed6\u5f53\u80f8\uff0c\u62f3\u672a\u81f3\uff0c\u52b2\u98ce\u5df2\u5c06\u4ed6\u8863 \u895f\u5439\u5f97\u632f\u52a8\u4e0d\u5df2\u3002 \u3000\u3000\u5389\u9e57\u957f\u7b11\u4e00\u58f0\uff0c\u4e0d\u9000\u53cd\u8fdb\uff0c\u8eab\u5b50\u5fae\u5fae\u4e00\u4fa7\uff0c\u6b3a\u8eab\u659c\u5165\uff0c\u53cc\u6307\u76f4\u53d6\u8499\u9762\u4eba\u53cc\u76ee\u3002 \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u4f55\u7b49\u529f\u529b\uff0c\u90a3\u5bb9\u4ed6\u53cc\u6307\u70b9\u5b9e\uff0c\u63a2\u51fa\u4e4b\u638c\u4e0d\u6536\uff0c\u53f3\u638c\u5df2\u659c\u659c\u65a9\u51fa\uff0c\u6240\u53d6\u4e4b\u4f4d \u6b63\u662f\u5389\u9e57\u8098\u4e0a\u66f2\u6c60\uff0c\u53d8\u62db\u4e4b\u901f\uff0c\u8ba4\u7a74\u4e4b\u51c6\uff0c\u5145\u5206\u8868\u73b0\u51fa\u4e00\u6d3e\u9ad8\u624b\u7684\u4e0d\u51e1\u3002 \u3000\u3000\u5389\u9e57\u4e0d\u5047\u601d\u7d22\u5730\u53d8\u70b9\u4e3a\u5288\uff0c\u540c\u65f6\u53e6\u4e00\u62f3\u7531\u4e0b\u5411\u4e0a\u64a9\u51fa\uff0c\u6b63\u662f\u5d06\u5cd2\u795e\u62f3\u4e2d\u7684\u201c\u5929\u706b\u71ce \u539f\u201d\u3002 \u3000\u3000\u201c\u5929\u706b\u71ce\u539f\u201d\u672c\u662f\u5b88\u4e2d\u5e26\u653b\u7684\u4e00\u8bb0\u5999\u62db\uff0c\u5728\u5251\u795e\u5389\u9e4a\u624b\u4e0b\u65bd\u6765\u66f4\u89c1\u5a01\u529b\uff0c\u4e0a\u638c\u6a2a\u5288 \u4e4b\u52bf\u624d\u53d1\uff0c\u4e0b\u62f3\u5374\u540e\u53d1\u800c\u5148\u81f3\uff0c\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\u3002 \u3000\u3000\u90a3\u77e5\u9053\u62f3\u624d\u9012\u51fa\uff0c\u91cd\u5fc3\u5ffd\u5931\uff0c\u654c\u4eba\u4e0d\u77e5\u600e\u5730\u5728\u5373\u5c06\u4e2d\u62f3\u7684\u4e00\u5239\u90a3\u95f4\u5982\u884c\u4e91\u6d41\u6c34\u5730\u6362 \u4e86\u65b9\u4f4d\u3002\u5389\u9e57\u4f55\u7b49\u7ecf\u9a8c\uff0c\u91cd\u5fc3\u867d\u5931\uff0c\u90a3\u8f83\u7f13\u7684\u638c\u529b\u4ecd\u65e7\u62cd\u51fa\uff0c\u8eab\u4f53\u5374\u501f\u8fd9\u4e00\u62cd\u7684\u4f59\u529b\u6062 \u590d\u4e86\u91cd\u5fc3\uff0c\u800c\u90a3\u4e00\u638c\u4ecd\u51c6\u786e\u5730\u62cd\u5411\u654c\u4eba\u201c\u8170\u773c\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u867d\u5219\u50cf\u662f\u5389\u9e57\u7740\u4e86\u8f9b\u6377\u7684\u9053\u513f\uff0c\u4e8b\u5b9e\u4e0a\u8f9b\u6377\u4e00\u9762\u6a2a\u8eab\u907f\u5f00\u4ed6\u4e00\u62cd\uff0c\u4e00\u9762\u5fc3\u4e2d \u5374\u6697\u6697\u8d5e\u53f9\u5389\u9e57\u7ecf\u9a8c\u4e30\u5bcc\u4e0e\u53d8\u62db\u7684\u901f\u6377\u2014\u2014 \u3000\u3000\u201c\u8981\u662f\u6211\u5904\u4e8e\u8fd9\u5931\u5374\u91cd\u5fc3\u7684\u60c5\u51b5\u4e0b\uff0c\u53ea\u6015\u5fc3\u614c\u610f\u4e71\uff0c\u76ca\u53d1\u4e0d\u53ef\u6536\u62fe\u7684\u4e86\u3002\u201d \u3000\u3000\u8fd9\u4e00\u6362\u62db\uff0c\u4e24\u4eba\u6b63\u597d\u6362\u4e86\u4e2a\u65b9\u5411\uff0c\u5389\u9e57\u53f3\u8896\u4e00\u6325\u4e4b\u95f4\uff0c\u5237\u5730\u4e00\u58f0\uff0c\u4e00\u6c6a\u5bd2\u5149\u95ea\u95ea\u7684 \u957f\u5251\u5df2\u7ecf\u5230\u4e86\u624b\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u9000\u4e86\u534a\u6b65\uff0c\u6ce8\u89c6\u7740\u5389\u9e57\u624b\u4e0a\u90a3\u53ea\u7279\u957f\u7684\u53e4\u5251\uff0c\u4e00\u79cd\u84dd\u68ee\u68ee\u7684\u5149\u8292\u6de1\u6de1\u6563\u51fa\uff0c\u663e \u7136\u662f\u4e00\u67c4\u6781\u4e0a\u4e58\u7684\u5b9d\u5251\u3002\u4ed6\u5fc3\u4e2d\u6697\u8d5e\u9053\uff1a\u201c\u597d\u4e00\u67c4\u5b9d\u5251\uff0c\u4e0d\u77e5\u6bd4\u6885\u53d4\u53d4\u90a3\u67c4\u2018\u6885\u9999\u5251\u2019 \u5982\u4f55\uff1f\u201d \u3000\u3000\u5f53\u65e5\u8f9b\u6377\u827a\u6210\u51fa\u9053\u65f6\uff0c\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u66fe\u5bf9\u4ed6\u8bf4\uff1a\u201c\u636e\u8bf4\u5d06\u5cd2\u5389\u8001\u8d3c\u5f97\u4e86\u4e00\u628a\u4e0a\u53e4 \u5947\u5251\uff0c\u7167\u4f20\u8bf4\u7684\u5f62\u72b6\u770b\u6765\u6781\u53ef\u80fd\u662f\u2018\u501a\u8679\u2019\u5b9d\u5251\uff0c\u82e5\u771f\u662f\u8fd9\u4e00\u67c4\u5b9d\u5251\u7684\u6d3b\uff0c\u6211\u8fd9\u4e00\u67c4 \u2018\u6885\u9999\u5251\u2019\u867d\u4e5f\u662f\u524d\u53e4\u5947\u73cd\uff0c\u4f46\u4e5f\u65e0\u6cd5\u514b\u4f4f\u5b83\uff0c\u5fc5\u987b\u52a0\u4e00\u79cd\u2018\u5343\u5e74\u6731\u7af9\u2019\u7684\u53f6\u6c41\uff0c\u91cd\u4f9d \u53e4\u6cd5\u51b6\u70bc\u8fc7\u624d\u80fd\u514b\u4f4f\u201c\u501a\u8679\u201d\u5251\u4e0a\u90a3\u5c42\u5b9d\u8292\uff0c\u800c\u90a3\u201c\u5343\u5e74\u6731\u7af9\u201d\u5374\u6b63\u597d\u5728\u54b1\u4eec\u5c71\u540e\u8c37\u53d1 \u73b0\u4e86\u4e00\u679d\uff0c\u7b49\u5b83\u719f\u4e86\u4e4b\u540e\u7acb\u523b\u5c31\u53ef\u8d8a\u7076\u51b6\u5251\uff0c\u660e\u5e74\u6b64\u65f6\u4f60\u56de\u5c71\u4e00\u8d9f\uff0c\u5c31\u53ef\u5c06\u6b64\u5251\u4ea4\u7ed9\u4f60\uff0c \u4ece\u6b64\u2018\u866c\u6280\u5251\u5f0f\uff0c\u914d\u4e0a\u2018\u6885\u9999\u5b9d\u5251\u2019\u91cd\u632f\u4e03\u5999\u795e\u541b\u5a01\u540d\uff0c\u54c8\u54c8\u3002\u201d\u8a00\u4e0b\u5145\u6ee1\u5f97\u8272\u3002 \u3000\u3000\u6b63\u56e0\u5982\u6b64\uff0c\u6240\u4ee5\u8f9b\u6377\u6ce8\u89c6\u7740\u5389\u9e57\u624b\u4e0a\u90a3\u84dd\u6c6a\u6c6a\u7684\u950b\u8292\uff0c\u5fc3\u60f3\uff1a\u201c\u4e00\u52a8\u4e0a\u624b\uff0c\u6211\u5175\u5203 \u4e0a\u5fc5\u4f1a\u5403\u4e8f\uff0c\u4e00\u5b9a\u5f97\u4ee5\u5947\u62db\u901f\u51b3\u5bf9\u65b9\u4e3a\u4e0a\u7b56\uff0c\u5509\uff0c\u96be\u602a\u6885\u53d4\u53d4\u4e00\u518d\u53ee\u549b\u6211\u76ee\u4e0b\u5343\u4e07\u4e0d\u8981 \u548c\u4e94\u5927\u5251\u6d3e\u516c\u5f00\u529b\u62fc\u2014\u2014\u4f46\u662f\uff0c\u4eca\u5929\u65e2\u662f\u78b0\u4e0a\u4e86\uff0c\u54fc\uff0c\u597d\u6b79\u4e5f\u5f97\u6597\u4ed6\u4e00\u4e0b\u3002\u201d \u3000\u3000\u4e00\u5ff5\u81f3\u6b64\uff0c\u518d\u4e0d\u72b9\u7591\u4f38\u624b\u51c6\u5907\u62d4\u5251\u3002 \u3000\u3000\u540c\u65f6\uff0c\u5389\u9e57\u90a3\u9634\u68ee\u68ee\u7684\u8bed\u8c03\u626c\u8d77\uff1a\u201c\u5c0f\u5b50\u4eae\u5175\u5203\u5427\uff01\u201d\u5237\u5730\u4e00\u58f0\uff0c\u8f9b\u6377\u5df2\u662f\u62b1\u5251\u5f85 \u654c\uff0c\u52b1\u9e4a\u81ea\u4f8d\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u201d\uff0c\u5c82\u80af\u5148\u52a8\u624b\uff0c\u4e5f\u6301\u5251\u4ee5\u5f85\u3002 \u3000\u3000\u8f9b\u6377\u9694\u7740\u8499\u5e03\u4e2d\uff0c\u5ffd\u7136\u4e00\u63d0\u6c14\uff0c\u542d\u7136\u957f\u5578\uff0c\u90a3\u5578\u58f0\u4e2d\u4e00\u7247\u51b7\u5cfb\uff0c\u5b9b\u5982\u51db\u98ce\u523a\u9aa8\uff0c\u53f3 \u624b\u957f\u5251\u5e73\u51fb\uff0c\u632f\u81c2\u4e00\u6296\uff0c\u96ea\u4eae\u7684\u5251\u5c16\u5728\u9ed1\u6697\u4e2d\u4e00\u9635\u8df3\u52a8\uff0c\u53d1\u51fa\u547c\u547c\u7834\u7a7a\u4e4b\u58f0\u3002 \u3000\u3000\u5bf9\u9762\u7684\u5389\u9e57\u5374\u9762\u8272\u5927\u53d8\uff0c\u6562\u60c5\u4ed6\u770b\u5f97\u6e05\u695a\uff0c\u90a3\u5251\u5c16\u6b63\u6784\u6210\u4e03\u6735\u6885\u82b1\uff0c\u800c\u4e14\u5de5\u6574\u5300\u5f53\uff0c \u4e00\u7b14\u4e0d\u82df\u3002\u4ed6\u5dee\u4e00\u70b9\u5f20\u53e3\u558a\u51fa\u201c\u6885\u5c71\u6c11\u201d\u6765\u3002 \u3000\u3000\u8f9b\u6377\u53c8\u662f\u4e00\u54fc\uff0c\u5251\u5149\u4e00\u531d\uff0c\u8eab\u8eaf\u5e73\u5730\u98d8\u524d\uff0c\u5251\u5c16\u9012\u51fa\u5bf9\u751a\u81f3\u59ff\u52bf\u90fd\u6ca1\u6709\u53d8\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u91cd\u73b0\u6c5f\u6e56\uff0c\u5389\u9e57\u4e5f\u6709\u8033\u95fb\uff0c\u5f53\u5e74\u6885\u5c71\u6c11\u867d\u6bd9\u547d\u5728\u81ea\u5df1\u7b49\u56db\u4eba\u624b\u4e2d\uff0c\u4f46\u90a3\u7ed8 \u58f0\u9634\u5f71\u7684\u4f20\u8bf4\u5230\u5e95\u4e5f\u4ee4\u4ed6\u6709\u70b9\u4e0d\u5b89\uff0c\u4e0d\u8fc7\u4ed6\u59cb\u7ec8\u4ee5\u4e3a\u90a3\u591a\u534a\u662f\u5192\u724c\u8d27\u7f62\u4e86\uff0c\u54ea\u77e5\u76ee\u4e0b\u8fd9 \u4e2a\u8499\u9762\u4eba\u90a3\u624b\u632f\u5251\u7684\u5de5\u592b\u5206\u660e\u662f\u4e03\u5999\u795e\u541b\u7279\u6b8a\u6807\u5fd7\uff0c\u800c\u4e14\u90a3\u4efd\u5185\u52b2\u5b9e\u5728\u6df1\u539a\u5f02\u5e38\uff0c\u83ab\u975e \u2014\u2014 \u3000\u3000\u8fd9\u65f6\u5bf9\u65b9\u7684\u5251\u5c16\u5df2\u75be\u82e5\u96f7\u7535\u5730\u653b\u5230\uff0c\u4ed6\u5fc3\u4e00\u6a2a\uff0c\u6697\u9053\uff1a\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u8fd9\u53ae\u81f3\u5c11\u548c\u6885 \u5c71\u6c11\u6709\u6781\u5927\u7684\u5173\u8054\uff0c\u4e00\u5e76\u6253\u53d1\u4e86\u4ee5\u514d\u540e\u60a3\u201d \u3000\u3000\u6740\u673a\u4e00\u8d77\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u957f\u5251\u6cdb\u7740\u4e00\u7247\u84dd\u5149\u75be\u523a\u5bf9\u65b9\u8109\u95e8\uff0c\u4ee5\u653b\u4e3a\u5b88\u3002 \u3000\u3000\u8499\u9762\u5ba2\u624d\u4e00\u53d8\u5f0f\uff0c\u5389\u9e57\u4e5f\u540c\u65f6\u5f2f\u62db\uff0c\u201c\u5389\u51e4\u671d\u9633\u201d\u76f4\u6307\u8f9b\u6377\u201c\u6c14\u6d77\u7a74\u201d\uff0c\u7ffb\u8155\u4e4b\u95f4\uff0c \u5251\u8eab\u7adf\u5e26\u55e1\u55e1\u4e4b\u58f0\u3002 \u3000\u3000\u8f9b\u6377\u5251\u5f0f\u624d\u53d8\uff0c\u4e00\u89c1\u5389\u9e57\u4e5f\u53d8\uff0c\u4e0d\u52a0\u601d\u7d22\u5730\u4f7f\u51fa\u201c\u98ce\u5f04\u6885\u5f71\u201d\u3002 \u3000\u3000\u201c\u5389\u51e4\u671d\u9633\u201d\u82e5\u65bd\u5b9e\uff0c\u8f9b\u6377\u7684\u201c\u98ce\u5f04\u6885\u5f71\u201d\u4e5f\u6b63\u597d\u9012\u6ee1\uff0c\u90a3\u65f6\u7684\u60c5\u5f62\u5c06\u662f\u5389\u9e4a\u957f\u5251 \u9012\u7a7a\uff0c\u800c\u8f9b\u6377\u5251\u5c16\u5c06\u79bb\u4ed6\u5589\u524d\u4e0d\u53ca\u4e00\u5bf8\u3002 \u3000\u3000\u5389\u9e57\u89c1\u8f9b\u6377\u5251\u5f0f\u624d\u51fa\uff0c\u5df2\u6599\u5230\u4e86\u540e\u679c\uff0c\u5f53\u4e0b\u66f4\u4e0d\u5f85\u201c\u5389\u98ce\u671d\u9633\u201d\u7528\u8001\uff0c\u957f\u5251\u4e00\u6325\uff0c \u8eab\u5f62\u914d\u5408\u8dc3\u8d77\uff0c\u5237\u7684\u4e00\u62db\u201c\u9b3c\u5251\u98de\u5bfb\u201d\uff0c\u5728\u6700\u4f73\u7684\u5730\u4f4d\u523a\u51fa\uff0c\u76f4\u53d6\u5bf9\u65b9\u201c\u8093\u4e0a\u7a74\u201d\uff0c\u800c \u8f9b\u6377\u66f4\u51e0\u4e4e\u662f\u8eab\u4e0d\u7531\u5df1\u5730\u9012\u51fa\u4e00\u62db\u201c\u4e4d\u60ca\u6885\u9762\u201d\u3002 \u3000\u3000\u5389\u9e57\u53c8\u7acb\u523b\u77e5\u9053\u5982\u679c\u81ea\u5df1\u4e0d\u9a6c\u4e0a\u53d8\u62db\uff0c\u8fd9\u4e00\u8bb0\u539f\u6765\u795e\u5999\u65e0\u6bd4\u7684\u201c\u9b3c\u5251\u98de\u7075\u201d\u52bf\u5fc5\u8d70 \u7a7a\uff0c\u800c\u66f4\u7cdf\u7684\u662f\u654c\u4eba\u5251\u5c16\u5c06\u53c8\u6307\u7740\u81ea\u5df1\u65e0\u6cd5\u8eb2\u907f\u7684\u65b9\u4f4d\u3002 \u3000\u3000\u82e5\u662f\u65c1\u4eba\uff0c\u6b64\u65f6\u5df2\u4e34\u7edd\u5883\uff0c\u800c\u5389\u9e4a\u867d\u7136\u53d7\u5236\u4e8e\u4eba\uff0c\u5374\u6709\u60ca\u4eba\u7684\u5224\u65ad\u529b\uff0c\u6bcf\u6b21\u7686\u80fd\u5373 \u65e9\u53d8\u5f0f\uff0c\u4e0d\u8e48\u7edd\u5883\u3002 \u3000\u3000\u201c\u563f\u201d\u5730\u4e00\u58f0\u5410\u6c14\uff0c\u5389\u9e57\u53c8\u786c\u751f\u751f\u64a4\u56de\u4e86\u62db\u5f0f\uff0c\u8f7b\u98d8\u98d8\u843d\u5728\u4e09\u5c3a\u4e4b\u5916\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u53cc\u65b9\u6362\u4e86\u4e09\u62db\uff0c\u5251\u5c16\u90fd\u6ca1\u6709\u78b0\u4e00\u4e0b\uff0c\u800c\u5389\u9e57\u5df2\u4e24\u6b21\u9891\u4e8e\u7edd\u5883\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u8fd9\u5389\u9e57\u5e94\u53d8\u4e4b\u673a\u654f\uff0c\u7aef\u7684\u5e73\u751f\u672b\u9047\uff0c\u800c\u4ed6\u5251\u5f0f\u529f\u529b\u66f4\u8fdc\u5728\u6885\u53d4\u53d4 \u6240\u53d9\u4e4b\u4e0a\uff0c\u96be\u9053\u4ed6\u5341\u5e74\u4e2d\u8fdb\u6b65\u5982\u6b64\u8fc5\u901f\uff1f\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\u4e0d\u7981\u60f3\u8d77\u81ea\u5df1\u521a\u51fa\u9053\u5c31\u6253\u7b97\u53ea\u8eab\u5411\u4e94\u5927\u5251\u6d3e\u6311\u6218\uff0c\u5982\u4eca\u770b\u6765\uff0c\u82e5\u975e\u5c0f\u6222\u5c9b \u4e00\u756a\u5947\u9047\uff0c\u53ea\u6015\u8fde\u773c\u4e0b\u8fd9\u4e00\u4e2a\u4eba\u90fd\u5bf9\u4ed8\u4e0d\u4e86\u5462\u3002 \u3000\u3000\u90a3\u5389\u9e57\u66f4\u60ca\u6050\u5f97\u65e0\u4ee5\u590d\u52a0\uff1a\u201c\u8fd9\u53ae\u62db\u5f0f\u786e\u662f\u4e03\u5999\u795e\u541b\u7684\u2018\u866c\u679d\u5251\u5f0f\u2019\uff0c\u4f46\u4f3c\u6bd4\u4ee5\u524d \u53c8\u8be1\u8bb8\u591a\uff0c\u4f3c\u4e4e\u6bcf\u62db\u90fd\u6070\u5de7\u514b\u4f4f\u6211\u8fd9\u5d06\u5cd2\u5251\u6cd5\u4e00\u6837\uff0c\u83ab\u975e\u771f\u662f\u2014\u2014\u201d \u3000\u3000\u201c\u54fc\u2014\u2014\u201d\u53c8\u662f\u90a3\u51b7\u5cfb\u7684\u9f3b\u58f0\u4f20\u4e86\u8fc7\u6765\uff0c\u4ed6\u5ffd\u7136\u53d1\u89c9\u8fd9\u51b7\u7b11\u58f0\u7aef\u7684\u5341\u5206\u50cf\u90a3\u5341\u5e74\u524d \u7684\u4e03\u5999\u795e\u541b\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u51db\u3002 \u3000\u3000\u8f9b\u6377\u5df2\u4e3b\u52a8\u5c55\u5f00\u201c\u866c\u679d\u5251\u6cd5\u201d\u653b\u4e86\u4e0a\u6765\uff0c\u91cd\u91cd\u5251\u5f71\u5b9b\u5982\u60ca\u6ce2\u6012\u6d9b\u6c79\u6d8c\u800c\u4e0b\u3002 \u3000\u3000\u5251\u795e\u5389\u9e57\u65e2\u79f0\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u201d\uff0c\u5176\u5251\u4e0a\u9020\u8be3\u53ef\u60f3\u800c\u77e5\uff0c\u53ea\u89c1\u4ed6\u5389\u543c\u4e00\u58f0\uff0c\u771f\u529b\u704c\u6ce8 \u5251\u5c16\uff0c\u90a3\u6de1\u6de1\u84dd\u68ee\u68ee\u4e4b\u5149\u6597\u7136\u66b4\u957f\uff0c\u950b\u8292\u4f3c\u4e4e\u8981\u8131\u9896\u800c\u51fa\uff0c\u5251\u5149\u970d\u970d\u4e2d\uff0c\u55e1\u55e1\u4e4b\u58f0\u4e0d\u7edd\uff0c \u5728\u5b8c\u5168\u53d7\u5236\u7684\u62db\u5f0f\u4e2d\u7adf\u7136\u6709\u5b88\u6709\u653b\u3002 \u3000\u3000\u8f9b\u6377\u5251\u5c16\u4e0e\u90a3\u7f15\u84dd\u5149\u4e00\u89e6\uff0c\u8fde\u5fd9\u628a\u5251\u8eab\u4e00\u6a2a\uff0c\u4e0d\u6562\u548c\u5b83\u76f8\u78b0\uff0c\u4f46\u5ffd\u7136\u4e00\u80a1\u5bd2\u6c14\u7adf\u4ece \u5251\u4e0a\u76f4\u4f20\u4e0a\u6765\uff0c\u8f9b\u6377\u5927\u5403\u4e00\u60ca\uff0c\u614c\u5fd9\u4e2d\u594b\u529b\u4e00\u9000\uff0c\u8dc3\u540e\u4e09\u6b65\uff0c\u4f4e\u9996\u5bdf\u770b\u5206\u660e\uff0c\u7adf\u662f\u90a3\u5d06 \u5cd2\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u6000\u4e2d\u8fd8\u62b1\u7740\u4e00\u4eba\uff0c\u90a3\u4eba\u4f3c\u4e4e\u660f\u8ff7\u4e0d\u9192\u5374\u662f\u5929\u7edd\u5251\u8bf8\u845b\u660e\u3002 \u3000\u3000\u5251\u795e\u5389\u9e57\u8138\u8272\u6709\u5982\u6f06\u4e86\u5c42\u58a8\u4e00\u822c\uff0c\u4ee4\u4eba\u987f\u751f\u5bd2\u610f\uff0c\u4ed6\u7f13\u7f13\u8d70\u8fd1\u4e8e\u4e00\u98de\uff0c\u53ea\u89c1\u4e8e\u4e00\u98de \u5934\u53d1\u6563\u4e71\uff0c\u8863\u886b\u7834\u788e\uff0c\u795e\u60c5\u6781\u662f\u72fc\u72c8\uff0c\u8fd9\u65f6\u89c1\u5e08\u7236\u8138\u8272\u4e0d\u5bf9\uff0c\u53ea\u5685\u5685\u5730\u9053\uff1a\u201c\u90a3\u2014\u2014\u91d1 \u8001\u5927\u91d1\u8001\u4e8c\u2014\u2014\u201d \u3000\u3000\u5389\u9e57\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u4ed6\u7adf\u4e0d\u6562\u8bf4\u4e0b\u53bb\uff0c\u4f46\u4ed6\u4ecd\u5bf9\u7ad9\u5728\u5bf9\u9762\u7684\u8499\u9762\u4eba\u6295\u4ee5\u60ca\u8bb6\u7684\u4e00\u773c\uff1a \u662f\u4ec0\u4e48\u4eba\u5728\u2018\u5929\u4e0b\u7b2c\u4e00\u5251\u2019\u624b\u4e0b\u6597\u4e86\u8fd9\u4e48\u4e45\u5c45\u7136\u8eab\u5168\u65e0\u5fd8\uff1f \u3000\u3000\u5389\u9e57\u770b\u4e86\u770b\u660f\u8ff7\u4e2d\u7684\u8bf8\u845b\u660e\uff0c\u7acb\u523b\u53d1\u73b0\u4ed6\u5de6\u80a9\u4e0a\u8863\u886b\u5df2\u6210\u7247\u7247\u788e\u5757\uff0c\u968f\u98ce\u98d8\u52a8\u3002\u6311 \u5f00\u4e00\u65b9\u7834\u895f\u540e\uff0c\u8bf8\u845b\u660e\u80a9\u80bf\u4e0a\u8d6b\u7136\u5370\u7740\u4e00\u53ea\u9ed1\u8272\u638c\u5370\uff0c\u4e94\u6307\u5b9b\u7136\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u770b\u5230\u8fd9\u4e9b\uff0c\u7acb\u523b\u660e\u767d\u8fd9\u5fc5\u662f\u91d1\u8001\u5927\u72ec\u95e8\u638c\u529b\u7684\u6770\u4f5c\uff0c\u4f46\u4ed6\u4ecd\u7136\u633a\u7acb\u539f\u5730\uff0c\u6ca1 \u6709\u4f5c\u58f0\u3002 \u3000\u3000\u5389\u9e57\u5bd2\u7740\u8138\u50cf\u662f\u5728\u67e5\u770b\u8bf8\u845b\u660e\u4f24\u52bf\uff0c\u5176\u5b9e\u5fc3\u4e2d\u6b63\u98de\u5feb\u5730\u6253\u7740\u4e3b\u610f\uff1a\u201c\u6885\u5c71\u6c11\u662f\u54b1\u4eec \u4eb2\u624b\u5bb0\u4e86\u7684\uff0c\u7edd\u4e0d\u4f1a\u9519\uff0c\u8fd9\u8499\u9762\u5c0f\u5b50\u96be\u9053\u662f\u4ed6\u540e\u8f88\u95e8\u4eba\uff1f\u4e0d\u53ef\u80fd\uff0c\u4e0d\u53ef\u80fd\uff0c\u5355\u4ed6\u90a3\u4efd\u5185 \u52b2\uff0c\u6ca1\u6709\u4e00\u7532\u5b50\u4ee5\u4e0a\u7684\u529f\u529b\u662f\u529e\u4e0d\u5230\u7684\uff0c\u90a3\u4e48\uff0c\u4ed6\u53c8\u4f1a\u662f\u8c01\u5462\uff1f\u201d \u3000\u3000\u7684\u786e\uff0c\u6b64\u65f6\u8f9b\u6377\u90a3\u624b\u529f\u592b\u53ea\u6709\u51fa\u81ea\u6885\u5c71\u6c11\u672c\u4eba\u4e4b\u624b\u624d\u4e0d\u81f4\u4ee4\u4eba\u60ca\u5947\u3002 \u3000\u3000\u8bf8\u845b\u660e\u80a9\u4e0a\u7684\u9ed1\u638c\u4ed6\u4e0d\u662f\u6ca1\u6709\u770b\u89c1\uff0c\u4ed6\u53e3\u4e2d\u4e0d\u4f4f\u72e0\u58f0\u5730\u5583\u5583\u5ff5\u9053\uff1a\u201c\u91d1\u8001\u5927\uff0c\u91d1\u8001 \u4e8c\uff0c\u54b1\u4eec\u8d70\u7740\u77a7\u2014\u2014\u201d\u5fc3\u4e2d\u5374\u54ea\u6709\u4e00\u4e1d\u60f3\u5230\u8fd9\u4e2a\u95ee\u9898\uff0c\u53ea\u4e0d\u505c\u5730\u601d\u7d22\u7740\u8fd9\u4e2a\u8499\u9762\u4eba\u3002 \u3000\u3000\u8f9b\u6377\u4ecd\u7136\u76f4\u7acb\u539f\u5730\uff0c\u90a3\u7a0d\u5e26\u7626\u524a\u7684\u8eab\u6750\u4f18\u96c5\u5730\u633a\u7acb\u7740\uff0c\u8499\u5dfe\u4e0a\u90a3\u4e00\u53cc\u7738\u5b50\u4ecd\u7136\u5c04\u51fa \u51b7\u51b7\u7684\u5149\u8f89\u3002 \u3000\u3000\u5389\u9e57\u5ffd\u7136\u5bf9\u4e8e\u4e00\u98de\u559d\u58f0\uff1a\u201c\u8d70\uff01\u201d\u770b\u90fd\u4e0d\u770b\u8f9b\u6377\u4e00\u773c\uff0c\u8f6c\u8eab\u5f80\u91d1\u6c0f\u5144\u5f1f\u53bb\u5411\u8ffd\u53bb\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u62b1\u7740\u8bf8\u845b\u660e\u4e5f\u8ddf\u4e86\u4e0a\u53bb\uff0c\u4e34\u8d70\u8fd8\u5411\u8fd9\u4e2a\u8499\u9762\u4eba\u6295\u4e86\u60ca\u5947\u7684\u4e00\u773c\u3002 \u3000\u3000\u5389\u9e57\u8fd9\u4e00\u8d70\u7684\u786e\u662f\u806a\u660e\u7684\uff0c\u773c\u524d\u8fd9\u4e2a\u8499\u9762\u4eba\u6df1\u539a\u7684\u529f\u529b\u56f0\u60d1\u7740\u4ed6\uff0c\u4ed6\u4ecd\u4e0d\u80fd\u76f8\u4fe1\u4e03 \u5999\u795e\u541b\u4f1a\u6b7b\u800c\u590d\u6d3b\uff0c\u4f46\u8499\u9762\u4eba\u7cbe\u5999\u7684\u5251\u6cd5\u6b63\u662f\u58f0\u540d\u5929\u4e0b\u7684\u201c\u866c\u679d\u5251\u5f0f\u201d\uff0c\u81f3\u5c11\u8fd9\u662f\u5343\u51c6 \u4e07\u786e\u7684\uff0c\u4ed6\u51b3\u4e0d\u80fd\u628a\u4e00\u4e16\u201c\u82f1\u540d\u201d\u8d4c\u6ce8\u5728\u8fd9\u4e0d\u77e5\u771f\u9762\u76ee\u7684\u8499\u9762\u5ba2\u8eab\u4e0a\uff0c\u8fd9\u4e00\u8d70\uff0c\u65e2\u53ef\u6682 \u65f6\u6446\u8131\uff0c\u53c8\u53ef\u7ef4\u6301\u4ed6\u90a3\u539f\u6709\u7684\u50b2\u6162\u72c2\u6001\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u4ecd\u7acb\u539f\u5730\uff0c\u6ca1\u6709\u8ffd\u62e6\uff0c\u4ed6\u5fc3\u4e2d\u60f3\uff1a\u201c\u7b49\u2018\u6885\u9999\u5251\u2019\u91cd\u51b6\u597d\u4e86\uff0c\u5c31\u6709\u4f60\u4e50\u7684 \u4e86\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u7684\u80cc\u5f71\u624d\u6d88\u5931\uff0c\u8f9b\u6377\u53c8\u542c\u5230\u4e00\u9635\u8863\u5e26\u7834\u7a7a\u7684\u75be\u97f3\uff0c\u679c\u7136\u6765\u8def\u4e0a\u5954\u6765\u4e00\u4eba\u3002 \u3000\u3000\u63a5\u8fd1\u5c0f\u5e99\u65f6\uff0c\u90a3\u4eba\u81ea\u7136\u653e\u6162\u4e86\u811a\u6b65\uff0c\u6708\u5149\u4e0b\u53ea\u89c1\u90a3\u4eba\u4e00\u88ad\u9752\u886b\uff0c\u6f47\u6d12\u7684\u8eab\u6750\u4e0a\u662f\u4e00 \u5f20\u4fca\u7f8e\u7684\u8138\u5b54\uff0c\u659c\u98de\u4eba\u9b13\u7684\u53cc\u7709\u4e0b\uff0c\u4e00\u53cc\u661f\u6708\u5c04\u51fa\u667a\u6167\u7684\u5149\u8292\uff0c\u7adf\u662f\u548c\u8f9b\u6377\u5206\u624b\u4e86\u7684\u5434 \u51cc\u98ce\u3002 \u3000\u3000\u8f9b\u6377\u56de\u9996\u89c1\u5389\u9e57\u7b49\u5df2\u8fdc\u53bb\uff0c\u8eab\u5b50\u8e72\u4e0b\u6765\uff0c\u501f\u7740\u4e00\u6392\u6811\u7684\u63a9\u853d\uff0c\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\u4ece\u5e99\u540e \u7ed5\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u53ea\u89c1\u5434\u51cc\u98ce\u4e5f\u6b63\u6253\u7b97\u63a8\u5f00\u5e99\u95e8\uff0c\u90a3\u5468\u56f4\u9634\u6c89\u7684\u6c14\u6c1b\u81ea\u7136\u5730\u4f7f\u5434\u51cc\u98ce\u4fca\u7f8e\u7684\u8138\u5b54\u7f29\u6210 \u4e00\u7247\u7d27\u5f20\u7684\u795e\u60c5\u3002 \u3000\u3000\u8f9b\u6377\u8f7b\u7ed5\u5230\u4ed6\u8eab\u540e\uff0c\u5237\u5730\u5c06\u957f\u5251\u62d4\u51fa\uff0c\u5251\u51fa\u9798\u662f\u6e05\u8106\u5730\u201c\u5361\u201d\u4e00\u58f0\uff0c\u50cf\u662f\u5728\u4e00\u6e56\u5e73 \u5982\u955c\u9762\u7684\u6c34\u4e2d\u6295\u4eba\u4e00\u7c92\u77f3\u5b50\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5982\u4e00\u9635\u65cb\u98ce\u4e00\u822c\u8f6c\u8fc7\u8eab\u6765\uff0c\u957f\u5251\u5df2\u5728\u624b\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u628a\u626f\u4e0b\u8499\u5dfe\uff0c\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u5434\u51cc\u98ce\u4e5f\u7b11\u4e86\u8d77\u6765\u9053\uff1a\u201c\u554a\uff01\u662f\u4f60\uff0c\u6377\u5f1f\uff0c\u4f60\u771f\u987d\u76ae\uff0c\u771f\u628a\u6211\u5413\u4e86\u4e00\u8df3\u5462\u3002\u201d \u3000\u3000\u8f9b\u6377\u6545\u610f\u9053\uff1a\u201c\u90a3\u5929\u4f60\u7a81\u7136\u8dd1\u6389\uff0c\u6211\u4e00\u4eba\u548c\u6d77\u5929\u53cc\u715e\u62fc\u6597\uff0c\u5dee\u4e00\u70b9\u9001\u4e86\u547d\u54e9\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u542c\u7f62\u5927\u5403\u4e00\u60ca\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u51ed\u4f60\u7684\u8f7b\u529f\u5f15\u5f00\u4ed6\u4eec\u518d\u8bbe\u6cd5\u6e9c\u6389\u5e94\u8be5\u6ca1\u6709\u95ee\u9898\uff0c \u54ea\u77e5\u9053\u4f60\u771f\u548c\u4ed6\u4eec\u62fc\u4e86\u8d77\u6765\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5c31\u5c06\u7ecf\u8fc7\u544a\u77e5\u5434\u51cc\u98ce\uff0c\u51cc\u98ce\u672c\u6765\u5341\u5206\u7d27\u5f20\uff0c\u4f46\u89c1\u8f9b\u6377\u8bf4\u6765\u5b09\u76ae\u7b11\u8138\uff0c\u4e5f\u4e0d\u7531\u7b11 \u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u7aef\u7684\u798f\u7f18\u6df1\u539a\u3002\u201d \u3000\u3000\u8f9b\u6377\u672c\u6765\u4ece\u5c0f\u517b\u6210\u4e86\u9634\u6c89\u800c\u504f\u6fc0\u7684\u6027\u683c\uff0c\u4f46\u5728\u8fd9\u4e2a\u65b0\u7ed3\u8bc6\u7684\u5144\u5f1f\u9762\u524d\uff0c\u5374\u53d8\u5f97\u6709\u8bf4 \u6709\u7b11\u3002 \u3000\u3000\u5434\u51cc\u98ce\u4e5f\u5c06\u81ea\u5df1\u7684\u7ecf\u8fc7\u8bf4\u4e86\u51fa\u6765\uff0c\u6700\u540e\u4ed6\u8bf4\uff1a\u201c\u6211\u8feb\u90a3\u8bf8\u845b\u660e\uff0c\u53c8\u78b0\u4e0a\u90a3\u5389\u8001\u8d3c\uff0c \u662f\u4ee5\u4e00\u8def\u4f5c\u6697\u8bb0\u53eb\u4f60\u6765\uff0c\u51c6\u5907\u5408\u529b\u7ed9\u4ed6\u70b9\u5389\u5bb3\u770b\u770b\uff0c\u540e\u6765\u6211\u53c8\u63a2\u51fa\u5389\u8001\u8d3c\u548c\u4ec0\u4e48\u4e10\u5e2e\u6709 \u74dc\u845b\u2026\u2026\u6211\u4e00\u8def\u8ffd\u8e2a\u800c\u6765\uff0c\u5230\u8fd9\u91cc\u5374\u5931\u4e86\u4ed6\u4eec\u8e2a\u8ff9\uff0c\u54c8\uff01\u5012\u78b0\u4e0a\u4e86\u4f60\u3002\u201d \u3000\u3000\u8f9b\u6377\u628a\u81ea\u5df1\u548c\u5389\u9e57\u62fc\u6597\u7684\u60c5\u5f62\u8bf4\u4e86\u4e00\u904d\uff0c\u51cc\u98ce\u9053\uff1a\u201c\u539f\u6765\u4f60\u5df2\u78b0\u4e0a\u4e86\u4ed6\u4e86\u2014\u2014\u201d\u4ed6 \u60f3\u5230\u51ed\u8f9b\u6377\u4e00\u8eab\u672c\u9886\u5c45\u7136\u5948\u4f55\u90a3\u5389\u9e57\u4e0d\u5f97\uff0c\u81ea\u5df1\u60f3\u4ee5\u5355\u8eab\u62a5\u7236\u4ec7\uff0c\u524d\u9014\u53ea\u6015\u9eef\u6de1\u5f97\u5f88\uff0c \u4e0d\u7981\u8f7b\u53f9\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u8f9b\u6377\u4f55\u7b49\u806a\u654f\uff0c\u88c5\u7740\u7565\u6709\u6240\u609f\u7684\u6837\u5b50\u9053\uff1a\u201c\u554a\uff01\u5bf9\u4e86\uff0c\u8fd8\u6709\u5341\u51e0\u5929\u5c31\u662f\u4e94\u5927\u5251\u6d3e\u7684 \u6cf0\u5c71\u5927\u4f1a\uff0c\u54b1\u4eec\u5c31\u53bb\u4e00\u8d9f\uff0c\u4e5f\u8ba9\u5929\u4e0b\u4eba\u77e5\u9053\u2018\u5355\u5251\u65ad\u9b42\u2019\u7edd\u827a\u6709\u4f20\uff0c\u5927\u54e5\uff0c\u54b1\u4eec\u8fd9\u5c31\u53bb \u5427\uff01\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u53c8\u6fc0\u8d77\u4e86\u5434\u51cc\u98ce\u7684\u4e07\u4e08\u96c4\u5fc3\uff0c\u4ed6\u5251\u7709\u4e00\u626c\uff0c\u6717\u58f0\u9053\uff1a\u201c\u5434\u67d0\u5b66\u827a\u867d\u6709\u6127\u5148\u4eba\uff0c \u4f46\u4e5f\u597d\u6b79\u8981\u8fd9\u6279\u81ea\u547d\u6b63\u6d3e\u540d\u95e8\u7684\u5c0f\u4eba\u77e5\u9053\u5389\u5bb3\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e5f\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u5927\u54e5\u5728\u6211\u9762\u524d\u600e\u4e48\u81ea\u79f0\u5434\u67d0\uff1f\u54b1\u4eec\u8fd9\u5c31\u8d70\u5427\u3002\u201d \u3000\u3000\u7199\u548c\u7684\u9633\u5149\u666e\u7167\u7740\u5927\u5730\uff0c\u9053\u8def\u4e0a\u6628\u591c\u7684\u96e8\u9732\u88ab\u5f15\u5165\u5e72\u71e5\u7684\u9ec4\u6c99\u4e2d\uff0c\u4f46\u7ecf\u9633\u5149\u4e00\u6652\uff0c \u4e00\u4e1d\u4e1d\u6c34\u6c14\u5192\u4e86\u51fa\u6765\uff0c\u66ff\u8fd9\u660e\u5a9a\u7684\u666f\u8272\u52a0\u5165\u4e00\u4e1d\u6a21\u7cca\u4e4b\u7f8e\u3002 \u3000\u3000\u5f97\u5f97\u8e44\u58f0\uff0c\u5f2f\u9053\u8f6c\u51fa\u4e24\u5339\u767d\u9a6c\uff0c\u7c73\u9ec4\u8272\u7684\u9633\u5149\u6d12\u5728\u6d01\u767d\u7684\u6bdb\u9b03\u4e0a\uff0c\u95ea\u8000\u7740\u8c61\u7259\u822c\u5149 \u8292\u3002 \u3000\u3000\u9a6c\u4e0a\u7684\u4eba\u90fd\u662f\u4e00\u822c\u7684\u5e74\u8f7b\uff0c\u4e00\u822c\u7684\u79c0\u4fca\uff0c\u66f4\u5947\u7684\u662f\u4e24\u4eba\u90fd\u4f3c\u5728\u6c89\u601d\u4e2d\u3002 \u3000\u3000\u5de6\u9762\u767d\u886b\u7684\u9752\u5e74\u6b63\u6c89\u601d\u7740\uff1a\u201c\u8f9b\u6377\u554a\uff01\u8f9b\u6377\u554a\uff01\u773c\u524d\u7684\u654c\u4eba\u662f\u4e00\u6d41\u7684\u9b54\u5934\uff0c\u4f60\u5343\u4e07 \u4e0d\u80fd\u7a0d\u4e3a\u5927\u610f\u554a\u2014\u2014\u201d\u4ed6\u60f3\u8d77\u81ea\u5df1\u88ab\u201c\u6d77\u5929\u53cc\u715e\u201d\u903c\u4eba\u60ac\u5d16\uff0c\u4e0d\u7981\u6697\u53f9\u4e00\u58f0\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\u5982\u679c\u8ba9\u4eba\u628a\u8fd9\u4e8b\u4f20\u5165\u6c5f\u6e56\uff0c\u8bf4\u662f\u4e00\u4e2a\u4eba\u529b\u6218\u6d77\u5929\u53cc\u715e\u6570\u5343\u62db\u4e0d\u5206\u80dc\u8d1f\uff0c\u6050\u6015 \u8981\u9707\u52a8\u5929\u4e0b\uff01\u540c\u65f6\uff0c\u53f3\u9762\u84dd\u886b\u7684\u7f8e\u5c11\u5e74\u5374\u5583\u5583\u81ea\u7977\uff1a\u201c\u7236\u4eb2\u82f1\u7075\u5728\u4e0a\uff0c\u4fdd\u4f51\u4e0d\u5b5d\u513f\u624b\u5203 \u4ec7\u4eba\u2014\u2014\u201d \u3000\u3000\u8e44\u58f0\u626c\u5904\uff0c\u4e8c\u9a91\u5df2\u5306\u5306\u800c\u8fc7\u3002 \u3000\u3000\u4e00\u8def\u884c\u6765\uff0c\u4e8c\u4eba\u8fb9\u8d70\u8fb9\u8c08\uff0c\u4e1d\u6beb\u4e0d\u89c9\u5bc2\u5bde\u3002 \u3000\u3000\u90a3\u5251\u795e\u5389\u9e57\u7684\u529f\u592b\u786e\u5b9e\u610f\u5916\u5730\u9ad8\u5f3a\uff0c\u65e0\u8bba\u662f\u5185\u529f\u3001\u5916\u529b\u90fd\u662f\u4e0a\u4e58\u4e4b\u81f3\uff0c\u4e0d\u8fc7\u8fd9\u5012\u53cd \u800c\u6fc0\u8d77\u8f9b\u6377\u7684\u96c4\u5fc3\uff0c\u6562\u60c5\u4ed6\u662f\u60f3\u5230\u4ee5\u5389\u9e57\u7684\u529f\u529b\uff0c\u5341\u5e74\u524d\u8fd8\u4e0d\u662f\u4e56\u4e56\u81e3\u670d\u5728\u6885\u53d4\u53d4\u4e4b\u4e0b\uff1f \u3000\u3000\u5434\u51cc\u98ce\u5012\u6ca1\u6709\u600e\u4e48\u6837\uff0c\u4ed6\u81ea\u5df1\u5fc3\u5934\u6709\u6570\uff0c\u5389\u9e57\u7684\u529f\u529b\u662f\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u4f55\u51b5\u8fd8\u6709\u53e6\u5916 \u4e09\u4e2a\u5f3a\u5927\u7684\u9ad8\u624b\u5462\uff1f\u4e0d\u8fc7\u4ed6\u5374\u662f\u5014\u5f3a\u7684\u4eba\uff0c\u53cd\u5012\u52a0\u5f3a\u4e86\u6124\u6012\u7684\u4ec7\u5fc3\uff0c\u4e0b\u4e86\u7834\u91dc\u6c89\u821f\u7684\u51b3 \u5b9a\uff01 \u3000\u3000\u591a\u5c11\u5929\u6765\uff0c\u5434\u51cc\u98ce\u5bf9\u8f9b\u6377\u795e\u5999\u7684\u529f\u592b\u5df2\u4f69\u670d\u5230\u4e86\u6781\u70b9\u3002\u81f3\u5c11\u8f9b\u6377\u7684\u5185\u529f\u9020\u8be3\u5df2\u8fbe\u5230 \u80fd\u6536\u655b\u7cbe\u5149\u5955\u5955\u7684\u773c\u795e\u7684\u5730\u6b65\u4e86\u3002 \u3000\u3000\u8fd9\u4e00\u6bb5\u8def\u662f\u4ece\u6e56\u5317\u5230\u6cb3\u5357\u7684\u5b98\u9053\uff0c\u4e2d\u95f4\u88ab\u6850\u67cf\u5c71\u6240\u9694\uff0c\u5b98\u9053\u662f\u4f9d\u5c71\u800c\u7b51\uff0c\u82e5\u662f\u987a\u7740 \u5b98\u9053\u800c\u884c\uff0c\u5219\u8981\u591a\u8d39\u4e0a\u4e00\u767e\u591a\u91cc\u8def\u7684\u65f6\u8fb0\uff0c\u4e8c\u4eba\u6765\u5230\u9053\u8fb9\uff0c\u5546\u91cf\u4e00\u756a\uff0c\u9f50\u653e\u9a6c\u5954\u5411\u5c71\u9053 \u800c\u884c\u3002 \u3000\u3000\u4e8c\u4eba\u4ed7\u7740\u4e00\u8eab\u529f\u592b\uff0c\u60f3\u7ffb\u8fc7\u5c71\u5934\uff0c\u7701\u4e0b\u5c06\u8fd1\u4e00\u5929\u7684\u65f6\u95f4\u3002 \u3000\u3000\u5165\u4eba\u5f97\u5c71\u533a\uff0c\u4e8c\u4eba\u4e0d\u518d\u52d2\u9a6c\u7f13\u884c\uff0c\u9f50\u4e00\u653e\u677e\u624b\u4e2d\u7f30\u513f\uff0c\u98ce\u9a70\u7535\u9a76\u822c\u5954\u5411\u67cf\u916e\u5c71\u4e2d\uff0c \u4e0d\u6d88\u7247\u523b\uff0c\u4fbf\u6d88\u5931\u5728\u5c71\u9053\u56de\u5f2f\u5904\u3002 \u3000\u3000\u9a6c\u8e44\u58f0\u5f97\u5f97\uff0c\u4e8c\u4eba\u9a70\u9a8b\u5728\u5c71\u9053\u4e0a\uff0c\u626c\u8d77\u6f2b\u5929\u98ce\u6c99\u3002 \u3000\u3000\u8fd9\u65f6\u5df2\u662f\u5165\u79cb\u65f6\u5206\uff0c\u5c71\u4e2d\u66f4\u662f\u91d1\u98ce\u9001\u723d\uff0c\u6850\u67cf\u5c71\u4e0a\u5374\u662f\u7a00\u89c1\u68ee\u6797\uff0c\u53ea\u662f\u5149\u79c3\u79c3\u7684\u4e00 \u7247\uff0c\u5076\u800c\u4e00\u4e8c\u682a\u6811\u513f\u8038\u7acb\u5728\u65c1\uff0c\u4e5f\u90fd\u53f6\u513f\u6e10\u67af\uff0c\u663e\u5f97\u6709\u4e9b\u8427\u6761\u7684\u6c14\u6c1b\uff01 \u3000\u3000\u552f\u4e00\u7684\u662f\u5929\u6c14\u751a\u597d\uff0c\u84dd\u5929\u4e00\u78a7\uff0c\u5929\u9ad8\u6c14\u723d\uff0c\u4e8c\u4eba\u4e00\u8def\u884c\u6765\uff0c\u5230\u4e5f\u6709\u4e0d\u5c11\u4e50\u8da3\u3002 \u3000\u3000\u84e6\u5730\uff0c\u773c\u524d\u5730\u52bf\u7a81\u7a84\uff0c\u4ec5\u6709\u4e00\u6761\u513f\u901a\u5f84\uff0c\u7a84\u5f97\u4ec5\u80fd\u5bb9\u4e00\u4eba\u4e00\u9a91\u52c9\u5f3a\u901a\u8fc7\uff0c\u4e8c\u4eba\u4e00\u6536 \u9a6c\u7f09\uff0c\u7f13\u4e0b\u52bf\u6765\uff0c\u6253\u91cf\u4e00\u756a\uff0c\u4f46\u89c1\u51fa\u4e86\u8fd9\u901a\u9053\uff0c\u524d\u9762\u5730\u52bf\u9661\u4f4e\uff0c\u800c\u4e14\u602a\u7684\u662f\u4e00\u4e2a\u5728\u6850\u67cf \u5c71\u4e0b\u5c11\u89c1\u7684\u5c0f\u5c0f\u6811\u6797\u3002 \u3000\u3000\u4e8c\u4eba\u4e8e\u662f\u7f13\u7f13\u884c\u53bb\uff0c\u5012\u662f\u5434\u51cc\u98ce\u884c\u5728\u524d\u9762\uff0c\u4e00\u8def\u6162\u6162\u901a\u8fc7\u5c0f\u5f84\u3002 \u3000\u3000\u5c0f\u5f84\u957f\u7ea6\u5345\u5bfb\u4e08\uff0c\u5f84\u8fb9\u91ce\u8349\u4e1b\u751f\uff0c\u548c\u524d\u5c71\u4e00\u5e26\u9ec4\u571f\u904d\u5730\u7684\u60c5\u5f62\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u4e8c\u4eba\u884c\u5f97\u4e00\u534a\uff0c\u5ffd\u7136\u4e00\u9635\u5175\u5203\u4ea4\u51fb\u58f0\u968f\u98ce\u4f20\u6765\uff0c\u4e14\u9690\u9690\u6742\u6709\u4e00\u4e8c\u58f0\u54ed\u557c\uff0c\u4f20\u81ea\u90a3\u4e0d \u8fdc\u7684\u6797\u5b50\u4e2d\u3002\u4e8c\u4eba\u5fae\u5fae\u4e00\u6014\uff0c\u9f50\u52a0\u5feb\u9a6c\u513f\uff0c\u90a3\u6599\u8def\u9762\u592a\u7a84\uff0c\u9a6c\u513f\u4e0d\u6562\u5feb\u5954\uff0c\u4ec5\u957f\u5636\u4e00\u58f0\uff0c \u5e76\u4e0d\u52a0\u5feb\u901f\u5ea6\u3002 \u3000\u3000\u8fd9\u65f6\u6765\u5f97\u66f4\u8fd1\uff0c\u5175\u5203\u4ea4\u51fb\u58f0\u66f4\u6e05\u6670\u5730\u4f20\u6765\uff0c\u8f9b\u6377\u9053\uff1a\u201c\u597d\u50cf\u662f\u6709\u4e09\u4e2a\u4eba\u5728\u4ea4\u624b\u2014\u2014\u201d \u6562\u60c5\u4ed6\u662f\u6253\u90a3\u5175\u5203\u58f0\u6709\u4e09\u79cd\u4e0d\u540c\u7684\u58f0\u97f3\u6240\u6742\u5408\u800c\u6210\u542c\u51fa\u6765\u7684\u3002 \u3000\u3000\u5434\u51cc\u98ce\u70b9\u4e86\u70b9\u5934\uff0c\u84e6\u5730\uff0c\u5175\u5203\u4e4b\u58f0\u5927\u4f5c\uff0c\u4f46\u4ec5\u4ec5\u4e00\u4e0b\uff0c\u4fbf\u560e\u7136\u800c\u6b62\uff0c\u53ea\u5269\u4e0b\u90a3\u94ff\u9535 \u7684\u4f59\u58f0\uff0c\u7f13\u7ed5\u5728\u7a7a\u4e2d\u3002 \u3000\u3000\u4e8c\u4eba\u540c\u65f6\u4e00\u60ca\uff0c\u6562\u60c5\u8fd9\u4e00\u4e0b\u786c\u649e\u786c\u6240\u53d1\u51fa\u7684\u5639\u4eae\u58f0\u97f3\u51b3\u975e\u6c5f\u6e56\u5eb8\u624b\u6240\u80fd\u529e\u5230\uff0c\u4e8c\u4eba \u4e0d\u518d\u505c\u7559\uff0c\u8eab\u8eaf\u9f50\u8131\u978d\u98de\u51fa\uff0c\u8f7b\u5de7\u5730\u7684\u843d\u5728\u6797\u8fb9\u3002 \u3000\u3000\u63a2\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u4e8c\u4e2a\u4eba\u6b63\u5728\u4ea4\u624b\uff0c\u65c1\u8fb9\u5374\u5750\u7740\u4e00\u4e2a\u5973\u4eba\uff0c\u6b63\u5728\u54ed\u557c\u3002 \u3000\u3000\u518d\u4e00\u6253\u91cf\uff0c\u53ea\u89c1\u53e6\u6709\u4e00\u4e2a\u5e74\u7ea6\u56db\u5341\u4e03\u516b\u7684\u5927\u6c49\u6b63\u5728\u641c\u7d22\u65c1\u8fb9\u7684\u4e00\u8f86\u9a6c\u8f66\uff0c\u800c\u4e14\u5730\u4e0a \u6a2a\u4e03\u575a\u516b\u5730\u8eba\u7740\u4e00\u5927\u5806\u6b7b\u5c38\uff01 \u3000\u3000\u4e8c\u4eba\u9f50\u628a\u773c\u5149\u96c6\u4e2d\u5728\u6253\u6597\u7684\u4e8c\u4eba\u8eab\u4e0a\uff0c\u4f46\u662f\u80cc\u7740\u7684\u4e00\u4eba\u53cc\u624b\u6301\u7740\u4e8c\u822c\u5175\u5203\uff0c\u5374\u662f\u4e0d \u540c\u79cd\u7c7b\u7684\uff0c\u5de6\u624b\u6301\u7684\u662f\u4e00\u67c4\u5251\uff0c\u53f3\u624b\u5374\u4f7f\u7684\u4e00\u652f\u9524\u513f\uff0c\u800c\u9762\u5bf9\u7740\u81ea\u5df1\u7684\u5374\u662f\u4e00\u4e2a\u5e74\u7ea6\u56db \u5341\u4f59\u7684\u4e2d\u5e74\u4eba\uff0c\u624b\u6301\u957f\u5251\uff0c\u6562\u60c5\u90a3\u4e09\u822c\u5175\u5203\u4e0d\u65f6\u4ea4\u649e\uff0c\u662f\u4ee5\u53d1\u51fa\u4e09\u822c\u4e0d\u540c\u7684\u5175\u5668\u58f0\u97f3\u3002 \u3000\u3000\u90a3\u624b\u6301\u957f\u5251\u7684\u4eba\u529f\u529b\u751a\u9ad8\uff0c\u65e9\u5df2\u53d6\u5f97\u4f18\u52bf\uff0c\u4e00\u652f\u957f\u5251\u5ffd\u4e0a\u5ffd\u4e0b\uff0c\u4e0d\u65f6\u6492\u51fa\u6f2b\u5929\u5251\u82b1\u3002 \u3000\u3000\u90a3\u5de6\u5251\u53f3\u9524\u7684\u6c49\u5b50\u5df2\u662f\u4e0d\u652f\uff0c\u8fde\u8fde\u540e\u9000\u3002 \u3000\u3000\u84e6\u5730\u90a3\u6301\u957f\u5251\u7684\u4eba\u5927\u53f1\u4e00\u58f0\uff0c\u957f\u5251\u5012\u5288\u4e0b\u6765\uff0c\u90a3\u53f3\u9524\u5de6\u5251\u7684\u4eba\u4f3c\u4e4e\u4e0d\u613f\u786c\u62fc\uff0c\u540e\u9000 \u4e00\u6b65\uff0c\u60f3\u907f\u5f00\u767e\u7834\u5929\u60ca\u822c\u7684\u4e00\u62db\u3002 \u3000\u3000\u54ea\u77e5\u5bf9\u624b\u4e0d\u5f85\u62db\u5f0f\u7528\u8001\uff0c\u7a81\u5730\u4e00\u632f\u957f\u5251\uff0c\u5bd2\u5149\u4e00\u5410\uff0c\u4ece\u5251\u5708\u4e2d\u731b\u653b\u4e00\u5251\u3002 \u3000\u3000\u90a3\u5de6\u5251\u53f3\u9524\u7684\u6c49\u5b50\u4e0d\u865e\u6709\u6b64\uff0c\u8eab\u5f62\u6025\u95ea\uff0c\u53f3\u624b\u9524\u513f\u53cd\u70b9\u5411\u5bf9\u624b\u8109\u95e8\u3002 \u3000\u3000\u54ea\u77e5\u5bf9\u624b\u6b64\u62db\u53c8\u662f\u865a\u62db\uff0c\u957f\u5251\u84e6\u5730\u4e00\u632f\uff0c\u4ecd\u662f\u539f\u5f0f\u5012\u5288\u800c\u4e0b\uff0c\u90a3\u5de6\u5251\u53f3\u9524\u7684\u6c49\u5b50\u4e0d \u6599\u5bf9\u624b\u53d8\u62db\u5982\u6b64\u901f\u5ea6\uff0c\u773c\u770b\u95ea\u8eb2\u4e0d\u53ca\uff0c\u53ea\u597d\u5251\u9524\u4e92\u76f8\u4e00\u649e\uff0c\u98de\u8eab\u9f13\u8db3\u5185\u529b\uff0c\u51c6\u5907\u786c\u62fc\u4e00 \u8bb0\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4e09\u822c\u5175\u5668\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u7136\u63a5\u89e6\uff0c\u90a3\u6301\u957f\u5251\u7684\u4e2d\u5e74\u957f\u7b11\u4e00\u58f0\uff0c \u5185\u529b\u9661\u53d1\uff0c\u4f46\u95fb\u201c\u94ff\u201d\u7684\u4e00\u58f0\uff0c\u5bf9\u624b\u9524\u5251\u540c\u65f6\u51cc\u7a7a\u98de\u8d77\u3002 \u3000\u3000\u957f\u7b11\u58f0\u4e2d\uff0c\u90a3\u624b\u6301\u957f\u5251\u7684\u6c49\u5b50\u53cc\u8db3\u6025\u6643\uff0c\u4e00\u8fde\u8e22\u51fa\u4e03\u516b\u811a\u4e4b\u591a\uff0c\u90a3\u5de6\u5251\u53f3\u9524\u7684\u6c49\u5b50 \u8f93\u62db\u540e\u518d\u53d7\u6b64\u4e00\u8f6e\u6025\u653b\uff0c\u767b\u65f6\u4e00\u9635\u614c\u4e71\uff0c\u88ab\u8e22\u4e2d\u8dcc\u5728\u5730\u4e0a\u3002 \u3000\u3000\u84e6\u5730\u4eba\u5f71\u4e00\u95ea\uff0c\u90a3\u5728\u4e00\u65c1\u641c\u7d22\u9a6c\u8f66\u7684\u5927\u6c49\u8dc3\u4e86\u8fc7\u6765\uff0c\u4e00\u628a\u6276\u8d77\u90a3\u5012\u5728\u5730\u4e0a\u7684\u6c49\u5b50\uff0c \u5411\u90a3\u4e2d\u5e74\u559d\u9053\uff1a\u201c\u9601\u4e0b\u771f\u597d\u8eab\u624b\uff0c\u4e14\u63a5\u5927\u7237\u4e00\u638c\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u90a3\u4e2d\u5e74\u5df2\u7eb3\u5251\u5165\u9798\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5c71\u5de6\u53cc\u8c6a\uff0c\u6b66\u827a\u901a\u795e\uff0c\u600e\u4e48\u6765\u5230\u6850 \u67cf\u5c71\u533a\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e00\u542c\uff0c\u4e0d\u89c9\u5fae\u60ca\uff0c\u60f3\u4e0d\u5230\u8fd9\u4e8c\u4e2a\u5927\u6c49\u7adf\u662f\u72ec\u9738\u5c71\u4e1c\u7684\u5f3a\u76d7\uff0c\u4e00\u4e3a\u6458\u661f\u624b\u53f8\u7a7a\u5b97\uff0c \u4e00\u4e3a\u795e\u5251\u91d1\u9524\u6797\u5c11\u768b\uff0c\u6614\u5e74\u4faf\u53d4\u53d4\u8bf4\u6b66\u6797\u638c\u6545\u65f6\uff0c\u4e5f\u66fe\u6781\u529b\u8d5e\u8bf4\u6b64\u4e8c\u4eba\u7684\u6b66\u672f\uff0c\u5c24\u5176\u662f \u6458\u661f\u624b\uff0c\u66f4\u662f\u4e00\u7b49\u4e00\u7684\u9b54\u5934\u3002\u6b64\u65f6\u90a3\u4e2d\u5e74\u4eba\u7adf\u6253\u8d25\u90a3\u795e\u5251\u91d1\u9524\uff0c\u5b9e\u5728\u4ee4\u4eba\u60ca\u5f02\uff01 \u3000\u3000\u6b63\u6c89\u541f\u95f4\uff0c\u90a3\u6276\u7740\u91d1\u9524\u5927\u6c49\u7684\u4e2d\u5e74\u6c49\u5b50\u2014\u2014\u4e5f\u5c31\u662f\u6458\u661f\u624b\u53f8\u7a7a\u5b97\uff0c\u60f3\u662f\u5403\u90a3\u4e2d\u5e74\u543c \u7834\u540d\u53f7\uff0c\u4e0d\u89c9\u4e00\u60ca\uff0c\u7b54\u9053\uff1a\u201c\u9601\u4e0b\u529f\u529b\u4e0d\u51e1\uff0c\u4f46\u9700\u77e5\u2018\u5149\u68cd\u2026\u2026\u2019\u3002\u201d \u3000\u3000\u8bdd\u97f3\u65b9\u843d\uff0c\u90a3\u4e2d\u5e74\u4eba\u5df2\u4f3c\u77e5\u8bdd\u4e2d\u610f\u4e49\uff0c\u8bf4\u9053\uff1a\u201c\u53f8\u7a7a\u5144\u4f11\u5f97\u8bef\u4f1a\uff0c\u5728\u4e0b\u59d3\u8c22\u540d\u957f\u537f \u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u90a3\u6458\u661f\u624b\u4e0d\u7981\u60ca\u5f02\u5730\u5475\u4e86\u4e00\u58f0\uff0c\u5c31\u662f\u88ab\u8e22\u4e2d\u7a74\u9053\u7684\u795e\u5251\u91d1\u9524\u4e5f\u4e0d\u7531\u54fc\u4e86 \u4e00\u58f0\uff0c\u53f8\u7a7a\u5b97\u63a5\u53e3\u9053\uff1a\u201c\u60f3\u4e0d\u5230\u9601\u4e0b\u7adf\u662f\u70b9\u82cd\u638c\u95e8\u843d\u82f1\u5251\u8c22\u5927\u4fa0\uff1f\u201d \u3000\u3000\u8c22\u957f\u537f\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c71\u5de6\u53cc\u8c6a\u5411\u6765\u8bb2\u7a76\u4e49\u6c14\uff0c\u8fd9\u4e00\u70b9\u8c22\u67d0\u4eba\u4e5f\u8fd8\u6df1\u77e5\uff01\u4f46\u4e0d \u60f3\u4e8c\u4f4d\u6df1\u5c71\u62e6\u52ab\uff0c\u4e14\u5c3d\u8bdb\u6beb\u65e0\u6b66\u6280\u8eab\u65e0\u5bf8\u94c1\u7684\u8001\u5c11\u4e03\u53e3\uff0c\u4e0b\u624b\u672b\u514d\u8fc7\u8fa3\u4e00\u4e9b\u513f\u5427\uff1f\u201d \u3000\u3000\u5c71\u5de6\u53cc\u8c6a\u6599\u4e0d\u5230\u70b9\u82cd\u638c\u95e8\u4f1a\u6765\u81f3\u534e\u5317\uff0c\u4ed6\u4eec\u4e5f\u65e9\u5c31\u9707\u60ca\u843d\u82f1\u5251\u7684\u5a01\u540d\uff0c\u5fc3\u4e2d\u5df2\u840c\u9000 \u5fd7\uff0c\u90a3\u77e5\u5bf9\u65b9\u53e3\u6c14\u51b7\u786c\u7684\u6570\u8bf4\u81ea\u5df1\u4e00\u756a\uff0c\u6012\u706b\u4e0a\u5347\uff0c\u53f8\u7a7a\u5b97\u4e0d\u7531\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u662f \u5e72\u6b64\u884c\u4e3a\u6d3b\uff0c\u4e0b\u624b\u81ea\u7136\u91cd\u4e00\u4e9b\uff0c\u8c22\u8001\u5e08\u82e5\u662f\u4e0d\u5fcd\u2014\u2014\u201d\u8bf4\u7740\u5f80\u6797\u8fb9\u5012\u4e0b\u7684\u4e03\u516b\u5177\u6b7b\u5c38\u4e00 \u6307\u3002 \u3000\u3000\u8c22\u957f\u537f\u968f\u4ed6\u6240\u6307\u4e00\u770b\uff0c\u4f46\u89c1\u90a3\u4e03\u4eba\u5df2\u662f\u6c14\u7edd\u591a\u65f6\uff0c\u4e0d\u89c9\u51b7\u7136\u8bf4\u9053\uff1a\u201c\u8bf4\u4e0d\u5f97\uff0c\u8c22\u67d0 \u4eba\u8981\u8bf7\u4e8c\u4f4d\u8d50\u6559\u4e86\uff01\u201d \u3000\u3000\u8bf4\u5b8c\u8eab\u5f62\u4e00\u6643\uff0c\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u957f\u5251\u51fa\u624b\u3002 \u3000\u3000\u6458\u661f\u624b\u54c8\u54c8\u4e00\u7b11\uff0c\u98de\u8d77\u4e00\u811a\uff0c\u649e\u5f00\u6797\u5c11\u768b\u7a74\u9053\uff0c\u4e00\u6446\u624b\uff0c\u8eab\u5f62\u4e00\u52a8\uff0c\u4e00\u6325\u94c1\u62f3\uff0c\u4fbf \u60f3\u7a7a\u624b\u640f\u6597\uff01 \u3000\u3000\u8c22\u957f\u537f\u4f55\u7b49\u4eba\u7269\uff0c\u89c1\u5bf9\u624b\u624b\u65e0\u5bf8\u94c1\uff0c\u53cd\u624b\u63d2\u56de\u957f\u5251\uff0c\u8eab\u5b50\u6709\u5982\u6d41\u6c34\u884c\u4e91\uff0c\u9000\u540e\u5bfb\u4e08\uff01 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u53f8\u7a7a\u5b97\u94c1\u62f3\u6253\u7a7a\uff0c\u8db3\u8ddf\u7740\u5730\uff0c\u518d\u4e00\u62db\u201c\u6bd2\u86c7\u51fa\u6d1e\u201d\uff0c\u8d70\u4e2d\u5bab\uff0c\u8e0f \u6d2a\u95e8\uff0c\u957f\u9a71\u76f4\u6363\u3002 \u3000\u3000\u843d\u82f1\u5251\u4f55\u7b49\u529f\u529b\uff0c\u771f\u8fd8\u4e0d\u5c06\u6458\u661f\u624b\u653e\u5728\u773c\u5185\uff0c\u53cc\u638c\u4e00\u5408\uff0c\u4e0b\u76d8\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u4e0a\u8eab\u9661\u7136 \u6a2a\u79fb\u6570\u5c3a\uff0c\u53cc\u638c\u731b\u5411\u5916\u4e00\u5c01\uff0c\u4e00\u5f0f\u201c\u53cc\u649e\u638c\u201d\u731b\u51fb\u53f8\u7a7a\u5b97\u53cc\u808b\u3002 \u3000\u3000\u6458\u661f\u624b\u8eab\u5f62\u6025\u505c\uff0c\u76d8\u6253\u8c22\u957f\u537f\u8170\u9645\u3002 \u3000\u3000\u843d\u82f1\u5251\u4e0b\u76d8\u4ecd\u7136\u9489\u7acb\u4e0d\u52a8\uff0c\u8170\u95f4\u7528\u529b\uff0c\u5411\u540e\u5185\u9677\u4e8c\u5bf8\uff0c\u5de6\u624b\u4e00\u5708\uff0c\u6263\u5411\u6458\u661f\u624b\u8109\u95e8\uff0c \u53f3\u624b\u4e00\u5f0f\u201c\u7384\u9e1f\u753b\u6c99\u201d\uff0c\u659c\u88ad\u53f8\u7a7a\u5b97\u7709\u5fc3\u3002 \u3000\u3000\u4e8c\u4eba\u5728\u4e00\u8fb9\u6253\u4e2a\u4e0d\u4e86\uff0c\u8f9b\u6377\u5728\u6811\u4e0a\u5374\u548c\u5434\u51cc\u98ce\u4e0d\u4f4f\u5546\u91cf\u3002 \u3000\u3000\u5434\u51cc\u98ce\u542c\u77e5\u4e2d\u5e74\u4eba\u7adf\u662f\u4e94\u5927\u5b97\u6d3e\u4e4b\u4e00\u638c\u95e8\u4eba\uff0c\u5fc3\u4e2d\u4ec7\u706b\u4e0a\u5347\uff0c\u6068\u4e0d\u5f97\u7acb\u523b\u4e0b\u6811\u6253\u51fb\uff0c \u5012\u662f\u8f9b\u6377\u5c06\u4ed6\u62d6\u4f4f\uff0c\u5728\u4ed6\u8033\u8fb9\u5c0f\u58f0\u8bf4\u9053\uff1a\u201c\u5927\u54e5\u4e0d\u5fc5\u5fc3\u6025\uff0c\u6614\u5e74\u5728\u5929\u7ec5\u7011\u5408\u51fb\u4f2f\u7236\u7684\u5374 \u662f\u6b64\u4eba\u4e4b\u7236\u2014\u2014\u56de\u98ce\u5251\u5ba2\u8c22\u661f\uff01\u6b64\u4eba\u2014\u2014\u201d\u4ed6\u672c\u6765\u60f3\u52a0\u8d5e\u8c22\u957f\u537f\u51e0\u53e5\uff0c\u4f46\u662f\u60f3\u8d77\u8c22\u957f\u537f \u4e43\u662f\u70b9\u4f24\u5e08\u7236\u7684\u51f6\u624b\uff0c\u867d\u660e\u77e5\u4ed6\u51fa\u4e8e\u4e0d\u5f97\u5df2\uff0c\u4f46\u4e5f\u5347\u8d77\u4e00\u80a1\u65e0\u540d\u4e4b\u706b\uff0c\u4e0d\u60f3\u518d\u8bf4\u4e0b\u53bb\u3002 \u3000\u3000\u4e8c\u4eba\u8fd9\u53a2\u4e00\u8c08\uff0c\u6b63\u9002\u8c22\u957f\u537f\u548c\u90a3\u6458\u661f\u624b\u518d\u5ea6\u8bf4\u50f5\u800c\u52a8\u624b\uff0c\u5434\u51cc\u98ce\u5f97\u77e5\u6b64\u4eba\u5e76\u975e\u81ea\u5df1 \u6740\u7236\u4ec7\u4eba\uff0c\u800c\u662f\u5176\u5b50\uff0c\u5fc3\u4e2d\u867d\u7136\u4e0d\u5e73\uff0c\u4f46\u542c\u90a3\u843d\u82f1\u5251\u8c22\u957f\u537f\u7adf\u662f\u6b63\u6c14\u51db\u7136\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u6e10 \u751f\u597d\u611f\uff0c\u8fd9\u65f6\u4e8c\u4eba\u4e00\u4ea4\u624b\uff0c\u6811\u4e0a\u4e8c\u4eba\u4e5f\u90fd\u76fc\u90a3\u8c22\u957f\u537f\u80fd\u591f\u83b7\u80dc\u3002 \u3000\u3000\u8c22\u957f\u537f\u6b64\u65f6\u5df2\u5c06\u201c\u4e03\u7edd\u624b\u6cd5\u201d\u4fbf\u4e86\u51fa\u6765\uff0c\u5a01\u52bf\u6781\u5927\uff0c\u800c\u6458\u661f\u624b\u6b64\u65f6\u4e5f\u5c06\u4ed6\u6210\u540d\u7684\u62f3 \u62db\u201c\u6458\u661f\u5341\u516b\u5f0f\u201d\u4fbf\u4e86\u51fa\u6765\u3002 \u3000\u3000\u4e8c\u4eba\u90fd\u662f\u6c5f\u6e56\u4e0a\u7f55\u89c1\u7684\u9ad8\u624b\uff0c\u8fd9\u4e00\u4ea4\u624b\uff0c\u7cbe\u5999\u4e4b\u81f3\u3002\u8f9b\u6377\u5728\u6811\u4e0a\u89c2\u6218\uff0c\u4e5f\u4e0d\u7981\u6697\u8d5e\u3002 \u3000\u3000\u6458\u661f\u624b\u6bcf\u653b\u843d\u82f1\u5251\u4e00\u62db\uff0c\u8f9b\u6377\u5728\u5fc3\u4e0b\u4e5f\u90fd\u4e3a\u8c22\u957f\u537f\u60f3\u89e3\u6551\u4e4b\u62db\uff0c\u53cd\u4e4b\u8c22\u957f\u537f\u653b\u53f8\u7a7a \u5b97\u4e5f\u662f\u4e00\u6837\u3002 \u3000\u3000\u987b\u77e5\u8f9b\u6377\u6b64\u65f6\u529f\u529b\u6781\u6df1\uff0c\u662f\u4ee5\u4e8c\u4eba\u4e00\u62ac\u4e00\u5f0f\u5728\u5fc3\u4e2d\u90fd\u80fd\u5f88\u5feb\u7684\u60f3\u51fa\u7834\u62db\uff0c\u4f46\u7a76\u7adf\u4e5f \u4e0d\u7531\u5927\u5927\u8d5e\u53f9\u4e8c\u4eba\u7684\u53cd\u5e94\u548c\u4e34\u654c\u7ecf\u9a8c\uff01 \u3000\u3000\u5c24\u5176\u662f\u53f8\u7a7a\u5b97\uff0c\u7ecf\u9a8c\u4e4b\u5bcc\uff0c\u8c22\u957f\u537f\u4efb\u4e00\u865a\u62db\u90fd\u9a97\u4e0d\u4e86\u4ed6\uff0c\u51fa\u624b\u72e0\u8fa3\u91c9\u5feb\u6377\uff0c\u5b9e\u5728\u4ee4 \u4eba\u77aa\u76ee\uff0c\u4e0d\u7531\u4e0d\u89c9\u201c\u4e09\u5206\u7ecf\u9a8c\uff0c\u4e03\u5206\u5de5\u592b\u201d\u7684\u8bdd\u662f\u6b63\u786e\u4e0d\u8fc7\u7684\u4e86\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u5ffd\u7136\u5c71\u9053\u4e0a\u53c8\u662f\u4e00\u9635\u9a6c\u8e44\u58f0\uff0c\u77ac\u606f\u95f4\u5df2\u5954\u8fd1\uff0c\u8f9b\u5446\u4e8c\u4eba\u4e00\u770b\uff0c\u53ea\u89c1\u6765\u8005 \u5171\u6709\u4e8c\u9a91\uff0c\u4e3a\u9996\u4e00\u4eba\u5e74\u7ea6\u4e03\u65ec\uff0c\u8eab\u7740\u845b\u8863\u3002\u5954\u5230\u8fd1\u5904\uff0c\u90a3\u845b\u8863\u4eba\u4f3c\u4e5f\u542c\u5230\u6709\u5175\u5668\u58f0\uff0c\u4e0d \u89c9\u4e00\u505c\u9a6c\u52bf\uff0c\u56de\u9996\u548c\u7537\u540e\u4e00\u4eba\u6253\u4e86\u4e2a\u624b\u52bf\u8fd9\u65f6\u6797\u5185\u4e8c\u4eba\u6218\u81f3\u5206\u9645\uff0c\u8c22\u957f\u537f\u5df2\u5c06\u201c\u4e03\u7edd\u624b \u6cd5\u201d\u6700\u51cc\u5389\u7684\u5341\u5f0f\u4f7f\u51fa\uff0c\u903c\u5f97\u53f8\u7a7a\u5b97\u8fde\u8d70\u9669\u62db\uff0c\u52c9\u5f3a\u7528\u201c\u6458\u661f\u5341\u516b\u5f0f\u201d\u4e2d\u4e09\u4e2a\u6551\u547d\u5947\u5f0f \u2014\u2014-\u201c\u9b3c\u7bad\u98de\u601c\u201d\uff0c\u201c\u96f7\u52a8\u4e07\u7269\u201d\uff0c\u201c\u5929\u7f57\u9003\u5211\u201d\u624d\u95ed\u4f4f\u4e0d\u7edd\u653b\u52bf\u3002 \u3000\u3000\u500f\u5730\u6797\u5916\u6709\u4eba\u957f\u58f0\u541f\u9053\uff1a\u201c\u957f\u5929\u4e00\u70b9\u78a7\uff0c\u6653\u6708\u4e94\u66f4\u5bd2\u2026\u2026\uff1f \u3000\u3000\u8bdd\u97f3\u521a\u843d\uff0c\u5c71\u5de6\u4e8c\u8c6a\u8138\u8272\u540c\u65f6\u4e00\u53d8\uff0c\u8c22\u957f\u537f\u4e5f\u662f\u8138\u8272\u4e00\u5bd2\uff0c\u5237\u5730\u6536\u62db\uff0c\u8df3\u51fa\u5708\u5916\u9053\uff1a \u201c\u5c71\u5de6\u53cc\u8c6a\u7edd\u827a\u5df2\u7136\u9886\u6559\uff0c\u8c22\u67d0\u56e0\u6709\u6025\u4e8b\uff0c\u5148\u884c\u5931\u966a\uff01\u201d \u3000\u3000\u8bdd\u97f3\u5306\u5306\uff0c\u8bf4\u5230\u6700\u540e\u4e00\u5b57\uff0c\u4eba\u5df1\u7a7f\u51fa\u5c0f\u6797\u5b50\u3002 \u3000\u3000\u8f9b\u3001\u5434\u4e8c\u6b63\u85cf\u8eab\u6811\u4e2d\uff0c\u56de\u9996\u4e00\u770b\uff0c\u53ea\u89c1\u541f\u8bd7\u7684\u4eba\u6b63\u662f\u90a3\u845b\u8863\u8001\u8005\uff0c\u4ee4\u4eba\u60ca\u5f02\u7684\u662f\u6b64 \u4eba\u4e0d\u4f46\u4e00\u65e0\u9f99\u949f\u4e4b\u6001\uff0c\u800c\u4e14\u4e2d\u6c14\u5145\u6c9b\u4e4b\u6781\u3002 \u3000\u3000\u843d\u82f1\u5251\u8c22\u957f\u537f\u7a7f\u51fa\u6797\u6765\uff0c\u76f4\u6251\u90a3\u845b\u8863\u8001\u8005\uff0c\u6c89\u58f0\u543c\u9053\uff1a\u201c\u4efb\u8001\u82f1\u96c4\uff0c\u4f60\u53ef\u4e5f\u7b97\u662f\u6210 \u540d\u4e4b\u4eba\uff0c\u600e\u4e48\u4e00\u518d\u6b3a\u9a97\u5728\u4e0b\uff1f\u201d \u3000\u3000\u90a3\u59d3\u4efb\u7684\u8001\u8005\u51b7\u7136\u4e00\u7b11\u9053\uff1a\u201c\u597d\u4e2a\u843d\u82f1\u5251\uff0c\u4f60\u4eec\u70b9\u82cd\u95e8\u4eba\u81ea\u52a8\u6295\u5165\u672c\u6d3e\uff0c\u4f60\u505a\u638c\u95e8 \u7684\u5374\u5c3d\u627e\u54b1\u4eec\u9ebb\u70e6\uff0c\u4e5f\u4e0d\u6015\u53eb\u4eba\u803b\u7b11\uff1f\u201d \u3000\u3000\u6811\u4e0a\u4e8c\u4eba\u89c1\u8fd9\u4efb\u8001\u513f\u4e00\u6765\uff0c\u8c22\u957f\u537f\u4fbf\u6025\u6210\u5982\u6b64\uff0c\u5927\u6982\u662f\u6709\u5173\u4ec0\u4e48\u70b9\u82cd\u6d3e\u7684\u58f0\u8a89\u95ee\u9898\uff0c \u540c\u65f6\u66f4\u6478\u4e0d\u6e05\u8fd9\u8001\u513f\u7684\u6765\u5386\uff0c\u4e0d\u89c9\u540c\u65f6\u4e00\u6014\u3002 \u3000\u3000\u8c22\u957f\u537f\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4efb\u5353\u5ba3\u8001\u5339\u592b\uff0c\u53ef\u4e0d\u662f\u8c22\u67d0\u6709\u610f\u62cc\u8845\uff0c\u8c22\u67d0\u8981\u5f97\u7f6a\u4e86\uff01\u201d\u8bdd\u4e2d \u5145\u6ee1\u72c2\u6012\u3002 \u3000\u3000\u90a3\u77e5\u4efb\u5353\u5ba3\u5e76\u4e0d\u52a8\u6012\uff0c\u5c3d\u6de1\u7136\u4e00\u7b11\uff0c\u56de\u9996\u5bf9\u8eab\u540e\u90a3\u4e2a\u4e2d\u5e74\u4eba\u4e00\u6446\u624b\uff0c\u4e8c\u4eba\u4e00\u5de6\u4e00\u53f3 \u5206\u5f00\u3002 \u3000\u3000\u8c22\u957f\u537f\u89c1\u72b6\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u83ab\u8bf4\u4f60\u4eec\u4e8c\u4e2a\uff0c\u5c31\u662f\u5934\u513f\u4e00\u9f50\u6765\uff0c\u8c22\u67d0\u7167\u6837\u63a5\u4e0b\u3002\u201d \u3000\u3000\u54ea\u77e5\u90a3\u4efb\u5353\u5ba3\u4ecd\u4e0d\u4f5c\u58f0\uff0c\u624b\u81c2\u4e00\u9707\uff0c\u4e24\u8896\u98d8\u5904\uff0c\u4e00\u80a1\u52b2\u98ce\u76f4\u653e\u5411\u4e09\u56db\u4e08\u5916\u4e00\u682a\u7897\u53e3 \u5c0f\u6811\uff0c\u638c\u98ce\u5230\u5904\uff0c\u6811\u8eab\u5f2f\u5f97\u4e00\u5f2f\uff0c\u4efb\u5353\u5ba3\u84e6\u5730\u5410\u6c14\u5f00\u58f0\uff0c\u90a3\u682a\u5c0f\u6811\u767b\u65f6\u5f90\u5f90\u8fde\u6839\u98de\u8d77\uff0c \u987a\u7740\u4ed6\u638c\u529b\u98de\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u9732\u5f97\u9ad8\u660e\u4e4b\u6781\uff0c\u5c24\u5176\u4ee4\u4eba\u60ca\u5f02\u7684\u4e43\u662f\u4ed6\u4e00\u638c\u56de\u6536\u7684\u529b\u9053\u7adf\u4e1d\u6beb\u4e0d\u51cf\u4e8e\u53d1\u653e\u7684 \u529b\u9053\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\uff0c\u8c22\u957f\u537f\u51b7\u54fc\u4e00\u58f0\uff0c\u8eab\u8eaf\u6709\u82e5\u8131\u5f26\u4e4b\u7bad\uff0c\u7eb5\u7acb\u7a7a\u4e2d\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u95ea\u7535 \u822c\u5df2\u5728\u6811\u8eab\u4e0a\u52d2\u4e86\u4e00\u5708\u3002 \u3000\u3000\u6811\u8eab\u7f13\u7f13\u98de\u5230\u4efb\u5353\u5ba3\u624b\u4e0a\uff0c\u4efb\u5353\u5ba3\u4f38\u624b\u4e00\u63a5\u5fae\u4e00\u7528\u529b\uff0c\u201c\u55ef\u5431\u201d\u4e00\u58f0\uff0c\u5c0f\u6811\u9f50\u8170\u800c \u65ad\uff0c\u6562\u60c5\u662f\u8c22\u957f\u537f\u4e00\u5251\u4e4b\u529f\u3002 \u3000\u3000\u8c22\u957f\u537f\u8fd9\u4e00\u4e0b\u5e73\u767d\u53d1\u96be\uff0c\u663e\u5f97\u8fd1\u4e4e\u72c2\u6a2a\uff0c\u4efb\u5353\u5ba3\u7981\u4e0d\u4f4f\u51b7\u51b7\u4e00\u54fc\u9053\uff1a\u201c\u59d3\u8c22\u7684\u4f11\u72c2\uff0c \u4f60\u4ed4\u7ec6\u542c\u7740\uff1a\u2018\u5173\u4e2d\u9738\u4e5d\u8c6a\uff0c\u6cb3\u6d1b\u552f\u4e00\u5251\uff0c\u6d77\u5185\u5c0a\u4e03\u5999\uff0c\u4e16\u5916\u6709\u4e09\u4ed9\uff01\u2019\u5982\u4eca\u6cb3\u6d1b\u4e00\u5251 \u5c38\u5bd2\u5df2\u4e45\uff0c\u4e16\u5916\u4e09\u4ed9\u4e0d\u590d\u4e2d\u571f\uff0c\u4e03\u5999\u795e\u541b\u867d\u7136\u66fe\u4f20\u51fa\u73b0\uff0c\u4e5f\u53ea\u4e0d\u8fc7\u4f20\u8bf4\u800c\u5df2\uff0c\u82b8\u82b8\u6b66\u6797 \u4e2d\uff0c\u5173\u4e2d\u4e5d\u8c6a\u5df2\u663e\u7136\u6210\u4e86\u9886\u8896\u2026\u2026\u201d \u3000\u3000\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u8c22\u957f\u537f\u5df2\u65ad\u559d\u9053\uff1a\u201c\u95f2\u8bdd\u5c11\u8bf4\uff0c\u5c31\u7b97\u4f60\u6709\u2018\u6d77\u5929\u53cc\u715e\u2019\u6491\u8170\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u4efb\u5353\u5ba3\u51b7\u7136\u4e00\u7b11\uff0c\u7ee7\u7eed\u9053\uff1a\u201c\u6d77\u5929\u4e8c\u4f4d\u8001\u524d\u8f88\u5df2\u51b3\u610f\u518d\u7ec4\u4e5d\u9738\uff0c\u91cd\u6574\u65d7\u9f13\uff0c\u4f60\u542c\u7740\uff0c \u6d77\u5929\u4e5d\u9738\u4e2d\u9664\u6d77\u5929\u53cc\u715e\u53ca\u6614\u5e74\u5f52\u9690\u4e8c\u4f4d\uff0c\u5171\u56db\u4f4d\u5916\uff0c\u5916\u52a0\u7684\u4e94\u4eba\u4fbf\u662f\u5c71\u5de6\u53cc\u8c6a\uff0c\u533a\u533a\u5728 \u4e0b\u548c\u957f\u5929\u4e00\u78a7\u767d\u5144\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u4e00\u6307\u8eab\u65c1\u7684\u4e2d\u5e74\u4eba\uff0c\u53c8\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u4fbf\u662f\u4f60\u59d3\u8c22\u7684\u5e08\u5f1f\uff0c\u5343\u624b\u5251\u5ba2\u9646\u65b9\u9646\u8001 \u5f1f\uff01\u201d \u3000\u3000\u8c22\u957f\u537f\u6b64\u884c\u4e43\u662f\u4e3a\u4e86\u8ffd\u6355\u4e00\u4e2a\u5077\u53d6\u4e86\u70b9\u82cd\u9547\u5c71\u5bc6\u7b08\u7684\u5e08\u5f1f\u2014\u2014\u5343\u624b\u5251\u5ba2\u9646\u65b9\u2014\u2014\u8fd9 \u65f6\u786e\u77e5\u90a3\u9646\u65b9\u7adf\u5df2\u52a0\u4eba\u5173\u4e2d\u4e5d\u8c6a\u4e4b\u4e00\uff0c\u4e0d\u7531\u5927\u60ca\uff0c\u534a\u54cd\u7b54\u4e0d\u51fa\u8bdd\u6765\uff01 \u3000\u3000\u4efb\u5353\u5ba3\u51b7\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u4ed6\u81ea\u613f\u7684\uff0c\u5476\uff0c\u4f60\u770b\u2014\u2014\u201d\u8bf4\u7740\u63a2\u624b\u4eba\u6000\uff0c\u51fa\u4e00\u5c01\u4fe1 \u7f04\uff0c\u4e22\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8c22\u957f\u537f\u63a5\u5728\u624b\u91cc\uff0c\u62c6\u5f00\u7c97\u7c97\u4e00\u770b\uff0c\u5df1\u77e5\u679c\u662f\u5e08\u5f1f\u624b\u7b14\uff0c\u5fc3\u4e2d\u5927\u5931\u6240\u671b\uff0c\u53e3\u4e2d\u5374\u9053\uff1a \u201c\u90a3\u4e48\u90a3\u79d8\u7b08\u5462\uff1f\u201d \u3000\u3000\u4efb\u5353\u5ba3\u7b54\u9053\uff1a\u201c\u90a3\u662f\u9646\u8001\u5f1f\u7684\u4e8b\u4e86\uff0c\u8001\u592b\u5e76\u4e0d\u77e5\u4e0e\uff01\u59d3\u8c22\u7684\u4f60\u4e00\u518d\u6311\u8845\uff0c\u8001\u592b\u603b\u662f \u9690\u5fcd\uff0c\u4e0d\u8fc7\u662f\u60f3\u501f\u4f60\u8fd9\u5f20\u5634\u4f20\u904d\u6b66\u6797\uff0c\u5173\u4e2d\u4e5d\u8c6a\u4e1c\u5c71\u518d\u8d77\uff0c\u5426\u5219\u52a8\u8d77\u624b\u6765\uff0c\u4f60\u8fd8\u6709\u547d \u5417\uff1f\u201d\u8bf4\u7740\u987f\u4e86\u4e00\u4e0b\uff0c\u4e0d\u5f85\u8c22\u957f\u537f\u7b54\u8bdd\uff0c\u53c8\u9053\uff1a\u201c\u4eca\u513f\u5374\u8981\u8ba9\u4f60\u5403\u70b9\u513f\u82e6\u5934\uff0c\u8ba9\u5929\u4e0b\u4eba \u5f97\u77e5\u5173\u4e2d\u4e5d\u8c6a\u4e0d\u662f\u597d\u60f9\u7684\uff0c\u4e94\u5927\u5b97\u6d3e\u4ecd\u987b\u81e3\u670d\u5728\u54b1\u4eec\u4e4b\u4e0b\u2014\u2014\u201d \u3000\u3000\u8bdd\u97f3\u65b9\u843d\uff0c\u8c22\u957f\u537f\u5df2\u662f\u4e00\u58f0\u72c2\u7b11\uff0c\u4e00\u632f\u624b\u4e2d\u957f\u5251\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u8bf4\uff01\u597d\u8bf4\uff01\u54b1\u5c31\u5148\u6765 \u8bd5\u8bd5\u4e5d\u8c6a\u7684\u5a01\u98ce\uff01\u201d \u3000\u3000\u521a\u52a8\u8eab\uff0c\u4efb\u5353\u5ba3\u51b7\u51b7\u5730\u9053\uff1a\u201c\u59d3\u8c22\u7684\uff0c\u4f60\u4ed4\u7ec6\u4f30\u91cf\u4f30\u91cf\uff0c\u54b1\u4eec\u56db\u4eba\u4e8c\u524d\u4e8c\u540e\u5bf9\u4ed8\u4f60\uff0c \u4f60\u8fd8\u8981\u901e\u5f3a\u4e48\uff1f \u3000\u3000\u201d \u3000\u3000\u8c22\u957f\u537f\u95fb\u8a00\u56de\u9996\u4e00\u671b\uff0c\u679c\u89c1\u5c71\u5de6\u53cc\u8c6a\u5e7f\u5de6\u4e00\u53f3\u7ad9\u5728\u81ea\u5df1\u8eab\u540e\u5bfb\u4e08\u4e4b\u5904\uff0c\u4e00\u58f0\u4e0d\u54cd\u3002 \u5fc3\u4e2d\u6697\u81ea\u4f30\u8ba1\uff0c\u60c5\u77e5\u9003\u5f00\u65e0\u671b\uff0c\u4ed6\u79c9\u6027\u521a\u76f4\uff0c\u4e0d\u518d\u8bf4\u8bdd\uff0c\u624b\u4e2d\u957f\u5251\u4e00\u70b9\uff0c\u76f4\u88ad\u5411\u4efb\u5353\u5ba3\u3002 \u3000\u3000\u539f\u6765\u81ea\u9646\u65b9\u5077\u4e66\u4e0b\u5c71\uff0c\u8c22\u957f\u537f\u4e07\u91cc\u8ffd\u8e2a\uff0c\u4e00\u8def\u4e0a\u51e0\u756a\u90fd\u53ef\u5f97\u624b\uff0c\u4f46\u603b\u662f\u6709\u4eba\u6697\u4e2d\u76f8 \u52a9\u9646\u65b9\u9003\u8d70\uff0c\u4e00\u76f4\u8ffd\u5230\u6e56\u5317\u8fb9\u5883\uff0c\u5374\u9047\u89c1\u4efb\u5353\u5ba3\u3002\u4e8c\u4eba\u65e9\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u4efb\u5353\u5ba3\u544a\u8bc9\u8c22\u957f \u537f\u9646\u65b9\u9003\u5411\u6850\u67cf\u5c71\u533a\uff0c\u8c22\u957f\u537f\u6025\u8ffd\u800c\u81f3\uff0c\u5374\u9022\u5c71\u5de6\u53cc\u8c6a\u6253\u52ab\u4eba\u5bb6\uff0c\u4e8e\u662f\u63d2\u5165\u4e00\u624b\uff0c\u600e\u6599 \u4efb\u5353\u5ba3\u6709\u610f\u9a97\u8c22\u957f\u537f\u5230\u6b64\u800c\u52a0\u4ee5\u56f4\u51fb\uff0c\u662f\u4ee5\u5230\u4ed6\u541f\u51fa\u4e8c\u4eba\u540d\u53f7\uff1a\u201c\u957f\u5929\u4e00\u70b9\u78a7\uff08\u957f\u5929- \u78a7\u767d\u98ce\u3002\uff09\u6653\u6708\u4e94\u66f4\u5bd2 \u3000\u3000(\u6653\u6708\u4e94\u66f4\u5bd2\u5fc3\u638c\u4efb\u5353\u5ba3\uff09\u65f6\uff0c\u8c22\u957f\u537f\u624d\u77e5\u662f\u4eba\u5bb6\u7684\u9b3c\u8ba1\uff0c\u662f\u4ee5\u8bf4\u50f5\u52a8\u624b\u3002 \u3000\u3000\u4e14\u8bf4\u8c22\u957f\u537f\u72b9\u4e14\u5b64\u519b\u82e6\u6218\uff0c\u4e00\u8fde\u6570\u62db\uff0c\u4fbf\u88ab\u56db\u4eba\u5408\u529b\u903c\u9000\u3002 \u3000\u3000\u5728\u6811\u4e0a\u4f0f\u7740\u7684\u8f9b\u6377\uff0c\u548c\u5434\u51cc\u98ce\u4e8c\u4eba\u7565\u4e00\u5546\u91cf\uff0c\u8f9b\u6377\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7ed9\u5434\u51cc\u98ce\u4e00\u6253\u62db\u547c\uff0c \u638f\u51fa\u4e00\u65b9\u624b\u5dfe\u5c06\u8138\u5b54\u8499\u4f4f\uff0c\u5237\u5730\u7eb5\u5c06\u4e0b\u53bb\u3002 \u3000\u3000\u5730\u4e0a\u4e94\u4eba\u6b63\u6218\u5f97\u6025\u5207\uff0c\u8f9b\u6377\u7a7f\u5165\u5708\u4e2d\uff0c\u767b\u65f6\u4e94\u4eba\u4e00\u8d77\u4f4f\u624b\uff0c\u9f50\u6ce8\u89c6\u6765\u4eba\u3002 \u3000\u3000\u53ea\u89c1\u6765\u4eba\u9762\u8499\u4e00\u65b9\u624b\u5dfe\uff0c\u4e0a\u9576\u7740\u4e03\u6735\u6b63\u6b63\u7684\u6885\u82b1\uff0c\u6765\u4eba\u51b7\u54fc\u9053\uff1a\u201c\u5173\u4e2d\u4e5d\u8c6a\u600e\u6837\uff1f \u6885\u67d0\u5e74\u7eaa\u867d\u8001\uff0c\u4f46\u662f\u2014\u2014\u563f\uff01\u201d \u3000\u3000\u8f9b\u6377\u6545\u610f\u4e00\u58f0\u6da9\u7684\u7b11\u58f0\u6536\u53e3\uff0c\u4ee4\u4eba\u751f\u51fa\u4e00\u79cd\u4e0d\u5bd2\u800c\u6817\u7684\u611f\u89c9\u3002 \u3000\u3000\u201c\u56db\u8c6a\u201d\u5012\u8fd8\u7f62\u4e86\uff0c\u843d\u82f1\u5251\u8c22\u957f\u537f\u6597\u7136\u8138\u8272\u5927\u53d8\uff0c\u591a\u5c11\u5e74\u6765\uff0c\u6bcf\u65f6\u6bcf\u523b\uff0c\u8fd9\u4e00\u4ef6\u4e8b \u5b9e\u72e0\u72e0\u5730\u541e\u566c\u7740\u4ed6\u7684\u5fc3\uff0c\u65e0\u8fb9\u6094\u610f\u523a\u75db\u7740\u4ed6\uff0c\u4e0d\u60f3\u4e00\u65e6\u771f\u7684\u53c8\u89c1\u5230\u4e86\u5341\u5e74\u524d\u7684\u6545\u4eba\u2014\u2014 \u867d\u7136\u662f\u8499\u7740\u9762\u513f\u7684\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u773c\u65c1\u89c2\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u4ed6\u5bf9\u843d\u82f1\u5251\u8c22\u957f\u537f\u6709\u7740\u6df1\u5207\u7684\u4e86\u89e3\uff0c\u4f46\u4e00\u77ac\u95f4\uff0c\u4ed6\u53c8\u51b7 \u7136\u4e00\u54fc\uff0c\u8bf4\u9053\uff1a\u201c\u5173\u4e2d\u4e5d\u8c6a\u4e1c\u5c71\u518d\u8d77\u5c31\u51ed\u4f60\u4eec\u8fd9\u4e00\u6279\u70c2\u8d27\uff1f\u8001\u5b9e\u8bf4\uff0c\u6211\u6885\u67d0\u4eba\u7b2c\u4e00\u4e2a\u5c31 \u4e0d\u670d\u4ee5\u4f60\u4eec\u8fd9\u7b49\u529f\u592b\u4fbf\u80fd\u548c\u533a\u533a\u9f50\u540d\uff01\u201d\u8fd9\u4e00\u756a\u8bdd\u8bf4\u5f97\u50b2\u6162\u5df2\u6781\u3002 \u3000\u3000\u4efb\u5353\u5ba3\u5148\u8fd8\u5403\u4e86\u4e00\u60ca\uff0c\u8fd9\u65f6\u95fb\u8a00\u5927\u6012\uff0c\u65ad\u543c\u9053\uff1a\u201c\u4e94\u5251\u6d3e\u7684\u5251\u4e0b\u4ea1\u9b42\uff0c\u8fd8\u60f3\u5728\u6b66\u6797 \u4e2d\u91cd\u6811\u65d7\u5e1c\u4e48\uff1f\u54c8\u54c8\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u5403\u4ed6\u4e00\u9635\u8ba5\u7b11\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u6012\u5316\u9053\uff1a\u201c\u662f\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u4efb\u5353\u5ba3\u8fd9\u65f6\u662f\u6012\u6781\u800c\u7b11\uff0c\u89c1\u8f9b\u6377\u6012\u58f0\u76f8\u5316\uff0c\u84e6\u5730\u7b11\u58f0\u6709\u82e5\u91d1\u9523\u76f8\u51fb\u4e0b\u58f0\uff0c\u201c\u94ff\u9535\u201d \u800c\u6b62\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u4efb\u5353\u5ba3\u7b11\u58f0\u65b9\u6b62\uff0c\u53cc\u638c\u4e00\u626c\u4e00\u7acb\uff0c\u201c\u5bd2\u5fc3\u638c\u529b\u201d\u4e14\u7136\u53d1\u51fa\u3002 \u3000\u3000\u4ed6\u81ea\u4ee5\u4e3a\u8fd9\u4e00\u4e0b\u53d1\u96be\u5306\u4fc3\uff0c\u4e03\u5999\u795e\u541b\u5fc5\u4e0d\u4f1a\u9632\u7740\uff0c\u90a3\u77e5\u8f9b\u6377\u51b7\u54fc\u4e00\u58f0\uff0c\u8eab\u5b50\u4e0d\u4f46\u4e0d \u5411\u540e\u9000\uff0c\u540e\u800c\u524d\u8de8\u4e00\u6b65\uff0c\u5de6\u624b\u4e00\u6454\u4e00\u5e26\uff0c\u4e00\u80a1\u6781\u5f3a\u7684\u529b\u9053\u51cc\u7a7a\u5288\u51fa\u2014\u2014 \u3000\u3000\u4e8c\u80a1\u6c14\u6d41\u4e00\u649e\u4e4b\u4e0b\uff0c\u8f9b\u6377\u987a\u624b\u4e00\u6325\uff0c\u4efb\u5353\u5ba3\u7a81\u89c9\u5bf9\u65b9\u638c\u529b\u5f3a\u8fc7\u81ea\u5df1\u4f55\u6b62\u6570\u500d\uff0c\u5fc3\u4e2d \u4e00\u5bd2\uff0c\u8eab\u5f62\u4e0d\u7531\u4e00\u7a92\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u56db\u8fb9\u89c2\u6218\u7684\u4eba\u90fd\u4e0d\u7531\u5fc3\u5934\u5927\u9707\uff0c\u60f3\u8fd9\u6d77\u5185\u4e00\u4ee3\u9b3c\u624d\u4e03\u5999\u795e\u541b\u7adf\u7136\u6ca1\u6709\u6b7b\u5728\u4e94 \u6d3e\u8054\u624b\u4e4b\u4e0b\uff0c\u529f\u529b\u786e\u5b9e\u662f\u8d85\u51e1\u4eba\u5723\u4e86\u3002 \u3000\u3000\u5c71\u5de6\u53cc\u8c6a\u4e2d\u7684\u795e\u5251\u91d1\u9524\u6797\u5c11\u768b\u5df2\u7136\u6c89\u4e0d\u4f4f\u6c14\uff0c\u563f\u7684\u4e00\u58f0\uff0c\u4e00\u638c\u5288\u5411\u8f9b\u6377\u7684\u8098\u90e8\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u7136\u4e00\u7b11\uff0c\u638c\u5f0f\u7a0d\u7a0d\u5f80\u540e\u4e00\u503e\uff0c\u8fd0\u7528\u201c\u9ecf\u201d\u5b57\u8bc0\uff0c\u767b\u7684\u53c8\u5c06\u6797\u5c11\u768b\u7684\u638c\u5f0f\u63a5\u4e86 \u4e0b\u6765\u3002 \u3000\u3000\u8f9b\u6377\u6bd4\u65f6\u529f\u529b\uff0c\u5df1\u88ab\u5e73\u51e1\u4e0a\u4eba\u7528\u201c\u63d0\u7cca\u704c\u9879\u201d\u7684\u624b\u6cd5\u6253\u901a\uff0c\u529f\u529b\u589e\u8fdb\u4e00\u7532\u5b50\uff0c\u52a0\u4e0a \u5df2\u5f97\u6885\u5c71\u6c11\u5168\u90e8\u771f\u4f20\u7684\u62db\u5f0f\uff0c\u8fd0\u7528\u8d77\u6765\uff0c\u5fc5\u5b9a\u8f7b\u800c\u6613\u4e3e\u5730\u53ef\u6253\u8d25\u4efb\u5353\u5ba3\u548c\u6797\u5c11\u768b\uff0c\u4f46\u4ed6 \u5374\u662f\u5192\u7740\u4e03\u5999\u795e\u541b\u7684\u540d\u513f\uff0c\u7adf\u5b58\u6709\u7528\u5185\u529b\u5f3a\u649e\u7684\u5fc3\u610f\uff0c\u662f\u4ee5\u5410\u638c\u63a5\u4f4f\u4e8c\u4eba\u653b\u52bf\uff01\uff01 \u3000\u3000\u4efb\uff0c\u6797\u4e8c\u4eba\u90fd\u662f\u6c5f\u6e56\u4e0a\u4e00\u7b49\u4e00\u7684\u9b54\u5934\uff0c\u529f\u529b\u4e4b\u9ad8\uff0c\u4e5f\u90fd\u66fe\u540d\u9707\u4e00\u5e26\uff0c\u8fd9\u65f6\u5408\u624b\u4e4b\u529b\uff0c \u53ef\u60f3\u800c\u77e5\uff01\uff01 \u3000\u3000\u8f9b\u6377\u51b7\u54fc\u4e00\u58f0\uff0c\u201c\u563f\u201d\u7684\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u7a81\u7136\u771f\u529b\u6ea2\u5f3a\uff0c\u539f\u6765\u4ed6\u5df2\u4f7f\u51fa\u4e86\u516b\u6210\u7684\u529b\u9053\u3002 \u3000\u3000\u4efb\uff0c\u6797\u4e8c\u4eba\u4e0d\u60f3\u4e03\u5999\u795e\u541b\u7684\u5185\u529b\u5982\u6b64\u9ad8\u5f3a\uff0c\u4e5f\u9f50\u5f00\u58f0\u5410\u6c14\uff0c\u52a0\u5f3a\u638c\u5f0f\uff01 \u3000\u3000\u6458\u661f\u624b\u53f8\u7a7a\u5b97\u662f\u4f55\u7b49\u8001\u7ec3\u7684\u6c5f\u6e56\uff0c\u4e00\u671b\u4e4b\u4e0b\u5df2\u77e5\u8f9b\u6377\u4e43\u662f\u8981\u5f3a\u63a5\uff0c\u51b7\u7b11\u4e00\u58f0\uff0c\u8de8\u6b65 \u4e0a\u524d\u3002 \u3000\u3000\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u6562\u60c5\u662f\u53f8\u7a7a\u5b97\u4e00\u638c\u5288\u4e86\u4e0b\u53bb\uff0c\u8f9b\u6377\u5fc3\u5934\u4e00\u9707\uff0c\u52c9\u5f3a\u503e\u638c\u63a5\u4f4f\u6765\u52bf\u3002 \u3000\u3000\u53f8\u7a7a\u5b97\u5916\u53f7\u6458\u661f\u624b\uff0c\u5176\u638c\u4e0a\u9020\u8be3\u53ef\u60f3\u800c\u77e5\uff0c\u8f9b\u6377\u4e00\u63a5\uff0c\u5fc3\u5934\u4e00\u9635\u72c2\u8df3\uff0c\u6597\u7136\u957f\u5438\u4e00 \u53e3\u6c14\uff0c\u5300\u548c\u771f\u6c14\uff0c\u5341\u6210\u529b\u9053\u5df2\u7136\u53d1\u53bb\u3002 \u3000\u3000\u8981\u77e5\u8f9b\u6377\u6b64\u65f6\u4e0d\u4f46\u662f\u62db\u5f0f\uff0c\u5c31\u662f\u529f\u529b\u4e5f\u8db3\u4ee5\u548c\u5929\u4e0b\u4efb\u4f55\u9ad8\u624b\u6297\u8861\uff0c\u4f46\u8fd9\u65f6\u4ee5\u4e00\u654c\u4e09 \u4e2a\u9876\u5c16\u513f\u7684\u4eba\u7269\uff0c\u4e5f\u4e0d\u514d\u6709\u4e9b\u513f\u5403\u529b\u3002 \u3000\u3000\u201c\u5173\u4e2d\u4e5d\u8c6a\u201d\u4e2d\u4e09\u4e2a\u5df2\u51fa\u4e86\u624b\uff0c\u53ea\u6709\u201c\u957f\u5929\u4e00\u78a7\u201d\u767d\u98ce\u5c1a\u5bd2\u8138\u7ad9\u4e00\u65c1\u3002\u8fd9\u767d\u98ce\u6614\u5e74 \u5014\u8d77\u6c5f\u6e56\uff0c\u51ed\u4e00\u8eab\u7edd\u5b66\u6253\u904d\u5927\u6c5f\u5357\u5317\uff0c\u529f\u529b\u91cf\u662f\u6df1\u539a\uff0c\u4e3a\u4eba\u4e5f\u6700\u662f\u9634\u9669\u3002 \u3000\u3000\u4ee5\u4ed6\u8fd9\u79cd\u529f\u529b\u548c\u7ecf\u9a8c\u90a3\u8fd8\u770b\u4e0d\u51fa\u201c\u4e03\u5999\u795e\u541b\u201d\u5df2\u662f\u5168\u529b\u65bd\u4e3a\uff0c\u53ea\u8981\u81ea\u5df1\u4e00\u52a0\u624b\uff0c\u5bf9 \u624b\u5fc5\u4f24\u65e0\u7591\uff0c\u4f46\u662f\u65c1\u8fb9\u8fd8\u7ad9\u7740\u4e00\u4e2a\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u81ea\u5df1\u51fa\u624b\uff0c\u5bf9\u65b9\u5fc5\u4e0d\u653e\u8fc7\uff0c\u4e00\u6218\u4e4b\u4e0b\uff0c \u9e7f\u6b7b\u8c01\u624b\u5c1a\u672a\u53ef\u6599\uff0c\u662f\u4ee5\u8fdf\u8fdf\u4e0d\u80af\u52a8\u624b\u3002 \u3000\u3000\u84e6\u5730\u8f9b\u6377\u53c8\u662f\u4e00\u58f0\u5927\u53f1\uff0c\u539f\u6765\u662f\u4f53\u5185\u771f\u6c14\u8fd0\u8f6c\u5fae\u7a92\uff0c\u767b\u65f6\u8eab\u5f62\u540e\u9000\u3002 \u3000\u3000\u6b64\u65f6\u53cc\u65b9\u662f\u6210\u52bf\u5747\u529b\u654c\u4e4b\u5f0f\uff0c\u4f46\u5047\u82e5\u8f9b\u6377\u6536\u638c\u540e\u9000\uff0c\u4e09\u4eba\u7684\u5408\u529b\u5fc5\u4e0d\u4f1a\u914d\u5408\u5f97\u5f88\u5747 \u5300\uff0c\u4ee5\u8f9b\u6377\u7684\u529f\u529b\uff0c\u5fc5\u53ef\u81ea\u4fdd\uff0c\u53cd\u4e4b\u82e5\u4efb\uff0c\u6797\uff0c\u53f8\u7a7a\u4e09\u4eba\u6536\u638c\uff0c\u5219\u8f9b\u6377\u4e00\u4eba\u4e4b\u529b\u5408\u51fb\u4e4b \u4e0b\uff0c\u4e09\u4eba\u90fd\u5f97\u91cd\u4f24\uff0c\u662f\u4ee5\u8868\u9762\u4e0a\u770b\u8d77\u6765\u662f\u8f9b\u6377\u5931\u5229\uff0c\u4f46\u4e8b\u5b9e\u4e0a\u90a3\u4e09\u4eba\u5374\u662f\u6210\u9a91\u864e\u4e4b\u52bf\u54e9\uff01 \u3000\u3000\u4f46\u8f9b\u6377\u6b64\u65f6\u4e43\u662f\u9876\u5192\u7740\u201c\u4e03\u5999\u795e\u541b\u201d\u7684\u5927\u540d\u513f\uff0c\u5c82\u53ef\u6536\u638c\u793a\u5f31\uff0c\u662f\u4ee5\u8f9b\u6377\u4ecd\u594b\u529b\u62b5 \u6297\u3002 \u3000\u3000\u767d\u98ce\u5728\u4e00\u65c1\u6743\u8861\u4e0d\u4e86\uff0c\u5fc3\u5934\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u5927\u8de8\u4e00\u6b65\uff0c\u731b\u5438\u4e00\u53e3\u957f\u6c14\uff0c\u51c6\u5907\u4ee5\u795e\u529f\u649e \u51fb\u2014\u2014 \u3000\u3000\u5728\u4ed6\u610f\u6599\u4e4b\u4e2d\uff0c\u8c22\u957f\u537f\u5fc5\u7136\u4f1a\u51fa\u624b\u963b\u62e6\uff0c\u662f\u4ee5\u773c\u89d2\u4e00\u659c\uff0c\u5374\u89c1\u8c22\u957f\u537f\u9762\u8272\u6728\u7136\uff0c\u63e1 \u5251\u624b\u4e0b\u5782\uff0c\u4f3c\u4e4e\u5df2\u7136\u5165\u8ff7\uff0c\u4e00\u7247\u832b\u7136\u4e4b\u8272\u3002 \u3000\u3000\u767d\u98ce\u5fc3\u4e2d\u4e00\u559c\uff0c\u53f3\u638c\u7f13\u7f13\u63a8\u51fa\uff0c\u638c\u5fc3\u5fae\u767b\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6797\u8fb9\u4e00\u58f0\u66b4\u54cd\uff0c\u4e00\u6761 \u4eba\u5f71\u5982\u98de\u800c\u51fa\uff0c\u5de6\u638c\u4e00\u5708\uff0c\u53f3\u638c\u4e00\u5212\uff0c\u4e00\u62db\u4e8c\u5f0f\uff0c\u5408\u51fb\u800c\u4e0b\uff0c\u6b63\u8fce\u7740\u767d\u98ce\u4e00\u51fb\u4e4b\u52bf\u3002 \u3000\u3000\u8981\u77e5\u4e03\u5999\u795e\u541b\u626c\u540d\u5929\u4e0b\u4e8e\u5345\u5e74\u524d\uff0c\u4e07\u513f\u4e4b\u5927\uff0c\u540d\u513f\u4e4b\u54cd\uff0c\u5b9e\u4e3a\u6d77\u5185\u7b2c\u4e00\u4eba\uff0c\u767d\u98ce\u6b64 \u65f6\uff0c\u4e00\u5fc3\u4e00\u610f\u4ee5\u4e3a\u8f9b\u6377\u4fbf\u662f\u4e03\u5999\u795e\u541b\uff0c\u90a3\u8fd8\u6562\u6709\u4e00\u4e1d\u4e00\u6beb\u7684\u5927\u610f\uff0c\u4e00\u638c\u6253\u53bb\uff0c\u867d\u662f\u98ce\u58f0 \u6beb\u65e0\uff0c\u4f46\u5a01\u529b\u5374\u662f\u5947\u731b\uff0c\u8db3\u53ef\u88c2\u767e\u788e\u7891\u3002 \u3000\u3000\u5728\u4e00\u65c1\u6811\u4e0a\u9690\u4f0f\u7740\u7684\u5434\u51cc\u98ce\u65e9\u5df2\u5fcd\u8010\u4e0d\u4f4f\uff0c\u8fd9\u65f6\u89c1\u8f9b\u6377\u5904\u4e8e\u5371\u5883\uff0c\u7eb5\u4e86\u4e0b\u6765\uff0c\u786c\u67b6 \u4e00\u638c\u3002 \u3000\u3000\u6614\u5e74\u6cb3\u6d1b\u4e00\u5251\u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\u4e00\u5251\u79f0\u9738\u534e\u4e2d\uff0c\u638c\u4e0a\u529f\u592b\u4ea6\u662f\u7edd\u9876\uff0c\u5434\u51cc\u98ce\u81ea\u5e7c\u968f\u5f02 \u4eba\u5b66\u4e60\uff0c\u5bb6\u4f20\u7edd\u5b66\uff0c\u638c\u6cd5\u90a3\u8fd8\u9519\u5f97\u4e86\uff0c\u4e00\u62db\u4e8c\u5f0f\u786c\u64a9\u767d\u98ce\u53cc\u8098\u3002 \u3000\u3000\u767d\u98ce\u4f46\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u654c\u638c\u5df2\u5230\uff0c\u563f\u7136\u60ca\u547c\uff0c\u638c\u5fc3\u5916\u767b\uff0c\u672c\u6765\u6beb\u4e0d\u5e26\u98ce\u58f0\u7684\u4e00\u638c\u7a81\u7136 \u98ce\u4e91\u4e4b\u58f0\u5927\u8d77\uff0c\u5927\u6982\u662f\u7a81\u7136\u52a0\u5f3a\u7684\u539f\u6545\uff0c\u4e8c\u4e08\u65b9\u5706\u5de6\u53f3\u7684\u5730\u65b9\uff0c\u6c14\u6d41\u7adf\u81ea\u51b2\u6fc0\u800c\u65cb\uff0c\u58f0 \u52bf\u60ca\u4eba\u4e4b\u6781\u3002 \u3000\u3000\u5434\u51cc\u98ce\u4e0d\u6599\u654c\u624b\u638c\u529b\u5982\u6b64\u5f3a\u539a\uff0c\u54fc\u4e00\u58f0\uff0c\u5de6\u624b\u4e00\u6536\uff0c\u95ea\u7535\u822c\u518d\u5411\u5916\u4e00\u6c89\uff0c\u4e00\u62db\u201c\u5f00 \u5c71\u9053\u6d41\u201d\u786c\u649e\u800c\u51fa\u3002\u638c\u5f0f\u501f\u4e00\u6536\u4e00\u653e\u4e4b\u95f4\uff0c\u771f\u529b\u5df2\u53eb\u81f3\u5341\u6210\u3002 \u3000\u3000\u767d\u98ce\u6025\u5207\u4e4b\u95f4\u4e0d\u8fa8\u654c\u53cb\uff0c\u4e0d\u6562\u9020\u6b21\uff0c\u4f46\u5bf9\u624b\u529b\u9053\u5b9e\u5728\u592a\u5f3a\uff0c\u4e5f\u4e0d\u6562\u4e1d\u6beb\u4fdd\u7559\uff0c\u5168\u529b \u4e00\u6325\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u4e0d\u557b\u662f\u5f3a\u78b0\u5f3a\uff0c\u786c\u5bf9\u786c\uff0c\u5434\u51cc\u98ce\u6597\u89c9\u4e00\u822c\u7edd\u5927\u7684\u529b\u9053\u628a\u81ea\u5df1\u51cc\u7a7a\u4e0b\u51fb\u5343\u65a4\u4e4b \u529b\uff0c\u786c\u5c01\u4e0a\u53bb\uff0c\u81ea\u5df1\u8eab\u4f53\u4e0d\u7531\u4e0a\u5347\u6570\u4e08\uff0c\u6025\u770b\u90a3\u767d\u98ce\u65f6\uff0c\u4e5f\u5403\u4e0d\u4f4f\u4e00\u649e\u4e4b\u529b\uff0c\u767b\u767b\u8fde\u9000 \u4e03\u516b\u6b65\u3002 \u3000\u3000\u4e8c\u4eba\u4e00\u62fc\u4e4b\u4e0b\uff0c\u5434\u51cc\u98ce\u5fc3\u4e2d\u6709\u6570\uff0c\u81ea\u5df1\u7684\u529f\u529b\u662f\u7565\u900a\u4e8e\u5bf9\u65b9\uff0c\u5728\u7a7a\u4e2d\u7565\u4e00\u6446\u624b\uff0c\u545b \u5527\u4e00\u54cd\uff0c\u65ad\u9b42\u5b9d\u5251\u6492\u5728\u624b\u4e2d\uff0c\u6296\u8d77\u6f2b\u5929\u5251\u82b1\uff0c\u5012\u6492\u4e0b\u6765\u3002 \u3000\u3000\u767d\u98ce\u5728\u786c\u62fc\u4e4b\u4e0b\uff0c\u4e5f\u5fae\u89c9\u6c14\u52a8\uff0c\u89c1\u654c\u4eba\u5175\u5203\u51fa\u624b\uff0c\u4e0d\u6562\u505c\u7559\uff0c\u53cc\u624b\u8f7b\u5de7\u4e00\u7ffb\uff0c\u4e00\u652f \u56db\u5c3a\u5de6\u53f3\u7684\u7cbe\u94a2\u6000\u6756\u4e5f\u5230\u4e86\u624b\u4e0a\u3002\u5434\u51cc\u98ce\u5728\u7a7a\u4e2d\u89c1\u767d\u98ce\u64a4\u53d6\u5175\u5203\u7684\u624b\u6cd5\u4fbf\u77e5\u4ed6\u5175\u5203\u4e0a\u7684 \u529f\u592b\u5fc5\u4e5f\u4e0d\u5f31\uff0c\u6e05\u53f1\u4e00\u58f0\uff0c\u523a\u5c06\u4e0b\u6765\u3002 \u3000\u3000\u767d\u98ce\u66b4\u53f1\u4e00\u58f0\uff0c\u6000\u6756\u4ea4\u76f8\u51fb\u5904\uff0c\u201c\u6cbc\u201d\u7684\u54cd\u8d77\u9707\u8033\u6b32\u804b\u7684\u4e00\u58f0\u66b4\u54cd\u3002\u547c\u547c\u98ce\u58f0\u4e2d\uff0c \u4e8c\u4eba\u5df2\u4ea4\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u4e00\u65c1\u51b7\u843d\u7684\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u6b64\u65f6\u90a3\u4e00\u5e55\u5e55\u7684\u5f80\u4e8b\u5386\u5386\u5982\u5728\u773c\u524d\uff0c\u5bf9\u4e00\u65c1\u9163\u6218\u4e0d\u95ee\u3002 \u3000\u3000\u662f\u5341\u5e74\u524d\u7684\u4e8b\u4e86\uff0c\u5728\u90a3\u6606\u4ed1\u4e94\u534e\u7684\u7edd\u8c37\u4e2d\uff0c\u795e\u541b\u548c\u56db\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u5bf9\u9635\uff0c\u4e94\u6d3e\u4e2d \u6606\u4ed1\u51cc\u7a7a\u6b65\u865a\u5353\u5927\u4fa0\u56e0\u5728\u5929\u7ec5\u7011\u524d\u56f4\u653b\u5434\u8bcf\u4e91\u53d7\u91cd\u4f24\u53bb\u4e16\uff0c\u8c22\u957f\u537f\u7684\u7236\u4eb2\u4e5f\u662f\u4e00\u6218\u800c\u6b7b\uff0c \u4ed6\u4e3a\u4e86\u540d\u58f0\u548c\u6027\u547d\uff0c\u7adf\u4e0d\u987e\u4e00\u5207\u7684\u4e0b\u624b\u6697\u7b97\u6b63\u5728\u548c\u4e09\u5927\u6d3e\u7684\u9ad8\u624b\u6597\u5185\u529b\u7684\u795e\u541b\u3002 \u3000\u3000\u5341\u5e74\u6765\uff0c\u8fd9\u4e8b\u60c5\u65e0\u4e00\u5206\u79d2\u4e0d\u5728\u50cf\u6bd2\u86c7\u822c\u541e\u566c\u7740\u4ed6\u7684\u5fc3\u2014\u2014\u4ed6\u4e0b\u610f\u8bc6\u7684\u505a\u51fa\u4e00\u4e2a\u88ad\u51fb \u7684\u59ff\u6001\uff0c\u90a3\u6562\u60c5\u662f\u4ed6\u7528\u70b9\u82cd\u201c\u4e03\u7edd\u624b\u201d\u52a0\u5bb3\u4e8e\u6885\u5c71\u6c11\u65f6\u6240\u7528\u7684\u62db\u5f0f\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u53d1\u72c2\u4f3c\u7684\u77aa\u89c6\u7740\u53cc\u624b\uff0c\u4e00\u5239\u65f6\u4ed6\u4eff\u4f5b\u53d1\u89c9\u4ed6\u6ee1\u624b\u90fd\u6cbe\u6ee1\u8840\u8165\uff0c\u540c\u65f6\u4ed6\u8138\u4e0a\u4e5f \u505a\u51fa\u4e00\u4e2a\u53ef\u6015\u7684\u8868\u60c5\u3002 \u3000\u3000\u84e6\u5730\u5f53\u7684\u4e00\u58f0\u66b4\u54cd\uff0c\u6562\u60c5\u662f\u6000\u6756\u548c\u5b9d\u5251\u4ea4\u89e6\u4e86\u4e00\u6b21\uff0c\u4ed6\u88ab\u8fd9\u4e00\u58f0\u60ca\u9192\u8fc7\u6765\uff0c\u62ac\u5934\u4e00 \u671b\uff0c\u53ea\u89c1\u5434\u51cc\u98ce\u548c\u957f\u5929\u4e00\u78a7\u4e8c\u4eba\u6597\u5f97\u6b63\u9163\uff0c\u504f\u9996\u4e00\u77a7\uff0c\u90a3\u8fb9\u201c\u4e03\u5999\u795e\u541b\u201d\u8fd8\u5728\u548c\u201c\u6653\u6708 \u5bd2\u5fc3\u638c\u201d\u53ca\u201c\u5c71\u5de6\u53cc\u8c6a\u201d\u62fc\u6597\u5185\u529b\u3002 \u3000\u3000\u4e00\u4e2a\u5ff5\u5934\u6709\u5982\u95ea\u7535\u822c\u901a\u8fc7\u4ed6\u7684\u8111\u9645\uff0c\u4ed6\u8138\u4e0a\u5fae\u5fae\u4e00\u9635\u75c9\u90a3\u5f20\u4fca\u79c0\u7684\u8138\u5b54\u7acb\u523b\u53d8\u6210\u72f0 \u72de\u53ef\u6016\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u5f20\u76ee\u4e00\u671b\u5434\u3001\u767d\u4e8c\u4eba\u7684\u6218\u52bf\uff0c\u5fc3\u4e2d\u7acb\u523b\u4e0b\u4e86\u4e00\u4e2a\u65ad\u8a00\uff0c\u4efb\u4f55\u4e00\u4eba\u4e0d\u53ef\u80fd\u5728\u4e00\u65f6\u534a \u523b\u4e2d\u5206\u51fa\u624b\u6765\u3002\u4ed6\u518d\u770b\u4e86\u770b\u201c\u4e03\u5999\u795e\u541b\u201d\uff0c\u500f\u5730\u5341\u5e74\u524d\u7684\u60c5\u666f\u53c8\u5982\u5386\u5386\u5728\u76ee\uff0c\u53ea\u4e0d\u8fc7\u90a3 \u4e09\u5927\u6d3e\u7684\u9ad8\u624b\u5374\u53d8\u6210\u4e86\u201c\u5c71\u5de6\u53cc\u8c6a\u201d\u548c\u201c\u6653\u6708\u5bd2\u5fc3\u638c\u201d\u7f62\u4e86\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u597d\u50cf\u5e76\u6ca1\u6709\u5bf9\u6211\u62b1\u6709\u62a5\u590d\u7684\u610f\u5fd7\uff01\u201d\u4ed6\u5fd6\u9053\uff1a\u201c\u4f46\u662f\u6211\u90a3\u4e00\u51fb\u65e0\u8bba\u5982\u4f55 \u81f3\u5c11\u4f7f\u4ed6\u529f\u529b\u5168\u5931\u7684\uff0c\u600e\u4e48\u4ed6\u7adf\u2014\u2014\u201d \u3000\u3000\u201c\u4e0d\u8fc7\u73b0\u5728\u53ea\u8981\u518d\u7528\u2018\u4e03\u7edd\u624b\u6cd5\u2019\u70b9\u4ed6\u7684\u2018\u5929\u7075\u2019\u3001\u2018\u5929\u4fc3\u2019\u7a74\u9053\uff0c\u90a3\u6015\u662f\u94c1\u4eba\u4e5f \u4f1a\u7acb\u6bd9\uff01\u563f\uff01\u8fd9\u6b21\u4e0b\u624b\u4e0d\u53ef\u518d\u4fdd\u7559\u4e00\u4e9b\u4e86\uff01\u201d \u3000\u3000\u201c\u5475\uff01\u4e0d\u5bf9\uff01\u4ed6\u5230\u5e95\u548c\u6211\u6709\u4ec0\u4e48\u5927\u4ec7\u6068\u5475\uff1f\u6211\u5c82\u53ef\u4e00\u518d\u6697\u7b97\u4e8e\u4ed6\uff01\u201d\u8fd9\u4e2a\u5ff5\u5934\u500f\u7136 \u5347\u8d77\uff0c\u4e0d\u89c9\u4f7f\u4ed6\u8138\u4e0a\u5fae\u5fae\u4e00\u7ea2\u3002 \u3000\u3000\u201c\u4e0d\uff01\u4e0d\uff01\u6211\u5982\u4e0d\u4e0b\u624b\uff0c\u4ed6\u5c82\u4e0d\u8981\u7f6e\u6211\u4e8e\u6b7b\u5730\uff01\u65e9\u95fb\u4e03\u5999\u795e\u541b\u662f\u4e00\u4e2a\u6781\u6bd2\u8fa3\u7684\u5bb6\u4f19 \u5462\u3002\u201d\u4ed6\u81ea\u6211\u89e3\u5632\u7684\u6697\u9053\u3002 \u3000\u3000\u4e00\u8fde\u4e32\u7684\u5ff5\u5934\u50cf\u95ea\u7535\u822c\u63a0\u8fc7\uff0c\u4f46\u4ed6\u4fca\u7f8e\u7684\u8138\u5b54\u4e0a\u5df2\u53d8\u6362\u4e86\u6570\u79cd\u4e0d\u540c\u7684\u9762\u8272\uff01 \u3000\u3000\u201c\u6211\u7684\u5e74\u7eaa\u8fd8\u4e0d\u7b97\u5927\uff0c\u4e3a\u4ec0\u4e48\u8981\u968f\u7740\u4e00\u5757\u513f\u6b7b\u53bb\uff1f\u201d\u4ed6\u4e0d\u89e3\u4f3c\u7684\u81ea\u95ee\u3002 \u3000\u3000\u9661\u7136\u4ed6\u5ff5\u8d77\u5728\u5341\u5e74\u524d\u4ed6\u4e5f\u662f\u4e3a\u4e86\u8fd9\u4e00\u4e2a\u53ef\u6015\u800c\u53ef\u5351\u7684\u5ff5\u5934\u624d\u4e0b\u7684\u624b\u2014\u2014 \u3000\u3000\u201c\u751f\u547d\u548c\u540d\u671b\u53c8\u7b97\u5f97\u4ec0\u4e48\uff1f\u201d\u8fd9\u4e00\u53e5\u8bdd\u5728\u4ed6\u7b2c\u4e00\u6b21\u4e0b\u624b\u6697\u7b97\u6885\u5c71\u6c11\u540e\u5e38\u81ea\u6127\u81ea\u6094\u7684 \u81ea\u95ee\uff0c\u6b64\u65f6\u60f3\u5230\u5374\u683c\u5916\u89c9\u5f97\u523a\u8033\u3002 \u3000\u3000\u73b0\u5728\u4ed6\u611f\u5230\u4e86\u771f\u6b63\u7684\u56f0\u60d1\u2014\u2014 \u3000\u3000\u867d\u7136\u4ed6\u5728\u90a3\u6f2b\u957f\u7684\u5c81\u6708\u4e2d\u65e0\u65f6\u65e0\u523b\u4e0d\u5728\u81ea\u8d23\uff0c\u4f46\u5230\u4e86\u8fd9\u201c\u826f\u673a\u201d\u518d\u6765\u4e34\u7684\u65f6\u5019\uff0c\u4ed6 \u5374\u53c8\u5174\u8d77\u4e86\u8fd9\u79cd\u5ff5\u5934\u3002 \u3000\u3000\u84e6\u5730\u4ed6\u53c8\u60f3\u5230\u4e86\u90a3\u8ffd\u6094\u7684\u75db\u82e6\uff0c\u90a3\u53d7\u7740\u6162\u6027\u5fc3\u7075\u4e0a\u632b\u6298\u7684\u96be\u582a\uff0c\u4ed6\u201c\u563f\u201d\u7684\u5410\u51fa\u5438 \u6ee1\u5168\u8eab\u7684\u771f\u529b\uff01 \u3000\u3000\u201c\u6211\u5c82\u80fd\u4e00\u9519\u518d\u9519\uff1f\u5341\u5e74\u7684\u75db\u82e6\u8fd8\u4e0d\u591f\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u6068\u6068\u4e00\u8dfa\u811a\uff0c\u53cd\u624b\u63d2\u4e0a\u957f\u5251\u3002\u5e72\u786c\u7684\u571f\u5730\u4e0a\u987f\u65f6\u88c2\u5f00\u4e00\u5927\u7247\u6765\uff01'}),showOnHomepage:!0},{name:"\u7b2c \u4e03 \u7ae0",content:(0,s.jsx)(s.Fragment,{children:'\u5c71\u98ce\u5439\u7740\u6811\u53f6\u6c99\u6c99\u53d1\u54cd\uff0c\u6674\u7a7a\u4e07\u91cc\uff0c\u5b9b\u5982\u84dd\u8272\u7684\u5899\u5e55\u5782\u5728\u56db\u5468\uff0c\u53ea\u897f\u65b9\u5c71\u5cf0\u4e0e\u5929\u76f8 \u63a5\u7684\u5730\u65b9\uff0c\u4e00\u6735\u5b64\u5355\u767d\u4e91\u505c\u5728\u90a3\u513f\uff0c\u90a3\u6d01\u767d\u66f4\u663e\u51fa\u4e86\u5929\u7684\u84dd\u3002 \u3000\u3000\u8c22\u957f\u537f\u4ef0\u9996\u89c2\u5929\uff0c\u4ed6\u7684\u5fc3\u5982\u5c0f\u9e7f\u822c\u4e71\u649e\u8d77\u6765\uff0c\u4ed6\u4e0d\u505c\u5730\u81ea\u95ee\uff1a\u201c\u6211\u8be5\u4e0d\u8be5\u52a9\u8fd9\u2018\u4e03 \u5999\u795e\u541b\u2019\u4e00\u81c2\u4e4b\u529b\uff1f\u201d \u3000\u3000\u4e00\u5206\u949f\u524d\u4ed6\u8fd8\u5728\u4e0d\u4f4f\u8003\u8651\uff1a\u201c\u6211\u8be5\u4e0d\u8be5\u518d\u4e0b\u6bd2\u624b\uff1f\u201d\u4f46\u8fd9\u65f6\u4ed6\u7684\u5fc3\u60c5\u4f5c\u4e86\u4e00\u767e\u516b\u5341 \u5ea6\u7684\u5927\u6539\u53d8\u3002 \u3000\u3000\u8bf4\u51fa\u6765\u4e5f\u8bb8\u8c22\u957f\u537f\u81ea\u5df1\u90fd\u4f1a\u5403\u60ca\uff0c\u4ed6\u4ece\u5341\u5e74\u524d\u7b2c\u4e00\u773c\u89c1\u4e86\u4e03\u5999\u795e\u541b\uff0c\u5fc3\u4e2d\u5c31\u6df1\u6df1\u5730 \u94a6\u4f69\u6885\u5c71\u6c11\u7684\u98ce\u5ea6\uff0c\u800c\u8fd9\u5ff5\u5934\u5341\u5e74\u6765\u4e0d\u4f46\u6ca1\u6709\u56e0\u4e3a\u4ed6\u7684\u6697\u7b97\u6885\u5c71\u6c11\u800c\u51cf\u9000\uff0c\u53cd\u800c\u5728\u5fc3\u7684 \u6df1\u5904\u4e0d\u65ad\u7684\u6ecb\u957f\uff0c\u53ea\u4e0d\u8fc7\u4e00\u5f53\u4ed6\u5ff5\u53ca\u6b64\u65f6\uff0c\u4ed6\u7acb\u523b\u5c3d\u91cf\u628a\u6885\u5c71\u6c11\u60f3\u6210\u4e00\u4e2a\u6bd2\u8fa3\u9a84\u50b2\u7684\u5bb6 \u4f19\uff0c\u4ee5\u5bbd\u6055\u81ea\u5df1\u7684\u7f6a\u884c\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u5fc3\u4e2d\u4ea4\u6218\u7740\u3002\u8001\u5b9e\u8bf4\uff0c\u4ed6\u662f\u60f3\u4e0a\u53bb\u52a9\u201c\u4e03\u5999\u795e\u541b\u201d\u4e00\u81c2\u4e4b\u529b\u7684\uff0c\u4f46\u662f\u771f\u8981\u4ed6 \u4e0a\u53bb\u65f6\uff0c\u4ed6\u7adf\u6000\u7740\u4e00\u79cd\u201c\u4e0d\u80af\u8ba4\u9519\u201d\u7684\u5fc3\u60c5\uff0c\u77dc\u6301\u7740\u4e0d\u80af\u4e0a\u524d\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5ffd\u7136\u201c\u563f\u201d\u7684\u4e00\u58f0\u95f7\u54fc\u4f20\u4e86\u8fc7\u6765\uff0c\u8c22\u957f\u537f\u4e3e\u76ee\u4e0a\u671b\uff0c\u53ea\u89c1\u201c\u4e03\u5999\u795e\u541b\u201d\u529b\u654c \u90a3\u5c71\u5de6\u53cc\u8c6a\u53ca\u6653\u6708\u5bd2\u5fc3\u638c\u4efb\u5353\u5ba3\uff0c\u5df1\u5230\u4e86\u4e00\u53d1\u5343\u94a7\u7684\u5173\u5934\u3002 \u3000\u3000\u5f53\u5e74\u4e03\u5999\u795e\u541b\u4ee5\u5185\u529b\u786c\u654c\u4e09\u5927\u5251\u6d3e\u638c\u95e8\uff0c\u800c\u5982\u4eca\u5c71\u5de6\u53cc\u8c6a\u53ca\u4efb\u5353\u5ba3\u7684\u5185\u529b\u9020\u8be3\u8f83\u4e4b \u5341\u5e74\u524d\u7684\u4e09\u6d3e\u638c\u95e8\u5e76\u4e0d\u591a\u8ba9\uff0c\u7531\u6b64\u53ef\u89c1\u51fa\u8f9b\u6377\u6b64\u65f6\u529f\u529b\u4e4b\u6df1\u539a\u3002 \u3000\u3000\u84e6\u7136\uff0c\u5475\u5475\u4e00\u58f0\u957f\u7b11\uff0c\u6797\u4e2d\u7eb5\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u53ea\u89c1\u4ed6\u5e74\u7ea6\u4e09\u65ec\uff0c\u4e00\u88ad\u9ec4\u886b\uff0c\u817f\u4e0a\u5374\u6253 \u7740\u4e00\u6761\u7ed1\u817f\uff0c\u663e\u5f97\u6709\u70b9\u4e0d\u4f26\u4e0d\u7c7b\u3002 \u3000\u3000\u8c22\u957f\u537f\u56de\u9996\u4e00\u770b\u6b64\u4eba\uff0c\u8138\u4e0a\u989c\u8272\u5927\u53d8\u3002 \u3000\u3000\u90a3\u4eba\u5374\u5355\u638c\u4e00\u626c\uff0c\u631f\u7740\u52b2\u98ce\u5411\u201c\u4e03\u5999\u795e\u541b\u201d\u6253\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u6b63\u4ee5\u5168\u529b\u548c\u5bf9\u624b\u626f\u6210\u5e73\u624b\uff0c\u90a3\u4eba\u638c\u529b\u518d\u52a0\u4e0a\u6765\uff0c\u8eb2\u65e0\u53ef\u8eb2\uff0c\u52bf\u5fc5\u843d\u4e2a\u91cd\u4f24 \u7684\u573a\u9762\uff0c\u4ed6\u4e00\u6025\u4e4b\u4e0b\uff0c\u5fc3\u795e\u4e00\u4e71\uff0c\u7acb\u611f\u5bf9\u65b9\u5185\u529b\u903c\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u84e6\u7136\u4e00\u58f0\u6e05\u53f1\uff1a\u201c\u53db\u5f92\uff0c\u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u53ea\u89c1\u8c22\u957f\u537f\u624b\u6301\u957f\u5251\uff0c\u4e00\u4e2a\u201c\u98ce\u6325\u788e\u7d6e\u201d\u7684\u5f0f\u5b50\uff0c\u7eb5\u4e86\u4e0a\u6765\uff0c\u5251\u5c16\u76f4\u6307\u6765\u4eba\u8155\u4e0a\u8981 \u7a74\u3002 \u3000\u3000\u8f9b\u6377\u540c\u65f6\u4e5f\u662f\u6e05\u5578\u4e00\u58f0\uff0c\u4e00\u79cd\u6f5c\u5728\u7684\u5185\u529b\u9661\u7136\u53d1\u51fa\uff0c\u672c\u6765\u7d27\u8d34\u5728\u4e00\u8d77\u7684\u624b\u638c\uff0c\u7a81\u7136 \u8df3\u4e86\u5f00\u53bb\uff0c\u53cc\u65b9\u638c\u95f4\u4fdd\u6301\u7740\u534a\u5c3a\u7684\u8ddd\u79bb\u3002 \u3000\u3000\u4efb\u5353\u5ba3\u53ca\u5c71\u5de6\u53cc\u8c6a\u867d\u5219\u5927\u60ca\uff0c\u4f46\u5c82\u7518\u793a\u5f31\uff0c\u638c\u4e0a\u9f50\u9f50\u52a0\u52b2\uff0c\u53c8\u5411\u4e0a\u903c\u8fdb\u4e86\u4e00\u4e9b\u2014\u2014 \u4f46\u662f\u90a3\u4e48\u4e00\u70b9\uff0c\u4ed6\u4eec\u7684\u638c\u529b\u5c31\u5982\u78b0\u5728\u94c1\u58c1\u4e0a\u4e00\u822c\uff0c\u534a\u5bf8\u4e5f\u96be\u524d\u79fb\u3002 \u3000\u3000\u8fd9\u6837\u53cc\u65b9\u9694\u7a7a\u903c\u7740\u638c\u529b\uff0c\u8f9b\u6377\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u53cc\u638c\u731b\u7136\u5411\u540e\u4e00\u5e26\uff0c\u547c\u7684 \u4e00\u58f0\u5c06\u5bf9\u65b9\u638c\u529b\u5e26\u81f3\u540e\u65b9\uff0c\u4ed6\u5374\u6a2a\u8de8\u4e00\u5927\u6b65\u3002 \u3000\u3000\u5c71\u5de6\u53cc\u8c6a\u53ea\u89c9\u5bf9\u65b9\u6297\u529b\u7a81\u5931\uff0c\u4e0d\u7531\u81ea\u4e3b\u5411\u524d\u4e00\u6251\uff0c\u63a5\u7740\u88ab\u4eba\u5bb6\u4e00\u5e26\uff0c\u638c\u529b\u843d\u7a7a\uff0c\u4e09 \u4eba\u51e0\u4e4e\u540c\u65f6\u731b\u7136\u5411\u524d\u8e0f\u51fa\u4e00\u6b65\u624d\u7a33\u4f4f\u8eab\u5f62\uff0c\u201c\u6251\u201d\u7684\u4e00\u58f0\uff0c\u5c06\u5730\u4e0a\u7684\u9ec4\u571f\u8e22\u8d77\u6f2b\u5929\u7070\u7802\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u786c\u62fc\u7684\u50f5\u5c40\u6253\u5f00\uff0c\u5c71\u5de6\u53cc\u8c6a\u4efb\u5353\u5ba3\u867d\u89c9\u8138\u4e0a\u6709\u70b9\u6302\u4e0d\u4e0b\uff0c\u4f46\u4e5f\u6709\u4e00\u70b9\u8f7b\u677e\u7684 \u611f\u89c9\uff0c\u6562\u60c5\u4ed6\u4eec\u4e5f\u77e5\u9053\u50f5\u6301\u4e0b\u53bb\u843d\u4e0d\u5f97\u4ec0\u4e48\u597d\u7ed3\u679c\u3002 \u3000\u3000\u8499\u9762\u4e2d\u7684\u8f9b\u6377\u5411\u5de6\u9762\u671b\u53bb\uff0c\u8c22\u957f\u537f\u6a2a\u7740\u957f\u5251\uff0c\u6b63\u548c\u90a3\u540e\u6765\u4e4b\u4eba\u6012\u76ee\u5bf9\u89c6\uff0c\u8f9b\u6377\u4f55\u7b49 \u673a\u667a\uff0c\u4e00\u671b\u800c\u77e5\u6b64\u4eba\u5fc5\u662f\u90a3\u4e2a\u70b9\u82cd\u53db\u9006\u9646\u65b9\u4e86\u3002 \u3000\u3000\u518d\u770b\u53f3\u9762\uff0c\u90a3\u957f\u5929\u4e00\u78a7\u767d\u98ce\u53cc\u638c\u5982\u98de\uff0c\u864e\u864e\u6709\u58f0\u5730\u76d8\u65cb\u75be\u8f6c\uff0c\u800c\u5434\u51cc\u98ce\u5374\u4e00\u5251\u5bd2\u5149 \u95ea\u95ea\uff0c\u5251\u5f0f\u7ef5\u7ef5\u4e0d\u7edd\uff0c\u4f3c\u4e4e\u5728\u9010\u6e10\u7f29\u5c0f\u5708\u5b50\uff0c\u8f9b\u6377\u867d\u77e5\u767d\u98ce\u529f\u529b\u5728\u5434\u51cc\u98ce\u4e4b\u4e0a\uff0c\u4f46\u5728\u5434 \u51cc\u98ce\u90a3\u201c\u4e03\u5341\u4e8c\u62db\u65ad\u9b42\u5251\u201d\u672a\u65bd\u5b8c\u4e4b\u524d\uff0c\u4ed6\u4e5f\u5fc5\u7136\u4e0d\u80fd\u62a2\u5f97\u653b\u52bf\uff0c\u662f\u4ee5\u4ed6\u653e\u5fc3\u5730\u56de\u9996\u518d \u770b\u90a3\u8c22\u957f\u537f\u2014\u2014 \u3000\u3000\u8fd9\u65f6\u8c22\u957f\u537f\u5df2\u5f00\u59cb\u5bf9\u53db\u95e8\u7684\u5e08\u5f1f\u52a8\u4e0a\u4e86\u624b\uff0c\u53ea\u542c\u4ed6\u65ad\u559d\u4e00\u58f0\uff1a\u201c\u53db\u9006\u8fd8\u4e0d\u4e0e\u6211\u675f\u624b \u5c31\u64d2\uff01\u201d \u3000\u3000\u5251\u5c16\u6296\u51fa\u4e00\u7247\u661f\u5149\u76f4\u523a\u9646\u65b9\u5de6\u80a9\uff0c\u9646\u65b9\u6c89\u7740\u4e00\u5f20\u8138\uff0c\u51b7\u54fc\u4e00\u58f0\uff0c\u5411\u53f3\u8de8\u51fa\u534a\u6b65\uff0c\u5de6 \u624b\u4e00\u7ffb\uff0c\u80cc\u4e0a\u957f\u5251\u5df2\u5230\u4e86\u624b\u4e0a\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u4e24\u5251\u76f8\u4ea4\uff0c\u5404\u81ea\u8361\u4e86\u5f00\u53bb\uff0c\u9646\u65b9\u5374\u501f\u52bf \u518d\u5411\u53f3\u8de8\u51fa\u4e00\u6b65\u3002 \u3000\u3000\u9646\u65b9\u5de6\u624b\u6301\u5251\uff0c\u51b7\u58f0\u9053\uff1a\u201c\u8c22\u957f\u537f\uff0c\u4f60\u6211\u5e08\u5144\u5f1f\u4e4b\u8c0a\u5df2\u7edd\uff0c\u6b64\u540e\u6211\u9646\u67d0\u6240\u884c\u6240\u4e3a\u4e0d \u70e6\u5e08\u5144\u8d39\u5fc3\u2014\u2014\u201d \u3000\u3000\u201d \u3000\u3000\u867d\u7136\u4ed6\u8bf4\u5e08\u5144\u5f1f\u60c5\u8c0a\u5df2\u65ad\uff0c\u4f46\u51e0\u5341\u5e74\u7684\u558a\u60ef\u7684\u79f0\u547c\uff0c\u4e00\u65f6\u6539\u4e0d\u8fc7\u6765\uff0c\u662f\u4ee5\u4ed6\u4e0d\u81ea\u4e3b \u4ecd\u558a\u51fa\u201c\u5e08\u5144\u201d\u4e24\u5b57\u3002 \u3000\u3000\u8c22\u957f\u537f\u6309\u5251\u4e0d\u52a8\uff0c\u5de6\u624b\u5374\u7a81\u5411\u8170\u95f4\u4e00\u638f\uff0c\u53d6\u51fa\u4e00\u4e2a\u9f0e\u5f62\u5c0f\u724c\uff0c\u6717\u58f0\u9053\uff1a\u201c\u9646\u65b9\uff0c\u4f60 \u89c1\u4e86\u5e08\u95e8\u2018\u5723\u9f0e\u2019\u8fd8\u4e0d\u8dea\u4e0b\uff1f\u201d \u3000\u3000\u9646\u65b9\u8138\u4e0a\u62bd\u52a8\u4e86\u4e00\u4e0b\uff0c\u4f46\u7acb\u523b\u53c8\u6062\u590d\u4e86\u539f\u6765\u7684\u51b7\u9762\u5b54\uff0c\u4ed6\u9634\u607b\u607b\u5730\u9053\uff1a\u201c\u6211\u65e9\u5c31\u544a \u8bc9\u4f60\uff0c\u9646\u65b9\u6b63\u5f0f\u8131\u79bb\u4e86\u70b9\u82cd\u6d3e\uff0c\u4f60\u2018\u5723\u9f0e\u2019\u53c8\u600e\u5730\uff1f\u201d \u3000\u3000\u8c22\u957f\u537f\u89c1\u4ed6\u7adf\u6562\u8511\u89c6\u5e08\u95e8\u201c\u5723\u9f0e\u201d\uff0c\u4e0d\u7981\u6c14\u5f97\u6d51\u8eab\u53d1\u6296\uff0c\u4ed6\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u4f60\u2026\u2026\u4f60 \u7adf\u6562\u2026\u2026\u201d\u5c31\u53e3\u7ed3\u8bf4\u4e0d\u4e0b\u53bb\u4e86\u3002 \u3000\u3000\u90a3\u6653\u6708\u5bd2\u5fc3\u638c\u4efb\u5353\u5ba3\u4e00\u76f4\u9634\u607b\u607b\u5730\u6ce8\u89c6\u7740\u8c22\u957f\u537f\uff0c\u8fd9\u65f6\u89c1\u4ed6\u88ab\u6c14\u5f97\u53e3\u77aa\u76ee\u5446\uff0c\u5ffd\u7136 \u5355\u624b\u4e00\u6296\uff0c\u4e00\u70b9\u5bd2\u661f\u52bf\u6bd4\u6d41\u661f\u5730\u5954\u5411\u8c22\u957f\u537f\u5de6\u8170\u201c\u5929\u6795\u201d\u6b7b\u7a74\u3002 \u3000\u3000\u8c22\u957f\u537f\u5de6\u624b\u9ad8\u4e3e\u201c\u5723\u9f0e\u201d\uff0c\u4e00\u76f4\u6ca1\u6709\u653e\u4e0b\uff0c\u662f\u4e4b\u8170\u4e0a\u201c\u5929\u6795\u201d\u5b8c\u5168\u66b4\u9732\uff0c\u6b64\u65f6\u4ed6\u6025 \u6012\u4ee5\u4e0b\u8033\u76ee\u5931\u806a\uff0c\u773c\u770b\u90a3\u6697\u5668\u5c31\u8981\u6253\u4e0a\u4e86\u5657\u7684\u4e00\u58f0\uff0c\u90a3\u6697\u5668\u88ab\u6a2a\u91cc\u98de\u6765\u4e00\u7269\u649e\u843d\u5730\u4e0a\u3002 \u3000\u3000\u8c22\u957f\u537f\u9661\u7136\u4e00\u60ca\uff0c\u5411\u5730\u4e0a\u4e00\u770b\uff0c\u90a3\u51fb\u843d\u6697\u5668\u7684\u7adf\u662f\u4e00\u7c92\u7ec6\u7802\uff1b\u4e0d\u6d88\u8bf4\uff0c\u8fd9\u662f\u8f9b\u6377\u6240 \u53d1\u7684\u3002 \u3000\u3000\u90a3\u5434\u51cc\u98ce\u4e0e\u767d\u98ce\u6b64\u523b\u4e5f\u505c\u4e0b\u4e86\u624b\uff0c\u9646\u65b9\u5ffd\u7136\u4e00\u626c\u624b\u52bf\uff0c\u5c71\u5de6\u53cc\u8c6a\u80cc\u5411\u8f9b\u6377\uff0c\u5374\u9f50\u9f50 \u53cd\u624b\u4e00\u638c\u5288\u51fa\uff0c\u4e24\u80a1\u52b2\u75be\u7684\u638c\u98ce\u5408\u800c\u4e3a\u4e00\u5730\u7a81\u88ad\u8f9b\u6377\u2014\u2014 \u3000\u3000\u540c\u65f6\u6653\u6708\u5bd2\u5fc3\u638c\u4efb\u5353\u5ba3\u53cc\u638c\u4e5f\u9661\u7136\u53d1\u96be\uff0c\u631f\u7740\u53cc\u80a1\u5bd2\u98ce\u51fb\u5411\u60ca\u6115\u4e2d\u7684\u8c22\u957f\u537f\u2014\u2014 \u3000\u3000\u9646\u65b9\u5374\u51fa\u4eba\u610f\u6599\u4e4b\u5916\u5730\u53cd\u624b\u4e00\u5251\u523a\u5411\u80cc\u7acb\u7740\u7684\u5434\u51cc\u98ce\uff0c\u201c\u4e03\u79bd\u5c55\u7ffc\u201d\u8fd9\u62db\u53e4\u602a\u7684\u62db \u5f0f\uff0c\u5728\u4ed6\u5de6\u624b\u65bd\u51fa\u4e4b\u4e0b\u76ca\u66f4\u663e\u5f97\u602a\u5f02\uff1b\u5434\u51cc\u98ce\u6b63\u5f85\u53cd\u624b\u5e94\u654c\uff0c\u80cc\u4e0a\u53c8\u611f\u53d7\u88ad\uff0c\u6562\u60c5\u957f\u5929 \u4e00\u78a7\u767d\u98ce\u4e5f\u4e58\u673a\u52a8\u624b\u2014\u2014 \u3000\u3000\u8fd9\u4e00\u4e0b\u4e94\u4eba\u6709\u5982\u4e8b\u5148\u9884\u5907\u597d\u7684\u4e00\u822c\uff0c\u5404\u5404\u51fa\u4eba\u610f\u6599\u5730\u5077\u88ad\u51fa\u624b\uff0c\u5b9e\u5728\u592a\u5df2\u9634\u6bd2\uff0c\u5434\u3001 \u8f9b\u3001\u8c22\u4e09\u4eba\u7acb\u523b\u9677\u5165\u5371\u5883\u2014\u2014 \u3000\u3000\u9996\u5148\u8f9b\u6377\u53d1\u89c9\u5c71\u5de6\u53cc\u8c6a\u52a8\u624b\u65f6\uff0c\u654c\u4eba\u638c\u98ce\u5df2\u662f\u88ad\u80f8\uff0c\u4ed6\u77e5\u4e24\u4eba\u529f\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u53cc\u8db3 \u9661\u7136\u51b2\u8d77\uff0c\u4e00\u9762\u5355\u638c\u501f\u52bf\u53d1\u52b2\uff0c\u582a\u582a\u907f\u8fc7\u9669\u62db\uff0c\u540c\u65f6\u201c\u5361\u201d\u7684\u4e00\u58f0\uff0c\u5728\u4ed6\u843d\u5730\u4e4b\u524d\uff0c\u4e00 \u67c4\u957f\u5251\u5df2\u5230\u4e86\u624b\u4e0a\u3002 \u3000\u3000\u8c22\u957f\u537f\u867d\u4ece\u60ca\u6115\u4e2d\u5306\u4fc3\u53d1\u62db\uff0c\u4f46\u4ed6\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u4e00\u5251\u659c\u659c\u523a\u51fa\uff0c\u76f4\u53d6\u4efb\u5353 \u201c\u80a9\u80db\u201d\u7a74\u3002\u8fd9\u4e00\u5251\u6839\u672c\u4e0d\u6210\u62db\u5f0f\uff0c\u4f46\u5728\u6b64\u65f6\u5374\u662f\u552f\u4e00\u7684\u5999\u62db\uff1a\u201c\u653b\u201d\u6b63\u662f\u6700\u597d\u7684 \u201c\u5b88\u201d\u3002 \u3000\u3000\u4f46\u4ed6\u773c\u89d2\u5374\u77a5\u5230\u5434\u51cc\u98ce\u7684\u5371\u666f\u2014\u2014 \u3000\u3000\u9646\u65b9\u7684\u201c\u4e03\u79bd\u5c55\u7ffc\u201d\u5728\u5de6\u624b\u5251\u5f0f\u65bd\u51fa\u6765\uff0c\u7aef\u7684\u53e4\u602a\u5f97\u5f88\uff0c\u5434\u51cc\u98ce\u4e34\u654c\u7ecf\u9a8c\u4e0d\u591f\uff0c\u4e00 \u65f6\u7adf\u7a92\u4e86\u4e00\u4e0b\u2014\u2014 \u3000\u3000\u8fd9\u8fd8\u4e0d\u5927\u7d27\uff0c\u66f4\u7cdf\u7684\u662f\u957f\u5929\u4e00\u78a7\u767d\u98ce\u7684\u638c\u5df2\u5230\u4e86\u4ed6\u7684\u8eab\u540e\u3002 \u3000\u3000\u9646\u65b9\u7684\u201c\u4e03\u79bd\u5c55\u7ffc\u201d\u867d\u7136\u602a\u5f02\uff0c\u4f46\u4e5f\u8981\u770b\u5bf9\u624b\u662f\u4f55\u4eba\uff0c\u82e5\u662f\u6362\u4e86\u8f9b\u6377\uff0c\u7eb5\u4ed6\u4e34\u654c\u7ecf \u9a8c\u4e0d\u8db3\uff0c\u201c\u8679\u679d\u5251\u5f0f\u201d\u5fc5\u7136\u4f1a\u8eab\u4e0d\u7531\u5df1\u7684\u65bd\u51fa\uff0c\u4e0d\u4ec5\u201c\u4e03\u79bd\u5c55\u7ffc\u201d\u8fce\u5203\u800c\u7834\uff0c\u9646\u65b9\u5fc5\u7136 \u906d\u5230\u81f4\u547d\u4e4b\u53cd\u51fb\u3002 \u3000\u3000\u800c\u5434\u51cc\u98ce\u6b64\u65f6\u6700\u5927\u7684\u5371\u673a\u662f\u5728\u80cc\u540e\u767d\u98ce\u7684\u638c\u88ad\uff0c\u8f9b\u6377\u867d\u7136\u751a\u4e3a\u8f7b\u677e\u5730\u8eb2\u8fc7\u5c71\u5de6\u53cc\u8c6a \u4e4b\u51fb\uff0c\u5374\u4e00\u65f6\u6ca1\u6709\u53d1\u89c9\u5434\u51cc\u98ce\u7684\u5371\u5c40\uff0c\u7b49\u4ed6\u53d1\u89c9\u65f6\uff0c\u53ea\u89c1\u8c22\u957f\u537f\u4e00\u58f0\u95f7\u54fc\u7adf\u76d8\u65cb\u6251\u53bb\u2014 \u2014 \u3000\u3000\u8c22\u957f\u537f\u89c1\u5230\u5434\u51cc\u98ce\u7684\u5371\u5883\uff0c\u4e0d\u77e5\u600e\u7684\u5ffd\u7136\u70ed\u8840\u4e0a\u51b2\uff0c\u4f7f\u4ed6\u5fd8\u5374\u4e00\u5207\uff0c\u4ed6\u628a\u5df2\u70b9\u51fa\u7684 \u4e00\u5251\u786c\u786c\u6536\u56de\uff0c\u4e0d\u987e\u4efb\u5353\u5ba3\u7684\u638c\u529b\uff0c\u731b\u7136\u8dc3\u8d77\uff0c\u4e5f\u662f\u4e00\u62db\u201c\u4e03\u79bd\u5c55\u7ffc\u201d"\u6251\u95ee\u767d\u98ce\uff0c\u53ea \u542c\u5f97\u5657\u7684\u4e00\u58f0\uff0c\u4efb\u5353\u5ba3\u7684\u638c\u5df1\u6253\u4e2d\u4ed6\u7684\u5de6\u817f\uff0c\u4f46\u4ed6\u8fd8\u662f\u7eb5\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4efb\u5353\u5ba3\u7684\u638c\u529b\u5728\u8c22\u957f\u537f\u4e00\u5251\u523a\u51fa\u4e4b\u65f6\uff0c\u5df2\u81ea\u6536\u56de\u4e94\u6210\uff0c\u662f\u4ee5\u867d\u7136\u6253\u5b9e\uff0c\u5374\u53d7\u4f24\u4e0d\u91cd\u3002 \u3000\u3000\u201c\u4e03\u79bd\u5c55\u7ffc\u201d\u5230\u4e86\u70b9\u82cd\u638c\u95e8\u4eba\u7684\u624b\u4e0a\uff0c\u5a01\u52bf\u53c8\u81ea\u4e0d\u540c\uff0c\u53ea\u89c1\u56db\u65b9\u516b\u9762\u90fd\u662f\u5251\u5f71\u5288\u4e0b\uff0c \u771f\u5982\u4e03\u79bd\u540c\u5c55\u5341\u56db\u53ea\u7fc5\u8180\u4e00\u822c\u3002 \u3000\u3000\u767d\u98ce\u53ea\u597d\u731b\u7136\u6536\u638c\uff0c\u5012\u4e09\u6b65\u800c\u907f\u5f00\u8c22\u957f\u537f\u4e00\u51fb\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u8c22\u957f\u537f\u8d8a\u8fc7\u4ed6\u7684\u5934\u9876\uff0c \u843d\u5728\u5730\u4e0a\uff0c\u843d\u5730\u8eab\u5f62\u8dc4\u8e09\uff0c\u663e\u7136\u662f\u5de6\u817f\u53d7\u4f24\u4e4b\u6545\u3002 \u3000\u3000\u540c\u8fdb\u53ee\u53ee\u4e00\u9635\u4e71\u54cd\uff0c\u6562\u60c5\u5434\u51cc\u98ce\u5371\u6025\u4e2d\u65bd\u51fa\u201c\u65ad\u9b42\u5251\u201d\u4e2d\u7684\u201c\u65e0\u5e38\u64a4\u7eb2\u201d\uff0c\u5c01\u4f4f\u4e86 \u9646\u65b9\u7684\u201c\u4e03\u79bd\u5c55\u7ffc\u201d\u3002 \u3000\u3000\u5173\u4e2d\u4e5d\u8c6a\u4e2d\u4e94\u8c6a\u9f50\u65bd\u6697\u7b97\uff0c\u4f46\u5374\u90fd\u843d\u4e86\u7a7a\u2014\u2014\u9664\u4e86\u8c22\u957f\u537f\u817f\u4e0a\u6328\u4e86\u4e00\u4e0b\u5b50\u3002 \u3000\u3000\u5434\u51cc\u98ce\u867d\u7136\u77e5\u9053\u6740\u7236\u4e4b\u4ec7\u5e76\u975e\u8c22\u957f\u537f\uff0c\u4f46\u65e0\u8bba\u5982\u4f55\u4e0d\u613f\u548c\u8fd9\u4ec7\u4eba\u4e4b\u5b50\u5e76\u80a9\u4f5c\u6218\u7684\uff0c \u8f9b\u6377\u4e5f\u6709\u7740\u5dee\u4e0d\u591a\u7684\u610f\u8bc6\uff0c\u4f46\u662f\u76ee\u4e0b\u7684\u5f62\u52bf\uff0c\u81ea\u7136\u5730\u628a\u4e09\u4eba\u62c9\u5728\u4e00\u6761\u7ebf\u4e0a\u3002 \u3000\u3000\u4e16\u4e0a\u7684\u4e8b\u603b\u662f\u76f8\u5bf9\u7684\uff0c\u5434\u51cc\u98ce\u8fd9\u6837\u60f3\u5f53\u7136\u662f\u4f9d\u4ed6\u7684\u89c2\u70b9\uff0c\u4e8b\u5b9e\u4e0a\u8c22\u957f\u537f\u4e4b\u7236\u8c22\u661f\u5f53 \u5e74\u867d\u66fe\u53c2\u52a0\u56f4\u88ad\u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\uff0c\u4f46\u5374\u6b7b\u5728\u5434\u8bcf\u4e91\u7684\u624b\u4e2d\uff0c\u5982\u679c\u4ece\u8c22\u957f\u537f\u7684\u89c2\u70b9\u770b\uff0c\u5434 \u51cc\u98ce\u5c82\u4e0d\u4e5f\u6210\u4e86\u4ed6\u7684\u201c\u6740\u7236\u5927\u4ec7\u201d\u4e4b\u5b50\uff1f\u5f53\u7136\uff0c\u6b64\u523b\u4ed6\u5e76\u4e0d\u77e5\u9053\u5434\u51cc\u98ce\u7684\u59d3\u540d\u3002 \u3000\u3000\u201c\u5636\u201d\u7684\u4e00\u58f0\uff0c\u5434\u51cc\u98ce\u65ad\u9b42\u5251\u631f\u7740\u4e00\u7f15\u5bd2\u98ce\u5411\u5bf9\u9762\u7684\u957f\u5929\u4e00\u78a7\u767d\u98ce\u5f53\u80f8\u5212\u5230\u3002 \u3000\u3000\u540c\u65f6\u4e00\u58f0\u66f4\u5c16\u9510\u523a\u8033\u7684\u5636\u58f0\u53d1\u81ea\u8f9b\u6377\uff0c\u8f9b\u6377\u96c4\u539a\u7684\u5185\u529b\u4ece\u5251\u5c16\u4e0a\u903c\u51fa\uff0c\u79bb\u5c71\u5de6\u53cc\u8c6a \u5c1a\u6709\u4e94\u5c3a\uff0c\u5df2\u4ee4\u53cc\u8c6a\u611f\u5230\u52b2\u529b\u6251\u9762\uff1b\u53f8\u7a7a\u5b97\u548c\u6797\u5c11\u768b\u7684\u957f\u886b\u5439\u5f97\u76f4\u5411\u540e\u98d8\u3002 \u3000\u3000\u7eb5\u7136\u5f53\u524d\u90fd\u79f0\u5f97\u4e0a\u4e00\u6d41\u7684\u597d\u624b\uff0c\u4f46\u662f\u8f9b\u6377\u8fd9\u79cd\u201c\u5251\u6c14\u201d\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\uff0c\u53f8\u7a7a\u5b97\u548c \u6797\u5c11\u768b\u4e0d\u7981\u6697\u4e2d\u751f\u4e86\u602f\u610f\u3002 \u3000\u3000\u8c22\u957f\u537f\u4e5f\u7acb\u65f6\u914d\u5408\u884c\u52a8\uff0c\u5237\u5730\u4e00\u62db\u201c\u6a2a\u98de\u6e21\u6c5f\u201d\u523a\u51fa\uff0c\u6240\u53d6\u7684\u90e8\u4f4d\u5374\u662f\u767d\u98ce\u540e\u9762\u4e09 \u5bf8\u5904\u3002 \u3000\u3000\u867d\u7136\u4e09\u4eba\u5206\u522b\u52a8\u624b\uff0c\u90a3\u65f6\u95f4\u5374\u5feb\u5f97\u50cf\u662f\u540c\u65f6\u53d1\u62db\u4e00\u822c\uff0c\u767d\u98ce\u89c1\u5434\u51cc\u98ce\u5251\u5f0f\u98d8\u5ffd\u4e0d\u5b9a\uff0c \u4e0d\u6613\u5c01\u67b6\uff0c\u6b63\u5f85\u95ea\u8eab\u9000\u540e\uff0c\u8c22\u957f\u537f\u7684\u5251\u5b50\u6b63\u597d\u9012\u4e0a\uff0c\u4ed6\u5ffd\u7136\u89c9\u5f97\u80cc\u540e\u5bd2\u98ce\u8986\u4f53\uff0c\u51ed\u7ecf\u9a8c \u77e5\u9053\u654c\u4eba\u5251\u5c16\u79bb\u81ea\u5df1\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u5bf8\uff0c\u53ea\u8981\u81ea\u5df1\u7565\u52a8\uff0c\u7acb\u523b\u7b49\u4e8e\u81ea\u52a8\u51d1\u4e0a\u53bb\u53d7\u6233\u4e00\u822c\uff0c\u6025 \u5fd9\u4e2d\u53ea\u597d\u9661\u7136\u53d1\u51fa\u638c\u529b\uff0c\u4ee5\u653b\u4ee3\u5b88\u3002 \u3000\u3000\u5434\u51cc\u98ce\u8eab\u5b50\u7565\u4fa7\uff0c\u907f\u5f00\u4ed6\u7684\u638c\u52bf\uff0c\u624b\u4e2d\u5251\u4f9d\u7136\u659c\u5212\u4e0b\u53bb\uff0c\u64e6\u7684\u4e00\u58f0\uff0c\u4e24\u4eba\u5404\u5411\u5de6\u53f3 \u8dc3\u5f00\uff0c\u767d\u98ce\u7684\u957f\u8896\u5df2\u88ab\u524a\u53bb\u534a\u53ea\u3002 \u3000\u3000\u8c22\u957f\u537f\u7565\u4e00\u6325\u5251\uff0c\u4e00\u8bb0\u6781\u5e73\u5e38\u7684\u62db\u5f0f\u5c31\u903c\u5f97\u957f\u5929\u4e00\u78a7\u767d\u98ce\u72fc\u72c8\u4e0d\u582a\uff0c\u8fd9\u5c31\u662f\u7ecf\u9a8c\u53ef \u8d35\u3002 \u3000\u3000\u4e00\u58f0\u6012\u543c\uff0c\u6653\u6708\u5bd2\u5fc3\u638c\u4efb\u5353\u5ba3\u548c\u5343\u624b\u5251\u5ba2\u9646\u65b9\u6251\u4e86\u4e0a\u6765\uff0c\u5c24\u5176\u662f\u9646\u65b9\uff0c\u53cc\u773c\u4e2d\u5c04\u51fa \u72f0\u72de\u7684\u6740\u6c14\uff0c\u663e\u7136\u4ed6\u60f3\u85c9\u7740\u8fd9\u4ee5\u4f17\u51cc\u5be1\u7684\u673a\u4f1a\u9664\u53bb\u8fd9\u4e2a\u5fc3\u8179\u5927\u60a3\u7684\u5e08\u5144\u3002 \u3000\u3000\u5176\u4ed6\u56db\u8c6a\u53c8\u4f55\u5f53\u4e0d\u5982\u6b64\u6253\u7b97\uff1f\u53ea\u542c\u5f97\u4e00\u7247\u66b4\u543c\u58f0\u4e2d\u4e94\u4eba\u5404\u65bd\u7edd\u6280\u5408\u624b\u653b\u4e86\u4e0a\u6765\uff0c\u5c71 \u5de6\u53cc\u8c6a\u66f4\u53d6\u51fa\u4e86\u5175\u5203\u3002 \u3000\u3000\u8bd5\u60f3\u6d77\u5929\u53cc\u715e\u81ea\u539f\u6765\u5173\u4e2d\u4e5d\u8c6a\u6563\u4f19\u4e4b\u540e\uff0c\u86f0\u4f0f\u5341\u591a\u5e74\uff0c\u4e00\u65e6\u4e1c\u5c71\u518d\u8d77\uff0c\u5176\u6536\u7f57\u7684\u4eba \u9009\u5fc5\u662f\u4e00\u7b49\u7684\u597d\u624b\uff0c\u800c\u8fd9\u4e94\u4e2a\u65b0\u8840\u805a\u4e8e\u4e00\u8d77\uff0c\u5408\u529b\u65bd\u4e3a\uff0c\u90a3\u5a01\u529b\u662f\u53ef\u60f3\u800c\u77e5\u4e86\u3002 \u3000\u3000\u4e94\u4eba\u5fc3\u4e2d\u4e5f\u90fd\u662f\u8fd9\u79cd\u60f3\u6cd5\uff0c\u90a3\u51cc\u5389\u7684\u653b\u52bf\u4ece\u4e94\u4e2a\u529f\u529b\u6df1\u539a\u7684\u624b\u4e2d\u53d1\u51fa\uff0c\u5a01\u529b\u771f\u5927\u5f97 \u60ca\u4eba\uff0c\u5c24\u5176\u6653\u6708\u5bd2\u5fc3\u638c\u53ca\u957f\u5929\u4e00\u78a7\u4e24\u4eba\uff0c\u867d\u662f\u4ee5\u8089\u638c\u653b\u654c\uff0c\u5a01\u52bf\u5374\u5c24\u5176\u4ee4\u4eba\u96be\u9632\u3002 \u3000\u3000\u8c22\u957f\u537f\u89c1\u5173\u4e2d\u4e94\u8c6a\u8fd9\u7b49\u58f0\u52bf\uff0c\u6697\u60ca\u9053\uff1a\u201c\u5c31\u662f\u5f53\u5e74\u56db\u5927\u6d3e\u638c\u95e8\u4eba\u8054\u624b\u65f6\uff0c\u5a01\u529b\u4f3c\u4e4e \u90fd\u4e0d\u8fc7\u5982\u6b64\u5462\uff0c\u4eca\u65e5\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u5434\u51cc\u98ce\u8fd8\u662f\u7b2c\u4e00\u6b21\u906d\u5230\u8fd9\u7b49\u5927\u573a\u9762\uff0c\u66f4\u662f\u7d27\u5f20\u5f97\u624b\u5fc3\u6c81\u6c57\u3002 \u3000\u3000\u7136\u800c\u8fd9\u4e03\u4e2a\u4eba\u90fd\u6599\u9519\u4e86\u4e00\u70b9\uff0c\u4ed6\u4eec\u4ecd\u6ca1\u6709\u6478\u6e05\u8f9b\u6377\u6b64\u65f6\u7684\u529f\u529b\u2014\u2014 \u3000\u3000\u53ea\u542c\u5f97\u4ed6\u90a3\u5636\u5636\u5251\u6c14\uff0c\u6597\u76db\u5c16\u9510\u7684\u54cd\u58f0\u5728\u6c79\u6d8c\u7684\u91d1\u98ce\u62f3\u6d6a\u4e2d\u9ad8\u9ad8\u5347\u8d77\uff0c\u8f9b\u6377\u8499\u5dfe\u4e0a \u7684\u53cc\u76ee\u4e2d\u5c04\u51fa\u4ee4\u4eba\u6218\u6817\u7684\u5149\u8292\uff0c\u201c\u5927\u884d\u5341\u5f0f\u201d\u7684\u7edd\u62db\u5df2\u7136\u4f7f\u51fa\u2014\u2014 \u3000\u3000\u7531\u4e8e\u5bf9\u624b\u591a\u4e0d\u662f\u4e94\u5927\u5251\u6d3e\u7684\u95e8\u4eba\uff0c\u6885\u5c71\u6c11\u201c\u866c\u679d\u5251\u6cd5\u201d\u867d\u7136\u795e\u5999\uff0c\u4f46\u4f3c\u4e4e\u5931\u53bb\u4e86 \u201c\u6b63\u597d\u76f8\u514b\u201d\u7684\u7279\u6027\uff0c\u662f\u4ee5\u8f9b\u6377\u7d22\u6027\u4f7f\u51fa\u201c\u5927\u884d\u5341\u5f0f\u201d\u3002 \u3000\u3000\u8fd9\u5251\u5f0f\u5f53\u65e5\u5e73\u51e1\u5927\u5e08\u66fe\u5938\u53e3\u201c\u5929\u4e0b\u65e0\u53cc\u201d\uff0c\u8f9b\u6377\u6bcf\u4f7f\u4e00\u6b21\uff0c\u603b\u80fd\u591a\u53d1\u73b0\u5176\u4e2d\u4e00\u4e9b\u5999 \u5904\uff0c\u800c\u5176\u5a01\u529b\u65e0\u5f62\u4e2d\u4e5f\u589e\u52a0\u4e86\u4e00\u4e9b\u3002\u8fd9\u65f6\u5636\u5636\u5251\u6c14\u4e2d\u4ed6\u957f\u5251\u534a\u5288\u534a\u6307\uff0c\u4e00\u6cfb\u800c\u4e0b\uff0c\u6b63\u662f \u201c\u98de\u9601\u6d41\u821f\u201d\u4e00\u5f0f\uff0c\u53ea\u89c1\u4ed6\u5251\u5149\u98d8\u5ffd\uff0c\u5b9b\u5982\u5929\u9a6c\u884c\u7a7a\uff0c\u65e0\u6240\u4e0d\u81f3\uff0c\u663e\u7136\u5a01\u529b\u6bd4\u529b\u6597\u6d77\u5929 \u53cc\u715e\u65f6\u53c8\u589e\u8fdb\u4e86\u4e00\u5c42\u3002 \u3000\u3000\u5bf9\u9762\u7684\u6b63\u662f\u5c71\u5de6\u53cc\u8c6a\uff0c\u53f8\u7a7a\u5b97\u6325\u7740\u72ec\u95e8\u5175\u5668\u4e94\u884c\u8f6e\u4e00\u62db\u201c\u9738\u738b\u6297\u9f0e\u201d\u5411\u5de6\u5c01\u51fa\uff0c\u800c \u6797\u5c11\u768b\u7684\u4e00\u5251\u4e00\u9524\u5374\u53cc\u53cc\u5411\u53f3\u5c01\u51fa\uff0c\u5251\u9524\u8f6e\u5728\u864e\u864e\u98ce\u58f0\u4e2d\u6784\u6210\u4e00\u94dc\u5899\u94c1\u58c1\uff0c\u7aef\u7684\u6beb\u65e0\u7834 \u7efd\u3002 \u3000\u3000\u8f9b\u6377\u957f\u5251\u7eed\u523a\uff0c\u52bf\u5fc5\u78b0\u4e0a\u4e09\u79cd\u5175\u5668\uff0c\u4f46\u4e8b\u5b9e\u5927\u51fa\u610f\u6599\uff0c\u201c\u6ecb\u201d\u7684\u4e00\u58f0\uff0c\u8f9b\u6377\u7684\u5251\u5c16 \u7adf\u900f\u4e86\u8fdb\u53bb\uff0c\u76f4\u53d6\u6797\u5c11\u768b\u7684\u54bd\u5589\uff0c\u800c\u59cb\u7ec8\u5374\u6ca1\u6709\u5175\u5668\u76f8\u63a5\u7684\u58f0\u97f3\u3002 \u3000\u3000\u6797\u5c11\u768b\u76f4\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u4ef0\u5929\u540e\u5012\uff0c\u4e00\u4e2a\u201c\u94c1\u677f\u6865\u201d\u529f\u592b\u65bd\u51fa\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u8f9b\u6377\u5251 \u53c8\u6536\u56de\uff0c\u4f46\u5df2\u5728\u6797\u5c11\u768b\u9888\u4e0a\u7559\u4e0b\u4e00\u9053\u8840\u75d5\u3002 \u3000\u3000\u6797\u5c11\u768b\u5230\u7eb5\u5f00\u4e4b\u540e\u624d\u611f\u89c9\u5230\u75bc\u75db\uff0c\u4e00\u7f15\u9c9c\u8840\u4ece\u5589\u4e0a\u6ef4\u4e86\u4e0b\u6765\uff0c\u5982\u679c\u518d\u6df1\u4e00\u5206\u7684\u8bdd\u2026\u2026 \u3000\u3000\u5c71\u5de6\u53cc\u8c6a\u88ab\u6253\u5f97\u8ff7\u8ff7\u7cca\u7cca\uff0c\u4e0d\u670d\u5730\u76f8\u89c6\u4e00\u773c\uff0c\u9f50\u9f50\u6325\u52a8\u5175\u5203\u3002\u4e3b\u52a8\u653b\u4e0a\uff0c\u8f9b\u6377\u51b7\u54fc \u4e86\u4e00\u58f0\uff0c\u5251\u5f0f\u5012\u8f6c\uff0c\u5e73\u7f13\u523a\u51fa\u53bb\uff0c\u6301\u5251\u7684\u624b\u7a33\u5982\u6cf0\u5c71\uff0c\u4f46\u5251\u5c16\u5374\u5728\u52b2\u98ce\u4e2d\u95ea\u95ea\u5730\u4e0d\u4f4f\u8df3 \u52a8\uff0c\u6b63\u662f\u5927\u884d\u5251\u5f0f\u4e2d\u7684\u201c\u95f2\u4e91\u6f6d\u5f71\u201d\u2014\u2014 \u3000\u3000\u53e6\u4e00\u8fb9\uff0c\u8c22\u5434\u4e8c\u4eba\u53cc\u6218\u9646\u65b9\u53ca\u4efb\u3001\u767d\u4e09\u4eba\uff0c\u60c5\u5f62\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5728\u4efb\u767d\u4e8c\u4eba\u96c4\u539f\u7684\u638c\u529b\u4e2d\uff0c\u65ad\u9b42\u5251\u65bd\u4e0d\u51fa\u5a01\u529b\uff0c\u800c\u9646\u65b9\u7684\u4e00\u8def\u5de6\u624b\u5251\u6cd5\u66f4\u89c9 \u9b3c\u5947\u6bd2\u8fa3\uff0c\u6240\u5e78\u8c22\u957f\u537f\u8bc6\u5f97\u4ed6\u5251\u6cd5\u8981\u8bc0\uff0c\u5c55\u5f00\u70b9\u82cd\u5251\u6cd5\u82e6\u82e6\u652f\u6491\u3002 \u3000\u3000\u4efb\u4f55\u7b28\u86cb\u4e5f\u77e5\u9053\u8001\u662f\u6328\u6253\u662f\u6700\u5371\u9669\u7684\u6218\u7565\uff0c\u5434\u51cc\u98ce\u5fc3\u4e00\u6a2a\uff0c\u94a2\u7259\u4e00\u632b\uff0c\u624b\u4e2d\u65ad\u9b42\u5251 \u987a\u7740\u4efb\u5353\u5ba3\u51fb\u6765\u4e00\u638c\u4e4b\u52bf\u731b\u6765\u4e00\u7ffb\uff0c\u4e00\u7ee7\u5bd2\u5149\u7a81\u5982\u5176\u6765\u5730\u5230\u4e86\u767d\u98ce\u989d\u524d\uff0c\u6b63\u662f\uff1a\u201c\u4e03\u5341 \u4e8c\u8def\u65ad\u9b42\u5251\u201d\u4e2d\u6551\u547d\u653b\u5f0f\u201c\u9b3c\u738b\u628a\u706b\u201d\u3002 \u3000\u3000\u4efb\u5353\u5ba3\u638c\u52bf\u7528\u8001\uff0c\u4e00\u65f6\u4e0d\u53ca\u6536\u52bf\uff0c\u767d\u98ce\u88ab\u4ed6\u201c\u9b3c\u738b\u628a\u706b\u201d\u4e00\u8bb0\u602a\u62db\u7a7a\u88ad\u5f97\u4e00\u65f6\u4e0d\u77e5 \u6240\u63aa\uff0c\u9646\u65b9\u89c1\u52bf\u5927\u60ca\uff0c\u5237\u5730\u4e00\u5251\u6a2a\u98de\u8fc7\u6765\u60f3\u523a\u5434\u51cc\u98ce\u201c\u66f2\u6c60\u201d\u3002 \u3000\u3000\u8c22\u957f\u537f\u4f55\u7b49\u7ecf\u9a8c\uff0c\u4e00\u89c1\u5434\u51cc\u98ce\u53d1\u62db\u60c5\u5f62\u7acb\u523b\u77e5\u4ed6\u7528\u610f\uff0c\u6697\u601d\u8fd9\u4e00\u4e0b\u8f6c\u5b88\u4e3a\u653b\u7684\u8f6c\u6298 \u70b9\uff0c\u5c82\u80fd\u8ba9\u9646\u65b9\u5f97\u624b\uff0c\u5237\u5730\u4e00\u6311\uff0c\u8155\u4e0a\u53eb\u8db3\u771f\u529b\uff0c\u7adf\u786c\u5f80\u9646\u65b9\u5251\u4e0a\u8fce\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u53cc\u5251\u76f8\u4ea4\uff0c\u8ff8\u51fa\u4e00\u7f15\u706b\u661f\uff0c\u4f46\u5374\u53d1\u51fa\u201c\u5657\u201d\u7684\u4e00\u58f0\uff0c\u4e0d\u50cf\u662f\u91d1\u5c5e\u76f8\u63a5\uff0c\u6562\u60c5\u53cc\u65b9\u90fd \u662f\u4ee5\u5185\u529b\u8d2f\u6ce8\u5728\u5251\u8eab\u4e0a\u3002 \u3000\u3000\u201c\u5431\u5431\u201d\u4e24\u58f0\u8df3\u9707\u4e4b\u58f0\uff0c\u9646\u65b9\u4ed3\u7136\u9000\u4e86\u4e00\u6b65\uff0c\u8bba\u529f\u529b\uff0c\u4ed6\u8981\u8f93\u5e08\u5144\u4e00\u7b79\u3002 \u3000\u3000\u800c\u540c\u65f6\u90a3\u4e00\u8fb9\uff0c\u957f\u5929\u4e00\u78a7\u767d\u98ce\u867d\u7136\u5012\u7eb5\u907f\u5f00\u5251\u5c16\uff0c\u4f46\u662f\u5434\u51cc\u98ce\u7acb\u523b\u7d27\u63a5\u7740\u65bd\u51fa\u201c\u4e94 \u9b3c\u62a1\u53c9\u201d\uff0c\u96ea\u78a7\u5251\u5149\u4e2d\u4e94\u8def\u653b\u51fa\uff0c\u4e00\u65f6\u8f6c\u5b88\u4e3a\u653b\uff0c\u7ef5\u7ef5\u800c\u4e0a\u3002 \u3000\u3000\u7b49\u5230\u4efb\u5353\u5ba3\u548c\u9646\u65b9\u8d76\u4e0a\u51fb\u51fa\u65f6\uff0c\u867d\u7136\u5434\u8c22\u4e8c\u4eba\u4ecd\u5c45\u52a3\u52bf\uff0c\u4f46\u5df2\u4e0d\u518d\u4e00\u5473\u6328\u6253\u7684\u60c5\u51b5 \u4e86\u3002 \u3000\u3000\u6653\u6708\u5bd2\u5fc3\u638c\u72e0\u72e0\u6a2a\u5288\u4e24\u638c\uff0c\u6253\u7b97\u901f\u6218\u901f\u51b3\uff0c\u800c\u957f\u5929\u4e00\u78a7\u548c\u5343\u624b\u5251\u5ba2\u4e5f\u5b58\u7740\u540c\u6837\u7684\u5fc3 \u601d\uff0c\u4e00\u4e2a\u53cc\u62f3\u76f4\u6363\uff0c\u4e00\u4e2a\u957f\u5251\u5c01\u540e\uff0c\u4e00\u65f6\u62f3\u58f0\u5251\u5f71\u5bc6\u5e03\uff0c\u75be\u52b2\u8feb\u4eba\u2014\u2014 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u5ffd\u7136\u4e00\u58f0\u95f7\u54fc\uff0c\u63a5\u7740\u94ff\u7136\u4e00\u58f0\uff0c\u5939\u7740\u4e00\u58f0\u75db\u82e6\u7684\u4f4e\u53eb\uff0c\u4f7f\u4e09\u4eba\u540c\u8fdb\u53d1\u51fa \u7684\u72e0\u62db\u4e00\u9f50\u4f4f\u4e86\u624b\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u795e\u5251\u91d1\u9524\u6797\u5c11\u768b\u5782\u7740\u53f3\u624b\u91d1\u9524\uff0c\u5de6\u624b\u957f\u5251\u843d\u5728\u5730\u4e0a\uff0c \u80a9\u4e0a\u8863\u886b\u7ffb\u88c2\uff0c\u9690\u9690\u900f\u51fa\u4e00\u9053\u8840\u75d5\uff0c\u90a3\u6458\u661f\u624b\u53f8\u7a7a\u5b97\u624b\u4e0a\u5175\u5203\u91cf\u6ca1\u6709\u51fa\u624b\uff0c\u4f46\u5de6\u895f\u4ece\u9886 \u53e3\u4e0b\u5230\u8896\u5b50\u6839\u672c\u4e0d\u6210\u8863\u886b\uff0c\u88ab\u524a\u6210\u7247\u7247\u788e\u5e03\u3002 \u3000\u3000\u90a3\u201c\u4e03\u5999\u795e\u541b\u201d\u5374\u624b\u6a2a\u957f\u5251\uff0c\u7a33\u7136\u633a\u7acb\uff0c\u6ce8\u89c6\u7740\u5c71\u5de6\u53cc\u8c6a\u3002 \u3000\u3000\u6653\u6708\u5bd2\u5fc3\u638c\u4efb\u5353\u5ba3\u5728\u65b0\u4e5d\u8c6a\u6392\u884c\u8f83\u9ad8\uff0c\u4fe8\u7136\u4ee5\u9996\u9886\u81ea\u5c45\uff0c\u4ed6\u7565\u4e00\u76d8\u7b97\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9 \u2018\u4e03\u5999\u795e\u541b\u2019\u518d\u73b0\u6c5f\u6e56\uff0c\u6b66\u529f\u7aef\u7684\u9ad8\u5f3a\uff0c\u8fd9\u8c22\u957f\u537f\u4e5f\u4e0d\u597d\u6597\uff0c\u55ef\uff0c\u5c31\u662f\u53e6\u5916\u90a3\u5c0f\u5b50\u5251\u6cd5 \u4e5f\u6781\u4e86\u5f97\uff0c\u6797\u8001\u5f1f\u663e\u7136\u53c8\u6302\u4e86\u5f69\uff0c\u518d\u6253\u4e0b\u53bb\u53ea\u6015\u51f6\u591a\u5409\u5c11\u2014\u2014\u201d \u3000\u3000\u4e00\u5ff5\u53ca\u6b64\uff0c\u4ed6\u5411\u540c\u4f34\u559d\u9053\uff1a\u201c\u70b9\u5b50\u722a\u5b50\u786c\uff0c\u5e76\u80a9\u5b50\u626f\u6d3b\uff01\u201d\u540c\u65f6\u5f53\u5148\u65bd\u5f00\u201c\u6653\u6708\u5bd2 \u5fc3\u638c\u201d\uff0c\u5bf9\u7740\u5434\u51cc\u98ce\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5343\u624b\u5251\u5ba2\u9646\u65b9\u4e5f\u770b\u51fa\u5434\u51cc\u98ce\u662f\u8f83\u5f31\u7684\u4e00\u73af\uff0c\u957f\u5251\u6325\u5904\uff0c\u4e5f\u8ddf\u4e86\u4e0a\u53bb\u3002\u767d\u98ce\u548c\u53f8\u7a7a\u5b97 \u62a4\u7740\u6797\u5c11\u768b\u4e5f\u5f80\u5916\u51b2\u3002 \u3000\u3000\u8f9b\u6377\u6a2a\u5251\u51b7\u55e4\u4e00\u58f0\uff0c\u5e76\u4e0d\u963b\u62e6\uff0c\u8c22\u957f\u537f\u89c1\u8f9b\u6377\u4e0d\u52a8\uff0c\u4e5f\u6309\u5251\u4e0d\u52a8\uff0c\u5434\u51cc\u98ce\u95ea\u8eb2\u4efb\u5353 \u5ba3\u7684\u201c\u6653\u6708\u5bd2\u5fc3\u638c\u201d\uff0c\u4e00\u8dc3\u7eb5\u8d77\u6570\u4e08\uff0c\u7b49\u5230\u843d\u5730\u65f6\uff0c\u5173\u4e2d\u4e94\u8c6a\u5df2\u7eb5\u51fa\u8001\u8fdc\u4e86\u3002 \u3000\u3000\u5173\u4e2d\u4e5d\u8c6a\u4e1c\u5c71\u518d\u8d77\uff0c\u4f46\u51e0\u4e2a\u6210\u540d\u9ad8\u624b\u9996\u6b21\u51fa\u624b\u5c31\u5403\u4e86\u4e8f\uff0c\u8c01\u53eb\u4ed6\u4eec\u78b0\u4e0a\u4e86\u201c\u4e03\u5999\u795e \u541b\u201d\u5462\uff1f \u3000\u3000\u4e00\u4e0b\u5b50\u5c31\u9759\u4e86\u4e0b\u6765\uff0c\u8f9b\u6377\u770b\u90a3\u88ab\u5c71\u5de6\u53cc\u8c6a\u6740\u5bb3\u7684\u8f66\u592b\u4ec6\u4eba\u603b\u6709\u5341\u591a\u4e2a\u4e4b\u591a\uff0c\u5c38\u9996\u8eba \u7740\u4e00\u5927\u5806\uff0c\u8840\u6d41\u904d\u5730\uff0c\u60e8\u4e0d\u5fcd\u7779\u3002 \u3000\u3000\u5ffd\u7136\u5434\u51cc\u98ce\u4e00\u58f0\u60ca\u547c\uff0c\u8f9b\u6377\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u843d\u82f1\u5251\u8c22\u957f\u537f\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u6084\u6084\u8d70\u4e86\uff0c \u62ac\u773c\u671b\u65f6\u53ea\u89c1\u8fdc\u5904\u5e73\u829c\u5c3d\u5904\uff0c\u4f9d\u7a00\u53ef\u8fa8\u51fa\u4ed6\u6a21\u7cca\u7684\u80cc\u5f71\u3002 \u3000\u3000\u5434\u51cc\u98ce\u8f7b\u53f9\u4e86\u4e00\u58f0\uff1a\u201c\u6377\u5f1f\uff0c\u8fd9\u8c22\u957f\u537f\u5012\u662f\u4e00\u6761\u6c49\u5b50\uff0c\u53ea\u662f\u2014\u2014 \u3000\u3000\u53ea\u662f\u4ed6\u4e43\u662f\u6697\u7b97\u6885\u53d4\u53d4\u7684\u6b63\u70b9\u513f\uff0c\u54b1\u4eec\u5c82\u2014\u2014\u201d \u3000\u3000\u4ed6\u5b9e\u5728\u4e0d\u597d\u8bf4\u201c\u8c22\u957f\u537f\u4e43\u662f\u4ed6\u6740\u7236\u4ec7\u4eba\u4e4b\u5b50\u201d\uff0c\u4ed6\u4e0d\u81ea\u5bf9\u8c22\u957f\u537f\u5df2\u6709\u4e86\u76f8\u5f53\u7684\u597d\u611f\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u6b63\u81ea\u60f3\u7740\u8fd9\u4f4d\u5e76\u80a9\u4f5c\u6218\u7684\u201c\u4ec7\u4eba\u201d\uff0c\u8f7b\u8f7b\u53f9\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5c71\u98ce\u9001\u6765\u9635\u9635\u60b2\u5207\u7684\u6ce3\u58f0\uff0c\u4f7f\u4ed6\u4eec\u4e24\u4eba\u60f3\u8d77\u8fd8\u6709\u4e00\u4e2a\u672a\u906d\u51f6\u6740\u7684\u5973\u5b50\uff0c\u9f50\u9f50\u8f6c\u8eab\u8d70 \u8fd1\uff0c\u53ea\u89c1\u4e00\u4e2a\u9752\u8863\u5973\u5b50\u4f0f\u5728\u4e00\u5177\u5c38\u8eab\u4e0a\u75db\u54ed\uff0c\u90a3\u5973\u5b50\u770b\u6765\u5e74\u7eaa\u751a\u8f7b\uff0c\u6700\u591a\u4e0d\u8fc7\u5341\u516b\u4e5d\u5c81\uff0c \u4fee\u957f\u7684\u8eab\u8eaf\u5728\u4e0d\u505c\u5730\u8d77\u4f0f\u7740\uff0c\u4ee4\u4eba\u751f\u601c\u3002 \u3000\u3000\u5730\u4e0a\u7684\u5c38\u9996\u662f\u4e00\u4e2a\u8001\u8005\uff0c\u80e1\u5b50\u5df2\u6709\u70b9\u82b1\u767d\uff0c\u80f8\u819b\u4e0a\u88ab\u523a\u4e86\u4e00\u5251\uff0c\u65e9\u5df2\u6b7b\u53bb\uff0c\u770b\u6765\u5230 \u50cf\u662f\u8fd9\u5973\u5b50\u7684\u7236\u4eb2\u3002 \u3000\u3000\u4e24\u4eba\u8d70\u5230\u5973\u5b50\u8eab\u540e\uff0c\u90a3\u5973\u5b50\u72b9\u4e0d\u53d1\u89c9\uff0c\u90a3\u51c4\u51c4\u6ce3\u58f0\u5b9b\u5982\u5deb\u5ce1\u733f\u557c\uff0c\u675c\u9e43\u6ce3\u8840\uff0c\u50ac\u4eba \u65ad\u80a0\u3002 \u3000\u3000\u9694\u4e86\u534a\u5929\uff0c\u8fd8\u662f\u5434\u51cc\u98ce\u8f7b\u5524\u4e86\u4e00\u58f0\uff1a\u201c\u59d1\u5a18\u2014\u2014\u8d3c\u5b50\u4eec\u90fd\u5df2\u7ecf\u8d70\u4e86\u2014\u2014\u201d\u3002 \u3000\u3000\u90a3\u5973\u5b50\u4f3c\u4e4e\u4e00\u60ca\uff0c\u7f13\u7f13\u8f6c\u8fc7\u5934\u6765\uff0c\u8fd9\u4e00\u8f6c\u5934\uff0c\u4ee4\u8f9b\u6377\u53ca\u5434\u51cc\u98ce\u5fc3\u7075\u4e00\u9707\u3002\u6562\u60c5\u8fd9\u5973 \u5b50\u7adf\u662f\u51fa\u5947\u7684\u7f8e\uff0c\u5377\u53d1\u4e91\u9b13\u4e0b\u662f\u4e00\u5f20\u9e45\u86cb\u5f62\u7684\u9762\u988a\uff0c\u7ec6\u7709\u5982\u67f3\uff0c\u9f3b\u82e5\u60ac\u80c6\uff0c\u96ea\u767d\u7684\u76ae\u80a4 \u91cc\u5374\u9690\u9690\u900f\u51fa\u4e00\u4e1d\u7ea2\u6655\uff0c\u5927\u773c\u775b\u91cc\u4e24\u7736\u6cea\u6c34\uff0c\u76ca\u53d1\u663e\u5f97\u695a\u695a\u52a8\u4eba\u3002 \u3000\u3000\u5434\u51cc\u98ce\u6597\u7136\u4e00\u9707\uff0c\u5fc3\u4e2d\u50cf\u662f\u4e00\u5f20\u5e73\u9759\u7684\u5f13\u7a81\u7136\u88ab\u4eba\u62c9\u52a8\u5f26\u7d22\uff0c\u6296\u98a4\u4e0d\u5df2\uff0c\u4ed6\u6697\u9053\uff1a \u201c\u8fd9\u59d1\u5a18\u5b9e\u5728\u592a\u7f8e\uff0c\u53ea\u6709\u7528\u2018\u589e\u4e4b\u4e00\u5206\u5219\u592a\u957f\uff0c\u51cf\u4e4b\u4e00\u5206\u5219\u592a\u77ed\uff0c\u65bd\u7c89\u5219\u592a\u767d\uff0c\u6577\u6731\u5219 \u592a\u8d64\u2019\u6765\u5f62\u5bb9\u624d\u6070\u5f53\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e5f\u89c9\u5f97\u8fd9\u5973\u5b50\u7edd\u8273\u60ca\u4eba\uff0c\u6bd4\u4e4b\u81ea\u5df1\u8ba4\u8bc6\u7684\u65b9\u5c11\u78a7\u3001\u91d1\u6885\u9f84\u72b9\u6709\u8fc7\u4e4b\uff0c\u4f3c\u4e4e\u53ea\u6709 \u90a3\u65e0\u6781\u5c9b\u4e3b\u7684\u638c\u73e0\u83c1\u513f\u624d\u80fd\u548c\u5979\u4e00\u8f83\u957f\u77ed\u3002\u8fd9\u4e00\u4e0b\uff0c\u90a3\u51e0\u4e2a\u7f8e\u4e3d\u7684\u5f71\u5b50\u987f\u65f6\u98d8\u5165\u8111\u6d77\uff0c \u5c11\u78a7\u7684\u5a07\u61a8\uff0c\u6885\u9f84\u7684\u6e29\u67d4\uff0c\u83c1\u513f\u7684\u7edd\u8273\uff0c\u7684\u786e\uff0c\u8fd9\u4e9b\u662f\u591a\u4e48\u503c\u5f97\u56de\u5fc6\u7684\u4e8b\uff0c\u4f46\u662f\u73b0\u5728\uff0c \u8fd9\u4e00\u5207\u90fd\u6210\u4e86\u5e7b\u666f\uff0c\u4ed6\u60f3\u5230\u5c11\u78a7\u53ca\u83c1\u513f\u6bd9\u8eab\u72c2\u6d9b\uff0c\u6885\u9f84\u751f\u6b7b\u4e0d\u660e\uff0c\u4e0d\u7981\u9f3b\u5934\u4e00\u9178\u3002 \u3000\u3000\u90a3\u5c11\u5973\u539f\u6765\u54ed\u5f97\u751a\u4e3a\u60b2\u5207\uff0c\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e24\u4e2a\u7537\u5b50\u7ad9\u5728\u8eab\u540e\uff0c\u987f\u65f6\u6b62\u4f4f\u4e86\u54ed\u58f0\uff0c \u83b9\u83b9\u6cea\u5149\u4f9d\u7a00\u53ef\u89c1\u4e00\u4e2a\u662f\u8499\u7740\u9762\u5b54\u7684\u4eba\uff0c\u53e6\u4e00\u4e2a\u5374\u662f\u4fca\u79c0\u65e0\u6bd4\u7684\u5c11\u5e74\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u5979\u8138 \u4e0a\u5ffd\u7136\u4e00\u9635\u7ea2\u6655\uff0c\u5979\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u4ec0\u4e48\u539f\u56e0\uff0c\u5fc3\u4e2d\u4e00\u9635\u53d1\u614c\uff0c\u90a3\u7f8e\u5c11\u5e74\u53cc\u773c\u4e2d\u5c04\u51fa\u5173\u5207 \u7684\u5149\u8f89\uff0c\u4ee4\u5979\u4e0d\u6562\u6b63\u89c6\uff0c\u7ec8\u4e8e\uff0c\u5979\u4e00\u4f4e\u5934\uff0c\u53c8\u4f4e\u58f0\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8f9b\u6377\u4ece\u5e7b\u666f\u4e2d\u88ab\u60ca\u9192\uff0c\u4ed6\u5237\u5730\u63d2\u597d\u4e86\u5251\uff0c\u89c1\u90a3\u5c11\u5973\u6b63\u4f4e\u5934\u62bd\u6ce3\uff0c\u5434\u51cc\u98ce\u7684\u8138\u4e0a\u5374\u6ee1 \u662f\u7126\u6025\u548c\u5173\u6000\u7684\u6837\u5b50\uff0c\u4ed6\u6014\u4e86\u4e00\u6014\uff0c\u7acb\u523b\u660e\u767d\u4e86\u5434\u51cc\u98ce\u6b64\u65f6\u7684\u5fc3\u5883\u3002 \u3000\u3000\u8f9b\u6377\u8d70\u8fd1\u4e86\u4e24\u6b65\uff0c\u811a\u6b65\u58f0\u4ee4\u90a3\u5c11\u5973\u62ac\u8d77\u4e86\u5934\uff0c\u5979\u770b\u4e86\u770b\u8f9b\u6377\u9762\u4e0a\u8499\u5dfe\u7684\u4e03\u6735\u6885\u82b1\uff0c \u4f3c\u4e4e\u6709\u4e9b\u5bb3\u6015\u5730\u9000\u7f29\u4e86\u4e00\u4e0b\uff0c\u8f9b\u6377\u95ee\u9053\uff1a\u201c\u8bf7\u95ee\u59d1\u5a18\u82b3\u540d\uff1f\u59d1\u5a18\u662f\u600e\u6837\u548c\u8fd9\u6279\u5f3a\u76d7\u9047\u4e0a \u7684\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u505c\u987f\u4e86\u54ed\u58f0\uff0c\u51c4\u51c4\u60e8\u60e8\u7684\u8bf4\u51fa\u5979\u7684\u7ecf\u8fc7\uff0c\u867d\u7136\u662f\u8f9b\u6377\u95ee\u5979\uff0c\u4f46\u5979\u56de\u7b54\u65f6\u5374\u4e00 \u76f4\u770b\u7740\u5434\u51cc\u98ce\uff0c\u4f3c\u4e4e\u6709\u70b9\u5bb3\u6015\u8f9b\u6377\u7684\u6a21\u6837\u3002 \u3000\u3000\u539f\u6765\u8fd9\u5c11\u5973\u59d3\u82cf\uff0c\u82b3\u540d\u8559\u82b7\uff0c\u7236\u4eb2\u82cf\u9e3f\u97ec\u672c\u662f\u671d\u5ef7\u4e00\u4e2a\u540f\u90e8\u4f8d\u90ce\uff0c\u4e2d\u5e74\u4e27\u59bb\uff0c\u4ec5\u5f97 \u4e00\u4e2a\u5973\u513f\uff0c\u89c6\u82e5\u638c\u73e0\uff0c\u82cf\u9e3f\u97ec\u7231\u59bb\u751a\u7b03\uff0c\u4e00\u76f4\u4e0d\u66fe\u7eed\u5f26\uff0c\u7236\u5973\u4e8c\u4eba\u76f8\u4f9d\u4e3a\u547d\u3002\u90a3\u5e74\u5934\u540f \u90e8\u5f85\u90ce\u5b98\u867d\u4e0d\u5c0f\uff0c\u4f46\u82e5\u53ea\u51ed\u4e00\u70b9\u85aa\u4ff8\u5b9e\u5728\u5c11\u5f97\u53ef\u4f36\uff0c\u82cf\u9e3f\u97ec\u662f\u5bd2\u82e6\u51fa\u8eab\uff0c\u4e3e\u76ee\u65e0\u4eb2\u5730\u81ea \u53d1\u81ea\u6124\uff0c\u624d\u51ed\u79d1\u4e3e\u505a\u4e86\u5b98\uff0c\u4ed6\u7980\u6027\u6b63\u76f4\uff0c\u90a3\u91cc\u7701\u5f97\u8d2a\u6c61\u641c\u62ec\u7684\u90a3\u4e00\u5957\uff0c\u662f\u4ee5\u5b98\u867d\u4e0d\u5c0f\uff0c \u5374\u843d\u5f97\u4e24\u8896\u6e05\u98ce\uff0c\u56db\u58c1\u8427\u7136\u3002 \u3000\u3000\u7136\u800c\u5176\u4ed6\u671d\u5ef7\u5927\u5458\u5374\u65e0\u4e00\u4e0d\u8d2a\u6c61\u641c\u62ec\uff0c\u89c6\u8d22\u82e5\u547d\uff0c\u82cf\u9e3f\u97ec\u4e00\u8154\u62a5\u56fd\u96c4\u5fc3\uff0c\u88ab\u78e8\u5f97\u51b0 \u6d88\u74e6\u89e3\uff0c\u4ed6\u7ec8\u4e8e\u770b\u7834\u8fd9\u4e00\u5957\uff0c\u8f9e\u4e86\u5b98\u643a\u5e26\u5973\u513f\u6253\u7b97\u56de\u6e56\u5357\u5bb6\u4e61\uff0c\u4ee5\u5ea6\u665a\u5e74\uff0c\u867d\u7136\u5728\u5bb6\u4e61 \u4e5f\u6ca1\u6709\u4ec0\u4e48\u4eb2\u4eba\uff0c\u4f46\u662f\u201c\u4eba\u4e0d\u4eb2\u571f\u4eb2\u201d\uff0c\u4ed6\u8001\u4eba\u5bb6\u6f02\u6cca\u4e00\u4e16\uff0c\u603b\u60f3\u9aa8\u8089\u5f52\u846c\u6545\u571f\u3002 \u3000\u3000\u5c71\u5de6\u53cc\u8c6a\u5374\u770b\u8d70\u4e86\u773c\uff0c\u53ea\u6253\u542c\u5f97\u82cf\u9e3f\u97ec\u662f\u4e2a\u671d\u5ef7\u5927\u5458\uff0c\u5374\u6ca1\u6599\u5230\u82cf\u9e3f\u7a3b\u662f\u4e2a\u4e24\u8896\u6e05 \u98ce\u7684\u6e05\u5b98\uff0c\u4ed6\u4eec\u89c1\u82cf\u5bb6\u8f66\u8f86\u5f80\u6850\u67cf\u5c71\u8d70\uff0c\u6b63\u597d\u4efb\u5353\u5ba3\u547d\u4ed6\u4eec\u5230\u6850\u67cf\u5c71\u4f1a\u5408\uff0c\u9884\u5907\u56f4\u51fb\u843d \u82f1\u5251\u8c22\u957f\u537f\uff0c\u4e8e\u662f\u4e00\u8def\u8ddf\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u82cf\u9e3f\u97ec\u7684\u8f66\u9a6c\u53ea\u6709\u4e00\u8f86\u5927\u8f66\uff0c\u5b8c\u5168\u4e0d\u662f\u4e00\u4e2a\u5927\u5458\u5f52\u4e61\u7684\u6a21\u6837\uff0c\u4f46\u5c71\u5de6\u53cc\u8c6a\u5374\u6599\u5b9a\u8fd9 \u8f66\u8f86\u6108\u5c11\uff0c\u8db3\u89c1\u8f66\u4e2d\u5fc5\u662f\u73cd\u8d35\u7684\u4e1c\u897f\uff0c\u8fd9\u4e00\u4e0b\u66f4\u4e0b\u4e86\u52a8\u624b\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u53ef\u4f36\u82cf\u9e3f\u97ec\u53ca\u4e00\u5e72\u4ec6\u4eba\u90fd\u906d\u4e86\u6bd2\u624b\uff0c\u53cc\u8c6a\u5374\u8fde\u4e00\u4e2a\u94dc\u94b1\u4e5f\u6ca1\u6709\u641c\u5230\uff0c\u6b63\u5f85\u903c\u95ee\u82cf\u8559 \u82b7\u65f6\uff0c\u5374\u78b0\u4e0a\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u624d\u53cc\u65b9\u52a8\u4e0a\u4e86\u624b\u3002 \u3000\u3000\u4ee5\u540e\u7684\u4e8b\uff0c\u82cf\u8559\u82b7\u4f0f\u5728\u8001\u7236\u5c38\u4e0a\u75db\u54ed\uff0c\u5bf9\u8f9b\u6377\u7b49\u4eba\u7684\u53ae\u6740\u6839\u672c\u4e0d\u95fb\u4e0d\u95ee\uff0c\u662f\u4ee5\u4e0d\u6e05 \u695a\u3002 \u3000\u3000\u82cf\u8559\u82b7\u8bf4\u5230\u8fd9\u91cc\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u53c8\u4f4e\u58f0\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5434\u51cc\u98ce\u548c\u8f9b\u6377\u5bf9\u8fd9\u4e00\u4e2a\u54ed\u5f97\u68a8\u82b1\u5e26\u96e8\u7684\u5927\u59d1\u5a18\uff0c\u5b9e\u5728\u611f\u5230\u675f\u624b\u65e0\u7b56\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5bf9\u8f9b\u6377\u671b\u4e86\u671b\uff0c\u53c8\u5bf9\u82cf\u59d1\u5a18\u671b\u4e86\u671b\uff0c\u6b63\u60f3\u542f\u53e3\uff0c\u8f9b\u6377\u5374\u62a2\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5feb \u529d\u529d\u5979\u5427\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u8138\u4e0a\u4e00\u7ea2\uff0c\u4f46\u4ecd\u4e0a\u524d\u5bf9\u82cf\u8559\u82b7\u9053\uff1a\u201c\u59d1\u5a18\u8bf7\u6682\u8282\u54c0\uff0c\u76ee\u4e0b\u8fd8\u662f\u5148\u5c06\u4ee4\u5c0a\u9057\u4f53 \u5b89\u846c\u91cd\u8981\u3002\u201d \u3000\u3000\u90a3\u82cf\u59d1\u5a18\u679c\u7136\u6b62\u4f4f\u4e86\u54c0\u6ce3\uff0c\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u62bd\u51fa\u957f\u5251\u5728\u5730\u4e0a\u6398\u4e86\u4e00\u4e2a\u6d1e\uff0c\u5c06\u82cf\u9e3f\u7a3b\u7684 \u5c38\u4f53\u57cb\u4e86\u8fdb\u53bb\u3002\u53c8\u53e6\u6316\u4e86\u4e00\u4e2a\u5927\u5751\uff0c\u8f66\u592b\u4ec6\u4eba\u7684\u5c38\u9996\u4e00\u9f50\u57cb\u597d\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5ffd\u7136\u4ece\u6811\u4e0b\u642c\u6765\u4e00\u65b9\u5de8\u77f3\uff0c\u51c6\u5907\u7528\u5251\u5728\u4e0a\u9762\u523b\u51e0\u4e2a\u5b57\uff0c\u8f9b\u6377\u63a5\u8fc7\u5de8\u77f3\uff0c\u4f38\u51fa \u53f3\u6307\uff0c\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u771f\u529b\u8d2f\u6ce8\u6307\u5c16\uff0c\u7565\u4e00\u601d\u7d22\uff0c\u5728\u5de8\u77f3\u523b\u4e0b\u201c\u540f\u90e8\u4f8d\u90ce\u82cf\u516c\u9e3f\u97ec\u4e4b\u5893\u201d \u51e0\u4e2a\u5927\u5b57\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u8fd0\u6307\u5982\u98ce\uff0c\u77f3\u5c51\u7c89\u98de\uff0c\u6240\u523b\u4e4b\u5b57\u4e00\u7b14\u4e0d\u82df\uff0c\u6709\u5982\u5200\u65a7\u6240\u523b\u822c\uff0c\u666e\u5929\u4e4b\u4e0b\u529f\u529b \u6240\u53ca\u6b64\u8005\uff0c\u6050\u6015\u5be5\u5be5\u65e0\u51e0\u54e9\u3002 \u3000\u3000\u8f9b\u6377\u523b\u5b8c\u4e4b\u540e\uff0c\u957f\u5401\u4e00\u53e3\u6c14\uff0c\u8fd9\u5176\u4e2d\u5305\u542b\u7740\u4e00\u4e1d\u81ea\u6170\u7684\u559c\u60a6\u3002 \u3000\u3000\u82cf\u8559\u82b7\u5bf9\u8fd9\u4e00\u5e55\u7edd\u9876\u6b66\u529f\u8868\u6f14\u4e1d\u6beb\u4e0d\u89c9\uff0c\u6ee1\u542b\u7684\u6cea\u773c\u4e0d\u65f6\u5077\u770b\u5434\u51cc\u98ce\u4e00\u4e0b\uff0c\u8138\u4e0a\u4e00 \u79cd\u5947\u602a\u7684\u8868\u60c5\uff0c\u771f\u4e0d\u77e5\u662f\u60b2\u662f\u60ca\u3002 \u3000\u3000\u76f4\u5230\u4e24\u4eba\u628a\u8fd9\u4e00\u5207\u90fd\u5fd9\u5b8c\u4e86\uff0c\u82cf\u59d1\u5a18\u624d\u5411\u4e24\u4eba\u8c22\u9053\uff1a\u201c\u96be\u5973\u627f\u4e24\u4f4d\u6069\u4eba\u6253\u6551\uff0c\u53c8\u627f \u4e3a\u5148\u7236\u6536\u655b\u9ab8\u9aa8\uff0c\u6b64\u6069\u6b64\u5fb7\u6c38\u4e16\u96be\u62a5\uff0c\u8bf7\u5148\u53d7\u6211\u4e00\u62dc\u3002\u201d\u8bf4\u7740\u5c31\u8981\u8dea\u4e0b\u53bb\u3002 \u3000\u3000\u5434\u51cc\u98ce\u4e00\u6025\uff0c\u4f38\u624b\u60f3\u6276\u4f4f\uff0c\u5ffd\u7136\u4e00\u60f3\u4e0d\u59a5\uff0c\u4f38\u51fa\u7684\u624b\u5728\u534a\u7a7a\u4e2d\u505c\u4e86\u4e0b\u6765\uff0c\u5e78\u800c\u8f9b\u6377 \u53cc\u8896\u4e00\u62c2\uff0c\u4e00\u80a1\u65e0\u5f62\u7684\u52b2\u529b\u786c\u5c06\u5979\u6258\u4f4f\u3002 \u3000\u3000\u82cf\u59d1\u5a18\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u53ef\u4f9d\u9760\u7684\u4eb2\u621a\uff0c\u60f3\u4e86\u597d\u534a\u5929\uff0c\u624d\u60f3\u8d77\u7236\u4eb2\u6709\u4e00\u4e2a\u4eb2\u4fe1\u7684\u90e8\u4e0b\u5728 \u6d4e\u5b81\u505a\u77e5\u53bf\uff0c\u53ef\u4ee5\u6295\u9760\uff0c\u8f9b\u5434\u4e24\u4eba\u5546\u91cf\u4e00\u4e0b\uff0c\u51b3\u5b9a\u62a4\u9001\u5979\u5230\u6d4e\u5b81\u3002 \u3000\u3000\u82cf\u8559\u82b7\u611f\u8c22\u4e4b\u4f59\uff0c\u54ea\u91cc\u8fd8\u6709\u522b\u7684\u610f\u89c1\uff0c\u4e8e\u662f\u4e09\u4eba\u4e00\u8d77\u4e0a\u8def\u3002 \u3000\u3000\u5434\u51cc\u98ce\u7b2c\u4e00\u773c\u89c1\u4e86\u82cf\u59d1\u5a18\u5c31\u4ece\u5fc3\u5e95\u4e2d\u9707\u8361\u8d77\u6765\uff0c\u4e00\u8def\u4e0a\u867d\u7136\u8f9b\u6377\u5728\u65c1\uff0c\u4f46\u90a3\u5173\u6ce8\u5475 \u62a4\u4e4b\u60c5\u4ecd\u4e0d\u65f6\u81ea\u7136\u6d41\u9732\uff0c\u82cf\u59d1\u5a18\u65b0\u906d\u5927\u53d8\uff0c\u4e3e\u76ee\u65e0\u4eb2\uff0c\u5728\u7bf7\u8f66\u4e0d\u65f6\u6697\u4e2d\u5f39\u6cea\uff0c\u552f\u6709\u5bf9\u5434 \u51cc\u98ce\u7684\u5173\u6ce8\u95ee\u5019\uff0c\u9664\u4e86\u7531\u8877\u7684\u611f\u8c22\u5916\uff0c\u53e6\u6709\u4e00\u79cd\u4eb2\u5207\u4e4b\u610f\uff01 \u3000\u3000\u4ec6\u4ec6\u98ce\u5c18\uff0c\u4e24\u4eba\u62a4\u7740\u82cf\u5973\u628a\u884c\u7a0b\u987f\u65f6\u51cf\u6162\u4e86\u4e0b\u6765\uff0c\u5230\u6d4e\u5b81\u65f6\uff0c\u7b97\u7b97\u8ddd\u6cf0\u5c71\u5927\u4f1a\u65e5\u5b50 \u4e0d\u8fc7\u4e94\u5929\u4e86\u3002 \u3000\u3000\u8f66\u505c\u5728\u77e5\u53bf\u516c\u9986\u95e8\u53e3\uff0c\u82cf\u59d1\u5a18\u62ff\u7236\u4eb2\u7684\u540d\u5e16\uff0c\u8bf7\u8859\u5f79\u9001\u4e86\u8fdb\u53bb\uff0c\u8f9b\u5434\u4e24\u4eba\u4e0d\u613f\u591a\u803d \u6401\uff0c\u4fbf\u6b32\u8f9e\u522b\u3002\u8fd9\u4e9b\u65e5\u5b50\u4ee5\u6765\uff0c\u82cf\u59d1\u5a18\u5df1\u9690\u7ea6\u77e5\u4e24\u4eba\u90fd\u662f\u6c5f\u6e56\u4e2d\u7684\u4fa0\u58eb\uff0c\u77e5\u9053\u7559\u4e5f\u65e0\u76ca\uff0c \u53ea\u5f97\u542b\u6cea\u9053\u522b\uff0c\u5434\u51cc\u98ce\u5728\u90a3\u83b9\u83b9\u6cea\u5149\u4e2d\uff0c\u53e6\u611f\u5230\u4e00\u756a\u9500\u9b42\u6ecb\u5473\u3002 \u3000\u3000\u201c\u4e24\u4f4d\u529e\u5b8c\u4e8b\u4ee5\u540e\uff0c\u5343\u4e07\u8bf7\u6765\u4e0e\u5c0f\u5973\u5b50\u518d\u89c1\u4e00\u9762\u2026\u2026\u201d\u5979\u8bf4\u5230\u8fd9\u91cc\uff0c\u5df2\u662f\u786c\u54bd\uff0c\u800c \u8859\u95e8\u91cc\u5df1\u4f20\u6765\u4e00\u9635\u55a7\u95f9\uff0c\u6562\u60c5\u77e5\u53bf\u4ee5\u4e3a\u82cf\u5927\u4eba\u4eb2\u5230\uff0c\u8fde\u5fd9\u51fa\u8fce\u3002 \u3000\u3000\u8f9b\u6377\u5411\u82cf\u59d1\u5a18\u9053\u58f0\u73cd\u91cd\uff0c\u4e00\u62c9\u5434\u51cc\u98ce\u624b\uff0c\u559d\u58f0\uff1a\u201c\u8d70\u201d\uff0c\u4e24\u4eba\u5306\u5306\u800c\u53bb\u3002 \u3000\u3000\u4e00\u76f4\u8d70\u51fa\u57ce\u95e8\uff0c\u4e24\u4eba\u4e00\u76f4\u90fd\u6ca1\u6709\u8bb2\u8bdd\uff0c\u8f9b\u6377\u770b\u5434\u51cc\u98ce\u90a3\u5fc3\u4e0d\u5728\u7109\u7684\u6837\u5b50\uff0c\u5ffd\u7136\u6545\u610f \u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u5230\u90a3\u91cc\u53bb\u554a\uff1f\u201d \u3000\u3000\u5434\u51cc\u98ce\u6597\u7136\u60ca\u8d77\uff0c\u4e00\u65f6\u7ed3\u5df4\u4e86\u534a\u5929\u624d\u60f3\u51fa\u6765\u9053\uff1a\u201c\u54b1\u4eec\u5f53\u7136\u662f\u53bb\u2014\u2014\u662f\u2014\u2014\u662f\u53bb\u6cf0 \u5c71\u554a\uff01\u201d\u3002 \u3000\u3000\u8f9b\u6377\u5411\u4ed6\u795e\u79d8\u7684\u4e00\u7b11\uff0c\u5434\u51cc\u98ce\u4fca\u8138\u4e0a\u4e00\u7ea2\uff0c\u4e24\u4eba\u7684\u8eab\u5f71\u9010\u6e10\u6d88\u5931\u5728\u6eda\u6eda\u9ec4\u5c18\u4e2d\u3002 \u3000\u3000\u6674\u7a7a\u4e00\u78a7\u3002\u521d\u79cb\u7684\u65f6\u5206\uff0c\u534e\u4e2d\u5df2\u5fae\u900f\u4e00\u4e8c\u5206\u5bd2\u610f\uff0c\u867d\u7136\u662f\u8273\u9633\u5f53\u7a7a\uff0c\u4f46\u5374\u5145\u6ee1\u7740\u51b7 \u7a7a\u6c14\u3002 \u3000\u3000\u6cf0\u5c71\u53f7\u79f0\u5929\u4e0b\u7b2c\u4e00\u5cb3\uff0c\u5c31\u662f\u4eba\u5c71\u7684\u8def\u5f84\u4e5f\u6709\u4e00\u91cc\u534a\u957f\uff0c\u5374\u56e0\u7ec8\u5e74\u884c\u4eba\u6e38\u5ba2\u4e0d\u7edd\uff0c\u9053 \u8def\u5bbd\u9614\u5f97\u5f88\u3002 \u3000\u3000\u8def\u65c1\u9694\u4e0d\u591a\u8fdc\u4fbf\u6709\u4e00\u682a\u5927\u6811\uff0c\u4e24\u65c1\u5bf9\u7acb\uff0c\u6811\u961f\u5dee\u4e0d\u591a\u8981\u5c06\u6574\u4e2a\u9053\u9762\u906e\u76d6\u8d77\u6765\u4e86\u3002 \u3000\u3000\u8def\u9762\u5de6\u53f3\u90fd\u662f\u4e00\u7247\u9752\u8471\u7684\u8349\u5730\uff0c\u7ef5\u5ef6\u5927\u534a\u4e2a\u5c71\u533a\uff0c\u5927\u7ea6\u662f\u592a\u8302\u76db\u7684\u539f\u6545\uff0c\u867d\u662f\u79cb\u5b63\uff0c \u5374\u8fd8\u662f\u9752\u7fe0\u5982\u6625\u3002 \u3000\u3000\u9635\u9635\u5fae\u98ce\u4e0d\u65f6\u5e26\u6765\u6811\u53f6\u7c41\u7c41\u5730\u6447\u54cd\u58f0\uff0c\u653e\u773c\u671b\u53bb\uff0c\u5c0f\u5f84\u867d\u5a49\u8712\u5982\u86c7\uff0c\u4f46\u5982\u662f\u773c\u529b\u597d \u7684\u4eba\uff0c\u4ecd\u53ef\u8fa8\u51fa\u90a3\u5c0f\u5f84\u7684\u7aef\u5934\u7ed3\u675f\u5728\u4e00\u7247\u5149\u647a\u647a\u7684\u77f3\u6797\u4e2d\u3002 \u3000\u3000\u201c\u7684\u5f97\u201d\uff0c\u201c\u7684\u5f97\u201d\uff0c\u9a6c\u8e44\u58f0\uff0c\u8f94\u94c3\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u60f3\u662f\u90a3\u540d\u9707\u5929\u4e0b\u7684\u201c\u6cf0\u5c71\u5927\u4f1a\u201d \u5438\u5f15\u7740\u66f4\u591a\u7684\u6b66\u6797\u4eba\u58eb\uff0c\u5f80\u6765\u8fd9\u7075\u5c71\u3002 \u3000\u3000\u518d\u6709\u4e00\u5929\u4fbf\u662f\u201c\u6cf0\u5c71\u5927\u4f1a\u201d\u7684\u65e5\u5b50\uff0c\u8fd9\u6b66\u6797\u68a6\u5915\u6302\u6000\u7684\u76db\u5178\uff0c\u5c06\u8981\u51b3\u5b9a\u4e94\u5927\u5b97\u6d3e\u4e0b \u4e00\u4ee3\u7684\u5f62\u52bf\u3002 \u3000\u3000\u7a0d\u4e3a\u6709\u4e00\u4e9b\u7ecf\u9a8c\u7684\u4eba\u7269\u4fbf\u53ef\u4ee5\u77e5\u9053\u8fd9\u6b21\u6cf0\u5c71\u5927\u4f1a\u5374\u9690\u4f0f\u7740\u5927\u5927\u7684\u5371\u673a\uff0c\u91cd\u5219\u6574\u4e2a\u6b66 \u6797\u5c06\u8840\u98ce\u8165\u96e8\uff0c\u8f7b\u5219\u4e94\u5927\u5b97\u6d3e\u4f1a\u652f\u79bb\u7834\u788e\u3002\u5f53\u7136\uff0c\u8fd9\u5371\u673a\u8fd8\u4e0d\u5b8c\u5168\u662f\u7531\u4e8e\u201c\u4e03\u5999\u795e\u541b\u201d \u518d\u73b0\u6c5f\u6e56\u6240\u81f4\uff01 \u3000\u3000\u672a\u724c\u65f6\u5206\uff0c\u8273\u9633\u5f53\u7a7a\uff0c\u9053\u65c1\u90a3\u719f\u6089\u800c\u60a6\u8033\u7684\u8f94\u8e44\u58f0\u518d\u5ea6\u626c\u8d77\u5728\u8fd9\u6b63\u5348\u4e00\u523b\u5e73\u9759\u4e2d\uff0c \u5239\u65f6\u9053\u8fb9\u8f6c\u51fa\u4e8c\u9a91\u3002 \u3000\u3000\u4e8c\u4eba\u90fd\u662f\u4e00\u822c\u5e74\u8f7b\uff0c\u4e5f\u90fd\u5177\u6709\u4e00\u822c\u4fca\u7f8e\u7684\u9762\u5bb9\uff0c\u4f18\u7f8e\u800c\u633a\u76f4\u7684\u8eab\u6750\u7aef\u6b63\u7684\u5750\u5728\u9a6c\u4e0a\uff0c \u88ab\u9633\u5149\u7167\u6620\u7740\uff0c\u534a\u8fb9\u900f\u51fa\u53ef\u7231\u7684\u7c73\u9ec4\u8272\uff0c\u800c\u5730\u9762\u4e0a\u5374\u659c\u659c\u7684\u5370\u51fa\u4e8c\u4e2a\u77ed\u77ed\u7684\u5f71\u513f\u3002\u8fd9\u4e8c \u4eba\u5927\u6982\u4e5f\u662f\u6765\u89c1\u8bc6\u8fd9\u6cf0\u5c71\u5927\u4f1a\u7684\uff0c\u5c24\u5176\u662f\u5de6\u8fb9\u90a3\u4eba\uff0c\u80cc\u4e0a\u4e14\u914d\u7740\u4e00\u67c4\u957f\u5251\uff0c\u5012\u50cf\u662f\u6b66\u6797 \u4e2d\u4eba\u3002 \u3000\u3000\u5927\u6982\u662f\u7531\u4e8e\u8def\u9014\u7684\u52b3\u7d2f\uff0c\u4e8c\u4eba\u6ca1\u6709\u5f00\u53e3\u4ea4\u8c08\uff0c\u4f46\u95fb\u7684\u5f97\u8e44\u58f0\uff0c\u6e05\u8106\u94c3\u58f0\uff0c\u4e8c\u4eba\u5df2\u5306 \u5306\u800c\u8fc7\u3002 \u3000\u3000\u8fd9\u6cf0\u5c71\u5927\u4f1a\u867d\u662f\u58f0\u540d\u8fdc\u64ad\uff0c\u4f46\u6b64\u6b21\u5374\u662f\u7b2c\u4e8c\u6b21\u5f00\u4f1a\uff0c\u8fdc\u5728\u4e00\u5341\u4e94\u5e74\u524d\uff0c\u90a3\u65f6\u4e94\u5927\u5b97 \u6d3e\u53ec\u96c6\u5929\u4e0b\u82f1\u96c4\u805a\u4e8e\u6cf0\u5c71\uff0c\u4ee5\u6b66\u8bba\u53cb\uff0c\u5e76\u63a8\u51fa\u5929\u4e0b\u7b2c\u4e00\u5251\u3002 \u3000\u3000\u5f53\u65f6\u6b66\u6797\u4e2d\u5173\u4e2d\u4e5d\u8c6a\u5df2\u661f\u6563\u96f6\u843d\uff0c\u5e76\u6ca1\u6709\u4eba\u53c2\u52a0\uff0c\u4e16\u5916\u4e09\u4ed9\u8fdc\u5728\u4e2d\u539f\u4ee5\u5916\uff0c\u66f4\u662f\u4e0d \u5c51\u5927\u4e2d\u539f\uff0c\u4e2d\u5dde\u4e8c\u5927\u5947\u4eba\u4e4b\u4e00\u4e03\u5999\u795e\u541b\u5374\u53c8\u56e0\u5fc3\u6c14\u9ad8\u50b2\uff0c\u4e0d\u5c51\u4e0e\u4e94\u5927\u5b97\u6d3e\u90a3\u4e00\u6279\u201c\u51e1\u592b\u201d \u4e3a\u4f0d\uff0c\u5012\u53ea\u6709\u5355\u5251\u65ad\u9b42\u6cb3\u6d1b\u4e00\u5251\u5434\u8bcf\u4e91\u4e00\u4eba\u4e00\u9a91\u5230\u4e86\u6cf0\u5c71\u3002 \u3000\u3000\u4ee5\u5434\u8bcf\u4e91\u7684\u529f\u592b\uff0c\u4e94\u5927\u5b97\u6d3e\u81ea\u77e5\u4e0d\u654c\uff0c\u5f53\u65f6\u5d06\u5cd2\u5389\u9e57\u4fbf\u6781\u529b\u4e3b\u5f20\u4e94\u6d3e\u8054\u624b\u5728\u4f1a\u671f\u5173 \u540e\u51fb\u6bd9\u5434\u8bcf\u4e91\uff0c\u4e8e\u662f\u6606\u4ed1\u7684\u51cc\u7a7a\u6b65\u865a\u5353\u817e\uff0c\u70b9\u82cd\u7684\u56de\u98ce\u5251\u5ba2\u8c22\u661f\uff0c\u6b66\u5f53\u7684\u8d64\u9633\u5b50\uff0c\u5ce8\u5d4b \u7684\u82e6\u5eb5\u4e0a\u4eba\u548c\u5251\u795e\u5389\u9e57\u4e94\u5251\u5408\u58c1\uff0c\u5c06\u5355\u5251\u5434\u8bcf\u4e91\u6bd9\u5728\u5929\u7ec5\u7011\u524d\uff0c\u800c\u5251\u795e\u5389\u9e57\u4fbf\u5750\u4e0a\u6b66\u6797 \u7b2c\u4e00\u5251\u7684\u5b9d\u5ea7\u3002 \u3000\u3000\u5341\u4e94\u5e74\u540e\uff0c\u6cf0\u5c71\u5927\u4f1a\u518d\u5ea6\u4e34\u53f0\uff0c\u867d\u662f\u89c4\u5b9a\u4e0a\u4e00\u5c4a\u53c2\u4e0e\u8005\u7686\u4e0d\u5f97\u51fa\u624b\uff0c\u4f46\u4e94\u5927\u6d3e\u7684\u4eba \u624d\u6d4e\u6d4e\uff0c\u96be\u514d\u53c8\u8981\u53d1\u751f\u51b2\u7a81\uff0c\u5176\u4e2d\u5305\u85cf\u7978\u5fc3\uff0c\u5404\u5b58\u5fc3\u673a\uff0c\u5927\u6709\u5f20\u5f13\u62d4\u5f29\u4e4b\u52bf\uff01\u4e14\u8bf4\u8fd9\u4e2a \u5c11\u5e74\u6765\u5230\u8def\u5934\uff0c\u6b47\u7247\u523b\uff0c\u5de6\u9762\u90a3\u4eba\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u524d\u9762\u5730\u52bf\u7a81\u53d8\uff0c\u6eaa\u6c34\u6d6e\u6dd9\uff0c\u6e05\u51c9\u660e\u51c0\uff0c \u96be\u514d\u5012\u522b\u6709\u4e00\u756a\u60c5\u8da3\u54e9\u3002\uff01\u201d \u3000\u3000\u6562\u60c5\u4ed6\u4fe9\u4fbf\u662f\u5306\u5306\u8d76\u6765\u7684\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u3002 \u3000\u3000\u8f9b\u6377\u95fb\u8a00\u5fae\u5fae\u4e00\u7b11\uff0c\u6253\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u5341\u4e08\u9053\u8def\u7a81\u65ad\uff0c\u88ab\u4e00\u6761\u4e0d\u5927\u4e0d\u5c0f\u7684\u6eaa\u6c34\u9694\u65ad\uff0c \u53ea\u6709\u4e00\u5ea7\u62f1\u6865\u4e3a\u901a\u8def\uff0c\u6865\u7684\u90a3\u4e00\u7aef\u5374\u662f\u4e00\u7247\u4e1b\u6797\uff0c\u6797\u6df1\u4e0d\u77e5\u5904\u3002 \u3000\u3000\u4e8c\u4eba\u4e00\u8def\u884c\u6765\uff0c\u4ec6\u4ec6\u98ce\u5c18\uff0c\u6b64\u65f6\u6765\u5230\u6eaa\u524d\uff0c\u500d\u89c9\u51c0\u723d\uff0c\u4e00\u65f6\u7adf\u9676\u9189\u5728\u5982\u753b\u7684\u60c5\u666f\u4e2d\u3002 \u3000\u3000\u7565\u4e3a\u4f11\u606f\uff0c\u9f50\u51fa\u5c0f\u6865\uff0c\u7a7f\u5165\u5bc6\u6797\u3002 \u3000\u3000\u5ffd\u95fb\u4e0d\u8fdc\u5904\u9635\u9635\u96f7\u9e23\uff0c\u58f0\u97f3\u6c89\u95f7\u65e0\u6bd4\uff0c\u4e8c\u4eba\u9f50\u9f50\u4e00\u6014\uff0c\u6025\u5faa\u58f0\u884c\u53bb\uff0c\u5f20\u76ee\u4e00\u671b\uff0c\u5374 \u89c1\u662f\u4e00\u6761\u7011\u5e03\u3002 \u3000\u3000\u4e8c\u4eba\u7acb\u8eab\u5904\u8ddd\u7011\u5e03\u7ea6\u83ab\u5eff\u4e08\uff0c\u4f46\u89c9\u7011\u5e03\u6c34\u52bf\u6781\u52b2\uff0c\u8fdc\u770b\u53ea\u89c1\u4e00\u5339\u767d\u7ee2\u76f4\u5f80\u4e0b\u6cfb\uff0c\u6545 \u800c\u53d1\u51fa\u96f7\u9e23\u7684\u58f0\u97f3\u3002 \u3000\u3000\u7011\u5e03\u4f4e\u5904\u4e0d\u77e5\u6df1\u6709\u51e0\u8bb8\uff0c\u53ea\u662f\u4e00\u7247\u767d\u832b\u832b\u7684\u6c34\u6c14\uff0c\u4f7f\u5bc6\u6797\u4e2d\u66f4\u611f\u6f6e\u6e7f\uff0c\u6562\u60c5\u90a3\u6761\u5c0f \u6eaa\u4fbf\u662f\u7531\u6b64\u7011\u5e03\u6784\u6210\u3002 \u3000\u3000\u4e8c\u4eba\u6b63\u611f\u53f9\u9020\u7269\u8005\u4e4b\u795e\u5947\uff0c\u5434\u51cc\u98ce\u773c\u5feb\uff0c\u7a81\u5730\u4e00\u4f38\u624b\uff0c\u6307\u4e00\u6307\u90a3\u5339\u7ee2\u7684\u5de6\u65b9\uff0c\u5583\u5583 \u5ff5\u9053\uff1a\u201c\u5929\u7ec5\u7011\uff01\u201d \u3000\u3000\u8f9b\u6377\u968f\u4ed6\u6240\u6307\u671b\u53bb\uff0c\u4f46\u89c1\u6781\u9ad8\u7684\u7011\u5e03\u5de6\u4fa7\u679c\u7136\u523b\u7740\u201c\u5929\u7ec5\u7011\u201d\u4e09\u4e2a\u5b57\uff0c\u56de\u9996\u4e00\u671b\u5434 \u51cc\u98ce\uff0c\u679c\u7136\u795e\u8272\u5927\u53d8\u3002 \u3000\u3000\u8f9b\u6377\u662f\u4e2a\u8fc7\u6765\u4eba\uff0c\u6709\u8fc7\u7c7b\u4f3c\u7684\u7ecf\u9a8c\uff0c\u4ed6\u4f53\u4f1a\u5f97\u51fa\u5434\u51cc\u98ce\u6b64\u523b\u7684\u5fc3\u60c5\uff0c\u4ed6\u53c8\u80fd\u8bf4\u4ec0\u4e48 \u5462\uff1f \u3000\u3000\u5929\u7ec5\u7011\u7684\u6c34\u52bf\u597d\u6bd4\u94f6\u6cb3\u6cfb\u5730\uff0c\u51b2\u6fc0\u5728\u6df1\u57ab\u4e2d\u53d1\u51fa\u96f7\u9e23\uff0c\u6c14\u52bf\u4f55\u7b49\u58ee\u4e3d\u4f18\u7f8e\uff0c\u5434\u51cc\u98ce \u5bf9\u8fd9\u4e00\u5207\u5982\u4e0d\u95fb\u4e0d\u95ee\uff0c\u53ea\u7d27\u54ac\u7740\u7259\uff0c\u5583\u5583\u7684\u4f4e\u8bed\u3002 \u3000\u3000\u84e6\u5730\u5434\u51cc\u98ce\u5fae\u7d27\u9a6c\u50f5\uff0c\u7684\u7684\u4e0a\u524d\uff0c\u8f9b\u6377\u832b\u7136\u8ddf\u5728\u8eab\u540e\uff0c\u4e00\u76f4\u6765\u5230\u7011\u524d\u4e0d\u53ca\u4e09\u4e08\u624d\u505c \u4e0b\u9a6c\u6765\u3002 \u3000\u3000\u5434\u51cc\u98ce\u98d8\u8eab\u4e0b\u9a6c\uff0c\u8d70\u5411\u4e00\u4e2a\u77d7\u7acb\u7684\u5c71\u77f3\uff0c\u8f9b\u6377\u968f\u773c\u671b\u53bb\uff0c\u53ea\u89c1\u5927\u77f3\u4e0a\u5251\u75d5\u7d2f\u7d2f\uff0c\u4e14 \u90fd\u6df1\u6df1\u52d2\u5165\u77f3\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4ed6\u7684\u773c\u524d\u7acb\u523b\u6784\u51fa\u4e00\u526f\u53ae\u6740\u7684\u56fe\u6837\uff1a\u65ad\u9b42\u5251\u7aed\u529b\u82e6\u6597\u4e94\u540d\u9ad8 \u624b\uff0c\u800c\u6e85\u8840\u7011\u524d\uff01 \u3000\u3000\u5434\u51cc\u98ce\u4f4e\u4f4e\u4e00\u543c\uff0c\u84e6\u5730\u865a\u7a7a\u594b\u529b\u6253\u51fa\u4e00\u62f3\uff0c\u62f3\u98ce\u6fc0\u8361\u5904\uff0c\u90a3\u6025\u52b2\u7684\u6d41\u6c34\u4e5f\u88ab\u51b2\u5f97\u5fae \u5fae\u5206\u5f00\u4e00\u9699\u3002 \u3000\u3000\u201c\u597d\u638c\u529b\uff01\u201d \u3000\u3000\u84e6\u7684\u5de6\u65b9\u4e00\u4eba\u5927\u58f0\u559d\u91c7\uff0c\u8f9b\u3001\u5434\u4e8c\u4eba\u5faa\u58f0\u5bfb\u53bb\uff0c\u53ea\u89c1\u8fdc\u5904\u8d70\u51fa\u4e24\u4eba\uff0c\u4e8c\u4eba\u773c\u529b\u9510\u5229\uff0c \u5df2\u770b\u51fa\u662f\u5d06\u5cd2\u7684\u4e8e\u4e00\u98de\u548c\u53e6\u4e00\u4e2a\u6c49\u5b50\u3002 \u3000\u3000\u8d70\u5230\u8fd1\u5904\uff0c\u65b9\u624d\u8fa8\u51fa\u90a3\u4e00\u4e2a\u6c49\u5b50\u5374\u662f\u5728\u90a3\u8352\u5e99\u524d\u66fe\u62e6\u963b\u91d1\u8001\u5927\u800c\u51fa\u624b\u7684\u59d3\u53f2\u7684\u6c49\u5b50\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u4e00\u8def\u8dd1\u6765\uff0c\u8001\u8fdc\u4fbf\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u677f\u522b\u6765\u65e0\u6089\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5f88\u597d\uff01\u5f88\u597d\uff01\u201d\u5fae\u5fae\u4e00\u987f\u53c8\u9053\uff0c\u201c\u4e8e\u5927\u4fa0\u6b64\u6765\u5fc5\u662f\u95f4\u9f0e\u5251\u4f1a \u4e86\uff1f\u201d \u3000\u3000\u4e8e\u4e00\u98de\u563f\u563f\u4e00\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u677f\u679c\u771f\u55dc\u6b66\u5982\u72c2\uff0c\u5012\u4e0d\u6599\u4f60\u6bd4\u6211\u8fd8\u5148\u6765\u4e00\u6b65\u54e9\u3002\u201d \u3000\u3000\u8f9b\u6377\u89c1\u4e8e\u4e00\u98de\u7edd\u53e3\u4e0d\u63d0\u6bd4\u6b66\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u6697\u7b11\uff0c\u5df1\u77e5\u4ed6\u8fde\u53d7\u632b\u6298\uff0c\u72c2\u6a2a\u4e4b\u6001\u5927\u51cf\uff0c\u968f \u53e3\u5e94\u9053\uff1a\u201c\u54ea\u91cc\uff0c\u53ea\u4e0d\u8fc7\u60f3\u501f\u6b64\u77bb\u4ef0\u5929\u4e0b\u82f1\u96c4\u98ce\u91c7\u7f62\u4e86\u3002\u201d \u3000\u3000\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8f9b\u8001\u677f\u5148\u884c\u4e5f\u4e0d\u901a\u77e5\u6211\u4e00\u58f0\uff0c\u5012\u5bb3\u6211\u5f80\u6b66\u6c49\u767d\u8dd1\u4e00\u8d9f \u54e9\uff01\u201d \u3000\u3000\u534a\u6708\u524d\u8f9b\u6377\u548c\u4e8e\u4e00\u98de\u7ea6\u5b9a\u5728\u6b66\u6c49\u4f1a\u9f50\u4e00\u540c\u524d\u5f80\u6cf0\u5c71\uff0c\u54ea\u77e5\u8f9b\u6377\u884c\u8ff9\u5306\u5306\uff0c\u65e9\u5c31\u628a\u6b64 \u4e8b\u5fd8\u53bb\uff0c\u8fd9\u65f6\u6025\u5207\u95f4\u542c\u5230\u4e8e\u4e00\u98de\u5982\u6b64\u8bf4\uff0c\u4e4d\u95fb\u4e4b\u4e0b\u4f3c\u89c9\u4e8e\u4e00\u98de\u8bdd\u4e2d\u6709\u56e0\uff0c\u8138\u8272\u4e00\u53d8\uff0c\u597d \u5728\u4e8e\u4e00\u98de\u5e76\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u8f9b\u6377\u4fe1\u53e3\u80e1\u626f\u9053\uff1a\u201c\u5728\u4e0b\u6700\u8fd1\u63a5\u529e\u4e00\u5b97\u6781\u5927\u7684\u4e70\u5356\uff0c\u662f\u4ee5\u5fd9\u5f97\u9a6c\u4e0d\u505c\u8e44\uff0c\u4e07\u5e78\u5982\u671f \u529e\u59a5\uff0c\u5426\u5219\u4fbf\u8981\u8bef\u4e86\u4f1a\u671f\u5462\uff1f\u5475\uff0c\u90a3\u5b97\u4e70\u5356\u91cc\u6709\u4e00\u7c92\u62f3\u5934\u5927\u5c0f\u7684\u7ea2\u94bb\u77f3\uff0c\u4e0d\u7792\u4e8e\u5927\u4fa0\u8bf4\uff0c \u5c0f\u5f1f\u867d\u662f\u5e72\u8fd9\u4e00\u884c\uff0c\u5012\u4e5f\u4ece\u672a\u89c1\u8fc7\u5982\u6b64\u5927\u7684\u94bb\u77f3\u54e9\uff01\u201d \u3000\u3000\u4ed6\u540e\u9762\u534a\u6bb5\u8bdd\u53e5\u5b8c\u5168\u770b\u89c1\u4e8e\u4e00\u98de\u9762\u8272\u5fae\u5e26\u72b9\u8c6b\u800c\u4fe1\u53e3\u80e1\u5439\u7684\uff0c\u4e0d\u60f3\u4e8e\u4e00\u98de\u5012\u771f\u7684 \u201c\u554a\uff01\u201d\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u6709\u8fd9\u7b49\u5927\u7684\u5b9d\u77f3\uff1f\u4e0b\u6b21\u5c0f\u5f1f\u5012\u8981\u89c1\u8bc6\u89c1\u8bc6\uff01\u201d \u3000\u3000\u8f9b\u6377\u53ea\u5f97\u4e0d\u7f6e\u53ef\u5426\u7684\u55ef\u4e86\u4e00\u58f0\uff0c\u5f00\u53e3\u5c94\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u5c0f\u5f1f\u65b0\u8fd1\u7ed3\u8bc6\u7684\u5434\u51cc\u98ce\u5434\u5144\u3002\u201d \u3000\u3000\u8bf4\u7740\u6307\u4e00\u6307\u5434\u51cc\u98ce\uff0c\u540c\u65f6\u4e5f\u5c06\u4e8e\u4e00\u98de\u4ecb\u7ecd\u7ed9\u5434\u51cc\u98ce\u8ba4\u8bc6\u3002 \u3000\u3000\u5434\u51cc\u98ce\u89c1\u8f9b\u6377\u673a\u667a\u5982\u6b64\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u7531\u4f69\u670d\uff0c\u4e5f\u88c5\u7740\u4ece\u672a\u89c1\u8fc7\u4e8e\u4e00\u98de\u7684\u6837\u5b50\uff0c\u9053\u4e86\u58f0 \u4e45\u4ef0\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u5012\u723d\u5feb\uff0c\u5c06\u90a3\u53f2\u59d3\u7684\u6c49\u5b50\u4ecb\u7ecd\u4e86\u4e00\u4e0b\uff0c\u90a3\u59d3\u53f2\u7684\u5524\u7740\u53f2\u548c\u5eb7\uff0c\u662f\u4e8e\u4e00\u98de\u7684\u5e08 \u5f1f\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u63a5\u7740\u53c8\u9053\uff1a\u201c\u8fd9\u4f4d\u5434\u5144\u7684\u529f\u592b\u53ef\u771f\u4e0d\u5f31\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u5434\u5144\u662f\u5c0f\u5f1f\u65b0\u8fd1\u7ed3\u8bc6\u7684\uff0c\u638c\u4e0a\u529f\u592b\u662f\u6709\u540d\u7684\u2014\u2014\u201d \u3000\u3000\u4e8e\u4e00\u98de\u70b9\u70b9\u5934\u9053\uff1a\u201c\u5355\u51ed\u521a\u624d\u90a3\u4e00\u62f3\u52b2\u9053\uff0c\u8db3\u53ef\u6324\u8eab\u5929\u4e0b\u9ad8\u624b\u4e4b\u5217\u54e9\uff01\u201d \u3000\u3000\u5434\u51cc\u98ce\u8fde\u9053\uff1a\u201c\u8fc7\u5956\uff01\u8fc7\u5956\uff01\u201d\u5fc3\u4e2d\u5012\u4e5f\u4f69\u670d\u4e8e\u4e00\u98de\u7684\u773c\u529b\u548c\u7ecf\u9a8c\u3002 \u3000\u3000\u518d\u8c08\u5f97\u6570\u53e5\uff0c\u4e8e\u4e00\u98de\u9053\uff1a\u201c\u5c0f\u5f1f\u6b64\u6b21\u53c2\u4e0e\u5251\u4f1a\uff0c\u662f\u548c\u5bb6\u5e08\u53ca\u5e08\u5144\u6765\u7684\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5047\u610f\u201c\u5475\u201d\u4e86\u4e00\u58f0\uff0c\u4e8e\u4e00\u98de\u7ee7\u7eed\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u4f9d\u5c0f\u5f1f\u4e4b\u89c1\uff0c\u6b64\u6b21\u5251\u4f1a\u5fc5\u4f1a\u5f15\u8d77 \u6b66\u6797\u4e2d\u4e00\u573a\u5267\u70c8\u7684\u6218\u6597\uff01\u5230\u65f6\u5019\u573a\u9762\u5b9a\u662f\u6df7\u4e71\u5f97\u5f88\uff0c\u8f9b\u5144\u8eab\u65e0\u6b66\u6280\uff0c\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5dee\u9519 \u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\u4e5f\u98ce\u95fb\u4e9b\u2018\u5173\u4e2d\u4e5d\u8c6a\u2019\u3001\u2018\u4e03\u5999\u795e\u541b\u2019\u51fa\u4e16\u7684\u4e8b\u60c5\uff0c\u4f46\u5230\u5e95 \u4e0d\u4fe1\u4ed6\u4eec\u8fd8\u80fd\u5f3a\u8fc7\u5c0a\u5e08\uff1f\u201d \u3000\u3000\u4e8e\u4e00\u98de\u82e6\u7b11\u70b9\u4e86\u70b9\u5934\uff0c\u9053\uff1a\u201c\u4f46\u613f\u5982\u6b64\u3002\u201d \u3000\u3000\u8bf4\u7740\u5fae\u5fae\u4e00\u53f9\u53c8\u9053\uff1a\u201c\u5c0f\u5f1f\u5f97\u5148\u8d76\u56de\u4f1a\u573a\uff0c\u53bb\u89c1\u89c1\u5404\u6d3e\u7684\u7cbe\u82f1\u4fca\u6770\uff0c\u54b1\u4eec\u5c31\u6b64\u544a \u522b\u3002\u201d \u3000\u3000\u8f9b\u6377\u70b9\u70b9\u5934\uff0c\u63d6\u624b\u4f5c\u522b\u3002 \u3000\u3000\u9001\u8d70\u4e8e\u3001\u53f2\u4e8c\u4eba\uff0c\u8f9b\u6377\u7b11\u7740\u5bf9\u5434\u51cc\u98ce\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u4e00\u8ba1\u53c8\u751f\u4e86\u5947\u6548\uff0c\u5230\u65f6\u5019\u4e03\u5999\u795e \u541b\uff0c\u6cb3\u6d1b\u4e00\u5251\u9f50\u73b0\u4f1a\u573a\uff0c\u4e94\u5927\u6d3e\u7684\u5bb6\u4f19\u4e0d\u77e5\u8981\u591a\u4e48\u5403\u60ca\u54e9\uff01\u201d \u3000\u3000\u8bf4\u7740\u4e00\u9f50\u8de8\u4e0a\u5750\u9a91\uff0c\u5954\u5411\u5c71\u53bb\uff01 \u3000\u3000\u6cf0\u5c71\u5251\u4f1a\u4f1a\u573a\u8bbe\u5728\u65e5\u89c2\u5cf0\u5934\uff0c\u4e8c\u4eba\u7565\u8fa8\u5730\u52bf\uff0c\u52a0\u5feb\u5750\u9a91\uff0c\u84e6\u5730\u80cc\u540e\u4e00\u9635\u6025\u5954\u58f0\u4f20\u8fc7 \u6765\uff0c\u663e\u5f97\u8d76\u8def\u8005\u811a\u7a0b\u4e0d\u51e1\uff0c\u4e8c\u4eba\u5fae\u5fae\u4e00\u6014\uff0c\u5fc3\u60f3\u53ef\u80fd\u662f\u522b\u6d3e\u82f1\u96c4\u8d76\u6765\u805a\u4f1a\uff0c\u4e8e\u662f\u4e5f\u4e0d\u653e \u5728\u5fc3\u4e0a\u3002 \u3000\u3000\u90a3\u8d76\u8def\u8005\u597d\u5feb\u7684\u811a\u7a0b\uff0c\u53ea\u8fc7\u4e00\u523b\uff0c\u8ffd\u8005\u548c\u9a6c\u513f\u9053\u5c3e\u76f8\u8854\uff0c\u8fde\u8f9b\u3001\u5434\u4e8c\u4eba\u4e5f\u89c9\u4e00\u60ca\u3002 \u3000\u3000\u6cf0\u5c71\u5c71\u9053\u672c\u5bbd\uff0c\u4f46\u662f\u5728\u8fd9\u4e0a\u5761\u4e4b\u65f6\uff0c\u4e5f\u4ec5\u80fd\u5bb9\u4e8c\u9a6c\u9f50\u8fc7\u3002\u6765\u8005\u4f3c\u4e4e\u4e0d\u9632\u6709\u4eba\u9a6c\u5728\u6b64\uff0c \u4e00\u65f6\u6536\u4e0d\u4f4f\u8db3\uff0c\u5927\u53f1\u4e00\u58f0\u786c\u751f\u751f\u98de\u8d77\u8eab\u6765\uff0c\u7adf\u4ece\u8f9b\u3001\u5434\u4e8c\u4eba\u5934\u9876\u98de\u8d8a\u8fc7\u53bb\u3002 \u3000\u3000\u6765\u4eba\u4f3c\u4e5f\u77e5\u7406\u4e8f\uff0c\u4e0d\u6562\u7a0d\u505c\uff0c\u811a\u624d\u843d\u5730\uff0c\u4fbf\u5982\u98de\u800c\u53bb\u3002\u8f9b\u3001\u5434\u4e8c\u4eba\u4f55\u7b49\u773c\u529b\uff0c\u65e9\u5df2 \u77a5\u89c1\u6b63\u662f\u90a3\u5929\u7edd\u5251\u8bf8\u845b\u660e\uff0c\u76f8\u5bf9\u4e00\u7b11\uff0c\u968f\u5373\u8ddf\u4e0a\u3002 \u3000\u3000\u6765\u5230\u6708\u89c2\u5cf0\u524d\uff0c\u53ea\u89c1\u4eba\u5f71\u5e4c\u5e4c\uff0c\u5148\u5230\u8005\u751a\u591a\uff0c\u4e8c\u4eba\u8003\u8651\u5728\u516c\u5171\u573a\u6240\u51fa\u73b0\u592a\u591a\uff0c\u5fc5\u6709 \u6240\u5931\uff0c\u662f\u4ee5\u7a0d\u5fae\u5546\u91cf\uff0c\u9f50\u8f6c\u5411\u6cf0\u5c71\u5317\u9762\uff0c\u51c6\u5907\u4e00\u6e38\u5cb1\u5b97\u4e08\u4eba\u5cf0\u3002 \u3000\u3000\u4e08\u4eba\u5cf0\u90e8\u4f4d\u5947\u9669\uff0c\u4e71\u767e\u5d99\u5ccb\uff0c\u68d8\u4e1b\u904d\u5730\uff0c\u4e8c\u4eba\u597d\u5bb9\u6613\u624d\u722c\u5230\u5cf0\u9876\u3002 \u3000\u3000\u6cf0\u5c71\u53f7\u79f0\u4e94\u5cb3\u4e4b\u9996\uff0c\u8fd9\u6700\u76db\u540d\u7684\u5cf0\u5934\u679c\u771f\u4e0d\u51e1\uff0c\u867d\u7136\u662f\u79cb\u5b63\uff0c\u4f46\u4ecd\u98ce\u5149\u5982\u753b\uff0c\u4e8c\u4eba \u7acb\u4e8e\u9876\u5cf0\uff0c\u987f\u89c9\u5929\u4e0b\u4e4b\u5c0f\uff0c\u5b87\u5b99\u4e4b\u5927\uff0c\u5fc3\u4e2d\u540c\u6709\u6240\u611f\u3002 \u3000\u3000\u5c24\u5176\u662f\u767e\u611f\u8426\u5fc3\uff0c\u611f\u6168\u4e07\u5206\uff0c\u60f3\u5230\u5bb6\u4ec7\u3001\u5e08\u4ec7\u5982\u6d77\uff0c\u4e0d\u7531\u53d1\u58f0\u957f\u5578\u3002 \u3000\u3000\u8f9b\u6377\u4e3a\u4eba\u5fc3\u7ec6\u65e0\u6bd4\uff0c\u5728\u6b64\u6292\u60c5\u53d1\u610f\u4e4b\u65f6\uff0c\u4ecd\u80fd\u63a7\u5236\u4e0d\u8ba9\u5185\u529b\u6e17\u63c9\u4e8e\u5578\u58f0\u4e2d\uff0c\u4f46\u4e2d\u6c14 \u5df2\u6bd4\u4e00\u822c\u4eba\u8981\u5145\u6c9b\u5f97\u591a\uff0c\u6e05\u8361\u7684\u8fdc\u4f20\u51fa\u53bb\u3002 \u3000\u3000\u84e6\u5730\u4e00\u4e1d\u60ca\u547c\u548c\u4e00\u58f0\u53f1\u58f0\u4f20\u4e86\u8fc7\u6765\uff0c\u4e8c\u4eba\u5fae\u5fae\u4e00\u6014\uff0c\u9f50\u4fef\u8170\u671b\u4e0b\u53bb\uff0c\u4f46\u89c1\u5c71\u8170\u5904\u9690 \u7ea6\u7ad9\u6709\u4e8c\u4eba\uff0c\u8fd8\u4f3c\u6b63\u5728\u4e89\u5435\u3002\u8f9b\u6377\u4e00\u6253\u624b\u5f0f\uff0c\u4e24\u4eba\u9f50\u7eb5\u4e0b\u53bb\uff0c\u627e\u4e00\u7247\u9690\u77f3\u85cf\u8eab\u5b50\uff0c\u53ea\u89c1 \u4e00\u4e2a\u8499\u9762\u7684\u4eba\u548c\u4e00\u4e2a\u5e74\u7ea6\u516d\u65ec\u7684\u8001\u4eba\u5728\u4e89\u5435\u3002 \u3000\u3000\u90a3\u8001\u4eba\u8bf4\u9053\uff1a\u201c\u8001\u592b\u597d\u610f\u6559\u4f60\u4e0d\u8981\u81ea\u6740\uff0c\u4f60\u5374\u5982\u6b64\u4e0d\u8bc6\u597d\u6b79\u8bdd\u672b\u8bf4\u5b8c\uff0c\u90a3\u8499\u9762\u4eba\u6325 \u6325\u624b\u6b62\u4f4f\u8bdd\u5934\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u84e6\u5730\u547b\u541f\u4f3c\u7684\u72c2\u547c\u4e00\u58f0\uff0c\u8f6c\u8eab\u5982\u98de\u800c\u53bb\uff0c\u5374\u9690\u7ea6\u4f20\u6765\u9635\u9635\u62bd \u6ce3\u4e4b\u58f0\uff01\u8001\u4eba\u9a82\u4e86\u4e00\u58f0\uff1a\u201c\u771f\u662f\u75af\u5b50\u3002\u201d \u3000\u3000\u8f9b\u5434\u4e8c\u4eba\u770b\u5f97\u597d\u4e0d\u7cca\u6d82\uff0c\u5374\u77a5\u89c1\u65c1\u8fb9\u5730\u4e0a\u6a2a\u7740\u4e00\u67c4\u957f\u5251\uff0c\u624d\u77e5\u5927\u6982\u662f\u90a3\u8499\u9762\u4eba\u52a8\u5ff5 \u81ea\u7edd\uff0c\u800c\u90a3\u8001\u4eba\u6551\u4ed6\u4e00\u547d\uff0c\u540c\u65f6\u5fc3\u4e2d\u4e5f\u5947\u602a\u90a3\u8499\u9762\u4eba\u7adf\u6709\u4ec0\u4e48\u4e8b\u4e0d\u80fd\u653e\u4e0b\u5fc3\u800c\u6b32\u4ee5\u4e00\u6b7b \u4e86\u4e4b\uff1f \u3000\u3000\u8fd9\u65f6\u90a3\u8001\u8005\u89c1\u90a3\u8499\u9762\u4eba\u53cd\u8eab\u4fbf\u8d70\uff0c\u4e0d\u89c9\u4e00\u6014\uff0c\u968f\u5373\u5fae\u4e00\u53f9\u606f\uff0c\u62fe\u8d77\u5730\u4e0a\u957f\u5251\u3002\u4fe1\u6b65 \u8d70\u6765\u3002 \u3000\u3000\u8f9b\u3001\u5434\u4e24\u4eba\u8eb2\u5728\u77f3\u540e\uff0c\u5fc3\u4e2d\u5927\u4e3a\u5403\u60ca\uff0c\u6562\u60c5\u8fd9\u5bb6\u4f19\u6b63\u5411\u7740\u81ea\u5df1\u9690\u8eab\u4e4b\u5730\u884c\u6765\uff0c\u65e0\u8bba \u5982\u4f55\u4e5f\u8eb2\u4e0d\u5f00\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u5934\u8111\u6e05\u9192\uff0c\u5fc3\u77e5\u8fd9\u8001\u8005\u5e76\u65e0\u6076\u610f\uff0c\u4e0d\u59a8\u51fa\u8eab\u4e00\u8fce\uff0c\u968f\u5373\u4e00\u62cd\u5434\u51cc\u98ce\uff0c\u90a3\u77e5\u5434\u51cc \u98ce\u4f1a\u9519\u4ed6\u7684\u610f\u601d\uff0c\u84e6\u5730\u4e00\u7acb\u8eab\u5f62\uff0c\u545b\u5577\u4e00\u58f0\uff0c\u7cbe\u5149\u66b4\u957f\uff0c\u957f\u5251\u51fa\u624b\u3002 \u3000\u3000\u90a3\u8001\u8005\u672c\u662f\u65e0\u610f\u8d70\u6765\uff0c\u51d1\u5de7\u6b63\u671d\u7740\u4e8c\u4eba\u85cf\u8eab\u4e4b\u5730\uff0c\u6b64\u65f6\u7a81\u89c1\u4e00\u5251\u6a2a\u6321\uff0c\u4e0d\u89c9\u4e00\u6014\uff0c \u5927\u60ca\u540e\u9000\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5fae\u5fae\u6325\u5251\uff0c\u8679\u5149\u541e\u5410\uff0c\u58f0\u52bf\u60ca\u4eba\uff01 \u3000\u3000\u84e6\u5730\uff0c\u90a3\u8001\u8005\u53d1\u72c2\u4f3c\u5730\u66b4\u53f1\u4e00\u58f0\u9053\uff1a\u201c\u554a\uff01\u65ad\u9b42\u5251\u2014\u2014\u201d \u3000\u3000\u65ed\u65e5\u521d\u5347\uff0c\u671d\u9732\u8ff7\u832b\uff0c\u6cf0\u5c71\u5251\u4f1a\u7b2c\u4e00\u5929\u5f00\u59cb\u3002 \u3000\u3000\u65e5\u89c2\u5cf0\u524d\uff0c\u7fa4\u96c4\u805a\u96c6\uff0c\u6cf0\u5c71\u52bf\u9ad8\uff0c\u8fd9\u65e5\u51fa\u5947\u666f\u66f4\u662f\u5947\u7edd\u7532\u5929\u4e0b\u3002\u4f46\u89c1\u971e\u5149\u4e07\u4e08\uff0c\u8679 \u5f69\u5fae\u6258\u6301\u7740\u4e00\u8f6e\u65ed\u65e5\u5189\u5189\u4e0a\u5347\uff0c\u7fa4\u8c6a\u90fd\u4e0d\u7981\u6c89\u9189\u4e8e\u5947\u666f\u4e4b\u4e2d\u3002 \u3000\u3000\u6b64\u6b21\u6cf0\u5c71\u5251\u4f1a\u4e43\u7531\u5929\u4e0b\u7b2c\u4e00\u5b97\u6d3e\u6b66\u5f53\u4e3b\u6301\uff0c\u793c\u9f13\u58f0\u4e2d\uff0c\u6b66\u5f53\u638c\u95e8\u8d64\u9633\u9053\u957f\u6602\u7136\u800c\u51fa\u3002 \u3000\u3000\u6cf0\u5c71\u5251\u4f1a\u672c\u662f\u4ee5\u6b66\u4f1a\u53cb\uff0c\u4e0d\u9650\u5b97\u6d3e\uff0c\u4f46\u82b8\u82b8\u6b66\u6797\u4e2d\u53c8\u6709\u51e0\u4eba\u80fd\u8d85\u5f97\u8fc7\u4e94\u5927\u5b97\u6d3e\u7684\u4eba \u624d\uff1f\u662f\u4ee5\u6bcf\u6b21\u867d\u8bf4\u662f\u4ee5\u6b66\u4f1a\u5929\u4e0b\u8c6a\u6770\uff0c\u4f46\u5374\u662f\u4e94\u5927\u5b97\u6d3e\u7684\u4e89\u6597\u3002 \u3000\u3000\u7136\u800c\u8fd9\u6b66\u6797\u76db\u4f1a\uff0c\u5374\u662f\u5341\u5e74\u96be\u89c1\u4e00\u6b21\u7684\u76db\u4f1a\u3002\u6ca1\u6709\u4eba\u613f\u610f\u653e\u5f03\u8fd9\u4e2a\u89c2\u6469\u673a\u4f1a\u3002 \u3000\u3000\u8d64\u9633\u9053\u58eb\u6602\u7136\u8d70\u5230\u4f1a\u573a\u4e2d\u592e\uff0c\u5fae\u5fae\u7a3d\u9996\uff0c\u542f\u53e3\u9053\uff1a\u201c\u5341\u5e74\u524d\uff0c\u5cb1\u5b97\u4e4b\u9876\uff0c\u655d\u6d3e\u9053\u53d1 \u6cf0\u5c71\u5251\u4f1a\uff0c\u7ed3\u679c\u5929\u4e0b\u516c\u63a8\u5d06\u5cd2\u7684\u5389\u5927\u4fa0\u4e3a\u5929\u4e0b\u7b2c\u4e00\u5251\u2014\u2014\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u5fae\u5fae\u4e00\u987f\uff0c\u5d06\u5cd2\u7684\u95e8\u4eba\u7acb\u523b\u54cd\u8d77\u9707\u5929\u4ef7\u7684\u4e00\u58f0\u6b22\u547c\uff01 \u3000\u3000\u8d64\u9633\u9053\u957f\u5fae\u5fae\u4e00\u7b11\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u5341\u5e74\u540e\u7684\u4eca\u65e5\uff0c\u5929\u4e0b\u6b66\u6797\u4eba\u58eb\u91cd\u96c6\u6b64\u5730\uff0c\u5e76\u6b32\u63a8 \u51fa\u5929\u4e0b\u5251\u672f\u4e4b\u4e3b\uff0c\u4f46\u6709\u4e00\u4e2a\u89c4\u5b9a\uff0c\u563f\uff0c\u51e1\u662f\u4e0a\u4e00\u6b21\u53c2\u52a0\u8fc7\u7684\u82f1\u96c4\u5c31\u4e0d\u5f97\u518d\u53c2\u52a0\uff01\u201d \u3000\u3000\u4ed6\u5728\u5d06\u5cd2\u6d3e\u4eba\u7684\u6b22\u547c\u4e4b\u4e0b\u8f7b\u8a00\u7ec6\u8bed\uff0c\u4f46\u4ecd\u6e05\u6670\u7684\u4f20\u5165\u6bcf\u4e00\u4e2a\u4eba\u7684\u8033\u4e2d\uff0c\u53ef\u89c1\u4ed6\u529f\u529b \u4e0d\u51e1\uff01 \u3000\u3000\u8d64\u9633\u9053\u957f\u63a5\u7740\u53c8\u9053\uff1a\u201c\u54b1\u6b66\u6797\u4e2d\u4eba\uff0c\u4e0d\u5584\u865a\u793c\u4fd7\u5957\uff0c\u5c31\u8bf7\u5404\u4f4d\u82f1\u96c4\u8d50\u6559\uff01\u201d\u8bf4\u7f62\u518d \u4e00\u7a3d\u9996\uff0c\u7f13\u6b65\u9000\u4e0b\u3002 \u3000\u3000\u522b\u770b\u8fd9\u4e00\u7fa4\u82f1\u8c6a\u7ad9\u6ee1\u5927\u534a\u4e2a\u65e5\u89c2\u5cf0\uff0c\u4f46\u5927\u591a\u6570\u90fd\u662f\u62b1\u7740\u89c1\u8bc6\u7684\u5fc3\u601d\uff0c\u662f\u4ee5\u8d64\u9633\u8fd9\u8fb9 \u4e00\u9000\uff0c\u5927\u5bb6\u4fbf\u7a83\u7a83\u5730\u8c08\u4e86\u8d77\u6765\u3002 \u3000\u3000\u9996\u5148\u6700\u4e3a\u5927\u5bb6\u6240\u6ce8\u610f\u7684\u83ab\u8fc7\u4e8e\u5ce8\u5d4b\u3001\u6606\u4ed1\u4e8c\u6d3e\u5c1a\u6ca1\u6709\u4e00\u4eba\u53c2\u52a0\uff0c\u5176\u6b21\u4fbf\u662f\u70b9\u82cd\u6d3e\u4ec5 \u5230\u4e86\u4e00\u4e2a\u5343\u624b\u5251\u5ba2\u9646\u65b9\uff0c\u8fd9\u4e09\u6d3e\u8fdc\u4e0d\u53ca\u5d06\u5cd2\u548c\u6b66\u5f53\u4e8c\u6d3e\u7684\u4eba\u624b\u4f17\u591a\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u65e5\u4e0b\u5cf0\u5ffd\u7136\u4e0a\u6765\u4e86\u4e00\u4e2a\u9762\u5bb9\u6e05\u77bf\u7684\u8001\u548c\u5c1a\u548c\u4e8c\u4e2a\u5e74\u9752\u548c\u5c1a\uff0c\u7fa4\u8c6a\u89c1\u4e86\uff0c \u4e00\u9f50\u8083\u7136\u52a8\u5bb9\u3002 \u3000\u3000\u90a3\u548c\u5c1a\u4e0a\u5f97\u5cf0\u6765\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u963f\u5f25\u9640\u4f5b\uff0c\u8001\u50e7\u8fdf\u4e86\u4e00\u6b65\uff01\u201d \u3000\u3000\u8d64\u9633\u9053\u957f\u89c1\u4e86\u5fd9\u4e0a\u524d\u7a3d\u9996\u9053\uff1a\u201c\u82e6\u5eb5\u4e0a\u4eba\uff0c\u4e00\u522b\u5341\u5e74\u2014\u2014\u201d \u3000\u3000\u8bdd\u97f3\u65b9\u843d\uff0c\u82e6\u5eb5\u5df2\u957f\u7b11\u9053\uff1a\u201c\u9053\u53cb\u4e0d\u5fc5\u5ba2\u6c14\uff0c\u8d2b\u50e7\u6b64\u6b21\u51fa\u5c71\uff0c\u53ea\u4e0d\u8fc7\u662f\u4e0d\u60f3\u7834\u90a3\u5341 \u5e74\u524d\u7684\u8a93\u7ea6\u7f62\u4e86\uff01\u201d \u3000\u3000\u8d64\u9633\u9053\u58eb\u8138\u957f\u5fae\u5fae\u4e00\u70ed\uff0c\u9ed8\u9ed8\u5750\u4e0b\u3002 \u3000\u3000\u7fa4\u8c6a\u89c1\u5ce8\u5d4b\u82e6\u5eb5\u4e0a\u4eba\u7387\u95e8\u4eba\u53ca\u65f6\u8d76\u6765\uff0c\u53c8\u4e0d\u89c9\u7a83\u7a83\u79c1\u8bae\uff0c\u5608\u6742\u4e2a\u4e0d\u4e86\u3002 \u3000\u3000\u8d64\u9633\u9053\u957f\u7b49\u4eba\u58f0\u7a0d\u505c\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u6cf0\u5c71\u5251\u4f1a\u8fd9\u5c31\u5f00\u59cb\uff0c\u6709\u54ea\u4f4d\u82f1\u96c4\u80fd\u591f\u9996\u5148\u2026\u2026\u201d \u3000\u3000\u84e6\u5730\u4eba\u7fa4\u4e2d\u4e00\u58f0\u66b4\u543c\uff0c\u5237\u5730\u7eb5\u51fa\u4e00\u4eba\uff0c\u6253\u65ad\u8d64\u9633\u9053\u957f\u7684\u8bdd\u5934\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5341\u5e74\u524d \u6cf0\u5c71\u5251\u4f1a\u4e5f\u662f\u7531\u54b1\u4eec\u8349\u83bd\u7eff\u6797\u82f1\u96c4\u6d2a\u8001\u524d\u8f88\u9996\u5148\u4eae\u76f8\uff0c\u96be\u9053\u4e94\u5927\u6d3e\u7684\u9ad8\u624b\u90fd\u53ea\u4f1a\u89c2\u4eba\u865a \u5b9e\uff0c\u624d\u80fd\u52a8\u624b\u5417\uff01\u201d \u3000\u3000\u8fd9\u4e00\u756a\u8bdd\u786e\u5b9e\u72c2\u5984\u5df2\u6781\uff0c\u7adf\u5c06\u4e94\u5927\u6d3e\u5168\u7ed9\u9a82\u4e0a\u3002\u8d64\u9633\u9053\u957f\u51b7\u54fc\u4e00\u58f0\uff0c\u95ea\u76ee\u4e00\u671b\uff0c\u4e0d \u7531\u5927\u60ca\uff0c\u6d2a\u58f0\u9053\uff1a\u201c\u539f\u6765\u662f\u5c71\u5de6\u53cc\u8c6a\u6797\u65bd\u4e3b\uff0c\u8d2b\u9053\u6709\u5931\u8fce\u8fd3\uff01\u201d \u3000\u3000\u6797\u5c11\u768b\u50b2\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6797\u67d0\u4f46\u51ed\u638c\u4e2d\u4e00\u652f\u5251\uff0c\u6597\u80c6\u6562\u5411\u5929\u4e0b\u82f1\u96c4\u8bf7\u6c42\u8d50\u6559\uff01\u201d \u3000\u3000\u4ed6\u672c\u6765\u8bf4\u7684\u662f\u4e00\u756a\u573a\u9762\u8bdd\uff0c\u4f46\u56e0\u4ed6\u53e3\u624d\u4e0d\u597d\uff0c\u53c8\u56e0\u6c14\u52bf\u51cc\u4eba\uff0c\u662f\u4ee5\u522b\u4eba\u53cd\u8bef\u4ee5\u4e3a\u6b64 \u4eba\u72c2\u50b2\u4e0d\u53ef\u4e00\u4e16\uff0c\u4f46\u53c8\u9274\u4e8e\u5c71\u5de6\u53cc\u8c6a\u4e4b\u540d\uff0c\u53ea\u6562\u6697\u4e2d\u5492\u9a82\u3002 \u3000\u3000\u84e6\u5730\u4e00\u6761\u4eba\u5f71\u6392\u4f17\u800c\u51fa\uff0c\u621f\u6307\u9a82\u9053\uff1a\u201c\u9601\u4e0b\u53e3\u51fa\u72c2\u8a00\uff0c\u5fc5\u6709\u4ec0\u4e48\u8fc7\u4eba\u4e4b\u5904\uff1f\u201d \u3000\u3000\u4f17\u4eba\u4e00\u770b\uff0c\u5374\u662f\u5d06\u5cd2\u7684\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u8fd9\u4e00\u51fa\u73b0\uff0c\u4f17\u4eba\u90fd\u77e5\u5251\u4f1a\u7acb\u5373\u5f00\u59cb\uff0c\u4e0d\u7531\u9000\u540e\u4e00\u6b65\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u8bdd\u65b9\u51fa\u53e3\uff0c\u6797\u5c11\u768b\u957f\u7b11\u4e00\u58f0\uff0c\u201c\u545b\u5577\u201d\u5b9d\u5251\u51fa\u624b\uff0c\u4ed6\u7528\u7684\u662f\u5de6\u624b\u5251\uff0c\u662f\u4ee5\u62db \u5f0f\u751a\u662f\u602a\u5f02\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u51b7\u7136\u4e00\u7b11\uff0c\u957f\u5251\u5e26\u4e00\u9053\u8679\u5149\uff0c\u659c\u622e\u5411\u6797\u5c11\u768b\u5de6\u808b\uff0c\u6797\u5c11\u768b\u4e00\u5f0f\u201c\u5c11\u9633\u518d\u5f15\u201d\uff0c \u5de6\u624b\u5251\u5f0f\u4e00\u541e\uff0c\u628a\u4e8e\u4e00\u98de\u653b\u52bf\u767b\u65f6\u7a92\u4f4f\uff01 \u3000\u3000\u4e8e\u4e00\u98de\u672c\u662f\u53d7\u5389\u9e57\u6307\u793a\u51fa\u6765\uff0c\u5df1\u6559\u4e86\u4ed6\u5236\u654c\u4e4b\u62db\uff0c\u662f\u4ee5\u5e76\u4e0d\u614c\u5fd9\uff0c\u957f\u5251\u7565\u6536\u5373\u653e\uff0c \u4ecd\u622e\u5411\u795e\u5251\u91d1\u9524\u6797\u5c11\u768b\u80f8\u524d\u7d2b\u5bab\u7a74\u9053\u3002 \u3000\u3000\u6797\u5c11\u768b\u539f\u5f0f\u4e0d\u53d8\uff0c\u53f3\u638c\u7a81\u6c89\uff0c\u4ee5\u201c\u5b54\u96c0\u5f00\u5c4f\u201d\u4e4b\u5f0f\u60f3\u5c01\u4f4f\u5251\u52bf\uff0c\u597d\u7528\u5de6\u624b\u5251\u53cd\u653b\u3002 \u90a3\u77e5\u4e8e\u4e00\u98de\u957f\u5251\u4e0d\u5288\uff0c\u518d\u529b\u731b\u622e\uff0c\u7a81\u5730\u5316\u4e3a\u201c\u5389\u98ce\u671d\u9633\u201d\u4e4b\u5f0f\uff0c\u6797\u5c11\u768b\u4e0d\u6599\u4e8e\u4e00\u98de\u7adf\u5982 \u6b64\u80c6\u5927\uff0c\u7528\u8d70\u4e2d\u5bab\u3001\u8e0f\u6d2a\u95e8\u7684\u62db\u5f0f\u653b\u6765\uff0c\u4e0d\u7531\u5fae\u5fae\u4e00\u60ca\uff0c\u8eab\u4f53\u500f\u5730\u4ef0\u5929\u7ffb\u4e0b\uff0c\u53f3\u638c\u5374\u7528 \u4e00\u62db\u201c\u62cd\u817f\u809a\u201d\u53cd\u62cd\u800c\u4e0a\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u62db\u5f0f\u8d70\u7a7a\uff0c\u654c\u5f0f\u5df1\u5230\uff0c\u5fd9\u8dc3\u5728\u7a7a\u4e2d\uff0c\u957f\u5251\u4e00\u9635\u9707\u52a8\uff0c\u731b\u70c8\u5411\u4e0b\u6233\u53bb\u5341\u4f59\u5251\u3002 \u3000\u3000\u6797\u5c11\u768b\u8eab\u5b50\u5df2\u6210\u6c34\u5e73\uff0c\u4e0d\u80fd\u518d\u95ea\uff0c\u84e6\u5730\u201c\u563f\u201d\u7684\u5f00\u6c14\u5410\u58f0\uff0c\u8db3\u8ddf\u9489\u7262\uff0c\u5de6\u624b\u5251\u5f0f\u5316 \u4f5c\u201c\u592a\u516c\u6492\u7f51\u201d\uff0c\u5728\u9762\u524d\u5e03\u6210\u4e00\u7247\u5149\u5e55\uff0c\u5251\u8eab\u6446\u52a8\u65f6\uff0c\u53ef\u89c1\u5176\u771f\u529b\u6ea2\u53d1\uff0c\u9690\u7ea6\u5e26\u6709\u98ce\u96f7 \u4e4b\u58f0\uff0c\u6562\u60c5\u662f\u60f3\u7528\u5185\u529b\u76f8\u62fc\u3002 \u3000\u3000\u53ea\u542c\u201c\u5bdf\u201d\uff0c\u201c\u5bdf\u201d\u6570\u54cd\uff0c\u4e8e\u4e00\u98de\u5251\u5b50\u201c\u6258\u201d\u7684\u88ab\u5f39\u8d77\u4e00\u5c3a\u591a\u9ad8\uff0c\u6797\u5c11\u768b\u5750\u7acb\u4e0d\u7a33\uff0c \u5fae\u9000\u534a\u6b65\uff0c\u7acb\u8d77\u8eab\u6765\u3002 \u3000\u3000\u540d\u5bb6\u4ea4\u624b\uff0c\u5230\u5e95\u4e0d\u51e1\uff0c\u5c24\u5176\u6797\u5c11\u768b\u5728\u5371\u4e2d\u6c42\u80dc\uff0c\u66f4\u7528\u5f97\u6f02\u4eae\u3002 \u3000\u3000\u56db\u5468\u89c2\u6218\u8005\u65e0\u4e00\u5eb8\u624b\uff0c\u81ea\u7136\u4e5f\u662f\u884c\u5bb6\uff0c\u770b\u5230\u6b64\u5904\uff0c\u4e0d\u7ea6\u800c\u540c\u5927\u58f0\u559d\u91c7\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u597d\u5bb9\u6613\u62a2\u5230\u4f18\u52bf\uff0c\u5374\u88ab\u6797\u5c11\u768b\u62a2\u56de\uff0c\u4e0d\u7531\u5fae\u611f\u6c14\u59a5\uff0c\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\u3002 \u3000\u3000\u6797\u5c11\u768b\u867d\u7136\u81ea\u77e5\u529f\u529b\u5728\u654c\u624b\u4e4b\u4e0a\uff0c\u4f46\u9274\u4e8e\u521a\u624d\u5931\u793c\u7684\u60c5\u666f\uff0c\u518d\u4e5f\u4e0d\u6562\u8f7b\u654c\uff0c\u56e0\u6b64\u4e0d \u613f\u5148\u53d1\u96be\u3002 \u3000\u3000\u4e8c\u4eba\u62b1\u5251\u51dd\u89c6\uff0c\u5f62\u52bf\u5927\u4e3a\u7d27\u5f20\uff0c\u6b63\u662f\u5f20\u5f29\u62d4\u5251\u4e4b\u52bf\u3002\u6b63\u5728\u8fd9\u65f6\uff0c\u5ffd\u7136\u4eba\u7fa4\u4e2d\u5237\u5730\u7a9c \u51fa\u4e00\u4e2a\u5e74\u7ea6\u516d\u65ec\u7684\u8001\u5e74\u4eba\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u505c\u624b\uff0c\u505c\u624b\uff01\u201d \u3000\u3000\u4e8e\u3001\u6797\u4e8c\u4eba\u6b63\u62b1\u5251\u5bf9\u7acb\uff0c\u867d\u542c\u89c1\u90a3\u8001\u4eba\u7684\u8bdd\uff0c\u5374\u8c01\u4e5f\u4e0d\u653e\u5206\u795e\u3002 \u3000\u3000\u90a3\u8001\u8005\u84e6\u5730\u51c4\u5389\u4e00\u58f0\u957f\u7b11\uff0c\u7f13\u6b65\u8d70\u5411\u573a\u4e2d\u3002\u4f17\u4eba\u89c1\u8fd9\u8001\u8005\u9762\u8c8c\u4e0d\u51e1\uff0c\u4f46\u5374\u88ab\u79cd\u96be\u4ee5 \u5f62\u5bb9\u7684\u8868\u60c5\u5f04\u5f97\u9762\u5bb9\u72f0\u72de\u53ef\u6016\u3002 \u3000\u3000\u5c24\u5176\u662f\u90a3\u4e00\u58f0\u602a\u7b11\uff0c\u9976\u662f\u5149\u5929\u5316\u65e5\uff0c\u4f17\u4eba\u90fd\u5fae\u611f\u5bd2\u610f\u3002\u8fd9\u65f6\uff0c\u4e8e\u3001\u6797\u4e8c\u4eba\u4e5f\u6536\u4f4f\u4e86 \u5251\uff0c\u90a3\u8001\u8005\u8fd9\u4e00\u51fa\u73b0\uff0c\u5012\u5f15\u8d77\u5168\u5cf0\u7684\u6ce8\u610f\u3002 \u3000\u3000\u8d64\u9633\u9053\u957f\u51b7\u563f\u4e00\u58f0\u9053\uff1a\u201c\u8001\u65bd\u4e3b\u4e5f\u60f3\u6765\u8bba\u5251\u4e48\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u84e6\u5730\u56de\u7740\uff0c\u5411\u8d64\u9633\u9053\u957f\u72e0\u6bd2\u4e00\u77aa\u3002 \u3000\u3000\u8d64\u9633\u9053\u4eba\u4f55\u7b49\u529f\u529b\uff0c\u4f46\u88ab\u8fd9\u8001\u4eba\u4e00\u77aa\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u51db\uff0c\u4e0d\u6562\u76f4\u89c6\u3002 \u3000\u3000\u987b\u77e5\u8d64\u9633\u5b50\u867d\u8eab\u5f52\u7384\u95e8\uff0c\u4f46\u4e00\u751f\u4e2d\u5374\u505a\u8fc7\u6570\u4ef6\u4e0d\u5149\u4e0d\u91c7\u7684\u4e8b\uff0c\u6240\u4ee5\u90a3\u8001\u8005\u7ffb\u773c\u4e00\u77aa\uff0c \u5012\u6709\u70b9\u4f7f\u4ed6\u5fc3\u865a\uff01 \u3000\u3000\u90a3\u8001\u8005\u84e6\u5730\u56de\u9996\uff0c\u5472\u7259\u51b2\u7740\u5389\u9e57\u602a\u7b11\uff0c\u811a\u4e0b\u4e14\u4e00\u6b65\u6b65\u8d70\u5411\u5389\u9e57\u505c\u8eab\u5904\u3002 \u3000\u3000\u90a3\u8001\u8005\u597d\u751f\u79bb\u5947\uff0c\u4e3e\u52a8\u4f3c\u6709\u4e9b\u5931\u5e38\uff0c\u5389\u9e57\u89c1\u4ed6\u671d\u81ea\u5df1\u8d70\u6765\uff0c\u5fc3\u4e2d\u867d\u662f\u4e0d\u60a6\uff0c\u5374\u4e5f\u6709 \u4e9b\u53d1\u6bdb\u3002 \u3000\u3000\u4f46\u5389\u9e57\u65e2\u79f0\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u201d\uff0c\u54ea\u80fd\u9732\u51fa\u4e1d\u6beb\u6015\u610f\uff0c\u662f\u4ee5\u4e5f\u76f4\u773c\u671b\u7740\u8001\u8005\u3002 \u3000\u3000\u90a3\u8001\u8005\u6765\u5230\u8fd1\u5904\uff0c\u84e6\u5730\u4e00\u7acb\uff0c\u8138\u4e0a\u5fae\u5fae\u4e00\u9635\u62bd\u564e\uff0c\u53cc\u76ee\u4e2d\u9690\u7ea6\u7684\u9732\u51fa\u4e00\u80a1\u6bd2\u5149\uff01\u5389 \u9e57\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8001\u8005\u5374\u542f\u53e3\u9053\uff1a\u201c\u4f60\u8001\u4fbf\u662f\u5251\u795e\u5389\u9e57\u5427\uff1f\u201d \u3000\u3000\u5389\u9e57\u4f55\u7b49\u5014\u50b2\uff0c\u51b7\u7136\u4e0d\u8bed\uff0c\u633a\u76f4\u7684\u8eab\u5b50\u52a8\u4e5f\u4e0d\u52a8\uff0c\u4ec5\u6709\u4e00\u9897\u9996\u7ea7\u5fae\u5fae\u4e0b\u6c89\u4e00\u4e0b\uff0c\u53c8 \u6062\u590d\u539f\u72b6\uff0c\u7b97\u662f\u56de\u7b54\u3002 \u3000\u3000\u90a3\u8001\u8005\u84e6\u5730\u53c8\u662f\u4e00\u9635\u51c4\u7136\u957f\u7b11\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u5341\u56db\u5e74\u4e86\uff0c\u8001\u8872\u65e0\u65f6\u65e0\u523b\u4e0d\u8bb0\u5f97\u4f60\uff01\u201d \u3000\u3000\u81ea\u90a3\u8001\u8005\u51fa\u73b0\u540e\uff0c\u4f17\u4eba\u90fd\u9ed8\u9ed8\u8be7\u89c6\uff0c\u662f\u4ee5\u56db\u5468\u751a\u662f\u6c89\u9759\uff0c\u90a3\u8001\u8005\u8fd9\u4e00\u58f0\u602a\u7b11\uff0c\u6709\u82e5 \u602a\u67ad\u557c\u54ed\uff0c\u5728\u9759\u5bc2\u4e2d\u8361\u8d77\u4f17\u4eba\u7684\u5fc3\u5f26\uff0c\u90fd\u4e0d\u89c9\u8eab\u611f\u5bd2\u610f\u3002 \u3000\u3000\u5389\u9e57\u770b\u90a3\u8001\u8005\u7684\u53e3\u6c14\u50cf\u662f\u548c\u81ea\u5df1\u6709\u7740\u4ec0\u4e48\u8840\u6d77\u6df1\u4ec7\u4f3c\u7684\uff0c\u4f46\u81ea\u5df1\u600e\u4e48\u4e5f\u4e0d\u80fd\u591f\u8bb0\u8d77 \u66fe\u7ecf\u8bc6\u5f97\u8fd9\u4e48\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u8001\u4eba\u84e6\u5730\u91cc\u4f38\u51fa\u53f3\u624b\uff0c\u9012\u5230\u5389\u9e57\u9762\u524d\uff0c\u53e3\u4e2d\u5374\u602a\u58f0\u9053\uff1a\u201c\u4f60\u770b\u770b\u6211\u7684\u624b\u2014\u2014\u201d \u3000\u3000\u5389\u9e57\u5012\u4ee5\u4e3a\u4ec0\u4e48\u4ec7\u6068\u5168\u5173\u8fd9\u4e00\u53ea\u624b\uff0c\u4e0d\u7981\u4f4e\u9996\u6ce8\u89c6\u7740\uff0c\u5374\u5e76\u6ca1\u6709\u53d1\u73b0\u4efb\u4f55\u602a\u5f02\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8001\u4eba\u5de6\u624b\u4e00\u7ffb\uff0c\u4e00\u67c4\u5315\u9996\u9732\u51fa\u8863\u8896\uff0c\u987a\u52bf\u4e00\u9001\uff0c\u63d2\u5411\u5389\u9e57\u8179\u90e8\u3002 \u3000\u3000\u5389\u9e57\u5168\u88ab\u4e00\u652f\u53f3\u624b\u6240\u5438\u5f15\uff0c\u4f46\u5374\u5e76\u6ca1\u6709\u53d1\u73b0\u4ec0\u4e48\u602a\u5f02\u53ef\u6016\u4e4b\u5904\uff0c\u4ed6\u662f\u4f55\u7b49\u8001\u7ec3\uff0c\u60c5 \u77e5\u5fc5\u662f\u4e0a\u5f53\u3002 \u3000\u3000\u8001\u4eba\u7684\u5315\u9996\u53ea\u79bb\u5389\u9e57\u4e0d\u5230\u4e09\u5bf8\uff0c\u5389\u9e4a\u84e6\u5730\u7ffb\u8155\u4e00\u5212\u3002 \u3000\u3000\u8fd9\u62db\u201c\u7384\u4e4c\u5212\u6c99\u201d\u5389\u9e4a\u5728\u8fd9\u7b49\u5371\u5883\u4f7f\u51fa\uff0c\u4e14\u5939\u4e0a\u4e86\u201c\u91d1\u521a\u638c\u529b\u201d\uff0c\u53ea\u542c\u5f97\u201c\u5494\u6298\u201d \u4e00\u58f0\uff0c\u5315\u9996\u7adf\u81ea\u4ed6\u624b\u6307\u62c2\u5904\u6298\u65ad\u3002 \u3000\u3000\u84e6\u5730\u53c8\u662f\u4e00\u6761\u4eba\u5f71\u51b2\u51fa\uff0c\u770b\u6837\u5b50\u662f\u60f3\u534f\u52a9\u8001\u4eba\uff0c\u5389\u9e57\u51b7\u54fc\u4e00\u58f0\uff0c\u53f3\u624b\u4e00\u5410\uff0c\u4e00\u58f0\u60e8 \u53eb\uff0c\u90a3\u8001\u4eba\u5df2\u88ab\u6253\u51fa\u4e00\u4e08\u4ee5\u5916\u3002 \u3000\u3000\u90a3\u5728\u7a7a\u4e2d\u7684\u4eba\u6765\u4e0d\u53ca\u6551\u52a9\uff0c\u53ea\u6025\u5f97\u5927\u53f1\u4e00\u58f0\uff0c\u5237\u5730\u5012\u7a9c\u4e0b\u6765\uff0c\u6276\u8d77\u5c06\u8981\u5012\u5730\u7684\u8001\u4eba\u3002 \u3000\u3000\u603b\u7b97\u5389\u9e57\u624b\u4e0b\u7559\u4e86\u60c5\uff0c\u8001\u4eba\u53ea\u5410\u51fa\u4e8c\u53e3\u9c9c\u8840\uff0c\u4ecd\u80fd\u52c9\u5f3a\u7acb\u5728\u5730\u4e0a\u3002 \u3000\u3000\u56db\u5468\u7684\u82f1\u8c6a\u90fd\u4e3a\u8fd9\u7a81\u8d77\u7684\u4e8b\u6545\u60ca\u5f97\u5446\u4e86\uff0c\u53cd\u800c\u6b62\u4f4f\u4e86\u5608\u6742\u7684\u60ca\u547c\u3002 \u3000\u3000\u5389\u9e57\u867d\u9003\u8fc7\u5927\u9669\uff0c\u4f46\u5374\u4e5f\u60ca\u51fa\u4e00\u8eab\u51b7\u6c57\uff0c\u6012\u6c14\u52c3\u52c3\u5730\u8bf4\u9053\uff1a\u201c\u8001\u592b\u4e0e\u9601\u4e0b\u65e0\u6028\u65e0\u4ec7\uff0c \u4f55\u4ee5\u8981\u4e0b\u6b64\u7b49\u6bd2\u624b\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u52c9\u5f3a\u559d\u9053\uff1a\u201c\u5389\u8d3c\uff0c\u6211\u4e0e\u4f60\u8a93\u4e0d\u4e24\u7acb\uff0c\u4e0d\u5171\u6234\u5929\uff0c\u8fd8\u8bf4\u6ca1\u6709\u6028\u4ec7\u2014\u2014\u201d\u800c\u540e \u53c8\u5583\u5583\u81ea\u8bed\u4e00\u9635\uff0c\u518d\u559d\u9053\uff1a\u201c\u4eca\u65e5\u5929\u4e0b\u82f1\u96c4\u6bd5\u96c6\uff0c\u8001\u592b\u5982\u4e0d\u628a\u4f60\u7684\u8d3c\u76d7\u884c\u4e3a\u6296\u51fa\u6765\uff0c\u6b7b \u4e5f\u4e0d\u80fd\u7791\u76ee\uff01\u201d \u3000\u3000\u8bf4\u7740\u53c8\u4f3c\u53d1\u72c2\u822c\u5bf9\u5389\u9e57\u7b49\u9053\uff1a\u201c\u8001\u8d3c\uff0c\u5341\u4e94\u5e74\u524d\u5929\u7ec5\u7011\u524d\u7684\u4e8b\u4f60\u4eec\u8fd8\u8bb0\u5f97\u4e48\uff1f\u563f\u563f\uff01 \u4f60\u4eec\u90fd\u662f\u5927\u82f1\u96c4\uff0c\u8fd9\u7b49\u5c0f\u4e8b\u6015\u65e9\u5df2\u5fd8\u4e86\uff0c\u8001\u8872\u4f59\u5fe0\u5bf9\u5f53\u65f6\u60c5\u5f62\u5374\u662f\u5386\u5386\u5982\u5728\u76ee\u524d\uff01\u53ef\u601c \u6211\u90a3\u4e3b\u4eba\u60e8\u6b7b\uff0c\u5341\u51e0\u5e74\u6765\u5374\u8ba9\u4f60\u4eec\u6d88\u9065\u6cd5\u5916\u3002\u5929\u53ef\u601c\u89c1\uff0c\u4eca\u65e5\u6211\u4e3b\u4eba\u540e\u4ee3\u957f\u6210\uff0c\u6211\u53ea\u6068 \u65b9\u624d\u6ca1\u6709\u523a\u6b7b\u4f60\u8fd9\u8001\u8d3c\uff0c\u4f46\u662f\u81ea\u6709\u53d6\u4f60\u547d\u4e4b\u4eba\u2014\u2014\u201d \u3000\u3000\u7fa4\u8c6a\u4e00\u542c\u539f\u6765\u662f\u5929\u7ec5\u7011\u524d\u7684\u4e8b\uff0c\u987f\u65f6\u8054\u60f3\u5230\u5341\u4e94\u5e74\u524d\u4e2d\u5dde\u602a\u6770\u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\u7684\u4e00 \u6bb5\u516c\u6848\uff0c\u4e0d\u89c9\u7acb\u65f6\u5bc2\u9759\u4e0b\u6765\u3002\u5389\u9e57\u60f3\u5df2\u77e5\u9053\u662f\u4f55\u4e8b\uff0c\u8138\u8272\u4e0d\u7531\u94c1\u9752\u3002 \u3000\u3000\u8981\u77e5\u6614\u5e74\u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\u60e8\u6b7b\u5929\u7ec5\u7011\u524d\uff0c\u5929\u4e0b\u867d\u65e0\u4eba\u4e0d\u77e5\uff0c\u4f46\u660e\u767d\u5176\u4e2d\u7ec6\u8282\u7684\u5374\u5c11 \u4e4b\u53c8\u5c11\u3002 \u3000\u3000\u8001\u8005\u89c1\u7fa4\u8c6a\u9759\u4e86\u4e0b\u6765\uff0c\u7528\u6781\u5176\u602a\u5f02\u800c\u53c8\u6781\u5e73\u9759\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u8001\u8872\u4f59\u5fe0\u672c\u662f\u5434\u5927\u4fa0 \u5434\u8bcf\u4e91\u7684\u5bb6\u4ec6\uff0c\u5341\u4e94\u5e74\u524d\uff0c\u4e94\u5927\u5b97\u6d3e\u904d\u9080\u6b66\u6797\u540c\u9053\u8d74\u5cb1\u5b97\u8bba\u5251\uff0c\u90a3\u65f6\u5434\u5927\u4fa0\u5e74\u65b9\u56db\u5341\u4f59 \u5c81\uff0c\u81ea\u662f\u4e0d\u7518\u793a\u5f31\uff0c\u4fbf\u51c6\u5907\u51fa\u53d1\u8d74\u4f1a\uff01\u201d \u3000\u3000\u201c\u90a3\u65f6\u5434\u5927\u4fa0\u6709\u4e00\u4e2a\u5feb\u4e50\u7684\u5bb6\u5ead\u548c\u516d\u5c81\u5927\u7684\u513f\u5b50\uff0c\u4e0d\u5e78\u5434\u592b\u4eba\u5374\u5728\u751f\u5b50\u540e\u7b2c\u4e8c\u5e74\u6b7b \u53bb\u3002\u4e8e\u662f\u7167\u987e\u513f\u5b50\u7684\u5de5\u4f5c\u4fbf\u7531\u6211\u4f59\u5fe0\u529e\u7406\uff0c\u90a3\u5b69\u5b50\u6d3b\u6cfc\u806a\u660e\u4e4b\u6781\uff0c\u786e\u4e0d\u6127\u4e3a\u5434\u5bb6\u540e\u4ee3\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u75db\u82e6\u7684\u8138\u4e0a\u6e10\u6e10\u9732\u51fa\u4e00\u4e1d\u7b11\u5bb9\uff0c\u50cf\u662f\u5728\u56de\u5fc6\u7740\u90a3\u6614\u65e5\u7684\u65f6\u5149\u3002 \u3000\u3000\u7fa4\u8c6a\u4e3b\u8981\u662f\u8981\u542c\u5434\u8bcf\u4e91\u7684\u6b7b\u56e0\uff0c\u8fd9\u65f6\u5374\u542c\u90a3\u4f59\u5fe0\u5c3d\u8bf4\u4e9b\u4e0d\u5173\u8981\u7d27\u7684\u8bdd\uff0c\u4e0d\u89c9\u5fae\u5fae\u8be7 \u5f02\uff0c\u4f46\u4e5f\u65e0\u4eba\u51fa\u58f0\u3002 \u3000\u3000\u90a3\u4f59\u5fe0\u987f\u4e86\u4e00\u987f\uff0c\u9661\u7136\u5927\u58f0\u5bf9\u5389\u9e57\u7b49\u9053\uff1a\u201c\u8001\u8d3c\uff0c\u4f60\u770b\u6e05\u695a\u70b9\u7f62\uff0c\u8fd9\u4fbf\u662f\u5434\u5bb6\u7684\u540e \u4ee3\u5434\u51cc\u98ce\uff0c\u4e5f\u4fbf\u662f\u4f60\u4eec\u7684\u50ac\u547d\u8005\u2014\u2014\u201d\u8bf4\u7740\u4e00\u6307\u8eab\u8fb9\u6276\u4f4f\u4ed6\u7684\u5c11\u5e74\u3002 \u3000\u3000\u5389\u9e57\u8138\u8272\u94c1\u9752\uff0c\u53f3\u624b\u5df2\u6309\u5728\u5251\u67c4\u4e0a\uff0c\u4f46\u4ee5\u4ed6\u7684\u8eab\u4efd\uff0c\u5c82\u80fd\u591f\u5728\u4f17\u76ee\u70af\u70af\u4e0b\u4e00\u518d\u5411\u4e00 \u4e2a\u6b66\u6280\u6781\u4f4e\u7684\u8001\u4eba\u4e0b\u6bd2\u624b\uff1f \u3000\u3000\u4f59\u5fe0\u60f3\u662f\u795e\u60c5\u592a\u5df1\u6fc0\u52a8\uff0c\u5fcd\u4e0d\u4f4f\u5f20\u53e3\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\uff0c\u98a4\u6296\u7740\u9053\uff1a\u201c\u5434\u5927\u4fa0\u53f7\u79f0\u5355\u5251 \u65ad\u9b42\uff0c\u4e94\u5927\u6d3e\u6709\u54ea\u4e00\u4e2a\u80fd\u591f\u662f\u4ed6\u5bf9\u624b\uff1f\u662f\u4ee5\u5434\u5927\u4fa0\u81ea\u8d1f\u5f97\u7d27\uff0c\u5b64\u8eab\u524d\u5f80\uff0c\u8fde\u5175\u5668\u2018\u65ad\u9b42 \u795e\u5251\u2019\u4e5f\u90fd\u6ca1\u6709\u5e26\u53bb\uff01\u201d \u3000\u3000\u201c\u4e94\u5927\u5b97\u6d3e\u7684\u672c\u610f\u5927\u6982\u4ee5\u4e3a\u5434\u5927\u4fa0\u7edd\u4e0d\u4f1a\u8d74\u7ea6\uff0c\u90a3\u77e5\u5434\u5927\u4fa0\u8840\u6c14\u65b9\u521a\uff0c\u771f\u7684\u5982\u65f6\u62b5 \u8fbe\u3002\u4ed6\u8fd9\u4e00\u5230\uff0c\u5929\u4e0b\u7b2c\u4e00\u5251\u5fc5\u662f\u975e\u4ed6\u83ab\u5c5e\u4e86\u3002\u4e94\u5927\u5b97\u6d3e\u8d77\u4e86\u6050\u614c\uff0c\u4e8e\u662f\u4fbf\u60f3\u51fa\u4e00\u4e2a\u6781\u5176 \u5351\u9119\u7684\u624b\u6bb5\uff01\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u795e\u60c5\u751a\u662f\u6fc0\u52a8\uff0c\u54ac\u7259\u5207\u9f7f\uff0c\u6124\u6168\u5df2\u6781\uff01 \u3000\u3000\u201c\u5f53\u65f6\u8001\u8872\u548c\u5434\u5927\u4fa0\u4e00\u540c\u51fa\u5c71\uff0c\u5434\u51cc\u98ce\u5bc4\u6258\u5728\u4e00\u4e2a\u53cb\u4eba\u5bb6\u4e2d\u3002\u90a3\u662f\u5251\u4f1a\u7684\u524d\u4e00\u5929\uff0c \u5434\u5927\u4fa0\u548c\u6211\u4e00\u540c\u5728\u5929\u7ec5\u7011\u4e0b\u6563\u6b65\uff0c\u4e94\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u4e00\u9f50\u6765\u5230\uff0c\u5434\u5927\u4fa0\u4f3c\u4e0d\u613f\u6211\u5728\u8eab\u8fb9\uff0c \u4fbf\u53eb\u6211\u7acb\u5230\u4e00\u65c1\u53bb\uff0c\u4f46\u8001\u8872\u600e\u80fd\u653e\u5f97\u4e0b\u5fc3\uff0c\u662f\u4ee5\u8fdf\u8fdf\u4e0d\u80af\u8d70\u5f00\uff0c\u5434\u5927\u4fa0\u89c1\u4e94\u5927\u638c\u95e8\u4eba\u5df2 \u8fd1\uff0c\u5411\u6211\u559d\u9053\uff1a\u2018\u4f60\u82e5\u8ba4\u6211\u662f\u4e3b\u4eba\uff0c\u5c31\u5feb\u5feb\u79bb\u5f00\u3002\u2019\u6211\u53ea\u597d\u8eb2\u5728\u4e00\u65c1\u7684\u5927\u77f3\u7f1d\u4e2d\u3002\u201d \u3000\u3000\u201c\u4e94\u6d3e\u7684\u4eba\u624b\u662f\u5389\u9e57\u3001\u8d64\u9633\u3001\u82e6\u5eb5\u3001\u8c22\u661f\u548c\u51cc\u7a7a\u6b65\u865a\u5353\u817e\u5353\u5927\u4fa0\uff01\u54c8\u54c8\uff01\u6211\u6ca1\u6709\u8bb0 \u9519\u5427\uff01\u201d \u3000\u3000\u5389\u9e57\u51b7\u7136\u54fc\u4e86\u4e00\u58f0\uff0c\u5fc3\u4e2d\u5374\u5728\u60f3\u5982\u4f55\u5236\u6b62\u4ed6\u8bf4\u51fa\u6765\u3002 \u3000\u3000\u4f59\u5fe0\u7ee7\u7eed\u9053\uff1a\u201c\u5434\u5927\u4fa0\u5f88\u5ba2\u6c14\u7684\u8fce\u7740\u4e94\u4eba\uff0c\u4e94\u4eba\u5374\u975e\u8981\u5206\u80dc\u8d1f\u4e0d\u53ef\uff01\u8001\u592b\u5f53\u65e5\u82e5\u975e \u542c\u4e3b\u4eba\u8bdd\u8eb2\u4e86\u8d77\u6765\uff0c\u5fc5\u4e5f\u906d\u4e86\u6bd2\u624b\uff0c\u5c82\u80fd\u6b64\u523b\u6765\u6296\u9732\u4f60\u4eec\u7684\u81ed\u53f2\uff1f\u201d \u3000\u3000\u201c\u4f17\u4eba\u8bf4\u4e2a\u4e0d\u4e86\uff0c\u7ec8\u4e8e\u8bf4\u50f5\u52a8\u624b\uff0c\u82e6\u5eb5\u4e0a\u4eba\u9996\u5148\u8bf4\u51fa\u6597\u5185\u529b\uff0c\u5434\u5927\u4fa0\u81ea\u7136\u7b54\u5e94\uff01\u201d \u3000\u3000\u201c\u4f46\u6bd4\u6b66\u7684\u65b9\u6cd5\u662f\u4e94\u4eba\u4e2d\u9009\u51fa\u56db\u4eba\u548c\u5434\u5927\u4fa0\u4e00\u4eba\u5bf9\u638c\uff0c\u53e6\u4e00\u4eba\u5728\u65c1\u505a\u88c1\u5224\uff0c\u4ee5\u5345\u6570 \u4e3a\u8ba1\uff0c\u54fc\uff0c\u771f\u516c\u5e73\uff01\u201d \u3000\u3000\u201c\u5434\u5927\u4fa0\u4e0d\u77e5\u5bf9\u65b9\u9b3c\u8ba1\uff0c\u50b2\u7136\u51fa\u638c\uff0c\u4e94\u4eba\u4e2d\u53ea\u6709\u5353\u817e\u672a\u51fa\u638c\uff0c\u5728\u4e00\u65c1\u8ba1\u6570\u3002\u6570\u5230\u7b2c \u5eff\u4e0b\u65f6\uff0c\u5434\u5927\u4fa0\u5df2\u5fae\u5c45\u4e0b\u98ce\u3002\u201d \u3000\u3000\u201c\u987b\u77e5\u5345\u4e0b\u4e3a\u65f6\u867d\u6682\uff0c\u4f46\u662f\u56db\u4e2a\u638c\u95e8\u4eba\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u5185\u5bb6\u9ad8\u624b\uff0c\u5434\u5927\u4fa0\u53c8\u80fd\u6301\u5f97\u4f4f \u5b9e\u5df2\u4e0d\u51e1\u3002\u201d \u3000\u3000\u201c\u5434\u5927\u4fa0\u84e6\u5730\u53cc\u76ee\u4e00\u7741\uff0c\u5185\u529b\u9661\u957f\uff0c\u987f\u65f6\u6273\u56de\u5e73\u5c40\u3002\u8fd9\u65f6\u5df2\u6570\u5230\u5eff\u4e00\u3001\u4e8c\uff0c\u5353\u817e\u5353 \u5927\u4fa0\u597d\u50cf\u6709\u4ec0\u4e48\u4e8b\u4e0d\u80fd\u8003\u8651\u51b3\u5b9a\uff0c\u8138\u4e0a\u9634\u6674\u4e0d\u5b9a\uff0c\u4f46\u7ec8\u4e8e\u505a\u4e00\u4e2a\u575a\u51b3\u7684\u8868\u60c5\uff0c\u521a\u597d\u8fd9\u65f6 \u4e5f\u6570\u5230\u7b2c\u5345\uff0c\u5f53\u65f6\u6211\u4e0d\u61c2\u4e3a\u4ec0\u4e48\u5353\u817e\u4f1a\u505a\u51fa\u8fd9\u4e2a\u8868\u60c5\uff0c\u4f46\u540e\u6765\u6211\u660e\u767d\u539f\u6765\u662f\u4e94\u4eba\u7684\u9b3c\u8ba1\uff0c \u60f3\u4e58\u5434\u5927\u4fa0\u6b63\u5728\u5168\u529b\u4f7f\u4e3a\u7684\u65f6\u5019\uff0c\u7531\u5353\u817e\u5077\u88ad\uff0c\u4f46\u5353\u817e\u5012\u5e95\u662f\u6b63\u9053\u4e2d\u4eba\uff0c\u6ca1\u6709\u4f5c\u51fa\u6b64 \u4e8b\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u7fa4\u8c6a\u90fd\u60ca\u547c\u4e00\u58f0\uff0c\u5389\u9e57\u7b49\u4eba\u90fd\u94c1\u9752\u7740\u8138\uff0c\u4e0d\u53d1\u4e00\u8a00\uff01 \u3000\u3000\u8001\u4eba\u5374\u7ee7\u7eed\u9053\uff1a\u201c\u5434\u5927\u4fa0\u4e00\u8dc3\u800c\u8d77\u9053\uff1a\u2018\u5185\u529b\u5df1\u9886\u6559\uff0c\u4e0d\u77e5\u4e94\u4f4d\u8fd8\u8981\u8d50\u6559\u4e9b\u4ec0 \u4e48\uff1f\u2019\u201d \u3000\u3000\u201c\u5389\u9e57\u7b49\u4eba\u5927\u6982\u662f\u4e0d\u6ee1\u5353\u817e\u6ca1\u6709\u5b9e\u884c\u4ed6\u4eec\u8fd9\u79cd\u4e0b\u6d41\u7684\u52fe\u5f53\uff0c\u662f\u4ee5\u90fd\u72e0\u77aa\u4e86\u4ed6\u4e00\u773c\uff01\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u7fa4\u8c6a\u90fd\u53d1\u51fa\u60ca\u547c\uff0c\u4f46\u5927\u90fd\u4e0d\u80fd\u7f6e\u4fe1\u3002 \u3000\u3000\u4f59\u5fe0\u7684\u4e2d\u6c14\u8d8a\u53d1\u8870\u5f31\uff0c\u5434\u51cc\u98ce\u7f13\u7f13\u5730\u62cd\u7740\u4ed6\u7684\u80cc\uff0c\u8f7b\u8f7b\u5524\u4ed6\u4e0d\u8981\u518d\u52c9\u5f3a\u652f\u6491\u4e0b\u53bb\u3002 \u3000\u3000\u4f59\u5fe0\u5fae\u5fae\u6447\u5934\uff0c\u7528\u66f4\u5fae\u5f31\u4e00\u4e9b\u7684\u58f0\u97f3\u9053\uff1a\u201c\u5353\u817e\u8138\u8272\u5fae\u53d8\uff0c\u4f46\u5389\u9e57\u5374\u8f6c\u5411\u5434\u5927\u4fa0\uff0c \u8981\u6c42\u6bd4\u8bd5\u5251\u672f\uff0c\u5434\u5927\u4fa0\u53f7\u79f0\u6cb3\u6d1b\u4e00\u5251\u548c\u5355\u5251\u65ad\u9b42\uff0c\u5176\u5251\u4e0a\u9020\u8be3\u53ef\u60f3\u800c\u77e5\uff0c\u5f53\u7136\u4e94\u4f4d\u638c\u95e8 \u4eba\u4e5f\u4e0d\u4f1a\u758f\u5ffd\u8fd9\u4e00\u70b9\uff0c\u662f\u4ee5\u4e94\u4eba\u5408\u51fb\uff01\u5434\u5927\u4fa0\u65ad\u9b42\u5251\u5e76\u4e0d\u5728\u8eab\uff0c\u4ec5\u524a\u4e00\u6839\u6811\u679d\u505a\u5251\uff0c\u548c \u4e94\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u65af\u6740\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e00\u6218\u662f\u8001\u8872\u4e00\u751f\u7684\u4ec5\u89c1\u7684\u6076\u6597\uff0c\u4e94\u4eba\u6240\u5e03\u7684\u5251\u9635\u751a\u662f\u602a\u5f02\uff0c\u597d\u50cf\u4e13\u95e8\u662f\u5b88\uff0c\u4f46 \u5374\u5b88\u5f97\u6709\u5982\u91d1\u6876\u94c1\u58c1\uff01\u201d \u3000\u3000\u201c\u5434\u5927\u4fa0\u5403\u4e8f\u5728\u5b9d\u5251\u4e0d\u5728\u624b\u4e2d\uff0c\u4e00\u679d\u6811\u679d\u7a76\u7adf\u6709\u6240\u987e\u5fcc\uff0c\u662f\u4ee5\u5f88\u60f3\u62a2\u593a\u4e94\u4eba\u4e4b\u4e00\u7684 \u5175\u5203\uff0c\u4f46\u4e94\u4eba\u7684\u529f\u592b\u90fd\u662f\u4e00\u7b49\u4e00\u7684\uff0c\u90a3\u4f1a\u4e0a\u5f53\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4f59\u5fe0\u7684\u58f0\u97f3\u4f59\u53d1\u5fae\u5f31\uff0c\u773c\u770b\u662f\u5185\u4f24\u8f6c\u91cd\uff0c\u5434\u51cc\u98ce\u6b63\u8981\u542f\u53e3\u8bf7\u4ed6\u4f11\u606f\u4e00\u4f1a\uff0c \u4f59\u5fe0\u5374\u5931\u58f0\u53eb\u9053\uff1a\u201c\u8ba9\u6211\u8bf4\u4e0b\u53bb\uff01\u8ba9\u6211\u8bf4\u4e0b\u53bb\uff01\u201d\u8138\u4e0a\u808c\u8089\u4e00\u9635\u62bd\u52a8\uff0c\u5f62\u76f8\u72f0\u72de\u53ef\u6016\u3002 \u5434\u51cc\u98ce\u62ac\u5934\u671b\u4e86\u671b\u5389\u9e57\u3001\u8d64\u9633\u3001\u82e6\u5eb5\uff0c\u53ea\u89c1\u4ed6\u4eec\u90fd\u9677\u4e8e\u6c89\u601d\u4e2d\uff0c\u8138\u4e0a\u94c1\u9752\uff0c\u6beb\u65e0\u8868\u60c5\u3002 \u3000\u3000\u84e6\u5730\u5389\u9e57\u5fae\u4e00\u6446\u624b\uff0c\u90a3\u5d06\u5cd2\u95e8\u4eba\u53f2\u548c\u5eb7\u4f1a\u610f\uff0c\u6392\u4f17\u800c\u51fa\uff0c\u4f59\u5fe0\u4f55\u7b49\u7ecf\u9a8c\uff0c\u5df2\u77e5\u5fc5\u662f \u5389\u9e4a\u4e0d\u4fbf\u4eb2\u81ea\u51fa\u624b\uff0c\u662f\u4ee5\u6253\u53d1\u5f92\u513f\u60f3\u6740\u53bb\u81ea\u5df1\uff0c\u5fc3\u4e2d\u6012\u706b\u9ad8\u70e7\uff0c\u72c2\u53f1\u4e00\u58f0\uff0c\u77aa\u7740\u53f2\u548c \u5eb7\u2026\u2026\u53f2\u548c\u5eb7\u89c1\u4f59\u5fe0\u6ee1\u76ee\u7ea2\u4e1d\uff0c\u72e0\u72e0\u77aa\u7740\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u53d1\u6bdb\uff0c\u84e6\u5730\u4f59\u5fe0\u8eab\u65c1\u5434\u51cc\u98ce\u66b4 \u53f1\u4e00\u58f0\uff1a\u201c\u4f4f\u624b\uff01\u201d\u66f4\u89c9\u6b63\u6c14\u51db\u7136\uff0c\u5fc3\u4e2d\u4e00\u865a\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\uff0c\u957f\u5251\u843d\u5730\uff01 \u3000\u3000\u4f59\u5fe0\u89c1\u53f2\u548c\u5eb7\u957f\u5251\u843d\u5730\uff0c\u4e5f\u4e0d\u7981\u6ee1\u610f\u7684\u5e72\u7b11\u4e00\u4e0b\u3002 \u3000\u3000\u53f2\u548c\u5eb7\u5fc3\u4e2d\u867d\u7136\u7f9e\u6127\u96be\u5f53\uff0c\u4f46\u4e5f\u4e0d\u597d\u8bf4\u4ec0\u4e48\uff0c\u7adf\u50f5\u7ad9\u5728\u90a3\u513f\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u7fa4\u8c6a\u90fd\u770b\u5728\u773c\u4eba\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5bf9\u5389\u9e57\u5927\u8d77\u53cd\u611f\uff0c\u4f59\u5fe0\u53c8\u5e72\u54b3\u4e00\u58f0\uff0c\u624d\u5f00\u53e3\u9053\uff1a \u201c\u5434\u5927\u4fa0\u8fde\u8bd5\u51e0\u6b21\uff0c\u90fd\u4e0d\u80fd\u62a2\u5230\u4e00\u67c4\u5251\uff0c\u8001\u8872\u5f53\u65f6\u5fc3\u6025\u5982\u711a\uff0c\u771f\u60f3\u4e0a\u524d\u76f8\u52a9\uff01\u201d \u3000\u3000\u201c\u84e6\u5730\u90a3\u5353\u817e\u4f3c\u662f\u4e0d\u5fcd\uff0c\u62db\u5f0f\u5fae\u5fae\u4e00\u653e\uff0c\u8001\u8872\u529f\u592b\u867d\u662f\u4e0d\u6210\uff0c\u4f46\u4e5f\u770b\u5f97\u51fa\u90a3\u662f\u6709\u610f \u7684\uff0c\u5434\u5927\u4fa0\u826f\u673a\u5c82\u53ef\u9519\u8fc7\uff1f\u4e00\u95ea\u4fbf\u51fa\u4e86\u5251\u9635\u3002\u201d \u3000\u3000\u201c\u521a\u597d\u8fd9\u65f6\u8d64\u9633\u9053\u58eb\u4e00\u5251\u524a\u6765\uff0c\u5434\u5927\u4fa0\u65e9\u5df1\u51fa\u9635\uff0c\u90a3\u8fd8\u628a\u4ed6\u653e\u5728\u773c\u4e2d\uff0c\u53ea\u4e00\u4f38\u624b\u62cd \u5728\u8d64\u9633\u8098\u4e0a\uff0c\u4fbf\u593a\u4e0b\u4e00\u67c4\u957f\u5251\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u6b64\u65f6\u90fd\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u4e0d\u7ea6\u800c\u540c\u5411\u8d64\u9633\u9053\u58eb\u770b\u53bb\uff0c\u8d64\u9633\u9053\u58eb\u82cd\u8001\u800c\u767d\u7699\u7684\u8138\u4e0a\uff0c \u4e5f\u4e0d\u7531\u5fae\u5fae\u6cdb\u51fa\u7ea2\u6f6e\u6765\uff01 \u3000\u3000\u201c\u5434\u5927\u4fa0\u6709\u5251\u5728\u8eab\uff0c\u5982\u864e\u6dfb\u7ffc\uff0c\u90a3\u77e5\u90a3\u56de\u98ce\u5251\u5ba2\u8c22\u661f\u7a81\u5730\u4e00\u5251\u523a\u5411\u90a3\u6606\u4ed1\u7684\u51cc\u7a7a\u6b65 \u865a\u5353\u817e\uff01\u201d \u3000\u3000\u5353\u817e\u6b64\u523b\u4e5f\u77e5\u81ea\u5df1\u5012\u6208\u5df2\u88ab\u4ed6\u4eba\u770b\u51fa\uff0c\u4e0d\u597d\u89e3\u91ca\uff0c\u53ea\u597d\u4e0a\u5251\u5c01\u53bb\uff0c\u6b64\u65f6\u4ed6\u8eab\u4fa7\u7684\u82e6 \u5eb5\u4e0a\u4eba\u5374\u4e5f\u638c\u5251\u9f50\u4f7f\uff0c\u914d\u5408\u8c22\u661f\u7684\u5251\u5f0f\uff0c\u9f50\u653b\u8fc7\u53bb\u3002\u201d \u3000\u3000\u201c\u6218\u573a\u53d8\u5316\u4e00\u77ac\u5343\u91cc\uff0c\u5353\u817e\u4e0d\u6599\u524d\u540e\u53d7\u654c\uff0c\u5f53\u573a\u4e2d\u4e86\u8c22\u661f\u4e00\u5251\uff01\u201d \u3000\u3000\u201c\u5434\u5927\u4fa0\u4f55\u7b49\u4eba\u7269\uff0c\u5df2\u77e5\u5353\u5927\u4fa0\u6570\u6b21\u76f8\u6551\uff0c\u6b64\u65f6\u53cd\u800c\u53d7\u4f24\uff0c\u5927\u53f1\u4e00\u58f0\uff0c\u4e00\u5251\u6539\u5411\u56de \u98ce\u5251\u5ba2\u8c22\u661f\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u62db\u662f\u5434\u5927\u4fa0\u542b\u6124\u800c\u53d1\uff0c\u5df2\u662f\u5168\u529b\u65bd\u4e3a\uff0c\u4f46\u5374\u758f\u5ffd\u8eab\u540e\u8fd8\u7acb\u6709\u4e8c\u4e2a\u5f3a\u654c\u3002\u201d \u3000\u3000\u201c\u5389\u9102\u4e00\u5251\u52b2\u65a9\u800c\u4e0b\uff0c\u5434\u5927\u4fa0\u5927\u5403\u4e00\u60ca\uff0c\u52aa\u529b\u5e73\u4e0b\u8eab\u5b50\uff0c\u4f46\u957f\u5251\u5df2\u4e0e\u8c22\u661f\u76f8\u4ea4\u3002\u201d \u3000\u3000\u201c\u5434\u5927\u4fa0\u8eab\u4f53\u4e00\u987f\uff0c\u5185\u529b\u5f53\u7136\u4f7f\u4e0d\u51fa\u6765\uff0c\u8c22\u661f\u957f\u5251\u4e00\u6311\uff0c\u5434\u5927\u4fa0\u5251\u5b50\u51fa\u624b\u8fdc\u98de\u3002\u201d \u3000\u3000\u201c\u5434\u5927\u4fa0\u8fd9\u4e00\u5931\u5251\uff0c\u5c40\u52bf\u5f53\u7136\u66f4\u5371\uff0c\u5353\u817e\u84e6\u5730\u4e00\u58f0\u957f\u5578\uff0c\u8eab\u5b50\u817e\u7a7a\uff0c\u5411\u90a3\u8131\u624b\u957f\u5251 \u8ffd\u53bb\uff0c\u6562\u60c5\u662f\u60f3\u628a\u957f\u5251\u6293\u8fd8\u7ed9\u5434\u5927\u4fa0\u3002\u201d \u3000\u3000\u201c\u5353\u4fa0\u4eba\u79f0\u51cc\u7a7a\u6b65\u865a\uff0c\u8f7b\u529b\u5353\u7edd\u5df1\u6781\uff0c\u4e0d\u6d88\u4e00\u7a9c\uff0c\u5df1\u6293\u7740\u5251\u67c4\uff0c\u90a3\u77e5\u5ffd\u7684\u53d1\u51fa\u4e00\u58f0 \u60e8\u53eb\uff0c\u8eab\u5f62\u6025\u6025\u5760\u4e0b\u3002\u201d \u3000\u3000\u201c\u8001\u8872\u5f53\u65f6\u8eab\u5728\u9690\u5904\uff0c\u4e00\u65f6\u614c\u4e71\uff0c\u5e76\u672a\u770b\u6e05\u662f\u8c01\u4e0b\u7684\u6bd2\u624b\uff0c\u4f46\u9690\u7ea6\u53ef\u8fa8\u5353\u5927\u4fa0\u4e2d\u7684 \u662f\u4e00\u679a\u73af\u5f62\u6697\u5668\u3002\u201d \u3000\u3000\u201c\u5353\u5927\u4fa0\u8eab\u5f71\u6025\u5760\uff0c\u8eab\u4e0b\u4fbf\u662f\u5929\u7ec5\u7011\u7684\u8c37\u5e95\uff0c\u843d\u4e0b\u7684\u662f\u51c6\u6b7b\u65e0\u751f\u3002\u201d \u3000\u3000\u201c\u8001\u8872\u4eb2\u89c1\u5353\u5927\u4fa0\u66fe\u52aa\u529b\u6323\u624e\u4e8c\u6b21\uff0c\u5374\u65e0\u6cd5\u518d\u7a9c\u56de\u5d16\u8fb9\uff0c\u8001\u8872\u85cf\u8eab\u4e4b\u5904\u4e0e\u5730\u9762\u5e73\u884c\uff0c \u4f46\u89c1\u4ed6\u4e34\u5760\u4e0b\u65f6\uff0c\u6296\u624b\u5c06\u957f\u5251\u63b7\u51fa\u3002\u201d \u3000\u3000\u201c\u5434\u5927\u4fa0\u89c1\u5353\u817e\u4e09\u756a\u56db\u6b21\u8425\u6551\u81ea\u5df1\uff0c\u89c1\u4ed6\u9047\u96be\uff0c\u90a3\u80fd\u4e0d\u6025\uff0c\u72c2\u547c\u4e00\u58f0\uff0c\u5df1\u81ea\u6251\u5230\u3002\u201d \u3000\u3000\u201c\u8fce\u9762\u8679\u5149\u4e00\u95ea\uff0c\u5434\u5927\u4fa0\u4f38\u624b\u6293\u4f4f\u5353\u5927\u4fa0\u63b7\u51fa\u4e4b\u5251\uff0c\u8eab\u8eaf\u6597\u7136\u4e00\u632b\uff0c\u4e0d\u5dee\u5206\u6beb\u7684\u505c \u5728\u5d16\u8fb9\u3002\u201d \u3000\u3000\u201c\u5434\u5927\u4fa0\u731b\u7136\u5f2f\u4e0b\u8170\u8eab\uff0c\u5c3d\u91cf\u4f38\u51fa\u5de6\u624b\u60f3\u62c9\u8d77\u5353\u5927\u4fa0\uff0c\u4f46\u8001\u8872\u4eb2\u773c\u671b\u89c1\u53ea\u5dee\u4e0a\u4e00\u5398\uff0c \u5434\u5927\u4fa0\u7684\u624b\u5c16\u4fbf\u80fd\u89e6\u53ca\u5353\u5927\u4fa0\u7684\u9876\u5fc3\u53d1\u8b6c\u513f\uff0c\u4f46\u8fd8\u662f\u843d\u7a7a\u4e86\u3002\u201d \u3000\u3000\u201c\u5434\u5927\u4fa0\u4e00\u628a\u635e\u7a7a\uff0c\u767b\u65f6\u4e00\u58f0\u72c2\u547c\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8c22\u661f\u3001\u5389\u9e57\u4e8c\u5251\u653b\u5411\u5434\u5927\u4fa0 \u4e0b\u76d8\uff0c\u800c\u8d64\u9633\u9053\u58eb\u5374\u5f92\u624b\u786c\u7528\u5288\u7a7a\u638c\u6253\u5411\u5434\u5927\u4fa0\u540e\u5fc3\u3002\u201d \u3000\u3000\u201c\u7978\u8d77\u8427\u5899\uff0c\u5434\u5927\u4fa0\u518d\u4e5f\u6599\u4e0d\u5230\u5728\u60b2\u75db\u4e4b\u65f6\u906d\u4e09\u4e2a\u9ad8\u624b\u8fde\u51fb\uff0c\u6700\u7cdf\u7684\u4fbf\u662f\u5434\u5927\u4fa0\u7acb \u8db3\u5730\u65e0\u5411\u524d\u79fb\u7684\u4f59\u5730\u4e86\uff01\u201d \u3000\u3000\u201c\u90a3\u6015\u662f\u795e\u4ed9\u4e5f\u4e0d\u80fd\u8eb2\u907f\u4e09\u4e2a\u4e0d\u540c\u65b9\u4f4d\u88ad\u6765\u7684\u7edd\u5999\u653b\u52bf\uff01\u8001\u8872\u7684\u4e00\u9897\u5fc3\u5c06\u8981\u8df3\u51fa\u6765 \u4e86\uff0c\u84e6\u7684\u5434\u5927\u4fa0\u4e0d\u670d\u6c14\u7684\u4e00\u54fc\uff0c\u5de6\u638c\u731b\u70c8\u5411\u540e\u4e00\u62db\uff0c\u540c\u65f6\u8eab\u4f53\u5411\u524d\u4e00\u7eb5\uff0c\u53f3\u624b\u957f\u5251\u7528 \u2018\u5012\u9634\u53cd\u628a\u6492\u661f\u624b\u2019\u52a0\u4e0a\u2018\u5c0f\u5929\u661f\u2019\u5185\u5bb6\u771f\u529b\u63b7\u51fa\uff01\u201d \u3000\u3000\u201c\u8fd9\u4e8c\u4e0b\u653b\u52bf\u662f\u5434\u5927\u4fa0\u6bd5\u751f\u7cbe\u529b\u96c6\u805a\uff0c\u771f\u662f\u53ef\u4ee5\u5f00\u5c71\u88c2\u77f3\uff0c\u56de\u98ce\u5251\u8c22\u661f\u767b\u65f6\u95f7\u54fc\u4e00 \u58f0\uff0c\u88ab\u7ed3\u5b9e\u7684\u6253\u5728\u80f8\u524d\uff0c\u98de\u51fa\u4e00\u4e08\u591a\u8fdc\u3002\u201d \u3000\u3000\u201c\u90a3\u63b7\u51fa\u7684\u4e00\u5251\u5374\u51c6\u786e\u7684\u88ad\u5411\u5389\u9e57\u3002\u5389\u9e57\u4e0d\u6599\u5bf9\u624b\u5728\u52bf\u7aed\u4e4b\u65f6\u72b9\u80fd\u51fa\u6b64\u5947\u62db\uff0c\u6ca1\u547d \u4e00\u5251\u5c01\u53bb\uff0c\u4f46\u5185\u529b\u4fee\u4e3a\uff0c\u5f3a\u5f31\u7acb\u5224\uff0c\u2018\u53ee\u2019\u7684\u4e00\u58f0\uff0c\u5389\u9e4a\u7684\u5251\u88ab\u9707\u5f97\u8131\u624b\u98de\u5f00\u6570\u4e08\uff01\u201d \u3000\u3000\u201c\u90a3\u957f\u5251\u4ecd\u529b\u52bf\u4e0d\u8870\u7684\u76f4\u8fdb\uff0c\u5374\u4e94\u597d\u5954\u5411\u6b63\u5728\u53d1\u638c\u7684\u8d64\u9633\u9053\u58eb\uff0c\u8d64\u9633\u9053\u4eba\u89c1\u957f\u5251\u6765 \u5f0f\u592a\u5f3a\u52b2\uff0c\u5251\u8eab\u98ce\u96f7\u4e4b\u58f0\u5f3a\u6781\uff0c\u90a3\u6562\u8f7b\u5984\u7528\u5288\u7a7a\u638c\u786c\u62cd\uff01\u53ea\u89c1\u4ed6\u5fd9\u7740\u8e72\u4e0b\u8eab\u5b50\uff0c\u603b\u7b97\u4ed6 \u89c1\u8bc6\u591a\u5e7f\uff0c\u53ca\u65f6\u95ea\u8eb2\uff0c\u53ea\u542c\u5f97\u2018\u535f\u2019\u7684\u4e00\u58f0\uff0c\u4ed6\u7684\u9053\u9afb\u513f\u9f50\u6839\u5243\u53bb\uff01\u201d \u3000\u3000\u201c\u90a3\u957f\u5251\u4e3a\u52bf\u4e0d\u8870\uff0c\u518d\u5f80\u524d\u5954\uff0c\u597d\u4e00\u4f1a\u624d\u5760\u843d\u5730\u4e0a\u3002\u201d \u3000\u3000\u201c\u8001\u8872\u6025\u770b\u90a3\u5434\u5927\u4fa0\u65f6\u5df2\u4e0d\u89c1\u8e2a\u5f71\uff0c\u773c\u770b\u662f\u88ab\u5bb3\u4e86\uff0c\u5389\u9e57\u6b63\u6728\u7136\u7acb\u5728\u5d16\u8fb9\u4e0a\uff0c\u671b\u7740 \u6df1\u5d16\u51fa\u795e\u3002\u201d \u3000\u3000\u201c\u82e6\u5eb5\u4e0a\u4eba\u5728\u4e00\u65c1\u770b\u63a2\u90a3\u56de\u98ce\u5251\u5ba2\u8c22\u661f\u7684\u4f24\u52bf\uff0c\u5929\u7ec5\u7011\u524d\u767b\u65f6\u6c89\u9759\u5982\u6b7b\u5bc2\uff01\u8001\u8872\u5f53 \u65f6\u66fe\u6570\u6b21\u60f3\u51b2\u51fa\u62fc\u547d\uff0c\u4f46\u60f3\u5230\u5434\u5bb6\u5c11\u4e3b\u5c1a\u6258\u5728\u53cb\u5bb6\uff0c\u53ea\u597d\u6309\u637a\u4e00\u53e3\u6c14\uff0c\u6084\u7136\u9003\u53bb\u3002\u201d \u3000\u3000\u201c\u56de\u5230\u5bb6\u4e2d\uff0c\u53cb\u4eba\u544a\u8bc9\u8001\u8872\u5c11\u4e3b\u5728\u6570\u5929\u524d\u7a81\u7136\u5931\u8e2a\uff0c\u8fd9\u4e0d\u557b\u6674\u5929\u9739\u96f3\uff0c\u6700\u540e\u4e00\u70b9\u5e0c \u671b\u4e5f\u81ea\u7834\u706d\u4e86\uff0c\u771f\u662f\u6b32\u6b7b\u4e0d\u6210\u3002\u6df1\u6df1\u7684\u4ec7\u6068\u4f7f\u6211\u9690\u5fcd\u4e86\u4e00\u5341\u56db\u5e74\uff0c\u5929\u53ef\u601c\u89c1\u6628\u5929\u5728\u4e08\u4eba \u5cf0\u4e0b\u5de7\u9047\u5434\u5bb6\u5c11\u4e3b\u5df2\u957f\u5927\u6210\u4eba\uff0c\u5434\u5bb6\u6709\u540e\uff0c\u8001\u8872\u867d\u6b7b\u65e0\u61be\uff0c\u4fbf\u51c6\u5907\u62fc\u547d\u523a\u6740\u5389\u8d3c\u4f60\u4eec\u8fd9 \u4e00\u73ed\u72d7\u72fc\uff0c\u6765\u62a5\u5434\u5927\u4fa0\u7684\u6df1\u4ec7\u548c\u7b54\u8c22\u5353\u5927\u4fa0\u7684\u539a\u6069\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6bb5\u5f80\u4e8b\uff0c\u4f59\u5fe0\u4e00\u53e3\u6c14\u8bf4\u5b8c\uff0c\u7fa4\u8c6a\u90fd\u542c\u5f97\u5982\u75f4\u5982\u9189\uff0c\u90a3\u91cc\u8fd8\u6709\u4e00\u4e1d\u4e00\u6beb\u6000\u7591\uff0c\u60f3 \u4e0d\u5230\u8fd9\u9886\u8896\u6b66\u6797\u4e2d\u7684\u4eba\u7269\u7adf\u662f\u5982\u6b64\u5351\u9119\u7684\u5c0f\u4eba\u3002 \u3000\u3000\u504c\u5927\u7684\u65e5\u89c2\u5cf0\u5934\u5374\u6c89\u9759\u65e0\u6bd4\uff0c\u867d\u7136\u662f\u767d\u5929\uff0c\u4f46\u9ad8\u5904\u98ce\u5bd2\uff0c\u91d1\u98ce\u9001\u723d\uff0c\u677e\u5578\u5982\u6d9b\uff0c\u60c5 \u666f\u58ee\u4e3d\u5df2\u6781\uff01 \u3000\u3000\u84e6\u5730\u5e7d\u5e7d\u4e00\u58f0\u957f\u53f9\uff0c\u5728\u9759\u5730\u91cc\u4f20\u51fa\u8001\u8fdc\u8001\u8fdc\u53bb\uff0c\u4f17\u8c6a\u7fa4\u5411\u56de\u97f3\u53d1\u58f0\u5904\u671b\u53bb\uff0c\u53ea\u89c1\u6797 \u6728\u5bc6\u5bc6\uff0c\u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u5bc6\u679d\u4e2d\uff0c\u5750\u7740\u4e00\u4e2a\u4eba\uff0c\u85c9\u7740\u6811\u679d\u6321\u4f4f\u4e86\u4f17\u4eba\u7684\u89c6\u7ebf\uff0c\u4ed6\u6b63\u9677\u4eba\u6781\u5ea6\u7684\u75db\u82e6\u4e2d\uff0c\u90a3\u4fca \u7f8e\u7684\u8138\u4e0a\uff0c\u808c\u8089\u62bd\u6410\u7740\uff0c\u4ed6\u4fbf\u662f\u90a3\u843d\u82f1\u5251\u5ba2\u8c22\u957f\u537f\u3002 \u3000\u3000\u843d\u82f1\u5251\u6765\u5230\u5cf0\u5934\u5df2\u4e45\uff0c\u662f\u4ee5\u4f59\u5fe0\u4e00\u5e2d\u53ef\u6b4c\u53ef\u6ce3\u7684\u8bdd\u5168\u90e8\u6536\u5165\u8033\u4e2d\uff0c\u5f53\u4ed6\u542c\u5230\u5353\u817e\u80fd \u591f\u5728\u6781\u5ea6\u77db\u76fe\u4e2d\u4ecd\u4e0d\u5931\u4e8e\u4fa0\u4e49\uff0c\u5fc3\u4e2d\u5b9b\u5982\u5200\u5272\uff0c\u53ef\u89c1\u4e00\u5ff5\u4e4b\u5dee\uff0c\u6069\u6028\u7acb\u660e\uff0c\u81ea\u5df1\u4e00\u5931\u8db3 \u6210\u5343\u53e4\u6068\uff0c\u5fcd\u4e0d\u4f4f\u957f\u53f9\u5931\u58f0\u3002 \u3000\u3000\u5389\u9e57\u84e6\u5730\u5fc3\u4e2d\u4e00\u52a8\uff0c\u66fc\u58f0\u541f\u9053\uff1a\u201c\u4e94\u5251\u632f\u4e2d\u539f\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u6811\u53f6\u6413\u6860\u4e0a\u679c\u7136\u4e00\u9635\u6b3e\u6b3e\u6447\u52a8\u3002 \u3000\u3000\u5341\u5e74\u524d\uff0c\u4e94\u5927\u5b97\u6d3e\u5408\u74a7\u8fde\u51fb\u4e03\u5999\u795e\u541b\uff0c\u5f53\u65f6\u4ed6\u4eec\u4e5f\u66fe\u6599\u5230\u8fd9\u4e2a\u76d6\u4e16\u5947\u4eba\u5fc5\u6709\u540e\u4eba\u6765 \u627e\u4ed6\u4eec\u62a5\u590d\uff0c\u662f\u4ee5\u4ed6\u4eec\u5b9a\u4e00\u4e2a\u5207\u53e3\u2014\u2014\u201c\u4e94\u5251\u632f\u4e2d\u539f\u201d\uff0c\u53ea\u8981\u5176\u4e2d\u4efb\u4f55\u4e00\u4e2a\u4eba\u542c\u5230\u6b64\u8bed\uff0c \u5219\u5fc5\u5373\u65f6\u8d76\u5230\u5408\u5e03\u5251\u9635\uff0c\u5bf9\u4ed8\u4ec7\u4eba\u3002\u5389\u9e57\u65b9\u624d\u542c\u7740\u90a3\u4e00\u58f0\u6d69\u53f9\uff0c\u5fc3\u60f3\u53ef\u80fd\u662f\u8c22\u957f\u537f\uff0c\u662f \u4ee5\u541f\u51fa\u5207\u53e3\u76f8\u63a2\uff0c\u8c22\u957f\u537f\u5728\u6811\u4e0a\u542c\u5f97\uff0c\u5fc3\u4e2d\u597d\u751f\u6fc0\u52a8\uff01 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u90a3\u8001\u4ec6\u4f59\u5fe0\u7684\u751f\u547d\u6cb9\u706f\u5df2\u71c3\u5230\u4e86\u6781\u70b9\uff0c\u53ea\u542c\u4ed6\u5438\u8fdb\u4e00\u53e3\u6c14\uff0c\u5636\u58f0\u53eb\u9053\uff1a \u201c\u6740\u5440\uff0c\u6740\u6b7b\u8fd9\u4e9b\u8d3c\u5b50\u5440\uff01\u201d \u3000\u3000\u7fa4\u8c6a\u4e2d\u9976\u662f\u6709\u4e9b\u662f\u6740\u4eba\u4e0d\u7728\u773c\u7684\u9b54\u5934\uff0c\u4f46\u4e5f\u7981\u4e0d\u4f4f\u70ed\u6cea\u76c8\u7736\uff0c\u53ef\u601c\u7684\u8001\u4eba\u5410\u51fa\u6700\u540e \u7684\u4e00\u53e3\u6c14\uff0c\u840e\u9893\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5434\u51cc\u98ce\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u65ad\u9b42\u5251\u631f\u7740\u8679\u5149\u5982\u75af\u5982\u72c2\u6251\u5411\u5389\u9e57\uff0c\u5389\u9e57\u4e0d\u6562\u4e1d\u6beb\u5927\u610f\uff0c\u5168\u795e \u4e00\u5251\u5c01\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u5f53\u65f6\u8f9b\u6377\u3001\u5434\u51cc\u98ce\u4e8c\u4eba\u5728\u4e08\u4eba\u5cf0\u5e95\u9047\u89c1\u90a3\u602a\u8001\u4eba\uff0c\u4e66\u4e2d\u5df2\u4ea4\u5f85\u8fc7\uff0c\u6b63\u662f\u90a3\u8001\u4ec6 \u4f59\u5fe0\u3002\u4f59\u5fe0\u5f53\u65f6\u8ba4\u51fa\u65ad\u9b42\u5251\uff0c\u6b23\u559c\u6b32\u72c2\uff0c\u5f53\u7740\u5434\u3001\u8f9b\u4e8c\u4eba\u5c06\u4e00\u5341\u4e94\u5e74\u7684\u6069\u6028\u8be6\u7ec6\u8bf4\u4e86\u4e00 \u6b21\uff0c\u4f9d\u5434\u51cc\u98ce\u8981\u627e\u56db\u5927\u6d3e\uff08\u6606\u4ed1\u5df2\u4e0d\u7b97\u5728\u5185\uff09\u5728\u5929\u7ec5\u7011\u524d\u51b3\u6597\uff0c\u4f46\u4f59\u5fe0\u5374\u4e3b\u5f20\u6b21\u65e5\u7531\u4ed6 \u51fa\u624b\u884c\u523a\u3002 \u3000\u3000\u90a3\u77e5\u523a\u6740\u4e0d\u6210\uff0c\u53ea\u597d\u5728\u5929\u4e0b\u82f1\u96c4\u9762\u524d\u6296\u51fa\u8fd9\u4e00\u6bb5\u516c\u6848\uff0c\u66f4\u4f7f\u5389\u9e57\u7b49\u4eba\u96be\u582a\u3002 \u3000\u3000\u4f59\u5fe0\u53d7\u4f24\uff0c\u8f9b\u6377\u4e0d\u662f\u6ca1\u6709\u770b\u89c1\uff0c\u53ea\u4e0d\u8fc7\u4ed6\u4e3a\u4eba\u5fc3\u7ec6\uff0c\u5fc3\u60f3\u65f6\u673a\u5c1a\u672a\u5c45\u719f\uff0c\u4e0d\u80fd\u4ee5 \u201c\u8f9b\u4e5d\u9e4f\u6216\u4e03\u5999\u795e\u541b\u201d\u7684\u540e\u4ee3\u59ff\u6001\u51fa\u73b0\uff0c\u662f\u4ee5\u4ecd\u7136\u6df7\u5728\u4eba\u7fa4\u4e2d\u3002 \u3000\u3000\u8fd9\u65f6\u89c1\u5434\u51cc\u98ce\u7adf\u8dc3\u51fa\u62fc\u547d\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u95ea\u773c\u4e00\u671b\uff0c\u89c1\u7fa4\u8c6a\u90fd\u5168\u795e\u8d2f\u6ce8\u6597\u573a\uff0c\u5fc3\u5ff5\u4e00 \u52a8\uff0c\u7528\u6700\u5feb\u6377\u7684\u624b\u6cd5\u8131\u4e0b\u5916\u9762\u7684\u7070\u8272\u7f69\u886b\uff0c\u9732\u51fa\u4e00\u88ad\u9752\u888d\uff0c\u5e76\u5f20\u4e0a\u4e00\u5e55\u8499\u5dfe\uff0c\u53cd\u624b\u5c06\u7070 \u886b\u63b7\u5165\u8eab\u540e\u6797\u4e2d\uff0c\u5237\u5730\u7a9c\u5165\u6218\u573a\u3002 \u3000\u3000\u8f9b\u6377\u7684\u4e00\u5207\u52a8\u4f5c\u4e0d\u8fc7\u5728\u6781\u77ed\u7684\u4e00\u77ac\u95f4\u5b8c\u6210\uff0c\u800c\u5168\u5fc3\u6ce8\u89c6\u6597\u573a\u7684\u4f17\u8c6a\u81ea\u7136\u6ca1\u6709\u53d1\u73b0\uff0c \u4f46\u8f9b\u6377\u5374\u5ffd\u7565\u4e86\u5728\u6797\u4e2d\u5c45\u9ad8\u4e34\u4e0b\uff0c\u7aef\u6b63\u5750\u7740\u7684\u843d\u82f1\u5251\u5ba2\u8c22\u957f\u537f\u3002 \u3000\u3000\u8c22\u957f\u537f\u628a\u4ed6\u4e00\u5207\u52a8\u4f5c\u6e05\u695a\u7684\u770b\u5728\u773c\u4e2d\uff0c\u4ed6\u53ef\u662f\u5927\u5927\u7684\u5403\u60ca\u4e86\uff01 \u3000\u3000\u4ed6\u4e5f\u66fe\u548c\u201c\u4e03\u5999\u795e\u541b\u201d\u4f1a\u8fc7\u9762\uff0c\u4ee5\u4e03\u5999\u795e\u541b\u7684\u8eab\u624b\uff0c\u4f7f\u4ed6\u5728\u65e0\u53ef\u5948\u4f55\u7684\u60c5\u5f62\u53ea\u5f97\u76f8 \u4fe1\u4ed6\u6b7b\u91cc\u9003\u751f\uff0c\u4f46\u662f\u6b64\u523b\u4ed6\u5374\u4eb2\u773c\u770b\u89c1\u90a3\u53c8\u66fe\u6380\u8d77\u4e00\u5ea6\u98ce\u6ce2\u7684\u201c\u4e03\u5999\u795e\u541b\u201d\u662f\u4e00\u4e2a\u4fca\u7f8e \u7684\u5c11\u5e74\uff0c\u60f3\u6765\u8fd9\u4fbf\u662f\u4ed6\u4e3a\u4f55\u6bcf\u6b21\u51fa\u624b\u90fd\u8981\u7528\u8499\u5dfe\u7684\u539f\u56e0\u4e86\u3002 \u3000\u3000\u5047\u5b9a\u8fd9\u5c11\u5e74\u662f\u795e\u541b\u7684\u4f20\u4eba\uff0c\u4f46\u4e3a\u4f55\u6709\u5982\u6b64\u9ad8\u5999\u7684\u529f\u592b\uff1f\u8fd9\u4e00\u70b9\u786e\u5b9e\u4ee4\u4ed6\u767e\u601d\u4e0d\u5f97\u5176 \u89e3\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u50cf\u9b3c\u9b45\u4e00\u6837\u51fa\u73b0\u5728\u65e5\u89c2\u5cf0\u4e0a\uff0c\u7fa4\u82f1\u90fd\u4e0d\u7ea6\u800c\u540c\u60ca\u60f6\u51fa\u58f0\uff0c\u201c\u6885\u5c71\u6c11\u201d \u5e76\u4e0d\u53d1\u8a00\uff0c\u4ec5\u51b7\u7136\u4e00\u54fc\uff0c\u5fae\u5fae\u6325\u52a8\u957f\u5251\u3002 \u3000\u3000\u8679\u65e5\u6620\u7740\u5251\u8eab\uff0c\u968f\u7740\u5251\u8eab\u7684\u6446\u52a8\uff0c\u95ea\u8000\u51fa\u4e00\u5708\u4e00\u5708\u7684\u5149\u8f89\uff0c\u523a\u773c\u593a\u76ee\u5df2\u6781\u3002 \u3000\u3000\u5389\u9e57\u5df2\u548c\u201c\u4e03\u5999\u795e\u541b\u201d\u5bf9\u8fc7\u4e00\u9762\uff0c\u5012\u4e0d\u600e\u4e48\u6837\uff0c\u5ce8\u5d4b\u7684\u201c\u82e6\u5eb5\u4e0a\u4eba\u201d\uff0c\u6b66\u5f53\u7684\u201c\u8d64 \u9633\u9053\u58eb\u201d\u4ec5\u4ec5\u542c\u8bf4\u8fc7\u201c\u4e03\u5999\u795e\u541b\u201d\u91cd\u73b0\u6c5f\u6e56\u7684\u6d88\u606f\uff0c\u4f46\u5e76\u6ca1\u6709\u4eb2\u8eab\u76ee\u7779\u3002 \u3000\u3000\u6b64\u523b\u201c\u4e03\u5999\u795e\u541b\u201d\u7aef\u7136\u7acb\u5728\u81ea\u5df1\u9762\u524d\uff0c\u4e8c\u4eba\u7684\u5fc3\u90fd\u6c89\u91cd\u7684\u8df3\u52a8\u4e00\u4e0b\uff01 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u7684\u76ee\u5149\u6b63\u597d\u8f6c\u6ce8\u5728\u4e8c\u4e2a\u7384\u95e8\u9ad8\u624b\u8eab\u4e0a\uff0c\u4e8c\u4eba\u4e0d\u7981\u624b\u5fc3\u5fae\u6dcc\u51b7\u6c57\u3002 \u3000\u3000\u7fa4\u8c6a\u90fd\u662f\u8584\u5177\u58f0\u540d\u7684\u4eba\u58eb\uff0c\u90a3\u4f1a\u4e0d\u77e5\u9053\u6d77\u5185\u7b2c\u4e00\u5947\u4eba\u4e03\u5999\u795e\u541b\u7684\u540d\u5934\uff0c\u867d\u7136\u90fd\u6000\u7740 \u5c06\u4fe1\u534a\u7591\u7684\u5fc3\u7406\uff0c\u4f46\u4e5f\u4e0d\u7981\u5c4f\u606f\u800c\u89c2\u3002 \u3000\u3000\u6cf0\u5c71\u7edd\u9876\uff0c\u4e00\u65e5\u4e4b\u95f4\uff0c\u5929\u4e0b\u8d6b\u8d6b\u58f0\u540d\u7684\u9876\u5c16\u4eba\u7269\u51e0\u4e4e\u5168\u51fa\uff0c\u8fd9\u5012\u662f\u82b8\u82b8\u6b66\u6797\u4e2d\u5f88\u5c11 \u89c1\u7684\u4e00\u56de\u4e8b\u3002 \u3000\u3000\u5434\u51cc\u98ce\u7528\u51fa\u201c\u65ad\u9b42\u5251\u6cd5\u201d\u4e2d\u653b\u52bf\u6700\u51cc\u5389\u7684\u62db\u5f0f\uff0c\u5389\u9e57\u867d\u7136\u529f\u529b\u6df1\u539a\uff0c\u4e5f\u4e00\u65f6\u65e0\u6cd5\u8fd8 \u624b\u3002 \u3000\u3000\u5434\u51cc\u98ce\u53cc\u76ee\u6b32\u88c2\uff0c\u731b\u780d\u51fa\u4e00\u5251\u201c\u9b3c\u738b\u628a\u706b\u201d\u3002\u5389\u9e57\u563f\u5730\u5410\u6c14\u5f00\u58f0\uff0c\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c \u5251\u8eab\u6325\u52a8\uff0c\u201c\u501a\u8679\u201d\u5251\u5b50\u7cbe\u5149\u66b4\u957f\uff0c\u767b\u65f6\u5c06\u5434\u51cc\u98ce\u653b\u52bf\u5c3d\u6570\u5c01\u4e0b\uff0c\u62a2\u56de\u4e3b\u52a8\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u51b7\u7136\u4e00\u54fc\uff0c\u53f3\u624b\u957f\u5251\u95ea\u7535\u822c\u6233\u51fa\uff0c\u201c\u547c\u201d\u7684\u63a5\u4f4f\u5389\u9e57\u653b\u52bf\uff0c\u4ed6\u4e0d\u662f\u4e0d \u77e5\u201c\u501a\u8679\u201d\u5b9d\u5251\u7684\u795e\u5999\uff0c\u662f\u4ee5\u5f3a\u7528\u5185\u529b\u6c79\u6d8c\u8d2f\u6ce8\uff0c\u201c\u5bdf\u201d\uff0c\u201c\u5bdf\u201d\uff0c\u201c\u501a\u8679\u201d\u5251\u5728\u957f\u5251 \u4e0a\u8df3\u52a8\u4e0d\u505c\uff0c\u4f46\u90fd\u4e1d\u6beb\u4e0d\u80fd\u635f\u53ca\u4e03\u5999\u795e\u541b\u7684\u5175\u5668\u3002 \u3000\u3000\u5389\u9e57\u5df2\u9886\u6559\u8fc7\u201c\u795e\u541b\u201d\u7684\u529f\u592b\uff0c\u4e0d\u6562\u7a0d\u5f85\uff0c\u52aa\u529b\u6536\u62db\u540e\u9000\uff0c\u795e\u541b\u957f\u5251\u4e00\u5f39\uff0c\u5f39\u8d77 \u201c\u501a\u8679\u201d\u795e\u5251\u7ea6\u6709\u534a\u5c3a\uff0c\u957f\u7b11\u4e00\u58f0\u3002 \u3000\u3000\u56db\u5468\u6797\u7acb\u7684\u4f17\u8c6a\u540c\u5927\u5403\u4e00\u60ca\uff0c\u5929\u4e0b\u7b2c\u4e00\u5251\u7adf\u5728\u7b2c\u4e00\u4e2a\u62db\u9762\u4e0b\u4fbf\u5403\u4e86\u4e8f\uff0c\u8fd9\u7b49\u529f\u529b\uff0c \u83ab\u975e\u90a3\u6d77\u5185\u5947\u4eba\u201c\u4e03\u5999\u795e\u541b\u201d\u4eb2\u8eab\u624d\u80fd\u529e\u5230\u3002 \u3000\u3000\u5389\u9e57\u8df3\u540e\u5bfb\u4e08\uff0c\u9ad8\u58f0\u541f\u9053\u201c\u4e94\u5251\u9707\u4e2d\u539f\u201d\uff0c\u58f0\u97f3\u5df2\u6709\u4e9b\u98a4\u6296\u3002\u82e6\u5eb5\uff0c\u8d64\u9633\u957f\u5251\u8fce\u65e5 \u800c\u51fa\uff0c\u8c22\u957f\u537f\u5728\u679d\u5934\u4e0a\u72b9\u7591\u4e86\u4e00\u4e0b\uff0c\u4ed6\u662f\u4e00\u4e2a\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\uff0c\u4e0d\u80fd\u80cc\u5931\u4fe1\u7ea6\u2014\u2014 \u3000\u3000\u867d\u7136\u4ed6\u662f\u6781\u4e0d\u613f\u610f\uff01\u4ed6\u811a\u5c16\u5fae\u5fae\u7528\u529b\uff0c\u8eab\u4f53\u817e\u7a7a\u800c\u8d77\uff0c\u7eb5\u843d\u573a\u5e95\u3002 \u3000\u3000\u5389\u9e57\u5411\u4ed6\u5fae\u4e00\u9894\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u8c22\u4e16\u5144\u522b\u6765\u65e0\u6059\uff1f\u201d \u3000\u3000\u8c22\u957f\u537f\u52c9\u5f3a\u70b9\u4e86\u70b9\u5934\uff0c\u545b\u5577\u4e00\u58f0\uff0c\u957f\u5251\u4e5f\u81ea\u51fa\u624b\u3002 \u3000\u3000\u8f9b\u6377\u65e9\u5c31\u4ece\u795e\u541b\u90a3\u91cc\u77e5\u8fd9\u56db\u5927\u5251\u6d3e\u6240\u5e03\u7684\u5251\u9635\u7684\u5389\u5bb3\uff0c\u5c24\u5176\u662f\u9632\u5b88\u7684\u65b9\u9762\uff0c\u66f4\u662f\u5bc6 \u96c6\u6709\u82e5\u5343\u519b\u4e07\u9a6c\u3002 \u3000\u3000\u5fc3\u5ff5\u624d\u52a8\uff0c\u56db\u4eba\u5df1\u7acb\u597d\u65b9\u4f4d\uff0c\u4e00\u79cd\u719f\u6089\u800c\u81ea\u7136\u7684\u4e60\u60ef\u4f7f\u8c22\u957f\u537f\u4e5f\u8f7b\u5feb\u7684\u7acb\u5728\u81ea\u5df1\u7684 \u65b9\u4f4d\u4e0b\u3002 \u3000\u3000\u70b9\u82cd\u7684\u638c\u95e8\u4eba\u4e00\u5230\uff0c\u7fa4\u8c6a\u4e5f\u4e0d\u89c9\u4e00\u6014\uff0c\u5c24\u5176\u662f\u81ea\u90a3\u6850\u67cf\u5c71\u4e00\u6218\uff0c\u9646\u65b9\u548c\u6797\u5c11\u768b\u4e8c\u4eba \u62fc\u547d\u9003\u4e86\u51fa\u6765\uff0c\u8fd9\u65f6\u53c8\u89c1\u5bf9\u624b\uff0c\u90fd\u4e0d\u7981\u5fc3\u5bd2\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u6e05\u5578\u4e00\u58f0\uff0c\u957f\u5251\u6296\u52a8\u6709\u82e5\u585e\u5916\u98de\u82b1\uff0c\u5434\u51cc\u98ce\u7684\u5bb6\u4f20\u5251\u5f0f\u53ef\u4e5f\u4e0d\u5f31\uff0c\u4ece \u8f9b\u6377\u5bc6\u9ebb\u7684\u5251\u5f0f\u4e2d\u9012\u51fa\u4e8c\u5251\uff0c\u51b7\u4e0d\u9632\u653b\u5411\u8d64\u9633\u3002 \u3000\u3000\u5389\u9e57\u201c\u501a\u8679\u201d\u5251\u4e00\u4e3e\uff0c\u5251\u9635\u7acb\u53d1\uff0c\u4f46\u89c1\u5251\u5149\u5bc6\u9ebb\uff0c\u4ea4\u7ec7\u82e5\u7f51\uff0c\u5251\u9635\u679c\u7136\u4e0d\u540c\u51e1\u54cd\u3002 \u3000\u3000\u8f9b\u6377\u957f\u5251\u6025\u6325\u800c\u4e0a\uff0c\u4e00\u6307\u201c\u5bd2\u6885\u5410\u854a\u201d\u84e6\u5730\u53d8\u4f5c\u201c\u51b7\u6885\u62c2\u9762\u201d\uff0c\u8fce\u9762\u731b\u523a\u5389\u9e57\uff0c\u800c \u5434\u51cc\u98ce\u5ffd\u7684\u5012\u53d1\u4e00\u62db\u201c\u9b3c\u738b\u628a\u706b\u201d\u7edd\u9876\u653b\u52bf\uff0c\u53cd\u523a\u5728\u5b9a\u4f4d\u4e0a\u7684\u82e6\u5eb5\uff0c\u4e8c\u4eba\u8054\u624b\u5a01\u529b\u4e4b\u5927\uff0c \u4e5f\u786e\u5b9e\u60ca\u4eba\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u4f46\u4e0d\u5b88\uff0c\u800c\u4e14\u8fd8\u5168\u529b\u62a2\u653b\uff0c\u957f\u5251\u9707\u5e45\u6e10\u6e10\u6269\u5927\uff0c\u5230\u6700\u9876\u7684\u65f6\u5019\u731b\u7684\u4e00\u5f0f\u201c\u6885 \u82b1\u4e09\u5f04\u201d\uff0c\u957f\u5251\u55e1\u55e1\u4e4b\u58f0\u5927\u4f5c\u3002\u8d64\u9633\u9053\u4eba\u957f\u9aef\u65e0\u98ce\u800c\u52a8\uff0c\u6562\u60c5\u5185\u529b\u4e5f\u53eb\u81f3\u7edd\u9876\uff0c\u4e00\u5251\u5c01 \u53bb\u3002 \u3000\u3000\u5434\u51cc\u98ce\u659c\u5730\u91cc\u4e00\u5251\u95ea\u7535\u523a\u51fa\uff0c\u5f53\u7684\u6321\u4e86\u4e00\u4e0b\uff0c\u8fd9\u5374\u662f\u516d\u4eba\u516d\u67c4\u957f\u5251\u7b2c\u4e00\u6b21\u76f8\u51fb\u7684\u58f0 \u97f3\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u8f9b\u6377\u5f15\u5251\u731b\u523a\u8c22\u957f\u537f\uff0c\u8c22\u957f\u537f\u4eba\u79f0\u201c\u843d\u82f1\u5251\u201d\uff0c\u8f7b\u529f\u81ea\u662f\u4e0d\u5f31\uff0c\u6b65\u5c65\u5fae\u6ed1\uff0c \u95ea\u51fa\u7a7a\u6863\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u5251\u8d70\u7a7a\uff0c\u659c\u5730\u91cc\u4e00\u5251\u98de\u51fa\uff0c\u767e\u5fd9\u4e2d\u77a5\u89c1\u6b63\u662f\u90a3\u5439\u6bdb\u53ef\u65ad\u7684\u201c\u501a\u8679\u201d\u5251\uff0c\u5fc3\u4e2d \u5403\u60ca\uff0c\u94c1\u8155\u4e00\u6536\uff0c\u5185\u529b\u6ce8\u8d2f\u5251\u8eab\uff0c\u5fae\u5fae\u4e00\u632b\u3002 \u3000\u3000\u5389\u9e57\u5251\u8d70\u8f7b\u7075\uff0c\u201c\u5bdf\u201d\u7684\u4e00\u58f0\uff0c\u5df2\u5728\u5251\u5c16\u4e0a\u52d2\u5f97\u4e00\u52d2\u3002 \u3000\u3000\u8f9b\u6377\u867d\u5185\u529b\u8d2f\u6ce8\uff0c\u4f46\u501a\u8679\u4e43\u5148\u5929\u795e\u5668\uff0c\u4ecd\u5728\u5251\u5c16\u4e0a\u52d2\u4e86\u4e00\u6761\u53e3\u5b50\uff0c\u9976\u662f\u8fd9\u6837\uff0c\u5389\u9e57 \u4e5f\u60ca\u4f69\u8f9b\u6377\u7684\u5185\u529b\u4fee\u4e3a\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u94c1\u8155\u4e00\u632b\uff0c\u957f\u5251\u81ea\u53f3\u81f3\u5de6\uff0c\u5212\u4e00\u9053\u5706\u5f27\uff0c\u505c\u5728\u9762\u524d\u3002 \u3000\u3000\u4ed6\u51b7\u563f\u4e00\u58f0\uff0c\u98df\u6307\u95ea\u7535\u5f39\u51fa\uff0c\u201c\u6258\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u4e00\u5bf8\u591a\u7684\u5251\u5c16\u5df2\u81ea\u5389\u9e57\u52d2\u53e3\u800c\u65ad\uff0c \u53ea\u89c1\u4e00\u70b9\u5bd2\u5149\u98de\u5411\u6b63\u524d\u65b9\u7684\u8d64\u9633\u9053\u58eb\u3002 \u3000\u3000\u8d64\u9633\u9053\u58eb\u957f\u5251\u4e00\u6325\uff0c\u628a\u90a3\u4e00\u6bb5\u5251\u5c16\u513f\u62cd\u843d\u5c18\u57c3\uff0c\u800c\u5434\u51cc\u98ce\u4e00\u53e3\u957f\u5251\u5df2\u81ea\u4f7f\u7528\u201c\u9b3c\u7bad \u98de\u78f7\u201d\u9012\u81f3\u8eab\u524d\u4e0d\u53ca\u4e09\u5bf8\u3002 \u3000\u3000\u8d64\u9633\u9053\u957f\u6025\u5fd9\u4e2d\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u80f8\u524d\u5185\u9677\uff0c\u8db3\u4e0b\u4e0d\u52a8\uff0c\u9976\u662f\u8fd9\u6837\uff0c\u4e5f\u542c\u5f97\u201c\u78fa\u201d\u7684 \u4e00\u58f0\uff0c\u80f8\u8863\u88ab\u5272\u7834\u4e00\u6761\u53e3\u5b50\u513f\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\u201c\u4e03\u5999\u795e\u541b\u201d\u84e6\u7684\u4e00\u5f0f\u201c\u674e\u5e7f\u5c04\u77f3\u201d\uff0c\u5251\u5c16\u631f\u7740\u4e00\u7f15\u5bd2\u98ce\u6025\u5954\u800c\u51fa\uff0c\u8d70\u7684\u65b9 \u5411\u5374\u662f\u5251\u795e\u5389\u9e57\u5fc5\u7ecf\u4e4b\u5730\u3002 \u3000\u3000\u5389\u9e57\u5fc3\u4e2d\u5927\u559c\uff0c\u201c\u501a\u8679\u201d\u5251\u5e73\u5e73\u62cd\u4e0b\uff0c\u60f3\u4e00\u4e3e\u6298\u65ad\u201c\u4e03\u5999\u795e\u541b\u201d\u957f\u5251\uff0c\u90a3\u77e5\u8f9b\u6377\u563f \u563f\u4e00\u58f0\u51b7\u7b11\uff0c\u957f\u5251\u731b\u7136\u4e00\u6536\uff0c\u5de7\u5999\u7684\u4e00\u65cb\uff0c\u501a\u8679\u5251\u5149\u8fc7\u5904\uff0c\u4ec5\u524a\u53bb\u90a3\u5df1\u65ad\u7684\u5251\u5c16\u9876\u7aef\u7684 \u4e00\u534a\uff0c\u7acb\u523b\u90a3\u6298\u5c16\u7684\u5251\u53c8\u6210\u4e86\u4e00\u67c4\u9510\u5229\u7684\u5251\u5b50\uff0c\u53ea\u4e0d\u8fc7\u6bd4\u539f\u6765\u77ed\u4e86\u4e00\u5bf8\u800c\u5df2\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u84e6\u5730\u53c8\u662f\u4e00\u58f0\u957f\u5578\uff0c\u5251\u62db\u7a81\u53d8\uff0c\u4e00\u65f6\u5708\u5185\u6f2b\u5f00\u5251\u5149\u7acb\u523b\u6536\u6b62\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u957f\u5251\u7a81\u7136\u4e00\u6162\uff0c\u7f13\u7f13\u523a\u51fa\uff0c\u5251\u8eab\u6539\u53d8\u76f4\u524a\u800c\u4e3a\u5e73\u62cd\u4e4b\u52bf\uff0c\u5251\u5149\u6709\u82e5\u60ca \u6d9b\u88c2\u5cb8\u822c\u51b2\u62cd\u800c\u53bb\uff0c\u5251\u5c16\u8fd8\u4e0d\u65f6\u8df3\u52a8\uff0c\u4e13\u70b9\u5411\u80f8\u524d\u8179\u4e0a\u7684\u4e3b\u8981\u7a74\u9053\u3002\u8fd9\u6b63\u662f\u5f53\u4eca\u5929\u4e0b\u7b2c \u4e00\u5251\u672f\u201c\u5927\u884d\u795e\u5251\u201d\u7684\u8d77\u624b\u5f0f\u2014\u2014\u201c\u65b9\u751f\u4e0d\u606f\u201d\u3002 \u3000\u3000\u5927\u884d\u5251\u62db\u4e00\u5171\u5341\u5f0f\uff0c\u5176\u4e2d\u6bcf\u4e00\u5f0f\u5730\u53c8\u542b\u4e94\u4e2a\u53d8\u5316\uff0c\u4e00\u5171\u662f\u5341\u62db\u4e94\u5341\u5f0f\uff0c\u6b63\u5408\u5927\u884d\u4e4b \u6570\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u9996\u62db\u201c\u65b9\u751f\u4e0d\u606f\u201d\u624d\u51fa\u624b\uff0c\u500f\u5730\u5251\u8eab\u4e00\u6c89\u4e00\u5212\uff0c\u7acb\u65f6\u4f7f\u51fa\u4e94\u4e2a\u62db\u5f0f\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e94\u4e2a\u53d8\u5316\u4f3c\u662f\u4e94\u4e2a\u4eba\u540c\u65f6\u4f7f\u51fa\u4e00\u62db\uff0c\u800c\u6bcf\u4eba\u7684\u62db\u5f0f\u5374\u90fd\u975e\u5e73\u51e1\u62db\u5f0f\u53ef\u6bd4\uff0c\u5176 \u653b\u52bf\u4e4b\u5f3a\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u56db\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u89c1\u6b64\u62db\u653b\u52bf\u5947\u5927\uff0c\u5176\u4e2d\u6709\u524a\uff0c\u6709\u70b9\uff0c\u6709\u6233\uff0c\u751a\u81f3\u8fd8\u6709\u5212\uff0c\u653b\u52bf\u4e4b \u5f3a\uff0c\u5b9e\u5728\u53ef\u79f0\u5947\u7edd\u5929\u4e0b\u3002 \u3000\u3000\u4e0d\u5f97\u5df2\u4f7f\u51fa\u5251\u9635\u7684\u6551\u547d\u5b88\u5f0f\u201c\u516b\u65b9\u98ce\u96e8\u201d\u3002 \u3000\u3000\u53ea\u89c1\u56db\u652f\u957f\u5251\u7834\u4f8b\u7684\u76f8\u89e6\uff0c\u201c\u5f53\u201d\u7684\u6d2a\u54cd\u4e00\u4e0b\uff0c\u56db\u652f\u5251\u5b50\u5f39\u5f00\uff0c\u56db\u4eba\u5404\u501f\u6b64\u4e00\u5f39\u4e4b \u5f0f\uff0c\u5728\u8eab\u524d\u5e03\u4e0a\u4e00\u5f20\u5251\u5e55\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u5c01\u5f00\u6b64\u62db\u3002 \u3000\u3000\u201c\u5927\u884d\u795e\u5251\u201d\u65e2\u5df2\u4f7f\u51fa\uff0c\u5947\u62db\u8fde\u7ef5\u4e0d\u7edd\uff0c\u201c\u5f00\u4e91\u6f6d\u5f71\u201d\uff0c\u201c\u7269\u6362\u661f\u79fb\u201d\u602a\u62db\u53e0\u751f\u3002 \u3000\u3000\u56db\u4eba\u7ecf\u9a8c\u4f55\u7b49\u8001\u5230\uff0c\u5728\u5168\u795e\u5e94\u4ed8\u4e0b\uff0c\u5c1a\u80fd\u52c9\u5f3a\u56f0\u4f4f\u8f9b\uff0c\u5434\u4e8c\u4eba\u3002 \u3000\u3000\u56db\u4eba\u4e2d\u8c22\u957f\u537f\u672c\u6765\u6beb\u65e0\u6218\u610f\uff0c\u4f46\u4ed6\u662f\u94c1\u94ee\u94ee\u7684\u6c49\u5b50\uff0c\u65e2\u5df2\u52a0\u624b\u800c\u4e14\u53c8\u66fe\u5141\u8bfa\u7684\u4e8b\uff0c \u5c82\u80fd\u5931\u4fe1\u800c\u7559\u4e0b\u8bdd\u67c4\u4e3a\u5929\u4e0b\u6b66\u6797\u540c\u9053\u8bf4\u5634\uff0c\u518d\u52a0\u4e0a\u4ed6\u4e5f\u8d8a\u6218\u8d8a\u6fc0\u53d1\u8c6a\u6027\uff0c\u662f\u4ee5\u4e5f\u65bd\u5168\u529b \u5468\u65cb\uff0c\u201c\u4e03\u7edd\u8eab\u6cd5\u201d\uff0c\u201c\u767e\u79bd\u5251\u6cd5\u201d\u4e5f\u4f7f\u5230\u5341\u6210\u3002 \u3000\u3000\u56db\u5927\u6d3e\u4e2d\u5012\u662f\u4ee5\u5ce8\u5d4b\u82e6\u5eb5\u4e0a\u4eba\u5b88\u5f97\u6700\u597d\uff0c\u4e00\u5957\u5ce8\u5d4b\u201c\u62b1\u7389\u5251\u6cd5\u201d\u5b88\u5f97\u6709\u5982\u94dc\u5899\u94c1\u58c1\u3002 \u800c\u4e5f\u53ea\u6709\u5389\u9e57\u4ed7\u7740\u501a\u8679\u795e\u5175\u548c\u8f83\u6df1\u5185\u529b\u80fd\u5076\u800c\u653b\u51fa\u6570\u62db\u3002 \u3000\u3000\u8fd9\u4e00\u573a\u6218\u4e89\u786e\u662f\u6b66\u6797\u7f55\u89c1\uff0c\u5341\u5e74\u524d\u4e94\u5927\u5b97\u6d3e\u5408\u51fb\u5434\u8bcf\u4e91\u548c\u4e03\u5999\u795e\u541b\u90fd\u5728\u7edd\u4eba\u8ff9\u7684\u5730 \u65b9\u4e3e\u884c\uff0c\u662f\u4ee5\u5f88\u5c11\u6709\u4eba\u76ee\u51fb\uff0c\u8fd9\u4e00\u573a\u7531\u56db\u5927\u6d3e\u548c\u201c\u4e03\u5999\u795e\u541b\u201d\uff0c\u201c\u5355\u5251\u65ad\u9b42\u201d\u7684\u540e\u4ee3\u62fc \u6597\uff0c\u786e\u662f\u5341\u5206\u53ef\u89c2\u7684\u4e86\u3002 \u3000\u3000\u56db\u5927\u5251\u6d3e\u7684\u540e\u4e00\u8f88\u5168\u90fd\u6309\u5251\u800c\u7acb\uff0c\u4f46\u59cb\u7ec8\u627e\u4e0d\u7740\u673a\u4f1a\u52a0\u5165\u534f\u52a9\uff0c\u4e8e\u4e00\u98de\u5fc3\u4e2d\u751a\u60ca\u90a3 \u65e5\u548c\u8f9b\u8001\u677f\u4e00\u884c\u7684\u5434\u51cc\u98ce\u7adf\u4f1a\u662f\u5434\u8bcf\u4e91\u7684\u540e\u4ee3\uff0c\u5fc3\u4e2d\u60f3\u5230\u8f9b\u6377\uff0c\u56db\u5468\u4e00\u5bfb\uff0c\u5374\u5e76\u6ca1\u6709\u8f9b \u8001\u677f\u7684\u8e2a\u8ff9\u3002\u4f46\u4ed6\u5374\u7edd\u5bf9\u60f3\u4e0d\u5230\u8f9b\u6377\u7adf\u4f1a\u662f\u5192\u540d\u7684\u201c\u4e03\u5999\u795e\u541b\u201d\uff0c\u5c24\u5176\u662f\u8f9b\u6377\u65e2\u8499\u7740\u9762\uff0c \u53c8\u6362\u4e86\u8863\u888d\uff01 \u3000\u3000\u84e6\u5730\u91cc\u5c71\u8170\u4e0a\u4e00\u58f0\u957f\u5578\uff0c\u5237\u5730\u7eb5\u4e0a\u4e00\u4eba\u3002 \u3000\u3000\u53ef\u602a\u7684\u662f\u90a3\u4eba\u4e5f\u662f\u8499\u7740\u9762\u7684\uff0c\u800c\u4e14\u6b65\u4f10\u8e09\u8dc4\uff0c\u75af\u75af\u98a0\u98a0\u3002\u8fd9\u65f6\u65e5\u89c2\u5cf0\u56db\u5468\u56f4\u6ee1\u4e86\u89c2 \u6218\u7684\u4e09\u5c71\u4e94\u5cb3\u7684\u6c49\u5b50\u3002\u90a3\u8499\u9762\u4eba\u5165\u8def\u88ab\u963b\uff0c\u84e6\u5730\u4e00\u649e\uff0c\u786c\u6324\u8fc7\u53bb\u3002 \u3000\u3000\u7ad9\u5728\u5c71\u77f3\u53e3\u7684\u662f\u4e00\u4e2a\u5524\u4f5c\u98de\u5929\u864e\u7684\u6c49\u5b50\uff0c\u51b7\u4e0d\u9632\u88ab\u8499\u9762\u4eba\u4e00\u649e\uff0c\u8dcc\u8dcc\u51b2\u51b2\u597d\u51e0\u6b65\u624d \u505c\u6b62\u3002 \u3000\u3000\u98de\u5929\u864e\u56de\u9996\u4e00\u770b\uff0c\u90a3\u8499\u9762\u4eba\u6b63\u6324\u8fc7\u6765\uff0c\u5fc3\u4e2d\u5927\u6012\uff0c\u6012\u559d\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4eba\uff0c\u4e71\u6324\u4e71 \u649e\u4ec0\u4e48\uff1f\u627e\u6b7b\u5417\uff1f\u201d \u3000\u3000\u90a3\u8499\u9762\u4eba\u542c\u4e86\uff0c\u84e6\u5730\u91cc\u4e00\u638c\u6253\u5411\u98de\u5929\u864e\uff0c\u98de\u5929\u864e\u89c1\u6765\u4eba\u6beb\u4e0d\u8bb2\u7406\uff0c\u5fc3\u4e2d\u66f4\u6012\uff0c\u4e00\u62f3 \u53cd\u51fb\u9762\u4e0a\u3002 \u3000\u3000\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u8499\u9762\u4eba\u597d\u5927\u5185\u529b\uff0c\u98de\u5929\u864e\u624b\u8155\u5f53\u573a\u9707\u6298\uff0c\u60e8\u53eb\u4e00\u58f0\uff0c\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u4f17\u4eba\u6b63\u5446\u5f97\u4e00\u5446\uff0c\u8499\u9762\u4eba\u84e6\u5730\u53d1\u8db3\u51b2\u5927\u6218\u5708\uff0c\u6562\u60c5\u4ed6\u4e5f\u6709\u4e00\u67c4\u5251\uff0c\u62d4\u51fa\u5954\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8f9b\u6377\uff0c\u5434\u51cc\u98ce\u767e\u5ea6\u4e2d\u4e00\u77a5\uff0c\u90a3\u8499\u9762\u4eba\u597d\u50cf\u6b63\u662f\u6628\u65e5\u5728\u4e08\u4eba\u5cf0\u4e0b\u60f3\u5bfb\u81ea\u5c3d\u7684\u8499\u9762\u6c49\u5b50\u3002 \u3000\u3000\u56db\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u597d\u4e0d\u5bb9\u6613\u5c01\u4f4f\u8f9b\u6377\u4e8c\u62db\u653b\u52bf\uff0c\u8fd9\u8499\u9762\u4eba\u5ffd\u7684\u5954\u5165\uff0c\u5251\u9635\u7acb\u523b\u6df7\u4e71\u3002 \u3000\u3000\u826f\u673a\u4e0d\u53ef\u590d\u5f97\uff0c\u8f9b\uff0c\u5434\u4e8c\u4eba\u6b63\u60f3\u7a9c\u51fa\u5251\u9635\uff0c\u90a3\u77e5\u90a3\u8499\u9762\u4eba\u4e00\u8fde\u6570\u5251\u5374\u53c8\u653b\u5411\u4e24\u4eba\uff0c \u8f9b\uff0c\u5434\u4e8c\u4eba\u51dd\u795e\u63a5\u4e86\u6570\u62db\uff0c\u90a3\u56db\u5927\u6d3e\u7684\u5251\u9635\u53c8\u4e58\u673a\u91cd\u65b0\u5e03\u7f6e\u4e00\u4e0b\u3002 \u3000\u3000\u90a3\u8499\u9762\u4eba\u4e00\u8fde\u6570\u5251\u653b\u4e0d\u4e0b\uff0c\u84e6\u5730\u5927\u559d\u4e00\u58f0\uff0c\u53cd\u8eab\u5237\u5237\u5c31\u662f\u4e8c\u5251\uff0c\u53cd\u8fce\u9762\u523a\u5411\u5389\u9e57\u548c \u82e6\u5eb5\u3002 \u3000\u3000\u8fd9\u8499\u9762\u4eba\u4e0d\u5b88\u89c4\u77e9\uff0c\u80e1\u4e71\u51b2\u5165\u56db\u5927\u5251\u624b\u7684\u5408\u9635\u4e2d\uff0c\u6307\u4e1c\u6253\u897f\uff0c\u51fb\u5357\u51fb\u5317\uff0c\u529f\u529b\u53c8\u6df1 \u5f97\u7d27\uff0c\u4f46\u770b\u6765\u4e5f\u4e0d\u50cf\u662f\u5e2e\u52a9\u201c\u4e03\u5999\u795e\u541b\u201d\u7684\uff0c\u56e0\u4e3a\u4ed6\u4e5f\u4e0d\u65f6\u53d1\u51fa\u6781\u51f6\u72e0\u7684\u62db\u5f0f\u653b\u51fb\u201c\u4e03 \u5999\u795e\u541b\u201d\uff0c\u770b\u4ed6\u60c5\u5f62\u4f3c\u4e4e\u6709\u70b9\u8fd1\u4e8e\u75af\u72c2\u3002 \u3000\u3000\u4e94\u5927\u5251\u6d3e\u7684\u9635\u6cd5\u4e43\u662f\u5341\u591a\u5e74\u524d\u4e3a\u4e86\u5408\u6355\u4e00\u79cd\u6b66\u6797\u5947\u73cd\u201c\u8702\u9e1f\u201d\u6240\u7ec3\u6210\u7684\uff0c\u4e0d\u8fc7\u5f53\u65f6 \u53ea\u6709\u56f4\u5b88\u4e4b\u5f0f\uff0c\u800c\u6ca1\u6709\u56f4\u653b\u4e4b\u52bf\uff0c\u81ea\u4ece\u5341\u5e74\u524d\u4ed6\u4eec\u56f4\u653b\u6885\u5c71\u6c11\u4e4b\u540e\uff0c\u53c8\u5408\u529b\u52a0\u5927\u8bb8\u591a\u5389 \u5bb3\u653b\u52bf\uff0c\u7aef\u7684\u582a\u79f0\u7edd\u65e0\u6f0f\u6d1e\u3002 \u3000\u3000\u90a3\u8499\u9762\u4eba\u7684\u62db\u5f0f\u5341\u5206\u53e4\u602a\u800c\u6bd2\u72e0\uff0c\u53ea\u6709\u8f9b\u6377\u770b\u51fa\u6765\uff0c\u6b63\u662f\u90a3\u540d\u9707\u5929\u4e0b\u7684\u6bd2\u541b\u91d1\u4e00\u9e4f \u6240\u521b\u7684\u201c\u767e\u8db3\u5251\u6cd5\u201d\uff0c\u800c\u8fd9\u8499\u9762\u4eba\u4e0d\u7528\u8bf4\u5b9a\u662f\u90a3\u201c\u5929\u9b54\u201d\u91d1\u6b39\u4e86\u3002\u800c\u4e14\u8f9b\u6377\u53d1\u73b0\u8fd9\u8499\u9762 \u4eba\u6b63\u662f\u65e5\u524d\u5728\u4e08\u4eba\u5cf0\u51c6\u5907\u81ea\u6740\u7684\u8499\u9762\u4eba\uff0c\u5fc3\u60f3\u770b\u4ed6\u75af\u75af\u98a0\u98a0\uff0c\u96be\u9053\u771f\u6709\u70b9\u4e0d\u6b63\u5e38\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5929\u9b54\u91d1\u6b39\u4e00\u8fde\u4e09\u62db\u90fd\u88ab\u82e6\u5eb5\u4e0a\u4eba\u5c01\u4e86\u56de\u6765\uff0c\u4e0d\u77e5\u600e\u5730\u5ffd\u7136\u72c2\u6027\u5927\u53d1\uff0c\u53cc\u8db3\u4e00\u8e6c\uff0c \u8eab\u5251\u5408\u4e00\u5730\u5f80\u524d\u76f4\u523a\uff0c\u4e94\u4eba\u6240\u5408\u7684\u9635\u5fc3\u4e0d\u8fc7\u516d\u4e03\u5c3a\u65b9\u5706\uff0c\u4ed6\u8fd9\u594b\u529b\u4e00\u7eb5\uff0c\u52bf\u5fc5\u7acb\u523b\u649e\u4e0a \u5bf9\u9762\u7684\u8d64\u9633\u9053\u957f\u53ca\u5389\u9e57\u7684\u5251\u5e55\uff0c\u4f46\u662f\u5389\u9e57\u4e00\u58f0\u66b4\u53f1\uff0c\u957f\u5251\u4e00\u4f38\uff0c\u84dd\u5149\u6597\u957f\uff0c\u55ef\u6298\u4e00\u58f0\uff0c \u91d1\u6b39\u957f\u5251\u53ea\u5269\u4e86\u4e00\u4e2a\u67c4\u513f\u3002 \u3000\u3000\u540c\u65f6\u4e00\u58f0\u6e05\u5578\uff0c\u5b9b\u5982\u8001\u9f99\u6e05\u541f\u3001\u4e24\u6761\u4eba\u5f71\u6709\u5982\u884c\u4e91\u6d41\u6c34\u822c\uff0c\u7adf\u4ece\u5bc6\u96c6\u5982\u7f51\u7684\u5251\u5e55\u4e2d \u8d70\u4e86\u51fa\u6765\uff0c\u800c\u4e14\u6b65\u5c65\u5b89\u7965\uff0c\u6709\u82e5\u7f13\u6b65\u884c\u51fa\u4e00\u822c\u3002 \u3000\u3000\u53ee\u7684\u4e00\u58f0\u66b4\u54cd\uff0c\u4e09\u652f\u5251\u5b50\u649e\u5728\u4e00\u8d77\uff0c\u6562\u60c5\u662f\u8d64\u9633\uff0c\u82e6\u5eb5\uff0c\u843d\u82f1\u5251\u4e09\u4eba\u540c\u65f6\u53d1\u62db\u963b\u62e6\uff0c \u4f46\u5374\u843d\u4e86\u7a7a\uff0c\u5e78\u597d\u6ca1\u6709\u5389\u9e57\u5728\u5185\uff0c\u5426\u5219\u5176\u4ed6\u4e8c\u652f\u5251\u5b50\u5fc5\u88ab\u6298\u65ad\u3002 \u3000\u3000\u201c\u4e03\u5999\u795e\u541b\u201d\u633d\u7740\u5434\u51cc\u98ce\u7684\u624b\uff0c\u4f18\u96c5\u5730\u7ad9\u5728\u4e00\u4e08\u4e4b\u5916\u3002 \u3000\u3000\u53ea\u6709\u8c22\u957f\u537f\u662f\u77e5\u9053\u201c\u4e03\u5999\u795e\u541b\u201d\u4e43\u662f\u4e00\u4e2a\u9752\u5e74\u4eba\u4e54\u88c5\u7684\u867d\u7136\u4ed6\u5e76\u4e0d\u77e5\u9053\u8f9b\u6377\u7684\u59d3\u540d \u2014\u2014 \u3000\u3000\u4f46\u4ed6\u6b64\u65f6\u6b63\u601d\u7d22\u7740\u8fd9\u9752\u5e74\u4e00\u8eab\u5947\u7edd\u7684\u795e\u529f\uff0c\u4ed6\u60f3\uff1a\u201c\u5341\u5e74\u524d\u6885\u5c71\u6c11\u672c\u4eba\u4e5f\u4e0d\u8fc7\u5982\u6b64 \u5475\uff0c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\uff0c\u5509\uff0c\u6211\u662f\u8be5\u88ab\u6dd8\u6c70\u4e86\u3002\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4e0d\u8fc7\u624d\u4e09\u5341\u4e03\u5c81\u3002 \u3000\u3000\u5176\u4ed6\u4e09\u4e2a\u638c\u95e8\u4eba\u4e5f\u6014\u6014\u5730\u82e6\u601d\u7740\uff0c\u8f9b\u6377\u51fa\u9635\u7684\u6b65\u4f10\u5b9e\u5728\u592a\u602a\u4e86\uff0c\u4ed6\u4eec\u82e6\u82e6\u601d\u7d22\u4e0d\u51fa \u81ea\u5df1\u9635\u6cd5\u5230\u5e95\u6709\u4ec0\u4e48\u7834\u7efd\uff1f \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4eec\u7684\u9635\u6cd5\u662f\u6ca1\u6709\u7834\u7efd\u7684\uff0c\u5012\u6963\u7684\u662f\u4ed6\u4eec\u78b0\u4e0a\u4e86\u6167\u5927\u5e08\u201c\u8bd8\u6469\u795e\u6b65\u201d\uff0c\u518d \u52a0\u4e0a\u91d1\u6b39\u7684\u4e00\u5473\u62fc\u547d\u4e71\u523a\uff0c\u624d\u88ab\u8f9b\u6377\u5229\u7528\u4e0a\u673a\u4f1a\uff0c\u201c\u8bd8\u6469\u795e\u6b65\u201d\u7684\u795e\u5965\uff0c\u53c8\u5c82\u662f\u8fd9\u51e0\u4e2a \u8001\u513f\u6240\u80fd\u60f3\u901a\uff1f \u3000\u3000\u5237\u5730\u4e00\u4e0b\uff0c\u91d1\u6b39\u4e58\u4eba\u6014\u7740\u65f6\u4e5f\u8dc3\u51fa\u4e86\u9635\u5fc3\uff0c\u7acb\u5728\u8f9b\u5434\u4e24\u8eab\u4eba\u8fb9\u4e0d\u53ca\u4e00\u4e08\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u5728\u60f3\uff1a\u201c\u8fd9\u5251\u9635\u60f3\u4e0d\u5230\u8fd9\u6837\u96be\u6597\uff0c\u8fd8\u6709\u90a3\u5389\u9e57\u7684\u5b9d\u5251\u4e5f\u662f\u4e2a\u9ebb\u70e6\uff0c\u54fc\uff0c\u7b49\u6211 \u90a3\u2018\u6885\u9999\u5251\u2019\u91cd\u6cbb\u6210\u529f\u540e\uff0c\u54b1\u4eec\u518d\u6597\u6597\u770b\u3002\u201d \u3000\u3000\u5389\u9e57\u6781\u5feb\u5730\u76d8\u7b97\u7740\uff1a\u201c\u60f3\u4e0d\u5230\u6885\u5c71\u6c11\u771f\u7684\u6b7b\u800c\u590d\u751f\u4e86\uff0c\u90a3\u5434\u8bcf\u4e91\u7684\u513f\u5b50\u867d\u8f83\u5f31\uff0c\u4f46 \u4e5f\u4e0d\u5bb9\u8f7b\u89c6\uff0c\u8fd8\u6709\u90a3\u4e2a\u75af\u75af\u98a0\u98a0\u7684\u8499\u9762\u4eba\uff0c\u4e0d\u77e5\u662f\u654c\u662f\u53cb\uff0c\u4eca\u65e5\u518d\u6597\u4e0b\u53bb\uff0c\u5b9e\u5728\u4e0d\u4e0a \u7b97\u2026\u2026\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u7acb\u523b\u6717\u58f0\u9053\uff1a\u201c\u4eca\u65e5\u6cf0\u5c71\u5927\u4f1a\u6682\u65f6\u505c\u6b62\uff0c\u5bb9\u4ee5\u540e\u518d\u8ba2\u65e5\u6bd4\u8d5b\u3002\u201d\u8bf4\u7f62\u5bf9 \u82e6\u5eb5\u7b49\u4eba\u4f5c\u4e86\u4e00\u4e2a\u773c\u8272\uff0c\u51e0\u4eba\u4e5f\u6709\u540c\u6837\u7684\u5fc3\u7406\uff0c\u5404\u5411\u5f1f\u5b50\u95e8\u4eba\u6253\u4e2a\u62db\u547c\uff0c\u559d\u58f0\uff1a\u201c\u8d70\u201d\uff0c \u6570\u5341\u6761\u4eba\u5f71\u4e00\u9f50\u8dc3\u8d77\uff0c\u843d\u5728\u5d16\u4e0b\uff0c\u53ea\u6709\u8c22\u957f\u537f\u5fae\u5fae\u4e00\u6014\uff0c\u4ece\u53cd\u65b9\u5411\u4e5f\u7eb5\u4e0b\u4e86\u5c71\u3002 \u3000\u3000\u7fa4\u8c6a\u591a\u662f\u4e3a\u6367\u573a\u6765\u7684\uff0c\u89c1\u5404\u5927\u5251\u6d3e\u90fd\u5df2\u8d70\u4e86\uff0c\u53c8\u6df1\u77e5\u6885\u5c71\u6c11\u4e0d\u597d\u60f9\uff0c\u4e5f\u90fd\u7eb7\u7eb7\u4e0b\u5c71\u3002 \u3000\u3000\u5c71\u5de6\u53cc\u638c\u4e2d\u7684\u795e\u5251\u91d1\u9524\u6797\u5c11\u768b\u53ca\u5343\u624b\u5251\u5ba2\u9646\u65b9\u4e5f\u6df7\u5728\u4eba\u4e1b\u4e2d\u8d70\u4e86\uff0c\u4ed6\u4eec\u5bf9\u201c\u4e03\u5999\u795e \u541b\u201d\u867d\u6000\u6068\uff0c\u4f46\u662f\u51ed\u4eba\u5bb6\u90a3\u4efd\u5a01\u52bf\uff0c\u4ed6\u4eec\u6562\u53ea\u8eab\u4e0a\u53bb\u6311\u6218\u5417\uff1f \u3000\u3000\u4e00\u4e0b\u5b50\uff0c\u5c71\u4e0a\u5c31\u9759\u4e0b\u6765\u4e86\u3002\u98ce\u5439\u7684\u58f0\u97f3\u90fd\u80fd\u542c\u5f97\u89c1\u3002 \u3000\u3000\u73b0\u5728\u53ea\u5269\u4e0b\u4e86\u4e09\u4e2a\u4eba\uff0c\u8f9b\u6377\uff0c\u5434\u51cc\u98ce\u548c\u90a3\u4e2a\u201c\u5929\u9b54\u201d\u91d1\u6b39\u3002\u4e09\u4eba\u4e2d\u5012\u6709\u4e24\u4e2a\u4eba\u662f\u8499 \u7740\u9762\u7684\u3002 \u3000\u3000\u8f9b\u6377\u60f3\u8d77\u85cf\u5728\u6797\u4e2d\u7684\u90a3\u5957\u7f69\u886b\uff0c\u7acb\u523b\u8d70\u8fc7\u53bb\u62fe\u4e86\u8d77\u6765\uff0c\u5f53\u4ed6\u56de\u6765\u65f6\uff0c\u8fdc\u8fdc\u671b\u89c1\u4e86\u4e00 \u6869\u602a\u4e8b\u3002 \u3000\u3000\u53ea\u89c1\u8499\u7740\u9762\u7684\u91d1\u6b39\u5ffd\u7136\u77aa\u7740\u773c\u671b\u7740\u5434\u51cc\u98ce\uff0c\u90a3\u53cc\u773c\u73e0\u4e2d\u5c04\u51fa\u4e00\u79cd\u96be\u4ee5\u5f62\u5bb9\u7684\u53e4\u602a\u5149 \u8292\uff0c\u4ed6\u5ffd\u7136\u4e00\u6b65\u4e00\u6b65\u903c\u8fd1\u5434\u51cc\u98ce\uff0c\u5634\u91cc\u53fd\u54e9\u5495\u565c\u7684\u4e0d\u77e5\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5ffd\u7136\u611f\u5230\u4e00\u80a1\u5bd2\u610f\u4ece\u811a\u5fc3\u5e95\u4e0b\u76f4\u5192\u4e0a\u6765\uff0c\u4ed6\u6253\u4e86\u4e2a\u5bd2\u6218\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u9000\u540e\u56db \u4e94\u6b65\u3002 \u3000\u3000\u91d1\u6b39\u53c8\u8fdb\u4e86\u4e09\u6b65\uff0c\u5434\u51cc\u98ce\u611f\u5230\u65e0\u6bd4\u7684\u6050\u6016\uff0c\u53c8\u9000\u4e86\u4e09\u6b65\u3002 \u3000\u3000\u8f9b\u6377\u5ffd\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u539f\u6765\u4ed6\u53d1\u73b0\u5434\u51cc\u98ce\u80cc\u540e\u5c31\u662f\u60ac\u5d16\uff0c\u800c\u5434\u51cc\u98ce\u811a\u8ddf\u79bb\u5d16\u8fb9\u4e0d\u8fc7\u4e00 \u5c3a\uff0c\u5434\u51cc\u98ce\u5374\u4e1d\u6beb\u6ca1\u53d1\u89c9\u3002 \u3000\u3000\u91d1\u6b39\u5ffd\u7136\u53d1\u72c2\u4f3c\u7684\u5927\u7b11\uff1a\u201c\u4f60\u2014\u2014 \u3000\u3000\u4f60\u7684\u8138\u5b54\u771f\u6f02\u4eae\uff0c\u6211\u6068\u4f60\uff0c\u6211\u8981\u6740\u4f60\u2026\u2026\u563b\u563b\uff0c\u4f60\u4e0d\u662f\u6f02\u4eae\u5417\uff1f\u6211\u4e5f\u66fe\u6f02\u4eae\u8fc7\u5475\uff0c \u563b\u563b\u2026\u2026\u6211\u8981\u6740\u4f60\u2026\u2026\u563b\u2026\u2026\u201d \u3000\u3000\u5434\u51cc\u98ce\u5927\u6012\uff0c\u731b\u7136\u58ee\u80c6\u5927\u559d\u4e00\u58f0\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d\u62fc\u547d\u4e00\u628a\u6293\u51fa\uff0c\u90a3\u77e5\u91d1\u6b39\u52a8\u4e5f\u4e0d\u52a8\uff0c \u5bdf\u7684\u4e00\u58f0\uff0c\u91d1\u6b39\u7684\u8499\u5dfe\u88ab\u6293\u4e86\u4e0b\u6765\uff0c\u53ea\u542c\u5f97\u4e24\u58f0\u60ca\u53eb\uff0c\u523a\u7834\u4e86\u5b81\u9759\u7684\u5c71\u5cf0\u3002 \u3000\u3000\u539f\u6765\u8499\u5dfe\u4e0b\u9762\u662f\u4e00\u5f20\u5947\u4e11\u7684\u8138\uff0c\u9f3b\u6881\u4ece\u4e2d\u95f4\u88ab\u780d\u65ad\uff0c\u8138\u4e0a\u9ed1\u9ed1\u7684\u75a4\u5411\u5916\u7ffb\u51fa\uff0c\u7ea2\u8089 \u9732\u5728\u76ae\u5916\u9762\uff0c\u9664\u4e86\u4e00\u53cc\u773c\u775b\uff0c\u8138\u4e0a\u4f3c\u4e4e\u88ab\u4eba\u7528\u529b\u5212\u4e86\u51e0\u4e0b\uff0c\u662f\u4ee5\u76ae\u8089\u5012\u5377\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u60c5\u5f62\u4e0d\u5bf9\uff0c\u65bd\u51fa\u201c\u8bd8\u6469\u795e\u6b65\u201d\u7684\u529f\u529b\uff0c\u8eab\u5b50\u771f\u6bd4\u4e00\u53ea\u75be\u7bad\u8fd8\u5feb\u5730\u6251\u4e86\u8fc7\u6765\uff0c \u8eab\u4f53\u7834\u7a7a\u65f6\u7adf\u53d1\u51fa\u9e23\u9e23\u7684\u5c16\u5578\u2014\u2014\u4f46\u662f\u8f9b\u6377\u7684\u624b\u6b63\u6251\u5728\u91d1\u6b39\u4e00\u5239\u90a3\u524d\u843d\u811a\u7684\u5730\u4e0a\uff0c\u4e00\u58f0 \u60ca\u53eb\uff0c\u91d1\u6b39\u62b1\u7740\u5434\u51cc\u98ce\u4e00\u8d77\u51b2\u51fa\u5d16\u8fc7\uff0c\u6d41\u661f\u822c\u843d\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u540c\u6837\u715e\u4e0d\u4f4f\uff0c\u547c\u5730\u4e00\u4e0b\u51b2\u4e86\u51fa\u53bb\uff0c\u4f46\u662f\u8fd9\u7b49\u751f\u6b7b\u5173\u5934\u5c31\u663e\u51fa\u4ed6\u7980\u8d4b\u7684\u673a\u7075\uff0c \u201c\u6251\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u7684\u4e94\u6307\u63d2\u5165\u4e86\u77f3\u5d16\uff0c\u867d\u7136\u51b2\u52b2\u4ecd\u4f7f\u4ed6\u5e26\u51fa\u6570\u5bf8\u2014\u2014 \u3000\u3000\u4ed6\u7684\u624b\u6307\u5c31\u5728\u77f3\u5d16\u4e0a\u5212\u51fa\u4e94\u9053\u5bf8\u6df1\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u4ed6\u624b\u4e0a\u4e00\u4f7f\u52b2\uff0c\u8eab\u5b50\u7acb\u7ffb\u4e86\u4e0a\u6765\uff0c\u843d\u5730\u65f6\u8f7b\u5f97\u5b9b\u5982\u4e00\u5f20\u67af\u53f6\u843d\u5730\u3002 \u3000\u3000\u8fd9\u4e9b\u52a8\u4f5c\u5374\u662f\u808c\u8089\u7684\u81ea\u7136\u53cd\u5e94\uff0c\u4e1d\u6beb\u6ca1\u6709\u7ecf\u8fc7\u4ed6\u7684\u5927\u8111\uff0c\u56e0\u4e3a\u4ed6\u6b64\u65f6\u5927\u8111\u4e2d\u660f\u660f\u6d51 \u6d51\uff0c\u53ea\u662f\u4e00\u7247\u7a7a\u767d\u3002 \u3000\u3000\u5d16\u4e0b\u9762\u4e91\u96fe\u6eda\u6eda\uff0c\u4e0d\u77e5\u5176\u6df1\u3002 \u3000\u3000\u4ed6\u7684\u5934\u8111\u4e2d\u50cf\u662f\u6062\u590d\u5230\u4e86\u6d2a\u8352\u7684\u8fdc\u53e4\u65f6\u4ee3\uff0c\u6d51\u6d51\u7136\u4e7e\u5764\u4e0d\u5206\uff0c\u4ed6\u7684\u5589\u5934\u53d1\u51fa\u53ea\u6709\u4ed6 \u81ea\u5df1\u542c\u5f97\u51fa\u7684\u54c0\u9e23\uff0c\u8fd9\u4e0d\u662f\u54ed\uff0c\u4f46\u6bd4\u54ed\u66f4\u60b2\u60e8\u4e07\u500d\u3002 \u3000\u3000\u5c71\u98ce\u6e10\u52b2\uff0c\u4ed6\u7684\u8863\u886b\u6ca5\u6ca5\u4f5c\u54cd\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u4ed6\u7684\u9762\u5dfe\u8fce\u98ce\u800c\u63ed\uff0c\u98d8\u626c\u4e86\u4e24\u4e0b\uff0c\u5c31\u98de \u843d\u5d16\u5e95\u3002 \u3000\u3000\u4e0d\u77e5\u4e0d\u89c9\u7684\u6d41\u4e0b\u70ed\u6cea\uff0c\u6cea\u73e0\u7f13\u7f13\u5730\u6cbf\u7740\u9762\u988a\u6d41\u4e0b\u6765\uff0c\u505c\u4e86\u5bf8\u4f1a\uff0c\u6ef4\u5728\u895f\u524d\u3002 \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u7684\u5934\u8111\u6e05\u9192\u8fc7\u6765\uff0c\u4ed6\u53d7\u7740\u6709\u751f\u4ee5\u6765\u4ece\u672a\u6709\u7684\u75db\u82e6\uff0c\u4ed6\u73b0\u5728\u6df1\u6df1\u76f8\u4fe1\uff0c\u53cb\u60c5 \u5bf9\u4ed6\u6bd4\u7231\u60c5\u66f4\u4e3a\u91cd\u8981\u3002 \u3000\u3000\u5468\u906d\u9759\u6781\u4e86\uff0c\u4ed6\u5634\u5507\u6296\u52a8\u7740\uff0c\u4f46\u8bf4\u4e0d\u51fa\u4e00\u4e2a\u5b57\u6765\u3002 \u3000\u3000\u65e5\u89c2\u5cf0\u4e0a\u987f\u65f6\u9759\u4e86\u4e0b\u6765\uff0c\u5c71\u98ce\u5439\u5f97\u6811\u68a2\u6c99\u6c99\u4f5c\u54cd\uff0c\u8f9b\u6377\u7acb\u5728\u5d16\u65c1\uff0c\u4fef\u671b\u811a\u4e0b\u6eda\u6eda\u4e91 \u96fe\uff0c\u6df1\u4e0d\u77e5\u5e95\uff0c\u4e0d\u7981\u957f\u53f9\u4e00\u58f0\uff0c\u4ed6\u5583\u5583\u81ea\u8bed\u9053\uff1a \u3000\u3000\u201c\u8f9b\u6377\u554a\uff01\u4f60\u771f\u662f\u4e00\u4e2a\u4e0d\u7965\u7684\u4eba\uff0c\u51e1\u662f\u5bf9\u4f60\u751f\u4e86\u611f\u60c5\u7684\u4eba\u5c31\u5f97\u906d\u5230\u4e0d\u5e73\uff0c\u7238\u5988\u60e8\u6b7b\uff0c \u6885\u53d4\u53d4\u53d7\u4e86\u6697\u7b97\uff0c\u4faf\u4e8c\u53d4\u88ab\u4eba\u6740\u6b7b\uff0c\u5c11\u78a7\u548c\u83c1\u513f\u846c\u8eab\u6d77\u5e95\uff0c\u6885\u9f84\u4e0b\u843d\u4e0d\u660e\uff0c\u8001\u5929\u554a\uff01\u4f60 \u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u6b8b\u9177\uff0c\u53c8\u593a\u53bb\u4e86\u5927\u54e5\u7684\u547d\uff01\u201d\u98ce\u8d77\u5904\uff0c\u4e91\u6d9b\u6c79\u6d8c\uff0c\u851a\u6210\u5947\u89c2\u3002 \u3000\u3000\u201c\u5f85\u6211\u4e86\u7ed3\u8fd9\u4e9b\u6069\u4ec7\uff0c\u5c31\u957f\u4f34\u90a3\u68b5\u58f0\u9752\u706f\uff0c\u505a\u534a\u4e16\u7684\u6728\u5934\u4eba\u7b97\u4e86\u2026\u2026\u3002\u201d \u3000\u3000\u201c\u5927\u54e5\u554a\uff01\u597d\u597d\u5b89\u606f\u5427\uff01\u6211\u4f1a\u66ff\u4f60\u590d\u4ec7\u7684\uff01\u201d \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u60f3\u5230\u90a3\u4e2a\u7f8e\u4e3d\u7684\u82cf\u8559\u82b7\uff0c\u4ed6\u5fc3\u60f3\uff1a\u201c\u82cf\u59d1\u5a18\u66fe\u4e00\u518d\u8981\u6211\u4eec\u53bb\u770b\u5979\u4e00\u6b21\uff0c\u5176 \u5b9e\u53ea\u662f\u5e0c\u671b\u518d\u89c1\u5927\u54e5\u4e00\u9762\u7f62\u4e86\uff0c\u5982\u4eca\u6211\u600e\u4e48\u53bb\u89c1\u5979\u5462\uff1f\u5509\uff0c\u4e16\u4e0a\u4e3a\u4ec0\u4e48\u8981\u6709\u8fd9\u8bb8\u591a\u60b2\u60e8 \u7684\u4e8b\u5462\uff1f\u201d \u3000\u3000\u4ed6\u6108\u60f3\u6108\u70e6\u607c\uff0c\u5ffd\u7136\u53cc\u8db3\u4e00\u8e6c\uff0c\u53cd\u8eab\u8d70\u53bb\u5c06\u4e49\u4ec6\u4f59\u5fe0\u7684\u5c38\u4f53\u57cb\u4e86\uff0c\u8eab\u5f62\u9661\u7136\u62d4\u8d77\u516d \u4e03\u4e08\u9ad8\uff0c\u5012\u7a7f\u8fc7\u4e00\u7247\u6811\u6797\uff0c\u60ca\u8d77\u4e24\u53ea\u5927\u9e1f\uff0c\u4ed6\u7684\u8eab\u4f53\u5374\u547c\u7684\u4e00\u58f0\u4ece\u4e24\u53ea\u9e1f\u4e4b\u95f4\u98de\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4e24\u53ea\u9e1f\u4e92\u76f8\u4e00\u9e23\uff0c\u4f3c\u4e4e\u5947\u602a\u8fd9\u4e9b\u5e73\u5e38\u53cc\u811a\u8d70\u8def\u7684\u5bb6\u4f19\u600e\u4e48\u4e5f\u4f1a\u98de\uff1f \u3000\u3000\u56db\u5ddd\u6cef\u6c5f\u4e0b\u6e38\uff0c\u6709\u4e00\u6761\u6885\u6eaa\uff0c\u4ece\u5c71\u8c37\u6d41\u7ecf\u4e00\u4e2a\u5927\u576a\uff0c\u5524\u4f5c\u6c99\u9f99\u576a\uff0c\u576a\u4e0a\u7a00\u843d\u6751\u820d\uff0c \u9e21\u72ac\u76f8\u95fb\uff0c\u662f\u4e2a\u4e16\u5916\u6843\u6e90\uff0c\u6885\u6eaa\u5939\u5cb8\u6570\u767e\u91cc\u5185\uff0c\u5168\u662f\u7ea2\u767d\u53e4\u6885\uff0c\u4e2d\u65e0\u6742\u6811\u3002 \u3000\u3000\u65f6\u81f3\u51ac\u81f3\uff0c\u5bd2\u98ce\u9f13\u7740\u545c\u545c\u7684\u58f0\u54cd\uff0c\u628a\u6f2b\u5929\u96ea\u82b1\u5377\u5f97\u7c89\u7eb7\u98de\u821e\uff0c\u5929\u662f\u7070\u7684\uff0c\u5730\u662f\u94f6\u767d \u7684\uff0c\u576a\u56f4\u7684\u6885\u6797\u5f00\u5f97\u767e\u82b1\u4e89\u8273\uff0c\u6e05\u9999\u6012\u653e\uff0c\u70b9\u70b9\u7ea2\u767d\u6620\u5728\u96ea\u5730\u4e0a\uff0c\u851a\u6210\u5947\u8da3\u3002 \u3000\u3000\u5de6\u89d2\u4e00\u95f4\u8305\u68da\uff0c\u9876\u76d6\u7740\u539a\u539a\u7684\u767d\u96ea\uff0c\u6d3b\u50cf\u662f\u8981\u538b\u5f97\u90a3\u68da\u9876\u584c\u4e0b\u6765\u4f3c\u7684\u3002 \u3000\u3000\u68da\u5185\u653e\u7740\u4e00\u5f20\u77f3\u684c\uff0c\u4e24\u4e2a\u8001\u8005\u5728\u76f8\u5bf9\u5955\u68cb\uff0c\u65c1\u8fb9\u56f4\u4e86\u51e0\u4e2a\u95f2\u4eba\u89c2\u68cb\uff0c\u68cb\u5b50\u843d\u76d8\u53d1\u51fa \u6e05\u8106\u7684\u58f0\u54cd\uff0c\u6562\u60c5\u90a3\u68cb\u76d8\u4e5f\u662f\u77f3\u505a\u7684\u3002 \u3000\u3000\u8305\u68da\u4e24\u9762\u65e0\u58c1\uff0c\u672c\u6765\u751a\u51b7\uff0c\u4f46\u68da\u89d2\u5374\u70e7\u7740\u4e00\u5806\u706b\uff0c\u9635\u9635\u767d\u70df\u5f25\u6f2b\uff0c\u67f4\u706b\u53d1\u51fa\u6bd5\u5265\u6bd5 \u5265\u7684\u58f0\u97f3\uff0c\u5374\u900f\u51fa\u4e00\u80a1\u4ee4\u4eba\u5fc3\u795e\u4ff1\u723d\u7684\u6e05\u9999\uff0c\u6562\u60c5\u70e7\u7684\u662f\u4e00\u5806\u677e\u679d\u3002 \u3000\u3000\u53f3\u9762\u95e8\u5e18\u6380\u8d77\uff0c\u8d70\u8fdb\u4e00\u4e2a\u4eba\u6765\uff0c\u90a3\u4eba\u767d\u53d1\u98d8\u98d8\uff0c\u5934\u53d1\u51e0\u4e4e\u843d\u5f97\u5149\u9876\uff0c\u8138\u4e0a\u76b1\u7eb9\u5bc6\u5e03\uff0c \u663e\u5f97\u5f02\u5e38\u82cd\u8001\uff0c\u4f46\u90a3\u4e3e\u6b62\u4e2d\u81ea\u7136\u6d41\u9732\u51fa\u4e00\u80a1\u4ee4\u4eba\u5fc3\u6298\u7684\u5a01\u6b66\u3002 \u3000\u3000\u8fd9\u8001\u4eba\u5e74\u7eaa\u770b\u6765\u603b\u5728\u4e03\u65ec\u4ee5\u4e0a\u4e86\uff0c\u53ea\u89c1\u4ed6\u4e00\u9762\u6296\u4e86\u6296\u76ae\u888d\u4e0a\u9762\u7684\u96ea\u82b1\uff0c\u53e6\u4e00\u53ea\u624b\u63d0 \u7740\u4e00\u4e2a\u7a7a\u9152\u58f6\uff0c\u6562\u60c5\u662f\u8981\u53bb\u6cbd\u9152\u7684\u3002 \u3000\u3000\u56f4\u89c2\u68cb\u6218\u7684\u51e0\u4e2a\u4eba\u4e00\u89c1\u8001\u8005\uff0c\u4f3c\u4e4e\u5341\u5206\u606d\u656c\uff0c\u9f50\u58f0\u9053\uff1a\u201c\u6885\u516c\u5174\u81f4\u597d\uff0c\u5728\u8fd9\u5927\u98ce\u96ea \u8fd8\u6765\u770b\u4e0b\u68cb\uff1f\u201d \u3000\u3000\u90a3\u8001\u4eba\u6148\u7965\u5730\u7b11\u4e86\u7b11\uff0c\u9053\uff1a\u201c\u6211\u662f\u53bb\u6865\u5934\u6cbd\u4e00\u58f6\u2018\u6885\u5b50\u9999\u2019\u8001\u9152\u7684\uff0c\u987a\u4fbf\u6765\u770b\u770b\u5434 \u8001\u4e0b\u68cb\uff1f\u201d \u3000\u3000\u5750\u5728\u5bf9\u9762\u7684\u8001\u5934\u6b63\u662f\u5434\u8001\uff0c\u4ed6\u62ac\u8d77\u5934\u6765\u5411\u8fd9\u8001\u8005\u70b9\u9996\u4e3a\u793c\u9053\uff1a\u201c\u539f\u6765\u662f\u6885\u8001\u5148\u751f\u2014 \u2014\u201d\u63a5\u7740\u53c8\u62c8\u5b50\u6c89\u601d\u3002 \u3000\u3000\u6885\u5148\u751f\u4e0d\u7981\u5403\u4e86\u4e00\u60ca\uff0c\u4ed6\u7d20\u77e5\u8fd9\u5434\u8001\u4e43\u662f\u95fb\u540d\u5929\u4e0b\u7684\u68cb\u5955\u9ad8\u624b\uff0c\u636e\u8bf4\u5df2\u6709\u4e5d\u6bb5\u68cb\u529b\uff0c \u76ee\u524d\u4e0e\u8fd9\u80cc\u5bf9\u81ea\u5df1\u4e4b\u4eba\u5bf9\u5955\uff0c\u7adf\u4f3c\u5341\u5206\u5403\u529b\uff0c\u4e0d\u7531\u8d70\u8fd1\u6253\u91cf\u90a3\u4eba\u3002 \u3000\u3000\u65c1\u8fb9\u4e00\u4eba\u5fd9\u5bf9\u6885\u8001\u5148\u751f\u4ecb\u7ecd\u9053\uff1a\u201c\u8fd9\u4f4d\u91d1\u6874\u5148\u751f\u4e43\u662f\u4eac\u57ce\u7b2c\u4e00\u9ad8\u624b\uff0c\u8def\u8fc7\u54b1\u4eec\u6c99\u9f99 \u576a\uff0c\u7279\u5411\u5434\u8001\u6311\u6218\u5341\u5c40\u3002\u201d \u3000\u3000\u6885\u8001\u5148\u751f\u542c\u4e86\u4e0d\u7981\u4e00\u60ca\uff0c\u6562\u60c5\u4ed6\u4e5f\u77e5\u9053\u8fd9\u540d\u6ee1\u4eac\u5e08\u7684\u56f4\u68cb\u9ad8\u624b\u91d1\u6874\u4e4b\u540d\u3002 \u3000\u3000\u8fd9\u65f6\u684c\u4e0a\u68cb\u5c40\u5df2\u5230\u4e86\u5c06\u5b8c\u9636\u6bb5\uff0c\u663e\u7136\u5434\u8001\u5c45\u4e0d\u5229\u7684\u5f62\u52bf\uff0c\u662f\u4ee5\u5434\u8001\u624b\u62c8\u4e00\u5b50\uff0c\u4e00\u76f4 \u82e6\u601d\u4e0d\u51b3\u3002 \u3000\u3000\u5468\u56f4\u65c1\u89c2\u8005\u9664\u4e86\u6885\u8001\u5148\u751f\u4ece\u6ca1\u6709\u89c1\u8fc7\u4ed6\u4e0b\u68cb\u4ee5\u5916\uff0c\u5168\u662f\u5185\u884c\u4eba\uff0c\u90fd\u77e5\u5434\u8001\u5f62\u52bf\u6781\u4e3a \u4e0d\u5229\uff0c\u8fd9\u4e00\u5b50\u5173\u7cfb\u5c24\u5927\uff0c\u4e0d\u7531\u90fd\u4e3a\u4ed6\u62c5\u5fe7\uff0c\u597d\u50cf\u5434\u8001\u8f93\u4e86\uff0c\u5c31\u662f\u5730\u65b9\u4e0a\u4eba\u7684\u7f9e\u8fb1\u4e00\u822c\u3002 \u3000\u3000\u8fd9\u65f6\u95e8\u5e18\u4e00\u52a8\uff0c\u53c8\u8d70\u8fdb\u4e00\u4eba\u6765\uff0c\u4f17\u4eba\u90fd\u5728\u6ce8\u610f\u68cb\u5c40\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\u6765\u4eba\u3002\u53ea\u6709\u6885\u8001\u5148 \u751f\u56de\u9996\u4e00\u770b\uff0c\u8fd9\u4e00\u770b\uff0c\u987f\u65f6\u4ee4\u4ed6\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u539f\u6765\u8fdb\u6765\u7684\u4eba\u4e43\u662f\u4e00\u4e2a\u4e2d\u5e74\u5112\u751f\uff0c\u9762\u8c8c\u6e05\u7fdf\u800c\u6ee1\u6d12\uff0c\u9762\u5b54\u5374\u751a\u964c\u751f\uff0c\u663e\u7136\u4e0d\u662f\u672c\u5730 \u4e61\u4eba\uff0c\u5947\u7684\u662f\u8fd9\u4e48\u51b7\u7684\u5927\u96ea\u5929\uff0c\u4ed6\u4ece\u5916\u9762\u8d70\u5165\uff0c\u8eab\u4e0a\u4e00\u4e1d\u96ea\u82b1\u90fd\u6ca1\u6709\uff0c\u800c\u4e14\u8eab\u4e0a\u53ea\u7740\u4e86 \u4e00\u88ad\u9752\u8272\u5355\u888d\uff0c\u4e0a\u9762\u5374\u6ca1\u6709\u4e00\u70b9\u5bd2\u51b7\u4e4b\u8272\u3002 \u3000\u3000\u8fd9\u79cd\u60c5\u5f62\u663e\u7136\u662f\u6765\u4eba\u5177\u6709\u6781\u4e0a\u4e58\u7684\u5185\u529f\uff0c\u8fd9\u60c5\u5f62\u5bf9\u6885\u8001\u5148\u751f\u6765\u8bf4\u662f\u591a\u4e48\u719f\u6089\u554a\uff0c\u4f46 \u73b0\u5728\uff0c\u8fd9\u4e9b\u90fd\u6210\u4e86\u8fc7\u53bb\u6765\u4eba\u5411\u684c\u4e0a\u68cb\u5c40\u77a5\u4e86\u4e00\u773c\uff0c\u521a\u79bb\u5f00\u7684\u773c\u5149\u53c8\u79fb\u4e86\u56de\u6765\uff0c\u6562\u60c5\u4ed6\u4e5f \u88ab\u8fd9\u60ca\u9669\u7684\u68cb\u8c31\u5438\u5f15\u4f4f\u4e86\u3002 \u3000\u3000\u8fd9\u4e2d\u5e74\u5112\u751f\u5411\u5434\u8001\u53ca\u91d1\u6874\u6253\u91cf\u4e86\u4e00\u773c\uff0c\u4f3c\u4e4e\u60ca\u5947\u4e24\u4eba\u7684\u68cb\u529b\uff0c\u5e76\u4e14\u7acb\u523b\u53ef\u4ee5\u770b\u51fa\u4ed6 \u4e5f\u5728\u6c89\u601d\uff0c\u66ff\u72b9\u8c6b\u4e0d\u51b3\u7684\u5434\u8001\u60f3\u4e00\u7740\u5999\u6c41\u3002 \u3000\u3000\u68da\u5185\u5b89\u9759\u6781\u4e86\uff0c\u53ea\u6709\u706b\u820c\u718a\u718a\u548c\u677e\u679d\u6bd5\u6bd5\u5265\u5265\u7684\u7206\u54cd\u7740\u3002 \u3000\u3000\u5434\u8001\u7684\u68cb\u5b50\u8fd8\u60ac\u5728\u7a7a\u4e2d\uff0c\u4ed6\u7684\u4e00\u53cc\u767d\u7709\u51e0\u4e4e\u76b1\u5230\u4e00\u8d77\u53bb\u4e86\uff0c\u5bf9\u9762\u7684\u91d1\u6874\u5374\u6e10\u6e10\u9732\u51fa \u5f97\u610f\u4e4b\u8272\u3002 \u3000\u3000\u65f6\u95f4\u4e00\u5206\u4e00\u5999\u8fc7\u53bb\uff0c\u5434\u8001\u7684\u68cb\u5b50\u8fd8\u662f\u6ca1\u6709\u51b3\u5b9a\uff0c\u5ffd\u7136\u6885\u8001\u5148\u751f\u7528\u7a7a\u9152\u58f6\u7684\u58f6\u5634\u5f80\u68cb \u76d8\u5de6\u9762\u4e00\u4e2a\u7a7a\u683c\u4e0a\u4e00\u6307\uff0c\u9053\uff1a\u201c\u5434\u8001\uff0c\u8fd9\u513f\u8fd8\u6709\u4e00\u4e2a\u7a7a\u683c\u513f\u54e9\u3002\u201d \u3000\u3000\u51e0\u4eba\u4e00\u542c\u4fbf\u77e5\u4ed6\u6ee1\u53e3\u5916\u884c\uff0c\u4f46\u90a3\u4e2d\u5e74\u5112\u751f\u7acb\u523b\u73b0\u51fa\u4e00\u8138\u60ca\u8bb6\u65e0\u6bd4\u7684\u989c\u8272\u3002 \u3000\u3000\u6885\u8001\u5148\u751f\u50cf\u662f\u770b\u5f97\u4e0d\u8010\u70e6\u4e86\uff0c\u5411\u4f17\u4eba\u70b9\u70b9\u5934\uff0c\u9053\uff1a\u201c\u6211\u8fd8\u5f97\u53bb\u6865\u5934\u6cbd\u9152\u5462\uff0c\u53bb\u8fdf\u4e86 \u90a3\u9648\u5e74\u2018\u6885\u5b50\u9999\u2019\u53ea\u6015\u8981\u5356\u5b8c\u4e86\u54e9\u3002\u201d\u8bf4\u7f62\u8f6c\u8eab\u8d70\u51fa\u8305\u68da\u3002 \u3000\u3000\u4e2d\u5e74\u5112\u751f\u8138\u4e0a\u60ca\u5bb9\u672a\u6d88\uff0c\u5434\u8001\u68cb\u5b50\u201c\u54af\u201d\u7684\u4e00\u58f0\u843d\u4e86\u4e0b\u6765\uff0c\u6b63\u662f\u6885\u8001\u5148\u751f\u65b9\u624d\u6240\u6307 \u4e4b\u5904\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u65c1\u89c2\u7684\u51e0\u4eba\u4e5f\u60ca\u547c\u51fa\u6765\uff0c\u539f\u6765\u8fd9\u4e00\u5b50\u6240\u843d\uff0c\u987f\u65f6\u7adf\u5c06\u5168\u90e8\u68cb\u5c40\u6539\u53d8\u4e86\u5f62\u52bf\uff0c \u5434\u8001\u5927\u6709\u8f6c\u8d25\u4e3a\u80dc\u4e4b\u52bf\u3002 \u3000\u3000\u5927\u5bb6\u7edd\u4e0d\u76f8\u4fe1\u90a3\u4e2a\u5e73\u7d20\u4e0d\u4f1a\u4e0b\u68cb\u7684\u6885\u8001\u5148\u751f\u7adf\u80fd\u60f3\u51fa\u8fd9\u4e00\u7740\u5999\u68cb\uff0c\u5fc3\u4e2d\u90fd\u60f3\u662f\u51d1\u5de7 \u7f62\u4e86\u3002 \u3000\u3000\u91d1\u6874\u82e6\u601d\u7247\u523b\uff0c\u53f9\u9053\uff1a\u201c\u8fd9\u4e00\u7740\u68cb\u7aef\u7684\u5999\u7edd\u5929\u4e0b\uff0c\u6211\u91d1\u6874\u81ea\u53f9\u5f17\u5982\u3002\u201d \u3000\u3000\u5434\u8001\u77e5\u9053\u81ea\u5df1\u662f\u88ab\u6885\u8001\u5148\u751f\u63d0\u9192\u7684\uff0c\u4e0d\u7ba1\u6885\u8001\u5148\u751f\u662f\u4e0d\u662f\u6709\u610f\uff0c\u81f3\u5c11\u80dc\u5f97\u4e0d\u7b97\u5149\u91c7\uff0c \u5fae\u5fae\u4e00\u7b11\uff0c\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u90a3\u4e2d\u5e74\u5112\u751f\u5374\u9762\u5e26\u5f02\u8272\u6084\u6084\u5730\u9000\u51fa\u4e86\u68da\u95e8\uff0c\u7f13\u7f13\u800c\u884c\uff0c\u6b65\u5c65\u4e0e\u5e38\u4eba\u65e0\u5f02\uff0c\u4f46\u6b65\u5b50\u5374 \u5927\u5f97\u51fa\u5947\uff0c\u4e09\u4e24\u6b65\u5df2\u5728\u6570\u4e08\u4e4b\u5916\uff0c\u51db\u51bd\u7684\u5317\u98ce\u5439\u5f97\u547c\u547c\u5c16\u53eb\uff0c\u4ed6\u90a3\u4e00\u88ad\u5355\u888d\u5374\u52a8\u90fd\u672a\u52a8 \u4e00\u4e0b\uff0c\u96ea\u5730\u4e0a\u8fde\u4e00\u4e2a\u8db3\u5370\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u5583\u5583\u81ea\u8bed\uff1a\u201c\u90a3\u8001\u513f\u82e5\u662f\u771f\u7684\u6709\u610f\u6307\u70b9\uff0c\u90a3\u4e48\u90a3\u4e00\u68cb\u5b9e\u5728\u592a\u5999\u4e86\uff0c\u563f\uff0c\u4e0d\u53ef\u80fd\u7f62\uff0c \u96be\u9053\u4e16\u4e0a\u8fd8\u6709\u68cb\u827a\u8d85\u51fa\u6211\u7684\uff1f\u201d \u3000\u3000\u4f46\u4ed6\u7684\u6ce8\u610f\u529b\u7acb\u523b\u88ab\u576a\u7f18\u90a3\u5343\u767e\u682a\u6885\u6811\u5438\u5f15\u4f4f\u4e86\uff0c\u4ed6\u7f13\u7f13\u8d70\u5411\u524d\u53bb\u3002 \u3000\u3000\u5929\u8272\u66f4\u6697\u4e86\uff0c\u96ea\u82b1\u5374\u6108\u98de\u6108\u7d27\uff0c\u5730\u4e0a\u79ef\u96ea\u6015\u5df2\u6709\u5c3a\u591a\u6df1\u4e86\uff0c\u8fdc\u8fdc\u8d70\u6765\u4e00\u4e2a\u8001\u6001\u9f99\u949f \u7684\u5f71\u5b50\uff0c\u90a3\u8001\u4eba\u4e3e\u6b65\u7ef4\u8270\u5730\u5728\u96ea\u5730\u4e0a\u6491\u7740\uff0c\u76ae\u888d\u5b50\u4e0a\u767d\u767d\u7684\u4e00\u5c42\uff0c\u5de6\u624b\u63d0\u7740\u4e00\u4e2a\u9152\u58f6\uff0c \u58f6\u76d6\u867d\u76d6\u5f97\u7d27\u7d27\u7684\uff0c\u4f46\u4e00\u9635\u9635\u9187\u51bd\u7684\u9152\u9999\u5473\u4ecd\u4ece\u58f6\u5634\u4e2d\u900f\u4e86\u51fa\u6765\u3002\u8001\u4eba\u8db3\u8fc7\u7684\u5730\u65b9\uff0c\u7559 \u4e0b\u4e00\u4e2a\u4e2a\u6df1\u6df1\u7684\u8db3\u5370\uff0c\u4f46\u5bfb\u5373\u53c8\u88ab\u843d\u96ea\u63a9\u76d6\u4f4f\u4e86\u3002 \u3000\u3000\u8001\u4eba\u6765\u8fd1\uff0c\u6b63\u662f\u90a3\u4e2a\u6885\u8001\u5148\u751f\uff0c\u4ed6\u6cbd\u4e86\u9152\u8d70\u56de\u6765\u3002 \u3000\u3000\u4ed6\u6b63\u6697\u5730\u91cc\u60f3\u7740\uff1a\u201c\u90a3\u5112\u751f\u597d\u7eaf\u7684\u529f\u2014\u2014\u5509\uff0c\u60f3\u5f53\u5e74\u51b0\u5c71\u70c8\u706b\u91cc\u6211\u4e5f\u662f\u4e00\u88ad\u8584\u886b\uff0c \u73b0\u5728\u8fd9\u4e00\u70b9\u98ce\u96ea\u5c31\u53d7\u4e0d\u4e86\uff0c\u5509\uff0c\u771f\u662f\u8001\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u7ad9\u4f4f\u4e86\u811a\uff0c\u539f\u6765\u90a3\u5112\u751f\u6b63\u7ad9\u5728\u576a\u7f18\u89c2\u6d77\uff0c\u4e00\u88ad\u9752\u8863\u5bf8\u7740\u94f6\u8272\u7684\u5927\u5730\uff0c\u5b9b\u82e5 \u795e\u4ed9\u4e2d\u4eba\u3002 \u3000\u3000\u201c\u5343\u5c71\u51b0\u96ea\u4e07\u91cc\u6c99\uff0c\u8349\u4e3a\u7c1f\u5e2d\u4e3a\u5bb6\uff0c\u4f9d\u7a00\u82b1\u843c\u60c5\u96be\u8fa8\u2014\u2014\u201d \u3000\u3000\u541f\u5230\u8fd9\u91cc\uff0c\u6885\u8001\u5148\u751f\u5927\u5403\u4e00\u60ca\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5112\u751f\u6587\u624d\u4e4b\u9ad8\uff0c\u7aef\u7684\u5e73\u751f\u4ec5\u89c1\uff0c\u8fd9\u2018\u4f9d \u7a00\u82b1\u843c\u60c5\u96be\u8fa8\u2019\u582a\u79f0\u7edd\u5999\u597d\u8f9e\uff0c\u4e0d\u77e5\u4ed6\u4e0b\u4e00\u53e5\u5982\u4f55\u5bf9\u6cd5\uff1f\u201d \u3000\u3000\u90a3\u5112\u751f\u5927\u6982\u4e5f\u56e0\u8fd9\u53e5\u201c\u4f9d\u7a00\u82b1\u843c\u60c5\u96be\u8fa8\u201d\u592a\u5999\u592a\u5999\uff0c\u4e00\u65f6\u627e\u4e0d\u51fa\u540c\u6837\u597d\u7684\u4e0b\u4e00\u53e5\u6765 \u6536\u5c3e\uff0c\u662f\u4ee5\u541f\u548f\u4e86\u534a\u5929\uff0c\u8fd8\u6ca1\u6709\u5bfb\u5230\u5999\u53e5\u3002 \u3000\u3000\u5ffd\u7136\u540e\u9762\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u63a5\u9053\uff1a\u201c\u98d8\u6e3a\u82ac\u99a8\u5e7b\u4ea6\u4f73\uff01\u201d \u3000\u3000\u90a3\u5112\u751f\u4e00\u62cd\u5927\u817f\uff0c\u4e0d\u7981\u53eb\u9053\uff1a\u201c\u597d\u4e00\u53e5\u2018\u98d8\u6e3a\u82ac\u99a8\u5e7b\u4ea6\u4f73\u2019\uff01\u201d \u3000\u3000\u8fd9\u65f6\u5df2\u8fd1\u9ec4\u660f\uff0c\u8fdc\u5904\u5c71\u96fe\u8d77\u98ce\uff0c\u6885\u6797\u5c3d\u5165\u96fe\u4e2d\uff0c\u82b1\u843c\u7eb7\u7eb7\u96be\u8fa8\uff0c\u679c\u771f\u4f3c\u5e7b\u8fd8\u771f\u3002 \u3000\u3000\u5112\u751f\u56de\u9996\u4e00\u770b\uff0c\u6b63\u662f\u90a3\u6885\u8001\u5148\u751f\u3002 \u3000\u3000\u5112\u751f\u5bf9\u6885\u8001\u5148\u751f\u4e00\u63d6\u9053\uff1a\u201c\u5c0f\u751f\u884c\u6e38\u534a\u751f\uff0c\u8fd8\u662f\u7b2c\u4e00\u6b21\u78b0\u4e0a\u4e4b\u5148\u751f\u8fd9\u79cd\u7edd\u4e16\u6587\u624d\uff0c \u5c31\u662f\u65b9\u624d\u90a3\u4e00\u7740\u5999\u68cb\uff0c\u8bba\u653b\u5982\u5927\u6c5f\u4e1c\u53bb\uff0c\u8bba\u5b88\u5219\u94c1\u58c1\u94dc\u5899\uff0c\u786e\u662f\u5999\u7edd\u4eba\u5bf0\u3002\u201d \u3000\u3000\u6885\u8001\u5148\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u8fd8\u4e86\u4e00\u63d6\u9053\uff1a\u201c\u670b\u53cb\u98ce\u91c7\u7edd\u4fd7\uff0c\u8001\u592b\u5fc3\u6298\u4e0d\u5df2\u3002\u201d \u3000\u3000\u90a3\u5112\u751f\u9053\uff1a\u201c\u5c0f\u751f\u5b66\u6587\u4e0d\u6210\uff0c\u53bb\u800c\u5b66\u5251\uff0c\u5b66\u5251\u4e0d\u6210\uff0c\u53bb\u800c\u5b66\u753b\uff0c\u865a\u5ea6\u534a\u4e16\uff0c\u4e00\u65e0\u6240 \u6210\uff0c\u4eca\u5929\u5e78\u9047\u8001\u5148\u751f\uff0c\u5148\u751f\u4e0d\u5acc\uff0c\u53ef\u613f\u5bf9\u6b64\u826f\u666f\u4e00\u8c08\uff1f\u201d \u3000\u3000\u6885\u8001\u5148\u751f\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u56fa\u6240\u613f\u4e5f\uff0c\u975e\u6562\u8bf7\u8033\u3002\u201d \u3000\u3000\u63a5\u7740\u4e24\u4eba\u95ee\u4e86\u59d3\u6c0f\uff0c\u90a3\u5112\u751f\u81ea\u79f0\u59d3\u5434\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u8c08\uff0c\u7adf\u7136\u5341\u5206\u6295\u673a\uff0c\u5927\u6709\u76f8\u89c1\u6068\u665a\u4e4b\u611f\u3002 \u3000\u3000\u90a3\u5112\u751f\u6697\u9053\uff1a\u201c\u6211\u65e0\u6068\u751f\u81ea\u547d\u5929\u4e0b\u7edd\u624d\uff0c\u5c82\u6599\u5728\u8fd9\u91cc\u7adf\u78b0\u4e0a\u8fd9\u4e48\u4e00\u4e2a\u4eba\u7269\uff0c\u53ef\u60dc\u4ed6 \u4e0d\u7cbe\u6b66\u827a\uff0c\u5426\u5219\u53ea\u6015\u6211\u65e0\u6068\u751f\u65e0\u8bba\u6587\u624d\u6b66\u529f\u90fd\u4f1a\u8f93\u4ed6\u4e00\u7b79\u5462\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u5112\u751f\u662f\u4e1c\u6d77\u65e0\u6781\u5c9b\u5c9b\u4e3b\uff0c\u4e16\u5916\u4e09\u4ed9\u4e2d\u7684\u65e0\u6068\u751f\uff0c\u81f3\u4e8e\u4ed6\u79bb\u5c9b\u5165\u4e2d\u539f\u7684\u7f18\u6545\u8fd9 \u91cc\u6682\u4e14\u4e0d\u63d0\u3002 \u3000\u3000\u6885\u8001\u5148\u751f\u5ffd\u7136\u9053\uff1a\u201c\u5434\u5144\u4f55\u5fc5\u81ea\u8c26\u592a\u751a\uff0c\u65b9\u624d\u5434\u5144\u867d\u81ea\u4e91\u5b66\u5251\u4e0d\u6210\uff0c\u60f3\u6765\u6b66\u5b66\u4e0a\u9020 \u8be3\u5fc5\u6df1\uff0c\u8001\u592b\u867d\u5bf9\u6b64\u9053\u5916\u884c\uff0c\u5374\u751a\u94a6\u7fa1\u53e4\u4fa0\u58eb\u4ed7\u5251\u884c\u4e49\u4e4b\u98ce\u54e9\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u8c08\u5f97\u5174\u8d77\uff0c\u6717\u7b11\uff1a\u201c\u96d5\u866b\u5c0f\u6280\uff0c\u96be\u5165\u6cd5\u773c\uff0c\u4eca\u65e5\u5434\u67d0\u6b23\u5f97\u77e5\u97f3\uff0c\u4e14\u821e\u4e00\u5251\u4e3a \u5148\u751f\u5fa1\u9664\u5bd2\u6c14\u3002\u201d\u4ed6\u5fc3\u4e2d\u5374\u7b11\u6885\u8001\u5c45\u7136\u6ca1\u6709\u770b\u51fa\u81ea\u5df1\u4e00\u8eab\u7edd\u9876\u5185\u529f\u7684\u7279\u5f81\u3002 \u3000\u3000\u8bf4\u7740\u4e0a\u524d\u6298\u4e86\u4e00\u679d\u6885\u679d\uff0c\u9053\u58f0\uff1a\u201c\u732e\u4e11\uff01\u201d\u5c31\u821e\u4e86\u8d77\u6765\u3002 \u3000\u3000\u867d\u8bf4\u821e\u5251\uff0c\u4f46\u5230\u4e86\u65e0\u6068\u751f\u624b\u4e2d\u4f9d\u7136\u602a\u62db\u5947\u5f0f\u5c42\u51fa\uff0c\u7cbe\u5f69\u7edd\u4f26\u3002 \u3000\u3000\u5c24\u5176\u90a3\u6885\u679d\u7aef\u53d1\u51fa\u5636\u5636\u5251\u6c14\uff0c\u5728\u547c\u547c\u5317\u98ce\u4e2d\u523a\u8033\u5f02\u5e38\u3002 \u3000\u3000\u6885\u8001\u5148\u751f\u5374\u5fc3\u4e2d\u4e0d\u65ad\u6c81\u51fa\u51b7\u6c57\uff0c\u4ed6\u9677\u5165\u4e00\u4e2a\u6781\u5ea6\u7684\u7d27\u5f20\u4e2d\uff0c\u4ed6\u9ed8\u9ed8\u81ea\u601d\uff1a \u3000\u3000\u201c\u6211\u867d\u7136\u5168\u8eab\u529f\u529b\u5c3d\u5931\uff0c\u73b0\u5728\u6709\u5982\u5e38\u4eba\uff0c\u4f46\u5341\u5e74\u6765\u9ed8\u9ed8\u82e6\u601d\uff0c\u53cd\u800c\u60f3\u901a\u8bb8\u591a\u6b66\u5b66\u4e0a \u7684\u9053\u7406\uff0c\u662f\u4ee5\u76ee\u4e0b\u529f\u592b\u867d\u5931\uff0c\u6b66\u5b66\u5374\u662f\u6709\u8fdb\u65e0\u9000\uff0c\u4f46\u662f\u8fd9\u5112\u751f\u5251\u5c16\u7684\u5251\u6c14\u7adf\u7ec3\u5230\u7389\u5973\u5f52 \u771f\u7684\u5730\u6b65\uff0c\u5c31\u7b97\u6211\u529f\u529b\u4e0d\u5931\uff0c\u4e5f\u4e07\u4e07\u505a\u4e0d\u5230\uff0c\u8fd9\u4eba\u662f\u8c01\u5462\uff1f\u96be\u9053\u9664\u4e86\u4e16\u5916\u4e09\u4ed9\uff0c\u6d77\u5185\u8fd8 \u6709\u5f3a\u8fc7\u6211\u7684\uff1f\u201d\u6562\u60c5\u6885\u8001\u5148\u751f\u6599\u5b9a\u4e16\u5916\u4e09\u4ed9\u662f\u4e0d\u4f1a\u6d89\u8db3\u4e2d\u539f\u7684\uff0c\u800c\u4ed6\u90a3\u91cc\u6599\u5f97\u5230\uff0c\u773c\u524d \u8fd9\u4eba\u6b63\u662f\u4e16\u5916\u4e09\u4ed9\u4e4b\u4e00\u5462\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u7acb\u523b\u5c31\u88ab\u65e0\u6068\u751f\u7684\u5251\u5f0f\u5438\u5f15\u5f97\u65e0\u6687\u5206\u5fc3\u4e86\uff0c\u5929\u751f\u55dc\u6b66 \u3000\u3000\u7684\u6027\u5b50\u4f7f\u4ed6\u6c89\u5fc3\u5728\u601d\u7d22\u65e0\u6068\u751f\u5251\u5f0f\u7684\u5999\u5904\u53ca\u5229\u5f0a\u3002 \u3000\u3000\u65e0\u6068\u751f\u4e5f\u53d1\u73b0\u8fd9\u6885\u8001\u5148\u751f\u6bcf\u5f53\u4ed6\u65bd\u51fa\u4e00\u62db\u65f6\uff0c\u5148\u662f\u60ca\u8bb6\uff0c\u7136\u540e\u8138\u4e0a\u8138\u51fa\u91ca\u7136\u7684\u8868\u60c5\u3002 \u4e00\u8fde\u51e0\u62db\u90fd\u662f\u5982\u6b64\uff0c\u65e0\u6068\u751f\u4e0d\u7981\u52a8\u4e86\u7591\uff0c\u4ed6\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5ffd\u7136\u65bd\u51fa\u4e09\u62db\uff1a\u201c\u6653\u98ce\u6b8b\u6708\u201d\uff0c \u201c\u96fe\u5931\u697c\u53f0\u201d\u53ca\u201c\u6708\u8ff7\u6d25\u6e21\u201d\u3002 \u3000\u3000\u53ea\u662f\u5728\u7b2c\u4e8c\u5f0f\u201c\u96fe\u5931\u697c\u53f0\u201d\u65f6\uff0c\u6545\u610f\u5356\u4e86\u4e00\u70b9\u7834\u7efd\u3002 \u3000\u3000\u4e09\u62db\u65bd\u5b8c\uff0c\u4ed6\u505c\u5251\u51dd\u89c6\u6885\u8001\uff0c\u53ea\u89c1\u6885\u8001\u5148\u751f\u51dd\u76ee\u8fdc\u89c6\uff0c\u534a\u5929\u624d\u9053\uff1a\u201c\u5434\u5148\u751f\u65b9\u624d\u65bd \u7684\u4e09\u62db\u771f\u597d\u770b\uff0c\u53ef\u5426\u518d\u821e\u4e00\u904d\u8ba9\u8001\u673d\u4ed4\u7ec6\u6b23\u8d4f\u4e00\u4e0b\uff1f\u201d \u3000\u3000\u65e0\u6068\u751f\u5fc3\u4e2d\u6697\u60ca\uff0c\u53c8\u5c06\u65b9\u624d\u4e09\u62db\u821e\u4e86\u4e00\u6b21\uff0c\u540c\u6837\u662f\u201c\u96fe\u5931\u697c\u53f0\u201d\u4e00\u62db\u5356\u4e86\u7834\u7efd\u3002 \u3000\u3000\u6885\u8001\u5148\u751f\u5ffd\u7136\u8131\u53e3\u9053\uff1a\u201c\u4f60\u90a3\u7b2c\u4e8c\u62db\u662f\u5426\u6709\u70b9\u4e0d\u5bf9\u2014\u2014\u201d\u4ed6\u8bf4\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u60f3\u8d77\u81ea \u5df1\u4e43\u662f\u201c\u4e0d\u61c2\u6b66\u827a\u201d\uff0c\u662f\u4ee5\u8fde\u5fd9\u4f4f\u53e3\u3002 \u3000\u3000\u4f46\u65e0\u6068\u751f\u5df2\u662f\u559d\u7684\u4e00\u58f0\uff0c\u4e00\u628a\u6293\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u9053\uff1a\u201c\u80fd\u770b\u5f97\u51fa\u6211\u8fd9\u7834\u7efd\u7684\u4eba\uff0c\u53ef\u8bf4\u5929\u4e0b\u6ca1\u6709\u51e0\u4e2a\uff0c\u8fd9\u53ae\u7adf\u770b\u4e86\u51fa\u6765\uff0c\u554a\uff0c \u4ed6\u59d3\u6885\u2014\u2014\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u66f4\u4e0d\u591a\u60f3\uff0c\u4e00\u628a\u6293\u4e86\u4e0a\u6765\u3002 \u3000\u3000\u6885\u8001\u5148\u751f\u81ea\u7136\u5730\u811a\u4e0b\u4e00\u7eb5\uff0c\u4f46\u7acb\u523b\u53d1\u89c9\u53cc\u811a\u67d4\u800c\u65e0\u529b\uff0c\u6839\u672c\u7eb5\u8dc3\u4e0d\u8d77\u3002 \u3000\u3000\u65e0\u6068\u751f\u7684\u4e00\u6293\u95ea\u7535\u822c\u6263\u4e86\u4e0b\u6765\uff0c\u6885\u8001\u5148\u751f\u7684\u53f3\u624b\u4e00\u7ffb\uff0c\u4e94\u6307\u6781\u5de7\u5999\u5730\u642d\u4e0a\u4e86\u65e0\u6068\u751f \u7684\u8109\u95e8\uff0c\u4f46\u662f\u5374\u67d4\u5f31\u65e0\u529b\uff0c\u4ecd\u7136\u88ab\u65e0\u6068\u751f\u6293\u4e86\u547d\u8109\u3002 \u3000\u3000\u65e0\u6068\u751f\u5389\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u6885\u8001\u5148\u751f\u5bf9\u4e8e\u8fd9\u4e00\u70b9\u4e5f\u4e0d\u80fd\u91ca\u6000\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u662f\u8c01\uff1f\u201d \u3000\u3000\u8fd9\u4e00\u4e0b\uff0c\u6885\u8001\u5148\u751f\u53cd\u5012\u91ca\u7136\u4e86\uff0c\u4e16\u5916\u4e09\u4ed9\u6709\u6b64\u529f\u529b\uff0c\u662f\u5f53\u7136\u7684\u4e8b\u60c5\u3002 \u3000\u3000\u4ed6\u7684\u773c\u5149\u89e6\u53ca\u65e0\u6068\u751f\u90a3\u7cbe\u5149\u66b4\u5c04\u7684\u9635\u5b50\uff0c\u5ffd\u7136\u611f\u5230\u4e00\u9635\u96c4\u5fc3\u594b\u53d1\uff0c\u4ed6\u7684\u767d\u53d1\u4e00\u9635\u5f02 \u6837\u7684\u6296\u52a8\uff0c\u5927\u58f0\u559d\u9053\uff1a\u201c\u6885\u5c71\u6c11\uff01\u4f60\u542c\u8fc7\u5417\uff1f\u201d \u3000\u3000\u90a3\u795e\u6001\u54ea\u91cc\u8fd8\u662f\u4e2a\u8001\u6001\u9f99\u949f\u7684\u6a21\u6837\uff0c\u8fde\u65e0\u6068\u751f\u90fd\u611f\u5230\u4e00\u9635\u4e0d\u6562\u6b63\u89c6\u3002 \u3000\u3000\u65e0\u6068\u751f\u72e0\u58f0\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u6885\u5c71\u6c11\uff0c\u4eca\u65e5\u53eb\u4f60\u2014\u2014\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u611f\u89c9\u51fa\u6885\u5c71\u6c11\u624b\u8109\u4e0a\u7684\u9aa8\u8089\u677e\u6563\u800c\u65e0\u5f39\u6027\uff0c\u5b8c\u5168\u662f\u5931\u4e86\u529f\u529b\u7684\u6837\u5b50\uff0c\u4ed6\u7684\u72e0 \u8bdd\u7a81\u7136\u505c\u4e86\u53e3\uff0c\u4ed6\u8f7b\u8f7b\u653e\u5f00\u4e86\u7d27\u6263\u7684\u624b\u3002 \u3000\u3000\u4ed6\u5341\u5206\u660e\u767d\u6885\u5c71\u6c11\u6b64\u523b\u7684\u75db\u82e6\uff0c\u4e00\u4e2a\u8d85\u4eba\u53d8\u6210\u4e86\u4e00\u4e2a\u51e1\u4eba\uff0c\u8fd9\u79cd\u75db\u82e6\u4ed6\u80fd\u591f\u60f3\u5f97\u5230\uff0c \u56e0\u4e3a\u4ed6\u4e5f\u662f\u4e00\u4e2a\u8d85\u4eba\u3002 \u3000\u3000\u6885\u5c71\u6c11\u8f7b\u8f7b\u6447\u4e86\u6447\u767d\u5934\uff0c\u50cf\u662f\u4e0d\u63a5\u53d7\u65e0\u6068\u751f\u7684\u540c\u60c5\u4f3c\u7684\uff0c\u6b64\u523b\u4ed6\u5bf9\u4e16\u4e0a\u4efb\u4f55\u540c\u60c5\u90fd \u770b\u6210\u4e00\u79cd\u601c\u60dc\uff0c\u4e03\u5999\u795e\u541b\u7adf\u53d7\u4eba\u601c\u60dc\uff0c\u201c\u54fc\uff01\u201d\u4ed6\u53c8\u91cd\u91cd\u6447\u4e86\u5934\u3002 \u3000\u3000\u4ed6\u5014\u5f3a\u5730\u9053\uff1a\u201c\u4e16\u5916\u4e09\u4ed9\u4e0d\u8fc7\u5c14\u5c14\uff0c\u4f60\u90a3\u4e09\u62db\u5251\u6cd5\u4e2d\u4f9d\u7136\u6709\u6bdb\u75c5\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u90a3\u62db\u201c\u96fe\u5931\u697c\u53f0\u201d\u867d\u662f\u6545\u610f\u5356\u7684\u7834\u7efd\uff0c\u4f46\u5982\u975e\u7edd\u9876\u9ad8\u624b\u7edd\u770b\u4e0d\u51fa\u6765\uff0c\u662f\u4ee5\u4ed6 \u4fe9\u6545\u610f\u9053\uff1a\u201c\u4f60\u4e14\u8bf4\u6765\u770b\u770b\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u9053\uff1a\u201c\u6211\u53ea\u8981\u5de6\u9762\u7ed9\u4f60\u4e00\u8bb0\u2018\u97e6\u62a4\u62a1\u6746\u2019\uff0c\u53f3\u9762\u7ed9\u4f60\u4e00\u8bb0\u2018\u4e39\u9633\u6e21\u82c7\u2019\uff0c\u5c31 \u80fd\u903c\u4f60\u9732\u51fa\u5de6\u9762\u7834\u7efd\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u6697\u601d\u8fd9\u4e24\u62db\u7528\u5f97\u679c\u7136\u5341\u5206\u795e\u5999\uff0c\u5f53\u4e0b\u5c31\u624b\u821e\u8db3\u8e48\u9053\uff1a\u201c\u6211\u5de6\u9762\u867d\u9732\u7834\u7efd\uff0c\u53ef \u662f\u811a\u4e0b\u4e43\u662f\u2018\u76d8\u5f13\u5c04\u96d5\u2019\u7684\u52bf\u5b50\uff0c\u53ea\u8981\u4f60\u4e00\u53d1\u62db\uff0c\u6211\u53cc\u811a\u9a6c\u4e0a\u8e22\u4f60\u4e39\u7530\u8981\u7a74\uff0c\u5de6\u638c\u2018\u6a2a \u5288\u534e\u5c71\u2019\uff0c\u53f3\u6307\u53d6\u4f60\u53cc\u76ee\uff0c\u4f60\u8eb2\u5f97\u4e86\u4e0b\u5c31\u8eb2\u4e0d\u4e86\u4e0a\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u60f3\u4e86\u4e00\u4f1a\uff0c\u5fae\u5fae\u4e00\u54c2\u9053\uff1a\u201c\u82e5\u662f\u6211\u7528\u6211\u90a3\u2018\u866c\u679d\u5251\u5f0f\u2019\u4e2d\u7684\u2018\u5bd2\u6885\u5410\u854a\u2019\uff0c \u7acb\u523b\u5c31\u53eb\u4f60\u4e0d\u6687\u81ea\u4fdd\uff0c\u53ea\u662f\u2018\u866c\u679d\u5251\u5f0f\u2019\u4e43\u662f\u6211\u81ea\u5df1\u6240\u521b\uff0c\u8bf4\u4e0e\u4f60\u542c\u4f60\u4e5f\u660e\u767d\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u4e00\u542c\u6b64\u4eba\u662f\u6885\u5c71\u6c11\uff0c\u7acb\u523b\u77e5\u9053\u81ea\u5df1\u5bf9\u90a3\u846c\u8eab\u6ce2\u6f9c\u7684\u9752\u5e74\u2014\u2014\u4e5f\u5c31\u662f\u8f9b\u2014\u2014\u662f \u8bef\u4f1a\u4e86\uff0c\u5fc3\u60f3\u5bb3\u90a3\u4eba\u846c\u8eab\u5927\u6d77\uff0c\u4e0d\u7981\u6709\u70b9\u5185\u759a\uff0c\u800c\u5bf9\u773c\u524d\u8fd9\u6885\u5c71\u6c11\u771f\u6068\u4e0d\u5f97\u7acb\u6bd9\u638c\u4e0b\u3002 \u53ca\u89c1\u6885\u5c71\u6c11\u529f\u529b\u5168\u5931\uff0c\u8001\u6001\u9f99\u949f\uff0c\u6839\u672c\u4e0d\u50cf\u4e00\u4e2a\u73a9\u5f04\u5973\u4eba\u7684\u6deb\u8d3c\uff0c\u4e0d\u7981\u5bf9\u7f2a\u4e5d\u5a18\u7684\u6b7b\u6000 \u7591\u8d77\u6765\u3002 \u3000\u3000\u800c\u4e14\u6885\u5c71\u6c11\u7684\u7edd\u4e16\u5947\u624d\u4f7f\u4ed6\u5fc3\u4e2d\u8d77\u4e86\u4e00\u70b9\u60fa\u60fa\u76f8\u60dc\u4e4b\u5904\u3002 \u3000\u3000\u987b\u77e5\u65e0\u6068\u751f\u6587\u6b66\u5b66\u672f\uff0c\u65e0\u4e00\u4e0d\u901a\uff0c\u5e73\u751f\u4ee5\u624d\u81ea\u8d1f\uff0c\u90a3\u5e73\u51e1\u4e0a\u4eba\u4f55\u7b49\u6b66\u529f\uff0c\u65e0\u6068\u751f\u5374 \u4e0d\u653e\u5728\u773c\u5185\uff0c\u6697\u9053\uff1a\u201c\u4efb\u4ed6\u529f\u529b\u76d6\u4e16\uff0c\u4e0d\u8fc7\u4e00\u4ecb\u6b66\u592b\u8033\u3002\u201d\u53ef\u89c1\u5176\u81ea\u8d1f\u4e4b\u9ad8\u3002 \u3000\u3000\u4f46\u4ed6\u5374\u6ca1\u6709\u6599\u5230\u6d77\u5185\u7684\u4e03\u5999\u795e\u541b\u4e5f\u662f\u4e00\u4e2a\u76d6\u4e16\u5947\u624d\uff0c\u4e03\u827a\u51a0\u7edd\u6d77\u5185\uff0c\u9664\u4e86\u529f\u529b\u56e0\u65e0 \u6068\u751f\u4ed9\u679c\u5947\u7f18\u4e0d\u80fd\u53ca\u5916\uff0c\u5176\u4ed6\u751a\u81f3\u6bd4\u4ed6\u66f4\u6709\u8fc7\u4e4b\u3002 \u3000\u3000\u5f53\u5e74\u4e03\u5999\u795e\u541b\u540d\u566a\u4e00\u65f6\uff0c\u65e0\u6068\u751f\u5bf9\u4ed6\u4e5f\u6709\u8033\u95fb\uff0c\u4f46\u4ed6\u600e\u4e48\u4e5f\u4e0d\u4fe1\u5929\u4e0b\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u8fd9 \u7b49\u5947\u624d\uff0c\u4ed6\u66fe\u7b11\u5bf9\u7f2a\u4e03\u5a18\u9053\uff1a\u201c\u6b3a\u4e16\u76d7\u540d\u4e4b\u5f92\u8033\u3002\u201d\u73b0\u5728\u4ed6\u89c1\u4e86\u6885\u5c71\u6c11\u7684\u5947\u624d\uff0c\u4e0d\u7981\u5fc3 \u751f\u77e5\u97f3\u4e4b\u611f\u3002 \u3000\u3000\u5f53\u4e0b\u54c8\u54c8\u6717\u7b11\u9053\uff1a\u201c\u8bba\u5185\u529f\uff0c\u4f60\u529f\u529b\u5373\u4f7f\u4e0d\u5931\uff0c\u6015\u4e5f\u975e\u6211\u5bf9\u624b\uff0c\u4f46\u8bba\u5251\u672f\uff0c\u5219\u5404\u4eba \u806a\u660e\u624d\u667a\u4e0d\u540c\uff0c\u4e0e\u529f\u529b\u5173\u7cfb\u8f83\u5c11\uff0c\u54b1\u4eec\u4ee5\u624b\u4ee3\u5251\uff0c\u4ee5\u53e3\u4ee3\u624b\uff0c\u6765\u4e2a\u53e3\u4e0a\u8c08\u5175\u5982\u4f55\uff1f\u554a\uff0c \u770b\u4f60\u624b\u4e0a\u662f\u58f6\u9648\u5e74\u8001\u9152\uff0c\u54b1\u4eec\u5c31\u6765\u716e\u9152\u8bba\u5251\u5427\uff0c\u4e5f\u7b97\u5f97\u4e00\u6869\u96c5\u4e8b\uff0c\u54c8\u54c8\u3002\u201d \u3000\u3000\u8bf4\u7740\u6298\u4e0b\u51e0\u679d\u6885\u679d\uff0c\u7528\u624b\u634f\u6210\u4e00\u628a\uff0c\u5728\u53cc\u638c\u4e2d\u4e00\u6413\uff0c\u53ea\u89c1\u4ed6\u6697\u7528\u771f\u529b\uff0c\u731b\u7136\u4e00\u6413\uff0c \u6885\u679d\u7a81\u7136\u5192\u51fa\u4e86\u9635\u9635\u767d\u70df\uff0c\u8f70\u7136\u7740\u706b\uff0c\u987f\u65f6\u5c06\u5730\u4e0a\u7684\u96ea\u5316\u4e86\u4e00\u5927\u6ee9\u3002 \u3000\u3000\u6885\u5c71\u6c11\u770b\u4ed6\u7528\u672c\u8eab\u4e09\u6627\u771f\u706b\u6413\u71c3\u5e26\u96ea\u6885\u679d\uff0c\u529f\u529b\u81f3\u5c11\u5df2\u5728\u767e\u5e74\u4e4b\u4e0a\uff0c\u4f46\u9762\u5bb9\u5374\u662f\u7ffb \u7ffb\u4e2d\u5e74\uff0c\u4e45\u95fb\u65e0\u6781\u5c9b\u4e3b\u9a7b\u989c\u4e0d\u8001\uff0c\u770b\u6765\u679c\u5982\u5176\u8a00\u3002 \u3000\u3000\u706b\u820c\u5377\u4e86\u8d77\u6765\uff0c\u65e0\u6068\u751f\u5c06\u51e0\u679d\u706b\u679d\u67b6\u597d\uff0c\u6210\u4e86\u4e00\u4e2a\u706b\u5806\uff0c\u6885\u5c71\u6c11\u5c06\u9152\u58f6\u5f80\u706b\u4e0a\u9762\u4e00 \u653e\uff0c\u7247\u523b\u9635\u9635\u9152\u9999\u4ece\u58f6\u4e2d\u98d8\u51fa\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5355\u624b\u5fae\u626c\uff0c\u4e00\u5708\u4e4b\u95f4\uff0c\u53cc\u6307\u5e76\u7acb\u5982\u5251\u5df2\u81ea\u51fa\u51fb\uff0c\u6b63\u662f\u201c\u866c\u679d\u5251\u5f0f\u201d\u4e2d\u7684\u201c\u5bd2 \u6885\u5410\u854a\u3002\u201d \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c \u516b \u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c \u516b \u7ae0 \u3000\u3000\u201c\u5bd2\u6885\u5410\u854a\u201d\u672c\u6765\u662f\u5168\u653b\u7684\u52bf\u5b50\uff0c\u501f\u7740\u4e00\u5708\u518d\u5410\u7684\u624b\u6cd5\uff0c\u521a\u597d\u53ef\u4ee5\u5c01\u4f4f\u5bf9\u624b\u7684\u653b\u52bf\uff0c \u7136\u540e\u518d\u70b9\u51fa\u4e00\u6307\uff0c\u597d\u53eb\u654c\u4eba\u9632\u4e0d\u80dc\u9632\u3002 \u3000\u3000\u65e0\u6068\u751f\u77a5\u89c1\u4e4b\u95f4\uff0c\u5df2\u77e5\u5999\u5904\uff0c\u6697\u601d\u4efb\u4f55\u653b\u52bf\uff0c\u5747\u4f1a\u88ab\u4e00\u5708\u4e00\u5410\u4e4b\u5f0f\u5c01\u4e0b\uff0c\u5fc3\u4e2d\u4e0d\u7531 \u6697\u6697\u656c\u4f69\u6885\u5c71\u6c11\u7684\u624d\u5e72\uff0c\u5fae\u5fae\u4e00\u987f\uff0c\u84e6\u5730\u4f38\u624b\u5728\u5730\u4e0a\u865a\u865a\u4e00\u5212\uff0c\u767b\u65f6\u73b0\u51fa\u4e8c\u6761\u66f2\u7ebf\u6765\uff0c \u4e03\u5999\u795e\u541b\u4f55\u7b49\u529f\u529b\u5df2\u77e5\u8fd9\u4e8c\u6761\u66f2\u7ebf\u7684\u610f\u601d\u4e43\u662f\u8868\u660e\u4ed6\u5728\u201c\u5bd2\u6885\u5410\u854a\u201d\u5c1a\u672a\u653b\u5230\u4e4b\u65f6\uff0c\u4fbf \u6536\u56de\u201c\u6a2a\u5288\u534e\u5c71\u201d\u7684\u5f0f\u5b50\uff0c\u811a\u4e0b\u5e76\u4e14\u6539\u201c\u76d8\u5f13\u5c04\u96d5\u201d\u800c\u5012\u8e29\u4e03\u661f\uff0c\u521a\u597d\u53ef\u4ee5\u907f\u8fc7\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u5fae\u5fae\u4e00\u987f\uff0c\u84e6\u5730\u91cc\u65e0\u6068\u751f\u53c8\u662f\u4e00\u5212\uff0c\u540c\u65f6\u6b64\u7528\u4e00\u4e2a\u8fde\u70b9\u5e26\u62b9\u7684\u5f0f\u5b50\u3002\u6885\u5c71 \u6c11\u4e00\u77a5\u4e4b\u4e0b\uff0c\u4e0d\u5047\u601d\u7d22\uff0c\u50b2\u7136\u9053\uff1a\u201c\u4f60\u8fd9\u62db\u534e\u5c71\u795e\u62f3\u4e2d\u7684\u2018\u81ea\u89e3\u91d1\u94c3\u2019\u56fa\u7136\u8bba\u653b\u8bba\u5b88\u90fd \u6709\u82e5\u91d1\u94dc\u94c1\u58c1\uff0c\u4f46\u9047\u4e0a\u6211\u7684\u2018\u866c\u679d\u5251\u5f0f\u2019\u53ef\u5c31\u4e0d\u884c\u4e86\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u5de6\u624b\u4e00\u5f39\uff0c\u8fc5\u901f\u5730\u4e00\u63a2\u81c2\uff0c\u65b9\u5411\u5374\u662f\u659c\u63a0\u800c\u4e0a\uff0c\u6d3b\u50cf\u4e00\u679d\u6885\u51e0\u4e4d\u7136\u6a2a\u51fa\uff0c \u6b63\u662f\u7edd\u5b66\u201c\u51b7\u6885\u62c2\u9762\u201d\uff01 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u5148\u8fd8\u60f3\u4e0d\u7528\u72ec\u95e8\u7edd\u827a\u548c\u6d77\u5185\u5947\u4eba\u76f8\u640f\uff0c\u4f46\u4ea4\u6362\u4e00\u62db\u591a\uff0c\u4fbf\u77e5\u4e03\u5999\u795e \u541b\u679c\u662f\u6d77\u5185\u5947\u6750\uff0c\u4e2d\u539f\u4e00\u5207\u62db\u5f0f\uff0c\u90fd\u4f3c\u6b63\u88ab\u4ed6\u72ec\u521b\u7684\u201c\u866c\u679d\u5251\u5f0f\u201d\u76f8\u5236\u3002 \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e94\u6307\u5fae\u5f20\uff0c\u53f3\u624b\u5374\u5f53\u80f8\u4fa7\u638c\u800c\u7acb\u3002\u8fd9\u4e00\u62db\u5f0f\u5e76\u6ca1\u6709\u540d\u79f0\u513f\uff0c\u4f46\u5374\u662f\u65e0\u6068 \u751f\u7684\u7edd\u5b66\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u770b\u4ed6\u4ec5\u7528\u5355\u638c\u62a4\u80f8\uff0c\u4e00\u722a\u786c\u649e\uff0c\u81ea\u5df1\u662f\u5148\u884c\u53d1\u96be\uff0c\u7167\u7406\u8bf4\u65e0\u6781\u5c9b\u4e3b\u51b3\u4e0d\u53ef \u80fd\u4ec5\u7528\u4e00\u638c\u4fbf\u80fd\u62db\u67b6\u5f97\u4f4f\uff0c\u4f46\u6885\u5c71\u6c11\u77e5\u9053\u4ed6\u7684\u529f\u529b\u5df2\u8fbe\u201c\u7389\u7384\u5f52\u771f\u201d\u7684\u5730\u6b65\uff0c\u529f\u529b\u6bd4\u81ea \u5df1\u8981\u9ad8\uff0c\u8fd9\u4e00\u62db\u4f7f\u5f97\u751a\u662f\u5408\u7406\u3002 \u3000\u3000\u5f53\u4e0b\u5fae\u5fae\u4e00\u54c2\uff0c\u53f3\u81c2\u5fae\u62a1\uff0c\u53cc\u638c\u4e3a\u62f3\uff0c\u4e00\u5708\u4e4b\u95f4\uff0c\u5728\u98d8\u98d8\u638c\u5f71\u4e2d\uff0c\u5c0f\u6307\u95ea\u7535\u4f38\u51fa\uff0c \u4e00\u94a9\u4e00\u5212\u3002 \u3000\u3000\u540c\u65f6\u5de6\u624b\u81ea\u5de6\u81f3\u53f3\uff0c\u5fae\u5212\u534a\u5f27\uff0c\u4ee5\u8865\u4e3a\u5b88\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u529f\u529b\u4e0d\u6b86\u4e43\u662f\u6700\u5927\u5f31\u70b9\uff0c\u662f\u4ee5\u6bcf\u653b\u4e00\u62db\uff0c\u5fc5\u7559\u4e00\u624b\u5728\u540e\u9632\u5b88\uff0c \u5426\u5219\u65e0\u6068\u751f\u53ea\u8981\u786c\u51fa\u4e00\u5f0f\uff0c\u81ea\u5df1\u4e00\u5b9a\u975e\u843d\u8d25\u4e0d\u53ef\u2014\u2014 \u3000\u3000\u867d\u5219\u662f\u53e3\u5934\u8bba\u62db\uff0c\u4f46\u4e03\u5999\u795e\u541b\u4f55\u7b49\u4eba\u7269\uff0c\u4e00\u4e1d\u4e00\u6beb\u4e5f\u4e0d\u7559\u4eba\u8bf4\u5634\uff0c\u8fd9\u4e00\u70b9\u5c31\u662f\u65e0\u6781 \u5c9b\u4e3b\u65e0\u6068\u751f\u4e5f\u751a\u662f\u5d07\u656c\u7684\u2014\u2014\u65e0\u6068\u751f\u89c1\u654c\u62db\u53c8\u81f3\uff0c\u4e14\u5728\u51cc\u5389\u653b\u52bf\u4e2d\uff0c\u5939\u7740\u201c\u91d1\u521a\u6307\u201d\u529f\uff0c \u8fd9\u4e00\u4e0b\u662f\u9690\u853d\u975e\u5e38\uff0c\u53ef\u8bf4\u6bd2\u8fa3\u4e4b\u6781\u3002 \u3000\u3000\u201c\u54fc\uff01\u8fd9\u8001\u5bb6\u4f19\u679c\u7136\u662f\u540d\u4e0d\u865a\u4f20\uff0c\u6bd2\u8fa3\u5f97\u5f88\uff01\u201d \u3000\u3000\u65e0\u6068\u751f\u5fc3\u4e2d\u6697\u5fd6\uff0c\u53c8\u840c\u8d77\u4e00\u4e1d\u538c\u6076\u548c\u4ec7\u89c6\u4e03\u5999\u795e\u541b\u7684\u610f\u601d\u3002 \u3000\u3000\u624b\u4e2d\u84e6\u5730\u4e00\u6446\uff0c\u521a\u60f3\u65bd\u51fa\u201c\u53cc\u649e\u638c\u201d\u52a0\u4e0a\u201c\u62cd\u809a\u817f\u201d\u7684\u62db\u5f0f\uff0c\u5fc3\u4e2d\u5374\u662f\u4e00\u52a8\u3002 \u3000\u3000\u5047\u5982\u8fd9\u4e00\u5f0f\u4f7f\u51fa\uff0c\u60c5\u5f62\u662f\u65e0\u6068\u751f\u7684\u201c\u53cc\u649e\u638c\u201d\u5c06\u631f\u60ca\u5929\u52a8\u5730\u7684\u638c\u98ce\u903c\u4f7f\u6885\u5c71\u6c11\u6536\u62db\uff0c \u4f46\u5374\u4e0d\u514d\u8981\u4f7f\u6885\u5c71\u6c11\u548c\u81ea\u5df1\u7684\u201c\u62cd\u809a\u817f\u201d\u63a5\u89e6\u3002 \u3000\u3000\u201c\u62cd\u809a\u817f\u201d\u4e43\u662f\u592a\u6781\u95e8\u4e2d\u62db\u5f0f\uff0c\u7528\u4e0a\u201c\u9ecf\u201d\u5b57\u51b3\uff0c\u5a01\u80c1\u5fc5\u5927\uff0c\u662f\u4ee5\u4e03\u5999\u795e\u541b\u514d\u4e0d\u4e86 \u4e00\u5b9a\u548c\u4ed6\u76f8\u63a5\u800c\u53d8\u6210\u62fc\u8bd5\u5185\u529b\u7684\u6837\u5b50\uff0c\u867d\u5219\u6885\u5c71\u6c11\u7684\u529f\u592b\u4e0d\u662f\u4e0d\u53ef\u80fd\u6446\u8131\u8fd9\u4e2a\u50f5\u5c40\uff0c\u4f46 \u4e5f\u5fc5\u5b9a\u4f1a\u5f88\u72fc\u72c8\u7684\uff0c\u4f46\u4e03\u5999\u795e\u541b\u8fd9\u7b49\u4eba\u7269\uff0c\u5fc5\u4e0d\u4f1a\u907f\u5f00\uff0c\u8fd9\u610f\u601d\u4fbf\u662f\u8bf4\u6b64\u62db\u4e00\u51fa\uff0c\u4e03\u5999 \u795e\u541b\u5373\u4f1a\u843d\u8d25\uff01 \u3000\u3000\u65e0\u6068\u751f\u98de\u5feb\u7684\u8f6c\u5ff5\u5934\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u6211\u65e0\u6068\u751f\u5c82\u53ef\u4e00\u518d\u501a\u4ed7\u8f83\u9ad8\u7684\u529f\u529b\u53d6\u80dc\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u65e2\u5b9a\uff0c\u500f\u5730\u53d8\u62db\uff0c\u786c\u62c6\u4e00\u62db\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u4e0d\u5047\u601d\u7d22\uff0c\u98de\u5feb\u7684\u6bd4\u51fa\u4e8c\u5f0f\uff0c\u5374\u662f\u866c\u679d\u5251\u62db\u4e2d\u7684\u8fde\u73af\u6740\u624b\uff0c\u53ea\u89c1\u4ed6\u4e8c\u638c\u9f50 \u4e3e\uff0c\u4e00\u5408\u4e4b\u4e0b\uff0c\u53cc\u638c\u5411\u4e0a\u4e00\u7ffb\u4e00\u538b\uff0c\u6b63\u662f\u201c\u4e4d\u60ca\u6885\u591a\u201d\u7684\u62db\u5f0f\u3002 \u3000\u3000\u65e0\u6068\u751f\u5012\u771f\u6599\u4e0d\u5230\u6885\u5c71\u6c11\u5251\u6cd5\u5982\u6b64\u7cbe\u5999\uff0c\u8fd9\u4e00\u5f0f\u201c\u4e4d\u60ca\u6885\u591a\u201d\u8868\u9762\u770b\u53bb\u5219\u662f\u4e00\u7ffb\u4e00 \u538b\u7684\u5f0f\u5b50\uff0c\u4f46\u5374\u904d\u88ad\u654c\u624b\u201c\u5929\u7075\u201d\u3001\u201c\u7d2b\u5bab\u201d\u7b49\u5927\u7a74\u3002 \u3000\u3000\u9976\u662f\u65e0\u6068\u751f\u624d\u667a\u76d6\u4e16\uff0c\u4e03\u5999\u795e\u541b\u6b64\u62db\u4e00\u51fa\uff0c\u4e5f\u4e0d\u7531\u8138\u8272\u5927\u53d8\uff0c\u4e00\u65f6\u6014\u5728\u4e00\u65c1\uff0c\u8138\u4e0a \u51fa\u73b0\u6c89\u601d\u7684\u6837\u5b50\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u5fc3\u4e2d\u6709\u6570\uff0c\u6b64\u62db\u4e43\u662f\u81ea\u5df1\u521b\u62db\u65f6\u4e00\u518d\u601d\u8003\u8fc7\uff0c\u786e\u5b9e\u53ef\u5c3d\u5236\u5929\u4e0b\u5404\u6d3e\u7edd\u62db\uff0c \u5fc3\u4e2d\u4e5f\u628a\u4e0d\u5b9a\u8fd9\u6570\u5341\u5e74\u58f0\u540d\u5c79\u7acb\u4e0d\u5012\u7684\u4e16\u5916\u4e09\u4ed9\u6709\u5426\u7edd\u5b66\u53ef\u4ee5\u514b\u5236\u81ea\u5df1\u7684\u62db\u5f0f\uff0c\u662f\u4ee5\u8138 \u4e0a\u767b\u65f6\u4e5f\u9732\u51fa\u7d27\u5f20\u7684\u795e\u8272\u3002 \u3000\u3000\u65e0\u6068\u751f\u6c89\u601d\u7247\u523b\uff0c\u84e6\u7136\u4e00\u4f38\u624b\uff0c\u5de6\u624b\u4e00\u6325\uff0c\u53f3\u624b\u4e00\u5708\u4e4b\u95f4\u7f13\u7f13\u9012\u51fa\u3002 \u3000\u3000\u8fd9\u662f\u4e00\u624b\u65e0\u540d\u65e0\u53f7\u7684\u62db\u5f0f\uff0c\u4f46\u5982\u7528\u6765\u5bf9\u6321\u201c\u4e4d\u60ca\u6885\u591a\u201d\u8fd9\u62db\uff0c\u5374\u662f\u9002\u5f53\u4e0d\u8fc7\uff0c\u800c\u4e14 \u5b88\u4e2d\u8fd8\u5939\u6709\u53cd\u653b\u7684\u5f0f\u5b50\u2014\u2014 \u3000\u3000\u5982\u679c\u8f9b\u6377\u5728\u8fd9\u91cc\u7684\u8bdd\uff0c\u4ed6\u5fc5\u5b9a\u4f1a\u611f\u53f9\u51fa\u58f0\u7684\u2014\u2014 \u3000\u3000\u56e0\u4e3a\u5f53\u4ed6\u5728\u5c0f\u6222\u5c9b\u4e0a\u548c\u4e16\u5916\u4e09\u4ed9\u4e4b\u9996\u5e73\u51e1\u4e0a\u4eba\u8fc7\u62db\u65f6\uff0c\u4e5f\u66fe\u4f7f\u51fa\u8fd9\u201c\u4e4d\u60ca\u6885\u9762\u7edd\u62db \u591a\u201d\uff0c\u5e73\u51e1\u4e0a\u4eba\u4e5f\u66fe\u5728\u82e6\u601d\u4e4b\u540e\uff0c\u81ea\u521b\u4e00\u62db\u6765\u7834\u89e3\uff0c\u5374\u6b63\u662f\u548c\u8fd9\u65f6\u65e0\u6068\u751f\u601d\u51fa\u7684\u62db\u5f0f\u4e00 \u822c\u65e0\u4e8c\u54e9\uff01 \u3000\u3000\u6885\u5c71\u6c11\u89c1\u65e0\u6068\u751f\u7adf\u5728\u81ea\u5df1\u5e73\u751f\u5f97\u610f\u7684\u7edd\u5b66\u4e0b\uff0c\u601d\u51fa\u7834\u89e3\u7684\u62db\u5f0f\uff0c\u4e0d\u7531\u6014\u4e86\u4e00\u6014\u3002 \u3000\u3000\u65e0\u6068\u751f\u7a0d\u7a0d\u4e00\u505c\uff0c\u500f\u5730\u6bd4\u51fa\u4e00\u4e2a\u62db\u5f0f\uff0c\u6562\u60c5\u6b63\u662f\u65e0\u6781\u5c9b\u7684\u7edd\u5b66\u2014\u2014\u201c\u7834\u7389\u62f3\u6cd5\u201d\uff0c \u4e0d\u8fc7\u5374\u662f\u4ee5\u5251\u62db\u9012\u51fa\u3002 \u3000\u3000\u65e0\u6068\u751f\u81ea\u548c\u6885\u5c71\u6c11\u4ee5\u53e3\u4ee3\u6218\uff0c\u4f5c\u80dc\u8d1f\u4e4b\u4e89\uff0c\u59cb\u7ec8\u5904\u4e8e\u5b88\u7684\u5730\u4f4d\uff0c\u8fd9\u65f6\u4e00\u653b\u4e4b\u4e0b\uff0c\u5374 \u662f\u5a01\u529b\u5927\u5f97\u51fa\u5947\uff0c\u6b63\u6240\u8c13\u201c\u4e0d\u9e23\u5219\u5df2\uff0c\u4e00\u9e23\u60ca\u4eba\u201d\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u6b63\u5728\u5bf9\u65e0\u6068\u751f\u7684\u602a\u62db\u4e0d\u80fd\u91ca\u4e8e\u6000\uff0c\u8fd9\u65f6\u89c1\u5bf9\u65b9\u4e0d\u5b88\u53cd\u653b\uff0c\u5fc3\u4e2d\u96c4\u5fc3 \u594b\u53d1\uff0c\u51b7\u7136\u4e00\u54fc\uff0c\u5fc3\u4e2d\u98de\u5feb\u4e00\u8f6c\uff0c\u5374\u5728\u6240 \u3000\u3000\u5b66\u4e2d\u59cb\u7ec8\u627e\u4e0d\u51fa\u4e00\u62db\u53ef\u4ee5\u5c01\u4f4f\u5bf9\u65b9\u8fd9\u4e00\u62db\uff01 \u3000\u3000\u4e03\u5999\u795e\u541b\u4f55\u7b49\u4eba\u7269\uff0c\u95ed\u773c\u5fae\u601d\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u76ee\u5149\u5982\u70ac\uff0c\u95ea\u76ee\u9053\uff1a\u201c\u65e0\u6068\u751f\uff0c\u4f60\u8fd9\u62db \u8bba\u653b\u53ef\u8bf4\u9510\u5229\u65e0\u6bd4\uff0c\u4f46\u8bba\u5b88\u5374\u5f97\u53e6\u62e9\u62db\u5f0f\u76f8\u52a9\uff01\u201d \u3000\u3000\u65e0\u6068\u751f\u6717\u58f0\u9053\uff1a\u201c\u8bf7\u6559\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u5fae\u5fae\u4e00\u7b11\uff0c\u5ffd\u9053\uff1a\u201c\u9152\u6e29\u597d\u5566\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u6307\u90a3\u6b63\u67b6\u5728\u6885\u679d\u4e0a\u7684\u4e00\u58f6\u201c\u6885\u5b50\u9999\u201d\uff0c\u987a\u624b\u53d6\u4e0b\u60ac\u5728\u5634\u4e0a\u7684\u4e00\u53ea\u74f7\u571f\u8d28\u7684\u676f\u513f \u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u662f\u5148\u5c1d\u5c1d\u8fd9\u7f8e\u9152\u5427\uff01\u201d \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u5fae\u5fae\u9894\u9996\uff0c\u77a5\u89c1\u81ea\u5df1\u5e76\u65e0\u76db\u9152\u4e4b\u5668\uff0c\u968f\u5373\u4f38\u624b\u4e00\u635e\uff0c\u635e\u8d77\u4e00\u624b\u7684\u96ea \u82b1\uff0c\u968f\u624b\u4f5c\u6a21\uff0c\u5fae\u4e00\u5851\u634f\uff0c\u4f7f\u90a3\u79ef\u96ea\u5fae\u6210\u676f\u5f62\uff0c\u9053\uff1a\u201c\u8352\u5c71\u91ce\u5730\uff0c\u6ca1\u6709\u5668\u5177\uff0c\u6743\u4e14\u4ee5\u6b64 \u4ee3\u676f\uff0c\u5411\u8001\u5148\u751f\u7d22\u8ba8\u7f8e\u9152\u4e00\u676f\uff01\u201d \u3000\u3000\u6885\u5c71\u6c11\u660e\u77e5\u4ed6\u663e\u793a\u5185\u529b\u9020\u8be3\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff01\u597d\u8bf4\uff01\u201d \u3000\u3000\u968f\u7740\u5fae\u503e\u9152\u58f6\uff0c\u503e\u51fa\u4e00\u80a1\u6885\u5b57\u9999\u7f8e\u9152\u3002 \u3000\u3000\u987b\u77e5\u6b64\u9152\u4e43\u662f\u521a\u624d\u70e7\u70eb\u7684\uff0c\u7167\u7406\u8bf4\u5012\u5165\u65e0\u6068\u751f\u90a3\u4ee5\u96ea\u4f5c\u6210\u7684\u676f\u4e2d\uff0c\u4e00\u5b9a\u4f1a\u4f7f\u90a3\u56e2\u96ea \u513f\u7194\u6210\u51b0\u6c34\u624d\u662f\uff0c\u9152\u4eba\u676f\u4e2d\uff0c\u4e1d\u6beb\u4e0d\u6ea2\uff0c\u5012\u50cf\u90a3\u96ea\u676f\u513f\u662f\u74f7\u571f\u4f5c\u6210\u4e00\u822c\u3002 \u3000\u3000\u9976\u662f\u6885\u5c71\u6c11\u89c1\u591a\u8bc6\u5e7f\uff0c\u529f\u53c2\u9020\u5316\uff0c\u4e5f\u4e0d\u7531\u6298\u670d\uff01 \u3000\u3000\u6562\u60c5\u65e0\u6068\u751f\u786c\u51ed\u4e00\u80a1\u771f\u706b\u62a4\u7740\u90a3\u96ea\u676f\u513f\uff0c\u4f7f\u5b83\u4e0d\u7194\u4e00\u4e1d\u4e00\u6beb\uff0c\u8fd9\u4e00\u70b9\u5185\u529b\u4fee\u4e3a\uff0c\u6885 \u5c71\u6c11\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u662f\u7edd\u975e\u81ea\u5df1\u6240\u80fd\u53ca\u7684\u3002 \u3000\u3000\u6ce8\u6ee1\u4e00\u676f\uff0c\u6885\u5c71\u6c11\u4e5f\u81ea\u659f\u4e00\u676f\uff0c\u4e3e\u676f\u5bf9\u996e\u3002 \u3000\u3000\u8fd9\u201c\u6885\u5b50\u9999\u201d\u6b63\u662f\u672c\u5730\u7279\u4ea7\uff0c\u5b8c\u5168\u662f\u7528\u5939\u9053\u7684\u6885\u513f\u917f\u6210\uff0c\u82b1\u9999\u5473\u6e17\u5165\u9152\u4e2d\uff0c\u522b\u6709\u4e00 \u80a1\u99a5\u70c8\u7684\u5473\u9053\u3002 \u3000\u3000\u65e0\u6068\u751f\u9152\u624d\u5165\u53e3\uff0c\u5df2\u662f\u8d5e\u53e3\u4e0d\u7edd\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5fae\u7b11\u548c\u65e0\u6068\u751f\u5e72\u676f\uff0c\u76f4\u5230\u4e03\u5de1\uff0c\u624d\u653e\u4e0b\u9152\u58f6\u9053\uff1a\u201c\u9601\u4e0b\u521a\u624d\u90a3\u62db\u7edd\u5b66\uff0c\u4e0a\u76d8 \u597d\u50cf\u662f\u865a\u5f0f\uff0c\u4e0b\u76d8\u5374\u8e29\u4e03\u661f\uff0c\u968f\u673a\u53ef\u53d8\u4e3a\u516b\u5366\u4e4b\u65b9\u4f4d\uff0c\u654c\u624b\u5982\u679c\u4e0d\u5bdf\uff0c\u5148\u8ba9\u4e0a\u76d8\uff0c\u4f60\u5fc5 \u7acb\u53d8\u4e0b\u76d8\u7684\u4e03\u661f\u4f4d\u81f3\u516b\u5366\uff0c\u7136\u540e\u7528\u2018\u8fde\u73af\u811a\u2019\u88ad\u654c\uff0c\u518d\u8f6c\u4e0a\u4e09\u8def\u7684\u865a\u5f0f\u4e3a\u5b9e\uff0c\u653b\u52bf\u53d8\u5316 \u4e0d\u53ef\u8bf4\u4e0d\u591a\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u6545\u610f\u505c\u987f\u4e00\u4e0b\uff0c\u65e0\u6068\u751f\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4e0d\u6562\u95ee\u4ec0\u4e48\u62db\u5f0f\u53ef\u4ee5\u7834\u89e3\uff1f\u201d \u3000\u3000\u6885\u5c71\u6c11\u9053\uff1a\u201c\u6211\u4e0d\u776c\u4f60\u4e0a\u76d8\u7684\u653b\u52bf\uff0c\u4e0b\u76d8\u987a\u7740\u4f60\u7531\u4e03\u661f\u53d8\u4e3a\u516b\u5366\u7684\u5f0f\u5b50\u8e22\u51fa\u6570\u811a\uff0c \u518d\u7b49\u4f60\u4e0a\u76d8\u56f4\u865a\u4e3a\u5b9e\u4e4b\u9645\uff0c\u2018\u529b\u65a9\u84dd\u5173\u2019\u7684\u5f0f\u5b50\u6253\u4f60\u80c1\u4e0b\u2018\u7ae0\u95e8\u2019\u3001\u2018\u7d2b\u9e29\u2019\uff0c\u4f60\u5c31\u4e0d \u6687\u81ea\u4fdd\u2014\u2014\u201d \u3000\u3000\u6885\u5c71\u6c11\u8bf4\u5230\u8fd9\u91cc\uff0c\u65e0\u6068\u751f\u5df1\u6ee1\u9762\u60ca\u5bb9\uff0c\u5306\u5306\u9053\uff1a\u201c\u4e14\u6162\uff0c\u6211\u5728\u4f60\u4e0b\u76d8\u8e22\u51fa\u2018\u8fde\u73af\u811a\u2019 \u65f6\uff0c\u7acb\u5373\u53d8\u4e0a\u76d8\uff0c\u7528\u592a\u6781\u2018\u9ecf\u2019\u5b57\u8bc0\u5316\u5f00\u2014\u2014\u201d\u4e03\u5999\u795e\u541b\u6f5c\u5fc3\u5fae\u601d\uff0c\u53c8\u51fa\u5bf9\u7b56\uff0c\u659c\u659c\u7684 \u6bd4\u51fa\u4e00\u5f0f\u3002 \u3000\u3000\u4e8c\u4eba\u5f00\u59cb\u4e43\u662f\u7528\u5e73\u751f\u7684\u7cbe\u7edd\u62db\u5f0f\u6765\u6d4b\u9a8c\u5bf9\u624b\uff0c\u8fd9\u65f6\u5374\u7531\u6162\u53d8\u5feb\uff0c\u53ea\u89c1\u4e8c\u4eba\u53e3\u820c\u624b\u811a \u9f50\u52a8\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u90fd\u8bf4\u5f97\u5341\u5206\u5feb\u6377\u3002 \u3000\u3000\u65e0\u6068\u751f\u5df2\u5c06\u201c\u7834\u7389\u62f3\u6cd5\u201d\u5c55\u5f00\uff0c\u6885\u5c71\u6c11\u4e5f\u5c55\u5f00\u201c\u866c\u679d\u5251\u6cd5\u201d\u4e2d\u7684\u8fde\u73af\u6740\u62db\u3002 \u3000\u3000\u4e0d\u5230\u7247\u523b\uff0c\u4e8c\u4eba\u5df2\u4ee5\u53e3\u4ee3\u5251\uff0c\u6298\u4e86\u5c06\u8fd1\u4e94\u516d\u5341\u62db\uff0c\u5374\u662f\u8f8e\u94e2\u5e76\u91cd\uff0c\u4e0d\u5206\u4e0a\u4e0b\u3002 \u3000\u3000\u65e0\u6068\u751f\u8d8a\u6253\u8d8a\u60ca\uff0c\u5fc3\u4e2d\u5e73\u65e5\u4ee5\u4e3a\u4e2d\u539f\u6b66\u5b66\u51cb\u843d\u7684\u5ff5\u5934\u767b\u65f6\u4e0d\u653b\u81ea\u6d88\uff0c\u6697\u6697\u8d5e\u53f9\u9053\uff1a \u201c\u6211\u65e0\u6068\u751f\u81ea\u4ee5\u4e3a\u4e00\u4ee3\u5947\u4eba\uff0c\u4f46\u82e5\u4e0d\u662f\u90a3\u5343\u5e74\u6731\u679c\uff0c\u770b\u6765\u8fd9\u9b3c\u624d\u7684\u529f\u529b\u53ef\u8981\u548c\u6211\u4e0d\u76f8\u4e0a \u4e0b\uff0c\u800c\u62db\u5f0f\u4e4b\u5947\u4f3c\u8fd8\u6709\u8fc7\u4e4b\u54e9\uff01\u6700\u96be\u5f97\u7684\u4fbf\u662f\u8fd9\u5bb6\u4f19\u4e0d\u4f46\u6b66\u5b66\u901a\u795e\uff0c\u800c\u4e14\u6587\u624d\u3001\u68cb\u827a\uff0c \u597d\u50cf\u6bcf\u4e00\u6837\u90fd\u51cc\u9a7e\u4e8e\u6211\u4e4b\u4e0a\u54e9\uff1f\u54fc\uff01\u53ef\u60dc\uff0c\u8fd9\u7b49\u4eba\u624d\u7adf\u4f1a\u662f\u4e00\u4e2a\u5927\u5927\u7684\u6deb\u8d3c\u3002 \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u5fc3\u4e2d\u4e0d\u7531\u8054\u60f3\u5230\u4e3a\u4e03\u5999\u795e\u541b\u800c\u6b7b\u7684\u7f2a\u4e5d\u5a18\uff0c\u5fc3\u5934\u706b\u8d77\uff0c\u5374\u8fc5\u901f\u53c8\u8f6c\u5ff5\u5230 \u81ea\u5df1\u59bb\u3001\u5973\u4e0b\u843d\u4e0d\u660e\uff0c\u5fc3\u4e2d\u4e00\u9635\u6fc0\u52a8\uff0c\u624b\u5934\u7f13\u4e86\u4e00\u7f13\u2014\u2014 \u3000\u3000\u4e03\u5999\u795e\u541b\u6b63\u5728\u7528\u81ea\u5df1\u5e73\u751f\u7cbe\u529b\u6240\u521b\u7684\u201c\u866c\u679d\u5251\u6cd5\u201d\u548c\u65e0\u6068\u751f\u4e92\u62c6\uff0c\u8fd9\u65f6\u65e0\u6068\u751f\u5fc3\u795e \u4e00\u758f\uff0c\u638c\u6cd5\u5fae\u5fae\u4e00\u6162\uff0c\u6885\u5c71\u6c11\u628a\u63e1\u826f\u673a\uff0c\u53cc\u624b\u8fde\u626c\uff0c\u4e00\u8fde\u4e0b\u4e86\u5341\u4f59\u62db\u6740\u624b\u3002 \u3000\u3000\u65e0\u6068\u751f\u5fc3\u4e2d\u4e00\u60ca\uff0c\u5fd9\u51dd\u795e\u63a5\u4e86\u6570\u62db\uff0c\u4f46\u4e5f\u663e\u5f97\u5341\u5206\u5306\u5fd9\u548c\u72fc\u72c8\u3002\u4e03\u5999\u795e\u541b\u51b7\u51b7\u4e00\u54fc\uff0c \u65e0\u6068\u751f\u767b\u65f6\u96c4\u5fc3\u5927\u53d1\uff0c\u53cc\u624b\u4e00\u5708\u4e00\u9012\uff0c\u4e5f\u7528\u6740\u624b\u53cd\u51fb\u8fc7\u6765\u3002 \u3000\u3000\u518d\u62c6\u5f97\u6570\u62db\uff0c\u65e0\u6068\u751f\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\u4e86\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6682\u4e14\u4f4f\u624b\uff0c\u6211 \u6709\u4e00\u4e8b\u76f8\u8bf7\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u5fae\u5fae\u4e00\u6014\uff0c\u505c\u4e0b\u624b\u9053\uff1a\u201c\u597d\u8bf4\uff01\u8001\u592b\u4e0d\u6562\u5f53\uff01\u201d \u3000\u3000\u65e0\u6068\u751f\u8138\u8272\u4e00\u6c89\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u77e5\u9053\u7f2a\u4e5d\u5a18\u5417\uff1f\u201d \u3000\u3000\u6885\u5c71\u6c11\u9661\u7136\u5927\u5403\u4e00\u60ca\uff0c\u5168\u8eab\u6709\u82e5\u7535\u51fb\uff0c\u6014\u5728\u4e00\u8fb9\u4e0d\u77e5\u6240\u63aa\uff0c\u6d3b\u50cf\u662f\u53d7\u7740\u4ec0\u4e48\u5f88\u5927\u7684 \u6253\u51fb\u4f3c\u7684\u3002 \u3000\u3000\u65e0\u6068\u751f\u6012\u6c14\u52c3\u52c3\u7684\u9053\uff1a\u201c\u4f60\u8fd9\u8001\u8d3c\uff0c\u4e07\u6b7b\u4e0d\u5f97\u8d4e\u5176\u7f6a\uff0c\u4f60\u6709\u6ca1\u6709\u5929\u826f\uff1f\u5bb3\u5f97\u5979\u6d3b\u6d3b \u75af\u98a0\u800c\u6b7b\uff01\u201d \u3000\u3000\u6885\u5c71\u6c11\u6709\u82e5\u4e0d\u95fb\u4e0d\u95ee\uff0c\u8138\u4e0a\u73b0\u51fa\u4e00\u79cd\u832b\u7136\u7684\u8868\u60c5\uff0c\u53ea\u662f\u542c\u5230\u201c\u5979\u75af\u98a0\u800c\u6b7b\u201d\u51e0\u4e2a\u5b57\uff0c \u4ed6\u76b1\u7eb9\u5bc6\u9762\u7684\u8138\u4e0a\u62bd\u6410\u4e86\u4e00\u4e0b\u2014\u2014 \u3000\u3000\u771f\u7684\uff0c\u4ed6\u50cf\u662f\u75f4\u4e86\uff0c\u90a3\u5f20\u6e29\u67d4\u7684\u4fcf\u8138\u5728\u4ed6\u8111\u6d77\u4e2d\u5370\u5f97\u591a\u4e48\u6df1\u523b\u554a\uff01\u4f46\u662f\uff0c\u5979\u6b7b\u4e86\uff0c \u6b7b\u5f97\u5f02\u5e38\u51c4\u60e8\uff0c\u8fd9\u662f\u8c01\u7684\u7f6a\u8fc7\uff1f \u3000\u3000\u5f53\u4ed6\u7a0d\u4e3a\u9192\u89c9\uff0c\u4ed6\u7acb\u523b\u60f3\u5230\u4e3a\u4ec0\u4e48\u65e0\u6068\u751f\u8981\u5982\u6b64\u6076\u72e0\u72e0\u7684\u5bf9\u81ea\u5df1\uff1f\u806a\u660e\u7684\u4ed6\u7acb\u523b\u60f3 \u5230\u8fd9\u662f\u4e00\u4e2a\u8bef\u4f1a\u3002 \u3000\u3000\u65e0\u6068\u751f\u59cb\u7ec8\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u8fd9\u65f6\u8f7b\u8f7b\u54fc\u4e86\u4e00\u58f0\uff0c\u90a3\u77e5\u6885\u5c71\u6c11\u4e5f\u51b7\u54fc\u4e86\u4e00\u58f0\u2014\u2014 \u3000\u3000\u6885\u5c71\u6c11\u6697\u9053\uff1a\u201c\u4e5d\u5a18\u4e4b\u6b7b\uff0c\u5c31\u7b97\u662f\u7531\u6211\u6885\u5c71\u6c11\u8d77\uff0c\u53c8\u5c82\u80fd\u8d23\u602a\u4e8e\u6211\uff1f\u8fd9\u663e\u7136\u662f\u8bef\u4f1a\uff0c \u4f46\u662f\u6211\u4f55\u5fc5\u8981\u548c\u4ed6\u89e3\u91ca\uff0c\u54fc\uff0c\u8fd9\u53ae\u5206\u660e\u662f\u76ee\u7779\u4e5d\u5a18\u8eab\u6b7b\u7684\uff0c\u4ee5\u4ed6\u90a3\u4e48\u9ad8\u7684\u529f\u529b\u7adf\u7136\u5750\u89c6 \u4e0d\u6551\uff0c\u54fc\uff0c\u8bf4\u4e0d\u5f97\u2014\u2014 \u3000\u3000\u504f\u6fc0\u7684\u601d\u60f3\u5728\u4ed6\u8111\u6d77\u4e2d\u5954\u653e\u7740\uff0c\u4ed6\u6108\u60f3\u6108\u6c14\uff0c\u4f3c\u4e4e\u771f\u770b\u5230\u4e5d\u5a18\u8f97\u8f6c\u98a0\u72c2\uff0c\u800c\u65e0\u6068\u751f \u5750\u89c6\u8896\u624b\u7684\u60c5\u5f62\uff0c\u4e0d\u7981\u53c8\u91cd\u91cd\u54fc\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u65e0\u6068\u751f\u5fc3\u5934\u6b63\u662f\u706b\u8d77\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u7a81\u7136\u53c8\u89c1\u6885\u5c71\u6c11\u54fc\u4e86\u4e00\u58f0\uff0c\u5389\u543c\u9053\uff1a\u201c\u72c2\u8d3c\u554a\u72c2 \u8d3c\uff0c\u4e8f\u4f60\u6ee1\u8179\u5947\u624d\uff0c\u7adf\u4e0d\u81ea\u68c0\u70b9\uff0c\u6211\u65e0\u6068\u751f\u8bf4\u4e0d\u5f97\u4eca\u65e5\u8981\u66ff\u5929\u884c\u9053\uff01\u201d\u8bf4\u7740\u4e3e\u638c\u4e0b\u5288\u2014 \u2014 \u3000\u3000\u6885\u5c71\u6c11\u5374\u51b7\u7b11\u4e00\u58f0\uff0c\u7f13\u7f13\u7741\u5f00\u53cc\u76ee\uff0c\u77aa\u7740\u65e0\u6068\u751f\u3002\u65e0\u6068\u751f\u6b63\u5f85\u5288\u4e0b\u7684\u4e00\u638c\u7adf\u81ea\u6295\u6562 \u7acb\u523b\u5288\u4e0b\u2014\u2014 \u3000\u3000\u5c31\u5728\u6b64\u65f6\u5ffd\u7136\u80cc\u540e\u4e00\u4eba\u9ad8\u547c\uff1a\u201c\u4ec0\u4e48\u4eba\u6562\u4f24\u543e\u5e08\uff1f\u201d \u3000\u3000\u58f0\u97f3\u5c1a\u5728\u5341\u4e08\u4e4b\u5916\uff0c\u4f46\u970e\u65f6\u65e0\u6068\u751f\u611f\u89c9\u52b2\u529b\u903c\u80cc\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u60ca\uff0c\u8d76\u7d27\u6536\u4f4f\u4e0b\u5288\u4e4b \u52bf\uff0c\u56de\u8eab\u4e00\u8896\u62c2\u51fa\u2014\u2014 \u3000\u3000\u60f3\u662f\u6765\u4eba\u662f\u6025\u5207\u53d1\u638c\uff0c\u53cc\u65b9\u90fd\u65e0\u6cd5\u8eb2\u95ea\uff0c\u53ea\u542c\u5f97\u7830\u7136\u4e00\u54cd\uff0c\u4e16\u5916\u4e09\u4ed9\u7684\u65e0\u6839\u751f\u7adf\u88ab \u9707\u5f97\u53cc\u80a9\u4e00\u6643\uff0c\u6765\u4eba\u5374\u88ab\u9707\u5f97\u5012\u9000\u4e24\u6b65\u3002 \u3000\u3000\u867d\u8bf4\u65e0\u6068\u751f\u5306\u4fc3\u53d1\u62db\uff0c\u529b\u9053\u6ca1\u6709\u7528\u8db3\uff0c\u4f46\u662f\u8fd9\u4e00\u8896\u65e2\u662f\u51fa\u81ea\u4e16\u5916\u4e09\u4ed9\u4e4b\u624b\uff0c\u4e00\u4e3e\u624b \u4e4b\u95f4\u5df2\u8db3\u4ee5\u81f4\u4eba\u6b7b\u547d\uff0c\u4f46\u6765\u4eba\u5374\u53ea\u88ab\u9707\u51fa\u4e24\u6b65\uff0c\u5f53\u7136\u4ee4\u4ed6\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53cc\u4e07\u4e00\u671d\u76f8\u89c1\u4e4b\u4e0b\uff0c\u66f4\u662f\u5927\u60ca\uff0c\u539f\u6765\u8fd9\u4eba\u7adf\u662f\u8f9b\u6377\uff01\u65e0\u6068\u751f\u5728\u60ca\u9707\u4e4b\u4f59\uff0c\u8fd8\u6709\u5c11\u8bb8 \u5e86\u5e78\uff0c\u672c\u6765\u4ed6\u4ee5\u4e3a\u8f9b\u6377\u662f\u846c\u8eab\u6d77\u5e95\u4e86\u7684\uff0c\u6bcf\u5f53\u4ed6\u5e73\u5fc3\u9759\u6c14\u60f3\u7740\u65f6\uff0c\u603b\u89c9\u6709\u4e00\u4efd\u5185\u759a\uff0c\u73b0 \u5728\u8f9b\u6377\u4e0d\u4ec5\u6ca1\u6709\u846c\u8eab\u6d77\u5e95\uff0c\u800c\u4e14\u4f3c\u4e4e\u529f\u529b\u5927\u589e\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u8f9b\u6377\u5df2\u6012\u9053\uff1a \u3000\u3000\u201c\u4f60\u5e72\u4ec0\u4e48\u8981\u6697\u7b97\u6211\u6885\u53d4\u53d4\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6027\u60c5\u672c\u5c31\u504f\u6fc0\uff0c\u6069\u6028\u4e4b\u5fc3\u5341\u5206\u5f3a\u70c8\uff0c\u4ed6\u672c\u5bf9\u65e0\u6068\u751f\u5c31\u5341\u5206\u6000\u6068\uff0c\u8fd9\u65f6\u89c1\u4ed6\u4e3e\u638c \u6b32\u5288\u6885\u53d4\u53d4\uff0c\u4e0d\u7981\u66f4\u6012\uff0c\u5f53\u4ed6\u60f3\u5230\u6885\u53d4\u53d4\u662f\u5168\u8eab\u6b66\u529f\u5e9f\u53bb\u7684\u65f6\u5019\uff0c\u4ed6\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u4e86\u2014\u2014 \u3000\u3000\u201c\u54fc\uff0c\u5802\u5802\u4e16\u5916\u4e09\u4ed9\uff0c\u7adf\u5bf9\u4e00\u4e2a\u6ca1\u6709\u6b66\u529f\u7684\u4eba\u6697\u7b97\uff0c\u4f60\u8fd9\u79cd\u4eba\uff0c\u7b80\u76f4\uff0c\u7b80\u76f4\u2014\u2014\u6211 \u5012\u8bf4\u4e0d\u4e0a\u6765\u4e86\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u4e5f\u4e0d\u7981\u52c3\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u7b80\u76f4\u600e\u5730\uff1f\u201d \u3000\u3000\u8f9b\u6377\u51b7\u7b11\u4e00\u58f0\uff1a\u201c\u7b80\u76f4\u755c\u7272\u4e0d\u5982\uff01\u201d\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u600e\u4e48\u4f1a\u9a82\u51fa\u8fd9\u79cd\u8bdd\u6765\u3002 \u3000\u3000\u65e0\u6839\u751f\u6c14\u5f97\u53e3\u7ed3\uff0c\u731b\u5438\u4e00\u53e3\u6c14\u624d\u51b7\u9759\u4e0b\u6765\uff0c\u4ed6\u4fca\u79c0\u7684\u8138\u4e0a\u53c8\u6062\u590d\u4e86\u60ef\u5e38\u7684\u51b7\u5cfb\uff0c\u5634 \u89d2\u4e0a\u5e26\u7740\u4e0d\u5c51\u7684\u51b7\u7b11\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u65e0\u77e5\u5c0f\u8f88\uff0c\u5c82\u53ef\u53e3\u5410\u72c2\u8a00\uff01\u201d \u3000\u3000\u90a3\u77e5\u8f9b\u6377\u5df2\u7ea2\u4e86\u773c\uff0c\u4ecd\u5927\u559d\u9053\uff1a\u201c\u4f60\u8fd9\u72e0\u6bd2\u8001\u9b3c\uff0c\u6839\u672c\u6ca1\u6709\u8d44\u683c\u4e3a\u4eba\u5c0a\u957f\uff0c\u6211\u53ea\u66ff \u4e16\u5916\u4e09\u4ed9\u7684\u540d\u5934\u53ef\u60dc\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u4ecd\u7136\u51b7\u51b7\u9053\uff1a\u201c\u5c0f\u5b50\u4e0d\u5306\u597d\u6b79\uff0c\u8bf4\u4e0d\u5b9a\u6211\u65e0\u6068\u751f\u8981\u7ba1\u6559\u4f60\u4e86\uff01\u201d \u3000\u3000\u8f9b\u6377\u563f\u7684\u4e00\u58f0\uff0c\u51dd\u76ee\u4e00\u671b\uff0c\u53ea\u89c9\u65e0\u6068\u751f\u53cc\u8896\u5c31\u50cf\u6709\u51e0\u767e\u53ea\u8896\u5b50\u4e00\u822c\uff0c\u81ea\u5df1\u534a\u9762\u8981\u7a74 \u65e0\u4e00\u4e0d\u5728\u654c\u52bf\u4e4b\u5185\uff0c\u800c\u4e14\u8896\u4e2d\u4e4b\u95f4\u900f\u51fa\u9635\u9635\u5bd2\u98ce\u2014\u2014 \u3000\u3000\u82e5\u662f\u51e0\u6708\u524d\uff0c\u8f9b\u6377\u53c8\u5c06\u4e00\u62db\u4e5f\u8eb2\u4e0d\u8fc7\u5730\u675f\u624b\u5c31\u64d2\uff0c\u4f46\u662f\u6b64\u65f6\u8f9b\u6377\u5927\u975e\u6614\u65e5\uff0c\u7adf\u8fce\u9762 \u524d\u8de8\u534a\u6b65\u3002 \u3000\u3000\u6885\u5c71\u6c11\u529f\u529b\u91cf\u5931\uff0c\u6b66\u5b66\u4ecd\u5728\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6377\u513f\uff0c\u7528\u2018\u6885\u5360\u5148\u6625\u2019\u653b\u4ed6\u4e0b\u76d8\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u7684\u610f\u601d\u662f\u4ee5\u653b\u4e3a\u5b88\uff0c\u4f46\u662f\u773c\u524d\u4e00\u82b1\uff0c\u8f9b\u6377\u7adf\u4ece\u4e24\u53ea\u631f\u5e26\u9510\u52b2\u4e4b\u98ce\u7684\u8896\u5b50\u4e4b\u4e2d \u95ea\u4e86\u8fc7\u53bb\uff0c\u800c\u4e14\u4e00\u6643\u5df2\u5230\u4e86\u65e0\u6068\u751f\u80cc\u540e\u8fd9\u4e00\u4e0b\u5b50\u6885\u5c71\u6c11\u65e0\u6068\u751f\u53cc\u53cc\u5927\u9a87\uff0c\u6885\u5c71\u6c11\u60ca\u7684\u662f \u8f9b\u6377\u6240\u7528\u62db\u5f0f\u7adf\u975e\u81ea\u5df1\u6240\u6388\uff0c\u800c\u5de7\u5999\u5219\u5c24\u6709\u8fc7\u4e4b\u3002\u65e0\u6068\u751f\u60ca\u7684\u662f\u8f9b\u6377\u90a3\u4e00\u6b65\u4e4b\u95f4\uff0c\u6697\u542b \u7384\u673a\uff0c\u4f3c\u4e4e\u662f\u90a3\u5c0f\u6222\u5c9b\u4e3b\u6167\u5927\u5e08\u7684\u4e0d\u4f20\u4e4b\u79d8\u2014\u2014\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\uff01 \u3000\u3000\u4f46\u4ed6\u4ecd\u4e0d\u80fd\u7f6e\u4fe1\uff0c\u5f53\u4e0b\u559d\u4e86\u4e00\u58f0\uff1a\u201c\u4f60\u518d\u63a5\u4e00\u62db\u8bd5\u8bd5\uff01\u201d \u3000\u3000\u638c\u4e0b\u624b\u4e2d\u52b2\u9053\u53c8\u52a0\u4e86\u4e24\u6210\uff0c\u5355\u638c\u4e00\u98d8\u4e4b\u95f4\uff0c\u5b9b\u5982\u5927\u5370\u638c\u7684\u5f0f\u5b50\u4e00\u822c\u76d6\u4e86\u4e0b\u6765\u2014\u2014 \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u529f\u529b\u867d\u7136\u5927\u975e\u6614\u6bd4\uff0c\u4f46\u662f\u65e0\u6068\u751f\u8fd9\u62db\u5df2\u7528\u51fa\u4e86\u516b\u6210\u4ee5\u4e0a\u771f\u529b\uff0c\u8f9b\u6377\u4e0d\u7981\u5fc3 \u4e2d\u4e00\u602f\uff0c\u624b\u4e2d\u53cc\u638c\u4e00\u5708\uff0c\u534a\u62db\u201c\u6885\u5410\u5947\u9999\u201d\u5c1a\u672a\u65bd\u8db3\uff0c\u811a\u4e0b\u5df2\u5982\u884c\u4e91\u6d41\u6c34\u822c\u9000\u4e86\u5f00\u53bb\u2014 \u2014 \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u7136\u547c\u7684\u4e00\u9635\u602a\u5578\uff0c\u4e00\u6761\u767d\u5f71\u4ece\u576a\u4e0b\u98de\u8dc3\u800c\u6765\uff0c\u8fdc\u770b\u8fc7\u53bb\uff0c\u4f9d\u7a00\u53ef\u8fa8\u51fa \u6b63\u662f\u4e00\u4e2a\u767d\u8863\u4eba\u4ee5\u4e0a\u4e58\u8f7b\u529f\u75be\u9a70\u800c\u81f3\u3002 \u3000\u3000\u90a3\u4eba\u811a\u7a0b\u6781\u5feb\uff0c\u800c\u59ff\u6001\u7f8e\u5999\u4e4b\u6781\uff0c\u8fdc\u770b\u5b9b\u82e5\u4e00\u53ea\u767d\u8776\u98de\u7fd4\uff0c\u65e0\u6068\u751f\u3001\u8f9b\u6377\uff0c\u6885\u5c71\u6c11 \u90fd\u4e0d\u7981\u5f15\u76ee\u6ce8\u89c6\u3002\u90a3\u4eba\u8d70\u5f97\u8fd1\u65f6\uff0c\u5ffd\u7136\u54c8\u54c8\u957f\u7b11\uff0c\u90a3\u7b11\u58f0\u6709\u5982\u591c\u67ad\u957f\u557c\uff0c\u5341\u5206\u523a\u8033\u3002 \u3000\u3000\u8f9b\u6377\u770b\u4ed6\u4e24\u814b\u4e0b\u8fd8\u631f\u7740\u4e24\u4e2a\u4eba\uff0c\u770b\u6765\u4e24\u4eba\u90fd\u5df2\u660f\u8ff7\uff0c\u8f6f\u7ef5\u7ef5\u5730\u4efb\u4ed6\u631f\u7740\uff0c\u5fc3\u4e2d\u4e0d\u7981 \u4f69\u670d\u8fd9\u4eba\u529f\u529b\uff0c\u5e26\u7740\u4e24\u4eba\u90a3\u6709\u8fd9\u4efd\u8f7b\u7075\u529f\u529b\u3002 \u3000\u3000\u90a3\u4eba\u5ffd\u7136\u505c\u4f4f\u7b11\u58f0\uff0c\u6717\u58f0\u9053\uff1a\u201c\u65e0\u6781\u5c9b\u4e3b\uff0c\u8fd8\u8bc6\u5f97\u6211\u5417\uff1f\u201d\u90a3\u58f0\u97f3\u771f\u6bd4\u65b9\u624d\u7684\u7b11\u58f0 \u8fd8\u8981\u96be\u542c\u51e0\u5206\u3002 \u3000\u3000\u65e0\u6068\u751f\u51dd\u76ee\u4e00\u770b\uff0c\u5fc3\u4e2d\u731b\u7701\uff0c\u8fd9\u767d\u8863\u6c49\u5b50\u6b63\u662f\u548c\u81ea\u5df1\u66fe\u6709\u4e00\u9762\u4e4b\u7f18\u7684\u4e1c\u6d77\u76d7\u738b\u2014\u2014 \u7389\u9aa8\u9b54\u3002 \u3000\u3000\u65e0\u6068\u751f\u7acb\u523b\u60f3\u5230\u7389\u9aa8\u9b54\u624b\u4e0b\u5728\u6d77\u4e0a\u73a9\u7684\u4e00\u624b\u6bc1\u8239\u52fe\u5f53\uff0c\u5fc3\u4e2d\u867d\u7136\u5927\u6012\uff0c\u4f46\u8868\u9762\u4e0a\u4ecd \u4fdd\u6301\u90a3\u4e00\u4efd\u51b7\u51b7\u7684\u6001\u5ea6\uff0c\u4e0d\u5c51\u7684\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u7389\u9aa8\u9b54\u4f60\u624b\u4e0b\u90a3\u4ec0\u4e48\u59d3\u6210\u7684\u8235\u4e3b\u624b\u4e0b\u771f \u5dee\u52b2\u554a\uff0c\u6211\u672c\u6765\u8fd8\u9053\u4e1c\u6d77\u6d77\u76d7\u81ea\u4ece\u4f60\u8001\u5144\u63a5\u7ba1\u4e4b\u540e\uff0c\u4e00\u5b9a\u5a01\u52bf\u5927\u975e\u6614\u6bd4\uff0c\u90a3\u91cc\u77e5\u9053\u5374\u662f \u6bcf\u51b5\u6108\u4e0b\uff0c\u6211\u505a\u5144\u5f1f\u7684\u770b\u4e86\u771f\u662f\u5931\u671b\u5f97\u5f88\uff0c\u4e00\u6c14\u4e4b\u4e0b\u628a\u4e09\u6761\u8239\u90fd\u9001\u8fdb\u4e86\u6d77\u9f99\u738b\u5bab\u3002\u201d \u3000\u3000\u4ed6\u672c\u4ee5\u4e3a\u7389\u9aa8\u9b54\u542c\u4e86\u4e4b\u540e\u5fc5\u5b9a\u60ca\u6012\u4ea4\u52a0\uff0c\u90a3\u77e5\u7389\u9aa8\u9b54\u53ea\u5fae\u5fae\u70b9\u5934\uff0c\u4f3c\u4e4e\u65e9\u5df2\u77e5\u9053\u4e86 \u4e00\u822c\uff0c\u4e00\u76f4\u7b49\u4ed6\u8bf4\u5b8c\uff0c\u624d\u7f13\u7f13\u9053\uff1a\u201c\u5c31\u662f\u56e0\u4e3a\u6211\u7389\u9aa8\u9b54\u627f\u6d77\u4e0a\u5144\u5f1f\u77a7\u5f97\u8d77\uff0c\u5c0a\u79f0\u4ffa\u4e00\u58f0 \u5934\u513f\uff0c\u6240\u4ee5\u4eca\u65e5\u624d\u6765\u6709\u4e00\u4e8b\u6c42\u4f60\u8001\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u5fc3\u60f3\uff1a\u201c\u4f60\u6d3e\u4eba\u6697\u7b97\u4e8e\u6211\uff0c\u6211\u8fd8\u6ca1\u627e\u4f60\u7b97\u5e10\uff0c\u4f60\u53c8\u6709\u4ec0\u4e48\u82b1\u6837\uff1f\u201d \u3000\u3000\u53ea\u89c1\u7389\u9aa8\u9b54\u7ee7\u7eed\u9053\uff1a\u201c\u4f60\u8001\u4e5f\u4f55\u9053\uff0c\u54b1\u4eec\u5403\u8fd9\u884c\u996d\u7684\u6700\u91cd\u8981\u5c31\u662f\u5730\u76d8\uff0c\u4ee5\u524d\u5f80\u6765\u4e1c \u6d77\u7684\u8239\u8236\u90fd\u5f97\u7ecf\u8fc7\u54b1\u4eec\u5341\u6c99\u7fa4\u5c9b\uff0c\u4f46\u662f\u6700\u8fd1\u7531\u4e8e\u65b0\u53d1\u73b0\u822a\u8def\uff0c\u5546\u4eba\u90fd\u7ed5\u9053\u65e0\u6781\u5c9b\u800c\u884c\uff0c \u8fd9\u6837\u54b1\u4eec\u5144\u5f1f\u53ef\u5c31\u6ca1\u6709\u996d\u5403\u5566\uff0c\u6240\u4ee5\u5728\u4e0b\u6597\u80c6\u8bf7\u6c42\u65e0\u6781\u5c9b\u4e3b\u4e00\u6869\u4e8b\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u6108\u542c\u6108\u4e0d\u662f\u5473\u9053\uff0c\u5fc3\u4e2d\u4e0d\u7981\u52c3\u7136\u5927\u6012\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u53ef\u662f\u8981\u6211\u65e0\u6781\u5c9b\u4f5c\u4e3a\u4f60 \u7684\u5730\u76d8\uff1f\u201d \u3000\u3000\u7389\u9aa8\u9b54\u5e72\u7b11\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\uff0c\u5728\u4e0b\u53ea\u662f\u8bf7\u5c9b\u4e3b\u8d4f\u54b1\u4eec\u5144\u5f1f\u4e00\u53e3\u996d\u5403\u3002\u201d \u3000\u3000\u8fd9\u7b49\u4e8e\u662f\u627f\u8ba4\u4e86\u65e0\u6068\u751f\u7684\u8bdd\uff0c\u65e0\u6068\u751f\u6012\u6781\u53cd\u7b11\uff0c\u7b11\u58f0\u6108\u6765\u6108\u9ad8\uff0c\u5b9b\u5982\u8001\u9f99\u6e05\u541f\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u5728\u9ec4\u5b50\u6c99\u7fa4\u5c9b\u53e6\u5e03\u7f6e\u4e86\u4e00\u4e2a\u5c0f\u5c9b\uff0c\u4e00\u5207\u4f4f\u5b85\u7528\u5177\u5c3d\u548c\u65e0\u6781\u5c9b\u539f \u6709\u7684\u5e76\u65e0\u4e24\u6837\uff01\u5982\u679c\u5c9b\u4e3b\u613f\u610f\u7684\u8bdd\uff0c\u5c31\u8bf7\u5c9b\u4e3b\u5c48\u4f4f\u90a3\u91cc\u3002\u201d \u3000\u3000\u65e0\u6839\u751f\u7b11\u58f0\u7a81\u505c\uff0c\u8138\u8272\u4e00\u6c89\uff0c\u5bf9\u7389\u9aa8\u9b54\u4e0d\u7406\u4e0d\u776c\uff0c\u4e00\u526f\u5b8c\u5168\u4e0d\u653e\u773c\u5185\u7684\u6837\u5b50\uff0c\u4f3c\u4e4e \u56de\u7b54\u53cd\u800c\u6709\u8fb1\u9762\u4e00\u822c\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u89c1\u65e0\u6068\u751f\u4e0d\u7406\u4e0d\u776c\uff0c\u5f53\u4e0b\u53c8\u5e72\u7b11\u4e00\u9053\uff1a\u201c\u65e0\u6068\u751f\uff0c\u4f60\u4e14\u770b\u4e00\u6869\u4e8b\u7269\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u56de\u9996\u4e00\u770b\uff0c\u53ea\u89c1\u7389\u9aa8\u9b54\u5c06\u631f\u5728\u814b\u4e0b\u7684\u4e24\u4eba\u9762\u5b54\u62ac\u4e86\u4e0a\u6765\uff0c\u65e0\u6068\u751f\u4e00\u770b\u4e4b\u4e0b\uff0c \u60ca\u5f97\u53eb\u4e86\u8d77\u6765\u8f9b\u6377\u4e00\u770b\uff0c\u4e5f\u9669\u4e9b\u53eb\u51fa\u4e86\u58f0\uff0c\u539f\u6765\u90a3\u660f\u8ff7\u4e2d\u7684\u4e24\u4eba\u7adf\u662f\u4ee5\u4e3a\u8eab\u846c\u6ce2\u5e95\u7684\u7f2a \u4e03\u5a18\u548c\u83c1\u513f\uff01 \u3000\u3000\u65e0\u6068\u751f\u53eb\u58f0\u672a\u6da1\uff0c\u5df2\u5982\u52b2\u77e2\u4e00\u822c\u6251\u5411\u7389\u9aa8\u9b54\uff0c\u4ed6\u8eab\u8eaf\u5b8c\u5168\u6c34\u5e73\u6a2a\u5728\u7a7a\u4e2d\uff0c\u5c31\u50cf\u98de\u8fc7 \u53bb\u4e00\u822c\uff0c\u5de6\u624b\u4e00\u62db \u3000\u3000\u201c\u96f7\u52a8\u4e07\u7269\u201d\u5168\u529b\u65bd\u51fa\uff0c\u53f3\u624b\u5374\u5f85\u673a\u62a2\u6551\u660f\u8ff7\u4e2d\u7684\u7231\u59bb\u7231\u5973\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u65e0\u6068\u751f\u8fd9\u4e00\u62db\u201c\u96f7\u52a8\u4e07\u7269\u201d\u653b\u52bf\u51cc\u5389\u65e0\u6bd4\uff0c\u771f\u662f\u7edd\u4e16\u9ad8\u4eba\u8eab\u624b\uff0c\u5fc3\u4e2d\u5927\u4e3a\u8d5e \u53f9\uff0c\u5374\u4e0d\u77e5\u7389\u9aa8\u9b54\u5982\u4f55\u7684\u5316\u89e3\uff1f \u3000\u3000\u90a3\u77e5\u65e0\u6068\u751f\u7684\u201c\u96f7\u52a8\u4e07\u7269\u201d\u6b63\u5f85\u4f7f\u8db3\u53d1\u52b2\uff0c\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff0c\u53c8\u786c\u751f\u751f\u5c06\u653b\u52bf\u6536\u4e86\u56de \u6765\uff0c\u8eab\u4f53\u5237\u5730\u843d\u5730\u3002 \u3000\u3000\u539f\u6765\u7389\u9aa8\u9b54\u5f85\u65e0\u6068\u751f\u62db\u5f0f\u5c06\u5230\u65f6\uff0c\u53cc\u624b\u6597\u7136\u7d27\u53e9\u7f2a\u4e03\u5a18\u53ca\u83c1\u513f\u7684\u8109\u95e8\uff0c\u4f5c\u52bf\u5f85\u53d1\uff0c \u65e0\u6839\u751f\u77e5\u9053\u53ea\u8981\u4ed6\u624b\u4e0a\u5185\u52b2\u4e00\u53d1\uff0c\u81ea\u5df1\u7231\u59bb\u7231\u5973\u5c31\u662f\u795e\u4ed9\u4e5f\u96be\u6d3b\u4e86\uff0c\u4e8e\u662f\u53ea\u597d\u786c\u786c\u6536\u56de \u4e86\u62db\u5f0f\uff01 \u3000\u3000\u7389\u9aa8\u9b54\u4e5f\u751a\u987e\u5fcc\uff0c\u624b\u5e26\u4e24\u4eba\u4e5f\u4e0d\u89c1\u4f5c\u52bf\u7528\u52b2\uff0c\u8eab\u4f53\u9661\u7136\u62d4\u8d77\u98d8\u843d\u5341\u4e08\u5916\u3002 \u3000\u3000\u6885\u5c71\u6c11\u53ca\u8f9b\u6377\u5bf9\u7389\u9aa8\u9b54\u7684\u540d\u5934\u4e5f\u6709\u6240\u95fb\uff0c\u8fd9\u65f6\u89c1\u4ed6\u8f7b\u529f\u4f73\u5999\uff0c\u5fc3\u4e2d\u90fd\u6697\u8d5e\u4e00\u58f0\uff0c\u53ea \u6709\u8f9b\u6377\u4e00\u9897\u5fc3\u5b8c\u5168\u7d27\u6302\u5728\u90a3\u660f\u8ff7\u4e0d\u9192\u7684\u83c1\u513f\u8eab\u4e0a\u3002 \u3000\u3000\u65e0\u6068\u751f\u88ab\u4ed6\u8fd9\u6837\u4e00\u6405\uff0c\u5f53\u771f\u6709\u70b9\u53d1\u614c\uff0c\u4f46\u5fc3\u6df1\u5904\u4ecd\u6709\u4e00\u4e1d\u9ad8\u5174\uff0c\u5012\u5e95\u8bc1\u660e\u7f2a\u4e03\u5a18\u548c \u83c1\u513f\u5e76\u6ca1\u6709\u846c\u8eab\u6d77\u5e95\u4e86\u3002 \u3000\u3000\u4f46\u4ed6\u4e0d\u6562\u5984\u52a8\uff0c\u4e8e\u662f\uff0c\u5468\u906d\u9759\u4e0b\u6765\u4e86\u3002 \u3000\u3000\u6c99\u9f99\u576a\u4e0a\u66ae\u9732\u7ed5\u88f3\uff0c\u94f6\u767d\u7684\u5927\u5730\u53cd\u6620\u51fa\u4e00\u7247\u7d2b\u7ea2\u7684\u665a\u971e\u6765\uff0c\u5bd2\u98ce\u4f9d\u7136\u8086\u52b2\u8fd9\u5468\u906d\u7a7a \u6c14\u662f\u5982\u6b64\u7684\u7d27\u5f20\uff0c\u7389\u9aa8\u9b54\u4e00\u88ad\u53e4\u602a\u7684\u767d\u886b\u677e\u6563\u5730\u5782\u7740\uff0c\u4f46\u662f\u5374\u4e1d\u6beb\u6ca1\u6709\u56e0\u52b2\u98ce\u800c\u88ab\u5e26\u52a8\uff0c \u663e\u7136\u662f\uff0c\u4ed6\u6b63\u5168\u8eab\u8fd0\u7740\u529f\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u7741\u7740\u8d64\u7ea2\u7684\u53cc\u773c\uff0c\u4f46\u662f\u4ed6\u4e0d\u6562\u7a0d\u4e3a\u5984\u52a8\uff0c\u5c24\u5176 \u662f\u5f53\u4ed6\u7684\u76ee\u5149\u89e6\u53ca\u5730\u4e0b\u660f\u8ff7\u4e0d\u9192\u7684\u7f2a\u4e03\u5a18\u548c\u7231\u5973\u83c1\u513f\u65f6\uff0c\u4ed6\u66f4\u662f\u53c8\u6025\u53c8\u4e71\uff0c\u7adf\u7136\u4e0d\u77e5\u6240 \u63aa\u8d77\u6765\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u4f9d\u7136\u5168\u795e\u6212\u5907\u7740\uff0c\u4e45\u4e45\u4e0d\u89c1\u65e0\u6068\u751f\u56de\u7b54\uff0c\u4ed6\u53c8\u52a0\u4e86\u4e00\u53e5\uff1a \u201c\u8fd9\u6761\u4ef6\u53ef\u8bf4\u7b80\u5355\u6781\u4e86\uff0c\u53ea\u8981\u5c9b\u4e3b\u9894\u4e00\u4e0b\u9996\uff0c\u5728\u4e0b\u7acb\u5373\u91ca\u653e\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u6839\u672c\u4e00\u4e2a\u5b57\u4e5f\u6ca1\u6709\u542c\u8fdb\u53bb\uff0c\u4efb\u4ed6\u6b66\u529f\u76d6\u4e16\uff0c\u806a\u660e\u7edd\u9876\uff0c\u4f46\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u4e5f\u6025 \u5f97\u624b\u5fc3\u6dcc\u6c57\uff0c\u4e0d\u77e5\u600e\u4e48\u529e\u624d\u597d\u3002 \u3000\u3000\u84e6\u5730\uff0c\u65e0\u6068\u751f\u5927\u559d\u4e00\u58f0\uff1a\u201c\u72c2\u8d3c\u62db\u6253\uff01\u201d\u624b\u6296\u5904\uff0c\u4e00\u622a\u67af\u679d\u5df2\u6d41\u661f\u822c\u6253\u51fa\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u8eab\u4f53\u4e00\u6643\uff0c\u4e00\u7247\u767d\u5f71\u4e2d\u8eab\u8eaf\u5df2\u8f6c\u4e86\u4e09\u767e\u516d\u5341\u5ea6\uff0c\u56de\u5230\u539f\u72b6\u65f6\uff0c\u90a3\u4e2a\u67af\u679d\u98de\u64e6 \u800c\u8fc7\u4f46\u662f\u5750\u5728\u5730\u4e0a\u7684\u6885\u5c71\u6c11\u5374\u53d1\u89c9\u65e0\u6068\u751f\u6253\u51fa\u67af\u679d\u65f6\uff0c\u6296\u624b\u4e4b\u72b6\u6709\u5f02\uff0c\u4ed6\u8f7b\u55c5\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u65e0\u6068\u751f\u5927\u540d\u4e45\u4ef0\uff0c\u4f55\u5fc5\u518d\u663e\u8fd9\u624b\u529f\u592b\uff0c\u53ea\u662f\u8fd9\u4e00\u622a\u67af\u679d\uff0c\u7b97\u662f \u7b54\u5e94\u8fd8\u662f\u4e0d\u7b54\u2014\u2014\u201d \u3000\u3000\u201c\u5e94\u201d\u5b57\u8fd8\u6ca1\u6709\u51fa\u53e3\uff0c\u679c\u7136\u4e0d\u51fa\u6885\u5c71\u6c11\u6240\u6599\u7684\uff0c\u90a3\u622a\u98de\u8d8a\u800c\u8fc7\u7684\u67af\u679d\u7adf\u53c8\u547c\u5730\u4e00\u58f0 \u8f6c\u56de\u4e86\u5934\uff0c\u6210\u4e00\u6bb5\u5f27\u7ebf\u5730\u88ad\u5411\u7389\u9aa8\u9b54\u7684\u80cc\u5fc3\u2014\u2014 \u3000\u3000\u7389\u9aa8\u9b54\u53ea\u77e5\u9053\u662f\u80cc\u540e\u6709\u4eba\u6697\u7b97\uff0c\u4e00\u65cb\u8eab\u95f4\u547c\u547c\u5288\u51fa\u4e24\u638c\u2014\u2014 \u3000\u3000\u201c\u535c\u201d\u5730\u4e00\u58f0\uff0c\u90a3\u622a\u67af\u679d\u7adf\u7136\u88ab\u4ed6\u51cc\u5389\u7684\u638c\u98ce\u5377\u98de\u800c\u53bb\uff0c\u649e\u5728\u4e08\u5916\u7684\u6885\u6811\u4e0a\uff0c\u800c\u4e14 \u6df1\u6df1\u5730\u63d2\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u7ad9\u5728\u6885\u5c71\u6c11\u8eab\u65c1\u7684\u8f9b\u6377\u6b63\u65b9\u8d5e\u53f9\u65e0\u6068\u751f\u7684\u795e\u5999\u53d1\u9556\u624b\u6cd5\uff0c\u53ea\u89c1\u65e0\u6068\u751f\u5df2\u4e58\u7389\u9aa8\u9b54\u8f6c \u8eab\u51fa\u638c\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u6709\u5982\u4e00\u7f15\u8f7b\u70df\u822c\u5411\u5730\u4e0a\u7684\u7f2a\u4e03\u5a18\u53ca\u83c1\u513f\u6251\u4e86\u4e0a\u53bb\u2014\u2014 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u8fd9\u4e00\u6251\u4e43\u662f\u5168\u529b\u65bd\u4e3a\uff0c\u90a3\u5feb\u867d\u5feb\u5230\u65e0\u4ee5\u590d\u52a0\uff0c\u4f46\u4e3e\u6b62\u4e4b\u8f7b\u7075\u4e5f\u5230\u4e86\u6781\u70b9\uff0c \u4f3c\u4e4e\u6574\u4e2a\u4eba\u5728\u7a7a\u4e2d\u7a81\u7136\u95f4\u5931\u53bb\u4e86\u91cd\u91cf\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u53d1\u89c9\u88ab\u81ea\u5df1\u626b\u51fa\u7684\u4e43\u662f\u65e0\u6068\u751f\u5148\u524d\u6253\u51fa\u7684\u4e00\u622a\u67af\u679d\uff0c\u7acb\u523b\u77e5\u9053\u4e0d\u5bf9\uff0c\u4e00\u62db \u201c\u80cc\u5c01\u9f99\u5bab\u201d\u65bd\u51fa\uff0c\u8eab\u4f53\u5982\u95ea\u7535\u822c\u8f6c\u56de\uff0c\u4f46\u662f\uff0c\u65e0\u6068\u751f\u7684\u624b\u6307\u79bb\u7f2a\u4e03\u5a18\u9886\u53e3\u5df4\u4e0d\u53ca\u534a\u5bf8 \u2014\u2014 \u3000\u3000\u7389\u9aa8\u9b54\u6025\u5f97\u5927\u53eb\u4e00\u58f0\uff0c\u767d\u8896\u4e00\u62c2\uff0c\u53f3\u638c\u631f\u7740\u6bd5\u751f\u529f\u529b\uff0c\u52bf\u6bd4\u5954\u96f7\u5730\u780d\u5411\u65e0\u6068\u751f\u5c0f\u81c2 \u2014\u2014 \u3000\u3000\u65e0\u6068\u751f\u867d\u7136\u88ab\u6551\u7231\u59bb\u7231\u5973\u4e4b\u60c5\u6025\u660f\u4e86\u5934\uff0c\u4f46\u662f\u7ecf\u9a8c\u544a\u8bc9\u4ed6\uff0c\u53ea\u8981\u4e00\u8ba9\u7389\u9aa8\u9b54\u78b0\u4e0a\u5c0f \u81c2\uff0c\u4e0d\u7ba1\u5185\u529f\u600e\u6837\u9ad8\u5f3a\uff0c\u8fd9\u53ea\u624b\u5c31\u7b97\u5e9f\u4e86\u3002 \u3000\u3000\u7535\u5149\u706b\u77f3\u95f4\uff0c\u4ed6\u53ea\u5f97\u6682\u7f13\u6293\u7f2a\u4e03\u5a18\u9886\u53e3\uff0c\u5306\u4fc3\u5730\u5c06\u4e0b\u6293\u4e4b\u638c\u53d8\u4e3a\u4e0a\u62cd\u2014\u2014 \u3000\u3000\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u5c3d\u7ba1\u65e0\u6068\u751f\u5306\u4fc3\u53d8\u62db\uff0c\u529b\u9053\u6ca1\u6709\u65bd\u5168\uff0c\u4f46\u662f\u7389\u9aa8\u9b54\u5df1\u88ab\u9707\u5f97\u80a9\u7a9d\u53d1 \u9ebb\u3002\u4e0d\u8fc7\u65e0\u6068\u751f\u5230\u5e95\u65e0\u6cd5\u78b0\u4e0a\u7f2a\u4e03\u5a18\u7684\u9886\u5b50\u3002 \u3000\u3000\u4f46\u662f\u65e0\u6068\u751f\u4f55\u7b49\u4eba\u7269\uff0c\u4e00\u624b\u5e94\u654c\uff0c\u53e6\u4e00\u624b\u5374\u4ecd\u817e\u51fa\u53bb\u63d0\u7f2a\u4e03\u5a18\u7684\u8863\u895f\u2014\u2014 \u3000\u3000\u53ea\u542c\u7389\u9aa8\u9b54\u51b7\u51b7\u4e00\u54fc\uff0c\u767d\u8896\u4e00\u6325\u4e00\u5377\uff0c\u4e00\u80a1\u65e0\u81ed\u65e0\u5473\u7684\u5f69\u8272\u70df\u96fe\u4ece\u8896\u53e3\u4e2d\u55b7\u51fa\uff01 \u3000\u3000\u65e0\u6068\u751f\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u5230\u7389\u9aa8\u9b54\u662f\u6709\u540d\u7684\u8001\u6bd2\u7269\uff0c\u8fd9\u5f69\u8272\u70df\u96fe\u5fc5\u662f\u4ec0\u4e48\u6bd2\u6076\u4e4b\u96fe\uff0c\u4e5f \u987e\u4e0d\u5f97\u518d\u6293\u7f28\u4e03\u5a18\u8863\u895f\uff0c\u7ffb\u638c\u5728\u5730\u4e0a\u4e00\u6309\uff0c\u8eab\u4f53\u66b4\u9000\uff0c\u540c\u65f6\u5c4f\u4f4f\u547c\u5438\u2014\u2014 \u3000\u3000\u65e0\u6068\u751f\u4e00\u770b\u7389\u9aa8\u9b54\u8896\u4e2d\u98de\u51fa\u5f69\u8272\u70df\u96fe\uff0c\u5fc3\u4e2d\u7acb\u77e5\u4e0d\u5999\uff0c\u7acb\u65f6\u62b1\u8d77\u6885\u5c71\u6c11\u4e00\u540c\u9000\u540e\uff0c \u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u201c\u5feb\u9000\uff01\u201d \u3000\u3000\u540c\u65f6\u4f38\u624b\u6293\u4f4f\u4e03\u5999\u795e\u541b\uff0c\u8db3\u4e0b\u4e0d\u505c\u5730\u98de\u8dc3\u800c\u8d77\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u611f\u5934\u4e0a\u5ffd\u7136\u4e00\u660f\uff0c\u8fde\u5fd9\u5c4f\u4f4f\u547c\u5438\u4f7f\u51fa\u201c\u6697\u9999\u6d6e\u5f71\u201d\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u5012\u98de\u51fa\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u529f\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u8fd9\u4e00\u62db\u201c\u9999\u95fb\u5341\u91cc\u201d\u8db3\u8db3\u98de\u51fa\u4e03\u516b\u4e08\u624d\u843d\u4e86\u4e0b\u6765\uff0c\u811a\u5c16\u624d \u78b0\u5730\uff0c\u8033\u8fb9\u201c\u547c\u201d\u5730\u4e00\u58f0\uff0c\u65e0\u6068\u751f\u6276\u7740\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u4ece\u65c1\u8fb9\u8d85\u8fc7\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u5927\u5927\u53f9\u670d\uff0c\u518d\u770b\u90a3\u7389\u9aa8\u9b54\u65f6\uff0c\u53c8\u662f\u4e00\u60ca\u2014\u2014 \u3000\u3000\u539f\u6765\u8fd9\u4e00\u56de\u513f\u529f\u592b\uff0c\u7389\u9aa8\u9b54\u7adf\u5728\u6240\u7acb\u4e4b\u5730\u4e00\u4e08\u5468\u56f4\u8fc5\u901f\u5730\u8d70\u4e86\u4e00\u5708\uff0c\u4e24\u8896\u6325\u5904\uff0c\u4f3c \u4e4e\u6709\u4e00\u4e9b\u6781\u7ec6\u7684\u7c89\u672b\u843d\u4e86\u4e0b\u6765\uff0c\u970e\u65f6\u4ed6\u6240\u7acb\u7684\u5730\u9762\u4e0a\u7684\u767d\u96ea\u4ece\u5916\u9762\u5f00\u59cb\u53d8\u8272\uff0c\u6e10\u6e10\u8513\u5ef6 \u5230\u4e2d\u5fc3\uff0c\u7389\u9aa8\u9b54\u8fc5\u901f\u5730\u5728\u7f2a\u4e03\u5a18\u53ca\u83c1\u513f\u53e3\u585e\u5927\u4e86\u4e00\u7c92\u4e38\u513f\u3002 \u3000\u3000\u4e0d\u6d88\u7247\u523b\uff0c\u5c31\u53d8\u6210\u4e86\u4e00\u4e2a\u76f4\u5f84\u4e00\u4e08\u7684\u7070\u8272\u96ea\u5708\uff0c\u800c\u7389\u9aa8\u9b54\u7b49\u4e09\u4eba\u5219\u5728\u5706\u5708\u4e2d\u5fc3\uff0c\u886c \u7740\u56db\u5468\u7684\uff0c\u8fd9\u7070\u9ed1\u8272\u7684\u5706\u5708\u5c31\u5982\u5728\u767d\u7eb8\u4e0a\u753b\u4e0a\u53bb\u4e00\u822c\u6e05\u6670\uff0c\u7389\u9aa8\u9b54\u90a3\u4e00\u88ad\u96ea\u767d\u7684\u8863\u886b\u4e5f \u5c31\u76ca\u53d1\u663e\u5f97\u53e4\u602a\u800c\u523a\u76ee\u3002 \u3000\u3000\u96ea\u82b1\u4ecd\u7136\u98d8\u7740\uff0c\u4f46\u4e00\u89e6\u53ca\u90a3\u7070\u8272\u5706\u5708\uff0c\u5c31\u5316\u6210\u4e86\u6c34\u3002 \u3000\u3000\u65e0\u6068\u751f\u7ecf\u8fc7\u4e00\u6b21\u5192\u9669\u5931\u8d25\uff0c\u5fc3\u4e2d\u53cd\u800c\u9759\u4e0b\u6765\uff0c\u51dd\u76ee\u601d\u7d22\u62a2\u6551\u59bb\u5973\u7684\u529e\u6cd5\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5ffd\u7136\u7f13\u7f13\u9053\uff1a\u201c\u8001\u592b\u8bc6\u5f97\u90a3\u7389\u9aa8\u9b54\u6240\u65bd\u4e4b\u6bd2\u4e43\u662f\u540d\u53eb\u2018\u900f\u9aa8\u65ad\u9b42\u7802\u2019\uff0c\u6240\u5e03 \u4e4b\u5730\uff0c\u4e09\u4e2a\u65f6\u8fb0\u4e4b\u5185\uff0c\u4eba\u755c\u8d70\u8fc7\uff0c\u4e0d\u8bba\u7a7f\u4e86\u591a\u539a\u7684\u978b\u9774\uff0c\u5fc5\u7136\u7acb\u523b\u4e2d\u6bd2\u8eab\u4ea1\uff0c\u53ea\u662f\u8fd9\u79cd \u6bd2\u7269\u6781\u96be\u914d\u5236\uff0c\u636e\u8bf4\u914d\u5236\u4e4b\u65b9\u5df2\u5931\u4e86\u4f20\uff0c\u4e0d\u77e5\u8fd9\u7389\u9aa8\u9b54\u600e\u4e48\u4f1a\u7684\uff1f\u201d \u3000\u3000\u8f9b\u6377\u672c\u5bf9\u65e0\u6068\u751f\u53ca\u7f2a\u4e03\u5a18\u6076\u611f\u91cd\u6781\uff0c\u4f46\u8fd9\u65f6\u88ab\u7389\u9aa8\u9b54\u64d2\u4f4f \u3000\u3000\u7684\uff0c\u6709\u4e00\u4e2a\u6b63\u662f\u90a3\u7f8e\u4e3d\u7684\u83c1\u513f\uff0c\u8fd9\u5c31\u4e0d\u540c\u4e86\uff0c\u662f\u4ee5\u4ed6\u4ecd\u5341\u5206\u6025\u4e8e\u6551\u4eba\u3002 \u3000\u3000\u8fd9\u65f6\u4e00\u542c\u6885\u53d4\u53d4\u8bf4\u51fa\u8fd9\u6bd2\u7802\u4e4b\u540d\uff0c\u5fc3\u4e2d\u5ffd\u7136\u4e00\u52a8\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u4e00\u58f0\u957f\u5578\uff0c\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u8eab\u5f62\u6709\u5982\u4e00\u53ea\u5927\u9a6c\u822c\u98de\u8fdb\u4e86\u7070\u5708\uff0c\u4eba\u5728\u7a7a\u4e2d\uff0c\u53f3 \u624b\u4e00\u7ffb\uff0c\u545b\u7136\u4e00\u58f0\uff0c\u957f\u5251\u5df2\u5230\u4e86\u624b\u4e2d\uff0c\u5bb4\u65f6\u5316\u6210\u4e00\u7247\u5149\u5f71\u5411\u9a84\u7acb\u5708\u4e2d\u7684\u7389\u9aa8\u9b54\u5934\u9876\u76d6\u4e0b\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u89c1\u90a3\u9752\u5e74\u4e00\u6643\u8eab\uff0c\u662f\u8fde\u4eba\u5e26\u5251\u5230\u4e86\u5934\u9876\uff0c\u5fc3\u4e2d\u4e0d\u7531\u4e00\u60ca\uff0c\u9f13\u8db3\u771f\u529b\uff0c\u547c\u547c\u4e24 \u53ea\u767d\u8896\u626b\u51fa\uff0c\u5de6\u521a\u53f3\u67d4\uff0c\u521a\u8005\u76f4\u51fb\u8f9b\u6377\u524d\u8179\uff0c\u67d4\u8005\u5219\u6697\u542b\u5203\u52b2\uff0c\u5377\u5411\u8f9b\u6377\u624b\u4e2d\u957f\u5251\u2014\u2014 \u3000\u3000\u8f9b\u6377\u6697\u563f\u4e00\u58f0\uff0c\u731b\u63d0\u771f\u6c14\uff0c\u78c1\u7684\u4e00\u58f0\uff0c\u4e00\u7f15\u5251\u6c14\u4ece\u5251\u5c16\u900f\u51fa\uff0c\u5251\u5149\u4e00\u531d\u5b9b\u5982\u957f\u86c7\u51fa \u6d1e\uff0c\u6b63\u662f\u201c\u866c\u679d\u5251\u6cd5\u201d\u7684\u7cbe\u5999\u7edd\u5b66\u201c\u6885\u5360\u5148\u6625\u201d\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u867d\u89c9\u8fd9\u9752\u5e74\u5251\u62db\u4e4b\u8be1\u5947\u5927\u51fa\u610f\u5916\uff0c\u4f46\u81ea\u5fd6\u529f\u529b\u6df1\u539a\uff0c\u4e24\u8896\u4e0a\u771f\u529b\u8d2f\u6ce8\uff0c\u4f9d\u7136 \u6709\u5982\u4e00\u53cc\u767d\u8272\u7684\u957f\u97ad\u822c\u5377\u5c06\u4e0a\u6765\u3002 \u3000\u3000\u7830\u7684\u4e00\u58f0\u66b4\u54cd\uff0c\u8f9b\u6377\u4e00\u4fa7\u8eab\u4e4b\u95f4\uff0c\u7adf\u4ece\u7389\u9aa8\u9b54\u5de6\u8896\u52b2\u9053\u4e2d\u6ed1\u4e86\u8fc7\u6765\uff0c\u957f\u5251\u4e00\u9886\uff0c \u201c\u6885\u5360\u5148\u6625\u201d\u5df2\u4f7f\u5230\u6781\u5904\uff0c\u53d1\u51fa\u66f4\u5f3a\u7684\u5251\u6c14\uff0c\u540c\u65f6\u5de6\u624b\u66f4\u4e58\u4e00\u843d\u52bf\u4e4b\u95ea\u7535\u822c\u6293\u5411\u5730\u4e0a\u7f2a \u4e03\u5a18\u53ca\u83c1\u513f\uff0c\u6562\u60c5\u4ed6\u6253\u7b97\u4ee5\u5251\u6c14\u786c\u8feb\u7389\u9aa8\u9b54\u9000\u540e\u800c\u4e58\u9699\u6551\u5730\u4e0a\u4e4b\u4eba\u3002 \u3000\u3000\u54ea\u77e5\u7389\u9aa8\u9b54\u5185\u52b2\u6df1\u539a\u5f97\u5f88\uff0c\u8f9b\u6377\u7684\u5251\u6c14\u523a\u4eba\u4ed6\u7684\u53f3\u624b\u8896\u888d\u7adf\u4f3c\u523a\u4eba\u4e00\u6bb5\u6781\u539a\u7684\u673d\u6728 \u4e4b\u4e2d\uff0c\u8f9b\u6377\u6697\u53eb\u4e00\u58f0\u4e0d\u5999\uff0c\u8155\u4e2d\u52b2\u9053\u4e00\u53d1\uff0c\u7389\u9aa8\u9b54\u7684\u8863\u8896\u7adf\u4e5f\u7a81\u7136\u5316\u67d4\u4e3a\u521a\uff0c\u67d4\u8f6f\u7684\u5e03 \u8896\u7acb\u523b\u633a\u76f4\u5982\u68cd\uff0c\u800c\u8f9b\u6377\u5251\u5c16\u7adf\u7136\u5982\u78b0\u91d1\u5c5e\uff0c\u53d1\u51fa\u53ee\u53ee\u54af\u54af\u7684\u8df3\u52a8\u4e4b\u58f0\u2014\u2014 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8f9b\u6377\u4e00\u5251\u6ca1\u80fd\u9707\u9000\u7389\u9aa8\u9b54\uff0c\u4e0b\u843d\u4e4b\u52bf\u5df2\u81f3\u6781\u5904\uff0c\u53cc\u8db3\u7acb\u523b\u5c31\u8981\u78b0 \u5730\uff0c\u800c\u8fd9\u4e00\u78b0\u5730\uff0c\u7acb\u523b\u5c31\u8981\u4e2d\u90a3\u201c\u900f\u9aa8\u65ad\u9b42\u7802\u201d\u7684\u5267\u6bd2\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4e00\u6025\u4e4b\u4e0b\uff0c\u53cc\u773c\u53d1\u8d64\uff0c\u5de6\u624b\u5ffd\u7136\u5212\u4e86\u9053\u534a\u5f27\uff0c\u70b9\u81f3\u7389\u9aa8\u9b54\u773c\u524d\u2014\u2014 \u3000\u3000\u7389\u9aa8\u9b54\u53ea\u89c9\u8fd9\u4e00\u6307\u597d\u4e0d\u98d8\u5ffd\uff0c\u4f3c\u624b\u81ea\u5df1\u65e0\u8bba\u4ece\u65b9\u4f4d\u90fd\u96be\u8eb2\u8fc7\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u4e5f\u5c31\u662f\u81ea \u5df1\u6bcf\u4e00\u4e2a\u8981\u7a74\u90fd\u4f3c\u5728\u8f9b\u6377\u8fd9\u4e00\u6307\u5a01\u80c1\u4e4b\u4e0b\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u65e2\u60ca\u53c8\u6012\uff0c\u60f3\u4e0d\u5230\u8fd9\u540e\u751f\u5c0f\u5b50\u7adf\u6709\u5982\u6b64\u795e\u5999\u62db\u5f0f\uff0c\u6025\u5207\u4e2d\u53ea\u5f97\u4e00\u4efd\u8896\u52b2\uff0c\u5012 \u9000\u534a\u6b65\u2014\u2014 \u3000\u3000\u800c\u8f9b\u6377\u5c31\u4e58\u8fd9\u4e00\u5239\u90a3\u95f4\u62bd\u51fa\u53d7\u56f0\u7684\u957f\u5251\uff0c\u6ce2\u7684\u4e00\u58f0\u63d2\u5165\u4e86\u7070\u8272\u7684\u5730\u4e2d\u2014\u2014 \u3000\u3000\u624b\u4e0a\u5374\u4e00\u501f\u52b2\uff0c\u7acb\u523b\u4e00\u4e2a\u5373\u5c06\u78b0\u5730\u7684\u8eab\u7eb5\u8d77\u4e86\u4e08\u4f59\uff0c\u4ed6\u4e5f\u987e\u4e0d\u5f97\u8fd8\u63d2\u5728\u5730\u4e0a\u7684\u957f\u5251\uff0c \u5168\u529b\u4e00\u62d4\u53cc\u81c2\uff0c\u8eab\u5b50\u5982\u4e00\u53ea\u7bad\u77e2\u4e00\u822c\u8dc3\u51fa\u4e86\u6bd2\u5708\u2014\u2014 \u3000\u3000\u7389\u9aa8\u9b54\u867d\u7136\u9707\u60ca\u4e8e\u8f9b\u6377\u7684\u6b66\u529f\uff0c\u4f46\u662f\u5634\u89d2\u4e0a\u4ecd\u9732\u51fa\u4e86\u4e00\u4e2a\u4f3c\u5f97\u610f\u4f3c\u9634\u9669\u7684\u5fae\u7b11\u3002 \u3000\u3000\u9664\u4e86\u5931\u53bb\u529f\u529b\u7684\u4e03\u5999\u795e\u541b\uff0c\u65e0\u6068\u751f\u548c\u8f9b\u6377\u90fd\u582a\u79f0\u5f53\u4eca\u5929\u4e0b\u7b2c\u4e00\u6d41\u9ad8\u624b\uff0c\u4f46\u56e0\u7389\u9aa8\u9b54 \u4e00\u8eab\u662f\u6bd2\uff0c\u7adf\u7136\u5948\u4f55\u4ed6\u4e0d\u5f97\u3002 \u3000\u3000\u4e09\u4eba\u90fd\u51b7\u9759\u5730\u601d\u7d22\u7740\u9664\u6bd2\u4e4b\u7b56\uff0c\u5c24\u5176\u662f\u65e0\u6781\u5c9b\u4e3b\u2014\u2014\u56e0\u4e3a\u53ea\u8981\u9664\u4e86\u6bd2\uff0c\u4ed6\u81ea\u4fe1\u5728\u4e24 \u767e\u62db\u4e4b\u5185\u5fc5\u80fd\u53d6\u7389\u9aa8\u9b54\u4e4b\u6027\u547d\uff0c\u6b64\u5916\u660f\u8ff7\u592a\u4e45\u59bb\u5973\u5bf9\u4ed6\u4e5f\u662f\u4e00\u5927\u5fc3\u7406\u4e4b\u5a01\u80c1\u3002 \u3000\u3000\u96ea\u82b1\u4ecd\u7136\u7eb7\u98de\u2014\u2014 \u3000\u3000\u84e6\u5730\uff0c\u576a\u7f18\u5761\u4e0b\u4f20\u6765\u4e00\u9635\u9707\u5929\u72c2\u7b11\u58f0\u2014\u2014 \u3000\u3000\u6f2b\u5929\u5927\u96ea\u4e0b\uff0c\u4e00\u4e2a\u8001\u6c49\u6b6a\u6b6a\u659c\u659c\u5730\u8d70\u4e86\u4e0a\u6765\uff0c\u4ed6\u62ab\u5934\u6563\u53d1\uff0c\u84ec\u5934\u57a2\u9762\uff0c\u90a3\u4e00\u8eab\u7f18\u8272 \u957f\u888d\u5df2\u810f\u5f97\u6709\u4e09\u5206\u6cb9\u57a2\u4e86\uff0c\u4e0d\u8fc7\u5982\u679c\u4f60\u4ed4\u7ec6\u89c2\u5bdf\uff0c\u5fc5\u80fd\u53d1\u73b0\u90a3\u888d\u4e43\u662f\u6781\u4e0a\u4e58\u7684\u4e1d\u68c9\u888d\uff0c \u4e0d\u8fc7\u88ab\u5f04\u5f97\u592a\u810f\u7f62\u4e86\u3002 \u3000\u3000\u8fd9\u8001\u6c49\u4e00\u8def\u8d70\uff0c\u4e00\u8def\u4ef0\u5929\u72c2\u7b11\uff0c\u53e3\u4e2d\u9ad8\u58f0\u541f\u9053\uff1a\u201c\u7231\u9493\u9c7c\u8001\u7fc1\u582a\u7b11\uff0c\u5b50\u72b9\u51bb\u5c06\u56de\u53bb \u4e86\uff0c\u5bd2\u6c5f\u600e\u751f\u72ec\u9493\uff01\u54c8\u54c8\u54c8\u54c8\uff0c\u771f\u597d\u7b11\uff0c\u597d\u7b11\uff0c\u54c8\u54c8\u2026\u2026\u201d\u58f0\u5e26\u94ff\u9535\u4e4b\u97f3\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u53c8\u6b6a\u6b6a\u659c\u659c\u8d70\u4e86\u51e0\u6b65\uff0c\u653e\u773c\u4e00\u671b\uff0c\u5929\u8272\u5411\u665a\uff0c\u5927\u5730\u660f\u660f\uff0c\u4e0d\u77e5\u600e\u5730\uff0c\u4f3c\u88ab\u89e6 \u52a8\u5fc3\u6000\u4e00\u822c\uff0c\u545c\u545c\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u90a3\u54ed\u58f0\u968f\u7740\u5bd2\u98ce\u65f6\u9ad8\u65f6\u4f4e\uff0c\u545c\u545c\u54bd\u54bd\uff0c\u663e\u89c1\u4ed6\u54ed\u5f97\u751a\u4e3a\u60b2\u5207\u3002 \u3000\u3000\u53c8\u8d70\u4e86\u51e0\u6b65\uff0c\u8001\u6c49\u6e10\u6e10\u6b62\u4f4f\u4e86\u54ed\u58f0\uff0c\u75f4\u75f4\u8d70\u4e86\u51e0\u6b65\uff0c\u5ffd\u7136\u54a6\u4e86\u4e00\u58f0\uff0c\u505c\u5728\u4e00\u68f5\u5927\u6885 \u6811\u524d\u9762\u2014\u2014 \u3000\u3000\u53ea\u89c1\u90a3\u5947\u810f\u7684\u8138\u4e0a\u73b0\u51fa\u60ca\u8bb6\u7684\u795e\u60c5\uff0c\u63a5\u7740\u4ed6\u5446\u7acb\u7740\u6c89\u601d\u8d77\u6765\u2014\u2014 \u3000\u3000\u6e10\u6e10\u4ed6\u4f3c\u4e4e\u60f3\u8d77\u6765\u4e86\uff0c\u8fd9\u5730\u65b9\uff0c\u8fd9\u5761\u513f\uff0c\u8fd9\u6811\uff0c\u662f\u591a\u4e48\u719f\u6089\u554a\uff0c\u5bf9\u4e86\uff0c\u6b63\u662f\u4ed6\u7ae5\u5e74 \u66fe\u6e38\u73a9\u7684\u5730\u65b9\uff0c\u8fd9\u6885\u6811\u8fd8\u662f\u4ed6\u4eb2\u624b\u683d\u7684\u54e9\u4ed6\u50cf\u662f\u7a81\u7136\u8bb0\u5fc6\u8d77\u6765\uff0c\u53d1\u72c2\u4f3c\u7684\u5411\u5927\u6811\u6251\u4e86\u4e0a \u53bb\uff0c\u4ed6\u683d\u79cd\u65f6\u5c1a\u53ea\u6709\u5bf8\u7c97\u7684\u5e7c\u82d7\uff0c\u73b0\u5728\u7adf\u5408\u56f4\u4e86\uff0c\u6811\u5e72\u4e0a\u76d8\u9519\u4ea4\u52a0\u7684\u543c\u679d\uff0c\u66f4\u589e\u52a0\u4e86\u4e00 \u79cd\u529b\u7684\u7f8e\u2014\u2014 \u3000\u3000\u4ed6\u62b1\u4f4f\u5927\u6811\uff0c\u50cf\u662f\u4ece\u90a3\u9519\u6742\u7684\u76d8\u679d\u4e0a\u627e\u5230\u4e86\u5931\u53bb\u7684\u9752\u6625\u5c81\u6708\uff0c\u5ffd\u7136\uff0c\u4ed6\u53c8\u53f7\u9676\u5927\u54ed \u8d77\u6765\u3002 \u3000\u3000\u4ed6\u65ad\u65ad\u7eed\u7eed\u5730\u5636\u7740\uff1a\u201c\u6811\u72b9\u5982\u6b64\uff0c\u4eba\u4f55\u4ee5\u582a\uff1f\u6811\u72b9\u5982\u6b64\uff0c\u4eba\u4f55\u4ee5\u582a\u2026\u2026\u201d \u3000\u3000\u90a3\u54ed\u58f0\u5982\u675c\u9e43\u6ce3\u8840\uff0c\u53c8\u5982\u5deb\u5ce1\u733f\u557c\uff0c\u5728\u6d51\u7136\u96ea\u5929\u4e2d\uff0c\u65f6\u9ad8\u65f6\u4f4e\u4f20\u51fa\u53bb\u2014\u2014 \u3000\u3000\u576a\u4e0a\u7684\u65e0\u6781\u5c9b\u4e3b\u3001\u6885\u5c71\u6c11\u3001\u8f9b\u6377\uff0c\u751a\u81f3\u7389\u9aa8\u9b54\u90fd\u5fcd\u4e0d\u4f4f\u56de\u9996\u4e00\u770b\uff0c\u4f46\u53ea\u80fd\u89c1\u8fdc\u5904\u4e00 \u4e2a\u4eba\u53c8\u54ed\u53c8\u7b11\uff0c\u6b6a\u6b6a\u5012\u5012\u5730\u8d70\u4e0a\u576a\u6765\uff0c\u5fc3\u4e2d\u90fd\u6697\u54fc\u4e00\u58f0\uff1a\u201c\u75af\u5b50\uff01\u201d\u53c8\u5404\u81ea\u6f5c\u5fc3\u601d\u7d22\u5982 \u4f55\u6765\u6253\u7834\u8fd9\u5371\u9669\u7684\u50f5\u5c40\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u89c1\u81ea\u5df1\u7565\u65bd\u6bd2\u5668\uff0c\u5c31\u628a\u4e24\u4e2a\u9ad8\u624b\u96be\u4f4f\uff0c\u5176\u4e2d\u751a\u81f3\u5305\u62ec\u4e16\u5916\u4e09\u4ed9\u7684\u65e0\u6068\u751f\uff0c\u5fc3 \u4e2d\u4e0d\u7981\u4e00\u9635\u5f97\u610f\uff0c\u626c\u58f0\u9053\uff1a\u201c\u65e0\u6068\u751f\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u7b54\u5141\u4e86\u5427\uff01\u563f\u563f\uff0c\u8bba\u6b66\u529f\uff0c\u6211\u7389\u9aa8\u9b54 \u6bd4\u4f60\u4e16\u5916\u4e09\u4ed9\u5dee\uff0c \u3000\u3000\u8bba\u6bd2\uff0c\u54c8\u54c8\uff0c\u6211\u7389\u9aa8\u9b54\u4e0d\u5ba2\u6c14\u8981\u79f0\u4e00\u58f0\u4e3e\u4e16\u65e0\u53cc\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u54fc\u4e86\u4e00\u58f0\uff0c\u6562\u60c5\u4ed6\u5fc3\u4e2d\u5bf9\u7389\u9aa8\u9b54\u8fd9\u51e0\u53e5\u8bdd\u5012\u4e5f\u771f\u4ee5\u4e3a\u7136\u3002 \u3000\u3000\u90a3\u77e5\u7389\u9aa8\u9b54\u4e3e\u4e16\u65e0\u53cc\u7684\u201c\u53cc\u201d\u5b57\u624d\u51fa\u53e3 \u3000\u3000\u7a81\u7136\u4e00\u58f0\u6781\u4e3a\u94ff\u9535\u7684\u58f0\u97f3\u4f20\u4e86\u8fc7\u6765\uff1a \u3000\u3000\u201c\u4ec0\u4e48\u4eba\u6562\u5938\u5982\u6b64\u5927\u53e3\uff1f\u201d \u3000\u3000\u6240\u6709\u7684\u4eba\u4e00\u9f50\u56de\u9996\u770b\u53bb\uff0c\u53ea\u89c1\u6765\u4eba\u6b63\u662f\u90a3\u4e2a\u75af\u5b50\uff01\u5927\u5bb6\u5fc3\u4e2d\u90fd\u60f3\uff1a\u201c\u8fd9\u53ae\u65b9\u624d\u53c8\u54ed \u53c8\u95f9\uff0c\u5b8c\u5168\u662f\u4e2a\u75af\u5b50\uff0c\u4f46\u6b64\u523b\u5374\u53c8\u4e0d\u50cf\u4e2a\u75af\u5b50\u4e86\uff01\u201d \u3000\u3000\u8f9b\u6377\u548c\u6885\u5c71\u6c11\u90fd\u89c9\u6b64\u4eba\u597d\u9762\u719f\uff0c\u4f46\u8ddd\u79bb\u751a\u8fdc\uff0c\u53c8\u9ed1\u53c8\u6697\uff0c\u90fd\u6ca1\u6709\u770b\u51fa\u771f\u76f8\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u6b63\u5728\u81ea\u9e23\u5f97\u610f\uff0c\u7a81\u7136\u88ab\u4eba\u559d\u65ad\uff0c\u81ea\u7136\u5927\u6012\uff0c\u559d\u9053\uff1a\u201c\u4f55\u65b9\u6751\u592b\u7adf\u5230\u8fd9\u91cc\u6765\u6492 \u91ce\uff0c\u5feb\u62a5\u4e0a\u4f60\u732a\u540d\u72d7\u59d3\u2014\u2014\u201d \u3000\u3000\u90a3\u8001\u6c49\u845b\u7136\u6602\u9996\uff0c\u660f\u6697\u4e2d\u53ef\u89c1\u4e00\u53cc\u9635\u5b50\u7cbe\u5149\u66b4\u5c04\uff0c\u8f9b\u6377\u5fc3\u4e2d\u4e00\u52a8\uff0c\u6b63\u5f85\u5f00\u53e3\uff0c\u90a3\u4eba \u7a81\u7136\u820c\u7efd\u6625\u96f7\u5927\u559d\u4e00\u58f0\uff1a \u3000\u3000\u201c\u8001\u592b\u59d3\u91d1\uff0c\u8349\u5b57\u4e00\u9e4f\uff01\u201d \u3000\u3000\u6b64\u65f6\u5468\u906d\u9759\u6781\uff0c\u6b64\u94ff\u9535\u5982\u91d1\u94b1\u4e4b\u58f0\u5728\u5bd2\u51bb\u7684\u7a7a\u6c14\u4e2d\u4f20\u51fa\u8001\u8fdc\u3002 \u3000\u3000\u8f9b\u6377\u548c\u6885\u5c71\u6c11\u4e00\u542c\uff0c\u5fc3\u4e2d\u604d\u7136\u5927\u609f\uff0c\u7389\u9aa8\u9b54\u548c\u65e0\u6068\u751f\u5374\u65e0\u52a8\u4e8e\u8877\uff0c\u663e\u7136\u4ed6\u4eec\u4e45\u5c45\u6d77 \u5916\uff0c\u4e0d\u77e5\u91d1\u4e00\u9e4f\u4e4b\u540d\u3002 \u3000\u3000\u8fd9\u65f6\u91d1\u4e00\u9e4f\u4f3c\u4e4e\u795e\u667a\u6e05\u9192\uff0c\u4e1d\u6beb\u4e0d\u75af\uff0c\u7f13\u6b65\u8d70\u4e86\u8fc7\u53bb\uff0c\u90a3\u6cb9\u57a2\u7d2f\u7d2f\u7684\u8138\u4e0a\uff0c\u4f9d\u7136\u53ef \u4ee5\u770b\u51fa\u5de6\u5634\u89d2\u4e0a\u5e26\u7740\u4e00\u4e2a\u4e0d\u5c51\u7684\u51b7\u7b11\u3002 \u3000\u3000\u4ed6\u7ecf\u8fc7\u8f9b\u6377\u9762\u524d\uff0c\u773c\u5149\u5411\u8f9b\u6377\u8198\u4e86\u4e00\u773c\uff0c\u4f3c\u4e4e\u5728\u8bf4\uff1a\u201c\u5c0f\u5b50\u4f60\u4e5f\u6765\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u5012\u90fd\u88ab\u4ed6\u7684\u5947\u5f02\u4e3e\u52a8\u5f04\u5f97\u7cca\u6d82\uff0c\u53ea\u89c1\u4ed6\u7f13\u7f13\u8d70\u5411\u7389\u9aa8\u9b54\uff0c\u795e\u60c5\u751a\u662f\u5014\u50b2\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u5012\u4e5f\u4e0d\u77e5\u9053\u4ed6\u662f\u4ec0\u4e48\u6765\u5386\uff0c\u4e0d\u7981\u5012\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u51dd\u795e\u4ee5\u5f85\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u8d70\u5230\u201c\u900f\u9aa8\u65ad\u9b42\u7802\u201d\u7684\u7070\u5708\u65c1\uff0c\u77a7\u90fd\u4e0d\u77a7\u4e00\u773c\uff0c\u5766\u7136\u8d70\u4e86\u8fdb\u53bb\u2014\u2014 \u3000\u3000\u4f17\u4eba\u8fd9\u624d\u53d1\u73b0\u4ed6\u4e00\u8def\u8d70\u6765\uff0c\u96ea\u5730\u4e0a\u8fde\u4e00\u4e2a\u8db3\u8ff9\u90fd\u6ca1\u6709\u7559\u4e0b\uff0c\u8fde\u65e0\u6068\u751f\u4e5f\u4e0d\u7981\u6697\u60ca\u9053\uff1a \u3000\u3000\u201c\u6b64\u53ae\u770b\u6765\u529f\u529b\u53c8\u81ea\u4e0d\u5f31\uff0c\u4ee5\u524d\u4ee5\u4e3a\u4e2d\u539f\u65e0\u80fd\u4eba\uff0c\u770b\u6765\u4e0e\u4e8b\u5b9e\u4e0d\u7b26\u7684\u4e86\u3002\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u76f4\u8d70\u5230\u8ddd\u7389\u9aa8\u9b54\u4e0d\u53ca\u4e09\u6b65\uff0c\u624d\u505c\u4e0b\u6765\u51b7\u51b7\u95ee\u9053\uff1a\u201c\u542c\u4f60\u81ea\u5938\u6bd2\u5668\u5929\u4e0b\u65e0\u53cc\uff0c \u54c8\u54c8\uff0c\u4ffa\u8001\u513f\u7b2c\u4e00\u4e2a\u4e0d\u670d\u6c14\u2014\u2014\u201d \u3000\u3000\u7389\u9aa8\u9b54\u5148\u8fd8\u4ee5\u4e3a\u4ed6\u6709\u522b\u7684\u6765\u5934\uff0c\u8fd9\u65f6\u89c1\u4ed6\u7adf\u4f3c\u8981\u4e0e\u81ea\u5df1\u4e00\u8f83\u6bd2\u672f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u5b89\uff0c \u6697\u9053\u4f60\u662f\u627e\u6b7b\u3002 \u3000\u3000\u4e8e\u662f\u7389\u9aa8\u9b54\u4e5f\u8fd8\u4ee5\u51b7\u7b11\u9053\uff1a\u201c\u6211\u7389\u9aa8\u9b54\u4e0d\u9519\u8bf4\u4e86\u8fd9\u53e5\u8bdd\uff0c\u4f60\u4e0d\u670d\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u4ef0\u5929\u72c2\u7b11\uff0c\u53cc\u773c\u5411\u5929\uff0c\u6839\u672c\u4e0d\u7406\u4f1a\u4ed6\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u4e0d\u7981\u52c3\u7136\u5927\u6012\uff0c\u53f1\u9053\uff1a\u201c\u6211\u7389\u9aa8\u9b54\u8db3\u8ff9\u884c\u904d\u6d77\u5916\u7a77\u5c9b\u50fb\u91ce\uff0c\u90a3\u4e00\u79cd\u5947\u6bd2\u91ce\u8349 \u6ca1\u6709\u89c1\u8fc7\uff0c\u4f60\u4eec\u4e2d\u539f\u8fd9\u7b49\u4e95\u5e95\u4e4b\u86d9\u61c2\u5f97\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u95fb\u8a00\u4e00\u6014\uff0c\u6ca1\u6709\u7b54\u8bdd\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u4ee5\u4e3a\u4ed6\u88ab\u81ea\u5df1\u62a5\u51fa\u4e07\u513f\u5413\u5f97\u5446\u4e86\uff0c\u4e0d\u7981\u5927\u4e3a\u5f97\u610f\u3002\u90a3\u77e5\u91d1\u4e00\u9e4f\u9053\uff1a\u201c\u6211\u6b63\u5947 \u602a\u600e\u4e48\u8fd8\u6709\u4eba\u6562\u5728\u6211\u91d1\u4e00\u9e4f\u9762\u524d\u73ed\u95e8\u5f04\u65a7\uff0c\u539f\u6765\u9601\u4e0b\u662f\u86ee\u5937\u4e4b\u65cf\uff0c\u90a3\u5c31\u96be\u602a\u4e86\uff0c\u54c8\u54c8\u2014 \u2014\u201d \u3000\u3000\u7389\u9aa8\u9b54\u6012\u559d\u4e00\u58f0\uff0c\u767d\u8896\u6325\u5904\uff0c\u4e00\u7247\u5f69\u8272\u70df\u96fe\u5411\u91d1\u4e00\u9e4f\u9762\u4e0a\u55b7\u6765\u2014\u2014 \u3000\u3000\u91d1\u4e00\u9e4f\u7acb\u5730\u8ddd\u4ed6\u4e0d\u53ca\u4e09\u6b65\uff0c\u8fd9\u4e00\u7247\u6bd2\u96fe\u5c06\u5468\u5706\u4e94\u5c3a\u7684\u7a7a\u95f4\u5b8c\u5168\u7f69\u4eba\uff0c\u91d1\u4e00\u9e4f\u7edd\u96be\u9003 \u51fa\uff0c\u8fde\u65e0\u6068\u751f\u90fd\u4e0d\u7981\u54fc\u4e86\u4e00\u58f0\u2014\u2014 \u3000\u3000\u90a3\u77e5\u91d1\u4e00\u9e4f\u6602\u7136\u633a\u7acb\uff0c\u5ffd\u7136\u4ef0\u9996\u5bf9\u7a7a\u6df1\u5438\uff0c\u5c06\u4e00\u7247\u5f69\u8272\u5947\u6bd2\u7684\u70df\u96fe\u5c3d\u91cf\u5438\u4eba\u8179\u4e2d\uff01 \u3000\u3000\u7389\u9aa8\u9b54\u53c8\u60ca\u53c8\u6012\uff0c\u4f46\u4e00\u79cd\u4e89\u80dc\u4e4b\u5fc3\u6cb9\u7136\u800c\u8d77\uff0c\u4ed6\u81ea\u8896\u4e00\u6325\uff0c\u55d4\u7136\u559d\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c \u7b97\u4f60\u6709\u51e0\u5206\u529f\u592b\uff0c\u4f60\u53ef\u6562\u4e0e\u6211\u7389\u9aa8\u9b54\u518d\u8d4c\u547d\u4e00\u6b21\uff1f\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u54c8\u54c8\u72c2\u7b11\uff0c\u5e76\u4e0d\u56de\u7b54\u3002\u5ffd\u7136\u5411\u5708\u5916\u7684\u8f9b\u6377\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u4e0e\u6211\u628a\u90a3\u58f6\u9152\u62ff\u6765 \u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u6307\u5411\u6885\u5c71\u6c11\u6cbd\u6765\u7684\u90a3\u4e00\u58f6\u201c\u6885\u5b50\u9999\u201d\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u77e5\u4ed6\u8981\u505a\u4ec0\u4e48\uff0c\u4f46\u4ecd\u8d77\u8eab\u5c06\u90a3\u58f6\u9152\u62ff\u8d77\uff0c\u53ea\u89c1\u58f6\u4e0b\u4e4b\u706b\u867d\u7136\u65e9\u7184\uff0c\u4f46\u58f6\u5e95\u5c1a \u6e29\uff0c\u4ed6\u53eb\u4e86\u4e00\u58f0\uff1a \u3000\u3000\u201c\u91d1\u8001\u524d\u8f88\uff0c\u9152\u6765\u4e86\uff01\u201d \u3000\u3000\u5355\u624b\u4e00\u9001\uff0c\u9152\u58f6\u5e73\u5e73\u7a33\u5730\u4ece\u4e03\u4e08\u5f00\u5916\u98de\u4e86\u8fc7\u6765\uff0c\u6562\u60c5\u4ed6\u662f\u4e0d\u6562\u8d70\u8fd1\u90a3\u6bd2\u5708\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u5934\u90fd\u4e0d\u56de\uff0c\u4e00\u62db\u5c31\u5c06\u9152\u58f6\u63a5\u4f4f\uff0c\u800c\u4e14\u5c31\u50cf\u80cc\u540e\u751f\u773c\u4e00\u6837\uff0c\u6b63\u597d\u63e1\u4f4f\u9152\u58f6\u7684\u58f6 \u67c4\uff0c\u4e00\u6ef4\u90fd\u6ca1\u6709\u6ef4\u51fa\u3002 \u3000\u3000\u91d1\u4e00\u9e4f\u5c1a\u672a\u5f00\u53e3\uff0c\u7389\u9aa8\u9b54\u5df2\u5148\u62a2\u7740\u8bf4\uff1a\u201c\u6b63\u5408\u6211\u5fc3\u610f\uff0c\u8001\u5339\u592b\u6562\u996e\u6211\u4e00\u676f\u9152\u4e48\uff1f\u201d \u3000\u3000\u91d1\u4e00\u9e4f\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u6562\uff1f\u201d\u4f38\u624b\u5c06\u9152\u58f6\u9012\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u63a5\u8fc7\uff0c\u5c06\u58f6\u76d6\u53d6\u4e0b\uff0c\u53cd\u8fc7\u6765\u5c31\u50cf\u4e00\u53ea\u9152\u76cf\u4e00\u822c\uff0c\u4e3e\u58f6\u5012\u4e86\u4e00\u4e9b\u9152\uff0c\u5374\u5c06\u5de6\u624b \u6307\u7532\u4e00\u5f39\uff0c\u4f9d\u7a00\u53ef\u89c1\u4e00\u4e9b\u7c89\u672b\u5f39\u5165\u9152\u4e2d\u3002 \u3000\u3000\u4ed6\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u544a\u8bc9\u4f60\u4e5f\u4e0d\u59a8\uff0c\u6211\u8fd9\u9152\u4e2d\u4e0b\u6709\u2018\u7acb\u6b65\u65ad\u80a0\u2019\uff0c\u4f60\u82e5\u4e0d\u6562\u559d\u4e0b\uff0c\u73b0 \u5728\u6c42\u9976\u8fd8\u6765\u5f97\u53ca\u2014\u2014\u201d \u3000\u3000\u573a\u5916\u7684\u6885\u5c71\u6c11\u548c\u65e0\u6068\u751f\u4e00\u542c\u51e0\u4e4e\u60ca\u8d77\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u2018\u7acb\u6b65\u65ad\u80a0\u2019\u4e43\u662f\u4e16\u4e0a\u6700\u6bd2\u7684\u4e00 \u79cd\u836f\u7269\uff0c\u996e\u540e\u4e0d\u6d88\u4e00\u7728\u773c\u5de5\u592b\uff0c\u7acb\u523b\u7a7f\u80a0\u800c\u4ea1\uff0c\u4e0d\u77e5\u7389\u9aa8\u9b54\u4f55\u5904\u5f04\u6765\u7684\uff0c\u90a3\u91d1\u4e00\u9e4f\u5c82\u80fd \u670d\u4e0b\u2014\u2014\u201d \u3000\u3000\u54ea\u77e5\u91d1\u4e00\u9e4f\u66f4\u4e0d\u6253\u8bdd\uff0c\u63a5\u8fc7\u4e00\u53e3\u5c31\u996e\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u201c\u4f60\u4e5f\u6562\u996e\u6211\u4e00\u676f\u4e48\uff1f\u201d\u8bf4\u7740\u63a5\u8fc7\u58f6\u4e5f\u5728\u58f6\u76d6\u4e2d\u6ce8\u4e86\u4e00\u76cf\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u4ed4\u7ec6\u89c2\u5bdf\u4ed6\u624b\u6307\u8fde\u9152\u90fd\u6ca1\u6709\u78b0\u4e00\u4e0b\uff0c\u6839\u672c\u6ca1\u6709\u4e0b\u6bd2\u7684\u52a8\u4f5c\uff0c\u5fc3\u60f3\u5c31\u7b97\u6709\u6bd2\uff0c \u6211\u9884\u670d\u4e0b\u7684\u201c\u767e\u6bd2\u9f99\u6d8e\u201d\u4ec0\u4e48\u6bd2\u4e0d\u80fd\u89e3\uff0c\u559d\u53c8\u4f55\u59a8\uff1f \u3000\u3000\u5f53\u4e0b\u63a5\u8fc7\u58f6\u76d6\uff0c\u4e5f\u4e00\u53e3\u996e\u4e0b\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u529d\u8001\u5339\u592b\u8fd8\u662f\u5feb\u53bb\u529e\u540e\u4e8b\u2014\u2014\u201d \u3000\u3000\u8bdd\u672b\u8bf4\u5b8c\uff0c\u5ffd\u7136\u72c2\u53eb\u4e00\u58f0\uff0c\u7adf\u81ea\u5012\u5730\uff0c\u53cc\u811a\u4e00\u9635\u4e71\u8e22\uff0c\u4fbf\u505c\u4e0b\u4e0d\u52a8\u4e86\uff01 \u3000\u3000\u91d1\u4e00\u9e4f\u5374\u51b7\u51b7\u4e00\u6652\uff0c\u7f13\u6b65\u8d70\u51fa\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u53bb\u4e86\u3002 \u3000\u3000\u4e8b\u51fa\u7a81\u7136\uff0c\u65e0\u6068\u751f\u60ca\u5f97\u53e3\u5446\u76ee\u77aa\uff0c\u4ed6\u7d20\u4fe1\u7cbe\u901a\u767e\u6bd2\u7684\u7389\u9aa8\u9b54\uff0c\u7adf\u88ab\u4eba\u5bb6\u4ee5\u6bd2\u5236\u6bd2\u5730 \u6bd9\u4e86\uff0c\u201c\u4e2d\u539f\u65e0\u4eba\u201d\u8fd9\u53e5\u72c2\u8bdd\u518d\u4e5f\u8bf4\u4e0d\u51fa\u5634\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u548c\u6885\u5c71\u6c11\u5374\u662f\u6df1\u77e5\u91d1\u4e00\u9e4f\u4e43\u662f\u5f04\u6bd2\u7684\u7956\u5b97\uff0c\u7389\u9aa8\u9b54\u81ea\u8981\u901e\u5f3a\uff0c\u5f53\u7136\u4e0d\u662f\u5bf9\u624b\u4e86\u3002 \u3000\u3000\u8981\u77e5\u6bd2\u541b\u91d1\u4e00\u9e4f\u4e43\u662f\u5343\u5e74\u96be\u9047\u7684\u5927\u602a\u7269\uff0c\u5bf9\u5404\u79cd\u201c\u6bd2\u201d\u7684\u7814\u7a76\u9020\u8be3\uff0c\u867d\u4e0d\u662f\u7edd\u540e\uff0c \u4f46\u81f3\u5c11\u662f\u7a7a\u524d\u4e86\uff0c\u6b63\u56e0\u4e3a\u4ed6\u7ec8\u65e5\u4e0e\u6bd2\u4e3a\u4f0d\uff0c\u6027\u60c5\u4e5f\u66f4\u53d8\u5f97\u53e4\u602a\uff0c\u6240\u4ee5\u624d\u5f97\u4e86\u201c\u6bd2\u541b\u201d\u7684 \u540d\u53f7\u3002\u8fd1\u5e74\u4ed6\u795e\u667a\u5931\u5e38\uff0c\u867d\u7136\u4e00\u65b9\u9762\u662f\u5fc3\u60c5\u906d\u53d8\uff0c\u4f46\u4e3b\u8981\u8fd8\u662f\u56e0\u4e3a\u7ec8\u65e5\u7f6e\u8eab\u6bd2\u4e2d\uff0c\u8eab\u4f53 \u5df2\u88ab\u6bd2\u7d20\u6df1\u6df1\u6d78\u5165\uff0c\u4ed7\u7740\u5404\u79cd\u6bd2\u7684\u76f8\u514b\u76f8\u751f\u4e4b\u7406\uff0c\u751f\u547d\u867d\u4fdd\u5143\u865e\uff0c\u4f46\u795e\u7ecf\u4e2d\u6d78\u5165\u6bd2\u7d20\uff0c \u5c31\u663e\u5f97\u795e\u7ecf\u5931\u5e38\u4e86\uff0c\u4f46\u4e5f\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u4ed6\u8840\u6db2\u4e2d\u81ea\u7136\u751f\u51fa\u4e86\u6297\u62d2\u767e\u6bd2\u7684\u7279\u6027\uff0c\u5bf9\u4e00\u4e9b\u5916 \u6765\u7684\u6bd2\u7d20\u5df2\u505a\u5230\u4e0d\u6d78\u4e0d\u8d25\u7684\u5730\u6b65\u4e86\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u7684\u201c\u7acb\u6b65\u65ad\u80a0\u201d\u867d\u662f\u7f55\u4e16\u5947\u6bd2\uff0c\u4f46\u5c82\u80fd\u5948\u4f55\u8fd9\u4f4d\u6bd2\u541b\uff1f\u800c\u91d1\u4e00\u9e4f\u7565\u4f7f\u201c\u65e0\u5f71 \u4e4b\u6bd2\u201d\uff0c\u5c31\u4ee4\u4ed6\u7cca\u91cc\u7cca\u6d82\u7684\u9001\u4e86\u8001\u547d\u3002 \u3000\u3000\u65e0\u6068\u751f\u5fc3\u4e2d\u53c8\u60ca\u53c8\u559c\uff0c\u8eab\u5f62\u4e00\u6643\uff0c\u5df2\u98de\u8eab\u8fdb\u5165\u6bd2\u5708\uff0c\u5927\u8896\u4e00\u6325\uff0c\u5df1\u628a\u5730\u4e0a\u4e24\u4e2a\u8eab\u8eaf \u631f\u5728\u814b\u4e0b\uff0c\u773c\u770b\u8eab\u4f53\u5373\u5c06\u4e0b\u843d\uff0c\u5ffd\u5730\u53cc\u8db3\u4e00\u8361\uff0c\u8eab\u8eaf\u7adf\u501f\u8fd9\u4e00\u8361\u4e4b\u529b\u5012\u98de\u51fa\u5708\u3002 \u3000\u3000\u6b64\u65f6\u4ed6\u8eab\u4e0a\u8fd8\u631f\u6709\u4e24\u4eba\uff0c\u5c45\u7136\u4e0d\u7528\u4ee5\u8db3\u501f\u5730\uff0c\u5e76\u4e14\u662f\u6539\u53d8\u65b9\u5411\u5012\u98de\u51fa\u6765\uff0c\u8fd9\u79cd\u8f7b\u529f\uff0c \u771f\u5230\u4e86\u7089\u706b\u7eaf\u9752\u7684\u5883\u754c\u4e86\uff01 \u3000\u3000\u8f9b\u6377\u4e5f\u4e0d\u7981\u8dc3\u4e0a\u524d\u53bb\uff0c\u7ec6\u770b\u90a3\u83c1\u513f\u53ca\u7f15\u4e03\u5a18\u65f6\uff0c\u867d\u5219\u9762\u8272\u7a0d\u5e26\u6a35\u7cb9\uff0c\u4f46\u6c14\u6001\u5b89\u7965\uff0c \u5b9b\u5982\u719f\u7761\u4e00\u822c\uff0c\u5fc3\u77e5\u7389\u9aa8\u9b54\u5e76\u4e0d\u6562\u6298\u78e8\u5979\u4eec\u3002 \u3000\u3000\u65e0\u6068\u751f\u4f38\u624b\u5728\u4e24\u4eba\u80c1\u4e0b\u4e00\u62cd\u4e00\u63c9\uff0c\u4e24\u4eba\u7acb\u523b\u8f6c\u9192\uff0c\u800c\u4e14\u5e76\u65e0\u4e2d\u6bd2\u73b0\u8c61\uff0c\u663e\u7136\u7389\u9aa8\u9b54 \u5148\u5c06\u89e3\u836f\u653e\u5927\u4e8c\u4eba\u53e3\u4e2d\uff0c\u662f\u4ee5\u867d\u7136\u8eba\u5728\u6bd2\u5708\u5185\uff0c\u5e76\u672a\u53d7\u5230\u4fb5\u5bb3\u3002 \u3000\u3000\u83c1\u513f\u4e00\u8f6c\u9192\uff0c\u7741\u5f00\u4e00\u5bf9\u7f8e\u76ee\uff0c\u5927\u773c\u73e0\u8f6c\u4e86\u4e24\u8f6c\uff0c\u9996\u5148\u770b\u89c1\u7684\u662f\u7236\u4eb2\u6148\u7965\u5730\u4fef\u89c6\u7740\u81ea \u5df1\uff0c\u5979\u53eb\u4e86\u4e00\u58f0\uff1a\u201c\u7239\uff01\u201d\u5c31\u6251\u5728\u65e0\u6068\u751f\u6000\u4e2d\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u7b14\u8005\u81f3\u6b64\u4e14\u5c06\u65e0\u6068\u751f\u6d77\u4e0a\u9047\u96be\u540e\u7684\u7ecf\u8fc7\u8865\u53d9\u4e00\u904d\u2014\u2014 \u3000\u3000\u65e0\u6068\u751f\u88ab\u5de8\u6d6a\u51b2\u4eba\u832b\u832b\u5927\u6d77\uff0c\u867d\u7136\u4e0d\u505c\u7684\u4e0b\u6c89\uff0c\u4f46\u662f\u4ecd\u88ab\u65e0\u6068\u751f\u4ee5\u7edd\u9876\u8f7b\u529f\u6293\u4f4f\u5757 \u8239\u677f\uff0c\u968f\u6d6a\u98d8\u6d41\uff0c\u7b49\u5230\u66b4\u98ce\u96e8\u8fc7\u53bb\u4e4b\u540e\uff0c\u4ed6\u7adf\u88ab\u98d8\u5230\u5927\u9646\u6cbf\u6d77\u7684\u6d59\u6c5f\u6c99\u6ee9\u2014\u2014 \u3000\u3000\u65e0\u6068\u751f\u62fc\u7740\u4f59\u529b\u722c\u4e0a\u4e00\u5ea7\u5c0f\u5cf0\uff0c\u6781\u76ee\u8fdc\u773a\uff0c\u53ea\u89c1\u6d77\u4e0a\u5df2\u662f\u4e00\u7247\u98ce\u5e73\u6d6a\u9759\uff0c\u660e\u5a9a\u7684\u65e5 \u5149\u7167\u8000\u7740\uff0c\u95ea\u95ea\u7684\u5149\u70b9\u5728\u6ce2\u5c16\u513f\u4e0a\u8df3\u8dc3\uff0c\u4f46\u662f\uff0c\u90a3\u91cc\u8fd8\u6709\u90a3\u6bb5\u8239\u7684\u8e2a\u5f71\uff0c\u4e0d\u6d88\u8bf4\uff0c\u7f2a\u4e03 \u5a18\u548c\u83c1\u513f\u90fd\u846c\u8eab\u6d77\u5e95\u4e86\u2014\u2014 \u3000\u3000\u65e0\u6068\u751f\u5df2\u7ec3\u5230\u4e0d\u574f\u7684\u5883\u754c\uff0c\u4ecd\u5077\u5077\u6d12\u4e0b\u4e86\u51e0\u6ef4\u773c\u6cea\uff0c\u6cea\u73e0\u513f\u6ef4\u5728\u672c\u6765\u5c31\u900f\u6e7f\u7684\u8863\u895f \u4e0a\uff0c\u4e1d\u6beb\u6ca1\u6709\u611f\u89c9\uff0c\u7a81\u7136\u4ed6\u77aa\u7740\u773c\uff0c\u52d2\u8155\u9ad8\u6b4c\uff1a\u201c\u675c\u9e43\u8fd8\u77e5\u5982\u8bb8\u6068\uff0c\u6599\u4e0d\u557c\u6e05\u6cea\uff0c\u957f\u557c \u8840\uff01\u201d \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u6028\u6012\u90a3\u4e2a\u201c\u4e03\u5999\u795e\u541b\u201d\uff0c\u53ef\u60dc\uff0c\u201c\u4e03\u5999\u795e\u541b\u201d\u4e5f\u5df2\u846c\u8eab\u6d6a\u6d9b\u4e86\uff01 \u3000\u3000\u4ed6\u662f\u7edd\u671b\u4e86\uff0c\u6d3b\u5728\u4e16\u4e0a\u7a7a\u6709\u4e00\u8eab\u7edd\u4e16\u795e\u529f\u53c8\u6709\u4ec0\u4e48\u7528\u5904\uff1f\u4ed6\u4e0d\u996e\u4e0d\u98df\uff0c\u5728\u5c71\u5cf0\u4e0a\u8eba \u4e86\u4e24\u5929\u4e24\u591c\u3002 \u3000\u3000\u7b2c\u4e09\u5929\uff0c\u4e00\u4e2a\u5ff5\u5934\u5ffd\u7136\u95ea\u8fc7\u4ed6\u7684\u8111\u6d77\uff0c\u4ed6\u60f3\u5230\u5982\u679c\u4ed6\u6b7b\u4e86\uff0c\u90a3\u4e48\u8fd9\u4e00\u8eab\u6b66\u6280\u5c82\u4e0d\u662f \u8981\u7edd\u4e86\u5417\uff1f\u4e8e\u662f\u4ed6\u60f3\u5230\u8981\u627e\u4e00\u4e2a\u4f20\u4eba\u2014\u2014 \u3000\u3000\u5c31\u8fd9\u6837\uff0c\u4ed6\u5230\u4e86\u4e2d\u539f\u2026\u2026 \u3000\u3000\u73b0\u5728\u7231\u5973\u7adf\u597d\u597d\u7684\u5728\u81ea\u5df1\u6000\u4e2d\uff0c\u59bb\u5b50\u4e5f\u597d\u597d\u7684\u5728\u81ea\u5df1\u8eab\u65c1\uff0c\u4ed6\u9ed8\u9ed8\u611f\u8c22\u4e0a\u82cd\uff0c\u4e0a\u5929 \u5bf9\u4ed6\u771f\u592a\u5173\u6ce8\u4e86\u3002 \u3000\u3000\u81f3\u4e8e\u90a3\u201c\u5bb3\u6b7b\u201d\u7f2a\u4e5d\u5a18\u7684\u6885\u5c71\u6c11\uff0c\u4ed6\u53c8\u5077\u5077\u77a5\u4e86\u4e00\u773c\uff0c\u90a3\u4e1d\u4e1d\u767d\u53d1\u5728\u5bd2\u98ce\u4e2d\u98d8\u52a8\uff0c \u5dcd\u5dcd\u7136\u7684\u9f99\u949f\u8001\u6001\uff0c\u4ed6\u7684\u6c14\u5168\u6d88\u4e86\uff0c\u662f\u4ee5\u4ed6\u4e0d\u518d\u5bf9\u7f2a\u4e03\u5a18\u8bf4\u660e\uff0c\u4ec5\u7f13\u7f13\u56de\u8eab\u5411\u6885\u5c71\u6c11\u7565 \u4e00\u70b9\u9996\uff0c\u62c9\u7740\u59bb\u5973\u4e00\u7eb5\u8eab\uff0c\u5982\u4e00\u53ea\u5927\u9e1f\u822c\u817e\u7a7a\u800c\u8d77\u2014\u2014 \u3000\u3000\u83c1\u513f\u4e00\u62ac\u5934\uff0c\u9661\u7136\u770b\u89c1\u4e86\u8f9b\u6377\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u4f46\u662f\u53ea\u60ca\u53eb\u4e86\u58f0\uff1a \u3000\u3000\u201c\u554a\uff01\u4f60\u2014\u2014\u4f60\u2014\u2014\u201d\u5c31\u88ab\u65e0\u6068\u751f\u5e26\u51fa\u4e8e\u4e08\u4e4b\u5916\uff0c\u4e24\u4e2a\u8d77\u843d\u5c31\u5931\u53bb\u8e2a\u8ff9\u3002 \u3000\u3000\u65f7\u91ce\u4e2d\u4ecd\u4f20\u51fa\u4e86\u8f9b\u6377\u7684\u53eb\u58f0\uff1a\u201c\u83c1\u513f\u2014\u2014\u7b49\u4e00\u7b49\u201d \u3000\u3000\u6885\u5c71\u6c11\u671b\u4e86\u671b\u6005\u82e5\u6240\u5931\u7684\u8f9b\u6377\uff0c\u95ee\u9053\uff1a\u201c\u6377\u513f\uff0c\u4f60\u8ba4\u5f97\u5979\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9ed8\u9ed8\u70b9\u4e86\u5934\u3002 \u3000\u3000\u6885\u53d4\u53d4\u5728\u8f9b\u6377\u7684\u8138\u4e0a\u627e\u5230\u4e86\u7b54\u6848\uff0c\u5386\u7ecf\u6ca7\u6851\u7684\u4ed6\u53ea\u5fc3\u4e2d\u8f7b\u53f9\u4e00\u58f0\uff0c\u53e3\u4e2d\u5374\u4ee5\u4e00\u79cd\u632f \u594b\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6377\u513f\uff0c\u6211\u770b\u90a3\u2018\u6885\u9999\u5251\u2019\u4eca\u591c\u5c31\u53ef\u4ee5\u5927\u529f\u544a\u6210\u4e86\u201d\u3002 \u3000\u3000\u8f9b\u6377\u9661\u7136\u60ca\u8d77\uff0c\u60f3\u8d77\u81ea\u5df1\u66fe\u8c6a\u6c14\u5e72\u4e91\u5730\u7acb\u8a93\uff0c\u4e0d\u7981\u611f\u5230\u60ed\u6127\uff0c\u8f6c\u8eab\u7b54\u9053\uff1a\u201c\u6885\u53d4\u53d4\uff0c \u54b1\u4eec\u5148\u56de\u5bb6\u7f62\u2014\u2014\u201d \u3000\u3000\u58f0\u97f3\u663e\u7136\u5df2\u6062\u590d\u5f80\u65e5\u7684\u8c6a\u6c14\uff0c\u6885\u53d4\u53d4\u6380\u9aef\u5fae\u7b11\u4e86\u4e00\u7b11\u9053\uff1a\u201c\u554a\u2014\u2014\u5bf9\u4e86\uff0c\u90a3\u7389\u9aa8\u9b54 \u66fe\u4e0b\u8fc7\u4ec0\u4e48\u2018\u900f\u9aa8\u65ad\u9b42\u7802\u2019\uff0c\u90a3\u4e00\u5757\u571f\u5730\u4e09\u4e2a\u65f6\u8fb0\u4e0d\u53ef\u6709\u4eba\u8d70\u52a8\uff0c\u73b0\u5728\u6377\u513f\u4f60\u6700\u597d\u5728\u8fd9 \u513f\u505c\u4e00\u4f1a\u513f\u5427\uff0c\u7b49\u90a3\u6bd2\u513f\u5931\u6548\uff0c\u5343\u4e07\u4e0d\u8981\u4f7f\u4eba\u8bef\u8fc7\uff01\u201d \u3000\u3000\u8bf4\u7740\u5fae\u5fae\u4e00\u7b11\uff0c\u63d0\u8d77\u5730\u4e0a\u7684\u9152\u58f6\uff0c\u8f6c\u8eab\u8d70\u53bb\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4e5f\u81ea\u5fae\u7b11\uff0c\u77a7\u7740\u6885\u53d4\u53d4\u8d70\u8fdc\u4e86\uff0c\u624d\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u7389\u9aa8\u9b54\u7684\u5c38\u8eab\uff0c\u6211\u8fd8\u662f\u628a \u4ed6\u57cb\u4e86\u5427\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u4e0d\u518d\u8fdf\u7591\uff0c\u4e8e\u662f\u5728\u8def\u65c1\u6316\u4e86\u4e00\u4e2a\u6d1e\u7a74\uff0c\u60f3\u53bb\u62b1\u8d77\u7389\u9aa8\u9b54\u7684\u5c38\u8eab\u653e\u8fdb\u6d1e\u91cc\uff0c \u5fc3\u4e2d\u51db\u7136\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u7389\u9aa8\u9b54\u4e00\u8eab\u662f\u6bd2\uff0c\u6211\u8fd8\u662f\u4e0d\u8981\u63a5\u89e6\u7684\u597d\u3002\u201d \u3000\u3000\u60f3\u4e86\u4e00\u4e0b\uff0c\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u865a\u7a7a\u5411\u90a3\u6b7b\u53bb\u7684\u7389\u9aa8\u9b54\u6293\u53bb\uff0c\u53ea\u89c1\u4e00\u80a1\u52b2\u98ce\u8fc7\u53bb\u7adf\u5c06\u5c38 \u4f53\u63a8\u52a8\u8d77\u6765\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u52a0\u8fdf\u7591\uff0c\u9661\u7136\u53d8\u6293\u4e3a\u63a8\uff0c\u865a\u865a\u4e00\u51fb\uff0c\u7389\u9aa8\u9b54\u7684\u8eab\u5b50\u6d3b\u751f\u751f\u7684\u597d\u50cf\u4eba\u6258\u7740\u4f3c\u7684\uff0c \u5e73\u5e73\u98de\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u76f8\u4e86\u76f8\u8ddd\u79bb\uff0c\u201c\u563f\u201d\u5730\u5410\u4e86\u4e86\u771f\u6c14\uff0c\u5782\u624b\u800c\u4e0b\u3002 \u3000\u3000\u7389\u9aa8\u9b54\u8eab\u5b50\u5374\u4e0d\u504f\u4e0d\u659c\u843d\u5165\u7a74\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u60f3\u7d22\u6027\u7528\u4e00\u4f1a\u5185\u529f\uff0c\u7701\u53bb\u9ebb\u70e6\uff0c\u968f\u5373\u4e3e\u638c\u4e00\u62c2\uff0c\u638c\u98ce\u7ef5\u7ef5\u54cd\u8d77\uff0c\u547c\u5730\u628a\u5806\u79ef \u5728\u7a74\u8fb9\u7684\u6ce5\u571f\u626b\u5165\u7a74\u5185\uff0c\u8986\u76d6\u5728\u7389\u9aa8\u9b54\u8eab\u5b50\u4e0a\u3002 \u3000\u3000\u7728\u773c\u529f\u592b\uff0c\u4e00\u4ee3\u540d\u4eba\u5c31\u957f\u7720\u5730\u4e0b\uff0c\u8f9b\u6377\u4e0d\u89c9\u5fae\u5fae\u611f\u53f9\uff0c\u4e0a\u524d\u6253\u7d27\u6ce5\u571f\uff0c\u5fc3\u4e2d\u4e0b\u6709\u4e00 \u4e1d\u559c\u6170\uff0c\u6562\u60c5\u662f\u81ea\u5df1\u4e00\u53e3\u771f\u6c14\u53c8\u53ef\u4ee5\u7ef4\u6301\u5f97\u66f4\u4e45\u4e86\uff01 \u3000\u3000\u57cb\u597d\u7389\u9aa8\u9b54\uff0c\u4e0d\u518d\u6709\u4e8b\uff0c\u62ac\u5934\u4e00\u671b\u5929\u8272\uff0c\u53ea\u89c1\u5df2\u662f\u7834\u6653\u65f6\u5206\uff0c\u800c\u4e14\u4e91\u953a\u6e10\u6563\uff0c\u592a\u9633 \u6709\u5373\u5c06\u51fa\u6765\u7684\u8d8b\u52bf\uff01 \u3000\u3000\u6298\u817e\u534a\u591c\uff0c\u867d\u7136\u662f\u65e0\u6708\u65e0\u661f\uff0c\u4f46\u904d\u5730\u81ea\u7691\u7691\u7684\u5168\u662f\u96ea\uff0c\u6620\u5c04\u51fa\u6765\u7684\u5149\u8f89\uff0c\u5012\u4e5f\u4e0d\u5f31\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u7565\u6709\u6240\u611f\uff0c\u60f3\u5230\u521a\u624d\u90a3\u4e00\u573a\u6bd2\u6218\uff0c\u4e5f\u4e0d\u7531\u5fc3\u60ca\uff0c\u5ffd\u751f\u5947\u611f\uff0c\u5fd6\u9053\uff1a\u201c\u6c5f\u6e56 \u4e0a\u9b3c\u5bb5\u592a\u591a\uff0c\u81ea\u5df1\u5047\u5982\u6709\u4e00\u70b9\u513f\u2018\u6bd2\u2019\u7684\u77e5\u8bc6\uff0c\u4ee5\u540e\u884c\u9053\u5012\u6bd4\u8f83\u65b9\u4fbf\u3002\u201d\u968f\u7740\u60f3\u5230\u90a3\u91d1 \u4e00\u9e4f\u8457\u7684\u2018\u6bd2\u7ecf\u2019\u6b63\u662f\u968f\u8eab\u6240\u5e26\uff0c\u53ea\u662f\u6ca1\u6709\u65f6\u95f4\u7814\u7a76\u7f62\u4e86\uff0c\u4ee5\u540e\u5982\u6709\u7a7a\u95f2\uff0c\u5fc5\u5b9a\u8981\u597d\u597d \u7814\u8bfb\u624d\u662f\u3002 \u3000\u3000\u987b\u77e5\u8f9b\u6377\u4e3a\u4eba\u65f7\u8fbe\uff0c\u5e76\u6ca1\u6709\u62d8\u8c28\u7684\u89c2\u5ff5\uff0c\u60f3\u5230\u4fbf\u505a\uff0c\u8fd9\u6837\u5374\u9020\u6210\u4e00\u4e2a\u4ed7\u201c\u6885\u9999\u5251\u201d \u548c\u201c\u6bd2\u201d\u626c\u540d\u5929\u4e0b\u7684\u5947\u4fa0\uff0c\u6b64\u662f\u540e\u8bdd\u4e0d\u63d0\u3002 \u3000\u3000\u60f3\u5230\u6bd2\u7ecf\uff0c\u4e0d\u7531\u6697\u6094\u81ea\u5df1\u521a\u624d\u6ca1\u6709\u628a\u5b83\u8fd8\u7ed9\u539f\u8457\u8005\u6bd2\u541b\uff0c\u4f46\u5373\u8f6c\u5ff5\uff0c\u6b64\u4e66\u5728\u81ea\u5df1\u8fd9 \u513f\uff0c\u5012\u4e5f\u4e0d\u5fc5\u9001\u8fd8\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u5766\u7136\u3002 \u3000\u3000\u6b63\u80e1\u601d\u4e71\u60f3\u95f4\uff0c\u5929\u8272\u65e9\u660e\uff0c\u679c\u7136\u96ea\u6b62\u5929\u6674\uff0c\u5929\u6c14\u6bd4\u8f83\u6696\u4e9b\u3002 \u3000\u3000\u4e91\u6de1\u98ce\u6e05\uff0c\u5927\u96ea\u65b9\u6b62\uff0c\u9633\u5149\u9732\u51fa\u4e91\u973e\uff0c\u653e\u5c04\u51fa\u90a3\u548c\u6696\u7684\u5149\u7ebf\uff0c\u6620\u5c04\u5728\u767d\u7691\u7684\u96ea\u5730\u4e0a\uff0c \u53d1\u51fa\u523a\u76ee\u7684\u5149\u8f89\u3002 \u3000\u3000\u5927\u5730\u51e0\u4e4e\u5b8c\u5168\u7b3c\u7f69\u5728\u7691\u7691\u767d\u96ea\u4e4b\u4e0b\uff0c\u4e00\u671b\u65e0\u6839\uff0c\u5c31\u53ea\u5269\u90a3\u5939 \u3000\u3000\u9053\u7684\u6885\u513f\uff0c\u968f\u98ce\u6296\u52a8\u62ab\u5728\u8eab\u4e0a\u7684\u96ea\u82b1\uff0c\u633a\u7acb\u5728\u8fd9\u52a8\u4eba\u7684\u96ea\u666f\u4e4b\u4e2d\uff0c\u4ee4\u4eba\u770b\u6765\u5fc3\u795e\u4e0d \u7531\u4e3a\u4e4b\u4e00\u7545\u3002 \u3000\u3000\u8f9b\u6377\u76ee\u9001\u90a3\u76d6\u4ee3\u5947\u4eba\u4e16\u5916\u4e09\u4ed9\u4e4b\u4e00\u7684\u65e0\u6068\u751f\u5982\u98de\u8d70\u540e\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\uff0c\u957f\u5401\u4e00 \u53e3\u6c14\u3002 \u3000\u3000\u6885\u5360\u5148\u6625\uff0c\u5bd2\u6885\u65e9\u653e\u2014\u2014 \u3000\u3000\u84e6\u5730\u91cc\u767d\u5730\u4e0a\u4eba\u5f71\u4e00\u6643\uff0c\u5728\u521a\u9732\u51fa\u7684\u592a\u9633\u4e0b\uff0c\u62d6\u4e0b\u4e8c\u6761\u4fee\u957f\u7684\u5f71\u5b50\uff0c\u6562\u60c5\u662f\u6709\u4e8c\u4e2a \u4eba\u8e0f\u96ea\u800c\u6765\u3002 \u3000\u3000\u8f9b\u6377\u8d1f\u624b\u800c\u7acb\uff0c\u6c89\u9189\u5728\u8fd9\u52b2\u79c0\u7684\u98ce\u5149\u4e2d\u3002 \u3000\u3000\u6041\u5730\u98ce\u513f\u5982\u6b64\u52b2\u6025\uff0c\u4f46\u5374\u63d0\u4e0d\u8d77\u4e00\u4e1d\u4e00\u6beb\u4ed6\u7684\u888d\u89d2\u3002 \u3000\u3000\u4fe1\u6b65\u8d70\u52a8\uff0c\u77a5\u89c1\u90a3\u6885\u53d4\u53d4\u7684\u5c4b\u5b50\uff0c\u4e0d\u7981\u53c8\u89e6\u53ca\u5fc3\u6000\uff0c\u5fae\u53f9\u4e00\u58f0\uff0c\u4f46\u7acb\u523b\u5374\u53c8\u611f\u5230\u4e00 \u79cd\u83ab\u540d\u7684\u632f\u594b\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u53ea\u8981\u6885\u9999\u5251\u4e00\u51b6\u597d\uff0c\u4e0d\u518d\u6015\u90a3\u5389\u9e57\u7684\u501a\u8679\u795e\u5251\uff0c\u5fc5\u53ef\u4e00\u5149 \u4e03\u5999\u795e\u541b\u7684\u540d\u5934\uff0c\u800c\u4e14\u4e5f\u4e00\u5b9a\u53ef\u4ee5\u5bfb\u7740\u90a3\u6d77\u5929\u53cc\u715e\uff0c\u62a5\u5374\u4e0d\u5171\u6234\u5929\u4e4b\u4ec7\uff01\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u795e\u91c7\u98de\u626c\uff0c\u4f46\u8f6c\u5ff5\u60f3\u5230\u548c\u81ea\u5df1\u5171\u751f\u6b7b\u7684\u5434\u51cc\u98ce\u65f6\uff0c\u5fc3\u4e2d\u53c8\u662f\u4e00\u75db\u3002 \u3000\u3000\u6b63\u6c89\u541f\u95f4\uff0c\u5e76\u6ca1\u6709\u53d1\u73b0\u90a3\u6025\u5954\u8fc7\u6765\u7684\u4eba\u5f71\uff0c\u7b49\u5230\u53d1\u73b0\u6709\u8863\u6289\u7834\u98ce\u65f6\uff0c\u6025\u4e00\u53cd\u9996\uff0c\u53ea \u89c1\u4e24\u6761\u4eba\u5f71\u5df1\u5982\u98de\u800c\u81f3\uff0c\u65e0\u5de7\u4e0d\u5de7\u6b63\u76f4\u5954\u800c\u6765\uff0c\u8ddd\u90a3\u6709\u6bd2\u767d\u96ea\u5708\u5df2\u4ec5\u5728\u4e94\u4e08\u5de6\u53f3\u4e86\uff01 \u3000\u3000\u5fc3\u4e2d\u4e00\u6025\uff0c\u4e0d\u53ca\u547c\u558a\uff0c\u8eab\u4f53\u7acb\u523b\u817e\u8d77\u2014\u2014 \u3000\u3000\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u5e03\u6ee1\u5168\u8eab\uff0c\u865a\u7a7a\u4e00\u638c\u5288\u53bb\uff0c\u9053\uff1a\u201c\u524d\u8fdb\u4e0d\u5f97\uff01\u201d \u3000\u3000\u90a3\u4e8c\u4e2a\u6765\u4eba\u9661\u89c9\u638c\u98ce\u88ad\u9762\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u767e\u5fd9\u4e2d\u4e0d\u6687\u95ea\u8eb2\uff0c\u4e5f\u786c\u751f\u62cd\u51fa\u4e8c\u638c\u3002 \u3000\u3000\u4e09\u80a1\u638c\u98ce\u4e00\u51b2\uff0c\u8f9b\u6377\u9661\u89c9\u5bf9\u65b9\u52b2\u9053\u597d\u5927\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u8dc4\u8e09\u9000\u540e\u6570\u6b65\uff0c\u800c\u90a3\u4e8c\u4e2a\u6765\u4eba \u4e5f\u88ab\u8f9b\u633a\u4e00\u638c\u9707\u5f97\u4ece\u534a\u7a7a\u843d\u5728\u5730\u4e0a\u3002\u8f9b\u6377\u4e0d\u5f85\u8eab\u4f53\u7acb\u7a33\u4fbf\u9053\uff1a\u201c\u4e14\u6162\uff0c\u90a3\u5757\u5730\u8d70\u4e0d\u5f97\u2014 \u2014\u201d \u3000\u3000\u90a3\u4e8c\u4e2a\u4eba\u6115\u7136\u7acb\u5b9a\uff0c\u4e0d\u89e3\u5730\u9053\uff1a\u201c\u9601\u4e0b\u662f\u8bf4\uff0c\u8fd9\u5757\u571f\u5730\u54b1\u4eec\u4e0d\u53ef\u4ee5\u884c\u8d70\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u7ad9\u7684\u5730\u65b9\u662f\u6885\u6811\u4e0b\u9762\uff0c\u5149\u7ebf\u4e0d\u597d\uff0c\u662f\u4ee5\u6765\u4eba\u770b\u4e0d\u6e05\u695a\uff0c\u8f9b\u6377\u5012\u6e05\u695a\u7684\u6253\u91cf\u4e86\u4e8c \u4eba\uff0c\u60ca\u9053\uff1a \u3000\u3000\u201c\u554a\uff0c\u539f\u6765\u662f\u91d1\u6c0f\u5144\u5f1f\uff0c\u662f\u7684\u3002\u8fd9\u571f\u5730\u4e0a\u9644\u6709\u5947\u6bd2\uff0c\u9976\u662f\u529f\u592b\u9ad8\u7edd\uff0c\u4e5f\u6321\u4e0d\u4f4f\u6b64\u900f \u9aa8\u65ad\u9b42\u7802\u3002\u201d \u3000\u3000\u539f\u6765\u6765\u8005\u5374\u662f\u8f9b\u6377\u66fe\u7ecf\u9022\u7740\u7684\u4e10\u5e2e\u62a4\u6cd5\u91d1\u6c0f\u5144\u5f1f\u2014\u2014\u91d1\u5143\u4f2f\u548c\u91d1\u5143\u4ef2\u3002 \u3000\u3000\u91d1\u6c0f\u5144\u5f1f\u8fd8\u6ca1\u6709\u53d1\u73b0\u6321\u7740\u4ed6\u4eec\u7684\u4eba\u6b63\u662f\u8f9b\u6377\uff0c\u53ea\u5f90\u5f90\u7684\u4fef\u9996\u6ce8\u89c6\u7740\u5730\u9762\uff0c\u53c8\u4e0d\u89e3\u7684 \u9894\u9996\u77a7\u7740\u90a3\u7ad9\u5728\u6885\u6811\u4e0b\u7684\u4eba\u513f\u2014\u2014\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u8fc5\u901f\u7684\u8bf4\u51fa\u539f\u59d4\uff0c\u91d1\u6c0f\u6606\u4ef2\u4e0d\u7531\u5f97\u5012\u62bd\u4e00\u53e3\u51b7\u6c14\uff0c\u4f46\u5f53\u4ed6\u4eec\u53d1\u73b0\u62e6\u8def\u8005\u662f\u4ed6\u4eec \u66fe\u7ecf\u76f8\u8bc6\u7684\u8f9b\u6377\u65f6\uff0c\u5012\u751f\u51fa\u4e00\u79cd\u91ca\u7136\u4e8e\u6000\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u6562\u60c5\u4ee5\u4ed6\u4eec\u5144\u5f1f\u4e8c\u4eba\u7684\u638c\u529b\u4e4b\u548c\uff0c\u624d\u80fd\u548c\u5bf9\u65b9\u4e00\u51fb\uff0c\u8fd9\u79cd\u4eba\u7269\uff0c\u6c5f\u6e56\u4e0a\u6709\u591a\u5c11\u54e9\uff1f \u3000\u3000\u91d1\u6c0f\u5144\u5f1f\u7ed5\u9053\u90a3\u53ef\u538c\u7684\u5708\u513f\uff0c\u8d70\u5411\u8f9b\u6377\u4e00\u62f1\u624b\uff0c\u7528\u7740\u4e00\u79cd\u5f88\u7126\u6025\u7684\u58f0\u97f3\u8bf4\u9053\uff1a \u3000\u3000\u201c\u591a\u8c22\u8f9b\u8001\u5f1f\u6307\u793a\uff0c\u4e0d\u8fc7\u655d\u5144\u5f1f\u6b64\u523b\u5c1a\u6709\u6025\u4e8b\uff0c\u6b64\u6069\u53ea\u6709\u540e\u4f1a\u624d\u62a5\u3002\u201d\u8bf4\u7740\u4e24\u4eba\u5df2 \u51cc\u7a7a\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u5bf9\u4e8c\u4eba\u751f\u6709\u6781\u5927\u597d\u611f\uff0c\u8fd9\u65f6\u89c1\u4e8c\u4eba\u597d\u50cf\u7acb\u523b\u4fbf\u8981\u7ee7\u7eed\u8d76\u8def\uff0c\u4e0d\u7531\u6025\u5fd9\u8bf4\u9053\uff1a \u201c\u624b\u8db3\u4e4b\u52b3\uff0c\u4f55\u8db3\u6302\u9f7f\uff0c\u4e8c\u4f4d\u6709\u4f55\u7b49\u91cd\u8981\u4e8b\uff1f\u7adf\u8981\u5982\u6b64\u8d76\u8def\uff0c\u5c0f\u5f1f\u5012\u613f\u80fd\u5fae\u6548\u5176\u52b3\u54e9\uff01\u201d \u3000\u3000\u91d1\u6c0f\u5144\u5f1f\u8eab\u5f62\u4e00\u632b\uff0c\u91d1\u5143\u4f2f\u56de\u7b54\u9053\uff1a\u201c\u655d\u5e2e\u5e2e\u4e3b\u6709\u96be\uff0c\u4e0d\u6687\u591a\u7559\u2014\u2014\u201d\u8bf4\u7740\u5fae\u5fae\u4e00 \u987f\u3002 \u3000\u3000\u7ad9\u5728\u4e00\u65c1\u7684\u91d1\u5143\u4ef2\u5fae\u62c9\u4ed6\u4e00\u4e0b\uff0c\u8eab\u8eaf\u6025\u7eb5\uff0c\u4f3c\u662f\u8feb\u4e0d\u6025\u5f85\u7684\u6837\u5b50\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u77e5\u9053\u90a3\u5e2e\u4e3b\u5fc5\u7136\u5c31\u662f\u90a3\u53ef\u7231\u7684\u5b69\u5b50\uff0c\u89c1\u4ed6\u6709\u96be\uff0c\u4e0d\u89c9\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8131\u53e3\u9053\uff1a \u201c\u5728\u4ec0\u4e48\u5730\u65b9\u5475\uff1f\u201d \u3000\u3000\u91d1\u6c0f\u6606\u4ef2\u5df2\u53bb\u5f97\u8fdc\u4e86\uff0c\u957f\u58f0\u7b54\u9053\uff1a\u201c\u5728\u6e56\u5357\u5883\u5185\uff0c\u4e0d\u6562\u6709\u52b3\u5927\u9a7e\uff0c\u540e\u4f1a\u6709\u671f\u3002\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u8eab\u5f62\u5df2\u6e3a\u5728\u767d\u7691\u7691\u7684\u96ea\u5730\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u671b\u7740\u4e8c\u4eba\u4ecd\u65e7\u548c\u65e9\u5148\u89c1\u9762\u65f6\u4e00\u6837\u7684\u6253\u626e\u2014\u2014\u9ad8\u9ad8\u7684\u7ea2\u5e3d\u548c\u9ebb\u5e03\u8863\u670d\u2014\u2014 \u3000\u3000\u5fc3\u4e2d\u4e5f\u5fae\u5fae\u4e00\u52a8\u5fd6\u5fd6\uff1a\u201c\u521a\u624d\u91d1\u8001\u5927\u5206\u660e\u6709\u8bf7\u6211\u52a9\u62f3\u7684\u6837\u5b50\uff0c\u4f46\u4ed6\u5f1f\u5f1f\u5374\u62c9\u8dd1\u4ed6\uff0c \u770b\u6765\u4e10\u5e2e\u8fd9\u6b21\u53d7\u7684\u96be\u5012\u662f\u4e0d\u5c0f\u54e9\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u5fd6\u9053\uff1a\u201c\u4e10\u5e2e\u7684\u4eba\u7269\u597d\u50cf\u90fd\u662f\u795e\u79d8\u4e0d\u8fc7\u7684\uff0c\u4f46\u5fc3\u80a0\u5374\u975e\u5e38\u597d\uff0c\u53cd\u6b63\u73b0\u5728\u65e0\u4e8b\uff0c \u4e0d\u5982\u987a\u6c5f\u53bb\u6e56\u5357\u770b\u770b\uff0c\u76f8\u673a\u884c\u4e8b\uff01\u201d \u3000\u3000\u4e3b\u610f\u65e2\u5b9a\uff0c\u4e0d\u518d\u5446\u7acb\uff0c\u671b\u671b\u5929\u8272\uff0c\u5df2\u77e5\u5927\u6982\u662f\u4e09\u4e2a\u65f6\u8fb0\u7684\u9650\u671f\u4e86\uff0c\u968f\u5373\u4e0a\u524d\u68c0\u89c6\uff0c \u53ea\u89c1\u90a3\u5708\u513f\u5df2\u7531\u7c89\u7070\u8272\u8f6c\u6210\u767d\u8272\uff0c\u60f3\u662f\u6bd2\u6027\u5df2\u8fc7\u3002 \u3000\u3000\u4e8e\u662f\u7f13\u6b65\u800c\u884c\uff0c\u8d70\u5411\u6885\u53d4\u53d4\u6240\u5c45\u7684\u8305\u5c4b\u3002 \u3000\u3000\u8def\u7a0b\u5e76\u4e0d\u9065\u8fdc\uff0c\u4e0d\u5230\u4e00\u76cf\u8336\u7684\u65f6\u95f4\uff0c\u4fbf\u8fdb\u5165\u5c4b\u5b50\u3002 \u3000\u3000\u6885\u53d4\u53d4\u7684\u5bb6\uff0c\u8f9b\u6377\u5df2\u79bb\u522b\u5e74\u4f59\uff0c\u6b64\u65f6\u91cd\u6e38\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u751f\u51fa\u4e00\u79cd\u65e7\u5730\u91cd\u6e38\u7684\u91cd\u6e29\u65e7\u68a6 \u4e4b\u611f\u3002 \u3000\u3000\u6885\u5c71\u6c11\u65e9\u5df2\u5f85\u5728\u4e2d\u5802\uff0c\u89c1\u8f9b\u6377\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6377\u513f\uff0c\u5feb\u5c06\u4e00\u5e74\u6765\u7684\u4e8b\u513f\u8bf4\u6765\u542c\u542c\u3002\u201d \u3000\u3000\u8f9b\u6377\u606d\u58f0\u4ece\u547d\uff0c\u5c06\u4e00\u5e74\u6765\u7684\u4e8b\u513f\u4e00\u4ef6\u4ef6\u7528\u5f88\u7b80\u7565\u7684\u8bf4\u6cd5\u8bf4\u51fa\u6765\uff0c\u6885\u5c71\u6c11\u2014\u2014\u7559\u795e\u503e \u542c\uff0c\u5f53\u8f9b\u6377\u8bf4\u5230\u4faf\u53d4\u53d4\u60e8\u906d\u6bd2\u624b\u65f6\uff0c\u6885\u5c71\u6c11\u4e0d\u7531\u54ac\u7259\u5207\u9f7f\u5341\u5206\u60b2\u75db\u7684\u54ed\u7740\u3002 \u3000\u3000\u5c24\u5176\u662f\u8bf4\u5230\u5c0f\u6222\u5c9b\u5947\u9047\u65f6\uff0c\u6885\u5c71\u6c11\u66f4\u662f\u6025\u8bf8\u4e8e\u8272\uff0c\u4f46\u7b49\u5230\u4ed6\u8bf4\u5230\u4ee5\u4ed6\u4e00\u4eba\u7684\u529f\u529b\u7adf \u80fd\u548c\u201c\u6d77\u5929\u53cc\u715e\u201d\u529b\u6218\u4e0a\u5343\u62db\uff0c\u5374\u53cd\u800c\u9732\u51fa\u91ca\u7136\u7684\u6837\u5b50\u3002 \u3000\u3000\u6562\u60c5\u662f\u4ed6\u66fe\u76ee\u7779\u8f9b\u6377\u548c\u65e0\u6068\u751f\u5bf9\u638c\u7684\u60c5\u5f62\u3002 \u3000\u3000\u4f46\u5f53\u8f9b\u6377\u8bf4\u5230\u6cf0\u5c71\u5927\u4f1a\u7684\u65f6\u5019\uff0c\u6885\u5c71\u6c11\u5374\u4ec5\u4e0d\u5c51\u7684\u4e00\u7b11\u3002\u5e74\u4f59\u7684\u4e8b\uff0c\u5012\u4e5f\u4e0d\u7b97\u5c11\uff0c \u6574\u6574\u8bf4\u4e86\u4e00\u4e8c\u4e2a\u65f6\u8fb0\uff0c\u6885\u5c71\u6c11\u542c\u5b8c\u540e\uff0c\u4e0d\u51fa\u4e00\u58f0\uff0c\u597d\u4f3c\u9677\u5165\u6c89\u601d\u3002 \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u90a3\u5389\u8001\u8d3c\u7684\u2018\u501a\u8679\u2019\u5b9e\u662f\u5148\u5929\u795e\u5175\uff0c\u950b\u5229\u65e0\u6bd4\uff0c\u4e0d\u77e5\u6885\u9999\u5251\u80fd\u5426\u80dc\u8fc7\u2014 \u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u89c1\u6885\u5c71\u6c11\u4ecd\u5728\u6c89\u601d\u4e2d\uff0c\u4e0d\u50cf\u5728\u51dd\u542c\uff0c\u521a\u4e0a\u4f4f\u53e3\uff0c\u6885\u5c71\u6c11\u5374\u9053\uff1a\u201c\u90a3\u6167\u5927 \u5e08\u4f20\u4f60\u7684\u4ec0\u4e48\u795e\u5947\u7684\u6b65\u6cd5\uff0c\u4f60\u518d\u6f14\u4e00\u904d\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u70b9\u5934\u9053\uff1a\u201c\u8fd9\u8bd8\u6469\u6b65\u6cd5\u662f\u6167\u5927\u5e08\u6bd5\u751f\u5fc3\u8840\u2014\u2014\u201d\u8bf4\u7740\u8d77\u8eab\u8bd5\u4e86\u4e00\u904d\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5fae\u5fae\u6c89\u541f\uff0c\u9053\uff1a\u201c\u679c\u7136\u662f\u53e4\u602a\u5df2\u6781\uff0c\u90a3\u5927\u884d\u795e\u5251\u4e5f\u4f7f\u4e00\u904d\uff0c\u8ba9\u6211\u5f00\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u6687\u601d\u7d22\uff0c\u4ece\u8d77\u624b\u5f0f\u201c\u65b9\u751f\u4e0d\u606f\u201d\u5230\u6536\u5f0f\u201c\u56de\u5cf0\u8f6c\u6298\u201d\uff0c\u4e00\u5171\u5341\u5f0f\uff0c\u4e94\u5341\u4e2a\u53d8 \u5316\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5728\u8f9b\u6377\u6f14\u8fd9\u4e8c\u5957\u4e16\u5916\u9ad8\u4eba\u7684\u7edd\u827a\u65f6\uff0c\u90fd\u5168\u795e\u8d2f\u6ce8\uff0c\u6c89\u601d\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u9053\uff1a \u201c\u4f60\u6709\u6ca1\u6709\u8bd5\u8fc7\u628a\u4e8c\u95e8\u7edd\u827a\u5408\u800c\u4e3a\u4e00\uff0c\u90a3\u5c31\u662f\u8e0f\u7740\u8bd8\u6469\u6b65\u6cd5\uff0c\u6325\u52a8\u5927\u884d\u5251\u5f0f\u2014\u2014\u201d \u3000\u3000\u679c\u7136\u662f\u4e00\u4ee3\u5b97\u5e08\u3002\u4e00\u8a00\u65b9\u51fa\uff0c\u5df1\u60ca\u9192\u8f9b\u6377\uff0c\u6597\u89c9\u8305\u585e\u987f\u5f00\uff0c\u6b22\u7136\u9053\uff1a\u201c\u8fd9\u80fd\u5b8c\u6210\uff01\u201d \u8bf4\u7740\u4fbf\u6f5c\u5fc3\u601d\u7d22\u914d\u5408\u4e4b\u65b9\u3002 \u3000\u3000\u6885\u5c71\u6c11\u89c1\u4ed6\u609f\u5fc3\u5982\u6b64\u4e4b\u9ad8\uff0c\u5fc3\u4e2d\u4e5f\u81ea\u6b22\u7136\uff0c\u4e0d\u518d\u6253\u6270\uff0c\u8d70\u5165\u5185\u5ba4\uff0c\u53bb\u770b\u90a3\u6b63\u5728\u7089\u4e2d \u6cbb\u7ec3\u7684\u201c\u6885\u9999\u5251\u201d\u3002 \u3000\u3000\u6885\u9999\u5251\u672c\u5df2\u662f\u76d6\u4e16\u5947\u5251\u518d\u52a0\u4e0a\u90a3\u201c\u5343\u5e74\u6731\u7af9\u201d\uff0c\u51fa\u7089\u540e\u5fc5\u5b9a\u950b\u5229\u5f02\u5e38\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u4e00\u76f4\u5b88\u5019\u5728\u7089\u65c1\uff0c\u4e00\u76f4\u5230\u508d\u665a\uff0c\u6885\u9999\u5251\u5f00\u51fa\u7089\uff0c\u8d70\u51fa\u7089\u5ba4\uff0c\u53ea\u89c1\u8f9b \u6377\u5df1\u7ad9\u7acb\u5ba4\u4e2d\uff0c\u4e00\u62db\u4e00\u5f0f\uff0c\u7f13\u7f13\u6bd4\u5212\u7740\uff0c\u6885\u5c71\u6c11\u5fc3\u4e2d\u4e86\u7136\uff0c\u77e5\u9053\u8f9b\u6377\u679c\u7136\u5df2\u6e10\u9886\u609f\u3002 \u3000\u3000\u518d\u8fc7\u7247\u523b\uff0c\u8f9b\u6377\u5ffd\u7136\u4e0d\u52a8\uff0c\u6885\u5c71\u6c11\u4f55\u4ed6\u5fc5\u662f\u9047\u7740\u4ec0\u4e48\u96be\u9898\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u8fde\u8bd5\u4e86\u4e5d\u6b21\uff0c\u624d\u970d\u7136\u800c\u609f\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u6885\u53d4\u53d4\u5df2\u7ad9\u5728\u4e00\u65c1\uff0c\u6b63\u6b32\u89c1\u793c\uff0c\u6885\u5c71 \u6c11\u9661\u7136\u62d4\u51fa\u957f\u5251\uff0c\u5411\u8f9b\u6377\u63b7\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u5047\u601d\u7d22\uff0c\u63a5\u5251\u5728\u624b\uff0c\u4fbf\u5728\u8fd9\u4e0d\u5927\u4e0d\u5c0f\u7684\u5385\u5185\u6bd4\u5212\u7740\u521a\u624d\u987f\u609f\u7684\u5251\u62db\u3002 \u3000\u3000\u4f46\u89c1\u5251\u6c14\u7eb7\u7eb7\uff0c\u6b65\u6cd5\u98d8\u5ffd\uff0c\u679c\u7136\u500d\u89c9\u5a01\u52bf\u3002 \u3000\u3000\u8f9b\u6377\u8fde\u6f14\u6570\u904d\uff0c\u8d8a\u7ec3\u8d8a\u719f\u6089\uff0c\u5251\u5149\u6597\u8f6c\uff0c\u5251\u98ce\u6ea2\u52b2\uff0c\u8fde\u6885\u5c71\u6c11\u6b64\u7b49\u529f\u529b\uff0c\u4e5f\u4e0d\u7531\u5927 \u52a0\u559d\u91c7\uff01 \u3000\u3000\u821e\u5230\u7b2c\u56db\u904d\uff0c\u624d\u505c\u4e0b\u624b\u6765\uff0c\u89c1\u6885\u53d4\u53d4\u5728\u4e00\u65c1\u5fae\u7b11\uff0c\u9762\u5e26\u8d5e\u8bb8\u4e4b\u8272\uff0c\u5fd9\u9053\uff1a\u201c\u6885\u53d4\u53d4\uff0c \u8fd9\u4e8c\u95e8\u7edd\u827a\u5408\u5728\u4e00\u8d77\uff0c\u679c\u7136\u662f\u5a01\u529b\u5927\u589e\uff0c\u518d\u52a0\u4e0a\u65e0\u575a\u4e0d\u6467\u7684\u795e\u5251\uff0c\u90a3\u4e94\u5927\u5b97\u6d3e\u7684\u5251\u9635\u53c8 \u7b97\u5f97\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u6885\u5c71\u6c11\u5fae\u5fae\u9894\u9996\uff0c\u8bf4\u9053\uff1a\u201c\u7ec3\u4e86\u4e00\u5929\u5566\uff0c\u5feb\u6765\u5403\u996d\u3002\u201d \u3000\u3000\u9910\u7f62\uff0c\u6885\u5c71\u6c11\u95ee\u8d77\u8f9b\u6377\u4eca\u540e\u884c\u8e2a\uff0c\u8f9b\u6377\u4fbf\u628a\u81ea\u5df1\u53c8\u4e8c\u5ea6\u9022\u7740\u91d1\u6c0f\u6606\u4ef2\u7684\u4e8b\u8bf4\u51fa\u6765\uff0c \u5e76\u544a\u8bc9\u6885\u5c71\u6c11\u81ea\u5df1\u51b3\u5b9a\u5148\u5230\u6e56\u5357\u4e00\u884c\u3002 \u3000\u3000\u6885\u5c71\u6c11\u81ea\u7136\u8d5e\u6210\uff0c\u5bbf\u5f97\u4e00\u665a\uff0c\u6b21\u65e5\u6e05\u6668\uff0c\u4fbf\u9884\u5907\u52a8\u8eab\u3002 \u3000\u3000\u8f9b\u6377\u624d\u56de\u5bb6\u4e00\u5929\uff0c\u53c8\u8981\u8fdc\u884c\uff0c\u4e0d\u7531\u5fc3\u751f\u4f9d\u4f9d\u4e4b\u611f\uff0c\u6885\u5c71\u6c11\u5fae\u7b11\u7740\uff0c\u628a\u6885\u9999\u5251\u7cfb\u5728\u8f9b \u6377\u80cc\u4e0a\u8bf4\u9053\uff1a \u3000\u3000\u201c\u8fd9\u5251\u5b50\u968f\u6211\u6885\u5c71\u6c11\u98d8\u8361\u5927\u6c5f\u5357\u5317\u8fd1\u5eff\u4f59\u5e74\uff0c\u4e0d\u77e5\u8bdb\u8fc7\u591a\u5c11\u6076\u4eba\uff0c\u4eca\u65e5\u4f60\u91cd\u4ed7\u5251\u884c \u9053\u6c5f\u6e56\uff0c\u4e00\u5b9a\u8981\u4fdd\u5168\u2018\u4e03\u5999\u795e\u541b\u2019\u7684\u58f0\u540d\uff0c\u62a5\u5374\u90a3\u5927\u4ec7\uff0c\u60f3\u90a3\u2018\u6d77\u5929\u53cc\u715e\u2019\u4e5f\u5fc5\u4f1a\u5728\u6885 \u9999\u5251\u4e0b\u4f0f\u8bdb\u7684\u3002\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u7684\u4e00\u756a\u8bdd\uff0c\u4e0d\u4f46\u5f15\u8d77\u81ea\u5df1\u7684\u8c6a\u5174\uff0c\u5c31\u662f\u8fde\u8f9b\u6377\u4e5f\u89c9\u81ea\u5df1\u4f7f\u547d\u91cd\u5927\uff0c\u96c4\u5fc3\u4e07 \u4e08\u54e9\uff01 \u3000\u3000\u62dc\u53d7\u8fc7\u5b9d\u5251\u540e\uff0c\u6885\u5c71\u6c11\u53c8\u9053\uff1a\u201c\u90a3\u5355\u5251\u65ad\u9b42\u7684\u513f\u5b50\uff0c\u6216\u8bb8\u672a\u6b7b\u4e5f\u672a\u53ef\u77e5\uff0c\u4f60\u6709\u673a\u4f1a \u6700\u597d\u53bb\u6253\u542c\u4e00\u4e0b\u5b50\uff0c\u518d\u8005\u4f60\u8fd9\u4f1a\u529f\u592b\u5927\u5927\u8fdb\u6b65\uff0c\u6216\u5df1\u8d85\u51fa\u5f53\u5e74\u6211\u884c\u9053\u65f6\'\u7684\u529f\u592b\uff0c\u53ef\u4ee5 \u4e0d\u518d\u501f\u7528\u6211\u7684\u58f0\u540d\uff0c\u4e0d\u5fc5\u518d\u4ee5\u4e03\u5999\u795e\u541b\u7684\u59ff\u6001\u51fa\u73b0\uff0c\u60f3\u4f60\u4e5f\u672a\u80fd\u4fdd\u6301\u4e0d\u5931\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u96c4\u5fc3\u594b\u53d1\u7684\u8bf4\u9053\uff1a\u201c\u6885\u53d4\u53d4\u8bf7\u653e\u5fc3\uff0c\u6377\u513f\u5fc5\u80fd\u5982\u4f60\u6240\u613f\u3002\u201d \u3000\u3000\u4e8c\u4eba\u4e00\u518d\u6bb7\u6bb7\u8bdd\u522b\uff0c\u8f9b\u6377\u624d\u544a\u522b\u8d70\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u96ea\u5df2\u505c\u4e0b\uff0c\u6674\u7a7a\u4e00\u78a7\uff0c\u592a\u9633\u9ad8\u7167\uff0c\u8f9b\u6377\u5728\u8fd9\u7edd\u65e0\u4eba\u8ff9\u7684\u8352\u9053\u4e0a\uff0c\u7528\u4e0a\u4e58\u8f7b\u529f\u9a70 \u5411\u6cef\u6c5f\uff0c\u5728\u5c1a\u672b\u6eb6\u5c3d\u7684\u96ea\u5730\u4e0a\uff0c\u62d6\u7740\u4e00\u4e2a\u4fee\u957f\u7684\u5f71\u513f\uff0c\u7d27\u968f\u7740\u8eab\u4f53\uff0c\u5982\u4e00\u6761\u9ed1\u7ebf\u5212\u8fc7\u767d \u96ea\uff0c\u5982\u98de\u800c\u8fc7\uff01 \u3000\u3000\u6cef\u6c5f\u672c\u6765\u662f\u5408\u957f\u6c5f\u4e00\u9f50\u6d41\u7684\uff0c\u7ecf\u8fc7\u6570\u5343\u5e74\u7684\u53d8\u66f4\uff0c\u5c06\u4e00\u6761\u6cb3\u6d41\u5206\u4e00\u4e3a\u4e8c\uff0c\u8fd9\u56db\u5ddd\u7684 \u6c99\u9f99\u576a\u8ddd\u6cef\u6c5f\u5e76\u4e0d\u7b97\u8fdc\uff0c\u4ec5\u6709\u5341\u591a\u91cc\u8def\u7a0b\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u8def\u5954\u6765\uff0c\u4e0d\u5230\u4e00\u4e2a\u65f6\u8fb0\uff0c\u6e21\u53e3\u5df2\u7ecf\u5728\u671b\u3002 \u3000\u3000\u6c5f\u8fb9\u4eba\u4f17\u751a\u662f\u6742\u4e71\uff0c\u8f9b\u6377\u653e\u6162\u811a\u7a0b\uff0c\u7f13\u6b65\u884c\u53bb\u3002 \u3000\u3000\u8fd9\u65f6\u6c5f\u8fb9\u5df2\u6324\u6ee1\u4e86\u8981\u8fc7\u6e21\u7684\u4eba\uff0c\u6e21\u53e3\u65c1\u8fb9\u5168\u505c\u4e86\u4e00\u6761\u4e00\u6761\u8239\u513f\u548c\u4e00\u6392\u4e00\u6392\u7684\u6728\u7b4f\uff0c \u8fd0\u8d27\u548c\u8f7d\u4eba\u90fd\u751a\u662f\u7e41\u6742\u3002 \u3000\u3000\u8fd9\u6e21\u53e3\u672c\u6765\u4e0d\u751a\u7e41\u534e\uff0c\u4f46\u5230\u8fc7\u6e21\u7684\u65f6\u5019\uff0c\u5374\u4e5f\u751a\u70ed\u95f9\uff0c\u8f9b\u6377\u8d70\u5230\u6c5f\u8fb9\uff0c\u4f46\u89c1\u5e76\u5217\u7684 \u8239\u513f\u90fd\u5df2\u63a5\u8fd1\u5ba2\u6ee1\uff0c\u5fd9\u4e0a\u5f97\u4e00\u8258\u8239\uff0c\u5bf9\u68a2\u516c\u9053\uff1a \u3000\u3000\u201c\u53ef\u662f\u8981\u987a\u6c5f\u800c\u4e0a\uff0c\u53bb\u4e09\u5ce1\u5417\uff1f\u201d \u3000\u3000\u68a2\u516c\u70b9\u4e86\u70b9\u5934\uff0c\u8f9b\u6377\u4e8e\u662f\u627e\u4e2a\u5730\u65b9\u5750\u4e0b\u3002 \u3000\u3000\u518d\u6b47\u5f97\u4e00\u76cf\u8336\u65f6\u5019\uff0c\u8239\u53ea\u9884\u5907\u5f00\u884c\uff0c\u68a2\u516c\u89e3\u5f00\u5927\u7f06\uff0c\u7a0d\u7a0d\u4e58\u7bd9\uff0c\u8239\u53ea\u987a\u6c34\u800c\u4e0b\u3002 \u3000\u3000\u8f9b\u6377\u8fdc\u671b\u957f\u6c5f\uff0c\u53ea\u89c1\u5e73\u8361\u8361\u7684\u4e00\u671b\u65e0\u9645\uff0c\u5230\u89c6\u7ebf\u4ea4\u70b9\u5904\uff0c\u4ecd\u662f\u4e00\u7247\u82cd\u7070\uff0c\u659c\u671b\u90a3\u6cef \u6c5f\uff0c\u5374\u53ea\u662f\u7ec6\u7ec6\u4e00\u7ebf\uff0c\u548c\u957f\u6c5f\u76f8\u6bd4\uff0c\u4e0d\u77e5\u76f8\u5dee\u597d\u8fdc\u3002 \u3000\u3000\u8239\u53ea\u968f\u6c34\u800c\u6c89\uff0c\u4e0d\u5feb\u4e5f\u4e0d\u6162\uff0c\u52e4\u7684\u68a2\u516c\u4ecd\u62e8\u5f97\u4e8c\u6d46\uff0c\u8d56\u7684\u68a2\u516c\u5374\u52a8\u4e5f\u4e0d\u52a8\uff0c\u968f\u6ce2\u9010 \u6d41\u3002 \u3000\u3000\u5929\u6c14\u4ecd\u7136\u5f88\u51b7\uff0c\u4e58\u5ba2\u90fd\u7f29\u5728\u8231\u91cc\uff0c\u8f9b\u6377\u60f3\u6b23\u8d4f\u4e00\u4e0b\u98ce\u666f\uff0c\u72ec\u81ea\u5750\u5728\u8231\u5916\u3002 \u3000\u3000\u6c5f\u98ce\u6e10\u8d77\uff0c\u8239\u884c\u52a0\u901f\uff0c\u4e0d\u5230\u4e00\u987f\u996d\u65f6\u95f4\uff0c\u5df2\u9a76\u51fa\u5341\u591a\u91cc\u8def\u7a0b\u3002\u524d\u9762\u4fbf\u662f\u6709\u540d\u7684\u9752\u9f99 \u9669\u6ee9\uff0c\u53e4\u4eca\u4ee5\u6765\u4e0d\u77e5\u6709\u591a\u5c11\u8239\u513f\u846c\u8eab\u5728\u8fd9\u91cc\uff0c\u518d\u61d2\u7684\u68a2\u5206\u5230\u4e86\u8fd9\u91cc\u4e5f\u4e0d\u6562\u6020\u6162\uff0c\u90fd\u7ad9\u8d77 \u6765\u7f13\u4f4f\u8239\u52bf\uff0c\u53eb\u5ba2\u4eba\u628a\u884c\u674e\u90fd\u653e\u5728\u8231\u5185\uff0c\u4ee5\u514d\u7ffb\u51fa\u8239\u5f26\u3002 \u3000\u3000\u6c34\u6d41\u52a0\u6025\uff0c\u8239\u884c\u6108\u901f\uff0c\u7b80\u76f4\u662f\u6709\u5982\u4e00\u53ea\u8131\u5f26\u7684\u7bad\u513f\u3002 \u3000\u3000\u201c\u9752\u9f99\u9669\u6ee9\u5df2\u7136\u5728\u671b\uff0c\u68a2\u516c\u5f7c\u6b64\u5406\u559d\u7740\uff0c\u4e1c\u62e8\u4e8c\u6868\uff0c\u897f\u6491\u4e00\u7bd9\uff0c\u90fd\u5728\u8feb\u4e0d\u6025\u5f85\u4e4b\u95f4\uff0c \u95ea\u53bb\u4e0d\u77e5\u591a\u5c11\u6697\u7901\u3002 \u3000\u3000\u9669\u6ee9\u7684\u4e2d\u6bb5\u6c5f\u9762\u9661\u7a84\uff0c\u6709\u4e00\u6bb5\u6c34\u6d41\u7531\u4e8c\u8fb9\u7684\u7901\u77f3\u51b2\u79ef\uff0c\u5f62\u6210\u4e00\u4e2a\u6f6d\u72b6\u7684\u6c34\u9762\uff0c\u521d\u770b \u5c31\u50cf\u662f\u4e00\u7247\u6b7b\u6c34\u9576\u5728\u6025\u6e4d\u4e2d\u4e00\u822c\uff0c\u8239\u53ea\u5230\u4e86\u8fd9\u91cc\uff0c\u90fd\u662f\u4e00\u7f13\u3002 \u3000\u3000\u68a2\u516c\u4e58\u673a\u5927\u58f0\u5bf9\u8239\u4e2d\u7684\u5ba2\u4eba\u9053\uff1a\u201c\u524d\u9762\u6709\u66f4\u9669\u7684\u5730\u65b9\uff0c\u4f17\u4eba\u8bf7\u628a\u884c\u674e\u653e\u5728\u8231\u5e95\u3002\u201d \u3000\u3000\u6562\u60c5\u4ed6\u662f\u60f3\u4ee5\u91cd\u91cf\u589e\u52a0\u7a33\u5ea6\u3002 \u3000\u3000\u8bdd\u65b9\u8bf4\u5b8c\uff0c\u8981\u5730\u90a3\u5cb8\u8fb9\u6c99\u6ee9\u6709\u4eba\u5927\u53eb\u9053\uff1a\u201c\u5582\uff01\u68a2\u516c\uff01\u8fc7\u6e21\u2014\u2014\u201d \u3000\u3000\u68a2\u516c\u5faa\u58f0\u4e00\u671b\uff0c\u53ea\u89c1\u6709\u4e00\u4e2a\u4eba\u7ad9\u5728\u8ddd\u8239\u7ea6\u6478\u516d\u4e08\u7684\u5cb8\u4e0a\uff0c\u5927\u58f0\u547c\u5524\u3002 \u3000\u3000\u8f9b\u6377\u773c\u5c16\uff0c\u5df2\u770b\u51fa\u90a3\u4eba\u5e74\u7ea6\u56db\u5341\u4e94\u516d\uff0c\u6253\u626e\u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u6ee1\u8138\u8679\u9aef\uff0c\u5374\u8eab\u7740\u4e00\u88ad\u4e66 \u751f\u5112\u888d\u3002 \u3000\u3000\u68a2\u516c\u5927\u58f0\u7b54\u9053\uff1a\u201c\u6e21\u8239\u5df2\u7ecf\u6ee1\uff0c\u600e\u80fd\u518d\u52a0\u4e00\u4eba\uff0c\u524d\u9762\u53ef\u662f\u5168\u7a0b\u4e2d\u6700\u9669\u7684\u5730\u6bb5\u201d \u3000\u3000\u90a3\u6c49\u5b50\u53eb\u9053\uff1a\u201c\u6211\u53ea\u662f\u4e00\u4eba\uff0c\u5e76\u6ca1\u6709\u884c\u674e\u2014\u2014\u201d \u3000\u3000\u68a2\u516c\u5df4\u4e0d\u8010\u70e6\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u770b\u89c1\u8fd9\u8b66\u6212\u7ebf\u5417\uff0c\u5403\u6c34\u5df1\u5230\u6700\u6df1\u7684\u5730\u6b65\u4e86\u3002\u201d\u8bf4\u7740 \u4e00\u6307\u8239\u5f26\u4e0a\u5212\u7684\u4e00\u9053\u767d\u7ebf\uff0c\u679c\u7136\u6c34\u5df2\u76d6\u5230\u7ebf\u9876\u4e86\u3002 \u3000\u3000\u8bf4\u5b8c\u53cc\u7bd9\u4e00\u6491\uff0c\u8239\u884c\u5982\u7bad\u3002 \u3000\u3000\u8f9b\u6377\u671b\u90a3\u4eba\uff0c\u53ea\u89c1\u4ed6\u8138\u4e0a\u6302\u7740\u4e00\u4ed8\u4e0d\u5c51\u7684\u51b7\u7b11\u3002 \u3000\u3000\u6c5f\u6c34\u6025\u5954\uff0c\u8239\u884c\u6108\u901f\u2014\u2014 \u3000\u3000\u84e6\u5730\u91cc\uff0c\u68a2\u516c\u5927\u53f1\u4e00\u58f0\u9053\uff1a\u201c\u6ce8\u610f\u4e86\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u95ea\u76ee\u4e00\u77a5\uff0c\u53ea\u89c1\u6c5f\u5fc3\u575a\u7acb\u7740\u4e00\u5757\u6781\u5927\u7684\u77f3\u5757\uff0c\u521a\u597d\u5360\u4f4f\u6c5f\u9762\u4e00\u534a\u5bbd\u7684\u5730\u65b9\uff0c\u53ea \u6709\u4e8c\u65c1\u53ef\u4ee5\u901a\u8239\u3002 \u3000\u3000\u77f3\u4e0a\u523b\u7740\u5dee\u522b\u5927\u7684\u4e09\u5b57\u2014\u2014\u201c\u671b\u6211\u6765\u3002\u201d \u3000\u3000\u90a3\u4e09\u5b57\u96d5\u523b\u7684\u751f\u52a8\u6709\u529b\u5df2\u6781\uff0c\u53ef\u8c13\u9b3c\u65a7\u795e\u5de5\uff0c\u4f46\u201c\u671b\u6211\u6765\u201d\u4e09\u5b57\u5374\u4e0d\u77e5\u4f55\u610f\u3002 \u3000\u3000\u6c5f\u6c34\u5728\u6b64\u5954\u817e\u76ca\u901f\uff0c\u6709\u82e5\u4e07\u9a6c\u5343\u519b\uff0c\u51b2\u6fc0\u5728\u5927\u77f3\u5757\u4e0a\u6e85\u8d77\u6781\u5927\u7684\u6d6a\u82b1\uff0c\u6784\u6210\u8db3\u4ee5\u4e00 \u53e3\u6c14\u4fbf\u541e\u6ca1\u4e00\u6761\u5927\u8239\u7684\u6f29\u6da1\uff0c\u9976\u662f\u8f9b\u6377\u4e00\u8eab\u7edd\u9876\u529f\u592b\uff0c\u4e5f\u4e0d\u7531\u6697\u6697\u5fc3\u60ca\uff01 \u3000\u3000\u8239\u884c\u5feb\u6781\uff0c\u4e0d\u5230\u7247\u523b\uff0c\u8ddd\u90a3\u77f3\u5757\u4ec5\u53ea\u4e94\u516d\u4e08\uff0c\u800c\u8239\u901f\u5374\u4e1d\u6beb\u4e0d\u56e0\u68a2\u516c\u7684\u62fc\u547d\u963b\u901f\u800c \u51cf\u3002 \u3000\u3000\u90a3\u68a2\u516c\u7d27\u5f20\u5df2\u6781\uff0c\u53cc\u624b\u63e1\u7bd9\uff0c\u84e6\u5730\u91cc\u5410\u6c14\u5f00\u58f0\uff0c\u201c\u563f\u201d\u5730\u5c06\u957f\u7bd9\u7528\u529b\u4e00\u6491\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u4e00\u8eab\u808c\u8089\u6709\u82e5\u8001\u6811\u6413\u85e4\uff0c\u4ea4\u9519\u51f8\u51fa\uff0c\u80cc\u4e0a\u68c9\u886b\u90fd\u4f3c\u5feb\u8981\u6323\u88c2\u4e00\u6837\u3002 \u3000\u3000\u8239\u884c\u672c\u901f\uff0c\u518d\u52a0\u4e0a\u4e00\u6491\uff0c\u7b80\u76f4\u6709\u5982\u98de\u884c\u2014\u2014 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u8f9b\u6377\u84e6\u95fb\u53ca\u8863\u6289\u98d8\u7a7a\u58f0\uff0c\u8f6c\u76ee\u4e00\u671b\uff0c\u4e0d\u7531\u5927\u60ca\u5931\u8272\uff0c\u4e00\u4e2a\u5e38\u4eba\u4e0d\u80fd\u7f6e\u4fe1 \u7684\u573a\u9762\u51fa\u73b0\u2014\u2014 \u3000\u3000\u90a3\u866c\u9aef\u6c49\u5b50\u5728\u8ddd\u79bb\u90a3\u4e48\u8fdc\u7684\u5730\u65b9\uff0c\u7834\u7a7a\u5189\u5189\u7eb5\u5411\u8239\u4e2d\uff0c\u65c5\u5ba2\u90fd\u65e9\u8eb2\u5728\u8231\u4e2d\uff0c\u68a2\u516c\u4e5f \u6b63\u5168\u795e\u5173\u6ce8\uff0c\u53ea\u6709\u8f9b\u6377\u4e00\u4eba\u770b\u89c1\uff0c\u90a3\u4eba\u597d\u72c2\uff0c\u8db3\u6b65\u865a\u5ffd\uff0c\u6765\u52bf\u975e\u5e38\u5feb\u901f\uff0c\u7adf\u7136\u6bd4\u90a3\u6025\u884c \u4e2d\u7684\u8239\u8fd8\u8981\u5feb\uff0c\u773c\u770b\u5c31\u8981\u843d\u5728\u8239\u4e0a\u3002 \u3000\u3000\u84e6\u5730\u91cc\u90a3\u4eba\u53cc\u8db3\u865a\u8e6c\uff0c\u8eab\u5f62\u53c8\u9022\u62d4\u8d77\u6570\u5c3a\uff0c\u4f3c\u4e4e\u6709\u610f\u627e\u90a3\u4e0d\u51c6\u4ed6\u4e0a\u8239\u7684\u68a2\u516c\u9ebb\u70e6\uff0c \u6a2a\u98d8\u8fc7\u53bb\uff0c\u7728\u773c\u4fbf\u843d\u5728\u90a3\u68a2\u516c\u6597\u5927\u7684\u7b3c\u5e3d\u7684\u5e3d\u6cbf\u4e0a\u3002 \u3000\u3000\u90a3\u4eba\u8f7b\u529f\u597d\u751f\u4e86\u5f97\uff0c\u771f\u53ef\u6bd4\u5f97\u4e00\u53f6\u5760\u5730\uff0c\u518d\u52a0\u4e0a\u68a2\u516c\u5168\u795e\u5173\u6ce8\u6491\u7bd9\u6273\u6d46\uff0c\u6839\u672c\u4e0d\u77e5 \u6709\u4e00\u4e2a\u5927\u6c49\u5df2\u7ad9\u5728\u81ea\u5df1\u5934\u9876\u3002\u8239\u884c\u5982\u98de\uff0c\u6c5f\u6ce2\u5fae\u8361\uff0c\u90a3\u4eba\u8eab\u8eaf\u597d\u50cf\u4e00\u5f20\u67af\u53f6\uff0c\u968f\u7740\u4e0a\u4e0b \u6447\u52a8\uff0c\u5374\u5e73\u8861\u5982\u5e38\u3002 \u3000\u3000\u8fd9\u4e00\u624b\u9732\u5f97\u5341\u5206\u9ad8\u660e\uff0c\u8fde\u8f9b\u6377\u6b64\u7b49\u529f\u592b\u90fd\u4e0d\u7531\u5fc3\u60ca\uff0c\u5c24\u5176\u662f\u5728\u5982\u6b64\u901f\u5ea6\u4e0b\uff0c\u90a3\u4eba\u7adf \u80fd\u51c6\u786e\u7684\u843d\u5728\u8239\u4e2d\uff0c\u8fd9\u4efd\u529f\u529b\u5b9e\u5728\u662f\u9a87\u95fb\u7684\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u8fd9\u6c49\u5b50\u7684\u529f\u592b\u5982\u6b64\u9ad8\u660e\uff0c\u5374\u72af\u4e86\u4e00\u70b9\u5356\u5f04\u7684\u6bdb\u75c5\uff0c\u5fc5\u975e\u6b63\u4eba\u541b\u5b50\uff0c \u770b\u4ed6\u5341\u5206\u6025\u4e8e\u8d76\u8def\uff0c\u4f1a\u4e0d\u4f1a\u662f\u53bb\u6e56\u5357\u548c\u4e10\u5e2e\u4f5c\u5bf9\u7684\u54e9\uff1f\u65e2\u7136\u884c\u52a8\u5982\u6b64\u5f20\u72c2\uff0c\u975e\u6253\u542c\u4ed6\u7684 \u76ee\u7684\u4e0d\u53ef\u3002\u201d\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u6709\u4e86\u8ba1\u7b56\u3002 \u3000\u3000\u6b63\u6c89\u541f\u95f4\uff0c\u90a3\u8239\u884c\u5f97\u597d\u5feb\uff0c\u5df2\u7ecf\u7b14\u76f4\u5954\u5411\u90a3\u77f3\u5757\u800c\u53bb\uff0c\u84e6\u5730\u91cc\u90a3\u68a2\u516c\u201c\u62cd\u201d\u7684\u653e\u4e0b \u6728\u6d46\uff0c\u7528\u53cc\u624b\u53bb\u638c\u8235\uff0c\u770b\u4ed6\u90a3\u6837\u5b50\uff0c\u662f\u8981\u4fdd\u6301\u76f4\u7ebf\u524d\u8fdb\u4f3c\u7684\u3002 \u3000\u3000\u6b63\u524d\u9762\u4fbf\u662f\u90a3\u5927\u77f3\u513f\uff0c\u8239\u513f\u5982\u679c\u8981\u76f4\u7ebf\u800c\u8fdb\uff0c\u5c82\u4e0d\u662f\u649e\u4e0a\u53bb\u8981\u7c89\u8eab\u788e\u9aa8\u5417\uff1f \u3000\u3000\u9976\u662f\u8f9b\u6377\u5b9a\u529b\u5982\u6b64\uff0c\u4e5f\u4e0d\u7531\u60ca\u53eb\u51fa\u58f0\uff0c\u90a3\u5356\u5f04\u8f7b\u529f\u7ad9\u5728\u68a2\u516c\u6597\u7b7e\u4e0a\u7684\u6c49\u5b50\u60f3\u6765\u4e5f\u662f \u4ece\u672a\u8d70\u8fc7\u6c34\u8def\uff0c\u4ed6\u8eab\u7acb\u9ad8\u5904\uff0c\u89c2\u7684\u683c\u5916\u6e05\u5207\uff0c\u4e0d\u7531\u5931\u60ca\u66b4\u53f1\u51fa\u58f0\uff01 \u3000\u3000\u8fd8\u6765\u4e0d\u53ca\u5f00\u53e3\u6012\u9a82\uff0c\u90a3\u8239\u513f\u5df2\u5bf9\u51c6\u201c\u671b\u6211\u6765\u201d\u7684\u5927\u77f3\u513f\u51b2\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u68a2\u516c\u5168\u8eab\u5fae\u8e72\uff0c\u60f3\u662f\u5168\u529b\u638c\u8235\uff0c\u4ecd\u7136\u4fdd\u6301\u7b14\u76f4\u524d\u8fdb\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u6c5f\u6c34\u5954\u5230\u8fd9 \u91cc\uff0c\u88ab\u5927\u77f3\u963b\u4f4f\uff0c\u5206\u4e3a\u4e8c\u652f\uff0c\u8239\u513f\u4e0d\u5dee\u4e00\u5206\u4e00\u5398\uff0c\u201c\u54d7\u5566\u201d\u4e00\u58f0\u66b4\u54cd\uff0c\u7d27\u7d27\u8d34\u7740\u5927\u767e\u53f3 \u4fa7\uff0c\u8d70\u4e86\u4e00\u4e2a\u201c\u4e4b\u201d\u5b57\u5f27\u5f62\uff0c\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u8f6c\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u5927\u6982\u662f\u5f2f\u513f\u8f6c\u5f97\u592a\u6025\uff0c\u8f9b\u6377\u89c9\u5f97\u8eab\u5b50\u4e00\u9635\u4e0d\u7a33\uff0c\u90a3\u8239\u513f\u7684\u5de6\u8fb9\u6df1\u6df1\u6d78\u5728\u6c34\u91cc\uff0c\u6c5f\u6c34 \u53ea\u5dee\u4e00\u4e8c\u5206\u6c34\u4fbf\u8981\u8fdb\u8231\uff0c\u800c\u90a3\u53f3\u5f26\u5374\u8fde\u8239\u5e95\u513f\u90fd\u7ffb\u9732\u5728\u6c34\u5e73\uff0c\u5047\u82e5\u6709\u751a\u4e48\u884c\u674e\u8fd8\u653e\u5728\u8239 \u5934\uff0c\u4e0d\u65e9\u5c31\u4f1a\u6454\u51fa\u8239\u8231\u624d\u602a\u54e9\uff01 \u3000\u3000\u7531\u4e8e\u901f\u5ea6\u548c\u79bb\u5fc3\u529b\u7684\u4f5c\u7528\uff0c\u6c5f\u6c34\u767b\u65f6\u88ab\u6253\u8d77\u4e00\u5927\u7247\u6765\uff0c\u8231\u4e2d\u4e58\u5ba2\u591a\u534a\u8d70\u60ef\u8fd9\u6761\u6c34\u8def\uff0c \u5e76\u6ca1\u6709\u4eba\u53d1\u51fa\u60ca\u547c\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u4e0d\u6599\u7adf\u662f\u5982\u6b64\uff0c\u91cd\u5fc3\u4e0d\u7a33\uff0c\u767b\u65f6\u8981\u6454\u51fa\u53bb\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8fde\u5fd9\u7eb5\u8eab\u4e0b\u8231\u6765\uff0c\u90a3 \u91cc\u77e5\u9053\u529b\u91cf\u592a\u5927\uff0c\u8eab\u4f53\u5c1a\u672a\u7740\u5730\uff0c\u4fbf\u6454\u51fa\u8231\u53bb\u3002 \u3000\u3000\u4ed6\u672c\u6765\u60f3\u7b49\u7740\u5730\u540e\u7acb\u523b\u4f7f\u51fa\u201c\u5343\u65a4\u5760\u201d\u7684\u529f\u592b\uff0c\u89c1\u52bf\u4e0d\u5bf9\uff0c\u84e6\u5730\u8eab\u5b50\u4e00\u5f13\uff0c\u767e\u5fd9\u4e2d \u4e00\u5e26\u90a3\u68a2\u516c\uff0c\u68a2\u516c\u5728\u5168\u795e\u638c\u8235\uff0c\u4ed6\u8fd9\u4e00\u5e26\u529b\u9053\u597d\u5927\uff0c\u68a2\u516c\u767b\u65f6\u7acb\u8db3\u4e0d\u7a33\uff0c\u8dcc\u51fa\u8231\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u5927\u6012\uff0c\u4f46\u6551\u4eba\u8981\u7d27\uff0c\u500f\u5730\u4f38\u53f3\u624b\u6293\u4f4f\u8239\u4e0a\u7684\u5f26\u7d22\uff0c\u4e00\u6491\u4e00\u7ffb\uff0c\u8eab\u4f53\u5df2\u98de\u4e86\u51fa\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u52a8\u4f5c\u597d\u5feb\uff0c\u7adf\u8d76\u5728\u90a3\u68a2\u516c\u7684\u524d\u5934\uff0c\u5de6\u624b\u95ea\u624b\u4e00\u6293\uff0c\u6b63\u64d2\u4f4f\u90a3\u68a2\u516c\u7684\u4e00\u53ea\u8db3\u8ddf\uff0c \u731b\u7684\u5f80\u6000\u4e2d\u4e00\u5e26\uff0c\u786c\u751f\u751f\u5c06\u4ed6\u98de\u51fa\u4e4b\u52bf\u62c9\u56de\uff0c\u653e\u56de\u8231\u91cc\u3002 \u3000\u3000\u90a3\u866c\u9aef\u6c49\u5b50\u5927\u7ea6\u81ea\u77e5\u7406\u5c48\uff0c\u5450\u5450\u5730\u7ad9\u5728\u4e00\u65c1\uff0c\u68a2\u516c\u65e9\u5df2\u5413\u5f97\u9762\u5982\u571f\u8272\uff0c\u5374\u59cb\u7ec8\u4e0d\u77e5 \u90a3\u6c49\u5b50\u600e\u4e48\u8fdb\u5165\u8239\u7684\u3002 \u3000\u3000\u8f9b\u6377\u6012\u54fc\u4e00\u58f0\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5f3a\u5fcd\u7740\u6012\u706b\uff0c\u5750\u4e86\u4e0b\u6765\uff0c\u56de\u9996\u77a5\u90a3\u201c\u671b\u6211\u6765\u201d\u7684\u5927\u77f3\u4e00 \u773c\uff0c\u53ea\u89c1\u6c34\u82b1\u66b4\u6e85\u4e2d\uff0c\u201c\u54d7\u5566\u201d\u4e00\u54cd\uff0c\u6562\u60c5\u53c8\u662f\u4e00\u53ea\u6e21\u8239\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u6e21\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u8fd9\u6c34\u4e0a\u7684\u64cd\u4f5c\u5012\u771f\u662f\u4e0d\u6613\uff0c\u521a\u624d\u82e5\u60f3\u8981\u8f6c\u8235\u95ea\u907f\u5927\u77f3\uff0c\u5c82\u4e0d\u521a\u597d\u4e0a\u524d \u9001\u6b7b\uff1f\u4e00\u5b9a\u8981\u4fdd\u6301\u5411\u5927\u77f3\u5782\u76f4\u65ad\u5411\u6025\u9a76\uff0c\u624d\u80fd\u6070\u5de7\u907f\u8fc7\uff0c\u5bf9\u90a3\u77f3\u513f\u6765\u8bf4\uff0c\u771f\u662f\u53ef\u8c13\u671b \u2018\u5b83\u2019\u800c\u6765\u4e86\uff0c\u60f3\u90a3\u7acb\u77f3\u7684\u4eba\u679c\u7136\u7528\u5fc3\u826f\u82e6\u2014\u2014\u201d \u3000\u3000\u6b63\u6c89\u541f\u95f4\uff0c\u53c8\u662f\u4e09\u56db\u53ea\u6e21\u8239\u5728\u6781\u5927\u7684\u503e\u5ea6\u4e0b\uff0c\u6e21\u8fc7\u9669\u5173\uff0c\u89c1\u90a3\u4e9b\u7ec8\u5e74\u64cd\u4f5c\u5728\u6c34\u4e0a\u7684 \u4eba\uff0c\u90fd\u4f3c\u4e0d\u5f53\u4f5c\u4e00\u56de\u4ec0\u4e48\u5f88\u96be\u7684\u4e8b\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u9635\u60ed\u6127\u3002 \u3000\u3000\u4e00\u9762\u80e1\u601d\u4e71\u60f3\uff0c\u90a3\u8239\u513f\u5df2\u5728\u5168\u901f\u4e0b\u9a70\u51fa\u5c06\u8fd1\u5eff\u591a\u91cc\uff0c\u773c\u770b\u4e09\u5ce1\u5728\u671b\uff0c\u4e58\u5ba2\u591a\u534a\u9884\u5907 \u6253\u68c0\u884c\u88c5\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u4e00\u60ca\uff0c\u8f6c\u76ee\u77a5\u89c1\u90a3\u866c\u9aef\u6c49\u4e5f\u81ea\u5750\u5728\u8239\u5f26\u4e0a\u6c89\u601d\uff0c\u60f3\u662f\u68a2\u516c\u89c1\u9669\u5173\u5df2\u8fc7\uff0c\u4e5f \u5e76\u4e0d\u518d\u5f3a\u4ed6\u4e0b\u8239\u3002 \u3000\u3000\u8f6c\u5ff5\u4e00\u601d\uff0c\u521a\u624d\u81ea\u5df1\u795e\u529f\u5c55\u9732\u4e4b\u65f6\uff0c\u597d\u50cf\u5e76\u6ca1\u6709\u4eba\u770b\u89c1\uff0c\u5f53\u4e0b\u7ad9\u8d77\u8eab\u9053\uff1a\u201c\u8fd9\u4f4d\u5144 \u53f0\u597d\u4fca\u7684\u8f7b\u8eab\u529f\u529b\u2014\u2014\u201d \u3000\u3000\u6562\u60c5\u4ed6\u662f\u60f3\u5957\u51fa\u90a3\u4eba\u7684\u884c\u8e2a\u3002 \u3000\u3000\u90a3\u4eba\u65e9\u5148\u89c1\u8f9b\u6377\u9732\u51fa\u4e00\u624b\uff0c\u672c\u5df2\u60ca\u5f02\u4e07\u5206\uff0c\u5374\u4e0d\u4fbf\u76f8\u8be2\uff0c\u8fd9\u65f6\u89c1\u8f9b\u6377\u4e3b\u52a8\u642d\u6c55\u6500\u8c08\uff0c \u65e9\u62b1\u7740\u4e00\u809a\u5b50\u7591\u95ee\uff0c\u62a2\u5148\u7b54\u9053\uff1a\u201c\u4e0d\u6562\uff0c\u4e0d\u6562\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u542c\u4ed6\u53e3\u97f3\u7adf\u50cf\u662f\u4e24\u5e7f\u4e00\u5e26\u7684\uff0c\u5fc3\u4e2d\u7591\u60d1\uff0c\u53e3\u4e2d\u7b54\u9053\uff1a\u201c\u6562\u95ee\u5144\u53f0\u8d35\u59d3\u5927\u540d\uff1f\u770b \u5144\u53f0\u8fd9\u6a21\u6837\u597d\u50cf\u6025\u7740\u8d76\u8def\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u7528\u5fc3\u89c2\u5bdf\u90a3\u6c49\u5b50\u7684\u8138\u8272\uff0c\u90a3\u6c49\u5b50\u5012\u662f\u795e\u8272\u4e0d\u53d8\uff0c\u6717\u58f0\u7b54\u9053\uff1a\u201c\u655d\u4eba\u59d3\u7fc1\uff0c \u5355\u540d\u6b63\uff0c\u9601\u4e0b\u8bf4\u5f97\u6b63\u5bf9\uff0c\u5728\u4e0b\u6b63\u8981\u8d76\u5230\u6e56\u5357\u53bb\u63a2\u770b\u53cb\u4eba\u5462\uff01\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u4e00\u51db\uff0c\u5fc3\u77e5\u6240\u6599\u591a\u534a\u662f\u5b9e\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4eba\u5982\u679c\u771f\u662f\u8d76\u53bb\u4e0e\u4e10\u5e2e\u4e3a\u654c\uff0c\u563f\uff01 \u91d1\u6c0f\u5144\u5f1f\u6050\u975e\u654c\u624b \u3000\u3000\u54e9\u3002\u201d\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u5c0f\u53ef\u8f9b\u6377\u4e5f\u6b63\u662f\u60f3\u53bb\u6e56\u5357\u7545\u6e38\u540d\u5c71\u5927\u6e56\u54e9\uff0c\u8fd9\u5012\u597d\uff0c\u5144\u53f0\u5982\u662f \u4e0d\u5f03\uff0c\u53ef\u5426\u540c\u9053\u800c\u884c\uff1f\u201d \u3000\u3000\u7fc1\u6b63\u5fd9\u7b54\u9053\uff1a\u201c\u8f9b\u5144\u90a3\u91cc\u7684\u8bdd\uff0c\u6709\u8f9b\u5144\u5982\u6b64\u529f\u592b\u7684\u4eba\u966a\u884c\uff0c\u4e0d\u4f46\u4e00\u8def\u5b89\u5168\u53ef\u9760\uff0c\u800c \u4e14\u53ef\u4ee5\u501f\u6b64\u8ba8\u6559\u54e9\u3002\u201d \u3000\u3000\u8f9b\u6377\u77e5\u9053\u4ed6\u7684\u610f\u601d\uff0c\u6b63\u662f\u5fc3\u4e2d\u4e0d\u670d\uff0c\u6709\u8f83\u91cf\u7684\u610f\u601d\uff0c\u4ec5\u4ec5\u6de1\u7136\u4e00\u7b11\uff0c\u968f\u53e3\u9053\uff1a\u201c\u90a3 \u91cc\uff0c\u5144\u5f1f\u7684\u529f\u592b\u90a3\u91cc\u53ca\u5f97\u4e0a\u7fc1\u5144\u5341\u5206\u4e4b\u4e00\uff01\u201d \u3000\u3000\u8bf4\u8bf4\u8c08\u8c08\uff0c\u8239\u513f\u5df2\u5230\u4e09\u5ce1\uff0c\u4e8c\u4eba\u4ed8\u8fc7\u8239\u94b1\uff0c\u4e00\u9f50\u5411\u6e56\u5357\u7701\u5883\u8d76\u53bb\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u8f9b\u6377\u66fe\u6570\u5ea6\u7528\u5de7\u8a00\u5708\u5957\uff0c\u7fc1\u6b63\u5374\u4e1d\u6beb\u4e0d\u9732\u53e3\u98ce\uff0c\u8f9b\u6377\u4e5f\u53ea\u597d\u76f8\u673a\u884c\u4e8b\uff0c\u4e0d\u9732 \u9a6c\u811a\u3002 \u3000\u3000\u4e8c\u4eba\u811a\u7a0b\u751a\u5feb\uff0c\u4e00\u8def\u4e0a\u7fc1\u6b63\u603b\u662f\u60f3\u548c\u8f9b\u6377\u6bd4\u8bd5\u811a\u7a0b\uff0c\u4f46\u8f9b\u6377\u603b\u662f\u4e00\u5473\u76f8\u8ba9\uff0c\u7fc1\u6b63\u5012 \u4e5f\u6ca1\u6709\u529e\u6cd5\u3002 \u3000\u3000\u8fd9\u5929\u508d\u665a\uff0c\u4e8c\u4eba\u5df2\u8d76\u8fdb\u4e86\u6e56\u5357\u7701\u5883\u5185\uff0c\u5b9e\u5728\u7d2f\u5f97\u5f88\uff0c\u4e8e\u662f\u51b3\u5fc3\u843d\u5e97\u6253\u5c16\uff0c\u597d\u5728\u5b98\u9053 \u5c3d\u5934\u4fbf\u662f\u4e00\u4e2a\u5c0f\u5c0f\u96c6\u9547\uff0c\u8d76\u5feb\u52a0\u7d27\u8db3\u6b65\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4fbf\u843d\u5165\u4e00\u5e97\u3002 \u3000\u3000\u6b63\u662f\u7528\u665a\u81b3\u7684\u65f6\u523b\uff0c\u4e8c\u4eba\u5fae\u4e00\u4f11\u606f\uff0c\u4fbf\u53eb\u5e97\u5bb6\u7528\u9910\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u51ac\u5b63\u4e2d\u671f\uff0c\u6e56\u5357\u8fd8\u597d\uff0c\u4e0d\u5341\u5206\u5bd2\uff0c\u4f46\u4e5f\u662f\u9634\u98ce\u5439\u6fc0\uff0c\u96ea\u82b1\u5fae\u98d8\u3002\u4e8c\u4eba\u5750\u5b9a\uff0c \u8981\u4e86\u4e00\u4efd\u70ed\u817e\u817e\u7684\u7c73\u9762\uff0c\u540c\u65f6\u4e5f\u8981\u4e86\u4e00\u65a4\u95fb\u540d\u5168\u56fd\u7684\u6e56\u5357\u814a\u83dc\u3002 \u3000\u3000\u679c\u7136\u540d\u4e0d\u865a\u4f20\uff0c\u4e8c\u4eba\u5403\u5f97\u5b9e\u5728\u5403\u4e0d\u4e0b\u7684\u6837\u5b50\u624d\u7f62\u624b\uff0c\u7b97\u4e00\u7b97\u5012\u4e5f\u5403\u4e86\u56db\u65a4\u814a\u8089\u3002 \u3000\u3000\u6b63\u5403\u5f97\u75db\u5feb\uff0c\u84e6\u5730\u91cc\u90a3\u53a2\u4e00\u4e2a\u7c97\u54d1\u55d3\u5b50\u7684\u8bf4\u9053\uff1a\u201c\u542c\u8bf4\u90a3\u5d06\u5cd2\u6d3e\u548c\u4e10\u5e2e\u5728\u672c\u7701\u4ea4\u6076\uff0c \u4e0d\u77e5\u662f\u771f\u8fd8\u662f\u5047\uff1f\u201d \u3000\u3000\u8bdd\u58f0\u6e05\u6670\u4f20\u6765\uff0c\u8f9b\u6377\u4e0d\u89c9\u5fae\u5fae\u4e00\u60ca\uff0c\u8d76\u5feb\u7559\u795e\u503e\u542c\uff0c\u8fd8\u987a\u773c\u77a5\u4e86\u90a3\u7fc1\u6b63\u4e00\u773c\uff0c\u679c\u89c1 \u4ed6\u4e5f\u662f\u5168\u795e\u8d2f\u6ce8\u3002\u8f9b\u6377\u5fc3\u4e2d\u6709\u6570\uff0c\u5df2\u77e5\u4ed6\u5fc5\u662f\u4e3a\u6b64\u4e8b\u800c\u6765\uff0c\u5374\u4e0d\u77e5\u548c\u4e10\u5e2e\u662f\u654c\u662f\u53cb\u3002 \u3000\u3000\u6b63\u6c89\u541f\u95f4\uff0c\u53e6\u5916\u4e00\u4e2a\u58f0\u97f3\u9053\uff1a\u201c\u563f\uff01\u9ec4\u8001\u5f1f\uff0c\u4f60\u6d88\u606f\u53ef\u771f\u592a\u4e0d\u7075\u4e86\uff0c\u522b\u8bf4\u4ea4\u6076\uff0c\u5d06 \u5cd2\u751a\u81f3\u6293\u4f4f\u4e86\u4e10\u5e2e\u7684\u65b0\u4e3b\u54e9\u2014\u2014\u201d \u3000\u3000\u8fd9\u4e2a\u6d88\u606f\uff0c\u8f9b\u6377\u5012\u662f\u5df2\u77e5\uff0c\u53ea\u542c\u90a3\u4eba\u7ee7\u7eed\u9053\uff1a\u201c\u6628\u5929\u542c\u8bf4\uff0c\u4e10\u5e2e\u8001\u5e2e\u4e3b\u7684\u62a4\u6cd5\u91d1\u6c0f \u5144\u5f1f\u53c8\u51fa\u4e86\u5c71\u54e9\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u542c\u4f17\u9010\u6e10\u51dd\u795e\u6ce8\u610f\uff0c\u5374\u542c\u90a3\u9010\u6e10\u5fae\u5f31\u7684\u58f0\u97f3\u65ad\u7eed\u4f20\u6765\u9053\uff1a\u201c\u91d1\u6c0f\u5144\u5f1f\u7684 \u529f\u592b\u4f60\u662f\u77e5\u9053\u7684\u2026\u2026\u542c\u8bf4\u2026\u2026\u4e00\u591c\u4e4b\u95f4\u95ef\u516d\u5173\u2026\u2026\u5d06\u5cd2\u5f1f\u5b50\u2026\u2026\u4e00\u584c\u7cca\u6d82\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u77e5\u91d1\u6c0f\u6606\u4ef2\u679c\u7136\u91cd\u65b0\u62a4\u6cd5\uff0c\u5927\u632f\u795e\u5a01\uff0c\u5fc3\u4e2d\u5fae\u5b89\uff0c\u4f46\u77a5\u89c1\u90a3\u7fc1\u6b63\u65f6\uff0c\u5374\u89c1\u4ed6 \u4e00\u8138\u9707\u60ca\u7684\u6837\u5b50\uff0c\u5fc3\u4e2d\u5df2\u7136\u786e\u5b9a\uff0c\u8fd9\u5bb6\u4f19\u5fc5\u7136\u662f\u548c\u4e10\u5e2e\u4e3a\u654c\u7684\u4e86\u3002 \u3000\u3000\u53c8\u542c\u5f97\u90a3\u59d3\u9ec4\u7684\u5c0f\u5b50\u7c97\u54d1\u7684\u8bf4\uff1a\u201c\u771f\u7684\u5417\uff1f\u8fd9\u5012\u662f\u4e00\u573a\u597d\u805a\u4f1a\uff0c\u54b1\u4eec\u53cd\u6b63\u6ca1\u4e8b\uff0c\u53ef \u5426\u8d76\u53bb\u4e00\u770b\uff1f\u8fd9\u4e2a\u70ed\u95f9\u51d1\u51d1\u5012\u4e5f\u4e0d\u59a8\u3002\u201d \u3000\u3000\u4ed6\u56e0\u4e3a\u55d3\u5b50\u8f83\u4e3a\u7c97\u54d1\uff0c\u662f\u4ee5\u58f0\u6d6a\u8f83\u9ad8\uff0c\u80fd\u591f\u6e05\u6670\u7684\u4f20\u5165\u8f9b\u6377\u548c\u7fc1\u6b63\u4e8c\u4eba\u7684\u8033\u4e2d\u3002 \u3000\u3000\u90a3\u53e6\u4e00\u4e2a\u58f0\u97f3\u652f\u51b7\u7b11\u4e00\u58f0\u9053\u3002\u201c\u4f60\u60f3\u4e48\uff1f\u201d \u3000\u3000\u90a3\u59d3\u9ec4\u7684\u6012\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u53e6\u4e00\u4eba\u9053\uff1a\u201c\u795e\u9706\u5854\u5468\u56f4\u4e94\u91cc\u5168\u7ed9\u4e8c\u65b9\u7684\u4eba\u7ed9\u6d3e\u4eba\u963b\u4f4f\u5566\uff0c\u8001\u767e\u59d3\u8fc7\u8def\u90fd\u4e0d\u653e\u4e00\u4e2a\uff0c \u50cf\u4f60\u8fd9\u4e00\u8eab\u6b66\u6797\u6253\u626e\u7684\u4eba\uff0c\u4eba\u5bb6\u80af\u653e\u4f60\u53bb\u51d1\u70ed\u95f9\uff1f\u201d \u3000\u3000\u90a3\u59d3\u9ec4\u7684\u54d1\u53e3\u65e0\u58f0\uff0c\u4e8c\u4eba\u5927\u6982\u8bdd\u4e0d\u6295\u673a\uff0c\u4e0d\u4e00\u4f1a\u4fbf\u53ea\u4f20\u6765\u4e00\u9635\u201c\u70ef\u54e9\u547c\u565c\u201d\u7684\u5403\u9762 \u58f0\u97f3\u3002 \u3000\u3000\u5730\u70b9\u65e2\u5df2\u77e5\u6653\uff0c\u8f9b\u6377\u5fc3\u4e2d\u81ea\u6709\u6253\u7b97\uff0c\u89c1\u90a3\u7fc1\u6b63\u8fd8\u4f4e\u5934\u6c89\u601d\uff0c\u4e0d\u7531\u51b2\u7740\u4ed6\u4e00\u7b11\u9053\uff1a \u201c\u6e56\u5357\u7684\u814a\u8089\u679c\u662f\u5339\u654c\u5168\u56fd\uff0c\u7fc1\u5144\u53ef\u4ee5\u4e3a\u662f\uff1f\u201d \u3000\u3000\u7fc1\u6b63\u5fae\u5fae\u4e00\u5b9a\u5fc3\u795e\uff0c\u5fd9\u7b54\u9053\uff1a\u201c\u81ea\u7136\uff01\u81ea\u7136\uff01\u5c0f\u5f1f\u4e5f\u6709\u540c\u611f\u3002\u201d \u3000\u3000\u4e8c\u4eba\u518d\u95f2\u8c08\u6570\u53e5\uff0c\u4e5f\u5c31\u5404\u81ea\u5f52\u623f\u3002 \u3000\u3000\u8f9b\u6377\u867d\u7136\u77e5\u9053\u53cc\u65b9\u4ea4\u6218\u7684\u5730\u65b9\u4e43\u662f\u5728\u795e\u9706\u5854\u4e2d\uff0c\u4f46\u5e76\u4e0d\u77e5\u795e\u9706\u5854\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u5fd9\u53bb \u8bf7\u95ee\u5e97\u4f19\uff0c\u597d\u5bb9\u6613\u624d\u5f04\u6e05\u695a\u539f\u6765\u6b63\u662f\u8ddd\u8fd9\u513f\u4e0d\u8fdc\u7684\u4e00\u5ea7\u5c71\u8fb9\uff0c\u5730\u4f4d\u5f88\u6709\u8352\u50fb\u3002 \u3000\u3000\u8f9b\u6377\u56de\u5230\u623f\u91cc\uff0c\u5fc3\u6f6e\u8d77\u4f0f\uff0c\u5fd6\u9053\uff1a\u201c\u4e10\u5e2e\u548c\u5d06\u5cd2\u597d\u50cf\u4ece\u6ca1\u6709\u4ec0\u4e48\u4ec7\u6076\uff0c\u4f46\u4e0a\u6b21\u5389\u9e57 \u4fbf\u66fe\u4eb2\u81ea\u8ffd\u51fb\u8fc7\u90a3\u91d1\u6c0f\u6606\u4ef2\u548c\u65b0\u5e2e\u4e3b\u9e4f\u513f\uff0c\u800c\u4e14\u597d\u50cf\u662f\u4e3a\u7740\u4e00\u67c4\u5251\u9798\u800c\u8d77\u7ea0\u7eb7\uff0c\u8fd9\u4e2a\u6211 \u5012\u4e0d\u7ba1\uff0c\u4e3b\u8981\u662f\u90a3\u53ef\u7231\u7684\u9e4f\u513f\u2014\u2014\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u773c\u524d\u53c8\u51fa\u73b0\u9e4f\u513f\u90a3\u6e05\u79c0\u800c\u53ef\u7231\u7684\u9762\u5bb9\u3002\u8f6c\u5ff5\u7ee7\u7eed\u4e0b\u53bb\uff0c\u5fd6\u9053\uff1a\u201c\u554a\uff0c\u90a3 \u7fc1\u6b63\u7a76\u7adf\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u6765\u5934\uff0c\u770b\u4ed6\u90a3\u5929\u6c5f\u4e0a\u7684\u8f7b\u8eab\u529f\u592b\u4f3c\u4e4e\u529f\u529b\u8fd8\u5728\u6211\u4e4b\u4e0a\u54e9\uff0c\u82e5\u662f\u548c \u4e10\u5e2e\u4e3a\u654c\uff0c\u91d1\u8001\u5927\uff0c\u91d1\u8001\u4e8c\u5012\u975e\u5e38\u5371\u9669\uff0c\u5374\u4e0d\u77e5\u5389\u8001\u8d3c\u662f\u600e\u4e48\u628a\u8fd9\u7b49\u4eba\u7269\u8bf7\u51fa\u6765\u7684\u3002\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u6697\u6697\u7d27\u5f20\uff0c\u5fd9\u76d8\u5750\u8fd0\u4e86\u4e00\u4e8c\u6b21\u529f\uff0c\u5b81\u5b9a\u5fc3\u795e\uff0c\u9661\u95fb\u8863\u6289\u7834\u7a7a\u58f0\uff0c\u6697 \u4e2d\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u4e2d\u77e5\u9053\u7fc1\u6b63\u5fc5\u7136\u5df2\u53bb\u795e\u9706\u5854\uff0c\u4e0d\u518d\u8fdf\u7591\uff0c\u62cd\u5f00\u7a97\u6237\uff0c\u5982\u98de\u8d76\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u5df2\u81ea\u5e97\u4f19\u90a3\u91cc\u95f4\u5f97\u795e\u9706\u5854\u6240\u5728\uff0c\u4e00\u8def\u98ce\u9a70\u7535\u9a76\uff0c\u679c\u7136\u4e0d\u4e45\u4fbf\u671b\u89c1\u4e0d\u8fdc\u524d\u4e00\u6761\u4eba \u5f71\u5982\u98de\u5954\u53bb\uff0c\u770b\u80cc\u5f71\u53ef\u4e0d\u662f\u90a3\u7fc1\u6b63\u7684\u6c49\u5b50\u5417\uff1f \u3000\u3000\u6562\u60c5\u662f\u4ed6\u8fd9\u5bb6\u4f19\u7c97\u4e2d\u6709\u7ec6\uff0c\u4e5f\u5411\u5e97\u4f19\u95ee\u6e05\u4e86\u8def\u9014\uff01 \u3000\u3000\u76ee\u6807\u65e2\u5df2\u53d1\u73b0\uff0c\u4e0d\u518d\u8fdf\u7591\uff0c\u84e6\u5730\u731b\u63d0\u4e8c\u53e3\u771f\u6c14\uff0c\u628a\u8f7b\u529f\u5c55\u5f00\u5341\u4e08\uff0c\u8db3\u4e0d\u70b9\u5730\uff0c\u5168\u901f \u8d76\u53bb\u3002 \u3000\u3000\u8d76\u8fd1\u524d\u4e86\uff0c\u7fc1\u6b63\u50cf\u662f\u4e0d\u77e5\u6709\u4eba\u8ddf\u8e2a\uff0c\u500f\u5730\u4e00\u8f6c\uff0c\u5411\u5de6\u8fb9\u4e00\u5806\u4e71\u77f3\u4e2d\uff0c\u8f9b\u6377\u77e5\u9053\u8f6c\u8fc7 \u4e71\u77f3\uff0c\u795e\u9706\u5854\u4fbf\u53ef\u5728\u671b\uff0c\u4e0d\u518d\u8fdf\u7591\uff0c\u201c\u547c\u201d\u7684\u98de\u7eb5\u8fc7\u53bb\u3002 \u3000\u3000\u6b63\u60f3\u8dc3\u8fc7\u4e71\u77f3\u8f6c\u5f2f\uff0c\u84e6\u5730\u4e00\u80a1\u5f3a\u52b2\u7684\u638c\u98ce\u8fce\u9762\u88ad\u6765\uff0c\u663e\u7136\u662f\u90a3\u7fc1\u6b63\u65e9\u5df2\u53d1\u73b0\u6709\u4eba\u8ddf \u8e2a\uff0c\u4e00\u638c\u5077\u88ad\u8fc7\u6765\u3002\u597d\u5728\u8f9b\u6377\u5012\u4e5f\u9632\u5230\u8fd9\u7740\uff0c\u8fde\u5fd9\u4e00\u632b\u8eab\u5f62\uff0c\u5374\u89c9\u8863\u886b\u730e\u730e\u4f5c\u54cd\uff0c\u6562\u60c5 \u662f\u638c\u98ce\u538b\u4f53\uff0c\u53ea\u5dee\u6570\u5bf8\u4fbf\u53ca\u5f97\u4e0a\u5730\u4f4d\u3002 \u3000\u3000\u767e\u5fd9\u4e2d\u731b\u63d0\u771f\u6c14\uff0c\u4e00\u638c\u865a\u62cd\uff0c\u5374\u662f\u7528\u4e86\u5341\u6210\u7684\u529b\u9053\uff0c\u638c\u98ce\u58f0\u52bf\u5012\u4e5f\u662f\u751a\u60ca\u4eba\u201c\u62cd\u201d \u5730\u4e00\u58f0\uff0c\u663e\u7136\u662f\u786c\u5bf9\u786c\uff0c\u8f9b\u6377\u8eab\u5b50\u5c1a\u5728\u7a7a\u4e2d\uff0c\u9661\u7136\u89c9\u5f97\u4e00\u80a1\u597d\u5927\u7684\u529b\u9053\u538b\u8eab\uff0c\u767b\u65f6\u88ab\u9707 \u843d\u5730\uff0c\u95ea\u773c\u770b\u90a3\u7fc1\u6b63\u65f6\uff0c\u5374\u4e5f\u8e09\u8dc4\u800c\u9000\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u6709\u6570\uff0c\u81ea\u5df1\u7684\u529b\u548c\u5bf9\u624b\u662f\u5b8c\u5168\u5e73\u624b\uff0c\u8fd9\u5012\u662f\u5f88\u5c11\u7684\u73b0\u8c61\u3002\u7fc1\u6b63\u5927\u5403\u4e00\u60ca\uff0c \u770b\u90a3\u6765\u4eba\u65f6\uff0c\u7adf\u662f\u548c\u81ea\u5df1\u5df2\u5171\u5bbf\u591a\u65e5\u7684\u8f9b\u6377\u3002 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c \u4e5d \u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c \u4e5d \u7ae0 \u3000\u3000\u8f9b\u6377\u5fc3\u60f3\u8fd8\u672a\u5230\u7834\u8138\u4e4b\u65f6\uff0c\u88c5\u4f5c\u5927\u60ca\u9053\uff1a\u201c\u539f\u6765\u662f\u7fc1\u5144\uff0c\u5c0f\u5f1f\u8e0f\u5bd2\u591c\u6e38\uff0c\u7fc1\u5144\u5012\u4ee4 \u6211\u5403\u4e86\u4e00\u60ca\u2014\u2014\u201d\u7fc1\u6b63\u6c14\u5728\u5fc3\u4e2d\uff0c\u8138\u4e0a\u53ef\u4e0d\u80fd\u8868\u73b0\u51fa\u6765\uff0c\u4fbf\u9053\uff1a\u201c\u5c0f\u5f1f\u53d1\u73b0\u4e00\u4e2a\u65e7\u65f6\u4ec7 \u4eba\uff0c\u662f\u4ee5\u8ffd\u6765\uff0c\u8f9b\u5144\u82e5\u662f\u65e0\u4e8b\uff0c\u6055\u5c0f\u5f1f\u5931\u966a\u2014\u2014\u201d\u8bdd\u97f3\u65b9\u843d\uff0c\u5df2\u52a8\u8eab\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u5f53\u9762\u6492\u8c0e\uff0c\u5012\u4e5f\u7f62\u4e86\uff0c\u53ef\u662f\u7fc1\u6b63\u5374\u5e76\u4e0d\u5f80\u4e71\u77f3\u5806\u4e2d\u8d70\u53bb\uff0c\u5374\u5411\u90a3\u4e00\u671b\u65e0\u9645 \u7684\u5d16\u9053\u4e0a\u76f4\u5954\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u5927\u60d1\u4e0d\u89e3\uff0c\u53c8\u4e0d\u597d\u52a8\u6b65\uff0c\u773c\u89c1\u4ed6\u8d8a\u8dd1\u8d8a\u8fdc\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4fbf\u6d88\u5931\u5728\u9ed1\u6697\u4e2d\uff0c\u5fc3\u4e2d \u4e00\u52a8\uff0c\u6025\u5fd9\u5faa\u8def\u800c\u53bb\uff0c\u5954\u5f97\u4e00\u76cf\u8336\u65f6\u523b\uff0c\u5df2\u53ef\u77a5\u89c1\u9053\u5de6\u4e00\u682a\u6811\u4e0a\u4f3c\u6709\u4e00\u70b9\u767d\u5f71\uff0c\u770b\u6765\u597d \u50cf\u6b63\u662f\u7fc1\u6b63\u7684\u8863\u8272\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u6562\u6020\u6162\uff0c\u731b\u529b\u4e00\u5954\uff0c\u8d70\u5230\u8fd1\u5904\uff0c\u5b9a\u76ee\u4e00\u770b\uff0c\u5374\u662f\u4e00\u88ad\u8863\u886b\u62ab\u6302\u5728\u6413\u679d\u4e0a\uff0c\u8fdc \u770b\u5f88\u50cf\u4e00\u4e2a\u4eba\u9690\u4f0f\u5728\u6811\u4e0a\uff0c\u8f9b\u6377\u5fc3\u77e5\u4e2d\u4e86\u7fc1\u6b63\u7684\u201c\u91d1\u8749\u8131\u58f3\u201d\u5999\u8ba1\uff0c\u5927\u611f\u60ed\u6127\uff0c\u5fd9\u5f80\u56de \u7a0b\u91cc\u731b\u8ffd\u3002 \u3000\u3000\u6309\u4e0b\u8fd9\u8fb9\u8f9b\u6377\u731b\u8ffd\u4e0d\u8868\uff0c\u4e14\u8bf4\u90a3\u91d1\u6c0f\u6606\u4ef2\u91d1\u5143\u4f2f\uff0c\u91d1\u5143\u4ef2\u4e8c\u4eba\u5f53\u65e5\u522b\u8fc7\u8f9b\u6377\uff0c\u4fbf\u8d76 \u5230\u6e56\u5357\u6765\uff0c\u4ed6\u4eec\u542c\u8bf4\u9e4f\u513f\u88ab\u56f0\uff0c\u5fc3\u4e2d\u90a3\u7126\u6025\u5c31\u591f\u53d7\u7684\uff0c\u771f\u53ef\u8c13\u201c\u8db3\u6b65\u4e0d\u505c\u201d\uff0c\u8db3\u8db3\u8d76\u4e86 \u4e00\u5929\u591a\u65f6\u95f4\uff0c\u624d\u8fdb\u5165\u7701\u5883\u3002 \u3000\u3000\u91d1\u6c0f\u6606\u4ef2\u4e00\u8e0f\u5927\u6e56\u5357\uff0c\u4fbf\u76f4\u5954\u795e\u9706\u5854\uff0c\u5374\u89c1\u90a3\u5854\u513f\u56db\u5468\u90fd\u7ad9\u6ee1\u4eba\uff0c\u7ec6\u5fc3\u4e00\u770b\uff0c\u5374\u662f \u4e10\u5e2e\u5357\u5206\u8235\u7684\u5e2e\u4e3b\u3002 \u3000\u3000\u539f\u6765\u4e10\u5e2e\u5206\u4e3a\u4e8c\u8235\uff0c\u4e00\u5728\u5317\uff0c\u4e00\u5728\u5357\uff0c\u5317\u5e2e\u4e5f\u5c31\u662f\u603b\u5e2e\u6240\u5728\uff0c\u5185\u5e2e\u5374\u5728\u6e58\u7ca4\u4e00\u5e26\uff0c \u8fd9\u5357\u90ed\u542c\u89c1\u603b\u5e2e\u4e3b\u7adf\u7136\u88ab\u6355\uff0c\u90a3\u80fd\u4e0d\u6025\uff0c\u5e2e\u4e3b\u9646\u52c7\u7adf\u5728\u4e00\u4e2a\u65f6\u8fb0\u95f4\u8c03\u52a8\u5168\u4f53\u4eba\u4f17\uff0c\u628a\u4e00 \u4e2a\u795e\u9706\u5854\u56f4\u5f97\u6c34\u6cc4\u4e0d\u901a\u3002 \u3000\u3000\u4f46\u662f\u795e\u5efa\u5854\u4e00\u5171\u9ad8\u4e00\u5341\u4e09\u5c42\uff0c\u5d06\u5cd2\u6d3e\u5728\u6bcf\u4e00\u5c42\u90fd\u8bbe\u5173\u5361\uff0c\u800c\u5728\u5854\u5e95\u7684\u5c0f\u6797\u5b50\u4e2d\u3002\u4e5f \u57cb\u4f0f\u4e0d\u5c11\u9ad8\u624b\uff0c\u9646\u52c7\u529f\u592b\u867d\u7136\u4e0d\u9519\uff0c\u4f46\u662f\u5bf9\u65b9\u5f3a\u4eba\u592a\u591a\uff0c\u53ea\u597d\u50f5\u6301\u4e00\u65c1\u3002 \u3000\u3000\u8fd9\u6837\u8017\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u9646\u52c7\u4e0d\u518d\u72b9\u7591\uff0c\u51c6\u5907\u5355\u5200\u8d74\u4f1a\uff0c\u6b63\u5728\u8fd9\u65f6\u5019\uff0c\u91d1\u6c0f\u6606\u4ef2\u8d76\u5230\uff0c \u4e09\u4e2a\u4eba\u4e00\u4f1a\u548c\uff0c\u90a3\u8fd8\u7ba1\u4ed6\u4ec0\u4e48\u660e\u5173\u6697\u5361\uff0c\u594b\u529b\u5411\u4e0a\u731b\u653b\uff0c\u5374\u7ea6\u675f\u5e2e\u4f17\u4e0d\u8981\u4e71\u6253\u4e71\u653b\uff0c\u53ea \u9759\u9759\u7684\u5b88\u5728\u4e0b\u9762\u4fbf\u662f\u3002 \u3000\u3000\u91d1\u6c0f\u6606\u4ef2\u5947\u529f\u8fc7\u4eba\uff0c\u4e00\u591c\u4e4b\u4e2d\u8fde\u8fc7\u516d\u5173\uff0c\u800c\u4e14\u4e0b\u624b\u6beb\u4e0d\u7559\u60c5\uff0c\u516d\u5173\u654c\u4eba\uff0c\u5168\u90e8\u90fd\u6253 \u5f97\u975e\u6b7b\u5373\u4f24\u3002 \u3000\u3000\u5230\u4e86\u7b2c\u4e03\u5c42\u5854\u4e0a\uff0c\u5374\u9047\u5230\u5b88\u5173\u7684\u4eba\u4e43\u662f\u5d06\u5cd2\u4e09\u7edd\u5251\u6240\u5e03\u7684\u201c\u4e09\u624d\u5251\u9635\u201d\uff0c\u529b\u6218\u4e4b\u4e0b\uff0c \u5927\u7ea6\u82e6\u6597\u4e86\u4e00\u4e2a\u65f6\u8fb0\uff0c\u4e09\u7edd\u5251\u624d\u4e0d\u654c\u9000\u540e\uff0c\u800c\u91d1\u6c0f\u5f1f\u5144\u548c\u9646\u52c7\uff0c\u5374\u4e5f\u662f\u771f\u6c14\u4e0d\u6d4e\u3002 \u3000\u3000\u4e8e\u662f\u4e09\u4eba\u5728\u5854\u4e0a\u9759\u606f\uff0c\u800c\u5bf9\u65b9\u4e5f\u4e0d\u6562\u5192\u7136\u52a8\u624b\uff0c\u4e00\u8017\u4e4b\u4e0b\uff0c\u53c8\u53bb\u4e86\u5927\u534a\u5929\u3002 \u3000\u3000\u91d1\u6c0f\u6606\u4ef2\u5fc3\u77e5\u654c\u4eba\u4e00\u5173\u5f3a\u4f3c\u4e00\u5173\uff0c\u81ea\u5df1\u8981\u5f3a\u95ef\u4e0a\u53bb\uff0c\u662f\u4e0d\u53ef\u80fd\uff0c\u4f46\u5929\u751f\u7684\u5f3a\u6027\u548c\u9646 \u52c7\u4e0d\u987e\u6b7b\u7684\u6027\u683c\uff0c\u4e09\u4eba\u4ecd\u7136\u820d\u547d\u4e0a\u95ef\u3002 \u3000\u3000\u654c\u4eba\u679c\u7136\u662f\u4e0d\u51fa\u6240\u6599\uff0c\u8d8a\u6765\u8d8a\u5f3a\uff0c\u9547\u5b88\u7b2c\u4e5d\u5c42\u5854\u7684\u662f\u56db\u4e2a\u4eba\uff0c\u91d1\u6c0f\u6606\u4ef2\u8840\u6218\u4e4b\u4e0b\uff0c \u8fde\u6bd9\u56db\u4eba\uff0c\u800c\u9646\u52c7\u906d\u5230\u81f4\u547d\u7684\u6253\u51fb\uff0c\u53ea\u80fd\u9000\u5728\u4e00\u8fb9\u4e86\u3002 \u3000\u3000\u91d1\u5143\u4f2f\uff0c\u91d1\u5143\u4ef2\u597d\u4e0d\u60b2\u4f24\uff0c\u8fd8\u62b1\u7740\u4e00\u7ebf\u5e0c\u671b\uff0c\u4fef\u8eab\u62b1\u8d77\u9646\u52c7\uff0c\u6b63\u51c6\u5907\u7ee7\u7eed\u5f80\u4e0a\u95ef\uff0c \u84e6\u5730\u91cc\u7978\u8d77\u8427\u5899\uff0c\u4e0a\u9762\u6709\u4eba\u7528\u6697\u5668\u6253\u4e86\u4e0b\u6765\uff0c\u91d1\u5143\u4ef2\u4e00\u624b\u62b1\u7740\u9646\u52c7\uff0c\u4e00\u624b\u53bb\u62e8\u6253\u6697\u5668\uff0c \u4f46\u7ec8\u4e0d\u6599\u654c\u4eba\u7684\u6697\u5668\u4e2d\u8fd8\u52a0\u6709\u98de\u714c\u9556\u8fd9\u7c7b\u53ef\u4ee5\u56de\u98de\u7684\u6697\u5668\uff0c\u91d1\u5143\u4ef2\u95ea\u8eb2\u4e0d\u53ca\uff0c\u773c\u770b\u90a3\u9556 \u513f\u4fbf\u8981\u9489\u4eba\u80cc\u5fc3\u4e0a\u3002 \u3000\u3000\u9646\u52c7\u84e6\u5730\u91cc\u5927\u543c\u4e00\u58f0\uff0c\u7528\u5c3d\u5e73\u751f\u4e4b\u529b\uff0c\u6323\u8131\u91d1\u5143\u4ef2\u7684\u6000\u62b1\uff0c\u8df3\u5728\u91d1\u5143\u4ef2\u7684\u80cc\u4e0a\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u201c\u5413\u201d\u7684\u4e00\u54cd\uff0c\u90a3\u9556\u513f\u9489\u7acb\u9646\u52c7\u80cc\u4e2d\uff0c\u9646\u52c7\u72c2\u547c\u4e00\u58f0\uff0c\u767b\u65f6\u6c14\u7edd\uff0c \u4f46\u603b\u7b97\u6551\u4e86\u91d1\u5143\u4ef2\u4e00\u547d\u3002 \u3000\u3000\u91d1\u6c0f\u5144\u5f1f\u4f55\u7b49\u6027\u60c5\uff0c\u60b2\u6781\u5374\u4e0d\u6ee1\u6cea\uff0c\u91d1\u5143\u4ef2\u6717\u58f0\u9053\uff1a\u201c\u9646\u8001\u5f1f\uff0c\u8fd9\u7b14\u4ec7\u6211\u91d1\u5143\u4ef2\u5fc5 \u5728\u4e00\u523b\u4e4b\u5185\u62a5\u5374\uff01\u201d \u3000\u3000\u8bdd\u58f0\u65a9\u94c1\u65ad\u9489\uff0c\u4e8c\u4eba\u5927\u8e0f\u6b65\u8d70\u4e0a\u697c\u68af\u3002 \u3000\u3000\u91d1\u5143\u4ef2\u5927\u58f0\u559d\u9053\uff1a\u201c\u8fd9\u652f\u98de\u714c\u9556\u513f\u662f\u90a3\u4e2a\u4e0d\u8981\u8138\u7684\uff1f\u201d \u3000\u3000\u90a3\u5854\u4e0a\u5374\u53ea\u7ad9\u6709\u4e8c\u4eba\uff0c\u91d1\u5143\u4ef2\u8bc6\u5176\u4e00\uff0c\u5374\u662f\u540d\u9707\u4e1c\u5357\u4e00\u5e26\u7684\u201c\u795e\u9556\u65ad\u9b42\u201d\u5434\u94ed\u3002 \u3000\u3000\u91d1\u5143\u4ef2\u8bdd\u5df1\u51fa\u53e3\uff0c\u90a3\u4e8c\u4eba\u90fd\u4e0d\u89c9\u4e00\u6014\uff0c\u90a3\u53e6\u5916\u4e00\u4e2a\u4eba\u65a5\u9053\uff1a\u201c"\u4ec0\u4e48\u4e1c\u897f\uff0c\u563f\uff0c\u770b \u6211\u4e00\u638c\u201d \u3000\u3000\u547c\u5730\u4e00\u638c\u5288\u6765\u3002 \u3000\u3000\u91d1\u5143\u4ef2\u5fc3\u4e2d\u9690\u75db\u9646\u52c7\u4e4b\u6b7b\uff0c\u5168\u90e8\u6012\u6c14\u53d1\u6cc4\u51fa\u6765\uff0c\u89c1\u5bf9\u65b9\u6765\u52bf\u6c79\u6c79\uff0c\u201c\u563f\u201d\u7136\u4e00\u6293\uff0c \u4e5f\u662f\u5168\u529b\u786c\u649e\u8fc7\u53bb\u3002 \u3000\u3000\u8981\u77e5\u91d1\u6c0f\u6606\u4ef2\u884c\u9053\u6c5f\u6e56\uff0c\u4ece\u6765\u4e0d\u7528\u5175\u5203\uff0c\u4ec5\u51ed\u4e00\u53cc\u624b\u722a\uff0c\u65bd\u7528\u201c\u9634\u98ce\u9ed1\u6c99\u638c\u201d\u548c\u654c \u4eba\u786c\u62fc\uff0c\u4f46\u89c1\u91d1\u5143\u4ef2\u5355\u6293\u4e00\u7ffb\u4e00\u53e9\uff0c\u201c\u556a\u201d\u7684\u4e00\u9707\uff0c\u5df2\u628a\u90a3\u5bb6\u4f19\u7684\u53f3\u81c2\u6d3b\u6d3b\u6253\u65ad\u3002 \u3000\u3000\u91d1\u5143\u4ef2\u5fc3\u4e2d\u6012\u6c14\u6f8e\u62dc\uff0c\u6293\u4f4f\u90a3\u4eba\u4e00\u6325\uff0c\u529b\u9053\u597d\u5927\uff0c\u4f46\u89c1\u90a3\u4eba\u50cf\u4e00\u652f\u7bad\u822c\u88ab\u6454\u5230\u5854\u8fb9\uff0c \u767b\u662f\u8111\u58f3\u7834\u88c2\uff0c\u8840\u8089\u6a2a\u98de\u3002\u91d1\u5143\u4ef2\u4e00\u7167\u9762\u4fbf\u51fb\u6bd9\u5bf9\u65b9\uff0c\u51b7\u7136\u4e00\u54fc\u9053\uff1a\u201c\u5434\u94ed\uff0c\u8fd9\u9556\u513f\u53ef \u662f\u4f60\u5a01\u9707\u4e1c\u5357\u7684\u4e1c\u897f\uff1f\u201d \u3000\u3000\u4f46\u89c9\u4ed6\u8bed\u6c14\u6b63\u4e49\u51db\u7136\uff0c\u5a01\u98ce\u51db\u51db\uff0c\u5434\u94ed\u89c1\u4ed6\u6253\u6b7b\u540c\u4f34\u7684\u5a01\u529b\uff0c\u4e0d\u7531\u5fc3\u602f\uff0c\u4f46\u95fb\u4ed6\u53e3 \u6c14\u8f7b\u72c2\uff0c\u6012\u706b\u4e0a\u5347\uff0c\u6709\u9053\u662f\uff1a\u201c\u6012\u4ece\u5fc3\u5934\u8d77\uff0c\u6076\u5411\u80c6\u8fb9\u751f\u201d\uff0c\u5927\u53f1\u9053\uff1a\u201c\u91d1\u8001\u4e8c\uff0c\u662f\u53c8 \u600e\u6837\u2014\u2014\u201d \u3000\u3000\u91d1\u5143\u4ef2\u6b63\u8981\u4ed6\u8fd9\u53e5\u8bdd\uff0c\u4e0d\u5f85\u4ed6\u8bf4\u5b8c\uff0c\u5df2\u662f\u51b7\u7b11\u9053\uff1a\u201c\u662f\u7684\u8bdd\uff0c\u4fbf\u8981\u4f60\u547d\u3002\u201d \u3000\u3000\u201c\u547d\u201d\u5b57\u624d\u4e00\u51fa\u53e3\uff0c\u4e8c\u638c\u4e00\u5408\u518d\u5410\uff0c\u7adf\u662f\u5fae\u5e26\u98ce\u96f7\u4e4b\u58f0\u3002 \u3000\u3000\u5434\u94ed\u4e0d\u6562\u5927\u610f\uff0c\u4e00\u638c\u7ad6\u7acb\uff0c\u4e00\u638c\u6a2a\u5288\u3002 \u3000\u3000\u90a3\u77e5\u91d1\u5143\u4ef2\u5de6\u80a9\u4e00\u8038\uff0c\u4e0d\u95ea\u4e0d\u8eb2\uff0c\u7adf\u4f3c\u8981\u786c\u53d7\u4e00\u638c\u3002 \u3000\u3000\u5434\u94ed\u5fc3\u77e5\u4e0d\u5999\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u6536\u638c\u5df2\u81ea\u4e0d\u53ca\uff0c\u53ea\u89c9\u201c\u62cd\u201d\u5730\u4e00\u638c\uff0c\u7ed3\u7ed3\u5b9e\u5b9e\u6253\u5728\u5bf9\u65b9 \u80a9\u4e0a\uff0c\u800c\u91d1\u5143\u4ef2\u7684\u4e00\u6293\u4e5f\u6293\u8fdb\u4e86\u5434\u94ed\u7684\u5929\u7075\uff01\u91d1\u5143\u4ef2\u5f62\u8fd1\u62fc\u547d\uff0c\u62fc\u7740\u81ea\u5df1\u53d7\u521b\uff0c\u4e5f\u628a\u654c \u4eba\u6bd9\u4e0b\uff0c\u4ed6\u53d7\u7684\u4e00\u638c\u5374\u4e5f\u4e0d\u8f7b\uff0c\u4f46\u89c9\u5de6\u80a9\u5267\u75db\uff0c\u80a9\u80bf\u9aa8\u786c\u751f\u751f\u88ab\u6253\u788e\u3002 \u3000\u3000\u91d1\u5143\u4ef2\u6643\u4e86\u4e00\u6643\uff0c\u7ec8\u4e8e\u7ad9\u5b9a\uff0c\u72c2\u7b11\u9053\uff1a\u201c\u9646\u8001\u5f1f\uff0c\u4f60\u770b\u770b\u5427\uff0c\u8fd9\u4e2a\u5bb6\u4f19\u4e5f\u53ea\u6bd4\u4f60\u591a \u6d3b\u4e86\u4e0d\u5230\u4e00\u523b\u65f6\u5206\u5566\u2014\u2014\u54c8\u2014\u2014\u201d \u3000\u3000\u91d1\u8001\u5927\u77e5\u9053\u5f1f\u5f1f\u7684\u6027\u683c\uff0c\u5e76\u4e0d\u51fa\u8a00\uff0c\u7b49\u5230\u4ed6\u72c2\u7b11\u53d8\u54ed\u7684\u65f6\u5019\uff0c\u624d\u6c89\u58f0\u9053\uff1a\u201c\u8001\u4e8c\uff0c \u8fd8\u5403\u5f97\u4f4f\u5417\uff1f\u201d \u3000\u3000\u91d1\u5143\u4ef2\u5fae\u5fae\u70b9\u5934\uff0c\u91d1\u5143\u4f2f\u51b7\u7b11\u9053\uff1a\u201c\u786c\u95ef\uff01\u201d \u3000\u3000\u4e8c\u4eba\u8eab\u5b50\u4e00\u6643\uff0c\u53c8\u76f4\u5411\u4e0a\u51b2\u3002 \u3000\u3000\u91d1\u6c0f\u5144\u5f1f\u5982\u6b64\u786c\u95ef\uff0c\u4e0d\u5230\u534a\u523b\u3002\u4fbf\u5230\u5854\u9876\uff0c\u4ece\u9636\u4e0a\u5f80\u4e0a\u770b\uff0c\u5df1\u53ef\u89c1\u5230\u9547\u5b88\u6700\u4e0a\u9762\u7684 \u4e00\u4e2a\u4eba\uff0c\u679c\u7136\u662f\u4e00\u624b\u6293\u4f4f\u4e00\u4e2a\u660f\u8ff7\u7684\u5b69\u513f\u2014\u2014\u90a3\u6b63\u662f\u88ab\u95ed\u4f4f\u7a74\u9053\u7684\u4e10\u5e2e\u5e2e\u4e3b\u9e4f\u513f\u3002 \u3000\u3000\u7b2c\u5341\u4e09\u5c42\u4e43\u662f\u795e\u9706\u4e4b\u9876\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u8001\u5927\u4e00\u811a\u8e39\u5f00\u697c\u95e8\uff0c\u5411\u91cc\u9762\u9ed1\u6c89\u6c89\u697c\u68af \u671b\u4e86\u4e00\u773c\uff0c\u4e00\u78b0\u91d1\u8001\u4e8c\uff0c\u53cc\u53cc\u8dc3\u8eab\u800c\u8fdb\u2014\u2014 \u3000\u3000\u4e24\u4eba\u5c1a\u672a\u843d\u5730\uff0c\u5ffd\u7136\u4e00\u58f0\u66b4\u543c\u4ece\u5de6\u65b9\u54cd\u8d77\uff1a\u201c\u6eda\u4e0b\u53bb\uff01\u201d\u63a5\u7740\u4e00\u80a1\u72c2\u98ce\u5982\u60ca\u6d9b\u88c2\u5cb8 \u822c\u51b2\u51fb\u8fc7\u6765\u2014\u2014 \u3000\u3000\u91d1\u8001\u5927\u771f\u6c14\u6597\u8d2f\u4e0b\u76d8\uff0c\u65bd\u51fa\u201c\u5343\u65a4\u9524\u201d\u7684\u529f\u592b\uff0c\u5c06\u8eab\u8eaf\u7a33\u7a33\u5b9a\u4f4f\uff0c\u5355\u638c\u770b\u90fd\u4e0d\u770b\u4e00 \u8bb0\u201c\u5012\u6253\u91d1\u949f\u201d\u5012\u6454\u8fc7\u53bb\u3002 \u3000\u3000\u90a3\u77e5\u6765\u4eba\u52a8\u4e5f\u4e0d\u52a8\uff0c\u91d1\u8001\u5927\u5012\u53cd\u88ab\u62d6\u51fa\u4e24\u6b65\uff01 \u3000\u3000\u5144\u5f1f\u4fe9\u4e00\u60ca\u8f6c\u8eab\uff0c\u548c\u6765\u4eba\u671d\u4e86\u76f8\uff0c\u53ea\u89c1\u90a3\u4eba\u52fe\u9f3b\u88c2\u5634\uff0c\u76ee\u5149\u95ea\u70c1\uff0c\u5144\u5f1f\u4e24\u4eba\u90fd\u8bc6\u5f97\uff0c \u6765\u4eba\u7adf\u662f\u52fe\u6f0f\u5c71\u7684\u9b54\u5934\u2014\u2014\u201c\u9752\u773c\u7ea2\u9b54\u201d\u970d\u5982\u98de\uff01 \u3000\u3000\u539f\u6765\u52fe\u6f0f\u5c71\u4e0a\u9690\u5c45\u7740\u4e24\u4e2a\u76d6\u4e16\u9b54\u5934\uff0c\u4e00\u4e2a\u5524\u7740\u52fe\u6f0f\u4e00\u602a\u7fc1\u6b63\uff0c\u53e6\u4e00\u4e2a\u5c31\u662f\u201c\u9752\u773c\u7ea2 \u9b54\u201d\uff0c\u4e24\u4eba\u4e43\u662f\u5e08\u5144\u5f1f\uff0c\u4e5f\u4e0d\u77e5\u51fa\u81ea\u4f55\u95e8\uff0c\u4f46\u4e00\u8eab\u529f\u592b\u5374\u7cbe\u7edd\u65e0\u6bd4\uff0c\u4e09\u5341\u5e74\u524d\u66fe\u53cc\u53cc\u51fa \u73b0\u6b66\u6797\uff0c\u5728\u5317\u56fa\u5c71\u5934\u4e00\u591c\u8fde\u632b\u6cb3\u6d1b\u5341\u4e8c\u4f4d\u9ad8\u624b\uff0c\u56e0\u800c\u5e08\u5144\u5f1f\u540d\u566a\u4e00\u65f6\uff0c\u4f46\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48 \u4e8b\uff0c\u7a81\u7136\u53cc\u53cc\u9690\u5c45\uff0c\u90a3\u201c\u9752\u773c\u7ea2\u9b54\u5934\u201d\u4e0d\u65f6\u51fa\u73b0\u6c5f\u6e56\uff0c\u201c\u52fe\u6f0f\u4e00\u602a\u201d\u5219\u5345\u5e74\u6765\u672a\u51fa\u6df1\u5c71 \u534a\u6b65\uff0c\u4f46\u5728\u6b66\u6797\u8001\u4e00\u8f88\u7684\u5fc3\u4e2d\uff0c\u4ecd\u7136\u6709\u7740\u4e0d\u53ef\u4e00\u4e16\u7684\u5a01\u540d\u3002 \u3000\u3000\u91d1\u8001\u5927\u4e00\u89c1\u539f\u6765\u7adf\u662f\u8fd9\u4e2a\u9b54\u5934\uff0c\u5fc3\u4e2d\u5df2\u77e5\u51ed\u81ea\u5df1\u4e00\u4eba\u4e4b\u529b\uff0c\u5fc5\u975e\u5176\u654c\u624b\uff0c\u4f46\u4e0d\u77e5\u8fd9 \u5bb6\u4f19\u600e\u4e48\u7adf\u4f1a\u5728\u8fd9\u513f\u51fa\u73b0\uff01 \u3000\u3000\u84e6\u7136\u4e00\u4e2a\u5ff5\u5934\u95ea\u8fc7\u5fc3\u5934\uff1a\u201c\u5206\u660e\u662f\u5d06\u5cd2\u6d3e\u548c\u54b1\u4eec\u7684\u6881\u5b50\uff0c\u600e\u4e48\u8fd9\u53ae\u5374\u6765\u5b88\u7b2c\u5341\u4e09\u5173\uff1f \u90a3\u5389\u9e57\u5374\u4e0d\u9732\u9762\uff1f\u800c\u4e14\u65b9\u624d\u90a3\u4e9b\u9f9f\u513f\u5b50\u5927\u90e8\u4efd\u90fd\u4e0d\u4f3c\u5d06\u5cd2\u5f1f\u5b50\u5462\uff1f\u201d \u3000\u3000\u9752\u773c\u7ea2\u9b54\u970d\u5982\u98de\u9634\u607b\u607b\u5730\u9053\uff1a\u201c\u4e24\u4e2a\u9b3c\u5b50\u9f50\u4e0a\uff01\u5426\u5219\u4f60\u4e0d\u662f\u5bf9\u624b\uff01\u201d \u3000\u3000\u91d1\u8001\u5927\u4e00\u626f\u5144\u5f1f\u8863\u886b\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u53cc\u53cc\u65bd\u51fa\u5e73\u751f\u7edd\u5b66\u201c\u9634\u98ce\u9ed1\u6c99\u638c\u201d\uff0c\u72e0\u6bd2\u7684\u62db\u5f0f \u5c3d\u91cf\u5f80\u970d\u5982\u98de\u8eab\u4e0a\u62db\u547c\u8fc7\u53bb\u3002 \u3000\u3000\u970d\u5982\u98de\u51b7\u54fc\u4e00\u58f0\uff0c\u53cc\u62f3\u4e00\u7acb\uff0c\u9f13\u52b2\u800c\u4e0a\u2014\u2014 \u3000\u3000\u970e\u65f6\u62f3\u811a\u6765\u5f80\uff0c\u547c\u547c\u98ce\u751f\uff0c\u4e09\u4e2a\u4e00\u6d41\u597d\u624b\u7adf\u81ea\u6218\u6210\u5e73\u624b\u3002 \u3000\u3000\u8fd9\u4e09\u4e2a\u8eab\u6cd5\u4f55\u7b49\u5feb\u6377\uff0c\u4e00\u6643\u5c31\u662f\u6570\u5341\u62db\u8fc7\u53bb\uff0c\u91d1\u8001\u4e8c\u53ea\u89c9\u80a9\u4e0a\u4f24\u52bf\u6108\u6765\u6108\u75db\uff0c\u7b80\u76f4 \u6709\u70b9\u652f\u6301\u4e0d\u4f4f\u7684\u6837\u5b50\uff0c\u4f46\u662f\u4ed6\u751f\u6765\u5014\u5f3a\u7684\u813e\u6c14\uff0c\u6012\u543c\u4e00\u58f0\uff0c\u7adf\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u5355\u638c\u6392\u51fa\u5168 \u8eab\u529f\u529b\u4e00\u628a\u6293\u4e0b\uff0c\u8eab\u4e0a\u8981\u7a74\u5b8c\u5168\u66b4\u9732\uff0c\u6beb\u4e0d\u7406\u4f1a\u2014\u2014 \u3000\u3000\u970d\u5982\u98de\u88ab\u8fd9\u7b49\u820d\u547d\u6253\u6cd5\u60ca\u5f97\u4e00\u6115\uff0c\u91d1\u6c0f\u5144\u5f1f\u5fc3\u610f\u65e9\u901a\uff0c\u547c\u7684\u4e00\u58f0\u91d1\u8001\u5927\u5df2\u4e00\u62db\u88ad\u4eba\uff0c \u957f\u81c2\u4e00\u4f38\uff0c\u5192\u9669\u76f4\u53d6\u970d\u5982\u98de\u80f8\u524d\u534e\u76d6\u2014\u2014 \u3000\u3000\u970d\u5982\u98de\u4e00\u89c1\u5927\u60ca\uff0c\u91d1\u8001\u5927\u7adf\u662f\u820d\u547d\u800c\u653b\uff0c\u81ea\u5df1\u867d\u7136\u80fd\u4efb\u610f\u51fb\u4e2d\u5176\u4e2d\u4e00\u4eba\uff0c\u4f46\u81ea\u5df1\u5374 \u4e5f\u975e\u88ab\u70b9\u4e2d\u4e0d\u53ef\uff0c\u6025\u5207\u95f4\u53ea\u597d\u4e00\u811a\u8e22\u51fa\u2014\u2014 \u3000\u3000\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u8001\u5927\u88ab\u8e22\u8d77\u98de\u51fa\uff0c\u7740\u7740\u5b9e\u5b9e\u649e\u5728\u5899\u58c1\u4e0a\uff0c\u4f46\u970d\u5982\u98de\u80f8\u524d\u534e\u76d6\u88ab\u5236\uff0c \u8f6f\u7ef5\u7ef5\u5012\u5728\u5730\u4e0a\u3002 \u3000\u3000\u91d1\u8001\u4e8c\u4e00\u89c1\u54e5\u54e5\u5403\u4e86\u4e8f\uff0c\u6012\u543c\u4e00\u58f0\u626c\u638c\u5bf9\u51c6\u970d\u5982\u98de\u8111\u95e8\u62cd\u4e0b\u2014\u2014 \u3000\u3000\u5c31\u5728\u6b64\u65f6\uff0c\u5ffd\u7136\u4e00\u4e2a\u9634\u68ee\u68ee\u7684\u53e3\u97f3\u4ece\u7a97\u5916\u4f20\u5165\uff1a\u201c\u7ed9\u6211\u4f4f\u624b\uff01\u201d\u4e00\u6761\u4eba\u5f71\u5237\u5730\u98de\u5165\uff0c \u91d1\u6c0f\u5144\u5f1f\u770b\u5f97\u771f\u5207\uff0c\u53ea\u89c1\u4ed6\u866c\u9aef\u98d8\u98d8\uff0c\u8eab\u6001\u5f02\u4eba\uff0c\u4e0d\u7981\u9f50\u53e3\u5927\u547c\uff1a\u201c\u52fe\u6ed1\u4e00\u602a\uff01\u201d \u3000\u3000\u52fe\u6f0f\u4e00\u602a\u7fc1\u6b63\u529f\u529b\u8fdc\u5728\u5176\u5e08\u5f1f\u970d\u5982\u98de\u4e4b\u4e0a\uff0c\u91d1\u6c0f\u5144\u5f1f\u81ea\u77e5\u7edd\u671b\uff0c\u5c31\u7b97\u4e24\u4eba\u4e0d\u4f24\u4e00\u9f50 \u4e0a\uff0c\u4e5f\u672a\u5fc5\u662f\u4eba\u5bb6\u5bf9\u624b\uff0c\u4f55\u51b5\u8fd9\u65f6\u4e24\u4eba\u90fd\u8d1f\u4e86\u4f24\uff01 \u3000\u3000\u5982\u679c\u8f9b\u6377\u5728\u573a\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u66f4\u60ca\uff0c\u56e0\u4e3a\u52fe\u6f0f\u4e00\u602a\u7adf\u662f\u548c\u4ed6\u4e00\u8def\u540c\u884c\u800c\u53c8\u4ee5\u91d1\u8749\u8131\u58f3\u800d \u5f04\u4ed6\u7684\u866c\u9aef\u6c49\u5b50\u7fc1\u6b63\uff01 \u3000\u3000\u7fc1\u6b63\u4f38\u624b\u89e3\u4e86\u970d\u5982\u98de\u7a74\u9053\uff0c\u5bf9\u4ed6\u9053\uff1a\u201c\u4f60\u5230\u4e0b\u9762\u53bb\u7167\u987e\u4e00\u4e0b\uff01\u201d \u3000\u3000\u970d\u5982\u98de\u5e94\u4e86\u4e00\u58f0\u800c\u53bb\uff0c\u7fc1\u6b63\u8138\u8272\u4e00\u6c89\uff0c\u5bf9\u91d1\u6c0f\u5144\u5f1f\u9a82\u9053\uff1a\u201c\u4e0d\u77e5\u5389\u5bb3\u7684\u8822\u4e1c\u897f\uff01\u201d \u4fef\u8eab\u5c06\u5730\u4e0a\u7684\u9e4f\u513f\u6276\u8d77\u3002 \u3000\u3000\u91d1\u6c0f\u5144\u5f1f\u6012\u6781\uff0c\u4f46\u5374\u4e0d\u6562\u5984\u52a8\uff0c\u7fc1\u6b63\u6545\u610f\u5927\u58f0\u8c03\u4f83\u9053\uff1a\u201c\u4f60\u4eec\u542c\u7740\uff0c\u6211\u6570\u4e94\u4e0b\uff0c\u82e5 \u662f\u6ca1\u6709\u4eba\u62e6\u6211\uff0c\u6211\u53ef\u5c31\u8981\u8d70\u4e86\u2014\u2014\u597d\uff0c\u6211\u73b0\u5c31\u5f00\u59cb\u6570\u2014\u2014 \u3000\u3000\u91d1\u8001\u5927\u53d7\u4f24\u751a\u91cd\uff0c\u91d1\u8001\u4e8c\u4e5f\u611f\u80a9\u4e0a\u4f24\u6108\u6765\u6108\u91cd\uff0c\u88ab\u52fe\u6f0f\u4e00\u602a\u7fc1\u6b63\u4e00\u903c\uff0c\u6012\u543c\u4e00\u58f0\uff0c \u6655\u5012\u5730\u4e0a\u2014\u2014 \u3000\u3000\u4e14\u8bf4\u8f9b\u633a\u88ab\u866c\u9aef\u6c49\u7528\u91d1\u8749\u58f3\u800d\u4e86\u4e00\u62db\uff0c\u5fc3\u5934\u5927\u6025\uff0c\u6025\u5fd9\u8f6c\u8eab\u75be\u5954\uff0c\u5e0c\u671b\u80fd\u963b\u6b62\u90a3\u513f \u9aef\u6c49\u4e0a\u5854\uff0c\u53ea\u8981\u4ed6\u4e00\u4e0a\u5854\uff0c\u4e10\u5e2e\u65e0\u4e00\u4f1a\u662f\u5bf9\u624b\uff01 \u3000\u3000\u624d\u5954\u51fa\u4e1b\u6797\uff0c\u8fdc\u8fdc\u77a7\u89c1\u4e00\u4eba\u98de\u7eb5\u5165\u5854\u9876\uff0c\u770b\u6765\u6b63\u662f\u90a3\u866c\u9aef\u6c49\u5b50\u2014\u2014 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u6025\uff0c\u811a\u4e0b\u66f4\u52a0\u7d27\uff0c\u5374\u542c\u89c1\u5854\u9876\u4f20\u51fa\u4e00\u58f0\u60ca\u547c\uff1a\u201c\u52fe\u6ed1\u4e00\u602a\uff01\u201d\u4ed6\u542c\u5f97\u51fa\u6b63 \u662f\u91d1\u6c0f\u5144\u5f1f\u7684\u58f0\u97f3\uff0c\u5fc3\u4e2d\u6597\u7136\u4e00\u52a8\uff0c\u6697\u9053\uff1a\u201c\u602a\u9053\u8fd9\u866c\u9aef\u6c49\u5b50\u6041\u5389\u5bb3\uff0c\u539f\u6765\u7adf\u662f\u2018\u52fe\u6ed1 \u4e00\u602a\u2019\u201d\u3002 \u3000\u3000\u6562\u60c5\u4ed6\u4e5f\u66fe\u542c\u6885\u53d4\u53d4\u63d0\u53ca\u6b64\u4eba\uff01 \u3000\u3000\u63a5\u7740\u52fe\u6f0f\u4e00\u602a\u7684\u72c2\u8a00\u4e00\u53e5\u53e5\u90fd\u4f20\u5165\u4ed6\u8033\u4e2d\uff0c\u4ed6\u62ac\u5934\u4e00\u671b\uff0c\u8eab\u8ddd\u5854\u8fb9\u5c1a\u6709\u5341\u4e08\u4e4b\u9065\uff0c \u800c\u52fe\u6ed1\u4e00\u602a\u7fc1\u6b63\u5df2\u5f00\u59cb\u4e00\u5b57\u4e00\u5b57\u7684\u6570\u7740\u2014\u2014 \u3000\u3000\u4f46\u8f9b\u6377\u751f\u6765\u504f\u6fc0\u7684\u6027\u5b50\uff0c\u6709\u7684\u5730\u65b9\u8fd1\u4e4e\u5f3a\u608d\uff0c\u4ed6\u51b3\u5b9a\u4e86\u4e00\u6869\u4e8b\uff0c\u5c31\u662f\u820d\u4e86\u547d\u4e5f\u8981\u529e \u5230\uff0c\u8fd9\u65f6\u4ed6\u6697\u6068\u81ea\u5df1\u7ecf\u9a8c\u4e0d\u591f\uff0c\u624d\u88ab\u52fe\u6f0f\u4e00\u602a\u5de7\u65bd\u91d1\u8749\u8131\u58f3\u6446\u8131\uff0c\u90a3\u201c\u795e\u9706\u201d\u5854\u9876\u7b2c\u5341 \u4e09\u5c42\u4e2d\u52fe\u6f0f\u4e00\u602a\u7684\u8bdd\u5168\u8fce\u98ce\u542c\u5165\u4e86\u8033\u2014\u2014 \u3000\u3000\u8fd9\u65f6\u7fc1\u6b63\u6d2a\u4eae\u7684\u58f0\u97f3\uff1a\u201c\u4e00\u2014\u2014\u4e8c\u2014\u2014\u201d\u4f20\u4e86\u8fc7\u6765\uff0c\u800c\u8f9b\u6377\u65bd\u51fa\u201c\u6697\u9999\u6d6e\u5f71\u201d\u7684\u8f7b \u529f\u7edd\u6280\u820d\u547d\u5730\u8dc3\u8d77\uff0c\u4ece\u5341\u4e08\u5916\u7adf\u81ea\u4e00\u98d8\u800c\u81f3\uff0c\u4f46\u662f\u6b63\u56e0\u4e3a\u79bb\u5f97\u592a\u8fdc\uff0c\u4ed6\u5230\u8fbe\u5854\u8fb9\u65f6\u9ad8\u5ea6 \u5df2\u4e0d\u591f\uff0c\u8f9b\u6377\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u53cc\u811a\u4e00\u8361\uff0c\u5947\u7edd\u5929\u4e0b\u7684\u8bd8\u6469\u6b65\u6cd5\u5df2\u7136\u65bd\u51fa\u2014\u2014 \u3000\u3000\u53ea\u89c1\u4ed6\u8eab\u4f53\u6597\u7136\u53c8\u4f38\u6570\u5c3a\uff0c\u4ed6\u6025\u5fd9\u4e2d\u4ef0\u9996\u4e00\u671b\uff0c\u81ea\u5df1\u5934\u9876\u4ec5\u53ca\u5854\u7684\u7b2c\u5341\u4e8c\u5c42\uff0c\u8ddd\u5341 \u4e09\u5c42\u9876\u5c1a\u5dee\u4e03\u516b\u5c3a\u4e4b\u9065\uff0c\u800c\u4ed6\u4e0a\u5347\u4e4b\u52bf\u5df2\u7aed\uff0c\u4e00\u53e3\u771f\u6c14\u5df2\u903c\u5f97\u4e0d\u80fd\u518d\u4e45\uff0c\u800c\u5934\u4e0a\u7fc1\u6b63\u7684 \u58f0\u97f3\uff1a \u3000\u3000\u201c\u2014\u2014\u4e09\u2014\u2014\u56db\u2014\u2014\u201d\u5df1\u81ea\u4f20\u51fa\u3002 \u3000\u3000\u4ed6\u6697\u9053\uff1a\u201c\u96be\u9053\u771f\u8981\u529f\u4e8f\u4e00\u7bd1\uff1f\u201d \u3000\u3000\u9ed1\u6697\u4e2d\uff0c\u4ed6\u6697\u4e00\u54ac\u7259\uff0c\u771f\u529b\u8d2f\u6ce8\u53f3\u81c2\uff0c\u731b\u7136\u524d\u4f38\uff0c\u201c\u7b03\u201d\u7684\u4e00\u58f0\uff0c\u7adf\u5c06\u90a3\u67c4\u5e26\u6765\u7684 \u957f\u5251\u9f50\u67c4\u63d2\u5165\u5370\u706b\u7816\u7684\u5854\u58c1\u4e2d\u2014\u2014 \u3000\u3000\u4ed6\u624b\u4e0a\u4e00\u501f\u5200\uff0c\u8eab\u4f53\u6709\u5982\u4e00\u53cc\u71d5\u5b50\u4e00\u822c\u7fe9\u7fe9\u7ffb\u98de\u800c\u4e0a\u2014\u2014 \u3000\u3000\u201c\u4e94\uff01\u201d \u3000\u3000\u7fc1\u6b63\u201c\u4e94\u201d\u5b57\u624d\u51fa\u53e3\uff0c\u5ffd\u7136\u4e00\u58f0\u60ca\u5929\u52a8\u5730\u822c\u7684\u543c\u58f0\u9707\u52a8\u4e86\u6bcf\u4e00\u4e2a\u4eba\u7684\u5fc3\u5f26\uff1a\u201c\u4f60\u7ed9 \u6211\u7ad9\u4f4f\uff01\u201d \u3000\u3000\u968f\u7740\u559d\u58f0\uff0c\u4e00\u6761\u4eba\u5f71\u6276\u7740\u96f7\u9706\u4e07\u94a7\u4e4b\u52bf\u4ece\u7a97\u5916\u98de\u5c06\u8fdb\u6765\uff0c\u5bf9\u7740\u52fe\u6f0f\u4e00\u602a\u7fc1\u6b63\u547c\u547c\u4e00 \u8fde\u5288\u51fa\u4e09\u638c\uff01 \u3000\u3000\u7fc1\u6b63\u53cc\u8db3\u9489\u7acb\uff0c\u4e0b\u76d8\u7a33\u7136\u4e0d\u52a8\uff0c\u4e0a\u8eab\u5de6\u6643\u53f3\u6446\uff0c\u4e00\u8fde\u95ea\u5f00\u4e09\u62db\uff0c\u4f46\u51cc\u5389\u7684\u638c\u98ce\u5df1\u4ee4 \u4ed6\u519c\u8882\u98d8\u821e\uff01 \u3000\u3000\u90a3\u4eba\u5374\u7a81\u7136\u540e\u9000\u4e00\u6b65\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u5feb\u5c06\u80a9\u4e0a\u9e4f\u513f\u653e\u4e0b\uff0c\u5426\u5219\u4f60\u4e0d\u662f\u6211\u5bf9\u624b\uff01\u201d \u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4eba\u6b63\u662f\u8f9b\u6377\uff01 \u3000\u3000\u7fc1\u6b63\u5012\u8fd8\u771f\u8bc6\u5f97\u5389\u5bb3\uff0c\u5c06\u80a9\u4e0a\u70b9\u4e86\u7a74\u9053\u7684\u9e4f\u513f\u653e\u5728\u5de6\u89d2\uff0c\u5411\u8f9b\u6377\u51b7\u7136\u4e00\u7b11\uff0c\u51dd\u76ee\u4ee5 \u5f85\u2014\u2014 \u3000\u3000\u8f9b\u6377\u77e5\u9053\u8fd9\u52fe\u6f0f\u4e00\u602a\u529f\u529b\u5353\u7edd\uff0c\u81ea\u5df1\u5bf9\u4ed6\u5b9e\u5728\u6ca1\u6709\u628a\u63e1\u53d6\u80dc\uff0c\u4f46\u662f\u4eca\u65e5\u4e4b\u52bf\uff0c\u9664\u4e86 \u4e00\u6218\u522b\u65e0\u7b2c\u4e8c\u6761\u9014\u5f84\uff0c\u4ed6\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u6697\u81ea\u6fc0\u52b1\u7740\u81ea\u5df1\uff1a\u201c\u8f9b\u6377\u554a\uff0c\u5c3d\u7ba1\u52fe\u6f0f\u4e00\u602a\u529f \u529b\u80dc\u8fc7\u4f60\uff0c\u4f60\u4eca\u65e5\u4e4b\u52bf\u662f\u8bb8\u80dc\u4e0d\u8bb8\u8d25\uff01\u201d \u3000\u3000\u4ed6\u5f85\u90a3\u53e3\u771f\u6c14\u8fd0\u884c\u4e86\u4e00\u5468\uff0c\u5ffd\u5730\u5f00\u58f0\u5410\u6c14\uff0c\u8eab\u5b50\u5b9b\u5982\u4e00\u9635\u65cb\u98ce\u4e00\u822c\u66f2\u8eab\u800c\u8fdb\uff0c\u53cc\u638c \u5374\u4f3c\u5200\u4f3c\u526a\u5730\u5207\u5411\u7fc1\u6b63\u4e24\u8109\u2014\u2014 \u3000\u3000\u7fc1\u6b63\u65e9\u5c31\u53d1\u89c9\u8f9b\u6377\u529f\u529b\u6df1\u539a\uff0c\u800c\u4e14\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u8eab\u8d1f\u4e00\u8eab\u7edd\u5b66\uff0c\u4f46\u6700\u4ee4\u4ed6\u62c5\u5fc3\u7684\u5374\u662f \u8f9b\u6377\u4f3c\u4e4e\u6709\u4e00\u79cd\u5185\u8574\u7684\u6f5c\u529b\uff0c\u800c\u4e14\u8fd9\u6f5c\u529b\u6df1\u4e0d\u53ef\u6d4b\uff0c\u5947\u7684\u662f\u8f9b\u6377\u672c\u4eba\u4e5f\u597d\u50cf\u5e76\u4e0d\u6e05\u695a\u81ea \u5df1\u5177\u6709\u8fd9\u79cd\u6f5c\u529b\uff0c\u5f53\u7136\uff0c\u4ed6\u662f\u4e1d\u6beb\u4e0d\u6562\u5927\u610f\u2014\u2014 \u3000\u3000\u8f9b\u6377\u53cc\u638c\u5207\u4e0b\uff0c\u771f\u53ef\u8bf4\u75be\u6bd4\u5954\u96f7\uff0c\u7fc1\u6b63\u5fc3\u4e2d\u4e00\u51db\uff0c\u4e00\u8bb0\u201c\u53cc\u638c\u7ffb\u5929\u201d\u594b\u529b\u4f7f\u51fa\uff0c\u5f85 \u53cc\u65b9\u5373\u5c06\u76f8\u78b0\u4e4b\u9645\uff0c\u6597\u7136\u4e00\u6536\u771f\u529b\uff0c\u53cc\u638c\u4e0a\u7ffb\u4e4b\u5f0f\u5df1\u6362\u6210\u7a7f\u88ad\u4e4b\u5f0f\uff0c\u76f4\u53d6\u8f9b\u6377\u80a9\u7a9d\u7435\u7436 \u9aa8\u2014\u2014 \u3000\u3000\u8f9b\u6377\u53cc\u638c\u843d\u7a7a\uff0c\u800c\u7fc1\u6b63\u7684\u653b\u52bf\u5df2\u5230\uff0c\u5f53\u4e0b\u5fae\u54fc\u4e00\u58f0\uff0c\u771f\u529b\u4e0b\u8d2f\u9a6c\u6b65\uff0c\u4e00\u4ef0\u4e0a\u8eab\u53d8\u4e3a \u201c\u76d8\u5f13\u5c04\u96d5\u201d\uff0c\u786c\u5c01\u800c\u51fa\u3002 \u3000\u3000\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u56db\u53ea\u624b\u638c\u78b0\u5728\u4e00\u8d77\uff0c\u53cc\u65b9\u90fd\u89c9\u624b\u5fc3\u4e00\u70ed\uff0c\u5404\u81ea\u9000\u540e\u4e00\u6b65\u3002 \u3000\u3000\u8f9b\u6377\u6697\u601d\uff1a\u201c\u8fd9\u771f\u662f\u51fa\u5e08\u4ee5\u6765\u6240\u9047\u7684\u7b2c\u4e00\u4e2a\u771f\u6b63\u52b2\u654c\uff0c\u4eca\u65e5\u83ab\u8981\u6298\u4e86\u5e08\u95e8\u5a01\u98ce\u2014\u2014\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u7275\u6302\uff0c\u624b\u4e0a\u81ea\u7136\u4e00\u6ede\uff0c\u7fc1\u6b63\u4f55\u7b49\u7ecf\u9a8c\u8001\u5230\uff0c\u53cc\u638c\u9f50\u98de\uff0c\u5c01\u4f4f\u8f9b\u6377\u9000\u8def\uff0c\u5de6\u811a \u8d77\u5904\u76f4\u8e22\u8f9b\u6377\u4e0b\u76d8\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6b63\u5f85\u62db\u53d8\uff0c\u654c\u4eba\u62db\u5f0f\u5df2\u9012\u8db3\uff0c\u6025\u5207\u4e2d\u53ea\u5f97\u5012\u8e29\u4e03\u661f\u6b65\uff0c\u53cc\u638c\u9f50\u6325\uff0c \u786c\u4ece\u5371\u52bf\u4e2d\u6253\u51fa\u4e03\u62f3\u2014\u2014 \u3000\u3000\u8f9b\u6377\u7684\u610f\u601d\u662f\u8981\u5f15\u7fc1\u6b63\u786c\u62fc\uff0c\u90a3\u77e5\u7fc1\u6b63\u72e1\u733e\u8001\u5230\uff0c\u8eab\u4e00\u5c48\uff0c\u7adf\u4ece\u8f9b\u6377\u80c1\u4e0b\u7a7f\u8fc7\uff0c\u5de6 \u638c\u5f15\u5904\uff0c\u53c8\u662f\u8f9b\u6377\u8111\u540e\u6b7b\u7a74\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4e00\u7740\u9519\u8fdf\uff0c\u7740\u7740\u53d7\u5236\uff0c\u4e00\u8fde\u5341\u4f59\u62db\u90fd\u5728\u5371\u9669\u4e2d\u582a\u582a\u8eb2\u8fc7\uff0c\u7fc1\u6b63\u89c1\u81ea\u5df1\u7a33\u5360\u4e0a\u98ce\uff0c \u4e0d\u7981\u6697\u559c\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u5e73\u751f\u5f97\u610f\u7edd\u5b66\u201c\u5f00\u5c71\u795e\u638c\u201d\u7a81\u7136\u65bd\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u88ab\u903c\u5f97\u5fc3\u5934\u706b\u8d77\uff0c\u4e58\u654c\u4e00\u8bb0\u201c\u7384\u9e1f\u5212\u6c99\u201d\u62db\u5f0f\u624d\u5c3d\u4e4b\u65f6\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u594b\u529b\u653b\u51fa \u4e00\u62db\u2014\u2014 \u3000\u3000\u970e\u662f\u6ee1\u5929\u638c\u5f71\uff0c\u638c\u98ce\u4e4c\u4e4c\u53d1\u54cd\uff0c\u4f3c\u4e4e\u65e0\u6240\u4e0d\u53ca\uff0c\u6b63\u662f\u4e16\u5916\u4e09\u4ed9\u4e4b\u9996\u5e73\u51e1\u5927\u5e08\u7684\u7edd\u4e16 \u5251\u6cd5\u201c\u5927\u884d\u5341\u5f0f\u201d\u4e2d\u7684\u201c\u65b9\u751f\u4e0d\u606f\u201d\uff0c\u53ea\u4e0d\u8fc7\u8f9b\u6377\u6b64\u65f6\u4ee5\u638c\u4ee3\u5251\u800c\u5df2\u3002 \u3000\u3000\u672c\u6765\u4ee5\u638c\u4e3a\u5251\u5a01\u529b\u5fc5\u7136\u5927\u51cf\uff0c\u4f46\u8f9b\u6377\u5728\u5927\u884d\u5341\u5f0f\u4e2d\u4ee5\u8fd9\u62db\u201c\u65b9\u751f\u4e0d\u606f\u201d\u6700\u6709\u5fc3\u5f97\uff0c \u8fd9\u65f6\u878d\u4f1a\u8d2f\u901a\u4e4b\u4e0b\uff0c\u7adf\u7136\u4e5f\u81ea\u5a01\u98ce\u51db\u51db\u2014\u2014 \u3000\u3000\u7fc1\u6b63\u5ffd\u7136\u89c1\u654c\u4eba\u8fd9\u62db\u5965\u5999\u65e0\u6bd4\uff0c\u4f3c\u4e4e\u5176\u4e2d\u53d8\u5316\u8fd8\u4e0d\u6b62\u6b64\uff0c\u800c\u4e14\u638c\u5f0f\u5947\u52b2\uff0c\u529b\u9053\u903c\u4eba\uff0c \u5f53\u4e0b\u7cbe\u795e\u4e00\u51db\uff0c\u4e5f\u81ea\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u62db\u201c\u98ce\u5377\u4e91\u6563\u201d\u7f13\u7f13\u62cd\u51fa\u2014\u2014 \u3000\u3000\u52fe\u6f0f\u4e00\u602a\u7684\u201c\u5f00\u5c71\u638c\u6cd5\u201d\u672c\u5c31\u4ee5\u529b\u4e3a\u4e3b\uff0c\u4ee5\u5de7\u4e3a\u8f85\uff0c\u8fd9\u201c\u98ce\u5377\u4e91\u6563\u201d\u66f4\u662f\u6a2a\u6253\u786c\u78b0 \u7684\u62db\u5f0f\uff0c\u6562\u60c5\u7fc1\u6b63\u89c1\u8f9b\u6377\u5306\u4fc3\u53d1\u62db\uff0c\u529b\u8574\u5fc5\u4e0d\u80fd\u7528\u8db3\uff0c\u7adf\u60f3\u4ee5\u786c\u78b0\u786c\u5730\u901f\u6218\u901f\u51b3\u3002 \u3000\u3000\u90a3\u77e5\u8f9b\u6377\u8fd9\u62db\u201c\u65b9\u751f\u4e0d\u606f\u201d\u770b\u6765\u4f3c\u4e4e\u5306\u5306\u53d1\u62db\uff0c\u5b9e\u5219\u771f\u529b\u5185\u8574\uff0c\u5468\u8eab\u5bc6\u5e03\uff0c\u8f9b\u6377\u53c8 \u662f\u542b\u6124\u800c\u53d1\uff0c\u4e0d\u8eb2\u4e0d\u95ea\u5730\u786c\u9012\u51fa\u53bb\u2014\u2014 \u3000\u3000\u8f70\u5730\u4e00\u58f0\u5de8\u54cd\uff0c\u4e24\u80a1\u5f3a\u6781\u7684\u529b\u9053\u8361\u5728\u4e00\u5904\uff0c\u8361\u8d77\u5708\u5708\u6c14\u6d41\uff0c\u6709\u5982\u9aa4\u8d77\u5927\u98ce\u4e00\u822c\uff0c\u5468 \u56f4\u7a97\u680f\u4e00\u9635\u4e71\u6447\u2014\u2014 \u3000\u3000\u8f9b\u6377\u548c\u7fc1\u6b63\u90fd\u662f\u53cc\u80a9\u4e00\u5e4c\uff0c\u7fc1\u6b63\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u518d\u63a5\u6211\u4e00\u638c\u8bd5\u8bd5\u770b\uff01\u201d \u3000\u3000\u53cc\u638c\u4e00\u9886\uff0c\u53c8\u662f\u4e00\u80a1\u72c2\u98ce\u626b\u4e86\u8fc7\u6765\u2014\u2014 \u3000\u3000\u8f9b\u6377\u66f4\u4e0d\u7b54\u8bdd\uff0c\u53cc\u819d\u5fae\u5f2f\uff0c\u53e3\u4e2d\u4f4e\u563f\u4e00\u58f0\uff0c\u5168\u8eab\u529f\u529b\u8d2f\u6ce8\u53cc\u638c\uff0c\u540c\u6837\u662f\u4e0d\u95ea\u4e0d\u907f\u5730 \u7f13\u7f13\u63a8\u51fa\u2014\u2014 \u3000\u3000\u8f70\u7136\u53c8\u662f\u4e00\u58f0\u5de8\u54cd\uff0c\u8f9b\u6377\u7fc1\u6b63\u53c8\u662f\u5404\u81ea\u4e00\u6643\uff0c\u7adf\u662f\u4f9d\u7136\u4e0d\u5206\u80dc\u8d1f\u2014\u2014 \u3000\u3000\u7fc1\u6b63\u5fc3\u5934\u706b\u8d77\uff0c\u4e0d\u987e\u4e00\u5207\u547c\u547c\u8fde\u5288\u56db\u638c\u2014\u2014 \u3000\u3000\u8f9b\u6377\u6c89\u54fc\u4e00\u58f0\uff0c\u6a2a\u7ad6\u8fde\u6325\uff0c\u4e5f\u786c\u63a5\u56db\u62db\uff0c\u4e1d\u638c\u4e0d\u7528\u5de7\u52b2\u3002\u4e00\u8fde\u516d\u4e0b\u786c\u78b0\u786c\uff0c\u4e24\u4eba\u5374 \u59cb\u7ec8\u9489\u7acb\u539f\u5730\uff0c\u53cc\u811a\u5206\u6beb\u672a\u79fb\uff0c\u8f9b\u6377\u501f\u7740\u4e00\u8f6e\u786c\u4ed7\uff0c\u53cd\u5c06\u4e0b\u98ce\u4e4b\u52bf\u53d8\u4e3a\u5e73\u6301\u4e4b\u5c40\uff01 \u3000\u3000\u8fd9\u51e0\u62db\u771f\u529b\u5927\u8d39\uff0c\u4f46\u8f9b\u6377\u52a8\u5374\u4e1d\u6beb\u4e0d\u611f\u75b2\u7d2f\uff0c\u76f8\u53cd\u7684\u5374\u89c9\u80f8\u4e2d\u8840\u6d41\u7545\u987a\uff0c\u8212\u7545\u65e0\u6bd4\u3002 \u3000\u3000\u539f\u6765\u8f9b\u6377\u81ea\u7ecf\u5e73\u51e1\u5927\u5e08\u4e0d\u501f\u4ee5\u201c\u63d0\u7cca\u704c\u9876\u201d\u7684\u529f\u592b\u786c\u5c06\u81ea\u8eab\u529f\u529b\u6253\u5165\u8f9b\u6377\u7a74\u9053\u4e2d\u540e\uff0c \u6b64\u65f6\u4ed6\u7684\u529f\u529b\u5df2\u5728\u4e00\u7532\u5b50\u5de6\u53f3\uff0c\u53ea\u662f\u4ed6\u81ea\u5df1\u90fd\u4e0d\u77e5\u81ea\u5df1\u4f1a\u6709\u8fd9\u6837\u5927\u7684\u6f5c\u529b\uff0c\u8fd9\u4e00\u9635\u6fc0\u6218\uff0c \u771f\u662f\u8f9b\u6377\u5e73\u751f\u6700\u8d39\u529b\u7684\u4e00\u573a\u62fc\u6597\uff0c\u5374\u628a\u4ed6\u7684\u5185\u5728\u6f5c\u529b\u7ed9\u5f15\u4e86\u51fa\u6765\uff0c\u662f\u4ee5\u51e0\u62db\u8fc7\u540e\uff0c\u4ed6\u4e0d \u4f46\u4e0d\u7d2f\uff0c\u53cd\u89c9\u7cbe\u795e\u5341\u8db3\u3002 \u3000\u3000\u8f9b\u6377\u60f3\u662f\u6253\u53d1\u4e86\u5174\uff0c\u66f4\u4e0d\u6253\u8bdd\uff0c\u4e24\u638c\u518d\u5ea6\u4e3b\u52a8\u5288\u51fa\uff0c\u7fc1\u6b63\u4e00\u6012\u4e4b\u4e0b\uff0c\u51b3\u4e0d\u9000\u8ba9\uff0c\u9f13 \u52a8\u8db3\u771f\u529b\uff0c\u4e00\u8fce\u800c\u4e0a\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5185\u5728\u7684\u6f5c\u529b\u88ab\u8fd9\u4e00\u9635\u786c\u62fc\u786c\u6253\u6fc0\u53d1\u65e0\u9057\uff0c\u5e73\u51e1\u4e0a\u4eba\u4ee5\u672c\u8eab\u529f\u529b\u8f93\u5165\u8f9b\u6377\u4f53\u5185\uff0c \u76f4\u5230\u73b0\u5728\u624d\u7b97\u662f\u771f\u6b63\u5168\u90e8\u548c\u8f9b\u6377\u7684\u5168\u8eab\u8840\u8109\u76f8\u878d\u800c\u53d1\u6325\u51fa\u6700\u5927\u5a01\u529b\uff0c\u8f9b\u6377\u53ea\u89c9\u53cc\u638c\u8fd0\u52b2 \u4e4b\u9645\uff0c\u8179\u5185\u4e00\u80a1\u70ed\u6d41\u9661\u7136\u4ece\u4e39\u7530\u5904\u6d8c\u4e86\u4e0a\u6765\uff0c\u80ba\u8151\u4e4b\u95f4\u771f\u6709\u8bf4\u4e0d\u51fa\u7684\u53d7\u7528\uff0c\u800c\u4ed6\u90a3\u731b\u6325 \u51fa\u7684\u4e00\u638c\uff0c\u5a01\u529b\u4e5f\u7adf\u5927\u5f97\u51fa\u5947\u3002 \u3000\u3000\u52fe\u6f0f\u4e00\u602a\u7fc1\u6b63\u6570\u5341\u5e74\u524d\u5c31\u5a01\u9707\u6b66\u6797\uff0c\u58f0\u540d\u4e4b\u76db\u5e76\u4e0d\u5728\u5173\u4e2d\u4e5d\u8c6a\uff0c\u6cb3\u6d1b\u4e00\u5251\u53ca\u5357\u5317\u4e8c \u541b\u7b49\u4eba\u4e4b\u4e0b\uff0c\u4e09\u5341\u5e74\u6765\uff0c\u8fd9\u662f\u5934\u4e00\u6b21\u516c\u7136\u91cd\u73b0\u6b66\u6797\uff0c\u672c\u5f85\u4ed7\u81ea\u5df1\u591a\u5e74\u82e6\u4fee\u7684\u51e0\u6837\u7edd\u6280\u518d \u632f\u58f0\u5a01\uff0c\u90a3\u77e5\u7adf\u78b0\u4e0a\u8fd9\u6837\u4e00\u4e2a\u9752\u5e74\u9ad8\u624b\uff0c\u4e0d\u4f46\u62f3\u6cd5\u7cbe\u5947\uff0c\u529f\u529b\u7adf\u4e5f\u80fd\u548c\u81ea\u5df1\u5e73\u5206\u79cb\u8272\uff0c \u8fd9\u65f6\u7684\u4e00\u638c\u63a8\u51fa\u4e5f\u662f\u65bd\u8db3\u4e86\u5341\u6210\u529f\u529b\uff0c\u6253\u7b97\u5c06\u5bf9\u65b9\u4e00\u638c\u51fb\u6bd9\u53ea\u542c\u5f97\u8f70\u7136\u4e00\u58f0\u66b4\u54cd\uff0c\u4e24\u80a1 \u5185\u5bb6\u771f\u529b\u76f8\u649e\u6fc0\u51fa\u7684\u65cb\u98ce\u7adf\u53d1\u51fa\u9e23\u9e23\u602a\u54cd\uff0c\u795e\u9706\u5854\u9876\u5e73\u65e5\u4e45\u65e0\u4eba\u6253\u626b\uff0c\u8fd9\u65f6\u5730\u4e0a\u7684\u7070\u5c18 \u66f4\u662f\u6f2b\u5929\u98de\u626c\u2014\u2014 \u3000\u3000\u52fe\u6f0f\u4e00\u602a\u53d1\u51fa\u4e00\u58f0\u95f7\u54fc\uff0c\u9a6c\u6b65\u6d6e\u52a8\uff0c\u54c4\u7684\u4e00\u58f0\u5012\u9000\u534a\u6b65\uff0c\u80f8\u5934\u7adf\u611f\u4e00\u9635\u8840\u6c14\u7ffb\u817e\u2014 \u2014 \u3000\u3000\u8f9b\u6377\u4e5f\u89c9\u4e00\u80a1\u6781\u5f3a\u7684\u529b\u9053\u4ece\u81ea\u5df1\u6325\u51fa\u7684\u52b2\u98ce\u4e2d\u6e17\u900f\u8fdb\u6765\uff0c\u4ed6\u53cc\u80a9\u4e00\u6447\u4e00\u6643\uff0c\u7ec8\u4e8e\u52aa \u529b\u5c06\u90a3\u529b\u9053\u5316\u53bb\uff0c\u53cc\u8db3\u4ecd\u7136\u7262\u9489\u5730\u9762\u2014\u2014 \u3000\u3000\u8f9b\u6377\u867d\u89c9\u654c\u4eba\u529f\u529b\u6781\u9ad8\uff0c\u4f46\u8fd9\u65f6\u80f8\u4e2d\u771f\u529b\u6ea2\u6f2b\uff0c\u8c6a\u6c14\u4e0a\u51b2\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u5de6\u638c\u4e00\u5708\uff0c \u53f3\u638c\u547c\u5730\u4e00\u58f0\u53c8\u81ea\u5288\u51fa\u3002 \u3000\u3000\u7fc1\u6b63\u5fc3\u4e2d\u611f\u5230\u4e00\u79cd\u65e0\u5730\u81ea\u5bb9\u7684\u96be\u8fc7\uff0c\u51e0\u5341\u5e74\u6765\u594b\u529b\u521b\u51fa\u6765\u7684\u4e07\u513f\u773c\u770b\u5373\u5c06\u6bc1\u4e8e\u4e00\u65e6\u3002 \u8fd9\u65f6\u89c1\u8f9b\u6377\u4e3e\u638c\u53c8\u662f\u4e00\u8bb0\u5288\u4e0b\uff0c\u4e0d\u7981\u987b\u53d1\u4ff1\u5f20\uff0c\u53cc\u76ee\u66b4\u7741\uff0c\u731b\u7136\u5f00\u58f0\u5410\u6c14\uff0c\u53cc\u638c\u5f53\u80f8\u5e73 \u5e73\u63a8\u51fa\u2014\u2014 \u3000\u3000\u8f9b\u6377\u662f\u4e0d\u4f1a\u4e86\u89e3\u4ed6\u7684\u5fc3\u60c5\u7684\uff0c\u4ed6\u600e\u4f1a\u60f3\u5230\u8fd9\u4e00\u638c\u5bf9\u4e8e\u8fd9\u602a\u50fb\u7684\u8001\u4eba\u662f\u4f55\u7b49\u7684\u91cd\u8981\uff1f \u4ed6\u53ea\u77e5\u81ea\u5df1\u6bcf\u4e00\u638c\u65bd\u51fa\u5a01\u529b\u51fa\u4e4e\u610f\u6599\u5730\u5927\u589e\uff0c\u5fc3\u795e\u4ff1\u5feb\u2014\u2014 \u3000\u3000\u8f70\u7684\u53c8\u4e00\u58f0\uff0c\u8f9b\u6377\u6643\u4e86\u6643\uff0c\u8e0f\u8fdb\u4e00\u6b65\uff0c\u529b\u8d2f\u5355\u81c2\uff0c\u53c8\u662f\u4e00\u638c\u62cd\u51fa\u3002 \u3000\u3000\u7fc1\u6b63\u529b\u8d2f\u53cc\u817f\uff0c\u62fc\u7740\u6ca1\u6709\u9000\u540e\uff0c\u594b\u529b\u53c8\u662f\u4e00\u638c\u5c01\u4e0a\uff0c\u53ea\u89c9\u8f9b\u6377\u638c\u4e0a\u529b\u9053\u4e00\u638c\u5f3a\u4f3c\u4e00 \u638c\uff0c\u8fd9\u4e00\u638c\u771f\u6709\u5f00\u5c71\u88c2\u767e\u4e4b\u5a01\uff0c\u51e0\u5341\u5e74\u7684\u7ecf\u9a8c\u544a\u8bc9\u4ed6\uff0c\u8fd9\u4e00\u638c\u5982\u679c\u63a5\u5b9e\u4e86\uff0c\u81ea\u5df1\u5185\u8151\u5168 \u6709\u9707\u4f24\u7684\u53ef\u80fd\uff0c\u4e8e\u662f\u4ed6\u5728\u53cc\u638c\u5c1a\u672a\u78b0\u4e0a\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u75be\u5982\u95ea\u7535\u5730\u540e\u9000\u4e00\u6b65\u3002\u4f46\u662f\u7830\u7684\u4e00 \u58f0\uff0c\u4ed6\u8fd8\u662f\u88ab\u9707\u9000\u4e00\u6b65\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u81ea\u5df1\u80f8\u4e2d\u529b\u9053\u5df2\u5230\u4e86\u9876\u5cf0\uff0c\u4ed6\u5feb\u7136\u957f\u5578\u4e00\u58f0\uff0c\u624b\u8d77\u638c\u843d\u2014\u2014 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u7684\u624b\u60ac\u5728\u534a\u7a7a\u4e2d\uff0c\u4ed6\u770b\u5230\u4e86\u4e00\u5f20\u4ece\u672a\u89c1\u8fc7\u7684\u8138\u5b54\u2014\u2014\u7fc1\u6b63\u8138\u4e0a\u7684\u808c\u8089\u62bd\u6410 \u6210\u4e00\u79cd\u53e4\u602a\u7684\u795e\u8272\uff0c\u53c8\u50cf\u662f\u51b7\u6f20\uff0c\u53c8\u50cf\u662f\u7edd\u671b\u2026\u2026 \u3000\u3000\u8f9b\u633a\u867d\u4e0d\u80fd\u5b8c\u4f1a\u4e86\u89e3\u8fd9\u8868\u60c5\u6240\u5305\u542b\u7684\u60c5\u7eea\uff0c\u4f46\u662f\u76f4\u89c9\u544a\u8bc9\u4ed6\uff0c\u90a3\u51b3\u4e0d\u662f\u6015\u6b7b\uff0c\u4e5f\u8bb8 \u67d0\u79cd\u56e0\u7d20\u5bf9\u4e8e\u4ed6\u6bd4\u6b7b\u66f4\u53ef\u6015\u591a\u500d\u3002 \u3000\u3000\u8f9b\u6377\u7684\u624b\u638c\u7f13\u7f13\u5782\u4e86\u4e0b\u6765\uff0c\u7fc1\u6b63\u7684\u8138\u8272\u4e5f\u6062\u590d\u4e86\u6b63\u5e38\uff0c\u73b0\u5728\u4ed6\u8111\u6d77\u5145\u6ee1\u7740\u7684\u53ea\u6709\u4e00 \u4e2a\u201c\u6012\u201d\u5b57\u3002\u4ed6\u51b7\u7136\u4e00\u54fc\uff0c\u52aa\u529b\u8c03\u5300\u4e86\u547c\u5438\uff0c\u53cc\u773c\u5145\u6ee1\u7740\u6740\u673a\uff0c\u72e0\u72e0\u5730\u76ef\u7740\u8f9b\u6377\uff0c\u4f7f\u8f9b \u6377\u611f\u5230\u4e00\u9635\u5fc3\u5bd2\u800c\u5c06\u76ee\u5149\u907f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u201c\u64e6\u201d\u7684\u4e00\u58f0\uff0c\u7fc1\u6b63\u62bd\u51fa\u4e86\u957f\u5251\u3002 \u3000\u3000\u8f9b\u6377\u50cf\u662f\u6ca1\u6709\u542c\u89c1\uff0c\u4ed6\u6b63\u5728\u60f3\uff1a\u201c\u4e3a\u4ec0\u4e48\u52fe\u6f0f\u4e00\u602a\u8981\u5982\u6b64\u72e0\u72e0\u76ef\u7740\u6211\uff1f\u54fc\uff0c\u4f60\u76ef\u7740 \u6211\u6211\u5c31\u6015\u4e86\u4f60\u5417\uff1f\u201d\u4ed6\u4e0d\u670d\u6c14\u5730\u62ac\u5934\u53cd\u77aa\u8fc7\u53bb\u2014\u2014 \u3000\u3000\u5176\u5b9e\u4ed6\u662f\u6709\u4e9b\u5fc3\u5bd2\u7684\uff0c\u53ea\u662f\u4ed6\u5929\u751f\u504f\u6fc0\u7684\u6027\u683c\u4ee4\u4ed6\u5982\u6b64\u3002 \u3000\u3000\u8fd9\u4e00\u62ac\u5934\uff0c\u4ed6\u77a7\u89c1\u4e86\u7fc1\u6b63\u624b\u4e2d\u7684\u957f\u5251\u3002 \u3000\u3000\u4ed6\u4e0b\u610f\u8bc6\u5730\u4f38\u624b\u62d4\u5251\uff0c\u4f46\u662f\u62d4\u4e86\u4e00\u4e2a\u7a7a\uff0c\u4ed6\u5ffd\u7136\u60f3\u8d77\u201c\u6885\u9999\u5251\u201d\u4ecd\u63d2\u5728\u5854\u5916\u58c1\u4e0a\u3002 \u3000\u3000\u201c\u63a5\u7740\uff01\u201d\u91d1\u8001\u4e8c\u6325\u52a8\u7740\u672a\u4f24\u7684\u624b\u81c2\u5728\u5730\u4e0a\u62fe\u8d77\u4e00\u67c4\u957f\u5251\u63b7\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u628a\u63a5\u8fc7\uff0c\u8155\u4e0a\u7528\u52b2\u4e00\u9707\uff0c\u5251\u5c16\u53d1\u51fa\u55e1\u7684\u4e00\u58f0\u3002 \u3000\u3000\u7fc1\u6b63\u5251\u8eab\u5e73\u51fb\uff0c\u5237\u7684\u4e00\u62db\u5411\u8f9b\u6377\u5de6\u80a9\u70b9\u5230\uff0c\u5251\u52bf\u5982\u8679\uff0c\u52b2\u98ce\u6251\u9762\uff0c\u5230\u4e86\u80a9\u524d\u5ffd\u5730\u55e1 \u7684\u4e00\u58f0\uff0c\u5251\u5c16\u7adf\u5316\u505a\u4e00\u7247\u5149\u70b9\u5206\u70b9\u8f9b\u6377\u8179\u4e0a\u4e09\u7a74\u2014\u2014 \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u529f\u529b\u6df1\u539a\uff0c\u5251\u62db\u53c8\u8be1\u5947\u65e0\u6bd4\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u51db\uff0c\u811a\u4e0b\u7a0d\u9000\u534a\u6b65\uff0c\u5de6\u624b\u5251\u8bc0\u4e00 \u5f15\uff0c\u53f3\u624b\u957f\u5251\u4e00\u5708\u800c\u51fa\uff0c\u6b63\u662f\u201c\u866c\u679d\u5251\u6cd5\u201d\u4e2d\u7684\u201c\u6885\u5410\u5947\u9999\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u957f\u5251\u9012\u51fa\uff0c\u5251\u5c16\u5636\u5636\u53d1\u54cd\uff0c\u663e\u7136\u4ed6\u8155\u4e0a\u771f\u529b\u53eb\u8db3\uff0c\u7f15\u7f15\u5251\u6c14\u76f4\u900f\u5251\u5c16\u3002\u7fc1\u6b63\u51dd \u76ee\u6ce8\u89c6\u8f9b\u6377\u5251\u5f0f\uff0c\u8138\u5e26\u8be7\u5f02\u4e4b\u8272\u3002 \u3000\u3000\u201c\u6885\u5410\u5947\u9999\u201d\u8fc5\u901f\u65e0\u6bd4\uff0c\u66f4\u517c\u8f9b\u6377\u53d1\u5f0f\u8f7b\u7075\uff0c\u5c45\u7136\u540e\u53d1\u800c\u5148\u81f3\uff0c\u7fc1\u6b63\u5251\u5c16\u79bb\u8f9b\u6377\u8179 \u4e0a\u201c\u4e95\u5e02\u7a74\u201d\u5c1a\u6709\u4e09\u5bf8\uff0c\u8f9b\u6377\u957f\u5251\u5df2\u521a\u521a\u9012\u81f3\u7fc1\u6b63\u8155\u4e0a\u201c\u66f2\u6c60\u201d\u4e0d\u53ca\u4e00\u5bf8\u2014\u2014 \u3000\u3000\u90a3\u77e5\u5c31\u5728\u6b64\u65f6\uff0c\u7a81\u7136\u7fc1\u6b63\u7684\u5251\u5c16\u95ee\u524d\u66b4\u4f38\uff0c\u8eab\u4f53\u5374\u5f80\u540e\u731b\u9000\uff0c\u547c\u7684\u4e00\u58f0\u8f9b\u6377\u7684\u957f\u5251 \u8d70\u4e86\u7a7a\uff0c\u800c\u7fc1\u6b63\u7684\u5251\u5c16\u5df1\u5230\u4e86\u8f9b\u6377\u8179\u4e0a\u8f9b\u6377\u4e0d\u6599\u4ed6\u62db\u5f0f\u8be1\u5947\u5982\u65af\uff0c\u6025\u5207\u4e2d\u811a\u4e0b\u5012\u8e29\u8ff7\u8e2a \u6b65\uff0c\u5728\u5343\u94a7\u4e00\u53d1\u4e2d\u4ed3\u4fc3\u9000\u540e\u3002 \u3000\u3000\u8f9b\u6377\u4f4e\u54fc\u4e00\u58f0\uff0c\u5251\u5149\u4e00\u626c\uff0c\u518d\u5ea6\u7331\u8eab\u800c\u4e0a\uff0c\u5237\u5237\u5237\u4e09\u5251\u4ece\u4e09\u4e2a\u4e0d\u540c\u65b9\u4f4d\u523a\u51fa\uff0c\u6700\u540e \u5251\u5c16\u5374\u96c6\u4e2d\u5728\u7fc1\u6b63\u201c\u6c14\u6d77\u201d\u8981\u7a74\u4e0a\uff0c\u5168\u662f\u201c\u866c\u679d\u5251\u5f0f\u201d\u4e2d\u7684\u5999\u7740\u3002 \u3000\u3000\u90a3\u77e5\u7fc1\u6b63\u4e5f\u662f\u5251\u5b50\u8fde\u6325\uff0c\u62db\u5f0f\u5168\u8d70\u504f\u950b\uff0c\u4e00\u8fde\u51e0\u4e2a\u602a\u62db\u5c06\u8f9b\u6377\u653b\u52bf\u6d88\u4e8e\u65e0\u5f62\u3002 \u3000\u3000\u52fe\u6f0f\u4e00\u602a\u5251\u5149\u8fde\u95ea\uff0c\u4e3b\u52a8\u800c\u4e0a\uff0c\u8f9b\u6377\u53ea\u89c9\u4ed6\u7684\u5251\u6cd5\u8be1\u5947\u65e0\u6bd4\uff0c\u4ee4\u4eba\u4e00\u773c\u770b\u4e0a\u53bb\u5c31\u751f \u4e00\u79cd\u201c\u65c1\u95e8\u5de6\u9053\u201d\u7684\u611f\u89c9\uff0c\u4f46\u504f\u504f\u8be1\u5947\u4e4b\u4e2d\u6697\u85cf\u6740\u7740\uff0c\u4ee4\u4eba\u9632\u4e0d\u80dc\u9632\u3002\u8fd9\u6b63\u662f\u52fe\u6f0f\u4e00\u602a \u7684\u5e73\u751f\u7edd\u5b66\u201c\u4ee4\u5937\u5251\u6cd5\u201d\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u7684\u8679\u679d\u5251\u5f0f\u867d\u7136\u7cbe\u5999\u8fdc\u80dc\uff0c\u4f46\u8be1\u5947\u5374\u4f3c\u72b9\u4e0d\u53ca\u4ee4\u5937\u5251\u6cd5\uff0c\u800c\u866c\u679d\u5251\u6cd5\u7684\u7279 \u70b9\u539f\u4e5f\u5728\u201c\u8be1\u5947\u201d\u4e24\u5b57\uff0c\u8fd9\u65f6\u5373\u7136\u5728\u8fd9\u65b9\u9762\u4e0d\u53ca\u5bf9\u65b9\uff0c\u5a01\u529b\u4e5f\u81ea\u5927\u51cf\uff0c\u8f9b\u6377\u53ea\u89c9\u597d\u4e9b\u5999 \u62db\u53d1\u6325\u4e0d\u51fa\u5a01\u529b\u3002 \u3000\u3000\u7fc1\u6b63\u4e00\u62db\u201c\u5389\u7634\u950b\u6d8c\u201d\uff0c\u957f\u5251\u5316\u6210\u4e00\u7247\u5149\u5e55\uff0c\u4f3c\u865a\u4f3c\u771f\uff0c\u5411\u8f9b\u6377\u5f53\u5934\u76d6\u4e0b\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4e0d\u89c9\u7cbe\u795e\u4e00\u51db\uff0c\u5fc3\u9053\uff1a\u201c\u6885\u53d4\u53d4\u7684\u2018\u866c\u679d\u5251\u5f0f\u2019\u5947\u7edd\u5929\u4e0b\uff0c\u96be\u9053\u8981\u8f93\u7ed9\u8fd9\u52fe\u6f0f \u4e00\u602a\uff1f\u201d\u5f53\u4e0b\u4e00\u54ac\u7259\uff0c\u4fa7\u8eab\u6b3a\u8fdb\uff0c\u957f\u5251\u4e00\u6325\uff0c\u5df2\u81ea\u6296\u51fa\u4e00\u7247\u5251\u5e55\uff0c\u8fce\u5c06\u4e0a\u53bb\u2014\u2014 \u3000\u3000\u5237\u7684\u4e00\u58f0\uff0c\u7fc1\u6b63\u865a\u62db\u5168\u6536\uff0c\u4e00\u5251\u4ece\u504f\u950b\u75be\u5982\u95ea\u7535\u7684\u523a\u4e86\u8fdb\u6765\u3002 \u3000\u3000\u201c\u5636\u201d\u58f0\u6597\u76db\uff0c\u8f9b\u6377\u5251\u5149\u66b4\u957f\uff0c\u7adf\u7136\u4e5f\u662f\u75be\u8d70\u504f\u950b\u800c\u51fa\uff0c\u6b63\u662f\u4e03\u5999\u795e\u541b\u5fc3\u8840\u6240\u805a\u7684 \u201c\u51b7\u6885\u62c2\u9762\u201d\u3002 \u3000\u3000\u8fd9\u4e24\u62db\u90fd\u4ece\u504f\u950b\u51fa\u624b\uff0c\u62db\u5f0f\u7adf\u7136\u5927\u540c\u5c0f\u5f02\uff0c\u4f46\u662f\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u5fc3\u8840\u6240\u805a\u7684\u201c\u51b7\u6885 \u62c2\u9762\u201d\u6bd5\u7adf\u80dc\u4e86\u4e00\u7740\uff0c\u8f9b\u6377\u7684\u5251\u5b50\u540e\u53d1\u800c\u5148\u81f3\uff0c\u5251\u5c16\u7684\u5251\u6c14\u903c\u5f97\u7fc1\u6b63\u6536\u62db\u800c\u9000\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u62db\u626d\u8f6c\u5c40\u52bf\uff0c\u8c6a\u6c14\u4e0a\u51b2\uff0c\u6325\u5251\u800c\u4e0a\u3002 \u3000\u3000\u7fc1\u6b63\u51b7\u54fc\u4e00\u58f0\uff0c\u7d27\u63a5\u7740\u7b2c\u4e8c\u4e2a\u5947\u62db\u201c\u51b7\u4e91\u64bc\u5bb5\u201d\u53c8\u81ea\u65bd\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u4ed6\u7684\u5251\u62db\u5927\u5f02\u5bfb\u5e38\uff0c\u4f3c\u4e4e\u5e26\u7740\u4e00\u79cd\u90aa\u6bd2\u4e4b\u6c14\uff0c\u53c8\u4f3c\u5305\u542b\u4e00\u79cd\u91ce\u86ee\u672a\u5f00\u5316\u7684 \u6b8b\u5389\u4e4b\u6c14\uff0c\u53e4\u602a\u5df2\u6781\u3002 \u3000\u3000\u53ea\u542c\u5f97\u542c\u58f0\u523a\u8033\uff0c\u5251\u5c16\u66b4\u4f38\uff0c\u6f2b\u7a7a\u90fd\u662f\u8f9b\u6377\u7684\u5251\u5f71\uff0c\u539f\u6765\u8f9b\u6377\u4e0d\u7531\u81ea\u4e3b\u7684\u65bd\u51fa\u4e86 \u201c\u5927\u884d\u5341\u5f0f\u201d\u7684\u8d77\u624b\u5f0f\u201c\u65b9\u751f\u4e0d\u606f\u201d\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u5251\u5149\u7531\u5de6\u53f3\u5f80\u4e2d\u4e00\u5408\uff0c\u75be\u523a\u800c\u51fa\uff0c\u4f3c\u7f13\u5b9e\u75be\uff0c\u4f3c\u865a\u5b9e\u771f\uff0c\u5b9b\u5982\u65e5\u5149\u666e\u7167\uff0c\u65e0 \u6240\u4e0d\u53ca\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7684\u201c\u5927\u884d\u5341\u5f0f\u201d\u4e43\u662f\u4ece\u7cbe\u5947\u795e\u5999\u7740\u624b\uff0c\u4f7f\u51fa\u4e4b\u65f6\u81ea\u7136\u6709\u4e00\u79cd\u51db\u7136\u6b63\u6c14\u4e4b\u611f\uff0c \u7fc1\u6b63\u7684\u5947\u62db\u8be1\u5f0f\u4e00\u78b0\u4e0a\u7acb\u523b\u5a01\u52bf\u5168\u5931\uff0c\u76f8\u53cd\u7684\u8f9b\u6377\u5251\u62db\u6709\u5982\u7ef5\u7ef5\u6c5f\u6c34\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u3002 \u3000\u3000\u5306\u5306\u6570\u5341\u62db\u5df2\u8fc7\uff0c\u53ea\u542c\u5f97\u201c\u55ef\u6298\u201d\u4e00\u58f0\uff0c\u4e24\u4eba\u5404\u81ea\u8dc3\u5f00\uff0c\u7fc1\u4e18\u624b\u4e2d\u53ea\u5269\u4e86\u4e00\u53ea\u5251\u67c4\uff0c \u6562\u60c5\u4ed6\u7684\u957f\u5251\u7adf\u88ab\u8f9b\u6377\u4ee5\u5185\u529b\u9707\u65ad\u3002 \u3000\u3000\u4ed6\u7684\u8138\u4e0a\u4e00\u7247\u6b7b\u7070\uff0c\u773c\u7736\u4e2d\u7adf\u5145\u6ee1\u7740\u6cea\u6c34\uff0c\u8f9b\u6377\u4ee5\u5947\u5f02\u7684\u773c\u5149\u5446\u671b\u7740\u4ed6\uff0c\u5fd8\u5374\u8fdb\u653b\u3002 \u3000\u3000\u7fc1\u4e18\u5ffd\u7136\u4e00\u8a00\u4e0d\u53d1\u8f6c\u8eab\u98de\u7eb5\u51fa\u5854\u3002 \u3000\u3000\u8f9b\u6377\u6697\u9053\uff1a\u201c\u5c31\u7b97\u6253\u8f93\u4e86\u4e5f\u4e0d\u7528\u4f24\u5fc3\u5230\u8fd9\u4e2a\u6837\u5b50\u554a\uff01\u201d \u3000\u3000\u4ed6\u600e\u4f1a\u6599\u5230\u4ed6\u79bb\u4e86\u7fc1\u6b63\u4e00\u62db\u6bd4\u6740\u4e86\u7fc1\u6b63\u8fd8\u4ee4\u4ed6\u96be\u582a\u5462\u2014\u2014 \u3000\u3000\u4e09\u5341\u5e74\u524d\u52fe\u6f0f\u4e00\u602a\u5728\u9ec4\u5c71\u795d\u878d\u5cf0\u9876\u548c\u5f53\u65f6\u6b66\u6797\u7b2c\u4e00\u4eba\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u8d4c\u6597\uff0c\u4ed6\u90a3\u8be1 \u5947\u7684\u201c\u4ee4\u5937\u5251\u6cd5\u201d\u4e5f\u4ee4\u6885\u5c71\u6c11\u7684\u201c\u866c\u679d\u5251\u6cd5\u201d\u611f\u5230\u68d8\u624b\uff0c\u4f46\u662f\u6885\u5c71\u6c11\u7a76\u7adf\u51ed\u7740\u529f\u529b\u6df1\u539a\uff0c \u5728\u7b2c\u4e09\u767e\u62db\u4e0a\u9707\u65ad\u4e86\u4ed6\u7684\u624b\u4e2d\u5251\uff0c\u4ece\u6b64\u7fc1\u6b63\u4e00\u6012\u9690\u5c45\u8fb9\u7586\uff0c\u82e6\u7ec3\u7edd\u6280\uff0c\u628a\u201c\u4ee4\u5937\u5251\u6cd5\u201d \u7ec3\u5f97\u66f4\u52a0\u602a\u5f02\u96be\u9632\uff0c\u5f53\u5e74\u4ed6\u662f\u7528\u8fd9\u5957\u5251\u6cd5\u5931\u624b\u7684\uff0c\u4ed6\u51c6\u5907\u7528\u8fd9\u5957\u5251\u6cd5\u627e\u56de\u573a\u9762\u6765\u3002 \u3000\u3000\u6885\u5c71\u6c11\u88ab\u4e94\u5927\u5251\u6d3e\u56f4\u653b\u7684\u6d88\u606f\u4e0d\u77e5\u4f7f\u4ed6\u591a\u4e48\u5931\u671b\uff0c\u4f46\u8fd1\u6765\u6885\u5c71\u6c11\u91cd\u73b0\u6b66\u6797\u7684\u4f20\u8bf4\u7ec8 \u4e8e\u4f7f\u4ed6\u79bb\u5f00\u52fe\u6f0f\u5c71\uff0c\u91cd\u5165\u4e2d\u539f\u3002 \u3000\u3000\u5f53\u8f9b\u6377\u4e00\u4eae\u5251\u62db\u65f6\uff0c\u4ed6\u53c8\u60ca\u53c8\u518d\u7684\u53d1\u89c9\u8f9b\u6377\u662f\u201c\u866c\u679d\u5251\u5f0f\u201d\u7684\u5ae1\u4f20\u4eba\uff0c\u4ed6\u4e00\u5fc3\u8981\u7528 \u4ee4\u5937\u5251\u6cd5\u884d\u4f4f\u8f9b\u633a\uff0c\u4f46\u662f\uff0c\u7ed3\u679c\u7adf\u548c\u4e09\u5341\u5e74\u524d\u4e00\u6837\uff0c\u4ed6\u88ab\u9707\u65ad\u4e86\u957f\u5251\uff0c\u6240\u4e0d\u540c\u7684\u662f\u4e09\u5341 \u5e74\u524d\u662f\u6885\u5c71\u6c11\u672c\u4eba\uff0c\u800c\u4e09\u5341\u540e\u5374\u662f\u4ed6\u7684\u4f20\u4eba\u3002 \u3000\u3000\u5982\u679c\u4ed6\u77e5\u9053\u8f9b\u633a\u6240\u7528\u4ee5\u81f4\u80dc\u7684\u5e76\u975e\u6885\u5c71\u6c11\u6240\u6388\uff0c\u4e43\u662f\u4e16\u5916\u4e09\u4ed9\u4e4b\u9996\u5e73\u51e1\u4e0a\u4eba\u7684\u201c\u5927 \u884d\u5251\u5f0f\u201d\uff0c\u4e5f\u8bb8\u4ed6\u4f1a\u89c9\u5f97\u597d\u8fc7\u4e00\u4e9b\u3002 \u3000\u3000\u8f9b\u6377\u53ef\u4e0d\u77e5\u9053\u8fd9\u4e9b\uff0c\u4ed6\u6014\u4e86\u4e00\u6014\uff0c\u8f6c\u8eab\u5411\u88ab\u70b9\u4e86\u7a74\u9053\u7684\u4e10\u5e2e\u5e7c\u4e3b\u9e4f\u513f\u8d70\u53bb\u3002 \u3000\u3000\u9e4f\u513f\u88ab\u70b9\u4e86\u8f6f\u9ebb\u7a74\uff0c\u4e0d\u80fd\u8f6c\u52a8\uff0c\u8f9b\u6377\u529b\u900f\u53cc\u638c\uff0c\u5728\u4ed6\u810a\u80cc\u4e0a\u4e00\u63c9\u4e00\u62cd\uff0c\u9e4f\u513f\u7f13\u7f13\u82cf \u9192\u3002\u8f9b\u6377\u53c8\u8f6c\u8eab\u8d70\u5411\u91d1\u6c0f\u5144\u5f1f\uff0c\u53ea\u89c1\u91d1\u8001\u5927\u5df2\u660f\u8ff7\u4e0d\u9192\uff0c\u800c\u91d1\u8001\u4e8c\u4ecd\u786c\u6491\u7740\u6276\u6301\u7740\u4ed6\u5927 \u54e5\u3002 \u3000\u3000\u8f9b\u6377\u638f\u51fa\u5200\u521b\u836f\u9012\u4e86\u8fc7\u53bb\uff0c\u91d1\u8001\u4e8c\u9ed8\u9ed8\u7684\u63a5\u8fc7\uff0c\u4ed6\u6ca1\u6709\u8bf4\u611f\u6fc0\u7684\u8bdd\uff0c\u4f46\u4ed6\u7684\u76ee\u5149\u4e2d \u6240\u8868\u793a\u7684\u6bd4\u8bf4\u4e00\u767e\u53e5\u8bdd\u8fd8\u8981\u6e05\u695a\u660e\u767d\u3002 \u3000\u3000\u8f9b\u6377\u6ce8\u89c6\u4ed6\u80a9\u4e0a\u7684\u4f24\u53e3\uff0c\u8fd9\u65f6\u660f\u8ff7\u7684\u91d1\u8001\u5927\u5df2\u7f13\u7f13\u9192\u8f6c\u8fc7\u6765\uff0c\u91d1\u8001\u4e8c\u53c8\u638f\u51fa\u4e24\u7c92\u9ed1 \u8272\u7684\u836f\u4e38\u585e\u5165\u4ed6\u53e3\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u5ffd\u611f\u80cc\u540e\u4e00\u53ea\u5c0f\u624b\u63e1\u4f4f\u4ed6\u7684\u8863\u89d2\uff0c\u4ed6\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u9e4f\u513f\u6084\u751f\u751f\u5730\u7ad9\u5728\u8eab\u540e\uff0c \u6ee1\u8138\u7070\u57a2\uff0c\u4e00\u53cc\u7075\u6d3b\u7684\u5927\u773c\u6674\u6e9c\u6e9c\u5730\u8f6c\u7740\uff0c\u8f9b\u6377\u5ffd\u7136\u53d1\u89c9\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u8fd9\u5b69\u5b50\u4f3c\u4e4e\u957f\u5927 \u4e86\u4e0d\u5c11\uff0c\u4e0a\u6b21\u76f8\u9047\u65f6\u7684\u90a3\u4e00\u5206\u7a1a\u6c14\u5df2\u51cf\u9000\u8bb8\u591a\u3002 \u3000\u3000\u9e4f\u513f\u8f7b\u5524\u9053\uff1a\u201c\u8f9b\u2014\u2014\u8f9b\u53d4\u53d4\u2014\u2014\u201d\u8fd9\u5b69\u5b50\u8bb0\u5fc6\u529b\u4e0d\u574f\uff0c\u8fd8\u8bb0\u5f97\u8f9b\u6377\u7684\u59d3\u540d\u3002\u4ed6\u671b \u4e86\u671b\u91d1\u8001\u4e8c\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u91d1\u8001\u4e8c\u70b9\u4e86\u70b9\u5934\uff0c\u4f3c\u4e4e\u8ba4\u4e3a\u201c\u8f9b\u53d4\u53d4\u201d\u6b63\u5e94\u8be5\u5982\u6b64\u79f0\u8c13\u3002 \u3000\u3000\u8f9b\u6377\u5e94\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u4ec0\u4e48\u4e8b\uff1f\u4f60\u8fd8\u662f\u53eb\u6211\u8f9b\u54e5\u54e5\u5427\u3002\u201d \u3000\u3000\u9e4f\u513f\u9053\uff1a\u201c\u4f60\u7684\u672c\u4e8b\u771f\u597d\uff0c\u6211\u867d\u7136\u4e0d\u80fd\u52a8\uff0c\u5374\u770b\u89c1\u4f60\u628a\u90a3\u574f\u86cb\u6253\u8dd1\u4e86\uff0c\u90a3\u574f\u86cb\u771f\u6ca1 \u7f9e\uff0c\u6253\u8f93\u4e86\u5c31\u54ed\uff0c\u8fd9\u4e48\u5927\u4e86\u8fd8\u54ed\u2014\u2014\u201d\u8bf4\u5230\u8fd9\u91cc\u5c0f\u8138\u4e0a\u53c8\u900f\u51fa\u4e00\u4e1d\u7b11\u5bb9\u3002 \u3000\u3000\u91d1\u8001\u4e8c\u9ed8\u9ed8\u4ece\u8170\u4e2d\u638f\u51fa\u4e24\u53ea\u706b\u7bad\uff0c\u4e00\u53ea\u7ea2\u7684\uff0c\u4e00\u53ea\u84dd\u7684\uff0c\u4ed6\u6311\u4e86\u4e00\u53ea\u84dd\u7684\uff0c\u8d70\u5230\u7a97 \u53e3\u671b\u5929\u4e0a\u653e\u4e86\u4e0a\u53bb\uff0c\u53ea\u89c1\u4e00\u7f15\u84dd\u5149\u7834\u7a7a\u800c\u53bb\uff0c\u5230\u4e86\u9876\u70b9\u4e00\u7206\u800c\u5f00\uff0c\u6709\u5982\u4e00\u6735\u76db\u5f00\u7684\u84dd\u8272 \u82b1\u513f\u3002 \u3000\u3000\u91d1\u8001\u4e8c\u8f6c\u8eab\u5411\u8f9b\u6377\u89e3\u91ca\u9053\uff1a\u201c\u54b1\u4eec\u8fd8\u6709\u51e0\u4e2a\u5144\u5f1f\u57cb\u4f0f\u5728\u5916\u9762\uff0c\u82e5\u662f\u653e\u7ea2\u7684\u706b\u82b1\u5c31\u662f \u54b1\u4eec\u95ef\u5854\u53d7\u963b\uff0c\u53ec\u4ed6\u4eec\u6765\u76f8\u52a9\uff0c\u82e5\u662f\u653e\u84dd\u7684\uff0c\u5c31\u662f\u6253\u6551\u5e2e\u4e3b\u5b8c\u6210\uff0c\u5524\u4ed6\u4eec\u6765\u6599\u7406\u5584\u540e\u3002\u201d \u3000\u3000\u5176\u5b9e\u91d1\u6c0f\u5144\u5f1f\u4f24\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u5374\u59cb\u7ec8\u4e0d\u4f1a\u653e\u7ea2\u706b\u7bad\uff0c\u53ea\u56e0\u5916 \u3000\u3000\u9762\u7684\u51e0\u4e2a\u4e10\u5e2e\u5144\u5f1f\u672c\u4e8b\u6709\u9650\uff0c\u82e5\u662f\u8fde\u91d1\u6c0f\u5144\u5f1f\u90fd\u5bf9\u4ed8\u4e0d\u4e86\uff0c\u5524\u4ed6\u4eec\u6765\u4e5f\u662f\u9001\u6b7b\uff0c\u662f \u4ee5\u91d1\u6c0f\u5144\u5f1f\u62fc\u7740\u91cd\u4f24\u4e5f\u4e0d\u653e\u7bad\u6c42\u63f4\uff0c\u8fd9\u4e5f\u662f\u91d1\u6c0f\u6606\u4ef2\u4fa0\u4e49\u4e4b\u5904\u3002 \u3000\u3000\u8f9b\u6377\u5411\u5854\u5916\u4e00\u671b\uff0c\u5ffd\u89c1\u4e00\u6761\u4eba\u5f71\u5982\u98de\u800c\u53bb\uff0c\u91d1\u8001\u4e8c\u9053\uff1a\u201c\u522b\u7ba1\u4ed6\uff0c\u8fd9\u4eba\u662f\u52fe\u6f0f\u4e00\u602a \u7fc1\u6b63\u4e4b\u5e08\u5f1f\u9752\u773c\u7ea2\u9b54\uff0c\u6562\u60c5\u4ed6\u5728\u5854\u4e0b\u53d1\u89c9\u4e0d\u5bf9\u4e5f\u8dd1\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u5ffd\u7136\u60f3\u8d77\uff1a\u201c\u4e10\u5e2e\u4e43\u662f\u56e0\u4e00\u5251\u9798\u624d\u4e0e\u5d06\u5cd2\u4ea4\u6076\uff0c\u600e\u4e48\u5c3d\u662f\u4e9b\u4ec0\u4e48\u52fe\u6f0f\u5c71\u7684\uff0c\u5374 \u4e0d\u89c1\u5389\u9e57\u9732\u9762\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5f53\u4e0b\u628a\u8fd9\u610f\u601d\u8bf4\u4e86\u51fa\u6765\uff0c\u91d1\u8001\u4e8c\u4e5f\u62cd\u817f\u9053\uff1a\u201c\u662f\u5475\uff0c\u54b1\u4eec\u4e5f\u6b63\u5728\u5947\u602a\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u9661\u7136\u8bb0\u8d77\u81ea\u5df1\u6885\u9999\u5b9d\u5251\u8fd8\u63d2\u5728\u5854\u5916\u58c1\u4e0a\uff0c\u554a\u4e86\u4e00\u58f0\uff0c\u8f6c\u8eab\u4ece\u7a97\u53e3\u8dc3\u51fa\u3002 \u3000\u3000\u91d1\u8001\u4e8c\u5fd9\u4f38\u5934\u51fa\u7a97\u4e00\u770b\uff0c\u53ea\u89c1\u8f9b\u6377\u5168\u8eab\u6241\u5e73\u5730\u8d34\u5728\u58c1\u4e0a\uff0c\u8db3\u5c16\u7d27\u62b5\u4f4f\u58c1\u4e0a\u7816\u7f1d\uff0c\u7adf \u7136\u5982\u4e00\u53ea\u5927\u58c1\u864e\u822c\u8d34\u5728\u5899\u4e0a\uff0c\u8fd9\u7b49\u529f\u592b\u6bd4\u4e4b\u4e00\u822c\u6240\u8c13\u7684\u201c\u58c1\u864e\u529f\u201d\u53c8\u4e0d\u77e5\u9ad8\u51fa\u591a\u5c11\uff0c\u56e0 \u4e3a\u58c1\u864e\u529f\u53ea\u80fd\u5728\u5899\u4e0a\u7f13\u7f13\u6e38\u52a8\uff0c\u8981\u8fd9\u6837\u505c\u4f4f\u4e0d\u52a8\u5730\u8d34\u5728\u5899\u4e0a\u5374\u662f\u4e07\u4e07\u4e0d\u80fd\uff0c\u8f9b\u6377\u8fd9\u624b\u529f \u592b\u4e43\u662f\u4ee5\u4e0a\u4e58\u8f7b\u529f\u914d\u5408\u6df1\u539a\u5185\u529f\u624d\u80fd\u529e\u5f97\u5230\u3002 \u3000\u3000\u4e14\u8bf4\u8f9b\u6377\u95ed\u7740\u4e00\u53e3\u6c14\u8d34\u5728\u5899\u4e0a\uff0c\u5374\u53d1\u73b0\u5899\u4e0a\u7684\u201c\u6885\u9999\u5251\u201d\u5df2\u4e0d\u7ffc\u800c\u98de\uff01 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u4e00\u9635\u731b\u9707\uff0c\u5b9b\u5982\u4ece\u5343\u4e08\u60ac\u5d16\u6389\u4eba\u6df1\u6e0a\uff0c\u4f46\u4ed6\u6bd5\u7adf\u67d0\u8d4b\u5f02\u51e1\uff0c\u4e00\u9635\u614c\u4e71\u540e\u9547 \u9759\u4e0b\u6765\uff0c\u4ed6\u6697\u81ea\u76d8\u7b97\uff1a\u4ec0\u4e48\u4eba\u80fd\u591f\u8d34\u5728\u8fd9\u5854\u58c1\u4e0a\u4ece\u5bb9\u62d4\u5251\uff1f\u6211\u8fd9\u4e00\u5251\u53ef\u8bf4\u63d2\u5f97\u76f8\u5f53\u6df1\u4e86\uff0c \u7edd\u4e0d\u53ef\u80fd\u662f\u5b83\u81ea\u5df1\u6389\u843d\u4e0b\u53bb\u7684\u2014\u2014\u201d \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5f53\u4eca\u6b66\u6797\u4e2d\u80fd\u6709\u50cf\u8f9b\u6377\u8fd9\u6837\u4ece\u5bb9\u8d34\u8eab\u5149\u5899\u4e0a\u7684\u529f\u529b\u8005\u5b9e\u662f\u5be5\u5be5\u53ef\u6570\uff0c\u90a3\u4e48 \u5728\u8fd9\u5be5\u5be5\u53ef\u6570\u7684\u51e0\u4eba\u4e2d\uff0c\u7a76\u7adf\u662f\u8c01\u76d7\u53bb\u4e86\u5b9d\u5251\uff1f \u3000\u3000\u8f9b\u6377\u7684\u76ee\u5149\u518d\u6b21\u843d\u5728\u63d2\u5251\u7684\u5b54\u4e0a\uff0c\u53ea\u89c1\u575a\u786c\u7684\u7816\u77f3\u4e0a\u4e00\u9053\u6574\u9f50\u7684\u53e3\uff0c\u76f4\u6df1\u4eba\u4e09\u5c3a\u4e4b \u591a\uff0c\u7816\u7f18\u6574\u9f50\u5149\u6ed1\uff0c\u6ca1\u6709\u4e1d\u6beb\u5d29\u843d\u7684\u73b0\u8c61\uff0c\u5c31\u5982\u5207\u597d\u7684\u8c46\u8150\u4e00\u822c\u3002 \u3000\u3000\u7a81\u7136\uff0c\u8f9b\u6377\u53d1\u73b0\u8fd9\u5251\u53e3\u65c1\u4e09\u5c3a\u5904\uff0c\u7adf\u4e5f\u6709\u4e00\u4e2a\u540c\u6837\u7684\u53e3\u5b50\uff0c\u8f9b\u6377\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u89c1\u90a3 \u53e3\u5b50\u6070\u5982\u4e00\u67c4\u5251\u8eab\u4e00\u822c\uff0c\u663e\u7136\u4e5f\u662f\u88ab\u5251\u5b50\u63d2\u5165\u7684\u75d5\u8ff9\u3002\u5947\u7684\u662f\u90a3\u5251\u53e3\u7816\u7f18\u4e5f\u662f\u5e73\u6574\u4e07\u5206\uff0c \u4e0d\u89c1\u4e1d\u6beb\u5d29\u843d\u3002 \u3000\u3000\u8f9b\u6377\u672c\u662f\u806a\u660e\u7edd\u9876\u7684\u4eba\uff0c\u8111\u7b4b\u4e00\u8f6c\uff0c\u5df2\u731c\u5230\u4e86\u51e0\u5206\uff0c\u4ed6\u6697\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u6885\u9999\u5251\u88ab\u5d06 \u5cd2\u5389\u9e57\u8001\u8d3c\u7ed9\u5077\u53bb\u4e86\uff0c\u4ed6\u5fc5\u662f\u4ed7\u7740\u501a\u8679\u5b9d\u5251\u63d2\u5165\u5854\u58c1\uff0c\u81ea\u5df1\u501f\u529b\u505c\u5728\u58c1\u4e0a\u624d\u76d7\u4e86\u6211\u7684 \u5251\u2026\u2026\u96be\u602a\u59cb\u7ec8\u4e0d\u89c1\u4ed6\u9732\u9762\u2014\u2014\u201d \u3000\u3000\u4ed6\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u6012\uff0c\u771f\u529b\u4e00\u61c8\uff0c\u8eab\u4f53\u987f\u65f6\u4e0b\u843d\uff0c\u4ed6\u5f85\u8eab\u5b50\u843d\u5230\u7b2c\u5341\u4e8c\u5c42\u7684 \u5c4b\u69fd\u65f6\uff0c\u624d\u4f38\u624b\u5728\u74e6\u80cc\u4e00\u6309\uff0c\u501f\u529b\u817e\u8eab\u800c\u8d77\uff0c\u7ffb\u8eab\u98d8\u5165\u5854\u9876\uff0c\u59ff\u52bf\u7f8e\u5999\u5df2\u6781\u3002 \u3000\u3000\u91d1\u8001\u4e8c\u559d\u4e86\u4e00\u58f0\u91c7\uff0c\u5bf9\u8f9b\u6377\u7684\u529f\u529b\u771f\u662f\u4f69\u670d\u65e0\u6bd4\u3002 \u3000\u3000\u91d1\u8001\u5927\u4e5f\u6e10\u6e10\u80fd\u6276\u7740\u7ad9\u8d77\u8eab\u6765\uff0c\u4ed6\u89c1\u8f9b\u6377\u9762\u8272\u4e0d\u5bf9\uff0c\u9042\u5f00\u53e3\u9053\uff1a\u201c\u8f9b\u5144\u82e5\u662f\u6709\u4ec0\u4e48 \u4e8b\u7528\u5f97\u7740\u54b1\u5144\u5f1f\u7684\uff0c\u5c3d\u7ba1\u5429\u5490\u4e0b\u6765\u5c31\u662f\u3002\u201d \u3000\u3000\u8f9b\u6377\u832b\u7136\u6447\u4e86\u6447\u5934\uff0c\u53c8\u5f3a\u7b11\u9053\uff1a\u201c\u6ca1\u6709\u4ec0\u4e48\uff0c\u6211\u6709\u4e00\u67c4\u666e\u901a\u957f\u5251\u7559\u5728\u58c1\u4e0a\uff0c\u65b9\u624d\u53bb \u770b\u65f6\u5374\u4e0d\u89c1\u4e86\uff0c\u60f3\u662f\u8dcc\u4e86\u4e0b\u53bb\u5427\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u7684\u4e2a\u6027\u9ad8\u50b2\u5f97\u5f88\uff0c\u82e5\u662f\u670b\u53cb\u6c42\u52a9\u4e8e\u4ed6\uff0c\u4ed6\u81ea\u662f\u70ed\u5ff1\u4e07\u5206\uff0c\u4f46\u82e5\u8981\u4ed6\u6c42\u4eba\u5e2e\u52a9\uff0c \u4ed6\u5374\u662f\u5927\u5927\u4e0d\u613f\uff0c\u662f\u4ee5\u4ed6\u5bf9\u5931\u5b9d\u5251\u4e4b\u4e8b\u652f\u543e\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u91d1\u6c0f\u5144\u5f1f\u90fd\u662f\u8c6a\u6770\u4e4b\u58eb\uff0c\u867d\u77e5\u8f9b\u6377\u8a00\u4e0d\u7531\u8877\uff0c\u4f46\u4e5f\u4e0d\u518d\u591a\u95ee\u3002 \u3000\u3000\u8f9b\u6377\u62b1\u62f3\u5bf9\u91d1\u6c0f\u6606\u4ef2\u9053\uff1a\u201c\u5144\u5f1f\u73b0\u5728\u6709\u4e00\u8981\u4e8b\uff0c\u5fc5\u987b\u7acb\u523b\u53bb\u529e\uff0c\u65e5\u540e\u4e24\u4f4d\u82e5\u662f\u6709\u4ec0 \u4e48\u4e8b\u8981\u627e\u5144\u5f1f\u7684\uff0c\u5144\u5f1f\u5343\u91cc\u4e4b\u5916\u5fc5\u7136\u661f\u591c\u8d76\u5230\u3002\u201d \u3000\u3000\u91d1\u6c0f\u5144\u5f1f\u89c1\u4ed6\u8138\u8272\u7126\u6025\uff0c\u77e5\u4ed6\u5fc5\u6709\u8981\u4e8b\uff0c\u53ea\u62b1\u62f3\u4e00\u793c\u9053\uff1a\u201c\u8f9b\u5144\u662f\u54b1\u4eec\u5f1f\u5144\u7684\u5927\u6069 \u4eba\uff0c\u4e5f\u662f\u4e10\u5e2e\u7684\u5927\u6069\u4eba\uff0c\u8fd9\u4e2a\u54b1\u4eec\u7ec8\u8eab\u4e0d\u6562\u5fd8\u3002\u201d \u3000\u3000\u8f9b\u6377\u5bf9\u9e4f\u513f\u9053\uff1a\u201c\u9e4f\u513f\u597d\u751f\u8ddf\u7740\u91d1\u53d4\u53d4\uff0c\u597d\u597d\u7ec3\u597d\u529f\u592b\uff0c\u5c06\u6765\u4e10\u5e2e\u5168\u9760\u4f60\u91cd\u632f\u58f0\u5a01 \u54e9\u3002\u201d \u3000\u3000\u8bf4\u7f62\u4e00\u8f6c\u8eab\u98de\u51fa\u5854\u9876\uff0c\u51e0\u4e2a\u8d77\u843d\u5df2\u5728\u4e09\u5341\u591a\u4e08\u4e4b\u5916\uff0c\u9e4f\u513f\u8ffd\u5230\u7a97\u53e3\u53eb\u9053\uff1a \u3000\u3000\u201c\u8f9b\u53d4\u53d4\u4ec0\u4e48\u65f6\u5019\u6765\u770b\u9e4f\u513f\u554a\uff1f\u201d \u3000\u3000\u58f0\u97f3\u4f20\u51fa\uff0c\u8f9b\u6377\u8eab\u5f71\u5df2\u6d88\u5931\u5728\u83bd\u83bd\u4e1b\u6797\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u6ee1\u5fc3\u7126\u6025\u5730\u5306\u5306\u8d76\u8def\uff0c\u4ed6\u5fc3\u4e2d\u6697\u60f3\uff1a\u201c\u95ef\u4e0a\u5d06\u5cd2\u540e\u7ed9\u4ed6\u5927\u95f9\u4e00\u573a\uff0c\u90a3\u5389\u9e57\u603b\u4e0d \u80fd\u4e0d\u9732\u9762\u4e86\u5427\uff0c\u54fc\uff0c\u53ea\u8981\u4ed6\u4e00\u9732\u9762\uff0c\u6211\u4e0d\u4f46\u8981\u8ba8\u56de\u5b9d\u5251\uff0c\u8fd8\u8981\u6e05\u4e00\u6e05\u54b1\u4eec\u4e4b\u95f4\u7684\u65e7\u5e10\u3002\u201d \u3000\u3000\u6240\u8c13\u65e7\u5e10\uff0c\u81ea\u7136\u662f\u6307\u5389\u9e57\u6697\u7b97\u6885\u5c71\u6c11\u7684\u8001\u6848\uff0c\u6b64\u523b\uff0c\u8f9b\u6377\u6839\u672c\u4e0d\u628a\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u201d \u7684\u5d06\u5cd2\u638c\u95e8\u653e\u5728\u773c\u5185\u3002 \u3000\u3000\u8fd9\u4e00\u6bb5\u8def\u751a\u662f\u8352\u50fb\uff0c\u8f9b\u6377\u53ef\u4ee5\u6beb\u65e0\u5fcc\u60ee\u5730\u65bd\u5c55\u8f7b\u529f\u7edd\u6280\u5954\u9a70\uff0c\u4ed6\u53ea\u89c9\u81ea\u4e0e\u52fe\u6f0f\u4e00\u602a \u4e00\u573a\u6fc0\u6218\uff0c\u81ea\u5df1\u529f\u529b\u4f3c\u4e4e\u53c8\u589e\u52a0\u4e86\u4e0d\u5c11\uff0c\u8fd9\u65f6\u4ed6\u53ea\u662f\u8f7b\u677e\u5730\u8dd1\u7740\uff0c\u4f46\u901f\u5ea6\u5374\u6781\u4e3a\u60ca\u4eba\u2014 \u2014 \u3000\u3000\u5ffd\u7136\u547c\u7684\u4e00\u58f0\uff0c\u4e00\u53ea\u9e3d\u5b50\u4ece\u4f4e\u7a7a\u60ca\u8fc7\uff0c\u8f9b\u6377\u773c\u5c16\uff0c\u65e9\u77a5\u89c1\u90a3\u9e3d\u5b50\u8db3\u4e0a\u7ed1\u4e86\u4e00\u6839\u7ea2\u5e26 \u5b50\uff0c\u663e\u7136\u662f\u9001\u4fe1\u7684\u9e3d\u5b50\u3002\u90a3\u5e74\u5934\u7528\u9e3d\u5b50\u4f20\u4fe1\u4e5f\u751a\u666e\u901a\uff0c\u8f9b\u6377\u5e76\u4e0d\u4ee5\u4e3a\u610f\u3002 \u3000\u3000\u8fce\u9762\u51c9\u98ce\u5439\u6765\uff0c\u5e26\u6765\u4e00\u4e1d\u6e7f\u5473\uff0c\u8f9b\u6377\u6697\u9053\uff1a\u201c\u524d\u9762\u5fc5\u6709\u6cb3\u6c34\u3002\u201d \u3000\u3000\u5954\u4e86\u4e0d\u5230\u534a\u76cf\u8336\u8fb0\u5149\uff0c\u7ed3\u679c\u542c\u89c1\u6d69\u6d69\u8361\u8361\u7684\u6c34\u58f0\uff0c\u8f9b\u6377\u4e0d\u7981\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u60f3\u81ea\u5df1\u5728 \u5916\u9762\u8dd1\u4e86\u8fd9\u4e9b\u65e5\u5b50\uff0c\u89c1\u8bc6\u7ecf\u9a8c\u7740\u5b9e\u4e5f\u589e\u957f\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u8d70\u5f97\u8fd1\u6765\uff0c\u679c\u7136\u89c1\u4e00\u6761\u5c0f\u6cb3\u6a2a\u5728\u524d\u9762\uff0c\u6cb3\u9762\u4e0d\u5bbd\uff0c\u4f46\u6c34\u6d41\u5374\u5341\u5206\u6e4d\u6025\uff0c\u53ea\u89c1\u6cb3\u6c34\u6d69 \u8361\uff0c\u6012\u6d9b\u6f8e\u6e43\uff0c\u4fef\u89c6\u4ee4\u4eba\u6655\u7729\u3002 \u3000\u3000\u5374\u4e5f\u51d1\u5de7\uff0c\u6b63\u5f53\u8f9b\u6377\u8d70\u5230\u6cb3\u8fb9\uff0c\u4e0a\u6e38\u51b2\u4e0b\u4e00\u53ea\u8239\u6765\uff0c\u53ea\u89c1\u8239\u4e2d\u7a7a\u7a7a\uff0c\u9664\u4e86\u4e00\u4e2a\u68a2\u516c \u6ca1\u6709\u4e00\u4e2a\u5ba2\u4eba\uff0c\u90a3\u68a2\u516c\u6b63\u7528\u957f\u7bd9\u53cd\u6491\uff0c\u51cf\u4f4e\u8239\u7684\u901f\u5ea6\uff0c\u4f3c\u4e4e\u6253\u7b97\u505c\u5c06\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u8239\u884c\u751a\u901f\uff0c\u4f3c\u4e4e\u4e0d\u53ef\u80fd\u7acb\u523b\u505c\u4f4f\uff0c\u4f46\u89c1\u90a3\u68a2\u516c\u4e0d\u614c\u4e0d\u5fd9\u4ece\u8231\u4e2d\u53d6\u51fa\u4e00\u6761\u5927\u7f06\uff0c\u5934 \u4e0a\u5708\u6210\u4e00\u4e2a\u5708\u5957\uff0c\u53ea\u89c1\u4ed6\u5728\u5934\u4e0a\u8f6c\u4e86\u4e24\u5708\uff0c\u547c\u7684\u4e00\u58f0\u629b\u4e86\u8fc7\u6765\uff0c\u90a3\u5708\u513f\u6070\u5de7\u5957\u5728\u5cb8\u8fb9\u4e00 \u4e2a\u5927\u6728\u6869\u4e0a\uff0c\u8f9b\u6377\u4e0d\u7981\u9a7b\u8db3\u53eb\u4e86\u4e00\u58f0\u597d\u3002 \u3000\u3000\u90a3\u68a2\u516c\u53cc\u8db3\u9489\u7acb\u8239\u677f\u4e0a\uff0c\u53cc\u624b\u52a0\u52b2\u4e00\u62c9\uff0c\u8239\u513f\u5c31\u7f13\u7f13\u9760\u5cb8\u3002 \u3000\u3000\u8f9b\u6377\u4e0a\u524d\u95ee\u9053\uff1a\u201c\u6562\u95ee\u5927\u54e5\u5f80\u5d06\u5cd2\u5c71\u600e\u4e48\u8d70\uff1f\u201d \u3000\u3000\u90a3\u68a2\u516c\u9053\uff1a\u201c\u987a\u8fd9\u6761\u6c34\u5230\u4e86\u6210\u5bb6\u9547\u518d\u5f80\u897f\u8d70\u3002\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u68a2\u516c\u4f60\u8fd9\u8239\u53ef\u662f\u8981\u5230\u6210\u5bb6\u9547\uff1f\u8f7d\u6211\u4e00\u8d9f\u600e\u6837\uff1f\u201d \u3000\u3000\u90a3\u68a2\u516c\u4eba\u5012\u4e0d\u9519\uff0c\u7b11\u9053\uff1a\u201c\u4ffa\u8fd9\u8239\u6b63\u662f\u5230\u6210\u5bb6\u9547\u7684\uff0c\u5ba2\u5b98\u8981\u642d\u53ea\u7ba1\u4e0a\u6765\u5c31\u662f\uff0c\u54b1\u4eec \u8def\u4e0a\u4e5f\u597d\u591a\u4e00\u4e2a\u804a\u5929\u7684\u4f19\u4f34\u3002\u201d \u3000\u3000\u8f9b\u6377\u8c22\u4e86\u4e00\u58f0\uff0c\u6b65\u4e0a\u8239\u5934\uff0c\u90a3\u68a2\u516c\u624b\u4e0a\u4e00\u6296\uff0c\u7ef3\u5957\u547c\u7684\u53c8\u98de\u56de\uff0c\u90a3\u8239\u7acb\u523b\u987a\u6d41\u800c\u4e0b\u3002 \u3000\u3000\u8239\u987a\u6c34\u52bf\uff0c\u751a\u662f\u8fc5\u901f\uff0c\u4e24\u5cb8\u666f\u7269\u5411\u540e\u98de\u5012\uff0c\u66f4\u663e\u51fa\u8239\u7684\u8f7b\u5feb\uff0c\u68a2\u516c\u5bf9\u8f9b\u6377\u9053\uff1a \u3000\u3000\u201c\u5ba2\u5b98\u4e0d\u662f\u672c\u5730\u4eba\u7f62\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5e94\u4e86\u4e00\u58f0\uff0c\u53cd\u95ee\u9053\uff1a\u201c\u6211\u770b\u4f60\u4e5f\u4e0d\u662f\u672c\u5730\u4eba\u5427\uff1f\u201d \u3000\u3000\u68a2\u516c\u9053\uff1a\u201c\u4ffa\u539f\u7c4d\u5c71\u4e1c\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\u53f9\u4e86\u4e00\u53e3\u6c14\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\u4ed6\u624d\u7ee7\u7eed\u9053\uff1a\u201c\u4ffa\u5bb6\u91cc\u672c\u662f\u79cd\u7530\u7684\uff0c\u90a3\u8d3c\u53ae\u9e1f\u7684\u53bf\u592a\u7237\u8981\u8ba8\u4ffa\u7684\u59b9\u5b50\u505a\u5c0f \u8001\u5a46\uff0c\u4ffa\u59b9\u5b50\u4e0d\u4ece\uff0c\u7ed3\u679c\u4ffa\u7237\u5a18\u88ab\u6349\u8fdb\u4e86\u8859\u95e8\uff0c\u6070\u5de7\u6cb3\u6c34\u6cdb\u6ee5\uff0c\u4ffa\u5bb6\u91cc\u7530\u56ed\u88ab\u6df9\u5f97\u4e00\u4e1d \u4e0d\u5269\uff0c\u5509\uff0c\u4ffa\u5c31\u6d41\u843d\u5230\u5f02\u4e61\u6765\u5566\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4e5f\u4e0d\u7981\u957f\u53f9\u4e00\u58f0\uff0c\u4ed6\u89c1\u90a3\u68a2\u516c\u9ed8\u5750\u8231\u5934\uff0c\u6b63\u5728\u6000\u5ff5\u5317\u65b9\u7684\u8001\u5bb6\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697\u53f9 \u9053\uff1a\u201c\u540c\u662f\u5929\u6daf\u6ca6\u843d\u4eba\uff0c\u76f8\u9022\u4f55\u5fc5\u66fe\u76f8\u8bc6\u3002 \u3000\u3000\u770b\u6765\u4e16\u4e0a\u5feb\u6d3b\u7684\u4eba\u56fa\u7136\u4e0d\u5c11\uff0c\u4f46\u662f\u7edd\u5927\u591a\u6570\u7684\u4eba\u90fd\u662f\u5fe7\u6101\u7684\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u60f3\u5230\u81ea\u5df1\u7684\u8eab\u4e16\uff0c\u65e0\u7aef\u7aef\u90a3\u4e9b\u53ef\u7231\u7684\u5029\u5f71\u53c8\u4e00\u4e00\u98d8\u5165\u8111\u6d77\uff0c\u4e00\u65f6\u597d\u50cf\u5929\u4e0b\u4e0d\u5982 \u610f\u7684\u4e8b\u90fd\u6d6e\u73b0\u5728\u773c\u524d\uff0c\u4ed6\u76f4\u60f3\u653e\u58f0\u5927\u54ed\u4e00\u573a\u3002 \u3000\u3000\u5ffd\u7136\u4ed6\u60f3\u5230\u90a3\u75af\u75af\u766b\u766b\u7684\u6bd2\u541b\u91d1\u4e00\u9e4f\uff0c\u4ed6\u60f3\uff1a\u201c\u50cf\u4ed6\u90a3\u6837\u957f\u6b4c\u72c2\u7b11\uff0c\u60f3\u600e\u6837\u5c31\u600e\u6837\uff0c \u5927\u6982\u603b\u6ca1\u6709\u70e6\u607c\u4e86\u5427\u3002\u201d \u3000\u3000\u4ed6\u8111\u6d77\u4e2d\u5145\u6ee1\u7740\u91d1\u4e00\u9e4f\u766b\u72c2\u7684\u5f71\u5b50\uff0c\u8033\u6735\u4e2d\u5168\u662f\u72c2\u653e\u7684\u7b11\u58f0\uff0c\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u90a3\u7b11 \u58f0\u5ffd\u7136\u5df1\u53d8\u6210\u4e86\u51c4\u5389\u800c\u9634\u68ee\u7684\u51b7\u7b11\uff0c\u8fd9\u662f\u6740\u7236\u6bcd\u5927\u4ec7\u201c\u6d77\u5929\u53cc\u715e\u201d\u7684\u7b11\u58f0\u554a\uff01 \u3000\u3000\u4ed6\u6e38\u76ee\u56db\u671b\uff0c\u5e76\u65e0\u6d77\u5929\u53cc\u715e\u7684\u5f71\u5b50\uff0c\u4ed6\u77e5\u9053\u662f\u81ea\u5df1\u7684\u5e7b\u89c9\u6240\u81f4\uff0c\u4f46\u662f\u8fd9\u4e48\u4e00\u6765\uff0c\u90a3 \u4e9b\u51c4\u60e8\u7684\u5f80\u4e8b\u4e00\u5e55\u4e00\u5e55\u5730\u6d6e\u8fc7\u773c\u524d\u2026\u2026 \u3000\u3000\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4ed6\u4e0d\u60f3\u8fd9\u4e9b\uff0c\u4e0d\u662f\u4e0d\u60f3\uff0c\u800c\u662f\u4e0d\u6562\u60f3\uff0c\u5176\u5b9e\u5728\u4ed6\u5185\u5fc3\u6700\u6df1\u5904\u54ea\u4e00\u5206\u949f \u54ea\u4e00\u79d2\u949f\u4e0d\u5728\u60f3\u7740\u8fd9\u4e9b\uff1f\u53ea\u662f\u4e00\u5f53\u4ed6\u9759\u4e0b\u6765\uff0c\u4ed6\u5c31\u80e1\u601d\u4e71\u60f3\u4e00\u4e9b\u5176\u4ed6\u7684\u4e8b\u7269\u6765\u51b2\u6de1\u8fd9\u4e9b \u6101\u601d\uff0c\u73b0\u5728\uff0c\u8fd9\u4e9b\u6101\u601d\u5982\u6cc9\u6c34\u4e00\u822c\u6d8c\u6d8c\u800c\u51fa\u2014\u2014 \u3000\u3000\u4ed6\u60f3\u5230\u6bcd\u4eb2\u5728\u53cc\u715e\u4fae\u8fb1\u4e0b\u6c42\u751f\u4e0d\u80fd\u6c42\u6b7b\u4e0d\u5f97\u7684\u60c5\u666f\uff0c\u90a3\u4e00\u5207\u4e00\u5207\u4ed6\u4fe9\u6e05\u6e05\u695a\u695a\u5730\u8bb0 \u7740\uff0c\u4e00\u4e1d\u4e00\u6beb\u4e5f\u6ca1\u6709\u5fd8\u6000\uff0c\u4ed6\u6bcf\u89c9\u5f97\u5982\u679c\u5fd8\u4e86\u4e00\u4e1d\uff0c\u4ed6\u5c31\u662f\u5bf9\u4e0d\u8d77\u7236\u6bcd\u2026\u2026 \u3000\u3000\u5f80\u4e8b\u98de\u5feb\u5730\u5728\u4ed6\u773c\u524d\u79fb\u52a8\uff0c\u7a81\u7136\u4ed6\u60f3\u5230\u5728\u5c0f\u6222\u5c9b\u4e0a\u8c6a\u653e\u4e00\u6b4c\u7684\u60c5\u666f\uff0c\u4ed6\u9661\u7136\u60ca\u9192\uff0c \u4e0d\u7981\u6d51\u8eab\u51fa\u4e86\u4e00\u9635\u51b7\u6c57\uff0c\u90a3\u8c6a\u653e\u7684\u6b4c\u8bcd\u4ed6\u8fd8\u8bb0\u5f97\uff1a\u201c\u4e71\u77f3\u5d29\u4e91\uff0c\u60ca\u6d9b\u88c2\u5cb8\uff0c\u5377\u8d77\u5343\u5806\u96ea\uff0c \u6c5f\u5c71\u5982\u753b\uff0c\u4e00\u65f6\u591a\u5c11\u8c6a\u6770\uff01\u201d \u3000\u3000\u4ed6\u4e0d\u7981\u529b\u8d2f\u53cc\u8db3\uff0c\u4ece\u76d8\u5750\u4e00\u8dc3\u800c\u8d77\uff0c\u62ac\u773c\u671b\u65f6\uff0c\u6c5f\u6d41\u6c79\u6d8c\uff0c\u767d\u6d6a\u6ed4\u6ed4\uff0c\u5954\u6d41\u9047\u5230\u5cb8 \u77f3\u963b\u8def\u65f6\uff0c\u5f20\u7259\u6e0d\u6cab\u5730\u72c2\u543c\uff0c\u524d\u4ec6\u540e\u7ee7\u5730\u5377\u62cd\uff0c\u4ed6\u5fd8\u5374\u4e00\u5207\u987e\u5fcc\uff0c\u632f\u58f0\u957f\u5578\u2014\u2014 \u3000\u3000\u55b7\u4eae\u7684\u5578\u58f0\u9707\u5f97\u5c71\u8c37\u9f50\u9e23\uff0c\u68a2\u516c\u7684\u8033\u819c\u9669\u4e9b\u88ab\u9707\u88c2\uff0c\u597d\u534a\u65e0\uff0c\u4ee5\u540e\u8fd8\u5728\u55e1\u55e1\u4f5c\u58f0\uff0c \u4ed6\u6697\u9053\uff1a\u201c\u8fd9\u5ba2\u5b98\u597d\u5927\u7684\u55d3\u5b50\u3002\u201d \u3000\u3000\u4e24\u5cb8\u4ece\u6797\u4e2d\u4e00\u9635\u4e71\u52a8\uff0c\u7fa4\u9e1f\u88ab\u5578\u58f0\u60ca\u8d77\uff0c\u9f50\u98de\u800c\u51fa\uff0c\u5f20\u7ffc\u8fbe\u6570\u5c3a\u7684\u79c3\u9e70\u6570\u5343\u53ea\u540c\u65f6 \u800c\u8d77\uff0c\u767b\u65f6\u853d\u906e\u6ee1\u7a7a\uff0c\u58ee\u89c2\u81f3\u6781\u3002 \u3000\u3000\u8f9b\u6377\u671b\u7740\u8fd9\u5dcd\u7136\u5947\u666f\uff0c\u987f\u65f6\u5ba0\u8fb1\u7686\u5fd8\uff0c\u6ee1\u5fc3\u5145\u6ee1\u7740\u5feb\u610f\uff0c\u6d0b\u6d0b\u81ea\u5f97\u2014\u2014 \u3000\u3000\u5ffd\u7136\u68a2\u516c\u53eb\u9053\uff1a\u201c\u5ba2\u5b98\uff0c\u6210\u5bb6\u9547\u5230\u4e86\uff01\u201d \u3000\u3000\u5929\u65b9\u7834\u6653\uff0c\u91d1\u4e91\u752b\u73b0\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5df2\u7ecf\u79bb\u5f00\u4e86\u6210\u5bb6\u9547\uff0c\u8fd9\u4e00\u5e26\u4eba\u70df\u7a20\u5bc6\uff0c\u8f9b\u6377\u53ea\u597d\u7f13\u7f13\u4ee5\u5e38\u4eba\u7684\u6b65\u4f10\u8d70\u7740\uff0c\u5c3d\u7ba1 \u4ed6\u7684\u5fc3\u4e2d\u7126\u6025\u4e07\u5206\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u7f13\u7f13\u5730\u884c\u7740\uff0c\u6210\u5bb6\u9547\u5230\u96c6\u5e86\u53bf\u4e0d\u8fc7\u4e24\u767e\u91cc\uff0c\u8f9b\u6377\u90fd\u8db3\u8db3\u8d70\u4e86\u4e09\u65e5\u534a\u624d\u5230\u3002 \u3000\u3000\u4e00\u8fdb\u96c6\u5e86\u53bf\u57ce\u95e8\uff0c\u4ed6\u5c31\u89c9\u5f97\u60c5\u5f62\u6709\u70b9\u5f02\u6837\uff0c\u8fd9\u5c0f\u53bf\u9547\u91cc\u7adf\u6765\u6765\u5f80\u5f80\u6709\u8bb8\u591a\u6c5f\u6e56\u4eba\u7269\uff0c \u7b49\u5230\u4ed6\u4ece\u6b63\u95e8\u5927\u8def\u4e00\u8f6c\u5f2f\u65f6\uff0c\u4ed6\u5c31\u604d\u7136\u5927\u609f\u4e86\u3002 \u3000\u3000\u539f\u6765\u7531\u6b63\u95e8\u5927\u8def\u4e00\u8f6c\u5f2f\uff0c\u7b2c\u4e00\u4e2a\u4eba\u773c\u7684\u5c31\u662f\u4e00\u5757\u4e08\u957f\u7684\u76f4\u6761\u62db\u724c\uff0c\u91d1\u8272\u7684\u5b57\u6709\u6597\u5927\uff1a \u3000\u3000\u201c\u5448\u7965\u9556\u5c40\u201d \u3000\u3000\u6562\u60c5\u90a3\u4e9b\u6c5f\u6e56\u6253\u626e\u7684\u4eba\u5168\u662f\u8ddf\u8fd9\u9556\u5c40\u6709\u5173\u7684\u3002 \u3000\u3000\u8f9b\u6377\u8d70\u5230\u4e00\u5bb6\u9152\u697c\u4e2d\uff0c\u62e3\u4e86\u4e00\u6240\u8f83\u6e05\u9759\u7684\u5ea7\u4f4d\uff0c\u51c6\u5907\u5ba2\u996d\u3002\u5ffd\u7136\u697c\u68af\u767b\u767b\u54cd\u5904\uff0c\u4e0a \u6765\u56db\u4e94\u4e2a\u9556\u5e08\u4e4b\u7c7b\u7684\u5927\u6c49\uff0c\u6b63\u597d\u5750\u5728\u8f9b\u6377\u7684\u5bf9\u9762\uff0c\u5927\u58f0\u5406\u559d\u5730\u8981\u4e86\u4e94\u65a4\u8001\u9152\uff0c\u5341\u65a4\u725b\u8089 \u5c31\u5f00\u59cb\u9ad8\u8c08\u9614\u8bba\u8d77\u6765\u3002 \u3000\u3000\u5de6\u9996\u90a3\u4e2a\u5927\u80e1\u5b50\u9053\uff1a\u201c\u8fd9\u6b21\u54b1\u4eec\u5144\u5f1f\u7b97\u662f\u683d\u5230\u5bb6\u4e86\uff0c\u5e78\u597d\u54b1\u4eec\u9556\u5934\u6709\u5148\u89c1\u4e4b\u660e\uff0c\u4e0d \u7136\u6697\u9556\u4e5f\u7ed9\u641c\u53bb\u7684\u8bdd\uff0c\u54b1\u4eec\u54e5\u513f\u4eec\u4e5f\u4e0d\u8981\u6df7\u4e86\u3002\u201d \u3000\u3000\u53f3\u8fb9\u4e00\u4e2a\u77ee\u5c0f\u7684\u6c49\u5b50\u54bd\u4e86\u4e00\u53e3\u725b\u8089\u9053\uff1a\u201c\u8c01\u53eb\u54b1\u4eec\u78b0\u4e0a\u5c71\u5de6\u53cc\u8c6a\u5462\uff1f\u51ed\u4eba\u5bb6\u53cc\u8c6a\u7684 \u540d\u5934\u54b1\u4eec\u5927\u4f19\u513f\u4e00\u9f50\u4e0a\u4e5f\u4e0d\u6210\u554a\uff0c\u542c\u8bf4\u4ed6\u4eec\u6700\u8fd1\u52a0\u5165\u4e86\u5173\u4e2d\u4e5d\u8c6a\u5462\uff01\u201d \u3000\u3000\u8f9b\u6377\u4e00\u542c\u5c71\u5de6\u53cc\u8c6a\uff0c\u7acb\u523b\u6ce8\u610f\u542c\u4e0b\u53bb\u2014\u2014 \u3000\u3000\u5de6\u9996\u65c1\u8fb9\u7684\u4e00\u4e2a\u80d6\u8001\u9053\uff1a\u201c\u8fd8\u8bf4\u54e9\uff0c\u54b1\u4eec\u8981\u662f\u6709\u2018\u6885\u9999\u795e\u5251\u2019\u8f9b\u6377\u7684\u4e00\u534a\u672c\u4e8b\uff0c\u53ef \u5c31\u4e0d\u6015\u4ec0\u4e48\u5c71\u5de6\u53cc\u8c6a\u5566\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e00\u542c\u201c\u6885\u9999\u795e\u5251\u8f9b\u6377\u201d\u51e0\u5b57\u4e0d\u7981\u5927\u60ca\uff0c\u5fc3\u60f3\u81ea\u5df1\u54ea\u6765\u4ec0\u4e48\u201c\u6885\u9999\u795e\u5251\u201d\u7684\u5916\u53f7\uff1f \u83ab\u975e\u53e6\u6709\u4e00\u4e2a\u4e5f\u53eb\u505a\u8f9b\u6377\uff1f \u3000\u3000\u53ea\u542c\u90a3\u9996\u5148\u53d1\u8bdd\u7684\u80e1\u5b50\u6c49\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u8001\u674e\u771f\u6ca1\u7f9e\uff0c\u51ed\u4f60\u8fd9\u5757\u6599\u518d\u7ec3\u4e00\u767e\u5e74\u4e5f\u53ca\u4e0d \u4e0a\u4eba\u5bb6\u8f9b\u5927\u4fa0\u4e00\u534a\u54e9\uff0c\u4f60\u60f3\u60f3\u52fe\u6f0f\u4e00\u602a\u7fc1\u6b63\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5728\u795e\u9706\u5854\u9876\u548c\u8f9b\u5927\u4fa0\u8d4c\u6597\u65f6\uff0c \u8bb2\u660e\u4e00\u573a\u62f3\u811a\u4e00\u573a\u5251\u672f\uff0c\u7ed3\u679c\u5927\u540d\u9f0e\u9f0e\u7684\u52fe\u6f0f\u4e00\u602a\u7adf\u786c\u63a5\u4e0d\u4e0b\u8f9b\u5927\u4fa0\u5341\u62f3\u2014\u2014\u201d \u3000\u3000\u80e1\u5b50\u6c49\u8bf4\u5f97\u7ed8\u58f0\u7ed8\u5f62\uff0c\u53e3\u6cab\u4e71\u98de\uff0c\u4eff\u4f5b\u4ed6\u81ea\u5df1\u53d8\u6210\u8f9b\u5927\u4fa0\u4e00\u822c\u3002 \u3000\u3000\u8f9b\u6377\u542c\u5f97\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u53ef\u6b63\u662f\u8bf4\u6211\u554a\uff0c\u600e\u4e48\u6211\u548c\u52fe\u6f0f\u4e00\u602a\u62fc\u6597\u7684\u6d88\u606f\u8fd9\u4e48 \u5feb\u5c31\u4f20\u5f00\u4e86\uff0c\u53ef\u7b11\u8fd9\u4e9b\u4eba\u52a0\u6cb9\u52a0\u918b\u5730\u4e0d\u77e5\u8981\u628a\u6211\u8bf4\u6210\u4ec0\u4e48\u4eba\u7269\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u90a3\u80e1\u5b50\u6c49\u4ecd\u5f97\u610f\u5730\u7ee7\u7eed\u8bf4\uff1a\u201c\u563f\u563f\uff0c\u7b2c\u4e8c\u573a\u7fc1\u6b63\u8981\u6bd4\u5251\u672f\uff0c\u4ed6\u90a3\u2018\u4ee4\u5937\u5251\u6cd5\u2019 \u53ef\u771f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u7ed3\u679c\uff0c\u563f\u563f\uff0c\u8f9b\u5927\u4fa0\u7528\u90a3\u4e2a\u2026\u2026\u90a3\u4e2a\u5251\u6cd5\u4e09\u62db\u5c31\u5c06\u4ed6\u5251\u5b50\u6311\u98de\uff0c\u624d\u626c \u957f\u800c\u53bb\uff0c\u8fd9\u4efd\u529f\u529b\u624d\u771f\u7b97\u5f97\u4e0a\u5927\u4fa0\u540d\u5934\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u867d\u7136\u6697\u9a82\u8fd9\u4e9b\u4eba\u55a7\u67d3\u5f97\u592a\u4e0d\u6210\u8bdd\uff0c\u4f46\u5fc3\u6df1\u5904\u4ecd\u514d\u4e0d\u4e86\u4e00\u9635\u7a83\u559c\u3002 \u3000\u3000\u53ea\u542c\u90a3\u77ee\u5b50\u53c8\u9053\uff1a\u201c\u94b1\u5927\u54e5\u4f60\u8bf4\u8fd9\u4f4d\u2018\u6885\u9999\u795e\u5251\u2019\u8f9b\u5927\u4fa0\u5f3a\u4e9b\u8fd8\u662f\u2018\u6b66\u6797\u4e4b\u79c0\u2019\u5f3a \u4e9b\uff1f\u201d \u3000\u3000\u80e1\u5b50\u6c49\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u2018\u6b66\u6797\u4e4b\u79c0\u2019\u5b59\u501a\u91cd\u4e48\uff1f\u201d \u3000\u3000\u77ee\u5b50\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u662f\u8c01\u3002\u201d \u3000\u3000\u80e1\u5b50\u6c49\u9053\uff1a\u201c\u8fd9\u4e24\u4f4d\u5927\u4fa0\u90fd\u662f\u4e00\u822c\u5e74\u9752\uff0c\u4e5f\u90fd\u6709\u4e00\u8eab\u4e86\u4e0d\u8d77\u7684\u529f\u592b\uff0c\u636e\u6211\u770b\u8f9b\u5927\u4fa0 \u867d\u7136\u5389\u5bb3\uff0c\u6050\u6015\u8fd8\u662f\u5b59\u5927\u4fa0\u5f3a\u4e9b\u513f\u3002\u201d \u3000\u3000\u90a3\u80d6\u5b50\u8001\u6c14\u6a2a\u79cb\u5730\u9053\uff1a\u201c\u4f55\u4ee5\u89c1\u5f97\uff1f\u201d \u3000\u3000\u80e1\u5b50\u6c49\u9053\uff1a\u201c\u6211\u8bf4\u4e00\u4e2a\u4eba\u4f60\u5c31\u77e5\u9053\u4e86\uff0c\u90a3\u6b64\u541b\u91d1\u8001\u7237\u5b50\u7684\u9ad8\u5f92\u5929\u9b54\u91d1\u6b39\u4f60\u4eec\u603b\u6653\u5f97 \u4e86\u5427\uff0c\u4ed6\u90a3\u624b\u529f\u592b\u771f\u662f\u5c3d\u5f97\u6b64\u541b\u4e4b\u4f20\uff0c\u53ef\u662f\u534a\u5e74\u524d\u66fe\u88ab\u5b59\u501a\u91cd\u5927\u4fa0\u4e00\u638c\u9707\u9000\u54e9\uff0c\u4f60\u60f3\u60f3 \u8fd9\u4efd\u529f\u592b\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u77ee\u5b50\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u5bf9\u4e5f\u7f62\u4e0d\u5bf9\u4e5f\u7f62\uff0c\u54b1\u4eec\u8fd8\u662f\u559d\u9152\u7684\u662f\u3002\u201d \u3000\u3000\u51e0\u4e2a\u54c8\u54c8\u4e00\u7b11\uff0c\u72fc\u541e\u864e\u54bd\u5730\u5927\u5403\u8d77\u6765\u3002 \u3000\u3000\u8f9b\u6377\u542c\u4ed6\u4eec\u8bf4\u4ec0\u4e48\u201c\u6b66\u6797\u4e4b\u79c0\u201d\u5b59\u501a\u91cd\uff0c\u5fc3\u4e2d\u4e00\u6014\u9053\uff1a\u201c\u600e\u4e48\u51fa\u4e86\u8fd9\u6837\u4e00\u4e2a\u9752\u5e74\u9ad8 \u624b\u6211\u90fd\u4e0d\u77e5\u9053\uff1f\u5475\uff0c\u5bf9\u4e86\uff0c\u4e00\u5b9a\u662f\u6211\u5728\u5c0f\u6222\u5c9b\u7684\u90a3\u4e00\u6bb5\u65f6\u95f4\u4ed6\u624d\u626c\u8d77\u6765\u7684\uff0c\u55ef\uff0c\u80fd\u628a\u91d1 \u6b39\u4e00\u638c\u9707\u9000\uff0c\u90a3\u529f\u592b\u7740\u5b9e\u4e86\u5f97\u3002\u201d \u3000\u3000\u60f3\u5230\u91d1\u6b39\uff0c\u4ed6\u7acb\u523b\u60f3\u5230\u90a3\u5f20\u88ab\u6bc1\u5bb9\u4e86\u7684\u4e11\u8138\uff0c\u62b1\u7740\u5434\u51cc\u98ce\u5927\u54e5\u4e00\u8d77\u6eda\u843d\u60ac\u5d16\uff0c\u4ed6\u4e0d \u7981\u957f\u53f9\u4e00\u58f0\uff0c\u96be\u9053\u91d1\u6b39\u4e5f\u50cf\u4ed6\u5e08\u7236\u91d1\u4e00\u9e4f\u4e00\u6837\u7684\u53d1\u75af\u4e86\u5417\uff1f \u3000\u3000\u8f9b\u6377\u542c\u90a3\u51e0\u4e2a\u954d\u5c40\u7684\u6c49\u5b50\u9152\u9163\u4e4b\u4f59\uff0c\u5f00\u59cb\u8a00\u4e0d\u53ca\u4e49\u8d77\u6765\uff0c\u4ed6\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u4ed8\u5e10\u51fa\u5e97\u3002 \u3000\u3000\u4e00\u8d70\u51fa\u9152\u5e97\uff0c\u4ed6\u5fc3\u4e2d\u6709\u4e00\u70b9\u614c\u4e71\u7684\u611f\u89c9\uff0c\u4ed6\u5b9a\u4e86\u5b9a\u795e\u6697\u9053\uff1a\u201c\u5148\u627e\u5d06\u5cd2\u8981\u56de\u5b9d\u5251\u518d \u8bf4\u3002\u201d \u3000\u3000\u79bb\u5f00\u96c6\u5e86\u57ce\uff0c\u5df2\u662f\u9ec4\u660f\u7684\u65f6\u5019\u4e86\u3002 \u3000\u3000\u897f\u5929\u7ea2\u4e91\u5982\u706b\uff0c\u971e\u5149\u56db\u5c04\uff0c\u8f9b\u6377\u5728\u5b98\u9053\u4e0a\u7f13\u7f13\u884c\u7740\uff0c\u4ed6\u5fc3\u60f3\uff1a\u201c\u4e0e\u5176\u665a\u4e0a\u5728\u5ba2\u6808\u91cc \u6295\u5bbf\uff0c\u5012\u4e0d\u5982\u4e58\u591c\u91cc\u65bd\u5c55\u8f7b\u529f\u8d76\u4e00\u7a0b\u3002\u201d \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u773c\u89d2\u77a5\u89c1\u4e00\u7269\uff0c\u4e00\u53ea\u9e3d\u5b50\u4ece\u5934\u4e0a\u98de\u8fc7\uff0c\u4ed6\u4ed4\u7ec6\u4e00\u77a7\uff0c\u53ea\u89c1\u9e3d\u817f\u4e0a\u65b9\u53c8\u7ed1\u7740 \u4e00\u6bb5\u7ea2\u5e26\u513f\uff0c\u5728\u5915\u9633\u4e0b\u7ea2\u5f97\u5f02\u5e38\u593a\u76ee\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u4e0d\u7981\u4e00\u52a8\uff0c\u96be\u9053\u4ecd\u662f\u4e0a\u6b21\u78b0\u5230\u7684\u90a3\u53ea\u9e3d\u5b50\uff1f \u3000\u3000\u8fd9\u65f6\u8f9b\u6377\u8eab\u540e\u6811\u53f6\u5ffd\u7136\u4e00\u9635\u5fae\u54cd\uff0c\u8f9b\u6377\u8eab\u5b50\u6709\u5982\u4e00\u9635\u65cb\u98ce\u822c\u8f6c\u4e86\u8fc7\u6765\uff0c\u5374\u6ca1\u6709\u770b\u89c1 \u4ec0\u4e48\u3002 \u3000\u3000\u4f46\u662f\u8f9b\u6377\u4ece\u7ecf\u9a8c\u4e2d\u5224\u65ad\u90a3\u5fae\u54cd\u5fc5\u662f\u4e00\u4e2a\u4eba\u6240\u5f04\u51fa\u7684\uff0c\u8f9b\u6377\u88c5\u7740\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u6211\u771f \u662f\u7591\u795e\u7591\u9b3c\uff0c\u6811\u53f6\u52a8\u4e00\u4e0b\u4e5f\u5927\u60ca\u5c0f\u602a\u3002\u201d \u3000\u3000\u88c5\u7740\u7ee7\u7eed\u8d76\u8def\uff0c\u4ed6\u539f\u4ee5\u4e3a\u90a3\u6811\u4e0a\u6709\u4eba\u7684\u8bdd\uff0c\u5fc5\u4f1a\u8ddf\u7740\u4ed6\uff0c\u90a3\u77e5\u4ed6\u8d70\u4e86\u5341\u4f59\u4e08\u8fdc\u7a81\u7136 \u4e00\u8f6c\uff0c\u80cc\u9762\u4ecd\u662f\u6ca1\u6709\u4eba\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u8d4c\u6c14\uff0c\u5c55\u5f00\u8f7b\u8eab\u529f\u592b\uff0c\u8eab\u8eaf\u6709\u5982\u8131\u5f26\u4e4b\u7bad\uff0c\u970e\u65f6\u5df2\u53bb\u4e86\u6570\u5341\u4e08\u3002 \u3000\u3000\u8fd9\u4e0b\u8f9b\u6377\u53ef\u53d1\u89c9\u80cc\u540e\u7740\u5b9e\u662f\u6709\u4eba\u8ddf\u8e2a\u7684\u4e86\uff0c\u800c\u4e14\u90a3\u4eba\u8f7b\u529f\u7adf\u4e5f\u5341\u5206\u4e86\u5f97\uff0c\u4f3c\u4e4e\u82e5\u5373 \u82e5\u79bb\u5730\u8ddf\u5728\u8f9b\u6377\u540e\u9762\u3002 \u3000\u3000\u8f9b\u6377\u6697\u4e2d\u51b7\u7b11\uff0c\u811a\u4e0b\u6e10\u6e10\u52a0\u52b2\uff0c\u901f\u5ea6\u4e5f\u968f\u7740\u589e\u5feb\uff0c\u90a3\u77e5\u8dd1\u4e86\u6570\u5341\u4e08\uff0c\u90a3\u4eba\u4ecd\u65e7\u5728\u76f8 \u5f53\u8ddd\u79bb\u5916\u7d27\u8ddf\u7740\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u6709\u70b9\u4e0d\u5fff\uff0c\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u811a\u5c16\u5fae\u70b9\uff0c\u8eab\u5f62\u98d8\u843d\u4e03\u516b\u4e08\u5916\uff0c\u6562\u60c5\u4ed6\u5df2\u65bd\u51fa \u4e86\u201c\u6697\u9999\u63a0\u5f71\u201d\u7684\u7edd\u9876\u8f7b\u529f\u3002 \u3000\u3000\u201c\u6697\u9999\u63a0\u5f71\u201d\u4e43\u662f\u4e03\u5999\u795e\u541b\u7684\u8f7b\u529f\u7edd\u6280\uff0c\u8f9b\u6377\u6b64\u65f6\u4f55\u7b49\u529f\u529b\uff0c\u65bd\u5c06\u51fa\u6765\u771f\u79f0\u5f97\u4e0a\u75be \u5982\u5954\u96f7\uff0c\u5f53\u4eca\u6b66\u6797\u4eba\u58eb\u80fd\u53ca\u5f97\u4e0a\u7684\uff0c\u7b80\u76f4\u662f\u5be5\u5be5\u65e0\u51e0\u3002 \u3000\u3000\u54ea\u91cc\u77e5\u9053\u5f53\u8f9b\u6377\u7528\u8db3\u4e86\u5341\u6210\u811a\u7a0b\uff0c\u4eba\u5bb6\u8fd8\u662f\u6ca1\u6709\u88ab\u62c9\u4e0b\u6765\u3002\u8f9b\u6377\u5fc3\u4e2d\u4e00\u52a8\uff0c\u7a81\u7136\u8db3 \u5c16\u7528\u529b\u4e00\u8e6c\uff0c\u8eab\u5b50\u5df2\u81f3\u4e03\u516b\u4e08\u4ee5\u5916\uff0c\u53cc\u8db3\u521a\u4e00\u89e6\uff0c\u7acb\u523b\u6253\u4e86\u4e00\u4e2a\u8f6c\u513f\uff0c\u53cd\u8fc7\u8eab\u6765\u3002 \u3000\u3000\u540e\u9762\u8ddf\u8e2a\u7684\u4eba\u4e0d\u865e\u6b63\u5728\u6bd4\u8d5b\u811a\u7a0b\u4e4b\u9645\uff0c\u8f9b\u6377\u8fd8\u4f1a\u53cd\u8fc7\u8eab\u6765\uff0c\u4e0d\u7531\u4e00\u6115\uff0c\u8eab\u8eaf\u5374\u4e00\u65f6 \u715e\u4e0d\u4f4f\uff0c\u5411\u524d\u98d8\u4e86\u4e00\u6bb5\u624d\u505c\u4e0b\u8eab\u6765\uff0c\u5446\u5728\u5f53\u5730\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u5bf9\u65b9\u6536\u4e0d\u4f4f\u52bf\uff0c\u4f46\u4e00\u98d8\u5374\u8d85\u8fc7\u4e94\u4e08\uff0c\u8fd9\u7b49\u8f7b\u8eab\u5de5\u592b\uff0c\u5b9e\u5728\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff0c\u5ffd \u7136\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8131\u53e3\u800c\u547c\u9053\uff1a\u201c\u9601\u4e0b\u53ef\u662f\u53f7\u79f0\u2018\u6b66\u6797\u4e4b\u79c0\u2019\uff1f\u201d \u3000\u3000\u90a3\u6765\u4eba\u5e74\u7ea6\u4e8c\u5341\u4e03\u3001\u516b\uff0c\u7709\u6e05\u76ee\u79c0\uff0c\u76f8\u8c8c\u751a\u662f\u6ed1\u7a3d\u53ef\u4eb2\u3002\u89c1\u8f9b\u6377\u5982\u6b64\u4e00\u95ee\uff0c\u5450\u5450\u9053\uff1a \u201c\u8fd9\u4e0d\u8fc7\u53ea\u662f\u6c5f\u6e56\u4e0a\u62ac\u4e3e\u5728\u4e0b\u6240\u9001\u7684\u53f7\u5934\uff0c\u5728\u4e0b\u90a3\u91cc\u6562\u5f53\uff0c\u5728\u4e0b\u59d3\u5b59\uff0c\u8349\u5b57\u501a\u91cd\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fae\u5fae\u70b9\u5934\u9053\uff1a\u201c\u5b59\u5927\u4fa0\u4e00\u8def\u8ddf\u968f\uff0c\u53ef\u6709\u4ec0\u4e48\u89c1\u6559\uff1f\u201d \u3000\u3000\u5b59\u501a\u91cd\u5446\u4e86\u4e00\u5446\uff0c\u4e00\u65f6\u7b54\u4e0d\u51fa\u6d3b\u6765\uff0c\u534a\u664c\u624d\u9053\uff1a\u201c\u82e5\u662f\u5c0f\u53ef\u773c\u5149\u4e0d\u5dee\uff0c\u9601\u4e0b\u53ef\u662f \u2018\u6885\u9999\u795e\u5251\u2019\u8f9b\u6377\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u70b9\u9996\u4f5c\u7b54\uff0c\u5b59\u501a\u91cd\u987f\u4e00\u987f\u624d\u9053\uff1a\u201c\u5c0f\u53ef\u8ddf\u968f\u5c0a\u9a7e\uff0c\u662f\u60f3\u8ba8\u6559\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u81ea\u5931\u6885\u9999\u5251\u4ee5\u6765\uff0c\u5fc3\u60c5\u4fbf\u4e0d\u592a\u6109\u5feb\uff0c\u800c\u4e14\u52a0\u4e0a\u4e00\u79cd\u597d\u80dc\u7684\u5fc3\u7406\uff0c\u542c\u89c1\u5b59\u501a\u91cd\u53e3 \u6c14\u597d\u50cf\u6709\u70b9\u4e0d\u628a\u81ea\u5df1\u653e\u5165\u773c\u5185\uff0c\u5fc3\u4e2d\u5fae\u6012\uff0c\u51b7\u7136\u9053\uff1a\u201c\u539f\u6765\u5c0a\u9a7e\u6b65\u6b65\u7d27\u8feb\u4e43\u4e3a\u7684\u662f\u8ba8\u6559 \u4e00\u4e8c\uff0c\u8fd9\u4e2a\u5728\u4e0b\u5012\u4e5f\u6709\u6b64\u610f\u2014\u2014 \u3000\u3000\u5b59\u501a\u91cd\u4e0d\u6599\u4e8c\u4e09\u53e5\u4fbf\u8bf4\u50f5\u8981\u52a8\u624b\uff0c\u4e5f\u4e0d\u4fbf\u518d\u89e3\u91ca\uff0c\u6014\u5728\u65c1\uff0c\u5012\u662f\u8f9b\u6377\u6700\u540e\u4e00\u53e5\u8bdd\uff0c \u6697\u793a\u597d\u50cf\u8981\u548c\u4ed6\u4e89\u80dc\uff0c\u6fc0\u53d1\u4ed6\u7684\u8c6a\u6027\uff0c\u5fae\u5fae\u8de8\u524d\u4e00\u6b65\uff0c\u9053\uff1a\u201c\u8f9b\u5144\u5373\u662f\u5982\u6b64\uff0c\u5c0f\u5f1f\u732e\u4e11 \u4e86\uff01\u201d \u3000\u3000\u8bf4\u7740\u7f13\u7f13\u62bd\u51fa\u80cc\u4e0a\u957f\u5251\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u7136\u4e0d\u8bed\uff0c\u89c1\u5bf9\u65b9\u5df1\u62bd\u51fa\u4f69\u5251\uff0c\u4e0d\u518d\u6020\u6162\uff0c\u53ea\u89c1\u4ed6\u53f3\u624b\u4e00\u62ac\uff0c\u8679\u5149\u8d77\u5904\uff0c\u957f\u5251 \u5df2\u8df3\u5165\u624b\u4e2d\u3002\u5355\u770b\u4ed6\u62d4\u5251\u7684\u52a8\u4f5c\uff0c\u4fbf\u6709\u4e00\u6d3e\u5b97\u5e08\u4e4b\u98ce\uff01 \u3000\u3000\u8fd9\u67c4\u5251\u4e43\u662f\u4ed6\u6885\u9999\u5251\u5931\u843d\u540e\u968f\u624b\u4e70\u7684\uff0c\u8fd9\u65f6\u957f\u5251\u5230\u624b\uff0c\u8c6a\u6c14\u76ca\u53d1\uff0c\u968f\u624b\u4e00\u632f\u2014\u2014\u8f9b \u6377\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u5927\u5c0f\u6218\u6597\u5df2\u4e0d\u4e0b\u534a\u767e\uff0c\u5c24\u5176\u662f\u6700\u8fd1\u4e00\u8fde\u6570\u6b21\u90fd\u662f\u548c\u4e00\u4e9b\u529f\u592b\u548c\u81ea\u5df1\u4e0d\u76f8 \u4e0a\u4e0b\u7684\u4eba\u62fc\u6597\uff0c\u5bf9\u4e8e\u62fc\u6597\u5df2\u6709\u4e86\u76f8\u5f53\u7684\u7ecf\u9a8c\u3002 \u3000\u3000\u76ee\u524d\u9762\u5bf9\u7684\u4e43\u662f\u58f0\u540d\u9f0e\u76db\u7684\u201c\u6b66\u6797\u4e4b\u79c0\u201d\u5b59\u501a\u91cd\uff0c\u4e0d\u6562\u4e1d\u6beb\u5927\u610f\uff0c\u5fae\u5fae\u62c8\u8d77\u957f\u886b\uff0c \u4ee5\u4fbf\u6253\u6597\u5bf9\u6bd4\u8f83\u4fd0\u843d\u4e00\u70b9\uff01\u4ed6\u62bd\u5251\uff0c\u6253\u6574\u886b\u4e00\u6c14\u5475\u6210\uff0c\u518d\u52a0\u4e0a\u6781\u81ea\u7136\u7684\u4e00\u632f\u624b\u4e2d\u957f\u5251\uff0c \u81ea\u7136\u53d1\u51fa\u201c\u55e1\u201d\u7684\u4e00\u58f0\uff0c\u8fd9\u4e00\u5207\u5bf9\u4ed6\u5df2\u6709\u4e86\u4e00\u79cd\u719f\u6089\u7684\u611f\u89c9\uff0c\u4ed6\u5fc3\u4e2d\u6697\u7b11\uff0c\u4e0b\u610f\u8bc6\u7684\u8fd8 \u60f3\u7528\u5de6\u624b\u53bb\u5f39\u52a8\u5251\u8eab\uff0c\u4f7f\u5251\u8eab\u8df3\u52a8\u6210\u4e03\u6735\u6885\u82b1\uff0c\u5f53\u7136\uff0c\u8fd9\u4e2a\u52a8\u4f5c\u5728\u4e0d\u4e45\u4ee5\u524d\u2014\u2014\u90a3\u65f6\u4ed6 \u8fd8\u662f\u4e03\u5999\u795e\u541b\u7684\u8eab\u4efd\u51fa\u73b0\u65f6\uff0c\u662f\u5341\u5206\u719f\u6089\u7684\u3002 \u3000\u3000\u84e6\u7136\uff0c\u4ed6\u5ffd\u7136\u611f\u5230\u80a1\u5251\u98ce\u88ad\u9762\uff0c\u8033\u8fc7\u542c\u5230\u5b59\u501a\u91cd\u7684\u58f0\u97f3\u9053\uff1a\u201c\u6ce8\u610f\u4e86\uff01\u201d\u8f9b\u6377\u811a\u6b65 \u4e00\u6ed1\uff0c\u540c\u65f6\u95f4\u957f\u5251\u4e00\u6325\u3002 \u3000\u3000\u5b59\u501a\u91cd\u4e00\u62db\u8d70\u7a7a\uff0c\u4e0d\u7b49\u62db\u7528\u8001\uff0c\u53cd\u624b\u4e00\u524a\uff0c\u53c8\u662f\u4e00\u62db\u4e8c\u5f0f\u653b\u4e86\u8fc7\u6765\u3002\u8f9b\u6377\u88ab\u4eba\u5bb6\u62a2 \u4e86\u5148\u673a\uff0c\u53ea\u597d\u5148\u884c\u56fa\u5b88\uff0c\u7136\u540e\u5f85\u673a\u800c\u52a8\uff0c\u4ee5\u4fbf\u593a\u56de\u4e3b\u52bf\u3002 \u3000\u3000\u5b59\u501a\u91cd\u4e00\u8fde\u51e0\u5251\u5b8c\u5168\u843d\u7a7a\uff0c\u4e0d\u662f\u88ab\u8f9b\u6377\u67b6\u56de\uff0c\u4fbf\u662f\u907f\u5f00\u3002\u4f46\u89c1\u4e8c\u652f\u5251\u8fde\u8fde\u95ea\u52a8\uff0c\u4e8c \u4e2a\u6b66\u6797\u540e\u8d77\u4e4b\u79c0\u4e92\u76f8\u62fc\u6597\uff0c\u4e00\u65f6\u4e0d\u5206\u4e0a\u4e0b\uff0c\u751a\u662f\u6fc0\u70c8\u3002 \u3000\u3000\u8f9b\u6377\u51dd\u795e\u63a5\u4e86\u51e0\u5251\uff0c\u5374\u59cb\u7ec8\u627e\u4e0d\u7740\u5bf9\u65b9\u7834\u7efd\uff0c\u4f46\u5374\u53d1\u89c9\u5bf9\u65b9\u4e43\u662f\u6b63\u5b97\u5c11\u6797\u5ae1\u4f20\u7684 \u201c\u8fbe\u6469\u795e\u5251\u201d\uff0c\u5fc3\u4e2d\u5fae\u60ca\uff0c\u5b88\u5f97\u66f4\u7d27\u3002 \u3000\u3000\u4e5f\u6709\u597d\u51e0\u6b21\uff0c\u8f9b\u6377\u60f3\u7528\u5185\u529b\u53bb\u786c\u5c01\u5bf9\u65b9\u5251\u5b50\uff0c\u4ee5\u4e89\u56de\u4e3b\u52a8\uff0c\u8fd9\u4e2a\u5ff5\u5934\u51fa\u4e8e\u4ed6\u4ee5\u4e3a\u4ed6 \u7684\u5185\u529b\u4fee\u4e3a\u5fc5\u5e94\u8f83\u5b59\u501a\u91cd\u4e3a\u6df1\uff0c\u4f46\u4ed6\u51dd\u795e\u6ce8\u610f\u90a3\u5b59\u501a\u91cd\u6bcf\u4e00\u5251\u5288\u51fa\uff0c\u5219\u9690\u5e26\u98ce\u96f7\u4e4b\u58f0\uff0c \u8fd9\u8868\u793a\u5bf9\u65b9\u7684\u5185\u529b\u9020\u8be3\u4e5f\u5df2\u8fbe\u4e0a\u4e0a\u4e4b\u9009\u4e86\uff01 \u3000\u3000\u8f9b\u6377\u731b\u7136\u60f3\u8d77\u90a3\u5931\u843d\u7684\u6885\u9999\u5251\uff0c\u5fc3\u4e2d\u7126\u6025\uff0c\u4e0d\u613f\u518d\u803d\u6401\u4e0b\u53bb\uff0c\u594b\u529b\u524a\u51fa\u4e00\u5251\uff0c\u4f46\u89c1 \u5251\u5f71\u6709\u5982\u6625\u8695\u5410\u4e1d\uff0c\u6251\u6d8c\u800c\u4e0a\uff0c\u800c\u4e14\u5251\u5f0f\u4e2d\u771f\u529b\u6ea2\u6ce8\uff0c\u5a01\u529b\u751a\u662f\u5f3a\u5927\u3002 \u3000\u3000\u5b59\u501a\u91cd\u4e00\u65f6\u5c01\u67b6\u4e0d\u4f4f\uff0c\u624b\u4e0a\u62db\u5f0f\u4e00\u7f13\uff0c\u5df2\u7ecf\u7ed9\u4e88\u8f9b\u6377\u6700\u4f73\u826f\u673a\u8f9b\u6377\u6253\u7b97\u901f\u6218\u901f\u51b3\uff0c \u4e0d\u518d\u62d6\u6ede\uff0c\u543c\u9053\uff1a\u201c\u4e14\u63a5\u6211\u8fd9\u62db\uff01\u201d \u3000\u3000\u540c\u65f6\u95f4\u624b\u4e2d\u957f\u5251\u7a81\u7136\u4f7f\u51fa\u4e0d\u4e45\u524d\u5728\u795e\u9706\u5854\u9876\u632b\u8d25\u52fe\u6f0f\u4e00\u602a\u7684\u201c\u5927\u884d\u795e\u5251\u201d\u6765\uff0c\u5f53\u7136\uff0c \u8fd9\u4e00\u5f0f\u662f\u8d77\u624b\u5f0f\uff1a\u201c\u65b9\u751f\u4e0d\u606f\u201d\u3002 \u3000\u3000\u5b59\u501a\u91cd\u4e00\u60ca\uff0c\u597d\u4e0d\u5bb9\u6613\u624d\u5c01\u4f4f\uff0c\u8f9b\u6377\u5df2\u662f\u5947\u62db\u8fed\u51fa\u3002 \u3000\u3000\u201c\u6b66\u6797\u4e4b\u79c0\u201d\u5b59\u501a\u91cd\u731b\u7136\u540e\u9000\u534a\u6b65\uff0c\u907f\u5f00\u8f9b\u6377\u7684\u201c\u5927\u884d\u795e\u5251\u201d\u4e2d\u7684\u7b2c\u56db\u5f0f\uff1a\u201c\u7269\u6362 \u661f\u79fb\u201d\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u4e14\u4f4f\uff01\u201d \u3000\u3000\u8f9b\u6377\u4e00\u6014\uff0c\u7528\u529b\u6536\u56de\u518d\u653b\u4e4b\u52bf\uff0c\u90a3\u5b59\u501a\u91cd\u4f3c\u4e4e\u60f3\u8981\u8bf4\u4ec0\u4e48\u8bdd\uff0c\u5374\u8fdf\u8fdf\u4e0d\u5f00\u53e3\u3002 \u3000\u3000\u8f9b\u6377\u6b63\u5947\u602a\u95f4\uff0c\u5b59\u501a\u91cd\u5ffd\u9053\uff1a\u201c\u6253\u6270\uff01\u54b1\u4eec\u540e\u4f1a\u6709\u671f\uff01\u201d \u3000\u3000\u5b59\u501a\u91cd\u5df2\u817e\u7a7a\u800c\u8d77\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4fbf\u843d\u5728\u5341\u6570\u4e08\u5916\u3002 \u3000\u3000\u8f9b\u6377\u6014\u5728\u4e00\u65c1\uff0c\u4ed6\u53ef\u771f\u4e0d\u660e\u767d\u5b59\u501a\u91cd\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u5176\u5b9e\u4ed6\u54ea\u91cc\u77e5\u9053\u5b59\u501a\u91cd\u6b64\u884c\u7684 \u4f7f\u547d\u662f\u5982\u4f55\u7684\u91cd\u5927\uff0c\u51e0\u4e4e\u8981\u5f71\u54cd\u6574\u4e2a\u6b66\u6797\u7684\u524d\u9014\u54e9\uff0c\u8fd9\u662f\u540e\u8bdd\u4e0d\u63d0\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u89e3\u7684\u6447\u4e86\u6447\u9996\uff0c\u81ea\u8bed\u9053\uff1a\u201c\u7ba1\u4ed6\u7684\uff01\u8fd8\u662f\u8d76\u8def\u8981\u7d27\uff01\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e0d\u518d\u5446\u7acb\uff0c\u80cc\u4e0a\u4f69\u5251\uff0c\u98de\u4e5f\u4f3c\u7684\u8d70\u53bb\u3002 \u3000\u3000\u5e73\u767d\u53c8\u88ab\u803d\u6401\u4e86\u5c06\u8fd1\u4e00\u4e2a\u65f6\u8fb0\uff0c\u53ea\u597d\u653e\u817f\u731b\u8d76\uff0c\u597d\u5728\u987a\u8def\u9053\u513f\u7b14\u76f4\u4e0b\u53bb\uff0c\u4fbf\u662f\u5d06\u5cd2 \u5c71\u533a\u3002 \u3000\u3000\u53c8\u662f\u4e00\u53ea\u7ed1\u7740\u7ea2\u7f0e\u5e26\u7684\u9e3d\u5b50\u98de\u4e86\u8fc7\u53bb\uff0c\u8f9b\u6377\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u626c\u62f3\u9065\u9065\u51fb\u53bb\uff0c\u201c\u5657\u201d\u5730 \u5c06\u9e3d\u5b50\u6253\u4e86\u4e0b\u6765\uff0c\u4ed6\u53d6\u4e0b\u7ea2\u5e26\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u7ed8\u7740\u4e24\u4e2a\u9ab7\u9abc\uff0c\u4ed6\u4e0d\u7981\u5927\u5403\u4e00\u60ca\u9053\uff1a \u3000\u3000\u201c\u6d77\u5929\u53cc\u715e\uff01\u201d \u3000\u3000\u6562\u60c5\u8fd9\u6b63\u662f\u6d77\u5929\u53cc\u715e\u7684\u8bb0\u53f7\uff0c\u4ed6\u5fc3\u9053\uff1a\u201c\u4e0d\u77e5\u53cc\u715e\u53ec\u96c6\u4f19\u4f34\u53c8\u8981\u5e72\u4ec0\u4e48\u574f\u4e8b\uff1f\u201d \u3000\u3000\u84e6\u7136\uff0c\u901a\u8fc7\u4eba\u5f71\u53c8\u662f\u4e00\u95ea\uff0c\u6709\u4e00\u4e2a\u548c\u5c1a\u6253\u626e\u7684\u4eba\u7ad9\u5728\u9053\u8def\u4e2d\u95f4\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a \u3000\u3000\u201c\u6765\u8005\u53ef\u662f\u8f9b\u6377\u8f9b\u5927\u4fa0\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u6599\u5728\u5982\u6b64\u8352\u533a\uff0c\u7adf\u8fd8\u6709\u51fa\u5bb6\u4eba\u627e\u81ea\u5df1\uff0c\u5fc3\u4e2d\u5927\u5947\uff0c\u8eab\u8eaf\u4e00\u632b\uff0c\u5b9a\u4e0b\u8eab\u6765\uff0c\u70b9 \u9996\u4f5c\u7b54\u3002 \u3000\u3000\u90a3\u548c\u5c1a\u5341\u5206\u5e74\u9752\uff0c\u5e74\u7ea6\u5345\u5de6\u53f3\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u624b\u4e0a\u5df2\u63e1\u4e86\u4e00\u67c4\u957f\u5251\uff0c\u65bd\u4e86\u4e00\u793c\u9053\uff1a\u201c\u671b\u8f9b\u65bd\u4e3b\u591a\u591a\u6307\u6b63\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u957f\u5251\u5df2\u662f\u5206\u5fc3\u523a\u5230\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u771f\u662f\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u7cca\u91cc\u7cca\u6d82\u53c8\u6709\u51fa\u5bb6\u4eba\u627e\u81ea\u5df1\u8ba8\u6559\uff0c\u542c\u4ed6\u7684\u53e3\u6c14\u597d\u50cf\u662f \u56e0\u81ea\u5df1\u662f\u65b0\u8fd1\u6210\u540d\u7684\u9ad8\u624b\u624d\u6765\u9886\u6559\u7684\uff0c\u4e5f\u61d2\u5f97\u548c\u4ed6\u8ba1\u8f83\uff0c\u53f3\u624b\u4e00\u5e26\uff0c\u201c\u545b\u54ea\u201d\u957f\u5251\u51fa\u9798\u3002 \u6325\u52a8\u4e4b\u95f4\uff0c\u4e00\u62db"\u95f2\u4e91\u6f6d\u5f71"\uff0c\u4ecd\u7136\u7528\u5927\u884d\u5341\u5f0f\u51fa\u51fb\u3002 \u3000\u3000\u90a3\u5e74\u9752\u548c\u5c1a\u529f\u592b\u4e5f\u751a\u662f\u9ad8\u660e\uff0c\u8fde\u6311\u5e26\u524a\uff0c\u628a\u8f9b\u6377\u8fd9\u62db\u5c01\u51fa\u95e8\u5916\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u4e0d\u7531\u4e00\u60ca\uff0c\u6562\u60c5\u8fd9\u548c\u5c1a\u7684\u5251\u8def\u5b8c\u5168\u548c\u521a\u624d\u548c\u81ea\u5df1\u4ea4\u624b\u7684\u201c\u6b66\u6797\u4e4b\u79c0\u201d\u5b59\u501a\u91cd \u4e00\u6837\uff0c\u90fd\u662f\u6b63\u5b97\u5c11\u6797\u5bfa\u5ae1\u4f20\u7684\u201c\u8fbe\u6469\u5251\u672f\u201d\uff01 \u3000\u3000\u90a3\u5e74\u9752\u548c\u5c1a\u5bf9\u8f9b\u6377\u62db\u5f0f\u5341\u5206\u7559\u795e\uff0c\u7b80\u76f4\u53ef\u4ee5\u8bf4\u662f\u76ee\u4e0d\u8f6c\u775b\u5730\u6ce8\u89c6\u7740\u8f9b\u6377\u7684\u4e00\u62db\u4e00\u5f0f\uff0c \u8f9b\u6377\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7a81\u7136\u6539\u53d8\u62db\u5f0f\uff0c\u53d8\u201c\u5927\u884d\u795e\u5251\u201d\u4e3a\u201c\u866c\u679d\u5251\u6cd5\u201d\uff0c\u5237\u5237\u5237\u5237\u4e00\u8fde\u56db\u4e94\u62db \u653b\u51fa\u3002 \u3000\u3000\u90a3\u5e74\u9752\u548c\u5c1a\u5148\u662f\u51dd\u795e\u6ce8\u89c6\u4e24\u62db\uff0c\u63a5\u7740\u8138\u4e0a\u9732\u51fa\u5931\u671b\u4e4b\u8272\uff0c\u84e6\u5730\u6536\u5251\u9053\uff1a \u3000\u3000\u201c\u6682\u505c\uff01\u201d \u3000\u3000\u8f9b\u6377\u89c1\u5bf9\u624b\u53c8\u662f\u4e0d\u8981\u6253\u4e86\uff0c\u597d\u5728\u81ea\u5df1\u6b63\u6709\u4e8b\u5728\u8eab\uff0c\u53cd\u5012\u5e0c\u671b\u4ed6\u5feb\u70b9\u505c\u624b\uff0c\u81ea\u5df1\u597d\u8d76 \u8def\u3002 \u3000\u3000\u90a3\u5e74\u9752\u548c\u5c1a\u8ba4\u771f\u5730\u6c89\u601d\u4e86\u597d\u4e00\u4f1a\uff0c\u624d\u91ca\u7136\u9053\uff1a\u201c\u5bf9\u4e86\uff01\u662f\u4e86\uff01\u201d \u3000\u3000\u8f6c\u76ee\u77a5\u89c1\u8f9b\u6377\u8fd8\u7ad9\u5728\u8eab\u65c1\uff0c\u4e0d\u7531\u9732\u51fa\u5c34\u5c2c\u4e4b\u8272\uff0c\u652f\u543e\u4e86\u4e00\u4e0b\uff0c\u84e6\u7136\u8f6c\u8eab\u98de\u5954\u800c\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u54c8\u54c8\u957f\u7b11\uff0c\u5fc3\u4e2d\u867d\u662f\u4e0d\u89e3\uff0c\u4f46\u603b\u6a21\u7cca\u77e5\u9053\u5c11\u6797\u5bfa\u5fc5\u662f\u5f88\u6ce8\u610f\u8fd9\u201c\u5927\u884d\u5341\u5f0f\u201d\uff0c \u8fd9\u5012\u662f\u751a\u4e0d\u5e73\u51e1\u7684\u4e8b\u5462\u3002 \u3000\u3000\u5fc3\u4e2d\u4e00\u9759\uff0c\u81ea\u7136\u53c8\u60f3\u5230\u90a3\u5931\u843d\u7684\u201c\u6885\u9999\u5251\u201d\uff0c\u5fc3\u4e2d\u7126\u6025\u5982\u711a\uff0c\u4e0d\u6562\u591a\u505c\u4e00\u5206\u949f\uff0c\u518d \u884c\u8d76\u8def\u3002 \u3000\u3000\u5c71\u9053\u8d8a\u6765\u8d8a\u5d0e\u5c96\uff0c\u4e5f\u8d8a\u6765\u8d8a\u8352\u50fb\u3002 \u3000\u3000\u5929\u8272\u6e10\u6e10\u9ed1\u6697\u4e86\uff0c\u9ec4\u660f\u5df2\u7136\u8fc7\u53bb\u2014\u2014 \u3000\u3000\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6708\u513f\u5df2\u51fa\u6765\u9ad8\u9ad8\u6302\u5728\u7a7a\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u5fc3\u4e00\u610f\u5728\u4e8e\u90a3\u201c\u6885\u9999\u5251\u201d\uff0c\u6b65\u6cd5\u867d\u662f\u4ece\u5bb9\u4e0d\u8feb\uff0c\u4f46\u6bcf\u4e00\u817e\u632a\uff0c\u4fbf\u5728\u56db\u4e94\u4e08\u4ee5 \u5916\uff0c\u5728\u94f6\u8272\u7684\u6708\u5149\u4e0b\uff0c\u597d\u50cf\u4e00\u6761\u6de1\u6de1\u7684\u9ec4\u7ebf\uff0c\u5728\u5730\u9762\u4e0a\u98de\u5feb\u7684\u79fb\u52a8\u7740\u3002 \u3000\u3000\u524d\u9762\u5c31\u662f\u4e00\u4e2a\u4e0d\u9ad8\u4e5f\u4e0d\u77ee\u7684\u5c71\u5761\uff0c\u8f9b\u6377\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u51b3\u5b9a\u4e00\u53e3\u6c14\u5954\u4e0a\u5c71\u5761\u9876\u5904\u3002 \u3000\u3000\u4f46\u95fb\u8863\u8882\u98d8\u98d8\uff0c\u5e26\u8d77\u9635\u9635\u98ce\u58f0\uff0c\u8f9b\u6377\u5df2\u4e00\u95ea\u800c\u8fc7\u2026\u2026 \u3000\u3000\u6708\u660e\u661f\u7a00\uff0c\u4e07\u8d56\u65e0\u58f0\u2014\u2014 \u3000\u3000\u5c71\u5761\u659c\u659c\u7684\uff0c\u4eba\u5f71\u88ab\u6708\u5149\u7167\u6620\u5728\u5730\u4e0a\uff0c\u672c\u6765\u662f\u4fee\u957f\u7684\u5f71\u5b50\uff0c\u4e5f\u7531\u4e8e\u5761\u659c\u800c\u7f29\u77ed\u4e86\u4e00 \u622a\u2014\u2014 \u3000\u3000\u592a\u8fc5\u901f\u7684\u539f\u6545\uff0c\u77ed\u77ed\u7684\u5f71\u513f\u968f\u7740\u90a3\u98de\u5954\u7684\u8eab\u4f53\uff0c\u597d\u50cf\u5728\u5730\u9762\u4e0a\u5212\u7740\u4e00\u6761\u9ed1\u7ebf\u4f3c\u7684\u3002 \u3000\u3000\u6000\u7740\u5341\u5206\u7126\u6025\u7684\u5fc3\u60c5\uff0c\u8f9b\u6377\u6b63\u4ee5\u5168\u901f\u75be\u5954\u7740\uff0c\u6ee1\u5fc3\u601d\u5ff5\u7740\u552f\u4e00\u53ef\u4ee5\u62b5\u6321\u90a3\u65e0\u575a\u4e0d\u6467 \u7684\u201c\u501a\u8679\u201d\u795e\u5251\u7684\u201c\u6885\u9999\u5251\u201d\uff1b\u66fc\u5999\u7684\u8eab\u6cd5\uff0c\u4e4d\u770b\u8fc7\u53bb\u597d\u50cf\u8db3\u4e0d\u70b9\u5730\uff0c\u8eab\u4f53\u6709\u82e5\u5f39\u4e38\u822c \u5728\u7a7a\u4e2d\u98de\u5feb\u63a0\u8fc7\uff0c\u5374\u4e1d\u6beb\u4e0d\u5e26\u98ce\u58f0\uff0c\u4ec5\u4ec5\u90a3\u8863\u8882\u5fae\u5fae\u5e26\u7740\u626c\u8d77\u6765\u800c\u5df2\uff01 \u3000\u3000\u5c71\u5761\u4e0a\u9759\u6084\u6084\u7684\uff0c\u5076\u800c\u4e00\u4e24\u4e1b\u6811\u6728\u4ea4\u6742\u751f\u5728\u5c71\u5761\u65c1\u8fb9\uff0c\u5a46\u88df\u7684\u5f71\u513f\u51e0\u4e4e\u8981\u906e\u7740\u6574\u4e2a \u5c71\u5761\u2014\u2014 \u3000\u3000\u84e6\u5730\u91cc\u90a3\u6811\u53f6\u6b3e\u6b3e\u6447\u52a8\u4e86\u4e00\u4e0b\uff0c\u4e00\u79cd\u76f4\u89c9\u548c\u4e00\u79cd\u7ecf\u9a8c\u4f7f\u8f9b\u6377\u5347\u8d77\u4e00\u4e2a\u4e0d\u7965\u7684\u5ff5\u5934\uff0c \u5c3d\u7ba1\u4ed6\u5fc3\u4e2d\u8fd8\u5145\u6ee1\u7740\u7126\u6025\uff0c\u4f46\u8eab\u4f53\u4ecd\u7136\u4e0d\u7531\u4e00\u632b\u3002 \u3000\u3000\u7b49\u5230\u4ed6\u9192\u89c9\u81ea\u5df1\u6025\u505c\u7684\u539f\u56e0\u800c\u641c\u7d22\u90a3\u4e1b\u6811\u6728\u7684\u65f6\u5019\uff0c\u5374\u53d1\u73b0\u6811\u4e2d\u4e0d\u8fc7\u7a7a\u7a7a\u7684\u4e00\u7247\uff0c \u5206\u660e\u662f\u591c\u98ce\u5fae\u62c2\u7684\u539f\u6545\u3002 \u3000\u3000\u8f9b\u6377\u54d1\u7136\u5931\u7b11\uff0c\u884c\u52a8\u6709\u5982\u6025\u7bad\uff0c\u8fde\u70b9\u6570\u70b9\uff0c\u5df2\u6062\u590d\u4e86\u6700\u9ad8\u901f\u5ea6\uff0c\u6b65\u5c65\u4ecd\u662f\u90a3\u4e48\u5b89\u7965\uff0c \u8eab\u5f62\u4ecd\u662f\u90a3\u6837\u66fc\u5999\uff01 \u3000\u3000\u8fd9\u4e2a\u5c71\u5761\u5e76\u4e0d\u7b97\u9ad8\uff0c\u4f46\u5374\u662f\u6742\u6811\u4e1b\u751f\uff0c\u867d\u7136\u662f\u51ac\u5b63\uff0c\u4f46\u7531\u4e8e\u5357\u65b9\u8f83\u4e3a\u6e29\u548c\uff0c\u662f\u4ee5\u6811 \u6728\u5e76\u6ca1\u6709\u67af\u840e\u3002 \u3000\u3000\u524d\u9762\u4fbf\u662f\u5c71\u5761\u9876\u7aef\uff0c\u8f9b\u6377\u731b\u63d0\u771f\u529b\uff0c\u4e00\u53e3\u6c14\u5954\u5230\u5c71\u9876\uff0c\u84e6\u5730\u91cc\u4ed6\u611f\u89c9\u5230\u6c14\u6c1b\u6709\u70b9\u513f \u4e0d\u5bfb\u5e38\uff0c\u90a3\u4ea4\u9519\u7684\u6811\u6728\u597d\u50cf\u591a\u4e86\u4e00\u70b9\uff0c\u56e0\u4e3a\u6709\u521a\u624d\u6811\u53f6\u65e0\u98ce\u81ea\u52a8\u7684\u4e8b\u60c5\uff0c\u8f9b\u6377\u7684\u8b66\u89c9\u63d0 \u9ad8\u4e0d\u5c11\uff0c\u5b9a\u795e\u770b\u53bb\uff0c\u90a3\u4e9b\u6811\u6728\u5206\u660e\u662f\u4ece\u4ed6\u5904\u79fb\u79cd\u8fc7\u6765\u7684\u2014\u2014 \u3000\u3000\u8f9b\u6377\u867d\u7136\u6e38\u8361\u6c5f\u6e56\u4ec5\u4ec5\u4e00\u5e74\u6709\u4f59\uff0c\u4f46\u6240\u7ecf\u5386\u7684\u591a\u534a\u662f\u9876\u5c16\u513f\u7684\u73a9\u610f\uff0c\u8033\u95fb\u76ee\u7779\u4e5f\u6709 \u4e86\u4e0d\u5c11\u7ecf\u9a8c\uff0c\u50cf\u8fd9\u79cd\u6bd4\u8f83\u7a00\u89c1\u7684\u201c\u7792\u5929\u8fc7\u6d77\u201d\u624b\u6cd5\uff0c\u8f9b\u6377\u5374\u80fd\u5728\u7ec6\u5bdf\u4e4b\u4e0b\uff0c\u8f7b\u6613\u53d1\u89c9\uff0c \u5b9e\u5728\u4e0d\u6613\u2014\u2014 \u3000\u3000\u6b63\u9192\u609f\u95f4\uff0c\u811a\u6b65\u4e00\u632b\uff0c\u4e0d\u7531\u5f80\u5de6\u4fa7\u8e0f\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u610f\u5916\u7684\u662f\u8e0f\u4e86\u4e00\u4e2a\u7a7a\uff0c\u8f9b\u6377\u521a\u521a\u9192\u609f\u81ea\u5df1\u662f\u843d\u5165\u9677\u9631\u7684\u65f6\u5019\uff0c\u8eab\u5b50\u5df2\u731b\u5f80\u4e0b\u843d\uff01 \u3000\u3000\u8f9b\u6377\u81ea\u7136\u7684\u4e00\u8e22\uff0c\u5728\u8feb\u4e0d\u6025\u5f85\u4e4b\u95f4\uff0c\u786c\u751f\u751f\u5347\u8d77\u534a\u5c3a\u3002 \u3000\u3000\u53cc\u817f\u8fde\u73af\u8e22\u51fa\uff0c\u4ef0\u5929\u659c\u63a0\u51fa\u4e86\u9677\u9631\uff0c\u4f46\u4e5f\u4ec5\u79bb\u5730\u9762\u534a\u5c3a\u800c\u5df2\u3002\u201c\u563f\u201d\uff01\u8f9b\u6377\u521a\u624d\u541f \u51fa\u4e00\u58f0\uff0c\u84e6\u5730\u8eab\u4f53\u53c8\u662f\u4e00\u4e2a\u8dc4\u8e09\uff0c\u6562\u60c5\u662f\u88ab\u90a3\u7b2c\u4e8c\u9053\u673a\u5173\u2014\u2014\u201c\u7eca\u7d22\u201d\u7eca\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u201c\u8352\u5c71\u9ed1\u591c\uff0c\u4e0d\u77e5\u600e\u4e48\u4f1a\u6709\u5982\u6b64\u4f0f\u5175\uff0c\u8bbe\u8ba1\u51fa\u8fd9\u6837\u591a\u7684\u673a\u5361\u6765\u6697\u7b97\u81ea\u5df1\uff1f\u201d\u8fd9\u4e2a\u5ff5 \u5934\u5982\u95ea\u7535\u822c\u63a0\u8fc7\u8f9b\u6377\u5fc3\u7530\uff0c\u6b63\u5728\u52aa\u529b\u7a33\u4f4f\u8eab\u5b50\uff0c\u91d1\u5203\u7834\u7a7a\u58f0\u8d77\u5904\uff0c\u654c\u4eba\u5df2\u4e58\u673a\u53d1\u51fa\u6697\u5668\u3002 \u3000\u3000\u8f9b\u6377\u8fa8\u522b\u98ce\u58f0\uff0c\u5df2\u77e5\u53d1\u6697\u5668\u8005\u5185\u529b\u7279\u5f3a\uff0c\u6b63\u5f85\u8dc3\u8d77\u8eb2\u907f\uff0c\u53ea\u95fb\u201c\u54c4\u201d\u7684\u4e8c\u58f0\uff0c\u80a9\u5934 \u548c\u5927\u817f\u4e0a\u5df2\u5404\u4e2d\u4e86\u4e8c\u679d\u6697\u5668\uff0c\u539f\u6765\u90a3\u6697\u5668\u76f4\u5230\u8ddd\u4ed6\u4e09\u5c3a\u4e4b\u5730\u624d\u8ba9\u4ed6\u542c\u51fa\u58f0\u97f3\uff0c\u662f\u4ee5\u8fde\u8f9b \u6377\u8fd9\u7b49\u8eab\u624b\u7adf\u7740\u4e86\u5927\u9053\u513f\u3002 \u3000\u3000\u8f9b\u6377\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u5c1a\u672a\u5982\u6b64\u683d\u8fc7\uff0c\u7adf\u5728\u5c1a\u672a\u770b\u89c1\u5bf9\u624b\u5f71\u513f\u7684\u65f6\u523b\u91cc\uff0c\u4fbf\u5403\u4e86\u6697\u4e8f\u3002 \u3000\u3000\u4e8c\u679a\u6697\u5668\u4f7f\u8f9b\u6377\u5b9b\u5982\u5200\u5272\u822c\u523a\u75c5\u4e86\u4e00\u4e0b\uff0c\u6012\u706b\u4e0a\u81ba\uff0c\u95ea\u76ee\u4e00\u77a5\uff0c\u5374\u4e0d\u89c1\u4e00\u4e2a\u4eba\u5f71\u513f\u3002 \u3000\u3000\u201c\u654c\u6697\u6211\u660e\u201d\uff0c\u8f9b\u6377\u5fc3\u4e2d\u7adf\u5347\u8d77\u4e00\u79cd\u4ece\u672a\u6709\u7684\u7d27\u5f20\uff0c\u4e00\u79cd\u4e0d\u7965\u7684\u9884\u611f\u7b3c\u7f69\u7740\u4ed6\uff0c\u771f \u6709\u201c\u56db\u9762\u695a\u6b4c\u201d\u7684\u5904\u5883\u4e86\u3002 \u3000\u3000\u201c\u9003\u201d\uff01\u4e00\u4e2a\u5ff5\u5934\u9661\u7136\u95ea\u8fc7\u8f9b\u6377\u7684\u8111\u9645\u3002 \u3000\u3000\u4ed6\u53ef\u4ee5\u611f\u89c9\u51fa\u6240\u4e2d\u7684\u6697\u5668\u867d\u7136\u6ca1\u6709\u6bd2\uff0c\u5374\u6253\u5f97\u5f88\u6df1\u5f88\u6df1\u3002\u800c\u5bf9\u624b\u7684\u624b\u6cd5\uff0c\u53c8\u5f02\u4e4e\u5bfb \u5e38\u7684\u597d\u3002\u6697\u5668\u5e26\u8d77\u7684\u98ce\u58f0\u7adf\u80fd\u5728\u8fdb\u5165\u4e09\u5c3a\u4ee5\u5185\u624d\u80fd\u5bdf\u89c9\uff0c\u8fd9\u79cd\u624b\u6cd5\uff0c\u6c5f\u6e56\u4e0a\u53ef\u80fd\u662f\u6781\u5c11 \u53ef\u89c1\u7684\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u5f3a\u5fcd\u7740\u80cc\u4e0a\u7684\u75bc\u75db\uff0c\u8db3\u4e0a\u7528\u529b\uff0c\u8eab\u5b50\u7a81\u7136\u4e00\u63a0\uff0c\u76f4\u5411\u5de6\u4fa7\u6797\u4e2d\u7a9c\u53bb\u3002\u4ed6\u5e76\u975e\u611a \u8822\u4e4b\u4eba\uff0c\u660e\u77e5\u654c\u4eba\u5fc5\u662f\u56db\u7528\u5bc6\u5e03\uff0c\u4f46\u4ecd\u5192\u9669\u4e00\u8bd5\u2014\u2014 \u3000\u3000\u679c\u7136\u4e0d\u51fa\u6240\u6599\uff0c\u90a3\u5bc6\u6797\u4e2d\u56de\u8fc7\u53bb\u7684\u662f\u8fce\u5934\u800c\u51fb\u7684\u4e00\u628a\u6697\u5668\u3002 \u3000\u3000\u5e78\u597d\u662f\u6709\u4e86\u51c6\u5907\uff0c\u8f9b\u6377\u5728\u7a7a\u4e2d\u4e00\u4ef0\uff0c\u8eab\u5b50\u7adf\u5728\u7535\u5149\u706b\u77f3\u95f4\u6c34\u5e73\u5012\u5c04\u800c\u51fa\uff0c\u65b9\u5411\u5374\u662f \u548c\u521a\u624d\u76f4\u5954\u7684\u6b63\u53cd\u9762\uff0c\u8fd9\u79cd\u8eab\u6cd5\u4e5f\u53ea\u6709\u7edd\u4f20\u7684\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\u624d\u80fd\u591f\u4f5c\u5f97\u5230\u7684\uff01 \u3000\u3000\u90a3\u628a\u6697\u5668\u6765\u5f97\u597d\u5feb\uff0c\u8f9b\u6377\u7684\u8eab\u5b50\u548c\u5730\u9762\u5df2\u6210\u5e73\u884c\uff0c\u9976\u662f\u8fd9\u6837\u7684\u89d2\u5ea6\uff0c\u4ecd\u8ba9\u4e00\u5934\u6697\u5668 \u5728\u978b\u5e95\u4e0a\u5212\u4e86\u4e00\u4e0b\u3002 \u3000\u3000\u8f9b\u6377\u8fd9\u4e2a\u65b9\u6cd5\u7eaf\u7cb9\u662f\u8bd5\u63a2\u7684\uff0c\u8eab\u5b50\u521a\u624d\u7a9c\u5411\u53f3\u65b9\uff0c\u6797\u4e2d\u84e6\u5730\u4e00\u58f0\u65ad\u559d\uff0c\u4e00\u80a1\u638c\u529b\u6025 \u5954\u800c\u81f3\u3002 \u3000\u3000\u8f9b\u6377\u6325\u52a8\u53cc\u81c2\uff0c\u731b\u89c9\u4e00\u9635\u523a\u75db\uff0c\u80a9\u80db\u4e0a\u7684\u90a3\u7c92\u6697\u5668\u7adf\u4f7f\u5f97\u4ed6\u5de6\u624b\u6709\u5982\u865a\u8bbe\u3002 \u3000\u3000\u4ed6\u594b\u529b\u4e00\u638c\u56de\u656c\u8fc7\u53bb\uff0c\u4f46\u5a01\u529b\u5374\u50cf\u662f\u51cf\u5f31\u4e86\u4e00\u4e8c\u5206\u3002 \u3000\u3000\u4e8c\u80a1\u72c2\u98da\u4e00\u89e6\uff0c\u8f9b\u6377\u987f\u611f\u4e0d\u652f\u3002 \u3000\u3000\u6709\u5982\u4e00\u652f\u68cd\u5b50\u6253\u4e0b\u6765\u4e00\u6837\uff0c\u8f9b\u6377\u88ab\u522b\u4eba\u7684\u638c\u529b\u6253\u4e86\u4e00\u4e2a\u8f6c\u513f\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\u8dcc\u5728\u5730 \u4e0b\u3002 \u3000\u3000\u8f9b\u6377\u65e9\u5728\u63d0\u638c\u56de\u656c\u4e4b\u65f6\u5df2\u77e5\u81ea\u5df1\u975e\u9000\u4e0d\u53ef\uff0c\u4f46\u552f\u4e00\u53ef\u4ee5\u5b89\u6170\u7684\u4fbf\u662f\u81ea\u5df1\u6025\u8feb\u95f4\u4e00\u6325 \u4e4b\u4e0b\uff0c\u7adf\u4e5f\u628a\u5bf9\u624b\u6253\u4e86\u4e00\u4e2a\u8ddf\u6597\u2014\u2014\u8fd9\u662f\u7531\u4e8e\u6797\u4e2d\u4e00\u9635\u66b4\u54cd\u548c\u547b\u541f\u800c\u77e5\u7684\uff01 \u3000\u3000\u8bd5\u63a2\u7684\u7ed3\u679c\u77e5\u9053\u4e86\uff0c\u90a3\u5c31\u662f\u2014\u2014 \u3000\u3000\u654c\u4eba\u7adf\u5728\u8fd9\u8352\u5c71\u4e0a\u8bbe\u4e0b\u4e86\u5341\u9762\u57cb\u4f0f\uff01 \u3000\u3000\u800c\u4e14\uff0c\u8fd8\u50cf\u662f\u975e\u8981\u53d6\u5f97\u81ea\u5df1\u6027\u547d\u624d\u7518\u5fc3\uff01 \u3000\u3000\u2014\u2014\u8f9b\u6377\u77e5\u9053\u9003\u8dd1\u662f\u7edd\u4e0d\u53ef\u80fd\u4e86\uff0c\u4f24\u53e3\u6709\u8d8a\u6765\u8d8a\u75db\u7684\u8d8b\u52bf\uff0c \u3000\u3000\u6b63\u5728\u6ca1\u6709\u5e94\u4ed8\u4e4b\u7b56\uff0c\u84e6\u5730\u91cc\u2014\u2014 \u3000\u3000\u6811\u53f6\u513f\u7c07\u7c07\u4e00\u9635\u4e71\u6643\uff0c\u51fa\u73b0\u4e86\u4e03\u516b\u4e2a\u4eba\u5f71\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u7167\u5f97\u5206\u5206\u660e\u660e\uff0c\u4e3a\u9996\u7684\u4eba\uff0c\u7adf\u7136\u662f\u8f9b\u6377\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\u201c\u6d77\u5929\u53cc\u715e\u201d\u7126\u6c0f \u5144\u5f1f\u3002 \u3000\u3000\u81ea\u4ece\u8f9b\u6377\u4e0b\u5c71\u4ee5\u6765\uff0c\u5df2\u7ecf\u4e24\u6b21\u9022\u7740\u201c\u5173\u4e2d\u4e5d\u8c6a\u201d\u7684\u5934\u5b50\u201c\u6d77\u5929\u53cc\u715e\u201d\uff0c\u5c24\u5176\u662f\u5728\u9f9f \u5c71\u4e00\u5f79\uff0c\u8f9b\u6377\u66fe\u88ab\u5bf9\u65b9\u6253\u4e0b\u4e07\u4e08\u6df1\u5d16\uff0c\u89c1\u9762\u4e4b\u4e0b\uff0c\u81ea\u662f\u5206\u5916\u773c\u7ea2\u3002 \u3000\u3000\u7126\u6c0f\u5144\u5f1f\u8138\u4e0a\u8868\u60c5\u6f20\u7136\u4e0d\u60ca\uff0c\u6562\u60c5\u4ed6\u4eec\u65e9\u5df2\u5bfc\u77e5\u90a3\u201c\u6885\u5c71\u6c11\u201d\u5e76\u6ca1\u6709\u88ab\u51fb\u6bd9\u5d16\u4e0b\u7684 \u6d88\u606f\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u65e2\u6012\u4e14\u60ca\uff0c\u95ea\u76ee\u671b\u53bb\uff0c\u90a3\u4e00\u5806\u4eba\u5f71\u4e2d\u6709\u597d\u4e9b\u719f\u4eba\uff0c \u3000\u3000\u770b\u6837\u5b50\u6b63\u662f\u4e1c\u5c71\u518d\u4e4d\u7684\u5173\u4e2d\u4e5d\u8c6a\u3002 \u3000\u3000\u4e5d\u8c6a\u7684\u5de5\u592b\uff0c\u8f9b\u6377\u591a\u534a\u9886\u6559\u8fc7\uff0c\u5047\u5982\u662f\u5bf9\u65b9\u4ee5\u4e00\u5bf9\u4e00\uff0c\u751a\u81f3\u4ee5\u4e8c\u654c\u4e00\uff0c\u8f9b\u6377\u90fd\u53ef\u4ee5 \u7a33\u6301\u4e0d\u8d25\uff0c\u4f46\u662f\u73b0\u5728\u5bf9\u65b9\u662f\u4e5d\u4e2a\u4eba\uff0c\u800c\u4e14\u81ea\u5df1\u53c8\u5728\u5c1a\u672a\u4ea4\u624b\u524d\u4fbf\u53d7\u4e86\u91cd\u521b\uff0c\u53c8\u4e00\u9635\u4e0d\u7965 \u7684\u9634\u5f71\u95ea\u8fc7\u8f9b\u6377\u7684\u5fc3\u7530\u3002 \u3000\u3000\u7126\u6c0f\u5144\u5f1f\u4e00\u77ac\u4e0d\u8f6c\u7684\u6ce8\u89c6\u7740\u8f9b\u6377\u3002 \u3000\u3000\u597d\u4e00\u4f1a\u7126\u5316\u624d\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u957f\u5f97\u597d\u50cf\uff01\u5dee\u4e0d\u591a\u662f\u4e00\u6a21\u4e00\u6837\u5462\u3002\u201d \u3000\u3000\u5fae\u5fae\u4e00\u987f\uff0c\u63a5\u7740\u9634\u68ee\u68ee\u5730\u9053\uff1a\u201c\u4f60\u53ef\u660e\u767d\u6211\u4eec\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9ed8\u7136\u4e0d\u8bed\uff0c\u4ed6\u662f\u7edd\u9876\u806a\u660e\u7684\u4eba\uff0c\u5df2\u7ecf\u9192\u609f\u2014\u2014 \u3000\u3000\u539f\u6765\u5f53\u65e5\u5728\u9f9f\u5c71\u7edd\u9876\uff0c\u8f9b\u6377\u4e34\u88ab\u6253\u4e0b\u5c71\u9876\u65f6\uff0c\u66fe\u88ab\u63ed\u53bb\u9762\u5e55\uff0c\u53cc\u715e\u5728\u6025\u5207\u95f4\uff0c\u4e5f\u4e0d \u80fd\u8fa8\u8ba4\uff0c\u4f46\u603b\u4f9d\u7a00\u89c9\u5f97\u6709\u70b9\u773c\u719f\u3002\u4e8b\u540e\u8f9b\u6377\u51fb\u8d25\u52fe\u6f0f\u4e00\u602a\u7fc1\u6b63\uff0c\u540d\u58f0\u5927\u632f\uff0c\u53cc\u715e\u81ea\u7136\u4e5f \u542c\u5230\uff0c\u7531\u4e8e\u8f9b\u6377\u662f\u59d3\u201c\u8f9b\u201d\uff0c\u63d0\u9192\u53cc\u715e\u8fd9\u5b69\u5b50\u9177\u4f3c\u4ece\u524d\u7684\u4f19\u4f34\u201c\u8f9b\u4e5d\u9e4f\u201d\uff0c\u53cc\u715e\u8be6\u7ec6\u5206 \u6790\u4e4b\u4e0b\uff0c\u5df2\u77e5\u5f53\u5e74\u8fd9\u5b69\u5b50\u7adf\u6ca1\u6709\u4ece\u726f\u725b\u4e0a\u8dcc\u6b7b\u3002 \u3000\u3000\u53cc\u715e\u7684\u5fc3\u80a0\u539f\u672c\u6bd2\u8fa3\uff0c\u51b3\u5fc3\u94f2\u5c3d\u540e\u6839\uff0c\u662f\u4ee5\u8bbe\u4e0b\u5341\u9762\u57cb\u4f0f\uff0c\u7b49\u5019\u8f9b\u4e5d\u9e4f\u7684\u540e\u4ee3\u6765\u4e34\u3002 \u3000\u3000\u8f9b\u6377\u601d\u8003\u518d\u4e09\uff0c\u5f3a\u5fcd\u4e0b\u6570\u6b21\u51c6\u5907\u62fc\u547d\u7684\u51b2\u52a8\uff0c\u81ea\u77e5\u4eca\u65e5\u5fc5\u5b9a\u6709\u6b7b\u65e0\u751f\u3002\u5fc3\u4e2d\u4e00\u6a2a\uff0c \u9ad8\u58f0\u9053\uff1a\u201c\u6d77\u5929\u53cc\u715e\uff0c\u4f60\u4eec\u65e2\u660e\u767d\uff0c\u563f\uff0c\u8fd8\u4e0d\u7eb3\u547d\uff01\u201d \u3000\u3000\u8bf4\u5230\u6700\u540e\uff0c\u58f0\u97f3\u5df2\u5fae\u98a4\u6296\uff0c\u60f3\u662f\u6124\u6012\u5df2\u6781\uff01 \u3000\u3000\u201c\u545b\u5577\u201d\u4e00\u58f0\uff0c\u8f9b\u6377\u5df2\u628a\u4f69\u5251\u6301\u5728\u624b\u4e2d\uff0c\u4f4e\u5934\u4e00\u77a5\uff0c\u53ea\u89c1\u624b\u4e2d\u957f\u5251\u867d\u7136\u5728\u6708\u5149\u4e4b\u4e0b \u95ea\u95ea\u6709\u5149\uff0c\u4f46\u5374\u8fdc\u4e0d\u53ca\u90a3\u201c\u6885\u9999\u5b9d\u5251\u201d\uff0c \u3000\u3000\u60f3\u8d77\u90a3\u6885\u9999\u5251\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u4e00\u9635\u832b\u7136\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e00\u573a\u662f\u6b7b\u591a\u751f\u5c11\uff0c\u6885\u9999\u5251\u662f\u6c38\u8fdc\u518d \u89c1\u4e0d\u5230\u7684\u5566\uff01\u201d \u3000\u3000\u4ed6\u607c\u6068\u7684\u4e00\u54fc\uff0c\u9f99\u541f\u822c\u4e00\u58f0\u957f\u5578\uff0c\u987f\u89c9\u8c6a\u6c14\u5e72\u4e91\uff0c\u5b58\u4e0b\u4e86\u7834\u65a7\u6c89\u821f\u7684\u51b3\u5fc3\uff0c\u4e5f\u6682\u65f6 \u5fd8\u8bb0\u4e86\u4f24\u53e3\u7684\u75bc\u75db\u3002 \u3000\u3000\u4ed6\u51b7\u51b7\u9053\uff1a\u201c\u554a\uff0c\u4e00\u5171\u662f\u4e03\u4e2a\u4eba\uff0c\u59d3\u7126\u707c\uff0c\u4e5d\u8c6a\u8fd8\u6709\u7684\u4eba\u54e9\uff1f\u201d \u3000\u3000\u7126\u5316\u957f\u7b11\u4e00\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u4e03\u4e2a\u4eba\u8fd8\u4e0d\u591f\u8981\u4f60\u7684\u547d\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u679c\u7136\u9634\u6bd2\u65e0\u6bd4\uff0c\u4e1d\u8c6a\u4e0d\u88ab\u8f9b\u6377\u6240\u6263\u3002 \u3000\u3000\u8f9b\u6377\u6717\u6717\u4e00\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e0a\u5427\uff01\u201d\u5fc3\u4e2d\u5374\u95ea\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff0c\u5fd6\u9053\uff1a\u201c\u662f\u4e86\uff0c\u4e00\u5b9a\u662f\u90a3\u53ea\u9e3d\u5b50\uff0c\u51c6\u662f\u53cc\u715e\u7528\u6765 \u53ec\u96c6\u540c\u4f34\u7684\uff0c\u88ab\u6211\u6253\u4e0b\uff0c\u679c\u7136\u662f\u5c11\u4e86\u4e00\u4e2a\u6653\u6708\u5bd2\u5fc3\u638c\uff0c\u4e0d\u77e5\u8fd8\u6709\u4e00\u4e2a\u5462\uff1f\u201d \u3000\u3000\u5fc3\u5ff5\u624d\u52a8\uff0c\u90a3\u5c71\u5de6\u53cc\u8c6a\u4e2d\u7684\u6797\u5c11\u768b\u4e0d\u58f0\u4e0d\u54cd\uff0c\u5df2\u662f\u903c\u8eab\u653b\u6765\u3002\u8f9b\u6377\u773c\u89c2\u56db\u8def\uff0c\u8033\u542c \u516b\u65b9\uff0c\u90a3\u5bb9\u6797\u5c11\u768b\u8feb\u8fd1\uff0c\u624b\u4e2d\u5175\u5203\u7834\u7a7a\u4e4b\u58f0\u6597\u76db\uff0c\u7adf\u4f3c\u5168\u529b\u800c\u4e3a\u2014\u2014 \u3000\u3000\u8f9b\u6377\u817f\u4e0a\u4e0d\u4fbf\uff0c\u5e78\u597d\u662f\u5de6\u80a9\u53d7\u4f24\uff0c\u4e8e\u662f\u4ed6\u53f3\u624b\u957f\u5251\u4e00\u6325\uff0c\u4e00\u58f0\u4e0d\u54cd\u5730\u75be\u523a\u800c\u51fa\uff0c\u6797 \u5c11\u768b\u867d\u7136\u5148\u52a8\uff0c\u5374\u4ecd\u62a2\u4e0d\u4e86\u5148\u673a\uff0c\u4ed6\u6012\u543c\u4e00\u58f0\uff0c\u659c\u8de8\u534a\u6b65 \u3000\u3000\u2014\u2014 \u3000\u3000\u8f9b\u6377\u94c1\u8155\u4e00\u632b\uff0c\u957f\u5251\u4e00\u5377\u800c\u51fa\uff0c\u5251\u5c16\u8fde\u95ea\uff0c\u5206\u523a\u5bf9\u65b9\u4e94\u4eba\u3002 \u3000\u3000\u7126\u6c0f\u5144\u5f1f\u89c1\u4ed6\u5251\u6cd5\u7cbe\u5947\uff0c\u53cc\u53cc\u7331\u8eab\u800c\u4e0a\uff0c\u5176\u4ed6\u51e0\u4e2a\u4e5f\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u6b66\u6797\u9ad8\u624b\uff0c\u4e2a\u4e2a \u90fd\u66fe\u8eab\u7ecf\u767e\u6218\uff0c\u4e00\u89c1\u53cc\u715e\u52a8\u4f5c\uff0c\u7acb\u523b\u5404\u81ea\u5360\u636e\u4f4f\u6700\u6709\u5229\u7684\u4f4d\u7f6e\uff0c\u66f4\u4e0d\u51fa\u58f0\uff0c\u4e00\u9f50\u6296\u51fa\u5175 \u5668\uff0c\u5408\u56f4\u800c\u4e0a\u2014\u2014 \u3000\u3000\u663e\u7136\u7684\uff0c\u4ed6\u4eec\u662f\u975e\u7f6e\u8f9b\u6377\u4e8e\u6b7b\u5730\u4e0d\u53ef\u4e86\u3002 \u3000\u3000\u65b0\u5173\u4e2d\u4e5d\u8c6a\u7684\u5175\u529b\u6bd4\u4e4b\u65e7\u4e5d\u8c6a\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d\u53ca\uff0c\u73b0\u5728\u7adf\u9f50\u4ee5\u5175\u5668\u5408\u653b\u4e00\u4e2a\u654c\u4eba\uff0c\u8fd9 \u4e0d\u80fd\u8bf4\u4e0d\u662f\u6b66\u6797\u201c\u58ee\u4e3e\u201d\u4e86\u5427\u3002\u4f55\u51b5\uff0c\u5bf9\u624b\u53ea\u662f\u4e00\u4e2a\u5e74\u4ec5\u4e8c\u5341\u7684\u9752\u5e74\u5462\u3002 \u3000\u3000\u8f9b\u6377\u65e9\u5c31\u4e0d\u5b58\u751f\u671b\uff0c\u7adf\u7136\u6beb\u65e0\u754f\u610f\uff0c\u957f\u5251\u4e00\u633d\uff0c\u4e00\u52a8\u62db\u5c31\u662f\u5e73\u51e1\u4e0a\u4eba\u7684\u7edd\u4e16\u5251\u6cd5\u2014 \u2014\u5927\u884d\u5341\u5f0f\u3002 \u3000\u3000\u4f17\u591a\u7684\u5175\u5668\u9f50\u6325\u53d1\u51fa\u7684\u7834\u7a7a\u4e4b\u58f0\u9e23\u7136\u4f5c\u54cd\uff0c\u8fd9\u5bf9\u8f9b\u6377\u6765\u8bf4\uff0c\u4e0d\u4ec5\u662f\u611f\u5230\u654c\u4eba\u7684\u529f\u529b \u7684\u6df1\u539a\uff0c\u800c\u4e14\u66f4\u662f\u4e00\u79cd\u60e8\u5389\u7684\u5fc3\u7406\u5a01\u80c1\u3002 \u3000\u3000\u4f46\u662f\uff0c\u5ffd\u7136\u5636\u7684\u4e00\u58f0\u5c16\u9510\u7684\u54cd\u8d77\uff0c\u8f9b\u6377\u5251\u5c16\u4e0a\u53d1\u51fa\u7684\u5251\u6c14\u7adf\u5c06\u6240\u6709\u7684\u7834\u7a7a\u4e4b\u58f0\u538b\u4e86 \u4e0b\u53bb\uff0c\u4ed6\u624b\u4e0a\u7684\u957f\u5251\u6781\u5feb\u5730\u5728\u524d\u540e\u5212\u51fa\u4e00\u9053\u5149\u4eae\u7684\u5f27\u5ea6\uff0c\u94ee\u7136\u800c\u51fa\uff0c\u4ecd\u662f\u5927\u884d\u5341\u5f0f\u7684\u9996 \u62db\u2014\u2014\u201c\u65b9\u751f\u4e0d\u606f\u201d\u3002 \u3000\u3000\u4e5d\u8c6a\u591a\u534a\u89c1\u8fc7\u8fd9\u4e00\u62db\uff0c\u5dee\u4e0d\u591a\u6bcf\u4e2a\u4eba\u90fd\u56de\u53bb\u82e6\u601d\u8fc7\u5bf9\u8fd9\u62db\u7684\u7834\u6cd5\uff0c\u867d\u7136\u6ca1\u6709\u60f3\u51fa\u4ec0 \u4e48\u5999\u62db\uff0c\u4f46\u5404\u81ea\u90fd\u60f3\u5230\u9632\u5b88\u4e4b\u7b56\uff0c\u8fd9\u65f6\u89c1\u8f9b\u6377\u8fd9\u62db\u65bd\u5c55\u51fa\u6765\uff0c\u4e00\u65f6\u5404\u4eba\u90fd\u65bd\u5c55\u4e86\u81ea\u5df1\u7684 \u5fc3\u5f97\u2014\u2014 \u3000\u3000\u7136\u800c\uff0c\u5e73\u51e1\u4e0a\u4eba\u4f55\u7b49\u4eba\u7269\uff0c\u8fd9\u65b9\u751f\u4e0d\u606f\u4e43\u662f\u5927\u884d\u5341\u5f0f\u4e2d\u6700\u5177\u5a01\u529b\u7684\u4e00\u62db\uff0c\u53d8\u5316\u7ec6\u5fae \u7e41\u591a\uff0c\u5f3a\u5982\u8f9b\u6377\u6b64\u65f6\u4e5f\u672a\u89c1\u5f97\u80fd\u767e\u5206\u4e4b\u767e\u5730\u9886\u609f\uff0c\u53c8\u5c82\u662f\u4ed6\u4eec\u51e0\u4eba\u6240\u80fd\u89e3\u7834\uff1f\u53ea\u542c\u5636\u5636 \u5251\u6c14\u58f0\u4e00\u9ad8\u4e00\u4f4e\uff0c\u60ca\u53eb\u58f0\u8d77\uff0c\u5343\u624b\u5251\u5ba2\u9646\u65b9\u80a9\u4e0a\u5df2\u4e2d\u4e86\u4e00\u5251\uff0c\u800c\u957f\u5929\u4e00\u78a7\u767d\u98ce\u7684\u8863\u8896\u4e5f \u88ab\u4e09\u5c3a\u9752\u950b\u524a\u53bb\u5c3a\u8bb8\u3002 \u3000\u3000\u8f9b\u6377\u6697\u9053\u4e00\u58f0\u53ef\u60dc\uff0c\u82e5\u662f\u817f\u4e0a\u4e0d\u4f24\uff0c\u6b64\u65f6\u4e58\u80dc\u8ffd\u51fb\uff0c\u81f3\u5c11\u80fd\u6536\u62fe\u5176\u4e2d\u4e00\u4eba\u3002 \u3000\u3000\u547c\u547c\u4e24\u80a1\u51cc\u5389\u65e0\u6bd4\u7684\u638c\u98ce\u88ad\u5411\u4f53\u540e\uff0c\u8f9b\u6377\u4e0d\u7528\u770b\u5c31\u77e5\u5fc5\u662f\u6d77\u5929\u53cc\u715e\uff0c\u4ed6\u8eab\u5b50\u90fd\u4e0d\u8f6c\uff0c \u53cd\u624b\u5c31\u662f\u4e00\u5251\uff0c\u5251\u5f0f\u4f3c\u6162\u5b9e\u5feb\uff0c\u98d8\u5ffd\u4e0d\u5b9a\uff0c\u6b63\u662f\u5927\u884d\u5341\u5f0f\u4e2d\u7684\u201c\u7269\u6362\u661f\u79fb\u201d\u3002 \u3000\u3000\u7126\u6c0f\u5144\u5f1f\u529f\u529b\u518d\u6df1\uff0c\u78b0\u5230\u8fd9\u7b49\u5947\u7edd\u5929\u4e0b\u7684\u5251\u5f0f\u4e5f\u662f\u4e00\u7a92\uff0c\u8f9b\u6377\u53d8\u62db\u8fc5\u901f\uff0c\u201c\u7269\u6362\u661f \u79fb\u201d\u624d\u53d1\u51fa\u4e00\u534a\uff0c\u5251\u5149\u5012\u5377\u53c8\u653b\u5411\u5de6\u9762\u7684\u6797\u5c11\u768b\uff0c\u5251\u6258\u4e00\u626c\uff0c\u5374\u5c01\u53bb\u5de6\u9762\u6458\u661f\u624b\u53f8\u7a7a\u5b97 \u7684\u5077\u88ad\u3002 \u3000\u3000\u5bd2\u5929\u4e00\u78a7\u767d\u98ce\u5927\u559d\u4e00\u58f0\uff0c\u5355\u638c\u5288\u51fa\uff0c\u6d77\u5929\u53cc\u715e\u4e5f\u4e58\u673a\u914d\u5408\u653b\u51fa\u4e00\u638c\uff0c\u4e09\u80a1\u7edd\u5f3a\u7684\u638c \u529b\u903c\u5f97\u8f9b\u6377\u8dd1\u8ddf\u9000\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u6797\u5c11\u768b\u548c\u9646\u65b9\u5175\u5203\u53cc\u6325\u4e58\u673a\u800c\u8fdb\uff0c\u8f9b\u6377\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u5251\u8d70\u504f\u950b\uff0c\u7adf\u662f\u866c\u679d\u5251\u6cd5\u4e2d\u7684 \u7edd\u62db\u201c\u51b7\u6885\u62c2\u9762\u201d\u2014\u2014 \u3000\u3000\u201c\u51b7\u6885\u62c2\u9762\u201d\u53c8\u5947\u53c8\u5feb\uff0c\u8f9b\u6377\u66f4\u662f\u6beb\u4e0d\u7559\u60c5\uff0c\u5343\u624b\u5251\u5ba2\u9646\u65b9\u6115\u5f97\u4e00\u6115\uff0c\u5251\u6c14\u5df2\u81ea\u6251 \u5230\uff0c\u6b63\u60ca\u614c\u95f4\uff0c\u6025\u95fb\u8f9b\u6377\u53c8\u662f\u51b7\u54fc\u4e00\u58f0\uff0c\u957f\u5251\u5374\u98de\u5feb\u7684\u6536\u56de\u3002 \u3000\u3000\u539f\u6765\u6d77\u5929\u53cc\u715e\u96c4\u539a\u7684\u638c\u529b\u53c8\u903c\u5f97\u8f9b\u6377\u653e\u5f03\u7edd\u597d\u673a\u4f1a\uff0c\u6536\u62db\u81ea\u4fdd\u2014\u2014 \u3000\u3000\u4f46\u662f\u53ea\u7f13\u5f97\u4e00\u7f13\uff0c\u8f9b\u6377\u7684\u5927\u884d\u5341\u5f0f\u53c8\u5df2\u65bd\u5f00\uff0c\u5251\u5f0f\u7ef5\u7ef5\u800c\u51fa\uff0c\u4efb\u4e5d\u8c6a\u731b\u653b\uff0c\u4e00\u65f6\u5374 \u8fd8\u6321\u5f97\u4f4f\u2014\u2014 \u3000\u3000\u4f46\u662f\u8f9b\u6377\u6e10\u6e10\u611f\u5230\u5251\u4e0a\u7684\u538b\u529b\u6108\u6765\u6108\u91cd\uff0c\u4ed6\u5636\u5636\u7684\u5251\u6c14\u4e5f\u6108\u6765\u6108\u5f31\uff0c\u867d\u7136\u5f31\uff0c\u4f46\u4ed6 \u8fd8\u5f97\u62fc\u529b\u5c06\u771f\u529b\u8d2f\u6ce8\uff0c\u56e0\u4e3a\u53ea\u8981\u5251\u6c14\u4e00\u8fc7\uff0c\u867d\u7136\u4ed6\u4f1a\u611f\u5230\u8f83\u4e3a\u8f7b\u677e\uff0c\u4f46\u662f\u654c\u4eba\u7acb\u523b\u4f1a\u6b3a \u8eab\u8fd1\u5230\u8089\u640f\u7684\u5730\u6b65\u2014\u2014 \u3000\u3000\u8f9b\u6377\u611f\u5230\u4f24\u53e3\u4e5f\u6108\u6765\u6108\u75db\u4e86\uff0c\u4ed6\u62fc\u529b\u659c\u5288\u51fa\u4e24\u5251\uff0c\u4ed6\u5fc3\u9053\uff1a\u201c\u8fd9\u6837\u5730\u4e0b\u53bb\u9664\u4e86\u6b7b\u6ca1 \u6709\u7b2c\u4e8c\u6761\u8def\uff0c\u6211\u7d22\u6027\u62fc\u547d\u5e72\u4ed6\u4e00\u4e2a\u7b97\u4e00\u4e2a\u2014\u2014\u201d \u3000\u3000\u5fc3\u5ff5\u65e2\u51b3\uff0c\u4ed6\u957f\u7b11\u4e00\u58f0\uff0c\u5fc3\u4e2d\u53cd\u800c\u5766\u7136\uff0c\u4ed6\u6697\u4e2d\u795d\u7977\uff1a\u201c\u7238\u5988\uff0c\u4f51\u5b69\u513f\u6740\u4ec7\uff01\u201d \u3000\u3000\u957f\u5251\u6325\u51fa\u5168\u662f\u866c\u679d\u5251\u5f0f\u4e2d\u7684\u8fdb\u624b\u62db\u5f0f\uff0c\u800c\u4e14\u4e13\u627e\u6d77\u5929\u53cc\u715e\u4e0b\u624b\u2014\u2014 \u3000\u3000\u4ed6\u8fd9\u79cd\u62fc\u547d\u6253\u6cd5\uff0c\u62db\u5f0f\u53c8\u8be1\u5947\u65e0\u6bd4\uff0c\u5173\u4e2d\u4e5d\u8c6a\u7adf\u7136\u9635\u52bf\u4e00\u4e71\uff0c\u4e00\u4e2a\u5ff5\u5934\u5982\u95ea\u7535\u822c\u7a7f \u8fc7\u4ed6\u7684\u8111\u6d77\u2014\u2014 \u3000\u3000\u201c\u9003\uff01\u201d \u3000\u3000\u4ed6\u201c\u51b7\u6885\u62c2\u9762\u201d\u3001\u201c\u6885\u82b1\u4e09\u5f04\u201d\u4e00\u9f50\u653b\u5411\u6d77\u5929\u53cc\u715e\uff0c\u8eab\u4f53\u5374\u9661\u7136\u540e\u9000\uff0c\u5f3a\u5fcd\u7740\u817f\u4e0a \u75bc\u75db\uff0c\u626d\u8eab\u8dc3\u8d77\u6570\u4e08\u3002 \u3000\u3000\u201c\u6253\uff01\u201d\u4e5d\u8c6a\u4e2d\u7684\u65b0\u624b\u201c\u9634\u98ce\u795e\u9556\u201d\u5de6\u4ef2\u671b\u6296\u624b\u6253\u51fa\u4e00\u628a\u6697\u5668\u3002 \u3000\u3000\u8f9b\u6377\u5728\u7a7a\u4e2d\u6ca1\u6709\u542c\u5230\u4e1d\u6beb\u7834\u98ce\u4e4b\u58f0\uff0c\u5fc3\u6599\u5fc5\u662f\u4e5d\u8c6a\u6446\u7684\u7a7a\u57ce\u8ba1\uff0c\u4f46\u7a81\u7136\u4e00\u4e2a\u5ff5\u5934\u95ea \u4e0a\u5fc3\u7530\uff1a \u3000\u3000\u201c\u65b9\u624d\u6211\u4e2d\u6697\u5668\u4e5f\u662f\u521d\u4e0d\u89c1\u98ce\u58f0\uff0c\u83ab\u975e\u8fd9\u6697\u5668\u6709\u5f02\u5e38\u4e4b\u5904\u2014\u2014\u201d \u3000\u3000\u5237\u7684\u4e00\u58f0\uff0c\u8f9b\u6377\u614c\u5fd9\u5730\u8ba9\u5411\u5730\u4e0a\uff0c\u679c\u7136\uff0c\u4e00\u628a\u6697\u5668\u98de\u7a7a\u800c\u53bb\u3002 \u3000\u3000\u539f\u6765\u8fd9\u201c\u9634\u98ce\u795e\u9556\u201d\u5de6\u4ef2\u671b\u7684\u6697\u5668\u529f\u592b\u6709\u4e00\u6869\u7279\u522b\u4e4b\u5904\uff0c\u4ed6\u53d1\u51fa\u7684\u6697\u5668\u5229\u7528\u7279\u6b8a\u624b \u6cd5\u80fd\u591f\u4ee4\u6697\u5668\u4e0d\u5e26\u7834\u98ce\u4e4b\u58f0\uff0c\u76f4\u5230\u8ddd\u654c\u4e09\u6c11\u4ee5\u5185\u5374\u9661\u7136\u52a0\u901f\uff0c\u654c\u4eba\u53d1\u89c9\u60f3\u9003\u65f6\uff0c\u5df2\u81ea\u4e0d \u53ca\uff0c\u8f9b\u6377\u7b2c\u4e00\u6b21\u5c31\u7740\u4e86\u4ed6\u7684\u9053\u513f\u624d\u53d7\u4f24\u7684\u3002 \u3000\u3000\u8f9b\u6377\u867d\u7136\u62fc\u547d\u6eda\u5730\u8eb2\u8fc7\u4e86\u6697\u5668\uff0c\u4f46\u662f\u4f24\u53e3\u5374\u88ab\u89e6\u649e\u5f97\u75db\u4e0d\u582a\u5fcd\uff0c\u4ed6\u54ac\u7d27\u7259\u521a\u7ad9\u8d77\u8eab\uff0c \u7830\u7684\u4e00\u58f0\uff0c\u80cc\u4e0a\u5df2\u4e2d\u4e86\u7126\u5316\u4e00\u638c\uff0c\u4ed6\u53ea\u89c9\u773c\u524d\u4e00\u9635\u53d1\u9ed1\uff0c\u5589\u5934\u4e00\u9635\u53d1\u751c\uff0c\u54c7\u7684\u4e00\u58f0\u5410\u51fa \u4e00\u53e3\u9c9c\u8840\u2014\u2014 \u3000\u3000\u4ed6\u731b\u7136\u5438\u8fdb\u4e00\u6c14\uff0c\u53cc\u811a\u4e00\u633a\uff0c\u7adf\u7136\u6323\u624e\u7740\u7ad9\u7acb\u8d77\u6765\uff0c\u4ed6\u8fd0\u6c14\u5f3a\u538b\u4f4f\u7ffb\u817e\u7684\u8840\u6c14\uff0c\u771f \u6c14\u8d2f\u6ce8\u5251\u8eab\uff0c\u6ecb\u5730\u4e00\u58f0\uff0c\u5251\u6c14\u8dc3\u7136\u800c\u51fa\uff0c\u53f3\u624b\u4e00\u633d\uff0c\u5251\u5149\u70b9\u70b9\u5f39\u51fa\uff0c\u5ffd\u5730\u4e00\u5251\u75be\u523a\u800c\u51fa\uff0c \u534a\u62db\u201c\u5bd2\u6885\u5410\u854a\u201d\u5c1a\u672b\u65bd\u5b8c\u4e00\u53d8\u800c\u4e3a\u201c\u6885\u5410\u5947\u9999\u201d\uff0c\u5251\u6c14\u4f3c\u4e4e\u5c01\u4ed6\u7684\u5bfb\u5e38\u957f\u5251\u589e\u4e86\u51e0\u5206 \u5a01\u529b\uff0c\u64e6\u7684\u4e00\u58f0\uff0c\u91d1\u9524\u795e\u5251\u6797\u5c11\u768b\u7684\u5251\u6258\u88ab\u4ed6\u524a\u53bb\uff0c\u624b\u80cc\u4e0a\u4e5f\u5212\u51fa\u6bb7\u7ea2\u7684\u4e00\u9053\u53e3\u5b50\u2014\u2014 \u3000\u3000\u6797\u5c11\u768b\u5c1a\u6ca1\u6709\u6765\u5f97\u53ca\u9000\u540e\uff0c\u8f9b\u6377\u7684\u5251\u950b\u5df2\u631f\u7740\u4e00\u7f15\u5bd2\u5149\u6307\u5411\u7126\u6c0f\u5144\u5f1f\u2014\u2014 \u3000\u3000\u6458\u661f\u624b\u53f8\u7a7a\u5b97\u53ca\u9634\u98ce\u795e\u9556\u5de6\u4ef2\u671b\u53cc\u53cc\u4fa7\u51fb\uff0c\u90a3\u77e5\u8f9b\u6377\u5168\u7136\u4e0d\u987e\uff0c\u5251\u62db\u6597\u53d8\u201c\u4e4d\u60ca\u6885 \u98ce\u201d\u7b14\u76f4\u523a\u5411\u7126\u5316\u2014\u2014 \u3000\u3000\u7126\u5316\u89c1\u8f9b\u6377\u8fd9\u7b49\u4e0d\u8981\u547d\u7684\u6253\u6cd5\uff0c\u4e0d\u7981\u5fae\u5fae\u4e00\u5446\uff0c\u8f9b\u6377\u5251\u5f0f\u4f55\u7b49\u901f\u6377\uff0c\u5251\u5149\u66b4\u957f\uff0c\u5b9b \u5982\u624b\u81c2\u7a81\u7136\u52a0\u957f\u4e00\u8282\u4e00\u822c\uff0c\u6ce2\u7684\u4e00\u4e0b\uff0c\u7126\u5316\u602a\u53eb\u4e00\u58f0\uff0c\u5de6\u80a9\u5df2\u88ab\u523a\u7a7f\u4e00\u5b54\u3002 \u3000\u3000\u7136\u800c\u5de6\u4ef2\u671b\u7684\u957f\u5251\u4e5f\u5728\u8f9b\u6377\u5de6\u80f8\u7559\u4e0b\u4e00\u9053\u5bf8\u6df1\u7684\u53e3\u5b50\uff0c\u8f9b\u6377\u7709\u5934\u90fd\u6ca1\u6709\u76b1\u4e00\u4e0b\uff0c\u9c9c \u7ea2\u7684\u8840\u4ece\u4f24\u53e3\u6d8c\u6d8c\u800c\u51fa\uff0c\u5728\u4ed6\u7684\u8863\u895f\u524d\u7559\u4e0b\u957f\u957f\u7684\u4e00\u9053\uff0c\u4ed6\u50cf\u4e00\u4e1d\u611f\u89c9\u4e5f\u6ca1\u6709\uff0c\u6f20\u7136\u5730\uff0c \u98de\u5feb\u5730\u6325\u52a8\u7740\u957f\u5251\uff0c\u5251\u5f0f\u6bd4\u539f\u5148\u66f4\u52a0\u51cc\u5389\u51e0\u5206\uff0c\u7740\u7740\u5b58\u7740\u4e24\u8d25\u4ff1\u4f24\u7684\u51b3\u5fc3\u2014\u2014 \u3000\u3000\u62fc\u6597\u6108\u6765\u6108\u60e8\u70c8\uff0c\u8840\u5149\u7eb7\u98de\u4e2d\uff0c\u8f9b\u6377\u6e10\u6e10\u8131\u529b\u6108\u6218\u6108\u9000\uff0c\u6e10\u6e10\u9000\u4e0a\u4e86\u5c71\u5761\u9876\u2014\u2014 \u3000\u3000\u6708\u5149\u6aac\u6aac\uff0c\u591c\u8272\u51c4\u7136\uff0c\u51c9\u98ce\u5439\u7740\uff0c\u867d\u4e0d\u50cf\u523a\u9aa8\u4e00\u822c\uff0c\u5374\u4e5f\u751a\u662f\u96be\u71ac\uff0c\u6de1\u6de1\u7684\u6e05\u8f89\u7167 \u7740\u5927\u5730\uff0c\u4f46\u6b64\u65f6\u6b64\u9645\u5374\u4e1d\u6beb\u6ca1\u6709\u548c\u5e73\u6e29\u67d4\u7684\u611f\u89c9\uff0c\u76f8\u53cd\u7684\uff0c\u7adf\u4ee4\u4eba\u6709\u8083\u6740\u7684\u7d27\u5f20\u2014\u2014 \u3000\u3000\u5761\u9876\u4e0a\uff0c\u516b\u6761\u4eba\u5f71\u8df3\u52a8\u7740\uff0c\u5982\u98ce\u822c\u52a8\u4f5c\u518d\u52a0\u4e0a\u65f6\u8098\u5c16\u9510\u7684\u5636\u5636\u4e4b\u58f0\uff0c\u66f4\u589e\u52a0\u51e0\u5206\u60e8 \u70c8\u7684\u6c14\u6c1b\u3002 \u3000\u3000\u7830\u7136\u4e00\u58f0\uff0c\u8f9b\u6377\u80cc\u4e0a\u53c8\u4e2d\u4e86\u4e00\u62db\uff0c\u52c9\u5f3a\u538b\u5236\u7684\u5185\u4f24\u518d\u4e5f\u63a7\u5236\u4e0d\u4f4f\uff0c\u4ed6\u6643\u4e86\u4e24\u6643\uff0c\u4f17 \u4eba\u4ee5\u4e3a\u4ed6\u5fc5\u7136\u5012\u4e0b\uff0c\u90a3\u77e5\u4ed6\u6643\u5f97\u4e24\u6643\uff0c\u54c7\u5730\u53c8\u662f\u4e00\u53e3\u9c9c\u8840\u55b7\u51fa\uff0c\u8fce\u9762\u5343\u624b\u5251\u5ba2\u9646\u65b9\u9996\u5f53 \u5176\u51b2\uff0c\u88ab\u9c9c\u8840\u55b7\u4e86\u4e00\u5934\uff0c\u6b63\u4f38\u624b\u62b9\u6293\uff0c\u60e8\u53eb\u58f0\u8d77\uff0c\u5df2\u88ab\u8f9b\u6377\u5f53\u80f8\u4e00\u5251\u8d2f\u5165\u2014\u2014 \u3000\u3000\u8f9b\u6377\u957f\u7b11\u4e00\u58f0\uff0c\u4f46\u58f0\u97f3\u5374\u6c99\u54d1\u800c\u65e0\u54cd\uff0c\u4ed6\u6b6a\u6b6a\u659c\u659c\u6325\u5251\u800c\u4e0a\uff0c\u52a8\u4f5c\u5374\u75be\u5feb\u5982\u98ce\u2014\u2014 \u3000\u3000\u4efb\u5173\u4e2d\u4e5d\u8c6a\u90fd\u662f\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5927\u9b54\u5934\uff0c\u89c1\u4e86\u8f9b\u6377\u8fd9\u6a21\u6837\uff0c\u4e5f\u81ea\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u66f4\u52a0 \u4e5d\u8c6a\u56f4\u653b\u8f9b\u6377\u4e00\u4eba\uff0c\u5fc3\u4e2d\u672c\u5c31\u6709\u4e9b\u60f4\u7136\uff0c\u56e0\u6b64\u90fd\u662f\u4e00\u6115\u3002 \u3000\u3000\u8f9b\u6377\u7684\u957f\u5251\u5374\u4e58\u7740\u8fd9\u4e00\u6115\u4e4b\u95f4\u8fde\u6f14\u7edd\u5b66\uff0c\u5237\u5730\u4e00\u5251\u4ece\u51fa\u4eba\u610f\u5916\u7684\u4f4d\u7f6e\u523a\u5411\u7126\u5316\u3001\u7126 \u52b3\uff0c\u7126\u6c0f\u5144\u5f1f\u88ab\u903c\u5f97\u8dc3\u8eab\u540e\u9000\uff0c\u8f9b\u6377\u5374\u77a7\u90fd\u4e0d\u77a7\u53cd\u624b\u4e00\u5251\u523a\u4e2d\u80cc\u540e\u7684\u6458\u661f\u624b\u53f8\u7a7a\u5b97\uff0c\u53f8 \u7a7a\u5b97\u72c2\u53eb\u4e00\u58f0\uff0c\u5012\u5728\u8840\u6cca\u4e2d\uff01 \u3000\u3000\u8f9b\u6377\u95ed\u4f4f\u4e00\u53e3\u6c14\uff0c\u65cb\u98ce\u4f3c\u5730\u8f6c\u8eab\u626c\u5251\uff0c\u7126\u52b3\u72c2\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u62fc\u5168\u529b\u731b\u53d1\u4e00\u638c\uff0c\u957f\u5929 \u4e00\u78a7\u767d\u98ce\u4e5f\u540c\u65f6\u52a0\u4e0a\u4e00\u638c\uff0c\u8f9b\u6377\u51dd\u795e\u5f15\u5251\u4e00\u5e26\uff0c\u6253\u7b97\u5316\u5f00\u6765\u52bf\uff0c\u90a3\u77e5\u4ed6\u771f\u529b\u5df2\u5c3d\uff0c\u654c\u4eba \u638c\u529b\u53ea\u5316\u53bb\u4e00\u534a\uff0c\u7acb\u523b\u80f8\u524d\u6709\u5982\u950b\u51fb\uff0c\u8033\u4e2d\u55e1\u4e00\u58f0\uff0c\u5f80\u540e\u4fbf\u5012\u2014\u2014 \u3000\u3000\u6797\u5c11\u768b\u98de\u8eab\u800c\u4e0b\uff0c\u90a3\u77e5\u8f9b\u6377\u84e6\u5730\u4e00\u8dc3\u800c\u8d77\uff0c\u5de6\u624b\u6301\u5251\u594b\u529b\u4e0a\u632a\uff0c\u5251\u4e00\u79bb\u624b\uff0c\u65cb\u98ce\u4f3c \u7684\u4e00\u56de\u8eab\uff0c\u53cd\u624b\u4e00\u638c\u62cd\u5411\u6b8b\u7126\u52b3\u2014\u2014 \u3000\u3000\u6797\u5c11\u768b\u5168\u529b\u6251\u4e0b\uff0c\u6b63\u5f85\u4e00\u62f3\u5c06\u8f9b\u6377\u6253\u6210\u8089\u997c\uff0c\u4e0d\u6599\u8f9b\u6377\u4e00\u5251\u8131\u624b\u63b7\u51fa\uff0c\u4e24\u4e0b\u5b50\u90fd\u662f \u5168\u529b\u800c\u53d1\uff0c\u76f4\u5413\u5f97\u4ed6\u624b\u811a\u65e0\u63aa\uff0c\u60e8\u53f7\u58f0\u8d77\uff0c\u957f\u5251\u7adf\u8d2f\u5589\u800c\u8fc7\uff0c\u4ed6\u4ecd\u51b2\u51fa\u4e08\u4f59\u65b9\u843d\u5728\u5730\u4e0a\uff01 \u3000\u3000\u5929\u6b8b\u7126\u52b3\u89c1\u8f9b\u6377\u5782\u6b7b\u6323\u624e\uff0c\u4e00\u638c\u65e0\u529b\u5730\u62cd\u6765\uff0c\u5355\u638c\u5fae\u7acb\uff0c\u5c31\u6253\u7b97\u5316\u53bb\u6765\u52bf\uff0c\u90a3\u77e5\u8fd9 \u638c\u4e43\u662f\u8f9b\u6377\u6700\u540e\u529f\u529b\u6240\u805a\uff0c\u770b\u4f3c\u65e0\u529b\uff0c\u5176\u5b9e\u5185\u52b2\u542b\u8574\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u7126\u52b3\u602a\u53eb\u4e00\u58f0\uff0c\u5012\u9000 \u4e08\u4f59\uff0c\u638c\u9aa8\u7adf\u9669\u4e9b\u88ab\u9707\u65ad\uff01 \u3000\u3000\u7136\u800c\u8f9b\u6377\u7ec8\u4e8e\u54c4\u5730\u5012\u4e0b\u4e86\u2014\u2014 \u3000\u3000\u53ef\u7b11\u5173\u4e2d\u4e5d\u8c6a\u4e03\u4eba\u56f4\u653b\u8f9b\u6377\uff0c\u7adf\u7136\u88ab\u51fb\u6bd9\u4e09\u4eba\uff0c\u5176\u4ed6\u51e0\u4eba\u4e5f\u53d7\u4e86\u4f24\uff0c\u867d\u7136\u8f9b\u6377\u4e5f\u5012 \u5728\u5730\u4e0a\uff0c\u4f46\u662f\u8fd9\u4ee3\u4ef7\u4e0d\u80fd\u8bf4\u4e0d\u5927\u5427\uff01 \u3000\u3000\u6d77\u5929\u53cc\u715e\u60ca\u6012\u5730\u4e92\u76f8\u770b\u4e86\u4e00\u773c\uff0c\u9f9f\u5c71\u9876\u53cc\u6218\u8f9b\u6377\u65f6\uff0c\u8f9b\u6377\u867d\u7136\u529f\u529b\u9ad8\u5f3a\uff0c\u4f46\u4ecd\u662f\u88ab \u4e24\u4eba\u903c\u4e0b\u60ac\u5d16\uff0c\u6570\u6708\u4e0d\u89c1\uff0c\u8f9b\u6377\u529f\u529b\u7adf\u53c8\u589e\u8fdb\u4e86\u8bb8\u591a\uff01 \u3000\u3000\u8f9b\u6377\u5012\u5728\u5730\u4e0a\uff0c\u5176\u5b9e\u5fc3\u4e2d\u5341\u5206\u6e05\u9192\uff0c\u53ea\u662f\u4ed6\u7684\u4f53\u529b\u5df2\u65e0\u6cd5\u652f\u6301\u4ed6\u7ad9\u8d77\u6765\uff0c\u4ed6\u8d34\u5728\u5730 \u4e0a\u7684\u8033\u6735\u542c\u89c1\u6e05\u6670\u7684\u811a\u6b65\u58f0\uff0c\u4e0d\u77e5\u662f\u7126\u52b3\u8fd8\u662f\u7126\u5316\uff0c\u53cd\u6b63\u662f\u6108\u6765\u6108\u8fd1\u4e86\u2026\u2026 \u3000\u3000\u4ed6\u60f3\uff1a\u201c\u5982\u679c\u6211\u8fd8\u6709\u4e00\u4e1d\u529b\uff0c\u6211\u5fc5\u6323\u624e\u7740\u5728\u5929\u7075\u76d6\u4e0a\u731b\u51fb\u4e00\u638c\uff0c\u514d\u5f97\u843d\u5165\u4ed6\u4eec\u7684\u624b \u4e2d\u2014\u2014\u201d\u7136\u800c\uff0c\u4ed6\u8fde\u5f2f\u6307\u5934\u7684\u529b\u6c14\u4e5f\u6ca1\u6709\u4e86\u2014\u2014 \u3000\u3000\u6b7b\uff0c\u5c31\u8981\u964d\u4e34\u4e86\u3002 \u3000\u3000\u4ed6\u7684\u5934\u8111\u53d8\u5f97\u5f02\u5e38\u51b7\u9759\uff0c\u5ffd\u7136\u90a3\u4e9b\u719f\u6089\u7684\u5f71\u5b50\u4e00\u4e00\u6d6e\u8fc7\u8111\u6d77\uff0c\u7236\u6bcd\u7684\u5927\u4ec7\uff0c\u6885\u53d4\u53d4\u3001 \u4faf\u4e8c\u53d4\u2026\u2026\u4e00\u5207\u90fd\u5b8c\u4e86\u2026\u2026 \u3000\u3000\u6700\u540e\uff0c\u4ed6\u60f3\u5230\u4e86\u5434\u51cc\u98ce\u2014\u2014\u90a3\u4e2a\u4f7f\u4ed6\u611f\u5230\u5929\u4f26\u4e4b\u4e50\u7684\u5434\u5927\u54e5\uff0c\u4e8e\u662f\u4ed6\u53c8\u60f3\u5230\u4e86\u90a3\u7f8e \u4e3d\u7684\u82cf\u60e0\u82b7\u2014\u2014 \u3000\u3000\u4ed6\u60f3\u5230\u82cf\u59d1\u5a18\u671d\u5915\u501a\u7a97\uff0c\u5728\u6eda\u6eda\u9ec4\u5c18\u4e2d\u7b49\u5019\u4ed6\u4eec\u7684\u5f52\u6765 \u3000\u3000\u2014\u2014\u5f53\u7136\u4ed6\u76f8\u4fe1\u4e3b\u8981\u662f\u4e3a\u4e86\u5434\u51cc\u98ce\u7684\u7f18\u6545\u2014\u2014\u4f46\u662f\u4ed6\u4eec\u66fe\u4eb2\u53e3\u7b54\u5e94\u4e00\u5b9a\u8981\u56de\u53bb\u89c1\u5979 \u4e00\u9762\uff0c\u4eb2\u53e3\u7b54\u5e94\u7684\u554a\uff01 \u3000\u3000\u4ed6\u60f3\u5230\u82cf\u59d1\u5a18\u83b9\u4eae\u7684\u6cea\u73e0\u4ece\u7a97\u53e3\u6ef4\u843d\u5c18\u571f\u2026\u2026 \u3000\u3000\u201c\u5434\u5927\u54e5\u6b7b\u4e86\uff0c\u5982\u679c\u6211\u4e00\u6b7b\uff0c\u5979\u5c06\u7b49\u4e00\u8f88\u5b50\u4e86\uff0c\u5979\u4e00\u5b9a\u4f1a\u7b49\u4e00\u8f88\u5b50\u7684\uff01\u201d \u3000\u3000\u4e00\u79cd\u4ece\u6765\u6ca1\u6709\u8fc7\u7684\u5f3a\u70c8\u6b32\u671b\u51b2\u4e0a\u8f9b\u6377\u7684\u5fc3\u7530\uff0c\u4ed6\u7528\u65e0\u6cd5\u542c\u89c1\u7684\u58f0\u97f3\u8bf4\u9053\uff1a\u201d \u3000\u3000\u8f9b\u6377\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff0c\u4f60\u6d3b\u5728\u4e16\u4e0a\u65e2\u8bf4\u4e0d\u4e0a\u5fe0\uff0c\u66f4\u8bf4\u4e0d\u4e0a\u5b5d\uff0c\u8fd9\u4e2a\u2018\u4fe1\u2019\u5b57\u597d\u6b79\u8981\u5b88\u554a\uff0c \u8f9b\u6377\u554a\uff0c\u4f60\u4e0d\u80fd\u6b7b\uff01\u201d \u3000\u3000\u811a\u6b65\u66f4\u8fd1\u4e86\uff0c\u90a3\u662f\u5929\u6b8b\u7126\u52b3\uff01 \u3000\u3000\u84e6\u7136\u2014\u2014 \u3000\u3000\u8f9b\u6377\u50cf\u662f\u5168\u8eab\u89e6\u4e86\u7535\uff0c\u547c\u5730\u4e00\u58f0\u4e00\u8dc3\u800c\u8d77\uff0c\u8eab\u4f53\u5df2\u5982\u4e00\u652f\u7bad\u822c\u5c04\u5411\u5761\u4e0b\u2014\u2014 \u3000\u3000\u4f17\u4eba\u53ea\u89c1\u4e00\u6761\u9ed1\u5f71\u5728\u7a7a\u4e2d\u4e0d\u501f\u529b\u5730\u98de\u817e\u4e09\u6b21\uff0c\u5c31\u6eda\u843d\u5165\u9ed1\u6697\u4e2d\u3002 \u3000\u3000\u4f17\u4eba\u60ca\u4e8e\u8fd9\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u795e\u5947\u8f7b\u529f\uff0c\u66f4\u60ca\u4e8e\u5782\u6b7b\u7684\u4eba\u7adf\u6709\u5982\u6b64\u60ca\u4eba\u7684\u529b\u91cf\u3002 \u3000\u3000\u4ed6\u4eec\u7684\u7ecf\u9a8c\u53ea\u80fd\u627e\u51fa\u4e00\u4e2a\u7406\u7531\uff1a\u4eba\u6b7b\u4ee5\u524d\u56de\u5149\u8fd4\u7167\u5f80\u5f80\u6709\u60ca\u4eba\u7684\u529b\u91cf\u4ea7\u751f\uff0c\u8f9b\u6377\u6eda \u4e86\u4e0b\u53bb\uff0c\u4f46\u5fc5\u7136\u7acb\u523b\u5730\u6b7b\u53bb\u7684\u2014\u2014\u4e0d\u53ef\u5426\u8ba4\uff0c\u4ed6\u4eec\u662f\u6709\u4e00\u4e9b\u81ea\u6211\u5b89\u6170\u7684\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u98de\u5feb\u5730\u8ffd\u4e86\u4e0b\u53bb\uff0c\u4f46\u662f\u9ed1\u591c\u68ee\u68ee\uff0c\u4e0d\u89c1\u8f9b\u6377\u7684\u201c\u5c38\u9996\u201d\u2014\u2014\u5f53\u7136\uff0c\u4ed6\u4eec\u4ecd \u662f\u5b81\u613f\u8bf4\u8f9b\u6377\u6eda\u4e0b\u53bb\u5fc5\u7136\u6b7b\u53bb\u4e86\u3002 \u3000\u3000\u5929\u6b8b\u7126\u52b3\u4ecd\u4e0d\u670d\u6c14\uff0c\u65bd\u5c55\u8f7b\u529f\u5728\u5468\u56f4\u5bfb\u4e86\u4e00\u904d\uff0c\u5374\u59cb\u7ec8\u4e0d\u89c1\u8f9b\u6377\u7684\u201c\u5c38\u9996\u201d \u3000\u3000\u8fd9\u65f6\u5761\u9876\u4e0a\u957f\u5929\u4e00\u78a7\u767d\u98ce\u5ffd\u53eb\u9053\uff1a\u201c\u8001\u5927\uff0c\u4e0b\u9762\u6709\u4eba\u6765\u4e86\u2014\u2014\u554a\uff0c\u8fd9\u5bb6\u4f19\u597d\u4fca\u7684\u8f7b \u529f\u2014\u2014\u201d \u3000\u3000\u7126\u52b3\u95fb\u8a00\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u60f3\u82e5\u662f\u8ba9\u4eba\u628a\u5173\u866b\u4e5d\u8c6a\u73b0\u5728\u8fd9\u526f\u72fc\u72c8\u50cf\u770b\u53bb\u7684\u8bdd\uff0c\u4ee5\u540e\u4e5f\u4e0d \u8981\u60f3\u6df7\u4e0b\u53bb\u4e86\uff0c\u8d76\u7d27\u5bf9\u5144\u5f1f\u6253\u4e2a\u624b\u52bf\uff0c\u8dc3\u4e0a\u659c\u5761\u3002 \u3000\u3000\u5c45\u9ad8\u4e0b\u671b\uff0c\u53ea\u89c1\u4e00\u6761\u4eba\u5f71\u6b63\u4ee5\u5168\u901f\u8d76\u4e86\u8fc7\u6765\uff0c\u90a3\u4eba\u8f7b\u529f\u597d\u751f\u4e86\u5f97\uff0c\u4e00\u8dc3\u6570\u4e08\u800c\u4e14\u4e1d \u6beb\u4e0d\u89c1\u6025\u4fc3\uff0c\u4e00\u6d3e\u5b89\u8be6\u6f47\u6d12\u4e4b\u6001\u3002 \u3000\u3000\u7126\u52b3\u5fc3\u9053\uff1a\u201c\u6b64\u4eba\u529f\u592b\u6781\u4e3a\u4e86\u4e0d\u8d77\uff0c\u6837\u5b50\u5374\u751a\u964c\u751f\uff0c\u6b64\u65f6\u6df1\u591c\u8d76\u6765\uff0c\u591a\u534a\u662f\u654c\u4e0d\u662f \u53cb\u2014\u2014\u201d \u3000\u3000\u4ed6\u56de\u5934\u770b\u4e86\u770b\u5730\u4e0a\u7684\u6b7b\u5c38\u4ee5\u53ca\u4f19\u4f34\u4f24\u75b2\u4e4b\u6001\uff0c\u7565\u4e3a\u6c89\u541f\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8d70\uff01\u201d \u3000\u3000\u5c71\u5761\u4e0b\uff0c\u7ecf\u8fc7\u4e00\u7247\u8346\u4e1b\u4e71\u77f3\uff0c\u76f4\u8fbe\u4e00\u6761\u5c0f\u6cb3\u65c1\uff0c\u6cbf\u5761\u867d\u7136\u602a\u77f3\u53c2\u5dee\uff0c\u8346\u68d8\u904d\u5730\uff0c\u4f46 \u662f\u6cb3\u7554\u5374\u662f\u51c4\u51c4\u82b3\u8349\uff0c\u867d\u7136\u662f\u5bd2\u51ac\uff0c\u4f46\u5374\u4e0d\u89c1\u67af\u9ec4\uff0c\u8fd9\u8bc1\u660e\u4e86\u91ce\u5c18\u8349\u7684\u5f3a\u608d\u62b5\u6297\u529b\u3002 \u3000\u3000\u6cb3\u7554\uff0c\u8eba\u7740\u4e00\u4e2a\u8eab\u8eaf\uff0c\u4ed6\u6ee1\u8eab\u8863\u886b\u6302\u5f97\u7834\u788e\u4e0d\u582a\uff0c\u9f3b\u4e0a\u4e5f\u5168\u662f\u4f24\u75d5\uff0c\u6562\u60c5\u662f\u4ece\u90a3\u4e9b \u8346\u68d8\u4e2d\u6eda\u4e0b\u6765\u7684\u5427\u3002 \u3000\u3000\u4ed6\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u6015\u662f\u2014\u2014 \u3000\u3000\u4e0d\uff0c\u4ed6\u6ca1\u6709\u6b7b\uff0c\u4ed6\u662f\u8f9b\u6377\uff0c\u4ed6\u6709\u8d85\u4eba\u7684\u751f\u547d\u529b\uff0c\u4ed6\u7684\u7cbe\u795e\u610f\u5fd7\u5e38\u652f\u6301\u7740\u4ed6\u505a\u5230\u5e38\u4eba \u65e0\u6cd5\u505a\u5230\u7684\u4e8b\u2014\u2014 \u3000\u3000\u4e0d\u8fc7\uff0c\u4ed6\u867d\u8fd8\u6709\u4e00\u4e1d\u6c14\u606f\uff0c\u4f46\u662f\u90a3\u662f\u4f55\u7b49\u5fae\u5f31\uff0c\u5931\u8840\u8fc7\u591a\uff0c\u52a0\u4e0a\u4e25\u91cd\u7684\u5185\u4f24\uff0c\u4ed6\u867d \u6ca1\u6709\u65ad\u6c14\uff0c\u4f46\u662f\u5df2\u6e10\u6e10\u6b65\u5411\u6b7b\u4ea1\u4e86\u3002 \u3000\u3000\u6b64\u523b\uff0c\u4ed6\u7684\u795e\u667a\u6e05\u6670\u5f97\u5f02\u4e4e\u5bfb\u5e38\u2014\u2014\u4e5f\u8bb8\u662f\u7531\u4e8e\u8089\u4f53\u5b8c\u5168\u9ebb\u6728\u7684\u539f\u6545\u5427\u3002 \u3000\u3000\u4ed6\u4e0d\u60f3\u7236\u6bcd\uff0c\u4e5f\u4e0d\u60f3\u6885\u53d4\u53d4\uff0c\u66f4\u4e0d\u60f3\u5176\u4ed6\uff0c\u4ed6\u8111\u6d77\u4e2d\u5168\u662f\u521a\u624d\u90a3\u573a\u60e8\u70c8\u7684\u62fc\u6597\uff0c\u6bcf \u4e00\u62db\u6bcf\u4e00\u5f0f\u4ed6\u90fd\u80fd\u6e05\u695a\u5730\u8bb0\u5f97\u3002 \u3000\u3000\u4ed6\u7684\u601d\u60f3\u6062\u590d\u4e86\u654f\u6377\uff0c\u4e5f\u8bb8\u6bd4\u5e73\u65f6\u8fd8\u8981\u654f\u6377\u4e00\u4e9b\uff0c\u90a3\u4e9b\u51f6\u72e0\u7684\u62db\u5f0f\u4e00\u4e00\u6d6e\u8fc7\u5fc3\u5708\uff0c \u5ffd\u7136\u4ed6\u60f3\u8d77\u5927\u884d\u5341\u5f0f\u4e2d\u90a3\u4e9b\u719f\u6089\u7684\u5f0f\u5b50\uff0c\u4ed6\u7684\u5fc3\u5934\u4e00\u9707\uff0c\u8bb8\u591a\u5947\u5999\u7684\u5730\u65b9\u6b64\u523b\u4ed6\u7a81\u7136\u9886 \u609f\u4e86\uff0c\u4e5f\u8bb8\u51f6\u72e0\u5730\u62fc\u6597\u540e\u52a0\u4ee5\u6f5c\u5fc3\u7684\u601d\u7d22\u548c\u56de\u5fc6\uff0c\u5e2e\u52a9\u4ed6\u542f\u5f00\u4e86\u65e0\u6570\u795e\u5999\u4e4b\u95e8\uff0c\u4ed6\u4e1d\u6beb \u4e0d\u89c9\u5f97\u81ea\u5df1\u5c31\u8981\u6b7b\u4e86\uff0c\u56e0\u4e3a\u90a3\u4e9b\u795e\u5947\u7684\u53d8\u5316\u548c\u65b0\u53d1\u73b0\u5360\u636e\u4e86\u4ed6\u5168\u90e8\u55dc\u6b66\u7684\u8111\u5b50\u3002 \u3000\u3000\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u9ed8\u9ed8\u81ea\u8bed\uff1a\u201c\u82e5\u662f\u65e9\u4e00\u4e9b\u60f3\u5230\u8fd9\u4e9b\uff0c\u6b64\u523b\u5c40\u9762\u4e5f\u8bb8\u8981\u4e0d\u540c\u4e86\u2014\u2014 \u554a\uff0c\u8fd9\u5927\u884d\u5341\u5f0f\u771f\u662f\u5999\u6781\u2014\u2014\u201d \u3000\u3000\u663e\u7136\uff0c\u4ed6\u53c8\u591a\u609f\u5230\u4e86\u8bb8\u591a\u8fd9\u5929\u4e0b\u7b2c\u4e00\u5947\u4eba\u6bd5\u751f\u7edd\u5b66\u4e2d\u7cbe\u5965\u4e4b\u5904\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u4ed6\u7684\u5251 \u672f\u53c8\u66f4\u7cbe\u8fdb\u4e86\u2014\u2014 \u3000\u3000\u7136\u800c\uff0c\u8fd9\u6709\u4ec0\u4e48\u7528\u5462\uff1f\u9664\u975e\u4ed6\u7528\u201c\u671d\u95fb\u9053\uff0c\u5915\u6b7b\u53ef\u77e3\u201d\u6765\u5b89\u6170\u81ea\u5df1\u2026\u2026 \u3000\u3000\u4e0d\u8bba\u600e\u6837\uff0c\u4ed6\u662f\u6e10\u6e10\u5730\u6b7b\uff0c\u6e10\u6e10\u5730\u67af\u840e\u4e86\u2026\u2026 \u3000\u3000\u5c71\u5761\u4e0a\uff0c\u6d77\u5929\u53cc\u715e\u7b49\u79bb\u5f00\u540e\uff0c\u4e00\u5207\u90fd\u662f\u9759\u6084\u6084\u7684\u2014\u2014 \u3000\u3000\u5237\u5730\u4e00\u58f0\uff0c\u4e00\u6761\u4eba\u5f71\u98de\u8dc3\u4e0a\u6765\uff0c\u90a3\u4efd\u8f7b\u7075\u6f47\u6d12\u6bd4\u4e4b\u65b9\u624d\u79bb\u5f00\u7684\u6d77\u5929\u53cc\u715e\u90fd\u6709\u8fc7\u4e4b\u800c \u65e0\u4e0d\u53ca\u2014\u2014 \u3000\u3000\u4ed6\u6115\u7136\u5730\u671b\u7740\u5730\u4e0a\u7684\u5c38\u4f53\uff0c\u4ed6\u624b\u4e2d\u63e1\u7740\u4e00\u6bb5\u7ea2\u8272\u7684\u7f0e\u5e26\uff0c\u90a3\u662f\u4ed6\u4ece\u4e00\u53ea\u9e3d\u5b50\u4e0a\u53d6\u4e0b \u6765\u7684\u2014\u2014\u8fd9\u4e5f\u662f\u4e5d\u8c6a\u53ea\u5230\u4e03\u8c6a\u7684\u539f\u56e0\u4e86\u3002 \u3000\u3000\u4ed6\u8f6c\u8fc7\u8eab\u6765\uff0c\u6708\u5149\u7167\u5728\u4ed6\u8138\u4e0a\uff0c\u660e\u4eae\u7684\u9635\u5b50\u95ea\u51fa\u667a\u6167\u7684\u5149\u8292\uff0c\u633a\u76f4\u7684\u9f3b\u6881\u4ee3\u8868\u7740\u6b63 \u76f4\u800c\u575a\u6bc5\uff0c\u90a3\u4fca\u7f8e\u5143\u6bd4\u7684\u9762\u5e9e\u5728\u6de1\u6de1\u6708\u5149\u4e0b\u66f4\u52a0\u663e\u5f97\u79c0\u9038\u4e0d\u7fa4\u3002 \u3000\u3000\u4ed6\uff0c\u7adf\u662f\u8dcc\u843d\u6cf0\u5c71\u65e5\u89c2\u5cf0\u4e0b\u7684\u5434\u51cc\u98ce\uff01 \u3000\u3000\u4ed6\u4e0d\u89e3\u5730\u5750\u5728\u4e00\u68f5\u6811\u4e0b\uff0c\u671b\u7740\u5730\u4e0a\u7684\u5c38\u9996\uff0c\u4ed6\u60f3\u5230\u8fd9\u4e9b\u65e5\u5b50\u6765\u81ea\u5df1\u7684\u7ecf\u5386\uff0c\u771f\u662f\u4e0d \u514d\u6709\u4e24\u4e16\u4e3a\u4eba\u4e4b\u611f\uff0c\u4ed6\u8f7b\u8f7b\u957f\u53f9\u4e86\u4e00\u58f0\uff0c\u90a3\u53f9\u58f0\u4e2d\u9664\u4e86\u832b\u7136\uff0c\u8fd8\u6709\u4e00\u4e1d\u611f\u6fc0\u4e0a\u82cd\u7684\u60c5\u610f \u2014\u2014 \u3000\u3000\u4e14\u8bf4\u90a3\u5929\u5434\u51cc\u98ce\u4e0e\u91d1\u6b39\u4e92\u62b1\u6eda\u4e0b\u60ac\u5d16\uff0c\u51cc\u98ce\u81ea\u91cf\u5fc5\u6b7b\uff0c\u4f46\u5728\u6b7b\u4e4b\u524d\uff0c\u5fc5\u987b\u5148\u6740\u6b7b\u91d1 \u6b39\uff0c\u624d\u80fd\u7791\u76ee\uff0c\u4e8e\u662f\u4ed6\u6084\u6084\u5730\u677e\u5f00\u4e86\u53f3\u624b\uff0c\u731b\u7136\u5411\u91d1\u6b39\u592a\u9633\u7a74\u7838\u53bb\uff0c\u90a3\u77e5\u91d1\u6b39\u4e5f\u4e0e\u4ed6\u4e00 \u822c\u5fc3\u601d\uff0c\u4e8c\u62f3\u5728\u7a7a\u4e2d\u76f8\u51fb\uff0c\u8fd9\u539f\u662f\u4e8c\u4eba\u81f4\u547d\u7684\u4e00\u51fb\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u51cc\u98ce\u53ea\u611f\u5230\u6c14\u8840\u7ffb\u817e\uff0c \u90a3\u53ea\u62b1\u7740\u91d1\u6b39\u7684\u5de6\u624b\uff0c\u4e5f\u4e0d\u7531\u81ea\u4e3b\u7684\u677e\u5f00\uff0c\u53f3\u624b\u66f4\u662f\u75bc\u75db\u6b32\u88c2\uff0c\u4e8c\u4eba\u8eab\u4f53\u4e00\u5206\u5f00\uff0c\u51cc\u98ce \u89c9\u5f97\u4e0b\u5760\u4e4b\u52bf\u66f4\u75be\uff0c\u5411\u4e0b\u4e00\u770b\uff0c\u767d\u832b\u832b\u7684\u4e00\u7247\uff0c\u4e0d\u77e5\u5230\u5e95\u6709\u591a\u6df1\uff0c\u4ed6\u4e0d\u987e\u75bc\u75db\uff0c\u53cc\u624b\u5411 \u5d16\u58c1\u4e71\u6293\uff0c\u60f3\u6500\u6293\u5230\u4efb\u4f55\u53ef\u501f\u529b\u7684\u4e1c\u897f\uff0c\u751a\u81f3\u4e00\u6839\u5c0f\u8349\u4e5f\u597d\uff0c\u7a81\u7136\uff0c\u4ed6\u89c9\u5f97\u811a\u4e0b\u8e0f\u5b9e\u4e86\uff0c \u5728\u8fd9\u751f\u6b7b\u5173\u5934\uff0c\u4ed6\u4e0d\u52a0\u601d\u7d22\u7684\u501f\u4e3a\u5411\u4e0a\u4e00\u7a9c\uff0c\u7565\u7a33\u4e0b\u843d\u8eab\u5b50\uff0c\u518d\u4f4e\u5934\u4e00\u770b\uff0c\u987f\u65f6\u5fc3\u4e2d\u5145 \u6ee1\u4e86\u4fa5\u5e78\u4e0e\u611f\u6fc0\u4e4b\u60c5\u3002\u539f\u6765\uff0c\u521a\u624d\u4ed6\u53ea\u6ce8\u610f\u5d16\u58c1\u4e0a\u9762\u6709\u6ca1\u6709\u4efb\u4f55\u53ef\u501f\u529b\u7684\u4e1c\u897f\uff0c\u6839\u672c\u6ca1 \u6709\u5728\u610f\u5230\u811a\u4e0b\u60c5\u51b5\uff0c\u6b64\u65f6\u4f4e\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u68f5\u7897\u53e3\u7c97\u7ec6\u7684\u6811\u6728\uff0c\u4ece\u77f3\u4e2d\u6a2a\u751f\u51fa\u6765\uff0c\u4ed6\u5728 \u7edd\u671b\u4e2d\u5ffd\u9022\u4e00\u7ebf\u751f\u673a\uff0c\u7cbe\u795e\u5927\u632f\uff0c\u501f\u7740\u4e0a\u7a9c\u4e0b\u529b\uff0c\u7a33\u4f4f\u4e0b\u5760\u4e4b\u52bf\uff0c\u8f7b\u98d8\u98d8\u7684\u843d\u5728\u6811\u5e72\u4e0a\uff0c \u4ed6\u660e\u767d\u81ea\u5df1\u662f\u6682\u65f6\u5f97\u6551\u4e86\uff0c\u5fc3\u60c5\u4e00\u677e\uff0c\u53ea\u89c9\u5f97\u80f8\u4e2d\u6c14\u8840\u4e0a\u6d8c\uff0c\u5589\u5934\u53d1\u751c\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f \u54c7\u7684\u4e00\u58f0\uff0c\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\u3002\u4ed6\u5fc3\u4e2d\u660e\u767d\u5148\u524d\u4e0e\u91d1\u6b39\u76f8\u51fb\uff0c\u9707\u52a8\u5185\u810f\uff0c\u521a\u624d\u6b7b\u91cc\u9003\u751f\uff0c\u4e0d \u4f46\u4e0d\u53ca\u8fd0\u529f\u5236\u6b62\u4f24\u52bf\u6076\u5316\uff0c\u53cd\u800c\u5984\u7528\u771f\u529b\uff0c\u65e0\u5f02\u706b\u4e0a\u52a0\u6cb9\uff0c\u4f24\u52bf\u5b9a\u7136\u52a0\u91cd\uff0c\u5f53\u4ed6\u5760\u4e0b\u60ac \u5d16\u65f6\uff0c\u539f\u4e0d\u5b58\u751f\u5ff5\uff0c\u4f46\u6b64\u523b\u65e2\u5df2\u5f97\u6551\uff0c\u6c42\u751f\u4e4b\u5ff5\u6cb9\u7136\u800c\u751f\uff0c\u4ed6\u8d76\u7d27\u95ed\u8d77\u53cc\u76ee\uff0c\u6452\u9664\u6742\u601d\uff0c \u4e00\u5fc3\u4e00\u610f\u8fd0\u8d77\u5185\u529f\u6765\uff0c\u4f46\u662f\u4e00\u53e3\u771f\u6c14\u5374\u90c1\u96c6\u80f8\u4e2d\uff0c\u59cb\u7ec8\u63d0\u4e0d\u4e0a\u6765\uff0c\u4ed6\u8bd5\u4e86\u51e0\u6b21\uff0c\u90fd\u6ca1\u6709 \u6210\u529f\uff0c\u7070\u5fc3\u7684\u53f9\u4e86\u53e3\u6c14\uff0c\u53f3\u624b\u7684\u75bc\u75db\uff0c\u4e5f\u6108\u6765\u6108\u589e\u52a0\u3002 \u3000\u3000\u96fe\u6c14\u6108\u6765\u6108\u6d53\uff0c\u4ed6\u611f\u5230\u5929\u8272\u4e5f\u6e10\u6e10\u6697\u4e86\uff0c\u5bd2\u98ce\u547c\u547c\uff0c\u65f6\u800c\u5982\u864e\u5578\u9f99\u541f\uff0c\u65f6\u800c\u5982\u90c1\u5987 \u591c\u6ce3\uff0c\u51cc\u98ce\u65bd\u5c55\u5343\u65a4\u5760\uff0c\u7a33\u7a33\u7684\u5750\u5728\u6811\u4e0a\uff0c\u8eab\u5b50\u5982\u9ecf\u5728\u6811\u679d\u4e0a\u4e00\u6837\uff0c\u968f\u7740\u6811\u679d\u8d77\u4f0f\u6447\u6446\uff0c \u4ed6\u7684\u5fc3\u60c5\u4e5f\u50cf\u6811\u679d\u4e00\u822c\u8d77\u4f0f\u4e0d\u5b9a\u2026\u2026\u513f\u65f6\u7684\u60c5\u666f\u6e05\u6e05\u695a\u695a\u7684\u6d6e\u5728\u773c\u524d\uff0c\u90a3\u4e2a\u6865\u4e0b\u7684\u6d41\u6c34\uff0c \u90a3\u8def\u65c1\u7684\u5c0f\u8305\u5c4b\uff0c\u5c4b\u65c1\u56db\u5468\u67d4\u8f6f\u7684\u5c0f\u8349\uff0c\u90a3\u513f\u6b63\u662f\u4ed6\u6bcf\u5929\u4e0b\u5348\u8eba\u7740\u4f11\u606f\uff0c\u4ef0\u89c6\u98d8\u6e3a\u767d\u4e91 \u7684\u597d\u5730\u65b9\uff0c\u708a\u70df\u6e10\u6e10\u5347\u8d77\u6765\uff0c\u76d8\u65cb\u7740\uff0c\u76d8\u65cb\u7740\uff0c\u5fae\u98ce\u5439\u6563\u4e86\u8885\u8885\u8f7b\u70df\uff0c\u5c0f\u8305\u5c4b\u95e8\u5f00\u4e86\uff0c \u6162\u6162\u5730\u73b0\u51fa\u4e86\u4e00\u5f20\u5a07\u7f8e\u7684\u5c0f\u8138\uff0c\u50cf\u82f9\u679c\u4e00\u6837\u7ea2\u7684\u53cc\u988a\uff0c\u50cf\u5c0f\u661f\u4e00\u6837\u4eae\u7684\u773c\u775b\uff0c\u4e00\u8df3\u4e00\u8dd1 \u7684\u5411\u4ed6\u5954\u6765\uff0c\u8111\u540e\u7684\u5c0f\u8fab\u5b50\u4e00\u6643\u4e00\u6643\uff0c\u8138\u4e0a\u6302\u6ee1\u4e86\u7a1a\u6c14\u7684\u7b11\u5bb9\u3002\u8dd1\u8fd1\u4e86\uff0c\u4ed6\u8d76\u7d27\u4e00\u8dc3\u800c \u8d77\uff0c\u7275\u7740\u90a3\u53cc\u6e29\u67d4\u6ed1\u817b\u7684\u5c0f\u624b\uff0c\u5954\u8fdb\u5c0f\u8305\u5c4b\uff0c\u6e29\u96c5\u7f8e\u4e3d\u7684\u5927\u5a18\uff0c\u603b\u662f\u5750\u5728\u684c\u65c1\u5bf9\u95e8\u53e3\u7684 \u6905\u5b50\uff0c\u5fae\u7b11\u7684\u671b\u7740\u4ed6\u4fe9\uff0c\u684c\u4e0a\u653e\u7740\u4e00\u4e24\u6837\u70ed\u6c14\u817e\u817e\u7684\u5c0f\u83dc\u80b4\u3002\u8fd9\u4e24\u6708\u6765\uff0c\u4ed6\u6d41\u8361\u6c5f\u6e56\uff0c \u4e0d\u77e5\u5403\u4e86\u591a\u5c11\u540d\u83dc\uff0c\u53ef\u662f\u4e0e\u5927\u5a18\u70e7\u7684\u83dc\u4e00\u6bd4\uff0c\u5374\u90fd\u662f\u7d22\u7136\u65e0\u5473\u2026\u2026 \u3000\u3000\u591c\u6df1\u4e86\uff0c\u4ed6\u8eab\u4e0a\u611f\u5230\u4e00\u9635\u5bd2\u610f\uff0c\u60f3\u5230\u773c\u4e0b\u8eab\u53d7\u91cd\u4f24\uff0c\u9677\u4e8e\u7edd\u5730\uff0c\u5c45\u7136\u8fd8\u6709\u5fc3\u601d\u53bb\u60f3 \u5927\u5a18\u70e7\u7684\u83dc\uff0c\u4e0d\u89c9\u5931\u7b11\uff0c\u4ed6\u6b63\u51c6\u5907\u8fd0\u529f\u5fa1\u5bd2\uff0c\u5ffd\u7136\u55c5\u5230\u4e00\u80a1\u6e05\u9999\uff0c\u4e00\u65f6\u80f8\u4e2d\u53d7\u7528\u65e0\u6bd4\uff0c \u8111\u4e2d\u4e5f\u6e10\u6e10\u5b81\u9759\u3002\u4ed6\u7528\u529b\u55c5\u7740\uff0c\u53ea\u89c9\u5f97\u8840\u6c14\u4e0d\u518d\u6c79\u6d8c\u4e0a\u51b2\uff0c\u771f\u6c14\u4e5f\u6e10\u6e10\u901a\u7545\uff0c\u4ed6\u5fc3\u4e2d\u660e \u767d\u4e00\u5b9a\u662f\u90a3\u80a1\u9999\u6c14\u7684\u529f\u7528\uff0c\u4f46\u4ed6\u56e0\u820d\u4e0d\u5f97\u5c31\u6b64\u505c\u55c5\uff0c\u6240\u4ee5\u5e76\u6ca1\u7acb\u523b\u53bb\u627e\u9999\u6c14\u7684\u6765\u6e90\uff0c\u95ed \u4e0a\u4e86\u53cc\u773c\uff0c\u4f5c\u8d77\u5410\u7eb3\u529f\u592b\uff0c\u5f53\u771f\u6c14\u8c41\u7136\u5728\u5168\u8eab\u6e38\u884c\u4e00\u5468\u540e\uff0c\u80f8\u4e2d\u8212\u7545\u65e0\u6bd4\uff0c\u53f3\u624b\u4f24\u75db\u4e5f \u5927\u4e3a\u51cf\u4f4e\u3002\u4ed6\u5f20\u5f00\u4e86\u773c\u775b\uff0c\u627e\u5bfb\u9999\u6c14\u662f\u4ece\u4f55\u5904\u53d1\u51fa\uff0c\u4e3e\u76ee\u4e00\u770b\uff0c\u5927\u611f\u60ca\u5947\uff0c\u539f\u6765\u5149\u79c3\u79c3 \u7684\u6a2a\u751f\u679d\u5e72\uff0c\u6b64\u65f6\u7a81\u7136\u751f\u51fa\u4e24\u7247\u7fe0\u7eff\u5c0f\u53f6\uff0c\u5c0f\u53f6\u4e2d\u95f4\u5939\u7740\u4e00\u7c92\u6731\u7ea2\u679c\u5b9e\uff0c\u98ce\u5411\u4ed6\u5750\u7684\u65b9 \u5411\u5439\u6765\uff0c\u9999\u6c14\u6108\u6765\u6108\u6d53\uff0c\u90a3\u7c92\u679c\u5b9e\u4e5f\u6108\u6765\u6108\u7ea2\uff0c\u51cc\u98ce\u6b63\u60f3\u8fd9\u5fc5\u662f\u7075\u836f\u5f02\u679c\uff0c\u5f53\u4e0b\u6500\u7740\u6811\uff0c \u5411\u679d\u524d\u79fb\u52a8\uff0c\u4ed6\u751f\u6015\u6811\u5e72\u5c16\u7aef\u592a\u7ec6\uff0c\u5403\u529b\u4e0d\u4f4f\uff0c\u79fb\u5230\u8ddd\u679c\u5b9e\u4e94\u516d\u5c3a\u8fdc\uff0c\u4e0d\u6562\u518d\u5411\u524d\u8fdb\uff0c \u677e\u5f00\u53f3\u624b\uff0c\u5de6\u624b\u6293\u7740\u6811\u5e72\uff0c\u5411\u524d\u4e00\u8361\uff0c\u53f3\u624b\u6b63\u597d\u6293\u4f4f\u679c\u5b50\uff0c\u6458\u4e86\u4e0b\u6765\uff0c\u6b64\u65f6\u6811\u679d\u53d7\u529b\u4e00 \u632f\uff0c\u5df2\u662f\u6447\u6447\u6b32\u6298\uff0c\u51cc\u98ce\u5c4f\u795e\u51dd\u6c14\uff0c\u53c8\u6162\u6162\u56de\u5230\u4e3b\u5e72\uff0c\u770b\u770b\u624b\u4e2d\u7684\u679c\u5b9e\uff0c\u7ea2\u5f97\u5341\u5206\u53ef\u7231\uff0c \u8fd8\u5728\u7ee7\u7eed\u957f\u5927\uff0c\u51cc\u98ce\u5fc3\u4e2d\u5f88\u5947\u602a\uff0c\u51dd\u76ee\u6ce8\u89c6\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u679c\u513f\u4e0d\u518d\u957f\u5927\uff0c\u5ffd\u7136\u7834\u88c2\uff0c\u4e00 \u80a1\u679c\u6d46\u55b7\u4e86\u51fa\u6765\uff0c\u51cc\u98ce\u6025\u5fd9\u5f20\u53e3\u5438\u63a5\uff0c\u5165\u53e3\u4f46\u89c9\u6e05\u4f8b\u7edd\u4f26\uff0c\u518d\u770b\u624b\u4e2d\u679c\u5b50\uff0c\u5df1\u7ecf\u53ea\u5269\u4e0b \u4e00\u5c42\u8584\u76ae\uff0c\u53ef\u662f\u4ecd\u7136\u9999\u90c1\u975e\u5e38\u3002\u4ed6\u820d\u4e0d\u5f97\u4e22\u6389\uff0c\u6b63\u5728\u60f3\u88c5\u5728\u4ec0\u4e48\u5730\u65b9\u6bd4\u8f83\u597d\uff0c\u65e0\u610f\u4e4b\u95f4 \u5728\u53e3\u888b\u4e2d\u6478\u7d22\u5230\u5c0f\u5c0f\u7684\u7389\u74f6\uff0c\u7a81\u7136\u4e00\u4e2a\u5ff5\u5934\u6d8c\u4e86\u4e0a\u6765\uff0c\u987f\u65f6\u4f7f\u4ed6\u5446\u82e5\u6728\u9e21\uff0c\u5fc3\u4e2d\u611f\u5230\u4e00 \u9635\u51b0\u51c9\uff0c\u4e00\u79cd\u7edd\u671b\u7684\u60c5\u7eea\uff0c\u5145\u6ee1\u4e86\u4ed6\u7684\u5fc3\u623f\uff0c\u4e00\u65f6\u95f4\uff0c\u4ed6\u8111\u4e2d\u50cf\u4e00\u5757\u767d\u7eb8\u4e00\u822c\uff0c\u4ec0\u4e48\u90fd \u4e0d\u60f3\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5343\u601d\u4e07\u60f3\u4e00\u9f50\u5728\u8111\u6d77\u4e2d\u6d6e\u8d77\u2026\u2026 \u3000\u3000\u4ed6\u6e05\u6670\u7684\u8bb0\u5f97\uff0c\u90a3\u5e74\uff0c\u4ed6\u4e5d\u5c81\u90a3\u5e74\u7684\u590f\u5929\uff0c\u4e00\u4e2a\u708e\u70ed\u7684\u4e2d\u5348\uff0c\u4ed6\u4e0e\u4e00\u7fa4\u5c0f\u670b\u53cb\uff0c\u4e00 \u9053\u5728\u5c0f\u6eaa\u4e2d\u73a9\u6c34\uff0c\u4ed6\u4e00\u5411\u80c6\u5b50\u5c31\u5f88\u5927\uff0c\u7387\u9886\u7740\u90a3\u7fa4\u5b69\u5b50\u6e38\u5411\u4e0a\u6d41\u3002\u4ed6\u4eec\u4ece\u5c0f\u5c31\u5728\u6eaa\u4e2d\u5b09 \u6c34\uff0c\u6240\u4ee5\u6c34\u6027\u90fd\u4e0d\u9519\uff0c\u5927\u4f19\u513f\u6108\u6e38\u6108\u8fdc\uff0c\u5ffd\u7136\uff0c\u4e00\u6761\u91d1\u8272\u5c0f\u9c7c\uff0c\u8df3\u51fa\u6c34\u9762\uff0c\u4ed6\u8d76\u7d27\u5411\u524d \u4e00\u51b2\uff0c\u60f3\u8981\u63a5\u4f4f\uff0c\u53ef\u662f\u6162\u4e86\u4e00\u6b65\uff0c\u5c0f\u9c7c\u53c8\u5165\u6c34\u4e2d\u3002\u4ed6\u5fc3\u4e2d\u4e0d\u820d\uff0c\u7acb\u523b\u6f5c\u4e0b\u6c34\u9762\uff0c\u770b\u89c1\u5c0f \u9c7c\u5c31\u5728\u524d\u9762\u4e0d\u8fdc\uff0c\u4ed6\u95ed\u4f4f\u6c14\uff0c\u6084\u6084\u5730\u4f38\u624b\u4e00\u6293\uff0c\u90a3\u77e5\u90a3\u91d1\u8272\u5c0f\u9c7c\uff0c\u4fa7\u8eab\u4e00\u95ea\uff0c\u4e0d\u4f46\u4e0d\u9003\uff0c \u53cd\u800c\u8fce\u4e0a\u6765\u4fbf\u662f\u4e00\u53e3\u3002\u4ed6\u5fc3\u60f3\u7ed9\u8fd9\u79cd\u5c0f\u9c7c\u54ac\u4e00\u53e3\u4e5f\u6ca1 \u3000\u3000\u4ec0\u4e48\u8981\u7d27\uff0c\u5f53\u65f6\u53ea\u611f\u5230\u624b\u6307\u5c16\u4e0a\u4e00\u9635\u9ebb\uff0c\u90a3\u6761\u660e\u660e\u5df1\u7ecf\u88ab\u6293\u7d27 \u3000\u3000\u7684\u5c0f\u9c7c\uff0c\u53c8\u4ece\u4ed6\u624b\u4e2d\u6e9c\u8d70\uff0c\u4ed6\u79c9\u6027\u575a\u6bc5\uff0c\u9532\u800c\u4e0d\u820d\uff0c\u51c6\u5907\u6d6e\u51fa\u6c34\u9762\u6362\u4e00\u53e3\u6c14\uff0c\u518d\u6f5c \u4e0a\u53bb\u6293\uff0c\u5f53\u4ed6\u9732\u51fa\u6c34\u9762\u65f6\uff0c\u4ed6\u7acb\u523b\u53d1\u73b0\uff0c\u6574\u4e2a\u53f3\u638c\u90fd\u53d8\u6210\u9ed1\u8272\uff0c\u4e00\u6761\u53f3\u81c2\u5168\u90e8\u9ebb\u6728\u3002\u4ed6 \u77e5\u9053\u4e00\u5b9a\u662f\u65b9\u624d\u90a3\u5c3e\u5c0f\u91d1\u9c7c\u8eab\u4e0a\u6709\u5267\u6bd2\uff0c\u5f53\u65f6\u6025\u5fd9\u4e0a\u5cb8\uff0c\u4e5f\u4e0d\u53ca\u544a\u8bc9\u540c\u4f34\uff0c\u98de\u5954\u56de\u5bb6\uff0c \u8dd1\u5230\u534a\u8def\uff0c\u5934\u6108\u6765\u6108\u660f\uff0c\u4ed6\u54ac\u7740\u7259\uff0c\u62fc\u547d\u652f\u6301\uff0c\u5f53\u4ed6\u8dd1\u5230\u79bb\u5bb6\u95e8\u4e94\u516d\u6b65\u7684\u5730\u65b9\uff0c\u88ab\u5c0f\u77f3 \u4e00\u62cc\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u5927\u558a\u4e00\u58f0\u4fbf\u660f\u5012\u4e86\u3002 \u3000\u3000\u4ed6\u660f\u4e86\u53c8\u9192\uff0c\u9192\u4e86\u53c8\u660f\uff0c\u795e\u5fd7\u59cb\u7ec8\u4e0d\u6e05\uff0c\u76f4\u5230\u7b2c\u4e8c\u5929\u4e0b\u5348\uff0c\u4ed6\u624d\u6e05\u9192\u8fc7\u6765\u3002\u4ed6\u7741\u8d77 \u65e0\u795e\u7684\u773c\u775b\uff0c\u770b\u89c1\u5927\u5a18\u548c\u963f\u5170\u4e24\u53cc\u7ea2\u80bf\u800c\u75b2\u5026\u7684\u773c\u775b\u6b63\u6ce8\u89c6\u7740\u4ed6\uff0c\u8fd8\u6709\u90a3\u4f4d\u6731\u592b\u5b50\u2014\u2014 \u79c1\u587e\u91cc\u7684\u4f5f\u54c4\u5148\u751f\uff0c\u8138\u8272\u51dd\u91cd\u7684\u6c89\u601d\u7740\u3002 \u3000\u3000\u201c\u6c34\u201d\u4ece\u4ed6\u5589\u7ba1\u91cc\u5410\u51fa\u4e00\u4e2a\u5b57\uff0c\u6d51\u8eab\u65e0\u4e00\u4e1d\u529b\u6c14\u3002\u53ea\u89c1\u5927\u5a18\u963f\u5170\u6731\u592b\u5b50\u8138\u4e0a\u90fd\u73b0\u51fa \u4e86\u7b11\u5bb9\uff0c\u963f\u5170\u90a3\u53cc\u5927\u773c\u7a81\u7136\u4e4b\u95f4\u660e\u4eae\u8d77\u6765\uff0c\u51dd\u89c6\u7740\u4ed6\uff0c\u76ee\u5149\u4e2d\u5145\u6ee1\u4e86\u7231\u601c\u3001\u81ea\u4f24\u3002\u4ed6\u5fc3 \u4e2d\u4e00\u9635\u8ff7\u60d1\uff0c\u52aa\u529b\u7741\u5f00\u6c89\u91cd\u7684\u773c\u76ae\uff0c\u4e5f\u51dd\u770b\u7740\u5979\u3002\u5ffd\u7136\uff0c\u963f\u5170\u8138\u8272\u5927\u53d8\uff0c\u4fef\u5012\u5e8a\u65c1\uff0c\u4ed6 \u5fc3\u4e2d\u4e00\u6025\uff0c\u4fbf\u53c8\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u5929\u5929\u7684\u597d\u8d77\u6765\uff0c\u4ed6\u77e5\u9053\u963f\u5170\u4e5f\u75c5\u5012\u4e86\uff0c\u6731\u592b\u5b50\u6bcf\u9694\u4e00\u5929\u4fbf\u6765\u770b\u4ed6\u4eec\u4e00\u6b21\uff0c\u6bcf\u6b21 \u6731\u592b\u5b50\u4ece\u963f\u5170\u5e8a\u65c1\u63a2\u8fc7\u8109\u540e\uff0c\u8138\u8272\u90fd\u5f88\u6c89\u91cd\uff0c\u5927\u5a18\u4e5f\u7ec8\u65e5\u5fe7\u4f24\u6101\u82e6\uff0c\u4ed6\u5fc3\u4e2d\u660e\u767d\u4e00\u5b9a\u662f \u963f\u5170\u75c5\u52bf\u6108\u6765\u6108\u91cd\uff0c\u4f46\u81ea\u5df1\u5168\u8eab\u5982\u8131\u8282\u4e00\u822c\uff0c\u4e00\u52a8\u90fd\u52a8\u4e0d\u4e86\u3002\u4ed6\u5c61\u6b21\u95ee\u5927\u5a18\u963f\u5170\u7684\u75c5\u51b5\uff0c \u5927\u5a18\u90fd\u5b89\u6170\u4ed6\uff0c\u544a\u8bc9\u4ed6\u4e0d\u8981\u7d27\u3002\u6709\u4e00\u5929\uff0c\u4ed6\u534a\u591c\u9192\u6765\uff0c\u542c\u5230\u5927\u5a18\u4e0e\u6731\u592b\u5b50\u5728\u8f7b\u58f0\u8c08\u8bdd\uff0c \u4ed6\u672c\u60f3\u7ffb\u8fc7\u53bb\u518d\u7761\uff0c\u5ffd\u7136\u4ed6\u542c\u5230\u6731\u592b\u5b50\u4ed6\u4eec\u5728\u8c08\u963f\u5170\u7684\u75c5\u52bf\uff0c\u4ed6\u7acb\u523b\u51dd\u795e\u5077\u542c\u3002 \u3000\u3000\u201c\u6211\u77a7\u963f\u5170\u8fd9\u5b69\u5b50\u591a\u534a\u662f\u4e2d\u4e86\u91d1\u86c7\u6bd2\uff0c\u4f46\u662f\u5979\u600e\u4e48\u4f1a\u4e2d\u6bd2\uff0c\u5012\u662f\u4ee4\u4eba\u96be\u89e3\u3002\u201d\u6731\u592b \u5b50\u8bf4\u9053\u3002 \u3000\u3000\u5927\u5a18\u63a5\u53e3\u9053\uff1a\u201c\u5982\u679c\u771f\u662f\u4e2d\u4e86\u86c7\u6bd2\uff0c\u96be\u9053\u9664\u201c\u8840\u679c\u201d\u5916\uff0c\u522b\u65e0\u4ed6\u6cd5\u533b\u6cbb\u5417\uff1f\u201d \u3000\u3000\u6731\u592b\u5b50\u9053\uff1a\u201c\u8fd9\u86c7\u539f\u662f\u5929\u4e0b\u4e09\u6bd2\u4e4b\u4e00\uff0c\u4e2d\u6bd2\u8005\uff0c\u4e0d\u51fa\u516b\u65f6\u8fb0\uff0c\u5168\u8eab\u65f6\u75db\u65f6\u75d2\uff0c\u96be\u8fc7 \u975e\u5e38\uff0c\u4efb\u4f60\u5b9a\u529b\u591a\u5f3a\uff0c\u6700\u540e\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u81ea\u6c42\u4e86\u7ed3\u3002\u800c\u4e14\u6700\u5389\u5bb3\u7684\u662f\u6b64\u6bd2\u975e\u65f7\u4e16\u96be\u9022\u7684 \u2018\u8840\u679c\u2019\u5c06\u5176\u6bd2\u6027\u6258\u4f4f\uff0c\u6cfb\u51fa\u4f53\u5916\uff0c\u5176\u4ed6\u4efb\u4f55\u4ed9\u4e39\u4e5f\u96be\u594f\u6548\u3002\u201d \u3000\u3000\u5927\u5a18\u786c\u54bd\u8bf4\u9053\uff1a\u201c\u4f60\u77a7\u963f\u5170\u8fd8\u6709\u6551\u5417\uff1f\u201d \u3000\u3000\u6731\u592b\u5b50\u957f\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u90a3\u65e5\u6211\u90a3\u5c0f\u534a\u74f6\u8840\u679c\u6c41\uff0c\u5168\u7ed9\u51cc\u98ce\u670d\u4e0b\uff0c\u4e5f\u662f\u89c1\u4ed6\u6bd2\u52bf\u6c89\u91cd\uff0c \u4e00\u65f6\u5fc3\u614c\u610f\u4e71\uff0c\u5176\u5b9e\u8fd9\u79cd\u7075\u836f\u4e13\u514b\u5929\u4e0b\u5404\u79cd\u86c7\u6bd2\uff0c\u53ea\u6d88\u6570\u6ef4\uff0c\u4fbf\u5df2\u8db3\u591f\uff0c\u6211\u77a7\u90a3\u65e5\u963f\u5170 \u53ef\u80fd\u662f\u4e00\u65f6\u60c5\u6025\uff0c\u7528\u53e3\u53bb\u5438\u51cc\u98ce\u624b\u6307\u4e0a\u7684\u4f24\u53e3\uff0c\u540e\u6765\u81ea\u5df1\u77e5\u9053\u4e2d\u6bd2\uff0c\u4f46\u5f3a\u5fcd\u7740\uff0c\u5979\u6015\u8840 \u679c\u6c41\u4e0d\u591f\uff0c\u5982\u679c\u6211\u4eec\u53d1\u89c9\u5979\u4e2d\u6bd2\uff0c\u5206\u4e00\u534a\u7ed9\u5979\u670d\u7528\uff0c\u4e5f\u8bb8\u4f1a\u803d\u8bef\u4e86\u51cc\u98ce\u7684\u75c5\u52bf\uff0c\u5509\uff01\u8fd9 \u5b69\u5b50\u5bf9\u51cc\u98ce\u4e00\u5f80\u60c5\u6df1\uff0c\u7adf\u820d\u547d\u6551\u4ed6\u3002 \u3000\u3000\u6211\u73b0\u5728\u7528\u836f\u5c06\u5979\u6bd2\u52bf\u903c\u4f4f\uff0c\u5e76\u4f7f\u5979\u660f\u7761\uff0c\u4ee5\u514d\u53d7\u5404\u79cd\u75db\u82e6\uff0c\u7b49\u660e\u513f\u5168\u8eab\u6bd2\u6c14\u90fd\u96c6\u4e2d \u5728\u4e00\u8d77\uff0c\u6211\u518d\u7528\u9488\u7099\u523a\u7a74\uff0c\u5c06\u6bd2\u4ece\u4e03\u7a8d\u903c\u51fa\uff0c\u597d\u5728\u5979\u4e2d\u6bd2\u4e0d\u592a\u6df1\uff0c\u4e5f\u8bb8\u6709\u51e0\u5206\u5e0c\u671b\u3002\u53ea \u662f\u2026\u2026\u53ea\u662f\u4e00\u53cc\u773c\u775b\u6050\u6015\u4e0d\u4fdd\u4e86\u3002\u201d \u3000\u3000\u5927\u5a18\u4f4e\u5934\u62bd\u6ce3\u7740\u2026\u2026 \u3000\u3000\u5341\u591a\u5e74\u4e86\uff0c\u90a3\u591c\u6731\u592b\u5b50\u4e0e\u5927\u5a18\u7684\u5bf9\u8bdd\uff0c\u51cc\u98ce\u8fd8\u662f\u4e00\u5b57\u672b\u5fd8\u3002\u957f\u65e5\u51dd\u601d\uff0c\u6df1\u5bb5\u68a6\u56de\uff0c \u4ed6\u6ca1\u6709\u4e00\u523b\u4e0d\u5728\u76d8\u7b97\u7740\u5982\u4f55\u627e\u5bfb\u8840\u679c\u4f7f\u963f\u5170\u590d\u660e\u3002 \u3000\u3000\u5982\u4eca\u81ea\u5df1\u5750\u7684\u8fd9\u68f5\u6811\u4e0d\u6b63\u5c31\u8ddf\u6731\u592b\u5b50\u6240\u8bf4\u8840\u679c\u6811\u4e00\u6837\u5417\uff1f \u3000\u3000\u53ef\u662f\uff0c\u90a3\u767e\u5e74\u4e00\u7ed3\u7684\u8840\u679c\u5462\uff1f \u3000\u3000\u4ed6\u81ea\u60ed\u81ea\u8d23\uff0c\u6012\u5929\u602a\u795e\uff0c\u53e3\u4e2d\u5583\u5583\u5492\u9053\uff1a\u201c\u5434\u51cc\u98ce\uff0c\u5434\u51cc\u98ce\uff0c\u4f60\u8fd9\u81ea\u79c1\u7684\u4e1c\u897f\uff0c\u4e3a \u4e86\u6551\u81ea\u5df1\u7684\u5185\u4f24\uff0c\u7adf\u5fd8\u8bb0\u4e86\u8fd9\u5341\u5e74\u6765\u523b\u5fc3\u94ed\u9aa8\u7684\u5927\u4e8b\uff0c\u4f60\u8fd9\u5351\u9119\u6015\u6b7b\u7684\u5bb6\u4f19\uff0c\u4f60\u8fd9\u5fd8\u6069 \u8d1f\u4e49\u7684\u6df7\u86cb\uff01\u201d\u4ed6\u6108\u9a82\u6108\u662f\u4f24\u5fc3\uff0c\u4e0d\u7531\u653e\u58f0\u75db\u54ed\uff0c\u54ed\u4e86\u4e00\u9635\uff0c\u60b2\u6124\u4e4b\u60c5\u7a0d\u51cf\uff0c\u60f3\u9053\uff1a \u201c\u8001\u5929\u7237\u4e3a\u4ec0\u4e48\u90a3\u4e48\u4e0d\u516c\u5e73\u5462\uff1f\u6211\u81ea\u5e7c\u7236\u6bcd\u53cc\u4ea1\uff0c\u597d\u4e0d\u5bb9\u6613\u9047\u4e0a\u4e00\u4e2a\u5f85\u6211\u5982\u5b50\u7684\u5927\u5a18\uff0c \u53ef\u662f\u6211\u5374\u7d2f\u5f97\u5979\u72ec\u751f\u7231\u5973\u53cc\u76ee\u5931\u660e\uff0c\u6211\u65e5\u591c\u8d39\u5fc3\u5bfb\u6c42\u8840\u679c\uff0c\u53ef\u662f\uff0c\u5374\u8fd9\u6837\u7684\u88ab\u6211\u7cdf\u584c\uff0c \u96be\u9053\u6211\u547d\u8fd0\u662f\u8fd9\u4e48\u4e0d\u7965\uff0c\u51e1\u662f\u5f85\u6211\u597d\u7684\u4eba\u90fd\u8981\u906d\u5230\u707e\u96be\u5417\uff1f\u201d \u3000\u3000\u201c\u6731\u592b\u5b50\u8bf4\u6211\u7236\u4eb2\u4e00\u751f\u4ed7\u4e49\u758f\u8d22\uff0c\u884c\u4fa0\u9664\u5978\uff0c\u53ef\u662f\u5230\u5934\u6765\uff0c\u4f9d\u7136\u4e0d\u514d\u547d\u4e27\u8352\u5c71\uff0c\u5c38 \u9aa8\u65e0\u5b58\uff0c\u8fd9\u96be\u9053\u662f\u6240\u8c13\u2018\u5929\u9053\u65e0\u4eb2 \u3000\u3000\uff0c\u5e38\u4e0e\u5584\u4eba\u2019\u5417\uff1f\u201d \u3000\u3000\u201c\u6211\u6bcd\u4eb2\u2014\u2014\u5927\u5a18\u6700\u4f69\u670d\u7684\u4eba\uff0c\u662f\u5317\u65b9\u6700\u6709\u540d\u7684\u624d\u5973\uff0c\u8bd7\u3001\u6b4c\u3001\u8d4b\u3001\u68cb\u3001\u7434\u3001\u4e66\u3001 \u753b\u3001\u5973\u7ea2\u3001\u70f9\u8c03\uff0c\u65e0\u4e00\u4e0d\u7cbe\uff0c\u5929\u8d44\u654f\u6377\u662f\u76d6\u4e16\u7684\u5929\u624d\uff0c\u53ef\u662f\u5979\uff0c\u5979\u5728\u751f\u4e0b\u6211\u4e4b\u540e\uff0c\u4fbf\u6084 \u6084\u79bb\u5f00\u8fd9\u4e2a\u4e16\u754c\uff0c\u96be\u9053\u4e16\u4e0a\u6108\u6709\u7075\u6027\u7684\u4e1c\u897f\u4fbf\u6108\u4e0d\u957f\u4e45\u5417\uff1f\u201d \u3000\u3000\u201c\u6731\u592b\u5b50\u5728\u6211\u75c5\u597d\u540e\uff0c\u4ed6\u5c31\u544a\u8bc9\u6211\u8eab\u4e16\uff0c\u4ece\u524d\u5927\u5a18\u9a97\u6211\u8bf4\u7236\u6bcd\u53d1\u613f\u5728\u6cf0\u5c71\u91d1\u5149\u5bfa\u4e2d \u82e6\u4fee\u4e8c\u5341\u5e74\uff0c\u6211\u4e00\u76f4\u4fe1\u4ee5\u4e3a\u771f\uff0c\u4e00\u65e6\u542c\u5230\u6731\u592b\u5b50\u8bf4\u6211\u7236\u4eb2\u547d\u4e27\u6b79\u5f92\u4e4b\u6697\u7b97\uff0c\u771f\u662f\u5982\u96f7\u8f70 \u9876\uff0c\u6211\u6e34\u671b\u7740\u518d\u8fc7\u51e0\u5e74\uff0c\u4fbf\u53ef\u770b\u89c1\u7239\u5988\u4eb2\u7231\u7684\u9762\u5bb9\uff0c\u53ef\u662f\u6211\u7684\u5e0c\u671b\u7c89\u788e\u4e86\uff0c\u4ee3\u66ff\u7684\u662f\u590d \u4ec7\u7684\u6012\u706b\u3002\u6731\u592b\u5b50\u662f\u7239\u7684\u5e08\u5144\uff0c\u4ed6\u544a\u77e5\u7239\u7684\u4ec7\u4eba\u662f\u8c01\uff0c\u53ea\u5c3d\u529b\u6559\u6211\u6b66\u827a\uff0c\u4ed6\u5e38\u81ea\u53f9\u5929\u8d44 \u592a\u5dee\uff0c\u5b66\u827a\u4e0d\u7cbe\uff0c\u4e3a\u6050\u803d\u8bef\u6211\u7684\u524d\u9014\uff0c\u4ed6\u53ea\u6559\u6211\u672c\u95e8\u57fa\u672c\u529f\u592b\uff0c\u53ef\u662f\u5927\u5a18\u6709\u4e00\u5929\u7a81\u7136\u62ff \u51fa\u4e86\u4e00\u672c\u518c\u5b50\uff0c\u4ea4\u7ed9\u6731\u592b\u5b50\u3002\u4ed6\u4e00\u770b\u4e4b\u4e0b\uff0c\u5927\u4e3a\u60ca\u5947\uff0c\u4fbf\u6559\u6211\u7167\u7740\u4e66\u4e0a\u6240\u5199\u53bb\u7ec3\uff0c\u4ed6\u81ea \u5df1\u5728\u65c1\u6307\u70b9\uff0c\u4ed6\u8bf4\u90a3\u662f\u6211\u7236\u4eb2\u2014\u2014\u4ed6\u4eec\u4e09\u5e08\u5144\u5f1f\u4e2d\u6b66\u827a\u6700\u9ad8\u5f3a\u7684\uff0c\u4e00\u751f\u6b66\u5b66\u7684\u7ed3\u6676\uff0c\u6211 \u65e5\u591c\u7ec3\u529f\uff0c\u8bfb\u4e66\u6765\u6253\u53d1\u6211\u7684\u65e5\u5b50\u3002\u201d \u3000\u3000\u201c\u6211\u751a\u81f3\u4e0d\u6562\u770b\u963f\u5170\u4e00\u773c\uff0c\u90a3\u526f\u5931\u53bb\u5149\u8f89\u7684\u79c0\u76ee\uff0c\u867d\u7136\u4f9d\u65e7\u662f\u90a3\u4e48\u7f8e\u4e3d\uff0c\u7136\u800c\uff0c\u5728 \u5b83\u540e\u9762\u5374\u662f\u6c38\u6052\u7684\u9ed1\u6697\uff0c\u6211\u53d1\u8a93\uff0c\u53ea\u8981\u963f\u5170\u80fd\u590d\u660e\uff0c\u6211\u4e00\u5207\u90fd\u53ef\u4ee5\u727a\u7272\uff0c\u4e00\u5207\u90fd\u53ef\u4ee5\u629b \u5f03\uff0c\u751a\u81f3\u662f\u6211\u7684\u70ed\u8840\uff0c\u6211\u7684\u5934\u9885\u3002\u201d \u3000\u3000\u201c\u963f\u5170\u6108\u53d8\u6108\u6e29\u67d4\u4e86\uff0c\u5979\u4e0d\u518d\u548c\u6211\u6597\u6c14\uff0c\u53ea\u662f\u6e29\u548c\u5730\u5f00\u5bfc\u6211\uff0c\u529d\u6211\u4e0d\u8981\u5c06\u6b64\u4e8b\u803f\u803f \u4e8e\u6000\uff0c\u5c06\u6765\u603b\u6709\u4e00\u5929\u53ef\u4ee5\u627e\u5230\u7075\u836f\uff0c\u6211\u867d\u77e5\u5e0c\u671b\u6e3a\u832b\uff0c\u53ef\u662f\u4e5f\u6e10\u6e10\u5b89\u5fc3\u4e00\u4e9b\uff0c\u7528\u5fc3\u7ec3 \u6b66\u3002\u201d \u3000\u3000\u90a3\u5929\uff0c\u5f53\u6211\u544a\u522b\u5e08\u7236\uff0c\u53ca\u5927\u5a18\u6bcd\u5973\u65f6\uff0c\u963f\u5170\u7684\u773c\u4e2d\u5145\u6ee1\u6cea\u6c34\uff0c\u5979\u52c9\u5f3a\u4e00\u7b11\u9053\uff1a\u2018\u5927 \u54e5\uff0c\u4f60\u521d\u5165\u6c5f\u6e56\uff0c\u4e00\u5207\u8981\u5c0f\u5fc3\uff0c\u62a5\u7236\u4ec7\u7b2c\u4e00\uff0c\u8840\u679c\u627e\u4e0d\u5230\u4fbf\u7b97\u4e86\u3002\u2019 \u3000\u3000\u201c\u6211\u5f53\u65f6\u51dd\u76ee\u770b\u5979\uff0c\u4e00\u65f6\u5343\u8a00\u4e07\u8bed\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002\u201d \u3000\u3000\u201c\u963f\u5170\uff0c\u6211\u77e5\u9053\uff0c\u4f60\u867d\u770b\u4e0d\u89c1\u6211\uff0c\u53ef\u662f\u4f60\u4e00\u5b9a\u611f\u89c9\u5f97\u5230\u4f60\u5927\u54e5\u4ed6\u60f3\u628a\u5168\u90e8\u7231\u601c\u4ece\u4ed6 \u90a3\u62d9\u7b28\u773c\u5149\u4e2d\u6ce8\u7ed9\u4f60\u3002\u201d \u3000\u3000\u201c\u963f\u5170\u6536\u4e86\u60b2\u5bb9\uff0c\u751c\u751c\u4e00\u7b11\u9053\uff1a\u2018\u597d\u5566\uff01\u5927\u54e5\u4f60\u4e0a\u8def\u5427\uff01\u2019 \u3000\u3000\u8fd9\u4e00\u7b11\uff0c\u5982\u767e\u82b1\u6012\u653e\uff0c\u5a07\u5a9a\u4e07\u72b6\uff0c\u67d4\u60c5\u6b3e\u6b3e\uff0c\u6211\u5f53\u65f6\u770b\u5f97\u75f4\u4e86\uff0c\u4e45\u4e45\u5446\u7acb\u4e0d\u5fcd\u79bb \u53bb\u3002\u201d \u3000\u3000\u201c\u963f\u5170\uff01\u963f\u5170\uff01\u6211\u53d1\u89c9\u4e86\u751f\u547d\u7684\u4ef7\u503c\u5728\u6709\u4e9b\u65f6\u5019\uff0c\u4e5f\u4f1a\u6bd4\u4e0d\u4e0a\u4e00\u4e2a\u6df1\u60c5\u7684\u5fae\u7b11\u54e9\uff01\u201d \u3000\u3000\u201c\u4f60\u8981\u6211\u6b7b\uff0c\u6211\u96be\u9053\u504f\u4f1a\u8bf4\u4e0d\u5417\uff1f\u201d \u3000\u3000\u201c\u5e08\u7236\u4ea4\u7ed9\u6211\u4e00\u679a\u7389\u74f6\uff0c\u4ed6\u518d\u4e09\u53ee\u5631\uff0c\u5018\u82e5\u627e\u5230\u8840\u679c\uff0c\u7acb\u523b\u653e\u5165\u7389\u74f6\u4e2d\uff0c\u8840\u679c\u4fbf\u4f1a \u81ea\u52a8\u5316\u4e3a\u6d46\u6db2\u3002\u201d \u3000\u3000\u201c\u6211\u63d0\u8d77\u4e86\u52c7\u6c14\uff0c\u6000\u7740\u5e0c\u671b\uff0c\u80cc\u8d1f\u7740\u957f\u5251\u53ca\u5c0f\u56ca\uff0c\u9022\u5c71\u8fc7\u5c71\uff0c\u9022\u6c34\u6d89\u6c34\uff0c\u98d8\u6cca\u5728\u540d \u5c71\u5927\u5ddd\u53ca\u8be1\u8bc8\u5343\u7aef\u7684\u6c5f\u6e56\u4e2d\uff0c\u8840\u679c\u6ca1\u5bfb\u627e\uff0c\u7236\u4ec7\u672a\u62a5\u5f97\uff0c\u4f46\u5e78\u8fd0\u7684\u7ed3\u8bc6\u4e86\u4e00\u4f4d\u809d\u80c6\u7167\u4eba \u7684\u5144\u5f1f\u2014\u2014\u8f9b\u6377\u3002\u4e00\u4e2a\u5929\u771f\uff0c\u8c6a\u653e\uff0c\u5014\u5f3a\u7684\u5b69\u5b50\uff0c\u867d\u7136\u4ed6\u6bd4\u81ea\u5df1\u53ea\u5c0f\u4e86\u534a\u5c81\uff0c\u53ef\u662f\u5374\u5b69 \u5b50\u6c14\u5f97\u5f88\u54e9\uff01\u201d \u3000\u3000\u201c\u597d\u4e0d\u5bb9\u6613\uff0c\u5728\u6cf0\u5c71\u5927\u4f1a\u4e0a\uff0c\u770b\u89c1\u4e86\u4ec7\u4eba\uff0c\u90a3\u540d\u91cd\u6b66\u6797\u7684\u4ec7\u4eba\uff0c\u6b63\u8981\u62fc\u547d\u62a5\u4ec7\uff0c\u53ef \u662f\uff0c\u90a3\u53ef\u6068\u7684\u4e11\u516b\u602a\uff0c\u90a3\u75af\u72c2\u7684\u4e11\u516b\u602a\uff0c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\u62b1\u7740\u6211\u4e00\u8d77\u6eda\u4e0b\u60ac\u5d16\u3002\u54fc\uff01\u8fd9\u8be5 \u6b7b\u7684\u4e1c\u897f\uff0c\u73b0\u5728\u53ea\u6015\u5df2\u662f\u7c89\u8eab\u788e\u9aa8\u4e86\u7f62\uff01\u201d \u3000\u3000\u4ed6\u601d\u6f6e\u8d77\u4f0f\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5929\u8272\u5df2\u662f\u5927\u660e\uff0c\u706b\u8f6e\u822c\u7684\u592a\u9633\u5df2\u722c\u4e0a\u4e86\u5c71\u5dc5\uff0c\u5c71\u8170\u56db\u5468\u7684\u6d53 \u96fe\u6162\u6162\u88ab\u84b8\u6563\uff0c\u91d1\u8272\u523a\u76ee\u7684\u9633\u5149\uff0c\u7a7f\u8fc7\u4e91\u96fe\uff0c\u6de1\u6de1\u7684\u6d12\u5e03\u5728\u51cc\u98ce\u4fca\u79c0\u9762\u5b54\u4e0a\uff0c\u53ea\u89c1\u4ed6\u8138 \u8272\u65f6\u800c\u51dd\u91cd\u6c89\u6bc5\uff0c\u65f6\u800c\u6fc0\u52a8\u75db\u82e6\uff0c\u65f6\u800c\u51c4\u51c9\u7f20\u7ef5\uff0c\u65f6\u800c\u5e7d\u7136\u795e\u5f80\uff0c\u6700\u540e\u4ed6\u4e00\u8dc3\u800c\u8d77\uff0c\u4ef0 \u5929\u4e00\u9635\u957f\u5578\uff0c\u8f7b\u76c8\u76c8\u7684\u7acb\u5728\u6811\u5e72\u4e0a\u3002 \u3000\u3000\u539f\u6765\u521a\u624d\u4ed6\u7ecf\u8fc7\u4e00\u573a\u6fc0\u70c8\u7684\u7406\u667a\u4e0e\u611f\u60c5\u7684\u6597\u4e89\uff0c\u5f53\u4ed6\u60f3\u5230\u7075\u836f\u5df2\u5931\uff0c\u963f\u5170\u7edd\u671b\u7684\u795e \u60c5\u65f6\uff0c\u70ed\u8840\u4e0a\u6d8c\uff0c\u51e0\u4e4e\u63a7\u5236\u4e0d\u4f4f\u81ea\u5df1\uff0c\u76f4\u60f3\u6d8c\u8eab\u5411\u4e0b\u4e00\u8df3\uff0c\u53ef\u662f\u5f53\u4ed6\u62ac\u5934\u4e00\u770b\uff0c\u4e91\u96fe\u6e10 \u6e10\u6d88\u878d\uff0c\u7ea2\u65e5\u5149\u8292\u4e07\u9053\uff0c\u7a81\u7136\u5fc3\u4e2d\u82e5\u6709\u6240\u609f\uff0c\u60f3\u9053\uff1a\u201c\u4e91\u96fe\u867d\u6d53\uff0c\u4f46\u662f\u5728\u592a\u9633\u7684\u5149\u832b\u4e0b \u603b\u662f\u4f1a\u6d88\u6563\uff0c\u6211\u547d\u9014\u591a\u96be\u4e0d\u4e5f\u50cf\u6ee1\u5929\u4e4c\u4e91\u6d53\u96fe\u5417\uff1f\u53ef\u662f\u6211\u547d\u8fd0\u4e2d\u7684\u592a\u9633\u662f\u4ec0\u4e48\u5462\uff1f\u554a\uff0c \u662f\u4e86\uff01\u90a3\u662f\u8981\u9760\u6211\u81ea\u5df1\u594b\u6597\uff0c\u6211\u81ea\u5df1\u52aa\u529b\uff0c\u6211\u81ea\u5df1\u6323\u624e\u7684\u52c7\u6c14\uff0c\u90a3\u5c31\u662f\u6211\u751f\u547d\u4e2d\u7684\u592a\u9633 \u554a\uff01\u201d \u3000\u3000\u201c\u5e08\u7236\u5e38\u8bf4\u53e4\u6765\u6210\u5927\u529f\u7acb\u5927\u4e1a\u8005\uff0c\u5f80\u5f80\u90fd\u662f\u2018\u77e5\u5176\u4e0d\u53ef\u800c\u4e3a\u4e4b\u2019\uff0c\u6211\u53d7\u8fd9\u6837\u4e00\u70b9\u632b \u6298\uff0c\u90a3\u53c8\u7b97\u5f97\u4e86\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5929\u8d44\u654f\u609f\u7edd\u4f26\uff0c\u6b64\u65f6\u4e00\u7ecf\u60f3\u901a\uff0c\u518d\u65e0\u7591\u4e49\uff0c\u4ed6\u6027\u5b50\u6c89\u6bc5\uff0c\u4e00\u7ecf\u51b3\u5b9a\uff0c\u5c31\u662f\u5200\u5c71\u67aa \u6797\u5728\u524d\uff0c\u4e5f\u4e0d\u4f1a\u534a\u9014\u800c\u5e9f\u3002 \u3000\u3000\u4ed6\u51dd\u795e\u76d8\u7b97\u4e86\u4e00\u4e0b\uff0c\u81ea\u5fd6\u51ed\u81ea\u5df1\u7684\u529f\u529b\uff0c\u5c31\u7b97\u4e0a\u9762\u6709\u6500\u9644\u7684\u4e1c\u897f\uff0c\u6050\u6015\u4e5f\u96be\u4ee5\u7331\u8eab \u800c\u4e0a\uff0c\u76ee\u524d\u53ea\u597d\u60f3\u6cd5\u8dc3\u4e0a\uff0c\u4ed6\u63d0\u8d77\u4e00\u53e3\u771f\u6c14\uff0c\u89c9\u5f97\u8fd0\u7528\u81ea\u5982\uff0c\u53c8\u4e0d\u653e\u5fc3\u7684\u6325\u52a8\u53f3\u624b\uff0c\u53d1 \u89c9\u75bc\u75db\u5168\u6d88\uff0c\u4ed6\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u5fc3\u4e2d\u660e\u767d\u8fd9\u5fc5\u5b9a\u662f\u8840\u679c\u7684\u6548\u7528\u3002 \u3000\u3000\u4ed6\u60f3\uff1a\u201c\u5148\u4ed4\u7ec6\u770b\u770b\u4e0b\u9762\u5f62\u52bf\u518d\u8bf4\u3002\u201d\u4e8e\u662f\uff0c\u65bd\u5c55\u5012\u6302\u91d1\u5e18\uff0c\u6574\u4e2a\u8eab\u5b50\u5411\u4e0b\uff0c\u4e00\u53cc \u811a\u5374\u7262\u7262\u6302\u5728\u6811\u4e0a\uff0c\u4e0b\u9762\u7684\u96fe\u6c14\u88ab\u65e5\u5149\u84b8\u878d\u4e86\u4e0d\u5c11\uff0c\u51cc\u98ce\u4e00\u76ee\u4e86\u7136\uff0c\u4f30\u8ba1\u8c37\u5e95\u79bb\u6811\u6839\u6781 \u5927\u7ea6\u4e03\u516b\u5341\u4e08\uff0c\u81ea\u5fd6\uff1a\u201c\u5982\u679c\u80fd\u627e\u5230\u4e94\u3001\u516d\u4e2a\u843d\u811a\u4e4b\u5904\uff0c\u5c31\u53ef\u4ee5\u5b89\u5168\u8df3\u4e0b\u3002\u5982\u679c\u53ea\u6709\u4e24 \u4e09\u53ef\u501f\u529b\u5904\uff0c\u4e5f\u53ea\u597d\u5192\u9669\u8dc3\u4e0b\uff0c\u8eab\u4f53\u53ea\u6015\u4f1a\u9707\u4f24\u54e9\uff01\u201d \u3000\u3000\u4ed6\u53cc\u76ee\u6765\u56de\u5de1\u89c6\uff0c\u7ec8\u4e8e\u53d1\u73b0\u4e00\u5757\u7a81\u51fa\u7684\u5c0f\u77f3\uff0c\u5927\u5c0f\u53ea\u5bb9\u5355\u811a\uff0c\u8ddd\u79bb\u7acb\u8eab\u4e4b\u5904\u53ea\u6015\u6709 \u5341\u51e0\u4e08\uff0c\u4ed6\u9ed8\u9ed8\u7977\u9053\uff1a\u201c\u8001\u5929\u4fdd\u4f51\u90a3\u5757\u77f3\u5934\u4e0d\u8981\u662f\u6d6e\u77f3\u624d\u597d\u3002\u201d \u3000\u3000\u4ed6\u5c06\u5168\u8eab\u52b2\u529b\u8fd0\u4e8e\u53f3\u624b\uff0c\u4ed6\u60f3\u8fd0\u7528\u91d1\u521a\u6307\uff0c\u627f\u62c5\u4e00\u90e8\u5206\u4e0b\u5760\u4e4b\u529b\uff0c\u4ed6\u51dd\u795e\u805a\u6c14\uff0c\u7eb5 \u8eab\u4e00\u8df3\uff0c\u75be\u5982\u6d41\u661f\uff0c\u53f3\u624b\u4e94\u6307\u4f7f\u529b\uff0c\u6293\u5411\u5d16\u58c1\uff0c\u90a3\u5c16\u903e\u91d1\u77f3\u7684\u5d16\u77f3\uff0c\u7adf\u4e5f\u88ab\u4ed6\u6293\u51fa\u4e94\u6761 \u4e0d\u6d45\u7684\u6307\u75d5\uff0c\u5f53\u4ed6\u8ddd\u79bb\u90a3\u5757\u767e\u5934\u8fdc\u6709\u4e09\u56db\u4e08\u65f6\uff0c\u4ed6\u5728\u7a7a\u4e2d\u770b\u51c6\u76ee\u6807\uff0c\u53cc\u817f\u4e00\u7f29\uff0c\u7ffb\u4e86\u4e00 \u4e2a\u7b4b\u6597\uff0c\u4ee5\u7f13\u4e0b\u5760\u4e4b\u52bf\uff0c\u7136\u540e\u8f7b\u98d8\u98d8\u5355\u811a\u70b9\u77f3\uff0c\u5f85\u4ed6\u611f\u89c9\u5230\u90a3\u5757\u77f3\u5934\u975e\u5e38\u7262\u56fa\uff0c\u624d\u5c06\u91cd \u5fc3\u4e0b\u653e\uff0c\u65bd\u5c55\u201c\u91d1\u9e21\u72ec\u7acb\u201d\u7a33\u4f4f\u8eab\u4f53\u3002 \u3000\u3000\u51cc\u98ce\u6362\u4e86\u53e3\u6c14\uff0c\u518d\u5f80\u4e0b\u770b\uff0c\u53ea\u89c1\u4e91\u96fe\u66f4\u8584\uff0c\u666f\u7269\u6e05\u6670\u975e\u5e38\uff0c\u6700\u5947\u602a\u7684\u662f\uff0c\u6bcf\u9694\u5341\u51e0 \u4e08\u5c31\u6709\u4e00\u5757\u5927\u5c0f\u4e00\u822c\u7684\u7a81\u51fa\u5c0f\u767e\uff0c\u597d\u50cf\u662f\u4eba\u5de5\u9020\u7684\u4e00\u6837\uff0c\u51cc\u98ce\u6697\u60f3\uff1a\u201c\u4ece\u4e0a\u4e0b\u8dc3\uff0c\u6bcf\u9694 \u5341\u591a\u4e08\u4e00\u5757\u5c0f\u77f3\u8fdc\u53ef\u52c9\u5f3a\u4ee5\u4f9b\u8eab\u4f53\u501f\u529b\uff0c\u53ef\u662f\u5982\u679c\u4ece\u4e0a\u4e0b\u7a9c\uff0c\u8fd9\u5341\u591a\u4e08\u8ddd\u79bb\u5374\u975e\u5c0f\u53ef\uff0c \u8fd9\u77f3\u5757\u5206\u660e\u662f\u4eba\u4e3a\u7684\uff0c\u5929\u4e0b\u96be\u9053\u6709\u5982\u6b64\u9ad8\u624b\uff1f\u201d \u3000\u3000\u4ed6\u6025\u4e8e\u8131\u9669\uff0c\u65e0\u6687\u591a\u60f3\uff0c\u5f53\u65f6\u5982\u6cd5\u6ce1\u5236\uff0c\u8fde\u7eed\u51e0\u8dc3\uff0c\u5df2\u5230\u8c37\u5e95\uff0c\u53ea\u89c1\u904d\u5730\u602a\u77f3\u78f7\u78f7\uff0c \u5730\u5f62\u6781\u4e3a\u5d0e\u9a71\uff0c\u4e09\u9762\u5168\u662f\u9ad8\u5cf0\uff0c\u53ea\u6709\u5357\u9762\u662f\u4e00\u4e2a\u7f3a\u53e3\uff0c\u4ed6\u65bd\u5c55\u8f7b\u529f\uff0c\u5954\u4e86\u8fc7\u53bb\uff0c\u53d1\u73b0\u4e00 \u6761\u5f2f\u66f2\u7684\u7f8a\u80a0\u5c0f\u9053\uff0c\u6cbf\u7740\u5c0f\u8def\u5f2f\u5f2f\u66f2\u66f2\u8f6c\u4e86\u51e0\u4e2a\u5f2f\uff0c\u5730\u52bf\u7a81\u7136\u5f00\u6717\uff0c\u524d\u9762\u662f\u4e00\u5927\u7247\u7fe0\u7eff \u7684\u7af9\u6797\u3002 \u3000\u3000\u4ed6\u6b63\u5728\u8003\u8651\u8981\u4e0d\u8981\u7a7f\u8fc7\u7af9\u6797\uff0c\u5ffd\u7136\u542c\u5230\u4e00\u9635\u6717\u6717\u7684\u8bfb\u4e66\u58f0\uff0c\u51cc\u98ce\u51dd\u795e\u542c\u53bb\uff0c\u539f\u6765\u662f \u5728\u6717\u8bfb\u5357\u534e\u7ecf\uff0c\u8bed\u58f0\u94ff\u9535\uff0c\u5982\u91d1\u77f3\u76f8\u51fb\uff0c\u65ad\u53e5\u5706\u6da6\uff0c\u5982\u73e0\u843d\u7389\u76d8\u3002\u51cc\u98ce\u4e0d\u7531\u542c\u5446\u4e86\uff0c\u6697 \u5fd6\uff1a\u201c\u6b64\u4eba\u53d1\u97f3\u867d\u5c0f\uff0c\u5374\u662f\u6e05\u8d8a\u5df2\u6781\uff0c\u8bed\u97f3\u7a7f\u8fc7\u98ce\u58f0\u7c41\u7c41\u7684\u7af9\u6797\uff0c\u4e0d\u4f46\u4e0d\u88ab\u5439\u6563\uff0c\u542c\u8d77 \u6765\u53cd\u6709\u5982\u5c31\u5728\u9762\u524d\uff0c\u5fc5\u6709\u7edd\u9876\u5185\u529f\u3002\u201d \u3000\u3000\u4ed6\u597d\u5947\u7684\u95ea\u5165\u7af9\u6797\uff0c\u5faa\u97f3\u800c\u53bb\uff0c\u8f6c\u4e86\u534a\u5929\uff0c\u58f0\u97f3\u6108\u6765\u6108\u8fdc\uff0c\u524d\u9762\u6b67\u8def\u8d8a\u6765\u8d8a\u591a\uff0c\u4ed6 \u4e0d\u7981\u609a\u7136\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u83ab\u975e\u662f\u9677\u5165\u4ec0\u4e48\u9635\u54e9\uff01\u201d\u5b9a\u4e0b\u795e\u6765\u4e86\u4ed4\u7ec6\u89c2\u671b\uff0c\u6bcf\u68f5\u7af9\u6811\u4f3c\u4e4e \u90fd\u662f\u4e00\u822c\u8ddd\u79bb\uff0c\u6bcf\u516b\u679d\u7af9\u5360\u4f4f\u516b\u4e2a\u65b9\u4f4d\uff0c\u56f4\u6210\u516b\u5366\u5f62\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u6015\u5c31\u662f\u5e08\u7236\u5e38\u8bf4\u7684\u516b \u5366\u9635\u4e86\uff0c\u6b64\u9635\u539f\u4e3a\u6b66\u5019\u6240\u521b\uff0c\u7edd\u4f20\u5df2\u4e45\uff0c\u96be\u9053\u5929\u4e0b\u7adf\u6709\u4eba\u8bc6\u5f97\uff1f\u201d\u8f6c\u5ff5\u53c8\u60f3\u9053\uff1a\u201c\u8fd9\u5fc5 \u4e3a\u6b64\u95f4\u4e3b\u4eba\u4e3a\u9632\u5916\u654c\u6240\u5e03\uff0c\u5982\u679c\u4e3b\u4eba\u6028\u6211\u5984\u5165\u7af9\u9635\uff0c\u4efb\u6211\u56f0\u5728\u9635\u4e2d\u4e0d\u52a0\u6307\u70b9\uff0c\u53ea\u6015\u4e0d\u6613 \u95ef\u51fa\u4e86\u3002\u201d \u3000\u3000\u4ed6\u60f3\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u7075\u673a\u4e00\u52a8\uff0c\u8eab\u5b50\u4e00\u5c48\uff0c\u4e00\u4e2a\u201c\u4e00\u9e64\u51b2\u5929\u201d\uff0c\u62d4\u4e86\u8d77\u6765\uff0c\u4ed6\u539f\u60f3\u7eb5 \u4e0a\u4e8c\u3001\u4e09\u4e08\uff0c\u518d\u7528\u53cc\u624b\u6293\u7740\u7af9\u6746\uff0c\u6500\u7331\u800c\u4e0a\uff0c\u90a3\u60f3\u5230\u4e00\u62d4\u4e4b\u4e0b\uff0c\u8eab\u4f53\u731b\u5347\u81f3\u4e94\u4e08\u5de6\u53f3\uff0c \u5df1\u7ecf\u63a5\u8fd1\u5c16\u68a2\uff0c\u4ed6\u5fc3\u4e2d\u5927\u4e3a\u60ca\u5947\uff0c\u4e5f\u4e0d\u6687\u7ec6\u60f3\uff0c\u53f3\u624b\u5728\u7af9\u652f\u4e0a\u4e00\u501f\u529b\uff0c\u8eab\u4f53\u518d\u4e0a\u5347\u4e09\u3001 \u56db\u5c3a\uff0c\u53cc\u811a\u7ad9\u5728\u5c16\u7aef\u4e0a\u3002 \u3000\u3000\u4ed6\u4e3e\u76ee\u4e00\u770b\uff0c\u5468\u56f4\u6570\u767e\u65b9\u4e08\u5168\u662f\u9ad8\u77ee\u4e00\u6837\u7684\u7af9\u5b50\uff0c\u7af9\u6797\u7684\u5c3d\u5934\u662f\u4e00\u7247\u7fe0\u7eff\u7684\u8349\u5730\uff0c \u8349\u5730\u4e2d\u592e\uff0c\u6709\u4e00\u5757\u5982\u5e73\u53f0\u822c\u7684\u5927\u77f3\uff0c\u90a3\u5757\u5927\u77f3\u901a\u4f53\u96ea\u767d\uff0c\u5149\u6ed1\u65e0\u6bd4\uff0c\u4e0a\u9762\u653e\u7740\u4e00\u672c\u4e66\uff0c \u4e00\u652f\u7389\u8427\u3002 \u3000\u3000\u51cc\u98ce\u5fc3\u60f3\uff1a\u201c\u521a\u624d\u8bfb\u4e66\u7684\u9ad8\u4eba\uff0c\u79bb\u6211\u7acb\u8eab\u4e4b\u5904\u4e0d\u8fc7\u4e8c\u4e09\u5341\u4e08\uff0c\u53ef\u662f\u6211\u5728\u7af9\u6797\u4e2d\u7a7f\u6765 \u7a7f\u53bb\uff0c\u4e5f\u4e0d\u77e5\u8dd1\u4e86\u5341\u51e0\u91cc\uff0c\u7adf\u7136\u8d70\u4e0d\u51fa\u8fd9\u767e\u5341\u6839\u7af9\u9635\uff0c\u770b\u6765\u8fd9\u9635\u6cd5\u975e\u5e38\u5389\u5bb3\uff0c\u5982\u679c\u6211\u4ece \u7af9\u5c16\u4e0a\u8dc3\u8fc7\u53bb\uff0c\u53ea\u6d88 \u3000\u3000\u51e0\u7a9c\uff0c\u4fbf\u53ef\u51b2\u51fa\u3002\u201d \u3000\u3000\u4f46\u662f\u4ed6\u518d\u4ed4\u7ec6\u4e00\u770b\uff0c\u5fc3\u4e2d\u6697\u6697\u53eb\u82e6\uff0c\u539f\u6765\u6bcf\u652f\u7af9\u5b50\u4e0e\u90bb\u8fd1\u7af9\u5b50\u90fd\u76f8\u9694\u4e03\u3001\u516b\u4e08\uff0c\u51cc \u98ce\u81ea\u4fe1\u53ef\u8dc3\u56db\u3001\u4e94\u4e08\uff0c\u8fd9\u6837\u662f\u4ed6\u521a\u624d\u4e0a\u7eb5\u65f6\uff0c\u529f\u529b\u5927\u589e\u7ed9\u4ed6\u7684\u4fe1\u5fc3\uff0c\u53ef\u662f\u8981\u60f3\u4ece\u8f6f\u8f6f\u7684 \u7af9\u5c16\u9876\u4e00\u8df3\u4e03\u516b\u4e08\uff0c\u90a3\u662f\u4e07\u4e07\u4e0d\u53ef\u80fd\uff0c\u4ed6\u6b63\u5728\u6c89\u541f\u8bbe\u6cd5\uff0c\u7a81\u7136\u8eab\u540e\u4e00\u4e2a\u82cd\u52b2\u6e29\u548c\u7684\u58f0\u97f3\uff1a \u201c\u50bb\u5b69\u5b50\uff0c\u8d76\u5feb\u4e0b\u6765\uff0c\u968f\u6211\u8d70\u3002\u201d \u3000\u3000\u51cc\u98ce\u56de\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u8eab\u540e\u4e00\u4e08\u5916\u7ad9\u7740\u4e00\u4e2a\u6e05\u5947\u8001\u8005\uff0c\u4e00\u8eab\u4e66\u751f\u6253\u4efd\uff0c\u6ee1\u8eab\u4e66\u5377\u6c14\u606f\uff0c \u51cc\u98ce\u53ea\u770b\u4e86\u4e00\u773c\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5fc3\u4e2d\u5bf9\u8fd9\u8001\u8005\u7adf\u662f\u5341\u5206\u4f9d\u604b\uff0c\u5341\u5206\u4fe1\u4efb\uff0c\u4e5f\u4e0d\u7ba1\u4ed6\u6709\u65e0\u6076 \u610f\uff0c\u4f9d\u8a00\u8df3\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u90a3\u8001\u8005\u89c1\u4ed6\u4ece\u4e94\u4e08\u7af9\u5c16\u843d\u4e0b\u6765\uff0c\u8f7b\u98d8\u98d8\u7684\u6ca1\u6709\u4e00\u4e1d\u58f0\u97f3\uff0c\u4e0d\u89c9\u6697\u6697\u70b9\u4e86\u70b9\u5934\uff0c\u6ee1\u8138 \u7b11\u5bb9\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u529f\u592b\u4e0d\u9519\u5440\uff01\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u4e3a\u4ec0\u4e48\u4e00\u4e2a\u4eba\u8dd1\u5230\u8fd9\u91cc\u6765\u5440\uff1f\u201d \u3000\u3000\u51cc\u98ce\u4ed4\u7ec6\u6253\u91cf\u90a3\u8001\u8005\uff0c\u53ea\u89c1\u4ed6\u65b9\u989d\u633a\u9f3b\uff0c\u867d\u7136\u4e24\u9b03\u82b1\u767d\uff0c\u53ef\u662f\u8138\u4e0a\u7ec6\u76ae\u5ae9\u8089\uff0c\u5374\u8fd8 \u663e\u5f97\u51fa\u4ed6\u5e74\u9752\u65f6\u7684\u82f1\u4fca\u4e0d\u7fa4\u3002\u51cc\u98ce\u6108\u770b\u6108\u662f\u656c\u7231\uff0c\u5fc3\u4e2d\u4e0d\u60f3\u9a97\u4ed6\uff0c\u606d\u8eab\u7b54\u9053\uff1a\u201c\u5f1f\u5b50\u59d3 \u5434\u540d\u51cc\u98ce\uff0c\u662f\u795e\u533b\u4fa0\u6731\u656c\u6587\u5f92\u5f1f\u3002\u201d \u3000\u3000\u8001\u8005\u5403\u4e86\u4e00\u60ca\u9053\uff1a\u201c\u6731\u656c\u6587\u662f\u4f60\u5e08\u7236\uff1f\u8fd9\u5b69\u5b50\u4e00\u5fc3\u7cbe\u7814\u533b\u9053\uff0c\u529f\u592b\u5374\u4e0d\u9ad8\u660e\uff0c\u4f60\u521a \u4e0d\u8868\u6f14\u90a3\u624b\u2018\u5e73\u6c99\u843d\u96c1\u2019\uff0c\u4f60\u5e08\u7236\u4e5f\u6ca1\u90a3\u4e48\u7f8e\u5999\u5440\uff01\u201d \u3000\u3000\u51cc\u98ce\u5fc3\u60f3\uff1a\u201c\u5e08\u7236\u5e74\u7eaa\u548c\u4ed6\u4e5f\u5dee\u4e0d\u591a\uff0c\u4ed6\u600e\u4e48\u558a\u5e08\u7236\u5b69\u5b50\u5462\uff1f\u201d\u4ed6\u542c\u5230\u8001\u4eba\u8d5e\u4ed6\uff0c \u5fc3\u4e2d\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u8baa\u8baa\u7b54\u9053\uff1a\u201c\u5f1f\u5b50\u529f\u592b\u662f\u4f9d\u7740\u5148\u7236\u6240\u9057\u7559\u4e0b\u7684\u8457\u4f5c\u7ec3\u6210\u7684\uff0c\u5e08\u7236\u53ea \u5728\u65c1\u6307\u70b9\uff0c\u5f1f\u5b50\u4ece\u672a\u89c1\u5e08\u7236\u65bd\u6b66\u529f\u3002\u201d \u3000\u3000\u8001\u4eba\u6c89\u541f\u4e00\u4f1a\u5947\u9053\uff1a\u201c\u4f60\u7239\u7239\u600e\u4f1a\u77e5\u9053\u672c\u95e8\u529f\u592b\u5462\uff1f\u554a\uff01\u4f60\u59d3\u5434\uff0c\u4f60\u7239\u53ef\u662f\u5434\u6cbc \u4e91\uff1f\u201d \u3000\u3000\u51cc\u98ce\u51c4\u7136\u70b9\u5934\u3002 \u3000\u3000\u201c\u4ed6\uff01\u4ed6\u600e\u4e48\u4f1a\u6b7b\u53bb\u5462\uff1f\u201d \u3000\u3000\u201c\u5bb6\u7236\u56e0\u540d\u671b\u592a\u9ad8\uff0c\u53d7\u6b66\u6797\u4e00\u822c\u5c0f\u4eba\u5992\u6068\uff0c\u88ab\u5d06\u5cd2\u638c\u95e8\u5389\u9e57\uff0c\u6b66\u5f53\u6d3e\u7d2b\u9633\u9053\u4eba\uff0c\u5ce8 \u5a9a\u82e6\u5eb5\u4e0a\u4eba\uff0c\u70b9\u82cd\u9ad8\u624b\u8c22\u661f\u8054\u624b\u6697\u7b97\uff0c\u547d\u4e27\u8352\u5c71\u3002\u201d\u51cc\u98ce\u60b2\u6124\u9053\uff0c\u4ed6\u73b0\u5728\u5df2\u4e0d\u5c06\u6606\u4ed1\u5353 \u5927\u4fa0\u89c6\u4e3a\u4ec7\u4eba\u4e86\u3002 \u3000\u3000\u8001\u4eba\u8138\u4e0a\u4e00\u9635\u6fc0\u6124\u9053\uff1a\u201c\u597d\uff0c\u5389\u9e57\u8fd9\u5c0f\u5b50\uff0c\u4ed6\u5e08\u7236\u4e34\u7ec8\u65f6\u8fd8\u6258\u6211\u7167\u987e\u4ed6\uff0c\u54fc\uff0c\u6211\u4e09 \u5341\u5e74\u4e0d\u51fa\u6c5f\u6e56\uff0c\u8fd9\u5c0f\u5b50\u7adf\u6562\u6740\u5bb3\u6211\u5e08\u4f84\uff0c\u8fd9\u7b14\u5e10\u5012\u8981\u7b97\u6e05\u695a\uff0c\u54fc\uff0c\u4e5f\u987e\u4e0d\u5f97\u4ed6\u5e08\u7236\u6e05\u865a \u5b50\u7684\u4ea4\u60c5\u5566\u3002\u201d \u3000\u3000\u51cc\u98ce\u521a\u624d\u542c\u8fd9\u8001\u8005\u7684\u53e3\u6c14\uff0c\u5fc3\u4e2d\u5df2\u9690\u7136\u660e\u767d\u8fd9\u8001\u4e66\u751f\u5fc5\u662f\u672c\u95e8\u4e2d\u8001\u524d\u8f88\uff0c\u6b64\u65f6\u542c\u4ed6 \u5982\u6b64\u4e00\u8bf4\uff0c\u5fc3\u4e2d\u66f4\u65e0\u7591\u4e49\uff0c\u5bfb\u601d\uff1a\u201c\u6731\u5e08\u7236\u5e38\u8bf4\uff0c\u592a\u6781\u95e8\u4f20\u5230\u4ed6\u81ea\u5df1\u5e08\u7236\u4e00\u4ee3\uff0c\u95e8\u6237\u5927 \u5149\uff0c\u51fa\u4e86\u4e24\u4e2a\u76d6\u4e16\u5947\u624d\uff0c\u5c31\u662f\u7239\u7684\u5e08\u7236\u548c\u5e08\u53d4\uff0c\u4e24\u4eba\u4e0d\u4f46\u6b66\u529f\u7edd\u9ad8\uff0c\u533b\u672f\u4e4b\u5999\uff0c\u76f4\u53ef\u5ab2 \u7f8e\u534e\u4f57\uff0c\u773c\u524d\u6b64\u4eba\u53ea\u6015\u5c31\u662f\u4e1c\u5cb3\u4e66\u751f\u4e91\u51b0\u82e5\u54e9\uff01\u201d\u5f53\u4e0b\u7ffb\u8eab\u4e0b\u8dea\uff0c\u53e9\u4e86\u4e24\u4e2a\u5934\u9053\uff1a\u201c\u98ce \u513f\u7ed9\u5e08\u53d4\u7956\u53e9\u5934\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u54c8\u54c8\u5927\u7b11\uff0c\u53cc\u624b\u4e00\u6325\uff0c\u51cc\u98ce\u53ea\u89c9\u4e00\u80a1\u5927\u529b\u4e00\u6258\uff0c\u4e0d\u7531\u81ea\u4e3b\u7684\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u600e\u4e48\u77e5\u6211\u662f\u4f60\u5fc3\u4e2d\u6240\u60f3\u7684\u4eba\uff1f\u201d \u3000\u3000\u51cc\u98ce\u7b54\u9053\uff1a\u201c\u521a\u624d\u5f1f\u5b50\u542c\u5e08\u53d4\u7956\u8bdd\u4e2d\uff0c\u660e\u660e\u662f\u672c\u95e8\u4e00\u4f4d\u8001\u524d\u8f88\uff0c\u60a8\u8001\u4eba\u5bb6\u6253\u626e\u4e0e\u5e08 \u7236\u6240\u8bf4\u53c8\u662f\u4e00\u6837\uff0c\u6240\u4ee5\u5f1f\u5b50\u624d\u6562\u80af\u5b9a\u3002\u201d \u3000\u3000\u8001\u4eba\u5fae\u7b11\u8d5e\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u771f\u806a\u660e\uff0c\u4f60\u957f\u5f97\u53ef\u4e0d\u50cf\u4f60\u7239\u54e9\uff01\u201d \u3000\u3000\u51cc\u98ce\u4e00\u751f\u4e0b\u6765\uff0c\u6bcd\u4eb2\u4fbf\u6492\u624b\u800c\u53bb\uff0c\u4e09\u5c81\u65f6\uff0c\u7236\u4eb2\u4e00\u53bb\u4e0d\u8fd4\uff0c\u4ed6\u8111\u6d77\u4e2d\u6839\u672c\u6ca1\u6709\u6bcd\u4eb2 \u7684\u5370\u8c61\uff0c\u7236\u4eb2\u97f3\u5bb9\u989c\u8c8c\u53ea\u662f\u4e00\u4e2a\u6a21\u7cca\u7684\u5f71\u5b50\uff0c\u8fd9\u662f\u4ed6\u4e00\u751f\u7684\u5927\u6068\u4e8b\uff0c\u6b64\u65f6\u8001\u4eba\u65e0\u610f\u63d0\u5230\uff0c \u51cc\u98ce\u5fc3\u60c5\u5927\u5927\u6fc0\u52a8\uff0c\u795e\u8272\u51c4\u7136\u6b32\u6ce3\u3002 \u3000\u3000\u8001\u4eba\u53d1\u89c9\u51cc\u98ce\u795e\u8272\u4e0d\u5bf9\uff0c\u5fc3\u77e5\u89e6\u52a8\u4ed6\u4f24\u5fc3\u4e4b\u4e8b\uff0c\u5fc3\u4e2d\u751a\u662f\u6b49\u7136\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u597d\u5b69\u5b50 \u522b\u4f24\u5fc3\uff0c\u7237\u7237\u6559\u4f60\u4e00\u5957\u529f\u592b\uff0c\u628a\u8fd9\u6279\u5978\u8d3c\u5168\u5bb0\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u8fd9\u51e0\u65e5\u6765\u5fc3\u4e2d\u53d7\u5c3d\u714e\u71ac\uff0c\u6b64\u65f6\u542c\u5230\u6148\u7965\u53ef\u7231\u7684\u8001\u4eba\uff0c\u4eb2\u5207\u7684\u5b89\u6170\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d \u4f4f\uff0c\u6251\u5230\u8001\u4eba\u6000\u4e2d\uff0c\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u4e1c\u5cb3\u4e66\u751f\u4e91\u51b0\u82e5\u8fd9\u5345\u5e74\u6765\u6ca1\u6709\u8e0f\u51fa\u6cf0\u5c71\u4e00\u6b65\uff0c\u7ec8\u65e5\u53ea\u4e0e\u6e05\u98ce\u4e3a\u4f34\uff0c\u660e\u6708\u4e3a\u53cb\uff0c\u6b64\u65f6 \u6000\u4e2d\u62b1\u7740\u4e00\u4e2a\u4fca\u79c0\u7684\u9752\u5e74\uff0c\u5fc3\u4e2d\u6108\u60f3\u6108\u7231\uff0c\u53e3\u4e2d\u53c8\u53cd\u590d\u5730\u8bf4\u9053\uff1a\u201c\u597d\u5b69\u5b50\u522b\u54ed\uff0c\u4e56\u5b69\u5b50 \u522b\u54ed\uff0c\u7237\u7237\u66ff\u4f60\u62a5\u4ec7\u5566\uff01\u201d \u3000\u3000\u51cc\u98ce\u54ed\u4e86\u4e00\u4f1a\uff0c\u7528\u53cc\u8896\u64e6\u4e86\u64e6\u773c\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u77a7\u98ce\u513f\u6b66\u529f\u53ef\u4e0d\u53ef\u4ee5\u7ec3\u5230\u2026\u2026\u7ec3\u5230 \u4e0e\u6211\u7239\u4e00\u6837\uff1f\u201d \u3000\u3000\u4ed6\u60f3\u5230\u8f9b\u6377\u90a3\u65e5\u5728\u6cf0\u5c71\u5927\u4f1a\u5a01\u98ce\u51db\u51db\uff0c\u539f\u60f3\u95ee\u53ef\u4e0d\u53ef\u4ee5\u7ec3\u5f97\u548c\u8f9b\u6377\u4e00\u6837\uff0c\u53ef\u662f\u8f6c\u5ff5 \u4e00\u60f3\uff1a\u201c\u7237\u7237\u53ef\u4e0d\u8ba4\u5f97\u8f9b\u6377\u5440\uff01\u201d \u3000\u3000\u4e1c\u5cb3\u4e66\u751f\u5b9e\u5728\u7231\u51cc\u98ce\u6781\u4e86\uff0c\u4e0d\u52a0\u601d\u7d22\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u6210\u95ee\u9898\uff0c\u4e0d\u6210\u95ee\u9898\u3002\u4f60\u600e\u4e48\u4f1a\u8dd1 \u5230\u8fd9\u6765\u5440\uff1f\u201d \u3000\u3000\u51cc\u98ce\u5f53\u65f6\u628a\u4ed6\u5982\u4f55\u53c2\u52a0\u6cf0\u5c71\u5927\u4f1a\uff0c\u5982\u4f55\u5760\u5d16\uff0c\u5982\u4f55\u5f97\u6551\uff0c\u5982\u4f55\u8bef\u98df\u8840\u679c\uff0c\u4e00\u4e00\u8bf4\u4e86 \u51fa\u6765\uff0c\u4ed6\u5929\u8d44\u654f\u6377\uff0c\u63aa\u8f9e\u5f97\u4f53\uff0c\u5f62\u5bb9\u5f97\u6709\u58f0\u6709\u8272\uff0c\u8001\u4eba\u772f\u7740\u773c\uff0c\u6d25\u6d25\u6709\u5473\u7684\u542c\u7740\uff0c\u5f53\u4ed6 \u542c\u5230\u51cc\u98ce\u5de7\u98df\u8840\u679c\uff0c\u8138\u4e0a\u795e\u8272\u5fae\u53d8\uff0c\u4f46\u968f\u5373\u6062\u590d\u7b11\u5bb9\u3002 \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u5b69\u5b50\uff0c\u4f60\u798f\u7f18\u771f\u662f\u4e0d\u5c0f\uff0c\u8fd9\u68f5\u8840\u679c\u6811\u662f\u767e\u5e74\u524d\u4e00\u4f4d\u9f99\u524d\u8f88\u8d39\u5c3d\u5fc3\u8840\u57f9\u517b \u51fa\u6765\u7684\uff0c\u6b64\u4eba\u5929\u6027\u9177\u7231\u82b1\u8349\uff0c\u4ed6\u77e5\u6b64\u6811\u5343\u5e74\u4e00\u7ed3\u5b9e\uff0c\u81ea\u5df1\u5bff\u6570\u6709\u9650\uff0c\u539f\u672c\u4e0d\u5b58\u4e13\u4e3a\u5df1\u6709 \u4e4b\u610f\uff0c\u53ea\u662f\u70ab\u8000\u81ea\u5df1\u683d\u82b1\u690d\u6811\u7684\u672c\u4e8b\u800c\u5df2\u3002\u6211\u9053\u8fd9\u6811\u8fd8\u8981\u534a\u6708\u624d\u7ed3\u679c\uff0c\u90a3\u65f6\u518d\u6765\u5b88\u62a4\uff0c \u60f3\u4e0d\u5230\u4f1a\u63d0\u524d\u5341\u6765\u5929\uff0c\u53ea\u6015\u662f\u6b64\u6811\u5438\u6536\u4f60\u7eaf\u9633\u4e4b\u6c14\uff0c\u63d0\u65e9\u6210\u719f\u54e9\uff01\u79cd\u690d\u6b64\u6811\u7684\u524d\u8f88\uff0c\u539f \u662f\u6211\u592a\u6781\u95e8\u4e2d\u6b7b\u5bf9\u5934\uff0c\u4ed6\u5927\u6982\u518d\u4e5f\u6599\u4e0d\u5230\u81ea\u5df1\u8f9b\u8f9b\u82e6\u82e6\u57f9\u80b2\u7684\u4ed9\u679c\uff0c\u7adf\u88ab\u592a\u6781\u95e8\u4e00\u4e2a\u5c0f \u5f92\u5b59\u4e0d\u77e5\u4e0d\u89c9\u7684\u4eab\u7528\u4e86\uff0c\u54c8\u54c8\uff01\u201d \u3000\u3000\u4ed6\u56de\u5934\u4e00\u770b\uff0c\u51cc\u98ce\u6ee1\u8138\u51c4\u60f6\u61ca\u4e27\u540e\u6094\u4e4b\u8272\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5b69\u5b50\u5fc3\u5730\u539a\u9053\uff0c\u670d\u98df\u6b64\u79cd\u5929 \u5730\u7075\u6c14\u6240\u79cd\u7684\u4ed9\u679c\uff0c\u539f\u662f\u5929\u4e0b\u6b66\u5b66\u517b\u6c14\u4e4b\u5929\uff0c\u68a6\u5bdd\u6240\u6c42\u7684\u4e8b\uff0c\u4ed6\u5de7\u98df\u6b64\u679c\uff0c\u4e0d\u4f46\u6beb\u65e0\u559c \u8272\uff0c\u7adf\u540e\u6094\u4e0d\u8be5\u53d6\u98df\uff0c\u4f7f\u6211\u7a7a\u624b\u65e0\u83b7\u3002\u201d \u3000\u3000\u4ed6\u7231\u6781\u51cc\u98ce\uff0c\u5904\u5904\u5411\u597d\u5730\u65b9\u60f3\uff0c\u5176\u5b9e\u51cc\u98ce\u4e00\u65b9\u9762\u56fa\u7136\u662f\u5fc3\u5185\u60ed\u6127\u5403\u4e86\u5e08\u7956\u5b88\u5019\u7684\u7075 \u679c\uff0c\u4e3b\u8981\u8fd8\u662f\u60f3\u5230\u7075\u836f\u518d\u96be\u6c42\u5f97\uff0c\u963f\u5170\u53cc\u76ee\u590d\u660e\uff0c\u5e0c\u671b\u975e\u5e38\u6e3a\u832b\u54e9\uff01 \u3000\u3000\u8001\u4eba\u5fae\u7b11\u9053\uff1a\u201c\u6211\u539f\u5728\u65e0\u610f\u4e2d\u53d1\u89c9\u6b64\u6811\uff0c\u5e76\u975e\u6709\u610f\u5b88\u5f85\uff0c\u4f60\u4e5f\u7528\u4e0d\u7740\u4e0d\u5b89\u3002\u201d \u3000\u3000\u51cc\u98ce\u5fc3\u5185\u8baa\u8baa\uff0c\u4ed6\u4ece\u4e0d\u6492\u8c0e\uff0c\u626d\u6029\u7b54\u9053\uff1a\u201c\u98ce\u513f\u60f3\u5230\u53e6\u5916\u4e00\u4ef6\u4e8b\uff0c\u5fc3\u4e2d\u5f88\u662f\u61ca\u6094\u3002\u201d \u3000\u3000\u51cc\u98ce\u62ac\u5934\u4e00\u770b\uff0c\u8001\u4eba\u8bc1\u6ce8\u89c6\u7740\u4ed6\uff0c\u8138\u4e0a\u5145\u6ee1\u6025\u5207\u6b32\u77e5\u4e4b\u60c5\uff0c\u5f53\u4e0b\u4fbf\u628a\u963f\u5170\u53cc\u76ee\u5931\u660e \u7684\u7ecf\u8fc7\uff0c\u4ece\u5934\u5230\u5c3e\u7684\u8bf4\u4e86\u4e00\u904d\uff0c\u5f53\u4ed6\u8bb2\u5230\u81ea\u5df1\u65e0\u610f\u670d\u98df\u8840\u679c\uff0c\u5e0c\u671b\u6bc1\u706d\u65f6\uff0c\u4e0d\u7981\u53c8\u662f\u51c4 \u7136\u6b32\u6ce3\u3002 \u3000\u3000\u8001\u4eba\u5f88\u662f\u611f\u52a8\uff0c\u6c89\u601d\u4e86\u4e00\u4f1a\u9053\uff1a\u201c\u76ee\u4e0b\u6211\u4e5f\u60f3\u4e0d\u5230\u4ec0\u4e48\u597d\u6cd5\u5b50\uff0c\u91d1\u86c7\u4e4b\u6bd2\u786e\u662f\u975e\u540c \u5c0f\u53ef\uff0c\u563f\uff0c\u4f60\u77a7\u6211\u771f\u8001\u7cca\u6d82\u5566\uff01\u5728\u8fd9\u7af9\u6797\u4e2d\u4f60\u8017\u4e86\u540d\u534a\u5929\uff0c\u6765\uff0c\u968f\u6211\u5230\u6211\u4f4f\u7684\u5c71\u6d1e\u53bb\u3002\u201d \u3000\u3000\u51cc\u98ce\u8ddf\u5728\u8001\u4eba\u8eab\u540e\uff0c\u5de6\u7a7f\u53f3\u8f6c\u51e0\u4e0b\u5c31\u8d70\u51fa\u7af9\u9635\uff0c\u5fc3\u4e2d\u9ed8\u9ed8\u8bb0\u7740\u8d70\u8fc7\u7684\u8def\u5f84\uff0c\u4e24\u4eba\u8d70 \u5230\u90a3\u5757\u5de8\u767e\u65c1\uff0c\u8001\u8005\u6307\u5411\u90a3\u77f3\u540e\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u6211\u5c45\u4f4f\u4e09\u5341\u5e74\u7684\u5c71\u6d1e\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u7ed5\u8fc7\u90a3\u5757\u9ad8\u8fbe\u4e8c\u4e08\u7684\u5927\u77f3\uff0c\u53ea\u89c1\u4e00\u4e2a\u5706\u5706\u7684\u6d1e\u77f3\uff0c\u5149\u7ebf\u751a\u662f\u660f\u6697\uff0c\u4e8c\u4eba\u8d70\u8fdb\u5c71 \u6d1e\uff0c\u51cc\u98ce\u89c9\u5f97\u5730\u4e0b\u751a\u662f\u5e72\u71e5\uff0c\u5168\u662f\u767d\u8272\u5ca9\u77f3\uff0c\u6d1e\u4e2d\u9648\u8bbe\u7b80\u5355\uff0c\u4e00\u5f20\u77f3\u5e8a\uff0c\u51e0\u5f20\u77f3\u6905\u3002\u51cc \u98ce\u60f3\u9053\uff1a\u201c\u5728\u8fd9\u5f27\u5bc2\u7684\u5c71\u8c37\uff0c\u5728\u8fd9\u6697\u6de1\u7684\u5c71\u6d1e\uff0c\u5ea6\u8fc7\u4e86\u4e09\u5341\u5e74\u6f2b\u6f2b\u7684\u5149\u9634\uff0c\u4e91\u7237\u7237\u4e3a\u4ec0 \u4e48\u8981\u8fd9\u6837\u6298\u78e8\u81ea\u5df1\u5462\uff1f\u201d \u3000\u3000\u8001\u4eba\u9053\uff1a\u201c\u98ce\u513f\uff0c\u4f60\u4e00\u65e5\u4e00\u591c\u6ca1\u4f11\u606f\uff0c\u5148\u5230\u5e8a\u4e0a\u53bb\u7761\u4e00\u89c9\u518d\u8bf4\uff0c\u5f85\u4f1a\u9192\u6765\u5982\u679c\u997f\u4e86\uff0c \u5c31\u4ece\u6b64\u6d1e\u5411\u524d\u8d70\uff0c\u4e00\u76f4\u901a\u5230\u540e\u5c71\u8170\uff0c\u90a3\u513f\u904d\u5c71\u904d\u91ce\u5168\u662f\u9c9c\u67a3\u3002\u7237\u7237\u4e5f\u8981\u53bb\u7ec3\u7ec3\u529f\u5566\u3002\u201d \u3000\u3000\u51cc\u98ce\u6b64\u65f6\u5fc3\u60c5\u4e00\u677e\uff0c\u7acb\u523b\u611f\u5230\u6709\u4e9b\u75b2\u5026\uff0c\u5f53\u4e0b\u4f9d\u8a00\u53bb\u7761\u3002 \u3000\u3000\u51cc\u98ce\u4e00\u89c9\u9192\u6765\uff0c\u5df2\u662f\u664c\u5348\u65f6\u5206\uff0c\u4ed6\u4e00\u8dc3\u4e0b\u5e8a\uff0c\u8d70\u51fa\u6d1e\u53e3\uff0c\u53ea\u89c1\u4e91\u7237\u7237\u6b63\u5750\u5728\u5927\u77f3\u4e0a \u4ef0\u671b\u5929\u8fb9\u7684\u767d\u4e91\uff0c\u795e\u6001\u975e\u5e38\u60a0\u626c\uff0c\u4ed6\u4e0d\u6562\u60ca\u6270\uff0c\u60f3\u9053\uff1a\u201c\u6211\u4f55\u4e0d\u5230\u540e\u5c71\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u4ed6\u53c8\u8dd1\u8fdb\u5c71\u6d1e\uff0c\u5411\u524d\u8d70\u4e86\u4e00\u4f1a\uff0c\u6e10\u6e10\u5f00\u6717\u8d77\u6765\uff0c\u8f6c\u4e00\u4e2a\u5f2f\uff0c\u7a81\u7136\u5149\u7ebf\u5927\u660e\uff0c\u539f\u6765\u5df2 \u5230\u5c3d\u5934\uff0c\u51cc\u98ce\u63a2\u5934\u4e00\u770b\uff0c\u539f\u6765\u5916\u9762\u662f\u659c\u5761\u5730\u52bf\uff0c\u9752\u4e1b\u4e1b\u7684\u957f\u6ee1\u4e86\u67a3\u5b50\u6811\uff0c\u6bcf\u68f5\u6811\u4e0a\u6302\u6ee1 \u4e86\u7ea2\u6f84\u6f84\u7684\u67a3\u513f\uff0c\u6709\u7684\u7adf\u548c\u62f3\u5934\u5dee\u4e0d\u591a\u5927\u5c0f\u3002\u51cc\u98ce\u5927\u4e3a\u60ca\u8bb6\uff0c\u4ece\u659c\u5761\u8d70\u4e86\u4e0b\u53bb\uff0c\u53ea\u89c1\u5761 \u5ea6\u6108\u6765\u6108\u662f\u503e\u659c\uff0c\u6700\u540e\u8d70\u5230\u8fb9\u4e0a\uff0c\u7adf\u53c8\u662f\u9661\u76f4\u60ac\u5d16\uff0c\u4ed6\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u5df2\u7ecf\u5230\u4e86\u5c71 \u811a\u5e95\uff0c\u5374\u4e0d\u77e5\u8fd9\u4e2a\u8c37\u5e95\u539f\u6765\u8fd8\u662f\u53ea\u5728\u5c71\u8170\u4e2d\uff0c\u4e5f\u4e0d\u77e5\u662f\u54ea\u5e74\uff0c\u9e1f\u513f\u542b\u7740\u7684\u67a3\u5b50\u6838\u6389\u5728\u8fd9 \u5761\u4e0a\uff0c\u7ec8\u4e8e\u7e41\u6b96\u6210\u6797\u3002\u201d\u4ed6\u68c0\u7740\u5927\u7684\u67a3\u5b50\uff0c\u6765\u4e86\u6ee1\u6ee1\u4e24\u6367\uff0c\u5954\u56de\u5c71\u6d1e\u3002 \u3000\u3000\u7a81\u7136\u4e00\u9635\u5a49\u8f6c\u7684\u8427\u58f0\u98d8\u4e86\u8d77\u6765\uff0c\u51cc\u98ce\u51dd\u795e\u542c\u4e86\u4e00\u4e0b\uff0c\u4f46\u89c9\u8427\u58f0\u51c4\u51c9\uff0c\u4f3c\u4e4e\u5929\u4e0b\u4e0d\u5982 \u610f\u7684\u4e8b\u60c5\u90fd\u4e00\u9f50\u4e34\u5934\uff0c\u51cc\u98ce\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u8db3\u4e0b\u7528\u52b2\uff0c\u7a9c\u4e0a\u5927\u77f3\uff0c\u4f38\u624b\u62b1\u4e91\u7237\u7237\u8bf4\u9053\uff1a \u201c\u4e91\u7237\u7237\uff0c\u522b\u5439\u5566\u3002\u201d\u4ed6\u624b\u4e2d\u539f\u6293\u6ee1\u9c9c\u67a3\uff0c\u6b64\u65f6\u4e24\u624b\u4e00\u677e\uff0c\u5168\u90e8\u843d\u5728\u5927\u77f3\u4e0a\u3002 \u3000\u3000\u4e91\u7237\u7237\u54c8\u54c8\u4e00\u58f0\u5927\u7b11\uff0c\u79fb\u5f00\u53e3\u8fb9\u7389\u8427\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u597d\u597d\u597d\uff0c\u7237\u7237\u4e0d\u5439\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u5439\u5f97\u597d\u751f\u51c4\u82e6\uff0c\u4f60\u5fc3\u4e2d\u60b2\u54c0\uff0c\u8bf4\u7ed9\u98ce\u513f\u542c\u597d\u4e48\uff1f\u201d \u3000\u3000\u4e91\u7237\u7237\u6478\u7740\u51cc\u98ce\u7684\u5934\u7b11\u9053\uff1a\u201c\u7237\u7237\u54ea\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u4f60\u53ef\u522b\u778e\u731c\uff0c\u6765\uff01\u54b1\u4eec\u4e00\u9f50\u6765\u7ec3 \u529f\u5427\uff01\u201d \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u6ee1\u8138\u7b11\u5bb9\uff0c\u53ef\u662f\u773c\u89d2\u4e0a\u5374\u662f\u6f6e\u6da6\u672a\u5e72\uff0c\u60f3\u5230\u4ed6\u4e00\u4e2a\u4eba\u5b64\u5b64\u5355\u5355\uff0c\u540c\u60c5\u4e4b\u5fc3 \u6cb9\u7136\u800c\u751f\uff0c\u8bf4\u9053\uff1a\u201c\u7237\u7237\uff0c\u5f85\u98ce\u513f\u529e\u5b8c\u4e8b\u4e86\uff0c\u4fbf\u6765\u8fd9\u513f\u966a\u4f60\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u6253\u8da3\u9053\uff1a\u201c\u90a3\u4f60\u7684\u5c0f\u5ab3\u5987\u513f\u5462\uff1f\u201d \u3000\u3000\u51cc\u98ce\u5ff8\u6029\u9053\uff1a\u201c\u5979\u2026\u5979\u4e5f\u4e00\u8d77\u6765\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u9053\uff1a\u201c\u90a3\u8fd9\u513f\u53ef\u70ed\u95f9\u5566\uff01\u54c8\u54c8\u3002\u201d \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c \u5341 \u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c \u5341 \u7ae0 \u3000\u3000\u4e91\u7237\u7237\u968f\u53c8\u6b63\u8272\u9053\uff1a\u201c\u672c\u95e8\u6b66\u529f\uff0c\u6700\u91cd\u609f\u6027\uff0c\u4f60\u5929\u8d44\u806a\u654f\uff0c\u90a3\u662f\u4e00\u5b9a\u80fd\u5b66\u597d\u7684\uff0c\u4f60 \u53c8\u5de7\u98df\u8840\u679c\uff0c\u5185\u529b\u5927\u589e\uff0c\u7ec3\u8d77\u529f\u6765\u5b9a\u53ef\u4e8b\u534a\u529f\u500d\u3002\u6211\u73b0\u5728\u4ee5\u672c\u95e8\u4e0a\u4e58\u6b66\u529f\u4f20\u4f60\uff0c\u4f60\u53ef\u8981 \u7b54\u5e94\u6211\u51b3\u4e0d\u7528\u6211\u4f20\u7684\u529f\u592b\u6ee5\u6740\u4e00\u4eba\u3002\u201d \u3000\u3000\u51cc\u98ce\u8083\u7136\u9053\uff1a\u201c\u5f1f\u5b50\u51b3\u4e0d\u6562\u8fdd\u80cc\u7237\u7237\u7684\u8bdd\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u9053\uff1a\u201c\u5f53\u5e74\u4f60\u7239\u7239\u51fa\u9053\u65f6\uff0c\u6211\u5e08\u5144\u56e0\u4ed6\u529f\u529b\u4e0d\u8db3\uff0c\u76f8\u7ea6\u5341\u5e74\u4e4b\u540e\u518d\u4f20\u4ed6\u592a\u6781 \u9547\u95e8\u4e4b\u5b9d\u2018\u5f00\u5c71\u4e09\u5f0f\u7834\u7389\u62f3\u2019\uff0c\u4e0d\u610f\u5e08\u5144\u5728\u4f60\u7239\u79bb\u5f00\u5e08\u95e8\u4e94\u5e74\u540e\uff0c\u7adf\u7136\u6492\u624b\u5f52\u5929\uff0c\u540e\u6765 \u6211\u4e5f\u9690\u5c45\u6b64\u5904\uff0c\u6240\u4ee5\u4f60\u7239\u7239\u59cb\u7ec8\u6ca1\u6709\u5b66\u5230\uff0c\u5f53\u5e74\u4f60\u7239\u7239\u5982\u679c\u5b66\u4e86\u8fd9\u5957\u62f3\u6cd5\uff0c\u867d\u4e0d\u89c1\u5f97\u80fd \u7a33\u80dc\u5389\u9e57\u90a3\u6279\u81ed\u5c0f\u5b50\uff0c\u81ea\u4fdd\u5374\u662f\u6709\u4f59\uff0c\u5509\uff01\u6211\u4eca\u65e5\u4f20\u7ed9\u4f60\u5427\u3002\u201d \u3000\u3000\u4ed6\u63a5\u7740\u53c8\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4e00\u822c\u4eba\u90fd\u4ee5\u4e3a\u592a\u6781\u95e8\u6b66\u529f\u662f\u8bb2\u7a76\u2018\u4ee5\u9759\u5236\u52a8\u2019\uff0c\u6b8a\u4e0d\u77e5\u672c\u95e8 \u6700\u5389\u5bb3\u7684\u529f\u592b\uff0c\u662f\u4e00\u5957\u521a\u731b\u7edd\u4f26\u7684\u62f3\u6cd5\uff0c\u98ce\u513f\uff0c\u4f60\u77a7\u4ed4\u7ec6\u4e86\u3002\u201d \u3000\u3000\u4e1c\u5cb3\u4e66\u751f\u4e91\u51b0\u82e5\u5f53\u4e0b\u5c31\u5728\u5927\u77f3\u4e0a\u4e00\u62db\u4e00\u5f0f\u6f14\u4e86\u8d77\u6765\uff0c\u4ed6\u8fd9\u5957\u7834\u7389\u62f3\u539f\u662f\u8d70\u521a\u731b\u8def\u5b50\uff0c \u51cc\u98ce\u76ee\u4e0d\u8f6c\u6674\u7684\u6ce8\u610f\u7740\uff0c\u53ea\u89c1\u4e91\u7237\u7237\u653b\u52bf\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u62f3\u98ce\u864e\u864e\uff0c\u51cc\u98ce\u867d\u7ad9 \u5728\u4e94\u516d\u5c3a\u5916\uff0c\u4e5f\u89c9\u4e00\u80a1\u5f88\u5927\u7684\u538b\u529b\uff0c\u51e0\u4e4e\u4f7f\u4ed6\u7acb\u8eab\u4e0d\u4f4f\uff0c\u4e1c\u5cb3\u4e66\u751f\u65bd\u5230\u7b2c\u516b\u62db\u65f6\u559d\u9053\uff1a \u3000\u3000\u201c\u98ce\u513f\uff0c\u4f60\u77a7\u6211\u8eab\u6cd5\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u52bf\u5b50\u7a81\u7136\u53d8\u7f13\uff0c\u5de6\u624b\u9022\u62db\u62c6\u62db\uff0c\u53d8\u4e3a\u5b88\u5fa1\u4e4b\u52bf\uff0c\u53f3\u624b\u659c\u5288\u51fa\u53bb\uff0c\u8eab\u5b50\u8de8\u524d\u4e00 \u6b65\uff0c\u53f3\u624b\u500f\u7684\u6536\u56de\uff0c\u5e73\u80f8\u63a8\u51fa\uff0c\u63a8\u4e86\u4e00\u534a\uff0c\u5ffd\u7136\u5411\u53f3\u5212\u4e86\u534a\u4e2a\u5708\u5b50\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u5408 \u529b\u731b\u7136\u5411\u524d\u63a8\u53bb\uff0c\u53ea\u542c\u89c1\u7830\u7684\u4e00\u58f0\uff0c\u4e00\u4e08\u65b9\u5916\uff0c\u4e00\u68f5\u7897\u53e3\u7af9\u5b50\uff0c\u8fde\u6839\u62d4\u8d77\u3002 \u3000\u3000\u51cc\u98ce\u89c1\u4e91\u7237\u7237\u65bd\u5c55\u201c\u5f00\u5c71\u4e09\u5f0f\u7834\u7389\u62f3\u201d\uff0c\u795e\u5a01\u51db\u51db\uff0c\u4e0d\u89c9\u5fc3\u795e\u4ff1\u9189\uff0c\u5fc3\u60f3\uff1a\u201c\u5373\u4f7f \u9047\u5230\u4e09\u56db\u9ad8\u624b\u56f4\u653b\uff0c\u6211\u53ea\u8981\u65bd\u5c55\u90a3\u6700\u540e\u4e09\u5f0f\uff0c\u5fc5\u7136\u65e0\u575a\u4e0d\u6467\uff0c\u51b2\u51fa\u4e00\u6761\u8840\u8def\uff0c\u90a3\u662f\u4e0d\u6210 \u95ee\u9898\u4e86\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u6536\u62db\u9053\uff1a\u201c\u8fd9\u62f3\u6cd5\u6700\u662f\u7b80\u5355\uff0c\u90a3\u6700\u540e\u5f00\u5c71\u4e09\u5f0f\uff0c\u2018\u5bfc\u6d41\u5e73\u5c71\u2019\u2018\u611a\u516c\u79fb\u5c71\u2019 \u2018\u516d\u4e01\u5f00\u5c71\u2019\uff0c\u662f\u8fde\u73af\u52bf\u5b50\uff0c\u529b\u9053\u8d8a\u6765\u8d8a\u662f\u5a01\u731b\uff0c\u5f85\u5230\u5de6\u53f3\u53cc\u638c\u5408\u529b\u5e73\u63a8\uff0c\u5f53\u4eca\u5929\u4e0b\u80fd \u786c\u63a5\u8fd9\u62db\u7684\u53ea\u6015\u6ca1\u6709\u51e0\u4eba\u4e86\uff0c\u54c8\u54c8\u3002\u201d \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u6ee1\u8138\u81ea\u8d1f\u4e4b\u8272\u3002\u521a\u624d\u7acb\u8db3\u4e4b\u5904\uff0c\u73b0\u51fa\u4e24\u4e2a\u6de1\u6de1\u7684\u811a\u5370\uff0c\u4e0d\u89c9\u9a87\u7136\uff0c\u5fc3\u4e2d\u5bf9 \u4e91\u7237\u7237\u7684\u6210\u5c31\uff0c\u4e5f\u6b23\u559c\u5f97\u5f88\u3002 \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u4e91\u7237\u7237\uff0c\u98ce\u513f\u7ec3\u4e00\u904d\u7ed9\u4f60\u770b\u3002\u201d \u3000\u3000\u51cc\u98ce\u609f\u6027\u539f\u9ad8\uff0c\u800c\u8fd9\u5957\u62f3\u6cd5\u62db\u5f0f\u53c8\u662f\u7b80\u5355\u5f97\u7d27\uff0c\u867d\u662f\u53ea\u770b\u4e86\u4e00\u904d\uff0c\u4e00\u62db\u4e00\u5f0f\u5374\u80fd\u4e1d \u6beb\u4e0d\u5dee\u7684\u65bd\u51fa\u6765\u3002 \u3000\u3000\u4e91\u7237\u7237\u4e50\u5f97\u5475\u5475\u7b11\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u771f\u96be\u4e3a\u4f60\u4e86\u3002\u6211\u53bb\u51c6\u5907\u4e00\u4e9b\u5403\u7684\u3002\u201d \u3000\u3000\u51cc\u98ce\u5fd9\u9053\uff1a\u201c\u8ba9\u98ce\u513f\u53bb\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u9053\uff1a\u201c\u597d\u597d\u7ec3\u4e60\u5427\uff0c\u90a3\u5f00\u5c71\u4e09\u5f0f\u529b\u9053\u8fd0\u7528\u6700\u662f\u5de7\u5999\uff0c\u4f60\u591a\u7ec3\u51e0\u904d\uff0c\u81ea\u5df1\u4f53\u4f1a \u4f53\u4f1a\u5427\uff01\u201d \u3000\u3000\u51cc\u98ce\u5fc3\u5185\u611f\u6fc0\uff0c\u4e13\u5fc3\u4e00\u81f4\u7684\u53c8\u91cd\u5934\u7ec3\u8d77\uff0c\u8fd9\u79cd\u786c\u62fc\u786c\u7684\u62f3\u6cd5\uff0c\u539f\u662f\u6781\u8017\u771f\u529b\uff0c\u51cc\u98ce \u7ec3\u4e86\u5341\u4f59\u904d\uff0c\u7cbe\u795e\u5374\u6108\u6765\u6108\u662f\u65fa\u76db\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u8840\u679c\u786e\u662f\u5929\u4e0b\u81f3\u5b9d\uff0c\u6211\u5728\u4e00\u65e5\u4e00\u591c\u95f4\u529f \u529b\u7adf\u7cbe\u8fdb\u5982\u6b64\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u5de6\u624b\u4e2d\u62ff\u7740\u4e00\u6587\u8721\u9e7f\u817f\uff0c\u53f3\u624b\u63d0\u7740\u4e00\u74f6\u67a3\u5b50\u9152\uff0c\u8f7b\u6b65\u8d70\u51fa\u5c71\u6d1e\uff0c\u53ea\u89c1\u51cc\u98ce\u8eab \u5f62\u7a33\u82e5\u6cf0\u5c71\uff0c\u51fa\u62f3\u5982\u98ce\uff0c\u59ff\u6001\u6781\u662f\u7f8e\u5999\uff0c\u5206\u660e\u662f\u4e00\u4e2a\u5185\u5bb6\u9ad8\u624b\u6a21\u6837\uff0c\u53ef\u662f\u62ac\u5934\u4e00\u770b\uff0c\u90a3 \u5f20\u4fca\u8138\u5374\u53c8\u900f\u51fa\u7a1a\u6c14\u7684\u795e\u6c14\uff0c\u5fc3\u5185\u6697\u6697\u60f3\u9053\uff1a\u201c\u8fd9\u771f\u662f\u4e00\u652f\u6b66\u6797\u5947\u8469\uff0c\u90a3\u963f\u5170\u53ea\u6015\u4e5f\u662f \u4e07\u5206\u60f9\u4eba\u601c\u7231\u54e9\uff01\u201d \u3000\u3000\u4ed6\u7231\u5c4b\u53ca\u4e4c\uff0c\u5fc3\u4e0b\u5bf9\u963f\u5170\u7adf\u4e5f\u5341\u5206\u5173\u5fc3\u7231\u62a4\u3002 \u3000\u3000\u4e91\u7237\u7237\u4e00\u8dc3\u4e0a\u4e86\u5927\u77f3\uff0c\u51cc\u98ce\u8f6c\u8eab\u76f8\u8fce\uff0c\u4e8c\u4eba\u5750\u5728\u77f3\u4e0a\uff0c\u8fb9\u5403\u8fb9\u8c08\uff0c\u6781\u4e3a\u878d\u6d3d\u3002 \u3000\u3000\u4e91\u7237\u7237\u5ffd\u9053\uff1a\u201c\u6211\u77a7\u4f60\u4f53\u6001\u8f7b\u76c8\uff0c\u6781\u662f\u9002\u5408\u7ec3\u8f7b\u529f\u3002\u4ece\u524d\u6211\u5728\u6c5f\u6e56\u4e0a\u8d70\u52a8\u65f6\uff0c\u6709\u4e00 \u6b21\u5076\u800c\u6551\u4e86\u4e00\u4e2a\u897f\u85cf\u50e7\u4eba\uff0c\u5f53\u6211\u51fb\u9000\u4e09\u4e2a\u56f4\u653b\u4ed6\u7684\u9ad8\u624b\uff0c\u56de\u9996\u6765\u770b\u65f6\uff0c\u90a3\u5bc6\u5b97\u50e7\u4eba\u5374\u5df2 \u56e0\u4f24\u52bf\u6c89\u91cd\u5944\u5944\u4e00\u606f\u3002\u4ed6\u5f88\u611f\u6fc0\u6211\uff0c\u77a7\u6211\u4e0d\u50cf\u574f\u4eba\uff0c\u4fbf\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u672c\u68b5\u6587\u79d8\u7c4d\u9001\u6211\uff0c \u5f53\u4ed6\u82e6\u6491\u7740\u544a\u8bc9\u6211\uff0c\u8fd9\u672c\u79d8\u85c9\u8f7d\u7740\u4fee\u7ec3\u4e00\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u8f7b\u529f\u7684\u65b9\u6cd5\uff0c\u539f\u662f\u4ed6\u5e08\u95e8\u81f3\u5b9d\u65f6 \u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u7791\u76ee\u6b7b\u53bb\u3002\u6211\u8d77\u521d\u4e5f\u4e0d\u5728\u610f\uff0c\u81ea\u4ed8\u5929\u4e0b\u5404\u6d3e\u8f7b\u8eab\u529f\u592b\u90fd\u662f\u5927\u540c\u5c0f\u5f02\uff0c\u540e \u6765\u9690\u5c45\u6b64\u5730\uff0c\u53d1\u73b0\u843d\u811a\u501f\u529b\u7684\u5c0f\u77f3\uff0c\u6bcf\u4e00\u4e2a\u9694\u4e86\u5341\u51e0\u4e08\u5de6\u53f3\uff0c\u5fc3\u60f3\uff0c\u4efb\u662f\u76d6\u4e16\u8f7b\u529f\uff0c\u4e00 \u7eb5\u5411\u4e0a\u4e4b\u52bf\uff0c\u81f3\u591a\u4e0d\u8fc7\u4e03\u516b\u4e08\uff0c\u53ef\u662f\u8fd9\u4e9b\u5c0f\u77f3\uff0c\u660e\u660e\u662f\u524d\u8f88\u7ec3\u8f7b\u529f\u6240\u7f6e\uff0c\u8fd9\u79cd\u4e00\u8dc3\u5341\u51e0 \u4e08\u7684\u8f7b\u529f\uff0c\u53ea\u6015\u662f\u53e6\u5916\u4e00\u79cd\u529f\u592b\u54e9\uff01\u6211\u53c8\u8f6c\u5ff5\u60f3\u5230\u90a3\u5bc6\u5b97\u50e7\u4eba\u7684\u5bc6\u7b08\uff0c\u5f53\u4e0b\u82e6\u5fc3\u7cbe\u7814\uff0c \u82e6\u4e8e\u4e0d\u8bc6\u68b5\u6587\uff0c\u77a7\u6765\u77a7\u53bb\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\u9053\u7406\u3002\u4f60\u5929\u8d44\u806a\u660e\uff0c\u5de7\u98df\u8840\u679c\uff0c\u5f85\u4f1a\u6211\u628a\u5bc6\u7b08\u8d60 \u4f60\uff0c\u8bf4\u4e0d\u5b9a\u4f60\u80fd\u609f\u51fa\u5176\u4e2d\u9053\u7406\uff0c\u7ec3\u6210\u8fd9\u8d85\u4e16\u7edd\u4fd7\u7684\u529f\u592b\u54e9\uff01\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u7237\u7237\u5f85\u6211\u771f\u597d\uff0c\u6211\u4e5f\u4e0d\u77e5\u8981\u600e\u6837\u62a5\u7b54\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u7b11\u9053\uff1a\u201c\u62a5\u7b54\u5417\uff1f\u90a3\u4e5f\u4e0d\u5fc5\uff0c\u53ea\u8981\u4f60\u5c0f\u5ab3\u5987\u513f\u70e7\u4e24\u6837\u83dc\u7ed9\u6211\u5c1d\u5c1d\u3002\u201d \u3000\u3000\u6562\u60c5\u51cc\u98ce\u5728\u4e91\u7237\u7237\u9762\u524d\u5938\u8fc7\u963f\u5170\u6bcd\u5973\u70f9\u8c03\u624b\u827a\u5929\u4e0b\u65e0\u53cc\u54e9\uff01 \u3000\u3000\u4e24\u4eba\u5c31\u8fd9\u6837\u5728\u8c37\u5e95\u4e00\u6559\u4e00\u5b66\u7cbe\u7814\u6b66\u529f\uff0c\u9ad8\u660e\u5e08\u7236\u78b0\u4e0a\u4e56\u5f92\u5f1f\uff0c\u8d8a\u6559\u5174\u8da3\u8d8a\u662f\u6d53\u539a\uff0c \u4e91\u7237\u7237\u628a\u81ea\u5df1\u51e0\u79cd\u4e0a\u4e58\u529f\u592b\u90fd\u503e\u56ca\u4f20\u6388\uff0c\u51cc\u98ce\u5374\u4e5f\u80fd\u5168\u90e8\u63a5\u53d7\u3002 \u3000\u3000\u4e00\u5929\u665a\u996d\u8fc7\u540e\uff0c\u51cc\u98ce\u5750\u5728\u77f3\u4e0a\u8c03\u606f\u5df1\u6bd5\uff0c\u5fc3\u5185\u4e00\u5c18\u4e0d\u67d3\uff0c\u7075\u53f0\u4e4b\u95f4\u6781\u662f\u6e05\u51c0\uff0c\u4ed6\u62ac \u5934\u4e00\u770b\uff0c\u5929\u8fb9\u4e00\u8f6e\u6ee1\u6708\uff0c\u60f3\u9053\uff1a\u201c\u6cf0\u5c71\u5927\u4f1a\u5230\u4eca\u5929\uff0c\u53ea\u6015\u5feb\u4e00\u4e2a\u6708\u4e86\uff0c\u65e5\u5b50\u8fc7\u5f97\u597d\u5feb \u5440\uff01\u201d \u3000\u3000\u51c9\u98ce\u8f7b\u62c2\u8fc7\u4ed6\u7684\u4fca\u8138\uff0c\u4ed6\u7ad9\u8d77\u6765\u4e00\u632f\u8863\u895f\uff0c\u4f4e\u5934\u770b\u770b\u81ea\u5df1\u4e00\u8eab\u65b9\u5dfe\u5112\u670d\uff0c\u4e0d\u7531\u6697\u6697 \u597d\u7b11\uff0c\u5fc3\u9053\uff1a\u201c\u4e91\u7237\u7237\u8fd9\u5957\u8863\u895f\u7a7f\u8d77\u6765\u751a\u662f\u5f97\u4f53\u8212\u9002\uff0c\u770b\u6765\u4ed6\u8001\u4eba\u5bb6\u5e74\u9752\u65f6\uff0c\u5f88\u8bb2\u7a76\u7a7f \u7740\u54e9\uff01\u201d\u4ed6\u8f7b\u8dc3\u800c\u53bb\uff0c\u8863\u5e26\u8fce\u98ce\u98d8\u66f3\uff0c\u81ea\u89c9\u751a\u662f\u6d12\u8131\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4e00\u9635\u4f4e\u6c89\u7684\u6ce3\u58f0\uff0c\u4ece\u7af9\u6797\u4e2d\u4f20\u51fa\u3002\u51cc\u98ce\u6b64\u65f6\u5185\u529f\u7cbe\u582a\uff0c\u8033\u76ee\u6781\u662f\u7075\u654f\uff0c\u4ed4\u7ec6 \u542c\u4e86\u4e00\u4e0b\uff0c\u7acb\u523b\u53d1\u73b0\u90a3\u662f\u4e91\u7237\u7237\u5c4f\u6c14\u6697\u6ce3\u3002\u4ed6\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e8b\u60c5\u7ec8\u4e8e\u7206\u53d1\u4e86\uff0c\u6211\u77a7\u7237\u7237 \u8fd9\u51e0\u5929\u6108\u6765\u6108\u662f\u4e0d\u4e50\uff0c\u5509\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u4e8b\uff0c\u7237\u7237\u4e0d\u77e5\u4e3a\u4e86\u4ec0\u4e48\uff0c\u628a\u81ea\u5df1\u5b9d\u8d35\u7684\u9752\u6625\uff0c\u57cb \u846c\u5728\u8fd9\u5b64\u82e6\u7684\u8c37\u91cc\u3002\u201d\u8f6c\u5ff5\u53c8\u60f3\u9053\uff1a\u201c\u5345\u591a\u5e74\u4e86\uff0c\u4ec0\u4e48\u75db\u82e6\u4e5f\u5e94\u8be5\u6e10\u6e10\u6de1\u5fd8\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8d8a\u542c\u6ce3\u58f0\u8d8a\u662f\u60b2\u51c9\uff0c\u60f3\u5230\u4e91\u7237\u7237\u7684\u6148\u7965\uff0c\u7adf\u7136\u53d7\u5230\u8fd9\u822c\u6298\u78e8\uff0c\u9f3b\u5934\u4e00\u9178\uff0c\u4e5f\u4e0d\u7981 \u6d41\u4e0b\u6cea\u6765\u3002\u4ed6\u98de\u5954\u5165\u6797\uff0c\u987a\u7740\u6ce3\u58f0\uff0c\u8f7b\u6b65\u8dd1\u5230\u4e91\u7237\u7237\u80cc\u540e\u3002\u53ea\u89c1\u4e91\u7237\u7237\u57cb\u5934\u80f8\u524d\uff0c\u540e\u80cc \u4e00\u8d77\u4e00\u4f0f\uff0c\u6b63\u5728\u4f24\u5fc3\u62bd\u6ce3\uff0c\u5168\u6ca1\u6ce8\u610f\u4ed6\u8d70\u5230\u8eab\u540e\u3002 \u3000\u3000\u51cc\u98ce\u5fcd\u8010\u4e0d\u4f4f\uff0c\u786c\u54bd\u9053\uff1a\u201c\u4e91\u7237\u7237\uff0c\u4f60\u522b\u4f24\u5fc3\u5566\uff0c\u4f60\u5fc3\u4e2d\u6709\u4e8b\uff0c\u8bf4\u7ed9\u98ce\u513f\u542c\uff0c\u98ce\u513f \u66ff\u4f60\u89e3\u5fe7\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u609a\u7136\u4e00\u60ca\uff0c\u996e\u6ce3\uff0c\u53cc\u8896\u64e6\u6cea\u3002 \u3000\u3000\u51cc\u98ce\u67d4\u58f0\u529d\u9053\uff1a\u201c\u7237\u7237\uff0c\u5345\u591a\u5e74\u4e86\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u96be\u9053\u4f60\u8fd8\u4e0d\u80fd\u5fd8\u6000\u5417\uff1f\u201d\u7237\u7237\u6ca1\u6709 \u56de\u7b54\uff0c\u6708\u5149\u7167\u5728\u4ed6\u8138\u4e0a\uff0c\u51cc\u98ce\u89c9\u5f97\u7a81\u7136\u4e4b\u95f4\u7237\u7237\u82cd\u8001\u4e86\u4e0d\u5c11\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4e91\u7237\u7237\u5ffd\u7136\u6fc0\u52a8\u9053\uff1a\u201c\u98ce\u513f\uff0c\u4e16\u4e0a\u7684\u75db\u82e6\u539f\u662f\u6ca1\u6cd5\u6bd4\u8f83\uff0c\u6ca1\u6cd5\u5f62\u5bb9\u7684\uff0c \u53ea\u6709\u4f60\u4eb2\u8eab\u4f53\u4f1a\uff0c\u4f60\u4eb2\u8eab\u9886\u53d7\uff0c\u624d\u80fd\u8fa8\u522b\u5b83\u7684\u82e6\u5473\uff0c\u98ce\u513f\u4f60\u61c2\u5417\uff1f\u771f\u6b63\u7684\u75db\u82e6\u4f60\u662f\u6c38\u8fdc \u5fd8\u4e0d\u4e86\u7684\uff0c\u4f60\u53ea\u6709\u52aa\u529b\u5b66\u4e60\u4e0e\u5b83\u5171\u5b58\uff0c\u98ce\u513f\uff0c\u98ce\u513f\uff0c\u4f60\u660e\u767d\u5417\uff1f\u201d \u3000\u3000\u51cc\u98ce\u5fc3\u4e2d\u867d\u7136\u4e0d\u751a\u660e\u767d\uff0c\u4f46\u89c1\u4e91\u7237\u7237\u6ee1\u8138\u671f\u5f85\u4e4b\u60c5\uff0c\u4e0d\u5fcd\u62c2\u4ed6\u4e4b\u610f\uff0c\u5f53\u4e0b\u70b9\u5934\u7b54\u9053\uff1a \u201c\u98ce\u513f\u5df2\u660e\u767d\u4e86\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u611f\u60c5\u6e10\u6e10\u5e73\u9759\uff0c\u795e\u8272\u60a0\u8fdc\u6148\u7965\u3002\u5ffd\u7136\u8f6c\u5934\u9053\uff1a\u201c\u4eca\u5929\u662f\u516b\u6708\u521d\u51e0\uff1f\u201d \u3000\u3000\u51cc\u98ce\u521a\u624d\u770b\u8fc7\u523b\u5728\u7af9\u6746\u4e0a\u7528\u4ee5\u4ee3\u5386\u7684\u5200\u75d5\uff0c\u7b54\u9053\uff1a\u201c\u516b\u6708\u5341\u56db\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u9053\uff1a\u201c\u4f60\u6765\u4e86\u4e00\u4e2a\u6708\u5566\uff0c\u6211\u538b\u7bb1\u5e95\u7684\u6b66\u529f\u90fd\u4f20\u7ed9\u4f60\u4e86\uff0c\u4f60\u8fd8\u6709\u8bb8\u591a\u5927\u4e8b\u672a\u529e\uff0c \u660e\u5929\u8fc7\u4e86\u4e2d\u79cb\uff0c\u4f60\u51fa\u5c71\u53bb\u5427\uff01\u62a5\u5b8c\u7236\u4ec7\uff0c\u4f60\u53ef\u5343\u4e07\u522b\u5fd8\u8bb0\u628a\u963f\u5170\u5e26\u6765\uff0c\u8ba9\u6211\u77a7\u77a7\u5979\u7684\u773c \u6674\u3002\u201d \u3000\u3000\u51cc\u98ce\u4e0e\u4ed6\u867d\u53ea\u76f8\u5904\u4e00\u6708\uff0c\u53ef\u662f\u5bf9\u4ed6\u975e\u5e38\u4f9d\u604b\uff0c\u7136\u800c\u60f3\u5230\u81ea\u5df1\u8eab\u4e0a\u5927\u4e8b\uff0c\u786c\u8d77\u5fc3\u80a0\uff1a \u3000\u3000\u201c\u7237\u7237\uff0c\u98ce\u513f\u4e00\u5b9a\u6765\u966a\u4f60\u3002\u201d \u3000\u3000\u4e91\u7237\u7237\u9053\uff1a\u201c\u597d\u5566\uff0c\u5929\u8272\u4e0d\u65e9\uff0c\u4f60\u4e5f\u8be5\u6b47\u6b47\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u4f9d\u8a00\u8fdb\u6d1e\uff0c\u8eba\u5728\u7528\u6811\u679d\u7af9\u53f6\u94fa\u8d77\u7684\u5e8a\u4e0a\uff0c\u5fc3\u4e2d\u601d\u6f6e\u7ffb\u6eda\uff0c\u7237\u7237\u7684\u8bdd\u4f3c\u4e4e\u53c8\u98d8\u5230 \u8033\u8fb9\uff1a\u201c\u771f\u6b63\u7684\u75db\u82e6\uff0c\u4f60\u662f\u6c38\u8fdc\u4e0d\u80fd\u5fd8\u6000\uff0c\u4f60\u53ea\u6709\u5b66\u4e60\u4e0e\u5b83\u540c\u5728\uff0c\u4e0e\u5b83\u5171\u5b58\u3002\u201d\u201c\u5047\u5982 \u6709\u4e00\u5929\u2026\u2026\u6709\u4e00\u5929\u90a3\u963f\u5170\u4e0e\u6211\u6c38\u522b\uff0c\u6211\u2026\u2026\u6211\u53ef\u6709\u52c7\u6c14\u6d3b\u4e0b\u53bb\u5417\uff1f\u6211\u53ef\u6709\u52c7\u6c14\u4e0e\u8fd9\u65e0\u7a77 \u5c3d\u7684\u75db\u82e6\u5171\u5b58\u5728\u8fd9\u4e16\u4e0a\u5417\uff1f\u201d\u201c\u4e0d\uff0c\u51b3\u4e0d\u4f1a\u7684\uff0c\u8001\u5929\u7237\uff0c\u8001\u5929\u7237\uff0c\u6211\u77e5\u4f60\u4e0d\u4f1a\u5bf9\u6211\u8fd9\u4e48 \u6b8b\u9177\u7684\u3002\u201d \u3000\u3000\u4ed6\u867d\u5b89\u6170\u81ea\u5df1\uff0c\u53ef\u662f\u5fc3\u4e2d\u5374\u6709\u4e00\u79cd\u4e0d\u7965\u7684\u9884\u611f\u2026\u2026 \u3000\u3000\u7b2c\u4e09\u5929\u65e9\u4e0a\uff0c\u51cc\u98ce\u5f3a\u5fcd\u60b2\u4f24\uff0c\u8f9e\u522b\u4e91\u7237\u7237\u3002\u4ed6\u4e00\u518d\u8981\u6c42\u4e91\u7237\u7237\u4e0d\u8981\u518d\u4f24\u5fc3\uff0c\u5230\u8c37\u5916 \u53bb\u6e38\u5c71\u73a9\u6c34\uff0c\u7237\u7237\u53ea\u662f\u5fae\u7b11\u7684\u6447\u5934\uff0c\u53cd\u590d\u53ee\u5631\u51cc\u98ce\u53eb\u4ed6\u65e9\u65e5\u628a\u963f\u5170\u5e26\u6765\u7ed9\u7237\u7237\u770b\u3002 \u3000\u3000\u51cc\u98ce\u6536\u8d77\u611f\u60c5\uff0c\u98de\u6b65\u51fa\u8c37\uff0c\u5f53\u4ed6\u6b63\u8dd1\u5230\u8def\u65c1\u65f6\uff0c\u4e91\u7237\u7237\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\u8ffd\u4e86\u8fc7\u6765\uff0c\u624b \u4e2d\u62ff\u7740\u4e00\u4e2a\u5c0f\u74f7\u74f6\u3002\u51cc\u98ce\u4f4f\u8db3\u9053\uff1a\u201c\u7237\u7237\uff0c\u4f60\u8fd8\u6709\u4ec0\u4e48\u4e8b\u8981\u5429\u5490\u5417\uff1f\u201d \u3000\u3000\u4e91\u7237\u7237\u9053\uff1a\u201c\u4f60\u5e08\u7236\u533b\u672f\u867d\u9ad8\uff0c\u5374\u662f\u98df\u53e4\u4e0d\u5316\uff0c\u867d\u80fd\u5bf9\u75c7\u4e0b\u836f\uff0c\u5374\u4e0d\u5584\u89e6\u7c7b\u65c1\u901a\uff0c \u90a3\u65e5\u963f\u5170\u8eab\u4e2d\u86c7\u6bd2\uff0c\u4ed6\u53ea\u60f3\u5230\u7528\u836f\u5c06\u6bd2\u6258\u51fa\uff0c\u5374\u5fd8\u8bb0\u4ee5\u6bd2\u5236\u6bd2\uff0c\u91d1\u86c7\u4e4b\u6bd2\u4e0e\u5a31\u86a3\u4e4b\u6bd2\uff0c \u6b63\u76f8\u514b\u5236\u3002\u6211\u73b0\u4e0b\u60f3\u51fa\u8fd9\u6cd5\u5b50\uff0c\u53ea\u662f\u963f\u5170\u53cc\u76ee\u5df1\u76f2\uff0c\u4e5f\u662f\u6789\u7136\u3002\u8fd9\u74f6\u4e2d\u88c5\u7684\u662f\u4e07\u5e74\u6e29\u7389 \u6240\u5b55\u80b2\u7684\u7075\u6cc9\uff0c\u662f\u6211\u6614\u5e74\u8d39\u5c3d\u5fc3\u8840\u5728\u96ea\u5c71\u5934\u5bfb\u83b7\uff0c\u529f\u80fd\u751f\u808c\u53bb\u8150\uff0c\u8d77\u6b7b\u56de\u751f\uff0c\u74f6\u5185\u4e00\u5171 \u53ea\u5269\u5341\u6ef4\uff0c\u4f60\u53ef\u8981\u73cd\u60dc\u4f7f\u7528\u3002\u201d \u3000\u3000\u51cc\u98ce\u63a5\u8fc7\u8c22\u4e86\uff0c\u518d\u5411\u4e91\u7237\u7237\u544a\u8f9e\uff0c\u7136\u540e\u65bd\u5c55\u8f7b\u529f\uff0c\u518d\u4e0d\u56de\u5934\uff0c\u5f84\u81ea\u5954\u5411\u8c37\u5916\u3002 \u3000\u3000\u4ed6\u75be\u5954\u4e86\u4e00\u9635\uff0c\u5fc3\u5185\u76d8\u7b97\u9053\uff1a\u201c\u6211\u4e0e\u963f\u5170\u7ea6\u4ed6\u4e00\u5e74\u4e4b\u540e\u518d\u56de\u6545\u4e61\uff0c\u73b0\u5728\u8fd8\u6709\u534a\u5e74\u5de6 \u53f3\uff0c\u4f55\u4e0d\u5148\u4e0a\u5d06\u5cd2\uff0c\u627e\u5389\u9e57\u90a3\u8001\u8d3c\u8bd5\u8bd5\u4e91\u7237\u7237\u6559\u6211\u7684\u9ad8\u62db\u3002\u201d \u3000\u3000\u4ed6\u4e3b\u610f\u65e2\u5b9a\uff0c\u5230\u4e86\u4e00\u4e2a\u5927\u9547\uff0c\u95ee\u4e86\u53bb\u5d06\u5cd2\u5c71\u7684\u8def\u9014\uff0c\u8d76\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8fd9\u65e5\u4ed6\u8def\u8fc7\u9655\u5317\uff0c\u5929\u8272\u5df2\u8fd1\u660f\u9ed1\uff0c\u4ed6\u89c1\u8def\u5f84\u6e10\u6e10\u5d0e\u5c96\uff0c\u53c8\u4e0d\u89c1\u6751\u843d\uff0c\u5fc3\u4e2d\u6b63\u81ea\u7126\u6025\uff0c \u7a81\u7136\u4e00\u53ea\u7edd\u5927\u767d\u9e3d\u4ece\u4ed6\u5934\u9876\u98de\u8fc7\uff0c\u4ed6\u89c1\u90a3\u767d\u9e3d\u751a\u662f\u795e\u4fca\u53ef\u7231\uff0c\u5f53\u4e0b\u7ae5\u5fc3\u5927\u8d77\uff0c\u8ffd\u4e0a\u524d\u53bb\uff0c \u4e00\u638c\u5411\u7a7a\u51fb\u53bb\uff0c\u90a3\u9e3d\u513f\u98de\u5f97\u672c\u4f4e\uff0c\u6b64\u65f6\u53d7\u6b64\u52b2\u9053\u4e00\u51fb\uff0c\u660f\u843d\u4e0b\u6765\uff0c\u51cc\u98ce\u89c1\u9e3d\u5b50\u8db3\u4e0b\u7cfb\u7740 \u4e00\u5757\u7ea2\u7f0e\uff0c\u5fc3\u4e2d\u5927\u5947\uff0c\u4ed6\u89e3\u5f00\u5e26\u5b50\uff0c\u5c55\u7f0e\u4e00\u77a7\uff0c\u8138\u8272\u7acb\u53d8\u3002 \u3000\u3000\u4ed6\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u54fc\uff0c\u53c8\u662f\u8fd9\u4e24\u4e2a\u8be5\u6b7b\u7684\u4e1c\u897f\uff0c\u4e0d\u77e5\u8fd9\u7fa4\u8d25\u7c7b\u53c8\u8981\u5e72\u4ec0\u4e48\u4f24\u5929\u5bb3\u7406 \u7684\u4e8b\u60c5\uff0c\u54fc\uff0c\u53eb\u6211\u5434\u51cc\u98ce\u649e\u7740\u53ef\u8981\u4f38\u624b\u7ba1\u4e00\u7ba1\u3002\u201d \u3000\u3000\u539f\u6765\u90a3\u7ea2\u7f0e\u4e0a\u753b\u7740\u4e24\u4e2a\u53ef\u6016\u7684\u9ab7\u9ac5\u5934\uff0c\u6b63\u662f\u6d77\u5929\u53cc\u715e\u7684\u4fe1\u53f7\u3002 \u3000\u3000\u51cc\u98ce\u5fc3\u9053\uff1a\u201c\u8fd9\u6d77\u5929\u53cc\u715e\u6b66\u529f\u786e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u4e5f\u4e0d\u77e5\u649e\u7740\u4ec0\u4e48\u6837\u5389\u5bb3\u7684\u654c\u4eba\uff0c\u7adf\u53d1 \u53f7\u6551\u6c42\u63f4\uff0c\u60f3\u53ec\u96c6\u4e5d\u8c6a\u5171\u540c\u5bf9\u4ed8\u3002\u201d\u4ed6\u5ffd\u53c8\u60f3\u9053\uff1a\u201c\u6d77\u5929\u53cc\u715e\u662f\u8f9b\u6377\u5f1f\u7684\u6740\u7236\u4ec7\u4eba\uff0c\u4e0d \u8981\u662f\u6377\u5f1f\u5bfb\u4e0a\u95e8\u53bb\uff0c\u76f8\u7ea6\u62fc\u6597\u563f\uff01\u201d\u4ed6\u60f3\u5230\u8f9b\u6377\u7684\u6b66\u529f\u9ad8\u5f3a\uff0c\u89c9\u5f97\u6b64\u4e8b\u5f88\u6709\u53ef\u80fd\uff0c\u5185\u5fc3 \u5927\u662f\u5173\u5fc3\u3002 \u3000\u3000\u4ed6\u5bfb\u601d\u9053\uff1a\u201c\u6377\u5f1f\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u4e5f\u96be\u654c\u4e5d\u8c6a\u7684\u56f4\u653b\uff0c\u6211\u5f97\u8d76\u5feb\u53bb\u5e2e\u52a9\u4ed6\uff0c\u6740\u4e00\u4e2a\u75db \u5feb\u3002\u521a\u624d\u9e3d\u513f\u4ece\u5357\u98de\u6765\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u5c31\u5728\u5357\u9762\u5c71\u4e0a\u51b3\u6597\u54e9\uff01\u201d \u3000\u3000\u4ed6\u7acb\u523b\u65bd\u5c55\u201c\u516b\u6b65\u8d76\u87fe\u201d\u5954\u5411\u5357\u9762\u7684\u4e18\u9675\uff0c\u5929\u8272\u5df2\u7ecf\u5168\u6697\u4e86\uff0c\u524d\u9014\u904d\u5730\u8346\u68d8\uff0c\u65e0\u8def \u53ef\u901a\uff0c\u51cc\u98ce\u4e00\u63d0\u6c14\u5c55\u5f00\u4e0a\u4e58\u8f7b\u529f\uff0c\u8eab\u4f53\u51e0\u8dc3\u4e4b\u4e0b\uff0c\u5df1\u7ecf\u5954\u5230\u5c71\u811a\uff0c\u8033\u4e2d\u6025\u95fb\u5175\u5203\u4ea4\u51fb\u58f0\uff0c \u4ed6\u6025\u4e2d\u4e0d\u6687\u5bfb\u627e\u4e0a\u5c71\u4e4b\u8def\uff0c\u770b\u51c6\u843d\u811a\u4e4b\u5904\uff0c\u76f4\u62d4\u800c\u4e0a\u3002 \u3000\u3000\u51cc\u98ce\u722c\u5230\u534a\u5c71\u8170\uff0c\u8033\u4e2d\u5175\u5203\u4e4b\u58f0\u6e10\u6e10\u758f\u843d\uff0c\u6700\u540e\u560e\u7136\u800c\u6b62\uff0c\u5fc3\u77e5\u80dc\u8d1f\u5df1\u5206\uff0c\u4e0d\u7531\u5927 \u6025\uff0c\u53ea\u89c1\u51e0\u6761\u9ed1\u5f71\u5411\u5c71\u90a3\u8fb9\u4e00\u95ea\u800c\u901d\uff0c\u4ed6\u8db3\u4e0b\u52a0\u52b2\uff0c\u7a9c\u5230\u5c71\u9876\u3002 \u3000\u3000\u90a3\u771f\u662f\u4e00\u5e45\u96f6\u4e71\u60e8\u6b8b\u7684\u60c5\u666f\uff0c\u4e09\u4e2a\u5c38\u4f53\u6a2a\u9648\u5728\u5c71\u5761\u4e0a\uff0c\u5176\u4e2d\u4e00\u4e2a\u6b7b\u6cd5\u5f88\u662f\u5947\u7279\uff0c\u4e00 \u67c4\u957f\u5251\u76f4\u8d2f\u54bd\u5589\uff0c\u51cc\u98ce\u4e0a\u524d\u4ed4\u7ec6\u4e00\u770b\uff0c\u8ba4\u5f97\u6b63\u662f\u4e5d\u8c6a\u4e4b\u4e00\u795e\u5251\u91d1\u9524\u6797\u5c11\u768b\uff0c\u5176\u4f59\u4e8c\u4eba\uff0c \u4ed6\u4e5f\u8ba4\u5f97\uff0c\u4e00\u4e2a\u662f\u5343\u624b\u5251\u5ba2\u9646\u65b9\uff0c\u4e00\u4e2a\u662f\u6458\u661f\u624b\u53f8\u7a7a\u5b97\u2026\u2026 \u3000\u3000\u591c\uff0c\u9759\u4e86\uff0c\u9759\u4e86\uff0c\u6811\u679d\u4e0a\u7684\u4e4c\u9e26\u4e0d\u518d\u5431\u5431\u5471\u5471\uff0c\u6015\u662f\u8d70\u8fdb\u68a6\u4e61\u4e86\u5427\uff01 \u3000\u3000\u5434\u51cc\u98ce\u5750\u5728\u6811\u4e0b\uff0c\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u4ed6\u5206\u6790\u4e00\u4e0b\u773c\u524d\u7684\u60c5\u52bf\uff0c\u5ffd\u7136\u4e00\u4e2a\u5ff5\u5934\u6d6e\u8d77\uff0c\u4ed6\u60f3\uff1a \u201c\u80fd\u591f\u624b\u5203\u4e09\u8c6a\u7684\u4eba\uff0c\u6c5f\u6e56\u4e0a\u53ea\u6015\u4e0d\u591a\uff0c\u4e00\u5b9a\u662f\u6377\u5f1f\u5e72\u7684\uff0c\u53ef\u662f\u957f\u5251\u51fa\u624b\uff0c\u539f\u662f\u62fc\u547d\u540c \u5f52\u4e8e\u5c3d\u7684\u62db\u5f0f\uff0c\u6377\u5f1f\u4e0d\u8981\u2026\u2026\u4e0d\u8981\u6709\u4ec0\u4e48\u4e0d\u6d4b\u54e9\uff01\u201d \u3000\u3000\u4ed6\u8d8a\u60f3\u8d8a\u662f\u5fc3\u5bd2\uff0c\u8dd1\u5230\u5c71\u5761\u7684\u90a3\u8fb9\uff0c\u4ed4\u7ec6\u5bdf\u770b\u3002\u8fd9\u5929\u665a\u4e0a\uff0c\u5929\u8272\u6781\u662f\u9634\u6697\uff0c\u6708\u513f\u8eb2 \u5728\u4e91\u91cc\uff0c\u4ed6\u6cbf\u7740\u5c71\u5761\u770b\u53bb\uff0c\u9ed1\u6f06\u6f06\u7684\u4e00\u7247\u8346\u68d8\u3002 \u3000\u3000\u51cc\u98ce\u8e31\u6765\u8e31\u53bb\uff0c\u773c\u6674\u4e0d\u653e\u8fc7\u6bcf\u6837\u53ef\u7591\u7684\u4e1c\u897f\uff0c\u4ed6\u5de7\u98df\u8840\u679c\uff0c\u76ee\u529b\u5927\u662f\u589e\u8fdb\uff0c\u5ffd\u7136\u4ed6 \u53d1\u73b0\u6709\u4e00\u5904\u8346\u68d8\u7279\u522b\u96f6\u4e71\uff0c\u4f3c\u4e4e\u66fe\u88ab\u91cd\u7269\u8df5\u8e0f\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff1a\u201c\u6377\u5f1f\u90a3\u79cd\u5014\u5f3a\u7684\u6027\u513f\uff0c\u53ea \u8981\u501f\u5f97\u4e00\u53e3\u6c14\u5728\uff0c\u4e5f\u4f1a\u6323\u624e\u9003\u751f\uff0c\u4e0d\u80af\u843d\u4e8e\u654c\u4eba\u4e4b\u624b\uff0c\u591a\u534a\u662f\u8d1f\u4f24\u6eda\u4e0b\uff0c\u521a\u624d\u90a3\u51e0\u6761\u9ed1 \u5f71\uff0c\u6050\u6015\u662f\u2018\u5173\u4e2d\u4e5d\u8c6a\u2019\u4f59\u5b7d\uff0c\u641c\u7d22\u6377\u5f1f\u672a\u83b7\uff0c\u53c8\u89c1\u6211\u98de\u6b65\u5165\u5c71\uff0c\u8fd9\u624d\u76f8\u5055\u79bb\u53bb\u54e9\uff01\u201d \u3000\u3000\u4ed6\u5929\u8d44\u806a\u654f\uff0c\u786e\u80fd\u5904\u5904\u6599\u4e8b\u5982\u795e\uff0c\u6b64\u65f6\u65ad\u5b9a\u8f9b\u6377\u5c31\u5728\u5c71\u5761\u9644\u8fd1\uff0c\u5f53\u4e0b\u6253\u70b9\u7cbe\u795e\uff0c\u8dc3 \u8eab\u800c\u4e0b\u3002 \u3000\u3000\u51cc\u98ce\u987a\u7740\u96f6\u4e71\u7684\u8346\u68d8\u5411\u524d\u8d70\uff0c\u8d70\u4e86\u4e00\u9635\uff0c\u53ea\u89c1\u524d\u9762\u8346\u68d8\u66f4\u5bc6\u539a\uff0c\u518d\u4e5f\u627e\u4e0d\u51fa\u4efb\u4f55\u75d5 \u9057\u8ff9\uff0c\u4ed6\u5fc3\u4e2d\u6b63\u81ea\u76d8\u7b97\uff0c\u5ffd\u7136\u4e00\u9635\u6025\u4fc3\u4f4e\u6c89\u7684\u547b\u541f\u58f0\uff0c\u4ece\u53f3\u524d\u65b9\u4f20\u6765\u3002 \u3000\u3000\u51cc\u98ce\u518d\u65e0\u7591\u610f\uff0c\u4e0d\u987e\u5bc6\u5bc6\u7684\u8346\u68d8\uff0c\u5faa\u58f0\u627e\u53bb\uff0c\u5ffd\u95fb\u6c34\u58f0\u6f0f\u6f0f\uff0c\u5e02\u9762\u7adf\u662f\u4e00\u6761\u5c0f\u6cb3\u3002 \u4ed6\u6325\u52a8\u957f\u5251\uff0c\u6e05\u9664\u963b\u788d\uff0c\u53ea\u89c1\u5728\u4e71\u8349\u5806\u4e2d\uff0c\u8eba\u7740\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u51cc\u98ce\u4e0a\u524d\u4e00\u770b\uff0c\u90a3\u4eba\u6b63\u662f\u8f9b\u6377\uff0c\u795e\u667a\u5df2\u662f\u660f\u8ff7\uff0c\u6ee1\u8eab\u4f24\u75d5\u3002 \u3000\u3000\u4ed6\u6025\u5fd9\u4fef\u8eab\u4e00\u63a2\uff0c\u53ea\u6709\u5fc3\u623f\u8fd8\u5728\u5fae\u5fae\u8df3\u52a8\u3002 \u3000\u3000\u51cc\u98ce\u5fc3\u4e2d\u5927\u662f\u4f24\u75db\uff0c\u773c\u89c1\u8fd9\u60c5\u903e\u624b\u8db3\u7684\u4e49\u5f1f\u751f\u5c11\u6b7b\u591a\uff0c\u5185\u5fc3\u771f\u6709\u5982\u4e94\u5185\u4ff1\u711a\u3002\u4ed6\u539f \u662f\u4e0d\u8f7b\u6613\u6d6a\u8d39\u611f\u60c5\u7684\u4eba\uff0c\u4f46\u662f\u4e00\u65e6\u4ed8\u51fa\u60c5\u611f\uff0c\u90a3\u4fbf\u662f\u7ec8\u751f\u4e0d\u6e1d\u4e86\u3002 \u3000\u3000\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u5ffd\u7136\u60f3\u5230\u4e91\u7237\u7237\u90a3\u74f6\u4e07\u5e74\u7075\u6cc9\uff0c\u7acb\u523b\u4f38\u624b\u4ece\u6000\u4e2d\u6478\u4e86\u51fa\u6765\uff0c\u5fc3\u60f3\uff1a \u201c\u6377\u5f1f\u867d\u662f\u6d51\u8eab\u4f24\u75d5\uff0c\u4f46\u90fd\u4e0d\u662f\u81f4\u547d\u4e4b\u51fb\uff0c\u76ee\u4e0b\u547c\u5438\u5fae\u5f31\uff0c\u5b9a\u662f\u53d7\u4e86\u6c89\u91cd\u5185\u4f24\uff0c\u800c\u4e14\u5931 \u8840\u8fc7\u591a\uff0c\u4ed6\u4e0d\u52a0\u601d\u7d22\uff0c\u62d4\u5f00\u74f6\u76d6\uff0c\u6311\u5f00\u8f9b\u6377\u54ac\u7d27\u7684\u7259\u5173\uff0c\u5012\u4e86\u4e09\u6ef4\u4e0b\u53bb\u3002 \u3000\u3000\u4ed6\u6536\u8d77\u4e86\u4e07\u5e74\u795e\u6cc9\uff0c\u7ec6\u77a7\u8f9b\u6377\u7684\u4f24\u52bf\uff0c\u5fc3\u5185\u66f4\u52a0\u4f24\u75db\uff0c\u53ea\u89c1\u638c\u4f24\uff0c\u5200\u4f24\uff0c\u6697\u5668\u4f24\uff0c \u8346\u68d8\u5272\u7834\u7684\u4f24\u75d5\uff0c\u5e03\u6ee1\u4e86\u8f9b\u6377\u7684\u5168\u8eab\uff0c\u51cc\u98ce\u786c\u7740\u5fc3\u80a0\uff0c\u7528\u5251\u5272\u5f00\u4f24\u53e3\u9644\u8fd1\u5df1\u4e0e\u8840\u6d46\u6cbe\u9ecf \u7684\u8863\u886b\uff0c\u4ed6\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4e0d\u5982\u4e58\u73b0\u5728\u6377\u5f1f\u672a\u9192\u524d\uff0c\u66ff\u4ed6\u6d17\u6da4\u5305\u624e\uff0c\u514d\u5f97\u4ed6\u591a\u53d7\u75db\u82e6\u3002\u201d \u3000\u3000\u51cc\u98ce\u89e3\u5f00\u5305\u88f9\uff0c\u53d6\u51fa\u4e00\u4e2a\u5927\u676f\uff0c\u98de\u5954\u5230\u5c0f\u6eaa\u8fb9\uff0c\u76db\u4e86\u6ee1\u6ee1\u4e00\u676f\u6e05\u6c34\u3002 \u3000\u3000\u4ed6\u8fd0\u529b\u6495\u788e\u5305\u88f9\u4e2d\u6362\u6d17\u7684\u8863\u886b\uff0c\u5f53\u4e0b\u5c31\u7ec6\u5fc3\u7684\u66ff\u8f9b\u6377\u88f9\u4f24\uff0c\u7b49\u5230\u5305\u5b8c\u4e86\u4f24\u53e3\uff0c\u51cc\u98ce \u53c8\u4f38\u624b\u5230\u8f9b\u6377\u9f3b\u7aef\uff0c\u53ea\u89c9\u8fd8\u6709\u4e9b\u5fae\u5fae\u547c\u5438\uff0c\u7a0d\u7a0d\u653e\u5fc3\u3002 \u3000\u3000\u6708\u513f\u6025\u800c\u9732\u51fa\u4e86\u4e4c\u4e91\u5806\uff0c\u51cc\u98ce\u4f46\u89c1\u8f9b\u6377\u9762\u8272\u60e8\u767d\u6015\u4eba\uff0c\u7b80\u76f4\u5c31\u50cf\u6b7b\u53bb\u4e00\u822c\uff0c\u60f3\u5230\u8f9b \u6377\u6614\u65e5\u6f47\u6d12\u98ce\u6d41\u7684\u6a21\u6837\uff0c\u4e0d\u89c9\u5fc3\u5982\u5200\u5272\u3002 \u3000\u3000\u60f3\u9053\uff1a\u201c\u6211\u4e0e\u6377\u5f1f\u5206\u624b\u4e0d\u5230\u4e24\u4e2a\u6708\uff0c\u4e16\u4e8b\u53d8\u8fc1\u5374\u662f\u8fd9\u4e48\u5927\uff0c\u96be\u9053\u5728\u6211\u547d\u8fd0\u4e2d\uff0c\u9664\u4e86 \u751f\u79bb\uff0c\u4fbf\u53ea\u662f\u6b7b\u522b\u4e86\u5417\uff1f\u201d \u3000\u3000\u591c\u51c9\u4f3c\u6c34\uff0c\u98ce\u58f0\u5982\u5578\u3002 \u3000\u3000\u5929\u6e10\u6e10\u4eae\u4e86\uff0c\u51cc\u98ce\u63c9\u4e86\u63c9\u4e00\u591c\u672a\u5408\u7684\u773c\u775b\u3002 \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u4ed6\u4e0d\u77e5\u63a2\u4e86\u8f9b\u6377\u51e0\u6b21\u9f3b\u606f\uff0c\u8f9b\u6377\u4ecd\u7136\u662f\u660f\u660f\u8ff7\u8ff7\u7684\u3002\u4ed6\u539f\u662f\u4e0d\u4fe1\u4efb\u8ff7\u4fe1\u7684\uff0c \u53ef\u662f\u5728\u8fd9\u8352\u5c71\u91cc\uff0c\u9762\u5bf9\u7740\u8fd9\u5944\u5944\u4e00\u606f\u7684\u4eba\uff0c\u4ed6\u5728\u4e0d\u89c9\u4e2d\u5bf9\u795e\u9b3c\u529b\u91cf\u8d77\u4e86\u4f9d\u8d56\u4e4b\u5fc3\uff0c\u4ed6\u9ed8 \u9ed8\u7977\u9053\uff1a\u201c\u8001\u5929\u7237\uff0c\u4f60\u628a\u6377\u5f1f\u9020\u5f97\u8fd9\u4e48\u5341\u5168\u5341\u7f8e\uff0c\u4f60\u603b\u4e0d\u4f1a\u629b\u5f03\u4e0d\u987e\u4ed6\u5427\uff01\u201d \u3000\u3000\u5ffd\u7136\uff0c\u8f9b\u6377\u53d1\u51fa\u4e86\u547b\u541f\u58f0\uff0c\u8eab\u5b50\u52a8\u4e86\u4e24\u4e0b\u3002 \u3000\u3000\u51cc\u98ce\u5927\u559c\uff0c\u4fef\u4e0b\u8eab\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u53ef\u597d\u4e86\u4e00\u70b9\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5634\u5507\u98a4\u52a8\u6b32\u8a00\u53ef\u662f\u59cb\u7ec8\u6ca1\u6709\u5f00\u53e3\u3002 \u3000\u3000\u51cc\u98ce\u67d4\u58f0\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u597d\u597d\u4f11\u606f\u5427\uff0c\u4f60\u4f24\u52bf\u4e00\u5b9a\u4f1a\u597d\u7684\u3002\u201d \u3000\u3000\u8f9b\u6377\u70b9\u4e86\u70b9\u5934\uff0c\u53c8\u660f\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u65f6\u660f\u65f6\u9192\uff0c\u51cc\u98ce\u6574\u5929\u5b88\u5728\u8eab\u8fb9\uff0c\u4e0d\u6562\u8fdc\u79bb\u3002 \u3000\u3000\u5230\u4e86\u508d\u665a\uff0c\u8f9b\u6377\u7a81\u53d1\u9ad8\u70e7\uff0c\u795e\u667a\u8ff7\u4e71\uff0c\u68a6\u4e2d\u80e1\u8a00\u4e71\u8bed\uff0c\u51cc\u98ce\u89c1\u4ed6\u547c\u5438\u6e10\u6e10\u7c97\u58ee\u5fc3\u4e0b \u7565\u5b89\uff0c\u5fc3\u77e5\u5fc5\u662f\u4f24\u53e3\u5316\u8113\uff0c\u60f3\u9053\uff1a\u201c\u4e91\u7237\u7237\u8bf4\u8fc7\u8fd9\u7075\u7389\u795e\u6cc9\uff0c\u662f\u6cbb\u5185\u5916\u4f24\u7684\u65e0\u4e0a\u5723\u836f\uff0c \u6211\u7528\u8fd9\u7075\u6cc9\u6c34\u53bb\u6d17\u4ed6\u5316\u8113\u7684\u4f24\u53e3\uff0c\u4e00\u5b9a\u751a\u662f\u6709\u6548\u3002\u201d\u4ed6\u5306\u5fd9\u7684\u8dd1\u5230\u6eaa\u8fb9\uff0c\u6320\u4e86\u4e00\u676f\u6c34\uff0c \u6458\u4e86\u4e24\u6ef4\u7075\u6cc9\u6db2\uff0c\u89e3\u5f00\u8f9b\u6377\u8eab\u4e0a\u5305\u624e\u7684\u5e03\u6761\uff0c\u6cbe\u7740\u6c34\u6162\u6162\u62c2\u6d17\u7740\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u8eab\u4e0a\u4e00\u9635\u6e05\u51c9\uff0c\u7741\u5f00\u5927\u773c\uff0c\u76f4\u89c6\u51cc\u98ce\u3002 \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u7741\u5f00\u4e86\u773c\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u4f46\u53c8\u89c1\u773c\u5149\u8fdf\u5446\uff0c\u4f3c\u662f\u4e0d\u8ba4\u81ea\u5df1\uff0c\u5fd9\u9053\uff1a\u201c\u6377\u5f1f\uff0c \u6211\u662f\u4f60\u5927\u54e5\uff0c\u4f60\u7684\u5927\u54e5\u5440\uff0c\u522b\u8d39\u5fc3\u601d\uff0c\u597d\u597d\u517b\u4f24\uff01\u201d \u3000\u3000\u8f9b\u6377\u53e3\u4e2d\u5583\u5583\uff0c\u58f0\u97f3\u751a\u662f\u4f4e\u6c89\uff0c\u51cc\u98ce\u77e5\u9053\u4ed6\u6709\u8981\u4e8b\u8981\u8bb2\uff0c\u5f53\u4e0b\u51d1\u8fd1\u51dd\u795e\u800c\u542c\u3002 \u3000\u3000\u201c\u6885\u2026\u2026\u9f84\u2026\u2026\u4faf\u4e8c\u53d4\u2026\u65b9\u5c11\u78a7\u2026\u2026\u6b7b\u4e86\u2026\u2026\u6b7b\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u4e00\u6014\u95ee\u9053\uff1a\u201c\u8c01\u6b7b\u4e86\uff1f\u201d \u3000\u3000\u201c\u6d77\u2026\u2026\u6d77\u2026\u2026\u662f\u2026\u2026\u662f\u8fd9\u6837\u2026\u2026\u8df3\u4e0b\u53bb\u7684\u3002\u201d \u3000\u3000\u51cc\u98ce\u529d\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u522b\u80e1\u601d\u4e71\u60f3\u5566\u3002\u201d \u3000\u3000\u201c\u662f\u8fd9\u6837\u2026\u2026\u8fd9\u6837\u8df3\u4e0b\u53bb\u7684\uff0c\u6211\u2026\u2026\u201d\u6211\u773c\u7741\u7741\uff0c\u770b\u5230\u6ce2\u6d6a\u2026\u2026\u6ce2\u6d6a\u5377\u6ca1\u4e86\u2026\u2026\u201d \u3000\u3000\u51cc\u98ce\u5fcd\u4e0d\u4f4f\u53c8\u95ee\u7b54\uff1a\u201c\u8c01\u8df3\u6d77\u5440\uff01\u201d \u3000\u3000\u201c\u65b9\u2026\u2026\u65b9\u5c11\u78a7\u2026\u2026\u6211\u2026\u2026\u6211\u2026\u2026\u539f\u662f\u5f88\u559c\u6b22\u5979\uff0c\u5f88\u559c\u6b22\u5440\uff01\u201d \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u6ee1\u8138\u51c4\u6006\u7f20\u7ef5\uff0c\u5fc3\u5185\u5df2\u660e\u767d\u5927\u534a\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u65b9\u5c11\u78a7\u662f\u4e00\u4f4d\u59d1\u5a18\uff0c\u5979\u6295\u6d77 \u81ea\u6740\u4e86\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u60f3\u4e86\u534a\u5929\uff0c\u70b9\u4e86\u4e00\u4e0b\u5934\u3002 \u3000\u3000\u51cc\u98ce\u67d4\u58f0\u5b89\u6170\u9053\uff1a\u201c\u90a3\u65b9\u59d1\u5a18\uff0c\u5b9a\u7136\u5f97\u6551\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u832b\u7136\u6447\u6447\u5934\uff0c\u4e00\u9897\u6cea\u73e0\u6d41\u5230\u988a\u8fb9\u3002 \u3000\u3000\u51cc\u98ce\u5fc3\u60f3\uff1a\u201c\u6211\u5e73\u65e5\u89c1\u6377\u5f1f\u5929\u771f\u987d\u76ae\uff0c\u77e5\u9053\u4ed6\u65e0\u5fe7\u65e0\u6101\uff0c\u60f3\u4e0d\u5230\u7adf\u4e5f\u4e3a\u2018\u60c5\u2019\u6240\u82e6\uff0c \u5509\uff01\u8fd9\u4e16\u4e0a\u771f\u662f\u75db\u82e6\u5f97\u5f88\u54e9\uff01\u201d \u3000\u3000\u4ed6\u89c1\u8f9b\u6377\u53c8\u6c89\u6c89\u7761\u53bb\uff0c\u5fc3\u4e0b\u5927\u5b89\uff0c\u7ee7\u7eed\u66ff\u4ed6\u6d17\u6da4\u3002\u51cc\u98ce\u8fd9\u7075\u6cc9\u6d17\u4f24\u7684\u4e3b\u610f\uff0c\u539f\u662f\u60c5 \u6025\u4e4b\u4e0b\u201c\u6025\u4e71\u6295\u533b\u201d\uff0c\u4e0d\u6599\u6b63\u662f\u5bf9\u75c7\u4e0b\u836f\uff0c\u90a3\u4e07\u5e74\u6e29\u7389\u7075\u6c14\u6240\u5b55\u7684\u6cc9\u6c34\uff0c\u53ea\u6d88\u4e00\u6ef4\uff0c\u4fbf \u80fd\u8d77\u6b7b\u56de\u751f\uff0c\u751f\u808c\u53bb\u8150\uff0c\u7528\u6765\u6d17\u62c2\u4f24\u53e3\uff0c\u6d88\u80bf\u53bb\u8113\u4e4b\u529f\uff0c\u786e\u662f\u795e\u5999\u65e0\u6bd4\u3002 \u3000\u3000\u6b21\u6668\uff0c\u8f9b\u6377\u795e\u667a\u5df2\u662f\u6e05\u9192\uff0c\u70e7\u4e5f\u5b8c\u5168\u9000\u4e86\uff0c\u51cc\u98ce\u8eab\u8fb9\u6240\u5e26\u5e72\u7cae\u5df2\u7ecf\u5403\u5c3d\uff0c\u4ed6\u89c1\u8f9b\u6377 \u4f24\u52bf\u5927\u6982\u4e0d\u4f1a\u53d8\u6076\uff0c\u5f53\u4e0b\u4fbf\u7528\u5e03\u6761\u628a\u8f9b\u6377\u80cc\u5728\u540e\u80cc\uff0c\u8d76\u5230\u4e00\u4e2a\u5927\u9547\u3002 \u3000\u3000\u5434\u51cc\u98ce\u843d\u4e86\u5e97\uff0c\u7167\u62a4\u8f9b\u6377\u7761\u597d\uff0c\u81ea\u5df1\u4e5f\u56e0\u8fde\u591c\u75b2\u52b3\u800c\u719f\u719f\u7761\u53bb\u3002 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5434\u51cc\u98ce\u4ece\u719f\u7761\u4e2d\u7a81\u7136\u611f\u5230\u88ab\u4e00\u9635\u70ed\u98ce\u5439\u9192\uff0c\u4ed6\u9661\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u53ea \u89c1\u6b63\u662f\u8f9b\u6377\u5728\u8eab\u65c1\u5bf9\u7740\u4ed6\u7684\u8033\u6735\u5439\u6c14\uff0c\u4ed6\u4e0d\u7981\u5927\u559c\u53eb\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u597d\u4e86\u5417\uff1f\u6377\u5f1f\u4f60\u2014 \u2014\u4f60\u771f\u987d\u76ae\uff0c\u624d\u597d\u4e9b\u5c31\u8d77\u6765\u80e1\u95f9\uff0c\u8fd8\u65e9\u54e9\uff0c\u5feb\u53bb\u8eba\u4e00\u4f1a\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u563b\u563b\u7b11\u9053\uff1a\u201c\u8fd8\u65e9\u54e9\uff1f\u4f60\u81ea\u5df1\u770b\u770b\u2014\u2014\u201d \u3000\u3000\u51cc\u98ce\u62ac\u5934\u4e00\u770b\u7a97\u5916\uff0c\u5df2\u662f\u65e5\u4e0a\u4e09\u7aff\u7684\u65f6\u5206\u4e86\uff0c\u4e0d\u7981\u6697\u9a82\u81ea\u5df1\u4e00\u89c9\u5982\u540c\u7761\u6b7b\u4e86\u4e00\u822c\u3002 \u3000\u3000\u8f9b\u6377\u5374\u6599\u77e5\u81ea\u5df1\u7684\u6027\u547d\u5fc5\u662f\u5434\u5927\u54e5\u6240\u6551\uff0c\u800c\u4ed6\u5fc5\u662f\u4e3a\u7167\u6599\u81ea\u5df1\u800c\u5f7b\u591c\u672a\u7720\u2026\u2026 \u3000\u3000\u51cc\u98ce\u89c1\u8f9b\u6377\u76ee\u5149\u70af\u7136\uff0c\u7cbe\u795e\u5065\u65fa\uff0c\u9664\u4e86\u5931\u8840\u8fc7\u591a\u9762\u8272\u82cd\u767d\u4e4b\u5916\uff0c\u7adf\u4f3c\u5df2\u7ecf\u75ca\u6108\uff0c\u5fc3 \u5934\u66f4\u662f\u5927\u559c\uff0c\u53eb\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u2014\u2014\u201d \u3000\u3000\u6562\u60c5\u4ed6\u53d1\u73b0\u8f9b\u6377\u6b63\u5728\u4f4e\u9996\u6c89\u601d\uff0c\u4e0d\u7531\u4e00\u6014\u9053\uff1a\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d \u3000\u3000\u201c\u5927\u54e5\uff0c\u4f60\u2014\u2014\u4f60\u5f85\u6211\u771f\u597d\uff0c\u6211\u5728\u60f3\uff0c\u6211\u8f9b\u6377\u7684\u51fa\u751f\u65f6\u8fb0\u5fc5\u7136\u602a\u6781\uff0c\u5426\u5219\u4e16\u4e0a\u5bf9\u6211 \u597d\u7684\u4eba\u600e\u4e48\u5982\u6b64\u4e4b\u597d\uff0c\u800c\u5bf9\u6211\u574f\u7684\u4eba\u4e5f\u5982\u6b64\u4e4b\u6076\uff1f\u554a\u2014\u2014\u4f60\u77a7\u6211\u60f3\u7cca\u6d82\u5566\uff0c\u8fd8\u6ca1\u6709\u95ee\u4f60 \u600e\u4e48\u4f1a\u9047\u4e0a\u6211\u7684\u5462\uff1f\u90a3\u5929\u548c\u90a3\u8be5\u6b7b\u7684\u91d1\u6b39\u4e00\u9f50\u6eda\u4e0b\u5c71\u5d16\uff0c\u6211\u53ea\u77e5\u9053\u4f60\u5fc5\u662f\u5b8c\u5566\uff0c\u6211\u66fe\u4e3a \u4f60\u2014\u2014\u201d \u3000\u3000\u4ed6\u672c\u662f\u8bf4\u201c\u4e3a\u4f60\u5927\u54ed\u4e00\u573a\u201d\uff0c\u4f46\u7acb\u523b\u60f3\u5230\u8fd9\u8bdd\u8bf4\u51fa\u4e0d\u751a\u5149\u91c7\uff0c\u662f\u4ee5\u505c\u4f4f\u4e86\u53e3\u3002 \u3000\u3000\u51cc\u98ce\u5012\u6ca1\u6709\u6ce8\u610f\u8fd9\u4e9b\uff0c\u4ed6\u8d76\u7d27\u5c06\u81ea\u5df1\u7684\u5947\u9047\u544a\u8bc9\u4e86\u8f9b\u6377\uff0c\u8bf4\u5230\u5999\u5904\uff0c\u8f9b\u6377\u4e0d\u7981\u559c\u5f97 \u8fde\u58f0\u53eb\u597d\u3002 \u3000\u3000\u51cc\u98ce\u8bf4\u5b8c\u540e\uff0c\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u90a3\u4e91\u7237\u7237\u7684\u6a21\u6837\u5fc5\u7136\u6781\u662f\u6148\u7965\uff0c\u54ea\u65e5\u6211\u4e5f\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u4f60\u5012\u8bf4\u8bf4\u4f60\u600e\u4f1a\u88ab\u5173\u4e2d\u4e5d\u8c6a\u4f24\u6210\u8fd9\u822c\u6a21\u6837\uff1f\u82e5\u4e0d\u662f\u9760\u4e91\u7237\u7237\u7684\u7075\u836f\uff0c\u6b64 \u523b\u53ea\u6015\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u51b7\u7b11\u9053\uff1a\u201c\u5173\u4e2d\u4e5d\u8c6a\u771f\u4e0d\u6127\u6323\u5f97\u4e86\u5f88\u5927\u7684\u540d\u5934\uff0c\u4ee5\u4f17\u51cc\u5be1\u81ea\u662f\u4e0a\u7b56\u5427\uff01\u4e0b\u6b21\u6211 \u78b0\u4e0a\u4e86\uff0c\u54fc\u2014\u2014\u201d \u3000\u3000\u63a5\u7740\u5c31\u628a\u81ea\u5df1\u6597\u52fe\u6f0f\u4e00\u602a\uff0c\u5931\u5251\uff0c\u9047\u4e5d\u8c6a\u56f4\u653b\u7b49\u4e8b\u4e00\u4e00\u8bf4\u4e86\u4e00\u904d\u3002 \u3000\u3000\u51cc\u98ce\u7b11\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u606d\u559c\u4f60\u554a\uff0c\u2018\u6885\u9999\u795e\u5251\u2019\u8fd9\u5916\u53f7\u6562\u60c5\u597d\u3002\u201d \u3000\u3000\u8f9b\u6377\u53f9\u9053\uff1a\u201c\u53ef\u60dc\u6885\u9999\u5251\u5df2\u88ab\u76d7\u53bb\u5566\uff0c\u53ea\u5f85\u6211\u660e\u65e5\u7565\u4e3a\u6062\u590d\uff0c\u5c31\u7acb\u523b\u4e0a\u5d06\u5cd2\u53bb\u5927\u95f9 \u4e00\u573a\u2014\u2014\u5927\u54e5\uff0c\u4f60\u4e5f\u8981\u53bb\u4e5f\u597d\u6e05\u6e05\u4e00\u65e7\u5e10\u3002\u201d \u3000\u3000\u6b21\u65e5\uff0c\u8f9b\u6377\u7adf\u7136\u5df2\u75ca\u6108\uff0c\u4ed6\u6b63\u5728\u5e8a\u4e0a\u6697\u81ea\u884c\u529f\uff0c\u51cc\u98ce\u5df1\u63a8\u95e8\u8fdb\u6765\uff0c\u89c1\u8f9b\u6377\u9762\u8272\u5df2\u6062 \u590d\u8840\u8272\uff0c\u4e0d\u7981\u53c8\u60ca\u53c8\u559c\u9053\uff1a \u3000\u3000\u201c\u4e91\u7237\u7237\u7684\u7075\u836f\u7aef\u7684\u5999\u7edd\uff0c\u6377\u5f1f\u4f60\u53d7\u4e86\u90a3\u4e48\u91cd\u7684\u4f24\uff0c\u6d41\u90a3\u4e48\u591a\u7684\u8840\uff0c\u7adf\u7136\u4e24\u5929\u4e4b\u5185 \u5c31\u5b8c\u5168\u6062\u590d\uff0c\u4e0d\u8fc7\u6377\u5f1f\uff0c\u4f60\u8fd8\u662f\u4f11\u606f\u4e00\u4e0b\u8f83\u4e3a\u7a33\u5f53\u3002\u201d \u3000\u3000\u4e8c\u4eba\u5728\u9547\u4e2d\u4f4f\u4e86\u4e94\u5929\uff0c\u8f9b\u6377\u56b7\u7740\u8981\u8d70\uff0c\u4e8e\u662f\u4e24\u4eba\u7ed3\u5e10\u542f\u7a0b\u3002 \u3000\u3000\u8f9b\u6377\u5ffd\u7136\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u5148\u6682\u65f6\u4e0d\u5230\u5d06\u5cd2\u53bb\u2014\u2014\u201d \u3000\u3000\u51cc\u98ce\u5947\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u662f\u7b54\u5e94\u90a3\u82cf\u59d1\u5a18\u8981\u53bb\u770b\u5979\u4e00\u6b21\u4e48\uff1f\u6211\u60f3\u5389\u8001\u8d3c\u65e2\u662f\u5d06\u5cd2\u4e00\u6d3e\u4e4b\u638c \u95e8\uff0c\u54b1\u4eec\u968f\u65f6\u53bb\u627e\u4ed6\uff0c\u4ed6\u603b\u4e0d\u80fd\u7f29\u5934\u4e0d\u89c1\uff0c\u662f\u4ee5\u6015\u8fd8\u662f\u5148\u53bb\u5c71\u4e1c\u770b\u770b\u82cf\u59d1\u5a18\u2014\u2014\u201d \u3000\u3000\u51cc\u98ce\u4e00\u542c\u5230\u82cf\u59d1\u5a18\uff0c\u7acb\u523b\u60f3\u8d77\u90a3\u7edd\u7f8e\u7684\u82cf\u60e0\u82b7\uff0c\u82cf\u59d1\u5a18\u90a3\u6e05\u6f88\u7684\u773c\u6674\u7acb\u523b\u6d6e\u5728\u4ed6\u773c \u524d\u3002\u4ed6\u6697\u9053\uff1a\u201c\u82cf\u59d1\u5a18\u90a3\u53cc\u773c\u775b\u771f\u50cf\u963f\u5170\u7684\u554a\uff0c\u53ef\u662f\u963f\u5170\u5df2\u7ecf\u5931\u4e86\u660e\u2014\u2014 \u3000\u3000\u6211\u66fe\u4e3a\u82cf\u59d1\u5a18\u90a3\u53cc\u773c\u775b\u800c\u5077\u5077\u5bf9\u5979\u6709\u4e86\u597d\u611f\uff0c\u800c\u5979\u4e5f\u4f3c\u5bf9\u6211\u5bc4\u51fa\u4e86\u4e0d\u5bfb\u5e38\u7684\u611f\u60c5\uff0c \u7136\u800c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u5f53\u6211\u51fa\u6b7b\u5165\u751f\u7684\u65f6\u5019\uff0c\u6211\u53ea\u80fd\u60f3\u5230\u963f\u5170\uff0c\u5176\u4ed6\u751a\u4e48\u90fd\u60f3\u4e0d\u5230\uff0c\u96be\u9053\u2026\u2026 \u96be\u9053\u6211\u771f\u4e0d\u559c\u6b22\u82cf\u59d1\u5a18\u5417\uff1f\u2026\u2026\u554a\uff0c\u5979\u90a3\u773c\u6674\uff0c\u90a3\u7edd\u4e16\u7684\u7f8e\u8273\u2026\u2026\u51cc\u98ce\u554a\uff0c\u4f60\u5343\u4e07\u4e0d\u8981 \u5f04\u5f97\u4e0d\u80fd\u81ea\u62d4\u554a\u2014\u2014\u201d \u3000\u3000\u4f46\u662f\u4ed6\u53c8\u60f3\u5230\uff1a\u201c\u6211\u662f\u8be5\u53bb\u770b\u5979\u5462\u8fd8\u662f\u4e0d\u8be5\uff1f\u6211\u53bb\u770b\u5979\u5bf9\u5979\u662f\u597d\u8fd8\u662f\u574f\uff1f\u4e0d\u8fc7\uff0c\u6211 \u66fe\u7b54\u5141\u8fc7\u8981\u53bb\u770b\u5979\u7684\uff0c\u6211\u603b\u4e0d\u80fd\u5bf9\u4e00\u4e2a\u5973\u5b50\u5931\u4fe1\u5427\uff1f\u201d \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u4eec\u4e00\u540c\u8d70\u5411\u5c71\u4e1c\u3002 \u3000\u3000\u5546\u90b1\uff0c\u8fd9\u53e4\u57ce\u4e2d\u5145\u6ee1\u7740\u5546\u4e1a\u7684\u6c14\u606f\uff0c\u65e9\u4e0a\u7684\u9633\u5149\u4ece\u8857\u9053\u4e0a\u7167\u8fc7\u53bb\uff0c\u5168\u662f\u4e00\u6392\u6574\u9f50\u7684 \u5e97\u574a\u62db\u724c\uff0c\u663e\u5f97\u4e00\u7247\u5347\u5e73\u666f\u6c14\u7684\u6837\u5b50\u3002 \u3000\u3000\u7136\u800c\u8def\u9762\u5374\u662f\u4e0d\u592a\u597d\uff0c\u9ec4\u571f\u7684\u8def\u9762\u4e0a\u5076\u800c\u4e00\u8f86\u9a6c\u8f66\u8d70\u8fc7\uff0c\u5c31\u626c\u8d77\u853d\u7a7a\u7684\u9ec4\u5c18\uff0c\u4e45\u4e45 \u4e0d\u6563\u3002 \u3000\u3000\u5434\u51cc\u98ce\u548c\u8f9b\u6377\u4ece\u57ce\u5916\u4ec6\u4ec6\u98ce\u5c18\u5730\u8d76\u4e86\u8fdb\u6765\uff0c\u4ed6\u4eec\u770b\u51c6\u4e86\u4e00\u5bb6\u996d\u5e97\uff0c\u62cd\u4e86\u62cd\u8eab\u4e0a\u7684\u7070 \u5c18\uff0c\u5306\u5306\u8d70\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u4e00\u843d\u5ea7\uff0c\u4ed6\u4eec\u5c31\u53eb\u4e86\u5ba2\u996d\uff0c\u6562\u60c5\u4ed6\u4eec\u8d76\u8def\u8fde\u65e9\u996d\u90fd\u6ca1\u6709\u5403\u3002 \u3000\u3000\u90a3\u5e97\u5c0f\u4e8c\u7aef\u4e86\u83dc\u996d\u4e0a\u6765\uff0c\u671d\u7740\u4e24\u4eba\u8eab\u4e0a\u7684\u4f69\u5251\u6253\u91cf\u4e86\u4e00\u756a\uff0c\u4e00\u62ac\u773c\u89c1\u8f9b\u6377\u6b63\u77aa\u7740\u4ed6\uff0c \u5413\u5f97\u5fd9\u966a\u7b11\u9053\uff1a \u3000\u3000\u201c\u4e24\u4f4d\u82f1\u96c4\u53ef\u662f\u63a5\u4e86\u6b66\u5f53\u8d64\u9633\u9053\u957f\u7684\u9080\u8bf7\u8981\u4e0a\u594e\u5c71\u7684\uff1f\u201d \u3000\u3000\u8f9b\uff0c\u5434\u4e8c\u4eba\u4e0d\u89c9\u4e00\u6014\uff0c\u8f9b\u6377\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u8d64\u9633\u9053\u957f\uff1f\u4e0a\u594e\u5c71\u5e72\u4e48\u554a\uff1f\u201d \u3000\u3000\u90a3\u5c0f\u4e8c\u5475\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u539f\u6765\u4e8c\u4f4d\u7237\u8fd8\u4e0d\u77e5\u9053\u5440\uff0c\u8fd9\u4e8b\u7aef\u7684\u662f\u8f70\u52a8\u5929\u4e0b\u54e9\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u8981\u8f70\u52a8\u5929\u4e0b\u554a\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u9053\uff1a\u201c\u8fd9\u51e0\u5929\u6210\u5343\u7684\u82f1\u96c4\u597d\u6c49\u90fd\u8def\u8fc7\u54b1\u4eec\u8fd9\u91cc\u8d76\u5f80\u594e\u5c71\uff0c\u5c0f\u7684\u662f\u542c\u51e0\u4f4d\u82f1\u96c4 \u5728\u8fd9\u5e97\u91cc\u8c08\u5929\u624d\u77e5\u9053\u7684\uff0c\u8bf4\u662f\u90a3\u8d64\u9633\u9053\u957f\u53d1\u4e86\u8bf7\u8d34\u9080\u8bf7\u5929\u4e0b\u82f1\u96c4\u805a\u96c6\u594e\u5c71\uff0c\u8bf4\u8981\u5408\u529b\u5bf9 \u4ed8\u4e24\u4e2a\u4ec0\u4e48\u897f\u65b9\u5937\u65cf\u6765\u7684\u4eba\u7269\uff0c\u6211\u8bf4\u8fd9\u5c31\u602a\u5566\uff0c\u4e24\u4e2a\u5916\u56fd\u86ee\u5b50\u6765\u4e86\u4e5f\u8981\u60ca\u52a8\u8fd9\u8bb8\u591a\u82f1\u96c4 \u597d\u6c49\u53bb\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u542c\u5f97\u4e0d\u8010\uff0c\u95ee\u9053\uff1a\u201c\u662f\u4ec0\u4e48\u6837\u7684\u86ee\u5b50\u554a\uff1f\u201d \u3000\u3000\u5e97\u5c0f\u4e8c\u539f\u662f\u8981\u5356\u5f04\u81ea\u5df1\u89c1\u8bc6\u7684\u610f\u601d\uff0c\u5176\u5b9e\u5bf9\u771f\u76f8\u4e5f\u4e0d\u751a\u4e86\u89e3\uff0c\u8fd9\u65f6\u8f9b\u6377\u4e00\u95ee\uff0c\u4ed6\u5fd9 \u7740\u6293\u5934\u6414\u8111\uff0c\u4e0d\u77e5\u56de\u7b54\uff0c\u5ffd\u89c1\u95e8\u53e3\u4e00\u4e2a\u6b66\u6797\u4eba\u7269\u8d70\u8fdb\uff0c\u5fd9\u53eb\u9053\uff1a\u201c\u5c0f\u7684\u8fd8\u662f\u542c\u8fd9\u4f4d\u7237\u8bf4 \u7684\u5462\uff0c\u4f60\u4eec\u95ee\u8fd9\u4f4d\u7237\u4ed6\u51c6\u77e5\u9053\u5f97\u6e05\u695a\u3002\u201d\u81ea\u5df1\u5374\u4e00\u6e9c\u70df\u5730\u8dd1\u4e86\u3002 \u3000\u3000\u90a3\u4eba\u542c\u5c0f\u4e8c\u7684\u8bdd\uff0c\u4e0d\u89c9\u4e00\u6014\uff0c\u53ca\u89c1\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u4e8c\u4eba\u6c14\u8d28\u8f69\u6602\uff0c\u5fd9\u4e00\u62b1\u62f3\u9053\uff1a\u201c\u9601 \u4e0b\u6709\u4f55\u4e8b\u60f3\u95ee\u5728\u4e0b\uff1f\u201d \u3000\u3000\u5434\u51cc\u98ce\u5fd9\u8d77\u8eab\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u9053\uff1a\u201c\u54b1\u4eec\u5728\u8bf4\u90a3\u4e24\u4e2a\u5916\u56fd\u86ee\u5937\u7684\u4e0d\u8bc6\u597d\u6b79\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u6697\u8d5e\u5434\u5927\u54e5\u7b54\u5f97\u5999\u6781\u3002 \u3000\u3000\u90a3\u6c49\u5b50\u679c\u7136\u4ee5\u4e3a\u8f9b\u5434\u4e8c\u4eba\u4e5f\u662f\u8981\u4e0a\u594e\u5c71\u7684\uff0c\u9042\u9053\uff1a\u201c\u662f\u554a\uff0c\u54b1\u4eec\u8fd9\u6b21\u8981\u662f\u8d4c\u6597\u8f93\u4e86\uff0c \u90a3\u4e48\u4e2d\u539f\u6b66\u6797\u4eba\u7269\u53ef\u5c31\u6c38\u8fdc\u7ffb\u4e0d\u5f97\u8eab\u5566\u2014\u2014\u201d \u3000\u3000\u8f9b\u5434\u4e8c\u4eba\u88c5\u5f97\u4f3c\u4e4e\u65e9\u5c31\u77e5\u9053\uff0c\u4e0d\u751a\u60ca\u8bb6\u7684\u6a21\u6837\uff0c\u90a3\u4eba\u7eed\u9053\uff1a\u201c\u8bd5\u60f3\u8fd9\u4e24\u4e2a\u86ee\u5b50\u8981\u54b1 \u4eec\u4e2d\u539f\u6b66\u6797\u516c\u8ba4\u4ed6\u4eec\u7684\u4ec0\u4e48\u2018\u91d1\u4f2f\u80dc\u4f5b\u2019\u4e3a\u6b66\u6797\u76df\u4e3b\uff0c\u8fd8\u8981\u5341\u4e94\u4f4d\u6b66\u6797\u9f0e\u9f0e\u5927\u540d\u7684\u4eba\u7269 \u8ddf\u4ed6\u4eec\u56de\u53bb\u671d\u62dc\u90a3\u2018\u91d1\u4f2f\u80dc\u4f5b\u2019\uff0c\u8fd9\u7b49\u6c14\u54b1\u4eec\u600e\u4e48\u53d7\u5f97\u4f4f\uff1f\u4e0d\u8fc7\u8fd9\u6b21\u89c1\u8d64\u9633\u9053\u957f\u90a3\u90d1\u91cd \u7684\u60c5\u5f62\uff0c\u53ea\u6015\u8fd9\u4e24\u4e2a\u86ee\u5b50\u529f\u592b\u9ad8\u5f97\u5f88\u54e9\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u6697\u6012\uff0c\u53e3\u4e2d\u5374\u6f2b\u5e94\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u86ee\u5b50\u60f3\u5fc5\u662f\u51fa\u8eab\u91ce\u86ee\u4e4b\u5e2e\uff0c\u5426\u5219\u600e\u4e48\u5982\u6b64 \u6b3a\u4eba\u592a\u751a\uff1f\u201d \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u806a\u660e\u65e0\u6bd4\uff0c\u7b54\u5f97\u771f\u50cf\u662f\u8981\u4e0a\u594e\u5c71\u7684\u4eba\u4e00\u822c\uff0c\u90a3\u4eba\u679c\u7136\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u86ee\u5b50\u662f \u4ece\u5929\u4e9b\u6765\u7684\uff0c\u4ed6\u4eec\u8fd8\u8bf4\uff0c\u2018\u542c\u8bf4\u8fd1\u51e0\u5341\u5e74\u4e2d\u539f\u6700\u4e86\u5f97\u7684\u4e00\u4e2a\u662f\u6cb3\u6d1b\u4e00\u5251\u5434\u8bcf\u4e91\uff0c\u4e00\u4e2a\u662f \u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\uff0c\u53ef\u60dc\u8fd9\u4e24\u4eba\u6b7b\u4e86\uff0c\u5426\u5219\u4e5f\u597d\u53eb\u4ed6\u4eec\u89c1\u8bc6\u89c1\u8bc6\u5929\u7afa\u7684\u6b66\u827a\u3002\u2019\u5509\uff0c\u771f\u53ef \u60dc\u8fd9\u4e24\u4f4d\u5947\u4eba\u6b7b\u4e86\uff0c\u5426\u5219\u5012\u597d\u53eb\u8fd9\u86ee\u5b50\u89c1\u8bc6\u89c1\u8bc6\u4e2d\u539f\u7684\u6b66\u827a\u54e9\uff01\u201d \u3000\u3000\u4e24\u4eba\u542c\u5f97\u5fc3\u4e2d\u66f4\u6012\uff0c\u53e3\u5934\u5374\u652f\u543e\u4e86\u51e0\u53e5\uff0c\u5c31\u4f1a\u5e10\u800c\u51fa\u3002 \u3000\u3000\u5230\u4e86\u8def\u4e0a\uff0c\u8f9b\u6377\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u5929\u5170\u6765\u7684\u86ee\u5b50\u597d\u6a2a\uff0c\u54b1\u4eec\u7d22\u6027\u5230\u594e\u5c71\u53bb\u8ba9\u4ed6\u89c1\u8bc6\u89c1\u8bc6 \u6cb3\u6d1b\u4e00\u5251\u548c\u4e03\u5999\u795e\u541b\u7684\u529f\u592b\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u9053\uff1a\u201c\u54b1\u8fd9\u51e0\u65e5\u8d76\u8def\u6253\u5c71\u8def\u5c0f\u5f84\u91cc\u8d70\uff0c\u51fa\u4e86\u8fd9\u4e48\u4e00\u6869\u5927\u4e8b\u7adf\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4e8e\u662f\u4e24\u4eba\u6253\u542c\u4e86\u594e\u5c71\u7684\u8def\u5f84\uff0c\u4e00\u8def\u524d\u5f80\u3002 \u3000\u3000\u594e\u5c71\u4e0a\uff0c\u91d1\u78a7\u8f89\u714c\u5730\u77d7\u7acb\u7740\u4e00\u6240\u5927\u9053\u89c2\uff0c\u5c4b\u6a90\u53c2\u5dee\u3002\u74e6\u693d\u6bd4\u90bb\uff0c\u4e43\u662f\u6b66\u5f53\u6d3e\u5728\u5317\u65b9 \u6700\u5927\u7684\u4e00\u6240\u9053\u89c2\uff0c\u6b63\u4e2d\u201c\u65e0\u4e3a\u5385\u201d\u4e2d\u51e0\u767e\u4eba\u6b63\u70ed\u95f9\u5730\u8c08\u7740\uff0c\u8fd9\u4e9b\u5dee\u4e0d\u591a\u90fd\u662f\u6b66\u6797\u77e5\u540d\u4e4b \u58eb\uff0c\u63a5\u4e86\u6b66\u5f53\u638c\u95e8\u8d64\u9633\u9053\u957f\u7684\u9080\u8bf7\u8d76\u6765\u7684\u3002 \u3000\u3000\u4e0a\u5c71\u7684\u8def\u4e0a\u4e5f\u8fd8\u6709\u8bb8\u591a\u597d\u6c49\u9646\u7eed\u8d76\u5230\uff0c\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u5c31\u6df7\u5728\u4eba\u7fa4\u4e2d\uff0c\u8ddf\u7740\u5927\u4f19\u513f\u4e0a \u5c71\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5929\u4e9b\u6765\u7684\u5937\u4eba\u5e76\u6ca1\u6709\u8bf4\u8981\u4e2d\u539f\u5341\u4e94\u4e2a\u5927\u540d\u5bb6\u8ddf\u4ed6\u4eec\u56de\u53bb\u671d\u62dc\uff0c\u53ea\u662f\u8bf4\u4e86\u4e94 \u5927\u5251\u6d3e\u638c\u95e8\uff0c\u800c\u8d64\u9633\u9053\u957f\u786c\u628a\u5173\u4e2d\u4e5d\u8c6a\u548c\u5173\u5916\u4e09\u7701\u76df\u4e3b\u201c\u8fb9\u585e\u5927\u4fa0\u201d\u98ce\u67cf\u6768\u4e00\u9f50\u62c9\u4e0a\uff0c \u51d1\u6210\u5341\u4e94\u4eba\uff0c\u662f\u60f3\u6fc0\u8d77\u5929\u4e0b\u6b66\u6797\u540c\u4ec7\u654c\u60c5\u4e4b\u5fc3\uff0c\u514d\u5f97\u5929\u4e1d\u602a\u5ba2\u4e13\u95e8\u5bf9\u4ed8\u4e94\u5927\u5251\u6d3e\u3002 \u3000\u3000\u4ed6\u867d\u77e5\u201c\u8fb9\u585e\u5927\u4fa0\u201d\u98ce\u67cf\u6768\u5728\u5173\u5916\u53e6\u6210\u4e00\u6d3e\uff0c\u4e0e\u4e2d\u539f\u7d20\u4e0d\u76f8\u5e72\uff0c\u5fc5\u4e0d\u4f1a\u524d\u6765\uff0c\u4f46\u5fc3 \u60f3\u5982\u80fd\u62c9\u4e0a\u5173\u4e2d\u4e5d\u8c6a\u4e5f\u5c31\u5b9e\u529b\u5927\u589e\u4e86\uff0c\u4f46\u4ed6\u90a3\u91cc\u53c8\u4f1a\u60f3\u5230\u5173\u4e2d\u4e5d\u8c6a\u5df2\u88ab\u8f9b\u6377\u4e00\u6218\u62fc\u5f97\u6b7b \u4f24\u8fde\u8fde\uff0c\u4e5d\u8c6a\u53ea\u5269\u4e0b\u4e86\u516d\u8c6a\u4e86\u54e9\uff01 \u3000\u3000\u8f9b\u6377\u7684\u4e0a\u5c71\u5e76\u975e\u8981\u4e3a\u4e94\u5927\u5251\u6d3e\u52a9\u62f3\uff0c\u4e3b\u8981\u8fd8\u662f\u56e0\u4e3a\u5929\u7afa\u6765\u4eba\u72c2\u8a00\u4e0d\u60ed\uff0c\u8fb1\u53ca\u6cb3\u6d1b\u4e00 \u5251\u548c\u4e03\u5999\u795e\u541b\uff0c\u800c\u4e14\u4ed6\u5fc3\u60f3\u4e94\u5927\u5251\u6d3e\u5fc5\u4e5f\u805a\u4e8e\u4e00\u5385\uff0c\u5230\u65f6\u6b63\u597d\u4e00\u4e86\u65e7\u5e10\uff0c\u514d\u5f97\u81ea\u5df1\u518d\u56db \u5904\u5954\u6ce2\u3002 \u3000\u3000\u4e0d\u4e00\u4f1a\uff0c\u5927\u4f19\u89c1\u90fd\u8fdb\u4e86\u201c\u65e0\u4e3a\u5385\u201d\uff0c\u8f9b\u6377\u773c\u5c16\uff0c\u65e9\u89c1\u53f0\u4e0a\u5750\u7740\u6b66\u5f53\u7684\u8d64\u9633\u9053\u957f\u3001\u5ce8 \u5a9a\u7684\u82e6\u5eb5\u4e0a\u4eba\u548c\u90a3\u70b9\u82cd\u7684\u843d\u82f1\u5251\u8c22\u957f\u537f\uff0c\u5374\u4e0d\u89c1\u76d7\u4e86\u6885\u9999\u5251\u7684\u5389\u9e57\u3002 \u3000\u3000\u5434\u3001\u8f9b\u4e8c\u4eba\u6df7\u5728\u7fa4\u4f17\u4e2d\uff0c\u62e3\u4e86\u4e00\u5904\u4e0d\u663e\u773c\u7684\u5730\u65b9\u7acb\u5b9a\uff0c\u89c1\u56db\u5468\u4e71\u54c4\u54c4\u7684\uff0c\u65e0\u4eba\u6ce8\u610f \u4ed6\u4eec\uff0c\u8f9b\u6377\u8fd9\u624d\u9053\uff1a\u201c\u5927\u54e5\uff0c\u65b9\u624d\u4e0a\u5c71\u65f6\u4f60\u53ef\u770b\u89c1\u4e00\u6761\u4eba\u5f71\u5728\u5c71\u4e0b\u75be\u5954\u800c\u6765\uff1f\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u662f\u554a\uff0c\u6211\u77a7\u90a3\u4eba\u8f7b\u529f\u4fca\u6781\uff0c\u53ea\u662f\u65b9\u624d\u4e0d\u4fbf\u8bf4\u8bdd\uff0c\u6240\u4ee5\u6ca1\u51fa\u58f0\u3002\u201d \u3000\u3000\u8f9b\u6377\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u77a7\u90a3\u4eba\u5f71\u4e5d\u6210\u662f\u90a3\u2018\u6b66\u6797\u4e4b\u79c0\u2019\u2014\u2014\u201d \u3000\u3000\u51cc\u98ce\u66fe\u542c\u8f9b\u6377\u8bf4\u8fc7\u201c\u6b66\u6797\u4e4b\u79c0\u201d\u53ca\u5c11\u6797\u548c\u5c1a\u7cca\u91cc\u7cca\u6d82\u5730\u548c\u8f9b\u6377\u8fc7\u62db\u7684\u4e8b\uff0c\u5fc3\u9053\uff1a \u201c\u96be\u602a\u8fd9\u2018\u6b66\u6797\u4e4b\u79c0\u2019\u80fd\u548c\u8f9b\u6377\u6597\u4e2a\u65d7\u9f13\u76f8\u5f53\uff0c\u770b\u6765\u8f7b\u529f\u679c\u7136\u4e86\u5f97\u2014\u2014\u96be\u9053\u4ed6\u4e5f\u662f\u8d64\u9633 \u9053\u957f\u8bf7\u6765\u7684\uff1f\u201d \u3000\u3000\u4ed6\u81ea\u670d\u8840\u679c\u4ee5\u6765\uff0c\u8f7b\u8eab\u529f\u592b\u6700\u662f\u5927\u8fdb\uff0c\u8fd9\u4e00\u8def\u6765\u66fe\u548c\u8f9b\u6377\u8d5b\u8fc7\u811a\u7a0b\uff0c\u7adf\u548c\u8f9b\u6377\u7684 \u201c\u6697\u9999\u63a0\u5f71\u201d\u7edd\u6280\u76f8\u5dee\u65e0\u51e0\uff0c\u8f9b\u6377\u4e5f\u4e3a\u4ed6\u8fd9\u79cd\u5343\u8f7d\u96be\u9022\u7684\u4ed9\u7f18\u5e86\u5e78\u4e0d\u5df2\uff0c\u7136\u800c\u4ed6\u600e\u77e5\u51cc \u98ce\u66fe\u4e3a\u670d\u4e0b\u90a3\u8840\u679c\u9669\u4e9b\u81ea\u8d23\u5bfb\u6b7b\u54e9\uff01 \u3000\u3000\u5ffd\u7136\uff0c\u4e00\u4e2a\u9752\u5e74\u9053\u58eb\u8dd1\u6765\uff0c\u60f3\u662f\u6b66\u5f53\u95e8\u4e0b\u7684\u5f1f\u5b50\uff0c\u4ed6\u5bf9\u8d64\u9633\u9053\u957f\u8bf4\u4e86\u53e5\u8bdd\uff0c\u8d64\u9633\u9053 \u957f\u8138\u8272\u4e00\u53d8\uff0c\u7ad9\u8d77\u8eab\u6765\u6717\u58f0\u9053\uff1a\u201c\u5404\u4f4d\u9759\u4e00\u9759\u3002\u201d \u3000\u3000\u4ed6\u7684\u5185\u529b\u751a\u5f3a\uff0c\u58f0\u97f3\u5982\u6d2a\u949f\u822c\u76d6\u8fc7\u4f17\u4eba\u5608\u6742\u4e4b\u58f0\uff0c\u7fa4\u8c6a\u7acb\u523b\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u63a5\u7740\u9053\uff1a\u201c\u5929\u7afa\u9ad8\u624b\u5df2\u7ecf\u5230\u4e34\u2014\u2014\u201d \u3000\u3000\u201c\u65e0\u4e3a\u5385\u201d\u4e0a\u987f\u65f6\u8083\u9759\u4e0b\u6765\uff0c\u8d64\u9633\u9053\u957f\u4e3e\u624b\u4e00\u6325\uff0c\u95e8\u4e0b\u4e24\u4e2a\u9752\u5e74\u9053\u58eb\u8d70\u5230\u5385\u95e8\u53e3\uff0c \u5927\u95e8\u4e00\u5f00\uff0c\u4e24\u4e2a\u5de8\u4eba\u51b2\u4e86\u8fdb\u6765\uff0c\u4f17\u4eba\u770b\u65f6\uff0c\u53ea\u89c1\u8fd9\u4e24\u4eba\u597d\u4e0d\u5e9e\u5927\uff0c\u524d\u9762\u4e00\u4eba\u4e0a\u8eab\u5947\u957f\uff0c \u6015\u4e0d\u6709\u4e94\u516d\u5c3a\u4e4b\u957f\uff0c\u518d\u52a0\u4e0a\u53cc\u817f\uff0c\u5168\u8eab\u51e0\u4e4e\u5c31\u6709\u4e08\u4f59\uff0c\u540e\u9762\u4e00\u4eba\u867d\u7136\u4e5f\u662f\u8eab\u9ad8\u8180\u5706\uff0c\u4f46 \u662f\u8eab\u7740\u4e00\u88ad\u5112\u670d\uff0c\u66f4\u52a0\u767d\u9762\u65e0\u9b13\uff0c\u662f\u4ee5\u663e\u5f97\u6587\u96c5\u5f97\u591a\u3002 \u3000\u3000\u5f53\u5148\u58ee\u6c49\u8eab\u4e0a\u7a7f\u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u4f46\u5934\u9876\u5374\u662f\u4e00\u9897\u548c\u5c1a\u5149\u5934\uff0c\u4ed6\u8ff8\u6765\u4ee5\u540e\u5c31\u5f15\u9888\u56db\u987e\uff0c \u4f3c\u4e4e\u662f\u5728\u5bfb\u627e\u4ec0\u4e48\u4eba\uff0c\u4f46\u662f\u4ed6\u7684\u773c\u5149\u56db\u5904\u4e00\u5c04\u4e4b\u540e\uff0c\u9762\u4e0a\u5ffd\u7136\u9732\u51fa\u5931\u671b\u4e4b\u8272\uff0c\u8f6c\u9996\u5bf9\u540e \u9762\u7684\u201c\u5112\u751f\u201d\u9053\uff1a\u201c\u963f\u559c\u7c73\uff0c\u4f30\u4ec0\u6469\u8bc3\u5c14\uff0c\u4e4c\u6cd5\u5404\u5404\u54e9\u67e5\u3002\u201d \u3000\u3000\u58f0\u97f3\u6709\u5982\u7834\u9523\uff0c\u4f17\u4eba\u90fd\u611f\u4e00\u9635\u8033\u9e23\uff0c\u529f\u529b\u6d45\u7684\u53ea\u89c9\u8033\u4e2d\u55e1\u55e1\u76f4\u54cd\uff0c\u597d\u534a\u5929\u542c\u4e0d\u89c1\u522b \u7684\u58f0\u97f3\u3002 \u3000\u3000\u90a3\u5112\u751f\u6253\u626e\u7684\u5937\u4eba\u7528\u624b\u5f80\u524d\u4e00\u6307\uff0c\u793a\u610f\u8981\u4ed6\u5230\u524d\u9762\u4ed4\u7ec6\u627e\u4e00\u627e\u3002 \u3000\u3000\u8fd9\u58ee\u6c49\u679c\u7136\u524d\u884c\u6324\u4eba\u4eba\u4e1b\uff0c\u4e1c\u63a8\u897f\u649e\uff0c\u88ab\u649e\u8005\u65e0\u4e0d\u4ef0\u5929\u7ffb\u5012\uff0c\u5475\u5475\u53eb\u75db\uff0c\u90a3\u58ee\u6c49\u5374 \u4f3c\u6ca1\u4e8b\u4e00\u822c\uff0c\u4f9d\u7136\u5728\u4eba\u7fa4\u4e2d\u4e1c\u7a7f\u897f\u7a7f\uff0c\u6beb\u65e0\u793c\u6570\u3002 \u3000\u3000\u6e10\u6e10\u90a3\u58ee\u6c49\u8d70\u5230\u5434\u51cc\u98ce\u8eab\u65c1\uff0c\u5434\u51cc\u98ce\u6697\u4e2d\u4e00\u54fc\uff0c\u771f\u529b\u8d2f\u6ce8\u53cc\u817f\uff0c\u90a3\u86ee\u5b50\u8d70\u5230\u8eab\u8fb9\uff0c \u7167\u4f8b\u5730\u4e00\u649e\uff0c\u90a3\u77e5\u660e\u660e\u649e\u7740\u5434\u51cc\u98ce\u7684\u8eab\u8eaf\uff0c\u5374\u5982\u649e\u5927\u4e00\u5806\u68c9\u82b1\uff0c\u5fc3\u4e2d\u6697\u53eb\u4e0d\u5999\uff0c\u6b63\u8981\u6536 \u52b2\u800c\u9000\uff0c\u5ffd\u611f\u4e00\u80a1\u67d4\u6e29\u7684\u52b2\u529b\u53cd\u5f39\u4e0a\u6765\uff0c\u4ed6\u602a\u53eb\u4e00\u58f0\uff0c\u5b9b\u5982\u6674\u5929\u4e00\u4e2a\u5927\u9739\u96f3\uff0c\u786c\u786c\u63a8\u51fa \u4e00\u638c\uff0c\u90a3\u77e5\u90a3\u9634\u67d4\u4e4b\u52b2\u7a81\u7136\u53c8\u6d88\u4e8e\u65e0\u5f62\uff0c\u5927\u4e2a\u5b50\u51b2\u51fa\u4e24\u6b65\u624d\u7a33\u7a33\u7ad9\u4f4f\u3002\u4ed6\u7741\u7740\u602a\u773c\u72e0\u72e0 \u76ef\u4f4f\u5434\u51cc\u98ce\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4e00\u770b\u5c31\u77e5\u5434\u5927\u54e5\u5df2\u628a\u592a\u6781\u95e8\u201c\u4ee5\u67d4\u5236\u521a\u201d\u7684\u8981\u51b3\u5e94\u7528\u5230\u968f\u5fc3\u6240\u6b32\u7684\u5883\u754c\u4e86\uff0c\u5fc3 \u4e2d\u7740\u5b9e\u4e3a\u4ed6\u6b22\u559c\uff0c\u4e0d\u7981\u9ad8\u58f0\u53eb\u597d\u3002 \u3000\u3000\u51cc\u98ce\u5bf9\u4ed6\u56de\u89c6\uff0c\u4e8c\u4eba\u76f8\u5bf9\u4e00\u7b11\uff0c\u53cb\u60c5\u7684\u6e29\u6696\u5728\u4e24\u4eba\u8fd9\u4e00\u7b11\u4e4b\u95f4\u6084\u6084\u5730\u900f\u5165\u5bf9\u65b9\u7684\u5fc3 \u623f\u3002 \u3000\u3000\u90a3\u201c\u5112\u751f\u201d\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u4e0d\u6599\u4e2d\u539f\u8fd8\u771f\u6709\u4e9b\u4eba\u6750\u5462\u2014\u2014\u201d\u4ed6\u7684\u6c49\u8bed\u7adf\u662f\u5341\u5206\u6d41\u5229\u3002 \u3000\u3000\u5f53\u4ed6\u7684\u773c\u5149\u843d\u5728\u5434\u51cc\u98ce\u7684\u8138\u4e0a\u65f6\uff0c\u4e0d\u7981\u6014\u4f4f\u4e86\uff0c\u4ed6\u6697\u4e2d\u81ea\u8bed\uff1a\u201c\u60f3\u4e0d\u5230\u4e2d\u539f\u7adf\u6709\u8fd9 \u822c\u4fca\u79c0\u4eba\u7269\u2014\u2014\u201d\u4ed6\u4e00\u5411\u81ea\u4ee5\u4e3a\u82f1\u4fca\u6e05\u6d12\uff0c\u5728\u90a3\u86ee\u5937\u4e4b\u90a6\u4e2d\u81ea\u7136\u662f\u6709\u5982\u9e64\u7acb\u9e21\u7fa4\uff0c\u4f46\u662f \u4e0e\u5434\u51cc\u98ce\u8fd9\u7b49\u7edd\u4e16\u7f8e\u7537\u5b50\u76f8\u8f83\u4e4b\u4e0b\uff0c\u90a3\u5c31\u9eef\u7136\u5931\u8272\u4e86\u3002 \u3000\u3000\u90a3\u201c\u5112\u751f\u201d\u4e00\u62db\u624b\u53eb\u56de\u90a3\u86ee\u5b50\uff0c\u6717\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u5144\u5f1f\u4e45\u6155\u4e2d\u539f\u6b66\u5b66\uff0c\u4eca\u65e5\u4e2d\u539f\u8c6a\u4fa0 \u9f50\u805a\u4e00\u5ba4\uff0c\u6b63\u597d\u4ee4\u54b1\u4eec\u5144\u5f1f\u4e00\u5f00\u773c\u754c\uff0c\u540c\u65f6\uff0c\u54b1\u4eec\u613f\u610f\u5728\u8fd9\u91cc\u5019\u6559\u4e24\u573a\uff0c\u53ea\u8981\u54b1\u4eec\u8d25\u4e86 \u4e00\u573a\uff0c\u6211\u5144\u5f1f\u4e24\u4eba\u7acb\u523b\u6389\u5934\u8d70\u8def\uff0c\u8981\u662f\u6211\u4eec\u4e8c\u573a\u5168\u80dc\uff0c\u54c8\u54c8\uff0c\u4e0b\u9762\u7684\u8bdd\u65e9\u5df2\u544a\u8bc9\u6b66\u5f53\u8d64 \u9633\u9053\u957f\u4e86\u2014\u2014\u201d \u3000\u3000\u5ea7\u4e2d\u7fa4\u8c6a\u8038\u7136\u52a8\u5bb9\uff0c\u867d\u7136\u5fc3\u4e2d\u6012\u6781\uff0c\u4f46\u89c1\u90a3\u4e2a\u5937\u4eba\u5206\u660e\u6b66\u827a\u7edd\u9ad8\uff0c\u5426\u5219\u5c82\u6562\u53e3\u51fa\u72c2 \u8a00\uff1f \u3000\u3000\u53f0\u524d\u7684\u8d64\u9633\u9053\u957f\u5bf9\u5ea7\u65c1\u7684\u82e6\u5eb5\u4e0a\u4eba\u548c\u8c22\u957f\u537f\u9053\uff1a\u201c\u4eca\u65e5\u662f\u54b1\u4eec\u4e94\u5927\u5251\u6d3e\u751f\u6b7b\u5b58\u4ea1\u7684 \u5173\u5934\u4e86\uff0c\u82e5\u662f\u6211\u4eec\u51e0\u4eba\u8d25\u4e86\u2026\u2026\u5509\uff0c\u4e0d\u5fc5\u8bf4\u4e86\u3002\u201d \u3000\u3000\u8d64\u9633\u9053\u957f\u60f3\u5230\u81ea\u5df1\u4e00\u751f\u884c\u4e8b\uff0c\u9887\u505a\u4e86\u51e0\u4ef6\u4e0d\u5149\u4e0d\u91c7\u7684\u4e8b\u60c5\uff0c\u96be\u9053\u5802\u5802\u6b66\u5f53\u4e00\u6d3e\u5c31\u8981 \u56e0\u6b64\u800c\u65ad\u9001\uff1f \u3000\u3000\u5ce8\u5a9a\u82e6\u5eb5\u4e0a\u4eba\u4f4e\u9996\u5ba3\u4e86\u4e00\u58f0\u4f5b\u53f7\uff0c\u51db\u7136\u9053\uff1a\u201c\u8bf4\u4e0d\u5f97\u54b1\u4eec\u53ea\u597d\u628a\u51e0\u6839\u8001\u9aa8\u5934\u62fc\u4e0a\u4e86\uff0c \u54b1\u4eec\u6dfb\u4e3a\u6b66\u6797\u4e94\u5927\u5b97\u6d3e\u638c\u95e8\u4eba\uff0c\u82e5\u662f\u4e0d\u8eab\u5148\u58eb\u5352\uff0c\u53ea\u6015\u8981\u4ee4\u5929\u4e0b\u597d\u6c49\u9f7f\u51b7\u2014\u2014\u201d \u3000\u3000\u70b9\u82cd\u7684\u843d\u82f1\u5251\u8c22\u957f\u537f\u4f3c\u4e4e\u5fc3\u4e8b\u91cd\u91cd\uff0c\u59cb\u7ec8\u4e0d\u89c1\u4ed6\u5f00\u53e3\u3002 \u3000\u3000\u8d64\u9633\u9053\u957f\u9053\uff1a\u201c\u5389\u5144\u600e\u4e48\u8fd8\u6ca1\u6709\u6765\uff0c\u5426\u5219\u51ed\u4ed6\u90a3\u624b\u5d06\u5cd2\u795e\u5251\u5f53\u53ef\u6253\u5934\u4e00\u9635\uff0c\u632b\u632b\u4ed6 \u4eec\u7684\u9510\u6c14\u3002\u201d \u3000\u3000\u90a3\u5112\u751f\u6253\u626e\u7684\u5937\u4eba\u5927\u58f0\u9053\uff1a\u201c\u7b2c\u4e00\u573a\u7531\u6211\u5e08\u5144\u52a0\u5927\u5c14\u51fa\u9635\uff0c\u4e2d\u539f\u82f1\u96c4\u54ea\u4f4d\u51fa\u573a\uff1f\u201d \u3000\u3000\u4ed6\u5185\u529f\u679c\u7136\u6df1\u539a\uff0c\u4e00\u5b57\u4e00\u5b57\u8bf4\u51fa\uff0c\u9707\u5f97\u5c4b\u74e6\u7c0c\u7c0c\u800c\u52a8\uff0c\u4f17\u4eba\u90fd\u662f\u884c\u5bb6\uff0c\u4e00\u542c\u5c31\u77e5\u4ed6 \u867d\u662f\u90a3\u86ee\u5b50\u7684\u5e08\u5f1f\uff0c\u529f\u529b\u53ea\u6015\u72b9\u5728\u52a0\u5927\u5c14\u4e4b\u4e0a\u3002 \u3000\u3000\u8d64\u9633\u9053\u957f\u89c1\u5d06\u5cd2\u5389\u9e57\u59cb\u7ec8\u4e0d\u66fe\u8d76\u5230\uff0c\u5fc3\u4e2d\u7126\u6025\uff0c\u53c8\u4e0d\u597d\u610f\u601d\u53eb\u82e6\u5eb5\u4e0a\u4eba\u51fa\u9635\uff0c\u4e00\u6025 \u4e4b\u4e0b\uff0c\u53ea\u597d\u51c6\u5907\u4eb2\u81ea\u51fa\u9635\u2014\u2014\u82e6\u5eb5\u4e0a\u4eba\u4e00\u628a\u626f\u4f4f\u4ed6\u7684\u9053\u888d\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8fd8\u662f\u8ba9\u8001\u8872\u53bb\u63a5 \u8fd9\u86ee\u5b50\u51e0\u62db\u5427\uff0c\u9053\u957f\u548c\u8c22\u8d24\u4f84\u8bf7\u4e3a\u6211\u63a0\u9635\u3002\u201d \u3000\u3000\u8d64\u9633\u9053\u957f\u53ee\u5631\u9053\uff1a\u201c\u6b64\u5f79\u5173\u7cfb\u975e\u540c\u5c0f\u53ef\uff0c\u4e0a\u4eba\u5343\u4e07\u4e0d\u8981\u5b58\u5ba2\u6c14\u4e4b\u5fc3\u3002\u201d \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u66f4\u4e0d\u7b54\u8bdd\uff0c\u7f13\u7f13\u7ad9\u8d77\u6b65\u5165\u5927\u5385\uff0c\u53e3\u4e2d\u9053\uff1a \u3000\u3000\u201c\u8d2b\u50e7\u5ce8\u5d4b\u82e6\u5eb5\uff0c\u613f\u63a5\u8fd9\u4f4d\u52a0\u65bd\u4e3b\u7684\u9ad8\u62db\u3002\u201d \u3000\u3000\u4ed6\u58f0\u97f3\u867d\u5c0f\uff0c\u4f46\u5374\u4ee4\u5168\u573a\u6bcf\u4e2a\u4eba\u8033\u4e2d\u542c\u5f97\u4e00\u6e05\u4e8c\u695a\uff0c\u663e\u793a\u8001\u548c\u5c1a\u5185\u529f\u4fee\u4e3a\u786e\u662f\u4e0d\u51e1\u3002 \u3000\u3000\u90a3\u9ad8\u58ee\u86ee\u5b50\u52a0\u5927\u5c14\u4e00\u89c1\u82e6\u5eb5\u4e0a\u4eba\uff0c\u795e\u8272\u4e00\u53d8\uff0c\u5e76\u53cd\u95ee\u4ed6\u5e08\u5f1f\u9053\uff1a\u201c\u5404\u5e0c\u7c73\u5c14\uff0c\u96c5\u534e \u5df4\u62c9\u53ef\u8036\uff1f\u201d \u3000\u3000\u4ed6\u5e08\u5f1f\u4e5f\u6253\u91cf\u4e86\u82e6\u5eb5\u4e00\u773c\uff0c\u6447\u4e86\u6447\u5934\u9053\uff1a\u201c\u5f17\u5e0c\u54e9\uff0c\u5e0c\u963f\u7f57\u5ce8\u5d4b\u66f4\u5df4\u3002\u201d \u3000\u3000\u86ee\u5b50\u8138\u4e0a\u53c8\u9732\u51fa\u5931\u671b\u4e4b\u8272\u3002\u4f17\u4eba\u53ea\u542c\u61c2\u201c\u5ce8\u5d4b\u201d\u4e24\u5b57\uff0c\u53ea\u4f9d\u7a00\u611f\u89c9\u51fa\u90a3\u52a0\u5927\u5c14\u4e43\u662f \u5411\u4ed6\u5e08\u5f1f\u8bf4\u4e00\u4ef6\u6709\u5173\u82e6\u5eb5\u4e0a\u4eba\u7684\u4e8b\uff0c\u800c\u4ed6\u5e08\u5f1f\u5374\u662f\u56de\u7b54\u4e86\u5426\u5b9a\u7684\u7b54\u6848\u3002 \u3000\u3000\u7fa4\u96c4\u90fd\u77e5\u8fd9\u4e00\u6218\u4e43\u662f\u6709\u5173\u5929\u4e0b\u6b66\u6797\u7684\u5174\u4ea1\u524d\u9014\uff0c\u65e0\u4e0d\u5168\u795e\u8d2f\u6ce8\uff0c\u800c\u4e14\u6bcf\u4e2a\u4eba\u90fd\u5e0c\u671b \u82e6\u5eb5\u4e0a\u4eba\u4e00\u62f3\u5f97\u80dc\uff0c\u5c3d\u7ba1\u4f17\u4eba\u4e2d\u4e5f\u6709\u548c\u4e94\u5927\u5251\u6d3e\u6709\u6881\u5b50\u7684\uff0c\u4f46\u662f\u5728\u6b64\u5229\u5bb3\u76f8\u540c\u7684\u60c5\u5f62\u4e0b\uff0c \u5c31\u90fd\u5e0c\u671b\u82e6\u5eb5\u4e0a\u4eba\u5feb\u5feb\u5f97\u80dc\u4e86\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u8d70\u81f3\u52a0\u5927\u5c14\u9762\u524d\uff0c\u5408\u5341\u4e3a\u793c\uff0c\u53cc\u76ee\u51dd\u89c6\u5bf9\u65b9\uff0c\u5168\u795e\u8d2f\u6ce8\u4ee5\u5f85\u3002 \u3000\u3000\u90a3\u52a0\u5927\u5c14\u66f4\u4e0d\u6253\u8bdd\uff0c\u66b4\u542d\u4e00\u58f0\uff0c\u5f53\u80f8\u5c31\u662f\u4e00\u62f3\u6253\u51fa\uff0c\u4ed6\u90a3\u543c\u58f0\u624d\u51fa\uff0c\u62f3\u98ce\u5df2\u5230\uff0c\u800c \u4e14\u51cc\u5389\u4e4b\u6781\u3002 \u3000\u3000\u82e6\u5eb5\u4e00\u542c\u4ed6\u62f3\u98ce\u5c31\u7ec6\u52a0\u5927\u5c14\u5b8c\u5168\u662f\u5916\u5bb6\u8def\u5b50\uff0c\u4f46\u662f\u52b2\u9053\u4e4b\u5f3a\u7aef\u7684\u5e73\u751f\u4ec5\u89c1\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u5728\u4e94\u5927\u5251\u6d3e\u4e2d\u539f\u4ee5\u5185\u529b\u4fee\u4e3a\u79f0\u8457\uff0c\u5e73\u751f\u5927\u5c0f\u62fc\u6597\u4e0d\u4e0b\u767e\u4f59\u573a\uff0c\u50cf\u52a0\u5927\u5c14\u8fd9 \u7b49\u5f3a\u52b2\u7684\u529b\u9053\u8fd8\u662f\u7b2c\u4e00\u6b21\u78b0\u5230\uff0c\u5f53\u4e0b\u8eab\u4f53\u4e0d\u52a8\uff0c\u53cc\u62f3\u8d70\u5f27\u7ebf\u76f4\u70b9\u52a0\u5927\u5c14\u5173\u8282\u4e24\u65c1\u7684\u201c\u9526 \u5e26\u7a74\u201d\u2014\u2014 \u3000\u3000\u54ea\u77e5\u52a0\u5927\u5c14\u8c8c\u4f3c\u7c97\u8c6a\uff0c\u53d8\u62db\u901f\u6377\u65e0\u6bd4\uff0c\u547c\u5730\u4e00\u58f0\uff0c\u5355\u81c2\u4e0b\u6c89\uff0c\u4e00\u6c89\u4e4b\u4e0b\u53c8\u7acb\u523b\u4e0a\u6311\uff0c \u786c\u8fce\u82e6\u5eb5\u4e0a\u4eba\u7684\u5939\u51fb\u4e4b\u52b2\u2014\u2014 \u3000\u3000\u52a0\u5927\u5c14\u53c8\u662f\u66b4\u543c\u4e00\u58f0\uff0c\u82e6\u5eb5\u4e0a\u4eba\u53ea\u89c9\u53cc\u81c2\u4e00\u9707\uff0c\u8fde\u5fd9\u6a2a\u8de8\u534a\u6b65\uff0c\u5316\u53bb\u654c\u52bf\uff0c\u5fc3\u4e2d\u5374 \u60ca\u5f02\u5df2\u6781\uff01 \u3000\u3000\u4e0d\u8bf4\u82e6\u5eb5\u4e0a\u4eba\uff0c\u5c31\u8fde\u4e00\u65c1\u7684\u8f9b\u6377\u53ca\u5434\u51cc\u98ce\u4e5f\u5927\u5403\u4e00\u60ca\uff0c\u8f9b\u6377\u6697\u9053\uff1a\u201c\u8fd9\u5937\u4eba\u5206\u660e\u7eaf \u662f\u5916\u5bb6\u8def\u5b50\uff0c\u600e\u4e48\u90a3\u521a\u5f3a\u4e4b\u52b2\u4e2d\u5374\u5e26\u7740\u4e00\u4e1d\u6781\u4e3a\u53e4\u602a\u7684\u9634\u67d4\u4e4b\u52b2\uff1f\u4e00\u5408\u4e4b\u4e0b\u5a01\u529b\u5927\u589e\uff0c \u8fd9\u5012\u662f\u5947\u4e86\uff0c\u96be\u602a\u4eba\u8bf4\u5937\u4eba\u6b66\u529f\u5927\u5f02\u4e2d\u539f\uff0c\u770b\u6765\u6b64\u8bed\u8bda\u4e0d\u865a\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u4f4e\u58f0\u5bf9\u8f9b\u6377\u9053\uff1a\u201c\u8fd9\u86ee\u5b50\u6b66\u529f\u5927\u662f\u53e4\u602a\uff0c\u53ea\u6015\u82e6\u5eb5\u4e0a\u4eba\u63a5\u4e0d\u4e0b\u767e\u62db\u3002\u201d \u3000\u3000\u90a3\u8fb9\u53c8\u662f\u4e00\u58f0\u9707\u5929\u5927\u543c\uff0c\u631f\u7740\u547c\u547c\u62f3\u98ce\u58f0\uff0c\u6562\u60c5\u52a0\u5927\u5c14\u6bcf\u6253\u4e00\u62f3\u5fc5\u53d1\u4e00\u58f0\u5927\u559d\u3002\u76f4 \u9707\u5f97\u4f17\u4eba\u8033\u4e2d\u55e1\u55e1\u4f5c\u54cd\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u5fc3\u9053\uff1a\u201c\u4e0e\u5176\u53d7\u5236\u4e8e\u4eba\u6328\u6253\uff0c\u4e0d\u5982\u62fc\u7740\u7528\u5185\u52b2\u548c\u4ed6\u62a2\u653b\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u51b3\uff0c\u5f53\u4e0b\u4e00\u58f0\u957f\u5578\uff0c\u53cc\u62f3\u4e00\u9519\uff0c\u5c55\u5f00\u5ce8\u5d4b\u201c\u9752\u6851\u62f3\u6cd5\u201d\uff0c\u7740\u7740\u7528\u4e0a\u771f\u529b\uff0c\u548c \u52a0\u5927\u5c14\u62a2\u653b\u8d77\u6765\u3002 \u3000\u3000\u5012\u5e95\u662f\u59dc\u8001\u800c\u5f25\u8fa3\uff0c\u4ed6\u8fd9\u8f6e\u62a2\u653b\u7684\u662f\u660e\u667a\u4e4b\u4e3e\uff0c\u4e00\u65f6\u62f3\u98ce\u638c\u5f71\uff0c\u4e8c\u4eba\u6597\u4e2a\u96be\u5206\u96be\u820d\u3002 \u3000\u3000\u8f9b\u6377\u6697\u9053\uff1a\u201c\u53ea\u6709\u8fd9\u79cd\u7ecf\u9a8c\u548c\u4e34\u654c\u673a\u53d8\uff0c\u662f\u5e08\u7236\u65e0\u6cd5\u6559\u7684\u2014\u2014\u3002\u201d \u3000\u3000\u90a3\u52a0\u5927\u5c14\u4f3c\u4e4e\u6ca1\u60f3\u5230\u4e2d\u539f\u9ad8\u624b\u771f\u6709\u4e00\u624b\uff0c\u4ed6\u6108\u6253\u6108\u662f\u5fc3\u559c\uff0c\u8138\u4e0a\u9732\u51fa\u7b11\u5bb9\uff0c\u638c\u52bf\u5374 \u8d8a\u6765\u6b32\u51cc\u5389\uff0c\u90a3\u543c\u58f0\u4e5f\u53d8\u5f97\u66f4\u54cd\u66f4\u5bc6\uff0c\u771f\u662f\u52bf\u6bd4\u5954\u96f7\uff0c\u597d\u591a\u4eba\u5fcd\u4e0d\u4f4f\u8981\u7528\u624b\u8499\u4f4f\u8033\u6735\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u8138\u4e0a\u59cb\u7ec8\u9547\u9759\u5f97\u5f88\uff0c\u62fc\u51fa\u6570\u5341\u5e74\u4fee\u4e3a\u548c\u4ed6\u62a2\u4e0a\u98ce\uff0c\u5fc3\u4e2d\u5374\u6e10\u611f\u4e0d\u5999\u2014\u2014 \u3000\u3000\u8d64\u9633\u9053\u957f\u5fc3\u4e2d\u6697\u60ca\u9053\uff1a\u201c\u8fd9\u5937\u5b50\u62f3\u811a\u597d\u751f\u5389\u5bb3\uff0c\u5e78\u597d\u6211\u65b9\u624d\u6ca1\u6709\u4e0b\u53bb\u6253\u5934\u9635\uff0c\u5426 \u5219\u2026\u2026\u771f\u4e0d\u582a\u8bbe\u60f3\uff0c\u54b1\u4eec\u4e94\u5927\u5251\u6d3e\u4e2d\u5b9e\u5728\u4e5f\u53ea\u6709\u82e6\u5eb5\u4e0a\u4eba\u80fd\u652f\u6301\u5f97\u4f4f\u2014\u2014\u3002\u6562\u60c5\u8d64\u9633\u9053 \u957f\u548c\u5251\u795e\u5389\u9e57\u90fd\u662f\u957f\u4e8e\u5251\u672f\u800c\u758f\u4e8e\u62f3\u638c\u3002 \u3000\u3000\u90a3\u5112\u751f\u6253\u626e\u7684\u5937\u4eba\u59cb\u7ec8\u795e\u6001\u81ea\u82e5\u5730\u770b\u7740\u4e2d\u539f\u7fa4\u8c6a\uff0c\u5bf9\u90a3\u8fb9\u75be\u6597\u77a7\u90fd\u4e0d\u77a7\u4e00\u773c\uff0c\u4f3c\u4e4e \u65e9\u5c31\u6599\u5b9a\u80dc\u5238\u5728\u63e1\u3002 \u3000\u3000\u521a\u521a\u62c6\u5230\u767e\u62db\u4e0a\uff0c\u90a3\u52a0\u5927\u5c14\u5927\u559d\u4e00\u58f0\u4e4b\u540e\u53c8\u602a\u53eb\u4e00\u58f0\uff0c\u5927\u7ea6\u662f\u6c49\u8bed\u201c\u7740\uff01\u201d\u7684\u610f\u601d \u2014\u2014 \u3000\u3000\u53ea\u89c1\u4ed6\u4e00\u62f3\u4ece\u51fa\u4eba\u610f\u8868\u7684\u53e4\u602a\u5730\u65b9\u6253\u51fa\uff0c\u773c\u770b\u82e6\u5eb5\u5c31\u5c06\u4e0d\u654c\uff0c\u5385\u4e2d\u7fa4\u8c6a\u5927\u60ca\u5931\u58f0\u2014 \u2014 \u3000\u3000\u4f46\u82e6\u5eb5\u4e0a\u4eba\u6570\u5341\u5e74\u529f\u529b\u975e\u540c\u5c0f\u53ef\uff0c\u5ce8\u5d4b\u201c\u795e\u884c\u8ff7\u8e2a\u6b65\u201d\u4e5f\u662f\u6b66\u6797\u4e00\u7edd\uff0c\u53ea\u89c1\u4ed6\u8fde\u8e29 \u8ff7\u8e2a\uff0c\u53ea\u80fd\u907f\u8fc7\uff01 \u3000\u3000\u52a0\u5927\u5c14\u505c\u624b\u4e0d\u653b\uff0c\u54a6\u4e86\u4e00\u58f0\uff0c\u53c8\u662f\u4e00\u62db\u602a\u62db\u62f3\u65bd\u51fa\u2014\u2014 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u8fde\u8fde\u5012\u9000\uff0c\u4f46\u5374\u4ecd\u662f\u52c9\u5f3a\u907f\u4e86\u5f00\u53bb\u3002\u52a0\u5927\u5c14\u53c8\u662f\u5927\u54a6\u4e00\u58f0\uff0c\u624d\u6325\u62f3\u800c\u4e0a\u2014 \u2014 \u3000\u3000\u4e00\u8fde\u4e09\u62db\uff0c\u52a0\u5927\u5c14\u54a6\u4e86\u4e09\u58f0\uff0c\u4f3c\u4e4e\u82e6\u5eb5\u4e0a\u4eba\u65e9\u5c31\u8be5\u8d25\u7684\u6837\u5b50\uff0c\u82e6\u5eb5\u4e0a\u4eba\u4e0d\u7981\u53c8\u6025\u53c8 \u6012\uff0c\u4f46\u52a0\u5927\u5c14\u62db\u5f0f\u59d4\u5b9e\u592a\u602a\uff0c\u83ab\u8bf4\u53d1\u62db\u8fd8\u51fb\uff0c\u5c31\u8fde\u81ea\u4fdd\u4e5f\u6210\u95ee\u9898\u3002 \u3000\u3000\u5927\u7ea6\u662f\u7b2c\u4e00\u767e\u4e00\u5341\u62db\u4e0a\uff0c\u52a0\u5927\u5c14\u4ecd\u662f\u54a6\u4e86\u4e00\u58f0\u540e\uff0c\u811a\u4e0b\u5374\u62bd\u7a7a\u8fde\u626b\u4e09\u811a\uff0c\u82e6\u5eb5\u62fc\u547d \u4e00\u95ea\uff0c\u867d\u7136\u8eb2\u5f00\u4e86\u53bb\uff0c\u4f46\u64e6\u7684\u4e00\u58f0\uff0c\u895f\u4e0a\u50e7\u888d\u88ab\u6495\u4e0b\u4e00\u5927\u5e45\u3002 \u3000\u3000\u7fa4\u96c4\u4e00\u58f0\u60ca\u547c\uff0c\u4f46\u7acb\u523b\u53d8\u5f97\u6b7b\u4e00\u822c\u7684\u6c89\u5bc2\uff0c\u6240\u6709\u7684\u4eba\u5fc3\u90fd\u5982\u538b\u4e0a\u4e86\u5343\u65a4\u94c1\u5757\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u94c1\u9752\u7740\u8138\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u8fd9\u4e00\u573a\u8d2b\u50e7\u8ba4\u8f93\u2014\u2014\u201d \u3000\u3000\u52a0\u5927\u5c14\u542c\u4e0d\u61c2\u6c49\u8bed\uff0c\u53c8\u542c\u4f17\u4eba\u60ca\u547c\uff0c\u4ee5\u4e3a\u82e6\u5eb8\u4ecd\u4e0d\u670d\u8f93\uff0c\u7adf\u6c14\u5f97\u5927\u53eb\u4e00\u58f0\uff0c\u5168\u529b\u5bf9 \u51c6\u82e6\u5eb5\u5f53\u80f8\u4e00\u62f3\u2014\u2014 \u3000\u3000\u82e6\u5eb5\u65b0\u8d25\u4e4b\u9645\uff0c\u795e\u4e0d\u5b88\u820d\uff0c\u7b49\u5230\u53d1\u89c9\u65f6\uff0c\u5df2\u81ea\u4e0d\u53ca\u95ea\u907f\uff0c\u773c\u770b\u52a0\u5927\u5c14\u8fd9\u4e00\u62db\u60ca\u5929\u52a8 \u5730\u4e4b\u62f3\u52b2\u5c31\u8981\u7740\u5b9e\u6253\u4e2d\u2014\u2014 \u3000\u3000\u7fa4\u96c4\u53d1\u51fa\u4e00\u7247\u6012\u543c\u58f0\uff0c\u6839\u672c\u542c\u4e0d\u51fa\u662f\u9a82\u4ec0\u4e48\u8bdd\u2014\u2014 \u3000\u3000\u5c31\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u5385\u95e8\u88ab\u4eba\u4e00\u811a\u8e22\u5f00\uff0c\u4e00\u6761\u4eba\u5f71\u5982\u98de\u800c\u81f3\uff0c\u547c\u5730\u51cc \u7a7a\u6325\u51fa\u4e00\u638c\uff0c\u8fce\u5411\u52a0\u5927\u5c14\u7684\u4e00\u62f3\u2014\u2014 \u3000\u3000\u7830\u4e00\u58f0\u95f7\u54cd\uff0c\u52a0\u5927\u5c14\u7adf\u88ab\u9707\u9000\u4e24\u6b65\uff0c\u90a3\u4eba\u4e58\u4e00\u9707\u4e4b\u52bf\u9000\u98de\u51fa\u4e08\u4f59\u843d\u5728\u5899\u8fb9\uff01 \u3000\u3000\u4f17\u4eba\u5b9a\u773c\u770b\u65f6\uff0c\u53ea\u89c1\u6765\u4eba\u662f\u4e2a\u82f1\u633a\u9752\u5e74\uff0c\u5927\u90e8\u5206\u4eba\u90fd\u751a\u611f\u773c\u751f\uff0c\u4e00\u90e8\u5206\u4eba\u5374\u5927\u547c\u51fa \u53e3\uff1a\u201c\u6b66\u6797\u4e4b\u79c0\uff01\u201d \u3000\u3000\u6765\u4eba\u6b63\u662f\u65b0\u8fd1\u540d\u6ee1\u6c5f\u6e56\u7684\u6b66\u6797\u4e4b\u79c0\u5b59\u501a\u91cd\uff01 \u3000\u3000\u4f17\u4eba\u7acb\u523b\u7206\u51fa\u4e00\u58f0\u9707\u5929\u4ef7\u7684\u53eb\u597d\u58f0\uff0c\u867d\u7136\u7b2c\u4e00\u573a\u662f\u82e6\u5eb5\u8f93\u4e86\uff0c\u4f46\u5b59\u501a\u91cd\u8fd9\u4e00\u638c\u4f3c\u4e4e \u4f7f\u4f17\u4eba\u51fa\u4e86\u53e3\u9e1f\u6c14\u4f3c\u7684\u3002 \u3000\u3000\u90a3\u4e9b\u4e0d\u8bc6\u5b59\u501a\u91cd\u7684\u4eba\u90fd\u4e0d\u7981\u7a83\u7a83\u79c1\u8bae\uff0c\u4ed6\u4eec\u4e0d\u6599\u6b66\u6797\u4e4b\u79c0\u7684\u529f\u529b\u5982\u6b64\u4e4b\u9ad8\uff0c\u800c\u4eba\u5374 \u5982\u6b64\u5e74\u8f7b\u3002 \u3000\u3000\u5434\u51cc\u98ce\u672a\u89c1\u8fc7\u5b59\u501a\u91cd\uff0c\u6084\u6084\u5bf9\u8f9b\u6377\u9053\uff1a\u201c\u8fd9\u6b66\u6797\u4e4b\u79c0\u529f\u529b\u7684\u786e\u6df1\u539a\uff01\u201d \u3000\u3000\u8f9b\u6377\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff0c\u6211\u548c\u4ed6\u4ea4\u8fc7\u624b\u2014\u2014\u201d\u4ed6\u60f3\u8d77\u90a3\u83ab\u660e\u5176\u5999\u7684\u4e00\u573a\u6253\u6597\uff0c\u771f \u6068\u4e0d\u5f97\u8981\u73b0\u5728\u5c31\u4e0a\u53bb\u5411\u5b59\u501a\u91cd\u95ee\u4e2a\u6e05\u695a\u3002 \u3000\u3000\u90a3\u201c\u5112\u751f\u201d\u538b\u5236\u4f4f\u52a0\u5927\u5c14\u7684\u6012\u706b\uff0c\u6717\u58f0\u9053\uff1a\u201c\u65b9\u624d\u7b2c\u4e00\u4ed7\u5927\u5bb6\u6709\u76ee\u5171\u7779\u662f\u655d\u5e08\u5144\u80dc \u4e86\uff0c\u73b0\u5728\u5c31\u7531\u5728\u4e0b\u91d1 \u3000\u3000\u9c81\u5384\u5411\u4e2d\u539f\u82f1\u96c4\u8ba8\u6559\u7b2c\u4e8c\u573a\u2014\u2014\u201d \u3000\u3000\u8bf4\u7f62\u4e5f\u4e0d\u89c1\u4ed6\u4f5c\u52bf\u7528\u52b2\uff0c\u8eab\u4f53\u9661\u7136\u98d8\u8d77\uff0c\u76f4\u843d\u5728\u4e03\u4e08\u4e4b\u5916\u7684\u5927\u5385\u4e2d\u5fc3\uff0c\u843d\u4e0b\u65f6\u8f7b\u5982 \u843d\u53f6\uff0c\u4f46\u5f53\u4ed6\u4e00\u6b65\u8de8\u5f00\u65f6\uff0c\u9752\u7816\u7684\u5730\u4e0a\u7adf\u73b0\u51fa\u4e24\u4e2a\u534a\u5bf8\u6df1\u7684\u8db3\u5370\u3002 \u3000\u3000\u4f17\u4eba\u5fcd\u4e0d\u4f4f\u60ca\u53eb\u51fa\u58f0\uff0c\u65e0\u4e00\u4eba\u518d\u6562\u51fa\u6218\uff0c\u8d64\u9633\u9053\u957f\u548c\u8c22\u957f\u537f\u4e92\u671b\u4e00\u773c\u6447\u4e86\u6447\u5934\uff0c\u4e00 \u65e0\u53ef\u65bd\u2014\u2014 \u3000\u3000\u83ab\u8bf4\u4ed6\u4eec\uff0c\u5c31\u8fde\u8f9b\u6377\u4e5f\u81ea\u89c9\u529e\u4e0d\u5230\u8fd9\u624b\u529f\u592b\uff0c\u800c\u8fd9\u91d1\u9c81\u5384\u5e74\u7eaa\u770b\u6765\u4e0d\u8fc7\u4e09\u5341\uff0c\u4e0d\u77e5 \u600e\u5730\u7adf\u6709\u8fd9\u6837\u6df1\u529f\u529b\uff1f\u96be\u602a\u4ed6\u72c2\u9a84\u5982\u65af\u2014\u2014 \u3000\u3000\u91d1\u9c81\u5384\u4e00\u8fde\u53eb\u4e86\u4e09\u6b21\uff0c\u4e2d\u539f\u82f1\u96c4\u7adf\u65e0\u4eba\u80fd\u51fa\u6218\uff0c\u4ed6\u4e0d\u7981\u66f4\u662f\u6c14\u9ad8\u8dbe\u626c\uff0c\u5f97\u610f\u4e07\u5206\u3002 \u3000\u3000\u8f9b\u6377\u6108\u77a7\u6108\u4e0d\u987a\u773c\uff0c\u6b63\u5f85\u820d\u547d\u4e0a\u524d\uff0c\u5ffd\u7136\u5237\u7684\u4e00\u6761\u4eba\u5f71\u98d8\u5411\u4e2d\u5385\uff0c\u6717\u58f0\u9053\uff1a \u3000\u3000\u201c\u5728\u4e0b\u5b59\u501a\u91cd\u5411\u91d1\u82f1\u96c4\u8ba8\u6559\u51e0\u62db\u3002\u201d \u3000\u3000\u6b66\u6797\u4e4b\u79c0\u65b9\u624d\u90a3\u638c\u9707\u52a0\u5927\u5c14\u7684\u4e00\u624b\u5341\u5206\u6f02\u4eae\uff0c\u90a3\u77e5\u91d1\u9c81\u5384\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u5bf9 \u624b\uff01\u201d \u3000\u3000\u63a5\u7740\u53c8\u52a0\u4e00\u53e5\uff1a\u201c\u4f60\u548c\u52a0\u5927\u5c14\u6597\u6597\u5012\u662f\u4e00\u5bf9\u513f\uff01\u201d\u8a00\u4e0b\u81ea\u8d1f\u5df2\u6781\u3002 \u3000\u3000\u5b59\u501a\u91cd\u53c8\u60ca\u53c8\u6012\uff0c\u4ed6\u4e5f\u81ea\u77e5\u4e0d\u662f\u91d1\u9c81\u5384\u5bf9\u624b\uff0c\u800c\u4e14\u81ea\u5df1\u8eab\u4e0a\u8fd8\u8d1f\u7740\u5929\u5927\u7684\u8d23\u4efb\uff0c\u60f3 \u5230\u8fd9\u91cc\u4e0d\u7981\u8fdb\u9000\u4e24\u4e0d\u5f97\uff0c\u5927\u662f\u5c34\u5c2c\u3002 \u3000\u3000\u8f9b\u6377\u70ed\u8840\u4e0a\u6d8c\uff0c\u53c8\u5f85\u633a\u8eab\u800c\u51fa\uff0c\u5ffd\u7136\u4e00\u4e2a\u6781\u4e3a\u548c\u972d\u53ef\u4eb2\u7684\u58f0\u97f3\uff1a\u201c\u597d\u554a\uff0c\u5a03\u513f\uff0c\u7ec8 \u4e8e\u627e\u5230\u4f60\u4e86\uff0c\u5feb\u8ddf\u6211\u8d70\u2014\u2014\u201d \u3000\u3000\u90a3\u58f0\u97f3\u6781\u662f\u4f4e\u5f31\uff0c\u4f46\u662f\u5168\u573a\u6bcf\u4e2a\u4eba\u4e00\u5b57\u4e00\u5b57\u542c\u5f97\u65e0\u4e0d\u6e05\u6670\u4e4b\u6781\uff0c\u628a\u4e00\u4e9b\u5176\u4ed6\u7684\u58f0\u54cd \u5168\u90e8\u538b\u4e86\u4e0b\u53bb\uff0c\u4e0d\u7981\u90fd\u662f\u4e00\u60ca\uff0c\u9f50\u8f6c\u8fc7\u8138\u6765\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u767d\u81c2\u8001\u8005\u7b11\u772f\u772f\u5730\u5728\u8f9b\u6377\u8eab \u540e\u3002 \u3000\u3000\u8fd9\u8001\u8005\u7ea2\u5149\u6ee1\u9762\uff0c\u7b11\u5bb9\u53ef\u638f\uff0c\u767d\u9aef\u5df1\u7eb7\u7eb7\u53d8\u6210\u7c73\u9ec4\u8272\u3002\u4f17\u4eba\u5bf9\u8fd9\u8001\u8005\u7686\u751a\u964c\u751f\uff0c\u663e \u7136\u4e0d\u662f\u539f\u5728\u5385\u4e2d\u7684\uff0c\u4f46\u662f\u653e\u7740\u8fd9\u5927\u5385\u4e00\u4eba\u5728\uff0c\u7adf\u6ca1\u6709\u4e00\u4e2a\u4eba\u77a7\u89c1\u4ed6\u662f\u600e\u4e48\u8fdb\u6765\u7684\u3002 \u3000\u3000\u8f9b\u6377\u5374\u662f\u5927\u559c\u671b\u5916\uff0c\u539f\u6765\u8fd9\u8001\u8005\u7adf\u662f\u4e16\u5916\u4e09\u4ed9\u4e4b\u9996\u7684\u5e73\u51e1\u4e0a\u4eba\uff01 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u50ac\u9053\uff1a\u201c\u5a03\u513f\uff0c\u5feb\u8ddf\u6211\u8d70\u554a\uff01\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u89c9\u4e00\u6014\uff0c\u5fc3\u9053\uff1a\u201c\u4f60\u8981\u6211\u5230\u54ea\u91cc\u53bb\u554a\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u8f9b\u6377\u7684\u6a21\u6837\uff0c\u5ffd\u7136\u9053\uff1a\u201c\u6211\u90a3\u5927\u884d\u5341\u5f0f\u6700\u8fd1\u53c8\u521b\u51fa\u4e00\u62db\u6765\uff0c\u6781\u5999\u4e0d\u53ef\u8a00\uff0c \u4f60\u5feb\u8ddf\u6211\u53bb\uff0c\u6211\u597d\u6559\u7ed9\u4f60\u3002\u201d \u3000\u3000\u8f9b\u6377\u55dc\u6b66\u82e5\u72c2\uff0c\u4e0e\u5173\u4e2d\u4e5d\u8c6a\u4e00\u6218\u4e4b\u540e\uff0c\u53c8\u9886\u609f\u4e86\u4e0d\u5c11\u8bc0\u7a8d\uff0c\u95fb\u8a00\u81ea\u662f\u5927\u559c\u2014\u2014 \u3000\u3000\u65c1\u7684\u4eba\u5374\u5f04\u5f97\u83ab\u660e\u5176\u5999\uff0c\u53ea\u89c1\u8001\u5934\u5b50\u5634\u5507\u5fae\u5fae\u8fd0\u52a8\uff0c\u5374\u542c\u4e0d\u5230\u4e00\u4e1d\u58f0\u97f3\uff0c\u539f\u6765\u5e73\u51e1 \u4e0a\u4eba\u65bd\u51fa\u4e86\u4e0a\u4e58\u7684\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u529f\u592b\u3002 \u3000\u3000\u4f46\u662f\u8f9b\u6377\u7acb\u523b\u60f3\u5230\u8fd9\u573a\u4e2d\u539f\u6b66\u6797\u80dc\u8d1f\u4e4b\u4e89\u5c1a\u672a\u4e86\u7ed3\uff0c\u4e8e\u662f\u5bf9\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u665a\u8f88\u5c1a \u8981\u5f85\u8fd9\u91cc\u7684\u4e8b\u6253\u53d1\u4e86\u624d\u80fd\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6025\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4e8b\u6709\u4ec0\u8981\u7d27\uff0c\u4f60\u8ddf\u6211\u8d70\u554a\uff0c\u5426\u5219\u6211\u8001\u513f\u53ef\u8981\u8f93\u7ed9\u90a3\u6167\u5927\u5e08 \u2014\u2014\u201d \u3000\u3000\u5927\u6982\u662f\u4ed6\u60f3\u5230\u8bf4\u6f0f\u4e86\u5634\uff0c\u8fde\u5fd9\u505c\u4f4f\uff0c\u4f46\u8f9b\u6377\u5df2\u5927\u611f\u5947\u602a\uff0c\u6014\u7136\u671b\u7740\u4ed6\u3002 \u3000\u3000\u4f17\u4eba\u53ea\u89c1\u5e73\u51e1\u5fc3\u4eba\u5634\u5df4\u8fde\u52a8\uff0c\u8f9b\u6377\u5374\u8138\u8272\u65f6\u559c\u65f6\u6014\uff0c\u4e0d\u7981\u66f4\u52a0\u7cca\u6d82\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u60f3\u662f\u6025\u5f97\u8981\u547d\u4e86\uff0c\u7adf\u5fd8\u4e86\u7528\u201c\u4f20\u97f3\u5165\u5bc6\u201d\u7684\u529f\u592b\uff0c\u5927\u58f0\u56b7\u9053\uff1a\u201c\u8fd9\u91cc\u7684\u4e8b \u6709\u4ec0\u4e48\u8981\u7d27\u554a\uff1f\u201d \u3000\u3000\u8fd9\u4e0b\u5b50\u4f17\u4eba\u53ef\u542c\u6e05\u695a\u4e86\uff0c\u90a3\u91d1\u9c81\u5371\u672c\u5c31\u4e0d\u8010\u5e73\u51e1\u4e0a\u4eba\u7684\u6253\u6270\uff0c\u8fd9\u65f6\u51b7\u51b7\u63a5\u9053\uff1a\u201c\u8001 \u5339\u592b\u4e0d\u77e5\u6df1\u6d45\uff0c\u80e1\u8a00\u4e71\u8bed\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e0d\u77e5\u6709\u591a\u5c11\u5e74\u6ca1\u6709\u4eba\u6562\u8fd9\u6837\u5bf9\u4ed6\u8bf4\u8bdd\u4e86\uff0c\u95fb\u8a00\u4e0d\u7981\u5947\u9053\uff1a\u201c\u4f60\u518d\u8bf4\u4e00\u904d\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u6a21\u6837\u53e4\u602a\uff0c\u90fd\u4e0d\u7981\u5931\u58f0\u5927\u7b11\uff0c\u91d1\u9c81\u5384\u5927\u6012\u9053\uff1a\u201c\u6211\u8bf4\u4f60\u8fd9\u8001\u5339\u592b\u80e1\u8a00\u4e71\u8bed\uff0c \u8fd8\u4e0d\u7ed9\u6211\u6eda\u5f00\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u770b\u4f60\u50cf\u662f\u6709\u6025\u5fd9\u7684\u4e8b\uff0c\u4f60\u4e14\u8bf4\u7ed9\u6211\u542c\u542c\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5ffd\u7136\u4e00\u4eba\u60ca\u53eb\u8d77\u6765\uff1a\u201c\u4f60\u4eec\u770b\uff0c\u4f60\u4eec\u770b\uff01\u201d \u3000\u3000\u4f17\u4eba\u4f4e\u5934\u4e00\u770b\uff0c\u4e00\u9f50\u60ca\u53eb\u8d77\u6765\uff0c\u539f\u6765\u5730\u4e0a\u88ab\u91d1\u9c81\u5384\u8e29\u9677\u4e0b\u53bb \u3000\u3000\u7684\u4e24\u4e2a\u811a\u5370\u8fd9\u65f6\u5df2\u6062\u590d\u4e86\u539f\u72b6\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5374\u5634\u5e26\u7b11\u5bb9\uff0c\u4e00\u8bed\u4e0d\u53d1\u3002 \u3000\u3000\u4f17\u4eba\u867d\u4e0d\u77e5\u8fd9\u662f\u4ec0\u4e48\u529f\u592b\uff0c\u4f46\u90fd\u77e5\u8fd9\u6bd4\u91d1\u9c81\u5384\u8e29\u9677\u9752\u7816\u53c8\u4e0d\u77e5\u96be\u4e86\u51e0\u500d\u3002 \u3000\u3000\u91d1\u9c81\u5384\u4e5f\u662f\u5927\u60ca\u5931\u8272\uff0c\u5fc3\u60f3\uff1a\u201c\u4eca\u756a\u5b8c\u4e86\uff0c\u4e0d\u6599\u4e2d\u539f\u6709\u8fd9\u7b49\u5947\u4eba\uff0c\u5206\u660e\u6c14\u529f\u5df2\u7ec3\u5230 \u7089\u706b\u7eaf\u9752\u7684\u5730\u6b65\u3002\u201d \u3000\u3000\u4f46\u4ed6\u539f\u662f\u5978\u733e\u65e0\u6bd4\u7684\u4eba\uff0c\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u6697\u9053\uff1a\u201c\u770b\u4ed6\u5e74\u9f84\uff0c\u8f88\u4efd\u5fc5\u7136\u6781\u9ad8\uff0c\u6211\u4e14\u6fc0\u4ed6 \u4e00\u6fc0\u3002\u201d \u3000\u3000\u5f53\u4e0b\u6539\u5bb9\u9053\uff1a\u201c\u521a\u624d\u8a00\u8bed\u5192\u72af\uff0c\u5c1a\u671b\u524d\u8f88\u591a\u591a\u5305\u6db5\uff0c\u655d\u5e08\u5144\u5f1f\u6b64\u6b21\u5949\u5e08\u547d\u524d\u6765\u5b8c\u5168 \u662f\u6b23\u6155\u4e2d\u539f\u6b66\u5b66\uff0c\u655d\u5e08\u5144\u5f1f\u548c\u8fd9\u4e9b\u597d\u6c49\u5df2\u5b9a\u4e86\u6bd4\u6b66\u4e4b\u7ea6\uff0c\u539f\u662f\u2014\u2014\u201d \u3000\u3000\u4f17\u4eba\u542c\u4e86\u5404\u5404\u5927\u60ca\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e24\u4e2a\u5937\u5b50\u5df2\u662f\u8fd9\u7b49\u96be\u60f9\uff0c\u539f\u6765\u4ed6\u8fd8\u6709\u4e00\u4e2a\u5e08\u7236\uff01\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5374\u559c\u9053\uff1a\u201c\u539f\u6765\u4f60\u4eec\u662f\u8981\u6bd4\u6597\u7684\uff0c\u90a3\u6562\u60c5\u597d\uff0c\u5feb\u5feb\u6253\u7ed9\u6211\u8001\u4eba\u5bb6\u770b\u3002\u201d \u3000\u3000\u91d1\u9c81\u5384\u5927\u559c\u9053\uff1a\u201c\u90a3\u4e48\u54b1\u4eec\u8bf7\u8001\u524d\u8f88\u6307\u6b63\u2014\u2014\u201d\u5fc3\u4e2d\u5374\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u8fd9\u8001\u9b3c\u662f \u4e0d\u597d\u610f\u601d\u52a8\u624b\u7684\u4e86\uff0c\u53ea\u8981\u6211\u80dc\u4e86\u8fd9\u4e00\u4ed7\u5c31\u662f\u5927\u529f\u544a\u6210\u3002\u201d \u3000\u3000\u5f53\u4e0b\u5927\u58f0\u53c8\u5411\u7fa4\u8c6a\u6311\u6218\u4e00\u904d\uff0c\u8d64\u9633\u9053\u957f\u7adf\u7136\u4e0d\u6562\u5e94\u6218\u3002 \u3000\u3000\u90a3\u6b66\u6797\u4e4b\u79c0\u5374\u9677\u5165\u6df1\u601d\u4e2d\uff0c\u4f4e\u5934\u4e0d\u8bed\u3002 \u3000\u3000\u8f9b\u6377\u773c\u4e2d\u663e\u51fa\u51db\u7136\u4e4b\u8272\uff0c\u4ed6\u6b63\u8981\u52a8\u6b65\uff0c\u5434\u51cc\u98ce\u6084\u6084\u95ee\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u8981\u4e0a\u53bb\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6bc5\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u5434\u51cc\u98ce\u4f4e\u58f0\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u8fd8\u662f\u8ba9\u6211\u8bd5\u8bd5\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7684\u5bc6\u97f3\u53c8\u4f20\u5165\u8f9b\u6377\u8033\u4e2d\uff1a\u201c\u5c0f\u5a03\u513f\u4f60\u81ea\u4fe1\u6253\u5f97\u8d62\uff1f\u90a3\u86ee\u5937\u6b66\u529f\u5f3a\u5f97\u5f88\u5462\u3002\u201d \u3000\u3000\u8f9b\u6377\u4f4e\u58f0\u9053\uff1a\u201c\u665a\u8f88\u81ea\u5fd6\u4e0d\u662f\u5bf9\u624b\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6012\u9053\uff1a\u201c\u4f60\u518d\u8bf4\u4e00\u904d\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u665a\u8f88\u81ea\u611f\u6050\u975e\u5bf9\u624b\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u95ee\u9053\uff1a\u201c\u6211\u8001\u513f\u662f\u5426\u66fe\u6559\u8fc7\u4f60\u6b66\u827a\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u524d\u8f88\u6210\u5168\u4e4b\u6069\u665a\u8f88\u6c38\u4e0d\u6562\u5fd8\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4e86\uff0c\u4f60\u7b97\u5f97\u6211\u8001\u4eba\u5bb6\u7684\u534a\u4e2a\u5f92\u513f\uff0c\u4f60\u60f3\u60f3\u5e73\u51e1\u4e0a\u4eba\u7684\u5f92\u513f\u80fd \u4e0d\u5982\u4eba\u5bb6\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u77aa\u7136\u4e0d\u77e5\u5982\u4f55\u56de\u7b54\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136\u60f3\u8d77\u81ea\u5df1\u6765\u6b64\u7684\u539f\u610f\uff0c\u795e\u79d8\u5730\u7b11\u9053\uff1a\u201c\u5a03\u513f\uff0c\u6211\u770b\u4f60\u771f\u6c14\u76f4\u900f\u795e\u5ead\uff0c \u529f\u529b\u4f3c\u4e4e\u6bd4\u5728\u5c0f\u6222\u5c9b\u65f6\u5927\u6709\u8fdb\u5c55\uff0c\u4f60\u7528\u5168\u529b\u6253\u6211\u4e00\u62f3\uff0c\u8bd5\u8bd5\u4f60\u5012\u5e95\u6709\u591a\u5c11\u65a4\u4e24\uff1f\u8bb0\u4f4f\uff0c \u8981\u7528\u4e0a\u5168\u529b\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u77e5\u4ed6\u662f\u4f55\u610f\uff0c\u53ea\u77e5\u9053\u4ed6\u771f\u8981\u8bd5\u8bd5\u81ea\u5df1\u662f\u5426\u654c\u5f97\u8fc7\u91d1\u9c81\u5384\uff0c\u5f53\u4e0b\u529b\u8d2f\u5355\u638c\uff0c\u5c3d \u529b\u6253\u51fa\u2014\u2014 \u3000\u3000\u78b0\u5730\u4e00\u58f0\uff0c\u5e73\u51e1\u4e0a\u4eba\u53cc\u80a9\u7adf\u662f\u4e00\u6447\uff0c\u9669\u4e9b\u7acb\u8db3\u4e0d\u4f4f\uff0c\u4ed6\u4e0d\u7adf\u5927\u559c\u9053\uff1a\u201c\u6210\u4e86\uff01\u6210 \u4e86\uff01\u201d \u3000\u3000\u8f9b\u6377\u4ee5\u4e3a\u4ed6\u662f\u8bf4\u81ea\u5df1\u80fd\u548c\u91d1\u9c81\u5384\u4e00\u6297\uff0c\u4e0d\u7981\u5927\u5947\u3002 \u3000\u3000\u800c\u66f4\u5947\u7684\u5219\u662f\u65c1\u89c2\u7684\u7fa4\u8c6a\u4e86\uff0c\u4ed6\u4eec\u542c\u4e0d\u89c1\u5e73\u51e1\u4e0a\u4eba\u7684\u4f20\u97f3\u5165\u5bc6\uff0c\u53ea\u89c1\u8f9b\u6377\u65f6\u60ca\u65f6\u6014\uff0c \u53c8\u6253\u4e86\u5e73\u51e1\u4e0a\u4eba\u4e00\u638c\uff0c\u771f\u662f\u4e08\u516b\u91d1\u521a\u6478\u4e0d\u7740\u5934\u8111\u3002 \u3000\u3000\u90a3\u6d51\u86ee\u5b50\u52a0\u5927\u5c14\u4e0d\u8010\u5df2\u6781\uff0c\u95ee\u9053\uff1a\u201c\u5e0c\u91cc\u6c99\uff0c\u52a0\u5df4\u7f57\u4e5f\u80e1\u4e9a\uff1f\u201d\u4ed6\u7684\u610f\u601d\u662f\uff1a\u201c\u5e08 \u5f1f\uff0c\u8fd9\u8001\u9b3c\u5728\u5e72\u4ec0\u4e48\u554a\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4f3c\u4e4e\u61c2\u5f97\u4ed6\u7684\u8bdd\uff0c\u95fb\u8a00\u5927\u6012\u9053\uff1a\u201c\u4e1d\u5df4\u4e95\u547c\uff0c\u683c\u91cc\u6469\u6cb3\u800c\u661f\u57fa\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u7684\u7adf\u4e5f\u662f\u86ee\u4eba\u7684\u8bed\u8a00\uff0c\u91d1\u9c81\u5384\u4e0d\u7531\u5927\u6025\uff0c\u56e0\u4e3a\u5e73\u51e1\u4e0a\u4eba\u662f\u8bf4\uff1a\u201c\u4f60\u6562\u9a82\u6211\u8001\u4eba \u5bb6\uff0c\u6211\u8981\u6559\u8bad\u4f60\u3002\u201d \u3000\u3000\u91d1\u9c81\u5384\u5fd9\u7528\u6c49\u8bed\u9053\uff1a\u201c\u8001\u524d\u8f88\u6b47\u6012\uff0c\u5bb6\u5e08\u66fe\u4e00\u518d\u53ee\u5631\u4ed6\u4e0d\u53ef\u5f00\u7f6a\u4e2d\u539f\u524d\u8f88\u9ad8\u4eba\uff0c\u4ed6 \u662f\u6d51\u4eba\uff0c\u524d\u8f88\u4e0d\u8981\u8ba1\u8f83\u3002\u201d \u3000\u3000\u4ed6\u8a00\u4e0b\u4e4b\u610f\u4e0d\u8fc7\u662f\u63d0\u9192\u5e73\u51e1\u4e0a\u4eba\u4e43\u662f\u524d\u8f88\u9ad8\u4eba\uff0c\u90a3\u5c31\u4e0d\u80fd\u4ee5\u5927\u538b\u5c0f\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u4ed6\u6b3a\u6211\u4e2d\u539f\u6ca1\u6709\u4eba\u61c2\u5f97\u68b5\u8bed\uff0c\u554a\uff0c\u4f60\u7684\u610f\u601d\u662f\u8bf4\u6211\u4ee5\u5927\u538b\u5c0f\uff0c\u597d\uff0c \u597d\uff0c\u4f60\u65b9\u624d\u4e0d\u662f\u5728\u6311\u6218\u4e48\uff1f\u6211\u9a6c\u4e0a\u8981\u6211\u5f92\u513f\u5e94\u6218\u3002\u201d \u3000\u3000\u8bf4\u7740\u5bf9\u8f9b\u6377\u62db\u62db\u624b\u9053\uff1a\u201c\u5a03\u513f\uff0c\u6765\uff0c\u6211\u6559\u4f60\u4e00\u624b\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u5927\u559c\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u5e73\u51e1\u4e0a\u4eba\u53c8\u7528\u4f20\u97f3\u4e4b\u6cd5\u5c06\u81ea\u5df1\u65b0\u521b\u7684\u4e00\u8bb0\u7edd\u62db\u6559\u7ed9\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u542c\u5f97\u5fc3\u8df3\u535c\u535c\uff0c\u56e0\u4e3a\u8fd9\u62db\u771f\u662f\u5999\u7edd\u4eba\u5bf0\uff0c\u800c\u4e14\u4e0e\u90a3\u539f\u6709\u5341\u62db\u5bc6\u5207\u914d\u5408\uff0c\u5a01\u529b\u66f4 \u662f\u500d\u589e\u3002\u90a3\u77e5\u6559\u4e86\u4e00\u534a\uff0c\u5e73\u51e1\u4e0a\u4eba\u5ffd\u9053\uff1a\u201c\u6709\u4eba\u5728\u5077\u542c\u5462\uff0c\u6211\u8001\u4eba\u5bb6\u7d22\u6027\u544a\u8bc9\u4ed6\uff0c\u770b\u4ed6 \u53c8\u600e\u80fd\u8010\u4f55\u4f60\uff1f\u201d \u3000\u3000\u91d1\u9c81\u5384\u679c\u7136\u9762\u7ea2\u8033\u8d64\uff0c\u539f\u6765\u4ed6\u6b63\u662f\u7528\u4e0a\u4e58\u5185\u529f\u6452\u9664\u6742\u5ff5\uff0c\u60f3\u6536\u542c\u5e73\u51e1\u4eba\u4eba\u7684\u8bdd\uff0c\u5374 \u88ab\u5e73\u51e1\u4e0a\u4eba\u4e00\u8bed\u6307\u7834\u3002 \u3000\u3000\u63a5\u7740\u5e73\u51e1\u4e0a\u4eba\u5c31\u5f53\u9762\u5927\u58f0\u5c06\u90a3\u534a\u62db\u4f20\u7ed9\u8f9b\u6377\uff0c\u5176\u4ed6\u6bcf\u4eba\u867d\u90fd\u542c\u5f97\u4e00\u6e05\u4e8c\u695a\uff0c\u5374\u662f\u4e00 \u4e1d\u4e0d\u61c2\uff0c\u8f9b\u6377\u5374\u662f\u559c\u4e0a\u7709\u68a2\uff0c\u5b57\u5b57\u7262\u8bb0\u5fc3\u7530\u3002 \u3000\u3000\u6559\u62db\u65e2\u6bd5\uff0c\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u5a03\u513f\uff0c\u597d\u597d\u6253\u4e00\u67b6\u554a\u3002\u201d \u3000\u3000\u90a3\u91d1\u9c81\u5384\u867d\u89c9\u5e73\u51e1\u4e0a\u4eba\u6b66\u529f\u6df1\u4e0d\u53ef\u6d4b\uff0c\u4f46\u4ed6\u5c31\u4e0d\u4fe1\u81ea\u5df1\u4f1a\u6253\u4e0d\u8fc7\u8f9b\u6377\uff0c\u662f\u4ee5\u5927\u523a\u523a \u5730\u9053\uff1a\u201c\u54b1\u4eec\u6bd4\u5175\u5203\u8fd8\u662f\u62f3\u811a\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5374\u662f\u504f\u6fc0\u6027\u5b50\u7684\u4eba\uff0c\u4ed6\u89c1\u91d1\u9c81\u5384\u7684\u72c2\u6001\uff0c\u7d22\u6027\u4e0d\u7406\u4ed6\uff0c\u6296\u624b\u62d4\u51fa\u957f\u5251\uff0c\u547c\u5730\u5f53 \u80f8\u5c31\u523a\u2014\u2014 \u3000\u3000\u91d1\u9c81\u5384\u4e0d\u6599\u4e2d\u539f\u4e5f\u6709\u8fd9\u7b49\u4e0d\u77e5\u793c\u6570\u7684\u4eba\uff0c\u4e0d\u7981\u52c3\u7136\u5927\u6012\uff0c\u547c\u5730\u4e00\u58f0\uff0c\u4ece\u8170\u4e0a\u892a\u4e0b\u4e00 \u6839\u8f6f\u7d22\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u8f9b\u6377\u4e0a\u53bb\u63a5\u6218\uff0c\u4e0d\u7531\u8bae\u8bba\u7eb7\u7eb7\uff0c\u4e0d\u77e5\u662f\u8c01\u4f20\u51fa\u6b64\u4eba\u5c31\u662f\u65b0\u8fd1\u5927\u8d25\u52fe\u6f0f\u4e00\u602a\u7684 \u201c\u6885\u9999\u795e\u5251\u201d\u8f9b\u6377\u65f6\uff0c\u66f4\u662f\u5168\u573a\u54c4\u7136\u4e86\u3002 \u3000\u3000\u8d64\u9633\u9053\u957f\u7b49\u4eba\u5148\u672a\u770b\u89c1\u8f9b\u6377\uff0c\u8fd9\u65f6\u5374\u662f\u9762\u8272\u5927\u53d8\uff0c\u53c8\u6015\u8f9b\u6377\u5f97\u80dc\uff0c\u53c8\u5e0c\u671b\u8f9b\u6377\u5f97\u80dc \u2014\u2014\u4ed6\u4eec\u4e5f\u77e5\u9053\u8f9b\u6377\u5316\u88c5\u4e03\u5999\u795e\u541b\u7684\u4e00\u6bb5\u4e8b\u3002 \u3000\u3000\u91d1\u9c81\u5371\u90a3\u6839\u957f\u7d22\u4e4c\u4eae\u4eae\u5730\uff0c\u4e0d\u77e5\u662f\u4ec0\u4e48\u8d28\u6599\u5236\u6210\uff0c\u7adf\u662f\u80fd\u67d4\u80fd\u521a\uff0c\u5389\u5bb3\u4e4b\u6781\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u4e0a\u624b\u5c31\u662f\u5927\u884d\u5341\u5f0f\u7684\u7edd\u62db\u201c\u6708\u4e91\u6f6d\u5f71\u201d\uff0c\u53ea\u89c1\u4e07\u70b9\u94f6\u5149\u88ad\u5411\u91d1\u9c81\u5384\u5468\u8eab\u8981\u7a74 \u2014\u2014 \u3000\u3000\u91d1\u9c81\u5384\u4e00\u6296\u4e4b\u95f4\u957f\u97ad\u53d8\u6210\u4e00\u6839\u957f\u68cd\uff0c\u4e00\u6a2a\u4e4b\u95f4\u8fde\u6253\u8f9b\u6377\u8155\u4e0a\u4e09\u7a74\uff0c\u4ed6\u5185\u5916\u517c\u4fee\uff0c\u6bd4 \u8d77\u52a0\u5927\u5c14\u6765\u66f4\u662f\u5389\u5bb3\u5f97\u591a\uff0c\u957f\u7d22\u9876\u7aef\u7adf\u53d1\u51fa\u545c\u545c\u5f02\u54cd\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u6211\u81ea\u5c0f\u57ce\u5c9b\u5947\u9047\u4e4b\u540e\uff0c\u529f\u529b\u5927\u589e\uff0c\u5251\u5c16\u5df1\u80fd\u968f\u610f\u53d1\u51fa\u5251\u6c14\uff0c \u4f46\u8981\u60f3\u5982\u4ed6\u8fd9\u822c\u7528\u4e00\u6839\u8f6f\u7d22\u53d1\u51fa\u5251\u6c14\uff0c\u5374\u662f\u4e07\u4e07\u4e0d\u80fd\uff01\u201d \u3000\u3000\u5fc3\u4e2d\u4e00\u51db\uff0c\u8fde\u5fd9\u6536\u62db\u6362\u5f0f\uff0c\u90a3\u91d1\u9c81\u5384\u4f55\u7b49\u72e1\u8bd8\uff0c\u957f\u7d22\u5012\u5377\uff0c\u4e58\u865a\u800c\u5165\u2014\u2014 \u3000\u3000\u9ad8\u624b\u8fc7\u62db\uff0c\u4e00\u4e1d\u5206\u5fc3\u6563\u610f\u4e5f\u80fd\u5f71\u54cd\u80dc\u8d1f\uff0c\u8f9b\u6377\u4e00\u7740\u5931\u673a\uff0c\u7acb\u523b\u9677\u5165\u82e6\u6218\u4e2d\u3002\u91d1\u9c81\u5384 \u62db\u5f0f\u4e4b\u5947\uff0c\u786e\u662f\u4e16\u4e0a\u65e0\u53cc\uff0c\u53ea\u89c1\u4ed6\u90a3\u957f\u7d22\u65f6\u97ad\u65f6\u68cd\uff0c\u65f6\u5251\u65f6\u67aa\uff0c\u5ffd\u786c\u5ffd\u8f6f\uff0c\u5ffd\u521a\u5ffd\u67d4\uff0c \u66f4\u517c\u4ed6\u5185\u529b\u6df1\u539a\u4e4b\u6781\uff0c\u7d22\u5934\u4e0d\u65f6\u53d1\u51fa\u545c\u545c\u602a\u54cd\uff1b\u8f9b\u6377\u5b8c\u5168\u5904\u4e8e\u88ab\u52a8\uff01 \u3000\u3000\u5434\u51cc\u98ce\u5bf9\u8fd9\u6377\u5f1f\u7231\u62a4\u5907\u81f3\uff0c\u8fd9\u65f6\u89c1\u4ed6\u9677\u4e8e\u5371\u5883\uff0c\u4e0d\u7981\u53cc\u62f3\u7d27\u634f\uff0c\u51b7\u6c57\u76f4\u5192\u3002 \u3000\u3000\u5168\u573a\u4f17\u8c6a\u4e5f\u90fd\u7d27\u5f20\u65e0\u6bd4\uff0c\u56e0\u4e3a\u8fd9\u662f\u5173\u7cfb\u6b66\u6797\u5174\u4ea1\u7684\u6700\u540e\u4e00\u6218\uff01 \u3000\u3000\u91d1\u9c81\u5384\u602a\u62db\u767e\u51fa\uff0c\u66f4\u517c\u529f\u529b\u6df1\u539a\uff0c\u8f9b\u6377\u82e5\u4e0d\u662f\u8fd1\u6765\u529f\u529b\u6fc0\u589e\uff0c\u53ea\u6015\u65e9\u5df2\u8d25\u843d\uff01 \u3000\u3000\u5728\u8fd9\u7b49\u5b8c\u5168\u4e0b\u98ce\u7684\u5f62\u52bf\u4e4b\u4e0b\uff0c\u8f9b\u6377\u786c\u786c\u5230\u62c6\u5341\u4e94\u62db\uff0c\u7b2c\u5341\u4e94\u62db\u624d\u8fc7\uff0c\u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136 \u53eb\u9053\uff1a\u201c\u8fd9\u86ee\u5b50\u5230\u5e95\u4e0d\u6210\u6750\uff0c\u521a\u624d\u82e5\u662f\u6539\u53d8\u97ad\u6cd5\uff0c\u65e9\u5c31\u80dc\u4e86\uff01\u201d \u3000\u3000\u4f17\u4eba\u90fd\u5927\u5403\u4e00\u60ca\uff0c\u600e\u4e48\u8fd9\u8001\u513f\u53c8\u5e2e\u8d77\u86ee\u5b50\u6765\u5566\uff1f\u5185\u4e2d\u6709\u51e0\u4e2a\u81ea\u4f5c\u806a\u660e\u7684\u7a83\u7a83\u79c1\u8bed\u9053\uff1a \u201c\u5fc5\u662f\u65b9\u624d\u8f9b\u5927\u4fa0\u6253\u4e86\u8fd9\u8001\u513f\u4e00\u638c\uff0c\u8fd9\u8001\u513f\u5c31\u5e2e\u90a3\u86ee\u5b50\uff0c\u5e0c\u671b\u86ee\u5b50\u5f97\u80dc\u3002\u201d \u3000\u3000\u53ea\u6709\u8f9b\u6377\u672c\u4eba\u4e00\u95f4\u6b64\u8bed\uff0c\u5b9b\u5982\u5f53\u5934\u68d2\u559d\uff0c\u5fc3\u9053\uff1a\u201c\u5e73\u51e1\u4e0a\u4eba\u660e\u8bf4\u6307\u70b9\u8fd9\u91d1\u9c81\u5384\uff0c\u5176 \u5b9e\u662f\u6307\u9192\u6211\u4e0d\u53ef\u58a8\u5b88\u6210\u89c4\uff0c\u65e9\u5e94\u6539\u53d8\u6218\u672f\uff0c\u5631\uff0c\u5bf9\u4e86\uff0c\u6211\u4eca\u65e5\u600e\u5730\u5982\u6b64\u62d8\u6ce5\u58a8\u5b88\uff1f\u201d \u3000\u3000\u5ff5\u5934\u4e00\u95ea\uff0c\u4ed6\u624b\u4e0a\u5df2\u662f\u53d8\u62db\uff0c\u53ea\u89c1\u4ed6\u957f\u5251\u4ece\u5de6\u800c\u53f3\uff0c\u5251\u5c16\u98a4\u6296\uff0c\u4e1d\u4e1d\u5251\u6c14\u8fde\u7ef5\u4e0d\u7edd\uff0c \u6b63\u662f\u5927\u884d\u5341\u5f0f\u4e2d\u7684\u7edd\u5999\u5b88\u5f0f\u201c\u6708\u5f02\u661f\u90aa\u201d\uff0c\u8f9b\u6377\u5f85\u5251\u5c16\u5212\u5230\u534a\u9014\u65f6\uff0c\u7a81\u7136\u624b\u8155\u4e00\u7ffb\uff0c\u5251 \u6c14\u6597\u76db\uff0c\u78c1\u7684\u4e00\u58f0\u957f\u5251\u504f\u523a\u800c\u51fa\uff0c\u5df2\u53d8\u6210\u4e86\u201c\u866c\u679d\u5251\u5f0f\u201d\u7684\u201c\u4e4d\u60ca\u6885\u9762\u201d\u2014\u2014 \u3000\u3000\u8fd9\u4e00\u62db\u6b63\u662f\u8f9b\u6377\u53d7\u4e86\u5e73\u51e1\u4e0a\u4eba\u63d0\u9192\u540e\uff0c\u5c06\u5927\u884d\u5341\u5f0f\u548c\u866c\u679d\u5251\u5f0f\u878d\u5408\u4f7f\u7528\u7684\u7edd\u7740\uff0c\u5a01 \u529b\u679c\u7136\u500d\u589e\uff0c\u91d1\u9c81\u5384\u54a6\u4e86\u4e00\u58f0\uff0c\u8fde\u9000\u4e24\u6b65\uff0c\u97ad\u7aef\u8fde\u53d1\u4e09\u62db\uff0c\u624d\u628a\u8f9b\u6377\u7684\u53cd\u653b\u4e4b\u52bf\u5316\u6389\uff01 \u3000\u3000\u7136\u800c\u8fd9\u4e00\u6765\uff0c\u8f9b\u6377\u603b\u7b97\u8131\u51fa\u5371\u5883\uff0c\u4ed6\u4e5f\u5012\u9000\u4e00\u6b65\uff0c\u731b\u5438\u4e00\u53e3\u771f\u6c14\u2014\u2014 \u3000\u3000\u91d1\u9c81\u5384\u4e00\u62a1\u957f\u7d22\uff0c\u76f4\u70b9\u8f9b\u6377\u95e8\u9762\uff0c\u8f9b\u6377\u4e0a\u8eab\u5411\u5de6\u4e00\u6643\uff0c\u8eab\u4f53\u5374\u5f80\u53f3\u95ea\u4e86\u5f00\u53bb\uff0c\u547c\u7684 \u4e00\u58f0\uff0c\u91d1\u9c81\u5384\u7684\u957f\u7d22\u5c31\u843d\u4e86\u7a7a\u2014\u2014 \u3000\u3000\u201c\u65e0\u4e3a\u5385\u201d\u4e2d\u7206\u51fa\u9707\u5929\u4ef7\u7684\u559d\u91c7\uff0c\u8f9b\u6377\u8fd9\u62db\u7740\u5b9e\u662f\u5999\u5f97\u5f88\uff0c\u6b63\u662f\u201c\u6697\u9999\u63a0\u5f71\u201d\u8f7b\u529f \u7edd\u6280\u4e2d\u7684\u5f0f\u5b50\u2014\u2014 \u3000\u3000\u7136\u800c\uff0c\u91d1\u9c81\u5384\u5374\u4e58\u7740\u843d\u7a7a\u7684\u52bf\u5b50\uff0c\u8eab\u5b50\u5f80\u524d\u4e00\u51b2\uff0c\u624b\u4e2d\u5374\u731b\u7136\u53d1\u52b2\uff0c\u201c\u5288\u62cd\u201d\u4e00\u58f0\uff0c \u957f\u7d22\u88ab\u6296\u5c06\u56de\u6765\uff0c\u7b14\u76f4\u5730\u5f80\u540e\u6253\u51fa\uff0c\u5374\u662f\u4e00\u4e1d\u4e0d\u5dee\u5730\u88ad\u5411\u8f9b\u6377\u7684\u54bd\u5589\u8981\u7a74\u2014\u2014 \u3000\u3000\u8fd9\u4e00\u62db\u602a\u5999\u517c\u5177\uff0c\u4e43\u662f\u91d1\u9c81\u5384\u5f97\u610f\u4e4b\u4f5c\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u5c31\u7b97\u8eb2\u788d\u5f00\uff0c\u4e5f\u5fc5\u72fc\u72c8\u4e0d \u582a\u4e86\uff01\u201d \u3000\u3000\u6562\u60c5\u6b64\u523b\u4ed6\u5bf9\u8f9b\u6377\u5df2\u4e0d\u6562\u8fc7\u5206\u8f7b\u89c6\u3002 \u3000\u3000\u90a3\u957f\u7d22\u7aef\u9876\u53d1\u51fa\u545c\u545c\u602a\u54cd\uff0c\u75be\u5982\u95ea\u7535\u5730\u70b9\u5411\u8f9b\u6377\uff0c\u90a3\u77e5\u957f\u7d22\u6536\u5230\u5c3d\u5934\uff0c\u5288\u62cd\u4e00\u58f0\uff0c \u4ecd\u662f\u843d\u4e86\u7a7a\uff01 \u3000\u3000\u6240\u6709\u7684\u4eba\u90fd\u6ca1\u6709\u770b\u89c1\u8f9b\u6377\u662f\u600e\u6837\u95ea\u8eb2\u8fc7\u53bb\u7684\uff0c\u53ea\u89c9\u773c\u82b1\u7f00\u4e71\uff0c\u8f9b\u6377\u5df1\u6362\u4e86\u4f4d\u7f6e\u2014\u2014 \u3000\u3000\u8fde\u5e73\u51e1\u4e0a\u4eba\u90fd\u4e0d\u7981\u60ca\u54a6\u4e00\u58f0\uff0c\u4ed6\u89c1\u8f9b\u6377\u65b9\u624d\u95ea\u8eb2\u7684\u6b65\u6cd5\u50cf\u662f\u5c0f\u6222\u5c9b\u4e3b\u6167\u5927\u5e08\u7684\u5f97\u610f \u7edd\u5b66\u201c\u8bd8\u6469\u795e\u6b65\u201d\u2014\u2014\u4ed6\u5e76\u4e0d\u77e5\u8f9b\u6377\u5df2\u5f97\u6167\u5927\u5e08\u7684\u9752\u7750\uff0c\u5b66\u5f97\u4e86\u8fd9\u4e00\u5957\u7edd\u5b66\u3002 \u3000\u3000\u8f9b\u6377\u597d\u4e0d\u5bb9\u6613\u7b49\u5230\u8fd9\u6837\u7684\u673a\u4f1a\uff0c\u4ed6\u8155\u4e0a\u594b\u529b\u4e00\u9707\uff0c\u5251\u6c14\u58f0\u9661\u7136\u76d6\u8fc7\u957f\u7d22\u6240\u53d1\u545c\u545c\u4e4b \u58f0\uff0c\u4e00\u62db\u201c\u51b7\u6885\u62c2\u9762\u201d\u5df2\u81ea\u4f7f\u51fa\u2014\u2014 \u3000\u3000\u666e\u901a\u4e8c\u6d41\u4ee5\u4e0a\u7684\u9ad8\u624b\u8fc7\u62db\u5c31\u5f88\u5c11\u6709\u201c\u62db\u5f0f\u7528\u8001\u201d\u7684\u6bdb\u75c5\u51fa\u4e86\uff0c\u56e0\u4e3a\u201c\u62db\u5f0f\u7528\u8001\u201d\u4e4b \u540e\u7684\u7ed3\u679c\uff0c\u5373\u4f7f\u4e0d\u8d25\u4e5f\u72fc\u72c8\u4e0d\u582a\uff0c\u9ad8\u624b\u8fc7\u62db\uff0c\u516d\u5206\u53d1\u56db\u5206\u6536\uff0c\u7ec8\u4e0d\u4ee4\u62db\u5f0f\u7528\u8001\uff0c\u91d1\u9c81\u5384 \u662f\u56e0\u5bf9\u81ea\u5df1\u8fd9\u4e00\u62db\u592a\u8fc7\u6709\u4fe1\u5fc3\uff0c\u4ee5\u81f4\u7740\u4e86\u8f9b\u6377\u7684\u9053\u513f\uff01 \u3000\u3000\u5f53\u4ed6\u62fc\u529b\u5b9a\u4f4f\u8eab\u8eaf\u4e4b\u65f6\uff0c\u8f9b\u6377\u7684\u5251\u5b50\u5df1\u75be\u523a\u800c\u81f3\uff0c\u4ed6\u4e0d\u7981\u5f00\u58f0\u5410\u6c14\uff0c\u957f\u7d22\u62a1\u5f97\u7b14\u76f4\uff0c \u5982\u6d41\u661f\u822c\u76f4\u70b9\u8f9b\u6377\u8155\u8109\uff0c\u4ee5\u653b\u4e3a\u5b88\u3002 \u3000\u3000\u8f9b\u6377\u5c82\u80fd\u653e\u8fc7\u6b64\u7b49\u5927\u597d\u826f\u673a\uff0c\u624b\u8155\u4e00\u5708\uff0c\u4e00\u9762\u8eb2\u8fc7\u4e86\u91d1\u9c81\u5384\u7684\u4e00\u70b9\uff0c\u540c\u65f6\u4e00\u80a1\u67d4\u52b2 \u7f13\u7f13\u900f\u51fa\uff0c\u8106\u786c\u7684\u957f\u5251\u7adf\u968f\u52bf\u4e00\u5f2f\uff0c\u5bfb\u5373 \u3000\u3000\u53ee\u7136\u5f39\u51fa\uff0c\u5251\u5c16\u6240\u6307\uff0c\u6b63\u662f\u91d1\u9c81\u5384\u808b\u9aa8\u4e0b\u7684\u201c\u7ae0\u95e8\u7a74\u201d\uff01 \u3000\u3000\u8fd9\u4e00\u4e0b\u8fde\u8f9b\u6377\u81ea\u5df1\u90fd\u611f\u9707\u60ca\uff0c\u8fd9\u80a1\u67d4\u52b2\u7528\u5f97\u5999\u51fa\u610f\u8868\uff0c\u5fc3\u60f3\u81ea\u5df1\u529f\u529b\u8fd1\u6765\u771f\u662f\u5927\u8fdb\uff0c \u4e0d\u7981\u4fe1\u5fc3\u9661\u589e\uff0c\u957f\u5578\u4e00\u58f0\uff01 \u3000\u3000\u91d1\u9c81\u5384\u89c1\u8f9b\u6377\u8fd9\u4e00\u5708\u5708\u5f97\u6781\u5999\uff0c\u7adf\u7136\u4e0d\u987e\u8f9b\u6377\u7684\u957f\u5251\uff0c\u624b\u4e0a\u52b2\u9053\u4e00\u6539\uff0c\u539f\u6765\u62a1\u5f97\u7b14 \u76f4\u7684\u957f\u7d22\u7adf\u7136\u547c\u5730\u5377\u4e0a\u8f9b\u6377\u624b\u8155\u2014\u2014\u8f9b\u6377\u4f5c\u68a6\u4e5f\u6599\u4e0d\u5230\u91d1\u9c81\u5384\u4f1a\u6709\u8fd9\u4e00\u624b\uff0c\u4ed6\u53ea\u597d\u518d\u5ea6 \u65bd\u51fa\u8bd8\u6469\u6b65\u6cd5\uff0c\u8eab\u5f62\u5982\u4e00\u7f15\u9752\u70df\u822c\u540e\u9000\u4e24\u6b65\u3002 \u3000\u3000\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u957f\u7d22\u9876\u7aef\u5012\u5377\u56de\u6765\uff0c\u4fa5\u662f\u8f9b\u6377\u9000\u5f97\u5feb\uff0c\u8155\u4e0a\u8863\u8896\u7adf\u88ab\u5377\u88c2\u4e00\u5927\u5757\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u6697\u4e2d\u53d1\u6012\uff0c\u6012\u706b\u4ee3\u66ff\u4e86\u754f\u60e7\uff0c\u4ed6\u8eab\u5b50\u4e00\u6643\uff0c\u5c48\u8eab\u76f4\u8fdb\uff0c\u5251\u5149\u70b9\u70b9\uff0c\u5168\u662f\u8fdb \u624b\u62db\u5f0f\u3002 \u3000\u3000\u91d1\u9c81\u5384\u6012\u543c\u4e00\u58f0\uff0c\u957f\u7d22\u62db\u5f0f\u53c8\u53d8\uff0c\u8fd9\u6b21\u7adf\u6bd4\u524d\u4e24\u6b21\u8fd8\u8981\u53e4\u602a\uff0c\u97ad\u58f0\u7d22\u5f71\u4e4b\u4e2d\u9690\u9690\u900f \u51fa\u4e00\u4e1d\u90aa\u6c14\u3002 \u3000\u3000\u7136\u800c\u8f9b\u6377\u6b64\u65f6\u5374\u662f\u51db\u7136\u4e0d\u60e7\uff0c\u4ed6\u624b\u4e0a\u201c\u5927\u884d\u5341\u5f0f\u201d\u548c\u201c\u866c\u679d\u5251\u5f0f\u201d\u4e92\u6613\u800c\u65bd\uff0c\u811a\u4e0b \u914d\u5408\u7740\u201c\u6d01\u6469\u795e\u6b65\u201d\uff0c\u8fd9\u4e09\u4ef6\u6d77\u5185\u5916\u5947\u4eba\u7684\u5f97\u610f\u7edd\u5b66\u914d\u5408\u4e00\u9f50\u65bd\u51fa\uff0c\u7adf\u4ee4\u91d1\u9c81\u5384\u7a7a\u5177\u8f83 \u6df1\u7684\u529f\u529b\u800c\u65e0\u6cd5\u62a2\u5f97\u4e0a\u98ce\uff01 \u3000\u3000\u5148\u524d\u4e94\u5341\u62db\u5185\uff0c\u8f9b\u6377\u72b9\u89c9\u6709\u4e9b\u5730\u65b9\u4e0d\u751a\u987a\u624b\uff0c\u4e94\u5341\u62db\u540e\uff0c\u6e10\u6e10\u5730\u6108\u6765\u6108\u89c9\u5f97\u5fc3\u5e94\u624b\uff0c \u6d41\u5229\u65e0\u6bd4\uff0c\u4e24\u79cd\u5251\u62db\u4e00\u5206\u4e00\u5408\u4e4b\u95f4\uff0c\u5a01\u529b\u7edd\u4f26\uff0c\u8f9b\u6377\u6108\u6253\u6108\u653e\uff0c\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u83ab\u4e0d\u4e2d \u80af\u5f02\u5e38\u3002 \u3000\u3000\u91d1\u9c81\u5384\u6108\u6253\u6108\u60ca\uff0c\u4e00\u54ac\u7259\uff0c\u5c06\u957f\u7d22\u4e0a\u704c\u6ce8\u5341\u6210\u529f\u529b\uff0c\u6253\u7b97\u4ee5\u786c\u53d6\u80dc\uff01 \u3000\u3000\u5385\u4e2d\u7fa4\u8c6a\u4e0d\u77e5\u8f9b\u6377\u5df2\u6e10\u5165\u4f73\u5883\uff0c\u53ea\u89c9\u91d1\u9c81\u5384\u7d22\u4e0a\u5578\u58f0\u6108\u6765\u6108\u54cd\uff0c\u6697\u4e2d\u66ff\u8f9b\u6377\u62c5\u5fc3\u4e0d \u5df2\u3002 \u3000\u3000\u8d64\u9633\u9053\u957f\uff0c\u82e6\u5eb5\u5927\u5e08\u76f8\u5bf9\u9a87\u7136\uff0c\u4e0d\u6599\u6708\u4f59\u4e0d\u89c1\uff0c\u8f9b\u6377\u529f\u529b\u7adf\u589e\u8fdb\u5982\u6b64\uff0c\u5e0c\u671b\u4ed6\u5f97\u80dc\uff0c \u53c8\u4e0d\u6562\u60f3\u4ed6\u5f97\u80dc\u4ee5\u540e\u7684\u540e\u679c\uff0c\u5fc3\u4e2d\u987f\u65f6\u77db\u76fe\u8d77\u6765\u3002 \u3000\u3000\u5306\u5306\u767e\u62db\u5df1\u8fc7\uff0c\u8f9b\u6280\u4ed7\u7740\u5251\u6cd5\u795e\u5999\uff0c\u786c\u62b5\u4f4f\u91d1\u544a\u5384\u6c79\u6d8c\u7684\u5185\u52b2\uff0c\u4ed6\u81ea\u89c9\u8d8a\u6253\u8d8a\u79f0\u624b\uff0c \u867d\u7136\u8981\u60f3\u53d6\u80dc\u5e76\u4e0d\u662f\u7b80\u5355\u4e4b\u4e8b\uff0c\u4e0d\u8fc7\u4ed6\u6b64\u65f6\u6839\u672c\u4e0d\u66fe\u60f3\u5230\u8fd9\u4e9b\uff0c\u4ed6\u53ea\u6697\u6697\u559c\u9053\uff1a\u201c\u82e5\u4e0d \u662f\u8fd9\u573a\u6076\u6597\uff0c\u6211\u90a3\u80fd\u8fd9\u4e48\u5feb\u5c31\u878d\u4f1a\u8d2f\u901a\u8d77\u6765\uff1f\u201d \u3000\u3000\u5c3d\u7ba1\u91d1\u9c81\u5384\u58f0\u70ed\u6c79\u6c79\uff0c\u4f46\u5306\u5306\u53c8\u662f\u767e\u62db\uff0c\u8f9b\u6377\u4f9d\u7136\u6ca1\u6709\u8d25\u843d\uff0c\u5385\u4e2d\u7fa4\u8c6a\u8fd9\u624d\u770b\u51fa\u4e00 \u4e9b\u7aef\u502a\u2014\u2014 \u3000\u3000\u6e10\u6e10\u8f9b\u6377\u53d1\u73b0\u91d1\u9c81\u5384\u624b\u4e0a\u653b\u52bf\u867d\u7136\u731b\u6781\uff0c\u4f46\u662f\u4e0b\u76d8\u5374\u4f3c\u6781\u5c11\u4f5c\u7528\uff0c\u60f3\u5230\u8fd9\u91cc\uff0c\u5fc3\u5ff5 \u4e00\u52a8\uff1a \u3000\u3000\u201c\u5bf9\u4e86\uff0c\u8fd9\u91d1\u9c81\u5384\u5168\u8eab\u529f\u592b\u4e4b\u4e2d\uff0c\u4e0b\u76d8\u4e43\u662f\u4ed6\u8f83\u5f31\u4e00\u73af\uff0c\u800c\u6211\u7684\u2018\u8bd8\u6469\u6b65\u6cd5\u2019\u795e\u5999 \u65e0\u6bd4\uff0c\u6b63\u5e94\u4ee5\u5df1\u4e4b\u5f3a\u5bf9\u5f7c\u4e4b\u5f31\u2014\u2014\u201d \u3000\u3000\u8fd9\u65f6\u4ed6\u624b\u4e0a\u662f\u4e00\u62db\u201c\u65b9\u751f\u4e0d\u606f\u201d\uff0c\u4e43\u662f\u5927\u884d\u5341\u5f0f\u4e2d\u6700\u5177\u5a01\u529b\u7684\u4e00\u5f0f\uff0c\u4f46\u662f\u8f9b\u6377\u8db3\u4e0b \u4e00\u6ed1\uff0c\u8eac\u8eab\u800c\u65bd\uff0c\u76f4\u53d6\u91d1\u9c81\u5384\u4e0b\u76d8\uff0c\u8fd9\u4e00\u62db\u53d8\u5f62\u800c\u4f7f\uff0c\u5a01\u529b\u5927\u51cf\uff0c\u7136\u800c\u6240\u653b\u4e4b\u5904\u4e43\u662f\u91d1 \u9c81\u5384\u4e0b\u76d8\uff0c\u7adf\u5c06\u4ed6\u903c\u5f97\u5012\u9000\u4e09\u6b65\u3002 \u3000\u3000\u8f9b\u6377\u624b\u4e0a\u7684\u201c\u65b9\u751f\u4e0d\u606f\u201d\u6b63\u8981\u6362\u5f0f\uff0c\u5ffd\u7136\u60f3\u5230\u5e73\u51e1\u4e0a\u4eba\u65b9\u624d\u4e34\u654c\u6240\u6388\u7684\u4e00\u62db\uff0c\u5f53\u4e0b \u5fc3\u5934\u5927\u559c\uff0c\u6697\u9053\uff1a \u3000\u3000\u201c\u5999\u554a\uff0c\u539f\u6765\u5e73\u51e1\u4e0a\u4eba\u7b2c\u4e00\u773c\u5c31\u770b\u51fa\u4e86\u91d1\u9c81\u5384\u7684\u5f31\u70b9\uff0c\u624d\u4f20\u6211\u8fd9\u4e00\u62db\uff0c\u8fd9\u4e00\u4e0b\u53ef\u8981 \u4f60\u96be\u9003\u4e00\u5251\u2014\u2014\u201d \u3000\u3000\u5fc3\u4e2d\u5927\u559c\uff0c\u624b\u5934\u56e0\u5206\u5fc3\u7565\u4e3a\u4e00\u6162\uff0c\u5237\u7684\u4e00\u58f0\uff0c\u8863\u8896\u88ab\u957f\u7d22\u5377\u53bb\u5c3a\u8bb8\u4e00\u5927\u5e45\uff0c\u4ed6\u8fde\u5fd9 \u65bd\u51fa\u8bd8\u6469\u795e\u6b65\u5012\u9000\u6570\u5c3a\u2014\u2014 \u3000\u3000\u4f17\u4eba\u89c1\u8f9b\u6377\u5403\u4e86\u4e8f\uff0c\u8138\u4e0a\u53cd\u5012\u663e\u51fa\u559c\u5bb9\uff0c\u602a\u54c9\uff01\u53ea\u6709\u5e73\u51e1\u4e0a\u4eba\u7b11\u563b\u563b\u5730\u80cc\u7740\u53cc\u624b\uff0c \u6697\u6697\u79f0\u8d5e\u8f9b\u6377\u5b7a\u5b50\u53ef\u6559\u3002 \u3000\u3000\u8f9b\u6377\u5de6\u624b\u5251\u8bc0\u4e00\u626c\uff0c\u53f3\u624b\u957f\u5251\u5e73\u633d\u5251\u82b1\uff0c\u5636\u7684\u4e00\u58f0\u76f4\u53d6\u91d1\u9c81\u5384\u7684\u201c\u671f\u95e8\u7a74\u201d\u2014\u2014 \u3000\u3000\u4e00\u8fde\u4e09\u62db\uff0c\u8f9b\u6377\u5168\u662f\u201c\u5927\u884d\u5341\u5f0f\u201d\u7684\u62db\u6570\uff0c\u91d1\u9c81\u5384\u89c1\u4ed6\u7a81\u7136\u4ece\u504f\u5947\u4e4b\u5f0f\u53d8\u4e3a\u4e25\u6b63\u4e4b \u6001\uff0c\u4e0d\u66f2\u5f97\u4e00\u6014\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u8fde\u5341\u62db\u5168\u662f\u5927\u884d\u5341\u5f0f\u7684\u62db\u5f0f\uff0c\u4ed6\u5c06\u88ab\u5173\u4e2d\u4e5d\u8f70\u56f4\u653b\u540e\u609f\u51fa\u7684\u5fc3\u6cd5\u6e17\u5165\u4f7f\u7528\uff0c \u679c\u7136\u5a01\u529b\u5927\u589e\uff0c\u91d1\u9f7f\u5384\u6025\u9053\uff1a\u201c\u4ed6\u8fd9\u5957\u5251\u6cd5\u867d\u7136\u9ad8\u660e\uff0c\u672c\u6765\u6211\u5c3d\u62e6\u5f97\u4f4f\uff0c\u600e\u4e48\u4e00\u4e0b\u5b50\u53c8 \u591a\u51fa\u8bb8\u591a\u53d8\u5316\u6765\uff1f\u201d \u3000\u3000\u5237\u5237\u4e00\u8fde\u4e09\u62db\uff0c\u8f9b\u6377\u5168\u5411\u4ed6\u4e0b\u76d8\u653b\u53bb\u3002\u91d1\u9c81\u5384\u9053\uff1a\u201c\u5b8c\u4e86\uff0c\u53c8\u7ed9\u8fd9\u53ae\u770b\u51fa\u6211\u7684\u5f31\u70b9 \u4e86\u2014\u2014\u201d\u8fde\u5fd9\u5012\u9000\u4e24\u6b65\u3002 \u3000\u3000\u8f9b\u6377\u957f\u5251\u4e00\u6a2a\uff0c\u7a81\u7136\u5316\u505a\u4e00\u7247\u5149\u5e55\u7f69\u5411\u91d1\u9c81\u5384\u7684\u4e0b\u76d8\uff0c\u6b63\u662f\u5e73\u51e1\u4e0a\u4eba\u65b9\u624d\u6240\u6388\u7684\u4e00 \u62db\uff01 \u3000\u3000\u91d1\u9c81\u5384\u957f\u7d22\u4e0b\u626b\uff0c\u771f\u529b\u704c\u6ce8\uff0c\u5ffd\u542c\u8f9b\u6377\u5927\u559d\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u5251\u5149\u624d\u6536\uff0c\u4ed6\u80a9\u5934\u5df2\u4e2d \u4e86\u4e00\u5251\u2014\u2014 \u3000\u3000\u4f17\u4eba\u53ea\u89c1\u5251\u5149\u8fde\u95ea\uff0c\u8eab\u5f62\u4e71\u6643\uff0c\u7136\u540e\u542c\u89c1\u8f9b\u6377\u820c\u7efd\u6625\u96f7\u5730\u4e00\u58f0\uff1a\u201c\u7740\uff01\u201d\u3002\u63a5\u7740\u4eba \u5f71\u9661\u5206\uff0c\u8f9b\u6377\u5355\u5251\u6a2a\u80f8\uff0c\u91d1\u9c81\u5384\u80a9\u4e0a\u8863\u886b\u7834\u788e\uff0c\u9c9c\u8840\u957f\u6d41\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5385\u4e2d\u66b4\u51fa\u9707\u5929\u96f7\u9e23\uff0c\u4f17\u4eba\u6b22\u547c\u4e4b\u58f0\u54cd\u5f7b\u4e91\u9704\uff01 \u3000\u3000\u91d1\u9c81\u5384\u8138\u8272\u94c1\u9752\uff0c\u4e00\u628a\u6293\u4f4f\u52a0\u5927\u5c14\u7684\u624b\u81c2\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u53bb\u4e86\uff0c\u201c\u65e0\u4e3a\u5385\u201d\u4e2d\u53c8\u7206\u51fa \u8f70\u5929\u5f69\u58f0\uff01 \u3000\u3000\u8f9b\u6377\u6253\u8d25\u4e86\u91d1\u9c81\u5384\uff0c\u53cd\u800c\u5fc3\u4e2d\u4e00\u9635\u8ff7\u7cca\uff0c\u4ed6\u4e0b\u610f\u8bc6\u5730\u63d2\u4e0a\u957f\u5251\uff0c\u832b\u832b\u770b\u7740\u72c2\u6b22\u7684\u4f17 \u4eba\u2026\u2026 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7b11\u772f\u772f\u5730\u9053\uff1a\u201c\u5a03\u513f\uff0c\u8fd9\u4e0b\u53ef\u771f\u626c\u540d\u7acb\u4e07\u5566\u2014\u2014\u554a\uff0c\u9669\u4e9b\u628a\u6b63\u4e8b\u5fd8\u5566\uff0c\u5feb \u8d70\u2014\u2014\u201d \u3000\u3000\u4e5f\u4e0d\u5f85\u8f9b\u6377\u540c\u610f\uff0c\u626f\u4f4f\u8f9b\u6377\u624b\u81c2\uff0c\u5982\u4e00\u53ea\u5927\u9e1f\u822c\u4ece\u4f17\u4eba\u5934\u4e0a\u98de\u8fc7\uff0c\u7a7f\u51fa\u5927\u5385\u2014\u2014\u5434 \u51cc\u98ce\u6025\u53eb\u9053\uff1a\u201c\u6377\u5f1f\u2014\u2014\u8001\u524d\u8f88\u8bf7\u7b49\u4e00\u4e0b\u2014\u2014\u201d \u3000\u3000\u6025\u5fd9\u8dd1\u51fa\u5385\u95e8\uff0c\u5e73\u51e1\u4e0a\u4eba\u548c\u8f9b\u6377\u53ea\u5269\u4e0b\u4e00\u4e2a\u6781\u5c0f\u7684\u80cc\u5f71\u4e86\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5bf9\u6377\u5f1f\u7231\u82e5\u540c\u80de\uff0c\u867d\u77e5\u90a3\u8001\u8005\u591a\u534a\u5c31\u662f\u5bf9\u8f9b\u6377\u6781\u6709\u9752\u776c\u7684\u5e73\u51e1\u4e0a\u4eba\uff0c\u4f46\u4ecd\u662f \u5341\u5206\u7126\u6025\u5730\u65bd\u5c55\u8f7b\u529f\u8ffd\u4e86\u4e0a\u53bb\u2014\u2014 \u3000\u3000\u4ed6\u6ca1\u60f3\u5230\u81ea\u5df1\u7684\u8f7b\u529f\u600e\u80fd\u548c\u5e73\u51e1\u4e0a\u4eba\u76f8\u6bd4\uff0c\u4e5f\u5fd8\u4e86\u5385\u4e2d\u7684\u6740\u7236\u4ec7\u4eba\u2014\u2014\u82e6\u5eb5\u53ca\u8d64\u9633\uff0c \u5fc3\u4e2d\u6b64\u65f6\u53ea\u6709\u4e00\u4e2a\u610f\u5ff5\uff0c\u5c31\u662f\u8ffd\u4e0a\u4ed6\u7684\u6377\u5f1f\uff0c\u81f3\u4e8e\u8ffd\u4e0a\u4e4b\u540e\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u4ed6\u4e5f\u62ff\u4e0d\u5b9a\u4e3b \u610f\u2014\u2014 \u3000\u3000\u51cc\u98ce\u89c1\u90a3\u8001\u50e7\u62d6\u7740\u8f9b\u6377\uff0c\u8eab\u5f62\u5fae\u5fae\u6570\u7eb5\uff0c\u4fbf\u5728\u51e0\u5341\u4e08\u5916\uff0c\u4ed6\u7aed\u529b\u8d76\u4e86\u51e0\u6b65\uff0c\u81ea\u77e5\u8d76 \u4e0d\u4e0a\uff0c\u5fc3\u4e0b\u6b63\u81ea\u65e0\u5948\uff0c\u5ffd\u95fb\u80cc\u540e\u98ce\u58f0\u547c\u547c\uff0c\u4e00\u6761\u4eba\u5f71\u548c\u4ed6\u64e6\u8eab\u800c\u8fc7\uff0c\u8eab\u5f62\u75be\u5982\u6d41\u661f\uff0c\u6b63 \u662f\u521a\u624d\u5728\u5927\u5385\u4e0a\u786c\u63a5\u90a3\u756a\u90a6\u6c49\u5b50\u4e00\u638c\u7684\u5c11\u5e74\u2014\u2014\u6b66\u6797\u4e4b\u79c0\uff0c\u51cc\u98ce\u5185\u5fc3\u6697\u60ca\uff1a\u201c\u6211\u5403\u4e86\u8840 \u679c\uff0c\u8f7b\u8eab\u529f\u592b\u624d\u7a81\u98de\u731b\u8fdb\uff0c\u6211\u77e5\u9053\u9664\u4e86\u6377\u5f1f\u5916\uff0c\u5f88\u96be\u518d\u6709\u4eba\u80fd\u4e0e\u6211\u5e76\u9a7e\u9f50\u9a71\uff0c\u60f3\u4e0d\u5230\u8fd9 \u5c11\u5e74\uff0c\u5e74\u9f84\u4e5f\u4e0d\u8fc7\u5927\u6211\u51e0\u5c81\uff0c\u4e0d\u4f46\u5185\u529f\u6df1\u6e5b\uff0c\u8f7b\u529f\u7adf\u4e5f\u5982\u6b64\u4e86\u5f97\u3002\u201d \u3000\u3000\u4ed6\u5185\u5fc3\u4e0d\u670d\uff0c\u5f53\u65f6\u4e5f\u63d0\u6c14\u98de\u5954\uff0c\u8ffd\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c1\u90a3\u5c11\u5e74\u9893\u7136\u800c\u56de\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u89c1\u4e86\u51cc\u98ce\u7a81\u7136\u53c8\u8ffd\u6765\uff0c\u4ed6\u6ca1\u8ffd\u4e0a\u5e73\u51e1\u4e0a\u4eba\uff0c\u6b63\u751f\u4e00\u809a\u5b50\u95f7\u6c14\uff0c\u6c89\u8138\u559d\u9053\uff1a \u201c\u4f60\u8dd1\u6765\u5e72\u4e48\uff1f\u201d \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u957f\u5f97\u5ae9\u76ae\u7ec6\u8089\uff0c\u751a\u662f\u6ed1\u7a3d\u53ef\u4eb2\uff0c\u62c9\u9762\u76b1\u7709\uff0c\u4f46\u8138\u4e0a\u4ecd\u7136\u7b11\u610f\uff0c\u6beb\u65e0\u5a01\u4e25\uff0c \u4e0d\u7531\u5bf9\u4ed6\u9887\u6709\u597d\u611f\u3002 \u3000\u3000\u51cc\u98ce\u662f\u5c11\u5e74\u5fc3\u6027\uff0c\u4ed6\u5bf9\u90a3\u5c11\u5e74\u867d\u6709\u7ed3\u7eb3\u4e4b\u5fc3\uff0c\u4f46\u53e3\u5934\u4e0a\u5374\u6beb\u4e0d\u793a\u5f31\uff0c\u5f53\u4e0b\u8f7b\u677e\u9053\uff1a \u201c\u6211\u539f\u4ee5\u4e3a\u4f60\u8ffd\u4e0a\u4e86\u90a3\u8001\u548c\u5c1a\u548c\u6211\u6377\u5f1f\u54e9\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u542c\u4ed6\u51fa\u8a00\u8ba5\u8bbd\uff0c\u6012\u9053\uff1a\u201c\u600e\u6837\uff0c\u4f60\u60f3\u600e\u6837\uff1f\u201d \u3000\u3000\u51cc\u98ce\u607c\u4ed6\u51fa\u8a00\u65e0\u72b6\uff0c\u6545\u4f5c\u60a0\u95f2\u9053\uff1a\u201c\u4e5f\u6ca1\u600e\u6837\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5927\u6012\u9053\uff1a\u201c\u597d\u72c2\u7684\u5c0f\u5b50\uff0c\u5728\u4e0b\u5012\u8981\u9886\u6559\u3002\u201d \u3000\u3000\u51cc\u98ce\u7b11\u9053\uff1a\u201c\u9886\u6559\uff01\u201d \u3000\u3000\u90a3\u5c11\u5e74\u53cc\u624b\u4e00\u63e1\u62f3\uff0c\u4ece\u80f8\u524d\u5e73\u63a8\u51fa\u6765\uff0c\u51cc\u98ce\u8bc6\u5f97\u8fd9\u662f\u5c11\u6797\u7edd\u624b\u767e\u6b65\u795e\u62f3\uff0c\u5f53\u65f6\u4e0d\u6562 \u6020\u6162\uff0c\u65bd\u5c55\u5f00\u5c71\u4e09\u5f0f\u4e2d\u201c\u516d\u4e01\u5f00\u5c71\u201d\u4e00\u5f0f\u8fce\u51fb\u4e0a\u53bb\uff0c\u4e8c\u4eba\u539f\u672c\u65e0\u610f\u4f24\u5bb3\u5bf9\u65b9\uff0c\u6240\u4ee5\u5747\u672a \u65bd\u51fa\u5168\u529b\uff0c\u62f3\u638c\u76f8\u78b0\uff0c\u5404\u626b\u9000\u540e\u4e24\u6b65\u3002 \u3000\u3000\u51cc\u98ce\u8d5e\u9053\uff1a\u201c\u597d\u529f\u592b\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5fc3\u91cc\u4e5f\u81ea\u6697\u4f69\u51cc\u98ce\u529f\u529b\u6df1\u539a\uff0c\u4ed6\u89c1\u51cc\u98ce\u8d5e\u4ed6\uff0c\u654c\u610f\u4e0d\u7531\u5927\u51cf\uff0c\u5f53\u4e0b\u4fbf\u9053\uff1a \u201c\u5728\u4e0b\u8eab\u6709\u6025\u4e8b\uff0c\u65e0\u6687\u9017\u7559\uff0c\u4ed6\u65e5\u6709\u7f18\uff0c\u518d\u9886\u6559\u9601\u4e0b\u9ad8\u62db\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5b8c\u8bdd\uff0c\u4e5f\u4e0d\u7b49\u51cc\u98ce\u56de\u7b54\uff0c\u5f84\u5411\u539f\u8def\u75be\u5954\u800c\u53bb\u3002 \u3000\u3000\u51cc\u98ce\u5bf9\u4ed6\u539f\u65e0\u6076\u610f\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u62e6\u963b\uff0c\u5ffd\u7136\u60f3\u5230\u6740\u7236\u4ec7\u4eba\u8fd8\u5728\u5385\u4e0a\uff0c\u7acb\u523b\u98de\u5954\u800c\u56de\u3002 \u3000\u3000\u4ed6\u7a9c\u8fdb\u5927\u5385\uff0c\u53ea\u89c1\u7a7a\u7a7a\u7684\u53ea\u6709\u51e0\u4e2a\u65e0\u540d\u4e4b\u8f88\uff0c\u539f\u6765\u4ed6\u521a\u624d\u8fd9\u4e00\u9017\u7559\uff0c\u4e2d\u539f\u8bf8\u597d\u6c49\u90fd \u8d70\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u4ed6\u626b\u4e86\u4e24\u773c\uff0c\u4e0d\u89c1\u4ec7\u4eba\u8e2a\u8ff9\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u7684\u4ec7\u4eba\u90fd\u662f\u8d6b\u8d6b\u6709\u540d\u4e4b\u8f88\uff0c\u4ed6 \u65e5\u6211\u767b\u95e8\u95ee\u7f6a\uff0c\u4ed6\u4eec\u5fc5\u7136\u4e0d\u81f4\u8eb2\u533f\uff0c\u8fd8\u6015\u627e\u4e0d\u7740\u5417\uff1f\u201d\u8f6c\u5ff5\u53c8\u60f3\u9053\uff1a\u201c\u521a\u624d\u90a3\u8001\u50e7\u6b66\u529f \u6df1\u4e0d\u53ef\u6d4b\uff0c\u4e0e\u6377\u5f1f\u53c8\u4f3c\u76f8\u8bc6\uff0c\u53ea\u6015\u591a\u534a\u662f\u6377\u5f1f\u5e38\u8bb2\u7684\u6d77\u5916\u4e09\u4ed9\u4e4b\u4e00\u5e73\u51e1\u4e0a\u4eba\uff0c\u770b\u4ed6\u5bf9\u6377 \u5f1f\u751a\u662f\u6b23\u8d4f\uff0c\u8fd9\u4e00\u53bb\u4e0d\u77e5\u53c8\u8981\u4f20\u6388\u6377\u5f1f\u591a\u5c11\u7edd\u5b66\u54e9\uff01\u201d \u3000\u3000\u201c\u6211\u7b54\u5e94\u8fc7\u82cf\u59d1\u5a18\u8981\u53bb\u770b\u5979\uff0c\u5012\u4e5f\u4e0d\u80fd\u5931\u4fe1\u4e8e\u5979\u3002\u201d \u3000\u3000\u4ed6\u76d8\u7b97\u5df2\u5b9a\uff0c\u4fbf\u542f\u7a0b\u8d74\u7ea6\u3002 \u3000\u3000\u5f53\u4ed6\u8d70\u5230\u5c71\u4e1c\u5883\u5185\uff0c\u53ea\u89c1\u6cbf\u8def\u90fd\u662f\u6276\u8001\u643a\u5e7c\uff0c\u80cc\u8d1f\u91cd\u7269\u7684\u4eba\uff0c\u4e00\u8138\u75b2\u4e4f\u795e\u8272\uff0c\u50cf\u662f \u9003\u96be\u907f\u5175\u7684\u6a21\u6837\uff0c\u5185\u5fc3\u5f88\u5947\u602a\uff0c\u5fc3\u60f3\u5f53\u4eca\u5929\u4e0b\u6e05\u5e73\uff0c\u600e\u4f1a\u6709\u5175\u71f9\u4e4b\u707e\uff0c\u7ec8\u7a76\u627e\u5230\u4e00\u4e2a\u957f \u8005\u8be2\u95f4\u539f\u56e0\u3002 \u3000\u3000\u90a3\u8001\u8005\u542c\u51cc\u98ce\u4e5f\u662f\u672c\u5730\u53e3\u58f0\uff0c\u77e5\u4ed6\u624d\u4ece\u4ed6\u4e61\u8fd4\u4e61\uff0c\u53f9\u606f\u9053\uff1a\u201c\u6708\u524d\u51e0\u573a\u6025\u96e8\uff0c\u9ec4\u6cb3 \u6c34\u91cf\u5927\u662f\u589e\u6da8\uff0c\u7ec8\u5728\u65b9\u5bb6\u6751\u51b2\u7834\u6cb3\u5824\uff0c\u6df9\u6ca1\u4e86\u5168\u6751\uff0c\u4ffa\u5bb6\u4e61\u79bb\u65b9\u5bb6\u6751\u4e0d\u8fc7\u767e\u5341\u91cc\uff0c\u8fd9\u624d \u5e26\u7740\u5bb6\u5c0f\u2026\u2026\u201d \u3000\u3000\u51cc\u98ce\u4e0d\u5f85\u4ed6\u8bf4\u5b8c\uff0c\u7126\u6025\u95ee\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u90a3\u6797\u6751\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u8001\u8005\u9053\uff1a\u201c\u5ba2\u5b98\u662f\u95ee\u9ad8\u5bb6\u6751\u897f\u4e94\u5341\u91cc\u7684\u6797\u6751\u4e48\uff1f\u5982\u4eca\u53ea\u6015\u5df2\u662f\u6c6a\u6d0b\u4e00\u7247\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u5411\u8001\u8005\u9053\u4e86\u8c22\uff0c\u8db3\u4e0d\u7a0d\u505c\u5411\u4e1c\u8d76\u53bb\u3002 \u3000\u3000\u4ed6\u60f3\u5230\u5927\u5a18\u6bcd\u5973\u7684\u5a07\u5f31\uff0c\u9047\u5230\u8fd9\u51f6\u731b\u5929\u707e\uff0c\u53ea\u6015\u51f6\u591a\u5409\u5c11\uff0c\u5185\u5fc3\u6709\u5982\u706b\u711a\uff0c\u4e5f\u987e\u4e0d \u5f97\u767d\u65e5\u4e4b\u4e0b\u5f15\u4eba\u6ce8\u76ee\uff0c\u65bd\u5c55\u8f7b\u529f\uff0c\u53d1\u8db3\u98de\u5954\u3002 \u3000\u3000\u4ed6\u4ece\u65e9\u8dd1\u5230\u508d\u665a\uff0c\u4e2d\u5348\u4e5f\u4e0d\u53ca\u5403\u996d\uff0c\u53ea\u89c1\u8def\u4e0a\u96be\u6c11\u6108\u6765\u6108\u591a\uff0c\u5fc3\u5185\u6108\u89c9\u61ca\u70ed\uff0c\u5f85\u4ed6 \u8d76\u5230\u8ddd\u6797\u6751\u4ec5\u6709\u767e\u4f59\u91cc\uff0c\u4e00\u95ee\u96be\u6c11\uff0c\u624d\u77e5\u6797\u6751\u5468\u56f4\u5341\u91cc\u4e8e\u6628\u591c\u6df9\u6ca1\u3002 \u3000\u3000\u51cc\u98ce\u4e00\u542c\uff0c\u6709\u5982\u7126\u96f7\u8f70\u9876\uff0c\u4ed6\u5446\u5446\u7684\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u60f3\uff0c\u4ed6\u5f3a\u5236\u81ea\u5df1\u7684\u4f24\u75db\uff0c\u60f3\u7740\u63f4\u6551 \u963f\u5170\u6bcd\u5973\u7684\u6cd5\u5b50\u3002 \u3000\u3000\u4ed6\u5bfb\u601d\u9053\uff1a\u201c\u90a3\u4e2a\u8305\u623f\u672c\u662f\u4f9d\u7740\u5c71\u5761\u8fde\u7b51\u7684\uff0c\u5730\u52bf\u751a\u662f\u9ad8\u4ea2\uff0c\u5982\u679c\u722c\u5728\u5c4b\u9876\u4e0a\uff0c\u5927 \u534a\u65e5\u4e4b\u95f4\uff0c\u6c34\u6015\u4e5f\u6df9\u4e0d\u5230\u3002\u6797\u6751\u65e2\u5df2\u6df9\u6c34\uff0c\u9646\u8def\u662f\u8d70\u4e0d\u901a\u4e86\uff0c\u4e0d\u5982\u5c31\u5728\u6b64\u96c7\u8239\u3002\u201d \u3000\u3000\u4ed6\u51fa\u9ad8\u4ef7\u96c7\u4e86\u4e00\u4e2a\u68a2\u516c\uff0c\u5212\u4e86\u4e00\u53ea\u5c0f\u8239\uff0c\u6eaf\u6c34\u800c\u4e0a\u3002 \u3000\u3000\u6b64\u65f6\u6c34\u52bf\u751a\u662f\u6e4d\u6025\uff0c\u90a3\u68a2\u516c\u8d39\u5c3d\u529b\u6c14\u5212\u53bb\uff0c\u8239\u884c\u4ecd\u7136\u751a\u6162\uff0c\u51cc\u98ce\u5185\u5fc3\u5927\u6025\uff0c\u5f53\u65f6\u5411 \u68a2\u516c\u8ba8\u4e86\u4e00\u53ea\u6868\uff0c\u8fd0\u8d77\u5185\u529b\uff0c\u5212\u4e86\u8d77\u6765\uff0c\u90a3\u5c0f\u8239\u5403\u4ed6\u8fd9\u53ea\u6868\u4e0d\u505c\u5730\u62e8\u6c34\uff0c\u679c\u7136\u524d\u8fdb\u795e\u901f\u3002 \u3000\u3000\u884c\u4e86\u4e09\u4e2a\u65f6\u8fb0\uff0c\u5df2\u662f\u5348\u591c\u65f6\u5206\uff0c\u90a3\u68a2\u516c\u7cbe\u75b2\u529b\u7aed\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u575a\u6301\u9760\u5cb8\u4f11\u606f\uff0c \u51cc\u98ce\u4e5f\u4e0d\u7406\u4f1a\u4ed6\uff0c\u4e00\u4e2a\u4eba\u64cd\u6868\u50ac\u821f\u7eed\u8fdb\u3002 \u3000\u3000\u53c8\u884c\u4e86\u4e00\u4f1a\uff0c\u6c34\u9762\u7a81\u7136\u5927\u5bbd\uff0c\u539f\u6765\u6c34\u9053\u4e5f\u5206\u4e0d\u51fa\u6765\uff0c\u53ea\u662f\u832b\u832b\u7684\u4e00\u7247\u6c6a\u6d0b\uff0c\u51cc\u98ce\u5fc3 \u77e5\u5230\u4e86\u6d2a\u6c34\u4e3a\u60a3\u7684\u533a\u57df\uff0c\u8ddd\u79bb\u6797\u6751\u5df2\u662f\u4e0d\u8fdc\uff0c\u594b\u8d77\u795e\u529b\uff0c\u8fd0\u6868\u5982\u98de\u3002 \u3000\u3000\u4ed6\u89c1\u6cbf\u9014\u6751\u843d\uff0c\u90fd\u5df2\u6df9\u6ca1\uff0c\u5f88\u591a\u6751\u6c11\u90fd\u722c\u5230\u6811\u68a2\u6216\u5c4b\u9876\u4e0a\uff0c\u624b\u4e2d\u70b9\u7740\u706b\u628a\u3002\u4f17\u4eba\u89c1 \u51cc\u98ce\u5c0f\u8239\u7ecf\u8fc7\uff0c\u7eb7\u7eb7\u6447\u52a8\u706b\u628a\uff0c\u5636\u58f0\u6c42\u6551\u3002 \u3000\u3000\u51cc\u98ce\u60f3\u5230\u963f\u5170\u6bcd\u5973\u8eab\u5904\u5371\u5883\uff0c\u5f53\u65f6\u786c\u8d77\u5fc3\u80a0\uff0c\u53ea\u4f5c\u6ca1\u6709\u542c\u89c1\u3002 \u3000\u3000\u6108\u6765\u6108\u8fd1\u6797\u6751\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u4e5f\u8d8a\u6765\u8d8a\u662f\u7d27\u5f20\uff0c\u624b\u5fc3\u4e0a\u51fa\u4e86\u4e00\u9635\u51b7\u6c57\uff0c\u4ed6\u60f3\uff1a\u201c\u53ea\u8981\u2026\u2026 \u53ea\u8981\u722c\u4e0a\u5c4b\u9876\uff0c\u90a3\u5c31\u4e0d\u4f1a\u6709\u4ec0\u4e48\u95ee\u9898\u4e86\u3002\u201d \u3000\u3000\u5c0f\u8239\u9a76\u8fdb\u6797\u6751\u4e86\uff01 \u3000\u3000\u51cc\u98ce\u4e00\u9897\u5fc3\u51e0\u4e4e\u8981\u8df3\u51fa\u53e3\u8154\uff0c\u4ed6\u4e3e\u76ee\u56db\u671b\uff0c\u90a3\u662f\u4e00\u7247\u65e0\u9645\u7684\u6c34\u9762\uff0c\u6574\u4e2a\u6797\u6751\u7684\u5efa\u7b51 \u7269\uff0c\u90fd\u88ab\u6df9\u5728\u6c34\u4e0b\uff0c\u53ea\u6709\u5c0f\u6eaa\u65c1\u51e0\u682a\u68a7\u6850\u6811\uff0c\u8fd8\u5728\u6c34\u9762\u9732\u51fa\u4e86\u6811\u5c16\u3002 \u3000\u3000\u4ed6\u5185\u5fc3\u6df1\u5904\u611f\u5230\u51b0\u51c9\uff0c\u4ed6\u72c2\u5954\u64cd\u821f\u4e00\u65e5\u4e00\u591c\uff0c\u5185\u529b\u6d88\u8017\u5df2\u5c3d\uff0c\u6b64\u65f6\u652f\u6301\u4ed6\u8eab\u4f53\u7684 \u201c\u5e0c\u671b\u201d\uff0c\u53c8\u544a\u5e7b\u706d\uff0c\u53ea\u89c9\u5168\u8eab\u8f6f\u5f31\uff0c\u518d\u4e5f\u63d0\u4e0d\u52a8\u5927\u6728\u6868\uff0c\u201c\u7830\uff01\u201d\u7684\u4e00\u58f0\uff0c\u6728\u6868\u843d\u5230 \u6728\u677f\u4e0a\uff0c\u4eba\u4e5f\u59d4\u987f\u5012\u5730\u3002 \u3000\u3000\u51cc\u98ce\u81ea\u5e7c\u5931\u6019\uff0c\u4e00\u76f4\u89c6\u5927\u5a18\u5982\u6148\u6bcd\u3002\u90a3\u963f\u5170\uff0c\u66f4\u662f\u4ed6\u5fc3\u76ee\u4e2d\u6700\u5b8c\u6574\uff0c\u6700\u7f8e\u4e3d\u7684\u5973\u5b69\uff0c \u4ed6\u4eec\u4fe9\uff0c\u867d\u7136\u5e76\u6ca1\u6709\u8bf4\u8fc7\u4e00\u53e5\u7231\u6155\u5bf9\u65b9\u7684\u8bdd\uff0c\u53ef\u662f\uff0c\u5f7c\u6b64\u95f4\u4eb2\u5207\u7684\u4f53\u8d34\uff0c\u6df1\u60c5\u7684\u5fae\u7b11\uff0c \u90a3\u4e0d\u80dc\u8fc7\u5343\u76df\u4e07\u8a93\u5417\uff1f \u3000\u3000\u4ed6\u5929\u6027\u751a\u662f\u6de1\u6cca\uff0c\u4e00\u751f\u6700\u5927\u7684\u5e0c\u671b\u5c31\u662f\u624b\u5203\u7236\u4ec7\uff0c\u5bfb\u6c42\u8840\u679c\uff0c\u4f7f\u963f\u5170\u91cd\u89c1\u5149\u660e\uff0c\u7136 \u540e\u2026\u2026\u7136\u540e\u5e26\u7740\u963f\u5170\u6bcd\u5973\uff0c\u4f4f\u5728\u4e00\u4e2a\u98ce\u666f\u5982\u753b\u7684\u5730\u65b9\u2026\u2026\u53ef\u662f\uff0c\u5982\u4eca\u5462\uff1f\u4e00\u751f\u7684\u7f8e\u68a6\uff0c \u7b97\u662f\u5b8c\u5168\u7834\u88c2\u7c89\u788e\u4e86\u2026\u2026 \u3000\u3000\u51cc\u98ce\u53ea\u89c9\u80f8\u4e2d\u4e00\u9635\u706b\u70ed\uff0c\u63a5\u7740\u4e00\u9635\u51b0\u51c9\uff0c\u4ed6\u4eff\u4f5b\u542c\u5230\u4e86\u6d41\u8840\u58f0\uff0c\u90a3\u662f\u5fc3\u623f\u5728\u6d41\u8840\u5427\uff0c \u4ed6\u4eff\u4f5b\u542c\u5230\u4e86\u7834\u88c2\u58f0\uff0c\u90a3\u662f\u5fc3\u623f\u5728\u788e\u88c2\u5427\uff01 \u3000\u3000\u4ed6\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u53cd\u590d\u541f\u9053\uff1a\u201c\u4e14\u592b\u5929\u5730\u4e3a\u7089\u516e\uff0c\u9020\u5316\u4e3a\u5de5\uff0c\u9634\u9633\u4e3a\u70ad\u516e\uff0c\u4e07\u7269\u4e3a \u94dc\u3002\u201d \u3000\u3000\u662f\u7684\uff0c\u5728\u8fd9\u4e2a\u4e16\u4e0a\u771f\u662f\u82e6\u591a\u4e50\u5c11\uff0c\u9664\u4e86\u751f\u79bb\u3001\u6b7b\u522b\u3001\u7edd\u671b\u3001\u75db\u82e6\uff0c\u54ea\u8fd8\u6709\u4ec0\u4e48\uff1f \u3000\u3000\u4ed6\u53ea\u89c9\u5f97\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e16\u4e0a\u4e00\u5207\u90fd\u4e0e\u4ed6\u4e0d\u518d\u6709\u5173\u8054\u4e86\uff0c\u4ed6\u7684\u601d\u60f3\u8fdb\u5230\u53e6\u5916\u4e00\u4e2a\u4e16 \u754c\u2026\u2026 \u3000\u3000\u201c\u90a3\u513f\u6ca1\u6709\u6101\u82e6\uff0c\u6ca1\u6709\u79bb\u522b\uff0c\u53ea\u6709\u6b22\u4e50\u2014\u2014\u6c38\u6052\u7684\u6b22\u4e50\uff0c\u904d\u5730\u90fd\u662f\u9c9c\u82b1\u3002\u90a3\u767d\u680f\u6746 \u4e0a\u9760\u7740\u4e00\u4e2a\u7f8e\u4e3d\u7684\u59d1\u5a18\uff0c\u5979\u6258\u7740\u5934\uff0c\u6b63\u5728\u60f3\u5ff5\u6211\uff0c\u76f8\u601d\u7684\u773c\u6cea\uff0c\u4e00\u9897\u9897\u50cf\u73cd\u73e0\uff0c\u6ef4\u5728\u9c9c \u8273\u7684\u82b1\u6735\u4e0a\uff0c\u90a3\u82b1\u5f00\u5f97\u66f4\u5a07\u8273\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u53e3\u4e2d\u5583\u5583\u9053\uff1a\u201c\u963f\u5170\uff0c\u963f\u5170\uff0c\u4f60\u522b\u54ed\uff0c\u5927\u54e5\u5c31\u6765\u966a\u4f60\u5566\uff01\u201d \u3000\u3000\u4ed6\u6b63\u5728\u5982\u75f4\u5982\u9189\uff0c\u7a81\u7136\uff0c\u80cc\u540e\u6709\u4eba\u63a8\u4ed6\u4e00\u628a\uff0c\u624d\u60ca\u7834\u4ed6\u7684\u5e7b\u5883\uff0c\u56de\u5934\u4e00\u770b\uff0c\u6b63\u662f\u90a3 \u68a2\u516c\u3002 \u3000\u3000\u539f\u6765\u9002\u624d\u4ed6\u6728\u6868\u843d\u5730\uff0c\u68a2\u516c\u5df2\u88ab\u60ca\u9192\uff0c\u70b9\u4e86\u4e00\u4e2a\u706b\u628a\uff0c\u722c\u5012\u7532\u677f\u4e0a\uff0c\u53ea\u89c1\u51cc\u98ce\u795e\u8272 \u5927\u53d8\uff0c\u8138\u4e0a\u6ca1\u6709\u4e00\u4e1d\u8840\u8272\uff0c\u75f4\u75f4\u5446\u5446\u5730\u5750\u5728\u8239\u5934\uff0c\u6b63\u60f3\u4e0a\u524d\u62db\u547c\uff0c\u5ffd\u53c8\u89c1\u4ed6\u8138\u9732\u60e8\u7b11\uff0c \u795e\u8272\u602a\u5f02\u4e4b\u6781\uff0c\u53e3\u4e2d\u53c8\u662f\u81ea\u8a00\u81ea\u8bed\uff0c\u518d\u4e5f\u6309\u7eb3\u4e0d\u4f4f\uff0c\u662f\u4ee5\u63a8\u4e86\u51cc\u98ce\u4e00\u628a\u3002 \u3000\u3000\u51cc\u98ce-\u60ca\u4e4b\u4e0b\uff0c\u601d\u6f6e\u987f\u53bb\uff0c\u56de\u5230\u73b0\u5b9e\uff0c\u4ed6\u82e6\u601d\u4eca\u540e\u7684\u884c\u6b62\uff0c\u4f46\u662f\u5fc3\u75db\u5982\u7ede\uff0c\u518d\u4e5f\u60f3 \u4e0d\u51fa\u4ec0\u4e48\u3002 \u3000\u3000\u5929\u8272\u65e5\u660e\uff0c\u4ed6\u5429\u5490\u68a2\u516c\u987a\u6c34\u5212\u56de\u3002 \u3000\u3000\u8fd9\u987a\u6c34\u884c\u821f\uff0c\u786e\u5b9e\u5feb\u6377\u65e0\u6bd4\uff0c\u4e0d\u6d88\u4e24\u4e2a\u65f6\u8fb0\uff0c\u4fbf\u5230\u8fbe\u5cb8\u8fb9\u3002\u51cc\u98ce\u832b\u7136\u4e0b\u4e86\u8239\uff0c\u5728\u4eba \u6c11\u7fa4\u4e2d\uff0c\u770b\u8fc7\u6bcf\u5f20\u9762\u5b54\uff0c\u4e5f\u4e0d\u89c1\u5927\u5a18\u6bcd\u5973\uff0c\u5f53\u65f6\u66f4\u80af\u5b9a\u4ed6\u4eec\u5df2\u906d\u5927\u6c34\u51b2\u8d70\u3002 \u3000\u3000\u4ed6\u4e07\u5ff5\u4ff1\u7070\uff0c\u4e0d\u613f\u6df7\u5728\u4e71\u7cdf\u7cdf\u7684\u96be\u6c11\u4e2d\uff0c\u4ed6\u53ea\u60f3\u4e00\u4e2a\u4eba\u6e05\u9759\u3001\u5b64\u72ec\u7684\u56de\u5fc6\u3002\u5480\u56bc\u6614 \u65e5\u6bcf\u4e00\u4e2a\u5c0f\u52a8\u4f5c\u3001\u6bcf\u4e00\u53e5\u8bdd\u3002 \u3000\u3000\u51cc\u98ce\u907f\u5f00\u5927\u9053\uff0c\u4e13\u62e3\u8352\u51c9\u7684\u5c71\u8def\uff0c\u7ffb\u5c71\u8d8a\u5cad\u6f2b\u65e0\u76ee\u5730\u7684\u8d70\u7740\uff0c\u997f\u4e86\u4fbf\u91c7\u51e0\u6839\u91ce\u83dc\u5145 \u9965\uff0c\u6e34\u4e86\u5c31\u6367\u4e00\u68d2\u6cc9\u6c34\u89e3\u6e34\u3002\u90a3\u5c71\u8def\u8fde\u5ef6\u4e0d\u7edd\uff0c\u4f3c\u4e4e\u6ca1\u6709\u4e00\u4e2a\u5c3d\u5934\uff0c\u51cc\u98ce\u5fc3\u60f3\uff1a\u201c\u8ba9\u8fd9 \u5c71\u8def\u7684\u5c3d\u5934\u4e5f\u5c31\u4f5c\u6211\u751f\u547d\u7684\u5c3d\u5934\u5427\uff01\u201d \u3000\u3000\u4ed6\u81ea\u66b4\u81ea\u5f03\uff0c\u884c\u4e86\u51e0\u65e5\uff0c\u5f62\u5bb9\u662f\u5927\u67af\u69c1\uff0c\u8fd9\u5929\u7ffb\u8fc7\u5c71\u5934\uff0c\u53ea\u89c1\u524d\u9762\u5c31\u662f\u4e00\u6761\u5b98\u9053\uff0c \u901a\u5230\u6d4e\u5b81\uff0c\u5fc3\u4e2d\u4e00\u60ca\u9053\uff1a\u201c\u82cf\u59d1\u5a18\u5c31\u4f4f\u5728\u6d4e\u5b81\uff0c\u6211\u53bb\u770b\u5979\u4e00\u8d9f\uff0c\u518d\u53bb\u627e\u90a3\u51e0\u4e2a\u8001\u8d3c\u62a5\u4ec7\uff0c \u7136\u540e\u2026\u2026\u201d\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u4eca\u540e\u7684\u5f52\u4f9d\u3002 \u3000\u3000\u51cc\u98ce\u8fdb\u4e86\u57ce\u3002 \u3000\u3000\u4ed6\u8d70\u8fc7\u4e24\u6761\u8857\uff0c\u89c1\u5230\u4e00\u5bb6\u9ed1\u6f06\u9576\u91d1\u7684\u5927\u95e8\uff0c\u95e8\u53e3\u7ad9\u5728\u4e24\u4e2a\u5175\u4e01\uff0c\u77e5\u662f\u77e5\u5e9c\u516c\u9986\u3002\u8d8b \u524d\u95ee\u9053\uff1a\u201c\u8fd9\u53ef\u662f\u77e5\u5e9c\u516c\u9986\u4e48\uff1f\u5728\u4e0b\u5434\u51cc\u98ce\u8bf7\u95ee\u82cf\u60e0\u82b7\u59d1\u5a18\u53ef\u5728\uff1f\u201d \u3000\u3000\u90a3\u5175\u4e01\u89c1\u4ed6\u5f62\u5bb9\u867d\u662f\u6194\u60b4\uff0c\u8863\u7740\u751a\u662f\u8934\u891b\uff0c\u4f46\u633a\u9f3b\u4fca\u76ee\uff0c\u4ecd\u662f\u4e00\u8868\u4eba\u624d\uff0c\u53c8\u542c\u4ed6\u95ee \u77e5\u5e9c\u4e49\u5973\uff0c\u77e5\u662f\u5927\u6709\u6765\u5386\u4e4b\u4eba\uff0c\u5f53\u4e0b\u4e0d\u6562\u6020\u6162\uff0c\u8dd1\u8fdb\u53bb\u901a\u62a5\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u51fa\u6765\u4e00\u4e2a\u7ba1\u5bb6\u6a21\u6837\u7684\u4eba\uff0c\u5411\u51cc\u98ce\u606d\u606d\u656c\u656c\u4e00\u63d6\u9053\uff1a \u3000\u3000\u201c\u5434\u516c\u5b50\u8bf7\u8ff8\uff0c\u5c0f\u59d0\u5728\u5385\u4e0a\u76f8\u5f85\u3002\u201d \u3000\u3000\u51cc\u98ce\u8fd8\u4e86\u4e00\u63d6\uff0c\u8ddf\u7740\u90a3\u7ba1\u5bb6\uff0c\u8d70\u4e86\u8fdb\u53bb\uff0c\u53ea\u89c1\u90a3\u77e5\u5e9c\u5e9c\u751a\u662f\u6c14\u6d3e\uff0c\u4e00\u6761\u5927\u8def\u76f4\u901a\u5ba2 \u5385\uff0c\u4e24\u65c1\u690d\u6ee1\u4e86\u7261\u4e39\uff0c\u7ea2\u82b1\u7eff\u53f6\uff0c\u5f00\u5f97\u975e\u5e38\u5a07\u8273\u3002 \u3000\u3000\u4ed6\u624d\u8d70\u4e86\u4e00\u534a\uff0c\u82cf\u60e0\u82b7\u5df2\u63a8\u5f00\u95e8\u8fce\u4e86\u4e0a\u6765\uff0c\u51cc\u98ce\u89c1\u5979\u7b11\u9765\u5982\u82b1\uff0c\u795e\u8272\u9ad8\u5174\u5df2\u6781\uff0c\u6570 \u6708\u4e0d\u89c1\uff0c\u867d\u7136\u7565\u89c1\u6e05\u7626\uff0c\u4f46\u8138\u4e0a\u7a1a\u6c14\u5927\u6d88\uff0c\u51fa\u843d\u5f97\u66f4\u4e3a\u660e\u4e3d\u3002 \u3000\u3000\u51cc\u98ce\u4e00\u63d6\u9053\uff1a\u201c\u82cf\u59d1\u5a18\u8fd1\u6765\u53ef\u597d\uff1f\u6211\u90a3\u6377\u5f1f\u672c\u548c\u6211\u4e00\u8d77\u6765\u770b\u4f60\uff0c\u4f46\u5728\u8def\u4e0a\u88ab\u4e00\u4f4d\u8001 \u524d\u8f88\u53eb\u53bb\uff0c\u4ed6\u53eb\u6211\u4ee3\u5411\u4f60\u81f4\u610f\u3002 \u3000\u3000\u82cf\u60e0\u82b7\u5fd9\u4e00\u88e3\u887d\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5434\u516c\u5b50\u5feb\u8bf7\u8fdb\u5c4b\uff0c\u90a3\u65e5\u4e00\u522b\uff0c\u6211\u65f6\u5fc3\u7275\u6302\uff0c\u65e5\u65e5\u76fc\u60a8 \u65e9\u6765\u770b\u6211\u2026\u2026\u201d\u5979\u8bf4\u5230\u8fd9\u513f\u53d1\u89c9\u8bed\u75c5\uff0c\u8138\u4e00\u7ea2\uff0c\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u51cc\u98ce\u77a7\u7740\u5979\u90a3\u53cc\u6e05\u6f88\u5982\u6c34\u7684\u5927\u773c\uff0c\u4e0d\u7518\u53c8\u60f3\u8d77\u963f\u5170\uff0c\u5fc3\u4e2d\u53f9\u9053\uff1a\u201c\u5509\uff01\u591a\u4e48\u50cf\u554a\uff01 \u53ef\u662f\u4e00\u4e2a\u8fd9\u4e48\u5e78\u8fd0\uff0c\u53e6\u4e00\u4e2a\u5374\u662f\u90a3\u4e48\u60b2\u60e8\uff0c\u8001\u5929\uff01\u8001\u5929\uff01\u4f60\u592a\u4e0d\u516c\u5e73\u4e86\u3002\u201d \u3000\u3000\u8559\u82b7\u89c1\u4ed6\u5ffd\u7136\u5446\u75f4\uff0c\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u53c8\u89c1\u4ed6\u8138\u8272\u6194\u60b4\uff0c\u4e0d\u89c9\u53c8\u7231\u53c8\u4f36\u3002 \u3000\u3000\u5979\u67d4\u58f0\u9053\uff1a\u201c\u5434\u76f8\u516c\uff0c\u60a8\u662f\u4ece\u6df9\u6c34\u5730\u65b9\u6765\u7684\u5417\uff1f\u201d \u3000\u3000\u51cc\u98ce\u70b9\u70b9\u5934\u3002\u8559\u82b7\u63a5\u7740\u9053\uff1a\u201c\u90a3\u9ec4\u6cb3\u786e\u662f\u5e74\u5e74\u6cdb\u6ee5\uff0c\u6cbb\u6cb3\u7684\u5b98\u513f\uff0c\u5e73\u65e5\u53ea\u77e5\u641c\u62ec\u6c11 \u8102\u6c11\u818f\uff0c\u4e00\u65e6\u5927\u6c34\u4e34\u5934\uff0c\u8dd1\u5f97\u6bd4\u8c01\u90fd\u5feb\u3002\u8fd9\u6b21\u5927\u6c34\uff0c\u5982\u679c\u4e8b\u5148\u9632\u8303\u5468\u8be6\uff0c\u603b\u4e0d\u81f3\u4e8e\u5982\u6b64\u3002 \u6211\u4e49\u7236\u4e3a\u6b64\u4e8b\u5927\u4e3a\u9707\u6012\uff0c\u5df2\u4e0a\u7701\u57ce\u53bb\u8bf7\u793a\u4e86\u3002 \u3000\u3000\u51cc\u98ce\u5fc3\u5ff5\u4e00\u52a8\uff0c\u6b63\u6b32\u5f00\u53e3\u76f8\u95f4\uff0c\u4f46\u82cf\u60e0\u82b7\u5374\u662f\u6b22\u6109\u5df2\u6781\uff0c\u53e3\u4e2d\u4e0d\u65ad\u5730\u8bf4\u522b\u540e\u4e4b\u4e8b\u3002 \u3000\u3000\u539f\u6765\u90a3\u5929\u82cf\u60e0\u82b7\u6295\u5954\u5979\u7236\u4eb2\u65e7\u90e8\u6c38\u6d4e\u77e5\u5e9c\uff0c\u90a3\u77e5\u5e9c\u59d3\u91d1\uff0c\u539f\u662f\u82cf\u60e0\u82b7\u7236\u4eb2\u4e00\u624b\u63d0\u62d4\uff0c \u89c1\u4e86\u82cf\u59d1\u5a18\uff0c\u81ea\u662f\u7231\u62a4\u5c0a\u656c\uff0c\u4ed6\u77e5\u82cf\u4f8d\u90ce\u4e00\u751f\u6b63\u76f4\uff0c\u8d64\u80c6\u5fe0\u5fc3\u4e3a\u56fd\u4e8b\u5fe7\uff0c\u7adf\u7136\u547d\u4e27\u8d3c\u5b50 \u4e4b\u624b\uff0c\u4e0d\u7981\u559f\u7136\u3002 \u3000\u3000\u8fd9\u91d1\u77e5\u5e9c\uff0c\u867d\u5df2\u5e74\u8fc7\u4e94\u65ec\uff0c\u819d\u4e0b\u4ecd\u662f\u865a\u865a\uff0c\u82cf\u60e0\u82b7\u89c1\u5979\u5f85\u81ea\u5df1\u4eb2\u5207\u6148\u7965\uff0c\u53c8\u542c\u4ed6\u65f6 \u65f6\u53f9\u606f\u81ea\u5df1\u547d\u4e2d\u65e0\u5b50\uff0c\u4fbf\u62dc\u4ed6\u4e3a\u4e49\u7236\uff0c\u91d1\u77e5\u5e9c\u53ea\u4e50\u5f97\u5982\u5f97\u7470\u5b9d\u3002 \u3000\u3000\u51cc\u98ce\u539f\u610f\u9017\u7559\u4e00\u523b\uff0c\u4fbf\u8981\u544a\u8f9e\uff0c\u4f46\u89c1\u82cf\u60e0\u82b7\u60c5\u610f\u6bb7\u6bb7\uff0c\u7adf\u4e0d\u5fcd\u5f00\u53e3\u3002 \u3000\u3000\u82cf\u60e0\u82b1\u8bf4\u4e86\u4e00\u9635\uff0c\u770b\u5230\u51cc\u98ce\u542c\u5f97\u5f88\u4e13\u5fc3\uff0c\u5fc3\u4e2d\u6697\u559c\u3002\u5979\u5ffd\u5bdf\u89c9\u9053\uff1a\u201c\u5434\u76f8\u516c\uff0c\u4f60\u77a7 \u6211\u9ad8\u5174\u5f97\u7cca\u6d82\u5566\uff01\u60a8\u4e00\u8def\u4e0a\u8d76\u6765\uff0c\u5b9a\u662f\u75b2\u5026\u4e86\uff0c\u6211\u8fd8\u5520\u5520\u53e8\u53e8\u7684\u7f57\u55e6\u3002\u60a8\u5148\u6362\u6362\u8863\uff0c\u4f11 \u606f\u4e00\u4f1a\u5427\uff01\u201d \u3000\u3000\u5979\u7acb\u523b\u5429\u5490\u5a62\u5b50\u5907\u6c34\uff0c\u51cc\u98ce\u53ea\u5f97\u4f9d\u5979\u3002 \u3000\u3000\u51cc\u98ce\u6c90\u6d74\u4e00\u756a\uff0c\u6362\u4e86\u4e00\u8eab\u8863\u895f\uff0c\u89c9\u5f97\u8eab\u5fc3\u8f7b\u5feb\u591a\u4e86\uff0c\u4f46\u90a3\u53ea\u662f\u8f6c\u77ac\u95f4\u7684\u8f7b\u677e\uff0c\u5728\u4ed6 \u5fc3\u7075\u7684\u6df1\u5904\uff0c\u8d1f\u62c5\u662f\u591a\u4e48\u6c89\u91cd\u554a\uff01 \u3000\u3000\u8559\u82b7\u5f85\u4ed6\u6c90\u6d74\u51fa\u6765\uff0c\u5f15\u4ed6\u5230\u4e86\u5367\u5ba4\u9053\uff1a\u201c\u60a8\u5148\u7761\u4e00\u4f1a\u4f11\u606f\u4f11\u606f\uff0c\u7b49\u5403\u665a\u996d\uff0c\u6211\u518d\u6765 \u558a\u60a8\u3002\u201d \u3000\u3000\u5230\u4e86\u638c\u706f\u65f6\u5206\uff0c\u51cc\u98ce\u8ddf\u7740\u5a62\u5973\uff0c\u7a7f\u8fc7\u4e24\u9053\uff0c\u53ea\u89c1\u524d\u9762\u662f\u4e00\u5706\u95e8\uff0c\u90a3\u5a62\u5973\u9053\uff1a\u201c\u8fd9\u662f \u6211\u4eec\u5c0f\u59d0\u4f4f\u7684\u5730\u65b9\u3002\u201d \u3000\u3000\u51cc\u98ce\u8d70\u8fdb\u5706\u95e8\uff0c\u9635\u9635\u6e05\u9999\u6251\u9f3b\uff0c\u539f\u6765\u904d\u5730\u90fd\u662f\u8309\u8389\uff0c\u5047\u5c71\u540e\u662f\u55b7\u6c34\u6cc9\uff0c\u6708\u5149\u7167\u5728\u6c34 \u73e0\u4e0a\uff0c\u95ea\u95ea\u53d1\u5149\uff0c\u666f\u8272\u751a\u662f\u5b9c\u4eba\u3002 \u3000\u3000\u51cc\u98ce\u89c1\u8559\u82b7\u5750\u5728\u684c\u8fb9\u76f8\u4f8d\uff0c\u684c\u4e0a\u653e\u4e86\u51e0\u6837\u83dc\u770b\uff0c\u6025\u5fd9\u5750\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u6b49\u7136\u9053\uff1a\u201c\u8ba9\u4f60\u4e45\u7b49\u4e86\u3002\u201d \u3000\u3000\u8559\u82b7\u7b11\u9053\uff1a\u201c\u5434\u76f8\u516c\uff0c\u60a8\u793c\u8282\u771f\u91cd\uff0c\u6765\uff0c\u54b1\u4eec\u5148\u559d\u9152\u3002\u201d\u4ed6\u8bf4\u5230\u201c\u54b1\u4eec\u201d\u4e0d\u89c9\u6709\u4e9b \u7f9e\u6da9\u3002 \u3000\u3000\u51cc\u98ce\u4e5f\u6ca1\u6709\u6ce8\u610f\uff0c\u4e3e\u8d77\u9152\u6765\uff0c\u4e00\u996e\u800c\u5c3d\uff0c\u90a3\u9152\u751a\u662f\u6e05\u51bd\u3002\u8559\u82b7\u5374\u53ea\u7565\u4e00\u6cbe\u5507\u3002 \u3000\u3000\u5979\u6bb7\u6bb7\u76f8\u529d\uff0c\u51cc\u98ce\u5fc3\u5185\u6101\u7d6e\u91cd\u91cd\uff0c\u6b63\u60f3\u501f\u9152\u6d47\u6101\uff0c\u4e00\u676f\u676f\u53ea\u7ba1\u5f80\u4e0b\u5012\u3002 \u3000\u3000\u5979\u81ea\u5df1\u4e5f\u559d\u4e0b\u4e00\u676f\uff0c\u8138\u4e0a\u5fae\u6655\uff0c\u706f\u5149\u4e0b\uff0c\u53ea\u89c1\u5979\u96ea\u767d\u5ae9\u5f97\u51fa\u6c34\u7684\u53cc\u988a\uff0c\u900f\u51fa\u6d45\u6d45\u7684 \u7ea2\u8272\uff0c\u76f4\u5982\u5947\u82b1\u521d\u653e\uff0c\u6668\u9732\u521d\u51dd\u3002 \u3000\u3000\u5979\u7a81\u7136\u9053\uff1a\u201c\u90a3\u65e5\u6211\u89c1\u8f9b\u2014\u2014\u8f9b\u76f8\u516c\u558a\u60a8\u5927\u54e5\uff0c\u771f\u662f\u7fa1\u6155\u5f97\u5f88\uff0c\u6211\u2026\u2026\u6211\u60f3\uff0c\u6709\u4e00 \u5929\u6211\u4e5f\u80fd\u558a\u4f60\u5927\u54e5\uff0c\u90a3\u624d\u597d\u54e9\uff01\u201d \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u559d\u4e86\u4e00\u4e9b\u9152\uff0c\u795e\u6001\u5927\u662f\u6d3b\u6cfc\uff0c\u5b9e\u662f\u5a07\u61a8\u53ef\u7231\uff0c\u53ea\u6050\u62c2\u5979\u4e4b\u610f\uff0c\u4fbf\u9053\uff1a\u201c\u6211 \u4e5f\u5f88\u60f3\u6709\u4e00\u4e2a\u50cf\u4f60\u8fd9\u6837\u7684\u59b9\u5b50\u3002\u201d \u3000\u3000\u8559\u82b7\u559c\u9053\uff1a\u201c\u5927\u54e5\uff0c\u771f\u7684\u4e48\uff1f\u4f60\u4e5f\u522b\u518d\u53eb\u6211\u82cf\u59d1\u5a18\u957f\uff0c\u82cf\u59d1\u5a18\u77ed\u7684\u4e86\uff0c\u6211\u5988\u53eb\u6211\u5c0f \u60e0\uff0c\u4f60\u5c31\u8fd9\u6837\u53eb\u6211\u5427\uff01\u201d \u3000\u3000\u5979\u53c8\u63a5\u7740\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8d70\u4e86\u540e\uff0c\u6211\u771f\u60f3\u5ff5\u4f60\uff0c\u6211\u5929\u5929\u7b97\u7740\u65e5\u5b50\uff0c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a \u4f1a\u6765\u770b\u6211\u7684\uff0c\u4eca\u65e9\u513f\uff0c\u6211\u542c\u559c\u9e4a\u5728\u679d\u4e0a\u5471\u5471\u7684\u53eb\uff0c\u6211\u4fbf\u77e5\u5927\u54e5\u4f1a\u6765\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u5c0f\u8559\u59b9\u5b50\uff0c\u6211\u2026\u2026\u6211\u3002\u201d \u3000\u3000\u8559\u82b7\u63a5\u53e3\u9053\uff1a\u201c\u5927\u54e5\u4f60\u4e0d\u7528\u8bb2\uff0c\u6211\u77e5\u9053\u4f60\u4e5f\u5728\u60f3\u5ff5\u6211\u3002\u201d \u3000\u3000\u201c\u6211\u4e49\u7236\uff0c\u4ed6\u89c1\u6211\u6574\u5929\u4e0d\u4e50\uff0c\u4ee5\u4e3a\u6211\u751f\u75c5\u4e86\uff0c\u5927\u54e5\uff0c\u6211\u5fc3\u91cc\u62c5\u5fe7\uff0c\u996d\u4e5f\u5403\u4e0d\u4e0b\uff0c\u5927 \u54e5\uff0c\u4f60\u4e0d\u518d\u79bb\u5f00\u6211\u5427\uff01\u201d \u3000\u3000\u201c\u5927\u54e5\uff0c\u6211\u77e5\u9053\u4f60\u4e0d\u613f\u4f4f\u5728\u8fd9\u513f\uff0c\u4f60\u8981\u884c\u4fa0\u6c5f\u6e56\uff0c\u96be\u9053\u6211\u8fd8\u4f1a\u4e0d\u613f\u8ddf\u7740\u4f60\u5417\uff1f\u201d \u3000\u3000\u51cc\u98ce\u542c\u4ed6\u8bf4\u5f97\u4e00\u5f80\u60c5\u6df1\uff0c\u5fc3\u4e2d\u5f88\u662f\u611f\u52a8\u3002\u90a3\u8559\u82b7\u5750\u5f97\u79bb\u4ed6\u5f88\u8fd1\uff0c\u53ea\u89c9\u5979\u5410\u6c14\u5982\u5170\uff0c \u7f8e\u79c0\u7edd\u4f26\u3002 \u3000\u3000\u4ed6\u672c\u4e0d\u5584\u4e8e\u559d\u9152\uff0c\u6b64\u65f6\u501f\u9152\u6d88\u6101\uff0c\u9189\u610f\u5df2\u662f\u751a\u6df1\uff0c\u4ed6\u62ac\u5934\u4e00\u89c1\u8559\u82b7\u6b63\u671b\u7740\u4ed6\uff0c\u773c\u5149 \u4e2d\u5305\u542b\u7740\u5343\u601c\u4e07\u7231\u3002 \u3000\u3000\u51cc\u98ce\u89c9\u5f97\u90a3\u773c\u5149\u975e\u5e38\u719f\u6089\uff0c\u4ed6\u9152\u9189\u4e4b\u4e0b\uff0c\u5b9a\u529b\u5927\u4e3a\u51cf\u4f4e\uff0c\u51dd\u76ee\u770b\u4e86\u4e00\u9635\uff0c\u518d\u4e5f\u5fcd\u8010 \u4e0d\u4f4f\uff0c\u4f38\u624b\u6349\u4f4f\u60e0\u6b62\u5c0f\u624b\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u59b9\u5b50\uff0c\u4f60\u771f\u597d\u770b\u3002\u201d \u3000\u3000\u8559\u82b7\u6323\u4e86\u4e00\u4e0b\u6ca1\u6709\u6323\u8131\uff0c\u4fbf\u4efb\u4ed6\u63e1\u7740\uff0c\u4e00\u80a1\u70ed\u6d41\u4ece\u51cc\u98ce\u624b\u638c\uff0c\u4f20\u5230\u5979\u5168\u8eab\uff0c\u5979\u5fc3\u4e2d \u751c\u871c\u65e0\u6bd4\u3002 \u3000\u3000\u5979\u81ea\u5e7c\u4e27\u6bcd\uff0c\u7236\u4eb2\u5bf9\u5979\u867d\u7136\u65e0\u5fae\u4e0d\u81f3\uff0c\u53ef\u662f\u8fd1\u4e00\u5e74\u6765\uff0c\u6bcf\u5f53\u4e00\u4e2a\u4eba\uff0c\u5bf9\u7740\u6625\u82b1\u79cb\u6708 \u65f6\uff0c\u5728\u5fc3\u7075\u6df1\u5904\uff0c\u4f1a\u611f\u5230\u83ab\u540d\u7684\u7a7a\u865a\u3002\u6b64\u65f6\uff0c\u90a3\u7a7a\u865a\u88ab\u5145\u5b9e\u4e86\uff0c\u4e16\u754c\u7a81\u7136\u53d8\u5f97\u7f8e\u4e3d\u4e86\uff0c \u4e00\u5207\u90fd\u662f\u90a3\u4e48\u53ef\u7231\u5440\uff01 \u3000\u3000\u51cc\u98ce\u5583\u5583\u9053\uff1a\u201c\u59b9\u5b50\uff01\u201d \u3000\u3000\u8559\u82b7\u67d4\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4ec0\u4e48\u4e8b\uff1f\u201d \u3000\u3000\u51cc\u98ce\u65ad\u65ad\u7eed\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u60f3\u2026\u2026\u4eb2\u4eb2\u4f60\u7684\u773c\u775b\u2026\u2026\u201d \u3000\u3000\u8559\u82b7\u5927\u4e3a\u7f9e\u6025\uff0c\u4f46\u5979\u5929\u6027\u6781\u662f\u6e29\u67d4\uff0c\u773c\u89c1\u51cc\u98ce\u6ee1\u9762\u671f\u5f85\u4e4b\u8272\uff0c\u5979\u4e0d\u5fcd\u62d2\u7edd\uff0c\u4e5f\u4e0d\u60f3 \u62d2\u7edd\u3002\u5979\u95ed\u4e0a\u4e86\u773c\uff0c\u9886\u53d7\u8fd9\u521d\u543b\u7684\u6ecb\u5473\uff0c\u5728\u8fd9\u4e00\u77ac\u95f4\uff0c\u5979\u4e0d\u518d\u8981\u4e16\u4e0a\u4efb\u4f55\u4e1c\u897f\u2014\u2014\u4e00\u5207 \u90fd\u50cf\u767d\u4e91\u90a3\u6837\u98d8\u6e3a\uff0c\u90a3\u6837\u4e0d\u91cd\u8981\u4e86\u3002 \u3000\u3000\u5979\u89c9\u5f97\u51cc\u98ce\u53ea\u662f\u4e00\u6b21\u4e00\u6b21\u4eb2\u5979\u7684\u773c\u6674\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u4ed6\u786e\u662f\u81f3\u8bda\u541b\u5b50\uff0c\u4f46\u672a\u514d\u592a\u53e4 \u677f\u4e86\u4e9b\u3002\u201d \u3000\u3000\u5979\u7741\u5f00\u4e86\u773c\uff0c\u53ea\u89c1\u51cc\u98ce\u5982\u9189\u5982\u75f4\uff0c\u5fc3\u60f3\uff1a\u201c\u5927\u54e5\u53ea\u6015\u4e50\u660f\u4e86\u3002\u201d \u3000\u3000\u7a81\u7136\uff0c\u7a97\u5916\u4e00\u58f0\u51c4\u51c9\u7684\u53f9\u606f\u3002 \u3000\u3000\u51cc\u98ce\u6c89\u601d\u5728\u6614\u65e5\u7684\u60c5\u666f\u4e2d\uff0c\u662f\u4ee5\u4ee5\u4ed6\u8fd9\u4e48\u9ad8\u529f\u529b\uff0c\u7adf\u4f1a\u6ca1\u6709\u542c\u89c1\u3002\u8559\u82b7\u6c89\u9189\u5728\u6e29\u85af \u4e2d\uff0c\u53ea\u613f\u5b87\u5b99\u6c38\u8fdc\u505c\u7559\u5728\u6b64\u523b\uff0c\u4e16\u4e16\u4e0d\u53d8\uff0c\u54ea\u8fd8\u4f1a\u7559\u610f\u7a97\u5916\u7684\u53f9\u606f\u5462\uff1f \u3000\u3000\u4e16\u4e0a\u7684\u4e8b\uff0c\u5728\u67d0\u67d0\u4e2d\u4f3c\u65e9\u6709\u5b89\u6392\uff0c\u5982\u679c\u51cc\u98ce\u521a\u624d\u542c\u5230\u53f9\u606f\uff0c\u8d76\u5feb\u51fa\u53bb\uff0c\u4ed6\u8fd9\u4e00\u751f\u4fbf \u5b8c\u5168\u6539\u53d8\u4e86\u3002 \u3000\u3000\u5047\u77f3\u5c71\u540e\uff0c\u5750\u7740\u4e00\u4e2a\u7ea4\u5f31\u7684\u59d1\u5a18\uff0c\u5728\u4e0d\u505c\u5730\u62bd\u6ce3\u7740\uff0c\u65e0\u60c5\u7684\u98ce\u5439\u8fc7\u5979\u6302\u7740\u6cea\u73e0\u7684\u8138\uff0c \u5979\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u6218\u2014\u2014\u90a3\u662f\u4ece\u5fc3\u5e95\u900f\u51fa\u7684\u5bd2\u610f\u3002 \u3000\u3000\u5979\u62bd\u6ce3\u4e86\u4e00\u9635\uff0c\u5fc3\u4e2d\u6124\u6068\u6e10\u6d88\uff0c\u4e00\u79cd\u4ece\u672a\u6709\u7684\u81ea\u5351\u611f\u88ad\u4e0a\u4e86\u5fc3\u5934\u3002 \u3000\u3000\u201c\u4eba\u5bb6\u662f\u77e5\u5e9c\u5343\u91d1\uff0c\u6211\u53ea\u662f\u4e00\u4e2a\u2026\u2026\u4e00\u4e2a\u778e\u4e86\u773c\u7684\u4e61\u6751\u59d1\u5a18\uff0c\u600e\u80fd\u548c\u4eba\u5bb6\u6bd4\u554a\uff01\u201d \u5979\u5fc3\u60f3\uff1a\u201c\u5927\u54e5\uff0c\u6211\u4e0d\u6068\u4f60\uff0c\u6211\u4e5f\u4e0d\u602a\u4f60\u4e86\uff0c\u6211\u539f\u662f\u914d\u4e0d\u4e0a\u4f60\u5440\uff01\u5927\u54e5\uff0c\u4f60\u4e0d\u8981\u518d\u8bb0\u7740 \u6211\u8fd9\u4e2a\u50bb\u59d1\u5a18\u4e86\uff0c\u4f60\u548c\u82cf\u59d1\u5a18\u597d\u5427\uff01\u201d\u5979\u662f\u591a\u4e48\u7ea4\u5f31\u5440\uff01\u4e00\u751f\u751f\u957f\u5728\u8bda\u6734\u7684\u4e61\u4e0b\uff0c\u4ece\u672a \u53d7\u5230\u6b3a\u9a97\u9669\u6076\u7684\u6ecb\u5473\uff0c\u6b64\u65f6\u9661\u7136\u4e4b\u95f4\uff0c\u53d1\u89c9\u81ea\u5df1\u4e00\u5fc3\u76f8\u7231\uff0c\u8ba4\u4e3a\u91cf\u5b8c\u7f8e\u7684\u4eba\uff0c\u7adf\u7136\u9a97\u4e86 \u5979\uff0c\u79fb\u60c5\u522b\u604b\uff0c\u5fc3\u4e0b\u60b2\u82e6\uff0c\u771f\u5982\u6bd2\u86c7\u5728\u4e00\u70b9\u70b9\u5543\u5403\u5979\u7684\u5fc3\u623f\u3002 \u3000\u3000\u7231\u60c5\uff0c\u7ec8\u4e8e\u6218\u80dc\u4e86\u4e00\u5207\u5992\u6068\uff0c\u5979\u5fc3\u60f3\u9053\uff1a\u201c\u6211\u8fd8\u662f\u7231\u7740\u5927\u54e5\u7684\uff0c\u53ea\u8981\u5927\u54e5\u597d\uff0c\u6211\u8fd8 \u8981\u6c42\u4ec0\u4e48\u5462\uff1f\u5927\u54e5\u4e0e\u90a3\u82cf\u59d1\u5a18\uff0c\u539f\u662f\u4e00\u5bf9\u4f73\u5076\uff0c\u6211\u53c8\u4f55\u5fc5\u53c2\u5939\u5176\u4e2d\uff0c\u4f7f\u5927\u54e5\u4e3a\u96be\u5462\uff1f\u8d70 \u5427\uff01\u8d70\u5427\uff01\u628a\u8fd9\u8eab\u5b50\u5c31\u846c\u9001\u5728\u90a3\u832b\u832b\u7684\u4e16\u4e0a\u7b97\u4e86\u5427\uff01\u201d \u3000\u3000\u5979\u7ad9\u8d77\u6765\uff0c\u7f13\u6b65\u8d70\u4e86\uff0c\u6708\u5149\u7167\u7740\u5979\u7684\u5f71\u5b50\uff0c\u62d6\u5f97\u957f\u957f\u7684\u3002\u5979\u867d\u770b\u4e0d\u89c1\u81ea\u5df1\u7684\u5f71\u5b50\uff0c \u4f46\u5979\u5fc3\u60f3\uff1a\u201c\u4ece\u4eca\u4ee5\u540e\uff0c\u6211\u662f\u4e00\u4e2a\u5b64\u72ec\u7684\u4eba\u4e86\uff0c\u5f71\u5b50\uff0c\u5f71\u5b50\uff0c\u53ea\u6709\u4f60\u6765\u966a\u6211\u4e86\u3002\u201d \u3000\u3000\u5979\u6e10\u6e10\u8d70\u8fdc\u4e86\uff0c\u4e00\u4e2a\u9ad8\u8d35\u7684\u7075\u9b42\uff0c\u6d88\u5931\u5728\u65e0\u8fb9\u7684\u9ed1\u6697\u4e2d\u2026\u2026 \u3000\u3000\u6b21\u6668\uff0c\u5434\u51cc\u98ce\u5411\u82cf\u8559\u82b7\u544a\u8f9e\u3002 \u3000\u3000\u8559\u82b7\u77e5\u4ed6\u8981\u53bb\u62a5\u7236\u4ec7\uff0c\u4e5f\u4e0d\u6562\u62e6\u963b\uff0c\u51cc\u98ce\u6b63\u8981\u52a8\u8eab\uff0c\u5ffd\u7136\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u9053\uff1a\u201c\u82cf\u59d1 \u5a18\u5e72\u7239\u662f\u8fd9\u9c81\u897f\u516b\u53bf\u77e5\u5e9c\uff0c\u6211\u4f55\u4e0d\u6258\u4ed6\u6253\u542c\u6253\u542c\u963f\u5170\u6bcd\u5973\u7684\u4e0b\u843d\uff1f\u201d \u3000\u3000\u5f53\u4e0b\uff0c\u4ed6\u5411\u60e0\u84dd\u8bf4\u4e86\uff0c\u60e0\u84dd\u542c\u4ed6\u8bf4\u5230\u963f\u5170\uff0c\u6ee1\u8138\u6df1\u60c5\uff0c\u7231\u601c\uff0c\u5fc3\u4e2d\u5f88\u4e0d\u597d\u53d7\u3002 \u3000\u3000\u5979\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u4e00\u4e2a\u5ff5\u5934\u95ea\u8fc7\uff0c\u5979\u51e0\u6b21\u60f3\u5f00\u53e3\u8bf4\uff0c\u4f46\u662f\u81ea\u79c1\u7684\u5fc3\u7406\uff0c\u5374\u963b\u6b62\u4e86\u5979\u3002 \u3000\u3000\u4e16\u754c\u4e0a\u53ea\u6709\u5ac9\u5992\u81ea\u79c1\uff0c\u624d\u80fd\u4f7f\u4e00\u4e2a\u6e29\u67d4\u4ec1\u6148\u7684\u59d1\u5a18\uff0c\u7a81\u7136\u4e4b\u95f4\u53d8\u4f5c\u4e00\u4e2a\u6b8b\u5fcd\u7684\u5973\u5b69\u3002 \u3000\u3000\u8559\u82b7\u5fc3\u5185\u4ea4\u6218\uff0c\u5979\u5230\u5e95\u51fa\u8eab\u540d\u95e8\uff0c\u81ea\u5e7c\u53d7\u7236\u4eb2\u85b0\u6dd8\uff0c\u6b63\u4e49\u611f\u6781\u5f3a\uff0c\u5979\u806a\u660e\u7edd\u9876\uff0c\u6628 \u591c\u89c1\u51cc\u98ce\u540e\u6765\u795e\u8272\u7a81\u53d8\u6f20\u7136\uff0c\u4f3c\u6709\u65e0\u9650\u5fc3\u4e8b\uff0c\u5fc3\u4e0b\u5df2\u731c\u5230\u4e00\u4e24\u5206\uff0c\u6b64\u523b\u542c\u4ed6\u5982\u6b64\u4e00\u8bf4\uff0c \u66f4\u662f\u604d\u7136\u5927\u609f\uff0c\u5979\u660e\u77e5\u8fd9\u4e00\u8bf4\u51fa\uff0c\u81ea\u5df1\u4e00\u751f\u7684\u5e78\u798f\u4fbf\u6e9c\u8d70\u4e86\uff0c\u53ef\u662f\u7236\u4eb2\u8c06\u8c06\u7684\u6559\u8bf2\uff0c\u53c8 \u98de\u5230\u8033\u8fb9\uff0c\u8fd9\u4e00\u523b\uff0c\u4f7f\u5979\u771f\u6bd4\u5341\u5e74\u8fd8\u8981\u96be\u5ea6\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u77e5\u8f6c\u4e86\u51e0\u767e\u6b21\u5ff5\u5934\u3002 \u3000\u3000\u6700\u540e\uff0c\u5979\u51b3\u5b9a\u4e86\uff0c\u9ad8\u8d35\u7684\u60c5\u64cd\u6218\u80dc\u4e86\u3002 \u3000\u3000\u5979\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u90a3\u963f\u5170\u59d1\u5a18\uff0c\u53ef\u662f\u957f\u5f97\u975e\u5e38\u5c0f\u5de7\u6807\u81f4\u5417\uff1f\u201d \u3000\u3000\u51cc\u98ce\u89c1\u5979\u4e45\u4e45\u4e0d\u8a00\uff0c\u4f3c\u4e4e\u5728\u6c89\u601d\u4e00\u96be\u89e3\u7684\u95ee\u9898\uff0c\u6b64\u65f6\u7a81\u542c\u51fa \u3000\u3000\u8bed\u76f8\u95ee\uff0c\u53ea\u9053\u5979\u662f\u95ee\u660e\u963f\u5170\u7279\u5f81\uff0c\u597d\u66ff\u81ea\u5df1\u5bfb\u6211\uff0c\u4e0d\u7531\u597d\u751f\u611f\u6fc0\u9053\uff1a\u201c\u5c0f\u60e0\u59b9\u5b50\uff0c \u963f\u5170\u6b63\u662f\u50cf\u4f60\u8bb2\u7684\u90a3\u6a21\u6837\uff0c\u8bf7\u4f60\u7279\u522b\u7559\u5fc3\u4e00\u70b9\u5979\u53cc\u76ee\u662f\u778e\u7684\u3002\u201d \u3000\u3000\u8559\u82b7\u8f6c\u8eab\u5bf9\u5a62\u5973\u9053\uff1a\u201c\u4f60\u53bb\u53eb\u963f\u5170\u59d1\u5a18\u6765\u89c1\u5434\u76f8\u516c\u5427\uff1f\u201d \u3000\u3000\u5979\u6b64\u8a00\u4e00\u51fa\uff0c\u5927\u51fa\u51cc\u98ce\u610f\u6599\u4e4b\u5916\uff0c\u4ed6\u7b80\u76f4\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7684\u8033\u6735\uff0c\u5fd9\u95ee\u9053\uff1a\u201c\u59b9\u5b50\uff0c \u4f60\u2026\u2026\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5a62\u5973\u4f3c\u4e5f\u4e0d\u61c2\u8559\u82b7\u7684\u8bdd\uff0c\u7741\u5927\u773c\u775b\uff0c\u5446\u5446\u5730\u770b\u7740\u8559\u82b7\u3002 \u3000\u3000\u8559\u82b7\u9053\uff1a\u201c\u6211\u662f\u53eb\u4f60\u53bb\u628a\u5c0f\u5170\u8bf7\u6765\u3002\u201d \u3000\u3000\u90a3\u5a62\u5973\u604d\u7136\u5927\u609f\uff0c\u554a\u4e86\u4e00\u58f0\uff0c\u98de\u6b65\u8d76\u51fa\uff0c\u51cc\u98ce\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u8ddf\u4e86\u51fa\u53bb\u3002 \u3000\u3000\u8559\u82b7\u89c1\u51cc\u98ce\u795e\u8272\u6b22\u6109\uff0c\u5173\u6ce8\u4e4b\u60c5\u6ea2\u4e8e\u8a00\u8868\uff0c\u5fc3\u4e2d\u89c9\u5f97\u4e00\u9635\u7edd\u671b\uff0c\u63a9\u8138\u5954\u56de\u5367\u623f\u3002 \u3000\u3000\u201c\u5979\u662f\u2026\u2026\u4ec0\u4e48\u2026\u2026\u65f6\u5019\u2026\u2026\u65f6\u5019\u8d70\u7684\uff1f\u201d \u3000\u3000\u5c0f\u8299\u9053\uff1a\u201c\u6628\u5929\u665a\u4e0a\u3002\u201d \u3000\u3000\u51cc\u98ce\u95ee\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u7a81\u7136\u8981\u8d70\uff1f\u201d \u3000\u3000\u5c0f\u8299\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u5979\u4e34\u8d70\u65f6\u592e\u6211\u9012\u7ed9\u5434\u76f8\u516c\u4e00\u5c01\u4fe1\uff0c\u90a3\u7ba1\u5bb6\u56e0\u5979\u5e76\u975e\u4e2b\u73af\u4f7f \u5973\uff0c\u53ea\u662f\u8001\u5927\u7237\u51fa\u5de1\u65f6\u6551\u56de\u7684\u5b64\u5973\uff0c\u6240\u4ee5\u4e5f\u4e0d\u80fd\u963b\u6b62\uff0c\u5c31\u8ba9\u5979\u8d70\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u6025\u9053\uff1a\u201c\u4f60\u5feb\u628a\u90a3\u5c01\u4fe1\u62ff\u6765\u3002\u201d \u3000\u3000\u4ed6\u5f97\u77e5\u963f\u5170\u8fd8\u5728\u4eba\u95f4\uff0c\u5fc3\u4e2d\u60ca\u559c\u6b32\u72c2\uff0c\u4e5f\u4e0d\u6687\u7ec6\u60f3\u5979\u4e3a\u4ec0\u4e48\u8981\u79bb\u5f00\u81ea\u5df1\u2014\u2014\u4ed6\u5b8c\u5168 \u5fd8\u4e86\u6628\u65e5\u9152\u9189\u4e4b\u4e8b\u54e9\uff01\u4ed6\u63a5\u8fc7\u4fe1\uff0c\u6b63\u60f3\u62c6\u5f00\u6765\u770b\uff0c\u5ffd\u7136\u80cc\u540e\u4e00\u58f0\u6e29\u67d4\u58f0\u97f3\u9053\uff1a\u201c\u5927\u54e5\uff0c \u4f60\u53ef\u8981\u597d\u597d\u4fdd\u91cd\u3002\u201d \u3000\u3000\u51cc\u98ce\u8f7b\u8eab\u4e00\u770b\uff0c\u53ea\u89c1\u8559\u82b7\u6cea\u75d5\u6ee1\u9762\uff0c\u4e0d\u89c9\u751a\u611f\u6b49\u610f\uff0c\u4f46\u4ed6\u6025\u4e8e\u8ffd\u8d76\u963f\u5170\uff0c\u4e00\u65f6\u4e4b\u95f4 \u4e5f\u60f3\u4e0d\u51fa\u4ec0\u4e48\u6cd5\u5b50\u5b89\u6170\u5979\u3002 \u3000\u3000\u4ed6\u9053\uff1a\u201c\u59b9\u5b50\uff0c\u4f60\u5f85\u6211\u597d\uff0c\u6211\u5fc3\u91cc\u77e5\u9053\uff0c\u5f85\u6211\u8ffd\u5230\u963f\u5170\uff0c\u518d\u6765\u627e\u4f60\u3002\u201d \u3000\u3000\u82cf\u8559\u82b7\u51c4\u7136\u70b9\u70b9\u5934\u3002 \u3000\u3000\u51cc\u98ce\u5411\u5979\u4e00\u62db\u624b\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u5f84\u81ea\u98de\u6b65\u79bb\u53bb\u3002 \u3000\u3000\u5979\u7ad9\u5728\u95e8\u53e3\uff0c\u770b\u89c1\u51cc\u98ce\u7684\u5f71\u5b50\u6e10\u6e10\u6a21\u7cca\u4e86\uff0c\u5185\u5fc3\u4e00\u7247\u7a7a\u865a\u3002\u201c\u6211\u5df2\u6ee1\u8db3\u4e86\uff0c\u90a3\u6df1\u60c5 \u7684\u4e00\u543b\u2014\u2014\u867d\u7136\u4ed6\u5fc3\u4e2d\u5728\u60f3\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u53ef\u662f\uff0c\u6211\u5374\u5b8c\u5168\u6ee1\u8db3\u4e86\u3002\u201d \u3000\u3000\u201c\u5728\u65e5\u540e\u60a0\u957f\u7684\u65e5\u5b50\u91cc\uff0c\u6211\u4e5f\u4e0d\u518d\u5b64\u82e6\u4e86\uff0c\u90a3\u771f\u503c\u5f97\u6211\u56de\u5fc6\u4e00\u751f\u54e9\uff01\u6211\uff0c\u6211\u2026\u2026\u8981 \u7ee7\u7eed\u6d3b\u4e0b\u53bb\uff0c\u751f\u547d\u7684\u8def\u9014\uff0c\u539f\u6765\u5c31\u662f\u8fd9\u6837\u7684\u554a\uff01\u201d \u3000\u3000\u4e24\u884c\u6e05\u6cea\uff0c\u6162\u6162\u6d41\u5230\u988a\u8fb9\u3002 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c\u5341\u4e00\u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c\u5341\u4e00\u7ae0 \u3000\u3000\u6012\u6f6e\u6f8e\u6e43\uff0c\u2014\u2014 \u3000\u3000\u5927\u6222\u5c9b\u4e0a\uff0c\u671d\u9633\u66ff\u5c9b\u4e0a\u7684\u6811\u6728\u52a0\u4e86\u7c89\u84dd\u8272\u7684\u4e00\u5c42\u5916\u7f18\uff0c\u7c89\u7ea2\u8272\u7684\u5929\uff0c\u91d1\u9ec4\u8272\u7684\u6ce2\u6d9b\u2026\u2026 \u3000\u3000\u4e00\u822c\u5c0f\u8239\u6084\u6084\u5730\u9760\u4e86\u5cb8\uff0c\u867d\u8bf4\u8239\u7684\u5e95\u5df2\u89e6\u4e86\u6c99\uff0c\u4f46\u662f\u8ddd\u79bb\u5e72\u7684\u6c99\u6ee9\uff0c\u4ecd\u6709\u4e94\u4e08\u4e4b\u8fdc\u3002 \u3000\u3000\u8239\u4e0a\u4e24\u4e2a\u4eba\uff0c\u8239\u9996\u5750\u7740\u662f\u4e00\u4e2a\u76f8\u8c8c\u5f02\u51e1\u7684\u8001\u50e7\uff0c\u8239\u5c3e\u5750\u7684\u5374\u662f\u4e00\u4e2a\u5e74\u8f7b\u82f1\u4fca\u7684\u5c11\u5e74\u2014\u2014 \u4e0d\u8981\u8bf4\uff0c\u8fd9\u4e24\u4eba\u5c31\u662f\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u548c\u8f9b\u6377\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u5728\u594e\u5c71\u4e00\u6218\u632b\u8d25\u4e86\u5929\u5170\u9ad8\u624b\u91d1\u9c81\u5384\u4e4b\u540e\uff0c\u201c\u6885\u9999\u795e\u5251\u201d\u7684\u540d\u5934\u4f20\u904d\u4e86\u6b66\u6797\uff0c\u5904\u5904\u53ef \u95fb\u8d5e\u626c\u7684\u58f0\u97f3\uff0c\u7136\u800c\u8fd9\u4e9b\u8f9b\u6377\u4e00\u4e1d\u4e5f\u4e0d\u77e5\u9053\uff0c\u56e0\u4e3a\u4ed6\u632b\u8d25\u4e86\u91d1\u9c81\u5384\u4e4b\u540e\uff0c\u7acb\u523b\u5c31\u83ab\u660e\u5176\u5999\u5730 \u88ab\u5e73\u51e1\u4e0a\u4eba\u62d6\u7740\u8d70\u4e86\u2026\u2026\u73b0\u5728\uff0c\u4ed6\u4eec\u7adf\u5230\u4e86\u5927\u6222\u5c9b\u3002 \u3000\u3000\u8f9b\u6377\u95ee\u4e86\u51e0\u6b21\uff0c\u5e73\u51e1\u4e0a\u4eba\u603b\u662f\u795e\u79d8\u5730\u9053\uff1a\u201c\u53cd\u6b63\u4f60\u8ddf\u6211\u6765\u6709\u597d\u5904\u5c31\u662f\u4e86\u3002\u201d \u3000\u3000\u6216\u8005\uff0c\u53ea\u5f97\u610f\u5730\u4e00\u7b11\uff0c\u5e76\u4e0d\u56de\u7b54\u3002 \u3000\u3000\u8f9b\u6377\u5bf9\u8fd9\u4f4d\u5bf9\u81ea\u5df1\u66fe\u6709\u6388\u827a\u4e4b\u5fb7\u7684\u5947\u4eba\uff0c\u7740\u5b9e\u94a6\u656c\uff0c\u5fc3\u4e2d\u867d\u7136\u6025\u7740\u8fd8\u6709\u8bb8\u591a\u4e8b\u8981\u529e\uff0c\u4f46 \u662f\u4e5f\u4e0d\u597d\u8bf4\u51fa\uff0c\u53ea\u597d\u8ddf\u7740\u5e73\u51e1\u4e0a\u4eba\u8dd1\u3002 \u3000\u3000\u7b49\u5230\u8239\u51fa\u4e86\u6d77\uff0c\u4ed6\u77e5\u9053\u6025\u4e5f\u6ca1\u6709\u7528\uff0c\u7d22\u6027\u5fc3\u4e00\u6a2a\uff0c\u6682\u65f6\u4e0d\u53bb\u60f3\u90a3\u4e9b\u4e8b\u60c5\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e5f\u4e0d\u627e\u4ed6\u8bf4\u8bdd\uff0c\u53ea\u795e\u79d8\u5730\u5fae\u7b11\u7740\u5750\u5728\u8239\u9996\uff0c\u8f9b\u6377\u4e0d\u7981\u751a\u89c9\u65e0\u804a\uff0c\u5446\u5750\u5728\u8239\u9996\uff0c \u90a3\u6597\u91d1\u9c81\u5384\u7684\u4e00\u62db\u4e00\u5f0f\u53c8\u6d6e\u4e0a\u4ed6\u7684\u5fc3\u5934\u3002 \u3000\u3000\u4ed6\u60f3\u9053\uff1a\u201c\u90a3\u91d1\u9c81\u5384\u7684\u5e74\u7eaa\u6bd4\u6211\u5927\u4e0d\u4e86\u591a\u5c11\uff0c\u770b\u6765\u9876\u591a\u4e09\u5341\u591a\uff0c\u4f46\u662f\u5185\u529f\u5374\u6df1\u5f97\u7d27\uff0c\u6211 \u81ea\u88ab\u5e73\u51e1\u4e0a\u4eba\u8f93\u5927\u5185\u529b\u4e4b\u540e\uff0c\u6bcf\u7ecf\u4e00\u573a\u6076\u6597\uff0c\u529f\u529b\u53c8\u89c9\u589e\u8fdb\u4e0d\u5c11\uff0c\u7adf\u7136\u4ecd\u4e0d\u662f\u90a3\u65af\u7684\u654c\u624b\uff0c \u8981\u4e0d\u662f\u5e73\u51e1\u4e0a\u4eba\u4e34\u654c\u4f20\u6388\u7684\u90a3\u4e00\u62db\u2014\u2014\u55ef\uff0c\u90a3\u62db\u771f\u5999\u7edd\uff0c\u5939\u5728\u7cbe\u5947\u7edd\u4f26\u7684\u2018\u5927\u884d\u5341\u5f0f\u2019\u4e2d\u771f \u662f\u5999\u6781\uff0c\u6050\u6015\u91d1\u9c81\u5384\u529f\u529b\u518d\u6df1\u4e00\u70b9\u4e5f\u8981\u7740\u6211\u7684\u9053\u513f\u554a\uff0c\u6211\u4f55\u4e0d\u5982\u6b64\u2014\u2014\u201d \u3000\u3000\u539f\u6765\u4ed6\u7a81\u7136\u60f3\u5230\u90a3\u8d62\u5f97\u91d1\u9c81\u5384\u7684\u4e00\u62db\u539f\u662f\u56e0\u91d1\u9c81\u5384\u4e0b\u76d8\u8f83\u5dee\uff0c\u6240\u4ee5\u624d\u4f6f\u653b\u4e0b\u76d8\u5b9e\u88ad\u4e0a \u8eab\uff0c\u4f46\u82e5\u5bf9\u4ed8\u522b\u7684\u4e0b\u76d8\u529f\u592b\u6781\u4f73\u7684\u4eba\u5c82\u4e0d\u5b8c\u5168\u5931\u6548\uff1f\u4f46\u662f\u4ed6\u7acb\u523b\u53c8\u60f3\u5230\u81ea\u5df1\u4f55\u4e0d\u5c06\u62db\u5f0f\u7565\u52a0 \u53d8\u5316\uff0c\u4e0d\u4e00\u5b9a\u9650\u5b9a\u8981\u5148\u653b\u4e0b\u76d8\uff0c\u90a3\u4e48\u5c82\u4e0d\u53ef\u4ee5\u56e0\u4eba\u800c\u53d8\uff0c\u66f4\u589e\u5a01\u529b\u5417\uff1f \u3000\u3000\u60f3\u5230\u8fd9\u91cc\u4e0d\u7981\u5fc3\u5934\u5927\u559c\uff0c\u8131\u53e3\u53eb\u9053\uff1a\u201c\u5999\u6781\u4e86\uff0c\u5999\u6781\u4e86\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136\u63a5\u53e3\u9053\uff1a\u201c\u7b49\u4f1a\u513f\u8fd8\u6709\u66f4\u5999\u7684\u54e9\uff01\u201d \u3000\u3000\u8f9b\u6377\u62ac\u5934\u770b\u65f6\uff0c\u53ea\u89c1\u5e73\u51e1\u4e0a\u4eba\u7b11\u541f\u541f\u5730\u671b\u7740\u4ed6\uff0c\u8138\u4e0a\u5145\u6ee1\u7740\u5f97\u610f\u7684\u6a21\u6837\uff0c\u4e0d\u89c9\u4e00\u6014\u2014\u2014 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u5fc5\u662f\u5728\u60f3\u6211\u8001\u4eba\u5bb6\u4f20\u4f60\u7684\u90a3\u51e0\u624b\u5427\uff0c\u54c8\u54c8\uff0c\u5999\u7684\u8fd8\u5728\u540e\u9762 \u54e9\u2014\u2014\u201d \u3000\u3000\u5fd9\u7136\u8239\u8eab\u4e00\u9707\uff0c\u4fbf\u4e0d\u518d\u524d\u8fdb\uff0c\u539f\u6765\u8239\u5e95\u5df2\u89e6\u4e86\u6d77\u5e95\u7684\u6c99\u77f3\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53eb\u9053\uff1a\u201c\u5230\u5566\uff01\u5a03\u513f\u4e0a\u5cb8\u554a\uff01\u201d \u3000\u3000\u8bf4\u7740\u8eab\u5b50\u4e00\u6643\uff0c\u7adf\u5982\u4e00\u53ea\u5927\u9e1f\u822c\u98de\u4e0a\u4e86\u4e94\u4e08\u4e4b\u5916\u7684\u5e72\u71e5\u6c99\u5730\u4e0a\uff0c\u5947\u7684\u662f\u90a3\u6c34\u4e2d\u7684\u8239\uff0c\u7adf \u4e1d\u6beb\u6ca1\u6709\u5012\u9000\uff01 \u3000\u3000\u8f9b\u6377\u9a87\u7136\u6697\u9053\uff1a \u3000\u3000\u201c\u4e00\u8dc3\u4e94\u516d\u4e08\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u662f\u8981\u8fd9\u8239\u513f\u4e00\u70b9\u90fd\u4e0d\u540e\u9000\uff0c\u8fd9\u7b49\u8f7b\u529f\u771f\u4ee4\u4eba\u96be\u4fe1\u7684\u4e86\uff0c\u6211\u2014 \u2014\u6211\u53ef\u4e0d\u6210\u2014\u2014\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u811a\u5e95\u7528\u529b\uff0c\u5782\u76f4\u5730\u5347\u8d77\u4e24\u4e09\u4e08\u9ad8\u2014\u2014\u5f53\u7136\u8239\u662f\u4e0d\u4f1a\u540e\u9000\u2014\u2014\u7136\u540e\u8eab\u4f53\u524d\u6298\uff0c\u7adf\u5728 \u7a7a\u4e2d\u5982\u7bad\u4e00\u822c\u659c\u5c04\u4e0b\u6765\uff0c\u843d\u5730\u4e4b\u5904\uff0c\u4e5f\u5230\u4e86\u5e72\u71e5\u7684\u6c99\u6ee9\u4e0a\u3002 \u3000\u3000\u8fd9\u624b\u529f\u592b\u867d\u4e0d\u53ca\u5e73\u51e1\u4e0a\u4eba\u7684\u7f8e\u5999\uff0c\u4f46\u4e5f\u6781\u4e86\u4e0d\u8d77\uff0c\u5e73\u51e1\u4e0a\u4eba\u5475\u5475\u7b11\u9053\uff1a\u201c\u5a03\u513f\uff0c\u771f\u6709\u4f60 \u7684\uff0c\u6709\u8bdd\u540c\u4f60\u8bb2\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u6014\u6014\u5730\u8ddf\u4ed6\u8d70\u53bb\uff0c\u8f6c\u4e86\u4e00\u4e24\u4e2a\u5f2f\uff0c\u6811\u6797\u4e2d\u7adf\u51fa\u73b0\u4e00\u6240\u6728\u5c4b\u6765\u3002 \u3000\u3000\u90a3\u6728\u5c4b\u5916\u8868\u4e1c\u4e00\u5757\u6728\u677f\uff0c\u897f\u4e00\u6761\u7af9\u5b50\uff0c\u975e\u5e38\u4e0d\u96c5\uff0c\u663e\u7136\u662f\u5e73\u51e1\u4e0a\u4eba\u81ea\u5df1\u9489\u7684\u3002\u8f9b\u6377\u8ddf\u7740 \u4ed6\u8d70\u8fd1\uff0c\u5e73\u51e1\u4e0a\u4eba\u4e00\u628a\u63a8\u5f00\u95e8\u800c\u8fdb\u3002 \u3000\u3000\u8fd9\u6728\u5c4b\u5916\u8868\u867d\u662f\u4e0d\u4f73\uff0c\u91cc\u9762\u5374\u8fd8\u633a\u8212\u670d\u7684\uff0c\u5149\u7ebf\u5145\u8db3\uff0c\u5730\u4e0a\u8fd8\u94fa\u4e86\u4e00\u5c42\u67d4\u8f6f\u7684\u5730\u6bef\uff0c\u8f9b \u6377\u4e0d\u7531\u8f7b\u5475\u4e86\u4e00\u58f0\uff0c\u8fd9\u5176\u4e2d\u542b\u6709\u4e00\u4e1d\u60ca\u53f9\u7684\u610f\u601d\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u62ff\u8d77\u4e00\u53ea\u5947\u5f62\u602a\u72b6\u7684\u6728\u6905\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e5f\u662f\u6211\u81ea\u5df1\u505a\u7684\uff0c\u600e\u6837\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u5f88\u597d\u5f88\u597d\uff01\u53ea\u662f\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u76b1\u7709\u9053\uff1a\u201c\u53ea\u662f\u600e\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u53ea\u662f\u592a\u810f\u4e86\u4e00\u70b9\u513f\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5475\u5475\u5927\u7b11\uff0c\u987a\u624b\u628a\u6905\u5b50\u653e\u4e0b\uff0c\u6905\u9762\u4e0a\u679c\u7136\u7070\u5c18\u5bc6\u5e03\uff0c\u88ab\u4ed6\u4e00\u6293\uff0c\u5df1\u7559\u4e0b\u51e0\u4e2a\u6307 \u75d5\u3002 \u3000\u3000\u8f9b\u6377\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u8001\u524d\u8f88\u5524\u665a\u8f88\u6765\u7a76\u7adf\u662f\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6253\u65ad\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u614c\uff0c\u6211\u5f85\u4f1a\u513f\u518d\u544a\u8bc9\u4f60\u2014\u2014\u55ef\uff0c\u5a03\u513f\uff0c\u4f60\u8bf4\u4e16\u754c\u4e0a\u6700\u96be\u5e94\u4ed8 \u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u5947\u9053\uff1a\u201c\u4e0d\u77e5\u524d\u8f88\u662f\u6307\u54ea\u4e00\u65b9\u9762\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u662f\u95ee\u54ea\u4e00\u79cd\u4eba\u6700\u662f\u96be\u4e8e\u5e94\u4ed8\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6014\u4e86\u6014\uff0c\u6447\u4e86\u6447\u5934\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6b63\u7ecf\u9053\uff1a\u201c\u5a03\u513f\uff0c\u6211\u544a\u8bc9\u4f60\uff0c\u5929\u4e0b\u6700\u96be\u5e94\u4ed8\u7684\u5c31\u662f\u5973\u4eba\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u54a6\u4e86\u4e00\u58f0\uff0c\u4ed6\u9669\u4e9b\u5fcd\u4e0d\u4f4f\u5657\u55e4\u7b11\u4e86\u51fa\u6765\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u54ea\u77e5\u5e73\u51e1\u4e0a\u4eba\u7b11\u4e86\u4e00\u7b11\uff0c\u53c8\u4e0d\u7b54\u8bdd\u4e86\u3002 \u3000\u3000\u4efb\u8f9b\u6377\u806a\u660e\u7edd\u9876\u4e5f\u88ab\u5f04\u5f97\u83ab\u660e\u5176\u5999\uff0c\u4e0d\u7981\u5446\u5446\u6014\u5728\u4e00\u65c1\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136\u7b11\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u8bf4\u6211\u8001\u4eba\u5bb6\u7684\u62f3\u811a\u529f\u592b\u5982\u4f55\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u4e0a\u4eba\u7684\u62f3\u5251\u90fd\u662f\u76d6\u7edd\u5929\u4e0b\u7684\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u662f\u4e48\uff1f\u55ef\uff01\u4f60\u4e14\u770b\u8fd9\u4e00\u62db\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u53ea\u89c1\u4ed6\u5927\u8896\u4e00\u6325\uff0c\u53cc\u638c\u8fde\u98de\uff0c\u638c\u8896\u4e4b\u95f4\u7adf\u751f\u4e4c\u4e4c\u5f02\u54cd\uff0c\u5c24\u5176\u5947\u7684\u662f\u8f9b\u6377\u7ad9\u5728\u534a\u6b65\u4e4b \u5916\u7adf\u4e5f\u4e1d\u6beb\u4e0d\u89c9\u98ce\u52bf\uff0c\u8fd9\u7b49\u5185\u52b2\u542b\u8574\u7684\u81f3\u9ad8\u529f\u592b\uff0c\u771f\u5df2\u5230\u4e86\u7089\u706b\u7eaf\u9752\u7684\u5730\u6b65\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u770b\u4ed6\u90a3\u53cc\u638c\u4e4b\u95f4\u5999\u7528\u65e0\u7a77\uff0c\u4e0d\u7981\u5728\u4e00\u65c1\u6f5c\u5fc3\u601d\u7d22\uff0c\u6b64\u65f6\u4ed6\u529f\u529b\u5df2\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u60f3\u4e86 \u534a\u523b\u7adf\u81ea\u9886\u609f\uff0c\u4e0d\u7531\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u554a\uff0c\u6211\u61c2\u4e86\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u4e00\u8dc3\u51fa\u95e8\uff0c\u53eb\u9053\uff1a\u201c\u5a03\u513f\uff0c\u51fa\u6765\u6211\u6559\u4f60\u8fd9\u5957\u638c\u6cd5\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u5174\u5927\u559c\uff0c\u4e00\u8dc3\u800c\u51fa\uff0c\u8fd8\u6765\u4e0d\u53ca\u79f0\u8c22\uff0c\u5e73\u51e1\u4e0a\u4eba\u5df2\u5f00\u59cb \u3000\u3000\u89e3\u91ca\u4ed6\u90a3\u5957\u638c\u6cd5\u7684\u8bc0\u8981\u62db\u5f0f\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u8fd9\u4e00\u542c\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u9635\u5fc3\u82b1\u6012\u653e\uff0c\u90a3\u5e73\u51e1\u4e0a\u4eba\u6240\u8bf4\u7684\u638c\u6cd5\u771f\u662f\u524d\u6240\u672a\u95fb\uff0c\u90a3\u638c\u6307\u4e4b \u95f4\uff0c\u786e\u5b9e\u5999\u4eba\u6beb\u5398\u3002\u4ee5\u8f9b\u6377\u7684\u529f\u529b\u667a\u6167\uff0c\u8db3\u8db3\u7ec3\u4e86\u4e00\u6574\u5929\u624d\u4f1a\u5341\u62db\u3002 \u3000\u3000\u8f9b\u6377\u55dc\u6b66\u82e5\u72c2\uff0c\u867d\u7136\u5fc3\u4e2d\u751a\u591a\u7591\u95ee\uff0c\u4f46\u6b64\u65f6\u5fc3\u4e2d\u5168\u662f\u5145\u6ee1\u7740\u90a3\u4e9b\u7edd\u5999\u62db\u5f0f\uff0c\u7ec8\u65e5\u5e9f\u5bd0\u5fd8 \u98df\u5730\u601d\u7d22\uff0c\u7ec3\u4e60\uff0c\u5176\u4ed6\u6839\u672c\u60f3\u4e0d\u5230\u3002 \u3000\u3000\u76f4\u5230\u7b2c\u4e94\u5929\uff0c\u8f9b\u6377\u5df2\u5b66\u4f1a\u4e86\u516d\u5341\u62db\uff0c\u4ed6\u5ffd\u7136\u60f3\u5230\uff1a\u201c\u5e73\u51e1\u4e0a\u4eba\u5524\u6211\u6765\u96be\u9053\u53ea\u662f\u8981\u6559\u6211\u8fd9 \u5957\u638c\u6cd5\uff1f\u4ed6\u4e00\u8def\u4e0a\u795e\u6001\u751a\u662f\u795e\u79d8\uff0c\u7a76\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f\u554a\uff0c\u6211\u8fd8\u6709\u8bb8\u591a\u4e8b\u8981\u8d76\u7740\u529e\uff0c\u600e\u4e48\u5c3d\u5728\u8fd9 \u513f\u8017\u4e0b\u4e86\u2014\u2014\u201d \u3000\u3000\u4ed6\u5fc3\u60f3\u5e73\u51e1\u4e0a\u4eba\u5bf9\u81ea\u5df1\u8fd9\u4e48\u597d\uff0c\u81ea\u5df1\u82e5\u662f\u5bf9\u4ed6\u8bf4\u660e\u539f\u59d4\uff0c\u5fc5\u7136\u51c6\u4ed6\u8d76\u56de\u4e2d\u539f\uff0c\u4f46\u662f\u4ed6\u60f3 \u5230\u90a3\u4e9b\u5947\u5999\u7684\u638c\u6cd5\uff0c\u4ed6\u5fc3\u9053\uff1a\u201c\u8fd9\u638c\u6cd5\u5b9e\u5728\u592a\u5999\uff0c\u82e5\u662f\u653e\u5f03\u4e86\u673a\u4f1a\uff0c\u5c82\u4e0d\u662f\u53ef\u60dc\uff1f\u201d \u3000\u3000\u8fd9\u65f6\u80cc\u540e\u4e00\u4e2a\u54c8\u54c8\u6717\u7b11\u9053\uff1a\u201c\u5a03\u513f\uff0c\u6562\u60c5\u4ec0\u4e48\u5730\u65b9\u7ec3\u4e0d\u5bf9\u52b2\u4e86\uff1f\u8fd9\u4e5f\u96be\u602a\u4f60\uff0c\u8fd9\u5957\u638c\u6cd5 \u5524\u505a\u2018\u7a7a\u7a7a\u638c\u6cd5\u2019\uff0c\u662f\u6211\u8001\u4eba\u5bb6\u6700\u8fd1\u624d\u60f3\u51fa\u6765\u7684\uff0c\u5f53\u4eca\u5929\u4e0b\u53ea\u6015\u6ca1\u6709\u4e00\u5957\u638c\u6cd5\u53ef\u4ee5\u6321\u4f4f\u6211\u4e03 \u5341\u4e8c\u62db\u2014\u2014\u55ef\uff0c\u4f60\u77a7\u6211\u7cca\u6d82\u5566\uff0c\u4ee5\u6211\u7684\u529f\u529b\u522b\u4eba\u81ea\u7136\u63a5\u4e0d\u4f4f\u4e03\u5341\u4e8c\u62db\u5566\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u4ee5\u6211\u7684\u529f\u529b\u5462\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u4f60\u7ec3\u6210\u4e86\u81ea\u7136\u5c31\u77e5\u9053\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u8138\u4e0a\u4e00\u6d3e\u5f97\u610f\u4e4b\u8272\uff0c\u5f53\u4e0b\u5fc3\u4e00\u6a2a\uff0c\u6697\u9053\uff1a\u201c\u7ba1\u4ed6\u7684\uff0c\u5b66\u5b8c\u8fd9\u4e03\u5341\u4e8c\u62db\u540e\u518d\u53e6\u4f5c \u6253\u7b97\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u5a03\u513f\uff0c\u8fd9\u5957\u638c\u6cd5\u5bb3\u6211\u8001\u4eba\u5bb6\u8db3\u8db3\u4e00\u4e2a\u6708\u6ca1\u6709\u7761\u89c9\u624d\u60f3\u51fa\u6765\uff0c\u4f60\u5b66\u4e86\u8be5 \u600e\u6837\u8c22\u6211\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u5fc3\u4e2d\u5145\u6ee1\u611f\u8c22\u4e4b\u60c5\uff0c\u4e49\u6b63\u4e25\u8f9e\u9053\uff1a\u201c\u4e0a\u4eba\u6709\u4ec0\u4e48\u8981\u5dee\u9063\u665a\u8f88\u7684\uff0c\u665a\u8f88\u65e0\u6240\u4e0d \u4ece\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u80af\u7b54\u5e94\u6211\u4e00\u4e8b\uff1f\u6211\u5148\u58f0\u660e\u8fd9\u4e8b\u751a\u662f\u4e0d\u6613\u3002\u201d \u3000\u3000\u8f9b\u6377\u539f\u672c\u751a\u662f\u51b2\u52a8\uff0c\u6beb\u4e0d\u8003\u8651\uff0c\u6717\u58f0\u9053\uff1a\u201c\u83ab\u8bf4\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u5341\u4ef6\u4e5f\u4e0d\u8981\u7d27\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u597d\uff01\u4f60\u5148\u7ec3\u5427\uff0c\u4ee5\u540e\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u90a3\u7a7a\u7a7a\u638c\u6cd5\u867d\u7136\u662f\u4e03\u5341\u4e8c\u62db\uff0c\u5176\u5b9e\u4e2d\u95f4\u7684\u53d8\u5316\u4f55\u6b62\u4e07\u5343\uff0c\u771f\u4e0d\u6127\u4e3a\u5e73\u51e1\u4e0a\u4eba\u7cbe\u5fc3\u6770\u4f5c\uff0c \u8f9b\u6377\u5341\u65e5\u5185\u52c9\u5f3a\u628a\u62db\u5f0f\u8bb0\u4f4f\uff0c\u4f46\u5176\u4e2d\u8bb8\u591a\u7cbe\u5999\u4e4b\u5904\u4ecd\u65e0\u6cd5\u9886\u4f1a\u3002 \u3000\u3000\u53c8\u7ec3\u4e86\u4e94\u65e5\uff0c\u8f9b\u6377\u4e0d\u77e5\u4e0d\u89c9\u5728\u5927\u6222\u5c9b\u4e0a\u5df1\u5f85\u4e86\u534a\u6708\uff0c\u800c\u53c8\u4e00\u5957\u7edd\u4e16\u6b66\u529f\u4ece\u5e73\u51e1\u4e0a\u4eba\u79fb\u5230 \u4e86\u8f9b\u6377\u7684\u8eab\u4e0a\u3002 \u3000\u3000\u8fd9\u5929\uff0c\u665a\u996d\u540e\uff0c\u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136\u53c8\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u8bf4\u4e16\u4e0a\u6700\u96be\u60f9\u7684\u662f\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e00\u6014\uff0c\u6697\u9053\uff1a\u201c\u600e\u4e48\uff0c\u8fd9\u8bdd\u53c8\u6765\u4e86\uff1f\u201d \u3000\u3000\u4f46\u95ea\u773c\u770b\u90a3\u5e73\u51e1\u4e0a\u4eba\uff0c\u4e00\u8138\u6b63\u7ecf\u4e4b\u8272\uff0c\u5f53\u4e0b\u7b11\u9053\uff1a\u201c\u6211\u77e5\u9053\u662f\u5973\u4eba\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e00\u62cd\u817f\u9053\uff1a\u201c\u662f\u554a\uff01\u5973\u4eba\u662f\u6700\u96be\u60f9\u7684\uff0c\u548c\u5973\u4eba\u6253\u4ea4\u9053\u662f\u975e\u5403\u4e8f\u4e0d\u53ef\u7684\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u5927\u5947\uff0c\u6697\u9053\uff1a\u201c\u96be\u9053\u5e73\u51e1\u4e0a\u4eba\u548c\u4ec0\u4e48\u5973\u4eba\u6253\u4ea4\u9053\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u5c0f\u6222\u5c9b\u4e0a\u90a3\u5c3c\u5a46\u4f60\u662f\u89c1\u8fc7\u7684\u4e86\uff0c\u8fd9\u5c3c\u59d1\u66f4\u662f\u5973\u4eba\u4e2d\u6700\u96be\u60f9\u7684\uff0c\u6211\u8001\u4eba \u5bb6\u548c\u5979\u8d4c\u6597\u4ece\u6765\u6ca1\u6709\u5f97\u8fc7\u4fbf\u5b9c\uff0c\u4e0a\u6b21\u88ab\u5979\u90a3\u9b3c\u95e8\u9635\u513f\uff0c\u5c06\u6211\u56f0\u4e86\u6574\u6574\u5341\u5e74\uff0c\u5e78\u597d\u5929\u9053\u8fd8\u5728\uff0c \u6ca1\u6709\u8ba9\u6211\u8001\u4eba\u5bb6\u4e00\u4e16\u82f1\u540d\u4e5f\u8d54\u4e0a\u53bb\uff0c\u53ef\u662f\u6211\u8fd9\u4e8f\u4e5f\u5403\u5f97\u591f\u5927\u5566\uff0c\u4ece\u90a3\u6b21\u8d77\uff0c\u6211\u7acb\u4e86\u4e00\u4e2a\u91cd \u8a93\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u6108\u542c\u6108\u5947\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u91cd\u8a93\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6b63\u8272\u9053\uff1a\u201c\u6211\u53d1\u8a93\u4eca\u751f\u6c38\u4e0d\u518d\u548c\u5973\u4eba\u52a8\u624b\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u90a3\u53ef\u4e0d\u5999\u5566\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u82e5\u662f\u90a3\u6167\u5927\u5e08\u518d\u6765\u5bfb\u4f60\u8001\u4eba\u5bb6\u6597\u6c14\uff0c\u4f60\u8001\u5c82\u4e0d\u8981\u5403\u5927\u4e8f\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53eb\u9053\uff1a\u201c\u5012\u7ed9\u4f60\u8fd9\u5a03\u513f\u8bf4\u5bf9\u5566\uff0c\u524d\u4e9b\u65e5\u5b50\u90a3\u5c3c\u5a46\u679c\u771f\u7528\u98de\u9e3d\u4f20\u4e66\u5411\u6211\u6311\u6218\uff0c\u8bf4 \u662f\u5979\u6700\u8fd1\u53d1\u660e\u4e86\u4e00\u5957\u638c\u6cd5\uff0c\u5982\u4f55\u5982\u4f55\u4e86\u4e0d\u8d77\uff0c\u8981\u548c\u6211\u6bd4\u5212\u6bd4\u5212\uff0c\u6211\u56de\u4fe1\u544a\u8bc9\u5979\u6211\u4e0d\u5e94\u6218\uff0c\u7ed3 \u679c\u8fd9\u5c3c\u5a46\u53ef\u6076\uff0c\u7adf\u626c\u8a00\u6211\u8001\u4eba\u5bb6\u4e0d\u6562\u548c\u5979\u6bd4\u5212\uff0c\u6211\u8001\u4eba\u5bb6\u8d8a\u60f3\u8d8a\u662f\u6c14\u6124\uff0c\u6240\u4ee5\u5c31\u627e\u5230\u4f60\u5566\u2014 \u2014\u201d \u3000\u3000\u8f9b\u6377\u7b54\u9053\uff1a\u201c\u627e\u5230\u6211\u5566\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5f97\u610f\u5730\u7b11\u9053\uff1a\u201c\u662f\u5566\uff0c\u6211\u8bf4\u8981\u4f60\u66ff\u6211\u5e72\u4e00\u6869\u4e8b\uff0c\u6b63\u662f\u8981\u4f60\u7528\u6211\u4f20\u4f60\u7684\u638c\u6cd5\uff0c\u53bb \u4ee3\u6211\u548c\u8001\u5c3c\u5a46\u6bd4\u5212\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u6025\u9053\uff1a\u201c\u90a3\u4e0d\u6210\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u522b\u6015\uff0c\u522b\u6015\uff0c\u6211\u90a3\u5957\u638c\u6cd5\u4e43\u662f\u4e13\u95e8\u5bf9\u4ed8\u8001\u5c3c\u5a46\uff0c\u4f60\u7edd\u5403\u4e0d\u4e86\u4e8f\u3002\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u63d2\u53e3\u9053\uff1a\u201c\u554a\uff01\u4f60\u53ef\u662f\u6015\u8001\u5c3c\u5a46\u529f\u529b\u6df1\u539a\uff1f\u4f60\u60f3\u4f60\u82e5\u7528\u8fd9\u5957\u638c\u6cd5\u548c\u5979\u5468\u65cb\u4e24 \u767e\u62db\u4ee5\u4e0a\uff0c\u8001\u5c3c\u5a46\u8fd8\u80fd\u8d56\u7740\u8001\u8138\u4e0d\u670d\u6986\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5206\u8fa9\u9053\uff1a\u201c\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e0d\u60a6\u9053\uff1a\u201c\u600e\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u90a3\u6167\u5927\u5e08\u66fe\u4f20\u665a\u8f88\u2018\u6d01\u6469\u795e\u6b65\u2019\uff0c\u665a\u8f88\u5c82\u80fd\u548c\u5979\u52a8\u624b\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u6211\u4ee5\u4e3a\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u539f\u6765\u662f\u8fd9\u4e2a\uff0c\u8fd9\u6709\u4ec0\u4e48\u8981\u7d27\uff0c\u4f60\u53c8\u4e0d\u662f\u548c \u5979\u771f\u6b63\u6027\u547d\u76f8\u640f\uff1f\u800c\u4e14\u4f60\u66fe\u7b54\u5e94\u6211\u7684\uff0c\u4f60\u6562\u53cd\u6094\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6697\u4e2d\u53eb\u82e6\uff0c\u5374\u4e0d\u80fd\u518d\u8bf4\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u660e\u513f\u5c31\u53bb\u3002\u201d \u3000\u3000\u63a5\u7740\u5c31\u4e0d\u518d\u8bf4\u8bdd\uff0c\u6562\u60c5\u4ed6\u5df2\u8fd0\u6c14\u7528\u529f\u8d77\u6765\u3002 \u3000\u3000\u5c0f\u57df\u5c9b\u4e0a\u77f3\u7b0b\u77d7\u7acb\uff0c\u6709\u5982\u4e00\u4e2a\u5de8\u4eba\u633a\u7acb\u5728\u851a\u84dd\u7684\u5929\u7a7a\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u5728\u8239\u4e0a\u8fdc\u8fdc\u77a7\u89c1\u90a3\u4e00\u6839\u6839\u7c97\u5927\u7684\u77f3\u7b0b\uff0c\u60f3\u5230\u81ea\u5df1\u5728\u8fd9\u5c0f\u5c9b\u4e0a\u6240\u5f97\u7684\u4e00\u8fde\u4e32\u5947\u9047\uff0c\u4e0d \u7981\u6ee1\u80f8\u611f\u6168\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5374\u5feb\u6d3b\u5730\u6325\u52a8\u4e24\u8896\uff0c\u7528\u5185\u5bb6\u771f\u529b\u9f13\u8239\u524d\u8fdb\uff0c\u8239\u9996\u7834\u5f00\u4e00\u6761\u767d\u5c0f\u6d6a\uff0c\u6025\u901f\u5730\u524d \u884c\u2014\u2014 \u3000\u3000\u8239\u5230\u4e86\u5cb8\uff0c\u4e24\u4eba\u5c31\u98de\u8eab\u4e0a\u5c9b\uff0c\u5e73\u51e1\u4e0a\u4eba\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u6717\u58f0\u9053\uff1a\u201c\u8001\u5c3c\u5a46\uff0c\u6211\u6765\u5e94\u6218 \u5566\uff01\u201d \u3000\u3000\u4e0d\u591a\u65f6\uff0c\u77f3\u7b0b\u9635\u4e2d\u73b0\u51fa\u4e00\u6761\u4eba\u5f71\uff0c\u51e0\u4e2a\u8d77\u843d\u5df1\u5230\u4e86\u9762\u524d\uff0c\u6b63\u662f\u5c0f\u57df\u5c9b\u4e3b\u6167\u5927\u5e08\u3002 \u3000\u3000\u8f9b\u6377\u4e0a\u524d\u62dc\u89c1\uff0c\u6167\u5927\u5e08\u4e00\u6325\u50e7\u888d\uff0c\u4e00\u80a1\u6781\u5f3a\u7684\u52b2\u98ce\u5c06\u8f9b\u6377\u76f4 \u3000\u3000\u5f80\u4e0a\u62ac\uff0c\u53e3\u4e2d\u51b7\u51b7\u9053\uff1a\u201c\u7f62\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u90a3\u52b2\u5927\u800c\u4e0d\u731b\uff0c\u76f4\u4f3c\u4e0e\u5c06\u81ea\u5df1\u62ac\u4e0a\u7a7a\u4e2d\u4e00\u822c\uff0c\u8f9b\u6377\u4e0d\u7981\u731b\u5438\u4e00\u53e3\u6c14\uff0c\u529b\u8d2f\u4e24 \u817f\uff0c\u4ecd\u662f\u4e00\u63d6\u5230\u5730\u3002 \u3000\u3000\u6167\u5927\u5e08\u54a6\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u529f\u529b\u53c8\u5927\u8fdb\u4e86\u3002\u201d \u3000\u3000\u63a5\u7740\u8f6c\u5934\u5bf9\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u65e9\u77e5\u9053\u4f60\u81ed\u548c\u5c1a\u4e0a\u6b21\u56de\u4fe1\u4e0d\u5e94\u6218\u4e43\u662f\u7f13\u5175\u4e4b\u8ba1\uff0c\u8fd9\u51e0\u5929\u5fc5 \u662f\u57cb\u5934\u82e6\u7814\uff0c\u60f3\u51fa\u4ec0\u4e48\u65b0\u62db\u5f0f\uff0c\u6240\u4ee5\u5c31\u6765\u5e94\u6218\u4e86\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4efb\u5979\u5632\u8c03\u5b8c\u6bd5\u624d\u4e00\u63d6\u9053\uff1a\u201c\u8001\u5c3c\u5a46\uff0c\u6211\u8001\u4eba\u5bb6\u8fd9\u53a2\u6709\u793c\u4e86\u2014\u2014\u6211\u8fd9\u867d\u662f\u5e94\u6218\uff0c \u5374\u6709\u4e00\u70b9\u513f\u4e0d\u540c\u2014\u2014\u201d \u3000\u3000\u6167\u5927\u5e08\u51b7\u51b7\u54fc\u4e86\u4e00\u58f0\u3002\u5e73\u51e1\u4e0a\u4eba\u7eed\u9053\uff1a \u3000\u3000\u201c\u6211\u662f\u4e0d\u80fd\u548c\u4f60\u52a8\u624b\u7684\u4e86\uff0c\u6211\u90a3\u638c\u6cd5\u90fd\u4f20\u7ed9\u4e86\u8fd9\u5a03\u513f\uff0c\u4f60\u53ef\u6562\u548c\u4ed6\u8fc7\u62db\uff1f\u201d \u3000\u3000\u6167\u5927\u5e08\u4e00\u8bed\u4e0d\u53d1\uff0c\u4ef0\u5929\u957f\u7b11\uff0c\u6839\u672c\u4e0d\u7406\u4f1a\u5e73\u51e1\u4e0a\u4eba\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e0d\u7981\u6012\u9053\uff1a\u201c\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6167\u5927\u5e08\u9053\uff1a\u201c\u4e0d\u6562\u5e94\u6218\u4e5f\u7f62\u4e86\uff0c\u5374\u8fd8\u8981\u5f04\u8fd9\u8bb8\u591a\u82b1\u5934\uff0c\u8d2b\u5c3c\u4eca\u5929\u7b97\u662f\u5f00\u4e86\u773c\u754c\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u542c\u4e86\u7a81\u7136\u4e5f\u4ef0\u5929\u5927\u7b11\uff0c\u6167\u5927\u5e08\u53ea\u51b7\u7136\u4e00\u54fc\u5e76\u4e0d\u7406\u4f1a\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u5979\u4e0d\u7406\u4f1a\uff0c\u7ee7\u7eed\u54c8\u54c8\u5927\u7b11\uff0c\u4ed6\u529f\u529b\u6df1\u6781\uff0c\u7b11\u5f97\u53c8\u54cd\u53c8\u957f\uff0c\u6167\u5927\u5e08\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f \u9053\uff1a\u201c\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8fd9\u624d\u505c\u4f4f\u7b11\u58f0\u9053\uff1a\u201c\u4e0d\u6562\u5e94\u6218\u4e5f\u7f62\u4e86\uff0c\u5374\u8fd8\u8981\u5f04\u8bb8\u591a\u82b1\u5934\uff0c\u8001\u548c\u5c1a\u4eca\u5929\u7b97\u662f\u5f00 \u4e86\u773c\u754c\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u548c\u6167\u5927\u5e08\u4e00\u5b57\u4e0d\u5dee\uff0c\u6167\u5927\u5e08\u4e0d\u7981\u6012\u9053\uff1a\u201c\u4e0d\u6562\u5e94\u6218\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6307\u4e86\u6307\u8f9b\u6377\u9053\uff1a\u201c\u4f60\u6562\u4e0e\u4ed6\u8fc7\u62db\u4e48\uff1f\u201d \u3000\u3000\u6167\u5927\u5e08\u6602\u9996\u51b7\u54fc\uff0c\u77a7\u90fd\u4e0d\u77a7\u8f9b\u6377\u4e00\u773c\u3002 \u3000\u3000\u8fd9\u6837\u5e73\u51e1\u4e0a\u4eba\u4e0d\u613f\u548c\u6167\u5927\u5e08\u52a8\u624b\uff0c\u6167\u5927\u5e08\u53c8\u4e0d\u613f\u548c\u8f9b\u6377\u52a8\u624b\uff0c\u4e8e\u662f\u53cc\u65b9\u7acb\u523b\u50f5\u4f4f\u4e86\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u523b\uff0c\u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136\u559c\u9053\uff1a\u201c\u6709\u4e86\uff01\u6709\u4e86\uff01\u201d \u3000\u3000\u6167\u5927\u5e08\u77aa\u76ee\u9053\uff1a\u201c\u6709\u4e86\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e2a\u5999\u8ba1\uff0c\u6211\u5df1\u5c06\u6211\u7684\u638c\u6cd5\u4f20\u7ed9\u4e86\u8fd9\u5a03\u513f\uff0c\u4f60\u4e5f\u5feb\u628a\u4f60\u7684\u638c\u6cd5\u4f20\u7ed9 \u4ed6\uff0c\u7136\u540e\u53eb\u4ed6\u7528\u4f60\u7684\u638c\u6cd5\u548c\u6211\u8fc7\u62db\uff0c\u7528\u6211\u7684\u638c\u6cd5\u548c\u4f60\u8fc7\u62db\uff0c\u8c01\u7684\u62db\u6cd5\u4e0d\u6210\uff0c\u8fd9\u4e00\u6bd4\u5c31\u6bd4\u51fa\u6765 \u5566\u3002\u201d \u3000\u3000\u6167\u5927\u5e08\u51b7\u7136\u9053\uff1a\u201c\u8fd9\u6cd5\u5b50\u5012\u4e0d\u9519\uff0c\u53ea\u662f\u6211\u90a3\u638c\u6cd5\u4e43\u662f\u6211\u5fc3\u8840\u6240\u805a\uff0c\u5c82\u80fd\u8f7b\u6613\u4f20\u7ed9\u8fd9\u5a03 \u513f\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u81ea\u5df1\u597d\u5bb9\u6613\u60f3\u51fa\u7684\u5999\u6cd5\u53c8\u88ab\u6167\u5927\u5e08\u56de\u7edd\uff0c\u4e0d\u7981\u6012\u9053\uff1a\u201c\u4f60\u8fd8\u6015\u4ed6\u672c\u4e8b\u8d85\u8fc7 \u4f60\uff1f\u8001\u5c3c\u5a46\u65e2\u60e9\u822c\u7684\u5c0f\u6c14\uff0c\u54b1\u4eec\u4e0d\u6bd4\u4e5f\u7f62\u3002\u201d \u3000\u3000\u8bf4\u7f62\u8f6c\u8eab\u5c31\u8d70\uff0c\u6167\u5927\u5e08\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u9053\uff1a\u201c\u6bd4\u5c31\u6bd4\uff0c\u5a03\u513f\u6765\uff0c\u6211\u8fd9\u5c31\u4f20\u4f60\u638c\u6cd5\u2014\u2014\u81ed\u548c\u5c1a \u53ef\u4e0d\u8bb8\u5077\u770b\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u6211\u8001\u513f\u5c82\u4f1a\u5e0c\u7f55\u4f60\u90a3\u51e0\u624b\uff0c\u6211\u5230\u90a3\u8fb9\u53bb\uff0c\u4f60\u603b\u653e\u5fc3\u4e86\u3002\u201d \u3000\u3000\u6167\u5927\u5e08\u628a\u8f9b\u6377\u5e26\u5230\u5c9b\u7684\u897f\u7aef\uff0c\u5f00\u59cb\u6089\u5fc3\u4f20\u6388\u3002 \u3000\u3000\u8f9b\u6377\u8fde\u5f97\u4e24\u5927\u5947\u4eba\u7684\u5f97\u610f\u4e4b\u5b66\uff0c\u5b9e\u662f\u559c\u5f97\u5fc3\u75d2\u96be\u6414\uff0c\u4f46\u4ed6\u77e5\u9053\u8fd9\u79cd\u4e16\u5916\u9ad8\u4eba\u4ece\u4e0d\u6536\u5f92\uff0c \u8fd9\u79cd\u673a\u4f1a\u786e\u662f\u5343\u8f7d\u96be\u9022\uff0c\u81ea\u7136\u5168\u795e\u8d2f\u6ce8\u52aa\u529b\u7814\u4e60\u3002 \u3000\u3000\u6167\u5927\u5e08\u8fd9\u5957\u638c\u6cd5\u65e0\u602a\u4e4e\u8981\u5bfb\u5e73\u51e1\u4e0a\u4eba\u6311\u6218\uff0c\u7684\u786e\u662f\u9b3c\u795e\u83ab\u6d4b\uff0c\u8f9b\u6377\u5b66\u4e86\u4e94\u4e03\u65e5\u624d\u5b66\u4e86\u4e00 \u534a\uff0c\u4ed6\u5fc3\u4e2d\u6697\u53f9\u9053\uff1a \u3000\u3000\u201c\u4e0d\u6599\u5929\u4e0b\u771f\u6709\u8fd9\u7b49\u9b3c\u795e\u83ab\u6d4b\u638c\u6cd5\uff0c\u82e5\u662f\u914d\u4e0a\u90a3\u2018\u8bd8\u6469\u795e\u6b65\u2019\uff0c\u5e73\u51e1\u4e0a\u4eba\u7684\u2018\u7a7a\u7a7a\u638c \u6cd5\u2019\u5c3d\u7ba1\u7cbe\u5947\u65e0\u4f26\uff0c\u6015\u4e5f\u4e0d\u90a3\u4e48\u5c31\u4e00\u5b9a\u80dc\u5f97\u4e86\u8fd9\u5957\u638c\u6cd5\uff0c\u82e5\u662f\u4e16\u5916\u4e09\u4ed9\u4e00\u9f50\u5408\u529b\u7814\u7a76\u6b66\u5b66\uff0c \u5c82\u4e0d\u662f\u66ff\u5929\u4e0b\u6b66\u6797\u591a\u521b\u65e0\u6570\u6b66\u529f\uff1f\u201d \u3000\u3000\u6b63\u601d\u7d22\u95f4\uff0c\u5ffd\u7136\u4e00\u6761\u4eba\u5f71\u5feb\u4f3c\u95ea\u7535\u822c\u5730\u98de\u4e86\u8fc7\u6765\uff0c\u4e00\u7728\u773c\u95f4\u4f7f\u5230\u4e86\u9762\u524d\uff0c\u5374\u662f\u90a3\u5e73\u51e1\u4e0a \u4eba\uff01 \u3000\u3000\u6167\u5927\u5e08\u9053\uff1a\u201c\u81ed\u548c\u5c1a\u600e\u4e48\u53c8\u8dd1\u6765\u5566\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6025\u9053\uff1a\u201c\u54b1\u4eec\u4e0d\u6bd4\u5566\uff0c\u6211\u5927\u6222\u5c9b\u4e0a\u6765\u4e86\u5f3a\u4eba\uff0c\u6211\u5f97\u8d76\u7d27\u56de\u53bb\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u63d0\u8d77\u624b\u4e0a\u4e00\u7269\uff0c\u4f17\u4eba\u4e00\u770b\uff0c\u53ea\u89c1\u4ed6\u624b\u4e2d\u63d0\u7740\u4e00\u53ea\u6b7b\u9e70\uff0c\u54bd\u5589\u8fb9\u4e0a\u63d2\u7740\u4e00\u652f\u77ed\u7bad\u3002 \u3000\u3000\u8f9b\u6377\u8ba4\u5f97\u90a3\u9e70\uff0c\u6b63\u662f\u5927\u6222\u5c9b\u4e0a\u7684\u9e70\uff0c\u60f3\u662f\u88ab\u4eba\u5c04\u4e86\u6323\u624e\u7740\u98de\u5230\u8fd9\u91cc\u6b7b\u4e86\uff0c\u5f53\u4e0b\u4e0a\u524d\u7ec6\u770b \u90a3\u7bad\u7fbd\u3002 \u3000\u3000\u90a3\u7bad\u7fbd\u7684\u8d28\u6599\u6837\u5f0f\u90fd\u6781\u602a\u5f02\uff0c\u770b\u6765\u4e0d\u662f\u4e2d\u571f\u6240\u5236\uff0c\u4f46\u7ec6\u770b\u4e0a\u9762\u5374\u523b\u7740\u5c0f\u5c0f\u4e09\u4e2a\u6c49\u5b57\u3002 \u3000\u3000\u8f9b\u6377\u51d1\u8fd1\u4e00\u770b\uff0c\u539f\u6765\u7adf\u662f\u201c\u91d1\u9c81\u5384\u201d\u4e09\u4e2a\u5b57\u3002 \u3000\u3000\u8f9b\u6377\u554a\u4e86\u4e00\u58f0\uff0c\u628a\u5b57\u62ff\u7ed9\u5927\u6222\u5c9b\u4e3b\u770b\uff0c\u5927\u6222\u5c9b\u4e3b\u4e00\u770b\u4e4b\u540e\uff0c\u6012\u9053\uff1a\u201c\u539f\u6765\u662f\u8fd9\u80e1\u5c0f\u5b50\uff0c \u54fc\uff0c\u4ed6\u7adf\u6562\u6740\u6211\u9e70\u513f\u2014\u2014\u201d \u3000\u3000\u63a5\u7740\u8f6c\u8eab\u5c31\u8d70\uff0c\u8f9b\u6377\u5fc3\u60f3\u65e2\u662f\u91d1\u9c81\u5384\uff0c\u53ea\u4f2f\u591a\u534a\u662f\u8981\u5bfb\u81ea\u5df1\u6666\u6c14\uff0c\u7acb\u523b\u53eb\u9053\uff1a\u201c\u4e0a\u4eba\u4e14 \u6162\uff0c\u665a\u8f88\u4e5f\u53bb\u2014\u2014\u201d \u3000\u3000\u6167\u5927\u5e08\u5374\u54fc\u4e86\u4e00\u58f0\uff0c\u4e00\u8dc3\u6570\u91cc\u843d\u5728\u77f3\u6797\u4e2d\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5e26\u7740\u8f9b\u6377\u6467\u8239\u75be\u884c\uff0c\u6e10\u6e10\u884c\u8fd1\u5927\u6222\u5c9b\uff0c\u8fdc\u8fdc\u5c31\u671b\u89c1\u4e24\u8258\u5927\u8239\u6cca\u5728\u5cb8\u8fb9\uff0c\u5e73\u51e1\u4e0a \u4eba\u5fc3\u4e2d\u4e00\u6025\uff0c\u53cc\u8896\u5411\u540e\u4e00\u62c2\uff0c\u4e24\u80a1\u6392\u5c71\u5012\u6d77\u7684\u5185\u5bb6\u771f\u529b\u63a8\u51fa\uff0c\u8239\u884c\u5982\u7bad\uff0c\u8239\u5e95\u867d\u5df2\u89e6\u4e86\u6c99\uff0c \u4f46\u4ecd\u5982\u7bad\u4e00\u822c\u76f4\u51b2\u6c99\u6ee9\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u50ac\u9053\uff1a\u201c\u5a03\u513f\uff0c\u5feb\uff01\u201d \u3000\u3000\u4e00\u624b\u633d\u8d77\u8f9b\u6377\uff0c\u811a\u4e0b\u7528\u529b\uff0c\u8eab\u4f53\u817e\u7a7a\u800c\u8d77\u76f4\u843d\u5728\u516d\u4e03\u4e08\u7684\u4e00\u68f5\u8001\u6797\u6811\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u5c45\u9ad8\u4e34\u4e0b\uff0c\u53ea\u89c1\u4e0b\u9762\u6797\u4e2d\u4e00\u5927\u7fa4\u4eba\u6b63\u5728\u62fc\u6597\uff0c\u7adf\u662f\u5341\u4e03\u516b\u4e2a\u548c\u5c1a\u56f4\u7740\u56db\u4e2a\u4eba\u62fc\u6597\uff0c \u90a3\u56db\u4e2a\u4eba\u6253\u626e\u751a\u662f\u53e4\u602a\uff0c\u5012\u6709\u4e09\u4e2a\u4eba\u662f\u548c\u5c1a\u88c5\u675f\uff0c\u5374\u90fd\u7740\u4e86\u4e00\u8eab\u5927\u7ea2\u8888\u88df\uff0c\u53e6\u4e00\u4eba\u5374\u662f\u5112\u751f \u6253\u626e\uff0c\u56db\u4e2a\u4eba\u8f6e\u6d41\u53d1\u638c\uff0c\u4f3c\u4e4e\u5a01\u529b\u5927\u5f97\u5f02\u5e38\uff0c\u90a3\u4e9b\u548c\u5c1a\u6ca1\u6709\u4e00\u4e2a\u6562\u8fd1\u8eab\u3002 \u3000\u3000\u5f85\u90a3\u5112\u751f\u4e00\u53d1\u638c\uff0c\u8f9b\u6377\u604d\u7136\u5927\u609f\uff0c\u4f4e\u58f0\u5bf9\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u90a3\u5112\u751f\u6253\u626e\u6b63\u662f\u91d1\u9c81\u5384\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u51b7\u7b11\u9053\uff1a\u201c\u90a3\u4e09\u4e2a\u548c\u5c1a\u662f\u9ad8\u624b\uff0c\u54fc\uff0c\u602a\u4e0d\u5f97\u91d1\u9c81\u5384\u8fd9\u5c0f\u5b50\u6562\u5230\u5927\u6222\u5c9b\u6765\u6492\u91ce\uff0c \u539f\u6765\u6709\u4e86\u9760\u5c71\u3002\u201d \u3000\u3000\u8f9b\u6377\u95fb\u8a00\u6ce8\u89c6\u90a3\u7ea2\u8863\u548c\u5c1a\uff0c\u53ea\u89c1\u5176\u4e2d\u4e00\u4eba\u547c\u5730\u4e00\u638c\u5288\u51fa\uff0c\u6b63\u9762\u4e00\u4e2a\u4e2d\u5e74\u548c\u5c1a\u95ea\u8eab\u4e00\u8ba9\uff0c \u7830\u7684\u4e00\u58f0\uff0c\u540e\u9762\u4e00\u68f5\u6876\u53e3\u5927\u7684\u5de8\u677e\u7adf\u7136\u5e94\u58f0\u800c\u6298\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u9a87\u7136\uff0c\u6697\u601d\uff1a\u201c\u8fd9\u4e09\u4e2a\u7ea2\u8863\u548c\u5c1a\u529f\u529b\u4e4b\u9ad8\u53ea\u6015\u4e0d\u5728\u4e16\u5916\u4e09\u4ed9\u4e4b\u4e0b\uff0c\u5b9a\u662f\u90a3\u91d1\u9c81 \u5384\u7684\u5e08\u8f88\u7684\u4e86\u2014\u2014\u201d \u3000\u3000\u56de\u770b\u5e73\u51e1\u4e0a\u4eba\uff0c\u5374\u89c1\u4ed6\u6b63\u51dd\u89c6\u7740\u90a3\u4e00\u7fa4\u548c\u5c1a\uff0c\u8138\u4e0a\u795e\u8272\u751a\u662f\u53e4\u602a\uff0c\u8f9b\u6377\u4e0d\u7981\u5927\u5947\uff0c\u4e5f\u7ec6 \u770b\u90a3\u7fa4\u548c\u5c1a\uff0c\u53ea\u89c1\u5171\u662f\u4e00\u5341\u516b\u4e2a\uff0c\u5176\u4e2d\u5374\u5939\u7740\u4e00\u4e2a\u4fd7\u5bb6\u9752\u5e74\uff0c\u7ec6\u770b\u4e4b\u4e0b\uff0c\u7adf\u662f\u90a3\u201c\u6b66\u6797\u4e4b \u79c0\u201d\u5b59\u501a\u91cd\uff01 \u3000\u3000\u8f9b\u6377\u604d\u7136\u9053\uff1a\u201c\u5475\uff0c\u5c11\u6797\u5bfa\uff01\u201d \u3000\u3000\u518d\u770b\u90a3\u5341\u4e03\u4e2a\u5c11\u6797\u50e7\u4eba\u548c\u5b59\u501a\u91cd\uff0c\u516b\u6210\u662f\u6309\u7740\u4e00\u79cd\u6781\u7eaf\u719f\u7684\u6b65\u6cd5\u56f0\u7740\u7ea2\u8863\u50e7\u53ca\u91d1\u9c81\u5384\uff0c \u4ed6\u5fc3\u4e2d\u4e00\u9707\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u6015\u5c31\u662f\u95fb\u540d\u5929\u4e0b\u7684\u2018\u7f57\u6c49\u9635\u2019\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u4e0d\u597d\uff0c\u90a3\u5929\u5170\u548c\u5c1a\u5c31\u8981\u65bd\u8fa3\u624b\uff0c\u5c11\u6797\u548c\u5c1a\u5c31\u8981\u843d\u8d25\uff0c\u54b1\u4eec\u5feb\u53bb\u2014\u2014 \u201d \u3000\u3000\u54cd\u58f0\u624d\u843d\uff0c\u4eba\u5df1\u817e\u7a7a\u800c\u8d77\uff0c\u8f9b\u6377\u4e00\u6014\uff0c\u7d27\u63a5\u7740\u4e5f\u4e0d\u8eab\u800c\u8d77\uff0c\u5728\u7a7a\u4e2d\u5df2\u95fb\u60ca\u53eb\u58f0\u8d77\uff0c\u539f\u6765 \u8fd9\u4e09\u4e2a\u7ea2\u8863\u548c\u5c1a\u53ca\u91d1\u5907\u5384\u679c\u7136\u5df1\u53cd\u5b88\u4e3a\u653b\uff0c\u7740\u7740\u8fdb\u51fb\u3002 \u3000\u3000\u5237\u5730\u4e00\u58f0\uff0c\u5e73\u51e1\u4e0a\u4eba\u5df1\u5982\u98de\u9a6c\u884c\u7a7a\u822c\u964d\u4e86\u4e0b\u6765\uff0c\u4e24\u8896\u4e00\u626c\uff0c\u6b63\u597d\u628a\u5f53\u5148\u4e00\u4e2a\u7ea2\u8863\u50e7\u7684\u4e00 \u638c\u63a5\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u7830\u7684\u4e00\u58f0\uff0c\u90a3\u7ea2\u8863\u501f\u88ab\u9707\u5f97\u8eab\u8eaf\u4e00\u7a92\uff0c\u5e73\u51e1\u4e0a\u4eba\u7adf\u4e5f\u662f\u53cc\u80a9\u4e71\u6643\uff0c\u4e24\u4eba\u90fd\u60ca\u54a6\u4e00\u58f0\u3002 \u3000\u3000\u90a3\u7ea2\u8863\u548c\u5c1a\u9762\u5982\u7b06\u6597\uff0c\u6012\u77aa\u5e73\u51e1\u4e0a\u4eba\u4e00\u773c\uff0c\u626c\u638c\u53c8\u662f\u4e00\u8bb0\u63a8\u51fa\u2014\u2014 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53cd\u624b\u4e00\u8bb0\u524a\u51fa\uff0c\u4e0d\u8fdb\u4e0d\u8ba9\u7684\u786c\u8fce\u4e0a\u53bb\uff0c\u7830\u7136\u4e00\u54cd\uff0c\u4e24\u4eba\u7adf\u90fd\u9000\u540e\u534a\u6b65\uff01 \u3000\u3000\u8fd9\u771f\u662f\u767e\u5e74\u6765\u4ece\u672a\u6709\u7684\u4e8b\uff0c\u4ee5\u6167\u5927\u5e08\u65e0\u6068\u751f\u7684\u529f\u529b\uff0c\u5728\u548c\u529f\u529b\u6df1\u539a\u7684\u5e73\u51e1\u4e0a\u4eba\u8fc7\u62db\u65f6\u90fd \u5c3d\u91cf\u907f\u514d\u548c\u4ed6\u786c\u78b0\uff0c\u8fd9\u5929\u5170\u548c\u5c1a\u7adf\u7136\u548c\u5e73\u51e1\u4e0a\u4eba\u786c\u6253\u786c\u62c9\uff0c\u96be\u602a\u5e73\u51e1\u4e0a\u4eba\u8981\u6697\u60ca\u4e0d\u5df2\u3002 \u3000\u3000\u90a3\u5929\u5170\u548c\u5c1a\u5fc3\u4e2d\u5374\u66f4\u662f\u60ca\u5f02\uff0c\u4ed6\u638c\u4e0a\u529f\u592b\u5728\u5929\u5170\u662f\u7b2c\u4e00\u9ad8\u624b\uff0c\u7adf\u88ab\u5e73\u51e1\u4e0a\u4eba\u9707\u5f97\u6709\u4e9b\u5fc3 \u6c14\u6d6e\u52a8\uff01 \u3000\u3000\u90a3\u91d1\u9c81\u5384\u6307\u7740\u8f9b\u6377\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5c31\u662f\u8fd9\u5c0f\u5b50\uff01\u201d \u3000\u3000\u7ad9\u5728\u6700\u540e\u7684\u7ea2\u8863\u50e7\u6253\u91cf\u4e86\u8f9b\u6377\u4e00\u773c\uff0c\u64cd\u4f5c\u751f\u786c\u7684\u6c49\u8bed\u9053\uff1a\u201c\u4f60\u53ef\u662f\u8fd9\u548c\u5c1a\u7684\u5f92\u5f1f\uff1f\u201d\u8bf4 \u7740\u6307\u4e86\u6307\u5e73\u51e1\u4e0a\u4eba\u3002 \u3000\u3000\u8f9b\u6377\u6b63\u5f85\u56de\u7b54\uff0c\u5e73\u51e1\u4e0a\u4eba\u559d\u9053\uff1a\u201c\u5a03\u513f\uff0c\u522b\u7406\u4ed6\uff01\u201d \u3000\u3000\u90a3\u5929\u7afa\u50e7\u77aa\u4e86\u5e73\u51e1\u4e0a\u4eba\u4e00\u773c\u3002\u5ffd\u7136\u5e72\u7b11\u9053\uff1a\u201c\u8fd9\u4f4d\u60f3\u5fc5\u662f\u4eba\u79f0\u4e16\u5916\u4e09\u4ed9\u4e2d\u7684\u5e73\u51e1\u4e0a\u4eba \u4e86\uff0c\u8d2b\u50e7\u5144\u5f1f\u80fd\u89c1 \u3000\u3000\u8fd9\u7b49\u4e16\u5916\u9ad8\u4eba\uff0c\u4f55\u5e78\u4e4b\u6709\u3002\u201d \u3000\u3000\u63a5\u7740\u6307\u7740\u5148\u524d\u548c\u5e73\u51e1\u4e0a\u4eba\u5bf9\u638c\u7684\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u662f\u655d\u5e08\u5144\u4f2f\u7f57\u5404\u7b54\u2014\u2014\u201d \u3000\u3000\u53c8\u6307\u7740\u53e6\u4e00\u4e2a\u5168\u9762\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u662f\u655d\u5e08\u5f1f\u76d8\u706f\u5b5a\u5c14\u2014\u2014\u8d2b\u50e7\u662f\u91d1\u4f2f\u80dc\u5937\uff0c\u655d\u5144\u5f1f\u4eba\u79f0 \u2018\u6052\u6cb3\u4e09\u4f5b\u2019\uff0c\u563f\u563f\uff0c\u5176\u5b9e\u6052\u6cb3\u53ea\u662f\u6761\u5c0f\u6cb3\uff0c\u54b1\u4eec\u5144\u5f1f\u603b\u60f3\u82e5\u662f\u80fd\u6539\u6210\u2018\u9ec4\u6cb3\u4e09\u4f5b\u2019\uff0c\u90a3\u53ef \u771f\u6709\u610f\u601d\uff0c\u518d\u8bf4\u54b1\u4eec\u5165\u4f4f\u4e2d\u539f\u5bf9\u4e2d\u56fd\u6b66\u6797\u4e5f\u5927\u6709\u88e8\u76ca\uff0c\u4e2d\u539f\u6b66\u6797\u4eba\u7269\u6ca1\u6709\u4e00\u4e2a\u4e0d\u662f\u9ad8\u5174\u4e07\u5206 \u7684\u2014\u2014\u53ea\u662f\uff0c\u53ea\u662f\u4ee4\u5f92\u5374\u786c\u6765\u67b6\u6881\uff0c\u672c\u6765\u8fd9\u4e8b\u6211\u4e5f\u4e0d\u7ba1\uff0c\u4e0d\u8fc7\u54b1\u4eec\u4e00\u6253\u542c\u4e4b\u4e0b\uff0c\u539f\u6765\u662f\u4f60\u4e16 \u5916\u4e09\u4ed9\u505a\u4ed6\u7684\u9760\u5c71\uff0c\u8fd9\u4e2a\u54b1\u4eec\u5c31\u8981\u7ba1\u4e00\u7ba1\u5566\u2026\u2026\u201d \u3000\u3000\u8fd9\u65f6\u53cc\u65b9\u62fc\u6597\u65e9\u5df2\u505c\u6b62\uff0c\u90a3\u5341\u51e0\u4e2a\u548c\u5c1a\u7a81\u7136\u7531\u4e00\u4e2a\u8001\u548c\u5c1a\u5e26\u7740\u8d70\u6765\uff0c\u5230\u4e86\u5e73\u51e1\u4e0a\u4eba\u9762 \u524d\uff0c\u4e00\u9f50\u8dea\u4e86\u4e0b\u53bb\uff0c\u8001\u548c\u5c1a\u9053\uff1a\u201c\u5f1f\u5b50\u5c11\u6797\u7b2c\u5341\u56db\u4ee3\u638c\u95e8\u667a\u656c\u7387\u95e8\u4e0b\u62dc\u89c1\u7075\u7a7a\u7956\u5e08\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8138\u8272\u5927\u53d8\uff0c\u4e00\u8dc3\u800c\u8d77\uff0c\u53cc\u624b\u4e71\u6447\u9053\uff1a\u201c\u548c\u5c1a\u4f60\u627e\u9519\u4e86\uff0c\u8d2b\u2014\u2014\u8d2b\u50e7\u4e0d\u662f\u7075\u7a7a\uff0c \u7075\u7a7a\u65e9\u5c31\u6b7b\u4e86\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u867d\u662f\u548c\u5c1a\u88c5\u675f\uff0c\u4f46\u767e\u5e74\u6765\u65e9\u5df2\u4e0d\u4ee5\u548c\u5c1a\u81ea\u89c6\uff0c\u8fd9\u201c\u8d2b\u50e7\u201d\u4e24\u5b57\u8bf4\u5f97\u597d\u751f\u751f\u7409\u3002 \u3000\u3000\u5c11\u6797\u5bfa\u7684\u8f88\u5206\u662f\u6309\u7075\u6e05\u660e\u667a\u81ea\u5b9a\u6765\u6392\u7684\uff0c\u8fd9\u5c11\u6797\u5bfa\u638c\u95e8\u662f\u201c\u667a\u201d\u5b57\u8f88\uff0c\u800c\u4ed6\u8bf4\u7684\u7075\u7a7a\u7adf \u662f\u56db\u8f88\u4ee5\u524d\u7684\u201c\u7075\u201d\u5b57\u8f88\u3002 \u3000\u3000\u90a3\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u89c1\u8fd9\u7fa4\u548c\u5c1a\u7a81\u7136\u5bf9\u5e73\u51e1\u4e0a\u4eba\u62dc\u8dea\uff0c\u90fd\u4e0d\u7981\u4e00 \u3000\u3000\u8f9b\u6377\u5374\u9661\u7136\u60f3\u8d77\u90a3\u5b59\u501a\u91cd\u53ca\u5c11\u6797\u548c\u5c1a\u65e0\u6545\u627e\u81ea\u5df1\u8f83\u91cf\u7684\u4e8b\u6765\uff0c\u4ed6\u6697\u9053\uff1a\u201c\u5c11\u6797\u5bfa\u7684\u4eba\u663e \u7136\u53d1\u73b0\u5e73\u51e1\u4e0a\u4eba\u7684\u2018\u5927\u884d\u5341\u5f0f\u2019\u624d\u627e\u6211\u8f83\u91cf\u7684\uff0c\u8fd9\u8001\u548c\u5c1a\u65b9\u624d\u53c8\u79f0\u5e73\u51e1\u4e0a\u4eba\u4ec0\u4e48\u2018\u7075\u7a7a\u7956 \u5e08\u2019\uff0c\u96be\u9053\u5e73\u51e1\u4e0a\u4eba\u5f53\u771f\u4e0e\u5c11\u6797\u5bfa\u6709\u5173\u8054\uff1f\u55ef\uff0c\u5e73\u51e1\u4e0a\u4eba\u65b9\u624d\u867d\u9053\u2018\u7075\u7a7a\u2019\u65e9\u5c31\u6b7b\u5566\u2019\uff0c\u4f46 \u662f\u663e\u7136\u4ed6\u662f\u8ba4\u5f97\u2018\u7075\u7a7a\u2019\u7684\u4e86\uff0c\u83ab\u975e\u2014\u2014\u201d \u3000\u3000\u8fd9\u65f6\u90a3\u91d1\u9c81\u5384\u7684\u5e08\u7236\u91d1\u4f2f\u80dc\u5937\u9053\uff1a\u201c\u5e73\u51e1\u5927\u5e08\uff0c\u54b1\u4eec\u7d22\u6027\u8bf4\u4e2a\u6e05\u723d\uff0c\u4eca\u65e5\u6052\u6cb3\u4e09\u4f5b\u8981\u627e \u4e16\u5916\u4e09\u4ed9\u8f83\u91cf\u4e00\u4e0b\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4f3c\u4e4e\u5fc3\u4e71\u5982\u9ebb\uff0c\u8f6c\u9996\u5bf9\u8dea\u5728\u5730\u4e0a\u7684\u5c11\u6797\u50e7\u9053\uff1a\u201c\u4f60\u4eec\u627e\u9519\u4e86\uff0c\u6211\u2014\u2014\u8d2b\u50e7\u771f\u4e0d \u662f\u7075\u7a7a\u554a\u2014\u2014\u201d \u3000\u3000\u90a3\u667a\u656c\u8001\u548c\u5c1a\u53e9\u5934\u9053\uff1a\u201c\u7956\u5e08\u8fd8\u8981\u7792\u5f1f\u5b50\u4e48\uff1f\u90a3\u8f9b\u5e08\u7956\u7684\u5251\u6cd5\u6b63\u662f\u5c11\u6797\u5931\u4f20\u7684\u79d8\u6280\u554a\u2014 \u2014\u201d \u3000\u3000\u6562\u60c5\u4ed6\u6bd4\u201c\u7075\u7a7a\u201d\u77ee\u4e86\u56db\u8f88\uff0c\u4ed6\u79f0\u8f9b\u6377\u4e3a\u5e08\u7956\u662f\u4ee5\u4e3a\u8f9b\u6377\u662f\u5e73\u51e1\u4e0a\u4eba\u7684\u5f92\u5f1f\u4e4b\u6545\u3002 \u3000\u3000\u90a3\u91d1\u4f2f\u80dc\u5937\u4e0d\u8010\u9053\uff1a\u201c\u5e73\u51e1\u4e0a\u4eba\u82e5\u4e0d\u6562\u5e94\u6218\u4e5f\u7f62\uff0c\u53ea\u8981\u628a\u8fd9\u59d3\u8f9b\u7684\u5c0f\u5b50\u8ba9\u54b1\u4eec\u5e26\u53bb\u2014\u2014 \u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5fc3\u4e71\u5982\u9ebb\uff0c\u5ffd\u542c\u6052\u6cb3\u4e09\u4f5b\u8981\u5e26\u8f9b\u6377\u53bb\uff0c\u4e0d\u7981\u6012\u9053\uff1a\u201c\u653e\u5c41\uff01\u201d\u3002 \u3000\u3000\u4ed6\u4e24\u5934\u65e0\u63aa\u4e4b\u4e0b\uff0c\u5fc3\u4e00\u6a2a\uff0c\u6697\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u53ea\u597d\u4e00\u8d70\u4e86\u4e4b\u3002\u201d \u3000\u3000\u5f53\u4e0b\u4e00\u628a\u6293\u4f4f\u8f9b\u6377\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u6597\u7136\u65bd\u51fa\u7edd\u4e16\u8f7b\u529f\uff0c\u7728\u773c\u4e0d\u89c1\u8e2a\u5f71\u3002 \u3000\u3000\u6052\u6cb3\u4e09\u4f5b\u4e0d\u6599\u4ed6\u800d\u51fa\u8fd9\u4e00\u624b\uff0c\u4e00\u6014\u4e4b\u4e0b\u6025\u53eb\u4e00\u58f0\uff0c\u4e5f\u5982\u98de\u8ffd\u53bb\uff0c\u53ea\u5269\u4e0b\u5446\u8dea\u5728\u5730\u4e0a\u7684\u5341 \u516b\u4e2a\u5c11\u6797\u5f1f\u5b50\u2014\u2014 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6781\u559c\u8f9b\u6377\uff0c\u4ed6\u77e5\u6052\u6cb3\u4e09\u4f5b\u662f\u8981\u6740\u4e86\u8f9b\u6377\uff0c\u7528\u8f9b\u6377\u7684\u5174\u9885\u5230\u4e2d\u539f\u53bb\u9547\u538b\u4eba\u5fc3\uff0c\u66ff \u4ed6\u5f92\u513f\u91d1\u9c81\u5384\u51fa\u6c14\uff0c\u4e00\u9762\u75be\u5954\uff0c\u4e00\u9762\u4f4e\u58f0\u5bf9\u8f9b\u6377\u9053\uff1a\u201c\u90a3\u51e0\u4e2a\u5929\u7afa\u8001\u9b3c\u662f\u60f3\u8981\u4f60\u5c0f\u547d\uff0c\u6211\u8001 \u4eba\u5bb6\u4e0d\u4f9d\uff0c\u4f46\u6211\u4e00\u4e2a\u4eba\u5374\u4e0d\u662f\u4ed6\u4eec\u7684\u5bf9\u624b\uff0c\u4e3a\u4eca\u53ea\u6709\u4e00\u6761\u8def\u597d\u8d70\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u51b0\u96ea\u806a\u660e\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u5230\u5c0f\u6222\u5c9b\u53bb\uff01\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u6b63\u662f\uff0c\u54b1\u4eec\u5feb\u8d76\uff01\u201d \u3000\u3000\u8f9b\u6377\u6c89\u541f\u4e86\u4e00\u4f1a\u9053\uff1a\u201c\u53ea\u662f\uff0c\u53ea\u662f\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u53ea\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u53ea\u662f\u6015\u90a3\u6167\u5927\u5e08\u4e0d\u80af\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u6211\u597d\u6b79\u8981\u6fc0\u5979\u51fa\u624b\u2014\u2014\u6211\u4eec\u4e00\u5230\u5c0f\u6222\u5c9b\uff0c\u5f85\u6211\u4e0a\u4e86\u5cb8\uff0c\u4f60\u7acb\u523b\u9a7e\u8239\u5230\u65e0 \u6781\u5c9b\u53bb\u627e\u90a3\u65e0\u6068\u751f\uff0c\u90a3\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u6b66\u529f\u5f3a\u6781\u4e86\uff0c\u53ea\u6015\u6211\u4e0e\u6167\u5927\u5e08\u4e24\u4eba\u62b5\u4e0d\u4f4f\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e00\u542c\u8981\u5230\u65e0\u6781\u5c9b\u53bb\uff0c\u987f\u65f6\u5fc3\u4e2d\u4e00\u9707\uff0c\u83c1\u513f\u90a3\u5982\u82b1\u5a07\u9765\u7acb\u523b\u6d6e\u4e0a\u5fc3\u5934\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u633d\u7740\u8f9b\u6377\u5954\u5230\u6d77\u8fb9\uff0c\u8ddd\u90a3\u5c0f\u8247\u5c1a\u6709\u5341\u4e08\uff0c\u5df1\u662f\u817e\u7a7a\u98de\u8d77\uff0c\u4eba\u843d\u5728\u5c0f\u821f\uff0c\u51b2\u529b\u5374 \u6070\u5de7\u4f7f\u5c0f\u8247\u51b2\u5165\u6d77\u6c34\u4e2d\uff0c\u501f\u7740\u4e00\u4e2a\u6d6a\u9000\u56de\uff0c\u5c0f\u821f\u5df1\u98d8\u51fa\u4e08\u4f59\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u628a\u64cd\u8d77\u6728\u6868\uff0c\u731b\u529b\u4e00\u677f\uff0c\u5c0f\u821f\u5982\u7bad\u800c\u524d\uff0c\u5e73\u51e1\u4e0a\u4eba\u5374\u7ad9\u8239\u4e0a\u53cc\u8896\u8fde\u6325\uff0c\u7528\u5185\u529b\u9f13 \u821f\u524d\u8fdb\u3002 \u3000\u3000\u8f9b\u6377\u867d\u7136\u4e0d\u5584\u9a7e\u8239\uff0c\u4f46\u4ed6\u81c2\u52b2\u6781\u5f3a\uff0c\u5343\u6868\u677f\u51fa\u4e0d\u4e0b\u6570\u767e\u65a4\uff0c\u662f\u4ee5\u4e0d\u6d88\u51e0\u677f\uff0c\u8239\u513f\u5df1\u5230\u4e86 \u6d77\u4e2d\u3002 \u3000\u3000\u56de\u9996\u4e00\u671b\uff0c\u90a3\u6052\u6cb3\u4e09\u4f5b\u53ca\u91d1\u9c81\u5384\u4e5f\u5306\u5306\u8d76\u5230\u6d77\u8fb9\uff0c\u8df3\u4e0a\u4e00\u6761\u5927\u8239\uff0c\u542f\u951a\u8ffd\u6765\u3002 \u3000\u3000\u5927\u8239\u4e0a\u5e06\u6868\u5e76\u7528\uff0c\u6781\u662f\u5feb\u6377\uff0c\u4f46\u8f9b\u6377\u8fd9\u8fb9\u5374\u4ed7\u7740\u5c0f\u821f\u8f7b\u5feb\uff0c\u662f\u4ee5\u4e0d\u81f4\u88ab\u8d76\u4e0a\u3002 \u3000\u3000\u518d\u5212\u5f97\u51e0\u6868\uff0c\u8f9b\u6377\u62bd\u7a7a\u56de\u9996\u4e00\u770b\uff0c\u53ea\u89c1\u4e09\u4e2a\u7ea2\u8863\u50e7\u90fd\u7ad9\u5728\u8239\u4e0a\u7528\u8863\u8896\u9f13\u8239\u524d\u8fdb\uff0c\u662f\u4ee5\u5927 \u8239\u901f\u5ea6\u5927\u589e\uff0c\u7adf\u662f\u6e10\u6e10\u8ffd\u8fd1\u2014\u2014 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4fef\u8eab\u5728\u8239\u5934\u63d0\u8d77\u94c1\u951a\uff0c\u5c06\u7ef3\u5b50\u626f\u53bb\uff0c\u5f85\u5927\u8239\u8ffd\u8fd1\uff0c\u7a81\u7136\u6597\u624b\u5c06\u94c1\u951a\u6253\u51fa\u2014\u2014 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4f55\u7b49\u529f\u80fd\uff0c\u90a3\u94c1\u951a\u7adf\u631f\u9e23\u9e23\u4e4b\u602a\u54cd\uff0c\u75be\u5982\u6d41\u661f\u5730\u98de\u4e86\u8fc7\u53bb\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u5927\u8239\u4e0a \u7adf\u6709\u4e09\u5f20\u4e3b\u5e06\u56e0\u6845\u6881\u88ab\u6298\uff0c\u843d\u4e86\u4e0b\u6765\uff0c\u90a3\u5927\u8239\u901f\u5ea6\u987f\u65f6\u4e00\u51cf\u2014\u2014 \u3000\u3000\u8f9b\u6377\u8fde\u5fd9\u8d81\u673a\u8fd0\u6868\u5982\u98de\uff0c\u8239\u884c\u5982\u7bad\u3002 \u3000\u3000\u4e00\u4e2a\u5927\u6d6a\u6380\u8d77\u5c0f\u821f\uff0c\u5c0f\u6222\u5c9b\u5df2\u6a21\u7cca\u5728\u671b\u2014\u2014 \u3000\u3000\u6ce2\u6d9b\u6c79\u6d8c\uff0c\u6d6a\u82b1\u62cd\u51fb\u5904\uff0c\u6fc0\u52a8\u6f2b\u5929\u6c34\u661f\u2014\u2014 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7ad9\u5728\u8239\u9996\uff0c\u771f\u529b\u704c\u6ce8\uff0c\u53cc\u8896\u4ea4\u76f8\u5f80\u540e\u62c2\u51fa\uff0c\u5185\u5bb6\u771f\u529b\u53d1\u51fa\uff0c\u518d\u52a0\u4e0a\u8f9b\u6377\u90a3\u6bcf\u4e00 \u4e0b\u90fd\u6709\u51e0\u767e\u65a4\u7684\u529b\u9053\u677f\u6868\uff0c\u8239\u66f4\u662f\u6709\u5982\u5929\u9a6c\u884c\u7a7a\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u65f6\u56de\u9996\u773a\u671b\uff0c\u679c\u7136\u90a3\u8258\u8239\u4ecd\u7136\u5728\u540e\u9762\u8ffd\uff0c\u9762\u4e14\u901f\u5ea6\u4e5f\u751a\u662f\u60ca\u4eba\uff0c\u5e76\u6ca1\u6709\u6108\u8ddd\u6108\u8fdc \u7684\u8d8b\u52bf\u3002 \u3000\u3000\u5c0f\u6222\u5c9b\u5df1\u7136\u5728\u671b\uff0c\u5e73\u51e1\u4e0a\u8981\u53cc\u8896\u731b\u7136\u4e00\u62c2\uff0c\u5c0f\u821f\u767b\u65f6\u5982\u8131\u5f26\u4e4b\u7bad\uff0c\u4e00\u51b2\u4e4b\u4e0b\uff0c\u8ddd\u5cb8\u8fb9\u4ec5 \u6709\u5341\u4e08\u5f00\u5916\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u84e6\u7136\u987a\u7740\u8239\u52bf\u4e00\u51b2\uff0c\u8eab\u4f53\u817e\u7a7a\u800c\u8d77\uff0c\u7531\u4e8e\u5c0f\u8239\u524d\u8fdb\u7684\u529b\u91cf\uff0c\u52a0\u4e0a\u5e73\u51e1\u4e0a\u4eba\u76d6\u4e16 \u8f7b\u529f\uff0c\u7adf\u4e00\u63a0\u5341\u4e08\u7f57\u521a\u521a\u843d\u8db3\u5728\u5cb8\u4e0a\uff0c\u8f9b\u6377\u4e00\u8bed\u4e0d\u53d1\uff0c\u64cd\u6868\u4e00\u677f\uff0c\u8f6c\u8fc7\u8239\u9996\u5411\u65e0\u6781\u5c9b\u79bb\u53bb\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u624d\u4e0a\u5f97\u5cb8\uff0c\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u8239\u4e5f\u5df1\u8feb\u8fd1\u4e86\uff0c\u5e73\u51e1\u4e0a\u4eba\u9a7b\u8db3\u9065\u671b\uff0c\u77a5\u89c1\u4e09\u4f5b\u5728\u8239 \u4e0a\uff0c\u7528\u5185\u529b\u50ac\u821f\uff0c\u95ea\u7535\u5373\u8fd1\uff0c\u4e00\u8d77\u6280\u4e0a\u5cb8\u8fb9\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6e05\u5578\u4e00\u58f0\uff0c\u543c\u9053\uff1a\u201c\u63a5\u62db\u2014\u2014\u201d \u3000\u3000\u4e58\u90a3\u4e09\u4f5b\u8eab\u5b50\u4ecd\u5728\u7a7a\u4e2d\uff0c\u5288\u7a7a\u4e00\u62f3\u72c2\u5288\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u6210\u52bf\u597d\u5927\uff0c\u53d1\u51fa\u7684\u52b2\u98ce\u76f4\u88ad\u4e09\u4eba\u3002\u4e09\u4eba\u8eab\u5728\u7a7a\u4e2d\uff0c\u771f\u6c14\u63d0\u4e0d\u4e0a\u6765\uff0c\u4e0d\u7531\u5927\u60ca\u9f50 \u547c\u3002 \u3000\u3000\u4e3a\u9996\u4e00\u4e2a\u62ab\u53d1\u5934\u9640\u6012\u53f1\u4e00\u58f0\uff0c\u731b\u529b\u4e00\u62f3\u7531\u4e0a\u538b\u51fb\u800c\u4e0b\uff0c\u5e73\u51e1\u4e0a\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u62f3\u5f0f\u7a81\u7136\u5168 \u6536\uff0c\u7b49\u90a3\u5934\u9640\u62db\u5f0f\u7528\u8001\uff0c\u5de6\u62f3\u95ea\u7535\u5288\u51fa\uff0c\u7adf\u7528\u7684\u662f\u201c\u70d8\u4e91\u6258\u6708\u201d\u786c\u6253\u786c\u6376\u4e4b\u5f0f\uff01 \u3000\u3000\u90a3\u5934\u9640\u4e00\u6765\u8eab\u5728\u7a7a\u4e2d\uff0c\u4e8c\u6765\u771f\u6c14\u5df2\u7136\u6df7\u6d4a\uff0c\u786c\u62c9\u4e4b\u4e0b\uff0c\u8eab\u4f53\u88ab\u5e73\u51e1\u4e0a\u4eba\u4e00\u51fb\u4e4b\u529b\u5e26\u5f97\u7ffb \u4e86\u4e00\u4e2a\u7b4b\u6597\uff0c\u5f80\u6d77\u5fc3\u76f4\u843d\u4e0b\u53bb\uff0c\u800c\u5e73\u51e1\u4e0a\u4eba\u5374\u7a33\u7acb\u6709\u82e5\u6cf0\u5c71\uff0c\u4ec5\u4ec5\u8863\u6289\u62c2\u8d77\u800c\u5df2\u3002 \u3000\u3000\u84e6\u5730\u91cc\uff0c\u8239\u4e2d\u98de\u51fa\u4e00\u5757\u8239\u677f\uff0c\u6b63\u843d\u5728\u90a3\u5934\u9640\u8db3\u4e0b\uff0c\u5934\u9640\u501f\u529b\u4e00\u8dc3\uff0c\u5df1\u843d\u5728\u5cb8\u4e0a\uff0c\u8239\u4e0a\u4e5f \u8ddf\u7740\u63a0\u51fa\u4e00\u4eba\uff0c\u770b\u90a3\u5112\u751f\u6253\u626e\uff0c\u6b63\u662f\u90a3\u5728\u65e0\u4e3a\u5385\u89c1\u8fc7\u7684\u91d1\u9c81\u5384\uff01 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e00\u51fb\u4e4b\u4e0b\uff0c\u632b\u4e86\u90a3\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u7684\u9996\u4e00\u4f4d\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u56de\u8eab\u63a0\u5f00\uff0c\u5f80\u5c9b\u5fc3\u5954 \u53bb\u3002 \u3000\u3000\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u5728\u201c\u5927\u6222\u5c9b\u201d\u4e0a\u89c1\u8fc7\u8fd9\u501f\u4eba\u7684\u529f\u592b\uff0c\u81ea\u77e5\u5355\u6253\u72ec\u6597\u81ea\u5df1\u4e09\u4eba\u90fd\u6ca1\u6709\u53d6\u80dc\u7684 \u628a\u63e1\uff0c\u4f46\u5982\u4eca\u56db\u4eba\u5728\u4e00\u8d77\uff0c\u4e0d\u518d\u8fdf\u7591\uff0c\u98de\u4e5f\u4f3c\u5f80\u5927\u6222\u5c9b\u4e3b\u5954\u53bb\u7684\u65b9\u5411\u731b\u8ffd\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u867d\u7136\u529f\u529b\u76d6\u4e16\uff0c\u4f46\u5bf9\u65b9\u4e09\u4eba\u7684\u529f\u529b\u5b9e\u5728\u592a\u5f3a\uff0c\u81ea\u77e5\u4ee5\u4e00\u654c\u4e09\u51b3\u65e0\u80dc\u7406\uff0c\u662f\u4ee5\u51fa \u4e3b\u610f\u5f15\u4e09\u4eba\u5230\u5c0f\u6222\u5c9b\u4e0a\uff0c\u60f3\u5408\u540c\u6167\u5927\u5e08\u8fde\u624b\u6297\u654c\uff0c\u5fc3\u601d\u4e00\u52a8\uff0c\u8eab\u4f53\u72c2\u7136\u4e00\u8f6c\uff0c\u5411\u90a3\u77f3\u6797\u6240\u5e03 \u7684\u201c\u5f52\u5143\u53e4\u9635\u201d\u7a9c\u53bb\u3002 \u3000\u3000\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u811a\u7a0b\u4e0d\u6162\uff0c\u5e73\u51e1\u5927\u5e08\u624d\u5165\u5f97\u77f3\u6797\uff0c\u4e09\u4eba\u4e5f\u5df1\u8ddf\u5230\uff0c\u6052\u6cb3\u4e09\u4f5b\u6b64\u6b21\u5165\u4e2d\u539f\u672c \u8981\u89c1\u89c1\u632b\u8d25\u81ea\u5df1\u4e24\u4e2a\u5f97\u610f\u95e8\u5f92\u7684\u5c11\u5e74\uff0c\u90a3\u77e5\u5374\u9022\u9ad8\u624b\uff0c\u5c24\u5176\u662f\u5e73\u51e1\u4e0a\u4eba\uff0c\u529f\u529b\u4f3c\u8fd8\u5728\u81ea\u5df1\u4e09 \u4eba\u4e4b\u4e0a\uff0c\u4e09\u4eba\u5fc3\u4e2d\u60f4\u7136\u4e0d\u670d\uff0c\u662f\u4ee5\u4e34\u65f6\u6539\u53d8\u8ba1\u5212\uff0c\u51b3\u5fc3\u548c\u5e73\u51e1\u4e0a\u4eba\u597d\u597d\u8f83\u91cf\u4e00\u756a\u3002 \u3000\u3000\u4e09\u4f5b\u7684\u89c1\u8bc6\u4e5f\u751a\u4e3a\u591a\u5e7f\uff0c\u4e00\u77a5\u4e4b\u4e0b\uff0c\u5df2\u77e5\u8fd9\u77f3\u6797\u6b63\u662f\u6240\u5e03\u7684\u4e00\u79cd\u4ec0\u4e48\u9635\u6cd5\uff0c\u4f46\u4e09\u4eba\u81ea\u89c6 \u6781\u9ad8\uff0c\u505a\u7136\u4e0d\u60e7\uff0c\u62a2\u6b65\u5165\u9635\uff0c\u8fde\u91d1\u9c81\u5384\u4e5f\u8ddf\u8fdb\u77f3\u6797\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u66fe\u88ab\u5c0f\u6222\u5c9b\u4e3b\u7528\u8fd9\u53e4\u9635\u56f0\u4e86\u8fd1\u5341\u5e74\uff0c\u8fd8\u662f\u8f9b\u633a\u6307\u793a\uff0c\u624d\u5f97\u4ee5\u51fa\u9635\uff0c\u8fd9\u5bf9\u4ed6\u7684\u5370 \u8c61\u53ef\u4ee5\u8bf4\u6781\u4e3a\u6df1\u523b\uff0c\u662f\u4ee5\u4e8b\u9694\u751a\u4e45\uff0c\u7adf\u4e5f\u80fd\u8bb0\u5f97\u8fd9\u53e4\u602a\u7684\u9635\u6cd5\uff0c\u5de6\u8f6c\u53f3\u5f2f\uff0c\u8f6c\u51fa\u9635\u672b\uff0c\u5374\u89c1 \u6052\u6cb3\u4e09\u4f5b\u4e00\u884c\u56db\u4eba\u5728\u53e4\u9635\u4e2d\u4e1c\u7ed5\u897f\u8f6c\uff0c\u663e\u7136\u5df1\u88ab\u56f0\u4f4f\uff0c\u4e0d\u7531\u54c8\u54c8\u5927\u7b11\u3002 \u3000\u3000\u8981\u77e5\u8fd9\u5f52\u5143\u53e4\u9635\u4ee5\u5e73\u51e1\u4e0a\u4eba\u53c2\u7985\u5341\u5e74\uff0c\u4ecd\u4e0d\u53ef\u89e3\uff0c\u6052\u6cb3\u4e09\u4f5b\u518d\u5927\u672c\u9886\uff0c\u4e5f\u81ea\u7136\u88ab\u56f0\u5728\u77f3 \u6797\u5185\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6709\u8fc7\u8fd9\u79cd\u7ecf\u9a8c\uff0c\u8fd9\u65f6\u89c1\u53c8\u6709\u4eba\u548c\u81ea\u5df1\u4e00\u6837\u88ab\u56f0\uff0c\u4ed6\u672c\u6027\u4e0d\u7f81\uff0c\u4e0d\u7531\u54c8\u54c8\u5927\u7b11\uff0c \u795e\u8272\u95f4\u5341\u5206\u6b23\u559c\u6ed1\u7a3d\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5f97\u610f\u4e86\u4e00\u9635\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u98de\u5feb\u5f80\u5c9b\u91cc\u5954\u53bb\uff0c\u60f3\u627e\u90a3\u5c0f\u6222\u5c9b\u4e3b\u6167\u5927\u5e08\u6765\u5bf9\u654c\u3002 \u3000\u3000\u5c0f\u6222\u5c9b\u65b9\u5706\u4e0d\u8fc7\u5341\u91cc\uff0c\u5e73\u51e1\u5927\u5e08\u6b64\u7b49\u811a\u7a0b\uff0c\u90a3\u6d88\u7247\u523b\uff0c\u4fbf\u6765\u5230\u5c9b\u5fc3\uff0c\u8fdb\u5165\u623f\u4e2d\uff0c\u5374\u4e0d\u89c1 \u4eba\u5f71\uff0c\u5c0f\u6222\u5c9b\u4e3b\u5206\u660e\u4e0d\u5728\u5c9b\u4e0a\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5927\u5931\u6240\u671b\uff0c\u53c8\u60ca\u53c8\u6025\u3002 \u3000\u3000\u987b\u77e5\u5e73\u51e1\u4e0a\u4eba\u867d\u7136\u4e3a\u4eba\u4e0d\u7f81\uff0c\u4f46\u597d\u80dc\u5fc3\u5374\u6781\u4e3a\u6d53\u539a\uff0c\u4ed6\u548c\u6167\u5927\u5e08\u4e3a\u4e89\u5f3a\u8d4c\u80dc\uff0c\u4e00\u56f0\u5c31\u662f \u5341\u5e74\uff0c\u7531\u6b64\u4fbf\u53ef\u89c1\u4e00\u6591\u3002\u4ed6\u5e73\u65e5\u603b\u4ee5\u4e3a\u81ea\u5df1\u529f\u529b\u662f\u76d6\u4e16\u65e0\u654c\uff0c\u800c\u73b0\u5728\u5374\u9022\u5f02\u90a6\u6765\u7684\u4e09\u4e2a\u9ad8 \u624b\uff0c\u8bf4\u5b9e\u8bdd\u529f\u592b\u7adf\u4e0d\u5728\u4ed6\u4e4b\u4e0b\uff0c\u800c\u4e14\u53e3\u53e3\u58f0\u58f0\u8981\u541e\u6ca1\u6574\u4e2a\u6b66\u6797\uff0c\u4ed6\u7684\u529f\u592b\u662f\u6765\u81ea\u4e2d\u539f\uff0c\u5c82\u80fd \u5bb9\u5f02\u90a6\u91ce\u4eba\u5982\u6b64\u72c2\u5984\uff0c\u51b5\u4e14\u4ed6\u8fd8\u662f\u4e00\u4e2a\u6781\u7aef\u597d\u80dc\u7684\u4eba\u54e9\uff01\u662f\u4ee5\u4ed6\u51fa\u8ba1\u60f3\u4f7f\u201c\u4e16\u5916\u4e09\u4ed9\u201d\u4e00\u8d77 \u548c\u90a3\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u6298\u6597\u4e00\u4e0b\u5b50\uff0c\u77a7\u77a7\u5230\u5e95\u662f\u4e2d\u539f\u6b66\u5b66\u80dc\u8fd8\u662f\u5f02\u57df\u5f3a\u3002 \u3000\u3000\u4ed6\u6709\u8fd9\u4e2a\u51b3\u5fc3\uff0c\u4f46\u504f\u6167\u5927\u5e08\u597d\u4f3c\u4e0d\u5728\u5c71\u4e0a\uff0c\u773c\u89c1\u81ea\u5df1\u4e00\u4eba\u51b3\u8ba1\u654c\u4e0d\u4f4f\u4e09\u4eba\uff0c\u5047\u82e5\u4ed6\u4e0d \u654c\uff0c\u4e16\u5916\u4e09\u4ed9\u5176\u4f59\u4e8c\u5b50\u5c31\u662f\u8054\u624b\u4e5f\u4e0d\u89c1\u5f97\u654c\u5f97\u4f4f\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\uff0c\u90a3\u4e48\u4e2d\u539f\u5c06\u6c38\u4e45\u6ca6\u4e3a\u5f02\u65cf\u4eba \u7684\u6b66\u529b\u4e0b\u4e86\uff01 \u3000\u3000\u4ed6\u53ef\u6ca1\u6709\u60f3\u5230\u81ea\u5df1\u53ef\u4ee5\u4e58\u4e09\u4f5b\u4ecd\u56f0\u5728\u9635\u4e2d\u65f6\u4e00\u8d70\u4e86\u4e4b\uff0c\u7686\u56e0\u4ed6\u4e43\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u6839\u672c\u60f3\u4e0d \u5230\u8fd9\u4e00\u5934\u4e0a\u6765\uff01 \u3000\u3000\u4ed6\u731b\u4e00\u987f\u8db3\uff0c\u53cd\u8eab\u4fbf\u8d70\u3002 \u3000\u3000\u84e6\u5730\u91cc\u2014\u2014 \u3000\u3000\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u50cf\u662f\u4ec0\u4e48\u91cd\u7269\u5760\u5730\uff0c\u9690\u9690\u7684\uff0c\u5c0f\u5c9b\u90fd\u6709\u4e00\u70b9\u9707\u52a8\u7684\u6837\u5b50\uff0c\u5e73\u51e1\u4e0a\u4eba\u5fae\u5403 \u4e00\u60ca\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5df2\u77e5\u5fc5\u662f\u6052\u6cb3\u4e09\u4f5b\u8d70\u4e0d\u51fa\u53e4\u9635\uff0c\u7adf\u7528\u86ee\u529b\u52a8\u624b\u63a8\u6298\u77f3\u7b0b\u3002 \u3000\u3000\u5f53\u5e74\u4ed6\u88ab\u56f0\u65f6\uff0c\u4e5f\u66fe\u60f3\u7528\u529b\u63a8\u65ad\u767e\u7b0b\uff0c\u4f46\u5fc3\u77e5\u5047\u82e5\u80fd\u63a8\u6298\u77f3\u6797\uff0c\u81ea\u5df1\u529f\u529b\u5fc5\u4f1a\u635f\u5931\u4e00\u7532 \u5b50\u4ee5\u4e0a\uff0c\u770b\u6765\u8fd9\u6052\u6cb3\u4e09\u4f5b\u7adf\u4e5f\u4f5c\u5982\u6b64\u6253\u7b97\u4e86\uff0c\u6700\u7cdf\u7684\u662f\u5bf9\u65b9\u6709\u4e09\u4e2a\u4eba\uff0c\u4eba\u5bb6\u4e09\u4eba\u8054\u624b\u9f50\u63a8\uff0c \u4e0d\u4f46\u53ef\u4ee5\u51cf\u5c11\u635f\u4f24\uff0c\u800c\u4e14\u53ef\u4ee5\u4e00\u4e00\u628a\u6b64\u53e4\u9635\u6bc1\u6389\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u8fd9\u53e4\u9635\u4e43\u662f\u6167\u5927\u5e08\u82b1\u4e86\u6781\u5927\u7684\u529b\u91cf\u624d\u5efa\u6210\uff0c\u81ea\u5df1\u81ea\u4ee5\u4e3a\u5999\u8ba1\u628a\u654c\u4eba\u5f15\u4eba\u53e4\u9635\uff0c\u5047 \u82e5\u56e0\u6b64\u6bc1\u9635\uff0c\u81ea\u5df1\u5b9e\u5728\u5bf9\u4e0d\u8d77\u5979\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u8eab\u4f53\u5df2\u817e\u7a7a\u63a0\u8fc7\u9635\u524d\u3002 \u3000\u3000\u9635\u4e2d\u6052\u6cb3\u4e09\u4f5b\u6b63\u51c6\u5907\u5408\u624b\u786c\u51fb\u7b2c\u4e8c\u6839\u77f3\u7b0b\uff0c\u5e73\u51e1\u5927\u5e08\u5fc3\u4e2d\u4e00\u6025\uff0c\u5927\u53f1\u9053\uff1a\u201c\u5582\uff0c\u6709\u672c\u9886 \u7684\u8d70\u51fa\u9635\u6765\u2014\u2014\u201d \u3000\u3000\u90a3\u4e09\u4f5b\u4e2d\u7b2c\u4e8c\u4f4d\uff0c\u4ea6\u5373\u662f\u91d1\u9c81\u5384\u7684\u5e08\u7236\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u4ee5\u4e3a\u8fd9\u9635\u6709\u4ec0\u4e48\u53e4\u602a\uff0c\u8001\u5b9e \u8bf4\uff0c\u8fd9\u6837\u4e00\u4e2a\u9635\uff0c\u6211\u4eec\u8fd8\u4e0d\u653e\u5b83\u5728\u773c\u5185\u54e9\u2014\u2014\u201d \u3000\u3000\u4ed6\u867d\u7136\u4f1a\u8bf4\u6c49\u8bed\uff0c\u4f46\u5374\u751f\u786c\uff0c\u4e0d\u82e5\u4ed6\u5f92\u513f\u91d1\u9c81\u5384\u7eaf\u719f\u3002 \u3000\u3000\u8bdd\u58f0\u5c1a\u672a\u843d\u4e0b\uff0c\u84e6\u5730\u91cc\u77f3\u6797\u540e\u9762\u4e00\u4e2a\u58f0\u97f3\u51b7\u51b7\u63a5\u53e3\u9053\uff1a\u201c\u597d\u5927\u7684\u53e3\u6c14\uff0c\u4f60\u5012\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u6052\u6cb3\u4e09\u4f5b\u5927\u5403\u4e00\u60ca\uff0c\u4ee5\u4ed6\u4eec\u81ea\u5df1\u7684\u529f\u592b\uff0c\u5341\u4e08\u4e4b\u5185\uff0c\u843d\u53f6\u53ef\u8fa8\uff0c\u8fd9\u4f1a\u513f\u7adf\u6709\u4eba\u65e0\u58f0\u65e0\u606f \u8d70\u6765\uff0c\u4ed6\u4eec\u4e0d\u80fd\u53d1\u89c9\uff0c\u8fd9\u4efd\u8f7b\u529f\uff0c\u53ef\u8bf4\u9a87\u95fb\u52a8\u542c\u7684\u4e86\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5fc3\u4e2d\u5df2\u77e5\u5fc5\u662f\u6167\u5927\u5e08\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u8001\u5c3c\u5a46\uff0c\u5feb\u6765\uff0c\u6765\u5f97\u6b63\u597d\u2014\u2014\u201d \u3000\u3000\u77f3\u6797\u540e\u6167\u5927\u5e08\u51b7\u7136\u4e00\u54fc\uff0c\u6052\u6cb3\u4e09\u4f5b\u84e6\u89c1\u773c\u524d\u4e00\u82b1\uff0c\u6167\u5927\u5e08\u5df2\u7531\u77f3\u6797\u540e\u63a0\u5230\u77f3\u6797\u524d\u548c\u5e73\u51e1 \u4e0a\u4eba\u76f8\u5bf9\u800c\u7acb\u3002 \u3000\u3000\u8fd9\u4e00\u4e2a\u8eab\u6cd5\u53e4\u602a\u5df2\u6781\uff0c\u4e09\u4f5b\u6ca1\u6709\u4eba\u80fd\u6e05\u695a\u770b\u89c1\u4eba\u5bb6\u662f\u600e\u4e48\u6837\u63a0\u8fc7\u7684\uff0c\u4e16\u95f4\u7adf\u6709\u8fd9\u6837\u7684\u8f7b \u529f\uff0c\u4e09\u4f5b\u90fd\u4e0d\u7531\u5927\u60ca\u5931\u8272\u3002\u5e73\u51e1\u4e0a\u4eba\u770b\u5728\u773c\u5185\uff0c\u6697\u7b11\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8001\u5c3c\u5a46\u8fd9\u4e00\u5957\u2018\u8bdd\u6469\u6b65\u6cd5\u2019\u679c\u7136\u4e3e\u4e16\u65e0\u53cc\uff0c\u795e\u5999\u65e0\u6bd4\uff0c\u83ab\u8bf4\u4f60\u6052\u6cb3\u4e09\u4f5b\uff0c\u5c31\u662f\u8001\u8872\u4e5f \u81ea\u53f9\u5f17\u5982\u54e9\uff01\u201d \u3000\u3000\u6167\u5927\u5e08\u63a0\u5230\u5e73\u51e1\u4e0a\u4eba\u9762\u524d\uff0c\u51b7\u7136\u9053\uff1a\u201c\u81ed\u548c\u5c1a\u53c8\u5230\u8fd9\u91cc\u5435\u95f9\u4f5c\u4ec0\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5fc3\u77e5\u8fd9\u5f53\u513f\u8fd8\u662f\u4e0d\u8981\u62c9\u4e0b\u8138\u6765\u4e3a\u5999\uff0c\u4e8e\u662f\u54c8\u54c8\u4e00\u7b11\uff0c\u4e00\u672c\u6b63\u7ecf\u9053\uff1a \u3000\u3000\u201c\u5e73\u65e5\u4f60\u8001\u5c3c\u5a46\u6df1\u5c45\u7b80\u51fa\uff0c\u4e0d\u95ee\u4e16\u4e8b\uff0c\u4eca\u65e5\u4f60\u5374\u975e\u51fa\u5934\u4e0d\u53ef\u2014\u2014\u201d\u8bf4\u7740\u4fbf\u628a\u6052\u6cb3\u4e09\u4f5b\u548c \u4e24\u4e2a\u5f92\u5f1f\u60f3\u4eba\u4e3b\u4e2d\u539f\u7684\u4e8b\u7565\u8ff0\u4e00\u8ff0\u3002 \u3000\u3000\u6167\u5927\u5e08\u89c1\u4ed6\u8bf4\u5f97\u8bda\u6073\uff0c\u4e00\u65f6\u5012\u4e0d\u597d\u4f5c\u7b54\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u5979\u8138\u4e0a\u72b9\u6709\u4e0d\u4fe1\u4e4b\u8272\uff0c\u5927\u6012\u5fd6\u9053\uff1a\u201c\u6211\u5e73\u51e1\u4e0a\u4eba\u4e00\u751f\u4e0d\u6c42\u4eba\uff0c\u4eca\u65e5\u4e3a\u987e\u5168\u5927 \u5c40\u5fcd\u6c14\u541e\u58f0\uff0c\u4f60\u4e0d\u7b54\u5e94\u4e5f\u7f62\uff0c\u7adf\u597d\u4f3c\u4e0d\u80fd\u7f6e\u4fe1\uff0c\u96be\u9053\u6211\u5e73\u51e1\u4e0a\u4eba\u7adf\u8981\u6765\u9a97\u4f60\uff1f\u201d\u4ed6\u8d8a\u60f3\u8d8a \u6c14\uff0c\u800c\u6167\u5927\u5e08\u4ecd\u7136\u4e0d\u80fd\u4f5c\u51b3\u5b9a\uff0c\u51b7\u7136\u4e00\u54fc\uff0c\u53f1\u9053\uff1a\u201c\u4e0d\u60f3\u5c0f\u6222\u5c9b\u4e3b\u7adf\u662f\u6015\u4e8b\u7684\u4eba\u3002\u201d \u3000\u3000\u6167\u5927\u5e08\u6012\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u6015\u4e8b\u4e86\uff1f\u8fd9\u5012\u8981\u8ffd\u7a76\u8ffd\u7a76\uff01\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u51b7\u51b7\u6fc0\u9053\uff1a\u201c\u4eba\u5bb6\u903c\u5230\u54b1\u4eec\u2018\u4e16\u5916\u4e09\u4ed9\u2019\u5934\u4e0a\uff0c\u4f60\u4ecd\u7136\u4e0d\u6562\u51fa\u624b\u2014\u2014\u201d \u3000\u3000\u6167\u5927\u5e08\u600e\u4e0d\u660e\u767d\u4ed6\u7528\u610f\u5728\u6fc0\u5c06\uff0c\u4ec5\u4ec5\u51b7\u54fc\u4e00\u58f0\uff0c\u6253\u65ad\u4ed6\u7684\u8bdd\u5934\u3002\u5e73\u51e1\u4e0a\u4eba\u6fc0\u5c06\u4e4b\u8ba1\u4e0d \u6210\uff0c\u53cd\u88ab\u6167\u5927\u5e08\u51b7\u7136\u5632\u7b11\uff0c\u4e0d\u7531\u6709\u70b9\u8001\u7f9e\u6210\u6012\u7684\u6837\u5b50\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u9053\u6211\u5e73\u51e1\u4e0a\u4eba\u662f\u6253\u4e0d\u8fc7 \u4eba\u5bb6\u624d\u6765\u8bf7\u4f60\u81ed\u5c3c\u59d1\uff1f\u201d \u3000\u3000\u6167\u5927\u5e08\u9053\uff1a\u201c\u4f60\u65e2\u6253\u5f97\u8fc7\u4f55\u4e0d\u4e00\u4eba\u53bb\u62b5\u654c\uff1f\u201d \u3000\u3000\u5979\u987f\u4e86\u987f\u53c8\u9053\uff1a\u201c\u4ee5\u521a\u624d\u4ed6\u4eec\u4e09\u4eba\u63a8\u77f3\u7b0b\u7684\u529f\u529b\uff0c\u4f60\u80fd\u4ee5\u4e00\u654c\u4e09\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6697\u6697\u5fd6\u9053\uff1a\u201c\u679c\u7136\u4e0d\u654c\u3002\u201d\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u654c\u4e0d\u8fc7\u53c8\u600e\u4e48\u6837\uff0c\u8001\u5c3c\u5a46\u4e0d\u51fa\u624b\uff0c\u6211 \u81ea\u53bb\u627e\u65e0\u6068\u751f\u53bb\u3002\u201d\u8bf4\u7740\u8f6c\u8eab\u4f5c\u52bf\u4fbf\u8981\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u6167\u5927\u5e08\u7a81\u9053\uff0c\u201c\u4e14\u6162\u2014\u2014\u201d\u7b49\u5e73\u51e1\u4e0a\u4eba\u8f6c\u8fc7\u8eab\u6765\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u81ed\u548c\u5c1a\uff0c\u4f60\u4ee5\u524d\u9017\u6012\u8d2b\u5c3c \u548c\u8d2b\u5c3c\u6253\u95f9\u7684\u4e8b\uff0c\u54b1\u4eec\u4ee5\u540e\u4e00\u6982\u4e0d\u8bb0\u2014\u2014\u201d\u5979\u8bf4\u5f97\u5f88\u6162\uff0c\u4f46\u8bed\u6c14\u65a9\u94c1\u65ad\u9489\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5df2\u77e5\u5979\u5df2\u7b54\u5e94\uff0c\u4e5f\u7f13\u7f13\u5fae\u7b11\u9053\uff1a\u201c\u8001\u5c3c\u5a46\u56f0\u6211\u5341\u5e74\uff0c\u54b1\u4eec\u8fd9\u7b14\u5e10\u4e00\u7b14\u52fe\u6d88\u2014\u2014 \u201d \u3000\u3000\u6167\u5927\u5e08\u500f\u7136\u4f38\u51fa\u624b\u6765\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e00\u6014\uff0c\u968f\u5373\u4f1a\u610f\uff0c\u4f38\u51fa\u53f3\u624b\u8f7b\u8f7b\u5728\u5979\u624b\u4e0a\u62cd\uff0c\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\u2014\u2014\u201d \u3000\u3000\u6167\u5927\u5e08\u8f7b\u58f0\u9053\uff1a\u201c\u5feb\u9a6c\u4e00\u97ad\uff01\u201d \u3000\u3000\u5728\u77f3\u9635\u4e2d\u7684\u6853\u6cb3\u4e09\u4f5b\u89c1\u4e24\u4eba\u4e00\u8a00\u4e00\u53e5\uff0c\u8c08\u4e2a\u4e0d\u4e86\uff0c\u6839\u672c\u6ca1\u6709\u628a\u81ea\u5df1\u653e\u5927\u773c\u5185\uff0c\u4e0d\u7531\u5927 \u6012\uff0c\u91d1\u9c81\u5384\u53f1\u9053\uff1a\u201c\u5582\uff0c\u4f60\u4eec\u4ee5\u4e3a\u6211\u4eec\u51fa\u4e0d\u4e86\u8fd9\u9635\u513f\uff1f\u201d \u3000\u3000\u6167\u5927\u5e08\u50b2\u7136\u6839\u672c\u4e0d\u4e88\u4ee5\u7406\u4f1a\u3002 \u3000\u3000\u5012\u662f\u5e73\u51e1\u4e0a\u4eba\u7b54\u9053\uff1a\u201c\u662f\u53c8\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u6052\u6cb3\u4e09\u4f5b\u4e2d\u7684\u4e8c\u4f5b\uff0c\u4ea6\u5373\u662f\u91d1\u9c81\u5384\u7684\u5e08\u7236\u9053\uff1a\u201c\u6211\u4eec\u628a\u4f60\u8fd9\u77f3\u5934\u5c3d\u6570\u63a8\u7ffb\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u51b7\u7136\u9053\uff1a\u201c\u4f60\u5012\u8bd5\u8bd5\u770b\u3002\u201d \u3000\u3000\u4e09\u4f5b\u4e0d\u518d\u5ba2\u6c14\uff0c\u731b\u7136\u5410\u6c14\u5f00\u58f0\uff0c\u9f50\u51fa\u4e00\u5802\uff0c\u671d\u9762\u524d\u90a3\u4e00\u682a\u77f3\u7b0b\u63a8\u53bb\u3002\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\u5e73 \u51e1\u4e0a\u4eba\u4e00\u62f3\u51fa\u624b\uff0c\u4e5f\u5288\u51fa\u4e00\u638c\u3002 \u3000\u3000\u8fd9\u4e00\u638c\u7528\u7684\u4e43\u662f\u767e\u6b65\u795e\u62f3\u7684\u529f\u592b\uff0c\u5e73\u51e1\u5927\u5e08\u867d\u662f\u865a\u865a\u4e00\u6363\uff0c\u529b\u9053\u548c\u52b2\u98ce\u5df1\u662f\u6392\u7a7a\u51fb\u51fa\u3002 \u3000\u3000\u6052\u6cb3\u4e09\u4f5b\u51b7\u54fc\u4e00\u58f0\uff0c\u9f50\u628a\u529b\u9053\u8f6c\u5411\u5e73\u51e1\u5927\u5e08\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u84e6\u7136\u4e00\u6536\uff0c\u4ed6\u5185\u529b\u9020\u8be3\u9ad8\u6781\uff0c\u529b\u9053\u6536\u53d1\u81ea\u5982\uff0c\u4e00\u6536\u4e4b\u540e\uff0c\u771f\u529b\u5168\u6cc4\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8fd9\u4e00\u62f3\u662f\u6545\u610f\u6253\u5411\u4e00\u5757\u7a7a\u5730\uff0c\u5e76\u6ca1\u6709\u77f3\u7b0b\uff0c\u4ed6\u7a81\u7684\u4e00\u6536\u62db\u5f0f\uff0c\u4e09\u4f5b\u6536\u62db\u4e0d\u53ca\uff0c \u6253\u5728\u5730\u4e0b\u3002 \u3000\u3000\u4e09\u4eba\u8fd9\u4e00\u5f0f\u597d\u5927\u5a01\u529b\uff0c\u90a3\u4e48\u5e72\u786c\u7684\u571f\u5730\uff0c\u7adf\u88ab\u51fb\u51fa\u4e00\u4e2a\u6d45\u6d45\u7684\u571f\u5751\uff0c\u5c0f\u5757\u5c0f\u5757\u7684\u6ce5\u571f\u6f2b \u5929\u98de\u821e\u7740\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u54c8\u54c8\u957f\u7b11\uff0c\u5939\u6742\u7740\u6052\u6cb3\u4e09\u4f5b\u66b4\u6012\u7684\u5316\u58f0\u54cd\u7740\u4e00\u56e2\uff0c\u663e\u5f97\u8fd9\u60c5\u52bf\u5341\u5206\u6df7\u4e71\u7684\u6a21 \u6837\u3002 \u3000\u3000\u6167\u5927\u5e08\u5230\u5e95\u6709\u70b9\u53ef\u60dc\u81ea\u5df1\u5fc3\u8840\u4ea4\u96c6\u7684\u5f52\u5143\u53e4\u9635\uff0c\u84e6\u7136\u8eab\u5b50\u4e00\u52a8\uff0c\u7a9c\u4e0a\u77f3\u7b0b\u7684\u9876\u5c16\u4e0a\uff0c\u51b7 \u51b7\u9053\uff1a\u201c\u4e0a\u6765\u5427\u3002\u201d \u3000\u3000\u6052\u6cb3\u4e09\u4f5b\u5fc3\u4e2d\u6012\u706b\u4e0a\u81ba\uff0c\u987e\u4e0d\u5f97\uff0c\u4e00\u9f50\u7eb5\u4e0a\u77f3\u7b0b\uff0c\u53ea\u6709\u91d1\u9c81\u5384\u7559\u5728\u5730\u4e0a\uff0c\u540c\u65f6\u95f4\u5e73\u51e1\u4e0a \u4eba\u4e5f\u843d\u5728\u77f3\u7b0b\u5c16\uff0c\u5bf9\u6052\u6cb3\u4e09\u4f5b\u53eb\u9053\uff1a\u201c\u54b1\u4eec\u5c31\u5728\u8fd9\u77f3\u7b0b\u4e0a\u51b3\u80dc\u8d1f\u5427\uff01\u201d \u3000\u3000\u201c\u6853\u6cb3\u4e09\u4f5b\u201d\u5df2\u662f\u6012\u706b\u4e2d\u70e7\uff0c\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u6253\u5728\u4e00\u56e2\u3002 \u3000\u3000\u4e14\u8bf4\u8f9b\u6377\u522b\u8fc7\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u4ee5\u540e\uff0c\u5168\u901f\u8d76\u5f80\u65e0\u6781\u5c9b\uff0c\u65e0\u6781\u5c9b\u8ddd\u5c0f\u6222\u5c9b\u4e0d\u8fdc\uff0c\u4ec5\u6709\u4e94 \u516d\u4e2a\u65f6\u8fb0\u7684\u8def\u7a0b\uff0c\u8f9b\u6377\u5fc3\u77e5\u4e8b\u6025\uff0c\u6709\u5173\u4e2d\u539f\u6b66\u6797\u540d\u8a89\uff0c\u66f4\u662f\u5168\u529b\u4f7f\u4e3a\u3002 \u3000\u3000\u5929\u6c14\u5f88\u597d\uff0c\u592a\u9633\u666e\u5c04\uff0c\u6620\u5728\u6d77\u6c34\u4e0a\uff0c\u95ea\u95ea\u8000\u8d77\u7247\u7247\u91d1\u5149\uff0c\u5f88\u8fdc\u5f88\u8fdc\u7684\u5929\u8fb9\u548c\u6d77\u76f8\u8fde\uff0c\u851a \u84dd\u8272\u7684\u4e00\u7247\uff0c\u5e73\u9759\u800c\u5b89\u8be6\u3002\u5076\u800c\u6d77\u98ce\u62c2\u8fc7\uff0c\u5e26\u8fc7\u6d77\u6c34\u6210\u54b8\u7684\u5473\u9053\uff0c\u8f9b\u6377\u8fd9\u8258\u72ec\u6845\u821f\u5728\u987a\u98ce\u4e0b \u9971\u5f20\u4e09\u89d2\u5e06\uff0c\u98de\u5feb\u7684\u9a70\u7740\u3002 \u3000\u3000\u6ce2\u6d9b\u5e76\u4e0d\u5927\uff0c\u6ca1\u6709\u6c79\u6d8c\u7684\u6837\u5b50\uff0c\u53ea\u6709\u5fae\u98ce\u62c2\u8fc7\uff0c\u5fae\u5fae\u7684\u8d77\u4f0f\uff0c\u4e00\u4e2a\u4e00\u4e2a\u6d6a\u513f\u8ffd\u9010\u800c\u53bb\u3002 \u3000\u3000\u6d77\u6c34\u662f\u6df1\u84dd\u8272\uff0c\u5929\u7a7a\u662f\u6d45\u84dd\u8272\uff0c\u4e24\u8fb9\u56db\u5cb8\u5168\u6ca1\u6709\u8fb9\u9645\uff0c\u518d\u52a0\u4e0a\u6674\u7a7a\u4e00\u78a7\uff0c\u4e07\u91cc\u65e0\u4e91\uff0c\u4ee4 \u4eba\u6709\u7740\u6781\u5176\u8fbd\u9614\u7684\u611f\u89c9\u3002 \u3000\u3000\u5076\u800c\u4e00\u4e8c\u53ea\u7eaf\u767d\u7684\u6d77\u9e25\u6bd4\u7ffc\u800c\u98de\uff0c\u5b89\u8be6\u800c\u66fc\u5999\u7684\u98de\u884c\u59ff\u6001\uff0c\u900f\u51fa\u90a3\u4e48\u6837\u5b50\u7684\u548c\u5e73\u6c14\u6c1b\u3002 \u3000\u3000\u6d77\u4e0a\u70df\u6ce2\u6d69\u6e3a\uff0c\u8f9b\u6377\u6bcf\u4e00\u6868\u6709\u529b\u7684\u62cd\u5728\u6c34\u4e0a\uff0c\u4f7f\u5f97\u8239\u884c\u6709\u5982\u8bf4\u5f26\u4e4b\u7bad\uff0c\u52a0\u4e4b\u987a\u98ce\u800c\u9a70\uff0c \u66f4\u662f\u6709\u5982\u5feb\u9a6c\u52a0\u97ad\u3002 \u3000\u3000\u6e10\u6e10\u7684\u5728\u90a3\u9065\u8fdc\u7684\u5929\u8fb9\uff0c\u51fa\u73b0\u90a3\u4e48\u4e00\u7ebf\u6de1\u6de1\u7684\u7070\u5f71\uff0c\u6562\u60c5\u662f\u65e0\u6781\u5c9b\u5df2\u662f\u5728\u671b\u4e86\u3002 \u3000\u3000\u6d77\u5916\u4e09\u4ed9\uff0c\u5927\u6222\u5c9b\u9996\uff0c\u5c0f\u6222\u5c45\u6b21\uff0c\u65e0\u6781\u65ad\u540e\uff0c\u800c\u4ee5\u65e0\u6781\u5c9b\u6700\u4e3a\u9614\u5927\uff0c\u800c\u4e14\u5730\u4f4d\u4e5f\u6700\u9002 \u4e2d\uff0c\u6b63\u597d\u548c\u5927\u3001\u5c0f\u6218\u5c9b\u6210\u7b49\u8170\u4e09\u89d2\u5f62\u800c\u5c45\u4e2d\u3002 \u3000\u3000\u6162\u6162\u7684\uff0c\u6108\u6765\u6108\u8fd1\u4e86\uff0c\u8f9b\u6377\u5df2\u53ef\u4ee5\u6e05\u6670\u7684\u770b\u6e05\u5c9b\u4e0a\u7684\u4e00\u5207\uff0c\u8239\u884c\u6e10\u6162\uff0c\u6ce2\u6d9b\u4e5f\u6e10\u5927\uff0c\u6562 \u60c5\u662f\u9760\u5cb8\u7684\u539f\u6545\u3002 \u3000\u3000\u5c9b\u4e0a\u4e24\u8fb9\u5168\u662f\u6811\u6728\uff0c\u800c\u4e14\u663e\u7136\u662f\u6709\u4eba\u5de5\u79cd\u690d\uff0c\u90a3\u4e9b\u6811\u6728\u90fd\u957f\u5f97\u5341\u5206\u6574\u9f50\uff0c\u7b14\u76f4\u7684\u5939\u51fa\u4e00 \u6761\u89d2\u9053\u3002 \u3000\u3000\u4e0a\u5f97\u5cb8\u6765\uff0c\u5cb8\u8fb9\u90fd\u662f\u6c99\u6ee9\uff0c\u6ce2\u6d6a\u5728\u5cb8\u8fb9\u603b\u6bd4\u5728\u6d77\u4e2d\u95f4\u8981\u5927\u5f97\u591a\uff0c\u51b2\u51fb\u5728\u5cb8\u4e0a\uff0c\u7531\u4e8e\u662f\u6c99 \u5b50\u7684\u539f\u6545\uff0c\u53d1\u51fa\u201c\u5494\u201d\u201c\u5bdf\u201d\u7684\u58f0\u97f3\uff0c\u4f4e\u6c89\u800c\u60a6\u8033\u3002 \u3000\u3000\u6c99\u6ee9\u5f62\u6210\u4e00\u4e2a\u659c\u659c\u7684\u5761\u513f\uff0c\u722c\u4e0a\u5761\u513f\uff0c\u90a3\u5730\u9762\u4e0a\u90fd\u94fa\u7740\u4e00\u5c42\u7ec6\u5c0f\u7684\u77f3\u5b50\uff0c\u4ee5\u514d\u96e8\u6c34\u51b2 \u79ef\uff0c\u4f7f\u8def\u9762\u584c\u4e0b\u5761\u513f\u53bb\u3002 \u3000\u3000\u987a\u7740\u5939\u836b\u7684\u6811\u513f\u5f80\u524d\u8d70\uff0c\u7ea6\u83ab\u76cf\u8336\u65f6\u523b\uff0c\u8f9b\u6377\u4e0d\u6562\u5728\u65e0\u6781\u5c9b\u4e0a\u4f7f\u5c55\u8f7b\u529f\uff0c\u4ec5\u7f13\u7f13\u7684\u884c \u8d70\u3002 \u3000\u3000\u8d70\u5230\u89d2\u9053\u5c3d\u5934\uff0c\u5411\u5de6\u8f6c\u4e00\u4e2a\u5f2f\uff0c\u5939\u9053\u7684\u6811\u6728\u8f83\u4e3a\u7a00\u6563\uff0c\u4f46\u662f\u6811\u4e0e\u6811\u4e4b\u95f4\u5374\u957f\u6ee1\u4e86\u4e9b\u91ce\u82b1 \u91ce\u8349\uff0c\u8fdc\u770b\u8fc7\u53bb\uff0c\u7ea2\u7ea2\u9ec4\u9ec4\u4e00\u5927\u7247\uff0c\u8335\u8335\u800c\u53ef\u7231\u3002 \u3000\u3000\u8f9b\u6377\u4e5f\u66fe\u5230\u8fc7\u5927\u3001\u5c0f\u6222\u5c9b\uff0c\u4e24\u4e2a\u5c9b\u7684\u5c9b\u4e3b\u867d\u90fd\u662f\u76d6\u4e16\u5947\u4eba\uff0c\u4f46\u5c9b\u4e0a\u5e03\u7f6e\u5374\u90fd\u7b80\u964b\u4e0d\u582a\uff0c \u5c0f\u6222\u5c9b\u4e0a\u5149\u79c3\u79c3\u7684\uff0c\u5927\u6222\u5c9b\u4e0a\u66f4\u662f\u4e71\u4e03\u516b\u7cdf\uff0c\u90fd\u8fdc\u4e0d\u53ca\u65e0\u6781\u5c9b\u3002 \u3000\u3000\u773c\u524d\u7eff\u836b\u4e3a\u76d6\uff0c\u82b3\u8349\u4e3a\u8335\uff0c\u5c24\u5176\u662f\u4ece\u53ea\u6709\u5355\u8c03\u84dd\u8272\u7684\u6d77\u4e2d\u4e0a\u6765\uff0c\u76ca\u53d1\u89c9\u5f97\u4e94\u5149\u5341\u8272\uff0c\u7f8e \u4e0d\u80dc\u6536\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u5f88\u5927\uff0c\u5149\u5c31\u662f\u8fd9\u4e00\u6761\u9053\u8def\u5c31\u6709\u4e00\u91cc\u591a\u957f\uff0c\u8d70\u5230\u5c3d\u5934\uff0c\u4fbf\u89c1\u5c4b\u89d2\uff0c\u6562\u60c5\u8fd9\u624d\u7b97\u5230\u4e86 \u65e0\u6781\u5c9b\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u7565\u6574\u8863\u51a0\uff0c\u9ad8\u58f0\u5411\u90a3\u5c4b\u5b50\u53eb\u9053\uff1a \u3000\u3000\u201c\u665a\u8f88\u8f9b\u6377\uff0c\u62dc\u89c1\u65e0\u6068\u751f\u524d\u8f88\uff0c\u5e76\u6709\u6025\u4e8b\u76f8\u6c42\u2014\u2014\u201d \u3000\u3000\u5c4b\u4e2d\u4e00\u7247\u6c89\u9759\uff0c\u9759\u6084\u6084\u50cf\u662f\u6839\u672c\u6ca1\u6709\u4eba\u5728\u7684\u6a21\u6837\u3002 \u3000\u3000\u8f9b\u6377\u8bd5\u7740\u5f80\u91cc\u9762\u8d70\uff0c\u8f6c\u8fc7\u90a3\u4e00\u4e1b\u82b1\u6811\uff0c\u5230\u8fbe\u5c4b\u524d\uff0c\u5ffd\u7136\u773c\u524d\u4e00\u82b1\uff0c\u4e0d\u7531\u5f97\u60ca\u53f9\u51fa\u58f0\u3002 \u3000\u3000\u539f\u6765\u8fd9\u65e0\u6781\u5c9b\u4e3b\u751f\u6027\u98ce\u96c5\uff0c\u867d\u7136\u9690\u5c45\u5728\u6d77\u5916\uff0c\u5374\u82b1\u8d39\u8bb8\u591a\u5fc3\u529b\u5e03\u7f6e\u65e0\u6781\u5c9b\uff0c\u4f4f\u5c4b\u5b8c\u5168\u91c7 \u7528\u4e16\u5916\u9ad8\u4eba\u7684\u6d01\u6734\uff0c\u8f9b\u6377\u4e00\u8def\u884c\u6765\uff0c\u6240\u89c1\u7684\u5c3d\u662f\u82b1\u8349\u6811\u6728\uff0c\u76ee\u4e0d\u6687\u89c6\uff0c\u8d70\u5165\u5c9b\u5fc3\uff0c\u773c\u524d\u666f\u7269 \u5374\u662f\u4e00\u53d8\u3002 \u3000\u3000\u4f46\u89c1\u4e00\u5e62\u53e4\u6734\u7684\u623f\u5b50\uff0c\u56db\u5468\u5e76\u6ca1\u6709\u79cd\u690d\u5947\u5f02\u82b1\u6728\uff0c\u5374\u56f4\u7740\u957f\uff0c\u4e86\u4e00\u5757\u7eff\u6cb9\u6cb9\u7684\u8349\u76ae\uff0c\u4e00 \u6761\u5c0f\u5f84\u901a\u51fa\u6765\uff0c\u901a\u5230\u548c\u5927\u8def\u8fde\u5728\u4e00\u8d77\uff0c\u5c0f\u5f84\u94fa\u5f97\u5e73\u5766\u5df2\u6781\uff0c\u5f2f\u5f2f\u66f2\u66f2\u7684\u3002 \u3000\u3000\u4e1c\u9996\u6709\u4e00\u6761\u5c0f\u6cb3\uff0c\u5927\u7ea6\u662f\u5f15\u53d6\u6d77\u6c34\u5bfc\u81f4\u7684\uff0c\u4e00\u773c\u671b\u53bb\uff0c\u4fbf\u77e5\u662f\u4eba\u5de5\u5f00\u8f9f\uff0c\u6cb3\u9762\u4ec5\u4ec5\u5bbd\u7ea6 \u4e00\u4e8c\u4e08\uff0c\u6cb3\u6c34\u6d41\u52a8\u5f97\u5f88\u7f13\uff0c\u4e2d\u95f4\u8fd8\u6709\u4e00\u8c61\u5f81\u6027\u7684\u6865\u3002 \u3000\u3000\u53e4\u96c5\u800c\u5145\u6ee1\u7740\u7f8e\u611f\uff0c\u6c14\u6c1b\u975e\u5e38\u6e05\u4e3d\uff0c\u8f9b\u6377\u987f\u65f6\u611f\u89c9\u5fc3\u795e\u4e00\u7545\uff0c\u795e\u667a\u6709\u4e00\u79cd\u6e05\u51c9\u7684\u611f\u89c9\u3002 \u3000\u3000\u8fd9\u513f\u79bb\u6d77\u5df2\u662f\u5f88\u8fdc\u4e86\uff0c\u542c\u4e0d\u5230\u6d77\u6c34\u51b2\u51fb\u7684\u58f0\u97f3\u3002 \u3000\u3000\u5468\u906d\u5f88\u5929\u7136\uff0c\u50cf\u662f\u6ca1\u6709\u4eba\u7684\u6837\u5b50\u3002 \u3000\u3000\u8f9b\u6377\u6e10\u6e10\u6c89\u9189\u4e86\uff0c\u4ed6\u81ea\u5c0f\u53d7\u76d6\u4e16\u624d\u4eba\u6885\u5c71\u6c11\u85b0\u9676\uff0c\u5df1\u7ecf\u81ea\u7136\u517b\u6210\u4e00\u79cd\u9ad8\u6df1\u7684\u827a\u672f\u611f\uff0c\u5bf9 \u4e8e\u8fd9\u79cd\u6734\u5b9e\u7684\u6c14\u6c1b\uff0c\u66f4\u52a0\u611f\u5230\u771f\u5b9e\u548c\u96be\u4e3a\u2014\u2014 \u3000\u3000\u84e6\u7136\u8033\u8fb9\u54cd\u8d77\u4e00\u9635\u58f0\u97f3\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u7adf\u5230\u5c9b\u4e0a\u6765\u4e86\u2014\u2014\u201d \u3000\u3000\u8f9b\u633a\u95ea\u7535\u822c\u56de\u8eab\u4e00\u8f6c\uff0c\u77a5\u76ee\u4e4b\u4e0b\uff0c\u7adf\u662f\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u3002 \u3000\u3000\u8f9b\u633a\u6b64\u884c\u5168\u662f\u4e3a\u4e86\u8981\u8bf7\u4ed6\u51fa\u624b\uff0c\u8fd9\u65f6\u89c1\u5230\u4ed6\uff0c\u4e0d\u7531\u5927\u559c\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u90a3\u5929\u6068\u751f\u51b7\u7136\u9053\uff1a \u201c\u5f20\u83c1\u5462\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6014\u5f97\u4e00\u6014\uff0c\u7b54\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u65e0\u6068\u751f\u5389\u58f0\u9053\uff1a\u201c\u4f60\u2014\u2014\u4f60\u2014\u2014\u54fc\u54fc\uff01\u201d \u3000\u3000\u60f3\u662f\u4ed6\u6c14\u6124\u5df2\u6781\uff0c\u8bdd\u90fd\u8bb2\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u72b6\u5fc3\u5934\u4e00\u6014\uff0c\u5450\u5450\u9053\uff1a\u201c\u5f20\u83c1\uff0c\u4f60\u662f\u8bf4\u4ee4\u63f4\uff1f\u201d \u3000\u3000\u65e0\u6839\u751f\u8138\u8272\u94c1\u9752\uff0c\u7528\u529b\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u4e00\u9707\uff0c\u6025\u58f0\u9053\uff1a\u201c\u5979\u4e0d\u5728\u5c9b\u4e0a\u5417\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u51b7\u7136\u9053\uff1a\u201c\u5979\uff0c\u5979\u5728\u5341\u5929\u524d\u5435\u7740\u8981\u53bb\u627e\u4f60\uff0c\u563f\uff0c\u4e00\u53bb\u4e0d\u8fd4\u2014\u2014\u201d\u8f9b\u6377\u542c\u5230\u90a3\u5f20\u83c1 \u7adf\u5343\u91cc\u8fe2\u8fe2\u53bb\u627e\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u6709\u4e00\u70b9\u751c\u751c\u7684\u611f\u89c9\uff0c\u4f46\u8f6c\u5ff5\u60f3\u5230\u5f20\u83c1\u4e00\u4e2a\u4eba\u6beb\u65e0\u6c5f\u6e56\u7ecf\u9a8c\uff0c \u884c\u9053\u6c5f\u6e56\u4e0d\u77e5\u8981\u95f9\u51fa\u597d\u5927\u9ebb\u70e6\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u51e0\u5929\u6d41\u6d6a\u5728\u6d77\u5916\uff0c\u5979\u53bb\u4ec0\u4e48\u5730 \u65b9\u627e\u54e6\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u9053\uff1a\u201c\u5979\u8bf4\u662f\u5230\u4e2d\u539f\u53bb\u4e86\uff0c\u5509\uff0c\u83c1\u513f\u5e74\u5e7c\u65e0\u77e5\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u62a2\u7740\u9053\uff1a\u201c\u8fd9\u4e2a\u665a\u8f88\u4e5f\u66fe\u60f3\u5230\uff0c\u53ea\u662f\u665a\u8f88\u73b0\u5728\u6709\u7d27\u6025\u4e4b\u4e8b\u5728\u8eab\uff0c\u7b49\u6b64\u4e8b\u4e00\u4e86\uff0c\u7acb\u523b \u8e0f\u904d\u795e\u5dde\uff0c\u627e\u56de\u4ee4\u5a9b\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u89c1\u8f9b\u6377\u597d\u4f3c\u5e76\u4e0d\u7126\u6025\u81ea\u5df1\u5973\u513f\u5931\u8e2a\u7684\u4e8b\uff0c\u7adf\u8fd8\u8981\u5728\u529e\u5b8c\u4ec0\u4e48\u4e8b\u4ee5\u540e\u53bb\u627e\u5979\uff0c\u770b\u6765 \u4ed6\u5bf9\u83c1\u513f\u662f\u6beb\u4e0d\u5173\u5fc3\uff0c\u5012\u662f\u83c1\u513f\u81ea\u4f5c\u591a\u60c5\u3002 \u3000\u3000\u4ed6\u8d8a\u60f3\u8d8a\u662f\u6c14\u6124\uff0c\u5fcd\u4e0d\u4f4f\u6012\u53f1\u4e00\u58f0\uff0c\u5c31\u60f3\u51fa\u624b\u4e00\u62f3\u628a\u8f9b\u6377\u6253\u6b7b\u3002\u7a81\u7136\u4e00\u4e2a\u5ff5\u5934\u4e00\u8f6c\uff0c\u5fd6 \u9053\uff1a\u201c\u83c1\u513f\u60f3\u662f\u5bf9\u4ed6\u60c5\u6df1\u610f\u91cd\uff0c\u5047\u82e5\u6211\u73b0\u5728\u5c31\u628a\u4ed6\u6253\u6b7b\uff0c\u83c1\u513f\u8fd9\u4e00\u8f88\u5b50\u90fd\u8981\u548c\u6211\u8fc7\u4e0d\u53bb\uff0c\u8fd9 \u4e8b\u4e07\u4e07\u9c81\u83bd\u4e0d\u5f97\u2014\u2014\u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u5389\u58f0\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6211\u8fd9\u5c31\u628a\u4f60\u8d76\u51fa\u5c9b\u53bb\uff0c\u6211\u9650\u4f60\u4e09\u58f0\u4e4b\u5185\uff0c\u8fdc\u8fdc\u79bb\u6b64\u5c9b\uff0c \u6c38\u8fdc\u4e0d\u8981\u89c1\u6211\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4e00\u6014\uff0c\u7b54\u4e0d\u4e0a\u8bdd\u6765\u3002 \u3000\u3000\u65e0\u6068\u751f\u51b7\u51b7\u9053\uff1a \u3000\u3000\u201c\u4e00\u2014\u2014\u4e8c\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u5927\u6025\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u4e14\u6162\uff01\u6211\u82e5\u4e0d\u662f\u6709\u6025\u4e8b\u76f8\u6c42\uff0c\u6839\u672c\u4e5f\u4e0d\u4f1a\u8e0f\u4e0a\u8fd9\u5c9b\u534a\u6b65\uff0c\u53ea \u662f\uff0c\u53ea\u662f\u4e8b\u5173\u5929\u4e0b\u6b66\u6797\u2014\u2014\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8bf4\u51fa\u6765\uff0c\u4ee5\u4e3a\u65e0\u6068\u751f\u5fc5\u4f1a\u8ffd\u7a76\u662f\u4ec0\u4e48\u6025\u4e8b\uff0c\u54ea\u91cc\u77e5\u9053\u65e0\u6068\u751f\u706b\u5728\u5fc3\u5934\uff0c\u6839\u672c\u6ca1 \u6709\u542c\u89c1\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff0c\u51b7\u7136\u6570\u9053\uff1a\u201c\u4e09\u2014\u2014\u201d\u5fae\u5fae\u4e00\u987f\u53c8\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u662f\u4e0d\u628a\u6211\u653e\u5728\u773c\u5185 \u4e86\uff0c\u770b\u6211\u65e0\u6068\u751f\u628a\u4f60\u8fd9\u4e0d\u77e5\u597d\u6b79\u7684\u5c0f\u5b50\u8d76\u4e0b\u5c9b\u53bb\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u5df2\u662f\u4e00\u62f3\u6253\u6765\u3002 \u3000\u3000\u8f9b\u6377\u7262\u7acb\u4e0d\u52a8\uff0c\u4e0d\u8eb2\u4e0d\u95ea\uff0c\u65e0\u6068\u751f\u5012\u4e5f\u4e0d\u597d\u6253\u4e0b\u53bb\uff0c\u6536\u624b\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u4f60\u4e0d\u52a8\u624b\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6717\u6717\u9053\uff1a\u201c\u8bba\u529f\u592b\uff0c\u665a\u8f88\u600e\u80fd\u53ca\u5f97\u4e0a\u5c9b\u4e3b\u4e4b\u4e07\u4e00\u5462\uff1f\u4e0d\u8fc7\uff0c\u5c9b\u4e3b\u82e5\u662f\u631f\u6280\u800c\u51cc\u4eba\uff0c \u8bf4\u4e0d\u5f97\uff0c\u8f9b\u67d0\u6211\u5012\u8981\u51b2\u649e\u2014\u2014\u201d \u3000\u3000\u987b\u77e5\u8f9b\u6377\u5929\u751f\u50b2\u9aa8\uff0c\u4ece\u4e0d\u53d7\u4efb\u4f55\u4eba\u7684\u6c14\uff0c\u4eca\u513f\u4e43\u662f\u56e0\u4e3a\u5e73\u51e1\u4e0a\u4eba\u88ab\u56f0\u5728\u5c0f\u6222\u5c9b\u4e0a\uff0c\u6025\u987b \u65e0\u6068\u751f\u76f8\u52a9\uff0c\u4e8b\u5173\u7d27\u8981\uff0c\u624d\u5fcd\u6c14\u541e\u58f0\u3002 \u3000\u3000\u4f46\u90a3\u91cc\u77e5\u9053\u65e0\u6068\u751f\u7531\u4e8e\u7231\u5973\u5931\u8e2a\uff0c\u7adf\u800c\u4e0d\u8fd1\u60c5\u7406\uff0c\u8f9b\u6377\u5927\u975e\u5112\u5f31\u4e4b\u4eba\uff0c\u6c14\u6781\u51fa\u8a00\u51b2\u649e\uff0c \u4e00\u53e3\u6c14\u8bf4\u5b8c\uff0c\u4e1d\u6beb\u4e0d\u89c9\u540e\u6094\uff0c\u53cd\u5230\u6709\u70b9\u75db\u5feb\u7684\u611f\u89c9\u3002 \u3000\u3000\u65e0\u6068\u751f\u4e0d\u6599\u8f9b\u6377\u6709\u5982\u6b64\u5927\u80c6\uff0c\u5fc3\u4e2d\u4e00\u6014\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff0c\u6709\u5fd7\u6c14\uff0c\u4f60\u8981\u51b2\u649e\u6211\u65e0\u6068\u751f\uff0c\u8fd8 \u5f97\u53bb\u518d\u82e6\u7ec3\u5341\u5e74\u5eff\u8f7d\u2014\u2014\u201d\u8bf4\u7740\u4e0d\u5c51\u7684\u4e00\u7b11\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u8bed\u6c14\u4e2d\u597d\u4f3c\u6839\u672c\u77a7\u4e0d\u8d77\u81ea\u5df1\u7684\u529f\u592b\uff0c\u77e5\u9053\u8fd9\u4e43\u662f\u7531\u4e8e\u81ea\u5df1\u4e0a\u6b21\u548c\u4ed6\u4ea4\u624b\u4e00\u4e2a\u7167 \u9762\u5373\u88ab\u64d2\u5f97\u7684\u539f\u6545\uff0c\u8fd9\u4e0d\u557b\u662f\u77a7\u4e0d\u8d77\u6885\u53d4\u53d4\u7684\u529f\u592b\uff0c\u4e0d\u7531\u6012\u706b\u4e0a\u51b2\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u5012\u8ba4\u4e3a\u4e0d \u5fc5\u5982\u6b64\u4e4b\u4e45\u2014\u2014\u201d\u8bf4\u7740\u4e5f\u8fd8\u62a5\u4ee5\u4e0d\u5c51\u7684\u4e00\u7b11\u3002 \u3000\u3000\u65e0\u6068\u751f\u5927\u6012\uff0c\u53f1\u9053\uff1a\u201c\u90a3\u4f60\u4fbf\u8bd5\u4e00\u8bd5\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u8eab\u4f53\u4e00\u52a8\uff0c\u53cc\u638c\u631f\u52bf\u4f5c\u201c\u6cf0\u5c71\u538b\u9876\u201d\u4e4b\u5f0f\u5f53\u5934\u76d6\u95ee\u8f9b\u6377\u4e0a\u76d8\uff0c\u653b\u52bf\u597d\u4e0d\u9510 \u5229\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u638c\u4e2d\u5e26\u6709\u5176\u4ed6\u62db\u5f0f\uff0c\u865a\u5ffd\u4e0d\u5b9a\uff0c\u81ea\u5df1\u6709\u8fc7\u7ecf\u9a8c\uff0c\u4e0a\u6b21\u81ea\u5df1\u4fbf\u5931\u624b\u5728\u8fd9\u624b\u529f\u592b\u4e4b \u4e0b\uff0c\u8fd9\u624b\u529f\u592b\u4e43\u662f\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u7684\u7edd\u6280\u201c\u62c2\u7a74\u201d\u624b\u6cd5\u3002 \u3000\u3000\u8f9b\u6377\u4e0a\u4e00\u6b21\u5f53\uff0c\u5b66\u4e00\u6b21\u4e56\uff0c\u51b5\u4e14\u8fd1\u6765\u529f\u592b\u76f4\u7ebf\u4e0a\u5347\uff0c\u800c\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u672c\u66fe\u7279\u522b\u6388\u4ed6 \u7834\u89e3\u65e0\u6068\u751f\u62c2\u7a74\u624b\u6cd5\u7684\u62db\u5f0f\uff0c\u662f\u4ee5\u4e0d\u614c\u4e0d\u5fd9\uff0c\u53cc\u817f\u5fae\u66f2\uff0c\u5f85\u65e0\u6068\u751f\u53cc\u638c\u653b\u5230\u9876\u5fc3\u4e0d\u53ca\u56db\u5bf8\u7684 \u5730\u65b9\uff0c\u624d\u7528\u529b\u5411\u5de6\u4e00\u8f6c\u3002 \u3000\u3000\u65e0\u6068\u751f\u51b7\u51b7\u4e00\u7b11\uff0c\u53cc\u638c\u4e00\u5206\uff0c\u5316\u4f5c\u5343\u767e\u4e2a\u5730\u65b9\u653b\u5230\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u614c\u4e0d\u5fd9\uff0c\u8981\u7684\u5c31\u662f\u8981\u65e0\u6068\u751f\u5982\u6b64\uff0c\u84e6\u7136\u53cc\u638c\u7ffb\u5411\u4e0a\uff0c\u5341\u6307\u5fae\u626c\uff0c\u5de6\u624b\u4e00\u98a4\uff0c\u53f3\u624b \u8ddf\u8fdb\uff0c\u6b63\u662f\u5728\u5c0f\u6222\u5c9b\u4e0a\u5927\u6222\u5c9b\u4e3b\u6240\u6307\u793a\u7834\u89e3\u201c\u62c2\u7a74\u624b\u6cd5\u201d\u7684\u62db\u5f0f\u3002 \u3000\u3000\u65e0\u6068\u751f\u5fc3\u4e2d\u4ee5\u4e3a\u4e00\u51fb\u5fc5\u80fd\u547d\u4e2d\uff0c\u4f46\u89c1\u8f9b\u6377\u62db\u5f0f\u7adf\u7136\u5982\u6b64\u5947\u7279\uff0c\u50cf\u662f\u521a\u597d\u7834\u89e3\u81ea\u5df1\uff0c\u8fd9\u62db \u5fc3\u4e2d\u5fae\u5fae\u4e00\u60ca\u3002 \u3000\u3000\u8f9b\u6377\u5de6\u624b\u53f3\u624b\u4e00\u98a4\u4e00\u5410\uff0c\u987f\u65f6\u628a\u65e0\u6068\u751f\u4ece\u56db\u9762\u516b\u65b9\u653b\u6765\u7684\u62db\u5f0f\u5c3d\u6d88\u4e3a\u65e0\u5f62\uff0c\u5fc3\u77e5\u65e0\u6068\u751f \u5c1a\u6709\u6740\u624b\uff0c\u4e0d\u6562\u505c\u7559\uff0c\u53f3\u624b\u987a\u52bf\u4e00\u6325\uff0c\u8eab\u5f62\u5f27\u5f62\u540e\u9000\u3002 \u3000\u3000\u8fd9\u4e00\u6325\u4e4b\u4e0b\uff0c\u529b\u9053\u5927\u5f97\u51fa\u5947\uff0c\u53d1\u51fa\u201c\u547c\u201d\u7684\u4e00\u58f0\u3002 \u3000\u3000\u65e0\u6068\u751f\u62db\u5f0f\u8d70\u7a7a\uff0c\u8eab\u4f53\u5f80\u540e\u4e00\u64a4\uff0c\u95ea\u8fc7\u8f9b\u6377\u62f3\u98ce\uff0c\u800c\u8f9b\u6377\u5374\u4e5f\u9000\u540e\u597d\u8fdc\uff0c\u7ad9\u5728\u90a3\u91cc\u3002 \u3000\u3000\u8f9b\u6377\u81ea\u51fa\u9053\u4ee5\u6765\uff0c\u529f\u529b\u771f\u6709\u4e00\u65e5\u5343\u91cc\u4e4b\u52bf\uff0c\u5c24\u5176\u6700\u8fd1\uff0c\u529f\u529b\u5df2\u5230\u8fbe\u4ed6\u4e2a\u4eba\u5dc5\u5cf0\u72b6\u6001\uff0c\u5f3a \u5982\u52fe\u6f0f\u4e00\u602a\uff0c\u4e5f\u8d25\u5728\u4ed6\u624b\u4e0b\u3002\u7136\u800c\u65e0\u6068\u751f\u662f\u4f55\u7b49\u529f\u529b\uff0c\u8f9b\u6377\u81ea\u5fd6\u4ecd\u5dee\u5f97\u751a\u8fdc\uff0c\u662f\u4ee5\u4e0d\u613f\u548c\u4ed6 \u6253\u8fd9\u4e00\u573a\u67b6\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u90a3\u65e0\u6068\u751f\u5374\u518d\u903c\u4e0a\u6765\u5288\u51fa\u4e00\u638c\u3002 \u3000\u3000\u65e0\u6839\u751f\u662f\u6068\u6781\u4e86\u8f9b\u6377\uff0c\u51fa\u62f3\u4e4b\u95f4\uff0c\u7adf\u5e26\u98ce\u96f7\u4e4b\u58f0\uff0c\u60f3\u662f\u4ed6\u5df1\u7528\u4e0a\u4e03\u6210\u771f\u529b\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u6562\u786c\u67b6\uff0c\u4ed6\u731b\u4e00\u62bd\u8eab\uff0c\u8eab\u5f62\u5982\u7bad\u4e00\u822c\u5012\u9000\u4e08\u4f59\uff0c\u4ed6\u6b63\u5f85\u5f00\u53e3\uff0c\u5ffd\u7136\u773c\u524d\u4e00\u82b1\uff0c\u65e0 \u6068\u751f\u5927\u8896\u6325\u5904\uff0c\u53c8\u5230\u4e86\u8f9b\u6377\u773c\u524d\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u5c9b\u4e3b\u4e14\u6162\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u7684\u52b2\u529b\u5df2\u5230\u4e86\u80f8\u524d\uff0c\u8f9b\u6377\u8feb\u4e0d\u5f97\u5df2\uff0c\u53cc\u638c\u5e73\u80f8\u63a8\u51fa\u547c\u7684\u4e00\u58f0\uff0c\u65e0\u6781\u5c9b\u4e3b\u7684\u52b2\u529b\u7adf \u7136\u5168\u6536\uff0c\u8f9b\u6377\u6b64\u62db\u4e43\u662f\u594b\u8db3\u5168\u529b\uff0c\u518d\u4e5f\u6536\u52bf\u4e0d\u4f4f\uff0c\u51b2\u8dcc\u5411\u524d\u2014\u2014 \u3000\u3000\u65e0\u6068\u751f\u53cd\u624b\u4e00\u638c\u5288\u5411\u8f9b\u6377\u80cc\u4e0a\uff0c\u773c\u770b\u5c31\u8981\u6253\u4e0a\uff0c\u4ed6\u7a81\u7136\u628a\u52b2\u9053\u6536\u56de\u4e24\u6210\u3002 \u3000\u3000\u8f9b\u6377\u76f4\u611f\u80cc\u4e0a\u52b2\u538b\u5982\u5c71\uff0c\u4ed6\u8eab\u5b50\u7565\u4fa7\uff0c\u53cc\u638c\u5206\u51fa\uff0c\u4e00\u4e0a\u4e00\u4e0b\uff0c\u4e0a\u9762\u7684\u638c\u597d\u8fce\u5411\u65e0\u6068\u751f\u4e0b \u538b\u4e4b\u52bf\uff0c\u4e0b\u9762\u7684\u4e00\u638c\u5374\u5728\u5730\u4e0a\u4e00\u6309\uff0c\u8eab\u5b50\u5982\u7bad\u4e00\u822c\u659c\u63a0\u800c\u51fa\u3002 \u3000\u3000\u65e0\u6068\u751f\u4e0d\u7981\u66f4\u662f\u6124\u6012\uff0c\u53cc\u638c\u4e00\u7ffb\uff0c\u5b9b\u5982\u5927\u9e70\u4e00\u822c\u6251\u51fb\u800c\u4e0b\u8f9b\u6377\u529f\u529b\u6bd4\u4ed6\u76f8\u5dee\u751a\u8fdc\uff0c\u5fc3\u4e2d \u867d\u7136\u5927\u6025\uff0c\u4f46\u662f\u82e6\u4e8e\u65e0\u6687\u5f00\u53e3\uff0c\u53ea\u597d\u52c9\u5f3a\u548c\u65e0\u6068\u751f\u5468\u65cb\u3002 \u3000\u3000\u4e09\u62db\u4e4b\u540e\uff0c\u8f9b\u6377\u60f3\u5230\u5e73\u51e1\u4e0a\u4eba\u5728\u5c0f\u6222\u5c9b\u7684\u5371\u5883\uff0c\u4e0d\u7981\u5fc3\u5982\u706b\u711a\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u62bd\u8eab\u5c31\u9000\u3002 \u3000\u3000\u8fd9\u6b21\u65e0\u6781\u5c9b\u4e3b\u5012\u6ca1\u6709\u7acb\u523b\u8ffd\u51fb\u800c\u4e0a\uff0c\u8f9b\u6377\u5fd9\u5598\u606f\u9053\uff1a\u201c\u5c9b\u4e3b\u8bf7\u6682\u606f\u6012\uff0c\u665a\u8f88\u6765\u6b64\u4e43\u662f\u5949 \u5e73\u51e1\u4e0a\u4eba\u4e4b\u547d\u6765\u8bf7\u5c9b\u4e3b\u524d\u5f80\u5c0f\u6222\u5c9b\uff0c\u6709\u8981\u4e8b\u76f8\u5546\uff0c\u6167\u5927\u5e08\u4e5f\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u51b7\u51b7\u559d\u9053\uff1a\u201c\u4ec0\u4e48\u5e73\u51e1\u4e0a\u4eba\uff0c\u6167\u5927\u5e08\uff0c\u4e16\u5916\u4e09\u4ed9\u7684\u53ec\u53f7\u662f\u4f60\u4e71\u53eb\u5f97\u7684\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u89c9\u4e00\u6014\uff0c\u968f\u5373\u60f3\u9053\uff1a\u201c\u4ed6\u5fc5\u4e0d\u4fe1\u6211\u7684\u8eab\u4efd\u2014\u2014\u201d \u3000\u3000\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u8f9b\u6377\u957f\u5251\u5df2\u5230\u4e86\u624b\u4e0a\uff0c\u4ed6\u8eab\u5b50\u4e00\u5e4c\uff0c\u957f\u5251\u5bf9\u7a7a\u659c\u659c\u5288\u51fa\uff0c\u5251\u5c16\u78c1\u58f0\u54cd \u5904\uff0c\u9661\u7136\u5316\u505a\u5343\u4e07\u5251\u5f71\u5f53\u7a7a\u7f69\u4e0b\uff0c\u4ed6\u53cc\u8db3\u6781\u5176\u66fc\u5999\u5730\u524d\u540e\u4e00\u53d8\uff0c\u624b\u4e0a\u5251\u5f0f\u5df1\u7ecf\u4ece\u6781\u590d\u6742\u7684\u5251 \u5f71\u4e2d\u4e00\u5212\u800c\u51fa\uff01 \u3000\u3000\u90a3\u5251\u5c16\u4e4b\u5251\u6c14\u539f\u4e0d\u653e\u5728\u65e0\u6068\u751f\u773c\u5185\uff0c\u4f46\u8f9b\u6377\u624b\u4e2d\u7684\u5251\u5f0f\u4e43\u662f\u5947\u7edd\u5929\u4e0b\u7684\u201c\u5927\u884d\u5341\u5f0f\u201d\uff0c \u8db3\u4e0b\u6240\u8e29\u7684\u4e43\u662f\u6167\u5927\u5e08\u7684\u5e73\u751f\u7edd\u5b66\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\uff0c\u8fd9\u5c31\u4ee4\u65e0\u6068\u751f\u5927\u60ca\u4e0d\u5df2\u4e86\uff01 \u3000\u3000\u666e\u5929\u4e4b\u4e0b\uff0c\u8eab\u5177\u8fd9\u4e24\u79cd\u76d6\u4e16\u7edd\u5b66\u7684\uff0c\u53ea\u6015\u4ec5\u8f9b\u6377\u4e00\u4eba\u8033\uff01 \u3000\u3000\u65e0\u6068\u751f\u5728\u5bf9\u4ed8\u7389\u9aa8\u9b54\u65f6\u66fe\u89c1\u8fc7\u8f9b\u6377\u65bd\u5c55\u8bd8\u6469\u6b65\u6cd5\uff0c\u90a3\u65f6\u4ed6\u8fd8\u4e0d\u80fd\u6df1\u4fe1\uff0c\u56e0\u4e3a\u6167\u5927\u5e08\u7684\u602a \u813e\u6c14\u4ed6\u662f\u77e5\u9053\u7684\uff0c\u4f46\u662f\u5982\u4eca\u8f9b\u6377\u6240\u65bd\u7684\u4e0d\u4ec5\u4e0d\u6298\u4e0d\u6263\u7684\u662f\u90a3\u8bd8\u6469\u6b65\u6cd5\uff0c\u800c\u4e14\u624b\u4e0a\u6240\u65bd\u7684\u7adf\u662f \u5e73\u51e1\u4e0a\u4eba\u7684\u201c\u5927\u884d\u5341\u5f0f\u201d\uff01 \u3000\u3000\u4f46\u662f\u65e0\u6068\u751f\u5374\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\u82b1\u8a00\u5de7\u8bed\u9a97\u5f97\u90a3\u4e24\u4e2a\u7cca\u6d82\uff0c\u5374\u9a97\u4e0d\u5f97\u6211\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7531\u5fc3\u5934\u706b\u8d77\uff0c\u4f46\u4ed6\u7acb\u523b\u5c31\u60f3\u5230\u81ea\u5df1\u6240\u8d1f\u7684\u4f7f\u547d\uff0c\u5f53\u4e0b\u5f3a\u5fcd\u6012\u706b\uff0c\u6f5c\u5fc3\u601d\u7d22\u5f53\u524d\u4e4b \u8ba1\u3002 \u3000\u3000\u4ed6\u672c\u806a\u654f\u65e0\u6bd4\uff0c\u5fc3\u60f3\uff1a\u201c\u4e3a\u4eca\u4e4b\u8ba1\u53ea\u5f97\u6fc0\u4ed6\u4e00\u6fc0\u4e86\u2014\u2014\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4ed6\u5148\u4ef0\u5929\u5475\u5475\u957f\u7b11\uff0c\u58f0\u52a8\u571f\u77f3\u3002 \u3000\u3000\u65e0\u6068\u751f\u51b7\u51b7\u9053\uff1a\u201c\u5c0f\u5b50\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7406\u4f1a\u4ed6\uff0c\u5374\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u5509\uff0c\u4e0d\u6599\u4e16\u5916\u4e09\u4ed9\u865a\u5f97\u6d6a\u540d\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u6012\u9053\uff1a\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u6211\u8bf4\u6709\u4e00\u4eba\u6b66\u529f\u8fdc\u80dc\u4e8e\u4f60\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u660e\u77e5\u4ed6\u7684\u6545\u8a00\u76f8\u6fc0\uff0c\u4f46\u4ed6\u5374\u5fcd\u8010\u4e0d\u4f4f\uff0c\u5f53\u4e0b\u6012\u543c\u9053\uff1a\u201c\u4f60\u8bf4\u662f\u8c01\uff1f\u5728\u4ec0\u4e48\u5730 \u65b9\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u544a\u8bc9\u4f60\u4e5f\u4e0d\u6d4e\u4e8b\uff0c\u4f60\u53cd\u6b63\u4e0d\u6562\u53bb\u2014\u2014\u201d \u3000\u3000\u8fd9\u8bdd\u66f4\u662f\u660e\u663e\u662f\u8981\u76f8\u6fc0\uff0c\u4f46\u65e0\u6068\u751f\u5c31\u662f\u54bd\u4e0d\u4e0b\u8fd9\u53e3\u6c14\uff0c\u5927\u543c\u9053\uff1a\u201c\u4f60\u8bf4\uff0c\u4f60\u8bf4\u4ed6\u5728\u54ea \u91cc\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u6211\u6562\u6253\u8d4c\u4f60\u6253\u4e0d\u8fc7\u4ed6\uff0c\u544a\u8bc9\u4f60\u4e5f\u7f62\uff0c\u4ed6\u6b63\u5728\u5c0f\u6222\u5c9b\u4e0a\u2014\u2014\u6211\u6562\u4e0e\u4f60\u6253\u8d4c\u2014\u2014 \u201d \u3000\u3000\u65e0\u6068\u751f\u6012\u9053\uff1a\u201c\u82e5\u662f\u4f60\u8d4c\u8f93\u4e86\u600e\u6837\uff1f\u201d \u3000\u3000\u8f9b\u6377\u53cc\u773c\u4e00\u7ffb\uff0c\u53c8\u6709\u4e86\u8ba1\u7b56\uff0c\u5927\u58f0\u9053\uff1a\u201c\u6211\u82e5\u8f93\u4e86\u6211\u8d1f\u8d23\u66ff\u4f60\u5bfb\u5f97\u83c1\u513f\u2014\u2014\u6211\u4e0e\u4e10\u5e2e\u9887 \u6709\u4ea4\u60c5\uff0c\u4e10\u5e2e\u5144\u5f1f\u904d\u5e03\u5929\u4e0b\uff0c\u5fc5\u80fd\u5bfb\u5f97\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u5fc3\u4e2d\u5374\u6697\u9053\uff1a\u201c\u5c31\u662f\u4e0d\u6253\u8d4c\u6211\u4e5f\u8981\u53bb\u5bfb\u83c1\u513f\u554a\uff01\u201d \u3000\u3000\u65e0\u6068\u751f\u542c\u4ed6\u8bf4\u8d1f\u8d23\u5bfb\u627e\u83c1\u513f\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u9a6c\u4e0a\u9053\uff1a\u201c\u597d\uff0c\u5c31\u8fd9\u4e48\u529e\uff0c\u82e5\u662f\u6211\u8f93\u4e86\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u77e5\u65e0\u6068\u751f\u5fc5\u662f\u628a\u4ed6\u770b\u6210\u5978\u733e\u72e1\u72ef\u4e4b\u5f92\uff0c\u7d22\u6027\u6545\u4f5c\u5978\u7b11\u9053\uff1a\u201c\u82e5\u662f\u5c9b\u4e3b\u8f93\u4e86\uff0c\u665a\u8f88 \u53ef\u8981\u8bf7\u5c9b\u4e3b\u6307\u70b9\u4e24\u624b\uff01\u201d \u3000\u3000\u8fd9\u4e00\u6765\u65e0\u6068\u751f\u679c\u7136\u5927\u4fe1\uff0c\u5fc3\u60f3\uff1a\u201c\u5e73\u51e1\u4e0a\u4eba\u548c\u6167\u5927\u5e08\u5fc5\u662f\u7740\u4e86\u8fd9\u5978\u5c0f\u5b50\u7684\u9053\u513f\u624d\u628a\u7edd\u827a \u4f20\u7ed9\u4ed6\u7684\u2014\u2014\u54fc\u2014\u2014\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u6717\u58f0\u9053\uff1a\u201c\u6210\uff0c\u5c31\u4f9d\u4f60\uff01\u201d \u3000\u3000\u8f9b\u6377\u6697\u5618\u4e00\u53e3\u6c14\uff0c\u5927\u58f0\u9053\uff1a\u201c\u541b\u5b50\u4e00\u8a00\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u54fc\u4e86\u4e00\u58f0\u7d27\u63a5\u7740\u9053\uff1a\u201c\u5feb\u9a6c\u52a0\u97ad\uff01\u201d \u3000\u3000\u65e5\u7167\u4e2d\u5929\uff0c\u5c0f\u6222\u5c9b\u4e0a\u7684\u6218\u6597\u6108\u52a0\u6fc0\u70c8\uff0c\u5927\u6222\u5c0f\u6222\u5c9b\u4e3b\u5e76\u80a9\u4f5c\u6218\uff0c\u4f46\u5bf9\u65b9\u4e5f\u662f\u76d6\u4e16\u7684\u9ad8 \u624b\uff0c\u4e94\u4eba\u6fc0\u6218\u4e4b\u4e0b\uff0c\u5012\u662f\u5929\u7afa\u602a\u5ba2\u591a\u4e86\u4e00\u4eba\uff0c\u5360\u4e86\u4e0a\u98ce\u3002 \u3000\u3000\u5c0f\u6222\u5c9b\u4e3b\u8fd8\u597d\uff0c\u6000\u7740\u4e3e\u4e16\u65e0\u53cc\u7684\u8eab\u6cd5\u2014\u2014\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\uff0c\u5728\u5371\u6025\u65f6\u4fbf\u80fd\u4e00\u95ea\u800c\u8fc7\uff0c\u91c7\u53d6 \u6e38\u6597\u65b9\u5f0f\uff0c\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u5374\u4e0d\u540c\u4e86\uff0c\u4ed6\u5929\u751f\u4e00\u4ed8\u53e4\u602a\u813e\u6c14\uff0c\u548c\u90a3\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u4e2d\u4e4b\u9996\u7684 \u4f2f\u7f57\u5404\u7b54\u6709\u610f\u786c\u6253\u786c\u649e\u3002 \u3000\u3000\u672c\u6765\uff0c\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u53ef\u4ee5\u8f85\u52a9\u5c0f\u6222\u5c9b\u4e3b\uff0c\u7528\u8bd8\u6469\u6b65\u6cd5\u914d\u4e0a\u6740\u624b\u653b\u654c\u3002\u4f46\u504f\u751f\u5bf9\u624b\u5012 \u4e5f\u662f\u4e00\u4e2a\u6781\u5f3a\u7684\u5185\u5bb6\u9ad8\u624b\uff0c\u5927\u6222\u5c9b\u4e3b\u4e00\u8fde\u4e09\u638c\u9707\u4e4b\u4e0d\u9000\uff0c\u6012\u6c14\u4e0a\u51b2\uff0c\u53cc\u8db3\u9489\u7acb\u5728\u77f3\u6797\u4e0a\uff0c\u5c3d \u91c7\u7528\u786c\u649e\u4e4b\u5f0f\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6012\u6c14\u771f\u53d1\uff0c\u638c\u5f0f\u5982\u98ce\uff0c\u548c\u4f2f\u7f57\u5404\u7b54\u786c\u62c6\u4e86\u5eff\u591a\u62db\uff0c\u5e73\u51e1\u4e0a\u4eba\u5012\u5e95\u5185\u529b\u4fee\u4e3a\u8f83\u4f2f \u7f57\u5404\u7b54\u5fae\u9ad8\uff0c\u4f2f\u7f57\u5404\u7b54\u5df1\u89c9\u771f\u529b\u4e0d\u7ee7\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u4e00\u62fc\uff0c\u5012\u82e6\u574f\u4e86\u6167\u5927\u5e08\uff0c\u5979\u4ee5\u4e00\u654c\u4e8c\u4f5b\uff0c\u5168\u529b\u4e0a\u4e58\u8f7b\u529f\u95ea\u8eb2\uff0c\u800c\u91d1\u4f2f\u80dc\u5937\u548c\u76d8\u706f \u5b5a\u5c14\u4e0d\u65f6\u52a0\u4e0a\u4e00\u62db\u4e8c\u5f0f\u53cd\u653b\u5e73\u51e1\u4e0a\u4eba\u7684\u80cc\u90e8\uff0c\u4f7f\u4e8c\u4e2a\u5c9b\u4e3b\u90fd\u5fc3\u611f\u529b\u4e0d\u4ece\u5fc3\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u813e\u6c14\u53e4\u602a\uff0c\u6167\u5927\u5e08\u4e5f\u4e0d\u4fbf\u53eb\u4ed6\u653e\u5f03\u786c\u6253\u7684\u65b9\u5f0f\u6765\u52a9\u5979\uff0c\u662f\u4ee5\u4efb\u4e16\u5916\u4e09\u4ed9\u4e3a\u9996\u4e8c \u4f4d\u529f\u529b\u76d6\u4e16\uff0c\u4ecd\u662f\u7ad9\u5728\u5b8c\u5168\u4e0b\u98ce\u4e4b\u52bf\u52c9\u5f3a\u6253\u6210\u5e73\u624b\u3002\u4f55\u51b5\u77f3\u9635\u4e0b\u9762\u8fd8\u6709\u4e00\u4e2a\u864e\u89c6\u803d\u803d\u7684\u91d1\u9c81 \u5384\u54e9\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8d8a\u6253\u8d8a\u6012\uff0c\u8c6a\u6c14\u51b2\u5929\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u53cc\u638c\u7ffb\u98de\u9f50\u51fa\uff0c\u4e00\u8fde\u5288\u51fa\u5341\u4f59\u638c\u3002\u8fd9\u51e0\u62db\u662f \u5e73\u51e1\u4e0a\u4eba\u5168\u529b\u704c\u6ce8\uff0c\u4f2f\u7f57\u5404\u7b54\u786c\u62c6\u4e4b\u4e0b\uff0c\u5fc3\u795e\u4e00\u6447\uff0c\u9669\u4e9b\u5410\u51fa\u8840\u6765\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u51b7\u7136\u4e00\u7b11\uff0c\u4f46\u5fc3\u4e2d\u4e5f\u4e0d\u7531\u4e00\u7d27\uff0c\u6562\u60c5\u4ed6\u8fd9\u6570\u638c\u4e00\u51fa\uff0c\u4e5f\u8017\u53bb\u5927\u534a\u5185\u5bb6\u771f\u529b\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u89c1\u5e08\u5144\u8eab\u4f53\u6447\u6446\u4e0d\u7a33\uff0c\u4e0d\u7531\u4e00\u60ca\uff0c\u8eab\u4f53\u63a0\u4e86\u8fc7\u6765\uff0c\u53cc\u624b\u75be\u70b9\u5411\u5e73\u51e1\u4e0a\u4eba\u80cc\u90e8\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u542c\u98ce\u8fa8\u58f0\uff0c\u51b7\u51b7\u4e00\u54fc\uff0c\u8eab\u4f53\u7a33\u7acb\u4e0d\u52a8\uff0c\u5927\u8896\u888d\u8882\u98d8\u98d8\u800c\u8d77\uff0c\u5de6\u53f3\u4e00\u8fb9\u98de\u51fa\u4e00 \u638c\uff0c\u770b\u4e5f\u4e0d\u770b\uff0c\u4fbf\u662f\u4e00\u5f0f\u201c\u53cc\u649e\u638c\u201d\u53cd\u62cd\u800c\u51fa\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u89c1\u5bf9\u65b9\u80cc\u5bf9\u81ea\u5df1\uff0c\u8eab\u4f53\u7a33\u7acb\u6709\u82e5\u6cf0\u5c71\uff0c\u53cc\u638c\u62cd\u51fa\u6709\u82e5\u95ea\u7535\uff0c\u800c\u4e14\u8ba4\u654c\u4e4b\u51c6\u3001\u771f \u662f\u751f\u5e73\u4ec5\u89c1\uff0c\u53ea\u8fd9\u4e00\u70b9\uff0c\u4fbf\u662f\u4e00\u6d3e\u5927\u5b97\u5e08\u7684\u6837\u5b50\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5fc3\u6298\u3002 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u786c\u67b6\u5927\u6222\u5c9b\u4e3b\u6570\u638c\uff0c\u7adf\u5403\u4e86\u5c0f\u5c0f\u7684\u5185\u4f24\uff0c\u4e0d\u7531\u5927\u6012\uff0c\u52aa\u529b\u8c03\u517b\u7ffb\u80dc\u7684\u8840\u6c14\uff0c\u4e24 \u638c\u5e73\u80f8\u63a8\u51fa\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53cc\u638c\u5df2\u62cd\u51fa\u5e94\u654c\uff0c\u773c\u89c1\u654c\u624b\u53cc\u638c\u5411\u5916\u753b\u4e86\u4e00\u4e2a\u5706\u513f\uff0c\u77e5\u9053\u540e\u9762\u5fc5\u662f\u731b\u62db\uff0c\u6025\u5207 \u95f4\u6536\u56de\u5de6\u624b\u7684\u62db\u5f0f\uff0c\u5355\u5355\u53f3\u624b\u4e00\u62cd\u4e00\u7ffb\uff0c\u6539\u201c\u53cc\u649e\u638c\u201d\u4e4b\u5f0f\u4e3a\u201c\u62cd\u809a\u817f\u201d\uff0c\u4ecd\u7136\u662f\u786c\u6311\u786c\u6253 \u7684\u8def\u5b50\u3002 \u3000\u3000\u5de6\u624b\u6536\u56de\uff0c\u6025\u5207\u7684\u4e00\u6c89\u4e00\u5410\uff0c\u7adf\u7136\u4e00\u62db\u4e8c\u5f0f\uff0c\u524d\u540e\u5bf9\u654c\u3002\u6167\u5927\u5e08\u770b\u89c1\u8fd9\u60c5\u5f62\uff0c\u4e0d\u7531\u5927\u5403 \u4e00\u60ca\uff0c\u6562\u60c5\u5979\u5fc3\u4e2d\u6709\u6570\uff0c\u5e73\u51e1\u4e0a\u4eba\u518d\u9ad8\u7684\u6b66\u827a\u529f\u529b\uff0c\u4e5f\u4e0d\u80fd\u540c\u65f6\u5e94\u4ed8\u4e24\u4e2a\u524d\u540e\u5939\u529f\u7684\u4e00\u7b49\u4e00 \u9ad8\u624b\u3002 \u3000\u3000\u5fc3\u4e2d\u4e00\u6025\uff0c\u8eab\u5b50\u4e00\u95ea\u4e4b\u95f4\uff0c\u4e00\u6643\u4e00\u8dc3\uff0c\u521a\u597d\u4ece\u90a3\u76d8\u706f\u5b5a\u5c14\u7684\u8eab\u8fb9\u800c\u8fc7\uff0c\u8fd9\u4e43\u662f\u201c\u8bd8\u6469\u6b65 \u6cd5\u201d\u4e2d\u7684\u7cbe\u7cb9\uff0c\u76d8\u706f\u5b5a\u5c14\u8fd9\u7b49\u529f\u529b\uff0c\u773c\u770b\u5979\u6765\u8fd1\u4e86\uff0c\u5288\u51fa\u4e24\u638c\uff0c\u5374\u90fd\u8d70\u4e86\u7a7a\u3002 \u3000\u3000\u6167\u5927\u5e08\u5982\u6b64\u7684\u529f\u529b\uff0c\u4f46\u7a76\u7adf\u662f\u5973\u4eba\u5bb6\uff0c\u662f\u4ee5\u5e73\u65e5\u5f88\u5c11\u7528\u5185\u529b\u786c\u6253\u786c\u62fc\uff0c\u6b64\u523b\u513f\u4e8b\u60c5\u8feb\u4e0d \u6025\u5f85\uff0c\u8fd8\u9694\u5341\u4f59\u6839\u767e\u7b0b\u7684\u8ddd\u79bb\uff0c\u53cc\u62f3\u4e00\u5408\uff0c\u865a\u6363\u800c\u51fa\uff0c\u7adf\u662f\u4e0a\u4e58\u5185\u5bb6\u62f3\u62db\u2014\u2014\u201c\u767e\u6b65\u795e\u62f3\u201d \u4e4b\u5f0f\u3002 \u3000\u3000\u6167\u5927\u5e08\u5e73\u65e5\u5f88\u5c11\u7528\u786c\u51fb\u4e4b\u5f0f\uff0c\u4f46\u4e00\u51fb\u4e4b\u4e0b\uff0c\u62f3\u98ce\u6709\u82e5\u6012\u6d9b\u6392\u7a92\uff0c\u6025\u6d8c\u800c\u51fa\uff0c\u6392\u51fa\u62f3\u98ce\uff0c \u53d1\u51fa\u5636\u5636\u98ce\u54cd\u3002 \u3000\u3000\u6167\u5927\u5e08\u8fd9\u4e00\u51fb\u662f\u5bf9\u51c6\u91d1\u4f2f\u80dc\u5937\u800c\u53d1\u7684\uff0c\u91d1\u4f2f\u80dc\u5937\u524d\u6709\u5e73\u51e1\u4e0a\u4eba\u4e4b\u5f0f\uff0c\u540e\u6709\u6167\u5927\u5e08\u5939\u51fb\uff0c \u8fd9\u4e00\u6765\uff0c\u53cd\u5012\u4fbf\u4ed6\u6210\u4e3a\u5939\u51fb\u7684\u5bf9\u8c61\uff0c\u91d1\u4f2f\u80dc\u5937\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u4f53\u5f80\u659c\u8fb9\u649e\u53bb\uff0c\u5de6\u624b\u4e00\u6c89\uff0c\u63a5\u4e86 \u5e73\u51e1\u4e0a\u4eba\u201c\u62cd\u809a\u817f\u201d\u4e4b\u5f0f\uff0c\u867d\u7136\u5e73\u51e1\u4e0a\u4eba\u662f\u5355\u624b\uff0c\u4f46\u4ed6\u4e5f\u89c9\u6709\u82e5\u5343\u65a4\u91cd\u9524\u9524\u4e86\u4e00\u4e0b\uff0c\u8eab\u5b50\u4e0d \u7531\u4e00\u6643\u3002 \u3000\u3000\u6167\u5927\u5e08\u7684\u201c\u767e\u6b65\u795e\u62f3\u201d\u773c\u770b\u4fbf\u8981\u6253\u5728\u4ed6\u7684\u80cc\u4e0a\uff0c\u4f46\u5c0f\u6222\u5c9b\u4e3b\u4f55\u7b49\u4eba\u7269\uff0c\u600e\u80fd\u6697\u7b97\u4e8e\u4eba\uff0c \u89c1\u5e73\u51e1\u4e0a\u4eba\u540e\u56f4\u5df2\u89e3\uff0c\u786c\u786c\u4e00\u5410\u5185\u529b\uff0c\u62e8\u504f\u51c6\u5934\u3002 \u3000\u3000\u5c0f\u6222\u5c9b\u4e3b\u6c14\u529f\u7cbe\u7eaf\uff0c\u504f\u51fb\u4e4b\u4e0b\uff0c\u865a\u7a7a\u7adf\u5c06\u4e00\u6839\u6574\u6574\u7684\u767e\u7b0b\u5c16\u513f\u626b\u53bb\uff0c\u77f3\u5c51\u6f2b\u5929\u98de\u821e\u3002 \u3000\u3000\u8fd9\u4e00\u8bb0\u795e\u62f3\u771f\u53ef\u8c13\u201c\u767e\u6b65\u795e\u62f3\u201d\uff0c\u9694\u90a3\u4e48\u8fdc\u4e00\u63a8\u4e4b\u4e0b\uff0c\u90a3\u6837\u575a\u56fa\u7684\u77f3\u5934\u4e5f\u88ab\u6253\u98de\u4e00\u5927\u7247 \u6765\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u4f5b\u7acb\u8db3\u4e0d\u7a33\uff0c\u8dcc\u4e0b\u77f3\u7b0b\uff0c\u843d\u81f3\u9635\u5185\u3002 \u3000\u3000\u90a3\u5e73\u51e1\u4e0a\u4eba\u672c\u6765\u771f\u529b\u5df2\u7136\u4e0d\u6d4e\u3002\u800c\u4e14\u662f\u7528\u4e00\u624b\u4e4b\u529b\uff0c\u548c\u6052\u6cb3\u4e09\u4f5b\u4e4b\u9996\u4f2f\u7f57\u5404\u7b54\u542b\u5fff\u800c\u53d1 \u7684\u62f3\u52bf\u76f8\u89e6\uff0c\u4e8c\u4eba\u5fc3\u5934\u90fd\u662f\u4e00\u9707\u3002 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u8840\u6c14\u672c\u5df2\u7ffb\u817e\uff0c\u5f3a\u538b\u6e05\u4e00\u638c\u5288\u51fa\uff0c\u548c\u5e73\u51e1\u4e0a\u4eba\u7684\u62f3\u98ce\u4e00\u649e\uff0c\u5fc3\u5934\u5fae\u611f\u4e00\u70ed\uff0c\u52aa \u529b\u518d\u52a0\u529b\u9053\uff0c\u60f3\u628a\u5e73\u51e1\u4e0a\u4eba\u6253\u4e0b\u77f3\u7b0b\u53bb\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e5f\u89c9\u8eab\u5b50\u4e00\u9635\u6447\u6446\u4e0d\u5b9a\uff0c\u51b7\u51b7\u4e00\u54fc\uff0c\u638c\u5fc3\u95ea\u7535\u822c\u5411\u5916\u4e00\u767b\uff0c\u4e00\u80a1\u529b\u9053\u518d\u5ea6\u6392\u7a7a \u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u80a1\u529b\u9053\u597d\u751f\u53e4\u602a\uff0c\u4e0d\u4f46\u521a\u5de7\u62b5\u4f4f\u4f2f\u7f57\u5404\u7b54\u52bf\u82e5\u5954\u96f7\u7684\u4e00\u62f3\uff0c\u800c\u4e14\u8fd8\u5f80\u65c1\u8fb9\u4e00\u5e26\u3002 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u4e0d\u9632\u6709\u6b64\uff0c\u8eab\u4f53\u88ab\u8fd9\u529b\u9053\u4e00\u5e26\u4e4b\u4e0b\uff0c\u8f6c\u4e86\u4e00\u4e2a\u5708\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u52aa\u529b\u903c\u4f4f\u771f\u6c14\uff0c \u624d\u7ad9\u5728\u77f3\u4e0a\u6ca1\u6709\u8dcc\u4e0b\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5fc3\u5934\u4e00\u9707\uff0c\u81ea\u77e5\u771f\u529b\u5728\u5f3a\u649e\u5f3a\u6253\u4e4b\u9645\u5df2\u7136\u53d7\u635f\uff0c\u4e0d\u6562\u6258\u5927\uff0c\u4e5f\u76d8\u819d\u5750\u5728\u77f3\u7b0b\u4e0a \u8fd0\u529f\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u4e94\u4e2a\u4eba\u53ea\u6709\u5c0f\u6222\u5c9b\u4e3b\u548c\u76d8\u706f\u5b5a\u5c14\u8fd8\u5728\u77f3\u7b0b\u4e0a\uff0c\u84e6\u7136\u91cc\uff0c\u5237\u5237\u4e24\u6761\u4eba\u5f71\u98de\u4e0a\u77f3\u7b0b \u6765\uff0c\u6167\u5927\u5e08\u62ac\u773c\u4e00\u770b\uff0c\u7adf\u662f\u90a3\u8dcc\u4e0b\u77f3\u6797\u7684\u91d1\u4f2f\u80dc\u5937\u548c\u91d1\u9c81\u5384\u3002 \u3000\u3000\u6167\u5927\u5e08\u77e5\u9053\u5e73\u51e1\u4e0a\u4eba\u662f\u771f\u529b\u5927\u4e4f\uff0c\u5fc3\u77e5\u4ed6\u8fd0\u529f\u518d\u5feb\uff0c\u4e5f\u8981\u534a\u4e2a\u65f6\u8fb0\u624d\u5f97\u597d\u8f6c\uff0c\u76ee\u524d\u81ea\u5df1 \u4ee5\u4e00\u654c\u4e09\uff0c\u5982\u7528\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\u6e38\u6597\u8fd8\u53ef\u4ee5\u652f\u6301\u4e00\u4e0b\uff0c\u8f6c\u773c\u77a5\u89c1\u91d1\u4f2f\u80dc\u5937\uff0c\u4f46\u89c1\u4ed6\u5de6\u624b\u597d\u4f3c\u5782 \u7740\u7684\u50cf\u662f\u53d7\u4e86\u8f7b\u4f24\uff0c\u6697\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5e73\u51e1\u4e0a\u4eba\u4e00\u51fb\u80fd\u4f24\u4e24\u4e2a\u4eba\uff0c\u8fd9\u4efd\u672c\u4e8b\uff0c\u53ef\u771f\u4e0d\u5f97\u4e86\uff0c\u6211\u5c0f\u6222\u5c9b\u4e3b\u96be\u9053\u5c31\u4e0d\u80fd\u4f5c\u5230\u2014\u2014 \u201d \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u8c6a\u6c14\u4e0a\u5347\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u5582\uff01\u770b\u6765\u4f60\u50cf\u662f\u4f24\u4e86\uff1f\u6211\u5c0f\u6222\u5c9b\u4e3b\u81ea\u4f1a\u7b49\u2014\u2014\u201d \u3000\u3000\u5979\u8bdd\u65b9\u51fa\u53e3\uff0c\u91d1\u4f2f\u80dc\u5937\u5df2\u51b7\u7136\u9053\uff1a\u201c\u54fc\u54fc\u2014\u2014\u201d \u3000\u3000\u4ed6\u4e16\u5c45\u5929\u5170\uff0c\u53ea\u77e5\u4e1c\u6d77\u6709\u4e16\u5916\u4e09\u4ed9\uff0c\u4f46\u4ed6\u4eec\u4e09\u4f5b\u6ee1\u4ee5\u4e3a\u4ee5\u81ea\u5df1\u7684\u529f\u592b\uff0c\u7edd\u4e0d\u6015\u90a3\u4e16\u5916\u4e09 \u4ed9\uff0c\u90a3\u77e5\u81ea\u91d1\u9c81\u5384\u53d7\u632b\u540e\uff0c\u4ed6\u4eec\u4e09\u4eba\u8054\u8882\u6765\u5230\u4e2d\u539f\uff0c\u5374\u4e0d\u6599\u4e16\u5916\u4e09\u4ed9\u7adf\u6709\u5982\u6b64\u529f\u529b\u3002 \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u51fa\u6cb3\u9c81\uff0c\u91cc\u4eea\u4e4c\u5fb7\uff0c\u54e5\u672c\u5730\u4e4f\u6d69\u5475\u2014\u2014\u201d \u3000\u3000\u4ed6\u8a00\u4e2d\u4e4b\u610f\u4e43\u662f\u8981\u5e08\u5f1f\u76d8\u706f\u5b5a\u5c14\u548c\u6167\u5927\u5e08\u6253\u4e00\u9635\uff0c\u81ea\u5df1\u53bb\u770b\u5e08\u5144\u4f2f\u7f57\u5404\u7b54\u7684\u4f24\u52bf\uff0c\u6167\u5927 \u5e08\u5728\u4e00\u65c1\u83ab\u660e\u5176\u5999\uff0c\u4e0d\u7ec6\u4ed6\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u8bf4\u5b8c\uff0c\u8eab\u4f53\u4e00\u8361\uff0c\u63a0\u5411\u5e73\u51e1\u4e0a\u4eba\u548c\u4f2f\u7f57\u5404\u7b54\u4e8c\u4eba\u8c03\u606f\u5904\u3002\u6167\u5927\u5e08\u5fc3\u4e2d\u4e00\u6025\uff0c\u53ea \u5f53\u4ed6\u662f\u53bb\u4f24\u5bb3\u6b63\u5728\u8fd0\u529f\u7684\u5e73\u51e1\u4e0a\u4eba\uff0c\u5927\u53f1\u4e00\u58f0\uff0c\u63a0\u8eab\u8ffd\u53bb\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u5012\u4e0d\u77e5\u6167\u5927\u5e08\u8bef\u4f1a\uff0c\u53cc\u8db3\u4e00\u70b9\uff0c\u8eab\u5f62\u5982\u7bad\u63a0\u53bb\uff0c\u6167\u5927\u5e08\u4e0d\u6599\u4ed6\u8f7b\u529f\u5982\u6b64\u5feb\u6cd5\uff0c \u5403\u4e86\u4e00\u60ca\uff0c\u91d1\u4f2f\u80dc\u5937\u5df2\u7136\u63a0\u5230\u4f2f\u7f57\u5f84\u7b54\u8eab\u65c1\u63a2\u4e86\u63a2\u8109\u606f\u3002 \u3000\u3000\u6167\u5927\u5e08\u7684\u8f7b\u529f\u672c\u6765\u53ef\u79f0\u4e3e\u4e16\u65e0\u53cc\uff0c\u5c24\u5176\u662f\u914d\u4e0a\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\uff0c\u66f4\u662f\u795e\u5947\u65e0\u6bd4\uff0c\u4f46\u548c\u8fd9\u91d1 \u4f2f\u80dc\u5937\u4e00\u6bd4\u4e4b\u4e0b\uff0c\u7adf\u4f3c\u6162\u4e86\u4e00\u4e9b\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5927\u5927\u60ca\u9a87\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u89c1\u5979\u8ddf\u6765\uff0c\u4f1a\u610f\u51b7\u51b7\u7684\u9053\uff1a\u201c\u4f60\u5f53\u6211\u662f\u4ec0\u4e48\u4eba\uff1f\u4f1a\u53bb\u4f24\u8fd9\u6b63\u5728\u8c03\u606f\u7684\u4eba\u4e48\uff1f\u201d \u3000\u3000\u4f46\u5979\u5374\u6697\u4e2d\u60ca\u9053\uff1a\u201c\u6211\u90a3\u8bd8\u6469\u6b65\u6cd5\u795e\u529f\u65e0\u53cc\uff0c\u4f46\u8bba\u5230\u5feb\u901f\u4e24\u5b57\uff0c\u6050\u6015\u7adf\u4e0d\u53ca\u8fd9\u53ae\u54e9\u3002\u201d \u3000\u3000\u6167\u5927\u5e08\u89c1\u4ed6\u63a2\u89c6\u4f2f\u7f57\u5404\u7b54\uff0c\u5df2\u77e5\u662f\u8bef\u4f1a\u3002 \u3000\u3000\u6b63\u5728\u8fd9\u65f6\uff0c\u5927\u6222\u5c9b\u4e3b\u84e6\u7136\u62ac\u5934\u5bf9\u91d1\u4f2f\u80dc\u5937\u9053\uff1a\u201c\u4f60\u4e14\u4e0d\u8981\u5f97\u610f\uff0c\u4f60\u9053\u4f60\u4eec\u5316\u5916\u4e4b\u6c11\u7684\u6b66 \u5b66\u80fd\u6bd4\u4e2d\u539f\u5f3a\u4e48\uff1f\u7b49\u4e00\u4f1a\u2014\u2014\u563f\u563f\u2014\u2014\u201d \u3000\u3000\u4ed6\u663e\u7136\u5c1a\u672a\u5b8c\u5168\u6062\u590d\uff0c\u4e2d\u6c14\u4f3c\u6709\u4e0d\u8db3\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u4e0d\u53bb\u7406\u4ed6\uff0c\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u8fd9\u53eb\u7740\u534e\u5937\u4e4b\u4e89\uff0c\u7b49\u4f1a\u770b\u770b\u5230\u5e95\u662f\u8c01\u80dc\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u518d\u5ea6\u95ed\u4e0a\u773c\u6765\uff0c\u7528\u529f\u8c03\u606f\u3002 \u3000\u3000\u4e14\u8bf4\u8f9b\u6377\u548c\u65e0\u6068\u751f\u79bb\u5f00\u65e0\u6781\u5c9b\uff0c\u9a76\u821f\u5982\u98de\u5411\u5c0f\u6222\u5c9b\u8d76\u6765\u3002\u5c0f\u6222\u5c9b\u8ddd\u65e0\u6781\u5c9b\u5e76\u4e0d\u5341\u5206\u9065 \u8fdc\u3002\u4e8c\u4eba\u4e00\u8def\u4e0a\u7528\u529b\u50ac\u821f\uff0c\u8239\u884c\u6781\u901f\uff0c\u5728\u6d77\u6d0b\u9762\u4e0a\u5212\u4e86\u4e00\u6761\u957f\u957f\u7684\u6d6a\u82b1\u3002 \u3000\u3000\u8fd9\u4e8b\u5173\u7cfb\u5168\u6b66\u6797\u76db\u8870\uff0c\u65e0\u6068\u751f\u4e5f\u4e0d\u6562\u6020\u6162\uff0c\u7528\u8db3\u5185\u529b\uff0c\u62c2\u5f80\u540e\u9762\uff0c\u6bcf\u62c2\u4e00\u4e0b\uff0c\u8239\u513f\u4fbf\u80fd \u9a76\u51fa\u4e8e\u4f59\u4e08\u6765\uff0c\u4e00\u4f1a\u513f\u5c0f\u6222\u5c9b\u4fbf\u6a21\u7cca\u5728\u671b\u3002 \u3000\u3000\u65e0\u6068\u751f\u867d\u548c\u6167\u5927\u5e08\u3001\u5e73\u51e1\u4e0a\u4eba\u5e76\u79f0\u4e16\u5916\u4e09\u4ed9\uff0c\u4f46\u4ea4\u5f80\u5e76\u4e0d\u9891\uff0c\u5c24\u5176\u662f\u5bf9\u6167\u5927\u5e08\uff0c\u65e0\u6068\u751f \u8fde\u5c0f\u6222\u5c9b\u90fd\u6ca1\u6709\u8e0f\u4e0a\u8fc7\u534a\u6b65\uff0c\u6b64\u523b\u5230\u5f97\u8fd1\u5904\uff0c\u4e0d\u7531\u591a\u591a\u6253\u91cf\u51e0\u773c\u3002 \u3000\u3000\u767b\u4e0a\u9646\u6765\uff0c\u4e8c\u4eba\u9f50\u65bd\u8f7b\u529f\u731b\u8d76\u3002 \u3000\u3000\u8f9b\u6377\u8f7b\u529f\u867d\u81ea\u4e0d\u51e1\uff0c\u4f46\u6bd4\u8d77\u65e0\u6068\u751f\u6765\u4ecd\u5dee\u4e86\u51e0\u7b79\uff0c\u65e0\u6068\u751f\u6025\u4e0d\u8feb\u5f85\uff0c\u4e0d\u65f6\u6276\u8f9b\u6377\u4e00\u628a\uff0c \u4e8c\u4eba\u6709\u82e5\u5728\u9ec4\u786c\u7684\u6c99\u571f\u4e0a\u5212\u8fc7\u4e8c\u9053\u9ed1\u7ebf\uff0c\u901f\u5ea6\u60ca\u4eba\u5df2\u6781\u3002 \u3000\u3000\u5c0f\u6222\u5c9b\u65b9\u5706\u4e0d\u8fc7\u5341\u91cc\uff0c\u4e24\u4eba\u6b64\u7b49\u811a\u7a0b\uff0c\u4e0d\u6d88\u7247\u523b\uff0c\u4fbf\u6765\u5230\u90a3\u77f3\u6797\u6240\u5e03\u7684\u5f52\u5143\u53e4\u9635\u524d\u3002 \u3000\u3000\u84e6\u7136\uff0c\u77f3\u7b0b\u9635\u4e0a\u65b9\u5dee\u4e0d\u591a\u540c\u65f6\u7684\u53d1\u51fa\u4e24\u58f0\u9f99\u541f\u822c\u7684\u957f\u5578\uff0c\u5578\u58f0\u90fd\u662f\u4f4e\u6c89\u6709\u529b\u5df2\u6781\uff0c\u663e\u793a \u90a3\u53d1\u5578\u7684\u4e24\u4eba\u7684\u5185\u529f\u9020\u8be3\u90fd\u662f\u5df2\u8fbe\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\u3002 \u3000\u3000\u65e0\u6068\u751f\u4e00\u6014\uff0c\u968f\u5373\u4f1a\u610f\u9053\uff1a\u201c\u4ece\u90a3\u4e24\u58f0\u5578\u58f0\u53ef\u77e5\u662f\u4e8c\u4e2a\u5185\u529b\u6781\u9ad8\u7684\u9ad8\u624b\u5728\u4f24\u540e\u8c03\u606f\u597d\u7814 \u53d1\u7684\uff0c\u770b\u6765\u5927\u5c0f\u6222\u4e8c\u5c9b\u4e3b\u95f4\u5fc5\u6709\u5176\u4e00\u53d7\u4f24\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u7eb5\u8eab\u98de\u4e0a\u77f3\u7b0b\u3002 \u3000\u3000\u90a3\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u548c\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u4e4b\u9996\u4f2f\u7f57\u5404\u7b54\u679c\u4e0d\u51fa\u65e0\u6068\u751f\u6240\u6599\uff0c\u8fd0\u529f\u4e4b\u540e\uff0c\u540c\u65f6 \u590d\u539f\u3002 \u3000\u3000\u7531\u4e8e\u4e24\u4eba\u5185\u529b\u9020\u8be3\u76f8\u5dee\u6709\u9650\uff0c\u662f\u4ee5\u590d\u539f\u5f97\u4e5f\u5dee\u4e0d\u591a\u5feb\uff0c\u4e24\u4eba\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\uff0c\u9762\u5bf9\u9762\u7acb \u7740\uff0c\u5927\u6709\u518d\u62fc\u7684\u610f\u601d\u3002 \u3000\u3000\u90a3\u8fb9\u6167\u5927\u5e08\u5c55\u5f00\u8bd8\u6469\u6b65\u6cd5\u548c\u91d1\u4f2f\u80dc\u5937\u3001\u76d8\u706f\u5b5a\u5c14\uff0c\u91d1\u9c81\u5384\u4e09\u4eba\u6e38\u6597\uff0c\u6781\u52c9\u5f3a\u5730\u4ee5\u4e0b\u98ce\u4e4b \u52bf\u7ef4\u6301\u4e0d\u8d25\u3002 \u3000\u3000\u7531\u4e8e\u5e73\u51e1\u4e0a\u4eba\u548c\u4f2f\u7f57\u5404\u7b54\u540c\u65f6\u590d\u539f\uff0c\u6218\u5c40\u53c8\u81ea\u4e00\u53d8\uff0c\u91d1\u4f2f\u80dc\u5937\u5fc3\u77e5\u4f2f\u7f57\u5404\u7b54\u4f24\u540e\u6050\u975e\u5e73 \u51e1\u4e0a\u4eba\u4e4b\u654c\uff0c\u5fd9\u652f\u91d1\u9c81\u5384\u53bb\u5e2e\u5927\u5e08\u4f2f\u62b5\u6321\u90a3\u5e73\u51e1\u4e0a\u4eba\u3002 \u3000\u3000\u84e6\u7136\u91cc\uff0c\u77f3\u6797\u4e0a\u4eba\u5f71\u4e00\u95f4\uff0c\u91d1\u9c81\u5384\u5403\u4e86\u4e00\u60ca\uff0c\u5f53\u4ed6\u5206\u8fa8\u662f\u4e8c\u6761\u4eba\u5f71\u5982\u98de\u8d76\u6765\u662f\uff0c\u5927\u53f1\u4e00 \u58f0\uff0c\u5168\u529b\u4e00\u638c\u51fb\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5047\u4f7f\u662f\u522b\u7684\u4eba\uff0c\u4ed6\u4e5f\u8bb8\u4f1a\u8003\u8651\u6765\u8005\u662f\u654c\u662f\u53cb\uff0c\u4e0d\u53ef\u80fd\u4fbf\u4e00\u638c\u6253\u8fc7\u53bb\uff0c\u4f46\u91d1\u9c81\u5384\u4e43\u662f\u5929\u7afa \u6765\u5ba2\uff0c\u6839\u672c\u5728\u4e2d\u539f\u6ca1\u6709\u53cb\u4eba\uff0c\u6765\u7684\u4eba\u5b9a\u662f\u654c\u65b9\uff0c\u662f\u4ee5\u4e0d\u8003\u8651\u4fbf\u4e00\u638c\u5bf9\u53bb\u3002 \u3000\u3000\u524d\u9762\u7684\u4eba\u5f71\u6b63\u662f\u8d76\u6765\u52a9\u62f3\u7684\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\uff0c\u6839\u672c\u8eab\u4f53\u4ec5\u4ec5\u5f80\u4fa7\u4e00\u95f4\u4e4b\u95f4\uff0c\u4fbf\u63a0\u4e86\u8fc7 \u53bb\u3002 \u3000\u3000\u540e\u9762\u7684\u4e00\u4eba\u6b63\u662f\u8f9b\u6377\uff0c\u4ed6\u4e0d\u50cf\u65e0\u6068\u751f\u822c\u4e00\u95ea\u800c\u8fc7\uff0c\u5374\u662f\u4e0d\u5ba2\u6c14\u7684\u53cc\u638c\u4e00\u5410\uff0c\u56de\u51fb\u8fc7\u53bb\uff0c \u786c\u786c\u7684\u63a5\u4e86\u4e00\u62db\u3002 \u3000\u3000\u91d1\u9c81\u5384\u4e00\u649e\u540e\u9000\uff0c\u77a5\u773c\u4e4b\u95f4\uff0c\u7adf\u662f\u5728\u90a3\u201c\u65e0\u4e3a\u5385\u201d\u4e0a\u632b\u8d25\u81ea\u5df1\u7684\u8f9b\u6377\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c\u770b\u6765 \u597d\u50cf\u4ed6\u53c8\u6709\u4e86\u8fdb\u6b65\u3002\u65e0\u6068\u751f\u76f4\u5954\u8fc7\u53bb\uff0c\u5954\u5230\u90a3\u4f2f\u7f57\u5404\u7b54\u8eab\u540e\u5927\u53f1\u9053\uff1a\u201c\u63a5\u62db\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u4e00\u5802\u659c\u5288\u800c\u51fa\u3002 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u867d\u7136\u4e0d\u61c2\u201c\u63a5\u62db\u201d\u7684\u610f\u601d\uff0c\u4f46\u542c\u638c\u98ce\uff0c\u5df2\u77e5\u6709\u654c\uff0c\u4ed6\u81ea\u89c6\u751a\u9ad8\uff0c\u51b7\u51b7\u4e00\u7b11\uff0c\u53cd \u624b\u4e00\u638c\u5288\u51fa\u3002 \u3000\u3000\u65e0\u6068\u751f\u77e5\u4ed6\u6258\u5927\uff0c\u51b7\u51b7\u4e00\u7b11\u9053\uff1a\u201c\u597d\u638c\u6cd5\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u84e6\u7136\u5288\u51fa\u7684\u638c\u4e00\u6c89\u4e00\u5e26\u3002 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u4f5c\u68a6\u4e5f\u60f3\u4e0d\u5230\u8eab\u540e\u53c8\u662f\u4e00\u4e2a\u529f\u529b\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\u7684\u9ad8\u624b\uff0c\u4e00\u4e2a\u8f7b\u654c\uff0c\u4e0d\u4f46\u53cd\u51fb\u7684 \u638c\u529b\u88ab\u5378\u5728\u4e00\u8fb9\uff0c\u800c\u4e14\u8eab\u5f62\u4e5f\u88ab\u5bf9\u65b9\u4e00\u5e26\u4e4b\u4e0b\uff0c\u8f6c\u4e86\u4e00\u4e2a\u8f6c\u513f\uff0c\u9762\u5bf9\u65e0\u6068\u751f\u3002 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u53c8\u5403\u4e86\u4e00\u6b21\u9634\u4e8f\uff0c\u4e0d\u7531\u8001\u7f9e\u6210\u6012\uff0c\u6012\u5316\u4e00\u58f0\uff0c\u4e00\u638c\u5e73\u80f8\u6253\u51fa\u3002 \u3000\u3000\u4e8c\u80a1\u529b\u9053\u4e00\u89e6\uff0c\u65e0\u6068\u751f\u4e00\u6643\uff0c\u800c\u4f2f\u7f57\u5404\u7b54\u4e5f\u4e0d\u7531\u4e00\u9707\uff0c\u9669\u4e9b\u540e\u9000\uff0c\u5fc3\u4e2d\u7684\u60ca\u9a87\u53ef\u771f\u662f\u592a \u5927\u4e86\uff0c\u5fd6\u9053\uff1a\u201c\u963f\u91cc\u53e4\u5e0c\uff0c\u9646\u65af\uff0c\u9a6c\u591a\u5468\u5c3c\u53e4\u8bfa\uff0c\u8377\u8377\u561f\u2014\u2014\u201d \u3000\u3000\u4ed6\u662f\u60f3\u9053\uff1a\u201c\u771f\u4e0d\u53ef\u601d\u8bae\uff0c\u4e00\u4e0b\u5b50\u8fd9\u8352\u5c9b\u7adf\u6709\u4e09\u4e2a\u6b64\u7b49\u8eab\u624b\uff0c\u6052\u6cb3\u4e09\u4f5b\u7684\u5a01\u540d\u53ef\u80fd\u4f1a\u6bc1 \u4e8e\u4e00\u65e6\u54e9\u2014\u2014\u201d \u3000\u3000\u4ed6\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7531\u5fae\u5fae\u6c14\u9981\u3002 \u3000\u3000\u90a3\u8fb9\u5e73\u51e1\u4e0a\u4eba\u5df2\u5927\u7b11\u5bf9\u65e0\u6068\u751f\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u591a\u5e74\u4e0d\u89c1\u5566\uff0c\u771f\u6709\u4f60\u7684\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u6b63\u8272\u7684\u7b54\u9053\uff1a\u201c\u4e0a\u4eba\u8fc7\u5956\u2014\u2014\u201d \u3000\u3000\u4ed6\u5728\u4e16\u5916\u4e09\u4ed9\u4e2d\u548c\u5e73\u51e1\u4e0a\u4eba\u4ea4\u60c5\u8f83\u597d\uff0c\u5728\u5e73\u51e1\u4e0a\u4eba\u4e24\u7532\u5b50\u751f\u65e5\u65f6\uff0c\u66fe\u9001\u4e86\u5e73\u51e1\u4e0a\u4eba\u4e00\u5177 \u4e0a\u53e4\u94c1\u94ee\uff0c\u4e8c\u4eba\u5e73\u65e5\u867d\u6781\u5c11\u4f1a\u9762\uff0c\u5374\u5f88\u719f\u6089\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u4eca\u513f\u54b1\u4eec\u4e16\u5916\u4e09\u4ed9\u5168\u5728\u8fd9\u91cc\uff0c\u4ed6\u4eec\u6052\u6cb3\u4e09\u4f5b\u7adf\u5ba3\u79f0\u8981\u5f81\u670d\u4e2d\u539f\u6b66\u6797\uff0c \u73b0\u5728\u662f\u4ee5\u4e00\u654c\u4e00\uff0c\u54b1\u4eec\u53ef\u5343\u4e07\u4e0d\u80fd\u6709\u635f\u540d\u5934\u2014\u2014\u201d\u8fd9\u8bdd\u540d\u7740\u662f\u5411\u65e0\u6068\u751f\u8bf4\uff0c\u5176\u5b9e\u662f\u6fc0\u90a3\u6167\u5927 \u5e08\uff0c\u6167\u5927\u5e08\u54ea\u91cc\u4e0d\u660e\u767d\u4ed6\u7684\u610f\u601d\uff0c\u4e0d\u7531\u8c6a\u6c14\u4e0a\u5347\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53c8\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u53bb\u5bf9\u4ed8\u90a3\u4e2a\u6ee1\u9762\u76b1\u7eb9\u7684\u5bb6\u4f19\uff0c\u54b1\u4eec\u662f\u4e00\u5bf9\u4e00\u2014\u2014\u201d \u3000\u3000\u90a3\u6ee1\u9762\u76b1\u7eb9\u7684\u5bb6\u4f19\uff0c\u662f\u6307\u6052\u6cb3\u4e09\u4f5b\u4e4b\u672b\u76d8\u706f\u5b5a\u5c14\uff0c\u4ed6\u7684\u610f\u601d\u662f\u8981\u4ee5\u4e16\u5916\u4e09\u4ed9\u6392\u884c\u548c\u90a3\u6052 \u6cb3\u4e09\u4f5b\u987a\u7740\u6b21\u5e8f\u5730\u4e09\u4eba\u6bd4\u5212\u3002 \u3000\u3000\u65e0\u6068\u751f\u6de1\u6de1\u7b11\u9053\uff1a\u201c\u8fd9\u6562\u60c5\u597d\u3002\u201d \u3000\u3000\u8bf4\u7740\u4fbf\u63a0\u5230\u90a3\u4e2a\u6ee1\u9762\u76b1\u7eb9\u7684\u76d8\u706f\u5b5a\u5c14\u524d\u9762\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u5927\u5bb6\u7ad9\u5b9a\u65b9\u4f4d\uff0c\u4ef0\u5929\u54c8\u54c8\u957f\u7b11\uff0c\u9707\u5f97\u767e\u7b0b\u6797\u7c0c\u7c0c\u4f5c\u54cd\uff0c\u7136\u540e\u4ed6\u5927\u559d\u4e00\u58f0\uff1a \u201c\u4e0a\u554a\u2014\u2014\u201d \u3000\u3000\u8bf4\u7f62\u5f53\u5148\u4e00\u62f3\u6325\u5411\u4f2f\u7f57\u5404\u7b54\u3002 \u3000\u3000\u6167\u5927\u5e08\u548c\u65e0\u6781\u5c9b\u4e3b\u4e5f\u5404\u81ea\u52a8\u4e86\u624b\u3002\u8f9b\u6377\u5728\u4e00\u65c1\u76ee\u7779\u5f53\u4eca\u8fd9\u9876\u5c16\u513f\u7684\u516d\u5927\u9ad8\u624b\u62fc\u6597\uff0c\u771f\u662f \u76ee\u7729\u53e3\u5446\u3002 \u3000\u3000\u84e6\u7136\uff0c\u90a3\u91d1\u9c81\u5384\u9f13\u52b2\u4e00\u638c\u5bf9\u51c6\u8f9b\u6377\u5288\u6765\uff0c\u60f3\u662f\u4ed6\u60f3\u8d77\u594e\u5c71\u4e4b\u8d25\uff0c\u4e0d\u7981\u6012\u4e0a\u5fc3\u5934\uff0c\u6068\u4e0d\u5f97 \u4e00\u53e3\u5c06\u8f9b\u6377\u541e\u5c06\u4e0b\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u88ab\u4ed6\u638c\u98ce\u60ca\u8d77\uff0c\u5de6\u8db3\u6a2a\u8de8\uff0c\u53f3\u638c\u547c\u5730\u8fce\u5c06\u4e0a\u53bb\uff0c\u53ea\u542c\u5f97\u7830\u7684\u4e00\u58f0\uff0c\u8f9b\u6377\u88ab\u9707\u9000\u534a \u6b65\uff01 \u3000\u3000\u867d\u7136\u5982\u6b64\uff0c\u90a3\u91d1\u9c81\u5384\u5df1\u611f\u5230\u5927\u5403\u4e00\u60ca\u4e86\uff0c\u56e0\u4e3a\u4ed6\u53d1\u89c9\u8f9b\u6377\u6bd4\u4e4b\u4e00\u6708\u524d\u529f\u529b\u53c8\u6709\u8fdb\u6b65\uff01 \u3000\u3000\u8fd9\u4e00\u638c\u4ee4\u8f9b\u6377\u8bb0\u8d77\u81ea\u5df1\u529f\u529b\u6bd4\u5bf9\u65b9\u7565\u900a\uff0c\u5f53\u4e0b\u4e00\u9519\u8eab\uff0c\u53cc\u638c\u5982\u98ce\u98d8\u7d6e\u822c\u6325\u51fa\u4e09\u638c\u3002 \u3000\u3000\u8fd9\u4e09\u638c\u770b\u4f3c\u8f7b\u6d6e\uff0c\u5b9e\u5219\u67d4\u52b2\u6697\u542b\uff0c\u91d1\u9c81\u5384\u4f55\u7b49\u529f\u529b\uff0c\u4e00\u770b\u4e4b\u4e0b\u4e86\u7136\u4e8e\u80f8\uff0c\u53cd\u8eab\u659c\u649e\uff0c\u53cc \u638c\u53d8\u800c\u4e3a\u722a\uff0c\u76f4\u6263\u8f9b\u6377\u8109\u95e8\uff0c\u53ea\u5f85\u8f9b\u6377\u4e00\u95ea\u8eab\uff0c\u4ed6\u53f3\u80a9\u5c31\u80fd\u76f4\u649e\u8f9b\u6377\u201c\u534e\u76d6\u201d\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u6599\u4ed6\u51fa\u6b64\u602a\u62db\uff0c\u4e00\u65f6\u65e0\u6cd5\u7834\u89e3\uff0c\u53ea\u597d\u65bd\u51fa\u6167\u5927\u5e08\u7684\u7edd\u4e16\u795e\u529f\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\u4e00\u95ea\u800c \u51fa\u3002 \u3000\u3000\u53ea\u542c\u201c\u53ee\u201d\u7136\u4e00\u58f0\uff0c\u8f9b\u6377\u62bd\u51fa\u4e86\u957f\u5251\u2014\u2014 \u3000\u3000\u91d1\u9c81\u5384\u4e00\u89c1\u4ed6\u4eae\u51fa\u957f\u5251\uff0c\u65b0\u4ec7\u65e7\u6068\u4e00\u8d77\u6d8c\u4e0a\u5fc3\u5934\uff0c\u5237\u7684\u4e5f\u5c06\u8170\u95f4\u957f\u7d22\u53d6\u4e86\u4e0b\u6765\uff01 \u3000\u3000\u8f9b\u6377\u4e00\u9886\u957f\u5251\uff0c\u5636\u5730\u4e00\u58f0\u76f4\u53d6\u91d1\u9c81\u5384\u7684\u5c0f\u8179\uff0c\u91d1\u9c81\u5384\u957f\u7d22\u4e0d\u5b88\u5148\u653b\uff0c\u957f\u7d22\u62a1\u5f97\u7b14\u76f4\u5730\u70b9 \u5411\u8f9b\u6377\u7709\u5fc3\u2014\u2014 \u3000\u3000\u8f9b\u6377\u6602\u7136\u4e0d\u9000\uff0c\u5934\u9888\u4e00\u95f4\u4e4b\u95f4\uff0c\u624b\u4e2d\u5251\u5f0f\u5df2\u4e00\u8fde\u653b\u51fa\u4e94\u5f0f\uff0c\u5168\u662f\u866c\u679d\u5251\u5f0f\u4e2d\u7684\u7cbe\u534e\u3002 \u3000\u3000\u91d1\u9c81\u5384\u957f\u7d22\u5b9b\u5982\u51fa\u6d1e\u86df\u9f99\uff0c\u7ffb\u6eda\u4e4b\u95f4\uff0c\u8fde\u6d88\u5e26\u6253\u5730\u5316\u53bb\u4e86\u8f9b\u6377\u7684\u51cc\u5389\u653b\u52bf\uff01 \u3000\u3000\u8fd9\u56db\u5bf9\u4e00\u6d41\u7684\u9ad8\u624b\u5728\u5c0f\u6222\u5c9b\u4e0a\u5c55\u5f00\u4e86\u751f\u6b7b\u7684\u62fc\u6597\uff0c\u65e5\u5934\u5df1\u6e10\u6e10\u897f\u504f\uff0c\u77f3\u7b0b\u4e00\u6839\u6839\u7684\u5f71\u5b50 \u4e5f\u7531\u77ed\u800c\u957f\uff01 \u3000\u3000\u8f9b\u6377\u548c\u91d1\u9c81\u5384\u7684\u62fc\u6597\u4e0d\u51fa\u767e\u62db\u5c31\u81ea\u52a8\u5730\u505c\u4e0b\u4e86\u624b\uff0c\u539f\u6765\u4ed6\u4eec\u540c\u65f6\u4e3a\u6052\u6cb3\u4e09\u4f5b\u53ca\u4e16\u5916\u4e09\u4ed9 \u7684\u62fc\u6597\u5438\u5f15\u4f4f\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u5782\u7740\u957f\u5251\uff0c\u51dd\u89c6\u7740\u90a3\u4e09\u5bf9\u7cbe\u5f69\u7edd\u4f26\u7684\u53ae\u6740\uff0c\u5c24\u5176\u662f\u5e73\u51e1\u4e0a\u4eba\u548c\u6167\u5927\u5e08\uff0c\u4ed6\u4eec\u90fd\u66fe\u4f20 \u8fc7\u8f9b\u6377\u6b66\u827a\uff0c\u8f9b\u6377\u89c1\u8fd9\u4e24\u4f4d\u76d6\u4e16\u5947\u4eba\u5404\u5c06\u81ea\u5df1\u7684\u5f97\u610f\u7edd\u5b66\u65bd\u5f97\u5a01\u98ce\u51db\u51db\uff0c\u4e0d\u7531\u66f4\u662f\u5fc3\u7729\u76ee \u9a70\uff01 \u3000\u3000\u5f53\u5e74\u8f9b\u6377\u77a7\u5e73\u51e1\u4e0a\u4eba\u548c\u6167\u5927\u5e08\u5728\u5c0f\u6222\u5c9b\u4e0a\u62fc\u6597\uff0c\u662f\u4e00\u5927\u8fdb\u76ca\uff0c\u8fd9\u65f6\u518d\u770b\u4e24\u4eba\u548c\u6052\u6cb3\u4e09\u4f5b \u4e2d\u7684\u4f2f\u7f57\u5404\u7b54\u53ca\u91d1\u4f2f\u80dc\u5937\u62fc\u6597\uff0c\u66f4\u662f\u5927\u6709\u5fc3\u5f97\u3002 \u3000\u3000\u53ea\u662f\u5e73\u51e1\u4e0a\u4eba\u53cc\u638c\u5ffd\u5288\u5ffd\u6307\uff0c\u4e00\u65f6\u201c\u5927\u884d\u5341\u5f0f\u201d\u6742\u5728\u638c\u6307\u4e4b\u4e2d\uff0c\u4e00\u65f6\u53c8\u6362\u6210\u65b0\u8fd1\u6240\u521b\u7684 \u201c\u7a7a\u7a7a\u62f3\u6cd5\u201d\uff0c\u66f4\u6709\u65f6\u7528\u51fa\u4e00\u4e9b\u4e0d\u77e5\u540d\u4f46\u5999\u7edd\u4eba\u5bde\u7684\u602a\u62db\u2014\u2014\u8fd9\u4e9b\u5b8c\u5168\u662f\u5e94\u5bf9\u5f53\u65f6\u60c5\u51b5\u6240\u4e34 \u65f6\u521b\u51fa\u6765\u7684\u3002 \u3000\u3000\u518d\u770b\u90a3\u6167\u5927\u5e08\uff0c\u624b\u4e0a\u6240\u7528\u7684\u638c\u6cd5\u6b63\u662f\u4f20\u7ed9\u8f9b\u6377\u800c\u672a\u4f20\u5168\u7684\u638c\u6cd5\uff0c\u811a\u4e0b\u5374\u914d\u5408\u7740\u66fc\u5999\u76d6\u4e16 \u7684\u201c\u9752\u4e91\u6b65\u6cd5\u201d\uff0c\u90a3\u91d1\u4f2f\u80dc\u5937\u7684\u8f7b\u529f\u867d\u7136\u5feb\u5f97\u51fa\u5947\uff0c\u4f46\u662f\u5728\u4e34\u654c\u4e4b\u9645\u5374\u8fdc\u4e0d\u53ca\u8bd8\u6469\u6b65\u6cd5\u795e \u5999\uff01 \u3000\u3000\u90a3\u91d1\u9c81\u5384\u4e5f\u540c\u6837\u5168\u795e\u6ce8\u89c6\u7740\u573a\u4e2d\u62fc\u6597\uff0c\u53ea\u662f\u4ed6\u4e43\u662f\u51dd\u795e\u6ce8\u89c6\u7740\u4ed6\u7684\u5e08\u7236\u548c\u5e08\u4f2f\uff0c\u540c\u6837 \u7684\uff0c\u4ed6\u4e3a\u4ed6\u5e08\u7236\u4eec\u7684\u795e\u5947\u6b66\u529f\u611f\u5230\u6fc0\u52a8\u4e07\u5206\uff01 \u3000\u3000\u5ffd\u7136\uff0c\u91d1\u9c81\u5384\u4ece\u5e08\u4f2f\u4f2f\u7f57\u5404\u7b54\u7684\u4e00\u62db\u4e2d\u609f\u51fa\u4e00\u4e0b\u5999\u7740\uff0c\u4ed6\u5927\u559d\u4e00\u58f0\uff0c\u7acb\u523b\u8fd0\u7528\u51fa\u6765\u5f80\u8f9b \u6377\u8eab\u4e0a\u62db\u547c\u8fc7\u6765\uff0c\u8f9b\u6377\u6b63\u5728\u5168\u795e\u8d2f\u6ce8\u770b\u5e73\u51e1\u4e0a\u4eba\u7684\u4e00\u8bb0\u602a\u62db\uff0c\u5ffd\u611f\u52b2\u98ce\u538b\u80cc\uff0c\u4ed6\u77a7\u4e5f\u4e0d\u77a7\u5730 \u53cd\u624b\u4e00\u628a\u529b\u51fa\uff0c\u91d1\u9c81\u5384\u867d\u7136\u62db\u5f0f\u5947\u7edd\uff0c\u4f46\u7adf\u88ab\u4ed6\u8feb\u5f97\u6a2a\u8de8\u534a\u6b65\uff01 \u3000\u3000\u8fd9\u6b63\u662f\u4ed6\u4ece\u5e73\u51e1\u4e0a\u4eba\u62db\u4e2d\u609f\u51fa\u7684\u4e00\u8bb0\u3002\u4e24\u4eba\u4ea4\u624b\u4e00\u62db\u540e\uff0c\u5404\u81ea\u7adf\u7136\u4f4f\u624b\uff0c\u4e00\u9f50\u56de\u8eab\u6ce8\u89c6 \u573a\u4e2d\u62fc\u6597\uff0c\u6bcf\u5f53\u4e24\u4eba\u609f\u51fa\u4e00\u62db\uff0c\u5c31\u56de\u8eab\u5bf9\u62c6\u4e00\u62db\uff0c\u7136\u540e\u53c8\u81ea\u6f5c\u5fc3\u601d\u7d22\u3002\u8fd9\u573a\u60ca\u5929\u52a8\u5730\u7684\u62fc \u6597\uff0c\u5012\u4fbf\u5b9c\u4e86\u8fd9\u4e24\u4e2a\u9752\u5e74\u9ad8\u624b\uff0c\u4e24\u4eba\u5168\u529b\u65bd\u4e3a\uff0c\u5c24\u5176\u5f97\u76ca\u975e\u6d45\u3002 \u3000\u3000\u90a3\u4e09\u5bf9\u4eba\u62fc\u62c6\u5343\u62db\u4e4b\u540e\uff0c\u8f9b\u6377\u81ea\u5df1\u8fd8\u4e0d\u89c9\u5f97\uff0c\u91d1\u9c81\u5384\u5374\u662f\u60ca\u6012\u4e0d\u5df2\uff0c\u4ed6\u53ea\u89c9\u8f9b\u6377\u6bcf\u53d1\u4e00 \u62db\uff0c\u529f\u529b\u4f3c\u4e4e\u5c31\u589e\u8fdb\u51e0\u5206\uff0c\u81ea\u5df1\u867d\u7136\u5168\u529b\u82e6\u601d\uff0c\u4f46\u662f\u4ecd\u7136\u8ffd\u4e0d\u4e0a\u8f9b\u6377\u7684\u8fdb\u5883\uff01 \u3000\u3000\u77f3\u6797\u9635\u4e1c\u9762\uff0c\u65e0\u6781\u5c9b\u4e3b\u548c\u76d8\u706f\u5b5a\u5c14\u4e5f\u6253\u51fa\u4e86\u771f\u529b\uff0c\u4e24\u4eba\u5404\u81ea\u65bd\u51fa\u5e73\u751f\u7edd\u6280\uff0c\u7740\u7740\u62a2\u653b\u3002 \u3000\u3000\u65e0\u6068\u751f\u5e74\u9f84\u867d\u5728\u4e16\u5916\u4e09\u4ed9\u4e2d\u7b97\u662f\u6700\u5c0f\uff0c\u4f46\u662f\u4ed6\u66fe\u670d\u98df\u4ed9\u679c\uff0c\u529f\u529b\u4e4b\u9ad8\uff0c\u6bd4\u8d77\u5e73\u51e1\u4e0a\u4eba\u6765 \u4e5f\u4e0d\u591a\u8ba9\uff0c\u90a3\u76d8\u706f\u5b5a\u5c14\u4e00\u8fde\u6362\u4e86\u4e03\u79cd\u638c\u6cd5\uff0c\u59cb\u7ec8\u65e0\u6cd5\u62a2\u5f97\u5148\u673a\u3002 \u3000\u3000\u53ea\u542c\u5f97\u65e0\u6068\u751f\u5927\u559d\u4e00\u58f0\uff0c\u5355\u638c\u5982\u98ce\u6363\u51fa\u2014\u2014 \u3000\u3000\u76d8\u706f\u5b5a\u5c14\u5355\u811a\u9489\u7acb\u77f3\u7b0b\u4e0a\uff0c\u8eab\u4f53\u4e00\u5708\uff0c\u53e6\u4e00\u53cc\u817f\u5982\u94c1\u68cd\u822c\u76d8\u65cb\u626b\u51fa\uff0c\u52b2\u98ce\u4e4b\u76db\uff0c\u7adf\u4ee4\u65e0 \u6068\u751f\u8863\u8882\u98d8\u8d77\u3002 \u3000\u3000\u65e0\u6068\u751f\u5927\u5486\u4e00\u58f0\uff1a\u201c\u6765\u5f97\u597d\u2014\u2014\u201d \u3000\u3000\u8eab\u8eaf\u8dc3\u5728\u7a7a\u4e2d\uff0c\u53cc\u6ee8\u53cc\u4e91\u822c\u62b9\u4e0b\uff0c\u5f85\u76d8\u706f\u5b5a\u5c14\u6325\u62f3\u76f8\u67b6\uff0c\u4ed6\u7a81\u7136\u5355\u638c\u6025\u6363\u800c\u4e0b\u2014\u2014 \u3000\u3000\u76d8\u706f\u5b57\u5c14\u7528\u8db3\u529b\u9053\uff0c\u5f80\u4e0a\u4e00\u677f\u3002 \u3000\u3000\u65e0\u6068\u751f\u5fc3\u4e2d\u4e00\u51db\uff0c\u731b\u7136\u4f7f\u7528\u4e00\u5f0f\uff1a\u201c\u60ca\u9e3f\u4e00\u77a5\u201d\uff0c\u53cc\u811a\u8fde\u73af\u4ea4\u76f8\u8e22\u51fa\u3002 \u3000\u3000\u65e0\u6068\u751f\u4e00\u7740\u5931\u7b56\uff0c\u7adf\u8d70\u9669\u62db\u3002\u76d8\u706f\u5b5a\u5c14\u6d78\u6deb\u6b66\u5b66\u5df2\u6709\u516b\u5341\u4f59\u5e74\uff0c\u8111\u7b4b\u8f6c\u90fd\u4e0d\u8f6c\uff0c\u624b\u6307\u75be \u4f38\uff0c\u70b9\u5411\u65e0\u6068\u751f\u811a\u8e1d\u4e0a\u7684\u201c\u516c\u5b59\u7a74\u201d\u3002 \u3000\u3000\u8fd9\u201c\u516c\u5b59\u7a74\u201d\u4f4d\u4e8e\u811a\u8e81\u9aa8\u95f4\uff0c\u5047\u82e5\u8ba9\u4eba\u70b9\u7740\uff0c\u4e00\u6761\u817f\u7acb\u523b\u5f97\u5e9f\uff0c\u65e0\u6839\u751f\u4e34\u5371\u4e0d\u4e71\uff0c\u811a\u677f \u7528\u529b\u5f80\u5185\u4e00\u626d\uff0c\u62a4\u7740\u2018\u516c\u5b59\u7a74\u2019\u7528\u811a\u80cc\u810a\u90e8\u8fce\u5411\u76d8\u706f\u5b57\u5c14\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u7528\u5f97\u53c8\u5947\u53c8\u9669\uff0c\u76d8\u706f\u5b5a\u5c14\u4e00\u70b9\u8d70\u7a7a\uff0c\u6536\u638c\u4e0d\u4f4f\uff0c\u4e00\u638c\u6253\u5728\u65e0\u6068\u751f\u7684\u978b\u4e0a\uff0c\u8eab\u8eaf\u4e0d \u7531\u4e00\u6643\u3002 \u3000\u3000\u65e0\u6068\u751f\u7528\u4e86\u5982\u6b64\u9669\u62db\uff0c\u624d\u7b97\u5f3a\u6e21\u8fc7\u96be\u5173\uff0c\u4e0d\u7531\u5927\u6012\uff0c\u51b7\u51b7\u5bf9\u76d8\u706f\u828b\u5c14\u9053\u201c\u597d\u2014\u2014\u54fc\uff01\u201d \u3000\u3000\u8bdd\u65b9\u51fa\u53e3\uff0c\u84e6\u7136\u60f3\u5230\u5bf9\u65b9\u6839\u672c\u4e0d\u61c2\u6c49\u8bed\uff0c\u8bf4\u4e86\u4e5f\u542c\u4e0d\u61c2\uff0c\u4e0d\u7531\u54d1\u7136\u5931\u7b11\u3002 \u3000\u3000\u65e0\u6068\u751f\u4f55\u66fe\u5982\u6b64\u72fc\u72c8\u8fc7\uff0c\u5fc3\u4e2d\u6012\u706b\u9e70\u817e\uff0c\u4e00\u5f0f\u201c\u5e73\u6b65\u9752\u4e91\u201d\uff0c\u8eab\u8eaf\u6597\u7136\u4e0a\u5347\uff0c\u76d8\u65cb\u5728\u76d8 \u706f\u5b5a\u5c14\u4e0a\u7a7a\uff0c\u84e6\u5730\u91cc\u7528\u4e00\u5f0f\u201c\u6cf0\u5c71\u593a\u9635\u201d\u4e4b\u5f0f\uff0c\u5411\u4e0b\u865a\u865a\u4e00\u6309\u3002 \u3000\u3000\u76d8\u706f\u5b5a\u5c14\u4ece\u65e0\u6068\u751f\u795e\u8272\u95f4\u4fbf\u53ef\u770b\u51fa\u5bf9\u65b9\u5df2\u662f\u771f\u706b\u4e0a\u5347\uff0c\u51fa\u624b\u4e4b\u95f4\uff0c\u6015\u662f\u81f4\u547d\u7684\u62db\u5f0f\uff0c\u4e0d \u6562\u5927\u610f\u5206\u6beb\uff0c\u51dd\u795e\u4ee5\u5f85\u3002 \u3000\u3000\u65e0\u6068\u751f\u4e00\u5f0f\u5288\u51fa\uff0c\u76d8\u706f\u5b5a\u5c14\u54c8\u54c8\u4e00\u7b11\uff0c\u53cc\u62f3\u5408\u63e1\uff0c\u5411\u4e0a\u7a7a\u51b2\u4e00\u62f3\uff0c\u770b\u4ed6\u7684\u62db\u5f0f\u5012\u6709\u70b9\u50cf \u4e2d\u539f\u7684\u201c\u51b2\u5929\u70ae\u201d\u7684\u62db\u5f0f\uff0c\u4f46\u5a01\u529b\u5374\u662f\u5927\u5f97\u51fa\u5947\u3002 \u3000\u3000\u65e0\u6068\u751f\u548c\u4ed6\u4e00\u62fc\uff0c\u8eab\u8eaf\u6597\u7136\u88ab\u4e00\u80a1\u5927\u529b\u95ee\u4e0a\u4e00\u6258\uff0c\u4e0d\u4f46\u81ea\u5df1\u653b\u654c\u7684\u529f\u529b\u5168\u5931\uff0c\u53cd\u88ab\u56de\u656c \u8fc7\u6765\u3002 \u3000\u3000\u518d\u770b\u90a3\u76d8\u706f\u5b5a\u5c14\u65f6\uff0c\u53ea\u89c1\u4ed6\u8eab\u5b50\u5fae\u5fae\u6447\u6643\uff0c\u663e\u7136\u6bd4\u81ea\u5df1\u8fd8\u8981\u5403\u4e8f\uff0c\u4e0d\u7531\u8c6a\u6c14\u4e0a\u51b2\uff0c\u54c8\u54c8 \u4e00\u7b11\uff0c\u771f\u6c14\u7adf\u5728\u7a7a\u4e2d\u4e00\u6563\u4e00\u6536\uff0c\u5df2\u8f6c\u90a3\u6df7\u6d4a\u7684\u4e00\u53e3\u771f\u6c14\u4e3a\u4e4b\u6e05\u7eaf\uff0c\u53cc\u638c\u4e00\u5212\uff0c\u518d\u5ea6\u5411\u4e0b\u865a\u865a \u4e00\u6309\u3002 \u3000\u3000\u76d8\u706f\u5b5a\u5c14\u5df2\u77e5\u65e0\u6068\u751f\u4e43\u662f\u8981\u5728\u7a7a\u4e2d\u548c\u81ea\u5df1\u786c\u62fc\uff0c\u8fd9\u6837\u6253\u4e0b\u53bb\uff0c\u5bf9\u65b9\u53ef\u4ee5\u628a\u8eab\u8eaf\u5347\u9ad8\u5316\u5f00 \u52b2\u9053\uff0c\u81ea\u5df1\u53ea\u597d\u88ab\u4eba\u5bb6\u50cf\u9489\u9489\u5b50\u4e00\u6837\u9489\u5165\u571f\u4e2d\u4e86\u3002 \u3000\u3000\u5fc3\u5ff5\u4e00\u52a8\uff0c\u7adf\u8e0f\u516b\u5366\u65b9\u4f4d\u800c\u884c\u3002 \u3000\u3000\u540c\u65f6\u95f4\u53cc\u62f3\u4e5f\u4e0d\u65ad\u5f80\u4e0a\u51b2\u631a\uff0c\u62b5\u5fa1\u65e0\u6068\u751f\u4e0b\u538b\u4e4b\u5f0f\uff0c\u800c\u81ea\u5df1\u8fb9\u884c\u8fb9\u6253\uff0c\u4e5f\u628a\u65e0\u6068\u751f\u7684\u529b \u9053\u5316\u4e8e\u65e0\u5f62\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u8fde\u6570\u6b21\uff0c\u65e0\u6068\u751f\u6253\u5f97\u6027\u8d77\uff0c\u559d\u58f0\uff1a\u201c\u597d\uff0c\u518d\u63a5\u4e00\u62db\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u5e76\u975e\u662f\u548c\u90a3\u76d8\u706f\u5b5a\u5c14\u8bf4\u7684\u5012\u662f\u4e3a\u81ea\u5df1\u5410\u6c14\u5f00\u58f0\u52a9\u5f3a\u62f3\u52bf\u7814\u53d1\uff0c\u662f\u4ee5\u8bdd\u58f0\u5982\u559d\uff0c\u76f4 \u53ef\u88c2\u77f3\u3002\u559d\u58f0\u624d\u843d\uff0c\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u53cc\u62f3\u4e00\u5de6\u4e00\u53f3\uff0c\u8fd0\u7528\u201c\u53cc\u96f7\u704c\u8033\u201d\u4e4b\u5f0f\uff0c\u5408\u51fb\u76d8\u706f\u5b5a\u5c14 \u5929\u7075\u4e24\u4fa7\u3002 \u3000\u3000\u76d8\u706f\u5b57\u5c14\u867d\u7136\u53cc\u62f3\u4e00\u9519\uff0c\u6709\u82e5\u4e24\u6761\u51fa\u6d1e\u957f\u86df\uff0c\u7adf\u4e00\u5408\u4e00\u5206\u5730\uff0c\u53cd\u51fb\u65e0\u6068\u751f\u5939\u51fb\u7684\u4e24\u6761\u624b \u81c2\u4e0a\u7684\u201c\u8098\u7a74\u201d\u3002 \u3000\u3000\u65e0\u6068\u751f\u65e9\u6599\u6709\u6b64\uff0c\u95ea\u7535\u95f4\u4e00\u7f29\u624b\uff0c\u5de6\u624b\u5f53\u80f8\uff0c\u53f3\u624b\u59c6\u6307\u4e2d\u6307\u51fa\u6709\u82e5\u888b\u5f62\uff0c\u75be\u5212\u800c\u8fc7\u3002 \u3000\u3000\u8fd9\u4e00\u5f0f\u4e43\u662f\u65e0\u6068\u751f\u5e73\u751f\u7edd\u5b66\uff0c\u5524\u4f5c\u201c\u767d\u9e7f\u6302\u888b\u201d\uff0c\u6709\u4e0d\u53ef\u601d\u8bae\u7684\u5a01\u529b\u3002\u8bf4\u65f6\u8fdf\u90a3\u65f6\u5feb\uff0c \u65e0\u6068\u751f\u53f3\u624b\u95ea\u7535\u75be\u51fa\uff0c\u4ece\u53f3\u624b\u4e0b\u4fa7\u7ffb\u51fa\u4e00\u53e9\uff0c\u5411\u76d8\u706f\u5b5a\u5c14\u80f8\u524d\u201c\u5e02\u4e95\u201d\u7a74\u9053\u3002 \u3000\u3000\u5175\u6cd5\u4e0a\u6709\u6240\u8c13\uff1a\u201c\u77e5\u5df1\u77e5\u5f7c\uff0c\u767e\u6218\u767e\u80dc\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u65e9\u6599\u76d8\u706f\u5b5a\u5c14\u4f1a\u5206\u51fb\u81ea\u5df1\u5de6\u53f3\u624b\uff0c\u504f\u7a7a\u51fa\u4e2d\u76d8\uff0c\u662f\u4ee5\u9884\u5148\u51fa\u62db\u8d70\u4e2d\u5bab\uff0c\u5165\u6d2a\u95e8\uff0c \u4e0b\u6740\u624b\uff01 \u3000\u3000\u76d8\u706f\u5b5a\u5c14\u4e0d\u865e\u6709\u6b64\uff0c\u53cc\u624b\u5206\u5f00\uff0c\u62db\u5f0f\u7528\u8001\uff0c\u6536\u62db\u4e0d\u53ca\uff0c\u773c\u770b\u53ea\u6709\u7b49\u6b7b\uff0c\u4f46\u4ed6\u5982\u6b64\u529f\u529b\u4e5f \u4e0d\u80af\u5c31\u6b64\u670d\u8bdb\uff0c\u53cc\u817f\u8fde\u62ac\uff0c\u8e22\u5411\u65e0\u6068\u751f\u5c06\u843d\u5730\u7684\u4e0b\u76d8\u3002 \u3000\u3000\u65e0\u6068\u751f\u51b7\u51b7\u4e00\u7b11\uff0c\u53f3\u624b\u653b\u52bf\u4e0d\u53d8\uff0c\u53cc\u8db3\u4e00\u8361\uff0c\u4e00\u53e3\u771f\u6c14\u704c\u6ce8\uff0c\u5f80\u4e0a\u4e00\u7ffb\uff0c\u5de7\u5999\u7684\u95ea\u8fc7\u76d8 \u706f\u5b5a\u7684\u653b\u52bf\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u4e00\u53e3\u771f\u6c14\u5374\u8981\u704c\u6ce8\u4e0b\u76d8\uff0c\u53c8\u8981\u704c\u6ce8\u653b\u654c\uff0c\u975e\u662f\u65e0\u6068\u751f\u6b64\u7b49\u529f\u529b\uff0c\u4e5f\u4e0d\u8db3\u4ee5\u6210\u529f\u3002 \u3000\u3000\u65e0\u6068\u751f\u773c\u770b\u4e00\u631a\u6210\u529f\uff0c\u800c\u5927\u5c0f\u6222\u5c9b\u4e3b\u597d\u4f3c\u548c\u90a3\u4e8c\u4f5b\u6b63\u5bf9\u6253\u5f97\u96be\u5206\u96be\u89e3\uff0c\u5fc3\u4e2d\u4e0d\u7531\u8e0c\u8e87\u6ee1 \u5fd7\u3002 \u3000\u3000\u84e6\u7136\uff0c\u4ed6\u5ffd\u89c9\u80f8\u53e3\u4e00\u7d27\uff0c\u771f\u529b\u98de\u5feb\u7684\u6563\u53bb\uff0c\u4e0d\u7531\u5927\u5403\u4e00\u60ca\uff0c\u5fae\u5fae\u563f\u4e86\u58f0\uff0c\u52aa\u529b\u63d0\u8d77\u4e00\u53e3 \u771f\u6c14\uff0c\u4ed6\u4e0d\u63d0\u4e5f\u7f62\uff0c\u4e00\u63d0\u4e4b\u4e0b\uff0c\u80f8\u53e3\u7adf\u662f\u4e00\u9635\u5267\u75db\u3002 \u3000\u3000\u8eab\u4f53\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u7830\u7684\u8dcc\u5728\u5730\u4e0b\u3002 \u3000\u3000\u76d8\u706f\u5b5a\u5c14\u672c\u5df2\u65e0\u6551\uff0c\u4f46\u4ecd\u5b58\u7740\u4e24\u8d25\u4e4b\u610f\u81ea\u7136\u7684\u4f38\u624b\u4e00\u5f39\uff0c\u51fb\u5411\u65e0\u6068\u751f\u5589\u5499\uff0c\u8fd9\u4e00\u5f0f\u4e43\u662f \u653b\u6240\u5fc5\u6551\uff0c\u800c\u4ed6\u81ea\u5df1\u4e5f\u521a\u597d\u53ef\u4ee5\u9003\u51fa\u751f\u547d\u3002 \u3000\u3000\u65e0\u6068\u751f\u771f\u529f\u6597\u7136\u4e4b\u95f4\u6563\u5931\uff0c\u654c\u4eba\u653b\u5230\u81ea\u5df1\u8981\u5bb3\uff0c\u4e00\u79cd\u6781\u5176\u81ea\u7136\u7684\u53cd\u5e94\u4fc3\u4f7f\u4ed6\u52aa\u529b\u5f80\u540e\u79fb \u4e86\u4e00\u70b9\u3002 \u3000\u3000\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u76d8\u706f\u5b5a\u5c14\u4e00\u51fb\u8d70\u7a7a\uff0c\u4ec5\u62c2\u8fc7\u65e0\u6068\u751f\u7684\u8863\u895f\u3002 \u3000\u3000\u76d8\u706f\u828b\u5c14\u4e5f\u4e0d\u77e5\u65e0\u6068\u751f\u4e3a\u4f55\u6597\u7136\u4e4b\u95f4\u7adf\u4f1a\u5982\u6b64\uff0c\u4ed6\u81ea\u6301\u8eab\u4efd\uff0c\u4e0d\u597d\u518d\u4e0a\u524d\u4e0b\u624b\uff0c\u5446\u5728\u4e00 \u65c1\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u56db\u5bf9\u4ea4\u624b\u7684\u4eba\u90fd\u4e0b\u624b\u6765\uff0c\u65e0\u6068\u751f\u5012\u5728\u5730\u4e0a\uff0c\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6570\u6b21 \u60f3\u63d0\u771f\u6c14\uff0c\u4f46\u90fd\u5f92\u52b3\u65e0\u529f\uff0c\u4e0d\u7531\u6025\u5f97\u51b7\u6c57\u76f4\u5192\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6765\u5230\u4ed6\u7684\u8eab\u8fb9\uff0c\u6309\u4e86\u6309\u4ed6\u7684\u80cd\u606f\uff0c\u5374\u4e1d\u6beb\u6ca1\u6709\u5f02\u72b6\uff0c\u4e0d\u7531\u675f\u624b\u65e0\u7b56\uff0c\u800c\u6167\u5927\u5e08 \u548c\u8f9b\u6377\u4e5f\u90fd\u5fc3\u6025\u5341\u5206\u3002 \u3000\u3000\u90a3\u8fb9\u6052\u6cb3\u4e09\u4f5b\u548c\u91d1\u9c81\u5384\u56db\u4eba\uff0c\u4e5f\u662f\u5446\u5446\u7684\u53d1\u6014\uff0c\u53ea\u6709\u91d1\u4f2f\u80dc\u5937\u7684\u8111\u7b4b\u98de\u5feb\u7684\u8f6c\u52a8\u7740\u3002 \u3000\u3000\u6052\u6cb3\u4e09\u4f5b\u4e2d\u4ee5\u91d1\u4f2f\u80dc\u5937\u9a6c\u9996\u662f\u77bb\uff0c\u867d\u7136\u4ed6\u662f\u4f2f\u7f57\u5404\u7b54\u7684\u5e08\u5f1f\uff0c\u4f46\u4e3a\u4eba\u751a\u5de5\u5fc3\u8ba1\uff0c\u662f\u4ee5\u53ef \u8bf4\u662f\u4e09\u4eba\u4e2d\u7684\u667a\u56ca\u3002\u4ed6\u72b9\u8c6b\u4e0d\u51b3\uff0c\u5fc3\u4e2d\u4e0d\u65ad\u5fd6\u9053\uff1a\u201c\u8fd9\u4fca\u7f8e\u7684\u4e2d\u5e74\u4e66\u751f\u4e0d\u77e5\u662f\u600e\u4e48\u56de\u4e8b\u60c5\uff0c \u770b\u4ed6\u90a3\u5012\u5730\u4e0d\u8d77\u7684\u6a21\u6837\u5012\u50cf\u662f\u766b\u75c7\u5b50\u53d1\u4f5c\u4e00\u822c\uff0c\u5047\u82e5\u54b1\u4eec\u6b64\u65f6\u53d1\u52a8\u653b\u52bf\u2014\u2014\u563f\u5ff5\u5934\u95ea\u8fc7\u4ed6\u7684 \u8111\u9645\uff0c\u4ed6\u8138\u4e0a\u73b0\u51fa\u4e00\u4e2a\u72f0\u72de\u7684\u7b11\u5bb9\uff0c\u4f46\u662f\u63a5\u7740\u53c8\u60f3\u9053\uff1a\u201c\u4e0d\u8fc7\uff0c\u5047\u82e5\u6211\u4eec\u4e58\u673a\u653b\u51fb\uff0c\u81f3\u591a\u4e0d \u8fc7\u628a\u90a3\u4e2d\u5e74\u4e66\u751f\u6253\u6b7b\uff0c\u5f04\u5f97\u597d\u7684\u8bdd\u9876\u591a\u52a0\u4e0a\u90a3\u59d3\u8f9b\u7684\u5c0f\u5b50\uff0c\u800c\u90a3\u4e24\u4e2a\u5927\u5c0f\u6222\u5c9b\u4e3b\u5374\u662f\u5948\u4f55\u4ed6 \u4eec\u4e0d\u5f97\uff0c\u563f\uff0c\u8fd9\u5bf9\u54b1\u4eec\u540d\u5934\u53ef\u5927\u6709\u635f\u5931\uff0c\u771f\u53ef\u8c13\u5f97\u4e0d\u507f\u5931\u2014\u2014\u201d \u3000\u3000\u987b\u77e5\u6052\u6cb3\u4e09\u4f5b\u867d\u7136\u6ca1\u6709\u9053\u4e49\u53ef\u8a00\uff0c\u4f46\u662f\u5e73\u751f\u6781\u7231\u60dc\u7fbd\u6bdb\uff0c\u662f\u4ee5\u91d1\u4f2f\u80dc\u5937\u72b9\u8c6b\u4e0d\u51b3\uff0c\u8138\u4e0a \u795e\u8272\u9634\u6674\u4e0d\u5b9a\u3002 \u3000\u3000\u90a3\u8fb9\u5927\u6222\u5c9b\u4e3b\u5341\u5206\u7126\u6025\uff0c\u8fd0\u7528\u5185\u529b\u6253\u4eba\u65e0\u6068\u751f\u4f53\u5185\uff0c\u4f46\u6548\u679c\u5374\u662f\u5b8c\u5168\u76f8\u53cd\u7684\uff0c\u800c\u4e14\u65e0\u6068 \u751f\u8fd8\u50cf\u662f\u53d7\u4e86\u5f88\u5927\u7684\u75db\u82e6\uff0c\u8fd9\u5012\u662f\u5927\u6222\u5c9b\u4e3b\u6599\u60f3\u4e0d\u5230\u7684\u3002 \u3000\u3000\u6167\u5927\u5e08\u7ad9\u4e00\u65c1\uff0c\u6ce8\u89c6\u7740\u65e0\u6839\u751f\u7684\u8138\u8272\u3002\u89c9\u5f97\u4ed6\u9762\u4e0a\u82cd\u767d\u4e4b\u95f4\u8fd8\u5fae\u5fae\u6cdb\u51fa\u4e4c\u6e05\uff0c\u6167\u5927\u5e08\u89c1 \u8bc6\u591a\u5e7f\uff0c\u5fc3\u4e2d\u6709\u6570\uff0c\u77e5\u9053\u5fc5\u662f\u4ec0\u4e48\u5185\u75be\u7a81\u53d1\uff0c\u4f46\u4ed6\u5374\u4e5f\u4e0d\u660e\u767d\u4ee5\u65e0\u6068\u751f\u8fd9\u7b49\u529f\u529b\u5c82\u4f1a\u6709\u5185\u4f24 \u4f0f\u5728\u4f53\u5185\u800c\u4ed6\u81ea\u5df1\u90fd\u4e0d\u660e\u767d\uff1f \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u4e00\u518d\u6c89\u541f\uff0c\u7ec8\u4e8e\u6717\u6717\u9053\uff1a\u201c\u5582\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4f60\u4eec\u5df2\u6709\u4e00\u4eba\u75c5\u53d1\uff0c\u54b1\u4eec\u6052\u6cb3\u4e09\u4f5b \u5c82\u80fd\u518d\u548c\u4f60\u7b49\u8fc7\u62db\uff0c\u563f\u2014\u2014\u662f\u4ee5\u4eca\u65e5\u2014\u2014\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u5927\u6222\u5c9b\u4e3b\u5df2\u77e5\u4ed6\u610f\uff0c\u5fc3\u7ec6\u4ed6\u660e\u767d\u51b3\u8ba8\u4e0d\u4e86\u597d\u53bb\uff0c\u4e0d\u5982\u89c1\u597d\u4fbf\u6536\uff0c\u518d\u6765\u4e00\u6bb5 \u987a\u6c34\u4eba\u60c5\uff0c\u4e0d\u7531\u54c8\u54c8\u4e00\u7b11\u3002\u91d1\u4f2f\u80dc\u5937\u5fae\u5fae\u4e00\u987f\uff0c\u53c8\u9053\uff1a\u201c\u662f\u4ee5\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4fbf\u6b64\u4f5c\u7f62\uff0c\u4f60\u4eec\u4e2d \u56fd\u6709\u53e5\u8bdd\uff1a\u2018\u9752\u5c71\u4e0d\u6539\uff0c\u7eff\u6c34\u957f\u6d41\u2019\uff0c\u563f\uff0c\u54b1\u4eec\u65e5\u540e\u6709\u5565\u518d\u6765\u8ba8\u6559\uff0c\u4eca\u65e5\u591a\u8c22\u56db\u4f4d\u7684\u9ad8\u62db \u5566\uff0c\u5c31\u6682\u4f5c\u522b\u2014\u2014\u201d \u3000\u3000\u4ed6\u7528\u6c49\u8bed\u548c\u5929\u7afa\u5492\u8bed\u5404\u8bf4\u4e00\u6b21\uff0c\u8fd8\u5f97\u610f\u5730\u5e72\u7b11\u4e86\u6570\u58f0\uff0c\u4e00\u6446\u624b\uff0c\u4fbf\u60f3\u548c\u5176\u4f59\u4e09\u4eba\u4e00\u540c\u8d70 \u53bb\u3002 \u3000\u3000\u6167\u5927\u5e08\u5ffd\u7136\u51b7\u51b7\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u80fd\u8d70\u5f97\u51fa\u53bb\u4e48\u2014\u2014\u201d \u3000\u3000\u91d1\u4f2f\u80dc\u4f5b\u4e00\u6014\uff0c\u6253\u91cf\u4e00\u4e0b\u56db\u5468\uff0c\u201c\u5f52\u5143\u53e4\u9635\u201d\u4ed6\u4eec\u662f\u9886\u6559\u8fc7\u7684\uff0c\u679c\u662f\u5965\u5999\uff0c\u867d\u662f\u5728\u77f3\u9635 \u9635\u4e0a\uff0c\u4f46\u4ecd\u662f\u832b\u7136\u4e0d\u77e5\u5982\u4f55\u843d\u811a\u3002 \u3000\u3000\u6167\u5927\u5e08\u51b7\u7b11\u4e00\u58f0\uff0c\u4e0d\u53d1\u4e00\u8bed\uff0c\u7eb5\u8eab\u4fbf\u5f80\u524d\u8d70\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u4f5b\u7b49\u4eba\u77e5\u5979\u662f\u5728\u9886\u81ea\u5df1\u51fa\u9635\uff0c\u4e0d\u6562\u518d\u51fa\u5927\u8a00\uff0c\u8ddf\u5728\u5979\u8eab\u540e\uff0c\u4e00\u540c\u8d70\u51fa\u53e4\u9635\u53bb\u3002 \u3000\u3000\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u671b\u7740\u4e94\u4eba\u80cc\u5f71\uff0c\u54c8\u54c8\u5927\u7b11\uff0c\u76f4\u5230\u4e94\u4eba\u53bb\u5f97\u8fdc\u4e86\uff0c\u624d\u6536\u5f80\u7b11\u58f0\u5bf9\u65e0\u6839\u751f \u9053\uff1a\u201c\u8001\u5f1f\u5230\u5e95\u751a\u600e\u4e48\u56de\u4e8b\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u52c9\u58f0\u9053\uff1a\u201c\u8fd9\u786e\u662f\u592a\u5947\u4e86\uff0c\u8fde\u6211\u81ea\u5df1\u90fd\u4e0d\u660e\u767d\uff0c\u4e0d\u8fc7\u6211\u731c\u60f3\u53ea\u6709\u4e00\u4e2a\u53ef\u80fd\uff0c\u4e0a\u4eba \u53ef\u542c\u5230\u8fc7\u4e16\u4e0a\u6709\u4e00\u79cd\u6bd2\u836f\u53ef\u4ee5\u85cf\u5728\u4eba\u7684\u4f53\u5185\u8bb8\u4e45\u8bb8\u4e45\u6f5c\u4f0f\u4e0d\u53d1\uff0c\u800c\u5230\u5b9a\u671f\u624d\u7a81\u7136\u53d1\u4f5c\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5f39\u5f39\u8111\u95e8\u6c89\u601d\u9053\uff1a\u201c\u95ee\u522b\u7684\u6211\u5012\u77e5\u9053\u4e00\u4e9b\uff0c\u8fd9\u2018\u6bd2\u2019\u6211\u53ef\u662f\u4e00\u7a8d\u4e0d\u901a\u2014\u2014\u201d \u3000\u3000\u4ed6\u8fb9\u8bf4\u8fb9\u60f3\uff0c\u84e6\u7136\u53eb\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u542c\u8bf4\u6709\u4e00\u79cd\u6bd2\u836f\uff0c\u53eb\u4f5c\u2018\u78a7\u7389\u65ad\u80a0\u2019\uff0c\u4fbf\u6709\u8fd9\u6837\u5b50\u7684 \u7279\u6027\u201d \u3000\u3000\u65e0\u6068\u751f\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u90a3\u2018\u78a7\u7389\u65ad\u80a0\u2019\u53ef\u662f\u8272\u5448\u78a7\u7eff\uff0c\u65e0\u5473\u65e0\u55c5\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53eb\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6b63\u662f\uff0c\u4f60\u53ef\u662f\u4e2d\u4e86\u8fd9\u79cd\u6bd2\uff1f\u201d \u3000\u3000\u65e0\u6068\u751f\u70b9\u4e86\u70b9\u5934\u9053\uff1a\u201c\u4e0a\u4eba\u53ef\u66fe\u542c\u8fc7\u2018\u7389\u9aa8\u9b54\u2019\u4e4b\u540d\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u5475\uff0c\u6211\u77e5\u9053\u3002\u5c31\u662f\u90a3\u4e2a\u6d77\u76d7\u5934\u5b50\u2014\u2014\u201d \u3000\u3000\u8bfb\u8005\u4e00\u5b9a\u8fd8\u8bb0\u5f97\uff0c\u5f53\u65e5\u65e0\u6068\u751f\u4e0e\u7389\u9aa8\u9b54\u624b\u9ec4\u5b50\u6c99\u603b\u8235\u4e3b\u6210\u4e00\u9752\u5728\u6d77\u4e0a\u76f8\u9047\u65f6\uff0c\u65e0\u6781\u5c9b\u4e3b \u66fe\u6beb\u4e0d\u5728\u4e4e\u5730\u996e\u4e0b\u5bf9\u65b9\u4e00\u676f\u7eff\u8272\u7684\u9187\u9152\u2014\u2014 \u3000\u3000\u65e0\u6068\u751f\u628a\u8fd9\u6bb5\u5f80\u4e8b\u8bf4\u4e86\u51fa\u6765\uff0c\u4ed6\u5598\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6211\u60f3\u5982\u679c\u662f\uff0c\u90a3\u5fc5\u662f\u7531\u4e8e\u90a3\u676f\u9152\u4e86\u2014\u2014 \u563f\uff0c\u8fd9\u4f1a\u513f\u6211\u4e0d\u66fe\u5984\u52a8\u771f\u529b\u53cd\u800c\u597d\u53d7\u4e86\u4e00\u4e9b\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6414\u4e86\u6414\u5149\u5934\uff0c\u4e00\u65e0\u53ef\u65bd\u3002 \u3000\u3000\u65e5\u8272\u5df2\u66ae\uff0c\u7ea2\u65e5\u897f\u6c89\uff0c\u6731\u7ea2\u8272\u7684\u6ce2\u5149\u968f\u7740\u6c79\u6d8c\u7684\u665a\u6f6e\u4e0a\u4e0b\u95ea\u52a8\uff0c\u5c0f\u6222\u5c9b\u4e0a\u6240\u6709\u7684\u767e\u7b0b\u90fd \u6210\u4e86\u4e00\u534a\u7d2b\u8272\u4e00\u534a\u91d1\u9ec4\u8272\uff0c\u9ad8\u9ad8\u5730\u77d7\u7acb\u5728\u665a\u971e\u4e2d\u3002 \u3000\u3000\u65e0\u6068\u751f\u9ed8\u9ed8\u6697\u81ea\u8fd0\u529f\uff0c\u4f46\u662f\u4e00\u53e3\u771f\u6c14\u59cb\u7ec8\u63d0\u4e0d\u8d77\u6765\uff0c\u4ed6\u751a\u81f3\u80fd\u611f\u5230\u8eab\u4e2d\u7684\u6bd2\u4e0d\u4ec5\u53d1\u4f5c\uff0c \u800c\u4e14\u5df1\u7ecf\u5f00\u59cb\u8513\u5ef6\u5f00\u4e86\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u65e0\u8a00\u5730\u5446\u7ad9\u5728\u4e00\u65c1\uff0c\u4e5f\u662f\u675f\u624b\u65e0\u7b56\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4e00\u4e2a\u5ff5\u5934\u5982\u95ea\u7535\u822c\u7a7f\u8fc7\u8f9b\u6377\u7684\u8111\u6d77\uff0c\u4ed6\u6ee1\u9762\u559c\u5bb9\u5730\u53eb\u51fa\u4e86\u58f0\u2014\u2014 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c\u5341\u4e8c\u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c\u5341\u4e8c\u7ae0 \u3000\u3000\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u867d\u662f\u529f\u529b\u76d6\u4e16\uff0c\u4f46\u5bf9\u4e8e\u4e0b\u6bd2\u89e3\u6bd2\u8fd9\u4e00\u95f4\u5374\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c\u65e0\u6068\u751f\u4e2d\u6bd2\u770b \u6765\u975e\u6d45\uff0c\u4f46\u4ed6\u4e5f\u53ea\u6709\u65c1\u89c2\uff0c\u675f\u624b\u65e0\u7b56\u3002 \u3000\u3000\u53ea\u662f\u89c1\u8f9b\u6377\u5927\u559c\u8fc7\u671b\uff0c\u7cbe\u795e\u4e0d\u7531\u4e00\u632f\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u9149\uff0c\u662f\u89e3\u836f\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u6447\u4e86\u6447\u5934\uff0c\u6b22\u58f0\u9053\uff1a\u201c\u8fd9\u4e1c\u897f\uff0c\u6211\u770b\u8fd9\u4e1c\u897f\u51c6\u6210\u3002\u201d \u3000\u3000\u8bf4\u7740\u638f\u51fa\u90a3\u6000\u4e2d\u7684\u4e00\u672c\u4e66\u6765\uff0c\u626c\u4e86\u626c\u9053\uff1a\u201c\u6709\u4e86\u8fd9\u672c\u4e66\uff0c\u4ec0\u4e48\u6bd2\u4e0d\u8be6\u7ec6\u7684\u8bb0\u8f7d\u5728\u4e0a\u9762\u2014 \u2014\u201d \u3000\u3000\u6562\u60c5\u4ed6\u90a3\u4e00\u518c\u4e66\u6b63\u662f\u6bd2\u672f\u5929\u4e0b\u79f0\u9996\u7684\u5317\u541b\u201c\u6bd2\u541b\u91d1\u4e00\u9e4f\u201d\u6bd5\u751f\u5fc3\u8840\u6240\u4f5c\u7684\u6bd2\u7ecf\uff0c\u90a3\u4e00\u65e5 \u91d1\u4e00\u9e4f\u7684\u5973\u513f\u91d1\u6885\u9f84\u628a\u8fd9\u672c\u6bd2\u7ecf\u7559\u7ed9\u8f9b\u6377\uff0c\u8f9b\u6377\u4e66\u4e0d\u79bb\u8eab\uff0c\u4f46\u4e00\u6765\u8fde\u906d\u5947\u9047\uff0c\u4e8c\u6765\u6025\u4e8b\u7f20 \u8eab\uff0c\u6839\u672c\u65e0\u6687\u53bb\u770b\u5b83\uff0c\u800c\u4e14\u51e0\u4e4e\u90fd\u5fd8\u4e86\u3002 \u3000\u3000\u8fd9\u5f53\u513f\u7075\u673a\u4e00\u52a8\uff0c\u6709\u4e86\u6bd2\u7ecf\uff0c\u4ec0\u4e48\u6bd2\u8fd8\u4e0d\u662f\u8fce\u5203\u800c\u89e3\uff1f \u3000\u3000\u5927\u6222\u5c9b\u4e3b\u63a5\u8fc7\u201c\u6bd2\u7ecf\u201d\uff0c\u770b\u4e86\u770b\u5c01\u76ae\uff0c\u5ff5\u9053\uff1a\u201c\u6bd2\u7ecf\u2014\u2014\u91d1\u4e00\u9e4f\u4f5c\uff0c\u554a\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u63a5\u53e3\u9053\uff1a\u201c\u91d1\u8001\u524d\u8f88\u90a3\u65e5\u5728\u6c99\u9f99\u576a\u4ee5\u6bd2\u653b\u6bd2\u6740\u6b7b\u90a3\u7389\u9aa8\u9b54\uff0c\u8fd9\u672c\u4e66\u53ef\u662f\u4ed6\u8001\u4eba\u5bb6\u6bd5 \u751f\u5fc3\u8840\u54e9\u2014\u2014\u201d \u3000\u3000\u5927\u6222\u5c9b\u4e3b\u4e0d\u7531\u60ca\u8be7\u51fa\u58f0\u3002 \u3000\u3000\u8f9b\u6377\u53c8\u9053\uff1a\u201c\u91d1\u8001\u524d\u8f88\u6bd2\u672f\u5929\u4e0b\u65e0\u53cc\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u63a5\u8fc7\u6bd2\u7ecf\uff0c\u8fc5\u901f\u5730\u7ffb\u5f00\u770b\u53bb\u3002 \u3000\u3000\u8fd9\u6bd2\u7ecf\u4e0a\u5305\u7f57\u4e07\u8c61\uff0c\u5b87\u5185\u6d77\u5916\u6bcf\u4e00\u79cd\u6bd2\u8349\u3001\u6bd2\u86c7\uff0c\u751a\u81f3\u662f\u6709\u6bd2\u7684\u751f\u7269\uff0c\u51e0\u4e4e\u5168\u90e8\u5728\u5185\uff0c \u76f4\u770b\u5f97\u8f9b\u6377\u5fc3\u60ca\u80c6\u6218\uff0c\u4f46\u5fc3\u4e2d\u5374\u7531\u8877\u7684\u4f69\u670d\u90a3\u53c8\u766b\u53c8\u8c10\u7684\u8001\u4eba\u2014\u2014\u91d1\u4e00\u9e4f\u3002 \u3000\u3000\u8f9b\u6377\u5f88\u5feb\u7684\u6d4f\u89c8\u8fc7\u53bb\uff0c\u90a3\u6bd2\u7ecf\u4e2d\u8fd8\u4e0d\u65f6\u52a0\u4e0a\u4e00\u4e24\u5e45\u63d2\u56fe\uff0c\u8f9b\u6377\u8d8a\u770b\u8d8a\u60ca\uff0c\u5fc3\u4e2d\u4e00\u52a8\u3002 \u3000\u3000\u987b\u77e5\u8f9b\u6377\u4e3a\u4eba\u6027\u672c\u653e\u8fbe\uff0c\u5929\u751f\u597d\u5b66\uff0c\u662f\u4ee5\u5e76\u4e0d\u4ee5\u4e3a\u6bd2\u672f\u4e43\u662f\u90aa\u9053\u65c1\u95e8\u7684\uff0c\u5fc3\u4e2d\u4e00\u52a8\uff0c\u773c \u89c1\u8fd9\u6bd2\u7ecf\u4e0a\u771f\u662f\u201c\u6bd2\u201d\u4e0d\u80dc\u6536\uff0c\u7adf\u52a8\u5ff5\u8981\u5b66\u4e60\u4e0b\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u5ff5\u4e4b\u95f4\uff0c\u5df2\u4e0b\u51b3\u5fc3\uff0c\u5f88\u5feb\u7684\u7ffb\u7740\u4e66\uff0c\u5374\u59cb\u7ec8\u4e0d\u89c1\u6709\u90a3\u4ec0\u4e48\u201c\u78a7\u7389\u65ad\u80a0\u201d\u7684\u540d\u79f0\u6216\u89e3 \u6cd5\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u9759\u5750\u4e00\u8fb9\uff0c\u4ed4\u7ec6\u8c03\u8fd0\u771f\u6c14\uff0c\u8138\u4e0a\u795e\u8272\u4e00\u7247\u6f20\u7136\uff0c\u5012\u662f\u5e73\u51e1\u4e0a\u4eba\u5f88\u7126\u6025\u7684 \u671b\u7740\u8f9b\u6377\u3002 \u3000\u3000\u53c8\u8fc7\u4e86\u4e00\u523b\uff0c\u6167\u5927\u5e08\u4e5f\u5df2\u56de\u6765\uff0c\u8f9b\u6377\u5df1\u5dee\u4e0d\u591a\u5feb\u8981\u628a\u4e00\u518c\u4e66\u90fd\u7ffb\u5b8c\uff0c\u4f46\u4ecd\u6ca1\u6709\u6211\u7740\u8fd9 \u201c\u78a7\u7389\u65ad\u80a0\u201d\u7684\u540d\u5b57\u3002 \u3000\u3000\u5306\u5306\u53c8\u662f\u6570\u9875\uff0c\u773c\u770b\u6bd2\u7ecf\u53ea\u5269\u4e0b\u6700\u540e\u51e0\u9875\uff0c\u5ffd\u7136\uff0c\u8f9b\u6377\u7cbe\u795e\u4e00\u632f\uff0c\u6562\u60c5\u90a3\u4e66\u4e0a\u7aef\u6b63\u7684\u5199 \u7740\u4e8c\u4e2a\u5b57\uff1a\u201c\u7279\u4f8b\u201d\u3002 \u3000\u3000\u201c\u2018\u7389\u9aa8\u9b54\u2019\u65e2\u7136\u7528\u6765\u6bd2\u65e0\u6068\u751f\uff0c\u5fc5\u975e\u666e\u901a\u7684\u6bd2\u7269\uff0c\u8fd9\u7279\u4f8b\u4e2d\u591a\u534a\u4f1a\u6709\u2014\u2014\u201d \u3000\u3000\u4ed6\u5fd6\u9053\uff0c\u4e00\u9762\u4ed4\u7ec6\u7684\u5bfb\u627e\u3002 \u3000\u3000\u84e6\u7136\uff0c\u56db\u4e2a\u5927\u5b57\u5448\u73b0\u5728\u773c\u524d\uff0c\u53ef\u4e0d\u662f\u201c\u78a7\u7389\u65ad\u80a0\u201d\u56db\u5b57\uff1f \u3000\u3000\u8f9b\u6377\u7981\u4e0d\u4f4f\u5927\u58f0\u53eb\u9053\uff1a\u201c\u6709\u4e86\u6709\u4e86\uff0c\u8fd9\u73a9\u610f\u770b\u6765\u6765\u5934\u4e0d\u5c0f\u5462\u2014\u2014\u201d \u3000\u3000\u4ed6\u63a5\u7740\u4fbf\u7167\u4e66\u4e0a\u5ff5\u9053\uff1a\u201c\u78a7\u7389\u65ad\u80a0\uff0c\u539f\u672c\u4e3a\u690d\u7269\uff0c\u4e2d\u571f\u7edd\u8ff9\uff0c\u5f62\u4e3a\u56db\u53f6\u4e00\u854a\uff0c\u65e0\u679c\uff0c\u4e3a \u6b64\u690d\u7269\u4e4b\u8349\u6c41\u2026\u2026\u201d \u3000\u3000\u4ed6\u98de\u5feb\u7684\u5ff5\u7740\uff0c\u4e5f\u61d2\u5f97\u7ba1\u8fd9\u79cd\u4ecb\u7ecd\uff0c\u8df3\u8fc7\u6570\u884c\uff0c\u627e\u90a3\u6cbb\u7597\u7684\u65b9\u6cd5\uff0c\u53c8\u7ee7\u7eed\u5ff5\u4e0b\u53bb\uff0c\u9053\uff1a \u201c\u2026\u2026\u6bd2\u6027\u6781\u6d53\uff0c\u4e0e\u4e00\u7acb\u6b65\u65ad\u80a0\u5e76\u79f0\u201c\u53cc\u65ad\u80a0\u201d\uff0c\u4e14\u6f5c\u4f0f\u6027\u6781\u5927\uff0c\u4f18\u4e8e\u4f53\u5185\uff0c\u4efb\u5185\u529f\u9ad8\u6df1\uff0c \u4ea6\u4e0d\u6613\u5bdf\u89c9\uff0c\u6b64\u7269\u4e43\u5929\u5730\u95f4\u6700\u4e3a\u5389\u5bb3\u4e4b\u7269\u2026\u2026\u201d \u3000\u3000\u201c\u6cbb\u7597\u4e4b\u65b9\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u4ec5\u6709\u4e00\u7269\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5ff5\u5230\u8fd9\u91cc\uff0c\u8010\u4e0d\u4f4f\u58f0\u97f3\u4e5f\u5fae\u73b0\u7d27\u4fc3\uff0c\u663e\u793a\u4ed6\u4e5f\u5341\u5206\u7d27\u5f20\uff0c\u9ad8\u58f0\u7ee7\u7eed\u9053\uff1a \u3000\u3000\u201c\u4ec5\u6709\u4e00\u7269\uff0c\u5373\u2018\u706b\u7389\u51b0\u5fc3\u2019\uff0c\u6b64\u7269\u5168\u5929\u4e0b\u53ea\u6709\u5317\u71d5\u7136\u5c71\u9876\u4ea7\u6709\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u795e\u8272\u9aa4\u7136\u4e00\u53d8\uff0c\u5fd6\u9053\uff1a\u201c\u71d5\u7136\u5c71\u8ddd\u6b64\u5f53\u6709\u4e07\u91cc\u4e4b\u9065\uff0c\u83ab\u8bf4\u73b0\u5728\u6025\u6025\u9700\u8981\uff0c\u4e00\u65f6 \u4e0d\u80fd\u8d76\u5230\uff0c\u5c31\u662f\u80fd\u591f\u5230\u8fbe\uff0c\u4e5f\u4e0d\u89c1\u5f97\u5c31\u80fd\u7acb\u523b\u5bfb\u7740\u2014\u2014\u201d \u3000\u3000\u5374\u542c\u90a3\u8f9b\u6377\u58f0\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u6cd5\u2014\u2014\u201d \u3000\u3000\u539f\u6765\u5f53\u65e5\u91d1\u4e00\u9e4f\u4f5c\u6b64\u4e66\u65f6\uff0c\u6bcf\u4e00\u79cd\u6bd2\u7269\uff0c\u90fd\u6709\u7cbe\u7ec6\u8be6\u6ce8\u89e3\u91ca\uff0c\u800c\u4e14\u8fd8\u52a0\u4ee5\u81ea\u5df1\u6570\u5341\u5e74\u7684 \u5fc3\u5f97\u3002 \u3000\u3000\u8fd9\u78a7\u7389\u65ad\u80a0\u662f\u91d1\u4e00\u9e4f\u665a\u5e74\u624d\u5f97\u77e5\uff0c\u5f53\u65f6\u9664\u4e86\u706b\u7389\u51b0\u5fc3\u5916\uff0c\u786e\u5b9e\u7f3a\u4e4f\u4ed6\u6cd5\u533b\u6cbb\uff0c\u4f46\u91d1\u4e00\u9e4f \u6df1\u77e5\u706b\u7389\u51b0\u5fc3\u4e3e\u4e16\u96be\u5bfb\uff0c\u662f\u4ee5\u51b3\u5fc3\u518d\u627e\u51fa\u53e6\u5916\u4e00\u4e2a\u6cd5\u5b50\u3002 \u3000\u3000\u51ed\u4ed6\u5728\u6bd2\u4e2d\u6df7\u4e86\u4e00\u751f\uff0c\u52a0\u4e0a\u6781\u6df1\u7684\u5185\u529f\u548c\u6781\u9ad8\u7684\u5929\u8d44\uff0c\u7ec8\u4e8e\u5728\u6f5c\u5fc3\u601d\u7d22\u4e0b\u9886\u609f\u4e86\u53e6\u4e00\u4e2a \u65b9\u6cd5\uff0c\u4e8e\u662f\u4ed6\u7acb\u523b\u628a\u6b64\u6cd5\u5199\u5165\u6bd2\u7ecf\u4e0a\u9762\u3002 \u3000\u3000\u8f9b\u6377\u6b22\u58f0\u7684\u628a\u90a3\u65b9\u5b50\u8bf4\u4e86\u51fa\u6765\uff0c\u5e73\u51e1\u4e0a\u4eba\u4e0d\u7531\u201c\u554a\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u6562\u60c5\u8fd9\u4e2a\u65b9\u6cd5\u662f\u592a\u5371\u9669\u4e86\u4e00\u4e9b\u3002 \u3000\u3000\u539f\u6765\uff0c\u5927\u51e1\u8fd9\u79cd\u6f5c\u4f0f\u6027\u7684\u5927\u6bd2\u6027\uff0c\u5728\u6bd2\u53d1\u7684\u65f6\u5019\uff0c\u4e5f\u6108\u5feb\u6377\uff0c\u5047\u82e5\u5728\u5b83\u5c1a\u672a\u6563\u5165\u8840\u8109\uff0c \u8fd8\u662f\u6574\u4e2a\u5728\u4f53\u5185\u4e4b\u65f6\uff0c\u7531\u4e00\u4e2a\u5185\u5bb6\u7edd\u9876\u9ad8\u624b\u7528\u5185\u529b\u628a\u5b83\u903c\u51fa\u4fbf\u53ef\u65e0\u59a8\u3002 \u3000\u3000\u4f46\u662f\u8fd9\u201c\u78a7\u7389\u65ad\u80a0\u201d\u4e00\u5165\u4f53\u5185\uff0c\u4fbf\u4f1a\u805a\u5728\u4eba\u4f53\u4e2d\u6700\u91cd\u59bb\u7684\u8840\u8109\u4e2d\uff0c\u90a3\u5c31\u662f\u8bf4\u5728\u4ece\u9876\u5fc3\u5230 \u5fc3\u810f\u7684\u8fd9\u4e00\u5e26\u3002 \u3000\u3000\u5982\u679c\u8981\u628a\u5b83\u903c\u51fa\u4f53\u5185\uff0c\u975e\u8981\u9876\u5fc3\u4e0a\u7740\u624b\u4e0d\u53ef\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u3001\u6167\u5927\u5e08\u3001\u8f9b\u6377\u90fd\u662f\u4e00\u7b49\u4e00\u7684\u597d\u624b\uff0c\u600e\u4f1a\u4e0d\u77e5\u5047\u82e5\u8981\u52a8\u624b\u903c\u6bd2\uff0c\u90a3\u975e\u8981\u5728\u9876\u5fc3 \u201c\u6ce5\u4e38\u5bab\u201d\u4e0a\u62cd\u4e00\u638c\u3002 \u3000\u3000\u201c\u6ce5\u4e38\u5bab\u201d\u4e43\u662f\u4eba\u751f\u7a74\u9053\u7684\u603b\u7ed3\u4e4b\u5730\uff0c\u4e00\u638c\u62cd\u4e0b\uff0c\u90a3\u4e48\u7acb\u523b\u529f\u529b\u5168\u5931\uff0c\u6709\u5982\u51e1\u4eba\uff0c\u800c\u4e14 \u75db\u82e6\u4e07\u5206\u3002 \u3000\u3000\u4e2d\u6bd2\u8005\u529f\u529b\u5373\u6d88\u4e0b\u4e00\u70b9\u672c\u80fd\u5185\u5728\u7684\u6f5c\u529b\u518d\u4e5f\u4e0d\u80fd\u7ef4\u6301\uff0c\u201c\u78a7\u7389\u65ad\u80a0\u201d\u4e4b\u6bd2\u7acb\u523b\u8fc5\u901f\u7684\u6563 \u5f00\u3002 \u3000\u3000\u8981\u5728\u8fd9\u65f6\uff0c\u89c2\u597d\u65f6\u523b\uff0c\u518d\u62cd\u4e00\u638c\uff0c\u63a5\u7740\u7528\u5185\u529b\u6e21\u4eba\u4f53\u5185\uff0c\u597d\u751f\u903c\u51fa\u4f53\u5916\uff0c\u624d\u80fd\u6563\u5374\u6b64 \u6bd2\u3002 \u3000\u3000\u201c\u6ce5\u4e38\u201d\u7a74\u5982\u6b64\u91cd\u8981\uff0c\u5047\u82e5\u4e0b\u624b\u7684\u4eba\u4e00\u5206\u5931\u624b\uff0c\u4e2d\u6bd2\u8005\u7acb\u523b\u6b7b\u53bb\uff0c\u53cd\u4e4b\u5047\u82e5\u4e0b\u624b\u8f7b\u4e86\u4e00 \u5206\uff0c\u90a3\u4e2d\u6bd2\u8005\u4e0d\u4f46\u767d\u53d7\u4e00\u6b21\u75db\u82e6\uff0c\u800c\u4e14\u5bf9\u4ed6\u5185\u529b\u4fee\u4e3a\u4e5f\u6709\u635f\u5bb3\u3002 \u3000\u3000\u8fd9\u4e2a\u6cd5\u5b50\u867d\u7136\u80fd\u6210\uff0c\u4f46\u592a\u8fc7\u5371\u9669\uff0c\u662f\u4ee5\u8fde\u5e73\u51e1\u4e0a\u4eba\u3001\u6167\u5927\u5e08\u6b64\u7b49\u4eba\u7269\uff0c\u4e5f\u4e0d\u7531\u60ca\u8be7\u51fa \u58f0\u3002 \u3000\u3000\u5f53\u5e74\u6bd2\u541b\u91d1\u4e00\u9e4f\u9886\u609f\u6b64\u65b9\uff0c\u4fbf\u60f3\u4e16\u4e0a\u7edd\u65e0\u6b64\u7b49\u529f\u529b\u7684\u4eba\uff0c\u662f\u4ee5\u8fd9\u6cd5\u5b50\u5fc5\u7136\u4f9d\u65e7\u662f\u65e0\u6cd5 \u529f\uff0c\u4f46\u4ed6\u8fd8\u662f\u5c06\u5b83\u5199\u5728\u6bd2\u7ecf\u4e0a\uff0c\u7b97\u505a\u662f\u4ed6\u4e00\u751f\u7814\u7a76\u6bd2\u5b66\u7684\u4e00\u70b9\u513f\u5fc3\u5f97\uff01 \u3000\u3000\u5e73\u51e1\u571f\u4eba\u662f\u5168\u5fc3\u4f69\u670d\u8fd9\u4f5c\u6bd2\u7ecf\u7684\u91d1\u4e00\u9e4f\uff0c\u89c1\u8bc6\u7adf\u662f\u5982\u6b64\u591a\u5e7f\uff0c\u5c31\u8fde\u6167\u5927\u5e08\u6b64\u7b49\u597d\u5f3a\u4eba \u7269\uff0c\u4e5f\u4e0d\u7531\u5fc3\u6298\uff01 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u82e6\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u8001\u5c3c\u5a46\uff0c\u8fd9\u5012\u662f\u4e00\u4e2a\u96be\u9898\u5462\uff1f\u201d \u3000\u3000\u6167\u5927\u5e08\u9ed8\u7136\u70b9\u9996\u9053\uff1a\u201c\u5047\u82e5\u662f\u54b1\u4eec\u4e8c\u4eba\u8fde\u624b\u7684\u8bf4\u8bdd\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u90a3\u6050\u6015\u66f4\u9669\u2014\u2014\u201d \u3000\u3000\u6167\u5927\u5e08\u70b9\u4e86\u70b9\u5934\uff0c\u8f9b\u6377\u660e\u767d\u4ed6\u4eec\u4e43\u662f\u60f3\u4e8c\u4eba\u8fde\u624b\uff0c\u5185\u529b\u4e0d\u82e5\u4e00\u4e2a\u7eaf\u719f\uff0c\u66f4\u6613\u51fa\u9669\uff0c\u81ea\u5df1 \u529f\u529b\u8fd8\u5dee\uff0c\u53ea\u5f97\u9ed8\u7136\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u90a3\u53ea\u5f97\u8d70\u7740\u77a7\u4e86\uff0c\u8001\u5c3c\u5a46\uff0c\u4f60\u52a8\u624b\uff1f\u201d \u3000\u3000\u6167\u5927\u5e08\u5fae\u5fae\u6447\u9996\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u8fd9\u5f53\u53e3\u513f\u4e0a\u54b1\u4eec\u4e0d\u5fc5\u518d\u5ba2\u6c14\uff0c\u8001\u5b9e\u8bf4\uff0c\u8d2b\u5c3c\u7684\u5185\u529b\u4fee \u4e3a\uff0c\u81ea\u8ba4\u6bd4\u4f60\u8981\u5dee\u4e0a\u4e00\u7b79\u54e9\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e0d\u518d\u8a00\u8bed\uff0c\u8f6c\u8eab\u5bf9\u9759\u5750\u7684\u65e0\u6068\u751f\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u89c9\u5f97\u597d\u4e9b\u5417\uff1f\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u6717\u6717\u4e00\u7b11\uff0c\u6253\u65ad\u5e73\u51e1\u4e0a\u4eba\u7684\u8bf4\u513f\uff1a\u201c\u4e0a\u4eba\u4e0d\u5fc5\u7126\u6025\uff0c\u6211\u65e0\u6068\u751f\u518d\u4e0d\u6210\uff0c\u8fd9\u82e6\u513f\u8fd8 \u633a\u5f97\u4f4f\u3002\u201d \u3000\u3000\u4ed6\u663e\u7136\u662f\u52c9\u5f3a\u800c\u53d1\uff0c\u8bed\u8c03\u8f6e\u5230\u6700\u540e\uff0c\u5df2\u7136\u5fae\u5fae\u98a4\u6296\u3002\u6717\u6717\u7b11\u58f0\uff0c\u4e5f\u6108\u6765\u6108\u6296\uff0c\u800c\u5fae\u5e26\u5c16 \u58f0\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6df1\u77e5\u4ed6\u7684\u6027\u683c\uff0c\u54c8\u54c8\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u771f\u6709\u4f60\u7684\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u53f3\u624b\u5927\u8896\u4e00\u95f4\u4e4b\u4e0b\uff0c\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5fc3\u4e2d\u6709\u6570\uff0c\u8fd9\u4e00\u4efd\u5dee\u4e8b\u53ef\u662f\u5341\u5206\u8270\u96be\u7684\uff0c\u53ea\u8981\u4e0b\u624b\u5fae\u5fae\u4e00\u9519\u52b2\u9053\uff0c\u4fbf\u662f\u9057\u61be\u7ec8 \u751f\u3002 \u3000\u3000\u4ed6\u77e5\u9053\u4ee5\u65e0\u6068\u751f\u6b64\u7b49\u529f\u529b\uff0c\u81ea\u5df1\u4e00\u638c\u62cd\u4e0b\uff0c\u4ed6\u5fc5\u4f1a\u6781\u81ea\u7136\u7684\u751f\u51fa\u4e00\u80a1\u53cd\u6297\u7684\u529b\u9053\uff0c\u867d\u7136 \u662f\u6781\u5c0f\u91cf\u7684\uff0c\u4f46\u4e5f\u53ef\u80fd\u81f4\u4ee5\u5e73\u51e1\u4e0a\u4eba\u5931\u624b\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u5728\u65e0\u6068\u751f\u8bf4\u8bdd\u4e4b\u9645\uff0c\u7a81\u7136\u4e0b\u624b\u3002 \u3000\u3000\u8fd9\u4e00\u5355\u662f\u5e73\u51e1\u4e0a\u4eba\u7684\u771f\u529f\u529b\uff0c\u529b\u9053\u662f\u4e09\u5206\u53d1\uff0c\u4e03\u5206\u6536\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u6709\u82e5\u95ea\u7535\uff0c\u5927\u8896\u624d \u6446\uff0c\u4e00\u638c\u5df2\u7136\u63a5\u5b9e\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6df1\u77e5\u8f7b\u91cd\uff0c\u4e00\u53cd\u5e73\u65e5\u5b09\u7b11\u7684\u6478\u6837\uff0c\u4e00\u638c\u624d\u89e6\u53ca\u65e0\u6068\u751f\u201c\u6ce5\u4e38\u201d\uff0c\u60a0\u7136\u5f80\u5916\u4e00\u95ea \u4e00\u5708\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e00\u89e6\u4e4b\u4e0b\uff0c\u529b\u9053\u5168\u6536\uff0c\u65e0\u6068\u751f\u4f46\u89c9\u9876\u5fc3\u4e00\u9707\uff0c\u5168\u8eab\u771f\u529b\u8fc5\u901f\u7684\u6563\u53bb\uff0c\u4e00\u70b9\u771f\u7075 \u518d\u4e5f\u538b\u4e0d\u4f4f\u8109\u9053\u4e2d\u7684\u6bd2\u6027\uff0c\u6781\u5feb\u7684\u6563\u5c06\u5f00\u6765\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e0d\u6562\u4e1d\u6beb\u5927\u610f\uff0c\u5de6\u624b\u4e00\u6643\u4e4b\u4e0b\uff0c\u70b9\u51fa\u4e8c\u6307\u3002 \u3000\u3000\u8fd9\u4e8c\u6307\u4e43\u662f\u865a\u7a7a\u70b9\u5411\u65e0\u6068\u751f\u7684\u201c\u7d2b\u5bab\u201d\u548c\u201c\u7ae0\u95e8\u201d\u7a74\u9053\u3002\u76ee\u7684\u4e43\u5728\u4e8e\u8bd5\u63a2\u65e0\u6068\u751f\u4f53\u5185\u6bd2 \u6027\u6563\u884c\u7684\u60c5\u5f62\u3002 \u3000\u3000\u4e0d\u8bf4\u8f9b\u6377\uff0c\u5c31\u662f\u7d20\u6765\u9762\u4e0a\u51b7\u6f20\u3001\u6027\u5982\u51b0\u971c\u7684\u5c0f\u6222\u5c9b\u4e3b\u6167\u5927\u5e08\uff0c\u4e5f\u4e0d\u7531\u7d27\u5f20\u7684\u53cc\u624b\u4e92\u76f8\u7d27 \u63e1\u4f4f\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u76ee\u4e0d\u8f6c\u775b\uff0c\u77aa\u7740\u65e0\u6068\u751f\uff0c\u84e6\u7136\uff0c\u4ed6\u77a5\u89c1\u65e0\u6068\u751f\u4fca\u9038\u7684\u8138\u4e0a\uff0c\u597d\u4f3c\u9690\u9690\u6563\u8fc7\u4e00\u4e1d \u75db\u82e6\u7684\u8868\u60c5\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4f55\u7b49\u529f\u529b\uff0c\u5df2\u77e5\u662f\u201c\u78a7\u7389\u65ad\u80a0\u201d\u5f00\u59cb\u653b\u5fc3\u3002 \u3000\u3000\u84e6\u5730\u91cc\uff0c\u5e73\u51e1\u4e0a\u4eba\u7ed3\u820c\u77aa\u76ee\uff0c\u6709\u5982\u6625\u96f7\u822c\u543c\u4e86\u4e00\u58f0\u2014\u2014\u65e0\u6068\u751f\u987f\u65f6\u5fc3\u4e2d\u4e00\u9707\uff0c\u7075\u53f0\u7a7a \u660e\uff0c\u8138\u4e0a\u75db\u82e6\u72b6\u7a0d\u5f04\uff0c\u5e73\u51e1\u4e0a\u4eba\u5de6\u624b\u5df1\u5982\u95ea\u7535\u822c\u518d\u62cd\u51fa\u4e00\u638c\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7528\u4f5b\u95e8\u6700\u4e0a\u4e58\u7684\u6c14\u529f\u9020\u8be3\uff0c\u53d1\u51fa\u201c\u72ee\u5b50\u543c\u201d\u7684\u529f\u592b\uff0c\u6682\u65f6\u9707\u9192\u65e0\u6068\u751f\u7684\u795e\u667a\uff0c \u628a\u63e1\u65f6\u95f4\uff0c\u4e00\u5355\u6309\u4e0b\u3002 \u3000\u3000\u624b\u638c\u5c1a\u8ddd\u201c\u6ce5\u4e38\u201d\u9876\u5fc3\u4e09\u5bf8\u5de6\u53f3\uff0c\u638c\u5fc3\u95ea\u7535\u4e00\u5410\u3002 \u3000\u3000\u8f9b\u6377\u6452\u4f4f\u547c\u6c14\uff0c\u5df2\u77e5\u8fd9\u4e00\u638c\u62cd\u4e0b\uff0c\u5e73\u51e1\u4e0a\u4eba\u7acb\u523b\u8981\u65bd\u5f00\u5185\u529b\uff0c\u6e21\u5165\u65e0\u6068\u751f\u4f53\u5185\uff0c\u6210\u8d25\u5168 \u5728\u6b64\u4e00\u4e3e\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u624b\u638c\u6309\u5b9e\uff0c\u7f13\u7f13\u5438\u8d77\u4e00\u53e3\u771f\u6c14\uff0c\u5410\u5165\u65e0\u6068\u751f\u4f53\u4e2d\uff0c\u52aa\u529b\u5f80\u201c\u6ce5\u4e38\u201d\u5bab\u7a74\u9053\u4e0b\u903c \u53bb\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8fd9\u4e00\u638c\u7528\u7684\u529b\u9053\u6070\u5230\u597d\u5904\uff0c\u8fd9\u4e00\u4e2a\u96be\u5173\u603b\u7b97\u6e21\u8fc7\u53bb\uff0c\u8f9b\u6377\u548c\u6167\u5927\u5e08\u90fd\u4e0d\u7531\u8212\u4e00 \u53e3\u6c14\u3002 \u3000\u3000\u7136\u800c\u5e73\u51e1\u4e0a\u4eba\u81ea\u5df1\u5fc3\u4e2d\u6709\u6570\uff0c\u522b\u770b\u521a\u624d\u90a3\u4e00\u638c\u6309\u4e0b\u53bb\uff0c\u5168\u529b\u63a7\u5236\u7740\uff0c\u4e0d\u5f97\u6709\u4e00\u4e1d\u4e00\u6beb\u7684 \u5dee\u9519\uff0c\u4e00\u53e3\u771f\u6c14\u5df2\u7ecf\u5dee\u4e0d\u591a\u5168\u4ee5\u704c\u6ce8\uff0c\u81ea\u5df1\u5185\u529b\u6e21\u5165\u65e0\u6068\u751f\u4f53\u5185\u903c\u6bd2\uff0c\u8fd8\u4e0d\u77e5\u80fd\u4e0d\u80fd\u5b8c\u6210 \u5462\uff1f\u4f46\u53e3\u5934\u53c8\u4e1d\u6beb\u5206\u795e\u4e0d\u5f97\uff0c\u53ea\u597d\u5168\u529b\u652f\u6301\u65bd\u4e3a\u3002 \u3000\u3000\u65f6\u95f4\u4e00\u5206\u4e00\u79d2\u4e2d\u8fc7\u53bb\uff0c\u5e73\u51e1\u4e0a\u4eba\u5934\u9876\u4e0a\u5192\u51fa\u84b8\u84b8\u767d\u6c14\uff0c\u767d\u9aef\u65e0\u98ce\u800c\u632f\uff0c\u8d56\u8d56\u6447\u52a8\uff0c\u8138\u8272 \u5982\u51b0\uff0c\u7d27\u5f20\u5df2\u6781\u3002 \u3000\u3000\u6167\u5927\u5e08\u4e0d\u76f8\u4fe1\u8fd9\u4e48\u4e00\u4ef6\u8270\u96be\u5de5\u4f5c\uff0c\u4f1a\u88ab\u5e73\u51e1\u4e0a\u4eba\u5982\u6b64\u987a\u5229\u5730\u5b8c\u6210\uff0c\u5979\u5fc3\u4e2d\u59cb\u7ec8\u4e0d\u80fd\u653e\u4e0b \u4e00\u4e1d\u6beb\u4e0d\u8f7b\u677e\u7684\u76ef\u89c6\u7740\u3002 \u3000\u3000\u679c\u7136\uff0c\u5e73\u51e1\u4e0a\u4eba\u7684\u8eab\u4f53\u84e6\u5730\u6709\u82e5\u9152\u8f9e\uff0c\u6447\u6446\u4e0d\u5b9a\uff0c\u8f9b\u6377\u5403\u4e00\u60ca\uff0c\u8eab\u4f53\u500f\u5730\u63a0\u8d77\uff0c\u60f3\u4e0a\u524d \u5bdf\u770b\u3002 \u3000\u3000\u4ed6\u5fc3\u77e5\u5fc5\u662f\u5e73\u51e1\u4e0a\u4eba\u5185\u529b\u4e0d\u7ee7\uff0c\u60f3\u51fa\u624b\u76f8\u52a9\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u81ea\u5df1\u529f\u592b\u6bd4\u5e73\u51e1\u4e0a\u4eba\u4e0d\u77e5\u5dee \u5374\u597d\u8fdc\uff0c\u4e07\u4e00\u51fa\u624b\u4e0d\u6210\u5bf9\u5e73\u51e1\u4e0a\u4eba\u6216\u65e0\u6068\u751f\uff0c\u751a\u81f3\u81ea\u5df1\u4e09\u4e2a\u4eba\u90fd\u662f\u5341\u5206\u4e0d\u5229\u7684\uff0c\u662f\u4ee5\u8eab\u4f53\u4e0d \u7531\u4e3a\u4e4b\u4e00\u632b\u3002 \u3000\u3000\u8fd9\u5f53\u513f\u91cc\uff0c\u773c\u524d\u4e00\u82b1\uff0c\u4e00\u6761\u4eba\u5f71\u5df2\u8d8a\u8fc7\u81ea\u5df1\u3002 \u3000\u3000\u8f9b\u6377\u60f3\u90fd\u4e0d\u7528\u60f3\uff0c\u77e5\u9053\u5b9a\u662f\u5c0f\u6222\u5c9b\u4e3b\u6167\u5927\u5e08\u3002 \u3000\u3000\u6167\u5927\u5e08\u597d\u5feb\u884c\u52a8\uff0c\u95ea\u5f97\u4e00\u95ea\uff0c\u5df2\u63a0\u5230\u5e73\u51e1\u4e0a\u4eba\u8eab\u524d\u3002 \u3000\u3000\u5979\u65e9\u77e5\u9053\uff0c\u4e8b\u60c5\u4e0d\u4f1a\u5982\u6b64\u987a\u5229\uff0c\u662f\u4ee5\u59cb\u7ec8\u5168\u795e\u704c\u6ce8\uff0c\u4e00\u89c1\u5e73\u51e1\u4e0a\u4eba\u8eab\u4f53\u5fae\u6643\uff0c\u4fbf\u77e5\u81ea\u5df1 \u6240\u6599\u4e0d\u5dee\uff0c\u5e73\u51e1\u4e0a\u4eba\u679c\u662f\u5185\u529b\u6709\u6240\u4e0d\u7ee7\uff0c\u7acb\u523b\u65bd\u5c55\u201c\u8bd8\u6469\u795e\u6b65\u201d\uff0c\u95ea\u5230\u4ed6\u8eab\u524d\u3002 \u3000\u3000\u4f46\u89c1\u6167\u5927\u5e08\u5de6\u624b\u75be\u4f38\u98df\u6307\uff0c\u51c6\u786e\u7684\u843d\u5728\u5e73\u51e1\u4e0a\u4eba\u7684\u201c\u5fd7\u5802\u7a74\u9053\u201d\u4e0a\uff0c\u5185\u529b\u5df2\u6e21\u5165\u5e73\u51e1\u4e0a \u4eba\u4f53\u5185\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u5e73\u51e1\u4e0a\u4eba\u7cbe\u795e\u5927\u632f\uff0c\u6362\u53bb\u4e00\u53e3\u65e9\u5df2\u6d51\u6d4a\u7684\u771f\u6c14\uff0c\u5185\u529b\u4e0d\u65ad\u6e21\u5165\u65e0\u6068\u751f\u4f53\u5185\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u660e\u767d\uff0c\u8fd9\u4e00\u6765\uff0c\u5e73\u51e1\u4e0a\u4eba\u56fa\u7136\u8131\u9669\uff0c\u4f46\u6167\u5927\u5e08\u548c\u4ed6\u7684\u5185\u529b\u5047\u82e5\u4e0d\u80fd\u914d\u5408\u5f97\u5929\u8863 \u65e0\u7f1d\uff0c\u90a3\u4e48\uff0c\u4e0d\u4f46\u65e0\u6068\u751f\u751f\u547d\u96be\u4fdd\uff0c\u5c31\u662f\u5927\u3001\u5c0f\u6222\u5c9b\u4e3b\uff0c\u4e5f\u90fd\u4f1a\u8eab\u53d7\u91cd\u4f24\uff01 \u3000\u3000\u662f\u4ee5\u8f9b\u6377\u7684\u5fc3\u60c5\uff0c\u6bd4\u4e4b\u5148\u524d\uff0c\u66f4\u662f\u7d27\u5f20\uff0c\u4f46\u4ed6\u81ea\u77e5\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u53ea\u5728\u4e00\u65c1\u76ee\u77aa\u53e3\u5446\u7684\u671b\u7740 \u4e09\u4eba\u3002 \u3000\u3000\u8fd9\u91cc\uff0c\u5c45\u4e8e\u4e1c\u6d77\u4e09\u5c9b\u4e4b\u4e2d\u7684\u5c0f\u6222\u5c9b\u4e0a\uff0c\u662f\u4e00\u7247\u6b7b\u9759\u7684\uff0c\u6d77\u8fb9\u79bb\u8fd9\u91cc\u5f88\u8fdc\uff0c\u6d6a\u5578\u4e4b\u58f0\u4e0d\u80fd \u4f20\u6765\u3002 \u3000\u3000\u6709\u4e00\u70b9\u5fae\u98ce\uff0c\u62c2\u7740\u5bc2\u9759\u4e2d\u7684\u56db\u4eba\uff0c\u8863\u8882\u6447\u6446\u5904\uff0c\u53d1\u51fa\u7684\u58f0\u97f3\uff0c\u5468\u906d\u5f88\u4e3a\u548c\u8c10\u2014\u2014\u4f46\u5b9e\u9645 \u4e0a\u5374\u6709\u5982\u4e00\u5f20\u7d27\u5f20\u7684\u5f26\u3002 \u3000\u3000\u8f9b\u6377\u5446\u5446\u5730\u671b\u7740\uff0c\u5927\u6222\u5c9b\u4e3b\u4e00\u624b\u7d27\u7d27\u5730\u6309\u5728\u65e0\u6068\u751f\u7684\u201c\u6ce5\u4e38\u201d\u4e0a\uff0c\u6167\u5927\u5e08\u7684\u624b\u6307\u5374\u7d27\u8d34 \u5e73\u51e1\u4e0a\u4eba\u7684\u5fd7\u5802\u7a74\uff0c\u65e0\u6839\u751f\u76d8\u819d\u800c\u5750\uff0c\u8138\u4e0a\u795e\u60c5\u751a\u4e3a\u53e4\u602a\u3002 \u3000\u3000\u5c06\u8fd1\u4e00\u767e\u5e74\uff0c\u4e1c\u6d77\u4e16\u5916\u4e09\u4ed9\u4ece\u6ca1\u6709\u6253\u8fc7\u6b63\u7ecf\u7684\u4ea4\u9053\uff0c\u8c01\u4e5f\u60f3\u4e0d\u5230\uff0c\u5728\u8fd9\u91cc\u7adf\u4f1a\u805a\u96c6\u4e00 \u8d77\uff0c\u800c\u4e14\u8fd8\u5408\u7528\u5185\u529b\u7597\u4f24\u54ea\u3002 \u3000\u3000\u8f9b\u6377\u9ed8\u7136\u7948\u7977\uff0c\u5e0c\u671b\u65e0\u6068\u751f\u80fd\u75ca\u6108\uff0c\u540c\u65f6\u95f4\uff0c\u4e5f\u4ed4\u7ec6\u68c0\u770b\u6bd2\u7ecf\uff0c\u77e5\u9053\u6bd2\u4e00\u903c\u4e0b\uff0c\u7acb\u523b\u5c31 \u8981\u91c7\u53d6\u653e\u8840\u7684\u65b9\u5f0f\u3002 \u3000\u3000\u8f9b\u6377\u7f13\u7f13\u8d70\u8fd1\uff0c\u770b\u90a3\u65e0\u6068\u751f\u6ce5\u4e38\u4e0a\u88ab\u5927\u6222\u5c9b\u4e3b\u6309\u4f4f\uff0c\u8138\u4e0a\u4e00\u5c42\u6de1\u6de1\u9ed1\u6c14\u5f88\u6162\u5730\u5f80\u4e0b\u964d\uff0c \u8f9b\u6377\u77e5\u9053\uff0c\u5927\u3001\u5c0f\u6222\u4e3b\u7684\u5185\u529b\uff0c\u5df2\u7136\u53d1\u6325\u6548\u529b\u4e86\u3002 \u3000\u3000\u9ed1\u6c14\u9010\u6e10\u4e0b\u964d\uff0c\u8f9b\u6377\u6ce8\u89c6\u7740\uff0c\u7b49\u5019\u7740\u673a\u4f1a\uff0c\u5fc3\u60c5\u4ecd\u7136\u662f\u7d27\u5f20\u7684\uff0c\u8f6c\u773c\u671b\u671b\u5e73\u51e1\u4e0a\u4eba\u548c\u6167 \u5927\u5e08\uff0c\u4e24\u4eba\u8138\u4e0a\u5b9d\u76f8\u7aef\u58ee\uff0c\u60f3\u90fd\u5df2\u52a8\u7528\u4f5b\u95e8\u5fc3\u6cd5\u3002 \u3000\u3000\u666e\u5929\u4e4b\u4e0b\uff0c\u6709\u8c01\u80fd\u662f\u5927\u3001\u5c0f\u6222\u5c9b\u4e3b\u7684\u654c\u624b\uff1f\u800c\u8fd9\u4e24\u4f4d\u76d6\u4e16\u5947\u4eba\u8054\u624b\u4e4b\u4e0b\uff0c\u6709\u4ec0\u4e48\u4e8b\u4e0d\u80fd \u591f\u5b8c\u6210\uff1f\u7136\u800c\uff0c\u8fd9\u90fd\u662f\u4e00\u4ef6\u4ee4\u4e24\u4eba\u90fd\u6ca1\u6709\u628a\u63e1\u7684\u96be\u9898\uff0c\u5047\u82e5\u4e24\u4eba\u7684\u5185\u529b\u4e0d\u76f8\u914d\u5408\uff0c\u529b\u9053\u867d \u5f3a\uff0c\u5374\u4e5f\u5f92\u7136\u3002 \u3000\u3000\u8f9b\u6377\u5f88\u660e\u767d\u8fd9\u4e2a\u9053\u7406\u3002\u4ed6\u77e5\u9053\uff0c\u4e5f\u53ea\u6709\u6167\u5927\u5e08\u5982\u6b64\u9ad8\u6df1\u7684\u5185\u529b\uff0c\u624d\u80fd\u548c\u5e73\u51e1\u4e0a\u4eba\u76f8\u914d \u5408\u3002 \u3000\u3000\u9ed1\u6c14\u4e0b\u964d\uff0c\u5df1\u5230\u624b\u81c2\u4e0a\uff0c\u65e0\u6068\u751f\u53f3\u624b\u5782\u7740\uff0c\u90a3\u9ed1\u6c14\u5df2\u88ab\u5927\u3001\u5c0f\u6222\u5c9b\u4e3b\u7684\u5185\u529b\u903c\u5230\u805a\u5728\u65e0 \u6068\u751f\u53f3\u624b\u4e2d\u6307\u4e0a\u70b9\u3002 \u3000\u3000\u8f9b\u6377\u4ece\u6000\u4e2d\u62ff\u51fa\u4e00\u4e2a\u53e4\u94dc\u7684\u5c0f\u74f6\u5b50\uff0c\u671b\u671b\u65e0\u6068\u751f\u4e00\u6839\u6709\u82e5\u9ed1\u70ad\u7684\u4e2d\u6307\uff0c\u4ed6\u77e5\u9053\u8fd9\u4fbf\u662f\u90a3 \u6f5c\u4f0f\u5728\u65e0\u6068\u751f\u4f53\u5185\u7684\u201c\u78a7\u7389\u65ad\u80a0\u201d\u4e86\u3002 \u3000\u3000\u8fd9\u73a9\u610f\u4e4b\u6bd2\uff0c\u5929\u4e0b\u65e0\u53cc\uff0c\u8f9b\u6377\u4e0d\u6562\u6cbe\u4e0a\uff0c\u624b\u6307\u5fae\u4f38\uff0c\u865a\u7a7a\u5f80\u65e0\u6068\u751f\u6307\u5c16\u4e00\u52d2\uff0c\u4e00\u80a1\u6307\u98ce \u8fc7\u5904\uff0c\u65e0\u6068\u751f\u53f3\u624b\u4e2d\u6307\u5c16\u4e0a\uff0c\u987f\u65f6\u73b0\u51fa\u4e00\u9053\u4e0d\u592a\u6df1\u7684\u53e3\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u52a8\u4f5c\u5982\u98ce\uff0c\u5c0f\u74f6\u5df1\u9760\u8fd1\u90a3\u53e3\u5b50\uff0c\u679c\u7136\u4f24\u53e3\u4e2d\u6d41\u51fa\u4e00\u6ef4\u6ef4\u7684\u8840\u6765\uff0c\u8fd9\u6b63\u662f\u90a3\u78a7\u7389\u65ad \u80a0\uff01 \u3000\u3000\u78a7\u7389\u65ad\u80a0\u8272\u4f5c\u78a7\u7eff\uff1b\u800c\u4e14\u6676\u83b9\u53d1\u4eae\uff0c\u4e00\u6ef4\u4e00\u6ef4\uff0c\u771f\u6709\u70b9\u50cf\u4e00\u5c0f\u5757\u7684\u7fe1\u7fe0\u78a7\u7389\uff0c\u53ef\u7231\u5df2 \u6781\u3002 \u3000\u3000\u65ad\u80a0\u6bd2\u6db2\u4e00\u6ef4\u6ef4\u51fa\uff0c\u679c\u7136\u4e0d\u540c\u51e1\u54cd\uff0c\u843d\u5165\u74f6\u4e2d\uff0c\u94ff\u7136\u6709\u58f0\uff0c\u5012\u50cf\u662f\u91cd\u5982\u91d1\u5c5e\u4e00\u6837\u3002 \u3000\u3000\u800c\u4e14\u6bcf\u6ef4\u5165\u74f6\uff0c\u90fd\u53d1\u51fa\u4e00\u80a1\u6d53\u70df\uff0c\u53ef\u89c1\u5176\u6bd2\u6027\u4e4b\u70c8\u3002 \u3000\u3000\u8f9b\u6377\u6015\u90a3\u6d53\u70df\u6709\u6bd2\uff0c\u6452\u4f4f\u547c\u5438\uff0c\u770b\u89c1\u90a3\u6bd2\u6db2\u6ef4\u5165\u74f6\u4e2d\uff0c\u4e0d\u7531\u6709\u4e00\u79cd\u5fc3\u60ca\u80c6\u8df3\u7684\u611f\u89c9\uff01 \u3000\u3000\u522b\u770b\u8fd9\u6bd2\u6db2\uff0c\u6ef4\u5f97\u6162\uff0c\u8db3\u8db3\u6709\u987f\u996d\u65f6\u523b\uff0c\u624d\u6ef4\u5b8c\u5168\u3002\u5c11\u8bf4\u4e5f\u6709\u5927\u534a\u74f6\uff0c\u6c89\u7538\u7538\u7684\uff0c\u597d\u4e0d \u60ca\u4eba\uff01 \u3000\u3000\u8f9b\u6377\u8c28\u614e\u7684\u65cb\u4e0a\u76d6\u5b50\uff0c\u653e\u5728\u6000\u4e2d\u3002 \u3000\u3000\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u7b49\u90a3\u6700\u540e\u4e00\u70b9\u6ef4\u51fa\uff0c\u624d\u6536\u638c\u957f\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u9000\u5728\u4e00\u65c1\u3002\u6167\u5927\u5e08\u9ed8\u9ed8\u6536 \u56de\u653e\u5728\u5e73\u51e1\u4e0a\u4eba\u5fd7\u5802\u7a74\u4e0a\u7684\u624b\u638c\uff0c\u548c\u5e73\u51e1\u4e0a\u4eba\u4e00\u540c\u8fd0\u529f\u8c03\u606f\u3002 \u3000\u3000\u76d8\u5750\u5728\u5730\u4e0a\u7684\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\uff0c\u773c\u7738\u513f\u5fae\u5f20\uff0c\u4e00\u6d3e\u7384\u95e8\u6b63\u5b97\u7684\u6253\u5750\u6a21\u6837\uff0c\u7f13\u7f13\u7684\u628a\u4e00\u53e3 \u771f\u6c14\u4e0a\u63d0\uff0c\u5728\u5468\u8eab\u4e0a\u4e0b\u8fd0\u884c\u4e00\u5468\u540e\uff0c\u518d\u8fd0\u6c14\u8c03\u606f\u3002 \u3000\u3000\u96be\u5173\u5df1\u8fc7\uff0c\u603b\u7b97\u65e0\u6068\u751f\u5185\u529b\u9020\u8be3\u597d\uff0c\u4e0d\u81f3\u5f71\u54cd\u5927\u3001\u5c0f\u6222\u5c9b\u4e3b\uff0c\u5012\u662f\u8f9b\u6377\u5728\u4e00\u65c1\u89c1\u4e09\u4eba\u8c03 \u606f\uff0c\u5fc3\u4e2d\u4ecd\u7136\u662f\u7d27\u5f20\u7684\u3002 \u3000\u3000\u826f\u4e45\uff0c\u4e16\u5916\u4e09\u4ed9\u90fd\u4ece\u4f24\u635f\u4e2d\u6062\u590d\u8fc7\u6765\uff0c\u65e0\u6068\u751f\u7ffb\u8eab\u8df3\u8d77\uff0c\u4ef0\u5929\u8fd0\u6c14\u957f\u5578\u4e00\u58f0\u3002 \u3000\u3000\u8fd9\u4e00\u5578\u4e43\u662f\u4ed6\u542b\u52b2\u800c\u53d1\uff0c\u58f0\u97f3\u597d\u4e0d\u6e05\u8d8a\uff0c\u6709\u82e5\u6625\u96f7\u7834\u7a7a\uff0c\u4f20\u51fa\u8001\u8fdc\u53bb\uff0c\u55b7\u4eae\u7684\u53cd\u5c04\u8fc7 \u6765\u3002 \u3000\u3000\u8fd9\u58f0\u5578\u58f0\u597d\u751f\u60a0\u957f\uff0c\u4f46\u56db\u4eba\u90fd\u662f\u5185\u5bb6\u9ad8\u624b\uff0c\u5df1\u542c\u51fa\u65e0\u6068\u751f\u5578\u58f0\u4e2d\u4e2d\u6c14\u4ecd\u6709\u4e0d\u8db3\uff0c\u77e5\u4ed6\u5c1a \u672a\u5b8c\u5168\u6062\u590d\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u54c8\u54c8\u4e00\u7b11\u9053\uff1a\u201c\u8001\u5c3c\u5a46\uff0c\u603b\u7b97\u54b1\u4eec\u4e0d\u8fb1\u4f7f\u547d\u3002\u201d \u3000\u3000\u6167\u5927\u5e08\u5fae\u5fae\u4e00\u7b11\uff0c\u5e76\u6ca1\u6709\u56de\u7b54\u3002 \u3000\u3000\u8f9b\u6377\u770b\u4e86\u770b\u624b\u4e2d\u7684\u6bd2\u7ecf\uff0c\u5bf9\u65e0\u6839\u751f\u9053\uff1a\u201c\u524d\u8f88\uff0c\u7167\u8fd9\u7ecf\u4e0a\u8bf4\uff0c\u524d\u8f88\u4e4b\u6bd2\u867d\u5df2\u7597\u597d\uff0c\u4f46\u4ecd \u5f97\u4f11\u606f\u4e09\u4e24\u4e2a\u6708\uff0c\u5426\u5219\u5bf9\u5185\u529b\u65b9\u9762\u6709\u788d\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u54fc\u4e86\u4e00\u58f0\uff0c\u4e0d\u7f6e\u53ef\u5426\u3002\u4ed6\u5e76\u975e\u4e0d\u77e5\u597d\u6b79\uff0c\u4f46\u4ed6\u6614\u65e5\u66fe\u8c6a\u8bed\u4e2d\u539f\u6b66\u6797\uff0c\u4eba\u6750\u51cb\u843d\uff0c \u8fd9\u51e0\u6708\u6765\uff0c\u4ed6\u4e5f\u66fe\u5230\u8fc7\u4e2d\u571f\uff0c\u8bc1\u5b9e\u4e2d\u539f\u6b66\u5b66\u6837\u6837\u4e0d\u5dee\uff0c\u800c\u4e14\uff0c\u5404\u79cd\u65c1\u95e8\u5de6\u9053\uff0c\u4e94\u82b1\u516b\u95e8\uff0c\u4e5f \u90fd\u6837\u6837\u6709\u4eba\u7cbe\u901a\uff0c\u8fd9\u6b21\u81ea\u5df2\u7684\u6027\u547d\uff0c\u4fbf\u4ece\u8fd9\u201c\u6bd2\u541b\u201d\u7684\u624b\u4e2d\u6361\u6765\uff0c\u53ef\u662f\u4ed6\u751f\u6027\u9ad8\u50b2\uff0c\u6709\u8a00\u5728 \u5148\uff0c\u662f\u4ee5\u4ec5\u4ec5\u51b7\u54fc\u4e00\u58f0\uff0c\u5fc3\u4e2d\u4ecd\u662f\u5f88\u611f\u6fc0\u7684\u3002 \u3000\u3000\u672c\u6765\u8fd9\u5f53\u513f\u60c5\u5f62\u6709\u82e5\u7d27\u5f20\u7684\u5f26\uff0c\u8fd9\u4e00\u6765\uff0c\u5374\u53c8\u8f7b\u677e\u65e0\u6bd4\uff0c\u5e73\u51e1\u4e0a\u4eba\u7b11\u53e3\u76c8\u76c8\uff0c\u4e0d\u77e5\u5f97\u610f \u7740\u4ec0\u4e48\u3002 \u3000\u3000\u84e6\u5730\u6167\u5927\u5e08\u5bf9\u65e0\u6068\u751f\u9053\uff1a\u201c\u5f20\u65bd\u4e3b\uff0c\u4f60\u5bf9\u90a3\u77f3\u6797\u53d1\u4e00\u638c\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u5fc3\u77e5\u5979\u5fc3\u7ec6\uff0c\u653e\u4e0d\u4e0b\u5fc3\uff0c\u8981\u81ea\u5df1\u53d1\u638c\uff0c\u85c9\u4ee5\u770b\u770b\u81ea\u5df1\u7684\u6bd2\u6839\u53bb\u4e86\u6ca1\u6709\uff0c\u5fc3\u4e2d\u611f \u6fc0\u3002\u5fae\u5fae\u4e00\u7b11\u3002\u5934\u4e5f\u4e0d\u56de\uff0c\u53cd\u624b\u4e00\u62cd\uff0c\u5411\u4e00\u5ea7\u77f3\u7b0b\u62cd\u53bb\u3002 \u3000\u3000\u65e0\u6068\u751f\u8fd9\u4e00\u638c\u7eaf\u662f\u5185\u4e3a\uff0c\u865a\u865a\u4e00\u6309\uff0c\u529b\u9053\u597d\u4e0d\u60ca\u4eba\uff0c\u4f46\u95fb\u201c\u8f70\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u77f3\u7b0b\u5de6\u53f3\u4e00 \u9635\u6447\u8361\uff0c\u5374\u5e76\u6ca1\u6709\u5012\u4e0b\u3002 \u3000\u3000\u65e0\u6068\u751f\u5fae\u7b11\u9053\uff1a\u201c\u771f\u6c14\u8fd0\u884c\u4e0d\u59a8\uff0c\u987a\u5229\u5982\u5e38\u2014\u2014\u201d \u3000\u3000\u6167\u5927\u5e08\u70b9\u4e86\u70b9\u5934\uff0c\u5e73\u51e1\u4e0a\u4eba\u54c8\u54c8\u9053\uff1a\u201c\u770b\u6837\u5b50\uff0c\u8001\u5f1f\u53ea\u8981\u518d\u6709\u5341\u5929\u4e00\u6708\uff0c\u4fbf\u53ef\u6062\u590d\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u70b9\u70b9\u5934\uff0c\u5fc3\u60f3\u81ea\u5df1\u4f24\u52bf\u5df2\u597d\u5927\u534a\uff0c\u5e73\u65e5\u548c\u5927\u3001\u5c0f\u6222\u5c9b\u4e3b\u90fd\u65e0\u751a\u4ea4\u60c5\uff0c\u518d\u803d\u4e0b\u53bb\uff0c \u4e5f\u4e0d\u751a\u597d\uff0c\u4e8e\u662f\u6717\u58f0\u9053\uff1a\u201c\u5c0f\u751f\u62dc\u53d7\u4e24\u4f4d\u4e4b\u8d50\uff0c\u6b64\u6069\u5f85\u5bb9\u65e5\u540e\u9a70\u62a5\u2014\u2014\u201d\u8bf4\u7740\u5bf9\u6167\u5927\u5e08\u548c\u5e73 \u51e1\u4e0a\u4eba\u4e00\u63d6\uff0c\u8f6c\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u4e16\u5916\u4e09\u4ed9\u672c\u6765\u81ea\u89c6\u90fd\u751a\u9ad8\uff0c\u5e73\u51e1\u4e0a\u4eba\u548c\u6167\u5927\u5e08\u867d\u7136\u4e3a\u65e0\u6068\u751f\u51fa\u5f88\u5927\u529b\uff0c\u65e0\u6068\u751f\u5fc3\u4e2d\u611f \u8c22\uff0c\u53e3\u4e2d\u5374\u5e76\u4e0d\u8bf4\u51fa\u6765\uff0c\u4ec5\u4ec5\u884c\u793c\u800c\u9000\u3002 \u3000\u3000\u6167\u5927\u5e08\u548c\u5e73\u51e1\u4e0a\u4eba\u65e9\u5df2\u4e0d\u5728\u4e4e\u8fd9\u4e9b\uff0c\u5e73\u51e1\u4e0a\u4eba\u54c8\u54c8\u9053\uff1a\u201c\u597d\u8bf4\uff01\u597d\u8bf4\uff01\u548c\u5c1a\u61d2\u4e00\u6b65\u4e0d\u518d \u8fdc\u9001\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u65e0\u6068\u751f\u5df2\u98d8\u51fa\u4e24\u4e09\u4e08\u3002 \u3000\u3000\u8f9b\u6377\u7a81\u5730\u8eab\u4f53\u4e00\u52a8\uff0c\u5411\u65e0\u6068\u751f\u8ffd\u53bb\uff0c\u53eb\u9053\uff1a\u201c\u524d\u8f88\u7a0d\u5f85\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u8eab\u4f53\u4e00\u987f\uff0c\u8f6c\u8eab\u6765\u671b\u7740\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u5450\u5450\u9053\uff1a\u201c\u524d\u8f88\u6253\u8d4c\u4e4b\u4e8b\uff0c\u5df2\u80dc\u90a3\u76d8\u706f\u5b5a\u5c14\uff0c\u665a\u8f88\u5fc5\u5f53\u5c3d\u529b\u627e\u5bfb\u4ee4\u5a9b\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u5fc3\u60f3\u81ea\u5df1\u786e\u5b9e\u80dc\u5f97\u90a3\u6ee1\u9762\u76b1\u7eb9\u7684\u5bb6\u4f19\uff0c\u53ea\u56e0\u6bd2\u4f24\u7a81\u53d1\u624d\u529f\u4e8f\u4e00\u7c27\u3002\u4f46\u5fc3\u4e2d\u5374\u56e0\u6b64 \u5bf9\u8f9b\u6377\u7a0d\u5177\u597d\u611f\uff0c\u51dd\u795e\u671b\u4e86\u8f9b\u6377\u4e24\u773c\uff0c\u624d\u8f6c\u8eab\u5954\u53bb\u3002 \u3000\u3000\u90a3\u8fb9\u6167\u5927\u5e08\u7ad9\u8d77\u8eab\u6765\uff0c\u5bf9\u5e73\u51e1\u4e0a\u4eba\u77aa\u4e86\u51e0\u773c\uff0c\u4e0d\u53d1\u4e00\u8a00\uff0c\u4e5f\u8d70\u56de\u5c9b\u4e2d\u3002\u5e73\u51e1\u4e0a\u4eba\u6df1\u77e5\u5979 \u6027\u683c\uff0c\u5475\u5475\u4e00\u9635\u5927\u7b11\uff0c\u76f4\u5230\u6167\u5927\u5e08\u8d70\u4eba\u8f6c\u89d2\u5904\uff0c\u624d\u6536\u4e0b\u58f0\u6765\u3002 \u3000\u3000\u8f9b\u6377\u76ee\u9001\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u8d70\u540e\uff0c\u7f13\u7f13\u8d70\u56de\u77f3\u9635\uff0c\u770b\u89c1\u5e73\u51e1\u4e0a\u4eba\u8138\u4e0a\u8868\u60c5\u53e4\u602a\uff0c\u5fc3\u4e2d\u4e0d\u7531 \u4e00\u6014\uff0c\u8d70\u8fd1\u6765\u4e5f\u76d8\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5929\u8272\u6e10\u6697\u4e0b\u6765\u4e86\uff0c\u5915\u9633\u897f\u4e0b\uff0c\u591c\u8272\u6e10\u6d53\u3002 \u3000\u3000\u9760\u8fd1\u6d77\u5cb8\uff0c\u6d77\u98ce\u5165\u591c\u9010\u6e10\u52a0\u5927\uff0c\u5e73\u51e1\u4e0a\u4eba\u7684\u767d\u8272\u50e7\u888d\u968f\u98ce\u800c\u8361\uff0c\u5374\u662f\u7070\u8272\u7684\u4e00\u7247\u3002 \u3000\u3000\u8f9b\u6377\u671b\u7740\u6c89\u9ed8\u7684\u5e73\u51e1\u4e0a\u4eba\uff0c\u5fc3\u4e2d\u77e5\u9053\u5e73\u51e1\u4e0a\u4eba\u5fc5\u662f\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u8bf4\uff0c\u4f46\u4ed6\u4e0d\u5f00\u53e3\u8bf4\uff0c\u81ea \u5df1\u4e5f\u4e0d\u597d\u95ee\u3002 \u3000\u3000\u4e24\u4e2a\u65f6\u8fb0\u524d\uff0c\u8fd9\u91cc\u8fd8\u662f\u5728\u9f99\u4e89\u864e\u6597\uff0c\u534e\u5937\u76f8\u640f\uff0c\u7136\u800c\uff0c\u8fd9\u4e9b\u5df2\u4e3a\u6d6e\u4e91\uff0c\u968f\u98ce\u800c\u6563\uff01 \u3000\u3000\u4e5f\u8bb8\u662f\u592a\u5bc2\u9759\u4e86\uff0c\u8fdc\u65b9\u7684\u6d77\u6d9b\u58f0\u9690\u9690\u6709\u8282\u594f\u7684\u4f20\u6765\uff0c\u8f9b\u6377\u9ed8\u7136\u7684\u5750\u7740\uff0c\u4e00\u76f4\u7d27\u5f20\u7684\u5fc3 \u5f26\uff0c\u7531\u4e8e\u548c\u8c10\u7684\u6c14\u6c1b\uff0c\u800c\u8f7b\u677e\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5929\u8fb9\u7b2c\u4e00\u9897\u661f\u513f\u51fa\u73b0\u4e86\uff0c\u5728\u9ed1\u6697\u4e2d\u5212\u8fc7\u4e00\u9053\u5149\u660e\u7684\u5f27\u5ea6\u2026\u2026 \u3000\u3000\u7693\u6708\u5f53\u7a7a\uff0c\u591c\u8272\u5982\u6c34\uff0c\u9ed1\u8272\u7684\u5929\u7a7a\u900f\u51fa\u4e00\u4e1d\u6df1\u84dd\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5750\u5728\u77f3\u4e0a\uff0c\u4ef0\u9996\u51dd\u89c6\u7740\u9ed1\u6697\u7684\u957f\u7a7a\uff0c\u4ed6\u4e24\u9053\u96ea\u767d\u7684\u957f\u7709\u5fae\u5fae\u58c1\u5728\u4e00\u8d77\uff0c\u7ea2\u6da6\u7684 \u8138\u5b54\u4e0a\u900f\u51fa\u4e00\u6d3e\u9690\u9690\u7684\u6101\u601d\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u89e3\u5730\u671b\u7740\u8001\u4eba\u2014\u2014\u4e5f\u8bb8\u8bf4\u5728\u7b49\u5f85\u5e73\u51e1\u4e0a\u4eba\u5f00\u53e3\u8fd8\u6765\u5f97\u786e\u5207\u4e9b\u3002 \u3000\u3000\u826f\u4e45\uff0c\u5e73\u51e1\u4e0a\u4eba\u5f00\u53e3\u9053\uff1a\u201c\u5a03\u513f\uff0c\u6211\u2014\u2014\u6211\u8bf4\u4e2a\u6545\u4e8b\u7ed9\u4f60\u542c\u542c\u3002\u201d \u3000\u3000\u8f9b\u6377\u5947\u602a\u5730\u55ef\u4e86\u4e00\u58f0\uff0c\u6ce8\u89c6\u7740\u5e73\u51e1\u4e0a\u4eba\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4ecd\u662f\u51dd\u89c6\u7740\u957f\u7a7a\uff0c\u4f3c\u4e4e\u5728\u90a3\u6df1\u65e0\u7a77\u5c3d\u7684\u9ed1\u8272\u540e\u9762\u5bfb\u6c42\u4e00\u4e9b\u88ab\u9057\u5fd8\u4e86\u7684\u5f80\u4e8b\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u9053\uff1a\u201c\u5927\u7ea6\u662f\u767e\u591a\u5e74\u524d\u7f62\u2014\u2014\u90a3\u65f6\uff0c\u4e2d\u539f\u7684\u6b66\u6797\u9886\u8896\u662f\u5c11\u6797\u3002\u5c11\u6797\u5bfa\u79c9\u627f\u8fbe\u6469\u7956 \u5e08\u7684\u5404\u79cd\u7edd\u827a\uff0c\u867d\u7136\u5e74\u4e45\u65e7\u6df1\u6709\u597d\u4e9b\u795e\u529f\u5df2\u7ecf\u7edd\u4f20\uff0c\u4f46\u662f\u5c31\u51ed\u5b83\u6b63\u5b97\u7684\u5185\u5bb6\u771f\u4f20\u4ecd\u4e0d\u662f\u6b66\u6797 \u5176\u4ed6\u5404\u6d3e\u6240\u80fd\u53ca\u7684\u2026\u2026\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u505c\u4e86\u505c\uff0c\u4f46\u7acb\u523b\u53c8\u7ee7\u7eed\u9053\uff1a\u201c\u53ef\u662f\u8fd1\u767e\u591a\u5e74\u6765\uff0c\u6b66\u6797\u7684\u6cf0\u5c71\u5317\u6597\u5df2\u4e0d\u662f\u5c11 \u6797\u5bfa\uff0c\u6c5f\u6e56\u4e0a\u4e5f\u4e0d\u89c1\u5c11\u6797\u50e7\u4eba\u7684\u8e2a\u8ff9\uff0c\u751a\u81f3\u6709\u4e9b\u5c11\u6797\u5f1f\u5b50\u88ab\u4eba\u6b3a\u4fae\u4e86\uff0c\u4e5f\u6ca1\u6709\u65c1\u4eba\u51fa\u5934\uff0c\u4e8e \u662f\u65c1\u4eba\u53ea\u77e5\u9053\u5c11\u6797\u5bfa\u4eba\u6750\u51cb\u843d\uff0c\u58f0\u8a89\u4e00\u843d\u5343\u4e08\uff0c\u5374\u4e0d\u77e5\u8fd9\u5176\u4e2d\u8fd8\u6709\u4e00\u8282\u9690\u60c5\u54e9\u3002\u201d \u3000\u3000\u8f9b\u6377\u542c\u4ed6\u8bf4\u5c11\u6797\u5bfa\uff0c\u66f4\u662f\u51dd\u795e\u503e\u542c\uff0c\u53ea\u542c\u5e73\u51e1\u4e0a\u4eba\u63a5\u7740\u9053\uff1a\u201c\u90a3\u65f6\u5c11\u6797\u5bfa\u7684\u638c\u95e8\u65b9\u4e08\u662f \u7075\u955c\u5927\u5e08\uff0c\u4ed6\u7684\u5e08\u5f1f\u7075\u7a7a\u5927\u5e08\u662f\u85cf\u7ecf\u9601\u7684\u4e3b\u6301\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u542c\u4ed6\u8bf4\u5230\u201c\u7075\u7a7a\u5927\u5e08\u201d\uff0c\u4e0d\u7531\u554a\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u779f\u4e86\u4ed6\u4e00\u773c\uff0c\u7eed\u9053\uff1a\u201c\u7075\u7a7a\u5927\u5e08\u505a\u4e86\u85cf\u7ecf\u9601\u7684\u4e3b\u6301\uff0c\u7ec8\u65e7\u95ed\u95e8\u6f5c\u5fc3\u82e6\u601d\u85cf\u7ecf\u9601 \u4e2d\u90a3\u4e9b\u7956\u4f20\u4ec5\u5269\u7684\u4e00\u4e9b\u6b8b\u7f3a\u4e0d\u5168\u7684\u795e\u529f\u2014\u2014\u672c\u6765\u90a3\u4e9b\u5931\u4f20\u7684\u795e\u529f\u53ea\u4e00\u9cde\u534a\u722a\uff0c\u4f46\u662f\u7075\u7a7a\u5927\u82e6 \u601d\u4e09\u5341\u4f59\u5e74\uff0c\u7adf\u7136\u88ab\u4ed6\u786c\u662f\u641e\u901a\uff0c\u4e8e\u662f\u8bb8\u591a\u5931\u4f20\u591a\u5e74\u7684\u7edd\u827a\u53c8\u91cd\u73b0\u4e8e\u7075\u7a7a\u5927\u5e08\u7684\u8eab\u4e0a\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4f3c\u4e4e\u611f\u5230\u5e73\u51e1\u4e0a\u4eba\u96ea\u767d\u7684\u7709\u6bdb\u4e0b\u4e00\u53cc\u9635\u5b50\u4e2d\uff0c\u7cbe\u5149\u7a81\u7136\u5c04\u51fa\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6b47\u4e86\u6b47\u9053\uff1a\u201c\u540e\u6765\uff0c\u540e\u6765\u4e3a\u4e86\u2014\u2014\u4e3a\u4e86\u4e00\u6869\u4e8b\uff0c\u5c11\u6797\u5bfa\u5185\u8d77\u5267\u53d8\uff0c\u638c\u95e8\u4eba\u7075\u955c \u5927\u5e08\u548c\u7075\u7a7a\u5927\u5e08\u4e00\u9f50\u79bb\u5f00\u4e86\u5c11\u6797\u5bfa\uff0c\u7075\u955c\u7684\u5927\u5f1f\u5b50\u53f0\u51c0\u63a5\u4efb\u638c\u95e8\u3002\u4e3a\u4e86\u8fd9\u4ef6\u4e8b\u4ed6\u5b9a\u4e0b\u4e86\u4e00\u6761 \u95e8\u89c4\uff0c\u51e1\u662f\u5c11\u6797\u5bfa\u7684\u548c\u5c1a\uff0c\u5982\u975e\u638c\u95e8\u7279\u8bb8\uff0c\u7ec8\u751f\u4e0d\u51c6\u51fa\u5bfa\u534a\u6b65\uff0c\u800c\u975e\u751f\u6b7b\u5173\u5934\uff0c\u7edd\u4e0d\u51c6\u4e0e\u4eba \u52a8\u624b\u2014\u2014\u4e8e\u662f\uff0c\u5c11\u6797\u50e7\u4eba\u7edd\u8db3\u6c5f\u6e56\uff0c\u5c11\u6797\u5f1f\u5b50\u7edd\u4e0d\u4e0e\u4eba\u52a8\u624b\uff0c\u800c\u4eba\u4eec\u5c31\u4ee5\u4e3a\u5c11\u6797\u5bfa\u4eba\u6750\u51cb \u843d\uff0c\u4e00\u843d\u5343\u4e08\u2014\u2014\u201d \u3000\u3000\u201c\u7075\u7a7a\u5927\u5e08\u548c\u7075\u955c\u5927\u5e08\u79bb\u5f00\u4e86\u5c11\u6797\u5bfa\uff0c\u65e0\u5f02\u5c06\u8bb8\u591a\u5c11\u6797\u7edd\u6280\u5e26\u8d70\uff0c\u5c11\u6797\u5bfa\u7684\u50e7\u4eba\u5bf9\u7956\u4f20 \u6b66\u5b66\u81ea\u7136\u66f4\u662f\u65e0\u6cd5\u4e86\u89e3\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u806a\u660e\u7edd\u9876\uff0c\u4ed6\u542c\u5230\u8fd9\u91cc\uff0c\u8bb8\u591a\u5148\u524d\u7684\u7591\u7aa6\u5728\u8111\u6d77\u4e2d\u4e00\u6643\u800c\u8fc7\uff0c\u4ed6\u5bf9\u8fd9\u4e9b\u5df1\u6709\u4e86\u5927\u6982 \u7684\u4e86\u89e3\u2014\u2014\u4ed6\u77e5\u9053\uff0c\u90a3\u767e\u5e74\u524d\u8eab\u8d1f\u8fbe\u6469\u5931\u4f20\u795e\u529f\u7684\u7075\u7a7a\u5927\u5e08\uff0c\u5c31\u662f\u773c\u524d\u7684\u5e73\u51e1\u4e0a\u4eba\uff01 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5c11\u6797\u5bfa\u4ee5\u540e\u7684\u4e8b\u5012\u771f\u548c\u8f9b\u6377\u6599\u60f3\u7684\u5dee\u4e0d\u591a\u2014\u2014 \u3000\u3000\u53f0\u51c0\u5927\u5e08\u5b9a\u4e86\u8fd9\u6761\u95e8\u89c4\uff0c\u4ed6\u53bb\u4e16\u4e4b\u540e\uff0c\u7ecf\u8fc7\u4e24\u4ee3\u4f20\u5230\u667a\u656c\u5927\u5e08\u2014\u2014\u5c11\u6797\u5bfa\u73b0\u4efb\u7684\u638c\u95e8 \u4eba\u3002 \u3000\u3000\u767e\u5e74\u6765\uff0c\u5c11\u6797\u5bfa\u4e0d\u65ad\u5730\u6709\u4eba\u5728\u82e6\u601d\u90a3\u4e9b\u7edd\u5b66\uff0c\u4f46\u662f\u59cb\u7ec8\u65e0\u6cd5\u878d\u4f1a\u8d2f\u901a\uff0c\u5c11\u6797\u50e7\u4eba\u77e5\u9053\u8981 \u60f3\u91cd\u632f\u5c11\u6797\u76d6\u4e16\u795e\u5a01\uff0c\u9664\u4e86\u90a3\u76d6\u4e16\u5947\u624d\u7684\u7075\u7a7a\u5927\u5e08\uff0c\u5df2\u65e0\u4ed6\u4eba\uff0c\u4f46\u662f\u7075\u7a7a\u5927\u5e08\u4e00\u53bb\u4e0d\u77e5\u8e2a \u8ff9\uff0c\u8fd1\u767e\u5e74\u65f6\u5149\uff0c\u53ea\u6015\u5df1\u6709\u53d8\u6545\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u4eec\u60f3\u5230\u4e86\u4e00\u70b9\uff0c\u7075\u7a7a\u5927\u5e08\u7eb5\u7136\u5df2\u6b7b\uff0c\u53ea\u6015\u4ed6\u66fe\u6709\u4f20\u4eba\u7ee7\u627f\u4ed6\u90a3\u4e00\u8eab\u5947\u5b66\u3002\u8fd9\u5e76 \u4e0d\u7b97\u56f0\u96be\uff0c\u53ea\u8981\u5230\u6b66\u6797\u4e2d\u53bb\u6253\u542c\uff0c\u4e0d\u96be\u80fd\u63a2\u51fa\u4e00\u4e9b\u7aef\u502a\u2014\u2014\u7136\u800c\u5bf9\u5c11\u6797\u50e7\u4eba\u6765\u8bf4\u5374\u662f\u4e00\u6869\u96be \u9898\uff0c\u56e0\u4e3a\u5c11\u6797\u5f1f\u5b50\u662f\u4e0d\u80fd\u79bb\u5f00\u5bfa\u95e8\u7684\u3002 \u3000\u3000\u667a\u656c\u5927\u5e08\u7ec8\u4e8e\u60f3\u51fa\u4e86\u4e00\u4e2a\u6cd5\u5b50\uff0c\u4ed6\u6536\u4e86\u4e00\u4e2a\u5929\u8d44\u5947\u4f73\u7684\u4fd7\u5bb6\u5f1f\u5b50\u2014\u2014\u5b59\u501a\u91cd\u3002 \u3000\u3000\u56e0\u4e3a\u53f0\u51c0\u5927\u5e08\u7684\u89c4\u5b9a\u662f\u201c\u51e1\u5c11\u6797\u548c\u5c1a\u7ec8\u8eab\u4e0d\u5f97\u79bb\u5bfa\u201d\uff0c\u5b59\u501a\u91cd\u53ef\u4e0d\u662f\u548c\u5c1a\u554a\uff01 \u3000\u3000\u667a\u656c\u5927\u5e08\u4f1a\u548c\u5c11\u6797\u5bfa\u4e2d\u6240\u6709\u7684\u957f\u8f88\uff0c\u5404\u5c06\u81ea\u5df1\u6240\u77e5\u9053\u7684\u4e00\u4e9b\u6b8b\u7f3a\u4e0d\u5168\u7684\u7edd\u5b66\u7edf\u7edf\u4f20\u6388\u7ed9 \u4e86\u5b59\u501a\u91cd\uff0c\u6240\u4ee5\u5b59\u501a\u91cd\u827a\u6210\u4e0b\u5c71\u540e\uff0c\u7acb\u523b\u5c31\u6210\u4e86\u8f70\u52a8\u4e00\u65f6\u7684\u201c\u6b66\u6797\u4e4b\u79c0\u201d\uff01 \u3000\u3000\u5b59\u501a\u91cd\u7684\u4efb\u52a1\u5c31\u662f\u5bfb\u627e\u7075\u7a7a\u5927\u5e08\u7684\u4f20\u4eba\uff0c\u4e8e\u662f\u4ed6\u6ce8\u610f\u6b66\u6797\u4e2d\u4e00\u5207\u51fa\u7c7b\u62d4\u8403\u7684\u9ad8\u624b\u2014\u2014\u4e8e \u662f\u4ed6\u6ce8\u610f\u5230\u65b0\u8fd1\u540d\u566a\u5929\u4e0b\u7684\u201c\u6885\u9999\u795e\u5251\u201d\u8f9b\u6377\uff01 \u3000\u3000\u4ed6\u8ddf\u8e2a\u8f9b\u6377\uff0c\u65e0\u7f18\u65e0\u6545\u548c\u8f9b\u6377\u4ea4\u4e0a\u4e86\u624b\uff0c\u7b49\u5230\u8f9b\u6377\u65bd\u51fa\u5e73\u51e1\u4e0a\u4eba\u6240\u6388\u7684\u201c\u5927\u884d\u5341\u5f0f\u201d \u65f6\uff0c\u4ed6\u53c8\u60ca\u559c\u5730\u53d1\u73b0\u8f9b\u6377\u6240\u7528\u7684\u62db\u5f0f\u7adf\u4f3c\u5931\u4f20\u7684\u5c11\u6797\u7edd\u5b66\u201c\u5e03\u8fbe\u4e09\u5f0f\u201d\uff0c\u4e8e\u662f\u4ed6\u6ca1\u5934\u6ca1\u8111\u5730 \u505c\u6b62\u62fc\u6597\uff0c\u56de\u8eab\u5c31\u5f80\u5c11\u6797\u5bfa\u5954\u53bb\u2014\u2014 \u3000\u3000\u8dd1\u51fa\u4e0d\u53ca\u534a\u91cc\uff0c\u5374\u78b0\u4e0a\u5c11\u6797\u7b2c\u4e8c\u4ee3\u7684\u9996\u5f92\u81ea\u6cd5\uff0c\u4ed6\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u60f3\u5230\u81ea\u6cd5\u7adf\u5f97\u638c\u95e8 \u4eba\u7279\u51c6\u4e0b\u5c71\uff0c\u53ef\u89c1\u4e00\u5b9a\u53d1\u751f\u5929\u5927\u7684\u4e8b\u6545\u2014\u2014 \u3000\u3000\u81ea\u6cd5\u78b0\u4e0a\u5b59\u501a\u91cd\uff0c\u53eb\u4ed6\u7acb\u523b\u56de\u5c71\uff0c\u4e0d\u7528\u518d\u5728\u6c5f\u6e56\u4e0a\u80e1\u95ef\uff0c\u56e0\u4e3a\u5e08\u7236\u5df2\u53d1\u73b0\u4e1c\u6d77\u5927\u6222\u5c9b\u7684 \u5e73\u51e1\u4e0a\u4eba\u6781\u53ef\u80fd\u5c31\u662f\u767e\u5e74\u524d\u7684\u7075\u7a7a\u3002\u5b59\u501a\u91cd\u4e5f\u5c06\u81ea\u5df1\u548c\u8f9b\u6377\u4ea4\u624b\u7684\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\uff0c\u4ed6\u5bf9\u5e08\u5144 \u8bf4\uff1a\u201c\u5e08\u7236\u4eec\u6240\u8bf4\u7684\u53ea\u4e0d\u8fc7\u662f\u2018\u53ef\u80fd\u2019\uff0c\u800c\u773c\u4e0b\u7684\u8fd9\u4e00\u6761\u7ebf\u7d22\u662f\u94c1\u822c\u7684\u4e8b\u5b9e\uff0c\u54b1\u4eec\u5148\u63a2\u660e\u4e86 \u518d\u56de\u5bfa\u4e0d\u8fdf\u3002\u201d \u3000\u3000\u81ea\u6cd5\u548c\u5c1a\u542c\u4ed6\u8bf4\u5f97\u6709\u7406\uff0c\u4e8e\u662f\u7ed5\u6377\u5f84\u5230\u524d\u9762\u622a\u4f4f\u8f9b\u6377\uff0c\u8981\u6c42\u548c\u8f9b\u6377\u6bd4\u5212\uff0c\u7b49\u8f9b\u6377\u65bd\u51fa \u201c\u5927\u884d\u5341\u5f0f\u201d\u65f6\uff0c\u81ea\u6cd5\u51dd\u795e\u6ce8\u89c6\uff0c\u53d1\u89c9\u786e\u4f3c\u672c\u95e8\u5931\u4f20\u7684\u201c\u5e03\u8fbe\u4e09\u5f0f\u201d\uff0c\u4e8e\u662f\u4ed6\u548c\u5b59\u501a\u91cd\u5546\u91cf \u51fa\u9762\u95ee\u4e2a\u6e05\u695a\u2014\u2014 \u3000\u3000\u5b59\u501a\u91cd\u5c11\u5e74\u8001\u6210\uff0c\u5bf9\u5e08\u5144\u9053\uff1a\u201c\u773c\u4e0b\u54b1\u4eec\u518d\u51fa\u53bb\u591a\u534a\u4f1a\u5f15\u8d77\u4ed6\u7684\u8bef\u4f1a\uff0c\u54b1\u4eec\u4e0d\u5982\u5148\u7ed5\u5230 \u524d\u9762\u7684\u534e\u5bb6\u9547\u53bb\u7b49\u4ed6\uff0c\u7b49\u4ed6\u5230\u65f6\u518d\u597d\u8a00\u76f8\u95ee\u3002\u201d \u3000\u3000\u81ea\u6cd5\u548c\u5c1a\u867d\u662f\u9996\u5f92\uff0c\u4f46\u4e3a\u4eba\u5341\u5206\u968f\u548c\u3002\u5b59\u501a\u91cd\u53c8\u662f\u4e8c\u4ee3\u5f1f\u5b50\u4e2d\u6700\u53d7\u540c\u95e8\u5668\u91cd\u7684\u4eba\u7269\uff0c\u4ed6 \u4e5f\u5c31\u542c\u4e86\u5b59\u501a\u91cd\u7684\u8ba1\u8f83\uff0c\u65e5\u591c\u517c\u7a0b\u8d76\u5230\u4e86\u534e\u5bb6\u9547\u2014\u2014 \u3000\u3000\u4ed6\u4eec\u5728\u534e\u5bb6\u9547\u4e00\u7b49\u5c31\u7b49\u4e86\u56db\u4e94\u5929\uff0c\u5374\u4e0d\u89c1\u8f9b\u6377\u6765\u5230\u2014\u2014\u5f53\u7136\uff0c\u4ed6\u4eec\u4e0d\u77e5\u8f9b\u6377\u88ab\u5173\u4e2d\u4e5d\u8c6a \u56f4\u653b\uff0c\u9669\u4e9b\u513f\u9001\u4e86\u5c0f\u547d\u3002 \u3000\u3000\u76f4\u5230\u5929\u4e0b\u6b66\u6797\u9f50\u4f1a\u594e\u5c71\uff0c\u5b59\u501a\u91cd\u53c8\u53d1\u73b0\u8f9b\u6377\u7684\u8e2a\u8ff9\uff0c\u4ed6\u4e00\u9762\u8ddf\u8e2a\u4e0a\u4e86\u594e\u5c71\uff0c\u4e00\u547d\u7531\u81ea\u6cd5 \u548c\u5c1a\u8d76\u56de\u5c11\u6797\u5bfa\u62a5\u4fe1\u3002 \u3000\u3000\u7b49\u5230\u5e73\u51e1\u4e0a\u4eba\u7a81\u73b0\u201c\u65e0\u4e3a\u5385\u201d\uff0c\u4e34\u654c\u9762\u53d7\u8f9b\u6377\u7edd\u5b66\uff0c\u529b\u7834\u4e86\u5929\u4e9b\u6765\u5ba2\u91d1\u9c81\u5384\uff0c\u5b59\u501a\u91cd\u786e \u5b9a\u8f9b\u6377\u4e43\u662f\u7075\u7a7a\u5927\u5e08\u4f20\u4eba\uff0c\u6b63\u8981\u8bbe\u6cd5\u5957\u95ee\u65f6\uff0c\u5e73\u51e1\u4e0a\u4eba\u5374\u6293\u7740\u8f9b\u6377\u4e00\u53bb\u65e0\u8e2a\u3002 \u3000\u3000\u5b59\u501a\u91cd\u53ea\u597d\u8fde\u591c\u8d76\u56de\u5c11\u6797\uff0c\u4ed6\u5c06\u8f9b\u6377\u7684\u5251\u6cd5\u548c\u5e73\u51e1\u4e0a\u4eba\u7684\u5f62\u8c8c\u63cf\u8ff0\u4e00\u756a\uff0c\u667a\u656c\u5927\u5e08\u5ffd\u7136 \u559c\u6781\u6d41\u6cea\u9053\uff1a \u3000\u3000\u201c\u6211\u4f5b\u6709\u7075\uff0c\u7075\u7a7a\u7956\u5e08\u5df2\u6210\u4e0d\u574f\u4e4b\u8eab\uff0c\u73b0\u5728\u4ecd\u5728\u4eba\u95f4\uff0c\u5fc5\u662f\u90a3\u5e73\u51e1\u4e0a\u4eba\u65e0\u5f02\u2014\u2014\u201d \u3000\u3000\u4e8e\u662f\uff0c\u5c11\u6797\u5bfa\u6240\u6709\u7684\u91cd\u8981\u4eba\u7269\u503e\u5bfa\u800c\u51fa\uff0c\u9f50\u8d76\u5411\u5927\u6222\u5c9b\u2014\u2014 \u3000\u3000\u8fd9\u7fa4\u548c\u5c1a\u6084\u5730\u8d76\u7740\u8def\uff0c\u5374\u4e0d\u77e5\u5df2\u88ab\u4eba\u76ef\u4e0a\u4e86\u68a2\u2014\u2014 \u3000\u3000\u90a3\u91d1\u9c81\u5384\u548c\u4ed6\u5e08\u5144\u52a0\u5927\u5c14\u5230\u4e2d\u539f\u6765\u65f6\uff0c\u4ed6\u4eec\u5e08\u7236\u53ea\u5bf9\u4ed6\u4eec\u8bf4\uff1a\u201c\u4e2d\u539f\u7684\u6b66\u5b66\u6709\u9650\u5f97\u5f88\uff0c \u53ea\u6709\u4e00\u6d3e\u53eb\u505a\u5c11\u6797\u5bfa\u7684\u548c\u5c1a\u6bd4\u8f83\u5389\u5bb3\uff0c\u4f60\u4eec\u8981\u60f3\u5a01\u9707\u4e2d\u539f\u5c31\u5148\u5f97\u6253\u8de8\u8fd9\u4e9b\u548c\u5c1a\u3002\u201d \u3000\u3000\u2014\u2014\u5f53\u7136\uff0c\u4ed6\u4eec\u7684\u5e08\u7236\u5e76\u4e0d\u77e5\u767e\u5e74\u6765\u6b66\u6797\u5f62\u52bf\u5927\u6613\uff0c\u5c11\u6797\u5bfa\u5df2\u662f\u9ed8\u9ed8\u65e0\u95fb\u7684\u4e86\u3002 \u3000\u3000\u6240\u4ee5\u201c\u65e0\u4e3a\u5385\u201d\u5927\u4f1a\u5929\u4e0b\u82f1\u96c4\u65f6\uff0c\u90a3\u6d51\u4eba\u52a0\u5927\u5c14\u4e00\u8fdb\u6765\u5c31\u56db\u5904\u5bfb\u4eba\uff0c\u6b63\u662f\u60f3\u5bfb\u4ed6\u5e08\u7236\u6240 \u8c13\u7684\u201c\u5c11\u6797\u548c\u5c1a\u201d\uff0c\u7ed3\u679c\u5f53\u82e6\u5eb5\u4e0a\u4eba\u51fa\u573a\u7684\u65f6\u5019\uff0c\u4ed6\u5927\u559c\u4ee5\u4e3a\u662f\u5c11\u6797\u50e7\u4eba\uff0c\u4f46\u542c\u5f97\u61c2\u6c49\u8bed\u7684 \u91d1\u9c81\u5384\u544a\u8bc9\u4ed6\u82e6\u5eb5\u4e0a\u4eba\u5e76\u975e\u5c11\u6797\u4e43\u662f\u5ce8\u5a9a\u65f6\uff0c\u52a0\u5927\u5c14\u5927\u89c9\u5931\u671b\u3002 \u3000\u3000\u91d1\u9c81\u5384\u88ab\u8f9b\u6377\u632b\u8d25\u4e4b\u540e\uff0c\u6052\u6cb3\u4e09\u4f5b\u542c\u4e86\u4ed6\u4eec\u7684\u63cf\u7ed8\uff0c\u4e5f\u731c\u5230\u5927\u6222\u5c9b\u4e3b\u8eab\u4e0a\uff0c\u4e8e\u662f\u4ed6\u4eec\u4e09 \u4eba\u7531\u91d1\u9c81\u5384\u5e26\u8def\u5927\u4e86\u4e2d\u539f\u2014\u2014 \u3000\u3000\u4ed6\u4eec\u6b63\u6101\u4e0d\u77e5\u5927\u6222\u5c9b\u6240\u5728\u65f6\uff0c\u91d1\u9c81\u5384\u5374\u5077\u542c\u5230\u5c11\u6797\u50e7\u4eba\u7684\u8c08\u8bdd\uff0c\u77e5\u9053\u4ed6\u4eec\u4e5f\u8981\u53bb\u5bfb\u5927\u6222 \u5c9b\u4e3b\uff0c\u4e8e\u662f\u5c31\u6697\u6697\u5c3e\u968f\u7740\u5c11\u6797\u548c\u5c1a\uff0c\u8fd9\u56db\u4eba\u7684\u529f\u529b\u6df1\u539a\uff0c\u5c11\u6797\u50e7\u4eba\u7adf\u832b\u7136\u4e0d\u77e5\u3002 \u3000\u3000\u5230\u4e86\u5927\u6222\u5c9b\uff0c\u4e24\u4f19\u4eba\u90fd\u6251\u4e86\u7a7a\uff0c\u56e0\u4e3a\u5e73\u51e1\u4e0a\u4eba\u6b63\u5e26\u7740\u8f9b\u6377\u5728\u5c0f\u6222\u5c9b\u4e0a\u548c\u6167\u5927\u5e08\u8d4c\u80dc\uff0c\u7ed3 \u679c\u6052\u6cb3\u4e09\u4f5b\u53cd\u548c\u5c11\u6797\u50e7\u52a8\u4e0a\u4e86\u624b\u2026\u2026 \u3000\u3000\u2014\u2014\u8f9b\u6377\u867d\u662f\u51ed\u60f3\u50cf\uff0c\u4f46\u662f\u914d\u5408\u5e73\u51e1\u4e0a\u4eba\u6240\u8bf4\u7684\uff0c\u4ed6\u6599\u60f3\u7684\u548c\u4ee5\u4e0a\u6240\u8ff0\u7adf\u662f\u5dee\u4e0d\u591a\u3002 \u3000\u3000\u5929\u4e0a\u7684\u661f\u513f\u7728\u7740\u773c\uff0c\u6d77\u6d9b\u58f0\u5728\u8fd9\u606c\u9759\u7684\u591c\u4e2d\u683c\u5916\u6e05\u6670\uff0c\u5468\u906d\u90fd\u662f\u9ed1\u7684\uff0c\u53ea\u90a3\u6d77\u5cb8\u8fb9\u7f18\u4e0a \u4e00\u6761\u7ec6\u7a84\u7684\u6d6a\u82b1\u5728\u6cdb\u7740\u767d\u5149\u2014\u2014 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4f4f\u4e86\u76ee\uff0c\u4ef0\u5929\u89c2\u671b\uff0c\u767d\u9aef\u968f\u98ce\u800c\u52a8\uff0c\u50cf\u4e00\u5c0a\u77f3\u50cf\u822c\u4e00\u8bed\u4e0d\u53d1\u3002 \u3000\u3000\u8f9b\u6377\u6084\u58f0\u95ee\u9053\uff1a\u201c\u90a3\u4e2a\u8001\u65b9\u4e08\u7075\u955c\u5927\u5e08\u5462\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u5e08\u5144\u4ecd\u5728\u2014\u2014\u4e0d\uff0c\u7075\u955c\u5927\u5e08\u4ed6\u4ecd\u5728\u4eba\u95f4\uff01\u201d \u3000\u3000\u867d\u7136\u4ed6\u7acb\u523b\u6539\u53e3\uff0c\u4f46\u8fd9\u201c\u5e08\u5144\u201d\u4e24\u5b57\u5df2\u8bc1\u660e\u4e86\u4ed6\u6b63\u662f\u90a3\u7075\u7a7a\u5927\u5e08\uff01 \u3000\u3000\u8f9b\u6377\u6697\u9053\uff1a\u201c\u90a3\u7075\u955c\u5927\u5e08\u65e2\u662f\u5e73\u51e1\u4e0a\u4eba\u4e4b\u5e08\u5144\uff0c\u60f3\u6765\u5fc5\u4e5f\u7ec3\u6210\u4e0d\u574f\u4e4b\u8eab\uff0c\u662f\u4ee5\u4ecd\u5728\u4eba \u95f4\u2014\u2014\u554a\uff01\u5bf9\u4e86\uff0c\u5f53\u5e74\u5728\u5c0f\u6222\u5c9b\u4e0a\u4e58\u9e64\u800c\u6765\u5524\u8d70\u5e73\u51e1\u4e0a\u4eba\u7684\u8001\u548c\u5c1a\u96be\u9053\u5c31\u662f\u90a3\u7075\u955c\u5927\u5e08\uff1f\u201d \u3000\u3000\u8bfb\u8005\u5fc5\u7136\u8bb0\u5f97\uff0c\u5f53\u65e5\u8f9b\u6377\u5728\u5c0f\u6222\u5c9b\u4e0a\u8d70\u51fa\u201c\u5f52\u5143\u53e4\u9635\u201d\u540e\uff0c\u6b63\u5f53\u5e73\u51e1\u4e0a\u4eba\u4e0e\u6167\u5927\u5e08\u62fc\u6597 \u65f6\uff0c\u4e00\u4e2a\u9a91\u9e64\u8001\u50e7\u98de\u6765\u5c06\u5e73\u51e1\u4e0a\u4eba\u5524\u53bb\uff0c\u4e34\u884c\u65f6\u8fd8\u5bf9\u8f9b\u6377\u541f\u9053\uff1a \u3000\u3000\u201c\u864e\u8dc3\u9f99\u817e\u98de\u9ec4\u65f6\uff0c\u9e64\u5533\u4e00\u58f0\u6f47\u6e58\u53bb\u3002\u201d \u3000\u3000\u8fd9\u4e9b\u8bdd\u8f9b\u6377\u8fd8\u8bb0\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u4f46\u662f\u5374\u83ab\u89e3\u5176\u610f\u3002 \u3000\u3000\u84e6\u7136\u2014\u2014 \u3000\u3000\u6d77\u8fb9\u4e00\u6761\u8239\u6084\u6084\u5730\u9760\u4e0a\u4e86\u5cb8\uff0c\u8239\u4e0a\u8d70\u4e0b\u4e00\u6279\u4eba\u6765\uff0c\u4e00\u5171\u662f\u5341\u516b\u4eba\uff0c\u8d70\u8fd1\u65f6\uff0c\u53ea\u89c1\u6b63\u662f\u90a3 \u7fa4\u5c11\u6797\u548c\u5c1a\u3002 \u3000\u3000\u5c11\u6797\u7fa4\u50e7\u81ea\u5e73\u51e1\u4e0a\u4eba\u62c9\u7740\u8f9b\u6377\u98de\u8dd1\u6389\u4ee5\u540e\uff0c\u53ea\u5f97\u4e58\u7740\u8239\u7167\u7740\u5e73\u51e1\u4e0a\u4eba\u7684\u65b9\u5411\u5bfb\u6765\uff0c\u7136\u800c \u5927\u6d77\u832b\u832b\u4ed6\u4eec\u53c8\u4e0d\u77e5\u5c0f\u6222\u5c9b\u4e4b\u6240\u5728\uff0c\u4e00\u76f4\u6478\u5230\u6b64\u523b\u624d\u7b97\u627e\u5230\u4e86\u5c0f\u6222\u5c9b\u3002 \u3000\u3000\u5f53\u8f9b\u6377\u53d1\u73b0\u4e86\u8fd9\u6279\u548c\u5c1a\u65f6\uff0c\u90a3\u4e3a\u9996\u7684\u548c\u5c1a\u4e5f\u77a7\u89c1\u4e86\u8f9b\u6377\u53ca\u5e73\u51e1\u4e0a\u4eba\uff0c\u4ed6\u4eec\u6b22\u547c\u4e00\u58f0\uff0c\u98de \u5954\u800c\u6765\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5403\u4e86\u4e00\u60ca\uff0c\u8d77\u8eab\u5c31\u60f3\u56de\u8dd1\uff0c\u4f46\u662f\u5ffd\u7136\u4ed6\u7684\u50e7\u888d\u88ab\u4e00\u4eba\u7d27\u7d27\u626f\u4f4f\u3002 \u3000\u3000\u4ed6\u5fd9\u56de\u5934\u4e00\u770b\uff0c\u626f\u8863\u8896\u7684\u6b63\u662f\u8f9b\u6377\u3002 \u3000\u3000\u53ea\u89c1\u8f9b\u6377\u8138\u4e0a\u663e\u51fa\u51db\u7136\u4e4b\u8272\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4e0a\u4eba\uff0c\u60a8\u7edd\u4e0d\u80fd\u518d\u8eb2\u907f\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e0d\u7981\u4e00\u6115\uff0c\u53ea\u6b64\u7f13\u5f97\u4e00\u7f13\uff0c\u90a3\u51e0\u4e2a\u5c11\u6797\u548c\u5c1a\u597d\u5feb\u811a\u7a0b\uff0c\u5df2\u7eb5\u5230\u773c\u524d\u3002 \u3000\u3000\u4e00\u5341\u516b\u4eba\u5657\u5730\u4e00\u58f0\u53c8\u9f50\u9f50\u8dea\u4e0b\uff0c\u4e3a\u9996\u4ecd\u662f\u90a3\u5c11\u6797\u638c\u95e8\u667a\u656c\u5927\u5e08\uff0c\u90a3\u201c\u6b66\u6797\u4e4b\u79c0\u201d\u5b59\u501a\u91cd \u5374\u8dea\u5728\u6700\u540e\u3002 \u3000\u3000\u667a\u656c\u5927\u5e08\u53e9\u5934\u9053\uff1a\u201c\u7075\u7a7a\u7956\u5e08\uff0c\u60a8\u2014\u2014\u60a8\u8fd8\u8981\u9690\u7792\u5f1f\u5b50\u4e48\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6025\u5f97\u53cc\u624b\u4e71\u6447\uff0c\u5927\u58f0\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e0d\u662f\uff0c\u544a\u8bc9\u4f60\u4eec\u6211\u8001\u4eba\u5bb6\u4e0d\u662f\u7075\u7a7a\u5927\u5e08\u5c31\u4e0d \u662f\u7075\u7a7a\u5927\u5e08\u2014\u2014\u201d \u3000\u3000\u667a\u656c\u5927\u5e08\u60f3\u662f\u5450\u8a00\u4e8e\u53e3\uff0c\u554a\u4e86\u4e24\u58f0\u5374\u8bf4\u4e0d\u51fa\u8bdd\u6765\uff0c\u89c1\u5e73\u51e1\u4e0a\u4eba\u53c8\u8981\u8d77\u8eab\uff0c\u6025\u5f97\u53e9\u5934\u6d41\u6cea \u9053\uff1a\u201c\u5f1f\u5b50\u65e0\u80fd\uff0c\u53ea\u2014\u2014\u53ea\u671b\u7956\u5e08\u770b\u5728\u2014\u2014\u770b\u5728\u4f5b\u7956\u4efd\u4e0a\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5927\u53eb\u9053\uff1a\u201c\u6709\u8bdd\u597d\u8bf4\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u5feb\u83ab\u54ed\uff0c\u4e00\u54ed\u5c31\u6d53\u5305\u4e86\u2014\u2014\u201d \u3000\u3000\u667a\u656c\u5927\u5e08\u88ab\u5f04\u5f97\u54ed\u7b11\u4e0d\u5f97\uff0c\u4ed6\u60f3\u5230\u5c11\u6797\u5bfa\u5343\u5e74\u58f0\u5a01\u7684\u91cd\u62c5\uff0c\u5fc3\u4e2d\u4e00\u9635\u70ed\u8840\u4e0a\u6d8c\uff0c\u54c7\u5730\u4e00 \u58f0\u5410\u51fa\u4e00\u53e3\u9c9c\u8840\u6765\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e00\u770b\u5927\u60ca\uff0c\u62a2\u524d\u5728\u667a\u656c\u5927\u5e08\u80cc\u4e0a\u62cd\u4e86\u4e00\u638c\uff0c\u53c8\u5728\u4ed6\u80f8\u524d\u63c9\u4e86\u4e24\u4e0b\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14 \u9053\uff1a\u201c\u5509\uff01\u4f60\u4eec\u8fd9\u662f\u4f55\u82e6\u5462\uff1f\u6211\u2014\u2014\u6211\u544a\u8bc9\u4f60\u4eec\u5427\uff0c\u6211\u6b63\u662f\u90a3\u7075\u7a7a\u5927\u5e08\u2014\u2014\u201d \u3000\u3000\u667a\u656c\u4e00\u542c\u5e73\u51e1\u4e0a\u4eba\u3001\u627f\u8ba4\u81ea\u5df1\u662f\u7075\u7a7a\u5927\u5e08\uff0c\u4e0d\u7981\u559c\u5f97\u4e00\u8dc3\u800c\u8d77\uff0c\u4f46\u968f\u5373\u53c8\u8dea\u4e0b\u9053\uff1a\u201c\u5f1f \u5b50\u2014\u2014\u5f1f\u5b50\u4e0d\u77e5\u8be5\u8bf4\u4ec0\u4e48\u597d\uff0c\u7956\u5e08\u2014\u2014\u7956\u5e08\u2014\u2014\u8fd9\u4e9b\u5e74\u6765\u53ef\u5b89\u597d\uff1f\u5509\uff01\u5929\u53ef\u601c\u89c1\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u4ed6\u53c8\u4e0d\u7531\u81ea\u4e3b\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7684\u8138\u4e0a\u95ea\u8fc7\u4e00\u4e1d\u6fc0\u52a8\u7684\u8868\u60c5\uff0c\u4f46\u968f\u5373\u53c8\u6062\u590d\u51b7\u6f20\u7684\u9762\u5bb9\u3002 \u3000\u3000\u667a\u656c\u5927\u5e08\u98a4\u58f0\u9053\uff1a\u201c\u5f1f\u5b50\u6597\u80c6\u8bf7\u7956\u5e08\u56de\u5bfa\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u62ac\u5934\u7126\u6025\u5730\u6ce8\u89c6\u7740\u5e73\u51e1\u4e0a\u4eba\uff0c\u5176\u4ed6\u5c11\u6797\u5bfa\u7684\u548c\u5c1a\u4e5f\u90fd\u51dd\u89c6\u7740\u5e73\u51e1\u4e0a\u4eba\uff0c\u8f9b \u6377\u4e5f\u540c\u6837\u2014\u2014 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4ef0\u9996\u89c2\u5929\uff0c\u4e00\u8bed\u4e0d\u53d1\u3002 \u3000\u3000\u667a\u656c\u5927\u5e08\u53ea\u597d\u53c8\u9053\uff1a\u201c\u5f1f\u5b50\u667a\u656c\u7387\u5c11\u6797\u95e8\u4eba\u8bf7\u7956\u5e08\u77a7\u5728\u4f5b\u7956\u4efd\u4e0a\uff0c\u968f\u5f1f\u5b50\u56de\u53bb\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136\u957f\u53f9\u4e00\u58f0\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u505a\u4e86\u767e\u5e74\u7684\u91ce\u548c\u5c1a\uff0c\u8981\u6211\u56de\u53bb\u662f\u4e0d\u53ef\u80fd\u7684 \u4e86\u2014\u2014\u201d \u3000\u3000\u5c11\u6797\u7fa4\u50e7\u542c\u5230\u8fd9\u91cc\u90fd\u662f\u5fc3\u4e2d\u4e00\u6c89\uff0c\u4e0d\u6599\u5e73\u51e1\u4e0a\u4eba\u53c8\u63a5\u7740\u9053\uff1a\u201c\u53ea\u662f\uff0c\u53ea\u662f\u6211\u8001\u4eba\u5bb6\u7a76\u7adf \u662f\u51fa\u81ea\u5c11\u6797\u5bfa\u95e8\uff0c\u5e73\u751f\u6b66\u5b66\u867d\u7136\u5927\u591a\u81ea\u5df1\u6240\u521b\uff0c\u4f46\u662f\u57fa\u672c\u5374\u662f\u4ece\u85cf\u7ecf\u9601\u4e2d\u609f\u5f97\u7684\uff0c\u662f\u4ee5\u6211\u4e00 \u5b9a\u5c06\u8fd9\u767e\u5e74\u5e26\u8d70\u7684\u5c11\u6797\u7edd\u5b66\u5f52\u8fd8\u7ed9\u5c11\u6797\u2014\u2014\u201d \u3000\u3000\u667a\u656c\u5927\u5e08\u8fd8\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u7acb\u523b\u88ab\u4ed6\u80cc\u540e\u4e00\u4e2a\u8001\u548c\u5c1a\u626f\u8863\u6b62\u4f4f\u3002\u5e73\u51e1\u4e0a\u4eba\u53c8\u7ee7\u7eed\u9053\uff1a\u201c\u6211 \u77a7\u8fd9\u5a03\u513f\u751a\u662f\u806a\u660e\u53ef\u6559\uff0c\u5c31\u7740\u4ed6\u7559\u5728\u6211\u5c9b\u4e0a\uff0c\u6211\u5b9a\u7136\u628a\u6240\u6709\u5c11\u6797\u7edd\u5b66\u503e\u56ca\u76f8\u6388\u3002\u201d\u8bf4\u7740\u6307\u4e86 \u6307\u8dea\u5728\u6700\u540e\u7684\u5b59\u501a\u91cd\u3002 \u3000\u3000\u667a\u656c\u5927\u5e08\u89c1\u5e73\u51e1\u4e0a\u4eba\u5982\u6b64\u8bf4\uff0c\u77e5\u9053\u8981\u8bf7\u4ed6\u56de\u5bfa\u662f\u4e0d\u53ef\u80fd\u7684\u4e86\uff0c\u4f46\u5e73\u51e1\u4e0a\u4eba\u65e2\u7b54\u5e94\u4f20\u5b59\u501a \u91cd\u7edd\u827a\uff0c\u90a3\u4e48\u5c11\u6797\u5bfa\u7edd\u5b66\u91cd\u73b0\u603b\u7b97\u6709\u4e86\u5e0c\u671b\uff0c\u4e8e\u662f\u7ad9\u4e86\u8d77\u6765\u3002 \u3000\u3000\u8f9b\u6377\u5ffd\u7136\u89c1\u90a3\u667a\u656c\u5927\u5e08\u5341\u5206\u5c34\u5c2c\u5730\u77a7\u7740\u81ea\u5df1\uff0c\u4f3c\u4e4e\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u51b0\u96ea\u806a\u660e\uff0c\u7acb\u523b\u77e5\u9053\u667a \u656c\u5927\u5e08\u662f\u56e0\u81ea\u5df1\u8eab\u4efd\u800c\u4e3a\u96be\uff0c\u56e0\u4e3a\u667a\u656c\u5927\u5e08\u4ee5\u4e3a\u4ed6\u662f\u5e73\u51e1\u4e0a\u4eba\u7684\u5f92\u5f1f\uff0c\u90a3\u4e48\u4ed6\u5c31\u6210\u4e86\u5c11\u6797\u4f17 \u50e7\u7684\u524d\u8f88\uff0c\u800c\u4ed6\u5e74\u9f84\u53c8\u6041\u5c0f\uff0c\u662f\u4ee5\u4ed6\u7acb\u523b\u5de7\u5999\u5730\u4e0a\u524d\u5bf9\u5b59\u501a\u91cd\u9053\uff1a\u201c\u5b59\u5144\uff0c\u606d\u559c\u4f60\u5566\uff0c\u4f60\u7adf \u5f97\u4e86\u5e73\u51e1\u4e0a\u4eba\u8001\u524d\u8f88\u7684\u9752\u7750\uff0c\u8fd9\u771f\u662f\u5343\u8f7d\u4e00\u904d\u7684\u5947\u7f18\u54e9\u3002\u201d \u3000\u3000\u5b59\u501a\u91cd\u542c\u4ed6\u79f0\u5e73\u51e1\u4e0a\u4eba\u4e3a\u201c\u8001\u524d\u8f88\u201d\u800c\u4e0d\u79f0\u201c\u5e08\u7236\u201d\uff0c\u4e0d\u7981\u5927\u5947\u9053\uff1a\u201c\u600e\u4e48\u8f9b\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u54ea\u6709\u8fd9\u4efd\u798f\u6c14\u505a\u4e0a\u4eba\u7684\u5f92\u5f1f\uff0c\u4e0a\u4eba\u4e0d\u8fc7\u7565\u4e3a\u6307\u70b9\u5144\u5f1f\u7f62\u4e86\u2014\u2014\u201d \u3000\u3000\u8fd9\u53e5\u8bdd\u5c31\u660e\u767d\u8bf4\u51fa\u4ed6\u5e76\u975e\u5e73\u51e1\u4e0a\u4eba\u4e4b\u5f92\uff0c\u4e8e\u662f\u667a\u656c\u5927\u5e08\u9053\uff1a\u201c\u501a\u513f\uff0c\u4f60\u5343\u4e07\u5f97\u597d\u597d\u8ddf\u7740 \u7956\u5e08\u7ec3\u529f\uff0c\u54b1\u4eec\u5c11\u6797\u5bfa\u7684\u5149\u5927\u5168\u5728\u4f60\u8eab\u4e0a\u5566\u2014\u2014\u7956\u5e08\uff0c\u5f1f\u5b50\u4eec\u8fd9\u5c31\u56de\u53bb\u5566\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5fae\u5fae\u4e00\u7b11\uff0c\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u667a\u656c\u53c8\u5bf9\u8f9b\u6377\u5408\u4ec0\u9053\uff1a\u201c\u8f9b\u65bd\u4e3b\uff0c\u54b1\u4eec\u540e\u4f1a\u6709\u671f\u2014\u2014\u201d \u3000\u3000\u63a5\u7740\u7387\u9886\u95e8\u4eba\uff0c\u4e00\u884c\u5341\u4e03\u4eba\u5306\u5306\u800c\u53bb\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u671b\u7740\u8fd9\u7fa4\u201c\u540e\u8f88\u201d\u4e0a\u8239\u800c\u53bb\uff0c\u624d\u8f7b\u8f7b\u53f9\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u8f70\u7136\u4e00\u58f0\u5de8\u54cd\uff0c\u4e00\u7247\u9ed1\u5f71\u5982\u4e4c\u4e91\u76d6\u822c\u5730\u843d\u5411\u4e09\u4eba\u5934\u9876\u539f\u6765\u90a3\u6839\u77f3\u7b0b\u5403\u6052\u6cb3\u4e09\u4f5b\u638c \u529b\u524a\u53bb\u9876\u7aef\uff0c\u53c8\u88ab\u65e0\u6839\u751f\u4ee5\u4e0a\u4e58\u5185\u529b\u6253\u5728\u77f3\u6839\u90e8\uff0c\u8868\u9762\u867d\u7136\u65e0\u5f02\uff0c\u5176\u5b9e\u6839\u90e8\u5df2\u662f\u6298\u65ad\uff0c\u8fd9\u65f6 \u7adf\u8f70\u7136\u5012\u4e0b\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5927\u559d\u4e00\u58f0\uff0c\u53cc\u638c\u5411\u5916\u4e00\u5212\uff0c\u9661\u7136\u4e00\u5408\uff0c\u4e00\u80a1\u72c2\u98ce\u5377\u51fa\uff0c\u8f70\u7136\u53c8\u662f\u4e00\u58f0\u5de8\u54cd\uff0c\u90a3\u77f3\u7b0b \u7adf\u88ab\u51fb\u6210\u5343\u4e07\u788e\u5757\uff0c\u6f2b\u5929\u98de\u51fa\uff01 \u3000\u3000\u8f9b\u6377\u8fd9\u62db\u4e43\u662f\u65b0\u8fd1\u4ece\u5e73\u51e1\u4e0a\u4eba\u5b66\u6765\u7684\u201c\u7a7a\u7a7a\u638c\u6cd5\u201d\u4e2d\u7684\u4e00\u62db\uff0c\u5524\u4f5c\u201c\u98de\u6d6a\u6392\u7a7a\uff0c\u201d\u4e43\u662f \u7a7a\u7a7a\u638c\u6cd5\u4e2d\u6210\u529b\u6700\u5f3a\u7684\u4e00\u5f0f\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u559d\u91c7\u9053\uff1a\u201c\u5a03\u513f\uff0c\u771f\u597d\u638c\u529b\uff01\u201d \u3000\u3000\u6700\u60ca\u7684\u83ab\u8fc7\u4e8e\u6b66\u6797\u4e4b\u79c0\u5b59\u501a\u91cd\u4e86\uff0c\u4e24\u6708\u524d\u4ed6\u8fd8\u548c\u8f9b\u6377\u4ea4\u8fc7\u624b\uff0c\u4e0d\u6599\u4e24\u6708\u4e0d\u89c1\uff0c\u4ed6\u7684\u529f\u529b \u4f3c\u4e4e\u53c8\u7cbe\u8fdb\u4e86\u4e00\u5927\u622a\uff01 \u3000\u3000\u5929\u6e10\u6e10\u4eae\u4e86\uff0c\u66d9\u5149\u666e\u7167\uff0c\u5c0f\u6222\u5c9b\u4e0a\uff0c\u6653\u98ce\u6b8b\u6708\u2014\u2014 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5de6\u624b\u643a\u7740\u8f9b\u6377\uff0c\u53f3\u624b\u643a\u7740\u5b59\u501a\u91cd\uff0c\u7f13\u7f13\u8d70\u5411\u6d77\u6ee8\u3002 \u3000\u3000\u8239\u5230\u5927\u6222\u5c9b\uff0c\u5e73\u51e1\u4e0a\u4eba\u548c\u5b59\u501a\u91cd\u4e0a\u4e86\u5cb8\uff0c\u8f9b\u6377\u5374\u7559\u5728\u8239\u4e0a\u9053\uff1a\u201c\u665a\u8f88\u5c1a\u6709\u6025\u4e8b\u8981\u56de\u4e2d \u571f\uff0c\u5c31\u6b64\u544a\u522b\uff0c\u5f02\u65e5\u6709\u6687\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u5a03\u513f\u65e2\u6709\u2018\u8981\u4e8b\u2019\uff0c\u8d70\u5c31\u662f\u4e86\uff0c\u4e0d\u8981\u6765\u4ec0\u4e48\u5f02\u4e0e\u4e0d\u5f02\u7684\u4e00\u5957\u5566\u2014\u2014 \u597d\uff01\u501a\u513f\uff0c\u54b1\u4eec\u8d70\uff01\u201d \u3000\u3000\u8bf4\u7740\u4e00\u6293\u5b59\u501a\u91cd\uff0c\u4e24\u4e2a\u8d77\u843d\uff0c\u5c31\u6d88\u5931\u5728\u6811\u6797\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u6014\u6014\u5730\u671b\u7740\u4e24\u4eba\u80cc\u5f71\u6d88\u5931\uff0c\u4e00\u8f6c\u8eab\uff0c\u626f\u8d77\u5e06\u513f\uff0c\u5212\u5165\u6d77\u4e2d\u3002 \u3000\u3000\u6668\u98ce\u751a\u7d27\uff0c\u8239\u884c\u5982\u7bad\uff0c\u8f9b\u6377\u62ab\u895f\u5f53\u98ce\uff0c\u987f\u89c9\u5fc3\u65f7\u795e\u6021\uff0c\u4ed6\u5f15\u542d\u957f\u5578\uff0c\u5982\u9f99\u541f\u822c\u7684\u5578\u58f0\u968f \u7740\u6d77\u98ce\u4f20\u51fa\u8001\u8fdc\u2014\u2014 \u3000\u3000\u5ffd\u7136\uff0c\u6de1\u6de1\u7684\u96fe\u6c14\uff0c\u50cf\u8f7b\u7eb1\u822c\u4ece\u56db\u6d77\u5347\u8d77\uff0c\u98d8\u6e3a\u8885\u8885\u4e4b\u4e2d\uff0c\u4f7f\u5468\u56f4\u666f\u7269\u9677\u5165\u8ff7\u8ff7\u7cca\u7cca\u3002 \u3000\u3000\u970e\u65f6\uff0c\u96fe\u6d53\u4e86\u8d77\u6765\uff0c\u5468\u56f4\u90fd\u662f\u767d\u832b\u832b\u4e00\u7247\u3002\u8fd9\u9aa4\u8d77\u7684\u5927\u96fe\u6b63\u662f\u4e1c\u6d77\u7fa4\u5c7f\u95f4\u7684\u4e00\u5927\u7279\u8272\uff0c \u800c\u8fd9\u79cd\u65f6\u8d77\u7684\u5927\u96fe\u4e5f\u4e3a\u4e16\u5916\u4e09\u4ed9\u907f\u53bb\u4e0d\u5c11\u9a9a\u6270\u4e0e\u9ebb\u70e6\u3002\u5c31\u662f\u4e16\u5c45\u4e1c\u6d77\u7684\u6e14\u592b\u4eec\u90fd\u4e07\u5206\u987e\u5fcc\u8fd9 \u79cd\u6f2b\u5929\u6d53\u96fe\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u60f3\uff1a\u201c\u7eb5\u4f7f\u96fe\u5927\uff0c\u4f46\u6b64\u65f6\u98ce\u5411\u975e\u5e38\u7a33\u5b9a\uff0c\u6211\u53ea\u8981\u628a\u5b9a\u8235\u5411\uff0c\u597d\u6b79\u80fd\u822a\u884c\u5230\u4e2d\u539f\u6cbf \u5cb8\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u61d2\u6563\u5730\u5750\u4e86\u4e0b\u6765\uff0c\u4efb\u90a3\u5c0f\u8247\u5e73\u7a33\u800c\u8f7b\u5feb\u7684\u524d\u8fdb\u3002 \u3000\u3000\u5076\u800c\uff0c\u4ed6\u4fe9\u4e0b\u8eab\u53bb\uff0c\u4f38\u624b\u638f\u4e86\u638f\u6d77\u6c34\uff0c\u4fee\u957f\u7075\u5de7\u7684\u624b\u6307\u5728\u6d77\u6c34\u4e2d\u5212\u8d77\u51e0\u9053\u7ec6\u77ed\u7684\u767d\u7ebf\uff0c \u5bfb\u5373\u6d88\u5931\u2014\u2014 \u3000\u3000\u5927\u96fe\u4e2d\uff0c\u8239\u5728\u75be\u884c\uff0c\u8f9b\u6377\u65e0\u804a\u5730\u80e1\u601d\u4e71\u60f3\u7740\u3002 \u3000\u3000\u4e8e\u662f\uff0c\u4ed6\u60f3\u5230\u4e86\u90a3\u5a07\u8273\u65e0\u6bd4\u7684\u83c1\u513f\u2014\u2014 \u3000\u3000\u4f46\u6b64\u65f6\u5f20\u83c1\u5462\uff1f\u8f9b\u6377\u4e0d\u6562\u60f3\u50cf\u8fd9\u6beb\u65e0\u7ecf\u9a8c\u8fc7\u4eba\u5fc3\u9669\u6076\u7684\u7eaf\u6d01\u5c11\u5973\uff0c\u957f\u671f\u6d89\u8db3\u6c5f\u6e56\u2014\u2014 \u3000\u3000\u597d\u957f\u4e00\u6bb5\u65f6\u95f4\u8f9b\u6377\u5982\u6b64\u8eba\u7740\uff0c\u53c8\u5750\u8d77\uff0c\u96fe\u6108\u6765\u6108\u6d53\uff0c\u5373\u4f7f\u4ee5\u4ed6\u8d85\u4eba\u7684\u76ee\u529b\uff0c\u4e94\u4e08\u4ee5\u5916\u5df2 \u662f\u6d51\u6d4a\u4e00\u7247\u4e86\u3002 \u3000\u3000\u8247\u4fa7\u6d6a\u5934\u53d8\u6210\u6709\u89c4\u5f8b\u800c\u9ad8\u6602\u5730\u987a\u7740\u8239\u5934\u5411\u524d\u51b2\u53bb\uff0c\u8fdc\u5904\u4f20\u6765\u640f\u6d6a\u4e4b\u58f0\uff0c\u4fbf\u8f9b\u6377\u76f4\u89c9\u611f \u5230\u2014\u2014\u6d77\u5cb8\u8fd1\u4e86\u3002 \u3000\u3000\u4e00\u80a1\u83ab\u540d\u7684\u632f\u594b\u4f7f\u4ed6\u4ece\u8247\u4e2d\u7ad9\u7acb\u8d77\u6765\uff0c\u4e00\u53cc\u795e\u76ee\u7d27\u7d27\u6ce8\u89c6\u7740\u6b63\u524d\u65b9\uff0c\u671f\u5f85\u90a3\u9646\u5730\u7a81\u7136\u51fa \u73b0\u7684\u90a3\u4e00\u5239\u90a3\u2014\u2014 \u3000\u3000\u96fe\u5df1\u66f4\u6d53\uff0c\u8f9b\u6377\u4ec0\u4e48\u4e5f\u770b\u4e0d\u89c1\uff0c\u7a7a\u4e2d\u53d8\u5e7b\u83ab\u6d4b\u7684\u6c34\u6c14\uff0c\u5728\u4ed6\u773c\u524d\u663e\u51fa\u5404\u5f0f\u5404\u6837\u7684\u5e7b\u5f71\u3002 \u3000\u3000\u7a81\u7136\u4e00\u9635\u6868\u51fb\u6c34\u58f0\u2014\u2014 \u3000\u3000\u5c31\u5728\u79bb\u8239\u5934\u5341\u4e08\u5de6\u53f3\u98de\u5feb\u63a0\u8fc7\u4e00\u6761\u9ed1\u5f71\uff0c\u770b\u5230\u5012\u50cf\u662f\u6761\u5c0f\u8247\uff0c\u5982\u975e\u6709\u8fd9\u6837\u5927\u8f9b\u6377\u4e5f\u770b\u4e0d \u89c1\u4e86\u3002 \u3000\u3000\u6b64\u65f6\u8f9b\u6377\u56e0\u9760\u5cb8\u5728\u5373\uff0c\u53c8\u9022\u5982\u6b64\u5927\u96fe\uff0c\u98ce\u5e06\u65e9\u5df2\u843d\u4e0b\u800c\u901f\u5ea6\u4e5f\u5927\u51cf\uff0c\u4e0d\u7981\u5947\u602a\u4ec0\u4e48\u4eba\u6562 \u5728\u8fd9\u5927\u96fe\u4e2d\u5982\u6b64\u98de\u5feb\u5730\u5212\u8247\uff1f \u3000\u3000\u6b63\u5f53\u4ed6\u4e00\u5ff5\u81f3\u6b64\uff0c\u7a81\u7136\u524d\u9762\u53c8\u4e00\u5e9e\u5927\u9ed1\u5f71\u63a0\u8fc7\uff0c\u50cf\u662f\u8258\u5de8\u5927\u6d77\u8239\u3002\u4ee5\u5b83\u4e5f\u5c3d\u901f\u524d\u8fdb\u7684\u6a21 \u6837\u770b\u6765\uff0c\u597d\u4f3c\u6b63\u7d27\u8ffd\u90a3\u524d\u9762\u5c0f\u8247\u3002 \u3000\u3000\u60f3\u662f\u8239\u4e0a\u4e4b\u4eba\u6b63\u6ce8\u610f\u524d\u9762\u9003\u9038\u8005\uff0c\u53c8\u9047\u5230\u8fd9\u5927\u96fe\uff0c\u7adf\u6ca1\u6709\u53d1\u89c9\u4ece\u65c1\u6084\u6084\u800c\u6765\u7684\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u521a\u597d\u8d76\u5230\u90a3\u5927\u8239\u8239\u5c3e\uff0c\u4e00\u628a\u62c9\u4f4f\u8235\u4e0a\u7684\u7f06\u7ef3\uff0c\u597d\u5947\u5fc3\u7684\u8d8b\u4f7f\uff0c\u4ee4\u4ed6\u4e0d\u7531\u81ea\u4e3b\u60f3\u8ddf\u4e0a \u770b\u770b\u3002 \u3000\u3000\u5927\u8239\u7684\u901f\u5ea6\u5927\u7ea6\u8f83\u524d\u884c\u5c0f\u8247\u5feb\u4e9b\uff0c\u987a\u7740\u51fb\u6c34\u58f0\uff0c\u4e0d\u4e45\u5373\u6108\u8ffd\u6108\u8fd1\uff0c\u4ece\u58f0\u97f3\u542c\u6765\u5df2\u4e0d\u8db3\u4e94 \u4e08\u4e86\u2014\u2014 \u3000\u3000\u7a81\u7136\u4e00\u9635\u7b11\u58f0\u4ece\u5927\u8239\u4e0a\u66b4\u51fa\uff0c\u7d27\u63a5\u7740\u4e00\u4e2a\u5636\u54d1\u7684\u58f0\u97f3\u64cd\u7740\u751f\u786c\u6c49\u8bed\u8bf4\u9053\uff1a\u201c\u5c0f\u59ae\u5b50\u4e56\u4e56 \u5730\u522b\u518d\u8dd1\u4e86\uff0c\u6211\u5f92\u513f\u770b\u4e0a\u4f60\u5b9e\u662f\u4f60\u5929\u5927\u8363\u5e78\u5462\uff01\u201d \u3000\u3000\u9644\u5728\u5927\u8239\u4e0b\u7684\u8f9b\u6377\u4e00\u542c\u8fd9\u58f0\u97f3\uff0c\u7adf\u5413\u4e86\u4e00\u5927\u8df3\u3002 \u3000\u3000\u201c\u539f\u6765\u662f\u6052\u6cb3\u4e09\u4f5b\uff01\u88ab\u8ffd\u7684\u4eba\u4f1a\u662f\u8c01\uff1f\u542c\u4ed6\u79f0\u8c13\u5e94\u662f\u4e00\u4e2a\u5973\u5b50\u3002\u201d\u8f9b\u6377\u6697\u5fd6\u9053\uff0c\u4e00\u770b\u624b \u4e2d\u63e1\u7740\u7684\u7ef3\u7d22\uff0c\u679c\u7136\u7f16\u7ec7\u5f97\u4e0d\u4f3c\u4e2d\u539f\u6240\u4ea7\u3002 \u3000\u3000\u201c\u4ec0\u4e48\u5973\u5b50\u4f1a\u88ab\u91d1\u4f2f\u80dc\u5937\u770b\u4e0a\u4e86\uff1f\u201d\u8f9b\u6377\u5fc3\u4e2d\u53d1\u4e86\u4e00\u4e2a\u95ee\u53f7\u3002 \u3000\u3000\u524d\u9762\u7684\u5c0f\u8247\u4e2d\u4eba\u5e76\u4e0d\u5e94\u7b54\uff0c\u53ea\u542c\u6868\u58f0\u66f4\u6025\uff0c\u4f46\u64cd\u821f\u4eba\u4f3c\u4e4e\u7528\u529b\u8fc7\u4e45\uff0c\u51fa\u624b\u529b\u5df4\u4e0d\u751a\u96c4 \u539a\uff0c\u6240\u4ee5\u8001\u662f\u9003\u4e0d\u8fdc\u53bb\u3002 \u3000\u3000\u53c8\u4e00\u4e2a\u5e74\u9752\u7684\u53e3\u97f3\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u4f55\u5fc5\u6025\u6025\u9003\u5462\uff1f\u6211\u4eec\u53c8\u4e0d\u4f1a\u5403\u4f60\uff0c\u6709\u8bdd\u597d\u597d\u8bb2\u5440\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e00\u542c\u5373\u77e5\u662f\u91d1\u9c81\u5384\uff0c\u4e0d\u7981\u604d\u7136\u5927\u609f\uff0c\u5fc3\u60f3\uff1a\u201c\u9664\u975e\u662f\u91d1\u9c81\u5384\u770b\u4e0a\u4e86\u524d\u9762\u5c0f\u821f\u4e2d\u7684\u5973 \u5b50\uff0c\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u8fd8\u4f1a\u5bf9\u4f55\u4eba\u5982\u6b64\u5c06\u5c31\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u5bf9\u5176\u95e8\u4e0b\u751a\u4e3a\u4e25\u5389\uff0c\u4f46\u8fd9\u6392\u884c\u6700\u540e\u7684\u91d1\u9c81\u5384\u5374\u662f\u5927\u5f97\u5e08\u7236\u53ca\u5e08\u53d4\u4f2f\u7684 \u6069\u5ba0\uff0c\u4e0d\u4ec5\u56e0\u4ed6\u806a\u660e\u4f36\u4fd0\uff0c\u66f4\u56e0\u4ed6\u9762\u5bb9\u4fcf\u4fca\u800c\u5584\u4e8e\u53e3\u820c\u4e4b\u529f\uff0c\u6240\u4ee5\u91d1\u9c81\u5384\u5728\u4f17\u5e08\u5144\u5f1f\u4e2d\uff0c\u771f \u53ef\u8c13\u4efb\u6240\u6b32\u4e3a\u800c\u4e0d\u4f1a\u5f97\u4e0d\u5230\u4e86\u3002 \u3000\u3000\u201c\u54fc\uff01\u86ee\u5937\u4e4b\u6c11\u5982\u4f55\u914d\u5f97\u4e0a\u54b1\u4eec\u4e2d\u539f\u793c\u4e49\u4e4b\u90a6\u7684\u513f\u5973\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5bf9\u91d1\u9c81\u5384\u5df2\u6709\u6210\u89c1\uff0c\u5f53\u7136\u4e3a\u90a3\u5973\u5b50\u62b1\u5c48\u4e86\u3002 \u3000\u3000\u91d1\u9c81\u5384\u523a\u8033\u7684\u58f0\u97f3\u53c8\u4ece\u8239\u5934\u4f20\u8fc7\u6765\uff0c\u9053\uff1a\u201c\u59d1\u5a18\u8fd8\u5b88\u7740\u90a3\u81ed\u6c49\u5b50\u65e0\u5fae\u4e0d\u81f3\uff0c\u770b\u4ed6\u4f24\u5f97\u8fd9 \u6837\u91cd\uff0c\u8fd8\u6709\u4ec0\u4e48\u5e0c\u671b\u53ef\u6d3b\uff1f\u6254\u5728\u6d77\u91cc\u5582\u9c7c\u7b97\u4e86\uff01\u201d \u3000\u3000\u201c\u6211\u91d1\u9c81\u5384\u5728\u5929\u5170\u5bcc\u53ef\u654c\u56fd\uff0c\u59d1\u5a18\u6709\u4ec0\u4e48\u4e0d\u597d\u8ddf\u6211\u53bb\uff1f\u201d\u91d1\u9c81\u5384\u7adf\u60f3\u4ee5\u5229\u8bf1\u60d1\uff0c\u4e5f\u8bb8\u4ed6 \u4ee5\u4e3a\u4e2d\u539f\u7684\u5973\u5b50\u4f1a\u50cf\u4ed6\u672c\u56fd\u4eba\u4e00\u822c\u91cd\u8d22\u8f7b\u4e49\u5427\u3002 \u3000\u3000\u524d\u9762\u9003\u9038\u8005\u867d\u4ecd\u52a0\u52b2\u9f13\u6868\uff0c\u4f46\u4e5f\u5fcd\u4e0d\u4f4f\u9a82\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u597d\u72e0\u5fc3\u7684\u72d7\u86ee\u5b50\uff0c\u59d1\u5a18\u8a93\u5fc5\u62a5 \u6b64\u6740\u592b\u4e4b\u4ec7\uff01\u201d\u5fae\u786c\u7684\u6ce3\u8bed\uff0c\u5374\u7a81\u7136\u4f7f\u65c1\u89c2\u7684\u8f9b\u6377\u5982\u4e2d\u5de8\u68f0\uff0c\u4e00\u53ea\u624b\u7d27\u7d27\u6293\u4f4f\u7f06\u7ef3\u4e0d\u653e\uff0c\u53e3 \u4e2d\u5583\u5583\u8bf4\u9053\uff1a\u201c\u662f\u5979\uff1f\u7adf\u4f1a\u662f\u5979\uff1f\u2026\u2026\u201d \u3000\u3000\u84e6\u7136\uff0c\u51b2\u52a8\u7684\u5929\u6027\u4f7f\u4ed6\u5fd8\u6211\u8d77\u6765\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u4e5f\u50cf\u53d8\u6210\u4ed6\u81ea\u8eab\u7684\u4e8b\u60c5\u4e00\u822c\uff0c\u7a81\u7136\u4ed6\u4e00\u6d8c \u8eab\uff0c\u8f7b\u98d8\u98d8\u5730\u7ffb\u4e0a\u8239\u5c3e\u2014\u2014 \u3000\u3000\u6b64\u65f6\u96fe\u6c14\u5927\u6d53\uff0c\u8239\u5934\u4e0a\u7684\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u4e0e\u91d1\u9c81\u5384\u4ff1\u88ab\u96fe\u9690\u4f4f\uff0c\u8f9b\u6377\u5c4f\u4f4f\u6c14\uff0c\u653e\u5fc3\u5927\u80c6\u4e00 \u6b65\u6b65\u8e51\u8db3\u524d\u8fdb\uff0c\u679c\u7136\u884c\u4e0d\u5230\u4e94\u4e08\u524d\u9762\u5df2\u663e\u51fa\u56db\u6761\u4eba\u5f71\u2014\u2014 \u3000\u3000\u5f53\u4e2d\u7ad9\u7acb\u7684\u4e00\u4f4d\u8eab\u6750\u9ad8\u5927\uff0c\u5fc5\u662f\u4f2f\u7f57\u5404\u7b54\u65e0\u7591\uff0c\u65c1\u4e00\u5112\u751f\u5f53\u662f\u81ea\u547d\u4e0d\u51e1\u7684\u91d1\u9c81\u5384\u4e86\u3002 \u3000\u3000\u56db\u4eba\u5168\u795e\u8d2f\u6ce8\u5728\u524d\u9003\u8005\uff0c\u8c01\u4e5f\u672a\u6ce8\u610f\u5230\u540e\u9762\u63a9\u81f3\u7684\u8f9b\u6377\u3002 \u3000\u3000\u60f3\u662f\u524d\u9762\u64cd\u821f\u8005\u5bf9\u9644\u8fd1\u6d77\u5cb8\u76f8\u5f53\u719f\u6089\uff0c\u6b64\u65f6\u6868\u58f0\u7a81\u7136\u5411\u5de6\u4e00\u8f6c\uff0c\u8f9b\u6377\u8bb0\u5fc6\u4e2d\u6b64\u65b9\u5411\u6b63\u662f \u671d\u5411\u5cb8\u8fb9\u3002 \u3000\u3000\u7acb\u523b\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u8fde\u8235\u4e5f\u4e0d\u7528\uff0c\u516d\u8db3\u5f80\u5de6\u4e00\u538b\uff0c\u501f\u5927\u8239\u53ea\u7adf\u786c\u751f\u751f\u88ab\u4ed6\u4e09\u4eba\u8f6c\u6298\u8fc7\u53bb\uff0c \u4ecd\u7d27\u8ddf\u5728\u5c0f\u8247\u8eab\u540e\u3002 \u3000\u3000\u7a81\u7136\u4f2f\u7f57\u5404\u7b54\u7206\u51fa\u4e00\u8fde\u4e32\u78d4\u78d4\u5937\u8bed\uff1a\u201c\u5409\u91cc\u6469\u8bc3\u9632\u8fbe\uff0c\u52ff\u91ca\u54c8\u9609\u201d \u3000\u3000\u5e76\u4e14\u624b\u4e2d\u7adf\u4e3e\u8d77\u4e00\u7855\u5927\u94c1\u951a\u4f5c\u9065\u63b7\u72b6\uff0c\u65c1\u8fb9\u91d1\u9c81\u5384\u6025\u5f97\u8fde\u5fd9\u62e6\u4f4f\u2014\u2014 \u3000\u3000\u6b64\u65f6\u524d\u9762\u96fe\u6c14\u7a81\u5730\u5927\u76db\uff0c\u8f9b\u6377\u5f97\u5e73\u51e1\u4e0a\u4eba\u544a\u8bc9\u8fc7\u8fd9\u4e94\u662f\u8fdb\u4eba\u5ce1\u6e7e\u5185\u7684\u73b0\u8c61\uff0c\u56e0\u4e3a\u5ce1\u6e7e\u4e09 \u5c71\u73af\u9646\uff0c\u96fe\u6c14\u6781\u4e0d\u6613\u53d1\u6563\uff0c\u6545\u6108\u79ef\u6108\u6d53\u3002\u8fd9\u65f6\u5df2\u5feb\u81f3\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u7684\u5730\u6b65\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u81ea\u89c9\u66f4\u903c\u8fd1\u4e86\u4e00\u4e9b\uff0c\u8ddd\u79bb\u6052\u6cb3\u4e09\u4f5b\u7b49\u5df2\u4e0d\u8db3\u4e09\u4e08\uff0c\u5982\u975e\u4ed6\u56db\u4eba\u4ff1\u5168\u795e\u8d2f\u6ce8\u5728\u641c\u5bfb \u9003\u8d70\u4e4b\u5c0f\u8247\uff0c\u8fd8\u4f1a\u4e0d\u53d1\u73b0\u8f9b\u6377\u4e48\uff1f \u3000\u3000\u84e6\u5730\u91d1\u9c81\u5384\u53c8\u5f00\u53e3\u559d\u9053\uff1a\u201c\u59d1\u5a18\u901f\u901f\u505c\u6b62\uff0c\u5426\u5219\u6211\u5e08\u4f2f\u5373\u8981\u4ee5\u94c1\u951a\u6295\u63b7\u8fc7\u6765\u4e86\uff01\u201d\u6562\u60c5 \u4ed6\u4e5f\u53d1\u89c9\u5f62\u52bf\u7a81\u53d8\uff0c\u6d53\u96fe\u4f7f\u5f97\u56db\u4eba\u5feb\u5931\u53bb\u9003\u8239\u7684\u8e2a\u5f71\u3002 \u3000\u3000\u867d\u7136\u4e0d\u4e00\u4f1a\u513f\u524d\u9003\u8005\u8e2a\u8ff9\u5df2\u6e3a\uff0c\u4f46\u5faa\u6c34\u58f0\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u4ecd\u4ee5\u5176\u8d85\u51e1\u7684\u529f\u529b\uff0c\u9f13\u98ce\u800c\u884c\u7d27 \u8feb\u5728\u5c0f\u8247\u540e\u9762\u3002 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u6027\u6700\u6025\u71e5\uff0c\u6b64\u65f6\u65e9\u5df2\u5c06\u951a\u9ad8\u4e3e\u5728\u624b\uff0c\u53ea\u8981\u4e00\u65e0\u628a\u63e1\u8ffd\u5f97\u4e0a\u524d\u8239\uff0c\u4ed6\u5373\u8981\u51ed\u6868\u58f0 \u5c06\u5bf9\u65b9\u51fb\u6c89\uff0c\u4ee5\u514d\u6052\u6cb3\u4e09\u4f5b\u8ffd\u51cc\u5f31\u5973\u7684\u8baf\u606f\uff0c\u4f20\u4eba\u6c5f\u6e56\u53d7\u4eba\u803b\u7b11\u3002 \u3000\u3000\u8c01\u77e5\u5c31\u5728\u8fd9\u7d27\u5f20\u7684\u4e00\u523b\uff0c\u7a81\u7136\u5c0f\u8247\u6868\u58f0\u6d88\u5931\u4e86\uff0c\u7acb\u523b\u56db\u5468\u9664\u4e86\u6d77\u6d9b\u6c79\u6d8c\u4e4b\u5916\uff0c\u4e00\u4e1d\u58f0\u606f \u4e5f\u65e0\uff0c\u91d1\u4f2f\u80dc\u4e0e\u76d8\u706f\u5b5a\u5c14\u4e5f\u8fde\u5fd9\u53cc\u624b\u4e00\u6251\u4e00\u62c2\uff0c\u51cf\u53bb\u524d\u51b2\u901f\u5ea6\u7f13\u7f13\u505c\u4e0b\u6765\u3002 \u3000\u3000\u91d1\u9c81\u5384\u6b63\u7ad9\u5728\u8239\u5f26\u8fb9\uff0c\u84e6\u5730\u4ed6\u5927\u53eb\u8d77\u6765\uff0c\u9053\uff1a\u201c\u5f53\u5fc3\uff01\u53f3\u5f26\u6697\u7901\uff01\u201d\u5f53\u7136\u4ed6\u662f\u4ee5\u68b5\u8bed\u8bf4 \u7684\u3002 \u3000\u3000\u867d\u7136\u5927\u8239\u901f\u5ea6\u5df2\u662f\u5927\u51cf\uff0c\u4f46\u524d\u8fdb\u7684\u52a8\u529b\uff0c\u4ecd\u8db3\u4ee5\u88ab\u6697\u7901\u5c06\u8239\u649e\u51fb\u5f97\u56db\u5206\u4e94\u88c2 \u3000\u3000\u201c\u8f70\u9686\uff01\u201d \u3000\u3000\u5728\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u8fd8\u672a\u80fd\u53ca\u5f97\u505c\u8239\u7684\u5f53\u513f\uff0c\u6574\u4e2a\u821f\u8eaf\u5df2\u7a33\u7a33\u67b6\u4e0a\u6697\u7901\uff0c\u5c31\u662f\u201c\u6052\u6cb3\u4e09\u4f5b\u201d \u518d\u6709\u591a\u5927\u529f\u529b\u4e5f\u522b\u60f3\u5c06\u5b83\u79fb\u52a8\u5206\u6beb\u3002 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u6b63\u60f3\u7834\u53e3\u5927\u9a82\uff0c\u91d1\u4f2f\u80dc\u5937\u5374\u4e00\u6325\u624b\u5c06\u4ed6\u5236\u6b62\uff0c\u9762\u5bb9\u95ea\u8fc7\u4e00\u4e1d\u72f0\u72de\u7b11\u5bb9\u2014\u2014 \u3000\u3000\u201c\u59d1\u5a18\u597d\u751f\u806a\u6167\uff0c\u6211\u91d1\u4f2f\u80dc\u5937\u6df1\u611f\u94a6\u4f69\uff01\u201d\u91d1\u4f2f\u80dc\u5937\u64cd\u7740\u751f\u786c\u6c49\u8bed\u8bf4\u5b8c\uff0c\u7acb\u523b\u5411\u4f2f\u7f57\u5404 \u7b54\u6253\u4e86\u4e00\u4e2a\u624b\u52bf\uff0c\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u5fc3\u7075\u65e9\u901a\uff0c\u4f2f\u7f57\u5404\u7b54\u5f53\u7136\u660e\u767d\u4ed6\u7684\u601d\u60f3\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u6027\u673a\u8b66\uff0c\u65e9\u5df2\u6d1e\u6089\u91d1\u4f2f\u80dc\u5937\u7684\u9b3c\u8ba1\uff0c\u4e00\u8eac\u8eab\u5f62\u5982\u72f8\u732b\u822c\u53c8\u8de8\u524d\u4e09\u6b65\uff0c\u79bb\u91d1\u4f2f\u80dc\u5937 \u7b49\u5df2\u5f88\u8fd1\u4e86\u2014\u2014 \u3000\u3000\u91d1\u9c81\u5384\u7b49\u6b63\u6ce8\u610f\u7740\u524d\u65b9\uff0c\u4f55\u51b5\u5927\u96fe\u662f\u5982\u6b64\u6d53\uff0c\u51e0\u4e4e\u4f38\u624b\u4e0d\u89c1\u4e94\u6307\u7684\u5730\u6b65\uff0c\u600e\u4f1a\u79d1\u5230\u654c\u4eba \u4ece\u540e\u65b9\u63a9\u6765\uff0c\u4f55\u51b5\u53c8\u662f\u673a\u667a\u7edd\u4f26\u7684\u8f9b\u6377\u3002 \u3000\u3000\u679c\u7136\u4e0d\u4e00\u4f1a\u513f\uff0c\u79bb\u8239\u7ea6\u5eff\u4e08\u5904\uff0c\u4e00\u4e2a\u51b7\u51b7\u7684\u5973\u5b50\u58f0\u97f3\u8bf4\u9053\uff1a\u201c\u597d\u4e2a\u86ee\u72d7\uff0c\u73b0\u5728\u53ef\u5c1d\u5230\u59d1 \u5a18\u624b\u6bb5\u4e86\uff0c\u7b49\u4e0b\u53eb\u4f60\u4eec\u4e00\u4e2a\u4e2a\u53bb\u5582\u9c7c\u867e\u3002\u201d \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u54c8\u54c8\u4e00\u7b11\uff0c\u53f3\u624b\u4e00\u6325\u5904\uff0c\u503c\u7f57\u5404\u7b54\u94c1\u951a\u5df2\u63b7\u51fa\u624b\u2014\u2014 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u529f\u529b\u51e0\u4e4e\u4e0e\u5e73\u51e1\u4e0a\u4eba\u76f8\u4eff\u5937\uff0c\u8fd9\u4e00\u5c3d\u52bf\u800c\u4e3a\u52b2\u529b\u6709\u82e5\u5954\u96f7\uff0c\u53ea\u89c1\u90a3\u94c1\u951a\u631f\u7740 \u201c\u4e1d\u4e1d\u201d\u7834\u7a7a\u4e4b\u58f0\uff0c\u76f4\u5411\u53d1\u8bdd\u5904\u51fb\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u65e9\u6599\u5230\u5982\u6b64\uff0c\u84e6\u5730\u53d1\u96be\uff0c\u4e00\u4e2a\u8eab\u5b50\u98de\u5feb\u671d\u94c1\u951a\u53bb\u5411\u6251\u51fa\uff0c\u62bd\u7a7a\u7adf\u5411\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u7b49 \u56db\u4eba\u5404\u5288\u51fa\u4e00\u638c\u2014\u2014 \u3000\u3000\u91d1\u4f2f\u80dc\u4f5b\u7b49\u7a81\u89c9\u80cc\u540e\u52b2\u98ce\u6697\u88ad\uff0c\u90fd\u4e0d\u81ea\u89c9\u8f6c\u8fc7\u8eab\u6765\uff0c\u53cc\u624b\u62a4\u4f4f\u80f8\u524d\uff0c\u6253\u5b9a\u5148\u4fdd\u4f4f\u8eab\u8eaf\u518d \u8bf4\u3002 \u3000\u3000\u8f9b\u6377\u6b63\u8981\u4ed6\u4eec\u5982\u6b64\uff0c\u4e58\u56db\u4eba\u4e00\u7a92\u95f4\uff0c\u4e00\u6e9c\u8eab\u5f62\u65e9\u8d76\u51fa\u8239\u5934\uff0c\u7d27\u7d27\u8ffd\u5728\u94c1\u951a\u540e\u9762\u2014\u2014 \u3000\u3000\u56db\u4eba\u53d1\u89c9\u53d7\u9a97\u65f6\u5df2\u62e6\u51fb\u4e0d\u53ca\uff0c\u5176\u4e2d\u91d1\u9c81\u5384\u5bf9\u8f9b\u6377\u5370\u50cf\u6700\u6df1\uff0c\u867d\u77ed\u77ed\u4e00\u77a5\uff0c\u5df2\u770b\u6e05\u662f\u8f9b \u6377\uff0c\u4e0d\u89c9\u8131\u53e3\u547c\u9053\uff1a\u201c\u662f\u4ed6\uff1f\u8fd9\u5c0f\u5b50\uff01\u201d\u8fde\u5fd9\u5c06\u6b64\u4eba\u662f\u8f9b\u6377\u544a\u8bc9\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u7b49\u3002 \u3000\u3000\u8fd9\u7a81\u53d8\u53ea\u4e0d\u8fc7\u4e00\u5239\u90a3\u65f6\u95f4\uff0c\u4e0d\u8bf4\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u5728\u540e\u5927\u58f0\u5492\u9a82\uff0c\u800c\u8f9b\u6377\u98de\u51fa\u8239\u5934\u4e94\u4e08\u5df1\u8d76 \u4e0a\u94c1\u951a\u3002 \u3000\u3000\u8f9b\u6377\u5728\u5148\u524d\u5df2\u8bb0\u6e05\u53d1\u58f0\u5904\uff0c\u6b64\u523b\u771f\u6c14\u4e00\u6362\uff0c\u53cc\u8db3\u704c\u6ee1\u771f\u529b\u72e0\u72e0\u5f80\u94c1\u951a\u4e0a\u4e00\u987f\uff0c\u81ea\u5df1\u8eab\u4f53 \u88ab\u53cd\u7528\u529b\u6fc0\u5f97\u9ad8\u9ad8\u817e\u8d77\uff0c\u4e0d\u8fc7\u94c1\u951a\u5374\u4e5f\u56e0\u8f9b\u6377\u8fd9\u4e00\u811a\uff0c\u7a0d\u5fae\u5411\u4e0b\u504f\u53bb\u2014\u2014 \u3000\u3000\u201c\u6251\u901a\uff01\u201d \u3000\u3000\u94c1\u951a\u843d\u6c34\u58f0\uff0c\u7d27\u63a5\u7740\u4e00\u4e0b\u5973\u5b50\u60ca\u547c\u58f0\uff0c\u8f9b\u6377\u5728\u7a7a\u4e2d\u4e00\u8fde\u6362\u6570\u4e2a\u8eab\u5f62\uff0c\u51cf\u53bb\u524d\u98de\u901f\u5ea6\uff0c\u5f84 \u5f80\u53d1\u58f0\u5904\u843d\u4e0b\u3002 \u3000\u3000\u6b64\u65f6\u5927\u96fe\u5f25\u6f2b\uff0c\u8f9b\u6377\u53cc\u76ee\u7d27\u7d27\u6ce8\u89c6\u7740\u8db3\u4e0b\u4ecd\u662f\u770b\u4e0d\u89c1\u843d\u8db3\u70b9\u2014\u2014 \u3000\u3000\u8239\u4e0a\u4eba\u521a\u624d\u5927\u6982\u88ab\u94c1\u951a\u58f0\u52bf\u9a87\u5f97\u5fc3\u60ca\u80c6\u5bd2\uff0c\u6b64\u65f6\u53c8\u95fb\u5934\u9876\u52b2\u98ce\u547c\u547c\uff0c\u4e0d\u7981\u5c06\u624b\u4e2d\u6728\u6868\u4e00 \u6273\uff0c\u6574\u4e2a\u8239\u8eab\u786c\u5f80\u5de6\u79fb\u5f00\u4e94\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5c3d\u91cf\u5c06\u53cc\u8db3\u7f29\u8d77\uff0c\u4f46\u76f4\u5f85\u4ed6\u79bb\u6c34\u9762\u6dcc\u4e0d\u8db3\u4e24\u5c3a\uff0c\u624d\u53d1\u89c9\u81ea\u5df1\u811a\u4e0b\u7adf\u662f\u767d\u832b\u832b\u4e00\u7247\u6ce2 \u6d9b\uff0c\u4f55\u6765\u4e2a\u821f\uff1f \u3000\u3000\u8f9b\u6377\u5927\u60ca\u4e4b\u4e0b\uff0c\u53cc\u8896\u594b\u529b\u5411\u4e0b\u4e00\u538b\uff0c\u6574\u4e2a\u8eab\u5b50\u85c9\u7740\u6c34\u9762\u53cd\u9707\u4e4b\u529b\uff0c\u51ed\u7a7a\u53c8\u8dc3\u8d77\u4e09\u4e08\uff0c\u8fd9 \u4e0b\u4ed6\u518d\u4e5f\u4e0d\u6562\u5927\u610f\uff0c\u8fde\u5fd9\u5f00\u58f0\u547c\u9053\uff1a\u201c\u78a7\u59b9\uff1f\u662f\u4f60\u5417\uff1f\u201d \u3000\u3000\u7acb\u523b\u6709\u4e00\u6839\u6728\u6868\u4f38\u8fc7\u6765\uff0c\u8f9b\u6377\u7a33\u7a33\u843d\u5728\u6868\u4e0a\uff0c\u5fc3\u6697\u60ca\u8fd9\u6d53\u96fe\u5982\u6b64\u4e4b\u5927\uff0c\u5c45\u7136\u8eab\u9694\u817f\u5c3a\u4ecd \u4e0d\u80fd\u53d1\u73b0\u8eab\u65c1\u4e09\u5c3a\u4e4b\u5916\u7684\u5c0f\u8239\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5f97\u5230\u6728\u6868\u7684\u52a9\u529b\uff0c\u4e00\u6643\u8eab\u843d\u4eba\u8239\u5185\uff0c\u8499\u8499\u96fe\u6c14\u4e2d\uff0c\u6b63\u6709\u4e00\u53cc\u6e05\u6f88\u7684\u5927\u773c\uff0c\u7d27\u5f20\u5730\u6ce8 \u89c6\u7740\u4ed6\uff0c\u76ee\u5149\u4e2d\u54c0\u6028\u7684\u795e\u8272\u50cf\u5305\u542b\u7740\u65e0\u6bd4\u8f9b\u9178\u4e0e\u75db\u82e6\u3002 \u3000\u3000\u8f9b\u6377\u7acb\u5728\u8239\u5934\uff0c\u4f3c\u4e4e\u5728\u672a\u5f97\u5141\u8bb8\u524d\u4e0d\u6562\u5192\u5165\u5c0f\u8247\uff0c\u6b64\u65f6\u4ed6\u5fc3\u4e2d\u5347\u8d77\u83ab\u540d\u7684\u6050\u60e7\uff0c\u65e2\u6015\u5bf9 \u65b9\u4e0d\u662f\u5fc3\u76ee\u4e2d\u6240\u60f3\u50cf\u7684\u65b9\u5c11\u78a7\uff0c\u800c\u53c8\u5bb3\u6015\u662f\uff01 \u3000\u3000\u201c\u78a7\u59b9\uff01\u662f\u4f60\u5417\uff1f\u6211\u53ef\u4ee5\u4e0b\u6765\u5417\uff1f\u201d\u8f9b\u6377\u5728\u6b64\u5927\u96fe\u4e2d\u53ea\u89c9\u6b64\u5973\u90ce\u8f6e\u5eca\u5df2\u50cf\u6781\u65b9\u5c11\u78a7\uff0c\u4f46 \u5f25\u6f2b\u6c14\u906e\u63a9\u4e0b\u5979\u5374\u662f\u5982\u6b64\u51b7\uff0c\u51b7\u5f97\u8f9b\u6377\u4e0d\u6562\u542f\u53e3\u2014\u2014 \u3000\u3000\u90a3\u5973\u5b50\u4e45\u4e45\u4e0d\u7b54\uff0c\u8f9b\u6377\u4e5f\u4e45\u4e45\u7acb\u5728\u8239\u5934\uff0c\u76f8\u6301\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u90a3\u5973\u5b50\u624d\u5f00\u53e3\u5e73\u9759\u8bf4\u9053\uff1a \u201c\u4e0d\u9519\uff01\u6377\u2026\u2026\u8f9b\u5927\u54e5\uff0c\u662f\u6211\uff01\u60f3\u4e0d\u5230\u4f1a\u5728\u6b64\u78b0\u89c1\u4f60\uff01\u201d\u4f46\u8f9b\u6377\u542c\u5f97\u51fa\u5979\u8bed\u6c14\u4e2d\u5305\u542b\u7740\u7edd\u5927 \u7684\u75db\u82e6\u4e0e\u6fc0\u52a8\u3002 \u3000\u3000\u201c\u5401\uff01\u201d \u3000\u3000\u8f9b\u6377\u957f\u957f\u7f13\u4e00\u53e3\u6c14\uff0c\u81ea\u5632\u5730\u7b11\u7b11\u7136\u540e\u6b65\u4e0b\u8239\u8231\uff0c\u821f\u4e2d\u6a2a\u677f\u4e0a\u6b63\u5750\u7740\u4ee4\u4ed6\u96be\u5fd8\u7684\u65b9\u5c11\u78a7\u3002 \u201c\u4f46\u5979\u662f\u8fd9\u4e48\u51b7\u51b0\u51b0\uff01\u201d\u8f9b\u6377\u5fc3\u60f3\uff0c\u63a5\u7740\u6253\u7b97\u7f13\u548c\u4e00\u4e0b\u5468\u56f4\u51b0\u51b7\u7684\u6c14\u6c1b\uff0c\u4f46\u603b\u60f3\u4e0d\u51fa\u4ec0\u4e48\u9002 \u5f53\u7684\u8bdd\uff0c\u53ea\u597d\u82e6\u7b11\u9053\uff1a\u201c\u78a7\u59b9\uff0c\u771f\u9ad8\u5174\u80fd\u89c1\u5230\u4f60\uff0c\u4f60\u8fd9\u4e9b\u65e5\u5b50\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u8bf4\u5230\u6b64\uff0c\u7a81\u7136\u8fdc\u5904\u4f20\u6765\u6570\u58f0\u60ca\u547c\uff0c\u7d27\u63a5\u7740\u542c\u5f97\u91d1\u9c81\u5384\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff01\u5feb\uff01\u5feb\u8df3\u4e0a\u8fd9 \u7901\u77f3\u2014\u2014\u201d \u3000\u3000\u53c8\u4e00\u9635\u68b5\u8bed\u7684\u5492\u9a82\u58f0\uff0c\u8fd8\u6709\u4f2f\u7f57\u5404\u7b54\u6124\u6012\u7684\u543c\u53eb\u58f0\u2014\u2014 \u3000\u3000\u65b9\u5c11\u78a7\u81f3\u6b64\u624d\u9732\u51fa\u4e00\u4e1d\u51c4\u51c9\u7684\u7b11\u5bb9\u3002 \u3000\u3000\u8f9b\u6377\u6293\u4f4f\u8fd9\u673a\u4f1a\uff0c\u7acb\u523b\u8d5e\u9053\uff1a\u201c\u78a7\u59b9\u771f\u806a\u660e\uff0c\u8fd9\u8ba1\u7b56\u6211\u771f\u4f69\u670d\u5f97\u5f88\u3002\u201d \u3000\u3000\u65b9\u5c11\u78a7\u6de1\u6de1\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u8f9b\u5927\u4fa0\u8fc7\u5956\u4e86\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u542c\u51fa\u5979\u8bed\u4e2d\u9690\u9690\u542b\u6709\u6697\u523a\uff0c\u4ed6\u5bf9\u65b9\u5c11\u78a7\u9664\u4e86\u4e07\u5206\u62b1\u6b49\u5916\uff0c\u53ea\u6709\u65e0\u6bd4\u7684\u601c\u60dc\u4e86\uff0c\u66f4\u4f55 \u51b5\u4ed6\u5bf9\u65b9\u5c11\u78a7\u5e76\u4e0d\u662f\u5b8c\u5168\u5fd8\u60c5\u3002 \u3000\u3000\u201c\u78a7\u59b9\uff01\u6211\u2014\u2014\u6211\u5bf9\u4e0d\u8d77\u4f60\uff0c\u4ee5\u524d\u7684\u4e8b\u60c5\u522b\u63d0\u4e86\uff0c\u78a7\u59b9\u8fd1\u6765\u751f\u6d3b\u597d\u5417\uff1f\u201d \u3000\u3000\u65b9\u5c11\u671b\u7a81\u7136\u63a9\u9762\u75db\u54ed\u8d77\u6765\uff0c\u84e6\u5730\u5979\u53d8\u6868\u4e00\u5212\u5411\u53f3\u6a2a\u8fc7\u516d\u3001\u4e03\u4e08\uff0c\u7a81\u7136\u4ece\u8eab\u540e\u62b1\u8d77\u4e00\u4eba\uff0c \u4e00\u70b9\u8239\u8eab\u5373\u5411\u5916\u8dc3\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u5927\u60ca\uff0c\u5c1a\u4ee5\u4e3a\u5979\u8981\u5bfb\u77ed\u89c1\uff0c\u7acb\u523b\u4e5f\u8ddf\u8e2a\u8dc3\u8d77\uff0c\u4f46\u5f53\u4ed6\u843d\u4e0b\u65f6\u5374\u53d1\u73b0\u811a\u4e0b\u7adf\u662f\u5e72\u6c99\u5b9e \u5730\u2014\u2014 \u3000\u3000\u6b64\u65f6\u65b9\u5c11\u78a7\u65e9\u5df2\u9690\u8eab\u6d53\u96fe\uff0c\u8f9b\u6377\u5fae\u4e00\u505c\u987f\uff0c\u7acb\u523b\u8fa8\u6e05\u65b9\u5411\u5faa\u58f0\u8ffd\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u529f\u529b\u9ad8\u51fa\u65b9\u5c11\u78a7\u8bb8\u591a\uff0c\u4f55\u51b5\u5979\u624b\u4e2d\u5c1a\u62b1\u7740\u4e00\u4eba\uff0c\u6240\u4ee5\u8f9b\u6377\u4e0d\u4e45\u5c31\u8ffd\u53ca\u5979\uff0c\u53ea\u89c1\u65b9\u5c11 \u671b\u5c06\u90a3\u4eba\u62b1\u5f97\u7d27\u7d27\u7684\uff0c\u4e00\u8def\u557c\u54ed\u8dd1\u7740\u2014\u2014 \u3000\u3000\u8f9b\u6377\u53ea\u597d\u7262\u8ddf\u5728\u5979\u540e\u9762\uff0c\u51fa\u58f0\u5b89\u6170\u9053\uff1a\u201c\u78a7\u59b9\uff01\u96be\u9053\u4e0d\u80fd\u7ed9\u6211\u89e3\u91ca\u7684\u673a\u4f1a\u5417\uff1f\u201d \u3000\u3000\u65b9\u5c11\u78a7\u5934\u4e5f\u4e0d\u56de\uff0c\u4ecd\u7ee7\u7eed\u5954\u8dd1\uff0c\u5c31\u8fd9\u6837\u5728\u5d07\u5c71\u5cfb\u5cad\u4e2d\uff0c\u56de\u8f6c\u7ea6\u6709\u4e24\u4e09\u4e2a\u65f6\u8fb0\uff0c\u7adf\u5954\u81f3\u4e00 \u6d1e\u53e3\u2014\u2014 \u3000\u3000\u65b9\u5c11\u78a7\u6beb\u4e0d\u505c\u6b65\u76f4\u5954\u8fdb\u53bb\uff0c\u800c\u8f9b\u6377\u4e5f\u6beb\u4e0d\u72b9\u8c6b\u7acb\u523b\u8ddf\u8fdb\u2014\u2014 \u3000\u3000\u4e00\u5954\u8fdb\u6d1e\u7adf\u662f\u4e00\u4e2a\u5bbd\u655e\u7684\u5927\u5ca9\u7a74\uff0c\u5185\u4e2d\u73b0\u5206\u8bb8\u591a\u5c0f\u66f2\u9053\u901a\u5927\u66f4\u6df1\u5c42\uff0c\u65b9\u5c11\u671b\u4f3c\u4e4e\u5bf9\u6b64\u5730 \u5730\u5f62\u751a\u662f\u719f\u6089\uff0c\u76f4\u62e3\u5f53\u4e2d\u4e00\u6761\u5411\u5185\u6df1\u5165\u2014\u2014 \u3000\u3000\u8f6c\u4e86\u597d\u51e0\u4e2a\u8f6c\uff0c\u524d\u9762\u7adf\u73b0\u51fa\u4e00\u77f3\u5ba4\uff0c\u5185\u4e2d\u77f3\u5e8a\u3001\u77f3\u51f3\u3001\u77f3\u684c\u3001\u77f3\u6905\u4e00\u5e94\u4ff1\u5168\uff0c\u65b9\u5c11\u78a7\u5c06 \u624b\u4e2d\u4eba\u8f7b\u653e\u5728\u77f3\u5e8a\u4e0a\uff0c\u84e6\u5730\u8f6c\u8fc7\u8eab\u6765\u3002 \u3000\u3000\u8f9b\u6377\u505c\u5728\u77f3\u5c4b\u95e8\u53e3\uff0c\u7591\u60d1\u5730\u770b\u7740\u5185\u4e2d\u4e00\u5207\u2014\u2014 \u3000\u3000\u201c\u8f9b\u5927\u4fa0\u4e00\u8def\u8ddf\u6765\u4f5c\u751a\uff1f\u201d\u65b9\u5c11\u78a7\u5fae\u54fd\u5730\u9053\u3002 \u3000\u3000\u8f9b\u6377\u8138\u4e0a\u75db\u82e6\u5730\u62bd\u6410\u4e86\u51e0\u4e0b\uff0c\u53f9\u58f0\u9053\uff1a\u201c\u78a7\u59b9\uff01\u522b\u8fd9\u6837\u5bf9\u6211\uff0c\u7eb5\u4f7f\u6211\u6709\u5bf9\u4e0d\u8d77\u4f60\u7684\u5730 \u65b9\uff0c\u76f8\u9694\u8fd9\u6837\u4e45\uff0c\u4f60\u4e5f\u5e94\u8c05\u89e3\u6211\u554a\uff01\u201d \u3000\u3000\u65b9\u5c11\u78a7\u51b7\u54fc\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u2014\u2014\u4f60\u6ca1\u6709\u4ec0\u4e48\u5bf9\u4e0d\u4f4f\u6211\u7684\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8981\u6211\u8c05\u89e3\u7684\u3002\u201d \u3000\u3000\u77ed\u77ed\u7684\u6570\u8bed\u5374\u50cf\u53ea\u53ea\u5229\u5251\u822c\u7a7f\u900f\u8f9b\u6377\u7684\u5fc3\uff0c\u5982\u679c\u4e0d\u662f\u4ed6\u5bf9\u65b9\u5c11\u78a7\u62b1\u6709\u6127\u6068\uff0c\u4ee5\u4ed6\u6027\u683c\u65e9 \u8981\u987f\u8db3\u8d70\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u770b\u770b\u65b9\u5c11\u5784\u8eab\u540e\u9759\u8eba\u5728\u77f3\u5e8a\u7684\u90a3\u4eba\uff0c\u53ea\u89c1\u4ed6\u6ee1\u5934\u4e71\u53d1\u906e\u53bb\u5927\u534a\u8138\uff0c\u602a\u5f02\u7684\u88c5\u675f\u4f7f\u4eba \u770b\u6765\u89c9\u5f97\u4e0d\u4f26\u4e0d\u7c7b\uff0c\u4e3a\u4e86\u8981\u627e\u51fa\u7ee7\u7eed\u5728\u6b64\u5730\u7684\u7406\u7531\uff0c\u4e8e\u662f\u8f9b\u6377\u8bf4\u9053\uff1a \u3000\u3000\u201c\u4ed6\u662f\u8c01\uff1f\u770b\u6765\u53d7\u4f24\u5f88\u91cd\uff0c\u8ba9\u6211\u5e2e\u4f60\u5c06\u4ed6\u533b\u597d\u5427\uff01\u201d \u3000\u3000\u65b9\u5c11\u5784\u5947\u602a\u5730\u4e00\u7b11\uff1f\u8138\u4e0a\u95ea\u8fc7\u4e00\u4e1d\u6781\u4e0d\u81ea\u7136\u795e\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u6562\u6709\u52b3\u8f9b\u5927\u4fa0\uff0c\u6b64\u4eba\u662f\u8c01 \u5927\u4fa0\u4e5f\u65e0\u987b\u77e5\u6653\uff0c\u5c31\u8bf7\u60a8\u8d76\u5feb\u79bb\u5f00\u8fd9\u513f\uff01\u201d \u3000\u3000\u8fd9\u5de6\u4e00\u58f0\u5927\u4fa0\uff0c\u53f3\u4e00\u58f0\u5927\u4fa0\uff0c\u53eb\u5f97\u8f9b\u6377\u60ed\u6127\u800c\u65e0\u5730\u81ea\u5bb9\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4e0d\u80fd\u518d\u8a00\u8bed\uff0c\u6676\u83b9\u7684\u6cea\u73e0\u5728\u4ed6\u773c\u7736\u4e2d\u6eda\u52a8\uff0c\u4ed6\u7ec8\u4e8e\u6ca1\u6709\u8ba9\u5b83\u6ed1\u8dcc\u4e0b\u6765\uff0c\u4f46\u90a3\u79cd\u795e \u8272\uff0c\u4e0d\u4ec5\u5305\u542b\u54c0\u4f24\uff0c\u8fd8\u6709\u4e00\u4e1d\u5fae\u5fae\u7684\u6124\u6012\uff0c\u867d\u7136\u8f9b\u6377\u786e\u66fe\u6709\u8d1f\u8fc7\u65b9\u5c11\u78a7\u7684\u5730\u65b9\uff0c\u4f46\u7ecf\u8fc7\u8fd9\u4e48 \u591a\u6298\u78e8\uff0c\u5979\u4e5f\u5e94\u8c05\u89e3\u4ed6\uff0c\u7ed9\u4ed6\u7a0d\u5fae\u6170\u85c9\u624d\u5bf9\u3002\u8f9b\u6377\u60f3\u7740\uff0c\u5634\u5507\u53d1\u7740\u98a4\uff0c\u4e00\u76f4\u6296\u52a8\u8001\u534a\u5929\u624d\u8131 \u53e3\u800c\u51fa\uff0c\u9053\uff1a\u201c\u78a7\u59b9\uff01\u4f60\u2026\u2026\u4f60\u2026\u2026\u5509\uff01\u201d\u8bf4\u65f6\u4e24\u624b\u5fae\u5f20\u7740\uff0c\u773c\u4e2d\u5145\u6ee1\u5e0c\u5180\u88ab\u5e7b\u706d\u7684\u76ee\u5149\uff0c \u8138\u4e0a\u4e00\u7247\u5446\u75f4\u4e0e\u60b2\u601c\u2014\u2014 \u3000\u3000\u8fd9\u4e00\u58f0\u201c\u78a7\u59b9\u201d\u50cf\u4e00\u53ea\u5de8\u9524\uff0c\u91cd\u91cd\u51fb\u5728\u65b9\u5c11\u78a7\u5fc3\u6249\uff0c\u88ab\u7406\u667a\u538b\u4f4f\u7684\u611f\u60c5\uff0c\u4e00\u53d1\u518d\u4e5f\u4e0d\u53ef \u6536\u62fe\uff0c\u53ea\u662f\u5979\u4e5f\u6cea\u5982\u6cc9\u6d8c\uff0c\u4f38\u624b\u63a9\u9762\u6ce3\u9053\uff1a\u201c\u6377\u54e5\uff01\u6377\u54e5\uff01\u4e3a\u4ec0\u4e48\u53c8\u8ba9\u6211\u78b0\u89c1\u4f60\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u50f5\u786c\u7684\u8138\u4e0a\u7efd\u5f00\u4e86\u7b11\u5bb9\uff0c\u4e00\u4e1d\u5bbd\u6170\u7684\u6b22\u6b23\u7194\u5316\u4e86\u4ed6\u90c1\u79ef\u7684\u6101\u7ed3\uff0c\u81f3\u5c11\u65b9\u5c11\u78a7\u8fd8\u6ca1\u5fd8 \u8bb0\u4ed6\u554a\u2026\u2026 \u3000\u3000\u201c\u78a7\u59b9\uff01\u6211\u5b9e\u5728\u5bf9\u4e0d\u8d77\u4f60\uff0c\u5509\u5f53\u5e74\u7684\u4e8b\u4e0d\u8c08\u4e5f\u7f62\uff01\u4f60\u2026\u2026\u4f60\u5df1\u5ac1\u4eba\u4e86\u5417\uff1f\u201d\u8f9b\u6377\u8bf4\u65f6\u6307 \u4e86\u6307\u77f3\u5e8a\u4e0a\u53d7\u4f24\u7684\u90a3\u4eba\u3002 \u3000\u3000\u65b9\u5c11\u78a7\u70b9\u70b9\u5934\uff0c\u9762\u4e0a\u6d6e\u8d77\u6de1\u6de1\u4e00\u4e1d\u82e6\u7b11\u3002 \u3000\u3000\u201c\u662f\u8c01\uff1f\u201d\u8f9b\u6377\u5947\u602a\u5730\u95ee\u9053\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u660e\u767d\u2026\u2026 \u3000\u3000\u65b9\u5c11\u78a7\u5e7d\u6028\u7684\u4e00\u77a5\u8f9b\u6377\uff0c\u6781\u4e0d\u987e\u51fa\u53e3\u5730\u8bf4\u9053\uff1a\u201c\u91d1\u6b39\uff01\u201d \u3000\u3000\u8f9b\u6377\u60ca\u5f97\u7a81\u7136\u7d27\u7d27\u6293\u4f4f\u65b9\u5c11\u5784\u53cc\u80a9\uff0c\u6000\u7591\u5730\u518d\u95ee\u5979\u9053\uff1a\u201c\u662f\u91d1\u6b39\uff1f\u2018\u5929\u9b54\u2019\u91d1\u6b39\uff1f\u201d \u3000\u3000\u8fd8\u6ca1\u5f85\u65b9\u5c11\u78a7\u70b9\u5934\u7b54\u662f\uff0c\u8f9b\u6377\u5df2\u4e00\u6643\u8eab\u62a2\u81f3\u767e\u5e8a\u524d\u2014\u2014 \u3000\u3000\u65b9\u5c11\u5784\u4ee5\u4e3a\u8f9b\u6377\u5c1a\u672b\u5fd8\u8bb0\u524d\u4ec7\uff0c\u6025\u5f97\u5927\u53eb\u9053\uff1a\u201c\u6377\u54e5\uff01\u4f60\u4e0d\u80fd\u2026\u2026\u6211\u4e0d\u8bb8\u4f60\u4f24\u4ed6\uff01\u201d\u8bf4 \u65f6\u4e00\u628a\u62c9\u4f4f\u8f9b\u6377\u5de6\u624b\u3002 \u3000\u3000\u8f9b\u6377\u53f3\u624b\u8f7b\u8f7b\u4e00\u62c2\uff0c\u626b\u5f00\u8986\u5728\u90a3\u4eba\u9762\u4e0a\u7684\u4e71\u53d1\uff0c\u9a87\u7136\u4e00\u4e2a\u96be\u4ee5\u5fd8\u6000\u7684\u9762\u5bb9\u5448\u73b0\u5728\u4ed6\u773c \u524d\u2014\u2014 \u3000\u3000\u8fd9\u4eba\u4e0d\u662f\u91d1\u6b39\u662f\u8c01\uff1f\u8f9b\u6377\u5fc3\u4e2d\u6697\u601d\uff0c\u6df1\u800c\u957f\u7684\u4e24\u9053\u5200\u75d5\u5728\u9f3b\u6881\u4e0a\u753b\u4e86\u4e2a\u4ea4\u53c9\uff0c\u5f53\u4ed6\u60f3\u5230 \u91d1\u6b39\u6293\u4f4f\u5434\u51cc\u98ce\u843d\u4e0b\u60ac\u5d16\u7684\u75af\u72c2\u9762\u5b54\uff0c\u4e0d\u7981\u4f7f\u8f9b\u6377\u6253\u4e2a\u5bd2\u895f\u3002 \u3000\u3000\u8f9b\u6377\u53f9\u4e86\u53e3\u6c14\uff0c\u987a\u624b\u63a2\u4e86\u91d1\u6b39\u9f3b\u606f\uff0c\u5012\u751a\u5747\u5300\u6709\u529b\uff0c\u4e8e\u662f\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u4f24\u5f97 \u4e0d\u751a\u91cd\uff0c\u5927\u6982\u518d\u4f11\u606f\u4e2a\u628a\u65f6\u8fb0\u5373\u53ef\u4ee5\u6e05\u9192\u8fc7\u6765\u3002\u201d \u3000\u3000\u8f9b\u6377\u8f6c\u8138\u671b\u7740\u6b63\u5173\u5207\u6ce8\u89c6\u91d1\u6b39\u7684\u65b9\u5c11\u78a7\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5947\u602a\u4ed6\u4e24\u4eba\u600e\u4e48\u4f1a\u7ed3\u4e3a\u4e00\u5757\u7684\uff1f\u53c8\u600e \u4f1a\u8dd1\u5230\u8fd9\u8352\u50fb\u7684\u6d77\u8fb9\u5ca9\u533a\u6765\u4f4f\u5462\uff1f \u3000\u3000\u65b9\u5c11\u78a7\u84e6\u5730\u53d1\u89c9\u8f9b\u6377\u6b63\u7591\u60d1\u5730\u770b\u7740\u81ea\u5df1\uff0c\u4e0d\u7981\u7ea2\u98de\u53cc\u988a\uff0c\u8f7b\u8f7b\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u4e0d\u5230\u6211\u4f1a\u5ac1 \u7ed9\u4ed6\u662f\u5417\uff1f\u201d\u65b9\u5c11\u78a7\u779f\u4e86\u5e8a\u4e0a\u91d1\u6b39\u4e00\u773c\u3002 \u3000\u3000\u8f9b\u6377\u70b9\u70b9\u5934\u2014\u2014 \u3000\u3000\u65b9\u5c11\u671b\u53c8\u6de1\u6de1\u82e6\u7b11\uff0c\u62cd\u62cd\u65c1\u8fb9\u77f3\u6905\u8bf7\u8f9b\u6377\u5750\u4e0b\uff0c\u7136\u540e\u5a13\u5a13\u9053\u51fa\u4e00\u6bb5\u4e8b\u8ff9\u6765\u2014\u2014 \u3000\u3000\u201c\u4f60\u77e5\u9053\u90a3\u5929\u6211\u6295\u6c5f\u540e\u2026\u2026\u201d\u65b9\u5c11\u78a7\u542b\u7f9e\u5730\u671b\u671b\u6b63\u9884\u5907\u8046\u542c\u7684\u8f9b\u6377\uff0c\u8111\u4e2d\u53c8\u6d6e\u8d77\u90a3\u4f7f\u5979 \u7ec8\u751f\u4e5f\u4e0d\u80fd\u5fd8\u6000\u7684\u4e00\u5e55\u3002 \u3000\u3000\u8f9b\u6377\u5f53\u7136\u77e5\u9053\u5979\u8bf4\u7684\u662f\u4ec0\u4e48\uff0c\u60ed\u6127\u7684\u8868\u60c5\u4f7f\u4ed6\u8138\u8272\u663e\u5f97\u751a\u662f\u96be\u770b\uff0c\u65b9\u5c11\u78a7\u63d0\u8d77\u8fd9\u4e8b\u53c8\u4f7f \u4ed6\u60f3\u8d77\u5931\u8e2a\u4e45\u672a\u8054\u7cfb\u7684\u91d1\u6885\u9f84\u2014\u2014 \u3000\u3000\u201c\u5509\uff01\u6377\u54e5\u2026\u2026\u201d\u65b9\u5c11\u78a7\u77e5\u9053\u8f9b\u6377\u5fc3\u4e2d\u4e00\u5b9a\u5f88\u96be\u8fc7\uff0c\u800c\u81ea\u5df1\u53c8\u4f55\u5c1d\u4e0d\u96be\u8fc7\u5462\uff1f\u521d\u9022\u65f6\u7684 \u60ca\u559c\uff0c\u7ee7\u4e4b\u5f3a\u8feb\u81ea\u5df1\u5bf9\u4ed6\u7684\u51b7\u6de1\uff0c\u5df2\u4f7f\u5979\u591a\u5e74\u5bf9\u8f9b\u6377\u7684\u6068\u610f\u5b8c\u5168\u52fe\u9500\uff0c\u5e76\u4e14\u5982\u679c\u4e25\u683c\u8bf4\u4e00 \u70b9\uff0c\u81ea\u5df1\u4e5f\u6709\u8d1f\u4e8e\u4ed6\u5440\uff01\u65b9\u5c11\u671b\u5fc3\u60f3\uff0c\u56e0\u4e3a\u5979\u4e0d\u662f\u4e5f\u5ac1\u7ed9\u4ee5\u5f80\u6700\u75db\u6068\u7684\u4eba\u2014\u2014\u91d1\u6b39\uff1f \u3000\u3000\u201c\u9f84\u59d0\u59d0\u5982\u4f55\u4e86\uff1f\u201d\u65b9\u5c11\u671b\u81ea\u5df1\u4e5f\u4e0d\u77e5\u4e3a\u4f55\u4f1a\u558a\u51fa\u201c\u9f84\u59d0\u59d0\u201d\u7684\uff0c\u4f46\u770b\u8f9b\u6377\u75db\u6094\u7684\u8868 \u60c5\uff0c\u591a\u5c11\u4e5f\u731c\u51fa\u4e9b\u7aef\u502a\u3002 \u3000\u3000\u8f9b\u6377\u6ca1\u6709\u56de\u7b54\uff0c\u53ea\u6728\u7136\u6447\u6447\u5934\uff0c\u5fc3\u4e2d\u5bf9\u65b9\u5c11\u78a7\u7684\u653e\u8fc7\u91d1\u6885\u9f84\u4e5f\u5bbd\u6170\u4e86\u4e0d\u5c11\u2014\u2014 \u3000\u3000\u65b9\u5c11\u78a7\u4e0d\u987e\u518d\u95ee\u8d77\u4f7f\u8f9b\u6377\u75db\u5fc3\u7684\u4e8b\uff0c\u4ecd\u7ee7\u7eed\u5148\u524d\u8bdd\u9898\u9053\uff1a\u201c\u90a3\u5929\u6211\u6295\u6c34\u4ee5\u540e\uff0c\u6211\u6068\u4e00 \u5207\uff0c\u6211\u4e5f\u6068\u6211\u81ea\u5df1\uff0c\u4e8e\u662f\u6211\u5c4f\u4f4f\u6c14\u62fc\u547d\u8981\u5f80\u6c34\u4e0b\u94bb\uff0c\u60f3\u8ba9\u6c5f\u6c34\u5c06\u6211\u6df9\u6ca1\uff0c\u6c38\u8fdc\u6df9\u6ca1\uff0c\u4f46\u662f\u6d6a \u662f\u5982\u6b64\u5927\uff0c\u6211\u652f\u6301\u4e0d\u4e86\u51e0\u53e3\u6c14\u5373\u660f\u7edd\u8fc7\u53bb\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u968f\u7740\u5979\u7684\u53d9\u8ff0\uff0c\u601d\u6f6e\u53c8\u6eaf\u5230\u6614\u65e5\uff0c\u60f3\u7740\u65b9\u5c11\u78a7\u5728\u5927\u6c5f\u4e4b\u4e2d\u968f\u6ce2\u9010\u6d41\uff0c\u6162\u6162\u8fdc\u53bb\uff0c\u7ec8 \u81f3\u53bb\u6d88\u901d\u65e0\u8e2a\u2014\u2014 \u3000\u3000\u65b9\u5c11\u78a7\u7684\u58f0\u97f3\u5f88\u5e73\u9759\uff0c\u5f88\u59d4\u5a49\uff0c\u9664\u4e86\u9053\u51fa\u6570\u5e74\u6765\u6d41\u6d6a\u7684\u7ecf\u8fc7\u5916\uff0c\u5c3d\u91cf\u907f\u514d\u5f15\u8d77\u8f9b\u6377\u75db\u82e6 \u7684\u56de\u5fc6\u3002 \u3000\u3000\u201c\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6211\u9192\u4e86\u8fc7\u6765\u4e86\u5468\u8eab\u662f\u5982\u6b64\u6e7f\uff0c\u6211\u60f3\u5927\u6982\u662f\u51b7\u9192\u7684\u5427\uff01\u201d\u65b9\u5c11\u78a7\u4e00\u76f4\u8bf4 \u4e0b\u53bb\uff0c\u5076\u5c14\u773c\u7533\u95ea\u8fc7\u4e00\u4e1d\u7737\u604b\u6614\u65e5\u60c5\u666f\u7684\u76ee\u5149\u3002 \u3000\u3000\u2026\u2026\u6b64\u65f6\u5929\u5df2\u9ed1\u4e86\uff0c\u9eef\u6de1\u7684\u661f\u5149\u5728\u5929\u4e0a\u95ea\u70c1\u7740\uff0c\u6211\u611f\u89c9\u56db\u80a2\u61d2\u6563\u5df2\u6781\uff0c\u5fc3\u7075\u7684\u9ebb\u6728\u4e0e\u80a2 \u4f53\u75b2\u52b3\u4f7f\u6211\u9664\u4e86\u6c89\u9759\u5916\uff0c\u8fde\u6307\u5934\u4e5f\u4e0d\u60f3\u52a8\u52a8\u2014\u2014 \u3000\u3000\u6211\u5e73\u4ef0\u7740\u8eab\u5b50\uff0c\u4e5f\u4e0d\u77e5\u81ea\u5df1\u662f\u5728\u6c34\u4e0a\uff1f\u8fd8\u662f\u5728\u9646\u4e0a\uff1f\u6216\u5728\u8239\u4e2d\uff1f\u56e0\u4e3a\u8fd9\u79cd\u79cd\u5bf9\u6211\u90fd\u6beb\u65e0 \u5173\u7cfb\u3002 \u3000\u3000\u7a81\u7136\u6211\u89c9\u5f97\u8eab\u4fa7\u8fdc\u5904\u706b\u5149\u4e00\u4eae\uff0c\u63a5\u7740\u4e00\u4e2a\u5b69\u5b50\u53e3\u97f3\u547c\u9053\uff1a\u201c\u5976\u5976\uff01\u90a3\u4f4d\u59d1\u59d1\u5c31\u5728\u90a3 \u8fb9\uff01\u201d \u3000\u3000\u63a5\u7740\u4e00\u4e2a\u5987\u4eba\u7684\u53e3\u97f3\uff1a\u201c\u4e56\u5b69\u5b50\uff0c\u4f60\u5148\u8dd1\u53bb\u770b\u770b\uff0c\u4e0d\u8981\u8ba9\u8fd9\u53ef\u601c\u7684\u4eba\u51bb\u574f\u4e86\u3002\u201d \u3000\u3000\u53c8\u95fb\u5c0f\u5b69\u5e94\u4e86\u58f0\uff0c\u7acb\u523b\u65b9\u5c11\u78a7\u89c9\u5f97\u6709\u4eba\u5f88\u5feb\u8dd1\u81f3\u81ea\u5df1\u8eab\u4fa7\u3002 \u3000\u3000\u201c\u5976\u5976\uff01\u5979\u5df2\u7ecf\u9192\u4e86\uff0c\u554a\uff0c\u4f60\u770b\u5979\u5168\u8eab\u90fd\u6e7f\u900f\u4e86\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5987\u4eba\u4e5f\u8d70\u4e86\u8fc7\u6765\uff0c\u770b\u770b\u65b9\u5c11\u78a7\u9664\u4e86\u8eab\u4f53\u663e\u5f97\u865a\u5f31\u5916\u4e00\u5207\u5c1a\u597d\u597d\u7684\uff0c\u4e0d\u7981\u677e\u53e3\u6c14\uff0c \u9053\uff1a\u201c\u5509\uff01\u5c0f\u798f\u771f\u4e8f\u4e86\u4f60\u7684\u2026\u2026\u59d1\u5a18\uff01\u4f60\u611f\u89c9\u597d\u5417\uff1f\u201d\u6562\u60c5\u5979\u4e5f\u53d1\u89c9\u65b9\u5c11\u78a7\u9192\u4e86\u3002 \u3000\u3000\u65b9\u5c11\u78a7\u867d\u7136\u5fc3\u4e2d\u611f\u6fc0\u8fd9\u5987\u4eba\u7684\u597d\u5fc3\uff0c\u4f46\u5185\u5fc3\u7684\u4e00\u5207\u90fd\u53d8\u6210\u7edd\u671b\uff0c\u4e00\u5207\u90fd\u53d8\u5f97\u6f20\u7136\uff0c\u4ee5\u81f3 \u5bf9\u7740\u8fd9\u597d\u5fc3\u7684\u5987\u4eba\u8138\u662f\u8fd9\u822c\u51b0\u51b7\u3002 \u3000\u3000\u65b9\u5c11\u78a7\u8bf4\u5230\u6b64\u5904\uff0c\u8f9b\u6377\u7a81\u7136\u6253\u65ad\u8bdd\u9898\u95ee\u9053\uff1a\u201c\u4f60\u6f02\u5230\u4ec0\u4e48\u5730\u65b9\uff1f\u201d \u3000\u3000\u65b9\u5c11\u78a7\u770b\u770b\u8f9b\u6377\u8138\u4e0a\u5173\u5207\u7684\u795e\u60c5\uff0c\u5fc3\u4e2d\u4e5f\u89c9\u5f97\u751c\u6ecb\u6ecb\u7684\uff0c\u5c24\u5176\u4ed6\u76ee\u5149\u4e2d\u4e07\u7f15\u67d4\u60c5\u4e0d\u662f\u8fd8 \u50cf\u5f80\u6614\u4e00\u822c\u5417\uff1f \u3000\u3000\u201c\u5f53\u65f6\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u540e\u6765\u542c\u90a3\u6551\u6211\u7684\u6e14\u5987\u8bf4\uff0c\u624d\u77e5\u7adf\u662f\u8ddd\u79bb\u6b66\u6c49\u767e\u4f59\u91cc\u7684\u2018\u6768\u903b\u2019\u3002\u201d \u65b9\u5c11\u78a7\u5b89\u6170\u5730\u7b11\u9053\u3002 \u3000\u3000\u8f9b\u6377\u53f9\u9053\uff1a\u201c\u4f60\u547d\u8fd0\u6bd4\u6211\u8fd8\u597d\u4e9b\u2026\u2026\u5509\uff01\u6211\u2026\u2026\u201d \u3000\u3000\u65b9\u5c11\u78a7\u7684\u6cea\u6c34\u53c8\u6d8c\u51fa\u51fa\u773c\u7736\uff0c\u6570\u4e2a\u65f6\u8fb0\u524d\u7684\u6068\u610f\u65e9\u5df2\u88ab\u67d4\u60c5\u6240\u5316\uff0c\u53ea\u89c1\u5979\u8f7b\u8f7b\u63e1\u4e86\u63e1\u8f9b \u6377\u7684\u624b\uff0c\u6545\u610f\u88c5\u51fa\u7b11\u8138\uff0c\u6e29\u67d4\u5730\u9053\uff1a\u201c\u6377\u54e5\uff0c\u522b\u60f3\u4ee5\u524d\u4e86\u5427\uff01\u8ba9\u6211\u544a\u8bc9\u4f60\u4ee5\u540e\u7684\u4e8b\u60c5\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u70b9\u70b9\u5934\uff0c\u8f7b\u629a\u7740\u65b9\u5c11\u78a7\u96f6\u4e71\u800c\u7ec6\u957f\u7684\u79c0\u53d1\uff0c\u5fc3\u4e2d\u8bf4\u4e0d\u51fa\u662f\u4ec0\u4e48\u6ecb\u5473\uff0c\u552f\u4e00\u4f7f\u4ed6\u5b89\u6170 \u7684\u662f\u78a7\u59b9\u5df2\u7ecf\u6709\u4e86\u201c\u5f52\u5bbf\u201d\uff0c\u4e0d\u7ba1\u662f\u8c01\uff0c\u591a\u5c11\u5bf9\u4ed6\u7684\u5185\u759a\u6709\u4e86\u8865\u507f\u3002 \u3000\u3000\u65b9\u5c11\u78a7\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u81ea\u4ece\u6211\u88ab\u90a3\u6e14\u5987\u6551\u540e\uff0c\u6e14\u5987\u601c\u6211\u5b64\u82e6\u65e0\u4f9d\uff0c\u4f55\u51b5\u5979\u4e5f\u4ec5\u6709\u7956\u5b59\u4e24\u4eba \u76f8\u4f9d\u4e3a\u4f34\uff0c\u6240\u4ee5\u5c31\u8ba9\u6211\u7559\u5c45\u4e0b\u6765\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u6837\u8fc7\u4e86\u8fd1\u534a\u5e74\uff0c\u6211\u5bf9\u4e00\u5207\u4ece\u6b64\u7070\u5fc3\u4e86\uff0c\u6211\u7684\u611f\u60c5\u50cf\u69c1\u6728\u822c\u6c38\u8fdc\u6b7b\u6c89\u8fc7\u53bb\uff0c\u4f46\u4e00\u4e2a\u4eba \u7684\u547d\u8fd0\u5e76\u4e0d\u5982\u6b64\u5730\u7b80\u5355\u2026\u2026\u201d \u3000\u3000\u201c\u6211\u8fd8\u8bb0\u5f97\u90a3\u5929\u4e0b\u5348\uff0c\u672c\u662f\u521d\u6625\u5954\u653e\u65f6\u8282\uff0c\u7a81\u7136\u2026\u2026\u7a81\u7136\u91d1\u6b39\u6765\u4e86\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u542c\u5f97\u4e00\u9635\u7d27\u5f20\uff0c\u8eab\u5b50\u4e5f\u4e0d\u81ea\u89c9\u4ef0\u8d77\u3002 \u3000\u3000\u201c\u539f\u6765\u6e05\u9759\u800c\u606c\u6de1\u7684\u5c0f\u8305\u5c4b\u2014\u2014\u6e14\u5987\u7684\u5bb6\u3002\u201d\u65b9\u5c11\u78a7\u5982\u6b64\u8ff0\u8bf4\u7740\uff0c\u201c\u7a81\u7136\u6380\u8d77\u5927\u98ce \u6ce2\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e00\u65e5\u6211\u6b63\u5728\u966a\u90a3\u597d\u5fc3\u7684\u6e14\u5987\u505a\u5973\u7ea2\u2026\u2026\u201d\u65b9\u5c11\u78a7\u7565\u5e26\u8ffd\u5fc6\u7684\u795e\u8272\u2014\u2014 \u3000\u3000\u201c\u5657\u201d\uff01\u6572\u95e8\u7684\u58f0\u97f3\uff0c\u63a5\u7740\u4e00\u4e2a\u7537\u5b50\u53e3\u97f3\u53eb\u7740\uff1a\u201c\u5f00\u95e8\uff01\u78a7\u59b9\u51fa\u6765\uff01\u201d \u3000\u3000\u6211\u542c\u89c1\u8fd9\u58f0\u97f3\u8138\u90e8\u53d1\u81ea\u4e86\uff0c\u523a\u8033\u800c\u56a3\u5f20\u7684\u53eb\u5608\uff0c\u4e0d\u662f\u201c\u5929\u9b54\u91d1\u6b39\u201d\u8fd8\u4f1a\u662f\u8c01\uff1f \u3000\u3000\u9003\u662f\u9003\u4e0d\u4e86\uff0c\u6211\u5fc3\u91cc\u60f3\u7740\uff0c\u4e0d\u7981\u6478\u6478\u4e00\u76f4\u85cf\u5728\u6000\u4e2d\u7684\u5315\u9996\uff0c\u6162\u6162\u5c06\u95e8\u6253\u5f00\u2014\u2014 \u3000\u3000\u51fa\u73b0\u5728\u6211\u9762\u524d\u662f\u4e00\u4e2a\u8934\u891b\u800c\u75b2\u4e4f\u7684\u9752\u5e74\uff0c\u6211\u51e0\u4e4e\u8ba4\u4e0d\u51fa\u4ed6\u5373\u662f\u6700\u4ee4\u6211\u538c\u618e\u7684\u201c\u91d1\u6b39\u201d\u3002 \u3000\u3000\u201c\u78a7\u59b9\u2026\u2026\u78a7\u59b9\uff01\u4f60\u5bb3\u5f97\u6211\u597d\u82e6\uff01\u201d\u91d1\u6b39\u8bed\u6c14\u4ecd\u662f\u8fd9\u4e48\u4e13\u6a2a\uff0c\u4e00\u53cc\u624b\u6276\u4f4f\u95e8\u69db\u50cf\u662f\u8981\u8dcc \u4e0b\u6765\u2014\u2014 \u3000\u3000\u6211\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u91d1\u6b39\uff01\u4f60\u7ed9\u6211\u6eda\uff01\u6eda\u5f97\u8fdc\u8fdc\u7684\uff0c\u6211\u6c38\u8fdc\u4e0d\u8981\u518d\u89c1\u4f60\u2026\u2026\u518d\u89c1\u4f60\u4eec\u4e24\u4eba\u2014 \u2014\u201d\u6211\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\u4e3a\u4f55\u591a\u65e5\u5e73\u9759\u7684\u5fc3\u80f8\u4f1a\u7a81\u7136\u6fc0\u52a8\u8d77\u6765\u3002 \u3000\u3000\u91d1\u6b39\u5634\u5fae\u5f20\u5730\u671b\u7740\u6211\uff0c\u5f88\u4e45\u6ca1\u6709\u7406\u7684\u4e71\u53d1\u906e\u53bb\u4ed6\u4ece\u524d\u4fcf\u4fca\u7684\u9762\u5bb9\uff0c\u6211\u4ece\u672a\u89c1\u8fc7\u4ed6\u5982\u6b64\u4f4e \u58f0\u4e0b\u6c14\u8fc7\u8bf4\u9053\uff1a \u3000\u3000\u201c\u78a7\u59b9\u5f97\u7f6a\u4f60\u7684\u4eba\u5e76\u4e0d\u662f\u6211\u554a\uff01\u4e3a\u4f55\u8981\u8fde\u6211\u4e00\u5e76\u6068\u4e0a\u5462\uff1f\u4e0a\u5929\u53ef\u601c\u624d\u8ba9\u6211\u5bfb\u5f97\u4f60\uff0c\u6211\u8fd9 \u822c\u6df1\u7231\u4f60\uff0c\u4e3a\u4f55\u4f60\u603b\u8981\u4f24\u6211\u5fc3\u5462\uff1f\u201d \u3000\u3000\u6211\u6fc0\u52a8\u5f97\u63a9\u9762\u75db\u54ed\u8d77\u6765\uff0c\u53e3\u4e2d\u8fde\u8fde\u547c\u9053\uff1a\u201c\u6211\u6068\u2026\u2026\u6211\u6068\u4f60\u4eec\u4e24\u4eba\u2026\u2026\u554a\uff01\u91d1\u6b39\u4f60\uff01\u4f60 \u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u6b64\u65f6\u91d1\u6b39\u7a81\u7136\u6276\u4f4f\u80f8\u90e8\uff0c\u8138\u4e0a\u808c\u8089\u60e8\u767d\u5e76\u8fde\u7eed\u62bd\u52a8\u6570\u4e0b\uff0c\u7a81\u7136\u5012\u5728\u6211\u811a\u8fb9\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5fd6\u9053\uff1a\u201c\u5bf9\u4e86\uff01\u5fc5\u662f\u8fd9\u53ae\u4e2d\u4e86\u6211\u4e00\u638c\uff0c\u4e3a\u4e86\u5bfb\u78a7\u59b9\u7adf\u8fde\u65e5\u8dcb\u6d89\uff0c\u6ca1\u6709\u597d\u597d\u5c06\u606f\u8fc7\u624d \u4f1a\u5982\u6b64\u4e25\u91cd\uff0c\u5982\u6b64\u770b\u6765\u4ed6\u5bf9\u78a7\u59b9\u53ef\u662f\u771f\u611f\u60c5\u554a\u3002\u201d \u3000\u3000\u4e14\u4e0d\u8bf4\u8f9b\u6377\u5fc3\u4e2d\u8d77\u4f0f\uff0c\u65b9\u5c11\u78a7\u7ee7\u7eed\u53d9\u8ff0\u7740\uff1a\u201c\u78a7\u59b9\u6211\u2026\u2026\u6211\u5185\u4f24\u53d1\u4e86\u3002\u201d\u91d1\u6b39\u75db\u82e6\u5730\u547b \u541f\u7740\uff0c\u65e0\u52a9\u5730\u4f38\u51fa\u53f3\u624b\u2014\u2014 \u3000\u3000\u201c\u6211\u84e6\u5730\u5fc3\u8f6f\u4e86\uff0c\u867d\u7136\u91d1\u6b39\u5929\u6027\u51c9\u8584\uff0c\u5bf9\u6211\u5374\u662f\u4e00\u7247\u771f\u5fc3\uff0c\u4e8e\u662f\u6211\u8fde\u5fd9\u5c06\u4ed6\u6276\u81f3\u5e8a\u4e0a\u3002 \u3000\u3000\u7ecf\u8fc7\u6570\u65e5\u7684\u6cbb\u7597\uff0c\u4ed6\u7ec8\u4e8e\u597d\u8f6c\u8fc7\u6765\u2014\u2014\u201d \u3000\u3000\u201c\u78a7\u59b9\uff01\u201d\u8fd9\u4e00\u65e5\u4ed6\u5df2\u80fd\u5750\u8d77\uff0c\u8bda\u6073\u5730\u5bf9\u6211\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u5f88\u6068\u6211\uff0c\u6068\u6211\u7684\u4e3a \u4eba\u2026\u2026\u4f46\u662f\u2026\u2026\u4f46\u662f\u6211\u613f\u610f\u4e3a\u4f60\u6539\u8fc7\u81ea\u65b0\u7684\uff0c\u4f60\u77e5\u9053\u6211\u662f\u591a\u4e48\u7684\u7231\u4f60\u2026\u2026\u201d \u3000\u3000\u6211\u4e0d\u5f97\u4e0d\u88c5\u51fa\u51b7\u6f20\u7684\u6837\u5b50\uff0c\u867d\u7136\u5bf9\u4ed6\u7684\u6076\u611f\u662f\u5c11\u4e86\u5f88\u591a\uff0c\u4f46\u6211\u4ecd\u6447\u7740\u5934\u3002 \u3000\u3000\u201c\u597d\u5427\uff01\u6211\u4e0d\u6562\u52c9\u5f3a\u4f60\uff0c\u867d\u7136\u8fd9\u4e0d\u662f\u6211\u4ee5\u5f80\u7684\u4f5c\u98ce\u3002\u201d\u91d1\u6b39\u51fa\u5947\u5e73\u9759\u5730\u9053\uff0c\u76ee\u5149\u4e2d\u5f80\u65e5 \u51f6\u623e\u7684\u795e\u4e5e\u4e00\u4e1d\u4e5f\u65e0\uff0c\u53ea\u89c1\u4ed6\u7ee7\u7eed\u9053\uff1a\u201c\u4f46\u6211\u60f3\u77e5\u9053\uff0c\u4f60\u4e3a\u4f55\u5982\u6b64\u8ba8\u538c\u6211\uff1f\u5982\u6b64\u6068\u6211\u5462\uff1f\u96be \u9053\u4ec5\u4ec5\u4e3a\u7740\u8f9b\u6377\u90a3\u5c0f\u5b50\u5417\uff1f\u201d \u3000\u3000\u6211\u4e0d\u613f\u4ed6\u8c08\u5230\u4f60\u7684\u540d\u5b57\uff0c\u867d\u7136\u6211\u5fc3\u4e2d\u65f6\u5e38\u53cd\u590d\u5ff5\u7740\u5b83\u3002\u65b9\u5c11\u78a7\u7ee7\u7eed\u5bf9\u8f9b\u6377\u8bf4\uff1a \u3000\u3000\u201c\u4f55\u51b5\u7238\u5988\u7684\u60e8\u6b7b\uff0c\u90a3\u4e00\u5e55\u666f\u50cf\u53c8\u6e05\u6670\u6d6e\u4e0a\u6211\u8111\u6d77\uff0c\u50cf\u7740\u9b54\u822c\u7a81\u7136\u5bf9\u4ed6\u8bc5\u5492\u8d77\u6765\u3002\u201d \u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u8fd9\u6076\u9b54\uff01\u4f60\u8fde\u7236\u6bcd\u90fd\u80fd\u6740\uff0c\u6211\u8fd8\u6562\u559c\u6b22\u4f60\uff1f\u201d\u91d1\u6b39\u7684\u8138\u8272\u53d8\u4e86\uff0c\u6211\u4ece\u672a\u770b\u8fc7 \u4ed6\u5982\u6b64\u60ed\u6127\u8fc7\uff0c\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u5feb\u611f\u5728\u6211\u8840\u6db2\u4e2d\u5954\u6d41\u7740\uff0c\u7238\uff01\u5988\uff01\u867d\u7136\u4ed6\u4eec\u5e76\u4e0d\u662f\u6211\u4eb2\u751f\u7236 \u6bcd\uff0c\u5e76\u4e14\u5f3a\u8feb\u6211\u5ac1\u7ed9\u6211\u4e0d\u559c\u6b22\u7684\u4eba\uff0c\u4f46\u4ed6\u4eec\u603b\u6709\u517b\u80b2\u6211\u5341\u4f59\u5e74\u4e4b\u6069\u5440\uff01 \u3000\u3000\u201c\u9006\u5b50\uff01\u4f60\u8fd9\u6740\u4eb2\u4e4b\u9006\u5b50\uff01\u4f60\u8fd9\u4e0d\u5bb9\u6055\u7684\u9006\u5b50\uff01\u201d\u6211\u4e0d\u505c\u53eb\u558a\u7740\u3002 \u3000\u3000\u201c\u4f60\u81ea\u79f0\u7231\u6211\uff0c\u613f\u4e3a\u6211\u727a\u7272\u4e00\u5207\uff0c\u54fc\uff0c\u5982\u679c\u4f60\u5c06\u4f60\u81ea\u8ba4\u4e3a\u6f02\u4eae\u7684\u8138\u4e0a\u753b\u4e24\u5200\u6211\u5c31\u5ac1\u7ed9 \u4f60\u3002\u201d\u4e00\u65f6\u6c14\u60ef\u6211\u7adf\u5410\u51fa\u8fd9\u53e5\u8bdd\u3002 \u3000\u3000\u91d1\u6b39\u82cd\u767d\u7684\u8138\u4e0a\uff0c\u7a81\u7136\u9732\u51fa\u51b3\u7136\u7684\u795e\u8272\uff0c\u6124\u9053\uff1a\u201c\u78a7\u59b9\uff01\u5f53\u7136\u6211\u72af\u4e86\u6ed4\u5929\u5927\u9519\u4e0d\u5bb9\u5bbd \u8c05\uff0c\u4f46\u4f60\u8bf4\u7684\u8bdd\u53ef\u7b97\u6570\uff1f\u201d \u3000\u3000\u6211\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u84e6\u5730\u4ece\u6000\u4e2d\u62bd\u51fa\u5315\u9996\u4ea4\u7ed9\u4ed6\u9053\uff1a\u201c\u5212\u5427\uff01\u5212\u5427\uff01\u6211\u8981\u770b\u770b\u80fd\u6740\u7236\u6bcd\u7684 \u4eba\u80fd\u4e0d\u80fd\u5212\u81ea\u5df1\u7684\u8138\uff1f\u201d \u3000\u3000\u91d1\u6b39\u63a5\u8fc7\u5315\u9996\uff0c\u671b\u7740\u6211\u5931\u5e38\u7684\u72c2\u6001\uff0c\u7a81\u7136\u53cd\u624b\u4e24\u5251\uff0c\u7adf\u771f\u7684\u5728\u81ea\u5df1\u7684\u8138\u4e0a\u5212\u4e86\u4e00\u4e2a\u5341 \u5b57\uff0c\u4ed6\u72c2\u53eb\u4e24\u58f0\u201c\u59b9\u201d\uff0c\u9c9c\u8840\u4ece\u4ed6\u8138\u4e0a\u6cca\u6cca\u6d41\u4e0b\uff0c\u521a\u75c5\u6108\u865a\u5f31\u7684\u8eaf\u4f53\uff0c\u53d7\u4e0d\u4f4f\u8fd9\u7cbe\u795e\u4e0e\u8089\u4f53 \u7684\u53cc\u91cd\u6253\u51fb\uff0c\u7acb\u523b\u660f\u5012\u5728\u5e8a\u4e0a\u2014\u2014 \u3000\u3000\u6211\u88ab\u8fd9\u610f\u60f3\u4e0d\u5230\u7684\u53d8\u5316\u60ca\u5f97\u5446\u4e86\uff0c\u770b\u7740\u91d1\u6b39\u8138\u4e0a\u6df1\u800c\u7ea2\u7684\u4e24\u9053\u5341\u5b57\u4f24\u53e3\uff0c\u4e00\u79cd\u7f6a\u6076\u7684\u60e9 \u7f5a\u5728\u6211\u5fc3\u5934\u6ecb\u957f\u3002 \u3000\u3000\u201c\u554a\uff01\u65b9\u5c11\u78a7\u4f60\u4f5c\u4e86\u4ec0\u4e48\u4e8b\u554a\uff1f\u201d\u88ab\u60ca\u5413\u7740\u7684\u6211\uff0c\u4e22\u5f03\u91cd\u4f24\u7684\u91d1\u6b39\uff0c\u63a9\u9762\u98de\u5954\u800c\u53bb\uff0c\u50cf \u907f\u7f6a\u6076\u7684\u6df1\u6e0a\u822c\uff0c\u6211\u518d\u4e5f\u4e0d\u6562\u56de\u987e\u4e00\u4e0b\u90a3\u5c0f\u8305\u5c4b\u2014\u2014 \u3000\u3000\u201c\u4e8e\u662f\u6211\u53c8\u5f00\u59cb\u6d41\u6d6a\u4e86\u2026\u2026\u201d\u65b9\u5c11\u78a7\u8bf4\u81f3\u6b64\u5904\uff0c\u65e9\u6ce3\u5f97\u6cea\u6e7f\u6cbe\u88f3\uff0c\u80f8\u90e8\u6025\u5598\u5730\u62bd\u6410\uff0c\u50cf \u4e45\u7ecf\u5fe7\u60a3\u7684\u5b69\u5b50\uff0c\u9047\u5230\u4eb2\u4eba\u5c06\u5fc3\u4e2d\u90c1\u6124\u8981\u4e00\u5410\u800c\u5c3d\u7684\u6837\u5b50\u3002 \u3000\u3000\u8f9b\u6377\u62cd\u7740\u5979\u4e0a\u4e0b\u62bd\u52a8\u7684\u53cc\u80a9\uff0c\u629a\u6170\u5979\u9053\uff1a\u201c\u5b89\u9759\u70b9\uff01\u6162\u6162\u8bb2\uff01\u201d\u4ece\u4ed6\u77e5\u9053\u65b9\u5c11\u78a7\u5df2\u5c5e\u91d1 \u6b39\u540e\uff0c\u81ea\u7136\u7684\u5bf9\u5979\u53ea\u5269\u4e0b\u7eaf\u6d01\u7684\u53cb\u60c5\u3002 \u3000\u3000\u65b9\u5c11\u78a7\u6fc0\u52a8\u4e00\u4f1a\u624d\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u540e\u6765\u6211\u5728\u6c5f\u6e56\u4e0a\u6d41\u6d6a\uff0c\u95fb\u5230\u4e03\u5999\u795e\u541b\u8981\u5230\u6cf0\u5c71\u53c2\u52a0\u5927 \u4f1a\uff0c\u6211\u65e9\u5df2\u6000\u7591\u5230\u2018\u4e03\u5999\u795e\u541b\u2019\u5fc5\u662f\u4f60\uff0c\u6240\u4ee5\u6211\u65e0\u6cd5\u81ea\u4e3b\u5730\u5411\u5c71\u4e1c\u65b9\u5411\u884c\u53bb\u2026\u2026\u201d \u3000\u3000\u201c\u7b49\u6211\u8fbe\u5230\u6cf0\u5c71\u811a\u4e0b\u65f6\uff0c\u5927\u4f1a\u5df2\u7ecf\u4f5c\u9e1f\u517d\u6563\uff0c\u4f46\u6211\u7a81\u7136\u53d1\u73b0\u4e86\u91d1\u6b39\uff0c\u4ed6\u53c8\u662f\u4f24\u5f97\u8fd9\u822c \u91cd\uff0c\u4ece\u5ca9\u77f3\u8fb9\u722c\u4e0a\u6765\uff0c\u6bb7\u7ea2\u7684\u5200\u75d5\u4ecd\u9192\u76ee\u5730\u4ea4\u53c9\u7740\u2026\u2026\u201d \u3000\u3000\u201c\u4ed6\u4e5f\u770b\u89c1\u4e86\u6211\uff0c\u7adf\u52aa\u529b\u6323\u624e\u5411\u6211\u722c\u6765\uff0c\u53e3\u4e2d\u5c1a\u5583\u5541\u5ff5\u9053\uff1a\u2018\u78a7\u59b9\uff0c\u5bbd\u6055\u6211\uff01\u78a7\u59b9\uff0c\u518d \u522b\u79bb\u5f00\u6211\uff01\u2019 \u3000\u3000\u81f3\u6b64\u6211\u611f\u60c5\u5b8c\u5168\u5d29\u6e83\u4e86\uff0c\u601c\u60dc\u4ed6\u7684\u5fc3\u60c5\u4f7f\u6211\u53d8\u6210\u7231\u4ed6\u7684\u75f4\u5fc3\uff0c\u4e8e\u662f\u6211\u5e26\u7740\u4ed6\u6765\u4e86\u6b64\u5904\uff0c \u8fd9\u8352\u51c9\u65e0\u4eba\u7684\u5ca9\u533a\uff0c\u6c38\u8fdc\u79bb\u5f00\u4eba\u7fa4\uff0c\u5c31\u5b64\u5355\u7ec8\u5176\u4e00\u751f\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u81ea\u6b64\u624d\u660e\u767d\u65b9\u5c11\u78a7\u4e0e\u91d1\u6b39\u7ed3\u5408\u7684\u672c\u672b\uff0c\u5fc3\u4e2d\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u6ecb\u5473\u3002 \u3000\u3000\u201c\u4f46\u4f60\u600e\u4f1a\u88ab\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u8ffd\u4e0a\u5462\uff1f\u201d\u8f9b\u6377\u5947\u602a\u91d1\u6b39\u7684\u88ab\u6253\u4f24\u3002 \u3000\u3000\u65b9\u5c11\u78a7\u8138\u4e00\u7ea2\uff0c\u9053\uff1a\u201c\u8fd8\u4e0d\u662f\u4ed6\uff01\u201d\u5979\u6307\u7740\u91d1\u6b39\uff0c\u9053\uff1a\u201c\u4ed6\u8bf4\u5728\u6d1e\u91cc\u5446\u5f97\u70e6\u4e86\uff0c\u8981\u51fa\u53bb \u6563\u6563\u5fc3\u201d\u63a5\u7740\u53c8\u6068\u58f0\u8bf4\u9053\uff1a\u201c\u8c01\u77e5\u7adf\u78b0\u7740\u90a3\u4e09\u4e2a\u8001\u9b3c\uff0c\u8fd8\u6709\u4ed6\u4eec\u90a3\u8ba8\u538c\u7684\u5f92\u5f1f\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u70b9\u70b9\u5934\u9053\uff1a\u201c\u4e0d\u9519\uff01\u90a3\u4e09\u4eba\u5f92\u5f1f\u53eb\u2018\u91d1\u9c81\u5384\u2019\uff0c\u4ed6\u5bf9\u4f60\u600e\u6837\uff1f\u201d \u3000\u3000\u65b9\u5c11\u78a7\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u54fc\u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u4e0d\u662f\u597d\u4e1c\u897f\uff0c\u5982\u679c\u843d\u5728\u6211\u624b\u4e0a\u975e\u5c06\u4ed6\u788e\u5c38\u4e07 \u6bb5\uff01\u201d \u3000\u3000\u8f9b\u6377\u5df2\u731c\u51fa\u7aef\u502a\uff0c\u7b11\u9053\uff1a\u201c\u8c01\u53eb\u4f60\u957f\u5f97\u8fd9\u4e48\u6f02\u4eae\u5462\uff01\u201d \u3000\u3000\u6b64\u65f6\u4e24\u4eba\u5df2\u56de\u590d\u4ee5\u524d\u822c\u4eb2\u871c\u548c\u6c14\uff0c\u5f53\u7136\u4eb2\u871c\u7684\u6709\u4e9b\u8ddd\u79bb\uff0c\u65b9\u5c11\u78a7\u88ab\u566a\u5f97\u201c\u5550\u201d\u4e00\u58f0\uff0c\u54fc \u9053\uff1a\u201c\u8fd9\u5bb6\u4f19\u662f\u86e4\u6478\u60f3\u2014\u2014\u201d \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u7a81\u7136\u5e8a\u4e0a\u7684\u91d1\u6b39\u54fc\u4e86\u4e24\u58f0\uff0c\u9053\uff1a\u201c\u78a7\u59b9\uff01\u78a7\u59b9\uff01\u6c34\uff01\u6c34\uff01\u201d \u3000\u3000\u8f9b\u6377\u4e0e\u65b9\u5c11\u78a7\u84e6\u5730\u60ca\u9192\uff0c\u8f9b\u6377\u53d6\u7b11\u9053\uff1a\u201c\u4f60\u770b\uff01\u96c4\u5929\u9e45\u9192\u4e86\u5462\uff01\u201d \u3000\u3000\u65b9\u5c11\u78a7\u542b\u7f9e\u5730\u4e00\u7b11\uff0c\u7b11\u5bb9\u591a\u5c11\u542b\u70b9\u4f24\u611f\u7684\u610f\u5473\uff0c\u53ea\u89c1\u5979\u8fde\u5fd9\u5954\u8fc7\u53bb\uff0c\u53e3\u4e2d\u8fd8\u7ee7\u7eed\u9053\uff1a \u201c\u4f60\u77a7\uff01\u8fd9\u5c31\u662f\u90a3\u6700\u9ad8\u5927\u7684\u8001\u5934\u5b50\u6253\u4f24\u7684\uff01\u201d \u3000\u3000\u201c\u554a\uff01\u4f60\u8bf4\u7684\u2018\u4f2f\u7f57\u5404\u7b54\u2019\uff0c\u54fc\uff01\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u7adf\u662f\u8fd9\u6837\u7684\u5c0f\u4eba\uff01\u201d\u8f9b\u6377\u5e94\u9053\u3002 \u3000\u3000\u91d1\u6b39\u53c8\u8fde\u8fde\u53eb\u7740\u8981\u6c34\uff0c\u5f85\u65b9\u5c11\u78a7\u704c\u4e86\u5c11\u8bb8\u6c34\u4e0b\u53bb\uff0c\u4ed6\u53c8\u6726\u80e7\u7761\u53bb\u2014\u2014 \u3000\u3000\u201c\u554a\uff01 \u3000\u3000\u7a81\u7136\u8f9b\u6377\u8f7b\u547c\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u78a7\u59b9\uff0c\u4f60\u542c\u811a\u6b65\u58f0\uff01\u662f\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u7b49\u6765\u4e86\uff01\u201d \u3000\u3000\u65b9\u5c11\u78a7\u529f\u529b\u8f83\u8f9b\u6377\u6d45\u4e86\u8bb8\u591a\uff0c\u542c\u4e86\u4e00\u4f1a\u4ecd\u662f\u542c\u4e0d\u51fa\u4ec0\u4e48\uff0c\u4f46\u5979\u751a\u660e\u4e86\u6b64\u5730\u6c14\u5019\uff0c\u9053\uff1a \u201c\u5fc5\u5b9a\u662f\u96fe\u6563\uff0c\u5426\u5219\u867d\u7136\u7ad9\u7acb\u90a3\u5757\u5ca9\u77f3\u53ea\u8ddd\u6d77\u5cb8\u4e0d\u8db3\u516b\u4e08\uff0c\u4ed6\u4eec\u4ecd\u662f\u4e0d\u4f1a\u8df3\u8fc7\u6765\u7684\u3002\u201d \u3000\u3000\u8f9b\u6377\u8ddf\u968f\u5728\u65b9\u5c11\u78a7\u8eab\u540e\u5954\u8dd1\u65f6\uff0c\u6b63\u503c\u5927\u96fe\u6700\u6d53\uff0c\u5f53\u7136\u5bf9\u9644\u8fd1\u5730\u52bf\u4e00\u4e9b\u4e5f\u4e0d\u660e\u4e86\uff0c\u6240\u4ee5\u4ed6 \u95ee\u65b9\u5c11\u78a7\u9053\uff1a\u201c\u4f60\u8fd9\u5ca9\u6d1e\u5730\u52bf\u5982\u4f55\uff0c\u662f\u5426\u5f88\u5bb9\u6613\u88ab\u53d1\u73b0\uff1f\u201d \u3000\u3000\u65b9\u5c11\u78a7\u6447\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4eec\u521a\u627e\u6b64\u6d1e\u65f6\u5012\u82b1\u4e86\u4e0d\u5c11\u5fc3\u529b\uff0c\u4f46\u7ecf\u8fc7\u5c45\u4f4f\u8fd9\u4e48\u4e45\u56db\u5904\u65e9\u7559\u4e86 \u75d5\u8ff9\uff0c\u50cf\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u8fd9\u79cd\u8001\u7ecf\u9a8c\uff0c\u6211\u60f3\u5f88\u5feb\u5c31\u4f1a\u88ab\u4ed6\u4eec\u5bfb\u6765\u3002\u201d\u65b9\u5c11\u78a7\u663e\u5f97\u6709\u4e9b\u4f18\u8651\u3002 \u3000\u3000\u8f9b\u6377\u9ed8\u9ed8\u6c89\u601d\u4e00\u4f1a\uff0c\u5fc3\u77e5\u5e26\u7740\u8d1f\u4f24\u7684\u91d1\u6b39\u5fc5\u662f\u9003\u4e0d\u8fc7\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u7684\u8ffd\u8e2a\uff0c\u53ea\u597d\u6697\u6697\u51b3 \u5b9a\u5bf9\u7b56\uff0c\u9053\uff1a\u201c\u78a7\u59b9\uff01\u968f\u6211\u6765\uff0c\u54b1\u4eec\u53ef\u5f97\u4e3a\u4ed6\u4eec\u51c6\u5907\u4e9b\u4e1c\u897f\uff0c\u514d\u5f97\u8fd9\u4e9b\u5937\u65cf\u7b11\u6211\u4e2d\u539f\u65e0 \u7269\u2026\u2026\u201d \u3000\u3000\u6b64\u65f6\u6d1e\u5916\u679c\u5982\u65b9\u5c11\u78a7\u6240\u8bf4\uff0c\u6d53\u96fe\u5df2\u6d88\u6563\u65e0\u8e2a\uff0c\u5d07\u9ad8\u8d77\u4f0f\u7684\u5c71\u5cad\uff0c\u5ce6\u53e0\u91cd\u5cf0\u751a\u662f\u96c4\u5947\uff0c\u8f9b \u6377\u4e0e\u65b9\u5c11\u78a7\u6b63\u5728\u6d1e\u5185\u5fd9\u788c\u5e03\u7f6e\u7740\u2014\u2014 \u3000\u3000\u84e6\u5730\u8fdc\u8fdc\u5c71\u5dc5\u4e0a\u7a81\u73b0\u51fa\u56db\u6761\u4eba\u5f71\uff0c\u8fd9\u5f53\u7136\u662f\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u4e0e\u201c\u91d1\u9c81\u5384\u201d\u4e86\u3002 \u3000\u3000\u539f\u6765\u91d1\u4f2f\u80dc\u5937\u7b49\u88ab\u65b9\u5c11\u78a7\u7565\u65bd\u5c0f\u8ba1\uff0c\u8239\u7834\u821f\u6c89\uff0c\u56db\u4eba\u53ea\u597d\u7acb\u5728\u90a3\u6bb5\u4ed6\u8239\u7684\u7901\u77f3\u4e0a\uff0c\u867d\u7136 \u8fd9\u7901\u77f3\u8ddd\u5cb8\u53ea\u4e0d\u8fc7\u516b\u4e08\uff0c\u4f46\u5728\u6d53\u96fe\u4e2d\u5982\u4f55\u77e5\u6653\uff1f \u3000\u3000\u76f4\u5f85\u96fe\u6563\uff0c\u56db\u4eba\u624d\u770b\u6e05\u5f62\u52bf\u8df3\u4e0a\u5cb8\u6765\uff0c\u5185\u4e2d\u5f53\u4ee5\u4f2f\u7f57\u5404\u7b54\u6068\u5f97\u6700\u7259\u75d2\uff0c\u7acb\u523b\u50ac\u7740\u5176\u4ed6\u4e09 \u4eba\u52a0\u7d27\u8ffd\u8e2a\uff0c\u975e\u8981\u5c06\u8f9b\u6377\u7f6e\u4e8e\u6b7b\u5730\u4e0d\u53ef\u2014\u2014 \u3000\u3000\u5f53\u7136\u4ed6\u4eec\u7acb\u523b\u53d1\u73b0\u65b9\u5c11\u78a7\u4e0e\u91d1\u6b39\u6240\u7a9c\u4e0b\u7684\u75d5\u8ff9\uff0c\u6240\u4ee5\u5f88\u5feb\u5730\u8ddf\u4e0b\u6765\uff0c\u5e76\u4e14\u8ddd\u8fd9\u6d1e\u4e5f\u4e0d\u8fdc \u4e86\u2014\u2014 \u3000\u3000\u201c\u5e08\u7236\uff01\u201d\u91d1\u9c81\u5384\u4e00\u8fb9\u5954\u8dd1\u4e00\u9762\u5411\u91d1\u4f2f\u80dc\u5937\u6c42\u60c5\uff1a\u201c\u7b49\u4e00\u4e0b\u6349\u7740\u90a3\u59d1\u5a18\uff0c\u8bf7\u5e08\u7236\u9976\u5979\u4e00 \u547d\u5427\uff01\u201d \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u51b7\u51b7\u5730\u70b9\u5934\uff0c\u867d\u7136\u4ed6\u5bf9\u91d1\u9c81\u5384\u6709\u6c42\u5fc5\u6709\u5e94\uff0c\u4f46\u4ecd\u4e0d\u5f97\u4e0d\u6446\u51fa\u4e9b\u5e08\u7236\u7684\u67b6\u5b50\uff0c\u5f53 \u7136\u91d1\u9c81\u5384\u4e5f\u660e\u767d\u8fd9\u70b9\u3002 \u3000\u3000\u56db\u4eba\u8d8a\u8dd1\u79bb\u6d1e\u53e3\u8d8a\u8fd1\uff0c\u7a81\u7136\u91d1\u4f2f\u80dc\u5937\u9996\u5148\u53d1\u73b0\u8f9b\u6377\u85cf\u8eab\u7684\u5730\u65b9\uff0c\u84e6\u5730\u6307\u7740\u6d1e\u53eb\u9053\uff1a \u3000\u3000\u201c\u6469\u8bd8\u62c9\u8bc3\uff0c\u5b55\u7f57\uff0c\u963f\u9686\u9ece\uff01\u201d \u3000\u3000\u8bed\u610f\u5927\u6982\u662f\u8bf4\u201c\u4ed6\u4eec\u5fc5\u5b9a\u5728\u8fd9\u513f\u201d\u5427\uff01 \u3000\u3000\u4f2f\u7f57\u5404\u7b54\u4e0e\u76d8\u706f\u5b5a\u5c14\u6b63\u8981\u62a2\u8eab\u8fdb\u53bb\uff0c\u7a81\u7136\u6d1e\u5185\u4f20\u51fa\u8f9b\u6377\u51b7\u51b7\u7684\u58f0\u97f3\u9053\uff1a\u201c\u86ee\u5937\u7684\u5c0a\u5ba2\u6b64 \u65f6\u624d\u5230\uff0c\u8f9b\u6377\u5df1\u5019\u591a\u65f6\u3002\u201d \u3000\u3000\u56db\u4eba\u4e2d\u53ea\u6709\u91d1\u9762\u80dc\u5937\u4e0e\u91d1\u9c81\u5384\u542c\u5f97\u61c2\u6c49\u8bed\uff0c\u4f2f\u7f57\u5404\u7b54\u53ea\u542c\u51fa\u662f\u8f9b\u6377\u7684\u58f0\u97f3\uff0c\u4e00\u626c\u624b\u5373\u8981 \u62a2\u653b\u524d\u53bb\u2014\u2014 \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u867d\u662f\u7531\u201c\u5929\u7afa\u201d\u6765\u7684\uff0c\u4e5f\u660e\u767d\u4e2d\u539f\u6b66\u6797\u89c4\u77e9\uff0c\u5982\u4ee5\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u4e4b\u540d\uff0c\u6b3a\u538b\u4e00 \u4e2a\u540e\u751f\u5c0f\u8f88\uff0c\u4f20\u51fa\u53bb\u9762\u5b50\u603b\u4e0d\u597d\u770b\uff0c\u9664\u975e\u6709\u628a\u63e1\u5c06\u4ed6\u4eec\u4e09\u4eba\u90fd\u6bd9\u4e86\u2014\u2014 \u3000\u3000\u6240\u4ee5\u4ed6\u8fde\u5fd9\u5c06\u4f2f\u7f57\u5404\u7b54\u62e6\u4f4f\uff0c\u7136\u540e\u5bf9\u6d1e\u5185\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u597d\u5c0f\u5b50\uff01\u6709\u79cd\u7684\u7ed9\u8001\u5b50\u6eda\u51fa \u6765\uff01\u201d \u3000\u3000\u8f9b\u6377\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u4e00\u4e2a\u86ee\u5b50\uff0c\u539f\u6765\u4f60\u5230\u4e2d\u56fd\u5c31\u53ea\u5b66\u4f1a\u8fd9\u51e0\u53e5\u9a82\u4eba\u7684\u8bdd\uff01\u201d \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u4e00\u542c\u8f9b\u6377\u8fd9\u4e0d\u6b63\u662f\u660e\u660e\u77a7\u4e0d\u8d77\u81ea\u5df1\uff0c\u4f46\u654c\u6697\u6211\u660e\uff0c\u9664\u975e\u5c06\u4ed6\u4eec\u4e00\u5e76\u8bf1\u51fa\uff0c\u5426\u5219 \u5192\u5931\u8fdb\u53bb\u5403\u4ed6\u4eec\u8d70\u8131\u4e00\u4e2a\uff0c\u4e8b\u5173\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u9762\u5b50\u4e8b\u5927\u3002 \u3000\u3000\u91d1\u9c81\u5384\u5728\u65c1\u501a\u4ed7\u5e08\u5a01\uff0c\u52a0\u4e0a\u6709\u4ed6\u6c49\u8bed\u6d41\u5229\uff0c\u6240\u4ee5\u53eb\u9053\uff1a\u201c\u59d3\u8f9b\u7684\u51fa\u6765\uff0c\u54b1\u4eec\u518d\u6218\u4e09\u767e\u56de \u5408\u3002\u201d \u3000\u3000\u8f9b\u6377\u9690\u8eab\u6d1e\u5185\uff0c\u4ecd\u51b7\u51b7\u8bf4\u9053\uff1a\u201c\u8981\u6211\u51fa\u6765\u4e0d\u96be\uff0c\u4e0d\u8fc7\u4f60\u4eec\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u8bf4\u8bdd\u7b97\u4e0d\u7b97 \u6570\uff1f\u201d \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u4e0d\u77e5\u8f9b\u6377\u4e3a\u4f55\u4f1a\u51fa\u6b64\u8a00\uff0c\u8c28\u614e\u7b54\u9053\uff1a\u201c\u54b1\u4eec\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u5411\u6765\u8bf4\u4e00\u662f\u4e00\uff0c\u8bf4\u4e8c \u662f\u4e8c\uff0c\u5c0f\u5b50\u8981\u5f04\u4ec0\u4e48\u82b1\u6837\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7b54\uff0c\u7ee7\u7eed\u95ee\u9053\uff1a\u201c\u91d1\u9c81\u5384\uff0c\u4f60\u5462\uff1f\u201d \u3000\u3000\u91d1\u9c81\u5384\u4e00\u6014\uff0c\u8131\u53e3\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e5f\u4e00\u6837\uff01\u201d \u3000\u3000\u8f9b\u6377\u51b7\u54fc\u4e00\u58f0\u9053\uff1a\u201c\u597d\uff01\u8bf4\u5f97\u51a0\u5195\u5802\u7687\uff0c\u5982\u679c\u4f60\u4eec\u88ab\u6211\u8f9b\u67d0\u6307\u51fa\u5931\u4fe1\u7684\u5730\u65b9\uff0c\u4f60\u4eec\u53ef\u5f97 \u542c\u6211\u8f9b\u67d0\u4e00\u53e5\u8bdd\uff01\u201d \u3000\u3000\u91d1\u9c81\u5384\u5df2\u89c9\u51fa\u8f9b\u6377\u5fc5\u662f\u6301\u7740\u4ec0\u4e48\u8ba1\u7b56\uff0c\u6b63\u8981\u8b66\u544a\u5e08\u7236\uff0c\u91d1\u4f2f\u80dc\u5937\u5df2\u8131\u51fa\u53e3\u9053\uff1a\u201c\u54fc\uff01\u5047 \u5982\u771f\u4e2a\u5982\u6b64\uff0c\u83ab\u8bf4\u4e00\u53e5\uff0c\u54b1\u4eec\u5341\u53e5\u4e5f\u542c\u3002\u201d\u4ed6\u81ea\u4ee5\u4e3a\u8fd9\u201c\u5341\u53e5\u201d\u7528\u5f97\u5f88\u597d\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u770b\u4e09\u4f5b\u679c\u7136\u5165\u5708\u5957\uff0c\u4e0d\u7981\u5f97\u610f\u5730\u5927\u7b11\u8d77\u6765\uff0c\u9053\uff1a\u201c\u771f\u4e0d\u6127\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u4e4b\u540d\uff0c\u91d1 \u9c81\u5384\uff01\u4f60\u81ea\u5df1\u8bf4\uff0c\u4f60\u5728\u2018\u6cf0\u594e\u5c71\u65e0\u4e3a\u5385\u2019\u5bf9\u6211\u8bb8\u4e86\u4ec0\u4e48\u8bdd\uff1f\u54c8\uff01\u54c8\uff01\u201d \u3000\u3000\u91d1\u9c81\u5384\u4e00\u6014\uff0c\u5450\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u54e6\uff01\u201d\u7a81\u7136\u4ed6\u8bb0\u8d77\u539f\u6765\u4ed6\u66fe\u7b54\u5e94\u8f9b\u6377\uff0c\u5982\u679c\u8d25\u7ed9\u8f9b \u6377\u7684\u8bdd\uff0c\u5c06\u4e0d\u518d\u5165\u4e2d\u539f\u2014\u2014 \u3000\u3000\u8f9b\u6377\u77e5\u9053\u8fd9\u6279\u5929\u7afa\u602a\u5ba2\uff0c\u4ff1\u662f\u4e0d\u592a\u5b88\u4fe1\u7684\uff0c\u53ea\u597d\u8981\u5229\u7528\u4ed6\u4eec\u987e\u5168\u9762\u5b50\u7684\u5173\u7cfb\u6765\u8bd3\u4ed6\u4eec\uff0c \u4e8e\u662f\u63a5\u7740\u9053\uff1a\u201c\u73b0\u5728\u4f60\u4eec\u5f97\u542c\u6211\u4e00\u8a00\uff0c\u54b1\u4eec\u4e2d\u56fd\u6b66\u6280\u4e0a\u867d\u80dc\u4e0d\u4e86\u4f60\u4eec\u86ee\u5b50\u8bb8\u591a\uff0c\u4f46\u2018\u5f52\u5143\u53e4 \u9635\u2019\u4f60\u4eec\u603b\u62dc\u9886\u8fc7\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u6545\u610f\u5728\u8a00\u8f9e\u4e0a\u5c06\u4ed6\u4eec\u6298\u635f\u4e00\u756a\uff0c\u9053\uff1a\u201c\u6211\u8f9b\u67d0\u867d\u7136\u6b66\u827a\u6ca1\u5b66\u597d\uff0c\u4f46\u5e08\u7236\u8fd8\u6559\u4e86\u6211\u4e00 \u4e9b\u9635\u6cd5\uff0c\u8db3\u53ef\u800d\u800d\u4f60\u4eec\u3002\u73b0\u5728\u6211\u5750\u5728\u6d1e\u7a74\u5f53\u4e2d\uff0c\u4efb\u4f60\u4eec\u9009\u4e00\u4eba\uff0c\u53ea\u8981\u4e0d\u6bc1\u53bb\u6216\u63a8\u5012\u4efb\u4f55\u4e1c\u897f \u800c\u80fd\u6478\u7740\u6211\uff0c\u54b1\u4eec\u4e09\u4eba\u5373\u4efb\u51ed\u5904\u7f6e\u2026\u2026\u201d \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u4e0d\u7981\u72b9\u7591\u4e0d\u51b3\uff0c\u201c\u5f52\u5143\u53e4\u9635\u201d\u4ed6\u4eec\u662f\u9886\u6559\u8fc7\u4e86\uff0c\u8f9b\u5c0f\u5b50\u7684\u2018\u9635\u2019\u867d\u7136\u4e0d\u4f1a\u5f3a\u8fc7 \u5b83\uff0c\u4f46\u5374\u6709\u6761\u4ef6\u4e0d\u8bb8\u6467\u6bc1\u4efb\u4f55\u4e1c\u897f\uff0c\u800c\u81ea\u5df1\u51ed\u7740\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u7684\u540d\u5934\uff0c\u52bf\u5fc5\u4e0d\u80fd\u5728\u8fd9\u5c0f\u5b50\u9762 \u524d\u4f4e\u5934\u3002 \u3000\u3000\u4e14\u4e0d\u8bf4\u91d1\u4f2f\u80dc\u5937\u5728\u90a3\u4e3e\u68cb\u4e0d\u5b9a\uff0c\u91d1\u9c81\u5384\u6709\u89c1\u8f9b\u6377\u63ed\u4ed6\u75ae\u75a4\u65e9\u5df2\u6124\u6012\uff0c\u4e0d\u5f85\u5e08\u7236\u51b3\u5b9a\uff0c\u7a81 \u7136\u547c\u9053\uff1a\u201c\u5e08\u7236\u8ba9\u6211\u5c06\u8fd9\u5c0f\u5b50\u6293\u51fa\u6765\uff0c\u8c05\u4ed6\u6709\u591a\u5927\u80fd\u8010\u56f0\u4f4f\u6211\uff1f\u201d\u8bf4\u7740\u5373\u5411\u6d1e\u5185\u6b65\u8fdb\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u4e09\u4eba\u8f83\u8f9b\u6377\u7b97\u6765\u9ad8\u4e86\u4e00\u8f88\uff0c\u4e0d\u597d\u610f\u601d\u4eb2\u81ea\u51fa\u9a6c\uff0c\u53ea\u597d\u8ba9\u91d1\u9c81\u5384\u53bb\u5c1d\u8bd5\u4e86\u2014\u2014 \u3000\u3000\u4e14\u8bf4\u91d1\u9c81\u5384\u4e00\u6b65\u5165\u6d1e\u5185\uff0c\u53ea\u89c1\u6d1e\u4e2d\u77f3\u5806\u6797\u7acb\u2014\u2014\u6b63\u662f\u8f9b\u6377\u4e0e\u65b9\u5c11\u78a7\u7684\u6210\u679c\u2014\u2014\u800c\u8f9b\u6377\u58f0 \u97f3\u6b63\u4ece\u5f53\u4e2d\u4f20\u51fa\u3002 \u3000\u3000\u8981\u77e5\u8f9b\u6377\u53d7\u201c\u4e03\u5999\u795e\u541b\u201d\u6559\u5bfc\uff0c\u795e\u541b\u9664\u4e86\u201c\u8272\u201d\u4e00\u5999\u672a\u6388\u4ed6\u5916\uff0c\u5176\u4f59\u8f9b\u6377\u4ff1\u5df2\u6709\u9752\u51fa\u4e8e \u84dd\u4e4b\u52bf\uff0c\u201c\u5f52\u5143\u53e4\u9635\u201d\u8fd9\u4e48\u96be\u7684\u9635\u6cd5\u4ed6\u90fd\u5927\u90e8\u61c2\u5f97\uff0c\u968f\u4fbf\u6446\u4e2a\u9635\u6cd5\u5f53\u4e0d\u6210\u95ee\u9898\u3002 \u3000\u3000\u5c31\u8fd9\u6837\u91d1\u9c81\u5384\u5728\u9635\u4e2d\u8f6c\u4e86\u6570\u5468\uff0c\u56e0\u4e0d\u80fd\u6467\u6bc1\u4efb\u4f55\u4e1c\u897f\uff0c\u6240\u4ee5\u4e0d\u4e00\u4f1a\u513f\u5373\u8f6c\u5165\u6b67\u9053\u2014\u2014 \u3000\u3000\u524d\u9762\u66fe\u63d0\u8fc7\u6b64\u5c71\u6d1e\u7a74\u5f84\u591a\u800c\u590d\u6742\u4ea4\u9519\uff0c\u5982\u8d70\u9519\u8def\u9014\u975e\u53eb\u4f60\u7ed5\u4e2a\u5341\u5929\u534a\u6708\u4e0d\u80fd\u51fa\u6765\uff0c\u91d1\u9c81 \u5384\u88ab\u8f9b\u6377\u7565\u4f7f\u624b\u6cd5\uff0c\u5373\u8d70\u5165\u5c94\u9014\u3002 \u3000\u3000\u8f9b\u6377\u6545\u610f\u5728\u9635\u4e2d\u51b7\u7b11\u7740\u3002\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u7b49\u4e86\u4e8c\u4e2a\u65f6\u8fb0\u4e0d\u89c1\u91d1\u9c81\u5384\u51fa\u6765\uff0c\u65e9\u6025\u5f97\u66b4\u8df3\u5982 \u96f7\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u65f6\u673a\u6210\u719f\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u4e09\u4e2a\u8001\u7cca\u6d82\uff0c\u4f60\u4eec\u7684\u4e56\u5f92\u513f\u522b\u60f3\u51fa\u6765\u4e86\uff1f\u201d \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u6240\u6709\u5f1f\u5b50\u4e2d\uff0c\u6700\u5ba0\u53d7\u8fd9\u6700\u5e7c\u53c8\u6700\u806a\u660e\u7684\u91d1\u9c81\u5384\uff0c\u770b\u4ed6\u8fdb\u53bb\u5982\u6b64\u4e45\u8fd8\u672a\u51fa\u6765\uff0c\u4ee5 \u4e3a\u906d\u4e86\u4e0d\u6d4b\uff0c\u6025\u5f97\u5927\u60ca\u9053\uff1a\u201c\u59d3\u8f9b\u7684\u5c0f\u5b50\u6eda\u51fa\u6765\uff01\u6211\u7684\u91d1\u9c81\u5384\u4f24\u4e86\u4e00\u6839\u6c57\u6bdb\u770b\u6211\u91d1\u4f2f\u80dc\u5937\u4e00 \u638c\u8981\u4f60\u7684\u547d\uff01\u201d \u3000\u3000\u8f9b\u6377\u542c\u540e\u5927\u6012\uff0c\u84e6\u5730\u4ece\u6d1e\u5185\u98de\u51fa\uff0c\u843d\u5728\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u4e4b\u524d\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u597d\u72c2\u5984\u7684\u53e3\u6c14\uff0c \u6211\u8f9b\u67d0\u4e0d\u624d\uff0c\u5c1a\u8fd8\u4e0d\u5728\u4e4e\u5927\u5e08\u4e00\u638c\u5462\uff01\u201d \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u4e5f\u662f\u6025\u6012\u653b\u5fc3\uff0c\u547c\u9053\uff1a\u201c\u6211\u4e00\u638c\u6bd9\u4e0d\u4e86\u4f60\uff0c\u54b1\u4eec\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u6709\u4f60\u5728\u4e00\u5929\uff0c\u51b3 \u4e0d\u518d\u91cd\u5c65\u4e2d\u539f\u3002\u201d \u3000\u3000\u8f9b\u6377\u54c8\u54c8\u72c2\u7b11\uff0c\u9053\uff1a\u201c\u6b64\u8bdd\u5f53\u771f\uff1f\u201d \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u6c14\u5f97\u7528\u529b\u70b9\u70b9\u5934\u2014\u2014 \u3000\u3000\u8f9b\u6377\u7a7a\u5411\u6d1e\u5185\u5927\u558a\u9053\uff1a\u201c\u78a7\u59b9\uff01\u5c06\u90a3\u4eba\u5e26\u51fa\u6765\uff01\u201d \u3000\u3000\u679c\u7136\u4e0d\u4e00\u523b\u91d1\u9c81\u5384\u968f\u7740\u65b9\u5c11\u78a7\u6b65\u51fa\uff0c\u5927\u6982\u8d70\u4e86\u4e0d\u5c11\u51a4\u6789\u8def\uff0c\u6ee1\u9762\u6124\u6012\u7684\u795e\u8272\u2014\u2014 \u3000\u3000\u201c\u5927\u5e08\u8bf7\u51c6\u5907\u5427\uff01\u5982\u679c\u4e00\u638c\u51fb\u4e0d\u5012\u5728\u4e0b\uff0c\u53ef\u5c31\u5f97\u8bf7\u524d\u8f88\u56de\u8f6c\u5929\u7afa\uff0c\u6c38\u4e0d\u518d\u8e0f\u5165\u54b1\u4eec\u4e2d \u56fd\u3002\u201d \u3000\u3000\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u3001\u91d1\u9c81\u5384\u4ff1\u864e\u89c6\u7740\u8f9b\u6377\uff0c\u65b9\u5c11\u78a7\u5728\u65c1\u4e5f\u66ff\u4ed6\u7d27\u5f20\uff0c\u7a81\u7136\u8f9b\u6377\u8f6c\u8eab\u5411\u65b9\u5c11\u78a7 \u8bf4\u9053\uff1a\u201c\u78a7\u59b9\uff01\u5feb\u5feb\u8d81\u673a\u5e26\u91d1\u6b39\u9003\u5427\uff01\u518d\u4e0d\u8d70\u5f53\u5fc3\u4ed6\u4eec\u51fa\u5c14\u53cd\u5c14\u5c31\u6765\u4e0d\u53ca\u4e86\uff01\u201d \u3000\u3000\u65b9\u5c11\u78a7\u4ece\u8f9b\u6377\u53e3\u6c14\u4e2d\u3001\u76ee\u5149\u4e2d\u5f97\u5230\u4e86\u5979\u6e34\u671b\u800c\u6ca1\u6709\u5f97\u5230\u8fc7\u7684\u67d4\u60c5\uff0c\u4e3a\u4e86\u8f9b\u6377\u5979\u5e94\u8be5\u7559 \u4e0b\uff0c\u4e3a\u4e86\u91d1\u6b39\u5979\u5e94\u5f53\u9003\u8d70\uff0c\u5979\u8981\u4f5c\u4f55\u53d6\u820d\u5462\uff1f \u3000\u3000\u8f9b\u6377\u6b64\u65f6\u62b1\u7740\u4e0d\u53ea\u4e3a\u4e86\u65b9\u5c11\u78a7\uff0c\u66f4\u4e3a\u7740\u4e2d\u539f\u6b66\u6797\u800c\u727a\u7272\u7684\u7cbe\u795e\uff0c\u9762\u4e0a\u663e\u51fa\u51db\u7136\u4e0d\u60e7\u7684\u5a01 \u6b66\uff0c\u4f46\u5f53\u4ed6\u770b\u89c1\u65b9\u5c11\u78a7\u5a07\u5c0f\u65e0\u52a9\u7684\u795e\u60c5\uff0c\u4e0d\u7981\u8f6f\u5316\u4e86\uff0c\u53ea\u597d\u67d4\u58f0\u9053\uff1a \u3000\u3000\u201c\u78a7\u59b9\uff01\u5feb\u8d70\u5427\uff01\u522b\u4ee4\u6211\u6709\u7275\u6302\uff01\u8fd9\u86ee\u5b50\u7684\u4e00\u638c\u6211\u8fd8\u53d7\u5f97\u4e86\uff0c\u53ea\u6050\u4ed6\u4e0d\u5b88\u4fe1\uff0c\u5219\u4f60\u4eec\u8981 \u9003\u4e5f\u6765\u4e0d\u53ca\u4e86\uff01\u201d \u3000\u3000\u65b9\u5c11\u78a7\u832b\u7136\u70b9\u70b9\u5934\uff0c\u773c\u7736\u4e2d\u5145\u6ee1\u6cea\u6c34\uff0c\u7f13\u7f13\u6b65\u5165\u6d1e\u5185\uff0c\u867d\u7136\u5979\u6781\u4e0d\u613f\u610f\uff0c\u4f46\u4e5f\u4e0d\u5f97\u4e0d\u5e26 \u7740\u5c1a\u672a\u5b8c\u5168\u6e05\u9192\u7684\u91d1\u6b39\u8d70\u4e86\uff0c\u5f53\u7136\u8fd9\u4e0d\u5168\u662f\u56e0\u4e3a\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u7684\u539f\u56e0\u8f9b\u6377\u5f85\u65b9\u5c11\u78a7\u53bb\u540e\uff0c\u795e \u60c5\u4e3a\u4e4b\u4e00\u677e\uff0c\u957f\u5401\u4e00\u53e3\u6c14\u9759\u9759\u7acb\u5728\u91d1\u4f2f\u80dc\u5937\u524d\u2014\u2014 \u3000\u3000\u6e10\u6e10\u91d1\u4f2f\u80dc\u5937\u7684\u624b\u626c\u8d77\u4e86\uff0c\u957f\u957f\u7684\u9ec4\u6bdb\u56e0\u529f\u529b\u8fd0\u884c\uff0c\u7adf\u65e0\u98ce\u81ea\u52a8\uff0c\u53ea\u89c1\u4ed6\u4e24\u773c\u7262\u6ce8\u7740\u8f9b \u6377\uff0c\u4f7f\u5f97\u8f9b\u6377\u4efb\u4f55\u4e00\u4e2a\u52a8\u4f5c\u4e5f\u9003\u4e0d\u8fc7\u4ed6\u773c\u775b\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5c06\u5e73\u751f\u529f\u529b\u65e9\u5df2\u8fd0\u96c6\u5728\u53cc\u638c\uff0c\u6b64\u65f6\u4ed6\u5fc3\u4e2d\u4ec0\u4e48\u4e5f\u6ca1\u6709\u60f3\uff0c\u552f\u4e00\u7684\u5ff5\u5934\u53ea\u662f\u8981\u82e6\u6491\u8fd9 \u4e00\u638c\u2014\u2014 \u3000\u3000\u84e6\u7136\u91d1\u4f2f\u80dc\u5937\u201c\u563f\u201d\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u524d\u4e00\u540e\u5939\u7740\u98ce\u96f7\u4e4b\u58f0\u6392\u5c71\u5012\u6d77\u822c\u5939\u51fb\u8fc7\u6765\uff0c\u52b2\u529b\u7684\u96c4 \u539a\u8db3\u53ef\u5f00\u5c71\u88c2\u77f3\u2014\u2014 \u3000\u3000\u201c\u7830\uff01\u201d \u3000\u3000\u8f9b\u6377\u6beb\u4e0d\u8fdf\u7591\uff0c\u7adf\u5168\u529b\u8fce\u4e0a\u53bb\uff0c\u7acb\u523b\u6f2b\u5929\u9ec4\u6c99\u5f25\u6f2b\uff0c\u518d\u4e5f\u770b\u4e0d\u89c1\u4ec0\u4e48\u2014\u2014 \u3000\u3000\u6162\u6162\u9ec4\u6c99\u8dcc\u843d\u4e86\uff0c\u8f9b\u6377\uff0c\u91d1\u4f2f\u80dc\u5937\u90fd\u4ece\u8ff7\u7cca\u4e2d\u663e\u73b0\u51fa\u6765\uff0c\u8f9b\u6377\u8138\u8272\u53e4\u602a\u5730\u82cd\u767d\uff0c\u6447\u6447\u6643 \u6643\u5730\uff0c\u4f46\u662f\uff0c\u4ed6\u4e00\u6b65\u4e5f\u672a\u66fe\u79fb\u52a8\u3002 \u3000\u3000\u91d1\u4f2f\u80dc\u5937\u60ca\u5f02\u5730\u53f9\u606f\u4e00\u58f0\uff0c\u7a81\u7136\u4e00\u6325\u624b\uff0c\u7acb\u523b\u56db\u4eba\u5411\u6d77\u5cb8\u65b9\u5411\u98de\u9a70\u800c\u53bb\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5462\uff0c\u53ea\u89c1\u4ed6\u4e24\u624b\u4f4e\u5782\u7740\uff0c\u800c\u5341\u6307\u638c\u5fc3\u5374\u5fae\u5fae\u626c\u8d77\uff0c\u4f5c\u51fa\u4f3c\u6b32\u53cd\u51fb\u7684\u6a21\u6837\u2014\u2014 \u3000\u3000\u9ed1\u591c\u5df2\u964d\u4e34\uff0c\u5927\u5730\u4e0a\u56de\u590d\u5230\u539f\u59cb\u7684\u6c89\u9759\uff0c\u5929\u4e0a\u7b2c\u4e00\u9897\u661f\uff0c\u5c04\u51fa\u5b83\u9eef\u6de1\u7684\u5149\u660e\u2014\u2014 \u3000\u3000\u7a81\u7136\u8fdc\u8fdc\u4f20\u6765\u4e00\u9635\u6c89\u91cd\u7684\u811a\u6b65\u58f0\uff0c\u4f7f\u8f9b\u6377\u62fc\u9f13\u7740\u4f59\u529b\uff0c\u84e6\u5730\u632f\u4f5c\uff0c\u6726\u80e7\u5c71\u52bf\u4e2d\u4ec0\u4e48\u4e5f\u770b \u4e0d\u89c1\uff0c\u8f9b\u6377\u4e00\u53e3\u6cc4\u4e86\u7684\u771f\u6c14\u53c8\u52c9\u5f3a\u63d0\u4e86\u8d77\u6765\uff0c\u6697\u5fd6\u9053\uff1a\u201c\u4ec0\u4e48\u4e1c\u897f\uff1f\u662f\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\uff1f\u8fd8\u662f \u5784\u59b9\u56de\u8f6c\u6765\uff1f\u201d \u3000\u3000\u84e6\u5730\uff0c\u5c71\u56de\u5904\u8f6c\u51fa\u53ea\u7855\u5927\u5c71\u718a\uff0c\u5b83\u6f20\u7136\u5730\u77a5\u4e86\u8f9b\u6377\u4e00\u773c\uff0c\u5fae\u5fae\u5f20\u4e86\u5f20\u5927\u9f3b\u5b54\uff0c\u55c5\u4e86\u4e24 \u55c5\uff0c\u53c8\u6389\u5934\u53bb\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u987f\u65f6\u653e\u677e\uff0c\u4ed6\u81ea\u5632\u81ea\u5df1\u7684\u591a\u7591\uff0c\u4f46\u662f\u4ed6\u53d7\u91d1\u4f2f\u80dc\u5937\u7684\u90a3\u4e00\u638c\u5b9e\u5728\u592a\u91cd\u4e86\uff0c\u7ecf\u8fc7 \u8fd9\u4e00\u9635\u62fc\u529b\u632f\u4f5c\uff0c\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u54c7\u54c7\u4e00\u8fde\u5410\u51fa\u4e09\u53e3\u9c9c\u8840\uff0c\u201c\u5657\u201d\u5730\u8dcc\u5012\u4e0b\u53bb\u2014\u2014 \u3000\u3000\u6708\u5149\u4e4b\u4e0b\uff0c\u4e07\u661f\u9f50\u653e\uff0c\u8f9b\u6377\u9759\u8eba\u7740\uff0c\u8089\u4f53\u7684\u75db\u82e6\u5374\u8fdc\u4e0d\u53ca\u4ed6\u7cbe\u795e\u4e0a\u7684\u6109\u5feb\u2014\u2014\u6bd5\u7adf\uff0c\u4ed6 \u5b8c\u6210\u4e86\u4ed6\u7684\u4f7f\u547d\u3002 \u3000\u3000\u79cb\u610f\u5df2\u6df1\uff0c\u5728\u6e05\u6668\u508d\u665a\uff0c\u4e00\u79cd\u8083\u6740\u7684\u6c14\u6c1b\uff0c\u6f2b\u626c\u5728\u5317\u56fd\u7684\u539f\u91ce\u4e0a\uff0c\u6768\u67f3\u67af\u4e86\uff0c\u71d5\u5b50\u5357 \u98de\uff0c\u5c0f\u6865\u4e0b\u7684\u6d41\u6c34\uff0c\u67af\u5bc2\u65e0\u529b\u7684\u5411\u4e1c\u6d41\u7740\u3002 \u3000\u3000\u5df2\u662f\u521d\u66f4\u7684\u65f6\u5206\uff0c\u9ad8\u6717\u7684\u5929\u7a7a\uff0c\u51fa\u73b0\u4e86\u758f\u758f\u51e0\u9897\u5c0f\u661f\uff0c\u6de1\u6de1\u7684\u95ea\u70c1\u7740\uff0c\u663e\u5f97\u5929\u8def\u662f\u90a3\u4e48 \u9065\u8fdc\uff0c\u65e0\u6daf\u2026\u2026 \u3000\u3000\u5728\u6d1b\u57ce\u90ca\u4e94\u516d\u5341\u91cc\u5916\u7684\u5c0f\u4e18\u4e0a\uff0c\u6709\u4e00\u5ea7\u7834\u65e7\u7684\u53e4\u5e99\uff0c\u7c41\u7c41\u7684\u5c71\u98ce\uff0c\u5439\u8fc7\u90a3\u8150\u673d\u7684\u7a97\u69db\uff0c \u53d1\u51fa\u4e00\u9635\u9635\u7684\u6447\u6643\u58f0\uff0c\u4ee4\u4eba\u611f\u5230\u51c4\u51c9\u60b2\u6006\u3002 \u3000\u3000\u5b64\u706f\u4e0b\uff0c\u76d8\u5750\u7740\u4e00\u4e2a\u9ad8\u5927\u9ed1\u9762\u6c49\u5b50\uff0c\u5728\u4ed6\u5bf9\u9762\u5750\u7740\u4e00\u4e2a\u7a1a\u6c14\u6ee1\u8138\u7684\u5c11\u5e74\u2014\u2014\u4ed6\u867d\u7136\u957f\u5f97 \u751a\u662f\u4fee\u957f\uff0c\u53ef\u662f\u770b\u8d77\u6765\u53ea\u4e0d\u8fc7\u662f\u5341\u4e8c\u3001\u4e09\u5c81\u7684\u6a21\u6837\u3002 \u3000\u3000\u90a3\u9ed1\u8138\u6c49\u5b50\u5ffd\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u54b1\u4eec\u4e10\u5e2e\u5e2e\u4e3b\u65e2\u7136\u4f20\u4f60\u5927\u4f4d\uff0c\u7edf\u7387\u5929\u4e0b\u7fa4\u4e10\uff0c\u90a3\u9547\u5e2e\u4e4b\u5b9d \u2018\u767e\u7ed3\u638c\u6cd5\u2019\u5fc5\u5b9a\u4f20\u7ed9\u4f60\u4e86\u3002\u201d \u3000\u3000\u9e4f\u513f\u70b9\u70b9\u5934\u9053\uff1a\u201c\u90a3\u5929\u5e08\u7236\u4f20\u6211\u638c\u6cd5\u65f6\uff0c\u5df2\u662f\u8eab\u53d7\u91cd\u4f24\uff0c\u4ed6\u5f3a\u81ea\u652f\u6301\u6559\u4e86\u6211\u4e00\u904d\uff0c\u4fbf\u5012 \u5730\u660f\u4e86\u8fc7\u53bb\uff0c\u5f85\u4ed6\u518d\u9192\u8fc7\u6765\uff0c\u5c31\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u672c\u5c0f\u518c\uff0c\u53eb\u6211\u7167\u7740\u518c\u4e0a\u6240\u8f7d\uff0c\u81ea\u5df1\u53bb\u7ec3\u3002\u91d1\u53d4 \u53d4\uff0c\u4f60\u8981\u4e0d\u8981\u770b\u770b\u3002\u201d\u8bf4\u7740\uff0c\u4ed6\u4ece\u8863\u895f\u4e2d\u6478\u51fa\u4e00\u672c\u5c0f\u4e66\uff0c\u9012\u7ed9\u9ed1\u8138\u6c49\u5b50\u3002 \u3000\u3000\u90a3\u9ed1\u6c49\u6447\u624b\u9053\uff1a\u201c\u8fd9\u767e\u7ed3\u638c\u6cd5\u662f\u4e10\u5e2e\u5386\u4ee3\u5e2e\u4e3b\u5355\u4f20\uff0c\u4e10\u5e2e\u5f1f\u5b50\uff0c\u4efb\u662f\u8c01\u4e5f\u4e0d\u51c6\u5077\u5b66\u3002\u201d \u3000\u3000\u9e4f\u513f\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u6211\u4eec\u73b0\u5728\u5148\u627e\u4e00\u4e2a\u5730\u65b9\u9690\u85cf\u8d77\u6765\uff0c\u597d\u597d\u628a\u6b66\u529f\u7ec3\u5f3a\uff0c\u518d\u53bb\u62a5\u4ec7\u597d \u4e48\uff1f\u201d \u3000\u3000\u91d1\u53d4\u53d4\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u6211\u6709\u4e00\u4ef6\u4e8b\uff0c\u4e00\u76f4\u60f3\u8ddf\u4f60\u8bf4\uff0c\u73b0\u5728\u4f60\u65e2\u7136\u60f3\u8981\u7ec3\u6b66\u62a5\u4ec7\uff0c\u6b63\u5408\u6211\u7684 \u8ba1\u5212\u3002\u201d \u3000\u3000\u9e4f\u513f\u9053\uff1a\u201c\u4ec0\u4e48\u8ba1\u5212\u3002\u201d \u3000\u3000\u91d1\u53d4\u53d4\u9053\uff1a\u201c\u54b1\u4eec\u4e10\u5e2e\uff0c\u76ee\u4e0b\u96f6\u661f\u56db\u6563\uff0c\u662f\u6b65\u6b65\u8870\u843d\u4e86\u3002\u53ef\u662f\u4e10\u5e2e\u5f1f\u5b50\u4e2d\uff0c\u5fe0\u4e49\u4e4b\u58eb\u5927 \u6709\u4eba\u5728\uff0c\u53ea\u8981\u4e00\u671d\u5e2e\u4e3b\u632f\u81c2\u4e00\u547c\uff0c\u91cd\u65b0\u6062\u590d\u4ece\u524d\u76db\u51b5\uff0c\u90a3\u4e5f\u662f\u4e0d\u592a\u96be\u7684\u3002\u201d \u3000\u3000\u9e4f\u513f\u542c\u91d1\u53d4\u53d4\u5ffd\u7136\u8c08\u8d77\u4e10\u5e2e\u7684\u524d\u9014\u6765\uff0c\u60f3\u5230\u81ea\u5df1\u8eab\u8d1f\u6551\u5e2e\u5927\u4efb\uff0c\u4e0d\u89c9\u8c6a\u6c14\u5e72\u4e91\uff0c\u4ed6\u5e74\u7eaa \u867d\u5c0f\uff0c\u5374\u662f\u6781\u6709\u5fd7\u6c14\uff0c\u7acb\u523b\u63a5\u53e3\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u4f60\u662f\u8981\u6211\u5c31\u53bb\u53f7\u53ec\u5929\u4e0b\u4e10\u5e2e\u5f1f\u5b50\uff0c\u91cd\u632f\u5e2e\u5a01 \u5417\uff1f\u201d \u3000\u3000\u91d1\u53d4\u53d4\u6447\u5934\u9053\uff1a\u201c\u73b0\u5728\u4f60\u5e74\u7eaa\u8fd9\u4e48\u5c0f\uff0c\u6b66\u529f\u53c8\u6ca1\u6709\u7ec3\u6210\uff0c\u8981\u60f3\u7edf\u7387\u8fd9\u5929\u4e0b\u7b2c\u4e00\u5927\u5e2e\uff0c\u90a3 \u662f\u4e07\u4e07\u4e0d\u80fd\u7684\uff0c\u6211\u7684\u610f\u601d\u662f\u5148\u628a\u4f60\u9001\u5230\u6211\u4e00\u4e2a\u597d\u670b\u53cb\u8fb9\u585e\u5927\u4fa0\u98ce\u4f2f\u6768\u5bb6\u91cc\u53bb\uff0c\u82e6\u7ec3\u51e0\u5e74\u6b66 \u529f\u3002\u201d \u3000\u3000\u9e4f\u513f\u6025\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u90a3\u4e48\u4f60\u5462\uff1f\u201d \u3000\u3000\u91d1\u53d4\u53d4\u9053\uff1a\u201c\u6211\u4eec\u4e10\u5e2e\u7684\u89c4\u77e9\uff0c\u8001\u5e2e\u4e3b\u4e00\u6b7b\uff0c\u4ed6\u6240\u8058\u7684\u62a4\u6cd5\uff0c\u4fbf\u7b97\u89e3\u9664\u804c\u52a1\u4e86\u3002\u6211\u548c\u8001 \u4e8c\uff0c\u81ea\u7136\u4e0d\u80fd\u4f8b\u5916\u3002\u201d \u3000\u3000\u9e4f\u513f\u53eb\u9053\uff1a\u201c\u91d1\u53d4\u53d4\u6211\u4e0d\u8981\u79bb\u5f00\u4f60\uff0c\u6211\u4e0d\u8981\u5230\u4ec0\u4e48\u8fb9\u585e\u5927\u4fa0\u5bb6\u53bb\uff0c\u4f60\u2026\u2026\u5c31\u4f60\u6559\u9e4f\u513f\u7684 \u6b66\u529f\u4e0d\u53ef\u4ee5\u5417\uff1f\u201d \u3000\u3000\u91d1\u53d4\u53d4\u8f7b\u67d4\u58f0\u9053\uff1a\u201c\u50bb\u5b69\u5b50\uff0c\u90a3\u98ce\u5927\u4fa0\u6b66\u529f\u9ad8\u6211\u5341\u500d\u4e0d\u6b62\uff0c\u4f60\u5230\u90a3\u513f\u53bb\uff0c\u6700\u591a\u4e94\u5e74\uff0c\u4e0d \u4f46\u8001\u5e2e\u4e3b\u4f20\u7684\u529f\u592b\u53ef\u4ee5\u7ec3\u6210\uff0c\u800c\u4e14\u98ce\u5927\u4fa0\u72ec\u7acb\u4e00\u6d3e\u7684\u5173\u5916\u6b66\u529f\u4e5f\u53ef\u4ee5\u5b66\u5f97\uff0c\u5c82\u4e0d\u80dc\u8fc7\u8ddf\u7740\u53d4 \u53d4\u5230\u5904\u6d41\u6d6a\u5417\uff1f\u201d \u3000\u3000\u9e4f\u51e0\u5929\u6027\u6781\u662f\u6df3\u539a\uff0c\u4ed6\u5b64\u82e6\u96f6\u4ec3\uff0c\u9664\u4e86\u91d1\u53d4\u53d4\u5144\u5f1f\u5916\uff0c\u4e16\u4e0a\u518d\u65e0\u4eb2\u4eba\u3002\u91d1\u53d4\u53d4\u5144\u5f1f\u5bf9\u4ed6 \u771f\u53ef\u8c13\u4e25\u7236\u517c\u6148\u6bcd\uff0c\u8bf8\u822c\u7231\u62a4\uff0c\u6b64\u65f6\u9661\u7136\u542c\u5230\u91d1\u53d4\u53d4\u8981\u79bb\u5f00\u81ea\u5df1\uff0c\u5fc3\u4e2d\u5927\u662f\u60f6\u6025\u60b2\u75db\uff0c\u5f3a\u5fcd \u7740\u773c\u6cea\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u9e4f\u513f\u4f5c\u9519\u4e86\u4ec0\u4e48\u4e8b\u5417\uff0c\u60a8\u2026\u2026\u60a8\u4e3a\u4ec0\u4e48\u4e0d\u518d\u7ba1\u9e4f\u513f\u4e86\u3002\u201d \u3000\u3000\u91d1\u53d4\u53d4\u5fc3\u5185\u4e5f\u81ea\u51c4\u7136\u4e0d\u820d\uff0c\u4f46\u4ed6\u4e3a\u987e\u9e4f\u513f\u524d\u9014\uff0c\u72e0\u4e0b\u5fc3\u6765\uff0c\u6b63\u60f3\u6b63\u8a00\u5f00\u5bfc\uff0c\u5ffd\u7136\u4e00\u58f0\u51c4 \u5389\u5578\u58f0\u4f20\u4e86\u8fdb\u6765\uff0c\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\u3002 \u3000\u3000\u91d1\u53d4\u53d4\u6025\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u8001\u4e8c\u9047\u7740\u5f3a\u654c\u4e86\uff0c\u4f60\u2026\u2026\u4f60\u8d76\u5feb\u5411\u4e1c\u9003\u8d70\uff0c\u8fd9\u91cc\u7684\u4e8b\uff0c\u7531\u6211\u6765\u6253 \u53d1\uff0c\u5982\u679c\u2026\u2026\u5982\u679c\uff0c\u6211\u91d1\u8001\u5927\u80fd\u4fa5\u5e78\u6d3b\u7740\uff0c\u6211\u81ea\u4f1a\u5230\u6d1b\u9633\u7b49\u4f60\uff0c\u9e4f\u513f\uff0c\u8bb0\u7740\uff0c\u5982\u679c\u7b49\u6211\u4eec\u4e09 \u5929\u4e0d\u6765\uff0c\u4f60\u4e00\u4e2a\u4eba\u5230\u8fbd\u4e1c\u9526\u5dde\u53bb\u627e\u98ce\u5927\u4fa0\uff0c\u5c31\u8bf4\u662f\u6211\u53eb\u4f60\u53bb\u7684\u3002\u201d \u3000\u3000\u9e4f\u513f\u89c1\u4ed6\u8bf4\u5f97\u65a9\u91d1\u622a\u94c1\uff0c\u5fc3\u4e2d\u867d\u7136\u4e0d\u613f\uff0c\u53ef\u662f\u4ed6\u77e5\u91d1\u53d4\u53d4\u813e\u6c14\uff0c\u5f53\u4e0b\u4e5f\u4e0d\u8fa9\u8bba\uff0c\u70b9\u4e86\u70b9 \u5934\u3002 \u3000\u3000\u91d1\u8001\u5927\u5ffd\u53c8\u67d4\u58f0\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u4f60\u4eca\u540e\u53ef\u8981\u66f4\u52a0\u5c0f\u5fc3\u4e86\uff0c\u4f60\u91d1\u53d4\u53d4\u4e5f\u8bb8\u2026\u2026\u4e5f\u8bb8\uff0c\u4e0d\u518d\u6709 \u673a\u4f1a\u6765\u4fdd\u62a4\u4f60\u5566\u3002\u201d \u3000\u3000\u9e4f\u513f\u8fd9\u534a\u5e74\u6765\u968f\u91d1\u6c0f\u5144\u5f1f\u4e5f\u4e0d\u77e5\u7ecf\u5386\u8fc7\u591a\u5c11\u5371\u96be\uff0c\u4f46\u4ece\u6ca1\u89c1\u91d1\u53d4\u53d4\u8138\u8272\u5982\u6b64\u6c89\u91cd\uff0c\u5fc3\u77e5 \u5fc5\u662f\u9047\u7740\u6781\u5f3a\u654c\u4eba\uff0c\u4ed6\u6015\u91d1\u4e8c\u53d4\u4e00\u4eba\u4e0d\u652f\uff0c\u53cd\u800c\u50ac\u4fc3\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u4f60\u8d76\u5feb\u53bb\u5e2e\u4e8c\u53d4\u53d4\u5427\uff01 \u9e4f\u513f\u5728\u6d1b\u9633\u7b49\u4f60\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u6ce8\u89c6\u4e86\u9e4f\u513f\u4e00\u4e0b\uff0c\u53ea\u89c1\u4ed6\u8138\u4e0a\u7231\u6148\u6a2a\u6ea2\uff0c\u7a1a\u6c14\u56e2\u56e2\uff0c\u957f\u53f9\u4e00\u58f0\uff0c\u98de\u6b65\u5954\u53bb\u3002 \u3000\u3000\u9e4f\u513f\u5446\u7acb\u4e86\u4e00\u4f1a\uff0c\u5bfb\u601d\u9053\uff1a\u201c\u6211\u6b64\u523b\u53bb\u5e2e\u53d4\u53d4\uff0c\u5fc5\u7136\u5206\u6563\u4ed6\u4eec\u7684\u5fc3\uff0c\u53cd\u800c\u6108\u5e2e\u6108\u5fd9\uff0c\u5012 \u4e0d\u5982\u4f9d\u53d4\u53d4\u7684\u8bdd\uff0c\u5148\u5230\u6d1b\u9633\u53bb\u3002\u201d \u3000\u3000\u4ed6\u62d6\u7740\u6c89\u91cd\u7684\u811a\u6b65\uff0c\u6162\u6162\u5730\u8d70\u5411\u4e1c\u53bb\u3002 \u3000\u3000\u4ed6\u5fc3\u4e0d\u5728\u7109\u7684\u8d70\u7740\uff0c\u5ffd\u7136\u4ed6\u89c9\u5f97\u540e\u9762\u4e00\u9635\u98ce\u58f0\uff0c\u4ed6\u56de\u5934\u4e00\u770b\uff0c\u4e00\u4e2a\u4fca\u79c0\u7684\u9752\u5e74\uff0c\u5bb4\u7136\u800c \u7acb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u5c0f\u5f1f\uff0c\u4f60\u8d70\u8def\u771f\u4e0d\u7559\u5fc3\u5440\uff0c\u5dee\u4e00\u70b9\u5c31\u649e\u7740\u6211\u3002\u201d \u3000\u3000\u9e4f\u513f\u5fc3\u60f3\uff1a\u201c\u4f60\u4e5f\u592a\u4e0d\u7559\u5fc3\uff0c\u6211\u8d70\u5728\u524d\u9762\uff0c\u600e\u7684\u770b\u4e0d\u89c1\u6211\u3002\u201d \u3000\u3000\u4f46\u89c1\u90a3\u5c11\u5e74\u751a\u662f\u4fca\u96c5\u53ef\u4eb2\uff0c\u4fbf\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u6b63\u5728\u60f3\u4e8b\uff0c\u6240\u4ee5\u4e0d\u77e5\u81ea\u5df1\u6b63\u5728\u8def\u4e2d\u95f4\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u539f\u4e5f\u662f\u6ee1\u8179\u5fc3\u4e8b\uff0c\u662f\u4ee5\u8fde\u9e4f\u513f\u90fd\u6ca1\u6709\u77a7\u89c1\uff0c\u5230\u4e86\u9e4f\u513f\u8eab\u540e\uff0c\u8fd9\u624d\u53d1\u89c9\uff0c\u7acb\u523b\u8fd0\u529f \u6b62\u4f4f\u8eab\u8eaf\u3002\u4ed6\u5f00\u53e3\u8d23\u95ee\u9e4f\u513f\uff0c\u539f\u662f\u672a\u52a0\u601d\u7d22\u4e4b\u4e3e\uff0c\u6b64\u65f6\u89c1\u5bf9\u65b9\u53cd\u800c\u8868\u793a\u6b49\u610f\uff0c\u5fc3\u91cc\u5f88\u662f\u60ed \u6127\u3002\u4fbf\u9053\uff1a\u201c\u5c0f\u5f1f\uff0c\u4f60\u6709\u4ec0\u4e48\u5fc3\u4e8b\uff0c\u544a\u8bc9\u6211\uff0c\u6211\u4e00\u5b9a\u66ff\u4f60\u60f3\u6cd5\u89e3\u51b3\u3002\u201d \u3000\u3000\u9e4f\u513f\u5fc3\u60f3\uff1a\u201c\u521a\u624d\u4ed6\u5230\u6211\u8eab\u65c1\uff0c\u6211\u624d\u53d1\u89c9\uff0c\u867d\u8bf4\u662f\u5fc3\u4e0d\u5728\u7109\uff0c\u4f46\u6b64\u4eba\u8f7b\u8eab\u529f\u592b\u4e5f\u5b9e\u5728\u9ad8 \u660e\u3002\u6211\u4f55\u4e0d\u8bf7\u4ed6\u53bb\u52a9\u91d1\u53d4\u53d4\u4e00\u81c2\u4e4b\u529b\uff1f\u201d \u3000\u3000\u4ed6\u662f\u5c0f\u5b69\u5fc3\u6027\uff0c\u4e5f\u4e0d\u8003\u8651\u548c\u522b\u4eba\u53ea\u662f\u4e00\u9762\u4e4b\u7f18\uff0c\u53ea\u89c9\u90a3\u5c11\u5e74\u82f1\u4fca\u6b63\u76f4\uff0c\u5fc5\u662f\u4fa0\u4e49\u5fc3\u6000\uff0c \u4fbf\u9053\uff1a\u201c\u6211\u4e24\u4f4d\u91d1\u53d4\u53d4\u88ab\u574f\u4eba\u653b\u51fb\uff0c\u60c5\u52bf\u5f88\u662f\u5371\u9669\uff0c\u4f60\u53ef\u4e0d\u53ef\u4ee5\u53bb\u5e2e\u5fd9\u6253\u4e00\u67b6\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u89c1\u4ed6\u8bf4\u5f97\u5929\u771f\uff0c\u5fc3\u60f3\uff1a\u201c\u6211\u5de6\u53f3\u65e0\u4e8b\uff0c\u8fd9\u5b69\u5b50\u751a\u662f\u5fe0\u539a\uff0c\u4ed6\u7684\u91d1\u53d4\u53d4\u5fc5\u5b9a\u662f\u8c6a\u4fa0 \u4e4b\u8f88\uff0c\u6211\u4e14\u53bb\u52a9\u4ed6\u4e00\u52a9\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u95ee\u9053\uff1a\u201c\u4f60\u4e24\u4f4d\u91d1\u53d4\u53d4\u5728\u54ea\u91cc\u548c\u574f\u4eba\u6253\u5440\uff1f\u4f60\u91d1\u53d4\u53d4\u53eb\u4ec0\u4e48\u5462\uff1f\u201d \u3000\u3000\u9e4f\u513f\u542c\u4ed6\u8bed\u6c14\uff0c\u77e5\u4ed6\u5df2\u7ecf\u5141\u8bfa\uff0c\u5fc3\u4e2d\u5927\u559c\u9053\uff1a\u201c\u6211\u91d1\u53d4\u53d4\u5c31\u662f\u4e10\u5e2e\u62a4\u6cd5\u91d1\u8001\u5927\uff0c\u91d1\u8001 \u4e8c\u2026\u2026\u201d \u3000\u3000\u90a3\u5c11\u5e74\u542c\u5230\u8fd9\u91cc\uff0c\u5927\u5403\u4e00\u60ca\u5fd9\u9053\uff1a\u201c\u5feb\uff01\u5feb\uff0c\u4f60\u8d76\u5feb\u5e26\u6211\u53bb\u3002\u201d \u3000\u3000\u9e4f\u513f\u98de\u5feb\u5411\u6765\u8def\u5954\u8d70\uff0c\u90a3\u5c11\u5e74\u4e00\u7eb5\u8eab\uff0c\u7275\u7740\u9e4f\u51e0\u5c0f\u624b\uff0c\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u75be\u9a70\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u548c\u9e4f\u513f\u5954\u4e86\u534a\u76cf\u8336\u5149\u666f\uff0c\u542c\u5230\u6797\u4e2d\u4f20\u6765\u9635\u9635\u53f1\u559d\u58f0\uff0c\u4fbf\u4e00\u63d0\u6c14\uff0c\u62c9\u7740\u9e4f\u513f\uff0c\u7a9c\u8fdb\u5c0f \u6797\u3002 \u3000\u3000\u53ea\u89c1\u6797\u4e2d\u4e00\u5757\u7a7a\u5730\u4e0a\uff0c\u56db\u4e2a\u9053\u58eb\u5408\u6218\u4e00\u4e2a\u957f\u8eab\u6c49\u5b50\uff0c\u90a3\u6c49\u5b50\u4ee5\u53cc\u624b\u72ec\u6218\u4e09\u67c4\u957f\u5251\u548c\u4e00\u4e2a \u7a7a\u624b\u9053\u58eb\uff0c\u60c5\u52bf\u975e\u5e38\u9669\u6076\u3002 \u3000\u3000\u9e4f\u513f\u89c1\u91d1\u5927\u53d4\u72ec\u6597\u56db\u4eba\uff0c\u91d1\u4e8c\u53d4\u7adf\u4e0d\u5728\u65c1\uff0c\u4ed6\u77e5\u91d1\u6c0f\u5144\u5f1f\u4ece\u6765\u5bf9\u654c\u90fd\u662f\u4e24\u4eba\u9f50\u4e0a\uff0c\u6b64\u65f6 \u4e0d\u89c1\u91d1\u4e8c\u53d4\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u5fd9\u50ac\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u4f60\u8d76\u5feb\u53bb\u5e2e\u6211\u91d1\u5927\u53d4\uff0c\u6211\u8981\u53bb\u5bfb\u6211\u4e8c\u53d4\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u51dd\u671b\u7740\u6218\u573a\uff0c\u4f3c\u4e4e\u6ca1\u6709\u542c\u5230\u4ed6\u8bf4\u8bdd\uff0c\u9e4f\u513f\u65e0\u5948\uff0c\u4e3e\u76ee\u4e00\u770b\uff0c\u6025\u6597\u5df2\u505c\uff0c\u56db\u67c4\u957f\u5251 \u6307\u7740\u91d1\u53d4\u53d4\u56db\u5904\u8981\u7a74\uff0c\u5176\u4e2d\u4e00\u4e2a\u5e74\u8001\u9053\u58eb\u72de\u58f0\u9053\uff1a\u201c\u91d1\u8001\u5927\uff0c\u5feb\u628a\u5251\u9798\u4ea4\u6765\uff0c\u5426\u5219\uff0c\u54fc\uff0c\u8d2b \u9053\u53ef\u8981\u4e0d\u5ba2\u6c14\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u53d1\u58f0\uff0c\u9e4f\u513f\u53ea\u89c9\u8eab\u65c1\u5c11\u5e74\u8eab\u4f53\u4e00\u6296\u3002 \u3000\u3000\u90a3\u9053\u58eb\u53c8\u9053\uff1a\u201c\u91d1\u8001\u5927\uff0c\u4f60\u8fd8\u6562\u5014\u5f3a\u5417\uff1f\u6b64\u523b\u4f60\u4eec\u4e10\u5e2e\u5e2e\u4e3b\u5df1\u843d\u5728\u6211\u5f1f\u5b50\u624b\u4e2d\u4e86\uff0c\u4f60\u4ee5 \u4e3a\u90a3\u5c0f\u5e2e\u4e3b\u9003\u5f97\u5230\u6d1b\u9633\u5417\uff1f\u54c8\u54c8\u8d2b\u9053\u8001\u65e9\u6d3e\u4eba\u5728\u8def\u4e0a\u606d\u5019\u4e86\u3002\u4f60\u5982\u4e0d\u732e\u51fa\u5251\u9798\uff0c\u563f\u563f\u2026\u2026\u201d \u3000\u3000\u9e4f\u513f\u6108\u542c\u6108\u6012\uff0c\u518d\u4e5f\u5fcd\u8010\u4e0d\u4f4f\uff0c\u4fbf\u8981\u53bb\u6551\u91d1\u53d4\u53d4\uff0c\u53ea\u542c\u5230\u8eab\u65c1\u98ce\u58f0\u4e00\u7d27\uff0c\u90a3\u7f8e\u5c11\u5e74\u5df2\u7a9c \u4e86\u51fa\u53bb\u3002 \u3000\u3000\u573a\u4e2d\u4e94\u4eba\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u521a\u624d\u56e0\u4e3a\u4e89\u6597\u6fc0\u70c8\uff0c\u662f\u4ee5\u9e4f\u513f\u548c\u90a3\u5c11\u5e74\u8d70\u8fdb\u6811\u6797\uff0c\u9690\u4f0f\u5c31\u5728\u8fd1 \u65c1\uff0c\u7adf\u7136\u65e0\u4eba\u53d1\u89c9\u2014\u2014'}),showOnHomepage:!0},{name:"\u7b2c\u5341\u4e09\u7ae0",content:(0,s.jsx)(s.Fragment,{children:'\u90a3\u5c11\u5e74\u9053\uff1a\u201c\u8d64\u9633\u2026\u2026\u8d64\u9633\u8d3c\u9053\u3002\u771f\u5a01\u98ce\u554a\uff01\u4ee5\u4f17\u6b3a\u5be1\uff0c\u597d\u6740\u6c14\u554a\uff01\u201d\u4ed6\u4e0d\u60ef\u9a82\u4eba\uff0c\u662f \u4ee5\u9a82\u5f97\u7ed3\u7ed3\u5df4\u5df4\u3002 \u3000\u3000\u90a3\u5e74\u8001\u9053\u58eb\u4e00\u89c1\u90a3\u5c11\u5e74\uff0c\u8138\u8272\u7acb\u53d8\uff0c\u6c89\u58f0\u559d\u9053\uff1a\u201c\u597d\uff0c\u5434\u5c0f\u5b50\uff0c\u53c8\u78b0\u7740\u4f60\u5566\uff0c\u54b1\u4eec\u6b63\u597d \u4e86\u7ed3\u4e00\u4e0b\u3002\u201d \u3000\u3000\u539f\u6765\u90a3\u4fca\u79c0\u5c11\u5e74\u6b63\u662f\u5434\u51cc\u98ce\uff0c\u90a3\u5929\u4ed6\u544a\u522b\u82cf\u60e0\u82b7\uff0c\u904d\u5904\u5bfb\u627e\u963f\u5170\uff0c\u4ece\u5c71\u4e1c\u5230\u6cb3\u5357\uff0c\u53cd\u590d \u8dd1\u4e86\u51e0\u904d\uff0c\u4e5f\u6ca1\u6709\u6253\u542c\u5230\u4e00\u4e1d\u7ebf\u7d22\uff0c\u8fd9\u65e5\u6b63\u60f3\u8d76\u5230\u6d1b\u9633\u6295\u5bbf\uff0c\u8def\u4e0a\u78b0\u5230\u9e4f\u513f\uff0c\u4e00\u9f50\u5954\u5230\u6797 \u4e2d\uff0c\u6797\u4e2d\u751a\u662f\u6697\u6de1\uff0c\u516d\u4e2a\u4eba\u7684\u9762\u8c8c\u90fd\u6a21\u7cca\u4e0d\u6e05\uff0c\u4ed6\u539f\u60f3\u7acb\u523b\u52a0\u5165\u6218\u56f4\uff0c\u540e\u6765\u6108\u770b\u90a3\u5e74\u8001\u9053\u58eb \u8eab\u5f62\u6108\u719f\uff0c\u5fc3\u4e2d\u5de5\u5728\u7422\u78e8\uff0c\u573a\u4e2d\u5f62\u52bf\u5927\u53d8\uff0c\u5f85\u4ed6\u542c\u5230\u5e74\u957f\u9053\u58eb\u5f00\u53e3\u53d1\u8a00\uff0c\u7acb\u523b\u542c\u51fa\u662f\u6740\u7236\u4ec7 \u4eba\u2014\u2014\u8d64\u9633\u9053\u4eba\uff0c\u4fbf\u9a6c\u4e0a\u7a9c\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5434\u51cc\u98ce\u9053\uff1a\u201c\u4f60\u4eec\u6b66\u5f53\u6d3e\u662f\u60ef\u4e8e\u4ee5\u591a\u51fb\u5c11\u7684\uff0c\u4e00\u9f50\u4e0a\u6765\u5427\u3002\u201d \u3000\u3000\u8d64\u9633\u9053\u4eba\u8138\u4e0a\u5fae\u7ea2\uff0c\u6697\u5fd6\uff1a\u201c\u5c31\u51ed\u8fd9\u5c0f\u5b50\u5728\u6cf0\u5c71\u5927\u4f1a\u9732\u7684\u90a3\u51e0\u624b\uff0c\u5b9e\u5728\u6709\u9650\u5f97\u7d27\uff0c\u4f55\u5fc5 \u8981\u6211\u8981\u4eb2\u81ea\u51fa\u624b\u3002\u201d\u4fbf\u51b7\u7b11\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u522b\u5356\u72c2\uff0c\u4f60\u5982\u80fd\u6253\u8d25\u6211\u4e09\u4e2a\u5f92\u513f\uff0c\u9053\u7237\u4fbf\u653e\u4f60\u8d70 \u8def\u3002\u201d \u3000\u3000\u51cc\u98ce\u867d\u5f97\u672c\u95e8\u5e08\u7956\u4e91\u51b0\u82e5\u4eb2\u4f20\u4e0a\u4e58\u6b66\u529f\uff0c\u4f46\u5230\u5e95\u4ece\u672a\u4e0e\u4eba\u6b63\u5f0f\u4ea4\u624b\uff0c\u5fc3\u5185\u5fae\u602f\uff0c\u60f3\u9053\uff1a \u201c\u5148\u548c\u8fd9\u56db\u4e2a\u6742\u6bdb\u8bd5\u8bd5\uff0c\u5012\u662f\u4e0d\u9519\uff0c\u6253\u4e86\u5c0f\u7684\uff0c\u8fd8\u6015\u8001\u7684\u4e0d\u6210\uff1f\u201d \u3000\u3000\u8d64\u9633\u5927\u559d\u4e00\u58f0\u9053\uff1a\u201c\u4e00\u9e64\uff0c\u628a\u6211\u8fd9\u652f\u5251\u62ff\u53bb\uff0c\u597d\u597d\u4e0e\u8fd9\u5c0f\u5b50\u8f83\u91cf\u8f83\u91cf\uff0c\u83ab\u8981\u6298\u4e86\u6b66\u5f53\u5a01 \u540d\u3002\u201d\u8bf4\u7f62\u628a\u81ea\u5df1\u624b\u4e2d\u957f\u5251\u9012\u7ed9\u8eab\u65c1\u7a7a\u624b\u9053\u4eba\uff0c\u81ea\u5df1\u5374\u8d70\u5230\u91d1\u8001\u5927\u8ddf\u524d\u3002 \u3000\u3000\u51cc\u98ce\u5fc3\u5185\u4e00\u6025\uff0c\u4ed6\u6015\u8d64\u9633\u4e58\u673a\u4f24\u5bb3\u91d1\u8001\u5927\uff0c\u5fae\u5f62\u5fae\u52a8\uff0c\u5df2\u7ecf\u6321\u5728\u91d1\u8001\u5927\u8eab\u524d\uff0c\u53f3\u624b\u957f\u5251 \u4e00\u6325\u9053\uff1a\u201c\u8bf7\u4e0a\u5427\uff01\u201d \u3000\u3000\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53ea\u542c\u8eab\u540e\u201c\u6251\u201d\u7684\u4e00\u58f0\uff0c\u91d1\u8001\u5927\u5df2\u8dcc\u5750\u5012\u5730\u3002\u539f\u6765\u4ed6\u771f\u529b\u5df1\u8017\u5c3d\uff0c\u6b64\u65f6\u51cc\u98ce \u6325\u5251\uff0c\u5149\u8f89\u8000\u76ee\uff0c\u4e00\u9635\u660f\u7729\uff0c\u8dcc\u5750\u5012\u5730\u3002 \u3000\u3000\u5ffd\u7136\u6811\u540e\u5954\u51fa\u4e00\u4e2a\u5c0f\u5b69\uff0c\u54ed\u558a\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u60a8\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u91d1\u8001\u5927\u5f3a\u81ea\u652f\u6301\uff0c\u7741\u5f00\u773c\u5389\u58f0\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u6211\u53eb\u4f60\u8d70\uff0c\u600e\u7684\u4e0d\u542c\u6211\u8bdd\u3002\u201d \u3000\u3000\u9e4f\u513f\u54ed\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u6211\u4e0d\u8981\u79bb\u5f00\u4f60\uff0c\u6211\u8981\u548c\u4f60\u6b7b\u5728\u4e00\u5757\u513f\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u89c1\u4ed6\u6025\u5f97\u5c0f\u8138\u901a\u7ea2\uff0c\u867d\u662f\u6d95\u6cd7\u7eb5\u6a2a\uff0c\u795e\u8272\u5374\u575a\u6bc5\u65e0\u6bd4\uff0c\u5fc3\u77e5\u529d\u4e5f\u65e0\u76ca\uff0c\u4fbf\u67d4\u58f0 \u9053\uff1a\u201c\u9e4f\u513f\uff0c\u522b\u54ed\u5566\uff0c\u91d1\u53d4\u53d4\u7b54\u5e94\u4e0d\u518d\u79bb\u5f00\u4f60\u4e86\u3002\u201d \u3000\u3000\u9e4f\u513f\u5fc3\u4e2d\u5927\u559c\uff0c\u6307\u7740\u6b63\u7740\u51dd\u795e\u805a\u6c14\u7684\u51cc\u98ce\u9053:\u201c\u91d1\u53d4\u53d4\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u6253\u8d62\u7684\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e09\u4e2a\u9053\u58eb\u7ad9\u7740\u4e09\u4e2a\u65b9\u4f4d\uff0c\u628a\u51cc\u98ce\u56e2\u56e2\u56f4\u4f4f\u3002 \u3000\u3000\u7a81\u7136\u5de6\u8fb9\u9053\u58eb\u559d\u9053\uff1a\u201c\u770b\u62db\uff01\u201d\u76f4\u653b\u51cc\u98ce\u4e0b\u76d8\u3002 \u3000\u3000\u51cc\u98ce\u5411\u65c1\u4e00\u95ea\uff0c\u4e0d\u9000\u53cd\u8fdb\uff0c\u957f\u5251\u75be\u70b9\u53f3\u8fb9\u9053\u4eba\u3002\u90a3\u9053\u4eba\u89c1\u5251\u52bf\u75be\u5982\u6d41\u661f\uff0c\u5fc3\u5185\u5927\u9a87\uff0c\u5411 \u540e\u5012\u9000\u4e24\u6b65\u3002 \u3000\u3000\u51cc\u98ce\u4e0d\u5f85\u62db\u5f0f\u7528\u5c3d\uff0c\u53cd\u624b\u659c\u5288\u6b63\u524d\u654c\u4eba\uff0c\u4e24\u5251\u4e00\u89e6\uff0c\u51cc\u98ce\u7a81\u7684\u64a4\u5251\uff0c\u8fd0\u8d77\u771f\u529b\uff0c\u786c\u63a5\u5de6 \u8fb9\u9053\u58eb\u62e6\u8170\u4e00\u5251\u3002 \u3000\u3000\u4ed6\u79c9\u8d4b\u751a\u539a\uff0c\u53c8\u5de7\u98df\u8840\u679c\uff0c\u5185\u529b\u6df1\u6e5b\uff0c\u6bd4\u8d77\u8f9b\u6377\u4e5f\u53ea\u7565\u900a\u4e00\u7b79\uff0c\u6b64\u65f6\u867d\u53ea\u7528\u4e86\u4e94\u6210\u771f \u529b\uff0c\u9707\u5f97\u90a3\u9053\u58eb\u864e\u53e3\u53d1\u9ebb\uff0c\u957f\u5251\u51e0\u4e4e\u8131\u624b\u3002 \u3000\u3000\u51cc\u98ce\u5f97\u52bf\u76f4\u4e0a\uff0c\u53f3\u624b\u5251\u8d70\u504f\u950b\uff0c\u5de6\u624b\u65bd\u201c\u5f00\u5c71\u4e09\u5f0f\u7834\u7389\u638c\u201d\uff0c\u8eab\u5b50\u5728\u5251\u5e55\u4e2d\u7a7f\u6765\u7a7f\u53bb\uff0c \u4e09\u67c4\u957f\u5251\u6709\u65f6\u5dee\u4e00\u70b9\u523a\u4e0a\u8eab\uff0c\u5374\u53c8\u88ab\u4ed6\u8f7b\u8f7b\u95ea\u8fc7\u3002 \u3000\u3000\u8d64\u9633\u5728\u65c1\uff0c\u6108\u770b\u6108\u662f\u5fc3\u5bd2\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5c0f\u5b50\u6bd4\u8d77\u5f53\u5e74\u4ed6\u7236\u4eb2\uff0c\u5251\u672f\u66f4\u52a0\u8001\u7ec3\u51f6\u8fa3\uff0c\u8fd9\u5c0f \u5e74\u7eaa\uff0c\u4e5f\u4e0d\u77e5\u662f\u600e\u6837\u7ec3\u7684\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u89c1\u51cc\u98ce\u8eab\u6cd5\u5982\u98ce\uff0c\u62db\u5f0f\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u8db3\u8e0f\u516b\u5366\u65b9\u4f4d\uff0c\u795e\u6001\u6781\u662f\u6d12\u8131\uff0c \u6839\u672c\u4e0d\u50cf\u6b63\u5728\u5bf9\u654c\uff0c\u5fc3\u77e5\u4ed6\u5df2\u5c06\u592a\u6781\u95e8\u201c\u65ad\u9b42\u5251\u201d\u7ec3\u81f3\u5316\u5883\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5c11\u5e74\u5982\u4e0d\u662f\u4e3a\u62a4\u536b \u6211\u548c\u9e4f\u513f\uff0c\u4ee5\u5b88\u4e3a\u653b\uff0c\u90a3\u4e09\u4e2a\u81ed\u9053\u58eb\u65e9\u5012\u4e0b\u5566\u3002\u201d \u3000\u3000\u4ed6\u4e3e\u773c\u4e00\u770b\uff0c\u573a\u4e2d\u60c5\u52bf\u5df2\u53d8\uff0c\u51cc\u98ce\u5df2\u5360\u5c3d\u4e0a\u98ce\uff0c\u5de6\u4e00\u5251\uff0c\u53f3\u4e00\u5251\uff0c\u53ea\u6740\u5230\u4e09\u4e2a\u9053\u58eb\u6ee1\u5934 \u5927\u6c57\uff0c\u81ea\u987e\u4e0d\u6687\uff0c\u66f4\u8c08\u4e0d\u5230\u5408\u653b\u3002 \u3000\u3000\u6597\u5230\u5206\u9645\uff0c\u51cc\u98ce\u7a81\u7136\u98de\u8d77\u4e00\u811a\uff0c\u8e22\u5012\u4e00\u4e2a\u9053\u58eb\uff0c\u53f3\u624b\u65bd\u51fa\u65ad\u9b42\u5251\u6cd5\u6700\u540e\u4e09\u62db\uff0c\u201c\u5f31\u7d6e\u98d8 \u98ce\u201d\uff0c\u201c\u70b9\u70b9\u7e41\u661f\u201d\uff0c\u201c\u77f3\u7834\u5929\u60ca\u201d\uff0c\u53ea\u542c\u89c1\u4e24\u58f0\u60ca\u53eb\uff0c\u4e24\u4e2a\u9053\u58eb\u53cc\u53cc\u5012\u5730\u3002 \u3000\u3000\u539f\u6765\u51cc\u98ce\u65bd\u5230\u6700\u540e\u4e09\u62db\uff0c\u90a3\u4e24\u4e2a\u9053\u58eb\u53ea\u89c9\u773c\u82b1\u7f2d\u4e71\uff0c\u9762\u4e0a\u5bd2\u6c14\u68ee\u68ee\uff0c\u4e0d\u89c9\u9a87\u6781\u800c\u53eb\uff0c\u84e6 \u7136\u8db3\u4e0b\u4e00\u9ebb\uff0c\u90fd\u88ab\u70b9\u4e2d\u201c\u516c\u5b59\u7a74\u201d\u3002 \u3000\u3000\u91d1\u8001\u5927\u77a7\u5f97\u6e05\u6670\uff0c\u5fc3\u60f3\uff1a\u201c\u521a\u624d\u90a3\u4e09\u62db\uff0c\u773c\u770b\u81ed\u9053\u58eb\u4eec\u4fbf\u8981\u547d\u4e27\u5251\u4e0b\uff0c\u4ed6\u7adf\u786c\u786c\u6536\u56de\u5df2 \u51fa\u5251\u5f0f\uff0c\u6539\u523a\u53cc\u8db3\uff0c\u8fd9\u4fca\u5c11\u5e74\u4e0d\u4f46\u6b66\u529f\u9ad8\u6781\uff0c\u5fc3\u5730\u4e5f\u5f88\u662f\u4ec1\u6148\u3002\u201d \u3000\u3000\u8d64\u9633\u94c1\u9752\u4e86\u8138\uff0c\u4e0a\u524d\u89e3\u5f00\u4e09\u4eba\u7a74\u9053\uff0c\u786c\u8981\u66ff\u5f92\u513f\u627e\u56de\u573a\u9762\u3002 \u3000\u3000\u9e4f\u513f\u5ffd\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u4f60\u770b\u6211\u8bf4\u5f97\u5bf9\u4e0d\u5bf9\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9e4f\u513f\u9053\uff1a\u201c\u6211\u65e9\u8bf4\u4ed6\u80fd\u628a\u8fd9\u4e9b\u81ed\u9053\u58eb\u5168\u90e8\u6253\u8dd1\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u70b9\u5934\u4e0d\u8bed\uff0c\u6697\u81ea\u5fd6\u9053\uff1a\u201c\u8d64\u9633\u8d3c\u9053\u529f\u529b\u6df1\u539a\uff0c\u8fd9\u5c11\u5e74\u4e0e\u4ed6\u597d\u50cf\u6709\u5927\u4ec7\uff0c\u8fd9\u4e00\u4ea4 \u624b\uff0c\u975e\u4f24\u5373\u6b7b\u3002\u8d64\u9633\u6700\u662f\u65e0\u803b\uff0c\u5982\u679c\u4e0e\u4ed6\u5f92\u513f\u8054\u624b\u653b\u51fb\uff0c\u60c5\u52bf\u5927\u662f\u9669\u6076\uff0c\u76ee\u4e0b\u81ea\u5df1\u5168\u8eab\u8131 \u529b\uff0c\u65e0\u80fd\u76f8\u52a9\uff0c\u53ea\u80fd\u6fc0\u4ed6\u4e00\u6fc0\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u9053\uff1a\u201c\u8d64\u9633\u8d3c\u9053\uff0c\u4f60\u6253\u4e0d\u8d62\u4ed6\u7684\uff0c\u5927\u4f19\u513f\u4e00\u9f50\u4e0a\u554a\uff01\u201d \u3000\u3000\u8d64\u9633\u660e\u77e5\u76f8\u6fc0\uff0c\u4f46\u5fc3\u60f3\u51cc\u98ce\u5251\u6cd5\u867d\u9ad8\uff0c\u5185\u529b\u5374\u600e\u4e48\u4e5f\u80dc\u4e0d\u8fc7\u81ea\u5df1\u6570\u5341\u5e74\u6027\u547d\u4ea4\u4fee\u7684\u201c\u6df7 \u5143\u4e00\u6c14\u5148\u5929\u529f\u201d\uff0c\u5f53\u4e0b\u76d8\u7b97\u5df2\u5b9a\uff0c\u4fbf\u53f1\u9053\uff1a \u3000\u3000\u201c\u8d3c\u53eb\u5316\uff0c\u4f60\u66ff\u6211\u5b89\u9759\uff0c\u5bb0\u8fd9\u5c0f\u5b50\uff0c\u4f55\u987b\u522b\u4eba\u76f8\u52a9\u3002\u201d \u3000\u3000\u51cc\u98ce\u521a\u624d\u8fde\u8d25\u4e09\u4eba\uff0c\u4fe1\u5fc3\u5927\u589e\uff0c\u89c1\u8d64\u9633\u53e3\u53e3\u58f0\u58f0\u8981\u5bb0\u81ea\u5df1\uff0c\u5fc3\u5185\u5927\u4e3a\u607c\u6012\uff0c\u9a82\u9053\uff1a\u201c\u8d64 \u9633\u8d3c\u9053\uff0c\u4f11\u901e\u53e3\u820c\u4e4b\u5229\uff0c\u4eca\u65e5\u4fbf\u53eb\u4f60\u5f52\u5929\u3002\u201d \u3000\u3000\u8d64\u9633\u9053\u4eba\u5927\u6012\uff0c\u559d\u58f0\u201c\u63a5\u62db\u201d\uff0c\u53f3\u638c\u4fbf\u5411\u51cc\u98ce\u53f3\u80c1\u5288\u53bb\u3002 \u3000\u3000\u51cc\u98ce\u4e0d\u6562\u6020\u6162\uff0c\u4e00\u4e0a\u624b\u4fbf\u5c55\u5f00\u201c\u5f00\u5c71\u4e09\u5f0f\u7834\u7389\u62f3\u201d\uff0c\u51dd\u795e\u63a5\u62db\u3002 \u3000\u3000\u6597\u4e86\u534a\u664c\uff0c\u8d64\u9633\u89c1\u51cc\u98ce\u867d\u53ea\u662f\u53cd\u6765\u8986\u53bb\u7684\u5341\u62db\uff0c\u4f46\u5a01\u529b\u521a\u731b\u4e4b\u6781\uff0c\u81ea\u5df1\u638c\u6cd5\u867d\u662f\u7cbe\u5999\uff0c \u4f46\u6bcf\u88ab\u51cc\u98ce\u52b2\u529b\u6240\u8feb\uff0c\u7adf\u7136\u9012\u4e0d\u51fa\u6765\uff0c\u4e0d\u7531\u5fc3\u5185\u5927\u6025\uff0c\u8fde\u65bd\u6570\u8bb0\u6740\u7740\uff0c\u903c\u9000\u51cc\u98ce\u4e24\u6b65\uff0c\u65bd\u51fa \u6b66\u5f53\u9547\u5c71\u4e4b\u5b9d\u201c\u65e0\u6781\u795e\u529f\u62f3\u201d\u3002 \u3000\u3000\u8fd9\u201c\u65e0\u6781\u795e\u529f\u62f3\u201d\uff0c\u4e5f\u662f\u8d70\u521a\u731b\u8def\u5b50\uff0c\u5239\u65f6\u4e4b\u95f4\uff0c\u62f3\u98ce\u864e\u864e\uff0c\u4e24\u4eba\u77e5\u662f\u6027\u547d\u76f8\u640f\uff0c\u4e0d\u6562 \u4e1d\u6beb\u5927\u610f\uff0c\u53d1\u62db\u6108\u6765\u6108\u5feb\uff0c\u52b2\u529b\u6108\u6765\u6108\u6c89\u3002 \u3000\u3000\u91d1\u8001\u5927\u770b\u770b\u8eab\u65c1\u9e4f\u513f\uff0c\u89c1\u4ed6\u76ee\u4e0d\u8f6c\u6674\u5730\u76ef\u7740\u573a\u4e2d\u4e8c\u4eba\uff0c\u795e\u8272\u594b\u53d1\uff0c\u795e\u91c7\u98de\u626c\uff0c\u50cf\u662f\u81ea\u5df1 \u5728\u4e0e\u4eba\u640f\u6597\u4e00\u822c\uff0c\u4e0d\u7981\u5fc3\u4e2d\u6697\u53f9\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5230\u5e95\u5e74\u5e7c\uff0c\u4e0d\u77e5\u773c\u524d\u5371\u673a\uff0c\u8fd9\u4e8c\u4eba\u4e0d\u4f46\u81ea \u8eab\u6027\u547d\u76f8\u640f\uff0c\u8fd8\u5173\u7cfb\u6574\u4e2a\u4e10\u5e2e\u547d\u8fd0\uff0c\u4e07\u4e00\u90a3\u5c11\u5e74\u4e00\u62db\u5931\u7740\uff0c\u6211\u4eec\u8001\u4e8c\u751f\u6b7b\u4e0d\u660e\uff0c\u81ea\u5df1\u5185\u529b\u672a \u590d\uff0c\u4e10\u5e2e\u4fbf\u8981\u6bc1\u5728\u8fd9\u8d3c\u9053\u4e4b\u624b\u3002\u201d \u3000\u3000\u4ed6\u867d\u957f\u5f97\u7c97\u5927\uff0c\u4f46\u5fc3\u601d\u5374\u6781\u7ec6\u5bc6\uff0c\u6b64\u65f6\u5fc3\u60c5\u5927\u662f\u7d27\u5f20\uff0c\u624b\u4e0d\u7531\u5192\u51fa\u51b7\u6c57\u3002 \u3000\u3000\u4e8c\u4eba\u6597\u4e86\u5c06\u8fd1\u767e\u62db\uff0c\u51cc\u98ce\u5185\u529b\u5145\u6c9b\uff0c\u6beb\u65e0\u5026\u6001\uff0c\u8d64\u9633\u653b\u52bf\u51cc\u5389\uff0c\u5b88\u52bf\u4e25\u5bc6\uff0c\u4e5f\u4e0d\u89c1\u8d25 \u76f8\uff0c\u51cc\u98ce\u5f88\u4e0d\u8010\uff0c\u5fc3\u9053\uff1a\u201c\u4e0d\u7528\u9669\u62db\uff0c\u53ea\u6015\u4e0d\u6613\u53d6\u80dc\u3002\u201d \u3000\u3000\u4ed6\u770b\u90a3\u8d64\u9633\u9053\u8d3c\u7684\u5185\u529b\u4fee\u4e3a\uff0c\u4f3c\u4e0d\u5728\u81ea\u5df1\u4e4b\u4e0b\uff0c\u5047\u82e5\u4f7f\u7528\u9669\u62db\uff0c\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u5927\u6709\u5931\u624b\u7684 \u53ef\u80fd\uff0c\u662f\u4ee5\u4e00\u65f6\u4ecd\u662f\u8fdf\u8fdf\u4e0d\u80fd\u4e0b\u624b\u3002 \u3000\u3000\u518d\u8fc7\u5f97\u7247\u523b\uff0c\u5434\u51cc\u98ce\u84e6\u7136\u5927\u5316\u4e00\u58f0\uff0c\u53cc\u638c\u4e00\u5408\u4e4b\u4e0b\uff0c\u4e00\u5410\u4e00\u95ea\uff0c\u5de6\u624b\u6a2a\u5728\u80f8\u524d\uff0c\u53f3\u624b\u7a81 \u53d8\u201c\u5f00\u5c71\u4e09\u5f0f\u201d\u4e3a\u4e0a\u4e00\u5f0f\u201c\u4e94\u9b3c\u62db\u9b42\u201d\u3002 \u3000\u3000\u8fd9\u65ad\u9b42\u5251\u62db\u4e43\u662f\u6614\u5e74\u6cb3\u6d1b\u4e00\u5251\u5434\u8bcf\u4e91\u7684\u7edd\u6280\uff0c\u5434\u51cc\u98ce\u628a\u5b83\u7528\u62f3\u62db\u4f7f\u51fa\uff0c\u4e5f\u89c9\u5a01\u529b\u751a\u5927\uff0c \u4e00\u4f7f\u51fa\u6765\uff0c\u62db\u5f0f\u4e4b\u95f4\uff0c\u81ea\u7136\u6d41\u9732\u51fa\u4e00\u79cd\u72e0\u8fa3\u7684\u5473\u9053\u3002 \u3000\u3000\u8d64\u9633\u9053\u58eb\u51b7\u4e0d\u9632\u5434\u51cc\u98ce\u53d8\u786c\u6253\u786c\u649e\u7684\u62db\u5f0f\u53d8\u5316\u6765\u4e89\u80dc\uff0c\u53ea\u597d\u53cc\u638c\u4e00\u5408\uff0c\u540e\u9000\u4e00\u6b65\uff0c\u51c6\u5907 \u4e5f\u91c7\u6e38\u6597\u65b9\u5f0f\u3002 \u3000\u3000\u5434\u51cc\u98ce\u51b7\u51b7\u4e00\u55e4\uff0c\u5f53\u80f8\u800c\u7acb\u7684\u5de6\u62f3\u5411\u4e0b\u4e00\u6c89\uff0c\u53f3\u624b\u95ea\u7535\u5730\u5316\u5b9e\u4e3a\u865a\uff0c\u5012\u64a4\u800c\u56de\uff0c\u64a4\u5230\u8eab \u524d\u4e03\u5bf8\u5de6\u53f3\uff0c\u548c\u5de6\u624b\u540c\u65f6\u4e00\u753b\u5706\u5f27\uff0c\u865a\u7a7a\u6025\u6363\u800c\u51fa\u3002 \u3000\u3000\u540c\u65f6\u95f4\u91cc\uff0c\u5434\u51cc\u98ce\u84e6\u5730\u5410\u6c14\u5f00\u58f0\uff0c\u8fd9\u4e43\u662f\u6c14\u529f\u6240\u96c6\uff0c\u6709\u82e5\u6625\u96f7\u54a4\u7a7a\uff0c\u76f4\u53ef\u88c2\u77f3\uff0c\u597d\u4e0d\u60ca \u4eba\uff01 \u3000\u3000\u8d64\u9633\u9053\u957f\u5728\u6cf0\u5c71\u5929\u4e0b\u82f1\u96c4\u4f1a\u65f6\u9886\u6559\u8fc7\u51cc\u98ce\u7684\u8eab\u624b\uff0c\u90a3\u65f6\u89c1\u4ed6\u7684\u5251\u6cd5\u867d\u662f\u4e0d\u51e1\uff0c\u4f46\u5012\u4e0d\u8db3 \u4e3a\u60e7\uff0c\u90a3\u77e5\u534a\u5e74\u4e0d\u89c1\uff0c\u51cc\u98ce\u6b66\u827a\u7adf\u7cbe\u8fdb\u5982\u6b64\uff0c\u4e0d\u7531\u5fc3\u4e2d\u60ca\u9a87\u4ea4\u52a0\u3002 \u3000\u3000\u4f46\u4ed6\u81ea\u6043\u529f\u529b\u6df1\u539a\uff0c\u4e5f\u662f\u5927\u559d\u4e00\u58f0\uff0c\u5355\u638c\u5e73\u63a8\u800c\u51fa\uff0c\u4e43\u662f\u201c\u63a8\u7a97\u671b\u6708\u201d\u7684\u5f0f\u5b50\uff0c\u540c\u65f6\u94ee \u7136\u62bd\u51fa\u957f\u5251\u3002 \u3000\u3000\u4e24\u80a1\u52b2\u9053\u4e00\u89e6\uff0c\u51cc\u98ce\u5185\u529b\u7a81\u53d1\uff0c\u4f46\u4ed6\u5ffd\u89c9\u5f97\u8d64\u9633\u9053\u58eb\u638c\u529b\u4e00\u865a\uff0c\u90a3\u80a1\u52b2\u9053\u7adf\u7136\u6d88\u5931\u65e0 \u5f62\uff0c\u800c\u4ed6\u8fd9\u4e00\u8bb0\u5168\u529b\u65bd\u4e3a\u7684\u62db\u5f0f\u518d\u4e5f\u6536\u4e0d\u56de\u6765\uff01 \u3000\u3000\u8fd9\u5c31\u662f\u8d64\u9633\u9053\u58eb\u7ecf\u9a8c\u8001\u5230\u72e0\u6ed1\u7684\u5730\u65b9\uff0c\u773c\u770b\u51cc\u98ce\u4e00\u62db\u8d70\u7a7a\u5c31\u5f97\u843d\u9669\uff0c\u65c1\u8fb9\u7684\u91d1\u8001\u5927\u4e0d\u7981 \u6025\u5f97\u5927\u6bd4\u51fa\u58f0\u2014\u2014 \u3000\u3000\u51cc\u98ce\u7ecf\u9a8c\u867d\u5dee\uff0c\u4f46\u4ed6\u7980\u8d4b\u5f02\u4eba\uff0c\u53cd\u5e94\u5feb\u6781\uff0c\u5728\u8fd9\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u786c\u751f\u751f\u628a\u638c\u98ce\u5f80\u5de6\u4e00 \u632a\uff0c\u540c\u65f6\u8eab\u4f53\u501f\u529b\u5411\u53f3\u9762\u4e00\u8f6c\u2014\u2014 \u3000\u3000\u8f70\u7136\u4e00\u58f0\uff0c\u51cc\u98ce\u90a3\u62db\u201c\u611a\u516c\u79fb\u5c71\u201d\u6253\u5728\u5de6\u9762\u6797\u4e0a\uff0c\u6811\u679d\u6ce5\u571f\u88ab\u626b\u8d77\u4e00\u5927\u7247\u6765\uff0c\u800c\u4ed6\u7684\u8eab \u4f53\u5374\u501f\u529b\u4ece\u53f3\u9762\u6e9c\u6e9c\u8f6c\u4e86\u4e00\u767e\u516b\u5341\u5ea6\uff0c\u66fc\u5999\u5730\u95ea\u8eab\u800c\u9000\uff0c\u4e5f\u662f\u633a\u5251\u4ee5\u5f85\u3002 \u3000\u3000\u8d64\u9633\u9053\u58eb\u77a5\u4e86\u90a3\u626b\u53bb\u7684\u679d\u571f\u4e00\u773c\uff0c\u5fc3\u4e2d\u4e0d\u89c9\u9a87\u7136\uff0c\u4ed6\u60f3\u4e0d\u5230\u51cc\u98ce\u638c\u529b\u7adf\u4e5f\u96c4\u539a\u5982\u65af\u3002 \u3000\u3000\u51cc\u98ce\u9971\u5438\u4e00\u53e3\u6c14\uff0c\u6325\u5251\u800c\u4e0a\uff0c\u8fd9\u6b21\u4ed6\u5fc3\u4e2d\u6709\u6570\uff0c\u80c6\u6c14\u5927\u589e\uff0c\u51fa\u624b\u5c31\u5168\u662f\u201c\u65ad\u9b42\u5251\u6cd5\u201d\u4e2d \u7684\u7edd\u5b66\uff0c\u4e00\u8fde\u4e09\u62db\u7adf\u5c06\u8d64\u9633\u9053\u58eb\u903c\u9000\u6570\u6b65\u3002 \u3000\u3000\u8d64\u9633\u9053\u957f\u6025\u6012\u96be\u5374\uff0c\u6296\u624b\u4e5f\u5c55\u5f00\u6b66\u5f53\u201c\u4e5d\u5bab\u795e\u884c\u5251\u6cd5\u201d\u4e2d\u6700\u51cc\u5389\u7684\u201c\u9752\u4e91\u4e5d\u5f0f\u201d\u6253\u7b97\u62a2 \u56de\u4e3b\u52a8\u3002 \u3000\u3000\u90a3\u77e5\u51cc\u98ce\u4e00\u6b65\u4e5f\u4e0d\u8ba9\u5730\u62a2\u653b\u4e0d\u5df2\uff0c\u4ed6\u5251\u672f\u5df2\u5728\u8d64\u9633\u4e4b\u4e0a\uff0c\u5374\u56e0\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u6bcf\u6bcf\u4e0d\u80fd\u628a\u63e1 \u826f\u673a\uff0c\u770b\u5f97\u91d1\u8001\u5927\u51b7\u6c57\u76f4\u5192\u3002 \u3000\u3000\u75be\u6597\u4e2d\uff0c\u8d64\u9633\u9053\u58eb\u53c8\u662f\u8bc8\u5356\u7834\u7efd\uff0c\u60f3\u5f15\u51cc\u98ce\u4e0a\u5f53\uff0c\u51cc\u98ce\u867d\u7136\u5978\u6ed1\u4e0d\u8db3\uff0c\u4f46\u4ed6\u806a\u660e\u7edd\u9876\uff0c \u4e00\u770b\u5c31\u77e5\u8d64\u9633\u7528\u610f\uff0c\u4ed6\u6709\u610f\u5c48\u8eab\u800c\u8fdb\u5f85\u8d64\u9633\u4ee5\u4e3a\u4ed6\u4e0a\u5f53\uff0c\u53d8\u62db\u7a81\u51fa\u4e4b\u9645\uff0c\u4ed6\u9661\u7136\u65bd\u51fa\u201c\u65ad\u9b42 \u5251\u6cd5\u201d\u4e2d\u7684\u201c\u65e0\u5e38\u628a\u53c9\u201d\uff0c\u4e00\u6643\u8eab\u5230\u4e86\u8d64\u9633\u8eab\u540e\uff0c\u4e3e\u5251\u76f4\u523a\u2014\u2014 \u3000\u3000\u91d1\u8001\u5927\u9ad8\u53eb\u4e86\u58f0\u597d\uff0c\u4ee5\u4e3a\u8d64\u9633\u5fc5\u7136\u65e0\u6551\uff0c\u90a3\u77e5\u8d64\u9633\u4e34\u5371\u4e0d\u4e71\uff0c\u53cd\u624b\u4e00\u638c\u201c\u5012\u6253\u91d1\u949f\u201d\u76f4 \u88ad\u51cc\u98ce\u811a\u524d\uff0c\u6253\u7b97\u4ee5\u653b\u5236\u653b\uff01 \u3000\u3000\u8fd9\u4e00\u62db\u4e43\u662f\u5168\u529b\u800c\u53d1\uff0c\u529b\u9053\u975e\u540c\u5c0f\u53ef\uff0c\u51cc\u98ce\u5fc3\u4e2d\u4e00\u51db\uff0c\u5de6\u638c\u201c\u516d\u4e01\u5f00\u5c71\u201d\u8fce\u649e\u800c\u51fa\uff0c\u53f3 \u624b\u5251\u5f0f\u5374\u4e1d\u6beb\u4e0d\u53d7\u5f71\u54cd\u5730\u76f4\u523a\u51fa\u53bb\uff01 \u3000\u3000\u7830\u7136\u4e00\u58f0\u5de8\u54cd\uff0c\u51cc\u98ce\u8eab\u5b50\u5fae\u5fae\u4e00\u632b\uff0c\u4f46\u4ed6\u53f3\u624b\u5251\u5f0f\u5374\u4ecd\u98de\u5feb\u523a\u51fa\uff0c\u8d64\u9633\u9053\u957f\u518d\u5feb\u4e5f\u5c06\u6765 \u4e0d\u53ca\u9003\u907f\u2014\u2014 \u3000\u3000\u4f46\u4e0d\u77e5\u600e\u5730\uff0c\u51cc\u98ce\u7684\u957f\u5251\u5ffd\u7136\u7adf\u6162\u5f97\u4e00\u6162\uff01 \u3000\u3000\u8d64\u9633\u9053\u957f\u4f55\u7b49\u7ecf\u9a8c\uff0c\u8fde\u5fd9\u62fc\u529b\u524d\u8dc3\uff0c\u201c\u5530\u201d\u4e00\u58f0\uff0c\u4ed6\u80cc\u4e0a\u88ab\u5212\u5f00\u4e00\u6761\u53e3\u5b50\uff0c\u9c9c\u8840\u957f\u6d41\uff0c \u4f46\u603b\u7b97\u8ba9\u4ed6\u9003\u51fa\u5251\u4e0b\uff01 \u3000\u3000\u539f\u6765\u51cc\u98ce\u5373\u5c06\u5f97\u624b\u4e4b\u9645\uff0c\u7a81\u7136\u4e00\u79cd\u201c\u6740\u4eba\u201d\u7684\u6050\u60e7\u611f\u89c9\u88ad\u4e0a\u4ed6\u5fc3\u5934\uff0c\u4ed6\u5929\u6027\u5584\u826f\u65e0\u6bd4\uff0c \u4e00\u751f\u4ece\u672a\u6740\u8fc7\u4eba\uff0c\u867d\u7136\u773c\u524d\u662f\u4ed6\u6740\u7236\u5927\u4ec7\u4eba\uff0c\u4f46\u4e34\u523a\u4e4b\u65f6\u5374\u81ea\u7136\u751f\u51fa\u8fd9\u79cd\u611f\u89c9\uff0c\u4ee4\u4ed6\u7684\u5251\u5f0f \u4e0d\u7531\u81ea\u4e3b\u5730\u4e00\u7a92\uff01 \u3000\u3000\u91d1\u8001\u5927\u4e5f\u6014\u5f97\u4e00\u6014\uff0c\u518d\u770b\u90a3\u6b66\u5f53\u9053\u58eb\u65f6\uff0c\u53ea\u89c1\u4ed6\u4eec\u90fd\u8ddf\u7740\u8d64\u9633\u8dd1\u5f97\u8fdc\u4e86\uff01 \u3000\u3000\u51cc\u98ce\u8fd0\u4e86\u4e00\u53e3\u6c14\uff0c\u89c9\u5f97\u8eab\u4e0a\u6beb\u65e0\u5f02\u72b6\uff0c\u5f85\u4ed6\u518d\u4e3e\u5934\u4e00\u77a7\uff0c\u8d64\u9633\u548c\u4ed6\u4e09\u4e2a\u5f92\u513f\uff0c\u5df2\u6d88\u5931\u5728 \u4e1b\u6797\u4e2d\uff01 \u3000\u3000\u4ed6\u5929\u6027\u548c\u5e73\u6de1\u6cca\uff0c\u5fc3\u5730\u6781\u662f\u8f6f\u6148\uff0c\u81ea\u4ece\u51fa\u9053\u4ee5\u6765\uff0c\u4ece\u6ca1\u6709\u6740\u8fc7\u4efb\u4f55\u4eba\uff0c\u6b64\u523b\u773c\u89c1\u8d64\u9633\u8d1f \u4f24\u800c\u9041\uff0c\u660e\u77e5\u4e58\u80dc\u8ffd\u6251\uff0c\u5b9a\u53ef\u81f4\u8d64\u9633\u4e8e\u6b7b\u547d\uff0c\u62a5\u5f97\u7236\u4ec7\uff0c\u4f46\u5374\u8fdf\u8fdf\u4e0d\u80fd\u4e0b\u624b\u3002 \u3000\u3000\u4ed6\u81ea\u6211\u5b89\u6170\uff0c\u60f3\u9053\uff1a\u201c\u8981\u6740\u8fd9\u8d3c\u9053\uff0c\u673a\u4f1a\u8fd8\u591a\u54e9\uff01\u201d\u5982\u4eca\uff0c\u4ed6\u5df1\u5145\u6ee1\u81ea\u4fe1\uff0c\u5b9a\u80fd\u80dc\u8fc7\u8d64 \u9633\u3002\u4f46\u4e0d\u53ef\u5426\u8ba4\uff0c\u4ed6\u4ecd\u6709\u4e00\u70b9\u540e\u6094\u4e4b\u610f\u3002 \u3000\u3000\u9e4f\u513f\u89c1\u4ed6\u5446\u5446\u7acb\u7740\uff0c\u53ea\u9053\u4ed6\u4e5f\u53d7\u4e86\u5185\u4f24\uff0c\u6025\u9053\uff1a\u201c\u4f60\u53ef\u89c9\u5f97\u54ea\u513f\u4e0d\u8212\u670d\uff1f\u201d \u3000\u3000\u51cc\u98ce\u6447\u5934\u9053\uff1a\u201c\u5c0f\u5f1f\uff0c\u4f60\u653e\u5fc3\uff0c\u90a3\u8d3c\u9053\u600e\u80fd\u4f24\u6211\uff0c\u5012\u662f\u4f60\u91d1\u53d4\u53d4\uff0c\u5185\u529b\u6d88\u8017\u8fc7\u5ea6\uff0c\u6211\u8fd9 \u513f\u6709\u74f6\u7075\u6cc9\uff0c\u53ef\u4ee5\u5e2e\u4ed6\u8d76\u5feb\u6062\u590d\u54e9\u3002\u201d \u3000\u3000\u8bf4\u7f62\u4ece\u6000\u4e2d\u638f\u51fa\u201c\u4e07\u5e74\u7075\u6cc9\u201d\uff0c\u8d70\u5230\u4e94\u5728\u95ed\u76ee\u8c03\u606f\u7684\u91d1\u8001\u5927\u8ddf\u524d\u3002 \u3000\u3000\u91d1\u8001\u5927\u521a\u624d\u89c1\u51cc\u98ce\u9707\u4f24\u8d64\u9633\u9053\u4eba\uff0c\u8d64\u9633\u7387\u5f92\u9003\u8d70\uff0c\u4e00\u76f4\u60ac\u8d77\u7684\u5fc3\uff0c\u8fd9\u624d\u7b97\u662f\u653e\u4e0b\uff0c\u7acb\u523b \u6452\u9664\u6742\u5ff5\uff0c\u4f5c\u8d77\u5410\u7eb3\u529f\u592b\u3002 \u3000\u3000\u4ed6\u89c1\u51cc\u98ce\u8d70\u6765\uff0c\u7741\u773c\u9053\uff1a\u201c\u8bf7\u6559\u9601\u4e0b\u5927\u540d\u3002\u201d\u51cc\u98ce\u606d\u8eab\u7b54\u9053\uff1a\u201c\u665a\u8f88\u5434\u51cc\u98ce\u3002\u201d\u8bf4\u7740\uff0c \u4ed6\u628a\u624b\u4e2d\u7389\u74f6\u62d4\u5f00\uff0c\u9001\u5230\u91d1\u8001\u5927\u624b\u4e0a\u9053\uff1a\u201c\u8fd9\u662f\u4e07\u5e74\u6e29\u7389\u6240\u5b55\u7075\u6cc9\uff0c\u529f\u6548\u975e\u5e38\u795e\u5999\uff0c\u8001\u524d\u8f88 \u5148\u670d\u4e00\u6ef4\u518d\u8bf4\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u89c1\u4ed6\u8bf4\u5f97\u8bda\u6073\uff0c\u4fbf\u4e0d\u63a8\u8f9e\uff0c\u63a5\u8d77\u7389\u74f6\uff0c\u5012\u4e86\u4e00\u6ef4\u5165\u53e3\uff0c\u53ea\u89c9\u904d\u53e3\u82ac\u82b3\uff0c\u811a\u4e2d\u53d7\u7528 \u65e0\u6bd4\uff0c\u53c8\u95ed\u8d77\u4e86\u773c\uff0c\u8c03\u8fd0\u771f\u6c14\u3002 \u3000\u3000\u8fc7\u4e86\u534a\u664c\uff0c\u8001\u5927\u4e00\u8dc3\u800c\u8d77\uff0c\u62d6\u7740\u9e4f\u513f\uff0c\u4e00\u8d77\u5411\u5434\u51cc\u98ce\u62f1\u8eab\u4e00\u63d6\u9053\uff1a\u201c\u5434\u5927\u4f19\uff0c\u4f60\u66ff\u54b1\u4eec \u4e10\u5e2e\u62b5\u6321\u5f3a\u4ec7\uff0c\u4fdd\u62a4\u54b1\u4eec\u5c0f\u5e2e\u4e3b\uff0c\u6b64\u6069\u6b64\u5fb7\uff0c\u4e10\u5e2e\u5168\u4f53\u5f1f\u5b50\u4e0d\u6562\u7a0d\u5fd8\uff0c\u4f46\u6709\u5429\u5490\uff0c\u6c34\u91cc\u706b \u91cc\uff0c\u65e0\u4e0d\u4ece\u547d\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u6025\u6025\u8fd8\u793c\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u8001\u524d\u8f88\uff0c\u60a8\u5feb\u522b\u8fd9\u6837\uff0c\u665a\u8f88\u6709\u4e2a\u62dc\u5f1f\u540d\u53eb\u8f9b\u6377\uff0c\u5e38\u5411\u665a\u8f88 \u63d0\u53ca\u8001\u524d\u8f88\u7684\u82f1\u98ce\u9ad8\u4e49\uff0c\u665a\u8f88\u5fc3\u4e2d\u771f\u662f\u4ef0\u6155\u5f97\u5f88\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u9053\uff1a\u201c\u539f\u6765\u5434\u5927\u4fa0\u662f\u8f9b\u8001\u5f1f\u7684\u4e49\u5144\uff0c\u96be\u602a\u8fd9\u597d\u6b66\u529f\uff0c\u90a3\u4e48\u8001\u53eb\u5316\u6258\u4e2a\u5927\uff0c\u4e5f\u558a\u4f60 \u4e00\u58f0\u8001\u5f1f\u7f62\u3002\u201d \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u5f88\u662f\u8c6a\u8fc8\uff0c\u4e5f\u5c31\u4e0d\u518d\u62d8\u793c\uff0c\u95ee\u9053\uff1a\u201c\u4e10\u5e2e\u600e\u4e5f\u4f1a\u548c\u6b66\u5f53\u7ed3\u4ec7\uff1f\u201d \u3000\u3000\u91d1\u8001\u5927\u9053\uff1a\u201c\u8fd9\u4e8b\u8bf4\u6765\u8bdd\u957f\uff0c\u73b0\u5728\u5148\u5bfb\u8001\u4e8c\u5427\uff01\u201d \u3000\u3000\u51cc\u98ce\u7b54\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u4e8e\u662f\u4e09\u4eba\u4fbf\u5411\u524d\u641c\u7d22\u3002 \u3000\u3000\u8d70\u4e86\u5341\u4f59\u4e08\uff0c\u53ea\u89c1\u91d1\u8001\u4e8c\u9760\u5728\u4e00\u68f5\u5927\u6811\u4e0a\uff0c\u773c\u775b\u7741\u5f97\u5927\u5927\u7684\uff0c\u53cc\u624b\u7d27\u6293\u4e00\u6587\u957f\u5251\uff0c\u5251\u8eab \u5df2\u88ab\u4ed6\u626d\u8d77\u4e86\u51e0\u4e2a\u7ed3\u3002 \u3000\u3000\u9e4f\u513f\u89c1\u4ed6\u8138\u8272\u82cd\u767d\uff0c\u795e\u6001\u751a\u662f\u5413\u4eba\uff0c\u4e0a\u524d\u63a8\u4e00\u63a8\u4ed6\u53cc\u80a9\u9053\uff1a\u201c\u91d1\u4e8c\u53d4\uff0c\u9e4f\u513f\u6765\u5566\uff01\u201d \u3000\u3000\u8001\u4e8c\u6beb\u4e0d\u7406\u4f1a\uff0c\u9e4f\u513f\u5927\u5947\uff0c\u53cd\u8eab\u6b63\u60f3\u95ee\u91d1\u8001\u5927\uff0c\u53ea\u89c1\u4ed6\u5446\u5446\u7ad9\u7740\uff0c\u8138\u4e0a\u808c\u8089\u62bd\u6410\uff0c\u7259\u9f7f \u7d27\u7d27\u54ac\u7740\u4e0b\u5507\u3002 \u3000\u3000\u51cc\u98ce\u5185\u5fc3\u4e86\u7136\uff0c\u4e5f\u81ea\u611f\u5230\u51c4\u60e8\uff0c\u7528\u624b\u6478\u7740\u9e4f\u513f\u5934\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u9e4f\u513f\uff0c\u4f60\u91d1\u4e8c\u53d4\u5df2\u6b7b \u4e86\u3002\u201d \u3000\u3000\u9e4f\u513f\u4e00\u542c\uff0c\u5982\u7126\u96f7\u8f70\u9876\uff0c\u4f0f\u8eab\u628a\u4f4f\u91d1\u8001\u4e8c\u5c38\u4f53\u5927\u54ed\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u867d\u5e7c\uff0c\u53ef\u662f\u5df2\u7ecf\u5386\u8fc7\u591a\u6b21\u751f\u79bb\u6b7b\u522b\uff0c\u6b64\u65f6\u773c\u89c1\u89c6\u5df1\u82e5\u5b50\u7684\u53d4\u53d4\u53c8\u906d\u60e8\u6b7b\uff0c\u57cb\u5728\u5c0f \u5c0f\u5fc3\u7530\u4e2d\u7684\u60b2\u4f24\uff0c\u518d\u4e5f\u9690\u85cf\u4e0d\u4f4f\u3002\u8fd9\u4e00\u54ed\uff0c\u771f\u5982\u557c\u9e43\u8840\u6cea\uff0c\u51cc\u98ce\u5728\u65c1\uff0c\u4e5f\u4e0d\u7981\u9f3b\u9178\u4e0d\u5df2\u3002 \u3000\u3000\u51cc\u98ce\u770b\u90a3\u91d1\u8001\u4e8c\uff0c\u53ea\u89c1\u4ed6\u4f24\u5728\u80cc\u540e\uff0c\u663e\u7136\u53d7\u4e86\u6b66\u5f53\u9053\u58eb\u6697\u7b97\uff0c\u4ed6\u624b\u4e2d\u7d27\u6293\u7740\u4e00\u67c4\u957f\u5251\uff0c \u5251\u8eab\u88ab\u626d\u5f97\u5f2f\u66f2\uff0c\u4ed6\u638c\u4e0a\u5374\u76ae\u6bdb\u4e0d\u635f\uff0c\u6b63\u662f\u95fb\u540d\u5929\u4e0b\u7684\u9634\u98ce\u722a\u7684\u529f\u592b\uff0c\u90a3\u652f\u5251\u662f\u65b9\u624d\u90a3\u7a7a\u624b \u9053\u58eb\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u53cd\u8eab\u7740\u90a3\u91d1\u8001\u5927\uff0c\u53ea\u89c1\u4ed6\u76ee\u5149\u6108\u53d8\u6108\u5446\u6ede\uff0c\u77e5\u4ed6\u4f24\u5fc3\u6b32\u7edd\uff0c\u5fc3\u60f3\u5b89\u6170\u4ed6\u51e0\u53e5\uff0c\u4f46\u4e00\u65f6 \u4e4b\u95f4\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\u3002 \u3000\u3000\u84e6\u7684\uff0c\u91d1\u8001\u5927\u4ef0\u5929\u957f\u7b11\u8d77\u6765\u3002\u7b11\u58f0\u4e2d\uff0c\u6570\u5341\u5e74\u6765\u5144\u5f1f\u95f4\u76f8\u4eb2\u76f8\u7231\u7684\u60c5\u666f\uff0c\u4e00\u4e00\u95ea\u8fc7\u4ed6\u7684 \u8111\u6d77\u2026\u2026\u54e5\u513f\u4fe9\u5171\u540c\u521b\u540d\u7acb\u4e07\uff0c\u4e00\u5fc3\u8f85\u4f50\u4e10\u5e2e\uff0c\u54e5\u513f\u4fe9\u53d1\u8a93\u6c38\u4e0d\u5a36\u4eb2\uff0c\u6c38\u4e0d\u76f8\u79bb\u2026\u2026 \u3000\u3000\u7b11\u58f0\u6e10\u6e10\u4f4e\u6c89\uff0c\u6700\u540e\u7ec8\u4e8e\u53d8\u6210\u4e86\u996e\u6ce3\uff0c\u8c46\u5927\u7684\u6cea\u73e0\uff0c\u4e00\u9897\u9897\u6d41\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u6b62\u4f4f\u6ce3\u58f0\uff0c\u8f7b\u629a\u7740\u91d1\u8001\u4e8c\u6293\u7d27\u957f\u5251\u7684\u5927\u624b\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8001\u4e8c\uff0c\u4f60\u522b\u8d70\u554a\uff0c\u8fd8\u6709 \u66f4\u96be\u7684\u5173\u8981\u54b1\u4eec\u53bb\u95ef\uff0c\u8001\u4e8c\uff0c\u632f\u4f5c\u4e9b\u554a\uff0c\u4f60\u633a\u5f97\u4f4f\u4e48\uff1f\u201d \u3000\u3000\u7c0c\u7c0c\u98ce\u54cd\u4e2d\uff0c\u4ed6\u4f3c\u4e4e\u542c\u89c1\u91d1\u8001\u4e8c\u8c6a\u8fc8\u7684\u58f0\u97f3\uff1a\u201c\u8fd9\u70b9\u5c0f\u5f69\u7b97\u5f97\u4e86\u4ec0\u4e48\uff1f\u5927\u54e5\uff0c\u8fd9\u7b14\u5e10\u54b1 \u4eec\u8bb0\u4e0b\u4e86\uff01\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u4e5f\u8c6a\u8fc8\u5730\u5927\u7b11\u9053\uff1a\u201c\u95ef\u5427\uff01\u201d \u3000\u3000\u6e05\u98ce\u628a\u4ed6\u7684\u7b11\u58f0\u4f20\u5f97\u8001\u8fdc\uff0c\u53c8\u628a\u8fdc\u5904\u7684\u56de\u58f0\u5e26\u4e86\u56de\u6765\uff0c\u4e00\u65f6\u6ee1\u6797\u5b50\u90fd\u662f\u4ed6\u8c6a\u8fc8\u7684\u7b11\u58f0\u3002 \u3000\u3000\u84e6\u7136\uff0c\u4ed6\u4e00\u628a\u62b1\u8d77\u91d1\u8001\u4e8c\u7684\u5c38\u4f53\uff0c\u62d6\u7740\u9e4f\u513f\uff0c\u5411\u51cc\u98ce\u4e00\u63d6\uff0c\u53cd\u8eab\u5934\u4e5f\u4e0d\u56de\u5f84\u5411\u6765\u8def\u8d70 \u53bb\u3002 \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u6025\u75db\u4e4b\u4e0b\uff0c\u795e\u60c5\u8fd1\u4e4e\u660f\u4e71\uff0c\u5fc3\u4e2d\u5927\u662f\u653e\u5fc3\u4e0d\u4e0b\uff0c\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\u8ddf\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u4e09\u4eba\u8d70\u8fdb\u7834\u5e99\uff0c\u91d1\u8001\u5927\u653e\u4e0b\u80a9\u4e0a\u7684\u5c38\u4f53\uff0c\u80cc\u5bf9\u7740\u4e24\u4eba\u8dea\u4e0b\uff0c\u4f4e\u58f0\u7977\u9053\uff1a\u201c\u7956\u5e08\u7237\uff0c\u975e\u662f \u5f1f\u5b50\u4e0d\u91cd\u4fe1\u8a93\uff0c\u5b9e\u662f\u5978\u8d3c\u4eec\u903c\u4eba\u592a\u751a\uff0c\u5f1f\u5b50\u867d\u5df2\u53d1\u8a93\u4e0d\u518d\u8fc7\u95ee\u4e10\u5e2e\u8bf8\u4e8b\uff0c\u53ef\u662f\u5982\u4eca\u5e2e\u4e3b\u5e74 \u5e7c\uff0c\u6b66\u529f\u672a\u6210\uff0c\u5982\u679c\u5f1f\u5b50\u8fd9\u518d\u6492\u624b\u4e00\u8d70\uff0c\u7956\u5e08\u7237\u60a8\u8f9b\u82e6\u624b\u521b\u7684\u5a01\u9707\u5927\u6cb3\u5357\u5317\u6570\u767e\u5e74\u7684\u5927\u5e2e\uff0c \u4fbf\u8981\u4ece\u6b64\u74e6\u89e3\uff0c\u4e3a\u4eca\u4e4b\u8ba1\uff0c\u5f1f\u5b50\u53ea\u6709\u7834\u8a93\u4e86\u3002\u201d \u3000\u3000\u4ed6\u7977\u544a\u5b8c\u6bd5\uff0c\u8f6c\u8fc7\u8eab\u6765\uff0c\u8138\u8272\u51dd\u91cd\u5bf9\u9e4f\u513f\u8bf4\uff1a\u201c\u5e2e\u4e3b\uff0c\u6211\u91d1\u8001\u5927\u65e2\u7136\u5df2\u51b3\u5b9a\u91cd\u5165\u4e10\u5e2e\uff0c \u5c31\u8bf7\u60a8\u518d\u8058\u6211\u4e3a\u62a4\u6cd5\u5427\uff01\u201d \u3000\u3000\u9e4f\u513f\u6447\u5934\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u60a8\u5feb\u522b\u8fd9\u6837\u558a\u6211\uff0c\u6211\u2026\u2026\u6211\u60f3\u914d\u505a\u5e2e\u4e3b\u5462\uff1f\u201d \u3000\u3000\u4ed6\u6bd5\u7adf\u5e74\u5e7c\uff0c\u6b64\u65f6\u4e00\u542c\u91d1\u53d4\u53d4\u8981\u81ea\u5df1\u6267\u884c\u5e2e\u4e3b\u6743\u5229\uff0c\u4e0d\u89c9\u5927\u611f\u6050\u614c\u3002 \u3000\u3000\u91d1\u8001\u5927\u6c89\u58f0\u9053\uff1a\u201c\u8001\u5e2e\u4e3b\u4f20\u7ed9\u4f60\u5927\u4f4d\u65f6\uff0c\u4ed6\u53ef\u5429\u5490\u4e86\u4f60\u4e00\u4e9b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u9e4f\u513f\u89c1\u4ed6\u4ee5\u5927\u4e49\u76f8\u8d23\uff0c\u5185\u5fc3\u4e00\u51db\uff0c\u8c6a\u6c14\u7a81\u589e\uff0c\u4fbf\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u9e4f\u513f\u77e5\u9519\u5566\uff0c\u8058\u62a4\u6cd5\u662f \u600e\u4e48\u4e2a\u8058\u6cd5\uff1f\u201d \u3000\u3000\u91d1\u8001\u5927\u98de\u8eab\u8dd1\u4e86\u51fa\u53bb\uff0c\u6298\u4e86\u6839\u6811\u679d\uff0c\u5bf9\u9e4f\u513f\u9053\uff1a\u201c\u4f60\u62ff\u7740\u8fd9\u6839\u6811\u679d\uff0c\u5728\u6211\u80a9\u4e0a\u78b0\u4e24\u4e0b\uff0c \u7136\u540e\u5ba3\u5e03\u8058\u6211\u4e3a\u4e10\u5e2e\u7b2c\u5341\u516d\u4ee3\u62a4\u6cd5\uff0c\u8fd9\u4eea\u5f0f\u672c\u6781\u9686\u91cd\uff0c\u5317\u65b9\u597d\u6c49\u90fd\u88ab\u8bf7\u6765\u89c2\u793c\u3002\u5509\uff01\u73b0\u5728\u53ea \u6709\u2026\u2026\u53ea\u6709\u8bf7\u5434\u8001\u5f1f\u505a\u4e2a\u89c1\u8bc1\u5427\uff01\u201d \u3000\u3000\u9e4f\u513f\u89c1\u4ed6\u8138\u4e0a\u60b2\u60e8\uff0c\u4f46\u795e\u8272\u751a\u662f\u60a0\u626c\uff0c\u77e5\u4ed6\u5728\u56de\u5fc6\u4ed6\u5144\u5f1f\u7b2c\u4e00\u6b21\u88ab\u8058\u4e3a\u62a4\u6cd5\u7684\u76db\u51b5\uff0c\u6015 \u53c8\u5f15\u8d77\u4ed6\u7684\u54c0\u75db\uff0c\u4fbf\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u6211\u4eec\u5f00\u59cb\u7f62\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u70b9\u70b9\u5934\uff0c\u5411\u7740\u9e4f\u513f\u8dea\u4e0b\u3002 \u3000\u3000\u9e4f\u513f\u5927\u662f\u60f6\u6050\uff0c\u6b63\u5f85\u4f38\u624b\u53bb\u6276\uff0c\u91d1\u8001\u5927\u9053\uff1a\u201c\u8fd9\u662f\u4e10\u5e2e\u7684\u89c4\u77e9\uff0c\u5e2e\u4e3b\u4e0d\u53ef\u8fdd\u80cc\u3002\u201d \u3000\u3000\u9e4f\u513f\u5fc3\u5185\u65e0\u5948\uff0c\u4fbf\u5f88\u5feb\u7684\u7528\u6811\u679d\u5728\u91d1\u8001\u5927\u4e24\u80a9\u70b9\u4e86\u70b9\uff0c\u6717\u58f0\u9053\uff1a\u201c\u4e10\u5e2e\u7b2c\u5341\u516d\u4ee3\u5e2e\u4e3b\u674e \u9e4f\u8058\u91d1\u2026\u2026\u91d1\u53d4\u53d4\u4e3a\u5e2e\u4e3b\u62a4\u6cd5\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u77e5\u91d1\u8001\u5927\u7684\u540d\u5b57\uff0c\u800c\u4e14\u53c8\u558a\u60ef\u4e86\u91d1\u53d4\u53d4\uff0c\u662f\u4ee5\u8131\u53e3\u800c\u51fa\u3002 \u3000\u3000\u5434\u51cc\u98ce\u542c\u4ed6\u6ee1\u53e3\u7ae5\u97f3\uff0c\u4f46\u6c14\u5ea6\u6062\u5b8f\uff0c\u795e\u8272\u5e84\u4e25\uff0c\u5927\u6709\u5e2e\u4e3b\u98ce\u683c\uff0c\u4e0d\u7981\u6697\u81ea\u70b9\u5934\u3002 \u3000\u3000\u91d1\u8001\u5927\u7ad9\u8d77\u8eab\u6765\u5bf9\u51cc\u98ce\u8bf4\u9053\uff1a\u201c\u8001\u5f1f\uff0c\u4f60\u8ddf\u8d64\u9633\u8d3c\u9053\u4e5f\u6709\u6069\u6028\uff1f\u201d \u3000\u3000\u51cc\u98ce\u70b9\u5934\u7b54\u9053\uff1a\u201c\u4ed6\u662f\u6211\u6740\u7236\u4ec7\u4eba\u4e4b\u4e00\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u60f3\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u5927\u58f0\u9053\uff1a\u201c\u6c5f\u6e56\u4e0a\u4e45\u5c31\u6709\u4f20\u8bf4\u2018\u4e03\u5999\u795e\u541b\u2019\u6885\u5c71\u6c11\uff0c\u2018\u6cb3\u6d1b\u4e00 \u5251\u2019\u5434\u8bcf\u4e91\u90fd\u88ab\u6b66\u5f53\u8d64\u9633\uff0c\u5ce8\u5a9a\u82e6\u5eb5\uff0c\u5d06\u5cd2\u5389\u9e57\u6240\u6bc1\uff0c\u8001\u5f1f\u4f60\u4e5f\u59d3\u5434\uff0c\u53ef\u4e0e\u5434\u5927\u4fa0\u6709\u4ec0\u4e48\u5173 \u7cfb\u5417\u201d \u3000\u3000\u51cc\u98ce\u5e84\u5bb9\u7b54\u9053\uff1a\u201c\u6b63\u662f\u5bb6\u7236\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u53f9\u606f\u9053\uff1a\u201c\u6cb3\u6d1b\u4e00\u5251\u5434\u5927\u4fa0\u4e0e\u54b1\u4eec\u8001\u5e2e\u4e3b\u6700\u662f\u83ab\u9006\uff0c\u4e24\u4eba\u540c\u5728\u5927\u6cb3\u5357\u5317\u884c\u4fa0\u4ed7 \u4e49\uff0c\u5509\uff01\u60f3\u4e0d\u5230\u90fd\u6b7b\u4e8e\u5978\u5f92\u6697\u7b97\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u95ee\u9053\uff1a\u201c\u8d35\u5e2e\u53c8\u600e\u4f1a\u548c\u8d64\u9633\u7ed3\u6881\u5b50\uff1f\u201d \u3000\u3000\u91d1\u8001\u5927\u9053\uff1a\u201c\u8fd9\u662f\u5341\u591a\u5e74\u7684\u4e8b\u4e86\uff0c\u90a3\u65f6\u6c5f\u6e56\u4e0a\u51fa\u4e86\u4e24\u4e2a\u602a\u6770\uff0c\u4e00\u4e2a\u662f\u2018\u4e03\u5999\u795e\u541b\u2019\uff0c\u4e00 \u4e2a\u5c31\u662f\u4ee4\u5c0a\u3002\u8fd9\u4e24\u4eba\u6b66\u529f\u9ad8\u6781\uff0c\u5c24\u5176\u4ee4\u5c0a\u4e3a\u4eba\u884c\u4e8b\u53c8\u662f\u521a\u6b63\u4e0d\u963f\uff0c\u6240\u4ee5\u540d\u5934\u4e4b\u9ad8\uff0c\u5927\u6709\u538b\u5012 \u81ea\u547d\u4e3a\u56db\u5927\u6b63\u6d3e\u7684\u638c\u95e8\u4eba\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u4ece\u5df2\u6b7b\u8001\u4ec6\u5904\u542c\u8fc7\u8fd9\u6bb5\u5386\u53f2\uff0c\u4fbf\u63a5\u53e3\u9053\uff1a\u201c\u6240\u4ee5\u8fd9\u56db\u4e2a\u81ea\u547d\u6b63\u6d3e\u7684\u638c\u95e8\u4eba\uff0c\u5728\u5ac9\u5992 \u53ca\u7ef4\u62a4\u58f0\u540d\u7684\u524d\u63d0\u4e0b\uff0c\u5c31\u4e0d\u987e\u8eab\u4efd\u8054\u624b\u5bf9\u4ed8\u6885\u5927\u4fa0\u4e0e\u6211\u7239\u7239\u4e86\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u70b9\u5934\u9053\uff1a\u201c\u4e8b\u60c5\u5c31\u53d1\u751f\u5728\u56db\u5927\u95e8\u6d3e\u5408\u624b\u88ad\u51fb\u4e03\u5999\u795e\u541b\u90a3\u6b21\u5927\u6218\uff0c\u7ed3\u679c\u6885\u5927\u4fa0\u529b\u6218 \u8eab\u2018\u6b7b\u2019\uff0c\u8fd9\u56db\u4e2a\u638c\u95e8\u4eba\u8e0c\u8e87\u6ee1\u5fd7\u7684\u8d70\u4e86\uff0c\u53ef\u662f\u5176\u4e2d\u5d06\u5cd2\u638c\u95e8\u4eba\u5389\u9e57\u5374\u9057\u843d\u4e86\u4e00\u4e2a\u5251\u9798\uff0c\u8fd9 \u4e2a\u5251\u9798\u6070\u597d\u88ab\u8eb2\u5728\u77f3\u540e\u7684\u4e00\u540d\u4e10\u5e2e\u5f1f\u5b50\u62fe\u4e86\u53bb\u3002\u201d \u3000\u3000\u51cc\u98ce\u5fc3\u60f3\uff1a\u201c\u96be\u602a\u8d64\u9633\u53e3\u53e3\u58f0\u58f0\u903c\u7740\u91d1\u8001\u5927\u8981\u5251\u9798\uff0c\u4e0d\u8fc7\u8fd9\u65e2\u662f\u5389\u9e57\u4e4b\u7269\uff0c\u8d64\u9633\u4e3a\u4ec0\u4e48 \u8981\u82e6\u82e6\u76f8\u903c\u5462\uff1f\u201d \u3000\u3000\u91d1\u8001\u5927\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u5251\u9798\u672c\u6765\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u90a3\u540d\u4e10\u5e2e\u5f1f\u5b50\u53ea\u56e0\u89c1\u5b83\u96d5\u5de5\u7cbe\u7f8e\uff0c\u751a\u662f\u53e4 \u96c5\uff0c\u4e00\u65f6\u597d\u5947\uff0c\u4fbf\u62e3\u4e86\u8d77\u6765\uff0c\u60f3\u4e0d\u5230\u6700\u8fd1\u4e24\u5e74\uff0c\u6c5f\u6e56\u4e0a\u7a81\u7136\u4f20\u95fb\u6b66\u6797\u524d\u8f88\u602a\u4fa0\u9189\u9053\u4eba\u4e00\u8eab\u795e \u9b3c\u4e0d\u6d4b\u7684\u6b66\u529f\uff0c\u5c3d\u6570\u8bb0\u8f7d\u5728\u4e00\u672c\u6781\u5c0f\u79d8\u7b08\u4e0a\uff0c\u85cf\u5728\u4e00\u4e2a\u795e\u79d8\u7684\u5251\u9798\u4e2d\uff0c\u800c\u8fd9\u4e2a\u5251\u9798\u5df1\u843d\u4e8e \u2018\u4e10\u5e2e\u2019\u4e4b\u624b\u3002\u201d \u3000\u3000\u201c\u8fd9\u4e2a\u4f20\u8bf4\u6108\u6765\u6108\u795e\uff0c\u90a3\u4e10\u5e2e\u5f1f\u5b50\u5ffd\u7136\u60f3\u5230\u81ea\u5df1\u5341\u591a\u5e74\u524d\u62e3\u5230\u7684\u5251\u9798\u4e0e\u4f20\u8bf4\u4e2d\u5f88\u6709\u76f8\u4f3c \u4e4b\u5904\uff0c\u4fbf\u628a\u90a3\u5251\u9798\u732e\u7ed9\u8001\u5e2e\u4e3b\uff0c\u8001\u5e2e\u4e3b\u4ed4\u7ec6\u5bdf\u770b\uff0c\u4e5f\u4e0d\u89c1\u4efb\u4f55\u5947\u7279\u4e4b\u5904\uff0c\u4f46\u60f3\u5230\u6c5f\u6e56\u4eba\u8a00\u51ff \u51ff\uff0c\u5fc5\u6709\u51e0\u5206\u771f\u5b9e\u53ef\u4fe1\uff0c\u4fbf\u628a\u5251\u9798\u6536\u5728\u8eab\u65c1\u3002\u201d \u3000\u3000\u201c\u5389\u9e57\u540e\u6765\u4e5f\u542c\u5230\u4e86\u8fd9\u4e2a\u4f20\u8bf4\uff0c\u4ed6\u7565\u4e00\u7422\u78e8\uff0c\u4fbf\u65ad\u5b9a\u662f\u4ed6\u5341\u591a\u5e74\u5931\u53bb\u7684\u5251\u9798\uff0c\u5fc3\u4e2d\u65e2\u6094 \u53c8\u6068\uff0c\u6df1\u77e5\u81ea\u5df1\u4e00\u751f\u4f5c\u5b7d\u592a\u591a\uff0c\u8fd9\u66ae\u5e74\u4e4b\u65f6\uff0c\u96be\u4fdd\u4e0d\u6709\u9ad8\u624b\u5bfb\u4ec7\uff0c\u6240\u4ee5\u5bf9\u4e8e\u672c\u95e8\u6b66\u529f\u79d8\u7b08\uff0c \u4ed6\u600e\u80af\u653e\u8fc7\u5982\u6b64\u826f\u673a\uff1f\u6240\u4ee5\u4fbf\u5904\u5904\u4e0e\u6211\u4e10\u5e2e\u4e3a\u96be\uff0c\u60f3\u5230\u593a\u56de\u5251\u9798\u3002\u201d \u3000\u3000\u201c\u540e\u6765\u8001\u5e2e\u4e3b\u591c\u9047\u4ec7\u4f0f\uff0c\u547d\u4e27\u8352\u5c71\uff0c\u6211\u5144\u5f1f\u90a3\u65f6\u6b63\u5728\u5c71\u4e1c\u529e\u4e00\u4ef6\u5927\u4e8b\u3002\u8001\u5e2e\u4e3b\u4e34\u7ec8\u524d\u5de7 \u9047\u9e4f\u2026\u2026\u5c0f\u5e2e\u4e3b\uff0c\u4fbf\u628a\u4e10\u5e2e\u5e2e\u4ee4\u53ca\u5251\u9798\u4f20\u7ed9\u4e86\u5c0f\u5e2e\u4e3b\uff0c\u90a3\u5389\u9e57\u4e0d\u77e5\u600e\u7684\u6d88\u606f\u751a\u662f\u7075\u901a\uff0c\u77e5\u9053 \u5251\u9798\u5df2\u843d\u4e8e\u5c0f\u5e2e\u4e3b\u4e4b\u624b\uff0c\u4fbf\u4eb2\u81ea\u51fa\u52a8\uff0c\u53c8\u5de7\u90a3\u65f6\u54b1\u4eec\u4e10\u5e2e\u5317\u652f\u51fa\u4e86\u51e0\u4e2a\u53db\u5f92\uff0c\u4e58\u8001\u5e2e\u4e3b\u65b0 \u4e27\uff0c\u5c0f\u5e2e\u4e3b\u5e74\u5e7c\uff0c\u7adf\u60f3\u51ef\u89ce\u5e2e\u4e3b\u5927\u4f4d\uff0c\u4fbf\u548c\u5389\u9e57\u8fde\u624b\uff0c\u5939\u653b\u6211\u5144\u5f1f\u548c\u9e4f\u513f\u3002\u6211\u5144\u5f1f\u89c1\u654c\u4eba\u706b \u591a\u52bf\u4f17\u3002\u5c31\u8bf7\u5c0f\u5e2e\u4e3b\u6084\u6084\u5355\u72ec\u53bb\u6295\u5954\u672c\u5e2e\u5357\u652f\u9646\u5e2e\u4e3b\uff0c\u6211\u548c\u8001\u4e8c\u6545\u9732\u75d5\u8ff9\uff0c\u60f3\u5f15\u5f97\u5978\u8d3c\u53db\u5f92 \u8ffd\u8e2a\u6211\u5144\u5f1f\uff0c\u5c0f\u5e2e\u4e3b\u5c31\u53ef\u795e\u4e0d\u77e5\u9b3c\u4e0d\u89c9\u7684\u907f\u5f00\u4ed6\u4eec\uff0c\u4e0d\u6599\u8fd9\u7740\u7adf\u88ab\u5978\u8d3c\u8bc6\u7834\uff0c\u5f85\u5230\u6211\u5144\u5f1f\u53d1 \u89c9\u5927\u4e8b\u4e0d\u5999\uff0c\u8d76\u53bb\u8425\u6551\u5c0f\u5e2e\u4e3b\u65f6\uff0c\u5c0f\u5e2e\u4e3b\u5df2\u7ecf\u53d7\u4f24\u9003\u5230\u53e4\u5e99\uff0c\u5e78\u4e8f\u9047\u7740\u8f9b\u8001\u5f1f\uff0c\u51fa\u624b\u76f8\u52a9\uff0c \u8fd9\u624d\u6551\u4e86\u54b1\u4eec\u5c0f\u5e2e\u4e3b\u4e00\u547d\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u63a5\u53e3\u9053\uff1a\u201c\u90a3\u4e48\u8d64\u9633\u600e\u4e48\u5411\u8d35\u5e2e\u7d22\u53d6\u5251\u9798\u3002\u201d \u3000\u3000\u91d1\u8001\u5927\u6447\u5934\u53f9\u9053\uff1a\u201c\u6211\u5e2e\u4e0e\u6b66\u5f53\u7d20\u6765\u4e95\u6c34\u6cb3\u6c34\u4e0d\u76f8\u72af\uff0c\u8001\u5e2e\u4e3b\u5728\u751f\u4e4b\u65f6\uff0c\u7d20\u77e5\u8d64\u9633\u4e3a \u4eba\uff0c\u5c0f\u6c14\u5ac9\u5fcc\uff0c\u6240\u4ee5\u4e00\u5411\u544a\u8beb\u5e2e\u4e2d\u5f1f\u5b50\uff0c\u83ab\u4e0e\u6b66\u5f53\u5f1f\u5b50\u53d1\u751f\u51b2\u7a81\uff0c\u4ee5\u514d\u95e8\u6237\u76f8\u4e89\u3002\u5509\uff01\u8fd9\u8d64 \u9633\u4e5f\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u7adf\u4e0b\u8fd9\u822c\u6bd2\u624b\u6697\u7b97\u8001\u4e8c\uff0c\u53ea\u6015\u662f\u4e0e\u5389\u9e57\u8001\u8d3c\u53c8\u8054\u4e0a\u624b\u4e86\u5427\uff01\u201d \u3000\u3000\u5176\u5b9e\uff0c\u4ed6\u90a3\u77e5\u9053\uff0c\u90a3\u65e5\u8d64\u9633\u9053\u4eba\uff0c\u5728\u201c\u65e0\u4e3a\u5385\u201d\u4e2d\u89c1\u8f9b\u6377\u5927\u663e\u8eab\u624b\uff0c\u529b\u8d25\u5f3a\u654c\u91d1\u9c81\u5384\uff0c \u8eab\u6cd5\u4e4b\u5947\u771f\u662f\u95fb\u6240\u672a\u95fb\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u9a87\uff0c\u60f3\u5230\u8f9b\u6377\u65e5\u540e\u5bfb\u4ec7\uff0c\u81ea\u5df1\u600e\u751f\u62b5\u6321\u5f97\u4e86\uff0c\u8fd9\u624d\u4e0d\u987e \u9053\u4e49\uff0c\u79c1\u81ea\u51fa\u624b\u62a2\u593a\u5251\u9798\u3002 \u3000\u3000\u5434\u51cc\u98ce\u542c\u5b8c\u91d1\u8001\u5927\u8bb2\u5b8c\u7ecf\u8fc7\uff0c\u70b9\u5934\u4e0d\u8bed\uff0c\u5185\u5fc3\u5374\u5bfb\u601d\u9053\uff1a\u201c\u6211\u8fd9\u4e00\u4e2a\u591a\u6708\u6765\uff0c\u8dd1\u904d\u4e86\u5c71 \u4e1c\u6cb3\u5357\uff0c\u4e5f\u6ca1\u6709\u53d1\u73b0\u963f\u5170\u7684\u8e2a\u8ff9\uff0c\u5979\u53cc\u76ee\u5931\u660e\uff0c\u5728\u8fd9\u9669\u8bc8\u767e\u51fa\u7684\u6c5f\u6e56\u4e2d\uff0c\u5b9e\u5728\u662f\u5371\u9669\u6781\u4e86\uff0c \u5c31\u51ed\u6211\u4e00\u4e2a\u4eba\u8fd9\u6837\u627e\u4e0b\u53bb\uff0c\u90a3\u771f\u662f\u5927\u6d77\u635e\u9488\uff0c\u4e5f\u4e0d\u77e5\u8981\u627e\u5230\u90a3\u5929\uff0c\u554a\uff01\u5bf9\u4e86\uff0c\u6377\u5f1f\u8bf4\u8fc7\u4e10\u5e2e \u5f1f\u5b50\u904d\u5e03\u5929\u4e0b\uff0c\u8bf7\u4ed6\u4eec\u51fa\u624b\u76f8\u52a9\u8bbf\u8be2\uff0c\u5e0c\u671b\u5927\u5f97\u591a\u54e9\uff01\u201d \u3000\u3000\u4ed6\u6b63\u60f3\u5411\u91d1\u8001\u5927\u5f00\u53e3\uff0c\u4f46\u5ffd\u8f6c\u5ff5\u60f3\u5230\uff1a\u201c\u73b0\u5728\u4eba\u5bb6\u5e2e\u5185\u6b63\u662f\u591a\u4e8b\u4e4b\u79cb\uff0c\u6211\u6709\u6069\u4e8e\u4ed6\u4eec\u4e10 \u5e2e\uff0c\u8fd9\u4e00\u51fa\u53e3\u76f8\u6c42\uff0c\u91d1\u8001\u5927\u5fc5\u7136\u4e0d\u4fbf\u63a8\u8f9e\u3002\u5509\uff0c\u7f62\u4e86\uff0c\u7f62\u4e86\uff0c\u6211\u4f55\u5fc5\u4ee4\u522b\u4eba\u4e3a\u96be\u5462\uff1f\u6211\u7b54\u5e94 \u8fc7\u963f\u5170\uff0c\u6c38\u8fdc\u8981\u966a\u7740\u5979\u548c\u5927\u5a18\uff0c\u6211\u2026\u2026\u6211\u65e0\u8bba\u5728\u5929\u6daf\u6d77\u89d2\uff0c\u4e00\u5b9a\u8981\u628a\u5979\u627e\u56de\u6765\uff0c\u5982\u679c\u5979\u906d\u4e86 \u4e0d\u5e78\uff0c\u6211\u2026\u2026\u6211\u5c31\u968f\u5979\u53bb\u7f62\uff0c\u603b\u800c\u8a00\u4e4b\uff0c\u5929\u4e0b\u518d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u529b\u91cf\u80fd\u5c06\u6211\u4eec\u5206\u5f00\u7684\u4e86\u3002\u201d \u3000\u3000\u6708\u5149\u7167\u8fdb\u4e86\u7834\u673d\u7684\u7a97\u68c2\uff0c\u91d1\u8001\u5927\u89c1\u51cc\u98ce\u4fca\u8138\u4e0a\u95ea\u8fc7\u4e00\u9635\u575a\u6bc5\u795e\u8272\u2014\u2014\u867d\u7136\uff0c\u90a3\u53ea\u662f\u4e00\u5239 \u90a3\uff0c\u53ef\u662f\uff0c\u91d1\u8001\u5927\u5374\u80fd\u611f\u89c9\u5230\u4e00\u79cd\u65e0\u6bd4\u7684\u51db\u7136\u4e4b\u6c14\u2026\u2026 \u3000\u3000\u51cc\u98ce\u5ffd\u9053\uff1a\u201c\u660e\u513f\u5927\u5bb6\u90fd\u8981\u8d76\u8def\uff0c\u54b1\u4eec\u8fd9\u5c31\u4f11\u606f\u5427\uff01\u201d \u3000\u3000\u9e4f\u513f\u70b9\u70b9\u5934\uff0c\u5411\u76ef\u7740\u5b64\u706f\u53d1\u75f4\u7684\u91d1\u8001\u5927\u671b\u4e86\u671b\u9053\uff1a\u201c\u91d1\u53d4\u53d4\uff0c\u6211\u4eec\u7761\u5427\uff01\u201d \u3000\u3000\u91d1\u8001\u5927\u70b9\u70b9\u5934\uff0c\u5439\u7184\u4e86\u9762\u524d\u6cb9\u706f\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u6162\u6162\u8d70\u5230\u5899\u8fb9\u3002 \u3000\u3000\u6708\u5149\u4e0b\uff0c\u4ed6\u957f\u5927\u7684\u8eab\u8eaf\uff0c\u663e\u5f97\u6709\u4e9b\u9f99\u949f\uff01\u80cc\u540e\u7684\u5f71\u5b50\uff0c\u66f4\u5927\u5f97\u6015\u4eba\u4e86\u3002 \u3000\u3000\u7fcc\u6668\uff0c\u5434\u51cc\u98ce\u5306\u5306\u522b\u8fc7\u91d1\u8001\u5927\u4e0e\u9e4f\u513f\uff0c\u4ed6\u5bf9\u91d1\u8001\u5927\u6781\u662f\u5c0a\u656c\uff0c\u5bf9\u9e4f\u513f\u4e5f\u751a\u559c\u7231\uff0c\u539f\u60f3\u591a \u9017\u7559\uff0c\u53ef\u662f\u4e00\u770b\u5230\u91d1\u8001\u5927\u5c06\u8981\u57cb\u846c\u91d1\u8001\u4e8c\uff0c\u4fbf\u8d76\u7d27\u544a\u522b\u3002 \u3000\u3000\u4ed6\u5fc3\u60f3\uff1a\u201c\u4ece\u6b64\uff0c\u8fd9\u5bf9\u4e00\u751f\u672a\u66fe\u987b\u5965\u76f8\u79bb\u7684\u5144\u5f1f\uff0c\u4fbf\u8981\u751f\u6b7b\u6c38\u522b\u4e86\uff0c\u8fd9\u662f\u591a\u4e48\u4ee4\u4eba\u60b2\u54c0 \u96be\u582a\u554a\uff01\u6211\u8fd9\u4e00\u751f\uff0c\u6b22\u4e50\u7684\u65e5\u5b50\u662f\u90a3\u4e48\u5c11\u2014\u2014\u4e5f\u8bb8\u6c38\u8fdc\u4e0d\u518d\u6709\u4e86\u5427\uff0c\u53ef\u662f\u82e6\u96be\u7684\u65e5\u5b50\uff0c\u5374\u662f \u6f2b\u6f2b\u65e0\u5c3d\u7684\uff0c\u6211\u611f\u60c5\u7684\u62c5\u8d1f\uff0c\u5df2\u7ecf\u91cd\u5f97\u8981\u538b\u4f4f\u6211\u7684\u547c\u5438\u4e86\uff0c\u4f55\u5fc5\u8981\u518d\u770b\u8fd9\u751f\u79bb\u6b7b\u522b\u51c4\u60e8\u7684\u60c5 \u666f\u3002\u201d \u3000\u3000\u4ed6\u4f9d\u7167\u7740\u539f\u6765\u7684\u8ba1\u5212\u6f2b\u6b65\u8fdb\u4e86\u6d1b\u9633\u57ce\uff0c\u5df2\u662f\u664c\u5348\u65f6\u5206\uff0c\u5c31\u627e\u4e86\u4e00\u5bb6\u5e72\u51c0\u9152\u697c\uff0c\u9009\u4e86\u4e00\u5904 \u4e34\u7a97\u684c\u5b50\u5750\u4e0b\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u6574\u4e2a\u9152\u697c\u4e0a\u7684\u5ba2\u4eba\u90fd\u4e0d\u7ea6\u800c\u540c\u7684\u5411\u697c\u68af\u671b\u53bb\uff0c\u51cc\u98ce\u4e0d\u89c9\u751a\u662f\u597d\u5947\uff0c\u4e3e\u773c\u4e00\u770b\uff0c\u697c \u68af\u5c3d\u5904\uff0c\u4fcf\u751f\u751f\u7ad9\u7740\u4e00\u4e2a\u5341\u4e94\u516d\u5c81\u7684\u5c11\u5973\u3002 \u3000\u3000\u51cc\u98ce\u671b\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u90a3\u59d1\u5a18\u53cc\u76ee\u6df1\u5982\u7ff0\u6d77\uff0c\u6e05\u5982\u79cb\u6c34\uff0c\u767d\u7389\u822c\u7684\u9762\u988a\uff0c\u900f\u51fa\u6d45\u6d45\u7ea2\u6655\uff0c \u8fd8\u6302\u7740\u5929\u771f\u7684\u7b11\u610f\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u6574\u4e2a\u9152\u8086\u90fd\u53d8\u5f97\u9759\u6084\u6084\u7684\uff0c\u5927\u5bb6\u90fd\u88ab\u8fd9\u5c11\u5973\u7edd\u4e16\u5bb9\u5149\u6240\u9707\uff0c\u5728\u5979\u8138\u4e0a\uff0c\u6709\u4e00\u79cd\u5b89 \u8be6\u7684\u6c14\u6c1b\uff0c\u6709\u4e00\u79cd\u98d8\u9038\u7684\u7f8e\u8273\u3002 \u3000\u3000\u5e74\u8001\u7684\u9152\u5ba2\u5fc3\u91cc\u90fd\u60f3\uff1a\u201c\u6211\u5982\u679c\u6709\u8fd9\u4e48\u4e00\u4e2a\u7389\u96ea\u53ef\u7231\u7684\u5973\u513f\u8be5\u6709\u591a\u597d\u3002\u201d \u3000\u3000\u5e74\u8f7b\u7684\u9152\u5ba2\u5fc3\u9053\uff1a\u201c\u6211\u5982\u679c\u6709\u8fd9\u6837\u4e00\u4e2a\u53ef\u7231\u7684\u59b9\u59b9\u2026\u2026\u201d\u4ed6\u4eec\u5e76\u672a\u6562\u60f3\u5230\u5176\u4ed6\uff0c\u56e0\u4e3a\u90a3 \u5c11\u5973\u81f3\u7f8e\u4e4b\u4e2d\u8fd8\u663e\u51fa\u4e00\u79cd\u4ee4\u4eba\u671b\u800c\u751f\u656c\u7684\u9ad8\u8d35\u3002 \u3000\u3000\u51cc\u98ce\u4e5f\u89c9\u5f97\u90a3\u5c11\u5973\u53ef\u7231\u4e4b\u6781\uff0c\u4e0d\u7531\u5f97\u591a\u770b\u4e86\u4e24\u773c\uff0c\u90a3\u5c11\u5973\u4f3c\u4e4e\u5bdf\u89c9\u4e86\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u8d70\u5230 \u51cc\u98ce\u9762\u524d\u9053\uff1a \u3000\u3000\u201c\u5582\uff0c\u4f60\u77a7\u6211\u5e72\u5417\uff1f\u4f60\u77e5\u4e0d\u77e5\u9053\u6211\u8f9b\u5927\u54e5\u73b0\u5728\u5728\u54ea\u513f\uff1f\u201d \u3000\u3000\u51cc\u98ce\u53d1\u73b0\u5927\u5bb6\u773c\u5149\u90fd\u5411\u4ed6\u5c04\u8fc7\u6765\uff0c\u5fc3\u4e2d\u5927\u611f\u5c34\u5c2c\uff0c\u7adf\u7136\u6ca1\u6709\u542c\u6e05\u5979\u7684\u95ee\u8bdd\u3002 \u3000\u3000\u51cc\u98ce\u8d77\u8eab\u95ee\u9053\uff1a\u201c\u4f60\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u89c1\u4ed6\u4fca\u8138\u901a\u7ea2\uff0c\u672c\u60f3\u8d23\u95ee\u4ed6\u4e3a\u4ec0\u4e48\u6ca1\u6709\u542c\u6e05\u81ea\u5df1\u6240\u8bb2\uff0c\u8bdd\u5230\u53e3\u8fb9\uff0c\u53c8\u5fcd\u4f4f\u4e86\uff0c\u67d4 \u58f0\u9053\uff1a\u201c\u6211\u95ee\u4f60\u4e00\u4e2a\u59d3\u8f9b\u2026\u2026\u59d3\u8f9b\u7684\u5927\u54e5\uff0c\u4ed6\u2026\u2026\u4ed6\u773c\u775b\u5927\u5927\u7684\u3002\u201d \u3000\u3000\u51cc\u98ce\u51b2\u53e3\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u95ee\u7684\u53ef\u662f\u8f9b\u6377\u5f1f\u5417\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u7b11\u9765\u5982\u82b1\uff0c\u50cf\u662f\u6b22\u559c\u5df2\u6781\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u6b63\u662f\u8f9b\u2026\u2026\u8f9b\u6377\u5927\u54e5\uff0c\u4ed6\u662f\u4f60\u5f1f\u5f1f\uff0c\u90a3\uff0c \u90a3\u518d\u597d\u4e5f\u6ca1\u6709\uff0c\u4f60\u5feb\u5e26\u6211\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u8fd9\u65f6\u9152\u697c\u4e2d\u8bae\u8bba\u7eb7\u7eb7\uff0c\u4e00\u4e9b\u5fe0\u539a\u957f\u8005\uff0c\u90fd\u53d1\u51fa\u4f1a\u5fc3\u5fae\u7b11\uff0c\u4ed6\u4eec\u90fd\u89c9\u5f97\u8fd9\u5c11\u5973\u56fa\u7136\u5982\u6ee8\u6c34 \u767d\u83b2\u660e\u8273\u4e0d\u53ef\u65b9\u7269\uff0c\u90a3\u5c11\u5e74\u4e5f\u5982\u4e34\u98ce\u7389\u6811\uff0c\u4fca\u7f8e\u5df2\u6781\uff0c\u771f\u662f\u4e00\u5bf9\u74a7\u4eba\uff0c\u6240\u4ee5\u90fd\u6697\u6697\u4e3a\u4ed6\u4eec\u4e8c \u4eba\u559c\u6b22\u3002 \u3000\u3000\u90a3\u4e9b\u5e74\u8f7b\u7684\u4eba\uff0c\u770b\u5230\u90a3\u5c11\u5973\u51d1\u8fd1\u90a3\u5c11\u5e74\u6709\u8bf4\u6709\u7b11\uff0c\u5fc3\u4e2d\u9887\u6709\u9178\u610f\uff0c\u4f46\u4e00\u4e3e\u76ee\uff0c\u53ea\u89c1\u51cc\u98ce \u4fca\u8138\u95ea\u51fa\u7684\u5149\u8f89\uff0c\u518d\u4e00\u6253\u91cf\u81ea\u5df1\uff0c\u4e0d\u89c9\u4e00\u4e2a\u4e2a\u9762\u5982\u6b7b\u7070\uff0c\u81ea\u6127\u4e0d\u5982\u3002\u4f46\u4e00\u542c\u5230\u90a3\u5c11\u5973\u53e3\u53e3\u58f0 \u58f0\u6253\u542c\u53e6\u4e00\u4e2a\u7537\u5b50\uff0c\u5fc3\u4e2d\u90fd\u89c9\u60ca\u5947\uff0c\u4eba\u4eba\u90fd\u6697\u60f3\uff1a\u201c\u4e0d\u77e5\u90a3\u59d3\u8f9b\u7684\u5c0f\u5b50\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u7adf\u503c\u5f97 \u5979\u8fd9\u4e48\u5173\u5fc3\uff0c\u5509\uff0c\u8fd9\u6837\u7684\u59d1\u5a18\uff0c\u5982\u679c\u53ea\u8981\u2026\u2026\u8981\u6709\u4e00\u534a\u8fd9\u6837\u5173\u5fc3\u6211\uff0c\u5c31\u53eb\u6211\u6b7b\uff0c\u4e5f\u662f\u5fc3\u7518\u60c5 \u613f\u3002\u201d \u3000\u3000\u4f17\u9752\u5e74\u4e0d\u7ea6\u800c\u540c\u5730\u6f02\u4e86\u51cc\u98ce\u4e00\u773c\uff0c\u5fae\u5e26\u6311\u62e8\u8ba5\u8bbd\u7684\u4e00\u773c\uff0c\u90a3\u610f\u601d\u8bf4\uff1a\u201c\u5c0f\u5b50\uff0c\u4f60\u522b\u5f97 \u610f\uff0c\u90a3\u59d1\u5a18\u53e6\u6709\u610f\u4e2d\u4eba\u54e9\uff01\u201d \u3000\u3000\u5434\u51cc\u98ce\u4e0d\u7406\u4f1a\u4f17\u4eba\u76ee\u5149\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u4f60\u53ef\u662f\u59d3\u91d1\uff0c\u8fd8\u662f\u59d3\u65b9\uff1f\u201d \u3000\u3000\u90a3\u5c11\u5973\u5927\u773c\u4e00\u8f6c\uff0c\u5947\u9053\uff1a\u201c\u6211\u59d3\u5f20\uff0c\u5582\uff0c\u4f60\u600e\u4e48\u4f1a\u4ee5\u4e3a\u6211\u59d3\u91d1\u6216\u59d3\u65b9\u5462\uff1f\u201d \u3000\u3000\u51cc\u98ce\u89c1\u5979\u6ee1\u9762\u7126\u6025\u6000\u7591\u4e4b\u8272\uff0c\u5fc3\u4e2d\u609a\u7136\u4e00\u60ca\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u59d1\u5a18\u5bf9\u6377\u5f1f\u751a\u662f\u5173\u5fc3\uff0c\u90a3\u6b21\u6377 \u5f1f\u75c5\u4e2d\u68a6\u8bed\uff0c\u53ea\u6015\u662f\u80e1\u8a00\u4e71\u8bed\uff0c\u6211\u5207\u4e0d\u53ef\u8bf4\u51fa\uff0c\u4f24\u8fd9\u4f4d\u53ef\u7231\u59d1\u5a18\u7684\u5fc3\u3002\u201d \u3000\u3000\u4ed6\u5e72\u54b3\u4e00\u58f0\uff0c\u7b11\u9053\uff1a\u201c\u6211\u6709\u2026\u2026\u4e00\u4e2a\u59d3\u65b9\u7684\u670b\u53cb\uff0c\u957f\u5f97\u5f88\u50cf\u4f60\u3002\u201d \u3000\u3000\u4ed6\u4e00\u89c1\u8fd9\u5c11\u5973\uff0c\u5fc3\u91cc\u4fbf\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u4eb2\u5207\uff0c\u53ea\u89c9\u81ea\u5df1\u5e94\u8be5\u5904\u5904\u4fdd\u62a4\u5979\uff0c\u4e0d\u8ba9\u5979\u53d7\u4e1d\u6beb \u635f\u5bb3\uff0c\u662f\u4ee5\u4e3a\u4e86\u5b89\u6170\u5979\uff0c\u7adf\u7834\u4f8b\u8bf4\u4e86\u4e00\u6b21\u8c0e\u3002 \u3000\u3000\u539f\u6765\uff0c\u90a3\u5c11\u5973\u6b63\u662f\u4ece\u65e0\u6781\u5c9b\u6e9c\u51fa\u6765\u7684\u83c1\u513f\uff0c\u5979\u81ea\u4ece\u4e0a\u6b21\u8ddf\u7236\u4eb2\u65e0\u6068\u751f\u6bcd\u4eb2\u7f2a\u4e03\u5a18\u79bb\u5c9b\u5230 \u4e2d\u539f\u6765\uff0c\u867d\u7136\u5306\u5306\u8d76\u56de\uff0c\u4f46\u5979\u4ece\u5c0f\u4ece\u672a\u79bb\u8fc7\u65e0\u6781\u5c9b\uff0c\u5bf9\u4e2d\u539f\u4e00\u5207\uff0c\u5927\u611f\u5174\u8da3\uff0c\u800c\u4e14\u53c8\u7ed3\u8bc6\u4e86 \u4e00\u4e2a\u5927\u773c\u775b\u7684\u54e5\u54e5\u3002 \u3000\u3000\u4e00\u60f3\u5230\u90a3\u5927\u773c\u775b\u54e5\u54e5\uff0c\u5979\u5fc3\u4e2d\u4fbf\u611f\u559c\u60a6\uff0c\u540e\u6765\u6bcd\u5973\u88ab\u7389\u9aa8\u9b54\u64d2\u4f4f\u70b9\u4e86\u660f\u7a74\uff0c\u5f53\u7236\u4eb2\u65e0\u6068 \u751f\u89e3\u5f00\u5979\u7a74\u9053\u65f6\uff0c\u5979\u7b2c\u4e00\u773c\u4fbf\u77a7\u5230\u90a3\u53cc\u5927\u773c\u2014\u2014\u90a3\u53cc\u5145\u6ee1\u4e86\u5979\u4e0d\u80fd\u4e86\u89e3\u7684\u60c5\u610f\u7684\u5927\u773c\uff0c\u867d \u7136\uff0c\u4ed6\u4e0d\u4e86\u89e3\u90a3\u773c\u4e2d\u7684\u771f\u610f\uff0c\u53ef\u662f\u5728\u5979\u5fc3\u5e95\u4e0b\u5374\u6cdb\u8d77\u4e86\u4e1d\u4e1d\u751c\u5473\u3002 \u3000\u3000\u5979\u968f\u7740\u7236\u6bcd\u8fd4\u56de\u65e0\u6781\u5c9b\uff0c\u5fc3\u4e2d\u5341\u5206\u4e0d\u820d\uff0c\u5728\u5c9b\u4e0a\u4f4f\u4e86\u4e00\u4f1a\uff0c\u53ea\u89c9\u5c9b\u4e0a\u4e00\u5207\u90fd\u5f88\u65e0\u804a\uff0c\u5fc3 \u91cc\u53ea\u662f\u60f3\u5230\u4e2d\u539f\u98ce\u5149\u4e0e\u90a3\u5927\u773c\u775b\u54e5\u54e5\uff0c\u6700\u540e\u7ec8\u4e8e\u5fcd\u8010\u4e0d\u4f4f\uff0c\u4e58\u7740\u7236\u6bcd\u4eb2\u4e0d\u6ce8\u610f\uff0c\u5077\u5077\u6e9c\u4e86\u51fa \u6765\u3002 \u3000\u3000\u5979\u672c\u4e0d\u77e5\u8f9b\u6377\u59d3\u540d\uff0c\u4f46\u5728\u5c9b\u4e0a\u65e0\u610f\u95f4\u542c\u5230\u7236\u4eb2\u63d0\u8d77\uff0c\u4fbf\u7262\u8bb0\u5fc3\u4e2d\uff0c\u4e00\u8def\u4e0a\uff0c\u78b0\u7740\u4eba\u4fbf\u95ee \u5979\u8f9b\u5927\u54e5\u5728\u4f55\u5904\uff0c\u4e5f\u4e0d\u77e5\u95f9\u4e86\u591a\u5c11\u7b11\u8bdd\u3002\u5979\u81ea\u5e7c\u751f\u957f\u6d77\u5916\u5b64\u5c9b\uff0c\u53c8\u5728\u7236\u6bcd\u5375\u7ffc\u4e4b\u4e0b\uff0c\u5bf9\u4e8e\u4e16 \u4e8b\u53ef\u8c13\u4e00\u7a8d\u4e0d\u901a\uff0c\u843d\u5e97\u6295\u5bbf\uff0c\u4ece\u6765\u4e0d\u77e5\u8981\u4ed8\u4ec0\u4e48\u94b1\uff0c\u5403\u5b8c\u4f4f\u5b8c\u5c31\u8d70\uff0c\u4eba\u5bb6\u89c1\u5979\u5929\u771f\u8c8c\u7f8e\uff0c\u90fd \u8ba9\u5979\u4e09\u5206\uff0c\u662f\u4ee5\u4e00\u8def\u6765\uff0c\u5e76\u6ca1\u6709\u5403\u4ec0\u4e48\u4e8f\u3002 \u3000\u3000\u8fd9\u65e5\u5728\u9152\u697c\u4e0a\u89c1\u51cc\u98ce\u671b\u5979\uff0c\u53c8\u89c9\u51cc\u98ce\u751a\u662f\u4fca\u79c0\u53ef\u4eb2\uff0c\u4fbf\u5411\u4ed6\u6253\u542c\uff0c\u6ca1\u60f3\u5230\u4e71\u78b0\u4e71\u649e\uff0c\u5374 \u6b63\u597d\u78b0\u5bf9\u4e86\u4eba\u3002 \u3000\u3000\u83c1\u513f\u9053\uff1a\u201c\u90a3\u4e48\u8f9b\u5927\u54e5\u73b0\u5728\u5728\u54ea\u513f\uff1f\u201d \u3000\u3000\u51cc\u98ce\u89c1\u5979\u4e0d\u518d\u8ffd\u95ee\u81ea\u5df1\u5931\u8a00\uff0c\u5fc3\u4e2d\u5982\u91ca\u8d1f\u91cd\uff0c\u5fd9\u9053\uff1a\u201c\u6377\u5f1f\u5df1\u7ecf\u8ddf\u5e73\u51e1\u4e0a\u4eba\u53bb\u5927\u6222\u5c9b\u53bb \u4e86\u3002\u201d \u3000\u3000\u83c1\u513f\u559c\u9053\uff1a\u201c\u539f\u6765\u4ed6\u8ddf\u90a3\u8001\u548c\u5c1a\u4f2f\u4f2f\u53bb\u4e86\u4e1c\u6d77\uff0c\u548c\u5c1a\u4f2f\u4f2f\u6b66\u529f\u53ef\u9ad8\u5f97\u5f88\u554a\uff01\u201d \u3000\u3000\u51cc\u98ce\u542c\u4ed6\u53eb\u5e73\u51e1\u4e0a\u4eba\u4e3a\u548c\u5c1a\u4f2f\u4f2f\uff0c\u5fc3\u4e2d\u6697\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u59d1\u5a18\u5929\u771f\u5df2\u6781\uff0c\u6beb\u65e0\u5fc3\u673a\uff0c\u53ef \u662f\u4e00\u63d0\u5230\u4e0e\u6377\u5f1f\u76f8\u8bc6\u7684\u59d1\u5a18\uff0c\u5979\u4fbf\u7126\u6025\u4e0d\u60a6\uff0c\u770b\u6765\u5973\u5b50\u7684\u5ac9\u5fcc\u4e4b\u5fc3\uff0c\u662f\u5929\u751f\u5c31\u6709\u7684\uff0c\u963f\u5170\uff0c \u963f\u5170\uff0c\u6211\u4e0e\u90a3\u82cf\u59d1\u5a18\u4e5f\u4e0d\u8fc7\u53ea\u662f\u76f8\u8bc6\uff0c\u4f60\u53c8\u4f55\u5fc5\u8d1f\u6c14\u800c\u8d70\u5462\uff1f\u201d \u3000\u3000\u5979\u4e00\u60f3\u5230\u963f\u5170\uff0c\u5fc3\u5185\u4fbf\u611f\u4f24\u75db\uff0c\u7acb\u523b\u9eef\u7136\u4e0d\u8bed\u3002 \u3000\u3000\u83c1\u513f\u9053\uff1a\u201c\u5582\uff0c\u4f60\u600e\u4e48\u4e0d\u9ad8\u5174\u4e86\uff0c\u4f60\u59d3\u4ec0\u4e48\u5440\uff1f\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u6211\u59d3\u5434\uff0c\u540d\u53eb\u51cc\u98ce\u3002\u201d \u3000\u3000\u83c1\u513f\u9053\uff1a\u201c\u6211\u53eb\u5f20\u83c1\uff0c\u4f60\u5c31\u53eb\u6211\u83c1\u513f\u597d\u5566\uff01\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u4f60\u8f9b\u5927\u54e5\u53bb\u4e86\u5df2\u7ecf\u4e00\u4e2a\u591a\u6708\u4e86\uff0c\u73b0\u5728\u53ea\u6015\u8981\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u83c1\u513f\u6025\u9053\uff1a\u201c\u6211\u8fd9\u5c31\u53bb\u5927\u6222\u5c9b\uff0c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d \u3000\u3000\u51cc\u98ce\u6697\u5fd6\u81ea\u5df1\u672c\u6765\u5c31\u8981\u5f80\u6cb3\u5357\u5317\u65b9\u5bfb\u8bbf\uff0c\u6b63\u597d\u987a\u8def\u3002 \u3000\u3000\u4fbf\u9053\uff1a\u201c\u6211\u53ea\u80fd\u966a\u4f60\u8d70\u5230\u6c5f\u82cf\u8fb9\u5883\u201d \u3000\u3000\u83c1\u513f\u9053\uff1a\u201c\u90a3\u4e5f\u597d\uff0c\u54b1\u4eec\u5c31\u52a8\u8eab\u5427\u3002\u201d \u3000\u3000\u51cc\u98ce\u5306\u5306\u4ed8\u4e86\u9152\u5e10\uff0c\u4fbf\u548c\u83c1\u513f\u5411\u5317\u8d76\u53bb\u3002 \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u83c1\u513f\u8c08\u7684\u5c3d\u662f\u81ea\u5bb6\u5728\u65e0\u6781\u5c9b\u4e0a\u7684\u8da3\u4e8b\uff0c\u683d\u82b1\u3001\u79cd\u8349\u3001\u6355\u9c7c\u3001\u6349\u866b\uff0c\u51cc\u98ce\u81ea\u4ece\u79bb \u5f00\u5927\u5a18\u6bcd\u5973\u7ec8\u65e5\u4fbf\u5728\u5200\u67aa\u5c16\u4e0a\u6253\u6eda\uff0c\u6b64\u65f6\u542c\u5979\u5a13\u5a13\u9053\u6765\uff0c\u771f\u6709\u201c\u604d\u82e5\u9694\u4e16\u201d\u4e4b\u611f\u3002 \u3000\u3000\u83c1\u513f\u9053\uff1a\u201c\u65e0\u6781\u5c9b\u771f\u5927\uff0c\u4e0a\u9762\u904d\u5730\u9c9c\u82b1\uff0c\u975e\u5e38\u597d\u770b\uff0c\u53ea\u662f\u5c9b\u4e0a\u53ea\u6709\u7239\u5988\u548c\u6211\uff0c\u7239\u7239\u4e00\u5929 \u5230\u665a\uff0c\u4e0d\u662f\u8bfb\u4e66\uff0c\u5c31\u662f\u7ec3\u6b66\uff0c\u6211\u53ea\u6709\u8ddf\u5988\u5988\u73a9\uff0c\u54ea\u5929\u4f60\u548c\u8f9b\u5927\u54e5\u4e00\u8d77\u6765\uff0c\u4f4f\u4e0a\u51e0\u4e2a\u6708\u966a\u6211 \u73a9\uff0c\u90a3\u6709\u591a\u597d\u54e9\uff01\u201d \u3000\u3000\u51cc\u98ce\u89c1\u5979\u4e00\u8138\u7948\u6c42\u4e4b\u8272\uff0c\u5fd9\u9053\uff1a\u201c\u6211\u4e00\u5b9a\u5e38\u5e38\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u83c1\u513f\u53f9\u4e86\u4e00\u53e3\u6c14\u9053\uff1a\u201c\u7239\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u597d\u50cf\u5f88\u8ba8\u538c\u8f9b\u5927\u54e5\uff0c\u6211\u5c31\u6015\u7239\u7239\u4e0d\u51c6\u6211\u548c\u4ed6 \u73a9\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u4e0d\u4f1a\u7684\uff0c\u8f9b\u6377\u5f1f\u6b66\u529f\u65e2\u9ad8\uff0c\u4eba\u53c8\u806a\u660e\uff0c\u4f60\u7239\u7239\u5c06\u6765\u4e00\u5b9a\u4f1a\u559c\u6b22\u4ed6\u3002\u201d \u3000\u3000\u83c1\u513f\u542c\u51cc\u98ce\u8d5e\u8f9b\u6377\uff0c\u5fc3\u4e2d\u5f88\u611f\u53d7\u7528\uff0c\u63a5\u53e3\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8fd9\u4e48\u60f3\uff0c\u8f9b\u5927\u54e5\u548c\u4f60\u90fd\u662f\u4e16\u754c \u4e0a\u6700\u597d\u3001\u6700\u597d\u7684\u4eba\u3002\u201d \u3000\u3000\u51cc\u98ce\u5ffd\u9053\uff1a\u201c\u4f60\u7239\u7239\u540d\u5217\u2018\u4e16\u5916\u4e09\u4ed9\u2019\uff0c\u6b66\u529f\u4e00\u5b9a\u9ad8\u5f97\u4e0d\u5f97\u4e86\uff0c\u4f60\u8fd9\u6837\u806a\u660e\uff0c\u4e00\u5b9a\u5f97\u5230 \u4e0d\u5c11\u7edd\u5b66\u5427\uff01\u201d \u3000\u3000\u83c1\u513f\u9053\uff1a\u201c\u7239\u7239\u5e38\u9a82\u6211\u4e0d\u7528\u5fc3\u5b66\u6b66\uff0c\u5988\u8bf4\u5973\u5b69\u5b50\u53c8\u4e0d\u4e0e\u4eba\u52a8\u624b\uff0c\u4e0d\u9700\u8981\u6b66\u529f\u592a\u9ad8\uff0c\u7239\u5c31 \u4e0d\u8feb\u6211\u7ec3\u5566\uff0c\u53ea\u53eb\u6211\u7ec3\u8f7b\u529f\u3002\u201d \u3000\u3000\u51cc\u98ce\u8d5e\u9053\uff1a\u201c\u602a\u4e0d\u5f97\u4f60\u8f7b\u529f\u771f\u597d\u3002\u201d \u3000\u3000\u83c1\u513f\u5ae3\u7136\u4e00\u7b11\u3002 \u3000\u3000\u4e24\u4eba\u5bb5\u884c\u591c\u5bbf\uff0c\u611f\u60c5\u5f88\u662f\u878d\u6d3d\uff0c\u51cc\u98ce\u5904\u5904\u4ee5\u5927\u54e5\u81ea\u5c45\uff0c\u7ec6\u5fc3\u5475\u62a4\u5979\uff0c\u4e0d\u8ba9\u5979\u53d7\u4e1d\u6beb\u59d4 \u66f2\u3002 \u3000\u3000\u884c\u4e86\u51e0\u65e5\uff0c\u83c1\u513f\u5fc3\u6025\u8d76\u56de\uff0c\u5979\u5acc\u5927\u8def\u592a\u8fdc\uff0c\u4fbf\u548c\u51cc\u98ce\u65bd\u5c55\u8f7b\u529f\uff0c\u7ffb\u5c71\u8d8a\u5cad\uff0c\u6cb3\u5357\u5883\u5185\uff0c \u5c71\u8109\u751a\u662f\u5d0e\u5c96\uff0c\u4f46\u6b64\u4e24\u4eba\u4f55\u7b49\u529f\u592b\uff0c\u662f\u4ee5\u5982\u5c65\u5e73\u5730\u3002 \u3000\u3000\u8fd9\u65e5\uff0c\u8d70\u8fc7\u82cf\u5dde\uff0c\u5df2\u8fd1\u6d77\u8fb9\uff0c\u4e24\u4eba\u89c1\u5929\u8272\u5df2\u665a\uff0c\u5c31\u627e\u4e86\u4e00\u4e2a\u5c71\u6d1e\uff0c\u5750\u4e0b\u4f11\u606f\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u521d\u51ac\uff0c\u5929\u6c14\u751a\u4e3a\u5bd2\u51b7\uff0c\u51cc\u98ce\u5288\u4e86\u51e0\u6839\u6811\u679d\uff0c\u5728\u6d1e\u524d\u751f\u4e86\u706b\u6765\uff0c\u83c1\u513f\u4ece\u5305\u88b1\u4e2d\u53d6 \u51fa\u5e72\u7cae\uff0c\u5206\u4e00\u5927\u534a\u7ed9\u51cc\u98ce\uff0c\u4e24\u4eba\u5c31\u5750\u5728\u706b\u65c1\u9ed8\u9ed8\u5403\u4e86\u8d77\u6765\u3002 \u3000\u3000\u51cc\u98ce\u89c1\u83c1\u513f\u9ed8\u7136\u4e0d\u8bed\uff0c\u706b\u5149\u7167\u5f97\u5979\u7684\u5c0f\u8138\u7ea2\u7ea2\u7684\uff0c\u5c0f\u5634\u5fae\u7fd8\uff0c\u795e\u8272\u5f88\u662f\u9ed4\u7136\uff0c\u5fc3\u77e5\u5979\u4e0d \u820d\u660e\u65e5\u76f8\u522b\u3002\u60f3\u9053\uff1a\u201c\u8fd9\u59d1\u5a18\u5fc3\u5730\u771f\u662f\u6148\u7965\uff0c\u8f9b\u6377\u5f1f\u771f\u597d\u798f\u6c14\uff0c\u4ed6\u65e5\u78b0\u5230\u6377\u5f1f\uff0c\u6211\u8981\u597d\u597d\u529d \u4ed6\uff0c\u53ef\u8981\u4e00\u5fc3\u4e00\u610f\u7231\u7740\u8fd9\u4f4d\u59d1\u5a18\u3002\u54fc\uff0c\u4ec0\u4e48\u4eba\u4f1a\u6bd4\u5979\u66f4\u53ef\u7231\u5462\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u53c8\u6d6e\u8d77\u4e86\u963f\u5170\u7684\u60c5\u5f71\uff0c\u201c\u53ea\u6709\u963f\u5170\uff0c\u624d\u53ef\u4e0e\u5979\u6bd4\u7f8e\u3002\u201d\u4ed6\u60f3\u3002 \u3000\u3000\u5929\u4e0a\u7b2c\u4e00\u9897\u5c0f\u661f\u51fa\u73b0\u4e86\uff0c\u63a5\u7740\uff0c\u6708\u4eae\u4e5f\u722c\u4e0a\u4e86\u5c71\u5cf0\u3002 \u3000\u3000\u51cc\u98ce\u6253\u5f00\u8d34\u8eab\u800c\u85cf\u7684\u5c0f\u5305\uff0c\u53d6\u51fa\u4e00\u5f20\u4fe1\u7eb8\uff0c\u4ed6\u4e00\u904d\u53c8\u4e00\u904d\u7684\u770b\u7740\u4fe1\u4e0a\u7684\u53e5\u5b50\u2026\u2026 \u3000\u3000\u201c\u5927\u54e5\uff0c\u6211\u4e0d\u6c14\u4f60\uff0c\u6211\u771f\u7684\u4e0d\u6c14\u4f60\u2026\u2026\u82cf\u59d1\u5a18\u662f\u5f88\u597d\u7684\u59d1\u5a18\uff0c\u5979\u662f\u771f\u5fc3\u559c\u6b22\u4f60\u7684\uff0c\u4f60\u548c \u5979\u597d\u5427\uff0c\u4f60\u5343\u4e07\u4e0d\u8981\u518d\u60e6\u5ff5\u6211\u8fd9\u4e2a\u50bb\u4e2b\u5934\u4e86\u3002 \u3000\u3000\u5927\u54e5\uff0c\u6211\u8981\u8d70\u4e86\uff0c\u6211\u867d\u7136\u8d70\u5f97\u8fdc\u8fdc\u7684\uff0c\u53ef\u662f\uff0c\u5927\u54e5\uff0c\u963f\u5170\u8fd8\u662f\u5c5e\u4e8e\u4f60\u7684\uff0c\u5c31\u662f\u5343\u91cc\u4e07\u91cc \u5916\uff0c\u963f\u5170\u8fd8\u662f\u6c38\u8fdc\u795d\u798f\u4f60\u4eec\u2026\u2026\u201d \u3000\u3000\u51cc\u98ce\u770b\u4e86\u51e0\u904d\uff0c\u82e6\u601d\u90a3\u65e5\u4e0e\u82cf\u8559\u82b7\u76f8\u6664\u60c5\u5f62\uff0c\u518d\u4e5f\u60f3\u4e0d\u51fa\u4ec0\u4e48\u3002 \u3000\u3000\u201c\u963f\u5170\u7559\u4e66\u51fa\u8d70\uff0c\u4e00\u5b9a\u662f\u542c\u5230\u6211\u548c\u82cf\u59d1\u5a18\u8bf4\u4e86\u4ec0\u4e48\u4eb2\u70ed\u7684\u8bdd\uff0c\u53ef\u662f\u6211\u600e\u4e48\u60f3\u4e5f\u60f3\u4e0d\u51fa \u6765\uff0c\u96be\u9053\u6211\u90a3\u65e5\u9152\u540e\uff0c\u7adf\u771f\u7684\u505a\u51fa\u4ec0\u4e48\u5931\u793c\u7684\u4e8b\u5417\uff1f\u201d \u3000\u3000\u4ed6\u6108\u60f3\u6108\u662f\u5bb3\u6015\uff0c\u7adf\u7136\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\uff0c\u5fc3\u60f3\uff1a\u201c\u8981\u662f\u771f\u7684\u90a3\u6837\uff0c\u6211\u53c8\u600e\u5bf9\u5f97\u8d77\u82cf\u59d1 \u5a18\uff1f\u201d \u3000\u3000\u83c1\u513f\u7a81\u7136\u8bf4\u9053\uff1a\u201c\u5434\u5927\u54e5\uff0c\u4f60\u77a7\uff0c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51cc\u98ce\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u9897\u6d41\u661f\uff0c\u621b\u7136\u4e0b\u843d\uff0c\u5728\u5929\u7a7a\u4e2d\u5212\u51fa\u4e00\u9053\u91d1\u8272\u7684\u5149\u5f27\u3002 \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u8fd9\u662f\u6b92\u661f\u3002\u201d \u3000\u3000\u83c1\u513f\u70b9\u5934\u4e0d\u8bed\uff0c\u5185\u5fc3\u60f3\u9053\uff1a\u201c\u5988\u5988\u5e38\u8bf4\uff0c\u6bcf\u4e00\u9897\u661f\u5185\u5c31\u6709\u4e00\u4f4d\u4ed9\u4eba\uff0c\u8fd9\u4f4d\u4ed9\u4eba\uff0c\u4e0d\u77e5\u4e3a \u4e86\u4ec0\u4e48\uff0c\u7adf\u7136\u4e0d\u53bb\u505a\u4eba\u4eba\u7fa1\u6155\u7684\u795e\u4ed9\uff0c\u800c\u8981\u4e0b\u843d\u5230\u8fd9\u4e16\u4e0a\u6765\uff0c\u4e5f\u4e0d\u77e5\u662f\u7537\u4ed9\u8fd8\u662f\u5973\u4ed9\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u60f3\u9053\uff1a\u201c\u6211\u5c0f\u65f6\u5019\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u61c2\uff0c\u6574\u5929\u53ea\u662f\u73a9\u800d\uff0c\u6216\u7f20\u7740\u5988\u8bb2\u6545\u4e8b\uff0c\u7d2f\u4e86\u5c31\u8eba\u5728 \u8349\u5730\u4e0a\u7761\u4e00\u89c9\uff0c\u6e34\u4e86\u4fbf\u6458\u4e2a\u679c\u5b50\u6765\u5403\uff0c\u6211\u4ec0\u4e48\u90fd\u4e0d\u60f3\uff0c\u4ec0\u4e48\u90fd\u4e0d\u6015\uff0c\u53ea\u6709\u7238\u7238\u677f\u7740\u8138\u8feb\u6211\u7ec3 \u6b66\u529f\uff0c\u624d\u4f1a\u611f\u5230\u4e00\u4e1d\u5bb3\u6015\u3002\u53ef\u662f\uff0c\u8fd9\u6b21\u6211\u56de\u5230\u65e0\u6781\u5c9b\uff0c\u4e00\u5207\u4e1c\u897f\u90fd\u4e0d\u518d\u80fd\u4f7f\u6211\u53d1\u751f\u5174\u8da3\uff0c\u6211 \u53ea\u60f3\u7740\u8f9b\u5927\u54e5\uff0c\u62c5\u5fc3\u4ed6\u4e0d\u548c\u6211\u597d\uff0c\u5fc3\u4e2d\u771f\u662f\u82e6\u607c\u3002\u5509\uff0c\u96be\u9053\u4eba\u6108\u957f\u5927\uff0c\u4fbf\u6108\u4e0d\u5feb\u6d3b\u5417\uff1f\u201d \u3000\u3000\u5979\u5077\u773c\u4e00\u77a7\u51cc\u98ce\uff0c\u89c1\u4ed6\u624b\u4e2d\u62ff\u7740\u4e00\u5f20\u7eb8\uff0c\u6ee1\u8138\u7f20\u7ef5\u51c4\u607b\uff0c\u4fbf\u6084\u6084\u51d1\u8fd1\u53bb\u9053\uff1a\u201c\u5434\u5927\u54e5\uff0c \u4f60\u770b\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51cc\u98ce\u609a\u7136\u4e00\u63a0\uff0c\u8d76\u5fd9\u6536\u8d77\u963f\u5170\u7684\u4fe1\uff0c\u5f3a\u7b11\u9053\uff1a\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u8bf4\u6211\u4eec\u660e\u513f\u5c31\u8981\u5206\u624b\uff0c\u4f60\u5f97 \u5c3d\u5feb\u8d76\u53bb\uff0c\u5426\u5219\u53ea\u6015\u4f1a\u548c\u6377\u5f1f\u9519\u8fc7\u3002\u201d \u3000\u3000\u83c1\u513f\u4eba\u867d\u5929\u771f\uff0c\u4f46\u5374\u6781\u4e3a\u806a\u660e\uff0c\u4e00\u8def\u4e0a\u5979\u5df2\u53d1\u89c9\u51cc\u98ce\u867d\u7136\u6709\u8bf4\u6709\u7b11\uff0c\u53ef\u662f\u6bcf\u5f53\u4ed6\u4e00\u4e2a\u72ec \u5904\u65f6\uff0c\u603b\u662f\u795e\u8272\u60b2\u82e6\u3002\u5979\u95ee\u4e86\u51e0\u6b21\uff0c\u51cc\u98ce\u90fd\u662f\u652f\u543e\u4ee5\u5bf9\u3002\u5979\u5fc3\u60f3\uff1a\u201c\u4ed6\u6b66\u827a\u65e2\u9ad8\uff0c\u4eba\u53c8\u90a3\u4e48 \u4fca\u79c0\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u4f7f\u4ed6\u4e0d\u6ee1\u610f\u5462\uff1f\u6211\u4e0d\u5fc5\u5411\u4ed6\u8ffd\u95ee\uff0c\u4ee5\u514d\u5f15\u8d77\u4ed6\u4f24\u5fc3\uff0c\u7b49\u78b0\u5230\u8f9b\u6377\u5927\u54e5\uff0c\u5411 \u4ed6\u6253\u542c\uff0c\u90a3\u4fbf\u5f97\u4e86\u3002\u201d\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u5929\u771f\u7684\u5979\u7adf\u4e5f\u6653\u5f97\u76d8\u7b97\u4e86\u3002 \u3000\u3000\u83c1\u513f\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u6709\u7a7a\u4e00\u5b9a\u8981\u6765\u65e0\u6781\u5c9b\u3002\u201d \u3000\u3000\u51cc\u98ce\u70b9\u70b9\u7740\uff0c\u5ffd\u9053\uff1a\u201c\u4f60\u770b\u5230\u6377\u5f1f\uff0c\u5c31\u8bf7\u544a\u8bc9\u4ed6\uff0c\u4e24\u4e2a\u6708\u540e\u6211\u5728\u6d1b\u9633\u7b49\u4ed6\uff0c\u6211\u4eec\u7ea6\u5b9a\u53ef \u8981\u4e00\u8d77\u53bb\u62a5\u4ec7\u3002\u201d \u3000\u3000\u5929\u4e0a\u758f\u758f\u51e0\u9897\u661f\u513f\u5728\u6f06\u9ed1\u7684\u5929\u9645\u683c\u5916\u660e\u4eae\uff0c\u83c1\u513f\u7741\u7740\u660e\u4eae\u7684\u5927\u773c\u775b\uff0c\u6570\u7740\u70b9\u70b9\u661f\u5149\uff0c\u5979 \u7eaf\u6d01\u7684\u5fc3\u4e2d\u53c8\u6d6e\u4e0a\u8f9b\u6377\u591a\u60c5\u7684\u9762\u5bb9\u2014\u2014 \u3000\u3000\u9ed1\u84dd\u7684\u5929\uff0c\u758f\u758f\u7684\u661f\u5149\u2014\u2014 \u3000\u3000\u540c\u4e00\u65f6\u523b\u91cc\uff0c\u540c\u6837\u7684\u661f\u591c\u4e0b\uff0c\u5728\u5343\u767e\u91cc\u5916\u53e6\u4e00\u4eba\u4e5f\u6b63\u6000\u7740\u540c\u6837\u7684\u5fc3\u60c5\u5728\u4ef0\u770b\u7740\u5929\u5b98\uff0c\u6570 \u7740\u7a00\u843d\u7684\u661f\u8fb0\u2014\u2014 \u3000\u3000\u4ed6\uff0c\u6b63\u662f\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u5750\u5728\u5ca9\u6d1e\u53e3\uff0c\u51dd\u89c6\u7740\u9065\u8fdc\u7684\u5929\u8fb9\uff0c\u661f\u5149\u4e0b\uff0c\u4ed6\u7684\u767d\u7699\u7684\u8138\u5b54\u4e0a\u6709\u4e00\u79cd\u96be\u4ee5\u5f62\u5bb9\u7684\u53e4 \u602a\u7ea2\u6da6\u3002 \u3000\u3000\u4e5f\u8bb8\uff0c\u4ed6\u4e5f\u6b63\u5728\u60f3\u5ff5\u7740\u83c1\u513f\u5427\uff01 \u3000\u3000\u4ed6\u786c\u63a5\u4e86\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u4e2d\u91d1\u4f2f\u80dc\u5937\u7684\u4e00\u638c\uff0c\u800c\u4e14\u7531\u4e8e\u8eab\u4f53\u4e0d\u66fe\u9000\u52a8\uff0c\u4e00\u70b9\u4e5f\u4e0d\u80fd\u501f\u5de7\u529b \u6d88\u53bb\u654c\u52bf\uff0c\u662f\u4ee5\u91d1\u4f2f\u80dc\u5937\u90a3\u4e00\u5355\u662f\u7ed3\u7ed3\u5b9e\u5b9e\u6253\u4e2d\u4e86\u4ed6\uff0c\u4ee5\u91d1\u4f2f\u80dc\u5937\u7684\u529f\u529b\u800c\u8a00\uff0c\u8f9b\u6377\u5c31\u662f\u518d \u5f3a\u51e0\u5206\uff0c\u53ea\u6015\u4e5f\u4e0d\u662f\u5bf9\u624b\u2014\u2014\u7136\u800c\u73b0\u5728\uff0c\u4ece\u4ed6\u8138\u4e0a\u7684\u7ea2\u6da6\u770b\u6765\uff0c\u4ed6\u7684\u5185\u4f24\u81f3\u5c11\u5df2\u75ca\u6108\u4e86\u5341\u4e4b \u516b\u4e5d\uff0c\u4e0d\u6d88\u8bf4\uff0c\u662f\u7531\u4e8e\u4ed6\u81ea\u884c\u4ee5\u4e0a\u4e58\u5185\u529f\u7597\u6cbb\u7684\u7ed3\u679c\uff0c\u800c\u8fd9\u4efd\u529f\u529b\u4e5f\u7740\u5b9e\u79f0\u5f97\u4e0a\u7089\u706b\u7eaf\u9752 \u4e86\u3002 \u3000\u3000\u7684\u786e\uff0c\u4ed6\u662f\u5728\u60f3\u7740\u83c1\u513f\uff0c\u60f3\u7740\u90a3\u7f8e\u4e3d\u7edd\u4f26\u7684\u9762\u988a\uff0c\u90a3\u5929\u771f\u65e0\u90aa\u7684\u773c\u775b\u2026\u2026 \u3000\u3000\u6e10\u6e10\uff0c\u4ed6\u60f3\u5230\u4e86\u91d1\u6b39\u548c\u65b9\u5c11\u78a7\u3002 \u3000\u3000\u65b9\u5c11\u78a7\u662f\u7b2c\u4e00\u4e2a\u95ef\u8fdb\u4ed6\u5fc3\u9761\u7684\u5029\u5f71\uff0c\u867d\u7136\u7531\u4e8e\u547d\u8fd0\u7684\u5b89\u6392\u843d\u5f97\u4e86\u5982\u4eca\u7684\u60c5\u51b5\uff0c\u4f46\u662f\u90a3\u521d \u604b\u7684\u751c\u871c\u5c06\u6c38\u8fdc\u5b58\u5728\u8f9b\u6377\u7684\u5fc3\u4e2d\u3002 \u3000\u3000\u5f53\u65b9\u5c11\u78a7\u548c\u91d1\u6b39\u88ab\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u903c\u5f97\u8d70\u6295\u65e0\u8def\u7684\u65f6\u5019\uff0c\u8f9b\u6377\u4e0d\u987e\u4e00\u5207\u5730\u633a\u8eab\u800c\u51fa\uff0c\u786c\u751f \u751f\u5730\u63a5\u4e86\u91d1\u4f2f\u80dc\u5937\u4e00\u638c\uff0c\u5728\u90a3\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u5fd8\u4e86\u7236\u6bcd\u5927\u4ec7\u672a\u62a5\uff0c\u5e08\u95e8\u6069\u6028\u672a\u4e86\uff0c\u4e5f\u5fd8\u4e86\u4e16\u4e0a \u65e0\u6570\u5176\u4ed6\u8be5\u53bb\u505a\u7684\u5927\u4e8b\uff0c\u4ed6\u53ea\u662f\u70ed\u8840\u6cb8\u817e\uff0c\u8840\u6c14\u51b2\u52a8\uff0c\u81f3\u4e8e\u540e\u679c\uff0c\u4ed6\u8fde\u60f3\u90fd\u4e0d\u66fe\u60f3\u8fc7\u3002 \u3000\u3000\u8fd9\u6837\u8bf4\u6765\uff0c\u4ed6\u4ecd\u631a\u7231\u7740\u65b9\u5c11\u78a7\u5417\uff1f \u3000\u3000\u4ed6\u4e0d\u505c\u5730\u81ea\u95ee\uff1a\u201c\u8f9b\u6377\u554a\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8001\u662f\u4e22\u4e0d\u5f00\u5462\uff1f\u4f60\u4ecd\u5728\u4e0d\u65ad\u5730\u60f3\u5ff5\u7740\u5979\u505a\u4ec0\u4e48 \u554a\uff1f\u2026\u2026\u201d \u3000\u3000\u4e00\u9053\u5149\u534e\u5212\u8fc7\u606c\u9759\u7684\u9ed1\u591c\uff0c\u662f\u4e00\u9897\u661f\u5bbf\u8010\u4e0d\u4f4f\u957f\u7a7a\u7684\u5bc2\u83ab\uff0c\u6084\u6084\u5730\u9668\u843d\u4e16\u95f4\u3002 \u3000\u3000\u4ed6\u4e0d\u89e3\u5730\u601d\u7d22\u7740\u2014\u2014 \u3000\u3000\u201c\u6211\u4e0d\u4f1a\u518d\u7231\u604b\u7740\u5979\u5427\uff1f\u5982\u679c\u6211\u4e0d\u7231\u5979\uff0c\u4e3a\u4ec0\u4e48\u90a3\u65f6\u8282\u6211\u4f1a\u7ba1\u4e0d\u4f4f\u81ea\u5df1\u5730\u62fc\u547d\u800c\u51fa\uff0c\u96be \u9053\u53ea\u662f\u4e3a\u4e86\u4fa0\u4e49\u4e48\uff1f\u5982\u679c\u6211\u7231\u5979\uff0c\u6211\u5c31\u4e0d\u5e94\u8be5\u518d\u8fd9\u6837\u60f3\u7740\u5979\u554a\uff0c\u8ba9\u5979\u5e73\u5b89\u5730\u8ddf\u7740\u90a3\u91d1\u6b39\u5427\uff0c \u4e0d\u7ba1\u4ed6\u662f\u8c01\uff0c\u5979\u603b\u7b97\u6709\u4e86\u4e2a\u5f52\u5bbf\uff0c\u662f\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u5fc3\u4e2d\u987f\u65f6\u77db\u76fe\u8d77\u6765\u4e86\u3002 \u3000\u3000\u6d77\u6d9b\u6c79\u6d8c\uff0c\u6d6a\u58f0\u5728\u9759\u591c\u4e2d\u683c\u5916\u6e05\u6670\u3002 \u3000\u3000\u4eba\u5728\u8fd9\u6837\u7684\u60c5\u5883\u4e0b\uff0c\u601d\u60f3\u53d8\u5f97\u5f02\u5e38\u7684\u654f\u6377\u800c\u98d8\u5ffd\uff0c\u8f9b\u6377\u7684\u5fc3\u5982\u91ce\u9a6c\u4e00\u822c\u9a70\u9a8b\u5728\u5931\u53bb\u7684\u5c81 \u6708\u4e2d\u2014\u2014 \u3000\u3000\u6bcf\u4e00\u5f20\u719f\u6089\u7684\u9762\u5b54\u90fd\u5728\u4ed6\u8111\u6d77\u4e2d\u98de\u8fc7\uff0c\u5bf9\u6b64\u65f6\u7684\u8f9b\u6377\u771f\u6709\u5f02\u6837\u7684\u4eb2\u5207\u3002 \u3000\u3000\u7136\u800c\u5728\u4ed6\u8111\u6d77\u4e2d\u505c\u7559\u6700\u4e45\u7684\u4ecd\u662f\u90a3\u9f99\u949f\u6148\u7965\u7684\u6885\u53d4\u53d4\uff0c\u8f9b\u6377\u4e4b\u6709\u4eca\u5929\u5b8c\u5168\u662f\u7531\u4e8e\u6885\u53d4\u53d4 \u7684\u7167\u62c2\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4e00\u4e2a\u4ece\u6765\u6ca1\u6709\u8fc7\u7684\u201c\u5947\u602a\u201d\u5ff5\u5934\u95ea\u8fc7\u8f9b\u6377\u7684\u5fc3\u7530\uff1a\u201c\u4e16\u4e0a\u7684\u4eba\u7a76\u7adf\u8981\u600e\u6837\u624d\u7b97\u662f \u597d\u4eba\u554a\uff1f\u50cf\u91d1\u4e00\u9e4f\u3001\u91d1\u6b39\uff0c\u8fd9\u4e9b\u4eba\u96be\u9053\u5c31\u4e00\u5b9a\u662f\u574f\u4eba\u4e48\uff1f\u90a3\u4e9b\u6240\u8c13\u7684\u5584\u4eba\u96be\u9053\u771f\u6b63\u4e00\u4ef6\u574f\u4e8b \u4e5f\u4e0d\u66fe\u505a\u8fc7\u5417\uff1f\u201d \u3000\u3000\u806a\u660e\u7edd\u4e16\u7684\u4ed6\uff0c\u7adf\u88ab\u8fd9\u95ee\u9898\u8ff7\u60d1\u4f4f\u4e86\u3002 \u3000\u3000\u201c\u50cf\u6885\u53d4\u53d4\uff0c\u4ed7\u7740\u7edd\u4e16\u60ca\u624d\uff0c\u4e03\u827a\u6837\u6837\u7cbe\u7edd\uff0c\u4f46\u662f\u6b66\u6797\u4e2d\u63d0\u8d77\u2018\u4e03\u5999\u795e\u541b\u2019\u65f6\uff0c\u81f3\u591a\u662f \u2018\u754f\u2019\u800c\u5df2\uff0c\u5e76\u6ca1\u6709\u5b58\u7740\u2018\u656c\u2019\u7684\u5fc3\u7406\uff0c\u800c\u4e10\u5e2e\u7684\u91d1\u6c0f\u6606\u4ef2\u672c\u4e8b\u867d\u7136\u751a\u662f\u6709\u9650\uff0c\u53ef\u662f\u6c5f\u6e56\u4e0a \u63d0\u8d77\u91d1\u8001\u5927\u91d1\u8001\u4e8c\u6765\uff0c\u6ca1\u6709\u4e00\u4e2a\u4e0d\u7fd8\u8d77\u5927\u62c7\u6307\u8d5e\u58f0\u597d\uff0c\u53ef\u89c1\u8981\u505a\u4e00\u4e2a\u5389\u5bb3\u7684\u4eba\u7269\u751a\u662f\u5bb9\u6613\uff0c \u800c\u8981\u505a\u4e00\u4e2a\u597d\u4eba\u5374\u662f\u6781\u96be\u7684\u2026\u2026\u201d \u3000\u3000\u672c\u6765\uff0c\u8f9b\u6377\u662f\u4e2a\u504f\u6fc0\u7684\u4eba\uff0c\u867d\u7136\u4ed6\u4e5f\u66fe\u968f\u6885\u5c71\u6c11\u8bfb\u901a\u53e4\u4eca\u767e\u4e66\uff0c\u4f46\u662f\u5728\u4ed6\u5185\u5fc3\u6df1\u5904\uff0c\u5bf9 \u4e8e\u53e4\u5723\u8d24\u4e4b\u8bed\u5e76\u4e0d\u5341\u5206\u4ee5\u4e3a\u7136\uff0c\u4ed6\u5904\u4e16\u4e4b\u9645\u2018\u654c\u6211\u2019\u4e4b\u5fc3\u8fdc\u80dc\u4e8e\u2018\u662f\u975e\u2019\u4e4b\u5fc3\uff0c\u53ea\u8981\u5bf9\u4ed6\u4e00 \u5206\u597d\u7684\u4eba\uff0c\u4ed6\u5c31\u5341\u5206\u5bf9\u4eba\u597d\uff0c\u4e00\u5206\u5f85\u4ed6\u6076\u7684\uff0c\u4ed6\u4e5f\u5341\u5206\u8fd8\u62a5\u4e8e\u4eba\uff0c\u81f3\u4e8e\u522b\u4eba\u5982\u4f55\u770b\u6cd5\uff0c\u4ed6\u53ef \u7ba1\u4e0d\u5230\u3002 \u3000\u3000\u4f46\u662f\u8fd1\u65e5\u6765\uff0c\u4e5f\u8bb8\u662f\u5e74\u7eaa\u5927\u4e86\u4e00\u4e9b\uff0c\u4e5f\u8bb8\u662f\u7531\u4e8e\u548c\u5929\u6027\u6566\u539a\u7684\u5434\u51cc\u98ce\u76f8\u5904\u6240\u53d7\u7684\u5f71\u54cd\uff0c \u4ed6\u90a3\u504f\u6fc0\u7684\u672c\u6027\u6697\u4e2d\u8d77\u4e86\u53d8\u5316\uff0c\u4e0d\u8fc7\u8fd9\u79cd\u53d8\u5316\u4e5f\u8bb8\u8fde\u4ed6\u81ea\u5df1\u4e5f\u4e0d\u77e5\u9053\u7f62\u4e86\u3002 \u3000\u3000\u8b6c\u5982\u8bf4\uff0c\u4ee5\u524d\u4ed6\u5bf9\u6885\u53d4\u53d4\u662f\u76f2\u76ee\u5730\u5d07\u62dc\uff0c\u4f46\u6b64\u523b\u4ed6\u7adf\u6709\u4e86\u8fd9\u79cd\u7684\u60f3\u6cd5\uff0c\u8fd9\u4e0d\u80fd\u4e0d\u8bf4\u662f\u76f8 \u5f53\u5927\u7684\u6539\u53d8\u5427\u3002 \u3000\u3000\u4ed6\u7684\u601d\u60f3\u9a70\u9a8b\u7740\uff0c\u6700\u540e\uff0c\u4ed6\u7ec8\u4e8e\u81ea\u95ee\uff1a\u201c\u6211\u7b97\u5f97\u662f\u4e00\u4e2a\u597d\u4eba\u5417\uff1f\u201d \u3000\u3000\u8fd9\u6b63\u662f\u4e2d\u5fc3\u7684\u95ee\u9898\uff0c\u85cf\u5728\u4ed6\u5185\u5fc3\u6700\u6df1\u5904\u7684\u95ee\u9898\u3002\u8fd9\u4e9b\u65e5\u5b50\u4ee5\u6765\uff0c\u4ed6\u4ed7\u7740\u4e00\u8eab\u60ca\u4e16\u795e\u529f\u95ef \u4e0b\u4e86\u4e0d\u51e1\u7684\u4e07\u513f\uff0c\u201c\u6885\u9999\u795e\u5251\u201d\u521b\u6210\u4e86\u6b66\u6797\u4e2d\u65b0\u7684\u5d07\u62dc\u5076\u50cf\uff0c\u4f46\u662f\uff0c\u4ed6\u591f\u597d\u4e86\u5417\uff1f \u3000\u3000\u5f53\u4e00\u4e2a\u4eba\u6210\u4e86\u540d\u4ee5\u540e\uff0c\u4ed6\u7684\u884c\u52a8\u5c31\u4f1a\u81ea\u7136\u5730\u8c28\u614e\u8d77\u6765\uff0c\u8f9b\u6377\u6b64\u65f6\u591a\u5c11\u6709\u4e00\u70b9\u8fd9\u79cd\u5fc3\u7406\uff0c \u4ed6\u8981\u60f3\u4f7f\u201c\u6885\u9999\u795e\u5251\u201d\u771f\u6b63\u6210\u4e3a\u4eba\u4eec\u6b4c\u9882\u7684\u5bf9\u8c61\uff0c\u4e0d\u4ec5\u662f\u4e00\u4e2a\u201c\u6b66\u592b\u201d\u800c\u5df2\uff01 \u3000\u3000\u4ed6\u4e0d\u505c\u5730\u80e1\u601d\u4e71\u60f3\uff0c\u8fd9\u6b63\u662f\u5185\u529f\u7597\u4f24\u4f11\u606f\u671f\u95f4\u7684\u5fc5\u7136\u73b0\u8c61\u2014\u2014\u601d\u60f3\u4f1a\u53d8\u5f97\u683c\u5916\u51cc\u4e71\u3002 \u3000\u3000\u8bb8\u591a\u5947\u5947\u602a\u602a\u7684\u5ff5\u5934\u5728\u4ed6\u8111\u4e2d\u65cb\u8f6c\u7740\u2026\u2026 \u3000\u3000\u6700\u540e\uff0c\u4ed6\u53c8\u60f3\u5230\u81ea\u5df1\u6240\u9047\u5230\u7684\u4e09\u4e2a\u5973\u5b50\uff0c\u65b9\u5c11\u78a7\u3001\u91d1\u6885\u9f84\u3001\u5f20\u83c1\u3002 \u3000\u3000\u548c\u65b9\u5c11\u78a7\u7684\u91cd\u9022\u4f7f\u4ed6\u5bf9\u91d1\u6885\u9f84\u7684\u201c\u5931\u8e2a\u201d\u62b1\u7740\u8f83\u9ad8\u7684\u671f\u671b\uff0c\u4ed6\u60f3\uff0c\u603b\u6709\u4e00\u5929\u4ed6\u80fd\u5bfb\u7740\u5979 \u7684\u2014\u2014 \u3000\u3000\u4f46\u8fd9\u662f\u591a\u4e48\u8352\u8c2c\u7684\u60f3\u6cd5\u554a\uff0c\u4ed6\u6c38\u8fdc\u65e0\u6cd5\u6599\u5230\u6885\u9f84\u906d\u5230\u5982\u4f55\u7684\u4e0d\u5e78\u2014\u2014\u547d\u8fd0\u5728\u6349\u5f04\u4ed6\u4eec \u554a\uff01 \u3000\u3000\u63a5\u7740\u4ed6\u60f3\u5230\u83c1\u513f\u3002 \u3000\u3000\u201c\u6211\u548c\u5979\u76f8\u5904\u7684\u65e5\u5b50\u867d\u5c11\uff0c\u4f46\u5979\u5374\u662f\u90a3\u6837\u5730\u4ee4\u6211\u96be\u5fd8\uff0c\u6211\u4eec\u867d\u7136\u6ca1\u6709\u660e\u767d\u5730\u8bb2\u8fc7\u4ec0\u4e48\uff0c \u4f46\u5979\u51e0\u756a\u820d\u547d\u6551\u6211\u5bfb\u6211\uff0c\u8fd9\u5c82\u4e0d\u66f4\u80dc\u8fc7\u5343\u8a00\u4e07\u8bed\u5417\uff1f\u2026\u2026 \u3000\u3000\u6211\u548c\u5979\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u4ec0\u4e48\u5fe7\u6101\u7684\u4e8b\u4e5f\u60f3\u4e0d\u5230\uff0c\u6211\u53ea\u6709\u5feb\u4e50\uff0c\u65e0\u7a77\u7684\u5feb\u4e50\u2026\u2026\u8f9b\u6377\u554a\uff0c \u4f60\u5fc3\u6df1\u5904\u539f\u662f\u6700\u7231\u90a3\u83c1\u513f\u5417\uff1f \u3000\u3000\u4ed6\u4e0d\u80fd\u518d\u60f3\u4e86\uff0c\u534a\u4e2a\u65f6\u8fb0\u7684\u4f11\u606f\u671f\u95f4\u5df2\u8fc7\uff0c\u4ed6\u5fc5\u987b\u6536\u655b\u80e1\u601d\u4e71\u60f3\uff0c\u5168\u795e\u8d2f\u6ce8\u5730\u4f5c\u6700\u540e\u4e00 \u6b21\u8fd0\u529f\u3002 \u3000\u3000\u53ea\u89c1\u4ed6\u4e94\u5fc3\u5411\u5929\uff0c\u4e09\u82b1\u805a\u9876\uff0c\u8138\u4e0a\u9732\u51fa\u4e00\u6d3e\u548c\u7a46\u4e4b\u8272\uff0c\u6e10\u6e10\uff0c\u8111\u95e8\u4e0a\u5192\u51fa\u4e1d\u4e1d\u767d\u8272\u84b8 \u6c14\u3002 \u3000\u3000\u5ca9\u6d1e\u5916\u662f\u4e00\u7247\u5e73\u6c99\uff0c\u72ed\u957f\u800c\u5bbd\u9614\uff0c\u518d\u5411\u524d\u5c31\u662f\u6d77\u5cb8\u4e86\u3002 \u3000\u3000\u6d77\u6c34\u541e\u8680\u7740\u6c99\u5cb8\uff0c\u5012\u5377\u8d77\u4e00\u6761\u96ea\u767d\u7684\u6d6a\u82b1\uff0c\u6d9b\u58f0\u4f3c\u6709\u89c4\u5f8b\u5730\u54cd\u7740\u3002 \u3000\u3000\u84e6\u7136\u2014\u2014 \u3000\u3000\u4e24\u6761\u9ed1\u5f71\u51fa\u73b0\u5728\u6d77\u5cb8\u4e0a\uff0c\u867d\u7136\u9694\u5f97\u90a3\u4e48\u8fdc\uff0c\u4f46\u662f\u4ecd\u6e05\u695a\u53ef\u8fa8\u51fa\u8fd9\u4e24\u4eba\u5f02\u4e8e\u5e38\u4eba\u7684\u53e4\u602a\u5916 \u5f62\u3002 \u3000\u3000\u5c24\u5176\u5176\u4e2d\u4e00\u4e2a\u4f3c\u4e4e\u624b\u811a\u90fd\u6b8b\u7f3a\u4e0d\u9f50\u3002 \u3000\u3000\u4ed6\u4eec\u4e00\u8fb9\u8d70\uff0c\u4e00\u8fb9\u6bd4\u7740\u624b\u5f0f\uff0c\u4f3c\u4e4e\u5176\u4e2d\u4e00\u4e2a\u662f\u4e2a\u54d1\u5df4\u5462\u3002 \u3000\u3000\u6e10\u6e10\u8fd1\u4e86\uff0c\u661f\u5149\u4e0b\u4f9d\u7a00\u53ef\u8fa8\u90a3\u4e24\u5f20\u6050\u6016\u7684\u4e11\u8138\uff0c\u7adf\u7136\u662f\u90a3\u6d77\u5929\u53cc\u715e\uff01 \u3000\u3000\u4ed6\u4eec\u6df1\u77e5\u8fd9\u8352\u5cb8\u4e0a\u65e0\u4eba\u5c45\u4f4f\uff0c\u662f\u4ee5\u6beb\u65e0\u60ee\u5fcc\u5730\u8d70\u7740\uff0c\u811a\u6b65\u58f0\u5f88\u54cd\u2014\u2014 \u3000\u3000\u9ed1\u6697\u5ca9\u6d1e\u53e3\u7684\u8f9b\u6377\u88ab\u8fd9\u79cd\u811a\u6b65\u58f0\u60ca\u8d77\uff0c\u4ed6\u5fae\u7741\u773c\u775b\u4e00\u77a5\u4f46\u8fd9\u4e00\u77a5\uff0c\u4ee4\u4ed6\u518d\u4e5f\u65e0\u6cd5\u5e73\u9759\uff01 \u3000\u3000\u90a3\u4e11\u6076\u7684\u8138\u5b54\uff0c\u6b8b\u7f3a\u7684\u80a2\u4f53\uff0c\u8f9b\u6377\u7761\u68a6\u4e4b\u95f4\u90fd\u4e0d\u66fe\u5fd8\u8bb0\u8fc7\uff0c\u90a3\u662f\u4e0d\u5171\u6234\u5929\u7684\u6740\u7236\u6bcd\u5927\u4ec7 \u554a\uff01 \u3000\u3000\u4ed6\u4e5f\u77e5\u9053\u8fd9\u662f\u7597\u4f24\u7684\u7d27\u8981\u5173\u5934\uff0c\u4e00\u5206\u5927\u610f\u4e0d\u5f97\uff0c\u4f46\u4ed6\u4e00\u8fde\u63d0\u4e86\u4e94\u53e3\u6c14\u60f3\u538c\u5236\u80f8\u4e2d\u7684\u6f8e\u6e43 \u6012\u6f6e\uff0c\u5374\u59cb\u7ec8\u65e0\u6cd5\u505a\u5230\uff0c\u5176\u5b9e\u4ee5\u4ed6\u7684\u6027\u5b50\uff0c\u5c31\u662f\u5185\u529f\u518d\u6df1\u51e0\u500d\u4e5f\u662f\u6789\u7136\u3002 \u3000\u3000\u4ed6\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u7d22\u6027\u7ad9\u8d77\u8eab\u6765\u3002\u4ed6\u77e5\u9053\u8fd9\u4e00\u7ad9\u8d77\u6765\uff0c\u53c8\u5f97\u82b1\u4e24\u500d\u7684\u529f\u592b\u6765\u8865\u7597\uff0c\u4f46\u4ed6\u5b9e \u5728\u65e0\u6cd5\u63a7\u5236\u81ea\u5df1\u3002 \u3000\u3000\u4ed6\u8bd5\u4e86\u8bd5\u6362\u6c14\uff0c\u867d\u7136\u884c\u52a8\u5df2\u80fd\u81ea\u5982\uff0c\u4f46\u662f\u771f\u6c14\u5374\u65e0\u6cd5\u51dd\u805a\uff0c\u4e0e\u4eba\u52a8\u624b\u66f4\u4e0d\u662f\u65f6\u5019\u3002 \u3000\u3000\u53cc\u715e\u7684\u811a\u6b65\u53c8\u8fd1\u4e86\u4e9b\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u662f\u76f4\u5f80\u8ff7\u5ca9\u6d1e\u8d70\u6765\u7684\u5462\u3002 \u3000\u3000\u8f9b\u6377\u7126\u6025\u5730\u60f3\u9053\uff1a\u201c\u82e5\u662f\u5e73\u65f6\u8fd9\u4e24\u4e2a\u9b3c\u9001\u4e0a\u95e8\u6765\u6b63\u597d\u7701\u5374\u6211\u4e00\u756a\u5954\u6ce2\uff0c\u56e0\u4e3a\u8fd9\u4e24\u4e2a\u9b54\u5934 \u4e0d\u6bd4\u4e94\u5927\u5251\u6d3e\u638c\u95e8\u4eba\uff0c\u53ef\u4ee5\u968f\u65f6\u9690\u5c45\u8d77\u6765\uff0c\u90a3\u65f6\u8981\u627e\u4ed6\u4eec\u5c31\u9ebb\u70e6\u4e86\u3002\u53ea\u662f\u73b0\u5728\u6211\u65e0\u529b\u52a8\u624b\uff0c \u8fd9\u4fbf\u5982\u4f55\u662f\u597d\uff1f\u96be\u9053\u773c\u770b\u8fd9\u4e24\u4eba\u8d70\u6389\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4ed6\u6025\u6012\u4ea4\u52a0\uff0c\u4e00\u65f6\u83ab\u6240\u9002\u4ece\uff0c\u53cc\u624b\u5728\u8eab\u4e0a\u4e71\u6478\uff0c\u5e0c\u671b\u80fd\u627e\u51fa\u4e00\u70b9\u53ef\u8d44\u5229\u7528\u7684\u4e8b\u7269\uff0c \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u7684\u624b\u6307\u5728\u895f\u524d\u89e6\u53ca\u4e00\u7269\uff0c\u4e00\u4e2a\u5ff5\u5934\u4e00\u95f4\u800c\u8fc7\uff0c\u4ed6\u9669\u4e9b\u559c\u5f97\u5927\u53eb\u51fa\u58f0 \u3000\u3000\u53ea\u89c1\u4ed6\u4ece\u676f\u4e2d\u638f\u51fa\u4e00\u4e2a\u5c0f\u74f6\uff0c\u4ed6\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6b64\u541b\u91d1\u4e00\u9e4f\u2018\u6bd2\u7ecf\u2019\u4e0a\u8bf4\uff1a\u8fd9\u2018\u78a7\u7389\u65ad \u80a0\u2019\u4e00\u7ecf\u903c\u51fa\uff0c\u89e6\u53ca\u7a7a\u6c14\uff0c\u7acb\u523b\u6027\u8d28\u5927\u53d8\uff0c\u7531\u5185\u53d1\u53d8\u4e3a\u5916\u53d1\uff0c\u4e14\u4e27\u5931\u5176\u6f5c\u4f0f\u6027\uff0c\u5e76\u4e14\u666e\u901a\u87ba \u868c\u4e4b\u8089\u5373\u53ef\u89e3\u6bd2\uff0c\u662f\u4ee5\u5a01\u529b\u5927\u589e\u3002\u4f46\u6b64\u65f6\u6211\u6b63\u597d\u7528\u5b83\u4e00\u7528\u3002\u201d\u6562\u60c5\u90a3\u5c0f\u74f6\u513f\u4e2d\u6b63\u662f\u96c6\u5e73\u51e1\u4e0a \u4eba\u3001\u6167\u5927\u5e08\u4e24\u4eba\u4e4b\u529b\u6240\u903c\u51fa\u65e0\u6068\u751f\u8eab\u4e0a\u7684\u201c\u78a7\u7389\u65ad\u80a0\u201d\uff01 \u3000\u3000\u661f\u5149\u5fae\u5fae\u95ea\u70c1\uff0c\u8f9b\u6377\u79fb\u52a8\u8eab\u8eaf\uff0c\u5230\u4e00\u4e2a\u7a81\u51fa\u5ca9\u767e\u7684\u540e\u9762\u6f5c\u7740\uff0c\u5fc3\u6f6e\u8d77\u4f0f\u4e0d\u5b9a\uff0c\u8111\u6d77\u4e2d\u4e07 \u5ff5\u9f50\u96c6\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u6765\u5f97\u8fd1\u4e86\uff0c\u7126\u5316\u3001\u7126\u52b3\u4e24\u5144\u5f1f\u4f3c\u4e4e\u4e5f\u8d70\u5f97\u5341\u5206\u5730\u75b2\u4e4f\uff0c\u8f9b\u6377\u51e0\u4e4e\u53ef\u4ee5\u95fb\u89c1\u90a3\u6c89 \u91cd\u7684\u547c\u5438\u58f0\u3002 \u3000\u3000\u84e6\u7136\uff0c\u8f9b\u6377\u5fc3\u5ff5\u4e00\u52a8\uff0c\u98de\u5feb\u7684\u62d4\u5f00\u90a3\u7389\u74f6\uff0c\u5355\u624b\u63d0\u7740\u5411\u5916\u6492\u53bb\uff0c\u78a7\u7389\u65ad\u80a0\u6db2\u968f\u7740\u4ed6\u624b\u81c2 \u8f6c\u52a8\uff0c\u4e5f\u6574\u6574\u9f50\u9f50\u7684\u6492\u5728\u6d1e\u524d\u5e03\u4e0b\u4e00\u4e2a\u534a\u5706\u3002 \u3000\u3000\u65ad\u80a0\u6bd2\u6db2\u78a7\u7eff\u7684\u6c34\u6c41\u5728\u5929\u7a7a\u4e2d\u5212\u8fc7\uff0c\u8f7b\u843d\u6c99\u571f\u4e0a\uff0c\u4ecd\u7136\u53d1\u51fa\u4e00\u70b9\u6de1\u6de1\u7684\u7eff\u5149\uff0c\u5728\u9ed1\u591c \u4e2d\uff0c\u5e76\u4e0d\u600e\u6837\u663e\u660e\u3002 \u3000\u3000\u8f9b\u6377\u6beb\u4e0d\u505c\u6ede\uff0c\u62ac\u624b\u62fe\u8d77\u4e24\u5757\u62f3\u5927\u7684\u77f3\u5b50\uff0c\u5728\u4e00\u5757\u4e0a\u9762\u6492\u4e0b\u4e00\u4e9b\u6bd2\u6db2\uff0c\u51c6\u5907\u4e0b\u4e00\u6b65\u7684\u5de5 \u4f5c\u3002 \u3000\u3000\u5929\u6b8b\uff0c\u5929\u5e9f\u4e24\u5144\u5f1f\u4f5c\u68a6\u4e5f\u60f3\u4e0d\u5230\u8fd9\u7b49\u8352\u504f\u7684\u5730\u65b9\uff0c\u6b63\u6709\u4e00\u4e2a\u751f\u6b7b\u5bf9\u5934\u864e\u89c6\u803d\u803d\u7684\u671b\u7740\u4ed6 \u4eec\uff0c\u53ea\u53ef\u60dc\u4ed6\u529f\u529b\u672a\u590d\uff0c\u5426\u5219\u65e9\u5df2\u8df3\u8eab\u51fa\u6765\u62fc\u547d\u4e86\u3002\u4e24\u4eba\u4ecd\u662f\u4e00\u8def\u7b14\u76f4\u8d70\u6765\uff0c\u5230\u662f\u6d1e\u4e2d\u7684\u8f9b \u6377\u7d27\u5f20\u7684\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\u5462\u3002 \u3000\u3000\u66f4\u8fd1\u4e86\uff0c\u4e11\u6076\u53ef\u538c\u7684\u9762\u5b54\u5728\u9ed1\u6697\u4e2d\u66f4\u662f\u68ee\u68ee\u53ef\u6016\uff0c\u8f9b\u6377\u9ed8\u9ed8\u547c\u9053\uff1a\u201c\u671b\u7236\u6bcd\u5728\u5929\u4fdd\u4f51\uff0c \u8ba9\u5b69\u513f\u4fdd\u5f97\u4e00\u4e2a\u65f6\u8fb0\uff0c\u56f0\u4f4f\u8fd9\u4e24\u4e2a\u755c\u751f\u3002\u201d \u3000\u3000\u6d77\u98ce\u9891\u9891\u5439\u7740\uff0c\u6d77\u5929\u53cc\u715e\u6765\u5f97\u66f4\u8fd1\u4e86\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4e0d\u6562\u7528\u624b\u89e6\u53ca\u90a3\u5df2\u5e26\u6709\u6bd2\u6db2\u7684\u77f3\u5b50\uff0c\u7528\u978b\u5c16\u627e\u4e00\u5757\u6ca1\u6709\u6cbe\u6709\u6bd2\u6c41\u7684\u5730\u65b9\u5411\u4e0a\u4e00\u6311\uff0c \u53f3\u624b\u89c2\u5f97\u6e05\u5207\uff0c\u53e6\u4e00\u5757\u77f3\u5b50\u7834\u7a7a\u53d1\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u867d\u7136\u529f\u529b\u672a\u590d\uff0c\u4f46\u6697\u5668\u624b\u6cd5\u51c6\u5934\u4ecd\u5728\uff0c\u53ea\u95fb\u201c\u55d2\u201d\u7684\u4e00\u58f0\u6e05\u54cd\uff0c\u90a3\u5e26\u6709\u65ad\u80a0\u6bd2\u6db2\u7684 \u767e\u5b50\u88ab\u540e\u53d1\u7684\u77f3\u5b50\u51c6\u786e\u7684\u51fb\u4e0a\uff0c\u521a\u521a\u8981\u5f80\u4e0b\u5760\u7684\u9a71\u52bf\u88ab\u4e00\u51fb\u4e4b\u4e0b\uff0c\u518d\u5f80\u524d\u5e73\u5e73\u653e\u51fa\u4e8c\u4e09\u4e08 \u8fdc\uff0c\u843d\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u5618\u4e86\u4e00\u53e3\u6c14\uff0c\u95ea\u8eab\u5728\u77f3\u58c1\u4e4b\u540e\u3002 \u3000\u3000\u8f9b\u6377\u662f\u4f55\u7b49\u624b\u6cd5\uff0c\u90a3\u77f3\u5b50\u4e00\u5206\u4e0d\u5dee\u7684\u6d47\u5728\u65e9\u5148\u6240\u5e03\u7684\u4e00\u4e2a\u5708\u5b50\u6bd2\u7ebf\u7684\u540e\u9762\u4e94\u5bf8\u5de6\u53f3\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u5982\u6b64\u529f\u529b\uff0c\u54ea\u4f1a\u4e0d\u95fb\u90a3\u77f3\u513f\u5760\u5730\u4e4b\u58f0\uff0c\u4ed6\u4fe9\u53ef\u662f\u8dd1\u4e86\u5927\u534a\u751f\u7684\u6c5f\u6e56\uff0c\u54ea\u4f1a\u4e0d\u77e5 \u8fd9\u4e43\u662f\u6c5f\u6e56\u4e0a\u6240\u8c13\u201c\u6295\u77f3\u95ee\u8def\u201d\u7684\u65b9\u5f0f\uff1f\u4e24\u4eba\u4e00\u60ca\uff0c\u9f50\u5fd6\u9053\uff1a \u3000\u3000\u201c\u96be\u9053\u5982\u6b64\u7a77\u8352\u6781\u50fb\u7684\u6d77\u5c9b\u4e0a\u4ecd\u6709\u6b66\u6797\u4eba\u58eb\uff1f\u201d \u3000\u3000\u4ed6\u4fe9\u867d\u662f\u5403\u60ca\uff0c\u4f46\u4e24\u4eba\u5e73\u65e5\u7eb5\u6a2a\u6c5f\u6e56\uff0c\u6027\u683c\u5f3a\u608d\uff0c\u54ea\u91cc\u628a\u8fd9\u4ec0\u4e48\u201c\u6295\u77f3\u95ee\u8def\u201d\u653e\u5728\u5fc3 \u4e0a\uff0c\u5929\u6b8b\u7126\u5316\u8eab\u4f53\u4e00\u63a0\uff0c\u5df2\u5230\u6d1e\u53e3\u95ea\u773c\u4e00\u77a5\uff0c\u5e76\u4e0d\u89c1\u4eba\u5f71\u3002 \u3000\u3000\u8f9b\u6377\u8d34\u5899\u800c\u7acb\uff0c\u773c\u775b\u77aa\u5f97\u5927\u5927\u7684\uff0c\u6697\u4e2d\u5411\u90a3\u6d77\u5929\u53cc\u715e\u6253\u91cf\u3002 \u3000\u3000\u7126\u5316\u4e00\u77a5\u4e0d\u89c1\u4eba\u5f71\uff0c\u4e0d\u7531\u4e00\u6014\uff0c\u4fef\u8eab\u4e00\u77a7\uff0c\u53ea\u89c1\u534a\u4e08\u4ee5\u524d\u4e00\u9897\u77f3\u5b50\u8d6b\u7136\u5728\u76ee\uff0c\u663e\u7136\u662f\u521a \u624d\u6765\u4eba\u7528\u6765\u95ee\u8def\u7684\u3002 \u3000\u3000\u7126\u52b3\u7b49\u7740\u4e0d\u8010\uff0c\u4e5f\u63a0\u8fc7\u6765\u89c2\u770b\uff0c\u8f9b\u6377\u8eab\u5b50\u9760\u5728\u77f3\u58c1\u4e0a\uff0c\u8fd9\u4efd\u7d27\u5f20\u53ef\u5f97\u591f\u77a7\u7684\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u76ee\u4e0d\u8f6c\u775b\u7684\u6ce8\u89c6\u6d1e\u53e3\uff0c\u4e5f\u4e0d\u65f6\u626b\u77f3\u5b50\u4e00\u773c\uff0c\u8f9b\u6377\u6025\u5fd6\u9053\uff1a \u3000\u3000\u201c\u5343\u4e07\u4e0d\u8981\u8ba9\u4e24\u4e2a\u8001\u9b54\u5934\u770b\u51fa\u7834\u7efd\u624d\u597d\u2026\u2026\u201d \u3000\u3000\u4e5f\u8bb8\u662f\u7531\u4e8e\u5fc3\u7406\u4f5c\u7528\u7684\u539f\u6545\uff0c\u8fd9\u65f6\u523b\u91cc\uff0c\u4ed6\u500d\u89c9\u90a3\u77f3\u5b50\uff0c\u90a3\u6bd2\u6db2\u53d1\u51fa\u4e00\u79cd\u523a\u76ee\u7684\u7eff\u5149\uff0c \u6d77\u5929\u53cc\u715e\u6b64\u7b49\u7ecf\u9a8c\uff0c\u6ca1\u6709\u4e0d\u53d1\u73b0\u7684\u7406\u7531\uff0c\u4f46\u5b9a\u4e0b\u5fc3\u6765\u770b\u65f6\uff0c\u90a3\u4e0d\u8fc7\u4ec5\u662f\u4e00\u4e1d\u6de1\u7684\u7eff\u5f71\uff0c\u4ee5\u8f9b \u6377\u6b64\u7b49\u773c\u529b\uff0c\u4e5f\u4ec5\u9690\u9690\u8fa8\u51fa\u3002 \u3000\u3000\u65f6\u95f4\u4e00\u5206\u4e00\u79d2\u7684\u8fc7\u53bb\uff0c\u8f9b\u6377\u77e5\u9053\u8fd9\u4e2a\u9632\u7ebf\u82e5\u662f\u88ab\u654c\u4eba\u770b\u51fa\uff0c\u53ea\u4e0d\u8fc7\u4e00\u8de8\u51fa\u4e4b\u95f4\uff0c\u8d8a\u8fc7\u6bd2 \u5708\u548c\u77f3\u5b50\uff0c\u4fbf\u80fd\u5b89\u7136\u65e0\u6089\uff0c\u4e0d\u7531\u5fc3\u4e2d\u6108\u6025\uff0c\u53ea\u89c1\u7126\u5316\u6c89\u541f\u4e00\u4f1a\uff0c\u8e72\u4e0b\u8eab\u5b50\uff0c\u4f38\u624b\u53bb\u62fe\u90a3\u5e26\u6bd2 \u7684\u77f3\u5b50\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u8eab\u667a\u8ba1\uff0c\u8fd9\u77f3\u5b50\u662f\u6709\u610f\u53d1\u51fa\uff0c\u843d\u70b9\u5728\u90a3\u6bd2\u7ebf\u540e\u4e94\u516d\u5bf8\uff0c\u82e5\u662f\u6709\u4eba\u60f3\u62fe\u68c0\uff0c\u975e\u5f97\u8e0f \u5728\u6bd2\u7ebf\u4e0a\u4e0d\u53ef\uff0c\u5426\u5219\u4fbf\u591f\u4e0d\u4e0a\u5730\u4f4d\uff0c\u6d77\u5929\u53cc\u715e\u4e0d\u80fd\u4f8b\u5916\uff0c\u7126\u5316\u4f38\u624b\u8bd5\u8bd5\u5730\u4f4d\uff0c\u4fbf\u77e5\u987b\u8981\u4e0a \u524d\uff0c\u4e8e\u662f\u5fae\u5fae\u79fb\u52a8\u8eab\u5b50\u2026\u2026 \u3000\u3000\u6614\u5e74\u9ec4\u4e30\u4e5d\u8c6a\u6a2a\u884c\u795e\u5dde\uff0c\u5c60\u6bd2\u5927\u6c5f\u5357\u5317\uff0c\u6c5f\u6e56\u4e0a\u767d\u9053\u4eba\u58eb\u4e0d\u53ea\u4e00\u6b21\u8981\u56f4\u527f\u4e3a\u9996\u7684\u4e24\u4e2a\u9b54 \u661f\u201c\u6d77\u5929\u53cc\u715e\u201d\uff0c\u7531\u6b64\u4e5f\u953b\u7ec3\u6210\u201c\u6d77\u5929\u53cc\u715e\u201d\u7684\u9632\u4eba\u4e4b\u5fc3\u3002\u5e73\u65e5\u8def\u8fc7\uff0c\u5c31\u662f\u8349\u6728\u4e00\u52a8\uff0c\u98de\u9e1f \u4e00\u9e23\uff0c\u4e5f\u8981\u8ffd\u7a76\u5176\u7406\uff0c\u5c24\u5176\u662f\u8033\u76ee\u5931\u806a\u7684\u5929\u5e9f\u7126\u52b3\u66f4\u662f\u7279\u522b\u5fc3\u7ec6\uff0c\u4e5f\u5c31\u662f\u56e0\u6b64\uff0c\u4ed6\u4fe9\u4e0d\u77e5\u95ef \u8fc7\u591a\u5c11\u9669\u5173\uff0c\u9003\u8fc7\u591a\u5c11\u751f\u547d\u4e4b\u9669\u3002 \u3000\u3000\u672c\u6765\uff0c\u6709\u4eba\u6295\u77f3\u95ee\u8def\u867d\u4e0d\u662f\u4ec0\u4e48\u5e73\u5e38\u7684\u4e8b\uff0c\u4e5f\u7528\u4e0d\u7740\u5982\u6b64\u7d27\u5f20\uff0c\u4f46\u4e24\u4eba\u751f\u6027\u731c\u7591\uff0c\u4e0d\u80af \u8f7b\u6613\u653e\u8fc7\u3002 \u3000\u3000\u4e00\u5206\u4e00\u5206\uff0c\u7126\u5316\u7684\u624b\u63a5\u8fd1\u90a3\u77f3\u5b50\uff0c\u4ed6\u81ea\u7136\u7684\u518d\u79fb\u52a8\u4e00\u4e0b\uff0c\u6b63\u597d\u79fb\u52a8\u5728\u90a3\u6761\u6bd2\u7ebf\u4e0a\u9762\u3002 \u3000\u3000\u6d1e\u4e2d\u7684\u8f9b\u6377\uff0c\u7d27\u7d27\u7684\u54ac\u7740\u81ea\u5df1\u4e0b\u5507\uff0c\u5fc3\u60c5\u7d27\u5f20\u4e4b\u6781\u3002 \u3000\u3000\u84e6\u7136\uff0c\u7126\u52b3\u7a81\u5730\u4f38\u624b\u4e00\u6293\uff0c\u770b\u6a21\u6837\u662f\u8981\u6293\u56de\u90a3\u5df2\u4e2d\u8ba1\u7684\u7126\u5316\u2014\u2014 \u3000\u3000\u8f9b\u6377\u5927\u5403\u4e00\u60ca\uff0c\u4ee5\u4e3a\u4ed6\u5df2\u7aa5\u7834\u9b3c\u8ba1\uff0c\u6025\u5f97\u4e00\u8eab\u51b7\u6c57\u6709\u82e5\u6cc9\u6d8c\uff0c\u4f38\u624b\u4e0a\u4e0b\u4e00\u9635\u4e71\u6478\uff0c\u84e6\u7136 \u89e6\u53ca\u90a3\u672c\u91d1\u4e00\u9e4f\u4e00\u751f\u5fc3\u8840\u7684\u6bd2\u7ecf\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u4e0d\u7ba1\u4e09\u4e03\u4e8c\u5341\u4e00\uff0c\u6478\u51fa\u6765\u4e00\u4e0b\u63b7\u5c06\u51fa\u53bb\u3002 \u3000\u3000\u672c\u6765\uff0c\u7126\u52b3\u4f38\u624b\u6b32\u6293\u7126\u5316\uff0c\u53ea\u4e0d\u8fc7\u60f3\u53eb\u4ed6\u4e0d\u8981\u592a\u5fd9\uff0c\u6253\u7b97\u5148\u4e5f\u91c7\u7528\u201c\u6295\u77f3\u95ee\u8def\u201d\u7684\u65b9 \u5f0f\uff0c\u53cd\u95ee\u6d1e\u4e2d\u6709\u5426\u4eba\u8ff9\u3002\u4ed6\u60f3\u53eb\u5144\u957f\u628a\u90a3\u77f3\u5b50\u62fe\u8d77\u6253\u5165\u6d1e\u4e2d\uff0c\u53bb\u63a2\u865a\u5b9e\uff0c\u4f46\u8f9b\u6377\u53eb\u4f5c\u201c\u4f5c\u8d3c \u5fc3\u865a\u201d\uff0c\u8bef\u89e3\u4ed6\u7684\u610f\u601d\uff0c\u614c\u5fd9\u63b7\u51fa\u4e00\u672c\u6bd2\u7ecf\uff0c\u4e5f\u8bb8\u679c\u771f\u662f\u8f9b\u4e5d\u9e4f\u592b\u5987\u5728\u5929\u4e4b\u7075\u4fdd\u4f51\uff0c\u8f9b\u6377\u8fd9 \u4e00\u7740\u53ef\u771f\u78b0\u4e0a\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u7684\u672c\u610f\u539f\u662f\u60f3\u8981\u7528\u6bd2\u7ecf\u6765\u8bf1\u60d1\u53cc\u715e\uff0c\u6025\u52a8\u593a\u4e66\u4e4b\u5ff5\uff0c\u800c\u4e2d\u6bd2\u53d7\u4f24\uff0c\u8fd9\u672c\u662f\u5f88\u6e3a\u832b\u7684 \u4e8b\uff0c\u4f46\u4ed6\u53ef\u4e0d\u77e5\u5230\u9ec4\u4e30\u4e5d\u8c6a\u4e4b\u9996\u201c\u6d77\u5929\u53cc\u715e\u201d\u4e00\u751f\u5f15\u529b\u91cf\u9057\u61be\u7684\u4e43\u662f\u4e0d\u80fd\u6709\u4e00\u8eab\u6bd2\u672f\uff0c\u662f\u4ee5 \u4ed6\u4eec\u5f80\u5f80\u52a8\u624b\u6740\u4eba\u975e\u5f97\u771f\u67aa\u771f\u5200\u4e0d\u53ef\uff0c\u4e0d\u80fd\u50cf\u6bd2\u541b\u91d1\u4e00\u9e4f\u4e00\u6837\u6740\u4eba\u4e0d\u89c1\u8840\u3002 \u3000\u3000\u4ed6\u4eec\u5927\u534a\u751f\u7684\u65f6\u95f4\u5728\u6c5f\u6e56\u4e0a\u6df7\uff0c\u6781\u60f3\u5bfb\u627e\u4e00\u90e8\u6bd2\u7ecf\uff0c\u4f46\u5374\u59cb\u7ec8\u4e0d\u80fd\u5982\u613f\uff0c\u5982\u4eca\u4ed6\u4eec\u5047\u5982 \u770b\u89c1\u8f9b\u6377\u63b7\u51fa\u7684\u8fd9\u672c\u6bd2\u7ecf\uff0c\u771f\u4e0d\u77e5\u8981\u5982\u4f55\u6b22\u5929\u559c\u5730\u4e86\u3002 \u3000\u3000\u201c\u62cd\u201d\u7684\u4e00\u58f0\u6e05\u54cd\uff0c\u6bd2\u7ecf\u843d\u6ce8\u5730\u4e0a\uff0c\u5728\u5bc2\u9759\u7684\u591c\u91cc\uff0c\u8fd9\u4e00\u58f0\u54cd\u58f0\uff0c\u4f20\u51fa\u8001\u8fdc\u53bb\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u673a\u8b66\u7684\u5f80\u540e\u4e00\u9000\uff0c\u6253\u91cf\u843d\u51fa\u6765\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4ed6\u4e00\u53ea\u5373\u5c06\u6cbe\u4e0a\u6bd2\u6db2\u7684\u811a\uff0c\u5374\u4e5f \u56e0\u6b64\u9000\u56de\u2014\u2014 \u3000\u3000\u6d1e\u4e2d\u4ecd\u662f\u9759\u5bc2\u5bc2\u7684\uff0c\u53ef\u662f\uff0c\u5374\u6709\u4e00\u672c\u4e66\u98de\u4e86\u51fa\u6765\u3002 \u3000\u3000\u201c\u6d77\u5929\u53cc\u715e\u201d\u5012\u5e95\u662f\u591f\u673a\u8b66\u7684\uff0c\u4e24\u4eba\u4e00\u5de6\u4e00\u53f3\u659c\u659c\u7a7f\u5f00\uff0c\u4ee5\u9632\u6d1e\u53e3\u6709\u4ec0\u4e48\u6697\u5668\u4e4b\u7c7b\u53d1 \u51fa\u3002 \u3000\u3000\u7126\u5316\u51b7\u7136\u54fc\u4e86\u4e00\u58f0\uff0c\u7528\u6bd4\u9b3c\u54ed\u8fd8\u96be\u542c\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u6d1e\u4e2d\u662f\u54ea\u4f4d\u670b\u53cb\uff1f\u662f\u2018\u5408\u5b57\u2019\u4e0a\u7684 \u670b\u53cb\uff0c\u6709\u79cd\u732e\u51fa\u6765\u9732\u4e86\u9762\uff0c\u5c31\u51ed\u54b1\u4eec\u5144\u5f1f\u96be\u9053\u8fd8\u4e0d\u591f\u8d44\u683c\u63a5\u5f85\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u679c\u7136\u662f\u9053\u5730\u7eff\u6797\u4eba\u7269\uff0c\u51fa\u53e3\u4fbf\u662f\u6c5f\u6e56\u5207\u53e3\uff0c\u6d1e\u4e2d\u8f9b\u6377\u5e76\u4e0d\u7406\u4f1a\uff0c\u5374\u6697\u6094\u81ea\u5df1\u5fc3\u6025\uff0c\u5047 \u5982\u4e00\u8ba1\u4e0d\u6210\uff0c\u53c8\u8d54\u4e0a\u8fd9\u90e8\u6bd2\u7ecf\uff0c\u53ef\u7b97\u662f\u201c\u5077\u9e21\u4e0d\u7740\u8680\u628a\u7c73\u4e86\u201d\u3002 \u3000\u3000\u201c\u4e0d\u89c1\u68fa\u6750\u4e0d\u6d41\u6cea\uff0c\u670b\u53cb\uff0c\u54b1\u4eec\u95ef\u4e86\uff1f\u201d \u3000\u3000\u4ed6\u53e3\u5934\u5982\u6b64\u8bf4\uff0c\u8111\u5b50\u53ef\u4e0d\u4f5c\u5982\u6b64\u60f3\uff0c\u6253\u4e00\u4e2a\u624b\u52bf\u7ed9\u7126\u52b3\uff0c\u53eb\u4ed6\u6697\u6697\u8dd1\u5230\u6d1e\u53e3\u53bb\u5bdf\u770b\u3002 \u3000\u3000\u7126\u52b3\u548c\u7126\u5316\u5fc3\u610f\u65e9\u901a\uff0c\u4e00\u58f0\u4e0d\u54cd\uff0c\u63a0\u5230\u6d1e\u524d\uff0c\u84e6\u7136\uff0c\u4ed6\u77a5\u90a3\u672c\u843d\u5728\u5730\u4e0a\u7684\u4e66\u7684\u6851\u76ae\u7eb8\u9762 \u4e0a\uff0c\u7aef\u7aef\u6b63\u6b63\u7684\u523b\u5212\u7740\u4e24\u4e2a\u4e86\u2014\u2014 \u3000\u3000\u201c\u6bd2\u7ecf\u201d\u3002 \u3000\u3000\u8fd9\u4e24\u4e2a\u5b57\u4e43\u662f\u7126\u5316\u7126\u52b3\u5144\u5f1f\u51e0\u5341\u5e74\u6765\u68a6\u5bd0\u4ee5\u6c42\u7684\uff0c\u7adf\u7136\u5728\u8fd9\u8352\u50fb\u7684\u6d77\u9a6c\u4e0a\u53d1\u73b0\uff0c\u4ed6\u4e0d\u7531 \u4e00\u9635\u72c2\u559c\uff0c\u63a0\u4e86\u8fc7\u53bb\uff0c\u6253\u4e00\u4e2a\u624b\u52bf\u7ed9\u7126\u5316\uff0c\u4f38\u624b\u4fbf\u62fe\u3002 \u3000\u3000\u7126\u52b3\u4e94\u5b98\u4e0d\u5168\uff0c\u6027\u60c5\u51b7\u6f20\u800c\u5f02\u4e8e\u5e38\u4eba\uff0c\u867d\u7136\u673a\u667a\u8fc7\u4eba\uff0c\u4f46\u662f\u5374\u662f\u795e\u7ecf\u604d\u5ffd\uff0c\u4e00\u65e6\u6709\u6025\u5927 \u4e8b\u4ef6\u53d1\u751f\uff0c\u603b\u662f\u4e0d\u80fd\u63a7\u5236\u81ea\u5df1\uff0c\u4ed6\u8fd9\u65f6\u523b\u91cc\u65e9\u5c31\u5fd8\u4e86\u63d0\u9632\uff0c\u4f38\u624b\u62fe\u8d77\u3002 \u3000\u3000\u7126\u5316\u5230\u5e95\u4e0d\u540c\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u4e0d\u5fd9\u2014\u2014\u201d \u3000\u3000\u4f46\u4ed6\u5fd8\u8bb0\u5f1f\u5f1f\u4e43\u662f\u8033\u804b\u4e4b\u4eba\uff0c\u4e00\u987f\u8db3\uff0c\u8eab\u4f53\u6709\u5982\u4e00\u652f\u7bad\u63a0\u5230\u5f1f\u5f1f\u7126\u52b3\u8eab\u8fb9\uff0c\u770b\u89c1\u90a3\u6bd2\u7ecf \u7aef\u7aef\u5c31\u5728\u773c\u524d\uff0c\u5fc3\u795e\u4e00\u9635\u72c2\u559c\uff0c\u987e\u4e0d\u5f97\u518d\u963b\u6320\u80de\u5f1f\uff0c\u4f46\u4ed6\u5374\u987e\u8651\u8f83\u591a\uff0c\u4e00\u9762\u53bb\u62fe\u6bd2\u7ecf\uff0c\u4e00\u9762 \u8fd8\u5288\u7a7a\u6253\u51fa\u4e00\u638c\uff0c\u5411\u6d1e\u4e2d\u865a\u865a\u9065\u51fb\uff0c\u4ee5\u9632\u6709\u4ec0\u4e48\u6bd2\u6c41\u3002\u53ef\u7b11\u4ed6\u4fe9\u4e00\u65f6\u806a\u660e\uff0c\u5230\u5934\u6765\u4ecd\u662f\u4e0d\u80fd \u628a\u63e1\u81ea\u5df1\uff0c\u800c\u4e2d\u4e86\u8f9b\u6377\u7684\u6bd2\u8ba1\u2014\u2014 \u3000\u3000\u201c\u62cd\u201d\uff0c\u56db\u53ea\u811a\u4e00\u9f50\u7aef\u7acb\u5728\u6bd2\u6db2\u6240\u5e03\u7684\u5708\u5708\u4e0a\u9762\u3002\u78a7\u7389\u65ad\u80a0\u4e4b\u6bd2\u5929\u4e0b\u65e0\u53cc\uff0c\u6bd2\u6027\u4e4b\u70c8\uff0c \u4f7f\u5f97\u4e24\u4eba\u811a\u4e0a\u7684\u978b\u7acb\u523b\u7834\u70c2\u800c\u6cbe\u5230\u811a\u4e0a\uff0c\u6d77\u5929\u53cc\u715e\u9661\u7136\u9192\u609f\uff0c\u4ed6\u4eec\u5df2\u77e5\u4e2d\u4e86\u5bf9\u65b9\u7684\u6bd2\uff0c\u7531\u4e8e \u4e0d\u9ebb\u4e0d\u75d2\u7684\u611f\u89c9\uff0c\u77e5\u9053\u8fd9\u6bd2\u6027\u975e\u6d45\uff0c\u4ed6\u4eec\u8fde\u68c0\u9a8c\u6bd2\u4f24\u7684\u529f\u592b\u90fd\u6ca1\u6709\uff0c\u7acb\u523b\u76d8\u819d\u52a8\u7528\u5185\u529f\uff0c\u90a3 \u672c\u68a6\u5bd0\u4ee5\u6c42\u7684\u201c\u6bd2\u7ecf\u201d\uff0c\u53ea\u5dee\u4e24\u5bf8\u4fbf\u843d\u5165\u624b\u4e2d\uff0c\u4ecd\u7136\u9759\u9759\u7684\u843d\u5728\u5730\u4e0a\uff0c\u6d77\u98ce\u5439\u62c2\u8fc7\uff0c\u7ffb\u5f00\u5c01 \u9762\u53c8\u843d\u4e0b\uff0c\u53d1\u51fa\u201c\u5f8b\u5f8b\u201d\u7684\u8f7b\u58f0\u3002 \u3000\u3000\u9ed1\u6697\u91cc\uff0c\u6d1e\u4e2d\u8f9b\u6377\u77aa\u7740\u773c\u76f4\u5230\u53cc\u715e\u4e2d\u6bd2\u800c\u5012\uff0c\u624d\u653e\u5fc3\u7684\u5401\u4e86\u4e00\u53e3\u6c14\uff0c\u5b89\u7136\u4e00\u7b11\uff0c\u76d8\u5728\u5730 \u4e0a\u4e5f\u5f00\u59cb\u7528\u5185\u5bb6\u529f\u592b\u53bb\u6cbb\u7597\u90a3\u4ecd\u7136\u6ca1\u6709\u75ca\u6108\u7684\u4f24\u52bf\u2014\u2014 \u3000\u3000\u6d1e\u5916\u6d1e\u5185\u76d8\u5750\u7740\u4e09\u4eba\uff0c\u90fd\u662f\u4e3e\u4e16\u9ad8\u624b\uff0c\u800c\u4e14\uff0c\u4ed6\u4eec\u4e4b\u95f4\u53c8\u6709\u7740\u4e0d\u5171\u6234\u5929\u7684\u8840\u4ec7\uff0c\u8fd9\u6837\u7684 \u5de7\u4e8b\uff0c\u96be\u9053\u662f\u8001\u5929\u6709\u610f\u5b89\u6392\u597d\u4e86\u7684\u5417\uff1f \u3000\u3000\u5230\u8fd9\u91cc\uff0c\u7b14\u8005\u4f3c\u4e4e\u5e94\u8be5\u8865\u8ff0\u4e00\u7b14\u201c\u6d77\u5929\u53cc\u715e\u201d\u4e3a\u4f55\u4f1a\u5230\u8fd9\u7a77\u8352\u6781\u50fb\u7684\u5730\u57df\u6765\u7684\u539f\u56e0\u2014\u2014 \u3000\u3000\u5f53\u5e74\uff0c\u5173\u4e1c\u4e5d\u8c6a\u7b2c\u4e00\u6b21\u89e3\u6563\u4e4b\u65e5\uff0c\u53cc\u715e\u5fc3\u7070\u610f\u61d2\u7684\u6765\u5230\u8fd9\u4e2a\u5c9b\u4e0a\uff0c\u628a\u8fd9\u4e2a\u5c9b\u505a\u4e3a\u8001\u5bb6\uff0c \u4e0d\u65ad\u7684\u7cbe\u7814\u6b66\u5b66\u3002 \u3000\u3000\u4ed6\u4eec\u867d\u7136\u5c61\u906d\u632b\u6298\uff0c\u4f46\u5728\u8fd9\u5c9b\u4e0a\u751f\u6d3b\u4e45\u4e86\uff0c\u96c4\u5fc3\u53c8\u53d1\uff0c\u7ec8\u4e8e\u51fa\u5c9b\u518d\u6574\u65d7\u9f13\u3002 \u3000\u3000\u7136\u800c\uff0c\u8fd9\u4e00\u6b21\u66f4\u662f\u6709\u5982\u6619\u82b1\u4e00\u73b0\uff0c\u5728\u62e6\u963b\u8f9b\u6377\u4e00\u6218\u4e2d\uff0c\u4e5d\u8c6a\u51e0\u4e4e\u5168\u519b\u8986\u6ca1\uff01\u867d\u7136\uff0c\u4ed6\u4eec \u628a\u8f9b\u6377\u6bc1\u4e86\uff08\u4ed6\u4eec\u4ee5\u4e3a\u5982\u6b64\uff09\uff0c\u4f46\u4e5f\u6ca1\u6cd5\u5728\u6c5f\u6e56\u4e0a\u7acb\u8db3\u3002 \u3000\u3000\u7b49\u5230\u8f9b\u6377\u5728\u594e\u5c71\u65e0\u4e3a\u5385\u4e0a\u58f0\u5a01\u5927\u632f\uff0c\u4ed6\u4eec\u83b7\u77e5\u82b1\u4e86\u5982\u6b64\u4ee3\u4ef7\u8f9b\u6377\u5374\u5e76\u6ca1\u6709\u6b7b\u53bb\uff0c\u800c\u4e14\u542c \u4f20\u8bf4\uff0c\u8f9b\u6377\u7684\u529f\u592b\u66f4\u662f\u589e\u52a0\u3002 \u3000\u3000\u8fd9\u4e2a\u6d88\u606f\u7ed9\u53cc\u715e\u5e26\u6765\u66f4\u5927\u7684\u6253\u51fb\uff0c\u4ed6\u4eec\u662f\u7edd\u671b\u4e86\uff0c\u4ed6\u4eec\u60f3\u5230\u5047\u5982\u8f9b\u6377\u8fd9\u6b21\u518d\u6765\u62a5\u4ec7\uff0c\u4ed6 \u4eec\u53ef\u4e0d\u662f\u5bf9\u624b\u4e86\u3002 \u3000\u3000\u6c42\u751f\u7684\u6b32\u671b\uff0c\u4f7f\u4ed6\u4fe9\u7acb\u523b\u89e3\u6563\u5173\u4e2d\u4e5d\u8c6a\uff0c\u5728\u767e\u65e0\u53bb\u5904\u4e4b\u4e0b\uff0c\u4ed6\u4eec\u51b3\u610f\u5230\u8fd9\u8352\u5c9b\u8001\u5bb6\u4e0a \u6765\uff0c\u5374\u662f\u51a4\u5bb6\u8def\u7a84\uff0c\u4ed6\u4eec\u5343\u65b9\u767e\u8ba1\u8eb2\u907f\u7684\u8f9b\u6377\uff0c\u4e5f\u6b63\u5728\u8fd9\u91cc\uff01 \u3000\u3000\u4e09\u66f4\u65f6\u5206\uff0c\u5929\u8272\u4ecd\u7136\u662f\u90a3\u4e48\u9ed1\uff0c\u5e03\u6ee1\u4e86\u661f\u6597\u3002 \u3000\u3000\u4e00\u4e2a\u65f6\u8fb0\u5f88\u5feb\u5c31\u8fc7\u53bb\u4e86\uff0c\u8f9b\u6377\u80f8\u4e2d\u4e86\u7136\uff0c\u5185\u4f24\u5b8c\u5168\u75ca\u6108\uff0c\u4ed6\u5fae\u5fae\u63d0\u4e00\u53e3\u6c14\uff0c\u5728\u4f53\u5185\u5b8c\u6210 \u6700\u540e\u4e00\u6b21\u5706\u6ee1\u7684\u8fd0\u884c\uff0c\u8e0c\u8e87\u6ee1\u5fd7\u7684\u8d70\u51fa\u5c71\u6d1e\uff0c\u659c\u773c\u7747\u90a3\u6d77\u5929\u53cc\u715e\uff0c\u4ecd\u7136\u76d8\u819d\u800c\u5750\uff0c\u8f9b\u6377\u77e5 \u9053\uff0c\u4ed6\u4eec\u7684\u529f\u529b\uff0c\u4ec5\u80fd\u628a\u6bd2\u6027\u903c\u4f4f\uff0c\u800c\u4e0d\u80fd\u81ea\u7597\uff0c\u867d\u7136\uff0c\u65ad\u80a0\u6bd2\u6027\u5df2\u662f\u5927\u53d8\u800c\u5f31\u3002 \u3000\u3000\u8f9b\u6377\u7f13\u7f13\u5761\u5230\u53cc\u715e\u524d\u9762\uff0c\u62fe\u8d77\u90a3\u672c\u81f4\u53cc\u715e\u4e8e\u7edd\u5730\u7684\u6bd2\u7ecf\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a \u3000\u3000\u201c\u6bd2\u7ecf\uff0c\u53c8\u662f\u6bd2\u7ecf\uff0c\u6551\u4e86\u6211\u4e00\u547d\u3002\u201d \u3000\u3000\u8f9b\u6377\u628a\u6bd2\u7ecf\u6536\u5165\u6000\u4e2d\uff0c\u53cc\u624b\u626c\u8d77\uff0c\u5728\u53cc\u715e\u9876\u5fc3\u7591\u4e86\u7591\uff0c\u4e00\u638c\u4fbf\u81ea\u62cd\u4e0b\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4e00\u4e2a\u5ff5\u5934\u95ea\u8fc7\u5fc3\u5934\uff0c\u4ed6\u5fd6\u9053\uff1a\u201c\u8fd9\u6837\u5b50\uff0c\u6211\u4e0d\u8d39\u5439\u7070\u4e4b\u529b\u4fbf\u80fd\u6253\u6b7b\u8fd9\u4e24\u5934\u755c\u751f\uff0c \u4f46\u8fd9\u5e76\u975e\u6b63\u5927\u5149\u660e\u7684\u624b\u6bb5\uff0c\u6211\u8f9b\u6377\u600e\u80fd\u91c7\u7528\uff0c\u563f\uff0c\u8fd9\u65ad\u80a0\u6bd2\u6027\u5927\u53d8\uff0c\u53ea\u6d88\u7528\u6d77\u87ba\u8089\u4fbf\u80fd\u89e3 \u5f97\uff0c\u6211\u4f55\u4e0d\u628a\u4ed6\u4eec\u7684\u6bd2\u6027\u89e3\u53bb\uff0c\u518d\u7528\u771f\u529f\u592b\u53bb\u62fc\u547d\uff0c\u53cd\u6b63\u6211\u7684\u529f\u592b\u8db3\u4ee5\u80dc\u5f97\u4e24\u4eba\u3002\u201d \u3000\u3000\u5fc3\u5ff5\u65e2\u5b9a\uff0c\u6536\u56de\u62cd\u4e0b\u7684\u624b\uff0c\u51e0\u4e2a\u8d77\u843d\uff0c\u63a0\u5230\u6d77\u8fb9\uff0c\u6355\u6349\u5341\u591a\u4e2a\u6d77\u87ba\uff0c\u8010\u8010\u70e6\u70e6\u5730\u628a\u8089\u62d6 \u51fa\uff0c\u62ff\u53bb\u653e\u5728\u53cc\u715e\u9762\u524d\u53eb\u9053\uff1a\u201c\u5582\uff0c\u5403\u4e86\u8fd9\u4e2a\u4fbf\u80fd\u89e3\u6bd2\u3002\u201d \u3000\u3000\u53cc\u715e\u867d\u7136\u4e2d\u6bd2\uff0c\u795e\u667a\u4ecd\u6e05\uff0c\u4ed6\u4eec\u60f3\u4e0d\u5230\u6d1e\u4e2d\u7adf\u662f\u4ed6\u4eec\u5230\u5904\u8eb2\u907f\u7684\u8f9b\u6377\uff0c\u81ea\u4efd\u5fc5\u6b7b\uff0c\u4f46\u89c1 \u8f9b\u6377\u60f3\u4e0b\u624b\u53c8\u4e0d\u4e0b\u624b\uff0c\u5012\u4ee5\u4e3a\u8f9b\u6377\u6709\u610f\u8981\u51cc\u8fb1\u81ea\u5df1\uff0c\u4ed6\u4eec\u5e73\u65e5\u51cc\u8fb1\u4eba\uff0c\u5230\u5934\u6765\u8981\u9053\u4eba\u51cc\u8fb1\uff0c \u5fc3\u4e2d\u6012\u6781\uff0c\u89c1\u8f9b\u6377\u5ffd\u53c8\u62d6\u51fa\u87ba\u8089\u7ed9\u81ea\u5df1\u5403\uff0c\u771f\u4e0d\u80fd\u65ad\u5b9a\u8f9b\u6377\u662f\u4ec0\u4e48\u610f\u601d\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u4eec\u8fdf\u8fdf\u4e0d\u80af\u5403\u4e0b\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u8f9b\u6377\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5c82\u80fd\u62ff\u6bd2\u98df\u76f8\u5bb3\uff0c\u8fd9\u73a9\u610f\u53ef\u4ee5 \u89e3\u6bd2\u54e9\u2014\u2014\u201d\u8bf4\u7740\u628a\u8089\u9012\u7740\uff0c\u7ad9\u5728\u4e00\u65c1\u3002 \u3000\u3000\u53cc\u715e\u89c1\u4ed6\u8bf4\u5f97\u771f\u5207\uff0c\u4e00\u9f50\u5403\u4e0b\u6d77\u87ba\u8089\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u7136\u9053\uff1a\u201c\u6211\u5c31\u5728\u8fd9\u513f\u7b49\u4f60\u4f24\u597d\u4e86\u4ee5\u540e\u6765\u4e2a\u7b97\u603b\u5e10\u2014\u2014\u201d \u3000\u3000\u53cc\u715e\u5fc3\u77e5\u4eca\u65e5\u4e0d\u80fd\u82df\u514d\uff0c\u4e0d\u5982\u62fc\u62fc\u53ef\u80fd\u5c1a\u6709\u4e00\u7ebf\u751f\u673a\uff0c\u4e0d\u518d\u7b54\u8154\uff0c\u4e00\u540c\u7528\u529f\u3002 \u3000\u3000\u6d77\u87ba\u8089\u679c\u80fd\u89e3\u6bd2\uff0c\u4e0d\u5230\u534a\u4e2a\u65f6\u8fb0\uff0c\u7126\u5316\u5df2\u662f\u6bd2\u7d20\u5c3d\u53bb\uff0c\u770b\u770b\u8f9b\u6377\uff0c\u5750\u5728\u81ea\u5df1\u8eab\u524d\u7ea6\u83ab\u4e24 \u4e08\u5730\u5730\u65b9\u76d1\u89c6\u7740\u81ea\u5df1\uff0c\u867d\u662f\u76d8\u819d\u7528\u529f\uff0c\u4f46\u4e00\u53cc\u795e\u76ee\u4e0d\u65f6\u95ea\u6765\u95ea\u53bb\uff0c\u6ce8\u89c6\u53cc\u715e\uff0c\u50cf\u662f\u732b\u513f\u5b88\u5019 \u8001\u9f20\u4e00\u6837\u3002 \u3000\u3000\u7126\u5316\u4e0d\u7531\u6012\u6781\u800c\u53eb\u9053\uff1a\u201c\u59d3\u8f9b\u7684\uff0c\u8981\u6218\u4fbf\u6218\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u51b7\u51b7\u63a5\u53e3\u9053\uff1a\u201c\u5435\u4ec0\u4e48\uff0c\u4f60\u7684\u5c0f\u755c\u751f\u5f1f\u5f1f\u8fd8\u6ca1\u6709\u597d\u5462\uff1f\u201d \u3000\u3000\u7126\u5316\u6108\u6012\uff0c\u957f\u58f0\u9053\uff1a\u201c\u597d\uff01\u597d\u2014\u2014\u201d \u3000\u3000\u4ed6\u4e00\u65f6\u6012\u58f0\u53e3\u7ed3\uff0c\u53ea\u201c\u597d\uff01\u597d\uff01\u201d\u63a5\u4e0d\u4e0b\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u53bb\u7406\u4ed6\uff0c\u84e6\u7136\u7acb\u8d77\u62bd\u51fa\u957f\u5251\u9053\uff1a\u201c\u5343\u91cc\u8fe2\u8fe2\uff0c\u59d3\u7126\u7684\u4f60\u4eec\u8d76\u6765\u9001\u6b7b\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c \u6211\u8f9b\u67d0\u5e76\u6ca1\u6709\u4e58\u4eba\u4e4b\u5371\uff0c\u4f60\u4eec\u6b7b\u4e5f\u5e94\u65e0\u61be\u2014\u2014\u201d \u3000\u3000\u4ed6\u53e3\u53e3\u58f0\u58f0\u8bf4\u53cc\u715e\u5fc5\u6b7b\uff0c\u5012\u6fc0\u8d77\u53cc\u715e\u7684\u51f6\u6027\uff0c\u7126\u5316\u51b7\u7b11\u4e00\u58f0\uff0c\u5bf9\u7126\u52b3\u671b\u4e00\u773c\u9053\uff1a\u201c\u9e7f\u6b7b \u8c01\u624b\uff0c\u53ea\u6015\u672a\u77e5\uff01\u201d \u3000\u3000\u8f9b\u6377\u70b9\u70b9\u5934\uff0c\u4e0d\u518d\u53d1\u8a00\u3002 \u3000\u3000\u53c8\u8fc7\u987f\u996d\u65f6\u5206\uff0c\u7126\u52b3\u4e5f\u5df1\u5eb7\u590d\uff0c\u4e24\u5144\u5f1f\u5e76\u7acb\u4e00\u8d77\uff0c\u534a\u4e08\u5f00\u5916\uff0c\u8f9b\u6377\u62b1\u5251\u800c\u7acb\uff0c\u5468\u56f4\u7684\u6c14 \u6c1b\u5145\u6ee1\u7740\u7d27\u5f20\u3002 \u3000\u3000\u5929\u8272\u9ed1\u6697\uff0c\u661f\u5149\u70b9\u70b9\uff0c\u591c\u8272\u82cd\u832b\u2014\u2014 \u3000\u3000\u8f9b\u6377\u62b1\u5251\u9ed8\u7977\uff1a\u201c\u7238\u3001\u5988\uff0c\u5b69\u513f\u4eca\u65e5\u8a93\u5fd7\u590d\u4ec7\u2014\u2014\u201d \u3000\u3000\u7977\u6bd5\u957f\u5251\u4e00\u6325\uff0c\u201c\u55e1\u201d\u7684\u4e00\u58f0\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u9001\u547d\u6765\u5427\u2014\u2014\u201d \u3000\u3000\u6d77\u5929\u53cc\u715e\u5e76\u4e0d\u602a\u8f9b\u6377\u5982\u6b64\u72c2\u50b2\uff0c\u4ed6\u4eec\u81ea\u77e5\u4eca\u591c\u4e4b\u6218\u51f6\u591a\u5409\u5c11\uff0c\u4f46\u4e5f\u53ea\u5f97\u786c\u7740\u5934\u76ae\u4e00\u6218\u3002 \u3000\u3000\u8f9b\u6377\u957f\u5251\u6709\u5982\u621f\u7acb\uff0c\u811a\u6b65\u4e00\u5c55\uff0c\u6e05\u5578\u4e00\u58f0\uff0c\u5f53\u5148\u53d1\u52a8\u529f\u52bf\u3002 \u3000\u3000\u5f53\u5e74\uff0c\u5728\u9f9f\u5c71\u9876\u5cf0\uff0c\u8f9b\u6377\u66fe\u88ab\u53cc\u715e\u8054\u624b\u4e4b\u4e0b\uff0c\u6253\u4e0b\u5c71\u8c37\uff0c\u5728\u8352\u5c71\u4e18\u4e0a\uff0c\u88ab\u4e5d\u8c6a\u56f4\u653b\uff0c\u4e5f \u66fe\u91cd\u4f24\u5782\u6b7b\uff0c\u8fd9\u4e00\u6b21\u89c1\u9762\u4e4b\u4e0b\uff0c\u4e0d\u518d\u5ba2\u6c14\uff0c\u51fa\u624b\u4e4b\u5f0f\uff0c\u5c3d\u662f\u72e0\u6bd2\u62db\u5f0f\uff0c\u975e\u53d6\u53cc\u715e\u6027\u547d\u800c\u540e\u5fc3 \u7518\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u4e0d\u7b49\u8f9b\u6377\u957f\u5251\u653b\u8fd1\uff0c\u56db\u638c\u9f50\u9f50\u7ffb\u98de\uff0c\u5404\u81ea\u52a8\u7528\u5185\u5bb6\u771f\u529b\uff0c\u5e26\u8d77\u4e86\u72c2\u5578\u98ce\u58f0\uff0c\u6392 \u7a7a\u8fce\u51fb\u800c\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u54fc\u4e00\u58f0\uff0c\u957f\u5251\u4e00\u6307\uff0c\u4e0b\u6c89\u4e24\u5bf8\uff0c\u4e00\u5f0f\u201c\u76d8\u5c71\u4e0b\u6c34\u201d\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u4e00\u80a1\u5185\u5bb6\u5251 \u98ce\u81ea\u5251\u5c16\u53d1\u51fa\uff0c\u76f4\u649e\u6d77\u5929\u53cc\u715e\u3002 \u3000\u3000\u540c\u65f6\u95f4\u91cc\uff0c\u5de6\u624b\u5288\u51fa\u4e00\u638c\uff0c\u4e5f\u81ea\u53d6\u5411\u53cc\u715e\u4e0b\u76d8\u3002 \u3000\u3000\u8f9b\u6377\u5185\u529b\u9020\u8be3\u7a81\u98de\u731b\u8fdb\uff0c\u4e00\u62fc\u4e4b\u4e0b\uff0c\u53cc\u715e\u987f\u89c9\u5bf9\u65b9\u529b\u9053\u5947\u7a81\uff0c\u4e0d\u7531\u9f50\u9f50\u9000\u540e\uff0c\u800c\u8f9b\u6377\u5374 \u4ec5\u8eab\u5b50\u4e00\u6643\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u5c51\u4e00\u54fc\uff0c\u957f\u5251\u518d\u4e3e\uff0c\u4e00\u5f0f\u201c\u4e4d\u60ca\u6885\u9762\u201d\uff0c\u5e73\u524a\u800c\u51fa\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u4e4b\u9996\u5929\u6b8b\u7126\u5316\u731b\u7136\u4e00\u66f2\u8eab\u5f62\uff0c\u5de6\u53f3\u624b\u9f50\u626c\uff0c\u53cc\u81c2\u4e00\u5408\uff0c\u6240\u51fb\u90e8\u4f4d\u4e43\u662f\u8f9b\u6377\u817f\u4e0a \u201c\u5173\u5143\u7a74\u9053\u3002\u201d \u3000\u3000\u540c\u65f6\u5929\u5e9f\u7126\u52b3\u4e5f\u81ea\u51fa\u62db\uff0c\u4e00\u51fb\u4e4b\u4e0b\uff0c\u6253\u5411\u8f9b\u6377\u5de6\u80a9\u3002 \u3000\u3000\u8f9b\u6377\u62db\u5f0f\u843d\u7a7a\uff0c\u4e0d\u518d\u7528\u8001\uff0c\u5012\u9000\u4e00\u6b65\uff0c\u957f\u5251\u5f80\u56de\u4e00\u64a4\uff0c\u4e00\u5f0f\u201c\u9f99\u89d2\u7acb\u6218\u201d\uff0c\u53cd\u51fb\u7126\u5316\u3002 \u3000\u3000\u4e09\u4eba\u4e00\u62db\u4e00\u5f0f\uff0c\u4e0d\u5230\u76cf\u8336\u65f6\u5206\uff0c\u4fbf\u62c6\u4e86\u5c06\u8fd1\u767e\u62db\u3002 \u3000\u3000\u8f9b\u6377\u8d8a\u6218\u8d8a\u52c7\uff0c\u957f\u5251\u6108\u6325\u6108\u5feb\uff0c\u4f46\u89c1\u4e00\u56e2\u5149\u5f71\u56f4\u7740\u56db\u5904\u95ea\u52a8\uff0c\u6d77\u5929\u53cc\u715e\u6e10\u6e10\u5df2\u88ab\u903c\u5728\u5251 \u5708\u4e2d\u3002 \u3000\u3000\u9ed1\u6697\u4e2d\uff0c\u4e00\u9053\u5149\u534e\u6709\u5982\u9f99\u98de\u98ce\u821e\uff0c\u770b\u6a21\u6837\uff0c\u6d77\u5929\u53cc\u715e\u5df2\u7136\u5b8c\u5168\u5403\u4e8f\u4e86\uff0c\u8f9b\u6377\u5251\u5f0f\u4e0d\u505c\uff0c \u6d77\u5929\u53cc\u715e\u8d8a\u6218\u8d8a\u60ca\uff0c\u5b8c\u6210\u5904\u5728\u4e0b\u98ce\u4e4b\u5f0f\u3002 \u3000\u3000\u84e6\u7136\uff0c\u7126\u5316\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u62f3\u6fc0\u626c\u800c\u51fa\u3002 \u3000\u3000\u8fd9\u4e00\u62f3\u7126\u5316\u4e43\u662f\u60f3\u626d\u8f6c\u5c40\u52bf\uff0c\u7528\u51fa\u4e86\u4e00\u5341\u4e8c\u6210\u771f\u529b\uff0c\u529b\u9053\u4e4b\u5f3a\uff0c\u7adf\u5fae\u5fae\u5e26\u6709\u98ce\u96f7\u4e4b\u58f0\u3002 \u3000\u3000\u5929\u5e9f\u7126\u52b3\u5fc3\u610f\u5df2\u548c\u7126\u5316\u76f8\u901a\uff0c\u7126\u5316\u957f\u62f3\u624d\u51fa\uff0c\u7126\u52b3\u53cc\u638c\u5df2\u662f\u4e00\u5f0f\u201c\u53cc\u98de\u638c\u201d\uff0c\u659c\u98de\u800c \u51fa\uff0c\u53d6\u5411\u8f9b\u633a\u53cc\u80c1\u3002 \u3000\u3000\u8f9b\u6377\u957f\u5251\u5982\u8679\uff0c\u4e00\u541e\u4e00\u5410\uff0c\u5251\u5f0f\u5fae\u6536\uff0c\u7126\u5316\u94c1\u62f3\u6253\u51fa\uff0c\u89c2\u5f97\u6e05\u5207\uff0c\u95ea\u51fa\u5251\u5708\uff0c\u957f\u7b11\u9053\uff1a \u201c\u600e\u4e48\u6837\uff1f\u201d \u3000\u3000\u8f9b\u6377\u51b7\u5598\u9053\uff1a\u201c\u518d\u8bd5\u8bd5\u770b\u2014\u2014\u201d \u3000\u3000\u957f\u5251\u659c\u659c\u4e00\u5212\uff0c\u6479\u7136\u53d8\u62db\u5f0f\uff0c\u4e00\u5f0f\u201c\u51b7\u6885\u62c2\u9762\u201d\u659c\u659c\u524a\u51fa\uff0c\u8f9b\u6377\u4e43\u662f\u62b1\u7740\u53d6\u654c\u4eba\u6027\u547d\u800c \u540e\u7518\u5fc3\uff0c\u8fd9\u4e00\u62db\u5185\u529b\u8d2f\u6ce8\uff0c\u524a\u51fa\u4e4b\u540e\uff0c\u5251\u6c14\u6709\u5982\u60ca\u6d9b\u62cd\u51fb\uff0c\u5a01\u52bf\u9a87\u4eba\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u751f\u6027\u60c5\u602a\u5f02\u800c\u5014\u5f3a\uff0c\u5047\u82e5\u4eba\u6709\u4ec7\u4e8e\u4ed6\uff0c\u4ed6\u5fc5\u4ee5\u5341\u5206\u62a5\u590d\uff0c\u4f55\u51b5\u6d77\u5929\u53cc\u715e\u4e43\u6740\u7236\u6bcd \u4e4b\u4ec7\u4eba\uff0c\u4ed6\u6068\u4e4b\u5165\u9aa8\uff0c\u770b\u7740\u4e24\u5144\u5f1f\u4e00\u526f\u4e0d\u582a\u5165\u76ee\u7684\u4e11\u76f8\uff0c\u8d8a\u662f\u5200\u706b\u9e70\u80f8\uff0c\u6068\u4e0d\u5f97\u628a\u5bf9\u65b9\u4e24\u4eba \u788e\u5c38\u4e07\u65ad\u3002 \u3000\u3000\u8fd9\u4e00\u5f0f\u9012\u51fa\u7126\u5316\u5927\u5403\u4e00\u60ca\uff0c\u614c\u5fd9\u540e\u64a4\uff0c\u957f\u5251\u4e00\u6536\u518d\u523a\uff0c\u7528\u7684\u4e43\u662f\u5927\u884d\u5341\u5f0f\u4e2d\u7684\u201c\u5cf0\u56de\u8def \u8f6c\u201d\u3002\u8fd9\u4e00\u5f0f\u53d8\u5316\u4e4b\u591a\uff0c\u4ee4\u4eba\u548b\u820c\u3002 \u3000\u3000\u6d77\u5929\u53cc\u715e\u6559\u9886\u8fc7\u5927\u884d\u5341\u5f0f\u7684\u5a01\u529b\uff0c\u7126\u5316\u8eab\u4e0d\u505c\uff0c\u518d\u5411\u540e\u9000\u3002 \u3000\u3000\u8f9b\u6377\u957f\u5251\u4e00\u9886\uff0c\u8fd9\u4e00\u5f0f\u53d8\u5f97\u597d\u5feb\uff0c\u76f4\u523a\u5f2f\u4e3a\u6a2a\u524a\uff0c\u7126\u5316\u4e0d\u9632\uff0c\u7acb\u523b\u4fbf\u8981\u53d7\u4f24\uff0c\u7126\u52b3\u5927\u5927 \u5403\u60ca\uff0c\u53eb\u8db3\u771f\u529b\uff0c\u4e00\u638c\u6253\u51fa\uff0c\u62f3\u98ce\u6fc0\u8361\uff0c\u7a7a\u6c14\u53d1\u51fa\u9e23\u9e23\u4e4b\u58f0\uff0c\u597d\u4e0d\u60ca\u4eba\u3002 \u3000\u3000\u8f9b\u6377\u9661\u7136\u89c9\u5f97\u5251\u4e0a\u597d\u50cf\u88ab\u5343\u65a4\u9524\u6253\u5f97\u504f\u4e00\u504f\uff0c\u51c6\u5934\u5931\u53bb\uff0c\u5fc3\u4e2d\u4e5f\u6697\u60ca\u90a3\u7126\u52b3\u638c\u529b\u4e4b\u91cd\u3002 \u3000\u3000\u7126\u5316\u4e4b\u5371\u65e2\u89e3\uff0c\u53cc\u638c\u201c\u53cc\u9f99\u51fa\u6d77\u201d\uff0c\u5e76\u51fb\u800c\u51fa\uff0c\u8f9b\u6377\u84e6\u7136\u8eab\u4f53\u4e00\u4ef0\uff0c\u53cc\u8db3\u8fde\u62ac\uff0c\u8e22\u5411\u7126 \u5316\u4e0b\u76d8\uff0c\u7126\u52b3\u914d\u5408\u54e5\u54e5\u653b\u52bf\uff0c\u53cc\u62f3\u518d\u51fb\uff0c\u8f9b\u6377\u8eab\u5b50\u4e0d\u7a33\uff0c\u4e0d\u80fd\u786c\u63a5\uff0c\u540e\u9000\u6536\u62db\u3002 \u3000\u3000\u4e00\u8fde\u4e24\u6b21\uff0c\u653b\u52bf\u5c3d\u88ab\u90a3\u4e94\u5b98\u4e0d\u5168\u7684\u5e9f\u4eba\u7834\u574f\uff0c\u4e0d\u7531\u5927\u6012\uff0c\u4e00\u5251\u659c\u659c\u98de\u8d77\uff0c\u6253\u5411\u7126\u5316\u5fc3 \u53e3\u3002 \u3000\u3000\u7126\u52b3\u4e24\u6b21\u5f97\u901e\uff0c\u94c1\u62f3\u518d\u626c\uff0c\u731b\u70c8\u4e00\u51fb\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u51b7\u4e00\u54fc\uff0c\u5de6\u624b\u4e00\u6325\u4e00\u5f0f\u201c\u7a7a\u7a7a\u62f3\u62db\u201d\u4e2d\u7684\u201c\u4e07\u6cc9\u98de\u7a7a\u201d\u628a\u7126\u52b3\u4e07\u65a4\u529b\u5378\u5230\u4e00\u8fb9\uff0c \u7126\u52b3\u8eab\u8eaf\u4e0d\u7a33\uff0c\u51b2\u524d\u6570\u6b65\u3002 \u3000\u3000\u8f9b\u6377\u6068\u900f\u8fd9\u5bb6\u4f19\uff0c\u957f\u5251\u4e00\u8f6c\uff0c\u4e00\u5f0f\u201c\u5012\u5f15\u9634\u9633\u201d\uff0c\u53cd\u624b\u524a\u51fa\u3002 \u3000\u3000\u7126\u52b3\u91cd\u5fc3\u4e00\u5931\uff0c\u811a\u8ddf\u4e0d\u7a33\uff0c\u654c\u5251\u5df2\u7136\u653b\u8fd1\uff0c\u7acb\u523b\u5c31\u5f97\u4e27\u547d\u3002 \u3000\u3000\u4e09\u4e08\u4ee5\u5916\u7126\u5316\u63f4\u6c42\u4e0d\u53ca\uff0c\u53ea\u5f97\u7a7a\u81ea\u7740\u6025\u3002 \u3000\u3000\u7126\u52b3\u751f\u6027\u9aa1\u608d\uff0c\u89c1\u81ea\u5df1\u6027\u547d\u96be\u4fdd\uff0c\u4e0d\u7531\u751f\u51fa\u540c\u5f52\u4e8e\u5c3d\u7684\u60f3\u6cd5\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u5929\u5e9f\u7126 \u52b3\u53f3\u624b\u731b\u7136\u4e00\u5f15\uff0c\u62a4\u4f4f\u9876\u95e8\uff0c\u5de6\u624b\u4e0d\u987e\u654c\u5251\uff0c\u4e00\u62f3\u5bf9\u8f9b\u6377\u957f\u5251\u4e0a\u6253\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u5251\u523a\u5982\u98ce\uff0c\u4f46\u95fb\u201c\u5bdf\u201d\u7684\u4e00\u58f0\uff0c\u5929\u5e9f\u7126\u52b3\u6709\u53e3\u96be\u8a00\uff0c\u90a3\u53d1\u4e0d\u51fa\u58f0\u7684\u54d1\u5df4\u8154\u5b50\u786c\u751f\u751f \u7531\u4e8e\u5267\u75db\u7684\u539f\u6545\uff0c\u201c\u54d1\u201d\u7684\u51c4\u51c4\u4e00\u543c\uff0c\u4e00\u6761\u5de6\u81c2\u5df2\u7136\u88ab\u8f9b\u6377\u65a9\u65ad\u3002 \u3000\u3000\u7d27\u63a5\u7740\uff0c\u201c\u6258\u201d\u7684\u4e00\u54cd\uff0c\u8f9b\u6377\u5728\u767e\u5fd9\u4e2d\u907f\u53bb\u7126\u52b3\u62fc\u547d\u7684\u4e00\u62f3\uff0c\u90a3\u4e00\u62f3\u4e2d\u5fc3\u800c\u4eba\uff0c\u201c\u6258\u201d \u7684\u6253\u5728\u8f9b\u6377\u957f\u5251\u5251\u9102\u4e0a\u3002 \u3000\u3000\u8f9b\u6377\u4f46\u89c9\u5bf9\u65b9\u529b\u9053\u597d\u5927\uff0c\u624b\u5fc3\u4e00\u70ed\uff0c\u957f\u5251\u51e0\u4e4e\u8131\u624b\u800c\u98de\uff0c\u94c1\u8155\u4e00\u632b\uff0c\u529b\u6301\u957f\u5251\uff0c\u4f46\u95fb \u201c\u6258\u201d\u7684\u4e00\u58f0\uff0c\u7cbe\u94a2\u5236\u7684\u5251\u9102\uff0c\u9f50\u67c4\u800c\u6298\uff0c\u53ef\u77e5\u8fd9\u4e00\u62f3\u597d\u4e0d\u60ca\u4eba\uff01 \u3000\u3000\u8f9b\u6377\u5251\u5f0f\u4e0d\u505c\uff0c\u53cd\u624b\u4e00\u64a9\uff0c\u7126\u52b3\u63d0\u89c9\u5de6\u8138\u4e00\u51c9\uff0c\u4e00\u652f\u4ec5\u6709\u7684\u5de6\u8033\u88ab\u524a\u53bb\u3002\u8f9b\u6377\u54ac\u7259\u5207\u9f7f \u9053\uff1a \u3000\u3000\u201c\u4f60\u4e5f\u6709\u4eca\u5929\u2014\u2014\u201d \u3000\u3000\u5251\u5b50\u4e00\u6296\uff0c\u5206\u5fc3\u800c\u523a\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u4e00\u5207\u90fd\u5728\u6781\u77ed\u7684\u4e00\u77ac\u95f4\u5b8c\u6210\uff0c\u5929\u6b8b\u7126\u5316\u8eab\u4f53\u624d\u5230\uff0c\u8f9b\u6377\u4e00\u5251\u5df2\u7136\u5206\u5fc3\u76f4\u5165\uff0c\u5728\u5929 \u5e9f\u7126\u52b3\u7684\u8eab\u4f53\u4e0a\u7559\u4e86\u4e00\u4e2a\u900f\u660e\u7a9f\u7abf\u3002 \u3000\u3000\u53ef\u601c\u7126\u52b3\u4e00\u751f\u4f5c\u6076\uff0c\u5230\u5934\u6765\u4ecd\u5728\u4ec7\u4eba\u5251\u4e0b\u4f0f\u8bdb\uff01 \u3000\u3000\u7126\u52b3\u597d\u4e0d\u5f3a\u608d\uff0c\u4e34\u6b7b\u72b9\u6076\uff0c\u53f3\u638c\u4e34\u7a7a\u76f2\u76ee\u4e00\u51fb\uff0c\u53ea\u51fb\u5728\u5730\u4e0a\uff0c\u77f3\u5c51\u6f2b\u5929\u7eb7\u98de\uff0c\u70df\u96fe\u8ff7 \u6f2b\u3002 \u3000\u3000\u5929\u6b8b\u7126\u5316\u4e0d\u53bb\u6551\u63f4\uff0c\u773c\u89c1\u80de\u5f1f\u4f0f\u8bdb\uff0c\u81ea\u5fd6\u96be\u4e0e\u5339\u654c\uff0c\u4e58\u7740\u8f9b\u6377\u88ab\u6f2b\u5929\u77f3\u6c99\u8ff7\u8499\u4e4b\u9645\uff0c\u53cd \u8eab\u9003\u8d70\u3002 \u3000\u3000\u8f9b\u6377\u4f55\u7b49\u529f\u529b\uff0c\u8033\u95fb\u516b\u65b9\uff0c\u5df2\u77e5\u7126\u5316\u8981\u9003\uff0c\u8db3\u5c16\u70b9\u5730\uff0c\u817e\u7a7a\u63a0\u51fa\u90a3\u6f2b\u5929\u7070\u6c99\uff0c\u77a5\u76ee\u4e4b \u4e0b\uff0c\u89c1\u90a3\u5929\u6b8b\u7126\u5316\u5df2\u9003\u5728\u4e94\u4e08\u4ee5\u5916\u3002 \u3000\u3000\u6240\u8c13\u5929\u9053\u4e0d\u723d\uff0c\u65e0\u5de7\u4e0d\u5de7\uff0c\u7126\u5316\u4e00\u65f6\u5fc3\u6025\u5fd8\u8bb0\u521a\u624d\u4e2d\u6bd2\u7684\u60c5\u5f62\uff0c\u7adf\u4e0d\u63d0\u9632\u5730\u4e0a\u7684\u65ad\u80a0\u6bd2 \u5708\uff0c\u5929\u7f51\u6062\u6062\uff0c\u758f\u800c\u4e0d\u6f0f\uff0c\u5929\u6b8b\u4e00\u811a\u6b63\u597d\u8e0f\u5728\u6bd2\u6db2\u4e0a\uff0c\u8eab\u5b50\u4e00\u9635\u6447\u6446\u4e0d\u5b9a\uff0c\u6bd2\u6027\u5df2\u7136\u5185\u4fb5\u3002 \u8f9b\u6377\u4ef0\u5929\u51c4\u547c\u9053\uff1a \u3000\u3000\u201c\u7238\u3001\u5988\uff0c\u770b\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u957f\u5251\u8131\u624b\u800c\u98de\uff0c\u628a\u518d\u5ea6\u4e2d\u6bd2\u7684\u7126\u5316\u8d2f\u5fc3\u9876\u5728\u5730\u4e0a\u3002\u5173\u4e2d\u4e5d\u8c6a\u4e4b\u9996\u2014\u2014\u201c\u6d77\u5929\u53cc\u715e\u201d \u7ec8\u4e8e\u5728\u8fd9\u7a77\u8352\u6781\u50fb\u7684\u6d77\u5c9b\u4e0a\u4e86\u7ed3\u4ed6\u4eec\u7f6a\u6076\u7684\u4e00\u751f\uff01 \u3000\u3000\u84e6\u7136\uff0c\u4e00\u9635\u6d77\u98ce\u5439\u6765\uff0c\u628a\u8f9b\u6377\u7684\u51c4\u547c\u58f0\u97f3\u4f20\u81f3\u9065\u8fdc\u7684\u5929\u8fb9\uff0c\u6708\u513f\uff0c\u661f\u661f\uff0c\u6e05\u98ce\uff0c\u5b83\u4eec\u4f3c \u4e4e\u4e5f\u5728\u4e3a\u5b64\u5b50\u6ce3\u8840\u690e\u5fc3\u7684\u51c4\u547c\u800c\u6d41\u6cea\u2014\u2014 \u3000\u3000\u826f\u4e45\uff0c\u8f9b\u6377\u7f13\u6b65\u4e0a\u524d\uff0c\u64e6\u7684\u4e00\u58f0\u62d4\u51fa\u4e86\u5c38\u4f53\u4e0a\u7684\u957f\u5251\u3002 \u3000\u3000\u4ed6\u5bf9\u5730\u4e0a\u7684\u4e24\u5177\u5c38\u4f53\u77a7\u90fd\u4e0d\u77a7\uff0c\u5374\u4ef0\u9996\u671b\u7740\u9ed1\u6c89\u7684\u5929\u9645\uff0c\u591c\u98ce\u4e2d\uff0c\u5fae\u5fae\u661f\u5149\u4e0b\uff0c\u4ed6\u767d\u6790 \u7684\u8138\u5b54\u66f4\u52a0\u767d\u4e86\u3002 \u3000\u3000\u8d77\u521d\uff0c\u4ed6\u8111\u4e2d\u4e71\u6781\uff0c\u50cf\u662f\u4e07\u5934\u5343\u7eea\uff0c\u5374\u53c8\u4f3c\u4e00\u7247\u7a7a\u767d\u3002\u6e10\u6e10\u7684\uff0c\u90a3\u4e9b\u96f6\u4e71\u7684\u5f71\u5b50\u90fd\u6210\u4e86 \u5b8c\u6574\u7684\u5f62\u50cf\uff0c\u4e00\u4e00\u4ece\u4ed6\u8111\u6d77\u4e2d\u98d8\u8fc7\u2014\u2014 \u3000\u3000\u90a3\u662f\u591a\u4e48\u7684\u6df1\u523b\uff0c\u591a\u4e48\u7684\u6e05\u65b0\uff0c\u5c31\u50cf\u6628\u5929\u53d1\u751f\u7684\u4e00\u6837\uff0c\u4e91\u5357\uff0c\u6606\u660e\uff0c\u6ec7\u6c60\uff0c\u8f9b\u5bb6\u6751\u2026\u2026 \u3000\u3000\u6bcd\u4eb2\u8d64\u88f8\u5730\u5728\u5bd2\u98ce\u4e2d\u53d7\u7740\u60e8\u7edd\u4eba\u6027\u7684\u4fae\u8fb1\uff0c\u90a3\u773c\u4e2d\u6240\u6d41\u9732\u7684\u7edd\u671b\u548c\u7f9e\u6012\u2026\u2026\u7236\u4eb2\u7d27\u54ac\u7740 \u7259\uff0c\u98a4\u6296\u7684\u624b\u629a\u5728\u4ed6\u7684\u5934\u4e0a\uff0c\u7259\u6839\u9c9c\u8840\u4ece\u7259\u7f1d\u4e2d\u4e1d\u4e1d\u6e17\u51fa\u2026\u2026\u7136\u540e\uff0c\u6b7b\u5728\u4ec7\u4eba\u638c\u4e0b\u2026\u2026 \u3000\u3000\u8fd9\u4e00\u5e55\u4e00\u5e55\uff0c\u6709\u6761\u4e0d\u7d0a\u5730\u95ea\u8fc7\u8f9b\u6377\u7684\u5fc3\uff0c\u8f9b\u6377\u5fc3\u4e2d\u6709\u5982\u6012\u6d9b\u6c79\u6d8c\u822c\u8d77\u4f0f\u4e0d\u505c\uff0c\u4f46\u4ed6\u7684\u8138 \u4e0a\u5374\u6f20\u7136\u5f97\u6709\u5982\u4e00\u5f20\u767d\u7eb8\u3002 \u3000\u3000\u4ed6\u8138\u4e0a\u4e24\u884c\u6e05\u6cea\u7f13\u7f13\u5730\u6d41\u4e86\u4e0b\u6765\uff0c\u4e00\u6ef4\u4e00\u6ef4\u843d\u5728\u80f8\u524d\uff0c\u895f\u4e0a\u987f\u65f6\u6e7f\u4e86\u4e00\u7247\u3002 \u3000\u3000\u4ed6\u50cf\u4e00\u5c0a\u77f3\u50cf\u4e00\u822c\uff0c\u4fdd\u6301\u8fd9\u6837\u7684\u59ff\u52bf\u81f3\u5c11\u534a\u4e2a\u65f6\u8fb0\u4e4b\u4e45\u2014\u2014 \u3000\u3000\u7136\u800c\u4ed6\u7684\u5fc3\u4e2d\uff0c\u8fd9\u523b\u513f\u5df2\u8db3\u8db3\u8fc7\u4e86\u4e8c\u5341\u5e74\uff01 \u3000\u3000\u8f9b\u633a\u5e73\u65e5\u9664\u4e86\u5728\u5434\u51cc\u98ce\u9762\u524d\uff0c\u603b\u662f\u9634\u6c89\u800c\u5185\u5411\uff0c\u611f\u60c5\u6df1\u85cf\uff0c\u8fd9\u6b64\u65e5\u5b50\u6765\u4ed6\u4f3c\u4e4e\u5bf9\u7236\u6bcd\u7684 \u5927\u4ec7\u5df2\u662f\u5fd8\u6000\uff0c\u76f4\u5230\u8fd9\u65f6\uff0c\u4ed6\u624b\u5203\u4e86\u6d77\u5929\u53cc\u715e\uff0c\u90a3\u9690\u85cf\u5728\u5fc3\u6df1\u5904\u7684\u611f\u60c5\u4e00\u5e76\u7206\u53d1\u51fa\u6765\u2026\u2026 \u3000\u3000\u4e5f\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u5589\u5499\u4e2d\u53d1\u51fa\u4f4e\u6c89\u7684\u58f0\u97f3\uff1a\u201c\u7238\u3001\u5988\uff0c\u5b69\u513f\u66ff\u4f60\u4eec\u62a5\u4ec7\u4e86\u2014\u2014\u201d \u3000\u3000\u90a3\u773c\u6cea\u5982\u6cc9\u6c34\u822c\u6d8c\u51fa\uff0c\u6ed4\u6ed4\u4e0d\u7edd\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u4f4e\u58f0\u5531\u4e86\u8d77\u6765\uff1a \u3000\u3000\u201c\u5357\u5c9b\u70c8\u70c8\uff0c\u98d8\u98ce\u53d1\u53d1\uff0c\u6c11\u83ab\u4e0d\u6740\uff0c\u6211\u72ec\u4e0d\u5bb3\uff01 \u3000\u3000\u3000\u5357\u5c71\u5f8b\u5f8b\uff0c\u98d8\u98ce\u5f17\u5f17\uff0c\u6c11\u83ab\u4e0d\u6740\uff0c\u6211\u72ec\u4e0d\u5352\uff01\u201d \u3000\u3000\u4ed6\u53cd\u590d\u5730\u5531\u7740\uff0c\u58f0\u8c03\u6108\u6765\u6108\u9ad8\uff0c\u771f\u5982\u675c\u9e43\u6ce3\u8840\uff0c\u5deb\u5ce1\u733f\u557c\u3002 \u3000\u3000\u201c\u62cd\uff01\u201d\u4e00\u58f0\uff0c\u60ca\u7834\u6c89\u5bc2\u7684\u591c\uff0c\u4e5f\u60ca\u9192\u4e86\u75f4\u7136\u7684\u8f9b\u6377\uff0c\u4ed6\u4f4e\u5934\u4e00\u770b\uff0c\u624b\u4e2d\u957f\u5251\u5df2\u88ab\u4ed6\u6298 \u4e3a\u4e24\u622a\uff0c\u5de6\u624b\u6267\u7740\u5251\u8eab\uff0c\u53f3\u624b\u53ea\u5269\u4e86\u4e00\u4e2a\u67c4\u513f\u3002 \u3000\u3000\u4ed6\u7684\u53cc\u81c2\u7f13\u7f13\u4e86\u4e0b\u6765\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u5251\u8eab\u548c\u5251\u67c4\u4e00\u9f50\u5728\u5730\u4e0a\uff0c\u4ed6\u77a7\u90fd\u4e0d\u77a7\uff0c\u8f6c\u8eab\u5c31\u8d70\u2014\u2014 \u3000\u3000\u4e0d\u6d88\u4e24\u4e09\u8d77\u843d\uff0c\u4ed6\u7684\u5f71\u5b50\u5df2\u6d88\u5931\u5728\u91cd\u91cd\u7684\u9ed1\u6697\u4e4b\u4e2d\u3002 \u3000\u3000\u5c9b\u4e0a\uff0c\u9759\u9759\u7684\u8eba\u7740\u4e5f\u66fe\u6a2a\u884c\u4e00\u4e16\u7684\u201c\u6d77\u5929\u53cc\u715e\u201d\uff0c\u5728\u8fd9\u8352\u5c9b\u4e0a\uff0c\u53ea\u6709\u6d77\u6c34\uff0c\u6d6a\u82b1\u548c\u5e73\u6c99 \u966a\u7740\u4e24\u4e2a\u7f6a\u6076\u7684\u7075\u9b42\uff0c\u5982\u679c\u8fd8\u8981\u8bf4\u6709\uff0c\u90a3\u4fbf\u662f\u66fe\u81f4\u4ed6\u4eec\u4e8e\u6b7b\u5730\u7684\u65ad\u80a0\u6bd2\u6db2\u2014\u2014 \u3000\u3000\u6d77\u5cb8\u4e0a\uff0c\u8f9b\u6377\u9ad8\u626c\u8d77\u5e06\uff0c\u4e00\u821f\u8f7b\u8f7b\u6ed1\u51fa\u6d77\u5cb8\uff0c\u5f53\u5929\u8fb9\u6700\u540e\u4e00\u9897\u661f\u7184\u706d\u65f6\uff0c\u5c0f\u821f\u53ea\u5728\u6a21\u7cca \u7684\u5730\u5e73\u7ebf\u4e0a\u73b0\u51fa\u4e00\u70b9\u5f71\u5b50\u3002 \u3000\u3000\u9ece\u660e\u4e86\uff0c\u5929\u9645\u73b0\u51fa\u4e00\u4e1d\u66d9\u5149\u2014\u2014 \u3000\u3000\u5b81\u6ce2\uff0c\u9ece\u660e\u2014\u2014 \u3000\u3000\u91d1\u9ec4\u8272\u7684\u671d\u9633\uff0c\u7167\u5728\u6e2f\u5f2f\u4e2d\uff0c\u5fae\u5fae\u7684\u6ce2\u6d9b\u6380\u8d77\u4e00\u4e2a\u4e2a\u91d1\u8272\u7684\u5c16\u513f\u3002 \u3000\u3000\u6668\u98ce\u5439\u6765\u4e00\u80a1\u54b8\u6e7f\u800c\u7565\u5e26\u8165\u5473\u7684\u6d77\u7684\u6c14\u606f\uff0c\u51fa\u6e2f\u7684\u8239\u8236\u4e0a\u68a2\u516c\u4eec\u5406\u559d\u4e4b\u58f0\u6b64\u8d77\u5f7c\u843d\uff0c\u4e0d \u7edd\u4e8e\u8033\u3002\u81ea\u53e4\u5c31\u662f\u4e1c\u5357\u6cbf\u6d77\u7684\u5927\u6e2f\uff0c\u6700\u8fd1\u7531\u4e8e\u6e2f\u53e3\u6c34\u6d45\u53ca\u6cc9\u5dde\u7684\u5174\u8d77\uff0c\u5df1\u9010\u6e10\u663e\u5f97\u4e0d\u53ca\u4ee5\u524d \u7e41\u8363\u4e86\u3002 \u3000\u3000\u5f53\u5e74\u610f\u5927\u5229\u4eba\u9a6c\u53ef\u52c3\u6d1b\u5728\u5143\u671d\u505a\u5b98\uff0c\u56de\u56fd\u540e\u6240\u64b0\u7684\u201c\u4e1c\u65b9\u89c1\u95fb\u5f55\u201d\u4e2d\u66fe\u5938\u5b81\u6ce2\u65e5\u96c6\u4e91\u5e06 \u5343\u4f59\uff0c\u4e3a\u4e16\u754c\u7b2c\u4e00\u5927\u6e56\uff0c\u8fd9\u8bdd\u867d\u7136\u6709\u70b9\u8fc7\u4efd\uff0c\u4f46\u5b81\u6ce2\u5374\u662f\u5f53\u65f6\u6c34\u8fd0\u7684\u5927\u7ad9\u3002 \u3000\u3000\u6b63\u5f53\u5927\u4f19\u513f\u51fa\u6e2f\u7684\u65f6\u5019\uff0c\u4e00\u53ea\u843d\u4e86\u5e06\u7684\u5c0f\u8239\u6084\u6084\u5212\u4e86\u8fdb\u6765\uff0c\u90a3\u5c0f\u8239\u597d\u751f\u53e4\u602a\uff0c\u9760\u4e86\u5cb8\u4e4b \u540e\uff0c\u4e00\u4e2a\u9752\u5e74\u5112\u751f\u8d70\u4e86\u51fa\u6765\uff0c\u8239\u4e0a\u5c31\u518d\u6ca1\u6709\u4eba\u4e86\uff0c\u7a7a\u8361\u6cca\u5728\u90a3\u513f\uff0c\u90a3\u9752\u5e74\u5112\u751f\u50cf\u662f\u6beb\u4e0d\u7406\u4f1a \u90a3\u5c0f\u8239\uff0c\u72ec\u4e2a\u513f\u76f4\u8d70\u4e0a\u5cb8\u3002 \u3000\u3000\u6e2f\u5f2f\u540e\u9762\u5c31\u662f\u5c71\u5761\uff0c\u90a3\u9752\u5e74\u4e00\u88ad\u5e03\u886b\uff0c\u8fde\u884c\u674e\u5305\u90fd\u6ca1\u6709\u4e00\u4e2a\uff0c\u5374\u5f84\u76f4\u5f80\u5c71\u5761\u4e0a\u8d70\u53bb\u3002 \u3000\u3000\u7ffb\u8fc7\u5c71\u5761\uff0c\u8fdb\u5f97\u8c37\u4e2d\uff0c\u53ea\u89c1\u4e00\u7247\u6797\u6728\u8471\u90c1\uff0c\u4e0e\u6e2f\u53e3\u7801\u5934\u4e0a\u90a3\u79cd\u70ed\u95f9\u4e4b\u666f\u5927\u4e0d\u76f8\u540c\u3002 \u3000\u3000\u90a3\u9752\u5e74\u7565\u5fae\u9a7b\u4e86\u9a7b\u811a\uff0c\u4ef0\u5934\u770b\u4e86\u770b\u5929\u8272\uff0c\u671d\u9633\u4e0b\u7167\u7740\u4ed6\u633a\u79c0\u7684\u8eab\u6750\u5b9b\u5982\u7389\u6811\u4e34\u98ce\uff0c\u767d\u6670 \u7684\u8138\u4e0a\u5fae\u5e26\u7740\u4e00\u4e1d\u5fe7\u8272\u3002 \u3000\u3000\u5929\u4e0a\u767d\u4e91\u53d8\u5e7b\u65e0\u9645\uff0c\u4ed6\u8f7b\u53f9\u4e86\u4e00\u58f0\uff0c\u81ea\u8bed\u9053\uff1a\u201c\u8f9b\u6377\u554a\uff0c\u5929\u5730\u8fd9\u4e48\u5927\u4f60\u5230\u54ea\u91cc\u53bb\u5bfb\u83c1\u513f \u5462\uff1f\u201d \u3000\u3000\u4f46\u662f\u7acb\u523b\uff0c\u4ed6\u8138\u4e0a\u53d8\u4e3a\u575a\u6bc5\u4e4b\u8272\uff0c\u4ed6\u6697\u9053\uff1a\u201c\u83c1\u513f\u4e3a\u4e86\u6211\u53ef\u4ee5\u4e09\u756a\u56db\u6b21\u5730\u820d\u547d\u76f8\u52a9\uff0c\u96be \u9053\u6211\u8f9b\u6377\u8fd9\u70b9\u4e8b\u5c31\u754f\u96be\u4e86\u4e48\uff1f\u5c31\u662f\u8d70\u904d\u4e09\u6c5f\u56db\u6d77\u6211\u597d\u6b79\u4e5f\u5f97\u5bfb\u7740\u5979\u3002\u201d \u3000\u3000\u4ed6\u7ee7\u7eed\u524d\u8fdb\uff0c\u80cc\u810a\u633a\u5f97\u7b14\u76f4\u5730\u3002 \u3000\u3000\u6ca1\u6709\u591a\u4e45\uff0c\u4ed6\u53c8\u9a7b\u8db3\u4e86\uff0c\u539f\u6765\u662f\u8fdc\u5904\u4f20\u6765\u4e00\u9635\u53e4\u602a\u7684\u5578\u58f0\uff0c\u90a3\u5578\u58f0\u8f7b\u5fae\u5f97\u5f88\uff0c\u6df7\u5728\u5c71\u98ce \u4e2d\u7b80\u76f4\u5206\u8fa8\u5b83\u4e0d\u51fa\u6765\uff0c\u4f46\u5b83\u624d\u53d1\u51fa\uff0c\u4ed6\u5c31\u9a7b\u8db3\u503e\u542c\u4e86\uff0c\u8fd9\u79cd\u529f\u529b\u548c\u673a\u8b66\uff0c\u5f53\u771f\u8bf4\u5f97\u4e0a\u767b\u5cf0\u9020 \u6781\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u5fae\u8fa8\u4e86\u8fa8\u53d1\u58f0\u7684\u65b9\u5411\uff0c\u8eab\u5b50\u4e00\u8f6c\uff0c\u501f\u7740\u8fd9\u4e00\u626d\u4e4b\u95f4\uff0c\u8eab\u5b50\u7adf\u7136\u817e\u7a7a\u98de\u51fa\u4e09\u3001\u56db\u4e08\uff0c\u59ff \u52bf\u7f8e\u5999\u5df2\u6781\u3002 \u3000\u3000\u4e0d\u6d88\u51e0\u4e2a\u8d77\u843d\uff0c\u4ed6\u5df2\u63a5\u8fd1\u4e86\u53d1\u58f0\u4e4b\u5904\uff0c\u4ed6\u731b\u7136\u505c\u4f4f\uff0c\u90a3\u4e48\u5927\u7684\u51b2\u52b2\u5728\u4ed6\u53cc\u8db3\u66fc\u5999\u5730\u4e00\u8361 \u4e4b\u95f4\u5168\u90e8\u6d88\u4e8e\u65e0\u5f62\uff0c\u8fde\u5730\u4e0a\u5c18\u571f\u90fd\u4e0d\u66fe\u626c\u8d77\u3002 \u3000\u3000\u4ed6\u7331\u8eab\u8dc3\u4e0a\u4e00\u68f5\u5927\u6811\uff0c\u4fef\u89c6\u4e0b\u53bb\u2014\u2014 \u3000\u3000\u8fd9\u4e00\u770b\uff0c\u51e0\u4e4e\u4ee4\u4ed6\u6b22\u547c\u51fa\u58f0\u2014\u2014 \u3000\u3000\u53ea\u89c1\u4e0b\u9762\u4e00\u4e2a\u5c11\u5e74\u6b63\u5728\u7ec3\u4e60\u62f3\u811a\uff0c\u90a3\u5578\u58f0\u7adf\u662f\u4ece\u4ed6\u6325\u52a8\u53cc\u62f3\u4e4b\u95f4\u7814\u53d1\u51fa\u7684\uff0c\u53ea\u89c1\u4ed6\u4e0a\u4e0b \u98de\u821e\uff0c\u8eab\u5b50\u8f7b\u7075\u4e4b\u4e2d\u81ea\u4ee4\u4eba\u6709\u4e00\u79cd\u7a33\u91cd\u7684\u611f\u89c9\uff0c\u8fd9\u65f6\u4ed6\u8f6c\u8fc7\u8eab\u6765\uff0c\u663e\u51fa\u4e00\u5f20\u4fca\u7f8e\u7edd\u4f26\u7684\u8138 \u5b54\uff0c\u6b63\u662f\u5434\u51cc\u98ce\u54e9\uff01 \u3000\u3000\u8f9b\u6377\u5728\u6811\u4e0a\u5f3a\u5fcd\u4f4f\u6b22\u547c\uff0c\u5fc3\u4e2d\u6697\u559c\u9053\uff1a\u201c\u5927\u54e5\u81ea\u670d\u8840\u679c\u540e\uff0c\u529f\u529b\u731b\u8fdb\uff0c\u4e00\u6708\u6765\u4e0d\u89c1\uff0c\u4ed6 \u529f\u529b\u6709\u4e0d\u5c11\u8fdb\u76ca\uff0c\u8fd9\u7b49\u7edd\u4e16\u8f7b\u529f\u9664\u975e\u662f\u6211\uff0c\u4e2d\u539f\u53ea\u6015\u8fd8\u6211\u4e0d\u51fa\u7b2c\u4e8c\u4e2a\u5462\u3002\u201d \u3000\u3000\u8fd9\u65f6\u5434\u51cc\u98ce\u624b\u4e0a\u62db\u5f0f\u6108\u7ec3\u6108\u5f3a\uff0c\u5ffd\u7136\u4e00\u8f6c\u8eab\u547c\u5730\u5288\u51fa\u4e00\u638c\uff0c\u6fc0\u51fa\u6f2b\u5929\u7802\u5c18\uff0c\u4ed6\u53cc\u8db3\u4e00 \u9519\uff0c\u4e00\u6643\u8eab\u53c8\u662f\u4e00\u638c\u5288\u51fa\uff0c\u53d1\u51fa\u545c\u545c\u602a\u54cd\uff0c\u663e\u7136\u529b\u9053\u6bd4\u7b2c\u4e00\u638c\u8fd8\u8981\u5f3a\uff0c\u4ed6\u638c\u52bf\u672a\u7adf\uff0c\u8eab\u5b50\u4e00 \u8f6c\uff0c\u53c8\u662f\u4e00\u62f3\u5f53\u80f8\u63a8\u51fa\uff0c\u545c\u545c\u602a\u54cd\u8d8a\u8d8b\u5c16\u9510\uff0c\u62cd\u7684\u4e00\u58f0\uff0c\u8fdc\u5728\u4e08\u5916\u7684\u4e00\u68f5\u7897\u53e3\u677e\u6811\u7adf\u7136\u5e94\u58f0 \u800c\u6298\u3002 \u3000\u3000\u4ed6\u505c\u4e0b\u4e86\u624b\uff0c\u504f\u5934\u60f3\u4e86\u60f3\uff0c\u6084\u58f0\u9053\uff1a\u201c\u4e00\u6708\u6765\uff0c\u6211\u8fd9\u2018\u5f00\u5c71\u4e09\u5f0f\u2019\u4f3c\u4e4e\u8fdb\u6b65\u4e0d\u5c11\uff0c\u53ea\u662f \u7b2c\u4e8c\u62db\u2018\u611a\u516c\u79fb\u5c71\u2019\u8f6c\u5230\u2018\u516d\u4e01\u5f00\u5c71\u2019\u65f6\u4f3c\u4e4e\u771f\u529b\u4e0d\u5982\u5176\u4ed6\u51e0\u62db\u90a3\u4e48\u987a\u548c\uff0c\u5927\u6982\u662f\u529f\u529b\u4e0d\u6d4e \u7684\u539f\u6545\u5427\u2014\u2014\u55ef\uff0c\u6211\u5f97\u597d\u597d\u7ec3\u7ec3\uff0c\u4e0d\u7136\u5c06\u6765\u548c\u6377\u5f1f\u4e00\u6bd4\uff0c\u53ef\u5dee\u5f97\u8fdc\u4e86\u2014\u2014\u201d \u3000\u3000\u5ffd\u7136\u6811\u4e0a\u4f20\u51fa\u7b11\u58f0\uff0c\u4e00\u4e2a\u6e05\u4eae\u7684\u58f0\u97f3\uff1a\u201c\u5631\uff0c\u6211\u4e5f\u8981\u591a\u591a\u7ec3\u4e60\uff0c\u4e0d\u7136\u5c06\u6765\u548c\u5927\u54e5\u4e00\u6bd4\u5dee \u5f97\u8fdc\u4e86\u2014\u2014\u201d \u3000\u3000\u51cc\u98ce\u4e00\u542c\uff0c\u60ca\u559c\u8fc7\u671b\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6377\u5f1f\uff01\u201d \u3000\u3000\u58f0\u72b9\u672b\u4e86\uff0c\u8f9b\u6377\u5df2\u5982\u4e00\u7247\u67af\u53f6\u98d8\u843d\u5728\u773c\u524d\u3002 \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u53e3\u89d2\u5e26\u7b11\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u8f9b\u6377\u5ffd\u7136\u5927\u559d\u4e00\u58f0\uff1a\u201c\u63a5\u62db\uff01\u201d \u3000\u3000\u5f53\u80f8\u4e00\u638c\u5288\u51fa\uff0c\u529b\u9053\u4e4b\u5f3a\uff0c\u4ee4\u51cc\u98ce\u8863\u8882\u98d8\u53d1\u3002 \u3000\u3000\u51cc\u98ce\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u672c\u80fd\u4ee4\u4ed6\u5fae\u9000\u534a\u6b65\uff0c\u5de6\u638c\u4e00\u5708\u4e00\u6293\uff0c\u6253\u7b97\u6d88\u53bb\u6765\u52bf\u3002 \u3000\u3000\u54ea\u77e5\u4e00\u6293\u4e4b\u4e0b\uff0c\u6293\u4e86\u4e2a\u7a7a\uff0c\u8f9b\u6377\u53f3\u638c\u6781\u5176\u98d8\u5ffd\u5730\u62b9\u81f3\uff0c\u4e94\u6307\u5206\u5f20\u5904\uff0c\u6b63\u662f\u81ea\u5df1\u5f53\u80f8\u4e94 \u7a74\u3002 \u3000\u3000\u51cc\u98ce\u4e0d\u53ca\u7ec6\u601d\uff0c\u5411\u5de6\u4e00\u4fa7\uff0c\u53f3\u638c\u5374\u4ece\u9762\u5f27\u7ebf\u653b\u51fa\u4e00\u5f0f\uff0c\u65f6\u95f4\u7a7a\u95f4\u90fd\u914d\u5408\u5f97\u7f8e\u5999\u65e0\u6bd4\uff0c\u6b63 \u662f\u201c\u7834\u7389\u62f3\u201d\u4e2d\u7684\u7edd\u7740\u2014\u2014\u201c\u77f3\u7834\u5929\u60ca\u201d\u3002 \u3000\u3000\u8f9b\u6377\u53eb\u4e86\u58f0\uff1a\u201c\u65bd\u5f97\u597d\uff01\u201d\u5de6\u624b\u4e00\u7ffb\uff0c\u4e94\u6307\u9f50\u51fa\uff0c\u6b63\u662f\u5e73\u51e1\u5927\u5e08\u65b0\u8fd1\u4f20\u6388\u7684\u201c\u7a7a\u7a7a\u638c \u6cd5\u201d\u4e2d\u7684\u201c\u4e07\u6cc9\u98de\u7a7a\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u5f0f\u6b63\u903c\u5f97\u51cc\u98ce\u65bd\u51fa\u201c\u5f00\u5c71\u4e09\u5f0f\u201d\u4e2d\u7684\u7b2c\u4e00\u5f0f\uff1a\u201c\u5f00\u5c71\u5bfc\u6d41\u3002\u201d \u3000\u3000\u51cc\u98ce\u53eb\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u600e\u4e48\u2014\u2014\u201d \u3000\u3000\u4f46\u624b\u4e0a\u5374\u4e0d\u5bb9\u4ed6\u7a0d\u7f13\uff0c\u4ed6\u8eab\u5b50\u4e00\u8f6c\uff0c\u4e00\u8bb0\u5288\u51fa\uff0c\u6b63\u662f\u5f00\u5c71\u4e09\u5f0f\u4e2d\u7684\u201c\u5f00\u5c71\u5bfc\u6d41\u201d\u3002 \u3000\u3000\u5f53\u4ed6\u52b2\u529b\u624d\u53d1\uff0c\u4ed6\u7acb\u523b\u60f3\u5230\uff1a\u201c\u5bf9\u4e86\uff0c\u5fc5\u662f\u6377\u5f1f\u65b9\u624d\u5728\u6811\u4e0a\u89c1\u6211\u7ec3\u62f3\uff0c\u53c8\u542c\u6211\u8bf4\u8bdd\u5c0f\u6027 \u5b50\u53d1\u4e86\uff0c\u8981\u627e\u6211\u4e89\u4e2a\u80dc\u8d1f\uff0c\u6211\u672c\u975e\u4ed6\u5bf9\u624b\uff0c\u4f55\u5fc5\u548c\u4ed6\u4e89\u6597\uff1f\u8ba9\u4ed6\u5360\u70b9\u4e0a\u98ce\u4fbf\u4e86\u3002\u201d \u3000\u3000\u7535\u5149\u706b\u767e\u95f4\uff0c\u4ed6\u786c\u786c\u6536\u56de\u4e24\u6210\u529b\u9053\u3002 \u3000\u3000\u54ea\u77e5\u8f9b\u6377\u4e00\u6643\u8eab\u7ed5\u5230\u4ed6\u80cc\u540e\uff0c\u53cc\u638c\u9f50\u53d1\uff0c\u6240\u53d6\u90e8\u4f4d\u6781\u662f\u53e4\u602a\uff0c\u8feb\u5f97\u51cc\u98ce\u53ea\u597d\u65bd\u51fa\u7b2c\u4e8c\u5f0f \u201c\u611a\u516c\u79fb\u5c71\u201d\u3002 \u3000\u3000\u8f9b\u6377\u9661\u65bd\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\uff0c\u4e00\u6643\u800c\u9000\uff0c\u5355\u638c\u6a2a\u98de\uff0c\u6b63\u662f\u201c\u7a7a\u7a7a\u62f3\u6cd5\u201d\u4e2d\u7684\u5341\u4e00\u5f0f\u201c\u7a7a\u5b9e\u4e24 \u65e0\u201d\u3002 \u3000\u3000\u8f9b\u6377\u6240\u53d6\u6240\u7acb\u7684\u90e8\u4f4d\uff0c\u6b63\u662f\u201c\u5f00\u5c71\u4e09\u5f0f\u201d\u6700\u540e\u4e00\u5f0f\u201c\u516d\u4e01\u5f00\u5c71\u201d\u6700\u6709\u5229\u7684\u5730\u4f4d\uff0c\u51cc\u98ce\u6beb \u4e0d\u601d\u7d22\u5730\u88ab\u5f15\u51fa\u7b2c\u4e09\u5f0f\uff01 \u3000\u3000\u201c\u611a\u516c\u79fb\u5c71\u201d\u8f6c\u4e3a\u201c\u516d\u4e01\u5f00\u5c71\u201d\u65f6\uff0c\u51cc\u98ce\u80f8\u524d\u540c\u6837\u89c9\u5f97\u771f\u529b\u4e0d\u7545\uff0c\u5374\u89c1\u8f9b\u6377\u5e76\u4e0d\u786c\u63a5\uff0c \u53ea\u662f\u95ea\u8eab\u800c\u8fdb\u3002 \u3000\u3000\u6b63\u53e4\u602a\u95f4\uff0c\u8f9b\u6377\u53c8\u662f\u201c\u4e07\u6cc9\u98de\u7a7a\u201d\u6253\u6765\uff0c\u903c\u5f97\u4ed6\u518d\u65bd\u7b2c\u4e00\u5f0f\u201c\u5f00\u5c71\u5bfc\u6d41\u3002\u201d \u3000\u3000\u51cc\u98ce\u539f\u672c\u806a\u660e\u7edd\u9876\uff0c\u89c1\u8f9b\u6377\u4e0d\u505c\u5f15\u4ed6\u65bd\u8fd9\u4e09\u62db\uff0c\u5fc3\u4e2d\u731c\u60f3\u6377\u5f1f\u5982\u6b64\u5fc5\u6709\u6df1\u610f\uff0c\u5f53\u4e0b\u51dd\u795e \u8d2f\u6ce8\u3002 \u3000\u3000\u679c\u7136\u8f9b\u6377\u53c8\u662f\u540c\u6837\u62db\u5f0f\u5f15\u4ed6\u7b2c\u4e8c\u5f0f\u201c\u611a\u516c\u79fb\u5c71\u201d\u3002 \u3000\u3000\u63a5\u7740\uff0c\u8f9b\u6377\u8fd8\u662f\u4ee5\u201c\u7a7a\u5b9e\u4e24\u65e0\u201d\u5f15\u4ed6\u65bd\u51fa\u201c\u516d\u4e01\u5f00\u5c71\u201d\uff0c\u4f46\u8fd9\u6b21\u8f9b\u6377\u8eab\u8eaf\u7a81\u7136\u5728\u7a7a\u4e2d\u4e00 \u7a92\u2014\u2014 \u3000\u3000\u672c\u6765\u4ed6\u201c\u516d\u4e01\u5f00\u5c71\u201d\u662f\u76f4\u80f8\u800c\u51fa\uff0c\u4f46\u8fd9\u65f6\u4ed6\u4e0d\u5f97\u4e0d\u731b\u7136\u5012\u8f6c\u771f\u6c14\uff0c\u659c\u5288\u800c\u51fa\uff0c\u90a3\u77e5\u7830\u7684 \u4e00\u58f0\uff0c\u529b\u9053\u53cd\u800c\u52a0\u5f3a\uff0c\u4e08\u5916\u4e00\u68f5\u5927\u6811\u53f6\u90fd\u4e0d\u66fe\u6643\u52a8\u4e00\u4e0b\u5c31\u5e94\u58f0\u800c\u6298\uff01 \u3000\u3000\u800c\u4e14\u51cc\u98ce\u7a81\u7136\u53d1\u73b0\u539f\u5148\u65bd\u5230\u8fd9\u91cc\u80f8\u4e2d\u90a3\u79cd\u4e0d\u7545\u7684\u73b0\u8c61\u5df2\u5168\u7136\u6d88\u5931\u3002 \u3000\u3000\u4ed6\u5446\u5f97\u4e00\u5446\u53c8\u4ece\u201c\u611a\u516c\u79fb\u5c71\u201d\u6362\u5230\u201c\u516d\u4e01\u5f00\u5c71\u201d\uff0c\u4f9d\u6837\u5c06\u771f\u6c14\u5012\u8f6c\uff0c\u659c\u5288\u800c\u51fa\uff0c\u679c\u7136\u80f8 \u4e2d\u7545\u7136\uff0c\u800c\u4e14\u529b\u9053\u72b9\u5927\u3002 \u3000\u3000\u4ed6\u4e00\u559c\u5927\u53eb\u51fa\u58f0\uff0c\u77e5\u9053\u8f9b\u6377\u770b\u51fa\u81ea\u5df1\u6bdb\u75c5\uff0c\u6545\u610f\u5f15\u81ea\u5df1\u9022\u654c\u6539\u6b63\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5927\u662f\u611f\u6fc0\uff0c \u53eb\u9053\uff1a\u201c\u554a\uff0c\u6377\u5f1f\uff0c\u771f\u8be5\u8c22\u4f60\uff0c\u600e\u4e48\u770b\u51fa\u8fd9\u6bdb\u75c5\u6765\u7684\uff1f\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u662f\u65b0\u8fd1\u5b66\u4e86\u5e73\u51e1\u4e0a\u4eba\u4e00\u5957\u2018\u7a7a\u7a7a\u62f3\u6cd5\u2019\u624d\u609f\u51fa\u8fd9\u9053\u7406\u6765\u7684\uff0c\u6211\u77a7\u4f60 \u2018\u5f00\u5c71\u4e09\u5f0f\u2019\u5a01\u529b\u91cf\u731b\uff0c\u4f46\u4f3c\u4e4e\u8fd0\u6c14\u7565\u6709\u4e0d\u5bf9\u52b2\u7684\u5730\u65b9\uff0c\u65b9\u624d\u5728\u6811\u4e0a\u548c\u7a7a\u7a7a\u62f3\u6cd5\u7684\u62f3\u7406\u4e00\u5bf9 \u5370\uff0c\u5c31\u77e5\u9053\u5566\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u6377\u5f1f\u4f60\u771f\u597d\u798f\u6c14\uff0c\u8fde\u5f97\u4e16\u5916\u4e09\u4ed9\u7684\u771f\u4f20\uff0c\u8fd9\u4e00\u8d9f\u5fc5\u7136\u6536\u83b7\u6781\u591a\u5427\u2014\u2014\u554a\uff0c\u6211 \u5dee\u70b9\u5fd8\u8bb0\u544a\u8bc9\u4f60\uff0c\u6709\u4e00\u4e2a\u59d3\u5f20\u7684\u5c0f\u59d1\u5a18\u5230\u5904\u5bfb\u4f60\uff0c\u6211\u544a\u8bc9\u5979\u4f60\u591a\u534a\u5728\u5927\u6222\u5c9b\uff0c\u5979\u5c31\u5306\u5306\u8dd1\u53bb \u4e86\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4e00\u542c\u8dc3\u8d77\u4e08\u4f59\uff0c\u5927\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5feb\uff0c\u5feb\u8d70\u2014\u2014\u201d \u3000\u3000\u8bf4\u7f62\u8f6c\u8eab\u5c31\u8dd1\uff0c\u51cc\u98ce\u53eb\u4e86\u4e00\u58f0\uff0c\u4e5f\u62fc\u529b\u8ffd\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u8f9b\u5434\u4e24\u4eba\u98de\u5954\u800c\u524d\uff0c\u4e0d\u6d88\u7247\u523b\u53c8\u56de\u5230\u6e2f\u8fb9\u3002\u8f9b\u6377\u4e00\u770b\u81ea\u5df1\u9a7e\u6765\u7684\u90a3\u5c0f\u8239\u4ecd\u6cca\u5728\u90a3\u91cc\uff0c\u53ea \u662f\u5cb8\u8fb9\u56f4\u4e86\u8bb8\u591a\u4eba\uff0c\u4f3c\u4e4e\u5728\u770b\u90a3\u5947\u602a\u7684\u65e0\u4e3b\u602a\u8239\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u633d\u51cc\u98ce\u624b\uff0c\u9661\u7136\u8dc3\u8d77\uff0c\u55d6\u7684\u4e00\u4e0b\u8d8a\u8fc7\u4f17\u4eba\u5934\u4e0a\uff0c\u843d\u5728\u821f\u4e2d\uff0c\u501f\u90a3\u51b2\u529b\u628a\u5c0f\u821f\u6ed1\u51fa \u6570\u4e08\uff0c\u4e24\u6868\u4e00\u6273\uff0c\u5df2\u5982\u7bad\u4e00\u822c\u51fa\u4e86\u6e2f\u5f2f\u3002 \u3000\u3000\u7a7a\u7559\u5cb8\u4e0a\u7684\u4f17\u4eba\u60ca\u9a87\u5f97\u53e3\u77aa\u76ee\u5446\uff01 \u3000\u3000\u5c0f\u821f\u51fa\u4e86\u6d77\uff0c\u8f9b\u6377\u624d\u628a\u81ea\u5df1\u548c\u83c1\u513f\u7684\u5173\u7cfb\u53ca\u534e\u5937\u4e89\u6597\uff0c\u65e0\u6068\u751f\u7597\u6bd2\u7b49\u8bf4\u4e86\u4e00\u904d\uff0c\u6700\u540e\u8bf4 \u5230\u81ea\u5df1\u5927\u4ec7\u5df2\u62a5\uff0c\u51cc\u98ce\u4e0d\u7531\u559c\u5411\u4ed6\u606d\u8d3a\u3002 \u3000\u3000\u4f46\u662f\u51cc\u98ce\u7acb\u523b\u60f3\u5230\u81ea\u5df1\u5927\u4ec7\u672a\u590d\uff0c\u8fd8\u6709\u963f\u5170\u4e5f\u6ca1\u6709\u5bfb\u7740\uff0c\u5fc3\u4e2d\u4e00\u65f6\u5fe7\u95f7\u8d77\u6765\uff0c\u4e0d\u7531\u53f9\u4e86 \u4e00\u58f0\u3002 \u3000\u3000\u8f9b\u6377\u51b0\u96ea\u806a\u660e\uff0c\u62d4\u4e86\u4e24\u6868\uff0c\u8f7b\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff01\u201d \u3000\u3000\u51cc\u98ce\u5e94\u9053\uff1a\u201c\u55ef\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4f4e\u58f0\u9053\uff1a\u201c\u54b1\u4eec\u518d\u56de\u4e2d\u539f\u7b2c\u4e00\u4ef6\u4e8b\u5c31\u53bb\u5bfb\u8d64\u9633\u3001\u5389\u9e57\u3001\u82e6\u5eb5\u4ed6\u4eec\uff0c\u4e86\u4e00\u4e86\u4f2f\u7236\u548c\u6885 \u53d4\u53d4\u7684\u5927\u4ec7\u3002\u201d \u3000\u3000\u51cc\u98ce\u77e5\u4ed6\u5b89\u6170\u81ea\u5df1\uff0c\u5fc3\u4e2d\u6b63\u662f\u5f77\u5fa8\u65e0\u4f9d\u7684\u65f6\u5019\uff0c\u542c\u5230\u8fd9\u8bdd\u4e00\u65f6\u6fc0\u52a8\uff0c\u4e00\u628a\u6293\u4f4f\u8f9b\u6377\u7684\u80a9 \u8180\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u771f\u597d\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u611f\u60c5\u66f4\u6613\u51b2\u52a8\uff0c\u4ed6\u4e5f\u63e1\u4f4f\u51cc\u98ce\u7684\u624b\uff0c\u575a\u51b3\u5730\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5f85\u54b1\u4eec\u62a5\u4e86\u4ec7\uff0c\u90a3\u65f6\uff0c\u6211 \u4eec\u5144\u5f1f\u4fe9\u4ed7\u5251\u6c5f\u6e56\uff0c\u8f70\u8f70\u70c8\u70c8\u5e72\u4e00\u756a\uff01\u201d \u3000\u3000\u51cc\u98ce\u542c\u4ed6\u8bf4\u5f97\u8c6a\u58ee\uff0c\u5fc3\u4e2d\u6101\u95f7\u5927\u51cf\uff0c\u90a3\u6653\u5f97\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u7a81\u7136\u963f\u5170\u7684\u9762\u5bb9\u53c8\u6e05\u6670\u5730\u6d6e\u5728 \u4ed6\u773c\u524d\uff0c\u4ed6\u5fc3\u4e2d\u731b\u7136\u4e00\u9707\uff0c\u4e00\u79cd\u4e0d\u7965\u7684\u9884\u611f\u7b3c\u7f69\u7740\u4ed6\u2014\u2014 \u3000\u3000\u8f9b\u6377\u731b\u4e00\u8f6c\u8235\uff0c\u5927\u6222\u5c9b\u5df2\u7136\u5728\u671b\uff01 \u3000\u3000\u65e5\u5f53\u6b63\u4e2d\uff0c\u5149\u8000\u5165\u76ee\u3002 \u3000\u3000\u5927\u57df\u5c9b\u6d77\u5cb8\u5df2\u5230\uff0c\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u53cc\u53cc\u4ece\u5c0f\u8239\u4e0a\u8d70\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5ffd\u7136\u8f9b\u6377\u53f9\u4e86\u4e00\u58f0\uff0c\u51cc\u98ce\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u4ece\u6c99\u6ee9\u90a3\u9762\u4f4e\u7740\u5934\u7f13\u7f13\u8d70\u6765\uff0c\u4ed4\u7ec6\u4e00 \u770b\uff0c\u4f4e\u58f0\u60ca\u53eb\u9053\uff1a\u201c\u5b59\u501a\u91cd\uff01\u201d \u3000\u3000\u8f9b\u6377\u5b9a\u773c\u4e00\u770b\uff0c\u6b63\u662f\u90a3\u6b66\u6797\u4e4b\u79c0\u5b59\u501a\u91cd\u3002 \u3000\u3000\u8f9b\u5434\u4e8c\u4eba\u4e0a\u524d\u51e0\u6b65\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5b59\u5144\uff0c\u522b\u6765\u65e0\u7f94\uff1f\u201d \u3000\u3000\u90a3\u5b59\u501a\u91cd\u62ac\u5934\u770b\u4ed6\u4eec\uff0c\u7b11\u4e86\u7b11\uff0c\u53c8\u4f4e\u5934\u524d\u884c\uff0c\u90a3\u4e00\u7b11\u4f3c\u4e4e\u5341\u5206\u52c9\u5f3a\u3002 \u3000\u3000\u8f9b\u6377\u5947\u602a\u5730\u5bf9\u51cc\u98ce\u671b\u4e86\u4e00\u773c\uff0c\u518d\u770b\u90a3\u5b59\u501a\u91cd\u53cc\u7709\u5fae\u58c1\uff0c\u6c89\u7740\u8138\u5b54\uff0c\u4f3c\u4e4e\u5341\u5206\u4e0d\u9ad8\u5174\u7684\u6837 \u5b50\u3002 \u3000\u3000\u8f9b\u6377\u5f85\u8d70\u8fd1\u95ee\u9053\uff1a\u201c\u5b59\u5144\uff0c\u5e73\u51e1\u4e0a\u4eba\u5728\u5c9b\u4e0a\u4e48\uff1f\u201d \u3000\u3000\u5b59\u501a\u91cd\u70b9\u4e86\u70b9\u5934\uff0c\u5ffd\u7136\u5bf9\u4e8c\u4eba\u82e6\u7b11\u4e00\u4e0b\uff0c\u5306\u5306\u8d70\u5230\u6d77\u8fb9\uff0c\u9a7e\u8d77\u4e00\u6761\u5c0f\u8239\uff0c\u626c\u5e06\u800c\u53bb\u3002 \u3000\u3000\u8d70\u5f97\u51e0\u4e08\uff0c\u5ffd\u7136\u4e00\u6761\u4eba\u5f71\u4e00\u6643\uff0c\u8f7b\u98d8\u98d8\u5730\u843d\u5728\u4e24\u4eba\u9762\u524d\uff0c\u90a3\u4efd\u8f7b\u7075\u76f4\u4ee4\u4eba\u6709\u5fd8\u5374\u91cd\u91cf\u7684 \u611f\u89c9\u3002 \u3000\u3000\u4e24\u4eba\u5b9a\u773c\u4e00\u770b\uff0c\u6b63\u662f\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u3002 \u3000\u3000\u8f9b\u6377\u8fde\u5fd9\u65bd\u793c\u9053\uff1a\u201c\u4e0a\u4eba\uff0c\u665a\u8f88\u6765\u770b\u4f60\u5566\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5475\u5475\u5927\u7b11\u9053\uff1a\u201c\u5a03\u513f\u522b\u9a97\u6211\u8001\u4eba\u5bb6\u554a\uff0c\u6211\u77a7\u4f60\u8138\u8272\u4e0d\u5bf9\uff0c\u5b9a\u662f\u6709\u4e8b\u8981\u627e\u6211\uff0c \u3000\u3000\u5374\u8bf4\u4ec0\u4e48\u6765\u770b\u6211\u2014\u2014\u54a6\uff0c\u8fd9\u662f\u8c01\u554a\uff1f\u201d \u3000\u3000\u4ed6\u6253\u91cf\u4e86\u51cc\u98ce\u4e24\u773c\uff0c\u88c5\u7740\u4ece\u6765\u6ca1\u6709\u89c1\u8fc7\u7684\u6a21\u6837\u9053\uff1a\u201c\u8fd9\u662f\u8c01\u5bb6\u7684\u5a03\u513f\uff0c\u957f\u5f97\u597d\u4fca\u554a\uff0c \u55ef\uff0c\u6211\u8001\u4eba\u5bb6\u5e74\u8f7b\u7684\u65f6\u5019\u6050\u6015\u6ca1\u6709\u8fd9\u6837\u4fca\u54e9\u3002\u201d \u3000\u3000\u51cc\u98ce\u65e9\u4ece\u8f9b\u6377\u53e3\u4e2d\u77e5\u9053\u8fd9\u4f4d\u76d6\u4e16\u5947\u4eba\u7684\u813e\u6c14\uff0c\u8fde\u5fd9\u65bd\u793c\u9053\uff1a\u201c\u665a\u8f88\u5434\u51cc\u98ce\uff0c\u53c2\u89c1\u524d \u8f88\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5567\u5567\u8fde\u8d5e\u51cc\u98ce\u957f\u5f97\u4fca\uff0c\u7136\u540e\u624d\u9053\uff1a\u201c\u5a03\u513f\u6765\u627e\u6211\u8001\u4eba\u5bb6\u51c6\u6ca1\u597d\u4e8b\u3002\u4ec0\u4e48\u4e8b \u554a\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u65e0\u6781\u5c9b\u4e3b\u7684\u5973\u513f\u5f20\u83c1\uff0c\u4e0d\u77e5\u6709\u6ca1\u6709\u6765\u8fc7\u8fd9\u513f\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6115\u4e86\u6115\u9053\uff1a\u201c\u6ca1\u6709\u554a\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u987f\u7136\u4e00\u7d27\uff0c\u4f46\u4ed6\u4ecd\u52c9\u5f3a\u88c5\u7740\u7b11\u4e86\u7b11\u9053\uff1a\u201c\u554a\u2014\u2014\u554a\u2014\u2014\u201d \u3000\u3000\u5e95\u4e0b\u7684\u8bdd\u5374\u518d\u4e5f\u8bf4\u4e0d\u51fa\u4e86\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u4f60\u53ef\u662f\u66ff\u90a3\u65e0\u6068\u751f\u5bfb\u4ed6\u7684\u5973\u513f\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u7126\u6025\u4e0d\u582a\uff0c\u6839\u672c\u4e0d\u66fe\u542c\u89c1\u4ed6\u8bf4\u4ec0\u4e48\uff0c\u53ea\u5fc3\u4e0d\u5728\u7109\u5730\u70b9\u4e86\u70b9\u5934\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u72b6\u5ffd\u7136\u6012\u9053\uff1a\u201c\u53ef\u662f\u90a3\u65e0\u6068\u751f\u903c\u4f60\u5bfb\u5979\u5973\u513f\uff1f\u54fc\uff0c\u522b\u6015\u4ed6\uff0c\u4ed6\u82e5\u518d\u903c\u4f60\uff0c\u6211 \u8001\u4eba\u5bb6\u53ef\u4e0d\u4f9d\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5fd9\u9053\uff1a\u201c\u4e0d\u662f\uff0c\u4e0d\u662f\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u7ba1\u4ed6\u662f\u4e0d\u662f\uff0c\u54b1\u4eec\u5149\u8fdb\u5c4b\u53bb\u518d\u8bf4\u3002\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u83c1\u513f\u65e2\u4e0d\u66fe\u6765\u8fc7\uff0c\u54b1\u4eec\u5c31\u4e0d\u6253\u6270\u4e86\u2014\u2014\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e00\u77aa\u53cc\u76ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u4f60\u4eec\u5c31\u8981\u8d70\uff1f\u90a3\u53ef\u4e0d\u6210\u2014\u2014\u201d \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c\u5341\u56db\u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c\u5341\u56db\u7ae0 \u3000\u3000\u8f9b\u6377\u548c\u51cc\u98ce\u89c1\u5e73\u51e1\u4e0a\u4eba\u5927\u53d1\u813e\u6c14\uff0c\u77e5\u9053\u4ed6\u751f\u6027\u5982\u6b64\uff0c\u4e0d\u7531\u4e3a\u4e4b\u6697\u7b11\u3002\u8f9b\u6377\u5fcd\u7b11\u9053:\u201c\u4e0d \u8d70\uff01\u4e0d\u8d70\uff01\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8f6c\u6012\u4e3a\u559c\u9053\uff1a\u201c\u4e0d\u5bf9\u4f60\u4eec\u51f6\u4e00\u70b9\uff0c\u4f60\u4eec\u4e0d\u77e5\u9053\u6211\u7684\u5389\u5bb3\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u518d\u4e5f\u5fcd\u4e0d\u4f4f\u7b11\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5ffd\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u521a\u624d\u6765\u65f6\uff0c\u4e00\u5b9a\u770b\u89c1\u90a3\u5b59\u501a\u91cd\u4e86\u3002\u201d \u3000\u3000\u8f9b\u6377\u70b9\u70b9\u5934\uff0c\u4ed6\u77e5\u9053\u5e73\u51e1\u4e0a\u4eba\u7684\u813e\u6c14\uff0c\u8fd9\u6837\u5b50\u8bf4\u6cd5\u5fc5\u5b9a\u6709\u4ec0\u4e48\u4e8b\u8981\u4ea4\u5f85\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u987f\u4e86\u987f\uff0c\u5374\u53c8\u8f6c\u53e3\u9053\uff1a\u201c\u90a3\u65e5\u5728\u5c0f\u6222\u5c9b\u4e0a\uff0c\u4f60\u66fe\u5e94\u8bfa\u65e0\u6068\u751f\u53bb\u5e2e\u4ed6\u5bfb\u627e\u5973\u513f\uff0c \u4f46\u8fd9\u6837\u5927\u7684\u5929\u4e0b\uff0c\u4f60\u5374\u5230\u90a3\u91cc\u53bb\u627e\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u542c\u540e\u4e0d\u7531\u66f4\u662f\u597d\u7b11\u3002\u4ed6\u77e5\u9053\u5e73\u51e1\u4e0a\u4eba\u5fc5\u6709\u4ec0\u4e48\u96be\u4e8e\u51fa\u53e3\u7684\u4e8b\uff0c\u53c8\u4e0d\u597d\u610f\u601d\u76f4\u63a5\u8bf4\u51fa \u6765\u3002\u6240\u4ee5\u624d\u8def\u51fa\u4e00\u70b9\u53e3\u98ce\uff0c\u60f3\u53eb\u8f9b\u6377\u4e3b\u52a8\u95ee\uff0c\u4ed6\u624d\u597d\u542f\u53e3\uff0c\u4f46\u8f9b\u6377\u6545\u610f\u88c5\u4f5c\u4e0d\u7406\uff0c\u53ea\u597d\u80e1\u8bcc \u4e9b\u95f2\u8bdd\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u6697\u7b11\uff0c\u53e3\u4e2d\u542b\u542b\u7cca\u7cca\u201c\u54e6\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5012\u662f\u51cc\u98ce\u5728\u540e\u9762\u5fcd\u4e0d\u4f4f\u60f3\u63d2\u53e3\u76f8\u7b54\uff0c\u5374\u88ab\u8f9b\u6377\u6b62\u4f4f\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8fd9\u53e5\u8bdd\u6839\u672c\u6ca1\u6709\u7ecf\u8fc7\u5927\u8111\uff0c\u81ea\u5df1\u4e5f\u4e0d\u6653\u5f97\u81ea\u5df1\u95ee\u7684\u662f\u4ec0\u4e48\uff0c\u5fc3\u4e2d\u4e0d\u65ad\u7684\u76d8\u7b97 \u7740\uff0c\u542c\u8f9b\u6377\u55ef\u55ef\u54e6\u54e6\uff0c\u4e5f\u8ddf\u7740\u9894\u9996\u201c\u54e6\u201d\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u8f9b\u6377\u77e5\u9053\u81ea\u5df1\u6240\u6599\u4e0d\u865a\uff0c\u4e0d\u7531\u51b2\u7740\u5e73\u51e1\u4e0a\u4eba\u4e00\u7b11\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5fc3\u4e2d\u76d8\u7b97\u4e0d\u5b9a\uff0c\u89c1\u8f9b\u6377\u4e00\u7b11\uff0c\u8ddf\u7740\u4e5f\u662f\u50bb\u7136\u4e00\u7b11\u3002 \u3000\u3000\u534a\u664c\uff0c\u4ed6\u89c1\u4e24\u4e2a\u5c11\u5e74\u90fd\u773c\u7741\u7741\u7684\u6ce8\u89c6\u81ea\u5df1\uff0c\u5fc3\u4e2d\u4e00\u6025\uff0c\u518d\u60f3\u4e0d\u51fa\u65b9\u6cd5\u6765\u5f00\u4e00\u4e2a\u5934\u513f\uff0c\u4fbf \u54ac\u7259\u9053\uff1a\u201c\u5b59\u501a\u91cd\u8fd9\u5c0f\u5b50\uff0c\u4f60\u89c1\u4ed6\u8d70\u4e86\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u3001\u51cc\u98ce\u4e00\u9f50\u9894\u9996\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u63a5\u53e3\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u5bf9\u4e86\uff0c\u4f60\u4eec\u4e00\u5b9a\u5f88\u5947\u602a\u662f\u5417\uff1f\u8981\u77e5\u8fd9\u5176\u4e2d\u6709\u4e00\u6bb5\u5f88\u5927\u7684\u539f\u56e0 \u54e9\uff01\u5185\u5bb9\u5f88\u4e3a\u7cbe\u5f69\uff0c\u4f60\u4eec\u8981\u542c\u5417\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u6025\u4e4b\u4e0b\uff0c\u60f3\u4e0d\u8d77\u522b\u7684\u65b9\u6cd5\u5f15\u4e24\u4eba\u7b54\u8bdd\uff0c\u7adf\u7528\u8fd9\u79cd\u65e0\u8d56\u7684\u65b9\u5f0f\u3002 \u3000\u3000\u8f9b\u6377\u548c\u51cc\u98ce\u54c8\u54c8\u4e00\u7b11\uff0c\u5e73\u51e1\u4e0a\u4eba\u4e0d\u7531\u5fae\u611f\u5c34\u5c2c\uff0c\u5927\u58f0\u4f6f\u6012\u9053\uff1a\u201c\u7b11\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u51cc\u98ce\u5413\u4e86\u4e00\u8df3\uff0c\u5fd9\u6b62\u7b11\u9053\uff1a\u201c\u4e0d\u7b11\uff01\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6ee1\u610f\u5730\u81ea\u6211\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6211\u5c31\u8bb2\u4e86\u2014\u2014\u201d \u3000\u3000\u539f\u6765\u5f53\u65e5\u5e73\u51e1\u4e0a\u4eba\u7b54\u5e94\u4e86\u5c11\u6797\u50e7\u8981\u6559\u5b59\u501a\u91cd\u6b66\u529f\uff0c\u539f\u662f\u4e0d\u5f97\u5df2\u4e4b\u4e8b\uff0c\u4ed6\u5929\u6027\u65e0\u62d8\u65e0\u675f\uff0c \u8981\u4ed6\u4e00\u62db\u4e00\u5f0f\u4f20\u4eba\u6b66\u529f\uff0c\u771f\u662f\u5927\u5927\u96be\u4e8b\uff0c\u90a3\u6b66\u6797\u4e4b\u79c0\u5b59\u501a\u91cd\u53c8\u4e0d\u4f3c\u8f9b\u6377\u5584\u4e8e\u8bf4\u7b11\u8ba8\u597d\uff0c\u6574\u65e5 \u53ea\u662f\u606d\u606d\u656c\u656c\u4e0d\u82df\u8a00\u7b11\uff0c\u5e73\u51e1\u5927\u5e08\u6108\u77a7\u6108\u4e0d\u987a\u773c\uff0c\u5bfb\u601d\u6446\u8131\u3002 \u3000\u3000\u4ed6\u60f3\u4e86\u534a\u5929\uff0c\u7ec8\u4e8e\u60f3\u51fa\u4e00\u6761\u5999\u8ba1\u3002\u4ed6\u6bcf\u5929\u6559\u5b59\u501a\u91cd\u4e00\u5927\u5806\u5c11\u6797\u7edd\u5b66\uff0c\u9650\u4ee4\u4ed6\u5f53\u5929\u7ec3\u597d\uff0c \u5426\u5219\u5c31\u4e0d\u518d\u6559\uff0c\u4ed6\u539f\u4ee5\u4e3a\u53ef\u4ee5\u627e\u5230\u501f\u53e3\uff0c\u8d76\u5b59\u501a\u91cd\u79bb\u5c9b\uff0c\u8c01\u77e5\u5b59\u501a\u91cd\u5916\u8868\u5e84\u91cd\u5b88\u793c\uff0c\u4eba\u5374\u662f \u806a\u660e\u5f97\u5f88\uff0c\u6df1\u77e5\u81ea\u5df1\u62c5\u8d1f\u6574\u4e2a\u5c11\u6797\u5bfa\u590d\u5174\u91cd\u4efb\uff0c\u662f\u4ee5\u54ac\u7259\u62fc\u547d\u628a\u5e73\u51e1\u4e0a\u4eba\u6240\u6388\u751f\u751f\u8bb0\u4e0b\u7ec3 \u4f1a\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u4e00\u65f6\u96be\u4ed6\u4e0d\u5012\uff0c\u53ea\u6709\u6bcf\u5929\u52a0\u91cd\u529f\u8bfe\uff0c\u8fd9\u5929\uff0c\u4ed6\u4e00\u53e3\u6c14\u4f20\u4e86\u5b59\u501a\u91cd\u4e00\u5957\u5c11\u6797\u7edd \u827a\u201c\u767e\u6b65\u795e\u62f3\u201d\uff0c\u73b0\u52a0\u4e0a\u201c\u5927\u884d\u5341\u5f0f\u201d\uff0c\u5b59\u501a\u91cd\u81ea\u662f\u65e0\u6cd5\u7ec3\u4f1a\uff0c\u5e73\u51e1\u4e0a\u4eba\u4fbf\u677f\u7740\u8138\u9053\uff1a\u201c\u6211 \u8001\u4eba\u5bb6\u6bcf\u5929\u8f9b\u8f9b\u82e6\u82e6\u6559\u4f60\uff0c\u54fc\uff0c\u4f60\u7adf\u6562\u4e0d\u7528\u5fc3\u5b66\uff0c\u660e\u513f\u5c31\u4e0a\u8def\u5427\uff01\u201d\u5176\u5b9e\u4ed6\u5fc3\u91cc\u9887\u611f\u60ed\u6127\uff0c \u56e0\u4e3a\u5b59\u501a\u91cd\u5b9e\u5728\u5f88\u662f\u7528\u529f\u3002 \u3000\u3000\u5b59\u501a\u91cd\u867d\u77e5\u662f\u7956\u5e08\u7237\u6709\u610f\u4e3a\u96be\uff0c\u4f46\u4ed6\u4e0d\u6562\u9876\u649e\uff0c\u5413\u5f97\u53ea\u662f\u53e9\u5934\u6c42\u9976\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u66f4\u662f\u4e0d\u559c\uff0c\u6447\u624b\u9053\uff1a\u201c\u8d77\u6765\uff0c\u8d77\u6765\uff0c\u522b\u518d\u505a\u78d5\u5934\u866b\u4e86\uff0c\u6211\u8001\u4eba\u5bb6\u8bf4\u4e00\u5c31\u4e00\u3002\u201d \u3000\u3000\u5b59\u501a\u91cd\u65e0\u5948\uff0c\u4ed6\u5fc3\u5185\u6697\u5fd6\u8fd9\u4e00\u4e2a\u6708\u81ea\u5df1\u786e\u662f\u5b66\u5230\u8bb8\u591a\u7edd\u4e16\u5947\u5b66\uff0c\u4f46\u6709\u4e9b\u4e1c\u897f\u53ea\u662f\u786c\u751f\u751f \u80cc\u4e0b\u6765\uff0c\u5e76\u4e0d\u77e5\u5176\u4e2d\u5965\u5999\u4e4b\u5904\uff0c\u81ea\u77e5\u518d\u7ec3\u4e0b\u53bb\uff0c\u4e00\u5b9a\u4e8b\u500d\u529f\u534a\uff0c\u5012\u4e0d\u5982\u5148\u505c\u4e00\u6bb5\u65f6\u95f4\uff0c\u5f85\u81ea \u5df1\u53c2\u609f\u7ec3\u719f\u540e\uff0c\u518d\u6765\u6c42\u6559\uff0c\u4fbf\u9053\uff1a\u201c\u7075\u7a7a\u9ad8\u7956\u5e08\u7237\uff0c\u6211\u660e\u513f\u5c31\u8d70\uff0c\u7b49\u8fc7\u4e9b\u65f6\u5019\u518d\u6765\u770b\u60a8\u8001\u4eba \u5bb6\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u542c\u4ed6\u80af\u8d70\uff0c\u5fc3\u5185\u5982\u91ca\u91cd\u8d1f\uff0c\u4e5f\u4e0d\u7ba1\u5b59\u501a\u91cd\u65e5\u540e\u662f\u5426\u771f\u7684\u518d\u6765\uff0c\u8fde\u58f0\u9053\uff1a\u201c\u90a3\u5f88 \u597d\uff0c\u90a3\u5f88\u597d\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u773c\u4e00\u770b\u5b59\u501a\u91cd\u6ee1\u8138\u5931\u671b\u9eef\u7136\uff0c\u4e0d\u89c9\u5fae\u611f\u6b49\u610f\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u53ef\u4e0d\u8981\u6c14\u9981\uff0c\u6211 \u8001\u4eba\u5bb6\u4e00\u8eab\u672c\u9886\u5dee\u4e0d\u591a\u90fd\u4f20\u7ed9\u4f60\u5566\uff0c\u597d\u597d\u53bb\u7ec3\uff0c\u54fc\uff0c\u6c5f\u6e56\u4e0a\u53ea\u6015\u96be\u78b0\u5230\u5bf9\u624b\u4e86\u3002\u201d \u3000\u3000\u6b21\u65e5\uff0c\u5e73\u51e1\u4e0a\u4eba\u6b63\u628a\u5b59\u501a\u91cd\u6253\u53d1\u8d70\uff0c\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u5c31\u8d76\u5230\u5927\u6222\u5c9b\u6765\uff0c\u5e73\u51e1\u4e0a\u4eba\u81ea\u89c9\u8d76\u8d70 \u5b59\u91cd\u7684\u5999\u8ba1\uff0c\u771f\u662f\u5927\u5927\u6770\u4f5c\uff0c\u662f\u4ee5\u8feb\u4e0d\u6025\u5f85\u5c31\u5411\u8f9b\u5434\u4e24\u4eba\u5439\u5618\u3002 \u3000\u3000\u8f9b\u6377\u8d5e\u9053\uff1a\u201c\u60a8\u8001\u4eba\u5bb6\u8fd9\u62db\u771f\u9ad8\u660e\uff0c\u786c\u8f6f\u5e76\u65bd\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5927\u4e50\uff0c\u5475\u5475\u7b11\u9053\uff1a\u201c\u5a03\u513f\uff0c\u6211\u8001\u4eba\u5bb6\u751f\u5e73\u5403\u6367\u53d7\u6fc0\uff0c\u5c11\u4e0d\u4e86\u53c8\u8981\u4f20\u4f60\u4e24\u624b\u3002\u201d \u3000\u3000\u8f9b\u6377\u5927\u559c\uff0c\u6b63\u5f85\u5f00\u53e3\u79f0\u8c22\uff0c\u5ffd\u89c1\u65e5\u5df2\u504f\u897f\uff0c\u60f3\u5230\u6b64\u884c\u76ee\u7684\uff0c\u84e6\u7136\u4e00\u60ca\uff0c\u4fbf\u60f3\u53c8\u5411\u5e73\u51e1\u4e0a \u4eba\u544a\u8f9e\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4e00\u9635\u4ee4\u4eba\u5fc3\u65f7\u6021\u7684\u6e05\u9999\uff0c\u968f\u7740\u51c9\u98ce\u5438\u4e86\u8fc7\u6765\uff0c\u51cc\u98ce\u53ea\u89c9\u90a3\u9999\u6c14\u751a\u662f\u719f\u6089\uff0c\u4ed6\u731b\u55c5 \u4e86\u51e0\u53e3\uff0c\u4e00\u4e2a\u5ff5\u5934\u95ef\u4e0a\u5fc3\u5934\uff0c\u4e5f\u4e0d\u53ca\u5411\u4e8c\u4eba\u8bf4\uff0c\u66f4\u987a\u7740\u9999\u6c14\u98de\u5954\u8fc7\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u5fc3\u9053\uff1a\u201c\u6211\u5434\u5927\u54e5\u5e73\u5e38\u505a\u4e8b\u4ece\u5bb9\u4e0d\u4e71\uff0c\u4ece\u6ca1\u6709\u89c1\u8fc7\u4ed6\u8fd9\u4e48\u5306\u5306\u5fd9\u5fd9\u8fc7\uff0c\u4e00\u5b9a\u662f\u53d1\u73b0 \u4e86\u4ec0\u4e48\u60ca\u4eba\u5927\u4e8b\u3002\u201d\u4fbf\u8981\u4e3e\u6b65\u8ddf\u8e2a\u4e0a\u53bb\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u795e\u79d8\u4e00\u7b11\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5a03\u513f\uff0c\u54b1\u4eec\u5077\u5077\u8dd1\u8fc7\u53bb\uff0c\u770b\u90a3\u4fca\u5a03\u513f\u6363\u4ec0\u4e48\u9b3c\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e00\u770b\u5e73\u51e1\u4e0a\u4eba\u795e\u8272\uff0c\u66f4\u77e5\u4ed6\u5df2\u660e\u767d\u51cc\u98ce\u884c\u4e3a\uff0c\u5f53\u4e0b\u70b9\u70b9\u5934\uff0c\u5c31\u548c\u5e73\u51e1\u4e0a\u4eba\u65bd\u5c55\u8f7b \u529f\uff0c\u8ffd\u4e0a\u524d\u53bb\u3002 \u3000\u3000\u8dd1\u4e86\u4e00\u9635\uff0c\u9999\u6c14\u6108\u6765\u6108\u6d53\uff0c\u5e73\u51e1\u4e0a\u4eba\u5ffd\u9053\uff1a\u201c\u5c31\u662f\u8fd9\u91cc\u4e86\u3002\u201d\u4ed6\u4e00\u62d6\u8f9b\u6377\uff0c\u5c31\u5728\u4e00\u5757\u5927 \u77f3\u540e\u9690\u8eab\u3002 \u3000\u3000\u8f9b\u6377\u4f38\u51fa\u4e00\u53ea\u773c\u775b\uff0c\u53ea\u89c1\u51cc\u98ce\u7ad9\u5728\u56db\u4e94\u5341\u4e08\u5916\u4e00\u58f0\u7a81\u51fa\u5ca9\u77f3\u4e0a\uff0c\u624b\u821e\u8db3\u8e48\uff0c\u795e\u8272\u6b22\u6109\u5df2 \u6781\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6084\u58f0\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u77a7\u90a3\u77f3\u65c1\u751f\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e00\u77a7\uff0c\u4f46\u89c1\u4e00\u68f5\u6a2a\u751f\u5c0f\u6811\uff0c\u957f\u5728\u767e\u58c1\u4e2d\uff0c\u4e1d\u6beb\u4e0d\u89c1\u7279\u5f02\u4e4b\u5904\u3002\u4fbf\u9053\uff1a\u201c\u60a8\u6307\u7684\u662f\u90a3 \u88f8\u5c0f\u6811\u5417\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\u3002\u201d\u5ffd\u53c8\u9053\uff0c\u201c\u5a03\u513f\uff0c\u4f60\u77a7\u4ed6\u53e3\u4e2d\u5ff5\u5ff5\u6709\u8bcd\uff0c\u54b1\u4eec\u518d\u8d70\u8fd1\u4e9b\u53bb \u542c\u542c\u3002\u201d \u3000\u3000\u8f9b\u6377\u56de\u5934\u89c1\u5e73\u51e1\u4e0a\u4eba\u6ee1\u8138\u8dc3\u8dc3\u6b32\u8bd5\u4e4b\u8272\uff0c\u4e0d\u7531\u597d\u7b11\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u5e73\u51e1\u4e0a\u4eba\u82e6\u4fee\u4e09\u7532\u5b50\uff0c \u8f88\u5206\u4e4b\u5c0a\uff0c\u6b66\u529f\u4e4b\u9ad8\uff0c\u53ea\u6015\u666e\u5929\u4e4b\u4e0b\u518d\u96be\u627e\u51fa\u7b2c\u4e8c\u4eba\uff0c\u53ef\u662f\u4ed6\u813e\u6c14\u5374\u8fd8\u662f\u597d\u80dc\u597d\u5947\uff0c\u5509\uff0c \u2018\u6240\u8c13\u6c5f\u5c71\u6613\u6539\uff0c\u79c9\u6027\u96be\u79fb\u2019\u3002\u5929\u6027\uff0c\u90a3\u662f\u6700\u96be\u6539\u7684\uff0c\u50cf\u6211\u8fd9\u6837\u504f\u6fc0\u51b2\u52a8\uff0c\u4e5f\u4e0d\u77e5\u54ea\u5929\u624d\u80fd \u53d8\u5f97\u50cf\u6211\u5434\u5927\u54e5\u4e00\u822c\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u4ed6\u4e0d\u8bed\uff0c\u4fbf\u4e0d\u7406\u4f1a\u4ed6\uff0c\u8f7b\u6b65\u8d70\u5411\u524d\u53bb\u3002\u8f9b\u6377\u6c89\u541f\u4e86\u4e00\u4e0b\uff0c\u4e5f\u8ddf\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u9690\u4f0f\u5728\u79bb\u51cc\u98ce\u8fd1\u65c1\u4e24\u4e09\u4e08\u767e\u540e\uff0c\u51cc\u98ce\u5168\u795e\u6ce8\u610f\u90a3\u68f5\u6a2a\u751f\u5c0f\u6811\uff0c\u662f\u4ee5\u5e76\u672a\u53d1\u89c9\u3002 \u3000\u3000\u8f9b\u6377\u4ed4\u7ec6\u4e00\u770b\uff0c\u53ea\u89c1\u90a3\u6811\u5149\u79c3\u79c3\u4e0d\u751f\u4e00\u7247\u53f6\u5b50\uff0c\u4f46\u662f\u5c16\u7aef\u5374\u751f\u7740\u4e00\u7c92\u7ea2\u5982\u8840\u7684\u5c0f\u679c\uff0c\u4ed6 \u7565\u4e00\u6c89\u541f\uff0c\u4e0d\u7531\u604d\u7136\u5927\u609f\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u679c\u513f\u53ea\u6015\u591a\u534a\u662f\u5927\u54e5\u4e0a\u6b21\u5760\u4e0b\u6cf0\u5c71\u60ac\u5d16\u5de7\u98df\u7684\u8840 \u679c\u3002\u201d \u3000\u3000\u4ed6\u518d\u4e00\u770b\uff0c\u51cc\u98ce\u5de6\u624b\u6293\u4f4f\u6811\u679d\u4e00\u8361\uff0c\u53f3\u624b\u5df2\u628a\u7ea2\u8272\u679c\u5b50\u91c7\u5230\uff0c\u8eab\u5b50\u8f7b\u76c8\u7f8e\u5999\uff0c\u4e0d\u7531\u559d\u58f0 \u91c7\u9053\uff1a\u201c\u597d\u529f\u592b\uff01\u201d \u3000\u3000\u51cc\u98ce\u542c\u5230\u8f9b\u6377\u58f0\u97f3\uff0c\u6b63\u60f3\u53d1\u8bdd\u62db\u547c\uff0c\u5e73\u51e1\u4e0a\u4eba\u4e5f\u4ece\u8fd1\u65c1\u73b0\u8eab\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u597d\u5440\uff01\u6211\u8001\u4eba\u5bb6\u8f9b\u8f9b\u82e6\u82e6\u683d\u7684\u8840\u679c\uff0c\u7b49\u4e86\u767e\u5e74\u4e4b\u4e45\uff0c\u597d\u4e0d\u5bb9\u6613\u4eca\u5929\u624d\u7ed3 \u679c\uff0c\u4f60\u5374\u91c7\u4e86\u53bb\uff0c\u5feb\u62ff\u6765\uff0c\u5feb\u62ff\u6765\u3002\u201d \u3000\u3000\u51cc\u98ce\u5fc3\u9053\uff1a\u201c\u8fd9\u5e73\u51e1\u4e0a\u4eba\u5e74\u5df2\u4e8c\u767e\u6709\u4f59\uff0c\u4ed6\u8bf4\u6b64\u6811\u662f\u4ed6\u6240\u690d\uff0c\u6b64\u4e8b\u5927\u6709\u53ef\u80fd\uff0c\u4ed6\u65e2\u7b49\u4e86 \u767e\u5e74\uff0c\u6211\u5c82\u80fd\u91c7\u6458\u800c\u53bb\uff0c\u5509\uff0c\u7f62\u4e86\uff01\u7f62\u4e86\u3002\u201d \u3000\u3000\u4ed6\u6bc5\u7136\u628a\u8840\u679c\u4ea4\u7ed9\u5e73\u51e1\u4e0a\u4eba\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u4ed6\u53c8\u5931\u671b\uff0c\u53c8\u7126\u6025\uff0c\u4fca\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u77e5\u4ed6\u5fc3\u4e2d\u6781\u60f3\u83b7\u5f97\u8840\u679c\uff0c\u5374\u80fd\u6beb\u4e0d\u8fdf\u7591 \u7684\u8fd8\u7ed9\u81ea\u5df1\uff0c\u8fd9\u79cd\u54c1\u6027\u771f\u662f\u96be\u5f97\uff0c\u4fbf\u60f3\u628a\u8840\u679c\u8d60\u7ed9\u51cc\u98ce\uff0c\u4f46\u5ffd\u8f6c\u5ff5\u53c8\u60f3\u9053\uff1a\u201c\u6211\u518d\u6025\u6025\u4ed6\uff0c \u77a7\u77a7\u8fd9\u4fca\u5a03\u6025\u51fa\u773c\u6cea\uff0c\u4e5f\u662f\u597d\u7684\u3002\u201d \u3000\u3000\u4ed6\u9ad8\u58f0\u9053\uff1a\u201c\u8fd9\u8840\u679c\u53ef\u662f\u5929\u5730\u95f4\u4e8c\u5927\u7075\u679c\uff0c\u5929\u4e0b\u53ea\u6709\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u6240\u98df\u7684\u5343\u5e74\u6731\u679c\uff0c \u529f\u6548\u9ad8\u8fc7\u8840\u679c\u3002\u5582\uff0c\u6211\u8001\u4eba\u5bb6\u53ef\u8981\u5403\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4ed6\u771f\u7684\u4e3e\u8d77\u624b\u628a\u8840\u679c\u9001\u5230\u53e3\u8fb9\u3002 \u3000\u3000\u4ed6\u539f\u4ee5\u4e3a\u5434\u51cc\u98ce\u4f1a\u5927\u6025\u5931\u8272\uff0c\u53ea\u89c1\u4ed6\u795e\u8272\u5e73\u548c\uff0c\u4f3c\u4e4e\u8ba4\u4e3a\u8fd9\u662f\u5f88\u5e94\u8be5\u7684\u4e8b\uff0c\u4e0d\u7531\u5927\u5927\u611f \u52a8\uff0c\u67d4\u58f0\u5411\u51cc\u98ce\u9053\uff1a\u201c\u5a03\u513f\uff0c\u6211\u8001\u4eba\u5bb6\u662f\u7ed9\u4f60\u5f00\u73a9\u7b11\u7684\uff0c\u6211\u8001\u4eba\u5bb6\u5df2\u6210\u4e0d\u574f\u4e4b\u8eab\uff0c\u5c82\u80fd\u518d\u50cf \u90a3\u6ca1\u51fa\u606f\u7684\u65e0\u6068\u751f\uff0c\u9760\u8349\u672b\u529f\u589e\u52a0\u529f\u529b\uff0c\u5582\uff0c\u5a03\u513f\uff0c\u4f60\u628a\u8840\u679c\u62ff\u53bb\u3002\u201d \u3000\u3000\u51cc\u98ce\u5fc3\u60c5\u5927\u662f\u6fc0\u52a8\uff0c\u53cc\u624b\u98a4\u6296\u63a5\u8fc7\u8840\u679c\uff0c\u89e3\u5f00\u8863\u895f\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u5c0f\u7389\u74f6\uff0c\u8d76\u5feb\u628a\u8840 \u679c\u653e\u8fdb\u53bb\uff0c\u4ed6\u6b63\u5fd9\u7740\uff0c\u201c\u62cd\u201d\u7684\u4e00\u58f0\uff0c\u6389\u4e0b\u4e00\u672c\u5c0f\u518c\u3002 \u3000\u3000\u51cc\u98ce\u6108\u60f3\u6108\u662f\u611f\u6fc0\uff0c\u60c5\u4e0d\u81ea\u7981\u7684\u6251\u4e0a\u524d\u53bb\uff0c\u62b1\u7740\u5e73\u51e1\u4e0a\u4eba\uff0c\u6d41\u4e0b\u6cea\u6765\u3002\u4ed6\u786c\u54bd\u9053\uff1a \u201c\u8001\u2026\u2026\u524d\u2026\u2026\u8f88\uff0c\u4f60\u5f85\u6211\u771f\u597d\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u6478\u7740\u4ed6\u7684\u5934\u9053\uff1a\u201c\u4e56\u5a03\u513f\uff0c\u5feb\u83ab\u54ed\uff0c\u5feb\u83ab\u54ed\uff0c\u4e00\u54ed\u5c31\u6d53\u5305\u4e86\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u6536\u6cea\u9053\uff1a\u201c\u5e76\u4e0d\u662f\u98ce\u513f\u60f3\u5f97\u8840\u679c\uff0c\u5b9e\u5728\u662f\u6211\u6709\u4e00\u4e2a\u670b\u53cb\uff0c\u5979\u53cc\u76ee\u5931\u660e\uff0c\u98ce\u513f\u7b54\u5e94 \u8fc7\u5c31\u662f\u8d70\u904d\u5929\u6daf\u6d77\u89d2\u4e5f\u8981\u5bfb\u5230\u8840\u679c\uff0c\u4f7f\u5979\u91cd\u89c1\u5149\u660e\u3002\u4e0a\u6b21\u6211\u5728\u6cf0\u5c71\u4e08\u4eba\u5cf0\u4e0b\uff0c\u8bef\u98df\u4e00\u68f5\u8840 \u679c\uff0c\u8d77\u521d\u6211\u5e76\u672a\u60f3\u5230\u90a3\u662f\u5343\u8f7d\u96be\u9022\u7684\u7075\u679c\uff0c\u5f85\u5230\u6211\u5403\u4e0b\u540e\uff0c\u8fd9\u624d\u60f3\u8d77\u6b63\u662f\u81ea\u5df1\u65e5\u591c\u76f8\u6c42\u4e1c \u897f\uff0c\u5df2\u662f\u540e\u6094\u83ab\u53ca\u3002\u6211\u77e5\u9053\u4eca\u751f\u518d\u96be\u9022\u5230\uff0c\u60f3\u4e0d\u5230\u60a8\u8fd9\u5c9b\u4e0a\u4e5f\u6709\u8fd9\u6811\uff0c\u800c\u4e14\u6b63\u597d\u8d76\u4e0a\u5b83\u7ed3 \u679c\uff0c\u8fd0\u9053\u771f\u662f\u597d\u6781\u5566\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u89c1\u4ed6\u559c\u6c14\u6d0b\u6d0b\uff0c\u4fca\u8138\u53d1\u51fa\u4e00\u79cd\u4ee4\u4eba\u8ff7\u60d8\u7684\u5149\u8f89\uff0c\u4f46\u773c\u89d2\u6cea\u75d5\u72b9\u5b58\uff0c\u5b9e\u662f\u5929\u771f\u53ef \u7231\uff0c\u4ed6\u5ffd\u7136\u7075\u673a\u4e00\u52a8\u4fbf\u95ee\u9053\uff1a\u201c\u4f60\u90a3\u670b\u53cb\u4e00\u5b9a\u662f\u4e2a\u5973\u5a03\u513f\uff1f\u4f60\u53ef\u8981\u8001\u5b9e\u8bb2\u51fa\u6765\u3002\u201d \u3000\u3000\u51cc\u98ce\u4e07\u6599\u4e0d\u5230\u4ed6\u7adf\u4f1a\u95ee\u8fd9\u4e2a\u95ee\u9898\uff0c\u4ed6\u751f\u5e73\u4e0d\u5584\u8bf4\u8c0e\uff0c\u53ea\u597d\u7ea2\u7740\u8138\u70b9\u5934\u8bf4\u9053\uff1a\u201c\u662f\uff01\u201d \u3000\u3000\u8f9b\u6377\u672c\u6765\u6b63\u5728\u7ffb\u9605\u4ece\u5730\u4e0a\u6361\u8d77\u7684\u5c0f\u518c\u5b50\uff0c\u7a81\u542c\u5230\u4ed6\u5434\u5927\u54e5\u6709\u4e00\u4e2a\u5973\u670b\u53cb\uff0c\u8fde\u5fd9\u4e5f\u51d1\u4e0a \u6765\uff0c\u805a\u7cbe\u4f1a\u795e\u5730\u542c\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u95ee\u9053\uff1a\u201c\u5979\u4e3a\u4ec0\u4e48\u778e\u4e86\u773c\u5440\uff1f\u201d \u3000\u3000\u51cc\u98ce\u77e5\u9053\u4e0d\u80fd\u9690\u7792\uff0c\u4fbf\u628a\u81ea\u5df1\u548c\u963f\u5170\u7684\u7ecf\u8fc7\u539f\u539f\u672c\u672c\u7684\u8bb2\u4e86\u51fa\u6765\uff0c\u5f85\u4ed6\u8bf4\u5230\u963f\u5170\u8d1f\u6c14\u79bb \u8d70\uff0c\u4eba\u6d77\u832b\u832b\u81ea\u5df1\u4e0d\u77e5\u4f55\u5904\u53bb\u5bfb\uff0c\u4e0d\u7981\u53c8\u5782\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u53ea\u662f\u6447\u5934\uff0c\u53cd\u590d\u9053\uff1a\u201c\u5a03\u513f\uff0c\u6211\u65e9\u5c31\u8bf4\u8fc7\u5929\u4e0b\u6700\u96be\u60f9\u7684\u83ab\u8fc7\u4e8e\u5973\u4eba\uff0c\u6211\u8001\u4eba \u5bb6\uff0c\u4ec0\u4e48\u90fd\u4e0d\u6015\uff0c\u5c31\u6015\u548c\u599e\u513f\u6253\u4ea4\u9053\uff0c\u4e0a\u6b21\u8981\u4e0d\u662f\u8f9b\u6377\u8fd9\u5a03\u513f\u8bc6\u7834\u90a3\u4ec0\u4e48\u2018\u5f52\u5143\u53e4\u9635\u2019\uff0c\u6211 \u8001\u4eba\u5bb6\u53ef\u5c31\u8981\u683d\u5230\u8001\u5c3c\u5a46\u624b\u4e2d\u3002\u4f60\u4e24\u5a03\u513f\u957f\u5f97\u90fd\u4fca\uff0c\u4ee5\u540e\u9ebb\u70e6\u8fd8\u591a\u54e9\uff01\u201d \u3000\u3000\u8f9b\u6377\u542c\u5b8c\u5434\u51cc\u98ce\u7684\u53d9\u8ff0\uff0c\u5fc3\u60c5\u5927\u5927\u6fc0\u52a8\uff0c\u70ed\u8840\u76f4\u5f80\u4e0a\u51b2\uff0c\u5fd8\u8bb0\u4e86\u81ea\u5df1\u4e5f\u6b63\u8981\u5bfb\u627e\u7740\u83c1 \u513f\uff0c\u5c31\u8981\u52a8\u8eab\u66ff\u51cc\u98ce\u5bfb\u627e\u963f\u5170\uff0c\u662f\u4ee5\u5e76\u672b\u542c\u6e05\u5e73\u51e1\u4e0a\u4eba\u6240\u8bf4\u3002\u4ed6\u9053\uff1a\u201c\u5434\u5927\u54e5\uff0c\u6211\u4eec\u8fd9\u5c31\u52a8 \u8eab\u53bb\u627e\u5170\u59d1\u5a18\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u597d\u751f\u611f\u6fc0\uff0c\u6b63\u8981\u5f00\u53e3\u5411\u5e73\u51e1\u4e0a\u4eba\u544a\u522b\uff0c\u5e73\u51e1\u4e0a\u4eba\u5ffd\u5bf9\u8f9b\u6377\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u624b\u4e0a\u62ff \u7684\u662f\u4ec0\u4e48\u4e66\u3002\u201d \u3000\u3000\u8f9b\u6377\u7b54\u9053\uff1a\u201c\u8fd9\u662f\u5434\u5927\u54e5\u521a\u624d\u8eab\u4e0a\u6389\u4e0b\u6765\u7684\uff0c\u91cc\u9762\u5168\u662f\u4e9b\u9b3c\u753b\u7b26\u2026\u2026\u201d \u3000\u3000\u51cc\u98ce\u63a5\u53e3\u62a2\u7740\u9053\uff1a\u201c\u8fd9\u662f\u6211\u5e08\u53d4\u7956\u4e1c\u5cb3\u4e66\u751f\u4e91\u51b0\u82e5\u8f6c\u9001\u6211\u7684\uff0c\u4ed6\u8bf4\u662f\u4e00\u4e2a\u5929\u7afa\u50e7\u4eba\u4e34\u6b7b \u4e4b\u524d\u4ea4\u7ed9\u4ed6\u7684\uff0c\u91cc\u9762\u5168\u662f\u7ec3\u8f7b\u529f\u7684\u65b9\u6cd5\uff0c\u53ef\u60dc\u5168\u662f\u68b5\u6587\uff0c\u4efb\u8c01\u4e5f\u770b\u4e0d\u61c2\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8fde\u58f0\u50ac\u4fc3\u9053\uff1a\u201c\u5feb\u7ed9\u6211\u770b\u770b\u3002\u201d \u3000\u3000\u8f9b\u6377\u6025\u5fd9\u9012\u4e86\u8fc7\u53bb\uff0c\u5e73\u51e1\u4e0a\u4eba\u7ffb\u4e86\u6570\u9875\uff0c\u8138\u8272\u53d8\u51dd\u91cd\uff0c\u8f6c\u8eab\u5c31\u5411\u5c4b\u4e2d\u8dd1\u53bb\u3002 \u3000\u3000\u51cc\u98ce\u60f3\u8ddf\u8fc7\u53bb\uff0c\u8f9b\u6377\u8fde\u8fde\u963b\u6b62\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8fd8\u8bb0\u5f97\u90a3\u5929\u6211\u4eec\u5728\u2018\u65e0\u4e3a\u5385\u2019\u6597\u90a3\u86ee \u5b50\u91d1\u9c81\u5384\u7684\u60c5\u5f62\uff1f\u201d \u3000\u3000\u51cc\u98ce\u60f3\u4e86\u60f3\uff0c\u5927\u559c\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u5bf9\u4e86\uff0c\u6562\u60c5\u4e0a\u4eba\u61c2\u5f97\u68b5\u6587\u7684\u3002\u201d \u3000\u3000\u8f9b\u6377\u70b9\u5934\u9053\uff1a\u201c\u6b63\u662f\uff0c\u6211\u770b\u5e73\u51e1\u4e0a\u4eba\u591a\u534a\u77a7\u51fa\u4e86\u4ec0\u4e48\u7279\u522b\u4e8b\u6545\uff0c\u9700\u8981\u4e00\u4e2a\u4eba\u9759\u5fc3\u53c2\u609f\uff0c \u6211\u4eec\u4e14\u83ab\u53bb\u6253\u6270\u4ed6\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u90a3\u4e48\u4e58\u8fd9\u65f6\u5019\uff0c\u4f60\u4f5c\u5411\u5bfc\uff0c\u5e26\u6211\u6e38\u6e38\u5927\u6222\u5c9b\u53ef\u597d\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5927\u58f0\u53eb\u597d\uff0c\u4e24\u4eba\u643a\u7740\u624b\uff0c\u5c31\u5411\u5c9b\u540e\u8d70\u53bb\u3002 \u3000\u3000\u90a3\u5927\u6222\u5c9b\u540e\u5c9b\u539f\u662f\u6d77\u4e2d\u73ca\u745a\u5f62\u6210\uff0c\u662f\u4ee5\u5c9b\u4e0a\u5bf8\u8349\u4e0d\u751f\uff0c\u4e24\u4eba\u8d70\u8fd1\u6d77\u8fb9\uff0c\u4f46\u89c1\u602a\u77f3\u78f7\u78f7\uff0c \u5b64\u5cf0\u633a\u633a\uff0c\u9ec4\u6c99\u6f20\u6f20\uff0c\u5b9b\u5982\u6c99\u6f20\u98ce\u5149\uff0c\u6c14\u52bf\u751a\u662f\u96c4\u4f1f\u3002\u51cc\u98ce\u9053\uff1a\u201c\u53e4\u4eba\u90fd\u8bf4\u5317\u5c71\u5357\u6c34\uff0c\u60f3\u4e0d \u5230\u5728\u8fd9\u6c5f\u5357\u6d77\u5916\u5b64\u5c9b\uff0c\u7adf\u6709\u5982\u6b64\u5b8f\u4f1f\u666f\u8272\uff0c\u5929\u4e0b\u4e4b\u5927\uff0c\u771f\u662f\u65e0\u5947\u4e0d\u6709\u4e86\u3002\u201d \u3000\u3000\u84e6\u7684\uff0c\u4e00\u4e2a\u5927\u6d6a\u6253\u5411\u5cb8\u8fb9\uff0c\u5377\u8d77\u5343\u767e\u5757\u788e\u77f3\uff0c\u5e26\u5230\u6d77\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u9ad8\u58f0\u541f\u9053\uff1a\u201c\u5927\u6c5f\u4e1c\u53bb\uff0c\u6d6a\u6dd8\u5c3d\u5343\u53e4\u98ce\u6d41\u4eba\u7269\u2026\u2026\u201d \u3000\u3000\u4ed6\u541f\u5230\u8fd9\u91cc\uff0c\u5ffd\u7136\u6b62\u4f4f\uff0c\u5fc3\u5185\u60f3\u5230\uff1a\u201c\u5927\u6c5f\u72b9\u80fd\u5982\u6b64\uff0c\u4f55\u51b5\u4e00\u671b\u65e0\u9645\u7684\u5927\u6d77\u54e9\u3002\u4eba\u751f\u5728 \u8fd9\u4e16\u4e0a\uff0c\u90a3\u771f\u662f\u6e3a\u5c0f\u5f97\u5f88\uff0c\u4efb\u4f60\u662f\u76d6\u4e16\u82f1\u96c4\uff0c\u5230\u5934\u6765\u4e5f\u4e0d\u8fc7\u662f\u4e00\u576f\u9ec4\u571f\u3002\u6211\uff0c\u6211\u53ef\u8981\u5728\u8fd9\u6709 \u9650\u7684\u5e74\u534e\uff0c\u505a\u51fa\u4e9b\u8f89\u714c\u4ee4\u4eba\u6c38\u8fdc\u4e0d\u5fd8\u7684\u5927\u4e8b\uff0c\u8fd9\u624d\u4e0d\u8f9c\u8d1f\u7236\u6bcd\u751f\u6211\uff0c\u6885\u53d4\u53d4\u6559\u6211\u7684\u4e00\u756a\u5fc3 \u8840\u3002\u201d \u3000\u3000\u987f\u65f6\uff0c\u4ed6\u96c4\u5fc3\u4e07\u4e08\uff0c\u8f6c\u8eab\u5bf9\u6b63\u671b\u7740\u9065\u8fdc\u6d77\u5e73\u9762\u7684\u5434\u51cc\u98ce\u8bf4\u9053\uff1a\u201c\u5434\u5927\u54e5\uff0c\u54b1\u4eec\u5148\u53bb\u628a\u5389 \u9e57\u90a3\u4e2a\u8d3c\u5b50\u5bb0\u4e86\uff0c\u518d\u53bb\u627e\u963f\u5170\u548c\u5f20\u83c1\u3002\u201d \u3000\u3000\u51cc\u98ce\u4e5f\u662f\u8c6a\u6c14\u5e72\u4e91\uff0c\u7acb\u523b\u70b9\u5934\u7b54\u5e94\u3002\u8f9b\u6377\u53c8\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u4e0a\u6b21\u88ab\u6052\u6cb3\u4e09\u4f5b\u638c\u4f24\uff0c\u6211\u81ea \u5df1\u7528\u5185\u529f\u7597\u4f24\u65f6\uff0c\u6211\u4e00\u76f4\u82e6\u601d\u4e00\u4e2a\u95ee\u9898\uff0c\u73b0\u5728\u6211\u53ef\u60f3\u901a\u4e86\uff0c\u4e00\u4e2a\u4eba\u5728\u4e16\u4e0a\uff0c\u5982\u679c\u53ea\u662f\u5f92\u7136\u6b66 \u827a\u9ad8\u5f3a\uff0c\u53ea\u662f\u4f7f\u4eba\u4eba\u6015\u4f60\uff0c\u90a3\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u8981\u4eba\u4eba\u90fd\u656c\u91cd\u4f60\uff0c\u5fc3\u670d\u4f60\uff0c\u624d\u662f\u771f\u6b63\u7684\u8c6a\u6770\uff0c\u4ece \u4eca\u4ee5\u540e\uff0c\u6211\u53ef\u8981\u5411\u8fd9\u65b9\u9762\u52aa\u529b\uff0c\u53ea\u662f\u6211\u5929\u6027\u592a\u504f\u6fc0\uff0c\u5927\u54e5\uff0c\u4f60\u53ef\u8981\u597d\u597d\u6307\u5bfc\u6211\uff0c\u6559\u8bad\u6211\u3002\u201d \u3000\u3000\u51cc\u98ce\u542c\u4ed6\u8bf4\u5f97\u5f88\u662f\u8bda\u6073\uff0c\u518d\u770b\u4ed6\u8138\u8272\u5e73\u548c\u60a0\u8fdc\uff0c\u6614\u65e5\u90a3\u79cd\u9ad8\u50b2\u548c\u5bf9\u4efb\u4f55\u4eba\u4efb\u4f55\u7269\u90fd\u7565\u5e26 \u8f7b\u85d0\u7684\u773c\u795e\uff0c\u5df1\u88ab\u4e00\u79cd\u98de\u9038\u6b63\u76f4\u53d6\u4ee3\uff0c\u4e0d\u7531\u5927\u559c\uff0c\u4f38\u51fa\u53f3\u624b\u6293\u7740\u8f9b\u6377\u5de6\u624b\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u606d\u559c \u4f60\uff0c\u4f60\u53c8\u8fdb\u4e86\u4e00\u6b65\u5566\uff0c\u4e91\u7237\u7237\u8bf4\u8fc7\uff0c\u8981\u7ec3\u6210\u7edd\u4e16\u6b66\u529f\uff0c\u4e0d\u4f46\u8981\u5929\u8d44\u654f\u609f\uff0c\u800c\u4e14\u8981\u80f8\u6000\u5bbd\u9614\uff0c \u80fd\u591f\u5305\u7f57\u4e07\u8c61\uff0c\u4f60\u7684\u5929\u8d44\u662f\u6ca1\u6709\u8bdd\u8bf4\u7684\uff0c\u73b0\u5728\u4f60\u80fd\u609f\u5230\u5584\u6076\u662f\u975e\uff0c\u4e0d\u518d\u968f\u6027\u800c\u4e3a\uff0c\u6709\u4ec7\u5fc5 \u62a5\uff0c\u90a3\u80f8\u895f\u81ea\u4f1a\u5f00\u6717\uff0c\u6d77\u9614\u5929\u7a7a\uff0c\u65e5\u540e\u7684\u6210\u5c31\uff0c\u771f\u662f\u4e0d\u53ef\u9650\u91cf\u54e9\uff01\u201d \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u79f0\u8d5e\u81ea\u5df1\uff0c\u5fc3\u4e2d\u611f\u5230\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u4fbf\u62c9\u5f00\u8bdd\u9898\uff0c\u7b11\u9053\uff1a\u201c\u5927\u54e5\uff0c\u80fd\u4f7f\u4f60\u8fd9\u79cd \u7edd\u4e16\u7f8e\u7537\u5b50\u5982\u6b64\u6df1\u60c5\u7684\u59d1\u5a18\uff0c\u53ea\u6015\u662f\u5929\u9999\u56fd\u8272\u5462\uff01\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u6377\u5f1f\u4f60\u522b\u53d6\u7b11\uff0c\u6211\u66fe\u89c1\u8fc7\u7684\u5973\u5b69\u5b50\u8bba\u7f8e\uff0c\u81ea\u7136\u8981\u63a8\u5f20\u83c1\u7b2c\u4e00\u5566\u3002\u201d \u3000\u3000\u8f9b\u6377\u5f88\u611f\u53d7\u7528\uff0c\u51cc\u98ce\u5ffd\u6b63\u8272\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u5f20\u59d1\u5a18\u662f\u4e2a\u5f88\u5584\u826f\u7684\u5973\u5b69\u5b50\uff0c\u4f60\u53ef\u8981\u4e00\u5fc3\u4e00\u610f \u7231\u5979\uff0c\u4fdd\u62a4\u5979\u3002\u54e6\uff01\u5bf9\u4e86\uff0c\u4e0a\u6b21\u4f60\u88ab\u5173\u4e2d\u4e5d\u8c6a\u91cd\u4f24\u65f6\uff0c\u53e3\u53e3\u58f0\u58f0\u558a\u4e00\u4e2a\u59d3\u65b9\u7684\u548c\u4e00\u4e2a\u59d3\u91d1\u7684 \u59d1\u5a18\uff0c\u5979\u4eec\u53ef\u90fd\u662f\u8c01\u5440\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9eef\u7136\uff0c\u4fbf\u628a\u91d1\u6885\u9f84\u5931\u8e2a\uff0c\u65b9\u5c11\u78a7\u5ac1\u7ed9\u5929\u9b54\u91d1\u6b39\u7684\u7ecf\u8fc7\uff0c\u9664\u4e86\u96be\u4e8e\u5f00\u53e3\u5730\u65b9\uff0c\u90fd\u8bb2\u4e86 \u51fa\u6765\u3002 \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u539f\u6765\u4f60\u662f\u4e3a\u4e86\u65b9\u59d1\u5a18\uff0c\u8fd9\u624d\u633a\u8eab\u53d7\u6052\u6cb3\u4e09\u4f5b\u4e00\u638c\uff0c\u4f60\u8fd9\u6837\u4e3a\u5979\u820d\u751f\u6321\u654c\uff0c\u603b \u7b97\u62a5\u7b54\u4e86\u5979\u4e00\u756a\u6df1\u60c5\uff0c\u5979\u73b0\u5728\u5df2\u6709\u5f52\u5bbf\uff0c\u90a3\u5f88\u597d\uff0c\u53ea\u662f\u91d1\u59d1\u5a18\u2026\u2026\u597d\u5728\u5f20\u83c1\u5fc3\u5730\u5584\u826f\uff0c\u603b\u6709 \u89e3\u51b3\u7684\u529e\u6cd5\u3002\u201d \u3000\u3000\u8f9b\u6377\u6fc0\u52a8\u9053\uff1a\u201c\u5927\u54e5\u8bf4\u5f97\u662f\uff0c\u6211\u5e38\u5e38\u60f3\uff0c\u4f17\u751f\u82b8\u82b8\uff0c\u53ef\u662f\u5c31\u6709\u90a3\u6837\u5de7\uff0c\u4ece\u5343\u767e\u4e2a\u4eba\u4e2d\uff0c \u4f60\u5c31\u53ea\u4f1a\u7231\u4e0a\u5979\u4e00\u4e2a\uff0c\u90a3\u4e48\u4f60\u4e3a\u5979\u727a\u7272\uff0c\u4e3a\u5979\u62fc\u547d\uff0c\u5f88\u5f53\u7136\u7684\u4e8b\u4e86\u3002\u201d \u3000\u3000\u4e24\u4eba\u4e92\u5410\u5fc3\u4e8b\uff0c\u8c08\u5f97\u5f88\u662f\u878d\u6d3d\uff0c\u5982\u6d77\u4e00\u822c\u7684\u53cb\u60c5\u6ecb\u6da6\u7740\u4ed6\u4eec\u4e24\u9897\u8d64\u5b50\u4e4b\u5fc3\u3002\u5929\u8272\u6e10\u6e10\u6697 \u6de1\u4e0b\u6765\u3002 \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u6211\u4eec\u4e00\u8d77\u53bb\u770b\u770b\u5e73\u51e1\u4e0a\u4eba\u53bb\u3002\u201d \u3000\u3000\u4e24\u4eba\u6162\u6162\u8d70\u5230\u5c9b\u4e2d\u5e73\u51e1\u4e0a\u4eba\u4f4f\u7684\u5c0f\u5c4b\uff0c\u5e73\u51e1\u4e0a\u4eba\u5750\u5728\u684c\u8fb9\uff0c\u6b63\u5728\u6c89\u601d\uff0c\u5ffd\u7136\u4ed6\u4e00\u62cd\u5149\u5149 \u7684\u5927\u8111\u95e8\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u5bf9\u4e86\uff0c\u5bf9\u4e86\uff0c\u8fd9\u51e0\u624b\u5012\u771f\u5999\u3002\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u4ed6\u5c31\u5411\u8f9b\u5434\u4e8c\u4eba\u5fae\u7b11\u9053\uff1a\u201c\u4e24\u4e2a\u5a03\u513f\uff0c\u54b1\u4eec\u6765\u8d5b\u8d5b\u8db3\u529b\uff0c\u4f60\u4eec\u4e24\u4e2a\u5168\u529b\u5411\u524d\u8dd1\uff0c \u770b\u6211\u8001\u4eba\u5bb6\u8868\u6f14\u4e00\u624b\u7ed9\u4f60\u4eec\u77a7\u3002\u201d \u3000\u3000\u8f9b\u5434\u4e8c\u4eba\u867d\u7136\u83ab\u540d\u5176\u5999\uff0c\u4f46\u77e5\u4e0a\u4eba\u5fc5\u6709\u6df1\u610f\uff0c\u4fbf\u5404\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u4f9d\u8a00\u5411\u524d\u5954\u53bb\u3002 \u3000\u3000\u5954\u4e86\u4e00\u9635\uff0c\u4e8c\u4eba\u4f46\u89c9\u80cc\u540e\u6beb\u65e0\u58f0\u97f3\uff0c\u77e5\u9053\u5e73\u51e1\u4e0a\u4eba\u5e76\u672a\u8ddf\u6765\uff0c\u4f46\u53cd\u8eab\u4e00\u770b\uff0c\u5927\u5403\u4e00\u60ca\uff0c \u539f\u6765\u5e73\u51e1\u4e0a\u4eba\u597d\u7aef\u7aef\u7ad9\u5728\u8eab\u540e\u3002\u8f9b\u6377\u4e0d\u670d\uff0c\u53d1\u8db3\u72c2\u5954\uff0c\u8fd9\u56de\u4ed6\u53ef\u7559\u610f\u8eab\u540e\uff0c\u53ea\u89c1\u5e73\u51e1\u4e0a\u4eba\u53cc \u8db3\u79bb\u5730\u6570\u5bf8\uff0c\u7d27\u7d27\u8ddf\u5728\u8eab\u540e\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u8d77\u6b65\u7528\u52b2\uff0c\u771f\u5982\u51cc\u7a7a\u865a\u6e21\uff0c\u6e43\u6d12\u5df2\u6781\u3002 \u3000\u3000\u8f9b\u6377\u6b62\u6b65\u9053\uff1a\u201c\u60a8\u8001\u4eba\u5bb6\u8fd9\u624b\u771f\u5e05\uff0c\u8fd9\u662f\u90a3\u79d8\u7b08\u6240\u8f7d\u5427\uff01\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u70b9\u70b9\u5934\uff0c\u5434\u51cc\u98ce\u4e5f\u8d76\u4e0a\u524d\u6765\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u5bf9\u8f9b\u6377\u9053\uff1a\u201c\u5a03\u513f\uff0c\u5929\u4e0b\u8f7b\u8eab\u529f\u592b\uff0c\u4f60\u9053\u54ea\u79cd\u8eab\u6cd5\u6700\u4e3a\u795e\u5999\uff1f\u201d \u3000\u3000\u8f9b\u6377\u7b54\u9053\uff1a\u201c\u4f9d\u665a\u8f88\u60f3\uff0c\u5982\u679c\u8981\u7b97\u8eab\u6cd5\u795e\u5947\u96be\u4ee5\u6349\u6478\uff0c\u8981\u63a8\u5c0f\u6222\u5c9b\u6167\u5927\u5e08\u7684\u2018\u8bd8\u6469\u6b65 \u6cd5\u2019\u3002\u201d \u3000\u3000\u5e73\u51e1\u5927\u5e08\u70b9\u5934\u9053\uff1a\u201c\u6211\u8001\u4eba\u5bb6\u4e5f\u662f\u8fd9\u4e48\u60f3\uff0c\u53ef\u662f\u4f60\u4e0a\u6b21\u770b\u6211\u4eec\u2018\u4e16\u5916\u4e09\u4ed9\u2019\u5927\u6218\u2018\u6052\u6cb3 \u4e09\u4f5b\u2019\uff0c\u4f60\u53ef\u77a7\u51fa\u4ec0\u4e48\u5f02\u6837\u6765\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u665a\u8f88\u89c9\u5f97\u90a3\u4e09\u4eba\u8f7b\u529f\u4e4b\u5feb\uff0c\u771f\u6709\u5982\u9b3c\u86c7\uff0c\u8981\u8bba\u901f\u5ea6\uff0c\u6bd4\u8d77\u6167\u5927\u5e08\u7684\u2018\u8bd8\u6469\u6b65 \u6cd5\u2019\uff0c\u6050\u6015\u5c1a\u9ad8\u4e00\u7b79\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u559c\u9053\uff1a\u201c\u5a03\u513f\uff0c\u771f\u806a\u660e\uff0c\u6211\u8001\u4eba\u5bb6\u5f53\u5929\u7b49\u2018\u6052\u6d77\u4e09\u4f5b\u2019\u8d70\u540e\uff0c\u82e6\u601d\u4ed6\u4eec\u8eab\u6cd5\uff0c \u53ea\u89c9\u4e0e\u4e2d\u571f\u5404\u95e8\u5404\u6d3e\u5927\u4e0d\u76f8\u540c\uff0c\u60f3\u4e86\u534a\u5929\uff0c\u4e5f\u60f3\u4e0d\u51fa\u4ec0\u4e48\u9053\u7406\u6765\uff0c\u521a\u624d\u4e00\u770b\u6b64\u4e66\uff0c\u8fd9\u624d\u604d\u7136 \u5927\u609f\u3002\u201d \u3000\u3000\u51cc\u98ce\u63d2\u53e3\u9053\uff1a\u201c\u8fd9\u672c\u4e66\u4e0a\u66fe\u8bb0\u8f7d\u7684\uff0c\u53ef\u5c31\u662f\u6052\u6cb3\u4e09\u4f5b\u90a3\u4e00\u95e8\u7684\u8f7b\u529f\u5417\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8d5e\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u7b28\uff0c\u6765\uff0c\u6765\uff0c\u6211\u8bb2\u4e2a\u6545\u4e8b\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u6052\u6cb3\u4e09\u4f5b\u8fd9\u4e00\u6d3e\u539f\u662f\u5929\u5170\u6469\u67b7\u5bc6\u5b97\uff0c\u6559\u4e2d\u5f1f\u5b50\u4e00\u751f\u82e6\u4fee\uff0c\u7cbe\u7814\u4f5b\u7406\uff0c\u6b66 \u529f\uff0c\u662f\u4ee5\u4ee3\u4ee3\u90fd\u51fa\u4e86\u4e0d\u5c11\u795e\u80fd\u901a\u5929\u7684\u5f97\u9053\u9ad8\u50e7\u3002\u4f46\u662f\u6559\u4e2d\u5927\u4f4d\u4f20\u5230\u8fd9\u6052\u6cb3\u4e09\u4f5b\u624b\u4e2d\uff0c\u8fd9\u4e09\u4eba \u96c4\u624d\u5927\u7565\uff0c\u600e\u80af\u6f5c\u8eab\u82e6\u4fee\uff0c\u662f\u4ee5\u6539\u53d8\u6559\u89c4\uff0c\u5e7f\u6536\u5f1f\u5b50\uff0c\u4e0d\u4f46\u72ec\u9738\u5929\u7afa\uff0c\u7adf\u60f3\u6269\u5c55\u52bf\u529b\u4e8e\u4e2d \u539f\u3002\u201d \u3000\u3000\u8f9b\u6377\u6124\u7136\u9053\uff1a\u201c\u53ea\u6015\u6ca1\u6709\u8fd9\u4e48\u7b80\u4fbf\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u63a5\u7740\u9053\uff1a\u201c\u2018\u6052\u6cb3\u4e09\u4f5b\u2019\u4e00\u5171\u6536\u4e86\u516d\u4e2a\u5f92\uff0c\u5176\u4e2d\u6700\u5c0f\u7684\u5c31\u662f\u4e0a\u6b21\u5728\u65e0\u4e3a\u5385\u8000\u6b66 \u626c\u5a01\u7684\u91d1\u9c81\u5384\u4e86\u3002\u8fd9\u516d\u4e2a\u4eba\u4e2d\uff0c\u7b2c\u56db\u4e2a\u662f\u4e2a\u82e6\u884c\u50e7\u540d\u53eb\u5df4\u9c81\u65af\uff0c\u4ed6\u56e0\u770b\u4e0d\u60ef\u5e08\u7236\u5012\u884c\u9006\u65bd\uff0c \u624b\u6bb5\u72e0\u8fa3\uff0c\u4fbf\u5e38\u5e38\u8fdb\u529d\u5fe0\u8a00\uff0c\u4f46\u4ed6\u5e08\u7236\u4eec\u4e0d\u4f46\u4e0d\u542c\uff0c\u53cd\u800c\u5bf9\u4ed6\u538c\u6076\u8d77\u6765\uff0c\u5389\u5bb3\u7684\u6b66\u827a\u4e5f\u4e0d\u6559 \u4ed6\u3002\u201d \u3000\u3000\u201c\u540e\u6765\uff0c\u6709\u4e00\u6b21\uff0c\u4e00\u4e2a\u5929\u7afa\u4eba\u62ff\u7740\u7956\u4f20\u7684\u79d8\u7b08\u6295\u5954\u6052\u6cb3\u4e09\u4f5b\u6559\u4ed6\u7ec3\u4e66\u4e0a\u6b66\u529f\uff0c\u800c\u6052\u6cb3\u4e09 \u4f5b\u4e5f\u53ef\u4ee5\u7167\u4e66\u7ec3\u4e60\uff0c\u8fd9\u4eba\u5bf9\u4e8e\u6b66\u5b66\uff0c\u53ef\u8bf4\u662f\u5b8c\u5168\u4e0d\u61c2\uff0c\u4ed6\u77e5\u81ea\u5df1\u8fd9\u672c\u7956\u4f20\u79d8\u7b08\uff0c\u7684\u786e\u8f7d\u6709\u4e00 \u79cd\u81f3\u9ad8\u529f\u592b\uff0c\u4ed6\u8bbf\u95ee\u4e86\u8bb8\u591a\u6b66\u5e08\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\u61c2\u4e66\u4e0a\u7684\u529f\u592b\uff0c\u6700\u540e\u542c\u8bf4\u6052\u6cb3\u4e09\u4f5b\u6b66\u529f\u662f\u5168\u5929 \u7afa\u7b2c\u4e00\uff0c\u4fbf\u60f3\u51fa\u8fd9\u4e2a\u4ea4\u6362\u7684\u65b9\u6cd5\u3002\u201d \u3000\u3000\u8f9b\u6377\u5fcd\u8010\u4e0d\u4f4f\uff0c\u63d2\u53e3\u9053\uff1a\u201c\u90a3\u5c31\u662f\u6211\u5927\u54e5\u8fd9\u672c\u79d8\u7b08\u4e86\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u6b63\u662f\u8fd9\u672c\uff0c\u8fd9\u5c0f\u518c\u8017\u662f\u8fbe\u6469\u79d8\u7b08\u4e2d\u7684\u8f7b\u529f\u7bc7\uff0c\u6052\u6cb3\u4e09\u4f5b\u4e00\u89c1\uff0c\u81ea\u7136\u662f\u5927 \u559c\u8fc7\u671b\uff0c\u4f46\u4ed6\u4eec\u600e\u80fd\u5bb9\u8bb8\u5929\u7afa\u5883\u5185\u518d\u51fa\u9ad8\u624b\uff0c\u662f\u4ee5\u4e0d\u4f46\u4e0d\u6559\u90a3\u4eba\u529f\u592b\uff0c\u53cd\u800c\u6697\u6697\u628a\u90a3\u4eba\u5bb3 \u4e86\uff0c\u8fd9\u672c\u79d8\u7b08\u4fbf\u88ab\u4ed6\u4eec\u4e09\u4eba\u636e\u4e3a\u5df1\u6709\u3002\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u6052\u6cb3\u4e09\u4f5b\u6b66\u529f\u867d\u7136\u9ad8\u5f3a\u65e0\u6bd4\uff0c\u60f3\u4e0d\u5230\u4eba\u54c1\u5374\u5982\u6b64\u5351\u4e0b\uff0c\u54fc\uff0c\u4e0b\u6b21\u518d\u649e\u7740\u4ed6 \u4eec\uff0c\u597d\u6b79\u4e5f\u8981\u62fc\u4e2a\u6b7b\u6d3b\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u63a5\u7740\u9053\uff1a\u201c\u8fd9\u4e8b\u88ab\u4ed6\u4eec\u7b2c\u56db\u5f1f\u5b50\u77e5\u9053\u4e86\uff0c\u4ed6\u5192\u6b7b\u963b\u6b62\u65e0\u6548\uff0c\u77e5\u9053\u5e08\u7236\u5bf9\u81ea\u5df1\u5df2 \u5b58\u6709\u7591\u5fc3\uff0c\u672c\u4eba\u53c8\u4e0d\u613f\u540c\u6d41\u5408\u6c61\uff0c\u60f3\u4e86\u534a\u5929\uff0c\u53ea\u6709\u9003\u8d70\u4e00\u6761\u8def\u3002\u4f46\u4ed6\u4e00\u60f3\uff0c\u4e09\u4e2a\u5e08\u7236\u73b0\u5728\u6b66 \u529f\u5df2\u662f\u96be\u9022\u654c\u624b\uff0c\u5c06\u6765\u7ec3\u6210\u7ecf\u4e0a\u529f\u592b\uff0c\u5c82\u4e0d\u662f\u5982\u864e\u6dfb\u7ffc\uff0c\u4efb\u4ed6\u4e3a\u6076\uff0c\u65e0\u4eba\u80fd\u5236\u4e86\u5417\uff1f\u4e8e\u662f\u5077 \u5077\u4e58\u5927\u5bb6\u90fd\u4e0d\u6ce8\u610f\uff0c\u5077\u4e86\u79d8\u7b08\u9003\u53bb\u3002\u201d \u3000\u3000\u201c\u8fd9\u4eba\u54c1\u683c\u751a\u662f\u9ad8\u5c1a\uff0c\u4ed6\u4e3a\u4e86\u8868\u793a\u81ea\u5df1\u76d7\u4e66\u5e76\u975e\u662f\u60f3\u5077\u5b66\uff0c\u800c\u662f\u9632\u6b62\u4ed6\u5e08\u7236\u5f02\u65e5\u6b66\u529f\u592a \u9ad8\uff0c\u8086\u610f\u4e3a\u6076\uff0c\u662f\u4ee5\u7acb\u8a93\u7ec8\u8eab\u4e0d\u770b\u4e66\u4e2d\u6240\u8f7d\u3002\u4ed6\u5728\u4e2d\u571f\u6570\u5341\u5e74\uff0c\u6052\u6cb3\u4e09\u4f5b\u56e0\u4e3a\u7fbd\u6bdb\u672a\u4e30\uff0c\u662f \u4ee5\u5e76\u672a\u5230\u4e2d\u539f\u8ffd\u6355\u4ed6\u3002\u5a03\u513f\u4ed6\u600e\u4e48\u628a\u8fd9\u672c\u4e66\u4ea4\u7ed9\u4f60\u4e91\u7956\u5e08\u53d4\uff0c\u4f60\u4e00\u5b9a\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u51cc\u98ce\u542c\u5f97\u4eba\u795e\uff0c\u95fb\u8a00\u7b54\u9053\uff1a\u201c\u4e91\u7956\u5e08\u53d4\u6709\u4e00\u6b21\u9047\u5230\u4ed6\u88ab\u51e0\u4e2a\u4eba\u56f4\u653b\uff0c\u51fa\u624b\u6551\u4e86\u4ed6\uff0c\u4f46\u4ed6 \u5df2\u8eab\u53d7\u91cd\u4f24\uff0c\u81ea\u77e5\u5fc5\u6b7b\uff0c\u662f\u4ee5\u628a\u8fd9\u79d8\u7b08\u9001\u7ed9\u7956\u5e08\u53d4\u3002\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u8fd9\u4e66\u4e0a\u6240\u8f7d\u7684\u786e\u662f\u975e\u540c\u5c0f\u53ef\uff0c\u90a3\u6052\u6cb3\u4e09\u4f5b\u53ea\u5b66\u4f1a\u4e86\u4e00\u534a\uff0c\u662f\u4ee5\u8eab\u6cd5\u8f7b\u76c8 \u6709\u4f59\uff0c\u5374\u5acc\u4e0d\u591f\u51dd\u91cd\u3002\u540e\u534a\u90e8\u6240\u8f7d\u662f\u8981\u6709\u6781\u4e0a\u4e58\u7684\u5185\u529f\u624d\u80fd\u7ec3\u4e60\uff0c\u6240\u4ee5\u5c31\u662f\u90a3\u82e6\u884c\u50e7\u4e0d\u5077 \u53bb\uff0c\u6052\u6cb3\u4e09\u4f5b\u5f53\u5e74\u4e5f\u4e0d\u80fd\u7ec3\u3002\u201d \u3000\u3000\u8f9b\u6377\u51cc\u98ce\u4e0d\u7ea6\u800c\u540c\u95ee\u9053\uff1a\u201c\u60a8\u8001\u4eba\u5bb6\u521a\u624d\u90a3\u624b\u51cc\u7a7a\u865a\u6e21\uff0c\u53ef\u662f\u8fd9\u4e66\u540e\u534a\u90e8\u6240\u8bb0\u8f7d\u7684 \u5417\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u4e0d\u7b54\uff0c\u5ffd\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4e0a\u6b21\u6052\u6cb3\u4e09\u4f5b\u6ca1\u6709\u8ba8\u5230\u4fbf\u5b9c\u56de\u53bb\uff0c\u4f46\u4ed6\u4eec\u5fc3\u6000\u53f5\u6d4b\uff0c\u867d \u7136\u4ed6\u4eec\u672c\u4eba\u4e0d\u4f1a\u518d\u5192\u7136\u5927\u4e2d\u539f\uff0c\u4f46\u662f\u4ed6\u4eec\u90a3\u4e2a\u5c0f\u5f92\u513f\u91d1\u9c81\u5384\u4e0a\u6b21\u5728\u5927\u5ead\u5382\u4f17\u4e2d\u683d\u5728\u8f9b\u6377\u5a03\u513f \u624b\u91cc\uff0c\u8fdf\u65e9\u8981\u62a5\u4ec7\u3002\u201d\u8bf4\u5230\u8fd9\u91cc\u5e73\u51e1\u4e0a\u4eba\u5ffd\u7136\u9a7b\u53e3\u4e0d\u8bed\uff0c\u53cc\u76ee\u5fae\u7ffb\uff0c\u597d\u534a\u5929\u624d\u9053\uff1a\u201c\u5a03\u513f\uff0c \u4f60\u628a\u8001\u5c3c\u5a46\u4f20\u7ed9\u4f60\u7684\u8bd8\u6469\u6b65\u6cd5\u65bd\u4e00\u904d\u77a7\u77a7\u3002\u201d \u3000\u3000\u8f9b\u6377\u5f53\u4e0b\u628a\u56db\u5341\u4e5d\u8def\u6b65\u6cd5\u65bd\u5b8c\uff0c\u5e73\u51e1\u4e0a\u4eba\u7b11\u9053\uff1a\u201c\u8fd9\u2018\u6d01\u6469\u6b65\u6cd5\u2019\u4f60\u5c31\u662f\u4e0d\u65bd\uff0c\u6211\u8001\u4eba \u5bb6\u4e5f\u77e5\u9053\u5b83\u7684\u597d\u5904\uff0c\u4e34\u654c\u4e4b\u9645\u679c\u771f\u5999\u5165\u6beb\u5398\uff0c\u6240\u4ee5\u8fd9\u5c31\u5947\u602a\u5566\u2014\u2014\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u5947\u602a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u9053\uff1a\u201c\u8fd9\u5929\u4e1d\u8f7b\u529f\u5982\u8bba\u5feb\u6377\u786e\u662f\u4e16\u4e0a\u65e0\u53cc\uff0c\u8bd8\u6469\u6b65\u6cd5\u4e5f\u4e0d\u662f\u5bf9\u624b\uff0c\u4f46\u662f\u6052\u6cb3\u4e09 \u4f5b\u6ca1\u6709\u5b66\u5168\uff0c\u4e34\u654c\u6beb\u65e0\u7cbe\u5fae\u53d8\u5316\uff0c\u4f46\u662f\u4ee5\u6052\u6cb3\u4e09\u4f5b\u7684\u529f\u529b\uff0c\u4e34\u654c\u4e4b\u9645\u4e5f\u4e0d\u77e5\u53d8\u5316\uff0c\u96be\u9053\u4ed6\u4eec \u5b66\u4e86\u8fd9\u5957\u8f7b\u529f\uff0c\u53ea\u662f\u4e3a\u5feb\u800c\u5df2\u5417\uff1f\u201d \u3000\u3000\u8f9b\u6377\u5434\u51cc\u98ce\u4e24\u4eba\u4e5f\u89c9\u5947\u602a\uff0c\u5e73\u51e1\u4e0a\u4eba\u53c8\u9053\uff1a\u201c\u5982\u679c\u6211\u8001\u773c\u4e0d\u82b1\uff0c\u8fd9\u5929\u7afa\u8f7b\u529f\u5fc5\u7136\u8fd8\u6709\u4e00 \u6869\u5947\u5999\u529f\u7528\uff0c\u65e5\u540e\u4f60\u4eec\u82e5\u518d\u78b0\u4e0a\u6052\u6cb3\u4e09\u4f5b\u5c31\u77e5\u6211\u8a00\u4e0d\u865a\u4e86\u2014\u2014\u597d\u5566\uff0c\u65e2\u7136\u8fd9\u4e66\u662f\u4f60\u4eec\u7684\uff0c\u6211 \u5c31\u628a\u8fd9\u8f7b\u529f\u8bc0\u8981\u6559\u4f60\u4eec\u5427\u3002\u201d \u3000\u3000\u5f53\u65f6\u4ed6\u4fbf\u628a\u8f7b\u529f\u79d8\u8bc0\u4f20\u7ed9\u4e24\u4eba\uff0c\u4e24\u4eba\u90fd\u662f\u7edd\u9876\u5929\u8d44\uff0c\u81ea\u662f\u4e00\u70b9\u5373\u900f\u3002 \u3000\u3000\u7b49\u5230\u8f7b\u529f\u4f20\u6388\u5b8c\u6bd5\uff0c\u8f9b\u6377\u731b\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u65e5\u5df2\u843d\u897f\uff0c\u8fde\u5fd9\u8d77\u8eab\u544a\u8f9e\uff0c\u5e73\u51e1\u4e0a\u4eba\u89c1\u4e24\u4eba\u90fd \u662f\u9762\u5e26\u7126\u6025\u4e4b\u8272\uff0c\u77e5\u9053\u4e24\u4eba\u90fd\u6709\u201c\u6025\u4e8b\u201d\uff0c\u5fae\u5fae\u4e00\u7b11\u9053\uff1a \u3000\u3000\u201c\u8981\u8d70\u5c31\u8d70\uff0c\u6211\u8001\u4eba\u5bb6\u53ef\u4e0d\u5e0c\u7f55\u3002\u201d \u3000\u3000\u8f9b\u6377\u51cc\u98ce\u4e8c\u4eba\u65bd\u4e86\u4e00\u793c\uff0c\u98de\u5feb\u8f7b\u8eab\u800c\u53bb\uff0c\u8eab\u540e\u4f20\u6765\u5e73\u51e1\u4e0a\u4eba\u5185\u529b\u5145\u6c9b\u7684\u7b11\u58f0\u3002 \u3000\u3000\u5728\u8239\u4e0a\uff0c\u8f9b\u6377\u51cc\u98ce\u8ba1\u5212\u597d\u5148\u4e0a\u5d06\u5cd2\u627e\u5389\u9e57\u8ba8\u56de\u5b9d\u5251\uff0c\u987a\u4fbf\u7ea6\u597d\u4e94\u5927\u5251\u6d3e\u4e00\u7b97\u8001\u5e10\uff0c\u800c\u4e14 \u4e5f\u53ef\u4ee5\u6cbf\u9014\u8bbf\u8bbf\u83c1\u513f\u548c\u963f\u5170\u7684\u6d88\u606f\u3002 \u3000\u3000\u5341\u5929\u4e4b\u540e\uff0c\u6c5f\u6e56\u4e0a\u4f20\u51fa\u4e00\u4ef6\u4e8b\uff0c\u201c\u6885\u9999\u795e\u5251\u201d\u8f9b\u6377\u548c\u201c\u5355\u5251\u65ad\u9b42\u201d\u7684\u513f\u5b50\u5434\u51cc\u98ce\u4e0a\u5d06\u5cd2 \u5bfb\u5389\u9e57\uff0c\u5389\u9e57\u5374\u907f\u800c\u4e0d\u89c1\uff0c\u654f\u611f\u7684\u4eba\u4f1a\u611f\u89c9\u5230\u5251\u795e\u5389\u9e57\u201c\u5929\u4e0b\u7b2c\u4e00\u5251\u201d\u7684\u5934\u8854\u5c06\u4e0d\u4fdd\u4e86\u3002 \u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u81ea\u4ece\u5f53\u65e5\u6cf0\u5c71\u5927\u4f1a\u4e4b\u540e\uff0c\u5251\u795e\u5389\u9e57\u5c31\u59cb\u7ec8\u4e0d\u89c1\u4e86\u8e2a\u8ff9\uff01\u6167\u5fc3\u4eba\u5fc5\u7136\u77e5\u9053\u8fd9\u5176\u4e2d \u6709\u4ec0\u4e48\u53e4\u602a\u5427\uff01 \u3000\u3000\u521d\u51ac\u65f6\u5206\u3002 \u3000\u3000\u5bd2\u51c9\u523a\u9aa8\u7684\u5317\u98ce\u8086\u52b2\u2014\u2014 \u3000\u3000\u53f7\u79f0\u795e\u5dde\u7b2c\u4e00\u5251\u6d3e\u7684\u5d06\u5cd2\uff0c\u6574\u4e2a\u540d\u5c71\u57cb\u5728\u7247\u7247\u767d\u4e91\u4e4b\u4e0b\uff0c\u94f6\u7136\u832b\u832b\uff0c\u4e00\u7247\u8083\u6740\u51c4\u51c9\u4e4b \u8272\u3002 \u3000\u3000\u4e5f\u8bb8\u662f\u5730\u52bf\u9ad8\uff0c\u6c14\u5019\u6108\u5bd2\u3002\u6628\u591c\u91cc\u9e45\u6bdb\u5927\u7684\u96ea\u82b1\u6e29\u5929\u98de\u821e\uff0c\u9634\u9709\u6c89\u6c89\u7684\u5929\u7a7a\uff0c\u4e00\u65e9\u8fd8\u662f \u4e1d\u6beb\u4e0d\u6563\uff0c\u53ea\u662f\u5929\u516c\u4f5c\u7f8e\uff0c\u5012\u662f\u5927\u96ea\u505c\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u9752\u5143\u89c2\u2014\u2014\u8fd9\u53f7\u79f0\u795e\u5dde\u7b2c\u4e00\u5251\u6d3e\u7684\u53d1\u6e90\u5730\uff0c\u5728\u5927\u96ea\u6ec2\u6cb1\u4e2d\u5dcd\u7136\u77d7\u7acb\u3002\u7edd\u65e9\uff0c\u89c2\u524d\u4fbf\u6709\u4e00 \u5bf9\u9762\u6e05\u76ee\u79c0\u7684\u5e7c\u7ae5\u5728\u5fd9\u7740\u6253\u626b\u95e8\u9636\u3002 \u3000\u3000\u5927\u96ea\u65b9\u6b62\uff0c\u5c71\u9876\u4e0a\u79ef\u96ea\u76c8\u5c3a\uff0c\u4e24\u4e2a\u9752\u886b\u50ee\u5404\u6301\u4e00\u67c4\u626b\u5e1a\uff0c\u4f7f\u52b2\u7684\u62c2\u626b\uff0c\u77a7\u4ed6\u4eec\u4e3e\u624b\u6295\u8db3 \u95f4\uff0c\u663e\u7136\u751a\u662f\u6709\u529b\uff0c\u6253\u626b\u96ea\u82b1\uff0c\u4e1d\u6beb\u4e0d\u9732\u754f\u7f29\u4e4b\u6001\uff0c\u5230\u5e95\u662f\u540d\u95e8\u5927\u6d3e\uff0c\u8fde\u8fd9\u7b49\u4e0d\u61c2\u4e5f\u662f\u4e00\u8eab \u529f\u592b\u3002 \u3000\u3000\u9759\u6781\u4e86\uff0c\u591c\u6765\u5927\u96ea\u98d8\u821e\uff0c\u5929\u5bd2\u5730\u51bb\uff0c\u4e00\u5207\u751f\u7269\u90fd\u754f\u7f29\u4e0d\u524d\uff0c\u662f\u4ee5\u6574\u4e2a\u5d06\u5cd2\u5c71\u4e0a\u5bc2\u7136\u65e0 \u58f0\uff0c\u53ea\u6709\u4e24\u4e2a\u5e7c\u50ee\u4e00\u9762\u6253\u626b\uff0c\u4e00\u9762\u563b\u7b11\uff0c\u53d1\u51fa\u7684\u5a07\u5ae9\u7ae5\u97f3\u5728\u7a7a\u6c14\u4e2d\u56de\u8361\u3002 \u3000\u3000\u4ed6\u4eec\u4e24\u4eba\u624b\u8db3\u5e76\u7528\uff0c\u4e0d\u4e00\u4f1a\u4fbf\u626b\u5f00\u4e00\u6761\u5f88\u5bbd\u7684\u752c\u9053\uff0c\u957f\u957f\u5730\u901a\u51fa\u53bb\uff0c\u770b\u770b\u4ed6\u4eec\u7684\u5e74\u9f84\uff0c \u8f83\u5e74\u957f\u7684\u624d\u4e0d\u8fc7\u5341\u4e09\u56db\u5c81\uff0c\u8f83\u5c0f\u7684\uff0c\u624d\u4ec5\u4ec5\u5341\u5c81\u5de6\u53f3\uff0c\u4e24\u4eba\u5230\u5e95\u7ae5\u5fc3\u672a\u6cef\uff0c\u518d\u62c2\u5f97\u51e0\u626b\uff0c\u4e00 \u9f50\u505c\u624b\uff0c\u90a3\u5c0f\u4e00\u70b9\u9053\uff1a\u201c\u6e05\u54e5\uff0c\u6211\u4e0d\u60f3\u626b\u4e86\u2014\u2014\u201d \u3000\u3000\u90a3\u88ab\u79f0\u4f5c\u6e05\u98ce\u7684\u5e74\u9f84\u8f83\u957f\uff0c\u968f\u53e3\u9053\uff1a\u201c\u770b\u770b\u5929\u8272\uff0c\u4e0d\u51fa\u6b63\u5348\u53c8\u6709\u4e00\u573a\u5927\u96ea\uff0c\u54b1\u4eec\u662f\u767d\u8d39 \u5fc3\u529b\u4e86\u2014\u2014\u201d\u8bf4\u7740\u6307\u6307\u90a3\u9634\u973e\u7684\u5929\u8272\u3002 \u3000\u3000\u90a3\u4e2a\u7684\u70b9\u5934\u56b7\u9053\uff1a\u201c\u65e2\u662f\u5982\u6b64\u8bf4\uff0c\u4f55\u5fc5\u8fd8\u8981\u6253\u626b\u2014\u2014\u201d \u3000\u3000\u6e05\u98ce\u7b54\u9053\uff1a\u201c\u8bf4\u662f\u5982\u6b64\uff0c\u6211\u4e5f\u4e0d\u60f3\u626b\u4e86\uff0c\u6765\uff0c\u660e\u6708\u5f1f\uff0c\u597d\u4e45\u6ca1\u6709\u7ec3\u4e60\u8fc7\u62db\u4e86\uff0c\u542c\u8bf4\u65e5\u524d \u8bf8\u845b\u53d4\u53d4\u6307\u70b9\u4e86\u4f60\u90a3\u5957\u2018\u8ffd\u4e91\u62f3\u2019\u2014\u2014\u201d \u3000\u3000\u4ed6\u6240\u8bf4\u7684\u8bf8\u845b\u53d4\u53d4\u4e0d\u7528\u8bf4\u4fbf\u662f\u90a3\u6b66\u6797\u7b2c\u4e00\u5251\u2014\u2014\u5251\u795e\u5389\u9e57\u624b\u4e0b\u7684\u201c\u4e09\u7edd\u5251\u5ba2\u201d\u4e4b\u9996\u8bf8\u845b \u660e\u4e86\u3002 \u3000\u3000\u90a3\u660e\u6708\u5c0f\u7ae5\u4e0d\u5f85\u6e05\u98ce\u8bf4\u5b8c\uff0c\u62a2\u7740\u9053\uff1a\u201c\u5bf9\u5566\uff0c\u5bf9\u5566\uff0c\u8ffd\u4e91\u62f3\u2026\u2026\u54a6\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u5c1a\u672a\u8bf4\u5b8c\uff0c\u773c\u73e0\u6ef4\u6e9c\u6e9c\u7684\u6253\u8f6c\uff0c\u84e6\u7136\u60ca\u54a6\u4e00\u58f0\u3002 \u3000\u3000\u6e05\u98ce\u5927\u611f\u5947\u602a\uff0c\u5927\u58f0\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u660e\u6708\u4f38\u624b\u6307\u6307\u90a3\u5ea7\u9053\u89c2\uff0c\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u4f60\u770b\u662f\u8c01\u5230\u54b1\u4eec\u89c2\u6765\u6295\u67ec\u5e16\u5b50\u2014\u2014\u201d \u3000\u3000\u6e05\u98ce\u987a\u7740\u4ed6\u624b\u6307\u7684\u5730\u65b9\u770b\u53bb\uff0c\u53ea\u89c1\u9752\u5143\u89c2\u7684\u95e8\u724c\u4e0a\u7aef\u6b63\u7684\u9489\u4e86\u4e00\u5f20\u62dc\u67ec\u6a21\u6837\u7684\u7eb8\u7247\u5b50\u3002 \u4e24\u4eba\u4e00\u822c\u5fc3\u601d\uff0c\u4e00\u9f50\u5954\u4e86\u8fc7\u53bb\uff0c\u4f46\u89c1\u4e24\u9053\u9752\u5f71\u4e00\u95f4\uff0c\u4e24\u4eba\u5df2\u6765\u5230\u95e8\u524d\u2014\u2014 \u3000\u3000\u6e05\u98ce\u4e00\u4e2a\u7bad\u6b65\u4e0a\u4e86\u5c4b\u69fd\uff0c\u4ed4\u7ec6\u6253\u91cf\u90a3\u7eb8\u7247\uff0c\u679c\u7136\u662f\u4e00\u7eb8\u62dc\u67ec\uff0c\u7528\u5927\u7ea2\u989c\u8272\u7684\u5c01\u7eb8\u5305\u7740\uff0c \u5728\u94f6\u7691\u7691\u7684\u96ea\u5730\u4e0a\u76ca\u53d1\u663e\u5f97\u9c9c\u8273\u593a\u76ee\u3002 \u3000\u3000\u770b\u6837\u5b50\u8fd9\u62dc\u67ec\u662f\u4eba\u5bb6\u6628\u65e5\u591c\u665a\u653e\u4e0a\u7684\uff0c\u800c\u4e14\u653e\u5728\u5317\u98ce\u7684\u5730\u65b9\uff0c\u5e76\u6ca1\u6709\u88ab\u96ea\u82b1\u6cbe\u6e7f\uff0c\u663e\u7136 \u5bf9\u65b9\u662f\u4ece\u5bb9\u4e0d\u8feb\u7684\u6295\u67ec\uff0c\u800c\u6574\u4e2a\u9752\u5143\u89c2\uff0c\u9ad8\u624b\u4e91\u96c6\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u53d1\u73b0\u6628\u591c\u884c\u4eba\u767b\u5c71\u6295\u67ec\uff0c \u770b\u6765\u8fd9\u6295\u67ec\u4eba\u7684\u529f\u592b\u771f\u662f\u9ad8\u4e0d\u53ef\u6d4b\u7684\u4e86\u3002 \u3000\u3000\u6e05\u98ce\u5c0f\u5fc3\u628a\u62dc\u5e16\u5b50\u53d6\u4e5f\uff0c\u8df3\u4e0b\u5730\u6765\uff0c\u660e\u6708\u65e9\u5df2\u4e0d\u8010\u70e6\uff0c\u9ad8\u58f0\u53eb\u9053\uff1a\u201c\u54e5\u54e5\uff0c\u662f\u4ec0\u4e48\u73a9 \u610f\uff1f\u201d \u3000\u3000\u6e05\u98ce\u5fae\u5fae\u6447\u5934\u9053\uff1a\u201c\u679c\u7136\u662f\u4e00\u7eb8\u62dc\u67ec\uff0c\u4eba\u5bb6\u5bc6\u5bc6\u5c01\u8d77\uff0c\u6211\u4eec\u8fd8\u662f\u4e0d\u62c6\u4e3a\u5999\uff0c\u53bb\u627e\u8bf8\u845b\u53d4 \u53d4\u4ed6\u4eec\u53bb\u770b\u770b\uff0c\u4ed6\u4eec\u4e5f\u8bb8\u77e5\u9053\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u4f38\u624b\u633d\u7740\u660e\u6708\uff0c\u8df3\u8df3\u8e66\u8e66\u8d70\u5165\u89c2\u4e2d\u3002 \u3000\u3000\u53ea\u89c1\u8fce\u9762\u4eba\u5f71\u4e00\u95ea\uff0c\u4e00\u4e2a\u58f0\u97f3\u547c\u53eb\u9053\uff1a\u201c\u6e05\u98ce\u3001\u660e\u6708\uff0c\u4e00\u5927\u65e9\u5c31\u5435\u5435\u95f9\u95f9\uff0c\u600e\u4e48\u5730\u6ca1\u6709 \u626b\u597d\u5c31\u5077\u61d2\u6e9c\u4e48\uff1f\u201d \u3000\u3000\u968f\u7740\u8bed\u58f0\uff0c\u8d70\u51fa\u4e00\u4e2a\u5e74\u7ea6\u4e8c\u5341\u4e03\u516b\u7684\u7537\u5b50\uff0c\u6e05\u98ce\u3001\u660e\u6708\u4e00\u89c1\uff0c\u4e00\u8d77\u53eb\u9053\uff1a\u201c\u4e8e\u53d4\u53d4\uff0c\u5feb \u6765\u770b\u2014\u2014\u201d \u3000\u3000\u6562\u60c5\u8fd9\u59d3\u4e8e\u7684\u6c49\u5b50\u6b63\u662f\u90a3\u4e09\u7edd\u5251\u7684\u5c45\u4e2d\u5730\u7edd\u5251\u4e8e\u4e00\u98de\u3002 \u3000\u3000\u4e8e\u4e00\u98de\u5fae\u5fae\u7b11\u9053\uff1a\u201c\u770b\u4ec0\u4e48\uff1f\u201d\u8bf4\u7740\u4ece\u6e05\u5185\u624b\u4e2d\u628a\u5e16\u63a5\u8fc7\uff0c\u5c0f\u5fc3\u6495\u5f00\u4e00\u770b\uff0c\u4e0d\u89c9\u8138\u8272\u5927 \u53d8\uff0c\u6025\u6025\u5fd9\u5fd9\u95ee\u9053\uff1a\u201c\u6e05\u98ce\uff0c\u8fd9\u73a9\u610f\u513f\u662f\u5728\u4ec0\u4e48\u5730\u65b9\u62fe\u5230\u7684\u2014\u2014\u201d \u3000\u3000\u6e05\u98ce\u5c1a\u672a\u7b54\u8bb8\uff0c\u660e\u6708\u5374\u62a2\u7740\u9053\uff1a\u201c\u8fd9\u4e2a\u662f\u5728\u89c2\u95e8\u5927\u5323\u4e0a\u62fe\u5230\u7684\u2014\u2014\u201d \u3000\u3000\u4e8e\u4e00\u98de\u54fc\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u4f60\u4eec\u518d\u53bb\u6253\u626b\u5427\u2014\u2014\u201d\u8bf4\u7740\u9063\u51fa\u4e24\u7ae5\uff0c\u8fd4\u8eab\u6025\u6b65\u5165\u5185\uff0c\u8d70\u5230\u4e00 \u95f4\u623f\u524d\uff0c\u53e9\u95e8\u9053\uff1a\u201c\u5927\u5e08\u5144\uff0c\u5927\u5e08\u5144\u2026\u2026\u201d \u3000\u3000\u4ed6\u8fd9\u6025\u6025\u5fd9\u5fd9\u7684\u53e9\u95e8\uff0c\u5012\u60ca\u52a8\u4e86\u89c2\u4e2d\u5176\u4ed6\u7684\u4eba\uff0c\u4e8e\u4e00\u98de\u795e\u8272\u614c\u614c\u5f20\u5f20\uff0c\u4e0d\u7406\u4f17\u4eba\u7684\u8be2 \u95ee\uff0c\u7b49\u5929\u7edd\u5251\u8bf8\u845b\u660e\u542f\u95e8\uff0c\u6025\u5165\u623f\u4e2d\uff0c\u628a\u62dc\u67ec\u9012\u4e0a\u9053\uff1a\u201c\u8f9b\u6377\uff0c\u8f9b\u6377\u8fd9\u53ae\u7ec8\u4e8e\u6253\u4e0a\u95e8\u6765\u4e86\u2014 \u2014\u201d \u3000\u3000\u8bf8\u845b\u660e\u63a5\u8fc7\u62dc\u67ec\u4e00\u770b\uff0c\u53ea\u89c1\u67ec\u4e0a\u8d6b\u7136\u5199\u9053\uff1a\u201c\u6b66\u6797\u540e\u5b66\u8f9b\u6377\u3001\u5434\u51cc\u98ce\u4e66\u4e0a\u5251\u795e\u5389\u9e57\u8db3 \u4e0b\uff1a\u8db3\u4e0b\u4ee5\u2018\u5929\u4e0b\u7b2c\u4e00\u5251\u2019\u9886\u8896\u5782\u4e8c\u5341\u8f7d\uff0c\u60f3\u80cc\u5e74\u5929\u7ec5\u7011\u524d\u5355\u5251\u65ad\u9b42\u6388\u9996\uff0c\u4e94\u534e\u5c71\u4e0a\u4e03\u5999\u795e \u541b\u906d\u632b\uff0c\u6b64\u6069\u6b64\u5fb7\uff0c\u5fc5\u5f53\u62a5\u9a70\u3002\u540e\u5b66\u51b3\u4e8e\u6708\u5706\u4e4b\u65f6\u5728\u4e94\u534e\u5c71\u9876\u606d\u5019\u5927\u9a7e\uff0c\u60f3\u9601\u4e0b\u53f7\u79f0\u6b66\u6797\u7b2c \u4e00\u4eba\uff0c\u5fc5\u4e0d\u4ee4\u543e\u7b49\u5931\u671b\u4e5f\u3002 \u3000\u3000\u8f9b\u6377\uff0c\u5434\u51cc\u98ce\u987f\u9996\u3002\u201d \u3000\u3000\u8bf8\u845b\u660e\u5306\u5306\u770b\u5b8c\uff0c\u5bf9\u4e8e\u4e00\u98de\u9053\uff1a\u201c\u8fd9\u5434\u51cc\u98ce\u5373\u662f\u5434\u8bcf\u4e91\u4e4b\u5b50\uff0c\u548c\u5e08\u5c0a\u6709\u4e0d\u5171\u6234\u5929\u4e4b\u4ec7\uff0c \u770b\u6765\u8fd9\u4ef6\u4e8b\u975e\u5f97\u8ba9\u5e08\u5c0a\u81ea\u5df1\u53bb\u659f\u914c\u4e86\u3002\u201d \u3000\u3000\u4e8e\u4e00\u98de\u5374\u9053\uff1a\u201c\u5e08\u7236\u534a\u6708\u6765\u95ed\u5173\uff0c\u4e0d\u77e5\u4f1a\u4e0d\u4f1a\u8d23\u602a\u54b1\u4eec\u53bb\u6253\u6270\u2014\u2014\u201d \u3000\u3000\u8bf8\u845b\u660e\u5fae\u4e00\u6c89\u541f\uff0c\u6447\u5934\u9053\uff1a\u201c\u4e0d\uff0c\u8fd9\u4e8b\u60c5\u592a\u91cd\u8981\u2014\u2014\u201d \u3000\u3000\u539f\u6765\u5251\u795e\u5389\u9e57\u81ea\u6cf0\u5c71\u5927\u4f1a\u53d7\u632b\u4e4b\u540e\uff0c\u5fc3\u7070\u610f\u61d2\uff0c\u96c4\u5fc3\u6d88\u6c89\uff0c\u4f46\u4ed6\u5fc3\u673a\u751a\u6df1\uff0c\u5fc3\u4e2d\u77e5\u9053\u81ea \u5df1\u7ed3\u4e0b\u7684\u5f3a\u4ec7\uff0c\u975e\u53d6\u81ea\u5df1\u6027\u547d\u800c\u540e\u5fc3\u7518\uff0c\u662f\u4ee5\u4e0d\u662f\u4e00\u9690\u53ef\u4e86\u7684\u60c5\u5f62\u3002\u4ed6\u6df1\u77e5\u4ee5\u81ea\u5df1\u81ea\u4e3a\u4e00\u6d3e \u638c\u95e8\uff0c\u540d\u5934\u53c8\u662f\u5982\u6b64\u5927\uff0c\u4e00\u65e6\u5bf9\u65b9\u767b\u95e8\u7d22\u6218\uff0c\u81ea\u5df1\u51b3\u4e0d\u53ef\u80fd\u907f\u800c\u4e0d\u6218\uff0c\u662f\u4ee5\uff0c\u4ed6\u4e3a\u81ea\u5df2\u751f\u547d \u6253\u7b97\uff0c\u51b3\u5fc3\u95ed\u5173\u82e6\u7ec3\u3002\u5927\u51e1\u50cf\u4ed6\u8fd9\u7b49\u9ad8\u624b\uff0c\u60f3\u80fd\u767e\u5c3a\u7aff\u5934\u518d\u8fdb\u4e00\u6b65\uff0c\u90a3\u5fc5\u987b\u8981\u5f97\u5230\u4e9b\u4ec0\u4e48\u6b66 \u5b66\u5bc6\u7b08\u4e4b\u7c7b\uff0c\u7167\u7740\u53c2\u609f\u3002 \u3000\u3000\u5389\u9e57\u6df1\u660e\u6b64\u7406\uff0c\u9996\u5148\uff0c\u4ed6\u98ce\u95fb\u90a3\u5931\u53bb\u7684\u501a\u8679\u5251\u9798\u4e2d\u6709\u4e00\u672c\u53e4\u4eba\u6240\u4f5c\u7684\u201c\u6df7\u5143\u4e09\u7edd\u201d\u7684\u79d8 \u7b3c\uff0c\u4f46\u662f\u655b\u9798\u65e9\u5728\u5341\u4e94\u5e74\u524d\u4e00\u65f6\u5927\u610f\uff0c\u548c\u4e03\u5999\u795e\u541b\u52a8\u624b\u65f6\uff0c\u9057\u5fd8\u5728\u4e94\u534e\u5c71\u7684\u7edd\u9876\u800c\u88ab\u4e10\u5e2e\u62fe \u53bb\uff0c\u5f53\u7136\u4ed6\u5fc3\u6709\u4e0d\u7518\uff0c\u51b3\u5fc3\u5411\u4e10\u5e2e\u7d22\u56de\u3002 \u3000\u3000\u4f46\u662f\u4ed6\u8eab\u4e3a\u4e00\u6d3e\u4e4b\u5c0a\u5c82\u80fd\u5f3a\u62a2\u786c\u593a\uff0c\u81ea\u5df1\u4e0d\u597d\u51fa\u9762\uff0c\u4fbf\u547d\u5f1f\u5b50\u201c\u4e09\u7edd\u5251\u201d\u51fa\u9762\u62a2\u593a\uff0c\u5c82 \u6599\u4e0d\u662f\u91d1\u6c0f\u62a4\u6cd5\u7684\u654c\u624b\u800c\u4e00\u8d25\u6d82\u5730\uff0c\u4e8e\u662f\u4ed6\u53c8\u60f3\u8d77\u6614\u5e74\u7684\u8001\u53cb\u52fe\u6f0f\u4e00\u602a\u7fc1\u6b63\uff0c\u7075\u673a\u4e00\u52a8\uff0c\u7acb \u523b\u8bbe\u6cd5\u5c06\u7fc1\u6b63\u5f15\u51fa\u6df1\u5c71\uff0c\u800c\u4ee3\u4ed6\u53bb\u593a\u5251\u9798\uff0c\u8fd9\u4e00\u5207\u90fd\u662f\u4ed6\u5b89\u6392\u597d\u7684\uff0c\u4ee5\u7fc1\u6b63\u7684\u529f\u592b\uff0c\u6614\u5e74\u548c \u4e03\u5999\u795e\u541b\u529b\u6218\u6570\u767e\u5408\u624d\u8d25\u7684\u4eba\u7269\uff0c\u4e00\u5b9a\u4e0d\u4f1a\u518d\u6709\u5dee\u9519\uff0c\u54ea\u77e5\u65e0\u5de7\u4e0d\u5de7\u9047\u4e0a\u8f9b\u6377\uff0c\u4ece\u4e2d\u5f3a\u884c\u67b6 \u6881\uff0c\u795e\u529f\u51fb\u8d70\u7fc1\u6b63\u3002 \u3000\u3000\u5389\u9e57\u773c\u89c1\u5999\u8ba1\u53c8\u6ce1\u5f71\uff0c\u4e0d\u7531\u5927\u6025\uff0c\u4e0d\u8fc7\u4ed6\u662f\u57ce\u5e9c\u6781\u6df1\u7684\u4eba\uff0c\u81ea\u5fd6\u51fa\u6218\u5fc5\u975e\u8f9b\u6377\u4e4b\u654c\uff0c\u4e8e \u662f\u4e58\u673a\u5077\u53d6\u201c\u6885\u9999\u795e\u5251\u201d\uff0c\u8fdc\u8d70\u5d06\u5cd2\u3002\u4ed6\u6ee1\u4ee5\u4e3a\u81ea\u5df1\u6beb\u65e0\u8ff9\u8c61\u7559\u4e0b\uff0c\u5374\u5ffd\u7565\u4e86\u501a\u8679\u5251\u5728\u5899\u4e0a \u6240\u7559\u7684\u5251\u5b54\uff0c\u81f4\u88ab\u8f9b\u6377\u8bc6\u7834\uff0c\u5343\u91cc\u8d76\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u56de\u5d06\u5cd2\uff0c\u81ea\u77e5\u201c\u6df7\u5143\u4e09\u7edd\u201d\u79d8\u7b08\u4e0d\u80fd\u5230\u624b\uff0c\u54ea\u77e5\u65e0\u610f\u4e4b\u4e2d\u53c8\u53d1\u89c9\u4e86\u4e00\u672c\u5d06\u5cd2\u5931\u4f20\u8fd1 \u767e\u5e74\u7684\u5fc3\u6cd5\uff1a\u201c\u4e0a\u6e05\u6c14\u529f\u201d\u3002 \u3000\u3000\u8fd9\u4e0a\u6e05\u6c14\u529f\u4e43\u662f\u767e\u591a\u5e74\u524d\u5d06\u5cd2\u7edd\u5b66\uff0c\u5f53\u65f6\u5d06\u5cd2\u7b2c\u4e03\u4ee3\u638c\u95e8\u4eba\u4e00\u9752\u9053\u4eba\uff0c\u85c9\u6b64\u5728\u6c5f\u6e56\u4e0a\u5d2d \u9732\u5934\u89d2\uff0c\u4f7f\u5d06\u5cd2\u6d3e\u53d1\u626c\u5149\u5927\uff0c\u800c\u7ec8\u906d\u548c\u5d06\u5cd2\u4e3a\u90bb\u7684\u5927\u51c9\u6d3e\u7684\u5fc3\u5fcc\uff0c\u5f53\u65f6\u5927\u51c9\u7684\u4e03\u4e2a\u9ad8\u624b\u53f7\u79f0 \u5927\u51c9\u4e03\u5947\u4e00\u8d77\u4e0a\u5d06\u5cd2\u5c71\uff0c\u4e00\u9752\u9053\u957f\u5728\u9752\u5143\u89c2\u4e2d\u548c\u4e03\u5947\u4e00\u8a00\u4e0d\u548c\uff0c\u5927\u6253\u51fa\u624b\u3002 \u3000\u3000\u4e00\u9752\u9053\u4eba\u9ed8\u8fd0\u201c\u4e0a\u6e05\u6c14\u529f\u201d\uff0c\u767e\u6b65\u795e\u62f3\u5927\u5c55\u795e\u5a01\uff0c\u4e00\u8fde\u9065\u6253\u4e03\u62f3\uff0c\u5927\u51c9\u4e03\u5947\u6ca1\u6709\u4e00\u4e2a\u63a5 \u5f97\u4e0b\u6765\uff0c\u4e00\u8d77\u8d1f\u4f24\u800c\u9000\uff0c\u4ece\u6b64\u201c\u4e0a\u6e05\u6c14\u529f\u201d\u4e4b\u540d\u5934\u66f4\u662f\u54cd\u4eae\u3002 \u3000\u3000\u54ea\u77e5\u4e00\u9752\u9053\u4eba\u5ffd\u7136\u4e0d\u77e5\u56e0\u4ec0\u4e48\u4e8b\uff0c\u4ece\u6b64\u5728\u6c5f\u6e56\u4e0a\u5931\u8e2a\uff0c\u800c\u4e0a\u6e05\u6c14\u529f\u4e5f\u4ece\u6b64\u7edd\u4f20\uff0c\u800c\u5389\u9e57 \u7adf\u80fd\u4fa5\u5e78\u5f97\u7740\uff0c\u600e\u4e0d\u4ee4\u4ed6\u6b23\u559c\u6b32\u72c2\uff1f \u3000\u3000\u4e8e\u662f\u4ed6\u7acb\u523b\u95ed\u5173\u53c1\u609f\uff0c\u662f\u4ee5\u8f9b\u6377\u3001\u5434\u51cc\u98ce\u4e8c\u4eba\u4e0a\u5c71\u65f6\u5e76\u672a\u89c1\u5f97\u4ed6\u7684\u4eba\u5f71\uff0c\u4fbf\u662f\u8fd9\u4e2a\u539f \u6545\u3002 \u3000\u3000\u4ed6\u5728\u95ed\u5173\u4e4b\u671f\uff0c\u4e25\u7981\u95f2\u4eba\u6253\u6270\uff0c\u662f\u4ee5\u4e8e\u4e00\u98de\u4e0d\u6562\u628a\u62dc\u67ec\u7684\u4e8b\u4f20\u7ed9\u4ed6\u542c\uff0c\u4e5f\u5c31\u662f\u6015\u6253\u6270 \u4ed6\u3002\u5012\u662f\u8bf8\u845b\u660e\u8ba4\u4e3a\u4e8b\u6001\u4e25\u91cd\uff0c\u7ec8\u4e8e\u4e0a\u5c71\u53bb\u62a5\u544a\u5389\u9e57\u3002 \u3000\u3000\u5c71\u9876\u4e0a\u5927\u96ea\u65b9\u6b62\uff0c\u9634\u9634\u953a\u953a\uff0c\u8fd9\u540d\u5c71\u6b63\u6d3e\u4e2d\uff0c\u4e00\u7247\u548c\u7a46\u4e4b\u50cf\uff0c\u8c01\u53c8\u4f1a\u6599\u5230\u4f1a\u6709\u8840\u8165\u4e4b\u707e \u5c06\u8981\u964d\u4e34\u5728\u638c\u95e8\u4eba\u6b66\u6797\u7b2c\u4e00\u5251\u4e4b\u8eab\uff1f\u2026\u2026 \u3000\u3000\u51e0\u4e4e\u5728\u540c\u4e00\u4e2a\u6708\u4efd\u91cc\uff0c\u8bf4\u5148\u540e\u5dee\u522b\u4e5f\u53ea\u6709\u4e94\u516d\u5929\u5de6\u53f3\uff0c\u7384\u95e8\u6b63\u6d3e\u7cfb\u7684\u6b66\u5f53\u5c71\u4e0a\uff0c\u4e5f\u63a5\u5230 \u4e86\u4e00\u5f0f\u4e00\u6837\u7684\u8bf7\u5e16\u3002\u53ea\u662f\u6536\u542f\u4eba\u7684\u59d3\u540d\u6539\u53d8\u4e86\u800c\u5df2\u3002 \u3000\u3000\u800c\u4e14\uff0c\u8fd9\u62dc\u67ec\u662f\u76f4\u63a5\u6295\u9001\u7ed9\u638c\u95e8\u4eba\u8d64\u9633\u9053\u957f\u7684\u624b\u4e2d\uff0c\u9c9c\u7ea2\u7684\u5c01\uff0c\u523a\u76ee\u7684\u8bed\u53e5\uff0c\u4f7f\u8d1f\u4f24\u5c1a \u672b\u6108\u7684\u8d64\u9633\u9053\u957f\u76ca\u53d1\u611f\u5230\u5fc3\u7126\uff0c\u5185\u5fc3\u7684\u7d27\u5f20\uff0c\u6162\u6027\u6bd2\u7d20\u714e\u903c\u7740\u4ed6\u3002 \u3000\u3000\u8981\u77e5\u8d64\u9633\u9053\u957f\u8eab\u867d\u4eba\u7384\u95e8\uff0c\u4f46\u4e3a\u4eba\u4e0d\u7aef\uff0c\u5230\u5934\u6765\u62a5\u5e94\u4ecd\u7136\u5149\u4e34\uff0c\u4ed6\u4e5f\u660e\u77e5\u4e0d\u662f\u5bf9\u624b\uff0c\u4f46 \u662f\u4eba\u5bb6\u4e0b\u4e66\u7d22\u6218\uff0c\u81ea\u5df1\u4ee5\u638c\u95e8\u4eba\u7684\u8eab\u4efd\u5c82\u80fd\u4e0d\u5e94\u6218\uff1f \u3000\u3000\u5f3a\u5f31\u60ac\u6b8a\uff0c\u4ee5\u5df1\u4e4b\u529b\uff0c\u53bb\u548c\u8f9b\u6377\u8f83\u91cf\uff0c\u4e0d\u5f02\u4ee5\u5375\u51fb\u767e\uff0c\u4ed6\u81ea\u5185\u5fc3\u6df1\u5904\u518d\u4e5f\u627e\u4e0d\u51fa\u4e00\u4e1d\u672a \u6cef\u7684\u96c4\u5fc3\uff0c\u6240\u80fd\u627e\u51fa\u6765\u7684\uff0c\u90a3\u4e0d\u8fc7\u53ea\u662f\u540e\u6094\uff0c\u90a3\u662f\u4e3a\u65f6\u5df2\u8fdf\u7684\u4e86\u3002 \u3000\u3000\u4ed6\u4e0d\u65f6\u629a\u7740\u706b\u7ea2\u7684\u62dc\u67ec\uff0c\u6d69\u58f0\u957f\u53f9\uff0c\u6614\u5e74\uff0c\u5509\uff0c\u90a3\u5df1\u662f\u8fc7\u53bb\u7684\u4e8b\u4e86\u2026\u2026 \u3000\u3000\u8700\u9053\u4e0a\uff0c\u9686\u51ac\u65f6\u5206\u2026\u2026 \u3000\u3000\u8700\u7701\u3002\u957f\u6c5f\u4e0b\u6e38\uff0c\u6709\u4e00\u6761\u6885\u6eaa\uff0c\u4ece\u5c71\u8c37\u6d41\u7ecf\u4e00\u4e2a\u5927\u576a\uff0c\u8fd9\u5c31\u662f\u6c99\u9f99\u576a\u4e86\uff0c\u576a\u4e0a\u7a00\u843d\u6751 \u820d\uff0c\u9e21\u72ac\u76f8\u95fb\uff0c\u662f\u4e2a\u4e16\u5916\u6843\u6e90\u3002 \u3000\u3000\u8fd9\u6c99\u9f99\u576a\u65b9\u5706\u6728\u5c0f\u4e5f\u4e0d\u8fc7\u624d\u4ec5\u4ec5\u4e00\u91cc\u6709\u5947\uff0c\u4f46\u5374\u662f\u4e00\u6761\u6885\u6eaa\u6240\u6d41\u7ecf\uff0c\u6709\u4e00\u4e2a\u7279\u522b\u7684\u602a \u5904\uff0c\u90a3\u4fbf\u662f\u6eaa\u8fb9\u5939\u5cb8\u6570\u91cc\u5185\uff0c\u5168\u662f\u7ea2\u767d\u53e4\u6885\uff0c\u4e2d\u65e0\u6742\u6811\u3002 \u3000\u3000\u65f6\u6b63\u9686\u51ac\uff0c\u5bd2\u98ce\u9f13\u7740\u545c\u545c\u58f0\u54cd\u3002\u5929\u6c14\u6108\u51b7\uff0c\u6885\u513f\u662f\u633a\u5cf0\u800c\u7acb\uff0c\u8273\u5c55\u4e3d\u5bb9\uff0c\u548c\u5bd2\u51b7\u6297\u62d2\u3002 \u3000\u3000\u9053\u4e0a\u5927\u98ce\u5439\u5f97\u7d27\uff0c\u628a\u6f2b\u5929\u98de\u821e\u7684\u96ea\u82b1\u659c\u659c\u7684\u5439\u6563\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u70b9\u70b9\u767d\u96ea\u548c\u6735\u6735\u6885\u82b1\u76f8\u6620 \u6210\u8da3\uff0c\u851a\u4e3a\u5947\u89c2\uff0c\u597d\u4e00\u7247\u666f\u8272\uff01\u7edd\u65e9\uff0c\u5929\u8272\u9634\u973e\u65e0\u5149\uff0c\u770b\u90a3\u6a21\u6837\uff0c\u6d3b\u50cf\u662f\u8981\u518d\u843d\u4e0b\u66f4\u5927\u7684\u96ea \u82b1\u4f3c\u7684\u3002\u5b98\u9053\u4e0a\u9759\u6781\u4e86\uff0c\u4f60\u51e0\u4e4e\u53ef\u4ee5\u7ad9\u5728\u8fd9\u91cc\uff0c\u6e05\u6e05\u695a\u695a\u7684\u4e00\u76f4\u671b\u5230\u90a3\u4e00\u5934\uff0c\u800c\u4e0d\u53d1\u73b0\u4e00\u4e2a \u4eba\u5f71\u3002 \u3000\u3000\u9635\u9635\u5bd2\u98ce\u628a\u6885\u82b1\u7684\u6e05\u9999\u9001\u6765\uff0c\u8361\u6f3e\u5728\u7a7a\u6c14\u4e2d\uff0c\u518d\u52a0\u5468\u906d\u662f\u5982\u6b64\u5bc2\u9759\uff0c\u662f\u4ee5\u6c14\u6c1b\u663e\u5f97\u6c89 \u5bc2\u3002 \u3000\u3000\u84e6\u7136\uff0c\u8fdc\u65b9\u7684\u98ce\u628a\u4e00\u9635\u8584\u8584\u7684\u671d\u96fe\u5439\u6563\uff0c\u5728\u8def\u7684\u5c3d\u5934\u5904\u73b0\u51fa\u4e8c\u4e2a\u75be\u75be\u884c\u8d70\u7740\u7684\u4eba\u5f71\u3002 \u3000\u3000\u662f\u8c01\u4f1a\u5728\u7edd\u65e9\u65f6\u5206\u75be\u75be\u5954\u8def\uff1f \u3000\u3000\u6e10\u6e10\u7684\uff0c\u6765\u5f97\u8fd1\u4e86\uff0c\u4f4e\u6c89\u7684\u53ef\u4ee5\u9690\u7ea6\u542c\u89c1\u811a\u6b65\u58f0\u3002 \u3000\u3000\u7a81\u7136\uff0c\u9053\u8def\u53f3\u8fb9\u4e00\u95f4\u5e73\u5c4b\u7684\u7af9\u6249\u201c\u5440\u201d\u7136\u6253\u5f00\uff0c\u8d70\u51fa\u4e00\u4e2a\u5e74\u7ea6\u53e4\u7a00\u7684\u8001\u4eba\u3002\u4f46\u89c1\u4ed6\u767d\u81c2 \u98d8\u98d8\uff0c\u5934\u53d1\u51e0\u4e4e\u843d\u5f97\u5149\u79c3\uff0c\u8138\u4e0a\u76b1\u7eb9\u5bc6\u5e03\uff0c\u663e\u5f97\u5f02\u5e38\u82cd\u8001\uff0c\u4f46\u6295\u8db3\u4e4b\u95f4\uff0c\u5374\u6d41\u9732\u51fa\u4e00\u80a1\u4ee4\u4eba \u5fc3\u6298\u7684\u5a01\u6b66\u3002 \u3000\u3000\u8001\u4eba\u5bb6\u4eff\u4f5b\u662f\u542c\u5230\u4eba\u58f0\uff0c\u5f00\u4e86\u7af9\u6249\uff0c\u4fbf\u5411\u8def\u5934\u8fdc\u65b9\u773a\u671b\u8fc7\u53bb\uff0c\u679c\u7136\uff0c\u5728\u8584\u8584\u7684\u96fe\u6c14\u4e2d\uff0c \u51fa\u73b0\u4e24\u4e2a\u4eba\u5f71\uff0c\u597d\u5feb\u7684\u811a\u7a0b\uff0c\u4e0d\u6d88\u51e0\u7a0b\uff0c\u5df2\u7136\u63a5\u8fd1\u3002 \u3000\u3000\u522b\u770b\u8001\u4eba\u9f99\u949f\u4e4b\u6001\u8868\u9732\u65e0\u9057\uff0c\u76ee\u4e4b\u5229\uff0c\u5374\u6709\u5982\u9e70\u96bc\uff0c\u95ea\u773c\u4e00\u77a5\uff0c\u5df2\u7136\u770b\u6e05\u3002 \u3000\u3000\u6765\u4eba\u4e0d\u6d88\u6570\u70b9\uff0c\u6765\u5230\u95e8\u524d\u3002 \u3000\u3000\u8001\u4eba\u6b22\u58f0\u53eb\u9053\uff1a\u201c\u6377\u513f\u2014\u2014\u201d \u3000\u3000\u4e24\u4e2a\u8d76\u8def\u7684\u4eba\u6765\u95e8\u524d\uff0c\u4e00\u8d77\u62dc\u5728\u5730\u4e0b\u3002 \u3000\u3000\u6668\u5149\u4e4b\u4e2d\uff0c\u6e05\u695a\u7684\u6620\u51fa\u4e24\u4eba\u7684\u9762\u5b54\uff0c\u5e74\u9f84\u5747\u4e3a\u4e8c\u5341\u5c81\u4e0a\u4e0b\uff0c\u82f1\u6c14\u6bd5\u9732\uff0c\u4fca\u4fcf\u65e0\u6bd4\u3002 \u3000\u3000\u4e24\u4eba\u540c\u662f\u4e00\u88ad\u9752\u886b\uff0c\u6de1\u7136\u7684\u989c\u8272\uff0c\u76ca\u53d1\u886c\u6258\u51fa\u4e24\u4eba\u4e0d\u51e1\u7684\u4eea\u8868\uff0c\u5c24\u5176\u662f\u540e\u9762\u4e00\u4eba\uff0c\u66f4\u662f \u82f1\u5149\u7167\u4eba\uff0c\u957f\u659c\u659c\u7684\u5251\u63d2\u5728\u540e\u80a9\u4e0a\uff0c\u9ec4\u8272\u7684\u5251\u7a57\u5de6\u53f3\u98d8\u8361\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u8d77\u62dc\u5012\u5730\u4e0a\uff0c\u4e00\u540c\u9ad8\u547c\u9053\uff1a\u201c\u6885\u53d4\u53d4\u2026\u2026\u201d \u3000\u3000\u6562\u60c5\u8fd9\u8001\u4eba\u6b63\u662f\u4e8c\u5341\u8f7d\u524d\u540d\u9707\u795e\u5dde\u7684\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u3002\u800c\u8fd9\u4e24\u4e2a\u82f1\u4fca\u7684\u5e74\u9752\u4eba\uff0c\u6b63\u662f\u6885 \u5c71\u6c11\u548c\u5434\u8bcf\u4e91\u7684\u540e\u4eba\u2014\u2014\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u4e24\u4eba\u3002 \u3000\u3000\u6885\u5c71\u6c11\u54c8\u54c8\u9053\uff1a\u201c\u5feb\u8d77\u6765\uff0c\u6377\u513f\uff0c\u8fd9\u4f4d\u4e00\u5b9a\u4fbf\u662f\u5434\u8d24\u4f84\u7f62\u3002\u201d \u3000\u3000\u8f9b\u3001\u5434\u4e24\u4eba\u7ad9\u8d77\uff0c\u5434\u51cc\u98ce\u8fde\u58f0\u5e94\u662f\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5475\u5475\u5927\u7b11\uff0c\u9053\uff1a\u201c\u54c8\u54c8\uff0c\u6545\u4eba\u5b50\u55e3\u65e0\u6059\uff0c\u53c8\u662f\u5982\u6b64\u4eba\u624d\u51fa\u4f17\uff0c\u5434\u8d24\u5f1f\u82f1\u7075\u4e5d\u6cc9\u4e4b \u4e0b\u53ef\u4ee5\u5b89\u6170\u4e86\uff0c\u4f60\u4eec\u60f3\u6765\u8fd8\u6ca1\u6709\u5403\u8fc7\u65e9\u996d\u5427\uff1f\u522b\u518d\u5446\u5728\u98ce\u96ea\u4e2d\u4e86\uff0c\u5feb\u5feb\u8fdb\u5c4b\u91cc\u2026\u2026\u201d \u3000\u3000\u8bf4\u7740\u5f53\u5148\u8fdb\u5165\u5c4b\u4e2d\uff0c\u8f9b\u3001\u5434\u4e24\u4eba\u4e5f\u9c7c\u8d2f\u5165\u5185\u3002 \u3000\u3000\u5434\u51cc\u98ce\u81ea\u5e7c\u4e27\u5931\u53cc\u4eb2\uff0c\u4e00\u751f\u547d\u8fd0\u574e\u5777\uff0c\u82e6\u95f7\u65f6\u4ece\u6765\u6ca1\u6709\u4eba\u53bb\u5b89\u6170\uff0c\u53ea\u662f\u81ea\u5df1\u53d1\u6cc4\u800c\u5df2\uff0c \u8fd8\u56e0\u4ed6\u751f\u6027\u79c9\u5584\uff0c\u5b64\u82e6\u751f\u6d3b\uff0c\u5e76\u6ca1\u6709\u517b\u6210\u538c\u4e16\u4e4b\u611f\uff0c\u53ea\u662f\u8ffd\u6eaf\u6839\u6e90\uff0c\u6068\u6781\u90a3\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba \u800c\u5df1\u3002 \u3000\u3000\u4f46\u81ea\u4ed6\u4e0b\u5c71\u4ee5\u540e\uff0c\u9996\u9047\u8f9b\u6377\uff0c\u867d\u7136\u662f\u4e00\u4e2a\u653e\u8361\u4e0d\u7f81\u5c0f\u8282\u7684\u4eba\uff0c\u4f46\u4fa0\u80c6\u5929\u751f\uff0c\u548c\u4ed6\u751a\u662f\u4f55 \u5df1\uff0c\u4e24\u5e74\u4ee5\u6765\uff0c\u79bb\u591a\u4f1a\u5c0f\uff0c\u6bcf\u5f53\u4ed6\u5fc3\u4e8b\u6ee1\u6000\u4e4b\u9645\uff0c\u8f9b\u6377\u603b\u662f\u7528\u58ee\u5fd7\u8c6a\u5174\u6765\u5f00\u5bfc\u4ed6\uff0c\u4ece\u6765\u6ca1\u6709 \u6e29\u60c5\u5b89\u6170\u3002 \u3000\u3000\u7136\u800c\uff0c\u8fd9\u65f6\u4ed6\u89c1\u5fc3\u4eea\u5df2\u4e45\u7684\u6885\u53d4\u53d4\uff0c\u5e76\u4e0d\u50cf\u6c5f\u6e56\u4e0a\u4f20\u8bf4\u7684\u90a3\u6837\u51b7\u9177\uff0c\u800c\u4e14\u548c\u853c\u53ef\u4eb2\u4e4b \u6781\u3002 \u3000\u3000\u867d\u7136\uff0c\u89c1\u9762\u65f6\u53d4\u53d4\u4ec5\u8bf4\u4e86\u4e00\u4e24\u53e5\u8bdd\uff0c\u4f46\u5173\u6000\u4e4b\u60c5\uff0c\u679c\u7136\u6d41\u9732\uff0c\u4f7f\u4ed6\u89c9\u53d4\u53d4\u4eb2\u5207\u53ef\u7231\uff0c\u5fc3 \u4e2d\u751a\u662f\u611f\u52a8\uff0c\u5fc3\u4e2d\u57cb\u85cf\u7684\u611f\u60c5\u6292\u53d1\uff0c\u5fc3\u60c5\u6fc0\u52a8\u4e4b\u81f3\uff0c\u4e0d\u7531\u70ed\u6cea\u6ee1\u7736\u3002 \u3000\u3000\u6885\u5c71\u6c11\u6e05\u695a\u4ed6\u7684\u5fc3\u60c5\uff0c\u5fae\u5fae\u4e00\u7b11\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u6b64\u884c\u4ece\u4f55\u800c\u6765\uff1f\u770b\u6837\u5b50\u597d\u50cf\u5954\u6ce2\u4e0d\u5c11\u65f6 \u5019\uff0c\u4ee5\u8001\u673d\u770b\u6765\uff0c\u81f3\u5c11\u4e5f\u8d76\u4e86\u56db\u4e94\u767e\u91cc\u8def\u7a0b\uff01\u201d \u3000\u3000\u8f9b\u6377\u77e5\u4ed6\u662f\u5728\u6fc0\u8d77\u5434\u51cc\u98ce\u7684\u58ee\u5fd7\uff0c\u8d76\u5fd9\u56de\u7b54\u9053\uff1a\u201c\u54b1\u4eec\u6b63\u662f\u7531\u6b66\u5f53\u5c71\u8d76\u6765\u7684\u5462\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u4fbf\u628a\u548c\u6885\u53d4\u53d4\u522b\u540e\u7684\u7ecf\u8fc7\u9053\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5434\u51cc\u98ce\u679c\u7136\u63d0\u8d77\u5174\u8da3\uff0c\u4e0d\u65f6\u8865\u8ff0\u4e00\u4e24\u70b9\u8f9b\u6377\u9057\u6f0f\u7684\u5730\u65b9\u3002 \u3000\u3000\u5f53\u6885\u5c71\u6c11\u542c\u95fb\u201c\u6885\u9999\u5251\u201d\u88ab\u5251\u795e\u5389\u9e57\u7a83\u53bb\u65f6\uff0c\u4e0d\u7531\u5927\u6012\uff0c\u5927\u9a82\u5389\u9e57\u65e0\u803b\u3002\u4f46\u542c\u5230\u201c\u65e0\u4e3a \u5385\u201d\u4e0a\u8f9b\u6377\u5927\u65bd\u795e\u5a01\u65f6\uff0c\u5374\u662f\u8fde\u8fde\u70b9\u5934\u5609\u8bb8\u4e0d\u5df2\u3002 \u3000\u3000\u539f\u6765\u8fd9\u4e2a\u6d88\u606f\u4e5f\u65e9\u5728\u6c5f\u6e56\u4e0a\u4f20\u904d\u4e86\uff0c\u201c\u6885\u9999\u795e\u5251\u201d\u7684\u540d\u5934\u66f4\u662f\u5927\u5927\u53d1\u626c\uff0c\u4e03\u5999\u795e\u541b\u4e00\u751f \u597d\u80dc\uff0c\u5982\u4eca\u5f97\u6709\u6b64\u7b49\u4f20\u4eba\uff0c\u4e5f\u81ea\u751a\u662f\u5b89\u6170\u3002 \u3000\u3000\u5f53\u8f9b\u6377\u8f6c\u8ff0\u5230\u5c0f\u6222\u5c9b\u4e0a\uff0c\u534e\u5937\u4e4b\u4e89\uff0c\u4e1c\u6d77\u7684\u4e16\u5916\u4e09\u4ed9\u548c\u5316\u5916\u4e4b\u6c11\u6052\u6cb3\u4e09\u4f5b\u4f5c\u4e00\u573a\u540d\u5934\u4e4b \u4e89\u7684\u5927\u6218\uff0c\u548c\u65e0\u6068\u751f\u6bd2\u4f24\u7b49\u7b49\u5947\u4e4b\u53c8\u5947\u7684\u906d\u9047\u65f6\uff0c\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u4e0d\u7531\u5927\u5927\u60ca\u5f02\u3002\u4ee5\u4ed6\u5f53\u5e74 \u7684\u7ecf\u5386\uff0c\u59cb\u7ec8\u4e0d\u95fb\u5929\u7afa\u7adf\u6709\u6b64\u7b49\u9ad8\u624b\uff0c\u53e3\u4e2d\u8f7b\u547c\u201c\u6052\u6cb3\u4e09\u4f5b\u201d\u4e0d\u5df2\u3002 \u3000\u3000\u8bb2\u4e86\u8fd9\u6837\u591a\uff0c\u518d\u52a0\u4e0a\u7528\u8fc7\u65e9\u996d\uff0c\u5df2\u662f\u5feb\u5230\u5348\u95f4\u4e86\u3002 \u3000\u3000\u6885\u5c71\u6c11\u7b11\u772f\u772f\u7684\u62c8\u987b\u770b\u7740\u4e24\u4e2a\u53ef\u7231\u7684\u5b69\u5b50\uff0c\u5fc3\u4e2d\u90a3\u4efd\u5f97\u610f\u518d\u4e0d\u7528\u8bf4\uff0c\u534a\u54cd\uff0c\u624d\u60f3\u8d77\u6765\u95ee \u9053\uff1a\u201c\u5434\u8d24\u4f84\uff0c\u4f60\u6700\u8fd1\u4e5f\u662f\u8fed\u6709\u901a\u5408\uff0c\u5c24\u5176\u90a3\u68b5\u6587\u6240\u8f7d\u7684\u8f7b\u529f\uff0c\u5fc5\u5b9a\u662f\u9ad8\u660e\u65e0\u6bd4\u7684\u4e86\uff0c\u4f60\u4e14 \u65bd\u5c55\u51fa\u6765\u89c1\u8bc6\u89c1\u8bc6\uff1f\u201d \u3000\u3000\u5434\u51cc\u98ce\u5e94\u8bfa\u4e00\u58f0\uff0c\u7ad9\u8d77\u6765\uff0c\u8d70\u5230\u95e8\u5916\u5e9f\u576a\u4e0a\uff0c\u4ed6\u81ea\u5728\u5927\u6222\u5c9b\u4e0a\u88ab\u5e73\u51e1\u4e0a\u4eba\u89e3\u91ca\u6e05\u695a\u90a3\u4e9b \u68b5\u6587\uff0c\u65e5\u591c\u82e6\u82e6\u7ec3\u4e60\u8fd9\u79cd\u8eab\u6cd5\uff0c\u6210\u5c31\u751a\u5927\uff0c\u7ad9\u7acb\u8eab\u5b50\uff0c\u731b\u7136\u4e00\u63d0\u4e2d\u6c14\uff0c\u5237\u5730\u98de\u7a9c\u51fa\u53bb\u3002\u53ea\u89c1 \u8fd9\u5929\u751f\u7684\u8eab\u6cd5\u679c\u7136\u53e4\u602a\uff0c\u51cc\u98ce\u53cc\u8db3\u79bb\u5730\u4ec5\u6709\u534a\u5c3a\uff0c\u7b49\u4e8e\u8d34\u7740\u5730\u76ae\u800c\u98de\u884c\uff0c\u4f46\u901f\u5ea6\u4e4b\u5feb\uff0c\u4ee4\u4eba \u80fd\u4ee5\u7f6e\u4fe1\uff0c\u8863\u8882\u5fae\u6446\u5904\uff0c\u8eab\u4f53\u5df2\u7136\u843d\u5728\u4e03\u516b\u4e08\u5f00\u5916\u3002 \u3000\u3000\u6885\u5c71\u6c11\u4ed4\u7ec6\u89c2\u770b\uff0c\u4f46\u89c9\u8fd9\u79cd\u8eab\u6cd5\u7684\u901f\u5ea6\u7b80\u76f4\u4e0d\u53ef\u601d\u8bae\uff0c\u4e0d\u7531\u4f4e\u543c\u51fa\u4e00\u58f0\u201c\u597d\u201d\u5b57\uff01 \u3000\u3000\u987b\u548c\u4e03\u5999\u795e\u541b\u4e00\u751f\u529f\u592b\uff0c\u5728\u8f7b\u529f\u4e0a\u6210\u5c31\u4e5f\u662f\u751a\u5927\uff0c\u81ea\u521b\u201c\u6697\u9999\u63a0\u5f71\u201d\u8eab\u6cd5\uff0c\u4fbf\u662f\u6b66\u6797\u4e00 \u7edd\uff0c\u4f46\u4eca\u65e5\u548c\u8fd9\u5929\u7afa\u8eab\u6cd5\u6bd4\u8f83\u8d77\u6765\uff0c\u901f\u5ea6\u4e0a\u4fbf\u662f\u4e0d\u5982\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u5fae\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u201c\u5f53\u4eca\u5929\u4e0b\uff0c\u8f7b\u529f\u8eab\u6cd5\u5f53\u63a8\u6167\u5927\u5e08\u7684\u2018\u8bd8\u6469\u795e\u6b65\u2019\u6700\u4e3a\u795e \u5999\uff0c\u4f46\u8bba\u8d77\u901f\u5ea6\uff0c\u6050\u4ecd\u4e0d\u53ca\u8fd9\u79cd\u5929\u7afa\u8eab\u6cd5\uff01\u201d \u3000\u3000\u8f9b\u6377\u3001\u5434\u51cc\u98ce\u4e00\u8d77\u70b9\u9996\uff0c\u5f53\u65e5\u5e73\u51e1\u4e0a\u4eba\u4e5f\u5c31\u662f\u5982\u6b64\u8bf4\u8fc7\u3002 \u3000\u3000\u6885\u5c71\u6c11\u53c8\u9053\uff1a\u201c\u65e9\u5e74\u8001\u673d\u95ef\u8361\u6c5f\u6e56\u65f6\uff0c\u4e5f\u66fe\u98ce\u95fb\u5929\u7afa\u6709\u4e00\u79cd\u65c1\u95e8\u7684\u6b66\u5b66\uff0c\u4f46\u90fd\u59cb\u7ec8\u6ca1\u6709 \u4f20\u5230\u4e2d\u571f\uff0c\u770b\u6765\u679c\u662f\u6240\u4f20\u4e0d\u865a\u4e86\u3002\u201d \u3000\u3000\u4e09\u4eba\u53c8\u8c08\u4e86\u597d\u4e00\u4f1a\uff0c\u8f9b\u6377\u8bf4\u9053\uff1a\u201c\u6211\u548c\u5927\u54e5\u8fd9\u4e00\u6b21\u8d76\u6765\u662f\u60f3\u8bf7\u6885\u53d4\u53d4\u4f5c\u4e3b\u2026\u2026\u201d \u3000\u3000\u6885\u5c71\u6c11\u4e00\u6014\uff0c\u968f\u5373\u4f1a\u610f\u9053\uff1a\u201c\u5f88\u597d\uff01\u4f60\u4eec\u4e5f\u771f\u4e0d\u5fd8\u8001\u4e00\u8f88\u7684\u6559\u8bf2\uff0c\u8fd9\u4e00\u6bb5\u5341\u4f59\u5e74\u7684\u516c \u6848\uff0c \u3000\u3000\u6211\u60f3\u4e5f\u5e94\u8be5\u6709\u4e00\u4e2a\u4e86\u65ad\u4e86\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u63d2\u53e3\u9053\uff1a\u201c\u4e00\u8def\u6765\u5df2\u5728\u5d06\u5cd2\u3001\u6b66\u5f53\u4e24\u5904\u6295\u4e0b\u62dc\u67ec\uff0c\u9080\u7ea6\u4ed6\u4eec\u5728\u6708\u5706\u4e4b\u65e5\u5728\u4e94\u534e\u5c71\u4e0a \u4e00\u4f1a\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u5fae\u5fae\u9894\u9996\uff0c\u4e0d\u4f5c\u4e00\u8a00\u3002 \u3000\u3000\u8f9b\u6377\u53c8\u9053\uff1a\u201c\u6b64\u53bb\u5ce8\u5a9a\u4e0d\u8fdc\uff0c\u4eca\u65e5\u5c31\u53bb\u8d70\u4e00\u906d\uff0c\u53bb\u7ed9\u82e6\u5eb5\u4e0a\u4eba\u4e5f\u4e0b\u4e00\u5f20\u62dc\u5e16\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u60f3\u662f\u5fc3\u4e2d\u751a\u662f\u6fc0\u52a8\uff0c\u4e5f\u4e0d\u7b54\u8a00\uff0c\u4ec5\u9894\u9996\u793a\u610f\u3002 \u3000\u3000\u4e8b\u4e0d\u5b9c\u8fdf\uff0c\u5f53\u5929\u8f9b\u6377\u3001\u5434\u51cc\u98ce\u4fbf\u91cd\u8e0f\u5f81\u9014\uff0c\u8d76\u5230\u5ce8\u5d4b\u5c71\u53bb\u6295\u53d1\u62dc\u67ec\u3002\u81ea\u7136\uff0c\u4ee5\u4ed6\u4eec\u7684\u8f7b \u529f\uff0c\u4efb\u5ce8\u5d4b\u5c71\u4e0a\uff0c\u4e09\u6e05\u9053\u89c2\u4e2d\u9ad8\u624b\u5982\u4e91\uff0c\u4ed6\u4eec\u4ecd\u662f\u51fa\u8fdb\u81ea\u5982\u3002 \u3000\u3000\u70b9\u82cd\u8ddd\u6b64\u592a\u8fdc\uff0c\u4ed6\u4eec\u4e0d\u80fd\u518d\u8d76\u53bb\uff0c\u53cd\u6b63\u843d\u82f1\u5251\u8c22\u957f\u537f\u7684\u5185\u5fc3\u4e5f\u662f\u5f88\u77db\u76fe\u7684\uff0c\u8f9b\u6377\u5bf9\u4ed6\u751a \u5177\u597d\u611f\uff0c\u800c\u4e14\u5d06\u5cd2\u7684\u5251\u795e\u5389\u9e57\u4e5f\u7edd\u5bf9\u4f1a\u53bb\u9080\u8bf7\u4ed6\uff0c\u4e0d\u518d\u9ebb\u70e6\u4e00\u6b21\u4e86\u3002 \u3000\u3000\u6765\u6765\u56de\u56de\uff0c\u53c8\u8d39\u53bb\u4e00\u5929\u529f\u592b\uff0c\u8ba1\u7b97\u65e5\u5b50\uff0c\u4e00\u4e24\u5929\u4fbf\u5f97\u542f\u7a0b\uff0c\u4e24\u4eba\u96c7\u4e86\u4e00\u8f86\u751a\u5bbd\u655e\u7684\u9a6c \u8f66\uff0c\u8ba9\u6885\u53d4\u53d4\u5750\u4e0a\uff0c\u4e00\u8d77\u5954\u5411\u4e94\u534e\u5c71\u3002 \u3000\u3000\u4e94\u534e\u5c71\u8ddd\u6b64\u4e5f\u4e0d\u592a\u8fdc\uff0c\u4e09\u4eba\u4e00\u8def\u884c\u8d70\uff0c\u4e00\u8def\u6b23\u8d4f\u6cbf\u9014\u666f\u8272\uff0c\u6b63\u503c\u51ac\u65e5\uff0c\u904d\u5730\u767d\u96ea\uff0c\u867d\u7136 \u4e94\u534e\u5c71\u4f4d\u4e8e\u5357\u90e8\uff0c\u4f46\u4e00\u8def\u6240\u7ecf\u4e91\u8d35\u9ad8\u539f\uff0c\u5730\u52bf\u8f83\u9ad8\uff0c\u662f\u4ee5\uff0c\u5927\u96ea\u4ecd\u662f\u7eb7\u7eb7\u98de\u821e\u3002 \u3000\u3000\u4e09\u4eba\u90fd\u662f\u6000\u7740\u4e00\u6837\u7684\u5fc3\u7406\uff0c\u5927\u4ec7\u8f6c\u773c\u5373\u53ef\u62a5\u5374\uff0c\u5fc3\u4e2d\u90fd\u662f\u53c8\u6b22\u559c\u53c8\u6168\u7136\uff0c\u4f46\u4e24\u4e2a\u9752\u5e74\u4eba \u7684\u8c6a\u6c14\uff0c\u5374\u662f\u9ad8\u4e0d\u53ef\u6291\uff0c\u4f46\u95fb\u9a6c\u8e44\u7684\u7684\uff0c\u9a7e\u94c3\u6447\u8361\u5904\uff0c\u4e00\u884c\u4eba\u5306\u5306\u4fbf\u8fc7\u2014\u2014 \u3000\u3000\u70b9\u82cd\u5c71\u8109\u4e0a\u3002 \u3000\u3000\u4e00\u4e2a\u4e2d\u5e74\u7684\u6587\u58eb\uff0c\u7ad9\u5728\u5c71\u5d16\u7edd\u9876\uff0c\u8d1f\u624b\u800c\u7acb\u3002 \u3000\u3000\u770b\u6765\u8fd9\u4e2d\u5e74\u7684\u6587\u58eb\u6ee1\u6000\u5fc3\u4e8b\uff0c\u6d69\u7136\u957f\u53f9\uff0c\u4f46\u89c1\u53f3\u624b\u6267\u7740\u4e00\u65b9\u9ec4\u7eeb\uff0c\u53cd\u590d\u628a\u5f04\u4e0d\u5df2\u3002\u4ed6\u6b63 \u662f\u70b9\u82cd\u7684\u638c\u95e8\u4eba\u843d\u82f1\u5251\u5ba2\u8c22\u957f\u537f\u3002 \u3000\u3000\u5929\u5149\u4e0b\uff0c\u76ca\u53d1\u663e\u5f97\u9ec4\u5149\u6d41\u8f6c\uff0c\u4f46\u89c1\u7f0e\u4e0a\u7528\u9ed1\u7ebf\u7ee3\u4e86\u7aef\u7aef\u6b63\u6b63\u7684\u4e94\u4e2a\u5b57\uff1a\u201c\u4e94\u5251\u9707\u4e2d \u539f\u201d\u3002 \u3000\u3000\u6628\u591c\u91cc\uff0c\u5389\u9e57\u7528\u4e5d\u5339\u5feb\u9a6c\u9001\u6765\u8fd9\u9762\u4ee4\u65d7\uff0c\u8c22\u957f\u537f\u77e5\u9053\u4e0a\u4e00\u8f88\u7684\u590d\u6742\u7684\u6069\u4ec7\u5c06\u8981\u5728\u8fd9\u4e00\u6b21 \u7ed3\u675f\u4e86\u3002 \u3000\u3000\u5341\u591a\u5e74\u524d\uff0c\u4e00\u5ff5\u4e4b\u5dee\uff0c\u4f5c\u9519\u7684\u4e8b\uff0c\u5230\u4eca\u65e5\u4ecd\u7136\u6709\u82e5\u6bd2\u86c7\u4e00\u822c\u541e\u566c\u7740\u4ed6\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e00\u5207\uff0c \u5374\u662f\u6beb\u65e0\u529e\u6cd5\u80fd\u628a\u8fd9\u4e9b\u590d\u6742\u7684\u6069\u6028\u6392\u9664\u6f84\u6e05\u3002 \u3000\u3000\u5c71\u5761\u4e0b\uff0c\u8f9b\u6377\u7b49\u4eba\u5306\u5306\u800c\u8fc7\uff0c\u5c71\u5761\u4e0a\uff0c\u8c22\u957f\u537f\u6d69\u7136\u800c\u53f9\uff0c\u4ed6\u671b\u7740\u9a6c\u8f66\u78f7\u78f7\uff0c\u4ed6\u867d\u7136\u4e0d\u77e5 \u9053\u8f66\u4e0a\u4fbf\u662f\u4e0d\u5171\u6234\u5929\u7684\u4ec7\u4eba\uff0c\u4f46\u89c1\u90a3\u78f7\u78f7\u7684\u8f66\u8f74\u8f6c\u52a8\u4e4b\u4e0b\uff0c\u626c\u8d77\u6f2b\u5929\u98ce\u6c99\uff0c\u968f\u98ce\u800c\u8fc7\uff0c\u843d\u82f1 \u5251\u5ba2\u6df1\u6df1\u611f\u5230\u81ea\u5df1\u7684\u4e8b\u4e1a\u3001\u524d\u9014\uff0c\u4e5f\u5373\u5c06\u548c\u8fd9\u4e9b\u98ce\u6c99\u4e00\u6837\uff0c\u7acb\u523b\u6d88\u5931\u65e0\u8e2a\u2026\u2026 \u3000\u3000\u6885\u5360\u6625\u5148\uff0c\u51cc\u5bd2\u65e9\u653e\uff0c\u4e0e\u677e\u7af9\u4e3a\u4e09\u53cb\uff0c\u50b2\u51b0\u96ea\u800c\u72ec\u8273\u3002 \u3000\u3000\u65f6\u5f53\u9686\u51ac\uff0c\u6606\u660e\u57ce\u5916\u3002 \u3000\u3000\u4e94\u534e\u5c71\u4e2d\uff0c\u96ea\u6df1\u6885\u5f00\uff0c\u6d51\u82d4\u7f00\u7389\uff0c\u970f\u96ea\u8054\u82f1\uff0c\u867d\u4ecd\u4e25\u5bd2\u5982\u6545\uff0c\u4f46\u6885\u9999\u6c81\u5fc3\uff0c\u4ee4\u4eba\u5fc3\u813e\u795e \u9aa8\u7686\u6e05\u3002 \u3000\u3000\u540e\u5c71\u6df1\u5904\uff0c\u76f4\u58c1\u8fde\u4e91\uff0c\u7691\u7691\u767d\u96ea\u4e4b\u4e0a\uff0c\u7f00\u4ee5\u8001\u6885\u591a\u6839\uff0c\u7686\u4f3c\u767e\u5e74\u4ee5\u4e0a\u4e4b\u7269\uff0c\u866c\u679d\u5982\u94c1\uff0c \u6697\u9999\u6d6e\u5f71\uff0c\u771f\u4e0d\u77e5\u5929\u5730\u4e4b\u95f4\uff0c\u4f55\u6765\u5982\u6b64\u6e05\u5883\u3002 \u3000\u3000\u66ae\u8272\u82cd\u832b\uff0c\u591c\u5e55\u6e10\u7f69\uff0c\u6726\u80e7\u4e2d\u666f\u7269\u66f4\u89c1\u80dc\u7edd\u3002 \u3000\u3000\u5927\u96ea\u65e9\u6b62\uff0c\u5929\u8272\u5df2\u6e05\uff0c\u4e00\u8f6e\u6697\u6708\u7f13\u7f13\u4e0a\u5347\uff0c\u770b\u4e00\u770b\uff0c\u660e\u6708\u5df2\u7ecf\u957f\u6ee1\uff0c\u6b63\u662f\u6708\u5706\u4e4b\u65f6\u3002 \u3000\u3000\u5c71\u9634\u5904\uff0c\u8001\u6885\u4e4b\u4e0b\uff0c\u9759\u9759\u7684\u7acb\u7740\u4e09\u4eba\u3002 \u3000\u3000\u4e09\u4eba\u662f\u5e76\u6392\u800c\u7acb\uff0c\u4e2d\u95f4\u4e00\u4e2a\u4e43\u662f\u53e4\u7a00\u8001\u7fc1\uff0c\u9b13\u9aef\u5982\u94f6\uff0c\u4e00\u88ad\u957f\u888d\uff0c\u633a\u7acb\u5728\u96ea\u5730\u91cc\uff0c\u663e\u5f97 \u5341\u5206\u5b64\u5bc2\u6e05\u4fd7\u3002 \u3000\u3000\u4e24\u8fb9\u7684\u5374\u662f\u4e00\u53cc\u5e74\u7ea6\u4e8c\u5341\u7684\u5c11\u5e74\uff0c\u957f\u5f97\u597d\u4fca\u7f8e\uff0c\u4e00\u6837\u7684\u82f1\u6c14\u52c3\u52c3\uff0c\u5251\u7709\u6731\u5507\u3002 \u3000\u3000\u53ef\u602a\u7684\u4e24\u4eba\u9762\u4e0a\u5374\u90fd\u7b3c\u7f69\u4e00\u4e9b\u60b2\u6124\u7684\u60c5\u7eea\u3002 \u3000\u3000\u6253\u80cc\u540e\u770b\u53bb\uff0c\u4e24\u4e2a\u5c11\u5e74\u5374\u662f\u80cc\u8d1f\u957f\u5251\uff0c\u800c\u4e14\u4e00\u8eab\u4fd0\u843d\u6253\u626e\uff0c\u96ea\u5730\u4e2d\uff0c\u5251\u7a57\u626c\u8d77\uff0c\u76ca\u53d1\u886c \u6258\u51fa\u4e24\u4eba\u7684\u82f1\u633a\u3002 \u3000\u3000\u8001\u4eba\u53cc\u624b\u8d1f\u540e\uff0c\u957f\u8896\u540e\u5815\uff0c\u80cc\u6885\u800c\u7acb\uff0c\u9759\u9759\u7684\u6ca1\u6709\u5f00\u4e00\u58f0\u53e3\u3002 \u3000\u3000\u8fd9\u6837\u7684\u5927\u7684\u51b7\u5929\uff0c\u98de\u9e1f\u8d70\u517d\u7edd\u8ff9\uff0c\u5c31\u7b97\u662f\u6709\uff0c\u5728\u8fd9\u8584\u66ae\u70b9\u70b9\u4e4b\u9645\uff0c\u4e5f\u662f\u5e94\u5f52\u8fdb\u8001\u5de2\u7684 \u4e86\uff0c\u662f\u4ee5\u5468\u906d\u76ca\u53d1\u663e\u51fa\u4e00\u79cd\u5bc2\u9759\u7684\u6c14\u6c1b\u3002 \u3000\u3000\u65f6\u95f4\u4e00\u5206\u4e00\u79d2\u7684\u8fc7\u53bb\uff0c\u5de6\u9996\u7684\u4e00\u4e2a\u5c11\u5e74\uff0c\u751f\u50cf\u662f\u6709\u4e9b\u513f\u4e0d\u8010\u70e6\u4e86\uff0c\u63aa\u624b\u5728\u7709\u9645\u5411\u5c71\u9053\u671b \u4e00\u671b\uff0c\u5f00\u53e3\u9053\uff1a\u201c\u6708\u513f\u5373\u5c06\u5f53\u7a7a\u4e86\uff0c\u600e\u4e48\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672a\u8bf4\u5b8c\uff0c\u53f3\u9996\u7684\u5c11\u5e74\u7b11\u7740\u63a5\u53e3\u9053\uff1a\u201c\u6377\u5f1f\u83ab\u8981\u5fc3\u7126\uff0c\u90a3\u4e9b\u4eba\u7269\u80cc\u7740\u5982\u6b64\u7684\u5927\u540d\u5934\uff0c \u4e00\u5b9a\u4e0d\u4f1a\u8001\u7740\u8138\u76ae\u907f\u800c\u4e0d\u6218\u7684\u3002\u201d \u3000\u3000\u4e0d\u6d88\u8bf4\uff0c\u8fd9\u8001\u5c11\u4e09\u4eba\u6b63\u662f\u6885\u5c71\u6c11\u3001\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u6027\u5b50\u8f83\u6025\uff0c\u8010\u4e0d\u4f4f\u5de6\u53f3\u8d70\u52a8\uff0c\u95f7\u95f7\u9053\uff1a\u201c\u8d3c\u5b50\u4eec\u8fd8\u4e0d\u5feb\u6765\uff0c\u5bf9\u4e86\uff0c\u5434\u54e5\uff0c\u5f85\u4f1a\u54b1\u4eec \u8981\u597d\u597d\u6298\u8fb1\u4ed6\u4eec\u4e00\u756a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u58f0\u672a\u5b8c\uff0c\u8138\u8272\u5df2\u662f\u9aa4\u53d8\uff0c\u5434\u51cc\u98ce\u8138\u8272\u4ea6\u662f\u4e00\u5bd2\uff0c\u6562\u60c5\u5317\u98ce\u547c\u53f7\u5904\uff0c\u4e00\u9635\u5954\u817e\u4e4b\u58f0\u9690 \u9690\u4f20\u6765\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5fc3\u4e2d\u4e00\u9707\uff0c\u5df2\u77e5\u4ec7\u654c\u5230\u6765\u3002 \u3000\u3000\u5c06\u8fd1\u5341\u4e94\u5e74\u524d\uff0c\u540c\u6837\u7684\u5929\u6c14\uff0c\u540c\u6837\u7684\u65f6\u5019\uff0c\u4e5f\u4e8e\u540c\u6837\u7684\u5730\u70b9\uff0c\u6885\u5c71\u6c11\u5f53\u65f6\u4ee5\u4e03\u5999\u795e\u541b\u4e4b \u540d\u4e3a\u640f\u4e94\u5927\u5b97\u6d3e\u638c\u95e8\u8054\u624b\u5251\u9635\uff0c\u7ed3\u679c\u5728\u8be1\u8ba1\u4e4b\u4e0b\uff0c\u9669\u4e9b\u9001\u4e86\u547d\u3002 \u3000\u3000\u5982\u4eca\uff0c\u6885\u5c71\u6c11\u529f\u529b\u5168\u5931\uff1f\u4f46\u4e00\u5bf9\u5f92\u8f88\u7684\u529f\u592b\u5374\u5927\u6709\u9752\u51fa\u4e8e\u84dd\u4e4b\u52bf\uff0c\u5f3a\u4ec7\u5728\u773c\u5185\uff0c\u4ecd\u548c\u5341 \u4e94\u5e74\u524d\u4e00\u6837\u5730\u4e0d\u5c51\u4e00\u51fb\uff0c\u4f46\u662f\uff0c\u4e5f\u8bb8\u662f\u7531\u4e8e\u4e0b\u610f\u8bc6\u7684\u4f5c\u7528\uff0c\u4ed6\u5fc3\u4e2d\u5374\u6b62\u4e0d\u4f4f\u7684\u4e00\u9635\u72c2\u8df3\u3002 \u3000\u3000\u201c\u54fc\uff01\u5bf9\u8fd9\u51e0\u4e2a\u6bdb\u8d3c\u4f55\u5fc5\u5982\u6b64\u7d27\u5f20\u2014\u2014\u201d \u3000\u3000\u4ed6\u4e0d\u5c51\u7684\u81ea\u5fd6\u3002 \u3000\u3000\u4ed6\u6df1\u957f\u5438\u4e00\u53e3\u6c14\uff0c\u6885\u82b1\u6c81\u9f3b\u7684\u6e05\u9999\u751c\u751c\u7684\u4f20\u4eba\uff0c\u5e73\u9759\u4e86\u52a8\u8361\u7684\u5fc3\u60c5\u3002 \u3000\u3000\u6765\u4eba\u597d\u5feb\u811a\u7a0b\uff0c\u7247\u523b\uff0c\u8fdc\u5730\u91cc\u770b\u5230\u51e0\u6761\u6781\u6de1\u7684\u8eab\u5f71\uff0c\u6643\u773c\u95f4\u4fbf\u6765\u5230\u8fd1\u524d\uff0c\u53ea\u89c1\u4ed6\u4eec\u5728\u8c37 \u53e3\u7565\u7565\u4e00\u65cb\uff0c\u4fbf\u76f4\u5954\u800c\u6765\u3002 \u3000\u3000\u8fd1\u4e86\uff0c\u6e05\u6e05\u695a\u695a\u53ef\u4ee5\u6570\u660e\uff0c\u6765\u7684\u662f\u56db\u4e2a\u4eba\u3002 \u3000\u3000\u90a3\u56db\u4e2a\u4eba\u597d\u50cf\u4e5f\u4f3c\u5728\u6bd4\u8d5b\u8db3\u7a0b\u4f3c\u7684\uff0c\u51e0\u8d77\u843d\uff0c\u4fbf\u8dc3\u5230\u8ddf\u524d\uff0c\u6885\u5c71\u6c11\u548c\u8f9b\u3001\u5434\u4e09\u4eba\u7acb\u4e8e\u6885 \u6811\u4e4b\u540e\uff0c\u6708\u5149\u4e0b\uff0c\u6885\u6811\u82cd\u82cd\u7684\u5a46\u5a46\u5de8\u5f71\uff0c\u628a\u4e09\u4eba\u853d\u5f97\u5341\u5206\u9690\u5bc6\u3002 \u3000\u3000\u56db\u4eba\u6765\u5230\u9053\u524d\uff0c\u4e00\u8d77\u505c\u8eab\uff0c\u770b\u6765\u56db\u4e2a\u4eba\u7684\u8f7b\u8eab\u6cd5\u90fd\u662f\u5dee\u4e0d\u591a\uff0c\u4e0d\u8fc7\u4e00\u4e2a\u7626\u524a\u8001\u8005\u6bd4\u5176\u4ed6 \u4e09\u4eba\u90fd\u8981\u6765\u5f97\u524d\u4e00\u6b65\u3002 \u3000\u3000\u90a3\u8001\u8005\u7ad9\u4e0b\u8eab\u6765\uff0c\u56db\u5468\u7565\u4e00\u6253\u91cf\uff0c\u563f\u7136\u9053\uff1a\u201c\u8f9b\u6377\u90a3\u5c0f\u5b50\u770b\u6837\u5b50\u8fd8\u6ca1\u6709\u5230\u5462\uff1f\u2026\u2026\u201d \u3000\u3000\u540e\u9762\u8ddf\u7740\u4e09\u4eba\u4f3c\u4e4e\u548c\u8fd9\u8001\u8005\u4e0d\u592a\u5bf9\u52b2\uff0c\u9ed8\u7136\u6839\u672c\u6ca1\u6709\u7406\u4ed6\u3002 \u3000\u3000\u201c\u6708\u513f\u5df2\u767b\u4e2d\u5929\uff0c\u770b\u6765\u8f9b\u6377\u662f\u4e0d\u4f1a\u6765\u7684\u4e86\uff1f\u54b1\u4eec\u4e14\u7b49\u4ed6\u4e00\u4f1a\u2026\u2026\u201d \u3000\u3000\u4ed6\u8bdd\u672b\u8bf4\u5b8c\uff0c\u8001\u6885\u540e\u9762\u4e00\u4e2a\u58f0\u97f3\u63a5\u53e3\u9053\uff1a\u201c\u4e0d\u6562\u5f53\uff0c\u54b1\u4eec\u65e9\u5df2\u606d\u5019\u5927\u9a7e\uff01\u201d \u3000\u3000\u8bf4\u7740\u4ece\u6811\u6797\u540e\u9762\u8d70\u51fa\u4e24\u4e2a\u5c11\u5e74\u3002 \u3000\u3000\u90a3\u7626\u957f\u8001\u8005\u4e00\u884c\u56db\u4eba\u6b63\u662f\u5f53\u4eca\u6b66\u6797\u56db\u5927\u5b97\u6d3e\u638c\u95e8\u4eba\uff0c\u987a\u6b21\u662f\u5d06\u5cd2\u5251\u795e\u5389\u9e57\u3001\u6b66\u5f53\u8d64\u9633\u9053 \u957f\u3001\u5ce8\u5d4b\u82e6\u5eb5\u4e0a\u4eba\u548c\u70b9\u82cd\u843d\u82f1\u5251\u5ba2\u8c22\u957f\u537f\uff01 \u3000\u3000\u5389\u9e57\u4e0d\u6599\u8f9b\u6377\u65e9\u5df2\u5230\u6765\uff0c\u6014\u4e86\u4e00\u6014\uff0c\u5e72\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff01\u597d\u8bf4\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u90fd\u5df2\u53cc\u76ee\u53d1\u8d64\uff0c\u5c24\u5176\u662f\u5434\u51cc\u98ce\uff0c\u5636\u58f0\u53eb\u9053\uff1a\u201c\u5e9f\u8bdd\u5c11\u8bf4\u4e24\u53e5\uff0c\u54b1\u4eec\u8fd9\u5c31\u52a8 \u624b\u2014\u2014\u201d \u3000\u3000\u4ed6\u5e73\u65e5\u4e3a\u4eba\u5584\u826f\u8bda\u6073\uff0c\u5c31\u662f\u8fde\u9a82\u4eba\u90fd\u5f88\u5c11\uff0c\u4f46\u8fd9\u4e00\u4e0b\u662f\u6fc0\u52a8\u8fc7\u5ea6\uff0c\u4e00\u53cd\u5e73\u65e5\u4ece\u5bb9\u6ee1\u6d12\u7684 \u6001\u5ea6\u3002 \u3000\u3000\u5389\u9e57\u4e43\u662f\u8001\u6c5f\u6e56\u4e86\uff0c\u4ec7\u6740\u7684\u4e8b\u4ef6\u662f\u53f8\u7a7a\u89c1\u60ef\uff0c\u54c8\u54c8\u4e00\u7b11\u7b54\u9053\uff1a\u201c\u59d3\u5434\u7684\uff0c\u54b1\u4eec\u662f\u4e00\u6c5f\u4e8c \u6d77\u4e4b\u6068\uff0c\u5c31\u662f\u4f60\u4e0d\u627e\u4e0a\u95e8\u6765\uff0c\u6211\u5389\u67d0\u4eba\u4e5f\u5f97\u627e\u5230\u4f60\uff0c\u4f60\u4e14\u4e0d\u8981\u6025\u2014\u2014\u201d \u3000\u3000\u4ed6\u8fd9\u756a\u8bdd\u8bf4\u5f97\u597d\u4e0d\u8001\u7ec3\uff0c\u8eab\u540e\u8d64\u9633\u9053\u957f\u4e5f\u662f\u4e00\u7b11\u9053\uff1a\u201c\u5434\u65bd\u4e3b\u4e0d\u8981\u5fc3\u6025\uff0c\u65ad\u9b42\u5251\u548c\u4e03\u5999 \u795e\u541b\u540e\u4ee3\u7684\u53ec\u5524\uff0c\u54b1\u4eec\u90a3\u6562\u8fdd\u547d\uff0c\u563f\uff0c\u82e6\u5eb5\u4e0a\u4eba\uff0c\u4f60\u8bf4\u662f\u5417\uff1f\u2026\u2026\u201d \u3000\u3000\u5ce8\u5d4b\u7684\u82e6\u5eb5\u4e0a\u4eba\u563f\u563f\u4e00\u7b11\u989d\u9996\u3002 \u3000\u3000\u4ed6\u4eec\u8fd9\u4e00\u5bf9\u4e00\u7b54\uff0c\u4efb\u8f9b\u3001\u5434\u4e24\u4eba\u806a\u654f\u65e0\u6bd4\uff0c\u4e5f\u7b54\u4e0d\u4e0a\u8bdd\u6765\u3002 \u3000\u3000\u5389\u9e57\u7b49\u4e00\u884c\u56db\u4eba\uff0c\u679c\u7136\u4e0d\u751a\u548c\u8c10\uff0c\u5176\u4e2d\u53ea\u6709\u8d64\u9633\u9053\u957f\u548c\u82e6\u5eb5\u4e0a\u4eba\u4ea4\u60c5\u4e0d\u9519\uff0c\u5389\u9e57\u548c\u4ed6\u4eec \u662f\u52fe\u5fc3\u6597\u89d2\u8c8c\u5408\u795e\u79bb\uff0c\u800c\u8c22\u957f\u537f\u5374\u662f\u56e0\u903c\u8feb\u800c\u81f3\uff0c\u66f4\u662f\u548c\u4ed6\u4eec\u683c\u683c\u4e0d\u5165\u3002 \u3000\u3000\u8f9b\u6377\u6c89\u541f\u4e00\u4e0b\uff0c\u624d\u9053\uff1a\u201c\u665a\u8f88\u6295\u4e0b\u8bf7\u67ec\uff0c\u8bf7\u5404\u4f4d\u5927\u82f1\u96c4\u5230\u8fd9\u513f\u6765\uff0c\u62dc\u8d50\u795e\u529f\uff0c\u81f4\u4f7f\u5404\u4f4d \u5343\u91cc\u5954\u6ce2\uff0c\u5b9e\u4ee4\u665a\u8f88\u5185\u5fc3\u4e0d\u5b89\u3002\u597d\u5728\u5404\u4f4d\u90fd\u662f\u4e00\u4ee3\u5b97\u5e08\uff0c\u5fc5\u7136\u4e0d\u4f1a\u8ba1\u8f83\u4e8e\u6b64\u2026\u2026\u201d \u3000\u3000\u8f9b\u6377\u51b0\u96ea\u806a\u660e\uff0c\u8bf4\u51fa\u8fd9\u756a\u8bdd\u6765\uff0c\u8f6c\u5f2f\u62b9\u89d2\u7684\u8bdd\u4e2d\u6709\u8bdd\uff0c\u51e0\u58f0\u201c\u665a\u8f88\u201d\u4ee4\u8fd9\u51e0\u4e2a\u8001\u6c5f\u6e56\u5927 \u662f\u96be\u582a\uff0c\u4f46\u662f\u5bf9\u65b9\u4e2a\u4e2a\u534a\u751f\u6df7\u8ff9\u6c5f\u6e56\uff0c\u54ea\u4f1a\u4e0d\u660e\u767d\uff0c\u5389\u9e57\u957f\u58f0\u7b11\u9053\uff1a\u201c\u597d\u8bf4\u597d\u8bf4\uff0c\u54b1\u4eec\u8fd9\u662f \u65e7\u5730\u91cd\u6e38\uff0c\u9762\u5bf9\u9ad8\u5c71\u53e4\u6885\uff0c\u5fc3\u65f7\u795e\u6021\uff0c\u8f9b\u5c0f\u4fa0\u4e43\u662f\u4e03\u5999\u540e\u4ee3\uff0c\u5230\u4e0d\u662f\u4fd7\u4eba\u2014\u2014\u201d \u3000\u3000\u4ed6\u8bf4\u65e7\u5730\u91cd\u6e38\uff0c\u4e43\u662f\u6307\u5341\u4e94\u5e74\u524d\u4e94\u534e\u5c71\u4e0a\u51fb\u8d25\u4e03\u5999\u795e\u541b\u7684\u4e00\u56de\u4e8b\uff0c\u8f9b\u6377\u4e00\u542c\u4e4b\u4e0b\uff0c\u4e0d\u7531 \u5f97\u4e3a\u4e4b\u53e3\u585e\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5374\u51b7\u51b7\u7684\u9053\uff1a\u201c\u59d3\u5389\u7684\u5c11\u901e\u53e3\u820c\u4e4b\u5229\uff0c\u4f60\u4f5c\u6076\u534a\u751f\uff0c\u65e5\u5e38\u5728\u6c5f\u6e56\u4e0a\u4ee5\u9634\u8bc8\u6b3a\u4eba\uff0c \u4eca\u65e5\u4fbf\u662f\u4f60\u7684\u6b7b\u671f\uff0c\u95f2\u8bdd\u5012\u53ef\u4ee5\u5c11\u8bf4\u4e24\u53e5\u3002\u201d \u3000\u3000\u4ed6\u53e3\u624d\u4e0d\u751a\u597d\uff0c\u4f46\u8fd9\u4e43\u662f\u6012\u6124\u800c\u8a00\uff0c\u5389\u9e57\u7b49\u4eba\u5374\u611f\u6b63\u6c14\u51db\u7136\uff0c\u4e0d\u7531\u60f3\u5230\u81ea\u5df1\u5e73\u65e5\u4f5c\u6076\u6c5f \u6e56\u4e0a\u7684\u60c5\u5f62\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u548c\u8c22\u957f\u537f\u8fd8\u597d\uff0c\u5251\u795e\u548c\u8d64\u9633\u5374\u662f\u65e0\u6076\u4e0d\u4f5c\uff0c\u8fde\u60f3\u4e4b\u4e0b\u4e0d\u89c9\u8001\u7f9e\u6210\u6012\u3002 \u3000\u3000\u5389\u9e57\u5389\u58f0\u53eb\u9053\uff1a\u201c\u59d3\u5434\u7684\u5c0f\u5b50\u5982\u6b64\u81ea\u5927\uff0c\u54b1\u4eec\u8d70\u7740\u77a7\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u53cd\u8eab\u4fbf\u671b\u5de6\u624b\u7684\u4e00\u5757\u5e7f\u573a\u4e0a\u7eb5\u53bb\u9053\uff1a\u201c\u8fc7\u6765\u5427\uff0c\u5389\u67d0\u4eba\u9886\u6559\u795e\u541b\u548c\u65ad\u9b42\u5251\u7684\u771f\u4f20\u2014 \u2014\u201d \u3000\u3000\u4ed6\u8fd9\u4e00\u7eb5\u8d70\uff0c\u8d64\u9633\u7b49\u4eba\u4e5f\u90fd\u8ddf\u7740\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u66f4\u662f\u6beb\u65e0\u8fdf\u7591\u4e00\u9f50\u8ddf\u53bb\u3002 \u3000\u3000\u5c71\u836b\u9053\u4e0a\u6240\u6709\u4eba\u90fd\u5954\u8fc7\u53bb\uff0c\u8001\u6885\u4e4b\u4e0b\uff0c\u5b64\u7acb\u4e00\u4e2a\u8001\u4eba\uff0c\u6b63\u662f\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u3002 \u3000\u3000\u4ed6\u4e0d\u613f\u518d\u4e0e\u8fd9\u4e00\u6279\u5c0f\u4eba\u5bf9\u9762\uff0c\u4f46\u662f\u5fc3\u4e2d\u5374\u59cb\u7ec8\u4e0d\u80fd\u91ca\u7136\u3002\u4ed6\u51b7\u5982\u51b0\u971c\u7684\u76ee\u5149\u4ece\u6811\u679d\u4e1b\u4e2d \u6ce8\u89c6\u7740\u6bcf\u4e00\u4e2a\u4eba\uff0c\u4ec7\u6bd2\u7684\u706b\u7130\uff0c\u5e03\u6ee1\u80f8\u819b\u3002 \u3000\u3000\u5f53\u5904\u4e03\u5999\u795e\u541b\u4ee5\u51b7\u9177\u51fa\u540d\uff0c\u5341\u6570\u5e74\u7684\u9676\u51b6\uff0c\u5e76\u6ca1\u6709\u5b8c\u5168\u6539\u53bb\u3002\u6240\u8c13\u201c\u6c5f\u5c71\u6613\u6539\uff0c\u672c\u6027\u96be \u79fb\u201d\uff0c\u51b7\u9177\u7684\u4ed6\uff0c\u5e0c\u671b\u8fd9\u56db\u4e2a\u66fe\u7ecf\u6697\u7b97\u4ed6\u7684\u4eba\u7acb\u523b\u88ab\u62a5\u507f\u56de\u6765\u3002 \u3000\u3000\u84e6\u7136\uff0c\u4e00\u4e2a\u6a35\u7cb9\u5931\u795e\u7684\u8138\u5b54\u5370\u4eba\u4ed6\u7684\u773c\u5e18\uff0c\u4ed6\u611f\u5230\u4e00\u4e9b\u964c\u751f\uff0c\u4ed6\u5947\u602a\u7684\u4ed4\u7ec6\u4e00\u77a7\uff0c\u8ba4\u51fa \u6765\u6b63\u662f\u8c22\u957f\u537f\u3002 \u3000\u3000\u4ed6\u9661\u7136\u4e00\u6014\uff0c\u7acb\u523b\u60f3\u5f53\u5e74\u90a3\u82f1\u4fca\u7684\u5c11\u5e74\uff0c\u800c\u4eca\u65e5\u5374\u662f\u5982\u6b64\u7684\u5931\u795e\u843d\u9b44\uff0c\u6885\u5c71\u6c11\u5f88\u4e86\u89e3\u4ed6 \u7684\u5fc3\u60c5\uff0c\u4e0d\u89c9\u5fae\u5fae\u4e00\u53f9\u3002 \u3000\u3000\u601d\u6f6e\u4e0d\u5b9a\u7684\u8d77\u4f0f\u7740\uff0c\u90a3\u8fb9\u516d\u4eba\u5df1\u7ecf\u53eb\u4e0a\u4e86\u9635\uff0c\u4e0d\u6d88\u518d\u8bf4\uff0c\u56db\u4e2a\u638c\u95e8\u4ecd\u7136\u7528\u7684\u662f\u4ed6\u4eec\u7684\u770b \u5bb6\u672c\u9886\uff0c\u56db\u4eba\u5df2\u4e25\u6574\u7684\u5e03\u6210\u4e86\u5251\u9635\u3002 \u3000\u3000\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u6253\u4e2a\u62db\u547c\uff0c\u4e00\u8d77\u8d70\u5165\u9635\u4e2d\u3002 \u3000\u3000\u5251\u795e\u5389\u9e57\u5f53\u9635\u800c\u7acb\uff0c\u563f\u7136\u53eb\u9053\uff1a\u201c\u6545\u4eba\u6709\u540e\uff0c\u54b1\u4eec\u8001\u4e00\u8f88\u7684\u518d\u4e0d\u5c3d\u529b\uff0c\u5c82\u4e0d\u53eb\u4ed6\u4eec\u803b \u7b11\uff1f\u201d \u3000\u3000\u8bf4\u7740\u53cd\u624b\u4e00\u6325\uff0c\u201c\u545b\u6994\u201d\u4e00\u58f0\uff0c\u4e00\u9053\u8679\u5149\u51b2\u5929\u800c\u8d77\u3002 \u3000\u3000\u540c\u65f6\u95f4\u91cc\uff0c\u8d64\u9633\u3001\u82e6\u5eb5\u7684\u957f\u5251\u4e5f\u659c\u8df3\u51fa\u9798\u3002 \u3000\u3000\u4ed6\u4eec\u90fd\u662f\u6d78\u6deb\u5728\u5251\u6cd5\u4e0a\u6570\u5341\u5e74\u7684\u9ad8\u624b\uff0c\u5355\u770b\u4ed6\u4eec\u62d4\u5251\u7684\u59ff\u52bf\uff0c\u4fbf\u90fd\u6709\u4e00\u6d3e\u5927\u5b97\u5e08\u7684\u98ce \u5ea6\u3002 \u3000\u3000\u82e6\u5eb5\u7684\u5ce8\u5d4b\u5251\u6cd5\u5b88\u91cd\u4e8e\u653b\uff0c\u53ea\u95fb\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u8499\u8499\u9752\u5149\u4e00\u95f4\u4e2d\u7f29\uff0c\u76d8\u8eab\u4e00\u531d\uff0c\u8df3\u52a8\u6570 \u4e0b\uff0c\u82e6\u5eb5\u4e0a\u4eba\u5df2\u6301\u5251\u5728\u624b\u3002 \u3000\u3000\u770b\u4ed6\u8fd9\u4e2a\u624b\u6cd5\uff0c\u4fbf\u53ef\u4ee5\u77e5\u9053\u4ed6\u7684\u5251\u6cd5\u5df1\u5230\u767b\u5cf0\u9020\u6781\u7684\u5730\u6b65\uff0c\u5c31\u662f\u5728\u62d4\u5251\u7684\u65f6\u5019\uff0c\u4e5f\u90fd\u662f \u4e0d\u80af\u653e\u5f03\u6ce8\u610f\u62a4\u9632\u8eab\u4f53\uff0c\u7ecf\u9a8c\u548c\u624b\u6cd5\uff0c\u5c82\u80fd\u8bf4\u4e3a\u5e73\u5eb8\uff1f \u3000\u3000\u56db\u4e2a\u638c\u95e8\u4eba\u4e2d\uff0c\u53ea\u6709\u8c22\u957f\u537f\u7684\u5251\u8fdf\u8fdf\u6ca1\u6709\u51fa\u9798\u3002 \u3000\u3000\u5389\u9e57\u5f88\u6e05\u695a\u4ed6\u7684\u5fc3\u60c5\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u8c22\u4e16\u5144\uff0c\u8bf7\u2014\u2014\u201d \u3000\u3000\u8c22\u957f\u537f\u9eef\u7136\u957f\u53f9\uff0c\u53f3\u624b\u84e6\u7136\u4e00\u62ac\uff0c\u5251\u5b50\u5df2\u5230\u624b\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u4e5f\u4e0d\u518d\u8fdf\u7591\uff0c\u4e00\u8d77\u6301\u5251\u5728\u624b\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u51b7\u7684\u626b\u89c6\u6bcf\u4e00\u4e2a\u4eba\uff0c\u5f53\u4ed6\u76ee\u5149\u505c\u7559\u5728\u5389\u9e57\u8eab\u4e0a\u7684\u65f6\u5019\uff0c\u4e0d\u7531\u5927\u53f1\u9053\uff1a\u201c\u4e8f\u4f60\u8eab\u4e3a \u638c\u95e8\uff0c\u7adf\u7136\u5077\u7a83\u522b\u4eba\u4e4b\u7269\u2014\u2014\u201d \u3000\u3000\u6562\u60c5\u5389\u9e57\u624b\u4e2d\u7684\u4e00\u67c4\u957f\u5251\uff0c\u6b63\u662f\u5931\u53bb\u7684\u6885\u9999\u5251\u3002\u800c\u90a3\u67c4\u5389\u9e57\u539f\u6709\u7684\u201c\u501a\u8679\u795e\u5251\u201d\uff0c\u5374\u80cc \u8d1f\u5728\u80cc\u4e0a\u3002 \u3000\u3000\u5389\u9e57\u81ea\u77e5\u7406\u4e8f\uff0c\u4e0d\u63a5\u8fd9\u8bdd\u5934\uff0c\u51b7\u7136\u9053\uff1a\u201c\u4f60\u6562\u53d1\u62db\u5417\uff1f\u201d \u3000\u3000\u5434\u51cc\u98ce\u5927\u53f1\u4e00\u58f0\uff0c\u51b2\u5165\u5251\u9635\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u6f2b\u58f0\u5ba3\u4f5b\u53f7\uff0c\u957f\u5251\u5e73\u8170\u4e00\u6321\uff0c\u5de6\u53f3\u811a\u9f50\u8f6c\uff0c\u4f46\u89c1\u5251\u6c14\u8499\u8499\u4e4b\u4e2d\uff0c\u5251\u9635\u5df1\u7136\u53d1 \u52a8\u3002 \u3000\u3000\u8f9b\u6377\u6367\u7740\u4e00\u67c4\u5e73\u51e1\u94a2\u5251\uff0c\u5de6\u53f3\u4e00\u6643\uff0c\u914d\u5408\u7740\u5434\u51cc\u98ce\u7684\u75af\u72c2\u653b\u52bf\uff0c\u5e2e\u52a9\u4ed6\u5728\u8eab\u540e\u5e03\u4e0b\u5f20\u5b8c \u7f8e\u7684\u7f51\u3002 \u3000\u3000\u9ad8\u624b\u4ea4\u6218\u6bd5\u7adf\u4e0d\u51e1\uff0c\u5251\u6c14\u8499\u8499\uff0c\u516d\u4eba\u4ee5\u5feb\u6253\u5feb\uff0c\u5374\u59cb\u7ec8\u4e0d\u95fb\u4e00\u58f0\u5251\u5b50\u5175\u5203\u7684\u649e\u51fb\u58f0\uff01 \u3000\u3000\u8c22\u957f\u537f\u548c\u82e6\u5eb5\u5728\u5251\u9635\u4e2d\u5c45\u5b88\u7684\u5730\u4f4d\uff0c\u800c\u5389\u9e57\u548c\u8d64\u9633\u9053\u4eba\u4ee5\u653b\u654c\u4e3a\u4e3b\u3002 \u3000\u3000\u5389\u9e57\u53f7\u79f0\u5251\u795e\uff0c\u5728\u5251\u672f\u4e0a\u7684\u9020\u8be3\uff0c\u53ef\u60f3\u800c\u77e5\u3002 \u3000\u3000\u4ed6\u4e5f\u660e\u77e5\u4eca\u65e5\u4e4b\u6218\uff0c\u5409\u51f6\u83ab\u6d4b\uff0c\u4f46\u4ecd\u56fe\u632f\u4f5c\uff0c\u914d\u5408\u5251\u9635\uff0c\u5d06\u5cd2\u201c\u4e09\u7edd\u5251\u672f\u201d\u7684\u6740\u624b\u8fde\u8fde \u65bd\u51fa\u3002 \u3000\u3000\u6fc0\u6218\u4e2d\uff0c\u8d64\u9633\u9053\u58eb\u771f\u6c14\u704c\u6ce8\uff0c\u957f\u5251\u4e00\u9886\uff0c\u4e00\u5f0f\u201c\u6a2a\u98de\u957f\u6c5f\u201d\uff0c\u659c\u659c\u6311\u5411\u8f9b\u6377\u5c0f\u8179\uff0c\u800c\u5389 \u9e57\u4e5f\u914d\u5408\u4ed6\u523a\u5411\u5434\u51cc\u98ce\u3002 \u3000\u3000\u8d64\u9633\u9053\u58eb\u8001\u5978\u5de8\u6ed1\uff0c\u5916\u653b\u5185\u8574\uff0c\u5251\u98ce\u51db\u5389\uff0c\u653b\u52bf\u731b\u6781\uff0c\u8f9b\u6377\u4e0d\u7531\u4e00\u6014\uff0c\u4ed6\u548c\u5434\u51cc\u98ce\u5728\u6cf0 \u5c71\u5927\u4f1a\u6709\u8fc7\u6597\u5251\u9635\u7684\u7ecf\u9a8c\uff0c\u77e5\u9053\u8fc7\u56db\u5927\u5b97\u6d3e\u8054\u624b\u7684\u5251\u9635\uff0c\u786e\u662f\u7cbe\u5999\u65e0\u6bd4\uff0c\u8981\u60f3\u51b2\u51fa\uff0c\u975e\u5f97\u51fb \u5012\u5176\u4e2d\u4e4b\u4e00\u4e0d\u53ef\u3002 \u3000\u3000\u4ed6\u77e5\u81ea\u5df1\u529f\u529b\u5728\u56db\u4eba\u4efb\u4f55\u4e00\u4eba\u4e4b\u4e0a\uff0c\u662f\u4ee5\u786c\u6253\u786c\u649e\uff0c\u5bf9\u65b9\u5fc5\u8981\u5403\u4e8f\uff0c\u90a3\u56db\u5927\u5b97\u6d3e\u7684\u638c\u95e8 \u4eba\u4e00\u5411\u90fd\u987e\u5fcc\u8fd9\u4e00\u70b9\uff0c\u6545\u4e0d\u6562\u548c\u8f9b\u6377\u786c\u6253\u786c\u63a5\uff0c\u4e5f\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\u3002 \u3000\u3000\u4f46\u8d64\u9633\u9053\u58eb\u6b64\u65f6\u597d\u4f3c\u660e\u77e5\u6545\u72af\uff0c\u53c8\u597d\u4f3c\u5fc3\u6709\u6210\u7af9\uff0c\u7adf\u7136\u4e00\u53cd\u60ef\u4f8b\uff0c\u5f3a\u786c\u7684\u6253\u7b97\u8d70\u4e2d\u5bab\u51fb \u5165\u3002 \u3000\u3000\u8f9b\u6377\u6014\u4e86\u6014\uff0c\u51b7\u51b7\u4e00\u54fc\uff0c\u957f\u5251\u4e00\u5708\u3002 \u3000\u3000\u8fd9\u4e00\u5f0f\u4e43\u662f\u8f9b\u6377\u529f\u529b\u6240\u51dd\u805a\uff0c\u975e\u540c\u5c0f\u53ef\uff0c\u9e23\u7684\u4e00\u58f0\uff0c\u5410\u5411\u8d64\u9633\u9053\u58eb\u3002 \u3000\u3000\u54ea\u77e5\u8d64\u9633\u9053\u58eb\u8fd9\u4e00\u62db\u4e43\u662f\u5b9e\u800c\u865a\uff0c\u771f\u529b\u9661\u6563\uff0c\u5251\u5f0f\u5168\u6536\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8f9b\u6377\u7684\u62db\u5f0f \u5df2\u7136\u7528\u8001\u3002 \u3000\u3000\u8fd9\u5251\u9635\u7684\u53d8\u5316\u5230\u592a\u591a\uff0c\u8d64\u9633\u624d\u4e00\u6536\u62db\uff0c\u5de6\u4fa7\u7684\u843d\u82f1\u5251\u5ba2\u8c22\u957f\u537f\u7684\u957f\u5251\u4e71\u70b9\uff0c\u653b\u51fa\u6570\u5251\u3002 \u3000\u3000\u672c\u6765\u8c22\u957f\u537f\u4e43\u662f\u4e3b\u5b88\uff0c\u4f46\u8fd9\u4e00\u53d8\u4e4b\u4e0b\uff0c\u5251\u9635\u65b9\u5f0f\u7acb\u523b\u8ddf\u7740\u5927\u53d8\uff0c\u5a01\u529b\u4e5f\u589e\u5f3a\u4e0d\u5c11\u3002 \u3000\u3000\u8fd9\u4e00\u7740\u53d8\u5316\uff0c\u5b9e\u662f\u5947\u5f02\u590d\u6742\u5df2\u6781\uff0c\u8c22\u957f\u537f\u53f7\u79f0\u843d\u82f1\u5251\uff0c\u70b9\u82cd\u5fc3\u6cd5\u4e00\u5c55\uff0c\u957f\u5251\u64a4\u51fa\u6735\u6735\u5251 \u82b1\uff0c\u6f2b\u5929\u98de\u821e\u3002 \u3000\u3000\u8f9b\u6377\u767e\u5fd9\u4e4b\u4e2d\u957f\u5251\u4e00\u6536\uff0c\u4e00\u5f0f\u201c\u51b2\u5929\u800c\u8d77\u201d\uff0c\u957f\u5251\u6276\u7740\u4e00\u56e2\u5149\u534e\uff0c\u5de6\u53f3\u8df3\u52a8\uff0c\u4f46\u95fb \u201c\u53ee\u201d\u201c\u53ee\u201d\u6570\u58f0\uff0c\u786c\u786c\u63a5\u53bb\u8c22\u957f\u537f\u653b\u6765\u7684\u6570\u5251\u3002 \u3000\u3000\u843d\u82f1\u5251\u8c22\u957f\u537f\u867d\u7136\u4e0d\u613f\u548c\u8f9b\u3001\u5434\u7b49\u4eba\u52a8\u624b\uff0c\u4f46\u5f62\u52bf\u6240\u8feb\uff0c\u8fd9\u4e00\u52a8\u624b\u4e4b\u4e0b\uff0c\u6fc0\u53d1\u4ed6\u7684\u8c6a \u6027\uff0c\u4e00\u8fde\u6570\u62db\u88ab\u8f9b\u6377\u6321\u53bb\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u957f\u5251\u75be\u523a\u800c\u51fa\u3002 \u3000\u3000\u90a3\u5434\u51cc\u98ce\u89c1\u8f9b\u6377\u5251\u5f0f\u7a0d\u6ede\uff0c\u81ea\u5df1\u4e0d\u6562\u505c\u7559\uff0c\u957f\u5251\u731b\u7136\u201c\u5251\u6307\u5929\u5ead\u201d\uff0c\u5de6\u53f3\u4e00\u9635\u4e71\u626b\uff0c\u60f3 \u8981\u903c\u5f00\u5389\u9e57\u3002 \u3000\u3000\u5389\u9e57\u5df2\u548c\u90a3\u5434\u51cc\u98ce\u5bf9\u8fc7\u6570\u6b21\u5251\uff0c\u5df2\u77e5\u8fd9\u5c11\u5e74\u7684\u529f\u529b\u7a81\u98de\u731b\u8fdb\uff0c\u54ea\u6562\u5927\u610f\uff0c\u4e00\u5f0f\u201c\u4e09\u7edd\u98de \u5347\u201d\uff0c\u6885\u9999\u5251\u4e00\u4e3e\uff0c\u4fbf\u628a\u5434\u51cc\u98ce\u7684\u653b\u52bf\u5c01\u53bb\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u5251\u9635\u4e2d\u4eba\u5f71\u95ea\u4e71\uff0c\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u5c55\u5f00\u4ee5\u5feb\u6253\u5feb\u7684\u624b\u6cd5\uff0c\u53cc\u5251\u5408\u74a7\uff0c\u5de6\u53f3\u51b2\u7a81\u3002 \u3000\u3000\u5389\u9e57\u7b49\u4eba\u7684\u5251\u9635\u4e5f\u662f\u8d8a\u6253\u8d8a\u5feb\uff0c\u800c\u4e14\u529f\u529b\u8f83\u6df1\u7684\u82e6\u5eb5\u548c\u8d64\u9633\u4e24\u4eba\u7528\u5b88\u7684\u62db\u5f0f\uff0c\u800c\u8c22\u957f\u537f \u548c\u5389\u9e57\u5c55\u5f00\u75af\u72c2\u7684\u653b\u52bf\u3002 \u3000\u3000\u6708\u6b63\u4e2d\u5929\u2014\u2014 \u3000\u3000\u8001\u6885\u6811\u7684\u540e\u9762\uff0c\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u53cc\u76ee\u5fae\u95ed\uff0c\u4ed6\u4e0d\u7528\u53bb\u770b\u90a3\u8fb9\u7684\u53ae\u6740\uff0c\u4ed6\u6839\u672c\u53ef\u4ee5\u731c\u60f3\u5230 \u53cc\u65b9\u6240\u65bd\u7528\u7684\u62db\u5f0f\uff0c\u5305\u62ec\u4e00\u5207\u6740\u624b\u5728\u5185\u3002 \u3000\u3000\u4ed6\u6709\u4fe1\u5fc3\uff0c\u6377\u513f\u548c\u98ce\u513f\u4e00\u5b9a\u4f1a\u83b7\u80dc\u7684\uff0c\u56e0\u4e3a\uff0c\u4ed6\u4eec\u770b\u5bb6\u672c\u9886\u8fd8\u6ca1\u6709\u65bd\u5c55\u51fa\u6765\uff01\u6218\u573a\u4e0a\uff0c \u5434\u51cc\u98ce\u7684\u5251\u5df2\u4f7f\u51fa\u4e86\u5bb6\u4f20\u7684\u62db\u672f\u2014\u2014\u201c\u65ad\u9b42\u5251\u201d\u62db\uff0c\u53ea\u89c1\u4ed6\u201c\u4e94\u9b3c\u6295\u53c9\u201d\uff0c\u201c\u65e0\u5e38\u95ee\u8def\u201d\uff0c \u7edd\u62db\u8fed\u65bd\uff0c\u52a0\u4e0a\u8f9b\u6377\u957f\u5251\u6709\u82e5\u7075\u86c7\u6084\u7a9c\uff0c\u5ffd\u4e0a\u5ffd\u4e0b\uff0c\u201c\u866c\u679d\u5251\u6cd5\u201d\u7684\u7cbe\u534e\u201c\u51b7\u6885\u62c2\u9762\u201d\uff0c \u201c\u4e4d\u60ca\u6885\u9762\u201d\u7b49\u5f0f\uff0c\u53cc\u5251\u5408\u653b\u4e4b\u4e0b\uff0c\u5a01\u529b\u4e4b\u5927\uff0c\u4ee4\u4eba\u548b\u820c\u3002 \u3000\u3000\u56db\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u4e0d\u518d\u80fd\u786c\u5b88\u5f97\u4f4f\u4e86\uff0c\u4e0d\u7ea6\u800c\u540c\u88ab\u8fd9\u4e00\u756a\u731b\u653b\uff0c\u903c\u5f97\u9000\u5f00\u6570\u4e08\u6709\u5947\uff01\u8f9b \u6377\u957f\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u7384\u95e8\u6b63\u5b97\uff0c\u540d\u95e8\u5927\u6d3e\u7684\u5251\u6cd5\u4e0d\u8fc7\u5982\u6b64\u800c\u5df1\uff01\u201d \u3000\u3000\u4ed6\u8fd9\u8bdd\u513f\u8bf4\u5f97\u592a\u72c2\uff0c\u56db\u4f4d\u638c\u95e8\u4eba\u4e00\u751f\u751a\u662f\u7231\u60dc\u7fbd\u6bdb\uff0c\u5bf9\u81ea\u5df1\u8f9b\u82e6\u95ef\u51fa\u6765\u7684\u540d\u53f7\u66f4\u662f\u7231\u60dc \u65e0\u6bd4\uff0c\u8f9b\u6377\u7adf\u7136\u516c\u5f00\u4fae\u8fb1\uff0c\u56db\u4e2a\u4eba\u90fd\u4e0d\u7531\u5927\u5927\u53d1\u6012\u3002 \u3000\u3000\u8c22\u957f\u537f\u4e0d\u670d\u7684\u51b7\u54fc\u4e00\u58f0\uff0c\u5fd6\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u529f\u592b\u597d\uff0c\u8fd9\u7b49\u72c2\u8a00\uff0c\u5012\u4e5f\u4e0d\u5e94\u51fa\u53e3\u2014\u2014\u201d \u3000\u3000\u8d64\u9633\u7b49\u4eba\u66f4\u662f\u6012\u6c14\u4e0a\u5347\uff0c\u957f\u5251\u4e00\u6446\uff0c\u5411\u5176\u4ed6\u4e09\u4eba\u6253\u4e00\u4e2a\u62db\u547c\u3002 \u3000\u3000\u539f\u6765\u5f53\u5e74\u521b\u7acb\u8fd9\u5251\u9635\u65f6\uff0c\u662f\u4e13\u4e3a\u8054\u624b\u5bf9\u4ed8\u9ad8\u624b\u7684\uff0c\u662f\u4ee5\u5251\u9635\u4e2d\u4e00\u5207\u653b\u654c\u7684\u62db\u5f0f\uff0c\u5168\u90fd\u7559 \u4e0b\u4e00\u4e8c\u5206\u4fdd\u5b88\u7684\u4f59\u5730\uff0c\u76ee\u7684\u662f\u6015\u5168\u90e8\u53d1\u51fa\u53bb\u5bf9\u65b9\u529f\u529b\u6bd4\u81ea\u5df1\u9ad8\uff0c\u4e0d\u6613\u6536\u56de\u6765\u3002 \u3000\u3000\u4e0d\u8fc7\u4ed6\u4eec\u8fd9\u5251\u9635\u4e2d\u4e5f\u6709\u62fc\u547d\u7684\u62db\u5f0f\uff0c\u548c\u6551\u547d\u7684\u5b88\u5f0f\u3002\u8fd9\u4e8c\u5f0f\u662f\u5728\u62b5\u4e0d\u4f4f\u654c\u4eba\u7684\u653b\u52bf\u65f6\uff0c \u800c\u4e00\u8d77\u5168\u62fc\u547d\u8fdb\u653b\uff0c\u5927\u6709\u548c\u654c\u4e24\u8d25\u4ff1\u4f24\u7684\u610f\u601d\u3002 \u3000\u3000\u8fd9\u4e24\u5f0f\u81ea\u6709\u8fd9\u5251\u9635\u4ee5\u6765\uff0c\u5e76\u672a\u7528\u8fc7\uff0c\u53ea\u6709\u90a3\u4e00\u5b88\u5f0f\u201c\u516b\u65b9\u98ce\u96e8\u201d\u5728\u6cf0\u5c71\u5927\u4f1a\u65f6\u4f7f\u8fc7\u4e00 \u6b21\u3002 \u3000\u3000\u8fd9\u4e00\u6765\u8f9b\u6377\u72c2\u8a00\u6fc0\u6012\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u90fd\u662f\u540d\u57a2\u4e00\u65b9\u7684\u4eba\u7269\uff0c\u6240\u8c13\u201c\u4e34\u6b7b\u4e0d\u8fb1\u201d\uff0c\u6025\u6012\u4e4b \u4e0b\uff0c\u4e0b\u51b3\u5fc3\u7528\u5168\u529b\u53bb\u548c\u8fd9\u4e24\u4e2a\u5c11\u5e74\u5468\u65cb\u3002 \u3000\u3000\u53ea\u89c1\u5389\u9e57\u6885\u9999\u5251\u5e73\u4e3e\uff0c\u4e00\u6446\u800c\u524a\u3002 \u3000\u3000\u5176\u4f59\u4e09\u4eba\u5404\u81ea\u957f\u5251\u4ea4\u4e3e\uff0c\u731b\u653b\u8fc7\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u662f\u56db\u5927\u5b97\u6d3e\u7684\u7edd\u62db\uff0c\u5524\u4f5c\u201c\u4e5d\u6b7b\u4e00\u751f\u201d\uff0c\u4f46\u89c1\u5251\u5149\u7f24\u7eb7\uff0c\u68ee\u68ee\u5251\u6c14\u4e2d\uff0c\u5404\u81ea\u6d41\u9732 \u51fa\u5fc5\u6b7b\u7684\u51b3\u5fc3\u3002 \u3000\u3000\u8f9b\u6377\u5434\u51cc\u98ce\u4e0d\u7531\u5927\u5403\u4e00\u60ca\uff0c\u4f46\u89c1\u56db\u65b9\u9762\u5251\u5e55\u5bd2\u68ee\uff0c\u6bcf\u4e00\u4e2a\u5730\u4f4d\u90fd\u6709\u5251\u5b50\u7b3c\u7f69\u7740\uff0c\u6210\u4e3a\u4e00 \u5f20\u5929\u7f57\u5730\u7f51\u3002 \u3000\u3000\u5434\u51cc\u98ce\u75be\u54fc\u4e00\u58f0\uff0c\u65ad\u9b42\u5251\u731b\u7136\u5f39\u8d77\uff0c\u4e00\u5f0f\u201c\u4e94\u9b3c\u65ad\u9b42\u201d\uff0c\u5de6\u53f3\u4e0a\u4e0b\u8fde\u70b9\u8fde\u6233\uff0c\u77ac\u606f\u4e4b\u95f4 \u5df2\u6253\u51fa\u5341\u4f59\u5251\u3002 \u3000\u3000\u5389\u9e57\u51b7\u51b7\u4e00\u54fc\uff0c\u6885\u9999\u5251\u4e00\u8f6c\uff0c\u7830\u7136\u78d5\u5230\u5434\u51cc\u98ce\u5251\u5b50\u4e0a\uff0c\u5434\u51cc\u98ce\u5403\u4e86\u4e00\u60ca\uff0c\u5fc3\u60f3\u5bf9\u65b9\u7adf\u8981 \u6bd4\u8bd5\u5185\u529b\uff1f\u5ff5\u5934\u4e00\u52a8\uff0c\u65ad\u9b42\u5251\u659c\u659c\u4e00\u6307\uff0c\u771f\u529b\u8d2f\u6ce8\u3002 \u3000\u3000\u54ea\u91cc\u77e5\u9053\u654c\u4eba\u653b\u4e2d\u6709\u865a\uff0c\u5389\u9e57\u8eab\u968f\u5251\u8d70\uff0c\u5251\u5149\u7eb7\u7eb7\u4e2d\uff0c\u5bf9\u65b9\u597d\u5feb\u7684\u884c\u52a8\uff0c\u201c\u7830\u201d\u6570\u54cd\uff0c \u5df2\u6709\u4e24\u4e09\u67c4\u5251\u4e00\u8d77\u51fb\u5728\u5434\u51cc\u98ce\u5251\u4e0a\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5230\u5e95\u7ecf\u9a8c\u5dee\u4e86\u4e00\u4e9b\uff0c\u8bef\u4ee5\u4e3a\u5bf9\u65b9\u8981\u4ee5\u5185\u529b\u5f3a\u62fc\uff0c\u51dd\u795e\u4ee5\u5f85\uff0c\u5c31\u662f\u5982\u6b64\u4e00\u6162\uff0c\u5bf9\u65b9 \u4fbf\u8f6c\u52a8\uff0c\u4e09\u5251\u90fd\u4ee5\u5168\u529b\u51fb\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5434\u51cc\u98ce\u4f46\u89c9\u5bf9\u65b9\u529f\u529b\u597d\u5927\uff0c\u624b\u5fc3\u4e00\u7d27\uff0c\u957f\u5251\u51e0\u4e4e\u8131\u624b\u800c\u98de\uff0c\u6025\u5fd9\u4e00\u51dd\u771f\u6c14\uff0c\u624d\u628a\u6301\u4f4f\u3002 \u5c31\u8fd9\u6837\u8eab\u4f53\u5df2\u540e\u9000\u534a\u5c3a\u3002 \u3000\u3000\u8f9b\u6377\u89c1\u72b6\u5927\u60ca\uff0c\u53cd\u624b\u4e00\u5251\u524a\u53bb\uff0c\u5e2e\u51cc\u98ce\u628a\u8eab\u540e\u88ad\u6765\u7684\u6570\u5251\u6321\u53bb\uff0c\u4f46\u95fb\u201c\u55e4\u201d\u7684\u4e00\u58f0\uff0c\u5434 \u51cc\u98ce\u7684\u8170\u5e26\u5df2\u88ab\u524a\u53bb\u4e00\u8282\u3002 \u3000\u3000\u5bf9\u65b9\u5251\u5f0f\u4e0d\u505c\uff0c\u4ea4\u76f8\u800c\u6233\uff0c\u5434\u51cc\u98ce\u5de6\u53f3\u963b\u6321\uff0c\u5f62\u52bf\u582a\u5371\uff0c\u8f9b\u6377\u51b7\u7136\u4e00\u54fc\uff0c\u957f\u5251\u731b\u7136\u4e00\u753b \u800c\u4e0b\u3002 \u3000\u3000\u52b2\u98ce\u8d77\u5904\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u8f9b\u6377\u7684\u957f\u5251\u53c8\u6025\u5954\u800c\u53bb\u3002 \u3000\u3000\u4f46\u89c1\u5251\u8679\u4e00\u5708\uff0c\u8f9b\u6377\u6025\u632f\u5251\u5c16\uff0c\u88ad\u5411\u6bcf\u4e00\u4e2a\u7531\u8eab\u524d\u7ecf\u8fc7\u7684\u654c\u4eba\u3002 \u3000\u3000\u8fd9\u4e00\u5f0f\u6b63\u662f\u201c\u5927\u884d\u795e\u5251\u201d\u7684\u8d77\u624b\u5f0f\uff1a\u201c\u65b9\u751f\u4e0d\u606f\u201d\u3002 \u3000\u3000\u5434\u51cc\u98ce\u7684\u201c\u9b3c\u738b\u628a\u706b\u201d\u4e5f\u662f\u72e0\u6bd2\u5df2\u6781\uff0c\u5feb\u901f\u4ee4\u4eba\u548b\u820c\uff0c\u6276\u7740\u4e00\u7f15\u5251\u98ce\u76f4\u5954\u800c\u53bb\u3002 \u3000\u3000\u201c\u53ee\u201d\u201c\u53ee\u201d\u6570\u58f0\uff0c\u8f9b\u6377\u5185\u52b2\u8d2f\u6ce8\uff0c\u5de6\u53f3\u8df3\u52a8\uff0c\u6bcf\u4e2a\u7ecf\u8fc7\u7684\u654c\u4eba\u7684\u957f\u5251\u548c\u4ed6\u7684\u5251\u5b50\u4e00 \u4ea4\uff0c\u201c\u5bdf\u201d\u201c\u5bdf\u201d\u5f39\u5f00\uff0c\u800c\u5434\u51cc\u98ce\u72e0\u6bd2\u7684\u5251\u5f0f\u7834\u9699\u800c\u5165\uff0c\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\u3002\u8fd8\u662f\u4ed6\u4eec\u7ecf\u9a8c\u8001 \u5230\uff0c\u4e34\u5371\u4e0d\u4e71\uff0c\u56db\u5251\u4fbf\u4ea4\u76f8\u4e00\u51fb\uff0c\u201c\u53ee\u201d\u7684\u4e00\u58f0\uff0c\u7a81\u78b0\u800c\u5206\uff0c\u6551\u547d\u4e4b\u5f0f\uff1a\u201c\u516b\u65b9\u98ce\u96e8\u201d\u5728\u5343 \u94a7\u4e00\u53d1\u4e4b\u9645\u4f7f\u51fa\uff0c\u624d\u7b97\u9003\u51fa\u5251\u5708\u3002 \u3000\u3000\u77ac\u606f\u95f4\uff0c\u56db\u4f4d\u638c\u95e8\u4eba\u53c8\u88ab\u903c\u5f97\u540e\u9000\u5bfb\u4e08\uff01 \u3000\u3000\u5434\u51cc\u98ce\u957f\u5251\u5012\u6325\uff0c\u522e\u8d77\u4e00\u7247\u6ce5\u571f\uff0c\u957f\u7b11\u9053\uff1a\u201c\u518d\u4e0a\u6765\u5427\u2014\u2014\u201d \u3000\u3000\u56db\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u9ed8\u7136\u4e0d\u8bed\uff0c\u8f9b\u6377\u76ee\u5149\u5982\u7535\uff0c\u626b\u8fc7\u6bcf\u4e00\u4e2a\u4eba\u7684\u9762\u5b54\uff0c\u53ea\u89c1\u5389\u9e57\u7626\u7684\u9a6c\u8138 \u4e0a\u9690\u9690\u95ea\u8fc7\u4e00\u4e1d\u72e0\u6bd2\u7684\u795e\u8272\uff0c\u679c\u7136\u4ed6\u4e00\u4e3e\u6885\u9999\u5251\u8d70\u4e86\u8fc7\u6765\u3002 \u3000\u3000\u8f9b\u6377\u770b\u89c1\u6885\u9999\u5251\u5728\u4ed6\u624b\u4e2d\uff0c\u5a01\u98ce\u516b\u9762\uff0c\u51b3\u5fc3\u5148\u593a\u56de\u5b9d\u5251\u518d\u8bf4\uff0c\u5fc3\u5ff5\u5373\u5b9a\uff0c\u5389\u9e57\u5df2\u6765\u5230\u8fd1 \u5904\u3002 \u3000\u3000\u8d64\u9633\uff0c\u82e6\u5eb5\uff0c\u8c22\u957f\u537f\u4e5f\u90fd\u6325\u5251\u4e0a\u524d\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u7136\u4e00\u7b11\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u957f\u5251\u4e00\u6325\uff0c\u5df2\u5708\u5411\u5389\u9e57\u3002 \u3000\u3000\u5251\u795e\u5389\u9e57\u4f55\u7b49\u7ecf\u9a8c\uff0c\u84e6\u5730\u4e00\u505c\u6b62\u884c\u52a8\uff0c\u6885\u9999\u5251\u4e00\u64a9\uff0c\u4fbf\u60f3\u63a5\u62db\u3002 \u3000\u3000\u51cc\u98ce\u5df2\u77e5\u8f9b\u6377\u8981\u593a\u5b9d\u5251\uff0c\u4e0d\u8ba9\u5176\u4ed6\u4eba\u4ece\u4e2d\u4e88\u4ee5\u5e72\u963b\uff0c\u4e5f\u633a\u5251\u51fb\u5411\u8d64\u9633\u9053\u58eb\uff0c\u4ed6\u76ee\u7684\u662f\u8981 \u56f0\u4f4f\u8d64\u9633\uff0c\u597d\u8ba9\u8f9b\u6377\u80fd\u65e0\u540e\u987e\u4e4b\u5fe7\u3002 \u3000\u3000\u662f\u4ee5\u4ed6\u4e00\u62db\u624d\u51fa\uff0c\u500f\u7136\u53c8\u6536\u518d\u51fa\uff0c\u4e00\u8fde\u653b\u51fa\u4e8e\u4f59\u5251\uff0c\u679c\u7136\u5c06\u8d64\u9633\u548c\u82e6\u5eb5\u7684\u8054\u624b\u4e4b\u52bf\u5c01\u5728 \u4e00\u8fb9\u3002 \u3000\u3000\u843d\u82f1\u5251\u5ba2\u8c22\u957f\u537f\u957f\u5578\u4e00\u58f0\uff0c\u957f\u5251\u4e00\u6446\uff0c\u627e\u4e2a\u7834\u7efd\uff0c\u659c\u659c\u6311\u5411\u5434\u51cc\u98ce\u7684\u201c\u7075\u53f0\u201d\u91cd\u7a74\u3002\u4ed6 \u8fd9\u62db\u76ee\u7684\u662f\u903c\u51cc\u98ce\u653e\u624b\uff0c\u51cc\u98ce\u679c\u89c9\u5251\u98ce\u88ad\u4f53\uff0c\u6025\u5fd9\u53cd\u624b\u524a\u51fa\u4e00\u5251\u3002 \u3000\u3000\u90a3\u8fb9\u8f9b\u6377\u4e00\u5f0f\u201c\u98de\u9601\u6d41\u821f\u201d\u5316\u4f5c\u201c\u7269\u6362\u661f\u79fb\u201d\uff0c\u5927\u884d\u5251\u62db\u7684\u7cbe\u534e\u8fde\u8fde\u65bd\u51fa\uff0c\u9976\u662f\u5389\u9e57\u5982 \u6b64\u529f\u529b\uff0c\u4e5f\u4e0d\u7531\u5931\u8272\u3002 \u3000\u3000\u8f9b\u6377\u8d8a\u6253\u8d8a\u731b\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u957f\u5251\u5e73\u7a7a\u62cd\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u5f0f\u8868\u9762\u770b\u6765\u6beb\u65e0\u53d8\u5316\u53ef\u8a00\uff0c\u4f46\u5374\u8574\u85cf\u7740\u591a\u79cd\u6740\u624b\u62db\u5f0f\uff0c\u5389\u9e57\u5fc3\u4e2d\u660e\u767d\uff0c\u4e0d\u7531\u5927\u5927\u5403 \u60ca\u3002 \u3000\u3000\u6479\u7136\uff0c\u5251\u795e\u5389\u9e57\u957f\u5578\u4e00\u58f0\uff0c\u6885\u9999\u5251\u5e73\u67b6\u800c\u4e0a\uff0c\u201c\u5f53\u201d\u7684\u548c\u8f9b\u6377\u7684\u5251\u5b50\u78b0\u4e2a\u6b63\u7740\u3002\u8f9b\u6377\u7684 \u5185\u529b\u4e00\u53d1\uff0c\u5251\u8d70\u8f7b\u7075\uff0c\u60f3\u8981\u5f39\u5f00\u4ed6\u7684\u5251\u800c\u4f7f\u6740\u624b\u3002 \u3000\u3000\u54ea\u91cc\u77e5\u9053\u5bf9\u65b9\u7262\u7262\u7c98\u4f4f\uff0c\u4e00\u80a1\u65e0\u540d\u529b\u9053\u7ef5\u7ef5\u4f20\u6765\uff0c\u597d\u50cf\u5728\u8fd9\u4e00\u523b\u95f4\uff0c\u5bf9\u65b9\u7684\u5185\u529b\u4fee\u4e3a\u7a81 \u589e\u4e86\u8bb8\u591a\u3002 \u3000\u3000\u8f9b\u6377\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u6687\u7ec6\u60f3\uff0c\u786c\u786c\u6536\u56de\u4e86\u5185\u529b\uff0c\u5316\u4f5c\u201c\u9ecf\u201d\u5b57\u8bc0\uff0c\u628a\u6885\u9999\u795e\u5251\u9ecf\u6301\u4f4f\uff0c\u8eab \u5f62\u518d\u66f2\u8eab\u800c\u8fdb\u3002 \u3000\u3000\u8fd9\u4e00\u5207\u90fd\u662f\u4e00\u77ac\u95f4\u7684\u4e8b\uff0c\u8f9b\u6377\u77e5\u9053\u5251\u9635\u8f6c\u52a8\u6781\u5feb\uff0c\u654c\u4eba\u7684\u653b\u5f0f\u5fc5\u8981\u7684\u4ece\u56db\u65b9\u516b\u800c\u6765\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8f9b\u6377\u957f\u5251\u4e00\u6446\uff0c\u5438\u80f8\u6536\u8179\u5de6\u624b\u4ee5\u4e00\u5f0f\u201c\u7a7a\u7a7a\u62f3\u62db\u201d\u4e2d\u7684\u201c\u767e\u5ff5\u7686 \u7a7a\u201d\uff0c\u95ea\u7535\u822c\u5411\u5389\u9e57\u808b\u4e0b\u62b9\u53bb\u3002 \u3000\u3000\u5389\u9e57\u6beb\u4e0d\u8fdf\u7591\uff0c\u817e\u8eab\u4fbf\u9000\uff0c\u4f46\u624b\u4e2d\u5251\u5b50\u5374\u88ab\u8f9b\u6377\u5185\u529b\u6240\u9ecf\uff0c\u4f7f\u4e0d\u51fa\u52b2\u9053\uff0c\u51b7\u51b7\u4e00\u5316\uff0c\u5de6 \u624b\u4e5f\u662f\u4e00\u5f0f\u53cd\u51fb\u8fc7\u6765\u3002 \u3000\u3000\u8f9b\u6377\u7a81\u89c9\u8eab\u540e\u5251\u98ce\u88ad\u4f53\uff0c\u5df2\u77e5\u654c\u624b\u653b\u6765\uff0c\u4e0d\u6562\u6020\u6162\u5206\u6beb\uff0c\u731b\u7136\u5185\u529b\u4e00\u6536\uff0c\u6446\u8131\u957f\u5251\uff0c\u5f80 \u540e\u4e00\u5212\uff0c\u8eab\u5f62\u968f\u7740\u659c\u98de\uff0c\u201c\u5f53\u201d\u7684\u4e00\u58f0\u679c\u7136\u8361\u5f00\u654c\u5251\uff0c\u540c\u65f6\u501f\u6b64\u4e00\u529b\uff0c\u53c8\u5012\u7a9c\u800c\u56de\uff0c\u8fce\u7740\u5389 \u9e57\u4e00\u638c\u731b\u7136\u51fb\u4e0b\u3002 \u3000\u3000\u8fd9\u4e00\u5355\u662f\u542b\u52b2\u800c\u53d1\uff0c\u5fae\u5e26\u864e\u864e\u98ce\u58f0\uff0c\u5f88\u662f\u60ca\u4eba\u3002\u5389\u9e57\u5374\u662f\u4e0d\u614c\u4e0d\u5fd9\uff0c\u4f46\u8138\u4e0a\u795e\u8272\u7a00\u5fae\u4e00 \u53d8\uff0c\u8fce\u9762\u800c\u51fb\u2026\u2026 \u3000\u3000\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u53cc\u638c\u76f8\u51fb\u2014\u2014 \u3000\u3000\u8f9b\u6377\u8eab\u4f53\u5c1a\u5728\u7a7a\u4e2d\uff0c\u53ea\u89c9\u4e00\u80a1\u529b\u9053\u731b\u649e\u4e4b\u4e0b\uff0c\u4e0d\u7531\u4e3a\u4e4b\u5931\u8272\uff0c\u4f5c\u68a6\u4e5f\u6599\u4e0d\u5230\u5389\u9e57\u5185\u529b\u7a81 \u8fdb\u5982\u6b64\u4e4b\u591a\u3002 \u3000\u3000\u5389\u9e57\u54c8\u54c8\u4e00\u7b11\uff0c\u6885\u9999\u5251\u631f\u4e00\u7f15\u5251\u98ce\uff0c\u95ea\u7535\u6311\u5411\u8f9b\u6377\uff0c\u5fc3\u4e2d\u6697\u559c\uff0c\u5fc3\u60f3\u81ea\u5df1\u5947\u8ba1\u5f97\u901e\uff0c\u8f9b \u6377\u5fc5\u4e0d\u80fd\u8eb2\u3002 \u3000\u3000\u7136\u800c\uff0c\u8f9b\u6377\u767e\u5fd9\u4e2d\u5927\u53f1\u4e00\u58f0\uff0c\u8eab\u5f62\u9661\u7136\u4e00\u65cb\u2014\u2014 \u3000\u3000\u8f9b\u6377\u957f\u5578\u4e00\u58f0\uff0c\u767e\u5fd9\u4e4b\u4e2d\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\u7a81\u65bd\uff0c\u8fd9\u6b65\u6cd5\u7384\u5999\u4e4b\u6781\uff0c\u8f9b\u6377\u8eab\u8eaf\u7adf\u7136\u5728\u7a7a\u4e2d\u56e0 \u65cb\u8f6c\u4e4b\u529b\u4e3a\u4e4b\u4e00\u505c\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8f9b\u6377\u84e6\u7136\u501f\u7740\u4ec5\u6709\u7684\u4e00\u70b9\u529b\u9053\u5411\u4e0a\u4e00\u7a9c\uff0c\u957f\u5251\u201c\u5f53\u201d \u7684\u67b6\u5728\u5389\u9e57\u6885\u9999\u5251\u4e0a\u3002 \u3000\u3000\u8fd9\u4e00\u53e3\u771f\u6c14\u5df2\u7136\u5230\u8fbe\u5b8c\u5168\u6d4a\u6df7\u7684\u65f6\u5019\uff0c\u8f9b\u6377\u957f\u5251\u642d\u5b9e\uff0c\u8f6c\u4e00\u53e3\u771f\u6c14\uff0c\u6574\u4e2a\u8eab\u5b50\u540a\u6302\u5728\u6885 \u9999\u5251\u4e0a\uff0c\u540c\u65f6\u5de6\u624b\u4f9d\u6837\u753b\u846b\u82a6\uff0c\u53c8\u662f\u4e00\u638c\u649e\u53bb\u3002 \u3000\u3000\u5389\u9e57\u4e00\u5251\u6311\u7a7a\uff0c\u95ea\u76ee\u654c\u4eba\u53c8\u653b\u5230\uff0c\u6012\u543c\u4e00\u58f0\uff0c\u5de6\u624b\u4e00\u7acb\u4e00\u626c\uff0c\u518d\u786c\u649e\u800c\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u5de6\u624b\u4e00\u7ffb\uff0c\u4e00\u5f0f\u201c\u4e07\u6cc9\u98de\u7a7a\u201d\uff0c\u628a\u5389\u9e57\u5343\u65a4\u5f80\u4e00\u8fb9\uff0c\u8fd9\u4e00\u5f0f\u4e43\u662f\u5927\u6222\u5c9b\u4e3b\u5fc3\u8840\u7814 \u7a76\uff0c\u6548\u679c\u975e\u540c\u51e1\u54cd\u3002 \u3000\u3000\u51e0\u4e4e\u662f\u540c\u65f6\u95f4\u91cc\uff0c\u53ea\u89c1\u8f9b\u6377\u5de6\u53f3\u9f50\u626c\uff0c\u649e\u5411\u5389\u9e57\u4e0b\u76d8\u3002 \u3000\u3000\u5389\u9e43\u62db\u5f0f\u8d70\u8001\uff0c\u8eab\u5b50\u4e0d\u7a33\uff0c\u654c\u62db\u5df2\u81f3\uff0c\u8fdb\u653b\u4e0d\u5f97\uff0c\u4e0d\u7531\u5927\u611f\u72fc\u72c8\uff0c\u8f9b\u6377\u6e05\u5578\u53eb\u9053\uff1a\u201c\u64a4 \u624b\u2014\u2014\u201d \u3000\u3000\u53f3\u624b\u5185\u529b\u9012\u589e\uff0c\u4e0b\u76d8\u518d\u98de\u51fa\u4e24\u817f\u3002 \u3000\u3000\u5389\u9e57\u5230\u5e95\u6b66\u6797\u9ad8\u624b\uff0c\u7ecf\u9a8c\u8001\u5230\uff0c\u5f53\u673a\u7acb\u65ad\uff0c\u53f3\u624b\u4e00\u677e\u957f\u5251\uff0c\u5de6\u624b\u95ea\u7535\u822c\u8d25\u4e2d\u6c42\u80dc\uff0c\u4e00\u62f3 \u6253\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u957f\u7b11\u4e00\u58f0\uff0c\u53f3\u624b\u4e00\u6311\uff0c\u6885\u9999\u5251\u7834\u7a7a\u800c\u8d77\uff0c\u5de6\u624b\u77a7\u4e5f\u4e0d\u77a7\uff0c\u67b6\u53bb\u5389\u9e57\u7684\u653b\u52bf\uff0c\u53f3\u624b\u4e00 \u626c\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u8fd9\u53eb\u505a\u7269\u5f52\u539f\u4e3b\uff0c\u5389\u8001\u513f\u670d\u5417\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u626c\u63b7\u51fa\u624b\u4e2d\u5e73\u51e1\u94a2\u5251\uff0c\u987a\u624b\u635e\u4f4f\u6885\u9999\u5251\uff0c\u8fd9\u63b7\u5251\uff0c\u53d1\u8bed\uff0c\u63a5\u5251\u4e00\u6c14\u5475\u6210\u3002\u795e\u5668\u5230 \u624b\uff0c\u8c6a\u6c14\u4e0a\u51b2\uff0c\u4e0d\u7981\u9f99\u541f\u957f\u5578\u4e00\u58f0\u3002 \u3000\u3000\u54ea\u77e5\u5389\u9e57\u4f55\u7b49\u7ecf\u9a8c\uff0c\u867d\u8d25\u4e0d\u9981\uff0c\u5de6\u62f3\u6363\u7a7a\uff0c\u53f3\u624b\u4e00\u62db\u4e00\u9053\u9752\u5149\u51b2\u5929\u800c\u8d77\uff0c\u76d8\u7ed5\u4e00\u531d\uff0c\u5012 \u5288\u800c\u4e0b\u3002 \u3000\u3000\u539f\u6765\u4ed6\u628a\u63e1\u826f\u673a\uff0c\u95ea\u7535\u62d4\u51fa\u501a\u8679\u5251\u3002 \u3000\u3000\u4ed6\u62d4\u5251\u4e4b\u5feb\uff0c\u5b9e\u5728\u4ee4\u4eba\u548b\u820c\uff0c\u8f9b\u6377\u51b7\u4e0d\u9632\u9752\u5149\u4e34\u9762\uff0c\u60f3\u95ea\u907f\u5df2\u6765\u4e0d\u53ca\u3002 \u3000\u3000\u603b\u7b97\u662f\u8f9b\u6377\u5929\u8d44\u5947\u4f73\uff0c\u8fed\u6709\u5947\u9047\uff0c\u81ea\u5e7c\u517b\u6210\u5feb\u7684\u53cd\u5e94\uff0c\u4e00\u79cd\u76f4\u89c9\u4fc3\u4f7f\u4ed6\u84e6\u7136\u4e00\u5f0f\u201c\u51e4\u70b9 \u5934\u201d\uff0c\u597d\u5728\u80fd\u907f\u8fc7\u3002 \u3000\u3000\u5389\u9e57\u5251\u5f0f\u5982\u8679\uff0c\u9752\u5149\u95ea\u5904\uff0c\u9976\u662f\u8f9b\u6377\u907f\u5f97\u5feb\uff0c\u540e\u9876\u5fc3\u7684\u53d1\u9afb\u513f\u4e5f\u88ab\u626b\u843d\u4e0b\u6570\u6839\u5934\u53d1\u6765\uff01 \u3000\u3000\u8f9b\u6377\u867d\u907f\u8fc7\u9669\u62db\uff0c\u4f46\u4e5f\u60ca\u5f97\u51fa\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u5bf9\u5389\u9e57\u6b64\u7b49\u7ecf\u9a8c\u548c\u673a\u53d8\uff0c\u4e5f\u4e0d\u7531\u6df1\u6df1\u611f\u5230\u94a6 \u4f69\u3002 \u3000\u3000\u8f9b\u6377\u5b9a\u4e86\u5b9a\u795e\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u770b\u6765\u5389\u8001\u5934\u5b50\u8fd8\u85cf\u4e86\u7edd\u6280\u54e9\uff0c\u6709\u672c\u9886\u5c3d\u7ba1\u4f7f\u6765\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u6b64\u8bed\u4e43\u662f\u7531\u4e8e\u611f\u5230\u5389\u9e57\u5185\u529b\u7a81\u52a0\u7684\u539f\u6545\u3002 \u3000\u3000\u5012\u7ed9\u4ed6\u8bf4\u5bf9\u4e86\uff0c\u539f\u6765\u5251\u795e\u5389\u9e57\u5f97\u4e86\u672c\u95e8\u79d8\u5b66\u201c\u4e0a\u6e05\u6c14\u529f\u201d\u7684\u79d8\u7b08\uff0c\u7acb\u523b\u95ed\u5173\u82e6\u7ec3\uff0c\u90a3\u65e5 \u4e8e\u4e00\u98de\u62ff\u8f9b\u6377\u7684\u62dc\u8d34\u7ed9\u4ed6\u770b\uff0c\u4ed6\u7acb\u523b\u4e0b\u4e66\u7ed9\u70b9\u82cd\u8c22\u957f\u537f\uff0c\u800c\u4e14\u4ed6\u4e5f\u628a\u4e0a\u6e05\u6c14\u529f\u7684\u7cbe\u9ad3\u953b\u70bc\u4e86 \u5927\u534a\uff0c\u660e\u77e5\u8eb2\u4e0d\u8fc7\uff0c\u7acb\u523b\u52a8\u7a0b\u5357\u5954\u4e94\u534e\uff0c\u4ed6\u5230\u5e95\u662f\u8001\u5978\u5de8\u6ed1\u7684\u4eba\u7269\uff0c\u552f\u6050\u81ea\u5df1\u7684\u529f\u529b\u5c1a\u4e0d\u8db3 \u4ee5\u654c\u8f9b\u6377\uff0c\u662f\u4ee5\u4e00\u76f4\u9690\u4f0f\u7740\u529f\u529b\u3002 \u3000\u3000\u5230\u4e86\u8f9b\u6377\u593a\u5251\u7684\u65f6\u5019\uff0c\u624d\u7a81\u4f7f\u4e0a\u6e05\u6c14\u529f\uff0c\u679c\u7136\u5148\u4eba\u7684\u5947\u529f\u975e\u540c\u5c0f\u53ef\uff0c\u4e00\u51fb\u4e4b\u4e0b\uff0c\u51e0\u81f4\u7f6e \u8f9b\u6377\u4e8e\u6b7b\u5730\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u6765\uff0c\u80c6\u6c14\u7a0d\u58ee\uff0c\u4f46\u89c1\u8f9b\u6377\u5728\u4e34\u5371\u65f6\u53c8\u4f7f\u51fa\u90a3\u5947\u602a\u7684\u6b65\u6cd5\u65f6\uff0c\u60f3\u8d77\u6cf0\u5c71\u5927\u4f1a\u65f6\uff0c\u5bf9 \u65b9\u4fbf\u8d56\u4ee5\u7834\u9635\uff0c\u4e0d\u7531\u53c8\u5927\u5927\u6c14\u9981\u3002 \u3000\u3000\u5389\u9e57\u5077\u88ad\u53c8\u4e0d\u6210\uff0c\u5fc3\u4e2d\u8fde\u8fde\u53f9\u60dc\uff0c\u5251\u9635\u4e1d\u6beb\u4e0d\u505c\uff0c\u8f9b\u6377\u8eab\u540e\u5df2\u6709\u51e0\u5251\u653b\u5230\uff0c\u8f9b\u6377\u77a7\u90fd\u4e0d \u77a7\uff0c\u53cd\u624b\u4e1c\u524a\u76f8\u67b6\uff0c\u5316\u5f00\u653b\u52bf\uff0c\u50b2\u7136\u9053\uff1a\u201c\u59d3\u5389\u7684\u2014\u2014\uff0c\u4f60\u6ce8\u610f\u4e86\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u4e0d\u6b47\uff0c\u957f\u5251\u5982\u8679\u800c\u8d77\u3002 \u3000\u3000\u4ed6\u6885\u9999\u5251\u5230\u624b\uff0c\u4e0d\u518d\u72b9\u8c6b\uff0c\u8bd8\u6469\u6b65\u6cd5\u9661\u4f7f\uff0c\u8eab\u6cd5\u771f\u662f\u6709\u5982\u9b3c\u9b41\uff0c\u5de6\u53f3\u70b9\u6d88\uff0c\u5251\u5149\u95ea\u95ea\uff0c \u5434\u51cc\u98ce\u4e5f\u77e5\u5230\u4e86\u65f6\u5019\uff0c\u65ad\u9b42\u5251\u672f\u7a81\u53d1\u7a81\u53d8\uff0c\u914d\u5408\u5feb\u6377\u65e0\u5339\u7684\u5929\u7afa\u8eab\u6cd5\uff0c\u4e4d\u770b\u8fc7\u53bb\uff0c\u771f\u662f\u6709\u5982 \u4e00\u9053\u8679\u5149\u5e73\u7a7a\u800c\u8d77\uff0c\u58f0\u52bf\u60ca\u4eba\u4e4b\u6781\u3002 \u3000\u3000\u201c\u5bdf\u201d\u201c\u5bdf\u201d\u6570\u5251\uff0c\u53cc\u5251\u5408\u74a7\uff0c\u5de6\u53f3\u6447\u547c\uff0c\u4e24\u4eba\u90fd\u5728\u4ea4\u7ec7\u5251\u7f51\u7684\u5251\u9635\u4e2d\u63a0\u51fa\u8fd9\u540d\u9707\u5929\u4e0b \u7684\u5251\u9635\u3002 \u3000\u3000\u8fd9\u4e00\u6765\uff0c\u56db\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u66f4\u662f\u81ea\u77e5\u51f6\u591a\u5409\u5c11\u3002 \u3000\u3000\u8f9b\u6377\u6beb\u4e0d\u505c\u7559\uff0c\u8eab\u5b50\u4e00\u63a0\uff0c\u624d\u51fa\u5251\u9635\uff0c\u4fbf\u4e00\u5251\u6233\u5411\u5389\u9e57\uff0c\u540c\u65f6\u95f4\uff0c\u5de6\u638c\u4e5f\u53d1\u52a8\u653b\u52bf\uff0c\u6253 \u5411\u82e6\u5eb5\u3002 \u3000\u3000\u4e24\u4eba\u51fa\u5f97\u5251\u9635\uff0c\u72b9\u5982\u9c7c\u5f97\u6c34\uff0c\u957f\u5251\u5de6\u53f3\u8fde\u6446\uff0c\u4f46\u95fb\u201c\u5bdf\u201d\u201c\u5bdf\u201d\u6570\u54cd\uff0c\u8f9b\u6377\u5df2\u548c\u5389\u9e57\u7684 \u5251\u5b50\u76f8\u642d\u3002 \u3000\u3000\u4e24\u4eba\u5185\u529b\u9f50\u51fa\uff0c\u5389\u9e57\u77e5\u9053\u80dc\u8d25\u5728\u6b64\uff0c\u201c\u563f\u201d\u7136\u547c\u53eb\uff0c\u4e0a\u6e05\u6c14\u529f\u5df2\u7136\u53d1\u52a8\u3002\u4f46\u89c1\u4e24\u67c4\u795e\u5251 \u5251\u8eab\u201c\u5bdf\u201d\u201c\u5bdf\u201d\u5f39\u8d77\uff0c\u8f9b\u6377\u94c1\u8155\u4e00\u632f\uff0c\u201c\u6258\u201d\u7684\u4e00\u58f0\uff0c\u5389\u94a8\u518d\u4e5f\u628a\u4e0d\u4f4f\uff0c\u8131\u624b\u800c\u98de\u3002 \u3000\u3000\u5251\u795e\u5389\u9e57\u867d\u8d25\u4e0d\u4e71\uff0c\u5de6\u638c\u629a\u80f8\uff0c\u53f3\u62f3\u98d5\u7136\u6253\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u957f\u7b11\u4e00\u58f0\uff0c\u968f\u624b\u628a\u5251\u63d2\u5165\u571f\u4e2d\uff0c\u6325\u638c\u4e00\u51fb\u3002 \u3000\u3000\u4ed6\u7528\u7684\u529b\u9053\u597d\u602a\uff0c\u4e09\u5206\u53d1\uff0c\u4e03\u5206\u6536\uff0c\u4e00\u89e6\u4e4b\u4e0b\uff0c\u731b\u7136\u4e00\u5e26\uff0c\u201c\u62cd\u201d\u7684\u53cc\u638c\u76f8\u89e6\uff0c\u8f9b\u6377\u529b \u9053\u731b\u5438\uff0c\u767b\u65f6\u7262\u7262\u548c\u5389\u9e57\u5355\u638c\u76f8\u7c98\uff0c\u6bd4\u8bd5\u8d77\u5185\u529b\u6765\u3002 \u3000\u3000\u90a3\u8fb9\uff0c\u5434\u51cc\u98ce\u957f\u5251\u5982\u8679\uff0c\u62b5\u4f4f\u4e09\u4eba\uff0c\u5251\u5f0f\u9661\u7d27\uff0c\u5f3a\u903c\u4e09\u4eba\u5411\u5de6\u8fb9\u79fb\u52a8\uff0c\u4ed6\u548c\u8f9b\u6377\u662f\u6709\u9ed8 \u5951\u7684\uff0c\u662f\u4ee5\u628a\u8d64\u9633\u7b49\u4e09\u4eba\u90fd\u5411\u5de6\u89d2\u79fb\u52a8\u3002 \u3000\u3000\u5389\u9e57\u548c\u8f9b\u6377\u6bd4\u8bd5\u5185\u529b\uff0c\u5df2\u7136\u5206\u51fa\u4e0a\u4e0b\uff0c\u4e0a\u6e05\u6c14\u529f\u867d\u662f\u795e\u52c7\uff0c\u4ecd\u975e\u8f9b\u6377\u654c\u624b\uff0c\u6e10\u6e10\u5f80\u540e \u9000\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u548c\u8d64\u9633\u9053\u957f\u90fd\u660e\u767d\u8fd9\u4e2a\u60c5\u5f62\uff0c\u82e6\u4e8e\u4e0d\u80fd\u8131\u624b\u76f8\u63f4\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5251\u5f0f\u5982\u98ce\uff0c\u7f20\u7740\u4e09\u4eba\u3002 \u3000\u3000\u84e6\u7136\uff0c\u8d64\u9633\u548c\u8c22\u957f\u537f\u4e00\u53cc\u5251\u5b50\u903c\u4f4f\u51cc\u98ce\u5251\u62db\uff0c\u82e6\u5eb5\u4e58\u52bf\u8131\u51fa\uff0c\u5954\u5411\u5389\u9e57\uff0c\u5434\u51cc\u98ce\u5927\u5403\u4e00 \u60ca\uff0c\u957f\u5251\u4e00\u5708\uff0c\u6ca1\u6709\u62e6\u4f4f\uff0c\u90a3\u8fb9\u82e6\u5eb5\u5df2\u5954\u5230\u8fd1\u5904\uff0c\u4e00\u624b\u642d\u5728\u5251\u795e\u5389\u9e57\u80cc\u5fc3\u4e0a\uff0c\u4e00\u80a1\u5185\u529b\u4f20\u4e86 \u8fc7\u53bb\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u7684\u5185\u529b\u9020\u5012\u5e95\u4e0d\u51e1\uff0c\u8f9b\u6377\u4f46\u89c9\u624b\u4e0a\u4e00\u6c89\u3002 \u3000\u3000\u5fc3\u795e\u4e0d\u7531\u4e00\u8361\uff0c\u5fd9\u563f\u7136\u4e00\u58f0\uff0c\u9ed8\u8fd0\u771f\u529b\uff0c\u5b88\u4f4f\u5fc3\u795e\u3002 \u3000\u3000\u90a3\u8fb9\u5434\u51cc\u98ce\u89c1\u8f9b\u6377\u5e76\u65e0\u6301\u4e0d\u4f4f\u7684\u73b0\u8c61\uff0c\u957f\u5251\u6108\u5feb\uff0c\u8fde\u4e0b\u715e\u624b\uff0c\u56db\u5927\u5251\u6d3e\u7684\u5251\u9635\u5df2\u7834\uff0c\u53ea \u5269\u4e0b\u8d64\u9633\u548c\u8c22\u957f\u537f\u62b5\u4f4f\u5434\u51cc\u98ce\u7684\u653b\u52bf\uff0c\u8c22\u957f\u537f\u5df2\u6597\u51fa\u8c6a\u5174\uff0c\u201c\u4e03\u7edd\u201d\u8eab\u6cd5\u8fde\u8fde\u5c55\u51fa\uff0c\u4e00\u65f6\u4e0d \u81f4\u843d\u8d25\u3002 \u3000\u3000\u8d64\u9633\u9053\u58eb\u4e45\u6218\u4e0d\u654c\uff0c\u5fc3\u4e2d\u7126\u8e81\uff0c\u84e6\u7136\u5927\u5316\u4e00\u58f0\uff0c\u624b\u4e2d\u957f\u5251\u4e00\u6446\uff0c\u4e00\u5f0f\u201c\u4e5d\u5bab\u795e\u884c\u5251\u201d\u4e2d \u7684\u201c\u5954\u7535\u5165\u96f7\u201d\uff0c\u5ffd\u7136\u5316\u4f5c\u865a\u62db\u95ea\u8eab\u800c\u9000\u3002 \u3000\u3000\u5434\u51cc\u98ce\u4e0d\u865e\u6709\u6b64\uff0c\u968f\u5373\u9192\u609f\u8d64\u9633\u9053\u4e43\u662f\u8981\u4e58\u8f9b\u6377\u7528\u529b\u4e0d\u80fd\u9632\u5907\u4e4b\u9645\uff0c\u53bb\u4e0b\u6bd2\u624b\uff0c\u5fc3\u4e2d\u5927 \u60ca\uff0c\u65ad\u9b42\u4e00\u5251\u4e00\u8f6c\uff0c\u67b6\u5f00\u8c22\u957f\u537f\u7684\u4e00\u5251\uff0c\u52c9\u5f3a\u5411\u5de6\u8fb9\u4e00\u4fa7\uff0c\u5954\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u4f46\u95fb\u55e4\u7684\u4e00\u58f0\uff0c\u5434\u51cc\u98ce\u5df2\u5c3d\u529b\u95ea\u8eb2\uff0c\u4f46\u4ecd\u88ab\u6311\u7834\u4e00\u9053\u53e3\u5b50\uff0c\u800c\u8d64\u9633\u5df2\u53bb\u4e86\u4e24\u4e09\u4e08\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5927\u53f1\u4e00\u58f0\uff0c\u4ed6\u6df1\u77e5\u8d64\u9633\u9053\u4eba\u7684\u5fc3\u80a0\uff0c\u77e5\u9053\u4ed6\u4e0b\u624b\u4e0d\u77e5\u7f9e\u803b\uff0c\u8fd9\u4e00\u6025\uff0c\u4e0d\u987e\u4e00\u5207\uff0c \u4e00\u5f0f\u201c\u5e73\u6c99\u843d\u96c1\u201d\uff0c\u771f\u6c14\u8d2f\u6ce8\u4e4b\u9645\uff0c\u5929\u7afa\u8eab\u6cd5\u5df2\u7136\u4f7f\u51fa\u3002 \u3000\u3000\u5929\u7afa\u8eab\u5230\u5e95\u540d\u4e0d\u865a\u4f20\uff0c\u51cc\u98ce\u8eab\u7b80\u76f4\u6709\u82e5\u4e00\u7f15\u6e05\u70df\uff0c\u4e00\u8d77\u4e00\u843d\uff0c\u4fbf\u8ffd\u5230\u8d64\u9633\u8eab\u540e\u3002\u8d64\u9633\u5fc3 \u4e2d\u751a\u662f\u7126\u6025\uff0c\u811a\u4e0b\u62fc\u547d\u52a0\u52b2\uff0c\u4f46\u53ea\u95fb\u547c\u7684\u4e00\u58f0\uff0c\u8eab\u540e\u5df1\u6709\u98ce\u58f0\uff0c\u4e0d\u7531\u5927\u5403\u60ca\uff0c\u8fd9\u5434\u51cc\u98ce\u7684\u8f7b \u529f\u771f\u662f\u8d85\u51e1\u5165\u5723\u4e86\u3002 \u3000\u3000\u8d64\u9633\u9053\u4eba\u5927\u9a87\u4e4b\u4e0b\uff0c\u8fd0\u8db3\u5185\u529f\uff0c\u4f7f\u51fa\u6b66\u5f53\u6d3e\u7684\u9547\u5c71\u4e4b\u6280\uff0c\u4e5f\u662f\u4ed6\u591a\u5e74\u6765\u6ca1\u6709\u4f7f\u201c\u4e7e\u5143 \u6307\u201d\uff0c\u9065\u9065\u70b9\u5411\u8f9b\u6377\u3002 \u3000\u3000\u8fd9\u4e7e\u5143\u6307\u5a01\u529b\u751a\u5927\uff0c\u5434\u51cc\u98ce\u6025\u5f97\u53cc\u76ee\u5168\u8d64\uff0c\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u5f62\u51cc\u7a7a\u800c\u8d77\uff0c\u4e00\u5f0f\u201c\u5929\u9a6c\u884c \u7a7a\u201d\uff0c\u98de\u8fc7\u8d64\u9633\u5934\u9876\uff0c\u540c\u65f6\u95f4\u91cc\uff0c\u4e00\u811a\u731b\u5f80\u4e0b\u8dfa\u4e86\u4e0b\u53bb\u3002 \u3000\u3000\u8d64\u9633\u9053\u58eb\u4e0d\u6599\u5434\u51cc\u98ce\u5df2\u51cc\u4e0a\u7a7a\uff0c\u672c\u80fd\u7684\u505c\u8eab\uff0c\u4e00\u62db\u201c\u51e4\u70b9\u5934\u201d\uff0c\u52c9\u5f3a\u907f\u8fc7\uff0c\u5c31\u8fd9\u803d\u6401\uff0c \u8f9b\u6377\u5df2\u817e\u51fa\u624b\uff0c\u53cd\u638c\u62b5\u4f4f\u4ed6\u7684\u653b\u52bf\u3002 \u3000\u3000\u5434\u51cc\u98ce\u8eab\u5c1a\u5728\u7a7a\u4e2d\uff0c\u9661\u7136\u95f4\u5251\u5149\u88ad\u4f53\uff0c\u77a5\u76ee\u4e00\u770b\uff0c\u539f\u6765\u662f\u8c22\u957f\u537f\u968f\u540e\u653b\u5230\uff0c\u7ffb\u8eab\u4e0b\u6765\u8fce \u6218\u3002 \u3000\u3000\u8f9b\u6377\u4ee5\u4e00\u654c\u4e09\uff0c\u594b\u52c7\u4ee5\u6301\uff0c\u54c8\u54c8\u53eb\u9053\uff1a\u201c\u5404\u4f4d\u5927\u638c\u95e8\uff0c\u6211\u60f3\u4f60\u4eec\u5e94\u8bb0\u5f97\u5341\u5e74\u524d\uff0c\u4e5f\u662f\u7531 \u795e\u541b\u548c\u4e09\u4f4f\u5927\u4fa0\u6bd4\u5212\uff0c\u800c\u56e0\u8c22\u8001\u5e08\u8d50\u6559\u2014\u2014\u201d \u3000\u3000\u5389\u9e57\u4e58\u8f9b\u6377\u53e3\u4e2d\u8bf4\u8bdd\u5206\u795e\uff0c\u4e0a\u6e05\u6c14\u529f\u9661\u65bd\u5168\u529b\uff0c\u8f9b\u6377\u4f46\u89c9\u624b\u5fc3\u4e00\u70ed\uff0c\u5185\u529b\u731b\u5410\uff0c\u786c\u786c\u62b5 \u4f4f\uff0c\u53e3\u4e2d\u7b11\u8bed\u4e0d\u7edd\uff01 \u3000\u3000\u201c\u8c22\u8001\u5e08\u4e03\u7edd\u624b\u6cd5\uff0c\u795e\u541b\u62dc\u8d50\u4e00\u6307\uff0c\u679c\u662f\u540d\u4e0d\u865a\u4f20\u2026\u2026\u201d \u3000\u3000\u4ed6\u53e3\u53e3\u58f0\u58f0\uff0c\u8bed\u8bed\u8bbd\u523a\uff0c\u8c22\u957f\u537f\u5fc3\u5982\u5200\u5272\uff0c\u957f\u5251\u731b\u7136\u4e00\u9707\uff0c\u5434\u51cc\u98ce\u987f\u89c9\u5bf9\u65b9\u529b\u9053\u597d\u5927\uff0c \u65b9\u5f97\u4e00\u6014\uff0c\u8c22\u957f\u537f\u5df2\u8dc3\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u843d\u82f1\u5251\u9762\u8272\u94c1\u9752\uff0c\u53e3\u5659\u51b7\u7b11\u9053\uff1a\u201c\u59d3\u8f9b\u7684\uff0c\u54b1\u4eec\u51a4\u6709\u5934\uff0c\u503a\u6709\u4e3b\uff0c\u8fd8\u6709\u8fd9\u4f4d\u59d3\u5434\u7684\uff0c\u5f53 \u5e74\u6cb3\u6d1b\u4e00\u5251\u5355\u5251\u65ad\u9b42\u5434\u8bcf\u4e91\u5929\u7ec5\u7011\u524d\u51fb\u6bd9\u6211\u7236\uff0c\u8fd9\u4e00\u6069\u4ec7\u5230\u6211\u8c22\u67d0\u4e3a\u6b62\uff0c\u4e00\u7b14\u52fe\u9500\u2026\u2026\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u6a2a\u5251\u4fbf\u5f80\u5934\u4e0a\u62b9\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u672c\u5bf9\u957f\u537f\u751a\u4e3a\u597d\u611f\uff0c\u8bf4\u8fd9\u4e00\u756a\u8bdd\u53ea\u4e0d\u8fc7\u5fc3\u5b58\u8bbd\u523a\u800c\u5df2\uff0c\u4e0d\u60f3\u5bf9\u65b9\u591a\u5c11\u5e74\u6765\uff0c\u65e5\u65e5\u591c \u591c\u5f15\u4ee5\u4e3a\u61be\u7684\u4e5f\u72ec\u6b64\u4e00\u4e8b\uff0c\u8fd9\u53ef\u8c13\u201c\u58eb\u53ef\u6740\u4e0d\u53ef\u8fb1\u201d\uff0c\u843d\u82f1\u5251\u4f55\u7b49\u521a\u70c8\uff0c\u7acb\u840c\u6b7b\u5fd7\u3002 \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\u2014\u2014 \u3000\u3000\u5434\u51cc\u98ce\u5927\u5316\u4e00\u58f0\u9053\uff1a\u201c\u4f4f\u624b\u2014\u2014\u201d \u3000\u3000\u8fd9\u4e00\u58f0\u4e43\u662f\u5434\u51cc\u98ce\u5168\u8eab\u6c14\u529f\u4e4b\u7ed3\u96c6\uff0c\u58f0\u97f3\u6709\u5982\u91d1\u9f13\u77f3\u949f\u4e4b\u9e23\uff0c\u76f4\u53ef\u88c2\u77f3\u3002\u5728\u573a\u7684\u5168\u662f\u4e00 \u7b49\u4e00\u7684\u9ad8\u624b\uff0c\u4e5f\u4e0d\u89c9\u611f\u5230\u5fae\u5fae\u4e00\u9707\u3002 \u3000\u3000\u5434\u51cc\u98ce\u597d\u5feb\u7684\u8eab\u6cd5\uff0c\u7b49\u8c22\u957f\u537f\u4e00\u6014\u4e4b\u9645\uff0c\u5df1\u67b6\u4f4f\u843d\u82f1\u5251\uff0c\u8c22\u957f\u537f\u5fae\u5fae\u4e00\u58f0\u9053\uff1a\u201c\u597d\uff01\u59d3 \u5434\u7684\uff0c\u4f60\u8fd8\u4e0d\u6ee1\u610f\u4e48\u2026\u2026\u201d \u3000\u3000\u5434\u51cc\u98ce\u6717\u7b54\u9053\uff1a\u201c\u8c22\u8001\u5e08\u5343\u4e07\u4e0d\u8981\u8bef\u4f1a\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u4ed6\u5230\u5e95\u51fa\u9053\u4e4b\u65f6\u4e0d\u4e45\uff0c\u7ecf\u9a8c\u4e0d\u591a\uff0c\u4e0d\u77e5\u5982\u4f55\u8ff0\u660e\uff0c\u662f\u4ee5\u201c\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u4e24\u58f0\uff0c\u63a5\u4e0d\u4e0a \u8bdd\u6765\uff0c\u6025\u5f97\u4fca\u8138\u901a\u7ea2\u3002 \u3000\u3000\u84e6\u5730\u91cc\uff0c\u6885\u5f71\u540e\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u63a5\u53e3\u9053\uff1a\u201c\u8c22\u4e16\u5144\uff0c\u4f60\u77a7\u6211\u662f\u8c01\uff1f\u2026\u2026\u201d \u3000\u3000\u6885\u5f71\u4e4b\u540e\uff0c\u7a81\u51fa\u4eba\u58f0\uff0c\u800c\u4e14\u8fd9\u58f0\u8c03\u597d\u4e0d\u51b7\u51b0\uff0c\u8c22\u957f\u537f\u5fae\u5fae\u4e00\u6014\uff0c\u6885\u5f71\u4ea4\u9519\u4e4b\u95f4\uff0c\u7f13\u7f13\u5761 \u51fa\u4e00\u4e2a\u8001\u4eba\u3002 \u3000\u3000\u8c22\u957f\u537f\u6115\u7136\u4e00\u60ca\uff0c\u8138\u5982\u6b7b\u7070\uff0c\u534a\u664c\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u5728\u573a\u7684\u5404\u6d3e\u638c\u95e8\uff0c\u4e2a\u4e2a\u4e5f\u662f\u5982\u6b64\uff0c\u51fa\u73b0\u4ed6\u4eec\u773c\u524d\u7684\uff0c\u6b63\u662f\u4ed6\u4eec\u5341\u4f59\u5e74\u524d\u7528\u9b3c\u8ba1\u6697\u7b97\u800c\u7f6e \u4e8e\u6b7b\u5730\u7684\u795e\u5dde\u5357\u541b\u2014\u2014\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\uff01 \u3000\u3000\u6885\u5c71\u6c11\u6614\u5e74\u95ef\u8361\u5927\u6c5f\u5357\u5317\uff0c\u884c\u4e8b\u7d20\u4ee5\u51b7\u9177\u4e3a\u540d\uff0c\u8bf4\u51fa\u8bdd\u6765\uff0c\u4ecd\u8131\u4e0d\u4e86\u8fd9\u4e2a\u4e60\u6027\uff0c\u4ed6\u8fd9\u4e00 \u8bed\uff0c\u867d\u662f\u5e73\u5fc3\u9759\u6c14\uff0c\u4f46\u8bdd\u91cc\u97f5\u5473\uff0c\u5374\u81ea\u7136\u6709\u4e00\u79cd\u51b7\u51b7\u5473\u9053\uff0c\u6bd4\u8d77\u5389\u9e57\u90a3\u79cd\u88c5\u8154\u4f5c\u52bf\u7684\u8bf4\u8bdd\u8fd8 \u8981\u6709\u8fc7\u4e4b\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u8fd9\u4e00\u51fa\u73b0\uff0c\u56db\u5927\u5b97\u6d3e\u7684\u638c\u95e8\u4eba\u90fd\u5927\u60ca\u7279\u60ca\uff0c\u5fc3\u6b7b\u5982\u7070\uff0c\u6885\u5c71\u6c11\u5374\u6b63\u773c\u4e5f\u4e0d\u77a7\u4ed6 \u4eec\u4e00\u77a7\uff0c\u7f13\u7f13\u5411\u8c22\u957f\u537f\u9053\uff1a\u201c\u5929\u5c06\u5927\u4efb\u4e8e\u65af\u4eba\u4e5f\uff0c\u5fc5\u5148\u82e6\u5176\u5fc3\u5fd7\uff0c\u52b3\u5176\u7b4b\u9aa8\uff0c\u997f\u5176\u4f53\u80a4\uff0c\u7a7a \u4e4f\u5176\u8eab\uff0c\u884c\u62c2\u4e71\u5176\u6240\u4e3a\uff0c\u6240\u4ee5\u52a8\u5fc3\u5fcd\u6027\uff0c\u589e\u76ca\u5176\u6240\u4e0d\u80fd\uff0c\u4eba\u6052\u8fc7\uff0c\u7136\u540e\u80fd\u6539\u3002\u201d \u3000\u3000\u4e03\u5999\u795e\u541b\u5f53\u5e74\u4ee5\u6587\u6b66\u5168\u624d\u79f0\u8457\uff0c\u4ed6\u65e9\u5728\u5341\u5e74\u524d\u548c\u8c22\u957f\u537f\u4f1a\u7b2c\u4e00\u6b21\u9762\u7684\u65f6\u5019\uff0c\u4ed6\u4fbf\u6df1\u6df1\u5730 \u4e86\u89e3\u8c22\u957f\u537f\u7684\u5fc3\u5883\u3002\u4ed6\u5e38\u5e38\u81ea\u5fd6\uff1a\u201c\u5047\u82e5\u662f\u6211\uff0c\u6211\u4f1a\u600e\u6837\u54e9\uff1f\u201d \u3000\u3000\u867d\u7136\uff0c\u8c22\u957f\u537f\u7684\u4e00\u6307\uff0c\u5bf9\u4e8e\u4ed6\u7684\u529f\u592b\uff0c\u751a\u81f3\u751f\u547d\uff0c\u90fd\u6709\u51b3\u5b9a\u6027\u7684\u5f71\u54cd\uff0c\u4f46\u662f\u4ed6\u4ece\u5fc3\u5e95 \u91cc\uff0c\u5b8c\u5168\u80fd\u89c1\u8c05\u4e8e\u4ed6\uff0c\u4eba\u90fd\u8bf4\u6885\u5c71\u6c11\u5fc3\u91cf\u7a84\u72ed\uff0c\u6709\u4ec7\u5fc5\u62a5\uff0c\u4f46\u4ed6\u5bf9\u8c22\u957f\u537f\u7684\u5bbd\u5bb9\uff0c\u96be\u9053\u4e0d\u662f \u6055\u9053\u5417\uff1f \u3000\u3000\u8f9b\u6377\u51cc\u98ce\u5bf9\u8c22\u957f\u537f\u90fd\u6709\u597d\u611f\uff0c\u4f46\u662f\u4ed6\u4e43\u662f\u5e9f\u53bb\u6885\u5c71\u6c11\u6b66\u529f\u7684\u6b63\u70b9\u513f\uff0c\u8fd9\u65f6\u89c1\u6885\u53d4\u53d4\u51fa\u6b64 \u8bed\uff0c\u5fc3\u4e2d\u5df2\u77e5\u6885\u53d4\u53d4\u539f\u8c05\u4e86\u843d\u82f1\u5251\uff0c\u5fc3\u4e2d\u4e0d\u7981\u4e00\u559c\u3002 \u3000\u3000\u8c22\u957f\u537f\u4ece\u6885\u5c71\u6c11\u4e00\u51fa\u73b0\uff0c\u5fc3\u4e2d\u4e07\u5ff5\u4ff1\u7070\uff0c\u4e00\u65f6\u4e4b\u4e0b\u4ed6\u53c8\u50cf\u662f\u5343\u5934\u4e07\u7eea\uff0c\u53c8\u50cf\u662f\u4ec0\u4e48\u4e8b\u90fd \u8bb0\u4e0d\u5f97\uff0c\u53ea\u6728\u7136\u7acb\u7740\uff0c\u957f\u5251\u5c16\u513f\u5782\u5728\u5730\u4e0a\u3002 \u3000\u3000\u5f53\u4ed6\u542c\u5230\u201c\u2026\u2026\u4eba\u6052\u8fc7\u7136\u540e\u80fd\u6539\u2026\u2026\u201d\u65f6\uff0c\u4ed6\u987f\u65f6\u5b9b\u5982\u5de8\u96f7\u8f70\u9876\uff0c\u4e00\u65f6\u72b9\u5982\u5728\u4e07\u4e08\u6df1\u6e0a \u4e2d\u53d1\u73b0\u4e86\u53ef\u6500\u65f6\u4e4b\u7269\uff0c\u5341\u591a\u5e74\u6765\u7ed3\u90c1\u4e8e\u80f8\u7684\u6839\u4e8b\u4f3c\u4e4e\u8c41\u7136\u5f00\u901a\uff0c\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u4f3c\u4e4e\u4ece\u9752\u5e74 \u8dcc\u5165\u4e86\u8001\u5e74\uff0c\u4ed6\u4f3c\u4e4e\u61c2\u4e86\u8bb8\u591a\u65e0\u4ee5\u8a00\u5f62\u7684\u4e8b\u2026\u2026 \u3000\u3000\u4ed6\u7a81\u7136\u626c\u8d77\u624b\u4e2d\u957f\u5251\uff0c\u5bf9\u7740\u6885\u5c71\u6c11\u51dd\u89c6\u7247\u523b\uff0c\u9661\u7136\u6325\u5251\u201c\u64e6\u201d\u7684\u4e00\u58f0\uff0c\u58f0\u54cd\u672a\u6b47\uff0c\u5251\u4ea4 \u5de6\u624b\uff0c\u53c8\u662f\u201c\u64e6\u201d\u7684\u4e00\u58f0\u3002\u53ea\u89c1\u4ed6\u53cc\u624b\u9c9c\u8840\u6dcb\u6f13\uff0c\u4e24\u53ea\u5927\u59c6\u6307\u8dcc\u843d\u5730\u4e0a\uff0c\u4ed6\u7528\u4e2d\u98df\u4e24\u6307\u5939\u7740 \u957f\u5251\u594b\u529b\u4e00\u63b7\uff0c\u201c\u843d\u82f1\u5251\u201d\u5316\u4f5c\u4e00\u5708\u6d41\u5149\u76f4\u98de\u800c\u51fa\uff0c\u201c\u6251\u201d\u7684\u4e00\u58f0\u9489\u5728\u6811\u5e72\u4e0a\uff0c\u5251\u67c4\u5e26\u7740\u5c0f \u534a\u622a\u5251\u8eab\u5de6\u6709\u6447\u9707\u6643\u8361\uff0c\u63a5\u7740\u5934\u4e5f\u4e0d\u56de\u7684\u53bb\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u51cc\u98ce\u89c1\u4ed6\u81ea\u65ad\u53cc\u6307\uff0c\u8fd9\u4e00\u751f\u662f\u4e0d\u80fd\u7528\u5251\u4e86\u7684\uff0c\u5fc3\u4e2d\u4e00\u65f6\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u6885\u5c71\u6c11\u4ef0\u9996\u4e0d\u8bed\uff0c\u5fc3\u4e2d\u6697\u8d5e\u8c22\u957f\u537f\u4e0d\u6127\u662f\u6761\u6c49\u5b50\u3002 \u3000\u3000\u5c71\u98ce\u5439\u6765\uff0c\u6811\u679d\u7c07\u7c07\u800c\u6643\u3002 \u3000\u3000\u8f9b\u6377\u548c\u51cc\u98ce\u90fd\u4e0d\u89c9\u9eef\u7136\uff0c\u800c\u5389\u9e57\uff0c\u8d64\u9633\u3001\u82e6\u5eb5\u4e09\u4eba\uff0c\u90fd\u77e5\u4eca\u65e5\u6b7b\u52ab\u96be\u9003\uff0c\u5389\u9e57\u548c\u8d64\u9633\u4e34 \u6b7b\u4e0d\u6094\uff0c\u4e58\u8f9b\u6377\u5fc3\u795e\u5fae\u758f\u4e4b\u9645\uff0c\u594b\u529b\u518d\u653b\u3002 \u3000\u3000\u8f9b\u6377\u957f\u7b11\u4e00\u58f0\uff0c\u53cc\u638c\u5185\u5916\u76f8\u5206\uff0c\u786c\u751f\u751f\u7684\u628a\u5389\u9e57\u7684\u653b\u52bf\u62d2\u56de\uff0c\u5de6\u624b\u5374\u4e00\u6c89\u4e00\u524a\uff0c\u4e0d\u4f46\u628a \u8d64\u9633\u9053\u4eba\u7684\u638c\u529b\u6d88\u5378\uff0c\u800c\u4e14\u628a\u4ed6\u9707\u9000\u4e94\u516d\u6b65\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5df2\u77e5\u8f9b\u6377\u4e4b\u610f\uff0c\u957f\u5251\u4e00\u633a\uff0c\u63a5\u7740\u8d64\u9633\uff0c\u4e0d\u8ba9\u4ed6\u518d\u52a0\u5165\u6218\u5708\uff0c\u8d64\u9633\u4e5f\u77e5\u8f9b\u6377\u662f\u628a\u5df1 \u65b9\u7684\u5e2e\u624b\u56f0\u4f4f\uff0c\u597d\u8ba9\u51cc\u98ce\u9010\u4e00\u51fb\u7834\u3002 \u3000\u3000\u5434\u51cc\u98ce\u957f\u5251\u4e71\u5410\uff0c\u4ed6\u5fc3\u4e2d\u6700\u6068\u4fbf\u662f\u8d64\u9633\uff0c\u5c24\u5176\u662f\u91d1\u8001\u4e8c\u7684\u6b7b\u4e5f\u662f\u53d7\u4ed6\u8d50\u4e88\uff0c\u5fc3\u4e2d\u662f\u6108\u60f3 \u6108\u706b\uff0c\u7edd\u62db\u8fed\u51fa\u3002 \u3000\u3000\u8d64\u9633\u9886\u6559\u8fc7\u4ed6\u7684\u5389\u5bb3\uff0c\u90a3\u6562\u4e1d\u6beb\u5927\u610f\uff0c\u62db\u62db\u5f0f\u5f0f\u4e0d\u6c42\u6709\u529f\uff0c\u4f46\u6c42\u65e0\u8fc7\uff0c\u662f\u4ee5\u4e00\u65f6\u4e0d\u81f4\u843d \u8d25\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5728\u4e00\u65c1\u51b7\u773c\u65c1\u89c2\uff0c\u5df2\u77e5\u51cc\u98ce\u529f\u592b\u867d\u5c5e\u4e0a\u4e58\uff0c\u4f46\u7ecf\u9a8c\u5374\u751a\u6b20\u7f3a\uff0c\u4e0d\u7531\u76b1\u76b1\u7709\u5fd6\u9053\uff1a \u201c\u8fd9\u5b69\u5b50\u7684\u529f\u592b\u5df2\u6210\uff0c\u4f46\u5374\u6ca1\u6709\u6377\u513f\u90a3\u4e48\u673a\u667a\u2026\u2026\u201d \u3000\u3000\u6b63\u6c89\u541f\u95f4\uff0c\u5ffd\u89c1\u51cc\u98ce\u5251\u5149\u4e00\u95ea\uff0c\u8d70\u4e2d\u5bab\uff0c\u5927\u6d2a\u95e8\uff0c\u6b63\u9762\u653b\u5165\u8d64\u9633\u9053\u4eba\u7684\u8fd1\u4fa7\uff0c\u72e0\u72e0\u6233\u51fa \u4e00\u5f0f\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u7528\u5f97\u597d\u5999\uff0c\u8d64\u9633\u9053\u957f\u4e00\u6014\uff0c\u957f\u5251\u52c9\u529b\u4e00\u5708\uff0c\u60f3\u8981\u5c01\u5f00\u8fd9\u4e00\u5f0f\u81f4\u547d\u7684\u6253\u51fb\u3002 \u3000\u3000\u5434\u51cc\u98ce\u7a81\u7136\u7531\u7a7a\u800c\u865a\uff0c\u8d64\u9633\u9053\u58eb\u62db\u5f0f\u7528\u8001\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u89c1\u65f6\u4e0d\u4e88\u6211\uff0c\u51b7\u7136\u9053\uff1a\u201c\u653b\u4ed6\u4e0b\u76d8\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u4e00\u6014\uff0c\u968f\u5373\u9886\u609f\uff0c\u4e03\u5999\u795e\u541b\u4f55\u7b49\u529f\u529b\uff0c\u4f55\u7b49\u7ecf\u9a8c\uff0c\u5434\u51cc\u98ce\u5982\u8a00\u4e00\u811a\u95ea\u7535\u822c\u70b9\u51fa\uff0c \u6b63\u597d\u8e22\u5728\u8d64\u9633\u7684\u5251\u5b50\u4e0a\uff0c\u5b9d\u5251\u4e00\u5931\uff0c\u5413\u5f97\u9b42\u98de\u9b44\u6563\uff0c\u53cd\u8eab\u9000\u540e\u5341\u4f59\u6b65\u3002\u5434\u51cc\u98ce\u4ec7\u706b\u4e0a\u5347\uff0c\u53cc \u76ee\u5168\u8d64\uff0c\u4e00\u6b65\u4e00\u6b65\u901a\u8fc7\u53bb\uff0c\u543c\u9053\uff1a\u201c\u8d64\u9633\u8d3c\u9053\uff0c\u4f60\u4e00\u751f\u4f5c\u6076\u591a\u7aef\uff0c\u4eca\u65e5\u8fd8\u4e0d\u62ff\u547d\u3002\u201d \u3000\u3000\u8d64\u9633\u9053\u4eba\u5df2\u77e5\u975e\u4ed6\u654c\u624b\uff0c\u4f46\u5fc3\u4e2d\u4ecd\u4e0d\u653e\u5f03\u6700\u540e\u4e00\u4e2a\u5e0c\u671b\uff0c\u8eab\u4f53\u84e6\u7136\u4e00\u9000\uff0c\u53cd\u8eab\u8dcc\u5728\u5730 \u4e0a\uff0c\u4e00\u628a\u6293\u7740\u5389\u9e4a\u88ab\u8f9b\u6377\u9707\u98de\u7684\u90a3\u628a\u201c\u501a\u8679\u201d\u795e\u5251\uff0c\u5434\u51cc\u98ce\u5927\u53f1\u4e00\u58f0\uff0c\u95ea\u5251\u4fbf\u523a\u3002 \u3000\u3000\u8d64\u9633\u4e0d\u6599\u5434\u51cc\u98ce\u529f\u592b\u5982\u6b64\u5feb\uff0c\u6765\u4e0d\u53ca\u8f6c\u8eab\uff0c\u51cc\u98ce\u7684\u957f\u5251\u5df2\u523a\u5728\u8eab\u4e0a\uff0c\u4ed6\u4e34\u6b7b\u72b9\u6076\uff0c\u72c2\u543c \u4e00\u58f0\uff0c\u501a\u8679\u795e\u5251\u4ece\u808b\u4e0b\u731b\u63b7\u5411\u51cc\u98ce\u3002 \u3000\u3000\u51cc\u98ce\u8fd0\u5251\u5982\u98ce\uff0c\u95ea\u773c\u4fbf\u5728\u8d64\u9633\u8eab\u4e0a\u523a\u4e86\u4e00\u4e2a\u900f\u660e\u7684\u7a9f\u7abf\u3002\u5ffd\u89c9\u773c\u524d\u9752\u5149\u95ea\u52a8\uff0c\u501a\u8679\u5251\u88ab \u8d64\u9633\u4e34\u6b7b\u7684\u5185\u5bb6\u771f\u529b\u63b7\u51fa\uff0c\u7b14\u76f4\u98de\u6765\uff0c\u9690\u9690\u6709\u98ce\u96f7\u4e4b\u58f0\u3002\u4ed6\u5230\u5e95\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u4e00\u65f6\u7adf\u4e0d\u77e5\u6240 \u63aa\u3002 \u3000\u3000\u6885\u5c71\u6c11\u5927\u58f0\u543c\u9053\uff1a\u201c\u7528\u5251\u554a\u2014\u2014\u201d \u3000\u3000\u51cc\u98ce\u604d\u7136\u5927\u609f\uff0c\u65ad\u9b42\u5251\u4e5f\u662f\u8131\u624b\u800c\u98de\uff0c\u4fbf\u95fb\u545b\u5577\u4e00\u58f0\uff0c\u53cc\u5251\u5728\u7a7a\u4e2d\u76f8\u78b0\uff0c\u6fc0\u8d77\u4e00\u6735\u660e\u4eae \u7684\u706b\u82b1\u3002 \u3000\u3000\u501a\u8679\u5251\u4e07\u53e4\u5229\u5668\uff0c\u65ad\u9b42\u5251\u867d\u4e5f\u662f\u524a\u94c1\u5982\u6ce5\uff0c\u4f46\u4e00\u89e6\u4e4b\u4e0b\uff0c\u65ad\u9b42\u5251\u4e2d\u8170\u800c\u6298\uff0c\u4e0d\u8fc7\u201c\u501a \u8679\u201d\u5251\u4e5f\u88ab\u649e\u6b6a\u51c6\u5934\uff0c\u98de\u5411\u5de6\u65b9\u3002 \u3000\u3000\u8d64\u9633\u597d\u5927\u529b\u9053\uff0c\u501a\u8679\u867d\u88ab\u65ad\u9b42\u5251\u4e00\u6321\uff0c\u5251\u52bf\u5e76\u4e0d\u8870\u51cf\uff0c\u6709\u82e5\u4e00\u6735\u6d41\u661f\uff0c\u98de\u51fa\u4e8e\u4f59\u4e08\uff0c\u7adf \u8dcc\u4e0b\u5343\u4e08\u6df1\u5d16\u3002 \u3000\u3000\u501a\u8679\u5251\u4e43\u662f\u6b66\u6797\u7b2c\u4e00\u5229\u5668\uff0c\u4eca\u65e5\u5374\u8dcc\u4e0b\u4e07\u4e08\u6df1\u5d16\uff0c\u53c8\u91cd\u5f52\u7075\u5c71\u7edd\u5730\u3002 \u3000\u3000\u8fd9\u8fb9\u8d64\u9633\u9053\u4eba\u7684\u4e00\u58f0\u60e8\u53eb\uff0c\u60ca\u52a8\u4e86\u90a3\u8fb9\u7684\u4e24\u4eba\uff0c\u8f9b\u6377\u6717\u58f0\u9053\uff1a\u201c\u6885\u53d4\u53d4\u2014\u2014\u201d \u3000\u3000\u4ed6\u672c\u662f\u8981\u8bf4\u4e9b\u98ce\u51c9\u8bdd\u53bb\u6c14\u6c14\u5389\u9e57\u7b49\u4e8c\u4eba\uff0c\u4f46\u624d\u4e00\u5f00\u53e3\uff01\u5ffd\u89c1\u82e6\u5eb5\u4e0a\u4eba\u539f\u6765\u642d\u5728\u5389\u9e57\u80a9\u4e0a \u7684\u624b\u840e\u7136\u653e\u5f00\u3002 \u3000\u3000\u5389\u9e57\u89c9\u540e\u63f4\u7684\u529b\u9053\u4e00\u677e\uff0c\u4fbf\u77e5\u8981\u69fd\uff0c\u4ed6\u77e5\u82e6\u5eb5\u4e0a\u4eba\u4e43\u662f\u8981\u820d\u4ed6\u800c\u53bb\uff0c\u60c5\u6025\u4e4b\u4e0b\uff0c\u5927\u6012 \u9053\uff1a\u201c\u4e0a\u4eba\u8bf7\u7b49\u4e00\u4e0b\u2014\u2014\u201d \u3000\u3000\u5de6\u624b\u94c1\u638c\u53cd\u731b\u51fb\u3002 \u3000\u3000\u82e6\u5eb5\u4e0a\u4eba\u53cc\u624b\u4e00\u5206\uff0c\u786c\u63a5\u4e00\u5f0f\uff0c\u8eab\u4f53\u5374\u501f\u6b64\u5012\u9000\u4e09\u56db\u4e08\uff0c\u5982\u98ce\u7eb5\u53bb\u3002 \u3000\u3000\u5434\u51cc\u98ce\u65ad\u9b42\u5251\u867d\u5931\uff0c\u7a7a\u624b\u4e00\u7eb5\u4e0a\u524d\uff0c\u4fbf\u60f3\u963b\u62e6\uff0c\u84e6\u7136\u4e03\u5999\u795e\u541b\u5927\u58f0\u9053\uff1a\u201c\u98ce\u513f\uff0c\u7531\u4ed6\u53bb \u5427\uff01\u201d \u3000\u3000\u5434\u51cc\u98ce\u4e00\u6014\uff0c\u82e6\u5eb5\u5df2\u53bb\u5f97\u5f88\u8fdc\uff0c\u6885\u5c71\u6c11\u5fae\u53f9\u9053\uff1a\u201c\u6b64\u4eba\u5e73\u65e5\u4f5c\u6076\u5c1a\u5c11\uff0c\u53c8\u662f\u4f5b\u95e8\u4e2d\u4eba\uff0c \u5c31\u653e\u4ed6\u53bb\u5427\u3002\u201d \u3000\u3000\u8f9b\u6377\u4e58\u5389\u9e57\u82e6\u5eb5\u5185\u6597\uff0c\u594b\u8d77\u795e\u529b\uff0c\u628a\u5389\u9e57\u7684\u4e0a\u6e05\u6c14\u529f\u5012\u5377\u800c\u56de\uff0c\u5389\u9e57\u6574\u4e2a\u8eab\u4f53\u88ab\u9707\u51fa\u4e09 \u56db\u4e08\u3002 \u3000\u3000\u8f9b\u6377\u6a2a\u5251\u9053\uff1a\u201c\u59d3\u5389\u7684\uff0c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u51b3\u4e0d\u5584\u4e86\u2014\u2014\u201d \u3000\u3000\u5389\u9e57\u9893\u7136\u4e0d\u8bed\uff0c\u7a81\u7136\u957f\u53f9\u9053\uff1a\u201c\u7f62\u4e86\uff0c\u7f62\uff0c\u5389\u67d0\u4eca\u65e5\u8ba4\u683d\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u65b9\u843d\uff0c\u9661\u7136\u62ac\u8d77\u624b\u638c\uff0c\u4fbf\u5f80\u81ea\u5df1\u5929\u7075\u76d6\u4e0a\u51fb\u53bb\uff0c\u201c\u6251\u201d\u7684\u4e00\u58f0\uff0c\u7acb\u65f6\u8840\u8089\u6a21\u7cca\u2014\u2014 \u3000\u3000\u5251\u795e\u5389\u9e57\uff0c\u9634\u9669\u72e0\u8fa3\uff0c\u6a2a\u884c\u534a\u751f\uff0c\u6700\u540e\u5374\u6b7b\u5728\u81ea\u5df1\u638c\u4e0b\u2026\u2026 \u3000\u3000\u5c71\u98ce\u5439\u6765\uff0c\u9001\u6765\u9635\u9635\u677e\u9999\uff0c\u8c01\u80fd\u76f8\u4fe1\uff0c\u8fd9\u7075\u79c0\u7684\u5c71\u4e0a\u521a\u624d\u8fd8\u662f\u98ce\u4e91\u53d8\u8272\u7684\u6fc0\u70c8\u60e8\u6597\uff1f \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c\u5341\u4e94\u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c\u5341\u4e94\u7ae0 \u3000\u3000\u4e2d\u5dde\u4e94\u5927\u5251\u6d3e\u767e\u5e74\u6765\u81ea\u5c11\u6797\u5bfa\u4e0d\u95ee\u4e16\u4e8b\u540e\uff0c\u6267\u6b66\u6797\u4e4b\u725b\u8033\uff0c\u55a7\u55a7\u8d6b\u8d6b\uff0c\u4e0d\u53ef\u4e00\u4e16\u3002\u4f46\u662f \u5c31\u6b64\u4e00\u6218\uff0c\u5b8c\u5168\u6bc1\u5728\u8f9b\u6377\u3001\u5434\u51cc\u98ce\u7684\u624b\u4e2d\uff0c\u6240\u8c13\u6ca7\u6d77\u6851\u7530\uff0c\u767d\u4e91\u82cd\u72d7\uff0c\u4e16\u4e8b\u53d8\u5e7b\u4e4b\u5feb\uff0c\u4ee4\u4eba \u611f\u6168\u7cfb\u4e4b\u3002 \u3000\u3000\u4e94\u534e\u5c71\u4e0a\uff0c\u5bd2\u98ce\u6b63\u51bd\u3002 \u3000\u3000\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u4e00\u624b\u63e1\u7740\u4e00\u4e2a\u5c11\u5e74\u7684\u624b\uff0c\u51e0\u5341\u5e74\u6765\u7684\u6069\u6069\u6028\u6028\u5728\u4ed6\u8111\u6d77\u4e2d\u4e00\u6643\u800c\u8fc7\uff0c\u5341 \u4e94\u5e74\u524d\u5408\u529b\u6697\u7b97\u4ed6\u7684\u4ec7\u4eba\uff0c\u73b0\u5728\u5df2\u7ecf\u6b7b\u7684\u6b7b\uff0c\u9003\u7684\u9003\uff0c\u4ed6\u5fc3\u4e2d\u4f3c\u4e4e\u4e0d\u518d\u6709\u4ec0\u4e48\u7275\u6302\u4e86\u3002 \u3000\u3000\u4e24\u4e2a\u5c11\u5e74\u7684\u6b66\u529f\u4e0d\u53ea\u662f\u9752\u51fa\u4e8e\u84dd\u800c\u80dc\u4e8e\u84dd\uff0c\u7b80\u76f4\u53ef\u79f0\u4e2d\u539f\u767e\u5e74\u6700\u6770\u51fa\u7684\u4eba\u6750\uff0c\u5bf9\u4e03\u5999\u795e \u541b\u6765\u8bf4\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e0d\u6ee1\u8db3\u7684\uff1f \u3000\u3000\u5c71\u98ce\u84b8\u8d77\uff0c\u98ce\u4e91\u53d8\u8272\uff0c\u6885\u5c71\u6c11\u7eb5\u58f0\u9ad8\u6b4c\uff1a \u3000\u3000\u201c\u5bf9\u9152\u5f53\u6b4c\uff0c\u4eba\u751f\u51e0\u4f55\uff1f\u8b6c\u5982\u671d\u9732\uff0c\u53bb\u65e5\u82e6\u591a\uff0c\u6168\u5f53\u4ee5\u6177\uff0c\u5fe7\u601d\u96be\u5fd8\u2026\u2026\u201d \u3000\u3000\u4e00\u591c\u5927\u96ea\uff0c\u957f\u5b89\u57ce\u987f\u6210\u94f6\u8272\u4e16\u754c\u3002 \u3000\u3000\u6e05\u6668\uff0c\u96ea\u505c\u4e86\uff0c\u5929\u8272\u6e10\u6e10\u5f00\u6717\uff0c\u897f\u5927\u8857\u4e0a\u8d76\u9a74\u8f66\u513f\u7684\u8001\u738b\uff0c\u53f1\u559d\u62d6\u51fa\u6b63\u5728\u53d1\u6296\u7684\u9a74\u5b50\uff0c \u5957\u4e0a\u8f66\u513f\uff0c\u5f00\u95e8\u51fa\u53bb\u3002 \u3000\u3000\u4ed6\u62ac\u5934\u770b\u770b\u96ea\u540e\u9ad8\u6717\u7684\u84dd\u5929\uff0c\u518d\u77a7\u77a7\u5730\u4e0a\u76c8\u5c3a\u7684\u7684\u79ef\u96ea\uff0c\u5583\u5583\u9053\uff1a \u3000\u3000\u201c\u6628\u513f\u591c\u91cc\u8fd9\u573a\u5927\u96ea\uff0c\u53ea\u6015\u662f\u4ea4\u6625\u6765\u6700\u5927\u7684\u4e00\u56de\u54e9\uff1f\u201d \u3000\u3000\u4e00\u9635\u51db\u51bd\u5bd2\u98ce\u5439\u8fc7\uff0c\u4ed6\u4e0d\u7531\u81ea\u4e3b\u7684\u6253\u4e86\u4e00\u5bd2\u6817\uff0c\u62c9\u8d77\u4e86\u68c9\u5927\u8863\u7684\u9886\u5b50\uff0c\u76d6\u4f4f\u4e24\u8033\u3002 \u3000\u3000\u4e00\u8def\u4e0a\u4e0d\u89c1\u4e00\u4e2a\u884c\u4eba\uff0c\u8001\u738b\u5fc3\u9053\uff1a\u201c\u518d\u8fc7\u4e00\u4f1a\u513f\uff0c\u7b49\u5230\u5927\u5bb6\u90fd\u8d77\u8eab\u51fa\u95e8\uff0c\u8fd9\u6837\u6ed1\u7684\u8def\uff0c \u5c31\u662f\u5e73\u65e5\u4e0d\u96c7\u8f66\u513f\u7684\u4eba\uff0c\u4e5f\u53ea\u6709\u5149\u987e\u6211\u8001\u738b\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8d76\u5230\u897f\u5927\u8857\u4e2d\u6bb5\uff0c\u53ea\u89c1\u4e00\u5bb6\u5927\u95e8\u53e3\uff0c\u6b63\u6709\u4e00\u4e2a\u5c0f\u53ae\u5728\u626b\u96ea\uff0c\u8001\u738b\u773c\u5feb\uff0c\u7acb\u523b\u8ba4\u51fa\u662f\u5e73 \u65e5\u505a\u6563\u5de5\u5ea6\u65e5\u7684\u5c0f\u4f59\uff0c\u4fbf\u558a\u9053\uff1a\u201c\u5c0f\u4f59\uff0c\u96be\u602a\u4e00\u4e2a\u591a\u6708\u4e0d\u89c1\u4f60\u5566\uff0c\u539f\u6765\u4f60\u7adf\u8dd1\u5230\u6797\u5927\u7237\u5bb6\u53bb \u4e86\uff0c\u5582\uff01\u4f60\u665a\u4e0a\u600e\u6837\u4e5f\u4e0d\u6765\u63a8\u724c\u4e5d\u4e86\uff1f\u201d \u3000\u3000\u90a3\u5524\u7740\u5c0f\u4f59\u7684\u662f\u4e2a\u5341\u56db\u4e94\u5c81\u7684\u5065\u58ee\u5c11\u5e74\uff0c\u4ed6\u7a7f\u7684\u867d\u751a\u5355\u8584\u7834\u65e7\uff0c\u4f46\u7cbe\u795e\u6602\u626c\uff0c\u4e0d\u9732\u4e1d\u6beb \u5bd2\u610f\u3002 \u3000\u3000\u5c0f\u4f59\u9053\uff1a\u201c\u738b\u5927\u54e5\uff0c\u6211\u518d\u4e0d\u8d4c\u4e86\uff0c\u73b0\u5728\u6211\u53ef\u5fd9\u5f97\u5f88\uff0c\u6bcf\u665a\u5170\u59d1\u90fd\u8981\u6559\u6211\u8ba4\u5b57\u8bfb\u4e66\u3002\u201d \u3000\u3000\u8001\u738b\u54c8\u54c8\u9053\uff1a\u201c\u5012\u77a7\u4e0d\u51fa\u4f60\u5c0f\u4f59\uff0c\u8fd9\u5927\u5e74\u7eaa\u4e86\u7adf\u8fd8\u8bfb\u4e66\u8ba4\u5b57\uff0c\u96be\u9053\u8fd8\u60f3\u4e2d\u72b6\u5143\u4e0d\u6210\u3002\u201d \u3000\u3000\u5c0f\u4f59\u6b63\u8272\u9053\uff1a\u201c\u6211\u4ee5\u524d\u4e5f\u53ea\u9053\u54b1\u4eec\u7a77\u4e86\uff0c\u9664\u9760\u5356\u52b3\u529b\u6df7\u996d\u5403\uff0c\u90a3\u8fd8\u80fd\u5e72\u4ec0\u4e48\uff0c\u53ef\u662f\u81ea\u4ece \u5170\u59d1\u6559\u6211\u8bc6\u5b57\u5ff5\u4e66\u4ee5\u6765\uff0c\u8fd9\u79cd\u60f3\u6cd5\u53ef\u6709\u4e86\u6539\u53d8\u3002\u5170\u59d1\u8bf4\u7a77\u4eba\u4e5f\u662f\u4eba\uff0c\u4e3a\u4ec0\u4e48\u522b\u4eba\u80fd\u505a\u7684\u4e8b\uff0c \u54b1\u4eec\u4fbf\u4e0d\u80fd\u505a\u3002\u4f60\u522b\u7b11\u6211\u5e74\u7eaa\u592a\u5927\uff0c\u5170\u59d1\u8bf4\u5b8b\u671d\u6709\u4e2a\u59d3\u82cf\u7684\u5927\u5b66\u95ee\u5bb6\uff0c\u4ece\u4e8c\u5341\u51e0\u5c81\uff0c\u624d\u5f00\u59cb \u8bfb\u4e66\u54e9\uff01\u201d \u3000\u3000\u8001\u738b\u6447\u624b\u9053\uff1a\u201c\u6211\u53ef\u4e0d\u4e0e\u4f60\u4e89\u8fa8\uff0c\u90a3\u5170\u59d1\u6211\u53ea\u9053\u77e5\u5979\u624b\u827a\u5de7\u5999\uff0c\u60f3\u4e0d\u5230\u7adf\u8fd8\u662f\u4e2a\u77e5\u4e66\u8bc6 \u793c\u7684\u5973\u5b66\u58eb\u54e9\uff01\u201d \u3000\u3000\u5c0f\u4f59\u542c\u4ed6\u8d5e\u81ea\u5df1\u5fc3\u4e2d\u6700\u4f69\u670d\u7684\u4eba\uff0c\u4e0d\u7531\u5927\u559c\u9053\uff1a\u201c\u5170\u59d1\u53ef\u61c2\u5f97\u591a\u54e9\uff0c\u4f60\u6ca1\u5403\u8fc7\u5979\u70e7\u7684 \u83dc\uff0c\u90a3\u53ef\u662f\u597d\u5403\u6781\u4e86\u3002\u201d \u3000\u3000\u8001\u738b\u70b9\u5934\u53f9\u9053\uff1a\u201c\u5979\u548c\u65b9\u5a46\u5a46\u539f\u6765\u5c31\u4f4f\u5728\u6211\u5bb6\u7247\u9762\uff0c\u5979\u90a3\u624b\u523a\u7ee3\uff0c\u6211\u6d3b\u5230\u8fd9\u4e48\u5927\uff0c\u4e5f\u8fd8 \u6ca1\u6709\u89c1\u8fc7\u7b2c\u4e8c\u4e2a\u4eba\u6709\u8fd9\u80fd\u8010\uff0c\u4e0d\u8981\u8bf4\u5979\u662f\u778e\u5b50\uff0c\u5c31\u662f\u2018\u5149\u5b50\u2019\uff0c\u8c01\u80fd\u8d76\u5f97\u4e0a\u5979\u5462\uff1f\u5509\uff01\u8fd9\u4e48 \u597d\u7684\u4e00\u4e2a\u59d1\u5a18\u3002\u5c0f\u4f59\uff0c\u5509\uff0c\u4f60\u4eec\u8001\u7237\u2026\u2026\u201d \u3000\u3000\u201c\u5c0f\u4f59\uff01\u5c0f\u4f59\uff01\u201d\u4e00\u9635\u6e05\u8106\u53eb\u5524\u58f0\u4f20\u4e86\u51fa\u6765\u3002 \u3000\u3000\u5c0f\u4f59\u6025\u653e\u4e0b\u626b\u5e1a\uff0c\u5411\u8001\u738b\u70b9\u70b9\u5934\uff0c\u5c31\u5954\u4e86\u8fdb\u53bb\u3002 \u3000\u3000\u5c4b\u4e2d\u7089\u706b\u718a\u718a\uff0c\u9760\u7a97\u5750\u7740\u4e00\u4e2a\u6e05\u4e3d\u59d1\u5a18\u3002 \u3000\u3000\u5979\u5f00\u53e3\u4f4e\u58f0\u57cb\u6028\u9053\uff1a\u201c\u8fd9\u4e48\u51b7\uff0c\u5927\u6e05\u65e9\u53ea\u7a7f\u4e24\u4ef6\u5939\u8863\uff0c\u7740\u4e86\u51c9\u600e\u4e48\u529e\uff1f\u201d \u3000\u3000\u8bf4\u7740\uff0c\u4ece\u8eab\u540e\u62ff\u51fa\u4e00\u4ef6\u68c9\u8863\uff0c\u4fbf\u903c\u7740\u5c0f\u4f59\u7a7f\u3002 \u3000\u3000\u5c0f\u4f59\u521a\u624d\u5728\u96ea\u5730\u91cc\u90fd\u4e0d\u89c9\u51b7\uff0c\u6b64\u65f6\u5c4b\u4e2d\u751f\u706b\uff0c\u989d\u89d2\u5df2\u5fae\u51fa\u6c57\uff0c\u4f46\u542c\u90a3\u5973\u5b50\u67d4\u58f0\u57cb\u6028\uff0c\u5fc3 \u4e2d\u611f\u5230\u4e00\u9635\u6e29\u6696\uff0c\u7acb\u523b\u7a7f\u4e86\u4e0a\u53bb\u3002 \u3000\u3000\u5c0f\u4f59\u9053\uff1a\u201c\u5170\u59d1\uff0c\u8001\u7237\u540e\u5929\u53ef\u56de\u6765\u4e86\u5417\uff1f\u201d \u3000\u3000\u5170\u59d1\u9053\uff1a\u201c\u4e58\u4ed6\u8fd8\u6ca1\u56de\u6765\uff0c\u6211\u4eec\u5f85\u4f1a\u513f\u5230\u7262\u91cc\u77a7\u77a7\u65b9\u5a46\u5a46\u3002\u201d \u3000\u3000\u5c0f\u4f59\u9053\uff1a\u201c\u65b9\u5a46\u5a46\u5df1\u7ecf\u8d70\u4e86\u3002\u201d \u3000\u3000\u5170\u59d1\u5927\u60ca\u9053\uff1a\u201c\u5979\u51e0\u65f6\u88ab\u653e\u8d70\u7684\uff1f\u201d \u3000\u3000\u5c0f\u4f59\u9053\uff1a\u201c\u524d\u51e0\u5929\uff0c\u6211\u9047\u5230\u72f1\u5352\u8001\u674e\uff0c\u4ed6\u544a\u8bc9\u6211\u7684\u3002\u201d \u3000\u3000\u5170\u59d1\u5446\u4e86\u534a\u54cd\u53f9\u6c14\u9053\uff1a\u201c\u5509\uff01\u5979\u4e00\u4e2a\u4eba\u5e74\u7eaa\u90a3\u4e48\u5927\uff0c\u80fd\u8d70\u5230\u54ea\u53bb\u5462\uff1f\u662f\u6211\u5bb3\u4e86\u5979\u3002\u201d \u3000\u3000\u5c0f\u4f59\u9053\uff1a\u201c\u90a3\u600e\u80fd\u602a\u4f60\uff0c\u90a3\u4e9b\u6355\u5934\u513f\uff0c\u5c31\u53ea\u4f1a\u6b3a\u4fae\u8001\u5f31\u7a77\u4eba\uff0c\u54fc\uff0c\u771f\u6b63\u7684\u98de\u8d3c\u5927\u76d7\uff0c\u4ed6 \u4eec\u53ef\u8fde\u5f71\u513f\u4e5f\u78b0\u4e0d\u5230\u3002\u201d \u3000\u3000\u5170\u59d1\u6025\u9053\uff1a\u201c\u5c0f\u4f59\uff0c\u4f60\u4ee5\u540e\u5feb\u522b\u518d\u8bf4\uff0c\u88ab\u8001\u7237\u542c\u89c1\u4e86\uff0c\u53ef\u4e0d\u662f\u597d\u73a9\u7684\u3002\u201d \u3000\u3000\u5c0f\u4f59\u9053\uff1a\u201c\u54fc\uff01\u6211\u53ef\u4e0d\u6015\uff0c\u5927\u4e0d\u4e86\u88ab\u4ed6\u4eec\u53bb\u6740\u5934\u3002\u201d \u3000\u3000\u5170\u59d1\u8d4c\u6c14\uff1a\u201c\u597d\uff0c\u4f60\u4e0d\u542c\u8bdd\uff0c\u6211\u662f\u4e3a\u4f60\u597d\u5440\uff01\u201d \u3000\u3000\u5c0f\u4f59\u89c1\u5979\u8138\u4e0a\u5fae\u6012\uff0c\u5fc3\u4e2d\u5927\u6025\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5170\u59d1\uff0c\u60a8\u522b\u751f\u6c14\uff0c\u6211\u4ee5\u540e\u518d\u4e5f\u4e0d\u8bf4\u5566\uff01\u201d \u3000\u3000\u5170\u59d1\u5ae3\u7136\u4e00\u7b11\u9053\uff1a\u201c\u8fd9\u624d\u662f\u597d\u5b69\u5b50\u3002\u201d \u3000\u3000\u4e0b\u5348\uff0c\u5929\u8272\u66f4\u89c1\u6674\u6717\uff0c\u96ea\u540e\u521d\u96fe\uff0c\u7a7a\u6c14\u5341\u5206\u6e05\u9c9c\u3002 \u3000\u3000\u5170\u59d1\u6b63\u5728\u66ff\u5c0f\u4f59\u7f1d\u4e00\u4ef6\u5916\u886b\uff0c\u5ffd\u7136\u55c5\u5230\u4e00\u80a1\u6e05\u9999\uff0c\u4fbf\u95ee\u5c0f\u4f59\u9053\uff1a\u201c\u95e8\u65c1\u5170\u82b1\u53c8\u5f00\u4e86\u3002\u201d \u3000\u3000\u5c0f\u4f59\u9053\uff1a\u201c\u4e0d\u4f46\u5170\u82b1\u5f00\u4e86\uff0c\u6885\u4e5f\u5f00\u4e86\uff0c\u5bf9\u4e86\uff0c\u6211\u6458\u51e0\u679d\u6765\u63d2\u82b1\u74f6\u3002\u201d \u3000\u3000\u5170\u59d1\u9053\uff1a\u201c\u597d\u751f\u751f\u5f00\u5728\u6811\u4e0a\uff0c\u4e0d\u8981\u53bb\u6458\u5b83\uff0c\u90a3\u9999\u6c14\u597d\u95fb\u6781\u5566\uff0c\u6211\u8981\u8d70\u8fd1\u53bb\u55c5\u55c5\u3002\u201d \u3000\u3000\u5979\u8f7b\u6b65\u8de8\u51fa\u95e8\u69db\u8d70\u5411\u5927\u95e8\u5899\u8fb9\u7684\u6885\u6811\u4e0b\uff0c\u52a8\u4f5c\u4e4b\u4f36\u4fd0\uff0c\u5b8c\u5168\u4e0d\u50cf\u662f\u4e00\u4e2a\u53cc\u76ee\u5931\u660e\u7684\u4eba\u3002 \u3000\u3000\u5979\u5f2f\u4e0b\u8170\uff0c\u5fae\u55c5\u7740\u521d\u5f00\u7684\u8349\u5170\uff0c\u5fc3\u4e2d\u6d6e\u8d77\u4e86\u4e00\u5f20\u719f\u6089\u7684\u9762\u5b54\u3002 \u3000\u3000\u4ece\u5c0f\uff0c\u5979\u5c31\u7231\u82b1\uff0c\u5c24\u5176\u662f\u5170\u82b1\uff0c\u56e0\u4e3a\u8fd9\u548c\u5979\u540d\u5b57\u51d1\u5de7\u6709\u5173\u3002 \u3000\u3000\u201c\u5728\u6211\u773c\u6674\u672a\u778e\u4e4b\u524d\uff0c\u201d\u5979\u60f3\uff0c\u201c\u6bcf\u5e74\u521d\u51ac\uff0c\u5f53\u5c0f\u8305\u5c4b\u56db\u5468\u8349\u5170\u5f00\u653e\u7684\u65f6\u5019\uff0c\u6211\u603b\u7231\u4e00 \u4e2a\u4eba\u7ad9\u5728\u82b1\u4e1b\u4e2d\uff0c\u7528\u529b\u55c5\u7740\u90a3\u4ee4\u4eba\u5fd8\u4fd7\u7684\u6de1\u6de1\u7684\u9999\u6c14\uff0c\u6bcf\u5f53\u6211\u5fc3\u795e\u4ff1\u9189\u7684\u65f6\u5019\uff0c\u7a81\u7136\u4ece\u540e\u9762 \u4f38\u51fa\u4e00\u53cc\u5f3a\u800c\u529b\u7684\u624b\uff0c\u906e\u4f4f\u4e86\u6211\u53cc\u773c\uff0c\u8f7b\u58f0\u8981\u6211\u731c\u662f\u8c01\uff1f\u90a3\u662f\u5927\u54e5\u2014\u2014\u6211\u5fc3\u4e2d\u6700\u5d07\u62dc\u6700\u656c\u7231 \u7684\u5927\u54e5\uff0c\u6211\u4e0d\u7528\u731c\u4e5f\u77e5\u9053\u7684\u3002\u201d \u3000\u3000\u5979\u81ea\u54c0\u5730\u5fae\u7b11\u4e00\u4e0b\uff0c\u63a5\u7740\u60f3\u9053\uff1a\u201c\u540e\u6765\uff0c\u6211\u773c\u775b\u778e\u4e86\uff0c\u5988\u548c\u5927\u54e5\u5bf9\u6211\u66f4\u662f\u767e\u4f9d\u767e\u987a\uff0c\u6211 \u60f3\u8981\u4ec0\u4e48\uff0c\u5927\u54e5\u4ece\u6765\u6ca1\u6709\u4f7f\u6211\u5931\u671b\u8fc7\uff0c\u6211\u867d\u77a7\u4e0d\u89c1\u4ed6\u7231\u6211\u4f36\u6211\u7684\u76ee\u5149\uff0c\u53ef\u662f\u6211\u80fd\u611f\u89c9\u5230\u4ed6\u662f \u66f4\u52a0\u559c\u6b22\u6211\u4e86\uff0c\u5728\u8fd9\u4e16\u754c\u4e0a\uff0c\u53ea\u6709\u5988\uff0c\u53ea\u6709\u5927\u54e5\u662f\u771f\u6b63\u5f85\u6211\u597d\u7684\uff0c\u4e0d\u8981\u8bf4\u662f\u6211\u53cc\u76ee\u5931\u660e\uff0c\u5c31 \u662f\u6211\u53cc\u624b\u53cc\u811a\u90fd\u6b8b\u53bb\uff0c\u4ed6\u4eec\u4f9d\u7136\u4e0d\u4f1a\u5acc\u5f03\u6211\uff0c\u4f9d\u7136\u662f\u7231\u6211\u7684\u3002\u201d \u3000\u3000\u201c\u6211\u5929\u5929\u6570\u65e5\u5b50\uff0c\u5728\u5915\u9633\u4e0b\uff0c\u51dd\u671b\u7740\u90a3\u9065\u8fdc\u7684\u5c0f\u9053\uff0c\u867d\u7136\u6211\u77e5\u9053\u5927\u54e5\u81f3\u5c11\u8981\u534a\u5e74\u624d\u4f1a\u56de \u6765\uff0c\u53ef\u662f\u6211\u5374\u5e0c\u671b\u6709\u5947\u8ff9\u53d1\u751f\uff0c\u5929\u6697\u4e0b\u53bb\uff0c\u5929\u5e55\u4e0a\u95ea\u8d77\u4e86\u51e0\u9897\u6d41\u661f\uff0c\u5988\u7f1d\u7740\u68c9\u8863\uff0c\u65f6\u65f6\u62ac\u5934 \u770b\u7740\u9ad8\u6717\u7684\u82cd\u7a79\u2014\u2014\u5979\u5fc3\u91cc\u4e5f\u5728\u60e6\u5ff5\u7740\u5927\u54e5\u54e9\uff01\u6302\u5ff5\u7684\u65e5\u5b50\u663e\u5f97\u5f88\u6162\uff0c\u53ef\u662f\u5728\u5e0c\u671b\u2014\u2014\u5149\u660e \u7684\u5e0c\u671b\u9f13\u52b1\u4e0b\uff0c\u6211\u548c\u5988\u5e73\u9759\u7684\u8fc7\u7740\u3002\u201d \u3000\u3000\u201c\u51e0\u573a\u5927\u96e8\uff0c\u773c\u89c1\u6cb3\u6c34\u6108\u6765\u6108\u9ad8\u6da8\uff0c\u4eba\u4eec\u5f00\u59cb\u60f6\u6025\u4e0d\u5b89\uff0c\u53ef\u662f\u8c01\u90fd\u6ca1\u60f3\u5230\u90a3\u4f1a\u6765\u5f97\u8fd9\u4e48 \u5feb\uff0c\u90a3\u5929\u665a\u4e0a\u2026\u2026\u201d\u5979\u60f3\u5230\u6b64\u5904\u8138\u4e0a\u95ea\u8d77\u4e86\u4e00\u9635\u60ca\u60b8\u4e4b\u8272\uff0c\u663e\u7136\u7684\uff0c\u5728\u5979\u8111\u6d77\u4e2d\uff0c\u90a3\u591c\u7684\u60c5 \u666f\uff0c\u662f\u591a\u4e48\u6df1\u523b\u60ca\u60f6\u3002 \u3000\u3000\u201c\u5927\u6c34\u6765\u52bf\u771f\u5982\u5343\u519b\u4e07\u9a6c\uff0c\u5f85\u5988\u548c\u6211\u60ca\u9192\u65f6\uff0c\u6c34\u5df2\u6df9\u5230\u9f50\u80f8\uff0c\u6211\u548c\u5988\u4e00\u4e2a\u62b1\u7740\u4e00\u4e2a\u6728 \u6876\uff0c\u968f\u7740\u6c79\u6d8c\u6ce2\u6d9b\u98d8\u6d41\uff0c\u7a81\u7136\u4e00\u4e2a\u5927\u6d6a\u6253\u6765\uff0c\u5988\u548c\u6211\u5c31\u5206\u5f00\uff0c\u6211\u5fc3\u4e2d\u4e00\u6025\uff0c\u4fbf\u660f\u4e86\u8fc7\uff0c\u5f85\u6211 \u9192\u6765\uff0c\u5929\u8272\u6e10\u6e10\u4eae\u4e86\uff0c\u90a3\u771f\u60f3\u4e0d\u5230\uff0c\u5728\u660f\u6655\u8fc7\u53bb\u65f6\uff0c\u6211\u53cc\u624b\u7adf\u80fd\u7d27\u6293\u7740\u6728\u6876\u6ca1\u6709\u677e\u5f00\uff0c\u90a3\u662f \u4eba\u7c7b\u6c42\u751f\u7684\u672c\u80fd\u53d1\u6325\u5230\u4e86\u6700\u9ad8\u70b9\u5427\uff01\u201d\u5979\u81ea\u5632\u7684\u7b11\u4e86\u7b11\uff0c\u60f3\u9053\uff1a\u201c\u6211\u624b\u8db3\u90fd\u5feb\u51bb\u50f5\uff0c\u53ea\u542c\u5230 \u6eda\u6eda\u5de8\u6ce2\uff0c\u6c34\u58f0\u4f3c\u4e4e\u6108\u6765\u6108\u5927\uff0c\u5988\u5988\u5462\uff1f\u6211\u4eb2\u7231\u7684\u5988\u5988\u5462\uff1f\u4e00\u79cd\u4e0d\u7965\u7684\u611f\u89c9\u4ece\u6211\u5185\u5fc3\u6df1\u5904\u4f20 \u4e86\u51fa\u6765\u2026\u2026\u6211\u6108\u6765\u6108\u4e0d\u80fd\u652f\u6301\uff0c\u771f\u60f3\u4e00\u677e\u624b\u8ba9\u6ce2\u6d6a\u5377\u53bb\u7b97\u4e86\uff0c\u53ef\u662f\u6709\u4e00\u79cd\u65e0\u6bd4\u7684\u52c7\u6c14\u652f\u6301\u7740 \u6211\uff0c\u6211\u60f3\u5c31\u662f\u8981\u6b7b\uff0c\u4e5f\u8981\u518d\u89c1\u5927\u54e5\u4e00\u9762\u5440\uff01\u540e\u6765\uff0c\u6211\u7ec8\u4e8e\u5f97\u6551\u4e86\uff01\u88ab\u5de1\u89c6\u707e\u533a\u7684\u91d1\u5927\u4eba\u6559\u8d77 \u6765\uff0c\u8fd9\u91d1\u5927\u4eba\u4e3a\u4eba\u53ef\u771f\u662f\u597d\uff0c\u4ed6\u90a3\u4e49\u5973\u82cf\u59d1\u5a18\u4e5f\u6781\u662f\u548c\u5584\uff0c\u6211\u5bc4\u4f4f\u5728\u91d1\u5927\u4eba\u5bb6\u4e2d\uff0c\u5230\u5904\u6253\u542c \u5988\u5988\u7684\u8e2a\u8ff9\uff0c\u7136\u800c\uff0c\u4eba\u6d77\u832b\u832b\uff0c\u5c31\u7b97\u5e78\u8fd0\uff0c\u5988\u4e0d\u88ab\u5927\u6c34\u51b2\u53bb\uff0c\u6211\u53c8\u5230\u4f55\u5904\u53bb\u5bfb\u5979\u5462\uff1f\u6211\u76d8\u7b97 \u7740\u5230\u6c34\u9000\u540e\uff0c\u5c31\u7acb\u523b\u8fd4\u5bb6\uff0c\u8fd9\u6837\uff0c\u5f53\u5927\u54e5\u56de\u6765\u65f6\uff0c\u4e5f\u4e0d\u4f1a\u627e\u6211\u4e0d\u7740\u3002\u201d \u3000\u3000\u201c\u60f3\u4e0d\u5230\u5927\u54e5\u7adf\u4f1a\u548c\u82cf\u59d1\u5a18\u76f8\u8bc6\uff0c\u800c\u4e14\u90a3\u4e48\u719f\u6089\uff0c\u5927\u54e5\uff0c\u867d\u7136\u4e0d\u662f\u90a3\u79cd\u89c1\u5f02\u601d\u8fc1\uff0c\u8d1f\u5fc3 \u65e0\u826f\u7684\u4eba\uff0c\u53ef\u662f\uff0c\u6211\u4eb2\u8033\u542c\u5230\u7684\uff0c\u5927\u54e5\u90a3\u7231\u604b\u6a2a\u6ea2\u7684\u60c5\u8bdd\uff0c\u90a3\u96be\u9053\u4e0d\u662f\u771f\u7684\u5417\uff1f\u54fc\uff0c\u4ed6\u600e\u4e48 \u53ef\u4ee5\u5bf9\u53e6\u4e00\u4e2a\u5973\u5b69\u5b50\u8bf4\u51fa\u90a3\u79cd\u8bdd\u5462\uff1f\u201d\u5979\u60c5\u7eea\u53d8\u5f97\u5f88\u662f\u6fc0\u52a8\uff0c\u5fcc\u5992\u7684\u6012\u706b\u6162\u6162\u7684\u71c3\u70e7\u8d77\u6765\uff0c \u53ef\u662f\uff0c\u6e29\u67d4\u6709\u5982\u6c5f\u6d77\u4e00\u822c\u6df1\u907d\u7684\u5979\uff0c\u5200\u8f6c\u77ac\u95f4\uff0c\u6012\u610f\u4fbf\u6d88\uff0c\u8f6c\u5ff5\u60f3\u9053\uff1a\u201c\u5509\uff01\u5982\u4eca\u6211\u8fd8\u5c3d\u60f3 \u8fd9\u4e9b\u4e8b\u5e72\u4e48\uff1f\u6211\u76f8\u4fe1\u5927\u54e5\u5fc3\u4e2d\u8fd8\u662f\u4f1a\u8bb0\u5f97\u6211\u7684\uff0c\u82cf\u59d1\u5a18\u867d\u662f\u5927\u5bb6\u95fa\u79c0\uff0c\u4f46\u8981\u5360\u4f4f\u5927\u54e5\u5168\u90e8\u7684 \u5fc3\uff0c\u53ea\u6015\u4e5f\u6ca1\u6709\u8fd9\u4e48\u5bb9\u6613\u3002\u5509\uff0c\u5927\u54e5\u7231\u7740\u5979\u53c8\u60e6\u5ff5\u7740\u6211\uff0c\u4ed6\u4e00\u5b9a\u4e0d\u5feb\u6d3b\u7684\uff0c\u6211\u2026\u2026\u6211\u5012\u4e0d\u5982 \u90a3\u65e5\u88ab\u6c34\u51b2\u53bb\u3002\u201d\u5979\u6108\u60f3\u6108\u662f\u54c0\u4f24\uff0c\u5ffd\u7136\uff0c\u4e00\u9635\u54cd\u4eae\u7684\u51fb\u9523\u58f0\uff0c\u6253\u65ad\u4e86\u5979\u65e0\u5c3d\u7684\u54c0\u601d\u3002 \u3000\u3000\u5c0f\u4f59\u539f\u6765\u4e00\u76f4\u7ad9\u5728\u8eab\u65c1\uff0c\u4ed6\u89c1\u5170\u59d1\u795e\u8272\u51c4\u82e6\uff0c\u4e00\u65f6\u4e5f\u4e0d\u77e5\u5982\u4f55\u5b89\u6170\u5979\uff0c\u5fc3\u4e2d\u6b63\u81ea\u7eb3\u95f7\uff0c \u4ed6\u7ae5\u5fc3\u672a\u6df7\uff0c\u4e00\u542c\u9523\u58f0\uff0c\u5982\u91ca\u91cd\u8d1f\uff0c\u4fbf\u5954\u51fa\u53bb\u770b\u70ed\u95f9\u3002 \u3000\u3000\u963f\u5170\u6b63\u51c6\u5907\u56de\u623f\uff0c\u7a81\u7136\u4e00\u58f0\u6e05\u8106\u53eb\u58f0\uff1a\u201c\u5170\u59d1\u5a18\uff01\u5170\u59d1\u5a18\uff01\u201d \u3000\u3000\u5979\u773c\u867d\u770b\u4e0d\u89c1\uff0c\u4f46\u8033\u6735\u5374\u662f\u7075\u654f\u5df2\u6781\uff0c\u4f46\u89c9\u90a3\u58f0\u97f3\u751a\u662f\u719f\u6089\uff0c\u4f46\u9877\u523b\u95f4\u53c8\u60f3\u4e0d\u51fa\u5230\u5e95\u662f \u4f55\u4eba\uff1f \u3000\u3000\u5c0f\u4f59\u6025\u5fd9\u8fdb\u6765\u5598\u606f\u9053\uff1a\u201c\u54b1\u4eec\u9655\u897f\u65b0\u5de1\u629a\u91d1\u5927\u4eba\u7684\u5c0f\u59d0\uff0c\u5979\u5728\u53eb\u4f60\u54e9\uff01\u201d \u3000\u3000\u963f\u5170\u7565\u4e00\u6c89\u541f\uff0c\u604d\u7136\u5927\u609f\uff0c\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u662f\u82cf\u2026\u2026\u82cf\u59d1\u5a18\uff0c\u90a3\u4e48\u4ed6\u4e5f\u4e00\u5b9a\u6765\u5566\uff0c\u6211\u4f55\u5fc5 \u8981\u89c1\u4ed6\u4eec\u3002\u201d \u3000\u3000\u4fbf\u5bf9\u5c0f\u4f59\u8bf4\u9053\uff1a\u201c\u4f60\u53bb\u5bf9\u5979\u8bf4\uff0c\u6211\u5e76\u4e0d\u8ba4\u8bc6\u5979\uff0c\u4e00\u5b9a\u662f\u5979\u8ba4\u9519\u4eba\u4e86\u3002\u201d \u3000\u3000\u5c0f\u4f59\u5fc3\u4e2d\u597d\u751f\u4e3a\u96be\uff0c\u6b63\u5728\u8fd9\u65f6\uff0c\u82cf\u60e0\u82b7\u5df2\u7ecf\u8d70\u5230\u95e8\u53e3\uff0c\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u5170\u59d1\u5a18\uff0c\u4f60\u5f53\u771f\u4e0d \u8ba4\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u963f\u5170\u5fc3\u4e2d\u5fae\u7a98\uff0c\u60f3\u5230\u81ea\u5df1\u4e00\u751f\u5e78\u798f\uff0c\u5c31\u662f\u65ad\u9001\u4e8e\u6b64\u4eba\u4e4b\u624b\uff0c\u4e0d\u89c9\u6c14\u5f80\u4e0a\u51b2\uff0c\u8ba5\u8bbd\u9053\uff1a \u201c\u539f\u6765\u662f\u82cf\u5927\u5c0f\u59d0\uff0c\u6c11\u5973\u5bb6\u4e2d\u9648\u8bbe\u7b80\u964b\uff0c\u662f\u4ee5\u4e0d\u6562\u76f8\u63a5\u5f85\u82b3\u9a7e\u3002\u201d \u3000\u3000\u5979\u8bdd\u4e2d\u51fa\u53e3\uff0c\u5fc3\u4e2d\u5df2\u6709\u4e9b\u540e\u6094\uff0c\u5979\u7b80\u76f4\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u7adf\u80fd\u8bf4\u51fa\u8fd9\u79cd\u5c16\u9510\u4f24\u4eba\u7684\u8bdd\u3002 \u3000\u3000\u82cf\u60e0\u82b7\u5e76\u4e0d\u751f\u6c14\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5170\u59d1\u5a18\uff0c\u4f60\u8fd8\u751f\u6211\u6c14\uff1f\u4f60\u77e5\u9053\u4f60\u5434\u5927\u54e5\u73b0\u5728\u5728\u4ec0\u4e48\u5730 \u65b9\uff1f\u201d \u3000\u3000\u4e00\u63d0\u5230\u5434\u51cc\u98ce\uff0c\u963f\u5170\u60c5\u4e0d\u81ea\u7981\u7684\u6ce8\u610f\u8d77\u6765\uff0c\u5979\u6447\u6447\u5934\u9053\uff1a\u201c\u5979\u96be\u9053\u4e0d\u548c\u4f60\u5728\u4e00\u8d77\uff1f\u201d \u3000\u3000\u82cf\u60e0\u82b1\u51c4\u7136\u9053\uff1a\u201c\u4f60\u5434\u5927\u54e5\u6b63\u5728\u5929\u6daf\u6d77\u89d2\u5bfb\u4f60\u5462\uff1f\u201d \u3000\u3000\u963f\u5170\u4e00\u542c\uff0c\u987f\u65f6\u5982\u7126\u96f7\u8f70\u9876\uff0c\u5979\u5f3a\u81ea\u6587\u6301\uff0c\u98a4\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u53ef\u662f\u771f\u7684\u5417\uff1f\u201d \u3000\u3000\u82cf\u60e0\u82b7\u8d70\u4e0a\u524d\u6301\u7740\u5979\u53cc\u624b\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u5170\u59d1\u5a18\uff0c\u4e0d\uff0c\u6211\u53eb\u4f60\u5170\u59b9\u59b9\u597d\u5417\uff1f\u201d \u3000\u3000\u963f\u5170\u542c\u5979\u8bf4\u5f97\u8bda\u6073\uff0c\u4fbf\u70b9\u70b9\u5934\u3002 \u3000\u3000\u82cf\u60e0\u82b7\u5f88\u8bda\u6073\u7684\u8bf4\u9053\uff1a\u201c\u90a3\u5929\u4f60\u8d1f\u6c14\u4e00\u8d70\uff0c\u6b21\u6668\u5434\u5927\u54e5\u4e00\u77e5\u6b64\u4e8b\uff0c\u4fbf\u5982\u5931\u9b42\u843d\u9b44\uff0c\u4ed6\u8feb \u4e0d\u6025\u5f85\u7684\u5c31\u548c\u6211\u544a\u522b\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u5230\u54ea\u91cc\u53bb\u627e\u4f60\u4e86\uff1f\u5170\u59b9\uff0c\u5f53\u771f\uff0c\u5434\u5927\u54e5\u5c31\u53ea\u559c\u6b22\u4f60\u4e00\u4e2a\u4eba\u3002 \u4f60\u2026\u2026\u4f60\u771f\u6709\u798f\u6c14\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u7f9e\u6da9\u9053\uff1a\u201c\u5170\u59b9\uff0c\u4e0d\u6ee1\u4f60\u8bf4\uff0c\u4f60\u2026\u2026\u6211\u539f\u662f\u5f88\u559c\u6b22\u2026\u2026\u5f88\u518d\u6b22\u5434\u5927\u54e5\u7684\uff0c\u53ef\u662f\u6211 \u771f\u7b28\uff0c\u6211\u4e00\u76f4\u4e5f\u4ee5\u4e3a\u4ed6\u559c\u6b22\u6211\uff0c\u5230\u73b0\u5728\u6211\u624d\u660e\u767d\uff0c\u539f\u6765\u4ed6\u5fc3\u4e2d\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\u3002\u90a3\u65e5\u9152\u9189\uff0c\u4ed6 \u8bef\u8ba4\u6211\u4e3a\u4f60\uff0c\u662f\u4ee5\u9020\u6210\u8bef\u4f1a\uff0c\u5170\u59b9\uff0c\u4ed6\u7528\u60c5\u771f\u4e13\uff0c\u6709\u8fd9\u6837\u82f1\u4fca\u7684\u5c11\u5e74\uff0c\u4e13\u5fc3\u4e00\u610f\u7684\u7231\u4f60\uff0c\u4f60 \u771f\u5e78\u798f\uff0c\u6211\u2026\u2026\u6211\u4e5f\u66ff\u4f60\u9ad8\u5174\u3002\u201d \u3000\u3000\u963f\u5170\u6108\u542c\u6108\u662f\u54c0\u75db\uff0c\u6094\u6068\u3001\u81ea\u8d23\u7684\u60c5\u7eea\uff0c\u4e00\u9f50\u6d8c\u5230\u5979\u80f8\u4e2d\uff0c\u4f46\u89c1\u5979\u8138\u4e0a\u65f6\u800c\u7ea2\u6655\uff0c\u65f6\u800c \u60e8\u767d\uff0c\u6700\u540e\uff0c\u5979\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u5012\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u5c0f\u4f59\u8d76\u5fd9\u6276\u6276\u4f4f\u5979\uff0c\u82cf\u8559\u82b7\u6025\u9053\uff1a\u201c\u5170\u59b9\uff0c\u4f60\u600e\u4e48\u5566\uff01\u4f60\u54ea\u513f\u4e0d\u8212\u670d\uff1f\u201d \u3000\u3000\u963f\u5170\u5f3a\u60e8\u7b11\u9053\uff1a\u201c\u82cf\u59d0\u59d0\uff0c\u6211\u4e00\u65f6\u5934\u6655\uff0c\u6240\u4ee5\u652f\u6301\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u82cf\u60e0\u82b7\u9053\uff1a\u201c\u4f60\u5148\u8fdb\u5c4b\u4f11\u606f\uff0c\u6211\u4e5f\u8981\u8d70\u4e86\uff0c\u4eca\u665a\u957f\u5b89\u57ce\u7684\u7f19\u7ec5\u66ff\u6211\u4e49\u7236\u63a5\u98ce\uff0c\u6211\u4e5f\u8981 \u53bb\uff0c\u6539\u5929\u518d\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u963f\u5170\u70b9\u70b9\u5934\uff0c\u6276\u7740\u5c0f\u4f59\uff0c\u8d70\u8fdb\u5c4b\u91cc\uff0c\u5173\u8d77\u5367\u623f\u7684\u95e8\uff0c\u5bf9\u5c0f\u4f59\u8bf4\uff1a\u201c\u6211\u8981\u597d\u597d\u7761\u4e00\u89c9\uff0c\u4f60 \u53ef\u522b\u6765\u6253\u6270\u3002\u201d \u3000\u3000\u5c0f\u4f59\u521a\u624d\u542c\u5979\u548c\u82cf\u60e0\u82b7\u4e00\u6bb5\u5bf9\u8bdd\uff0c\u5fc3\u4e2d\u7565\u6709\u6240\u609f\uff0c\u53ea\u89c9\u4e0d\u5e78\u7684\u4e8b\u4fbf\u8981\u53d1\u751f\uff0c\u8131\u53e3\u9053\uff1a \u201c\u5170\u59d1\uff0c\u4f60\u53ef\u5343\u4e07\u522b\u6c14\u82e6\u3002\u201d \u3000\u3000\u963f\u5170\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u5c0f\u4f59\uff0c\u4f60\u522b\u778e\u60f3\uff0c\u6211\u6709\u4ec0\u4e48\u597d\u6c14\u7684\u3002\u201d \u3000\u3000\u5c0f\u4f59\u65e0\u5948\uff0c\u53ea\u5f97\u9000\u51fa\u3002 \u3000\u3000\u963f\u5170\u8eba\u5728\u5e8a\u4e0a\uff0c\u5fc3\u5185\u6709\u5982\u5200\u7ede\uff0c\u5979\u5fc3\u60f3\uff1a\u201c\u539f\u6765\u5927\u54e5\u8fd8\u662f\u8fd9\u4e48\u7231\u6211\u914c\uff0c\u6211\u2026\u2026\u8fd8\u6709\u4ec0\u4e48 \u9762\u76ee\u89c1\u4ed6\u5462\uff1f \u3000\u3000\u5728\u4ed6\u5fc3\u4e2d\uff0c\u6211\u4e00\u5b9a\u662f\u6700\u5b8c\u7f8e\u7684\u5973\u5b69\uff0c\u8fd9\u662f\u4e0d\u7528\u4ed6\u8bf4\uff0c\u6211\u4e5f\u660e\u767d\u7684\uff0c\u56e0\u4e3a\u8fd9\u6b63\u5982\u4ed6\u5728\u6211\u5fc3 \u4e2d\u7684\u5206\u91cf\u3002\u6211\u2026\u2026\u6211\u8981\u8bbe\u6cd5\u4f7f\u4ed6\u6c38\u8fdc\u4fdd\u6301\u8fd9\u4e2a\u5b8c\u7f8e\u7684\u5370\u50cf\uff0c\u4f46\u6709\u4ec0\u4e48\u65b9\u6cd5\u5462\uff1f\u554a\uff01\u5bf9\u4e86\uff01\u53ea \u6709\u6b7b\uff0c\u53ea\u6709\u6b7b\uff0c\u624d\u80fd\u8fbe\u5230\u8fd9\u79cd\u76ee\u7684\u3002\u201d \u3000\u3000\u60f3\u5230\u6b7b\uff0c\u5979\u5fc3\u4e2d\u6e10\u6e10\u5b89\u5b9a\u4e0b\u6765\uff0c\u8f6c\u5ff5\u53c8\u60f3\u9053\uff1a\u201c\u53ef\u662f\uff0c\u6211\u603b\u8fd8\u8981\u518d\u4f1a\u4ed6\u4e00\u9762\uff0c\u7136\u540e\uff0c\u7136 \u540e\u518d\u4e86\u5374\u6211\u8fd9\u4e00\u751f\u3002\u201d \u3000\u3000\u5979\u76d8\u7b97\u5df2\u5b9a\uff0c\u5fc3\u4e2d\u53cd\u89c9\u6cf0\u7136\u3002\u65f6\u5149\u5012\u6d41\u8fc7\u53bb\uff0c\u5979\u8fd9\u4e00\u751f\u77ed\u77ed\u5341\u591a\u4eca\u5e74\u5934\u7684\u60c5\u666f\uff0c\u4e00\u5e55\u5e55 \u5982\u98de\u7684\u4ece\u5979\u8111\u6d77\u6df1\u5904\u6d6e\u8d77\uff0c\u53c8\u98de\u5feb\u7684\u901d\u53bb\u3002 \u3000\u3000\u51ac\u9633\u7167\u5728\u5899\u4e0a\u672a\u878d\u7684\u79ef\u96ea\uff0c\u53cd\u6620\u7740\u5979\u60e8\u767d\u7684\u8138\uff0c\u5979\u7684\u5fc3\u6e10\u6e10\u4e0b\u6c89\uff0c\u4e0b\u6c89\u2026\u2026 \u3000\u3000\u4e16\u754c\u4e0a\u7684\u4e8b\uff0c\u5f80\u5f80\u90fd\u4e0d\u53ef\u601d\u8bae\u7684\uff0c\u5c31\u5728\u963f\u5170\u78b0\u5230\u82cf\u8559\u82b7\u7684\u7b2c\u4e8c\u5929\uff0c\u5434\u51cc\u98ce\u4e5f\u5230\u4e86\u957f\u5b89\uff0c \u800c\u4e14\u90a3\u4e48\u51d1\u5de7\u7684\u9047\u5230\u4e86\u82cf\u60e0\u82b7\u7684\u5a62\u5973\u5c0f\u8299\uff0c\u5c0f\u8299\u544a\u8bc9\u4ed6\u963f\u5170\u7684\u60c5\u5f62\uff0c\u51cc\u98ce\u5185\u5fc3\u7830\u7136\u76f4\u8df3\uff0c\u4ed6 \u95ee\u660e\u4e86\u963f\u5170\u7684\u4f4f\u5740\uff0c\u4fbf\u5954\u4e86\u53bb\u3002 \u3000\u3000\u539f\u6765\u5434\u51cc\u98ce\u548c\u8f9b\u6377\u5728\u4e94\u534e\u5c71\u548c\u56db\u5927\u6d3e\u638c\u95e8\u4eba\u51b3\u6597\u5927\u83b7\u5168\u80dc\u540e\uff0c\u51cc\u98ce\u7236\u4ec7\u5df1\u62a5\uff0c\u5fc3\u4e2d\u53ea\u6709 \u4e00\u4ef6\u7275\u6302\u4e4b\u4e8b\u2014\u2014\u5bfb\u627e\u963f\u5170\u6bcd\u5973\uff0c\u8f9b\u6377\u4e5f\u6025\u7740\u8981\u53bb\u627e\u90a3\u5929\u771f\u65e0\u90aa\u7684\u5f20\u83c1\uff0c\u662f\u4ee5\u4e24\u4eba\u544a\u522b\u201c\u4e03 \u5999\u795e\u541b\u201d\u6885\u5c71\u6c11\uff0c\u5206\u4e24\u8def\u5bfb\u8bbf\uff0c\u5e76\u7ea6\u5b9a\u4e00\u6708\u540e\u5728\u957f\u5b89\u897f\u57ce\u95e8\u4f1a\u9762\u3002 \u3000\u3000\u51cc\u98ce\u4e00\u8def\u4e0a\u8dcb\u5c71\u6d89\u6c34\uff0c\u4f46\u6beb\u65e0\u7ed3\u679c\uff0c\u7b97\u7b97\u4e0e\u8f9b\u6377\u7684\u7ea6\u671f\u5df2\u8fd1\uff0c\u65e0\u5948\u4e4b\u4e0b\uff0c\u53ea\u6709\u76f4\u5954\u957f \u5b89\uff0c\u8fd9\u65e5\u6e05\u6668\u8fdb\u4e86\u57ce\uff0c\u4e0d\u6599\u649e\u7740\u5c0f\u8299\uff0c\u5c0f\u8299\u56e0\u4e3a\u662f\u82cf\u60e0\u82b7\u8d34\u8eab\u5f85\u5973\uff0c\u662f\u4ee5\u5bf9\u4e8e\u5434\u51cc\u98ce\u3001\u963f\u5170 \u53ca\u82cf\u60e0\u82b7\u95f4\u7684\u8bef\u4f1a\u6781\u4e3a\u6e05\u695a\uff0c\u6628\u65e5\u82cf\u60e0\u82b7\u4e0e\u963f\u5170\u76f8\u4f1a\uff0c\u5979\u4e5f\u5c31\u5750\u5728\u82cf\u60e0\u82b7\u8f7f\u4e2d\uff0c\u5979\u5bf9\u51cc\u98ce\u5f88 \u611f\u540c\u60c5\uff0c\u6240\u4ee5\u4fbf\u6025\u6025\u544a\u8bc9\u4e86\u51cc\u98ce\u963f\u5170\u7684\u60c5\u51b5\u3002 \u3000\u3000\u51cc\u98ce\u4f9d\u7740\u5c0f\u82f1\u6240\u6307\u793a\uff0c\u8d70\u5230\u897f\u5927\u8857\uff0c\u5fc3\u4e2d\u6108\u6765\u6108\u7d27\u5f20\uff0c\u4e5f\u6108\u6765\u6108\u9ad8\u5174\uff0c\u4ed6\u5fc3\u60f3\uff1a\u201c\u8981\u662f \u963f\u5170\u53d1\u89c9\u6211\u7a81\u7136\u627e\u5230\u5979\uff0c\u5979\u4e0d\u77e5\u6709\u591a\u9ad8\u5174\uff0c\u5982\u4eca\uff0c\u82cf\u59d1\u5a18\u5373\u5df2\u7ed9\u5979\u89e3\u91ca\u6e05\u695a\uff0c\u5979\u4e00\u5b9a\u4e0d\u518d\u6068 \u6211\u4e86\uff0c\u5982\u679c\uff0c\u5979\u77e5\u9053\u5979\u5927\u54e5\u8d39\u5c3d\u5fc3\u529b\u7ec8\u4e8e\u628a\u90a3\u5343\u8f7d\u96be\u9022\u7684\u8840\u679c\u627e\u5230\u2014\u2014\u90a3\u80fd\u4f7f\u5979\u5728\u9ed1\u6697\u4e2d\u91cd \u89c1\u5149\u660e\u7684\u7075\u836f\uff0c\u5979\u4f1a\u600e\u6837\u611f\u6fc0\u6211\u5462\uff1f\u201d \u3000\u3000\u7ec8\u4e8e\uff0c\u4ed6\u5230\u4e86\u5c0f\u8299\u6240\u6307\u7684\u5c4b\u5b50\uff0c\u4ed6\u8f7b\u6b65\u4e0a\u524d\uff0c\u6572\u4e86\u4e24\u4e0b\u95e8\uff0c\u4e00\u4e2a\u5c0f\u53ae\u51fa\u6765\u5f00\u95e8\u3002 \u3000\u3000\u51cc\u98ce\u95ee\u9053\uff1a\u201c\u5170\u59d1\u5a18\u53ef\u5728\uff1f\u201d \u3000\u3000\u90a3\u4e2a\u53ae\u6b63\u662f\u5c0f\u4f59\uff0c\u4ed6\u6253\u91cf\u4e86\u51cc\u98ce\u4e24\u773c\uff0c\u5f15\u51cc\u98ce\u8fdb\u4e86\u5ba2\u5385\uff0c\u4fbf\u8fdb\u53bb\u62a5\u4fe1\u3002 \u3000\u3000\u51cc\u98ce\u4e3e\u76ee\u4e00\u770b\uff0c\u53ea\u89c1\u9648\u8bbe\u9887\u4e3a\u534e\u8d35\uff0c\u5fc3\u4e2d\u6b63\u81ea\u8be7\u5f02\uff0c\u6697\u5fd6\uff1a\u201c\u5c0f\u8299\u672b\u8bf4\u660e\u963f\u5170\u4f4f\u5728\u8c01 \u5bb6\uff0c\u8fd9\u4e3b\u4eba\u5f88\u662f\u6709\u94b1\u3002\u201d \u3000\u3000\u7b49\u4e86\u534a\u5929\uff0c\u4e5f\u4e0d\u89c1\u963f\u5170\u51fa\u6765\uff0c\u51cc\u98ce\u5fc3\u4e2d\u5f88\u662f\u4e0d\u5b89\uff0c\u6b63\u60f3\u7ad9\u8d77\u8eab\u8d70\u8fd1\u4e9b\u53bb\u770b\u770b\uff0c\u5ffd\u7136\u95e8\u5e18 \u5f00\u5904\uff0c\u663e\u51fa\u4e86\u4e00\u5f20\u4fcf\u751f\u751f\u7684\u4fcf\u8138\u3002 \u3000\u3000\u539f\u6765\u963f\u5170\u4e00\u542c\u5c0f\u4f59\u62a5\u544a\uff0c\u4fbf\u77e5\u662f\u51cc\u98ce\u5230\u6765\uff0c\u5979\u53ef\u4e07\u4e07\u6ca1\u6709\u60f3\u5230\uff0c\u4e8b\u60c5\u662f\u90a3\u4e48\u7a81\u7136\uff0c\u5979\u5929 \u5929\u76fc\u671b\u7740\u89c1\u51cc\u98ce\u4e00\u9762\uff0c\u53ef\u662f\u6b64\u65f6\u51cc\u98ce\u6765\u5230\uff0c\u5979\u5fc3\u4e2d\u72b9\u8c6b\u4e0d\u5b9a\uff0c\u7adf\u50cf\u505a\u9519\u4e8b\u7684\u5c0f\u5b69\uff0c\u5bb3\u6015\u89c1\u7236 \u6bcd\u4e00\u822c\u3002 \u3000\u3000\u6700\u540e\uff0c\u5979\u4e0b\u4e86\u51b3\u5fc3\uff0c\u60f3\u9053\uff1a\u201c\u4e16\u754c\u4e0a\u96be\u9053\u6709\u6bd4\u6b7b\u66f4\u4ee4\u4eba\u5bb3\u6015\u7684\u4e8b\u5417\uff1f\u6211\u6b7b\u90fd\u4e0d\u6015\uff0c\u90a3\u8fd8 \u6015\u4ec0\u4e48\uff1f\u201d\u662f\u4ee5\u4fbf\u8d70\u4e86\u51fa\u6765\u3002 \u3000\u3000\u90a3\u5f20\u8138\uff0c\u66fe\u4f7f\u51cc\u98ce\u5982\u75f4\u5982\u9189\u8fc7\uff0c\u4e5f\u66fe\u4f7f\u4ed6\u820d\u751f\u5fd8\u6b7b\u8fc7\uff0c\u6b64\u65f6\u9661\u7136\u51fa\u73b0\uff0c\u51cc\u98ce\u5446\u4e86\u4e00\u4f1a\uff0c \u7adf\u4e0d\u77e5\u8bf4\u4ec0\u4e48\u662f\u597d\u3002 \u3000\u3000\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u8d70\u4e0a\u524d\u4e24\u6b65\uff0c\u8f7b\u8f7b\u63e1\u4f4f\u963f\u5170\u7684\u624b\uff0c\u6fc0\u52a8\u9053\uff1a\u201c\u963f\u5170\uff0c\u6211\u2026\u2026\u6211\u603b\u7b97\u627e\uff0c\u627e \u5230\u4f60\u4e86\u3002\u201d \u3000\u3000\u963f\u5170\u987a\u52bf\u5012\u5728\u4ed6\u6000\u4e2d\uff0c\u53cd\u590d\u54ed\u5230\uff1a\u201c\u5927\u54e5\u4f60\u7ec8\u4e8e\u6765\u4e86\uff0c\u4f60\u7ec8\u4e8e\u6765\u4e86\uff0c\u6211\u5929\u5929\u76fc\u671b\u7740\u4f60\uff0c \u4f60\u7ec8\u4e8e\u627e\u6765\u4e86\u3002\u201d \u3000\u3000\u51cc\u98ce\u9f3b\u4e00\u9178\uff0c\u773c\u89d2\u542b\u6cea\uff0c\u67d4\u58f0\u529d\u9053\uff1a\u201c\u963f\u5170\uff0c\u5feb\u522b\u54ed\u4e86\uff0c\u5feb\u64e6\u5e72\u773c\u6cea\uff0c\u54b1\u4eec\u5e94\u8be5\u6b22\u559c\u624d \u5bf9\u5462\uff0c\u4f60\u771f\u7684\u522b\u54ed\u4e86\uff0c\u6211\u6709\u6837\u4e1c\u897f\u9001\u4f60\uff0c\u4f60\u4e00\u5b9a\u9ad8\u5174\u3002\u201d \u3000\u3000\u963f\u5170\u54ed\u4e86\u4e00\u9635\uff0c\u5fc3\u60c5\u6e10\u6e10\u5e73\u9759\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u662f\u6700\u540e\u4e00\u6b21\u89c1\u5230\u5927\u54e5\u4e86\uff0c\u4ece\u6b64\u4ee5\u540e\uff0c\u5927\u54e5\u4fbf \u6c38\u8fdc\u4e0d\u4f1a\u518d\u770b\u5230\u6211\u4e86\uff0c\u5bf9\uff0c\u6211\u5e94\u8be5\u4f7f\u4ed6\u5feb\u4e50\u624d\u5bf9\u3002\u201d \u3000\u3000\u5979\u64e6\u5e72\u4e86\u6cea\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u8fd9\u5927\u534a\u5e74\u5230\u4e86\u4e9b\u4ec0\u4e48\u5730\u65b9\uff0c\u4f2f\u7236\u7684\u4ec7\u62a5\u4e86\u5417\uff1f\u201d \u3000\u3000\u51cc\u98ce\u89c1\u5979\u4e00\u5f00\u53e3\u4fbf\u95ee\u81ea\u5df1\u7684\u62a5\u4ec7\u5927\u4e8b\uff0c\u5bf9\u4e8e\u5979\u81ea\u5df1\u7684\u8d56\u4ee5\u590d\u660e\u7684\u8840\u679c\u6709\u6ca1\u6709\u5f97\u5230\u7adf\u4e00\u5b57 \u4e0d\u63d0\uff0c\u51cc\u98ce\u5fc3\u4e2d\u5927\u5927\u611f\u52a8\uff0c\u4fbf\u9053\uff1a\u201c\u6211\u8fd9\u534a\u5e74\u591a\u7684\u7ecf\u5386\u771f\u662f\u53c8\u60ca\u9669\uff0c\u53c8\u6709\u8da3\uff0c\u5f85\u65e5\u540e\u6709\u7a7a\u6211 \u518d\u6162\u6162\u8bb2\u7ed9\u4f60\u542c\uff0c\u6211\u5305\u4f60\u7231\u542c\u3002\u5c31\u5728\u4e0d\u591a\u4e00\u4e2a\u6708\u524d\uff0c\u6211\u548c\u6377\u5f1f\u5728\u4e94\u534e\u5c71\uff0c\u4ee5\u4e8c\u654c\u56db\uff0c\u6740\u5f97\u56db \u5927\u95e8\u6d3e\u638c\u95e8\u4eba\uff0c\u843d\u82b1\u6d41\u6c34\uff0c\u90a3\u6b66\u5f53\u6d3e\u8d64\u9633\u9053\u4eba\uff0c\u5d06\u5cd2\u5389\u9e57\u90fd\u88ab\u6211\u4eec\u6740\u4e86\uff0c\u5f53\u5e74\uff0c\u4ed6\u4eec\u56db\u4eba\u8054 \u624b\u4ee5\u6b64\u9635\u5f0f\u5bb3\u4e86\u7239\u7239\uff0c\u54fc\uff0c\u4ed6\u4eec\u6ca1\u60f3\u5230\u5728\u5341\u591a\u5e74\u540e\uff0c\u4f1a\u4e27\u751f\u5728\u8fd9\u9635\u6cd5\u4e0a\u5427\uff01\u201d \u3000\u3000\u5979\u5fc3\u4e2d\u867d\u7136\u60b2\u82e6\uff0c\u4f46\u542c\u5230\u51cc\u98ce\u5927\u4ec7\u5df1\u62a5\uff0c\u4e5f\u4e0d\u7981\u8840\u8109\u8d32\u5f20\uff0c\u632f\u594b\u8d5e\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6740\u5f97 \u597d\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u963f\u5170\uff0c\u5927\u5a18\u5462\uff1f\u201d \u3000\u3000\u4e00\u63d0\u8d77\u5927\u5a18\uff0c\u963f\u5170\u53c8\u5fcd\u4e0d\u4f4f\u6d41\u4e0b\u773c\u6cea\uff0c\u5979\u62bd\u6ce3\u9053\uff1a\u201c\u5988\u591a\u534a\u88ab\u5927\u6c34\u51b2\u8d70\u4e86\u3002\u201d\u4fbf\u628a\u90a3\u65e5 \u5927\u6c34\u60c5\u5f62\u8bb2\u7ed9\u51cc\u98ce\u542c\u3002 \u3000\u3000\u51cc\u98ce\u67d4\u58f0\u5b89\u6170\u9053\uff1a\u201c\u963f\u5170\uff0c\u90a3\u4e00\u5b9a\u4e0d\u4f1a\u7684\uff0c\u8001\u5929\u7237\u6c38\u8fdc\u662f\u5e2e\u597d\u4eba\u7684\uff0c\u5927\u5a18\u4e00\u5b9a\u4f1a\u8f6c\u5371\u4e3a \u5b89\u3002\u201d \u3000\u3000\u51cc\u98ce\u63a5\u7740\u9053\uff1a\u201c\u963f\u5170\uff0c\u4f60\u731c\u6211\u9001\u4f60\u7684\u662f\u4ec0\u4e48\u4e1c\u897f\u201d\uff1f \u3000\u3000\u963f\u5170\u60f3\u4e00\u4f1a\uff0c\u6447\u5934\u9053\uff1a\u201c\u6211\u731c\u4e0d\u7740\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u4f60\u73b0\u5728\u6700\u5e0c\u671b\u7684\u662f\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u963f\u5170\u9053\uff1a\u201c\u53ea\u8981\u5988\u548c\u4f60\u5b89\u597d\uff0c\u6211\u8fd8\u5e0c\u671b\u4ec0\u4e48\u5462\uff1f\u8001\u5929\u7237\u90fd\u662f\u5c0f\u6c14\u7684\uff0c\u6211\u8981\u6c42\u592a\u591a\u4e86\uff0c\u53cd \u800c\u5931\u671b\u5f97\u5389\u5bb3\u3002\u201d \u3000\u3000\u51cc\u98ce\u4ece\u6000\u4e2d\uff0c\u53d6\u51fa\u4e24\u4e2a\u78c1\u74f6\uff0c\u4e00\u4e2a\u662f\u88c5\u7740\u4e91\u7237\u7237\u8d60\u9001\u7684\u4e07\u5e74\u7075\u6cc9\uff0c\u53e6\u4e00\u4e2a\u88c5\u7740\u5728\u5927\u6222\u5c9b \u5f97\u5230\u7684\u8840\u679c\u6c41\u3002 \u3000\u3000\u51cc\u98ce\u67d4\u58f0\u9053\uff1a\u201c\u963f\u5170\uff0c\u6211\u8bf4\u8fc7\u8981\u66ff\u4f60\u627e\u5230\u8840\u679c\uff0c\u4f7f\u4f60\u53cc\u773c\u590d\u660e\uff0c\u603b\u7b97\u7687\u5929\u4e0d\u8d1f\u82e6\u5fc3\u4eba\uff0c \u7adf\u8ba9\u6211\u627e\u7740\u4e86\u3002\u6765\uff0c\u6211\u66ff\u4f60\u533b\u6cbb\u3002\u201d \u3000\u3000\u963f\u5170\u611f\u5230\u4e00\u9635\u6b22\u6b23\u2014\u2014\u4f46\u90a3\u53ea\u662f\u4e00\u523b\uff0c\u5979\u60f3\u9053\uff1a\u201c\u65f6\u95f4\u8fc7\u53bb\u4e00\u523b\uff0c\u6211\u548c\u5927\u54e5\u5728\u4e00\u8d77\u7684\u65f6 \u5019\u4fbf\u77ed\u4e86\u4e00\u523b\uff0c\u4f55\u5fc5\u8981\u6cbb\u4ec0\u4e48\u773c\u6674\uff0c\u6765\u803d\u8bef\u8fd9\u5b9d\u8d35\u65f6\u95f4\u3002\u201d\u4fbf\u9053\uff1a\u201c\u54b1\u4eec\u5148\u8c08\u8c08\u522b\u7684\u4e8b\uff0c\u522b \u5fd9\u533b\u6cbb\u3002\u201d \u3000\u3000\u51cc\u98ce\u89c1\u5979\u795e\u8272\u5e73\u9759\uff0c\u5927\u611f\u610f\u5916\u7684\u8bf4\u9053\uff1a\u201c\u6731\u592b\u5b50\u8bf4\u8fc7\uff0c\u53ea\u8981\u628a\u8840\u679c\u6c41\u670d\u4e0b\uff0c\u9759\u606f\u4e09\u4e2a\u65f6 \u8fb0\u4fbf\u89c1\u529f\u6548\uff0c \u3000\u3000\u4f55\u51b5\u73b0\u5728\u53c8\u6709\u4e07\u5e74\u7075\u6cc9\uff0c\u53ef\u4ee5\u5148\u628a\u773c\u5185\u88ab\u6bd2\u6240\u4fb5\u70c2\u7684\u808c\u8089\u590d\u539f\uff0c\u963f\u5170\uff0c\u4f60\u5148\u5403\u4e0b\u8fd9\u74f6\u8840 \u679c\u6c41\u3002\u201d \u3000\u3000\u963f\u5170\u62d7\u4ed6\u4e0d\u8fc7\uff0c\u53ea\u5f97\u63a5\u8fc7\u7389\u74f6\uff0c\u4e00\u996e\u800c\u5c3d\u3002 \u3000\u3000\u51cc\u98ce\u8981\u4e86\u4e00\u676f\u6c34\uff0c\u5012\u4e86\u51e0\u6ef4\u7075\u6cc9\uff0c\u7528\u4e00\u5757\u5e72\u51c0\u68c9\u5e03\u6d78\u6e7f\uff0c\u5c0f\u5fc3\u66ff\u963f\u5170\u6d17\u5b8c\u4e86\u540e\uff0c\u4ed6\u7528\u5e03 \u628a\u53cc\u773c\u5305\u8d77\u6765\uff0c\u6b22\u7136\u9053\uff1a\u201c\u8fc7\u4e09\u4e2a\u65f6\u8fb0\uff0c\u5f53\u6211\u628a\u5e03\u62c6\u5f00\u65f6\uff0c\u4f60\u4fbf\u53ef\u4ee5\u91cd\u89c1\u5149\u660e\u4e86\u3002\u201d \u3000\u3000\u963f\u5170\u6e29\u67d4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8c22\u8c22\u4f60\u5566\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u963f\u5170\uff0c\u4f60\u8fd9\u5c31\u53bb\u4f11\u606f\u3002\u201d \u3000\u3000\u963f\u5170\u6447\u5934\u9053\uff1a \u3000\u3000\u201c\u4e0d\uff0c\u5927\u54e5\uff01\u6211\u8981\u542c\u4f60\u8bb2\u6545\u4e8b\u3002\u201d \u3000\u3000\u51cc\u98ce\u65e0\u5948\uff0c\u4fbf\u628a\u81ea\u5df1\u8fd9\u534a\u5e74\u7684\u7ecf\u5386\u62e3\u6709\u8da3\u7684\u8bf4\u7ed9\u5979\u542c\uff0c\u51cc\u98ce\u8d8a\u8bf4\u5174\u8da3\u8d8a\u9ad8\uff0c\u963f\u5170\u9759\u9759\u7684 \u542c\u7740\uff0c\u5f53\u5979\u542c\u5230\u51cc\u98ce\u7ecf\u5386\u8270\u82e6\uff0c\u624d\u628a\u8840\u679c\u5f97\u5230\uff0c\u4e0d\u7981\u611f\u6fc0\u6d41\u4e0b\u6cea\u6765\u3002 \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u73b0\u5728\uff0c\u82e6\u96be\u5df1\u7ecf\u8fc7\u4e86\uff0c\u4ec7\u4e5f\u62a5\u4e86\uff0c\u963f\u5170\uff0c\u54b1\u4eec\u56de\u5bb6\u53bb\uff0c\u79cd\u7530\u683d\u82b1\uff0c\u6c38\u8fdc\u5728\u4e00 \u8d77\uff0c\u518d\u4e5f\u4e0d\u8981\u5206\u79bb\u4e86\u3002\u201d \u3000\u3000\u963f\u5170\u5fae\u7b11\uff0c\u4f46\u7b11\u5bb9\u655b\u5904\uff0c\u773c\u89d2\u95ea\u8d77\u4e00\u79cd\u51c4\u51c9\u795e\u8272\u3002 \u3000\u3000\u51cc\u98ce\u559c\u6c14\u6d0b\u6d0b\uff0c\u662f\u4ee5\u5e76\u672a\u6ce8\u610f\uff0c\u4ed6\u7ee7\u7eed\u9053\uff1a\u201c\u54b1\u4eec\u627e\u5230\u5927\u5a18\uff0c\u6211\u53ef\u8981\u597d\u597d\u62a5\u7b54\u5979\u8001\u4eba \u5bb6\uff0c\u5bb6\u4e61\u7684\u623f\u5b50\u4e00\u5b9a\u88ab\u5927\u6c34\u51b2\u8d70\u4e86\uff0c\u90a3\u4e5f\u597d\uff0c\u6211\u4eec\u5c31\u642c\u5230\u6cf0\u5c71\u811a\u4e0b\uff0c\u5728\u90a3\u91cc\u76d6\u4e00\u680b\u623f\u5b50\uff0c\u8fd9 \u6837\u6211\u4eec\u4fbf\u53ef\u5e38\u5e38\u53bb\u770b\u4e91\u7237\u7237\u3002\u963f\u5170\uff0c\u90a3\u4e91\u7237\u7237\u559c\u6b22\u4f60\u5f97\u5f88\uff0c\u4ed6\u518d\u4e09\u53ee\u5631\u6211\u8981\u5e26\u4f60\u53bb\u89c1\u4ed6\u54e9\uff01 \u554a\uff01\u5bf9\u4e86\uff0c\u4ed6\u4f4f\u5728\u90a3\u513f\u67a3\u5b50\u771f\u597d\uff0c\u53c8\u5927\u53c8\u751c\uff0c\u4f60\u4e00\u5b9a\u559c\u6b22\u5403\u3002\u201d \u3000\u3000\u963f\u5170\u5ffd\u89c9\u773c\u6674\u53d1\u75d2\uff0c\u4f38\u624b\u53bb\u89e3\u8499\u4f4f\u53cc\u773c\u7684\u5e03\u5e26\u3002 \u3000\u3000\u51cc\u98ce\u5f00\u53e3\u963b\u6b62\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u611f\u89c9\uff1f\u201d \u3000\u3000\u963f\u5170\u9053\uff1a\u201c\u6211\u773c\u6674\u75d2\u5f97\u5f88\u3002\u201d \u3000\u3000\u51cc\u98ce\u5927\u559c\u9053\uff1a\u201c\u6210\u4e86\uff0c\u6210\u4e86\uff0c\u60f3\u4e0d\u5230\u8fd9\u7075\u836f\u529f\u6548\u771f\u5feb\uff0c\u963f\u5170\uff0c\u6c89\u4f4f\u6c14\uff0c\u6211\u6765\u66ff\u4f60\u89e3 \u5f00\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u9ed8\u7977\uff0c\u6025\u5fd9\u7684\u89e3\u5f00\u963f\u5170\u773c\u4e0a\u6240\u5305\u5e03\u5e26\uff0c\u963f\u5170\u53ea\u89c9\u4e00\u9635\u4e0d\u80fd\u5fcd\u53d7\u7684\u4eae\u5149\uff0c\u4f7f\u5979\u660f\u7729 \u5012\u5730\u3002 \u3000\u3000\u51cc\u98ce\u6025\u9053\uff1a\u201c\u963f\u5170\u600e\u4e48\u4e86\uff1f\u600e\u4e48\u4e86\uff1f\u201d \u3000\u3000\u963f\u5170\u6162\u6162\u7ad9\u8d77\u6765\uff0c\u5979\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u51dd\u89c6\u7740\u51cc\u98ce\uff0c\u534a\u664c\uff0c\u8c46\u5927\u7684\u6cea\u73e0\u987a\u988a\u6d41\u4e0b\u3002 \u3000\u3000\u51cc\u98ce\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u80fd\u770b\u89c1\u6211\u5427\uff1f\u201d \u3000\u3000\u963f\u5170\u70b9\u70b9\u5934\uff0c\u51cc\u98ce\u6b22\u53eb\u4e00\u58f0\uff0c\u62b1\u8d77\u5979\u9ad8\u5174\u5728\u5c4b\u4e2d\u6253\u8f6c\u3002 \u3000\u3000\u963f\u5170\u67d4\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u628a\u6211\u653e\u4e0b\u6765\u3002\u201d \u3000\u3000\u51cc\u98ce\u5fae\u4e00\u9519\u6115\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u77a7\u6211\u771f\u4e50\u660f\u5566\uff0c\u5bf9\uff0c\u963f\u5170\uff0c\u4f60\u53cc\u76ee\u521d\u590d\uff0c\u4e0d\u80fd\u4e45\u7528\u76ee\u529b\uff0c\u4f60 \u8d76\u5feb\u5230\u5e8a\u4e0a\u53bb\u7761\u4e00\u89c9\u3002\u201d\u8bf4\u7740\uff0c\u5c31\u62b1\u7740\u5979\u8d70\u8fdb\u5367\u5ba4\u53bb\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u628a\u963f\u5170\u653e\u5728\u5e8a\u4e0a\uff0c\u66ff\u5979\u76d6\u4e0a\u88ab\u5b50\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u6211\u7b49\u4f1a\u518d\u6765\u770b\u4f60\u3002\u201d \u3000\u3000\u963f\u5170\u6293\u4f4f\u51cc\u98ce\u7684\u624b\u6025\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u522b\u79bb\u5f00\u6211\u3002\u201d \u3000\u3000\u51cc\u98ce\u89c1\u5979\u8138\u4e0a\u7684\u795e\u8272\u60f6\u6025\uff0c\u4fbf\u4f9d\u8a00\u5750\u5728\u5e8a\u8fb9\u3002 \u3000\u3000\u963f\u5170\u6ce8\u89c6\u7740\u51cc\u98ce\uff0c\u4f46\u89c1\u51cc\u98ce\u4fca\u76ee\u4e2d\u5305\u542b\u7740\u5343\u822c\u601c\u7231\uff0c\u4ee4\u4eba\u4e0d\u80fd\u81ea\u6291\u3002 \u3000\u3000\u963f\u5170\u5ffd\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u76f8\u4e0d\u76f8\u4fe1\u5929\u4e0a\u6709\u4e2a\u4e50\u56ed\u3002\u201d \u3000\u3000\u51cc\u98ce\u832b\u7136\uff0c\u4e0d\u89e3\u5979\u95ee\u8bdd\u4e4b\u610f\uff0c\u6447\u5934\u9053\uff1a\u201c\u90a3\u6050\u6015\u662f\u5047\u7684\u3002\u201d \u3000\u3000\u963f\u5170\u597d\u751f\u5931\u671b\uff0c\u60f3\u9053\uff1a\u201c\u96be\u9053\u5988\u8bb2\u7684\u6545\u4e8b\u90fd\u4e0d\u662f\u771f\u7684\uff1f\u201d \u3000\u3000\u51cc\u98ce\u529d\u9053\uff1a\u201c\u4f60\u522b\u778e\u60f3\uff0c\u597d\u597d\u517b\u517b\u795e\u5427\u3002\u201d \u3000\u3000\u963f\u5170\u4e0d\u4f9d\uff0c\u7f20\u7740\u51cc\u98ce\u53ea\u662f\u8c08\u7740\u513f\u65f6\u7684\u8da3\u4e8b\uff0c\u51cc\u98ce\u542c\u5979\u5a13\u5a13\u8bf4\u8d77\uff0c\u4e0d\u7981\u4e5f\u56de\u5fc6\u8d77\u5c0f\u65f6\u60c5 \u666f\uff0c\u5185\u5fc3\u5f88\u611f\u6e29\u99a8\u3002 \u3000\u3000\u963f\u5170\u9053:\u201c\u5927\u54e5\uff0c\u4f60\u8fd8\u8bb0\u4e0d\u8bb0\u5f97\u6709\u4e00\u6b21\u54b1\u4eec\u4e0a\u5c71\u91c7\u91ce\u83dc\uff0c\u9047\u5230\u4e00\u5934\u5927\u7070\u72fc\uff1f\u201d \u3000\u3000\u51cc\u98ce\u63a5\u53e3\u7b11\u9053\uff1a\u201c\u90a3\u65f6\u6211\u4eec\u5413\u5f97\u624b\u811a\u90fd\u8f6f\u4e86\uff0c\u6c14\u90fd\u4e0d\u6562\u51fa\u91cd\u4e00\u4e9b\uff0c\u603b\u7b97\u6ca1\u88ab\u90a3\u53ea\u8be5\u6b7b\u7684 \u5927\u7070\u72fc\u53d1\u89c9\u3002\u201d \u3000\u3000\u963f\u5170\u9053\uff1a\u201c\u6211\u6c38\u8fdc\u8bb0\u5f97\uff0c\u90a3\u65f6\u4f60\u867d\u7136\u5413\u5f97\u4e0d\u5f97\u4e86\uff0c\u53ef\u662f\u4f60\u5c0f\u624b\u4e0a\u8fd8\u7d27\u63e1\u7740\u4e00\u679d\u6811\u679d\uff0c\u7ad9 \u5728\u6211\u524d\u9762\u4fdd\u62a4\u6211\uff0c\u5927\u54e5\uff0c\u4f60\u5f85\u6211\u771f\u597d\uff0c\u8981\u662f\u6211\u8fd9\u4e00\u751f\u65e0\u6cd5\u62a5\u7b54\u4f60\uff0c\u6211\u5c31\u662f\u53d8\u9b3c\u4e5f\u62a5\u7b54\u4f60\u7684\u6069 \u60c5\u3002\u201d \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u963f\u5170\uff0c\u4e0d\u8981\u518d\u8bf4\u4e27\u6c14\u8bdd\u4e86\uff0c\u6211\u4eec\u597d\u65e5\u5b50\u5df2\u5230\u4e86\uff0c\u963f\u5170\uff0c\u6211\u5bf9\u6c5f\u6e56\u4e0a\u7684\u4e8b\u4e00\u76f4 \u4e0d\u611f\u5174\u8da3\uff0c\u53ea\u8981\u548c\u4f60\u53ae\u5b88\u5728\u4e00\u8d77\uff0c\u5c31\u662f\u997f\u7740\u51bb\u7740\uff0c\u6211\u5fc3\u91cc\u4e5f\u662f\u9ad8\u5174\u7684\uff0c\u6211\u4eec\u4f4f\u5728\u5c71\u4e0b\uff0c\u5929\u5929 \u53ef\u4ee5\u4e00\u8d77\u53bb\u722c\u5c71\u3001\u542c\u6cc9\uff0c\u6563\u6b65\uff0c\u6458\u679c\u5b50\u3002\u8fd8\u6709\u8f9b\u6377\u5f1f\uff0c\u6211\u90a3\u6b66\u529f\u76d6\u4e16\u7684\u4e49\u5f1f\uff0c\u4ed6\u4e00\u5b9a\u4f1a\u5e38\u6765 \u770b\u6211\u4eec\uff0c\u963f\u5170\uff0c\u4f60\u8bf4\u8fd9\u79cd\u751f\u6d3b\u60ec\u610f\u4e0d\uff1f\u201d \u3000\u3000\u963f\u5170\u89c1\u4ed6\u4fca\u8138\u653e\u5149\uff0c\u795e\u8272\u6109\u5df2\u6781\uff0c\u5979\u51e0\u6b21\u60f3\u5f00\u53e3\u70b9\u9192\u4ed6\uff0c\u7adf\u662f\u4e0d\u5fcd\u51fa\u53e3\u3002 \u3000\u3000\u65e5\u5df2\u5f53\u4e2d\uff0c\u51cc\u98ce\u84e6\u7684\u60f3\u8d77\u548c\u8f9b\u6377\u7684\u7ea6\u4f1a\uff0c\u4fbf\u5411\u963f\u5170\u8bf4\u4e86\uff0c\u8d77\u8eab\u6b32\u8d70\u3002 \u3000\u3000\u963f\u5170\u6df1\u6df1\u671b\u4e86\u4ed6\u4e00\u773c\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u5f53\u771f\u6c38\u8fdc\u8bb0\u5f97\u6211\u4e48\uff1f\u201d \u3000\u3000\u51cc\u98ce\u4e00\u6115\uff0c\u968f\u5373\u70b9\u70b9\u5934\u3002 \u3000\u3000\u963f\u5170\u53c8\u9053\uff1a\u201c\u5927\u54e5\uff0c\u8b6c\u5982\u6211\u505a\u4e86\u4ec0\u4e48\u5bf9\u4e0d\u8d77\u4f60\u7684\u4e8b\uff0c\u4f60\u90fd\u80af\u2026\u2026\u90fd\u80af\u2026\u2026\u539f\u8c05\u5417\uff1f\u201d \u3000\u3000\u51cc\u98ce\u7b11\u9053\uff1a\u201c\u963f\u5170\uff0c\u4f60\u5904\u5904\u4e3a\u6211\u5411\u6211\uff0c\u600e\u4f1a\u5bf9\u6211\u4e0d\u8d77\u5462\uff1f\u201d \u3000\u3000\u963f\u5170\u957f\u5401\u4f4e\u58f0\u51c4\u7136\u9053\uff1a\u201c\u90a3\u6211\u5c31\u653e\u5fc3\u5566\uff01\u597d\uff0c\u5927\u54e5\u4f60\u53bb\u5427\uff1f\u201d \u3000\u3000\u51cc\u98ce\u8f6c\u8eab\u6b63\u5f85\u79bb\u53bb\uff0c\u963f\u5170\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u518d\u8ba9\u6211\u77a7\u77a7\u3002\u201d \u3000\u3000\u51cc\u98ce\u5185\u5fc3\u5927\u5947\uff0c\u53ea\u89c9\u963f\u5170\u884c\u52a8\u53e4\u602a\uff0c\u4f46\u4ed6\u5728\u72c2\u559c\u4e4b\u4e0b\uff0c\u7406\u667a\u5df2\u660f\uff0c\u662f\u4ee5\u5e76\u672a\u60f3\u5230\u5176\u4ed6\u3002 \u3000\u3000\u963f\u5170\u51dd\u671b\u7740\u51cc\u98ce\uff0c\u4f46\u89c9\u6b64\u751f\u5df2\u8db3\uff0c\u518d\u65e0\u7559\u604b\uff0c\u5979\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u4f60\u53ef\u8981\u5feb\u56de\u6765\u3002\u201d \u3000\u3000\u5979\u76ee\u9001\u51cc\u98ce\u8d70\u51fa\uff0c\u7b11\u610f\u987f\u6d88\uff0c\u5979\u60f3\uff1a\u201c\u4e16\u754c\u4e0a\u672c\u6765\u5c31\u6ca1\u6709\u592a\u7f8e\u6ee1\u7684\u4e8b\uff0c\u592a\u7f8e\u6ee1\u4e86\u90a3\u4e0d\u957f \u4e45\uff0c\u5c11\u5e74\u60c5\u4fa3\uff0c\u60c5\u6df1\u7231\u91cd\uff0c\u6bcf\u6bcf\u4e0d\u7ec8\u8001\uff0c\u611a\u592b\u4fd7\u5987\uff0c\u5f80\u5f80\u5055\u8001\u7ec8\u8eab\uff0c\u6211\u8fd9\u4e00\u751f\u4e5f\u591f\u4e86\uff0c\u6211\u5f97 \u5230\u6700\u9ad8\u8d35\u7684\u60c5\u611f\uff01\u867d\u7136\u90a3\u662f\u77ed\u6682\u7684\uff0c\u53ef\u662f\u6bd4\u8d77\u90a3\u4e9b\u7ec8\u751f\u6df7\u6df7\u6c8c\u6c8c\u7684\u7231\uff0c\u90a3\u53c8\u6709\u610f\u601d\u5f97\u591a \u4e86\u3002\u201d \u3000\u3000\u5979\u63a8\u5f00\u7a97\uff0c\u62ac\u5934\u770b\u7740\u78a7\u84dd\u7684\u6674\u7a7a\uff0c\u7528\u529b\u55c5\u7740\u8349\u5170\u7684\u82ac\u82b3\uff0c\u4e8e\u662f\uff0c\u5979\u5f88\u5e73\u9759\u7684\u53bb\u5b89\u6392\u81ea \u5df1\u2026\u2026 \u3000\u3000\u51cc\u98ce\u6ee1\u6000\u6b23\u559c\u5feb\u6b65\u51fa\u57ce\uff0c\u5230\u4e86\u57ce\u95e8\u5916\u4e00\u770b\u8f9b\u6377\u5e76\u672a\u6765\u5230\uff0c\u4ed6\u5c31\u5728\u9644\u8fd1\u968f\u610f\u8d70\u8d70\u3002 \u3000\u3000\u6b64\u65f6\u6b63\u5f53\u5929\u4e0b\u6e05\u5e73\uff0c\u53c8\u6070\u4e03\u6e2d\u6cb3\u5e73\u539f\u5173\u4e2d\u4e4b\u5730\u4e09\u5e74\u4e30\u6536\uff0c\u51cc\u98ce\u4f46\u89c1\u57ce\u9ad8\u58c1\u539a\uff0c\u6c14\u52bf\u58ee \u4e25\uff0c\u6765\u6765\u5f80\u5f80\u5546\u5356\u3001\u519c\u592b\u90fd\u9762\u5e26\u559c\u8272\uff0c\u51cc\u98ce\u4e0d\u89c9\u6021\u7136\u3002 \u3000\u3000\u4ed6\u7b49\u4e86\u534a\u4e2a\u65f6\u8fb0\uff0c\u4e5f\u4e0d\u89c1\u8f9b\u6377\u6765\u5230\uff0c\u5fc3\u77e5\u8f9b\u6377\u4e00\u5b9a\u6709\u4e8b\u7275\u6302\uff0c\u4fbf\u5411\u4e00\u5bb6\u5c0f\u5e97\u8001\u677f\u8981\u4e86\u7eb8 \u7b14\uff0c\u7559\u4e66\u89c2\u4e0a\uff0c\u544a\u8bc9\u4ed6\u81ea\u5df1\u6240\u5728\u4e4b\u5904\u3002 \u3000\u3000\u4ed6\u8f7b\u677e\u7684\u8d70\u7740\uff0c\u4f46\u89c9\u81ea\u5df1\u5f97\u5230\u5b87\u5b99\u95f4\u7684\u4e00\u5207\uff0c\u9633\u5149\u7167\u5728\u4ed6\u8eab\u4e0a\uff0c\u4ed6\u4e0d\u4f46\u611f\u89c9\u8eab\u4e0a\u6696\u6696 \u7684\uff0c\u5728\u4ed6\u5185\u5fc3\u7684\u6df1\u5904\u4e5f\u5145\u6ee1\u4e86\u6696\u610f\u3002 \u3000\u3000\u4ed6\u7ec6\u7ec6\u5480\u56bc\u963f\u5170\u7684\u8bdd\uff0c\u7a81\u7136\uff0c\u4e00\u79cd\u4ece\u672a\u6709\u7684\u611f\u89c9\u88ad\u51fb\u7740\u4ed6\uff0c\u5728\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u5206\u4e0d\u51fa\u662f\u559c \u662f\u60b2\uff0c\u53ea\u89c9\u624b\u8db3\u65e0\u63aa\uff0c\u4ed6\u5b9a\u5b9a\u795e\uff0c\u60f3\u9053\uff1a \u3000\u3000\u201c\u6211\u6015\u662f\u4e50\u660f\u4e86\u5427\uff01\u201d\u7136\u800c\u6050\u60e7\u7684\u9634\u5f71\u7a81\u7136\u6108\u53d8\u6108\u5927\uff0c\u6e10\u6e10\u7684\u7b3c\u7f69\u7740\u6574\u4e2a\u7684\u4ed6\u3002 \u3000\u3000\u51cc\u98ce\u539f\u662f\u6781\u806a\u660e\u7684\u4eba\uff0c\u6b64\u65f6\u72c2\u559c\u4e4b\u60c5\u4e00\u6d88\uff0c\u5934\u8111\u4fbf\u89c1\u6e05\u9192\uff0c\u5f53\u60f3\u5230\u963f\u5170\u6700\u540e\u5411\u4ed6\u4e00\u7b11\u7684 \u795e\u60c5\uff0c\u90a3\u771f\u662f\u7f20\u7ef5\u51c4\u6006\uff0c\u4f3c\u4e4e\u5fc3\u90fd\u788e\u4e86\u2026\u2026 \u3000\u3000\u4ed6\u6015\u6781\u4e86\uff0c\u4e0d\u987e\u4e00\u5207\u53d1\u8db3\u72c2\u5954\uff0c\u5f85\u4ed6\u8d76\u5230\uff0c\u53ea\u542c\u5230\u4e00\u9635\u54ed\u58f0\u4f20\u4e86\u51fa\u6765\u3002 \u3000\u3000\u51cc\u98ce\u5fc3\u77e5\u4e0d\u5999\uff0c\u4e00\u63d0\u6c14\u8d8a\u5899\u800c\u8fc7\uff0c\u5306\u5306\u8fdb\u5c4b\u91cc\uff0c\u53ea\u89c1\u963f\u5170\u5012\u5728\u5730\u4e0a\uff0c\u5c0f\u4f59\u4f0f\u5730\u75db\u54ed\u3002 \u3000\u3000\u5c0f\u4f59\u54ed\u9053\uff1a\u201c\u5170\u59d1\u6b7b\u4e86\uff0c\u4f60\u8fd8\u6765\u5e72\u5417\uff1f\u201d \u3000\u3000\u51cc\u98ce\u51b2\u4e0a\u524d\u53bb\uff0c\u62b1\u963f\u5170\uff0c\u4e00\u63a2\u8109\u606f\uff0c\u5df2\u662f\u624b\u8db3\u51b0\u51c9\uff0c\u4ed6\u773c\u524d\u4e00\u82b1\uff0c\u51e0\u4e4e\u660f\u8fc7\u53bb\u3002 \u3000\u3000\u4ed6\u8f7b\u8f7b\u653e\u4e0b\u963f\u5170\u5c38\u4f53\uff0c\u6f20\u7136\u7684\u5411\u56db\u5468\u77a5\u4e86\u4e00\u773c\uff0c\u5ffd\u7136\u4f4e\u58f0\u5531\u9053\uff1a \u3000\u3000\u201c\u5929\u957f\u5730\u4e45\uff0c\u4eba\u751f\u51e0\u65f6\uff0c\u5148\u540e\u65e0\u89c9\uff0c\u4ece\u5c14\u6709\u671f\u3002\u201d \u3000\u3000\u5531\u58f0\u65b9\u6b62\uff0c\u54c7\u7684\u4e00\u58f0\u55b7\u51fa\u4e24\u53e3\u9c9c\u8840\u6765\u3002 \u3000\u3000\u5c0f\u4f59\u62ac\u5934\u53ea\u89c1\u8fd9\u4fca\u5c11\u5e74\u5728\u4e00\u523b\u95f4\u5982\u540c\u53d8\u4e86\u4e00\u4e2a\u4eba\uff0c\u5728\u4ed6\u773c\u4e2d\u662f\u65e0\u9650\u9634\u6697\uff0c\u65e0\u9650\u7684\u7edd\u671b\uff0c \u4ee4\u4eba\u5982\u7f6e\u8eab\u5bd2\u6d0b\u7802\u91ce\uff0c\u5c0f\u4f59\u4e0d\u7981\u6253\u4e86\u4e2a\u5bd2\u6817\u3002 \u3000\u3000\u51cc\u98ce\u75db\u6781\u4e4b\u4e0b\uff0c\u53cd\u800c\u9547\u5b9a\uff0c\u4ed6\u4e0d\u518d\u8a00\u8bed\uff0c\u62b1\u7740\u963f\u5170\u5c38\u4f53\uff0c\u5934\u4e5f\u4e0d\u56de\uff0c\u5f84\u81ea\u8d70\u4e86\u3002 \u3000\u3000\u5c0f\u4f59\u6162\u6162\u64e6\u5e72\u773c\u6cea\uff0c\u5170\u59d1\u7684\u8bdd\u53c8\u6d6e\u5230\u8033\u8fb9\uff1a\u201c\u2026\u2026\u5c0f\u4f59\uff0c\u6211\u7684\u4e8b\u4f60\u90fd\u5f88\u660e\u767d\uff0c\u73b0\u5728\u6211\u8981 \u5230\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730 \u3000\u3000\u65b9\u53bb\uff0c\u4f60\u4eca\u540e\u53ef\u8981\u597d\u597d\u505a\u4eba\uff0c\u6211\u7684\u4e8b\uff0c\u4f60\u5343\u4e07\u522b\u5411\u5434\u516c\u5b50\u63d0\u8d77\u60f3\u5230\u6b64\uff0c\u5c0f\u4f59\u4e0d\u89c9\u53c8\u5782\u4e0b \u6cea\u6765\uff0c\u81ea\u8d23\u9053\uff1a \u3000\u3000\u201c\u5c0f\u4f59\uff0c\u4f60\u8fd9\u7b28\u4e1c\u897f\uff0c\u4f60\u7adf\u771f\u95ea\u4e3a\u5170\u59d1\u8981\u8fdc\u79bb\u4ed6\u53bb\uff0c\u4f60\u7adf\u60f3\u4e0d\u5230\u5979\u4f1a\u4e0a\u540a\u81ea\u6740\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u53c8\u60f3\u9053\uff1a\u201c\u65b9\u5a46\u5a46\u548c\u5170\u59d1\u539f\u662f\u6700\u597d\u7684\u4eba\uff0c\u53ef\u662f\u4ed6\u4eec\u7684\u7ed3\u679c\u5462\uff1f\u90a3\u8be5\u6b7b\u7684\u53bf\u957f\uff0c\u4ed6\u89c1 \u5170\u59d1\u8c8c\u7f8e\uff0c\u6d41\u6d6a\u5f02\u5730\uff0c\u7adf\u8bec\u5979\u4eec\u4e3a\u98de\u76d7\u5bb6\u5c5e\uff0c\u7136\u540e\u518d\u5047\u88c5\u51fa\u9762\u66ff\u5979\u6d17\u8131\u7f6a\u540d\u3002\u53ef\u601c\u5170\u59d1\u90a3\u77e5 \u4ed6\u7684\u8be1\u8ba1\uff0c\u4ed6\u4e58\u5170\u59d1\u5bf9\u4ed6\u611f\u6fc0\u4e0d\u9632\u65f6\uff0c\u7528\u8ff7\u836f\u73b7\u8fb1\u4e86\u5979\uff0c\u8fd9\u662f\u5927\u5bb6\u90fd\u77e5\u9053\u7684\uff0c\u53ef\u662f\u8fd9\u79cd\u5978\u6076 \u4e4b\u5f92\uff0c\u4f9d\u7136\u4f5c\u5b98\u53d1\u8d22\uff0c\u96be\u9053\u5c31\u662f\u5929\u7406\u5417\uff1f\u201d \u3000\u3000\u201c\u5170\u59d1\u5fcd\u8fb1\u5077\u751f\uff0c\u539f\u6765\u5c31\u662f\u4e3a\u4e86\u89c1\u5434\u516c\u5b50\u4e00\u9762\uff0c\u5982\u4eca\u5fc3\u4e8b\u5df2\u4e86\uff0c\u5979\u81ea\u7136\u4f1a\u53bb\u6b7b\u7684\uff0c\u5979\u4e0d \u8ba9\u5434\u516c\u5b50\u77e5\u9053\uff0c\u90a3\u662f\u8981\u5728\u5434\u516c\u5b50\u5370\u8c61\u91cc\u4fdd\u6301\u5b8c\u7f8e\u7684\u56de\u5fc6\uff0c\u53ef\u601c\u5979\u4e3a\u4e86\u7231\u5434\u516c\u5b50\uff0c\u7adf\u653e\u8fc7\u81ea\u5df1 \u59d4\u66f2\u5927\u4ec7\u3002\u8fd9\u4e8b\u53ea\u6709\u6211\u77e5\u9053\u5f97\u6700\u6e05\u695a\uff0c\u5170\u59d1\u4ece\u4e0d\u4ee5\u4e0b\u4eba\u5f85\u6211\uff0c\u5904\u5904\u4ee5\u5927\u4eba\u6001\u5ea6\u7167\u987e\u6211\uff0c\u6211\u5c0f \u4f59\u4e00\u751f\u54ea\u91cc\u6709\u4eba\u75bc\u8fc7\uff0c\u601c\u8fc7\u5462\uff1f\u5170\u59d1\uff0c\u5170\u59d1\uff0c\u6211\u5982\u679c\u4e0d\u66ff\u4f60\u62a5\u4ec7\u96ea\u6068\uff0c\u6211\u771f\u662f\u732a\u72d7\u4e0d\u5982\u3002\u201d \u3000\u3000\u4ed6\u6124\u6068\u7684\u51fa\u4e86\u95e8\uff0c\u6d41\u6d6a\u6c5f\u6e56\uff0c\u904d\u8bbf\u540d\u5e08\uff0c\u65e5\u540e\u7ec8\u6210\u9ad8\u624b\uff0c\u4e86\u4e86\u5fc3\u613f\uff0c\u6b64\u662f\u540e\u8bdd\u4e0d\u63d0\u3002 \u3000\u3000\u51cc\u98ce\u96c7\u4e86\u4e00\u8f86\u8f66\uff0c\u4ed6\u6015\u62b1\u7740\u963f\u5170\u5c38\u4f53\uff0c\u60f9\u4eba\u6ce8\u76ee\uff0c\u4e00\u5230\u90ca\u5916\uff0c\u4fbf\u987a\u624b\u629b\u7ed9\u8f66\u592b\u4e00\u952d\u94f6 \u5b50\uff0c\u62b1\u8d77\u963f\u5170\uff0c\u5982\u98de\u800c\u53bb\uff0c\u90a3\u8f66\u592b\u4ee5\u4e3a\u9047\u7740\u8d22\u795e\uff0c\u548b\u820c\u4e0d\u5df2\u3002 \u3000\u3000\u51cc\u98ce\u4e13\u8d70\u5c0f\u8def\uff0c\u5954\u4e86\u4e00\u9635\uff0c\u5230\u4e86\u4e00\u5904\u5c71\u811a\u4e4b\u4e0b\uff0c\u4ed6\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u5982\u75af\u72c2\u4e00\u822c\u7ffb\u8d8a\u5cad\uff0c \u90a3\u5c71\u8def\u751a\u662f\u5d0e\u5c96\uff0c\u51cc\u98ce\u8dd1\u5230\u4e00\u4e2a\u5c71\u6d1e\u91cc\uff0c\u628a\u963f\u5170\u653e\u4e0b\u3002 \u3000\u3000\u4ed6\u8fd9\u4e00\u751f\u82e6\u96be\u592a\u591a\uff0c\u6b64\u65f6\u5fc3\u610f\u5df1\u51b3\uff0c\u53cd\u89c9\u65e0\u6240\u4f9d\u604b\uff0c\u62d4\u51fa\u957f\u5251\uff0c\u6316\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u628a\u963f\u5170 \u846c\u4e86\uff0c\u5728\u5979\u575f\u524d\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u963f\u5170\uff0c\u5927\u54e5\u8fd9\u4e00\u751f\u662f\u966a\u5b9a\u4f60\u4e86\u2014\u2014\u65e0\u8bba\u5929\u4e0a\u3001\u5730\u4e0b\uff0c\u4f60\u7b49\u7740 \u5440\uff0c\u6211\u5c31\u6765\u4e86\u3002\u201d \u3000\u3000\u4ed6\u5982\u68a6\u5403\u5583\u5583\uff0c\u6ca1\u6709\u4e00\u4e1d\u611f\u60c5\u51b2\u52a8\uff0c\u597d\u50cf\u8fd9\u79cd\u51b3\u5b9a\uff0c\u90a3\u662f\u5929\u7ecf\u5730\u4e49\u7684\u4e8b\uff0c\u6839\u672c\u5c31\u4e0d\u7528\u8003 \u8651\u4e86\u3002 \u3000\u3000\u4ed6\u8f7b\u53f9\u4e00\u58f0\uff0c\u8d70\u5230\u5c71\u8fb9\uff0c\u592a\u9633\u5df2\u6e10\u504f\u897f\u4e86\uff0c\u957f\u5b89\u57ce\u4e00\u5207\u5386\u5386\u5728\u76ee\uff0c\u81ea\u89c9\u751f\u547d\u5df2\u81f3\u5c3d\u5934\uff0c \u5c31\u7ad9\u5728\u963f\u5170\u575f\u524d\uff0c\u4e3e\u8d77\u5251\u5f80\u8116\u5b50\u62b9\u53bb\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u89c9\u5f97\u53f3\u624b\u4e00\u9707\uff0c\u4e00\u80a1\u5927\u529b\u4f7f\u4ed6\u5b9d\u5251\u628a\u6301\u4e0d\u4f4f\uff0c\u4e00\u58f0\u82e5\u6d2a\u949f\u7684\u58f0\u97f3\uff1a\u201c\u8272\u5373\u662f \u7a7a\uff0c\u7a7a\u5373\u662f\u8272\uff0c\u82e6\u6d77\u65e0\u8fb9\uff0c\u56de\u5934\u662f\u5cb8\u3002\u201d\u2026\u2026 \u3000\u3000\u51cc\u98ce\u53ea\u89c9\u5982\u96f7\u8f70\u9876\uff0c\u53c8\u89c9\u5b9b\u5982\u88ab\u6cfc\u4e86\u4e00\u6876\u51b7\u6c34\uff0c\u4e00\u970e\u65f6\u95f4\uff0c\u53c8\u50cf\u662f\u7cca\u6d82\u4e86\uff0c\u53c8\u50cf\u662f\u6e05\u9192 \u5f97\u5f88\uff0c\u4ed6\u731b\u7136\u8f6c\u8eab\u4e00\u770b\uff0c\u5374\u4e0d\u89c1\u4e00\u4eba\uff0c\u4ed6\u4e3e\u76ee\u524d\u89c6\uff0c\u53ea\u89c1\u4e24\u4e2a\u9ed1\u5f71\u5982\u98de\u800c\u53bb\uff0c\u5176\u4e2d\u4e00\u4e2a\u662f\u7626 \u957f\u7684\u8001\u50e7\uff0c\u53e6\u4e00\u4e2a\u80cc\u5f71\u597d\u751f\u719f\u6089\uff0c\u5947\u7684\u662f\u90a3\u8001\u50e7\u808b\u4e0b\u4f3c\u4e4e\u631f\u7740\u4e00\u4e2a\u6655\u8ff7\u7684\u5973\u5b50\u2014\u2014 \u3000\u3000\u4f46\u4ed6\u5fc3\u4e2d\u4e00\u4e9b\u4e0d\u66fe\u60f3\u5230\u8fd9\u5176\u4e2d\u7684\u53e4\u602a\uff0c\u4ed6\u8111\u4e2d\u6d51\u6d51\u7136\uff0c\u4e5f\u4e0d\u77e5\u5728\u60f3\u4e9b\u4ec0\u4e48\uff0c\u4e00\u4f1a\u50cf\u662f\u5343 \u767e\u4e2a\u5de8\u6d9b\u5927\u6d6a\u5728\u6c79\u6d8c\uff0c\u4e00\u4f1a\u513f\u53c8\u50cf\u662f\u78a7\u6e56\u4e00\u5e73\u5982\u955c\uff0c\u6d9f\u6f2a\u4e0d\u751f\uff0c\u800c\u90a3\u201c\u8272\u5373\u662f\u7a7a\u2026\u2026\u7a7a\u5373\u662f \u8272\u2026\u2026\u201d \u3000\u3000\u51e0\u4e2a\u5b57\u6709\u5982\u6d2a\u949f\u822c\u5728\u4ed6\u8111\u4e2d\u54cd\u7740\u2026\u2026 \u3000\u3000\u7a81\u7136\uff0c\u4ed6\u50cf\u662f\u5927\u5f7b\u5927\u609f\u4e86\uff0c\u4ed6\u4fca\u7f8e\u7684\u8138\u4e0a\u6d41\u9732\u51fa\u4e00\u4e1d\u575a\u6bc5\u7684\u989c\u8272\uff0c\u4e8e\u662f\u4ed6\u4e3e\u6b65\u2014\u2014\u4f46 \u662f\uff0c\u7acb\u523b\u4ed6\u5927\u505c\u4f4f\u4e86\u3002\u4ed6\u5fc3\u4e2d\u6697\u9053\uff1a\u201c\u6211\u539f\u60f3\u53bb\u5bfb\u90a3\u4e91\u7237\u7237\uff0c\u4f34\u7740\u4ed6\u7ec8\u6b64\u4e00\u751f\u7b97\u4e86\uff0c\u4f46\u662f\u6211 \u548c\u6377\u5f1f\u7684\u7ea6\u4f1a\u5462\uff1f\u5c3d\u7ba1\u8fd9\u4e16\u4e0a\u518d\u4e5f\u6ca1\u6709\u4e00\u4ef6\u4e8b\u4f1a\u4ee4\u6211\u7275\u6302\uff0c\u4f46\u662f\u5927\u4e08\u592b\u7acb\u4e8e\u4e16\u5c82\u80fd\u8a00\u800c\u65e0 \u4fe1\uff1f\u6211\uff0c\u6211\u5f97\u7b49\u4ed6\uff0c\u7136\u540e\u2014\u2014\u5509\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u2018\u7136\u540e\u2014\u2014\u2019\u5462\uff1f\u201d \u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u4ed6\u9661\u7136\u60ca\u8d77\uff0c\u521a\u624d\u90a3\u8001\u548c\u5c1a\u80c1\u4e0b\u631f\u7684\u5973\u5b50\u597d\u751f\u773c\u719f\uff0c\u5012\u6709\u51e0\u5206\u50cf\u90a3\u83c1\u513f\u54e9\u2014 \u2014 \u3000\u3000\u4ed6\u66f4\u4e0d\u8fdf\u7591\uff0c\u4e00\u98d8\u8eab\u5411\u65b9\u624d\u90a3\u4eba\u65b9\u5411\u8ffd\u53bb\u3002 \u3000\u3000\u4ed6\u670d\u8840\u679c\u540e\uff0c\u8f7b\u529f\u4e4b\u9ad8\uff0c\u4e16\u95f4\u7f55\u6709\uff0c\u53ea\u89c1\u6709\u5982\u4e00\u7f15\u8f7b\u70df\u822c\u6eda\u6eda\u800c\u524d\uff0c\u4e0d\u4e00\u4f1a\u5c31\u5230\u4e86\u90ca \u91ce\u3002 \u3000\u3000\u8fd9\u65f6\uff0c\u5ffd\u7136\u4e00\u58f0\u6e05\u5578\u53d1\u81ea\u5de6\u9762\uff0c\u4ed6\u9661\u7136\u4e00\u9707\uff0c\u6536\u8db3\u957f\u5578\u76f8\u5e94\uff0c\u4e0d\u4e00\u4f1a\uff0c\u5de6\u9762\u5c0f\u4e18\u540e\u51fa\u73b0 \u4e00\u6761\u4eba\u5f71\uff0c\u90a3\u4eba\u901f\u5ea6\u5feb\u5f97\u4ee4\u4eba\u548b\u820c\uff0c\u53ea\u4e09\u56db\u7eb5\uff0c\u5c31\u8f7b\u8f7b\u98d8\u8fc7\u4e09\u5341\u591a\u4e08\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u5df2\u5230\u4e86\u773c \u524d\uff0c\u6b63\u662f\u8f9b\u6377\uff01\u5c3d\u7ba1\u4ed6\u8eab\u6cd5\u7f8e\u5999\u7edd\u4f26\uff0c\u4f46\u4ed6\u7684\u8138\u4e0a\u63a9\u4e0d\u4f4f\u4e00\u4e1d\u5931\u671b\u4e0e\u7126\u6025\u6df7\u548c\u7684\u795e\u8272\u3002\u663e \u7136\uff0c\u4ed6\u5e76\u6ca1\u6709\u5bfb\u5230\u83c1\u513f\u3002 \u3000\u3000\u51cc\u98ce\u89c1\u4e86\u8f9b\u6377\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u773c\u6cea\u9669\u4e9b\u593a\u7736\u800c\u51fa\uff0c\u4ed6\u5f3a\u5fcd\u4f4f\u6fc0\u52a8\uff0c\u98a4\u58f0\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u524d \u9762\u2026\u2026 \u3000\u3000\u524d\u9762\u6709\u4e00\u4eba\u2026\u2026\u4e00\u4e2a\u5973\u5b50\u2026\u2026\u597d\u50cf\u83c1\u513f \u3000\u3000\u4ed6\u8bf4\u5f97\u65ad\u65ad\u7eed\u7eed\uff0c\u4f46\u8f9b\u6377\u53ef\u542c\u61c2\u4e86\uff0c\u4ed6\u5fc3\u4e2d\u72c2\u559c\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u54b1\u4eec\u5feb\uff01\u201d\u5982\u98de\u800c\u524d\uff01 \u4ed6\u53ef\u6ca1\u6d3c\u610f\u5230\u51cc\u98ce\u7684\u795e\u8272\uff0c\u867d\u7136\u4fca\u7f8e\u4f9d\u65e7\uff0c\u4f46\u662f\u6a35\u60b4\u6d88\u7626\uff0c\u773c\u795e\u5e26\u7740\u4e00\u7247\u7070\u8272\uff0c\u6d3b\u50cf\u662f\u9aa4\u7136 \u8001\u4e86\u5341\u5e74\uff01 \u3000\u3000\u8f9b\u6377\u81ea\u7136\u60f3\u4e0d\u5230\u5206\u624b\u51e0\u65f6\uff0c\u4ed6\u5434\u5927\u54e5\u4e0d\u4ec5\u5df2\u5bfb\u5230\u963f\u5170\uff0c\u800c\u4e14\u5df2\u6000\u7740\u4e00\u9897\u7834\u788e\u4e86\u7684\u5fc3\uff01 \u3000\u3000\u90ca\u5916\u5c71\u9675\u8d77\u4f0f\uff0c\u4f46\u8fd9\u4e24\u4eba\u90fd\u662f\u5f53\u4e16\u4e00\u7b49\u4e00\u7684\u8f7b\u529f\uff0c\u90a3\u5d0e\u5c96\u9ec4\u571f\u9ad8\u539f\uff0c\u5728\u4ed6\u4eec\u811a\u4e0b\u5982\u5c65\u5e73 \u5730\u3002 \u3000\u3000\u7a81\u7136\uff0c\u4e24\u4eba\u505c\u4e0b\u811a\u6765\uff0c\u539f\u6765\u524d\u9762\u51fa\u73b0\u5206\u6b67\u4e24\u6761\u8def\u3002 \u3000\u3000\u51cc\u98ce\u9053\uff1a\u201c\u54b1\u4eec\u5404\u641c\u4e00\u6761\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u4e0d\u6210\uff0c\u82e5\u662f\u4e24\u6761\u8def\u78b0\u4e0d\u5230\u5934\uff0c\u90a3\u4e48\u54b1\u4eec\u5c31\u8d8a\u8d70\u8d8a\u8fdc\u5566\u2014\u2014\u201d \u3000\u3000\u4e24\u4eba\u597d\u751f\u4e3a\u96be\u3002\u8f9b\u6377\u9053\uff1a\u201c\u54b1\u4eec\u4e00\u8d77\u5f80\u5de6\u8d70\u5427\uff0c\u5929\u610f\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u201c\u5929\u610f\u201d\u4ed6\u4f4f\u4e86\u53e3\uff0c\u4ed6\u4e0b\u610f\u8bc6\u5730\u62ac\u5934\u770b\u4e86\u770b\u5929\uff0c\u851a\u84dd\u8272\u7684\uff0c\u5929\u89d2\u6709\u4e9b\u9ec4\u9ec4\u7d2b\u7d2b\uff0c\u5f53 \u9876\u4e0a\u4e00\u5927\u5757\u767d\u4e91\u2014\u2014 \u3000\u3000\u4e16\u4e8b\u7684\u5b89\u6392\uff0c\u6709\u65f6\u662f\u5de7\u4e4b\u53c8\u5de7\uff0c\u5982\u679c\u8f9b\u6377\u9009\u7684\u662f\u53f3\u8fb9\u4e00\u6761\u8def\uff0c\u4ed6\u7684\u4e00\u751f\u4e5f\u8bb8\u5c31\u6539\u53d8\u4e86\u6837 \u5b50\u3002 \u3000\u3000\u5de6\u9762\u90a3\u6761\u8def\u7684\u9876\u5934\uff0c\u662f\u4e00\u4e2a\u5c0f\u5c71\u8c37\uff0c\u4e24\u4eba\u5206\u5934\u5bfb\u4e86\u4e00\u56de\uff0c\u4e00\u70b9\u7ebf\u7d22\u90fd\u6ca1\u6709\uff0c\u8f9b\u6377\u53f9\u9053\uff1a \u201c\u54b1\u4eec\u591a\u534a\u8d70\u9519\u4e86\u8def\u2014\u2014\u201d \u3000\u3000\u51cc\u98ce\u5374\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u77a7\uff0c\u90a3\u8fb9\u6709\u4e2a\u5c71\u6d1e\uff0c\u54b1\u4eec\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u8f9b\u6377\u5b9b\u5982\u9ed1\u6697\u4e2d\u53d1\u73b0\u5149\u660e\uff0c\u4e00\u98de\u800c\u53bb\uff0c\u8fd9\u4e9b\u65e5\u5b50\u6765\uff0c\u4ed6\u4e0d\u77e5\u5931\u671b\u8fc7\u591a\u5c11\u6b21\uff0c\u4f46\u4ed6\u4ecd\u62b1\u7740 \u5de8\u5927\u7684\u5e0c\u671b\uff0c\u8fd9\u53ea\u80fd\u8bf4\u662f\u7231\u60c5\u7684\u529b\u91cf\u5728\u652f\u6301\u7740\u4ed6\u5427\u3002 \u3000\u3000\u8fdc\u8fdc\u671b\u53bb\uff0c\u5c71\u6d1e\u524d\u7adf\u597d\u4f3c\u7ad9\u4e86\u4e00\u4e2a\u4eba\uff0c\u6b63\u662f\uff0c\u662f\u4e00\u4e2a\u4eba\uff0c\u4ed6\u4f3c\u4e4e\u4e5f\u53d1\u73b0\u4e86\u8f9b\u3001\u5434\u4e8c\u4eba\uff0c \u800c\u4e14\u4ece\u8170\u95f4\u62bd\u51fa\u4e00\u67c4\u957f\u5251\uff0c\u4f5c\u6212\u5907\u4e4b\u6001\u3002 \u3000\u3000\u8f9b\u6377\u3001\u5434\u51cc\u98ce\u4e24\u4eba\u8dc3\u5230\u524d\u6d1e\uff0c\u9f50\u58f0\u54e6\u4e86\u4e00\u58f0\uff0c\u539f\u6765\u90a3\u4eba\u7adf\u662f\u6b66\u6797\u4e4b\u79c0\u5b59\u501a\u91cd\uff01 \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u6709\u5982\u4e07\u7bad\u9f50\u6233\uff0c\u65e2\u7136\u8fd9\u662f\u5b59\u501a\u91cd\uff0c\u90a3\u4e48\u548c\u83c1\u513f\u662f\u6ca1\u6709\u5173\u7cfb\u7684\u4e86\uff0c\u4f46\u4ed6\u4ecd\u52c9\u5f3a\u5730 \u9053\uff1a\u201c\u5b59\u5144\uff0c\u522b\u6765\u65e0\u6059\uff1f\u201d \u3000\u3000\u5b59\u501a\u91cd\u4e5f\u9053\uff1a\u201c\u4e24\u4f4d\u600e\u4e48\u5230\u8fd9\u513f\u6765\u2014\u2014\u201d \u3000\u3000\u51cc\u98ce\u5ffd\u7136\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u4f60\u77a7\u90a3\u662f\u8c01\uff1f\u201d \u3000\u3000\u8f9b\u6377\u987a\u6307\u8fc7\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u4e2a\u4eba\u9759\u9759\u8e72\u5728\u96ea\u5730\u4e0a\uff0c\u5bf9\u7740\u5730\u4e0a\u4e00\u4e2a\u5c0f\u6d1e\u4e0d\u505c\u5730\u5439\u6c14\uff0c\u624b\u4e2d \u62ff\u7740\u4e00\u67f1\u5927\u7ea2\u8272\u7684\u9999\u4e0d\u65ad\u5bf9\u6d1e\u4e2d\u85b0\uff0c\u8f9b\u6377\u5bf9\u201c\u6bd2\u201d\u7684\u73a9\u610f\u513f\u5a34\u719f\u4e8e\u80f8\uff0c\u4e00\u770b\u4fbf\u77e5\u6b64\u4eba\u5728\u6355\u6349 \u4e00\u79cd\u6781\u5c11\u6709\u7684\u6bd2\u86c7\uff0c\u5524\u4f5c\u201c\u91d1\u820c\u513f\u201d\u3002 \u3000\u3000\u4ed4\u7ec6\u4e00\u770b\uff0c\u4e0d\u7981\u5927\u60ca\uff0c\u539f\u6765\u90a3\u4eba\u9762\u4e0a\u5200\u75a4\u51f8\u51f9\uff0c\u7adf\u662f\u90a3\u5929\u9b54\u91d1\u6b39\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u4e00\u8f6c\uff0c\u53cd\u5012\u91ca\u7136\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u5929\u9b54\u91d1\u6b4c\u4e0d\u8fdc\u5343\u91cc\u8dd1\u5230\u8fd9\u91cc\uff0c\u60f3\u5fc5\u662f\u8981\u914d\u5236\u90a3\u2018\u8840 \u9b42\u6bd2\u7802\u2019\u3002\u201d \u3000\u3000\u6562\u60c5\u8fd9\u4e9b\u5168\u662f\u4ece\u6bd2\u7ecf\u800c\u6765\u3002 \u3000\u3000\u90a3\u5929\u9b54\u91d1\u6b39\u7aef\u7684\u662f\u5929\u4e0b\u7b2c\u4e8c\u5f04\u6bd2\u9ad8\u624b\uff0c\u5c45\u7136\u8fde\u773c\u6674\u90fd\u6ca1\u6709\u62ac\u4e00\u4e0b\u3002 \u3000\u3000\u51cc\u98ce\u6050\u8f9b\u6377\u5931\u671b\uff0c\u9053\uff1a\u201c\u6377\u5f1f\uff0c\u54b1\u4eec\u5230\u6d1e\u4e2d\u518d\u627e\u4e00\u8d9f\u3002\u201d \u3000\u3000\u90a3\u5b59\u501a\u91cd\u5374\u7d27\u5f20\u5730\u9053\uff1a\u201c\u4e0d\u6210\uff01\u201d \u3000\u3000\u8f9b\u6377\u4e0d\u7981\u5927\u5947\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u5b59\u501a\u91cd\u4f3c\u4e5f\u53d1\u89c9\u81ea\u5df1\u7d27\u5f20\u8fc7\u5ea6\uff0c\u89e3\u91ca\u9053\uff1a\u201c\u6211\u662f\u8bf7\u4e24\u4f4d\u6682\u65f6\u4e0d\u8981\u5230\u6d1e\u4e2d\u53bb\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u6025\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u4e5f\u8bb8\u662f\u4ed6\u5fc3\u4e2d\u7126\u6025\uff0c\u58f0\u97f3\u4e0d\u7981\u5927\u4e86\u4e00\u4e9b\uff0c\u90a3\u5b59\u501a\u91cd\u4f3c\u4e4e\u4e5f\u662f\u5fae\u6012\uff0c\u4f46\u5374\u4e00\u70b9\u6ca1\u6709\u8bf4\u8bdd\u3002 \u3000\u3000\u8f9b\u6377\u7591\u5fc3\u5927\u8d77\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\uff1f\u4f60\u8bf4\u2014\u2014\u201d \u3000\u3000\u5b59\u501a\u91cd\u4e5f\u6012\u9053\uff1a\u201c\u4e0d\u4e3a\u4ec0\u4e48\uff0c\u53c8\u600e\u6837\uff1f\u201d \u3000\u3000\u8f9b\u6377\u672c\u6765\u4ee5\u4e3a\u83c1\u513f\u4e0d\u53ef\u80fd\u5728\u6d1e\u4e2d\u2014\u2014 \u3000\u3000\u5b59\u501a\u91cd\u957f\u5251\u4e00\u7acb\uff0c\u6a2a\u6b65\u7ad9\u5728\u6d1e\u53e3\uff0c\u4f46\u8fd9\u6837\u4e00\u6765\uff0c\u5012\u975e\u8fdb\u53bb\u4e00\u770b\u4e0d\u53ef\uff0c\u4ed6\u4e00\u8a00\u4e0d\u53d1\u5c31\u51c6\u5907 \u8fdb\u6d1e\u3002 \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u5ffd\u7136\u4e00\u58f0\u9634\u68ee\u68ee\u5730\u51b7\u7b11\u6765\u81ea\u80cc\u540e\uff0c\u8f9b\u3001\u5434\u4e8c\u4eba\u8f6c\u8eab\u4e00\u770b\uff0c\u53ea\u89c1\u4e09\u4e08\u5916\u9ad8\u9ad8\u77ee \u77ee\u7ad9\u7740\u4e94\u4eba\uff01 \u3000\u3000\u4e3a\u9996\u4e4b\u4eba\u7adf\u662f\u5929\u5170\u9ad8\u624b\u91d1\u9c81\u5384\uff01 \u3000\u3000\u8f9b\u6377\u5927\u5403\u4e00\u60ca\uff0c\u53cd\u8eab\u89c6\u51dd\u76f8\u5f85\uff0c\u90a3\u91d1\u9c81\u5384\u4e0a\u524d\u4e24\u6b65\uff0c\u51b7\u54fc\u9053\uff1a\u201c\u8f9b\u5927\u4fa0\uff0c\u522b\u6765\u65e0\u6089 \u4e4e\uff1f\u201d \u3000\u3000\u8f9b\u6377\u51b7\u7b11\u4e00\u58f0\uff0c\u4e0d\u7f6e\u53ef\u5426\uff0c\u5634\u89d2\u4e0a\u6302\u7740\u4e00\u4e2a\u4e0d\u5c51\u7684\u51b7\u7b11\u3002 \u3000\u3000\u722c\u5728\u5730\u4e0a\u6349\u86c7\u7684\u5929\u9b54\u91d1\u6b39\u4ecd\u662f\u77a7\u90fd\u4e0d\u77a7\u8fd9\u8fb9\uff0c\u56e0\u4e3a\u4ed6\u52a8\u4e5f\u4e0d\u52a8\uff0c\u662f\u4ee5\u91d1\u9c81\u5384\u7b49\u4eba\u6839\u672c\u4e0d \u66fe\u53d1\u73b0\u4ed6\u3002 \u3000\u3000\u91d1\u9c81\u5384\u89c1\u8f9b\u6377\u4e0d\u7406\u776c\uff0c\u4e5f\u4e0d\u53d1\u6012\uff0c\u53ea\u51b7\u51b7\u9053\uff1a\u201c\u4eca\u65e5\u54b1\u4eec\u5144\u5f1f\u6709\u4e00\u70b9\u5c0f\u4e8b\u8981\u76f8\u6c42\u4e8e\u8f9b\u5927 \u4fa0\u2014\u2014\u55ef\uff0c\u8f9b\u5927\u4fa0\u8fd9\u67c4\u5b9d\u5251\u7aef\u7684\u662f\u5e0c\u4e16\u73cd\u54c1\u3002\u201d \u3000\u3000\u8f9b\u6377\u89c1\u4ed6\u77a5\u89c1\u81ea\u5df1\u5251\u67c4\u5c31\u770b\u51fa\u662f\u5b9d\u5251\uff0c\u773c\u529b\u7740\u5b9e\u5389\u5bb3\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u8fd9\u53ae\u6709\u4ec0\u4e48\u8981\u76f8\u6c42 \u4e8e\u6211\u7684\uff1f\u53ea\u6050\u6709\u4ec0\u4e48\u8be1\u8ba1\u2014\u2014\u201d \u3000\u3000\u53e3\u4e2d\u52a8\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9c81\u5384\u6de1\u6de1\u4e00\u7b11\u9053\uff1a\u201c\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u4ecd\u662f\u90a3\u53e5\u8001\u8bdd\uff0c\u54b1\u4eec\u6c42\u8f9b\u5927\u4fa0\u627f\u8ba4\u4e00\u53e5\u8bdd\uff0c\u54b1\u4eec\u611f\u6fc0 \u4e0d\u5c3d\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u5947\u9053\uff1a\u201c\u627f\u8ba4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9c81\u5384\u563f\u4e86\u4e24\u4e0b\u9053\uff1a\u201c\u53ea\u8981\u8f9b\u5927\u4fa0\u80af\u627f\u8ba4\u5929\u7afa\u6b66\u5b66\u5728\u4e2d\u539f\u4e4b\u4e0a\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u6012\u9053\uff1a\u201c\u5f53\u65e5\u6052\u6cb3\u4e09\u4f5b\u5728\u6218\u5c9b\u5927\u6218\u4e16\u5916\u4e09\u4ed9\uff0c\u4e09\u4f5b\u53ef\u66fe\u5360\u5f97\u4e00\u4e1d\u4fbf\u5b9c\uff1f\u54fc\uff01\u201d \u3000\u3000\u91d1\u9c81\u5384\u51b7\u7b11\u9053\uff1a\u201c\u655d\u5e08\u5c0a\u4eec\u89c1\u65e0\u6781\u5c9b\u4e3b\u5185\u75be\u7a81\u53d1\u624d\u7f62\u624b\u800c\u53bb\uff0c\u4e0d\u6599\u8f9b\u5927\u4fa0\u7adf\u4e0d\u8bc6\u597d\u6b79\u2014 \u2014\u201d \u3000\u3000\u8f9b\u6377\u6012\u4e0d\u53ef\u6291\uff0c\u54c8\u54c8\u5927\u7b11\u9053\uff1a\u201c\u9601\u4e0b\u627e\u59d3\u8f9b\u7684\u53ea\u662f\u4e3a\u8fd9\u4e00\u4ef6\u4e8b\u4e48\uff1f\u201d \u3000\u3000\u91d1\u9c81\u5384\u50b2\u7136\u70b9\u9996\u3002 \u3000\u3000\u8f9b\u6377\u5ffd\u89c9\u80f8\u4e2d\u70ed\u8840\u4e0a\u6d8c\uff0c\u4ed6\u518d\u4e5f\u7ba1\u4e0d\u4f4f\u81ea\u5df1\uff0c\u4ed6\u5fd8\u4e86\u8981\u5bfb\u627e\u7684\u83c1\u513f\uff0c\u4e5f\u5fd8\u4e86\u5f53\u524d\u7684\u5371 \u5883\uff0c\u5927\u58f0\u9053\uff1a\u201c\u59d3\u8f9b\u7684\u56de\u7b54\u4f60\uff0c\u53eb\u4f60\u5feb\u6eda\uff01\u201d \u3000\u3000\u7684\u786e\uff0c\u6b64\u523b\u4ed6\u5fd8\u5374\u4e86\u83c1\u513f\u2014\u2014\u4e5f\u8bb8\u65e5\u540e\u60f3\u8d77\u6765\u4ed6\u4f1a\u89c9\u5f97\u4e0d\u5b89\u2014\u2014\u4f46\u662f\u81f3\u5c11\u6b64\u523b\uff0c\u4ed6\u5fc3\u4e2d \u89c9\u5f97\u6709\u4ef6\u4e8b\u6bd4\u7231\u60c5\u3001\u751a\u81f3\u751f\u547d\u90fd\u66f4\u52a0\u91cd\u8981\u767e\u500d! \u3000\u3000\u91d1\u9c81\u5384\u5e72\u7b11\u4e00\u58f0\uff0c\u5e76\u4e0d\u7406\u4f1a\uff0c\u7adf\u81ea\u6307\u7740\u4e3a\u9996\u90a3\u77ee\u5c0f\u548c\u5c1a\u9053\uff1a\u201c\u8fd9\u4f4d\u662f\u655d\u5927\u5e08\u5144\uff0c\u6cd5\u53f7\u9640 \u5b9d\u6811\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u770b\u90a3\u77ee\u548c\u5c1a\uff0c\u53ea\u89c1\u4ed6\u4e24\u989d\u592a\u9633\u7a74\u9f13\u51fa\u8001\u9ad8\uff0c\u53cc\u76ee\u7cbe\u5149\u66b4\u5c04\uff0c\u8eab\u6750\u867d\u5c0f\uff0c\u4f46\u6c14\u5ea6\u6c89 \u7a33\uff0c\u5b9b\u5982\u6cf0\u5c71\u5dcd\u7acb\uff0c\u8f9b\u6377\u6697\u60ca\u9053\uff1a \u3000\u3000\u201c\u8fd9\u77ee\u548c\u5c1a\u5185\u529f\u4e4b\u6df1\uff0c\u53ea\u6015\u6bd4\u6052\u6cb3\u4e09\u4f5b\u90fd\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u8fd9\u4e94\u4eba\u4e2d\u8981\u7b97\u4ee5\u4ed6\u6700\u96be\u6597\u3002\u201d \u3000\u3000\u91d1\u9c81\u5384\u6307\u7740\u5de6\u9762\u90a3\u9ec4\u886b\u5934\u9640\u9053\uff1a\u201c\u8fd9\u662f\u4e8c\u5e08\u5144\u9752\u5c18\u7f57\u6c49\u2014\u2014\u201d \u3000\u3000\u63a5\u7740\u53c8\u6307\u7740\u5de6\u9762\u7b2c\u4e8c\u4eba\u9053\uff1a\u201c\u4e09\u5e08\u5144\u52a0\u5927\u5c14\u2014\u2014\u4e24\u4f4d\u662f\u89c1\u8fc7\u7684\u4e86\u3002\u201d \u3000\u3000\u6700\u540e\u6307\u7740\u53f3\u9762\u7684\u866c\u9aef\u957f\u5b50\u9053\uff1a\u201c\u8fd9\u662f\u56db\u5e08\u5144\u6e29\u6210\u767d\u7f57\uff0c\u54c8\u54c8\uff0c\u54b1\u4eec\u4e94\u5144\u5f1f\u4eba\u79f0\u5a46\u7f57\u4e94\u5947 \u8f9b\u6377\u60f3\u8d77\u90a3\u68b5\u6587\u8f7b\u529f\u79d8\u7b3c\u4e0a\u7684\u8bb0\u8ff0\uff0c\u5f53\u4e0b\u51b7\u51b7\u9053\uff1a\u201c\u563f\uff0c\u6015\u662f\u5a46\u7f57\u516d\u5947\u5427\uff01\u201d \u3000\u3000\u91d1\u9c81\u5384\u8138\u8272\u5927\u53d8\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u8f9b\u5927\u4fa0\u5012\u4f1a\u8bf4\u7b11\u8bdd\u2014\u2014\u95f2\u8bdd\u5c11\u8bf4\uff0c\u54b1\u4eec\u5144\u5f1f\u8fd9\u6b21\u6765\u5bfb\u8f9b \u5927\u4fa0\u4e43\u662f\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\u4ed6\u987f\u4e86\u4e00\u987f\uff0c\u7ee7\u7eed\u9053\uff1a\u201c\u8f9b\u5927\u4fa0\u4e0d\u80af\u627f\u8ba4\u5929\u5170\u6b66\u529f\u5728\u4e2d\u539f\u4e4b\u4e0a\uff0c\u90a3\u4e5f\u5c31\u7f62\u4e86\uff0c \u4e0d\u8fc7\u4e0a\u6b21\u54b1\u4eec\u5728\u594e\u5c71\u4e0a\u4ea4\u624b\uff0c\u5144\u5f1f\u56de\u53bb\u4ee5\u540e\u5c06\u8f9b\u5927\u4fa0\u7684\u795e\u529f\u7edd\u6280\u8bf4\u7ed9\u54b1\u5e08\u5144\u4eec\u542c\uff0c\u5927\u5bb6\u90fd\u4ef0 \u6155\u5f97\u5f88\uff0c\u6240\u4ee5\u2014\u2014\u6240\u4ee5\u54b1\u4eec\u5c31\u51b3\u5fc3\u5bfb\u8f9b\u5927\u4fa0\u8ba8\u6559\u4e00\u4e8c\u2014\u2014\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u4ed6\u53cc\u773c\u7262\u7262\u76ef\u4f4f\u8f9b\u6377\u3002 \u3000\u3000\u8f9b\u6377\u62fc\u547d\u51b7\u9759\u4e0b\u6765\uff0c\u628a\u773c\u524d\u5f62\u52bf\u98de\u5feb\u5730\u6253\u4e86\u4e00\u56de\u7b97\u76d8\uff0c\u4f46\u662f\u7adf\u60f3\u4e0d\u51fa\u4e00\u6761\u8131\u8eab\u7684\u529e\u6cd5\u3002 \u3000\u3000\u4ed6\u56de\u9996\u671b\u4e86\u671b\u5434\u51cc\u98ce\uff0c\u51cc\u98ce\u4e5f\u6b63\u671b\u7740\u4ed6\uff0c\u4ed6\u5bf9\u7740\u51cc\u98ce\u82e6\u7b11\u4e00\u4e0b\uff0c\u6084\u58f0\u95ee\u9053\uff1a\u201c\u600e\u6837\uff1f\u201d \u3000\u3000\u5434\u51cc\u98ce\u9ed8\u7136\u6447\u4e86\u6447\u5934\uff0c\u4f46\u4ed6\u7acb\u523b\u65a9\u94c1\u622a\u94c1\u5730\u9053\uff1a\u201c\u62fc\u4e00\u4e2a\u7b97\u4e00\u4e2a\uff01\u201d \u3000\u3000\u4ed6\u90a3\u4fca\u7f8e\u7684\u9762\u5b54\u4e0a\u9732\u51fa\u4e00\u79cd\u51db\u7136\u7684\u795e\u8272\uff0c\u8fd9\u79cd\u51db\u7136\u7684\u795e\u8272\u4ee4\u4ed6\u7684\u7edd\u4e16\u4fca\u79c0\u4e2d\u66f4\u589e\u4e86\u4e00\u4efd \u7537\u513f\u7684\u672c\u8272\uff01 \u3000\u3000\u8f9b\u6377\u56de\u8eab\u4ef0\u5929\u957f\u7b11\uff0c\u6717\u58f0\u9053\uff1a\u201c\u6613\u6c34\u6f47\u6f47\uff0c\u60b2\u98ce\u51c4\u51c4\uff0c\u5927\u4e08\u592b\u751f\u4e0d\u6210\u540d\uff0c\u6b7b\u5219\u846c\u86ee\u5937\u4e4b \u4e2d\u2014\u2014\u5927\u54e5\uff0c\u51ed\u8fd9\u4e94\u4e2a\u5316\u5916\u86ee\u5b50\u5c31\u5948\u4f55\u5f97\u54b1\u4eec\u5144\u5f1f\u4fe9\u5417\uff1f\u201d \u3000\u3000\u90a3\u91d1\u9c81\u5384\u55b7\u7136\u51b7\u7b11\u9053\uff1a\u201c\u5144\u5f1f\u4fe9\uff1f\u4f60\u4eec\u4e24\u4eba\u4e48\uff1f\u54c8\u2014\u2014\u4e24\u4eba\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u6b63\u5f85\u56de\u7b54\uff0c\u7a81\u7136\u80cc\u540e\u4e00\u4e2a\u54cd\u4eae\u7684\u58f0\u97f3\u63a5\u9053\uff1a\u201c\u4e09\u4eba\uff01\u201d \u3000\u3000\u91d1\u9c81\u5384\u6025\u5fd9\u56de\u9996\u4e00\u770b\uff0c\u53ea\u89c1\u4e09\u4e08\u4e4b\u5916\u4e00\u4e2a\u9752\u8863\u9752\u5e74\u53c9\u8170\u633a\u7acb\uff0c\u8170\u65c1\u957f\u5251\u7a57\u4e1d\u98d8\u98d8\uff0c\u6b63\u662f \u5730\u594e\u5c71\u4f1a\u8fc7\u7684\u201c\u6b66\u6797\u4e4b\u79c0\u201d\u5b59\u501a\u91cd\uff01 \u3000\u3000\u91d1\u9c81\u5384\u5e72\u7b11\u4e00\u58f0\uff0c\u51b7\u7136\u9053\uff1a\u201c\u597d\u554a\uff0c\u5c31\u8fde\u4f60\u7b97\u4e0a\u5427\u2014\u2014\u201d \u3000\u3000\u84e6\u7136\u5de6\u9762\u4e00\u4e2a\u9634\u607b\u607b\u7684\u58f0\u97f3\u9053\uff1a\u201c\u563f\u563f\uff0c\u8fd8\u6709\u6211\u54e9\uff01\u201d \u3000\u3000\u5a46\u7f57\u4e94\u5947\u4e00\u9f50\u8f6c\u8eab\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u5757\u5de8\u77f3\u4e0a\u7ad9\u7740\u4e00\u4e2a\u62ab\u5934\u6563\u53d1\u7684\u4e11\u8138\u5251\u58eb\uff0c\u90a3\u4eba\u8138\u4e0a\u4ea4\u53c9 \u4e24\u9053\u5200\u75a4\uff0c\u9f3b\u5b54\u6b8b\u7f3a\u4e0d\u5168\uff0c\u5f62\u6001\u6781\u662f\u53ef\u6016\uff0c\u6b63\u662f\u51f6\u540d\u6ee1\u5929\u4e0b\u7684\u5929\u9b54\u91d1\u6b39\uff01 \u3000\u3000\u91d1\u9c81\u5384\u89c1\u8fc7\u5929\u9b54\u91d1\u6b39\uff0c\u4f46\u89c9\u6b64\u523b\u4ed6\u9762\u5bb9\u6050\u6016\uff0c\u4e0d\u7531\u5fc3\u4e2d\u4e00\u9707\u3002 \u3000\u3000\u8f9b\u6377\u4e0d\u6599\u8fd9\u4e24\u4eba\u4f1a\u51fa\u624b\uff0c\u4e0d\u7531\u5fc3\u4e2d\u5927\u559c\uff0c\u4ed6\u8c6a\u6027\u723d\u6717\u5730\u957f\u7b11\u4e00\u58f0\uff0c\u53ee\u7136\u9661\u54cd\uff0c\u5251\u5149\u95ea \u5904\uff0c\u6885\u9999\u5b9d\u5251\u5df1\u5230\u4e86\u624b\u4e0a\u3002 \u3000\u3000\u51cc\u98ce\u4fca\u7f8e\u7684\u8138\u4e0a\u95ea\u8fc7\u4e00\u4e1d\u4ee4\u4eba\u6655\u7729\u7684\u5149\u91c7\uff0c\u4ed6\u6f47\u6d12\u5730\u4e00\u8de8\u6b65\u95f4\uff0c\u957f\u5251\u4e5f\u5230\u4e86\u624b\u4e2d\u3002 \u3000\u3000\u8f9b\u6377\u4f4e\u58f0\u5bf9\u51cc\u98ce\u9053\uff1a\u201c\u90a3\u4e3a\u9996\u7684\u77ee\u548c\u5c1a\u7531\u6211\u5bf9\u4ed8\u2014\u2014\u201d \u3000\u3000\u8bdd\u58f0\u672b\u5df2\uff0c\u5237\u5237\u4e24\u58f0\uff0c\u90a3\u6b66\u6797\u4e4b\u79c0\u5b59\u501a\u91cd\u53ca\u5929\u9b54\u91d1\u6b4c\u4e00\u9f50\u7eb5\u5230\u8eab\u8fb9\u3002 \u3000\u3000\u91d1\u9c81\u5384\u51b7\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u6562\u95ee\u4e24\u4f4d\u5c0a\u59d3\u5927\u540d\uff1f\u201d\u4ed6\u867d\u89c1\u8fc7\u4e24\u4eba\uff0c\u5374\u4e0d\u77e5\u4e8c\u4eba\u59d3\u540d\u3002 \u3000\u3000\u5b59\u501a\u91cd\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u6253\u5c31\u6253\uff0c\u95ee\u8fd9\u4e2a\u5e72\u4e48\uff1f\u201d \u3000\u3000\u90a3\u5929\u9b54\u91d1\u6b39\u5374\u54fc\u90fd\u4e0d\u54fc\u4e00\u58f0\uff0c\u7adf\u662f\u6839\u672c\u4e0d\u52a0\u7406\u776c\uff01 \u3000\u3000\u91d1\u9c81\u5384\u6c14\u6781\u53cd\u800c\u4ef0\u5929\u957f\u7b11\uff0c\u534a\u664c\u624d\u6b62\u4f4f\u7b11\u58f0\uff0c\u6068\u58f0\u9053\uff1a\u201c\u5c0f\u5b50\u4eec\u4f11\u72c2\uff0c\u4eca\u65e5\u5c31\u662f\u4f60\u7b49\u6bd9 \u547d\u4e4b\u65f6\uff01\u201d \u3000\u3000\u58f0\u54cd\u65b9\u6b47\uff0c\u547c\u5730\u4e00\u58f0\uff0c\u90a3\u6761\u6cb9\u4e4c\u4eae\u4eae\u7684\u957f\u7d22\u5df2\u5230\u4e86\u624b\u4e2d\uff0c\u78c1\u7684\u4e00\u58f0\u5c16\u9510\u5927\u54cd\uff0c\u957f\u7d22\u5df2\u5982 \u6bd2\u86c7\u7a7f\u8eab\u822c\u98de\u817e\u800c\u51fa\u3002 \u3000\u3000\u5434\u51cc\u98ce\u4e00\u9519\u6b65\u95f4\uff0c\u8679\u5149\u95ea\u8fc7\uff0c\u62a2\u8fce\u800c\u4e0a\uff0c\u5de6\u624b\u5251\u8bc0\u4e00\u7ed5\uff0c\u624b\u4e2d\u957f\u5251\u631f\u7740\u4e00\u7f15\u52b2\u98ce\u659c\u5288\u800c \u51fa\uff0c\u6b63\u662f\u65ad\u9b42\u5251\u6cd5\u4e2d\u7684\u51cc\u5389\u653b\u5f0f\u201c\u9b3c\u738b\u628a\u706b\u201d\u3002 \u3000\u3000\u90a3\u5176\u4f59\u7684\u5a46\u7f57\u56db\u5947\u89c1\u91d1\u9c81\u5384\u5df2\u52a8\u4e86\u624b\uff0c\u5404\u81ea\u8fc5\u901f\u5730\u6613\u4f4d\u800c\u7acb\uff0c\u51c6\u5907\u51fa\u624b\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u9886\u201c\u6885\u9999\u5b9d\u5251\u201d\uff0c\u56de\u5934\u5411\u5b59\u501a\u91cd\u3001\u5929\u9b54\u91d1\u6b39\u4e24\u4eba\u7565\u4e00\u70b9\u9996\uff0c\u4e00\u53cd\u8eab\u4e4b\u95f4\uff0c\u957f\u5251\u8f7b \u8f7b\u98d8\u51fa\uff0c\u75be\u5982\u95ea\u7535\u5730\u523a\u5411\u5a46\u7f57\u4e94\u5947\u7684\u8001\u5927\u5bc6\u9640\u5b9d\u6811\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u597d\u4e0d\u53e4\u602a\uff0c\u90a3\u6885\u9999\u5b9d\u5251\u7adf\u4f3c\u8f6f\u97ad\u4e00\u822c\u4f5c\u5f27\u5f62\u5730\u5f39\u5c06\u51fa\u53bb\uff0c\u90a3\u4e00\u5f39\u4e4b\u95f4\uff0c\u53d1\u51fa\u55e1\u7684 \u4e00\u58f0\uff0c\u5251\u5c16\u5374\u5728\u90a3\u4e00\u5239\u90a3\u4e4b\u95f4\u98de\u5feb\u5730\u8df3\u52a8\uff0c\u4e0a\u4e0b\u5de6\u53f3\u6b63\u597d\u6784\u6210\u4e00\u4e2a\u5706\u5708\u513f\uff0c\u7136\u800c\u5374\u5206\u6beb\u4e0d\u723d \u5730\u5708\u5728\u5bc6\u9640\u5b9d\u6811\u7684\u80f8\u524d\u56db\u5927\u7a74\u4e4b\u4e0a\uff01 \u3000\u3000\u5bc6\u9640\u5b9d\u6811\u4e0d\u79d1\u8f9b\u6377\u62db\u5f0f\u5982\u6b64\u795e\u5947\uff0c\u4ed6\u54a6\u4e86\u4e00\u58f0\uff0c\u53cc\u80a9\u9661\u7136\u4e0b\u6c89\uff0c\u77ee\u5c0f\u7684\u8eab\u5f62\u5728\u8f9b\u6377\u5251\u5c16 \u4e0b\u4e00\u7a9c\u800c\u8fc7\uff0c\u8eab\u8eaf\u4e0d\u5f85\u4f38\u76f4\uff0c\u53cc\u638c\u731b\u7136\u5411\u540e\u6454\u51fa\uff0c\u90a3\u7626\u5c0f\u7684\u624b\u638c\u4e4b\u95f4\uff0c\u7adf\u7136\u631f\u7740\u4e24\u80a1\u4f5c\u54cd\u7684 \u52b2\u98ce\uff0c\u76f4\u649e\u8f9b\u6377\u201c\u795e\u5ead\u201d\u3001\u201c\u7384\u673a\u201d\u4e24\u7a74\u3002 \u3000\u3000\u8f9b\u6377\u8eab\u5b50\u518d\u5feb\u4e5f\u4e0d\u53ca\u6536\u56de\u957f\u5251\uff0c\u4ed6\u53ea\u5f97\u5de6\u638c\u4e00\u5708\u800c\u51fa\uff0c\u786c\u8fce\u800c\u4e0a\uff0c\u7830\u7684\u4e00\u58f0\uff0c\u8f9b\u6377\u4ee5\u4e00 \u638c\u63a5\u4ed6\u4e24\u638c\uff0c\u767b\u65f6\u88ab\u9707\u9000\u4e24\u6b65\uff01\u8f9b\u6377\u6697\u9053\uff1a\u201c\u8fd9\u5bc6\u9640\u5b9d\u6811\u679c\u7136\u4e0d\u6127\u662f\u5a46\u7f57\u4e94\u5947\u4e4b\u9996\uff0c\u529f\u529b\u4e4b \u6df1\uff0c\u53ea\u6015\u72b9\u5728\u91d1\u9c81\u5384\u4e4b\u4e0a\uff01\u201d \u3000\u3000\u4ed6\u4f4e\u5934\u770b\u4e86\u770b\u624b\u4e2d\u6885\u9999\u5251\uff0c\u6e05\u4eae\u8000\u76ee\u7684\u5251\u5c16\u4e0a\u53d1\u51fa\u4e00\u4e1d\u4ee4\u4eba\u5bd2\u6817\u7684\u5149\u8292\uff0c\u91d1\u8272\u7684\u7a57\u4e1d\u5728 \u5fae\u98ce\u4e2d\u66f3\u66f3\u800c\u52a8\uff0c\u4ed6\u731b\u7136\u62ac\u5934\uff0c\u53cc\u76ee\u7cbe\u5149\u66b4\u5c04\uff0c\u767d\u6682\u7684\u8138\u5b54\u4e0a\u900f\u51fa\u4e00\u4e1d\u5f02\u6837\u7684\u7ea2\u6da6\uff0c\u4ed6\u957f\u5251 \u4e00\u541e\u4e00\u5410\uff0c\u5316\u4f5c\u4e07\u822c\u5251\u5f71\u5411\u5bc6\u9640\u5b9d\u6811\u5f53\u5934\u7f69\u4e0b\u2014\u2014\u5bc6\u9640\u5b9d\u6811\u65cb\u8eab\u4e4b\u95f4\uff0c\u624b\u4e2d\u5df2\u591a\u4e86\u4e00\u67c4\u5c0f \u94f2\uff0c\u90a3\u4e2a\u94f2\u975e\u91d1\u975e\u77f3\uff0c\u5374\u5149\u4eae\u8000\u773c\uff0c\u4e0d\u77e5\u662f\u4f55\u79cd\u8d28\u6599\u6240\u5236\u3002 \u3000\u3000\u5bc6\u9640\u5b9d\u6811\u4ece\u8f9b\u6377\u8fd9\u4e00\u5251\u4e2d\u89c9\u5230\u4e00\u79cd\u5e73\u751f\u672a\u6709\u7684\u611f\u89c9\u2014\u2014\u4ed6\u4f3c\u89c9\u5168\u8eab\u6bcf\u4e2a\u7a74\u9053\u65e0\u4e00\u4e0d\u5728\u8f9b \u6377\u5251\u5c16\u5a01\u80c1\u4e4b\u4e0b\uff0c\u5374\u62ff\u4e0d\u5b9a\u4ed6\u7a76\u7adf\u662f\u653b\u523a\u54ea\u91cc\u2014\u2014 \u3000\u3000\u4e0d\u8a00\u800c\u77e5\uff0c\u8f9b\u6377\u65bd\u51fa\u4e86\u5927\u884d\u5341\u5f0f\u7684\u8d77\u624b\u5f0f\uff1a\u201c\u65b9\u751f\u4e0d\u606f\uff01\u201d\u5bc6\u9640\u5b9d\u6811\u8111\u7b4b\u98de\u5feb\u5730\u8f6c\u7740\uff0c \u624b\u4e2d\u5c0f\u94f2\u5374\u672c\u80fd\u5730\u5411\u4e24\u8fb9\u6325\u51fb\u800c\u51fa\uff0c\u5f53\u8f9b\u6377\u5251\u5f0f\u843d\u4e0b\uff0c\u4ed6\u5374\u5012\u7a9c\u800c\u4e0a\uff0c\u5411\u4e24\u8fb9\u51fb\u51fa\u7684\u62db\u5f0f\u53d8 \u6210\u94f2\u5934\u70b9\u5411\u8f9b\u6377\u201c\u5de6\u819c\u7a74\u201d\uff0c\u94f2\u67c4\u5374\u70b9\u5411\u8f9b\u6377\u201c\u53f3\u5bab\u7a74\u201d\u4ed6\u8fd9\u62db\u672c\u662f\u4e0b\u610f\u8bc6\u81ea\u7136\u6325\u51fa\u7684\u62db \u5f0f\uff0c\u4f46\u5728\u6b64\u65f6\u5374\u662f\u5999\u7edd\u4eba\u8870\uff0c\u786c\u8feb\u5f97\u8f9b\u6377\u56de\u8eab\u81ea\u4fdd\uff01 \u3000\u3000\u90a3\u77e5\u8f9b\u6377\u5927\u559d\u4e00\u58f0\uff0c\u8eab\u8eaf\u9661\u7136\u4e00\u626d\uff0c\u8eaf\u4f53\u6781\u5176\u66fc\u5999\u5730\u4ece\u90a3\u5de6\u53f3\u4e24\u94f2\u4e4b\u4e2d\u4e00\u6643\u800c\u8fc7\uff0c\u51fa\u4e4e \u610f\u5916\u7684\uff0c\u5374\u653b\u5411\u5a46\u7f57\u4e94\u5947\u4e2d\u7684\u8001\u56db\u2014\u2014\u6e29\u6210\u767d\u7f57\uff01 \u3000\u3000\u539f\u6765\u5f53\u8f9b\u6377\u548c\u5bc6\u9640\u5b9d\u6811\u52a8\u624b\u4e4b\u9645\uff0c\u90a3\u51e0\u4eba\u4e5f\u5f00\u59cb\u4e86\u884c\u52a8\u3002 \u3000\u3000\u90a3\u4e94\u5947\u4e2d\u8001\u4e09\u52a0\u5927\u5c14\u89c1\u6b66\u6797\u4e4b\u79c0\u5b59\u501a\u91cd\u4f3c\u4e4e\u4e0d\u5c51\u5730\u51b7\u7b11\u671b\u7740\u4ed6\u4eec\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5927\u6012\uff0c\u8de8\u6b65 \u5c31\u60f3\u4e0a\u524d\u52a8\u624b\u3002 \u3000\u3000\u90a3\u77e5\u7ecf\u8fc7\u5929\u9b54\u91d1\u6b39\u8eab\u7554\u65f6\uff0c\u5929\u9b54\u91d1\u6b4c\u7a81\u7136\u4e00\u58f0\u4e0d\u54cd\u5730\u4e00\u5251\u523a\u51fa\uff0c\u800c\u4e14\u76f4\u53d6\u52a0\u5927\u5c14\u808b\u4e0b \u201c\u7ae0\u95e8\u7a74\u201d\u6781\u662f\u72e0\u8fa3\uff01\u6d51\u4eba\u52a0\u5927\u5c14\u8fde\u5fd9\u4e00\u6eda\u800c\u51fa\uff0c\u65b9\u624d\u8ba9\u5f00\u8fd9\u5077\u88ad\u7684\u4e00\u5251\u3002\u4ed6\u4f5c\u68a6\u4e5f\u60f3\u4e0d\u5230 \u4e16\u4e0a\u7adf\u6709\u6bd4\u4ed6\u8fd8\u6a2a\u7684\u4eba\uff0c\u5f53\u4e0b\u66b4\u543c\u4e00\u58f0\uff0c\u53cd\u8eab\u4e00\u62f3\u6253\u51fa\uff0c\u540c\u65f6\u62d4\u51fa\u80cc\u4e0a\u957f\u5251\uff0c\u5c48\u8eab\u800c\u4e0a\uff01 \u3000\u3000\u5b59\u501a\u91cd\u89c1\u6240\u6709\u7684\u4eba\u90fd\u52a8\u4e86\u624b\uff0c\u4ed6\u54c8\u54c8\u6717\u7b11\uff0c\u201c\u53ee\u201d\u7136\u4e00\u5f39\u914d\u5251\uff0c\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u6765\u5427\uff01\u201d \u6325\u5251\u8fce\u7740\u9762\u524d\u7684\u9752\u5c18\u7f57\u6c49\u800c\u4e0a\uff01 \u3000\u3000\u9752\u5c18\u7f57\u6c49\u6392\u884c\u4e94\u5947\u4e4b\u4e2d\u7684\u7b2c\u4e8c\u4f4d\uff0c\u4ed6\u5355\u5251\u4e00\u9886\uff0c\u63c9\u8eab\u800c\u4e0a\uff0c\u5269\u4e0b\u7684\u4e00\u4e2a\u6e29\u6210\u767d\u7f57\u5927\u611f\u4e0d \u8010\uff0c\u4ed6\u53ef\u4e0d\u61c2\u4ec0\u4e48\u6b66\u6797\u89c4\u77e9\uff0c\u5927\u559d\u4e00\u58f0\u5bf9\u51c6\u5b59\u501a\u91cd\u80cc\u540e\u5c31\u662f\u4e00\u62f3\u2014\u2014 \u3000\u3000\u8fd9\u6b63\u662f\u8f9b\u6377\u4ece\u5bc6\u9640\u5b9d\u6811\u5de6\u53f3\u4e24\u94f2\u4e4b\u95f4\u6643\u8fc7\u7684\u65f6\u5019\uff0c\u4ed6\u4e00\u773c\u77a5\u89c1\u6e29\u6210\u767d\u7f57\u7a81\u65bd\u5077\u88ad\uff0c\u5f53\u4e0b \u6d8c\u8eab\u76f4\u523a\uff0c\u6b32\u89e3\u5b59\u501a\u91cd\u4e4b\u5371\uff01 \u3000\u3000\u6b63\u662f\u8bf4\u8fdf\u90a3\u65f6\u5feb\uff0c\u53ea\u89c1\u6e29\u6210\u767d\u7f57\u62f3\u5f0f\u624d\u51fa\uff0c\u8f9b\u6377\u5251\u5c16\u5df2\u5e26\u7740\u4e00\u80a1\u523a\u5165\u8033\u819c\u7684\u5251\u6c14\u5982\u98de\u800c \u81f3\uff0c\u90a3\u6e29\u6210\u767d\u7f57\u529f\u529b\u6781\u662f\u4e86\u5f97\uff0c\u7adf\u662f\u786c\u751f\u751f\u6536\u56de\u62db\u5f0f\uff0c\u53cd\u624b\u62bd\u51fa\u957f\u5251\u77a7\u90fd\u4e0d\u77a7\u671b\u540e\u5c31\u662f\u4e00 \u5251\u2014\u2014 \u3000\u3000\u8f9b\u6377\u957f\u5251\u8fde\u6325\uff0c\u8679\u5149\u8d77\u843d\uff0c\u4e00\u62db\u5206\u523a\u800c\u51fa\uff0c\u8fde\u653b\u6e29\u6210\u767d\u7f57\u53ca\u5bc6\u9640\u5b9d\u6811\u4e24\u4eba\uff01 \u3000\u3000\u90a3\u65c1\u5434\u51cc\u98ce\u529b\u6597\u91d1\u9c81\u5384\uff0c\u5f62\u52bf\u53c8\u81ea\u4e0d\u540c\uff0c\u91d1\u9c81\u5384\u867d\u5728\u5a46\u7f57\u4e94\u5947\u4e2d\u662f\u6700\u5c0f\u7684\u4e00\u4e2a\uff0c\u4f46\u4ed6\u6781 \u5f97\u5e08\u8f88\u949f\u7231\uff0c\u529f\u529b\u4e4b\u9ad8\uff0c\u4ec5\u6b21\u4e8e\u5927\u5e08\u5144\u5bc6\u9640\u5b9d\u6811\uff0c\u957f\u7d22\u4e00\u76d8\uff0c\u7136\u540e\u7531\u4e0b\u800c\u4e0a\u5730\u6311\u5c06\u4e0a\u6765\uff0c\u6253 \u7b97\u5c48\u8eab\u800c\u8fc7\u2014\u2014 \u3000\u3000\u90a3\u77e5\u5434\u51cc\u98ce\u624b\u4e2d\u6240\u65bd\u7684\u4e43\u662f\u65ad\u9b42\u5251\u6cd5\u4e2d\u7684\u7edd\u62db\u201c\u9b3c\u738b\u628a\u706b\u201d\uff0c\u7adf\u7136\u5728\u4ed6\u957f\u7d22\u5c01\u9501\u4e4b\u4e2d\u4e00 \u7a7f\u800c\u5165\uff0c\u5251\u5c16\u95ea\u5904\uff0c\u76f4\u53d6\u4ed6\u817f\u4e0a\u5927\u7a74\u3002 \u3000\u3000\u4ed6\u6599\u4e0d\u5230\u51cc\u98ce\u5251\u5f0f\u5982\u6b64\u4e4b\u5feb\uff0c\u8fde\u5fd9\u4e24\u6253\u4e09\u6311\u624d\u7b97\u633d\u56de\u5384\u52bf\uff01\u5f53\u5e74\u6cb3\u6d1b\u4e00\u5251\u5434\u8bcf\u4e91\u5728\u5c45\u5eb8 \u5173\u5934\u529b\u6218\u201c\u957f\u767d\u4e09\u9e70\u201d\uff0c\u65ad\u9b42\u5b9d\u5251\u65bd\u5230\u201c\u9b3c\u738b\u628a\u706b\u201d\u8fd9\u62db\u4e0a\uff0c\u8fde\u4f24\u4e24\u654c\uff0c\u5269\u4e0b\u7684\u4e00\u9e70\u4e5f\u4e0d\u6218 \u800c\u9003\uff0c\u5355\u5251\u65ad\u9b42\u7684\u4e07\u513f\u4ece\u6b64\u626c\u6ea2\u5929\u4e0b\u3002\u6b64\u65f6\u51cc\u98ce\u540c\u6837\u65bd\u51fa\u8fd9\u62db\uff0c\u4ed6\u5e74\u7eaa\u867d\u8f7b\u5374\u8fde\u9022\u5947\u9047\uff0c\u5a01 \u529b\u53ea\u5728\u5f53\u5e74\u5434\u8bcf\u4e91\u4e4b\u4e0a\uff01 \u3000\u3000\u91d1\u9c81\u5384\u62ac\u5934\u671b\u4e86\u671b\u5bf9\u9762\u7684\u5434\u51cc\u98ce\uff0c\u53ea\u89c1\u4ed6\u4fca\u7f8e\u7edd\u4f26\u7684\u8138\u4e0a\u6cdb\u7740\u8000\u4eba\u7684\u5149\u8292\uff0c\u624b\u4e2d\u5251\u6a2a\u5728 \u80f8\u524d\u5b9b\u5982\u4e00\u78a7\u5bd2\u6f6d\uff0c\u4ed6\u53d1\u89c9\u51e0\u6708\u4e0d\u89c1\uff0c\u8fd9\u7f8e\u5c11\u5e74\u53c8\u6709\u4e86\u957f\u8db3\u7684\u8fdb\u6b65\uff01 \u3000\u3000\u4e8e\u662f\u4ed6\u73af\u76ee\u56db\u987e\uff0c\u53ea\u89c1\u4e8c\u5e08\u5144\u5df2\u4e0e\u6b66\u6797\u4e4b\u79c0\u5b59\u501a\u91cd\u6597\u5728\u4e00\u8d77\uff0c\u4e24\u53ea\u957f\u5251\u5b9b\u5982\u4e24\u6761\u9752\u9f99\u6f2b \u5929\u98de\u821e\uff0c\u4ed6\u53ea\u89c9\u6ee1\u5929\u90fd\u662f\u6a21\u7cca\u7684\u5251\u5f71\u3002 \u3000\u3000\u4ed6\u6697\u6697\u5fc3\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u4e8c\u5e08\u5144\u7684\u5251\u6cd5\u5728\u54b1\u4eec\u5144\u5f1f\u4e2d\u7b97\u5f97\u7b2c\u4e00\u4f4d\uff0c\u600e\u4e48\u7adf\u6536\u62fe\u4e0d\u4e86\u90a3\u5c0f \u5b50\uff0c\u96be\u9053\u8fd9\u56db\u4eba\u7adf\u90fd\u662f\u8fd9\u7b49\u9ad8\u624b\u4e48\uff1f\u201d \u3000\u3000\u4ed6\u4e00\u4e1d\u4e5f\u6ca1\u6709\u6599\u9519\uff0c\u8fd9\u56db\u4eba\u786e\u90fd\u662f\u5f53\u4eca\u4e2d\u539f\u6700\u51fa\u8272\u7684\u56db\u5927\u9ad8\u624b\uff01 \u3000\u3000\u5a46\u7f57\u4e94\u5947\u4e2d\u9664\u4e86\u4f7f\u94f2\u7684\u5bc6\u9640\u5b9d\u6811\u548c\u4f7f\u957f\u7d22\u7684\u91d1\u9c81\u5384\uff0c\u5c31\u8981\u7b97\u8fd9\u8001\u4e8c\u9752\u5c18\u7f57\u6c49\u4e86\u3002\u4ed6\u66fe\u51ed \u7740\u624b\u4e2d\u4e00\u53ea\u957f\u5251\u8fde\u8d25\u5929\u5170\u5341\u516b\u5251\u58eb\uff0c\u56e0\u6b64\u5728\u5929\u4e9b\u897f\u85cf\u4e00\u5e26\uff0c\u51e1\u662f\u4f7f\u5251\u7684\u4eba\uff0c\u6ca1\u6709\u4e0d\u77e5\u9053\u9752\u5c18 \u7f57\u6c49\u7684\u3002 \u3000\u3000\u4f46\u662f\u6b64\u523b\uff0c\u5b59\u501a\u91cd\u91cd\u65bd\u51fa\u65b0\u4ece\u5e73\u51e1\u4e0a\u4eba\u5904\u5b66\u5f97\u7684\u201c\u56de\u58c1\u5251\u5f0f\u201d\u2014\u2014\u8fd9\u5251\u6cd5\u4e43\u662f\u5c11\u6797\u5931\u4f20 \u7684\u7edd\u5999\u5b88\u5f0f\uff0c\u662f\u4ee5\u9752\u5c18\u7f57\u6c49\u624b\u4e0b\u867d\u7136\u653b\u52bf\u5982\u8679\uff0c\u4f46\u5b59\u501a\u91cd\u5374\u4e00\u6b65\u4e0d\u8ba9\u5730\u575a\u5b88\u56fa\u5c01\uff01 \u3000\u3000\u91d1\u9c81\u5384\u518d\u77a5\u5411\u5de6\u8fb9\u7684\u4e00\u5bf9\uff0c\u52a0\u5927\u5c14\u62f3\u5251\u4ea4\u52a0\uff0c\u66f4\u52a0\u4e0a\u5982\u96f7\u7684\u543c\u58f0\uff0c\u5982\u75af\u864e\u4e00\u822c\u5730\u5411\u90a3\u4e11 \u8138\u5c11\u5e74\u653b\u53bb\uff0c\u90a3\u4e11\u8138\u5c11\u5e74\u53bb\u4e1d\u6beb\u4e0d\u5ba2\u6c14\u5730\u4e5f\u662f\u62f3\u5251\u5e76\u65bd\u52a0\u62a2\u653b\uff0c\u6253\u5f97\u6781\u662f\u6fc0\u70c8\uff0c\u4f46\u4ed6\u5374\u6697\u6697 \u653e\u5fc3\uff0c\u5fd6\u9053\uff1a \u3000\u3000\u201c\u8fd9\u4e11\u5bb6\u4f19\u867d\u7136\u51f6\u72e0\uff0c\u5374\u662f\u8fd9\u56db\u4eba\u4e2d\u5374\u5f31\u7684\u4e00\u73af\uff0c\u52a0\u5927\u5c14\u5c3d\u5bf9\u4ed8\u5f97\u4e86\uff0c\u53ea\u8981\u6211\u6216\u5927\u5e08\u5144 \u6709\u4e00\u4eba\u5f97\u80dc\uff0c\u7ba1\u6559\u4ed6\u4eec\u56db\u4eba\u4e00\u4e2a\u4e5f\u8d70\u4e0d\u4e86\u2014\u2014\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u53c8\u56de\u89c6\u8f9b\u6377\u548c\u5927\u5e08\u5144\u7684\u62fc\u6597\u2014\u2014\u5c31\u5728\u8fd9\u65f6\uff0c\u8033\u8fb9\u54cd\u8d77\u51cc\u98ce\u6717\u7136\u7684\u53eb\u58f0\uff1a\u201c\u770b \u5251\uff01\u201d \u3000\u3000\u51cc\u98ce\u53eb\u58f0\u672a\u6cef\uff0c\u624b\u4e2d\u957f\u5251\u5df1\u5212\u7740\u4e00\u9053\u5149\u534e\u98de\u821e\u800c\u81f3\u3002\u91d1\u9c81\u5384\u5fc3\u4e2d\u76d8\u7b97\u4e07\u5e78\uff0c\u80c6\u6c14\u5927\u58ee\uff0c \u4ed6\u51b7\u7b11\u4e00\u58f0\uff0c\u77a7\u90fd\u4e0d\u77a7\u5730\u53cd\u8eab\u4e00\u7d22\u6325\u51fa\u3002 \u3000\u3000\u90a3\u957f\u7d22\u98de\u5feb\u5730\u5728\u7a7a\u4e2d\u6253\u4e86\u4e24\u4e2a\u5708\u513f\uff0c\u7d22\u5934\u513f\u5df1\u5230\u4e86\u51cc\u98ce\u81c2\u4e0a\u201c\u66f2\u6c60\u201d\u3002 \u3000\u3000\u51cc\u98ce\u53cc\u76ee\u51dd\u89c6\uff0c\u53f3\u624b\u6301\u5251\u968f\u7740\u91d1\u5907\u5384\u7684\u7d22\u5b50\u4e5f\u6253\u4e86\u4e24\u4e2a\u5708\u513f\uff0c\u6b63\u597d\u8eb2\u5f00\u6765\u52bf\uff0c\u624b\u4e2d\u957f\u5251 \u5f80\u4e0a\u4e00\u9876\uff0c\u518d\u5ea6\u65bd\u51fa\u201c\u9b3c\u738b\u628a\u706b\u201d\u7684\u7edd\u5b66\u3002 \u3000\u3000\u91d1\u9c81\u5384\u4e07\u6599\u4e0d\u5230\u51cc\u98ce\u5251\u672f\u5c45\u7136\u7cbe\u8fdb\u5982\u65af\uff0c\u4ed6\u518d\u4e5f\u4e0d\u6562\u6020\u6162\uff0c\u8155\u4e0a\u771f\u529b\u53eb\u8db3\uff0c\u4e00\u65f6\u5578\u58f0\u5927 \u8d77\uff0c\u6f2b\u5929\u90fd\u662f\u7d22\u5f71\u98de\u821e\u3002 \u3000\u3000\u51cc\u98ce\u81ea\u4ece\u4e94\u534e\u5c71\u4e00\u6218\u4e4b\u540e\uff0c\u672c\u8eab\u5251\u672f\u5df1\u5168\u90e8\u53d1\u6325\u65e0\u9057\uff0c\u53ea\u89c1\u4ed6\u6e05\u5578\u4e00\u58f0\uff0c\u5251\u5149\u970d\u970d\uff0c\u7adf \u7136\u548c\u5929\u4e9b\u9ad8\u624b\u91d1\u9c81\u5384\u62a2\u653b\u8d77\u6765\u3002 \u3000\u3000\u7a81\u7136\u6012\u53eb\u58f0\u8d77\uff0c\u539f\u6765\u90a3\u8fb9\u52a0\u5927\u5c14\u548c\u5929\u9b54\u91d1\u6b39\u4e24\u4eba\u5df2\u5230\u4e86\u8089\u640f\u7684\u9636\u6bb5\u2014\u2014 \u3000\u3000\u86ee\u5b50\u52a0\u5927\u5c14\u4e00\u751f\u8fd8\u6ca1\u6709\u89c1\u8fc7\u6bd4\u4ed6\u81ea\u5df1\u66f4\u6a2a\u7684\u4eba\uff0c\u8fd9\u65f6\u90a3\u5929\u9b54\u91d1\u6b39\u7adf\u7136\u6beb\u4e0d\u9000\u8ba9\u5730\u548c\u4ed6\u786c \u62fc\u786c\u649e\uff0c\u6bcf\u4e00\u62db\u4e00\u5f0f\u90fd\u662f\u4ece\u6a2a\u86ee\u4e0d\u582a\u7684\u5730\u4f4d\u9012\u8fdb\u6765\u7684\uff0c\u76f4\u6c14\u4ed6\u6012\u559d\u8fde\u5929\u3002 \u3000\u3000\u5c24\u5176\u6700\u4ee4\u4ed6\u5fff\u6012\u7684\u662f\u90a3\u5929\u9b54\u91d1\u6b39\u51b7\u51b7\u4e00\u5f20\u4e11\u8138\u4e0a\u663e\u51fa\u4e0d\u53ef\u4e00\u4e16\u7684\u72c2\u6001\uff0c\u886c\u7740\u90a3\u4e24\u9053\u6050\u6016 \u7684\u5200\u75a4\uff0c\u76ca\u53d1\u4ee4\u4eba\u96be\u582a\u3002 \u3000\u3000\u53ea\u89c1\u4eba\u5f71\u4e00\u5408\u4e00\u5206\uff0c\u5929\u9b54\u91d1\u6b39\u5de6\u80a9\u88ab\u52a0\u5927\u5c14\u5212\u7834\u4e00\u6761\u53e3\u5b50\uff0c\u9c9c\u8840\u957f\u6d41\uff0c\u4ed6\u54fc\u90fd\u6ca1\u6709\u54fc\u4e00 \u4e0b\uff0c\u53cc\u811a\u4e00\u6643\uff0c\u65bd\u51fa\u6b64\u541b\u91d1\u4e00\u9e4f\u7684\u6210\u540d\u7edd\u5b66\u201c\u767e\u8db3\u5251\u6cd5\u201d\u4e2d\u7684\u7edd\u62db\u201c\u6bd2\u86e4\u6a2a\u6574\u201d\uff0c\u8f7b\u6084\u6084\u5730 \u4e5f\u5728\u52a0\u5927\u5c14\u80f8\u524d\u8fd8\u523a\u4e86\u4e00\u5251\uff01 \u3000\u3000\u516d\u76d8\u5c71\u4e0b\uff0c\u94f6\u8272\u4e16\u754c\uff0c\u6f2b\u7a7a\u94f6\u5149\u98de\u8679\uff0c\u851a\u6210\u5947\u89c2\uff0c\u4e5d\u4e2a\u5f53\u4eca\u4e00\u7b49\u7684\u9752\u5e74\u9ad8\u624b\u5728\u6b64\u5c55\u5f00\u62fc \u6597\u3002 \u3000\u3000\u84e6\u7136\u2014\u2014 \u3000\u3000\u60ca\u53eb\u58f0\u9aa4\u8d77\uff0c\u867d\u7136\u6bcf\u4e2a\u4eba\u90fd\u5168\u795e\u8d2f\u6ce8\u4e8e\u8fc7\u62db\uff0c\u4f46\u4ecd\u4e0d\u4f4f\u60ca\u53eb\u51fa\u58f0\uff0c\u539f\u6765\u90a3\u5929\u7afa\u7b2c\u4e00\u5251\u5ba2 \u9752\u5c18\u7f57\u6c49\u548c\u6b66\u6797\u4e4b\u79c0\u5b59\u501a\u91cd\u7684\u62fc\u6597\u53d1\u751f\u4e86\u60ca\u4eba\u7684\u53d8\u5316\u2014\u2014 \u3000\u3000\u53ea\u89c1\u4e24\u4eba\u98de\u817e\u5728\u7a7a\u4e2d\uff0c\u5251\u5149\u4eba\u5f71\u6eda\u6210\u4e00\u5708\uff0c\u800c\u5251\u5149\u76d8\u531d\u7684\u8303\u56f4\u7adf\u7136\u8fdc\u53ca\u4e09\u4e08\u65b9\u5706\uff01 \u3000\u3000\u9752\u5c18\u7f57\u6c49\u4e45\u6218\u5b59\u501a\u91cd\u4e0d\u4e0b\uff0c\u4e00\u6012\u4e4b\u4e0b\u65bd\u51fa\u5e73\u751f\u7edd\u5b66\u201c\u767e\u5408\u6167\u5251\u201d\uff01 \u3000\u3000\u201c\u767e\u5408\u6167\u5251\u201d\u4e00\u5171\u5341\u4e5d\u5f0f\uff0c\u662f\u6614\u5e74\u5929\u7afa\u201c\u98de\u9f99\u6559\u201d\u9547\u6559\u4e4b\u5b9d\uff0c\u9752\u5c18\u7f57\u6c49\u5de7\u83b7\u5947\u7f18\u5f97\u4e86\u8fd9 \u5957\u5251\u6cd5\uff0c\u662f\u4ee5\u5e76\u975e\u51fa\u81ea\u6052\u6cb3\u4e09\u4f5b\u4e4b\u624b\u3002 \u3000\u3000\u9752\u5c18\u7f57\u6c49\u6210\u540d\u4ee5\u6765\uff0c\u53ea\u7528\u8fc7\u4e00\u6b21\u201c\u767e\u5408\u6167\u5251\u201d\u90a3\u6b21\u4ed6\u5171\u7528\u5341\u62db\uff0c\u5c31\u8fde\u8d25\u56db\u4e2a\u56f4\u653b\u4ed6\u7684\u5f3a \u654c\uff0c\u8fd9\u65f6\u4ed6\u65bd\u5c3d\u81ea\u5df1\u6240\u5b66\uff0c\u5374\u59cb\u7ec8\u653b\u4e0d\u8fdb\u5b59\u501a\u91cd\u7d27\u4e25\u7684\u5251\u5708\uff0c\u4e00\u6012\u4e4b\u4e0b\uff0c\u65bd\u51fa\u8fd9\u5957\u7edd\u5b66\uff01 \u3000\u3000\u201c\u767e\u5408\u6167\u5251\u201d\u4e00\u65bd\u51fa\uff0c\u5b59\u501a\u91cd\u7acb\u89c9\u5f62\u52bf\u4e0d\u5bf9\uff0c\u9752\u5c18\u7f57\u6c49\u5251\u5f0f\u4e4b\u4e2d\u751f\u51fa\u4e00\u79cd\u53e4\u602a\u7684\u65cb\u529b\uff0c \u6bcf\u6bcf\u4ee4\u4ed6\u5251\u5f0f\u5931\u53bb\u51c6\u5934\u3002 \u3000\u3000\u4e00\u8fde\u4e09\u62db\uff0c\u5b59\u501a\u91cd\u9669\u8c61\u73af\u751f\uff0c\u4ed6\u4e00\u6025\u4e4b\u4e0b\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u5251\u5b50\u4e00\u6a2a\uff0c\u9661\u7136\u8155\u4e0a\u53eb\u8db3\u771f\u529b\uff0c \u4e00\u5206\u4e00\u5408\u4e4b\u95f4\uff0c\u6d51\u7136\u63a8\u51fa\uff0c\u970e\u65f6\u6f2b\u5929\u90fd\u662f\u4ed6\u7684\u8679\u5149\u5251\u5f71\uff0c\u7adf\u662f\u201c\u5927\u884d\u5341\u5f0f\u201d\u4e2d\u7684\u201c\u65b9\u751f\u4e0d \u606f\u201d\uff01 \u3000\u3000\u8fd9\u201c\u5927\u884d\u5341\u5f0f\u201d\u4e43\u662f\u5e73\u51e1\u4e0a\u4eba\u4ece\u5931\u4f20\u7684\u201c\u5e03\u8fbe\u4e09\u5f0f\u201d\u4e2d\u5316\u51fa\u6765\u7684\uff0c\u8fd9\u201c\u65b9\u751f\u4e0d\u606f\u201d\u66f4\u662f \u5176\u4e2d\u6700\u7cbe\u5965\u5947\u5999\u7684\u4e00\u62db\uff0c\u8fd9\u4f5b\u95e8\u7edd\u5b66\u679c\u771f\u79f0\u5f97\u4e0a\u4e3e\u4e16\u65e0\u53cc\uff0c\u4f7f\u7528\u4e4b\u9645\uff0c\u5a01\u529b\u7adf\u662f\u56e0\u4eba\u800c\u5f02\uff0c \u5b59\u501a\u91cd\u65bd\u51fa\u867d\u4e0d\u53ca\u8f9b\u6377\u7684\u72e0\u8fa3\u7cbe\u5947\uff0c\u4f46\u4ed6\u4e43\u662f\u4f5b\u95e8\u5f1f\u5b50\uff0c\u8d77\u624b\u4e4b\u95f4\u81ea\u6709\u4e00\u756a\u6b63\u5927\u5b8f\u5e7f\u4e4b\u8c61\uff01 \u3000\u3000\u9752\u5c18\u7f57\u6c49\u773c\u89c1\u5f97\u624b\uff0c\u5de6\u53f3\u5404\u53d1\u51fa\u4e00\u80a1\u65cb\u52b2\uff0c\u90a3\u77e5\u4e24\u80a1\u5f3a\u52b2\u4e00\u78b0\u4e0a\u5bf9\u65b9\u5251\u5708\uff0c\u7a81\u7136\u6709\u5982\u77f3 \u843d\u5927\u6d77\uff0c\u6df1\u4e0d\u77e5\u5e95\u3002 \u3000\u3000\u4ed6\u60ca\u6012\u4e4b\u4e0b\uff0c\u5251\u5149\u8fde\u95ea\uff0c\u201c\u767e\u5408\u6167\u5251\u201d\u4e2d\u7684\u7edd\u62db\u5168\u90a3\u65bd\u51fa\uff0c\u540c\u65f6\u4ed6\u8eab\u5b50\u4e0a\u4e0b\u98de\u817e\uff0c\u6709\u5982 \u868a\u9f99\u51fa\u6d77\uff0c\u52c7\u4e0d\u53ef\u6321\u3002 \u3000\u3000\u5b59\u501a\u91cd\u4e00\u62db\u5f97\u624b\uff0c\u201c\u5927\u884d\u5341\u5f0f\u201d\u4e5f\u8fde\u65bd\u51fa\u624b\uff0c\u4ee5\u5feb\u62a2\u5feb\uff0c\u970e\u65f6\u5251\u5149\u98de\u821e\uff0c\u76d8\u65cb\u5f00\u6765\u2014\u2014 \u4e5f\u6b63\u662f\u4f17\u4eba\u60ca\u53eb\u4e4b\u65f6\u3002 \u3000\u3000\u8fd9\u4e24\u5957\u5947\u7edd\u5929\u4e0b\u7684\u5251\u6cd5\u6108\u6597\u6108\u75be\uff0c\u5251\u5149\u76d8\u531d\u8303\u56f4\u4e5f\u8d8a\u6765\u8d8a\u5927\uff0c\u8db3\u8db3\u7b3c\u7f69\u4e86\u4e94\u4e08\u65b9\u5706\uff01 \u3000\u3000\u5176\u4ed6\u7684\u4eba\u60df\u6050\u5f71\u54cd\u81ea\u5df1\u4eba\uff0c\u90fd\u81ea\u52a8\u9000\u5230\u4e94\u4e08\u5916\u4e4b\u5904\u53ae\u6740\u3002 \u3000\u3000\u91d1\u9c81\u5384\u89c1\u81ea\u5df1\u4e00\u65f6\u6536\u62fe\u4e0d\u4e86\u5434\u51cc\u98ce\uff0c\u4e8e\u662f\u5e0c\u671b\u5927\u5e08\u5144\u4e8c\u5e08\u5144\u4e4b\u4e2d\u81f3\u5c11\u6709\u4e00\u4e2a\u4eba\u83b7\u80dc\u3002\u54ea \u77e5\u90a3\u8fb9\u9752\u5c18\u7f57\u6c49\u65bd\u51fa\u4e86\u201c\u767e\u5408\u6167\u5251\u201d \u3000\u3000\u72b9\u81ea\u80dc\u4e0d\u4e86\u5bf9\u65b9\u3002\u5fc3\u4e2d\u4e0d\u7531\u60ca\u5f02\u65e0\u6bd4\uff0c\u4e8e\u662f\u4ed6\u4e58\u81ea\u5df1\u957f\u7d22\u5c01\u51fa\u7684\u5f53\u513f\uff0c\u8f6c\u770b\u5927\u5e08\u5144\u2014\u2014 \u3000\u3000\u8fd9\u4e00\u770b\uff0c\u51e0\u4e4e\u4ee4\u4ed6\u5fd8\u5374\u6325\u7d22\u5fa1\u654c\u2014\u2014 \u3000\u3000\u539f\u6765\u8f9b\u6377\u4e00\u4eba\u4e0e\u201c\u5a46\u7f57\u4e94\u5947\u201d\u4e2d\u7684\u8001\u5927\u5bc6\u9640\u5b9d\u6811\u4e0e\u8001\u56db\u6e29\u6210\u767d\u7f57\u4e24\u4eba\u62fc\u6597\uff0c\u4ed6\u8eab\u5904\u8fd9\u4e24 \u5927\u9ad8\u624b\u5939\u51fb\u4e4b\u4e2d\uff0c\u5927\u53d1\u795e\u5a01\uff0c\u5de6\u638c\u53f3\u5251\uff0c\u914d\u5408\u811a\u4e0b\u795e\u5947\u7edd\u4e16\u7684\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\uff0c\u867d\u7136\u5bc6\u9640\u5b9d\u6811 \u4e0e\u6e29\u6210\u767d\u7f57\u529f\u529b\u6df1\u6781\uff0c\u4f46\u4ed6\u51fa\u62db\u5982\u98ce\uff0c\u65f6\u800c\u201c\u5927\u884d\u5341\u5f0f\u201d\uff0c\u5de6\u638c\u4e0d\u65f6\u62a2\u51fa\u201c\u7a7a\u7a7a\u638c\u6cd5\u201d\u8f85\u52a9 \u653b\u52bf\uff0c\u66f4\u517c\u8eab\u5f62\u66fc\u5999\u65e0\u65b9\uff0c\u4e3e\u624b\u6295\u8db3\u83ab\u4e0d\u662f\u5f53\u4eca\u5929\u4e0b\u9876\u5c16\u5947\u4eba\u7684\u5f97\u610f\u7edd\u5b66\uff0c\u58f0\u5a01\u4e4b\u5927\uff0c\u51fa\u4eba \u610f\u8868\uff01 \u3000\u3000\u91d1\u9c81\u5384\u4e00\u614c\u4e4b\u4e0b\uff0c\u9669\u4e9b\u88ab\u51cc\u98ce\u72e0\u75be\u7684\u4e00\u5251\u523a\u4e2d\uff0c\u4ed6\u5927\u559d\u4e00\u58f0\uff0c\u4e00\u8fde\u4e94\u7d22\uff0c\u624d\u7b97\u635e\u56de\u5384 \u52bf\u3002 \u3000\u3000\u90a3\u8fb9\u4f20\u6765\u4e00\u58f0\u95f7\u559d\uff0c\u539f\u6765\u52a0\u5927\u5c14\u4e0e\u5929\u9b54\u91d1\u6b39\u5251\u5f0f\u76f8\u53e0\uff0c\u6697\u8f83\u5185\u529b\uff0c\u540c\u65f6\u4e24\u4eba\u5de6\u638c\u4e00\u9f50\u6253 \u51fa\uff0c\u7830\u7684\u4e00\u58f0\u8089\u78b0\u8089\u5730\u63a5\u4e86\u4e00\u62db\uff0c\u4e24\u4eba\u90fd\u6447\u6447\u6643\u6643\u5730\u9000\u540e\u6570\u6b65\uff0c\u8138\u8272\u82cd\u767d\uff0c\u4f46\u4e24\u4eba\u90fd\u6ca1\u6709\u54fc \u4e00\u4e0b\u3002 \u3000\u3000\u91d1\u9c81\u5384\u89c1\u60c5\u5f62\u4e0d\u5bf9\uff0c\u6597\u7136\u4e24\u7d22\u5c06\u51cc\u98ce\u957f\u5251\u5c01\u5f00\uff0c\u6717\u58f0\u53eb\u9053\uff1a\u201c\u59d3\u8f9b\u7684\u6709\u79cd\u7a0d\u505c\u4e00\u4e0b \u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u50b2\u7136\u4e00\u7b11\u9053\uff1a\u201c\u6709\u4f55\u4e0d\u6562\uff1f\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u5de6\u4e00\u638c\u53f3\u4e00\u5251\uff0c\u811a\u4e0b\u865a\u6b65\u4e00\u8d70\uff0c\u4fa7\u8eab\u5982\u7bad\u4e00\u822c\u9000\u4e86\u534a\u4e08\u3002 \u3000\u3000\u5176\u4f59\u51e0\u4eba\u89c1\u4ed6\u4eec\u4f4f\u624b\uff0c\u4e5f\u90fd\u62bd\u8eab\u6682\u505c\u3002 \u3000\u3000\u53ea\u89c1\u91d1\u9c81\u5384\u5e72\u7b11\u4e00\u58f0\u9053\uff1a\u201c\u4e2d\u539f\u6b66\u5b66\u679c\u771f\u8fd8\u6709\u4e24\u4e0b\u5b50\uff0c\u54b1\u4eec\u5144\u5f1f\u8fd8\u6709\u4e00\u4e2a\u4e0d\u6210\u6c14\u5019\u7684\u9635 \u52bf\u8981\u8bf7\u5404\u4f4d\u6307\u6559\uff0c\u563f\u563f\uff01\u5728\u8f9b\u5927\u4fa0\u8fd9\u79cd\u9635\u6cd5\u884c\u5bb6\u773c\u4e2d\uff0c\u54b1\u4eec\u53ef\u6210\u4e86\u73ed\u95e8\u5f04\u65a7\u4e86\u2014\u2014\u201d \u3000\u3000\u63a5\u7740\u53c8\u8f6c\u8eab\u5bf9\u5bc6\u9640\u5b9d\u6811\u7b49\u8bf4\u4e86\u4e00\u53e5\u68b5\u8bed\u3002 \u3000\u3000\u8f9b\u6377\u7b49\u4eba\u53ea\u89c9\u773c\u524d\u4e00\u82b1\uff0c\u5a46\u7f57\u4e94\u5947\u5df2\u6781\u8fc5\u901f\u5730\u5404\u81ea\u7ad9\u597d\u4f4d\u7f6e\uff0c\u6b63\u597d\u628a\u4ed6\u4eec\u56db\u4eba\u56f4\u5728\u4e2d \u592e\u3002 \u3000\u3000\u8fd9\u4e00\u4e0b\u8fde\u8f9b\u6377\u8fd9\u7b49\u9ad8\u624b\u90fd\u5f88\u5403\u60ca\uff0c\u56e0\u4e3a\u5a46\u7f57\u4e94\u5947\u95ea\u8eab\u4e4b\u5feb\uff0c\u5927\u51fa\u610f\u6599\uff0c\u53ea\u89c9\u4eba\u5f71\u4e00\u6643\uff0c \u5df2\u81ea\u7ad9\u597d\u4e86\u4f4d\u7f6e\uff01 \u3000\u3000\u90a3\u5bc6\u9640\u5b9d\u6811\u7a81\u7136\u5bf9\u91d1\u9c81\u5384\u9053\uff1a\u201c\u4f20\u8bdd\u65af\u5c14\uff0c\u6cb3\u7f57\u8fdc\u5b97\u6469\u5df4\uff0c\u56e0\u4f1a\u65af\u6d01\u661f\u57fa\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u662f\u8bf4\uff1a\u201c\u8fd9\u8f9b\u5c0f\u5b50\u4e0d\u8fc7\u4ed7\u7740\u602a\u62db\u4e00\u65f6\u4e86\u5f97\uff0c\u518d\u6253\u4e0b\u53bb\u6211\u81ea\u6709\u529e\u6cd5\u3002\u201d \u3000\u3000\u6562\u60c5\u4ed6\u4e00\u4e0a\u624b\u5c31\u88ab\u8f9b\u6377\u8fde\u65bd\u5947\u62db\u8feb\u5f97\u65bd\u4e0d\u51fa\u7edd\u5b66\uff0c\u662f\u4ee5\u5fc3\u4e2d\u5927\u662f\u4e0d\u670d\u3002 \u3000\u3000\u91d1\u9c81\u5384\u4e5f\u7528\u68b5\u8bed\u544a\u4ed6\uff1a\u201c\u4e0d\u7ba1\u600e\u6837\uff0c\u54b1\u4eec\u8fd8\u662f\u7528\u9635\u6cd5\u6bd4\u8f83\u7a33\u624e\u7a33\u6253\u3002\u201d \u3000\u3000\u83ab\u770b\u91d1\u9c81\u5384\u662f\u5a46\u7f57\u4e94\u5947\u4e2d\u7684\u5c0f\u4e48\uff0c\u7136\u800c\u5374\u662f\u4f17\u4eba\u7684\u667a\u56ca\uff0c\u8fde\u5bc6\u9640\u5b9d\u6811\u90fd\u542c\u4ece\u4ed6\u7684\u8ba1\u7b56\u3002 \u3000\u3000\u8f9b\u6377\u3001\u51cc\u98ce\u3001\u91d1\u6b39\u548c\u5b59\u501a\u91cd\u56db\u4eba\u867d\u7136\u6ca1\u6709\u5f00\u8154\uff0c\u4f46\u662f\u5f7c\u6b64\u5fc3\u4e2d\u90fd\u6709\u9ed8\u5951\u2014\u2014\u8fd9\u4e9b\u86ee\u5b50\u7684 \u9635\u6cd5\u5fc5\u7136\u53e6\u6709\u4e00\u5957\u3002 \u3000\u3000\u8f9b\u6377\u51b7\u9759\u5730\u628a\u5f62\u52bf\u6253\u91cf\u4e86\u4e00\u4f1a\uff0c\u7cbe\u901a\u5929\u4e0b\u9635\u6cd5\u7684\u4ed6\u7acb\u523b\u53d1\u89c9\u5bc6\u9640\u5b9d\u6811\u662f\u7ad9\u5728\u201c\u9635\u4e3b\u201d\u7684 \u5730\u4f4d\uff0c\u4ed6\u7684\u5de6\u53f3\u662f\u52a0\u5927\u5c14\u548c\u6e29\u6210\u767d\u7f57\uff0c\u4ed6\u7684\u5bf9\u9762\u662f\u9752\u5c18\u7f57\u6c49\u548c\u91d1\u9c81\u5384\uff01 \u3000\u3000\u4ed6\u63d0\u795e\u601d\u7d22\u7740\uff0c\u4ed6\u60f3\uff1a\u201c\u5927\u54e5\u3001\u5b59\u501a\u91cd\u3001\u91d1\u6b39\u4ed6\u4eec\u867d\u7136\u4e0d\u7cbe\u9635\u6cd5\uff0c\u4f46\u5fc5\u7136\u77e5\u9053\u2018\u4ee5\u9759\u5236 \u52a8\u2019\u7684\u57fa\u672c\u539f\u5219\uff0c\u662f\u4ee5\u5728\u5bf9\u65b9\u4e0d\u66fe\u52a8\u624b\u4e4b\u524d\uff0c\u4ed6\u4eec\u5fc5\u7136\u4e0d\u4f1a\u5148\u52a8\uff0c\u6211\u8981\u5192\u4e00\u4e2a\u9669\u2014\u2014\u201d \u3000\u3000\u5468\u56f4\u9759\u5f97\u8fde\u96ea\u82b1\u843d\u5730\u90fd\u4f1a\u53d1\u51fa\u6e05\u6670\u7684\u58f0\u97f3\uff0c\u8f9b\u6377\u53cc\u76ee\u51dd\u89c6\u7740\uff0c\u5fc3\u4e2d\u4e0d\u65ad\u7684\u76d8\u7b97\uff1a \u3000\u3000\u201c\u4e0d\u7ba1\u4ed6\u4eec\u662f\u4ec0\u4e48\u9635\uff0c\u8fd9\u77ee\u548c\u5c1a\u5fc5\u662f\u2018\u9635\u4e3b\u65e0\u7591\u2019\uff0c\u6211\u8981\u5728\u4ed6\u4eec\u624d\u53d1\u52a8\u7684\u4e00\u5239\u90a3\u95f4\uff0c\u51fa \u5947\u5236\u80dc\u5730\u5c06\u8fd9\u77ee\u548c\u5c1a\u51fb\u5012\u2014\u2014\u5373\u4f7f\u4e0d\u6210\uff0c\u4e5f\u81f3\u5c11\u51b2\u4e71\u4ed6\u4eec\u7684\u9635\u52bf\u2014\u2014 \u3000\u3000\u201c\u547c\u201d\u4e00\u58f0\uff0c\u5bc6\u9640\u5b9d\u6811\u7684\u5c0f\u94f2\u513f\u51cc\u7a7a\u4e00\u6325\u2014\u2014 \u3000\u3000\u679c\u7136\uff0c\u5434\u51cc\u98ce\u5b59\u501a\u91cd\u548c\u91d1\u6b4c\u90fd\u662f\u6309\u5251\u51dd\u89c6\uff0c\u7eb9\u98ce\u4e0d\u52a8\u2014\u2014 \u3000\u3000\u8f9b\u6377\u77e5\u9053\u65f6\u673a\u4e0d\u518d\uff0c\u4ed6\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u5251\u6c14\u58f0\u8d77\uff0c\u6885\u9999\u5b9d\u5251\u4e0a\u95ea\u51fa\u903c\u4eba\u7684\u5149\u8292\uff0c\u4ed6\u53cc\u811a \u66fc\u5999\u5730\u4e00\u9519\uff0c\u534a\u4e08\u7684\u8ddd\u79bb\u5982\u98de\u4e00\u822c\u4ece\u4ed6\u811a\u4e0b\u63a0\u8fc7\uff0c\u4ed6\u5251\u5c16\u8fde\u95ea\uff0c\u5149\u8292\u5c3d\u51fa\u4e09\u4e08\u4e4b\u5916\uff0c\u540c\u65f6\u5de6 \u638c\u6697\u85cf\u6740\u624b\u2014\u2014 \u3000\u3000\u8fd9\u62db\u6b63\u662f\u201c"\u5927\u884d\u5341\u5f0f\u201d\u4e2d\u6700\u72e0\u7684\u4e00\u5f0f\uff1a\u201c\u7269\u6362\u661f\u79fb\u201d\uff0c\u53ea\u662f\u5728\u8f9b\u6377\u624b\u4e2d\u65bd\u51fa\uff0c\u6bd4 \u4e4b\u65b9\u624d\u5b59\u501a\u91cd\u65bd\u65f6\u53e6\u6709\u4e00\u756a\u72e0\u8fa3\u7684\u5473\u9053\u3002 \u3000\u3000\u8f9b\u6377\u8fd9\u4e00\u6251\u662f\u65bd\u4e86\u5341\u4e8c\u6210\u5168\u529b\uff0c\u666e\u5929\u4e4b\u4e0b\uff0c\u9664\u4e86\u4e16\u5916\u4e09\u4ed9\u53ea\u6015\u6ca1\u6709\u51e0\u4e2a\u4eba\u6562\u786c\u652b\u5176\u950b\uff01 \u3000\u3000\u6240\u6709\u7684\u4eba\u90fd\u4e3a\u8f9b\u6377\u8fd9\u4ee5\u201c\u52a8\u201d\u5e94\u4ed8\u9635\u6cd5\u7684\u4e3e\u52a8\uff0c\u5927\u5403\u4e00\u60ca\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u8f9b\u6377\u7684\u6885 \u9999\u5b9d\u5251\u5df2\u98de\u6d8c\u800c\u4e0b\u2014\u2014 \u3000\u3000\u90a3\u5bc6\u9640\u5b9d\u6811\u5927\u559d\u4e00\u58f0\uff0c\u77ed\u94f2\u5982\u621f\u800c\u7acb\uff0c\u4e00\u80a1\u53e4\u602a\u65e0\u540d\u7684\u52b2\u9053\u4ece\u94f2\u5c16\u5c04\u51fa\u3002 \u3000\u3000\u8f9b\u6377\u6597\u7136\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u624b\u4e2d\u201c\u7269\u6362\u661f\u79fb\u201d\u7684\u5f0f\u5b50\u6b63\u4f7f\u5f97\u53d8\u5316\u65e0\u65b9\uff0c\u4fbf\u662f\u5bc6\u9640\u5b9d\u6811\uff0c\u90a3\u621f \u7acb\u7684\u94f2\u4e4b\u95f4\u7684\u52b2\u529b\u76f4\u4ee4\u4ed6\u611f\u5230\u65e0\u5904\u4e0b\u624b\uff0c\u4f3c\u4e4e\u65e0\u8bba\u4ece\u54ea\u4e2a\u65b9\u4f4d\u6233\u4e0b\u90fd\u5c06\u906d\u5230\u963b\u6321\uff0c\u7b80\u76f4\u662f\u65e0 \u61c8\u53ef\u51fb\u7684\u6837\u5b50\u3002 \u3000\u3000\u8fd9\u662f\u4ed6\u65bd\u201c\u5927\u884d\u5341\u5f0f\u201d\u4ee5\u6765\u7b2c\u4e00\u6b21\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u4ed6\u53f3\u624b\u5251\u5f0f\u53d1\u4e0d\u51fa\u53bb\uff0c\u5de6\u638c\u6697\u85cf\u7684\u6740\u624b \u4e5f\u65bd\u4e0d\u51fa\u800c\u4ed6\u51b2\u6251\u7684\u5f0f\u5b50\u5df2\u5c06\u5c3d\u2014\u2014 \u3000\u3000\u4ed6\u5f00\u59cb\u611f\u5230\u4e00\u9635\u7126\u6025\u2014\u2014\u4ed6\u4e0d\u8be5\u4f4e\u4f30\u4e86\u8fd9\u77ee\u548c\u5c1a\u7684\u529f\u529b\u2014\u2014 \u3000\u3000\u5c31\u5728\u8fd9\u65f6\uff0c\u8eab\u540e\u91d1\u5668\u63a5\u51fb\uff0c\u663e\u7136\u5176\u4ed6\u7684\u4eba\u4e5f\u52a8\u4e86\u624b\uff0c\u8f9b\u6377\u731b\u89c9\u80cc\u4e0a\u52b2\u98ce\u6251\u6765\uff0c\u4ed6\u4e0d\u52a0\u601d \u7d22\u5730\u53cc\u8db3\u4e00\u8361\uff0c\u8eab\u5f62\u66b4\u957f\uff0c\u8f7b\u5de7\u5730\u8eb2\u5f00\u654c\u88ad\uff0c\u6b63\u5f85\u8bbe\u6cd5\u76d8\u65cb\u4e0b\u51fb\uff0c\u7a81\u7136\u817f\u4e0a\u53c8\u611f\u52b2\u98ce\uff0c\u4ed6\u77a7 \u90fd\u4e0d\u77a7\u5c31\u77e5\u662f\u91d1\u9c81\u5384\u7684\u957f\u7d22\u7f20\u4e86\u4e0a\u6765\uff0c\u4ed6\u5fc3\u4e2d\u5927\u9a87\uff0c\u60f3\u4e0d\u5230\u8fd9\u9635\u6cd5\u8865\u4f4d\u5982\u6b64\u4e4b\u5feb\u3002 \u3000\u3000\u4ed6\u4e0a\u5347\u4e4b\u52bf\u521a\u5c3d\uff0c\u6b63\u8981\u4e0b\u843d\u4e4b\u9645\uff0c\u91d1\u9c81\u5384\u7684\u957f\u7d22\u4e5f\u6b63\u597d\u626b\u5230\u2014\u2014\u8fd9\u6b63\u662f\u91d1\u9c81\u5384\u8fd9\u4e00\u62db\u7684 \u5999\u5904\uff0c\u773c\u770b\u8f9b\u6377\u5c31\u5f97\u906d\u6b83\uff0c\u5ffd\u89c1\u4ed6\u53cc\u8db3\u53c8\u662f\u594b\u529b\u4e00\u8e22\uff0c\u8eab\u4f53\u7adf\u786c\u786c\u4e0d\u501f\u5916\u529b\u5730\u62d4\u8d77\u4e09\u5c3a\uff0c\u547c \u7684\u4e00\u58f0\uff0c\u91d1\u9c81\u5384\u7684\u957f\u7d22\u64e6\u9774\u5e95\u98de\u4e86\u8fc7\u53bb\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u843d\u5730\uff0c\u5de6\u53f3\u4e24\u822c\u5175\u5203\u5df2\u81ea\u9012\u5230\uff0c\u4ed6\u594b\u529b\u4e24\u5251\u524a\u51fa\uff0c\u95ea\u8eab\u534a\u9000\uff0c\u54ea\u77e5\u624d\u52a8\u6b65\uff0c\u91d1\u9c81 \u5384\u7684\u957f\u7d22\u53c8\u70b9\u5230\u9876\u95e8\u3002 \u3000\u3000\u4ed6\u5fc3\u4e2d\u5927\u4e3a\u60ca\u9a87\uff0c\u60f3\u4e0d\u5230\u8fd9\u9635\u6cd5\u5982\u6b64\u4e4b\u5feb\uff0c\u4f7f\u90a3\u8865\u4f4d\u6362\u62db\u4e4b\u4f3c\u4e4e\u6839\u672c\u4e0d\u9700\u8981\u65f6\u95f4\uff0c\u5c31\u5982 \u540c\u4e00\u4e2a\u4eba\u751f\u4e86\u516b\u53ea\u624b\u81c2\u4e00\u822c\uff0c\u8fd0\u7528\u7075\u4fbf\u4e4b\u6781\u3002 \u3000\u3000\u8f9b\u6377\u5012\u62bd\u4e00\u53e3\u51c9\u6c14\uff0c\u6885\u9999\u5251\u5237\u5237\u4e00\u8fde\u653b\u51fa\u4e8c\u62db\uff0c\u659c\u773c\u4e00\u770b\uff0c\u5434\u51cc\u98ce\u3001\u91d1\u6b39\u53ca\u5b59\u501a\u91cd\u4f3c\u4e4e \u4e5f\u662f\u624b\u614c\u8db3\u4e71\u3002 \u3000\u3000\u5a46\u7f57\u4e94\u5947\u7684\u9635\u6cd5\u6108\u8f6c\u6108\u5feb\uff0c\u5b9b\u5982\u767e\u5341\u6837\u5175\u5203\u540c\u65f6\u98de\u52a8\u4e00\u6837\uff0c\u8865\u62db\u6362\u5f0f\u5374\u50cf\u4e00\u4e2a\u4eba\u52a8\u624b\u4e00 \u822c\uff0c\u4e1d\u6beb\u4e0d\u4e71\uff0c\u8f9b\u6377\u8fde\u6362\u4e24\u5957\u5251\u6cd5\uff0c\u4e00\u4e1d\u4e0a\u98ce\u4e5f\u62a2\u4e0d\u5230\u3002 \u3000\u3000\u5bc6\u9640\u5b9d\u6811\u6b63\u9762\u53d1\u52a8\u653b\u52bf\uff0c\u91d1\u9c81\u5384\u548c\u9752\u5c18\u7f57\u6c49\u4e00\u6761\u957f\u7d22\u5348\u67c4\u5b9d\u5251\u4ece\u5bf9\u9762\u8f85\u653b\uff0c\u52a0\u4e0a\u52a0\u5927\u5c14 \u53ca\u6e29\u6210\u767d\u7f57\u7684\u4ece\u65c1\u4fa7\u51fb\uff0c\u7aef\u7684\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff0c\u5176\u5feb\u65e0\u6bd4\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u5251\u4e0a\u538b\u529b\u6108\u6765\u6108\u5927\uff0c\u4ed6\u4e00\u8d4c\u6c14\uff0c\u529b\u8d2f\u5355\u81c2\uff0c\u4e00\u8fde\u523a\u51fa\u4e8e\u591a\u5251\uff0c\u4f46\u5341\u62db\u4e00\u8fb9\uff0c\u53cd \u800c\u6108\u89c9\u4e0d\u5bf9\uff0c\u5bf9\u65b9\u6bcf\u4e00\u62db\u90fd\u4ece\u56db\u4e94\u4e2a\u65b9\u4f4d\u9012\u8fdb\u6765\uff0c\u5b9e\u5728\u5e94\u63a5\u4e0d\u6687\u3002 \u3000\u3000\u4ed6\u4e00\u9762\u54ac\u7259\u62fc\u6597\uff0c\u4e00\u9762\u7aed\u529b\u82e6\u601d\uff0c\u4f46\u4ed6\u60f3\u4e0d\u51fa\u8fd9\u79cd\u53e4\u602a\u9635\u52bf\u7684\u95e8\u9053\u3002 \u3000\u3000\u5bc6\u9640\u5b9d\u6811\u5f53\u80f8\u4e00\u94f2\u5288\u4e0b\uff0c\u8f9b\u6377\u53f3\u8db3\u534a\u9000\uff0c\u5374\u78b0\u4e0a\u5434\u51cc\u98ce\u7684\u80cc\u4e0a\uff0c\u539f\u6765\u4ed6\u4eec\u88ab\u56f4\u5f97\u6108\u6765\u6108 \u7d27\uff0c\u672c\u6765\u4e08\u591a\u7684\u5706\u5708\uff0c\u8fd9\u65f6\u56db\u4eba\u51e0\u4e4e\u662f\u80cc\u9760\u80cc\u7684\u529b\u6218\u4e86\u3002 \u3000\u3000\u8f9b\u6377\u957f\u53f9\u4e00\u58f0\uff0c\u5251\u5f0f\u4e00\u7f13\uff0c\u201c\u62cd\u201d\u7684\u88ab\u91d1\u9c81\u5384\u957f\u7d22\u5c3e\u5df4\u5377\u53bb\u81c2\u4e0a\u8863\u8896\u4e00\u8882\uff01 \u3000\u3000\u201c\u64e6\u201d\u7684\u4e00\u58f0\uff0c\u5b59\u501a\u91cd\u624b\u4e2d\u957f\u5251\u88ab\u9752\u5c18\u7f57\u6c49\u524a\u53bb\u4e00\u622a\uff0c\u4ed6\u8ddf\u8dd1\u9000\u4e86\u4e24\u6b65\u6276\u7740\u5434\u51cc\u98ce\u624d\u7acb \u7a33\u8eab\u8eaf\uff0c\u90a3\u9752\u5c18\u7f57\u6c49\u624b\u4e2d\u5251\u5b50\u4f3c\u4e4e\u4e0d\u662f\u51e1\u54c1\u5462\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5de6\u6491\u53f3\u652f\uff0c\u6c57\u5982\u96e8\u4e0b\uff0c\u4ed6\u53ea\u89c9\u53f3\u81c2\u4e0a\u6108\u6765\u6108\u91cd\uff0c\u6e10\u6e10\u7684\u652f\u6301\u4e0d\u4f4f\u2014\u2014 \u3000\u3000\u96ea\u82b1\u505c\u6b62\u98de\u821e\uff0c\u5929\u7a7a\u5374\u7070\u5f97\u5f88\uff0c\u4e4c\u9e45\u5c71\u50cf\u4e00\u4e2a\u5de8\u4eba\u822c\u8038\u7acb\u7740\u2014\u2014 \u3000\u3000\u5a46\u7f57\u4e94\u5947\u7684\u9635\u6cd5\u6108\u7f29\u6108\u5c0f\uff0c\u6108\u8f6c\u6108\u5feb\uff0c\u8f9b\u6377\u51cc\u98ce\u90fd\u611f\u5230\u652f\u6301\u4e0d\u4f4f\uff0c\u5b59\u501a\u91cd\u957f\u5251\u65ad\u4e86\u4e00\u622a \u66f4\u662f\u6beb\u65e0\u6597\u5fd7\u2014\u2014 \u3000\u3000\u53ea\u6709\u5929\u9b54\u91d1\u6b39\u4ecd\u7136\u4e00\u58f0\u4e0d\u54cd\u5730\u72e0\u547d\u62fc\u6740\uff0c\u4e2d\u539f\u56db\u4eba\u4e2d\u4ee5\u4ed6\u4e34\u654c\u7ecf\u9a8c\u6700\u4e30\u5bcc\uff0c\u800c\u4e14\u4ed6\u4e3a\u4eba \u6700\u5f3a\u608d\uff0c\u4f55\u51b5\u4ed6\u6b64\u65f6\u6839\u672c\u53ae\u6740\u5f97\u6709\u70b9\u8fd1\u4e8e\u53d1\u731b\uff0c\u4ed6\u4e1d\u6beb\u6ca1\u6709\u53d7\u5230\u5176\u4ed6\u4eba\u6c14\u9981\u5f71\u54cd\uff0c\u201c\u767e\u8db3 \u5251\u201d\u4e2d\u6bd2\u8fa3\u7684\u62db\u5f0f\u5c42\u51fa\u4e0d\u7a77\uff0c\u886c\u7740\u4ed6\u90a3\u526f\u4e11\u6076\u7684\u5c0a\u5bb9\uff0c\u771f\u662f\u6709\u5982\u9b3c\u9b41\u3002\u53ea\u89c1\u4ed6\u5de6\u4e00\u5251\u523a\u51fa\u4e00 \u534a\uff0c\u6597\u7136\u62bd\u56de\u5411\u53f3\u523a\u53bb\uff0c\u800c\u5de6\u9762\u7684\u6e29\u6210\u767d\u7f57\u4e5f\u6b63\u5728\u4e00\u5251\u5f80\u4ed6\u80a9\u4e0a\u523a\u5230\uff0c\u4ed6\u82e5\u6551\u6025\u5c31\u987e\u4e0d\u5f97\u4f24 \u654c\u2014\u2014 \u3000\u3000\u54ea\u77e5\u4ed6\u7709\u76ee\u4e00\u6380\uff0c\u6beb\u4e0d\u7406\u4f1a\u5730\u4ecd\u7136\u4e00\u5251\u523a\u51fa\uff0c\u6ce2\u4e00\u58f0\uff0c\u52a0\u5927\u5c14\u7684\u817f\u4e0a\u88ab\u4ed6\u523a\u4e86\u4e00\u5251\uff0c\u800c \u6e29\u6210\u767d\u7f57\u7684\u957f\u5251\u4e5f\u5728\u4ed6\u5de6\u80a9\u4e0a\u5212\u5f00\u5bf8\u6df1\u7684\u53e3\u5b50\u3002 \u3000\u3000\u4ed6\u7709\u90fd\u4e0d\u76b1\u5730\u53cd\u624b\u523a\u51fa\uff0c\u5bc6\u9640\u5b9d\u6811\u4e00\u95ea\u800c\u8fc7\uff0c\u4e58\u52bf\u4e00\u94f2\u76d6\u5411\u8f9b\u6377\u2014\u2014 \u3000\u3000\u8f9b\u6377\u4f3c\u4e4e\u5fc3\u4e0d\u5728\u7109\u5730\u968f\u624b\u4e00\u6321\uff0c\u5ffd\u7136\u91d1\u6b39\u6012\u559d\u9053\uff1a\u201c\u59d3\u8f9b\u7684\uff0c\u8fd9\u4e48\u6ca1\u79cd\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u9661\u7136\u5982\u96f7\u8f70\u9876\uff0c\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u4ed6\u7684\u6885\u9999\u5b9d\u5251\u88ab\u5bc6\u9640\u5b9d\u6811\u9707\u5f00\u4e09\u5c3a\uff0c\u9669\u4e9b\u8131\u624b\u98de \u51fa\uff0c\u4ed6\u731b\u63d0\u4e00\u53e3\u771f\u6c14\uff0c\u8eab\u5f62\u5de6\u626d\u53f3\u65cb\uff0c\u53cc\u8db3\u867d\u7136\u4e0d\u66fe\u79fb\u52a8\u5206\u6beb\uff0c\u4f46\u5374\u5de7\u5999\u5730\u95ea\u5f00\u4e00\u94f2\u4e00\u5251\uff0c \u4e0d\u6d88\u8bf4\uff0c\u4ed6\u65bd\u51fa\u4e86\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\u4e2d\u7684\u8eab\u6cd5\u3002 \u3000\u3000\u4ed6\u8f6c\u773c\u4e00\u770b\uff0c\u91d1\u6b39\u80a9\u4e0a\u9c9c\u8840\u957f\u6d41\uff0c\u51cc\u98ce\u5927\u6c57\u6dcb\u6f13\uff0c\u90a3\u5b59\u501a\u91cd\u5374\u6325\u7740\u4e00\u53ea\u65ad\u5251\u795e\u4e0d\u5b88\u820d\uff0c \u6597\u5fd7\u5168\u6d88\uff0c\u4ed6\u53f3\u624b\u201c\u51b7\u6885\u62c2\u9762\u201d\uff0c\u53f3\u638c\u201c\u4e07\u6cc9\u98de\u7a7a\u201d\uff0c\u903c\u5f00\u6e29\u6210\u767d\u7f57\u7684\u4e24\u5251\uff0c\u5927\u559d\u9053\uff1a\u201c\u5f53 \u5e74\u5c11\u6797\u7b2c\u4e03\u4ee3\u65b9\u4e08\u6167\u56e0\u5927\u5e08\uff0c\u5728\u7ec8\u5357\u5c71\u9876\u7528\u2018\u5e03\u8fbe\u4e09\u5f0f\u2019 \u3000\u3000\u8fde\u4f24\u6cb3\u6d1b\u4e8c\u5341\u4e00\u540d\u5251\u5ba2\uff0c\u90a3\u662f\u4f55\u7b49\u5a01\u98ce\uff0c\u59d3\u5b59\u7684\u5c31\u662f\u7b49\u4e0d\u6d4e\u4e48\uff1f\u201d \u3000\u3000\u5b59\u501a\u91cd\u4e00\u95f4\u6b64\u8bed\uff0c\u53ea\u89c9\u5b9b\u5982\u88ab\u4e07\u65a4\u9524\u6572\u4e86\u4e00\u8ba9\uff0c\u4ed6\u594b\u7136\u957f\u5578\uff0c\u81ea\u8a00\u9053\uff1a\u201c\u6211\u5b59\u501a\u91cd\u5343\u4e07 \u83ab\u8981\u6298\u4e86\u5c11\u6797\u5a01\u98ce\u3002\u201d \u3000\u3000\u8155\u4e0a\u52a0\u52b2\uff0c\u4e00\u652f\u65ad\u5251\u5982\u98de\u95ea\u52a8\uff0c\u663e\u7136\u4ed6\u5df1\u65bd\u51fa\u201c\u5927\u884d\u5341\u5f0f\u201d\u7684\u7edd\u62db\uff01 \u3000\u3000\u8f9b\u6377\u594b\u529b\u524a\u51fa\u4e24\u5251\uff0c\u811a\u4e0b\u4e00\u53d8\uff0c\u5de6\u811a\u5c16\u9489\u7acb\u5730\u9762\uff0c\u53f3\u811a\u6a2a\u626b\u51fa\u534a\u4e2a\u5706\u5f27\uff0c\u624b\u4e2d\u5b9d\u5251\u8fde\u88ad \u4e09\u4eba\u3002\u54ea\u77e5\u4ed6\u4e00\u53cd\u653b\uff0c\u7a81\u7136\u773c\u524d\u4e00\u82b1\uff0c\u4e00\u4e0b\u5b50\u56db\u4e94\u4ef6\u5175\u5203\u4e00\u9f50\u5230\u4e86\u773c\u524d\uff0c\u4ed6\u9a87\u5f97\u5012\u9000\u4e24\u6b65\uff0c \u624b\u4e2d\u8fde\u65bd\u51fa\u5927\u884d\u5341\u5f0f\u4e2d\u7684\u201c\u7269\u6362\u661f\u79fb\u201d\uff0c\u201c\u95f2\u4e91\u6f6d\u5f71\u201d\u624d\u52c9\u5f3a\u5316\u5f00\u3002\u4f46\u95fb\u5bc6\u9640\u5b9d\u6811\u5927\u58f0\u9053\uff1a \u201c\u5580\u6298\u5df4\u7f57\uff0c\u5e45\u6210\u82e6\u57fa\u6469\u7236\uff01\u201d \u3000\u3000\u4ed6\u559d\u58f0\u65b9\u6b47\uff0c\u5a46\u7f57\u4e94\u5947\u7684\u9635\u52bf\u9661\u7136\u5927\u53d8\uff0c\u4e94\u4e2a\u4eba\u6709\u5982\u8d70\u9a6c\u706f\u822c\u8f6c\u4e86\u8d77\u6765\uff0c\u672c\u6765\u4ed6\u4eec\u914d\u5408 \u5f97\u5df2\u662f\u5341\u5206\u8fc5\u901f\uff0c\u4f46\u6b64\u523b\u7adf\u53c8\u589e\u5feb\u500d\u4f59\uff0c\u7b80\u76f4\u662f\u4e94\u56e2\u7070\u5f71\u65cb\u6765\u65cb\u53bb\uff0c\u6bcf\u4e00\u62db\u90fd\u50cf\u662f\u4e94\u4ef6\u5175\u5203 \u540c\u65f6\u8fbe\u5230\u4e00\u822c\uff0c\u58f0\u52bf\u9a87\u4eba\uff01 \u3000\u3000\u4f46\u662f\u5bc6\u9640\u5b9d\u6811\u8fd9\u4e00\u53e5\u68b5\u8bed\u5374\u4ee4\u8f9b\u6377\u9661\u7136\u5927\u609f\uff0c\u4ed6\u5927\u53eb\u9053\uff1a\u201c\u5927\u54e5\uff0c\u54b1\u4eec\u4ee5\u5feb\u6253\u5feb\uff01\u201d \u3000\u3000\u5434\u51cc\u98ce\u4e5f\u662f\u604d\u7136\u800c\u609f\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u5c55\u5f00\u5929\u5170\u5bc6\u7b08\u5b66\u6765\u7684\u8f7b\u529f\u98de\u8f6c\u800c\u51fa\uff01 \u3000\u3000\u5f53\u65e5\u5e73\u51e1\u4e0a\u4eba\u66fe\u8bf4\u5929\u7afa\u8f7b\u529f\u5fc5\u7136\u53e6\u6709\u4e00\u6869\u5999\u7528\uff0c\u6562\u60c5\u6b63\u662f\u8fd9\u9635\u6cd5\u3002 \u3000\u3000\u53ea\u89c1\u9635\u4e2d\u4e24\u9053\u767d\u8679\u4e00\u531d\u4e00\u76d8\uff0c\u8f9b\u5434\u4e24\u4eba\u540c\u65f6\u5c55\u5f00\u5929\u4e9b\u8f7b\u529f\u7adf\u548c\u5a46\u7f57\u4e94\u5947\u62a2\u5feb\u8d77\u6765\u3002\u4e24\u4eba \u8f7b\u529f\u8eab\u6cd5\u4e0e\u5a46\u7f57\u4e94\u5947\u5982\u540c\u4e00\u8f99\uff0c\u970e\u65f6\u5c31\u6df7\u4eba\u4e94\u5947\u9635\u4e2d\uff0c\u4e94\u4eba\u7684\u9635\u5f0f\u987f\u65f6\u6210\u4e86\u4e03\u4eba\uff0c\u4f7f\u5f97\u5a46\u7f57 \u4e94\u5947\u4e0d\u77e5\u653b\u597d\u8fd8\u662f\u5b88\u597d\uff01 \u3000\u3000\u91d1\u9c81\u5384\u5927\u5403\u4e00\u60ca\uff0c\u5fc3\u9053\uff1a\u201c\u600e\u4e48\u8fd9\u4e24\u4e2a\u5c0f\u5b50\u7adf\u8bc6\u5f97\u54b1\u4eec\u7684\u8f7b\u529f\u7edd\u6280\uff1f\u201d \u3000\u3000\u4ed6\u518d\u770b\u4e00\u4e0b\uff0c\u66f4\u662f\u60ca\u6012\u4ea4\u52a0\uff0c\u539f\u6765\u8f9b\u5434\u4e24\u4eba\u4e0d\u4ec5\u6b65\u6cd5\u8eab\u6cd5\u65bd\u5f97\u548c\u4ed6\u4eec\u4e94\u4eba\u4e1d\u6beb\u4e0d\u5dee\uff0c\u751a \u81f3\u6709\u4e9b\u5947\u5999\u7684\u59ff\u52bf\u8fde\u81ea\u5df1\u90fd\u4e0d\u66fe\u89c1\u8fc7\u3002\u4ed6\u5927\u53eb\u4e00\u58f0\uff1a\u201c\u54af\u52d2\u6c34\u5e73\uff0c\u91d1\u5409\u2026\u2026\u201d \u3000\u3000\u7cbe\u4eae\u8679\u5149\u4e00\u95ea\uff0c\u8f9b\u6377\u548c\u5434\u51cc\u98ce\u5df2\u662f\u8eab\u5251\u5408\u4e00\u51b2\u5929\u800c\u51fa\uff0c\u6f47\u6d12\u5730\u843d\u5728\u9635\u5916\uff01 \u3000\u3000\u5a46\u7f57\u4e94\u5947\u60ca\u5f97\u9762\u9762\u76f8\u89d1\uff0c\u4f5c\u58f0\u4e0d\u5f97\u3002 \u3000\u3000\u5bc6\u9640\u5b9d\u6811\u7a81\u7136\u66b4\u53eb\u4e00\u58f0\uff1a\u201c\u51c6\u5df4\u65af\uff0c\u4ee4\u65af\u4e5f\u5c14\uff01\u201d \u3000\u3000\u8f9b\u6377\u867d\u4e0d\u61c2\u68b5\u8bed\uff0c\u4f46\u4e5f\u77e5\u9053\u4ed6\u662f\u8bf4\uff1a\u201c\u5c0f\u5b50\uff0c\u518d\u63a5\u6211\u4e00\u62db\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u60f3\uff1a\u201c\u4f60\u529f\u529b\u91cf\u9ad8\uff0c\u6211\u5c31\u6015\u4e86\u4f60\u4e48\uff1f\u5f53\u65e5\u91d1\u4f2f\u80dc\u4f5b\u7684\u4e00\u638c\u6211\u8fd8\u4e0d\u662f\u7167\u63a5\u4e86\u2014\u2014\u201d \u3000\u3000\u53ea\u89c1\u5bc6\u9640\u5b9d\u6811\u53d1\u987b\u4ff1\u5f20\u5730\u4e00\u6251\u800c\u81f3\uff0c\u5c0f\u94f2\u5df2\u63d2\u5728\u8170\u95f4\uff0c\u53cc\u638c\u5408\u4ec0\u4e00\u62cd\u4e00\u64e6\uff0c\u7acb\u5373\u6536\u56de\u3002 \u3000\u3000\u8f9b\u6377\u53ea\u89c9\u4e00\u80a1\u9634\u98ce\u8fce\u9762\u800c\u81f3\uff0c\u90a3\u9634\u98ce\u597d\u4e0d\u53e4\u602a\uff0c\u867d\u4e0d\u51cc\u5389\uff0c\u4f46\u5374\u6709\u4e00\u80a1\u529b\u9053\u76f4\u5982\u4e07\u65a4\u4e4b \u529b\u63a8\u5f97\u4ed6\u7acb\u8db3\u4e0d\u7a33\uff0c\u800c\u4e14\u9634\u98ce\u88ad\u5904\uff0c\u51b7\u5bd2\u523a\u9aa8\uff01 \u3000\u3000\u4ed6\u867d\u4e0d\u77e5\u8fd9\u4e43\u662f\u5bc6\u9640\u5b9d\u6811\u82e6\u7ec3\u6570\u5341\u5e74\u7684\u897f\u57df\u7edd\u6280\u201c\u767d\u9a7c\u5bd2\u5fc3\u638c\u201d\uff0c\u4f46\u4ed6\u77e5\u9053\u53ea\u8981\u81ea\u5df1\u53cc \u8db3\u52a8\u4e86\u534a\u5bf8\uff0c\u90a3\u5c31\u7b97\u662f\u8d25\u843d\u3002\u4f46\u4ed6\u5374\u65e0\u6cd5\u786c\u62fc\u51fa\u638c\uff0c\u56e0\u4e3a\u5bc6\u9640\u5b9d\u6811\u529f\u529b\u4e4b\u6df1\uff0c\u72b9\u5728\u4ed6\u4e4b\u4e0a\uff01 \u3000\u3000\u6240\u6709\u7684\u4eba\u90fd\u6ce8\u89c6\u7740\u8fd9\u4e00\u638c\uff0c\u53ea\u89c1\u8f9b\u6377\u53cc\u811a\u7262\u9489\u5730\u9762\uff0c\u6885\u9999\u5b9d\u5251\u201c\u5636\u201d\u7684\u4e00\u58f0\u51fb\u51fa\u4e00\u4e2a\u534a \u5706\uff0c\u4ed6\u5168\u8eab\u8863\u886b\u90fd\u88ab\u771f\u6c14\u9f13\u8d77\uff0c\u6709\u5982\u4ece\u5185\u704c\u9971\u4e86\u7a7a\u6c14\u4e00\u822c\u3002 \u3000\u3000\u83ab\u770b\u4ed6\u8fd9\u5251\u5c16\u53ea\u8f7b\u8f7b\u753b\u4e2a\u534a\u5706\uff1b\u4ed6\u8d77\u624b\u65f6\u4e43\u662f\u201c\u5927\u884d\u5341\u5f0f\u201d\u4e2d\u7684\u201c\u65b9\u751f\u4e0d\u606f\u201d\uff0c\u65bd\u4e86\u4e00 \u534a\u53c8\u6362\u4e3a\u201c\u7269\u6362\u661f\u79fb\u201d\uff0c\u4e0d\u5f85\u65bd\u5168\uff0c\u4e00\u6536\u4e4b\u4e0b\u5df2\u6210\u4e86\u534a\u62db\u201c\u51b7\u6885\u62c2\u9762\u201d\uff0c\u4e09\u4e2a\u76d6\u4e16\u540d\u62db\u7684\u4e00 \u534a\uff0c\u914d\u5408\u5f97\u5929\u8863\u65e0\u7f1d\uff01 \u3000\u3000\u5728\u573a\u6bcf\u4e2a\u4eba\u90fd\u662f\u5251\u6cd5\u540d\u5bb6\uff0c\u770b\u5230\u8f9b\u6377\u8fd9\u7b49\u7cbe\u7edd\u5251\u672f\u5374\u4e0d\u7981\u6697\u6697\u8d5e\u53f9\uff01 \u3000\u3000\u53ea\u542c\u5230\u547c\u7684\u4e00\u58f0\uff0c\u5bc6\u9640\u5b9d\u6811\u53d1\u51fa\u7684\u72c2\u98ce\u4ece\u8f9b\u6377\u5251\u5c16\u753b\u52a8\u7684\u534a\u5706\u4e24\u65c1\u6d8c\u4e86\u8fc7\u53bb\uff0c\u56e2\u56e2\u98de\u6c99 \u5251\u77f3\uff0c\u534a\u5706\u4e2d\u7684\u8f9b\u6377\u5374\u662f\u7709\u53d1\u4e0d\u4e3e\uff01 \u3000\u3000\u5bc6\u9640\u5b9d\u6811\u5446\u4e86\u4e00\u5446\uff0c\u5ffd\u7136\u4ef0\u5929\u957f\u7b11\uff0c\u4e00\u6446\u624b\uff0c\u5e26\u7740\u5e08\u5f1f\u4eec\u5012\u8dc3\u800c\u51fa\uff0c\u4e0d\u6d88\u51e0\u4e2a\u8d77\u843d\uff0c\u8eab \u5f62\u5df2\u81ea\u6d88\u5931\u3002 \u3000\u3000\u8f9b\u6377\u81ea\u7136\u5730\u4e00\u6296\u957f\u5251\uff0c\u201c\u55e1\u201d\u7684\u4e00\u58f0\uff0c\u6885\u9999\u5b9d\u5251\u53d1\u51fa\u9f99\u541f\u5c3a\u7684\u58f0\u54cd\uff0c\u4ed6\u53cd\u624b\u63d2\u5165\u9798\u4e2d\uff0c \u832b\u7136\u5446\u4e86\u534a\u664c\u624d\u8f6c\u8eab\u56de\u89c6\u3002 \u3000\u3000\u53ea\u89c1\u90a3\u5929\u9b54\u91d1\u6b39\u53c8\u72ec\u4e2a\u513f\u5310\u5728\u5730\u4e0a\u7b49\u4ed6\u90a3\u6761\u201c\u91d1\u820c\u513f\u201d\u4e86\u3002\u51cc\u98ce\u8eab\u65c1\u5374\u4e0d\u89c1\u4e86\u5b59\u501a\u91cd\u3002 \u3000\u3000\u6b63\u5947\u95f4\uff0c\u4e00\u53ea\u67d4\u8351\u7684\u624b\u629a\u5728\u4ed6\u7684\u80a9\u4e0a\uff0c\u4e00\u4e2a\u53ef\u7231\u800c\u719f\u6089\u7684\u58f0\u97f3\uff1a\u201c\u8f9b\u5927\u54e5\uff0c\u4f60\u7684\u5251\u672f\u771f \u4e86\u4e0d\u8d77\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u7684\u5fc3\u5dee\u70b9\u513f\u8df3\u51fa\u53e3\u8154\uff0c\u4ed6\u731b\u7136\u56de\u5934\uff0c\u6620\u5165\u773c\u5e18\u7684\u4e00\u5f20\u7eaf\u771f\u7f8e\u4e3d\u7684\u5c0f\u8138\uff0c\u4e0d\u662f\u5f20\u83c1\u662f \u8c01\uff1f \u3000\u3000\u4ed6\u9669\u4e9b\u9ad8\u5174\u5f97\u62e5\u62b1\u4e0a\u53bb\uff0c\u4e00\u628a\u6293\u4f4f\u5979\u7684\u5c0f\u624b\uff0c\u4e00\u65f6\u56db\u76ee\u76f8\u89c6\uff0c\u518d\u4e5f\u5206\u4e0d\u5f00\u6765\u3002 \u3000\u3000\u534a\u54cd\u624d\u60ca\u89c9\u83c1\u513f\u80cc\u540e\u8fd8\u7ad9\u7740\u4e24\u4eba\uff0c\u4e00\u4e2a\u67af\u7626\u9ad8\u957f\u7684\u8001\u548c\u5c1a\uff0c\u53e6\u4e00\u4e2a\u6b63\u662f\u5b59\u501a\u91cd\u3002 \u3000\u3000\u90a3\u8001\u50e7\u8f9b\u6377\u751a\u89c9\u773c\u719f\uff0c\u4ed4\u7ec6\u4e00\u60f3\uff0c\u731b\u7136\u60f3\u8d77\u6b63\u662f\u5c0f\u6222\u5c9b\u4e0a\u5524\u8d70\u5e73\u51e1\u4e0a\u4eba\u7684\u9a91\u9e64\u8001\u50e7\u3002 \u3000\u3000\u4ed6\u8fd8\u6ca1\u6709\u5f00\u53e3\uff0c\u83c1\u513f\u5df2\u4e0d\u505c\u5730\u9053\uff1a\u201c\u5927\u54e5\uff0c\u90a3\u5929\u6211\u5434\u5927\u54e5\u5206\u624b\u540e\uff0c\u7adf\u88ab\u4e00\u4e2a\u4e11\u8001\u9b3c\u6293 \u4f4f\uff0c\u6211\u6253\u4e0d\u8fc7\u4ed6\uff0c\u88ab\u4ed6\u70b9\u4e86\u7a74\uff0c\u540e\u6765\u6211\u800d\u9b3c\u8ba1\u9003\u8d70\u4e24\u6b21\uff0c\u53ef\u662f\u90fd\u88ab\u4ed6\u6349\u56de\u8f9b\u6377\u6b63\u8981\u63d2\u53e3\u76f8 \u95f4\uff0c\u83c1\u513f\u63a5\u7740\u9053\uff1a\u201c\u90a3\u8001\u9b3c\u8bf4\u4ed6\u662f\u7389\u9aa8\u9b54\u7684\u5e08\u5f1f\uff0c\u4ed6\u8bf4\u7239\u7239\u5bb3\u6b7b\u4ed6\u7684\u5e08\u5144\uff0c\u4ed6\u8981\u628a\u6211\u6293\u4f4f\uff0c \u903c\u7239\u7239\u5c31\u8303\uff0c\u54fc\uff0c\u4ed6\u7adf\u7528\u4e00\u79cd\u53e4\u602a\u624b\u6cd5\u70b9\u4e86\u6211\u4e09\u5341\u516d\u5927\u7a74\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u60ca\u5f97\u5475\u4e86\u4e00\u58f0\uff0c\u83c1\u513f\u4ecd\u4e0d\u505c\u5730\u9053\uff1a\u201c\u540e\u6765\u5e78\u597d\u8fd9\u548c\u5c1a\u4f2f\u4f2f\u78b0\u5230\u54b1\u4eec\uff0c\u4ed6\u4ece\u6211\u9888\u4e0a\u7389 \u94fe\u513f\u8ba4\u51fa\u662f\u7239\u7239\u7684\u4e1c\u897f\uff0c\u4ed6\u8bf4\u548c\u6211\u7239\u7239\u6709\u4e00\u9762\u4e4b\u7f18\uff0c\u5c31\u8981\u90a3\u8001\u9b3c\u653e\u4eba\uff0c\u90a3\u8001\u9b3c\u9a84\u50b2\u5f97\u5f88\uff0c\u8fd8 \u53eb\u8001\u5c1a\u4f2f\u4f2f\u4e0d\u8981\u591a\u7ba1\u95f2\u4e8b\uff0c\u5426\u5219\u5c31\u8981\u6740\u6b7b\u548c\u5c1a\u4f2f\u4f2f\u2014\u2014\u201d \u3000\u3000\u201c\u54fc\uff0c\u540e\u6765\u548c\u5c1a\u4f2f\u4f2f\u9732\u4e86\u4e00\u624b\u7edd\u9876\u6c14\u529f\uff0c\u628a\u90a3\u8001\u9b3c\u6253\u4f24\u5413\u8dd1\u4e86\uff0c\u5927\u54e5\uff0c\u548c\u5c1a\u4f2f\u4f2f\u7684\u6b66\u529f \u771f\u9ad8\uff0c\u6050\u6015\u548c\u7239\u7239\u90fd\u5dee\u4e0d\u591a\u54e9\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u6025\u9053\uff1a\u201c\u4f60\u88ab\u70b9\u4e86\u4e09\u5341\u516d\u5927\u7a74\uff0c\u540e\u6765\u600e\u4e48\u5566\uff1f\u201d \u3000\u3000\u83c1\u513f\u9053\uff1a\u201c\u548c\u5c1a\u4f2f\u4f2f\u628a\u6211\u5e26\u5230\u8fd9\u77f3\u6d1e\u4e2d\uff0c\u53eb\u8fd9\u4f4d\u2014\u2014\u8fd9\u4f4d\u5b59\u5927\u54e5\u5b88\u5728\u6d1e\u53e3\uff0c\u8bf4\u8c01\u90fd\u4e0d\u51c6 \u8fdb\u53bb\uff0c\u4ed6\u7528\u4e0a\u4e58\u5185\u529f\u66ff\u6211\u6253\u901a\u7a74\u8109\uff0c\u662f\u53d7\u4e0d\u5f97\u6253\u6270\u7684\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u60f3\u8d77\u5b59\u501a\u91cd\u4e0d\u51c6\u81ea\u5df1\u8fdb\u6d1e\u9669\u4e9b\u95f9\u7ffb\u7684\u60c5\u5f62\uff0c\u4e0d\u7981\u6b49\u7136\u3002 \u3000\u3000\u83c1\u513f\u54ad\u54ad\u5471\u5471\u5730\u8bc9\u8bf4\uff0c\u58f0\u97f3\u597d\u6bd4\u9ec4\u83ba\u51fa\u5c71\u8c37\u795e\u60c5\u53ef\u7231\u4e4b\u6781\uff0c\u8f9b\u6377\u4e0d\u7981\u770b\u5446\u4e86\u3002 \u3000\u3000\u5ffd\u7136\u90a3\u65c1\u4f20\u6765\u5b59\u501a\u91cd\u7684\u58f0\u97f3\uff1a\u201c\u7956\u5e08\u7237\uff0c\u60a8\u5343\u4e07\u8981\u56de\u53bb\u2014\u2014\u5f1f\u5b50\u597d\u4e0d\u5bb9\u6613\u5bfb\u7740\u60a8\uff0c\u60a8\u4e00 \u5b9a\u8981\u56de\u53bb\u3002\u201d \u3000\u3000\u8f9b\u6377\u5947\u602a\u5730\u4e00\u770b\uff0c\u53ea\u89c9\u5b59\u501a\u91cd\u8dea\u5728\u548c\u5c1a\u9762\u524d\u82e6\u6c42\uff0c\u90a3\u7626\u548c\u5c1a\u5374\u5fae\u7b11\u6447\u5934\uff0c\u8f9b\u6377\u604d\u7136\u5927 \u609f\uff0c\u8131\u53e3\u53eb\u9053\uff1a\u3000\u3000\u3000\u201c\u524d\u8f88\uff0c\u524d\u8f88\u53ef\u5c31\u662f\u5c11\u6797\u8001\u65b9\u4e08\u7075\u955c\u5927\u5e08\u2014\u2014\u201d \u3000\u3000\u8001\u50e7\u54c8\u54c8\u957f\u7b11\u6253\u65ad\u8f9b\u6377\u7684\u8bdd\uff0c\u5bf9\u7a7a\u957f\u5578\u4e00\u58f0\uff0c\u4e0d\u4e00\u4f1a\uff0c\u4e00\u53ea\u5de8\u5927\u65e0\u6bd4\u7684\u767d\u9e64\u964d\u843d\u811a\u524d\u3002 \u3000\u3000\u8f9b\u6377\u9661\u7136\u8bb0\u8d77\u4e00\u6869\u4e8b\uff0c\u5bf9\u7740\u83c1\u513f\u9053\uff1a\u201c\u54b1\u4eec\u8d76\u5feb\u5230\u6e56\u5357\u53bb\u627e\u4f60\u7239\u7239\uff0c\u524d\u4e09\u5929\u6211\u542c\u6c5f\u6e56\u4f20 \u8bf4\uff0c\u8bf4\u4ed6\u4e3a\u4e86\u627e\u4f60\u5728\u6c5f\u6e56\u4e0a\u4e71\u95ef\u4e71\u649e\uff0c\u53ea\u6015\u8981\u60f9\u51fa\u5927\u98ce\u6ce2\u5462\u2014\u2014\u201d \u3000\u3000\u83c1\u513f\u559c\u9053\uff1a\u201c\u7239\u7239\u4e5f\u6765\u627e\u6211\u5566\uff1f\u54a6\uff0c\u4f60\u770b\uff01\u201d \u3000\u3000\u8f9b\u6377\u5411\u524d\u4e00\u770b\uff0c\u987f\u65f6\u5927\u5403\u4e00\u60ca\uff0c\u539f\u6765\u51cc\u98ce\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u6084\u6084\u8d70\u4e86\uff0c\u4ed6\u51dd\u76ee\u8fdc\u89c6\uff0c\u53ea\u89c1\u5730 \u5e73\u7ebf\u5904\u53ea\u5269\u4e00\u4e2a\u6781\u5c0f\u7684\u9ed1\u5f71\uff0c\u4ed6\u4e00\u6025\u4e4b\u4e0b\u5c31\u60f3\u8ffd\u4e0a\u53bb\uff0c\u90a3\u8001\u50e7\u5ffd\u7136\u53eb\u9053\uff1a\u201c\u5a03\u513f\uff0c\u83ab\u8ffd\u4e86\uff0c \u8ba9\u4ed6\u53bb\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u4e00\u6014\uff0c\u6b62\u4f4f\u811a\u6b65\uff0c\u518d\u56de\u5934\u770b\u65f6\uff0c\u51cc\u98ce\u7684\u5f71\u5b50\u5df1\u81ea\u6d88\u5931\uff0c\u4ed6\u60f3\u5230\u51cc\u98ce\u4e0d\u501f\u5343\u91cc\u5954\u6ce2\u5730 \u4e3a\u81ea\u5df1\u5bfb\u627e\u83c1\u513f\uff0c\u8fd9\u65f6\u5374\u4e0d\u77e5\u4e3a\u4f55\u6084\u6084\u79bb\u53bb\uff0c\u60f3\u5230\u8fd9\u91cc\u4e0d\u89c9\u70ed\u8840\u4e0a\u6d8c\u90a3\u8001\u50e7\uff08\u7075\u955c\u5927\u5e08\uff09\u5531 \u7136\u53f9\u9053\uff1a \u3000\u3000\u201c\u6b64\u5b50\u5929\u8d44\u6781\u4f73\uff0c\u4fca\u7f8e\u7edd\u4e16\uff0c\u5374\u662f\u547d\u8fd0\u591a\u5384\uff0c\u7ec8\u662f\u4f5b\u95e8\u4e2d\u4eba\uff0c\u4ee5\u4ed6\u5929\u59ff\u7cbe\u7814\u4f5b\u7406\uff0c\u4ed6\u65e5 \u5fc5\u662f\u4e00\u4ee3\u9ad8\u50e7\uff0c\u4f60\u4eec\u8ba9\u4ed6\u53bb\u5427\uff01\u201d \u3000\u3000\u8f9b\u6377\u542c\u5f97\u4f3c\u61c2\u975e\u61c2\uff0c\u548c\u83c1\u513f\u60ca\u5947\u5730\u5bf9\u89c6\u4e00\u773c\u2014\u2014 \u3000\u3000\u7075\u955c\u5927\u5e08\u4ef0\u5929\u957f\u5578\uff0c\u98d8\u8eab\u8de8\u4e0a\u5927\u9e64\uff0c\u90a3\u9e64\u513f\u957f\u9e23\u4e00\u58f0\uff0c\u5982\u98de\u800c\u53bb\u2014\u2014 \u3000\u3000\u8f9b\u6377\u6fc0\u52a8\u5730\u671b\u7740\u5730\u5e73\u7ebf\u4e0a\uff0c\u4ed6\u4e0d\u660e\u767d\uff0c\u4e3a\u4ec0\u4e48\u51cc\u98ce\u4f1a\u6084\u7136\u800c\u53bb\uff1f\u6709\u4ec0\u4e48\u4e0d\u5e78\u53d1\u751f\u4e86\u4e48\uff1f \u3000\u3000\u8033\u65c1\u4f20\u6765\u83c1\u513f\u751c\u8106\u7684\u58f0\u97f3\uff1a\u201c\u5434\u5927\u54e5\u4e5f\u8bb8\u6709\u522b\u7684\u4e8b\u5427\uff0c\u4ed6\u6b66\u529f\u6781\u9ad8\uff0c\u6211\u4eec\u522b\u7a7a\u62c5\u5fc3\u5566\u201d \u3000\u3000\u8f9b\u6377\u611f\u5230\u4e00\u79cd\u76f4\u89c9\u544a\u8bc9\u4ed6\uff1b\u5434\u5927\u54e5\u8fd9\u4e00\u53bb\u4f3c\u4e4e\u8981\u6c38\u522b\u4e86\uff0c\u4ed6\u542c\u5230\u83c1\u513f\u7684\u8bdd\uff0c\u4f46\u4ed6\u6ca1\u6709\u51fa \u58f0\uff0c\u53ea\u662f\u4ece\u5fc3\u5e95\u91cc\u6697\u6697\u5730\u9053\uff1a\u201c\u4f46\u613f\u2014\u2014\u4f46\u613f\u4eba\u957f\u4e45\uff0c\u5343\u91cc\u5171\u8749\u5a1f\u2026\u2026\u201d \u3000\u3000\u6b63\u662f\uff1a\u864e\u8dc3\u9f99\u817e\u9ec4\u9ec4\u65e5\uff0c\u9e64\u9e23\u4e00\u58f0\u6ee1\u6e58\u53bb\u3002 \u3000\u3000\u9633\u6625\u4e09\u6708\uff0c\u91ce\u82b1\u9001\u9999\u2014\u2014 \u3000\u3000\u6c49\u9633\u57ce\u5916\u9f9f\u5c71\u4e0b\uff0c\u897f\u6708\u6e56\u7554\uff0c\u5e7d\u8513\u7fe0\u7af9\u4e4b\u4e2d\uff0c\u9690\u85cf\u7740\u4e00\u6240\u5bfa\u5e99\uff0c\u5e99\u95e8\u6a2a\u989d\u4e0a\u56db\u4e2a\u6597\u5927\u7684 \u5b57\uff1a\u201c\u6c34\u6708\u5eb5\u201d\u3002 \u3000\u3000\u6c34\u6708\u5ea6\u4f4d\u7f6e\u9669\u50fb\uff0c\u884c\u4eba\u4e0d\u5230\uff0c\u4f46\u8fd9\u65f6\u7adf\u6709\u4e24\u4e2a\u8863\u886b\u697c\u8934\u7684\u4e5e\u4e10\u8d70\u5230\u5e99\u524d\uff0c\u5176\u4e2d\u4e00\u4e2a\u8eab\u6750 \u8f83\u80d6\u7684\u8f7b\u8f7b\u6572\u4e86\u6572\u5e99\u95e8\u3002 \u3000\u3000\u5440\u7136\u4e00\u58f0\uff0c\u5f00\u95e8\u7684\u7adf\u662f\u4e00\u4e2a\u6e05\u4e3d\u7edd\u4f26\u7684\u5999\u9f84\u5973\u5c3c\uff0c\u96ea\u767d\u7684\u50e7\u8863\u4e0a\uff0c\u4e00\u5f20\u4fcf\u8273\u7684\u8138\uff0c\u4e4c\u9ed1 \u5927\u9635\u5b50\u5d4c\u5728\u79c0\u4e3d\u7684\u773c\u7736\u4e2d\uff0c\u8c61\u7259\u822c\u7684\u9f3b\u6881\uff0c\u697c\u6843\u822c\u7684\u5c0f\u5634\uff1b\u53ea\u662f\u5728\u90a3\u7edd\u4fd7\u660e\u4e3d\u4e4b\u4e2d\uff0c\u5374\u900f\u51fa \u4e00\u4e1d\u51c4\u82e6 \u3000\u3000\u90a3\u4e24\u4e5e\u4e10\u9661\u7136\u4e00\u6014\uff0c\u4e0d\u6599\u5728\u8fd9\u8352\u5510\u4e4b\u4e2d\u7adf\u6709\u5982\u6b64\u6e05\u4e3d\u8d85\u4fd7\u7684\u5c11\u5973\u2014\u2014\u5c24\u5176\u5947\u7684\u5979\u7adf\u662f\u4e2a \u5c3c\u59d1\u3002 \u3000\u3000\u662f\u4ec0\u4e48\u539f\u56e0\u4f7f\u5f97\u8fd9\u7f8e\u4e3d\u7684\u59d1\u5a18\u7528\u5979\u53ef\u7231\u7684\u9752\u6625\u6765\u966a\u4f34\u7740\u9752\u706f\u53e4\u4f5b\uff1f \u3000\u3000\u4e24\u4e2a\u4e5e\u4e10\u6014\u4e86\u4e00\u4f1a\uff0c\u4e00\u4e2a\u5e74\u9f84\u8f83\u5927\u7684\u7ed3\u5df4\u5730\u9053\uff1a\u201c\u59d1\u5a18\u2014\u2014\u554a\uff0c\u4e0d\u2014\u2014\u5c0f\u5e08\u5085\uff0c\u53ef\u4ee5\u7ed9 \u54b1\u4eec\u4e00\u4e9b\u6c34\u4e48\uff1f\u54b1\u4eec\u8d76\u8def\u8d76\u5f97\u592a\u6e34\u4e86\u2014\u2014\u201d \u3000\u3000\u90a3\u7f8e\u4e3d\u7684\u5c3c\u59d1\u6e29\u67d4\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u8f6c\u8eab\u8fc7\u53bb\u63d0\u4e86\u6c34\u58f6\u7ed9\u90a3\u4e24\u4e2a\u4e5e\u4e10\uff0c\u7136\u540e\u6084\u6084\u5730\u63a9\u4e0a\u4e86 \u95e8\u3002 \u3000\u3000\u90a3\u4e24\u4e2a\u4e5e\u4e10\u5750\u5728\u5eb5\u524d\u4e00\u68f5\u5927\u6811\u4e0b\uff0c\u4e00\u9762\u559d\u6c34\uff0c\u4e00\u9762\u5f00\u59cb\u95f2\u8c08\u201c\u5509\uff0c\u4eba\u6d77\u832b\u832b\u53eb\u54b1\u4eec\u53bb\u627e \u4e00\u4e2a\u53ea\u77e5\u53eb\u505a\u91d1\u6885\u9f84\u7684\u59d1\u5a18\uff0c\u8fd9\u771f\u662f\u96be\u4e8e\u5927\u6d77\u635e\u9488\u2014\u2014\u201d \u3000\u3000\u53e6\u4e00\u4e2a\u9053\uff1a\u201c\u8c01\u6559\u8f9b\u5927\u4fa0\u662f\u54b1\u4eec\u4e10\u5e2e\u7684\u5927\u6069\u4eba\u5462\uff1f\u8f9b\u5927\u4fa0\u6258\u54b1\u4eec\u7684\u4e8b\uff0c\u81ea\u4eec\u5c31\u662f\u8dd1\u6298\u4e86 \u817f\u4e5f\u5f97\u597d\u6b79\u529e\u5f97\u5230\u554a\u3002\u201d \u3000\u3000\u539f\u5148\u4e00\u4e2a\u9053\uff1a\u201c\u662f\u554a\uff0c\u8f9b\u5927\u4fa0\u90a3\u4efd\u6b66\u529f\u4eba\u54c1\u771f\u4f7f\u6211\u59d3\u94b1\u7684\u4f69\u670d\u5f97\u4e94\u4f53\u6295\u5730\uff0c\u83ab\u8bf4\u4ed6\u662f\u54b1 \u4eec\u7684\u601d\u4eba\uff0c\u5c31\u662f\u4ed6\u4e0d\u66fe\u5e2e\u8fc7\u54b1\u4eec\uff0c\u53ea\u8981\u4ed6\u77a7\u5f97\u8d77\u6211\u59d3\u94b1\u7684\uff0c\u80af\u5dee\u4f7f\u6211\u4e00\u58f0\uff0c\u6211\u4e5f\u7167\u6837\u5fc3\u7518\u60c5 \u613f\u5462\u3002\u201d \u3000\u3000\u4ed6\u4eec\u8c08\u5f97\u7545\u5feb\uff0c\u5374\u4e0d\u77e5\u5e99\u95e8\u91cc\u90a3\u5999\u9f84\u5973\u5c3c\u6b63\u4fa7\u8033\u503e\u542c\u7740\u54e9\u3002 \u3000\u3000\u5979\u542c\u5230\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u82cd\u767d\u5f97\u50cf\u51b0\u5757\u822c\u7684\u9762\u5b54\u5c31\u6cdb\u51fa\u5f02\u6837\u7684\u7ea2\u6655\uff0c\u663e\u7136\u7684\uff0c\u5979\u5185\u5fc3\u6b63\u5982\u6ce2 \u6f9c\u822c\u8d77\u4f0f\u4e0d\u5b9a\u3002 \u3000\u3000\u5979\u50cf\u662f\u8dcc\u4eba\u4e86\u5f80\u4e8b\u4e2d\uff0c\u5979\u7684\u9762\u4e0a\u900f\u7740\u5ae3\u7ea2\uff0c\u53cc\u7709\u867d\u7136\u5fae\u8e59\u9996\uff0c\u4f46\u5634\u89d2\u4e0a\u5374\u5408\u7740\u751c\u871c\u7684\u7b11 \u5bb9\u2014\u2014 \u3000\u3000\u6e10\u6e10\uff0c\u5979\u7684\u7b11\u5bb9\u6d88\u5931\uff0c\u5979\u7f8e\u4e3d\u7684\u8138\u4e0a\u73b0\u51fa\u4e00\u79cd\u5f02\u6837\u7684\u51c4\u82e6\uff0c\u90a3\u79cd\u53ef\u601c\u7684\u8868\u60c5\u4ee4\u6bcf\u4e00\u4e2a\u4eba \u89c1\u4e86\u90fd\u4f1a\u7531\u5185\u5fc3\u89c9\u5f97\u65e0\u6bd4\u7684\u611f\u52a8\u2014\u2014\uff0c\u5979\u7528\u529b\u5659\u7740\u6cea\u6c34\uff0c\u54fa\u54fa\u5730\u81ea\u8bed\uff1a \u3000\u3000\u201c\u6377\u54e5\u54e5\uff0c\u4f60\u6c38\u8fdc\u4e5f\u627e\u4e0d\u5230\u6211\u4e86\u2014\u2014\u201d \u3000\u3000\u201c\u8fd9\uff0c\u8fd9\u662f\u8001\u5929\u7237\u7684\u5b89\u6392\u554a\uff0c\u6211\u4ece\u751f\u4e0b\u6765\u7684\u90a3\u4e2a\u65f6\u8fb0\uff0c\u5c31\u6ce8\u5b9a\u6211\u8fd9\u4e00\u751f\u60b2\u60e8\u7684\u547d\u8fd0\uff0c\u4f46 \u662f\u8001\u5929\u7237\u4f60\u4e5f\u592a\u6b8b\u5fcd\u4e86\uff0c\u4f60\u4e3a\u4ec0\u4e48\u8981\u5c06\u8fd9\u6837\u4e00\u4e2a\u6c38\u8fdc\u65e0\u6cd5\u8865\u507f\u7684\u91cd\u5927\u7f6a\u5b7d\uff0c\u52a0\u5728\u8fd9\u6837\u4e00\u4e2a\u5f31 \u5973\u5b50\u7684\u5fc3\u4e0a\uff1f\u2026\u2026\u201d \u3000\u3000\u7136\u540e\uff0c\u5979\u518d\u60f3\u5230\u95e8\u5916\u90a3\u4e24\u4e2a\u4e5e\u4e10\u7684\u8c08\u8bdd\uff0c\u5979\u5b89\u6170\u5730\u81ea\u8a00\u9053\uff1a\u201c\u6377\u54e5\u54e5\u4ed6\u6bd5\u7adf\u8bb0\u7740\u6211\u7684\uff0c \u8fd9\u2026\u2026\u8fd9\u5df2\u7ecf\u591f\u4e86\uff0c\u5c31\u8fd9\u6837\u8ba9\u5b83\u7ed3\u675f\uff0c\u8fd9\u6837\u7684\u7ed3\u675f\u662f\u6700\u2026\u2026\u6700\u2018\u597d\u2019\u7684\u3002\u201d \u3000\u3000\u201c\u6377\u54e5\u54e5\uff0c\u4f60\u522b\u627e\u6211\u4e86\u5427\uff0c\u4f60\u627e\u4e0d\u5230\u7684\u2026\u2026\u6211\u5c06\u4e3a\u4f60\u795d\u798f\u2026\u2026\u201d \u3000\u3000\u5979\u8f7b\u8f7b\u8f6c\u8fc7\u8eab\uff0c\u4ef0\u671b\u7740\u795e\u6848\u4e0a\u7684\u89c2\u97f3\u4f5b\u50cf\uff0c\u65b9\u7a97\u5b54\u5916\u4e00\u675f\u9633\u5149\u6b63\u5de7\u7167\u5728\u89c2\u97f3\u7684\u8138\u4e0a\uff0c\u90a3 \u6148\u7965\u800c\u667a\u6167\u7684\u7709\u4e2d\u597d\u4f3c\u53d1\u51fa\u4ee4\u4eba\u51db\u7136\u7684\u5723\u6d01\u5149\u8f89\u3002 \u3000\u3000\u5979\u864f\u8bda\u5730\u8dea\u4e86\u4e0b\u6765\uff0c\u7f13\u7f13\u5730\u70b9\u71c3\u4e86\u4e00\u675f\u9999\uff0c\u5e84\u91cd\u5730\u63d2\u5165\u6848\u4e0a\u7684\u9999\u7089\uff0c\u4e00\u7f15\u8f7b\u70df\u88f3\u88d8\u4e0a \u5347\uff0c\u7ecf\u8fc7\u90a3\u675f\u9633\u5149\u65f6\uff0c\u53d8\u6210\u4e86\u9752\u8272\u7684\u4e00\u7247\u3002 \u3000\u3000\u5c0f\u5c3c\u59d1\u864f\u8bda\u7684\u7948\u7977\u58f0\u987a\u7740\u90a3\u7f15\u8f7b\u70df\u7f13\u7f13\u5347\u5165\u6d69\u6e3a\u7684\u5929\u5ead\u2014\u2014 \u3000\u3000\u5929\u8272\u4e00\u6697\uff0c\u592a\u9633\u53c8\u94bb\u5165\u6df1\u539a\u7684\u4e91\u5c42\u4e2d\u2026\u2026\u2026 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c\u5341\u516d\u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c\u5341\u516d\u7ae0 \u3000\u3000\u65f6\u95f4\u5012\u6eaf\u81f3\u4e09\u767e\u5e74\u524d\uff1b\u8fd9\u4e2a\u6545\u4e8b\u5f00\u59cb\u7684\u65f6\u5019\u2014\u2014 \u3000\u3000\u9526\u5dde\uff0c\u5c71\u6d77\u5173\u5916\uff0c\u5317\u98ce\u6012\u53f7\uff0c\u96ea\u82b1\u867d\u7136\u6e10\u6e10\u505c\u4e86\uff0c\u4f46\u662f\u98ce\u5374\u662f\u6108\u6765\u6108\u52b2\u3002 \u3000\u3000\u7070\u8272\u7684\u5929\u7a79\uff0c\u5929\u811a\u5904\u7565\u5448\u73b0\u4e73\u767d\u8272\uff0c\u8fd9\u5173\u5916\u7684\u51ac\u5929\uff0c\u6ee1\u76ee\u7684\u8427\u7136\u8083\u6740\u4e4b\u60c5\uff0c\u96ea\u662f\u505c\u4e86\uff0c \u4f46\u662f\u5730\u4e0a\u5df1\u94fa\u7740\u5c3a\u6df1\u539a\u96ea\uff0c\u597d\u4e00\u7247\u94f6\u8272\u4e16\u754c\u3002 \u3000\u3000\u96ea\u5806\u540e\u9762\uff0c\u8e72\u7740\u4e00\u4e2a\u5c0f\u7ae5\uff0c\u5e74\u7ea6\u56db\u4e94\u5c81\uff0c\u53ea\u662f\u4ed6\u957f\u5f97\u7ec6\u76ae\u5ae9\u8089\uff0c\u7709\u76ee\u6e05\u79c0\uff0c\u8138\u5706\u5982\u7403\uff0c \u5374\u95ea\u7740\u4e00\u53cc\u4e4c\u9ed1\u660e\u4eae\u7684\u5927\u773c\u6674\uff0c\u90a3\u6a21\u6837\u5f53\u771f\u53ef\u7231\u5f97\u5f88\u3002 \u3000\u3000\u8fd9\u5b69\u5b50\u7a7f\u7740\u4e00\u4ef6\u53c8\u5927\u53c8\u7834\u7684\u68c9\u8863\uff0c\u80a9\u4e0a\u80cc\u4e86\u4e00\u5c0f\u6346\u67af\u67f4\uff0c\u4e00\u53cc\u5c0f\u624b\u4ecd\u4e0d\u505c\u5730\u5728\u96ea\u4e2d\u7ffb\u62e3 \u67af\u67f4\uff0c\u5c0f\u624b\u51bb\u5f97\u901a\u7ea2\u3002 \u3000\u3000\u5ffd\u7136\u4ed6\u505c\u6b62\u62fe\u67f4\uff0c\u7f13\u7f13\u7ad9\u8d77\u8eab\u6765\uff0c\u8fce\u9762\u4e00\u9635\u5bd2\u98ce\uff0c\u5439\u5f97\u4ed6\u6253\u4e86\u4e00\u4e2a\u5bd2\u5664\uff0c\u4ed6\u6296\u64de\u4e86\u4e00 \u4e0b\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u96ea\u505c\u4e86\uff0c\u4eca\u665a\u53ea\u6015\u8fd8\u8981\u51b7\u5462\u3002\u201d \u3000\u3000\u5ffd\u7136\u4ed6\u77aa\u7740\u4e00\u53cc\u4e4c\u9ed1\u7684\u9635\u5b50\uff0c\u76ee\u4e0d\u8f6c\u775b\u5730\u671b\u7740\u5929\u7a7a\uff0c\u539f\u6765\u5929\u7a7a\u4e00\u53ea\u9ed1\u9e70\u76d8\u65cb\u7740\u98de\u4e86\u8fc7 \u6765\u3002 \u3000\u3000\u90a6\u5e94\u4e00\u8eab\u9ed1\u4eae\u6241\u6bdb\uff0c\u5934\u9876\u4e0a\u5374\u662f\u96ea\u767d\uff0c\u867d\u7136\u4e0d\u5927\uff0c\u5374\u795e\u9a8f\u5f97\u5f88\uff0c\u8fd9\u79cd\u9e70\u513f\u4e43\u662f\u8fbd\u4e1c\u6240\u4ea7 \u6700\u5389\u5bb3\u7684\u4e00\u79cd\uff0c\u5524\u505a\u201c\u6d77\u4e1c\u9752\u201d\uff0c\u8eab\u5f62\u867d\u4e0d\u751a\u5927\uff0c\u5374\u51f6\u5f97\u5389\u5bb3\uff0c\u5bfb\u5e38\u6bd4\u5b83\u5927\u4e0a\u4e00\u500d\u7684\u5140\u9e70\u4e5f \u4e0d\u6562\u62db\u60f9\u5b83\u3002 \u3000\u3000\u8fd9\u5c0f\u7ae5\u770b\u5b83\u8001\u662f\u7ed5\u7740\u5708\u513f\u76d8\u65cb\uff0c\u5fc3\u77e5\u5fc5\u6709\u539f\u6545\uff0c\u4e8e\u662f\u722c\u4e0a\u90a3\u96ea\u5806\u4e0b\u671b\uff0c\u679c\u7136\u8fdc\u5904\u6709\u4e00\u53ea \u96ea\u767d\u7684\u5c0f\u5154\u5728\u8dd1\u7740\uff0c\u90a3\u5154\u5468\u8eab\u96ea\u767d\uff0c\u82e5\u975e\u662f\u5728\u98de\u8dd1\uff0c\u6839\u672c\u5206\u8fa8\u4e0d\u51fa\u6765\u3002 \u3000\u3000\u90a3\u9e70\u8f6c\u5f97\u4e24\u8f6c\uff0c\u5ffd\u7136\u53cc\u7fc5\u4e00\u6536\uff0c\u8eab\u5f62\u5c31\u5982\u7bad\u77e2\u822c\u51b2\u4e86\u4e0b\u6765\uff0c\u90a3\u91ce\u5154\u56db\u8db3\u4e00\u7eb5\uff0c\u6ca1\u547d\u72c2 \u5954\u3002 \u3000\u3000\u4f46\u662f\u9e70\u513f\u8ba1\u7b97\u5f97\u6781\u7cbe\uff0c\u4e0b\u6251\u4e4b\u52bf\u6b63\u597d\u5728\u91ce\u5154\u524d\u9762\u4e00\u70b9\u513f\uff0c\u5154\u5b50\u5f80\u524d\u4e00\u9003\uff0c\u6b63\u597d\u78b0\u4e0a\u5b83\u7684 \u5229\u722a\u3002 \u3000\u3000\u7ad9\u5728\u96ea\u5806\u4e0a\u7684\u5c0f\u5b69\u770b\u5f97\u4e0d\u7981\u53eb\u51fa\u58f0\u97f3\u6765\uff0c\u773c\u89c1\u9e70\u722a\u5c31\u8981\u6293\u4e0a\u767d\u5154\u80cc\u4e0a\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6 \u5feb\uff0c\u5ffd\u89c1\u90a3\u767d\u5154\u5f80\u5de6\u4e00\u94bb\uff0c\u8eab\u5f62\u5374\u5f80\u53f3\u4e00\u7ffb\uff0c\u7acb\u65f6\u80cc\u810a\u57ab\u5730\uff0c\u56db\u811a\u671d\u5929\uff0c\u4e00\u53cc\u540e\u817f\u731b\u7136\u5f80\u4e0a \u4e00\u8e6c\u2014\u2014 \u3000\u3000\u4f46\u95fb\u4e00\u58f0\u60e8\u9e23\uff0c\u90a3\u201c\u6d77\u4e1c\u9752\u201d\u5ffd\u7136\u8dcc\u843d\u5730\u4e0a\uff0c\u6eda\u4e86\u4e00\u6eda\u4fbf\u5df2\u6b7b\u53bb\u3002 \u3000\u3000\u539f\u6765\u90a3\u767d\u514d\u540e\u817f\u4e00\u5883\uff0c\u6b63\u8e6c\u5728\u9e70\u8179\u4e0a\uff0c\u767b\u65f6\u628a\u9e70\u809a\u5b50\u8e6c\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u809a\u80a0\u6d41\u4e86\u4e00\u5730\u3002 \u3000\u3000\u90a3\u767d\u5154\u6eda\u4e86\u4e24\u6eda\uff0c\u4e5f\u5012\u4e0b\u4e0d\u52a8\u4e86\uff0c\u6562\u60c5\u5b83\u809a\u4e0a\u4e5f\u88ab\u6495\u53bb\u4e00\u5927\u5757\u76ae\u8089\uff0c\u8840\u6d41\u5982\u6ce8\u3002 \u3000\u3000\u4e1c\u5317\u91ce\u5154\u5f3a\u58ee\u4e07\u5206\uff0c\u5e38\u80fd\u501f\u4e00\u8e6c\u4e4b\u52bf\u6740\u6b7b\u5de8\u9e70\uff0c\u6709\u8bb8\u591a\u5357\u65b9\u4eba\u521d\u5230\u5317\u65b9\uff0c\u542c\u5f53\u5730\u730e\u6237\u8bf4 \u8d77\u8fd9\u7b49\u4e8b\u6765\uff0c\u90fd\u4e0d\u76f8\u4fe1\uff0c\u7b49\u5230\u4eb2\u773c\u76ee\u7779\u65f6\uff0c\u4e0d\u7981\u4e00\u4e2a\u4e2a\u76ee\u77aa\u53e3\u5446\u3002 \u3000\u3000\u4e14\u8bf4\u7ad9\u5728\u5761\u4e0a\u7684\u5c0f\u5a03\u513f\u77a7\u89c1\u8fd9\u5e55\u60c5\u666f\uff0c\u5c31\u4ece\u5761\u4e0a\u8dd1\u8fc7\u53bb\uff0c\u8d70\u8fd1\u770b\u65f6\uff0c\u53d1\u73b0\u90a3\u767d\u5154\u8eab\u8eaf\u5fae \u6296\uff0c\u4f3c\u4e4e\u5c1a\u672a\u6b7b\u53bb\uff0c\u8179\u4e0a\u521b\u53e3\u4e5f\u4ecd\u4e0d\u65ad\u6d41\u7740\u9c9c\u8840\u3002 \u3000\u3000\u4ed6\u628a\u514d\u513f\u6293\u4f4f\u4e00\u770b\uff0c\u90a3\u5154\u679c\u7136\u6ca1\u6b7b\uff0c\u88ab\u4ed6\u4e00\u9635\u6447\u52a8\uff0c\u7f13\u7f13\u7741\u5f00\u4e00\u53cc\u7ea2\u773c\u6674\u77aa\u7740\u4ed6\u3002\u5c0f\u5a03 \u513f\u89c1\u90a3\u5154\u901a\u4f53\u96ea\u767d\uff0c\u80a5\u5934\u5927\u8033\uff0c\u6a21\u6837\u5341\u5206\u53ef\u7231\uff0c\u90a3\u53cc\u7ea2\u773c\u775b\u4e2d\u4f3c\u4e4e\u6d41\u9732\u51fa\u4e00\u80a1\u75bc\u75db\u7684\u795e\u8272\uff0c \u53c8\u50cf\u662f\u5728\u4e5e\u6c42\u5e2e\u52a9\uff0c\u4e0d\u7981\u601c\u60af\u4e4b\u5fc3\u5927\u8d77\uff0c\u5fd9\u4ece\u53e3\u888b\u4e2d\u638f\u51fa\u4e00\u6761\u624b\u5dfe\u628a\u767d\u514d\u4f24\u53e3\u5305\u4f4f\u3002 \u3000\u3000\u4f46\u90a3\u521b\u53e3\u4f24\u5f97\u6781\u6df1\uff0c\u867d\u7528\u624b\u5dfe\u5305\u4f4f\uff0c\u4f46\u662f\u4ecd\u6b62\u4e0d\u4e86\u8840\uff0c\u90a3\u767d\u5154\u6108\u6765\u6108\u662f\u840e\u7f29\uff0c\u53cc\u8033\u5782 \u4e0b\uff0c\u773c\u6674\u4e5f\u7f13\u7f13\u95ed\u4e0a\uff0c\u773c\u770b\u662f\u4e0d\u6210\u7684\u4e86\uff0c\u5c0f\u7ae5\u4e0d\u7531\u614c\u4e71\u7684\u624b\u811a\uff0c\u4e0d\u77e5\u8981\u600e\u4e48\u529e\u624d\u597d\u3002 \u3000\u3000\u8fd9\u65f6\u5019\uff0c\u8fd1\u5904\u5c71\u5ce6\u4e0a\u7f13\u7f13\u8d70\u6765\u4e00\u4eba\u4e00\u9a91\u3002 \u3000\u3000\u90a3\u9a6c\u901a\u4f53\u96ea\u767d\uff0c\u5e76\u65e0\u4e00\u6839\u6742\u6bdb\uff0c\u6781\u662f\u795e\u9a8f\uff0c\u53e3\u4e2d\u4e0d\u65f6\u5410\u7740\u4e00\u56e2\u56e2\u767d\u6c14\uff0c\u9a6c\u4e0a\u5750\u7740\u4e00\u4e2a\u8001 \u8005\uff0c\u8fd9\u8001\u4eba\u65b9\u5934\u5927\u8138\uff0c\u9762\u5982\u91cd\u67a3\uff0c\u5374\u662f\u7ea2\u6da6\u5f02\u5e38\uff0c\u767d\u7709\u767d\u9aef\u4e2d\u900f\u51fa\u4e00\u4e1d\u6148\u7965\u53ef\u4eb2\uff0c\u4f46\u5947\u7684\u662f \u6148\u853c\u4e4b\u4e2d\u53c8\u4ee4\u4eba\u611f\u5230\u4e0d\u6012\u800c\u5a01\u3002 \u3000\u3000\u8001\u4eba\u52d2\u9a6c\u722c\u4e0a\u5c0f\u91cd\u65b9\u624d\u7acb\u8db3\u7684\u5c0f\u5761\uff0c\u505c\u4e0b\u9a6c\u6765\u56db\u767e\u773a\u671b\uff0c\u53ea\u89c1\u4e0d\u8fdc\u5904\u201c\u5c71\u6d77\u5173\u201d\u5728\u6de1\u6de1 \u96fe\u6c14\u4e2d\u5dcd\u7136\u8038\u7acb\uff0c\u9760\u8fd1\u5730\u9762\u5904\u56e0\u96fe\u6c14\u8f83\u6d53\uff0c\u5df2\u662f\u6b32\u73b0\u72b9\u9690\uff0c\u57ce\u697c\u4e0a\u6a2a\u989d\uff0c\u5374\u662f\u6e05\u6e05\u695a\u695a\u53ef \u89c1\uff0c\u201c\u5929\u4e0b\u7b2c\u4e00\u5173\u201d\u4e94\u4e2a\u5b57\u9f99\u98de\u98ce\u821e\uff0c\u6c14\u52bf\u78c5\u7934\u3002 \u3000\u3000\u8001\u4eba\u51dd\u76ee\u770b\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u53cc\u76ee\u7cbe\u5149\u66b4\u5c04\uff0c\u8fc7\u4e86\u4e00\u4f1a\u53c8\u957f\u53f9\u4e00\u58f0\uff0c\u4ed6\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u6211\u4e00 \u751f\u4ece\u6ca1\u6709\u8e0f\u8fdb\u6b64\u5173\u534a\u6b65\uff0c\u8fd9\u4e00\u53bb\uff0c\u4e0d\u77e5\u2014\u2014\u4e0d\u77e5\u8fd8\u6709\u6ca1\u6709\u547d\u80fd\u56de\u6765\uff0c\u5509\uff0c\u98ce\u67cf\u6768\uff0c\u4f60\u5343\u4e07\u4e0d \u8981\u628a\u4e00\u4e16\u82f1\u540d\u4ed8\u4e4b\u6d41\u6c34\u554a\uff01\u201d \u3000\u3000\u4ed6\u4e00\u4f4e\u5934\uff0c\u84e6\u7136\u77a7\u89c1\u5761\u4e0b\u5c0f\u7ae5\u62b1\u7740\u4e00\u53ea\u767d\u5154\u7684\u60c5\u666f\uff0c\u4e0d\u7531\u8f7b\u54a6\u4e00\u58f0\u3002 \u3000\u3000\u90a3\u4e2a\u5a03\u513f\uff0c\u62b1\u7740\u4e00\u53ea\u8840\u6d41\u4e0d\u6b62\uff0c\u5944\u5944\u4e00\u606f\u7684\u767d\u5154\uff0c\u5728\u8eab\u4e0a\u4e71\u6293\u4e71\u6478\uff0c\u5374\u6ca1\u6709\u4e00\u4ef6\u4e1c\u897f\u7ba1 \u7528\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u77a7\u89c1\u5de6\u9762\u96ea\u5806\u4e2d\u9732\u51fa\u4e00\u4e2a\u5ae9\u7eff\u8272\u7684\u5c0f\u5c16\u513f\uff0c\u4ed6\u4e0d\u7981\u5927\u559c\uff0c\u8fde\u5fd9\u4e00\u628a\u5c06\u96ea\u6293\u5f00\uff0c \u679c\u7136\u9732\u51fa\u4e00\u682a\u5c0f\u8349\u6765\u3002 \u3000\u3000\u5c0f\u7ae5\u628a\u7eff\u8349\u62d4\u5c06\u51fa\u6765\uff0c\u770b\u7740\u6839\u90e8\u7684\u9ec4\u8272\u5927\u7b0b\uff0c\u4e0d\u7981\u559c\u9053\uff1a \u3000\u3000\u201c\u554a\uff0c\u8fd9\u571f\u53c2\u597d\u5927\u2014\u2014\u201d \u3000\u3000\u8fd9\u79cd\u571f\u53c2\u5728\u4e1c\u5317\u5230\u5904\u90fd\u662f\uff0c\u662f\u4ee5\u5c0f\u7ae5\u4e00\u89c1\u5c31\u8ba4\u5f97\uff0c\u8fd9\u571f\u53c2\u6839\u4e2d\u7684\u6c41\u6c34\u6700\u80fd\u6b62\u8840\u957f\u808c\uff0c\u6c5f \u6e56\u90ce\u4e2d\u7684\u5200\u521b\u836f\u4e2d\u591a\u63ba\u6709\u8fd9\u4e1c\u897f\u3002 \u3000\u3000\u5c0f\u7ae5\u628a\u90a3\u571f\u53c2\u62ff\u5728\u624b\u4e2d\u7528\u529b\u4e00\u634f\uff0c\u90a3\u77e5\u8fd9\u571f\u53c2\u6839\u513f\u786c\u5f97\u5f02\u5e38\uff0c\u7adf\u662f\u634f\u5b83\u4e0d\u7834\uff0c\u4ed6\u4f4e\u5934\u4e00 \u77a7\uff0c\u5c0f\u767d\u5154\u53cc\u773c\u5df2\u7d27\u7d27\u5408\u4e0a\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5927\u6025\uff0c\u4e00\u628a\u5c06\u571f\u53c2\u653e\u5728\u53e3\u4e2d\uff0c\u7528\u7259\u9f7f\u7528\u529b\u4e00\u5431\u3002 \u3000\u3000\u201c\u5494\u201d\u4e00\u58f0\uff0c\u58f3\u513f\u7834\u88c2\uff0c\u91cc\u9762\u4e00\u5305\u751c\u6c41\u5168\u6ce8\u5165\u5c0f\u7ae5\u53e3\u4e2d\uff0c\u4ed6\u6b63\u5f85\u5410\u5c06\u51fa\u6765\uff0c\u5ffd\u7136\u53f3\u9762\u4e00 \u4e2a\u7126\u96f7\u822c\u7684\u58f0\u97f3\uff1a\u201c\u5140\uff0c\u4f60\u8fd9\u5c0f\u9b3c\u201d \u3000\u3000\u4ed6\u9aa4\u7136\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u201c\u5495\u201d\u4e00\u58f0\uff0c\u4e00\u53e3\u6c41\u6c34\u5168\u7ed9\u559d\u4e0b\u4e86\u809a\uff0c\u4ed6\u53ea\u89c9\u4e00\u80a1\u6e05\u51c9\u65e0\u6bd4\u7684\u6c41\u6c34 \u987a\u770b\u5589\u7ba1\u76f4\u6d41\u4e0b\u53bb\uff0c\u4ed6\u731b\u53ef\u4e00\u60ca\uff0c\u4e5f\u987e\u4e0d\u5f97\u770b\u53f3\u9762\u662f\u4ec0\u4e48\u4eba\u5728\u5927\u53eb\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u5e78\u597d\u58f3\u4e2d\u8fd8 \u6709\u4e00\u70b9\u6c34\u6c41\uff0c\u8fde\u5fd9\u5012\u5728\u767d\u5154\u7684\u4f24\u53e3\u4e0a\uff0c\u7528\u6bdb\u5dfe\u5305\u7740\u3002 \u3000\u3000\u8fd9\u4e1c\u897f\u771f\u7075\u9a8c\u65e0\u6bd4\uff0c\u4e00\u4f1a\u513f\uff0c\u514d\u809a\u4e0a\u4e0d\u4ec5\u6d41\u8840\u5168\u6b62\uff0c\u800c\u4e14\u7acb\u523b\u751f\u51fa\u4e00\u5c42\u6cb9\u76ae\u6765\u3002 \u3000\u3000\u4ed6\u4e00\u5fc3\u7167\u6599\u5c0f\u5154\uff0c\u7ade\u5c06\u65b9\u624d\u53f3\u8fb9\u90a3\u58f0\u5927\u543c\u7ed9\u5fd8\u4e86\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u624b\u4e2d\u5154\u5b50\u4e00\u9635\u6296\u52a8\uff0c\u767d\u514d \u7f13\u7f13\u7741\u5f00\u773c\u6674\uff0c\u56db\u9762\u77a7\u4e86\u77a7\uff0c\u50cf\u662f\u60a0\u60a0\u9192\u6765\u7684\u6a21\u6837\u3002 \u3000\u3000\u5c0f\u5a03\u513f\u4e0d\u7981\u5927\u559c\uff0c\u8f7b\u8f7b\u5c06\u5154\u5b50\u653e\u5728\u5730\u4e0a\uff0c\u90a3\u5154\u5b50\u6162\u6162\u7ad9\u4e86\u8d77\u6765\uff0c\u5ffd\u7136\u7528\u5634\u8f7b\u8f7b\u5728\u5c0f\u5a03\u513f \u624b\u80cc\u4e0a\u64e6\u4e86\u4e24\u4e0b\uff0c\u7f13\u7f13\u79bb\u5f00\u3002 \u3000\u3000\u5c0f\u7ae5\u6ee1\u5fc3\u559c\u6b22\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u5c0f\u767d\u5154\uff0c\u518d\u89c1\u3002\u201d \u3000\u3000\u90a3\u767d\u5154\u53c8\u56de\u5934\u6765\uff0c\u7741\u7740\u7ea2\u773c\u6674\u5bf9\u4ed6\u671b\u4e86\u4e24\u773c\uff0c\u5306\u5306\u8dd1\u53bb\u3002 \u3000\u3000\u767d\u5154\u8d70\u4e86\u4e4b\u540e\uff0c\u4ed6\u9661\u7136\u60f3\u8d77\u65b9\u624d\u90a3\u4e00\u58f0\u5927\u543c\u751a\u662f\u51fa\u5947\uff0c\u8fde\u5fd9\u5f80\u53f3\u8fb9\u4e0b\u770b\uff0c\u53ea\u89c1\u767d\u96ea\u904d \u5730\uff0c\u4e00\u4e1d\u4eba\u5f71\u4e5f\u6ca1\u6709\u3002 \u3000\u3000\u4ed6\u5fc3\u91cc\u6697\u9053\u4e00\u58f0\u5947\u602a\uff0c\u5374\u4e5f\u6ca1\u6709\u518d\u53bb\u60f3\u5b83\u3002 \u3000\u3000\u4ed6\u7f13\u7f13\u5750\u4e0b\u6765\uff0c\u5750\u5728\u4e00\u8282\u677e\u6728\u4e0a\uff0c\u7528\u624b\u65e0\u804a\u5730\u628a\u96ea\u82b1\u62e8\u5f00\uff0c\u4e0d\u4e00\u4f1a\uff0c\u4fbf\u62e8\u5f00\u5c3a\u65b9\u7684\u4e00\u5757 \u6ce5\u5730\u51fa\u6765\uff0c\u6ce5\u5730\u4e0a\u94fa\u7740\u4e24\u5757\u9752\u7816\uff0c\u9752\u7816\u5f53\u4e2d\u6210\u4e86\u4e00\u6761\u72ed\u6c9f\uff0c\u90a3\u4e9b\u62d4\u5f00\u7684\u96ea\u82b1\u53d7\u4ed6\u624b\u4e0a\u7684\u6e29\u70ed \u6e10\u6e10\u6eb6\u5316\uff0c\u4e8e\u662f\u4e00\u9053\u6c34\u7f13\u7f13\u6ce8\u5165\u72ed\u6c9f\u4e2d\u3002 \u3000\u3000\u4ed6\u5446\u671b\u7740\u90a3\u72ed\u6c9f\uff0c\u5fc3\u4e2d\u53c8\u5f00\u59cb\u80e1\u601d\u4e71\u60f3\u8d77\u6765\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u867d\u5c0f\uff0c\u4f46\u662f\u611f\u60c5\u5374\u6781\u662f\u4e30\u5bcc\u2014\u2014\u867d\u7136\u53ea\u662f\u4e00\u4e9b\u7a1a\u6c14\u7684\u60c5\u611f\uff0c\u4e16\u4e0a\u7684\u4e07\u4e8b\u4e07\u7269\uff0c\u4ed6 \u90fd\u89c9\u5f97\u6781\u4e3a\u53ef\u7231\uff0c\u5e38\u5e38\u671b\u7740\u4e00\u6735\u767d\u4e91\uff0c\u4ed6\u4f1a\u5446\u770b\u4e0a\u4e00\u4e2a\u591a\u65f6\u8fb0\u4e0d\u89c9\u538c\u5026\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ed6\u53c8\u6df1 \u6df1\u7231\u4e0a\u4e00\u6735\u534a\u5f00\u7684\u84d3\u857e\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u5fc3\u4e2d\u4e71\u60f3\u7740\uff1a\u201c\u8001\u5e08\u4e0a\u6b21\u8bf4\u968b\u7080\u5e1d\u5f00\u4e86\u4e00\u6761\u8fd0\u6cb3\uff0c\u5bb3\u6b7b\u6210\u5343\u6210\u4e07\u7684\u767e\u59d3\uff0c\u55ef\uff0c\u90a3 \u8fd0\u6cb3\u4e00\u5b9a\u5927\u6781\u5566\u2026\u2026\u201d \u3000\u3000\u201c\u8fd9\u4fbf\u662f\u6211\u7684\u8fd0\u6cb3\u2014\u2014\u201d\u4ed6\u671b\u7740\u9752\u7816\u4e2d\u7684\u5c0f\u6c34\u6c9f\uff0c\u201c\u54c8\uff0c\u8c01\u4e5f\u5f97\u4e58\u8239\u624d\u80fd\u8fc7\u5f97\u53bb\u2014\u2014\u201d \u3000\u3000\u8fd9\u65f6\u9752\u7816\u4e0a\u5ffd\u7136\u722c\u6765\u4e00\u53ea\u8682\u8681\uff0c\u4ece\u4e00\u5c0f\u6bb5\u677e\u9488\u4e0a\u8f7b\u8f7b\u722c\u5230\u201c\u5bf9\u5cb8\u201d\uff0c\u5c0f\u91cd\u4e0d\u7981\u4e50\u5f97\u7b11\u4e86 \u8d77\u6765\uff0c\u4ed6\u6697\u9053\uff1a\u201c\u5bf9\uff0c\u8fd9\u662f\u6865\uff0c\u54c8\uff0c\u8682\u8681\u513f\u8fc7\u6865\u3002\u201d \u3000\u3000\u4ed6\u4f3c\u4e4e\u4e3a\u90a3\u8682\u8681\u4e5f\u628a\u8fd9\u201c\u6c34\u6c9f\u201d\u5f53\u505a\u201c\u8fd0\u6cb3\u201d\u800c\u6b22\u751a\u3002 \u3000\u3000\u8fd9\u65f6\u4ed6\u5ffd\u7136\u60f3\u9053\uff1a\u201c\u5927\u4eba\u7684\u5fc3\u91cc\u771f\u5947\u602a\uff0c\u8bb8\u591a\u5c0f\u866b\u5c0f\u8681\u90fd\u77e5\u9075\u5b88\u7684\u6cd5\u5219\uff0c\u4ed6\u4eec\u5374\u662f\u4e0d\u80af \u9075\u5b88\u2014\u2014\u201d \u3000\u3000\u201c\u547c\u201d\u4e00\u58f0\uff0c\u4e00\u4e2a\u201c\u5927\u4eba\u201d\u7684\u817f\u8de8\u8fc7\u4ed6\u7684\u201c\u8fd0\u6cb3\u201d\uff0c\u505c\u5728\u4ed6\u9762\u524d\u3002 \u3000\u3000\u4ed6\u7565\u5e26\u60ca\u614c\u5730\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c9\u4e00\u4e2a\u9762\u8272\u7ea2\u6da6\u7684\u8001\u8005\u5fae\u7b11\u7ad9\u5728\u4ed6\u9762\u524d\u3002 \u3000\u3000\u4ed6\u5fae\u5fae\u6709\u70b9\u8d23\u602a\u8fd9\u8001\u4f2f\u4e0d\u9075\u5b88\u4ed6\u201c\u8fd0\u6cb3\u7684\u89c4\u5219\u201d\uff0c\u4f46\u662f\u5f53\u4ed6\u770b\u5230\u8001\u4eba\u76a4\u76a4\u767d\u53d1\u65f6\uff0c\u4ed6\u4e0d \u7981\u89c9\u5f97\u81ea\u5df1\u8d23\u602a\u4ed6\u5341\u5206\u4e0d\u5e94\u8be5\uff0c\u53ea\u597d\u6b49\u7136\u4e00\u7b11\u3002 \u3000\u3000\u90a3\u8001\u4eba\u6148\u7965\u5730\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u73a9\u5f97\u771f\u5f00\u5fc3\u662f\u5427\uff1f\u4f60\u53ef\u77e5\u9053\u65b9\u624d\u4f60\u9669\u4e9b\u5c31\u4e22\u4e86\u4e00\u6761\u5c0f \u547d\uff1f\u201d \u3000\u3000\u5c0f\u7ae5\u4e0d\u7981\u4e00\u6014\uff0c\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u8001\u4eba\u7b11\u9053\uff1a\u201c\u65b9\u624d\u4f60\u628a\u90a3\u2018\u5343\u5e74\u53c2\u738b\u2019\u653e\u8fdb\u5634\u91cc\u53bb\u65f6\uff0c\u53ef\u66fe\u542c\u5230\u5927\u543c\u4e00\u58f0\uff1f\u201d \u3000\u3000\u5c0f\u91cd\u9053\uff1a\u201c\u542c\u5230\uff0c\u542c\u5230\uff0c\u4e0d\u8fc7\u4ec0\u4e48\u662f\u2018\u5343\u5e74\u53c2\u738b\u2019\u554a\uff1f\u60a8\u662f\u6307\u90a3\u679d\u571f\u53c2\u4e48\uff1f\u201d \u3000\u3000\u8001\u4eba\u7b11\u9053\uff1a\u201c\u54c8\uff0c\u4e16\u4e0a\u54ea\u6709\u90a3\u4e48\u5927\u7684\u571f\u53c2\uff1f\u4f60\u7adf\u4e0d\u77e5\u9053\u2026\u2026\u5509\uff0c\u53ef\u89c1\u5929\u4e0b\u4e8b\u51a5\u51a5\u4e2d\u81ea\u6709 \u6ce8\u5b9a\uff0c\u8fd9\u7b49\u5947\u5b9d\u5b9e\u6ce8\u5b9a\u8981\u843d\u5165\u8fd9\u5a03\u513f\u4e4b\u53e3\uff0c\u4efb\u8c01\u4e5f\u65e0\u6cd5\u963b\u6b62\uff0c\u65b9\u624d\u90a3\u2018\u91d1\u6bdb\u795e\u733f\u2019\u767d\u4e15\u89c1\u5b9d \u8d77\u6b79\u610f\uff0c\u7ed3\u679c\u4e0d\u4f46\u5b9d\u7269\u6ca1\u6709\u5230\u624b\uff0c\u53cd\u800c\u5403\u6211\u767e\u6b65\u795e\u62f3\u9001\u4e86\u547d\uff0c\u5509\u3002\u201d \u3000\u3000\u5c0f\u7ae5\u867d\u7136\u542c\u4e0d\u592a\u61c2\uff0c\u4f46\u4ed6\u5929\u6027\u806a\u660e\uff0c\u8111\u7b4b\u4e00\u8f6c\uff0c\u9053\uff1a\u201c\u4f2f\u4f2f\uff0c\u60a8\u662f\u8bf4\uff0c\u65b9\u624d\u90a3\u5927\u543c\u4e00\u58f0 \u7684\u4eba\u60f3\u6765\u5bb3\u6211\uff0c\u7ed3\u679c\u53cd\u8ba9\u4f2f\u4f2f\u6253\u6b7b\u4e86\u662f\u5417\uff1f\u201d \u3000\u3000\u8001\u4eba\u7b11\u9053\uff1a\u201c\u55ef\uff0c\u4f60\u8fd9\u5a03\u513f\u771f\u806a\u660e\u3002\u201d\u201c\u8bf4\u7740\u6307\u4e86\u6307\u53f3\u9762\u96ea\u5806\u540e\u3002 \u3000\u3000\u5c0f\u7ae5\u8dd1\u8fc7\u53bb\u4e00\u770b\uff0c\u53ea\u89c1\u96ea\u5806\u540e\u679c\u7136\u8eba\u7740\u4e00\u4e2a\u6c49\u5b50\uff0c\u77a7\u90a3\u6a21\u6837\uff0c\u5df2\u662f\u6b7b\u53bb\u591a\u65f6\uff0c\u53ea\u56e0\u6b63\u5012 \u5728\u96ea\u5806\u540e\uff0c\u662f\u4ee5\u65b9\u624d\u6ca1\u6709\u770b\u89c1\u3002 \u3000\u3000\u5c0f\u7ae5\u77a7\u4e86\u4e00\u4f1a\uff0c\u4f4e\u58f0\u9053\uff1a\u201d\u4f60\u8fd9\u4eba\u771f\u662f\u7684\uff0c\u8981\u5403\u90a3\u571f\u53c2\u65e9\u70b9\u544a\u8bc9\u6211\u4e0d\u5c31\u5f97\u4e86\uff0c\u53cd\u6b63\u90a3\u767d \u5154\u53ea\u8981\u4e00\u70b9\u70b9\u5c31\u591f\u4e86\uff0c\u5e72\u4e48\u8981\u5077\u5077\u6478\u6478\u7684\u2026\u2026 \u3000\u3000\u90a3\u8001\u4eba\u4e0d\u7981\u4e00\u6014\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u662f\u8bf4\u6211\u4e0d\u8be5\u6740\u4ed6\uff1f\u201d \u3000\u3000\u5c0f\u7ae5\u70b9\u4e86\u70b9\u5934\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u4ed6\u53c8\u6447\u4e86\u6447\u5934\uff0c\u9053\uff1a\u201c\u6211\u4e0d\u77e5\u9053\u8001\u4eba\u5446\u5f97\u4e00\u5446\uff0c\u5ffd\u7136\u4ef0\u5929\u957f \u7b11\uff0c\u8de8\u4e0a\u767d\u9a6c\uff0c\u6296\u7f30\u800c\u53bb\u3002 \u3000\u3000\u5c0f\u7ae5\u6014\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u89c9\u5f97\u4e00\u80a1\u70ed\u6c14\u4ece\u5c0f\u8179\u4e0b\u76f4\u5192\u4e0a\u6765\uff0c\u970e\u65f6\u8eab\u5982\u9189\u9152\uff0c\u5934\u660f\u8111\u80c0\uff0c \u201c\u6251\u201d\u7684\u5750\u5728\u5730\u4e0a\u3002 \u3000\u3000\u8001\u4eba\u9a91\u9a6c\u8d70\u51fa\u51e0\u6b65\uff0c\u5ffd\u7136\u56de\u5934\u4e00\u671b\u2014\u2014 \u3000\u3000\u8fd9\u4e00\u671b\uff0c\u7aef\u7684\u503c\u5f97\u4e00\u4e66\uff0c\u53ea\u6b64\u56de\u5934\u4e00\u671b\uff0c\u4ece\u6b64\u5c31\u51b3\u5b9a\u4e86\u4eca\u540e\u4e94\u5341\u5e74\u6b66\u6797\u7684\u5927\u52bf\uff01 \u3000\u3000\u8001\u4eba\u671b\u89c1\u5c0f\u7ae5\u9762\u7ea2\u5982\u9189\uff0c\u5fc3\u5fd6\u9053\uff1a\u201c\u5343\u5e74\u53c2\u738b\u7684\u6548\u529b\u53d1\u4f5c\u4e86\uff0c\u6211\u73b0\u5728\u867d\u6709\u8981\u4e8b\uff0c\u4f46\u82e5\u4e0d \u52a9\u8fd9\u5a03\u513f\u4e00\u529b\uff0c\u5c82\u4e0d\u662f\u597d\u751f\u53ef\u60dc\u4e86\u8fd9\u6b66\u6797\u5947\u5b9d\uff1f\u201d \u3000\u3000\u624b\u4e2d\u4e00\u52a8\u9a6c\u7f30\uff0c\u56de\u5230\u539f\u5904\uff0c\u4f38\u638c\u6309\u5728\u5c0f\u7ae5\u8179\u4e0a\u3002 \u3000\u3000\u5c0f\u91cd\u53ea\u89c9\u4e00\u80a1\u6696\u6d41\u4ece\u8001\u8005\u638c\u4e2d\u4f20\u51fa\uff0c\u5c06\u81ea\u5df1\u8179\u5185\u70ed\u6c14\u5f15\u4eba\u56db\u4f53\u767e\u9ab8\uff0c\u767b\u65f6\u89c9\u5f97\u8212\u7545\u65e0 \u5730\uff0c\u4f46\u662f\u6d51\u8eab\u4e00\u4e1d\u529b\u4e5f\u7528\u4e0d\u51fa\u3002 \u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8001\u8005\u6536\u638c\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d \u3000\u3000\u5c0f\u7ae5\u9053\uff1a\u201c\u6211\u53eb\u9ad8\u6218\u3002\u201d \u3000\u3000\u8001\u4eba\u671b\u4e86\u4ed6\u4e00\u4f1a\uff0c\u4ece\u676f\u4e2d\u638f\u51fa\u4e00\u5f20\u7eb8\u6765\uff0c\u4e22\u5728\u5c0f\u7ae5\u8eab\u4e0a\u9053\uff1a\u201c\u8fd9\u7eb8\u4e0a\u753b\u6709\u51e0\u4e2a\u4eba\u50cf\uff0c\u4f60 \u4ee5\u540e\u597d\u597d\u7167\u7740\u7ec3\u7ec3\uff0c\u5305\u7ba1\u6709\u4f60\u597d\u5904\u3002\u201d \u3000\u3000\u9ad8\u6218\u60f3\u8bf4\u4e24\u53e5\u611f\u6fc0\u4e4b\u8bdd\uff0c\u4f46\u662f\u5168\u8eab\u8f6f\u68c9\u68c9\u7684\uff0c\u8fde\u5f20\u53e3\u8bf4\u8bdd\u7684\u529b\u6c14\u90fd\u50cf\u662f\u6ca1\u6709\u4e86\u3002 \u3000\u3000\u8001\u4eba\u4ece\u9a6c\u80cc\u5305\u56ca\u4e2d\u62ff\u51fa\u4e00\u5757\u6bdb\u5dfe\uff0c\u76d6\u5728\u4ed6\u8eab\u4e0a\uff0c\u60f3\u8bf4\u4ec0\u4e48\uff0c\u5374\u6b62\u4f4f\u53e3\uff0c\u8fc7\u4e86\u4e00\u4f1a\u9053\uff1a \u201c\u597d\u597d\u7761\u4e00\u89c9\u5427\u3002\u201d \u3000\u3000\u53cd\u8eab\u8dc3\u4e0a\u9a6c\uff0c\u4e00\u62cd\u9a6c\u81c0\uff0c\u9a6c\u8e44\u626c\u8d77\u9635\u9635\u96ea\u82b1\u53bb\u4e86\u3002 \u3000\u3000\u9ad8\u6218\u770b\u90a3\u6bdb\u5dfe\u5fae\u5fae\u653e\u4eae\uff0c\u4e5f\u4e0d\u77e5\u662f\u4ec0\u4e48\u6bdb\u7ec7\u7684\uff0c\u76d6\u5728\u8eab\u4e0a\u53c8\u8f7b\u53c8\u6696\uff0c\u6bdb\u5dfe\u4e2d\u592e\u5374\u7528\u7ec6\u7ebf \u7ee3\u7740\u4e00\u68f5\u5927\u67cf\u6811\uff0c\u4e00\u68f5\u5927\u6768\u6811\uff0c\u679d\u6001\u6276\u758f\uff0c\u6781\u662f\u751f\u52a8\u3002 \u3000\u3000\u5ffd\u7136\u773c\u6674\u89c9\u5f97\u5fae\u9178\uff0c\u4e00\u5408\u773c\uff0c\u7f13\u7f13\u5165\u7761\u3002 \u3000\u3000\u8fd9\u9635\u65f6\u95f4\uff0c\u8001\u4eba\u9a91\u7740\u767d\u9a6c\u5df4\u5230\u4e86\u5c71\u6d77\u5173\u524d\uff0c\u4e0d\u77e5\u600e\u7684\uff1f\u4ed6\u7f13\u7f13\u653e\u6162\u4e86\u9a6c\uff0c\u50cf\u662f\u4e0d\u613f\u5165\u5173 \u4f3c\u7684\u3002 \u3000\u3000\u84e6\u7136\uff0c\u4ed6\u50cf\u662f\u5ffd\u5730\u60ca\u8d77\uff0c\u4ef0\u9996\u770b\u4e86\u770b\u96c4\u4f1f\u7684\u57ce\u697c\uff0c\u6697\u9053\uff1a\u201c\u98ce\u67cf\u6768\uff0c\u98ce\u67cf\u6768\uff0c\u4f60\u662f\u754f\u602f \u4e48\uff1f\u90a3\u65e0\u6068\u751f\u867d\u5219\u540d\u6ee1\u5929\u4e0b\uff0c\u96be\u9053\u6211\u8fb9\u585e\u5927\u4fa0\u5c31\u771f\u6015\u4ed6\u4e0d\u6210\uff1f\u201d \u3000\u3000\u4ed6\u731b\u7136\u56de\u5934\uff0c\u53ea\u89c1\u8fdc\u5904\u9ad8\u5c71\u63a5\u5929\uff0c\u9876\u4e0a\u767d\u96ea\u9690\u5728\u4e91\u96fe\u4e4b\u4e2d\uff0c\u767d\u96ea\u832b\u832b\uff0c\u597d\u4e00\u7247\u7267\u91ce\u98ce \u5149\uff0c\u6714\u98ce\u5439\u6765\uff0c\u89e6\u9762\u751f\u5bd2\uff0c\u60f3\u5230\u81ea\u5df1\u96c4\u8e1e\u5173\u5916\u5782\u5345\u5e74\uff0c\u4e0d\u7531\u6602\u7136\u81ea\u8bed\uff1a\u201c\u98ce\u67cf\u6768\uff0c\u4f60\u6614\u65e5\u5a01 \u98ce\u4f55\u5728\uff1f\u201d \u3000\u3000\u4e8e\u662f\u594b\u7136\u4e00\u638c\u62cd\u5728\u9a6c\u81c0\u4e0a\uff0c\u5f97\u5f97\u5f97\u51b2\u5165\u5929\u4e0b\u7b2c\u4e00\u5173\u3002 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c\u5341\u4e03\u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c\u5341\u4e03\u7ae0 \u3000\u3000\u521d\u51ac\u65f6\u5206\uff0c\u539f\u91ce\u4e0a\u4e00\u7247\u8083\u6740\u3002 \u3000\u3000\u4e00\u5f2f\u6d41\u6c34\uff0c\u67af\u5bc2\u5411\u4e1c\u6d41\u7740\uff0c\u4e00\u68f5\u51b2\u5929\u7684\u6986\u6811\uff0c\u867d\u7136\u6811\u53f6\u5c3d\u843d\uff0c\u53ef\u662f\u679d\u5e72\u6709\u5982\u6a2a\u751f\u87e0\u9f99\uff0c \u6c14\u52bf\u751a\u662f\u96c4\u4f1f\uff0c\u6811\u540e\uff0c\u662f\u4e2a\u767e\u5341\u5bb6\u7684\u5c0f\u6751\u843d\uff0c\u56e0\u4e3a\u6751\u524d\u6709\u8fd9\u68f5\u5343\u5e74\u5927\u6986\u6811\uff0c\u6240\u4ee5\u5524\u505a\u201c\u6986 \u5e84\u201d\u3002 \u3000\u3000\u6e05\u6668\uff0c\u5929\u8272\u5f88\u662f\u6e05\u6717\uff0c\u8fdc\u5904\u7684\u5c71\u6e05\u6e05\u695a\u695a\u7684\u4e00\u76ee\u4e86\u7136\uff0c\u5728\u6751\u9996\u4e00\u5bb6\u5c0f\u8305\u5c4b\uff0c\u8dd1\u51fa\u4e2a\u5c0f\u7537 \u5b69\uff0c\u5507\u7ea2\u9f7f\u767d\uff0c\u957f\u5f97\u975e\u5e38\u4fca\u4fcf\uff0c\u770b\u6765\u4e5f\u4e0d\u8fc7\u4e03\u3001\u516b\u5c81\uff0c\u4e24\u53ea\u5c0f\u624b\u63d0\u7740\u6c34\u6876\uff0c\u8d70\u5230\u4e95\u8fb9\u3002 \u3000\u3000\u4ed6\u7a7f\u5f97\u5f88\u5355\u8584\uff0c\u4e5f\u4e0d\u89c1\u8bdd\u51fa\u5bd2\u51b7\u4e4b\u6001\uff0c\u653e\u4e0b\u7ef3\u5b50\uff0c\u5f88\u8f7b\u677e\u4fbf\u6253\u6ee1\u4e86\u4e24\u6876\u6c34\u3002 \u3000\u3000\u4ed6\u89c1\u5929\u8272\u5c1a\u65e9\uff0c\u6751\u91cc\u8fd8\u6ca1\u6709\u4eba\u8d77\u6765\uff0c\u628a\u6c34\u5012\u5165\u53a8\u623f\u5185\u7684\u6c34\u7f38\uff0c\u4fbf\u8d70\u51fa\u5750\u5728\u6986\u6811\u4e0b\uff0c\u9762\u5bf9 \u7740\u5c1a\u672a\u4ece\u5c71\u5934\u722c\u51fa\u7684\u592a\u9633\uff0c\u4e00\u5fc3\u4e00\u610f\u7ec3\u8d77\u5185\u529f\u6765\u3002 \u3000\u3000\u7b49\u5230\u8fd0\u6c14\u4e00\u5468\u540e\uff0c\u4f46\u89c9\u904d\u4f53\u6e29\u6696\uff0c\u8212\u9002\u5df2\u6781\uff0c\u5fc3\u4e2d\u4e0d\u7531\u81ea\u4e3b\u7684\u53c8\u60f3\u5230\u90a3\u4e2a\u4f20\u4ed6\u8fd9\u5957\u5de5\u592b \u7684\u8001\u4eba\u3002 \u3000\u3000\u201c\u4ed6\u662f\u591a\u4e48\u4ee4\u4eba\u4eb2\u8fd1\u5440\uff0c\u4ed6\u8001\u4eba\u5bb6\u8138\u4e0a\u867d\u7136\u5f88\u662f\u4e25\u8083\uff0c\u53ef\u662f\uff0c\u53ef\u662f\u2026\u2026\u53ef\u662f\u600e\u6837\u6211\u4e5f\u8bf4 \u4e0d\u51fa\u6765\uff0c\u9664\u4e86\u7239\uff0c\u53ea\u6015\u4e16\u4e0a\u518d\u4e5f\u6ca1\u6709\u8fd9\u6837\u597d\u7684\u4eba\u3002\u201d\u4ed6\u60f3\u5230\u90a3\u8001\u4eba\u6ee1\u8138\u6b63\u6c14\uff0c\u4e0d\u7531\u6108\u89c9\u5fc3 \u6298\u3002 \u3000\u3000\u201c\u8981\u662f\u6211\u4eec\u4e0d\u642c\u8d70\u7684\u8bdd\uff0c\u4ed6\u7b54\u5e94\u56de\u6765\u8fd8\u8981\u6559\u6211\u6b66\u529f\u54e9\uff01\u201d \u3000\u3000\u4ed6\u6b63\u5728\u56de\u5fc6\u4e09\u5e74\u524d\u7684\u5f80\u4e8b\uff0c\u5ffd\u89c9\u8138\u4e0a\u4e00\u51c9\uff0c\u4ed6\u4e00\u6014\uff0c\u63a5\u7740\u604d\u7136\u5927\u609f\uff0c\u56de\u8fc7\u5934\u6765\uff0c\u62b1\u7740\u4e00 \u5934\u5927\u9ec4\u725b\u7684\u5934\u9a82\u9053\uff1a\u201c\u8001\u9ec4\uff0c\u53c8\u662f\u4f60\uff0c\u574f\u4e1c\u897f\u3002\u201d \u3000\u3000\u90a3\u5934\u8001\u725b\uff0c\u8eab\u4f53\u867d\u5f88\u5e9e\u5927\uff0c\u53ef\u662f\u4e56\u5de7\u5df2\u6781\uff0c\u662f\u4ee5\u4e58\u7740\u5c0f\u7537\u5b69\u6b63\u5446\u5446\u51fa\u795e\u65f6\uff0c\u6084\u6084\u8d70\u5230\u4ed6 \u8eab\u540e\uff0c\u8210\u4e86\u4e00\u53e3\u3002 \u3000\u3000\u5c0f\u5b69\u4e0e\u725b\u5f88\u662f\u4eb2\u70ed\uff0c\u8001\u725b\u8ba9\u4ed6\u62b1\u7740\u5934\uff0c\u4e0d\u4f4f\u7684\u7528\u820c\u53bb\u8210\u4ed6\uff0c\u7537\u5b69\u7a81\u7136\u7ffb\u8eab\u9a91\u4e0a\uff0c\u53eb\u9053\uff1a \u201c\u8001\u9ec4\uff0c\u54b1\u4eec\u5230\u7530\u91cc\u53bb\u3002\u201d \u3000\u3000\u201c\u8001\u9ec4\u201d\u4f3c\u4e4e\u5b8c\u5168\u542c\u5f97\u61c2\u5b69\u5b50\u7684\u8bdd\uff0c\u5fae\u5fae\u6447\u90a3\u9897\u5927\u5934\u3002 \u3000\u3000\u5b69\u5b50\u9053\uff1a\u201c\u600e\u6837\uff0c\u4f60\u8fd8\u6ca1\u6709\u5403\u8fc7\u5e72\u8349\uff1f\u201d \u3000\u3000\u8001\u725b\u70b9\u70b9\u5934\u3002 \u3000\u3000\u5b69\u5b50\u9053\uff1a\u201c\u90a3\u4e48\u6211\u4eec\u4e00\u540c\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u90a3\u5b69\u5b50\u9a91\u7740\u725b\uff0c\u6162\u6162\u8d70\u5411\u8305\u5c4b\uff0c\u5ffd\u7136\u91cc\u9762\u4f20\u51fa\u4e00\u9635\u82cd\u8001\u7684\u53eb\u58f0\uff1a\u201c\u6218\u513f\uff0c\u600e\u6837\u8fd9\u65e9\u4fbf\u8d77 \u6765\u4e86\u3002\u201d \u3000\u3000\u90a3\u7537\u5b69\u95fb\u58f0\u6025\u5fd9\u7ffb\u8eab\u4e0b\u725b\uff0c\u8dd1\u8fdb\u5c4b\u91cc\uff0c\u5bf9\u7761\u5728\u5e8a\u4e0a\u4e2d\u5e74\u75c5\u6c49\u4f4e\u58f0\u9053\uff1a\u201c\u7238\uff0c\u4f60\u75c5\u597d\u4e9b\u4e86 \u5427\u3002\u201d \u3000\u3000\u90a3\u75c5\u4eba\u6447\u5934\u53f9\u9053\uff1a\u201c\u6218\u513f\uff0c\u6211\u8fd9\u75c5\u96be\u597d\u4e86\uff0c\u5927\u592b\u8bf4\u6211\u662f\u865a\u706b\u4e0a\u5347\u7684\u5927\u70ed\u75c7\uff0c\u5176\u5b9e\u4ed6\u90a3\u77e5 \u6211\u8fd9\u662f\u51e0\u5341\u5e74\u6765\u7684\u8001\u6bdb\u75c5\u3002\u6218\u513f\uff0c\u8fd9\u6837\u62d6\u4e0b\u53bb\u4e5f\u4e0d\u662f\u529e\u6cd5\uff0c\u6211\u8001\u5b9e\u544a\u8bc9\u4f60\uff0c\u7239\u5e74\u9752\u65f6\u6709\u4e00\u6b21 \u5728\u6218\u573a\u4e2d\u8d1f\u4f24\uff0c\u8170\u90e8\u4e2d\u4e86\u654c\u4eba\u7684\u836f\u7bad\uff0c\u7bad\u5934\u59cb\u7ec8\u6ca1\u6709\u53d6\u51fa\uff0c\u662f\u4ee5\u8170\u75db\u65f6\u53d1\uff0c\u8fd9\u6b21\u53d1\u4f5c\u751a\u662f\u5389 \u5bb3\uff0c\u53ea\u6015\u2026\u2026\u53ea\u6015\u2026\u2026\u201d \u3000\u3000\u6218\u513f\u6025\u5fd9\u963b\u6b62\uff0c\u67d4\u58f0\u5b89\u6170\u9053\uff1a\u201c\u7238\uff0c\u60a8\u5343\u4e07\u522b\u4e71\u60f3\uff0c\u60a8\u7684\u75c5\u4e00\u5b9a\u4f1a\u597d\u7684\u3002\u201d \u3000\u3000\u75c5\u4eba\u957f\u53f9\u4e00\u58f0\uff0c\u7f13\u7f13\u9053\uff1a\u201c\u5509\uff0c\u4f60\u5e74\u7eaa\u8fd9\u4e48\u5c0f\uff0c\u6211\u771f\u662f\u4e0d\u653e\u5fc3\uff0c\u4e07\u4e00\u6709\u4e2a\u4e09\u957f\u4e24\u77ed\uff0c\u6211 \u5728\u4e5d\u6cc9\u4e0b\u600e\u4e48\u80fd\u5411\u4f60\u5988\u4ea4\u5f85\uff1f\u201d \u3000\u3000\u6218\u513f\u89c9\u5f97\u5ba4\u5185\u7a7a\u6c14\u6c89\u95f7\uff0c\u7236\u4eb2\u8fd9\u51e0\u53e5\u8bdd\u4ee4\u4ed6\u5fc3\u75db\u5982\u7ede\uff0c\u5f3a\u5fcd\u7740\u773c\u6cea\u9053\uff1a\u201c\u7238\uff0c\u6211\u53bb\u70e7\u65e9 \u996d\u3002\u201d \u3000\u3000\u4ed6\u7236\u4eb2\u7a81\u7136\u95ee\u9053\uff1a\u201c\u54b1\u4eec\u7530\u91cc\u7684\u9ad8\u7cb1\u5168\u90e8\u6536\u5b8c\u4e86\u5417\uff1f\u54b1\u4eec\u6b20\u522b\u4eba\u7684\u7cae\u98df\uff0c\u53ef\u8981\u5148\u8fd8 \u6e05\u3002\u201d \u3000\u3000\u6218\u513f\u9053\uff1a\u201c\u6b20\u9694\u58c1\u6797\u4f2f\u4f2f\uff0c\u540e\u9762\u674e\u5927\u53d4\u90fd\u8fd8\u5566\u3002\u201d \u3000\u3000\u90a3\u4eba\u6ee1\u8138\u6148\u7231\uff0c\u51dd\u671b\u7740\u6218\u513f\u8d70\u53bb\u51c6\u5907\u65e9\u996d\uff0c\u4e0d\u7531\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\uff0c\u8fd9\u70b9\u5e74\u7eaa\uff0c\u5982 \u679c\u662f\u751f\u957f\u5728\u5bcc\u8d35\u4e4b\u5bb6\uff0c\u6b63\u662f\u65e0\u77e5\u65e0\u90aa\uff0c\u5b09\u620f\u7ec8\u65e5\uff0c\u7ed5\u5728\u7236\u6bcd\u819d\u65c1\u6492\u5a07\u4f7f\u8d56\u7684\u9ec4\u91d1\u5e74\u534e\uff0c\u53ef\u662f \u6218\u513f\u5462\uff1f\u4e0d\u4f46\u8981\u7ba1\u7530\u91cc\u7684\u4e8b\uff0c\u53c8\u8981\u670d\u4f8d\u6211\u8fd9\u75c5\u4eba\uff0c\u5509\uff0c\u751f\u800c\u8d2b\u82e6\uff0c\u90a3\u771f\u662f\u5341\u5206\u4e0d\u5e78\u7684\u3002\u201d \u3000\u3000\u559d\u8fc7\u51e0\u7897\u9ad8\u7cb1\u7ca5\uff0c\u6218\u513f\u9a91\u4e0a\u201c\u8001\u9ec4\u201d\uff0c\u53c8\u5f80\u7530\u91cc\u53bb\u5272\u6700\u540e\u4e00\u5757\u9ad8\u7cb1\uff0c\u4ed6\u5c0f\u624b\u63e1\u7740\u9570\u5200\uff0c \u8fd0\u7528\u5982\u98de\uff0c\u6bcf\u5f53\u4ed6\u5272\u5b8c\u4e00\u628a\uff0c\u201c\u8001\u9ec4\u201d\u4fbf\u628a\u53f6\u5b50\u56bc\u65ad\u5403\u53bb\u3002 \u3000\u3000\u592a\u9633\u6e10\u6e10\u51fa\u6765\u4e86\uff0c\u6218\u513f\u7d2f\u5f97\u6ee1\u5934\u5927\u6c57\uff0c\u9633\u5149\u7167\u5728\u9ec4\u91d1\u6295\u7684\u9ad8\u7cb1\u7c73\u4e0a\uff0c\u4ee4\u4eba\u6709\u4e00\u79cd\u4e30\u8db3\u7684 \u611f\u89c9\uff0c\u6218\u513f\u4ef0\u671b\u7740\u8038\u9ad8\u7684\u957f\u767d\u5c71\uff0c\u5728\u78a7\u84dd\u7684\u82cd\u7a79\u4e2d\u73ed\u7acb\u7740\uff0c\u771f\u5206\u4e0d\u51fa\u5929\u9ad8\u8fd8\u662f\u5c71\u9ad8\uff0c\u5fc3\u60c5\u4e0d \u89c9\u60a0\u7136\u795e\u5f80\uff0c\u4f4e\u5934\u770b\u7740\u811a\u65c1\u6210\u5806\u7684\u9ecd\u7c73\uff0c\u81ea\u89c9\u52b3\u82e6\u6ca1\u6709\u7a7a\u8d39\uff0c\u5f88\u611f\u5b89\u6170\uff0c\u4f46\u4ed6\u4e00\u60f3\u5230\u7236\u4eb2\u4e45 \u75c5\u96be\u6108\uff0c\u53c8\u4e0d\u7981\u60b2\u4ece\u4e2d\u6765\uff0c\u81ea\u5df1\u4e5f\u5206\u4e0d\u51fa\u5fc3\u4e2d\u662f\u5fe7\u662f\u559c\u3002 \u3000\u3000\u4ed6\u4f11\u606f\u4e86\u4e00\u4f1a\uff0c\u4fbf\u628a\u9ad8\u7cb1\u7c73\u88c5\u8fdb\u5e03\u888b\uff0c\u5ffd\u7136\u8eab\u540e\u4e00\u4e2a\u751c\u8106\u7684\u58f0\u97f3\u53eb\u9053\uff1a\u201c\u9ad8\u6218\uff0c\u4f60\u66ff\u6211 \u4f5c\u7684\u6587\u7ae0\u5462\uff1f\u8001\u5e08\u8bf4\u4eca\u5929\u4e0d\u4ea4\uff0c\u5c31\u8981\u6328\u624b\u5fc3\u54e9\uff01\u201d \u3000\u3000\u9ad8\u6218\u56de\u8fc7\u5934\uff0c\u770b\u7740\u8eab\u540e\u90a3\u7a1a\u6c14\u6ee1\u8138\u7684\u5c0f\u59d1\u5a18\uff0c\u6b49\u7136\u9053\uff1a\u201c\u554a\uff0c\u8fd9\u51e0\u5929\u771f\u662f\u5fd9\u6781\u4e86\uff0c\u5929\u5929 \u4e0a\u7530\u91cc\u4f5c\u5de5\uff0c\u771f\u2026\u2026\u771f\u5bf9\u4e0d\u8d77\uff0c\u6211\u7adf\u5fd8\u6389\u8981\u66ff\u4f60\u4f5c\u6587\uff0c\u7b49\u6211\u6536\u62fe\u597d\uff0c\u8fd9\u4fbf\u66ff\u4f60\u4f5c\u3002\u201d \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u5f88\u4e0d\u9ad8\u5174\uff0c\u53cc\u988a\u6da8\u5f97\u901a\u7ea2\uff0c\u55d4\u9053\uff1a\u201c\u54fc\uff0c\u4e0d\u4f5c\u5c31\u4e0d\u4f5c\uff0c\u8c01\u7a00\u7f55\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u5185\u5f88\u611f\u60ed\u6127\uff0c\u4f4e\u5934\u4e0d\u8bed\uff0c\u5c0f\u5973\u5b69\u53c8\u9053\uff1a\u201c\u4e0a\u6b21\u6c76\u59d0\u8981\u4f60\u4f5c\uff0c\u65e9\u4e0a\u544a\u8bc9\u4f60\uff0c\u4f60\u4e0b\u5348 \u5c31\u4f5c\u597d\u9001\u53bb\uff0c\u6211\u8001\u65e9\u5c31\u544a\u8bc9\u4f60\uff0c\u4f60\u7adf\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u54fc\uff0c\u4f60\u8bb0\u5f97\u597d\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u60f3\u5f00\u53e3\u8fa9\u62a4\uff0c\u53ef\u662f\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5979\u8d23\u5907\u81ea\u5df1\u7684\u53e5\u53e5\u90fd\u662f\u5b9e\u8bdd\uff0c\u6240\u4ee5\u4e0d\u77e5\u5982\u4f55\u542f\u53e3\u3002 \u3000\u3000\u4ed6\u5929\u6027\u6781\u4e3a\u67d4\u548c\u6b63\u76f4\uff0c\u5e74\u7eaa\u867d\u5c0f\uff0c\u522b\u4eba\u5f85\u4ed6\u7684\u597d\u5904\uff0c\u4ed6\u65f6\u65f6\u94ed\u523b\u5728\u5fc3\u4e2d\uff0c\u522b\u4eba\u9a82\u4ed6\u607c \u4ed6\uff0c\u4ed6\u5374\u5e76\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u4e0d\u7ba1\u662f\u591a\u4e48\u8270\u96be\u5371\u9669\u7684\u4e8b\uff0c\u53ea\u8981\u662f\u522b\u4eba\u8981\u6c42\u4ed6\uff0c\u4ed6\u4ece\u6765\u672a\u66fe\u62d2\u7edd\uff0c \u90fd\u662f\u5c3d\u529b\u800c\uff0c\u56e0\u4e3a\u4ed6\u4e0d\u613f\u4f24\u5bb3\u4efb\u4f55\u4eba\u2014\u2014\u751a\u81f3\u4efb\u4f55\u5c0f\u52a8\u7269\uff0c\u4ed6\u7239\u5e38\u629a\u6478\u7740\u4ed6\u7684\u5934\u53d1\u8bf4\u4ed6\u6bd4\u5973 \u5b69\u513f\u5fc3\u5730\u66f4\u6148\u7965\u3002 \u3000\u3000\u90a3\u5c0f\u59d1\u5a18\u89c1\u4ed6\u4e45\u4e45\u4e0d\u8bed\uff0c\u4e0d\u7981\u6709\u4e9b\u61ca\u607c\uff0c\u4f46\u53c8\u4e0d\u4fbf\u793a\u5f31\uff0c\u4fbf\u9053\uff1a\u201c\u4f60\u5012\u5148\u751f\u6c14\u4e86\uff0c\u597d\uff0c \u4f60\u8d76\u5feb\u53bb\u4f5c\u5427\uff0c\u5f85\u4f1a\u6211\u5230\u4f60\u5bb6\u53bb\u62ff\uff0c\u6211\u8fd8\u8981\u81ea\u5df1\u6284\u4e00\u904d\uff0c\u8001\u5e08\u8ba4\u5f97\u4f60\u7684\u5b57\u5475\uff01\u201d \u3000\u3000\u8bf4\u7f62\uff0c\u779f\u4e86\u9ad8\u6218\u4e00\u773c\uff0c\u6e29\u67d4\u4e00\u7b11\uff0c\u8f6c\u8eab\u4fbf\u6b32\u79bb\u5f00\u3002 \u3000\u3000\u9ad8\u6218\u60f3\u5230\u81ea\u5df1\u8fd8\u987b\u5230\u9547\u4e0a\u53bb\u6293\u836f\uff0c\u6b63\u60f3\u544a\u8bc9\u5979\uff0c\u4f46\u4e00\u770b\u5230\u5979\u5145\u6ee1\u81ea\u4fe1\u7684\u5c0f\u8138\uff0c\u6de1\u6de1\u7684\u9633 \u5149\u7167\u5728\u5979\u767d\u7699\u7684\u76ae\u80a4\u4e0a\uff0c\u7b80\u76f4\u597d\u50cf\u900f\u660e\u4e86\uff0c\u4ee4\u4eba\u6709\u4e00\u79cd\u51fa\u5c18\u7684\u611f\u89c9\uff0c\u4fbf\u4f4f\u53e3\u4e0d\u8bf4\u4e86\u3002 \u3000\u3000\u4ed6\u8f7b\u5401\u4e86\u4e00\u53e3\uff0c\u88c5\u6ee1\u4e86\u4e8c\u9ebb\u5e03\u888b\uff0c\u9a91\u4e0a\u201c\u8001\u9ec4\u201d\uff0c\u4e00\u6b65\u6b65\u8d70\u56de\u5bb6\u53bb\u3002 \u3000\u3000\u5750\u5728\u5bbd\u5bbd\u7684\u725b\u80cc\u4e0a\uff0c\u51c9\u98ce\u5439\u6765\uff0c\u9ad8\u6218\u53c8\u60f3\u8d77\u6628\u591c\u7684\u68a6\u5883\u2026\u2026 \u3000\u3000\u201c\u5988\u5728\u4e91\u7aef\u91cc\uff0c\u5979\u5168\u8eab\u88f9\u7740\u4e00\u5c42\u539a\u539a\u7684\u5f69\u8679\u2026\u2026\u5979\u5411\u6211\u62db\u624b\uff0c\u6211\u52aa\u529b\u2026\u2026\u52aa\u529b\u60f3\u770b\u6e05\u695a \u5988\u4eb2\u7231\u7684\u9762\u5bb9\uff0c\u53ef\u662f\u90a3\u53ef\u6076\u7684\u5f69\u4e91\uff0c\u7adf\u628a\u5988\u6574\u4e2a\u8138\u7b3c\u7f69\u7740\uff0c\u53ea\u80fd\u770b\u51fa\u4e00\u4e2a\u8f6e\u5ed3\uff0c\u6211\u771f\u60f3\u8df3\u4e0a \u53bb\u62b1\u5988\uff0c\u5988\u5411\u6211\u6447\u6447\u624b\u4fbf\u6d88\u5931\uff0c\u6211\u4e00\u6025\uff0c\u5c31\u9192\u6765\u4e86\u3002 \u3000\u3000\u6211\u4e94\u5c81\u65f6\uff0c\u5988\u79bb\u5f00\u7239\u548c\u6211\uff0c\u6211\u8fd8\u4ee5\u4e3a\u5988\u662f\u7761\u7740\u4e86\u5462\uff01\u5982\u679c\u2026\u2026\u5982\u679c\u90a3\u65f6\u6211\u77e5\u9053\u4eca\u540e\u518d\u89c1 \u4e0d\u5230\u5979\uff0c\u6211\u2026\u2026\u6211\u5b9a\u8981\u591a\u77a7\u5979\u51e0\u773c\uff0c\u5728\u6211\u5fc3\u4e2d\u7559\u4e0b\u6bd4\u8f83\u6df1\u7684\u5370\u8c61\u3002\u201d\u4ed6\u60f3\uff1a\u201c\u6211\u6bcf\u6b21\u4f5c\u68a6\uff0c \u68a6\u5230\u5988\u90fd\u770b\u4e0d\u6e05\u695a\u5979\u7684\u9762\u5b54\uff0c\u6211\u4ed4\u7ec6\u56de\u5fc6\u4e5f\u53ea\u5f97\u5230\u4e8c\u4e2a\u6a21\u7cca\u7684\u5f71\u5b50\uff0c\u5988\uff0c\u4f60\u54ea\u4e00\u5929\u80fd\u8ba9\u6211\u5728 \u68a6\u4e2d\u770b\u5f97\u6e05\u695a\u4e00\u70b9\u5462\uff1f\u201d\u60f3\u5230\u8fd9\u91cc\uff0c\u4e0d\u7981\u9f3b\u5934\u53d1\u9178\uff0c\u771f\u6b32\u653e\u58f0\u4e00\u54ed\u3002 \u3000\u3000\u4ed6\u8f7b\u6b65\u8d70\u5230\u7236\u4eb2\u5e8a\u8fb9\uff0c\u89c1\u7236\u4eb2\u6c89\u6c89\u7761\u7740\uff0c\u7565\u7565\u653e\u5fc3\uff0c\u4fbf\u63d0\u7b14\u66ff\u90a3\u5c0f\u5973\u5b69\u4f5c\u6587\u3002 \u3000\u3000\u539f\u6765\u9ad8\u6218\u4e00\u5bb6\u672c\u662f\u5c71\u897f\u671b\u65cf\uff0c\u5bb6\u4e2d\u4ee3\u4ee3\u90fd\u662f\u6267\u6208\u536b\u56fd\u7684\u6b66\u5c06\uff0c\u5148\u7956\u9ad8\u5ba0\u66f4\u662f\u5927\u5b8b\u7cbe\u5fe0\u5cb3 \u5143\u5e05\u624b\u4e0b\u7b2c\u4e00\u5458\u5927\u5c06\uff0c\u5f53\u5e74\u66fe\u4ee5\u4e00\u679d\u957f\u621f\u8fde\u6311\u7ffb\u91d1\u4eba\u5341\u4e8c\u8f86\u91cd\u9769\u534e\u8f66\uff0c\u7aef\u7684\u6210\u9707\u5929\u4e0b\uff0c\u529b\u5c3d \u6b89\u56fd\u4e4b\u65e5\uff0c\u5cb3\u5143\u5e05\u5982\u5931\u5de6\u53f3\u624b\uff0c\u540e\u6765\u4f20\u5230\u5546\u6218\u7236\u4eb2\u9ad8\u4e91\uff0c\u4ed6\u773c\u89c1\u6ee1\u6e05\u91ce\u5fc3\u663e\u9732\uff0c\u60f3\u8981\u541e\u5e76\u6211 \u4e2d\u534e\u5927\u597d\u6cb3\u5c71\uff0c\u4fbf\u6000\u7740\u6ee1\u8154\u70ed\u8840\uff0c\u4ed7\u7740\u5bb6\u4f20\u201c\u65e0\u654c\u621f\u6cd5\u201d\uff0c\u6295\u8eab\u8fbd\u4e1c\u7ecf\u7565\u718a\u5ef7\u5f3c\u5927\u5e05\u5ea6\u4e0b\uff0c \u5145\u5f53\u4e00\u5458\u53c2\u5c06\uff0c\u90a3\u718a\u7ecf\u7565\u96c4\u624d\u5927\u5fd7\uff0c\u6587\u6b66\u53cc\u5168\uff0c\u539f\u662f\u4e3a\u56fd\u5bb6\u5e72\u57ce\uff0c\u7ecf\u8425\u8fbd\u4e1c\uff0c\u6e05\u5175\u4e0d\u6562\u8d8a\u96f7 \u6c60\u534a\u6b65\uff0c\u65e0\u5948\u5927\u660e\u6c14\u6570\u5df2\u5c3d\uff0c\u541b\u4e3b\u660f\u5eb8\uff0c\u91cd\u7528\u5c0f\u4eba\uff0c\u718a\u5927\u5e05\u4e09\u542f\u4e09\u7f62\uff0c\u53d7\u5c3d\u5978\u4eba\u7275\u5236\uff0c\u76db\u4eac \u4e00\u6218\uff0c\u738b\u5316\u8d1e\u5750\u800c\u4e0d\u6551\uff0c\u7ec8\u4e8e\u88ab\u6e05\u5175\u4e2a\u4e2a\u51fb\u7834\uff0c\u718a\u5ef7\u5f3c\u88ab\u6267\u81f3\u4eac\u95ee\u7f6a\uff0c\u9ad8\u4e91\u773c\u89c1\u5fe0\u4e49\u4e4b\u58eb\u4e0d \u662f\u51b2\u950b\u9677\u9635\u4e3a\u56fd\u6350\u8eaf\uff0c\u5c31\u662f\u88ab\u5978\u81e3\u6a2a\u52a0\u8feb\u5bb3\uff0c\u539f\u6765\u9887\u6709\u4e2d\u5174\u7684\u5c40\u9762\uff0c\u5230\u5934\u6765\u70df\u6d88\u4e91\u6563\uff0c\u4e0d\u7531 \u4e07\u5ff5\u4ff1\u7070\uff0c\u53ea\u8eab\u8fd4\u4e61\uff0c\u5a36\u4e86\u4e00\u623f\u5ab3\u5987\uff0c\u79cd\u7530\u5ea6\u65e5\u3002 \u3000\u3000\u9ad8\u4e91\u59bb\u90d1\u6c0f\uff0c\u662f\u6e29\u67d4\u817c\u8146\u7684\u4e00\u4e2a\u7f8e\u4eba\u513f\uff0c\u4f53\u6001\u751a\u662f\u8584\u5f31\uff0c\u53ef\u662f\u624d\u540d\u751a\u8457\uff0c\u8bd7\u3001\u8f9e\u3001\u6b4c\u3001 \u8d4b\u3001\u68cb\u3001\u7434\u3001\u4e66\u3001\u753b\u6837\u6837\u90fd\u5f88\u7cbe\u901a\uff0c\u9ad8\u4e91\u4e2d\u5e74\u800c\u5a36\uff0c\u5a36\u5f97\u5982\u6b64\u4e00\u4e2a\u624d\u5973\uff0c\u81ea\u662f\u767e\u4f9d\u767e\u987a\uff0c\u90d1 \u6c0f\u4e5f\u5f88\u5d07\u62dc\u592b\u541b\uff0c\u592b\u59bb\u95f4\u76f8\u656c\u5982\u5bbe\uff0c\u4f09\u4e3d\u60c5\u6df1\u3002\u4e0d\u6599\u5c31\u5728\u9ad8\u6218\u4e94\u5c81\u65f6\uff0c\u5929\u5992\u7ea2\u989c\uff0c\u90d1\u6c0f\u64a4\u624b \u79bb\u5f00\u5979\u4eb2\u7231\u7684\u592b\u5a7f\u7a1a\u5b50\uff0c\u9ad8\u4e91\u7ecf\u6b64\u6253\u51fb\uff0c\u5fc3\u5982\u6b7b\u7070\uff0c\u628a\u59bb\u5b50\u846c\u4e86\uff0c\u4e3a\u514d\u89e6\u666f\u4f24\u60c5\uff0c\u4fbf\u643a\u5e26\u7740 \u9ad8\u6218\uff0c\u51fa\u5173\u5f00\u57a6\uff0c\u4ed6\u77e5\u5173\u5916\u5175\u8352\u9a6c\uff0c\u5c31\u5728\u5c71\u6d77\u5173\u9644\u8fd1\u4e70\u4e86\u4e00\u5757\u7530\uff0c\u79cd\u4e0b\u9ad8\u7cb1\u5927\u8c46\uff0c\u53ef\u662f\u4ed6\u5929 \u6027\u8c6a\u4fa0\u4ed7\u4eea\uff0c\u6709\u4e00\u6b21\u5931\u624b\u6253\u6b7b\u4e00\u4e2a\u6b3a\u538b\u826f\u6c11\u7684\u5b98\u519b\uff0c\u81ea\u77e5\u5173\u5185\u5173\u5916\u4e0d\u80fd\u7acb\u8db3\uff0c\u8fd9\u4fbf\u5e26\u7740\u9ad8 \u6218\uff0c\u8fdc\u8d70\u957f\u767d\u5c71\u4e0b\u3002 \u3000\u3000\u9ad8\u6218\u5199\u5b8c\u6587\u7ae0\uff0c\u6478\u7740\u5e8a\u5934\u7684\u94b1\u888b\uff0c\u6478\u4e86\u534a\u5929\uff0c\u6478\u51fa\u4e00\u5c0f\u5757\u788e\u94f6\uff0c\u5429\u5490\u201c\u8001\u9ec4\u201d\u4e0d\u8981\u8d70 \u8fdc\uff0c\u90a3\u5934\u8001\u725b\u5bf9\u4ed6\u975e\u5e38\u4f9d\u604b\uff0c\u53e3\u4e2d\u8fde\u53eb\uff0c\u4f3c\u4e4e\u8981\u8ddf\u7740\u4ed6\u53bb\u3002 \u3000\u3000\u9ad8\u6218\u8fde\u8fde\u6447\u624b\uff0c\u90a3\u8001\u725b\u6027\u5df1\u901a\u7075\uff0c\u7a81\u7136\u4f0f\u4e0b\u8eab\u6765\uff0c\u53e3\u4e2d\u54ac\u7740\u9ad8\u6218\u7684\u8863\u670d\uff0c\u793a\u610f\u9a91\u4e0a\uff0c\u9ad8 \u6218\u65e0\u5948\u53eb\u9053\uff1a\u201c\u6211\u8981\u8d76\u7d27\u8dd1\u5230\u9547\u4e0a\u53bb\u6293\u836f\uff0c\u4f60\u8d70\u5f97\u90a3\u4e48\u6162\u600e\u4e48\u884c\uff0c\u7b49\u4f1a\u5230\u9547\u4e0a\uff0c\u4eba\u5bb6\u90fd\u6536\u5e02 \u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u725b\u543c\u53eb\u4e24\u58f0\uff0c\u597d\u50cf\u751a\u4e0d\u670d\u6c14\uff0c\u9ad8\u6218\u53ea\u5f97\u9a91\u4e0a\uff0c\u201c\u8001\u9ec4\u201d\u56db\u811a\u4e00\u7acb\uff0c\u5982\u98de\u8dd1\u53bb\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u5927\u611f\u60ca\u5947\uff0c\u56e0\u4e3a\u5e73\u65e5\u201c\u8001\u9ec4\u201d\u6027\u5b50\u6e29\u826f\uff0c\u62d6\u8f66\u7281\u7530\u90fd\u662f\u6162\u541e\u541e\uff0c\u53ef\u662f\u5b83\u6c14\u529b\u5f88 \u957f\uff0c\u6240\u4ee5\u4e00\u5929\u5de5\u4f5c\u4e0b\u6765\uff0c\u6bd4\u522b\u5bb6\u7684\u725b\u5e76\u4e0d\u900a\u8272\uff0c\u60f3\u4e0d\u5230\u201c\u8001\u9ec4\u201d\u8fd8\u6709\u8fd9\u597d\u811a\u529b\u3002 \u3000\u3000\u201c\u8001\u9ec4\u201d\u8dd1\u5f97\u867d\u5feb\uff0c\u53ef\u662f\u9ad8\u6218\u5750\u5728\u80cc\u4e0a\uff0c\u5e73\u7a33\u5df2\u6781\uff0c\u5fc3\u4e2d\u5bf9\u8fd9\u8001\u53cb\uff0c\u53c8\u4f36\u53c8\u7231\uff0c\u53cc\u624b\u6293 \u7740\u5b83\u7684\u89d2\u53eb\u9053\uff1a\u201c\u2018\u8001\u9ec4\u2019\u4f60\u6162\u4e9b\u8dd1\uff0c\u4e0d\u7136\uff0c\u4f1a\u592a\u7d2f\u4e86\uff0c\u4fbf\u4e0d\u80fd\u8dd1\u56de\u3002\u201d \u3000\u3000\u201c\u8001\u9ec4\u201d\u4f4e\u53f1\u51e0\u58f0\uff0c\u7b97\u662f\u56de\u7b54\u4ed6\u7684\u597d\u610f\uff0c\u811a\u4e0b\u5374\u4e1d\u6beb\u4e0d\u505c\uff0c\u4e0d\u4e00\u4f1a\uff0c\u4fbf\u8dd1\u8fdb\u5e02\u9547\uff0c\u8fd9\u624d \u653e\u6162\u811a\u6b65\u3002 \u3000\u3000\u9547\u4e2d\u4eba\u8fdc\u8fdc\u89c1\u4e00\u4eba\u4e00\u725b\u5982\u98de\u8dd1\u6765\uff0c\u90fd\u60ca\u5446\u4e86\uff0c\u5927\u5bb6\u90fd\u4ece\u6765\u6ca1\u6709\u770b\u5230\u8fd9\u4e48\u5584\u8dd1\u7684\u725b\uff0c\u7b49\u5230 \u8d70\u8fd1\uff0c\u8001\u9ec4\u653e\u6162\uff0c\u8fd9\u624d\u770b\u6e05\u695a\uff0c\u539f\u6765\u725b\u80cc\u4e0a\u9a91\u7740\u4e00\u4e2a\u7b11\u5bb9\u53ef\u63ac\u7684\u4fca\u7ae5\uff0c\u90a3\u725b\u4f53\u5f62\u7279\u5927\uff0c\u5b69\u5b50 \u5750\u5728\u5b83\u80cc\u4e0a\uff0c\u663e\u5f97\u5927\u5c0f\u4e0d\u76f8\u79f0\uff0c\u751a\u662f\u597d\u7b11\u3002 \u3000\u3000\u9ad8\u6218\u89c9\u5f97\u5927\u5bb6\u90fd\u5728\u6ce8\u89c6\u4ed6\uff0c\u5f88\u4e0d\u597d\u610f\u601d\u7ffb\u8eab\u4e0b\u725b\uff0c\u4ed6\u6015\u9547\u4eba\u9017\u60f9\u201c\u8001\u9ec4\u201d\uff0c\u5f15\u8d77\u5b83\u725b\u813e \u6c14\u5413\u4eba\uff0c\u4fbf\u628a\u5b83\u62f4\u5728\u8def\u65c1\u6811\u4e0a\uff0c\u8001\u9ec4\u5bf9\u5b83\u5c0f\u4e3b\u4eba\u8fd9\u79cd\u4e0d\u4fe1\u4efb\u7684\u6001\u5ea6\uff0c\u5f88\u611f\u4e0d\u6ee1\uff0c\u62ac\u8d77\u5927\u5934\uff0c \u6012\u76ee\u5411\u56db\u5468\u770b\u4e86\u4e00\u773c\u3002 \u3000\u3000\u9ad8\u6218\u4e70\u4e86\u4e00\u5305\u8349\u836f\uff0c\u7528\u6389\u6700\u540e\u4e00\u5757\u788e\u94f6\uff0c\u5fc3\u4e2d\u611f\u5230\u5f88\u662f\u51c4\u60e8\uff0c\u60f3\u5230\u7239\u7684\u75c5\uff0c\u4ee5\u53ca\u7239\u90a3\u79cd \u7edd\u671b\u7684\u773c\u5149\uff0c\u9ad8\u6218\u867d\u7136\u4e0d\u77e5\u4ed6\u5fc3\u4e2d\u60f3\u4e9b\u4ec0\u4e48\uff0c\u53ef\u662f\u90a3\u79cd\u9634\u6697\uff0c\u6f20\u7136\u7684\u773c\u795e\uff0c\u4f3c\u4e4e\u6709\u4e00\u79cd\u76f4\u89c9 \u544a\u8bc9\u4ed6\uff0c\u7239\u7684\u75c5\u662f\u4e0d\u4f1a\u597d\u7684\u4e86\uff0c\u66f4\u5927\u7684\u4e0d\u5e78\u6b63\u6162\u6162\u7684\u964d\u4e34\u3002\u4ed6\u4ece\u5c0f\u5c31\u5728\u8270\u82e6\u4e2d\u594b\u6597\uff0c\u5bf9\u4e8e\u4f5c \u6d3b\uff0c\u53ef\u771f\u662f\u4e00\u628a\u597d\u624b\uff0c\u5bf9\u5916\u5bf9\u5185\u4e5f\u80fd\u4e95\u4e95\u6709\u6761\uff0c\u53ef\u662f\u5012\u5e95\u5e74\u9f84\u592a\u5e7c\uff0c\u4e0d\u65f6\u8fd8\u4f1a\u8868\u9732\u51fa\u4e00\u79cd\u53ef \u8d35\u7684\u7ae5\u5fc3\uff0c\u53ef\u7231\u53c8\u53ef\u7b11\u7684\u5b69\u5b50\u6c14\uff0c\u4ed6\u7239\u7684\u6b63\u76f4\u6177\u6168\uff0c\u4ed6\u5988\u7684\u6148\u67d4\u53ef\u4eb2\u7684\u6027\u683c\uff0c\u90fd\u4e00\u80a1\u8111\u513f\u5230 \u4ed6\u8eab\u4e0a\uff0c\u662f\u4ee5\u4ed6\u89c1\u522b\u4eba\u5bcc\u6709\u4e5f\u4e0d\u611f\u7fa1\u6155\uff0c\u5bf9\u4e8e\u81ea\u5df1\u7684\u7a77\u82e6\u5e76\u4e0d\u89c9\u5f97\u53ef\u803b\uff0c\u6751\u4e2d\u6700\u6709\u94b1\u7684\u6797\u5bb6 \u4e8c\u4f4d\u5c0f\u5973\u5b69\uff0c\u90fd\u548c\u4ed6\u73a9\u5f97\u5f88\u878d\u6d3d\uff0c\u4ed6\u5e76\u672a\u611f\u5230\u4e1d\u6beb\u81ea\u5351\u7684\u5fc3\u7406\uff0c\u5728\u4ed6\u5c0f\u5c0f\u5fc3\u7075\u4e2d\uff0c\u89c9\u5f97\u4e3a\u7236 \u4eb2\u727a\u7272\u4e00\u5207\u90fd\u662f\u5e94\u8be5\u7684\uff0c\u5728\u4ed6\u5c0f\u5c0f\u5fc3\u7075\u4e2d\uff0c\u5305\u5bb9\u7740\u50cf\u6d77\u4e00\u822c\u7684\u7231\uff0c\u5c06\u6765\u6709\u4e00\u5929\uff0c\u4ed6\u4f1a\u4ee5\u7231\u6765 \u5bf9\u5f85\u6bcf\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u4ed6\u71ac\u597d\u4e86\u836f\uff0c\u6797\u59d1\u5a18\u8dd1\u6765\u53d6\u90a3\u7bc7\u6587\u7ae0\uff0c\u9ad8\u6218\u9053\uff1a\u201c\u8bf7\u4f60\u544a\u8bc9\u8001\u5e08\uff0c\u6211\u6700\u8fd1\u4e0d\u80fd\u53bb\u4e0a\u5b66 \u5802\u3002\u201d \u3000\u3000\u6797\u59d1\u5a18\u7b11\u9053\uff1a\u201c\u597d\uff0c\u8001\u5e08\u5929\u5929\u5938\u4f60\uff0c\u8981\u6211\u4eec\u5927\u4f19\u513f\u90fd\u8ddf\u4f60\u5b66\u54e9\uff01\u201d \u3000\u3000\u9ad8\u6218\u7ea2\u7740\u8138\u9053\uff1a\u201c\u4f60\u522b\u6367\u6211\uff0c\u4f60\u4e0b\u6b21\u8981\u4f5c\u4ec0\u4e48\uff0c\u6211\u4e00\u5b9a\u65e9\u65e9\u505a\u597d\u3002\u201d \u3000\u3000\u6797\u59d1\u5a18\u542c\u4ed6\u67d4\u58f0\u8bf4\u8bdd\uff0c\u60f3\u5230\u81ea\u5df1\u65e9\u4e0a\u5bf9\u4ed6\u65e0\u793c\uff0c\u5f88\u611f\u60ed\u6127\uff0c\u4fbf\u62c9\u5f00\u8bdd\u9898\u95ee\u9053\uff1a\u201c\u9ad8\u4f2f\u4f2f \u75c5\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u9ad8\u6218\u9eef\u7136\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u7239\u7684\u75c5\u8fd8\u662f\u90a3\u4e2a\u8001\u6837\u5b50\uff0c\u4e0d\u77e5\u54ea\u5929\u624d\u4f1a\u597d\u3002\u201d \u3000\u3000\u6797\u59d1\u5a18\u67d4\u58f0\u5b89\u6170\u9053\uff1a\u201c\u4f60\u522b\u6025\uff0c\u603b\u6709\u4e00\u5929\u4f1a\u597d\u7684\u3002\u201d \u3000\u3000\u63a5\u7740\u53c8\u9053\uff1a\u201c\u5582\uff0c\u6211\u8d70\u5566\uff0c\u4f60\u53ef\u5343\u4e07\u522b\u544a\u8bc9\u522b\u4eba\uff0c\u5426\u5219\u88ab\u8001\u5e08\u77e5\u9053\u8981\u6328\u624b\u5fc3\u7684\u3002\u201d \u3000\u3000\u9ad8\u6218\u89c1\u5979\u8138\u4e0a\u795e\u60c5\u8f7b\u677e\u6d3b\u6cfc\uff0c\u4e0d\u7531\u4e5f\u88ab\u5979\u611f\u67d3\uff0c\u5fc3\u4e2d\u5feb\u6d3b\u4e86\u4e00\u4e9b\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u6328\u8fc7\u8001\u5e08 \u7684\u677f\u5b50\uff1f\u201d \u3000\u3000\u6797\u59d1\u5a18\u70b9\u5934\u6b63\u8272\u9053\uff1a\u201c\u4e0a\u6b21\u6211\u80cc\u4e66\u80cc\u4e0d\u4e0a\uff0c\u54fc\uff0c\u8fd9\u4ef6\u4e8b\u4f60\u660e\u660e\u77e5\u9053\uff0c\u8fd8\u8981\u88c5\u50bb\uff0c\u5582\uff0c\u4f60 \u8fde\u6211\u59d0\u59d0\u90fd\u4e0d\u8981\u8bb2\uff0c\u77e5\u9053\u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u542c\u5979\u4ee5\u5927\u4eba\u53e3\u543b\u5429\u5490\uff0c\u5f88\u611f\u5230\u597d\u7b11\uff0c\u6545\u610f\u9053\uff1a\u201c\u5047\u5982\u544a\u8bc9\u4f60\u59d0\u59d0\u4e86\u5462\uff1f\u201d \u3000\u3000\u6797\u59d1\u5a18\u6b63\u60f3\u79bb\u53bb\uff0c\u95fb\u8a00\u55d4\u9053\uff1a\u201c\u9ad8\u6218\uff0c\u4f60\u6562\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u6218\u8038\u8038\u80a9\uff0c\u4e0d\u518d\u8a00\u8bed\uff0c\u5185\u5fc3\u5374\u60f3\u5230\uff1a\u201c\u6211\u4e3a\u4ec0\u4e48\u4e0d\u6562\uff1f\u201d \u3000\u3000\u51ac\u9633\u659c\u659c\u5730\u6652\u7740\u5927\u5730\uff0c\u4e00\u53ea\u8001\u6bcd\u9e21\u5e26\u7740\u4e00\u7fa4\u5ae9\u6bdb\u5c0f\u9e21\uff0c\u61d2\u6d0b\u6d0b\u7684\u8d70\u6765\u8d70\u53bb\uff0c\u4e0d\u65f6\u7528\u722a\u5228 \u571f\uff0c\u5bfb\u4e9b\u866b\u8c55\u8682\u8681\uff0c\u5582\u7ed9\u5c0f\u9e21\u5403\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u975e\u5e38\u7a7a\u865a\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u81ea\u89c9\u65e0\u8da3\uff0c\u4fbf\u56de\u5230\u5c4b\u4e2d\uff0c\u53d6\u4e86\u4e66\u672c\uff0c\u5750\u5728\u4eba\u6986\u6811\u4e0b\uff0c\u6717 \u6717\u7684\u8bfb\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6574\u4e2a\u51ac\u5929\uff0c\u5c31\u8fd9\u6837\u6c89\u6c89\u95f7\u95f7\u8fc7\u53bb\uff0c\u4e0b\u96ea\u540e\u5b69\u5b50\u4eec\u7684\u96ea\u6218\uff0c\u96ea\u540e\u7684\u56f4\u730e\uff0c\u9ad8\u6218\u90fd\u6ca1\u6709\u53c2 \u52a0\u3002\u7236\u4eb2\u7684\u75c5\u4e00\u5929\u91cd\u4f3c\u4e00\u5929\uff0c\u773c\u770b\u5944\u5944\u4e00\u606f\uff0c\u9ad8\u6218\u6bcf\u5929\u62fc\u547d\u53bb\u6211\u4e9b\u96f6\u5de5\u4f5c\uff0c\u8d5a\u94b1\u6765\u66ff\u4ed6\u7236\u4eb2 \u533b\u75c5\uff0c\u4eba\u5bb6\u89c1\u4ed6\u5e74\u5e7c\uff0c\u90e8\u51c6\u5907\u7eb7\u7eb7\u89e3\u56ca\uff0c\u9001\u4ed6\u4e00\u4e9b\u94f6\u5b50\uff0c\u53ef\u662f\u4ed6\u4e00\u60f3\u5230\u7239\u7239\u6b63\u76f4\u521a\u6b63\u7684\u6027 \u683c\uff0c\u8c06\u8c06\u7684\u6559\u8bad\uff0c\u4fbf\u4e0d\u6562\u63a5\u53d7\uff0c\u4ed7\u7740\u529b\u5927\u8eab\u8f7b\uff0c\u4ec0\u4e48\u7c97\u6d3b\u4ed6\u4e5f\u53bb\u5e72\u3002 \u3000\u3000\u82e6\u96be\u7684\u65e5\u5b50\u7ec8\u4e8e\u6765\u4e34\u4e86\uff0c\u4e00\u5929\u508d\u665a\uff0c\u5929\u4e0a\u5f64\u4e91\u6ee1\u5e03\uff0c\u6b63\u8981\u4e0b\u5927\u96ea\u7684\u5f81\u8c61\uff0c\u9ad8\u6218\u9a91\u7740\u201c\u8001 \u9ec4\u201d\u56de\u6765\uff0c\u53d1\u89c9\u7236\u4eb2\u5df2\u7ecf\u660f\u8ff7\u8fc7\u53bb\uff0c\u4ed6\u5927\u6025\u4e4b\u4e0b\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\uff0c\u53ea\u62b1\u7740\u7236\u4eb2\u7684\u5934\u75db\u54ed\u3002 \u3000\u3000\u4ed6\u54ed\u4e86\u4e00\u9635\uff0c\u9ad8\u4e91\u795e\u667a\u6e10\u6e05\uff0c\u81ea\u77e5\u4e0d\u4e45\u4eba\u4e16\uff0c\u5f88\u5403\u529b\u9053\uff1a\u201c\u6218\u513f\uff0c\u522b\u2026\u2026\u54ed\u2026\u2026\u54ed\u4e86\uff0c \u7239\u2026\u2026\u771f\u6015\u2026\u2026\u771f\u6015\u652f\u6301\u2026\u2026\u4e0d\u4f4f\uff0c\u5728\u4f60\u2026\u2026\u56de\u6765\u2026\u2026\u56de\u6765\u524d\u5c31\u2026\u2026\u5c31\u2026\u2026\u8981\u53bb\u4e86\uff0c\u73b0 \u5728\u2026\u2026\u73b0\u5728\u603b\u2026\u2026\u7b97\u597d\uff0c\u54b1\u7237\u513f\u4fe9\u2026\u2026\u8fd8\u53ef\u4ee5\u2026\u2026\u89c1\u4e00\u9762\u3002\u201d \u3000\u3000\u9ad8\u6218\u54ed\u9053\uff1a\u201c\u7239\uff0c\u4f60\u4e0d\u4f1a\u6b7b\uff0c\u60a8\u4e0d\u4f1a\u2026\u2026\u4e0d\u4f1a\u6b7b\u7684\u3002\u201d \u3000\u3000\u9ad8\u4e91\u5598\u606f\u4e00\u9635\uff0c\u5f3a\u5fcd\u7740\u8170\u95f4\u7684\u5267\u75db\uff0c\u60e8\u7136\u9053\uff1a\u201c\u7239\u4e5f\u77e5\u4f60\u5e74\u7eaa\u592a\u5c0f\uff0c\u53ef\u662f\u7239\u5b9e\u5728\u4e0d\u80fd\u652f \u6301\u4e0b\u53bb\u4e86\uff0c\u6218\u513f\uff0c\u7239\u4eca\u540e\u4e0d\u80fd\u518d\u7167\u987e\u4f60\u5566\uff0c\u6218\u513f\uff0c\u542c\u8bdd\uff0c\u5343\u4e07\u522b\u518d\u54ed\u4e86\uff0c\u7239\u8fd8\u6709\u8bdd\u7ed9\u4f60 \u8bf4\u3002\u201d \u3000\u3000\u4ed6\u4e00\u53e3\u6c14\u8bf4\u5b8c\u8fd9\u6bb5\u8bdd\uff0c\u611f\u5230\u7cbe\u795e\u7a81\u7136\u632f\u594b\u8d77\u6765\uff0c\u9ad8\u6218\u89c1\u7236\u4eb2\u8138\u4e0a\u7ea2\u66b4\u65f6\u9732\uff0c\u559c\u9053\uff1a \u201c\u7239\uff0c\u4f60\u597d\u4e9b\u4e86\uff0c\u4f60\u606f\u606f\u5427\uff01\u6211\u53bb\u627e\u533b\u751f\u53bb\u3002\u201d \u3000\u3000\u9ad8\u4e91\u77e5\u662f\u56de\u5149\u8fd1\u7167\uff0c\u4fbf\u6b63\u8272\u9053\uff1a\u201c\u6218\u513f\uff0c\u4f60\u624d\u516b\u5c81\uff0c\u4eca\u540e\u4e00\u4e2a\u4eba\u6d6a\u8ff9\u5929\u6daf\uff0c\u4e00\u5b9a\u8981\u65f6\u65f6 \u523b\u523b\u8bb0\u4f4f\u7239\u7684\u8bdd\uff0c\u6211\u4eec\u9ad8\u5bb6\u4e16\u4e16\u4ee3\u4ee3\u5fe0\u4e49\u4f20\u5bb6\uff0c\u4f60\u5fc5\u987b\u8981\u505a\u4e00\u4e2a\u8f70\u8f70\u70c8\u70c8\u7684\u4eba\u3002\u4f60\u5e74\u7eaa\u5c0f\uff0c \u6709\u65f6\u96be\u514d\u5584\u6076\u4e0d\u5206\uff0c\u4f46\u53ea\u8981\u8bb0\u5f97\u7239\u4e00\u53e5\u8bdd\uff1a\u5f85\u4eba\u539a\uff0c\u523b\u5df1\u8584\uff0c\u5fc3\u5b58\u5fe0\u539a\uff0c\u4e3a\u5584\u6700\u4e50\u3002\u6218\u513f\uff0c \u4f60\u61c2\u7239\u7684\u610f\u601d\u5417\uff1f\u201d \u3000\u3000\u6218\u51e0\u5929\u6027\u6df3\u539a\uff0c\u5fc3\u4e2d\u867d\u7136\u4e0d\u751a\u4e86\u89e3\uff0c\u4f46\u4e0d\u5fcd\u4ee4\u7236\u4eb2\u5931\u671b\uff0c\u70b9\u5934\u9053\uff1a\u201c\u7239\uff0c\u4f60\u653e\u5fc3\uff0c\u6218\u513f \u5168\u61c2\u4e86\u3002\u201d \u3000\u3000\u9ad8\u4e91\u67d4\u58f0\u9053\uff1a\u201c\u7239\u4f20\u4f60\u7684\u9ad8\u5bb6\u4e03\u4e03\u56db\u5341\u4e5d\u8def\u65e0\u654c\u621f\u6cd5\uff0c\u4f60\u518d\u6f14\u4e00\u904d\uff0c\u6218\u513f\uff0c\u4f7f\u53bb\u628a\u957f\u621f \u62ff\u6765\u3002\u201d \u3000\u3000\u9ad8\u6218\u867d\u4e0d\u613f\u7247\u523b\u79bb\u5f00\u7236\u4eb2\uff0c\u53ef\u662f\u53c8\u4e0d\u6562\u8fdd\u80cc\uff0c\u53ea\u5f97\u5feb\u6b65\u53bb\u53d6\uff0c\u53ea\u89c1\u4ed6\u4e00\u53ea\u624b\u62ff\u7740\u524d\u534a\u6bb5 \u621f\u8eab\uff0c\u53e6\u4e00\u624b\u62ff\u7740\u621f\u65a1\uff0c\u53cc\u624b\u4e00\u5408\uff0c\u5361\u5bdf\u4e00\u58f0\uff0c\u4fbf\u5408\u5728\u4e00\u8d77\u3002 \u3000\u3000\u539f\u6765\u8fd9\u957f\u4fc4\u5236\u4f5c\u751a\u662f\u7cbe\u5de7\uff0c\u5e73\u65e5\u53ef\u4ee5\u6298\u4e3a\u4e8c\u8282\uff0c\u4ee5\u4fbf\u643a\u5e26\uff0c\u800c\u4e14\u524d\u534a\u6bb5\u53ef\u5f53\u5200\u65a7\u4f7f\uff0c\u5728 \u77ed\u5175\u76f8\u63a5\u65f6\uff0c\u6700\u662f\u9002\u7528\uff0c\u5982\u679c\u9047\u5230\u51b2\u950b\u9677\u9635\uff0c\u53ea\u6d88\u4e00\u6309\u673a\u7c27\uff0c\u4fbf\u6210\u957f\u5175\uff0c\u6210\u4e3a\u9a6c\u4e0a\u5229\u5668\uff0c\u90a3 \u621f\u950b\u4ece\u5357\u5b8b\u5df2\u6765\uff0c\u4e0d\u77e5\u996e\u4e86\u591a\u5c11\u4eba\u8840\uff0c\u662f\u4ee5\u6de1\u6de1\u53d1\u51fa\u4e00\u5c42\u8840\u5149\u3002 \u3000\u3000\u9ad8\u6218\u5f3a\u5fcd\u5fc3\u4e2d\u54c0\u75db\uff0c\u7ad9\u5728\u95e8\u53e3\u4e00\u62db\u4e00\u5f0f\u821e\u4e86\u8d77\u6765\uff0c\u9ad8\u4e91\u6491\u8d77\u8eab\u6765\uff0c\u51dd\u795e\u6ce8\u76ee\uff0c\u5f85\u5230\u9ad8\u6218 \u4f7f\u5b8c\u56db\u5341\u4e5d\u62db\uff0c\u4ed6\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u53cc\u624b\u4e00\u677e\uff0c\u53c8\u5012\u5728\u5e8a\u4e0a\u3002 \u3000\u3000\u9ad8\u6218\u6025\u6025\u8d70\u5230\u5e8a\u8fb9\uff0c\u628a\u957f\u621f\u5411\u5e8a\u5934\u4e00\u653e\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u4ed6\u7236\u4eb2\u5598\u606f\u9053\uff1a\u201c\u6218\u513f\uff0c\u4f60\u5929\u8d44\u5f88 \u597d\uff0c\u5b66\u8d77\u6b66\u6765\u6210\u5c31\u4e0d\u5b9a\u6bd4\u7239\u9ad8\u5f97\u591a\uff0c\u5728\u2026\u2026\u5728\u8fd9\u2026\u2026\u5175\u8352\u2026\u2026\u9a6c\u4e71\u2026\u2026\u7684\u65f6\u5019\u2026\u2026\u5b66\u6b66\u2026\u2026 \u5b66\u6b66\u2026\u2026\u6bd4\u2026\u2026\u6bd4\u5b66\u6587\u8981\u597d\uff0c\u6211\u2026\u2026\u6b7b\u2026\u2026\u6b7b\u4e86\u540e\u2026\u2026\u4f60\u2026\u2026\u4f60\u628a\u2026\u2026\u4e00\u5207\u2026\u2026\u4e00\u5207\u90fd\u5356 \u4e86\uff0c\u56de\u2026\u2026\u56de\u5230\u8001\u5bb6\u2026\u2026\u8001\u5bb6\u2026\u2026\u53bb\uff0c\u5982\u679c\uff0c\u80fd\u2026\u2026\u80fd\u518d\u78b0\u5230\u2026\u2026\u518d\u78b0\u5230\u90a3\u4f20\u4f60\u5185\u529f\u7684\u5947 \u4eba\uff0c\u5c31\u2026\u2026\u8ddf\u4ed6\u2026\u2026\u8ddf\u4ed6\u53bb\u5b66\u529f\u592b\uff0c\u5c06\u6765\u2026\u2026\u597d\u4e3a\u56fd\u5bb6\u505a\u4e00\u756a\u2026\u2026\u5927\u2026\u2026\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u6218\u773c\u770b\u7236\u4eb2\u6108\u6765\u6108\u4e0d\u6210\u4e86\uff0c\u5fc3\u5185\u4e0d\u77e5\u6240\u63aa\uff0c\u53ea\u6709\u5f3a\u5fcd\u773c\u6cea\u70b9\u5934\u7b54\u5141\u3002\u9694\u4e86\u4e00\u4f1a\u9ad8\u4e91\u53c8 \u9053\uff1a\u201c\u6218\u513f\uff0c\u4f60\u2026\u2026\u8d70\u8fd1\u4e9b\uff0c\u8ba9\u2026\u2026\u8ba9\u7239\u518d\u77a7\u77a7\u3002\u201d \u3000\u3000\u9ad8\u6218\u518d\u4e5f\u63a7\u5236\u4e0d\u4f4f\uff0c\u6cea\u5982\u96e8\u4e0b\uff0c\u4ed6\u7236\u4eb2\u4f38\u51fa\u4e24\u53ea\u65e0\u529b\u7684\u624b\uff0c\u6367\u7740\u9ad8\u6218\u7684\u5934\uff0c\u76ee\u5149\u4e2d\u6d41\u9732 \u7740\u5343\u822c\u6148\u7231\uff0c\u5583\u5583\u9053\uff1a\u201c\u6218\u513f\uff0c\u7239\u8981\u2026\u2026\u7239\u8981\u53bb\u4e86\uff0c\u4f60\u597d\u5c0f\uff0c\u597d\u5c0f\u554a\uff01\u201d \u3000\u3000\u9ad8\u6218\u611f\u5230\u7236\u4eb2\u53cc\u624b\u6e10\u6e10\u677e\u5f00\u4e86\uff0c\u53e3\u5507\u98a4\u52a8\uff0c\u50cf\u662f\u8981\u8bf4\u4ec0\u4e48\uff0c\u9ad8\u6218\u54ed\u9053\uff1a\u201c\u7239\uff0c\u4f60\u8981\u8bf4\u4ec0 \u4e48\uff1f\u201d \u3000\u3000\u201c\u56fd\u7834\u2026\u2026\u5bb6\u2026\u2026\u5bb6\u4ea1\u2026\u2026\u5fe0\u2026\u2026\u5b5d\u2026\u2026\u5fe0\u5b5d\u2026\u2026\u5723\u8d24\u2026\u2026\u4e4b\u2026\u2026\u5bb6\u6cd5\u3002\u201d \u3000\u3000\u9ad8\u4e91\u7528\u5c3d\u529b\u6c14\uff0c\u4ece\u5589\u5499\u4e2d\u5410\u51fa\u8fd9\u53e5\u8bdd\uff0c\u773c\u775b\u4e00\u95ed\uff0c\u64a4\u624b\u800c\u901d\u3002 \u3000\u3000\u597d\u957f\u4e00\u6bb5\u5bc2\u9759\uff0c\u9ad8\u6218\u5446\u5446\u671b\u7740\u8fc7\u53bb\u4e86\u7684\u7236\u4eb2\uff0c\u4ed6\u4e0d\u76f8\u4fe1\u90a3\u662f\u771f\u7684\u2014\u2014\u7136\u800c\u90a3\u6bd5\u7adf\u662f\u771f\u7684 \u6b7b\u3002\u8fd9\u662f\u5343\u4e07\u5e74\u6765\uff0c\u4ece\u65e0\u4eba\u8d85\u8d8a\u7684\u5927\u9650\uff0c\u591a\u5c11\u76d6\u4e16\u6765\u6770\u5230\u5934\u6765\u603b\u514d\u4e0d\u4e86\u5c48\u670d\u5728\u8fd9\u65e0\u6cd5\u8fc7\u8fc7\u7684 \u5173\u53e3\u3002 \u3000\u3000\u4ed6\u611f\u89c9\u81ea\u5df1\u773c\u524d\u662f\u4e00\u7247\u9ed1\u6697\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u6b63\u5411\u65e0\u5e95\u7684\u6df1\u6e0a\u4e2d\u5760\u843d\uff0c\u4eb2\u7231\u7684\u4eba\u513f\uff0c\u4e00\u4e2a\u4e2a \u5fcd\u5fc3\u7684\u79bb\u5f00\u4ed6\uff0c\u800c\u4e14\uff0c\u8d70\u5f97\u8fdc\u8fdc\u7684\uff0c\u4f7f\u4ed6\u6c38\u8fdc\u65e0\u6cd5\u518d\u8ffd\u5f97\u4e0a\u3002 \u3000\u3000\u4ed6\u5e74\u7eaa\u867d\u5e7c\uff0c\u53ef\u662f\u60c5\u611f\u6781\u662f\u4e30\u5bcc\uff0c\u6bcd\u4eb2\u6b7b\u65f6\uff0c\u4ed6\u8fd8\u4e0d\u503c\u5f97\u60b2\u54c0\uff0c\u4ee5\u4e3a\u6bcd\u4eb2\u662f\u7761\u7740\u4e86\uff0c\u53ef \u662f\uff0c\u5982\u4eca\u4ed6\u5fc3\u5e95\u656c\u7231\u7684\u7239\u53c8\u642c\u624b\u800c\u53bb\uff0c\u8fd9\u79cd\u60b2\u75db\u6c89\u91cd\u7684\u6253\u51fb\uff0c\u76f4\u4f7f\u4ed6\u4e0d\u77e5\u6240\u4e3a\uff0c\u8fde\u54ed\u90fd\u5fd8\u8bb0 \u4e86\u3002 \u3000\u3000\u4ed6\u4eff\u4f5b\u542c\u5230\u4e86\u4e5d\u5929\u4e4b\u4e0a\u6709\u9635\u9635\u54c0\u4e50\u4f20\u4e0b\u6765\uff0c\u662f\u90a3\u4e48\u60a0\u626c\uff0c\u90a3\u4e48\u9065\u8fdc\uff0c\u5239\u65f6\u95f4\uff0c\u4ece\u4ed6\u5fc3\u5e95 \u7684\u6df1\u5904\u4e5f\u8baf\u8d77\u4e86\u4f4e\u6c89\u54c0\u75db\u7684\u65cb\u5f8b\u3002 \u3000\u3000\u4e00\u5207\u90fd\u662f\u771f\u7684\uff0c\u4ed6\u7528\u529b\u63ea\u4e86\u4e00\u4e0b\u5927\u817f\uff0c\u8bc1\u5b9e\u4e86\u90a3\u4e0d\u662f\u68a6\u5883\uff0c\u7236\u4eb2\u82cd\u767d\u88ab\u75c5\u6298\u78e8\u800c\u67af\u7626\u7684 \u8138\u4e0a\uff0c\u867d\u7136\u4e24\u76ee\u95ed\u5f97\u7d27\u7d27\u7684\uff0c\u53ef\u662f\u8fd8\u6d41\u9732\u51fa\u4e00\u79cd\u6b63\u76f4\u4e0d\u5c48\u548c\u5927\u65e0\u754f\u7684\u795e\u8272\uff0c\u4ed6\u98de\u5feb\u7684\u77a5\u4e86\u4e00 \u773c\uff0c\u539f\u6765\u5c31\u6df1\u523b\u5728\u8111\u6d77\u4e2d\u7684\u5370\u8c61\uff0c\u53c8\u50cf\u518d\u91cd\u65b0\u523b\u753b\u4e00\u904d\uff0c\u66f4\u6e05\u6670\uff0c\u66f4\u6df1\u523b\u4e86\uff0c\u5341\u5e74\uff0c\u5eff\u5e74\uff0c \u5728\u4ed6\u6709\u751f\u4e4b\u5e74\uff0c\u7236\u4eb2\u7684\u97f3\u5bb9\u90a3\u5c06\u4e0d\u518d\u4f1a\u88ab\u65f6\u5149\u4e4b\u6d41\u51b2\u6de1\uff0c\u5149\u9634\uff0c\u53ea\u80fd\u52a0\u6df1\u5b83\u7684\u3002 \u3000\u3000\u84e6\u7684\uff0c\u80cc\u540e\u4e00\u53ea\u624b\u8f7b\u62cd\u7740\u4ed6\u7684\u53cc\u80a9\uff0c\u4e00\u4e2a\u6e29\u548c\u7684\u58f0\u97f3\u9053\uff1a\u201c\u9ad8\u8d24\u4f84\uff0c\u6b7b\u8005\u5df2\u53bb\uff0c\u4f60\u8fd9\u6837 \u54c0\u75db\u6700\u662f\u4f24\u8eab\uff0c\u4f60\u7239\u5728\u5730\u4e0b\u4e5f\u4f1a\u611f\u5230\u75db\u5fc3\u7684\u3002\u201d \u3000\u3000\u539f\u6765\u6797\u5bb6\u4e8c\u59ca\u59b9\u672c\u60f3\u8fd9\u9ad8\u6218\u53bb\u6349\u87cb\u87c0\uff0c\u5979\u4fe9\u7ad9\u5728\u95e8\u53e3\u8bd5\u4e86\u4e24\u58f0\uff0c\u9ad8\u6218\u6709\u5982\u672a\u95fb\uff0c\u59ca\u59b9\u4e24 \u5fc3\u4e2d\u5927\u5947\uff0c\u4f38\u5934\u5e7f\u770b\uff0c\u53ea\u89c1\u9ad8\u6218\u5750\u5728\u5e8a\u8fb9\uff0c\u76ee\u5149\u75f4\u5446\uff0c\u826f\u4e45\u4e5f\u4e0d\u89c1\u4ed6\u7728\u4e00\u4e0b\uff0c\u4e0d\u7981\u5927\u60e7\uff0c\u5306 \u5306\u5fd9\u5fd9\u53bb\u544a\u8bc9\u7239\u7239\uff0c\u6797\u8001\u7237\u4e00\u542c\uff0c\u5fc3\u5185\u4e86\u7136\uff0c\u4ed6\u611f\u5230\u5f88\u662f\u51c4\u60e8\uff0c\u9ad8\u6218\u5728\u8fd9\u201c\u6986\u5e84\u201d\uff0c\u6ca1\u6709\u4e00 \u4e2a\u4eba\u4e0d\u559c\u6b22\u4ed6\uff0c\u6797\u8001\u7237\u66f4\u662f\u7231\u4ed6\u5f97\u7d27\uff0c\u662f\u4ee5\u6025\u5fd9\u8d76\u6765\u529d\u6170\u3002 \u3000\u3000\u9ad8\u6218\u8f6c\u8fc7\u5934\u4e00\u770b\uff0c\u4e09\u53cc\u6e29\u67d4\u601c\u60af\u7684\u76ee\u5149\u6ce8\u89c6\u7740\u4ed6\uff0c\u5fc3\u5185\u7a81\u611f\u6e29\u6696\uff0c\u50cf\u662f\u5373\u5c06\u6eba\u6b7b\u4f2a\u4eba\uff0c \u7a81\u7136\u6500\u9644\u5230\u4efb\u4f55\u53ef\u501f\u529b\u7684\u4e1c\u897f\uff0c\u62b1\u7740\u6797\u8001\u7237\uff0c\u518d\u4e5f\u6309\u637a\u4e0d\u4f4f\uff0c\u54c0\u54c0\u75db\u54ed\u8d77\u6765\u3002 \u3000\u3000\u6797\u8001\u7237\u770b\u7740\u6000\u4e2d\u4fca\u79c0\u7684\u5b69\u5b50\u3002\u4e24\u8ddf\u7ea2\u80bf\uff0c\u8138\u4e0a\u6d95\u6cd7\u6cc5\u6a2a\u6821\uff0c\u5fc3\u5185\u53c8\u601c\u53c8\u7231\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e00 \u54ed\u5bf9\u9ad8\u6218\u6709\u76ca\u65e0\u5bb3\uff0c\u53ef\u4ee5\u628a\u90a3\u90c1\u79ef\u5728\u80f8\u4e2d\u54c0\u4f24\u5168\u90fd\u53d1\u6cc4\uff0c\u6240\u4ee5\u53ea\u662f\u4efb\u4ed6\u54ed\u53bb\u3002 \u3000\u3000\u90a3\u6797\u6c0f\u59ca\u59b9\uff0c\u5e73\u65e5\u867d\u7136\u80c6\u5927\u5fc3\u7c97\uff0c\u6b64\u65f6\u89c1\u9ad8\u6218\u54ed\u5f97\u54c0\u54c0\u6b32\u7edd\uff0c\u4e5f\u4e0d\u89c9\u6d41\u4e0b\u540c\u60c5\u4e4b\u6cea\u3002 \u3000\u3000\u826f\u4e45\uff0c\u9ad8\u6218\u89c9\u5f97\u80f8\u4e2d\u6bd4\u8f83\u677e\u7545\uff0c\u4fbf\u6536\u6cea\u9053\uff1a\u201c\u6797\u4f2f\u4f2f\uff0c\u7239\u53eb\u6211\u5728\u4ed6\u6b7b\u540e\uff0c\u56de\u5230\u8001\u5bb6\u5c71\u897f \u53bb\uff0c\u5c0f\u4f84\u6709\u4e2a\u8ba1\u8f83\uff0c\u60f3\u5c06\u7239\u7239\u5c38\u9aa8\u8fd0\u56de\u5bb6\u4e61\uff0c\u4e0e\u5a18\u5408\u846c\u5728\u4e00\u8d77\u3002\u201d \u3000\u3000\u6797\u4e4b\u7237\u9053\uff1a\u201c\u5c71\u897f\u79bb\u6b64\uff0c\u5343\u5c71\u4e07\u6c34\uff0c\u4f60\u5e74\u7eaa\u8fd9\u4e48\u5c0f\uff0c\u8fd8\u8981\u62a4\u9001\u9ad8\u8001\u5f1f\u7684\u7075\u68fa\uff0c\u771f\u662f\u8c08\u4f55 \u5bb9\u6613\u3002\u201d \u3000\u3000\u9ad8\u6218\u51c4\u7136\u8ffd\uff1a\u201c\u5148\u7236\u4e5f\u6599\u5230\u6b64\uff0c\u4ed6\u5429\u5490\u6211\u5c06\u4ed6\u9057\u9aa8\u706b\u5316\uff0c\u7528\u575b\u5b50\u88c5\u4e86\uff0c\u8fd9\u6837\u5e26\u5230\u5c71 \u897f\u3002\u201d \u3000\u3000\u6797\u8001\u7237\u9053\uff1a\u201c\u5165\u5173\u7684\u8def\u6700\u8fd1\u53ef\u4e0d\u5927\u5b81\u9759\uff0c\u76d7\u8d3c\u6563\u5175\u904d\u5730\u5982\u6bdb\uff0c\u4f60\u4e00\u4e2a\u4eba\u5b64\u8eab\u6b65\u884c\u81f3\u4e07\u91cc \u5916\uff0c\u53ea\u6015\u5f88\u662f\u8270\u96be\uff0c\u4f9d\u6211\u770b\u4f7f\u4e0d\u5982\u628a\u4f60\u7236\u4eb2\u846c\u4e86\uff0c\u5c31\u4f4f\u5728\u6211\u5bb6\uff0c\u7b49\u957f\u5927\u4e9b\uff0c\u518d\u56de\u6545\u4e61\u4e0d \u8fdf\u3002\u201d \u3000\u3000\u6797\u6c0f\u59ca\u59b9\u4e2d\u5927\u59ca\u6797\u6c76\u9053\uff1a\u201c\u9ad8\u5927\u53ef\uff0c\u4f60\u7559\u4e0b\u548c\u6211\u4eec\u4e00\u5757\u513f\u8bfb\u4e66\u73a9\u800d\u4e0d\u597d\u4e48\uff1f\u201d \u3000\u3000\u5c0f\u59b9\u59b9\u6797\u7389\u4e5f\u529d\u4ed6\u7559\u4e0b\u3002 \u3000\u3000\u9ad8\u6218\u6bc5\u7136\u9053\uff1a\u201c\u591a\u8c22\u6797\u4f2f\u4f2f\u53ca\u4e8c\u4f4d\u59d1\u5a18\u7684\u597d\u610f\uff0c\u5148\u7236\u66fe\u7ecf\u5429\u5490\u6211\u8981\u51fa\u5916\u78e8\u7ec3\uff0c\u8bbf\u5e08\u5b66 \u6b66\uff0c\u6240\u4ee5\u5c0f\u4f84\u4e0d\u6562\u3002\u201d \u3000\u3000\u6797\u4f2f\u4f2f\u8d5e\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u6709\u5fd7\u6c14\u3002\u201d \u3000\u3000\u6797\u7389\u77aa\u4ed6\u4e00\u773c\uff0c\u4f3c\u4e4e\u602a\u4ed6\u4e0d\u8bc6\u597d\u6b79\uff0c\u6797\u6c76\u779f\u4e86\u4ed6\u4e00\u773c\uff0c\u9732\u51fa\u9eef\u7136\u7684\u795e\u8272\u3002 \u3000\u3000\u4ed6\u5fc3\u4e00\u8f6f\uff0c\u4f46\u53c8\u60f3\u8d77\u7236\u4eb2\u4e34\u7ec8\u7684\u5631\u5490\uff0c\u5fc3\u5185\u6697\u81ea\u53d1\u8a93\u9053\uff1a\u201c\u9ad8\u6218\u554a\uff0c\u5c31\u662f\u5343\u5c71\u4e07\u6c34\uff0c\u5343 \u5200\u4e07\u7bad\u5728\u524d\uff0c\u4f60\u4e5f\u8981\u628a\u7239\u7684\u9aa8\u7070\u8fd0\u5230\u5bb6\u4e61\u53bb\u3002\u201d \u3000\u3000\u6797\u8001\u7237\u89c1\u4ed6\u5ffd\u9732\u51db\u7136\u4e4b\u8272\uff0c\u77e5\u4ed6\u610f\u5df2\u51b3\uff0c\u4fbf\u4e0d\u518d\u8a00\u8bed\uff0c\u5e26\u7740\u59ca\u59b9\u4e8c\u4eba\u79bb\u53bb\u3002 \u3000\u3000\u9ad8\u6545\u5fc3\u4e2d\u76d8\u6253\uff0c\u7236\u4eb2\u7684\u8bdd\u53c8\u98d8\u5230\u8033\u8fb9\uff1a\u201c\u628a\u4e00\u5207\u4e1c\u897f\u90fd\u5356\u4e86\u2026\u2026\u201d \u3000\u3000\u4ed6\u7684\u601d\u60f3\u7a81\u7136\u53d8\u5f97\u5f88\u6563\u4e71\uff0c\u5bb6\u4e2d\u9664\u4e86\u4e09\u95f4\u7834\u8305\u5c4b\uff0c\u51e0\u767e\u65a4\u9ad8\u7cb1\u5916\u771f\u662f\u4e00\u65e0\u6240\u6709\u4e86\uff0c\u552f\u4e00 \u503c\u94b1\u7684\u662f\u4ec0\u4e48\uff1f\u4ed6\u52aa\u529b\u53bb\u907f\u514d\u60f3\u8fd9\u4e2a\u95ee\u9898\uff0c\u6240\u4ee5\u601d\u60f3\u7a81\u7136\u53d8\u5f97\u5f88\u89c9\u6f2b\u6563\uff0c\u7136\u800c\u6700\u540e\u601d\u60f3\u7684\u7126 \u70b9\u53c8\u843d\u5728\u8fd9\u4e2a\u95ee\u9898\u4e0a\u3002 \u3000\u3000\u201c\u53ea\u6709\u2018\u8001\u9ec4\u2019\uff0c\u624d\u503c\u5f97\u4e9b\u94b1\u3002\u201d\u4ed6\u6700\u540e\u5583\u5583\u81ea\u8bed\u9053\uff0c\u201c\u53ef\u662f\uff0c\u2018\u8001\u9ec4\u2019\u8ddf\u7740\u6211\u4eec\u5df2\u7ecf \u56db\u4e94\u5e74\u4e86\uff0c\u5b83\u8f9b\u8f9b\u82e6\u82e6\u5de5\u4f5c\uff0c\u8f7d\u91cd\u8d1f\u8377\uff0c\u4ece\u6765\u6ca1\u6709\u534a\u70b9\u53cd\u6297\uff0c\u6211\u2026\u2026\u6211\u600e\u4e48\u5fcd\u5fc3\u5462\uff1f\u201d \u3000\u3000\u4ed6\u89c9\u5f97\u5fc3\u623f\u50cf\u7ed9\u9488\u523a\u4e86\u4e00\u4e0b\uff0c\u5bf9\u4e8e\u81ea\u5df1\u8fd9\u79cd\u5351\u9119\u7684\u60f3\u6cd5\u5f88\u662f\u60ed\u6127\u3002 \u3000\u3000\u201c\u518d\u600e\u6837\uff0c\u4e5f\u4e0d\u80fd\u628a\u2018\u8001\u9ec4\u2019\u5356\u4e86\u3002\u201d\u4ed6\u4e0b\u4e86\u51b3\u5fc3\u3002 \u3000\u3000\u201c\u8001\u9ec4\u201d\u6b63\u5728\u8305\u5c4b\u56db\u5468\u8d70\u6765\u8d70\u53bb\uff0c\u4e00\u9897\u5de8\u5927\u7684\u725b\u5934\u4e0d\u65f6\u4f38\u8fdb\u7a97\u53e3\uff0c\u6ce8\u89c6\u7740\u6c89\u601d\u7684\u5c0f\u4e3b \u4eba\uff0c\u663e\u7136\u7684\uff0c\u5bf9\u4e8e\u8001\u4e3b\u4eba\u7684\u6b7b\uff0c\u4ee5\u53ca\u5c0f\u4e3b\u4eba\u7684\u60b2\u54c0\uff0c\u5b83\u5fc3\u4e2d\u90fd\u660e\u767d\u5f97\u5f88\uff0c\u53ea\u53ef\u60dc\u4e0d\u80fd\u8bf4\u8bdd\u5b89 \u6170\uff0c\u6240\u4ee5\u663e\u5f97\u5f88\u6025\u8dfa\uff0c\u6700\u540e\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u4f4e\u543c\u4e24\u58f0\u3002 \u3000\u3000\u9ad8\u6218\u95fb\u58f0\u8dd1\u51fa\uff0c\u629a\u6478\u7740\u201c\u8001\u9ec4\u201d\uff0c\u5fc3\u4e2d\u771f\u662f\u601c\u7231\u4e07\u5206\uff0c\u201c\u8001\u9ec4\u201d\u4f0f\u4e0b\u8eab\uff0c\u4eb2\u6635\u7684\u8210\u7740\u9ad8 \u6218\u7684\u811a\u3002 \u3000\u3000\u706b\u5149\u718a\u718a\uff0c\u9ad8\u6218\u6ce8\u89c6\u7740\u7236\u4eb2\u7684\u9057\u4f53\u6e10\u6e10\u6d88\u5931\uff0c\u611f\u5230\u6b64\u751f\u518d\u65e0\u6240\u5e87\u836b\uff0c\u524d\u8fd9\u832b\u832b\uff0c\u4e0d\u7531\u53c8 \u60ca\u53c8\u75db\u3002 \u3000\u3000\u706b\u5149\u4e2d\uff0c\u4ed6\u81f3\u7231\u7684\u4eba\u6700\u540e\u53d8\u6210\u4e00\u5806\u7070\uff0c\u4ed6\u770b\u770b\u56db\u5468\u6751\u4eba\u90fd\u5e26\u7740\u60cb\u60dc\u6c89\u75db\u7684\u8ddf\u5149\uff0c\u4e0d\u7981\u9ed8 \u9ed8\u7948\u7977\u9053\uff1a\u201c\u7239\uff0c\u4f60\u5b89\u5fc3\u5427\uff0c\u597d\u4eba\u603b\u662f\u4e0d\u5bc2\u5bde\u7684\u3002\u201d \u3000\u3000\u4eba\u4eec\u6e10\u6e10\u79bb\u53bb\uff0c\u4ed6\u7ad9\u8d77\u8eab\u6765\uff0c\u628a\u9aa8\u7070\u653e\u5728\u575b\u5b50\u5185\uff0c\u56de\u5934\u4e00\u770b\uff0c\u201c\u8001\u9ec4\u201d\u725b\u773c\u4e2d\u4e5f\u95ea\u7740\u6676 \u83b9\u7684\u6cea\u5149\u3002 \u3000\u3000\u9ad8\u6218\u628a\u8305\u5c4b\u53ca\u4e00\u5207\u4e1c\u897f\u90fd\u5356\u4e86\uff0c\u53ef\u662f\u53ea\u591f\u4ed6\u507f\u8fd8\u7236\u4eb2\u5728\u751f\u4e4b\u65e5\u6240\u6b20\u7684\u533b\u836f\u8d39\uff01\u90a3\u662f\u4ed6\u4e00 \u76f4\u7792\u667a\u7236\u4eb2\u501f\u7684\u3002 \u3000\u3000\u522b\u4eba\u867d\u7136\u4e0d\u8981\u4ed6\u8fd8\uff0c\u53ef\u662f\u4ed6\u4e00\u60f3\u5230\u7236\u4eb2\u5e73\u65e5\u4e0d\u6c42\u4eba\u7684\u6027\u513f\uff0c\u89c9\u5f97\u81ea\u5df1\u4e0d\u80fd\u6709\u788d\u9ad8\u5bb6\u95e8 \u6237\uff0c\u518d\u5927\u7684\u82e6\u96be\uff0c\u4e5f\u8981\u4e00\u4e2a\u4eba\u53bb\u627f\u62c5\uff0c\u6240\u4ee5\u4ed6\u5584\u610f\u7684\u62d2\u7edd\u4e86\u6797\u4f2f\u4f2f\u7684\u8d60\u91d1\u3002 \u3000\u3000\u7275\u7740\u725b\uff0c\u4ed6\u4e00\u6b65\u4e00\u6b65\u8d70\u79bb\u201c\u6986\u5e84\u201d\uff0c\u5927\u5bb6\u770b\u7740\u4ed6\u77ee\u5c0f\u7684\u8eab\u5f62\u8fd8\u4e0d\u53ca\u201c\u8001\u9ec4\u201d\u9ad8\uff0c\u90fd\u4e0d\u7981 \u60e8\u7136\uff0c\u6447\u5934\u53f9\u9053\uff1a\u201c\u5509\uff0c\u8fd9\u5b69\u5b50\u3002\u201d \u3000\u3000\u9ad8\u6218\u56de\u8fc7\u5934\uff0c\u6797\u5bb6\u8fd8\u672a\u79bb\u5f00\uff0c\u6797\u4f2f\u4f2f\u548c\u4ed6\u4e24\u4e2a\u5973\u513f\u6325\u7740\u624b\uff0c\u4ed6\u7a81\u611f\u5fc3\u9178\uff0c\u773c\u89d2\u6d6e\u8d77\u6cea \u73e0\uff0c\u4f46\u8f6c\u5ff5\u60f3\u5230\u7236\u4eb2\u5e38\u5e38\u8bf4\u7684\u4e00\u53e5\u8bdd\u201c\u4e08\u592b\u6d41\u8840\u4e0d\u6d41\u6cea\u3002\u201d\u8d76\u7d27\u6536\u6cea\uff0c\u518d\u4e0d\u56de\u5934\uff0c\u6108\u8d70\u6108\u8fdc \u4e86\u3002 \u3000\u3000\u6797\u6c76\u3001\u6797\u7389\u770b\u5230\u9ad8\u6218\u8eab\u5f62\u6d88\u5931\u5728\u539f\u91ce\u4e0a\uff0c\u60f3\u5230\u9ad8\u6218\u5e73\u65e5\u5bf9\u81ea\u5df1\u7684\u8bf8\u822c\u597d\u5904\uff0c\u5fcd\u4e0d\u4f4f\u53cc\u53cc \u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u6797\u4f2f\u4f2f\u9053\uff1a\u201c\u4e56\u5973\u513f\uff0c\u522b\u54ed\u4e86\uff0c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u6797\u7389\u6b62\u6cea\u95ee\u9053\uff1a\u201c\u7239\uff0c\u9ad8\u2026\u2026\u9ad8\u5927\u54e5\u8981\u51e0\u65f6\u624d\u56de\u6765\u3002\u201d \u3000\u3000\u6797\u4f2f\u4f2f\u58f0\u5b89\u6170\u9053\uff1a\u201c\u4e56\u5973\u513f\uff0c\u4f60\u9ad8\u5927\u54e5\u662f\u4e2a\u6781\u6709\u5fd7\u6c14\u7684\u5b69\u5b50\uff0c\u5fc3\u5730\u53c8\u6148\u5584\u65e0\u6bd4\uff0c\u5c06\u6765\u4e00 \u5b9a\u4f1a\u6210\u4e86\u4e0d\u8d77\u7684\u4eba\u3002\u201d \u3000\u3000\u6797\u6c76\u4f4e\u58f0\u9053\uff1a\u201c\u4ed6\u2026\u2026\u4ed6\u4f1a\u4e0d\u4f1a\u6068\u6211\u548c\u59b9\u59b9\u5462\uff1f\u6211\u4eec\u5e73\u5e38\u2026\u2026\u5e73\u5e38\u5f85\u4ed6\u5f88\u51f6\uff0c\u5f88\u4e0d \u597d\u3002\u201d \u3000\u3000\u6797\u4f2f\u4f2f\u5475\u5475\u7b11\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u4f60\u65e2\u7136\u540e\u6094\u5f85\u4eba\u5bb6\u4e0d\u597d\uff0c\u90a3\u4e48\u4ece\u4eca\u4ee5\u540e\uff0c\u5bf9\u4e8e\u4f60\u7684\u670b\u53cb\u4fbf \u4e0d\u80fd\u518d\u4efb\u6027\u4e86\uff0c\u514d\u5f97\u522b\u4eba\u8d70\u540e\uff0c\u4f60\u53c8\u6094\u6068\u81ea\u5df1\u3002\u9ad8\u8d24\u4f84\u5e74\u9f84\u867d\u5c0f\uff0c\u53ef\u662f\u6c14\u5ea6\u5bbd\u5b8f\uff0c\u4ed6\u600e\u4f1a\u8bb0 \u5728\u5fc3\u4e0a\uff0c\u4e5f\u8bb8\u4f60\u4eec\u5e73\u65e5\u7684\u6076\u4f5c\u5267\uff0c\u4f1a\u4f7f\u4ed6\u6c38\u8fdc\u6000\u5ff5\u54e9\uff01\u201d \u3000\u3000\u6797\u5bb6\u59ca\u59b9\u7ea2\u7740\u8138\u542c\u4ed6\u7239\u6e29\u548c\u7684\u6559\u8bad\uff0c\u6797\u8001\u7237\u611f\u5230\u5f88\u5947\u602a\uff0c\u5e73\u65f6\u5201\u94bb\u7684\u4e8c\u4e2b\u5934\u4e5f\u4e00\u8a00\u4e0d \u53d1\uff0c\u4f4e\u5934\u542c\u8bad\uff0c\u5fc3\u4e2d\u7a81\u7136\u95ea\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff0c\u8138\u4e0a\u4e0d\u7531\u62d3\u51fa\u795e\u79d8\u7684\u7b11\u5bb9\uff0c\u6697\u9053\uff1a\u201c\u5b69\u5b50\u4e8b\uff0c\u5b69\u5b50 \u4e8b\uff01\u201d \u3000\u3000\u4e14\u8bf4\u9ad8\u6218\u79bb\u5f00\u201c\u6986\u5e84\u201d\uff0c\u5fc3\u4e2d\u601d\u6f6e\u8d77\u4f0f\u4e0d\u5b9a\uff0c\u4ed6\u4e0d\u6562\u518d\u4e8b\u9017\u7559\uff0c\u56e0\u4e3a\u90a3\u6837\u4ed6\u6015\u4f1a\u6539\u53d8\u81ea \u5df1\u7684\u51b3\u5fc3\uff0c\u4ed6\u7275\u7740\u201c\u8001\u9ec4\u201d\uff0c\u4e0d\u77e5\u4e0d\u89c9\u8d8a\u8fc7\u4e86\u51e0\u4e2a\u4e0d\u5761\uff0c\u56de\u5934\u4e00\u770b\uff0c\u4e00\u5927\u7247\u8d77\u4f0f\u7267\u91ce\uff0c\u65e0\u8fb9 \u65e0\u6daf\uff0c\u201c\u6986\u5e84\u201d\u6e10\u6e10\u6d88\u5931\u4e86\uff0c\u53ea\u6709\u90a3\u68f5\u51b2\u5929\u7684\u6986\u6811\u7684\u6811\u5c16\uff0c\u8fd8\u53ef\u9690\u7ea6\u7684\u770b\u89c1\u3002 \u3000\u3000\u4ed6\u8de8\u4e0a\u725b\u80cc\uff0c\u4f9d\u4f9d\u4e0d\u820d\u7684\u671b\u671b\u957f\u767d\u5c71\uff0c\u867d\u7136\u5df2\u7ecf\u662f\u6625\u5929\u4e86\uff0c\u53ef\u662f\u5c71\u5934\u79ef\u96ea\uff0c\u5728\u9633\u5149\u4e0b\u8fd8 \u95ea\u51fa\u5343\u767e\u9053\u523a\u76ee\u7684\u5149\u832b\uff0c\u8c61\u5f81\u7740\u5173\u5916\u5bcc\u4e3d\u548c\u96c4\u58ee\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u60f3\u8d77\u7267\u7ae5\u5728\u539f\u91ce\u4e0a\u7684\u6b4c\u58f0\uff0c\u90a3\u6b4c\u662f\uff1a \u3000\u3000\u201c\u957f\u767d\u5c71\uff0c\u957f\u767d\u5c71\uff0c\u9ad8\u9ad8\u8fde\u5929\u6a90\uff0c \u3000\u3000\u8fde\u5929\u6a90\uff0c\u63a5\u5929\u6e0a\uff0c\u957f\u767d\u9ed1\u5c71\u95f4\uff0c\u7267\u91ce\u4e07\u91cc\u6c38\u65e0\u8fb9\uff0c \u3000\u3000\u65e5\u513f\u5df2\u4e0b\uff01\u725b\u554a\uff01\u7f8a\u554a\uff01\u5feb\u56de\u6765\u554a\uff0c \u3000\u3000\u56de\u5230\u957f\u767d\u5c71\u4e0b\uff0c\u90a3\u513f\u624d\u662f\u4f60\u7684\u5bb6\uff0c\u90a3\u513f\u624d\u662f\u4f60\u7684\u5bb6\u3002\u201d \u3000\u3000\u6b4c\u58f0\u662f\u591a\u4e48\u4eb2\u5207\uff0c\u9ad8\u6218\u60f3\u5230\u90a3\u91cc\uff0c\u4e0d\u7531\u81ea\u8a00\u5411\u8bed\u8f7b\u8f7b\u5730\u9053\uff1a\u201c\u522b\u4e86\uff0c\u957f\u767d\u5c71\uff0c\u2018\u6986 \u5e84\u2019\uff0c\u5584\u826f\u7684\u4f2f\u4f2f\u53d4\u53d4\u4eec\uff01\u201d \u3000\u3000\u9ad8\u6218\u884c\u4e86\u6570\u65e5\uff0c\u76d8\u7f20\u5df1\u7ecf\u7528\u5c3d\uff0c\u8fd9\u65e5\u5929\u5df2\u8fd1\u665a\uff0c\u9644\u8fd1\u53c8\u65e0\u4eba\u5bb6\uff0c\u4ed6\u53ea\u6709\u997f\u7740\u809a\u76ae\u548c\u8001\u9ec4 \u627e\u4e00\u5904\u5c71\u6d1e\u7761\u4e86\u4e00\u665a\u3002\u7b2c\u4e8c\u5929\u65e9\u6668\u4ed6\u7ee7\u7eed\u524d\u8d70\uff0c\u8d70\u5230\u6b63\u5348\uff0c\u4e5f\u4e0d\u89c1\u4eba\u5bb6\uff0c\u5934\u8111\u997f\u5f97\u5fae\u5fae\u53d1 \u660f\uff0c\u5e78\u4e8f\u4ed6\u5e7c\u65f6\u8bef\u670d\u201c\u5343\u5e74\u53c2\u738b\u201d\uff0c\u53c8\u5728\u81ea\u5df2\u4e0d\u77e5\u4e0d\u89c9\u4e2d\u7ec3\u5c31\u5173\u5916\u6b63\u5b97\u5185\u529f\uff0c\u6240\u4ee5\u52c9\u5f3a\u652f\u6301 \u7684\u4f4f\u3002\u201c\u8001\u9ec4\u201d\u4e5f\u662f\u7126\u6025\u4e0d\u5b89\uff0c\u5b83\u4e0d\u65f6\u53bb\u627e\u4e9b\u5b83\u8ba4\u4e3a\u91cf\u9c9c\u7f8e\u7684\u5ae9\u8349\uff0c\u653e\u5728\u5c0f\u4e3b\u4eba\u9762\u524d\uff0c\u793a\u610f \u8981\u9ad8\u6218\u5403\uff0c\u9ad8\u6218\u53ea\u6709\u82e6\u7b11\u7684\u4efd\u3002 \u3000\u3000\u201c\u8001\u9ec4\u201d\u5927\u6168\u5fc3\u4e2d\u5947\u602a\u5c0f\u4e3b\u4eba\u7684\u884c\u52a8\uff0c\u5b83\u60f3\u8fd9\u6837\u9c9c\u5ae9\u7684\u4e1c\u897f\u4e0d\u5403\uff0c\u800c\u8981\u6328\u997f\uff0c\u201c\u4eba\u201d\u771f \u602a\uff0c\u5b83\u5fc3\u4e2d\u6108\u6765\u6108\u7126\u6025\uff0c\u53d1\u8db3\u72c2\u5954\uff0c\u8dd1\u4e86\u4e00\u4e2a\u591a\u65f6\u8fb0\uff0c\u53ea\u89c1\u524d\u9762\u6709\u4e00\u5904\u4eba\u5bb6\uff0c\u9ad8\u6218\u5fc3\u4e2d\u5927 \u559c\uff0c\u8dd1\u4e0a\u524d\u53bb\u6572\u95e8\u3002 \u3000\u3000\u6572\u4e86\u534a\u5929\uff0c\u4e5f\u4e0d\u89c1\u6709\u4eba\u51fa\u6765\uff0c\u9ad8\u6218\u5fc3\u4e2d\u5927\u611f\u5931\u671b\uff0c\u77e5\u9053\u4e3b\u4eba\u5b9a\u7136\u5916\u51fa\uff0c\u5c31\u7ed5\u5230\u5c4b\u5b50\u540e\u9762 \u53bb\u627e\u4e3b\u4eba\uff0c\u53ea\u89c1\u7eff\u6cb9\u6cb9\u4e00\u5927\u7247\u8543\u85af\u7530\u3002 \u3000\u3000\u4ed6\u997f\u5f97\u53d1\u614c\uff0c\u4e0d\u6687\u7ec6\u60f3\uff0c\u5954\u4e86\u8fc7\u53bb\uff0c\u770b\u770b\u56db\u8fb9\u65e0\u4eba\uff0c\u5c31\u4f38\u624b\u6293\u4e86\u4e24\u53ea\uff0c\u8fd9\u65f6\u6b63\u662f\u6625\u5929\uff0c \u8543\u85af\u63d2\u4e0b\u53bb\u4e0d\u8fc7\u4e00\u4e2a\u591a\u6708\uff0c\u6240\u4ee5\u53ea\u6709\u62f3\u513f\u90a3\u5927\uff0c\u4ed6\u5fc3\u60f3\u804a\u6027\u4e8e\u65e0\uff0c\u53c8\u60f3\u5230\u5e7c\u65f6\u5728\u5730\u4e0a\u6316\u6ce5 \u7076\uff0c\u70e4\u7ea2\u85af\u7684\u9999\u751c\u4e4b\u5473\uff0c\u4e0d\u89c9\u98df\u6307\u5927\u52a8\uff0c\u4f38\u624b\u4eba\u6000\u6478\u53d6\u706b\u79cd\uff0c\u5ffd\u7136\u65e0\u610f\u4e2d\u89e6\u7740\u7236\u4eb2\u7684\u9aa8\u7070 \u575b\uff0c\u4e0d\u7981\u5fc3\u51c9\u3002 \u3000\u3000\u7239\u7684\u6b63\u76f4\u5bb9\u8c8c\u53c8\u6d6e\u4e86\u8d77\u6765\uff0c\u7239\u7684\u8c06\u8c06\u6559\u8bad\u4e5f\u98d8\u5230\u8033\u8fb9\u2026\u2026\u201c\u5f85\u4eba\u539a\uff0c\u523b\u5df1\u8584\u201d \u3000\u3000\u4ed6\u8003\u8651\u4e86\u534a\u5929\uff0c\u809a\u5b50\u5b9e\u5728\u997f\u5f97\u7d27\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e48\u591a\uff0c\u6211\u53ea\u62ff\u4e24\u4e2a\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d \u3000\u3000\u53ef\u662f\u4ed6\u53c8\u60f3\u5230\u8001\u5e08\u8bb2\u7684\u5218\u5907\u5728\u9057\u5631\u4e2d\u7684\u4e24\u53e5\u8bdd\uff1a\u201c\u6bcb\u4ee5\u5584\u5c0f\u800c\u52ff\u4e3a\uff0c\u6bcb\u4ee5\u6076\u5c0f\u800c\u4e3a \u4e4b\u3002\u201d \u3000\u3000\u4e00\u523b\u95f4\uff0c\u4ed6\u50cf\u88ab\u91cd\u91cd\u51fb\u4e86\u4e00\u4e0b\uff0c\u8d76\u5feb\u628a\u62e8\u51fa\u6765\u7684\u8543\u85af\u57cb\u4e86\uff0c\u5bf9\u9002\u624d\u7684\u884c\u4e3a\u771f\u7f9e\u6127\u5f97\u7d27\u3002 \u4ed6\u4e3e\u76ee\u4e00\u671b\u9662\u5b50\u4e00\u7247\u9752\u7fe0\u7684\u7530\u5730\u5916\uff0c\u6ca1\u6709\u4e00\u4e2a\u4eba\uff0c\u5fc3\u4e2d\u7565\u7565\u653e\u5fc3\uff0c\u4fbf\u7275\u7740\u201c\u8001\u9ec4\u201d\u518d\u5f80\u524d \u53bb\uff0c\u201c\u8001\u9ec4\u201d\u7741\u5927\u725b\u773c\uff0c\u5e26\u7740\u7591\u95ee\u8d23\u5907\u7684\u76ee\u5149\u671b\u7740\u5c0f\u4e3b\u4eba\u3002 \u3000\u3000\u9ad8\u6218\u8f7b\u8f7b\u6478\u7740\u201c\u8001\u9ec4\u201d\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u2018\u8001\u9ec4\u2019\uff0c\u90a3\u8db3\u4eba\u5bb6\u7684\u4e1c\u897f\uff0c\u6211\u4eec\u4e0d\u53ef\u4ee5\u968f\u4fbf\u53d6 \u54e9\uff01\u201d \u3000\u3000\u8d70\u4e86\u4e00\u4f1a\uff0c\u524d\u9762\u662f\u4e00\u6761\u6e05\u6f88\u5c0f\u6eaa\uff0c\u9ad8\u6218\u5fc3\u60f3\uff1a\u201c\u8fd9\u6cb3\u91cc\u7684\u9c7c\u53ef\u4e0d\u662f\u6709\u4e3b\u4e4b\u7269\u4e86\u5427\uff01\u201d \u3000\u3000\u4ed6\u8131\u53bb\u4e0a\u8863\uff0c\u94bb\u8fdb\u6c34\u91cc\uff0c\u6b64\u65f6\u9686\u51ac\u521d\u8fc7\uff0c\u6eaa\u6c34\u8db3\u4ece\u5c71\u4e0a\u6eb6\u96ea\u6d41\u4e0b\uff0c\u662f\u4ee5\u51b7\u51db\u900f\u9aa8\uff0c\u9ad8\u6218 \u4ed7\u7740\u4f53\u8d28\u7d20\u5f3a\uff0c\u7528\u5185\u529f\u95ed\u4f4f\u6c14\uff0c\u5728\u6eaa\u5e95\u6478\u6765\u6478\u53bb\u3002 \u3000\u3000\u597d\u534a\u5929\uff0c\u4ed6\u6c34\u6293\u7740\u4e00\u5c3e\u9ca4\u9c7c\uff0c\u8fde\u5fd9\u7528\u624b\u7d27\u7d27\u6349\u7262\uff0c\u7ffb\u8eab\u4e0a\u5cb8\u3002 \u3000\u3000\u90a3\u9ca4\u9c7c\u6709\u65a4\u591a\u91cd\uff0c\u9ad8\u6218\u5fc3\u4e2d\u5927\u559c\uff0c\u81ea\u5fd6\u53ef\u4ee5\u9971\u98df\u4e00\u9910\uff0c\u53ef\u662f\u5f53\u4ed6\u62e8\u51fa\u5c0f\u5200\u6b63\u60f3\u6740\u9c7c\u53bb \u9cde\uff0c\u770b\u89c1\u90a3\u9c7c\u773c\u65c1\u6709\u4e00\u4e24\u6ef4\u6c34\u73e0\uff0c\u53cc\u76ee\u7a81\u8d77\uff0c\u6b7b\u547d\u6323\u624e\u3002 \u3000\u3000\u4ed6\u7a81\u7136\u5fc3\u4e00\u8f6f\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u9c7c\u4e5f\u4f1a\u54ed\u54e9\uff01\u771f\u53ef\u4f36\uff0c\u4e0d\u77e5\u6709\u6ca1\u6709\u7236\u6bcd\uff1f\u201d \u3000\u3000\u4ed6\u56e0\u4e3a\u592a\u591a\u7684\u7231\u5fc3\uff0c\u6240\u4ee5\u5f80\u5f80\u4f1a\u83ab\u540d\u5176\u5999\u7684\u4ea7\u751f\u4e00\u79cd\u53ef\u7b11\u7684\u540c\u60c5\u5fc3\uff0c\u6b64\u65f6\u4e00\u89c1\u9ca4\u9c7c\u773c\u65c1 \u7684\u6c34\u73e0\uff0c\u7adf\u4ee5\u4e3a\u662f\u6cea\u73e0\uff0c\u518d\u4e5f\u5fcd\u4e0d\u8d77\u5fc3\u4e0b\u624b\u6740\u5b83\u3002 \u3000\u3000\u4ed6\u8f7b\u53f9\u4e00\u58f0\u5583\u5583\u9053\uff1a\u201c\u9c7c\u513f\uff0c\u4f60\u53ef\u59a5\u5f53\u5fc3\u554a\uff0c\u518d\u88ab\u4eba\u6293\u5230\uff0c\u53ef\u5c31\u4e0d\u80af\u653e\u4f60\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7f62\u624b\u4e00\u677e\uff0c\u6c34\u82b1\u56db\u6e85\uff0c\u90a3\u5c3e\u9ca4\u9c7c\u5df1\u6f5c\u5230\u6df1\u6c34\u53bb\u4e86\u3002 \u3000\u3000\u4ed6\u611f\u8eab\u4e0a\u6709\u4e9b\u51b7\uff0c\u5c31\u9760\u5728\u6eaa\u8fb9\u5927\u6811\u4e0b\uff0c\u671b\u7740\u60a0\u60a0\u767d\u4e91\uff0c\u7ade\u7761\u53bb\u4e86\u3002 \u3000\u3000\u5ffd\u7136\uff0c\u4ed6\u88ab\u4e00\u4e2a\u6e05\u8106\u7684\u7ae5\u58f0\u60ca\u8d77\uff1a\u201c\u7239\uff0c\u4f60\u77a7\u4ed6\u591a\u53ef\u4f36\uff0c\u6211\u4eec\u628a\u5e72\u7cae\u5206\u4e00\u534a\u7ed9\u4ed6\u597d \u4e48\uff1f\u201d \u3000\u3000\u9ad8\u6218\u5faa\u58f0\u671b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u8001\u8005\uff0c\u5934\u6234\u7ffb\u8d77\u7684\u7f8a\u76ae\u5e45\uff0c\u624b\u4e2d\u7275\u7740\u4e00\u4e2a\u548c\u81ea\u5df1\u5e74\u9f84\u5dee\u4e0d\u591a\u7684 \u5973\u5b69\uff0c\u5934\u4e0a\u68b3\u7740\u4e24\u53ea\u8fab\u5b50\uff0c\u8138\u8272\u7ea2\u7ea2\u7684\uff0c\u5a07\u61a8\u6781\u4e86\uff0c\u4e8c\u4eba\u5c31\u7ad9\u5728\u8eab\u65c1\u4e0d\u8fdc\u3002 \u3000\u3000\u8001\u8005\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\uff0c\u4f60\u51b7\u4e0d\u51b7\uff0c\u997f\u4e0d\u4fc4\uff1f\u201d \u3000\u3000\u9ad8\u6218\u89c1\u4ed6\u8bed\u6c14\u4eb2\u5207\uff0c\u70b9\u5934\u9053\uff1a\u201c\u8001\u4f2f\uff0c\u4f60\u53ef\u77e5\u9644\u8fd1\u6709\u4eba\u5bb6\u5417\uff1f\u6211\u2026\u2026\u6211\u2026\u2026\u201d\u4ed6\u672c\u60f3\u544a \u8bc9\u8001\u8005\u81ea\u5df1\u5df2\u997f\u4e86\u4e00\u5929\u4e00\u591c\uff0c\u4f46\u5374\u7f9e\u4e8e\u51fa\u53e3\u3002 \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u8fd9\u51e0\u5341\u91cc\u5185\u7684\u786e\u4eba\u70df\u7a00\u5c11\uff0c\u6211\u770b\u4f60\u5e74\u7eaa\u5c0f\u5c0f\uff0c\u5b64\u8eab\u51fa\u95e8\uff0c\u4e00\u5b9a\u6709\u4ec0\u4e48\u8981\u7d27 \u7684\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u6218\u70b9\u5934\uff0c\u4fbf\u8bf4\u51fa\u81ea\u5df1\u8981\u9001\u7236\u9aa8\u56de\u4e61\uff0c\u90a3\u8001\u8005\u5403\u4e86\u4e00\u60ca\uff0c\u9053\uff1a\u201c\u5c71\u897f\u9ad8\u6b64\u4f55\u53ea\u4e07\u91cc\uff0c\u4f60 \u4e00\u4e2a\u4eba\u884c\u8def\u5b9e\u5728\u592a\u5371\u9669\u4e86\u2026\u2026\u201d \u3000\u3000\u90a3\u5c0f\u5973\u5b69\u63a5\u53e3\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8ddf\u6211\u4eec\u4e00\u8d77\u8d70\uff0c\u7b49\u6211\u7239\u529e\u5b8c\u4e8b\uff0c\u54b1\u4eec\u518d\u4e00\u8d77\u5165\u5173\u53ef\u597d\uff1f\u201d \u3000\u3000\u9ad8\u6218\u6447\u5934\uff0c\u67d4\u58f0\u62d2\u7edd\u5979\u7684\u597d\u610f\uff0c\u6b63\u5f85\u544a\u522b\uff0c\u90a3\u8001\u8005\u6c89\u4e00\u4f1a\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\uff0c\u4f60\u5148\u628a\u8fd9\u5305\u5e72 \u7cae\u5e26\u53bb\uff0c\u5426\u5219\u8fd9\u65b9\u5706\u767e\u91cc\u65e0\u4eba\uff0c\u4f60\u8fd8\u8981\u6328\u997f\u54e9\uff01\u5c0f\u5c0f\u5e74\u7eaa\u5b5d\u5fc3\u53ef\u8d35\uff0c\u6211\u672c\u5f53\u52a9\u4f60\u4e00\u81c2\u4e4b\u529b\uff0c \u53ef\u662f\u76ee\u4e0b\u5b9e\u5728\u662f\u8eab\u6709\u8981\u4e8b\uff0c\u65e0\u6687\u5206\u8eab\u3002\u201d \u3000\u3000\u9ad8\u6218\u89c1\u4ed6\u5b8c\u5168\u4ee5\u957f\u8f88\u6001\u5ea6\u771f\u8bda\u5bf9\u5f85\u81ea\u5df1\uff0c\u5fc3\u4e2d\u5f88\u662f\u611f\u52a8\uff0c\u77e5\u9053\u81ea\u5df1\u518d\u8981\u63a8\u8f9e\uff0c\u5fc5\u5b9a\u60f9\u8d77 \u4ed6\u4e0d\u5feb\uff0c\u4fbf\u53cc\u624b\u63a5\u8fc7\u4e00\u5305\u5e72\u7cae\uff0c\u79f0\u8c22\u9053\uff1a\u201c\u4e0d\u77e5\u8001\u4f2f\u8d35\u59d3\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u9053\uff1a\u201c\u6211\u59d3\u65b9\uff0c\u662f\u5173\u5916\u65b9\u5bb6\u7267\u573a\u4e3b\u4eba\u3002\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u6211\u53eb\u9ad8\u6218\uff0c\u5c06\u6765\u91cd\u56de\u5173\u5916\u4e00\u5b9a\u6765\u770b\u4f2f\u4f2f\u3002\u201d \u3000\u3000\u90a3\u5973\u5b69\u559c\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8bf4\u8bdd\u53ef\u8981\u7b97\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u6218\u70b9\u70b9\u5934\uff0c\u8001\u8005\u4f3c\u4e4e\u6709\u6025\u4e8b\uff0c\u64ae\u53e3\u957f\u5578\u4e00\u58f0\uff0c\u4e24\u5339\u9a6c\u4e00\u5927\u4e00\u5c0f\u4ece\u8349\u539f\u4e2d\u5982\u98de\u8dd1\u6765\u3002 \u3000\u3000\u8001\u8005\u9a91\u4e0a\u9a6c\uff0c\u56de\u5934\u770b\u5230\u9ad8\u6218\u4ece\u6811\u540e\u7275\u51fa\u4e00\u5934\u725b\uff0c\u725b\u89d2\u4e0a\u6302\u7740\u4e00\u4e2a\u5c0f\u5c0f\u7528\u6bdb\u6be1\u6346\u6210\u7684\u5305 \u88b1\uff0c\u4ed4\u7ec6\u4e00\u77a7\uff0c\u4e0a\u9762\u7ee3\u7740\u4e00\u68f5\u6768\u6811\uff0c\u4e00\u68f5\u67cf\u6811\uff0c\u4e0d\u7531\u5927\u653e\u5bbd\u5fc3\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u539f\u6765\u548c\u98ce\u8001 \u54e5\u6709\u5173\u7cfb\uff0c\u6211\u5012\u662f\u591a\u8651\u4e86\uff0c\u5c31\u51ed\u98ce\u5927\u54e5\u8fd9\u6807\u8bc6\uff0c\u5173\u5916\u7eff\u6797\u8c01\u6562\u4e0d\u4e56\u4e56\u653e\u884c\u3002\u201d \u3000\u3000\u4e00\u62cd\u9a6c\uff0c\u5e26\u7740\u90a3\u5c0f\u5973\u5b69\u75be\u9a70\u800c\u53bb\uff0c\u98ce\u58f0\u4e2d\u8fd8\u65ad\u65ad\u7eed\u7eed\u4f20\u6765\u5c0f\u5973\u5b69\u7684\u5631\u5490\u58f0\u3002 \u3000\u3000\u9ad8\u6218\u72fc\u541e\u864e\u54bd\u7684\u5927\u56bc\u8d77\u6765\uff0c\u5403\u5b8c\u4ee5\u540e\uff0c\u5fc3\u4e2d\u4e0d\u4f4f\u76d8\u7b97\u7740\uff0c\u4ed6\u60f3\uff1a\u201c\u8fd9\u53bb\u5c71\u897f\u8fd8\u4e0d\u77e5\u6709\u591a \u8fdc\uff0c\u73b0\u5728\u8eab\u65e0\u5206\u6587\uff0c\u600e\u6837\u53ef\u4ee5\u5230\u8fbe\u5462\uff1f\u201d \u3000\u3000\u4ed6\u53c8\u60f3\u5230\u5356\u725b\uff0c\u4f46\u7acb\u523b\u88ab\u81ea\u5df1\u5236\u6b62\uff0c\u5fc3\u5185\u6697\u9a82\u81ea\u5df1\u9053\uff1a\u201c\u9ad8\u6218\u554a\uff0c\u9ad8\u6218\uff0c\u4f60\u600e\u4e48\u8001\u60f3\u5230 \u53bb\u51fa\u5356\u4f60\u81ea\u5df1\u5fe0\u5b9e\u7684\u670b\u53cb\uff0c\u4f60\u8fd9\u5351\u9119\u7684\u4e1c\u897f\uff0c\u771f\u662f\u732a\u72d7\u4e0d\u5982\u3002\u201d \u3000\u3000\u4f46\u662f\u4e00\u4e2a\u5ff5\u5934\u7a81\u7136\u95ea\u8d77\uff1a\u201c\u662f\u7236\u4eb2\u9aa8\u7070\u91cd\u8981\uff0c\u8fd8\u662f\u2018\u8001\u9ec4\u2019\u91cd\u8981\uff0c\u7167\u8fd9\u60c5\u5f62\uff0c\u4e0d\u628a\u2018\u8001 \u9ec4\u2019\u5356\u4e86\uff0c\u600e\u4e48\u4e5f\u4e0d\u80fd\u56de\u5230\u5bb6\u4e61\uff0c\u2018\u8001\u9ec4\u2019\uff0c\u6211\u662f\u4e00\u5929\u90fd\u4e0d\u613f\u610f\u79bb\u5f00\u7684\uff0c\u5982\u679c\u5356\u6389\uff0c\u6211\u5728\u8fd9 \u4e16\u4e0a\u5c31\u66f4\u5b64\u96f6\u96f6\u4e86\uff0c\u6211\u60b2\u54c0\u4e5f\u6ca1\u6709\u5730\u65b9\u8bb2\uff0c\u6211\u53ef\u80fd\u4f1a\u4f24\u5fc3\u6b7b\u7684\uff0c\u53ef\u662f\uff0c\u53ef\u662f\u7239\u7684\u9aa8\u7070\u600e\u4e48\u529e \u5462\uff1f\u201d \u3000\u3000\u4ed6\u89c9\u5f97\u8fd9\u4e2a\u95f4\u9898\u597d\u751f\u96be\u4ee5\u51b3\u5b9a\uff0c\u60f3\u5230\u2018\u8001\u9ec4\u2019\u548c\u81ea\u5df1\u7684\u611f\u60c5\uff0c\u73b0\u5728\u5fc5\u987b\u4eba\u725b\u76f8\u79bb\uff0c\u4e0d\u89c9 \u5fc3\u788e\u4e86\u3002 \u3000\u3000\u6700\u540e\uff0c\u4ed6\u7ec8\u4e8e\u51b3\u5b9a\u4e86\uff0c\u4fca\u8138\u4e0a\u95ea\u8fc7\u4e00\u9635\u60e8\u75db\u7684\u795e\u8272\uff0c\u4ed6\u60f3\uff1a\u201c\u8fd9\u662f\u7239\u6700\u540e\u7684\u613f\u671b\uff0c\u5982\u679c \u6211\u90fd\u4e0d\u80fd\u505a\u5230\uff0c\u90a3\u4e48\u6211\u8fd8\u80fd\u7b97\u662f\u4eba\u5417\uff1f\u7239\u7239\uff0c\u4f60\u653e\u5fc3\u5427\uff0c\u6218\u513f\u51b3\u4e0d\u8fdd\u80cc\u4f60\u4e00\u53e5\u8bdd\u3002\u201d \u3000\u3000\u4ed6\u8df3\u4e0b\u725b\u80cc\uff0c\u7528\u8138\u8f7b\u8f7b\u64e6\u7740\u725b\u5934\uff0c\u773c\u6cea\u51e0\u4e4e\u593a\u7736\u800c\u51fa\uff0c\u4f46\u968f\u5373\u5f3a\u5fcd\u4f4f\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a \u201c\u2018\u8001\u9ec4\u2019\uff0c\u54b1\u4eec\u4e0d\u4e45\u5c31\u751a\u5206\u522b\u4e86\u3002\u201d \u3000\u3000\u8001\u9ec4\u89c1\u4ed6\u5f88\u662f\u60b2\u6006\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u539f\u56e0\uff0c\u4e5f\u8ddf\u7740\u95ee\u543c\u51e0\u58f0\u3002 \u3000\u3000\u53c8\u8d70\u4e86\u6570\u5341\u91cc\uff0c\u5230\u4e86\u4e00\u4e2a\u5927\u9547\uff0c\u9ad8\u6218\u72e0\u7740\u5fc3\uff0c\u53bb\u627e\u4e86\u4e00\u4e2a\u725b\u8d29\u6765\u770b\u725b\u3002 \u3000\u3000\u90a3\u725b\u8d29\u4e1c\u6478\u6478\uff0c\u897f\u62c9\u62c9\uff0c\u4f3c\u4e4e\u5f88\u611f\u6ee1\u610f\uff0c\u201c\u8001\u9ec4\u201d\u770b\u770b\u725b\u8d29\uff0c\u53c8\u671b\u671b\u4f24\u5fc3\u7684\u5c0f\u4e3b\u4eba\uff0c\u5fc3 \u5185\u4fbf\u5df2\u660e\u767d\uff0c\u4e00\u9897\u5927\u5934\u4e5f\u88f4\u54c0\u5f97\u5782\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u725b\u8d29\u548c\u9ad8\u6218\u8bae\u5b9a\u4ef7\u94b1\uff0c\u4fbf\u56de\u5bb6\u53bb\u53d6\uff0c\u9ad8\u6218\u629a\u6478\u7740\u725b\u8179\uff0c\u8f7b\u8f7b\u89e3\u4e0b\u6302\u5728\u89d2\u4e0a\u7684\u5305\u88b1\uff0c\u4e0d\u77e5 \u8bf4\u4ec0\u4e48\u662f\u597d\u3002\u534a\u664c\uff0c\u201c\u8001\u9ec4\u201d\u62ac\u8d77\u5934\u6765\uff0c\u51dd\u76ee\u770b\u4e86\u9ad8\u6218\u4e00\u773c\uff0c\u90a3\u773c\u5149\u9ad8\u6218\u7406\u4f1a\u5f97\u5230\uff0c\u662f\u5145\u6ee1 \u4e86\u601c\u60af\u5bbd\u6055\u7684\u610f\u601d\uff0c\u90a3\u597d\u50cf\u8bf4\uff1a\u201c\u5c0f\u4e3b\u4eba\u554a\uff0c\u6211\u4e0d\u602a\u4f60\uff0c\u53ea\u662f\u6211\u2018\u8001\u9ec4\u2019\u4e0d\u80fd\u518d\u66ff\u4f60\u505a\u4e8b\uff0c \u4e0d\u80fd\u518d\u4fdd\u62a4\u4f60\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fcd\u4e0d\u4f4f\u70ed\u6cea\u51b2\u51fa\uff0c\u62b1\u7740\u725b\u5934\u54ed\u9053\uff1a\u201c\u2018\u8001\u9ec4\u2019\u6211\u771f\u5bf9\u4e0d\u8d77\u4f60\uff0c\u53ef\u662f\u4e3a\u4e86\u7239\u7239\u7684\u9aa8 \u7070\uff0c\u6211\u53ea\u6709\u8fd9\u6837\u505a\u554a\uff01\u2018\u8001\u9ec4\u2019\uff0c\u6211\u5fc3\u91cc\u6bd4\u4f60\u66f4\u96be\u8fc7\u7684\u5440\u3002\u201d \u3000\u3000\u201c\u8001\u9ec4\u201d\u6447\u64ad\u5934\uff0c\u60b2\u9e23\u4e00\u58f0\uff0c\u56de\u5934\u8214\u53bb\u9ad8\u6218\u7684\u6cea\u6c34\u3002 \u3000\u3000\u9ad8\u6218\u786c\u54bd\u9053\uff1a\u201c\u2018\u8001\u9ec4\u2019\uff0c\u6211\u4e0d\u54ed\uff0c\u6211\u4e0d\u54ed\uff0c\u7239\u8bf4\u8fc7\u7537\u4eba\u4e0d\u8be5\u968f\u4fbf\u54ed\u7684\u3002\u201d\u4ed6\u867d\u53e3\u4e2d\u8bf4 \u4e0d\u54ed\uff0c\u53ef\u662f\u773c\u6cea\u5374\u4e0d\u53d7\u63a7\u5236\uff0c\u6f78\u7136\u800c\u4e0b\uff0c\u4ed6\u53c8\u8981\u62b1\u725b\uff0c\u53c8\u8981\u62ed\u6cea\uff0c\u5f04\u5f97\u624b\u8db3\u5fd9\u4e71\u3002 \u3000\u3000\u7a81\u7136\u8001\u9ec4\u6b22\u53eb\u4e00\u58f0\uff0c\u62ac\u8d77\u5934\u6765\u770b\u770b\u6b63\u5728\u72fc\u72c8\u7684\u4e3b\u4eba\uff0c\u4f3c\u4e4e\u5b83\u5df2\u60f3\u901a\u4e86\u4ec0\u4e48\u3002\u9ad8\u6218\u89c1\u5b83\u7a81 \u7136\u6b22\u559c\uff0c\u4e0d\u7981\u5927\u5947\uff0c\u6b63\u5728\u6b64\u65f6\uff0c\u90a3\u725b\u8d29\u5b50\u53d6\u94f6\u5f52\u6765\uff0c\u4ed6\u628a\u94f6\u5b50\u4ea4\u7ed9\u9ad8\u6218\uff0c\u5c31\u7528\u7ef3\u5b50\u6346\u201c\u8001 \u9ec4\u201d\u3002 \u3000\u3000\u9ad8\u6218\u773c\u89c1\u201c\u8001\u9ec4\u201d\u670d\u670d\u8d34\u8d34\u88ab\u725b\u8d29\u5e26\u8d70\uff0c\u4f46\u4e0d\u65f6\u56de\u8fc7\u5934\u6765\uff0c\u5e76\u65e0\u60b2\u621a\u4e4b\u8272\uff0c\u4ed6\u5fc3\u4e2d\u6108\u60f3 \u6108\u4e0d\u5fcd\uff0c\u4e0d\u7531\u4e5f\u8ddf\u7740\u725b\u8d29\u548c\u201c\u8001\u9ec4\u201d \u3000\u3000\u8d70\u51fa\u9547\u5916\u3002 \u3000\u3000\u201c\u8001\u9ec4\u201d\u5ffd\u7136\u957f\u9e23\u4e00\u58f0\uff0c\u50cf\u662f\u5411\u5c0f\u4e3b\u4eba\u544a\u522b\uff0c\u7136\u540e\u5c31\u4e0d\u518d\u56de\u5934\uff0c\u6b65\u6b65\u8d70\u8fdc\u4e86\u3002 \u3000\u3000\u66ae\u8272\u82cd\u82cd\uff0c\u201c\u8001\u9ec4\u201d\u548c\u725b\u8d29\u5728\u5730\u5e73\u7ebf\u4e0a\u9065\u8fdc\u5904\u53ea\u5269\u4e0b\u4e24\u4e2a\u9ed1\u70b9\u3002 \u3000\u3000\u98ce\u8d77\u4e86\uff0c\u5439\u5f97\u201c\u9752\u6c99\u5e10\u201d\u6c99\u6c99\u4f5c\u54cd\uff0c\u9ad8\u6218\u5583\u5583\u9053\uff1a\u201c\u2018\u8001\u9ec4\u2019\uff0c\u4ec0\u4e48\u75db\u82e6\u90fd\u7531\u54b1\u4eec\u4fe9\u6765 \u62c5\u5f53\u5427\u3002\u201d \u3000\u3000\u4ed6\u611f\u5230\u988a\u4e0a\u4e00\u51c9\uff0c\u5fc3\u4e2d\u6697\u6697\u5730\u9053\uff1a\u201c\u9ad8\u6218\uff0c\u9ad8\u6218\uff0c\u4f60\u53ef\u5343\u4e07\u522b\u518d\u54ed\u4e86\u3002\u201d \u3000\u3000\u5929\u9645\u73b0\u51fa\u51e0\u9897\u5c0f\u661f\uff0c\u5927\u5730\u4e00\u7247\u5bc2\u9759\uff0c\u53c8\u6709\u8c01\u6765\u5b89\u6170\u8fd9\u5931\u671b\u4f24\u5fc3\u7684\u5b69\u5b50\u5462\uff1f \u3000\u3000\u6625\u5929\uff0c\u6cb3\u7554\u6768\u67f3\u62bd\u51fa\u65b0\u679d\uff0c\u7530\u95f4\u63d2\u4e0a\u4e86\u7eff\u6cb9\u6cb9\u7684\u8c46\u82d7\uff0c\u5fae\u98ce\u5439\u6765\uff0c\u5982\u6ce2\u6d6a\u822c\u8d77\u4f0f\u7740\u3002 \u3000\u3000\u4ece\u7530\u95f4\u8d70\u51fa\u4e00\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5e74\uff0c\u6234\u7740\u5c16\u9876\u7684\u7b20\u5e3d\uff0c\u81ea\u8a00\u81ea\u8bed\u9053\uff1a\u201c\u597d\u65b0\u9c9c\u7684\u7a7a\u6c14\u3002\u201d \u3000\u3000\u4ed6\u653e\u4e0b\u8377\u9504\uff0c\u628a\u7b7e\u5e3d\u63a8\u5411\u8111\u540e\uff0c\u9732\u51fa\u6574\u5f20\u8138\u6765\uff0c\u4f46\u89c1\u4ed6\u76ae\u80a4\u767d\u6da6\uff0c\u4e30\u6717\u5982\u7389\uff0c\u751a\u662f\u4fca \u96c5\uff0c\u5b8c\u5168\u4e0d\u50cf\u519c\u592b\u6a21\u6837\u3002 \u3000\u3000\u4ed6\u4ece\u80cc\u540e\u53e3\u888b\u4e2d\u6478\u51fa\u4e00\u672c\u4e66\uff0c\u4e13\u5fc3\u4e00\u610f\u7684\u8bfb\u7740\u4e66\u3002 \u3000\u3000\u4ed6\u89c1\u5929\u8272\u8fd8\u65e9\uff0c\u201c\u66ae\u6625\u4e09\u6708\uff0c\u6c5f\u5357\u8349\u957f\uff0c\u6742\u82b1\u751f\u6811\uff0c\u7fa4\u83ba\u4e71\u98de\u2026\u2026\u201d\u4ed6\u4e00\u8fb9\u5ff5\u7740\uff0c\u5fc3\u4e2d \u5374\u5e7b\u60f3\u7740\u6c5f\u5357\u98ce\u5149\u3002 \u3000\u3000\u201c\u6c5f\u5357\u98ce\u5149\u5982\u753b\uff0c\u7aef\u7684\u4e00\u4e2a\u597d\u5730\u65b9\u3001\u6211\u8fdf\u65e9\u8981\u53bb\u6e38\u5386\u6e38\u5386\u3002\u201d \u3000\u3000\u4ed6\u60f3\u5230\u6b64\u5904\uff0c\u5c31\u653e\u4e0b\u4e66\u672c\uff0c\u5306\u5306\u8dd1\u8fd1\u6751\u91cc\uff0c\u8fce\u9762\u78b0\u7740\u4e00\u4f4d\u767d\u53d1\u8001\u7fc1\u3002\u95ee\u4ed6\u9053\uff1a\u201c\u7530\u91cc\u7684 \u4e8b\u90fd\u597d\u4e86\u5417\uff1f\u201d \u3000\u3000\u5c11\u5e74\u70b9\u5934\u9053\uff1a\u201c\u91ce\u8349\u90fd\u62d4\u5149\u4e86\uff0c\u5730\u4e5f\u6574\u5566\u3002\u201d \u3000\u3000\u8001\u7fc1\u671b\u82e5\u4ed6\u7684\u751f\u673a\u84ec\u52c3\u7684\u80cc\u5f71\uff0c\u76b1\u7eb9\u6ee1\u5e03\u7684\u8138\u4e5f\u5c55\u5f00\u4e86\uff0c\u7b11\u5bb9\u65f6\u9732\uff0c\u4f3c\u4e4e\u5728\u56de\u5fc6\u7740\u5e74\u9752 \u65f6\u4ee3\u7684\u5f80\u4e8b\uff0c\u5fc3\u4e2d\u9ed8\u9ed8\u8d5e\u9053\uff1a\u201c\u597d\u52e4\u5feb\u7684\u5c0f\u4f19\u5b50\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u8dd1\u8fdb\u5c4b\u91cc\uff0c\u4ece\u5e8a\u5e95\u4e0b\u6478\u51fa\u4e03\u516b\u4e2a\u6734\u6ee1\uff0c\u6709\u7684\u662f\u7b11\u53e3\u61a8\u7136\u7684\u5a03\u5a03\uff0c\u6709\u7684\u8db3\u80a5\u80a0\u5927\u809a \u7684\u8001\u732a\uff0c\u5c11\u5e74\u53c8\u5728\u6795\u4e0b\u4e71\u7ffb\uff0c\u7ffb\u51fa\u4e00\u5927\u5806\u96f6\u96f6\u843d\u843d\u7684\u7eb8\u7247\uff0c\u4e0a\u9762\u5c3d\u662f\u5199\u7684\u67d0\u5e74\u67d0\u65e5\u5b58\u4e86\u591a\u5c11 \u94b1\uff0c\u4ed6\u5f88\u5feb\u5730\u770b\u4e86\u4e00\u904d\uff0c\u53c8\u4ed4\u7ec6\u7b97\u4e86\u4e00\u904d\uff0c\u5fc3\u9053\uff1a\u201c\u8fd9\u5e10\u672c\u4e0a\u8bb0\u7740\u5df2\u6709\u4e00\u767e\u5eff\u4e24\u94f6\u5b50\uff0c\u5982\u679c \u6ca1\u6709\u8bb0\u9519\uff0c\u90a3\u4e48\u5c31\u591f\u4e86\u3002\u201d \u3000\u3000\u4ed6\u8010\u5fc3\u7684\u628a\u6734\u6ee1\u4e00\u4e2a\u4e2a\u6572\u7834\uff0c\u7acb\u523b\u5730\u4e0a\u5806\u8d77\u4e00\u5927\u5806\u788e\u94f6\uff0c\u90fd\u662f\u4e00\u4e24\u591a\u91cd\u4e00\u5c0f\u5757\u4e00\u5c0f\u5757 \u7684\uff0c\u4ed6\u70b9\u4e86\u4e00\u4e0b\uff0c\u548c\u81ea\u5df1\u6240\u8bb0\u5dee\u4e0d\u591a\uff0c\u4e0d\u7531\u5fc3\u4e2d\u5927\u559c\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u7ec8\u4e8e\u79ef\u6ee1\u4e86\u6211\u5e0c\u671b\u7684\u6570 \u76ee\uff0c\u6211\u6e38\u5386\u5929\u4e0b\u7684\u76ee\u7684\u5373\u5c06\u8fbe\u5230\u4e86\u3002\u201d \u3000\u3000\u4ed6\u4ece\u7a97\u53e3\u8fdc\u671b\u51fa\u53bb\uff0c\u4e00\u6279\u6279\u519c\u592b\u8fd9\u65f6\u624d\u90fd\u8377\u9504\u4e0a\u5c71\uff0c\u60f3\u5230\u81ea\u5df1\u8fd9\u5341\u5e74\u6765\u7825\u624b\u80fc\u8db3\uff0c\u52e4\u594b \u4e0d\u5df2\uff0c\u4e0d\u4f46\u613f\u671b\u5373\u5c06\u8fbe\u5230\uff0c\u800c\u4e14\u7239\u7239\u6240\u4f20\u7684\u201c\u9ad8\u5bb6\u621f\u6cd5\u201d\u7ec3\u5f97\u51fa\u795e\u4eba\u5316\uff0c\u90a3\u6148\u7965\u8001\u4eba\u4f20\u6388\u7684 \u5185\u529f\u4e5f\u7cbe\u8fdb\u4e0d\u5c11\uff0c\u8d70\u8d77\u8def\u6765\uff0c\u4f46\u89c9\u8f7b\u5feb\u5df2\u6781\uff0c\u4e08\u4f59\u7684\u5899\u4e5f\u80fd\u4e00\u8dc3\u800c\u8fc7\uff0c\u4e0d\u7981\u5341\u5206\u81ea\u5f97\u3002 \u3000\u3000\u95e8\u53e3\u7684\u6866\u6811\u957f\u5f97\u679d\u53f6\u8302\u76db\uff0c\u9ad8\u5927\u633a\u76f4\uff0c\u4ed6\u56de\u60f3\u521d\u8fd4\u6545\u4e61\u65f6\u90a3\u6811\u8fd8\u6ca1\u6709\u81ea\u5df1\u9ad8\uff0c\u8f6c\u773c\u95f4\uff0c \u5341\u5e74\u5c31\u8fc7\u53bb\u4e86\uff0c\u81ea\u5df1\u4e5f\u4ece\u5c0f\u5b69\u53d8\u6210\u5927\u4eba\u2014\u2014\u4ed6\u60f3\u4ed6\u5df2\u662f\u5927\u4eba\u4e86\u3002 \u3000\u3000\u60f3\u5230\u6b64\uff0c\u5fc3\u4e2d\u6709\u4e9b\u5b89\u7136\uff0c\u62ac\u5934\u4e00\u671b\uff0c\u65ed\u65e5\u521d\u5347\uff0c\u6c14\u8c61\u4e07\u5343\uff0c\u594b\u6597\u4e4b\u5fc3\u6cb9\u7136\u800c\u751f\uff0c\u5583\u5583 \u9053\uff1a\u201c\u9ad8\u6218\uff0c\u7239\u7239\u8981\u4f60\u4e3a\u56fd\u4e3a\u6c11\u505a\u4e00\u756a\u5927\u4e8b\uff0c\u5c82\u80fd\u6c38\u4e45\u7ec8\u8001\u662f\u4e61\u5462\uff1f\u201d \u3000\u3000\u6d1b\u9633\u9053\u4e0a\uff0c\u6625\u610f\u76ce\u7136\u3002 \u3000\u3000\u5929\u8272\u5df2\u6697\uff0c\u4e00\u5339\u7626\u9a6c\u4ece\u5927\u8ffd\u75be\u5954\u800c\u6765\uff0c\u4e0a\u9762\u5750\u7740\u4e00\u4e2a\u633a\u79c0\u5c11\u5e74\uff0c\u90a3\u9a6c\u50cf\u662f\u4ece\u8fdc\u5904\u5954\u6765\uff0c \u4e0d\u4f4f\u5598\u606f\u3002 \u3000\u3000\u5c11\u5e74\u5fc3\u4e2d\u76d8\u7b97\u4e00\u4f1a\uff0c\u5fc3\u60f3\u57ce\u95e8\u591a\u534a\u5df2\u5173\u3001\u4eca\u665a\u662f\u522b\u60f3\u8fdb\u57ce\u4e86\uff0c\u770b\u770b\u4e0d\u8fdc\u4e4b\u5904\u6709\u4e2a\u5c71\u795e \u5e99\uff0c\u706f\u706b\u5fae\u5f31\uff0c\u5c31\u62cd\u9a6c\u4e0a\u524d\u3002 \u3000\u3000\u5f85\u5230\u8d70\u8fd1\uff0c\u53ea\u89c1\u5e99\u95e8\u534a\u5f00\uff0c\u8f7b\u6b65\u4e0a\u524d\uff0c\u6b63\u60f3\u62db\u547c\u5e99\u50e7\uff0c\u4f46\u63a2\u5934\u4e00\u770b\uff0c\u4e0d\u7531\u5927\u5403\u4e00\u60ca\u3002 \u3000\u3000\u53ea\u89c1\u5e99\u5185\u9634\u6c14\u68ee\u68ee\uff0c\u86db\u4e1d\u56db\u5e03\uff0c\u5899\u89d2\u8fb9\u653e\u8005\u597d\u51e0\u5177\u68fa\u6728\uff0c\u4e00\u4e2a\u8001\u8005\u80cc\u95e8\u800c\u5750\uff0c\u7537\u540e\u4e00\u4e2a \u9ed1\u6c49\uff0c\u624b\u6267\u94a2\u5200\uff0c\u6ee1\u8138\u6740\u6c14\uff0c\u4e00\u6b65\u4e00\u6b65\u5c0f\u5fc3\u7ffc\u7ffc\u8d70\u8fd1\u8001\u8005\uff0c\u4ed6\u6bcf\u5411\u524d\u4e00\u6b65\u5c31\u505c\u4e0b\u4e00\u6b21\uff0c\u770b\u770b \u56db\u5468\u53ca\u8001\u8005\u52a8\u9759\uff0c\u770b\u6765\u5bf9\u8001\u8005\u6020\u60ee\u5df2\u6781\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u4e00\u60ca\u4e4b\u4e0b\uff0c\u51e0\u4e4e\u5931\u58f0\u53eb\u51fa\uff0c\u770b\u5230\u90a3\u675c\u6c49\u4fde\u8d70\u6108\u8fd1\uff0c\u8001\u8005\u4f3c\u4e4e\u4ecd\u672a\u53d1\u89c9\uff0c\u773c\u770b\u675c\u6c49 \u4e3e\u8d77\u94a2\u5200\u5c31\u8981\u8fce\u5934\u5288\u4e0b\uff0c\u4e00\u6025\u4e4b\u4e0b\uff0c\u4e0d\u6687\u7ec6\u60f3\uff0c\u62d4\u51fa\u80cc\u540e\u77ed\u621f\uff0c\u7eb5\u4e0a\u53bb\u65bd\u51fa\u201c\u65e0\u654c\u621f\u6cd5\u201d\u4e2d \u201c\u4e3e\u706b\u70e7\u5929\u201d\u5bf9\u51c6\u4e0b\u780d\u5200\u52bf\u4e00\u683c\u3002 \u3000\u3000\u7830\u7136\u4e00\u58f0\uff0c\u58ee\u6c49\u624b\u4e2d\u94a2\u5200\u9f50\u8170\u800c\u65ad\uff0c\u524d\u534a\u622a\u5200\u950b\u4ecd\u7136\u5411\u8001\u8005\u5f53\u5934\u843d\u53bb\uff0c\u5c11\u5e74\u6025\u5fd9\u77ed\u621f\u4e00 \u633a\uff0c\u4e00\u62db\u201c\u540e\u7fbf\u5c04\u6708\u201d\u628a\u5200\u5c16\u6253\u98de\u3002 \u3000\u3000\u4ed6\u5927\u663e\u8eab\u624b\u8fde\u65bd\u7edd\u62db\uff0c\u597d\u4e0d\u5bb9\u6613\u6551\u4e86\u8001\u4eba\u4e00\u547d\uff0c\u5fc3\u4e2d\u6b63\u81ea\u5f97\u610f\u3002 \u3000\u3000\u8033\u4e2d\u5374\u542c\u5230\u4e00\u58f0\u6012\u53f1\uff1a\u201c\u8c01\u8981\u4f60\u591a\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u5446\u4e86\u4e00\u5446\uff0c\u89c1\u90a3\u8001\u4eba\u4e0d\u77e5\u4f55\u65f6\u5df1\u8f6c\u8fc7\u8eab\u6765\uff0c\u58ee\u6c49\u7ad9\u5728\u8001\u4eba\u8eab\u65c1\uff0c\u624b\u4e2d\u8fd8\u62ff\u7740\u534a\u622a\u5200\uff0c \u4f5c\u52bf\u6b32\u780d\uff0c\u53ea\u662f\u8138\u4e0a\u795e\u8272\u75db\u82e6\u5df2\u6781\uff0c\u53cc\u76ee\u5706\u77aa\uff0c\u5446\u5982\u6728\u9e21\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u5fc3\u5730\u6148\u8f6f\uff0c\u53ea\u9053\u662f\u81ea\u5df1\u7528\u529b\u8fc7\u731b\uff0c\u5f92\u4f24\u4e86\u58ee\u6c49\u7684\u7b4b\u9aa8\uff0c\u5fc3\u4e2d\u5927\u611f\u6b49\u610f\uff0c\u67d4\u58f0\u9053\uff1a \u201c\u8fd9\u4f4d\u5927\u53d4\u4f60\u5e72\u5417\u8981\u6697\u7b97\u8001\u4f2f\u4f2f\uff0c\u6211\u4e00\u65f6\u6536\u624b\u4e0d\u4f4f\uff0c\u9707\u4f24\u4e86\u4f60\u54ea\u91cc\u4e86\uff1f\u201d \u3000\u3000\u90a3\u8001\u8005\u51b7\u54fc\u4e00\u58f0\uff0c\u5f88\u4e0d\u8010\u70e6\u9053\uff1a\u201c\u5c0f\u9b3c\uff0c\u4f60\u7ed9\u6211\u7ad9\u5230\u4e00\u8fb9\u53bb\uff0c\u5f85\u6211\u6536\u62fe\u4e86\u8fd9\u8d3c\u5b50\u540e\uff0c\u518d \u6765\u9886\u7f5a\u3002\u201d \u3000\u3000\u90a3\u5c11\u5e74\u5fd6\u9053\uff1a\u201c\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5982\u6b64\u6a2a\u7684\u8001\u4eba\uff0c\u66ff\u4ed6\u89e3\u4e86\u56f4\uff0c\u5012\u602a\u8d77\u6211\u6765\u3002\u201d \u3000\u3000\u4ed6\u5929\u6027\u5e73\u548c\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u60f3\u4e0d\u51fa\u9a82\u4eba\u7684\u8bdd\uff0c\u5c31\u4f9d\u8a00\u8d70\u5f00\u3002 \u3000\u3000\u8001\u8005\u4e0a\u524d\u4e00\u6b65\uff0c\u5bf9\u51c6\u90a3\u675c\u6c49\u80cc\u4e0a\u4e00\u62cd\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u6211\u9053\u6d1b\u9633\u4e09\u9738\u5728\u6c5f\u6e56\u4e0a\u603b\u7b97\u6709\u70b9\u4e07 \u513f\uff0c\u4e0d\u6599\u5c3d\u662f\u5077\u9e21\u6478\u72d7\u4e4b\u8f88\uff0c\u4e0d\u9519\uff0c\u4f60\u4e24\u4f4d\u5144\u957f\u90fd\u662f\u6211\u5bb0\u7684\uff0c\u4f60\u8981\u62a5\u4ec7\uff0c\u8001\u592b\u5c31\u6210\u5168\u4f60\u3002\u201d \u3000\u3000\u58ee\u6c49\u5636\u58f0\u53eb\u9053\uff1a\u201c\u8001\u8d3c\uff0c\u6740\u4eba\u507f\u547d\uff0c\u6b20\u503a\u8fd8\u94b1\uff0c\u4f60\u4e0d\u8fc7\u4e58\u8001\u7237\u4e0e\u90a3\u4e2a\u8d3c\u4ea4\u624b\u65f6\uff0c\u7a81\u65bd\u6697 \u7b97\uff0c\u4eca\u65e5\u4f60\u5bb6\u7237\u7237\u4e0e\u4f60\u62fc\u4e86\u3002\u201d \u3000\u3000\u8001\u8005\u8138\u4e0a\u7a81\u9732\u5fae\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u53ae\u81ea\u4ee5\u4e3a\u806a\u660e\uff0c\u5728\u8001\u592b\u9152\u4e2d\u5f04\u4e86\u624b\u811a\uff0c\u4ed6\u4e0d\u60f3\u60f3\u8001\u592b\u662f\u4f55 \u7b49\u4eba\uff0c\u5c82\u80fd\u88ab\u533a\u533a\u8499\u6c57\u836f\u8ff7\u5012\uff0c\u8d3c\u53ae\u9e1f\uff0c\u4f60\u77a7\u4ed4\u7ec6\u4e86\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u53f3\u624b\u4e00\u626c\uff0c\u4e00\u9053\u6c34\u7bad\u4ece\u6307\u5c16\u5c04\u51fa\uff0c\u7aef\u7aef\u6b63\u6b63\u6ce8\u5165\u4f9b\u684c\u4e0a\u4e00\u53ea\u9526\u58f6\u4e2d\uff0c\u9152\u9999\u56db\u6ea2\u3002 \u539f\u6765\u8001\u8005\u5df2\u7528\u4e0a\u4e58\u5185\u529f\u628a\u4f53\u5185\u836f\u9152\u4ece\u6307\u5c16\u8feb\u51fa\uff0c\u90a3\u58ee\u6c49\u4f3c\u4e4e\u60ca\u5446\u4e86\uff0c\u8f6c\u8eab\u5c31\u9003\u3002 \u3000\u3000\u8001\u8005\u54c8\u54c8\u957f\u7b11\uff0c\u7b11\u58f0\u65b9\u655b\uff0c\u559d\u9053\uff1a\u201c\u6211\u5929\u715e\u661f\u541b\u624b\u4e0b\u4ece\u65e0\u9003\u751f\u4e4b\u4eba\uff0c\u5c82\u80fd\u5728\u4f60\u8fd9\u574f\u80da\u8eab \u4e0a\u7834\u4e86\u89c4\u77e9\uff0c\u77a7\u4f60\u5e73\u65e5\u867d\u7136\u4f5c\u6076\u591a\u7aef\uff0c\u4f46\u4e3a\u4eba\u5012\u4e5f\u723d\u76f4\uff0c\u4e0e\u4f60\u4e00\u4e2a\u75db\u5feb\u4fbf\u4e86\u3002\u201d \u3000\u3000\u8bf4\u7f62\u53cc\u624b\u865a\u7a7a\u62b1\u62f3\uff0c\u5411\u524d\u4e00\u9001\uff0c\u53ea\u542c\u89c1\u4e00\u58f0\u95f7\u54fc\uff0c\u58ee\u6c49\u5728\u4e08\u4f59\u5916\u5411\u524d\u5012\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u867d\u4e0d\u77e5\u8001\u8005\u7528\u4e86\u4ec0\u4e48\u529f\u592b\uff0c\u80fd\u4f7f\u4e00\u4e08\u5f00\u5916\u7684\u654c\u4eba\u53d7\u521b\u840e\u987f\uff0c\u4f46\u4ed6\u6015\u8001\u8005\u518d\u4e0b\u6bd2 \u624b\uff0c\u6025\u5fd9\u7a9c\u51fa\uff0c\u9ad8\u58f0\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u4ed6\u65e2\u7136\u6ca1\u6709\u6740\u4f24\u60a8\uff0c\u60a8\u5c31\u9976\u4ed6\u4e00\u547d\u5427\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u81ea\u6301\u8eab\u4efd\uff0c\u4e5f\u4e0d\u7b54\u8bdd\uff0c\u51b7\u51b7\u77a5\u4e86\u5c11\u5e74\u4e00\u773c\uff0c\u5782\u624b\u8d70\u5f00\u3002 \u3000\u3000\u5c11\u5e74\u8d70\u8fd1\u58ee\u6c49\uff0c\u4e00\u6478\u624b\u8109\uff0c\u5df2\u662f\u51b0\u51c9\uff0c\u5fc3\u4e2d\u5927\u60ca\uff0c\u60f3\u5230\u9002\u624d\u8fd8\u662f\u4e00\u4e2a\u6d3b\u751f\u751f\u7684\u5927\u6c49\u3002\u8f6c \u773c\u5c31\u6b7b\u5728\u8001\u8005\u4e00\u4e3e\u624b\u4e4b\u95f4\uff0c\u4e0d\u7981\u5f88\u611f\u540c\u60c5\uff0c\u5bf9\u4e8e\u8001\u8005\u6709\u4e9b\u4e0d\u6ee1\u3002 \u3000\u3000\u4ed6\u5f00\u53e3\u95ee\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u4f60\u5230\u5e95\u548c\u4ed6\u6709\u4ec0\u4e48\u4ec7\uff0c\u4e00\u5b9a\u8981\u6740\u4ed6\u5462\uff1f\u201d \u3000\u3000\u8001\u8005\u5934\u4e5f\u4e0d\u56de\uff0c\u4e0d\u7406\u4ed6\u6240\u95ee\u3002\u5c11\u5e74\u53c8\u9053\uff1a\u201c\u4ed6\u867d\u7136\u6697\u7b97\u4f60\uff0c\u8fd9\u662f\u4ed6\u4e0d\u5bf9\uff0c\u53ef\u662f\u4f60\u672c\u4e8b\u8fd9 \u4e48\u5927\uff0c\u5c31\u662f\u653e\u8fc7\u4ed6\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u4f24\u4f60\u2026\u2026\u201d \u3000\u3000\u8001\u8005\u4f3c\u4e4e\u5f88\u4e0d\u8010\uff0c\u5389\u58f0\u9053\uff1a\u201c\u4f60\u518d\u565c\u55e6\uff0c\u8fde\u4f60\u4e5f\u5bb0\u4e86\u3002\u201d \u3000\u3000\u5c11\u5e74\u6297\u58f0\u9053\uff1a\u201c\u4f60\u672c\u9886\u867d\u5927\uff0c\u53ef\u662f\u4f60\u4e0d\u5206\u9752\u7ea2\u7682\u767d\u7684\u5c31\u80e1\u4e71\u6740\u4eba\uff0c\u4eba\u5bb6\u89c1\u7740\u4f60\u90fd\u50cf\u89c1\u7740 \u960e\u738b\u4e00\u822c\uff0c\u4e5f\u4e0d\u89c1\u5f97\u662f\u5a01\u98ce\u5440\uff01\u201d \u3000\u3000\u8001\u8005\u56de\u5934\u659c\u773c\u77a7\u4e86\u5c11\u5e74\u4e00\u773c\uff0c\u53ea\u89c1\u4ed6\u4e00\u523b\u95f4\u5ffd\u7136\u5927\u4e49\u51db\u7136\uff0c\u7a1a\u6c14\u5168\u6d88\u3001\u8138\u4e0a\u65e0\u4e1d\u6beb\u754f\u60e7 \u4e4b\u8272\uff0c\u4e0d\u89c9\u5fc3\u6298\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u53c8\u9053\uff1a\u201c\u73b0\u5728\u4ed6\u65e2\u7136\u5df2\u88ab\u6253\u6b7b\uff0c\u54b1\u4eec\u4fbf\u628a\u4ed6\u846c\u4e86\u5427\uff0c\u514d\u5f97\u653e\u5728\u8fd9\u91ce\u5916\uff0c\u88ab\u91ce\u72fc\u62d6 \u53bb\u5403\u4e86\u3002\u201d \u3000\u3000\u8001\u8005\u7a81\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u53eb\u4ec0\u4e48\uff0c\u4f60\u5e08\u7236\u662f\u8c01\uff1f\u201d \u3000\u3000\u5c11\u5e74\u9053\uff1a\u201c\u6211\u53eb\u9ad8\u6218\uff0c\u6211\u6ca1\u6709\u5e08\u7236\u3002\u201d \u3000\u3000\u8001\u8005\u60f3\u8d77\u4ed6\u65b9\u624d\u786c\u67b6\u6d1b\u9633\u4e09\u9738\u8001\u4e09\u201c\u7384\u7384\u5200\u201d\u8c22\u957f\u4e49\u4e00\u5200\uff0c\u5185\u529b\u751a\u662f\u5145\u6c9b\uff0c\u770b\u6765\u81f3\u5c11\u6709 \u5eff\u5e74\u7684\u706b\u5019\uff0c\u4f46\u4ed6\u5e74\u7eaa\u6700\u591a\u4e0d\u8fc7\u5341\u4e03\u3001\u5341\u516b\u5c81\uff0c\u53ea\u9053\u662f\u540d\u95e8\u9ad8\u5f1f\uff0c\u81ea\u5e7c\u4e60\u6b66\uff0c\u4e0d\u60f3\u7adf\u7136\u6ca1\u6709 \u5e08\u7236\uff0c\u5f53\u4e0b\u95ee\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5185\u529f\u662f\u4f55\u4eba\u4f20\u6388\uff1f\u201d \u3000\u3000\u9ad8\u6218\u4ece\u5c0f\u4e0d\u6253\u8bf3\u8bed\uff0c\u4fbf\u628a\u5e74\u5e7c\u65f6\u5de7\u9047\u767d\u53d1\u8001\u4eba\uff0c\u96ea\u5730\u8bef\u98df\u5343\u5e74\u53c2\u738b\u7684\u4e8b\u8bf4\u51fa\u3002 \u3000\u3000\u90a3\u8001\u8005\u6c89\u541f\u4e0d\u8bed\uff0c\u9ad8\u6218\u4e58\u673a\u6e9c\u51fa\uff0c\u7528\u621f\u6398\u4e86\u4e00\u4e2a\u5927\u6d1e\uff0c\u628a\u58ee\u6c49\u62b1\u53bb\u57cb\u4e86\u3002 \u3000\u3000\u4ed6\u8d70\u56de\u5e99\u5185\uff0c\u90a3\u8001\u4eba\u4ecd\u5728\u6c89\u601d\uff0c\u9ad8\u6218\u4ee5\u4e3a\u4ed6\u5728\u540e\u6094\u65b9\u624d\u6740\u4eba\uff0c\u63a5\u53d7\u4e86\u81ea\u5df1\u7684\u529d\u544a\uff0c\u4e8e\u662f \u67d4\u58f0\u5b89\u6170\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u60a8\u522b\u540e\u6094\u5566\uff0c\u4e00\u4e2a\u4eba\u6c14\u7684\u65f6\u5019\uff0c\u5c31\u4f1a\u4e0d\u7ba1\u4e00\u5207\u7684\u505a\u51fa\u4efb\u4f55\u4e8b\u6765\uff0c\u6211 \u6709\u65f6\u4e5f\u6c14\u5f97\u7528\u77f3\u5b50\u6253\u6b7b\u5077\u98df\u7684\u9ec4\u9f20\u72fc\u54e9\uff01\u201d \u3000\u3000\u90a3\u8001\u8005\u542c\u4ed6\u8bf4\u5f97\u5929\u771f\uff0c\u4e0d\u89c9\u5931\u7b11\uff0c\u81ea\u5df2\u5345\u5e74\u524d\uff0c\u7eb5\u6a2a\u6e56\u6d77\uff0c\u662f\u4e00\u4e2a\u4eba\u4eba\u60e7\u6015\u7684\u8001\u9b54\u5934\uff0c \u60f3\u4e0d\u5230\u5345\u5e74\u540e\uff0c\u91cd\u51fa\u6c5f\u6e56\uff0c\u7adf\u88ab\u4e00\u4e2a\u5a03\u513f\u4fbf\u8f6f\u5e76\u65bd\uff0c\u5f04\u5f97\u6ca1\u505a\u624b\u811a\u5904\u3002 \u3000\u3000\u8001\u8005\u4ef0\u5929\u957f\u7b11\uff0c\u58f0\u5982\u9f99\u541f\uff0c\u62cd\u62cd\u9ad8\u6218\u80a9\u8180\u9053\uff1a\u201c\u5a03\u513f\uff0c\u771f\u6709\u4f60\u7684\uff0c\u6211\u8001\u4eba\u5bb6\u670d\u4f60\u5566\u3002\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u60a8\u522b\u751f\u6c14\u3002\u201d \u3000\u3000\u8001\u8005\u7ec6\u77a7\u4e86\u4ed6\u4e24\u773c\uff0c\u559f\u7136\u53f9\u9053\uff1a\u201c\u7075\u949f\u4e8e\u65af\uff0c\u79c0\u53d1\u4e4e\u5916\uff0c\u6148\u800c\u539a\uff0c\u5bbd\u800c\u752b\uff0c\u9886\u8896\u7fa4\u4f26\uff0c \u975e\u5b50\u800c\u8c01\uff0c\u5929\u610f\u5982\u6b64\uff0c\u592b\u590d\u4f55\u8a00\u3002\u201d \u3000\u3000\u9ad8\u6218\u542c\u4ed6\u5ffd\u7136\u60bc\u6587\uff0c\u5927\u4e3a\u4e0d\u89e3\uff0c\u4fbf\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u4f60\u521a\u521a\u4f7f\u7684\u662f\u4ec0\u4e48\u529f\u592b\uff0c\u53ef\u4ee5\u628a\u4eba\u5bb6 \u5236\u670d\u5f97\u4e00\u52a8\u90fd\u4e0d\u80fd\u52a8\uff1f\u201d \u3000\u3000\u8001\u8005\u77e5\u9053\u9ad8\u6218\u53ea\u5b66\u4f1a\u4e00\u5957\u5bb6\u4f20\u621f\u6cd5\u53ca\u4e00\u8eab\u4e0a\u4e58\u5185\u529f\uff0c\u5176\u4ed6\u6b66\u6280\u662f\u4e00\u6168\u4e0d\u61c2\uff0c\u662f\u4ee5\u8fde\u70b9\u7a74 \u90fd\u770b\u4e0d\u51fa\uff0c\u4fbf\u7b11\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u77a7\u90a3\u624b\u529f\u592b\u600e\u6837\uff1f\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u771f\u5e05\u6781\u4e86\uff0c\u8001\u4f2f\u4f2f\uff0c\u4f60\u672c\u4e8b\u771f\u5927\uff0c\u4e00\u638c\u53ef\u4ee5\u6253\u6b7b\u4e00\u4e08\u5916\u7684\u4eba\uff0c\u665a\u8f88\u53ea\u8981\u6709\u60a8 \u4e00\u534a\u529f\u592b\u5c31\u597d\u5566\u3002\u201d \u3000\u3000\u8001\u8005\u5475\u5475\u7b11\u8fc7\uff1a\u201c\u5c0f\u5b50\uff0c\u60a8\u5634\u771f\u751c\uff0c\u6211\u8001\u4eba\u5bb6\u5c31\u628a\u8fd9\u624b\u4f20\u4e86\u4f60\u5427\uff01\u201d \u3000\u3000\u9ad8\u6218\u5927\u559c\uff0c\u8fde\u5fd9\u8dea\u4e0b\uff0c\u8001\u8005\u4f38\u624b\u4e00\u6276\uff0c\u4e0d\u7531\u5403\u4e86\u4e00\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5a03\u513f\u4f53\u5185\u771f\u529b\u4e0d\u5f31\uff0c \u867d\u8bf4\u662f\u5343\u5e74\u53c2\u738b\u4e4b\u529f\uff0c\u53ef\u662f\u5c0f\u5c0f\u5e74\u7eaa\u6709\u6b64\u6210\u5c31\uff0c\u90a3\u4e48\u4f20\u4ed6\u5185\u529f\u7684\u4eba\uff0c\u4e00\u5b9a\u662f\u7f55\u89c1\u9ad8\u624b\u4e86\uff0c\u6211 \u867d\u9690\u5c45\u5eff\u4f59\u591a\u5e74\u4e0d\u95ee\u6c5f\u6e56\u4e2d\u4e8b\uff0c\u53ef\u662f\u5929\u4e0b\u9664\u4e86\u2018\u4e1c\u6d77\u4e09\u4ed9\u2019\uff0c\u2018\u5357\u5317\u4e8c\u541b\u2019\u5916\uff0c\u96be\u9053\u8fd8\u53e6\u6709 \u9ad8\u624b\u4e0d\u6210\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u6614\u5e74\u786e\u662f\u53f1\u54a4\u6e56\u6d77\u7684\u597d\u6c49\uff0c\u662f\u4ee5\u9664\u4e86\u201c\u4e09\u4ed9\u201d\uff0c\u201c\u4e8c\u541b\u201d\uff0c\u4ed6\u4ee5\u4e3a\u5b87\u5185\u518d\u65e0\u9ad8 \u624b\uff0c\u4ed6\u9690\u5c45\u5eff\u4f59\u5e74\uff0c\u6b64\u6b21\u91cd\u5165\u6c5f\u80e1\uff0c\u7adf\u4e0d\u77e5\u8fd1\u5e74\u6765\u6c5f\u6e56\u4e0a\u51fa\u73b0\u4e86\u8bb8\u591a\u4e00\u7b49\u4e00\u6d41\u7684\u5e74\u8f7b\u5251\u5ba2\u3002 \u3000\u3000\u4ed6\u4f38\u51fa\u53f3\u638c\uff0c\u6309\u5728\u9ad8\u6218\u80a9\u4e0a\uff0c\u5185\u529b\u7f13\u7f13\u800c\u53d1\uff0c\u53ea\u89c9\u9ad8\u6545\u4f53\u5185\u771f\u529b\u4e00\u6536\u4e00\u6297\uff0c\u529b\u9053\u4e00\u6b21\u6b64 \u4e00\u6b21\u5f3a\u52b2\uff0c\u4e0d\u89c9\u604d\u7136\u5927\u609f\uff0c\u5fd6\u9053\uff1a\u201c\u5929\u4e0b\u5185\u529f\u80fd\u6536\u53d1\u5e76\u65bd\u7684\u6562\u8bf4\u53ea\u6709\u5173\u5916\u76df\u4e3b\u98ce\u67cf\u6768\u4e00\u6d3e\uff0c \u7167\u6b64\u770b\u6765\uff0c\u8fd9\u8001\u513f\u529f\u529b\u6df1\u539a\uff0c\u51b3\u4e0d\u5728\u6211\u4e4b\u4e0b\u3002\u201d \u3000\u3000\u8001\u8005\u9053\uff1a\u201c\u5a03\u513f\uff0c\u6211\u8fd9\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u4e0e\u5404\u6d3e\u5927\u662f\u4e0d\u540c\uff0c\u65e5\u540e\u4f60\u65bd\u5c55\u65f6\u5343\u4e07\u5c0f\u5fc3\uff0c\u4e00\u65e6\u88ab \u4eba\u8bc6\u7834\uff0c\u6211\u6614\u5e74\u4ec7\u4eba\u591a\u5f97\u4e0d\u80fd\u8ba1\u6570\uff0c\u90a3\u4f60\u53ef\u9ebb\u70e6\u5566\uff01\u201d \u3000\u3000\u9ad8\u6218\u70b9\u5934\u7b54\u5e94\uff0c\u90a3\u8001\u8005\u5f53\u4e0b\u5728\u706f\u4e0b\u5c31\u628a\u4eba\u8eab\u5404\u79cd\u7a74\u9053\u7684\u4f4d\u7f6e\u4ed4\u7ec6\u7684\u8bb2\u4e86\uff0c\u5e76\u4f20\u4e86\u70b9\u7a74\u624b \u6cd5\uff0c\u9ad8\u6218\u6089\u5fc3\u5b66\u4e60\uff0c\u82e6\u7ec3\u4e86\u534a\u591c\uff0c\u8001\u8005\u5df1\u547c\u547c\u7761\u53bb\u3002 \u3000\u3000\u9ad8\u6218\u81ea\u89c9\u624b\u6cd5\u7eaf\u719f\uff0c\u4e5f\u4f0f\u7740\u4f9b\u684c\u7761\u7740\u4e86\uff0c\u5f85\u4ed6\u9192\u6765\uff0c\u8001\u8005\u5df2\u8d70\uff0c\u4ed6\u89c1\u5929\u8272\u5927\u660e\uff0c\u5c31\u9a91\u7740 \u7626\u9a6c\u8fdb\u4e86\u57ce\u3002 \u3000\u3000\u9ad8\u6218\u8d70\u8fdb\u4e00\u5bb6\u5c0f\u5e97\uff0c\u8981\u4e86\u65e9\u996d\uff0c\u4ed6\u5de6\u8fb9\u684c\u5b50\u662f\u4e24\u4e2a\u6c5f\u6e56\u6c49\u5b50\uff0c\u4e00\u9ad8\u4e00\u77ee\uff0c\u8fb9\u5403\u8fb9\u5439\uff0c\u8c08 \u5f97\u5174\u9ad8\u91c7\u70c8\u3002 \u3000\u3000\u90a3\u9ad8\u6c49\u5b50\u9053\uff1a\u201c\u8001\u4e94\uff0c\u4f60\u77a7\u54b1\u4eec\u74e2\u628a\u5b50\u6709\u65e0\u628a\u63e1\u8d62\u8fc7\u6cb3\u6714\u53cc\u96c4\uff1f\u201d \u3000\u3000\u77ee\u6c49\u54ac\u4e86\u4e00\u53e3\u5927\u997c\uff0c\u542b\u542b\u7cca\u7cca\u9053\uff1a\u201c\u522b\u8bf4\u6cb3\u6714\u53cc\u96c4\uff0c\u5c31\u662f\u5d24\u5c71\u4e03\u715e\uff0c\u5144\u5f1f\u4e03\u4eba\uff0c\u4e2a\u4e2a\u90fd \u6709\u4e00\u8eab\u7edd\u827a\uff0c\u5c82\u662f\u597d\u60f9\u7684\u3002\u201d \u3000\u3000\u90a3\u9ad8\u6c49\u9053\uff1a\u201c\u542c\u8bf4\u6d1b\u9633\u4e09\u9738\u8001\u5927\u3001\u8001\u4e8c\u90fd\u7ed9\u4eba\u5bb0\u5566\u3002\u8fd9\u6837\u54b1\u4eec\u74e2\u628a\u5b50\u5c11\u4e86\u4e24\u4e2a\u5f3a\u654c\uff0c\u5012 \u662f\u597d\u6d88\u606f\u3002\u201d \u3000\u3000\u77ee\u6c49\u9053\uff1a\u201c\u8001\u516d\uff0c\u4f60\u522b\u9ad8\u5174\uff0c\u4f60\u60f3\u60f3\u770b\u4eba\u5bb6\u6d1b\u9633\u4e09\u9738\u529f\u592b\u53ef\u4e0d\u542b\u7cca\uff0c\u5728\u4e00\u591c\u4e4b\u95f4\u8ba9\u4eba\u795e \u4e0d\u77e5\uff0c\u9b3c\u4e0d\u89c9\u7ed9\u5e9f\u4e86\uff0c\u6b64\u4eba\u529f\u529b\u4e4b\u9ad8\uff0c\u53ef\u60f3\u800c\u77e5\uff0c\u5982\u679c\u6b64\u4eba\u51fa\u73b0\uff0c\u54b1\u4eec\u6cb3\u5357\u597d\u6c49\u53ea\u6015\u6ca1\u4e00\u4eba \u662f\u5bf9\u624b\u4e86\u5427\uff01\u201d \u3000\u3000\u90a3\u9ad8\u6c49\u9053\uff1a\u201c\u6628\u665a\u2018\u6d4e\u5357\u5927\u8c6a\u2019\uff0c\u2018\u79e6\u5cad\u53cc\u4fa0\u2019\u90fd\u5230\u5566\uff0c\u8fd9\u6b21\u5317\u65b9\u7eff\u6797\u5927\u4f1a\uff0c\u603b\u74e2\u628a\u5b50 \u5927\u4f4d\u5012\u5e95\u843d\u4e8e\u8c01\u4e8b\u5c1a\u4e0d\u53ef\u77e5\u54e9\uff01\u201d \u3000\u3000\u77ed\u6c49\u9053\uff1a\u201c\u8001\u516d\uff0c\u8d70\u5566\uff0c\u4e0b\u5348\u7ade\u6280\u5927\u4f1a\u5c31\u5f00\u59cb\uff0c\u54b1\u4eec\u4e5f\u8981\u56de\u53bb\u51c6\u5907\u51c6\u5907\u3002\u201d \u3000\u3000\u4e24\u4eba\u4ed8\u4e86\u5e10\uff0c\u5927\u6447\u5927\u6446\u8d70\u51fa\u5c0f\u5e97\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u60f3\uff1a\u201c\u6d1b\u9633\u4e09\u9738\u4e2d\u8001\u4e09\uff0c\u6628\u591c\u4e5f\u6b7b\u5728\u57ce\u90ca\u53e4\u5e99\uff0c\u8fd9\u4e9b\u6c5f\u6e56\u6c49\u5b50\uff0c\u4e00\u751f\u4e89\u5f3a\u6597\u80dc\uff0c \u5230\u5934\u6765\u547d\u4e27\u8352\u90ca\uff0c\u662f\u53c8\u4f55\u5fc5\u5462\uff1f\u771f\u662f\u7b28\u5f97\u5f88\u5440\u3002\u201d \u3000\u3000\u8f6c\u5ff5\u53c8\u60f3\u5230\uff1a\u201c\u8fd9\u5317\u65b9\u82f1\u96c4\u5927\u4f1a\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u4e2a\u6837\u5b50\uff0c\u6211\u4f55\u4e0d\u53bb\u89c1\u8bc6\u89c1\u8bc6\uff0c\u76f8\u673a\u529d\u529d\u5927 \u5bb6\uff0c\u4e0d\u5fc5\u81ea\u76f8\u6b8b\u6740\uff0c\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u65e2\u7136\u5931\u8eab\u7eff\u6797\uff0c\u5982\u679c\u53ea\u77e5\u6740\u4eba\u4ee5\u901e\uff0c\u5206\u8d43\u4ee5\u5bcc\uff0c\u90a3\u771f\u662f \u6c38\u5760\u5730\u72f1\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u6253\u5b9a\u4e3b\u610f\uff0c\u5c31\u5306\u5306\u5fd9\u5fd9\u8ddf\u4e0a\u524d\u53bb\u3002\u4ed6\u5929\u6027\u5b9e\u662f\u6de1\u6cca\u3002\u5904\u5904\u5f80\u597d\u5904\u60f3\uff0c\u80f8\u4e2d\u5c3d\u662f\u4e9b\u5584 \u826f\u53ef\u7231\u7684\u5ff5\u5934\uff0c\u628a\u522b\u4eba\u90fd\u60f3\u6210\u548c\u81ea\u5df1\u4e00\u822c\uff0c\u5176\u5b9e\u201c\u540d\u201d\u201c\u5229\u201d\u5f53\u524d\uff0c\u81ea\u53e4\u4ee5\u6765\uff0c\u53c8\u6709\u51e0\u4eba\u80fd \u8df3\u8d8a\u4e0d\u987e\u5462\uff1f \u3000\u3000\u4ed6\u8ffd\u5230\u4e24\u4e2a\u6c49\u5b50\u8eab\u540e\uff0c\u9053\uff1a\u201c\u4e24\u4f4d\u5927\u54e5\u8bf7\u7559\u6b65\uff0c\u5c0f\u5f1f\u6709\u4e8b\u76f8\u95ee\u3002\u201d \u3000\u3000\u9ad8\u3001\u77ee\u4e8c\u6c49\u679c\u7136\u6b62\u6b65\uff0c\u56de\u5934\u4e00\u770b\u6b63\u662f\u9002\u624d\u5728\u9152\u5e97\u4e2d\u76f8\u9047\u5c11\u5e74\uff0c\u4e0d\u7531\u5fae\u611f\u9519\u6115\u3002\u9ad8\u6218\u53c8 \u9053\uff1a\u201c\u5c0f\u5f1f\u9002\u624d\u542c\u4e24\u4f4d\u5927\u54e5\u8c08\u8d77\u7eff\u6797\u5927\u4f1a\uff0c\u771f\u662f\u5411\u5f80\u5f97\u5f88\uff0c\u4e0d\u77e5\u4e24\u4f4d\u53ef\u5426\u5e26\u5c0f\u5f1f\u53bb\u89c1\u8bc6\u4e00 \u756a\uff1f\u201d \u3000\u3000\u90a3\u9ad8\u6c49\u89c1\u4ed6\u8eab\u4e0a\u7a7f\u5f97\u6734\u7d20\uff0c\u4f46\u957f\u5f97\u5507\u7ea2\u9f7f\u767d\uff0c\u5f88\u662f\u53ef\u7231\uff0c\u4ed6\u672c\u662f\u76f4\u6027\u6c49\u5b50\uff0c\u89c1\u9ad8\u6218\u8c26\u548c \u6709\u793c\uff0c\u5148\u751f\u51e0\u5206\u597d\u611f\u3002\u95fb\u8a00\u7b54\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4e0d\u53ef\uff0c\u8fd9\u7eff\u6797\u7ade\u6280\u5927\u4f1a\u5728\u54b1\u4eec\u5e84\u91cc\u4e3e\u884c\uff0c\u5404\u8def \u82f1\u96c4\u90fd\u5df1\u805a\u96c6\uff0c\u4e0b\u5348\u5c31\u8981\u5f00\u59cb\uff0c\u8001\u5f1f\uff0c\u4f60\u662f\u54ea\u4e00\u6d3e\u95e8\u4e0b\u5440\uff1f\u201d \u3000\u3000\u9ad8\u6218\u4e0d\u5584\u8bf4\u8c0e\uff0c\u53ea\u5f97\u652f\u543e\u5176\u8bcd\uff0c\u62d6\u5f00\u8bdd\u9898\u9053\uff1a\u201c\u5c0f\u5f1f\u751f\u6027\u597d\u6b66\uff0c\u53ea\u662f\u672a\u9047\u540d\u5e08\uff0c\u6240\u4ee5\u5b66 \u5f97\u51e0\u624b\u5e84\u5bb6\u628a\u5f0f\u3002\u201d \u3000\u3000\u90a3\u9ad8\u6c49\u5b50\u77e5\u4ed6\u4e0d\u4fbf\u8bf4\u51fa\uff0c\u4e5f\u5c31\u4e0d\u518d\u76f8\u95ee\uff0c\u4e09\u4eba\u4e00\u884c\uff0c\u5411\u57ce\u4e1c\u8d70\u53bb\u3002 \u3000\u3000\u8d70\u4e86\u4e00\u523b\uff0c\u6765\u5230\u4e00\u5ea7\u5927\u9662\u843d\u524d\uff0c\u53ea\u89c1\u95e8\u53e3\u4e24\u5c0a\u77f3\u72ee\uff0c\u5927\u95e8\u662f\u9ed1\u6f06\u9576\u91d1\u8fb9\uff0c\u751a\u662f\u6c14\u6d3e\uff0c\u95e8 \u524d\u7ad9\u7740\u51e0\u4e2a\u58ee\u6c49\uff0c\u50cf\u662f\u63a5\u5f85\u6765\u5bbe\u3002 \u3000\u3000\u5ffd\u7136\u4ece\u91cc\u9762\u8d70\u51fa\u4e00\u4e2a\u4e2d\u5e74\u4e66\u751f\uff0c\u9762\u8c8c\u6e29\u6587\uff0c\u671b\u4e86\u4e09\u4eba\u4e00\u773c\uff0c\u5bf9\u77ee\u6c49\u5b50\u9053\uff1a\u201c\u5434\u8235\u4e3b\uff0c\u8fd9 \u4f4d\u8001\u5f1f\u662f\u54ea\u5bb6\u82f1\u96c4\u95e8\u4e0b\uff0c\u957f\u5f97\u597d\u4fca\u5440\uff01\u201d \u3000\u3000\u9ad8\u6218\u8138\u4e00\u7ea2\uff0c\u62b1\u62f3\u9053\uff1a\u201c\u5c0f\u53ef\u9ad8\u6218\uff0c\u60f3\u6765\u89c1\u8bc6\u5317\u65b9\u7eff\u6797\u82f1\u96c4\u5927\u4f1a\u3002\u201d \u3000\u3000\u90a3\u4e66\u751f\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u201d \u3000\u3000\u8bf4\u7f62\u53c8\u53bb\u62db\u547c\u65b0\u6765\u5ba2\u4eba\u3002 \u3000\u3000\u9ad8\u6c49\u5b50\u9053\uff1a\u201c\u9ad8\u8001\u5f1f\uff0c\u90a3\u4e2d\u5e74\u4e66\u751f\u5c31\u662f\u54b1\u4eec\u4e3b\u4eba\u957f\u5b50\uff0c\u4eba\u79f0\u2018\u94c1\u5251\u4e66\u751f\u2019\u6797\u51b2\uff0c\u9ad8\u8001 \u5f1f\uff0c\u4f60\u5f85\u4f1a\u5411\u53f3\u8fb9\u90a3\u95f4\u9662\u843d\u53bb\u3002\u81ea\u6709\u4eba\u62db\u547c\u4f60\u4f4f\u5bbf\uff0c\u54b1\u4eec\u4e0b\u5348\u89c1\u3002\u201d \u3000\u3000\u9ad8\u6218\u89c1\u4ed6\u5f88\u8bda\u6073\uff1a\u201c\u5fc3\u60f3\u6b64\u4eba\u867d\u662f\u7eff\u6797\uff0c\u4f46\u8fd8\u4e0d\u5931\u4e3a\u662f\u6761\u6b63\u76f4\u6c49\u5b50\u201d\uff0c\u4fbf\u4f9d\u8a00\u8d70\u5230\u53f3\u8fb9 \u9662\u843d\uff0c\u7a7f\u8fc7\u62f1\u95e8\uff0c\u53c8\u662f\u4e00\u756a\u5929\u5730\uff0c\u53ea\u89c1\u5047\u5c71\u55b7\u6cc9\uff0c\u82b1\u5f00\u5982\u7ec7\uff0c\u9c9c\u8349\u5982\u8335\uff0c\u5982\u4eba\u4ed9\u5883\uff0c\u5fc3\u4e2d\u6697 \u6697\u5fd6\u9053\uff1a\u201c\u8fd9\u5e84\u4e3b\u7aef\u7684\u6709\u94b1\uff0c\u53ea\u662f\u5982\u679c\u6765\u4e4b\u4e0d\u4e49\uff0c\u90a3\u4e48\u867d\u7136\u5bcc\u4e3d\u8c6a\u534e\uff0c\u53ea\u6015\u5fc3\u4e2d\u4e5f\u672a\u5fc5\u5feb \u6d3b\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u5e84\u843d\u5524\u7740\u201c\u6708\u4e91\u5c71\u5e84\u201d"\u3002\u4e3b\u4eba\u98ce\u4e91\u5251\u6797\u9aa7\u539f\u662f\u4f0f\u725b\u5c71\u7eff\u6797\u5927\u8c6a\uff0c\u4e0e\u5f53\u5e74\u5173 \u4e2d\u201c\u9ec4\u4e30\u4e5d\u8c6a\u201d\u9f50\u540d\uff0c\u540e\u6765\u6b66\u6797\u5927\u4fa0\u201c\u6cb3\u6d1b\u4e00\u5251\u201d\u5434\u8bcf\u4e91\u5d1b\u8d77\uff0c\u5434\u8bcf\u4e91\u5012\u4e5f\u656c\u91cd\u6797\u9aa7\u662f\u6761\u6c49 \u5b50\uff0c\u867d\u5219\u6295\u8eab\u7eff\u6797\uff0c\u4f46\u4e00\u751f\u672a\u72af\u6deb\u6212\uff0c\u624b\u4e0b\u4e5f\u591a\u80fd\u4e25\u5b88\u7eff\u6797\u9053\u4e49\uff0c\u662f\u4ee5\u5bf9\u4ed6\u5e76\u4e0d\u5e72\u6d89\u3002 \u3000\u3000\u53ef\u662f\u6709\u4e00\u6b21\uff0c\u6797\u9aa7\u624b\u4e0b\u6709\u4e00\u540d\u5f97\u529b\u5934\u76ee\u7adf\u52ab\u4e86\u4e00\u4f4d\u671d\u5ef7\u544a\u8001\u6e05\u5b98\uff0c\u800c\u4e14\u628a\u5168\u5bb6\u8001\u5c0f\u5341\u53e3 \u65a9\u7edd\uff0c\u5434\u8bcf\u4e91\u5f97\u77e5\u540e\u5fc3\u4e2d\u5927\u6012\uff0c\u5355\u8eab\u53ea\u5251\u6765\u5230\u8ffd\u4e91\u5251\u5927\u5be8\uff0c\u58f0\u8a00\u8981\u6797\u9aa7\u4ea4\u51fa\u90a3\u540d\u5934\u76ee\u3002\u6797\u9aa7 \u5f53\u65f6\u77e5\u5c48\u5728\u5df1\u65b9\uff0c\u53ef\u662f\u81ea\u4ed8\u5b9e\u529b\u575a\u5f3a\uff0c\u53c8\u53d7\u5de6\u53f3\u86ca\u611f\u3002\u90a3\u6cb3\u6d1b\u4e00\u5251\u5434\u8bcf\u4e91\uff0c\u4e5f\u662f\u5e74\u9752\u6c14\u76db\uff0c \u8a00\u8f9e\u8fc7\u6fc0\uff0c\u4e24\u4eba\u7ec8\u4e8e\u8bf4\u7ffb\uff0c\u52a8\u8d77\u624b\u6765\u3002 \u3000\u3000\u201c\u6cb3\u6d1b\u4e00\u5251\u201d\u5f53\u5e74\u662f\u5a01\u9707\u5317\u65b9\u5e74\u9752\u5927\u4fa0\uff0c\u529f\u529b\u4e4b\u9ad8\u4ee4\u4eba\u4e0d\u53ef\u6349\u6478\uff0c\u6797\u9aa7\u624b\u5e95\u867d\u4e5f\u4e0d\u5f31\uff0c \u4f46\u6bd4\u8d77\u6cb3\u6d1b\u4e00\u5251\uff0c\u5230\u5e95\u5dee\u4e86\u4e00\u7b79\u3002\u5f53\u5434\u8bcf\u4e91\u65bd\u51fa\u65ad\u9b42\u5251\u6cd5\u4e2d\u8fde\u73af\u4e09\u7edd\u5f0f\u201c\u65e0\u5e38\u628a\u53c9\u201d\u201c\u9b3c\u738b \u95ee\u8def\u201d\u201c\u70b9\u70b9\u78f7\u661f\u201d\u65f6\uff0c\u4e00\u4e2a\u6536\u624b\u4e0d\u4f4f\uff0c\u523a\u4f24\u6797\u9aa7\u53f3\u80a9\u3002 \u3000\u3000\u98ce\u4e91\u5251\u6797\u9aa7\u4ece\u6b64\u518d\u65e0\u9762\u76ee\u5728\u6c5f\u6e56\u4e0a\u6df7\uff0c\u4ed6\u4ea4\u51fa\u90a3\u6740\u4eba\u5934\u76ee\u540e\uff0c\u5c31\u89e3\u6563\u5927\u5be8\uff0c\u5e26\u7740\u5bb6\u5c0f\u4eb2 \u4fe1\uff0c\u9690\u5c45\u6b64\u5904\u3002 \u3000\u3000\u6cb3\u6d1b\u4e00\u5251\u5434\u8bcf\u4e91\uff0c\u7ecf\u6b64\u4e00\u5f79\uff0c\u5355\u8eab\u6311\u7ffb\u96c4\u636e\u4f0f\u725b\u5c71\u4e8e\u4f59\u5e74\u4e4b\u6797\u9aa7\uff0c\u58f0\u540d\u66f4\u662f\u5982\u65e5\u4e2d\u5929\uff0c \u7ec8\u4e8e\u60f9\u8d77\u4e2d\u5dde\u4e94\u5927\u5251\u6d3e\uff0c\u8054\u624b\u51fa\u51fb\uff0c\u547d\u4e27\u5929\u7ec5\u7011\u524d\u3002 \u3000\u3000\u98ce\u4e91\u5251\u6797\u9aa7\u867d\u8bf4\u9000\u51fa\u6c5f\u6e56\uff0c\u4f46\u6c5f\u5c71\u6613\u6539\uff0c\u79c9\u6027\u96be\u79fb\uff0c\u8fd8\u4e0d\u65f6\u548c\u6c5f\u6e56\u7eff\u6797\u4e92\u901a\u58f0\u606f\u3002\u6b64\u6b21 \u6cb3\u5357\u5168\u7701\u7eff\u6797\u5927\u4f1a\u51b3\u5b9a\u5728\u4ed6\u5e84\u4e2d\u4e3e\u884c\uff0c\u8fdc\u8fd1\u7eff\u6797\u90fd\u5c0a\u4ed6\u4e00\u58f0\u8001\u524d\u8f88\uff0c\u4ed6\u8fd9\u4eba\u5929\u751f\u597d\u540d\uff0c\u89c1\u5927 \u5bb6\u90fd\u7ed9\u4ed6\u9762\u5b50\uff0c\u81ea\u7136\u4e50\u4e8e\u63a5\u53d7\u3002 \u3000\u3000\u4e14\u8bf4\u9ad8\u6218\u88ab\u53f3\u9662\u7ba1\u5bb6\u5b89\u7f6e\u5728\u6700\u540e\u51e0\u95f4\u5c4b\u4e2d\uff0c\u4ed6\u5012\u4e5f\u4e0d\u5728\u4e4e\uff0c\u53ea\u89c1\u53f3\u9662\u90fd\u662f\u5e74\u9752\u4eba\uff0c\u4f46\u4e00 \u4e2a\u4e2a\u4e0d\u662f\u9a84\u6c14\u51cc\u4eba\uff0c\u5c31\u662f\u66b4\u623e\u4e4b\u8272\u4e0a\u8138\uff0c\u5fc3\u4e2d\u5f88\u611f\u4e0d\u8010\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u822c\u4eba\u591a\u534a\u4ed7\u7740\u7236\u4eb2\u6216\u8005 \u5e08\u7236\u7684\u58f0\u540d\uff0c\u5728\u6b64\u8000\u6b66\u626c\u5a01\u3002\u201d \u3000\u3000\u5403\u8fc7\u5348\u996d\uff0c\u4ed6\u60f3\u5927\u4f1a\u8fd8\u6709\u4e00\u4e2a\u65f6\u8fb0\u624d\u5f00\u59cb\uff0c\u5c31\u6f2b\u6b65\u5230\u5904\u8d70\u8d70\uff0c\u8d70\u4e86\u534a\u5929\uff0c\u8d70\u5230\u540e\u5e84\uff0c\u539f \u6765\u662f\u4e00\u7247\u6797\u56ed\uff0c\u683d\u6ee1\u4e86\u67f3\u6811\u3002 \u3000\u3000\u4ed6\u65e0\u804a\u7684\u8e22\u7740\u811a\u4e0b\u9ec4\u571f\uff0c\u6b63\u5f85\u79bb\u53bb\uff0c\u7a81\u7136\u542c\u89c1\u5175\u5203\u53f1\u559d\u4e4b\u58f0\uff0c\u5c31\u63a2\u8eab\u5165\u5185\u3002 \u3000\u3000\u53ea\u89c1\u6797\u4e2d\u4e00\u5757\u7a7a\u5730\u4e0a\uff0c\u4e8c\u4e2a\u9752\u5e74\u6b63\u5728\u6fc0\u70c8\u62fc\u6597\uff0c\u4e00\u4e2a\u4ed7\u7740\u957f\u5251\uff0c\u4e00\u4e2a\u821e\u7740\u5ce8\u7709\u523a\uff0c\u6740\u5f97 \u6709\u58f0\u6709\u8272\u3002 \u3000\u3000\u9ad8\u6218\u672c\u6765\u4e0d\u60f3\u591a\u7ba1\u95f2\u4e8b\uff0c\u5fc3\u60f3\u8fd9\u822c\u4eba\u90fd\u662f\u4e00\u6837\u65e0\u793c\u4e56\u5f20\uff0c\u4f46\u89c1\u90a3\u4f7f\u5251\u7684\u4eba\uff0c\u5251\u5251\u72e0\u8fa3\uff0c \u4f3c\u4e4e\u60f3\u7f6e\u4f7f\u90a3\u5ce8\u7709\u523a\u7684\u4eba\u4e8e\u6b7b\u547d\uff0c\u90a3\u4f7f\u5ce8\u7709\u523a\u7684\u9752\u5e74\uff0c\u5de6\u53f3\u906e\u67b6\uff0c\u773c\u770b\u5c31\u8981\u843d\u8d25\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u4e0d\u5fcd\uff0c\u4fbf\u7a9c\u51fa\u5927\u58f0\u53eb\u9053\uff1a\u201c\u4e24\u4f4d\u4f4f\u624b\u3002\u201d \u3000\u3000\u90a3\u4f7f\u5ce8\u7709\u523a\u7684\uff0c\u770b\u5230\u6709\u4eba\u51fa\u9762\u89e3\u56f4\uff0c\u4e0d\u7531\u5927\u559c\uff0c\u95fb\u58f0\u679c\u7136\u4f4f\u624b\uff0c\u4f7f\u5251\u7684\u9752\u5e74\u60f3\u662f\u6068\u6781\uff0c \u4e58\u52bf\u957f\u5251\u4e00\u633a\uff0c\u201c\u6bd2\u86c7\u51fa\u6d1e\u201d\uff0c\u5411\u5bf9\u624b\u5589\u5934\u523a\u53bb\u3002 \u3000\u3000\u9ad8\u6218\u53c8\u60ca\u53c8\u6012\uff0c\u4e0d\u6687\u591a\u60f3\uff0c\u53f3\u624b\u4e00\u4f38\uff0c\u77ed\u621f\u5728\u624b\uff0c\u633a\u8eab\u5411\u4f7f\u5251\u9752\u5e74\u8eab\u540e\u5288\u53bb\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u6b63\u8981\u5f97\u624b\uff0c\u7a81\u58f0\u80cc\u540e\u98ce\u58f0\u5927\u4f5c\uff0c\u53ea\u6709\u5148\u6c42\u81ea\u4fdd\uff0c\u9ad8\u6218\u539f\u4e0d\u60f3\u4f24\u4ed6\uff0c\u89c1\u4ed6\u56de\u5251\u6765 \u51fb\uff0c\u5c31\u5411\u540e\u9000\u4e86\u4e00\u6b65\u3002 \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u4e24\u4f4d\u5230\u5e95\u6709\u4f55\u5927\u4ec7\uff0c\u5b9a\u987b\u751f\u6b7b\u76f8\u62fc\uff1f\u201d \u3000\u3000\u90a3\u4f7f\u5251\u7684\u4e00\u8a00\u4e0d\u53d1\uff0c\u671d\u7740\u9ad8\u6218\u8fde\u523a\u4e09\u5251\uff0c\u9ad8\u6218\u5de6\u95ea\u53f3\u8eb2\uff0c\u53f3\u81c2\u8863\u895f\u8fd8\u662f\u88ab\u5212\u7834\u4e86\u4e00\u5757\u3002 \u3000\u3000\u9ad8\u6218\u5927\u6012\uff0c\u9a82\u9053\uff1a\u201c\u4e5f\u6ca1\u89c1\u8fc7\u8fd9\u4e48\u4e0d\u8bb2\u7406\u7684\u6d51\u4eba\u3002\u201d \u3000\u3000\u4f7f\u5251\u5c11\u5e74\u6c89\u58f0\u9053\uff1a\u201c\u4eca\u65e5\u5c31\u53eb\u4f60\u89c1\u89c1\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u77e5\u4e0d\u80fd\u5584\u4e86\uff0c\u53f3\u624b\u4e00\u6296\u77ed\u621f\uff0c\u98ce\u96f7\u4e4b\u58f0\u7acb\u4f5c\uff0c\u90a3\u5c11\u5e74\u89c1\u4ed6\u529f\u529b\u6df1\u539a\uff0c\u4e0d\u6562\u6020\u6162\uff0c \u5251\u8d70\u504f\u950b\uff0c\u8e0f\u4e2d\u5bab\uff0c\u76f4\u523a\u9ad8\u6218\u4e24\u773c\u3002 \u3000\u3000\u9ad8\u6218\u7acb\u523b\u65bd\u5c55\u201c\u9ad8\u5bb6\u621f\u6cd5\u201d\uff0c\u6a2a\u5288\u76f4\u780d\uff0c\u62db\u62db\u529b\u5927\u52bf\u6c89\u3002 \u3000\u3000\u8981\u77e5\u8fd9\u201c\u9ad8\u5bb6\u56db\u5341\u4e5d\u8def\u65e0\u654c\u621f\u6cd5\u201d\uff0c\u539f\u662f\u7528\u5728\u5343\u519b\u4e07\u9a6c\u4e2d\uff0c\u51b2\u950b\u9677\u9635\uff0c\u662f\u4ee5\u53ea\u662f\u8bb2\u6c42\u6210 \u731b\uff0c\u8bf4\u5230\u62db\u5f0f\u5de7\u5999\u5012\u4e5f\u4e0d\u89c1\u5f97\u5982\u4f55\u9ad8\u660e\uff0c\u9ad8\u6218\u81ea\u5e7c\u670d\u98df\u5343\u5e74\u53c2\u738b\uff0c\u53c8\u7ec3\u5c31\u4e0a\u4e58\u5185\u529f\uff0c\u771f\u529b\u6df1 \u957f\uff0c\u65bd\u8d77\u77ed\u621f\uff0c\u771f\u662f\u795e\u5a01\u51db\u51db\u3002 \u3000\u3000\u9ad8\u6218\u89c1\u4e45\u6218\u4ed6\u4e0d\u4e0b\uff0c\u5fc3\u5185\u5fae\u70e6\uff0c\u81ea\u5fd6\u7b2c\u4e00\u6b21\u4e0e\u4eba\u4ea4\u624b\uff0c\u5c31\u4e0d\u80fd\u53d6\u80dc\uff0c\u5c06\u6765\u5982\u4f55\u95ef\u8361\u6c5f \u6e56\uff0c\u53f3\u624b\u529b\u9053\u9aa4\u52a0\uff0c\u8fde\u65bd\u51e0\u62db\uff0c\u201c\u9738\u738b\u625b\u9f0e\u201d\uff0c\u201c\u4e3e\u706b\u70e7\u5929\u201d\uff0c\u201c\u6a2a\u65ad\u5927\u6cb3\u201d\uff0c\u90fd\u662f\u786c\u78b0\u786c \u7684\u5f0f\u5b50\uff0c\u90a3\u6301\u5251\u5c11\u5e74\uff0c\u89c1\u4ed6\u62db\u5f0f\u867d\u4e0d\u7cbe\u5947\uff0c\u4f46\u62db\u62db\u6c89\u731b\u65e0\u6bd4\uff0c\u81ea\u5df1\u53c8\u662f\u8f7b\u5175\u5668\uff0c\u53ea\u5f97\u8fde\u8fde\u540e \u9000\uff0c\u4e0d\u6562\u786c\u67b6\u3002 \u3000\u3000\u9ad8\u6218\u4e58\u52bf\u4e0a\u524d\uff0c\u5ffd\u89c1\u5c11\u5e74\u6325\u5251\u62b5\u6321\uff0c\u53f3\u808b\u95ea\u52a8\u5fae\u6162\uff0c\u9732\u51fa\u7834\u7efd\uff0c\u4ed6\u4e0d\u7531\u81ea\u4e3b\u7684\u6b3a\u8eab\u4e0a \u524d\uff0c\u5de6\u624b\u4e00\u8fdb\uff0c\u70b9\u4e86\u5c11\u5e74\u808b\u4e0b\u201c\u4e91\u53f0\u7a74\u201d \u3000\u3000\u539f\u6765\u4ed6\u4e00\u8fb9\u6253\u5c31\u60f3\u5230\u6628\u591c\u5728\u53e4\u5e99\u4e2d\u6240\u5b66\u6253\u7a74\u4e4b\u6cd5\uff0c\u4ed6\u89c1\u6559\u4ed6\u8fd9\u95e8\u529f\u7684\u8001\u8005\uff0c\u80fd\u591f\u51fa\u624b\u5c31 \u628a\u522b\u4eba\u5236\u5f97\u670d\u670d\u8d34\u8d34\uff0c\u5fc3\u4e2d\u5f88\u662f\u4f55\u670d\uff0c\u4ed6\u7ae5\u5fc3\u672a\u9000\uff0c\u5b66\u4f1a\u4e86\u540e\u4e5f\u60f3\u627e\u4eba\u8bd5\u8bd5\uff0c\u6b64\u65f6\u89c1\u5bf9\u65b9\u53f3 \u808b\u9732\u51fa\u6765\uff0c\u4e0d\u89c9\u89c1\u730e\u5fc3\u559c\uff0c\u7331\u8eab\u800c\u4e0a\uff0c\u70b9\u4e86\u5bf9\u65b9\u808b\u4e0b\u4e4b\u7a74\u3002 \u3000\u3000\u5ffd\u7136\u80cc\u540e\u4e00\u58f0\u9634\u7b11\uff0c\u9ad8\u6218\u8f6c\u8fc7\u5934\uff0c\u53ea\u89c1\u4eba\u5f71\u4e00\u95ea\uff0c\u4ed6\u6b63\u60f3\u8ffd\u4e0a\u53bb\uff0c\u4f46\u89c1\u90a3\u5c11\u5e74\u75db\u5f97\u51b7\u6c57 \u76f4\u6d41\uff0c\u5fc3\u4e2d\u5927\u662f\u4e0d\u5fcd\uff0c\u8bb0\u8d77\u4e86\u8001\u8005\u544a\u8beb\u7684\u8bdd\uff0c\u4e0d\u89c9\u5341\u5206\u540e\u6094\u3002 \u3000\u3000\u4ed6\u8d70\u5230\u90a3\u5c11\u5e74\u8eab\u65c1\uff0c\u7adf\u4e0d\u77e5\u5982\u4f55\u4e0b\u624b\u89e3\u7a74\uff0c\u539f\u6765\u8001\u8005\u53ea\u4f20\u4e86\u4ed6\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u5c31\u5306\u5306\u79bb \u53bb\uff0c\u662f\u4ee5\u9ad8\u6218\u4e5f\u4e0d\u77e5\u5982\u4f55\u89e3\u6551\uff0c\u90a3\u5c11\u5e74\u75db\u5f97\u8138\u8272\u53d1\u9752\uff0c\u9ad8\u6218\u5927\u6025\uff0c\u82e6\u601d\u6628\u591c\u8001\u8005\u62cd\u5f00\u58ee\u6c49\u7a74 \u9053\u7684\u624b\u6cd5\uff0c\u4f46\u53ea\u8bb0\u5f97\u8001\u8005\u5411\u58ee\u6c49\u80f8\u524d\u4e00\u62c2\uff0c\u4ed6\u5fc3\u60f3\uff1a\u201c\u4e0e\u5176\u5750\u5728\u8fd9\u513f\u5e72\u7740\u6025\uff0c\u5012\u4e0d\u5982\u8bd5\u8bd5 \u770b\u3002\u201d \u3000\u3000\u4e8e\u662f\u4ed6\u5c31\u5411\u5c11\u5e74\u80f8\u524d\u51fb\u638c\u638c\u62cd\u53bb\uff0c\u4ed6\u4e0d\u6562\u7528\u52b2\uff0c\u6015\u4f24\u4e86\u5c11\u5e74\u5185\u810f\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u8fd8\u9053\u4ed6\u662f\u6709\u610f\u620f\u5f04\uff0c\u76f4\u6c14\u5f97\u773c\u4e2d\u54ce\u706b\uff0c\u539f\u5148\u90a3\u4f7f\u5ce8\u7709\u523a\u7684\u5c11\u5e74\uff0c\u7ad9\u5728\u65c1\u8fb9\uff0c\u4f3c\u7b11 \u975e\u7b11\u7684\u770b\u7740\u3002 \u3000\u3000\u5f97\u5230\u9ad8\u6218\u62cd\u5230\u201c\u7ae0\u53f0\u7a74\u201d\u65f6\uff0c\u90a3\u5c11\u5e74\u7a81\u89c9\u5168\u8eab\u8840\u8109\u6d41\u901a\uff0c\u56db\u80a2\u5df1\u53ef\u6d3b\u52a8\uff0c\u4ed6\u5929\u6027\u9634\u6c89\uff0c \u4e00\u8a00\u4e0d\u53d1\uff0c\u8fd0\u5c3d\u5168\u8eab\u529f\u529b\uff0c\u4e00\u638c\u5411\u9ad8\u6218\u5934\u4e0a\u51fb\u53bb\u3002 \u3000\u3000\u9ad8\u6218\u8fd8\u5728\u4e00\u638c\u638c\u8bd5\u7740\u66ff\u4ed6\u89e3\u7a74\uff0c\u600e\u6599\u4ed6\u7a81\u7136\u542b\u6124\u51fb\u51fa\uff0c\u5e78\u4e8f\u4ed6\u81ea\u5e7c\u7ec3\u5c31\u4e0a\u4e58\u5185\u529f\uff0c\u53cd\u5e94 \u751a\u662f\u654f\u6377\uff0c\u5934\u4e00\u504f\uff0c\u8eab\u5b50\u5411\u540e\u4e00\u5012\uff0c\u603b\u7b97\u95ea\u8fc7\u4e3b\u529b\uff0c\u53ef\u662f\u80a9\u4e0a\u5374\u6328\u4e86\u4e00\u4e0b\uff0c\u9000\u540e\u51e0\u6b65\u8fd9\u624d\u7ad9 \u7a33\u3002 \u3000\u3000\u9ad8\u6218\u56e0\u65e0\u610f\u4e2d\u70b9\u4e86\u4ed6\u7684\u7a74\u9053\uff0c\u5fc3\u4e2d\u5f88\u611f\u62b1\u6b49\uff0c\u867d\u7136\u5de6\u80a9\u6328\u4e86\u4e00\u638c\uff0c\u75bc\u75db\u975e\u5e38\uff0c\u4e5f\u4e0d\u5728 \u610f\uff0c\u8f6c\u8eab\u4fbf\u60f3\u79bb\u53bb\u3002 \u3000\u3000\u7a81\u7136\u8fce\u9762\u8d70\u6765\u4e00\u4e2a\u5c11\u5973\uff0c\u9ad8\u6218\u671b\u4e86\u4e00\u773c\uff0c\u89c9\u5f97\u660e\u8273\u6781\u4e86\uff0c\u90a3\u5c11\u5973\u8d70\u8fd1\uff0c\u770b\u770b\u573a\u4e2d\u4e24\u5c11 \u5e74\uff0c\u55d4\u9053\uff1a\u201c\u4f60\u4e24\u4e2a\u53c8\u5728\u6253\u67b6\u4e86\uff1f\u201d \u3000\u3000\u90a3\u4e24\u4eba\u5bf9\u5c11\u5973\u6781\u4e3a\u656c\u754f\uff0c\u95fb\u8a00\u614c\u5fd9\u540c\u58f0\u8fa9\u9053\uff1a\u201c\u6211\u4eec\u662f\u5207\u78cb\u6b66\u529f\uff0c\u857e\u5e08\u59b9\uff0c\u4f60\u53ef\u5343\u4e07\u522b \u591a\u5fc3\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u8fd8\u8981\u6df7\u8d56\uff0c\u7239\u521a\u624d\u90fd\u770b\u89c1\u4e86\u3002\u201d \u3000\u3000\u4f7f\u5251\u7684\u5c11\u5e74\u6025\u9053\uff1a\u201c\u857e\u5e08\u59b9\uff0c\u8bf7\u4f60\u8d76\u5feb\u5411\u5e08\u7236\u6c42\u6c42\u60c5\u5427\uff01\u4ed6\u8001\u4eba\u5bb6\u6700\u80af\u542c\u4f60\u7684\u8bdd\u3002\u201d \u3000\u3000\u90a3\u4f7f\u5ce8\u7709\u523a\u7684\u5c11\u5e74\u60f6\u6025\u4e4b\u8272\u4e5f\u6ea2\u4e8e\u8a00\u8868\uff0c\u4ed6\u672c\u662f\u80f8\u65e0\u6210\u7af9\uff0c\u6b64\u65f6\u6025\u4e0d\u62e9\u8a00\uff0c\u9053\uff1a\u201c\u5c0f\u5e08 \u59b9\uff0c\u6211\u2026\u2026\u6211\u548c\u5927\u2026\u2026\u5927\u5e08\u5144\u662f\u4e3a\u4f60\u624d\u52a8\u624b\u7684\u5440\u2026\u2026\u201d \u3000\u3000\u88ab\u5524\u7740\u201c\u857e\u5e08\u59b9\u201d\u7684\u5c11\u5973\uff0c\u95fb\u8a00\u7f9e\u4e0d\u53ef\u5f53\uff0c\u9ad8\u58f0\u53f1\u9053\uff1a\u201c\u4e8c\u5e08\u54e5\uff0c\u4f60\u518d\u80e1\u53e3\u4e71\u8bf4\uff0c\u6211\u53bb \u544a\u7239\u7239\u3002\u201d \u3000\u3000\u201c\u4e8c\u5e08\u5144\u201d\u5927\u60ca\u5931\u8272\uff0c\u4e0d\u4f4f\u966a\u7b11\u592e\u6c42\uff0c\u4f7f\u5251\u5c11\u5e74\u95ee\u9053\uff1a\u201c\u5c0f\u5e08\u59b9\uff0c\u5e08\u7236\u5f53\u771f\u751f\u6c14\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5973\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u89c1\u8fc7\u7239\u53d1\u8fd9\u5927\u813e\u6c14\u3002\u201d \u3000\u3000\u9ad8\u6218\u542c\u4e86\u4e00\u4f1a\uff0c\u5fc3\u60f3\u8fd9\u4e24\u4e2a\u5c11\u5e74\u5bf9\u4ed6\u4eec\u81ea\u5df1\u7684\u5e08\u7236\u6015\u6210\u8fd9\u4e2a\u6837\u5b50\uff0c\u771f\u662f\u597d\u7b11\u3002\u4fbf\u6162\u6b65\u8d70 \u5f00\u3002 \u3000\u3000\u90a3\u5c11\u5973\u5ffd\u9053\uff1a\u201c\u4f60\u522b\u8d70\uff0c\u5f85\u4f1a\u7239\u7239\u7f5a\u8d77\u4eba\u6765\uff0c\u4f60\u4e5f\u6709\u4efd\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u4e0d\u670d\uff0c\u5fd6\u9053\uff1a\u201c\u4f60\u7239\u7239\u662f\u4ec0\u4e48\u4eba\uff0c\u6211\u5e72\u4e48\u8981\u53d7\u4ed6\u7ba1\u3002\u201d \u3000\u3000\u4f46\u4ed6\u5929\u6027\u5904\u5904\u8ba9\u4eba\u4e00\u6b65\uff0c\u662f\u4ee5\u5e76\u4e0d\u8fd8\u53e3\uff0c\u8038\u8038\u80a9\uff0c\u53cd\u8eab\u4f5c\u4e2a\u9b3c\u8138\uff0c\u5c31\u8d70\u51fa\u6797\u5916\uff0c\u9017\u5f97\u90a3 \u5c11\u5973\u63a9\u53e3\u800c\u7b11\u3002 \u3000\u3000\u9ad8\u6218\u53ea\u89c1\u5e84\u4e2d\u4eba\u4e00\u7fa4\u7fa4\u8d70\u5411\u5e7f\u573a\uff0c\u5fc3\u77e5\u7eff\u6797\u5927\u4f1a\u5373\u5c06\u5f00\u59cb\uff0c\u4e5f\u5c31\u6df7\u5728\u4eba\u7fa4\u4e2d\uff0c\u8d70\u5230\u5e7f\u573a \u4e0a\uff0c\u627e\u4e86\u4e00\u5904\u5750\u4e0b\u3002 \u3000\u3000\u573a\u4e2d\uff0c\u4e00\u5ea7\u5927\u53f0\uff0c\u51e1\u662f\u5728\u5317\u65b9\u7eff\u6797\u72ec\u5f53\u4e00\u9762\u6709\u5934\u6709\u8138\u7684\u597d\u6c49\uff0c\u90fd\u5750\u5728\u53f0\u4e0a\uff0c\u53f0\u4e3b\u6b63\u662f\u672c \u5e84\u4e3b\u4eba\u98ce\u4e91\u5251\u6797\u9aa7\uff0c\u8fd9\u65f6\u6162\u6162\u7ad9\u8d77\u8eab\u6765\uff0c\u5411\u56db\u5468\u4e00\u62f1\u624b\uff0c\u7fa4\u8c6a\u7acb\u523b\u4f4f\u53e3\u51dd\u795e\u800c\u542c\uff0c\u6574\u4e2a\u5e7f\u573a \u90fd\u9759\u4e86\u4e0b\u6765\u3002 \u3000\u3000\u98ce\u4e91\u5251\u6797\u9aa7\u5e72\u54b3\u4e86\u4e00\u58f0\uff0c\u9053\uff1a\u201c\u5404\u4f4d\u82f1\u96c4\uff0c\u5404\u4f4d\u597d\u6c49\uff0c\u4eca\u5929\u662f\u54b1\u4eec\u5317\u65b9\u82f1\u96c4\u5927\u4f1a\u5f00\u59cb\u7684 \u65e5\u5b50\uff0c\u627f\u5404\u4f4d\u77a7\u5f97\u8d77\u5728\u4e0b\uff0c\u501f\u655d\u5e84\u4e3e\u884c\uff0c\u5728\u4e0b\u62db\u5f85\u4e0d\u5468\uff0c\u5e0c\u671b\u5404\u4f4d\u591a\u591a\u5305\u6db5\u2026\u2026\u201d \u3000\u3000\u7fa4\u96c4\u4e03\u5634\u516b\u820c\u7eb7\u7eb7\u8c26\u8c22\uff0c\u6797\u9aa7\u63a5\u7740\u9053\uff1a\u201c\u54b1\u4eec\u5e73\u65e5\u5206\u6563\u5404\u5730\u6c34\u9646\u4e24\u9053\uff0c\u5f88\u96be\u6709\u673a\u4f1a\u4f1a \u9762\uff0c\u4eca\u513f\u4e58\u6b64\u805a\u4f1a\uff0c\u5927\u4f19\u513f\u5207\u78cb\u5207\u78cb\u6b66\u827a\uff0c\u771f\u662f\u4e00\u5927\u5feb\u4e8b\uff0c\u4fd7\u8bed\u8bf4\u5f97\u597d\uff1a\u2018\u82f1\u96c4\u51fa\u5c11\u5e74\u2019\uff0c \u5404\u4f4d\u8001\u5f1f\u5f85\u4f1a\u5927\u663e\u795e\u901a\uff0c\u4e5f\u6559\u81ea\u547d\u4fa0\u4e49\u9053\u7684\u77e5\u9053\u54b1\u4eec\u7eff\u6797\u4e2d\u4e5f\u5927\u6709\u4eba\u624d\u3002\u201d \u3000\u3000\u7fa4\u96c4\u8f70\u7136\u53eb\u597d\uff0c\u6797\u9aa7\u53c8\u9053\uff1a\u201c\u5982\u679c\u5404\u4f4d\u65e0\u5f02\u8bae\uff0c\u5728\u4e0b\u5c31\u5ba3\u5e03\u5927\u4f1a\u5f00\u59cb\u3002\u201d \u3000\u3000\u7fa4\u96c4\u70b9\u5934\u79f0\u662f\uff0c\u6797\u9aa7\u9053\uff1a\u201c\u4e0d\u77e5\u54ea\u4f4d\u82f1\u96c4\u5148\u4e0b\u573a\u8868\u6f14\u3002\u201d \u3000\u3000\u5ffd\u7136\u5750\u5728\u7b2c\u4e00\u6392\u4e00\u4e2a\u4e94\u65ec\u8001\u8005\u633a\u8eab\u800c\u51fa\uff0c\u8d70\u5230\u53f0\u4e2d\uff0c\u6c89\u58f0\u9053\uff1a\u201c\u8bf8\u4f4d\u5be8\u4e3b\u5f53\u5bb6\uff0c\u5144\u5f1f\u6709 \u4e2a\u91cd\u8981\u6d88\u606f\uff0c\u5173\u7cfb\u54b1\u4eec\u6574\u4e2a\u5317\u65b9\u6b66\u6797\u547d\u8fd0\u3002\u201d \u3000\u3000\u4ed6\u8bf4\u5230\u6b64\uff0c\u505c\u4e86\u4e00\u505c\uff0c\u5411\u56db\u5468\u626b\u4e86\u4e00\u773c\u3002\u4f17\u4eba\u90fd\u8bc6\u5f97\u8fd9\u4e94\u65ec\u8001\u8005\u662f\u540d\u9707\u5927\u6cb3\u5357\u5317\u7684\u5c71\u4e1c \u6d4e\u5357\u5927\u8c6a\u59ec\u672c\u5468\uff0c\u6b64\u4eba\u4e00\u8eab\u529f\u592b\u795e\u51fa\u9b3c\u6ca1\uff0c\u5bb6\u5c45\u6d4e\u5357\u57ce\u5916\uff0c\u8868\u9762\u770b\u6765\u4f3c\u4e2a\u5927\u5bcc\u7fc1\uff0c\u5176\u5b9e\u662f\u4e2a \u72ec\u884c\u76d7\u3002 \u3000\u3000\u6d4e\u5357\u5927\u8c6a\u7ee7\u7eed\u9053\uff1a\u201c\u5404\u4f4d\u5982\u679c\u4e0d\u5584\u5fd8\u7684\u8bdd\uff0c\u603b\u8fd8\u8bb0\u5f97\u5eff\u591a\u5e74\u524d\uff0c\u4e13\u95e8\u4e0e\u9053\u4e0a\u670b\u53cb\u4f5c\u5bf9\uff0c \u624b\u9ed1\u5fc3\u8fa3\u7684\u2018\u5929\u715e\u661f\u541b\u2019\u5427\uff01\u6b64\u4eba\u5f53\u5e74\u7a81\u7136\u5931\u8e2a\uff0c\u8fd9\u5eff\u591a\u5e74\u4e0d\u89c1\u8e2a\u8ff9\uff0c\u6c5f\u6e56\u4e0a\u4f20\u95fb\u5176\u4eba\u5df2 \u6b7b\uff0c\u53ef\u662f\u4f9d\u5144\u5f1f\u770b\u6765\uff0c\u6b64\u4eba\u5e76\u672a\u6b7b\u53bb\uff0c\u800c\u4e14\u6700\u8fd1\u5df2\u7136\u91cd\u5165\u6c5f\u6e56\u2026\u2026\u201d \u3000\u3000\u7fa4\u8c6a\u76f8\u987e\u5931\u8272\uff0c\u7eb7\u7eb7\u4ea4\u5934\u63a5\u8033\uff0c\u6d4e\u5357\u5927\u6765\u7f13\u7f13\u53c8\u9053\uff1a\u201c\u8bf8\u4f4d\u60f3\u60f3\u6d1b\u9633\u4e09\u9738\u5144\u5f1f\u4e09\u4eba\u4f55\u7b49 \u529f\u592b\uff0c\u8001\u5927\u3001\u8001\u4e8c\u7adf\u5728\u4e00\u591c\u95f4\u88ab\u4eba\u5e9f\u4e86\uff0c\u542c\u8bf4\u4e09\u9738\u4e2d\u8001\u4e09\u7384\u7384\u5200\u8c22\u957f\u4e49\uff0c\u53d1\u6682\u62a5\u5144\u957f\u4e4b\u4ec7\uff0c \u6628\u591c\u8ddf\u4e0a\u4e86\u6740\u4eba\u7684\u4e3b\u513f\uff0c\u5230\u73b0\u5728\u8fd8\u4e0d\u89c1\u5f52\u6765\uff0c\u53ea\u6015\u53c8\u662f\u51f6\u591a\u5409\u5c11\u4e86\u3002\u4f9d\u5728\u4e0b\u770b\u6765\uff0c\u6740\u4eba\u7684\u5b9a \u662f\u90a3\u8001\u9b54\u5934\u3002\u201d \u3000\u3000\u7fa4\u6765\u5fc3\u5185\u5927\u60e7\uff0c\u5404\u4eba\u5fc3\u4e2d\u90fd\u60f3\u5230\u5982\u679c\u90a3\u9b54\u541b\u518d\u51fa\u6c5f\u6e56\uff0c\u6574\u4e2a\u5317\u65b9\u7eff\u6797\u53ea\u6015\u518d\u96be\u5b89\u5b81\uff0c\u90a3 \u4e0e\u201c\u5929\u715e\u661f\u541b\u201d\u6614\u65e7\u6709\u6881\u5b50\u7684\u5be8\u4e3b\uff0c\u66f4\u662f\u60f6\u60f6\u4e0d\u5b89\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u4e00\u51db\uff0c\u60f3\u9053\uff1a\u201c\u6628\u665a\u5728\u53e4\u5e99\u4e2d\u6740\u6b7b\u6d1b\u9633\u4e09\u9738\u8001\u4e09\u7684\u6b63\u662f\u2018\u5929\u715e\u661f\u541b\u2019\uff0c\u770b\u6765\u8fd9 \u822c\u4eba\u90fd\u548c\u4ed6\u6709\u6df1\u4ec7\u5927\u6028\uff0c\u9002\u624d\u65e0\u610f\u4e2d\u9732\u4e86\u4e00\u624b\u4ed6\u8001\u4eba\u5bb6\u4f20\u7684\u70b9\u7a74\u624b\u6cd5\uff0c\u83ab\u8981\u88ab\u4eba\u8bc6\u7834\uff0c\u627e\u5230 \u6211\u5934\u4e0a\u6765\u3002\u201d\u5f53\u4e0b\u62ac\u5934\u51dd\u795e\u6ce8\u610f\u3002 \u3000\u3000\u5ffd\u7136\u6d4e\u5357\u5927\u8c6a\u5de6\u8fb9\u7684\u4e00\u4e2a\u4e2d\u5e74\u6c49\u5b50\u7ad9\u8d77\uff0c\u6717\u58f0\u9053\uff1a\u201c\u59ec\u5144\u89c1\u89e3\u7aef\u7684\u9ad8\u660e\uff0c\u53ea\u662f\u5c31\u51ed\u6d1b\u9633 \u4e09\u9738\u906d\u4eba\u6740\u6b7b\u4e3a\u8bc1\u636e\uff0c\u63a8\u65ad\u90a3\u8001\u9b54\u5934\u91cd\u51fa\u6c5f\u6e56\uff0c\u672a\u514d\u8fc7\u4e8e\u6b66\u65ad\u3002\u201d \u3000\u3000\u539f\u6765\u8fd9\u4e2d\u5e74\u6c49\u5b50\u662f\u5d24\u5c71\u4e03\u715e\u4e2d\u8001\u4e09\uff0c\u4e0e\u5c71\u4e1c\u6d4e\u5357\u5927\u8c6a\u7d20\u6709\u6881\u5b50\uff0c\u6b64\u65f6\u542c\u5230\u6d4e\u5357\u5927\u8c6a\u5371\u8a00 \u8038\u542c\uff0c\u4e0d\u89c9\u5341\u5206\u4e0d\u8010\uff0c\u4ed6\u5e74\u7eaa\u624d\u56db\u5341\u591a\u5c81\uff0c\u5f53\u5e74\u521d\u51fa\u9053\uff0c\u5929\u715e\u661f\u541b\u5373\u5df2\u9690\u53bb\uff0c\u662f\u4f3c\u5bf9\u5929\u715e\u661f \u541b\u8ba4\u8bc6\u4e0d\u6df1\uff0c\u770b\u5230\u5927\u5bb6\u6020\u60ee\u5df2\u6781\uff0c\u5fc3\u5185\u6709\u6c14\uff0c\u5c31\u8d77\u8eab\u53cd\u9a73\u3002 \u3000\u3000\u6d4e\u5357\u5927\u8c6a\u51b7\u51b7\u9053\uff1a\u201c\u5144\u5f1f\u867d\u7136\u662f\u4e2a\u8349\u5305\uff0c\u4f46\u4e5f\u77e5\u51fa\u8a00\u8c28\u614e\uff0c\u51b3\u4e0d\u6562\u5192\u5145\u901e\u80fd\u3002\u201d \u3000\u3000\u4f17\u4ece\u90fd\u77e5\u5d24\u5c71\u4e03\u715e\u4e2d\u8001\u4e09\u65e0\u654c\u795e\u62f3\u6731\u590d\u541b\u662f\u4e2a\u8349\u5305\u813e\u6c14\uff0c\u95fb\u8a00\u4e0d\u7531\u54c4\u5802\u5927\u7b11\u3002 \u3000\u3000\u65e0\u654c\u795e\u62f3\u864e\u543c\u5bf8\u58f0\uff0c\u53eb\u9053\uff1a\u201c\u4f60\u5e72\u5417\u9a82\u4eba\u3002\u201d\u5c31\u8981\u51b2\u4e0a\u524d\u53bb\uff0c\u5d24\u5c71\u4e03\u715e\u8001\u5927\u593a\u547d\u53cc\u7b14\u6025 \u5fd9\u559d\u6b62\u3002 \u3000\u3000\u6d4e\u5357\u5927\u8c6a\u63a5\u7740\u9053\uff1a\u201c\u5144\u5f1f\u867d\u672a\u770b\u5230\u8001\u9b54\u5934\u672c\u4eba\uff0c\u53ef\u662f\u5374\u4eb2\u773c\u89c1\u5230\u8001\u9b54\u5934\u5f1f\u5b50\uff0c\u65bd\u51fa\u8003\u9b54 \u72ec\u95e8\u624b\u6cd5\u3002\u2018\u79c0\u9aa8\u6253\u7a74\u2019\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u56db\u5ea7\u7686\u60ca\uff0c\u6d4e\u5357\u5927\u8c6a\u63a5\u7740\u9053\uff1a\u201c\u6b64\u4eba\u5e74\u7eaa\u9752\u9752\uff0c\u529f\u529b\u5df2\u662f\u4e0d\u51e1\uff0c\u73b0\u5728\u5c31\u6df7\u5728 \u53f0\u4e0b\uff0c\u4f9d\u5144\u5f1f\u770b\uff0c\u591a\u534a\u662f\u5929\u715e\u8001\u9b54\u6d3e\u6765\u5367\u5e95\u7684\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u5927\u60ca\uff0c\u81ea\u89c9\u5c71\u4e1c\u6d4e\u5357\u5927\u8c6a\u4e24\u9053\u76ee\u5149\u6709\u5982\u5229\u5251\uff0c\u505c\u5728\u4ed6\u9762\u4e0a\uff0c\u8d76\u5feb\u628a\u773c\u5149\u79fb\u5f00\uff0c \u88c5\u505a\u4e0d\u5728\u4e4e\u6a21\u6837\u3002 \u3000\u3000\u7fa4\u8c6a\u54c4\u7136\u800c\u8d77\uff0c\u7eb7\u7eb7\u53eb\u9053\uff1a\u201c\u662f\u8c01\uff0c\u662f\u8c01\uff01\u5148\u628a\u8fd9\u5c0f\u5b50\u6293\u8d77\u6765\uff0c\u7b49\u8001\u9b54\u6765\uff0c\u54b1\u4eec\u8054\u624b\u628a \u4ed6\u4e00\u9f50\u5e9f\u4e86\u3002\u201d \u3000\u3000\u6d4e\u5357\u5927\u6e21\u6b63\u5f85\u5f00\u53e3\uff0c\u9ad8\u6218\u5fc3\u77e5\u6b64\u65f6\u4e0d\u9003\uff0c\u5f85\u4f1a\u5927\u5bb6\u4e00\u56f4\u4e0a\uff0c\u53ef\u5c31\u8dd1\u4e0d\u4e86\u4e86\uff0c\u4e00\u6478\u80cc\u540e\u5175 \u5203\u3002\u4ece\u4eba\u4e1b\u4e2d\u7a7f\u51fa\uff0c\u62d4\u817f\u5c31\u8d70\u3002 \u3000\u3000\u53ea\u542c\u89c1\u8033\u8fb9\u4e00\u58f0\u51b7\u7b11\uff0c\u6d4e\u5357\u5927\u8c6a\u4ece\u53f0\u4e0a\u98de\u8eab\u8d8a\u8fc7\u81ea\u5df1\uff0c\u6a2a\u62e6\u5728\u524d\u65b9\u9ad8\u6218\u4e00\u621f\u5288\u53bb\uff0c\u6d4e\u5357 \u5927\u5e86\u4e5f\u4e0d\u95ea\u8ba9\uff0c\u5934\u4e00\u4f4e\uff0c\u7331\u8eab\u800c\u4e0a\uff0c\u53cd\u624b\u64d2\u62ff\uff0c\u8981\u7a7a\u624b\u593a\u9ad8\u6218\u5175\u5668\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u5927\u6025\uff0c\u53f3\u624b\u77ed\u621f\u5c3d\u662f\u8fdb\u653b\u62db\u5f0f\uff0c\u5de6\u624b\u914d\u5408\u5929\u715e\u661f\u541b\u6240\u6388\u72ec\u95e8\u70b9\u7a74\u624b\u6cd5\uff0c\u6d4e\u5357\u5927 \u8c6a\u7684\u6b66\u529f\u867d\u9ad8\uff0c\u4f46\u5bf9\u9ad8\u6545\u5de6\u624b\u602a\u62db\uff0c\u751a\u662f\u6020\u60ee\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e5f\u4e0d\u6613\u53d6\u80dc\u3002 \u3000\u3000\u8fd9\u4e00\u803d\u6401\uff0c\u7fa4\u8c6a\u90fd\u56f4\u4e86\u8fc7\u6765\uff0c\u9ad8\u6218\u5fc3\u5185\u5fae\u602f\uff0c\u4e00\u4e2a\u5931\u624b\uff0c\u77ed\u621f\u51e0\u4e4e\u88ab\u5bf9\u65b9\u593a\u53bb\u3002 \u3000\u3000\u9ad8\u6218\u6108\u6218\u6108\u662f\u80c6\u602f\uff0c\u773c\u770b\u9ad8\u624b\u6797\u7acb\uff0c\u864e\u89c6\u7708\u7708\uff0c\u81ea\u5df1\u4e00\u4e2a\u4e5f\u4e0d\u6253\u4e0d\u8fc7\u3002 \u3000\u3000\u4ed6\u8fd9\u9ad8\u5bb6\u621f\u6cd5\u62db\u5f0f\u5e76\u4e0d\u5de7\u5999\uff0c\u5168\u9760\u529b\u9053\u6c89\u731b\uff0c\u6b64\u65f6\u4ed6\u52c7\u6c14\u4e27\u5931\uff0c\u81ea\u662f\u5a01\u529b\u5927\u51cf\uff0c\u90a3\u6d4e\u5357 \u5927\u8c6a\u4f3c\u4e4e\u4e0d\u613f\u4f24\u4ed6\uff0c\u51fa\u638c\u84c4\u529b\u4e0d\u53d1\u3002 \u3000\u3000\u6d4e\u5357\u5927\u8c6a\u8fde\u65bd\u7edd\u62db\uff0c\u9ad8\u6218\u77ed\u621f\u88ab\u4ed6\u529b\u9053\u6240\u8feb\uff0c\u7adf\u7136\u9012\u4e0d\u51fa\u53bb\uff0c\u773c\u770b\u5708\u5b50\u6108\u903c\u6108\u5c0f\uff0c\u6d4e\u5357 \u5927\u8c6a\u53f3\u638c\u7a81\u51fb\u9ad8\u6218\u5929\u7075\u7a74\uff0c\u9ad8\u6218\u5411\u5de6\u4e00\u95ea\uff0c\u6d4e\u5357\u5927\u8c6a\u5de6\u624b\u4e00\u4f38\u6b63\u6309\u4f4f\u9ad8\u6218\u80f8\u53e3\uff0c\u53f1\u58f0\u9053\uff1a \u201c\u5c0f\u5b50\uff0c\u5feb\u653e\u4e0b\u5175\u5668\u3002\u201d \u3000\u3000\u9ad8\u6218\u77e5\u4ed6\u5185\u529b\u4e00\u53d1\uff0c\u81ea\u5df1\u5fc3\u810f\u7acb\u788e\uff0c\u773c\u65f6\u60c5\u52bf\u786e\u662f\u9669\u6076\u5df2\u6781\u3002\u4f46\u4e00\u60f3\u5230\u7236\u4eb2\u751f\u5e73\u5b81\u6b7b\u4e0d \u5c48\u7684\u6027\u683c\uff0c\u6b64\u65f6\u4e07\u4e07\u4e0d\u80fd\u5c48\u670d\uff0c\u8d25\u4e86\u9ad8\u5bb6\u58f0\u667a\uff0c\u62fc\u7740\u88ab\u4ed6\u6253\u6b7b\uff0c\u4e5f\u4e0d\u80fd\u653e\u4e0b\u9ad8\u5bb6\u7956\u4f20\u5175\u5203\u3002 \u3000\u3000\u4ed6\u7b97\u8ba1\u5df2\u5b9a\uff0c\u594b\u4e0d\u987e\u8eab\uff0c\u53cc\u8db3\u8fd0\u52b2\uff0c\u5012\u7eb5\u4e00\u6b65\uff0c\u90a3\u5c71\u4e1c\u5927\u8c6a\u60f3\u4e0d\u5230\u9ad8\u6218\u5014\u5f3a\u5982\u6b64\uff0c\u4ed6\u672c \u65e0\u6740\u9ad8\u6218\u4e4b\u610f\uff0c\u638c\u52b2\u4e00\u5410\uff0c\u7acb\u523b\u53c8\u8fd0\u529f\u6d3b\u751f\u751f\u6536\u56de\uff0c\u94c1\u9752\u7740\u8138\u9053\uff1a\u201c\u5c0f\u5b50\uff0c\u8001\u592b\u77a7\u4f60\u5e74\u7eaa\u9752 \u9752\u6b7b\u4e86\u672a\u514d\u592a\u4e3a\u53ef\u601c\uff0c\u5feb\u653e\u4e0b\u5175\u5668\uff0c\u8bf4\u51fa\u4f60\u5e08\u7236\u5728\u54ea\uff0c\u6211\u4e5f\u4e0d\u4e3a\u96be\u4f60\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u4f60\u5206\u660e\u662f\u6015\u90a3\u8001\u4f2f\u4f2f\uff0c\u4f55\u5fc5\u5982\u6b64\u5356\u597d\u3002\u201d \u3000\u3000\u4ed6\u9003\u51fa\u6d4e\u5357\u5927\u8c6a\u4e4b\u638c\uff0c\u671b\u671b\u56db\u56f4\u9ad8\u9ad8\u77ee\u77ee\u7ad9\u6ee1\u4e86\u8bb8\u591a\u7eff\u6797\u597d\u6c49\uff0c\u60f3\u5230\u7236\u4eb2\u8bf4\u8fc7\u9ad8\u5bb6\u6218\u6cd5 \u5bf9\u4e8e\u51b2\u950b\u9677\u9635\u662f\u7ba1\u7528\uff0c\u4fbf\u7acb\u523b\u62bd\u51fa\u80cc\u540e\u621f\u6746\uff0c\u548c\u53f3\u624b\u6240\u6267\u621f\u8eab\u524d\u534a\u4e00\u5408\u3002 \u3000\u3000\u4ed6\u6253\u91cf\u4e00\u4e0b\uff0c\u60f3\u4ece\u654c\u4eba\u8f83\u5f31\u7684\u5730\u65b9\u51b2\u51fa\uff0c\u7a81\u7136\u53d1\u89c9\u4e00\u53cc\u5145\u6ee1\u98de\u8eab\u4e00\u811a\uff0c\u5411\u9ad8\u6218\u621f\u6746\u8e22 \u53bb\u3002 \u3000\u3000\u9ad8\u6218\u957f\u5175\u6311\u51fa\uff0c\u4e0d\u53ca\u6536\u56de\uff0c\u5012\u9000\u534a\u6b65\uff0c\u65e0\u654c\u795e\u62f3\u4e58\u52bf\u76f4\u4e0b\uff0c\u5de6\u624b\u4e8c\u6307\u201c\u9a71\u9f99\u63a2\u73e0\u201d\uff0c\u5411 \u9ad8\u6218\u53cc\u76ee\u70b9\u53bb\uff0c\u53f3\u624b\u5316\u62f3\u4e3a\u638c\uff0c\u76f4\u5411\u9ad8\u6218\u80f8\u524d\u6309\u6765\u3002 \u3000\u3000\u9ad8\u6218\u4e00\u4f4e\u5934\uff0c\u773c\u89c1\u654c\u4eba\u638c\u5df2\u9012\u8fd1\uff0c\u95ea\u907f\u4e0d\u53ca\uff0c\u5f53\u4e0b\u731b\u626c\u4e00\u53e3\u771f\u6c14\uff0c\u53f3\u638c\u62cd\u51fa\uff0c\u201c\u7830\u201d\u7136 \u4e00\u58f0\uff0c\u5404\u81ea\u540e\u9000\u6570\u6b65\u3002 \u3000\u3000\u7fa4\u6765\u89c1\u9ad8\u6218\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u5185\u529b\u5982\u6b64\u6df1\u6e5b\uff0c\u7adf\u80fd\u4e0e\u4ee5\u529b\u9053\u5a01\u731b\u8457\u540d\u7684\u65e0\u654c\u795e\u62f3\u4e0d\u76f8\u4e0a\u4e0b\uff0c\u4e0d \u89c9\u7eb7\u7eb7\u53eb\u4e86\u58f0\u201c\u597d\u201d\u3002 \u3000\u3000\u65e0\u654c\u795e\u62f3\u7f9e\u6127\u96be\u5f53\uff0c\u9002\u624d\u4ed6\u867d\u672a\u7528\u51fa\u5168\u529b\uff0c\u529b\u9053\u5374\u4e5f\u53d1\u51fa\u4e03\u516b\u5206\uff0c\u4ed6\u672c\u662f\u8349\u5305\u813e\u6c14\uff0c\u4e00 \u6012\u4e4b\u4e0b\uff0c\u4e5f\u5c31\u987e\u4e0d\u5f97\u8001\u5927\u593a\u547d\u53cc\u7b14\u544a\u8beb\u4ed6\u4e0d\u8981\u4f24\u9ad8\u6218\u7684\u8bdd\uff0c\u53ea\u89c1\u4ed6\u51fa\u62f3\u5982\u98de\uff0c\u62db\u62db\u51fb\u5411\u9ad8\u6218 \u8981\u5bb3\u3002 \u3000\u3000\u9ad8\u6218\u7ecf\u8fc7\u65b9\u624d\u4e00\u5bf9\u638c\uff0c\u4fe1\u5fc3\u5927\u589e\uff0c\u628a\u957f\u621f\u821e\u5f97\u4e0d\u900f\u98ce\uff0c\u90a3\u65e0\u654c\u795e\u62f3\u529f\u529b\u9ad8\u5f3a\uff0c\u4e5f\u4e0d\u6562\u592a \u8fc7\u903c\u8fd1\u3002 \u3000\u3000\u7fa4\u96c4\u81ea\u6301\u8eab\u5206\uff0c\u4e0d\u5c51\u51fa\u624b\u5408\u6218\u4e00\u4e2a\u5c11\u5e74\uff0c\u53ea\u662f\u56f4\u5728\u56db\u5468\uff0c\u6307\u6307\u70b9\u70b9\uff0c\u9632\u4ed6\u9003\u8d70\u3002 \u3000\u3000\u65e0\u654c\u795e\u62f3\u4e45\u6218\u4e0d\u4e0b\uff0c\u5fc3\u5185\u70e6\u71e5\u5df2\u6781\uff0c\u7a81\u7136\u62db\u5f0f\u4e00\u53d8\uff0c\u8eab\u5f62\u4e1c\u5012\u897f\u6b6a\uff0c\u65bd\u51fa\u751f\u5e73\u4ed7\u4ee5\u6210\u540d \u7684\u201c\u9189\u516b\u4ed9\u62f3\u201d\uff0c\u9ad8\u6218\u89c1\u4ed6\u62db\u5f0f\u602a\u5f02\uff0c\u6709\u5982\u9189\u6c49\uff0c\u5fc3\u5185\u6b63\u81ea\u7422\u78e8\uff0c\u51fa\u624b\u4e0d\u7531\u4e00\u6162\uff0c\u7adf\u5403\u5bf9\u65b9 \u5c06\u621f\u6746\u6293\u4f4f\u3002 \u3000\u3000\u65e0\u654c\u795e\u62f3\u5927\u751a\uff0c\u6b63\u8981\u8fd0\u52b2\u786c\u593a\uff0c\u5ffd\u7136\u80cc\u540e\u4e00\u4e2a\u6e05\u8d8a\u7684\u58f0\u97f3\uff1a\u201c\u5404\u4f4d\u8fd9\u5927\u5e74\u7eaa\uff0c\u4f55\u5fc5\u4e0e\u4e00 \u4e2a\u5b69\u5b50\u8fc7\u4e0d\u53bb\u3002\u201d \u3000\u3000\u7fa4\u96c4\u56de\u5934\u4e00\u77a7\uff0c\u53ea\u89c1\u8eab\u540e\u4e0d\u77e5\u4f55\u65f6\u8d70\u6765\u4e00\u4e2a\u9752\u5e74\u5112\u751f\uff0c\u7fa4\u96c4\u521a\u624d\u867d\u5728\u77a7\u70ed\u95f9\uff0c\u53ef\u662f\u8eab\u540e \u6765\u4e86\u4e00\u4e2a\u4eba\uff0c\u5927\u5bb6\u7adf\u7136\u6ca1\u6709\u53d1\u73b0\uff0c\u6b64\u4eba\u8f7b\u529f\u4e4b\u7eaf\uff0c\u771f\u4ee4\u4eba\u4e0d\u6562\u7f6e\u4fe1\u3002 \u3000\u3000\u90a3\u9752\u5e74\u5112\u751f\u5e73\u548c\u5730\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u4e5f\u6ca1\u6709\u5f97\u7f6a\u5404\u4f4d\uff0c\u5404\u4f4d\u5982\u6709\u672c\u4e8b\uff0c\u4f55\u4e0d\u53bb\u627e\u6559\u4ed6\u6b66\u529f\u7684 \u4eba\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u542c\u4ed6\u5728\u65c1\u89c2\u671b\u5df2\u4e45\uff0c\u5bf9\u4e8e\u6b64\u4e8b\u5168\u7136\u660e\u767d\uff0c\u5404\u4eba\u60f3\u5230\u81ea\u5df1\u4e0d\u6562\u53bb\u627e\u201c\u5929\u715e\u661f\u541b\u201d\u8f83 \u91cf\uff0c\u7adf\u7136\u7fa4\u8d77\u5bf9\u4ed8\u4ed6\u7684\u5f92\u513f\uff0c\u4e0d\u7981\u90fd\u6709\u4e9b\u7f9e\u6127\u3002 \u3000\u3000\u9ad8\u6218\u89c1\u6709\u4eba\u66ff\u4ed6\u89e3\u56f4\uff0c\u5fc3\u4e2d\u597d\u751f\u611f\u6fc0\uff0c\u773c\u5149\u4e0d\u7531\u8f6c\u5230\u90a3\u9752\u5e74\u5112\u751f\u9762\u4e0a\uff0c\u53ea\u89c1\u4ed6\u633a\u9f3b\u661f \u76ee\uff0c\u4fca\u7f8e\u7edd\u4f26\uff0c\u53ea\u662f\u4e3e\u6b62\u4e4b\u95f4\uff0c\u90fd\u5e26\u6709\u4e00\u79cd\u6df1\u523b\u75db\u82e6\u7684\u8868\u60c5\uff0c\u9ad8\u6218\u671b\u4e86\u4e24\u773c\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u51e0 \u4e4e\u60f3\u51fa\u8a00\u5b89\u6170\u4ed6\uff0c\u5ffd\u89c9\u624b\u4e2d\u4e00\u7d27\uff0c\u957f\u621f\u88ab\u5bf9\u65b9\u593a\u53bb\u3002 \u3000\u3000\u9ad8\u6218\u4e00\u5b9a\u795e\uff0c\u6b63\u8981\u4e0a\u524d\u62a2\u621f\uff0c\u90a3\u5112\u751f\u5ffd\u9053\uff1a\u201c\u8fd9\u4f4d\u5be8\u4e3b\u6b66\u529f\u7aef\u7684\u4e0d\u51e1\uff0c\u77a7\u5728\u4e0b\u9762\u4e0a\uff0c\u8bf7 \u5c06\u5175\u5668\u4ea4\u8fd8\u7ed9\u8fd9\u4f4d\u5c0f\u5f1f\uff0c\u653e\u4ed6\u8d70\u8def\u53ef\u597d\uff1f\u201d \u3000\u3000\u65e0\u654c\u795e\u62f3\u6012\u9053\uff1a\u201c\u4f60\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u6211\u5e72\u5417\u8981\u542c\u4f60\u8bdd\u3002\u201d \u3000\u3000\u90a3\u9752\u5e74\u5112\u751f\u9053\uff1a\u201c\u5728\u4e0b\u65e2\u662f\u51b2\u7740\u8fd9\u4f4d\u5c0f\u5f1f\u800c\u6765\uff0c\u5404\u4f4d\u8981\u662f\u4e0d\u653e\uff0c\u90a3\u4e48\u5728\u4e0b\u53ea\u6709\u2014\u2014\u201d \u3000\u3000\u90a3\u8fb9\u6d4e\u5357\u5927\u8c6a\u84e6\u7136\u60f3\u8d77\u4e00\u4eba\uff0c\u63a5\u53e3\u95ee\u9053\uff1a\u201c\u8bf7\u6559\u9601\u4e0b\u4e07\u513f\uff1f\u201d \u3000\u3000\u90a3\u5112\u751f\u9053\uff1a\u201c\u5728\u4e0b\u59d3\u5434\uff0c\u8349\u5b57\u51cc\u98ce\u2014\u2014\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u7fa4\u8c6a\u54d7\u7136\uff0c\u987b\u77e5\u5f53\u5e74\u5434\u51cc\u98ce\u51fa\u9053\u4ee5\u6765\uff0c\u77ed\u77ed\u51e0\u5e74\u4e2d\uff0c\u8f70\u8f70\u70c8\u70c8\u505a\u4e86\u51e0\u4ef6\u9707\u52a8 \u6c5f\u6e56\u7684\u5927\u4e8b\uff0c\u6cf0\u5c71\u5927\u4f1a\u4e2d\u4ed7\u5251\u5927\u6218\u5929\u4e0b\u7b2c\u4e00\u5251\u5389\u9e57\u58f0\u540d\u626c\u5929\u4e0b\uff0c\u53ea\u56e0\u4ed6\u540d\u6c14\u867d\u5927\uff0c\u4f46\u5e76\u4e0d\u5e38 \u5728\u6c5f\u6e56\u4e0a\u9732\u8138\uff0c\u8fd1\u5341\u5e74\u6765\u66f4\u4e0d\u89c1\u8e2a\u8ff9\uff0c\u662f\u4ee5\u5f53\u5e74\u6d4e\u5357\u5927\u8c6a\u59ec\u672c\u5468\u867d\u4e5f\u53c2\u4e0e\u6cf0\u5c71\u5927\u4f1a\uff0c\u4e00\u65f6\u4e4b \u95f4\u5374\u4e5f\u60f3\u4e0d\u8d77\u6765\u3002 \u3000\u3000\u65e0\u654c\u795e\u62f3\u89c1\u8001\u5927\u593a\u547d\u53cc\u7b14\u8fde\u65bd\u773c\u8272\uff0c\u77e5\u9053\u8fd9\u4e3b\u513f\u53ef\u4e0d\u597d\u60f9\uff0c\u5fc3\u4e2d\u867d\u662f\u4e0d\u670d\uff0c\u4f46\u4e0d\u6562\u8fdd\u80cc \u8001\u5927\u7684\u610f\u601d\uff0c\u8fd0\u52b2\u5c06\u621f\u5411\u5434\u51cc\u98ce\u63b7 \u3000\u3000\u6765\u4e86\u5f85\u5230\u621f\u5df2\u51fa\u624b\uff0c\u8fd9\u624d\u559d\u9053\uff1a\u201c\u5728\u4e0b\u9075\u4ece\u5434\u5927\u4fa0\u4e4b\u547d\uff0c\u5434\u5927\u4fa0\uff0c\u7559\u795e\u4e86\u3002\u201d \u3000\u3000\u90a3\u621f\u672c\u662f\u91cd\u5175\u5668\uff0c\u88ab\u4ed6\u8fd0\u529b\u4e00\u63b7\uff0c\u529b\u9053\u975e\u540c\u5c0f\u53ef\uff0c\u4f17\u4eba\u53ea\u89c1\u5434\u51cc\u98ce\u671b\u90fd\u4e0d\u671b\u4e00\u773c\uff0c\u7b49\u5230 \u957f\u621f\u98de\u8fd1\uff0c\u4f38\u51fa\u53cc\u624b\u4e00\u5408\uff0c\u957f\u621f\u5c31\u5230\u624b\u4e2d\u3002\u51cc\u98ce\u8c22\u9053\uff1a\u201c\u8fd9\u4f4d\u5be8\u4e3b\u7ed9\u5728\u4e0b\u9762\u5b50\uff0c\u5728\u4e0b\u5728\u6b64\u8c22 \u8fc7\u4e86\u3002\u201d \u3000\u3000\u8f6c\u8eab\u5bf9\u9ad8\u6218\u9053\uff1a\u201c\u8fd9\u4f4d\u5c0f\u5f1f\uff0c\u5927\u5bb6\u7b54\u5e94\u4e0d\u518d\u4e3a\u96be\u4f60\u4e86\uff0c\u4f60\u8d76\u5feb\u8d70\u5427\u3002\u201d \u3000\u3000\u9ad8\u6218\u867d\u662f\u7b2c\u4e00\u6b21\u548c\u4ed6\u89c1\u9762\uff0c\u4f46\u5fc3\u4e2d\u4e0d\u77e5\u600e\u4e48\u7adf\u5bf9\u4ed6\u5341\u5206\u4f9d\u604b\uff0c\u4fbf\u8d70\u8fd1\u63a5\u53e3\u56de\u7b54\u9053\uff1a\u201c\u5927 \u53d4\uff0c\u54b1\u4eec\u4e00\u5757\u513f\u8d70\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u51dd\u671b\u9ad8\u6218\u4e00\u773c\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u6211\u8981\u4e0a\u957f\u5b89\u53bb\uff0c\u4f60\u5230\u54ea\u513f\uff1f\u201d \u3000\u3000\u9ad8\u6218\u89c1\u4ed6\u4e00\u7b11\uff0c\u8138\u4e0a\u51c4\u82e6\u4e4b\u8272\u5927\u51cf\uff0c\u90a3\u7edd\u7f8e\u5bb9\u5149\u4e2d\u53c8\u73b0\u51fa\u4e00\u79cd\u98de\u9038\u7684\u795e\u60c5\uff0c\u4e0d\u89c9\u5927\u611f\u9ad8 \u5174\uff0c\u8bf4\u9053\uff1a\u201c\u5434\u5927\u53d4\uff0c\u6211\u5de6\u53f3\u662f\u6e38\u5386\u5929\u4e0b\uff0c\u589e\u957f\u89c1\u95fb\uff0c\u4e5f\u6ca1\u6709\u4e00\u5b9a\u53bb\u5904\uff0c\u60a8\u5c31\u5e26\u6211\u4e0a\u957f\u5b89 \u5427\u3002\u201d \u3000\u3000\u7fa4\u8c6a\u89c1\u4ed6\u4e24\u4eba\u4e00\u95ee\u4e00\u7b54\uff0c\u5168\u7136\u6ca1\u6709\u628a\u81ea\u5df1\u653e\u5728\u773c\u5185\uff0c\u4e0d\u7981\u90fd\u611f\u5230\u96be\u582a\uff0c\u90a3\u8ffd\u4e91\u5251\u6797\u9aa7\u76ef \u7740\u5434\u51cc\u98ce\u5de6\u770b\u53f3\u770b\uff0c\u5ffd\u7136\u5389\u58f0\u9053\uff1a\u201c\u4f60\u662f\u65ad\u9b42\u5251\u5434\u8bcf\u4e91\u4ec0\u4e48\u4eba\uff1f\u201d \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u51fa\u8a00\u4e0d\u900a\uff0c\u6b63\u5f85\u53d1\u4f5c\uff0c\u4f46\u8f6c\u5ff5\u5341\u60f3\uff1a\u201c\u8fd9\u8ffd\u4e91\u5251\u4e00\u5b9a\u548c\u7239\u6614\u5e74\u6709\u6881\u5b50\uff0c\u73b0\u5728\u7239 \u5893\u6728\u5df2\u62f1\uff0c\u4ec0\u4e48\u4ec7\u6068\u90fd\u53ef\u4ee5\u89e3\u9664\u4e86\uff0c\u6211\u4f55\u5fc5\u518d\u5f97\u7f6a\u4e8e\u4ed6\uff0c\u5509\uff0c\u4e16\u4e0a\u7684\u6069\u6028\u60c5\u4ec7\u662f\u6c38\u8fdc\u7f20\u7ef5\u4e0d \u65ad\u7684\u3002\u201d \u3000\u3000\u4ed6\u4e0d\u7531\u53c8\u60f3\u5230\u81ea\u5df1\u7684\u4f24\u5fc3\u4e8b\uff0c\u5fc3\u4e2d\u66f4\u89c9\u7d22\u7136\uff0c\u8fd9\u5341\u5e74\u6765\uff0c\u4ed6\u4e00\u76f4\u5728\u751f\u6b7b\u8fb9\u7f18\u714e\u71ac\uff0c\u6614\u65e5 \u5c11\u5e74\u8c6a\u60c5\uff0c\u5df2\u5927\u90e8\u78e8\u51cf\uff0c\u4fbf\u548c\u58f0\u9053\uff1a\u201c\u8ffd\u9b42\u5251\u6b63\u662f\u5148\u7236\uff0c\u4e0d\u77e5\u5e84\u4e3b\u6709\u4f55\u6307\u6559\u3002\u201d \u3000\u3000\u8ffd\u4e91\u5251\u6797\u9aa7\u6124\u7136\u957f\u7b11\uff0c\u4e45\u4e45\u4e0d\u65ad\uff0c\u7b11\u58f0\u4e2d\u5145\u6ee1\u4e86\u6740\u6c14\uff0c\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\uff0c\u7b11\u6bd5\u51c4\u58f0\u9053\uff1a \u201c\u597d\uff0c\u597d\uff0c\u4f60\u662f\u5434\u8bcf\u4e91\u7684\u513f\u5b50\uff0c\u5434\u8bcf\u4e91\uff0c\u5434\u8bcf\u4e91\uff0c\u4f60\u7adf\u5148\u6211\u800c\u53bb\uff0c\u54b1\u4eec\u4e4b\u95f4\u7684\u5e10\u53ef\u6ca1\u6709\u7b97\u6e05 \u554a\uff01\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u5fae\u611f\u5bd2\u610f\uff0c\u62ac\u5934\u4e00\u770b\u5434\u51cc\u98ce\uff0c\u53ea\u89c1\u4ed6\u4f3c\u4e4e\u5728\u6c89\u601d\u4ec0\u4e48\uff0c\u5fc3\u795e\u4ff1\u9189\uff0c\u5168\u7136\u6ca1\u6709\u6ce8 \u610f\u6797\u9aa7\u6240\u8a00\uff0c\u4fbf\u53eb\u9053\uff1a\u201c\u5434\u5927\u53d4\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u5f85\u4f1a\u518d\u60f3\u5427\uff0c\u4eba\u5bb6\u8981\u6211\u4f60\u62a5\u4ec7\u54e9\uff01\u201d \u3000\u3000\u5434\u51cc\u98ce\u5fc3\u4e2d\u4e00\u60ca\uff0c\u6614\u65e5\u6e29\u99a8\u548c\u60e8\u75db\u7684\u5f80\u4e8b\u50cf\u8f7b\u70df\u8584\u96fe\u4e00\u822c\u4ece\u8111\u6d77\u4e2d\u901d\u53bb\uff0c\u6b63\u8272\u7b54\u9053\uff1a \u201c\u6797\u5e84\u4e3b\uff0c\u5148\u7236\u5df2\u8fc7\u4e16\uff0c\u4ece\u524d\u6709\u4ec0\u4e48\u5bf9\u4e0d\u4f4f\u5e84\u4e3b\u7684\uff0c\u5728\u4e0b\u5728\u6b64\u5411\u60a8\u9053\u6b49\u3002\u201d \u3000\u3000\u5ffd\u7136\u4ece\u6797\u9aa7\u8eab\u540e\u7ad9\u51fa\u4e00\u4e2a\u4e2d\u5e74\u4e66\u751f\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4f60\u5012\u8bf4\u5f97\u8f7b\u677e\uff0c\u5bb6\u7236\u80cc\u5e74\u62dc\u5434\u5927\u4fa0\u7236\u4eb2 \u4e00\u5251\u4e4b\u8d50\uff0c\u6570\u5341\u5e74\u6765\u65e5\u591c\u4e0d\u6562\u7a0d\u5fd8\u5982\u6b64\u5927\u6069\uff0c\u4eca\u65e5\u6b63\u597d\u4e58\u6b64\u673a\u4f1a\uff0c\u7531\u54b1\u4eec\u540e\u4e00\u8f88\u6765\u4e86\u7ed3\u3002\u201d \u3000\u3000\u51cc\u98ce\u89c1\u6797\u73ed\u957f\u5b50\u201c\u94c1\u5251\u4e66\u751f\u201d\u6797\u51b2\u633a\u8eab\u800c\u51fa\uff0c\u77e5\u9053\u4e0d\u80fd\u5584\u4e0d\uff0c\u4ed6\u5728\u6cf0\u5c71\u4f34\u7740\u592a\u6781\u95e8\u7956\u5e08 \u96ea\u82e5\u51b0\u82e6\u4fee\u5341\u8f7d\uff0c\u4e0d\u4f46\u5251\u6cd5\u7cbe\u8fdb\uff0c\u800c\u4e14\u5bf9\u4e8e\u4e16\u95f4\u4e00\u5207\u90fd\u770b\u5f97\u66f4\u52a0\u6df1\u523b\uff0c\u5bf9\u4e8e\u6c5f\u6e56\u4e0a\u4e89\u5f3a\u6597 \u72e0\uff0c\u5df2\u7ecf\u5927\u5927\u4e0d\u611f\u5174\u8da3\uff0c\u662f\u4ee5\u5bf9\u65b9\u867d\u5219\u4e00\u518d\u76f8\u903c\uff0c\u8fd8\u5728\u72b9\u8c6b\u4e0d\u80af\u51fa\u624b\u3002 \u3000\u3000\u6797\u51b2\u53c8\u9053\uff1a\u201c\u5982\u679c\u5434\u5927\u4fa0\u8ba4\u4e3a\u4e0d\u5c51\u4e0e\u5728\u4e0b\u6bd4\u5212\uff0c\u90a3\u4e48\u5728\u4e0b\u4e5f\u4e0d\u5fc5\u81ea\u8ba8\u6ca1\u8da3\uff0c\u5434\u5927\u4fa0\u4f60\u53ea \u7ba1\u8d70\uff0c\u53ea\u662f\u2026\u2026\u53ea\u662f\u2014\u2014\u201d \u3000\u3000\u6d4e\u5357\u5927\u8c6a\u59ec\u672c\u5468\u5fae\u5fae\u6447\u5934\uff0c\u53f9\u9053\uff1a\u201c\u59d3\u6797\u7684\u4e5f\u592a\u8fc7\u5206\uff0c\u59d3\u5434\u7684\u5f53\u5e74\u4f55\u7b49\u5a01\u540d\uff0c\u4ed6\u4e00\u518d\u7ed9 \u4f60\u9762\u5b50\uff0c\u4e5f\u5c31\u7b97\u4e86\uff0c\u4f55\u5fc5\u4e00\u5b9a\u8981\u903c\u4ed6\u51fa\u624b\uff1f\u201d \u3000\u3000\u4ed6\u4e3e\u76ee\u4e00\u770b\uff0c\u8eab\u65c1\u7231\u5973\u53ea\u662f\u51dd\u89c6\u7740\u5148\u524d\u548c\u81ea\u5df1\u52a8\u624b\u7684\u5c11\u5e74\uff0c\u76ee\u5149\u4e2d\u6d0b\u6ea2\u7740\u4e07\u822c\u67d4\u60c5\uff0c\u518d \u770b\u770b\u90a3\u5c11\u5e74\u4f3c\u4e4e\u5e76\u6ca1\u6709\u6ce8\u610f\u81ea\u5df1\u5973\u513f\uff0c\u53ea\u4e00\u5fc3\u4e00\u610f\u671b\u7740\u5434\u51cc\u98ce\uff0c\u63e1\u7740\u5434\u51cc\u98ce\u7684\u624b\u7ad9\u5728\u4ed6\u8eab \u65c1\uff0c\u4e0d\u89c9\u5927\u662f\u6c14\u607c\uff0c\u6012\u54fc\u4e86\u58f0\u3002 \u3000\u3000\u9ad8\u6218\u89c1\u5434\u5927\u4fa0\u88ab\u4eba\u6b3a\u4fae\uff0c\u4ed6\u4e00\u5411\u5e73\u548c\u7684\u813e\u6c14\uff0c\u7a81\u7136\u4e4b\u95f4\u6fc0\u52a8\u8d77\u6765\uff0c\u81ea\u5df1\u7adf\u7136\u63a7\u5236\u4e0d\u4f4f\u5bf9 \u7740\u6797\u51b2\uff0c\u53eb\u9053\uff1a\u201c\u53ea\u662f\u600e\u6837\uff0c\u4f60\u6709\u79cd\u5c31\u8bf4\u51fa\u6765\u3002\u201d \u3000\u3000\u9ad8\u6218\u5ffd\u7136\u611f\u5230\u5434\u5927\u53d4\u624b\u5fc3\u4e00\u6296\uff0c\u50cf\u662f\u51b3\u5b9a\u4e86\u4e00\u4ef6\u5927\u4e8b\uff0c\u53ea\u542c\u89c1\u5434\u5927\u53d4\u8f7b\u53f9\u4e00\u58f0\u9053\uff1a\u201c\u65e2 \u7136\u5982\u6b64\uff0c\u90a3\u4e48\u5728\u4e0b\u53ea\u6709\u63a5\u62db\u4e86\u3002\u201d \u3000\u3000\u6797\u51b2\u51b7\u7136\u4e00\u7b11\uff0c\u53cd\u624b\u62d4\u5251\uff0c\u5de6\u624b\u4e00\u9886\u5251\u8bc0\uff0c\u5c31\u6b32\u8fdb\u653b\uff0c\u4f46\u89c1\u5434\u51cc\u98ce\u53cc\u624b\u7a7a\u7a7a\uff0c\u4f3c\u4e4e\u6ee1\u4e0d \u5728\u610f\uff0c\u4e0d\u7531\u6012\u9053\uff1a\u201c\u9601\u4e0b\u600e\u4e0d\u62d4\u5251\uff1f\u96be\u9053\u6211\u6797\u67d0\u4eba\u4e0d\u914d\u4e0e\u9601\u4e0b\u6bd4\u5251\u4e48\uff1f\u201d \u3000\u3000\u51cc\u98ce\u8f7b\u58f0\u9053\uff1a\u201c\u5728\u4e0b\u5c31\u51ed\u7a7a\u624b\u4e0e\u5c11\u5e84\u4e3b\u73a9\u73a9\u3002\u201d \u3000\u3000\u4ed6\u6b64\u8a00\u8868\u9762\u4e0a\u8bf4\u5f97\u751a\u662f\u8f7b\u677e\uff0c\u5176\u5b9e\u5bf9\u4e8e\u6797\u51b2\u53ef\u8bf4\u8511\u89c6\u5df2\u6781\uff0c\u4ed6\u89c1\u5bf9\u65b9\u6108\u903c\u6108\u7d27\uff0c\u4e0d\u7981\u6fc0 \u8d77\u6765\u60c5\uff0c\u5fc3\u60f3\u53cd\u6b63\u4e0d\u80fd\u5584\u7f62\uff0c\u5012\u4e0d\u5982\u663e\u70b9\u5a01\u98ce\u7ed9\u4f60\u4eec\u77a7\u77a7\u3002 \u3000\u3000\u201c\u94c1\u5251\u4e66\u751f\u201d\u6797\u51b2\u4e3a\u4eba\u57ce\u5e9c\u6781\u6df1\uff0c\u542c\u5230\u51cc\u98ce\u72c2\u8a00\uff0c\u4e0d\u4f46\u6ca1\u6709\u6c14\u660f\uff0c\u53cd\u800c\u5fd6\u9053\uff1a\u201c\u8fd9\u53ae\u81ea \u6301\u529f\u529b\u5546\u5f3a\uff0c\u6211\u4f55\u4e0d\u62e3\u4e2a\u73b0\u6210\u4fbf\u5b9c\uff0c\u6211\u4e3a\u7236\u4eb2\u96ea\u803b\uff0c\u522b\u4eba\u4e5f\u4e0d\u4f1a\u803b\u7b11\u3002\u201d \u3000\u3000\u76d8\u7b97\u5df2\u5b9a\uff0c\u53eb\u9053\uff1a\u201c\u5434\u5927\u4fa0\uff0c\u770b\u5251\u3002\u201d \u3000\u3000\u957f\u5251\u4e00\u6325\uff0c\u821e\u4e86\u4e00\u4e2a\u5251\u82b1\uff0c\u7a81\u5730\u5411\u524d\u4e00\u633a\uff0c\u76f4\u523a\u51cc\u98ce\u9762\u95e8\uff0c\u51cc\u98ce\u8eab\u5b50\u4e0d\u52a8\uff0c\u5934\u5f80\u5de6\u504f\uff0c \u95ea\u8fc7\u4e00\u62db\uff0c\u7b11\u9053\uff1a\u201c\u597d\u4e00\u62db\u6bd2\u9f99\u51fa\u6d1e\u3002\u201d \u3000\u3000\u94c1\u5251\u4e66\u751f\u6797\u51b2\u4e00\u8a00\u672a\u53d1\uff0c\u957f\u5251\u62db\u62db\u51fb\u5411\u51cc\u98ce\u8981\u5bb3\uff0c\u51cc\u98ce\u5fae\u6012\uff0c\u6697\u9053\uff1a\u201c\u8fd9\u53ae\u4e0d\u8bc6\u597d\u6b79\uff0c \u4eca\u65e5\u4e4b\u4e8b\u4e0d\u9732\u70b9\u771f\u672c\u4e8b\uff0c\u53ea\u6015\u4e0d\u6613\u8131\u8eab\u3002\u201d \u3000\u3000\u5ffd\u7136\u6797\u51b2\u4e00\u5251\u5411\u51cc\u98ce\u5c0f\u8179\u523a\u6765\uff0c\u51cc\u98ce\u77a7\u5f97\u4ed4\u7ec6\uff0c\u4e00\u811a\u8e29\u4f4f\u957f\u5251\uff0c\u8eab\u5f62\u5fae\u52a8\uff0c\u4e24\u6307\u5411\u6797\u51b2 \u53cc\u76ee\u70b9\u53bb\u3002 \u3000\u3000\u6797\u51b2\u89c1\u5bf9\u65b9\u62db\u5f0f\u5feb\u82e5\u95ea\u7535\uff0c\u53cc\u6307\u5df2\u8fd1\u81ea\u5df2\u9762\u95e8\uff0c\u4e0d\u7531\u5927\u9a87\uff0c\u53ea\u5f97\u677e\u4e0b\u53f3\u624b\u4e4b\u5251\uff0c\u5411\u540e\u4e00 \u7a9c\u3002 \u3000\u3000\u51cc\u98ce\u5f2f\u8eab\u62fe\u5251\uff0c\u5ffd\u95fb\u80cc\u540e\u98ce\u58f0\uff0c\u4e00\u8f6c\u8eab\u4e3e\u5251\u76f8\u67b6\u3002\u539f\u6765\u98ce\u4e91\u5251\u6797\u9aa7\u89c1\u81ea\u5df1\u513f\u5b50\u4e00\u51fa\u624b\u4fbf \u88ab\u4ec7\u4eba\u628a\u5251\u593a\u53bb\uff0c\u5fc3\u4e2d\u53c8\u60ca\u53c8\u6025\uff0c\u987e\u4e0d\u5f97\u6c5f\u6e56\u9053\u4e49\uff0c\u7a81\u65bd\u5077\u88ad\u3002 \u3000\u3000\u51cc\u98ce\u4e0e\u5bf9\u65b9\u786c\u63a5\u4e00\u5251\uff0c\u89c9\u5f97\u98ce\u4e91\u5251\u5185\u529b\u5145\u6c9b\uff0c\u4e0d\u6562\u8f7b\u654c\uff0c\u7acb\u5373\u5c55\u5f00\u201c\u65ad\u9b42\u5251\u6cd5\u201d\uff0c\u53f3\u624b \u8fde\u65bd\u7edd\u62db\uff0c\u5de6\u624b\u65bd\u51fa\u201c\u5f00\u5c71\u4e09\u5f0f\u201d\u7834\u7389\u62f3\uff0c\u8fde\u7ef5\u4e0d\u7edd\u3002 \u3000\u3000\u6797\u9aa7\u51dd\u795e\u63a5\u62db\uff0c\u773c\u89c1\u5bf9\u65b9\u653b\u52bf\u5982\u957f\u6c5f\u5927\u6cb3\uff0c\u6ed4\u6ed4\u4e0d\u7edd\uff0c\u6bd4\u8d77\u5f53\u5e74\u5434\u8bcf\u4e91\u6709\u8fc7\u4e4b\u800c\u65e0\u4e0d \u53ca\uff0c\u4e0d\u7531\u5fc3\u5185\u5927\u9a87\uff0c\u5fae\u4e00\u758f\u795e\uff0c\u53f3\u80a9\u95ea\u52a8\u7565\u6162\uff0c\u5bf9\u65b9\u957f\u5251\u5411\u808b\u4e0b\u523a\u6765\uff0c\u8fde\u5fd9\u540e\u9000\u3002\u60ca\u9b42\u752b \u5b9a\uff0c\u4f46\u89c1\u773c\u524d\u5bd2\u661f\u70b9\u70b9\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u4e0d\u77e5\u5982\u4f55\u62db\u67b6\uff0c\u53ea\u5f97\u4e3e\u5251\u62a4\u4f4f\u95e8\u9762\uff0c\u5ffd\u89c9\u53f3\u624b\u4e00\u9ebb\uff0c \u957f\u5251\u518d\u4e5f\u628a\u6301\u4e0d\u4f4f\uff0c\u8131\u624b\u98de\u53bb\u3002 \u3000\u3000\u539f\u6765\u5434\u51cc\u98ce\u89c1\u4ed6\u8def\u51fa\u7834\u7efd\uff0c\u7acb\u5373\u4e58\u52bf\u76f4\u4e0a\uff0c\u8fd0\u8d77\u5185\u529b\uff0c\u628a\u4e00\u62db\u201c\u70b9\u70b9\u78f7\u661f\u201d\u65bd\u5f97\u987e\u76fc\u751f \u59ff\uff0c\u76f4\u5982\u6ee1\u5929\u5251\u5e55\uff0c\u90a3\u98ce\u4e91\u5251\u679c\u7136\u4e0d\u77e5\u6240\u63aa\uff0c\u51cc\u98ce\u5251\u950b\u4e00\u633a\uff0c\u523a\u4e2d\u4ed6\u53f3\u81c2\u80a9\u80bf\u7a74\u3002 \u3000\u3000\u98ce\u4e91\u5251\u4e3e\u76ee\u770b\u770b\u56db\u5468\u7eff\u6797\u597d\u6c49\uff0c\u4e00\u4e2a\u4e2a\u4f3c\u7b11\u975e\u7b11\u7684\u671b\u7740\u4ed6\uff0c\u60f3\u5230\u5f53\u5e74\u5c31\u662f\u8d25\u5728\u8fd9\u4e24\u62db \u4e0a\uff0c\u81ea\u5df1\u7cbe\u7814\u51e0\u5341\u5e74\uff0c\u4ecd\u7136\u65e0\u6cd5\u7834\u53bb\uff0c\u4e0d\u7531\u7f9e\u6127\u96be\u5f53\uff0c\u771f\u6b32\u6a2a\u5251\u81ea\u520e\u3002 \u3000\u3000\u51cc\u98ce\u56de\u5934\u4e00\u62c9\u9ad8\u6218\uff0c\u5411\u7fa4\u8c6a\u6325\u6325\u624b\uff0c\u51e0\u4e2a\u8d77\u843d\uff0c\u8d8a\u5899\u800c\u53bb\uff0c\u7fa4\u8c6a\u89c1\u4ed6\u4e0d\u6570\u62db\u5c31\u5c06\u98ce\u4e91\u5251 \u7236\u5b50\u51fb\u8d25\uff0c\u4e0d\u7531\u76f8\u987e\u9a87\u7136\uff0c\u518d\u4e5f\u6ca1\u6709\u4eba\u6562\u51fa\u624b\u76f8\u62e6\u4e86\u3002 \u3000\u3000\u4e14\u8bf4\u51cc\u98ce\u5e26\u7740\u9ad8\u6218\u8df3\u8fc7\u5899\u5934\uff0c\u9ad8\u6218\u89c1\u4ed6\u62c9\u7740\u81ea\u5df1\u6beb\u4e0d\u8d39\u529b\u4e00\u8dc3\u800c\u7ffb\u8fc7\u4e09\u4e08\u7684\u9ad8\u5899\uff0c\u5fc3\u4e2d \u771f\u662f\u4f69\u670d\u6781\u4e86\uff0c\u4fbf\u9053\uff1a\u201c\u5434\u5927\u53d4\uff0c\u60a8\u8f7b\u529f\u771f\u597d\uff0c\u60a8\u771f\u4e86\u4e0d\u8d77\u3002\u201d \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u6ee1\u8138\u7fa1\u6155\u4e4b\u8272\uff0c\u7b11\u9053\uff1a\u201c\u96be\u9053\u6b66\u529f\u597d\u5c31\u4e86\u4e0d\u8d77\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u6218\u70b9\u70b9\u5934\uff0c\u5ffd\u53c8\u6447\u5934\u9053\uff1a\u201c\u5982\u679c\u53ea\u662f\u6b66\u529f\u597d\uff0c\u90a3\u4e48\u5f53\u7136\u6ca1\u6709\u4ec0\u4e48\u4e86\u4e0d\u5f97\uff0c\u53ef\u662f\u50cf\u5927\u53d4 \u8fd9\u6837\uff0c\u529f\u592b\u65e2\u9ad8\uff0c\u53c8\u5904\u5904\u8ba9\u4eba\u4e00\u6b65\uff0c\u90a3\u624d\u53eb\u771f\u6b63\u4e86\u4e0d\u8d77\u54e9\uff01\u201d \u3000\u3000\u51cc\u98ce\u5fc3\u5934\u4e00\u9707\uff0c\u60f3\u9053\uff1a\u201c\u8fd9\u5b69\u5b50\u5fc3\u5730\u6148\u7965\uff0c\u662f\u975e\u5584\u6076\u5206\u5f97\u6781\u662f\u6e05\u695a\uff0c\u77a7\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\uff0c\u529f \u529b\u5df2\u662f\u4e0d\u51e1\uff0c\u65e5\u540e\u5fc5\u6210\u5927\u5668\u3002\u201d\u5c31\u6b63\u8272\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\uff0c\u4e00\u4e2a\u4eba\u5e76\u4e0d\u4e00\u5b9a\u8981\u4ee5\u6b66\u529b\u538b\u670d\u522b\u4eba\u624d \u7b97\u5a01\u98ce\uff0c\u50cf\u521a\u624d\u7684\u4e8b\uff0c\u6211\u7ed9\u4ed6\u4eec\u673a\u4f1a\u2026\u2026\u6211\u7ed9\u4ed6\u4eec\u673a\u4f1a\u53ef\u4ee5\u4e0d\u5fc5\u52a8\u6b66\uff0c\u800c\u5927\u5bb6\u90fd\u4fdd\u6301\u5c0a\u4e25\uff0c \u53ef\u662f\u2026\u2026\u53ef\u662f\u4ed6\u4eec\u5374\u903c\u7740\u6211\u3002\u5c0f\u5f1f\u5f1f\uff0c\u771f\u6b63\u7684\u5c0a\u4e25\u5e76\u4e0d\u9760\u6b66\u529b\u6765\u4fdd\u6301\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u5927\u662f\u611f\u52a8\uff0c\u4ed6\u5929\u6027\u548c\u5e73\u6de1\u6cca\uff0c\u8fd9\u756a\u8bdd\u81ea\u662f\u6781\u4e3a\u7231\u542c\uff0c\u53eb\u9053\uff1a\u201c\u5434\u5927\u53d4\uff0c\u6218\u513f\u542c \u4f60\u7684\u8bdd\u3002\u201d \u3000\u3000\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u5434\u5927\u53d4\u4e24\u773c\u671b\u5411\u8fdc\u5904\uff0c\u5915\u9633\u4f59\u8f89\u6b63\u7167\u7740\u4ed6\u7684\u8138\uff0c\u795e\u8272\u975e\u5e38\u5e84\u4e25\uff0c\u9ad8\u6218\u5fc3 \u4e2d\u5ffd\u7136\u4e00\u4e2a\u5ff5\u5934\u95ea\u8fc7\uff0c\u95ee\u9053\uff1a\u201c\u5434\u5927\u53d4\u3002\u4f60\u8ba4\u4e0d\u8ba4\u5f97\u90a3\u6d4e\u5357\u5927\u8c6a\u3002\u201d \u3000\u3000\u51cc\u98ce\u4e00\u6014\uff0c\u7b54\u9053\uff1a\u201c\u542c\u8bf4\u4ed6\u662f\u4e00\u4e2a\u5343\u91cc\u72ec\u884c\u76d7\u3002\u201d \u3000\u3000\u9ad8\u6218\u53c8\u95ee\u9053\uff1a\u201c\u4ed6\u662f\u597d\u4eba\u8fd8\u662f\u574f\u4eba\u3002\u201d \u3000\u3000\u51cc\u98ce\u6447\u5934\u9053\uff1a\u201c\u542c\u8bf4\u6b64\u4eba\u52ab\u5bcc\u6d4e\u8d2b\uff0c\u5012\u4e5f\u4e0d\u5931\u662f\u6761\u6c49\u5b50\uff0c\u4f60\u95ee\u8fd9\u4e2a\u5e72\u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u8138\u4e0a\u5fae\u7ea2\uff0c\u4ed6\u4e0d\u5584\u64a4\u8c0e\uff0c\u8bb7\u8bb7\u4e0d\u77e5\u6240\u5bf9\u3002 \u3000\u3000\u51cc\u98ce\u77a7\u4e86\u4ed6\u4e00\u773c\uff0c\u89c1\u4ed6\u5ffd\u7136\u6ee1\u8138\u5ff8\u6029\uff0c\u4e5f\u4e0d\u77e5\u4ed6\u5fc3\u4e2d\u60f3\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u4e24\u4eba\u4e00\u8def\u4e0a\u76f8\u5904\u6781\u662f\u878d\u6d3d\uff0c\u9ad8\u6218\u89c1\u5434\u5927\u53d4\u603b\u662f\u90c1\u90c1\u5be1\u6b22\uff0c\u5c31\u60f3\u5c3d\u65b9\u6cd5\u6765\u66ff\u4ed6\u89e3\u95f7\u3002 \u3000\u3000\u8fd9\u65e5\u65e9\u4e0a\u4e24\u4eba\u8fdb\u4e86\u957f\u5b89\u57ce\uff0c\u9ad8\u6218\u89c1\u5434\u5927\u53d4\u6108\u6765\u6108\u662f\u51c4\u82e6\uff0c\u5fc3\u4e2d\u4e5f\u611f\u5230\u5982\u5927\u77f3\u538b\u5728\u80f8\u5934\uff0c \u5f88\u4e0d\u5feb\u6d3b\u3002\u4e24\u4eba\u843d\u4e86\u540e\uff0c\u5403\u8fc7\u65e9\u996d\uff0c\u51cc\u98ce\u5ffd\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\u6211\u6559\u4f60\u4e00\u5957\u529f\u592b\uff0c\u660e\u513f\u54b1\u4eec\u5c31\u8981\u5206 \u624b\u5566\u3002\u201d \u3000\u3000\u9ad8\u6218\u53c8\u559c\u53c8\u60ca\uff0c\u8131\u53e3\u95ee\u9053\uff1a\u201c\u5434\u5927\u53d4\uff0c\u4f60\u5230\u54ea\u513f\u53bb\uff1f\u201d \u3000\u3000\u51cc\u98ce\u9eef\u7136\u4e0d\u8bed\uff0c\u826f\u4e45\u624d\u8bf4\u9053\uff1a\u201c\u6211\u2026\u2026\u6211\u8981\u5230\u5f88\u8fdc\u5f88\u8fdc\u7684\u5730\u65b9\u53bb\uff0c\u770b\u770b\u8001\u670b\u53cb\u3002\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u4f60\u8fd8\u56de\u6765\u77a7\u6211\u5417\uff1f\u201d \u3000\u3000\u51cc\u98ce\u89c1\u4ed6\u5bf9\u81ea\u5df1\u60c5\u6df1\u5982\u65af\uff0c\u4e5f\u4e0d\u89c9\u6709\u4e9b\u51c4\u7136\uff0c\u7b11\u9053\uff1a\u201c\u4f60\u884c\u8d70\u6c5f\u6e56\uff0c\u6211\u4eec\u65e5\u540e\u5f53\u7136\u6709\u518d \u89c1\u7684\u673a\u4f1a\uff0c\u597d\uff0c\u54b1\u4eec\u5148\u6765\u7ec3\u529f\u3002\u201d \u3000\u3000\u5f53\u4e0b\u51cc\u98ce\u5c31\u628a\u592a\u6781\u95e8\u9707\u95e8\u4e4b\u5b9d\u5f00\u5c71\u4e09\u5f0f\u7834\u7389\u62f3\u4f20\u7ed9\u9ad8\u6218\uff0c\u9ad8\u6218\u5929\u8d44\u9896\u609f\uff0c\u5185\u529b\u6df1\u6e5b\uff0c\u53c8 \u5403\u4e86\u201c\u5343\u5e74\u53c2\u738b\u201d\uff0c\u662f\u4ee5\u7ec3\u529f\u592b\u90fd\u662f\u4e8b\u534a\u529f\u500d\uff0c\u51cc\u98ce\u89c1\u4ed6\u5728\u77ed\u77ed\u534a\u5929\u5185\uff0c\u80fd\u628a\u8fd9\u5957\u62f3\u6cd5\u7cbe \u4e49\uff0c\u5168\u7136\u9886\u7565\uff0c\u4e5f\u4e0d\u7531\u5fc3\u4e2d\u6697\u8d5e\u3002 \u3000\u3000\u9ad8\u6218\u5728\u5ba4\u4e2d\u7ec3\u4e86\u51e0\u904d\uff0c\u4fbf\u5750\u5728\u5e8a\u4e0a\u601d\u60f3\u5176\u4e2d\u5965\u5999\uff0c\u5f53\u4ed6\u60f3\u5230\u7cbe\u5fae\u4e4b\u5904\u3002\u4e0d\u89c9\u5fc3\u795e\u4ff1\u9189\uff0c \u8df3\u4e0b\u5e8a\u6765\uff0c\u53cc\u624b\u5de6\u53f3\u5411\u7a7a\u5404\u753b\u534a\u5708\uff0c\u84e6\u7136\u4e00\u5408\uff0c\u5e73\u63a8\u51fa\u53bb\u3002 \u3000\u3000\u8fd9\u6b63\u662f\u5f00\u5c71\u4e09\u5f0f\u4e2d\u7684\u6700\u540e\u4e00\u62db\u201c\u611a\u516c\u79fb\u5c71\u201d\uff0c\u4e5f\u662f\u5a01\u529b\u6700\u5927\u7684\u4e00\u62db\uff0c\u5982\u679c\u7ec3\u5230\u9876\u70b9\uff0c\u7aef \u7684\u5929\u575a\u4e0d\u6467\uff0c\u9ad8\u6218\u53cc\u638c\u63a8\u51fa\u53bb\u4e00\u534a\uff0c\u7a81\u7136\u60f3\u8d77\u8fd9\u662f\u5ba4\u5185\uff0c\u8fd9\u4e00\u62db\u65bd\u51fa\uff0c\u53ea\u6015\u8fde\u623f\u5b50\u4e5f\u8981\u51fb \u57ae\uff0c\u614c\u5fd9\u4e00\u61c8\u529f\uff0c\u4e0b\u76d8\u8fd0\u529f\uff0c\u8eab\u5b50\u6ef4\u6e9c\u6e9c\u8f6c\u4e86\u4e00\u4e2a\u5708\u5b50\uff0c\u603b\u7b97\u6ca1\u6709\u524d\u503e\u8dcc\u5012\u3002 \u3000\u3000\u4ed6\u60f3\uff1a\u201c\u6211\u4f55\u4e0d\u5230\u91ce\u5916\u53bb\u7ec3\u7ec3\u3002\u201d\u770b\u770b\u5434\u5927\u53d4\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u79bb\u53bb\uff0c\u53ea\u9053\u4ed6\u51fa\u5916\u6563\u5fc3\uff0c\u4e5f \u4e0d\u5728\u610f\uff0c\u4fe1\u6b65\u5c31\u5f80\u57ce\u90ca\u8d70\u53bb\uff0c\u51fa\u5f97\u57ce\u95e8\uff0c\u8d70\u4e86\u534a\u664c\uff0c\u53ea\u89c1\u524d\u9762\u4e00\u5904\u4e18\u9675\uff0c\u5c31\u5728\u5c71\u811a\u4e0b\u9a7b\u8db3\u53cd \u590d\u7ec3\u4e60\uff0c\u5239\u65f6\u638c\u98ce\u547c\u547c\uff0c\u9ad8\u6218\u81ea\u89c9\u62db\u5f0f\u6108\u6765\u6108\u719f\uff0c\u529b\u9053\u4e5f\u6108\u6765\u6108\u6c89\uff0c\u4e0d\u7531\u5927\u559c\u3002 \u3000\u3000\u7ec3\u4e86\u5f88\u4e45\uff0c\u989d\u89d2\u89c1\u6c57\uff0c\u5c31\u5750\u4e0b\u6765\u4f11\u606f\u4e00\u4f1a\uff0c\u5ffd\u7136\u4e00\u6761\u706b\u5f71\u4ece\u4ed6\u8eab\u65c1\u6643\u8fc7\uff0c\u9ad8\u6218\u5b9a\u795e\u4e00 \u770b\uff0c\u6b63\u662f\u5434\u5927\u53d4\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u5bf9\u9999\u70db\uff0c\u4f4e\u7740\u5934\u5982\u98de\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u6b63\u60f3\u51fa\u8a00\u62db\u547c\uff0c\u4f46\u662f\u5434\u5927\u53d4\u811a\u7a0b\u592a\u5feb\uff0c\u7adf\u6765\u4e0d\u53ca\u53eb\u558a\uff0c\u9ad8\u6218\u4e5f\u8d76\u5fd9\u7ffb\u8eab\u7ad9\u8d77\u6765\uff0c\u5411\u5434 \u5927\u53d4\u53bb\u8def\u8ffd\u53bb\u3002 \u3000\u3000\u8ffd\u4e86\u4e00\u4f1a\uff0c\u4e5f\u4e0d\u89c1\u5434\u5927\u53d4\u5f71\u5b50\uff0c\u5fc3\u4e2d\u6b63\u611f\u5947\u602a\uff0c\u84e6\u7684\u4e00\u9635\u4f4e\u6c89\u5982\u68a6\u7684\u58f0\u97f3\u968f\u98ce\u98d8\u6765\uff0c\u9ad8 \u6218\u5faa\u58f0\u5411\u5de6\u8dd1\u53bb\u3002\u53ea\u89c1\u5434\u5927\u53d4\u80cc\u5411\u7740\u4ed6\uff0c\u5750\u5728\u524d\u9762\u4e0d\u8fdc\u4e00\u5904\u575f\u524d\uff0c\u53e3\u4e2d\u5583\u5583\u4f4e\u8bed\uff0c\u9ad8\u6218\u51dd\u795e \u53bb\u542c\u3002\u4e5f\u542c\u4e0d\u6e05\u695a\u8bf4\u4e9b\u4ec0\u4e48\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u60f3\uff1a\u201d\u5434\u5927\u53d4\u8bf4\u8981\u53bb\u4f1a\u8001\u670b\u53cb\uff0c\u539f\u6765\u4ed6\u7684\u670b\u53cb\u5df2\u7ecf\u6b7b\u53bb\uff0c\u96be\u602a\u5434\u5927\u53d4\u90a3\u4e48\u4e0d\u6109 \u5feb\u3002\u201d \u3000\u3000\u5c71\u98ce\u7c41\u7c41\uff0c\u666f\u8272\u5f88\u662f\u51c4\u51c9\u3002 \u3000\u3000\u6e10\u6e10\u7684\uff0c\u5434\u5927\u53d4\u58f0\u97f3\u5fae\u9ad8\uff0c\u4f3c\u4e4e\u662f\u5728\u4e0e\u4eba\u4e89\u8fa9\uff0c\u9ad8\u6218\u4e0d\u7531\u53c8\u8d70\u8fd1\u4e9b\uff0c\u53ea\u5440\u89c1\u4ed6\u9053\uff1a\u201c\u963f \u5170\uff0c\u963f\u5170\uff0c\u6211\u5fc3\u4e2d\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\uff0c\u4f60\u96be\u8ffd\u8fd8\u4e0d\u660e\u767d\u5417\uff1f\u6211\u5929\u5929\u665a\u4e0a\u4f5c\u68a6\u68a6\u5230\u4f60\uff0c\u4f60\u603b\u662f\u4e00\u53e5 \u8bdd\u4e0d\u8bf4\u5c31\u8d70\u5f00\uff0c\u963f\u5170\uff0c\u4f60\u8fd8\u6c14\u4f60\u5927\u54e5\u5417\uff1f\u201d \u3000\u3000\u58f0\u97f3\u51c4\u51c9\uff0c\u50cf\u662f\u4ece\u5fc3\u5e95\u503e\u8bc9\u800c\u51fa\uff0c\u9ad8\u6218\u60f3\u9053\uff0c\u201c\u5434\u5927\u53d4\u548c\u8c01\u5728\u8bb2\u8bdd\u5440\uff1f\u201d \u3000\u3000\u5434\u51cc\u98ce\u53c8\u9053\uff1a\u201c\u963f\u5170\uff0c\u5341\u5e74\u4e86\uff0c\u5927\u54e5\u6709\u54ea\u4e00\u5929\u4e0d\u5728\u60f3\u4f60\uff0c\u53c8\u6709\u632a\u4e00\u5929\u662f\u5feb\u6d3b\u7684\uff1f\u6211\u5929\u5929 \u90fd\u5728\u60f3\u4f60\u4e3a\u4ec0\u4e48\u5fcd\u5fc3\u79bb\u5f00\u6211\uff0c\u53ef\u662f\uff0c\u53ef\u662f\u963f\u5170\uff0c\u4f60\u5927\u54e5\u771f\u7b28\uff0c\u600e\u4e48\u6837\u4e5f\u60f3\u4e0d\u51fa\u6765\u2026\u2026\u5927\u54e5\u8981 \u6709\u4ec0\u4e48\u5730\u65b9\u5bf9\u4e0d\u4f4f\u4f60\uff0c\u4f60\u6253\u6211\uff0c\u9a82\u6211\uff0c\u751a\u81f3\u4e8e\u6740\u6211\uff0c\u6211\u4e5f\u662f\u7518\u5fc3\u60c5\u613f\uff0c\u53ef\u662f\u4f60\u8fd9\u4e48\u4e00\u8d70\uff0c\u5269 \u4e0b\u7684\u65e0\u8fb9\u75db\u82e6\uff0c\u8981\u4f60\u5927\u54e5\u4e00\u4e2a\u4eba\u627f\u62c5\uff0c\u963f\u5170\u554a\uff0c\u5927\u54e5\u7684\u5fc3\u90fd\u788e\u4e86\u2026\u2026\u201d \u3000\u3000\u9ad8\u6218\u542c\u4ed6\u5982\u6028\u5982\u8bc9\uff0c\u5fc3\u4e2d\u4e00\u5bd2\uff0c\u5490\u9053\uff1a\u201c\u539f\u6765\u5434\u5927\u53d4\u662f\u548c\u5893\u4e2d\u4eba\u8bf4\u8bdd\u3002\u201d \u3000\u3000\u63a5\u7740\u51cc\u98ce\u53cd\u53cd\u590d\u590d\u8bc9\u8bf4\u81ea\u5df1\u7684\u5bc2\u5bde\u75db\u82e6\uff0c\u9ad8\u6218\u542c\u4e86\u751a\u662f\u540c\u60c5\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e16\u4e0a\u7684\u4eba\u5feb\u6d3b \u7684\u5012\u662f\u5c11\uff0c\u75db\u82e6\u7684\u53ef\u662f\u591a\u5f97\u5f88\uff0c\u8981\u662f\u6211\u80fd\u591f\u5c3d\u89e3\u5929\u4e0b\u4eba\u7684\u75db\u82e6\uff0c\u90a3\u4e48\u5c31\u662f\u8981\u6211\u6b7b\u6389\uff0c\u6211\u4e5f\u662f \u613f\u610f\u7684\u3002\u201d \u3000\u3000\u9ad8\u6218\u7a81\u89c1\u5434\u5927\u53d4\u62ac\u8d77\u5934\u6765\uff0c\u5446\u5446\u7684\u770b\u7740\u6697\u6de1\u5730\u5929\u9645\uff0c\u90a3\u6708\u5149\u4e2d\u662f\u7edd\u671b\uff0c\u9634\u6697\u548c\u523b\u9aa8\u7684\u82e6 \u75db\uff0c\u9ad8\u6218\u671b\u4e86\u4e24\u773c\uff0c\u53ea\u89c9\u4ed6\u4e00\u5207\u90fd\u663e\u5f97\u90a3\u4e48\u6df1\u523b\uff0c\u90a3\u8868\u60c5\u53ea\u8981\u770b\u4e0a\u4e00\u773c\u5c31\u8db3\u4ee5\u4f7f\u4eba\u7ec8\u8eab\u523b\u5212 \u5728\u8111\u6d77\u4e2d\u3002\u9ad8\u6218\u5ffd\u7136\u89c9\u5f97\u81ea\u5df1\u5f88\u662f\u6d45\u8584\u3002 \u3000\u3000\u5434\u51cc\u98ce\u5ffd\u7136\u8f6c\u8eab\u9053\uff1a\u201c\u5c0f\u5f1f\u5f1f\uff0c\u51fa\u6765\u5427\u3002\u201d \u3000\u3000\u9ad8\u6218\u4f9d\u8a00\u8df3\u4e0b\uff0c\u5fc3\u4e2d\u6697\u4f69\u5434\u5927\u53d4\u529f\u529b\u6df1\u539a\uff0c\u5373\u4f7f\u5728\u60b2\u54c0\u4e2d\uff0c\u5374\u4e5f\u80fd\u987e\u53ca\u56db\u5468\u3002 \u3000\u3000\u51cc\u98ce\u4e5f\u4e0d\u8a00\u8bed\uff0c\u9ad8\u6218\u4e00\u770b\u90a3\u5e55\u7891\u4e0a\u5199\u7740\uff1a\u201c\u5170\u59d1\u5a18\u4e4b\u5893\u201d\u5893\u65c1\u6709\u4e00\u5bf9\u77f3\u72ee\uff0c\u5728\u8fd9\u8352\u5c71\u4e2d \u663e\u5f97\u5341\u5206\u5a01\u731b\u3002\u4e5f\u53ef\u4ee5\u770b\u51fa\u8fd9\u7b51\u5893\u4eba\u7684\u82e6\u5fc3\u3002 \u3000\u3000\u9ad8\u6218\u529d\u9053\uff1a\u201c\u5434\u5927\u53d4\uff0c\u54b1\u4eec\u56de\u53bb\u5427\u3002\u201d \u3000\u3000\u5434\u51cc\u98ce\u4e00\u5446\uff0c\u53e3\u4e2d\u832b\u7136\u5583\u5583\u9053\uff1a\u201c\u5f52\u4f55\u5904\uff0c\u5f52\u4f55\u5904\uff0c\u5929\u6daf\u65e0\u9645\uff0c\u4f55\u5904\u662f\u4e50\u571f\u2026\u2026\u201d\uff0c\u4e8e \u662f\u5bf9\u9ad8\u6218\u4e00\u6325\u624b\uff0c\u6f20\u7136\u7684\u77a5\u4e86\u56db\u5468\u4e00\u773c\uff0c\u65bd\u5c55\u4e0a\u4e58\u8f7b\u529f\uff0c\u98de\u5feb\u7684\u8d70\u8fdc\u4e86\u3002 \u3000\u3000\u9ad8\u6218\u4e00\u65f6\u4e4b\u95f4\uff0c\u4e0d\u77e5\u6240\u63aa\uff0c\u5446\u5446\u671b\u7740\u51cc\u98ce\u80cc\u5f62\u6d88\u5931\u5728\u5c71\u6797\u95f4\uff0c\u4f46\u89c9\u5929\u5730\u60a0\u60a0\uff0c\u4e0d\u5982\u610f\u7684 \u4e8b\u90fd\u9661\u7136\u6d8c\u4e0a\u5fc3\u5934\uff0c\u7236\u6bcd\u4eb2\u7231\u7684\u97f3\u5bb9\u548c\u6c38\u522b\u65f6\u7684\u60e8\u666f\u4e5f\u6d6e\u5728\u773c\u5e95\uff0c\u76f4\u6b32\u653e\u58f0\u4e00\u54ed\u2026\u2026 \u3000\u3000\u4e14\u8bf4\u9ad8\u6218\u671b\u7740\u5434\u5927\u53d4\u80cc\u5f71\u6d88\u5931\uff0c\u5fc3\u5185\u767e\u611f\u4ea4\u96c6\uff0c\u4ed6\u60f3\uff1a\u201c\u5434\u5927\u53d4\u662f\u60c5\u6df1\u4e49\u91cd\u7684\u4eba\uff0c\u8fd9\u575f \u91cc\u7684\u59d1\u5a18\u4e00\u5b9a\u662f\u4ed6\u5fc3\u4e2d\u6700\u7231\u7684\uff0c\u5509\uff01\u5434\u5927\u53d4\u90a3\u4e48\u82f1\u4fca\u6b63\u76f4\uff0c\u8001\u5929\u7237\u5374\u60e8\u9177\u7684\u628a\u75db\u82e6\u964d\u5728\u4ed6\u8eab \u4e0a\u3002\u771f\u662f\u4e0d\u516c\u5e73\uff0c\u4e0d\u516c\u5e73\u3002\u201d \u3000\u3000\u4ed6\u611f\u5230\u6709\u4e9b\u6fc0\u52a8\uff0c\u5750\u5728\u5893\u65c1\u60f3\uff0c\u975e\u5e38\u98d8\u5ffd\uff0c\u7a81\u7136\u4e00\u5bf9\u660e\u4eae\u7684\u5927\u773c\u6674\u4eff\u4f5b\u5728\u4ed6\u773c\u524d\u6d6e\u8d70\uff0c \u9ad8\u6218\u5fc3\u4e2d\u84e6\u7136\u751f\u51fa\u4e00\u79cd\u83ab\u540d\u5176\u5999\u7684\u5173\u5fc3\u548c\u4eb2\u5207\u3002 \u3000\u3000\u201c\u5979\u73b0\u5728\u4e0d\u77e5\u5728\u54ea\u91cc\uff0c\u90a3\u5929\u6211\u5306\u5306\u5fd9\u5fd9\u968f\u5434\u5927\u6743\u8dd1\u6389\uff0c\u4e5f\u6ca1\u6709\u591a\u77a7\u2026\u2026\u591a\u77a7\u5979\u4e00\u773c\u3002\u201d \u4ed6\u60f3\u5230\u6b64\uff0c\u8138\u4e0a\u5fae\u7ea2\u3002 \u3000\u3000\u201c\u5979\u662f\u4e00\u4e2a\u5f88\u597d\uff0c\u5f88\u597d\u7684\u5973\u5b69\u5b50\uff0c\u53ef\u662f\u5979\u7236\u4eb2\u5374\u662f\u4e00\u4e2a\u7eff\u6797\u5927\u76d7\uff0c\u5982\u679c\u5979\u7236\u4eb2\u662f\u4e2a\u4eba\u4eba \u656c\u4ef0\u7684\u5927\u4fa0\uff0c\u90a3\u53ef\u6709\u591a\u597d\u5440\uff01\u201d\u4ed6\u80e1\u601d\u4e71\u60f3\uff0c\u4e00\u5757\u6811\u53f6\u843d\u5728\u4ed6\u8138\u4e0a\uff0c\u6253\u65ad\u4e86\u9050\u601d\uff0c\u5fae\u60ca\u4e4b \u4e0b\uff0c\u4e0d\u89c9\u5931\u7b11\uff1a\u201c\u6211\u771f\u597d\u6ca1\u7531\u6765\uff0c\u5979\u7236\u4eb2\u662f\u4ec0\u4e48\u4eba\u5173\u6211\u4ec0\u4e48\u4e8b\u3002\u201d \u3000\u3000\u4ed6\u62ac\u5934\u4e00\u770b\u5929\u8272\uff0c\u4f9d\u7136\u662f\u9634\u6c89\u6c89\u7684\uff0c\u5c71\u91cc\u4e00\u7247\u5bc2\u9759\uff0c\u9ad8\u6218\u6709\u4e9b\u7d22\u7136\uff0c\u4e0d\u518d\u9017\u7559\uff0c\u4e0b\u5c71\u8fd4 \u5e97\u3002 \u3000\u3000\u4e14\u8bf4\u5434\u51cc\u98ce\u796d\u8fc7\u963f\u5170\u540e\uff0c\u5f98\u5f8a\u5728\u5893\u8fb9\uff0c\u60b2\u4e0d\u53ef\u6291\uff0c\u4ed6\u8fd9\u5341\u5e74\u6765\u966a\u7740\u4ed6\u5e08\u53d4\u7956\u4e1c\u5cb3\u4e66\u751f\u7075 \u51b0\u82e5\u5728\u6cf0\u5c71\u5e7d\u8c37\u4e2d\uff0c\u9664\u4e86\u7ec3\u6b66\uff0c\u5c31\u662f\u7cbe\u7814\u4f5b\u7406\uff0c\u4ed6\u5929\u8d44\u9896\u609f\u6de1\u6cca\uff0c\u5bf9\u4e8e\u4e00\u4e9b\u5927\u4e58\u4f5b\u7406\u90fd\u80fd\u9886 \u7565\uff0c\u53ef\u662f\u5bf9\u4e8e\u963f\u5170\u5fd7\u6b7b\u5374\u662f\u4e0d\u80fd\u91ca\u7136\uff0c\u6bcf\u4e00\u5ff5\u53ca\u3002\u5fc3\u4f24\u6b32\u65ad\uff0c\u6bcf\u5e74\u5230\u4e86\u963f\u5170\u81ea\u6740\u7684\u65e5\u5b50\uff0c\u4ed6 \u90fd\u5077\u5077\u5730\u4e0b\u6cf0\u5c71\uff0c\u5230\u957f\u5b89\u57ce\u5916\u963f\u5170\u5893\u4e0a\u56de\u5fc6\u6614\u65e5\u7684\u6e29\u99a8\uff0c\u966a\u4f34\u4e00\u4e0b\u6c38\u8fdc\u6d3b\u5728\u5fc3\u4e2d\u7684\u65e7\u4fa3\u3002 \u3000\u3000\u8fd9\u65e5\u4ed6\u5728\u5893\u4e0a\u5411\u963f\u5170\u503e\u8bc9\u81ea\u5df1\u7684\u75db\u82e6\uff0c\u660e\u77e5\u9ad8\u6218\u5728\u65c1\u5077\u542c\uff0c\u4f46\u4ed6\u4e00\u5fc3\u4e00\u610f\u6c89\u9189\u4e8e\u5f80\u4e8b\uff0c \u662f\u4ee5\u8d77\u521d\u5e76\u672a\u53eb\u7834\u3002\u540e\u6765\u53eb\u51fa\u9ad8\u6218\uff0c\u542c\u5230\u9ad8\u6218\u67d4\u58f0\u5b89\u6170\uff0c\u4ed6\u6b64\u65f6\u60c5\u611f\u4e4b\u5f26\u5df2\u7ecf\u8106\u5f31\u5230\u4e00\u89e6\u53ef \u65ad\uff0c\u95fb\u8a00\u773c\u6cea\u51e0\u4e4e\u6d41\u51fa\uff0c\u77e5\u9053\u6b64\u5904\u4e0d\u5b9c\u518d\u7559\uff0c\u4e3a\u514d\u88ab\u9ad8\u6218\u770b\u89c1\u81ea\u5df1\u7684\u6d41\u6cea\uff0c\u5c31\u98de\u5954\u800c\u53bb\u3002 \u3000\u3000\u4ed6\u8dd1\u4e86\u4e00\u9635\uff0c\u5fc3\u60c5\u7565\u7565\u5e73\u9759\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u8fd9\u5341\u5e74\u82e6\u4fee\u771f\u662f\u767d\u8d39\u4e86\uff0c\u6bcf\u5e74\u4e0b\u534a\u5e74\u6211\u8bfb\u4f5b\u7ecf \u8fdb\u5883\u751a\u5feb\uff0c\u5e76\u65e0\u6ede\u51dd\uff0c\u53ef\u662f\u4e00\u5230\u51ac\u672b\u6625\u521d\uff0c\u6211\u867d\u8eab\u5728\u6cf0\u5c71\uff0c\u53ef\u662f\u5fc3\u5374\u8001\u65e9\u8dd1\u5230\u957f\u5b89\u6765\uff0c\u8bfb\u8d77 \u7ecf\u6765\uff0c\u6ede\u800c\u4e0d\u901a\uff0c\u800c\u4e14\u8fd9\u60c5\u5f62\u6108\u6765\u6108\u662f\u663e\u8457\uff0c\u770b\u6765\u518d\u8fc7\u51e0\u5e74\uff0c\u6211\u5f97\u642c\u4e0b\u6cf0\u5c71\uff0c\u5230\u6b64\u5367\u591c\u76f8\u966a \u4e86\u3002\u201d \u3000\u3000\u4ed6\u8f6c\u5ff5\u53c8\u60f3\u9053\uff1a\u201c\u4e91\u7237\u7237\u8bf4\u8fc7\u771f\u7684\u75db\u82e6\u662f\u6c38\u8fdc\u4e0d\u4f1a\u5fd8\u6389\u7684\uff0c\u6c38\u8fdc\u65e0\u6cd5\u6bd4\u8f83\u7684\uff0c\u6211\u8fd9\u4e00\u751f \u65e2\u7136\u5fd8\u4e0d\u4e86\u8fc7\u53bb\u7684\u75db\u82e6\uff0c\u5728\u672a\u6765\u7684\u65e5\u5b50\u4f55\u4ee5\u81ea\u8651\u5462\uff1f\u5ff5\u7ecf\u5e76\u4e0d\u80fd\u51cf\u8f7b\u6211\u5fc3\u7075\u7684\u62c5\u8d1f\uff0c\u65f6\u95f4\u5e76 \u4e0d\u80fd\u51b2\u6de1\u6211\u7684\u8bb0\u5fc6\uff0c\u4f5b\u529d\u4eba\u628a\u751f\u6b7b\u54c0\u4e50\u90fd\u89c6\u505a\u98d8\u6d6e\u7684\u8f7b\u70df\uff0c\u53ef\u662f\u6211\u5374\u529e\u4e0d\u5230\uff0c\u4f5b\u95e8\u867d\u5e7f\uff0c\u770b \u6765\u4e5f\u6e21\u4e0d\u4e86\u6211\u8fd9\u65e0\u7f18\u7684\u4eba\u3002\u201d \u3000\u3000\u4ed6\u601d\u6f6e\u8d77\u4f0f\uff0c\u4e0d\u60f3\u8d70\u9519\u8def\u5934\uff0c\u8fdb\u5165\u4e1b\u5c71\u4e2d\uff0c\u4ed6\u89c1\u8def\u9014\u6108\u6765\u6108\u662f\u9669\u5cfb\uff0c\u4e5f\u4e0d\u5728\u610f\uff0c\u653e\u5f00\u811a \u6b65\uff0c\u5f80\u9ad8\u5904\u7a9c\u53bb\uff0c\u7ffb\u8fc7\u4e00\u5904\u5c71\u5761\uff0c\u53ea\u89c1\u5730\u52bf\u8c41\u7136\u5f00\u6717\uff0c\u4e00\u4f4d\u8305\u5c4b\u4f9d\u5761\u800c\u7acb\uff0c\u666f\u8272\u771f\u5982\u56fe\u753b\u4e00 \u822c\u3002 \u3000\u3000\u51cc\u98ce\u5fc3\u4e2d\u5927\u5947\uff0c\u6697\u5fd6\u9053\u7b49\u5730\u65b9\u4e5f\u6709\u4eba\u5bb6\uff0c\u591a\u534a\u662f\u9ad8\u4eba\u9690\u58eb\uff0c\u5c31\u8d70\u4e0a\u524d\u53bb\uff0c\u53ea\u542c\u5230\u4e00\u9635\u9635 \u7434\u58f0\u968f\u98ce\u4ece\u5c4b\u4e2d\u53d6\u51fa\uff0c\u97f3\u8c03\u94ff\u9535\uff0c\u5145\u6ee1\u4e86\u6b22\u4e50\u4e4b\u60c5\uff0c\u51cc\u98ce\u542c\u4e86\u4e00\u523b\uff0c\u77e5\u9053\u5728\u5f39\u4e00\u66f2\u201c\u4e4b\u5b50\u4e8e \u5f52\u201d\u3002\u5fc3\u60f3\uff1a\u201c\u8fd9\u4eba\u5fc3\u4e2d\u7684\u611f\u89c9\uff0c\u5b8c\u5168\u4ece\u7434\u58f0\u4e2d\u8868\u9732\u51fa\u6765\uff0c\u5c11\u7537\u5c11\u5973\u4e8e\u5f52\u4e4b\u559c\u771f\u662f\u4eba\u95f4\u4e4b\u5927 \u4e50\u3002\u6211\u4f55\u5fc5\u6253\u6270\u522b\u4eba\u7684\u6b22\u4e50\uff01\u201d \u3000\u3000\u4ed6\u6b63\u60f3\u79bb\u5f00\uff0c\u5ffd\u95fb\u7434\u58f0\u4e00\u53d8\uff0c\u5b9b\u5982\u79cb\u5c3d\u51ac\u6765\uff0c\u4e00\u7247\u8083\u6740\uff0c\u53c8\u5982\u5929\u6daf\u5b64\u5ba2\uff0c\u5bf9\u6708\u676f\u4e61\uff0c\u621b \u7136\u957f\u53f9\u3002 \u3000\u3000\u7a81\u7136\u7434\u58f0\u4e00\u6b62\uff0c\u4e00\u4e2a\u82cd\u8001\u7684\u58f0\u97f3\u53f9\u9053\uff1a\u201c\u65e0\u60c5\u6700\u662f\u6709\u60c5\uff0c\u2026\u2026\u82e5\u8bf4\u76f8\u601d\uff0c\u4f5b\u4e5f\u7709\u513f\u805a\uff0c \u82e5\u8bf4\u76f8\u601d\uff0c\u4f5b\u4e5f\u7709\u513f\u805a\u3002\u201d \u3000\u3000\u51cc\u98ce\u542c\u5f97\u4e00\u9707\uff0c\u60f3\u5230\u60c5\u7231\u7f20\u7ef5\u4e4b\u4e50\uff0c\u751f\u6b7b\u79bb\u522b\u4e4b\u82e6\uff0c\u4e0d\u7531\u5f97\u75f4\u4e86\uff0c\u5fc3\u4e2d\u53ea\u662f\u53cd\u590d\u56bc\u5473\u7740 \u90a3\u53e5\u8bdd\uff0c\u5341\u591a\u5e74\u6765\u803f\u803f\u4e8e\u80f8\u4e2d\u7684\u4e8b\u4f3c\u4e4e\u8f96\u7136\u800c\u901a\uff0c\u518d\u65e0\u7591\u4e49\uff0c\u53e3\u4e2d\u5583\u5583\u9053\uff1a\u201c\u60c5\u662f\u4f55\u7269\uff0c\u60c5 \u662f\u4f55\u7269\uff0c\u4f5b\u7956\u5e76\u6ca1\u6709\u53eb\u4eba\u4eec\u5fd8\u60c5\uff0c\u4ed6\u81ea\u5df1\u5c31\u662f\u601c\u4f17\u751f\u4e4b\u82e6\u800c\u727a\u7272\u4e00\u5df1\u4e4b\u5b89\u4e50\uff0c\u96be\u9053\u8fd9\u4e0d\u662f\u6709 \u60c5\u7684\u8868\u793a\uff1f\u6211\uff0c\u6211\u5230\u4e1c\u6d77\u5927\u6222\u5c9b\u53bb\u627e\u90a3\u5e73\u51e1\u5927\u5e08\u5243\u5ea6\u5f52\u4f9d\u5427\u3002\u201d \u3000\u3000\u4f46\u4ed6\u968f\u5373\u60f3\u9053\uff1a\u201c\u5e73\u51e1\u4e0a\u4eba\u65e0\u62d8\u65e0\u675f\uff0c\u4f55\u7b49\u81ea\u5728\uff0c\u4ed6\u8001\u4eba\u5bb6\u5929\u6027\u6062\u8c10\uff0c\u4e45\u5c45\u6d77\u5916\uff0c\u53ea\u6015 \u8fde\u5243\u5ea6\u89c4\u77e9\u90fd\u5fd8\u5149\u4e86\u3002\u6211\u6b64\u53bb\u627e\u4ed6\uff0c\u4e00\u5b9a\u4e0d\u5f97\u8981\u9886\uff0c\u5012\u4e0d\u5982\u5230\u5c11\u6797\u5bfa\u53bb\u3002\u201d \u3000\u3000\u4ed6\u76d8\u7b97\u5df2\u5b9a\uff0c\u80f8\u4e2d\u987f\u89c9\u5f00\u6717\uff0c\u5f80\u5d69\u5c71\u800c\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u9ad8\u6218\u5728\u957f\u5b89\u6e38\u89c8\u4e86\u51e0\u65e5\uff0c\u957f\u5b89 \u81ea\u53e4\u4ee5\u6765\u5c31\u662f\u5386\u4ee3\u7fa4\u738b\u5efa\u90fd\u4e4b\u5730\uff0c\u6587\u7269\u6c14\u52bf\u81ea\u662f\u4e0d\u51e1\uff0c\u9ad8\u6218\u8db3\u8ff9\u904d\u8e0f\u540d\u80dc\u53e4\u8ff9\uff0c\u5174\u81f4\u6781\u9ad8\u3002 \u3000\u3000\u8fd9\u65e5\u4ed6\u4ece\u90ca\u5916\u5f52\u6765\uff0c\u5df2\u662f\u5915\u9633\u897f\u4e0b\uff0c\u5a01\u9633\u53e4\u9053\u6765\u5f80\u4e4b\u4eba\u5982\u68ad\uff0c\u9ad8\u6218\u60f3\u8d85\u5143\u4eba\u66f2\u4e2d\u201c\u53e4 \u9053\u3001\u897f\u98ce\u3001\u7626\u9a6c\u201d\u3002\u5fc3\u60f3\u6b64\u60c5\u6b64\u666f\uff0c\u5012\u662f\u5341\u5206\u76f8\u4f3c\u3002 \u3000\u3000\u5ffd\u7136\u4e24\u5339\u9a8f\u9a6c\u8fce\u9762\u7f13\u6b65\u800c\u6765\uff0c\u9ad8\u6218\u4f46\u89c9\u773c\u524d\u4e00\u4eae\uff0c\u539f\u6765\u9a6c\u4e0a\u662f\u4e00\u7537\u4e00\u5973\uff0c\u90a3\u7537\u7684\u4e09\u65ec\u5de6 \u53f3\uff0c\u633a\u8eab\u9a91\u5728\u9a6c\u4e0a\uff0c\u82f1\u4fca\u975e\u51e1\uff0c\u773c\u4e2d\u9732\u51fa\u4e00\u79cd\u9ad8\u50b2\u795e\u8272\uff0c\u8eab\u65c1\u53e6\u9a91\u4e0a\u662f\u4f4d\u5168\u8eab\u96ea\u767d\u7f57\u8863\u7684\u59d1 \u5a18\uff0c\u4f53\u5f62\u7ea4\u5f31\uff0c\u7709\u76ee\u5982\u753b\uff0c\u4e0d\u65f6\u6307\u6307\u70b9\u70b9\u627e\u8bdd\u548c\u90a3\u9752\u5e74\u7537\u5b50\u8c08\u7b11\u3002 \u3000\u3000\u9ad8\u6218\u89c1\u8fd9\u5bf9\u7537\u5973\u54c1\u8c8c\u4fca\u96c5\uff0c\u4e0d\u7531\u591a\u671b\u4e86\u4e24\u773c\uff0c\u90a3\u7537\u7684\u4f3c\u4e4e\u89c9\u4e86\uff0c\u7709\u5934\u4e00\u76b1\uff0c\u5f85\u5230\u8d70\u8fd1\u9ad8 \u6218\uff0c\u53f3\u624b\u9a6c\u97ad\u6f2b\u4e0d\u7ecf\u610f\u4e00\u6325\uff0c\u76f4\u5411\u9ad8\u6218\u5288\u9762\u62bd\u6765\uff0c\u9ad8\u6218\u4e07\u4e07\u6599\u4e0d\u5230\u5bf9\u65b9\u7a81\u7136\u53d1\u96be\uff0c\u5934\u4e00\u504f\u4f38 \u624b\u53bb\u6293\u9a6c\u97ad\uff0c\u54ea\u77e5\u9a6c\u97ad\u7a81\u7136\u65b9\u5411\u4e00\u6539\uff0c\u671d\u9ad8\u6218\u53f3\u624b\u5377\u53bb\uff0c\u9ad8\u6218\u4e0d\u53ca\u8eb2\u907f\uff0c\u53f3\u624b\u5403\u4ed6\u9a6c\u97ad\u5377\u4e86 \u51e0\u5708\uff0c\u90a3\u9752\u5e74\u4e00\u7528\u52b2\u539f\u60f3\u628a\u9ad8\u6218\u5e26\u5230\u573a\u4e2d\uff0c\u8ba9\u4ed6\u6454\u4e00\u4e2a\u8ddf\u6597\uff0c\u60f3\u4e0d\u5230\u5bf9\u65b9\u7a33\u7a33\u7acb\u5728\u5730\u4e0a\uff0c\u5e76 \u672a\u88ab\u81ea\u5df1\u5377\u8d77\u5206\u6beb\uff0c\u4e0d\u7531\u5927\u6012\uff0c\u8fd0\u8d77\u5185\u52b2\uff0c\u53ea\u542c\u89c1\u5361\u5bdf\u4e00\u58f0\uff0c\u9a6c\u97ad\u9f50\u4e2d\u800c\u65ad\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u60f3\uff1a\u201c\u8fd9\u6837\u7684\u4eba\u54c1\uff0c\u600e\u4e48\u813e\u6c14\u98ce\u5ea6\u8fd9\u4e48\u574f\uff0c\u65e0\u7f18\u65e0\u6545\u5c31\u6253\u4eba\u3002\u201d \u3000\u3000\u90a3\u9752\u5e74\u4f3c\u4e4e\u8fd8\u60f3\u53d1\u5a01\uff0c\u9a6c\u4e0a\u7684\u59d1\u5a18\u8fde\u5fd9\u6447\u624b\uff0c\u4f4e\u58f0\u57cb\u6028\u9053\uff1a\u201c\u4f60\u4e00\u8def\u4e0a\u60f9\u4e8b\u8fd8\u4e0d\u591f\u591a \u4e48\uff1f\u54b1\u4eec\u5feb\u8d76\u4e00\u7a0b\uff0c\u5426\u5219\u4f60\u5e08\u7236\u53c8\u8981\u602a\u4f60\u8fdf\u5230\u5566\u3002\u201d \u3000\u3000\u58f0\u97f3\u6e29\u67d4\u60a6\u8033\u4e4b\u6781\uff0c\u9ad8\u6218\u7a81\u7136\u89c9\u5f97\u5728\u8fd9\u4e48\u67d4\u987a\u7684\u59d1\u5a18\u9762\u524d\u4e89\u5f3a\u6597\u72e0\uff0c\u771f\u662f\u5341\u5206\u4e0d\u6070\u5f53\u7684 \u4e8b\uff0c\u4fbf\u628a\u53f3\u624b\u6240\u6267\u534a\u622a\u9a6c\u97ad\u63b7\u56de\u9a6c\u4e0a\u9752\u5e74\uff0c\u8f6c\u8eab\u79bb\u53bb\u3002 \u3000\u3000\u90a3\u9752\u5e74\u5bf9\u4e8e\u59d1\u5a18\u7684\u8bdd\u5e76\u4e0d\u542c\u4ece\uff0c\u53e3\u4e2d\u7c97\u66b4\u9053\uff1a\u201c\u90a3\u5c0f\u5b50\u4e00\u53cc\u8d3c\u773c\u6e05\u6e9c\u6e9c\u7684\u4e71\u77a7\u4eba\uff0c\u4f60\u5f53 \u4ed6\u662f\u597d\u4eba\u4e48\uff1f\u4f60\u522b\u62a4\u7740\u4ed6\uff0c\u8ba9\u6211\u597d\u597d\u6559\u8bad\u4ed6\u4e00\u987f\u3002\u201d \u3000\u3000\u5c11\u5973\u67d4\u58f0\u9053\uff1a\u201c\u8fd9\u662f\u5927\u8def\u571f\uff0c\u4f60\u8981\u5927\u5bb6\u56f4\u4e0a\u6765\u770b\u5f69\u5417\uff1f\u597d\u5566\uff0c\u597d\u5566\uff0c\u542c\u6211\u8bdd\uff0c\u5f85\u4f1a\u6211\u5531 \u6b4c\u7ed9\u4f60\u542c\u3002\u201d \u3000\u3000\u9ad8\u6218\u672c\u5df2\u79bb\u53bb\uff0c\u542c\u4ed6\u4e00\u518d\u6076\u8a00\u76f8\u5bf9\uff0c\u800c\u4e14\u662f\u5f53\u7740\u5c11\u5973\u9762\u524d\u9a82\u81ea\u5df1\uff0c\u4ed6\u4e00\u5411\u5fc3\u5e73\u6c14\u548c\u7684\u5fc3 \u7530\uff0c\u7a81\u7136\u6124\u6012\u8d77\u6765\uff0c\u7acb\u6b65\u6012\u76ee\u800c\u89c6\u3002\u90a3\u9752\u5e74\u4e00\u5411\u662f\u5728\u5e08\u7236\u767e\u4f9d\u767e\u987a\u4e0b\u8fc7\u65e5\u5b50\uff0c\u4ece\u6765\u6ca1\u6709\u5403\u8fc7 \u534a\u4e1d\u82e6\u5934\uff0c\u60f3\u4e0d\u5230\u521d\u51fa\u6c5f\u6e56\uff0c\u5c31\u8fde\u4e00\u4e2a\u6bdb\u5934\u5c11\u5e74\u4e5f\u5948\u4e0d\u4f55\uff0c\u800c\u4e14\u9a6c\u97ad\u7ed9\u4ed6\u62c9\u65ad\uff0c\u771f\u65f6\u5947\u803b\u5927 \u8fb1\uff0c\u4ed6\u672c\u60f3\u5927\u8086\u53d1\u4f5c\uff0c\u4f46\u90a3\u5c11\u5973\u4e00\u518d\u76f8\u529d\uff0c\u53ea\u5f97\u5feb\u5feb\u606f\u6012\u3002\u6b64\u65f6\u4e00\u89c1\u9ad8\u6218\u7adf\u6562\u6012\u76ee\u4ee5\u5f85\uff0c\u4e0d \u7531\u6b63\u4e2d\u4e0b\u6000\uff0c\u4fa7\u8eab\u5bf9\u5c11\u5973\u9053\uff1a\u201c\u5e08\u59b9\uff0c\u8fd9\u5c0f\u5b50\u5206\u660e\u662f\u6765\u627e\u9ebb\u70e6\u7684\uff0c\u4f60\u522b\u62e6\u963b\u6211\uff0c\u8ba9\u6211\u8bd5\u8bd5\u5e08 \u7236\u7684\u65b0\u62db\u3002\u201d \u3000\u3000\u9ad8\u6218\u4e0a\u524d\u4e00\u6b65\uff0c\u51dd\u795e\u4fbf\u6b32\u63a5\u62db\uff0c\u5ffd\u7136\u4e00\u4e2a\u4eb2\u5207\u7684\u58f0\u97f3\u558a\u9053\uff1a\u201c\u5a03\u513f\uff0c\u522b\u548c\u8fd9\u79cd\u4e0d\u77e5\u597d\u6b79 \u7684\u4eba\u4e00\u822c\u89c1\u8bc6\u3002\u201d \u3000\u3000\u9ad8\u6218\u4e00\u56de\u5934\uff0c\u53ea\u89c1\u8eab\u540e\u7ad9\u7740\u4e00\u4e2a\u987b\u53d1\u7686\u76ee\u7684\u9ad8\u5927\u8001\u8005\uff0c\u5e84\u4e25\u4e2d\u900f\u51fa\u548c\u7766\uff0c\u4ee4\u4eba\u8083\u7136\u8d77 \u656c\uff0c\u9ad8\u6218\u89c9\u5f97\u751a\u662f\u9762\u719f\uff0c\u4f46\u4e00\u65f6\u4e4b\u95f4\uff0c\u7adf\u60f3\u4e0d\u51fa\u4ed6\u7a76\u7adf\u662f\u4f55\u4eba\u3002 \u3000\u3000\u8001\u4eba\u773c\u5149\u7a81\u7136\u79fb\u5230\u9a6c\u4e0a\u9752\u5e74\uff0c\u8138\u4e0a\u8868\u60c5\u4e0d\u5a01\u800c\u6012\uff0c\u90a3\u9752\u5e74\u867d\u5219\u72c2\u5984\u5f02\u5e38\uff0c\u4f46\u4f3c\u4e4e\u88ab\u8001\u4eba \u76ee\u5149\u6240\u6151\uff0c\u4e0d\u7531\u81ea\u4e3b\u4f4e\u4e0b\u5934\u6765\u3002\u8001\u4eba\u5ffd\u9053\uff1a\u201c\u4f60\u53bb\u544a\u8bc9\u4f60\u5e08\u7236\uff0c\u65b9\u5bb6\u7267\u573a\u5341\u6761\u4eba\u547d\uff0c\u4e10\u5e2e\u62a4 \u6cd5\u91d1\u8001\u88ab\u6253\u6210\u91cd\u4f24\uff0c\u8fd9\u4e24\u7b14\u5e10\u6211\u98ce\u67cf\u6768\u81ea\u4f1a\u627e\u4ed6\u6e05\u7b97\u3002\u201d \u3000\u3000\u90a3\u9752\u5e74\u6297\u58f0\u9053\uff1a\u201c\u5bb6\u5e08\u5c31\u662f\u8981\u89c1\u8bc6\u89c1\u8bc6\u98ce\u5927\u4fa0\u767e\u6b65\u795e\u62f3\u548c\u5148\u5929\u529f\uff0c\u662f\u4ee5\u51fa\u6b64\u4e0b\u7b56\u3002\u201d \u3000\u3000\u8001\u4eba\u559d\u9053\uff1a\u201c\u5c0f\u5b50\u65e0\u793c\uff01\u201d\u559d\u58f0\u65b9\u6bd5\uff0c\u4e5f\u4e0d\u89c1\u4ed6\u7528\u52b2\uff0c\u8eab\u5f62\u66b4\u7136\u62d4\u8d77\uff0c\u62cd\u62cd\u6253\u4e86\u9a6c\u4e0a\u9752 \u5e74\u4e24\u8bb0\u8033\u5149\u3002 \u3000\u3000\u90a3\u9752\u5e74\u81ea\u89c9\u5bf9\u7b54\u5f97\u4f53\uff0c\u6b63\u5728\u6d0b\u6d0b\u5f97\u610f\uff0c\u5ffd\u7136\u773c\u524d\u4e00\u638c\u6253\u6765\uff0c\u98d8\u5ffd\u5df2\u6781\uff0c\u4ed6\u8fde\u5fd9\u4e1c\u95ea\u897f \u8eb2\uff0c\u7adf\u7136\u6ca1\u6709\u8eb2\u8fc7\uff0c\u8fd8\u662f\u6328\u4e86\u4e24\u4e0b\uff0c\u8138\u4e0a\u7acb\u5373\u7ea2\u80bf\uff0c\u7259\u6839\u4e5f\u88ab\u6253\u677e\u3002 \u3000\u3000\u4ed6\u8eab\u65c1\u5c11\u5973\u5927\u611f\u4f24\u5fc3\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u5757\u4e1d\u5dfe\uff0c\u5c0f\u5fc3\u66ff\u4ed6\u62ed\u62b9\u8840\u8ff9\u67d4\u58f0\u5b89\u6170\uff0c\u4ed6\u7528\u624b\u4e00 \u683c\uff0c\u7c97\u58f0\u9053\uff1a\u201c\u4f60\u522b\u7ba1\u6211\uff0c\u6211\u548c\u8fd9\u8001\u8d3c\u62fc\u547d\u3002\u201d \u3000\u3000\u8001\u4eba\u6c89\u58f0\u9053\uff1a\u201c\u5c31\u51ed\u4f60\u4e00\u8def\u4e0a\u6a2a\u884c\u65e0\u793c\uff0c\u6b3a\u51cc\u5f31\u5c0f\uff0c\u5c31\u8be5\u597d\u597d\u7ed9\u4f60\u5403\u70b9\u82e6\u5934\uff0c\u8fd8\u4e0d\u5feb\u7ed9 \u6211\u8d70\u3002\u201d \u3000\u3000\u90a3\u9752\u5e74\u53e3\u4e2d\u867d\u7136\u5f3a\u786c\uff0c\u5fc3\u4e2d\u5374\u662f\u754f\u60e7\uff0c\u81ea\u5fd6\u5982\u679c\u518d\u4e0d\u8d70\u5f00\uff0c\u771f\u7684\u60f9\u6012\u5bf9\u65b9\uff0c\u82e6\u5934\u5403\u5f97\u66f4 \u5927\uff0c\u4f46\u662f\u8fd9\u756a\u53d7\u8fb1\uff0c\u80f8\u4e2d\u4e00\u53e3\u6076\u6c14\u4e07\u4e07\u5fcd\u5c06\u4e0d\u4e0b\uff0c\u9a82\u9053\uff1a\u201c\u4f60\u6709\u79cd\u5c31\u53bb\u627e\u6211\u5e08\u7236\u53bb\uff0c\u5728\u6211\u4eec \u540e\u8f88\u9762\u524d\u663e\u5a01\u98ce\uff0c\u7b97\u54ea\u4e00\u95f4\u597d\u6c49\uff1f\u201d \u3000\u3000\u98ce\u67cf\u6768\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u9a82\u5f97\u597d\uff0c\u9a82\u5f97\u597d\u3002\u7b97\u4f60\u6709\u79cd\uff0c\u6211\u8001\u4eba\u5bb6\u662f\u4f55\u7b49\u4eba\u7269\uff0c\u5c82\u80fd\u62ab\u4e0a\u4ee5\u5927 \u6b3a\u5c0f\u4e4b\u540d\uff0c\u597d\u5566\uff0c\u4f60\u5feb\u8d70\u5427\uff01\u201d \u3000\u3000\u90a3\u9752\u5e74\u539f\u662f\u4e0d\u987e\u6027\u547d\u7684\u9a82\u5c06\u51fa\u6765\uff0c\u6b64\u65f6\u4e00\u542c\u5bf9\u65b9\u53e3\u6c14\uff0c\u5e76\u672a\u53d1\u6012\uff0c\u5fc3\u4e2d\u5982\u91ca\u8d1f\u91cd\uff0c\u518d\u4e5f \u4e0d\u6562\u9017\u7559\uff0c\u4e00\u62cd\u9a6c\u548c\u5c11\u5973\u5982\u98de\u9a70\u53bb\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u84e6\u7136\u8bb0\u8d77\u8001\u8005\uff0c\u611f\u60c5\u5927\u4e3a\u6fc0\u52a8\uff0c\u62b1\u7740\u8001\u4eba\u6b22\u58f0\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u539f\u6765\u662f\u60a8\uff0c\u6218\u513f \u5929\u5929\u60f3\u4f60\u60f3\u5f97\u82e6\u5566\uff0c\u7239\u7239\u4e34\u7ec8\u65f6\u8fd8\u53eb\u6211\u627e\u60a8\u53bb\u5b66\u6b66\u529f\uff0c\u8001\u4f2f\u4f2f\uff0c\u4f60\u8fd9\u5341\u591a\u5e74\u5728\u54ea\u513f\u53bb\u4e86\uff0c\u8001 \u4f2f\u4f2f\u4f60\uff01\u4f60\u5934\u53d1\u66f4\u767d\u5566\uff0c\u6218\u513f\u51e0\u4e4e\u8ba4\u4e0d\u5f97\u4f60\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u81ea\u4ece\u7236\u4eb2\u6b7b\u540e\uff0c\u4e07\u91cc\u5b64\u8eab\u56de\u5230\u5bb6\u4e61\uff0c\u867d\u5219\u4eba\u4eba\u90fd\u5f85\u4ed6\u597d\uff0c\u4f46\u5230\u5e95\u6ca1\u6709\u9aa8\u8089\u81f3\u4eb2\u7684\u6e29 \u6696\uff0c\u8fd9\u65f6\u4e00\u89c1\u8001\u8005\u7adf\u662f\u5e7c\u5e74\u4f20\u6388\u81ea\u5df1\u5185\u529f\u7684\u4eba\uff0c\u771f\u5982\u6d6a\u5b50\u5728\u4ed6\u81f3\u7a81\u9022\u4eb2\u4eba\uff0c\u62b1\u7740\u8001\u8005\u8bed\u65e0\u4f26 \u6b21\u7684\u8bf4\u4e2a\u4e0d\u4f11\u3002 \u3000\u3000\u8001\u8005\uff08\u98ce\u67cf\u6768\uff09\u4f38\u624b\u629a\u7740\u9ad8\u6218\u7684\u5934\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u771f\u957f\u5927\u5566\u3002\u201d \u3000\u3000\u9ad8\u6218\u53ea\u662f\u8bb2\u7740\u8fd9\u5341\u591a\u5e74\u6765\u81ea\u5df1\u7684\u7ecf\u5386\uff0c\u8001\u8005\u542c\u4ed6\u8bf4\u5230\u9047\u7740\u201c\u5929\u715e\u661f\u541b\u201d\uff0c\u5e76\u4e14\u5b66\u4e86\u4e00\u5957 \u201c\u900f\u9aa8\u6253\u7a74\u201d\u7684\u624b\u6cd5\uff0c\u4e0d\u7531\u5927\u5403\u4e00\u60ca\uff0c\u95ee\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u53ef\u77e5\u6b64\u4eba\u662f\u4e2a\u6740\u4eba\u4e0d\u7728\u773c\u7684\u5927\u9b54\u5934 \u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u70b9\u70b9\u5934\u9053\uff1a\u201c\u6211\u77e5\u9053\u5927\u5bb6\u90fd\u6015\u4ed6\u5f97\u5f88\uff0c\u53ef\u662f\uff0c\u53ef\u662f\u4ed6\u5bf9\u6218\u513f\u5012\u662f\u4e0d\u574f\uff0c\u6211\u9a82\u4ed6\u80e1\u4e71 \u6740\u4eba\uff0c\u4ed6\u4e0d\u4f46\u6ca1\u6709\u6253\u6211\uff0c\u53cd\u800c\u6559\u4e86\u6211\u4e00\u5957\u529f\u592b\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u9053\uff1a\u201c\u4f60\u7684\u547d\u771f\u4e0d\u5c0f\uff0c\u5345\u591a\u5e74\u524d\u6211\u5728\u5173\u5916\u5c31\u542c\u8bf4\u5317\u65b9\u6709\u8fd9\u4e48\u4e00\u4e2a\u4eba\uff0c\u751f\u5e73\u4e0d\u5206\u5584 \u6076\uff0c\u529f\u592b\u53c8\u9ad8\uff0c\u53ea\u8981\u7a0d\u4e0d\u5982\u610f\u52a8\u624b\u5c31\u8981\u6740\u4eba\uff0c\u90a3\u65f6\u6211\u5c31\u60f3\u4f1a\u4f1a\u4ed6\u3002\u53ef\u662f\u4e00\u76f4\u6ca1\u6709\u673a\u4f1a\uff0c\u54fc\uff0c \u60f3\u4e0d\u5230\u8fd9\u53ae\u7adf\u6211\u4e0a\u95e8\u6765\uff0c\u5c06\u6211\u751f\u5e73\u597d\u53cb\u91d1\u8001\u5927\u6253\u6210\u91cd\u4f24\uff0c\u53c8\u5c06\u5173\u5916\u6700\u5927\u7684\u65b9\u5bb6\u7267\u573a\u4e3b\u767d\u5c71\u5251 \u5ba2\u65b9\u5e73\u5168\u5bb6\u5341\u53e3\u6740\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u7a81\u7136\u4e00\u4e2a\u5ff5\u5934\u95ea\u8d77\uff0c\u5fc3\u4e2d\u4e00\u75db\uff0c\u6025\u95ee\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u4f60\u8bf4\u7684\u662f\u771f\u7684\u4e48\uff1f\u90a3\u65b9\u2026\u2026\u65b9 \u8001\u4f2f\u662f\u5f88\u597d\u5f88\u597d\u7684\u4eba\u5440\uff0c\u6211\u2026\u2026\u6211\u8fd8\u7b54\u5e94\u8fc7\u5c06\u6765\u56de\u5173\u5916\u53bb\u770b\u4ed6\u7236\u5973\u7684\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u67d4\u58f0\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u8ba4\u5f97\u65b9\u5e73\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u6218\u60f3\u5230\u81ea\u5df1\u5728\u7edd\u5883\u4e2d\u8499\u4ed6\u7236\u5973\u8d60\u9001\u5e72\u7cae\u7684\u5f80\u4e8b\uff0c\u65b9\u8001\u4f2f\u6148\u7965\u5173\u5207\u7684\u8bdd\uff0c\u90a3\u5c0f\u5973\u5b69\u4e24\u53ea \u6643\u52a8\u7684\u5c0f\u8fab\u5b50\uff0c\u4e00\u65f6\u4e4b\u95f4\u90fd\u6d6e\u5728\u773c\u524d\uff0c\u4ed6\u672c\u662f\u4e2a\u60c5\u6df1\u4e49\u91cd\u7684\u4eba\uff0c\u60f3\u8d77\u65b9\u6c0f\u7236\u5973\u7684\u6069\u60e0\uff0c\u53ea\u89c9 \u70ed\u8840\u6cb8\u817e\uff0c\u4e0d\u80fd\u81ea\u5df1\u3002 \u3000\u3000\u98ce\u67cf\u6768\u89c1\u4ed6\u4e0d\u8a00\u8bed\uff0c\u8138\u4e0a\u6069\u6028\u8868\u60c5\u4e00\u95ea\u800c\u8fc7\uff0c\u53cc\u988a\u82cd\u767d\uff0c\u4e24\u773c\u55b7\u51fa\u706b\u82b1\uff0c\u6574\u4e2a\u4eba\u90fd\u53d8 \u4e86\uff0c\u6e29\u96c5\u5929\u771f\u90fd\u88ab\u4ec7\u6028\u6240\u4ee3\u66ff\uff0c\u98ce\u67cf\u6768\u770b\u4e86\u4e24\u773c\uff0c\u5fc3\u4e2d\u6781\u4e0d\u613f\u610f\u89c1\u4ed6\u53d1\u72e0\u7684\u8868\u60c5\uff0c\u5fd6\u9053\uff1a \u201c\u77a7\u4ed6\u75db\u5fc3\u8fd9\u4e2a\u6837\u5b50\uff0c\u4e00\u5b9a\u662f\u548c\u65b9\u5bb6\u5173\u7cfb\u5f88\u6df1\uff0c\u8fd9\u5b69\u5b50\u5929\u6027\u6781\u4e3a\u539a\u9053\uff0c\u4eba\u53c8\u806a\u660e\u7edd\u9876\uff0c\u5f02\u65e5 \u5fc5\u6210\u5927\u5668\u3002\u4ed6\u80f8\u4e2d\u5c3d\u662f\u7f8e\u597d\u7684\u5fc3\u601d\uff0c\u5982\u679c\u4ed6\u53d1\u89c9\u4e16\u754c\u4e0a\u7684\u7f6a\u6076\u662f\u8fd9\u4e48\u591a\uff0c\u90a3\u4e48\u4ed6\u5341\u5b9a\u4f1a\u4f24\u5fc3 \u5931\u671b\uff0c\u751a\u81f3\u4f1a\u653f\u53d8\u5584\u826f\u7684\u5929\u6027\uff0c\u6211\u8981\u5f00\u5bfc\u4ed6\u4e00\u756a\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u6b63\u60f3\u5f00\u53e3\uff0c\u5ffd\u89c1\u9ad8\u6218\u8138\u4e0a\u6124\u6012\u4e4b\u8272\u5c3d\u892a\uff0c\u4e24\u773c\u9eef\u6de1\u7684\u671b\u7740\u8fdc\u5904\uff0c\u795e\u8272\u5f88\u662f\u9893\u4e27\u3002 \u53e3\u4e2d\u53cd\u590d\u8bf4\u9053\uff1a\u201c\u5929\u715e\u661f\u541b\uff0c\u4f60\u9a97\u6211\uff0c\u4f60\u9a97\u4e86\u6211\uff0c\u4f60\u5e72\u5417\u8981\u6740\u6b7b\u6211\u65b9\u4f2f\u4f2f\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u89c1\u4ed6\u5931\u671b\u5df2\u6781\uff0c\u77e5\u9053\u4ed6\u5584\u826f\u5929\u6027\u6218\u80dc\u4e86\u6c79\u6d8c\u7684\u72e0\u6076\u5ff5\u5934\uff0c\u4e0d\u7531\u677e\u4e86\u53e3\u6c14\uff0c\u6b63\u8272 \u9053\uff1a\u201c\u5a03\u513f\uff0c\u662f\u975e\u5584\u6076\u4e4b\u95f4\u539f\u662f\u6781\u96be\u5206\u6e05\uff0c\u90a3\u5929\u715e\u661f\u541b\u5929\u6027\u672c\u6781\u504f\u6fc0\uff0c\u53c8\u52a0\u4e0a\u5f53\u5e74\u4e00\u6bb5\u4f24\u5fc3 \u5f80\u4e8b\uff0c\u662f\u4ee5\u517b\u6210\u4ed6\u75db\u6028\u5929\u4e0b\u4eba\u7684\u53d8\u6001\u5fc3\u7406\uff0c\u5a03\u513f\uff0c\u6b63\u90aa\u81ea\u53e4\u4e0d\u80fd\u5e76\u5b58\uff0c\u9664\u6076\u5373\u662f\u884c\u5584\uff0c\u4f60\u9664 \u53bb\u4e00\u4e2a\u6076\u4eba\uff0c\u4e5f\u8bb8\u5c31\u662f\u62ef\u6559\u4e86\u5343\u767e\u4e2a\u5584\u4eba\u3002\u201d \u3000\u3000\u4ed6\u8fd9\u756a\u8bdd\u8bf4\u5f97\u5927\u4e49\u51db\u7136\uff0c\u9ad8\u6218\u5fc3\u4e2d\u5927\u5927\u901a\u609f\uff0c\u539f\u6765\u9ad8\u6218\u5929\u771f\u65e0\u90aa\uff0c\u5fc3\u4e2d\u4e00\u76f4\u4ee5\u4e3a\u5929\u715e\u661f \u541b\u90a3\u591c\u5728\u53e4\u5e99\u4e2d\u63a5\u53d7\u4e86\u81ea\u5df1\u7684\u529d\u544a\uff0c\u60f3\u4e0d\u5230\u5929\u715e\u661f\u541b\u7adf\u7136\u53d8\u672c\u52a0\u5389\uff0c\u6740\u5bb3\u4e86\u81ea\u5df1\u7684\u6069\u4eba\uff0c\u4ed6 \u6709\u5982\u88ab\u4eba\u5f53\u5934\u91cd\u51fb\u4e00\u68d2\uff0c\u53c8\u5982\u53d7\u4eba\u6b3a\u9a97\u73a9\u5f04\uff0c\u80f8\u4e2d\u5148\u662f\u6012\u706b\u71c3\u70e7\uff0c\u63a5\u7740\u662f\u75db\u82e6\u5931\u671b\u3002 \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\u3002\u6218\u513f\u542c\u4f60\u7684\u8bdd\uff0c\u6211\u8ddf\u4f60\u53bb\u5b66\u529f\u592b\uff0c\u7b49\u5230\u6b66\u529f\u7ec3\u597d\uff0c\u4fbf\u53bb\u6253\u5929\u4e0b\u574f \u4eba\uff0c\u903c\u4ed6\u4eec\u90fd\u5b66\u597d\uff0c\u5982\u679c\u4ed6\u4eec\u8fd8\u8981\u574f\u4e0b\u53bb\uff0c\u6211\u5c31\u628a\u4ed6\u4eec\u6740\u2026\u2026\u6740\u6389\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u597d\u5b69\u5b50\uff0c\u8bf4\u5f97\u597d\uff0c\u54b1\u4eec\u5148\u627e\u4e2a\u5730\u65b9\u6b47\u6b47\uff0c\u6211\u8fd8\u6709\u4e8b\u8981\u544a\u8bc9\u4f60\u3002\u201d \u3000\u3000\u9ad8\u6545\u5c31\u548c\u98ce\u67cf\u6768\u8d70\u8fdb\u57ce\u5185\uff0c\u5230\u4e86\u9ad8\u6218\u6295\u5bbf\u7684\u5c0f\u5e97\uff0c\u4e24\u4eba\u5403\u5b8c\u665a\u996d\uff0c\u9ad8\u6218\u66ff\u98ce\u67cf\u6768\u5012\u4e86\u4e00 \u676f\u8336\uff0c\u8fd9\u4e00\u8001\u4e00\u5c0f\u5c31\u5728\u5ba4\u4e2d\u95f2\u804a\u3002 \u3000\u3000\u9ad8\u6218\u5ffd\u7136\u95ee\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u65b9\u5bb6\u7267\u573a\u88ab\u8001\u9b54\u7ed9\u6bc1\u4e86\uff0c\u96be\u9053\u5c31\u6ca1\u6709\u4e00\u4e2a\u4eba\u8fdb\u51fa\u5417\uff1f\u201d \u3000\u3000\u98ce\u67cf\u6768\u6c89\u91cd\u5730\u9053\uff1a\u201c\u53ea\u6709\u65b9\u573a\u4e3b\u7684\u72ec\u751f\u5973\u513f\u56e0\u4e3a\u56de\u5979\u5916\u5a46\u5bb6\uff0c\u4e8b\u53d8\u4e4b\u65e5\u4e0d\u5728\u573a\u5185\uff0c\u5f97\u4ee5 \u5e78\u514d\uff0c\u8fd9\u4e8b\u6211\u8001\u4eba\u5bb6\u5373\u5df2\u5305\u63fd\u4e0b\u6765\uff0c\u8fdf\u65e9\u8981\u8fd8\u4ed6\u4e00\u4e2a\u660e\u767d\u3002\u201d \u3000\u3000\u9ad8\u6218\u60f3\u5230\u5341\u5e74\u524d\u5728\u9014\u4e2d\u65b0\u9047\u7684\u5c0f\u5973\u5b69\uff0c\u90a3\u65f6\u662f\u591a\u4e48\u5a07\u61a8\u5e78\u798f\uff0c\u5982\u4eca\u5374\u6210\u4e3a\u5b64\u5973\uff0c\u90a3\u5c0f\u5973 \u5b69\u5631\u5490\u81ea\u5df2\u6709\u7a7a\u53bb\u770b\u5979\uff0c\u8a00\u72b9\u5728\u8033\uff0c\u53ef\u662f\u5982\u4eca\u5979\u5374\u5df2\u662f\u5bb6\u5ead\u7834\u788e\uff0c\u7236\u6bcd\u53cc\u4ea1\uff0c\u4e16\u4e8b\u7684\u53d8\u5e7b\uff0c \u771f\u662f\u592a\u5feb\u4e86\u5440\u3002 \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u65b9\u4f2f\u4f2f\u7684\u5973\u513f\u5fc3\u80a0\u771f\u597d\uff0c\u5979\u73b0\u5728\u5728\u54ea\u513f\uff0c\u6211\u7b54\u5e94\u8fc7\u8981\u53bb\u770b\u5979\u7684\u3002\u201d \u3000\u3000\u98ce\u4f2f\u6768\u9053\uff1a\u201c\u5979\u73b0\u5728\u8fd8\u5728\u5916\u5a46\u5bb6\uff0c\u5979\u5916\u7956\u7236\u662f\u5c71\u4e1c\u91d1\u5200\u8001\u5927\uff0c\u6614\u5e74\u9971\u662f\u5a01\u633a\u5317\u65b9\u7684\u6b66 \u5e08\uff0c\u5a03\u513f\uff0c\u4f60\u77e5\u9053\u6211\u5165\u5173\u6765\u5e72\u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u6447\u5934\uff0c\u98ce\u67cf\u6768\u53c8\u9053\uff1a\u201c\u5a03\u513f\uff0c\u90a3\u5e74\u4f60\u5728\u96ea\u5730\u91cc\u8bef\u98df\u5343\u5e74\u53c2\u738b\uff0c\u8981\u77e5\u9053\u8fd9\u79cd\u5929\u5730\u95f4\u7075 \u836f\uff0c\u6b63\u662f\u6b66\u6797\u4e2d\u4eba\u4eba\u68a6\u5bd0\u4ee5\u6c42\u7684\u4e1c\u897f\uff0c\u5982\u679c\u662f\u5185\u529f\u9ad8\u660e\u7684\u4eba\uff0c\u5403\u4e0b\u53bb\u8db3\u53ef\u62b5\u4e0a\u4e00\u7532\u5b50\u82e6\u4fee\u4e4b \u529f\u3002\u201d \u3000\u3000\u9ad8\u6218\u63d2\u53e3\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u6211\u4ece\u5c0f\u5c31\u5929\u5929\u7167\u7740\u60a8\u7ed9\u6211\u7684\u56fe\u4e0a\u6240\u8f7d\u53bb\u7ec3\uff0c\u60a8\u8bf4\u90a3\u5343\u5e74\u53c2\u738b\u53ef \u62b5\u4e00\u7532\u5b50\u529f\u529b\uff0c\u6211\u600e\u4e48\u88ab\u6d4e\u5357\u5927\u8c6a\u4e00\u4f38\u624b\u5c31\u6253\u8d25\u5462\uff1f\u201d \u3000\u3000\u98ce\u67cf\u6768\u9053\uff1a\u201c\u5a03\u513f\uff0c\u4f60\u6027\u5b50\u6e29\u539a\uff0c\u5fc3\u65e0\u65c1\u5ff5\uff0c\u6b63\u662f\u5b66\u4e60\u5185\u529f\u4e0a\u4e0a\u4eba\u6750\uff0c\u4f60\u4eca\u65e5\u6210\u5c31\u5df2\u7136 \u7edd\u4e0d\u5728\u6c5f\u6e56\u9ad8\u624b\u4e4b\u4e0b\uff0c\u53ea\u662f\u4f60\u5bf9\u6b66\u529f\u62db\u5f0f\u4e00\u6982\u4e0d\u61c2\uff0c\u6240\u4ee5\u4f1a\u88ab\u4eba\u4e00\u51fa\u624b\u5c31\u5236\u4f4f\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u63a5\u7740\u9053\uff1a\u201c\u6211\u5f53\u65f6\u4e00\u89c1\u5230\u4f60\uff0c\u771f\u662f\u5927\u5403\u4e00\u60ca\uff0c\u6211\u8d70\u904d\u5173\u5916\u4e5f\u4e0d\u89c1\u6709\u548c\u4f60\u6839\u9aa8\u4e00\u6837 \u597d\u7684\u4eba\uff0c\u6211\u751f\u5e73\u6536\u4e86\u4e24\u4e2a\u5f92\u513f\uff0c\u5374\u90fd\u662f\u4e2d\u4e0a\u4e4b\u8d28\uff0c\u540e\u6765\u53c8\u6559\u4e0d\u4e00\u4e2a\u8bb0\u540d\u5f1f\u5b50\u3002\u6b64\u4eba\u5929\u5206\u867d\u7136 \u8f83\u9ad8\uff0c\u53ef\u662f\u4e0e\u4f60\u4e00\u6bd4\uff0c\u5374\u662f\u5927\u5927\u4e0d\u5982\u3002\u201d \u3000\u3000\u9ad8\u6218\u542c\u98ce\u4f2f\u4f2f\u79f0\u8d5e\u81ea\u5df1\uff0c\u5f88\u611f\u4e0d\u597d\u610f\u601d\uff0c\u60f3\u8981\u8bf4\u4e00\u4e24\u53e5\u8c26\u8f9e\u7684\u8bdd\uff0c\u53ef\u662f\u60f3\u4e86\u534a\u5929\u4e5f\u60f3\u4e0d \u51fa\u6765\u3002 \u3000\u3000\u98ce\u67cf\u6768\u53c8\u9053\uff1a\u201c\u54b1\u4eec\u7ec3\u6b66\u529f\u7684\u6700\u6b22\u559c\u7684\u4e8b\u83ab\u8fc7\u4e8e\u80fd\u627e\u5230\u4e00\u4e2a\u806a\u660e\u542c\u8bdd\u7684\u5f92\u5f1f\uff0c\u6211\u627f\u7ee7\u7740 \u5173\u5916\u5929\u6c60\u6d3e\u4e00\u8109\uff0c\u79f0\u9738\u5173\u5916\u5345\u4f59\u5e74\uff0c\u53ef\u662f\u6211\u4e24\u4e2a\u5f92\u513f\uff0c\u53d7\u5929\u8d44\u6240\u9650\uff0c\u8fde\u6211\u4e00\u534a\u529f\u592b\u90fd\u5b66\u4e0d \u5230\u3002\u201d \u3000\u3000\u201c\u5f53\u65e5\u4f60\u8bef\u8ba4\u5343\u5e74\u53c2\u738b\u4e3a\u571f\u53c2\uff0c\u60f3\u66ff\u5c0f\u767d\u5154\u7597\u4f24\uff0c\u53ef\u662f\u53d7\u2018\u91d1\u6bdb\u795e\u733f\u2019\u4e00\u58f0\u5927\u543c\uff0c\u5413\u5f97 \u628a\u542b\u5728\u53e3\u4e2d\u53c2\u738b\u6d46\u6db2\u5168\u90e8\u54bd\u8fdb\u809a\u91cc\uff0c\u8fd9\u662f\u5929\u610f\uff0c\u8001\u5929\u7237\u8981\u6210\u5168\u4f60\uff0c\u4efb\u8c01\u4e5f\u65e0\u6cd5\u963b\u62e6\u3002\u201d \u3000\u3000\u9ad8\u6218\u542c\u4ed6\u5a13\u5a13\u8ffd\u7740\u513f\u65f6\u7684\u5f80\u4e8b\uff0c\u5fc3\u4e2d\u5f88\u611f\u6709\u8da3\uff0c\u7b11\u9053\uff1a\u201c\u662f\u554a\uff0c\u662f\u554a\uff0c\u8981\u4e0d\u662f\u91d1\u6bdb\u795e\u733f \u5927\u558a\u4e00\u58f0\uff0c\u6211\u53ef\u628a\u8fd9\u7075\u836f\u7ed9\u7cdf\u8e0f\u4e86\u3002\u201d \u3000\u3000\u98ce\u4f2f\u6768\u9053\uff1a\u201c\u6211\u5f53\u65f6\u56e0\u4e3a\u8eab\u6709\u6025\u4e8b\uff0c\u6b64\u4e8b\u5173\u7cfb\u7740\u6211\u8fb9\u5be8\u5927\u4fa0\u7684\u5a01\u540d\uff0c\u6211\u662f\u5929\u6c60\u6d3e\u638c\u95e8\uff0c \u5c82\u80fd\u8896\u624b\u4e0d\u7ba1\uff0c\u800c\u4e14\u5bf9\u65b9\u662f\u5929\u4e0b\u6570\u4e00\u6570\u4e8c\u7684\u5546\u624b\uff0c\u540d\u9707\u5bf0\u5b87\uff0c\u6211\u4e0d\u6562\u51b3\u5b9a\u6b64\u53bb\u80fd\u4e0d\u80fd\u5b89\u7136\u5f52 \u6765\uff0c\u6240\u4ee5\u6211\u51b3\u5b9a\u5148\u7ed9\u4f60\u4e00\u5377\u5185\u529f\u56fe\u89e3\uff0c\u8ba9\u4f60\u81ea\u5df1\u5148\u624e\u597d\u6839\u57fa\uff0c\u4ee5\u514d\u767d\u767d\u6d6a\u8d39\u7075\u836f\u529f\u6548\uff0c\u5982\u679c \u6211\u80fd\u5b89\u8eab\u8fd4\u56de\u5173\u5916\uff0c\u518d\u6765\u5bfb\u4f60\uff0c\u4f20\u6388\u6211\u5929\u6c60\u6d3e\u529f\u592b\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u8001\u4f2f\u4f2f\uff0c\u4f60\u5bf9\u624b\u6bd4\u5929\u715e\u661f\u541b\u6b66\u529f\u8fd8\u9ad8\u4e48\uff1f\u201d \u3000\u3000\u98ce\u67cf\u6768\u70f9\u70b9\u5934\u9053\uff1a\u201c\u5929\u715e\u661f\u541b\u529f\u529b\u867d\u9ad8\uff0c\u53ef\u662f\u6211\u8fd8\u6709\u628a\u63e1\u80fd\u591f\u5e94\u4ed8\uff0c\u53ef\u662f\u5bf9\u4e8e\u6b64\u4eba\uff0c\u5374 \u662f\u6beb\u65e0\u4fe1\u5fc3\u3002\u201d \u3000\u3000\u9ad8\u6218\u662f\u5c0f\u5b69\u5fc3\u6027\uff0c\u5fc3\u4e2d\u4e0d\u670d\uff0c\u5c31\u51b2\u53e3\u9053\uff1a\u201c\u96be\u9053\u662f\u4e09\u5934\u516d\u81c2\u4e4b\u4eba\uff1f\u5e08\u7236\uff0c\u4ed6\u53eb\u4ec0\u4e48 \u5440\uff1f\u201d \u3000\u3000\u8fb9\u5bdf\u5927\u4fa0\u98ce\u67cf\u6768\uff0c\u6298\u9ad8\u6218\u6539\u53e3\u53eb\u81ea\u5df1\u5e08\u7236\uff0c\u4e0d\u7981\u5927\u4e50\uff0c\u4e00\u629a\u957f\u987b\uff0c\u7b11\u9053\uff1a\u201c\u4e56\u5f92\u513f\uff0c\u95ee \u5f97\u597d\u3002\u201d \u3000\u3000\u539f\u6765\u4ed6\u5fc3\u4e2d\u7231\u6781\u9ad8\u6218\uff0c\u77e5\u9053\u9ad8\u6218\u5982\u679c\u62dc\u5728\u81ea\u5df1\u95e8\u4e0b\uff0c\u5c06\u6765\u6210\u5c31\u4e00\u5b9a\u5728\u81ea\u5df1\u4e4b\u4e0a\uff0c\u5173\u5916\u4e00 \u6d3e\u6b66\u672f\uff0c\u5c31\u8981\u9760\u4ed6\u53d1\u626c\u5149\u5927\u3002\u4ed6\u4e0d\u597d\u610f\u601d\u5f00\u53e3\u8bf4\u51fa\u8981\u6536\u9ad8\u6218\uff0c\u53ea\u6709\u4e00\u518d\u6697\u793a\uff0c\u4f46\u89c1\u9ad8\u6218\u4ecd\u7136 \u558a\u81ea\u5df1\u8001\u4f2f\u4f2f\uff0c\u5fc3\u4e2d\u4e0d\u7981\u5fae\u6025\uff0c\u5176\u5b9e\u4ed6\u54ea\u77e5\u9ad8\u6218\u5bf9\u4e8e\u6b66\u6797\u89c4\u77e9\u4e00\u7a8d\u4e0d\u901a\uff0c\u8ba4\u4e3a\u558a\u8001\u4f2f\u4f2f\u548c\u5e08 \u7236\u6839\u672c\u5c31\u6ca1\u6709\u5206\u522b\u3002 \u3000\u3000\u98ce\u67cf\u6768\u9053\uff1a\u201c\u5929\u4e0b\u6b66\u6797\u4e2d\u540d\u671b\u6700\u9ad8\u7684\u662f\u2018\u4e09\u4ed9\u2019\u2018\u4e8c\u541b\u2019\uff0c\u4e09\u4ed9\u662f\u5c45\u4e8e\u6d77\u5916\uff0c\u6210\u540d\u90fd\u5df2 \u5728\u4e00\u7532\u5b50\u4ee5\u4e0a\uff0c\u4e8c\u541b\u662f\u5173\u5185\u5357\u5317\u79f0\u96c4\uff0c\u4e0d\u8fc7\u4e3a\u5e08\u770b\u6765\uff0c\u4e8c\u541b\u540d\u5934\u867d\u5927\uff0c\u771f\u6b63\u672c\u9886\u5374\u5728\u4e09\u4ed9\u4e4b \u4e0b\uff0c\u4e09\u4ed9\u60a0\u6e38\u6d77\u5916\uff0c\u5f80\u5f80\u51e0\u5341\u5e74\u4e0d\u8986\u4e2d\u539f\uff0c\u53ef\u662f\u5076\u4e00\u9732\u9762\uff0c\u5fc5\u7136\u6709\u8d85\u4e16\u60ca\u4fd7\u7684\u8868\u73b0\uff0c\u662f\u4ee5\u6c5f \u6e56\u4e0a\u5bf9\u4e8e\u4e09\u4eba\u6108\u4f20\u6108\u662f\u795e\u5947\uff0c\u7adf\u6709\u4eba\u8ba4\u4e3a\u4ed6\u4eec\u5df2\u6210\u4e3a\u91d1\u521a\u4e0d\u574f\u4e4b\u8eab\uff0c\u957f\u751f\u4e0d\u6b7b\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u7565\u4e00\u505c\u6b47\uff0c\u559d\u4e86\u4e00\u53e3\u8336\uff0c\u9ad8\u6218\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u50ac\u4fc3\u9053\uff1a\u201c\u5e08\u7236\uff0c\u60a8\u8001\u4eba\u5bb6\u5728\u5173 \u5916\uff0c\u4ed6\u4eec\u4e09\u4ed9\u662f\u5728\u6d77\u4e0a\uff0c\u76f8\u9694\u51e0\u4e07\u91cc\uff0c\u600e\u4e48\u4f1a\u7ed3\u4ec7\u7684\u5440\uff1f\u201d \u3000\u3000\u98ce\u67cf\u6768\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6218\u513f\uff0c\u4f60\u5927\u5e08\u5144\u94c1\u9752\u539f\u827a\u6210\u4e4b\u540e\u4e00\u5411\u5728\u5173\u5916\u7ecf\u8425\u76ae\u8d27\uff0c\u4ed6\u4e3a\u4eba\u61a8 \u76f4\uff0c\u53e3\u4e0d\u906e\u8a00\uff0c\u5728\u4e00\u6b21\u9152\u540e\uff0c\u81ea\u79f0\u5173\u5916\u6b66\u529f\u5929\u4e0b\u65e0\u53cc\u3002\u4ed6\u540c\u4f19\u5546\u4eba\u4e5f\u4e00\u518d\u5439\u865a\uff0c\u5c31\u6fc0\u8d77\u548c\u4ed6 \u4eec\u4e00\u9f50\u559d\u9152\u7684\u5173\u4e2d\u9556\u5c40\u9556\u5934\u4eec\u4e0d\u6ee1\uff0c\u4f60\u5927\u5e08\u54e5\u8d77\u8eab\u548c\u4ed6\u4eec\u6253\u8d4c\uff0c\u626c\u8a00\u53ea\u7528\u53cc\u811a\uff0c\u5c31\u80fd\u628a\u4ed6\u4eec \u4e03\u516b\u4e2a\u9556\u5e08\u8e22\u7ffb\uff0c\u90a3\u4e9b\u597d\u4e8b\u7684\u4eba\u4e00\u518d\u51d1\u8da3\uff0c\u7ed3\u679c\u53cc\u65b9\u7ec8\u4e8e\u52a8\u624b\uff0c\u4f60\u5927\u5e08\u5144\u679c\u7136\u51ed\u7740\u6211\u5929\u6c60\u7edd \u6280\u2018\u8ff7\u8e2a\u811a\u6cd5\u2019\uff0c\u4e0d\u6570\u62db\u4fbf\u628a\u90a3\u4e9b\u8349\u5305\u9556\u5e08\u9521\u5012\u3002\u201d \u3000\u3000\u9ad8\u6218\u5927\u611f\u5174\u594b\uff0c\u53eb\u9053\uff1a\u201c\u5e08\u7236\uff0c\u5927\u5e08\u5144\u771f\u5a01\u98ce\u554a\uff01\u201d \u3000\u3000\u98ce\u67cf\u6768\u5fae\u5fae\u4e00\u7b11\uff1a\u9053\uff1a\u201c\u4f60\u5927\u5e08\u6b63\u5728\u6d0b\u6d0b\u5f97\u610f\uff0c\u7a81\u7136\u7eff\u5149\u4e00\u95ea\uff0c\u4e00\u4e2a\u5c0f\u5c0f\u7269\u4ef6\u76f4\u5411\u4ed6 \u2018\u4e91\u53f0\u2019\u7a74\u6253\u6765\uff0c\u6765\u52bf\u75be\u5982\u6d41\u661f\uff0c\u4f60\u5927\u5e08\u5144\u5411\u53f3\u4e00\u95ea\uff0c\u7adf\u6ca1\u95ea\u8fc7\uff0c\u536b\u4e2d\u53f3\u8170\u95f4\u201c\u7b11\u8f6f\u201d\u7a74\uff0c \u5f53\u65f6\u9178\u75d2\u96be\u71ac\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u54c8\u54c8\u72c2\u7b11\u8d77\u6765\u3002\u4ed6\u4f38\u624b\u4e00\u62d4\uff0c\u539f\u6765\u8170\u95f4\u6240\u4e2d\u7684\u7269\u4e8b\u7adf\u662f\u4e00\u6839\u677e \u9488\uff0c\u4f17\u4eba\u53ea\u9053\u4f60\u4e3a\u5e08\u54e5\u5f97\u610f\u5fd8\u5f62\uff0c\u5374\u4e0d\u77e5\u5df2\u7ecf\u906d\u5230\u6697\u7b97\u3002\u201d \u3000\u3000\u9ad8\u6218\u4e0d\u7981\u9a87\u7136\uff0c\u5fc3\u60f3\u4e00\u6839\u8f7b\u8f7b\u7684\u677e\u9488\uff0c\u7adf\u7136\u53ef\u4ee5\u5f53\u4f5c\u6697\u5668\u6765\u4f24\u4eba\uff0c\u6b64\u4eba\u529f\u529b\u53ea\u6015\u771f\u5728\u5929 \u715e\u661f\u541b\u4e4b\u4e0a\uff0c\u5f53\u4e0b\u4e0d\u5047\u601d\u7d22\u4fbf\u9053\uff1a\u201c\u5e08\u7236\uff0c\u6697\u7b97\u5e08\u54e5\u7684\u4eba\u662f\u60a8\u8bf4\u7684\u90a3\u4e2a\u5bf9\u5934\u4e86\uff1f\u201d \u3000\u3000\u98ce\u67cf\u6768\u70b9\u5934\u8d5e\u9053\uff1a\u201c\u5f92\u513f\uff0c\u4f60\u771f\u806a\u660e\uff0c\u4f60\u5927\u5e08\u54e5\u5f3a\u5fcd\u9178\u75db\uff0c\u81ea\u884c\u63a8\u5bab\u8fc7\u8840\uff0c\u5fd9\u4e86\u597d\u534a \u5929\uff0c\u603b\u7b97\u628a\u7a74\u9053\u89e3\u5f00\uff0c\u4e0a\u524d\u63a8\u5f00\u7a97\u5b50\u4e00\u770b\uff0c\u53ea\u89c1\u56db\u3001\u4e94\u4e08\u5916\u4e00\u68f5\u677e\u6811\u4e2d\uff0c\u7aef\u5750\u7740\u4e00\u4e2a\u4eba\u3002\u4f60 \u5e08\u5144\u7684\u813e\u6c14\u662f\u6709\u540d\u7684\u706b\u7206\uff0c\u660e\u77e5\u6b64\u4eba\u80fd\u5728\u56db\u4e94\u4e08\u5916\u628a\u4e00\u6839\u677e\u9488\u5f39\u51fa\uff0c\u800c\u4e14\u529b\u9053\u5f3a\u52b2\uff0c\u8ba4\u7a74\u5947 \u51c6\uff0c\u529f\u592b\u5df2\u7136\u51fa\u795e\u5165\u5316\uff0c\u4ed6\u81ea\u5df1\u771f\u662f\u4e07\u4e07\u4e0d\u53ca\uff0c\u53ef\u662f\u4ecd\u7136\u5fcd\u4e0d\u4f4f\u6c14\uff0c\u7834\u53e3\u6012\u9a82\u8d77\u6765\u3002\u90a3\u4eba\u5f85 \u4ed6\u9a82\u4e86\u51e0\u53e5\uff0c\u8eab\u5f62\u5fae\u52a8\uff0c\u98d8\u7136\u4e0b\u6811\u3002 \u3000\u3000\u201d\u4f60\u5927\u5e08\u54e5\u9a91\u864e\u96be\u4e0b\uff0c\u4e5f\u53ea\u6709\u786c\u7740\u5934\u76ae\uff0c\u8ddf\u4e86\u51fa\u53bb\uff0c\u90a3\u4eba\u4e5f\u4e0d\u8a00\u8bed\uff0c\u4e00\u811a\u4f38\u5411\u4f60\u5e08\u5144\u4e0b \u76d8\u8e22\u53bb\uff0c\u4f60\u5e08\u5144\u9000\u540e\u4e00\u6b65\uff0c\u54ea\u77e5\u4ed6\u7a81\u7136\u53d8\u8e22\u4e3a\u94a9\uff0c\u4f60\u5927\u5e08\u54e5\u5c31\u88ab\u4ed6\u5288\u9762\u62cc\u5012\u3002\u90a3\u4eba\u51b7\u51b7\u9053\uff1a \u2018\u5173\u5916\u5929\u6c60\u6b66\u529f\u4e0d\u8fc7\u5c14\u5c14\uff0c\u4f60\u8fd9\u53ae\u81ea\u5439\u811a\u6cd5\u5929\u4e0b\u65e0\u53cc\uff0c\u5176\u5b9e\u4e0b\u76d8\u5982\u6bd4\u7a00\u677e\uff0c\u8fd9\u6837\u770b\u6765\uff0c\u98ce\u8001 \u513f\u4e5f\u662f\u6d6a\u5f97\u865a\u540d\u4e4b\u8f88\uff0c\u6559\u51fa\u5982\u6b64\u8113\u80de\u3002\u2019\u201d \u3000\u3000\u9ad8\u6218\u53eb\u9053\uff1a\u201c\u4ed6\u6562\u4fae\u8fb1\u5e08\u7236\uff0c\u5f85\u6211\u5b66\u597d\u672c\u4e8b\uff0c\u4e00\u5b9a\u8981\u597d\u597d\u6253\u4ed6\u4e00\u987f\uff0c\u66ff\u60a8\u51fa\u6c14\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u7b11\u9053\uff1a\u201c\u4e56\u5f92\u513f\uff0c\u4f60\u6709\u8fd9\u756a\u5fd7\u6c14\uff0c\u5e08\u7236\u9ad8\u5174\u5f97\u5f88\u3002\u4f60\u5927\u5e08\u5144\u542c\u4ed6\u9a82\u6211\uff0c\u4e5f\u662f\u6c14\u6124 \u586b\u80f8\uff0c\u62fc\u51fa\u6027\u547d\u548c\u4ed6\u640f\u6597\uff0c\u4f46\u662f\u529f\u529b\u76f8\u5dee\u592a\u8fdc\uff0c\u4e0d\u6570\u62db\u53c8\u88ab\u70b9\u4e2d\u7a74\u9053\uff0c\u4ed6\u5f53\u4f17\u628a\u4f60\u5927\u5e08\u54e5\u7f9e \u8fb1\u4e00\u9635\uff0c\u518d\u89e3\u5f00\u4ed6\u7a74\u9053\uff0c\u51c6\u5907\u98d8\u7136\u800c\u53bb\uff0c\u4f60\u5927\u5e08\u53ef\u7a74\u9053\u4e00\u89e3\uff0c\u53c8\u5982\u4e00\u5934\u75af\u864e\u4e00\u822c\uff0c\u6ca1\u547d\u7684\u7f20 \u7740\u4ed6\u3002\u4ed6\u4e00\u6012\u4e4b\u4e0b\uff0c\u51fa\u624b\u6e10\u91cd\uff0c\u628a\u4f60\u5e08\u54e5\u9971\u6253\u4e00\u987f\uff0c\u8fde\u5934\u53d1\u90fd\u88ab\u4ed6\u6293\u53bb\u5927\u534a\u3002 \u3000\u3000\u201c\u90a3\u4eba\u4e34\u8d70\u65f6\u81ea\u88ab\u662f\u4e1c\u6d77\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u3001\u626c\u8a00\u4f60\u5e08\u54e5\u5982\u4e0d\u670d\u6c14\uff0c\u5c3d\u53ef\u56de\u53bb\u628a\u5e08\u7236\u8bf7 \u6765\uff0c\u4ed6\u5728\u65e0\u6781\u5c9b\u4e0a\u5019\u6559\u3002\u4f60\u5e08\u54e5\u53d7\u4e86\u8fd9\u5927\u4fae\u8fb1\uff0c\u81ea\u662f\u65e0\u6cd5\u5728\u6c5f\u6e56\u4e0a\u518d\u6df7\uff0c\u4fbf\u56de\u5230\u9526\u5dde\u5411\u6211\u54ed \u8bc9\uff0c\u6211\u77e5\u4f60\u5e08\u54e5\u8349\u5305\u813e\u6c14\uff0c\u4e00\u5b9a\u662f\u4ed6\u51fa\u8a00\u72c2\u5984\uff0c\u81ea\u53d6\u5176\u8fb1\uff0c\u5c31\u7f5a\u4ed6\u9762\u58c1\u4e09\u5e74\uff0c\u4e0d\u51c6\u5916\u53bb\uff0c\u91cd \u7ec3\u672c\u95e8\u5185\u529f\u3002\u6211\u751f\u5e73\u4ece\u672a\u8e0f\u5165\u5c71\u6d77\u5173\u534a\u6b65\uff0c\u53ef\u662f\u6b64\u4e8b\u975e\u540c\u5c0f\u53ef\uff0c\u5173\u7cfb\u7740\u6211\u5929\u6c60\u6d3e\u5a01\u671b\uff0c\u90a3\u65e0 \u6068\u751f\u65e2\u7136\u6307\u540d\u6311\u6218\uff0c\u4e3a\u5e08\u5982\u679c\u518d\u8981\u754f\u7f29\u4e0d\u524d\uff0c\u90a3\u4e48\u6211\u5929\u6c60\u6d3e\u6570\u767e\u5e74\u5728\u5173\u5916\u5a01\u540d\u3002\u5c31\u8981\u6bc1\u5728\u4e3a \u5e08\u624b\u4e2d\u3002\u201d \u3000\u3000\u9ad8\u6218\u63a5\u53e3\u9053\uff1a\u201c\u6240\u4ee5\u5e08\u7236\u56e0\u6b64\u6765\u4e0d\u53ca\u6559\u6211\uff0c\u53ea\u7559\u4e0b\u5185\u529f\u56fe\u8c31\uff0c\u5c31\u5355\u4eba\u5339\u9a6c\u5f80\u4e1c\u6d77\u53bb\u627e\u90a3 \u65e0\u6068\u751f\u8f83\u91cf\u4e86\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u70b9\u5934\u9053\uff1a\u201c\u4e3a\u5e08\u8001\u65e9\u77e5\u6b64\u4eba\u6210\u540d\u5df2\u4e45\uff0c\u529f\u529b\u76d6\u4e16\uff0c\u9a7b\u989c\u6709\u672f\uff0c\u5df2\u662f\u4ed9\u4f5b\u4e2d\u4eba\uff0c\u5374 \u6ca1\u60f3\u5230\u4ed6\u5bf9\u4e8e\u2018\u540d\u2019\u4e4b\u4e0a\u5b57\uff0c\u7adf\u7136\u4e5f\u53c2\u609f\u4e0d\u8fdb\uff0c\u540e\u6765\u6253\u542c\u5f97\u77e5\u4ed6\u56e0\u7231\u5973\u5931\u8e2a\uff0c\u51fa\u5c9b\u4e45\u5bfb\u4e0d \u83b7\uff0c\u5fc3\u4e2d\u70e6\u71e5\u3002\u6240\u4ee5\u4e00\u8def\u4e0a\u4e5f\u4e0d\u77e5\u9053\u6298\u8fb1\u4e86\u591a\u5c11\u6b66\u6797\u540c\u9053\uff0c\u51e0\u4e4e\u6380\u8d77\u4e2d\u539f\u6b66\u6797\u5927\u6ce2\u3002\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u4ed6\u5973\u513f\u79bb\u5bb6\u5916\u51fa\uff0c\u4ed6\u627e\u5bfb\u4e0d\u5230\u5c31\u62ff\u522b\u4eba\u51fa\u6c14\uff0c\u5e08\u7236\uff0c\u6211\u77a7\u4ed6\u548c\u5929\u715e\u661f\u541b\u4e00 \u6837\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u597d\u4eba\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u9053\uff1a\u201c\u65e0\u6068\u751f\u539f\u662f\u5bd2\u95e8\u4e66\u751f\uff0c\u4ed6\u8bfb\u9971\u7ecf\u4e66\uff0c\u5374\u662f\u8fde\u756a\u843d\u7b2c\uff0c\u591a\u6b21\u523a\u6fc0\u4e0b\u6027\u60c5\u81ea\u662f \u504f\u6fc0\uff0c\u4ed6\u5f53\u5e74\u4e00\u6012\u4e4b\u4e0b\u9a7e\u821f\u98d8\u6cca\u5927\u6d77\uff0c\u6765\u5230\u65e0\u6781\u5c9b\uff0c\u5de7\u98df\u4e00\u9897\u5343\u5e74\u6731\u679c\uff0c\u53c8\u5f97\u524d\u8f88\u9690\u4fa0\u9057\u4e0b \u795e\u529f\u79d8\u7b08\uff0c\u81ea\u6b64\u529f\u529b\u7a81\u98de\u731b\u8fdb\uff0c\u5bb9\u989c\u5e38\u8446\uff0c\u4e0e\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\uff0c\u5c0f\u6222\u5c9b\u4e3b\u6167\u5927\u5e08\u5e76\u79f0\u2018\u4e16 \u5916\u4e09\u4ed9\u2019\u3002\u201d \u3000\u3000\u9ad8\u6218\u542c\u5e08\u7236\u8bb2\u8d77\u6b66\u6797\u638c\u6545\uff0c\u89c9\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u60f3\u5230\u65e0\u6068\u751f\u53ea\u8eab\u98d8\u6cca\u6d77\u5916\uff0c\u7ec3\u6210\u7edd\u4e16\u6b66\u529f\uff0c \u5fc3\u4e2d\u5f88\u662f\u4f69\u670d\u4ed6\u7684\u6bc5\u529b\uff0c\u5bf9\u4e8e\u65e0\u6068\u751f\u6076\u611f\u5927\u51cf\uff0c\u5fd6\u9053\uff1a\u201c\u60f3\u90a3\u65e0\u6068\u751f\u5f53\u5e74\u4e00\u5b9a\u662f\u8003\u8bd5\u5931\u8d25\uff0c \u8eab\u4e0a\u53c8\u7a77\uff0c\u88ab\u903c\u5f97\u8d70\u5934\u65e0\u8def\uff0c\u8fd9\u624d\u5192\u9669\u51fa\u6d77\uff0c\u4f46\u662f\u7ec8\u51ed\u4ed6\u52c7\u6c14\u6bc5\u529b\uff0c\u6210\u5c31\u4e3a\u4e3e\u4e16\u95fb\u540d\u7684\u4eba\uff0c \u770b\u6765\u2018\u5929\u4e0b\u65e0\u96be\u4e8b\uff0c\u53ea\u6015\u6709\u5fc3\u4eba\u2019\u8fd9\u53e5\u8bdd\u662f\u4e0d\u9519\u7684\u3002\u6211\u9ad8\u6218\u867d\u7136\u7236\u6bcd\u65e9\u4ea1\uff0c\u53ef\u662f\u9047\u5230\u7684\u4eba\uff0c \u6ca1\u6709\u6301\u6211\u4e0d\u597d\u7684\uff0c\u5c31\u662f\u90a3\u5929\u7136\u661f\u541b\u8001\u9b54\u5934\u4e5f\u4e0d\u4f8b\u5916\u3002\u6211\u5728\u8fd9\u6837\u597d\u7684\u73af\u5883\u4e0b\uff0c\u5982\u679c\u4e0d\u80fd\u6210\u5c31\u4e00 \u756a\u5927\u4e8b\uff0c\u771f\u8db3\u6127\u5bf9\u7236\u6bcd\u751f\u6211\u4e00\u573a\u4e86\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u89c1\u9ad8\u6218\u8138\u4e0a\u7a81\u7136\u95ea\u8fc7\u632f\u594b\u7684\u4e4b\u8272\uff0c\u4f7f\u90a3\u672c\u5c31\u4fca\u7f8e\u7684\u8138\u4e0a\uff0c\u53c8\u591a\u6dfb\u4e86\u4e00\u4e1d\u82f1\u96c4\u672c \u8272\u3002\u4f7f\u6108\u770b\u6108\u7231\uff0c\u60c5\u4e0d\u81ea\u7981\u7684\u4f38\u624b\u6478\u6478\u9ad8\u6218\u7684\u8138\uff0c\u4ed6\u6b64\u65f6\u5bf9\u9ad8\u6218\u7684\u5fc3\u60c5\uff0c\u5df2\u7531\u4e25\u5e08\u5316\u4e3a\u6148 \u7236\uff0c\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u6211\u98ce\u67cf\u6768\u534a\u751f\u5b64\u82e6\uff0c\u65e0\u513f\u65e0\u5973\uff0c\u60f3\u4e0d\u5230\u5728\u8fd9\u5782\u66ae\u4e4b\u5e74\uff0c\u6536\u5f97\u5982\u6b64\u4e00\u4e2a\u4e56\u5de7 \u5f92\u513f\u3002\u201d \u3000\u3000\u9ad8\u6218\u751f\u542c\u5f97\u8d77\u52b2\uff0c\u89c1\u5e08\u7236\u7a81\u7136\u505c\u4f4f\u4e0d\u8bb2\uff0c\u6478\u7740\u81ea\u5df1\uff0c\u773c\u4e2d\u5c3d\u662f\u6148\u7231\uff0c\u4ed6\u6025\u4e8e\u8981\u542c\u4e0b\u53bb\uff0c \u50ac\u9053\uff1a\u201c\u5e08\u7236\uff0c\u540e\u6765\u600e\u6837\u4e86\uff1f\u201d \u3000\u3000\u98ce\u67cf\u6768\u5fae\u7b11\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u65e0\u6068\u751f\u6210\u540d\u4ee5\u540e\uff0c\u603b\u89c9\u81ea\u5df1\u5e74\u5e7c\u65f6\u6240\u53d7\u6298\u78e8\u592a\u591a\uff0c\u662f\u4ee5\u884c\u4e8b\u7387 \u6027\u800c\u4e3a\uff0c\u4f46\u6c42\u81ea\u5df1\u4e4b\u6240\u559c\uff0c\u4f46\u867d\u5982\u6b64\uff0c\u5bf9\u4e8e\u5584\u6076\u5206\u660e\uff0c\u72b9\u4e0d\u5931\u4e66\u751f\u672c\u8272\u3002\u201d \u3000\u3000\u201c\u4e3a\u5e08\u5165\u5173\u4ee5\u540e\uff0c\u5c31\u76f4\u5954\u4e1c\u6d77\u65e0\u6781\u5c9b\u53bb\uff0c\u54ea\u77e5\u4e00\u5230\u5c9b\u4e0a\uff0c\u5374\u6251\u4e86\u4e2a\u7a7a\uff0c\u90a3\u5929\u6068\u751f\u5df2\u4e0e\u5e73 \u51e1\u4e0a\u4eba\u3001\u6167\u5927\u5e08\u4e09\u4eba\u8054\u8882\u8d74\u5929\u7afa\uff0c\u5e94\u6052\u6cb3\u4e09\u4f5b\u4e4b\u7ea6\uff0c\u4f5c\u7b2c\u4e8c\u6b21\u534e\u5937\u4e4b\u4e89\u53bb\u4e86\u3002\u90a3\u65e5\u65e0\u6068\u751f\u7559 \u8a00\u77f3\u4e0a\uff0c\u4e3a\u5e08\u4e0a\u524d\u4ed4\u7ec6\u4e00\u770b\uff0c\u767b\u65f6\u51c9\u4e86\u534a\u622a\u3002\u201d \u3000\u3000\u9ad8\u6218\u6025\u95ee\u9053\uff1a\u201c\u600e\u6837\uff1f\u201d \u3000\u3000\u8fb9\u5be8\u5927\u4fa0\u9053\uff1a\u201c\u90a3\u77f3\u4e0a\u5b57\u8ff9\u662f\u65e0\u6068\u751f\u7528\u624b\u6307\u65bd\u5c55\u91d1\u521a\u6307\u5199\u7684\uff0c\u7528\u6307\u523b\u77f3\u539f\u672c\u4e0d\u96be\uff0c\u53ef\u662f \u90a3\u77f3\u4e0a\u5b57\u8ff9\u4e0d\u4f46\u80fd\u7b14\u8d70\u9f99\u86c7\uff0c\u800c\u4e14\u6bcf\u7b14\u6bcf\u5212\u6df1\u6d45\u5b8c\u5168\u4e00\u6837\uff0c\u8fd9\u5c31\u96be\u5230\u6781\u70b9\uff0c\u4e3a\u5e08\u81ea\u5fd6\u4e0d\u80fd\u529e \u5230\uff0c\u4fbf\u4e5f\u5c31\u7559\u4e66\u77f3\u4e0a\uff0c\u7ea6\u5b9a\u4e09\u5e74\u540e\u518d\u6765\u62dc\u8bbf\uff0c\u5c31\u8fd4\u56de\u8fbd\u4e1c\u82e6\u7ec3\u2018\u5148\u5929\u529f\u2019\uff0c\u60f3\u8981\u5728\u4e09\u5e74\u4e4b\u540e \u529f\u529b\u80fd\u4e0e\u4ed6\u76f8\u6297\u8861\u3002 \u3000\u3000\u4e09\u5e74\u4e4b\u540e\uff0c\u6211\u4f9d\u8a00\u53c8\u5230\u65e0\u6781\u5c9b\uff0c\u90a3\u65e0\u6839\u751f\u4e0e\u4ed6\u59bb\u5b50\u90fd\u5728\u5c9b\u4e0a\uff0c\u4e3a\u5e08\u8bf4\u660e\u6765\u610f\uff0c\u65e0\u6839\u751f\u4e5f \u4e0d\u591a\u8bf4\uff0c\u62db\u624b\u5c31\u5f15\u6211\u5230\u5c9b\u540e\u53bb\u3002\u539f\u6765\u65e0\u6781\u5c9b\u867d\u662f\u6d77\u5916\u8352\u5c9b\uff0c\u53ef\u662f\u98ce\u5149\u5982\u753b\uff0c\u4ed6\u8dd1\u5230\u4e00\u5ea7\u5c71\u5cf0 \u8fb9\u5bf9\u6211\u8bf4\u9053\uff1a\u4e45\u95fb\u5927\u4fa0\u2018\u767e\u6b65\u795e\u62f3\u2019\u53ca\u2018\u5148\u5929\u529f\u2019\u662f\u5173\u5916\u4e8c\u7edd\uff0c\u6b64\u5cf0\u5524\u505a\u96ea\u5cad\uff0c\u5cf0\u5dc5\u7ec8\u5e74\u79ef \u96ea\uff0c\u5c0f\u751f\u5c31\u5728\u5cf0\u4e0a\u9886\u6559\u5173\u5916\u6b66\u5b66\u3002 \u3000\u3000\u6211\u89c1\u4ed6\u6001\u5ea6\u50b2\u6162\uff0c\u5e76\u65e0\u4e1d\u6beb\u6000\u8c26\u4e4b\u610f\uff0c\u5fc3\u5185\u4e5f\u89c9\u751a\u6012\uff0c\u4fbf\u4e0d\u8ddf\u4ed6\u591a\u8bf4\uff0c\u968f\u7740\u4ed6\u76f4\u5f80\u7edd\u5cad \u7ffb\u53bb\uff0c\u65e0\u6068\u751f\u8f7b\u529f\u7aef\u7684\u9ad8\u5999\u5f97\u7d27\uff0c\u4e00\u8d77\u4e00\u843d\u5c31\u662f\u5341\u6765\u4e08\uff0c\u4e3a\u5e08\u5fc3\u60f3\u83ab\u8981\u5728\u672a\u6bd4\u5212\u524d\u5c31\u4e27\u4e86\u9510 \u6c14\uff0c\u4e00\u63d0\u6c14\u4e5f\u65bd\u5c55\u5e73\u6c99\u843d\u96c1\u8eab\u6cd5\uff0c\u4e0e\u4ed6\u9996\u5c3e\u800c\u884c\uff0c\u4ed6\u89c1\u59cb\u7ec8\u4e0d\u80fd\u4e22\u5f00\u6211\uff0c\u8eab\u5f62\u8d8a\u52a0\u8d8a\u5feb\uff0c\u4e0d \u6d88\u7247\u523b\uff0c\u90fd\u4e00\u9f50\u7ffb\u4e0a\u7edd\u5cad\u3002\u201d \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u4e3a\u5e08\u7684\u5e73\u6c99\u843d\u96c1\u8eab\u6cd5\u5df1\u4f7f\u81f3\u5341\u4e8c\u6210\uff0c\u59cb\u7ec8\u8db3\u548c\u4ed6\u4e0d\u5148\u4e0d\u540e\uff0c\u9996\u5c3e\u76f8\u884c\uff0c\u6765\u5230\u96ea \u5cad\uff0c\u5374\u89c1\u8fd9\u63d2\u5929\u62d4\u4e91\u3001\u5730\u52bf\u5947\u9669\u7684\u5c71\u5934\u4e0a\uff0c\u5374\u662f\u4e00\u4e2a\u65b9\u5706\u5f88\u5927\u7684\u5e73\u9876\uff0c\u5cf0\u9876\u4e0a\u79ef\u96ea\u6eb6\u6eb6\uff0c\u5230 \u548c\u5173\u5916\u7684\u98ce\u5149\u7565\u540c\u3002 \u3000\u3000\u90a3\u65e0\u6068\u751f\u6765\u5230\u9876\u4e0a\uff0c\u51b7\u51b7\u5bf9\u4e3a\u5e08\u9053\uff1a\u8fd9\u65b9\u5e73\u9876\uff0c\u5c1a\u8db3\u54b1\u4eec\u65bd\u5c55\u5427\uff1f \u3000\u3000\u6211\u5bf9\u4ed6\u8fd9\u79cd\u51b7\u6f20\u81ea\u9a84\u7684\u6001\u5ea6\u59cb\u7ec8\u4e0d\u80fd\u91ca\u7136\uff0c\u5f53\u4e0b\u70b9\u4e86\u70b9\u5934\uff0c\u5e76\u6ca1\u6709\u7b54\u8bdd\u3002 \u3000\u3000\u540e\u6765\u4e24\u4eba\u8bae\u5b9a\u5148\u5728\u62f3\u6cd5\u4e0a\u89c1\u4e2a\u8f93\u8d62\u3002\u90a3\u65e0\u6068\u751f\u679c\u662f\u8c6a\u52c7\uff0c\u7acb\u523b\u65bd\u51fa\u7edd\u9876\u529f\u592b\uff0c\u548c\u4e3a\u5e08\u5bf9 \u6298\u8d77\u6765\u3002 \u3000\u3000\u5f00\u5934\u6570\u62db\uff0c\u4e3a\u5e08\u6709\u610f\u91c7\u53d6\u786c\u78b0\u786c\u649e\u7684\u62db\u5f0f\uff0c\u4e0d\u7ba1\u4ed6\u62db\u5f0f\u53d8\u5316\u7684\u865a\u5b9e\uff0c\u9f13\u8db3\u5185\u529b\u4ee5\u767e\u6b65\u795e \u62f3\u4e00\u8fde\u5bf9\u4ed6\u865a\u649e\u4e09\u62f3\uff0c\u4ed6\u5012\u4e5f\u4e0d\u6562\u5927\u610f\uff0c\u5bf9\u6321\u4e86\u8fd9\u4e09\u5f0f\u3002 \u3000\u3000\u54b1\u4eec\u8fd9\u4e00\u76f8\u640f\uff0c\u6211\u5fc3\u4e2d\u6709\u6570\uff0c\u65e0\u6839\u751f\u5185\u529b\u9020\u8be3\u4e4b\u6df1\uff0c\u5b9e\u4e3a\u5e73\u751f\u6240\u4ec5\u89c1\u3002 \u3000\u3000\u800c\u65e0\u6068\u751f\u5374\u662f\u4e0d\u80af\u653e\u677e\u6bcf\u4e00\u4e2a\u673a\u4f1a\uff0c\u7d27\u63a5\u7740\u4fbf\u865a\u7a7a\u653b\u5411\u4e3a\u5e08\u4e00\u5341\u4e8c\u62db\uff0c\u4ed6\u8fd9\u4e00\u5341\u4e8c\u62db\u53ef \u771f\u975e\u540c\u5c0f\u53ef\uff0c\u4e43\u662f\u4ed6\u8fd1\u5e74\u6765\u65b9\u609f\u51fa\u7684\u638c\u6cd5\uff0c\u4e0d\u4f46\u5728\u5185\u529b\u5f3a\u649e\u65b9\u9762\uff0c\u5c31\u662f\u5728\u62db\u5f0f\u53d8\u5316\u65b9\u9762\uff0c\u4e5f \u65e0\u4e0d\u7cbe\u7ec6\u5165\u5fae\u3002 \u3000\u3000\u8fd9\u6837\u4e3a\u5e08\u7acb\u523b\u548c\u4ed6\u4ee5\u5feb\u640f\u5feb\uff0c\u77ac\u606f\u95f4\u4fbf\u6298\u4e86\u4e00\u767e\u591a\u5f0f\u3002\u84e6\u7136\uff0c\u4ed6\u632f\u58f0\u957f\u5578\uff0c\u624b\u4e0a\u653b\u52bf\u9661 \u7684\u4e00\u632b\u3002 \u3000\u3000\u4e3a\u5e08\u5f53\u65f6\u8eab\u5f62\u59cb\u7ec8\u548c\u4ed6\u76f8\u8ddd\u56db\u4e94\u5c3a\uff0c\u867d\u8bf4\u662f\u4ee5\u5feb\u640f\u5feb\uff0c\u4f46\u7528\u4ee5\u653b\u654c\u7684\u5374\u90fd\u662f\u767e\u6b65\u79cd\u62f3\u4e4b \u7c7b\uff0c\u865a\u7a7a\u5bf9\u638c\u3002 \u3000\u3000\u4ed6\u653b\u52bf\u4e00\u632b\uff0c\u4e3a\u5e08\u795e\u62f3\u4e4b\u5f0f\u5927\u9707\uff0c\u4e00\u8fde\u8001\u62f3\uff0c\u9f3b\u5f62\u5df1\u6b3a\u8fd1\u7ea6\u6709\u4e09\u5c3a\uff0c\u65e0\u6068\u751f\u5374\u4e0d\u8fdb\u4e0d \u9000\uff0c\u53cc\u624b\u5408\u62b1\u6709\u5982\u592a\u6781\uff0c\u5ffd\u5730\u4e00\u5408\uff0c\u4f46\u95fb\u547c\u7684\u4e00\u58f0\uff0c\u8fd9\u4e00\u5f0f\u597d\u751f\u5947\u602a\uff0c\u5185\u529b\u4ece\u8fd9\u4e00\u5408\u4e4b\u5f0f\uff0c \u6089\u6570\u6d8c\u51fa\uff0c\u52b2\u98ce\u6fc0\u8361\u5904\uff0c\u7adf\u53d1\u51fa\u98ce\u96f7\u4e4b\u58f0\u3002 \u3000\u3000\u4e3a\u5e08\u4e07\u6599\u4e0d\u5230\u8fd9\u65e0\u6068\u751f\u7684\u5185\u529b\u679c\u7136\u5df2\u8fbe\u2018\u7389\u7384\u5f52\u771f\u2019\u7684\u5730\u6b65\uff0c\u767e\u5fd9\u4e2d\uff0c\u795e\u62f3\u9661\u8d70\u9669\u62db\uff0c \u4e00\u4e0a\u4e00\u4e0b\uff0c\u4f7f\u51fa\u6211\u6709\u591a\u5e74\u4e0d\u7528\u7684\u6740\u624b\u62db\u5f0f\uff1a\u2018\u5954\u7535\u5165\u96f7\u2019\u6765\u3002 \u3000\u3000\u8fd9\u4e00\u5f0f\u4e00\u51fa\uff0c\u4e3a\u5e08\u5185\u529b\u4ed8\u4e4b\u4e00\u63b7\uff0c\u5934\u4e0a\u987b\u53d1\u9f50\u4e3e\uff0c\u5185\u529b\u6c89\u91cd\u5982\u5c71\uff0c\u540c\u65f6\u95f4\u91cc\uff0c\u8eab\u5f62\u5374\u5f27 \u5f62\u540e\u9000\uff0c\u4e3a\u7684\u662f\u7559\u4e0b\u9000\u8def\u3002 \u3000\u3000\u4e00\u89e6\u4e4b\u4e0b\uff0c\u65e0\u6839\u751f\u8eab\u5f62\u4e00\u9707\uff0c\u4e3a\u5e08\u540e\u9000\u8eab\u5f62\u4e5f\u662f\u4e00\u8dcb\uff0c\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u65e0\u6068\u751f\u53cc\u8db3\u98de \u8d77\uff0c\u5de6\u53f3\u8fde\u73af\uff0c\u4e00\u8fde\u8e22\u51fa\u4e03\u516b\u811a\u4e4b\u591a\u3002 \u3000\u3000\u597d\u5728\u4e3a\u5e08\u5148\u524d\u4fbf\u7559\u597d\u9000\u8def\uff0c\u5de6\u53f3\u95ea\u8361\uff0c\u518d\u52a0\u4e0a\u624b\u4e0a\u5185\u529b\u4e0d\u65ad\u75be\u6d8c\u800c\u51fa\uff0c\u624d\u5c06\u65e0\u6068\u751f\u8fd9\u60ca \u5929\u52a8\u5730\u7684\u8fde\u73af\u653b\u52bf\u963b\u4e0b\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u98ce\u67cf\u6768\u795e\u60c5\u4f3c\u4e4e\u4e00\u9707\uff0c\u58f0\u6d6a\u4e5f\u63d0\u9ad8\u597d\u591a\uff0c\u60f3\u662f\u8bf4\u5230\u7d27\u8981\u4e4b\u5904\uff0c\u89e6\u52a8\u8c6a\u6c14\uff0c\u76ee \u4e2d\u795e\u5149\u66b4\u5c04\uff0c\u77a5\u4e86\u9ad8\u6218\u4e00\u773c\uff0c\u4f46\u89c1\u4ed6\u4e13\u5fc3\u7684\u8046\u542c\u7740\uff0c\u8138\u4e0a\u6d41\u9732\u51fa\u4f3c\u4e4e\u662f\u4e3a\u5e08\u7236\u907f\u8fc7\u7684\u8fd9\u4e00\u9669 \u62db\u800c\u653e\u5fc3\u7684\u8868\u60c5\uff0c\u4e0d\u7531\u5fae\u5fae\u4e00\u7b11\uff0c\u957f\u5438\u4e00\u53e3\u6c14\uff0c\u53c8\u9053\uff1a\u201c\u65e0\u6068\u751f\u89c1\u4e3a\u5e08\u5316\u89e3\u5f00\u4ed6\u75af\u72c2\u7684\u653b \u52bf\uff0c\u4e0d\u7531\u4e3a\u4e4b\u4e00\u6014\u3002\u4e3a\u5e08\u5fc3\u5fd6\u81ea\u5df1\u4e00\u76f4\u5904\u4e8e\u88ab\u52a8\u5730\u4f4d\uff0c\u4e0d\u7531\u96c4\u5fc3\u52c3\u53d1\uff0c\u53cc\u638c\u4e00\u5408\uff0c\u865a\u7a7a\u63a8 \u51fa\uff0c\u968f\u7740\u53cc\u62f3\u4ea4\u9519\u98a4\u52a8\u4e4b\u4e0b\u5df2\u7528\u51fa\u4e8e\u591a\u4e2a\u7edd\u62db\uff0c\u62db\u62db\u88ad\u5411\u4ed6\u80f8\u8179\u5404\u5927\u8981\u7a74\uff0c\u65e0\u6068\u751f\u8eab\u5f62\u5de6\u53f3 \u4e00\u6643\uff0c\u84e6\u7136\u540e\u9000\u4e00\u6b65\uff0c\u53f3\u624b\u540c\u65f6\u4e00\u62c2\uff0c\u95ea\u7535\u822c\u4e00\u6c89\u4e00\u6293\uff0c\u7adf\u4fbf\u4ed6\u540d\u9707\u5929\u4e0b\u7684\u201c\u62c2\u7a74\u201d\u624b\u6cd5\u6765 \u7834\u89e3\u4e3a\u5e08\u7684\u653b\u52bf\uff0c\u6570\u62db\u8fc7\u540e\uff0c\u4ed6\u540e\u9000\u6570\u6b65\uff0c\u957f\u58f0\u9053\uff1a\u6682\u4f4f\uff01\u6682\u4f4f\uff01\u5173\u5916\u6b66\u5b66\u679c\u662f\u4e0d\u51e1\uff0c\u767e\u6b65 \u795e\u62f3\u5df2\u9886\u6559\u8fc7\u2026\u2026 \u3000\u3000\u4ed6\u8bf4\u5230\u8fd9\u513f\uff0c\u5fae\u5fae\u4e00\u987f\u3002\u4e3a\u5e08\u53ef\u77e5\u9053\u4ed6\u4e43\u662f\u56e0\u4e3a\u5e73\u751f\u7edd\u62db\u4ecd\u5948\u4f55\u4e0d\u4e86\u4e3a\u5e08\uff0c\u662f\u4ee5\u81ea\u77e5\u62f3 \u62db\u4e0a\u80dc\u8d1f\u7edd\u975e\u77ed\u95f4\u53ef\u8fa8\uff0c\u540c\u65f6\u4e3a\u5e08\u4e5f\u6df1\u77e5\u8981\u80dc\u5f97\u8fd9\u65e0\u6068\u751f\uff0c\u4e5f\u4e07\u4e07\u4e0d\u6613\u3002 \u3000\u3000\u662f\u4ee5\u63a5\u53e3\u9053\uff1a\u81ea\u53e4\u9053\u2018\u55a7\u5bbe\u4e0d\u593a\u4e3b\u2019\uff0c\u5f20\u5927\u4fa0\u5c3d\u7ba1\u5212\u4e0b\u9053\u6765\uff0c\u8001\u673d\u65e0\u4e0d\u5949\u966a\u3002 \u3000\u3000\u65e0\u6068\u751f\u7565\u4e00\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u5c0f\u751f\u4e0d\u81ea\u91cf\u529b\uff0c\u613f\u8ba8\u6559\u98ce\u5927\u4fa0\u7684\u2018\u5148\u5929\u6c14\u529f\u2019\u2026\u2026 \u3000\u3000\u6211\u957f\u7b11\u4e00\u58f0\uff0c\u7b54\u9053\uff1a\u597d\u8bf4\uff01\u597d\u8bf4\uff01 \u3000\u3000\u540c\u65f6\u6211\u5fc3\u4e2d\u4e5f\u5fd6\u9053\uff1a\u5185\u529b\u7684\u6bd4\u6b66\uff0c\u4e0d\u53ef\u6709\u4e00\u4e1d\u4e00\u6beb\u7684\u53d6\u5de7\uff0c\u4eca\u65e5\u98ce\u67cf\u6768\u4e00\u4e16\u6210\u540d\uff0c\u4fbf\u8981 \u51ed\u6b64\u5e7f\u6218\u4e86\u3002 \u3000\u3000\u8fd9\u6b21\u53c8\u662f\u90a3\u5929\u6068\u751f\u5148\u51fa\u624b\uff0c\u4ed6\u8eab\u5f62\u817e\u7a7a\uff0c\u53f3\u624b\u4e00\u638c\u5370\u6765\u3002\u4e3a\u5e08\u4ece\u4ed6\u8eab\u5f62\u4e0a\u770b\u6765\uff0c\u4fbf\u77e5\u5bf9 \u65b9\u5185\u529b\u4e1a\u5df2\u53d1\u52a8\uff0c\u5de6\u638c\u865a\u7a7a\u800c\u7acb\uff0c\u53f3\u62f3\u4e00\u7ffb\uff0c\u731b\u543c\u4e00\u58f0\uff0c\u4e00\u5f0f\u2018\u7384\u7b26\u6025\u63b7\u2019\uff0c\u659c\u6363\u800c\u51fa\uff0c\u7528 \u7684\u5374\u662f\u2018\u4e91\u69cc\u2019\u529b\u9053\uff0c\u4e09\u5206\u53d1\uff0c\u4e03\u5206\u6536\u3002 \u3000\u3000\u679c\u7136\uff0c\u4e00\u89e6\u4e4b\u4e0b\uff0c\u65e0\u6839\u751f\u731b\u7136\u4e00\u5e26\uff0c\u4e3a\u5e08\u2018\u4e91\u7cd9\u2019\u529b\u9053\u4e5f\u81ea\u53d1\u52a8\uff0c\u2018\u547c\u2019\u7684\u4e00\u58f0\uff0c\u4e24\u4eba \u8eab\u5f62\u5404\u81ea\u5e73\u5e73\u540e\u79fb\u5f00\u53bb\uff0c\u540c\u65f6\u624b\u4e0a\u539f\u5f0f\u4e0d\u53d8\uff0c\u5df1\u8f83\u4e0a\u4e86\u5185\u52b2\u3002 \u3000\u3000\u77a7\u90a3\u65e0\u6068\u751f\uff0c\u539f\u6765\u7acb\u8eab\u7684\u96ea\u5730\u4e0a\uff0c\u767d\u7691\u7691\u7684\u4e00\u7247\uff0c\u7adf\u8fde\u4e00\u4e1d\u4e00\u6beb\u7684\u5370\u513f\u4e5f\u6ca1\u6709\u7559\u4e0b\uff0c\u987b \u77e5\u4ed6\u8fd9\u4e00\u638c\u53d1\u51fa\u6015\u6709\u5343\u65a4\u4e4b\u529b\uff0c\u7acb\u8db3\u4e4b\u5730\u5374\u8fde\u5370\u513f\u4e5f\u6ca1\u7559\u4e0b\u3002\u6b64\u4eba\u7684\u5185\u529b\u9020\u8be3\u53ef\u771f\u6df1\u4e0d\u53ef\u6d4b \u4e86\u3002 \u3000\u3000\u4e3a\u5e08\u6025\u5fd9\u4fef\u773c\u5bdf\u770b\u81ea\u5df1\u7acb\u8db3\u7684\u5730\u4e0a\uff0c\u5374\u4e5f\u672a\u66fe\u7559\u4e0b\u4e00\u4e1d\u6de1\u6de1\u7684\u5370\u75d5\uff01\u5fc3\u4e2d\u4e00\u5bbd\uff0c\u624b\u4e0a\u4e0d \u5374\u6562\u5927\u610f\u4e1d\u6beb\uff0c\u5168\u529b\u50ac\u7528\u5185\u5bb6\u529b\u9053\u5bf9\u654c\u3002 \u3000\u3000\u50f5\u6301\u4e86\u4e00\u4f1a\uff0c\u4e3a\u5e08\u84e6\u7136\u5fc3\u751f\u4e00\u8ba1\uff0c\u5185\u529b\u9661\u7136\u4e00\u677e\uff0c\u65e0\u6068\u751f\u91cd\u82e5\u5c71\u5cb3\u7684\u638c\u529b\u7acb\u523b\u53cd\u51fb\u56de \u6765\u3002 \u3000\u3000\u4e3a\u5e08\u731b\u559d\u4e00\u58f0\uff0c\u53f3\u624b\u4e00\u6c89\u4e00\u9707\uff0c\u638c\u529b\u52a0\u4e0a\u8fd9\u4e00\u51b2\u4e4b\u529b\uff0c\u65e0\u6068\u751f\u652f\u6301\u4e0d\u4e86\uff0c\u8eab\u5f62\u4e0d\u7531\u79bb\u5730 \u800c\u8d77\uff0c\u547c\u7684\u5411\u540e\u63a0\u53bb\u3002 \u3000\u3000\u65e0\u6068\u751f\u5403\u4e86\u4e00\u60ca\uff0c\u767e\u5fd9\u4e4b\u4e2d\uff0c\u5de6\u624b\u4e00\u5e26\uff0c\u8eab\u5f62\u547c\u7684\u4e00\u58f0\uff0c\u53c8\u63a0\u56de\u539f\u6765\u505c\u8db3\u4e4b\u5730\uff0c\u4e3a\u5e08\u4e00 \u62db\u5f97\u624b\uff0c\u731b\u8fd0\u4e00\u53e3\u771f\u6c14\uff0c\u62b5\u4f4f\u65e0\u6068\u751f\u542b\u6012\u7684\u53cd\u51fb\u3002\u201d \u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u98ce\u67cf\u6768\u9a6d\u7136\u505c\u4e0b\u8bdd\u6765\u3002 \u3000\u3000\u9ad8\u6218\u5728\u4e00\u65c1\u542c\u5f97\u6b63\u662f\u7709\u98de\u8272\u821e\uff0c\u5168\u795e\u8d2f\u6ce8\uff0c\u98ce\u67cf\u6768\u8fd9\u4e00\u505c\u987f\uff0c\u9ad8\u6218\u4e0d\u7531\u8be7\u5f02\uff0c\u5374\u53c8\u4e0d\u6562 \u63d2\u53e3\u76f8\u95f4\u3002\u671b\u7740\u98ce\u67cf\u6768\uff0c\u53ea\u89c1\u4ed6\u53cc\u65e5\u4e2d\u795e\u5149\u51db\u51db\uff0c\u767d\u81c2\u5fae\u5fae\u800c\u52a8\uff0c\u50cf\u662f\u6b63\u503c\u64ae\u7740\u5f53\u65e5\u795e\u52c7\u7684 \u60c5\u5f62\uff0c\u9ad8\u6218\u770b\u5f97\u4e0d\u7531\u8c6a\u6c14\u5927\u53d1\uff0c\u96c4\u5fc3\u52c3\u8d77\uff0c\u5fcd\u4e0d\u4f4f\u95ee\u9053\uff1a\u201c\u540e\u6765\u5462\u2026\u2026\u201d \u3000\u3000\u98ce\u67cf\u6768\u83ab\u7136\u56de\u9192\uff0c\u63a5\u53e3\u9053\uff1a\u201c\u597d\uff01\u597d\uff01\u4e3a\u5e08\u8fd9\u5c31\u7ee7\u7eed\u9053\u6765\u2026\u2026\u201d \u3000\u3000\u539f\u6765\u5f53\u65e5\u98ce\u67cf\u6768\u548c\u65e0\u6068\u751f\u6bd4\u8bd5\u5185\u529b\uff0c\u4e24\u4eba\u5fc3\u4e2d\u6709\u6570\uff0c\u5185\u529b\u9020\u8be3\u786e\u5728\u4f2f\u4ef2\u4e4b\u95f4\uff0c\u867d\u8bf4\u6709\u5dee \u522b\uff0c\u4e5f\u4e0d\u8fc7\u53ea\u662f\u6781\u5176\u5fae\u5c0f\u7684\u7a0b\u5ea6\uff0c\u662f\u4ee5\u4e24\u4eba\u660e\u767d\u4e0d\u51fa\u5947\u62db\uff0c\u8fd9\u4e2a\u50f5\u5c40\u5374\u662f\u6253\u4e0d\u5f00\u6765\u3002 \u3000\u3000\u98ce\u67cf\u6768\u4e00\u638c\u5f97\u624b\uff0c\u65e0\u6068\u751f\u5168\u529b\u53cd\u653b\uff0c\u98ce\u67cf\u6768\u4f46\u89c9\u624b\u5fc3\u4e00\u70ed\uff0c\u864e\u543c\u4e00\u58f0\uff0c\u5185\u529b\u6d8c\u51fa\uff0c\u4e00\u62b5 \u4e4b\u4e0b\uff0c\u84e6\u7136\u611f\u5230\u52b2\u529b\u4e00\u7a7a\uff0c\u5fc3\u4e2d\u8fde\u5524\uff1a\u201c\u7cdf\u4e86\uff0c\u7cdf\u4e86\u2026\u2026\u201d \u3000\u3000\u8bf4\u65f6\u8fdf\uff0c\u90a3\u65f6\u5feb\uff0c\u65e0\u6068\u751f\u4ee5\u5176\u4eba\u4e4b\u9053\uff0c\u8fd8\u6cbb\u5176\u4eba\u4e4b\u8eab\uff0c\u52b2\u529b\u4e00\u6536\uff0c\u518d\u547c\u7136\u4e00\u5288\uff0c\u4e5f\u501f\u6b64 \u4e00\u51b2\u4e4b\u529b\uff0c\u767b\u65f6\u628a\u98ce\u67cf\u6768\u8eab\u5b50\u51fb\u5f97\u4e00\u9707\uff0c\u659c\u63a0\u5f00\u53bb\u3002 \u3000\u3000\u65e0\u6068\u751f\u54c8\u54c8\u4e00\u7b11\uff0c\u98ce\u67cf\u6768\u5fc3\u4e2d\u6094\u9053\uff1a\u201c\u597d\u4e0d\u5bb9\u6613\u624d\u51fa\u5947\u8ba1\u62a2\u5f97\u4e86\u5148\u673a\uff0c\u5374\u8ba9\u4ed6\u7528\u4e00\u6a21\u4e00 \u6837\u7684\u624b\u6cd5\u5e73\u53cd\u8d25\u5c40\uff0c\u8bf4\u4e0d\u5f97\u53ea\u6709\u633a\u800c\u8d70\u9669\u4e86\u3002\u201d \u3000\u3000\u4ed6\u53ef\u771f\u4e0d\u6562\u65ad\u5b9a\u81ea\u5df1\u662f\u5426\u53ef\u4ee5\u633a\u4f4f\u8fd9\u4e2a\u5927\u9669\uff0c\u4f46\u89c1\u4ed6\u957f\u563f\u4e00\u58f0\uff0c\u6089\u6570\u5410\u51fa\u80f8\u4e2d\u6df7\u6d4a\u771f \u6c14\uff0c\u501f\u8fd9\u5410\u6c14\u5f00\u58f0\u4e4b\u95f4\uff0c\u8eab\u5f62\u4e00\u6c89\uff0c\u53c8\u81ea\u957f\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u5de6\u53f3\u53cc\u638c\u4e00\u5408\u4e4b\u4e0b\uff0c\u6ef4\u6e9c\u6e9c\u5411\u4fa7\u4e00 \u504f\u3002 \u3000\u3000\u65e0\u6068\u751f\u4f55\u7b49\u529f\u529b\uff0c\u638c\u529b\u4e4b\u96c4\uff0c\u4e3e\u4e16\u96be\u4e0e\u5339\u654c\uff0c\u9002\u624d\u53c8\u501f\u8fd9\u4e00\u51b2\u4e4b\u529b\uff0c\u5374\u8ba9\u98ce\u67cf\u6768\u8fd0\u7528\u592a \u6781\u95e8\u4e2d\u6700\u521d\u6b65\u7684\u201c\u9ecf\u201d\u5b57\u8bc0\u7ed9\u5316\u4e86\u5f00\u53bb\uff0c\u8fd9\u5012\u662f\u65e0\u6068\u751f\u6240\u59cb\u6599\u4e0d\u53ca\u7684\u3002 \u3000\u3000\u4f46\u98ce\u67cf\u6768\u8fd9\u4e2a\u9669\u5012\u4e5f\u5192\u5f97\u592a\u5927\uff0c\u53ea\u89c9\u80f8\u53e3\u4e00\u7a92\uff0c\u773c\u4e2d\u5fae\u9ed1\uff0c\u731b\u7136\u4e00\u53f1\uff0c\u53d1\u52a8\u81ea\u5df1\u51e0\u5341\u5e74 \u6d78\u6deb\u7684\u5185\u5bb6\u529b\u9053\uff0c\u731b\u5730\u4e00\u5708\u518d\u5410\u3002 \u3000\u3000\u8fd9\u4e00\u5f0f\u4f20\u4e3a\u6253\u529b\u7684\u62db\u5f0f\u867d\u662f\u901a\u4fd7\u5bfb\u5e38\uff0c\u4f46\u5728\u6b64\u65f6\u4f7f\u51fa\uff0c\u9669\u5219\u9669\u77e3\uff0c\u5012\u662f\u9002\u5f53\u4e0d\u8fc7\u7684\u4e00 \u62db\u3002 \u3000\u3000\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u98ce\u67cf\u6768\u5185\u529b\u5c3d\u5410\uff0c\u4e0d\u4f46\u53d1\u51fa\u96c4\u6d51\u7684\u5355\u529b\uff0c\u800c\u4e14\u8fde\u5bf9\u65b9\u5343\u65a4\u4e4b\u529b\u4e5f\u4e00\u9f50\u53cd \u63a8\u56de\u53bb\uff0c\u58f0\u52bf\u4e4b\u731b\uff0c\u65e0\u53ef\u5339\u654c\u3002 \u3000\u3000\u65e0\u6068\u751f\u4e07\u6599\u4e0d\u5230\u5bf9\u65b9\u5927\u80c6\u5982\u6b64\uff0c\u563f\u7136\u4f4e\u54fc\u4e00\u58f0\uff0c\u8eab\u5f62\u6709\u82e5\u884c\u4e91\u6d41\u6c34\uff0c\u5f27\u5f62\u540e\u9000\u3002\u540c\u65f6\u95f4 \u91cc\uff0c\u53cc\u638c\u5df2\u5728\u8111\u524d\u6765\u56de\u4ea4\u9519\u4e86\u5341\u4f59\u5f0f\uff0c\u6bcf\u4e00\u5f0f\u90fd\u662f\u5185\u529b\u5916\u6d8c\uff0c\u624d\u582a\u582a\u63a5\u7740\u98ce\u67cf\u6768\u8fd9\u4e00\u638c\uff0c\u8138 \u4e0a\u4e0d\u7531\u4e00\u7ea2\uff0c\u54fc\u7136\u9053\uff1a\u201c\u597d\u638c\u529b\uff01\u201d \u3000\u3000\u84e6\u7136\uff0c\u65e0\u6068\u751f\u53cc\u638c\u4ea4\u76f8\u4e00\u5708\uff0c\u731b\u7136\u4e00\u5410\u3002 \u3000\u3000\u522b\u770b\u8fd9\u4e00\u638c\uff0c\u4e43\u662f\u65e0\u6068\u751f\u5185\u8eab\u529f\u529b\u96c6\u805a\u201c\u7389\u7384\u5f52\u771f\u201d\u4e4b\u5185\u5bb6\u7edd\u9876\u529b\u9053\uff0c\u4e5f\u5df1\u53d1\u51fa\u3002 \u3000\u3000\u98ce\u67cf\u6768\u77e5\u9053\u80dc\u8d25\u4e43\u5728\u6b64\u4e00\u4e3e\uff0c\u4ef0\u5929\u4e00\u547c\uff0c\u53cc\u638c\u63e1\u62f3\uff0c\u7f13\u7f13\u63a8\u51fa\u3002 \u3000\u3000\u201c\u8f70\u201d\u4e00\u58f0\uff0c\u4e24\u4eba\u76d6\u4e16\u9ad8\u4eba\u638c\u529b\u4e00\u89e6\uff0c\u6709\u82e5\u96f7\u52a8\u4e07\u7269\uff0c\u52b2\u98ce\u6fc0\u8361\u5904\uff0c\u5377\u8d77\u6f2b\u5929\u96ea\u82b1\uff0c\u4f46 \u662f\u4e24\u4eba\u90fd\u662f\u51dd\u5750\u5982\u5c71\uff0c\u52a8\u4e5f\u4e0d\u52a8\u3002 \u3000\u3000\u65e0\u6068\u751f\u5618\u4e86\u4e00\u53e3\u6c14\uff0c\u53c8\u6362\u4e86\u4e00\u53e3\u771f\u6c14\uff0c\u4e00\u79cd\u5148\u5929\u7684\u672c\u80fd\u5728\u4ed6\u4f53\u5185\u98de\u5feb\u8fd0\u884c\uff0c\u529f\u529b\u4e5f\u4e0d\u65ad \u6d8c\u51fa\u3002 \u3000\u3000\u98ce\u67cf\u6768\u53cc\u76ee\u5fae\u95ed\uff0c\u6697\u8fd0\u51e0\u5341\u5e74\u7684\u4fee\u517b\uff0c\u6765\u62b5\u6297\u5bf9\u65b9\u6709\u82e5\u6625\u8695\u5410\u4e1d\uff0c\u7ef5\u7ef5\u4e0d\u65ad\u7684\u653b\u51fb\uff01 \u3000\u3000\u65e0\u6781\u5c9b\u4e0a\u3002 \u3000\u3000\u96ea\u5cf0\u4e4b\u5dc5\u2026\u2026 \u3000\u3000\u767d\u832b\u832b\u7684\u4e00\u7247\uff0c\u4f7f\u5f97\u8fd9\u4e2a\u5c0f\u5c0f\u7684\u5929\u5730\u4f3c\u4e4e\u542b\u6709\u4e00\u80a1\u5723\u6d01\u4e0d\u53ef\u4fb5\u72af\u7684\u5473\u9053\u3002 \u3000\u3000\u5fae\u98ce\u5f00\u59cb\u9001\u62c2\u4e86\uff0c\u96ea\u5730\u4e0a\uff0c\u51fa\u77f3\u95f4\uff0c\u5bd2\u51c9\u523a\u9aa8\u3002\u8fdc\u65b9\uff0c\u5728\u82cd\u5f2f\u7684\u5c3d\u5934\u548c\u5305\u56f4\u7740\u5c0f\u5c9b\u7684\u5927 \u6d77\u832b\u832b\u76f8\u63a5\uff0c\u5e76\u5206\u4e0d\u51fa\u54ea\u91cc\u662f\u6c34\uff0c\u90a3\u91cc\u662f\u5929\u3002 \u3000\u3000\u6709\u6d77\u9e25\u81ea\u8fdc\u65b9\u6765\uff0c\u5b64\u7136\u9e3f\u53eb\u6570\u58f0\uff0c\u76d8\u65cb\u800c\u53bb\u3002 \u3000\u3000\u96ea\u5cad\u4e4b\u5c97\uff0c\u5b64\u7acb\u5c9b\u4e2d\uff0c\u82e5\u662f\u7acb\u4e8e\u5176\u4e0a\uff0c\u62ab\u4e0a\u4e00\u88ad\u767d\u888d\uff0c\u9065\u671b\u65e0\u8fb9\u5929\u6c34\u3002\u62c2\u9762\u5fae\u98ce\uff0c\u518d\u52a0 \u4e0a\u94f6\u767d\u7684\u79ef\u96ea\uff0c\u8fd9\u662f\u4f55\u7b49\u4ed9\u5883\uff1f\u7136\u800c\uff0c\u5728\u8fd9\u65e0\u8fb9\u666f\u8272\u4e4b\u4e2d\uff0c\u5374\u915d\u917f\u7740\u4e00\u573a\u60ca\u5fc3\u52a8\u9b44\u7684\u5927\u6218\u3002 \u3000\u3000\u84e6\u7136\uff0c\u96ea\u5cad\u5947\u6d77\u7684\u5c71\u9053\u4e0a\uff0c\u7adf\u5c14\u53c8\u51fa\u73b0\u4e00\u6761\u4eba\u5f71\u3002\u90a3\u4e2a\u4eba\u5f71\u52a8\u4f5c\u597d\u5feb\uff0c\u90a3\u6d88\u51e0\u70b9\uff0c\u4fbf\u6e21 \u8fc7\u5404\u4e2a\u9669\u963b\uff0c\u5b89\u7136\u6765\u5230\u5cad\u5dc5\u3002 \u3000\u3000\u5cf0\u5dc5\u5e73\u9876\u4e0a\uff0c\u4e24\u4e2a\u76d6\u4e16\u5927\u4fa0\u4ecd\u7136\u4f5c\u5185\u529b\u7684\u62fc\u6597\uff0c\u90a3\u4eba\u5f71\u6765\u5f97\u8fd1\u4e86\uff0c\u539f\u6765\u662f\u4e00\u4e2a\u4e2d\u5e74\u7f8e \u5987\u3002 \u3000\u3000\u65e0\u6068\u751f\u548c\u98ce\u67cf\u6768\u867d\u662f\u62fc\u8bd5\u7740\u5185\u529b\uff0c\u4f46\u4ecd\u662f\u8033\u542c\u516b\u65b9\uff0c\u8fd9\u7f8e\u5987\u624d\u4e0a\u5cf0\u6765\uff0c\u4e24\u4eba\u90fd\u60ca\u800c\u76f8 \u89c6\u3002 \u3000\u3000\u65e0\u6068\u751f\u4e0d\u7531\u60ca\u9053\uff1a\u201c\u4e03\u5a18\u2014\u2014\u600e\u4e48\u4f60\u2026\u2026\u201d \u3000\u3000\u90a3\u5987\u4eba\u4e94\u662f\u65e0\u6068\u751f\u7684\u59bb\u5b50\uff0c\u4e00\u4ee3\u5dfe\u5e3d\u82f1\u96c4\u7f2a\u4e03\u5a18\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u7b11\u7740\u6446\u6446\u624b\uff0c\u6253\u65ad\u65e0\u6068\u751f\u7684\u8bdd\u5934\uff0c\u5411\u98ce\u67cf\u6768\u88e3\u887d\u4e3a\u793c\uff0c\u9053\uff1a\u201c\u5984\u8eab\u7279\u6765\u4e3a\u98ce\u5927\u4fa0 \u548c\u5916\u5b50\u63a0\u9635\uff0c\u60f3\u4e24\u4f4d\u62fc\u6597\u5982\u6b64\u4e4b\u4e45\uff0c\u5fc5\u7136\u9700\u8981\u8fdb\u4e9b\u98df\u7269\u5427\uff1f\u201d \u3000\u3000\u5979\u53ef\u60f3\u5f97\u5468\u5230\uff0c\u53cd\u624b\u628a\u6302\u5728\u8eab\u540e\u7684\u4e00\u4e2a\u98df\u7bee\u53d6\u4e0b\u6765\uff0c\u63ed\u5f00\u98df\u76d2\uff0c\u539f\u6765\u662f\u51e0\u76d8\u7cbe\u81f4\u7684\u5c0f\u83b1 \u548c\u51e0\u65a4\u7f8e\u9152\u3002 \u3000\u3000\u98ce\u67cf\u6768\u54c8\u54c8\u4e00\u7b11\uff0c\u9053\uff1a\u201c\u98ce\u67cf\u6768\u5148\u6b64\u62dc\u8c22\u4e86\u2014\u2014\u201d \u3000\u3000\u65e0\u6068\u751f\u4e5f\u7b11\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\uff0c\u98ce\u5927\u4fa0\u54ea\u91cc\u7684\u8bdd\u2014\u2014\u201d \u3000\u3000\u4ed6\u4eec\u867d\u8bf4\u7740\u7b11\u8bdd\uff0c\u4f46\u624b\u4e2d\u5185\u529b\u5374\u5206\u6beb\u4e0d\u51cf\uff0c\u662f\u4ee5\u4e24\u4eba\u52c9\u5f3a\u5206\u795e\u8bf4\u8bdd\uff0c\u90fd\u4e0d\u5f97\u4e0d\u7acb\u523b\u6536 \u4f4f\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u5728\u4e00\u8fb9\u770b\u5f97\u5fae\u5fae\u4e00\u7b11\uff0c\u5fc3\u4e2d\u5374\u60ca\u9053\uff1a\u201c\u8fd9\u98ce\u8001\u513f\u7684\u5185\u529b\u7adf\u662f\u5982\u6b64\u7cbe\u6df1\uff1f\u201d \u3000\u3000\u53e3\u4e2d\u5374\u9053\uff1a\u201c\u4e24\u4f4d\u4e14\u6682\u4e00\u4f4f\u624b\uff0c\u5148\u5403\u4e00\u987f\u518d\u8bf4\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u54c8\u54c8\u7b11\u9053\uff1a\u201c\u54b1\u4eec\u8fb9\u6253\u8fb9\u5403\u4e5f\u6210\u2014\u2014\u201d \u3000\u3000\u8bf4\u7740\u63d0\u6c14\u5bf9\u51c6\u653e\u5728\u5de6\u4fa7\u7684\u4e00\u676f\u9152\u513f\u5f20\u53e3\u4e00\u5438\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u90a3\u676f\u9152\u5df2\u5165\u6c34\u7bad\u822c\u5c04\u5165\u4ed6 \u53e3\u4e2d\u3002 \u3000\u3000\u98ce\u67cf\u6768\u90a3\u80af\u793a\u5f31\uff0c\u6d2a\u58f0\u7b11\u9053\uff1a\u201c\u90a3\u5c31\u8c22\u8c22\u5566\u2014\u2014\u201d \u3000\u3000\u4e5f\u662f\u5f20\u53e3\u5438\u5c3d\u4e00\u676f\u7f8e\u9152\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u5927\u5403\u4e00\u60ca\uff0c\u53ef\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002 \u3000\u3000\u987b\u77e5\u4ed6\u4eec\u4e24\u4eba\u90fd\u5728\u4ee5\u7edd\u9876\u5185\u529b\u62fc\u6597\uff0c\u5fc3\u795e\u65e9\u5df2\u4e13\u6ce8\uff0c\u6b64\u65f6\u53c8\u62fc\u7740\u5206\u795e\u7528\u5185\u529b\u53bb\u5438\u90a3\u4e9b\u7f8e \u9152\uff0c\u8fd9\u6837\u4e00\u5fc3\u4e24\u7528\uff0c\u5047\u5982\u4ed6\u4eec\u6ca1\u6709\u7528\u529f\u5012\u4e5f\u7f62\u4e86\uff0c\u4f46\u4ed6\u4eec\u73b0\u4e0b\u4e43\u662f\u4ee5\u5168\u8eab\u529f\u529b\u76f8\u640f\uff0c\u4e00\u4e2a\u628a \u6301\u4e0d\u5b9a\uff0c\u8bf4\u4e0d\u5b9a\u7acb\u523b\u5f97\u53d7\u6df1\u91cd\u5185\u4f24\uff01 \u3000\u3000\u98ce\u67cf\u6768\u559d\u5f97\u51e0\u53e3\u7f8e\u9152\uff0c\u5fd6\u9053\uff1a\u201c\u4eca\u65e5\u4e4b\u4e8b\uff0c\u4e0d\u5206\u51fa\u80dc\u8d1f\uff0c\u53ea\u6015\u4e0d\u80fd\u4e86\u7ed3\uff0c\u65e0\u6068\u751f\u65e2\u7136\u8981 \u89c1\u8bc6\u6211\u5148\u5929\u6c14\u529f\uff0c\u6211\u5c31\u65bd\u51fa\u6765\u7ed9\u4ed6\u77a7\u77a7\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u5fc3\u610f\u4e00\u51b3\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u5982\u8001\u9f99\u6e05\u541f\uff0c\u4e45\u4e45\u4e0d\u7edd\uff0c\u8bf4\u9053\uff1a\u201c\u5c9b\u4e3b\u7559\u610f\uff0c\u5728\u4e0b\u663e \u4e11\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u54c8\u54c8\u5927\u7b11\uff0c\u58f0\u97f3\u6108\u6765\u6108\u9ad8\uff0c\u5230\u4e86\u540e\u6765\uff0c\u76f4\u5982\u51b2\u9704\u6d41\u661f\uff0c\u58f0\u91cf\u5b8f\u4f1f\uff0c\u9707\u52a8\u56db\u5468\u51b0\u96ea \u4e71\u98d8\u3002 \u3000\u3000\u98ce\u67cf\u6768\u5fc3\u9053\uff1a\u201c\u65e0\u6781\u4e3b\u60a0\u6e38\u4e16\u5916\uff0c\u53ef\u662f\u4e89\u5f3a\u6597\u80dc\u7684\u6027\u513f\u4ecd\u7136\u76db\u5f97\u7d27\uff0c\u6211\u65e0\u610f\u95f4\u957f\u5578\u4e00 \u58f0\uff0c\u4ed6\u5c31\u4e0d\u670d\u6c14\uff0c\u8fd9\u9635\u5927\u7b11\uff0c\u5206\u522b\u662f\u663e\u793a\u5185\u529f\u6765\u7684\u3002\u201d \u3000\u3000\u8fb9\u585e\u5927\u4fa0\u4e0d\u518d\u591a\u8a00\uff0c\u84e6\u7136\u8eab\u5f62\u5411\u540e\u5012\u7a9c\uff0c\u548c\u65e0\u6068\u751f\u76f8\u9694\u4e08\u4f59\u5750\u4e86\u4e0b\u6765\uff0c\u53cc\u624b\u63e1\u62f3\u76f8\u5408\u3002 \u3000\u3000\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\u77e5\u4ed6\u8981\u4ee5\u5173\u5916\u7edd\u5b66\u201c\u5148\u5929\u6c14\u529f\u201d\u76f8\u62fc\uff0c\u4e0d\u6562\u4e1d\u6beb\u5927\u610f\uff0c\u4e00\u63d0\u771f\u6c14\uff0c\u53cc\u638c \u5408\u4ec0\u3001\u4e5f\u76d8\u5750\u4e0b\u6765\u3002 \u3000\u3000\u5ffd\u7136\u98ce\u67cf\u6768\u53cc\u62f3\u7f13\u7f13\u5411\u7a7a\u865a\u53d1\uff0c\u5b9b\u5982\u63a8\u52a8\u5343\u94a7\u91cd\u7269\uff0c\u53d1\u987b\u7686\u5f20\uff0c\u65e0\u6839\u751f\u53cc\u638c\u5206\u5f00\uff0c\u5411\u524d \u62cd\u51fa\uff0c\u770b\u4f3c\u8f7b\u98d8\u98d8\u7684\uff0c\u5176\u5b9e\u5df2\u7ecf\u85cf\u81ea\u5df1\u6027\u547d\u4ea4\u4fee\u4e00\u7532\u5b50\u7684\u4e0a\u4e58\u5185\u529f\u3002 \u3000\u3000\u4e24\u80a1\u529b\u9053\u5728\u7a7a\u4e2d\u95f4\u4e00\u649e\uff0c\u98ce\u67cf\u6768\u62f3\u98ce\u867d\u5389\uff0c\u53ef\u662f\u7adf\u88ab\u65e0\u6068\u751f\u6240\u53d1\u9634\u67d4\u6c14\u529f\u5316\u89e3\u3002 \u3000\u3000\u98ce\u67cf\u6768\u559d\u4e00\u58f0\u597d\u3001\u4e5f\u4e0d\u89c1\u4ed6\u4f5c\u52bf\uff0c\u8eab\u5b50\u51ed\u7a7a\u524d\u8d77\uff0c\u53cc\u624b\u8fde\u53d1\u4e03\u62f3\uff0c\u65e0\u6068\u751f\u4e0d\u80af\u7a0d\u7a0d\u793a \u5f31\uff0c\u8eab\u5f62\u4e5f\u5411\u524d\u8fdb\uff0c\u5feb\u82e5\u95ea\u7535\u7684\u4e5f\u62cd\u51fa\u4e03\u638c\u3002 \u3000\u3000\u4e24\u4eba\u8fd9\u4e00\u6362\u62db\uff0c\u53ea\u770b\u5f97\u5728\u65c1\u7684\u7f2a\u4e03\u5a18\u82b1\u5bb9\u5931\u8272\uff0c\u5979\u867d\u77e5\u81ea\u5df1\u4e08\u592b\u6b66\u529f\u9ad8\u6781\uff0c\u53ef\u662f\u5979\u4ece\u672a \u89c1\u8fc7\u4ed6\u65bd\u51fa\u5982\u6b64\u7edd\u6280\uff0c\u8981\u77e5\u51ed\u7a7a\u6e21\u865a\uff0c\u5168\u51ed\u4e00\u53e3\u771f\u6c14\uff0c\u5b88\u53d7\u5916\u529b\uff0c\u7acb\u5373\u843d\u5730\uff0c\u4e24\u4eba\u4e0d\u4f46\u80fd\u54c6 \u53d1\u52b2\u653b\u654c\uff0c\u800c\u4e14\u80fd\u5728\u5bf9\u65b9\u731b\u70c8\u5185\u529b\u653b\u51fb\uff0c\u4f9d\u7136\u524d\u98de\uff0c\u8fd9\u79cd\u5185\u529f\u4fee\u4e3a\uff0c\u5df2\u5230\u7089\u706b\u7eaf\u9752\uff0c\u4e00\u53e3\u771f \u6c14\u53ef\u4ee5\u6570\u7528\u4e86\u3002 \u3000\u3000\u7f2a\u4e03\u5a18\u5fc3\u9053\uff1a\u201c\u8fd9\u5173\u5916\u6765\u7684\u91ce\u8001\u5934\uff0c\u529f\u529b\u771f\u7684\u5982\u6b64\u9ad8\u5f3a\u3002\u201d\u5979\u5fc3\u4e2d\u4e0d\u7981\u6709\u4e9b\u4e3a\u4e08\u592b\u62c5 \u5fe7\u3002 \u3000\u3000\u6b64\u65f6\u4e24\u4eba\u53ea\u9694\u5fae\u5c3a\uff0c\u65e0\u6068\u751f\u5fae\u5fae\u4e00\u7b11\uff0c\u63a8\u51fa\u53cc\u638c\uff0c\u63a5\u4e0a\u98ce\u67cf\u6768\u7684\u53cc\u62f3\uff0c\u4e24\u4eba\u8138\u4e0a\u795e\u8272\u7a81 \u53d8\u51dd\u91cd\uff0c\u7f2a\u4e03\u5a18\u4e00\u770b\u56db\u53ea\u624b\u4e24\u62f3\u4e24\u638c\u80f6\u9ecf\u5728\u4e00\u5757\uff0c\u4e1d\u6beb\u4e0d\u52a8\u3002 \u3000\u3000\u7f28\u4e03\u5a18\u5fc3\u4e2d\u5927\u6025\uff0c\u77e5\u9053\u4e24\u4eba\u6b63\u4ee5\u4e0a\u4e58\u5185\u529f\u76f8\u62fc\uff0c\u8fd9\u79cd\u6bd4\u6cd5\uff0c\u6beb\u65e0\u53d6\u5de7\u53ef\u8a00\uff0c\u6bd4\u5230\u6700\u540e\u529f \u529b\u5f31\u7684\u4e00\u65b9\uff0c\u4e0d\u6b7b\u5373\u4f24\u3002 \u3000\u3000\u98ce\u67cf\u6768\u89c1\u81ea\u5df1\u65e0\u575a\u4e0d\u6467\u7684\u5148\u5929\u5185\u529f\uff0c\u4e00\u6b21\u6b21\u53d1\u5c06\u51fa\u53bb\uff0c\u5bf9\u65b9\u529f\u529b\u5e76\u672a\u534a\u70b9\u4e0d\u6d4e\u4e4b\u8272\uff0c\u5fc3 \u795e\u4e00\u70e6\uff0c\u5ffd\u89c9\u5bf9\u65b9\u4e00\u80a1\u9634\u67d4\u529b\u9053\uff0c\u987a\u7740\u81ea\u5df1\u53cc\u62f3\u4f20\u4e0a\uff0c\u4e0d\u7531\u5927\u5403\u4e00\u63a0\uff0c\u8fde\u5fd9\u5c06\u529f\u529b\u53d1\u51fa\u5341\u4e8c \u5206\uff0c\u8fd9\u624d\u633d\u56de\u5371\u52bf\u3002 \u3000\u3000\u7f28\u4e03\u5a18\u53ea\u89c1\u98ce\u67cf\u6768\u8138\u4e0a\u6108\u6765\u6108\u7ea2\uff0c\u81ea\u5df1\u4e08\u592b\u8138\u4e0a\u6108\u6765\u6108\u767d\uff0c\u5fc3\u77e5\u80dc\u8d1f\u5373\u5206\uff0c\u5979\u5728\u672a\u5ac1\u65e0 \u6781\u5c9b\u4e3b\u4ee5\u524d\uff0c\u539f\u4e5f\u662f\u4e2a\u5927\u5927\u6709\u540d\u7684\u5973\u4fa0\uff0c\u4ec0\u4e48\u573a\u9762\u98ce\u6d6a\u4e5f\u89c1\u8fc7\u95ef\u8fc7\uff0c\u53ef\u662f\u6b64\u65f6\u7adf\u7136\u6389\u8f6c\u5934 \u53bb\uff0c\u4e0d\u6562\u518d\u770b\u3002 \u3000\u3000\u6b63\u5728\u6b64\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u5ffd\u7136\u4ece\u96ea\u5cad\u90a3\u8fb9\u8d70\u4e0a\u4e00\u4e2a\u8001\u548c\u5c1a\uff0c\u7f2a\u4e03\u5a18\u4e00\u89c1\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u9ad8\u58f0 \u53eb\u9053\uff1a\u201c\u5e73\u51e1\u5927\u5e08\uff0c\u8bf7\u5feb\u6765\u554a\u3002\u201d \u3000\u3000\u539f\u6765\u90a3\u8001\u548c\u5c1a\u6b63\u662f\u4e1c\u6d77\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u3002 \u3000\u3000\u5e73\u51e1\u4e0a\u4eba\u8d70\u5230\u4e8c\u4eba\u8eab\u65c1\uff0c\u5475\u5475\u7b11\u9053\uff1a\u201c\u4e24\u4f4d\u8001\u5f1f\uff0c\u542c\u6211\u4e00\u8a00\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u89c1\u5e73\u51e1\u4e0a\u4eba\u6765\u5230\u6709\u8bdd\u8981\u8bf4\uff0c\u4e0d\u597d\u610f\u601d\u518d\u6bd4\u4e0b\u53bb\uff0c\u98ce\u67cf\u6c64\u4e5f\u662f\u4e00\u822c\u5fc3\u601d\uff0c\u4e24\u4eba\u4e00\u64a4 \u771f\u529b\uff0c\u53cc\u53cc\u7ad9\u8d77\uff0c\u4e92\u76f8\u4e00\u63d6\u3002 \u3000\u3000\u98ce\u67cf\u6768\u4e00\u89c1\u81ea\u5df1\u5750\u4e0b\u96ea\u82b1\u5df1\u878d\u4e86\u4e00\u534a\uff0c\u65e0\u6068\u751f\u9002\u624d\u6240\u5750\u96ea\u5730\u5904\uff0c\u4f9d\u7136\u662f\u5b8c\u597d\u65e0\u635f\uff0c\u5fc3\u77e5 \u8fd9\u4e00\u8f83\u91cf\uff0c\u81ea\u5df1\u5df2\u8f93\u4e86\u534a\u7b79\uff0c\u4ed6\u662f\u540d\u6768\u5929\u4e0b\u7684\u5927\u4fa0\uff0c\u5c82\u80fd\u6df7\u8d56\uff0c\u5f53\u4e0b\u5411\u4e09\u4eba\u4f5c\u4e86\u4e00\u63d6\uff0c\u5bf9\u65e0 \u6068\u751f\u9053\uff1a\u201c\u5c9b\u4e3b\u624b\u4e0b\u7559\u60c5\uff0c\u98ce\u67d0\u5fc3\u5185\u81ea\u77e5\uff0c\u53ea\u662f\u5c9b\u4e3b\u82f1\u98ce\u9ad8\u4eba\uff0c\u98ce\u67d0\u5411\u5f80\u5f97\u7d27\uff0c\u5341\u5e74\u4e4b\u540e\uff0c \u518d\u6765\u5c9b\u4e0a\u8bf7\u6559\u3002\u201d \u3000\u3000\u65e0\u6068\u751f\u7b11\u9053\uff1a\u201c\u98ce\u5927\u4fa0\u529f\u529b\u76d6\u4e16\uff0c\u5c0f\u751f\u4e5f\u81ea\u4ec5\u670d\u5f97\u5f88\uff0c\u5929\u6daf\u867d\u5927\uff0c\u77e5\u5df1\u5374\u5c11\uff0c\u5f97\u4e00\u77e5\u5df1 \u592b\u590d\u4f55\u4f20\uff0c\u5341\u5e74\u4e4b\u540e\uff0c\u5c0f\u751f\u8bbe\u9152\u5cad\u4e0a\uff0c\u606d\u5019\u5927\u9a7e\u3002\u201d \u3000\u3000\u539f\u6765\u65e0\u6068\u751f\u751f\u5e73\u672a\u9022\u654c\u624b\uff0c\u6b64\u65f6\u89c1\u98ce\u4f2f\u6768\u6b63\u6c14\u51db\u7136\uff0c\u529f\u529b\u9ad8\u7edd\uff0c\u7ade\u751f\u76f8\u60dc\u4e4b\u5fc3\uff0c\u4ee5\u4ed6\u90a3 \u79cd\u4f36\u50b2\u813e\u6c14\uff0c\u7adf\u7136\u5bf9\u98ce\u67cf\u6768\u5ba2\u6c14\u8d77\u6765\u3002 \u3000\u3000\u98ce\u67cf\u6768\u65e0\u8138\u518d\u7559\uff0c\u8f6c\u8eab\u4e0b\u5cad\uff0c\u91cd\u8fd4\u5173\u5916\u3002 \u3000\u3000\u8fb9\u585e\u5927\u4fa0\u98ce\u67cf\u6768\u4e00\u53e3\u6c14\u628a\u5f53\u5e74\u5927\u6218\u65e0\u6781\u5c9b\u4e3b\u7684\u4e8b\u8bf4\u4e86\u4e00\u904d\uff0c\u9ad8\u6218\u53ea\u542c\u5f97\u5fc3\u795e\u4ff1\u9189\uff0c\u5fc3\u4e2d \u5bf9\u4e8e\u5e08\u7236\u5a01\u731b\u65e0\u6bd4\uff0c\u65e0\u6068\u751f\u7684\u6d12\u8131\u65e0\u6ede\uff0c\u5b9e\u662f\u94a6\u4f69\u4e4b\u6781\u3002 \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u73b0\u5728\u5c31\u662f\u53bb\u8d74\u5341\u5e74\u4e4b\u7ea6\u5417\uff1f\u201d \u3000\u3000\u98ce\u67cf\u6768\u70b9\u70b9\u5934\uff0c\u9ad8\u6218\u53c8\u9053\uff1a\u201c\u6218\u513f\u4e5f\u8ddf\u4f60\u53bb\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u9053\uff1a\u201c\u4f60\u5230\u676d\u5dde\u7b49\u6211\uff0c\u6211\u8fd9\u6b21\u8d74\u7ea6\uff0c\u5fc3\u4e2d\u5e76\u672a\u5b58\u4e89\u80dc\u597d\u540d\u4e4b\u5fc3\uff0c\u5012\u60f3\u548c\u90a3\u65e0\u6068\u751f \u7ed3\u4ea4\u3002\u4ed6\u90a3\u65e0\u6781\u5c9b\u4ece\u4e0d\u51c6\u5916\u5165\u8e0f\u4eba\uff0c\u6211\u6614\u5e74\u7b54\u5e94\u4e00\u4eba\u8d74\u7ea6\uff0c\u4f60\u867d\u662f\u6211\u5f92\u513f\uff0c\u5e26\u53bb\u5374\u4e5f\u4e0d \u4fbf\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u4e0d\u60a6\uff0c\u4f46\u4ed6\u4e0d\u6562\u548c\u5e08\u7236\u9876\u5634\uff0c\u53ea\u5f97\u7b54\u5e94\u4e0d\u53bb\u3002 \u3000\u3000\u4e14\u8bf4\u9ad8\u6218\u9075\u7167\u5e08\u7236\u8fb9\u585e\u5927\u4fa0\u98ce\u67cf\u6768\u7684\u5429\u5490\uff0c\u52a8\u8eab\u524d\u5f80\u676d\u57ce\u7b49\u4ed6\uff0c\u9ad8\u6218\u5fc3\u4e2d\u867d\u7136\u5f88\u60f3\u8ddf\u5e08 \u7236\u53bb\u89c1\u8bc6\u4e00\u4e0b\u65e0\u6781\u5c9b\u4e3b\u65e0\u6068\u751f\uff0c\u53ef\u662f\u98ce\u67cf\u6768\u518d\u4e09\u963b\u6b62\uff0c\u4ed6\u53ea\u6709\u5feb\u5feb\u542f\u7a0b\u3002 \u3000\u3000\u4ed6\u4ece\u5bb6\u4e61\u51fa\u5916\uff0c\u539f\u6765\u51c6\u5907\u6e38\u5386\u5929\u4e0b\uff0c\u6240\u4ee5\u4e70\u4e86\u4e00\u5339\u7626\u9a6c\uff0c\u53ef\u662f\u4e0a\u6b21\u5728\u7eff\u6797\u5927\u4f1a\u5306\u5306\u5fd9\u5fd9 \u968f\u7740\u5434\u51cc\u98ce\u8d70\u4e86\uff0c\u662f\u4ee5\u9a6c\u4e5f\u672a\u53ca\u5e26\u51fa\uff0c\u73b0\u5728\u8981\u8fdc\u884c\u5929\u6daf\uff0c\u4fbf\u611f\u5341\u5206\u4e0d\u4fbf\uff0c\u4ed6\u770b\u770b\u6000\u4e2d\u81ea\u5df1\u8f9b \u8f9b\u82e6\u82e6\u79ef\u50a8\u7684\u94f6\u5b50\uff0c\u6e10\u6e10\u51cf\u5c11\uff0c\u60f3\u5230\u6a2a\u76f4\u6709\u4e09\u4e2a\u6708\u65f6\u95f4\uff0c\u4e00\u6a2a\u5fc3\u5c31\u51b3\u5b9a\u6b65\u884c\u8d70\u53bb\u3002 \u3000\u3000\u4ed6\u4e00\u8def\u4e0a\u6d4f\u89c8\u98ce\u666f\uff0c\u5fc3\u60c5\u5012\u4e5f\u5341\u5206\u7545\u5feb\uff0c\u53ea\u662f\u5728\u591c\u534a\u68a6\u56de\u4e4b\u65f6\uff0c\u90a3\u5c71\u4e1c\u5927\u8c6a\u5973\u513f\u7684\u5982\u82b1 \u7684\u7b11\u9765\uff0c\u6b3e\u6b3e\u60c5\u6df1\u7684\u773c\u5149\uff0c\u8fd8\u4e0d\u65f6\u4f1a\u6d6e\u4e0a\u5fc3\u5934\u3002\u9ad8\u6218\u4e0d\u77e5\u4e0b\u4e86\u51e0\u6b21\u51b3\u5fc3\uff0c\u4e0d\u518d\u60f3\u5979\uff0c\u53ef\u662f\u6bcf \u5f53\u4ed6\u4e00\u4e2a\u4eba\u72ec\u5904\u65f6\uff0c\u4ed6\u5c31\u4f1a\u611f\u5230\u5bc2\u5bde\uff0c\u4eff\u4f5b\u6709\u4e2a\u5f88\u4eb2\u5207\u7684\u4eba\u5728\u8fdc\u65b9\uff0c\u81ea\u5df1\u65e0\u6cd5\u548c\u4ed6\u76f8\u4f1a\u3002 \u3000\u3000\u6b64\u65f6\u5df2\u662f\u521d\u590f\uff0c\u5929\u6c14\u6e10\u6e10\u708e\u70ed\uff0c\u9ad8\u6218\u6bcf\u65e5\u5929\u508d\u665a\u8d76\u5230\u4e00\u4e2a\u5730\u65b9\u4f4f\u4e0b\uff0c\u5403\u8fc7\u665a\u996d\u540e\uff0c\u603b\u7231 \u6d74\u770b\u6e05\u51c9\u7684\u665a\u98ce\uff0c\u5230\u5904\u905b\u8fbe\u4e00\u756a\uff0c\u6709\u65f6\uff0c\u4ed6\u4f1a\u7ad9\u5728\u6811\u65c1\u77a7\u7740\u5b69\u5b50\u4eec\u7528\u957f\u957f\u7af9\u6746\u53bb\u6349\u201c\u77e5 \u4e86\u201d\uff0c\u76f4\u5230\u5b69\u5b50\u4eec\u6bcf\u4eba\u624b\u4e2d\u90fd\u6349\u4e86\u4e00\u53ea\uff0c\u5174\u6559\u51b2\u51b2\u7684\u56de\u5bb6\u53bb\uff0c\u4ed6\u624d\u8ddf\u7740\u79bb\u5f00\u3002 \u3000\u3000\u53c8\u6709\u65f6\u4ed6\u7ad9\u5728\u67f3\u836b\u4e0b\uff0c\u671b\u7740\u6ed4\u6ed4\u7684\u9ec4\u6cb3\uff0c\u6c38\u65e0\u6b47\u6b62\u7684\u5411\u4e1c\u6d41\u7740\uff0c\u5076\u800c\u6709\u4e00\u4e24\u5c3e\u80a5\u58ee\u7684\u9ca4 \u9c7c\uff0c\u8dc3\u51fa\u6c34\u9762\uff0c\u8df3\u8dc3\u7740\uff0c\u8df3\u8dc3\u7740\uff0c\u4e8e\u662f\u513f\u65f6\u7684\u60c5\u666f\uff0c\u53c8\u6e05\u6670\u7684\u51fa\u73b0\u4e86\uff0c\u513f\u65f6\u7684\u79cd\u79cd\u8da3\u4e8b\u4e00\u95f4 \u800c\u8fc7\u3002\u9ad8\u6218\u771f\u5fcd\u4e0d\u4f4f\u60f3\u8dc3\u4e0b\u6c34\u53bb\u6293\u9c7c\uff0c\u53ef\u662f\u4e00\u770b\u81ea\u5df1\u8eab\u4e0a\u5112\u751f\u6253\u626e\uff0c\u7acb\u523b\u5174\u8da3\u5c31\u6d88\u5931\u4e86\uff0c\u4e8e \u662f\u4ed6\u6df1\u5207\u7684\u9886\u609f\u5230\u53ea\u6709\u5149\u9634\u2014\u2014\u5931\u53bb\u7684\u5149\u9634\uff0c\u90a3\u662f\u6c38\u8fdc\u65e0\u6cd5\u8ffd\u56de\uff0c\u7eb5\u7136\u6709\u79fb\u5c71\u5012\u6d77\u4e4b\u80fd\uff0c\u5374 \u4e5f\u4e0d\u80fd\u628a\u81ea\u5df1\u53ef\u7231\u7684\u7ae5\u5e74\u627e\u56de\uff0c\u5982\u679c\u8981\uff0c\u90a3\u4e48\u53ea\u6709\u4ece\u7247\u65ad\u7684\u56de\u5fc6\u4e2d\uff0c\u8ffd\u7d22\u4e00\u4e9b\u6b8b\u4f59\u7684\u75d5\u8ff9\u3002 \u3000\u3000\u8fd9\u5929\u4ed6\u6b63\u5728\u901b\u8857\uff0c\u5ffd\u7136\u80cc\u540e\u4e00\u4e2a\u719f\u6089\u7684\u58f0\u97f3\u5431\u5431\u5b64\u5b64\u7684\u6709\u8bf4\u6709\u7b11\uff0c\u9ad8\u6218\u56de\u5934\u4e00\u770b\uff0c\u767b\u65f6 \u5fc3\u4e2d\u51b0\u51c9\uff0c\u7565\u4e00\u6c89\u541f\uff0c\u5c31\u95ea\u8eab\u4eba\u7fa4\u4e2d\u3002 \u3000\u3000\u539f\u6765\u4ed6\u4e00\u773c\u770b\u53bb\uff0c\u80cc\u540e\u6b63\u662f\u6d4e\u5357\u5927\u8c6a\u4e4b\u5973\uff0c\u8fd9\u5f02\u5730\u76f8\u9022\u672c\u662f\u5f88\u6b22\u559c\u7684\u4e8b\uff0c\u53ea\u662f\u5728\u5979\u8eab \u65c1\uff0c\u5374\u7ad9\u7740\u4e2a\u4fcf\u4fca\u5c11\u5e74\uff0c\u4e24\u4eba\u795e\u6001\u4eb2\u5bc6\uff0c\u6ee1\u5fc3\u6b22\u6109\uff0c\u4eff\u4f5b\u662f\u591a\u5e74\u60c5\u4fa3\u3002 \u3000\u3000\u9ad8\u6218\u5728\u9ed1\u6697\u4e2d\u5077\u770b\u4e24\u4eba\u7684\u80cc\u5f71\uff0c\u80f8\u4e2d\u7a81\u7136\u4e00\u7d27\uff0c\u90a3\u5bbd\u5e7f\u6148\u548c\u7684\u80f8\u6000\uff0c\u4e00\u77ac\u95f4\u7a81\u7136\u53d8\u5f97\u72ed \u7a84\u8d77\u6765\uff0c\u53ea\u89c9\u5982\u706b\u70e7\u8eab\uff0c\u70e6\u71e5\u5fc3\u4f24\u3002 \u3000\u3000\u591a\u5c11\u5e73\u5e38\u4e00\u60f3\u5373\u901a\uff0c\u591a\u5c11\u5e73\u65e5\u4ed6\u8ba4\u4e3a\u7406\u6240\u5f53\u7136\u7684\u4e8b\uff0c\u90fd\u7a81\u7136\u6210\u4e3a\u60f3\u4e0d\u900f\u7684\u6b7b\u7ed3\uff0c\u4e8e\u662f\uff0c \u4ed6\u5bf9\u4eba\u751f\u6709\u4e86\u65b0\u7684\u770b\u6cd5\uff0c\u90a3\u5c31\u662f\u2014\u2014\u9664\u4e86\u6b22\u4e50\uff0c\u8fd8\u6709\u6c38\u6052\u7684\u60b2\u51c9\u3002 \u3000\u3000\u201c\u5434\u5927\u53d4\u90a3\u6df1\u523b\u6c89\u75db\u7684\u8868\u60c5\uff0c\u4e0d\u4e5f\u662f\u4e3a\u4e86\u90a3\u5893\u4e2d\u7684\u59d1\u5a18\u3002\u201d\u9ad8\u6218\u60f3\uff1a\u201c\u4ed6\u7684\u9752\u6625\u3001\u4e8b \u4e1a\uff0c\u90fd\u5c06\u966a\u4f34\u7740\u65e0\u7a77\u5c3d\u7684\u75db\u82e6\uff0c\u6e10\u6e10\u5728\u8fd9\u4e16\u4e0a\u78e8\u706d\uff0c\u5982\u679c\u4ed6\u4e0d\u9047\u7740\u5170\u59d1\u5a18\uff0c\u90a3\u4e48\u4ed6\u8fd9\u4e00\u751f\u4e00 \u5b9a\u5982\u4e0a\u5347\u7684\u592a\u9633\uff0c\u5149\u8292\u4e07\u4e08\uff0c\u53ef\u662f\u4e0a\u5929\u5b89\u6392\u7684\uff0c\u8c01\u4e5f\u6ca1\u6709\u529e\u6cd5\u6539\u53d8\u554a\uff01\u201d \u3000\u3000\u60f3\u5230\u5929\uff0c\u4ed6\u4e0d\u81ea\u89c9\u7684\u62ac\u8d77\u5934\u6765\uff0c\u5929\u9645\u7247\u7247\u706b\u70e7\u4e91\uff0c\u8fd8\u6709\u51e0\u9897\u5c0f\u661f\u987d\u76ae\u7684\u7728\u7740\u773c\u3002 \u3000\u3000\u5929\u8272\u6e10\u6e10\u6697\u4e0b\u6765\uff0c\u9ad8\u6218\u6709\u4e9b\u51b7\uff0c\u60c5\u7eea\u5982\u6012\u6d9b\u8d77\u4f0f\uff0c\u4e00\u6b21\u6b21\u51b2\u5411\u4ed6\u5185\u5fc3\u7684\u6df1\u5904\uff0c\u4ed6\u89c9\u5f97\u6709 \u4e9b\u652f\u6301\u4e0d\u4f4f\u4e86\uff0c\u81ea\u601c\u7684\u7b11\u4e86\u7b11\uff0c\u60b2\u54c0\u7684\u8038\u8038\u80a9\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u4ece\u5c0f\u968f\u4fbf\u4ec0\u4e48\u5fc3\u7231\u7684\u7269\u4e8b\uff0c\u90fd\u53ef \u4ee5\u6beb\u4e0d\u8003\u8651\u5730\u9001\u7ed9\u5c0f\u670b\u53cb\uff0c\u968f\u4fbf\u4ec0\u4e48\u7231\u5403\u7684\u98df\u7269\u90fd\u6beb\u4e0d\u541d\u556c\u7684\u5206\u7ed9\u5927\u5bb6\uff0c\u6211\u53ea\u9053\u4e16\u4e0a\u6ca1\u6709\u4ec0 \u4e48\u503c\u5f88\u4e89\u593a\u7684\uff0c\u53ea\u8981\u6211\u6709\u7684\uff0c\u4efb\u662f\u8c01\u4eba\u62ff\u53bb\uff0c\u6211\u4e5f\u4e0d\u4f1a\u73cd\u501f\uff0c\u53ef\u662f\uff0c\u2026\u2026\u53ef\u662f\u4e16\u4e0a\u539f\u6765\u8fd8\u6709 \u4e00\u4ef6\u4e1c\u897f\uff0c\u90a3\u662f\u65e0\u6cd5\u4e0e\u4eba\u5171\u7528\u554a\uff01\u201d \u3000\u3000\u4ed6\u5fc3\u60f3\uff1a\u201c\u4e00\u5207\u90fd\u4e0d\u91cd\u8981\u4e86\uff0c\u4e00\u5207\u90fd\u4e0d\u91cd\u8981\u4e86\u3002\u201d \u3000\u3000\u6708\u5149\u7167\u5728\u4ed6\u7684\u8eab\u4e0a\uff0c\u9ad8\u6218\u7a81\u7136\u60f3\u8d77\u5df2\u662f\u5341\u4e94\uff0c\u79bb\u5f00\u5e08\u7236\u7ea6\u671f\u53ea\u6709\u534a\u4e2a\u591a\u6708\uff0c\u5fc3\u4e2d\u4e00\u60ca\uff0c \u60f3\u8d77\u5e08\u7236\u6148\u7965\u6b63\u76f4\u7684\u98ce\u683c\uff0c\u81ea\u5df1\u6709\u8d1f\u4ed6\u6bb7\u6bb7\u7684\u671f\u671b\uff0c\u4e0d\u7531\u5927\u611f\u7f9e\u6127\uff0c\u7cbe\u795e\u4e00\u632f\uff0c\u65a9\u9489\u622a\u94c1\u7684 \u8bf4\u9053\uff1a\u201c\u9ad8\u6218\u554a\uff0c\u4f60\u8981\u5149\u5927\u5173\u5916\u6b66\u672f\uff0c\u4f60\u8981\u62ef\u6551\u751f\u6c11\u4e8e\u6c34\u706b\u4e4b\u4e2d\uff0c\u4f60\u5c31\u4e0d\u80fd\u518d\u80e1\u601d\u4e71\u60f3 \u4e86\u3002\u201d \u3000\u3000\u7136\u800c\u4ed6\u597d\u50cf\u542c\u5230\u5fc3\u4e2d\u4e00\u4e2a\u66f4\u4e3a\u575a\u5b9a\u800c\u4f4e\u6c89\u7684\u58f0\u97f3\uff1a\u201c\u9ad8\u6218\u554a\uff0c\u4f60\u5c06\u6c38\u8fdc\u4e5f\u5fd8\u4e0d\u6389\u90a3\u4f4d\u59d1 \u5a18\u7684\u3002\u201d \u3000\u3000\u8fd9\u4e00\u591c\uff0c\u9ad8\u6218\u53cd\u6765\u590d\u53bb\u7ec8\u662f\u4e0d\u80fd\u6210\u7720\uff0c\u90a3\u65e5\u5728\u98ce\u4e91\u5251\u6797\u9aa7\u5e84\u4e2d\u88ab\u56f4\uff0c\u9ad8\u6218\u5728\u7edd\u671b\u4e2d\u770b\u5230 \u4e86\u5e0c\u671b\uff0c\u662f\u4ee5\u867d\u5219\u8eab\u5904\u5371\u5883\uff0c\u5e76\u4e0d\u89c9\u4e1d\u6beb\u754f\u60e7\uff0c\u9ad8\u6218\u60f3\u9053\uff1a\u201c\u5979\u7528\u5173\u5207\u7684\u773c\u5149\u671b\u7740\u6211\uff0c\u539f\u6765 \u53ea\u662f\u601c\u60af\u6211\uff0c\u5982\u540c\u6211\u5e7c\u65f6\u5e38\u5e38\u53ef\u601c\u4e00\u53ea\u53d7\u4f24\u7684\u5c0f\u52a8\u7269\u3002\u6211\u5012\u8bef\u4f1a\u4ee5\u4e3a\u5979\u5f85\u6211\u597d\uff0c\u54fc\uff0c\u6211\u9ad8\u6218 \u662f\u7537\u5b50\u6c49\u5927\u4e08\u592b\uff0c\u53c8\u5c82\u80fd\u9700\u8981\u4eba\u53ef\u601c\u6211\uff1f\u201d \u3000\u3000\u4ed6\u60f3\u5230\u6b64\uff0c\u6012\u706b\u6e10\u6e10\u5347\u8d77\uff0c\u66f4\u662f\u7761\u4e0d\u7740\u89c9\uff0c\u63a8\u5f00\u7a97\u5b50\u8dc3\u51fa\u9662\u5916\uff0c\u53ea\u89c1\u7693\u6708\u5f53\u7a7a\uff0c\u591c\u51c9\u5982 \u4eba\uff0c\u4ed6\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u5fc3\u60c5\u6e10\u6e10\u5e73\u6291\u4e0b\u6765\u3002 \u3000\u3000\u9ad8\u6218\u4ee5\u4e3a\u65e5\u540e\u6d77\u9614\u5929\u7a7a\uff0c\u6c38\u8fdc\u4e0d\u4f1a\u518d\u78b0\u7740\u5979\uff0c\u7136\u540e\u4ed6\u6574\u5929\u5c06\u4e3a\u7ec3\u529f\u592b\uff0c\u884c\u4fa0\u4ed7\u4e49\u800c\u5fd9\u5f97 \u4e0d\u53ef\u5f00\u4ea4\uff0c\u90a3\u4e48\u5c31\u80fd\u628a\u5979\u7684\u5f71\u5b50\u6de1\u5fd8\uff0c\u53ef\u662f\uff0c\u4ed6\u5374\u60f3\u4e0d\u5230\u7b2c\u4e09\u5929\u4ed6\u4eec\u53c8\u5728\u8def\u4e0a\u76f8\u9022\u3002 \u3000\u3000\u6d4e\u5357\u5927\u8c6a\u7684\u5973\u513f\uff0c\u4ece\u5f88\u8fdc\u5c31\u770b\u89c1\u9ad8\u6218\uff0c\u5979\u62cd\u9a6c\u8ffd\u4e0a\u524d\u53bb\uff0c\u6b22\u58f0\u9053\uff1a\u201c\u5582\uff0c\u4f60\u8fd8\u8ba4\u5f97\u6211 \u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u84e6\u7136\u4e00\u60ca\uff0c\u770b\u770b\u5979\u8eab\u65c1\u65e0\u4eba\uff0c\u5fc3\u4e2d\u7565\u611f\u6b23\u6170\uff0c\u70b9\u5934\u7b54\u9053\uff1a\u201c\u90a3\u65e5\u5c0f\u53ef\u88ab\u56f4\uff0c\u5927\u5bb6\u90fd \u60f3\u6740\u4e4b\u800c\u5feb\uff0c\u53ea\u6709\u59d1\u5a18\u2026\u2026\u59d1\u5a18\u5173\u5fc3\u2026\u2026\u201d \u3000\u3000\u90a3\u5c11\u5973\u806a\u660e\u5df2\u6781\uff0c\u6253\u8fc7\u4ed6\u63d0\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u8138\u4e0a\u4e00\u7ea2\uff0c\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u5582\uff0c\u6211\u4e0d\u548c\u4f60\u8bf4\u8fd9\u4e2a\uff0c \u77e5\u9053\u6211\u79bb\u5bb6\u8dd1\u51fa\u8fd9\u4e48\u8fdc\u5e72\u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u60f3\uff1a\u201c\u8fd8\u4e0d\u662f\u548c\u90a3\u5c11\u5e74\u51fa\u6765\u6e38\u5c71\u73a9\u6c34\u3002\u201d\u4ed6\u6b64\u65f6\u7a81\u7136\u53d8\u5f97\u591a\u7591\u6613\u6012\uff0c\u60f3\u5230\u6b64\uff0c\u4e0d \u7981\u5341\u4e45\u7d22\u7136\u3002 \u3000\u3000\u90a3\u5c11\u5973\u89c1\u4ed6\u4e0d\u7b54\u8bdd\u3002\u8138\u4e0a\u9634\u6674\u4e0d\u5b9a\uff0c\u5fc3\u4e2d\u5927\u5947\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u60f3\u4ec0\u4e48\uff1f\u5e72\u5417\u4e0d\u7b54\u6211\u8bdd\u3002\u201d \u3000\u3000\u9ad8\u6218\u6de1\u6de1\u9053\uff1a\u201c\u5c0f\u53ef\u5e76\u4e0d\u77e5\u59d1\u5a18\u51fa\u5916\u4f55\u4e8b\uff0c\u662f\u4ee5\u4e0d\u6562\u5984\u81ea\u731c\u6d4b\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u542c\u4ed6\u51fa\u8a00\u51b7\u6f20\uff0c\u5fc3\u4e2d\u4e00\u9178\uff0c\u5490\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u7528\u4e86\u591a\u5c11\u65b9\u6cd5\u624d\u9a97\u5f97\u7239\u7239\u76f8\u4fe1\uff0c\u6211 \u82e5\u4e0d\u662f\u60f3\u89c1\u4f60\uff0c\u96be\u9053\u662f\u771f\u7684\u4e3a\u7740\u6e38\u5c71\u73a9\u6c34\u800c\u79bb\u5bb6\u4e48\uff1f\u201d \u3000\u3000\u5979\u5f88\u89c9\u6c14\u82e6\uff0c\u867d\u662f\u4e2a\u5343\u4f36\u767e\u5de7\u7684\u4eba\uff0c\u4e00\u65f6\u4e4b\u95f4\u4e5f\u627e\u4e0d\u51fa\u4ec0\u4e48\u8bdd\u597d\u8bb2\u3002 \u3000\u3000\u9ad8\u6218\u611f\u5230\u6c14\u6c1b\u6c89\u95f7\uff0c\u4fbf\u5411\u5c11\u5973\u4f5c\u4e86\u4e00\u63d6\u9053\uff1a\u201c\u591a\u8c22\u59d1\u5a18\u5173\u5fc3\uff0c\u5c0f\u53ef\u5c31\u6b64\u522b\u8fc7\u3002\u201d \u3000\u3000\u5c11\u5973\u773c\u7736\u4e00\u7ea2\uff0c\u67d4\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u53ef\u77e5\u9053\u4e0a\u6b21\u82f1\u96c4\u5927\u4f1a\uff0c\u4f60\u548c\u90a3\u4e2d\u5e74\u5112\u751f\u4e00\u8d70\uff0c\u5927\u5bb6\u51b3\u5b9a \u8981\u8054\u624b\u5bf9\u4ed8\u4f60\u5e08\u5f92\u4e24\u4eba\u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u6602\u7136\u9053\uff1a\u201c\u2018\u5929\u715e\u661f\u541b\u2019\u5e76\u4e0d\u662f\u6211\u5e08\u7236\uff0c\u4ed6\u4eec\u5982\u679c\u6015\u4e86\u90a3\u8001\u9b54\u5934\uff0c\u4e0d\u6562\u627e\u4ed6\uff0c\u60f3\u8981 \u6740\u6211\u51fa\u6c14\uff0c\u54fc\uff0c\u8fd9\u822c\u5f3a\u76d7\u5934\u513f\u6211\u4e5f\u4e0d\u6015\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u8138\u8272\u7a81\u7136\u60e8\u767d\u8d77\u6765\uff0c\u5979\u5fc3\u4e2d\u60f3\u5230\uff1a\u201c\u539f\u6765\u4ed6\u77a7\u4e0d\u8d77\u6211\u7239\u7239\u3002\u54fc\uff0c\u6211\u7239\u7239\u624d\u662f\u771f\u6b63 \u5927\u8c6a\u6770\uff0c\u4e00\u751f\u52ab\u5bcc\u6d4e\u8d2b\uff0c\u6d3b\u4eba\u65e0\u6570\uff0c\u8fd9\u5c0f\u5b50\u2026\u2026\u8fd9\u5c0f\u5b50\u2026\u2026\u8981\u662f\u8fd9\u8bdd\u7ed9\u7239\u7239\u542c\u89c1\uff0c\u4e0d\u9971\u6253\u4ed6 \u4e00\u987f\u624d\u602a\u3002\u201d \u3000\u3000\u5979\u6108\u60f3\u6108\u662f\u6124\u6012\uff0c\u8138\u8272\u7531\u81ea\u6fc0\u7ea2\uff0c\u62ac\u5934\u4e00\u770b\u9ad8\u6218\uff0c\u53ea\u89c1\u4ed6\u5931\u9b42\u843d\u9b44\u4e00\u822c\uff0c\u5fc3\u4e0b\u4e00\u8f6f\uff0c\u67d4 \u60c5\u987f\u751f\uff0c\u5934\u8111\u4e00\u8f6c\uff0c\u7b11\u9053\uff1a\u201c\u5582\uff0c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u6211\u603b\u4e0d\u80fd\u8001\u558a\u79f0\u5582\u5440\uff1f\u201d \u3000\u3000\u9ad8\u6218\u4e00\u65f6\u6fc0\u6124\uff0c\u8bdd\u4e00\u51fa\u53e3\uff0c\u5ffd\u7136\u60f3\u8d77\u5979\u7236\u4eb2\u4e5f\u662f\u72ec\u884c\u5927\u76d7\uff0c\u5fc3\u4e2d\u5927\u4e3a\u540e\u6094\uff0c\u53c8\u89c1\u5979\u8138\u8272 \u60e8\u767d\uff0c\u60f3\u8981\u5b89\u6170\u4e24\u53e5\uff0c\u4f46\u4e5f\u62c9\u4e0d\u4e0b\u8138\uff0c\u6240\u4ee5\u5f88\u662f\u72fc\u72c8\u3002 \u3000\u3000\u9ad8\u6218\u7b54\u9053\uff1a\u201c\u5c0f\u53ef\u9ad8\u6218\u3002\u201d \u3000\u3000\u5c11\u5973\u53c8\u9053\uff1a\u201c\u6211\u53eb\u59ec\u857e\u3002\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u6211\u5e08\u7236\u53eb\u6211\u5230\u676d\u5dde\u53bb\uff0c\u73b0\u5728\u7ea6\u671f\u5df1\u8fd1\uff0c\u6211\u5f97\u52a0\u7a0b\u8d76\u5f80\u3002\u201d \u3000\u3000\u59ec\u857e\u5947\u9041\uff1a\u201c\u4f60\u5e08\u7236\u65e2\u7136\u4e0d\u662f\u5929\u715e\u661f\u541b\uff0c\u90a3\u4e48\u5230\u5e95\u662f\u8c01\u5440\uff1f\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u6211\u5e08\u7236\u662f\u8fb9\u585e\u5927\u4fa0\u98ce\u67cf\u6768\u3002\u201d \u3000\u3000\u59ec\u857e\u6447\u6447\u5934\u9053\uff1a\u201c\u6211\u600e\u6ca1\u542c\u8fc7\u8fd9\u4e48\u4e00\u4e2a\u4eba\uff0c\u4ed6\u672c\u9886\u5927\u4e0d\u5927\uff1f\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u4ed6\u8001\u4eba\u5bb6\u529f\u529b\u6df1\u539a\uff0c\u5f53\u4e16\u6211\u4e0d\u51fa\u51e0\u4eba\uff0c\u5c31\u662f\u4eba\u4eba\u754f\u82e5\u86c7\u874e\u7684\u5929\u715e\u661f\u540e\uff0c\u6211\u77a7 \u4e5f\u4e0d\u662f\u4ed6\u8001\u4eba\u5bb6\u5bf9\u624b\u3002\u201d \u3000\u3000\u59ec\u857e\u4f38\u4f38\u820c\u5934\uff0c\u7b11\u9053\uff1a\u201c\u90a3\u4f60\u5e08\u7236\u672c\u9886\u771f\u5927\u7684\u5f88\uff0c\u9ad8\u2014\u2014\u9ad8\u5927\u54e5\uff0c\u96be\u602a\u4f60\u672c\u9886\u4e5f\u4e0d\u5c0f\uff0c \u90a3\u5929\u65e0\u654c\u795e\u62f3\u90fd\u5948\u4f60\u4e0d\u4f55\u3002\u201d \u3000\u3000\u9ad8\u6218\u542c\u4ed6\u558a\u81ea\u5df1\u201c\u5927\u54e5\u201d\uff0c\u5fc3\u4e2d\u4e00\u751c\uff0c\u5992\u6068\u6e10\u6d88\uff0c\u5c31\u9053\uff1a\u201c\u6211\u5e08\u7236\u5e76\u6ca1\u6709\u4f20\u6211\u4ec0\u4e48\uff0c\u4ed6 \u53ea\u6559\u4e86\u6211\u4e00\u5957\u5185\u529f\uff0c\u5982\u679c\u6211\u5b66\u4f1a\u4ed6\u8001\u4eba\u5bb6\u5de5\u592b\u4e09\u3001\u56db\u6210\uff0c\u90a3\u6279\u2014\u2014\u90a3\u6279\u4eba\u53c8\u5c82\u80fd\u62e6\u5f97\u4f4f \u6211\u3002\u201d \u3000\u3000\u4ed6\u5077\u773c\u4e00\u770b\u59ec\u96f7\uff0c\u8138\u4e0a\u767d\u4e2d\u900f\u7ea2\uff0c\u771f\u662f\u7f8e\u4e3d\u6781\u4e86\uff0c\u6b63\u4e13\u5fc3\u4e00\u81f4\u542c\u81ea\u5df1\u8bb2\u8bdd\uff0c\u5fc3\u4e2d\u4e0d\u7981\u6697 \u559c\u3002 \u3000\u3000\u59ec\u857e\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u4f60\u4e5f\u662f\u4ece\u5173\u5916\u6765\u7684\uff0c\u6211\u542c\u7239\u7239\u8bf4\u5173\u5916\u904d\u5730\u725b\u7f8a\uff0c\u5c71\u9ad8\u6c34\u6df1\uff0c\u771f\u662f\u58ee \u4e3d\u4e4b\u6781\uff0c\u51e0\u65f6\u4f60\u5e26\u6211\u53bb\u73a9\u73a9\u597d\u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u7b11\u9053\uff1a\u201c\u4f60\u7239\u7239\u6050\u6015\u8981\u6253\u6211\u54e9\uff01\u201d \u3000\u3000\u59ec\u857e\u6b63\u8272\u9053\uff1a\u201c\u6211\u7239\u6700\u75bc\u6211\uff0c\u6211\u8981\u6c42\u4ed6\u7684\u4e8b\uff0c\u4ece\u6765\u6ca1\u6709\u4e0d\u51c6\u7684\uff0c\u4ed6\u5982\u679c\u8fd8\u8981\u6253\u4f60\uff0c\u6211\u5c31 \u4e0d\u7406\u4ed6\uff0c\u4ed6\u6700\u6015\u6211\u4e0d\u7406\u4ed6\u54e9\u3002\u201d \u3000\u3000\u9ad8\u6218\u5927\u4e3a\u611f\u52a8\uff0c\u67d4\u58f0\u9053\uff1a\u201c\u4f60\u5f85\u6211\u771f\u597d\u3002\u201d \u3000\u3000\u59ec\u857e\u8138\u4e0a\u4e00\u7ea2\uff0c\u5fc3\u4e2d\u5374\u662f\u4e00\u751c\uff0c\u4f4e\u7740\u5934\u9053\uff1a\u201c\u9ad8\u5927\u53ef\uff0c\u4f60\u5e26\u6211\u5230\u676d\u5dde\u53bb\u73a9\u3002\u201d \u3000\u3000\u9ad8\u6218\u6447\u5934\u9053\uff1a\u201c\u4e0d\u884c\u4e0d\u884c\uff0c\u6211\u5e08\u7236\u8981\u5e26\u6211\u56de\u5230\u5173\u5916\u53bb\u5b66\u6b66\u529f\u7684\uff0c\u4f60\u8ddf\u53bb\u6709\u4ec0\u4e48\u610f\u601d\uff1f\u201d \u3000\u3000\u59ec\u857e\u4e0d\u4e50\uff0c\u561f\u5634\u9053\uff1a\u201c\u4f60\u4e0d\u5e26\u6211\u53bb\uff0c\u96be\u9053\u6211\u4e00\u4e2a\u4eba\u5c31\u4e0d\u80fd\u53bb\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u597d\u751f\u4e3a\u96be\uff0c\u5ffd\u7136\u60f3\u8d77\u6570\u65e5\u524d\u548c\u5979\u540c\u884c\u7684\u5c11\u5e74\uff0c\u4ed6\u6b63\u60f3\u5f00\u53e3\u76f8\u95f4\uff0c\u4f46\u968f\u53c8\u5fcd\u4f4f\u4e86\u3002 \u3000\u3000\u59ec\u857e\u89c1\u4ed6\u4e0d\u8bf4\u8bdd\uff0c\u4ee5\u4e3a\u4ed6\u751f\u6c14\u4e86\uff0c\u5fc3\u4e2d\u6709\u70b9\u4e0d\u5b89\uff0c\u5979\u81ea\u5df1\u4e5f\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48\u5e73\u7d20\u9a84\u7eb5\u5c0f\u6c14 \u7684\u813e\u6c14\uff0c\u5728\u8fd9\u5c11\u5e74\u4eba\u9762\u524d\u7adf\u7136\u4e00\u4e1d\u4e5f\u4e0d\u53d1\u4f5c\uff0c\u5979\u611f\u5230\u81ea\u5df1\u4ece\u672a\u5982\u6b64\u6e29\u67d4\u3002 \u3000\u3000\u59ec\u857e\u9053\uff1a\u201c\u6211\u6709\u4e00\u4e2a\u4e3b\u610f\u513f\uff0c\u54b1\u4eec\u4e00\u8d77\u8d76\u5230\u676d\u5dde\u53bb\uff0c\u5728\u90a3\u513f\u73a9\u4e0a\u51e0\u5929\uff0c\u7b49\u4f60\u5e08\u7236\u4e00\u5230\uff0c \u4f60\u8d70\u4f60\u7684\uff0c\u6211\u5c31\u56de\u5bb6\u53bb\u3002\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u4eba\u4ece\u676d\u5dde\u56de\u6d4e\u5357\uff0c\u8fd9\u4e00\u8def\u4e0a\u5e76\u4e0d\u5b89\u5b81\uff0c\u6211\u770b\u8fd8\u662f\u4ee5\u540e\u518d\u8bf4\u5427\u3002\u201d \u3000\u3000\u59ec\u857e\u9053\uff1a\u201c\u6211\u5982\u679c\u78b0\u5230\u574f\u4eba\uff0c\u53ea\u8981\u8bf4\u51fa\u6211\u7239\u7239\u7684\u540d\u5934\u3002\u8c05\u4ed6\u4eec\u4e5f\u4e0d\u6562\u628a\u6211\u600e\u6837\uff0c\u9ad8\u5927 \u54e5\uff0c\u54b1\u4eec\u8fd9\u5c31\u542f\u884c\u5427\u3002\u201d \u3000\u3000\u5979\u89c1\u9ad8\u6218\u5173\u5fc3\u81ea\u5df1\uff0c\u5f88\u662f\u5b89\u6170\u3002 \u3000\u3000\u9ad8\u6218\u65e0\u5948\uff0c\u53ea\u5f97\u5141\u8bb8\u3002\u59ec\u857e\u5ffd\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u4f60\u7b49\u7b49\u3002\u201d \u3000\u3000\u9ad8\u6218\u4e00\u6014\uff0c\u5979\u4e00\u52d2\u9a6c\u5411\u524d\u8dd1\u53bb\uff0c\u4e0d\u591a\u65f6\u7275\u6765\u4e00\u5339\u9a8f\u9a6c\uff0c\u5bf9\u9ad8\u6218\u9053\uff1a\u201c\u4f60\u770b\u8fd9\u9a6c\u600e\u6837\u3002\u54b1 \u4eec\u8981\u5148\u4f60\u5e08\u7236\u8d76\u5230\u676d\u57ce\uff0c\u6240\u4ee5\u6211\u5c31\u66ff\u4f60\u53bb\u9009\u4e86\u4e00\u5339\u597d\u9a6c\u3002\u201d \u3000\u3000\u9ad8\u6218\u89c1\u90a3\u9a6c\u9ad8\u5927\u795e\u9a8f\uff0c\u77e5\u975e\u51e1\u54c1\uff0c\u5fc3\u60f3\u5c31\u662f\u503e\u56ca\u4e5f\u672a\u5fc5\u4e70\u5f97\u8d77\u5982\u6b64\u597d\u9a6c\uff0c\u4ed6\u5929\u6027\u672c\u6781\u8c41 \u8fbe\uff0c\u63a5\u8fc7\u9a6c\u97ad\uff0c\u7eb5\u8eab\u9a91\u4e0a\uff0c\u53e3\u4e2d\u8fde\u9053\uff1a\u201c\u597d\u9a6c\uff0c\u597d\u9a6c\uff0c\u59ec\u59d1\u5a18\uff0c\u771f\u8c22\u8c22\u4f60\u5566\u3002\u201d \u3000\u3000\u59ec\u96f7\u542c\u4ed6\u4f9d\u7136\u59d1\u5a18\u957f\u59d1\u5a18\u77ed\u7684\u558a\uff0c\u5fc3\u4e2d\u672c\u6709\u4e9b\u4e0d\u4e50\uff0c\u4f46\u662f\u542c\u5230\u9ad8\u6218\u5bf9\u90a3\u5339\u9a6c\u8d5e\u53e3\u4e0d\u7edd\uff0c \u4e0d\u7981\u5341\u5206\u5f97\u610f\u3002 \u3000\u3000\u4e24\u4eba\u884c\u4e86\u6570\u65e5\uff0c\u5df1\u7ecf\u8d70\u8fd1\u6cb3\u5357\u6c5f\u82cf\u8fb9\u5883\uff0c\u9ad8\u6218\u628a\u5173\u5916\u98ce\u4fd7\u666f\u8272\uff0c\u90fd\u8be6\u7ec6\u7684\u8bb2\u7ed9\u59ec\u96f7\u542c\uff0c \u59ec\u96f7\u542c\u5230\u957f\u767d\u5c71\u4e0a\u7ec8\u5e74\u79ef\u96ea\uff0c\u677e\u82b1\u6c5f\u6d69\u6d69\u8361\u8361\uff0c\u5fc3\u4e2d\u5341\u5206\u5411\u5f80\u3002 \u3000\u3000\u59ec\u857e\u9053\uff1a\u201c\u6211\u4ece\u5c0f\u65f6\u5019\u5c31\u4f4f\u5728\u57ce\u91cc\uff0c\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\uff0c\u771f\u662f\u95f7\u6781\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u7b11\u9053\uff1a\u201c\u5173\u5916\u6700\u6709\u5404\u7684\u8fd8\u662f\u4e00\u671b\u65e0\u9645\u7684\u9752\u6c99\u5e10\u3002\u201d \u3000\u3000\u59ec\u857e\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u662f\u9752\u6c99\u5e10\u5440\uff01\u201d \u3000\u3000\u9ad8\u6218\u6697\u7b11\uff0c\u5fc3\u60f3\u9752\u6c99\u5e10\u5317\u65b9\u5230\u5904\u90fd\u662f\uff0c\u53ea\u4e0d\u8fc7\u6ca1\u6709\u50cf\u5173\u5916\u90a3\u6837\u8fde\u7ef5\u6570\u91cc\uff0c\u8fd9\u59d1\u5a18\u771f\u662f\u57ce \u4e2d\u4eba\u3002 \u3000\u3000\u59ec\u857e\u89c1\u4ed6\u542b\u7b11\u4e0d\u7b54\uff0c\u6c14\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u7b11\u7684\uff0c\u6211\u4e0d\u61c2\u5f53\u7136\u8981\u95ee\u3002\u201d \u3000\u3000\u9ad8\u6218\u7b54\u9053\uff1a\u201c\u9752\u6c99\u5e10\u5c31\u662f\u9ad8\u7cb1\u7530\u5440\uff0c\u56e0\u4e3a\u8fde\u5ef6\u4e00\u5927\u7247\u90fd\u662f\u9752\u7eff\u7eff\u7684\uff0c\u6240\u4ee5\u5c31\u53eb\u9752\u6c99 \u5e10\u3002\u201d \u3000\u3000\u59ec\u857e\u604d\u7136\u5927\u609f\uff0c\u7b11\u9053\uff1a\u201c\u8fd9\u4e2a\u540d\u5b57\u5012\u597d\u542c\uff0c\u5582\uff0c\u9ad8\u5927\u54e5\uff0c\u4f60\u5c0f\u65f6\u5019\u73a9\u8fc7\u6349\u8ff7\u85cf\u6ca1\u6709\uff1f\u201d \u3000\u3000\u9ad8\u6218\u70b9\u70b9\u5934\uff0c\u59ec\u857e\u53c8\u9053\uff1a\u201c\u4f60\u8bf4\u90a3\u9752\u6c99\u5e10\u4e00\u671b\u65e0\u9645\uff0c\u90a3\u4e48\u73a9\u8d77\u6349\u8ff7\u85cf\u6765\u5230\u662f\u4e2a\u8eb2\u85cf\u7684\u597d \u5730\u65b9\u3002\u201d \u3000\u3000\u9ad8\u6218\u89c1\u5979\u6ee1\u8138\u5929\u771f\uff0c\u4e0d\u7531\u5931\u7b11\uff0c\u59ec\u857e\u95ee\u9053\uff1a\u201c\u600e\u4e48\uff0c\u96be\u9053\u4e0d\u53ef\u4ee5\u4e48\uff1f\u201d \u3000\u3000\u9ad8\u6218\u5fae\u7b11\u9053\uff1a\u201c\u53ef\u4ee5\u53ef\u4ee5\uff0c\u53ea\u662f\u4f60\u4e00\u8eb2\u8fdb\u53bb\u522b\u4eba\u56fa\u7136\u627e\u4f60\u4e0d\u7740\uff0c\u4f60\u672c\u4eba\u6050\u6015\u4e5f\u96be\u5f97\u51fa\u6765 \u4e86\u3002\u201d \u3000\u3000\u59ec\u857e\u6447\u5934\u9053\uff1a\u201c\u771f\u7684\u8fd9\u4e48\u5389\u5bb3\u4e48\uff1f\u6211\u4e0d\u4fe1\u3002\u201d \u3000\u3000\u9ad8\u6218\u7b11\u7b11\u4e0d\u7b54\uff0c\u5ffd\u7136\u6307\u7740\u524d\u9762\u9053\uff1a\u201c\u7ffb\u8fc7\u8fd9\u5c71\uff0c\u5c31\u662f\u6c5f\u82cf\u5883\u5185\u4e86\u3002\u201d \u3000\u3000\u6b64\u65f6\u65e5\u6b63\u5f53\u4e2d\uff0c\u4e24\u4eba\u90fd\u89c9\u5f88\u95f7\u70ed\uff0c\u628a\u9a6c\u62f4\u5728\u6811\u4e0a\uff0c\u5c31\u5728\u6811\u4e0b\u4f11\u606f\uff0c\u9ad8\u6218\u5bf9\u59ec\u96f7\u9053\uff1a\u201c\u524d \u9762\u6c34\u58f0\u7011\u7011\uff0c\u5fc5\u7136\u6709\u6cc9\u6c34\u6d41\u51fa\uff0c\u6211\u53bb\u6253\u70b9\u6c34\u6765\uff0c\u4f60\u5750\u8fd9\u513f\u6b47\u6b47\u3002\u201d \u3000\u3000\u59ec\u857e\u867d\u8fde\u65e5\u8d76\u8def\uff0c\u5979\u4e0d\u50cf\u9ad8\u6218\u81ea\u5e7c\u7ec3\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u53c8\u98df\u5343\u5e74\u53c2\u738b\uff0c\u662f\u4ee5\u8eab\u4f53\u5f88\u662f\u75b2\u52b3\uff0c \u5c31\u4f9d\u9ad8\u6218\u7684\u8bdd\uff0c\u5750\u5728\u6811\u4e0b\uff0c\u95ed\u76ee\u517b\u795e\u3002 \u3000\u3000\u5979\u7b49\u4e86\u534a\u5929\uff0c\u5979\u4e0d\u89c1\u9ad8\u6218\u56de\u8f6c\uff0c\u5fc3\u4e2d\u5ffd\u7136\u70e6\u71e5\u8d77\u6765\uff0c\u4e5f\u5c31\u8d77\u8eab\u5411\u6797\u4e2d\u8d70\u53bb\u3002 \u3000\u3000\u5979\u60f3\u4e0d\u5230\u6797\u4e2d\u68d8\u523a\u8513\u751f\uff0c\u5979\u7236\u4eb2\u867d\u662f\u5927\u884c\u5bb6\uff0c\u53ef\u662f\u5979\u81ea\u5c0f\u4e0d\u7231\u7ec3\u6b66\uff0c\u6240\u4ee5\u8fde\u4ed6\u7236\u4eb2\u529f\u592b \u4e00\u6210\u4e5f\u6ca1\u5b66\u5230\uff0c\u6b64\u65f6\u89c1\u8def\u4e0a\u904d\u5730\u90fd\u662f\u68d8\u523a\uff0c\u7adf\u7136\u65e0\u6cd5\u4e3e\u6b65\u3002 \u3000\u3000\u5979\u7565\u4e00\u6c89\u541f\uff0c\u53ea\u89c9\u5fc3\u4e2d\u6108\u70e6\uff0c\u4eff\u4f5b\u5927\u7978\u65e2\u4e34\u5934\uff0c\u5ffd\u7136\u4e00\u58f0\u60ca\u53eb\u4f20\u6765\uff0c\u5979\u5fc3\u4e00\u9707\uff0c\u60f3\u9053\uff1a \u201c\u96be\u9053\u662f\u9ad8\u5927\u54e5\u9047\u9669\u4e86\uff1f\u201d \u3000\u3000\u60f3\u5230\u6b64\uff0c\u518d\u4e0d\u72b9\u8c6b\uff0c\u5c31\u5c55\u5f00\u8f7b\u529f\uff0c\u8e0f\u8457\u68d8\u523a\u524d\u8fdb\uff0c\u5979\u8f7b\u529f\u751a\u662f\u4f4e\u52a3\uff0c\u4e00\u7eb5\u4e0d\u8fc7\u4e00\u3001\u4e8c \u4e08\uff0c\u800c\u4e14\u6b65\u6cd5\u53c8\u4e0d\u51c6\u786e\uff0c\u90a3\u5c0f\u5f84\u539f\u662f\u5d0e\u5455\u4e0d\u5e73\uff0c\u53ef\u662f\u56e0\u4e3a\u690d\u7269\u4e1b\u751f\uff0c\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\u5730\u65b9\u7a81\u8d77 \u51f9\u4e0b\uff0c\u59ec\u82d7\u5f80\u5f80\u4e00\u7eb5\uff0c\u6b63\u597d\u843d\u5728\u5751\u5185\uff0c\u6b64\u65f6\u53c8\u662f\u590f\u5929\uff0c\u5979\u8863\u670d\u672c\u5c31\u5355\u8584\uff0c\u6240\u4ee5\u5168\u8eab\u90fd\u88ab\u5256 \u7834\uff0c\u624b\u4e0a\u66f4\u662f\u9c9c\u8840\u6dcb\u6f13\u3002 \u3000\u3000\u59ec\u857e\u611f\u5230\u6c57\u6c34\u6d78\u5728\u4f24\u53e3\u4e0a\uff0c\u706b\u8fa3\u7684\u523a\u75db\u6108\u6765\u6108\u52a0\u91cd\uff0c\u5979\u4e3e\u76ee\u4e00\u770b\uff0c\u957f\u9014\u832b\u832b\uff0c\u6839\u672c\u8fd8\u770b \u4e0d\u89c1\u4e00\u4e2a\u5c3d\u5934\uff0c\u5979\u5fc3\u4e2d\u4e00\u6c89\uff0c\u63a5\u7740\u4e00\u79cd\u65e0\u6bd4\u7684\u632f\u594b\u5145\u6ee1\u4e86\u5979\u5168\u8eab\u8840\u6db2\uff0c\u56e0\u4e3a\u5979\u4eff\u4f5b\u542c\u5230\u5979\u9ad8 \u5927\u54e5\u6b63\u5728\u547c\u6551\uff0c\u56e0\u4e3a\u5979\u4eff\u4f5b\u770b\u5230\u5979\u9ad8\u5927\u54e5\u6b63\u904d\u4f53\u9cde\u4f24\u7684\u671b\u7740\u5979\u8fd9\u8fb9\uff0c\u7b49\u5f85\u5979\u6765\u6551\u5f85\uff0c\u4e8e\u662f\uff0c \u5979\u9f13\u821e\u8d77\u6765\uff0c\u8d85\u4eba\u7684\u52c7\u6c14\u652f\u6301\u7740\u5979\uff0c\u5979\u5fd8\u4e86\u81ea\u5df1\u5e73\u65e5\u662f\u517b\u5c0a\u5904\u4f18\u7684\u5343\u91d1\uff0c\u4e5f\u5fd8\u4e86\u4ed6\u9ad8\u5927\u54e5\u662f \u4f55\u7b49\u529f\u592b\uff0c\u5982\u679c\u4ed6\u90fd\u65e0\u6cd5\u8131\u79bb\u5371\u5883\uff0c\u81ea\u5df1\u53c8\u600e\u4e48\u6210\u5462\uff1f\u5979\u53ea\u575a\u5b9a\u7740\u4e00\u4e2a\u4fe1\u5ff5\uff0c\u90a3\u5c31\u662f\u524d\u8fdb\u3002 \u3000\u3000\u597d\u957f\u7684\u8def\u54df\uff01\u6c34\u58f0\u867d\u7136\u5f88\u8fd1\uff0c\u53ef\u662f\u8f6c\u4e86\u4e00\u4e2a\u5f2f\uff0c\u53c8\u4e00\u4e2a\u5f2f\uff0c\u539f\u6765\u90a3\u6797\u4e2d\u8def\u9014\u66f2\u6298\uff0c\u4e1c\u5f2f \u897f\u8f6c\uff0c\u52a0\u4e0a\u91ce\u8349\u6a2a\u751f\uff0c\u660e\u77e5\u5c31\u5728\u524d\u9762\u4e0d\u8fdc\uff0c\u53ef\u662f\u8d70\u8d77\u6765\u5374\u8d70\u4e0a\u534a\u5929\u3002\u59ec\u96f7\u89c9\u5f97\u4f24\u53e3\u53d1\u9ebb\u4e86\uff0c \u5979\u54ac\u7d27\u7259\u6839\uff0c\u7ee7\u7eed\u524d\u8fdb\uff0c\u5ffd\u7136\u6c34\u58f0\u5982\u96f7\uff0c\u5929\u8272\u4e00\u4eae\uff0c\u67f3\u6697\u82b1\u660e\uff0c\u524d\u9762\u662f\u4e00\u5904\u5927\u7011\u5e03\u3002 \u3000\u3000\u59ec\u857e\u89c1\u76ee\u7684\u5730\u5df1\u8fbe\uff0c\u5fc3\u4e2d\u4e00\u677e\uff0c\u51e0\u4e4e\u5f53\u573a\u88c1\u5012\uff0c\u5979\u8fde\u5fd9\u4e00\u6276\u8eab\u65c1\u5c0f\u6811\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u4e3e\u76ee \u5411\u4e0b\u4e00\u770b\uff0c\u53ea\u89c9\u76ee\u7729\u80c6\u5bd2\u3002 \u3000\u3000\u539f\u6765\u90a3\u7011\u5e03\u4e0b\u662f\u4e2a\u6df1\u4e0d\u89c1\u5e95\u7684\u7edd\u8c37\uff0c\u8c37\u4e2d\u6c34\u6c14\u8ff7\u6726\uff0c\u5982\u70df\u5982\u96fe\uff0c\u4e5f\u4e0d\u77e5\u5012\u5e95\u6709\u591a\u6df1\uff0c\u7a81 \u7136\u4e00\u4e2a\u4e0d\u7965\u7684\u5ff5\u5934\u95ea\u8fc7\u5979\u5fc3\u5934\uff0c\u5979\u5413\u5f97\u54ed\u4e86\u8d77\u6765\u3002 \u3000\u3000\u5979\u662f\u5c71\u4e1c\u6d4e\u5357\u5927\u8c6a\u7684\u72ec\u751f\u5973\u513f\uff0c\u4ece\u5c0f\u5728\u7236\u6bcd\u767e\u822c\u5475\u62a4\u4e0b\u957f\u5927\uff0c\u51e0\u66fe\u901a\u8fc7\u8fd9\u822c\u9669\u6076\u4e4b\u4e8b\uff0c \u6b64\u65f6\u8352\u5c71\u4e4b\u4e2d\uff0c\u9ad8\u5927\u54e5\u53c8\u4e0d\u5728\u8eab\u65c1\uff0c\u5979\u6108\u54ed\u6108\u89c9\u5b64\u5355\u3002\u5979\u54ed\u4e86\u4e00\u4f1a\uff0c\u5ffd\u7136\u53d1\u89c9\u7011\u5e03\u8fb9\u4e0a\u6b65\u8ff9 \u6742\u4e71\uff0c\u6781\u50cf\u662f\u6709\u4eba\u5760\u843d\u7684\u6a21\u6837\uff0c\u518d\u56db\u4e0b\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u5757\u7a81\u8d77\u5927\u77f3\u4e0a\uff0c\u6709\u4e00\u4ef6\u7834\u70c2\u4e0d\u582a\u7684\u5916 \u8863\uff0c\u90a3\u6b63\u662f\u9ad8\u5927\u54e5\u6240\u7a7f\u7684\uff0c\u5979\u5fc3\u4e00\u6c89\uff0c\u5fd8\u8bb0\u4e86\u54ed\u6ce3\uff0c\u4f46\u89c9\u80f8\u4e2d\u4e00\u7247\u7a7a\u767d\uff0c\u4ec0\u4e48\u4e5f\u4e0d\u80fd\u60f3\uff0c\u826f \u4e45\uff0c\u5979\u5fc3\u5ff5\u4e00\u5b9a\uff0c\u5fd6\u9053\uff1a\u201c\u6211\u518d\u4ed4\u7ec6\u56db\u5468\u77a7\u4e00\u904d\uff0c\u5982\u679c\u4ecd\u7136\u627e\u4e0d\u5230\u9ad8\u5927\u54e5\uff0c\u90a3\u4e48\u4ed6\u591a\u534a\u662f\u5931 \u8db3\u60ac\u5d16\u4e86\uff0c\u6211\u4e5f\u5c31\u8df3\u4e0b\u53bb\u966a\u7740\u4ed6\u5427\u3002\u201d \u3000\u3000\u5979\u7236\u4eb2\u6d4e\u5357\u5927\u8c6a\u59ec\u672c\u5468\u867d\u7136\u5931\u8eab\u7eff\u6797\uff0c\u5374\u662f\u70ed\u8840\u6c49\u5b50\uff0c\u6027\u60c5\u4e2d\u4eba\uff0c\u4e00\u751f\u4e4b\u4e2d\u4e3a\u4eba\u4e4b\u5904\u5927 \u662f\u591a\u4e8e\u4e3a\u5df1\uff0c\u4e3a\u7b54\u77e5\u5df1\uff0c\u629b\u5934\u9885\uff0c\u6d12\u70ed\u8840\uff0c\u5728\u59ec\u672c\u5468\u8ba4\u4e3a\u662f\u7406\u6240\u5f53\u7136\uff0c\u4e0d\u5fc5\u8003\u8651\u4e4b\u4e8b\uff0c\u59ec\u857e \u5e74\u7eaa\u867d\u5c11\uff0c\u5374\u5c31\u9057\u4f20\u4ed6\u7236\u4eb2\u90a3\u79cd\u75f4\u60c5\u4efb\u6027\u7684\u6027\u513f\uff0c\u662f\u4ee5\u5979\u548c\u9ad8\u6218\u8ba4\u8bc6\u867d\u7136\u4e0d\u4e45\uff0c\u53ef\u662f\u4ed6\u5728\u5979 \u5fc3\u76ee\u4e2d\u5df2\u6210\u5076\u50cf\u3002 \u3000\u3000\u59ec\u857e\u5fc3\u5ff5\u4e00\u51b3\uff0c\u518d\u65e0\u65c1\u601d\uff0c\u5979\u6b64\u65f6\u60c5\u5df2\u6210\u75f4\uff0c\u5934\u8111\u5927\u662f\u660f\u4e71\uff0c\u5904\u5904\u5f80\u574f\u5904\u60f3\uff0c\u7ad9\u8d77\u6765\uff0c \u4fbf\u5728\u56db\u5468\u67e5\u627e\uff0c\u627e\u4e86\u534a\u5929\uff0c\u5e76\u4e0d\u89c1\u86db\u4e1d\u9a6c\u8ff9\uff0c\u5979\u5fc3\u4e2d\u6108\u6765\u6108\u51c9\uff0c\u6765\u56de\u5728\u7011\u5e03\u5468\u56f4\u54ed\u558a\u7740\u9ad8\u6218 \u7684\u540d\u5b57\uff0c\u90a3\u7a7a\u8c37\u56de\u97f3\uff0c\u51b7\u51b7\u4e0d\u7edd\uff0c\u4e00\u65f6\u95f4\u6574\u4e2a\u6797\u95f4\u5404\u5904\u90fd\u662f\u547c\u558a\u9ad8\u6218\u7684\u53eb\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\uff0c\u6d41 \u6c34\u4e4b\u58f0\u867d\u5927\uff0c\u5374\u4e5f\u63a9\u76d6\u4e0d\u4f4f\u3002 \u3000\u3000\u5915\u9633\u4f59\u8f89\u7167\u5728\u90a3\u7011\u5e03\u6fc0\u8d77\u7684\u6c34\u73e0\u4e0a\uff0c\u5e7b\u4ee3\u6210\u4e94\u989c\u516d\u8272\uff0c\u5929\u8fb9\u78a7\u84dd\uff0c\u666f\u8272\u7aef\u7684\u58ee\u4e3d\uff0c\u59ec\u857e \u5df2\u662f\u7cbe\u75b2\u529b\u5c3d\uff0c\u5979\u4e0b\u5b9a\u51b3\u5fc3\uff0c\u518d\u558a\u4e00\u6b21\uff0c\u5982\u679c\u9ad8\u6218\u518d\u6ca1\u6709\u7b54\u590d\uff0c\u5979\u5c31\u4e5f\u6295\u8eab\u6df1\u6e0a\uff0c\u5979\u9f13\u8db3\u771f \u6c14\uff0c\u53eb\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u9ad8\u6218\u5927\u54e5\uff0c\u4f60\u5728\u54ea\u91cc\u554a\uff1f\u201d \u3000\u3000\u826f\u4e45\uff0c\u5979\u542c\u5230\u4ece\u9065\u4f20\u7684\u5730\u65b9\u4f20\u6765\u540c\u6837\u53eb\u58f0\u3002 \u3000\u3000\u201c\u9ad8\u5927\u54e5\u2026\u2026\u9ad8\u6218\u5927\u54e5\uff0c\u4f60\u5728\u54ea\u91cc\u554a\uff01\u201d \u3000\u3000\u90a3\u58f0\u97f3\u4f3c\u4ece\u5929\u4e0a\u6765\uff0c\u4e00\u523b\u95f4\uff0c\u59ec\u96f7\u7a81\u53d1\u5947\u60f3\uff0c\u5979\u60f3\uff1a\u201c\u5927\u54e5\u6015\u5df2\u5230\u5929\u4e0a\u4e86\u7f62\u3002\u201d \u3000\u3000\u90a3\u5446\u5446\u671b\u7740\u5929\u9645\uff0c\u80f8\u4e2d\u4e00\u7247\u7a7a\u767d\uff0c\u4e0d\u81ea\u7531\u4e3b\u7684\u4e00\u6b65\u6b65\u8d70\u8fd1\u7edd\u5d16\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u4e0d\u89c9\u611f\u5230\u5bb3 \u6015\u3002 \u3000\u3000\u5979\u60f3\uff1a\u201c\u6211\u5982\u679c\u8fd9\u6837\u4e00\u8df3\uff0c\u5c31\u53ef\u4ee5\u548c\u9ad8\u5927\u54e5\u89c1\u9762\uff0c\u53ef\u662f\u7239\u7239\u5988\u5988\u4ee5\u540e\u7684\u65e5\u5b50\u600e\u4e48\u8fc7 \u5462\uff1f\u201d \u3000\u3000\u5411\u665a\u7684\u5fae\u98ce\u5439\u5f97\u5979\u8eab\u5e26\u98d8\u66f3\uff0c\u5982\u679c\u6709\u4eba\u6b64\u65f6\u7ecf\u8fc7\uff0c\u53ea\u5f53\u662f\u5ae1\u51e1\u4ed9\u5b50\uff0c\u4e34\u6df1\u6e0a\u98d8\u7136\u800c\u7acb\uff0c \u8c01\u53c8\u60f3\u5f97\u5230\u8fd9\u53ef\u601c\u7684\u5c11\u5973\uff0c\u6b63\u9762\u4e34\u751f\u4e0e\u6b7b\u7684\u6289\u62e9\u5462\uff1f \u3000\u3000\u59ec\u857e\u53c8\u60f3\uff1a\u201c\u5982\u679c\u6211\u4e0d\u8df3\uff0c\u90a3\u4e48\u65e5\u540e\u6b22\u4e50\u4fbf\u548c\u6211\u4e0d\u518d\u6709\u7f18\uff0c\u6d41\u5411\u5fc3\u4e2d\u7684\u773c\u6cea\u5c06\u65e0\u6cd5\u5ea6 \u91cf\u3002\u201d \u3000\u3000\u5ffd\u7136\u5979\u542c\u5230\u4e00\u58f0\u5947\u5f02\u7684\u58f0\u97f3\u4ece\u7011\u5e03\u4e0b\u4f20\u6765\uff0c\u5979\u5faa\u58f0\u4e00\u770b\uff0c\u7adf\u7136\u4e0d\u6562\u76f8\u4fe1\u81ea\u5df1\u773c\u6674\uff0c\u5979\u7528 \u529b\u7741\u773c\uff0c\u4ed4\u7ec6\u518d\u770b\uff0c\u767b\u65f6\u6b22\u559c\u65e0\u9650\u3002 \u3000\u3000\u5979\u8fd9\u5927\u60b2\u5927\u559c\u4e4b\u95f4\uff0c\u4eba\u5374\u518d\u4e5f\u652f\u6301\u4e0d\u4f4f\uff0c\u840e\u987f\u5012\u5730\uff0c\u8eab\u5f62\u79bb\u5f00\u60ac\u5d16\u4e5f\u4e0d\u8fc7\u534a\u5c3a\u5de6\u53f3\u3002 \u3000\u3000\u5979\u672c\u60f3\u7eb5\u58f0\u6b22\u547c\uff0c\u4f46\u7384\u770b\u4e0b\u9762\u60c5\u5f62\uff0c\u4e0d\u7981\u5fc3\u60ca\u8089\u8df3\uff0c\u7591\u4e91\u5927\u751f\uff0c\u53ea\u89c1\u9ad8\u6218\u80cc\u7740\u4e00\u4e2a\u5973 \u5b50\uff0c\u624b\u8db3\u5e76\u7528\u7684\u4ece\u60ac\u5d16\u4e0b\u6500\u767b\u4e0a\u6765\uff0c\u5979\u5fc3\u77e5\u5979\u5982\u679c\u9ad8\u58f0\u4e00\u547c\uff0c\u5f15\u5f97\u9ad8\u6218\u5fc3\u795e\u4e00\u758f\uff0c\u5c31\u5f88\u53ef\u80fd \u5931\u8db3\u6ed1\u4e0b\uff0c\u662f\u4ee5\u5fcd\u8457\u6ee1\u8154\u7591\u5fc3\uff0c\u773c\u7741\u7741\u7684\u76ef\u7740\u9ad8\u6218\u6e10\u6e10\u4e0a\u5347\u7684\u8eab\u5f62\uff0c\u9ad8\u6218\u5185\u529b\u867d\u7136\u6df1\u6e5b\uff0c\u53ef \u662f\u80cc\u540e\u80cc\u8d1f\u7740\u4e00\u4e2a\u4eba\uff0c\u8fd9\u4f55\u4e0a\u6500\u767b\uff0c \u3000\u3000\u6700\u662f\u8017\u8d39\u771f\u529b\uff0c\u662f\u4ee5\u6bcf\u722c\u6570\u4e08\uff0c\u5c31\u5f97\u4f11\u606f\u4e00\u4f1a\uff0c\u90a3\u60ac\u5d16\u672c\u6df1\uff0c\u4ed6\u4e13\u5fc3\u4e00\u610f\u7684\u722c\u4e86\u534a\u664c\uff0c \u4e5f\u53ea\u722c\u5230\u534a\u8170\u3002 \u3000\u3000\u59ec\u857e\u4ed4\u7ec6\u4e00\u770b\uff0c\u5fc3\u4e2d\u6e10\u6e10\u5b89\u5b9a\uff0c\u539f\u6765\u90a3\u60ac\u5d16\u867d\u7136\u9661\u76f4\uff0c\u56e0\u4e3a\u6c34\u6d41\u65e5\u591c\u51b2\u51fb\uff0c\u6bcf\u9694\u4e0d\u8fdc\u5c31 \u6709\u51f9\u51f8\u53ef\u501f\u529b\u7684\u77f3\u6d1e\u548c\u5ca9\u77f3\u3002 \u3000\u3000\u5979\u5fc3\u60c5\u65e2\u5b9a\uff0c\u5ffd\u7136\u5992\u610f\u5927\u76db\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5973\u5b50\u4e0d\u77e5\u662f\u4f55\u4eba\uff0c\u9ad8\u5927\u54e5\u8fd9\u6837\u4e0d\u8981\u547d\u7684\u6551\u5979\uff0c \u54fc\uff0c\u4ed6\u663e\u7136\u5e76\u4e0d\u628a\u6211\u653e\u5728\u5fc3\u4e0a\uff0c\u5426\u5219\uff0c\u5426\u5219\uff0c\u5c31\u4e0d\u60f3\u60f3\u6211\u5b64\u8eab\u7b49\u5f85\u7740\u4ed6\uff0c\u4ed6\u5374\u5192\u751f\u547d\u5371\u9669\u53bb \u6551\u4e0d\u76f8\u5173\u7684\u5973\u5b69\u5b50\u3002\u201d \u3000\u3000\u5979\u770b\u770b\u592a\u9633\u6e10\u6e10\u5411\u897f\u504f\u53bb\uff0c\u90a3\u7011\u5e03\u7684\u6c34\u58f0\u4e5f\u6108\u6765\u6108\u5c0f\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5927\u5947\uff0c\u5979\u5c31\u8ddf\u7740\u592a\u9633\u7684 \u5f71\u5b50\u5411\u897f\u8d70\u53bb\uff0c\u53ea\u89c1\u897f\u8fb9\u7684\u6d41\u6c34\u5374\u5982\u5343\u519b\u4e07\u9a6c\uff0c\u58f0\u52bf\u96c4\u58ee\uff0c\u4e0d\u89c9\u4eff\u4f5b\u5927\u609f\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u7011\u5e03 \u7684\u6d41\u6c34\u662f\u968f\u7740\u592a\u9633\u7684\u5f71\u5b50\u79fb\u52a8\u7684\uff0c\u90a3\u4e48\u65e9\u4e0a\u4e1c\u8fb9\u6c34\u5927\uff0c\u4e0b\u5348\u897f\u8fb9\u6c34\u5927\u3002\u9ad8\u5927\u54e5\u4e00\u5b9a\u8981\u4e3a\u4e86\u6551 \u90a3\u5973\u5b50\uff0c\u5760\u8eab\u8c37\u4e2d\uff0c\u8fd9\u5d16\u4e2d\u602a\u77f3\u78f7\u78f7\uff0c\u9ad8\u5927\u54e5\u5b9a\u662f\u6293\u4f4f\u4e86\u9644\u8eab\u7684\u77f3\u5757\uff0c\u53ef\u662f\u56e0\u4e3a\u6c34\u52bf\u592a\u5927\uff0c \u4e00\u76f4\u4e0d\u80fd\u4e0a\u722c\uff0c\u60f3\u4e0d\u5230\u8fd9\u7011\u5e03\u5176\u602a\uff0c\u4e0d\u7136\uff0c\u9ad8\u5927\u54e5\u53ef\u5c31\u5b8c\u4e86\u3002\u201d \u3000\u3000\u5979\u5929\u8d44\u662f\u7edd\u9876\u806a\u654f\uff0c\u6b64\u4e00\u63a8\u6d4b\uff0c\u7ade\u4e2d\u5341\u4e4b\u4e03\u516b\uff0c\u5979\u56de\u8eab\u4e00\u770b\u9ad8\u6218\uff0c\u4f3c\u4e4e\u771f\u529b\u4e0d\u7ee7\uff0c\u4e0a\u5347 \u901f\u5ea6\u6108\u6765\u6108\u6162\uff0c\u7a81\u7136\u4e00\u5931\u624b\uff0c\u59ec\u857e\u53cc\u773c\u4e00\u95ed\uff0c\u4e0d\u6562\u518d\u770b\uff0c\u4f46\u662f\u8fc7\u4e86\u4e00\u4f1a\u3001\u5e76\u6ca1\u6709\u60ca\u53eb\u4e4b\u58f0\uff0c \u5979\u7741\u5f00\u773c\u6674\u53ea\u89c1\u9ad8\u6218\u62b1\u7740\u4e00\u5757\u5c16\u77f3\uff0c\u4e0d\u505c\u7684\u7aef\u606f\u7740\u3002 \u3000\u3000\u5979\u5fc3\u4e2d\u5927\u75db\uff0c\u5bf9\u90a3\u5973\u5b50\u771f\u662f\u6068\u4e4b\u5982\u9aa8\uff0c\u6b63\u81ea\u76d8\u7b97\u5982\u4f55\u4f7f\u9ad8\u5927\u53ef\u65e9\u8131\u9669\u5883\uff0c\u9ad8\u6218\u53c8\u5f00\u59cb\u6162 \u6162\u56de\u4e0a\u722c\u3002 \u3000\u3000\u201c\u8fd8\u6709\u56db\u4e08\uff0c\u8001\u5929\u7237\u4fdd\u4f51\uff0c\u4e09\u4e08\u534a\uff0c\u4e09\u4e08\uff0c\u4e24\u4e08\u2026\u2026\u597d\u9a6c\u4e0a\u5c31\u8981\u5230\u4e86\u3002\u201d\u59ec\u857e\u5583\u5583\u9053\uff0c \u5979\u611f\u5230\u624b\u5fc3\u4e00\u51c9\uff0c\u539f\u6765\u53cc\u624b\u65e9\u5df1\u6c57\u900f\u3002 \u3000\u3000\u59ec\u857e\u770b\u6e05\u90a3\u5973\u5b50\u7684\u80cc\u5f71\uff0c\u53ea\u89c9\u751a\u662f\u82d7\u6761\uff0c\u5979\u5992\u5ff5\u65e9\u751f\uff0c\u53ea\u662f\u9002\u624d\u4e00\u9635\u60ca\u9669\uff0c\u5fc3\u65e0\u65c1\u987e\uff0c \u6b64\u65f6\u89c1\u9ad8\u6218\u5d16\u9876\u53ea\u6709\u51e0\u5c3a\uff0c\u5fc3\u4e2d\u60f3\u9053\uff1a\u201c\u6211\u4e14\u8eb2\u5728\u65c1\u8fb9\uff0c\u770b\u770b\u8fd9\u5973\u5b50\u5012\u5e95\u548c\u9ad8\u5927\u54e5\u662f\u4f55\u5173 \u7cfb\uff0c\u503c\u5f97\u5982\u6b64\u820d\u547d\u6551\u5979\u3002\u201d \u3000\u3000\u5979\u89c1\u9ad8\u6218\u7ec8\u4e8e\u4e0a\u4e86\u5d16\uff0c\u5c31\u95ea\u8eab\u6797\u5185\uff0c\u6ce8\u610f\u4ed6\u4eec\u7684\u884c\u52a8\u3002 \u3000\u3000----------- \u3000\u3000\u6b66\u4fa0\u6797 \u626b\u6821 \u7b2c\u5341\u516b\u7ae0>> \xb7 \u53e4\u9f99\u300a\u5251\u6bd2\u6885\u9999\u300b \u7b2c\u5341\u516b\u7ae0 \u3000\u3000\u59ec\u857e\u6ee1\u6000\u5992\u610f\u95ea\u8eab\u5165\u6797\uff0c\u53ea\u89c1\u9ad8\u6218\u624b\u4e2d\u4ecd\u7136\u62b1\u7740\u90a3\u4f4d\u59d1\u5a18\uff0c\u5411\u5979\u8fd9\u8fb9\u8d70\u6765\uff0c\u59ec\u857e\u4ee5\u4e3a\u9ad8 \u6218\u5df2\u7136\u53d1\u89c9\u81ea\u5df1\u85cf\u8eab\u4e4b\u5904\uff0c\u5fc3\u60f3\u53cd\u6b63\u4e0d\u80fd\u5077\u542c\u4ed6\u4eec\u8bb2\u8bdd\uff0c\u6b63\u60f3\u73b0\u8eab\u76f8\u89c1\uff0c\u5ffd\u89c1\u9ad8\u6218\u597d\u50cf\u75b2\u5026 \u6781\u4e86\uff0c\u8f7b\u8f7b\u628a\u624b\u4e2d\u5c11\u5973\u653e\u4e0b\uff0c\u4ed6\u81ea\u5df1\u4e5f\u5750\u4e0b\u6765\u4f11\u606f\u3002 \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u6211\u5bb6\u5c31\u5728\u524d\u9762\u4e0d\u8fdc\uff0c\u4f60\u966a\u6211\u56de\u53bb\u5427\uff0c\u7239\u7239\u89c1\u6211\u4e45\u4e45\u4e0d\u56de\u5bb6\uff0c\u4e00\u5b9a \u6025\u574f\u4e86\uff0c\u5443\uff0c\u4f60\u4e5f\u8be5\u6362\u4ef6\u8863\u670d\u3002\u201d \u3000\u3000\u59ec\u857e\u5fc3\u4e2d\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u539f\u6765\u5979\u548c\u9ad8\u5927\u53ef\u662f\u65e7\u8bc6\u3002\u201d \u3000\u3000\u9ad8\u6218\u6447\u5934\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e2a\u540c\u4f34\u5728\u6797\u5916\u7b49\u6211\uff0c\u6211\u53bb\u544a\u8bc9\u5979\u4e00\u58f0\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u9053\uff1a\u201c\u4e0d\u7528\u8fd9\u4e48\u5fd9\u5440\uff01\u4ece\u8fd9\u7011\u5e03\u8fb9\u8f6c\u4e2a\u5f2f\u513f\u5c31\u662f\u6211\u5bb6\uff0c\u54b1\u4eec\u5148\u5230\u5bb6\u53bb\uff0c\u4f60\u6362\u4ef6\u8863 \u886b\uff0c\u4f11\u606f\u4e00\u4f1a\uff0c\u518d\u53bb\u628a\u4f60\u670b\u53cb\u5e26\u6765\u4e0d\u597d\u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u4e0d\u8bed\uff0c\u4ed6\u81ea\u5fd6\u6b64\u65f6\u7b4b\u75b2\u529b\u6b47\uff0c\u5b9e\u5728\u96be\u4ee5\u6e21\u8fc7\u90a3\u8346\u6ee1\u5e03\u7684\u6811\u6797\u3002 \u3000\u3000\u5c11\u5973\u5ffd\u7136\u67d4\u58f0\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u4f60\u820d\u547d\u6551\u6211\uff0c\u6211\u5fc3\u4e2d\u81ea\u7136\u611f\u6fc0\uff0c\u81ea\u4ece\u2026\u2026\u81ea\u4ece\u4f60\u8d70\u4e86\u4ee5 \u540e\uff0c\u6211\u4eec\u5168\u5bb6\u90fd\u60e6\u5ff5\u7740\u4f60\uff0c\u524d\u5929\u54b1\u4eec\u8c08\u8d77\u4f60\uff0c\u59b9\u59b9\u8fd8\u5728\u540e\u6094\u5f85\u4f60\u592a\u4e0d\u597d\uff0c\u6211\u2026\u2026\u6211\u4ece\u524d\u4e5f\u5bf9 \u4f60\u5f88\u51f6\uff0c\u4f60\u8fd8\u60f3\u6211\u4eec\u4e0d\uff1f\u201d \u3000\u3000\u9ad8\u6218\u6fc0\u52a8\u5730\u8fc7\uff1a\u201c\u6797\u2026\u2026\u6797\u59d1\u5a18\uff0c\u4f60\u4eec\u5f85\u6211\u6709\u5982\u4e00\u5bb6\u4eba\uff0c\u6211\u5c82\u4e0d\u77e5\uff0c\u6211\u539f\u60f3\u6b66\u827a\u7ec3\u6210\uff0c \u518d\u56de\u5173\u5916\u77a7\u4f60\u4eec\uff0c\u60f3\u4e0d\u5230\u4f1a\u5728\u6b64\u5730\u76f8\u9022\uff0c\u6797\u4f2f\u4f2f\u597d\u5417\uff1f\u201d \u3000\u3000\u5c11\u5973\u4f4e\u58f0\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u4f60\u4ece\u5c0f\u558a\u6211\u4ec0\u4e48\uff0c\u73b0\u5728\u5c31\u53eb\u6211\u4ec0\u4e48\uff0c\u7239\u7239\u56e0\u4e3a\u53d7\u4e0d\u4e0b\u9791\u5b50\u7684\u6028 \u6c14\uff0c\u8fd9\u624d\u80cc\u4e95\u79bb\u5bb6\uff0c\u6765\u5230\u5173\u5185\uff0c\u60f3\u4e0d\u5230\u8fd9\u5317\u65b9\u5230\u5904\u6d41\u5bc7\u4f5c\u4e71\uff0c\u65e0\u5904\u53ef\u4ee5\u5b89\u5c45\uff0c\u6240\u4ee5\u53ea\u6709\u9690\u5c45 \u6b64\u5904\uff0c\u81ea\u5df1\u5f00\u8f9f\u4e86\u51e0\u4ea9\u5c71\u7530\u3002\u201d \u3000\u3000\u9ad8\u6218\u95ee\u9053\uff1a\u201c\u4f60\u59b9\u59b9\u8fd8\u662f\u90a3\u6837\u2026\u2026\u90a3\u6837\u987d\u76ae\u4e48\uff1f\u201d \u3000\u3000\u5c11\u5973\u7b11\u9053\uff1a\u201c\u4f60\u60f3\u5979\u4f1a\u53d8\u4e56\u5417\uff1f\u201d \u3000\u3000\u5c11\u5973\u53c8\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u4f60\u672c\u4e8b\u5df2\u7ecf\u591f\u5927\u5566\uff0c\u4f60\u521a\u624d\u80cc\u7740\u6211\u4ece\u8c37\u5e95\u6500\u767b\u3001\u6211\u867d\u7136\u770b\u4e0d\u61c2\u662f \u4ec0\u4e48\u529f\u592b\uff0c\u53ef\u662f\u6211\u77e5\u9053\u90a3\u4e00\u5b9a\u662f\u4e86\u4e0d\u8d77\u7684\u6b66\u529f\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u77e5\u5979\u5bf9\u6b66\u529f\u4e00\u7a8d\u4e0d\u901a\uff0c\u5fae\u5fae\u4e00\u7b11\uff0c\u5c11\u5973\u63a5\u7740\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5df2\u7ec3\u597d\u6b66\u827a\uff0c\u5c31\u522b\u5230\u5904 \u4e71\u95ef\uff0c\u548c\u6211\u4eec\u4f4f\u5728\u4e00\u8d77\u53ef\u597d\uff1f\u5927\u54e5\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u6211\u5dee\u4e0d\u591a\u6bcf\u5929\uff0c\u2026\u2026\u6bcf\u5929\u90fd\u5728\u60f3\uff0c\u4ece\u524d\u54b1\u4eec \u5728\u4e00\u8d77\u73a9\u662f\u591a\u4e48\u2026\u2026\u591a\u4e48\u5feb\u4e50\uff0c\u6211\u2026\u2026\u6211\u2026\u2026\u201d \u3000\u3000\u5979\u6108\u8bf4\u6108\u4f4e\uff0c\u9ad8\u6218\u5fc3\u4e2d\u5927\u5947\uff0c\u62ac\u773c\u4e00\u770b\uff0c\u53ea\u89c1\u5979\u8138\u4e0a\u60a0\u7136\u795e\u5f80\uff0c\u5fc3\u4e2d\u4e0d\u7531\u4e00\u52a8\u3002 \u3000\u3000\u59ec\u857e\u8eb2\u5728\u6811\u540e\uff0c\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5979\u542c\u5230\u90a3\u5c11\u5973\u7a81\u7136\u541e\u541e\u5410\u5410\uff0c\u4fbf\u8f7b\u8f7b\u62e8\u5f00\u6811\u53f6\uff0c\u5077\u77a7\u4e00 \u77a7\uff0c\u4f46\u89c1\u90a3\u5c11\u5973\u7ea2\u6655\u65f6\u9732\uff0c\u559c\u6c14\u6d0b\u6d0b\uff0c\u4f3c\u4e4e\u6b63\u5728\u5411\u5f80\u7740\u5e78\u798f\u7684\u672a\u6765\u3002 \u3000\u3000\u59ec\u857e\u672c\u662f\u4e2a\u5343\u4f36\u767e\u5de7\u7684\u4eba\uff0c\u5979\u81ea\u5df1\u4e5f\u662f\u4e2a\u5973\u5b69\u513f\uff0c\u5bf9\u4e8e\u8fd9\u79cd\u5973\u513f\u5fc3\u4e8b\u5982\u4f55\u4e0d\u660e\u4e86\uff0c\u5f53\u4e0b \u4e0d\u7531\u5927\u4e3a\u9707\u52a8\uff0c\u5fc3\u60f3\uff1a\u201c\u8fd9\u4e2b\u5934\u539f\u6765\u662f\u5927\u54e5\u5e7c\u5e74\u4f34\u4fa3\uff0c\u770b\u5979\u90a3\u6a21\u6837\u5206\u660e\u662f\u559c\u6b22\u4e0a\u5927\u54e5\u4e86\u3002\u201d \u3000\u3000\u59ec\u857e\u51e0\u4e4e\u60f3\u633a\u8eab\u8d70\u51fa\uff0c\u4f46\u662f\u4e00\u79cd\u65e0\u8c13\u7684\u76f4\u5c0a\u5fc3\u5374\u963b\u7740\u5979\uff0c\u5979\u60f3\u9053\uff1a\u201c\u6211\u5fc3\u4e2d\u53ea\u6709\u5927\u54e5\u4e00 \u4eba\uff0c\u6211\u5012\u8981\u77a7\u77a7\u5927\u54e5\u5fc3\u91cc\u662f\u5426\u53ea\u6709\u4e00\u4e2a\u6211\u3002\u201d \u3000\u3000\u90a3\u5c11\u5973\u89c1\u9ad8\u6218\u5446\u5446\u542c\u7740\uff0c\u4f3c\u4e4e\u65e0\u52a8\u4e8e\u8877\uff0c\u4e0d\u89c9\u6709\u4e9b\u607c\u6012\uff0c\u59ec\u857e\u770b\u5728\u8ddf\u91cc\u3002\u5fc3\u4e2d\u6697\u559c\u3002 \u3000\u3000\u5c11\u5973\u5ffd\u9053\uff1a\u201c\u54b1\u4eec\u8d76\u5feb\u8d70\u7f62\uff0c\u4f60\u6362\u4e86\u8863\u670d\u5c31\u53bb\u627e\u4f60\u7684\u670b\u53cb\uff0c\u522b\u8ba9\u4ed6\u4e45\u7b49\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u4f11\u606f\u4e86\u4e00\u523b\uff0c\u5df1\u7ecf\u6e10\u6e10\u6062\u590d\uff0c\u4ed6\u770b\u770b\u81ea\u5df1\u8863\u886b\u7834\u7834\u70c2\u70c2\uff0c\u5fc3\u60f3\u8fd9\u4e2a\u6837\u5b50\u7ed9\u59ec\u857e\u770b\u89c1 \u4e0d\u5927\u597d\uff0c\u5c31\u6276\u8d77\u5c11\u5973\uff0c\u5411\u524d\u8d70\u53bb\u3002 \u3000\u3000\u59ec\u857e\u770b\u5230\u4e24\u4eba\u8f6c\u4e86\u4e2a\u5f2f\u5c31\u6d88\u5931\u4e86\uff0c\u5fc3\u4e2d\u5ffd\u611f\u5b64\u5355\uff0c\u5979\u867d\u77e5\u9ad8\u6218\u9a6c\u4e0a\u4fbf\u4f1a\u5f52\u6765\uff0c\u53ef\u662f\u4e0d\u77e5 \u600e\u7684\uff0c\u8001\u662f\u5c40\u4fc3\u4e0d\u5b89\uff0c\u671b\u7740\u65e2\u5c06\u5782\u9ed1\u7684\u5929\u9645\uff0c\u80e1\u601d\u4e71\u60f3\u8d77\u6765\u3001 \u3000\u3000\u5979\u5148\u60f3\u5230\u6d4e\u5357\u57ce\u5916\u8fd9\u534e\u4e3d\u7684\u5ead\u9662\uff0c\u4e00\u6761\u690d\u6ee1\u7fe0\u7af9\u7684\u5e7d\u5f84\uff0c\u76f4\u901a\u5230\u4e00\u5c0f\u5de7\u7684\u697c\u623f\u3002 \u3000\u3000\u201c\u5c3d\u7ba1\u662f\u4ef2\u590f\u8fb9\u9645\uff0c\u90a3\u5c0f\u697c\u4ecd\u7136\u6e05\u51c9\u5982\u79cb\uff0c\u5fae\u98ce\u5439\u6765\uff0c\u82b1\u9999\u9635\u9635\uff0c\u9633\u5149\u900f\u8fc7\u78a7\u7eb1\uff0c\u6de1\u6de1 \u7684\u6652\u5e03\uff0c\u90a3\u5f71\u8272\u771f\u50cf\u6c34\u6676\u5bab\u4e00\u822c\u3002\u201d \u3000\u3000\u5979\u60f3\uff0c\u201c\u6211\u5c31\u5728\u90a3\u5ea6\u8fc7\u4e86\u5341\u591a\u4eca\u5e74\u534e\uff0c\u7239\u7239\u6559\u6211\u6b66\u827a\uff0c\u5988\u5988\u6559\u6211\u5ff5\u4e66\uff0c\u9752\u513f\u90a3\u5c0f\u9b3c\u7cbe\u7075 \u4e2b\u5934\uff0c\u6210\u5929\u966a\u4f34\u7740\u6211\uff0c\u6316\u7a7a\u5fc3\u601d\u6765\u8bf4\u7b11\u8ba8\u597d\uff0c\u751f\u6d3b\u5012\u4e5f\u8fc7\u5f97\u5f88\u4e0d\u5bc2\u5bde\u3002\u201d \u3000\u3000\u6c34\u58f0\u6108\u6765\u6108\u8fdc\uff0c\u5929\u9645\u51fa\u73b0\u7b2c\u5404\u9897\u5c0f\u661f\uff0c\u59ec\u857e\u77e5\u9053\u5927\u9633\u5df2\u7ecf\u4e0b\u5c71\uff0c\u5979\u76ee\u4e0d\u8f6c\u77ac\u7684\u770b\u7740\u524d \u9762\uff0c\u53e3\u4e2d\u5583\u5583\u9053\uff1a\u201c\u600e\u4e48\u8fd8\u4e0d\u6765\u5462\uff1f\u96be\u9053\u4ed6\u771f\u7684\u4e1d\u6beb\u4e0d\u628a\u6211\u653e\u5728\u5fc3\u4e0a\uff1f\u201d \u3000\u3000\u5979\u80f8\u4e2d\u5992\u610f\u6f8e\u6e43\uff0c\u5fd6\u9053\uff1a\u201c\u521a\u624d\u5982\u679c\u6211\u518d\u627e\u4e0d\u5230\u4ed6\uff0c\u5c31\u51e0\u4e4e\u6d8c\u8eab\u4e00\u8df3\uff0c\u53ef\u662f\u2026\u2026\u53ef \u662f\u2026\u2026\uff0c\u5c31\u662f\u4e45\u522b\u53d9\u65e7\uff0c\u4e5f\u5f97\u5148\u544a\u77e5\u6211\u4e00\u58f0\uff0c\u514d\u5f97\u8ba9\u6211\u4e45\u7b49\u6302\u5ff5\u5440\uff01\u201d \u3000\u3000\u5979\u51e0\u4e4e\u60f3\u8f6c\u8eab\u79bb\u53bb\uff0c\u7136\u800c\u5fc3\u77e5\u6b64\u4e8b\u5173\u7cfb\u7740\u81ea\u5df1\u4e00\u751f\uff0c\u5fc3\u4e2d\u6697\u4e0b\u51b3\u5b9a\uff01 \u3000\u3000\u201c\u5f53\u542c\u4e0d\u5230\u6c34\u58f0\u7684\u65f6\u5019\uff0c\u5982\u679c\u5927\u54e5\u518d\u4e0d\u6765\uff0c\u90a3\u4e48\u6211\u4fbf\u8d70\u5427\uff0c\u8fd9\u4e2a\u8eab\u5b50\uff0c\u5c31\u5728\u6c5f\u6e56\u4e0a\u98d8\u6cca \u7b97\u4e86\u3002\u201d \u3000\u3000\u5ffd\u7136\u4e24\u53ea\u5c0f\u677e\u9f20\u4ece\u6811\u4e0a\u6e9c\u4e0b\u6765\uff0c\u8d3c\u773c\u707c\u707c\u7684\u6ce8\u89c6\u7740\u5979\u8ddf\u524d\u7684\u4e00\u679a\u677e\u5b50\uff0c\u59ec\u857e\u8f7b\u8f7b\u7684\u628a\u677e \u5b50\u5411\u677e\u9f20\u629b\u53bb\uff0c\u770b\u5230\u5b83\u4eec\u4e89\u593a\u7740\uff0c\u4e0d\u7531\u53c8\u60f3\u8d77\u5e7c\u65f6\u7684\u73a9\u610f\u513f\u3002 \u3000\u3000\u201c\u6211\u5c0f\u65f6\u5019\u6700\u7231\u73a9\u6597\u87cb\u87c0\uff0c\u6211\u7684\u87cb\u87c0\u603b\u662f\u6700\u5389\u5bb3\u7684\uff0c\u9644\u8fd1\u5b69\u5b50\u7684\u87cb\u87c0\u6ca1\u6709\u4e00\u4e2a\u6253\u5f97\u8d62\u6211 \u7684\uff0c\u56e0\u4e3a\u90a3\u662f\u4e24\u4e2a\u5e08\u54e5\u4ece\u5c71\u4e0a\u6349\u6765\u7684\uff0c\u5927\u5e08\u54e5\uff0c\u4e8c\u5e08\u53ef\u6bd4\u6211\u90fd\u5927\u4e94\u3001\u516d\u5c81\uff0c\u4ed6\u4eec\u4ece\u5c0f\u5c31\u5bf9\u6211 \u597d\uff0c\u6211\u8bf4\u4ec0\u4e48\u8981\u4ec0\u4e48\uff0c\u4ed6\u4eec\u90fd\u662f\u767e\u4f9d\u767e\u987a\uff0c\u5343\u65b9\u767e\u8ba1\u66ff\u6211\u627e\u6765\uff0c\u53ef\u662f\u6211\u4e0d\u559c\u6b22\u5927\u5e08\u54e5\u90a3\u79cd\u9634 \u6c89\u6027\u683c\uff0c\u4e8c\u5e08\u54e5\u90a3\u79cd\u66b4\u71e5\u813e\u6c14\uff0c\u5012\u662f\u4e09\u5e08\u5144\u548c\u6211\u6bd4\u8f83\u73a9\u5f97\u6765\u3002\u201d \u3000\u3000\u6708\u4eae\u4ece\u5c71\u5dc5\u9732\u4e86\u51fa\u4e0d\uff0c\u6816\u6797\u7684\u5f52\u9e1f\u5431\u5431\u5730\u53eb\u7740\uff0c\u6253\u88ab\u4e86\u56db\u5468\u7684\u5bc2\u9759\uff0c\u59ec\u857e\u4ece\u5e7b\u5883\u4e2d\u56de\u5230 \u73b0\u5b9e\uff0c\u7ec6\u542c\u7740\u8fd8\u6709\u4e9b\u8bb8\u6c34\u58f0\uff0c\u5fc3\u4e0b\u7565\u5b89\uff0c\u53c8\u4ece\u73b0\u5b9e\u8dcc\u5165\u56de\u5fc6\u3002 \u3000\u3000\u201c\u6211\u56b7\u7740\u7239\u7239\u5e26\u6211\u51fa\u53bb\u89c1\u8bc6\uff0c\u7239\u88ab\u6211\u78e8\u5f97\u6ca1\u529e\u6cd5\uff0c\u5c31\u5e26\u6211\u53bb\u53c2\u52a0\u5317\u65b9\u7eff\u6797\u5927\u4f1a\uff0c\u8fd9\u662f\u6211 \u7b2c\u4e00\u6b21\u51fa\u5bb6\uff0c\u5c31\u78b0\u5230\u4e86\u9ad8\u5927\u54e5\uff0c\u4ece\u7b2c\u4e00\u773c\u770b\u5230\u4ed6\uff0c\u6211\u5c31\u77e5\u9053\u65e5\u540e\u518d\u4e5f\u5fd8\u4e0d\u4e86\uff0c\u6211\u4e0d\u7ba1\u4ed6\u662f\u5426 \u7239\u7239\u654c\u4eba\u7684\u5f92\u5f1f\uff0c\u6211\u53ea\u89c9\u5f97\u8fd9\u4e2a\u4eba\u5f88\u4eb2\u5207\uff0c\u5f88\u4eb2\u5207\uff0c\u751a\u81f3\u4e8e\u60f3\u65f6\u65f6\u523b\u523b\u7167\u987e\u4ed6\uff0c\u5c3d\u7ba1\u4ed6\u6216\u662f \u6211\u4eec\u7684\u4ec7\u4eba\uff0c\u53ef\u662f\u90a3\u4e5f\u6ca1\u6709\u529e\u6cd5\u554a\u3002\u201d \u3000\u3000\u201c\u6211\u8bf4\u6211\u8981\u51fa\u5916\u6e38\u73a9\uff0c\u7239\u7239\u5c31\u53eb\u4e09\u5e08\u5144\u966a\u6211\uff0c\u5176\u5b9e\u6211\u662f\u60f3\u627e\u9ad8\u5927\u54e5\uff0c\u6211\u7528\u8ba1\u9a97\u5f00\u4e09\u5e08 \u5144\uff0c\u771f\u51d1\u5de7\uff0c\u5728\u8def\u4e0a\u7adf\u7136\u78b0\u7740\u4e86\u3002\u201d \u3000\u3000\u201c\u5495\uff0c\u5495\u201d\u3002 \u3000\u3000\u732b\u5934\u9e70\u51c4\u5389\u7684\u8e44\u58f0\uff0c\u4ee4\u4eba\u6bdb\u9aa8\u609a\u7136\uff0c\u90a3\u5fae\u5f31\u7684\u6c34\u58f0\uff0c\u59ec\u857e\u4e5f\u542c\u4e0d\u5230\u4e86\uff0c\u5979\u611f\u5230\u5fc3\u5f80\u4e0b \u6c89\uff0c\u5992\u6028\u5b8c\u5168\u5316\u4e3a\u5e7d\u6028\uff0c\u5fc3\u9053\uff1a\u201c\u539f\u6765\u9ad8\u5927\u54e5\u662f\u5acc\u6211\u7239\u7239\u7684\uff0c\u4ed6\u8fd9\u6837\u5fcd\u5fc3\u5bf9\u6211\uff0c\u5c31\u662f\u56e0\u4e3a\u6211 \u662f\u4e00\u4e2a\u5f3a\u76d7\u5934\u513f\u7684\u5973\u513f\u554a\uff01\u201d \u3000\u3000\u5979\u5e0c\u671b\u5947\u8ff9\u51fa\u73b0\uff0c\u53ef\u662f\u524d\u9762\u9ed1\u538b\u538b\u7684\u4e00\u7247\uff0c\u767b\u65f6\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u6d41\u4e0b\u6cea\u6765\uff0c\u54ed\u9053\uff1a\u201c\u5927 \u54e5\uff0c\u6c34\u58f0\u542c\u4e0d\u89c1\u4e86\uff0c\u6c34\u58f0\u542c\u4e0d\u89c1\u4e86\uff0c\u6211\u8981\u8d70\u4e86\u3002\u201d \u3000\u3000\u591c\uff0c\u6797\u4e2d\u9664\u4e86\u90a3\u8be5\u6b7b\u7684\u732b\u5934\u9e70\u5076\u800c\u7684\u557c\u58f0\u5916\uff0c\u4e00\u7247\u5bc2\u9759\uff0c\u6816\u679d\u7684\u5f52\u9e1f\uff0c\u90fd\u5df1\u8d70\u5165\u4e86\u68a6 \u4e61\u3002 \u3000\u3000\u4e00\u6761\u9ed1\u5f71\u6162\u6162\u4ece\u8fdc\u5904\u8d70\u8fdb\u6797\u4e2d\uff0c\u4ed6\u62d6\u7740\u6c89\u91cd\u7684\u6b65\u5b50\uff0c\u4e00\u6b65\u6b65\u7684\u8d70\u7740\uff0c\u4e0d\u65f6\u53d1\u51fa\u201c\u6c99\u201d \u201c\u6c99\u201d\u7684\u58f0\u97f3\u3002 \u3000\u3000\u8d70\u8fd1\u4e86\uff0c\u624b\u4e0a\u62ff\u7740\u677e\u679d\u706b\u628a\uff0c\u5fae\u5f31\u7684\u706b\u5149\u6620\u7740\u4ed6\u60e8\u767d\u7684\u8138\uff0c\u751a\u81f3\u6709\u4e9b\u53d1\u9752\u4e86\u3002 \u3000\u3000\u201c\u6c99\u201d\uff0c\u201c\u6c99\u201d\uff0c\u201c\u6c99\u201d\u3002 \u3000\u3000\u811a\u6b65\u58f0\u6e10\u6e10\u8fdc\u4e86\uff0c\u663e\u7136\u662f\u8d70\u51fa\u4e86\u6811\u6797\u3002 \u3000\u3000\u201c\u59ec\u857e\uff01\u59ec\u857e\uff01\u201d \u3000\u3000\u4e00\u9635\u6025\u4fc3\u7684\u58f0\u97f3\u4f20\u5165\u6797\u4e2d\uff0c\u60ca\u8d77\u4e86\u6b63\u82b1\u751c\u7761\u4e2d\u7684\u4e4c\u9e26\u3002\u7acb\u523b\u5730\u7834\u574f\u4e86\u5b81\u9759\u7684\u5927\u5730\uff0c\u6574\u4e2a \u6797\u4e2d\u201c\u5471\u201d\u201c\u5471\u201d\u4e4b\u58f0\uff0c\u6b64\u8d77\u5f7c\u843d\u3002 \u3000\u3000\u6797\u5916\uff0c\u6708\u8272\u6726\u80e7\u3002 \u3000\u3000\u5927\u6811\u4e0b\uff0c\u4e00\u5339\u96ea\u767d\u7684\u9a8f\u9a6c\u65c1\u7ad9\u7740\u9002\u624d\u4ece\u6797\u4e2d\u8d70\u51fa\u7684\u9ed1\u5f71\uff0c\u4ed6\u6276\u7740\u6811\u5e72\uff0c\u8138\u4e0a\u66f4\u52a0\u60e8\u767d\uff0c \u6447\u6447\u6b32\u5760\u3002 \u3000\u3000\u201c\u5288\u5288\u201d\u201c\u62cd\u62cd\u201d\uff0c\u706b\u82d7\u53d1\u51fa\u5355\u8c03\u7684\u7206\u58f0\uff0c\u90a3\u9a6c\u8fb9\u7684\u5c11\u5e74\u6ee1\u8138\u7126\u6025\u4e4b\u8272\uff0c\u53e3\u4e2d\u5583\u5583\u9053\uff1a \u201c\u5979\u5230\u54ea\u513f\u53bb\u4e86\uff0c\u8fd9\u5c71\u8def\u53c9\u53e3\u6781\u591a\uff0c\u5f88\u5bb9\u6613\u8ff7\u9014\uff0c\u5979\u521d\u6b21\u79bb\u5bb6\uff0c\u5982\u679c\u8ff7\u5165\u5c71\u4e2d\uff0c\u5c31\u4e0d\u6613\u8d70 \u51fa\u3002\u201d \u3000\u3000\u4ed6\u7a81\u89c9\u80f8\u53e3\u4e00\u9635\u75bc\u75db\uff0c\u5fc3\u77e5\u9002\u624d\u7126\u6025\u8d76\u8def\uff0c\u53c8\u9707\u52a8\u4e86\u5185\u4f24\uff0c\u4e8e\u662f\u6df1\u6df1\u5438\u53ef\u6c14\uff0c\u5fcd\u4f4f\u75db\uff0c \u7ec6\u601d\u7740\u76ee\u4e0b\u60c5\u51b5\u3002 \u3000\u3000\u4ed6\u60f3\uff1a\u201c\u59ec\u59d1\u5a18\u591a\u534a\u662f\u4e45\u7b49\u6211\u4e0d\u6765\uff0c\u5230\u5904\u53bb\u627e\u6211\u800c\u8d70\u5931\u4e86\u8def\uff0c\u8fd9\u5c71\u8fde\u7ef5\u767e\u91cc\uff0c\u53eb\u6211\u4f55\u5904 \u53bb\u5bfb\u5979\u5462\uff1f\u201d \u3000\u3000\u4ed6\u6709\u4e9b\u540e\u6094\uff0c\u5fd6\u9053\uff1a\u201c\u5982\u679c\u6211\u4e00\u767b\u60ac\u5d16\uff0c\u5c31\u5148\u53bb\u544a\u8bc9\u5979\uff0c\u90a3\u4e48\u8fd9\u4e8b\u4fbf\u4e0d\u4f1a\u53d1\u751f\uff0c\u53ef\u662f\uff0c \u8fd9\u6837\u6211\u5c31\u8fde\u6797\u4f2f\u4f2f\u6700\u540e\u4e00\u9762\u4e5f\u89c1\u4e0d\u5230\u3002\u54fc\uff0c\u90a3\u53ae\u867d\u7136\u5389\u5bb3\uff0c\u6211\u7ec8\u4e8e\u628a\u4ed6\u903c\u4e0b\u7edd\u5d16\u3002\u201d \u3000\u3000\u4e00\u9635\u6e05\u98ce\u5439\u6765\uff0c\u4ece\u9a6c\u978d\u4e0a\u5439\u4e0b\u4e00\u5f20\u7eb8\uff0c\u4ed6\u8fde\u5fd9\u62fe\u8d77\u4e00\u770b\uff0c\u53ea\u89c1\u4e0a\u9762\u5199\u7740\u56db\u4e2a\u5b57\uff1a\u201c\u4f55\u5fc5 \u76f8\u8bc6\u3002\u201d \u3000\u3000\u4ed6\u5446\u5446\u7684\u77a7\u7740\u90a3\u7ee2\u79c0\u7684\u5b57\u4f53\uff0c\u77e5\u9053\u6b63\u65ed\u59ec\u857e\u6240\u5199\uff0c\u5fc3\u4e2d\u53cd\u590d\u601d\u91cf\u7740\u5176\u4e2d\u7684\u610f\u601d\uff0c\u4f46\u89c9\u773c \u524d\u4e00\u7247\u832b\u7136\uff0c\u53c8\u4e00\u4e2a\u4eb2\u5207\u7684\u4eba\u513f\u9065\u9065\u79bb\u5f00\u4e86\u4ed6\uff0c\u90a3\u60c5\u666f\u6b63\u5982\u540c\u7239\u7239\u79bb\u5f00\u4ed6\u4e00\u6837\u3002 \u3000\u3000\u4e0d\u77e5\u591a\u4e45\uff0c\u4ed6\u624b\u4e2d\u7684\u706b\u628a\u7184\u4e86\uff0c\u5927\u5730\u663e\u5f97\u66f4\u9634\u68ee\u9ed1\u6697\u4e86\u3002 \u3000\u3000\u676d\u5dde\u57ce\u5916\u3002 \u3000\u3000\u4e09\u5339\u9a6c\u4ece\u5b98\u9053\u5165\u57ce\uff0c\u53ea\u89c1\u524d\u9762\u4e00\u5339\u9a6c\u4e0a\u662f\u4e2a\u4fca\u79c0\u5c11\u5e74\uff0c\u53cc\u7709\u5fae\u76b1\uff0c\u6ee1\u8179\u5fc3\u4e8b\uff0c\u540e\u9762\u8ddf\u7740 \u4e00\u5bf9\u59d1\u5a18\uff0c\u8eab\u7740\u7d20\u8863\uff0c\u4e5f\u662f\u4e00\u8a00\u4e0d\u53d1\u3002 \u3000\u3000\u90a3\u5e74\u5e7c\u7684\u59d1\u5a18\u5ffd\u9053\uff1a\u201c\u676d\u5dde\u7ec8\u4e8e\u5230\u5566\uff0c\u8fd9\u51e0\u5929\u8d76\u8def\u771f\u7d2f\u6b7b\u4eba\u3002\u201d \u3000\u3000\u5c11\u5e74\u95fb\u8a00\u63a5\u53e3\u9053\uff1a\u201c\u7389\u59b9\u8bf4\u5f97\u4e0d\u9519\uff0c\u54b1\u4eec\u5c31\u53bb\u843d\u5e97\u4f11\u606f\u3002\u201d \u3000\u3000\u4ed6\u53cd\u8eab\u770b\u4e86\u90a3\u5e74\u957f\u7684\u59d1\u5a18\u4e00\u773c\u3002\u4f3c\u4e4e\u662f\u5f81\u6c42\u5979\u540c\u610f\uff0c\u90a3\u5e74\u957f\u7684\u59d1\u5a18\u6e29\u67d4\u4e00\u7b11\uff0c\u70b9\u70b9\u5934 \u9053\uff1a\u201c\u6b63\u662f\uff0c\u9ad8\u5927\u54e5\u6211\u77a7\u4f11\u5185\u4f24\u6108\uff0c\u5e94\u8be5\u597d\u597d\u517b\u6b47\u4e00\u9635\u5b50\u3002\u201d \u3000\u3000\u90a3\u9ad8\u59d3\u5c11\u5e74\u89c1\u5979\u67d4\u58f0\u5173\u5207\u81ea\u5df1\uff0c\u5fc3\u4e2d\u751a\u662f\u611f\u6fc0\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u53c8\u60f3\u8d77\u53e6\u5916\u4e00\u4e2a\u4eba\u3002 \u3000\u3000\u5e74\u5e7c\u7684\u59d1\u5a18\u9053\uff1a\u201c\u54b1\u4eec\u5148\u5403\u996d\uff0c\u73b0\u5728\u5df2\u7ecf\u8fc7\u5348\u4e86\u3002\u201d \u3000\u3000\u4e09\u4eba\u8d70\u8fdb\u8def\u65c1\u4e00\u5bb6\u9152\u697c\uff0c\u5728\u697c\u4e0a\u62e3\u4e86\u4e2a\u51ed\u7a97\u4f4d\u5b50\uff0c\u8981\u4e86\u83b1\u996d\u3002 \u3000\u3000\u6b63\u5403\u95f4\uff0c\u5ffd\u95fb\u557c\u58f0\u5f97\u5f97\uff0c\u90a3\u9ad8\u59d3\u5c11\u5e74\u5411\u4e0b\u4e00\u770b\uff0c\u53ea\u89c1\u4e00\u5339\u5168\u9ed1\u7684\u5c0f\u9a6c\u5982\u98de\u8d76\u6765\uff0c\u5230\u4e86\u9152 \u697c\u9762\u524d\u7136\u800c\u6b62\uff0c\u4e0a\u9762\u4e00\u4e2a\u5341\u4e8c\u3001\u4e09\u5c81\u7684\u7ae5\u5b50\uff0c\u4e00\u6309\u9a6c\u80cc\uff0c\u8f7b\u98d8\u98d8\u843d\u98de\u9a6c\u6765\uff0c\u59ff\u6001\u7f8e\u5999\u5df2\u6781\u3002 \u3000\u3000\u9ad8\u59d3\u5c11\u5e74\u4e0d\u7531\u8d5e\u9053\uff1a\u201c\u597d\u8eab\u6cd5\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u95fb\u58f0\u5411\u4e0a\u4e00\u77a7\uff0c\u4f46\u89c1\u4e00\u4e2a\u4fca\u96c5\u5c11\u5e74\u5411\u81ea\u5df1\u5fae\u7b11\u70b9\u5934\uff0c\u5fc3\u7531\u5f88\u662f\u5f97\u610f\uff0c\u53e3\u4e2d\u5429\u5490\u5802 \u500c\u5582\u9a6c\u3002\u4e5f\u8d70\u4e0a\u697c\u6765\uff0c\u5411\u9ad8\u59d3\u5c11\u5e74\u4e00\u62f1\u5c0f\u624b\u9053\uff1a\u201c\u8bf7\u6559\u8fd9\u4f4d\u5144\u2026\u2026\u5144\u53f0\u9ad8\u59d3\u5927\u540d\uff1f\u201d \u3000\u3000\u90a3\u5e74\u5e7c\u59d1\u5a18\u89c1\u4ed6\u6bd4\u81ea\u5df1\u8fd8\u5c0f\u4e00\u4e24\u5c81\uff0c\u867d\u7136\u8001\u6c14\u6a2a\u79cb\u7684\u5b66\u7740\u5927\u4eba\u8a00\u884c\uff0c\u53ef\u662f\u6ee1\u8138\u7a1a\u6c14\uff0c\u518d \u600e\u4e48\u4e5f\u88c5\u4e0d\u50cf\uff0c\u4e0d\u7531\u55e4\u7136\u800c\u7b11\u3002 \u3000\u3000\u9ad8\u59d3\u5c11\u5e74\u5e7c\u5c45\u5173\u5916\uff0c\u5bf9\u4e8e\u9a6c\u5339\u597d\u574f\u9274\u522b\u80fd\u529b\u751a\u5f3a\uff0c\u9002\u624d\u4e00\u89c1\u7ae5\u5b50\u7684\u5750\u9a91\u6d51\u8eab\u5e76\u65e0\u6742\u6bdb\uff0c \u9ed1\u4eae\u4eae\u7684\u6709\u5982\u9526\u7f0e\uff0c\u77e5\u662f\u767e\u5e74\u96be\u9022\u7684\u201c\u9f99\u9a79\u201d\uff0c\u5fc3\u60f3\u8fd9\u7ae5\u5b50\u5b9a\u662f\u5927\u6709\u6765\u5386\u7684\u4eba\uff0c\u5fd9\u9053\uff1a\u201c\u5144 \u5f1f\u59d3\u9ad8\u540d\u6218\uff0c\u8fd9\u4e24\u4f4d\u662f\u5728\u4e0b\u670b\u53cb\uff0c\u6797\u6c76\u6797\u7389\u59d1\u5a18\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u5411\u6797\u7389\u770b\u4e86\u4e00\u773c\uff0c\u53ea\u89c1\u5979\u6ee1\u8138\u4e0d\u4ee5\u4e3a\u7136\u7684\u6a21\u6837\uff0c\u5fc3\u60f3\uff1a\u201c\u4f60\u7b11\u4ec0\u4e48\uff0c\u50cf\u4f60\u8fd9\u822c\u5f31 \u4e0d\u7981\u98ce\u7684\u5973\u5b69\uff0c\u6211\u53ea\u8981\u4e00\u4e2a\u6307\u5934\u513f\u5c31\u80fd\u63a8\u5012\u3002\u201d \u3000\u3000\u4ed6\u672c\u60f3\u53d1\u4f5c\uff0c\u4f46\u662f\u4e00\u60f3\u81ea\u5df1\u662f\u51fa\u6c5f\u6e56\u884c\u4fa0\u4ed7\u4e49\u7684\uff0c\u5c82\u80fd\u548c\u4e00\u4e2a\u5973\u5b50\u4e89\u95f9\uff0c\u5982\u679c\u88ab\u4eba\u4f20 \u51fa\uff0c\u81ea\u5df1\u8fd9\u51e0\u4e2a\u6708\u6240\u95ef\u7684\u4e00\u70b9\u4e07\u513f\u53ef\u5c31\u6bc1\u5566\u3002 \u3000\u3000\u9ad8\u6218\u5429\u5490\u5802\u500c\u6dfb\u4e86\u4e00\u526f\u7b77\u5b50\uff0c\u7b11\u7740\u5bf9\u7ae5\u5b50\u9053\uff1a\u201c\u54b1\u4eec\u4e00\u89c1\u5982\u6545\uff0c\u6211\u5e74\u7eaa\u6bd4\u4f60\u5927\u51e0\u5c81\uff0c\u5c31 \u558a\u4f60\u4e00\u58f0\u5c0f\u5f1f\u53ef\u597d\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u542c\u4ed6\u8bf4\u5f97\u8bda\u6073\uff0c\u800c\u4e14\u5bf9\u9ad8\u6218\u751a\u611f\u6295\u7f18\uff0c\u4fbf\u4e0d\u518d\u77dc\u6301\uff0c\u70b9\u5934\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u8fd9\u6837\u6700 \u597d\u3002\u6211\u53eb\u2026\u2026\u6211\u53eb\u2026\u2026\u201d \u3000\u3000\u4ed6\u5ffd\u7136\u60f3\u5230\u4e00\u4e8b\uff0c\u4fbf\u4f4f\u53e3\u4e0d\u8bf4\u3002 \u3000\u3000\u6797\u7389\u89c1\u4ed6\u9f7f\u767d\u5507\u7ea2\uff0c\u773c\u775b\u53c8\u5927\u53c8\u4eae\uff0c\u79c0\u7f8e\u7edd\u4f26\uff0c\u5012\u6709\u4e03\u516b\u5206\u50cf\u4e66\u4e0a\u8d22\u795e\u7237\u5ea7\u4e0b\u7684\u6563\u8d22\u7ae5 \u5b50\uff0c\u5fc3\u4e2d\u5bf9\u4ed6\u9887\u6709\u597d\u611f\uff0c\u4f46\u770b\u4ed6\u81ea\u9ad8\u81ea\u5927\uff0c\u597d\u50cf\u5e76\u6ca1\u6709\u628a\u81ea\u5df1\u653e\u5728\u773c\u5185\uff0c\u4e0d\u89c9\u751a\u662f\u6c14\u607c\u3002\u5979 \u4e00\u5411\u5a07\u751f\u60ef\u517b\uff0c\u4eba\u4eba\u8ba9\u5979\u51e0\u5206\uff0c\u867d\u5219\u8fde\u8fc7\u53d8\u6545\uff0c\u813e\u6c14\u5e76\u672a\u6539\u53d8\uff0c\u54fc\u4e86\u4e00\u58f0\u9053\uff1a\u201c\u4f60\u53eb\u4ec0\u4e48\uff0c \u600e\u4e48\u4e0d\u8bf4\u5440\uff1f\u6211\u60f3\u4e00\u5b9a\u662f\u540d\u6ee1\u5929\u4e0b\u7684\u5927\u4fa0\u5ba2\u3002\u201d \u3000\u3000\u7ae5\u5b50\u7740\u4e86\u9ad8\u6218\u4e00\u773c\uff0c\u9ad8\u6218\u5fd9\u9053\uff1a\u201c\u6211\u8fd9\u4f4d\u5c0f\u59b9\u6700\u7231\u5f00\u73a9\u7b11\uff0c\u5c0f\u5f1f\uff0c\u4f60\u522b\u4ecb\u610f\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5fae\u5fae\u4e0a\u7b11\uff0c\u4e5f\u4e0d\u63a5\u53e3\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u9053\uff1a\u201c\u8fd9\u7ae5\u5b50\u867d\u7136\u7ae5\u5fc3\u672a\u6cef\uff0c\u53ef\u662f\u4e3e\u6b62\u4e4b\u95f4\uff0c\u6c14\u5ea6\u6d51\u5b8f\uff0c\u5b9a\u662f\u540d\u5bb6\u9ad8\u5f1f\u3002\u201d \u3000\u3000\u4e24\u4eba\u5929\u5357\u5730\u5317\u5730\u804a\u7740\uff0c\u90a3\u7ae5\u5b50\u5e74\u7eaa\u867d\u5c0f\uff0c\u5bf9\u4e8e\u6b66\u6797\u77e5\u8bc6\u5374\u6781\u662f\u4e30\u5b9e\uff0c\u4ed6\u53e3\u624d\u53c8\u597d\uff0c\u628a\u4e00 \u4e9b\u6c5f\u6e56\u8da3\u4e8b\u8bf4\u5f97\u6709\u58f0\u6709\u8272\uff0c\u6797\u7389\u542c\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u6709\u597d\u4e9b\u5730\u65b9\u5979\u4e0d\u61c2\uff0c\u4f46\u7ec8\u4e8e\u8d4c\u6c14\u6ca1\u6709\u5f00\u53e3\u53d1 \u95ee\u3002 \u3000\u3000\u6797\u6c76\u89c1\u9ad8\u6218\u6709\u8bf4\u6709\u7b11\uff0c\u8138\u4e0a\u9634\u7ff3\u5c3d\u9664\uff0c\u5fc3\u4e2d\u4e5f\u89c9\u5f00\u6717\uff0c\u4e0d\u65f6\u52a0\u4e0a\u4e00\u4e24\u53e5\u8d5e\u53f9\u7684\u8bdd\uff0c\u65e0\u4e0d \u6070\u5230\u597d\u5904\uff0c\u90a3\u7ae5\u5b50\u5bf9\u5979\u5927\u8d77\u77e5\u5df1\u4e4b\u611f\uff0c\u8bf4\u5f97\u66f4\u8d77\u52b2\u4e86\uff01 \u3000\u3000\u7ae5\u5b50\u5ffd\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u6211\u77a7\u4f60\u5185\u529f\u7cbe\u6e5b\uff0c\u4e00\u5b9a\u662f\u4ece\u5c0f\u5c31\u7ec3\u4e0a\u4e58\u5185\u529f\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u4e00\u60ca\uff0c\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\uff0c\u4f60\u600e\u77e5\u9053\uff1f\u201d \u3000\u3000\u201c\u7239\u7239\u8bf4\u8fc7\u51e1\u662f\u7ec3\u5c31\u4e0a\u4e58\u5185\u529f\uff0c\u592a\u9633\u7a74\u5e76\u4e0d\u7a81\u8d77\uff0c\u53ea\u662f\u5168\u8eab\u7b4b\u9aa8\u6709\u4e00\u5c42\u6de1\u6de1\u6cb9\u5149\u3002\u5927 \u54e5\uff0c\u4f60\u8eab\u4e0a\u6b63\u6709\u8fd9\u79cd\u6cb9\u5149\u3002\u201d \u3000\u3000\u9ad8\u6218\u5927\u4e3a\u4f69\u670d\uff0c\u7ae5\u5b50\u53c8\u9053\uff1a\u201c\u7239\u7239\u8bf4\u6211\u8981\u5341\u516d\u5c81\u624d\u80fd\u7ec3\u5230\u8fd9\u4e2a\u5730\u6b65\uff0c\u5982\u679c\u5185\u529f\u80fd\u591f\u7ec3\u6210 \u8fd9\u6837\u5b50\uff0c\u5b66\u8d77\u4ec0\u4e48\u529f\u592b\u90fd\u7b80\u4fbf\u6781\u4e86\u3002\u201d \u3000\u3000\u6797\u7389\u53eb\u9053\uff1a\u201c\u522b\u8001\u6c14\u6a2a\u79cb\u7684\u778e\u5439\u3002\u201d \u3000\u3000\u7ae5\u5b50\u4e0d\u7406\uff0c\u5ffd\u7136\u95ee\u8fc7\uff1a\u201c\u9ad8\u5927\u8033\u3002\u4f60\u662f\u4ece\u5317\u65b9\u6765\u7684\uff1f\u201d \u3000\u3000\u9ad8\u6218\u70b9\u5934\u79f0\u662f\u3002 \u3000\u3000\u7ae5\u5b50\u53c8\u9053\uff1a\u201c\u4f60\u4eec\u4e00\u8def\u53ef\u542c\u8bf4\u8fc7\u4e00\u4e2a\u9f0e\u9f0e\u5927\u540d\u7684\u5927\u4fa0\uff0c\u6885\u9999\u795e\u5251\u8f9b\u2026\u2026\u8f9b\u6377\u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u6447\u5934\u9053\uff1a\u201c\u5317\u65b9\u6b66\u6797\u6700\u8fd1\u6b64\u73b0\u4e86\u4e2a\u5927\u9b54\u5934\uff0c\u53eb\u5929\u715e\u661f\u541b\uff0c\u4eba\u4eba\u8c08\u864e\u8272\u53d8\uff0c\u6211\u5012\u6ca1\u542c \u5230\u8bf4\u4ec0\u4e48\u8f9b\u5927\u4fa0\u3002\u201d \u3000\u3000\u7ae5\u5b50\u751a\u611f\u5931\u671b\uff0c\u53cc\u988a\u80c0\u7ea2\u9053\uff1a\u201c\u90a3\u4e48\u6709\u4e00\u4e2a\u2026\u2026\u6709\u4e00\u4e2a\u4ed7\u4e49\u758f\u8d22\u7684\u5c0f\u4fa0\u91d1\u7ae5\u8f9b\u5e73\uff0c\u4f60\u4e00 \u5b9a\u542c\u8bf4\u8fc7\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u89c1\u4ed6\u6ee1\u8138\u6e34\u671b\u4e4b\u60c5\uff0c\u4e0d\u5fcd\u4f7f\u4ed6\u5931\u671b\uff0c\u6b63\u5728\u76d8\u7b97\u5982\u4f55\u7b54\u590d\u3001\u6797\u7389\u5374\u4e0d\u7ba1\u4e00\u5207\u8bf4\u9053\uff1a \u201c\u6ca1\u6709\uff0c\u6ca1\u6709\u4ec0\u4e48\u6885\u9999\u5927\u4fa0\uff0c\u4ec0\u4e48\u91d1\u7ae5\u8f9b\u5e73\uff0c\u54b1\u4eec\u90fd\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d\u3000\u3000\u3000\u3000\u90a3\u7ae5\u5b50\u53c8\u5931\u671b\u53c8 \u4f24\u5fc3\uff0c\u561f\u8d77\u5c0f\u5634\uff0c\u5f88\u4e0d\u9ad8\u5174\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4e9b\u4eba\u771f\u662f\u5b64\u964b\u5be1\u95fb\uff0c\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\u3002\u201d \u3000\u3000\u4fbf\u7ad9\u8d77\u8eab\u6765\uff0c\u5bf9\u9ad8\u6218\u9053\uff1a\u201c\u5c0f\u5f1f\u8fd8\u6709\u4e00\u70b9\u4e8b\u60c5\u8981\u529e\uff0c\u8fd9\u5c31\u79bb\u5f00\u676d\u57ce\uff0c\u5927\u54e5\u4f60\u2026\u2026\u4f60\u4eec\u5982 \u679c\u54ea\u5929\u5230\u56db\u5ddd\u6765\uff0c\u5343\u4e07\u8981\u5230\u6c99\u9f99\u576a\u6765\u627e\u6211\uff0c\u54b1\u4eec\u53ef\u4ee5\u597d\u597d\u73a9\u73a9\u3002\u201d \u3000\u3000\u4ed6\u672c\u53ea\u60f3\u7ea6\u9ad8\u6545\u4e00\u4eba\uff0c\u53ef\u662f\u4e00\u60f3\u6e29\u67d4\u7684\u6797\u6c76\uff0c\u771f\u50cf\u5927\u59ca\u59ca\u4e00\u822c\u5f85\u4ed6\uff0c\u4fbf\u8ffd\u4ed6\u4eec\u4e00\u9f50\u513f \u53bb\u3002 \u3000\u3000\u6797\u7389\u5446\u5446\u770b\u6e05\u4ed6\u524d\u5f00\uff0c\u5fc3\u4e2d\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u6ecb\u5473\uff0c\u4e5f\u4e0d\u662f\u6c14\uff0c\u4e5f\u4e0d\u662f\u6101\uff0c\u534a\u664c\uff0c\u624d\u5bf9\u9ad8 \u6218\u8fc7\uff1a\u201c\u8fd9\u4e2a\u5c0f\u5b69\u51c6\u662f\u91ce\u5a03\u513f\uff0c\u8dd1\u6765\u8dd1\u53bb\u6ca1\u4e00\u523b\u513f\u5b89\u9759\uff0c\u5927\u54e5\uff0c\u4f60\u770b\u4ed6\u8bf4\u4e86\u534a\u5929\uff0c\u8fde\u81ea\u5df1\u7684 \u540d\u5b57\u4e5f\u6ca1\u8bf4\u51fa\uff0c\u771f\u662f\u4e0d\u61c2\u793c\u8c8c\u3002\u201d \u3000\u3000\u6797\u6ce2\u8f7b\u8f7b\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u59b9\uff0c\u4f60\u60f3\u77e5\u9053\u4ed6\u540d\u5b57\u662f\u4e0d\uff1f\u201d \u3000\u3000\u6797\u7389\u5550\u9053\uff1a\u201c\u5478\uff0c\u8c01\u8981\u77e5\u9053\u4ed6\u540d\u5b57\uff0c\u5e0c\u7f55\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u542c\u5979\u59ca\u59b9\u53e3\u89d2\uff0c\u4e0d\u7531\u597d\u7b11\uff0c\u5077\u773c\u4e00\u77a7\u8fd9\u4e2a\u81ea\u5c0f\u5c31\u987d\u76ae\u5927\u80c6\u7684\u5c0f\u5973\u5b69\uff0c\u53ea\u89c1\u5979\u6655\u751f\u53cc \u988a\uff0c\u5fc3\u5185\u604d\u7136\u5927\u609f\uff0c\u60c5\u7eea\u4e0d\u7531\u4e5f\u8f7b\u677e\u8d77\u6765\u3002 \u3000\u3000\u9ad8\u6218\u5ffd\u9053\uff1a\u201c\u6c76\u59ca\uff0c\u660e\u5929\u5e08\u7236\u8981\u6765\u5566\uff0c\u6211\u5c31\u8981\u8ddf\u4ed6\u8001\u4eba\u5bb6\u8fd4\u56de\u5173\u5916\u7ec3\u6b66\uff0c\u4f60\u4eec\u59ca\u59b9\u5f53\u771f \u8981\u8ddf\u6211\u4eec\u53bb\u9526\u5dde\uff1f\u201d \u3000\u3000\u6797\u6c76\u51c4\u7136\u9053\uff1a\u201c\u54b1\u4eec\u59ca\u59b9\u5728\u8fd9\u4e16\u4e0a\u518d\u65e0\u4eb2\u4eba\uff0c\u5982\u679c\u4f60\u5e08\u7236\u4e0d\u7b54\u5e94\u5e26\u6211\u4eec\u53bb\uff0c\u90a3\u4e48\u6211\u4eec\u53ea \u6709\u5728\u6c5f\u6e56\u4e0a\u5012\u5904\u98d8\u6cca\u5566\u3002\u201d \u3000\u3000\u9ad8\u6218\u542c\u5979\u8bf4\u5f97\u60b2\u82e6\uff0c\u60f3\u8d77\u5979\u7239\u4e34\u7ec8\u7684\u5631\u5490\u3002\u6fc0\u52a8\u5730\u9053\uff1a\u201c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u80fd\u8ba9\u4f60\u4eec\u72ec\u81ea\u5728 \u6c5f\u6e56\u4e0a\u6df7\uff0c\u5e08\u7236\u6148\u7965\u65e0\u6bd4\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u4f1a\u7b54\u5e94\uff0c\u6c76\u59ca\uff0c\u4f60\u653e\u5fc3\u3002\u201d \u3000\u3000\u6797\u6c76\u751c\u751c\u4e00\u7b11\u9053\uff1a\u201c\u6211\u8001\u65e9\u5c31\u653e\u5fc3\u4e86\uff0c\u9ad8\u5927\u54e5\uff0c\u4f60\u4ece\u5c0f\u5c31\u5904\u5904\u7231\u62a4\u7740\u6211\u4eec\uff0c\u51b3\u4e0d\u8ba9\u6211\u4eec \u59ca\u59b9\u6709\u4e1d\u6beb\u635f\u5bb3\u3002\u201d \u3000\u3000\u9ad8\u6545\u5fae\u5fae\u4e00\u7b11\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53ea\u89c1\u6797\u6c76\u7f69\u5f80\u60c5\u6df1\u7684\u8bf4\u7740\uff0c\u5fc3\u5185\u4e0d\u89c9\u4e00\u51db\uff0c\u53c8\u6cdb\u8d77\u4e1d\u4e1d\u82e6 \u5473\uff0c\u60f3\u5230\u81ea\u5df1\u4e00\u5fc3\u4e00\u610f\u559c\u6b22\u59ec\u857e\uff0c\u53ef\u662f\u5979\u5374\u4e0d\u8f9e\u800c\u522b\uff0c\u5230\u5e95\u662f\u600e\u6837\u5f97\u7f6a\u5979\uff0c\u5230\u73b0\u5728\u4e5f\u4e0d\u660e \u767d\uff0c\u5fc3\u4e2d\u5927\u611f\u7d22\u7136\u65e0\u5473\u3002 \u3000\u3000\u4e09\u4eba\u843d\u4e86\u5e97\uff0c\u9ad8\u6218\u5bfb\u4e86\u4e00\u4e2a\u5e97\u5c0f\u4e8c\uff0c\u95ee\u660e\u53bb\u516d\u5408\u5854\u7684\u8def\u9014\uff0c\u4fbf\u5bf9\u6797\u6c0f\u59ca\u59b9\u9053\uff1a\u201c\u5e08\u7236\u7ea6 \u6211\u5728\u516d\u5408\u5854\u4f1a\u9762\uff0c\u6211\u5148\u53bb\u770b\u770b\u3002\u201d \u3000\u3000\u6797\u7389\u62a2\u7740\u9053\uff1a\u201c\u5927\u54e5\uff0c\u6211\u4e5f\u53bb\u3002\u201d \u3000\u3000\u9ad8\u6218\u5947\u9053\uff1a\u201c\u4f60\u4e0d\u662f\u8bf4\u7d2f\u6b7b\u4e86\uff1f\u73b0\u5728\u6709\u4f11\u606f\u53c8\u4e0d\u4f11\u606f\u4e86\u3002\u201d \u3000\u3000\u6797\u7389\u9053\uff1a\u201c\u6211\u73b0\u5728\u4e0d\u63d0\u4e86\u53ef\u4e0d\u53ef\u4ee5\uff1f\u201d \u3000\u3000\u9ad8\u6218\u65e0\u5948\uff0c\u53ea\u6709\u5e26\u7740\u5979\u9a91\u9a6c\u800c\u53bb\u3002 \u3000\u3000\u90a3\u516d\u5408\u5854\u5728\u676d\u5dde\u90ca\u5916\uff0c\u6fd2\u4e34\u5927\u6c5f\uff0c\u4f5c\u516b\u89d2\u5f62\uff0c\u6c14\u52bf\u6781\u4e3a\u96c4\u58ee\uff0c\u9ad8\u6218\u62d0\u7740\u6797\u7389\u7684\u624b\uff0c\u767b\u4e0a \u5854\u9876\uff0c\u4e3e\u76ee\u4e00\u671b\uff0c\u53ea\u89c1\u8fdc\u5904\u5e06\u5f71\u70b9\u70b9\uff0c\u5927\u6c5f\u5c31\u5728\u8db3\u5e95\uff0c\u7530\u4ea9\u5c0f\u5982\u68cb\u5c40\uff0c\u6e05\u98ce\u5439\u6765\uff0c\u76f4\u6b32\u4e58\u98ce \u5f52\u53bb\u3002 \u3000\u3000\u6797\u7389\u95ed\u7740\u773c\u4e0d\u6562\u5f80\u4e0b\u770b\uff0c\u9ad8\u6218\u773a\u671b\u826f\u4e45\uff0c\u4e0d\u89c9\u5fc3\u65f7\u795e\u6021\uff0c\u8d70\u4e0b\u5854\u6765\u3002 \u3000\u3000\u4e24\u4eba\u9a91\u9a6c\u5f52\u5e97\uff0c\u6797\u7389\u89c1\u9ad8\u6218\u9ed8\u7136\u4e0d\u8bed\uff0c\u773c\u89d2\u4f3c\u6709\u91cd\u5fe7\uff0c\u4fbf\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u4f60\u5fc3\u91cc\u5728\u60f3\u4ec0 \u4e48\uff1f\u5e72\u5417\u8fd9\u822c\u53d1\u6101\uff1f\u201d \u3000\u3000\u9ad8\u6218\u6447\u5934\u4e0d\u8bed\uff0c\u6797\u7389\u53c8\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u6211\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u3002\u201d \u3000\u3000\u9ad8\u6218\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u7389\u538b\u4f4e\u55d3\u5b50\uff0c\u795e\u79d8\u5730\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u4e0d\u53ef\u4ee5\u544a\u8bc9\u522b\u4eba\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u5927\u5947\uff0c\u70b9\u5934\u7b54\u5e94\u4e86\u3002 \u3000\u3000\u6797\u7389\u6084\u7136\u9053\uff1a\u201c\u4f60\u77e5\u9053\u4e0d\uff0c\u59d0\u59d0\u5f88\u662f\u559c\u6b22\u4f60\u3002\u201d \u3000\u3000\u9ad8\u6218\u4e00\u60ca\uff0c\u6447\u5934\u9053\uff1a\u201c\u4f60\u522b\u778e\u8bf4\u3002\u201d \u3000\u3000\u6797\u7389\u677f\u8d77\u9762\u5b54\uff0c\u6b63\u8272\u9053\uff1a\u201c\u6211\u5f53\u7136\u4e0d\u778e\u8bf4\uff0c\u81ea\u4ece\u4f60\u79bb\u5f00\u6986\u5e84\u540e\uff0c\u59ca\u59ca\u5c31\u5f88\u5c11\u771f\u6b63\u5feb\u4e50 \u8fc7\uff0c\u5979\u5e38\u5e38\u4e00\u4e2a\u4eba\u8dd1\u5230\u90a3\u88f8\u5927\u6986\u6811\u4e0b\uff0c\u4e00\u5750\u5c31\u662f\u534a\u5929\uff0c\u6709\u65f6\u53c8\u8dd1\u5230\u6211\u4eec\u4ece\u524d\u4e09\u4e2a\u4eba\u4e00\u5757\u73a9\u7684 \u5730\u65b9\uff0c\u6d41\u8fde\u5f98\u5f8a\uff0c\u8fde\u6211\u4e5f\u4e0d\u7406\uff0c\u6211\u672c\u6765\u4ee5\u4e3a\u4eba\u957f\u5927\u4e86\u5c31\u4f1a\u53d8\u6210\u8fd9\u6837\uff0c\u73b0\u5728\u6211\u53ef\u660e\u767d\u5566\uff0c\u5979\u662f \u5728\u60f3\u4f60\u54e9\uff01\u201d \u3000\u3000\u9ad8\u6218\u5f88\u662f\u611f\u52a8\uff0c\u60f3\u5230\u81ea\u5df1\u5b64\u82e6\u65e0\u4f9d\uff0c\u4f46\u5230\u5904\u53d7\u4eba\u7737\u987e\uff0c\u611f\u6fc0\u4e4b\u60c5\u6cb9\u7136\u800c\u751f\uff0c\u6267\u7740\u6797\u7389\u5c0f \u624b\u9053\uff1a\u201c\u4f60\u4eec\u59ca\u59b9\u90fd\u662f\u6700\u597d\u6700\u597d\u7684\u4eba\u3002\u201d \u3000\u3000\u6797\u7389\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u5e72\u5417\u6574\u5929\u6101\u7709\u82e6\u8138\uff0c\u5bb3\u5f97\u59d0\u59d0\u4e71\u731c\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff0c\u5979\u6bcf\u5929\u90fd\u8981\u5077\u5077\u54ed \u51e0\u6b21\u54e9\uff01\u201d \u3000\u3000\u9ad8\u6218\u95ee\u9053\uff1a\u201c\u5979\u54ed\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6797\u7389\u9053\uff1a\u201c\u5979\u89c1\u4f60\u4e0d\u5feb\u6d3b\uff0c\u5fc3\u91cc\u81ea\u7136\u4e5f\u4e0d\u5f00\u5fc3\uff0c\u4fbf\u5f88\u5bb9\u6613\u60f3\u5230\u7239\u7239\u7684\u60e8\u6b7b\uff0c\u90a3\u5c31\u5fcd\u4e0d\u4f4f \u8981\u54ed\u4e86\u3002\u201d \u3000\u3000\u5979\u8bf4\u5230\u6b64\uff0c\u773c\u7736\u4e00\u7ea2\uff0c\u9ad8\u6218\u8fde\u5fd9\u5b89\u6170\u9053\uff1a\u201c\u522b\u4f24\u5fc3\uff0c\u522b\u4f24\u5fc3\u3002\u201d \u3000\u3000\u6b63\u5728\u6b64\u65f6\uff0c\u8fce\u9762\u4e00\u9a6c\u98de\u9a70\u800c\u8fc7\uff0c\u9ad8\u6218\u770b\u6e05\u9a6c\u4e0a\u7684\u4eba\uff0c\u5fc3\u4e2d\u5927\u9707\uff0c\u4fbf\u5bf9\u6797\u7389\u9053\uff1a\u201c\u4f60\u5728\u6b64 \u7b49\u6211\u4e00\u4f1a\u3002\u201d \u3000\u3000\u4e00\u62cd\u9a6c\uff0c\u4e5f\u75be\u9a70\u8ffd\u53bb\uff0c\u6797\u7389\u5fc3\u4e2d\u5927\u5947\uff0c\u4e5f\u6ca1\u770b\u6e05\u6765\u4eba\uff0c\u4f46\u5979\u7d20\u6765\u80c6\u5927\u5fc3\u7c97\uff0c\u5bf9\u4e8b\u4e0d\u7231\u591a \u60f3\uff0c\u5c31\u5728\u8def\u65c1\u7b49\u5019\u3002 \u3000\u3000\u4e14\u8bf4\u9ad8\u6218\u5982\u98de\u8d76\u53bb\uff0c\u539f\u6765\u4ed6\u5185\u529f\u6df1\u6e5b\uff0c\u76ee\u529b\u6781\u5f3a\uff0c\u770b\u6e05\u5feb\u9a6c\u800c\u8fc7\u7684\u6b63\u8db3\u81ea\u5df1\u957f\u65e5\u51dd\u601d\uff0c \u591c\u534a\u68a6\u56de\uff0c\u65e0\u4e00\u523b\u6216\u5fd8\u7684\u610f\u4e2d\u4eba\u59ec\u857e\u3002\u4ed6\u5fc3\u60c5\u7830\u7136\u800c\u52a8\uff0c\u6697\u5fd6\u53ea\u8981\u8ffd\u8d76\u4e0a\u5979\uff0c\u5c31\u53ef\u95ee\u660e\u771f \u76f8\uff0c\u4ed6\u6253\u5b9a\u4e3b\u610f\uff0c\u4efb\u5979\u8d23\u9a82\uff0c\u7edd\u4e0d\u56de\u53e3\u4fbf\u662f\uff0c\u867d\u7136\u4ed6\u5e76\u4e0d\u4e86\u89e3\u81ea\u5df1\u505a\u9519\u4e86\u4ec0\u4e48\u4e8b\u3002 \u3000\u3000\u90a3\u524d\u9762\u9a6c\u4e0a\u6b63\u662f\u59ec\u857e\u3002 \u3000\u3000\u5176\u5b9e\u5979\u65e9\u5df2\u53d1\u73b0\u4e86\u9ad8\u6218\uff0c\u5979\u89c1\u9ad8\u6218\u53c8\u548c\u53e6\u5916\u4e00\u4e2a\u5c0f\u59d1\u5a18\u5e76\u9a91\u8c08\u7b11\uff0c\u5fc3\u60f3\u4ece\u6b64\u4e0d\u613f\u518d\u89c1\u8fd9 \u4e2a\u8d1f\u5fc3\u4eba\uff0c\u6240\u4ee5\u98de\u5feb\u8dd1\u8fc7\u3002 \u3000\u3000\u539f\u6765\u5979\u90a3\u591c\u7559\u4e66\u800c\u884c\uff0c\u771f\u662f\u67d4\u80a0\u5bf8\u65ad\uff0c\u60f3\u5230\u9ad8\u6218\u8d1f\u5fc3\u65e0\u826f\uff0c\u771f\u6068\u4e0d\u5f97\u5c31\u6b64\u6b7b\u53bb\uff0c\u9a91\u7740\u9a6c \u7adf\u4e0d\u77e5\u5411\u4f55\u5904\u53bb\uff0c\u4e71\u95ef\u4e71\u8d70\uff0c\u4e5f\u6765\u5230\u676d\u57ce\u3002 \u3000\u3000\u59ec\u857e\u542c\u5230\u540e\u9762\u8e44\u58f0\u6e10\u8fd1\uff0c\u5fc3\u77e5\u53ea\u8981\u88ab\u9ad8\u6218\u8d76\u4e0a\uff0c\u90a3\u4e48\u5728\u4ed6\u90a3\u5f20\u8bda\u6073\u7684\u8138\u5b54\u4e0b\uff0c\u81ea\u5df1\u518d\u4e5f \u786c\u4e0d\u8d77\u5fc3\u79bb\u5f00\uff0c\u65e5\u540e\u4e0d\u77e5\u8981\u53d7\u591a\u5c11\u6b3a\u9a97\uff0c\u4e00\u72e0\u5fc3\uff0c\u4e00\u8fde\u8fde\u626c\u97ad\uff0c\u50ac\u9a6c\u75be\u884c\u3002 \u3000\u3000\u8fd9\u4e24\u9a91\u4e00\u524d\u4e00\u540e\u75be\u82e5\u6d41\u661f\u7684\u8dd1\u7740\uff0c\u9ad8\u6218\u773c\u770b\u6108\u8ffd\u6108\u8fd1\uff0c\u5fc3\u4e0b\u6b63\u559c\uff0c\u5ffd\u7136\u659c\u5f84\u91cc\u53cd\u51b2\u51fa\u4e00 \u9a91\uff0c\u9a6c\u4e0a\u4eba\u9ad8\u547c\uff1a\u201c\u91c7\u82b1\u8d3c\uff0c\u91c7\u82b1\u8d3c\uff0c\u5feb\u66ff\u5c0f\u7237\u7559\u4e0b\u3002\u201d \u3000\u3000\u558a\u58f0\u65b9\u6bd5\uff0c\u5df2\u7ecf\u6321\u5728\u5546\u6218\u9762\u524d\uff0c\u9ad8\u6218\u6025\u5fd9\u62c9\u97ad\u6b62\u9a6c\uff0c\u4f46\u75be\u884c\u4e4b\u52bf\u975e\u540c\u5c0f\u53ef\uff0c\u90a3\u9a6c\u751a\u662f\u795e \u9a8f\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u591a\u5f62\u76f4\u7acb\u800c\u505c\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u6b63\u6ca1\u597d\u6c14\uff0c\u4f46\u5411\u524d\u4e00\u77a7\uff0c\u767b\u65f6\u557c\u7b11\u7686\u975e\uff0c\u539f\u6765\u6b63\u662f\u5348\u95f4\u5728\u9152\u697c\u4e0a\u7ed3\u8bc6\u7684\u7ae5\u5b50\u3002 \u3000\u3000\u90a3\u91cd\u5b50\u6ee1\u9762\u7f9e\u6127\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u6211\u4ee5\u4e3a\u4f60\u662f\u2026\u2026\u4f60\u662f\u91c7\u2026\u2026\u82b1\u8d3c\u3002\u201d \u3000\u3000\u7ecf\u8fc7\u8fd9\u4e00\u6253\u6270\uff0c\u59ec\u857e\u5df1\u7ecf\u8d70\u8fdc\u4e86\uff0c\u5c18\u571f\u626c\u5929\uff0c\u53ea\u770b\u89c1\u4e00\u4e2a\u5c0f\u9ed1\u70b9\u3002 \u3000\u3000\u9ad8\u6218\u82e6\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\uff0c\u771f\u4e0d\u51d1\u5de7\uff0c\u53ea\u5dee\u4e00\u70b9\u513f\u5c31\u8d76\u4e0a\u4e86\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u4e0d\u8981\u7d27\uff0c\u4e0d\u8981\u7d27\uff0c\u4f60\u9a91\u6211\u8fd9\u5343\u91cc\u9f99\u9a79\uff0c\u5c31\u7b97\u843d\u540e\u5341\u91cc\uff0c\u4e5f\u80fd\u5728\u4e00\u4e2a\u65f6\u8fb0\u5185 \u8d76\u4e0a\u3002\u201d \u3000\u3000\u4ed6\u5fc3\u542b\u6b49\u610f\uff0c\u4e8e\u662f\u4e0d\u52a0\u601d\u7d22\u4fbf\u60f3\u628a\u9f99\u9a79\u501f\u7ed9\u9ad8\u6218\uff0c\u4ed6\u53ef\u6ca1\u60f3\u5230\u8fd9\u5343\u91cc\u9a6c\u662f\u7236\u4eb2\u5316\u4e86\u65e0\u7a77 \u5fc3\u8840\u5f97\u6765\uff0c\u5c82\u80fd\u8f7b\u6613\u4ea4\u7ed9\u53ea\u6709\u4e00\u9762\u4e4b\u7f18\u7684\u4eba\u3002 \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u5fd6\u9053\uff1a\u201c\u5979\u65e2\u7136\u4e0d\u987e\u89c1\u6211\uff0c\u6211\u5c31\u662f\u8d76\u4e0a\u53bb\u89c1\u9762\uff0c\u4e5f\u53ea\u6709\u5f92\u589e\u4e24\u4eba\u7684\u75db\u82e6\uff0c\u7f62 \u4e86\uff0c\u7f62\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u591a\u8c22\u5c0f\u5f1f\uff0c\u4e0d\u5fc5\u4e86\u3002\u201d \u3000\u3000\u7ae5\u5b50\u9053\uff1a\u201c\u522b\u8c22\uff01\u522b\u8c22\uff0c\u6211\u542c\u5e97\u91cc\u5c0f\u4e8c\u5947\u8c08\u8d77\u6700\u8fd1\u676d\u5dde\u6709\u4e00\u540d\u5c11\u5e74\u91c7\u82b1\u8d3c\uff0c\u6211\u8fdc\u8fdc\u770b\u4e0d \u6e05\u695a\uff0c\u53ea\u9053\u4e00\u4e2a\u5927\u7537\u4eba\u8ffd\u8d76\u4e00\u4e2a\u5973\u4eba\uff0c\u591a\u534a\u5c31\u662f\u5c0f\u4e8c\u53ef\u6240\u8bf4\u7684\u91c7\u82b1\u8d3c\uff0c\u6240\u4ee5\u2026\u2026\u201d \u3000\u3000\u9ad8\u6218\u63a5\u53e3\u9053\uff1a\u201c\u6240\u4ee5\u5c31\u6fc0\u8d77\u4f60\u4fa0\u4e49\u5fc3\u80a0\uff0c\u9a71\u9a6c\u5f80\u63f4\u4e86\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u8138\u4e0a\u901a\u7ea2\uff0c\u9ad8\u6218\u7b11\u9053\uff1a\u201c\u5c0f\u5f1f\uff0c\u522b\u4ecb\u610f\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5feb\u5feb\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u4ecb\u610f\u3002\u201d \u3000\u3000\u5176\u5b9e\u8fd9\u7ae5\u5b50\u6839\u672c\u5c31\u4e0d\u503c\u201c\u91c7\u82b1\u201d\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4ed6\u5077\u5077\u51fa\u5bb6\uff0c\u5c31\u662f\u60f3\u626c\u540d\u7acb\u4e07\uff0c\u4e00\u8def\u4e0a\u5012 \u4e5f\u505a\u4e86\u51e0\u4ef6\u6551\u8d2b\u6276\u5f31\u7684\u4e49\u4e3e\uff0c\u5fc3\u4e0b\u5927\u662f\u81ea\u5f97\uff0c\u53ea\u9053\u6c5f\u6e56\u751f\u4eba\u4eba\u5747\u77e5\u3002\u8fd9\u65e5\u542c\u5c0f\u4e8c\u518d\u9ad8\u8c08\u6c5f\u6e56 \u5f02\u95fb\uff0c\u8bf4\u5230\u676d\u5dde\u51fa\u73b0\u4e2a\u529f\u529b\u7edd\u9ad8\u7684\u91c7\u82b1\u6deb\u8d3c\uff0c\u4eba\u4eba\u6028\u4e4b\u5165\u9aa8\u3002\u5374\u662f\u5948\u4f55\u4e0d\u5f97\uff0c\u4ed6\u672c\u662f\u4fa0\u4e49\u5929 \u6027\uff0c\u4fbf\u8ba1\u5212\u4e0b\u624b\u66ff\u6c11\u9664\u5bb3\uff0c\u4ed6\u539f\u60f3\u95ee\u95ee\u4ec0\u4e48\u53eb\u201c\u91c7\u82b1\u201d\uff0c\u53ef\u662f\u81ea\u6301\u8eab\u4efd\uff0c\u6015\u88ab\u522b\u4eba\u8ba5\u7b11\u4e0d\u61c2 \u4e8b\uff0c\u5fc3\u60f3\u7239\u7239\u8bf4\u8fc7\u6deb\u8d3c\u5c31\u662f\u5bf9\u5973\u5b50\u65e0\u793c\u7684\u4eba\uff0c\u4e8e\u662f\u5c31\u51fa\u57ce\u67e5\u8bbf\u3002 \u3000\u3000\u9ad8\u6218\u95ee\u8fc7\uff1a\u201c\u4f60\u4e8b\u60c5\u529e\u5b8c\u4e86\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u4e00\u6014\uff0c\u7b11\u9053\uff1a\u201c\u6211\u662f\u53bb\u9001\u4e00\u4efd\u793c\u7269\u7ed9\u96c1\u8361\u5927\u4fa0\uff0c\u5f85\u4f1a\u6211\u8fd8\u5f97\u53bb\u559d\u4ed6\u751f\u65e5\u9152\uff0c\u5927\u54e5\uff0c \u4f60\u4e5f\u53bb\u597d\u5417\uff1f\u201d \u3000\u3000\u9ad8\u6218\u9053\uff1a\u201c\u6211\u4e0d\u8bc6\u5f97\u96c1\u8361\u5927\u4fa0\u3002\u201d \u3000\u3000\u7ae5\u5b50\u9053\uff1a\u201c\u6ca1\u5173\u7cfb\uff0c\u6211\u4e5f\u4e0d\u8bc6\u5f97\u4ed6\uff0c\u542c\u8bf4\u6b64\u4eba\u662f\u6761\u9876\u5929\u7acb\u5730\u7684\u597d\u6c49\u5b50\uff0c\u4ed6\u8fd9\u6b21\u505a\u516d\u5341\u5927 \u5bff\uff0c\u5e76\u4e14\u58f0\u8a00\u5c01\u5200\u9000\u51fa\u6c5f\u6e56\uff0c\u8fd9\u9644\u8fd1\u51e0\u7701\u6b66\u6797\u540d\u5bb6\u5dee\u4e0d\u591a\u5168\u8bf7\u6765\u4e86\uff0c\u54b1\u4eec\u4e00\u5b9a\u6709\u70ed\u95f9\u597d \u77a7\u3002\u201d \u3000\u3000\u8bf4\u8bdd\u95f4\uff0c\u5df1\u8d70\u8fd1\u6797\u7389\u7b49\u5f85\u7684\u5730\u65b9\uff0c\u6797\u7389\u770b\u5230\u90a3\u7ae5\u5b50\uff0c\u53c8\u8ddf\u9ad8\u6218\u56de\u6765\uff0c\u5fc3\u4e2d\u5927\u559c\uff0c\u5374\u4e0d\u9732 \u4e8e\u5f62\u8272\u3002 \u3000\u3000\u6797\u7389\u7b11\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u4f60\u539f\u6765\u662f\u8ffd\u4ed6\u56de\u6765\u5440\u3002\u201d \u3000\u3000\u7ae5\u5b50\u6447\u6447\u5934\uff0c\u5bf9\u9ad8\u6218\u9053\uff1a\u201c\u54b1\u4eec\u8fd9\u5c31\u53bb\u5427\uff0c\u53bb\u8fdf\u4e86\u6050\u6015\u6ca1\u70ed\u95f9\u53ef\u77a7\u5566\u3002\u201d \u3000\u3000\u6797\u7389\u95ee\u9053\uff1a\u201c\u53bb\u54ea\u91cc\u5440\uff1f\u201d \u3000\u3000\u9ad8\u6218\u4fbf\u8bf4\u7ed9\u5979\u542c\uff0c\u6797\u7389\u56b7\u7740\u9053\uff1a\u201c\u6211\u4e5f\u8981\u53bb\uff0c\u6211\u4e5f\u8981\u53bb\u3002\u201d \u3000\u3000\u9ad8\u6218\u6b63\u8981\u5f00\u53e3\u963b\u6b62\uff0c\u90a3\u7ae5\u5b50\u5374\u9053\uff1a\u201c\u597d\u5566\uff0c\u5927\u4f19\u513f\u4e00\u8d77\u53bb\u3002\u201d \u3000\u3000\u6797\u7389\u542c\u5979\u5e2e\u81ea\u5df1\uff0c\u5f88\u662f\u9ad8\u5174\uff0c\u5411\u7ae5\u5b50\u70b9\u5934\u4e00\u7b11\uff0c\u8868\u793a\u8d5e\u8bb8\u3002 \u3000\u3000\u4e09\u4eba\u56de\u5e97\u544a\u8bc9\u6797\u6c76\uff0c\u6797\u6ce2\u5929\u6027\u817c\u8146\uff0c\u6700\u4e0d\u559c\u6b22\u4e71\u54c4\u54c4\u7684\u70ed\u95f9\uff0c\u5c31\u63a8\u8bf4\u75b2\u5026\uff0c\u5728\u5e97\u4e2d\u4f11 \u606f\u3002 \u3000\u3000\u4e00\u8def\u4e0a\uff0c\u7ae5\u5b50\u4e00\u8fb9\u9886\u7740\u8def\uff0c\u4e00\u8fb9\u4f83\u4f83\u800c\u8c08\u3002 \u3000\u3000\u7ae5\u5b50\u9053\uff1a\u201c\u96c1\u8361\u5927\u4fa0\u548c\u6297\u5dde\u8d3e\u4fa0\u662f\u751f\u6b7b\u4e4b\u4ea4\uff0c\u6240\u4ee5\u8d3e\u4fa0\u5c31\u66ff\u4ed6\u5728\u676d\u57ce\u8bbe\u4e0b\u5bff\u5bb4\uff0c\u8fd9\u8d3e\u4fa0 \u662f\u672c\u57ce\u7b2c\u4e00\u5bcc\u6237\uff0c\u72ec\u9738\u82cf\u676d\u7ef8\u4e1d\u4e00\u4e1a\uff0c\u7aef\u7684\u6709\u94b1\u3002\u201d \u3000\u3000\u9ad8\u6218\u8d5e\u9053\uff1a\u201c\u4f60\u61c2\u5f97\u771f\u591a\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5f97\u610f\u7b11\u9053\uff1a\u201c\u6211\u6ca1\u4e8b\u5c31\u78e8\u7740\u7239\u7239\u548c\u6885\u516c\u516c\u7ed9\u8bb2\u6545\u4e8b\uff0c\u6211\u7239\u7239\u5728\u5168\u56fd\u5404\u5730\u884c\u4fa0\uff0c\u4e00\u5e74 \u53ea\u6709\u8fc7\u5e74\u90a3\u51e0\u4e2a\u6708\u5728\u5bb6\uff0c\u4ed6\u5bf9\u4e8e\u6b66\u6797\u4e2d\u4e8b\uff0c\u81ea\u7136\u77e5\u9053\u5f97\u5f88\u6e05\u695a\u3002\u201d \u3000\u3000\u6797\u7389\u6b63\u5f85\u95ee\u4ed6\u7239\u7239\u662f\u8c01\uff0c\u5ffd\u7136\u4e00\u4e2a\u58ee\u6c49\u9a91\u9a6c\u8d70\u5230\u9762\u524d\uff0c\u4e00\u62f1\u624b\u9053\uff1a\u201c\u4e09\u4f4d\u53ef\u662f\u66ff\u96c1\u8361\u5927 \u4fa0\u79e6\u8001\u7237\u5b50\u795d\u5bff\u6765\u7684\u3002\u201d \u3000\u3000\u9ad8\u6218\u8fde\u5fd9\u62f1\u624b\u7b54\u9053\uff1a\u201c\u6b63\u662f\uff0c\u5c31\u8bf7\u58ee\u58eb\u5f15\u89c1\u3002\u201d \u3000\u3000\u90a3\u58ee\u6c49\u9053\uff1a\u201c\u597d\u8bf4\uff0c\u597d\u8bf4\u3002\u201d\u5c31\u5f15\u7740\u4e09\u4eba\u5411\u5de6\u8d70\u5230\u4e00\u5bb6\u5e84\u9662\u524d\uff0c\u8f6c\u8eab\u5bf9\u9ad8\u6218\u9053\uff1a\u201c\u4e3b\u4eba \u5c31\u5728\u5927\u5385\u5bb4\u5ba2\uff0c\u5728\u4e0b\u8fd8\u987b\u63a5\u5f15\u5ba2\u4eba\uff0c\u5c31\u6b64\u544a\u8f9e\u3002\u201d \u3000\u3000\u9ad8\u6218\u8c26\u8f9e\u4e86\u51e0\u53e5\uff0c\u4e09\u4eba\u4e00\u540c\u8d70\u8fdb\u5927\u5385\u3002 \u3000\u3000\u53ea\u89c1\u5927\u5385\u91cc\u706f\u706b\u8f89\u714c\u5f00\u767e\u4f59\u684c\uff0c\u56db\u5468\u70b9\u7740\u7ea2\u8272\u8fd0\u660e\u7684\u5927\u70db\u5982\u624b\u81c2\uff0c\u90a3\u706b\u9985\u6781\u4eae\uff0c\u5374\u6ca1\u6709 \u4e00\u70b9\u9ed1\u70df\uff0c\u70e7\u8d77\u6765\u8fd8\u53d1\u51fa\u9635\u9635\u6a80\u9999\u3002 \u3000\u3000\u7ae5\u793a\u60ca\u9053\uff1a\u201c\u8fd9\u662f\u7389\u95e8\u6a80\u9999\u70db\uff0c\u6211\u5bb6\u91cc\u4e5f\u6709\uff0c\u7239\u7239\u8bf4\u6b64\u70db\u540d\u8d35\u975e\u5e38\uff0c\u5386\u5e74\u6765\u90fd\u662f\u8d21\u7269\uff0c \u8fd9\u8d3e\u4fa0\u771f\u662f\u5bcc\u6709\u65e0\u6bd4\u3002\u201d \u3000\u3000\u4e09\u4eba\u88ab\u5b89\u7f6e\u5728\u5385\u89d2\u4e00\u684c\u5c11\u5e74\u4eba\u5e2d\u4e0a\uff0c\u8fd9\u684c\u4e3b\u4eba\u662f\u8d3e\u4fa0\u5f1f\u5b50\uff0c\u5927\u5bb6\u4e92\u901a\u59d3\u540d\uff0c\u8ffd\u58f0\u4e45\u4ef0\u3002 \u90a3\u7ae5\u5b50\u89c1\u9ad8\u6218\u5728\u65c1\uff0c\u4f3c\u4e4e\u4e0d\u613f\u8bf4\u51fa\u540d\u5b57\uff0c\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u624d\u81ea\u9053\u53eb\u505a\u5f20\u5e73\u3002 \u3000\u3000\u8fd9\u65f6\u9152\u5e2d\u5c1a\u672a\u5f00\u51fa\uff0c\u7fa4\u96c4\u8c08\u7b11\u55a7\u6742\uff0c\u9ad8\u6218\u53ea\u89c1\u8fd9\u5385\u5bbd\u655e\u4e4b\u6781\uff0c\u867d\u5219\u6446\u4e8e\u767e\u4f59\u5e2d\u9152\uff0c\u4e2d\u95f4 \u8fd8\u7a7a\u4e0b\u4e00\u5927\u5757\uff0c\u90a3\u6b63\u4e2d\u653e\u4e86\u4e00\u684c\u7528\u7ea2\u7ed2\u94fa\u7740\uff0c\u4e0a\u9762\u653e\u6ee1\u4e86\u793c\u7269\u3002 \u3000\u3000\u6b63\u55a7\u95f9\u7740\uff0c\u5ffd\u7136\u7b2c\u4e00\u6392\u4e2d\u95f4\u4e00\u5e2d\u7ad9\u8d77\u4e00\u4eba\uff0c\u9ad8\u6218\u4e00\u770b\uff0c\u89c1\u4ed6\u80a5\u5934\u5927\u8033\uff0c\u8863\u7a7f\u7f0e\u5b50\u957f\u886b\uff0c \u5148\u5e72\u54b3\u4e86\u4e24\u58f0\uff0c\u7136\u540e\u53d1\u8a00\u9053\uff1a\u201c\u8bf8\u4f4d\u8001\u5c11\u82f1\u96c4\u6084\u9759\uff0c\u8bf7\u542c\u5728\u4e0b\u4e00\u8a00\u3002\u201d \u3000\u3000\u58f0\u91cf\u5b8f\u4f1f\uff0c\u4e2d\u6c14\u5145\u6c9b\uff0c\u4f17\u4eba\u679c\u7136\u6b62\u4f4f\u8c08\u7b11\uff0c\u9ad8\u6218\u5fc3\u4e2d\u6697\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u4eba\u770b\u6765\u571f\u5934\u571f \u8111\uff0c\u4f46\u662f\u5185\u529b\u751a\u662f\u6df1\u539a\uff0c\u53ea\u6015\u5c31\u662f\u6b64\u95f4\u4e3b\u4eba\u3002\u201d \u3000\u3000\u90a3\u4eba\u63a5\u7740\u9053\uff1a\u201c\u4eca\u5929\u662f\u54b1\u4eec\u79e6\u8001\u54e5\u54e5\u516d\u5341\u5927\u5bff\uff0c\u53c8\u662f\u8001\u54e5\u54e5\u5c01\u5251\u9000\u9690\u4e4b\u65e5\uff0c\u54b1\u4eec\u79e6\u8001\u54e5 \u4e00\u751f\u95ef\u8361\u6c5f\u6e56\uff0c\u4e0a\u5bf9\u5929\uff0c\u4e0b\u5bf9\u5730\uff0c\u4e2d\u5bf9\u6b66\u6797\u5c0a\u5e7c\uff0c\u7eff\u6797\u8c6a\u6770\uff0c\u7aef\u7684\u53ef\u79f0\u5f97\u4e0a\u2018\u4ec1\u2019\u3001 \u2018\u6148\u2019\u3001\u2018\u5fe0\u2019\u3001\u2018\u4e49\u2019\u56db\u5b57\uff0c\u4eca\u671d\u65e2\u662f\u8001\u54e5\u54e5\u7684\u597d\u65e5\u5b50\uff0c\u6211\u505a\u5144\u5f1f\u7684\u6709\u5e78\u80fd\u591f\u66ff\u79e6\u8001\u54e5\u505a \u5bff\uff0c\u771f\u662f\u5e73\u751f\u5feb\u4e8b\uff0c\u5404\u4f4d\u5343\u91cc\u800c\u6765\uff0c\u62db\u5f85\u4e0d\u5468\u4e4b\u5904\uff0c\u53ea\u602a\u6211\u8fd9\u505a\u5546\u4eba\u7684\u5c0f\u6c14\uff0c\u65a4\u65a4\u8ba1\u8f83\uff0c\u53ef \u5343\u4e07\u522b\u8bf4\u6211\u8fd9\u8001\u54e5\u4e0d\u591f\u610f\u601d\u3002\u201d\u3000\u3000\u3000\u3000\u4f17\u4eba\u8f70\u7b11\u53eb\u597d\uff0c\u9ad8\u6218\u5fc3\u60f3\uff1a\u201c\u6b64\u4eba\u679c\u7136\u662f\u8d3e\u4fa0\uff0c\u77a7 \u4ed6\u4e00\u8eab\u529f\u592b\uff0c\u5374\u80fd\u6df1\u85cf\u82e6\u865a\uff0c\u8fd9\u79cd\u6c14\u5ea6\u7684\u786e\u975e\u51e1\u3002\u201d \u3000\u3000\u8d3e\u4fa0\u53c8\u9053\uff1a\u201c\u5ea7\u4e2d\u8bf8\u4f4d\u5b9a\u6709\u95fb\u540d\u800c\u6765\uff0c\u8fde\u6211\u8fd9\u8001\u54e5\u54e5\u9762\u90fd\u6ca1\u89c1\u8fc7\u7684\u4eba\uff0c\u73b0\u5728\u5c31\u8bf7\u5bff\u661f\u5411 \u5404\u4f4d\u8bf4\u51e0\u53e5\u8bdd\u3002\u201d \u3000\u3000\u4ed6\u65b9\u8bf4\u6bd5\uff0c\u540c\u684c\u9996\u5e2d\u7ad9\u8d77\u4e00\u4e2a\u8001\u8005\uff0c\u4e94\u77ed\u8eab\u6750\uff0c\u5934\u9876\u5168\u79c3\uff0c\u7709\u6bdb\u53c8\u957f\u53c8\u767d\uff0c\u7ea2\u5149\u6e05\u9762\uff0c \u771f\u6709\u70b9\u50cf\u90a3\u5357\u6781\u5bff\u661f\uff0c\u9ad8\u6218\u5411\u8eab\u65c1\u4e00\u770b\uff0c\u53ea\u89c1\u6797\u7389\u805a\u7cbe\u4f1a\u795e\u7684\u542c\u7740\u90a3\u7ae5\u5b50\u8bf4\uff0c\u4e24\u4eba\u5df2\u7136\u751a\u4e3a \u878d\u6d3d\u3002 \u3000\u3000\u90a3\u8001\u8005\u79bb\u5e2d\u8d70\u5230\u5385\u4e2d\uff0c\u5148\u5411\u56db\u5468\u4f5c\u4e86\u4e00\u4e2a\u7f62\u5708\u63d6\u8bf4\u9053\uff1a\u201c\u8001\u673d\u5448\u5404\u4f4d\u5173\u6000\uff0c\u771f\u662f\u611f\u6fc0\u4e0d \u5c3d\uff0c\u8001\u673d\u65e0\u6cd5\u62a5\u7b54\u8bf8\u4f4d\uff0c\u5f85\u4f1a\u8bf8\u4f4d\u591a\u5403\u70b9\uff0c\u591a\u559d\u70b9\uff0c\u5c31\u7b97\u770b\u5f97\u8d77\u8001\u673d\u3002\u201d \u3000\u3000\u4ed6\u672c\u5450\u53e3\u820c\uff0c\u6b64\u65f6\u5fc3\u4e2d\u6fc0\u52a8\uff0c\u66f4\u662f\u7ed3\u7ed3\u5df4\u5df4\uff0c\u597d\u4e0d\u5bb9\u6613\u4ea4\u5f85\u5b8c\u6bd5\uff0c\u4f17\u4eba\u542c\u4ed6\u8bf4\u5f97\u867d\u7136\u4f3c \u901a\u4e0d\u901a\uff0c\u4f46\u8bed\u6c14\u8bda\u6073\uff0c\u4e0d\u89c9\u6b22\u7136\u53eb\u597d\u3002 \u3000\u3000\u96c1\u8361\u5927\u4fa0\u53c8\u9053\uff1a\u201c\u8001\u673d\u662f\u4e2a\u7c97\u4eba\uff0c\u4e00\u751f\u884c\u4e8b\u53ea\u77e5\u9053\u51ed\u826f\u5fc3\u800c\u4e3a\uff0c\u60f3\u8001\u673d\u4f55\u5fb7\u4f55\u80fd\uff0c\u600e\u914d \u79f0\u5927\u4fa0\uff0c\u4ece\u4eca\u4ee5\u540e\uff0c\u8fd9\u6c5f\u6e56\u4e0a\u670b\u53cb\u9001\u7ed9\u8001\u673d\u597d\u73a9\u7684\u5916\u53f7\u2018\u96c1\u8361\u5927\u4fa0\u2019\uff0c\u5c31\u8fd8\u7ed9\u5927\u5bb6\uff0c\u4fd7\u8bed \u9053\uff1a\u201c\u957f\u6c5f\u540e\u6d6a\u63a8\u524d\u6d6a\u3002\u2019\u5e0c\u671b\u5ea7\u4e2d\u5404\u4f4d\u5c11\u5e74\u82f1\u96c4\uff0c\u5c06\u6765\u4e0d\u4f46\u80fd\u5728\u6c5f\u5357\u96c1\u8361\u79f0\u4fa0\uff0c\u66f4\u80fd\u51fa\u51e0 \u4e2a\u4e2d\u539f\u5927\u4fa0\uff0c\u795e\u5dde\u5927\u4fa0\u3002\u201d \u3000\u3000\u4f17\u4eba\u89c1\u4ed6\u7a81\u7136\u6d41\u5229\u8d77\u6765\uff0c\u4e0d\u7981\u90fd\u611f\u5947\u602a\uff0c\u53ea\u6709\u8d3e\u4fa0\u6697\u6697\u597d\u7b11\uff0c\u6562\u60c5\u4ed6\u8001\u65e9\u5c31\u66ff\u8fd9\u4f4d\u751f\u6b7b \u597d\u53cb\u51c6\u5907\u597d\u8bb2\u8bcd\uff0c\u53ef\u7b11\u8fd9\u4f4d\u8001\u54e5\u54e5\u5148\u524d\u6fc0\u52a8\u4e0d\u5df2\uff0c\u7adf\u7136\u968f\u53e3\u8bf4\u4e86\u4e00\u5927\u6bb5\u3002 \u3000\u3000\u96c1\u8361\u5927\u4fa0\u8fd9\u756a\u8bdd\u8bf4\u5f97\u5408\u60c5\u5408\u7406\uff0c\u5750\u4e0b\u8bf8\u5c11\u5e74\u66f4\u662f\u6fc0\u594b\u65e0\u6bd4\uff0c\u9f50\u58f0\u9f13\u638c\u53eb\u9053\uff1a\u201c\u591a\u8c22\u79e6\u8001 \u7237\u5b50\u9f13\u52b1\u3002\u201d \u3000\u3000\u4f17\u4eba\u90fd\u662f\u6b66\u529f\u9ad8\u660e\u4e4b\u58eb\uff0c\u8fd9\u4e00\u62cd\u638c\uff0c\u58f0\u5982\u96f7\u51fb\uff0c\u76f4\u51b2\u9704\u6c49\uff0c\u4ee4\u4eba\u632f\u594b\u4e0d\u5df2\u3002 \u3000\u3000\u6797\u7389\u6084\u6084\u5bf9\u90a3\u7ae5\u5b50\u9053\uff1a\u201c\u4f60\u77a7\u8fd9\u4f4d\u8001\u4f2f\u4f2f\u591a\u4e48\u53d7\u4eba\u7231\u6234\uff0c\u4e00\u4e2a\u4eba\u5982\u679c\u80fd\u88ab\u4eba\u8fd9\u6837\u5c0a\u656c\uff0c \u5c31\u662f\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u90a3\u4e5f\u8be5\u6ee1\u8db3\u4e86\u3002\u201d \u3000\u3000\u7ae5\u5b50\u70b9\u70b9\u5934\u9053\uff1a\u201c\u7239\u7239\u8bf4\u96c1\u8361\u5927\u4fa0\u662f\u6761\u597d\u6c49\u5b50\uff0c\u770b\u6765\u679c\u7136\u4e0d\u9519\uff0c\u53ea\u662f\u4ed6\u6b66\u529f\u867d\u9ad8\uff0c\u884c\u4e8b \u6b63\u6d3e\uff0c\u53ef\u60dc\u80f8\u65e0\u70b9\u58a8\uff0c\u6c14\u5ea6\u5c31\u4e0d\u80fd\u8ddf\u5434\u4f2f\u4f2f\u548c\u7239\u7239\u6bd4\u3002\u201d \u3000\u3000\u6797\u7389\u9053\uff1a\u201c\u4f60\u7239\u7239\u5f53\u771f\u8fd9\u4e48\u4e86\u4e0d\u8d77\u4e48\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u6b63\u8272\u9053\uff1a\u201c\u6211\u4ece\u6765\u6ca1\u89c1\u8fc7\u50cf\u4ed6\u90a3\u79cd\u771f\u6b63\u7684\u7537\u5b50\u6c49\uff0c\u5927\u4e08\u592b\u3002\u201d \u3000\u3000\u6797\u7389\u89c1\u4ed6\u795e\u8272\u7a81\u53d8\u51db\u91cd\uff0c\u4e0d\u6562\u8bf4\u7b11\uff0c\u4e00\u53e5\u5230\u53e3\u8fb9\u7684\u8bdd\u53c8\u7f29\u56de\u53bb\uff0c\u4fbf\u9053\uff1a\u201c\u6211\u7239\u7239\u4ed6\u4e5f\u662f \u4e2a\u4e86\u4e0d\u8d77\u7684\u4eba\u3002\u201d \u3000\u3000\u7ae5\u5b50\u9053\uff1a\u201c\u4f60\u7239\u7239\u5728\u54ea\u91cc\u3002\u201d \u3000\u3000\u6797\u7389\u51c4\u7136\u9053\uff1a\u201c\u88ab\u574f\u4eba\u6253\u6b7b\u4e86\u3002\u201d \u3000\u3000\u7ae5\u5b50\u4e00\u60ca\uff0c\u6293\u4f4f\u6797\u7389\u624b\u9053\uff1a\u201c\u662f\u8c01\uff1f\u662f\u8c01\uff1f\u6211\u66ff\u4f60\u62a5\u4ec7\u3002\u201d \u3000\u3000\u6797\u7389\u4f4e\u58f0\u9053\uff1a\u201c\u90a3\u574f\u4eba\u88ab\u9ad8\u5927\u54e5\u6253\u4e0b\u7edd\u5d16\u53bb\uff0c\u591a\u534a\u5df1\u7ecf\u6b7b\u5566\u3002\u201d \u3000\u3000\u7ae5\u5b50\u9053\uff1a\u201c\u5982\u679c\u8fd8\u6709\u574f\u4eba\u6b3a\u4fae\u4f60\uff0c\u6211\u66ff\u4f60\u6253\u3002\u201d \u3000\u3000\u6797\u7389\u5ae3\u7136\u7b11\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u5566\u3002\u201d \u3000\u3000\u539f\u6765\u90a3\u7ae5\u5b50\u5e74\u9f84\u867d\u5e7c\uff0c\u5929\u6027\u5374\u662f\u6781\u4e3a\u968f\u548c\uff0c\u4ed6\u53e3\u624d\u53c8\u597d\uff0c\u548c\u4ed6\u8c08\u8d77\u8bdd\u6765\uff0c\u771f\u662f\u5999\u8da3\u6a2a \u751f\uff0c\u4ee4\u4eba\u6709\u9187\u7136\u4e4b\u611f\uff0c\u867d\u7136\u6709\u65f6\u4f1a\u6765\u4e0a\u51e0\u53e5\u7ae5\u8a00\u7ae5\u8bed\uff0c\u53ef\u662f\u65e0\u4f24\u5927\u4f53\uff0c\u4f7f\u4eba\u542c\u5f97\u66f4\u662f\u6709\u8da3\u3002 \u6797\u7389\u8d77\u521d\u5bf9\u4ed6\u53ea\u662f\u8d4c\u6c14\u4e0d\u7406\uff0c\u540e\u6765\u542c\u4ed6\u6ed4\u6ed4\u8a00\u8c08\uff0c\u5fc3\u4e2d\u518d\u4e5f\u6309\u8010\u4e0d\u4f4f\uff0c\u4e5f\u548c\u4ed6\u5927\u8c08\u8d77\u6765\u3002 \u3000\u3000\u6b64\u65f6\u9152\u83dc\u5df2\u7ecf\u642c\u4e0a\uff0c\u8fd9\u676d\u5dde\u7684\u996e\u98df\u662f\u5929\u4e0b\u4e00\u7edd\uff0c\u8fd9\u756a\u8d3e\u4fa0\u4e3a\u8001\u54e5\u54e5\u505a\u5bff\uff0c\u5f53\u7136\u662f\u904d\u8bf7\u540d \u53a8\u540d\u83dc\uff0c\u90a3\u9152\u4e5f\u662f\u5345\u5e74\u201c\u5973\u513f\u7ea2\u201d\u9648\u5e74\u82b1\u96d5\uff0c\u53ea\u5403\u5f97\u4f17\u4eba\u7709\u5f00\u773c\u7b11\uff0c\u5404\u81ea\u8d5e\u7f8e\u4e0d\u7edd\u3002 \u3000\u3000\u96c1\u8361\u5927\u4fa0\u79e6\u658c\u770b\u770b\u9152\u5bb4\u5c06\u9611\uff0c\u5c31\u8d70\u5411\u5806\u7740\u793c\u7269\u7684\u684c\u4e0a\uff0c\u4e00\u4ef6\u4ef6\u62c6\u5f00\uff0c\u6bcf\u53d6\u51fa\u4e00\u4ef6\u793c\u54c1\uff0c \u5c31\u5411\u9001\u793c\u7684\u7fa4\u4fa0\u518d\u4e09\u8a00\u8c22\u3002 \u3000\u3000\u5239\u65f6\u95f4\u684c\u4e0a\u5b9d\u5149\u8f89\u6620\uff0c\u6709\u5c3a\u8bb8\u7684\u5927\u73ca\u745a\uff0c\u6709\u9e3d\u5375\u5927\u7684\u73cd\u73e0\uff0c\u771f\u662f\u7f8e\u4e0d\u80dc\u6536\uff0c\u96c1\u8361\u5927\u4fa0\u8001 \u6000\u5927\u7545\u3002 \u3000\u3000\u8d3e\u4fa0\u7ad9\u5728\u4ed6\u8eab\u65c1\uff0c\u66ff\u4ed6\u6574\u7406\uff0c\u8138\u4e0a\u6de1\u7136\u800c\u7b11\uff0c\u4eba\u4f3c\u4e4e\u628a\u8fd9\u5b9d\u7269\u5e76\u4e0d\u653e\u5728\u773c\u91cc\u3002 \u3000\u3000\u96c1\u8361\u5927\u4fa0\u770b\u89c1\u684c\u89d2\u4e0a\u6709\u4e00\u4e2a\u5305\u7ea2\u7f0e\u5305\u7684\u5927\u76d2\u5b50\uff0c\u4ed6\u4f38\u624b\u4e00\u63d0\uff0c\u53ea\u89c9\u8f7b\u82e5\u65e0\u7269\uff0c\u5fc3\u4e2d\u5927 \u5947\uff0c\u4e00\u770b\u7ea2\u7f0e\u4e0a\u5199\u7740\u201c\u96c1\u8361\u5927\u4fa0\u5927\u5bff\u4e4b\u559c\u3002\u665a\u8f88\u91d1\u7ae5\u8f9b\u5e73\u62dc\u201d\u3002 \u3000\u3000\u4ed6\u60f3\u4e0d\u8d77\u81ea\u5df1\u8ba4\u8bc6\u8fd9\u4e2a\u4eba\uff0c\u5fc3\u9053\u53ea\u6015\u662f\u6c5f\u6e56\u540e\u8f88\u9001\u7684\uff0c\u5c31\u6253\u5f00\u76d2\u76d6\uff0c\u201c\u547c\u201d\u7684\u4e00\u58f0\uff0c\u8df3 \u51fa\u4e00\u7269\u3002 \u3000\u3000\u96c1\u8361\u5927\u4fa0\u8eab\u5f62\u4e00\u64a4\uff0c\u95ea\u8fc7\u76d2\u4e2d\u4e4b\u7269\uff0c\u6b63\u81ea\u5fff\u6012\uff0c\u4e0d\u77e5\u662f\u4f55\u65b9\u4ec7\u4eba\u60f3\u6697\u7b97\u81ea\u5df1\uff0c\u4f46\u8f6c\u773c\u770b \u770b\u8d3e\u4fa0\uff0c\u53ea\u89c1\u4ed6\u6ee1\u8138\u7b11\u5bb9\uff0c\u4f17\u4eba\u4e5f\u662f\u9762\u6709\u559c\u8272\uff0c\u4f4e\u5934\u4e00\u77a7\uff0c\u539f\u6765\u76d2\u4e2d\u8df3\u51fa\u6765\u4e4b\u7269\u662f\u4e2a\u80d6\u80d6\u7684 \u94c1\u5a03\u5a03\uff0c\u62f1\u8d77\u5c0f\u624b\u5411\u5927\u5bb6\u5531\u7740\u80a5\u558f\uff0c\u5fc3\u4e2d\u4e5f\u4e50\u4e86\u3002 \u3000\u3000\u90a3\u94c1\u5a03\u5a03\u662f\u673a\u7c27\u6240\u63a7\u5236\uff0c\u4e0d\u4e45\u5c31\u505c\u6b62\u4e86\uff0c\u96c1\u8361\u5927\u4fa0\u5fc3\u60f3\u9001\u8fd9\u793c\u7684\u662f\u53eb\u4ec0\u4e48\u91d1\u7ae5\uff0c\u770b\u6765\u6b64 \u4eba\u5b69\u5b50\u6c14\u5f97\u5f88\uff0c\u5012\u662f\u540d\u7b26\u5176\u5b9e\u3002 \u3000\u3000\u9ad8\u6218\u5f00\u5fc3\u4e00\u7b11\uff0c\u6797\u7389\u89c9\u5f97\u90a3\u5a03\u5a03\u5f88\u662f\u597d\u73a9\uff0c\u4fbf\u5bf9\u8eab\u65c1\u7ae5\u5b50\u9053\uff1a\u201c\u90a3\u80d6\u5a03\u5a03\u771f\u53ef\u7231\u3002\u201d \u3000\u3000\u7ae5\u5b50\u8138\u4e0a\u4e0d\u52a8\u58f0\u8272\uff0c\u4f46\u5374\u63a9\u4e0d\u4f4f\u559c\u6c14\u6d0b\u6ea2\uff0c\u95fb\u8a00\u7b11\u9053\uff1a\u201c\u8fd9\u6709\u4ec0\u4e48\u4e86\u4e0d\u5f97\uff0c\u6211\u5bb6\u91cc\u4e5f\u6709 \u4e00\u4e2a\u3002\u201d \u3000\u3000\u6797\u7389\u597d\u751f\u7fa1\u6155\uff0c\u4f46\u60f3\u5230\u8fd9\u73a9\u610f\u513f\u5b9a\u662f\u7ae5\u5b50\u5fc3\u7231\u4e4b\u7269\uff0c\u5c31\u4f4f\u53e3\u4e0d\u8bf4\uff0c\u8fd9\u53ef\u7b97\u662f\u5979\u7b2c\u4e00\u6b21\u4e3a \u522b\u4eba\u7740\u60f3\u54e9\u3002 \u3000\u3000\u8d3e\u4fa0\u5ffd\u9053\uff1a\u201c\u5144\u5f1f\u6708\u524d\u5076\u800c\u5f97\u5230\u4e00\u6837\u7a00\u4e16\u5b9d\u7269\uff0c\u672c\u6765\u60f3\u8981\u732e\u7ed9\u6211\u8fd9\u4f4d\u8001\u54e5\u505a\u4e3a\u5bff\u793c\uff0c\u53ef \u662f\u6211\u8fd9\u8001\u54e5\u4fa0\u4e49\u80f8\u6000\uff0c\u4ed6\u8bf4\u4ed6\u65e2\u5df2\u51b3\u5b9a\u9000\u9690\uff0c\u8fd9\u4e2a\u4e8b\u7269\u7528\u4e0d\u7740\uff0c\u4e0d\u80af\u63a5\u53d7\uff0c\u5e76\u4e14\u5efa\u8bae\u4f5c\u5144\u5f1f \u7684\u8d60\u9001\u7ed9\u4e00\u4f4d\u884c\u4fa0\u4ed7\u4e49\u7684\u8c6a\u6770\uff0c\u4f5c\u4e3a\u4ed6\u6d4e\u4e16\u6551\u4eba\u4e4b\u7528\u3002\u201d \u3000\u3000\u4f17\u4eba\u542c\u4ed6\u8bf4\u4e86\u534a\u5929\uff0c\u5e76\u672a\u8bf4\u51fa\u5b9d\u7269\u540d\u79f0\uff0c\u4f46\u5747\u77e5\u8d3e\u4fa0\u5bcc\u53ef\u654c\u56fd\uff0c\u4ed6\u53e3\u4e2d\u7684\u7a00\u4e16\u5b9d\u7269\u81ea\u662f \u975e\u540c\u5c0f\u53ef\uff0c\u4e0d\u7531\u90fd\u4fa7\u8033\u503e\u542c\u3002 \u3000\u3000\u8d3e\u4fa0\u63a5\u7740\u9053\uff1a\u201c\u8bd5\u60f3\u6c5f\u6e56\u4e0a\u8c6a\u4fa0\u6bd4\u6bd4\u7686\u662f\uff0c\u800c\u8fd9\u5b9d\u7269\u53ea\u6709\u4e00\u5b97\uff0c\u4fd7\u8bed\u9053\uff0c\u2018\u5b9d\u5404\u6709 \u4e3b\u2019\uff0c\u5982\u679c\u8fd9\u5b9d\u7269\u4e3a\u4e00\u9876\u5929\u7acb\u5730\u7684\u597d\u6c49\u5b50\u6240\u6709\uff0c\u81ea\u7136\u662f\u4eba\u7269\u76f8\u5f97\u76ca\u5f70\uff0c\u9020\u798f\u6c5f\u6e56\uff0c\u4f46\u5982\u679c\u662f \u843d\u5165\u5978\u4eba\u4e4b\u624b\uff0c\u5374\u662f\u5927\u5927\u59d4\u66f2\u4e86\u8fd9\u5b9d\u7269\u3002\u50cf\u5144\u5f1f\u8fd9\u79cd\u5546\u8d3e\uff0c\u552f\u5229\u662f\u56fe\uff0c\u523b\u8584\u6210\u6027\uff0c\u81ea\u662f\u4e0d\u914d \u62e5\u6709\uff0c\u5e38\u8a00\u9053\uff1a\u201c\u5339\u592b\u4f55\u7f6a\uff0c\u6000\u58c1\u5176\u7f6a\u3002\u2019\u5144\u5f1f\u5982\u679c\u5984\u60f3\u9738\u6709\u6b64\u5b9d\uff0c\u4e0d\u4f46\u672a\u80fd\u9020\u798f\u751f\u6c11\uff0c\u53ea \u6015\u8fde\u547d\u513f\u4e5f\u4fdd\u4e0d\u4f4f\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u6697\u7b11\uff0c\u5fd6\u9053\uff1a\u201c\u6b64\u4eba\u53e3\u820c\u4fbf\u5229\uff0c\u4f46\u662f\u80f8\u895f\u5f00\u9614\uff0c\u7aef\u7684\u662f\u4e2a\u5f02\u4eba\u3002\u201d \u3000\u3000\u4f17\u4eba\u4e2d\u6709\u4e9b\u5c11\u5e74\uff0c\u5df1\u7ecf\u6309\u8010\u4e0d\u4f4f\uff0c\u7eb7\u7eb7\u95ee\u9053\uff1a\u201c\u51cc\u8001\u524d\u8f88\uff0c\u5012\u5e95\u662f\u4ec0\u4e48\u5b9d\u7269\uff0c\u6709\u8fd9\u4e48\u540d \u8d35\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u60f3\u6b64\u4eba\u539f\u6765\u59d3\u51cc\uff0c\u8d3e\u4fa0\u9053\uff1a\u201c\u8fd9\u662f\u6c5f\u6e56\u4e0a\u4f20\u95fb\u5931\u843d\u767e\u5e74\u7684\u2018\u98ce\u96f7\u6c34\u706b\u5b9d\u73e0\u2019\u3002\u201d \u3000\u3000\u5ea7\u4e2d\u4e00\u822c\u5c11\u5e74\uff0c\u56e0\u4e3a\u5e74\u8f7b\uff0c\u6240\u77e5\u6709\u9650\uff0c\u5012\u8fd8\u7f62\u4e86\uff0c\u90a3\u4e9b\u6b66\u6797\u524d\u8f88\uff0c\u5374\u90fd\u5927\u5403\u4e00\u60ca\u3002\u539f\u6765 \u6b64\u662f\u53e4\u6765\u4ea4\u8dbe\u56fd\u8fdb\u8d21\u4e4b\u7269\uff0c\u540e\u6765\u4e0d\u77e5\u4f55\u5e74\u4f55\u6708\u7ecf\u8fc7\u591a\u6b21\u6218\u4e71\uff0c\u81ea\u5bab\u5ef7\u6d41\u5165\u6c11\u95f4\uff0c\u4fbf\u4e00\u76f4\u4e3a\u6b66 \u6797\u4e2d\u4eba\u89c6\u4e3a\u81f3\u5b9d\uff0c\u767e\u5e74\u524d\u5c11\u6797\u5bfa\u4e09\u5927\u9ad8\u50e7\u4e4b\u4e00\u7075\u4e91\u548c\u5c1a\u6240\u5f97\uff0c\u7a7f\u5728\u4e00\u4e32\u5ff5\u73e0\u4e2d\uff0c\u7ec8\u65e5\u6302\u5728\u8eab \u4e0a\uff0c\u540e\u6765\u5c11\u6797\u5bfa\u906d\u9047\u5927\u53d8\uff0c\u4e09\u5927\u9ad8\u50e7\u7eb7\u7eb7\u79bb\u5bfa\u800c\u53bb\uff0c\u5176\u4e2d\u7b2c\u4e8c\u4f4d\u9ad8\u50e7\u7075\u7a7a\uff0c\u5373\u662f\u4e1c\u6d77\u5927\u6222\u5c9b \u5e73\u51e1\u4e0a\u4eba\u3002 \u3000\u3000\u76f8\u4f20\u8be5\u73e0\u80fd\u591f\u907f\u706b\u907f\u6c34\uff0c\u5e76\u5df2\u6df1\u5177\u7075\u6027\uff0c\u80fd\u591f\u9884\u535c\u5409\u51f6\uff0c\u544a\u8b66\u4e3b\u4eba\uff0c\u8fd8\u6709\u4e00\u9879\u6700\u5927\u597d \u5904\uff0c\u5c31\u662f\u5584\u89e3\u767e\u6bd2\uff0c\u4efb\u662f\u4e2d\u4e86\u5929\u4e0b\u81f3\u6bd2\u6697\u5668\uff0c\u53ea\u6d88\u5c06\u8be5\u73e0\u6eda\u8fc7\u4f24\u53e3\uff0c\u5c31\u5c31\u80fd\u5438\u51fa\u6bd2\u7d20\u3002 \u3000\u3000\u4f17\u4eba\u7eb7\u7136\u8033\u8bed\uff0c\u867d\u7136\u90fd\u90e8\u662f\u6c5f\u5357\u6b66\u6797\u540d\u5bb6\uff0c\u4ec0\u4e48\u5927\u573a\u9762\u90fd\u89c1\u8fc7\uff0c\u53ef\u662f\u4e00\u542c\u6b64\u73e0\uff0c\u5fc3\u4e2d\u90fd \u4e0d\u7981\u7830\u7136\u5fc3\u52a8\u3002 \u3000\u3000\u8d3e\u4fa0\u53c8\u9053\uff1a\u201c\u4eca\u65e5\u5728\u5ea7\u5404\u4f4d\uff0c\u4ec1\u5fc3\u4fa0\u884c\u81ea\u662f\u4e0d\u5fc5\u5144\u5f1f\u591a\u8bf4\uff0c\u8bf4\u529f\u592b\u4e48\uff0c\u53c8\u90fd\u662f\u6c5f\u5357\u9876\u513f \u5c16\u513f\u7684\u597d\u624b\uff0c\u6240\u4ee5\u5144\u5f1f\u80e1\u4e71\u60f3\u51fa\u4e00\u4e2a\u4e3b\u610f\uff0c\u5144\u5f1f\u539f\u6765\u662f\u9001\u7ed8\u79e6\u8001\u54e5\uff0c\u4ed6\u8001\u5144\u5b64\u82e6\u534a\u751f\uff0c\u6700\u7231 \u62a4\u82f1\u4fca\u4e0d\u7fa4\u7684\u540e\u8f88\uff0c\u5f85\u4f1a\u5c31\u8bf7\u5ea7\u4e2d\u5e74\u5728\u5eff\u4ee5\u4e0b\u7684\u5c0f\u5144\u5f1f\u4eec\uff0c\u5404\u81ea\u9732\u4e0a\u51e0\u624b\uff0c\u518d\u7531\u6211\u8fd9\u8001\u54e5\u8bc4 \u5b9a\uff0c\u529f\u592b\u6700\u5947\u5999\u9ad8\u5f3a\u7684\uff0c\u5c31\u662f\u98ce\u96f7\u6c34\u706b\u73e0\u7684\u5f97\u4e3b\u3002\u4e0d\u8fc7\u4eca\u65e5\u662f\u8001\u54e5\u54e5\u7684\u5927\u559c\u65e5\u5b50\uff0c\u54b1\u4eec\u53ef\u4e0d \u4fbf\u52a8\u624b\u8fc7\u62db\uff0c\u5982\u679c\u6709\u4e2a\u5931\u95ea\uff0c\u987b\u5f97\u5927\u5bb6\u9762\u76ae\u4e0d\u597d\u770b\uff0c\u6240\u4ee5\u53ea\u5e0c\u671b\u5404\u4f4d\u5728\u8fd9\u7ec3\u6b66\u5385\u4e2d\uff0c\u5404\u81ea\u5355 \u72ec\u6f14\u4e0a\u51e0\u62db\u3002\u201d \u3000\u3000\u4f17\u5c11\u5e74\u90fd\u662f\u4e00\u756a\u8dc3\u8dc3\u6b32\u8bd5\u7684\u795e\u8272\uff0c\u4ed6\u4eec\u5e76\u4e0d\u6e05\u695a\u90a3\u5b9d\u73e0\u53ef\u8d35\u4e4b\u5904\uff0c\u662f\u4ee5\u5927\u5bb6\u7684\u5fc3\u601d\u90fd\u662f \u60f3\u5728\u8fd9\u5927\u4f17\u5e7f\u5ead\u4e4b\u4e0b\u626c\u540d\u9732\u8138\u3002 \u3000\u3000\u6797\u7389\u6084\u6084\u5bf9\u9ad8\u6218\u9053\uff1a\u201c\u5927\u54e5\uff0c\u4f60\u4e5f\u53bb\u3002\u201d \u3000\u3000\u9ad8\u6218\u6447\u5934\u9053\uff1a\u201c\u6211\u90a3\u70b9\u529f\u592b\uff0c\u522b\u732e\u4e11\u5566\u3002\u201d \u3000\u3000\u6797\u7389\u53c8\u5bf9\u8eab\u65c1\u7ae5\u5b50\u9053\uff1a\u201c\u4f60\u5462\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u7b11\u7b11\uff0c\u6b63\u8272\u9053\uff1a\u201c\u8fd9\u98ce\u96f7\u6c34\u706b\u73e0\u6211\u542c\u7239\u7239\u8bf4\u8fc7\uff0c\u5176\u4e2d\u9690\u85cf\u4e86\u5f88\u5927\u7684\u79d8\u5bc6\uff0c\u8c01\u5f97\u7740 \u4e86\uff0c\u53ea\u6015\u5c06\u6765\u5927\u6709\u9ebb\u70e6\u3002\u201d \u3000\u3000\u6b64\u65f6\u8d3e\u4fa0\u5df2\u7ecf\u5429\u5490\u4ec6\u4eba\u5c06\u9152\u80b4\u6536\u8d77\uff0c\u5927\u5bb6\u56f4\u5728\u5927\u5385\u4e00\u8fb9\uff0c\u4e2d\u95f4\u7a7a\u51fa\u5f88\u5927\u4e00\u5757\u5730\u65b9\uff0c\u8fd9\u65f6 \u6709\u4eba\u62ac\u8fdb\u5175\u5668\u67b6\uff0c\u653e\u5728\u4e24\u65c1\u3002 \u3000\u3000\u5ffd\u7136\u4e00\u4e2a\u5c11\u5e74\uff0c\u4ece\u4eba\u7fa4\u4e2d\u8dc3\u51fa\uff0c\u5411\u5927\u5bb6\u4e00\u63d6\uff0c\u6717\u58f0\u9053\uff1a\u201c\u665a\u8f88\u848b\u5357\u732e\u4e11\uff0c\u8bf7\u5404\u4f4d\u82f1\u5806\u6307 \u6559\u3002\u201d \u3000\u3000\u9ad8\u6218\u89c1\u4ed6\u5f97\u733f\u81c2\u8702\u8170\uff0c\u751a\u662f\u82f1\u633a\uff0c\u4e0d\u89c9\u9887\u751f\u597d\u611f\uff0c\u90a3\u5c11\u5e74\u4ece\u5175\u5668\u67b6\u4e2d\u53d6\u6765\u4e00\u6839\u9554\u94c1\u957f \u67aa\uff0c\u731b\u5438\u4e00\u53e3\u771f\u6c14\uff0c\u4e00\u6296\u67aa\u6746\uff0c\u5f92\u8d77\u67aa\u82b1\uff0c\u5c31\u5728\u5385\u4e2d\u821e\u4e86\u8d77\u6765\u3002 \u3000\u3000\u4f46\u89c1\u4ed6\u8d8a\u821e\u8d8a\u9163\uff0c\u6e10\u6e10\u8eab\u5f62\u96be\u89c1\uff0c\u904d\u4f53\u767d\u5149\u7ed5\u8eab\uff0c\u6709\u5982\u68a8\u82b1\u7eb7\u7eb7\u843d\u5730\uff0c\u597d\u770b\u5df2\u6781\uff0c\u6797\u7389 \u4e0d\u7981\u7709\u98de\u8272\u821e\uff0c\u8f7b\u8f7b\u53f9\u9053\uff1a\u201c\u8fd9\u67aa\u6cd5\u771f\u662f\u597d\u770b\uff0c\u6211\u4e5f\u60f3\u5b66\u5b66\u6b66\u827a\u3002\u201d \u3000\u3000\u7ae5\u5b50\u9053\uff1a\u201c\u8fd9\u5012\u6ca1\u6709\u4ec0\u4e48\u5389\u5bb3\uff0c\u7b49\u4f1a\u624d\u6709\u5389\u5bb3\u7684\u5462\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u60f3\uff1a\u201c\u8fd9\u67aa\u6cd5\u867d\u5219\u597d\u770b\uff0c\u5176\u5b9e\u8f7b\u7075\u6709\u4f59\uff0c\u6c89\u731b\u4e0d\u8db3\uff0c\u4ea4\u8d77\u624b\u6765\uff0c\u5e76\u4e0d\u7ba1\u7528\uff0c\u6bd4\u8d77 \u7239\u7239\u6240\u4f20\u2018\u9ad8\u5bb6\u621f\u6cd5\u2019\u5dee\u8fdc\u4e86\u3002\u201d \u3000\u3000\u4ed6\u672c\u4eba\u4e5f\u662f\u4f7f\u7528\u957f\u5175\uff0c\u662f\u4ee5\u5bf9\u5c11\u5e74\u67aa\u6cd5\u4e00\u62db\u4e00\u5f0f\u90fd\u7559\u5fc3\u89c2\u770b\uff0c\u5ffd\u89c1\u5c11\u5e74\u8eab\u5f62\u4e00\u6ede\uff0c\u624b\u4e2d \u957f\u67aa\u987f\u65f6\u6162\u4e86\u8d77\u6765\uff0c\u4e1c\u6311\u897f\u523a\uff0c\u5168\u7136\u4e0d\u6210\u62db\u5f0f\uff0c\u9ad8\u6218\u770b\u4e86\u534a\u664c\uff0c\u4e5f\u770b\u4e0d\u51fa\u5176\u6240\u4ee5\u7136\u6765\uff0c\u4f46\u662f \u53ea\u89c9\u4e00\u80a1\u52b2\u98ce\u4ece\u67aa\u5c16\u53d1\u51fa\uff0c\u76f4\u5439\u5f97\u56db\u5468\u5927\u70db\u6643\u6643\u6b32\u706d\uff0c\u5fc3\u77e5\u8fd9\u5b9a\u662f\u4e00\u79cd\u6781\u5389\u5bb3\u7684\u67aa\u6cd5\u3002 \u3000\u3000\u90a3\u5c11\u5e74\u7a81\u7136\u957f\u8eab\u524d\u7a7f\uff0c\u75be\u5982\u95ea\u7535\uff0c\u865a\u7a7a\u8fde\u523a\u516d\u4e0b\uff0c\u4f17\u4eba\u611f\u5230\u5149\u7ebf\u4e00\u6697\uff0c\u539f\u6765\u5c11\u5e74\u9762\u524d\u4e08 \u4f59\u516d\u6839\u6a80\u9999\u70db\u5df2\u88ab\u4ed6\u67aa\u4e0a\u6240\u53d1\u52b2\u529b\u5439\u7184\uff0c\u4f17\u4eba\u6b63\u8981\u53eb\u597d\uff0c\u5c11\u5e74\u56de\u624b\u4e00\u523a\uff0c\u957f\u62a2\u8131\u624b\uff0c\u8eab\u5b50\u4e5f \u8ddf\u7740\u4e00\u8f6c\uff0c\u5411\u540e\u75be\u884c\uff0c\u53ea\u542c\u89c1\u547c\u7684\u4e00\u58f0\uff0c\u53c8\u7184\u4e86\u4e00\u6839\u5927\u70db\uff0c\u90a3\u5c11\u5e74\u624b\u6267\u957f\u67aa\uff0c\u6c14\u52bf\u96cd\u5bb9\u7684\u7ad9 \u5b9a\u3002\u539f\u6765\u90a3\u5c11\u5e74\u8eab\u5f62\u75be\u5982\u6d41\u661f\uff0c\u7adf\u7136\u8d76\u5728\u957f\u67aa\u4e4b\u524d\uff0c\u5f85\u5230\u8131\u624b\u957f\u67aa\u6253\u7184\u5927\u70db\uff0c\u5c31\u4f38\u624b\u63e1\u4f4f\u3002 \u3000\u3000\u96c1\u8361\u5927\u4fa0\u9ad8\u58f0\u53eb\u597d\uff0c\u8fde\u9053\uff1a\u201c\u829c\u6e56\u94f6\u67aa\u848b\u540d\u4e0d\u865a\u4f20\uff0c\u597d\u4e00\u62db\u2018\u4e91\u9f99\u516d\u73b0\u2019\u5440\uff0c\u848b\u5144\u6709\u5b50 \u5982\u6b64\uff0c\u8db3\u53ef\u81ea\u8c6a\u3002\u201d \u3000\u3000\u4eba\u7fa4\u4e2d\u8d70\u4e16\u4e00\u4e2a\u4e94\u65ec\u5de6\u53f3\u8001\u8005\uff0c\u8fde\u58f0\u8c26\u865a\u5bf9\u96c1\u8361\u5927\u4fa0\u9053\uff1a\u201c\u591a\u8c22\u79e6\u5144\u8c2c\u8d5e\uff0c\u72ac\u5b50\u81ea\u5e7c\u4e60 \u6b66\uff0c\u5728\u8fd9\u67aa\u4e0a\u5b9e\u6709\u5eff\u5e74\u5de5\u592b\uff0c\u79e6\u5144\u4f60\u770b\u8fd8\u8fc7\u5f97\u53bb\u4e48\uff1f\u201d \u3000\u3000\u96c1\u8361\u5927\u4fa0\uff1a\u201c\u597d\u5f97\u5f88\uff0c\u597d\u5f97\u5f88\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fc3\u4e2d\u4e5f\u5f88\u4f69\u670d\uff0c\u5fc3\u60f3\u8fd9\u5c11\u5e74\u5e74\u7eaa\u5341\u4e03\u516b\u5c81\u8eab\u624b\u5df2\u4e0d\u51e1\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u81ea\u5df1\u5185\u529b\u9020\u8be3\uff0c \u53d1\u51fa\u62db\u5f0f\u9690\u9690\u6709\u98ce\u96f7\u4e4b\u58f0\uff0c\u53ef\u662f\u80fd\u591f\u6251\u706d\u70db\u706b\uff0c\u529f\u529b\u4e5f\u81ea\u4e0d\u9519\u4e86\u3002 \u3000\u3000\u4f17\u5c11\u5e74\u76f8\u7ee7\u65bd\u5c55\u5e08\u95e8\u6216\u5bb6\u4f20\u7edd\u6280\uff0c\u8fd9\u4e9b\u4eba\u7684\u5e08\u957f\u90fd\u662f\u6c5f\u5357\u6b66\u6797\u6709\u6765\u5386\u4e4b\u4eba\uff0c\u4e00\u65f6\u4e4b\u95f4\uff0c \u5927\u5385\u4e2d\u5947\u6280\u8fed\u51fa\uff0c\u91c7\u58f0\u56db\u8d77\uff0c\u505a\u957f\u8f88\u7684\u89c1\u81ea\u5df1\u5f1f\u5b50\u90fd\u80fd\u4e0d\u8d1f\u6559\u8bf2\uff0c\u66ff\u672c\u95e8\u589e\u5149\u9732\u8138\uff0c\u5fc3\u4e2d\u90fd \u9ad8\u5174\u5f97\u5f88\uff0c\u5bf9\u4e8e\u90a3\u5f69\u54c1\u98ce\u96f7\u6c34\u706b\u5b9d\u73e0\uff0c\u5012\u4e0d\u592a\u5173\u5fc3\u4e86\u3002 \u3000\u3000\u8fd9\u65f6\u5385\u4e2d\u732e\u6280\u7684\u662f\u5e38\u5dde\u5343\u624b\u5927\u58eb\u4e4b\u5f92\uff0c\u8fd9\u5343\u624b\u5927\u58eb\u6697\u5668\u529f\u592b\u662f\u5929\u4e0b\u4e00\u7edd\uff0c\u5e74\u9752\u65f6\u9760\u7740\u6ee1 \u8eab\u5404\u5f0f\u5404\u6837\u7684\u6697\u5668\uff0c\u4f1a\u8fc7\u4e0d\u77e5\u591a\u5c11\u82f1\u96c4\uff0c\u4ece\u6765\u6ca1\u6709\u8f93\u8fc7\uff0c\u95ef\u4e0b\u5343\u624b\u5927\u58eb\u5916\u53f7\uff0c\u540e\u6765\u770b\u7834\u4e16 \u60c5\uff0c\u524a\u53d1\u4e3a\u50e7\uff0c\u522b\u4eba\u5c31\u6539\u79f0\u4ed6\u4e3a\u5343\u624b\u5927\u58eb\uff0c\u4ed6\u51fa\u5bb6\u4ee5\u540e\uff0c\u5bf9\u4e8e\u6614\u5e74\u6240\u7528\u6bd4\u8f83\u9634\u6bd2\u6697\u5668\u4e00\u6982\u629b \u5f03\uff0c\u53ef\u662f\u5bf9\u4e8e\u653e\u53d1\u6697\u5668\u529f\u592b\u5374\u662f\u6108\u6765\u6108\u7cbe\uff0c\u4e3a\u6050\u7edd\u6280\u5931\u4f20\uff0c\u5c31\u6536\u4e86\u4e00\u4e2a\u5c0f\u5f92\u513f\uff0c\u503e\u56ca\u76f8\u4f20\u3002 \u3000\u3000\u5343\u624b\u5927\u58eb\u5e7c\u5f92\uff0c\u5411\u56ca\u4e2d\u4e00\u6293\uff0c\u4e24\u624b\u63e1\u6ee1\u91d1\u9488\uff0c\u4f46\u89c1\u4ed6\u53cc\u624b\u8fde\u626c\uff0c\u4e00\u6839\u6839\u91d1\u9488\u7834\u7a7a\u800c\u53bb\uff0c \u76f8\u7ee7\u7a7f\u8d74\u70db\u706b\uff0c\u90a3\u91d1\u9488\u4f53\u79ef\u751a\u5c0f\u6a80\u9999\u70db\u706b\u529b\u6781\u5f3a\uff0c\u4e00\u70e7\u5373\u7ea2\uff0c\u53ea\u542c\u5230\u55e4\u4e4b\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u7247\u523b \u4e4b\u95f4\uff0c\u5728\u5899\u4e0a\u9489\u4e0b\u4e86\u516b\u4e2a\u5927\u5b57\uff1a\u201c\u79e6\u8001\u7237\u5b50\u534e\u8bde\u5927\u559c\u201d\u3002 \u3000\u3000\u90a3\u91d1\u9488\u70e7\u5f97\u901a\u7ea2\uff0c\u9489\u5728\u5899\u4e0a\u95ea\u8000\u7740\u6de1\u6de1\u7684\u5149\u7130\uff0c\u58ee\u4e3d\u5341\u5206\uff0c\u96c1\u8361\u5927\u4fa0\u629a\u9aef\u5927\u4e50\uff0c\u5475\u5475\u7b11 \u9053\uff1a\u201c\u771f\u96be\u4e3a\u5343\u624b\u5927\u58eb\u6559\u51fa\u5982\u6b64\u4e56\u5de7\u5f92\u513f\u3002\u201d \u3000\u3000\u4f17\u4eba\u90fd\u89c9\u8fd9\u82b1\u6837\u65b0\u5947\u6709\u8da3\uff0c\u771f\u4e8f\u90a3\u5c11\u5e74\u6316\u7a7a\u5fc3\u601d\u60f3\u51fa\uff0c\u90a3\u5c0f\u5c0f\u7684\u4e00\u6839\u91d1\u9488\uff0c\u5343\u624b\u5927\u58eb\u5f92 \u513f\u7adf\u80fd\u6839\u6839\u7a7f\u8fc7\u706b\u7130\uff0c\u6df1\u6df1\u9489\u5165\u6960\u6728\u58c1\u4e0a\uff0c\u8fd9\u624b\u6cd5\uff0c\u5185\u52b2\u9020\u8be3\u4e5f\u662f\u975e\u540c\u5c0f\u53ef\u4e86\u3002 \u3000\u3000\u4f17\u4eba\u89c1\u96c1\u8361\u5927\u4fa0\u751a\u662f\u6b23\u8d4f\u9002\u624d\u4e00\u5e55\uff0c\u90fd\u4e0d\u7531\u6697\u5fd6\uff0c\u8fd9\u5c0f\u5b50\u6050\u6015\u8981\u5f97\u5230\u91c7\u54c1\uff0c\u6b63\u5728\u6b64\u65f6\uff0c \u95ea\u51fa\u4e00\u4e2a\u5eff\u4e03\u3001\u516b\u7684\u9752\u5e74\uff0c\u6ee1\u8138\u900f\u7740\u673a\u4f36\u7cbe\u608d\u4e4b\u8272\u3002 \u3000\u3000\u90a3\u9752\u5e74\u5411\u5927\u5bb6\u884c\u4e86\u4e00\u672d\uff0c\u8d70\u4e0a\u524d\u53bb\uff0c\u4ece\u5175\u5668\u67b6\u4e0a\u53d6\u4e86\u4e00\u6587\u9752\u5251\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4e00\u9886\u5251\u8bc0\uff0c \u5c31\u5f00\u59cb\u8868\u6f14\u5251\u672f\u3002 \u3000\u3000\u9752\u5e74\u53f3\u624b\u4e00\u4f38\uff0c\u624b\u4e2d\u957f\u5251\u6325\u4e86\u534a\u4e2a\u5708\u5b50\u3002\u84c4\u52b2\u4e0d\u53d1\uff0c\u84e6\u5730\u8fde\u53d8\u6570\u62db\uff0c\u4e00\u541e\u4e00\u5410\uff0c\u5316\u4f5c\u4e07 \u822c\u5251\u5f71\uff0c\u4f17\u4eba\u90fd\u662f\u8d6b\u8d6b\u6709\u540d\u4e4b\u8f88\uff0c\u4f46\u89c9\u5251\u6c14\u68ee\u68ee\uff0c\u7adf\u7136\u770b\u4e0d\u51fa\u957f\u5251\u7a76\u7adf\u8981\u5bf9\u5411\u4f55\u65b9\uff0c\u4eff\u4f5b\u6bcf \u4e2a\u65b9\u5411\u90fd\u53ef\u5230\u8fbe\uff0c\u4e0d\u7531\u76f8\u53cd\u9a87\u7136\uff0c\u5404\u81ea\u5fd6\u9053\uff1a\u201c\u8fd9\u53ae\u5e74\u7eaa\u8f7b\u8f7b\uff0c\u5251\u5f0f\u5373\u662f\u8fd9\u822c\u602a\u5f02\uff0c\u4e0d\u5e97\u8bf4 \u4e0b\u9762\u8fd8\u6709\u6ed4\u6ed4\u4e0d\u7edd\u7684\u653b\u52bf\uff0c\u5c31\u662f\u8fd9\u8d77\u624b\u4e00\u62db\uff0c\u81ea\u5df1\u4e5f\u96be\u4ee5\u5e94\u4ed8\u3002\u201d \u3000\u3000\u90a3\u9752\u5e74\u4e00\u62db\u5df1\u6bd5\uff0c\u5373\u6536\u624b\u9053\uff1a\u201c\u665a\u8f88\u59d3\u4f59\u540d\u5fd7\u8fbe\uff0c\u751f\u5e73\u55dc\u6b66\u82e5\u72c2\uff0c\u8fd9\u5251\u5f0f\u662f\u665a\u8f88\u65e0\u610f\u4e2d \u4ece\u4e00\u9ad8\u4eba\u5904\u5b66\u5f97\uff0c\u665a\u8f88\u8d70\u904d\u5927\u6cb3\u5357\u5317\uff0c\u5411\u5404\u5730\u540d\u5bb6\u8bf7\u6559\uff0c\u6b64\u5f0f\u5012\u5e95\u662f\u5c5e\u4e8e\u4f55\u7b49\u62db\u672f\uff0c\u665a\u8f88\u597d \u5b66\u5168\u8fd9\u5957\u5251\u6cd5\uff0c\u5747\u4e0d\u5f97\u8981\u9886\u800c\u56de\uff0c\u8fd9\u624d\u5355\u8eab\u5230\u8fbe\u6c5f\u5357\uff0c\u542c\u8bf4\u6c5f\u5357\u7fa4\u4fa0\u90fd\u805a\u96c6\u5728\u6b64\u4e3a\u79e6\u8001\u524d\u8f88 \u795d\u5bff\uff0c\u665a\u8f88\u6597\u80c6\u6c42\u6559\uff0c\u5404\u4f4d\u90fd\u662f\u6b66\u5b66\u5927\u5bb6\uff0c\u5b9a\u80fd\u4e3a\u665a\u8f88\u91ca\u7591\u3002\u201d \u3000\u3000\u8bed\u8bcd\u8bda\u6073\uff0c\u4e0d\u542b\u4e1d\u6beb\u6076\u610f\u3002 \u3000\u3000\u6797\u7389\u8eab\u65c1\u7ae5\u5b50\u8138\u8272\u4e00\u53d8\uff0c\u5bf9\u6797\u7389\u9053\uff1a\u201c\u8fd9\u662f\u7239\u7239\u5251\u6cd5\uff0c\u6211\u5012\u8981\u95ee\u95ee\u4ed6\u662f\u4ece\u4f55\u5b66\u6765\u3002\u201d \u3000\u3000\u4ed6\u6b63\u60f3\u5f00\u53e3\u53d1\u95ee\uff0c\u84e6\u5730\u201c\u78b0\u201d\uff0c\u201c\u78b0\u201d\u4e24\u58f0\uff0c\u5385\u4e2d\u79bb\u5730\u4e24\u4e08\u5de6\u53f3\u7a97\u5b50\u88ab\u4eba\u51fb\u788e\uff0c\u98de\u8fdb\u4e00 \u4e2a\u8001\u8005\u3002 \u3000\u3000\u9ad8\u6218\u5b9a\u775b\u4e00\u770b\uff0c\u5fc3\u5934\u4e00\u9707\uff0c\u5fd6\u9053\uff1a\u201c\u5929\u715e\u661f\u541b\u76ee\u83ab\u8981\u662f\u627e\u6211\u6765\u7684\uff0c\u4ed6\u5f92\u513f\u88ab\u6211\u6253\u4e0b\u60ac \u5d16\uff0c\u751f\u6b7b\u4e0d\u660e\uff0c\u8fd9\u4e8b\u5982\u679c\u8001\u9b54\u5f97\u77e5\uff0c\u6211\u53ef\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u90a3\u8001\u8005\u5bd2\u7740\u8138\uff0c\u5927\u5587\u5587\u7684\u8d70\u5230\u5385\u4e2d\uff0c\u5bf9\u7740\u8d3e\u4fa0\u9053\uff1a\u201c\u4f60\u90a3\u98ce\u96f7\u6c34\u706b\u5b9d\u73e0\u5feb\u5feb\u62ff\u6765\u3002\u201d \u3000\u3000\u8d3e\u4fa0\u5b09\u76ae\u7b11\u8138\u9053\uff1a\u201c\u8fd9\u5b9d\u73e0\u5982\u679c\u662f\u533a\u533a\u7684\uff0c\u81ea\u7136\u5e94\u5f53\u9001\u7ed9\u524d\u8f88\uff0c\u53ea\u662f\u73b0\u5728\u5b9d\u73e0\u5df1\u4e0d\u662f\u533a \u533a\u4e4b\u7269\u4e86\u3002\u533a\u533a\u5df2\u9001\u7ed9\u5929\u4e0b\u82f1\u96c4\u4e86\u3002\u201d \u3000\u3000\u8001\u8005\u6012\u9053\uff1a\u201c\u6211\u5bfb\u8bbf\u534a\u6708\uff0c\u5f97\u77e5\u8fd9\u73e0\u843d\u5165\u4f60\u8fd9\u6240\u624b\u4e2d\uff0c\u8c01\u548c\u4f60\u565c\u55e6\uff0c\u4e56\u4e56\u7ed3\u6211\u8001\u4eba\u5bb6\u732e \u4e0a\u6765\u3002\u201d \u3000\u3000\u4f17\u5c11\u5e74\u5148\u524d\u89c1\u4ed6\u5927\u5587\u65e0\u793c\uff0c\u5fc3\u4e2d\u65e9\u5df2\u5fff\u7136\u4e0d\u670d\uff0c\u6b64\u65f6\u89c1\u4ed6\u6a2a\u86ee\u4e0d\u8bb2\u9053\u7406\uff0c\u4f17\u53e3\u4e0d\u7ea6\u800c\u540c \u559d\u9a82\u8d77\u6765\u3002 \u3000\u3000\u90a3\u8001\u8005\u4f3c\u751a\u4e0d\u8010\uff0c\u4ed6\u89c1\u5385\u4e0a\u5c11\u5e74\uff0c\u4e00\u4e2a\u4e2a\u90fd\u662f\u751f\u9f99\u6d3b\u864e\uff0c\u60f3\u5230\u81ea\u5df1\u5f92\u513f\u5944\u5944\u4e00\u606f\uff0c\u4e0d\u89c9 \u66f4\u611f\u70e6\u71e5\uff0c\u5389\u58f0\u5bf9\u8d3e\u4fa0\u9053\uff1a\u201c\u7ed9\u662f\u4e0d\u7ed9\uff1f\u201d \u3000\u3000\u8d3e\u4fa0\u89c1\u4ed6\u4ece\u7834\u7a97\u98de\u6765\uff0c\u8eab\u6cd5\u975e\u5e38\u602a\u5f02\uff0c\u5fc3\u4e2d\u5df2\u5b58\u6212\u5907\uff0c\u4ed6\u4e00\u751f\u73a9\u7b11\u5df2\u60ef\uff0c\u867d\u5219\u4e25\u9635\u4ee5 \u5f85\uff0c\u53e3\u4e2d\u5374\u662f\u4e0d\u4f4f\u8bf4\u7b11\u9053\uff1a\u201c\u8fd9\u5b9d\u73e0\u662f\u6211\u8001\u54e5\u54e5\u8d60\u9001\u7ed9\u5929\u4e0b\u82f1\u96c4\u4eec\u7684\u5f69\u54c1\uff0c\u8001\u82f1\u96c4\u529f\u592b\u867d\u7136 \u76d6\u4e16\uff0c\u53ea\u53ef\u60dc\uff0c\u53ea\u53ef\u60dc\u5e74\u7eaa\u5927\u4e86\u4e00\u70b9\u3002\u201d \u3000\u3000\u6b64\u8a00\u4e00\u51fa\uff0c\u4f17\u4eba\u8f70\u7136\u5927\u7b11\uff0c\u9ad8\u6218\u5fc3\u77e5\u8981\u7cdf\uff0c\u679c\u7136\u8001\u8005\u9634\u7b11\u4e00\u58f0\uff0c\u8eab\u5b50\u4e00\u52a8\uff0c\u53e3\u4e2d\u559d\u901a\uff1a \u201c\u5c0f\u5b50\u627e\u6b7b\u3002\u201d \u3000\u3000\u4e00\u638c\u5c31\u5411\u8d3e\u4fa0\u5f53\u80f8\u6309\u53bb\uff0c\u8d3e\u4fa0\u81ea\u4ed8\u5185\u529b\u4e0d\u5f31\uff0c\u53f3\u638c\u5411\u5916\u753b\u4e86\u534a\u5708\uff0c\u547c\u7136\u62cd\u51fa\u3002 \u3000\u3000\u90a3\u8001\u8005\u8fd9\u4e00\u638c\u867d\u7136\u770b\u6765\u5e73\u5e73\u65e0\u5947\uff0c\u5176\u5b9e\u5df1\u7ecf\u8574\u85cf\u7740\u201c\u5c0f\u5929\u661f\u201d\u5185\u5bb6\u771f\u529b\uff0c\u4e24\u638c\u4e00\u4ea4\uff0c\u8d3e \u4fa0\u95f7\u54fc\u4e00\u58f0\uff0c\u5411\u540e\u9000\u4e86\u56db\u4e94\u6b65\uff0c\u8dcc\u5012\u5750\u5730\uff0c\u4e00\u5f20\u53e3\u55b7\u51fa\u4e00\u53e3\u9c9c\u8840\u3002 \u3000\u3000\u8001\u8005\u4e0a\u524d\u4e00\u6b65\uff0c\u70b9\u4e86\u4ed6\u80f8\u524d\u7a74\u9053\u3002\u6b63\u60f3\u903c\u7740\u8d3e\u4fa0\u4ea4\u51fa\u5b9d\u73e0\uff0c\u5ffd\u7136\u80cc\u540e\u98ce\u58f0\u5927\u4f5c\uff0c\u4ed6\u5934\u4e5f \u4e0d\u56de\uff0c\u8eab\u5f62\u4e00\u6c89\u4e00\u63a0\uff0c\u5df2\u7136\u95ea\u8eab\u88ad\u51fb\u4ed6\u7684\u4eba\u8eab\u540e\uff0c\u4f38\u811a\u4e00\u8e22\uff0c\u628a\u654c\u4eba\u6b63\u9762\u8e22\u5012\u3002 \u3000\u3000\u7fa4\u4fa0\u89c1\u4ed6\u4e00\u62db\u201c\u8131\u888d\u6362\u4f4d\u201d\uff0c\u8eab\u5f62\u5feb\u5982\u9b3c\u8235\uff0c\u90a3\u8d3e\u4fa0\u96c1\u8361\u5927\u4fa0\u662f\u4f55\u7b49\u5a01\u540d\uff0c\u7adf\u88ab\u4ed6\u8f7b\u63cf \u6de1\u5199\u53cc\u53cc\u6253\u5012\uff0c\u6b64\u4eba\u529f\u529b\u771f\u53ef\u8c13\u6df1\u4e0d\u53ef\u6d4b\u4e86\u3002 \u3000\u3000\u8001\u8005\u51b7\u51b7\u9053\uff1a\u201c\u8c01\u4e0d\u670d\u6c14\uff0c\u5c31\u6eda\u4e0a\u6765\u3002\u201d \u3000\u3000\u7fa4\u4fa0\u867d\u77e5\u4e0d\u662f\u654c\u624b\uff0c\u4f46\u5374\u5fcd\u4e0d\u4e0b\u8fd9\u53e3\u9e1f\u6c14\uff0c\u53c8\u6709\u6570\u4eba\u51fa\u624b\uff0c\u4e0d\u6570\u62db\u90fd\u88ab\u8001\u8005\u70b9\u4e2d\u7a74\u9053\uff0c \u6709\u7684\u5446\u82e5\u6728\u9e21\uff0c\u6709\u7684\u75db\u5f97\u51b7\u6c57\u76f4\u6d41\u3002 \u3000\u3000\u9ad8\u6218\u81ea\u5fd6\u81ea\u5df1\u51fa\u624b\u4e0d\u81f4\u8fd9\u822c\u4e0d\u6d4e\uff0c\u6b63\u5728\u6c89\u541f\uff0c\u90a3\u7ae5\u5b50\u5929\u751f\u4fa0\u4e49\u5fc3\u80a0\uff0c\u770b\u5230\u8001\u8005\u70b9\u7a74\u624b\u6cd5 \u9634\u6bd2\u65e0\u6bd4\uff0c\u4e0d\u987e\u4e00\u5207\u5c31\u8981\u4e0b\u573a\u62fc\u547d\uff0c\u5ffd\u7136\u4e00\u53ea\u6e29\u8f6f\u6ed1\u817b\u7684\u5c11\u624b\u6293\u4f4f\u4ed6\u7684\u624b\uff0c\u4e00\u4e2a\u6e29\u67d4\u7684\u58f0\u97f3 \u9053\uff1a\u201c\u522b\u751f\u6c14\uff0c\u522b\u9c81\u83bd\uff0c\u8fd9\u8001\u5934\u5b50\u5389\u5bb3\u5f97\u7d27\u3002\u201d \u3000\u3000\u7ae5\u5b50\u4e00\u770b\u6797\u7389\uff0c\u53ea\u89c1\u5979\u6ee1\u8138\u5173\u6ce8\u4e4b\u8272\uff0c\u5fc3\u4e2d\u4e00\u8f6f\uff0c\u6084\u6084\u9053\uff1a\u201c\u4f60\u522b\u62c5\u5fc3\uff0c\u6211\u7528\u7239\u7239\u6559\u6211 \u7684\u5251\u6cd5\uff0c\u518d\u4e0d\u6d4e\u4e5f\u81ea\u4fdd\u65e0\u5bb3\u3002\u201d \u3000\u3000\u6797\u7389\u89c1\u4ed6\u4e00\u8138\u5927\u65e0\u754f\u7684\u795e\u8272\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u5979\u7adf\u4e0d\u80fd\u518d\u5f00\u53e3\u529d\u963b\uff0c\u4fbf\u9053\uff1a\u201c\u90a3\u4e48\u4f60\u548c\u9ad8\u5927 \u54e5\u4e24\u4e2a\u4eba\u8054\u624b\uff0c\u4e00\u8d77\u6253\u4ed6\u3002\u201d \u3000\u3000\u7ae5\u5b50\u70b9\u70b9\u5934\uff0c\u6b63\u8981\u8fdb\u9ad8\u6218\u4e00\u5757\u513f\u4e0b\u53bb\u548c\u8001\u8005\u6bd4\u5212\uff0c\u5ffd\u7136\u53f1\u559d\u4e4b\u58f0\u5927\u8d77\uff0c\u539f\u6765\u829c\u6e56\u94f6\u67aa\u848b \u541b\u5c71\u633a\u7740\u957f\u67aa\uff0c\u94f6\u5149\u95ea\u95ea\u88f9\u7740\u8001\u8005\u5927\u6218\u8d77\u6765\u3002 \u3000\u3000\u8001\u8005\u7a7a\u624b\u51dd\u63a5\u4e86\u51e0\u62db\uff0c\u89c9\u5f97\u848b\u5bb6\u67aa\u6cd5\u751a\u662f\u7cbe\u5999\uff0c\u5176\u4e2d\u5305\u7f57\u4e07\u8c61\uff0c\u878d\u4f1a\u4e86\u201c\u6768\u5bb6\u67aa\u201d\u201c\u5cb3 \u5bb6\u67aa\u201d\u7684\u5389\u5bb3\u6740\u624b\uff0c\u4ed6\u4e0d\u613f\u4e45\u4e8b\u7ea0\u7f20\uff0c\u547c\u547c\u8fde\u53d1\u4e09\u638c\uff0c\u903c\u9000\u848b\u541b\u5c71\uff0c\u8f6c\u8eab\u53d6\u4e86\u4e00\u53ea\u957f\u5251\uff0c\u8e0f \u4e2d\u5bab\uff0c\u8d70\u504f\u950b\uff0c\u8fde\u523a\u51e0\u5251\uff0c\u94f6\u67aa\u848b\u541b\u5c71\u53ea\u5f97\u5012\u9000\u4e0d\u5df2\u3002 \u3000\u3000\u8001\u8005\u51b7\u55e4\u4e00\u58f0\uff0c\u4e00\u5251\u5e73\u63a8\u800c\u4e0a\uff0c\u848b\u541b\u5c71\u5f00\u58f0\u5410\u6c14\u3002\u8fd0\u5c3d\u751f\u5e73\u529b\u6c14\uff0c\u67aa\u6746\u76f4\u51fb\u5251\u8eab\uff0c\u4ed6\u6697 \u5fd6\u81ea\u5df1\u8fd9\u957f\u67aa\u662f\u6709\u540d\u7684\u91cd\u5175\u5668\uff0c\u91cd\u8fbe\u56db\u5341\u4f59\u65a4\uff0c\u518d\u52a0\u4e0a\u8fd9\u6b21\u51fb\u4e0b\u7684\u529b\u9053\uff0c\u8001\u8005\u529f\u529b\u518d\u6df1\uff0c\u4e5f \u4e0d\u80fd\u4ee5\u8f7b\u8f7b\u7684\u4e00\u53ea\u5251\u53bb\u786c\u63a5\u3002 \u3000\u3000\u90a3\u77e5\u8001\u8005\u53f3\u624b\u7a81\u7136\u4e00\u7ffb\uff0c\u957f\u5251\u53cd\u800c\u538b\u5728\u94f6\u67aa\u4e4b\u4e0a\uff0c\u4ed6\u8fd9\u53d8\u62db\u5feb\u82e5\u95ea\u7535\uff0c\u4eba\u53ea\u89c1\u4ed6\u5251\u8eab\u9ecf \u7740\u67aa\u6746\uff0c\u8fde\u8f6c\u51e0\u4e2a\u5708\u5b50\uff0c\u94f6\u67aa\u848b\u541b\u5c71\u518d\u4e5f\u628a\u6301\u4e0d\u4f4f\u98de\u957f\u67aa\u8131\u51fa\u800c\u98de\u3002 \u3000\u3000\u8001\u8005\u54c8\u54c8\u957f\u7b11\uff0c\u3000\u3000\u7b11\u58f0\u65b9\u6bd5\uff0c\u4e00\u58f0\u559d\u201c\u6253\u201d\uff0c\u4f46\u89c1\u6f2b\u5929\u6697\u5668\uff0c\u7834\u7a7a\u800c\u6765\uff0c\u6562\u60c5\u662f\u5343\u624b \u5927\u58eb\u52a8\u4e0a\u624b\u4e86\u3002 \u3000\u3000\u8001\u8005\u4e3e\u8d77\u5251\u5728\u7a7a\u4e2d\u5212\u7740\u5708\u5b50\uff0c\u811a\u4e0b\u8e0f\u7740\u516b\u5366\u65b9\u4f4d\uff0c\u9aef\u987b\u7686\u5f20\uff0c\u90a3\u7ae5\u5b50\u5bf9\u9ad8\u6218\u9053\uff1a\u201c\u8fd9\u8001 \u5934\u8981\u7528\u5251\u6c14\u7834\u53bb\u6697\u5668\u3002\u201d \u3000\u3000\u90a3\u6697\u5668\u867d\u591a\uff0c\u53ef\u662f\u4e0d\u5230\u8001\u8005\u8eab\u65c1\u6570\u5c3a\uff0c\u4e0d\u662f\u88ab\u5251\u6321\u56de\uff0c\u5c31\u662f\u88ab\u5251\u4e0a\u6240\u53d1\u7f15\u7f15\u9510\u98ce\u5439\u6b6a\u3002 \u3000\u3000\u5343\u624b\u5927\u58eb\u4e00\u8fb9\u6536\u53bb\u6321\u56de\u6697\u5668\uff0c\u4e00\u8fb9\u4ecd\u7136\u4e0d\u505c\u7684\u7ee7\u7eed\u53d1\u51fa\u79cd\u79cd\u6697\u5668\uff0c\u94c1\u83b2\u5b50\uff0c\u91d1\u9488\uff0c\u8896 \u7bad\uff0c\u98de\u5200\uff0c\u6e90\u6e90\u4e0d\u7edd\u5411\u8001\u8005\u6253\u53bb\u3002 \u3000\u3000\u8001\u8005\u5fc3\u60f3\uff1a\u201c\u8fd9\u53ae\u4e24\u624b\u80fd\u591f\u653e\u51fa\u767e\u822c\u6697\u5668\uff0c\u7aef\u7684\u4e5f\u7b97\u662f\u4e2a\u5947\u4eba\uff0c\u4eca\u65e5\u7d22\u6027\u663e\u70b9\u672c\u4e8b\uff0c\u597d \u8ba9\u8fd9\u4e9b\u4e95\u4e2d\u4e4b\u86d9\uff0c\u5927\u5f00\u773c\u754c\u3002\u201d \u3000\u3000\u4ed6\u771f\u529b\u731b\u53d1\uff0c\u957f\u5251\u7adf\u7136\u53d1\u51fa\u4e00\u9635\u6e05\u8d8a\u7684\u547c\u5578\u58f0\uff0c\u90a3\u7ae5\u5b50\u5583\u5583\u81ea\u8bed\u9053\uff1a\u201c\u4e0d\u77e5\u7239\u7239\u80fd\u4e0d\u80fd \u5236\u670d\u4ed6\u3002\u7239\u7239\u5230\u5904\u884c\u4fa0\uff0c\u6211\u60f3\u4e00\u5b9a\u6709\u4e00\u5929\u4f1a\u649e\u7740\u4ed6\u7684\u3002\u201d \u3000\u3000\u6797\u7389\u67d4\u58f0\u9053\uff1a\u201c\u4e00\u5b9a\u80fd\u7684\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5947\u9053\uff1a\u201c\u4f60\u600e\u4e48\u9053\uff1f\u201d \u3000\u3000\u6797\u7389\u7b11\u9053\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u9053\u7406\uff0c\u4e0d\u8fc7\u6211\u603b\u662f\u89c9\u5f97\u4f60\u7239\u7239\u4e00\u5b9a\u662f\u4e2a\u5f88\u5389\u5bb3\u7684\u5927\u82f1 \u96c4\u3002\u201d \u3000\u3000\u7ae5\u5b50\u5f88\u662f\u9ad8\u5174\uff0c\u60c5\u4e0d\u81ea\u7981\u7684\u63e1\u7740\u6797\u7389\u53cc\u624b\u8fde\u6447\u9053\uff1a\u201c\u4f60\u771f\u597d\uff0c\u6211\u8d77\u521d\u4ee5\u4e3a\u4f60\u662f\u4e00\u4e2a\u4e00 \u4e2a\u2026\u2026\u201d \u3000\u3000\u6797\u7389\u63a5\u53e3\u9053\uff1a\u201c\u4e00\u4e2a\u53c8\u5c0f\u6c14\u53c8\u5201\u94bb\u7684\u6dd8\u6c14\u59d1\u5a18\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u8baa\u8baa\u4e00\u7b11\uff0c\u8033\u4e2d\u542c\u5230\u5251\u5578\u4e4b\u58f0\u6108\u6709\u6108\u54cd\uff0c\u6e10\u6e10\u76d6\u4f4f\u4e86\u6697\u5668\u7834\u7a7a\u4e4b\u58f0\uff0c\u4e3e\u76ee\u4e00\u77a7\uff0c \u4f46\u89c1\u5343\u624b\u5927\u58eb\u53d1\u51fa\u7684\u6697\u5668\u4e00\u5230\u5251\u5f71\u6240\u5305\u7684\u5730\u65b9\uff0c\u5c31\u5982\u77f3\u6c89\u5927\u6d77\uff0c\u4e0d\u89c1\u8e2a\u8ff9\uff0c\u8001\u8005\u6325\u52a8\u5458\u957f \u5251\uff0c\u8138\u4e0a\u7b11\u610f\u65f6\u9732\uff0c\u4f3c\u4e4e\u5bf9\u4e8e\u81ea\u5df1\u7684\u529f\u529b\uff0c\u751a\u611f\u6ee1\u610f\u3002 \u3000\u3000\u7ae5\u5b50\u60f3\uff1a\u201c\u8fd9\u662f\u4e07\u6d41\u5f52\u5b97\u7684\u4e0a\u4e58\u5185\u529f\uff0c\u6b64\u4eba\u5012\u5e95\u662f\u8c01\uff0c\u600e\u4e48\u4e0d\u66fe\u542c\u6885\u516c\u516c\u548c\u7239\u7239\u8bf4 \u8fc7\uff1f\u201d \u3000\u3000\u5343\u624b\u5927\u58eb\u4e00\u6478\u8eab\u4e0a\u7a7a\u7a7a\uff0c\u4e94\u516d\u888b\u6697\u5668\u90fd\u5df2\u53d1\u5b8c\uff0c\u7adf\u7136\u672a\u80fd\u5948\u4f55\u654c\u4eba\u4e00\u6839\u6beb\u6bdb\uff0c\u5fc3\u4e2d\u4e0d\u7981 \u611f\u5230\u4e00\u788e\u832b\u7136\uff0c\u4ed6\u51fa\u5bb6\u5df2\u4e45\uff0c\u55d4\u5ff5\u65e9\u9664\uff0c\u62f1\u624b\u5bf9\u8001\u8005\u9053\uff1a\u201c\u9601\u4e0b\u7aef\u7684\u662f\u8001\u8872\u751f\u5e73\u6240\u4f1a\u7b2c\u4e00\u9ad8 \u4eba\uff0c\u8001\u8872\u8d25\u5f97\u5fc3\u670d\u53e3\u670d\u3002\u201d \u3000\u3000\u8001\u8005\u4e00\u5410\u6c14\uff0c\u53ea\u542c\u5230\u6ed1\u5587\u4e00\u58f0\uff0c\u5927\u6279\u6697\u5668\u5760\u5730\uff0c\u539f\u6765\u9002\u624d\u90a3\u4e9b\u6697\u5668\u7adf\u88ab\u4ed6\u900f\u8fc7\u771f\u529b\u7684\u5b9d \u5251\u5438\u4f4f\u3002 \u3000\u3000\u8001\u8005\u9ad8\u58f0\u9053\uff1a\u201c\u4f60\u4e5f\u662f\u6211\u5929\u715e\u661f\u541b\u751f\u5e73\u6240\u4f1a\u7b2c\u4e00\u5947\u4eba\uff0c\u6ee1\u8eab\u7834\u94dc\u70c2\u94c1\u3002\u201d \u3000\u3000\u8001\u8005\u81ea\u62a5\u79f0\u53f7\uff0c\u4ed6\u4e00\u751f\u53ea\u5728\u5317\u65b9\u884c\u8d70\uff0c\u662f\u4ee5\u540d\u5934\u867d\u5927\uff0c\u6c5f\u5357\u7fa4\u4fa0\u5e76\u6ca1\u542c\u8fc7\u3002 \u3000\u3000\u9ad8\u6218\u4f4e\u5934\u4e00\u770b\uff0c\u90a3\u4e9b\u6697\u5668\u79cd\u7c7b\u867d\u591a\uff0c\u4f46\u6bcf\u4e00\u79cd\u90fd\u662f\u5c16\u950b\u6298\u53bb\uff0c\u5fc3\u60f3\u8fd9\u8001\u548c\u5c1a\u5927\u6982\u4e0d\u613f\u5984 \u5f00\u6740\u6212\uff0c\u4f46\u5bf9\u81ea\u5df1\u7684\u7edd\u6280\u53c8\u4e0d\u5fcd\u5f03\u800c\u4e0d\u7cbe\u7814\uff0c\u662f\u4ee5\u60f3\u51fa\u8fd9\u4e2a\u6cd5\u513f\uff0c\u8fd9\u6697\u5668\u5c31\u7b97\u6253\u4e2d\u4e86\uff0c\u4e5f\u4e0d \u80fd\u6253\u6b7b\u4eba\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u73af\u987e\u7fa4\u4fa0\u4e00\u773c\uff0c\u7eb5\u58f0\u53eb\u9053\uff1a\u201c\u6c5f\u5357\u6b66\u6797\u5982\u6b64\u8113\u80de\uff0c\u79e6\u8001\u5934\u8fd9\u70b9\u672c\u4e8b\uff0c\u4e5f\u914d\u79f0\u4ec0 \u4e48\u96c1\u8361\u5927\u4fa0\uff0c\u5929\u4e0b\u5927\u4fa0\u7684\u8138\u88ab\u4f60\u53ef\u4e22\u5149\u4e86\u3002\u201d \u3000\u3000\u5343\u624b\u5927\u58eb\u5fc3\u4e2d\u4e0d\u670d\uff0c\u5fd6\u9053\uff1a\u201c\u5982\u679c\u6211\u7528\u65e9\u5e74\u672a\u51fa\u5bb6\u524d\u7684\u706b\u5668\u6253\u4f60\uff0c\u4f60\u5c31\u662f\u6b66\u529f\u518d\u9ad8\uff0c\u4e5f \u88ab\u70e7\u5f97\u50cf\u7ea2\u888d\u5c06\u519b\u4e00\u6837\u6ee1\u5730\u4e71\u6eda\u3002\u201d \u3000\u3000\u7fa4\u4fa0\u89c1\u4ed6\u6b66\u529f\u592a\u9ad8\uff0c\u5982\u679c\u5984\u81ea\u4e0a\u524d\uff0c\u53ea\u662f\u81ea\u53d6\u5176\u8fb1\u800c\u5df2\uff0c\u4fbf\u90fd\u9ed8\u9ed8\u4e0d\u8bed\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u4e0a\u524d\u5230\u8d3e\u4fa0\u8ddf\u524d\u9634\u9634\u9053\uff1a\u201c\u5b9d\u73e0\u5462\uff1f\u4f60\u8fd9\u53ae\u518d\u4e0d\u5e72\u8106\uff0c\u6211\u8001\u4eba\u5bb6\u53ef\u8981\u53eb\u4f60\u5403\u70b9 \u82e6\u5934\uff0c\u5f85\u4f1a\u751f\u6b7b\u4e0d\u5f97\u4e86\u3002\u201d \u3000\u3000\u8d3e\u4fa0\u5636\u58f0\u9053\uff1a\u201c\u597d\u8d3c\u5b50\uff0c\u8981\u6740\u8981\u522e\uff0c\u542c\u7531\u5c0a\u4fbf\u3002\u201d \u3000\u3000\u7ae5\u624b\u6fc0\u8d77\u4e49\u6124\uff0c\u5bf9\u9ad8\u6218\u9053\uff1a\u201c\u9ad8\u5927\u54e5\uff0c\u54b1\u4eec\u4fe9\u4e00\u8d77\u6597\u6597\u8fd9\u8001\u5934\u3002\u201d \u3000\u3000\u9ad8\u6218\u4e5f\u662f\u5fff\u5fff\u4e0d\u5e73\uff0c\u70b9\u70b9\u5934\uff0c\u6b63\u5f85\u548c\u7ae5\u5b50\u4e00\u8d77\u8dc3\u51fa\uff0c\u5ffd\u7136\u80cc\u540e\u88ab\u4eba\u4e00\u62cd\uff0c\u9ad8\u6218\u4e00\u56de\u5934\uff0c \u767b\u65f6\u5927\u559c\u8fc7\u671b\uff0c\u5fd9\u9053\uff1a\u201c\u5e08\u7236\uff0c\u60a8\u5feb\u51fa\u624b\u5236\u8fd9\u8001\u9b54\u5427\u3002\u201d \u3000\u3000\u539f\u6765\u8eab\u540e\u6b63\u662f\u540d\u6ee1\u5173\u5916\u4e00\u4ee3\u5927\u4fa0\u98ce\u67cf\u6768\uff0c\u4ed6\u6084\u6084\u8d70\u8fdb\u5385\u5185\uff0c\u4f17\u4eba\u90fd\u5728\u77aa\u7740\u5929\u715e\u661f\u541b\u770b\u4ed6 \u5982\u4f55\u5904\u5236\u8d3e\u4fa0\uff0c\u5929\u715e\u661f\u541b\u4e00\u5fc3\u5728\u903c\u8d3e\u4fa0\u4ea4\u51fa\u5b9d\u73e0\uff0c\u662f\u4ee5\uff0c\u5927\u5bb6\u90fd\u6ca1\u53d1\u89c9\u3002 \u3000\u3000\u98ce\u67cf\u6768\u9053\uff1a\u201c\u6218\u513f\uff0c\u4e0d\u7528\u6025\uff0c\u9a6c\u4e0a\u5c31\u6709\u4eba\u6765\u5236\u4ed6\u3002\u201d \u3000\u3000\u7ae5\u5b50\u89c1\u9ad8\u6218\u558a\u4ed6\u5e08\u7236\uff0c\u53c8\u89c1\u4ed6\u5e84\u4e25\u5a01\u731b\u4e2d\u900f\u7740\u4eb2\u5207\uff0c\u5fc3\u77e5\u5b9a\u662f\u9ad8\u4eba\uff0c\u4f46\u4ed6\u4e0d\u4fbf\u9732\u51fa\u8eab \u4efd\uff0c\u4fbf\u77a7\u4e86\u98ce\u67cf\u6768\u51e0\u773c\uff0c\u5145\u6ee1\u4e86\u656c\u4ef0\u4e4b\u60c5\uff0c\u98ce\u67cf\u6768\u5411\u4ed6\u70b9\u5934\u7b11\u7b11\u3002 \u3000\u3000\u9ad8\u6218\u6025\u95ee\u9053\uff1a\u201c\u5e08\u7236\uff0c\u4f60\u8bf4\u7684\u662f\u8c01\u554a\uff1f\u600e\u4e48\u8fd8\u4e0d\u51fa\u6765\uff1f\u201d \u3000\u3000\u8fb9\u5be8\u5927\u4fa0\u98ce\u67cf\u6768\u4e00\u6307\u95e8\u53e3\u9053\uff1a\u201c\u6218\u513f\uff0c\u4f60\u770b\u90a3\u4e0d\u662f\u6765\u4e86\u3002\u201d \u3000\u3000\u9ad8\u6218\uff0c\u6797\u7389\uff0c\u548c\u90a3\u7ae5\u5b50\u4e00\u9f50\u8f6c\u5934\u770b\u53bb\uff0c\u4f46\u4e0d\u89c1\u4eba\u5f71\uff0c\u6b63\u5947\u602a\u95f4\uff0c\u53ea\u542c\u7830\u7136\u4e00\u58f0\uff0c\u5927\u95e8\u6253 \u5f00\uff0c\u4e00\u4e2a\u4eba\u8d70\u4e86\u8fdb\u6765\uff01 \u3000\u3000\u6b64\u4eba\u4e00\u4eba\uff0c\u4f17\u4eba\u5b9b\u5982\u773c\u524d\u4e00\u82b1\uff0c\u5df1\u81ea\u5230\u4e86\u5385\u4e2d\uff0c\u8eab\u5f62\u4e4b\u5feb\uff0c\u4ee4\u4eba\u548b\u820c\u3002 \u3000\u3000\u9ad8\u6218\u7ec6\u770b\u90a3\u4eba\uff0c\u53ea\u89c1\u4ed6\u5e74\u7ea6\u4e09\u65ec\uff0c\u9762\u5982\u7f8e\u7389\uff0c\u82f1\u6c14\u6bd5\u7136\uff0c\u8170\u95f4\u5251\u7a57\u98d8\u98d8\uff0c\u51db\u7136\u751f\u6210\u3002 \u3000\u3000\u6797\u7389\u5374\u89c1\u90a3\u91cd\u5b50\u9762\u9732\u559c\u8272\uff0c\u4f3c\u4e4e\u5f97\u610f\u5df2\u6781\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u8bc6\u5f97\u4ed6\u4e48\uff1f\u201d \u3000\u3000\u7ae5\u5b50\u70b9\u70b9\u5934\uff0c\u5ffd\u9053\uff1a\u201c\u6211\u5230\u90a3\u8fb9\u53bb\u77a7\u77a7\u3002\u201d \u3000\u3000\u4e5f\u4e0d\u5f85\u6797\u7389\u56de\u7b54\uff0c\u5c31\u5306\u5306\u94bb\u5165\u4eba\u7fa4\u62e5\u6324\u4e4b\u5904\uff0c\u6797\u7389\u5fc3\u4e2d\u4e0d\u7981\u5927\u5947\u3002 \u3000\u3000\u8fd9\u65f6\u4f17\u4eba\u4e5f\u770b\u89c1\u6b64\u4eba\uff0c\u970e\u65f6\u8f70\u96f7\u822c\u66b4\u51fa\u6b22\u547c\uff0c\u5927\u4f19\u513f\u6b22\u53eb\u9053\uff1a\u201c\u8f9b\u5927\u4fa0\u2026\u2026\u5feb\u2026\u2026\u5feb\u6551 \u51cc\u2026\u2026\u201d \u3000\u3000\u90a3\u4eba\u8eab\u5f62\u4e00\u8f6c\u4e4b\u95f4\uff0c\u5982\u9b3c\u9b45\u4e00\u822c\u5df2\u5230\u4e86\u8d3e\u4fa0\u4e4b\u65c1\uff0c\u5929\u715e\u661f\u541b\u6b63\u8981\u75db\u65bd\u6740\u624b\uff0c\u5ffd\u7136\u4e00\u53ea\u624b \u4e0d\u7f13\u4e0d\u75be\u5730\u4f38\u4e86\u8fc7\u6765\uff0c\u8f7b\u8f7b\u4e00\u628a\u5c06\u8d3e\u4fa0\u626f\u4e86\u5f00\u53bb\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u53ea\u89c9\u90a3\u624b\u4e00\u4ef6\u4e00\u7f29\u4e4b\u95f4\uff0c\u867d\u4e0d\u7b97\u5feb\uff0c\u4f46\u662f\u98d8\u5ffd\u4e4b\u81f3\uff0c\u81ea\u5df1\u7adf\u662f\u65e0\u5904\u4e0b\u624b\u64d2\u62ff\uff0c \u4e0d\u7981\u5927\u60ca\uff0c\u6b63\u5f85\u53d1\u8bdd\uff0c\u90a3\u4eba\u5df2\u9053\uff1a\u201c\u8fd9\u4f4d\u524d\u8f88\u548c\u51cc\u5927\u4fa0\u4e0d\u77e5\u6709\u4f55\u8fc7\u8282\uff0c\u51b2\u7740\u5728\u4e0b\u8584\u9762\u5927\u5bb6\u8bf4 \u5f00\u53ef\u597d\uff1f\u201d \u3000\u3000\u5929\u715e\u661f\u541b\u4e24\u773c\u4e00\u7ffb\uff0c\u602a\u58f0\u9053\uff1a\u201c\u5c0f\u5b50\u4f60\u59d3\u4ec0\u540d\u8c01\uff1f\u201d \u3000\u3000\u90a3\u5112\u751f\u6b63\u8272\u9053\uff1a\u201c\u5728\u4e0b\u8f9b\u6377\uff01\u201d \u3000\u3000\u4f17\u4eba\u4e0d\u7531\u81ea\u4e3b\u53c8\u662f\u8f70\u96f7\u822c\u6b22\u58f0\u5927\u559d\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u6210\u540d\u4e09\u5341\u5e74\u524d\uff0c\u90a3\u91cc\u542c\u8fc7\u8f9b\u6377\u7684\u4e07\u513f\uff0c\u8138\u8272\u4e00\u6c89\uff0c\u51b7\u51b7\u9053\uff1a\u201c\u4e73\u81ed\u672b\u5e72\u7684\u5a03\u513f \u4e5f\u6562\u79f0\u2019\u5927\u4fa0\u2018\u4e24\u5b57\uff0c\u4f60\u4e5f\u4e0d\u6015\u88ab\u6298\u6740\u4e48\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4e43\u662f\u5f53\u4eca\u5929\u4e0b\u7b2c\u4e00\u5927\u4fa0\uff0c\u5341\u591a\u5e74\u6765\u82e6\u4fee\uff0c\u529f\u529b\u6108\u6765\u6108\u7cbe\uff0c\u4eba\u5374\u6108\u6765\u6108\u8c26\u548c\uff0c\u5927\u975e\u6614 \u65e5\u504f\u6fc0\u5fff\u4e16\u4e4b\u6001\uff0c\u95fb\u8a00\u5e76\u4e0d\u53d1\u6012\uff0c\u5fae\u7b11\u9053\uff1a\u201c\u524d\u8f88\u6559\u8bad\u5f97\u662f\uff0c\u50cf\u665a\u8f88\u8fd9\u70b9\u5fb7\u884c\u90a3\u914d\u79f0\u5927\u4fa0\u4e24 \u5b57\u3002\u4e0d\u8fc7\u2026\u2026\u201d \u3000\u3000\u5929\u715e\u661f\u541b\u559d\u9053\uff1a\u201c\u4e0d\u8fc7\u600e\u6837\uff1f\u201d \u3000\u3000\u8f9b\u6377\u53cc\u76ee\u7cbe\u5149\u66b4\u5c04\uff0c\u6717\u6717\u9053\uff1a\u201c\u4e0d\u8fc7\u82e5\u662f\u524d\u8f88\u6267\u610f\u6ee5\u4f24\u65e0\u8f9c\uff0c\u4fbf\u662f\u665a\u8f88\u4e00\u62db\u4e00\u5f0f\u4e0d\u4f1a\u4e5f \u8981\u5411\u524d\u8f88\u8bf7\u6559\u51e0\u62db\u3002\u201d \u3000\u3000\u5929\u715e\u661f\u541b\u72c2\u7b11\u9053\uff1a\u201c\u597d\uff0c\u597d\uff0c\u4e0d\u6127\u2018\u5927\u4fa0\u2019\u4e24\u5b57\uff0c\u6211\u5012\u8981\u77a7\u77a7\u4f60\u7a76\u7adf\u4f1a\u51e0\u62db\u51e0\u5f0f\uff1f\u201d \u3000\u3000\u4ed6\u5fc3\u4e2d\u5374\u6697\u6697\u5fc3\u60ca\uff0c\u5fd6\u9053\uff1a\u201c\u8fd9\u5c0f\u5b50\u53cc\u76ee\u7cbe\u5149\u9661\u7136\u66b4\u5c04\uff0c\u53ef\u89c1\u539f\u5148\u662f\u628a\u7cbe\u5149\u95ea\u655b\uff0c\u96be\u9053 \u4ed6\u5185\u529f\u5df2\u5230\u4e86\u8fd9\u5c42\u5730\u6b65\uff1f\u201d\u8f9b\u6377\u9053\uff1a\u201c\u665a\u8f88\u8fd9\u70b9\u529f\u592b\u81ea\u7136\u4e0d\u662f\u524d\u8f88\u5bf9\u624b\u3002\u201d \u3000\u3000\u5929\u715e\u661f\u541b\u5012\u6ca1\u6709\u6599\u5230\u4ed6\u4f1a\u5982\u6b64\u597d\u8bf4\u8bdd\uff0c\u6014\u5f97\u4e00\u6014\uff0c\u968f\u5373\u559d\u9053\uff1a\u201c\u8ba9\u5f00\uff01\u201d \u3000\u3000\u8f9b\u6377\u5251\u7709\u4e00\u6380\uff0c\u4f83\u4f83\u9053\uff1a\u201c\u665a\u8f88\u518d\u6b21\u6073\u8bf7\u524d\u8f88\u624b\u4e0b\u7559\u60c5\u3002\u201d \u3000\u3000\u5929\u715e\u661f\u541b\u602a\u58f0\u9053\uff1a\u201c\u5947\u4e86\uff0c\u5947\u4e86\uff0c\u6211\u8001\u4eba\u5bb6\u51e0\u5341\u5e74\u4e0d\u51fa\u6c5f\u6e56\uff0c\u73b0\u5728\u6ca6\u843d\u5230\u8981\u53d7\u5c0f\u5b50\u4eec\u7684 \u7ba1\u675f\u4e86\uff0c\u563f\uff0c\u8981\u9976\u4ed6\u4e0d\u96be\uff0c\u4f60\u4e14\u6765\u8bd5\u51e0\u62db\u770b\u770b\u3002\u201d\uff0c \u3000\u3000\u8f9b\u6377\u6717\u58f0\u9053\uff1a\u201c\u524d\u8f88\u6b66\u6797\u5148\u8fdb\uff0c\u665a\u8f88\u8fd9\u4e00\u70b9\u5fae\u672b\u529f\u592b\uff0c\u54ea\u6562\u732e\u4e11\uff0c\u4e0d\u8fc7\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u54c8\u54c8\u957f\u7b11\uff0c\u5927\u58f0\u9053\uff1a\u201c\u5c0f\u53ef\u8f9b\u6377\u80fd\u5411\u524d\u8f88\u8bf7\u76ca\uff0c\u771f\u662f\u4f55\u5e78\u5982\u4e4b\u3002\u201d \u3000\u3000\u5929\u715e\u661f\u541b\u6c89\u58f0\u9053\uff1a\u201c\u5c0f\u5b50\u4f60\u6b7b\u800c\u65e0\u6028\uff1f\u201d \u3000\u3000\u8f9b\u6377\u731b\u7136\u5438\u6c14\u9053\uff1a\u201c\u8fd9\u4e2a\u81ea\u7136\uff01\u201d \u3000\u3000\u4f17\u4eba\u767b\u65f6\u53d1\u51fa\u4e00\u58f0\u4f3c\u60ca\u4f3c\u559c\u7684\u547c\u58f0\uff0c\u8f70\u5730\u9000\u4e86\u5f00\u53bb\uff0c\u9732\u51fa\u4e2d\u95f4\u7a7a\u573a\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u54c8\u54c8\u957f\u7b11\uff0c\u90a3\u7b11\u58f0\u76f4\u5982\u6709\u5f62\u4e4b\u7269\uff0c\u9707\u5f97\u5c4b\u5b87\u6643\u52a8\uff0c\u4f17\u4eba\u8033\u819c\u6b32\u88c2\uff0c\u7aef\u7684\u9738\u9053\u5df2 \u6781\u3002 \u3000\u3000\u5728\u90a3\u5982\u96f7\u7b11\u58f0\u4e2d\uff0c\u5ffd\u7136\u201c\u53ee\u201d\u4e00\u58f0\u8f7b\u8d8a\u4e4b\u58f0\u900f\u4e86\u8fc7\u6765\uff0c\u53ea\u89c1\u8f9b\u6377\u53f3\u624b\u4e00\u6325\uff0c\u5bd2\u5149\u970d\u970d\uff0c \u6885\u9999\u5b9d\u5251\u5df1\u5230\u4e86\u624b\u4e2d\u3002 \u3000\u3000\u5341\u5e74\u6765\uff0c\u8f9b\u6377\u4e00\u8eab\u529f\u592b\u7aef\u7684\u662f\u6108\u6765\u6108\u7eaf\uff0c\u624b\u4e2d\u957f\u5251\u66f4\u662f\u5a01\u9707\u5929\u4e0b\uff0c\u201c\u4e03\u5999\u795e\u541b\u201d\u6885\u5c71\u6c11 \u5f53\u5e74\u5168\u76db\u65f6\u671f\uff0c\u53ea\u6015\u4ea6\u65e0\u6b64\u65f6\u529f\u529b\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u7b11\u58f0\u9661\u6b62\uff0c\u9762\u9732\u6740\u6c14\uff0c\u53cc\u76ee\u77aa\u7740\u8f9b\u6377\u624b\u4e2d\u5251\u5149\u9053\uff1a\u201c\u597d\u5251\uff01\u201d \u3000\u3000\u8f9b\u6377\u6717\u58f0\u9053\uff1a\u201c\u524d\u8f88\u8bf7\u4eae\u5175\u5203\u3002\u201d \u3000\u3000\u5929\u715e\u661f\u541b\u5927\u7b11\u9053\uff1a\u201c\u8001\u5934\u5b50\u867d\u7136\u4e00\u628a\u9aa8\u5934\u4e86\uff0c\u5927\u6982\u8fd8\u4e0d\u81f4\u4e8e\u6ca1\u51fa\u606f\u8981\u548c\u5c0f\u4f19\u5b50\u52a8\u5200\u52a8\u67aa \u7684\u5730\u6b65\uff0c\u5c0f\u5b50\u4f60\u4e0a\u5475\u3002\u201d \u3000\u3000\u8f9b\u6377\u8138\u8272\u5fae\u53d8\uff0c\u4ed6\u81ea\u6614\u65e5\u957f\u5b89\u4e00\u6218\u5927\u7834\u5929\u7afa\u5a46\u7f57\u516d\u5947\u4e4b\u540e\uff0c\u4fa0\u540d\u904d\u4f20\u5929\u4e0b\uff0c\u51e0\u66fe\u78b0\u8fc7\u8fd9 \u7b49\u85d0\u89c6\u4ed6\u7684\u4eba\uff0c\u4ed6\u867d\u591a\u5e74\u82e6\u4fee\uff0c\u6027\u5b50\u5927\u5f02\u6614\u65e5\uff0c\u4f46\u662f\u5929\u751f\u8c6a\u6c14\u5374\u662f\u4e0d\u51cf\u5f53\u5e74\uff0c\u4ed6\u6717\u6717\u5927\u7b11 \u9053\uff1a\u201c\u524d\u8f88\u65e2\u5982\u6b64\u8bf4\uff0c\u6055\u665a\u8f88\u653e\u8086\u3002\u201d \u3000\u3000\u53ea\u89c1\u4ed6\u624b\u4e0a\u4e00\u6296\uff0c\u90a3\u6885\u9999\u5b9d\u5251\u53ee\u7136\u4e00\u8df3\uff0c\u5251\u5c16\u513f\u5de5\u6574\u5730\u6296\u6210\u4e00\u6735\u6885\u82b1\uff0c\u4f17\u8c6a\u77e5\u9053\u8fd9\u4e43\u662f \u8f9b\u5927\u4fa0\u8981\u7528\u201c\u4e03\u5999\u795e\u541b\u201d\u624b\u521b\u7684\u201c\u866c\u679d\u5251\u6cd5\u201d\u6765\u5bf9\u654c\u7684\u8baf\u53f7\uff0c\u4e0d\u7531\u9f50\u58f0\u5475\u4e86\u4e00\u58f0\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u9762\u4e0a\u867d\u72c2\uff0c\u6697\u4e2d\u5df2\u53d1\u73b0\u5bf9\u624b\u4e0d\u597d\u60f9\uff0c\u53cc\u638c\u5185\u52b2\u6697\u84c4\uff0c\u5916\u5f1b\u5185\u5f20\u3002 \u3000\u3000\u53ea\u542c\u5f97\u201c\u5636\u201d\u4e00\u58f0\u5c16\u9510\u523a\u8033\u7684\u58f0\u97f3\u5212\u7834\u957f\u7a7a\uff0c\u8f9b\u6377\u5251\u5c16\u4e0a\u631f\u7740\u51cc\u5389\u7684\u5251\u6c14\uff0c\u7b14\u76f4\u523a\u5411\u5929 \u715e\u661f\u541b\u7684\u201c\u795e\u5802\u201d\u5927\u7a74\uff0c\u51fa\u624b\u4e4b\u5feb\uff0c\u8ba4\u7a74\u4e4b\u51c6\uff0c\u7684\u786e\u662f\u540d\u5bb6\u98ce\u8303\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u957f\u7b11\u58f0\u8d77\uff0c\u8eab\u5f62\u4e00\u6643\u800c\u8fdb\uff0c\u5de6\u6307\u4e0a\u53d6\u654c\u76ee\uff0c\u6b65\u6cd5\u4e00\u9519\uff0c\u95ea\u8fc7\u8f9b\u6377\u957f\u5251\uff0c\u53f3\u817f\u8d77 \u5904\uff0c\u76f4\u8e22\u8f9b\u6377\u819d\u76d6\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u4e00\u51fa\u624b\u4e4b\u4e0b\uff0c\u5168\u662f\u5389\u5bb3\u65e0\u6bd4\u7684\u6740\u7740\uff0c\u800c\u4e14\u52b2\u9053\u4e4b\u5927\uff0c\u4ee4\u4e24\u4e08\u5916\u7684\u4f17\u4eba\u90fd\u51e0\u4e4e\u611f \u5230\u652f\u6301\u4e0d\u4f4f\u3002 \u3000\u3000\u4f46\u89c1\u8f9b\u6377\u5251\u7709\u4e00\u6311\uff0c\u624b\u4e2d\u957f\u5251\u5316\u6210\u4e00\u7247\u5251\u5f71\u7f69\u5411\u5929\u715e\u661f\u541b\u53f3\u817f\uff0c\u5929\u715e\u661f\u541b\u6b63\u5f85\u6536\u817f\uff0c\u731b \u95fb\u5251\u98ce\u5927\u9510\uff0c\u8f9b\u6377\u624b\u4e2d\u7684\u6885\u9999\u5251\u5df1\u75be\u903e\u95ea\u7535\u5730\u504f\u523a\u800c\u5165\uff0c\u6b63\u662f\u201c\u866c\u679d\u5251\u6cd5\u201d\u4e2d\u7684\u795e\u5999\u7edd\u62db \u201c\u51b7\u6885\u62c2\u9762\u201d\u3002 \u3000\u3000\u8fd9\u4e00\u62db\u4e13\u8d70\u504f\u950b\u7684\u602a\u62db\uff0c\u6885\u5c71\u6c11\u66fe\u5f15\u4e3a\u5e73\u751f\u6770\u4f5c\uff0c\u5f53\u5e74\u5927\u6222\u5c9b\u4e3b\u5e73\u51e1\u4e0a\u4eba\u90fd\u66fe\u8d5e\u53e3\u4e0d \u7edd\uff0c\u8fd9\u65f6\u8f9b\u633a\u529f\u529b\u72b9\u80dc\u5f53\u5e74\uff0c\u4f46\u89c1\u5251\u950b\u5185\u52b2\u6f6e\u6d8c\uff0c\u5439\u5f97\u5929\u715e\u661f\u541b\u987b\u7709\u4ff1\u5f20\uff01 \u3000\u3000\u5929\u715e\u661f\u541b\u4e00\u62db\u5931\u5229\uff0c\u8fde\u5fd9\u5012\u8e29\u4e03\u661f\u6b65\uff0c\u53cc\u638c\u95ea\u7535\u822c\u5c01\u51fa\u4e94\u62db\uff0c\u624d\u628a\u8f9b\u6377\u8fd9\u62db\u5378\u5728\u4e00\u65c1\uff01 \u3000\u3000\u4f17\u4eba\u5446\u5f97\u4e00\u5446\uff0c\u624d\u66b4\u51fa\u8f70\u96f7\u4f3c\u7684\u4e00\u58f0\u5927\u5f69\uff01 \u3000\u3000\u53ea\u89c1\u5929\u715e\u661f\u541b\u8138\u5982\u5bd2\u6f6d\uff0c\u9634\u607b\u607b\u5730\u9053\uff1a\u201c\u6211\u9053\u662f\u8c01\uff1f\u539f\u6765\u7684\u6885\u8001\u513f\u7684\u4f20\u4eba\uff0c\u54fc\uff01\u201d \u3000\u3000\u8f9b\u6377\u5fc3\u4e2d\u5927\u5947\uff0c\u5fc3\u60f3\u6211\u8fd9\u62db\u201c\u51b7\u6885\u62c2\u9762\u201d\u4e43\u8d77\u6885\u53d4\u53d4\u5927\u6218\u4e94\u5927\u5251\u6d3e\u4e4b\u540e\u624d\u609f\u51fa\u7684\uff0c\u4f60\u8fd9 \u8001\u513f\u81ea\u79f0\u9690\u9000\u4e86\u5345\u5e74\uff0c\u600e\u4f1a\u8bc6\u5f97\uff1f \u3000\u3000\u4ed6\u8138\u4e0a\u9739\u51fa\u72d0\u7591\u4e4b\u8272\uff0c\u90a3\u5929\u715e\u661f\u541b\u4f3c\u77e5\u4ed6\u610f\uff0c\u51b7\u7b11\u9053\uff1a\u201c\u9664\u4e86\u6885\u8001\u9b3c\u8fd9\u79cd\u4eba\uff0c\u5382\u5929\u4e0b\u6ca1 \u6709\u7b2c\u4e8c\u4e2a\u60f3\u5f97\u51fa\u8fd9\u79cd\u7f3a\u5fb7\u62db\u5f0f\uff0c\u563f\uff01\u201d \u3000\u3000\u8f9b\u6377\u6b63\u5f85\u5e76\u53e3\uff0c\u90a3\u5929\u715e\u661f\u541b\u5927\u559d\u4e00\u58f0\uff1a\u201c\u5420\uff01\u770b\u62db\uff01\u201d \u3000\u3000\u53cc\u638c\u4e00\u7ffb\uff0c\u5c31\u5982\u4e00\u9635\u65cb\u98ce\u822c\u4e91\u5377\u800c\u4e0a\uff0c\u4e00\u8fde\u4e03\u62f3\u6253\u51fa\uff0c\u62db\u5f0f\u4e4b\u8be1\u5947\u9634\u6bd2\uff0c\u7aef\u7684\u4e16\u4e0a\u65e0 \u53cc\uff0c\u8f9b\u6377\u5012\u9000\u4e09\u6b65\uff0c\u6b63\u5f85\u53cd\u51fb\uff0c\u54ea\u77e5\u5929\u715e\u661f\u541b\u5927\u559d\u4e00\u58f0\uff0c\u53c8\u662f\u4e03\u638c\u62cd\u51fa\uff0c\u8f9b\u6377\u957f\u5251\u8fde\u6f14\u7edd \u5b66\uff0c\u4ecd\u662f\u6b62\u4e0d\u4f4f\u5012\u9000\u540c\u6b65\uff0c\u970e\u65f6\u5385\u4e2d\u4f17\u8c6a\u4e0d\u7981\u5927\u58f0\u60ca\u558a\u8d77\u6765\uff0c\u8eb2\u5728\u4eba\u4e1b\u4e2d\u7684\u7ae5\u5b50\u4e0d\u7981\u77aa\u7740\u4e00 \u53cc\u5927\u773c\u6674\uff0c\u5fc3\u4e2d\u6251\u6251\u8df3\u52a8\u4e0d\u6b62\u3002 \u3000\u3000\u8f9b\u6377\u5144\u5bf9\u624b\u8fd9\u8def\u62f3\u6cd5\u72e0\u6bd2\u9634\u6076\uff0c\u8f83\u4e4b\u5f53\u5e74\u5929\u7afa\u6b66\u5b66\u72b9\u6709\u8fc7\u4e4b\uff0c\u81ea\u5df1\u53ea\u8981\u4e00\u4e2a\u758f\u795e\uff0c\u7acb\u523b \u5c31\u662f\u4e27\u547d\u5f53\u573a\u4e4b\u7978\uff0c\u624b\u4e2d\u5168\u529b\u653b\u4e86\u51e0\u62db\uff0c\u5374\u59cb\u7ec8\u62a2\u4e0d\u56de\u4e3b\u52a8\uff0c\u4ed6\u5fc3\u9053\uff1a\u201c\u518d\u8981\u662f\u9000\u4e24\u6b65\uff0c\u8fd9 \u8001\u513f\u62f3\u6cd5\u65bd\u5230\u4e2d\u950b\uff0c\u53ea\u6015\u4e07\u96be\u6273\u56de\u2014\u2014\u201d \u3000\u3000\u8f9b\u6377\u8eab\u7ecf\u767e\u6218\uff0c\u773c\u5149\u51c6\u786e\u65e0\u6bd4\uff0c\u679c\u7136\u5929\u715e\u661f\u541b\u5927\u559d\u4e00\u58f0\uff0c\u62f3\u52bf\u9aa4\u7d27\uff0c\u547c\u547c\u4e4b\u58f0\u6709\u5982\u72c2\u98ce \u66b4\u96e8\uff0c\u56f4\u89c2\u4f17\u8c6a\u4e0d\u7981\u60ca\u547c\u5927\u4f5c\u3002 \u3000\u3000\u53ea\u89c1\u8f9b\u633a\u6c89\u7740\u5e94\u6218\uff0c\u771f\u529b\u5341\u6210\u53eb\u8db3\uff0c\u6885\u9999\u5251\u4e0a\u7684\u5251\u6c14\u523a\u8033\u6b32\u88c2\uff0c\u4e00\u8fde\u5341\u62db\u8fc7\u53bb\uff0c\u8f9b\u6377\u52c9 \u529b\u6301\u4f4f\u4e0d\u9000\u4e4b\u52bf\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u5fc3\u4e2d\u6697\u8d5e\uff0c\u53cc\u62f3\u4e00\u5206\u4e00\u5408\uff0c\u5341\u6307\u5916\u6325\u2014\u2014 \u3000\u3000\u8f9b\u6377\u77e5\u4ed6\u6700\u5389\u5bb3\u7684\u6740\u7740\u5c31\u8981\u51fa\u624b\uff0c\u81ea\u5df1\u82e5\u662f\u518d\u4e0d\u80fd\u626d\u8f6c\u5c40\u52bf\uff0c\u8fd9\u4e00\u6218\u662f\u8f93\u5b9a\u4e86 \u3000\u3000\u53ea\u89c1\u4ed6\u53cc\u76ee\u7cbe\u5149\u66b4\u5c04\uff0c\u5f00\u58f0\u5410\u6c14\u5730\u5927\u53f1\u4e00\u58f0\uff0c\u6885\u9999\u5b9d\u5251\u9661\u7136\u5316\u4f5c\u5343\u4e07\u5bd2\u5149\uff0c\u970e\u65f6\u5251\u6c14\u5636 \u5636\uff0c\u5bd2\u5149\u970d\u970d\uff0c\u5929\u715e\u661f\u541b\u731b\u89c9\u81ea\u5df1\u767e\u822c\u653b\u52bf\u6709\u5982\u77f3\u6c89\u5927\u6d77\uff0c\u53cd\u800c\u81ea\u5df1\u5168\u8eab\u767e\u7a74\u4f3c\u4e4e\u5168\u7136\u9732\u5728 \u654c\u62db\u4e4b\u4e0b\uff0c\u4ed6\u602a\u543c\u4e00\u58f0\uff0c\u6025\u9000\u534a\u4e08\uff01 \u3000\u3000\u5728\u573a\u4f17\u4eba\u559d\u5f69\u4e4b\u58f0\u9707\u5929\u5e72\u4e91\uff0c\u5374\u65e0\u4eba\u8bc6\u5f97\u8fd9\u62db\u6b63\u662f\u201c\u5927\u884d\u5341\u5f0f\u201d\u7684\u8d77\u9996\u4e4b\u5f0f\u201c\u65b9\u751f\u4e0d \u606f\u201d\uff01 \u3000\u3000\u4e0d\u8fc7\u4f17\u4eba\u4e2d\u6709\u4e00\u90e8\u5206\u731b\u7136\u60f3\u8d77\u4e00\u4e8b\uff0c\u65b9\u624d\u90a3\u59d3\u4f59\u7684\u9752\u5e74\u5251\u5ba2\u6240\u65bd\u7684\u4e00\u62db\u6b63\u4e0e\u8fd9\u62db\u5341\u5206\u76f8 \u50cf\uff0c\u96be\u9053\u90a3\u59d3\u4f59\u7684\u548c\u8f9b\u5927\u4fa0\u6709\u5173\u8054\uff1f \u3000\u3000\u53ea\u89c1\u5385\u4e2d\u8f9b\u6377\u62b1\u5251\u5982\u5f13\uff0c\u731b\u5438\u771f\u6c14\uff0c\u8eab\u5f62\u7f13\u7f13\u5f80\u5de6\u79fb\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u90a3\u5929\u715e\u661f\u541b\u5374\u662f\u9762\u5bd2\u5982\u51b0\u5730\u7f13\u7f13\u5411\u53f3\u79fb\u4e86\u4e24\u6b65\u3002 \u3000\u3000\u8f9b\u6377\u811a\u624d\u505c\u6b65\uff0c\u5ffd\u7136\u731b\u53ef\u5f27\u5f62\u5730\u9000\u56de\u539f\u5730\uff0c\u90a3\u5929\u715e\u661f\u541b\u7adf\u4e5f\u540c\u6837\u98de\u5feb\u9000\u56de\u539f\u5730\u3002 \u3000\u3000\u4f17\u4eba\u867d\u7136\u4e0d\u61c2\uff0c\u4f46\u77e5\u9053\u8fd9\u4e24\u4eba\u4e00\u4e3e\u4e00\u52a8\u83ab\u4e0d\u9690\u542b\u9ad8\u6df1\u6b66\u5b66\u4e4b\u9053\uff0c\u5ffd\u7136\u5e7f\u4eba\u5927\u53eb\u9053\uff1a \u201c\u77a7\uff0c\u77a7\u90a3\u9752\u7816\u2014\u2014\u201d \u3000\u3000\u4f17\u4eba\u968f\u7740\u4e00\u770b\uff0c\u4e5f\u90fd\u60ca\u547c\u8d77\u6765\uff0c\u539f\u6765\u90a3\u9752\u7816\u5730\u4e0a\uff0c\u51e1\u662f\u5929\u715e\u661f\u541b\u6240\u8d70\u4e4b\u5730\uff0c\u9a87\u7136\u663e\u51fa\u4e00 \u4e2a\u4e2a\u6df1\u8fbe\u534a\u5bf8\u7684\u811a\u5370\u3002 \u3000\u3000\u559d\u91c7\u58f0\u4e2d\uff0c\u8f9b\u6377\u8eab\u5f62\u4e00\u957f\uff0c\u547c\u7684\u4e00\u58f0\uff0c\u8fce\u9762\u70b9\u51fa\u4e00\u5251\uff0c\u8fd9\u4e00\u5251\u70b9\u51fa\u529b\u9053\u4e0d\u98d8\u5ffd\uff0c\u4e00\u95ea\u4e4b \u4e0b\uff0c\u8eab\u5f62\u5374\u662f\u53f3\u865a\u5de6\u5b9e\uff0c\u4e00\u8fdb\u4e00\u9000\u3002 \u3000\u3000\u8eab\u6cd5\u4e4b\u5feb\uff0c\u4f46\u89c1\u4e00\u4e2a\u8679\u5149\u5de6\u53f3\u6447\u8361\uff0c\u597d\u4e0d\u60ca\u4eba\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u60c5\u77e5\u5bf9\u624b\u715e\u624b\u5c06\u81f3\uff0c\u53cc\u8db3\u7262\u5e76\uff0c\u76ee\u4e0d\u8f6c\u775b\uff0c\u4e00\u77a7\u4e4b\u4e0b\uff0c\u5374\u89c1\u8f9b\u6377\u53cc\u8db3\u865a\u6000\u82e5 \u8c37\uff0c\u4e00\u65f6\u7adf\u8bc6\u4e0d\u5f97\u6709\u4f55\u7528\u610f\u3002 \u3000\u3000\u8f9b\u6377\u4e00\u8fde\u5411\u5de6\u8dfa\u51fa\u4e03\u6b65\uff0c\u7b2c\u4e03\u6b65\u65b9\u4e00\u843d\u5730\uff0c\u8eab\u5f62\u9661\u7136\u4e00\u5f39\uff0c\u95ea\u7535\u4e5f\u4f3c\u63a0\u5411\u53f3\u8fb9\uff0c\u5374\u89c1\u4ed6 \u4e0a\u8eab\u4e00\u584c\uff0c\u63a0\u5411\u53f3\u8fb9\u4e4b\u5b9e\u662f\u865a\u62db\uff0c\u4e00\u6643\u4e4b\u4e0b\uff0c\u53c8\u5411\u5de6\u65b9\u8dfa\u51fa\u4e03\u6b65\u3002 \u3000\u3000\u8fd9\u6837\u4e00\u6765\uff0c\u8f9b\u6377\u8eab\u5f62\u4e00\u6643\u4e4b\u4e0b\uff0c\u5df2\u8f6c\u5230\u5929\u715e\u661f\u541b\u5de6\u4f7f\u4e0d\u5f53\u4e4b\u5730\uff0c\u5929\u715e\u661f\u541b\u731b\u53ef\u4e00\u53f1\uff0c\u970d \u5730\u4e00\u4e2a\u53cd\u8eab\uff0c\u9762\u5bf9\u7740\u8f9b\u6377\u3002 \u3000\u3000\u4f46\u8f9b\u6377\u4f55\u7b49\u529f\u529b\uff0c\u65f6\u673a\u4e00\u77ac\u800c\u8fc7\uff0c\u957f\u5251\u4e00\u9886\uff0c\u547c\u7684\u957f\u9a71\u76f4\u4eba\uff0c\u7528\u7684\u662f\u8d70\u4e2d\u5bab\uff0c\u8e0f\u6d2a\u95e8\u7684 \u62db\u5f0f\u3002 \u3000\u3000\u201c\u563f\u201d\uff0c\u5929\u715e\u661f\u541b\u53cc\u62f3\u4e00\u62b1\uff0c\u5408\u7acb\u5f53\u80f8\uff0c\u6709\u5982\u592a\u6781\uff0c\u95ea\u7535\u95f4\u5e76\u62f3\u4e00\u51fb\u800c\u51fa\u3002 \u3000\u3000\u98ce\u96f7\u4e4b\u58f0\u970e\u65f6\u5927\u4f5c\uff0c\u8f9b\u6377\u66b4\u5403\u4e00\u60ca\uff0c\u4ed6\u53ef\u60f3\u4e0d\u5230\u8fd9\u5929\u715e\u661f\u541b\u62f3\u4e0a\u7684\u529b\u9053\u7adf\u662f\u5982\u6b64\u521a\u731b\uff0c \u5fc3\u77e5\u5047\u82e5\u81ea\u5df1\u4e00\u5251\u6311\u51fa\uff0c\u771f\u529b\u4e0d\u53ca\u5bf9\u624b\uff0c\u4e00\u5b9a\u8981\u5403\u5927\u4e8f\uff0c\u6743\u8861\u4e4b\u4e0b\uff0c\u731b\u53ef\u5927\u543c\u4e00\u58f0\uff0c\u4e00\u6446\u624b \u4e2d\u957f\u5251\uff0c\u5728\u80f8\u524d\u5e03\u51fa\u4e00\u5f20\u5bc6\u4e0d\u900f\u98ce\u7684\u7f51\uff0c\u540c\u65f6\u95f4\u91cc\uff0c\u6025\u7136\u5b9a\u4e0b\u8eab\u5f62\u3002 \u3000\u3000\u201c\u53ee\u201d\uff0c\u201c\u53ee\u201d\uff0c\u5b9d\u5251\u632f\u52a8\u4e4b\u58f0\u5927\u4f5c\uff0c\u8f9b\u6377\u75be\u7136\u4e00\u6325\uff0c\u8eab\u5f62\u51cc\u7a7a\uff0c\u4e00\u7f29\u4e4b\u4e0b\uff0c\u5c06\u5bf9\u65b9\u5982 \u5c71\u5185\u529b\u5316\u89e3\u800c\u53bb\u3001\u540c\u65f6\u767e\u5fd9\u4e2d\u8fd8\u4e00\u8fde\u6233\u51fa\u5341\u4f59\u5251\u3002 \u3000\u3000\u56db\u5468\u89c2\u6218\u7684\u4eba\u8fd9\u65f6\u53cd\u5012\u5bc2\u9759\u4e86\u4e0b\u6765\uff0c\u4ed6\u4eec\u77e5\u9053\u5206\u80dc\u8d1f\u7684\u65f6\u5019\u7acb\u5373\u8981\u5230\u4e86\u3002\u731b\u7136\uff0c\u8f9b\u6377\u957f \u5251\u4e0d\u65ad\u632f\u52a8\uff0c\u53cc\u8db3\u5de6\u53f3\u5fae\u6643\uff0c\u731b\u53ef\u8679\u5149\u51b2\u5929\u800c\u8d77\uff0c\u4e00\u6cfb\u4e4b\u4e0b\uff0c\u5de6\u53f3\u4e00\u9635\u4e4b\u6446\u821e\uff0c\u8eab\u5f62\u9661\u7136\u5df1 \u4fb5\u8fd1\u4e09\u4e94\u4e08\u4e4b\u8fdc\u3002 \u3000\u3000\u201c\u563f\u201d\uff01\u5929\u715e\u661f\u541b\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u53ef\u771f\u60f3\u4e0d\u5230\u8fd9\u5bb6\u4f19\u7684\u8eab\u6cd5\u7adf\u662f\u5982\u6b64\u795e\u5999\uff0c\u4efb\u4ed6\u540d\u9707\u4e00 \u65b9\uff0c\u7eb5\u6a2a\u5929\u4e0b\uff0c\u8eab\u7ecf\u767e\u6218\uff0c\u529f\u529b\uff0c\u7ecf\u9a8c\uff0c\u65e0\u4e00\u4e0d\u662f\u4e0a\u4e0a\u4e4b\u9009\uff0c\u4f46\u5374\u4e00\u4e1d\u4e00\u6beb\u4e5f\u77a7\u4e0d\u51fa\u8fd9\u7b49\u795e \u5999\u53e4\u602a\u7684\u8eab\u6cd5\u3002 \u3000\u3000\u6562\u60c5\u8f9b\u6377\u5df2\u7528\u51fa\u4e16\u5916\u4e09\u4ed9\u6167\u5927\u5e08\u6240\u6388\u7684\u201c\u8bd8\u6469\u6b65\u6cd5\u201d\u3002 \u3000\u3000\u201c\u547c\u201d\u4e00\u58f0\uff0c\u8f9b\u6377\u4e58\u4ed6\u4e00\u6014\u4e4b\u4e0b\uff0c\u653b\u51fa\u4e00\u5251\u3002 \u3000\u3000\u957f\u5251\u4e00\u632f\uff0c\u5251\u5c16\u4e0d\u65ad\u8df3\u52a8\uff0c\u70b9\u51fa\u5343\u4e07\u5251\u661f\uff0c\u4f17\u4eba\u4f46\u89c1\u8679\u5149\u541e\u5410\u4e4b\u9645\uff0c\u7adf\u7136\u5168\u662f\u865a\u62db\uff0c\u970e \u65f6\u8f9b\u6377\u5df1\u6e10\u81f3\u5929\u715e\u661f\u541b\u8eab\u524d\u3002 \u3000\u3000\u5929\u715e\u661f\u541b\u731b\u53ef\u75be\u5636\u4e00\u58f0\uff0c\u53cc\u62f3\u503e\u5168\u529b\u51fb\u4e86\u51fa\u2014\u2014 \u3000\u3000\u8f9b\u6377\u8eab\u5f62\u9661\u7136\u5f39\u5728\u7a7a\u4e2d\uff0c\u52b2\u98ce\u6fc0\u8361\u5904\uff0c\u4ed6\u957f\u8863\u7eb7\u7eb7\u98d8\u7136\u62c2\u8d77\uff0c\u4f46\u5728\u8fd9\u4e00\u5239\u90a3\u95f4\uff0c\u8f9b\u6377\u81ea \u4e0a\u800c\u4e0b\uff0c\u53d1\u51fa\u4e00\u5f0f\u201c\u5927\u884d\u795e\u5251\u201d\u4e2d\u7684\u201c\u7269\u6362\u661f\u79fb\u201d\uff01 \u3000\u3000\u5251\u6c14\u5636\u5636\u800c\u8fc7\uff0c\u7535\u5149\u706b\u77f3\u95f4\u5df2\u7136\u7a81\u7834\u5929\u715e\u661f\u541b\u7684\u62f3\u5f71\uff0c\u70b9\u5411\u5929\u715e\u661f\u541b\u80a9\u5934\uff01 \u3000\u3000\u5929\u715e\u661f\u541b\u9762\u8272\u9661\u53d8\uff0c\u5168\u529b\u4e00\u584c\u53f3\u80a9\uff0c\u603b\u7b97\u4ed6\u89c1\u673a\u5f97\u65e9\uff0c\u8f9b\u6377\u4e00\u6311\u4e4b\u4e0b\uff0c\u4f46\u95fb\u5431\u4e00\u58f0\u80a9\u5934 \u8863\u886b\u88ab\u523a\u7834\u4e00\u9053\u53e3\u5b50\uff01 \u3000\u3000\u8f9b\u6377\u8eab\u5f62\u6709\u82e5\u795e\u9f99\u5347\u5929\uff0c\u4e00\u63a0\u4e4b\u4e0b\uff0c\u5728\u5929\u7a7a\u5212\u8fc7\u4e00\u9053\u5de5\u6574\u7684\u5706\u5f27\uff0c\u98d8\u98d8\u843d\u5728\u5730\u4e0a\uff0c\u4e00\u9886 \u624b\u4e2d\u5251\uff0c\u6e05\u6717\u7684\u8bf4\u4e86\u58f0\uff1a\u201c\u627f\u8ba9\uff01\u201d \u3000\u3000\u5929\u715e\u661f\u541b\u8d1f\u624b\u800c\u7acb\uff0c\u970e\u65f6\u4ed6\u628a\u80dc\u8d1f\u4e4b\u4e8b\u5b8c\u5168\u5fd8\u53bb\u4e86\uff0c\u5934\u8111\u4e2d\u53ea\u60f3\u5230\u8fd9\u6885\u5c71\u6c11\u7684\u4f20\u4eba\u600e\u4f1a \u6709\u5982\u6b64\u795e\u5947\u7684\u8eab\u6cd5\u3002 \u3000\u3000\u4e00\u5239\u65f6\uff0c\u4ed6\u5df2\u60f3\u904d\u4e86\u81ea\u5df1\u6240\u4f1a\u7684\u62db\u5f0f\u4e2d\u7ade\u6ca1\u6709\u4e0d\u5f0f\u80fd\u591f\u9632\u7740\u8fd9\u4e00\u5957\u53e4\u602a\u7684\u8eab\u6cd5\uff0c\u5fc3\u4e2d\u9661 \u7136\u4e00\u9707\uff0c\u4e00\u8dfa\u811a\uff0c\u4e5f\u6ca1\u6709\u7406\u4f1a\u8f9b\u6377\uff0c\u5583\u5583\u9053\uff1a\u201c\u7f62\u4e86\uff01\u7f62\u4e86\uff01\u201d \u3000\u3000\u53cd\u8eab\u5982\u98de\u800c\u53bb\u3002 \u3000\u3000\u767b\u65f6\u5927\u5385\u4e2d\u5347\u8d77\u9707\u5929\u4ef7\u7684\u559d\u91c7\uff01 \u3000\u3000\u8f9b\u6377\u5fb5\u76b1\u7740\u7709\uff0c\u8f7b\u8f7b\u628a\u5b9d\u5251\u63d2\u56de\uff0c\u5bf9\u90a3\u9707\u5929\u559d\u91c7\u58f0\u76f4\u5982\u672b\u89c9\uff0c\u4ed6\u773c\u5149\u4ece\u4eba\u4e1b\u4e2d\u4e00\u626b\uff0c\u660e \u58f0\u9053\uff1a\u201c\u5c0f\u7334\u513f\uff0c\u8fd8\u4e0d\u6eda\u51fa\u6765\uff1f\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u563b\u563b\u7b11\u7740\u5e94\u58f0\u8dd1\u4e86\u51fa\u6765\uff0c\u626f\u7740\u8f9b\u6377\u7684\u8896\u5b50\uff0c\u5927\u58f0\u53eb\u9053\uff1a\u201c\u7239\uff0c\u4f60\u672c\u9886\u771f\u5927\u3002\u201d \u3000\u3000\u8f9b\u6377\u54fc\u4e86\u4e00\u58f0\uff0c\u677f\u7740\u8138\u9053\uff1a\u201c\u4f60\u5728\u5916\u9762\u6df7\u5f97\u597d\u5440\uff0c\u4e5f\u4e0d\u5fc5\u56de\u5bb6\u5566\u3002\u201d \u3000\u3000\u90a3\u7ae5\u5b50\u88c5\u5f97\u5927\u53d7\u59d4\u5c48\u7684\u6a21\u6837\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u4e0d\u8fc7\u7a0d\u7a0d\u73a9\u4e86\u4e00\u8d9f\u2014\u2014\u201d\u3000\u3000\u3000\u3000\u4eba\u4e1b\u4e2d\u7684 \u6797\u7389\u672c\u6765\u89c1\u8f9b\u6377\u5251\u672f\u5982\u795e\uff0c\u98ce\u59ff\u82f1\u723d\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u4ef0\u6155\u4e0d\u5df2\uff0c\u5ffd\u89c1\u90a3\u7ae5\u5b50\u8dd1\u51fa\u5927\u58f0\u53eb \u201c\u7238\u201d\uff0c\u4e0d\u7531\u604d\u7136\u5927\u609f\uff0c\u5fc3\u4e2d\u60f3\u5230\u8fd9\u5c0f\u9b3c\u4e00\u89c1\u5230\u8f9b\u6377\u9762\u5e26\u559c\u8272\u7684\u60c5\u5f62\uff0c\u53c8\u60f3\u8d77\u4ed6\u66fe\u8ba4\u771f\u95ee\u81ea \u5df1\u8bc6\u4e0d\u8bc6\u5f97\u4e00\u4e2a\u4ed7\u4e49\u758f\u8d22\u7684\u5c0f\u4fa0\u8f9b\u5e73\u7684\u4e8b\uff0c\u4e0d\u7981\u7b11\u5f2f\u4e86\u8170\uff0c\u5fcd\u4e0d\u4f4f\u8dd1\u5c06\u51fa\u6765\uff0c\u7b11\u53eb\u9053\uff1a\u201c\u8f9b \u5c0f\u4fa0\uff0c\u4e86\u4e0d\u8d77\uff0c\u4ed7\u4e49\u758f\u8d22\u2014\u2014\u201d \u3000\u3000\u8f9b\u5e73\u4e0d\u7981\u5927\u7a98\uff0c\u8fde\u5fd9\u5927\u58f0\u5bf9\u8f9b\u6377\u9053\uff1a\u201c\u7238\u7238\uff0c\u6211\u4eec\u2014\u2014\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u56de\u5bb6\u5440\uff1f\u201d \u3000\u3000\u8f9b\u6377\u4f55\u7b49\u806a\u660e\uff0c\u4e00\u77a7\u60c5\u5f62\uff0c\u5fc3\u4e2d\u604d\u7136\uff0c\u518d\u4e5f\u6446\u4e0d\u51fa\u505a\u7236\u4eb2\u7684\u5a01\u4e25\uff0c\u7b11\u9053\uff1a\u201c\u8f9b\u5c0f\u4fa0\uff0c\u4f60 \u4fa0\u540d\u6ee1\u5929\u4e0b\u5566\uff0c\u54b1\u4eec\u5168\u542c\u4f60\u5429\u5490\u3002\u201d \u3000\u3000\u8f9b\u5e73\u4e0d\u7981\u9762\u7ea2\u8033\u8d64\uff0c\u8fde\u5fd9\u80e1\u4e71\u9053\uff1a\u201c\u8fd9\u4e24\u5929\uff0c\u5929\u6c14\u53ef\u95f7\u5f97\u5f88\u3002\u201d \u3000\u3000\u8f9b\u633a\u95ee\u9053\uff1a\u201c\u5c0f\u7334\u513f\uff0c\u4f60\u9001\u4e86\u4ec0\u4e48\u4e1c\u897f\u5c31\u6765\u9a97\u5403\uff1f\u201d \u3000\u3000\u91d1\u7ae5\u5b50\u8f9b\u5e73\u4e00\u6307\u684c\u4e0a\u4e2a\u7b11\u53e3\u61a8\u7136\u7684\u80d6\u94c1\u5a03\u5a03\uff0c\u8f9b\u633a\u771f\u662f\u557c\u7b11\u7686\u975e\uff0c\u4ed6\u867d\u4e3a\u4eba\u4e4b\u7236\uff0c\u4f46\u548c \u7231\u5b50\u6240\u6df7\u65f6\uff0c\u4e00\u5411\u6beb\u65e0\u4e25\u7236\u6001\u5ea6\uff0c\u6b64\u8fdb\u5fcd\u4fca\u4e0d\u4f4f\uff0c\u8fde\u9053\uff1a\u201c\u597d\u54c7\u597d\u54c7\uff0c\u4f60\u5988\u5988\u7ed9\u4f60\u7684\u5341\u4e00\u5c81 \u7684\u751f\u65e5\u793c\u54c1\uff0c\u4ed6\u7adf\u9001\u7ed9\u96c1\u8361\u5927\u4fa0\uff0c\u771f\u4e8f\u4f60\u60f3\u5f97\u51fa\u3002\u201d \u3000\u3000\u8fd9\u65f6\u4f17\u597d\u6c49\u90fd\u6270\u4e0a\u6765\u5411\u8f9b\u6377\u9053\u8d3a\uff0c\u8f9b\u633a\u81ea\u662f\u6781\u529b\u8c26\u865a\uff0c\u8f9b\u5e73\u4e58\u673a\u6e9c\u5f00\uff0c\u5374\u542c\u9ad8\u6218\u6b63\u4f4e\u58f0 \u5411\u4e00\u8001\u8005\u9053\uff1a\u201c\u2026\u2026\u8fd9\u59ca\u59b9\u4fe9\u5bb6\u7834\u4eba\u4ea1\uff0c\u6211\u82e5\u8ddf\u5e08\u7236\u53bb\u5173\u4e1c\u7ec3\u6b66\uff0c\u600e\u751f\u5b89\u7f6e\u2026\u2026\u201d \u3000\u3000\u770b\u9ad8\u6218\u53cc\u7709\u5fae\u5ead\uff0c\u4f3c\u4e4e\u96be\u4ee5\u89e3\u51b3\uff0c\u5f53\u4e0b\u4fa0\u4e49\u4e4b\u5fc3\u5927\u8d77\uff0c\u4e0a\u524d\u9053\uff1a\u201c\u6211\u548c\u7239\u7239\u5c31\u8981\u56de\u5bb6\uff0c \u6797\u5bb6\u59ca\u59ca\u4e0d\u5982\u6682\u65f6\u4f4f\u5230\u6211\u5bb6\u91cc\u53bb\u2014\u2014\u6211\u5bb6\u5728\u6c99\u9f99\u576a\u3002\u201d \u3000\u3000\u9ad8\u6218\u4e00\u542c\u6709\u7406\uff0c\u56de\u5934\u770b\u4e86\u770b\u98ce\u67cf\u6768\uff0c\u98ce\u67cf\u6768\u60f3\u4e86\u60f3\u9053\uff1a\u201c\u5982\u6b64\u751a\u597d\u3002\u201d \u3000\u3000\u9ad8\u6218\u5fd9\u9053\uff1a\u201c\u591a\u8c22\u4f60\u5566\u3002\u201d \u3000\u3000\u8f9b\u5e73\u9053\uff1a\u201c\u4e9b\u5fae\u4e4b\u52b3\uff0c\u4f55\u8db3\u6302\u9f7f\u3002\u201d \u3000\u3000\u9ad8\u6218\u89c1\u4ed6\u795e\u91c7\u98de\u626c\uff0c\u4e00\u526f\u6545\u4f5c\u8001\u6c14\u7684\u6a21\u6837\uff0c\u518d\u770b\u90a3\u8f9b\u6377\u795e\u59ff\u82f1\u98ce\uff0c\u60f3\u5230\u81ea\u5df1\u7239\u5a18\u5df2\u6210\u9ec4 \u571f\uff0c\u4e0d\u7981\u5fc3\u4e2d\u4e00\u9178\u3002 \u3000\u3000\u8f9b\u5e73\u4fbf\u5f15\u9ad8\u6218\u4ecb\u7ecd\u7ed9\u7236\u4eb2\u8ba4\u8bc6\uff0c\u8f9b\u6377\u7ec6\u770b\u9ad8\u6218\uff0c\u53ea\u89c1\u4ed6\u7709\u76ee\u4fca\u79c0\uff0c\u9aa8\u683c\u6e05\u5947\uff0c\u8d44\u8d28\u51b3\u4e0d \u5728\u7231\u5b50\u4e4b\u4e0b\uff0c\u4e0d\u7981\u9887\u4e3a\u559c\u6b22\u3002 \u3000\u3000\u9ad8\u6218\u6307\u7740\u5e08\u7236\u5411\u8f9b\u6377\u9053\uff1a\u201c\u8f9b\u53d4\u53d4\uff0c\u8fd9\u662f\u5bb6\u5e08\u8fb9\u585e\u5927\u4fa0\u98ce\u67cf\u6768\u3002\u201d \u3000\u3000\u8f9b\u6377\u5411\u98ce\u67cf\u6768\u62f1\u624b\u4e00\u63d6\u9053\uff1a\u201c\u665a\u8f88\u624d\u4ece\u65e0\u6781\u5c9b\u6765\uff0c\u4ece\u5bb6\u5cb3\u7236\u53e3\u4e2d\u5f97\u77e5\u98ce\u5927\u4fa0\u82f1\u98ce\u9ad8\u4e49\uff0c \u771f\u662f\u6b66\u6797\u6cf0\u6597\uff0c\u665a\u8f88\u6b63\u6101\u65e0\u7f18\u62dc\u8bc6\uff0c\u4e0d\u610f\u5728\u6b64\u76f8\u4f1a\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u89c1\u4ed6\u9002\u624d\u8eab\u624b\uff0c\u5fc3\u4e2d\u597d\u751f\u4f69\u670d\uff0c\u6697\u5fd6\u6b64\u4eba\u96c6\u6570\u4f4d\u524d\u8f88\u6b66\u529f\u4e8e\u4e00\u8eab\uff0c\u96be\u602a\u51fa\u7c7b\u62d4 \u8403\uff0c\u5f53\u4e0b\u8fde\u8fde\u8c26\u8c22\u9053\uff1a\u201c\u8001\u673d\u865a\u957f\u51e0\u5c81\uff0c\u8f9b\u5927\u4fa0\u4e0d\u89c1\u5f03\uff0c\u558a\u6211\u4e00\u58f0\u8001\u5144\u8db3\u77e3\uff0c\u6211\u5728\u676d\u57ce\u89c1\u8001 \u5f1f\u5e74\u7eaa\u9752\u9752\u5927\u663e\u8eab\u624b\uff0c\u91cd\u60e9\u6deb\u8d3c\uff0c\u771f\u5982\u795e\u9f99\u4e0d\u89c1\u9996\u5c3e\uff0c\u597d\u751f\u4ee4\u4eba\u53f9\u670d\uff0c\u201d \u3000\u3000\u8f9b\u6377\u9053\uff1a\u201c\u539f\u6765\u665a\u8f88\u4e00\u5207\u90fd\u5728\u8001\u524d\u8f88\u773c\u4e2d\uff0c\u8001\u524d\u8f88\u7684\u8f7b\u8eab\u529f\u592b\u771f\u662f\u6df1\u4e0d\u53ef\u6d4b\u3002\u201d \u3000\u3000\u98ce\u67cf\u6768\u89c1\u5927\u4e8b\u5df2\u5b9a\uff0c\u5c31\u5e26\u7740\u9ad8\u6218\u5411\u4f17\u4eba\u544a\u522b\uff0c\u5e76\u5411\u8f9b\u633a\u6bb7\u6bb7\u81f4\u610f\uff0c\u8981\u4ed6\u8f6c\u544a\u65e0\u6781\u5c9b\u4e3b\u65e0 \u6839\u751f\u76f8\u5f85\u4e4b\u60c5\u3002 \u3000\u3000\u9ad8\u6218\u5fcd\u4e0d\u4f4f\u56de\u5934\u5411\u8f9b\u5e73\u53ca\u6797\u6c0f\u59ca\u59b9\u544a\u8f9e\uff0c\u53ea\u89c1\u6797\u6c76\u773c\u4e2d\uff0c\u6cea\u5149\u6676\u83b9\uff0c\u5f3a\u5fcd\u54c0\u75db\uff0c\u60c5\u6df1\u6b3e \u6b3e\u7684\u671b\u7740\u81ea\u5df1\uff0c\u60f3\u5230\u81ea\u5df1\u4e00\u5fc3\u76f8\u7231\u7684\u59ec\u857e\u4e0d\u77e5\u5982\u4f55\u4e0d\u544a\u800c\u522b\uff0c\u800c\u6797\u6c76\u662f\u5e7c\u65f6\u6e38\u4f34\uff0c\u5bf9\u81ea\u5df1\u53c8 \u662f\u8fd9\u822c\u60c5\u6df1\uff0c\u5fc3\u4e2d\u771f\u662f\u4e71\u6210\u4e00\u56e2\uff0c\u4e0d\u77e5\u5982\u4f55\u662f\u597d\u3002 \u3000\u3000\u6625\u53bb\u79cb\u6765\uff0c\u53c8\u662f\u4e00\u5e74\u7684\u51ac\u5929\u3002 \u3000\u3000\u5927\u96ea\u7eb7\u98de\uff0c\u5bd2\u6885\u6012\u653e\uff0c\u6c99\u9f99\u576a\u6885\u6797\u6df1\u5904\uff0c\u4e00\u5e62\u5bbd\u655e\u7684\u5e73\u623f\uff0c\u95e8\u7a97\u7d27\u95ed\u7740\uff0c\u51db\u51bd\u7684\u5317\u98ce\u4e0d \u505c\u7684\u5439\u8fc7\uff0c\u5927\u5730\u6f2b\u6f2b\u5730\u7740\u4e0a\u4e86\u94f6\u8272\u7684\u65b0\u5986\u3002 \u3000\u3000\u5ba4\u5185\uff0c\u706b\u5149\u718a\u718a\uff0c\u6625\u610f\u76c8\u7136\uff0c\u4e00\u4e2a\u8001\u6001\u9f99\u949f\u7684\u4eba\uff0c\u6b63\u6367\u8d77\u4e00\u4e2a\u9152\u846b\u82a6\uff0c\u4e0d\u505c\u5730\u559d\u7740\u9152\uff0c \u5076\u800c\uff0c\u4e00\u6ef4\u9152\u4ece\u846b\u82a6\u8fb9\u6ef4\u5230\u706b\u76c6\u4e2d\uff0c\u7acb\u523b\u5730\u53d1\u51fa\u707f\u9752\u8272\u7684\u706b\u7130\uff0c\u663e\u7136\uff0c\u8fd9\u9152\u6027\u662f\u76f8\u5f53\u5f3a\u70c8 \u7684\u3002 \u3000\u3000\u8001\u4eba\u8eab\u65c1\u5750\u7740\u4e00\u4e2a\u5b69\u5b50\uff0c\u5fc3\u4e0d\u5728\u7109\u7684\u8bfb\u7740\u4e66\uff0c\u4e0d\u65f6\u5730\u671b\u671b\u7070\u8272\u7684\u5929\uff0c\u94f6\u8272\u7684\u5927\u5730\uff0c\u548c\u559d \u9152\u7684\u8001\u4eba\u7684\u767d\u80e1\u5b50\uff0c\u7136\u540e\uff0c\u8f7b\u8f7b\u5730\u5408\u4e0a\u4e66\uff0c\u53f9\u4e86\u53e3\u6c14\u9053\uff1a\u201c\u6885\u516c\u516c\uff0c\u8fd8\u6709\u4e09\u5929\u5c31\u8fc7\u5e74\u4e86\uff0c\u7239 \u7239\u5988\u5988\u600e\u4e48\u8fd8\u4e0d\u56de\u6765\uff1f\u201d \u3000\u3000\u6885\u516c\u516c\u7b11\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u88ab\u4ec0\u4e48\u4e8b\u7eca\u4f4f\u4e86\uff0c\u5728\u6c5f\u6e56\u884c\u8d70\uff0c\u5c31\u662f\u6709\u8bb8\u591a\u610f\u60f3\u4e0d\u5230\u7684\u4e8b\uff0c\u4f60\u8fd9 \u5a03\u513f\uff0c\u524d\u4e9b\u65e5\u5b50\u8fd8\u5077\u5077\u8dd1\u51fa\u8d70\u8bf4\u8981\u95ef\u8361\u6c5f\u6e56\uff0c\u626c\u540d\u7acb\u4e07\uff0c\u73b0\u5728\u5374\u8fde\u7239\u5a18\u90fd\u79bb\u4e0d\u5f00\u3002\u201d \u3000\u3000\u5b69\u5b50\u8138\u4e0a\u4e0a\u7ea2\uff0c\u8fa9\u9053\uff1a\u201c\u8c01\u8bf4\u6211\u79bb\u4e0d\u5f00\u7239\u7239\u5988\u5988\uff0c\u6211\u4e0d\u8fc7\u60f3\u5927\u5bb6\u5728\u4e00\u5757\u8fc7\u5e74\u6bd4\u8f83\u70ed\u95f9\u70b9 \u513f\u3002\u201d \u3000\u3000\u6885\u516c\u516c\u6148\u7965\u7684\u9053\uff1a\u201c\u5bf9\uff0c\u5bf9\uff0c\u5e73\u513f\u4f60\u8bf4\u5f97\u4e00\u70b9\u4e0d\u9519\uff0c\u5582\uff0c\u4f60\u4eca\u5e74\u8fc7\u5e74\u8fd8\u6562\u548c\u516c\u516c\u8d4c\u94b1 \u5417\uff1f\u201d \u3000\u3000\u90a3\u5b69\u5b50\u53cc\u7709\u4e00\u626c\u9053\uff1a\u201c\u53c8\u6709\u4ec0\u4e48\u4e0d\u6562\uff1f\u201d \u3000\u3000\u539f\u6765\u8fd9\u8001\u7684\u6b63\u662f\u6614\u5e74\u6c5f\u6e56\u4e0a\u540d\u5934\u6700\u54cd\u7684\u201c\u4e03\u5999\u795e\u541b\u201d\u6885\u5c71\u6c11\uff0c\u5c0f\u7684\u662f\u6885\u9999\u795e\u5251\u8f9b\u6377\u7684\u7231 \u5b50\uff0c\u91d1\u7ae5\u8f9b\u5e73\u3002 \u3000\u3000\u6885\u516c\u516c\u9053\uff1a\u201c\u5e73\u513f\uff0c\u4f60\u7684\u5c0f\u670b\u53cb\u5462\uff1f\u201d \u3000\u3000\u8f9b\u5e73\u4e00\u5476\u5634\u9053\uff1a\u201c\u5979\u5728\u8ddf\u5979\u59ca\u59ca\u5b66\u7ee3\u82b1\uff0c\u54fc\uff0c\u6211\u77a7\u5979\u5fc3\u91ce\u5f97\u5f88\uff0c\u5b66\u4ec0\u4e48\u7ee3\u82b1\uff0c\u8fd9\u4e00\u4f1a\u513f \u4e0d\u77e5\u53c8\u7528\u65ad\u51e0\u6839\u9488\u4e86\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u5475\u5475\u7b11\u9053\uff1a\u201c\u4f60\u5fc3\u4e0d\u91ce\uff1f\u8fd8\u6709\u8138\u9a82\u522b\u4eba\u4e48\uff1f\u4f60\u548c\u5979\u516b\u6210\u513f\u53c8\u5435\u67b6\u4e86\uff0c\u5bf9\u4e48\uff1f\u201d \u3000\u3000\u91d1\u91cd\u8f9b\u5e73\u5f88\u61ca\u607c\u7684\u9053\uff1a\u201c\u5979\u4e00\u5929\u5230\u665a\u56b7\u7740\u8981\u7ec3\u6b66\uff0c\u7ec3\u8f7b\u529f\uff0c\u6211\u5c31\u6559\u5979\u6700\u7b80\u5355\u7684\u8eab\u6cd5\u53e3 \u8bc0\uff0c\u5979\u53c8\u4e0d\u7528\u5fc3\u5b66\uff0c\u6628\u5929\u6211\u903c\u5f97\u7d27\u4e86\uff0c\u5979\u5c31\u4e71\u8df3\u4e71\u7eb5\u51e0\u4e0b\uff0c\u7ed3\u679c\u6454\u4e86\u4e00\u8de4\u91cd\u7684\uff0c\u5c31\u4e0d\u7406\u6211 \u4e86\uff0c\u6885\u516c\u516c\uff0c\u4f60\u770b\u6c14\u4e0d\u6c14\u4eba\uff1f\u201d \u3000\u3000\u6885\u5c71\u6c11\u9053\uff1a\u201c\u5e73\u513f\uff0c\u6211\u6559\u4f60\u4e00\u4e2a\u6cd5\u5b50\uff0c\u5305\u5979\u548c\u4f60\u8bf4\u8bdd\u3002\u4f60\u8dd1\u53bb\u8bf4\u6885\u516c\u516c\u8981\u8bb2\u6545\u4e8b\u5566\uff0c\u5979 \u662f\u6700\u7231\u542c\u7684\uff0c\u4e00\u5b9a\u4f1a\u95ee\uff1a\u201c\u771f\u7684\u5417\u2019\uff1f \u3000\u3000\u4f60\u5c31\u8bf4\uff1a\u201c\u5f53\u7136\u662f\u771f\u7684\uff0c\u5443\uff0c\u4f60\u4e0d\u662f\u4e0d\u7406\u6211\u5417\uff1f\u2019\u5979\u4e00\u5b9a\u7b11\u9a82\uff1a\u201c\u6211\u53c8\u6ca1\u548c\u4f60\u8bf4\u8bdd\u3002\u2019 \u4f60\u5c31\u8bf4\uff1a\u201c\u597d\u5566\uff0c\u597d\u5566\uff0c\u4f60\u5230\u5e95\u53bb\u4e0d\u53bb\u3002\u2019\u5979\u4fbf\u4f1a\u8ddf\u4f60\u6765\u4e0d\u518d\u751f\u6c14\uff0c\u8fd9\u6837\uff0c\u4f60\u4e5f\u4e0d\u5931\u9762\u5b50\uff0c \u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d \u3000\u3000\u8f9b\u5e73\u5fc3\u91cc\u6709\u4e9b\u8dc3\u8dc3\u6b32\u8bd5\uff0c\u4f46\u53e3\u4e2d\u8fd8\u5f3a\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u5979\u4e0d\u7406\u6211\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff0c\u6211\u53ef\u4e0d\u5728 \u4e4e\uff0c\u5979\u518d\u8fd9\u6837\u65e0\u793c\uff0c\u6211\u665a\u4e0a\u53c8\u53bb\u88c5\u9b3c\u5413\u5979\u3002\u6885\u516c\u516c\uff0c\u4e0a\u6b21\u5440\uff0c\u4f60\u5230\u670b\u53cb\u5bb6\u53bb\u4e0b\u68cb\u4e86\uff0c\u5979\u548c\u6211 \u8d4c\u6c14\uff0c\u6211\u6c14\u5979\u4e0d\u8fc7\uff0c\u665a\u4e0a\u7a7f\u8d77\u767d\u8863\uff0c\u6234\u4e0a\u7239\u7239\u7684\u4eba\u76ae\u9762\u5177\uff0c\u505a\u4e86\u4e00\u4e2a\u5047\u820c\u5934\u5728\u5979\u7a97\u53e3\u4e00\u7ad9\uff0c \u5979\u5413\u5f97\u5c16\u53eb\uff0c\u5927\u558a\uff0c\u603b\u7b97\u8fd8\u6709\u80c6\u5b50\uff0c\u6ca1\u6709\u660f\u5012\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u9053\uff1a\u201c\u4f60\u8fd9\u4e0d\u662f\u592a\u80e1\u95f9\u4e48\uff1f\u4f60\u628a\u5979\u5413\u574f\u4e86\u600e\u751f\u5f97\u4e86\uff1f\u201d \u3000\u3000\u8f9b\u5e73\u5f97\u610f\u9053\uff1a\u201c\u5979\u5e73\u5e38\u81ea\u5439\u80c6\u5b50\u591a\u4e48\u5927\uff0c\u5c31\u662f\u8001\u864e\u5927\u86c7\u5728\u5979\u9762\u524d\uff0c\u5979\u4e5f\u4e0d\u5bb3\u6015\uff0c\u6885\u516c \u516c\uff0c\u4f60\u731c\u4ed6\u5413\u5f97\u558a\u8c01\uff1f\u201d \u3000\u3000\u6885\u5c71\u6c11\u9053\uff1a\u201c\u4e00\u5b9a\u662f\u558a\u5979\u59ca\u59ca\u6216\u662f\u558a\u6885\u516c\u516c\u4e86\u3002\u201d \u3000\u3000\u8f9b\u5e73\u6447\u5934\u9053\uff1a\u201c\u5979\u5927\u558a\uff1a\u201c\u8f9b\u5e73\u8f9b\u5c0f\u4fa0\uff0c\u6709\u9b3c\u5440\uff0c\u4f60\u5feb\u6765\u6551\u6551\u6211\u5440\u3002\u2019\u6211\u5728\u7a97\u5916\u5fc3\u4e2d\u4e00 \u4e50\uff0c\u60f3\u5979\u5e73\u65e5\u6700\u4e0d\u80af\u53eb\u6211\u8f9b\u5c0f\u4fa0\uff0c\u8fd9\u65f6\u5927\u6982\u6015\u5f97\u7d27\uff0c\u4e5f\u7167\u6837\u53eb\u4e86\uff0c\u6211\u5c31\u628a\u9762\u5177\u548c\u767d\u8863\u4e00\u8131\uff0c \u957f\u820c\u5934\u62d4\u6389\u4e22\u5728\u6885\u6797\u4e2d\uff0c\u4ece\u7a97\u5b50\u8df3\u8fdb\uff0c\u5979\u89c1\u6211\u6765\u4e86\uff0c\u6251\u4e0a\u6765\u62b1\u2026\u2026\u201d \u3000\u3000\u8f9b\u5e73\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a81\u7136\u611f\u5230\u4e0d\u597d\u610f\u601d\uff0c\u5fc3\u60f3\u88ab\u4e00\u4e2a\u5973\u5b69\u5b50\u62b1\u5c82\u53ef\u4efb\u610f\u8bf4\u51fa\uff0c\u5f53\u4e0b\u5c31\u4f4f\u53e3\u4e0d \u8bf4\u4e86\uff0c \u3000\u3000\u6885\u5c71\u6c11\u73b2\u542c\u8fd9\u4e00\u53cc\u5c0f\u5973\u513f\u5435\u5435\u95f9\u95f9\u7684\u5c0f\u4e8b\uff0c\u5fc3\u662f\u5f88\u611f\u6709\u8da3\uff0c\u4ed6\u5fc3\u60f3\uff1a\u201c\u8f9b\u5e73\u8fd9\u5b69\u5b50\uff0c\u4efb \u8c01\u90fd\u5f97\u987a\u4ed6\u7684\u813e\u6c14\uff0c\u53ef\u662f\u5728\u6797\u7389\u8fd9\u4e2b\u5934\u9762\u524d\uff0c\u603b\u80af\u5403\u4e8f\uff0c\u8fd9\u5bf9\u5b69\u5b50\uff0c\u5404\u4eba\u90fd\u628a\u6700\u73cd\u8d35\u7684\u60c5\u611f \u4ed8\u7ed9\u5bf9\u65b9\uff0c\u53ea\u662f\u2014\u2014\u53ea\u662f\u4ed6\u4eec\u90fd\u8fd8\u4e0d\u660e\u767d\u7f62\u4e86\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u5ffd\u9053\uff1a\u201c\u5e73\u513f\uff0c\u4f60\u5343\u4e07\u4e0d\u53ef\u6bcf\u4e8b\u4efb\u610f\u800c\u4e3a\uff0c\u6709\u4e9b\u4e8b\u60c5\u505a\u9519\u4e86\uff0c\u53ef\u4ee5\u91cd\u505a\uff0c\u5931\u8d25 \u4e86\uff0c\u53ef\u4ee5\u9f13\u8d77\u52c7\u6c14\u518d\u5e72\uff0c\u53ef\u662f\u6709\u4e9b\u4e8b\uff0c\u4f60\u4e00\u751f\u53ea\u6709\u4e00\u6b21\u673a\u4f1a\uff0c\u5982\u679c\u4f60\u5931\u53bb\u5b83\uff0c\u4f60\u5c31\u7ec8\u8eab\u518d\u5f97 \u4e0d\u7740\u5566\u3002\u201d \u3000\u3000\u91d1\u7ae5\u8f9b\u5e73\u4f3c\u61c2\u975e\u61c2\uff0c\u770b\u89c1\u6885\u516c\u516c\u795e\u8272\u5947\u7279\uff0c\u4f3c\u4e4e\u6c89\u601d\u7740\u60a0\u8fdc\u7684\u4ece\u524d\uff0c\u5fc3\u4e2d\u6b63\u611f\u5947\u602a\uff0c\u6885 \u5c71\u6c11\u5ffd\u7136\u81ea\u5df2\u53d1\u89c9\u795e\u8272\u6709\u5f02\uff0c\u8fde\u5fd9\u5475\u5475\u7b11\u9053\uff1a\u201c\u5e73\u513f\uff0c\u4f60\u77a7\u6211\u771f\u8001\u7cca\u6d82\u4e86\uff0c\u600e\u4e48\u548c\u4f60\u8bb2\u8fd9\u4e9b \u6ca1\u610f\u601d\u7684\u8bdd\u3002\u201d \u3000\u3000\u8f9b\u5e73\u9053\uff1a\u201c\u6885\u516c\u516c\uff0c\u6211\u4e0d\u61c2\u5f97\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d \u3000\u3000\u6885\u5c71\u6c11\u9053\uff1a\u201c\u4f60\u4e0d\u5fc5\u61c2\u7684\uff0c\u5e73\u513f\uff0c\u96ea\u505c\u4e86\uff0c\u4f60\u5feb\u53bb\u62e3\u4e9b\u677e\u679d\uff0c\u665a\u4e0a\u54b1\u4eec\u628a\u706b\u70e7\u5f97\u65fa\u65fa \u7684\uff0c\u516c\u516c\u8bb2\u4e2a\u7cbe\u5f69\u7684\u6545\u4e8b\u3002\u201d \u3000\u3000\u91d1\u7ae5\u5927\u559c\uff0c\u4ed6\u672c\u5c31\u95f7\u5f97\u53d1\u614c\uff0c\u770b\u770b\u96ea\u505c\u5929\u5f00\uff0c\u5c31\u98de\u5feb\u8dd1\u51fa\u53bb\u62fe\u67af\u679d\u3002\u3000\u3000\u3000\u3000\u4e03\u5999\u795e\u541b \u6885\u5c71\u6c11\u53c8\u8dcc\u5165\u6c89\u601d\u2026\u2026 \u3000\u3000\u201c\u6709\u4e9b\u4e8b\uff0c\u53ea\u6709\u4e00\u6b21\u673a\u4f1a\uff0c\u5931\u53bb\u4e86\uff0c\u4fbf\u6c38\u8fdc\u5931\u53bb\uff0c\u50cf\u60c5\u611f\u5c31\u662f\u8fd9\u79cd\u4e1c\u897f\uff0c\u5f53\u4f60\u7b2c\u4e00\u6b21\u4ed8 \u51fa\u53bb\u2014\u2014\u90a3\u4e00\u5b9a\u662f\u5168\u90e8\u7684\u4ed8\u51fa\uff0c\u5982\u679c\u843d\u7a7a\u4e86\uff0c\u90a3\u4e48\u5c3d\u7ba1\u4f60\u6709\u901a\u5929\u7684\u80fd\u8010\uff0c\u6700\u591a\u4e5f\u53ea\u80fd\u6536\u56de\u4e00 \u70b9\u70b9\u513f\uff0c\u50cf\u6211\u8fd9\u79cd\u4eba\uff0c\u4e00\u751f\u53ea\u4ed8\u51fa\u53bb\u4e00\u6b21\uff0c\u5c31\u6c38\u8fdc\u6536\u4e0d\u56de\u6765\u4e86\u3002\u201d \u3000\u3000\u7f2a\u4e5d\u5a18\u7684\u5029\u5f71\u53c8\u4ece\u5fc3\u5e95\u6d6e\u8d77\uff0c\u6885\u5c71\u6c11\u5e73\u9759\u7684\u671b\u7740\u8fdc\u65b9\uff0c\u516b\u5341\u591a\u4e2a\u5e74\u5934\u7684\u6069\u6069\u6028\u6028\uff0c\u60c5\u5b7d \u7ea0\u7f20\uff0c\u90fd\u4e00\u5757\u513f\u6d41\u8fc7\u80f8\u4e2d\uff0c\u4f46\u89c9\u7075\u53f0\u65b9\u5bf8\u4e4b\u95f4\uff0c\u4e00\u7247\u6e05\u660e\uff0c\u4f46\uff0c\u90a3\u53ea\u662f\u4e00\u5239\u90a3\u3002 \u3000\u3000\u201c\u5982\u679c\u6ca1\u6709\u8f89\u714c\u7684\u9752\u5e74\uff0c\u90a3\u4e48\u5e74\u8001\u7684\u65f6\u4faf\u4fbf\u6ca1\u6709\u4ec0\u4e48\u597d\u5480\u56bc\u56de\u5fc6\u7684\u3002\u201d\u4ed6\u60f3\uff1a\u201c\u6211\u8fd9\u4e00 \u8eab\u53ef\u7b97\u662f\u591a\u5f69\u591a\u59ff\u9b44\u4e86\u3002\u201d\u4ed6\u773c\u89d2\u6d6e\u8d77\u4e86\u6de1\u6de1\u7684\u5fae\u7b11\uff1a\u201c\u5e74\u9752\u7684\u65e5\u5b50\uff0c\u662f\u88ab\u8ba4\u4e3a\u540d\u6ee1\u5929\u4e0b\u7684 \u602a\u4eba\uff0c\u7387\u6027\u505a\u7740\u81ea\u5df1\u7231\u505a\u7684\u4e8b\uff0c\u90a3\u7ba1\u522b\u4eba\u5bf9\u81ea\u5df1\u662f\u656c\u3001\u754f\u3001\u50e7\u3001\u6076\uff0c\u81ea\u547d\u4e0d\u51e1\uff0c\u631f\u7740\u51e0\u6837\u96d5 \u866b\u5c0f\u6280\uff0c\u5351\u89c6\u5929\u4e0b\u60a0\u60a0\u82cd\u751f\u3002\u201d \u3000\u3000\u201c\u4e2d\u5e74\u7684\u65f6\u4faf\uff0c\u6211\u9047\u4e0a\u4e86\u7f2a\u4e5d\u5a18\uff0c\u5728\u522b\u4eba\u5bf9\u6211\u201c\u4e03\u5999\u771f\u541b\u201d\u7684\u201c\u4e03\u5999\u201d\u4e2d\uff0c\u6709\u201c\u8272\u201d\u8fd9 \u4e00\u5999\uff0c\u5176\u5b9e\u90a3\u662f\u51e1\u592b\u4efd\u5b50\u7684\u89c1\u89e3\uff0c\u6211\u5230\u5904\u4e0e\u5973\u4eba\u6240\u6df7\uff0c\u4f55\u66fe\u4ed8\u4e0b\u534a\u4e1d\u611f\u60c5\uff0c\u7136\u800c\u5f53\u6211\u548c\u7f2a\u4e5d \u5a18\u7ed3\u4ea4\u540e\uff0c\u6211\u624d\u53d1\u89c9\u832b\u832b\u4e16\u4e0a\u8fd8\u6709\u53ef\u5bc4\u6258\u81ea\u5df1\u4e00\u5207\u7684\u2014\u2014\u5305\u62ec\u751f\u547d\u3001\u8d22\u5bcc\u548c\u60c5\u611f\uff0c\u90a3\u5c31\u662f\u76f8 \u7231\u7684\u4eba\u513f\uff0c\u8fd9\u662f\u81ea\u53e4\u4ee5\u6765\uff0c\u4e16\u4e16\u4ee3\u4ee3\u90fd\u4e0d\u53d8\u7684\u3002\u201d \u3000\u3000\u4ed6\u4ecd\u7136\u6302\u7740\u5fae\u7b11\uff0c\u4f46\u662f\u7b11\u610f\u4e2d\u5374\u591a\u4e86\u4e00\u79cd\u51c4\u51c9\u7684\u5473\u9053\uff0c\u767d\u53d1\u65e0\u98ce\u81ea\u52a8\uff0c\u663e\u7136\u662f\u5fc3\u60c5\u5728\u6fc0 \u52a8\u7740\u3002 \u3000\u3000\u4e16\u4eba\u5e38\u9053\uff1a\u201c\u4f17\u53e3\u94c4\u91d1\u201d\uff0c\u90a3\u662f\u4e0d\u9519\u7684\uff0c\u5c31\u8fde\u5979\u4e5f\u76f8\u4fe1\u6211\u662f\u73a9\u5f04\u611f\u60c5\u7684\u9b54\u738b\uff0c\u62c2\u8896\u800c \u53bb\uff0c\u6885\u5c71\u6c11\u554a\u6885\u5c71\u6c11\uff0c\u8fd9\u4e5f\u7b97\u662f\u4f60\u4e0d\u62d8\u7ec6\u884c\uff0c\u8352\u5510\u653e\u8361\u7684\u62a5\u5e94\u5427\u3002 \u3000\u3000\u201c\u6211\u5728\u4e07\u822c\u7edd\u671b\u4e0b\uff0c\u63a5\u53d7\u4e86\u4e2d\u5dde\u4e94\u5927\u5251\u6d3e\u7684\u6b7b\u7ea6\u4f1a\uff0c\u4e2d\u4e86\u6697\u7b97\uff0c\u529f\u529b\u5c3d\u5931\uff0c\u5979\u8bef\u4ee5\u6211 \u6b7b\uff0c\u54c0\u6025\u6210\u72c2\uff0c\u4e86\u7ed3\u6b8b\u751f\uff0c\u4e8e\u662f\uff0c\u6211\u4ece\u4e00\u4e2a\u53f1\u54a4\u98ce\u4e91\u7684\u795e\u541b\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u624b\u65e0\u7f1a\u9e21\u4e4b\u529b\u7684\u8001 \u4eba\u3002\u201d \u3000\u3000\u5176\u5b9e\u4ed6\u90a3\u77e5\u9053\u5f53\u4ed6\u8d74\u7ea6\u540e\u51e0\u5929\uff0c\u7f2a\u4e5d\u5a18\u5df2\u53d1\u89c9\u4e24\u4eba\u4e4b\u95f4\u662f\u8bef\u4f1a\uff0c\u5c31\u60f3\u8d76\u56de\u76f8\u805a\uff0c\u6ca1\u60f3\u5230 \u6c5f\u6e56\u4e0a\u7adf\u7136\u4f20\u95fb\u4e03\u5999\u795e\u541b\u6885\u5c71\u6c11\u5728\u4e94\u534e\u5c71\u4e27\u8eab\u4e8e\u4e2d\u5dde\u4e94\u5927\u5251\u6d3e\u7684\u8054\u624b\u5939\u51fb\uff0c\u5979\u5f53\u65f6\u53c8\u6025\u53c8 \u60b2\uff0c\u5fc3\u77e5\u6885\u5c71\u6c11\u5b9a\u662f\u56e0\u4e3a\u548c\u81ea\u5df1\u51b3\u88c2\uff0c\u8fd9\u624d\u4f24\u5fc3\u4e0d\u987e\u4e00\u5207\u7684\u63a5\u53d7\u4e94\u5927\u5251\u6d3e\u6311\u6218\uff0c\u5979\u5f53\u4e0b\u4fbf\u5b64 \u8eab\u4e0a\u5d06\u5cd2\u5c71\u53bb\u6211\u5389\u9e57\u62a5\u4ec7\uff0c\u4f46\u4e0d\u662f\u5389\u94a8\u5bf9\u624b\uff0c\u88ab\u5389\u9e43\u8d76\u4e0b\u5d06\u5cd2\u5c71\uff0c\u5979\u5929\u6027\u9ad8\u50b2\uff0c\u53d7\u6b64\u5927\u8fb1\uff0c \u800c\u4e14\u53c8\u4ee5\u4e3a\u6885\u5c71\u6c11\u5df2\u6b7b\uff0c\u662f\u4ee5\u5931\u53bb\u7406\u667a\uff0c\u7ec8\u65e5\u75af\u75af\u766b\u766b\u800c\u6b7b\u3002 \u3000\u3000\u5317\u98ce\u5439\u6563\u4e86\u5f64\u4e91\uff0c\u770b\u6837\u5b50\u96ea\u662f\u4e0d\u4f1a\u4e0b\u4e86\uff0c\u6885\u5c71\u6c11\u5fc3\u60f3\uff1a\u201c\u4e16\u4e8b\u7684\u53d8\u5316\u5c82\u662f\u4eba\u6240\u80fd\u9006\u6599\uff0c \u4e00\u4e2a\u4eba\u4ece\u8d85\u4eba\u53d8\u6210\u51e1\u4eba\uff0c\u90a3\u5fc3\u60c5\u6070\u5982\u505a\u4e86\u4e00\u68a6\uff0c\u68a6\u666f\u60c5\u6700\u771f\u3002\u7136\u800c\u4e00\u5207\u90fd\u4e0d\u5b58\u5728\u4e86\uff0c\u53ea\u6709\u7ecf \u5386\u8fc7\u8fd9\u79cd\u53d8\u5316\u7684\u4eba\uff0c\u624d\u80fd\u9886\u609f\u4ee5\u8fd9\u79cd\u590d\u6742\u7684\u611f\u60c5\u554a\uff01\u201d\u201c\u6885\u516c\u516c\uff0c\u4f60\u5728\u5e72\u5417\uff1f\u201d \u3000\u3000\u539a\u539a\u7f8a\u6bdb\u95e8\u5e18\u5f00\u5904\uff0c\u8dd1\u51fa\u4e00\u4e2a\u5c0f\u5973\u5b69\uff0c\u8138\u988a\u5ae9\u7ea2\uff0c\u5341\u5206\u53ef\u7231\u3002 \u3000\u3000\u6885\u5c71\u6c11\u4ece\u5e7b\u5883\u4e2d\u60ca\u9192\uff0c\u6413\u6413\u624b\u9053\uff1a\u201c\u7389\u5a03\u513f\uff0c\u6211\u8001\u4eba\u5bb6\u5728\u70e4\u706b\u54e9\uff01\u4f60\u4e5f\u6765\u4e48\uff1f\u201d \u3000\u3000\u539f\u6765\u90a3\u5973\u5b69\u6b63\u662f\u91d1\u7ae5\u8f9b\u5e73\u7684\u73a9\u4f34\u6797\u7389\u3002 \u3000\u3000\u6797\u7389\u8e0c\u8e87\u4e00\u4f1a\uff0c\u5ffd\u9053\uff1a\u201c\u6885\u516c\u516c\uff0c\u96ea\u505c\u4e86\uff0c\u6211\u66ff\u4f60\u5230\u6865\u5934\u6cbd\u9152\u53bb\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u7b11\u9053\uff1a\u201c\u4f60\u8fd9\u5a03\u513f\uff0c\u53c8\u6709\u4ec0\u4e48\u8981\u6c42\u6211\u8001\u4eba\u5bb6\uff0c\u5e72\u8106\u8bf4\u51fa\u6765\uff0c\u522b\u5728\u6211\u8001\u4eba\u5bb6\u9762\u524d\u5f04 \u9b3c\u4e86\u3002\u201d \u3000\u3000\u6797\u7389\u5c0f\u8138\u4e00\u7ea2\uff0c\u4e0d\u4f9d\u9053\uff1a\u201c\u4eba\u5bb6\u597d\u5fc3\u610f\u8981\u66ff\u4f60\u6253\u9152\uff0c\u4f60\u5012\u9a82\u4eba\u5bb6\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u6015\u7f9e\u4e86\u5979\uff0c\u6148\u7965\u7684\u7b11\u9053\uff1a\u201c\u597d\u5440\uff0c\u4f60\u66ff\u6211\u6cbd\u9152\uff0c\u8c22\u8c22\u4f60\u5566\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\uff0c\u6211\u8001\u4eba \u5bb6\u4e00\u5b9a\u5e2e\u4f60\u3002\u201d \u3000\u3000\u6797\u7389\u60f3\u4e86\u534a\u5929\uff0c\u6885\u5c71\u6c11\u4e0d\u4f4f\u50ac\u4fc3\uff0c\u5979\u624d\u7ed3\u7ed3\u5df4\u5df4\u9053\uff1a\u201c\u8f9b\u5e73\u8fd9\u91ce\u5b69\u5b50\uff0c\u4ed6\u53c8\u6c14\u6211\u2026\u2026\u4e0d \u7406\uff0c\u4e0d\u7406\u4eba\u5bb6\u4e86\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u5fc3\u4e2d\u6697\u6697\u597d\u7b11\uff0c\u5490\u9053\uff1a\u201c\u521a\u624d\u4e00\u4e2a\u544a\u72b6\u7684\u624d\u53bb\uff0c\u53c8\u6765\u4e00\u4e2a\u3002\u201d\u4fbf\u88c5\u5f97\u5f88\u4e25\u8083\u9053\uff1a \u201c\u6211\u4ee5\u4e3a\u4ed6\u5728\u4f60\u59ca\u59ca\u623f\u91cc\u548c\u4f60\u4e00\u5757\u73a9\uff0c\u60f3\u4e0d\u5230\u7adf\u7136\u4e0d\u5728\uff0c\u8fd9\u534a\u5929\u4e5f\u4e0d\u77e5\u4ed6\u5230\u54ea\u53bb\u4e86\uff0c\u521a\u624d\u8fd8 \u5728\u4e0b\u96ea\uff0c\u4ed6\u4f1a\u8dd1\u5230\u54ea\u91cc\u53bb\u5462\uff1f\u201d \u3000\u3000\u6797\u7389\u5927\u6025\uff0c\u5fd9\u9053\uff1a\u201c\u591a\u534a\u53c8\u662f\u5230\u540e\u9762\u5c71\u4e0a\u53bb\u4e86\uff0c\u90a3\u5c71\u8def\u672c\u72ed\u5c0f\uff0c\u73b0\u5728\u4e0b\u6ee1\u4e86\u51b0\u96ea\uff0c\u53c8\u6ed1 \u53c8\u6e7f\u5f88\u662f\u5371\u9669\uff0c\u6211\u53bb\u627e\u4ed6\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u9053\uff1a\u201c\u4e14\u6162\uff0c\u4ed6\u4e00\u8eab\u529f\u592b\u90fd\u6015\u6ed1\u5012\u6389\u4e0b\uff0c\u4f60\u53ea\u4f1a\u6bdb\u624b\u6bdb\u811a\u51e0\u4e0b\uff0c\u90a3\u600e\u4e48\u884c\u5440\u3002\u201d \u3000\u3000\u6797\u7389\u575a\u51b3\u9053\uff1a\u201c\u4e0d\u884c\u4e5f\u5f97\u884c\uff0c\u6885\u516c\u516c\uff0c\u4f60\u4e5f\u53bb\u597d\u4e0d\u597d\uff1f\u201d \u3000\u3000\u6885\u5c71\u6c11\u6545\u4f5c\u604d\u7136\u5927\u609f\uff0c\u8fde\u9053\uff1a\u201c\u539f\u6765\u5982\u6b64\uff0c\u539f\u6765\u5982\u6b64\u3002\u201d \u3000\u3000\u6797\u7389\u6025\u95ee\u9053\uff1a\u201c\u600e\u6837\uff1f\u201d \u3000\u3000\u6885\u5c71\u6c11\u9053\uff1a\u201c\u6211\u4e00\u5927\u65e9\u5c31\u770b\u5230\u8f9b\u5e73\u8fd9\u5a03\u513f\u6101\u7709\u82e6\u8138\uff0c\u8bf4\u4ec0\u4e48\u2018\u8001\u662f\u7ed9\u6211\u6c14\u53d7\uff0c\u4e0d\u7406\u4e5f \u7f62\uff0c\u8fd8\u662f\u8ddf\u7239\u7239\u53bb\u95ef\u6c5f\u6e56\uff0c\u4f3c\u540e\u5927\u5bb6\u522b\u89c1\u9762\u3002\u2019\u6211\u542c\u4ed6\u6ca1\u5934\u6ca1\u8111\u5730\u8bf4\uff0c\u5f53\u65f6\u7684\u786e\u662f\u4e0d\u61c2\uff0c\u73b0 \u5728\u624d\u660e\u767d\u5566\u3002\u201d \u3000\u3000\u6797\u7389\u773c\u7736\u4e00\u7ea2\uff0c\u4f4e\u58f0\u9053\uff1a\u201c\u6211\u5e76\u6ca1\u6709\u548c\u4ed6\u771f\u7684\u751f\u6c14\u5440\uff01\u5509\uff0c\u4ed6\u8fd9\u4eba\u4ec0\u4e48\u90fd\u6562\u505a\u7684\uff0c\u6885\u516c \u516c\uff0c\u4e0d\u8981\u662f\u53c8\u8dd1\u51fa\u5bb6\u4e86\u3002\u201d \u3000\u3000\u201c\u4f60\u65e2\u4e0d\u751f\u4ed6\u7684\u6c14\uff0c\u4e3a\u4ec0\u4e48\u8981\u4e0d\u7406\u4ed6\u5462\uff1f\u201d \u3000\u3000\u6797\u7389\u5fc5\u6025\u5982\u711a\uff0c\u6f2b\u53e3\u9053\uff1a\u201c\u4f60\u4e0d\u61c2\u7684\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u770b\u5979\u6025\u5f97\u773c\u6cea\u6c6a\u6c6a\uff0c\u77e5\u5979\u771f\u60c5\u6d41\u9732\uff0c\u4e0d\u5b9c\u518d\u9a97\uff0c\u5c31\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u522b\u6025\uff0c\u522b\u6025\uff0c \u8f9b\u5e73\u53bb\u62e3\u67af\u679d\u6765\u70e7\uff0c\u4ed6\u8bf4\u5047\u4f7f\u4f60\u51fa\u6765\u4e86\uff0c\u8bf7\u4f60\u4e5f\u4e00\u8d77\u53bb\u62e3\uff0c\u4ed6\u6b63\u5728\u6885\u82b1\u4e1b\u4e2d\u7b49\u4f60\u54e9\uff01\u201d \u3000\u3000\u6797\u7389\u5927\u559c\uff0c\u4e5f\u4e0d\u6687\u7ec6\u60f3\u6885\u516c\u516c\u521a\u624d\u9a97\u4ed6\u7684\u5410\u9732\u771f\u60c5\u7684\u53ef\u6076\uff0c\u5c31\u98de\u5954\u51fa\u53bb\u3002 \u3000\u3000\u6885\u5c71\u6c11\u4ece\u706b\u76c6\u4e0b\u53d6\u51fa\u4e00\u6839\u70d8\u5f97\u534a\u5e72\u7684\u677e\u679d\uff0c\u52a0\u5165\u5c06\u7184\u7684\u706b\u4e2d\uff0c\u671b\u7740\u6797\u7389\u7684\u80cc\u5f71\uff0c\u4e24\u652f\u5c0f \u8fab\u513f\u4e00\u6643\u4e00\u6643\uff0c\u4e0d\u7981\u5583\u5583\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4eba\u751f\uff0c\u5c0f\u7684\u6e10\u6e10\u957f\u6210\uff0c\u8001\u7684\u6e10\u6e10\u8870\u53bb\uff0c\u8fd9\u5c31\u6837\uff0c\u524d \u8f88\u7684\u529f\u4e1a\u6210\u5c31\u624d\u4f1a\u5ef6\u7eed\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u5288\u62cd\u201d\uff0c\u201c\u5288\u62cd\u201d\uff0c\u653e\u4e0b\u7684\u677e\u679d\u70e7\u7740\u4e86\uff0c\u6ee1\u5ba4\u53d1\u51fa\u4e00\u79cd\u51fa\u5c18\u7684\u6e05\u9999\u3002\u3000\u3000\u3000\u3000\u6797\u7389\u5fc5 \u6025\u5982\u711a\uff0c\u6f2b\u53e3\u9053\uff1a\u201c\u4f60\u4e0d\u61c2\u7684\u3002\u201d \u3000\u3000\u6885\u5c71\u6c11\u770b\u5979\u6025\u5f97\u773c\u6cea\u6c6a\u6c6a\uff0c\u77e5\u5979\u771f\u60c5\u6d41\u9732\uff0c\u4e0d\u5b9c\u518d\u9a97\uff0c\u5c31\u9053\uff1a\u201c\u5c0f\u5a03\u513f\uff0c\u522b\u6025\uff0c\u522b\u6025\uff0c \u8f9b\u5e73\u53bb\u62e3\u67af\u679d\u6765\u70e7\uff0c\u4ed6\u8bf4\u5047\u4f7f\u4f60\u51fa\u6765\u4e86\uff0c\u8bf7\u4f60\u4e5f\u4e00\u8d77\u53bb\u62e3\uff0c\u4ed6\u6b63\u5728\u6885\u82b1\u4e1b\u4e2d\u7b49\u4f60\u54e9\uff01\u201d \u3000\u3000\u6797\u7389\u5927\u559c\uff0c\u4e5f\u4e0d\u6687\u7ec6\u60f3\u6885\u516c\u516c\u521a\u624d\u9a97\u4ed6\u7684\u5410\u9732\u771f\u60c5\u7684\u53ef\u6076\uff0c\u5c31\u98de\u5954\u51fa\u53bb\u3002 \u3000\u3000\u6885\u5c71\u6c11\u4ece\u706b\u76c6\u4e0b\u53d6\u51fa\u4e00\u6839\u70d8\u5f97\u534a\u5e72\u7684\u677e\u679d\uff0c\u52a0\u5165\u5c06\u7184\u7684\u706b\u4e2d\uff0c\u671b\u7740\u6797\u7389\u7684\u80cc\u5f71\uff0c\u4e24\u652f\u5c0f \u8fab\u513f\u4e00\u6643\u4e00\u6643\uff0c\u4e0d\u7981\u5583\u5583\u8bf4\u9053\uff1a\u201c\u8fd9\u5c31\u662f\u4eba\u751f\uff0c\u5c0f\u7684\u6e10\u6e10\u957f\u6210\uff0c\u8001\u7684\u6e10\u6e10\u8870\u53bb\uff0c\u8fd9\u5c31\u6837\uff0c\u524d \u8f88\u7684\u529f\u4e1a\u6210\u5c31\u624d\u4f1a\u5ef6\u7eed\u4e0b\u53bb\u3002\u201d \u3000\u3000\u201c\u5288\u62cd\u201d\uff0c\u201c\u5288\u62cd\u201d\uff0c\u653e\u4e0b\u7684\u677e\u679d\u70e7\u7740\u4e86\uff0c\u6ee1\u5ba4\u53d1\u51fa\u4e00\u79cd\u51fa\u5c18\u7684\u6e05\u9999\u3002'}),showOnHomepage:!0}]}}]);